٣٠ ١٦ إنّك لا تدري ما يقول هذا الغلام','6','265',NULL,NULL,'0');
INSERT INTO [b10275] VALUES (2107,'٣٢ ١٧ البهلول بن حسّان، يبذل ماله للقريب والبعيد
٣٤ ١٨ إسحاق بن البهلول، يحدّث من حفظه بخمسين ألف حديث
٣٦ ١٩ القاضي أسد بن عمرو، يصلح قبلة جامع واسط
٣٧ ٢٠ أشعب الطامع بين سالم بن عبد الله وعبد الله بن عمرو بن عثمان
٤٠ ٢١ سالم بن عبد الله يقسم تمرا
٤١ ٢٢ الحد الذي بلغه طمع أشعب
٤٢ ٢٣ القاضي أبو الوليد الكندي، يأبى أن ينفذ قضاء يحيى بن أكثم
٤٤ ٢٤ التسليم للفقهاء سلامة في الدين
٤٥ ٢٥ نسب أبي الهيثم التنوخي
٤٦ ٢٦ القاضي البهلول بن إسحاق الأنباري
٤٧ ٢٧ لماذا سمّي بشّار بالمرعث ١
٤٨ ٢٨ لماذا سمّي بشّار بالمرعث ٢
٤٩ ٢٩ ارحمهم رحمك الله
٥٠ ٣٠ بين جعفر البرمكي وعبد الملك بن صالح الهاشمي
٥٢ ٣١ القاضي جعفر بن محمد بن عمّار
٥٤ ٣٢ وقف بعرفة ستا وخمسين وقفة على المذهب
٥٥ ٣٣ أبو محمد جعفر بن محمد بن أحمد التنوخي
٥٧ ٣٤ ما لي وللعيد
٥٨ ٣٥ أبو العيناء يرثي الحسن بن سهل
٥٩ ٣٦ القاضي أبو محمد الحسن بن أبي الشوارب','6','266',NULL,NULL,'0');
INSERT INTO [b10275] VALUES (2108,'٦٠ ٣٧ المنصور ينصح ولده المهدي، بالإقبال على الفقه والمغازي
٦٢ ٣٨ الحسن بن عمارة، يكرم أحد طلّاب الحديث
٦٣ ٣٩ عبيد الله بن محمد بن صفوان، يتقلّد للمهدي قضاء المدينة
٦٤ ٤٠ القاضي أبو حسان الزيادي، يضرب رجلا ألف سوط، ويتركه في الشمس حتى يموت
٦٦ ٤١ الخليفة الواثق يستقضي الحسن بن علي بن الجعد
٦٧ ٤٢ جريت مع الصبا طلق الجموح
٦٩ ٤٣ من شعر أبي عبد الله بن الحجاج
٧٠ ٤٤ لحية القاضي العوفي، تبلغ إلى ركبته
٧١ ٤٥ لحية القاضي العوفي، تعدّت كل قدر
٧٢ ٤٦ القاضي العوفي يلقي مسائله في المناظرة من الدفتر
٧٣ ٤٧ الحسين بن الضحاك الشاعر
٧٤ ٤٨ الراضي يستقضي أبا محمد الحسين بن عمر
٧٥ ٤٩ أبو علي التنوخي ينيب عنه أبا القاسم الكوفي في القضاء بالكوفة
٧٦ ٥٠ من مخاريق الحلّاج
٧٩ ٥١ محاكمة الحلّاج، وتنفيذ حكم الإعدام فيه
٩٣ ٥٢ الخليفة يدعو القاضي حفص بن غياث، فيستمهله حتى يفرغ من أمر الخصوم
٩٤ ٥٣ القاضي حفص بن غياث، تمرّ أحكامه وقضاياه كالقدح
٩٥ ٥٤ الحسن بن وهب يرثي أبا تمام الطائي
٩٦ ٥٥ مروا بالمعروف، وانهوا عن المنكر
٩٨ ٥٦ حسان بن سنان التنوخي، أدركته بركة دعاء أنس بن مالك','6','267',NULL,NULL,'0');
INSERT INTO [b10275] VALUES (2109,'١٠٠ ٥٧ حسان بن سنان التنوخي، كان نصرانيا وأسلم.
١٠١ ٥٨ افتتح القضاء بأعورين
١٠٣ ٥٩ من شعر خالد الكاتب
١٠٤ ٦٠ أبو سعد داود بن الهيثم بن إسحاق بن البهلول التنوخي الأنباري
١٠٦ ٦١ لماذا عرف بالثلّاج
١٠٧ ٦٢ ترفّق بأهل الجهل إن كنت ساقيا
١٠٨ ٦٣ ضمّ يا ضمّام، واحذر لا تنام
١٠٩ ٦٤ رأي أبي زيد الأنصاري، في أبي عبيدة والأصمعي
١١٠ ٦٥ للسريّ الرفاء يستهدي قدحا
١١١ ٦٦ أعجميّ يتنقّص الإمام عليا، فيضرب، ويطرد
١١٢ ٦٧ شبيب بن شيبة، يفزع إليه أهل البصرة في قضاء حوائجهم
١١٤ ٦٨ من حكم شبيب بن شيبة
١١٥ ٦٩ علام يؤتى المرء
١١٦ ٧٠ العباس الخياط، لا يثمر فيه الإحسان
١١٧ ٧١ من شعر ابن الأعرابي
١١٨ ٧٢ القاضي التنوخي ينيب عنه صدقة بن علي الموضلي على قضاء نصيبين وأعمالها
١١٩ ٧٣ لا عار في الصرف إذا بقيت المحاسن محروسة
١٢٠ ٧٤ المنصور العبّاسي، يضرب أسوأ الأمثال في القسوة
١٢٢ ٧٥ القاضي عبد الله بن أبي الشوارب
١٢٣ ٧٦ المنصور يضرب قهرمانه سبع درر
١٢٤ ٧٧ قطن بن معاوية الغلابي، يستسلم للمنصور','6','268',NULL,NULL,'0');
INSERT INTO [b10275] VALUES (2110,'١٢٩ ٧٨ القاضي عبد الله بن محمد، رافق الرشيد، وهلك بطوس
١٣١ ٧٩ القاضي عبد الله بن محمد الخلنجي، وعفته، وديانته
١٣٣ ٨٠ يا نفس صبرا لعلّ الخير عقباك
١٣٥ ٨١ أبو القاسم ابن بنت منيع، يفيد مائتي دينار من نسخ مغازي ابن إسحاق
١٣٧ ٨٢ ثلاثة يسلمن إلى الأجل
١٣٨ ٨٣ رأي ابن مهدي في سفيان، ومالك، وشعبة، وابن المبارك
١٣٩ ٨٤ عبد الله بن مصعب، يتكلم في أمر المدينة في العطاء والقسم
١٤٢ ٨٥ مدّ لك الله الحياة مدّا
١٤٣ ٨٦ القاضي عبد الرحمن بن إسحاق يحلّ محل إسماعيل بن حماد وبشر بن الوليد
١٤٤ ٨٧ أبو عبد الله الختلي يحدّث في البصرة بخمسين ألف حديث من حفظه
١٤٥ ٨٨ كأنّ رقيبا منك يرعى خواطري
١٤٦ ٨٩ عبيد الله بن أحمد بن غالب
١٤٧ ٩٠ لا تهجرنّي فإنّي لست ذا جلد
١٤٩ ٩١ من شعر أبي الحسن الكرخي
١٥٠ ٩٢ العالم العاقل ابن نفسه
١٥١ ٩٣ لا ردّ الله غربتك
١٥٢ ٩٤ أكني بغيرك
١٥٣ ٩٥ وظريف زوال وجد بوجد
١٥٤ ٩٦ القاضي عبد الملك بن حزم، توفي، فصلى عليه الرشيد','6','269',NULL,NULL,'0');
INSERT INTO [b10275] VALUES (2111,'١٥٥ ٩٧ المنصور يعفو عن أحد الثائرين عليه
١٥٧ ٩٨ قرشية اختارت لنفسها
١٥٩ ٩٩ عبد العزيز الأعرج لا يمسك شيئا لفرط سخائه
١٦١ ١٠٠ ابن البقال أحد المتكلمين الشيعة الزيدية
١٦٢ ١٠١ عبد العزيز بن حماد الدنقشي قاضي رامهرمز
١٦٤ ١٠٢ وتأخذ من جوانبنا الليالي
١٦٥ ١٠٣ كيف الظن بمن هو أرحم الراحمين
١٦٦ ١٠٤ تشابهت الطباع
١٦٧ ١٠٥ سقطت على الخبير
١٦٨ ١٠٦ فمن ذا يداوي جوى باطنا
١٦٩ ١٠٧ المتوكل يخيّر أهل بغداد
١٧٠ ١٠٨ الصاحي بموضع رجلي السكران، أعرف من السكران بموضع رجلي نفسه
١٧١ ١٠٩ المعتضد يستقضي أبا خازم القاضي
١٧٤ ١١٠ القاضي يحيى بن أكثم، يستخلف على الجانب الشرقي عيسى بن أبان
١٧٥ ١١١ بحق من أغراك بي زيدي
١٧٦ ١١٢ القاضي عمر بن حبيب العدوي، لم يرقاض أهيب منه
١٧٧ ١١٣ القاضي عمر بن حبيب العدوي، ينصبه المأمون قاضيا بالبصرة
١٨٠ ١١٤ القاضي أبو الحسين بن أبي عمر
١٨٢ ١١٥ ما مات من بقيت له بعد موته
١٨٣ ١١٦ غلبتني على الفؤاد الهموم','6','270',NULL,NULL,'0');
INSERT INTO [b10275] VALUES (2112,'١٨٤ ٢١٧ القاضي عثمان بن طلحة، كان لا يرتزق على القضاء
١٨٦ ١١٨ الفرّاء يقرّ للكسائي بالرئاسة
١٨٧ ١١٩ سبب تسمية صالح بصاحب المصلّى
١٩٠ ١٢٠ القاضي علي بن ظبيان، يجلس على بارية مثل البارية التي يجلس عليها الخصوم
١٩١ ١٢١ من شعر إسحاق بن إبراهيم الموصلي
١٩٣ ١٢٢ أنت لنا شمس، وفتح لنا قمر
١٩٥ ١٢٣ القاضي يحيى بن سعيد، قاضي السفاح على الهاشمية
١٩٦ ١٢٤ يحيى بن سعيد، يضطره ضيق حاله وكثرة ديونه، لتقلّد القضاء
١٩٨ ١٢٥ مطيع بن إياس، يرثي يحيى بن زياد الحارثي
١٩٩ ١٢٦ الفرّاء يملي دروسه من حفظه
٢٠٠ ١٢٧ أبو الغوث بن البحتري، يمدح ابن بسطام
٢٠١ ١٢٨ أبو حنيفة يشهد لأبي يوسف بأنّه أعلم من على الأرض
٢٠٣ ١٢٩ اللهم إنّي لم أجر في حكم حكمت به
٢٠٤ ١٣٠ أبو يوسف القاضي يصلّي في كل يوم مائتي ركعة
٢٠٥ ١٣١ ما برع أحد في علم إلّا دله على غيره من العلوم
٢٠٦ ١٣٢ الشريف أبو جعفر، ابن الجصاص المصري
٢٠٩ ١٣٣ لقد ذهب الحمار بأمّ عمرو
٢١٠ ١٣٤ والله لقد أنسيت
٢١١ ١٣٥ أبو جعفر في ضيافة أبي بكر المادرائي
٢١٣ ١٣٦ بين الشريف أبي جعفر وأبي زنبور الكاتب
