INSERT INTO [b10525] VALUES (409,'<span data-type="title" id=toc-307>الجزء الثاني</span>
<span data-type="title" id=toc-308>[تتمة التراجم]</span>
<span data-type="title" id=toc-309>(حرف الدال)</span>
<span data-type="title" id=toc-310>٢٤٩ - دماذ أبو غسّان اللغوىّ [١]</span>
من أصحاب أبي عبيدة. وكان قد قرأ من النحو إلى باب الواو والفاء.
ومن قول الخليل وأصحابه: أنّ ما بعدهما «١» ينتصب بإضمار أن. فساء فهمه عنه.
قال عبد الله بن حيّان النحوىّ: كتب دماذ إلى المازنىّ «٢»:
تفكرت في النحو حتى مللت ... وأتعبت نفسى» به والبدن
وأتعبت بكرا «٤» وأصحابه ... بطول المسائل «٥» فى كل فنّ
[فمن علمه ظاهر بيّن ... ومن علمه غامض قد بطن «٦»]
فكنت بظاهره عالما ... وكنت بباطنه ذا فطن «٧»
_________
[١] ترجمته فى بغية الوعاة ٢٤٨، وتلخيص ابن مكتوم ٧١، وسمط اللآلى ٣: ٥٧، وطبقات الزبيدىّ ١٢٨، والفهرست ٥٤.','2','5',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (410,'خلا «١» أن بابا عليه العفا ... ء للفاء يا ليته لم يكن
وللواو باب إلى جنبه ... من المقت أحسبه قد لعن
إذا قلت: هاتوا لماذا يقا ... ل: «لست بآتيك أو تأتين» «٢»
أجيبوا لما «٣» قيل هذا كذا ... على النصب قالوا: بإضمار أن
[وما إن رأيت لها موضعا ... فأعرف ما قيل إلا بظنّ «٤»]
[فقد خفت يا بكر من طول ما ... أفكّر فى أمر «أن» أن أجنّ]
ودماذ لقب؛ واسمه رفيع بن سلمة. وكان كاتب أبى عبيدة فى الأخبار، وكان من أوثق الناس عن أبى عبيدة فى الأخبار. وكان أبو حاتم إذا ذوكر فى شىء منها قال: عليكم بذاك الشيخ- يعنى أبا غسّان.
ويقال: إن المازنىّ نقل قدميه إلى أبى غسّان يسمع منه الأخبار.

<span data-type="title" id=toc-311>٢٥٠ - دومى الكوفىّ النحوىّ اللغوىّ العروضىّ [١]</span>
اسمه عمر بن محمد «٥» بن جعفر الزعفرانىّ، ويكنى أبا أحمد. كان له معرفة باللغة والنحو وفنون الشعر. وصنّف وروى عن ثعلب، وتأخر بعد زمانه طويلا.
_________
[١] ترجمته فى الفهرست ٨٤.','2','6',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (411,'وله من التصنيف: كتاب العروض؛ كبير. كتاب أىّ؛ طوّل فيه وأحسن. كتاب القوافى.
وكان يكتب خطا حسنا جميلا صحيحا فى غاية الصحة- رحمه الله. خلط المذهبين فى النحو.

<span data-type="title" id=toc-312>٢٥١ - دهمج بن محرز البصرىّ [١]</span>
من بنى نصر بن قعين، من بنى أسد بن خزيمة. فصيح لغوىّ. أفاد الناس فى زمانه، ونقلوا عنه.
وصنّف فى الغريب كتاب النوادر. قال محمد بن إسحاق النديم: «رواه عنه الحجاج «١» بن نصير الأنبارىّ. رأيته فى نحو مائة وخمسين ورقة، وفيه إصلاح [بخط «٢»] أبى عمر الزاهد».
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٠، والفهرست ٤٦؛ وذكره باسم «دهمج بن محرر».','2','7',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (412,'<span data-type="title" id=toc-313>(حرف الذال)</span>
<span data-type="title" id=toc-314>٢٥٢ - الذاكر النحوىّ المصرىّ [١]</span>
نحوىّ مشهور، كثير التفنن فيه. صاحب نكّت وهوامش وتعليقات مفيدة.
نقل عنه الكاتب الأديب المصرىّ فى مجموعه فوائد جمة. وكان الذاكر هذا قد أخذ عن ابن جنّى أبى الفتح علما كثيرا، واستوطن مصر، وأفاد بها، وتصدّر لإقراء هذا الشأن. وله شعر، منه ما هجا به أبا سعد التّسترىّ «١»:
تعاطيت تدبير الأمور سفاهة ... وأنت بدار الضّرب «٢» والصّرف أعرف
وإنى لأرجو أن أراك مجدّلا «٣» ... وريد المنايا من نجيعك يرعف «٤»
فكان الأمر فيه كما تمنّاه؛ فإنه قتل فى الموكب.
عاش الذاكر إلى حدود أربعين وأربعمائة، ومات بمصر فى زمن المستنصر.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٠ - ٧١.','2','8',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (413,'<span data-type="title" id=toc-315>(حرف الراء)</span>
<span data-type="title" id=toc-316>٢٥٣ - ربيعة البصرىّ [١]</span>
بدوىّ تحضّر. كان قيّما باللغة، فصيحا شاعرا مصنّفا، راوية للأخبار.
صنّف كتاب ما قيل فى الحيّات «١» من الشعر والرّجز. كتاب حنين الإبل إلى الأوطان.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧١، والفهرست ٥٠.','2','9',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (414,'<span data-type="title" id=toc-317>(حرف الزاى)</span>
<span data-type="title" id=toc-318>٢٥٤ - زيد بن الحسن بن زيد بن الحسين بن سعيد بن عصمة ابن حمير بن الحارث بن ذى رعين الأصغر، التاج أبو اليمن الكندى [١]</span>
البغداذىّ مولدا ومنشأ، الدمشقى دارا ووفاة. شيخ فاضل، حفظ القرآن الكريم فى صغره، وقرأ بالقراءات الكثيرة وله عشر سنين «١» على جماعة؛ منهم الشيخ أبو محمد عبد الله بن على بن أحمد، سبط أبى منصور الخياط. وروى عن عالم من المشايخ «٢»، وله مشيخة كبيرة على حروف المعجم. «٣»
وقرأ النحو على الشريف أبى السعادات بن الشّجرىّ وأبى محمد عبد الله بن الخشاب، واللغة على أبى منصور بن الجواليقىّ.
_________
[١] ترجمته فى إشارة التعيين الورقة ١٩، وبغية الوعاة ٢٤٩ - ٢٥٠، وتاريخ ابن الأثير ٩: ٣١٢، وتاريخ أبى الفدا ٣: ١١٧، وتاريخ ابن كثير ١٣: ٧١ - ٧٤، وتلخيص ابن مكتوم ٧١ - ٧٢، وابن خلكان ١: ١٩٦ - ١٩٧، وخريدة القصر ١: ١٠٠ - ١٠١، والذيل على الروضتين ٩٥ - ٩٨، وروضات الجنات ٣٠٠ - ٣٠١، وشذرات الذهب ٥: ٥٤ - ٥٥، وطبقات ابن قاضى شهبة ١: ٣٤١ - ٣٤٦، وطبقات القراء ١: ٢٩٧ - ٢٩٨، وكشف الظنون ١٠٧٠، ١٩٢٥، ومرآة الجنان ٤: ٢٥ - ٢٧، ومعجم الأدباء ١١: ١٧١ - ١٧٥، والنجوم الزاهرة ٦: ٢١٦ - ٢١٧.','2','10',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (415,'وسافر عن بغداد فى شبابه. وآخر ما كان بها فى سنة ثلاث وستين وخمسمائة، ودخل حلب، واستوطنها مدّة، وصحب بها بدر الدين حسن بن الداية النووىّ واليها، وكان يبتاع الخليع «١» من الملبوس، ويسافر به إلى بلد الروم، ويعود إلى حلب. ثم انتقل إلى دمشق، وصحب الأمير عز الدين فرّخشاه بن شاهنشاه بن أيوب، «٢» ابن أخى الملك الناصر صلاح الدين يوسف بن أيوب، وتقدّم عنده، واختص به، وسافر فى صحبته إلى الديار المصرية، واقتنى من كتب خزائنها- عند ما أبيعت فى الأيام الناصرية- كلّ نفيس، على قلّة ما ابتاعه.
وعاد إلى دمشق واستوطنها، «٣» وقصده الناس، ورووا عنه. وكان ليّنا فى الرواية، معجبا بنفسه فيما يذكره ويرويه ويقوله، وإذا نوظر جبّه بالقبيح، واستطال بغير الحقيقة. ولم يكن موفّق القلم فيما يسطّره «٤»، وقد رأيت له أشياء قد ذكرها لا تخلو من برد فى القول وفساد فى المعنى واستعجال فيما يخبر به.
ولقد أخبرنى بعض أهل الأدب من أهل حلب قال: حضرت عنده، وجرت مسألة، فقال فيها الخطأ، فقلت: قد قال فيها ابن جنّى كذا، فقال: ما قال بهذا','2','11',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (416,'أحد. فطلبت منه سرّ الصناعة لابن جنى، فأحضرها، وأخرجت منها الكلمة على ما قلت، فوقف عليها وتأمّلها، وكان جوابه: قد كنت أظن أنّ ابن جنى محقق إلى الآن! ولم يقم على تخطئته دليلا. واشتهر عنه أنه لم يكن صحيح العقيدة. والله أعلم.
كتب إلىّ بالإجازة غير مرة- عفا الله عنه- وذكر أن مولده فى سنة عشرين وخمسمائة، فى العشرين من شعبانها. وتوفى بدمشق ضحوة يوم الاثنين السادس من شوّال من سنة ثلاث عشرة وستمائة، وصلى عليه بعد صلاة العصر من هذا اليوم بجامعها، ودفن عشيته بجبل قاسيون، «١» عن ثلاث وتسعين سنة وستة عشر يوما «٢».
أنبأنا محمد بن محمد بن حامد فى كتابه «٣» - وذكر الكندىّ- فقال: «عالم شاعر نحوى عروضىّ متفنّن، متقن للأدب محسن، خبير بالنقد والتّزييف، مدقّق فى التقوية والتضعيف، ولم يزل متقربا عند الملوك، متاجرا فى سوق الفضل','2','12',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (417,'من غرره بالتبر المسبوك «١» والوشى «٢» المحوك؛ ما يكاد يسلم ذو أدب من محاككته ومحاققته، ومضايقته فى الطرق الخفية ومدافعته».
وأنشد له أشعارا منها «٣»:
هذه مبتدا الرسا ... ئل بل أوّل الخدم
ليس إلا التزام ما ... كان مولاى قد رسم
أيها العالم الذى ... شيّد المجد والكرم
والذى فضله أقا ... م مديحى على قدم
قد رزينا وصالكم ... والرزايا لها قيم
فلهذا دموعنا ... بعدكم فيضهنّ دم
وكان بحلب قبل مسيره إلى مصر متخصّصا بالأمير بدر الدين حسن، أخى مجد الدين بن الداية، ثم كتب إليه بعد مفارقته، يعرب عن معاتبته:
بنفسى من أعلقت كفى بحبله ... فأصبح لى فى ذروة المجد غارب «٤»
وجدت به مولى مريعا «٥» جنابه ... منيعا ترجّى من يديه المواهب
تعمّد إيناسى إلى أن لقيته ... كأنّى له من ضجعة المهد صاحب
وأدنى سرارى من سرائر «٦» قلبه ... فلم يبق من دون الضميرين حاجب','2','13',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (418,'وكان عصا موسى لدىّ وداده ... أظلّ ولى ما عشت فيه مآرب
فصار يرى بالظنّ فىّ معايبا ... توهّمها فى ودّ مثلى معايب
ولا عجب إن غيّر الدهر صاحبا ... فكلّ تصاريف الزمان عجائب
رمانى بأمر لا أبوح بذكره ... وأقبل بالإعراض عنى يعاقب
وأظهر لى حسن اللقاء تكلّفا ... ومن تحت إحسان اللقاء عقارب
وإنى على عتبى عليه لشيّق ... وإنى على شوقى إليه لعاتب
ولا ذنب منّى غير أنى ذخرته ... لدهرى ألا إنى إلى الدهر تائب
سيعلم والأيام فيها كفاية ... إذا ملت عنه قدر من هو ذاهب
وإن هو بعدى جرب الناس كلّهم ... ليحظى بمثلى ندّمته التجارب «١»

<span data-type="title" id=toc-319>٢٥٥ - زيد بن القاسم بن أسعد العامرىّ النيسابورىّ أبو الحسن الأديب [١]</span>
لا يشقّ فى اللغة غباره، ولا تحلق فى الاداب آثاره، وهو وأبوه وأبو العباس عمه، كلّهم أدباء نجباء فضلاء، متصدّرون فى الأدب، وإفادة علم العرب.
ولزيد شعر؛ منه فى الهجاء؛ وهو ما أنشده القاضى أبو جعفر البحّاثىّ «٢»:
الله أغنانى بعزّ جلاله ... عن جعفر والمبتغى من ماله
لا يعجبنّك قدّه وجماله ... فمناكر «٣» الأدباء تحت جماله
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٢.','2','14',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (419,'لا تنظرنّ إلى أبيه وجدّه ... وانظر إلى المذموم من أفعاله
وانظر إلى محبوبه وقرينه ... لترى خساسته وفرط سفاله
يا لائمى فى بغضه وهجائه ... أقصر فلم تعرف حقيقة حاله

<span data-type="title" id=toc-320>٢٥٦ - زيد بن سليمان الحجرىّ النحوىّ الأندلسىّ أبو الربيع المعروف بالبارد [١]</span>
كان عالما بالعربية واللغة؛ حسن الضبط للكتب؛ متقنا لها، وهو الذى جمع بين الأبواب فى كتاب الأخفش، واقتدى الناس به، وكانت الأبواب مفرّقة.
وتوفى سنة [ثلاثمائة] «١».

<span data-type="title" id=toc-321>٢٥٧ - زيد بن عطية الصّعدىّ اليمنىّ اللغوىّ [٢]</span>
من أهل صنعاء «٢»، ونسبه فى الربيعة من خولان، ومولده بناحية صعدة «٣».
وكان لغويا شاعرا منجّما حاسبا هندسيا، يسلّم إليه المنجمون هناك فى ديار صنعاء وصعدة النجوم والحساب. وله تصانيف فى ذلك؛ منها زيجان كبير وصغير، وأحكام نجومية، وفصول.
_________
[١] ترجمته فى بغية الوعاة ٢٥٠، وتكملة الصلة ١: ٧٣ - ٧٤، وتلخيص ابن مكتوم ٧٢، وطبقات الزبيدىّ ١٩٥.
[٢] ترجمته فى تلخيص ابن مكتوم ٧٢.','2','15',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (420,'وله شعر جيد متداول بين أدباء اليمن. مدح الأمير فاتك بن جياش بن نجاح «١» بزبيد «٢»، بقصيدة أوّلها:
لما رأت وضحا فى الرأس أفوافا ... ظلّت شموسا كأنّ الموت قد وافى «٣»
ما أنكرت من نجوم الليل طالعة ... يضمّها منه أوساطا وأطرافا
تجردت فى شواتى «٤» وهى طالعة ... كما سللت من الأغماد أسيافا
ولست خالع ثوب اللهو ما بقيت ... إن أنصف الشيب فى فودىّ أو حافا «٥»
منها فى الخروج إلى المدح:
ثم ادّلجنا على حدب معطّفة ... شوازب كقسىّ النّبع إخطافا «٦»
تطوى بنا البيد أجزاعا وصفصفة ... مع الحزونة أسفاحا وأشعافا «٧»
حتى انتهينا على كدّ إلى ملك ... يقرى الضيوف ويعطى المال إسرافا','2','16',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (421,'<span data-type="title" id=toc-322>٢٥٨ - زيد بن علىّ النحوىّ الفارسىّ أبو القاسم [١]</span>
ابن أخت [أبى] على الفارسىّ النحوىّ. نحوىّ كامل فاضل، أخذ النحو عن خاله، وروى عنه كتاب الإيضاح من تصنيفه.
وخرج عن فارس إلى العراق، وقصد الشام، واستوطن حلب لإقراء النحو بها؛ فقرءوا عليه، واستفاد أهلها منه، وعمّر إلى أن قرأ عليه الشريف أبو البركات عمر بن إبراهيم بن محمد بن محمد الزيدىّ الكوفى النحوىّ كتاب الإيضاح بحلب عند رحلته إليها من الكوفة فى شهر رجب سنة خمس وخمسين وأربعمائة. وروى الناس كتاب الإيضاح عن هذا الشريف عن أبى القاسم المذكور المدّة الطويلة بالكوفة.
قال أبو القاسم علىّ الدمشقىّ «١» فى كتابه «٢»: «زيد بن علىّ بن عبد الله أبو القاسم الفسوىّ الفارسىّ النحوىّ اللغوىّ. سكن دمشق مدّة، وأقرأ بها النحو واللغة، وأملى بها شرح الإيضاح لأبى على الفارسىّ، وشرح الحماسة. وحدّث عن الشيخ أبى الحسن بن أبى الحديد الدمشقىّ، وسمع منه جدّى القاضى أبو الفضل عمر بن أبى الحسن الدّهستانىّ «٣» وأبو الحسن على بن طاهر النحوىّ».
«توفى بطرابلس فى ذى الحجة سنة سبع وتسعين وأربعمائة. قاله لنا ابن الأكفانىّ».
قلت: فى هذا القول نظر؛ فإنه يكون قد مات قبل ذلك.
_________
[١] ترجمته فى بغية الوعاة ٢٥٠ - ٢٥١، وتلخيص ابن مكتوم ٧٢ - ٧٣، وروضات الجنات ٣٠٠، ومختصر ابن عساكر ٦: ٢٥، ومعجم الأدباء ١١: ١٧٦ - ١٧٧.
.','2','17',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (422,'<span data-type="title" id=toc-323>٢٥٩ - زيادة الله بن علىّ بن حسين التميمىّ الطّنبىّ [١]</span>
نزيل قرطبة. يكنى أبا مضر. كان من أهل العلم بالآداب واللغات والأشعار.
روى الناس عنه علما كثيرا، وكان كثير الإغراب.
كان مولده فى شعبان من سنة ست وثلاثين وثلاثمائة، وتوفى- رحمه الله- لعشر خلون من ربيع الأوّل سنة خمس عشرة وأربعمائة.

<span data-type="title" id=toc-324>٢٦٠ - زنجى بن المثنّى النحوىّ القيروانىّ [٢]</span>
كان مؤدّبا لكثير من رجال السلطان فى تلك الناحية، عالما بالعربية واللغة.

<span data-type="title" id=toc-325>٢٦١ - زهير بن ميمون الفرقبىّ النحوىّ الكوفىّ [٣]</span>
من علماء الكوفة. نحوىّ قارئ، همدانىّ؛ وإنما قيل له الفرقبىّ؛ لأنه كان يتّجر إلى ناحية فرقب، «١» فنسب إليها. وكان من أهل الكوفة، وكان مولى للنّخع.
وقيل لغيرهم.
وقال أبو بكر بن عياش: قلت لزهير الفرقبىّ بمكة: أنّى لك النحو؟ قال:
سمعناه من أصحاب أبى الأسود فأخذناه. قال: ومات زهير سنة خمس وخمسين ومائة. وقيل: سنة ست وخمسين ومائة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٣، والصلة لابن بشكوال ١: ١٩٢. والطبنى، بضم الطاء وسكون الباء: منسوب إلى طبنة، وهى بلد فى طرف إفريقية فتحها موسى بن نصير.
[٢] ترجمته فى بغية الوعاة ٢٤٩، وتلخيص ابن مكتوم ٧٣، وطبقات الزبيدى ١٦٦.
[٣] ترجمته فى تلخيص ابن مكتوم ٧٣، وطبقات القرّاء ١: ٢٩٥.','2','18',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (423,'وقال الهيثم بن عدىّ: رأيت زهيرا الفرقبىّ، وقد اجتمع عليه ناس يسألونه عن القراءات والعربية، وهو يجيبهم ويحتجّ على ما يقول بأشعار العرب. وكان يروى كثيرا من ذلك عن ميمون الأقرن. وكان أبو جعفر الرؤاسىّ يأخذ عنه، وكان عالما بالنّسب.
قال: ورأى النبى صلى الله عليه وسلم فى النوم وهو يقول: «يا زهير، عليك بالقرآن». فلم يكن بعد ذلك يتكلم فى غيره.','2','19',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (424,'<span data-type="title" id=toc-326>(حرف السين)</span>
<span data-type="title" id=toc-327>٢٦٢ - سليمان بن معبد أبو داود النحوىّ السّنجىّ المروزىّ [١]</span>
سمع النّضر بن شميل، والنّضر بن محمد الجرشىّ، «١» وسيار بن حاتم «٢»، والهيثم «٣» بن عدىّ، وعبد الرزاق «٤» بن همّام، والأصمعىّ، وغيرهم.
ورحل فى طلب العلم إلى العراق والحجاز ومصر واليمن، وذاكر الحفّاظ، مثل يحيى بن معين «٥»، وروى عنه مسلم بن «٦» الحجاج وأبو بكر بن أبى «٧» داود وأمثالهما. وكان ثقة. وكان له شعر، فمنه:
_________
[١] ترجمته فى الأنساب ٣١٣ ا، وبغية الوعاة ٢٦٣ - ٢٦٤، وتاريخ بغداد ٩: ٥١ - ٥٢، وتقريب التهذيب ١٠٢، وتلخيص ابن مكتوم ٧٣، وتهذيب التهذيب ٤: ٢١٩ - ٢٢٠، وخلاصة تذهيب الكمال ١٣١، وشذرات الذهب ٢: ١٣٦، وطبقات ابن قاضى شهبة ١: ٣٥٩ - ٣٦٠، واللباب ١: ٥٧٠، ومعجم الأدباء ١١: ٢٥٧ - ٢٥٨، ومعجم البلدان ٥: ١٤٧، والمنتظم (وفيات ٢٥٧)، والنجوم الزاهرة ٣: ٢٧، والوافى بالوفيات ج ٥ مجلد ١: ١٣٧.
والسنجىّ، بكسر السين وسكون النون وجيم: منسوب إلى سنج؛ وهى من قرى مرو.','2','20',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (425,'يا آمر الناس بالمعروف مجتهدا ... وإن رأى عاملا بالمنكر
انتهره ابدأ بنفسك قبل الناس كلّهم ... فأوصها واتل ما فى سورة البقرة
أتأمرون ببرّ تاركين له ... ناسين، ذلك دأب الخيّب الخسره
وإن أمرت ببرّ ثم كنت على ... خلافه لم تكن إلّا من الفجره
من كان بالعرف أمّارا وتاركه ... فذاك يسبق منه سيله مطره
قال أبو رجاء محمد بن حمدويه بن موسى: سليمان بن معبد من أهل السّنج.
جالس الأصمعىّ وجلّة الفقهاء. مات فى سنة سبع وخمسين ومائتين. زاد غيره فى ذى الحجة.

<span data-type="title" id=toc-328>٢٦٣ - سليمان بن محمد بن أحمد أبو موسى النحوىّ المعروف بالحامض [١]</span>
كان أحد المذكورين من العلماء بنحو الكوفيين. أخذ عن أبى العباس ثعلب،- وهو المقدّم من أصحابه- ومن خلفه بعد موته، وجلس مجلسه. وصنّف كتبا حسانا فى الأدب.
روى عنه أبو عمر الزاهد وأبو جعفر الأصبهانىّ المعروف ببزرويه «١»، غلام نفطويه. وكان ديّنا صالحا.
_________
[١] ترجمته فى الأنساب ١٥٢ ا، وبغية الوعاة ٢٦٢ - ٢٦٣، وتاريخ بغداد ٩: ٦١، وتلخيص ابن مكتوم ٧٣ - ٧٤، وابن خلكان ١: ٢١٤ - ٢١٥، وطبقات الزبيدىّ ١١٠ - ١١١، وطبقات ابن قاضى شهبة ١: ٣٥٨ - ٣٥٩، والفهرست ٧٩، وكشف الظنون ٧٢٣، ١٤٦٩، واللباب ١: ٢٧١، ومسالك الأبصار ج ٤ مجلد ٢: ٢٩٢، ومعجم الأدباء ١١: ٢٥٣ - ٢٥٥، والمنتظم (وفيات ٣٠٥)، والنجوم الزاهرة ٣: ١٩٣، ونزهة الألباء ٣٠٦ - ٣٠٧. قال ابن خلكان: «وإنما قيل له الحامض لأنه كانت له أخلاق شرسة، فلقب الحامض لذلك. ولما احتضر أوصى بكتبه لأبى فاتك المقتدرىّ، بخلابها أن تصير إلى أحد من أهل العلم».','2','21',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (426,'قال أبو الحسن محمد بن جعفر بن محمد بن هارون التميمى «١»: «وأما أبو موسى الحامض فكان أوحد الناس فى البيان والمعرفة بالعربية واللغة والشعر».
«قال أبو المعالى النقار: دخل الكوفة أبو موسى، وسمعت منه كتاب الإدغام عن ثعلب عن سلمة عن الفرّاء. قال أبو علىّ: فقلت له: أراك تلخّص الجواب تلخيصا ليس فى الكتب. قال: هذا ثمرة صحبة ثعلب أربعين سنة».
توفى أبو موسى الحامض ليلة الخميس لسبع بقين من ذى الحجة سنة خمس وثلاثمائة، وكان قد أخذ عن البصريين، وخلط النحوين، وكان حسن الوراقة فى الضّبط، وكان يتعصّب على البصريين فيما أخذ عليهم فى عربيتهم.
فمن تصنيفه: كتاب خلق الإنسان. كتاب النبات. كتاب الوحوش. كتاب مختصر النحو «٢».

<span data-type="title" id=toc-329>٢٦٤ - سليمان بن محمد بن سليمان أبو الربيع الخلّى اليمنىّ [١]</span>
من موضع باليمن؛ يعرف بالخلّة، قريب من بلد بنى مسلية، من مذحج. قرأ النحو باليمن، وانتقل فى مدنها فى حالة إقتار وغلاء، وخرج إلى مصر، وتوصّل إلى ملكها الكامل «٣»، وحضر مجلسه- وللكامل غرام بعلم النحو؛ يشتهى أن يخالط
_________
[١] ترجمته فى بغية الوعاة ٢٦٣، وتاريخ الإسلام للذهبى (وفيات ٦٥٠)، وتلخيص ابن مكتوم ٧٤، وطبقات ابن قاضى شهبة ١: ٣٥٨، ومعجم البلدان ٣: ٤٦٠.','2','22',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (427,'النحاة ليستفيد منهم، ويكره نحاة مصر- فقرّب هذا الغريب على بعد داره، وقرّر له معلوما هو بالنسبة إلى العدم قريب، فقنع به المذكور.
وسمعت أنه اجتاز بحلب فى شهور سنة سبع وعشرين وستمائة، واجتمع بنحاتها فلم يجدوا عنده شيئا يوجب التصدّر، ثم عاد إلى مصر عند عودة العساكر الكاملية عن البلاد الجزريّة «١» بعد أخذ آمد «٢» فى سنة ثلاثين وستمائة، وهو مقيم بمصر فى الصّحبة على حاله. «٣»

<span data-type="title" id=toc-330>٢٦٥ - سليمان بن سليمان بن حجاج بن عمير أبو أيوب [١]</span>
كان له حظ من معرفة النحو واللغة، من مشاهير الأندلسيين فى قطره. وله شعر «٤» مذكور متداول بينهم، يتناشدونه فى أنداء «٥» الأدب هناك. وله خطابة وبلاغة، وقال الشعر بعد أن أسنّ، فمن شعره فى ابن عم له:
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٤، وطبقات الزبيدىّ ٢٠٧ - ٢٠٨.','2','23',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (428,'قريب رحم بعيد مرحمة «١» ... ما نالنى من أذى فمنه وبه
أخذ علمه من الأدب عن أبى الغازى وغيره من العلماء. توفى سنة ثمان وثلاثين وثلاثمائة.

<span data-type="title" id=toc-331>٢٦٦ - سليمان بن أحمد بن محمد السّرقسطىّ أبو الربيع الأندلسىّ المقرئ اللغوىّ [١]</span>
رحل إلى المشرق، وروى عن جماعة من مشايخ بغداذ وغيرهم؛ كأبى بكر أحمد ابن على بن ثابت الخطيب وغيره. وروى عنه الناس، وأقرأ القرآن وأفاد اللغة.
وتكلموا فيه.
أخبرنا عبد الكريم بن محمد المروزىّ فى كتابه سماعا عليه ببلخ، أخبرنا أبو العباس أحمد بن محمد بن محمد بن الإخوة العطّار بقراءتى عليه فى داره، أخبرنا أبو الربيع سلمان ابن أحمد بن محمد السّرقسطىّ قراءة عليه وأنا أسمع، أخبرنا أبو بكر «٢» أحمد بن على ابن ثابت الحافظ، أخبرنا عبد الرحمن بن محمد بن فضالة الحافظ، أخبرنا الحسين ابن جعفر بن محمد، حدّثنا أحمد بن أبى طلحة، حدّثنا أحمد بن على السيارىّ،
_________
[١] ترجمته فى الأنساب ٢٩٦ ب، وتلخيص ابن مكتوم ٧٤، ولسان الميزان ٣: ٧٥ - ٧٦، وميزان الاعتدال ١: ٣٦٦، والمنتظم (وفيات سنة ٤٨٩)، والوافى بالوفيات ج ٥ مجلد ١: ٤٩، والسرقسطى، بفتح السين والراء وضم القاف وسكون السين الثانية: منسوب إلى سرقسطة، وهى مدينة على ساحل البحر من بلاد الأندلس.','2','24',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (429,'حدّثنا نصر بن على الجهضمىّ قال: كان فى جيرانى رجل طفيلىّ. وكنت إذا دعيت إلى مدعاة ركب لركوبى، فإذا دخلنا الموضع أكرم من أجلى. فاتخذ جعفر ابن سليمان أمير البصرة دعوة، فدعيت إليها، وقلت فى نفسى: والله إن جاء هذا الرجل معى لأخزينّه. فلما أن ركبت ركب لركوبى، ثم دخلت الدار، فدخل معى، وأكرم من أجلى. فلما حضرت المائدة قلت: حدّثنا درست بن زياد عن أبان بن طارق عن نافع عن ابن عمر- رضى الله عنه- عن النبى صلى الله عليه وسلم قال: «من مشى إلى طعام لم يدع إليه مشى فاسقا وأكل حراما» قال:
فقال الطّفيلىّ: استحييت لك يا أبا عمرو، مثلك يتكلّم بهذا الكلام على مائدة الأمير! فليس هاهنا أحد إلا يظنّ أنك رميته بهذا الكلام ثم إنك لا تستحى، تتحدّث عن درست «١» بن زياد، ودرست كذاب لا يحتج بحديثه، عن أبان بن طارق، «٢» وأبان كان صبيا من صبيان أهل «٣» المدينة يلعبون. ولكن أين أنت عما حدّثنا أبو عاصم النبيل عن ابن جريج «٤» عن أبى الزبير «٥» عن جابر «٦» - رضى الله عنه- عن النبى','2','25',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (430,'صلى الله عليه وسلم قال: «طعام الواحد يكفى الاثنين، وطعام الاثنين يكفى الأربعة»!.
قال نصر بن على: فكأنى ألقمت حجرا. فلما خرجنا من الدار أنشأ الطّفيلىّ يقول:
ومن ظنّ ممّن يلاقى الحروب ... بألّا يصاب فقد ظنّ عجزا
توفى أبو الربيع السّرقسطىّ الأندلسىّ فى يوم الجمعة تاسع شهر ربيع الآخر سنة تسع وثمانين وأربعمائة ببغداد، ودفن من يومه.

<span data-type="title" id=toc-332>٢٦٧ - سليمان بن أبى طالب عبد الله بن الفتى الحلوانىّ النهروانىّ أبو عبد الله [١]</span>
والد الحسن «١» بن سليمان الفقيه المدرس بالنظامية «٢». كان له حظ وافر من العربية، ومعرفة تامّة باللغة والأدب.
_________
[١] ترجمته فى الإكمال لابن ماكولا ١: ٢١٨ ب، وبغية الوعاة ٢٦٠، وتلخيص ابن مكتوم ٧٥، ودمية القصر ٨٧ - ٨٨، وشذرات الذهب ٣: ٣٩٩، وطبقات المفسرين للداودى ٨٢ ا- ٨٢ ب، وطبقات المفسرين للسيوطى ١٣، وطبقات ابن قاضى شهبة ١: ٣٥٥ - ٣٥٦، وعيون التواريخ (وفيات سنة ٤٩٤)، وكشف الظنون ١٣١٣، ومرآة الجنان ٣: ١٥٦، ومعجم الأدباء ١١: ٣٥١ - ٣٥٣، والوافى بالوفيات ج ٤ مجلد ٢: ٣٦٩ - ٣٧٠.','2','26',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (431,'نزل أصبهان وسكنها. وأكثر أئمة أصبهان وفضلائها قرءوا عليه الأدب.
ذكره يحيى بن منده «١» فى تاريخ أصبهان، فقال:
«سليمان بن عبد الله بن الفتى، البغداذىّ. قدم أصبهان، واستوطن بها.
وكان جميل الطريقة، فاضلا أديبا، حسن الخلق، إماما فى اللغة والنحو. صنف كتاب التفسير «٢». مسكنه فى باب الوزير قريب من الجامع».
وذكره الأمير ابن ماكولا «٣» فقال:
«وأما الفتى «٤»، أوّله فاء مفتوحة بعدها تاء معجمة باثنين من فوقها، فهو أبو عبد الله سليمان «٥» بن عبد الله. يعرف بابن الفتى. من أهل النّهروان؛ دخل بغداذ بعد سنة ثلاثين وأربعمائة، وتشاغل بالأدب، وقرأ على أبى الخطاب الجبلىّ والثمانينى وغيرهما من أدباء ذلك الوقت. وحضر عندى وتأدّب، وقال الشعر،','2','27',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (432,'وسافر إلى الجبل، وشاهدته بالرّىّ دفعات وبهمذان، ووجدته فاضلا مليح الشعر حسن الأدب حافظا».
وذكره الباخرزىّ «١» فقال: «عاشرته «٢» بالنّهروان سنة ثلاث وستين، ووجدته لطيف العشرة، وفتّشته عما يتحلّى به من علم الإعراب، فمدّ «٣» فيه أطناب «٤» الإطناب، حتى كاد يكون مكانه من المبرّد والزّجّاج، مكان الأسنّة من الزّجاج «٥». وهو مع هذا أشعر أبناء جنسه. فممّا أنشدنى لنفسه «٦» من قصيدة نظامية:
يا ظبية حلّت بباب الطاق «٧» ... بينى وبينك أوكد الميثاق
فوحقّ أيام الصّبى ووصالنا ... قسما بها وبنعمة الخلّاق
ما مرّ من يوم ولا من ليلة ... إلا إليك تجدّدت أشواقى
سقيا لأيام جنى لى طيبها ... ورد الخدود ونرجس الأحداق
وإذا أضرّت بى عقارب صدغها «٨» ... كانت مراشف ريقها ترياقى','2','28',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (433,'<span data-type="title" id=toc-333>٢٦٨ - سليمان بن حبّون النحوىّ الشاعر [١]</span>
من أهل الرّحبة «١». نزل حرّان «٢»، وقطن بها، وتصدّر لإفادة هذا الشأن. وكان مستوحشا من الناس، منقطعا عنهم، يقول شعرا مصنوعا قريب الحال، يقصد به الاستعطاء.
رأيته بحلب فى سنة ثمان وتسعين وخمسمائة، وقد حضر إليها من حرّان ليمدح الملك الظاهر غازى بن الملك الناصر «٣» صلاح الدين- سقى الله عهده- ونزل عند رجل من أهلها يعرف بابن خرخاز، ورأيته ينسخ فى كتاب القوافى للزجّاج، وذاكرته فى النحو، فرأيته نزقا «٤» لا يرغب أن يباحث فيه. وسألته: من لقيت من المشايخ؟ فقال: اصطحبت أنا والمهذّب بن العطّار فى الكلك «٥» إلى بغداذ؛ لم يزدنى على ذلك.
وتوجه بعد أن أخذ الجائزة عن مدحه إلى حرّان. ومات قريبا من ذلك فيما بلغنى، وخلّف بها بنات. وكان ظاهر أمره الإقلال. وسمعت أنه امتدح العادل
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٥.','2','29',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (434,'أبا بكر «١» بن أيوب عند ملكه حرّان، فلم يجزه شيئا، فذكر أبياتا عرّض له فيها بأنك جواد ما زلت؛ ولكنّ أرضنا غيّرتك، فقال: هجانا هذا الرجل بطريق لطيف. وبيت القصيدة:
قسما بآل محمد ... ما فوق ذلك من قسم
إن المليك محمدا ... لولاه ما عرف الكرم
يعطى اليراع براعة ... كالسيف «٢» يخضبه بدم
لكنّ تربة أرضنا ... نقلته عن تلك الشّيم

<span data-type="title" id=toc-334>٢٦٩ - سعيد بن أوس بن ثابت أبو زيد الأنصارىّ [١]</span>
صاحب النحو واللغة. حدّث عن عمرو بن عبيد وأبى عمرو بن العلاء.
روى عنه أبو عبيد القاسم بن سلّام، ومحمد بن سعد الكاتب، وأبو حاتم
_________
[١] ترجمته فى إشارة التعيين الورقة ١٩ - ٢٠، وأخبار النحويين البصريين ٥٢ - ٥٧، وبغية الوعاة ٢٥٤ - ٢٥٥، وتاريخ الإسلام للذهبى (وفيات سنة ٢١٥)، وتاريخ بغداد ٩: ٧٧ - ٨٠، وتاريخ أبى الفدا ٢: ٣٠، وتاريخ ابن كثير ١٠: ٢٦٩ - ٢٧٠، وتلخيص ابن مكتوم ٧٦، وتقريب التهذيب ٩٠، وتهذيب التهذيب ٤: ٣ - ٥، وتهذيب اللغة للأزهرى ١: ٥ - ٦، وخلاصة تذهيب الكمال ١١٥، وابن خلكان ١: ٢٠٧ - ٢٠٨، وشذرات الذهب ٢: ٣٤ - ٣٥، وطبقات الزبيدى ١١٦ - ١١٧، وطبقات ابن قاضى شهبة ١: ٣٤٩ - ٣٥١، وطبقات القراء لابن الجزرى ١: ٣٠٥، وطبقات المفسرين للداودى ٧٦ ب- ٧٧ ا، وعيون التواريخ (وفيات سنة ٢١٥)، والفهرست ٥٤ - ٥٥، وكشف الظنون ١٣٨٣، ١٤٠٩، ١٤٤٧، ١٤٥٤، ١٤٥٩، ١٤٦٥، ١٤٦٦، ١٤٧٢، ومراتب النحويين ٦٧ - ٧٠، ومرآة الجنان ٢: ٥٨ - ٥٩، والمزهر ٢:
٤٠٢، ٤١٩، ٤٦١، ومسالك الأبصار ج ٤ مجلد ٢: ٢٢٤ - ٢٢٥، والمعارف ٢٣٧، ومعجم الأدباء ١١: ٢١٢ - ٢١٧، والنجوم الزاهرة ٢: ٢١٠، ونزهة الألباء ١٧٣ - ١٧٩.','2','30',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (435,'السّجستانىّ وأبو زيد عمر بن شبة، وأبو حاتم الرازىّ. وكان ثقة ثبتا «١» من أهل البصرة.
قال ابن القدّاح «٢»: أبو زيد النحوى، سعيد بن أوس بن ثابت بن زيد ابن قيس بن زيد بن النعمان «٣» بن مالك بن ثعلبة بن كعب بن الخزرج. وشهد ثابت ابن زيد أحدا والمشاهد بعدها. وهو أحد العشرة الذين بعث عمر بن الخطاب- رضى الله عنه- مع أبى موسى الأشعرى إلى البصرة، وأحد الستة الذين جمعوا القرآن على عهد النبى صلى الله عليه وسلم «٤».
هكذا نسب أبى زيد فى هذه الرواية. وفيه إخلال؛ والصواب ما ذكره محمد ابن سعد «٥»، قال: «[أخبرنا «٦»] أبو زيد النحوى، واسمه سعيد بن أوس بن ثابت ابن بشير بن أبى زيد [قال «٧»]: ثابت «٨» بن زيد بن قيس [هو جدّى؛ وقد شهد أحدا «٩»]».','2','31',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (436,'قال أبو عثمان المازنىّ: كنا عند أبى زيد، فجاء الأصمعىّ، فأكبّ على رأسه وجلس، وقال: هذا عالمنا ومعلّمنا منذ عشر سنين «١».
قال أبو زيد الأنصارىّ: وقفت على قصّاب وعنده بطون، فقلت: «بكم البطنان يا غلام؟» قال: «بدرهمان يا ثقيلا».
وقال أبو زيد: وقفت بباب سليمان بن أبى العاص الثقفىّ على قصّاب، وقد أخرج بطنين سمينين موفورين، فعلّقهما، فقلت: بكم البطنان؟ فقال: بمصفعان يا مضرطان. قال: فغطيت رأسى وفررت؛ لئلا يسمع الناس فيضحكوا منى.
قال أبو زيد الأنصارىّ: كنا ببغداذ، فأردت الانحدار إلى البصرة، فقلت لابن أخى: اكترلنا. فجعل ينادى: «يا معشر الملاحون»؛ فقلت له: ويلك! ما تقول؟ قال: جعلت فداك! أنا مولع بالرفع «٢».
وقال روح بن عبادة: كنا عند شعبة «٣»، فضجر من الحديث، فرمى بطرفه، فرأى سعيد بن أوس فى أخريات الناس، فقال: يا أبا زيد:
استعجمت «٤» دارمىّ ما تكلّمنا ... والدّار لو كلّمتنا ذات أخبار','2','32',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (437,'إلىّ أبا زيد. فجاءه، فجعلا يتناشدان الأشعار، فقال بعض أصحاب الحديث:
يا أبا بسطام، نقطع إليك ظهور الإبل لنسمع منك حديث رسول الله- صلّى الله عليه وسلّم- فتدعنا وتقبل على الأشعار! قال: فرأيت شعبة قد غضب غضبا عليه وسلّم- فتدعنا وتقبل على الأشعار! قال: فرأيت شعبة قد غضب غضبا شديدا؛ ثم قال: يا هؤلاء، أنا أعلم بالأصلح لى. أنا والله الذى لا إله إلا هو فى هذا أسلم منى فى ذاك! قال أبو زيد: لقيت أبا حنيفة، فحدّثنى بحديث فيه: «يدخل الجنة قوم حفاة عراة منتنين قد أحمشتهم النار»، فقلت له: «منتنون قد محشتهم «١» النار».
فقال: ممّن «٢» أنت؟ قلت: من أهل البصرة، قال: كل أصحابك مثلك؟ قلت:
أنا أخسّهم حظّا فى العلم، فقال: طوبى لقوم تكون أخسّهم! وسرق أصحاب الحديث نعل أبى زيد، فكان إذا جاء أصحاب الشعر والعربية والأخبار رمى بثيابه ولم يتفقدها، وإذا جاء أصحاب الحديث جمعها كلها، وجعلها بين يديه وقال: ضمّ يا ضمّام، واحذر لا تنام.
مات أبو زيد الأنصارى سنة أربع عشرة ومائتين. وقيل سنة خمس عشرة ومائتين، وله ثلاث وتسعون سنة، بالبصرة.
وكان أبو زيد من أهل العدل والتشيّع، وكان ثقة، وكان عالما بالنحو، ولم يكن مثل سيبويه والخليل. وكان يونس أعلم منه بالنحو، وكان مثله فى اللغات. وكان أبو زيد أعلم من الأصمعى وأبى عبيدة بالنحو. وكان يقال [له]:
أبو زيد النحوىّ. وله كتاب فى تخفيف الهمز على مذهب النحويين. وفى كتبه المصنفة فى اللغة وشواهد النحو عن العرب ما ليس لغيره.','2','33',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (438,'وكان كثير السماع من العرب. وقال أبو زيد: سألنى الحكم بن قنبر عن «تعاهدت ضيعتى»، فقلت: «تعهّدت»، فقال: لا- وكان عنده ستة من الأعراب الفصحاء- فقلت: اسألهم. فسألهم «١». فكلّ قال: «تعهّدت». فقال:
يا أبا زيد، علم كنت سببه، أو كلاما نحو هذا.
ولم يأخذ أحد من علماء البصريين عن الكوفيين إلا أبا زيد، فإنه روى عن المفضّل فى أوّل كتابه «النوادر» «٢»، قال: أنشدنى المفضّل لضمرة بن ضمرة:
بكرت «٣» تلومك بعد وهن فى النّدى ... بسل عليك ملامتى وعتابى «٤»
وكان أبو زيد يلقّب أصحابه، فلقّب الجرمىّ بالكلب لجدله واحمرار عينيه، ولقب المازنى «تدرج» «٥» لمشيته، ولقب أبا حاتم برأس البغل «٦»، ولقب التوّزىّ','2','34',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (439,'أبا الوذواذ لخفة حركته وذكائه، ولقّب الزيادىّ طارقا، لأنه كان يأتيه بليل.
وكان هؤلاء أخذوا عن أبى زيد.
قال أبو زيد: أتيت بغداذ حين قام المهدى، فوافاه العلماء من كل بلدة بأنواع العلوم، فلم أر رجلا أفرس ببيت شعر من خلف، ولا عالما أبذل لعلمه من يونس.
وتوفى أبو زيد فيما قاله محمد بن إسحاق النديم سنة خمس عشرة ومائتين.
وقال: ««١» وله من الكتب المصنفة كتاب إيمان عثمان. كتاب حيلة ومحالة.
كتاب القوس «٢» والتّرس. كتاب مسائية «٣». كتاب المعزى. كتاب الإبل «٤». كتاب خلق الإنسان. كتاب الأبيات. كتاب المطر.
كتاب المياه. كتاب الغرائز. كتاب النبات والشجر. كتاب اللغات. كتاب قراءة أبى عمرو. كتاب النوادر. كتاب الجمع والتثنية. كتاب اللبن. كتاب بيوتات العرب. كتاب تخفيف الهمز. كتاب حياة «٥». كتاب المقتضب «٦». كتاب الوحوش.
كتاب الفرق. كتاب فعلت وأفعلت. كتاب غريب الأسماء.
كتاب الهمز. كتاب المصادر «٧». كتاب الحلبة. كتاب نابه ونبيه.
كتاب معانى القرآن. كتاب النحو الكبير. كتاب الصفات «٨»».','2','35',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (440,'<span data-type="title" id=toc-335>٢٧٠ - أخبار أبى الحسن سعيد بن مسعدة الأخفش الأوسط [١]</span>
هو أبو الحسن سعيد بن مسعدة المجاشعىّ، مولى مجاشع «١». أخذ النحو عن سيبويه- وكان أكبر منه- وصحب الخليل أولا، وكان معلّما لولد الكسائىّ.
وسبب ذلك أنه لما جرى بين الكسائىّ وسيبويه ما جرى من المناظرة «٢» رحل سيبويه إلى الأهواز. «٣»
_________
[١] ترجمته فى أخبار النحويين البصريين للسيرافى ٥٠ - ٥١، وإشارة التعيين الورقة ٢٠، وبغية الوعاة ٢٥٨، وتاريخ أبى الفدا ٢: ٢٩، وتلخيص ابن مكتوم ٧٧، وابن خلكان ١: ٢٠٨، وشذرات الذهب ٢: ٣٦، وطبقات الزبيدىّ ٤٥ - ٤٦، وطبقات ابن قاضى شهبة ١: ٣٥٥، وعيون التواريخ (وفيات سنة ٢١٠)، والفهرست ٥٢، وكشف الظنون ٢٠١، ١٣٩١، ١٤٣٨، ١٤٥١، ١٤٦٣، ١٦٧٠، ١٧٢٩، ١٧٣٠، ومراتب النحويين ١٠٩، ومرآة الجنان ٢: ٦١، والمزهر ٢: ٤٠٥، ٤١٩، ٤٤٤، ٤٦٣، ومسالك الأبصار ج ٤ مجلد ٢: ٢٨٣ - ٢٨٤، والمعارف ٢٢٧ - ٢٢٨، ومعجم الأدباء ١١: ٢٢٤ - ٢٣٠، ونزهة الألباء ١٨٤ - ١٨٨.
والأخفش فى اللغة: الصغير العينين مع سوء بصرهما. قال السيوطى فى بغية الوعاة ص ٤٣٦:
«الأخفش أحد عشر: أشهرهم ثلاثة: عبد الحميد بن عبد المجيد [الأكبر]، والأوسط سعيد بن مسعدة، والأصغر على بن سليمان، والرابع أحمد بن عمران، والخامس أحمد بن محمد الموصلىّ، والسادس خلف بن عمر، والسابع عبد الله بن محمد، والثامن عبد العزيز بن أحمد، والتاسع على بن محمد المغربى الشاعر، والعاشر على بن إسماعيل الفاطمى، والحادى عشر هارون بن موسى بن شريك».
وقال ابن خلكان: «كان يطلق على سعيد بن مسعدة الأخفش الأصغر، فلما ظهر على بن سليمان المعروف بالأخفش أيضا صار هذا وسطا».','2','36',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (441,'قال الأخفش «١»: فلما دخل إلى شاطئ البصرة وجّه إلىّ فجئته، فعرّفنى خبره مع البغداذىّ، وودّعنى ومضى إلى الأهواز، فتزوّدت وجلست فى سمارية «٢» حتى وردت بغداذ، فوافيت مسجد الكسائىّ، فصلّيت خلفه الغداة، فلما انفتل من صلاته، وقعد فى محرابه- وبين يديه الفرّاء والأحمر وهشام وابن سعدان- سلّمت عليه، وسألته عن مائة مسألة، فأجاب بجوابات خطّأته فى جميعها.
فأراد أصحابه الوثوب علىّ، فمنعهم من ذلك، ولم يقطعنى ما رأيتهم عليه ممّا كنت فيه.
فلما فرغت من المسائل قال لى الكسائىّ: بالله أنت أبو الحسن سعيد ابن مسعدة الأخفش؟ قلت: نعم، فقام إلىّ، وعانقنى وأجلسنى إلى جانبه، ثم قال لى: أولادى أحبّ أن يتأدّبوا بك، ويخرّجوا «٣» على يديك، وتكون معى غير مفارق لى. وسألنى ذلك فأجبته، إليه.
فلما اتصلت الأيام بالاجتماع، سألنى أن أؤلف له كتابا فى معانى القرآن، فألفت كتابى فى المعانى، فجعله إماما، وعمل عليه كتابا فى المعانى، وعمل الفرّاء كتابه فى المعانى عليهما. وقرأ عليه الكسائىّ كتاب سيبويه، ووهب له سبعين دينارا.
قال أبو حاتم سهل بن محمد السّجستانىّ- رحمه الله-: وأخذ الأخفش كتاب أبى عبيدة فى القرآن، فأسقط منه شيئا، وزاد شيئا، وأبدل منه شيئا، قال: فقلت له:','2','37',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (442,'اىّ شىء هذا الذى تصنع من هذا؟ من أعرف بالعربية؟ أنت أو أبو عبيدة؟
فقال: الكتاب لمن أصلحه، وليس لمن أفسده. قال: فلم يلتفت إلى كتابه، وصار مطّرحا.
قال أبو حاتم: وكان الأخفش رجل سوء، قدريّا شمريا. وهم صنف من القدرية «١»، نسبوا إلى [أبى] شمر «٢»، ولم يكن يغلو فيه.
وقال أيضا: كتابه فى المعانى صو يلح؛ إلا أن فيه مذاهب سوء فى القدر.
وكان أبو حاتم يعيب كتابه فى القرآن فى جمع الواحد.
وقال أبو حاتم فى كتابه فى القراءات؛ حيث ذكر القرّاء والعلماء: «كان فى المدينة علىّ الملقّب بالجمل وضع كتابا فى النحو لم يخلّ شيئا «٣»، فذهب. وأظن الأخفش هذا وضع كتابه فى النحو منه، ولذلك قال فيه: الزيت رطلان بدرهم.
والزيت لا يذكر بالبصرة؛ لأنه ليس بإدام لهم».
وقال الأخفش: لما دخلت بغداذ أتانى هشام الضرير، فسألنى عن مسائل عملها، وفروع فرّعها، فلما رأيت أن اعتماده واعتماد غيره من الكوفيين على المسائل عملت كتاب المسائل الكبير، فلم يعرفوا أكثر ما أوردته فيه.','2','38',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (443,'وقال أبو العباس أحمد بن يحيى: أوّل من أملى غريب كل بيت من الشعر تحته الأخفش- وكان ببغداذ- والطوسىّ مستمليه. قال: ولم أدركه؛ لأنه قبل عصرنا. وكان يقال له: الأخفش الراوية. وتوفى سنة خمس عشرة ومائتين.
أنبأنى الشريف النقيب محمد بن أسعد النحوىّ الجوّانىّ «١»، أخبرنا عبد السلام ابن مختار اللغوىّ عن ابن بركات السعيدىّ، أخبرنا محمد بن سهل الهروىّ، أخبرنا محمد بن الحسين اليمنىّ «٢» من كتابه قال: «أخبرنى أبو العباس أحمد بن محمد بن الوليد قال: أخبرنا أبو إسحاق الزّجّاج عن المبرّد قال: سعيد بن مسعدة مولى بنى مجاشع؛ وهو من أهل بلخ- وكان أجلع فيما أخبرنا به عن أبى حاتم- والأجلع: الذى لم تنطبق شفتاه. وكان يقول بالعدل».
قال أبو العباس المبرّد: أخبرنى المازنى قال: كان الأخفش أعلم الناس بالكلام وأحذقهم بالجدل، وكان غلام أبى شمر، وكان على مذهبه.
قال أبو العباس أحمد بن يحيى: حدّثنى سعيد بن سلم قال: دخل الفرّاء على سعيد بن سلم، فقال: قد جاءكم سيد أهل اللغة، وسيد أهل العربية. فقال الفرّاء:
أما مادام الأخفش- يعنى سعيد بن مسعدة- يعيش فلا.
والأخفش أحذق أصحاب سيبويه، وهو أسنّ منه، ولقى من لقيه من العلماء إلا الخليل. والطريق إلى كتاب سيبويه الأخفش؛ وذلك أن كتاب سيبويه لا يعلم أحد قرأه على سيبويه، ولا قرأه عليه سيبويه؛ ولكنه لما مات قرئ على الأخفش فشرحه وبينه. ولم يكن أيضا ناقصا فى اللغة. وله كتب مستحسنة.','2','39',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (444,'وكان أخذ عن أبى مالك النّميرىّ. وذكر المبرّد عن المازنىّ قال: قال الأخفش: سألت أبا مالك عن قول أمية بن [أبى] الصلت «١»:
سلامك ربّنا فى كلّ فجر ... بريئا ما تغنّثك الذّموم «٢»
فقلت: ما «تغنّثك»؟ فقال: ما تتعلّق بك.
وكان فيمن قرأ كتاب سيبويه أبو عمر الجرمىّ وأبو عثمان المازنىّ.
وذكر ابن مجاهد قال: حدّثنا ثعلب عن سلمة عن الأخفش قال: جاءنا الكسائىّ إلى البصرة، فسألنى أن أقرأ عليه كتاب سيبويه ففعلت، فوجه إلىّ خمسين دينارا. قال: وكان الأخفش يعلّم ولد الكسائىّ.
وقال المبرّد: الأخفش أكبر سنا من سيبويه؛ إلا أنه لم يأخذ عن الخليل، وكانا جميعا يطلبان، فجاءه الأخفش، فناظره بعد أن برع، فقال له الأخفش:
إنما أناظرك لأستفيد لاغير. قال: أترانى أشكّ فى هذا! وكان أبو العباس ثعلب يفضّل الأخفش ويقول: كان أوسع الناس علما.
وله كتب كثيرة فى العروض والنحو والقوافى.
قال ثعلب: ومات الأخفش بعد الفرّاء، ومات الفرّاء سنة سبع ومائتين، بعد دخول المأمون العراق بثلاث سنين.
وذكر ابن عبد الملك التاريخى فى كتابه: «حدّثنى الحسين بن إسماعيل البصرىّ، قال: سمعت العباس بن الفرج الرياشىّ يقول: أخبرنى الأخفش قال:
يهمز الحرف إذا كان فيه ألف وقبلها فتحة، وأنشد للعجاج «٣»:','2','40',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (445,'وخندف «١» هامة هذا العألم
فى قصيدته التى يقول فيها:
يا دار سلمى يا سلمى ثم اسلمى
فلما همز «العألم» للفتحة التى قبلها لم يكن مؤسّسا «٢»؛ لأنهم يجعلون الهمزة بمنزلة سائر الحروف [مثل] العين والقاف».
قال: «وكان أبو حية النميرىّ ممّن يهمز مثل هذا. قال: والواو إذا كانت قبلها ضمة همزوها، مثل «يؤقن». قال: فقلت له: فالياء إذا كانت قبلها كسرة؟
قال: لا أدرى».
وذكر الجاحظ أن أبا الحسن الأخفش كان يعلم ابنا للمعذّل بن غيلان يقال له: عبد الله، فكتب إلى المعذّل، وقد استجفى الغلام:
أبلغ أبا عمر إذا جئته ... بأنّ عبد الله لى جاف
قد أحكم الآداب طرّا فما ... يجهل شيئا غير إنصافى
فكتب إليه المعذّل:
إن يك عبد الله يجفوكم ... يكفيك إلطافى وإتحافى
وذكر محمد بن إسحاق النديم فى «٣» كتابه قال: «مات الأخفش سنة إحدى عشرة ومائتين، بعد الفراء». قال: «وقال البلخى فى كتاب فضائل خراسان: أصله من خوارزم، ويقال: توفّى فى سنة خمس عشرة ومائتين. وروى الأخفش عن حمّاد بن الزبرقان- وكان بصريا».','2','41',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (446,'وله من الكتب المصنفة: كتاب الأوسط فى النحو. كتاب تفسير معانى القرآن. كتاب المقاييس، فى النحو. كتاب الاشتقاق.
كتاب الأربعة. كتاب العروض. كتاب المسائل الكبير. كتاب القوافى. كتاب الملوك، كتاب معانى الشعر. كتاب وقف التمام. كتاب المسائل الصغير. كتاب الأصوات. كتاب صفات الغنم وعلاجها وأسنانها. [كتاب التّصريف «١»]».
ووقف أعرابى على مجلس الأخفش، فسمع كلامهم فى النحو، فحار وعجب وأطرق ووسوس «٢»، فقال له الأخفش: ما تسمع يا أخا العرب؟ قال: أراكم تتكلمون بكلامنا فى كلامنا بما ليس فى كلامنا، فأنشد الأخفش لبعض العرب:
ماذا لقيت من المستعربين ومن ... تأسيس نحوهم هذا الذى ابتدعوا
إن قلت قافية فيما يكون لها ... معنى يخالف ما قاسوا وما صنعوا
قالوا: لحنت وهذا الحرف منخفض ... وذاك نصب وهذا ليس يرتفع
وحرّشوا بين عبد الله واجتهدوا ... وبين زيد فطال الضّرب والوجع
إنى نشأت بأرض لا تشبّ بها ... نار المجوس ولا تبنى بها البيع «٣»
ولا يطا القرد والخنزير ساحتها ... لكن بها العين والذّيال والصّدع «٤»
ما كل قولى معروف لكم فحذوا ... ما تعرفون وما لا تعرفون دعوا','2','42',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (447,'كم بين قوم قد احتالوا لمنطقهم ... وآخرين على إعرابهم طبعوا
وبين قوم رأوا شيئا معاينة ... وبين قوم رأوا بعض الذى سمعوا
قال الأخفش سعيد بن مسعدة: كان أمير البصرة يقرأ: (إنّ الله وملائكته يصلّون) «١» بالرفع، فيلحن، فمضيت إليه ناصحا له، فزبرنى «٢» وتوعّدنى، وقال:
تلحّنون أمراءكم!.
ثم عزل وولى محمد بن سليمان، فكأنه تلّقاها من فم المعزول. فقلت فى نفسى: هذا هاشمىّ، ونصيحته واجبة، فخشيت أن يلقانى بما لقينى به الأوّل، ثم حملت نفسى على نصيحته، فصرت إليه وهو فى غرفة، ومعه أخوه والغلمان على رأسه؛ فقلت: أيها الأمير، جئت لنصيحة، قال: قل، قلت:
هذا- وأومأت إلى أخيه- فلما سمع ذلك قام أخوه، وفرّق الغلمان عن رأسه- وأخلانى- فقلت: أيها الأمير، أنتم بيت الشرف وأصل الفصاحة، وتقرأ:
(إنّ الله وملائكته) بالرفع، وهذا غير جائز، فقال: قد نصحت ونبهت، فجزيت خيرا، فانصرف مشكورا. فلما صرت فى نصف الدّرجة إذا الغلام يقول لى:
قف مكانك، فقعدت مروّعا، وقلت: أحسب أن أخاه أغراه بى؛ فإذا بغلة سفواء «٣» وغلام وبدرة وتخت «٤» ثياب وقائل يقول: البغلة والغلام والمال لك، أمر به الأمير. فانصرفت مغتبطا بذلك «٥».','2','43',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (448,'هشام «١» للمظفر «٢» بن أبى عامر. قال: فقال لى ابن القزاز: إن هشاما لضعيف.
هذه الجنة المذكورة هى أوّل أصل اتخذه عبد الرحمن «٣» بن معاوية، وكان فيها نخلة أدركتها بنفسى، ومنها ولدت كل نخلة بالأندلس. وفى ذلك يقول عبد الرحمن- وقد تنزه إليها، فرأى تلك النخلة فحنّ:
يا نخل أنت غريبة مثلى ... فى الغرب نائية عن الأصل
فابكى وهل تبكى ملثّمة ... عجماء لم تطبع على خبل
لو أنها مثلى إذا لبكت ... ماء الفرات ومنبت «٤» النخل
لكنّها ذهلت وأذهلنى ... بغضى بنى العباس عن أهلى
وكان أبو عثمان هذا حافظا للغة والعربية، حسن القيام بهما، ضابطا لكتبه، متفنّنا فى نقله. وله كتاب فى الردّ على صاعد بن الحسن اللغوىّ البغداذىّ ضيف محمد ابن أبى عامر، فى مناكير كتابه فى النوادر والغريب؛ المسمى بالفصوص، وأكثر التحامل عليه فيه.
وكان ثقة من أجلّ أصحاب أبى على القالى، ومن طريقته صحّت اللغة بالأندلس بعد أبى علىّ، ومن طريق أبى على بن أبى الحباب وأبى بكر الزّبيدىّ.','2','46',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (449,'وفقد أبو عثمان- فى وقعة قفلش، فلم يوجد حيا ولا ميتا- يوم السبت للنصف من ربيع الأوّل سنة أربعمائة؛ كذا ذكر ابن حيان وغيره. وذكر ابن عبد البرّ أن وفاته كانت فى أربع أو خمس وتسعين وثلاثمائة- رحمه الله.

<span data-type="title" id=toc-336>٢٧٤ - سعيد بن عيسى الأصفر الاندلسىّ [١]</span>
ساكن طليطلة. أبو عثمان. كان عالما بالنحو واللغة والأشعار، و [له] مشاركة فى المنطق وكتب الأخبار. وله شرح الجمل للزّجّاجىّ.
توفى نحو الستين والأربعمائة.

<span data-type="title" id=toc-337>٢٧٥ - سعيد بن المبارك بن على بن الدهان البغداذىّ أبو محمد [٢]</span>
من أهل المقتدية، إحدى المحال الشرقية. رجل عالم فاضل، كيس نبيه نبيل، له معرفة كاملة بالنحو، ويد باسطة فى الشعر.
رحل إلى أصبهان، وسمع بها، واستفاد من خزائن وقوفها. وكتب الكثير من كتب الأدب بخطه، وعاد إلى بغداد، واستوطنها زمانا، وأخذ الناس عنه
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٨، وروضات الجنات ٢٧٢، والصلة لابن بشكوال ١: ٢٢٢.
[٢] ترجمته فى إشارة التعيين الورقة ٢٠، وبغية الوعاة ٢٥٦ - ٢٥٧، وتاريخ الإسلام للذهبى (وفيات سنة ٥٦٩)، وتلخيص ابن مكتوم ٧٧، وخريدة القصر ١: ٨٢ - ٨٣، وابن خلكان ١: ٢٠٩ - ٢١٠، وروضات الجنات ٣١٤ - ٣١٥، وشذرات الذهب ٤: ٢٣٣، وطبقات ابن قاضى شهبة ١: ٣٥٢ - ٣٥٤، وطبقات المفسرين للداودى ٧٨ ا، والفلاكة والمفلوكين ١٢٦ - ١٢٧، وكشف الظنون ٧٢، ١١٦، ٢١٢، ٤٣٨، ٧٥٢، ٨٧٢، ٩٦٠، ١١٥٦، ١٢١٢، ١٢٦٥، ١٤٣٨، ١٥٦٣، ١٦٣٠، ١٩٧٧، ومرآة الجنان ٣: ٣٩٠، ومسالك الأبصار ج ٤ مجلد ٢: ٢٥٥ - ٢٤٧، ومعجم الأدباء ١١: ٢١٩ - ٢٢٣، والنجوم الزاهرة ٦: ٧٢، ونكت الهميان ١٥٨ - ١٥٩.','2','47',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (450,'شرح الإيضاح فى النحو لأبى على الفارسى، فى ثلاثه وأربعين مجلدا، وشرح اللمع شرحا كبيرا فى عدّة مجلدات، وصنف غير ذلك.
وخرج عن بغداذ قاصدا دمشق، واجتاز الموصل، وبها وزيرها جمال الدين الجواد «١» الأصبهانىّ، فارتبطه عنده، ومعه الاجتياز بالإحسان، وصدّره بالموصل للإقراء والإفادة والتصنيف. وكان آخر كتبه ببغداذ، وهى التى أتعب فيها خاطره وناظره، وبلغه أن الغرق قد استولى على بغداذ، فسير من يحضر كتبه إن كانت سالمة، فوجدها قد غرقت فيما غرق، وزادها على الغرق أنّ خلف مسكنه مدبغة فاض الماء منها إلى منزله؛ فأهلك الكتب زيادة على هلاكها، فلما أحضرت إليه أخذ فى تأملها على نتنها وتغير لونها. فأشير عليه بأن يبخّر ما سلم منها على فساده بشىء مما يغير الرائحة، فشرع فى تبخيرها باللّاذن، «٢» ولازم ذلك إلى أن بخّرها بما يزيد على ثلاثين رطلا من اللّاذن. فطلع ذلك إلى رأسه وعينيه، فأحدث له العمى، فانكف بصره قبل موته- رحمه الله- ونعوذ بالله من سوء التقدير، إنه هو اللطيف الخبير.
وكان مولده فى رجب سنة أربع وتسعين وأربعمائة بنهر طابق «٣».','2','48',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (451,'قال تاج الإسلام أبو سعيد عبد الكريم بن محمد المروزىّ «١»: سمعت أبا القاسم على بن الحسين بن هبة الله «٢» الحافظ الدّمشقى من لفظه بدمشق يقول: سمعت سعيد ابن المبارك بن الدّهان بنهر طابق ببغداذ يقول: رأيت فى النوم شخصا أعرفه، وهو ينشد شخصا كأنه حبيب له:
أيها الماطل دينى ... أملّى وتماطل
علّل القلب فإنّى ... قانع منك بباطل
قال: فرأيت سعيد بن المبارك بن الدّهان، وعرضت عليه هذه الحكاية، فقال: ما أعرفها. ولعل ابن الدهان نسى. وأبو القاسم على بن القاسم الدمشقى من أوثق الرواة، جمع له الحفظ والمعرفة.
قلت: وقد سمعت من يذكر عمّن حضر هذه الحكاية أن ابن الدّهان استملاها من ابن السمعانىّ. وقال: أخبرنى ابن السمعانىّ المروزىّ قال: أخبرنى أبو القاسم بن عساكر الدمشقىّ عنى أنى أخبرته ... وساق باقى الحكاية؛ فكأنما روى عن رجلين عن نفسه، وهو أغرب ما وقع فى طريق الرواية.
ومن شعر سعيد بن المبارك بن الدّهان:
أهوى الخمول لكى أظلّ مرفّها ... مما يعانيه بنو الأزمان
إن الرياح إذا عصفن رأيتها ... تولى الأذيّة شامخ الأغصان
وأنشد سعيد بن المبارك النحوىّ لنفسه:
بادر إلى العيش والأيام راقدة ... ولا تكن لصروف الدهر تنتظر
فالعمر كالكأس يبدو فى أوائله ... صفو وآخره فى قعره كدر','2','49',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (452,'ومن شعره أيضا:
أرى الفضل منّاح التأخّر أهله ... وجهل الغنى يسعى له فى التقدم
كذاك أرى الخفّاش ينجيه قبحه ... ويحتبس القمرىّ حسن الترنم
وشعره كثير. وتوفى- رحمه الله- بالموصل فى شهور سنة تسع وستين وخمسمائة.
ومن مصنفاته: كتاب شرح الإيضاح، ثلاثة وأربعون مجلدا، وكتاب شرح اللمع، ثلاثة مجلدات. كتاب شرح بيت من شعر الصالح صنفه للصالح بن رزّيك «١»، مجلد. كتاب العروض، مجلد. كتاب الدّروس «٢» فى النحو، مجلد. كتاب الفصول «٣» فى النحو، مجلد. كتاب الرسالة السعيدية فى المآخذ الكندية يشتمل على سرقات المتنبى، مجلد. كتاب تذكرته، وسماه زهر الرياض، سبعة مجلدات، رأيتها وملكتها بخطه «٤».','2','50',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (453,'أنبأنا محمد بن محمد بن حامد «١» فى كتابه قال: «الشيخ أبو محمد بن الدّهان النحوىّ، من أهل بغداذ، سعيد بن المبارك بن على بن الدّهان: بحر لا يغضغض «٢»، وحبر لا يغمض «٣»، سيبويه عصره، ووحيد دهره. لقيته ببغداذ فى وقت انتقالنا إليها، وكانت داره بالمقتدية فى جوارنا، وكان يقال حينئذ: النحويون ببغداذ أربعة: ابن الجواليقىّ وابن الشّجرىّ وابن الخشاب وابن الدّهان. وكان جماعته يتعصّبون له، ويفضّلونه على غيره، ويقصدون نحوه لنحوه، ثم قصد الموصل فى زمان جمال الدين الجواد، وسكن فى ظلّه الوارف، وحظى من فضله الوافر، وأقام بعده بها إلى أن توفى سنة تسع وستين وخمسمائة، وقد أضرّ بصره، واختلّ نظره- رحمه الله».

<span data-type="title" id=toc-338>٢٧٦ - سعيد بن أحمد بن محمد بن أحمد بن إبراهيم الميدانىّ النيسابورىّ [١]</span>
ولد الميدانىّ «٤» الأديب المشهور، صاحب كتاب الأمثال وغيره. وولده هذا ذكره البيهقىّ فى الوشاح فقال فى وصفه:
«من حطّ الرّحال [عنده] يوما للتحصيل والتعليم، فلا شكّ أنه يخيّم فى فضاء التبجيل والتعظيم، ولا يبقى صفر الراحة من المال، معطّل الباحة من حسن الحال،
_________
[١] ترجمته فى الأنساب ٥٤٨ ا، وبغية الوعاة ٢٥٤، وتلخيص ابن مكتوم ٧٨، وطبقات ابن قاضى شهبة ١: ٣٤٩. والميدانى، بفتح الميم وسكون الياء: منسوب إلى محلة تعرف بميدان زياد بنيسابور. قال السمعانى إنه توفى فى حدود سنة ٥٤٠. وفى بغية الوعاة وطبقات ابن قاضى شهبة أنه توفى سنة ٥٣٩.','2','51',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (454,'وهذا الإمام تأدّب بآداب أبيه اللائحة، واجتهد فى سلوك سبله الواضحة؛ حتى تحقق فيه قول القائل: «ما أشبه الليلة بالبارحة».
ومن منظومه قصيدة قالها فى الإمام فخر الدين عبد العزيز الكوفىّ:
غرض «١» العذول وملّ من تعنيفى ... وأماط عنه حبائل التخويف
لما رأى ألّا أريم «٢» من الضّنى ... مثواى رقّ لجسمى المنزوف «٣»
لانت عريكته «٤» وذلّ شماسه «٥» ... لمتيمّ رهن الغرام لهيف
من لى بقلب المستهام ومن له ... بجميل صبر للغرام رديف
طاف الهوى بهما جميعا مثل ما ... [طافت على] الأرواح ريح خريف
ومنها:
قالت خليلك رهطه «٦» كوفيّة ... فلأجل ذا بوفائه لا يوفى
قلت اخسئ فلقد نرى متوفّرا ... كلّ الوفاء لدى الامام الكوفى
شمس يعمّ الخافقين إياتها «٧» ... مأمونة من غيبة وكسوف
خاض العباب إلى العلوم فنالها ... موفورة والناس عند السّيف «٨»
لا زال صدر الدين فيه موشّحا ... بقلائد التيجان والتشريف
أضحى كمثل الشمس فى فلك العلا ... والشمس تستغنى عن التعريف','2','52',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (455,'<span data-type="title" id=toc-339>٢٧٧ - سعيد بن محمد الغسّانى النحوىّ القيروانىّ أبو عثمان [١]</span>
كان أستاذا فى كل فنّ، عالما بالعربية واللغة والجدل- وكان الجدل أغلب الفنون عليه- وكان دقيق النظر جدا، ثابت الحجّة، شديد العارضة «١»، حاضر الجواب، صحيح الخاطر.
وله كتب كثيرة: منها كتاب توضيح المشكل فى القرآن، وكتاب المقالات ردّ فيه على المذاهب أجمعين، وكتاب الاستيعاب، وكتاب الأمالى، وكتاب عصمة الدينيين، وكتاب العبادة الكبرى والصغرى، وكتاب الاستواء، إلى كتب كثيرة جملتها فى الاحتجاج على الملحدين.
وله مع أبى عبد الله المعلّم مسائل برز فيها، وظهرت حجته فيها، ثم أملاها سعيد على أصحابه، وسماها المجالس.
وكان العراقيون يوجهون إليه مع تلاميذهم من يعنّته ويسأله، فحدّث بعض أهل القيروان قال: أتوه يوما فألفوه فى الحمام، فتلقوه وهو خارج منه، فقالوا:
أعزك الله! كيف وجدت الحمام؟ فقال لهم: غاية فى الطّيب. فقالوا له: من جهة الذوق طيبه- أصلحك الله-؟ فقال لهم: يا حثالة الزنادقة، وإخوان المدابير، وتلاميذ الملحدين، أرأيتم قول الله تعالى: (حَتَّى «٢» إِذا كُنْتُمْ فِي الْفُلْكِ وَجَرَيْنَ بِهِمْ بِرِيحٍ طَيِّبَةٍ
)، أمن قبل الذوق وجد طيب الريح!.
_________
[١] ترجمته فى بغية الوعاة ٢٥٧، وتلخيص ابن مكتوم ٧٨، وروضات الجنات ٣١٤، وطبقات الزبيدىّ ١٦٢ - ١٦٤، وطبقات ابن قاضى شهبة ١: ٣٥٤ - ٣٥٥. وفى طبقات الزبيدىّ اسمه: «ابن الحداد سعيد بن محمد الغسانى»، وما ذكره هنا المؤلف فى ترجمته يوافق ما فى الطبقات.','2','53',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (456,'وكان لسعيد بالقيروان فى أوّل دخول الشيعة مقامات محمودة، ناضل فيها عن الدّين، وذبّ عن السّنن، حتى مثّله أهل القيروان فى حاله تلك بأحمد بن حنبل «١» أيام المحنة «٢»، وكان يناظرهم ويقول: قد أوفيت على التسعين، وما بى إلى العيش من حاجة، ولا بدّ لى من المناضلة عن الدين، وأن أبلغ فى ذلك عذرا. ففعل. وكان المعتمد عليه فيها؛ وذلك أنهم لما ملكوا البلد وأظهروا تبديل الشرائع، وإحالة السّنن، بدروا إلى رجلين كبيرين من أصحاب سحنون «٣» فقتلوهما، وعرّوا أجسادهما، ثم نودى عليهما: هذا جزاء من ذهب مذهب مالك «٤».','2','54',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (457,'<span data-type="title" id=toc-340>٢٧٨ - سعيد بن عبد الله بن دحيم الأزدىّ القرشىّ النحوىّ أبو عثمان [١]</span>
سكن إشبيلية. كان عالما بالآداب والأخبار، إماما فى كتاب سيبويه، ذا حظّ وافر فى علم اللغة، وشروح الأشعار وضروب الآداب والأخبار. وشيوخه فى ذلك الوقت أبو نصر هارون بن موسى ومحمد بن عاصم وابن أبى الحباب ومحمد ابن خطاب وغيرهم. وذكره ابن خزرج.
وتوفى يوم السبت لتسع خلون من شوال سنة تسع وعشرين وأربعمائة.

<span data-type="title" id=toc-341>٢٧٩ - سعدان بن المبارك النحوىّ الكوفىّ أبو عثمان [٢]</span>
مولى عاتكة، مولاة المهدى بن المعلّى بن أيوب بن طريف. والمبارك من سبى طخارستان «١». من علماء الكوفيين ورواتهم. وقد روى عن أبى عبيدة من البصريين.
وتوفى «٢». وله من الكتب: كتاب خلق الإنسان. كتاب الوحوش.
كتاب الأمثال. كتاب النقائص، رواه عن أبى عبيدة. كتاب الأرضين والمياه والجبال والبحار «٣».
_________
[١] ترجمته فى بغية الوعاة ٢٥٥، وتلخيص ابن مكتوم ٧٨، والصلة لابن بشكوال ١: ٢١٩.
وفى تلخيص ابن مكتوم: «سعيد بن عبد الله بن دحم».
[٢] ترجمته فى بغية الوعاة ٢٥٤، وتاريخ بغداد ٩: ٢٠٣، وتلخيص ابن مكتوم ٧٨ - ٧٩، والفهرست ٧١، ونزهة الألباء ٢٠٦. وما ذكره يوافق ما فى الفهرست وتاريخ بغداذ.','2','55',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (458,'<span data-type="title" id=toc-342>٢٨٠ - سلمة بن عاصم أبو محمد النحوىّ [١]</span>
من نحاة الكوفة. روى عن يحيى بن زياد الفرّاء كتبه. وحدّث عن أحمد ابن يحيى ثعلب، وكان أديبا فاضلا عالما. قال إدريس بن عبد الكريم: قال لى سلمة بن عاصم: أريد أن أسمع كتاب العدد من خلف «١». فقلت لخلف، فقال:
فليجئ، فلما دخل رفعه لأن يجلس فى الصّدر، فأبى وقال: لا أجلس إلا بين يديك. وقال: هذا حقّ التعلّم، فقال له خلف: جاءنى أحمد بن حنبل يسمع حديث أبى عوانة «٢»، فاجتهدت أن أرفعه، فأبى وقال: لا أجلس إلا بين يديك، أمرنا أن نتواضع لمن نتعلم منه.
وقال ثعلب: كان سلمة حافظا لتأدية ما فى الكتب، وكان ابن قادم حسن النظر فى العلل، وكان الطّوال حاذقا بإلقاء العربية.
وقال محمد بن القاسم بن بشار الأنبارىّ: كتاب سلمة أجود الكتب- يعنى كتابه فى معانى القرآن- قال: لأن سلمة كان عالما، وكان لا يحضر مجلس الفرّاء يوم الإملاء، ويأخذ المجالس ممّن يحضر ويتدبّرها، فيجد فيها السهو، فيناظر عليها الفرّاء، فيرجع عنه.
_________
[١] ترجمته فى بغية الوعاة ٢٦٠، وتلخيص ابن مكتوم ٧٩، وطبقات الزبيدى ٩٦، وطبقات ابن قاضى شهبة ١: ٣٥٦، وطبقات القرّاء لابن الجزرى ١: ٣١١، والفهرست ٦٧، وكشف الظنون ١٧٣٠، ومعجم الأدباء ١١: ٢٤٢ - ٢٤٣، ونزهة الألباء ٢٠٤ - ٢٠٥. قال ابن الجزرى: «توفى بعد السبعين ومائتين فيما أحسب»، وذكر صاحب كشف الظنون أنه توفى سنة ٣١٠. وذكر ياقوت له من الكتب المصنفة: معانى القرآن، والمسلوك فى العربية، وغريب الحديث.','2','56',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (459,'وكان ثعلب سمع كتاب المعانى للفرّاء من سلمة بن عاصم عن الفراء.
والحدود فى النحو ستون حدا، سمعها من سلمة عن الفرّاء أيضا. وأنشد ابن شقير «١» الشاعر فى سلمة:
لو تلفّفت فى كساء الكسائى ... وتفرّيت فروة الفرّاء
وتخلّلت بالخليل وأضحى ... سيبويه لديك عبد سباء «٢»
وتلبّست من سواد أبى الأس ... ود ثوبا يكنى أبا السوداء
لأبى الله أن يراك ذوو الأل ... باب إلا فى صورة الأغبياء
ورأيت فى المجموع الذى نقلت منه هذه الأبيات أبياتا أخرى؛ فلا أدرى:
أهى فى سلمة أم فى مثله من النحاة؛ وهى:
يا غليظ الطّباع يا أبرد النا ... س إلى اليوم منذ كنت صبيا
لو يقوم الخليل أو يبعث الل ... هـ من القبر يونس النحويّا
فأفاداك كلّ باب من النح ... وبعلاته لكنت غبيّا
أنت نىّ غثّ ركيك ولمّا ... تستحبّ النفوس ما كان نيّا
وقال أحمد بن يحيى ثعلب النحوىّ: جئت سلمة وهو غضبان، فقلت له:
مالك يا أبا محمد؟ فقال: جاءنى شيخ يزعم أن الفرّاء أخطأ قولهم «قائمين كان الزيدون» إذ كان لا يجيز «قائما ضربت زيدا». فقلت: عدّ عن هذا، إنما جاز «قائمين كان الزيدون» لأن «قائمين» خبر لكان، ولم يجز «قائما ضربت زيدا» لأن «قائما» ليس خبرا «لضربت»:
ورئى فى كمّ سلمة بن عاصم شعر العباس بن الأحنف، فقيل له: مثلك- أعزّك الله- يحمل هذا! فقال: ألا أحمل شعر من يقول:','2','57',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (460,'أسأت «١» إذ أحسنت ظنى بكم ... والحزم سوء الظن بالناس
يقلقنى «٢» شوقى فآتيكم ... والقلب مملوء من الياس

<span data-type="title" id=toc-343>٢٨١ - سلمة بن سعد النحوى الأندلسىّ القرطبىّ [١]</span>
يكنى أبا القاسم، يروى عن أبى الحسن الأنطاكىّ المقرئ وأبى بكر الزبيدىّ ومحمد بن يحيى الرّياحى ومحمد بن أصبغ النحوىّ. كان مشهورا بمعرفة الأدب؛ أخذ عنه أبو محمد قاسم بن إبراهيم الخزرجىّ كثيرا.

<span data-type="title" id=toc-344>٢٨٢ - سهل بن محمد أبو حاتم السّجستانىّ الجشمىّ النحوىّ اللغوىّ المقرئ [٢]</span>
نزيل البصرة وعالمها. قال المبرّد: سمعته يقول: قرأت كتاب سيبويه على الأخفش «٣» مرتين. وكان كثير الرّواية عن أبى زيد وأبى عبيدة والأصمعىّ، عالما
_________
[١] ترجمته فى تلخيص ابن مكتوم ٧٩.
[٢] ترجمته فى أخبار النحويين البصريين للسيرافىّ ٩٣ - ٩٦، وإشارة التعيين الورقة ٢١، والأنساب ٢٩١ ب، وبغية الوعاة ٢٦٥، وتاريخ الإسلام للذهبى (وفيات سنة ٢٥٠)، وتقريب التهذيب ١٠٤، وتلخيص ابن مكتوم ٧٩ - ٨٠، وتهذيب التهذيب ٤: ٢٥٧ - ٢٥٨، وابن خلكان:
١: ٢١٨ - ٢١٩، وشذرات الذهب ٢: ١٢١، وطبقات الزبيدى ٦٤ - ٦٧، وطبقات ابن قاضى شهبة ١: ٣٦١ - ٣٦٤، وطبقات القراء لابن الجزرى ١: ٣٢٠ - ٣٢١، وطبقات المفسرين للداودى، ٨٩ ب، وعيون التواريخ (وفيات سنة ٢٥٠)، والفلاكة والمفلوكين ٨٦، والفهرست ٥٨ - ٥٩، وكشف الظنون ٣٣، ١١٥، ١٢٣، ١٣٨٣، ١٤٢٩، ١٤٣٦، ١٤٣٩، ١٤٤٦، ١٤٤٩، ١٤٥٢، ١٤٥٤، ١٤٥٧، ١٤٥٨، ١٤٦٦، ١٤٦٩، ١٥٧٧، ١٧٨١، ومراتب النحويين ١٢٣، ١٣٠ - ١٣٤، ومرآة الجنان ٢: ١٥٦، والمزهر ٢: ٤٠٨، ٤١٩، ٤٤٥، ٤٦٤، ومسالك الأبصار ج ٤ مجلد ٢: ٢٣٣ - ٢٣٤، ومعجم الأدباء ١١: ٢٦٣ - ٢٦٥، والنجوم الزاهرة ٢: ٣٣٢، ونزهة الألباء ٢٥١ - ٢٥٤. والسجستانى، بكسر السين والجيم وسكون السين الثانية: منسوب إلى سجستان. وهو إقليم بين فارس والسند. وقال بعضهم:
بل هو منسوب إلى سجستانة، من قرى البصرة. والجشمىّ، بضم الجيم وفتح الشين: منسوب إلى جشم، وهو يطلق على عدّة قبائل: قال ابن خلكان: «ولا أدرى إلى أيها ينسب أبو حاتم المذكور».','2','58',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (461,'باللغة والشعر، حسن العلم بالعروض وإخراج المعمّى. وله شعر جيّد، ويصيب المعنى، ولم يكن حاذقا فى النحو.
وله مصنفات كثيرة فى اللغة والقرآن. قال المبرّد: ولو قدم [بغداذ «١»] لم يقم له منهم أحد.
وكان إذا التقى هو والمازنىّ فى دار عيسى «٢» بن جعفر الهاشمىّ تشاغل أو بادر خوفا من أن يسأله المازنىّ عن النحو.
وكان جّماعة للكتب، وكان يتّجر «٣» فيها. قال أبو العباس المبرّد: أتيت السّجستانىّ وأنا حدث، فرأيت منه بعض ما ينبغى أن تهجر حلقته له. فتركته مدّة، ثم صرت إليه، فعمّيت له بيتا لهرون الرشيد، فأجابنى «٤»:
أيا حسن الوجه قد جئتنا ... بداهية عجب فى رجب
فعمّيت بيتا وأخفيته ... فلم يخف بل لاح مثل الشهب
فأظهر مكنونه الطّيطوى «٥» ... وهتّك عنه الحمام الحجب
فذلّل ما كان مستصعبا ... لنا فتناولته من كثب
أيا من إذا ما دنونا له ... نأى وإذا ما نأينا اقترب
عذرناك إذ كنت مستحسنا ... وبيتك ذو الطير بيت عجب','2','59',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (462,'[سلام على النازح المغترب ... تحيّة صبّ به مكتئب «١»]
وله شعر كثير، وعليه اعتمد ابن دريد فى أكثر اللغة.
وتوفى أبو حاتم سنة خمس وخمسين ومائتين.
كان يقرأ عليه كتب الأخفش فيردّ فيها ردا حسنا. قال ابن الغازى: ثم رأيتها تقرأ على أبى الفضل الرياشىّ؛ فلا حول ولا قوّة إلا بالله، أىّ ندف كان يندفها.
قال الرياشىّ على قبر أبى حاتم: ذهب بعلم كثير. قيل له: كتبه؛ فقال الرّياشىّ: الكتب تؤدّى ما فيها، ولكن صدره! وقيل لأبى زيد: على من يقرأ بعدك؟ فقال: على أبى سهل. وكان أبو حاتم يتّهم بحب الصبيان، وكان بريئا من ذلك؛ إنما كان كثير الدّعابة، فوجد ذلك السبيل إلى عرضه.
وقال أبو عثمان الخزاعىّ: رأيت كأنى بين النائم واليقظان، وسمعت قائلا يقول «٢»:
أبو حاتم عالم بالعلوم ... وأهل العلوم له كالخول «٣»
عليكم أبا حاتم إنّه ... له بالقراءة علم جلل
فإن تفقدوه فلن تدركوا ... له ما حييتم بعلم بدل «٤»','2','60',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (463,'ودخل أعرابى مسجد البصرة، فتفقد أبا حاتم- وكان يختلف إليه- فأعلم بموته، فقال «١»:
يا بانى الدّنيا للذّاته ... أعظم بذكر الموت من هادم
أما ترى الإخوان قد سارعوا ... بقادم منهم على قادم
ومرّ من قد كنت تزهى به ... ولست مما ذاق بالسّالم
وليس نقص الأرض من جاهل «٢» ... مات ولكن ذاك من عالم
أما العراقان «٣» فقد أقفرا ... لحادث حلّهما قاصم
من كان للخطبة يعنى بها ... وللغريب المشكل القائم
[قد ذهب العلم بأعلامه ... والنحو من بعد أبى حاتم «٤»]
من للدواوين «٥» إذا حصّلت ... وكتب أملاك بنى هاشم
مفتاح قفل ضلّ مفتاحه ... ولؤلؤ «٦» يبقى بلا ناظم
يا مسجد البصرة لم تبكه ... بواكف من دمعك السّاجم
قال أبو بكر بن دريد: مات أبو حاتم بالبصرة فى رجب سنة خمس وخمسين «٧» ومائتين ودفن بسرّة المصلّى، وصلى عليه سليمان بن جعفر بن سليمان بن على بن عبد الله بن العباس بن عبد المطلب- وكان والى البصرة يومئذ.','2','61',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (464,'وقال مروان بن عبد الملك: توفى أبو حاتم فى المحرّم من هذا التاريخ. وقال آخر: مات فى هذا التاريخ، وكان يوما مطيرا، وصلى عليه سليمان بن القاسم أخو جعفر بن القاسم.
وله من الكتب: كتاب إعراب القرآن. كتاب ما تلحن فيه العامة.
كتاب الطير. كتاب المذكر والمؤنث. كتاب النبات. كتاب المقصور والممدود. كتاب الفرق. كتاب القراءات. كتاب المقاطع والمبادئ. كتاب الفصاحة. كتاب النخلة «١». كتاب الأضداد «٢». كتاب القسىّ والنبال والسهام. كتاب السيوف والرماح.
كتاب الدرع والترس. كتاب الوحوش. كتاب الحشرات. كتاب الهجاء. كتاب الزرع. كتاب خلق الإنسان. كتاب الإدغام، كتاب اللّبأ «٣» واللبن الحليب. كتاب الكرم. كتاب الشتاء والصيف.
كتاب النحل والعسل. كتاب الإبل. كتاب العشب «٤». كتاب الإتباع.
كتاب الخصب والقحط. كتاب اختلاف المصاحف. كتاب الشوق إلى الأوطان. كتاب الحرّ والبرد والشمس والقمر والليل والنهار. كتاب الفرق بين الآدميين وبين كل ذى روح.','2','62',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (465,'وكتابه فى القراءات مما يفخر به أهل البصرة؛ فإنه أجلّ كتاب صنّف فى هذا النوع إلى زمانه.
ولأبى حاتم كتاب كبير فى إصلاح المزال والمفسد، مشتمل على الفوائد الجمة. وما رؤى كتاب فى هذا الباب أنبل منه ولا أكمل «١».
وقال أبو حاتم سهل بن محمد السّجستانىّ: كنت «٢» عند أبى الحسن سعيد بن مسعدة الأخفش وعنده التّوّزىّ، فقال لى: يا أبا حاتم، ما صنعت فى كتاب المذكر والمؤنث؟
قلت: قد عملت فى ذلك شيئا، فقال: فما تقول فى الفردوس؟ قلت: ذكر.
قال: فإن الله عز وجل يقول: (الفردوس «٣» هم فيها خالدون). قال: قلت: ذهب إلى الجنة فأنّث. فقال لى التّوّزىّ: يا غافل، أما تسمع الناس يقولون: أسألك الفردوس الأعلى! فقلت له: يا نائم، الأعلى هاهنا «أفعل» وليس «بفعلى».
وذكر أبو حاتم سهل بن محمد قال: «كان «٤» جزئى على يعقوب «٥»، ومنزلتى عنده فيمن يقرأ أن أجلس إلى جنب من يقرأ عليه، فإذا فرغ أخذت من الموضع الذى يتركه، فأقرأ عليه. فجئت ذات يوم، ورجل يقرأ عليه من «سورة البقرة» حتى انتهى إلى قوله:
(وقال لهم نبيّهم «٦»). فابتدأت من هذا المكان، حتى انتهيت إلى قوله: (فلمّا جاوزه هو','2','63',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (466,'والذين آمنوا معه)، فحصبنى وقال: أحسن [أحسن]، فأعدت الحرف من غير إدغام، وقد كنت قرأت عليه الإدغام مرارا كثيرة، فقلت له: هذا لا يجوز الإدغام فيه، فقال: لم- وحدّثنى غير واحد عن أبى عمرو أنه كان يدغم؟. فقلت له: اتّهم الرواة، فإنهم لم يضبطوا عنه. فقال- وحدّثنى وأكثر منه. فقلت له: هذا لا يجوز [لأنّ] «١» بينهما واوا، وكيف تدغم الحرف فى الحرف وبينهما حرف آخر! فقال: اقرأ. فقرأت.
وكان الأخفش النحوىّ يجلس خلف أسطوانة يعقوب، فصرت إلى الأخفش، فسلمت عليه، فقال لى: يا رأس البغل، لعنك الله؛ تأبى إلا أن تعلم ما يعلم المشايخ! والله لا قرأ يعقوب إلا كما قلت».
واتفق أن ابن اللّيث الصّفّار «٢» صاحب سجستان ملك بعد موت أبى حاتم شيراز والأهواز، وخاف منه أهل البصرة أن يستولى على بلدهم. وسمع ابن الصّفار بموت أبى حاتم، واشتاقت نفسه إلى كتبه، فسيّر من ابتاعها من ورثته، ووقف أهل البصرة عن المزايدة فيها، خشية من ابن الصفّار ومصانعة له، فأبيعت بقيمة أربعة عشر ألف دينار، ونقلت إلى يعقوب، لم يترك منها شىء.

<span data-type="title" id=toc-345>٢٨٣ - سلمويه النحوىّ الكوفىّ [١]</span>
تلميذ الكسائى، أخذ عنه جزءا من النحو، وتصدّر لإفادته الطلبة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨٠، وبغية الوعاة ٢٦٠، وطبقات الزبيدى ٩٥.','2','64',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (467,'<span data-type="title" id=toc-346>٢٨٤ - سماك بن حرب بن أبى سعيد [١]</span>
محدّث «١» راوية. قال حمّاد «٢» الكاتب: كنا نأتى سماك بن حرف فنسأله عن الشعر، ويأتى أصحاب الحديث، فيقبل علينا ويدعهم ويقول: هؤلاء ثقلاء.

<span data-type="title" id=toc-347>٢٨٥ - السّرخسىّ [٢]</span>
من نحاة الكوفة، ونسبه أشهر من اسمه. واسمه عبد العزيز بن محمد، ويكنى أبا طالب. كان جارا لهشام الضرير، وكان يجلس للإفادة فى مسجد الترجمانية.
وله تصنيف فى النحو كبير، غير موجود.
_________
[١] ترجمته فى تاريخ الإسلام للذهبى (وفيات سنة ١٢٣)، وتلخيص ابن مكتوم ٨٠، وتقريب التهذيب ١٠٣، وتهذيب التهذيب ٤: ٢٣٢ - ٢٣٤، وخلاصة تذهيب الكمال ١٣٢، وشذرات الذهب ١: ١٦١، وطبقات الزبيدى ١١٣، وعيون التواريخ (وفيات سنة ١٢٣)، ومرآة الجنان ١: ٢٥٩ - ٢٦٠، والنجوم الزاهرة ١: ٢٩٠، والوافى بالوفيات ج ٥ مجلد ١: ١٥٨.
وما ذكره المؤلف من اسمه وترجمته يوافق ما فى طبقات الزبيدىّ. واسمه فى بقية كتب التراجم: سماك بن حرب بن أوس بن خالد الذهلىّ البكرىّ.
[٢] ترجمته فى الفهرست ٧٠.','2','65',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (468,'<span data-type="title" id=toc-348>٢٨٦ - سراج بن عبد الملك بن سراج أبو الحسين اللغوىّ الأندلسىّ [١]</span>
قرطبىّ نحوى مشهور فى زمانه، متصدّر للإفادة فى إقليمه، يقرأ عليه.
أنبأنا أبو طاهر السّلفىّ «١» فى إجازته العامة، حدّثنى أبو الوليد يوسف بن المفضل ابن الحسن الأنصارىّ القبذاقىّ «٢» بالإسكندرية بعد قفوله من الحجاز وتوجهه إلى الأندلس، حدّثنى أبو بكر يحيى بن محمد بن زيدان القرطبىّ بها، قال: حضرت مجلس أبى الحسين سراج بن عبد الملك بن سراج اللغوىّ، فقرئ عليه فى الموطّأ «٣»:
«لا قطع فى ثمر ولا كثر» «٤»، فأنشد لصاعد بن الحسن الرّبعىّ «٥»:
ومهفهف أبهى من القمر ... قهر الفؤاد بفاتر النّظر
خالسته تفّاح وجنته ... فأخذتها منه على غرر
فأخافنى قوم فقلت لهم: ... «لا قطع فى ثمر ولا كثر»
_________
[١] ترجمته فى بغية الملتمس للضبى ٢٩٠ - ٢٩١، وبغية الوعاة ٢٥١ - ٢٥٢، وتلخيص ابن مكتوم ٨٠، والديباج المذهب ١٢٦، والصلة لابن بشكوال ١: ٢٢٦، والمعجم لابن أباره ٣٠٥ - ٣٠٧، ومعجم الأدباء ١١: ١٨١ - ١٨٢. قال ابن مكتوم: «توفى أبو الحسن سراج يوم الإثنين لسبع بقين من جمادى الآخرة من سنة ثمان وخمسمائة بقرطبة، ومولده سنة تسع وثلاثين وأربعمائة- رحمه الله ورضى عنه».','2','66',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (469,'<span data-type="title" id=toc-349>٢٨٧ - سالم بن أحمد بن سالم بن أبى الصقر أبو المرجى النحوىّ العروضىّ العراقىّ [١]</span>
كانت له معرفة بالنحو وبقول الشعر، ويعرف عروضه وأوزانه. وله فى ذلك يد جيدة. سافر الكثير، ولقى جماعة من الأدباء، وأخذ عنهم، ونظم أرجوزة فى النحو على الأبواب كملحة أبى محمد الحريرىّ البصرىّ، وامتدح جماعة بقصائد من شعره.
وتوفى ببغداذ يوم الأحد خامس ذى القعدة سنة إحدى عشرة وستمائة، وصلى عليه فى هذا اليوم، وحمل إلى الجانب الغربىّ، ودفن فى مشهد موسى ابن جعفر.

<span data-type="title" id=toc-350>٢٨٨ - سلامة بن غيّاض [٢]</span>
بغين معجمة، وياء آخر الحروف مشدّدة. ابن أحمد. أبو الخير النحوىّ الشامىّ. من أهل كفر طاب. «١» كان أديبا فاضلا، له معرفة جيدة بالنحو واللغة، وله فى النحو تصانيف. قرأ بمصر على أبى الحسن على بن جعفر العرقىّ وابن القطّاع الصّقلّىّ اللغوىّ وغيرهما. وقدم العراق بعد سنة عشرين وخمسمائة، وأقام ببغداذ مدّة، وقرأ عليه قوم بها وسمعوا منه، ثم سار إلى واسط وأقام بها، ودرس بها النحو فى جامعها؛ علّقه عنه أبو الفتح بن زريق الحدّاد وجماعة معه، ورحل إلى
_________
[١] ترجمته فى بغية الوعاة ٢٥١، وتلخيص ابن مكتوم ٨٠ - ٨١، ومعجم الأدباء ١١:
١٧٨، والوافى بالوفيات ج ٤ مجلد ٢: ٣٠٤.
[٢] ترجمته فى بغية الوعاة ٢٥٩، وتاريخ الإسلام للذهبى (وفيات سنة ٥٣٣)، وتلخيص ابن مكتوم ٨١، وطبقات ابن قاضى شهبة ١: ٣٦٧ - ٣٦٨، ومعجم الأدباء ١١: ٢٣٣ - ٢٣٤، وكشف الظنون ٣٩٣.','2','67',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (470,'البصرة، ثم رحل إلى بلاد العجم، وجال فى أقطارها، وعاد بعد ذلك إلى الشام، واستوطن حلب، ومات بها فى شهور سنة أربع وثلاثين وخمسمائة، وخلف بها عقبا. ومن بنات ابنه من هو باق إلى الآن، ويعرفون بالعالمات النحويات، نسبة إليه.
وكان- رحمه الله- حسن الضبط والخط، كثير التنقيب والتحقيق، [وله رسالة فى فضل العربية والحث على تعليمها «١»]، وقعت إلىّ بخطه، وهى فى غاية الجودة والصحة وحسن النّقيبة «٢».

<span data-type="title" id=toc-351>٢٨٩ - سالم بن أبى الصقر أحمد بن سالم العروضىّ الملقب بالمنتجب [١]</span>
من ساكنى درب القرنفليّين ببغداذ. كانت له معرفة جيدة بالأدب والعروض وصناعة الشعر. قرأ على الشيخ أبى البقاء النحوىّ، وعلى الشيخ أبى الخير مصدّق بن شبيب، وعلى أبى البركات عبد الرحمن بن الأنبارى الشيخ الصالح النحوىّ، وصحب الوجيه النحوىّ، وسافر إلى بلاد العجم، وعاد إلى بغداذ، وتوفى بها فى اليوم الخامس من ذى القعدة، يوم الأحد سنة إحدى عشرة وستمائة، ودفن بمشهد موسى بن جعفر.
_________
[١] هو مكرر رقم ٢٨٧.','2','68',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (471,'<span data-type="title" id=toc-352>٢٩٠ - ساتكين بن أرسلان أبو منصور التركىّ المالكى الأديب [١]</span>
نزيل دمشق. كانت له فى النحو يد، وصنف فيه مقدّمة لطيفة. ذكره الحافظ أبو القاسم على بن عساكر فى تاريخه «١».

<span data-type="title" id=toc-353>٢٩١ - سليم بن أيوب بن سليم أبو الفتح الرازىّ الفقيه الأديب [٢]</span>
سكن الشام مرابطا محتسبا لنشر العلم، وصنف كتابا فى غريب الحديث. قال سليم: دخلت بغداذ فى حداثتى لطلب علم اللغة، فكنت آتى شيخا (ذكره)، فبكّرت فى بعض الأيام إليه، فقيل لى: هو فى الحمام. فمضيت نحوه، فعبرت فى طريقى على الشيخ أبى حامد الأسفرايينىّ «٢» وهو يملى، فدخلت المسجد وجلست مع الطلبة، فوجدته فى كتاب الصيام فى هذه المسألة: «إذا أولج ثم أحس بالفجر فنزع».
فاستحسنت ذلك وعلّقت الدرس على ظهر جزء كان معى، فلما عدت إلى منزلى
_________
[١] ترجمته فى بغية الوعاة ٢٥١، وتاريخ ابن عساكر ١٥: ١٤، وتلخيص ابن مكتوم ٨١، والوافى بالوفيات ج ٤ مجلد ٢: ٢٩١.
[٢] ترجمته فى تلخيص ابن مكتوم ٨١، وابن خلكان ١: ٢١٢ - ٢١٣، وطبقات الشافعية ٣: ١٦٨، والوافى بالوفيات ج ٥ مجلد ١: ٢٨. والرازى: منسوب إلى الرىّ، وهى مدينة عظيمة من بلاد الديلم. والنسبة على غير القياس.','2','69',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (472,'وجعلت أعيد الدرس حلا لى، وقلت: أتمّ هذا الكتاب- يعنى كتاب الصيام- فعلّقت كتاب الصيام، ولزمت الشيخ أبا حامد حتى علّقت عنه جميع التعليق.
وكان قد استوطن صور، وكان يقول: وضعت منى صور، ورفعت من أبى الحسن المحاملىّ «١» بغداذ.
وكان سليم ببغداذ ترد عليه الكتب من الرّىّ فلا يقرؤها؛ إلى أن استكمل ما أراد من أنواع العلم، ثم فتحها فوجد فيها من موت أهله وحدوث ما يشغل خاطره أمرا لو قرأه لاشتغل به عن الطلب. وكان فى أوّل أمره يطلب الأدب، ثم تفقه بعد الأربعين من عمره.
قال غيث بن على الأرمنازىّ الصّورىّ «٢»: غرق سليم بن أيوب الفقيه فى بحر القلزم «٣» عند ساحل جدّة بعد عوده من الحج، فى صفر سنة سبع وأربعين وأربعمائة، وكان قد نيّف على الثمانين، ودفن فى جزيرة بقرب الجار «٤» عند المخاضة.','2','70',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (473,'<span data-type="title" id=toc-354>٢٩٢ - سيبويه السّنجارىّ النحوىّ [١]</span>
قريب العهد فى زماننا هذا. رحل عن سنجار إلى بغداذ، وأخذ عن الكمال الأنبارىّ وعن عبد الرحيم العصار، وعاد إلى بلده سنجار، وتصدّر لإفادة هذا الشأن.
وكان ممن أدركته حرفة الأدب، وأحوجته الحاجة إلى الارتزاق بالتفقه على مذهب النعمان، وابتلى مع عيشه الأنكد بمدرّس يمتهنه فى المحافل، ويمنحه الإلواء عنه والتغافل، وله عائلة تحمله على الذلّ، وعنده إقلال صيّره الأخسّ الأقل. ولم يزل مكابد الفقر إلى أن صار إلى قبره. فسبحان من رزق الجاهل، وحرم الفاضل؛ صنع لا يفهم معناه، وحكم لا يستحلى مجناه؛ يفعل الله ما يشاء، ويحكم ما يريد، فله الحمد إذ لا يحمد على المكروه سواه. وكانت وفاته بسنجار فى حدود سنة ست وستمائة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨١ - ٨٢. والسنجارىّ، بكسر السين وسكون النون:
منسوب إلى سنجار؛ وهى من بلاد الجزيرة.','2','71',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (474,'<span data-type="title" id=toc-355>(حرف الشين)</span>
<span data-type="title" id=toc-356>٢٩٣ - شيبان بن عبد الرحمن أبو معاوية التميمىّ النحوىّ المؤدب البصرىّ [١]</span>
سكن الكوفة زمانا، ثم انتقل عنها إلى بغداذ، وحدّث بها عن الحسن البصرىّ «١» وقتادة، وكان يؤدّب سليمان بن داود الهاشمى ببغداذ. وكان شيبان النحوىّ ينسب إلى بطن يقال لهم نحو، وهم بنو نحو بن شمس (بضم الشين)، بطن من الأزد.
وذكر أبو الحسين بن المنادى: المنسوب إلى القبيلة من الأزد التى يقال لها نحو، هو يزيد النحوىّ، لا شيبان.
وقال أبو بكر عبد الله بن سليمان بن الأشعث: يزيد النحوىّ، هو يزيد بن أبى سعيد، وهو من بطن من الأزد يقال لهم بنو نحو، ليس من نحو العربية.
ولم يرو منهم الحديث إلا رجلان: أحدهما يزيد هذا. وسائر من يقال له النحوىّ
_________
[١] ترجمته فى الأنساب ٥٥٦ ا، وتاريخ بغداد ٩: ٢٧١ - ٢٧٤، وتذكرة الحفاظ ١: ٢٠٢ - ٢٠٣، وتقريب التهذيب ١١٠، وتلخيص ابن مكتوم ٨٢، وتهذيب التهذيب ٤: ٢٧٣ - ٢٧٤، وخلاصة تذهيب الكمال ١٤٢، وشذرات الذهب ١: ٢٥٩، وطبقات ابن سعد ٦: ٢٦٢، وطبقات ابن قاضى شهبة ٢: ٢ - ٣، وطبقات القرّاء لابن الجزرى ١: ٣٢٩، ومعجم الأدباء ١١: ٢٧٥ - ٢٧٦، ونزهة الألباء ٣٨ - ٤١.','2','72',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (475,'فمن نحو العربية؛ شيبان بن عبد الرحمن، وهارون بن موسى النحوىّ وأبو زيد النحوى. قال يحيى بن معين: شيبان ثقة. وهو صاحب كتاب صحيح «١».
يقال إنه مات ببغداذ فى خلافة المهدى، ودفن فى مقابر الخيزران. توفى سنة أربع وستين ومائة.

<span data-type="title" id=toc-357>٢٩٤ - شيث بن إبراهيم بن الحاج القفطىّ [١]</span>
الفقيه النحوى الزاهد العالم المتفنن. كان من أهل مدينة قفط، من صعيد مصر، وأهله أهل قرآن وخير وصلاح، أصحاب سنة وجماعة، أرباب تعصب فى ذلك، وقد كانوا يتظاهرون به فى الدولة العلوية القصرية، وعلم منهم ذلك فلم يعارضوا. وكان أخوه الفقيه محمد المقرئ «٢» ممن سلمت إليه صناعة القرآن فى الروايات وجودة التلاوة وطيب النغمة، ولم يزل مفيدا للناس فى مسجد له بمحلّة «٣» مفردة له ولأهله، تعرف بحارة ابن الحاج.
وكان الفقيه شيث هذا قيّما بعلم النحو، وله تصنيفان: أحدهما اسمه المختصر، وآخر أخصر منه سماه المعتصر من المختصر، وقد جدول فى المختصر جدولا لعوامل الإعراب، أجمع من رآه أنه لم يأت أحد بمثله. وله مسائل نحوية؛ أجوبة عن مآخذ أخذها عليه بعض النحاة، سماها حز الغلاصم «٤» وإفحام المخاصم «٥».
_________
[١] ترجمته فى بغية الوعاة ٢٦٧، وتلخيص ابن مكتوم ٨٢، والطالع السعيد ١٣٧ - ١٣٩، والديباج المذهب ١٢٧ - ١٢٩، ومعجم الأدباء ١١: ٢٧٧ - ٢٨١، ونكت الهميان ١٦٨ - ١٧٠.','2','73',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (476,'وكان يتفقه على مذهب مالك بن أنس. وله مسائل وتعاليق فى الفقه جميلة، وله كلام فى الرقائق.
وقد كان- رحمه الله- حسن العبارة مخلوقا من حذر، لم يره أحد ضاحكا قط ولا هازلا، وكان يسير فى أفعاله على سنن السلف الصالح، وكان ملوك البلاد يجلون قدره، ويرفعون ذكره.
وكان [القاضى] الفاضل عبد الرحيم بن علىّ البيسانىّ «١» يعرف قدره، ويعظم ذكره، ويقبل إشارته فى حق من يشفع فيه، وله إليه مكاتبات ومخاطبات يشهد بها ترسّله، وانتقل فى آخر عمره إلى مدينة قريبة من مدينته اسمها إقنا، وأقام بها لاشتهار كلمة السّنة بها، إلى أن توفىّ- رحمه الله- فيما بلغنى قريبا من سنة ستمائة «٢»، بعد أن طعن فى السن، وكف بصره.','2','74',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (477,'<span data-type="title" id=toc-358>٢٩٥ - الشّمر بن نمير النحوىّ المقرىء [١]</span>
كان من أهل العلم بالعربية واللغة، ورحل من قرطبة بعد التأدب بها إلى المشرق، فلقى رجالا من أهل الحديث، منهم حسين «١» بن [أبى «٢»] ضميرة، مولى رسول الله صلى الله عليه وسلم. واستوطن مصر، وروى عنه عبد الله بن وهب «٣» وغيره من نظرائه، وتوفى هنالك. وبقى له بالأندلس ابن يسمى عبد الرحمن، وكان يؤدّب ابن أبى عبده، واتصل بالأمير عبد الرحمن «٤» بن الحكم قبل أن يلى الأمر، فلما ولى قربه من تخصصه، وأنسه به.
وكان من ألطف الناس محلا عنده، وكان شاعرا مفلقا. وروى أن عبد الرحمن ابن الحكم أجنب فى بعض غزواته فلما قضى طهره، بعث فى طلب عبد الرحمن ابن الشّمر، فدخل [و «٥»] الوصيف يخفّف شعره، فقال له ابن الشّمر:
شاقك من قرطبة السّارى ... فى الليل لم يدر به دار
_________
[١] ترجمته فى بغية الوعاة ٢٦٧، وتاريخ علماء الأندلس ١: ١٦٦، وتلخيص ابن مكتوم ٨٢ - ٨٣، وطبقات الزبيدى ١٧٥، ولسان الميزان ٣: ١٥٣، وميزان الاعتدال ١: ٤٠٤.
وما ذكره المؤلف هنا يوافق ما فى طبقات الزبيدى.','2','75',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (478,'فأجابه بديهة:
زار فحيّا فى ظلام الدّجى ... أهلا به من زائر سار
فانصرف من غزوته، واستناب على الجيش من يقدم به إلى جلّيقية «١»

<span data-type="title" id=toc-359>٢٩٦ - شبيل بن عزرة الضّبعىّ [١]</span>
من خطباء الخوارج وعلمائهم. صاحب غريب، وهو القائل قصيدة الغريب.
وكان أوّلا شيعيا نحو سبعين سنة، ثم انتقل إلى الشّراة «٢»، وقال:
برئت من الروافض فى القيامه ... وفى دار المقامة والسلامه
أقام بالبصرة، وأخذ الناس عنه الغريب، ولم يزل بها إلى أن مات. وخلّف بها عقبا.

<span data-type="title" id=toc-360>٢٩٧ - شبيل بن عبد الرحمن الأديب النحوىّ النيسابورىّ [٢]</span>
ذكره الحاكم أبو عبد الله بن البيّع فى تاريخ نيسابور، وسماه «النحوى»، وقال عنه:
«سمع أبا عاصم الضّحّاك بن مخلد، وعبد الملك بن قريب الأصمعىّ. وروى عنه الحسن بن منصور السّلمىّ ومحمد بن عبد الوهاب العبدىّ».
_________
[١] ترجمته فى الاشتقاق ١٩٣، والبيان والتبيين ١: ٣٤٣، وتلخيص ابن مكتوم ٨٣، والحيوان ١: ٣١٣ - ٣١٤، والفهرست ٤٥، واللآلى ١٩٤ - ١٩٥. وانظر الأغانى ٢١: ٥٧، والأمالى ١: ٤٨، وخزانة الأدب ١: ٤٣. وما ذكر المؤلف يشبه ما فى البيان والتبيين والحيوان والفهرست. وقد ورد اسمه محرّفا فى الخزانة (شبل بن عمر)، وفى الأمالى (شبيل بن عمرو)، وفى الفهرست (شبيل بن عرعرة)، وفى القاموس (شبيل بن عروة)، والصواب ما ذكره المؤلف؛ كما ضبطه ابن دريد فى الاشتقاق ص ١٩٣، ونبه عليه الزبيدى فى تاج العروس.
[٢] ترجمته فى تلخيص ابن مكتوم ٨٣.','2','76',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (479,'<span data-type="title" id=toc-361>٢٩٨ - شمر أبو عمرو بن حمدويه الهروىّ اللغوىّ [١]</span>
الأديب الفاضل الكامل. إليه الرحلة فى هذا الفنّ من كل مكان، وكانت له عناية صادقة بهذا الشأن. رحل إلى العراق فى عنفوان «١» شبابه، فكتب الحديث، ولقى ابن الأعرابى وغيره من اللغويين، وسمع دواوين الشعر من وجوه شتى، ولقى جماعة من أصحاب أبى عمرو الشيبانىّ وأبى زيد الأنصارىّ وأبى عبيدة والفرّاء؛ منهم الرّياشىّ وأبو حاتم وأبو نصر وأبو عدنان وسلمة بن عاصم وأبو حسان.
ثم لما رجع إلى خراسان لقى أصحاب النّضر بن شميل، والليث بن المظفّر، فاستكثر منهم.
ولما ألقى عصاه بهراة «٢» ألف كتابا كبيرا فى اللغات أسسه على حروف المعجم، وابتدأ بحرف الجيم، فأشبعه وجوّده، إلا أنه طوّله فى الشواهد والشعر والروايات الجمة على أئمة العرب وغيرهم من المحدثين، وأودعه من تفسير القرآن بالروايات عن المفسرين، ومن تفسير غريب الحديث أشياء لم يسبقه إلى مثلها أحد تقدّمه، ولا أدرك شأوه فيه من بعده. ولما أكمل الكتاب ضنّ به فى حياته، ولم ينسخه طلابه، فلم يبارك له فيما فعله، حتى مضى لسبيله، فاختزل بعض أقار به ذلك الكتاب من تركته، واتصل بيعقوب «٣» بن الليث السّجزىّ، فقلده بعض أعماله، واستصحبه إلى فارس ونواحيها، وكان لا يفارقه ذلك الكتاب فى سفر ولا حضر.
_________
[١] ترجمته فى إشارة التعيين الورقة ٢١ - ٢٢، وبغية الوعاة ٢٢٦ - ٢٢٧، وتهذيب اللغة للأزهرى ١: ١٢، وكشف الظنون ١٤١٠، ومعجم الأدباء ١١: ٢٧٤ - ٢٧٥، ونزهة الألباء ٢٥٩ - ٢٦١. وما ذكره المؤلف يوافق ما فى مقدّمة تهذيب اللغة للأزهرى.','2','77',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (480,'ولما أناخ يعقوب بن الليث بسيب بنى ماوان من أرض السّواد وحطّ بها سواده، وركب فى جماعة من المقاتلة من عسكره مقدّرا لقاء الموفّق «١» وأصحاب السلطان فجّر الماء من النّهروان «٢» على معسكره، فغرق ذلك الكتاب فى جملة ما غرق من سواد العسكر.
قال الأزهرىّ: «ورأيت أنا من أوّل ذلك الكتاب تفاريق أجزاء بخط محمد بن قسورة، فتصفّحت أبوابها فرأيتها فى غاية الكمال. والله يغفر لأبى عمرو، ويتغمد زلّته. والضنّ بالعلم غير محمود ولا مبارك فيه». وتوفى شمر سنة خمس وخمسين ومائتين.

<span data-type="title" id=toc-362>٢٩٩ - شريح بن أحمد الشّجرى الأديب [١]</span>
ذكره الباخرزىّ وسجع له فقال: «أنجبت «٣» به ولاية نيمروز «٤»، فسار ذكره وطار، وملأ الأقطاب والأقطار، فكم من أدب أفاد، وشرح به كاسمه الفؤاد. وكان فى الشّعر قصير النّفس، ولم يكن يظفر به الرواة إلا فى الخلس، فمما أنشدنى له بهراة قوله فى العبدلكانى الزوزنىّ:
عبدلكانينا محلّى ... بالعلم والجانب العفيف
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨٣، ودمية القصر ٣٠٨، وذكره باسم «شريح بن عليم».','2','78',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (481,'مكحّل العين زوزنىّ ... [مذهبه «١»] مذهب المضيف
وله فى الزهد:
قد طال فى الذنب عمرى ... وما ارعويت فويحى
وفاض دمعى بسيل ... إذ جاد طرفى بسيح
وقد عدمت صريح ال ... تّقى فجئت بصبح
وليس يجدى صراخى ... وليس ينفع صيحى
فمنّ يا ربّ واشرح ... بالعفو صدر شريح','2','79',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (482,'<span data-type="title" id=toc-363>(حرف الصاد)</span>
<span data-type="title" id=toc-364>٣٠٠ - صالح بن اسحاق أبو عمر الجرمىّ النحوىّ [١]</span>
صاحب الكتاب المختصر فى النحو. بصرىّ قدم بغداذ، وناظر بها يحيى بن زياد الفرّاء. وقيل: إنه مولى بجيلة بن أنمار بن أراش بن الغوث بن خثعم.
وقيل له الجرمىّ؛ لأنه نزل فى جرم.
وكان ممّن اجتمع له مع العلم صحة المذهب وصحة الاعتقاد. وقيل إنه مولى لجرم «١» بن ربّان. وجرم من قبائل اليمن.
أخذ أبو عمر عن الأخفش وغيره. ولقى يونس بن حبيب، ولم يلق سيبويه.
وأخذ اللغة عن أبى عبيدة وأبى زيد والأصمعى وطبقتهم. وكان ذا دين وأخا ورع.
_________
[١] ترجمته فى أخبار أصبهان ١: ٣٤٦ - ٣٤٧، وأخبار النحويين البصريين للسيرافى ٧٢ - ٧٤، وإشارة التعيين الورقة ٢٢، والأنساب للسمعانى ١٢٨ ا، وبغية الوعاة ٢٦٨، وتاريخ بغداد ٩: ٣١٣ - ٣١٥، وتلخيص ابن مكتوم ٨٤، وابن خلكان ١: ٢٢٨، وروضات الجنات ٣٣٤ - ٣٣٥، وشذرات الذهب ٢: ٥٧، وطبقات الزبيدى ٤٦ - ٤٧، وطبقات ابن قاضى شهبة ٢: ٤ - ٥، وطبقات القرّاء لابن الجزرى ١: ٣٣٢، وعيون التواريخ (وفيات سنة ٢٢٥)، والفهرست ٥٦ - ٥٧، وكشف الظنون ٤، ٤٩٣، ١٦٣٠، واللباب لابن الأثير ١: ٢٢٢ - ٢٢٣، ومراتب النحويين ١٢٢، ومرآة الجنان ٢: ٩٠ - ٩١، والمزهر ٢: ٤٠٨، ٤١٩، ٤٢٨، ٤٦٣، ومسالك الأبصار ج ٤ مجلد ٢: ٢٨٤ - ٢٨٥، ومعجم الأدباء ١٢: ٥ - ٦، والنجوم الزاهرة ٢: ٢٤٣، ونزهة الألباء ١٩٨ - ٢٠٣.','2','80',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (483,'قال المبرّد: كان الجرمىّ أثبت القوم فى كتاب سيبويه، وعليه قرأت الجماعة، وكان عالما باللغة، حافظا لها، وله كتب انفرد بها، وكان جليلا فى الحديث والأخبار، وله كتاب فى السيرة عجيب.
قال ابن قادم: قدم أبو عمر الجرمىّ على الحسن بن سهل، فقال لى الفرّاء:
بلغنى أن أبا عمر الجرمىّ قدم، وأنا أحبّ أن ألقاه. فقلت له: فإنى أجمع بينكما.
فأتيت أبا عمر فأخبرته، فأجاب إلى ذلك، وجمعت بينهما، فلما نظرت إلى الجرمىّ قد غلب الفرّاء وأفحمه، ندمت على ذلك. قال ثعلب: قلت له: ولم ندمت؟ فقال: لأن علمى علم الفرّاء، فلما رأيته مقهورا قل فى عينى، ونقص علمه عندى.
مات الجرمىّ فى سنة خمس وعشرين ومائتين. وكان أبو عمر فقيها فى الدين.
وله فى النحو كتاب جيد يعرف بالفرخ، معناه فرخ كتاب سيبويه.
وكان أغوص على الاستخراج من المازنىّ. وكان المازنىّ أخذ منه. وإليه وإلى المازنىّ انتهى علم النحو فى زمانهما.
واجتمع الأصمعىّ والجرمىّ، فقال الأصمعىّ: يا أبا عمر، كيف تنشد قول الشاعر «١»:
قد كنّ يخبأن الوجوه تسترا ... فاليوم حين بدين «٢» للنظّار «٣»','2','81',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (484,'أو «بدأن»؟ فقال له: بل «بدأن». قال الأصمعى،: أخطأت؛ إنما هو «بدون»، أى برزن وظهرن «١».
وقال له أبو عمر الجرمىّ: يا أبا سعيد؛ كيف تصغر «مختارا» فقال الأصمعى: «مخيتير»، فقال له الجرمىّ: أخطأت، إنما هو «مخيّر» لأن التاء فيه زائدة.
وللجرمىّ من الكتب التى صنفها: كتاب الفرخ. كتاب الأبنية.
كتاب العروض. كتاب مختصر نحو المتعلمين. كتاب غريب سيبويه.
وذكره الحافظ أبو نعيم فى تاريخ أصبهان فقال: «صالح بن إسحاق أبو عمر الجرمىّ النحوىّ. قدم أصبهان مع فيض بن محمد عند منصرفه من الحج، فأعطاه يوم مقدمه عشرين ألف درهم. وكان يعطيه كل سنة اثنى عشر ألف درهم.','2','82',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (485,'يؤخذ عنه النحو والغريب. روى عن يزيد «١» بن زريع وعبد الوارث بن سعيد والبصريين «٢»».

<span data-type="title" id=toc-365>٣٠١ - صالح بن عادى العذرىّ الأنماطىّ المصرىّ النحوىّ [١]</span>
العبد الصالح. شيخى نزيل قفط. أصله من قرى مصر الشمالية، وسكن سلفه مصر، وعانى هو صناعة الأنماط، وقرأ على المتأخرين من مشايخ ابن برّىّ
_________
[١] ترجمته فى بغية الوعاة ٢٦٩، وتلخيص ابن مكتوم ٨٤ - ٨٥، والطالع السعيد ١٣٩ - ١٤٠. والأنماطى، بفتح الألف وسكون النون: منسوب إلى بيع الأنماط، وهى الفرش التى تبسط.','2','83',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (486,'- رحمه الله- وأكمل الصناعة على ابن برّىّ، وكان النحو على خاطره طريا، وكتب بخطه أصوله وحشّاها، وكانت فى غاية التحقيق والصحة.
وكان كثير المطالعة لكتب النحو، وكان على غاية من الدين والورع والنزاهة وقيام الليل ولزوم سمت المشايخ الصالحين، مستجاب الدعوة.
وكان قد حج، واجتاز بعد الحج بقفط، فرغّبه أهلها فى المقام بين أظهرهم للإفادة، فأقام. وأخذه إليه القاضى الخطيب أبو الحسن على بن أحمد بن جعفر بن عبد الباقى العثمانىّ «١»، من ولد أبان بن عثمان القفطىّ، الذى ما رأيت أكمل منه أدبا، ولا أغزر فضلا وذكاء. وضمن له كفايته، فأقام عنده مقدار خمسين سنة على غاية ما يكون من الرفاهية والإكرام، وخلطه بأهله، وكان يخدمه بنفسه على جلالة قدره؛ والتزم له أدبا ما التزمه أحد لشيخه- فرحمهما الله، وعفا عنهما.
قرأنا عليه، واستفدنا منه. وكان يجلس للإفادة ما بين الظهر والعصر بجامع قفط، وانتفع ببركته كلّ من صحبه، وأدركه فى آخر عمره نوع من الفالج فاعتقل له لسانه عن بعض النطق. وبعد ذلك ما أخّر مجالسه المفيدة للطلبة. ولم يزل على إقامة وظائفه من العبادة والإفادة إلى أن توفى- رحمه الله- فى شهور سنة ثلاث وتسعين وخمسمائة، وقد بلغ سنا عالية، ودفن بقفط- غفر الله له، وأعاد على كل مستحقّ الرحمة والتوفيق.

<span data-type="title" id=toc-366>٣٠٢ - صيغون أبو محمد الخيارىّ النحوىّ القيروانىّ الإفريقىّ المغربىّ [١]</span>
أحد النحاة فى ذلك القطر، وله بينهم اشتهار وذكر.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨٥. والخيارى، بكسر الخاء وفتح الياء آخر الحروف وبعده ألف وراء: منسوب إلى الخيار بن مالك بن زيد بن كهلان.','2','84',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (487,'<span data-type="title" id=toc-367>٣٠٣ - صعودا [١]</span>
ولقبه أشهر من اسمه. واسمه محمد بن هبيرة الأسدىّ أبو سعيد. أحد العلماء بالنحو الكوفىّ واللغة، وكان منقطعا إلى عبد الله بن المعتز. وصنف كتاب مختصر ما يستعمله الكاتب، وهذبه عبد الله بن المعتز «١».

<span data-type="title" id=toc-368>٣٠٤ - صاعد بن الحسن الرّبعىّ اللغوىّ أبو العلاء [٢]</span>
من بلاد الموصل. قرأ ببلاده اللغة على مشايخها، وحفظ منها الكثير، وتفنن فى فنون من الأدب.
وكان فصيح اللسان، حاضر الجواب سريعه؛ يجيب عن كل ما يسأل عنه؛ غير متوقّف؛ فنسب لإكثاره إلى الكذب. وبلغه أن اللغة بالأندلس مطلوبة، والآداب هناك مرغوب فيها من ملوكها ورعيتها، فارتحل إلى الأندلس، ودخلها فى حدود سنة ثمانين وثلاثمائة. والمستولى على ولاية الأندلس يومئذ من بنى أمية
_________
[١] ترجمته فى بغية الوعاة ١١٠، (وترجم له ترجمة أخرى فى صفحة ٩٢ باسم محمد بن القاسم)، وتاريخ بغداد ٣: ٣٧٠ - ٣٧١، وتلخيص ابن مكتوم ٨٥، والفهرست ٧٤، ومعجم الأدباء ١٩: ١٠٥.
[٢] ترجمته فى إشارة التعيين الورقة ٢٢، وبغية الملتمس ٣٠٦ - ٣١١، وبغية الوعاة ٢٦٧ - ٢٦٨، وتلخيص ابن مكتوم ٨٥، وجذوة المقتبس الورقة ١٠٢ - ١٠٣، وابن خلكان ١: ٢٢٩، والذخيرة لابن بسام ج ٤ مجلد ١: ٢ - ١٣، وروضات الجنات ٣٣٣ - ٣٣٤، وشذرات الذهب ٣: ٢٠٦ - ٢٠٧، والصلة لابن بشكوال ١: ٢٣٥ - ٢٣٦، والفلاكة والمفلوكين ٧٦ - ٧٧، وكشف الظنون ١٢٦١، والمعجب ١٩ - ٢٤، ومعجم الأدباء ١١: ٢٨١ - ٢٨٦، والمكتبة الصقلية ٦٢٥ - ٦٢٦، ٦٤٤، ٦٥٩، ونفح الطيب ٤: ٧٥ - ٨٤، ٩٣ - ٩٦.','2','85',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (488,'هشام «١» بن الحكم المؤيّد، وواليه على ما وراء بابه المنصور بن أبى عامر «٢» - وكان صاعد حسن الشعر فكه المجالسة- فأكرمه المنصور، وأحسن إليه وزاد.
وكان صاعد حسن الطريقة فى استخراج ما فى أيدى الناس من الأموال، جميل التوصّل إلى ذلك؛ فمن ذلك أنه عمل قميصا من خرق الصّلات التى وصلت إليه من المنصور بن أبى عامر، ولبسه بحضرته، وأتبعه الشكر والثناء. فشكره المنصور على ذلك، وزاد فى رفده.
وقد ألف كتاب الفصوص على مثل نوادر أبى على القالىّ. وكان يصنّف كتبا فى أخبار العشاق، ويسمى أسماء غريبة لا أصل لها، وينسب إليها كلاما منظوما ومنثورا؛ يرصّعها من قوله وقول غيره؛ فمنها كتاب الهجفجف «٣»، وكتاب الجوّاس. وكان المنصور مغرما بكتاب الجوّاس «٤»، يقرأ عليه كل ليلة شىء منه.','2','86',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (489,'ولما مات المنصور لم يحضر صاعد مجلس أنس بعده. وقد كان أولاده تولوا الأمر، فاعتذر عن الحضور بألم ادّعاه فى ساقه، وكان يمشى على عصا، والتزم ذلك. ومن شعره «١» فى هذا المعنى:
إليك حدوت ناجية «٢» الرّكاب ... محمّلة أمانى كالهضاب
وبعت ملوك أهل الشرق طرّا ... بواحدها وسيّدها اللّباب «٣»
ومنها:
إلى الله الشّكيّة من شكاة ... رمت ساقى «٤» وجلّ بها مصابى
وأقصتنى عن الملك المرجّى ... وكنت أرمّ «٥» حالى باقتراب
حسبت المنعمين على البرايا ... فألفيت اسمه صدر الحساب
وما قدّمته إلا كأنى ... أقدّم تاليا أمّ الكتاب
ومما وجدته أن المنصور سأله يوما: هل رأيت فيما وقع لك من الكتب كتاب «القوالب والزوابل» لمبرمان بن يزيد؟ فقال: نعم رأيته ببغداذ فى نسخة لأبى بكر بن دريد، بخط كأكرع النمل، فى جوانبها علامات للوضاع هكذا وهكذا.
فقال له: أما تستحى أبا العلاء من هذا الكذب! هذا كتاب عاملنا ببلد كذا يذكر فيه أن الأرض قد قلبت وزبلت. فأخذت من قوله ما سألتك عنه. فأخذ يحلف أن القول صادق حقيقة.','2','87',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (490,'وسأله يوما- وقدّامه تمر يأكل منه-: ما «التمركل» فى كلام العرب؟ فقال:
يقال: «تمركل» الرجل؛ إذا التف بكسائه، فقال: إنما ركّبت له اسما من التّمر والأكل؛ فقال: قد وافق ذلك أمرا كان. وله من هذا كثير.
ولولا مزحه وكثرة ما كان يأتى به فى تصانيفه ما كان إلا عالما. وقد اختبرت الكتب المطولة فى اللغة وغيرها، فوجد فيها حقيقة ما اتهم فيه. وكان صاعد غير صاعد؛ فى النحو مقصرا، وباللغة قيما. وله يد طولى فى استنباط معانى الشعر.
ومن عجيب سعادته أنه أهدى إلى المنصور بن أبى عامر أيّلا، «١» وكتب معه:
يا حرز كلّ مخوّف وأمان ك ... لّ مشرّد ومعزّ كلّ مذلّل
جدواك إن تخصص به فلأهله ... وتعمّ بالإحسان كلّ مؤمّل
كالغيث طبّق «٢» فاستوى فى وبله ... شعث البلاد مع المراد «٣» المبقل
الله عونك ما أبرّك للهدى ... وأشدّ وقعك فى الضّلال المشعل
ما إن رأت عينى- وعلمك شاهدى ... شروى علائك فى معمّ مخول «٤»
أندى بمقربة كسرحان الغضا «٥» ... ركضا وأوغل فى مثار القسطل
مولاى مؤنس غربتى متخطّفى ... من ظفر أيامى ممنّع معقلى
عبد نشلت بضبعه «٦» وغرسته ... فى نعمة أهدى إليك بأيلّ «٧»','2','88',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (491,'سميته «غرسية» وبعثته ... فى حبله ليتاح فيه تفاؤلى
فلئن قبلت فتلك أسنى نعمة ... أسدى بها ذو منحة وتطوّل
صبحتك غادية السرور وجلّلت ... أرجاء ربعك بالسحاب المخضل
فقضى فى سابق علم الله أن غرسية بن شانجة من ملوك الروم- وهو أمنع من النّجم- أسر فى ذلك اليوم بعينه الذى بعث فيه صاعد بالأيّل، وكان ذلك فى ربيع الآخر سنة خمس وثمانين وثلاثمائة.
وخرج صاعد عن الأندلس فى أيام الفتنة، وقصد جزيرة صقلّيّة، فمات بها قريبا من سنة عشر «١» وأربعمائة- وقد أسنّ.
قال ابن حيّان «٢» مؤرّخ الأندلس: «وجمع أبو العلاء صاعد للمنصور محمد بن أبى عامر كتابا سماه الفصوص، فى الأدب والأشعار. وكان ابتداؤه له فى شهر ربيع الأوّل سنة خمس وثمانين، وأكمله فى شهر رمضان المعظم، وأثابه عليه بخمسة آلاف دينار فى دفعة، وأمره أن يسمعه الناس بالمسجد الجامع بالزهراء، واحتشد له جماعة أهل الأدب ووجوه الناس «٣»».','2','89',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (492,'قال ابن حيّان: «وقرأته عليه منفردا سنة تسع وتسعين وثلاثمائة». قال أبو محمد بن حزم «١»: «توفى صاعد- رحمه الله- بصقلّيّة فى سنة تسع عشرة وأربعمائة».

<span data-type="title" id=toc-369>٣٠٥ - صالح الورّاق النيسابورىّ أبو إسحاق [١]</span>
هو تلميذ الشيخ أبى نصر إسماعيل بن حمّاد الجوهرىّ. كان أديبا فاضلا وصاحب خط جيّد صحيح. لازم الجوهرىّ، وأخذ عنه كتابه فى اللغة المسمّى الصّحاح، وغيره.
وكان صاحب أدب وشعر؛ فمن أشعاره ما أنشده له الأديب يعقوب بن أحمد- وهو أحسن ما قيل فى معنى دودة القزّ:
وبنات جيب ما انتفعت بعيشها ... ووأدتها «٢» فنفعننى بقبور
ثم انبعثن عواطلا فإذا لها ... قرن الكباش إلى جناح طيور
وله يهجو ابن زكريا المتكلم الأصبهانىّ:
أبا أحمد يا أشبه الناس كلّهم ... خلاقا وخلقا بالرّخال «٣» النواسج «٤»
لعمرك ما طالت بتلك اللّحى لكم ... حياة ولكن بالعقول الكواسج «٥»
_________
[١] ترجمته فى تلخيص ابن مكتوم ٣٥، ودمية القصر ٣٠٤، ٣٠٨، (وذكره باسم أبى صالح الورّاق)، وطبقات ابن قاضى شهبة ١: ١٧٠، ومعجم الأدباء ١: ١٦٢. وسبق للمؤلف ترجمته فى الجزء الأوّل ص ١٦٩ - ١٧٠ واسمه هناك «إبراهيم بن صالح أبو إسحاق النيسابورى الورّاق».','2','90',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (493,'<span data-type="title" id=toc-370>(حرف الضاد)</span>
<span data-type="title" id=toc-371>٣٠٦ - الضحاك أبو عاصم النبيل [١]</span>
كان قد نيّف على التسعين «١»، وهو ذكىّ بعلم الأدب والشعر وأيام العرب.
وهو أحد الرواة للحديث «٢».
وقال أبو زيد الأنصارىّ: كان أبو عاصم ضعيف العقل فى حديثه، وكان يطلب العربية، فيقال له: كيف تصغّر الضحاك؟ وهو اسمه- فيقول:
«ضحيكيك»، ثم «٣» تنبّل، فكان يزرى على غيره.
_________
[١] ترجمته فى الأنساب ٥٥٢ ب- ٥٥٣ ا، وبغية الوعاة ٢٧٠، وتاريخ الإسلام للذهبى (وفيات سنة ٢١٢)، وتذكرة الحفاظ ١: ٣٣٣ - ٣٣٤، وتقريب التهذيب ١١٦، وتلخيص ابن مكتوم ٨٦، وتهذيب التهذيب ٤: ٤٥٠ - ٤٥٣، والجواهر المضية ١: ٢٦٣ - ٢٦٥، وخلاصة تذهيب الكمال ١٤٩ - ١٥٠، وطبقات الزبيدى ٢٨ - ٢٩، وطبقات ابن قاضى شهبة ٢:
٦ - ٧، وعيون التواريخ (وفيات سنة ٢١٢)، ومروج الذهب ٢: ٣٤٠، ومعجم الأدباء ١٢:
١٥، والنجوم الزاهرة ٢: ٢٠٤، ٢٠٧. وهو- كما فى الأنساب-: الضحاك بن مخلد بن الضحاك ابن مسلم بن رافع بن رفيع بن الأسود بن عمرو بن رالان بن سلال بن ثعلبة بن شيبان الشيبانى النبيل البصرى.
واختلفوا فى سبب تلقيبه بالنبيل؛ فروى السمعانى فى الأنساب أنه سئل: لم سميت نبيلا، قال: كنا أبوى عاصم عند ابن جريج، وكنت أتجمل فى الثياب، فقال يوما: أين أبو عاصم النبيل؟ فسميت بنبيل. وفى تذكرة الحفاظ: «سمى نبيلا لنبله وعقله». وفى الجواهر المضية: «لقبته جارية لزفر الفقيه بذلك».','2','91',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (494,'<span data-type="title" id=toc-372>(حرف الطاء)</span>
<span data-type="title" id=toc-373>٣٠٧ - الطّوال النحوىّ الكوفىّ [١]</span>
ويكنى أبا عبد الله. من أصحاب محمد بن زياد الفرّاء النحوىّ. قال أبو العباس ثعلب: كان الطّوال حاذقا بإلقاء المسائل العربية، وكان سلمة «١» حافظا لتأدية ما فى الكتب، وكان ابن قادم «٢» حسن النظر فى العلل؛ وهؤلاء الثلاثة الأجلّاء من أصحاب الفرّاء. ولم يشتهر للطّوال تصنيف.

<span data-type="title" id=toc-374>٣٠٨ - طالب بن عثمان بن محمد بن أبى طالب أبو أحمد الأزدىّ النحوىّ المقرىء المؤدّب [٢]</span>
بغداذىّ. تصدر لإقراء القرآن والنحو. وتأدّب به جماعة، وكفّ بصره فى آخر عمره. وكان ثقة فى الرواية. مات فى سنة ست وتسعين وثلاثمائة.
_________
[١] ترجمته فى بغية الوعاة ٢٠، وتلخيص ابن مكتوم ٨٥، وطبقات الزبيدى ٩٦، وطبقات ابن قاضى شهبة ٢: ٣٣٩، والفهرست ٦٨. قال ابن مكتوم: «واسم الطوال محمد بن أحمد أبو عبد الله.
من أهل الكوفة. قدم بغداد، ولقى الأصمعى وروى عنه، وسمع منه أبو عمر حفص بن عمر المقرئ. ومات يوم الجمعة سلخ محرم سنة ثلاث وأربعين ومائتين».
[٢] ترجمته فى بغية الوعاة ٢٧١، وتاريخ بغداد ٩: ٣٦٥ - ٣٦٦، وتلخيص ابن مكتوم ٨٦، وطبقات القرّاء لابن الجزرىّ ١: ٣٣٨، ومعجم الأدباء ١٢: ١٦ - ١٧، ونزهة الألباء ٤٠٩ - ٤١٠.','2','92',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (495,'<span data-type="title" id=toc-375>٣٠٩ - طلحة بن كردان النحوىّ [١]</span>
نحوى عراقى مشهور. تصدر للإقراء والإفادة والرواية. من أصحاب أبى سعيد السّيرافىّ. [قال]: أنشد أبو بكر بن دريد لنفسه «١»:
لا تحتقر عالما وإن قصرت ... رتبته عن لحاظ رامقه «٢»
وانظر إليه بعين ذى كرم «٣» ... مهذّب الخلق «٤» فى طرائقه
فالمسك بينا تراه ممتهنا ... بفهر «٥» عطّاره وساحقه
حتى تراه بعارضى ملك ... وموضع التاج من مفارقه

<span data-type="title" id=toc-376>٣١٠ - طلحة بن محمد بن النّعمانىّ أبو محمد [٢]</span>
من النّعمانية؛ بلدة بين بغداذ وواسط. كان فاضلا عارفا باللغة والأدب، حسن الشعر، رقيق الطبع، كثير المحفوظ.
خرج إلى خراسان، وأقام ببلادها مدّة، وكانت ألسنة الفضلاء بها متفقة على الثناء عليه، والإطناب فى جودة شعره، وسرعة خاطره بالنّظم. ودخل خوارزم
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨٦.
[٢] ترجمته فى بغية الوعاة ٢٧٣، وتلخيص ابن مكتوم ٨٦، وخريدة القصر ١: ٥٢ - ٥٩، وطبقات ابن قاضى شهبة ٢: ٩، وعيون التواريخ (وفيات سنة ٥٠٩)، ومعجم الأدباء ١٢: ٢٦ - ٢٧، ونزهة الألباء ٤٦٠ - ٤٦١.','2','93',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (496,'وكان يوما يمشى فى سوق العشاق، فاستقبلته عجلة»
عليها حمار ميّت يحمله الدبّاغون إلى الصحراء لسلخه، فقال أبو عمرو عثمان بن محمد بن أحمد البقالىّ- وكان يمشى معه- فى ذلك:
يا حاملا صرت محمولا على عجله
فقال أبو محمد طلحة بن محمد بن النّعمانىّ مجيبا له:
وافاك موتك منتابا على عجله
وبلغ قولهما إلى الشريف أبى القاسم الفخر بن محمد العلوىّ، فقال:
والموت لا تتخطّى الحىّ رميته ... ولو تباطأ عنه الحىّ أزعج له

<span data-type="title" id=toc-377>٣١١ - طاهر بن محمد الرقبانىّ الصّقلىّ اللغوىّ [١]</span>
من أهلها المقيمين بها. تغلبىّ يدعى الوزير. لم يكن فى زمانه أعلم منه بلغة العرب وكلامها، ونثرها ونظامها. وكان رئيسا مقدّما جليلا معظما، وقصدته العلماء من كل مكان، فلقوا منه بحرا خضرما، «٢» وانتجعته الشعراء فوردوا قليبا «٣». وله شعر كان يخفيه، منه:
ألا أيّها القاضى الرفيع مناره ... ويا واطئا مجدا مناط الكواكب
أغثنى برأى منك يفرج كربتى ... وحل محسنا بينى وبين النوائب
وداركنى «٤» نحس الزمان فنحّه ... فما زلت قرنا «٥» للزمان المحارب
وعش سالما للجود ترأب صدعه ... طوال الليالى منعما غير سالب
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨٧، ومختصر المنتخل من الدرة الخطيرة الورقة ٨، والمكتبة الصقلية ٦٤٥. والرقبانىّ (فى الأصل): عظيم الرقبة.','2','94',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (497,'<span data-type="title" id=toc-378>٣١٢ - طاهر بن أحمد بن بابشاذ أبو الحسن النحوىّ المصرىّ [١]</span>
العلامة المشهور المذكور. أصله من العراق، وكان جدّه أو أبوه قدم مصر تاجرا. وكان جوهريا فيما قيل.
وطاهر هذا ممن ظهر ذكره؛ وسارت تصانيفه؛ مثل المقدّمة «١» فى النحو وشرحها، وشرح الجمل «٢» للزّجّاجىّ؛ سار كل منهما مسير الشمس.
وقد كان يتولى تحرير الكتب الصادرة عن ديوان الإنشاء فى الدولة القصرية بالديار المصرية إلى الأطراف؛ ليصلح ما لعلّه يجد بها من لحن خفىّ. وكان له على ذلك رزق سنىّ؛ مع رزقه على التصدّر للإقراء فى جامع عمرو بن العاص. واشتمل على العبادة والمطالعة.
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٢ - ٢٣، وبغية الوعاة ٢٧٢، وتلخيص ابن مكتوم ٨٧ - ٨٨، وحسن المحاضرة ١: ٢٢٨، وابن خلكان ١: ٢٣٥، وروضات الجنات ٣٣٨، وشذرات الذهب ٣: ٣٣٣ - ٣٣٤، وطبقات ابن قاضى شهبة ٢: ٧ - ٨، والفلاكة والمفلوكين ١١٦، وكشف الظنون ١١١، ٤٢٣، ٦٠٣، ١٦١٢، ١٨٠٤، ومرآة الجنان ٣: ٩٨، ومسالك الأبصار ج ٤ مجلد ٣: ٤٥٩ - ٤٦١، ومعجم الأدباء ١٢: ١٧ - ١٩ والنجوم الزاهرة ٥: ١٠٥، ونزهة الألباء ٤٣٢ - ٤٣٣، والوافى بالوفيات ج ٥ مجلد ١: ٢٠٥. قال ابن خلكان: «وبابشاذ، بباءين موحدتين، بينهما ألف ثم شين معجمة، وبعد الألف الثانية ذال معجمة. وهى كلمة عجمية، تتضمن الفرح والسرور».','2','95',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (498,'وجمع فى حالة انقطاعه تعليقة كبيرة فى النحو؛ قيل لنا: لو بيّضت قاربت خمسة عشر مجلدا، وسماها النحاة بعده الذين وصلت إليهم تعليق الغرفة وانتقلت هذه التّعليقة إلى تلميذه أبى عبد الله محمد بن بركات السعيدىّ النحوىّ اللغوىّ المتصدّر بموضعه والمتولى للتحرير. ثم انتقلت بعد ابن البركات المذكور إلى صاحبه أبى محمد عبد الله بن برّىّ النحوىّ المتصدّر فى موضعه والمتولّى للتحرير. ثم انتقلت بعده إلى صاحبه الشيخ أبى الحسين النحوىّ المنبوز بثلط «١» الفيل، المتصدّر فى موضعه.
وقيل إن كل واحد من هؤلاء كان يهبها لتلميذه المذكور، ويعهد إليه بحفظها. ولقد اجتهد جماعة من طلبة الأدب فى انتساخها، فلم يمكن.
ولما توفى أبو الحسين النحوىّ المقدّم ذكره، وبلغنى ذلك وأنا مقيم بحلب أرسلت من أثق به، وسألته تحصيل تعليق الغرفة بأى ثمن بلغت، وكتاب التذكرة لأبى علىّ. فلما عاد ذكر أن الكتابين وصلا إلى ملك مصر الكامل محمد «٢» بن العادل أبى بكر بن نجم الدين أيوب، فإنه يرغب فى النحو وغريب ما صنّف فيه.
وذكر أن سبب تزهّد طاهر بن بابشاذ- رحمه الله- أنه كان له قطّ قد أنس به وربّاه أحسن تربية، فكان طاهر الخلق، لا يخطف شيئا، ولا يؤذى على عادة القطط. وأنه يوما اختطف من يديه فرخ حمام مشوىّ، فعجب له، ثم عاد بعد أن غاب ساعة، فاختطف فرخا آخر وذهب؛ فتتبعه الشيخ إلى خرق فى البيت، فرآه قد دخل الخرق، وقفز منه إلى سطح قريب، وقد وضع الفرخ بين يدى قطّ هناك. فتأمله الشيخ فإذا القط أعمى مفلوج لا يقدر على الانبعاث.','2','96',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (499,'فتعجّب، وحضره قلبه، وقال: من لم يقطع بهذا القط- وقد سخّر له غيره يأتيه برزقه، ويخرج عن عادته المعهودة منه لإيصال الراحة إليه- لجدير ألّا يقطع بى! وأجمع رأيه على التخلّى والانفراد بعبادة الله. وضمّ أطرافه وباع ما حوله، وأبقى ما لا بدّ من الحاجة إليه، وانقطع فى غرفة بجامع عمرو، وأقام على ذلك مدّة.
ثم خرج ليلة من الغرفة إلى سطح الجامع، فزلّت رجله من بعض الطاقات المؤدّية للضوء إلى الجامع، فسقط وأصبح ميتا قد رزق الشهادة- رحمه الله. قيل:
وكان ذلك فى سنة أربع وخمسين وأربعمائة، وقيل بعد ذلك «١». والله أعلم.','2','97',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (500,'<span data-type="title" id=toc-379>(حرف العين)</span>
<span data-type="title" id=toc-380>٣١٣ - عبد الله بن إبراهيم بن عبد الله الخبرىّ المعلّم أبو حكيم [١]</span>
وخبر إحدى بلاد فارس. كان يسكن درب الشاكرية ببغداذ، وكانت له معرفة تامة بالفرائض والأدب واللغة، وكان مرضىّ الطريقة ديّنا. سمع الكثير من مشايخ زمانه. وهو جدّ محمد بن ناصر السّلامىّ «١» لأمه. وروى عنه محمد. وكان يكتب خطا حسنا صحيحا «٢».
_________
[١] ترجمته فى الأنساب ١٨٨ ا، وبغية الوعاة ٢٧٦، وتلخيص ابن مكتوم ٨٨، وطبقات الشافعية ٣: ٢٠٣ - ٢٠٤، وكشف الظنون ٦٩٢، ٧٧٩، واللباب ١: ٣٤٣، ومعجم الأدباء ١٢: ٤٦ - ٤٧.','2','98',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (501,'<span data-type="title" id=toc-381>٣١٤ - عبد الله بن أحمد بن أحمد بن أحمد بن الخشاب أبو محمد النحوىّ البغداذىّ [١]</span>
كان أديبا فاضلا عالما، له معرفة جيدة بالنحو واللغة والعربية والشعر والفرائض والحساب والحديث، حافظا لكتاب الله عز وجل، قد قرأه بالقراءات الكثيرة.
أخذ النحو عن أبى بكر بن جوامرد القطّان، ثم عن أبى الحسن على بن أبى زيد الفصيحىّ الأستراباذىّ، ثم عن الشريف أبى السعادات الشّجرىّ، وقاطعه وردّ عليه فى أماليه. وقرأ اللغة على أبى على الحسن بن على المحوّلىّ، وعلى أبى منصور الجواليقىّ وغيرهما.
وسمع الحديث من مشايخ وقته وأكثر، وكان حريصا على السماع، مداوما بالقراءة على المشايخ فى علوّ سنه. أقرأ الناس مدة، وتخرّج به جماعة فى علم النحو، وحدّث الكثير، ووصف بالفضل والعلم والمعرفة، وكان مطّرحا للتكلف فى مأكله وملبسه وحركاته، فيه بذاذة «١»، وكان يكثر لعب الشّطرنج، ويقعد لذلك أين وجده، ولا يراعى خسّة اللاعب والموضع، ويقف على حلق الطرق والمشعوذين وغير ذلك، وكان كلامه فى حلق الإفادة أجود من قلمه.
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٣، وبغية الوعاة ٢٧٦ - ٢٧٧، وتاريخ ابن الأثير ٩: ١١٤، وتاريخ الإسلام للذهبى (وفيات سنة ٥٦٧)، وتاريخ أبى الفدا ٣: ٥٢، وتلخيص ابن مكتوم ٨٨ - ٨٩، وخريدة القصر ١: ٨٢، وابن خلكان ١: ٢٦٧ - ٢٦٨، وطبقات ابن قاضى شهبة ٢: ١٧ - ٢٠، والفلاكة والمفلوكين ٧٨ - ٧٩، وكشف الظنون ١٠٨، ٦٠٢، ١٥٦٣، ١٧٩١، ١٧٩٥، ومرآة الجنان ٣: ٣٨١ - ٣٨٢، ومسالك الأبصار ج ٤ مجلد ٢: ٣١١ - ٣١٦، والمستفاد من ذيل تاريخ بغداد الورقة ٤٠، ومعجم الأدباء ١٢: ٤٧ - ٥٣، والمنتظم (وفيات سنة ٥٦٧)، والنجوم الزاهرة ٦: ٦٥.','2','99',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (502,'وكان ضيّق العطن ضجورا؛ ما صنّف تصنيفا فكمله. شرح كتاب الجمل لعبد القاهر الجرجانىّ، وترك أبوابا من وسط الكتاب ما تكلّم عليها، وقرئ عليه المصنّف، وكتب بخطه عليه وهو على هذه الصورة، غير معتذر من ذلك بعذر.
وشرح المقدّمة التى صنّفها الوزير ابن هبيرة «١»، وقطعها قبيل الإتمام، ووصل منها إلى باب النونين: الثقيلة والخفيفة، وعمل فى شرح اللمع «٢» مثل ذلك «٣».
وكانت له دار عتيقة ولأخ له ومن شاركهما فى ورثة أبيه، وله منها صفّة «٤» كبيرة منفردة، وبها بوارىّ «٥» قصب مفروشة، وفى صدرها ألواح من الخشب، مرصوص عليها كتب له، أقامت عدّة سنين ما أزيل عنها الغبار، وكانت تلك البوارىّ قد استترت بما عليها من التراب، يقعد فى جانب منها، والباقى على تلك الحالة. وقيل إن الطيور عششت فوق الكتب وفى أثنائها، وكان إذا تكلم على','2','100',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (503,'مسألة فى النحو منفردة ربما أجاد فى بعض الأوقات إذا خلا من ضجره، وكان لا يقتنى من الكتب إلا أردأها صورة، وأرخصها ثمنا. وله وظيفة فى بعض الأماكن ببغداذ. وتدل كتبه على صحّة ما ذكرته. وله شعر كشعر النحاة، فمنه ما قال ملغزا فى الكتاب:
وذى أوجه لكنّه غير بائح ... بسرّ وذو الوجهين للمرء يظهر
تناجيك بالأسرار أسرار «١» وجهه ... فتفهمها ما دمت بالعين تنظر
وله فى الشمعة:
صفراء لا من سقم مسّها ... كيف وكانت أمها «٢» الشافيه
عريانة باطنها مكتس ... فاعجب لها كاسية عاريه
توفى- رحمه الله وتجاوز عنه- فى عشية الجمعة، ثالث شهر رمضان سنة سبع وستين وخمسمائة بباب الأزج بدار أبى القاسم بن الفرّاء، وصلّى عليه يوم السبت بجامع السلطان، وتقدّم فى الصلاة عليه أبو النجم بن القابلة، ودفن بمقبرة أحمد، بباب حرب.
قال عبد الكريم بن محمد المروزىّ «٣»: عبد الله بن أحمد بن أحمد بن الخشاب أبو محمد، من ساكنى باب المراتب الشريفة، شاب «٤» كامل فاضل، له معرفة تامّة','2','101',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (504,'بالأدب واللغة والنحو والحديث، ويقرأ الحديث قراءة «١» حسنة صحيحة مفهومة.
سمع الكثير بنفسه، وجمع الأصول الحسان.
قال الإمام أبو شجاع عمر بن أبى الحسن البسطامىّ ببخارى: لما دخلت بغداذ قرأ علىّ أبو محمد الخشاب كتاب غريب الحديث لأبى محمد القتيبىّ قراءة ما سمعت قبلها مثلها فى الصحة والسرعة، وحضر جماعة من الفضلاء سماعها، وكانوا يريدون أن يأخذوا عليه فلتة لسانه، فلم يقدروا على ذلك.
أنبأنا محمد بن محمد بن حامد فى كتابه «٢» قال: «عبد الله بن أحمد بن أحمد بن أحمد بن عبد الله الخشاب. من أهل بغداذ. شيخنا فى علم الأدب، أعلم الناس بكلام العرب، وأعرفهم بعلوم شتى من النحو واللغة والتفسير والحديث والنسب، الطود السامى، والبحر الطامى. كان فضله على أفاضل الزمان، كفضل الشمس على النجوم، والبحر على الغدران. وله المؤلفات العزيزة، والمصنفات الحريزة، والكتب المفيدة، والفكر المجيدة. وإذا كتب كتابا بخطه يشترى بالمئين، وتتنافس عليه بواعث المستفيدين. وهو ألين سجيّة من الماء العذب، وأخشن حميّة من الغرار «٣» العضب. وما أظن أن الزمان يسمح بمثله، وأنّ الدهر العقيم ينتج أحدا فى فضله. كان كثير الإفادة، غزير الإجادة؛ غير أنه ينبو عن جواب سؤال الممتحنين، نبوة المستحقر المهين، ويعزّ على المتكبّر، ويذلّ للمتكّرم، متواضع عند العامة، مرتفع عند الملوك والخاصة. توفى ببغداذ سنة ثمان وستين وخمسمائة «٤»، فرأيته','2','102',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (505,'ليلة فى المنام كأنى أقول له: ما فعل الله بك؟ فقال: خيرا، فقلت: وهل يرحم الله الأدباء؟ قال: نعم، قلت: وإن كانوا مقصرين؟ قال: يجرى عتاب كثير ثم يكون النعيم».

<span data-type="title" id=toc-382>٣١٥ - عبد الله بن أسعد بن على بن عيسى أبو الفرج الموصلىّ الفقيه الشافعىّ المعروف بابن الدّهان [١]</span>
نحوىّ أديب، فاضل فقيه، شاعر. قدم الشام فى صحبة أبى سعد بن أبى عصرون «١» - وكان يلزم درسه- وسمع الحديث، وكتب بخطه، إلا أنه كان ضيّق العطن، ما كتب تصنيفا إلا اختصره برأيه، ولا يعنّ فيه أنه اختصره.
وكان يمدح الملوك. وهو الذى مدح الصالح بن رزّيك «٢» وزير مصر، وسيّر إليه المدحة، «٣» فسير إليه جملة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٨٩ - ٩٠، وخريدة القصر ٣: ٨٩ - ٩٩، وابن خلكان ١: ٢٥٦ - ٢٥٧، وطبقات ابن قاضى شهبة ٢: ٢٣ - ٢٤، والنجوم الزاهرة ٥: ٣٦٥ - ٣٦٦.','2','103',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (506,'وآخر أمره أنه تولى التدريس بحمص، وأقام بها إلى أن مات «١» هناك. وله أشعار. واستفيدت منه العربية، ودرسها بحمص فى جملة الفقه.

<span data-type="title" id=toc-383>٣١٦ - عبد الله بن أبى إسحاق الحضرمىّ، مولاهم [١]</span>
المقرئ النحوىّ العلامة فى علم العربية. بصرىّ؛ وهو فى أوّل الطبقة الرابعة من النحاة؛ لأنه أقدم اخذا فيمن شاركه فى الطبقة وأقدمهم موتا.
_________
[١] ترجمته فى أخبار النحويين البصريين للسيرافى ٢٥ - ٢٨، وبغية الوعاة ٢٨٢، وتاريخ ابن الأثير ٤: ٢٩٢، وتاريخ أبى الفدا ١: ٢٠٨، وتقريب التهذيب ١٢٥، وتلخيص ابن مكتوم ٩٠، وتهذيب التهذيب ٥: ١٤٨، وخزانة الأدب ١: ١١٥ - ١١٦، وخلاصة تذهيب الكمال ١٦٢، وطبقات الزبيدىّ ١١ - ١٣، وطبقات الشعراء لابن سلام ١١ - ١٣، وطبقات ابن قاضى شهبة ٢: ٢٢ - ٢٣، وطبقات القرّاء لابن الجزرىّ ١: ٤١٠، والفهرست ٤١، ومراتب النحويين ٢٠، ومسالك الأبصار ج ٤ مجلد ٢: ٢٦٩ - ٢٧٠، والمعارف ٢٣٠، والنجوم الزاهرة ١: ٣٠٣، ونزهة الألباء ٢٢ - ٢٥.','2','104',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (507,'والذين شاركوه فى العصر وعدّوا من الطبقة الرابعة أبو عمرو بن العلاء وعيسى بن عمر الثقفىّ وحماد بن سلمة وحمّاد بن الزّبرقان ومسلمة بن عبد الله. وكان لتقدّمه فى وقت الطلب زاحم عنبسة وميمونا الأقرن فى آخر عصرهما، فجعل فى أوّل هذه الطبقة.
أخذ قراءته عن يحيى بن يعمر ونصر بن عاصم. وقيل هو مولى حضرموت، وقيل مولى آل الحضرمىّ، وهم حلفاء بنى عبد شمس بن عبد مناف، ولذلك قال الفرزدق:
فلو كان عبد الله مولى هجوته ... ولكنّ عبد الله مولى مواليا «١»
وسئل يونس بن حبيب عن ابن أبى إسحاق وعلمه، فقال: هو والنحو سواء، أى هو الغاية، وقيل له: فأين علمه من علم الناس اليوم؟ قال: لو كان اليوم فى الناس أحد لا يعلم إلا علمه لضحك منه، ولو كان فيهم من له ذهنه ونفاذه ونظره كان أعلم الناس.
وقال أبو خليفة: قال ابن سلام: أوّل من بعج النحو ومدّ القياس وشرح العلل عبد الله بن أبى إسحاق، وكان معه أبو عمرو بن العلاء، وكان ابن أبى إسحاق أشدّ قياسا، وأبو عمرو أوسع علما بكلام العرب ولغتها وغريبها، وكان بلال بن','2','105',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (508,'أبى بردة جمع بينهما وهو على البصرة عامل لخالد بن عبد الله القسرىّ أيام هشام ابن عبد الملك. قال يونس: قال أبو عمرو: فغلبنى ابن أبى اسحاق يومئذ بالهمز، فنظرت فيه بعد ذلك، وبالغت فيه.
وحكى يونس أن أبا عمرو بن العلاء كان أشدّ تسليما للعرب، وكان ابن أبى إسحاق وعيسى بن عمر يطعنان على العرب، وكان عيسى يقول: أساء النابغة فى قوله حيث يقول: «فى أنيابها السم «ناقع» «١»؛ يقول: موضعها «ناقعا» «٢».
وكان ابن سيرين «٣» يبغض النحويين، وكان يقول: لقد بغّض إلينا هؤلاء المسجد، وكانت حلقته إلى جانب حلقة ابن أبى إسحاق.
وبلغ ابن أبى إسحاق أنه يعيب عليه تفسير الشعر ويقول: ما علمه بإرادة الشاعر! فقال ابن أبى إسحاق: إن الفتوى فى الشّعر لا تحلّ حراما، ولا تحرّم حلالا؛ وإنما نفتى فيما استتر من معانى الشعر، وأشكل من غريبه وإعرابه بفتوى سمعناها من غيرنا، أو اجتهدنا فيها آراءنا؛ فإن زللنا أو عثرنا فليس الزلل فى ذلك كالزلل فى عبارة الرؤيا، ولا العثرة فيها كالعثرة فى الخروج عما أجمعت عليه','2','106',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (509,'الأئمة من سنة الوضوء، وكرهته الجماعة من الاعتداء فى الطّهور. فبلغ ذلك ابن سيرين، فأقصر عما كان عليه من الإفراط فى الوضوء. وكان إذا جاءه الرجل يسأله عن الرؤيا، قال: هات حتى أظنّ لك.
وكان ابن أبى إسحاق بعد أن بلغه كلام ابن سيرين يقول: أظن الشاعر أراد كذا، واللغة توجب كذا. ثم اجتمع هو وابن سيرين فى جنازة، فقال ابن سيرين:
(كذلك «١» إنّما يخشى الله من عباده العلماء «٢»). فقال ابن أبى إسحاق: كفرت يا أبا بكر بعيبك على هؤلاء الذين يقيمون كتاب الله. فقال ابن سيرين: إن كنت أخطأت فأنا أستغفر الله. ورجع إلى حلقته.
وكان ابن أبى إسحاق يعتمد الإعراب فى عبارته حرفا واحدا، فمرّت به سنوّرة فقال: اخسى، فقال له: هذه، ألا قلت اخسئ «٣»!.
توفى عبد الله بن أبى إسحاق الحضرمىّ- رحمه الله- سنة سبع عشرة «٤» ومائة، وهو ابن ثمان وثمانين سنة، وصلى عليه بلال بن أبى بردة وهو أمير البصرة. وورث هذه العدة من السنين جماعة من نسله، فمنهم زيد بن عبد الله بن أبى إسحاق،','2','107',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (510,'مات وهو ابن ثمان وثمانين سنة، ومات يعقوب بن إسحاق بن زيد بن عبد الله ابن أبى إسحاق وهو إمام البصرة فى القراءة وهو ابن ثمان وثمانين سنة.
ومات عبد الله بن أبى إسحاق وقتادة بن دعامة فى يوم واحد، فشيّع الأدباء والأشراف جنازة ابن أبى إسحاق، وشيع النّساك والفقهاء جنازة قتادة بن دعامة.
قال ابن سلّام: قلت ليونس: هل سمعت من ابن أبى إسحاق شيئا؟ قلت له:
هل يقول أحد الصّويق- يعنى السّويق؟ قال: نعم، عمرو بن تميم تقولها. وما تريد [إلى] «١» هذا؟ عليك بباب من النحو يطّرد وينقاس.

<span data-type="title" id=toc-384>٣١٧ - عبد الله بن أبى سعيد الأنصارى الأندلسىّ النحوى الغريشىّ [١]</span>
نحوى فاضل. قرأ على مشايخ بلاده، ورحل إلى الشرق، ودخل مصر، وأفاد بها ونزل الإسكندرية.
أنبأنا أبو طاهر السّلفىّ نزيل الإسكندرية- رحمه الله- أنشدنا الفقيه الأديب أبو محمد عبد الله بن أبى سعيد الأنصارى الأندلسى النحوىّ- أبقاه الله- بمصر، أنشدنى عبد الحليم بن عبد الواحد الكاتب السّوسىّ بصقلّيّة لنفسه- وكتب لى بخطه:
_________
[١] ترجمته فى بغية الوعاة ٢٨٢ - ٢٨٣، وتلخيص ابن مكتوم ٩٠ - ٩١، وطبقات ابن قاضى شهبة ٢: ٣٦، ومعجم السفر للسلفىّ ١: ١٥٧ - ١٥٨. والغريشىّ، ضبطه ابن قاضى شهبة بفتح الغين المعجمة وكسر الراء ثم مثناة وشين معجمة.','2','108',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (511,'يقولون كثّر عبد الحليم ... فألّا اقتصادا وألا اقتصارا «١»!
وفضل أبى القاسم المجتبى ... كفانى احتجاجا لهم واعتذارا «٢»
ألم يعلموا أنّ فيض السماء ... على الأرض كثّر منها الثمارا
مآثر طالت فأضحى الطّوا ... ل من حلل المدح عنها قصارا
ومجد ينوب ثنائى مطارا ... وجود يغرّق شعرى بحارا
هو الشمس تجلو نهار العلا «٣» ... ومن لى بحلى يعمّ النّهارا
وفضل يعدّ نجوم السماء ... وزهر الرّياض ويحصى القطارا
تغار العلا لابن متكودها «٤» ... فلا تقبل المدح فيه اختصارا
ثم قال السّلفىّ: «أبو محمد عبد الله بن الغريشىّ هذا، كان ساكنا فى المحرس المشهور بالقشميرىّ، وكان من محارس الإسكندريه، ونسبته مستفادة تذكر مع العريشىّ.
توفى فى محرم سنة ثلاث وثلاثين وخمسمائة، وقد علقت «٥» عنه فوائد جمّة- رحمه الله. وكان عفيفا من أهل القرآن».','2','109',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (512,'<span data-type="title" id=toc-385>٣١٨ - عبد الله بن أبى سعيد أبو محمد النحوىّ الأندلسىّ المعروف بالكاسات [١]</span>
نحوىّ، قرأ النحو فى بلاده، وانتقل إلى الشرق، واستوطن مصر. وكان له بجامع عمرو بن العاص حلقة للإقراء والإفادة. وله شعر «١» كثير.
توفى سنة عشرين وخمسمائة فى صفر بمصر.

<span data-type="title" id=toc-386>٣١٩ - عبد الله بن برىّ بن عبد الجبار بن برىّ النحوىّ اللغوىّ [٢]</span>
المصرى المولد والمنشأ، المقدسىّ الأصل. سلفه من القدس، وولد هو بمصر سنة تسع وتسعين وأربعمائة، وبها نشأ، وقرأ العربية على مشايخ زمانه من المصريين والقادمين على مصر، وحصل له من ذلك ما لم يحصل لغيره، وانفرد بهذا الشأن، وقصده الطلبة من الآفاق.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٩٠، وخريدة القصر ٢: ١٧١ - ١٧٣.
[٢] ترجمته فى إشارة التعيين الورقة ٢٣ - ٢٤، وبغية الوعاة ٢٧٨ - ٢٧٩، وتاريخ ابن الأثير ٩: ١٧٥، وتاريخ الإسلام للذهبى (وفيات سنة ٥٨٢)، وتاريخ أبى الفدا ٣: ٧١، وتاريخ ابن كثير ١٢: ٣١٩ - ٣٢٠، وتلخيص ابن مكتوم ٩١، وحسن المحاضرة ١: ٢٢٨ - ٢٢٩، وابن خلكان ١: ٢٦٨ - ٢٦٩، وشذرات الذهب ٤: ٢٧٣ - ٢٧٤، وطبقات ابن قاضى شهبة ٢: ٢٤ - ٢٧، والفلاكة والمفلوكين ٧٩، وكشف الظنون ٧٤١، ١٠٧٢، ومرآة الجنان ٣: ٤٢٤، ومسالك الأبصار ج ٤ مجلد ٣: ٤٦١ - ٤٦٣، ومعجم الأدباء ١٢: ٥٦ - ٥٧، والنجوم الزاهرة ٦: ١٠٣.
قال ابن خلكان: «ويرىّ، بفتح الباء الموحدة وتشديد الراء المكسورة وبعدها ياء؛ وهو اسم علم يشبه النسبة».','2','110',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (513,'وكان جمّ الفوائد، كثير الاطلاع، عالما بكتاب سيبويه وعلله، وبغيره من الكتب النحوية، قيّما باللغة وشواهدها. وكان إليه التصفح فى ديوان الإنشاء؛ لا يصدر كتاب عن الدولة إلى ملك من ملوك النواحى إلا بعد أن يتصفّحه، ويصلح ما لعلّه فيه من خلل خفى.
وكان ينسب إلى الغفلة فى غير العلوم العربية؛ حتى ما يقوم بمصالح نفسه.
ويحكى عنه حكايات فى التغفل أجلّه عنها، وعن ذكر شىء منها.
وكانت كتبه فى غاية الصّحة والجودة، وإذا حشّاها أتى بكل فائدة. ورئى جماعة من تلاميذه متصدّرين متميزين. وأكثر الرؤساء بمصر استفادوا منه، وأخذوا عنه.
وكان قليل التصنيف؛ لم يشتهر له شىء سوى مقدّمة سماها اللّباب «١»، وجواب المسائل العشر «٢» التى سأل عنها أبو نزار ملك النحاة، وحاشيته على كتاب الصّحاح فإنها نقلت عن أصله وأفردت فجاءت ستة مجلدات «٣»، وسماها من أفردها التنبيه والإيضاح عما وقع فى كتاب الصّحاح «٤».
ولما مات- رحمه الله- وأبيعت كتبه حضرها الجمّ الغفير من الأجلّاء بمصر فى ذى القعدة سنة اثنتين وثمانين وخمسمائة.','2','111',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (514,'<span data-type="title" id=toc-387>٣٢٠ - عبد الله بن ثابت بن يعقوب بن قيس بن إبراهيم ابن عبد الله أبو محمد العبقسىّ المقرئ النحوىّ التّوّزىّ [١]</span>
سكن بغداذ، وروى بها عن أبيه «١» عن هذيل «٢» بن حبيب تفسير مقاتل «٣» بن سليمان. وروى أيضا عن عمر بن شبة «٤» النميرىّ.
حدّث عنه أبو عمر بن السماك وغيره. ولد فى سنة ثلاث وعشرين ومائتين فى آخرها، ومات فى سنة ثمان وثلاثمائة، ودفن بالرملية «٥».
_________
[١] ترجمته فى تاريخ بغداد ٩: ٤٢٦، وتاريخ ابن كثير ١١: ١٣٠ - ١٣١، وتلخيص ابن مكتوم ٩١، وطبقات ابن قاضى شهبة ٢: ٢٦ - ٢٧، وطبقات القراء لابن الجزرى ١:
٤١١ - ٤١٢، والنجوم الزاهرة ٣: ١٩٩. والعبقسى: منسوب إلى عبد القيس، وهى قبيلة من أسد، والتوزى، بفتح التاء وتشديد الواو: منسوب إلى توّز (ويقال توج أيضا)، وهى مدينة فى فارس عند بحر الهند.','2','112',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (515,'<span data-type="title" id=toc-388>٣٢١ - عبد الله بن جعفر بن درستويه بن المرزبان أبو محمد الفارسىّ الفسوىّ النحوىّ [١]</span>
نحوىّ جليل القدر، مشهور الذكر، جيّد التصانيف. روى عن جماعة من العلماء؛ منهم من مشايخ الأدب أبو العباس المبرّد، وعبد الله بن مسلم بن قتيبة.
وكان فسويا، سكن بغداذ إلى حين وفاته. قرأ على المبرّد الكتاب وبرع، وكان نظّارا. وكان شديد الانتصار لمذهب البصريين فى اللغة والنحو.
وأما تصانيفه ففى غاية الجودة والإتقان؛ منها تفسير كتاب الجرمىّ، وهو غاية فى بابه، ومنها كتابه فى النحو الذى يدعى الإرشاد، ومنها كتابه فى الهجاء وهو من أحسن كتبه، ومنها شرح الفصيح، وهو فى غاية الحسن والجودة يدلّ على الاطّلاع التام، وله ردّ على المفضّل فى الردّ على الخليل، كتاب مفيد.
كتاب الهداية، كتاب المقصور والممدود. كتاب غريب الحديث.
كتاب معانى الشعر. كتاب الحىّ والميت. كتاب التوسّط بين الأخفش
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٤، والإكمال لابن ما كولا الورقة ٢٧٧، وبغية الوعاة ٢٧٩ - ٢٨٠، وتاريخ الإسلام للذهبى (وفيات سنة ٣٤٧)، وتاريخ بغداد ٩: ٤٢٨ - ٤٢٩، وتاريخ أبى الفدا ٢: ١٠٢، وتاريخ ابن كثير ١١: ٢٣٣، وتلخيص ابن مكتوم ٩١ - ٩٢، وابن خلكان ١: ٢٥١ - ٢٥٢، وطبقات الزبيدىّ ٨٥ - ٨٦، وطبقات ابن قاضى شهبة ٢: ٢٧، وعيون التواريخ (وفيات سنة ٣٤٧)، والفهرست ٦٣، وكشف الظنون ١١٥، ٥٠٦، ٧٠٠، ٨٣٩، ١١٠٨، ١٢٧٢، ١٤١٥، ١٤٥١، ١٤٦١، ١٧٢٩، ١٧٣٠، ٢٠٤١، ومسالك الأبصار ج ٤ مجلد ٢: ٢٩٩ - ٣٠٠، والنجوم الزاهرة ٣: ٣٢١، ونزهة الألباء ٣٥٦ - ٣٥٧. و «درستويه»، ضبطه ابن ماكولا بفتح الدال والراء والواو. وضبطه السمعانى بضم الدال والراء وسكون السين وضم التاء وسكون الواو وفتح الياء. والفسوىّ: منسوب إلى فسا، وهى من مدن فارس.','2','113',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (516,'وثعلب فى تفسير القرآن واختيار أبى محمد فى ذلك. كتاب شرح المفضّليات، لم يتممه. كتاب شرح المقتضب، لم يتممه. كتاب تفسير السّبع الطوال، لم يتممه. كتاب المعانى فى القرآن، لم يتممه. كتاب تفسير الشىء، لم يتممه.
كتاب نقض الراوندىّ على النحويين. كتاب الردّ على بزرج العروضىّ.
كتاب الأزمنة، لم يتممه. كتاب الردّ على ثعلب فى اختلاف النحويين.
كتاب خبر قسّ بن ساعدة وتفسيره. كتاب شرح الكلام ونكته، لم يتممه.
كتاب الردّ على ابن خالويه فى الكلّ والبعض. كتاب الردّ على ابن مقسم فى اختياره. كتاب فى الأضداد. كتاب أخبار النحويين. كتاب الردّ على الفرّاء فى المعانى. كتاب جوامع العروض. كتاب الاحتجاج للفرّاء. كتاب تفسير قصيدة شبيل «١» بن عزرة. كتاب رسالة إلى نجيح الطولونى فى تفضيل العربية. كتاب الكلام على ابن قتيبة فى تصحيف العلماء. كتاب الردّ على أبى زيد البلخىّ فى النحو. كتاب الردّ على من قال بالزوائد وقال يكون فى الكلام حرف زائد. كتاب النصرة لسيبويه على جماعة النحويين، هو كبير لم يتممه. كتاب الانتصار لكتاب العين وأنه للخليل «٢».
قال: [عبد الله بن] جعفر: ولدت فى ثمان وخمسين ومائتين. وتوفى- رحمه الله- يوم الاثنين لسبع بقين من صفر سنة سبع وأربعين وثلاثمائة.','2','114',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (517,'<span data-type="title" id=toc-389>٣٢٢ - عبد الله بن الحسن أبو شعيب الحرّانى اللغوىّ [١]</span>
لغوىّ صدوق. أخذ من يعقوب بن السّكّيت وطبقته. قال الحرّانىّ: كتبت عن يعقوب بن السّكيت من سنة خمس وعشرين- يعنى ومائتين- إلى أن قتل.
قال: وقتل قبل المتوكل بسنة، وقتل المتوكل «١» سنة سبع وأربعين، وكان ما كتبه عنه مدّة إحدى وعشرين سنة «٢».

<span data-type="title" id=toc-390>٣٢٣ - عبد الله بن الحسن بن عشير اليابسىّ النحوىّ [٢]</span>
من جزيرة يابسة «٣»، من نحاة بلد الأندلس. قرأ بالأندلس على أبى الحسين سليمان بن محمد بن طراوة السّبائىّ المالقىّ النحوىّ بالأندلس؛ وقال: لم أر مثله. وكان يعظمه جدا. ورحل إلى الشرق، وتصدّر للإفادة بجامع الإسكندرية لإقراء القرآن والنحو. وكان له شعر كثير.
دفن «٤» بمقبرة باب البحر بالإسكندرية، ووصى أن يصلّى عليه أبو طاهر السّلفىّ، فلم يمكنه ذلك لوحل ومطر كان فى ذلك اليوم.
_________
[١] ترجمته فى تاريخ بغداد ٩: ٤٣٥ - ٤٣٧، وتاريخ ابن مكتوم ٩٢. والحرانى، بفتح الحاء وتشديد الراء: منسوب إلى حران، وهى مدينة بالجزيرة، من ديار ربيعة؛ ينسب إليها جماعة كثيرة من العلماء.
[٢] ترجمته فى بغية الوعاة ٢٨٠، وتلخيص ابن مكتوم ٩٢، ومعجم البلدان ٨: ٤٩٠.','2','115',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (518,'<span data-type="title" id=toc-391>٣٢٤ - عبد الله بن الحسين أبو المظفر النحوىّ [١]</span>
يعرف بالبغداذىّ، وهو مروزىّ الأصل. نشأ ببغداذ، وسكن سمرقند وتصدّر لإقراء العربية، وكان يذكر أنه كتب ببغداذ عن مشايخها، ولم يكن معه أصل.
ومات بسمرقند «١»، وكان ينشد عن أبى الطيّب المتنبّى.

<span data-type="title" id=toc-392>٣٢٥ - عبد الله بن الحسين بن عبد الله أبو البقاء النحوىّ الضرير [٢]</span>
العكبرىّ الأصل، البغداذى المولد والدار. كان نحويا فقيها مرضيا. تفقه على مذهب أحمد بن حنبل، وأخذ النحو عن أبى محمد بن الخشّاب وغيره، وروى عن مشايخ زمانه. وكان جمّاعة لفنون من العلم والمصنفات.
_________
[١] ترجمته فى بغية الوعاة ٢٨١، وتاريخ بغداد ٩: ٤٤٢، وتلخيص ابن مكتوم ٩٢. وما ذكره المؤلف يوافق ما فى تاريخ بغداد.
[٢] ترجمته فى بغية الوعاة ٢٨١، وتاريخ ابن الأثير ٩: ٣٢٨، وتاريخ الإسلام للذهبى (وفيات سنة ٦١٦)، وتاريخ أبى الفداء ٣: ١٢٤، وتاريخ ابن كثير ١٣: ٨٥، وتلخيص ابن مكتوم ٩٢، وابن خلكان ١: ٢٦٦ - ٢٦٧، وروضات الجنات ٤٥٣ - ٤٥٥، وشذرات الذهب ٥: ٦٧ - ٦٩، وطبقات ابن قاضى شهبة ٢: ٣٠ - ٣٤، وكشف الظنون ٨١، ٩٨، ١٠٨، ١٢٢، ٢١٢، ٢١٤، ٢٥٣، ٣٩٩، ٤٢٤، ٤٤٠، ٤٨٠، ٥١٨، ٦٩٢، ٧١٤، ٨١١، ١٢٧٣، ١٤٢٨، ١٥٤٣، ١٥٦٣، ١٧٧٤، ١٧٨٩، ١٨٢٠، ومرآة الجنان ٤: ٣٢ - ٣٣، والمستفاد من ذيل تاريخ بغداد الورقة ٤١، ومعجم البلدان ٦: ٢٠٣ - ٢٠٤، والنجوم الزاهرة ٦: ٢٤٦، ونكت الهميان ١٧٨ - ١٨٠. والعكبرى، بضم العين وسكون الكاف وفتح الباء: منسوب إلى عكبرا، وهى بلدة على دجلة، فوق بغداد بعشرة فراسخ.','2','116',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (519,'وله مصنفات حسان فى إعراب القرآن وقراءته، وإعراب الحديث والنحو واللغة والعربية. وشرح المقامات الحريرية، وشعر أبى الطيب المتنبى، وغير ذلك.
ولد فى سنة ثمان وثلاثين وخمسمائة، وتوفى ليلة الأحد ثامن شهر ربيع الآخر سنة ست عشرة وستمائة، ودفن يوم الأحد بباب حرب.
ومن تصانيفه: كتاب إعراب القرآن والقراءات «١». كتاب شرح الإيضاح. كتاب شرح اللّمع. كتاب اللّباب فى علل النحو. كتاب شرح المفصّل، لطيف. كتاب إعراب شعر الحماسة. كتاب شرح المتنبى «٢».
كتاب إعراب الحديث، لطيف.','2','117',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (520,'ومن شعره فيما قاله فى الوزير ناصر بن مهدىّ العلوىّ:
بك أضحى جيد الزمان محلّى ... بعد أن كان من حلاه مخلّى
لا يجاريك فى نجاريك «١» خلق ... أنت أعلى قدرا وأعلى محلّا
دمت تحيى ما قد أميت من الفض ... ل وتنفى فقرا وتطرد محلا
وقال داود بن أحمد بن يحيى الملهمىّ الشاعر يهجو أبا البقاء من أبيات:
وأبو البقاء عن الكتاب مخبّرا ... وتراه إن عدم الكتاب محيّرا
وكان- رحمه الله- إذا أراد التصنيف أحضرت له المصنفات فى ذلك الفن، وقرئ عليه منها، فإذا حصله فى خاطره أملاه. فكان يخلّ بكثير من المحتاج إليه. وما أحسن ما وصفه بعض الأدباء فقال: «أبو البقاء تلميذ تلاميذه»، أى هو تبع لهم فيما يلقونه عليه من القراءة عند الجمع من كلام المتقدّمين.

<span data-type="title" id=toc-393>٣٢٦ - عبد الله بن حمّود الزّبيدىّ الأندلسىّ [١]</span>
صاحب أبى على الفارسىّ الذى يذكره فى تصانيفه، الذى يقول: «سألنى الأندلسىّ»، و «قال الأندلسىّ».
كان عبد الله هذا قد صحب أبا على القالىّ بالأندلس، وأخذ عنه، ثم رحل إلى المشرق، فصحب أبا سعيد السّيرافىّ إلى أن مات، وصحب أبا على الفارسىّ فى مقامه وسفره إلى فارس وغيرها، وأخذ عنه وأكثر وبرع.
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٤، وبغية الوعاة ٢٨٢، وتلخيص ابن مكتوم ٩٣، وتكملة الصلة ٢: ٤٣٩ - ٤٤٠.','2','118',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (521,'ومن خبره مع أبى علىّ أن أبا علىّ غلّس يوما إلى الصلاة فى المسجد، فقام إليه عبد الله بن حمّود هذا من مذود- وكان لدابة أبى على خارج داره، وكان عبد الله قد بات فيه ليدلج إليه قبل الطلبة طلبا للسبق والأخذ من علمه- فارتاع منه أبو علىّ، وقال له: ويحك! من تكون؟ قال: أنا عبد الله الأندلسىّ، فقال:
إلى كم تتبعنى! والله إن على وجه الأرض أنحى منك!.
وذكر على بن عيسى بن الفرج الربعىّ صاحب [أبى] على، عبد الله بن حمّود الزّبيدىّ هذا فقال: «قرأ «١» على أبى على فى نوادر الأصمعىّ «أكأت الرجل «٢»» إذا رددته عنك، فقال له أبو على: ألحق هذه الكلمة بباب «أجأ»، فإنى لم أجد لها نظيرا غيرها. فسارع من حوله إلى كتابتها. قال الربعىّ: [فقلت] «٣» أيها الشيخ، ليس «أكأ» من «أجأ» فى شىء. قال: وكيف ذلك؟ قال: قلت لأن إسحاق بن إبراهيم الموصلىّ وقطربا حكما أنه يقال: «كاء «٤» الرجل»؛ إذا جبن. فخجل الشيخ وقال: إذا كان كذا فليس منه. فضرب كل واحد منهم على ما كتب.
ولم يرجع عبد الله بن حمود الزبيدىّ الأندلسىّ إلى بلاده، وما زال بالعراق إلى أن مات بها- رحمه الله. «٥»','2','119',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (522,'<span data-type="title" id=toc-394>٣٢٧ - عبد الله بن رستم اللغوىّ</span>
[١] مستملى يعقوب بن السّكّيت. كان قد استفاد من يعقوب وطبقته، وكتب بخطه الكثير، وأفاد الطالبين.

<span data-type="title" id=toc-395>٣٢٨ - عبد الله بن سعيد الأموىّ اللغوىّ [٢]</span>
لقى العلماء، ودخل البادية، وأخذ عن فصحاء الأعراب، وأخذ عنه العلماء وأكثروا فى كتبهم. وكان ثقة فى نقله.
وصنف كتبا، منها: كتاب النوادر. وكتاب رحل البيت. وكان جالس أعرابيا من بنى الحارث بن كعب، وسألهم عن النوادر والغريب، وكان مع ذلك حافظا للأخبار والشعر وأيام العرب.

<span data-type="title" id=toc-396>٣٢٩ - عبد الله بن سعيد بن مهدى الخوافىّ الكاتب أبو منصور [٣]</span>
أديب شاعر، لغوى فرضى حاسب. كان من أتم الناس مروءة وأكبرهم نفسا، كثير الرواية لكتب الأدب، وله فى اللغة تصانيف، وجمع مجاميع فى كل فن. ومن شعره:
وخود «١» جلا التوديع عندم خدّها ... كما فتقت أكمام ورد مضرّج
_________
[١] ترجمته فى بغية الوعاة ٢٨٢، وتاريخ بغداد ١٠: ٨١، وتلخيص ابن مكتوم ٩٣، وطبقات الزبيدىّ ١٤٤.
[٢] ترجمته فى بغية الوعاة ٢٨٢، وتلخيص ابن مكتوم ٩٣، وطبقات الزبيدىّ ١٣٤، والفهرست ٤٨.
[٣] ترجمته فى الأنساب ٢١٠ ب، وبغية الوعاة ٢٨٢، وتلخيص ابن مكتوم ٩٣ - ٩٤، وطبقات ابن قاضى شهبة ٢: ٣٥ - ٣٦، ونزهة الألباء ٣٣١ - ٣٣٢. والخوافىّ، بفتح الخاء والواو: منسوب إلى خواف؛ وهى من نواحى نيسابور، ينسب إليها كثير من العلماء. وقد ذكر السيوطىّ أن وفاته كانت سنة ٤٨٠.','2','120',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (523,'ولم أر بدرا قبلها عضّ فى الدّجى ... على عنم بالأقحوان المفلّج
تضاهى الدّجى فرعا وعينا وحاجبا ... سوى أنها كالصّبح عند التبلّج
رحلنا على اللذات من جانب الصّبا ... وقلت لأحداث الزّمان ألا اخرجى
وبتنا على رغم النّوى ننشر الهوى ... ونطوى رداء الليل طيّا وننتجى «١»
فلما تجلّى الصبح ثارت كأنّها ... غزال صريم لا غزالة منبج «٢»

<span data-type="title" id=toc-397>٣٣٠ - عبد الله بن عبد الله الأندلسىّ المعروف بالبرقى [١]</span>
كان عالما بالنحو واللغة، إماما فيهما، [عالما] بالعدد والهندسة. وله كتاب مشهور فى المسبّع. وكان رجلا ناسكا، ينسب إليه علم صناعة الكيمياء.
وكان الحكم المستنصر «٣» يعظّمه ويوقره ويروم الإسكار معه، فيقبضه ورعه، ويكفّه عن مداخلته زهده- رحمه الله ورضى عنه وأرضاه.

<span data-type="title" id=toc-398>٣٣١ - عبد الله بن عبد الله النحوىّ القياس [٢]</span>
كان نحويا قياسيا. وأصله من الأندلس، وسكن القيروان. وكان سرىّ الأخلاق، كثير المصادقة لمن صحب. وله أشعار حسنة، وكان من يحسده يقول:
هى من أشعار الأندلسيين. وكان متصلا بابن أبى جعفر المروزىّ ومادحا لابنه كثيرا.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٩٤.
[٢] ترجمته فى تلخيص ابن مكتوم ٩٤.','2','121',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (524,'<span data-type="title" id=toc-399>٣٣٢ - عبد الله بن على بن أحمد بن عبد الله المقرئ أبو محمد ابن بنت أبى منصور الخياط [١]</span>
إمام مسجد ابن جردة، قرأ القرآن بروايات، وتخرّج عليه جماعات كثيرة «١» ختموا عليه كتاب الله. وله معرفة بالنحو واللغة.
روى كتاب سيبويه عن أبى الكرم بن فاخر «٢»، ورواه لنا عنه زيد «٣» بن الحسن ابن زيد الكندىّ إجازة منه لنا، وقرأه عليه ابن سعدون القرطبىّ وابن البندار.
ووقع إلىّ الأصل بذلك، بحمد الله ومنه وكرمه.
وكان أبو محمد هذا متودّدا متواضعا، حسن القراءة والتلاوة فى المحراب، خصوصا فى ليالى رمضان يحضر عنده الناس للاستماع. وكان يقول شعرا قريبا.
وصنف تصانيف «٤» فى علوم القراءات، وأغرب فيها، فشنّع عليه بها، وخولف فيها، فرجع عنها.
_________
[١] ترجمته فى الأنساب ٢١٤ ب، وتاريخ ابن كثير ١٢: ٢٢٢، وخريدة القصر ١: ٨٣ - ٨٤، وتلخيص ابن مكتوم ٩٤، وشذرات الذهب ٤: ١٢٨ - ١٢٩، وطبقات ابن قاضى شهبة ٢:
٤٢ - ٤٤، وطبقات القرّاء لابن الجزرىّ ١: ٤٣٤ - ٤٣٥، وكشف الظنون ٢٠٦، ٣٣٨، ١٣٤٤، ١٤٩٩، ١٥٨٢، والمنتظم (وفيات سنة ٥٤١)، ومرآة الجنان ٤: ٢٧٥، ونزهة الألباء ٤٨٢ - ٤٨٤.','2','122',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (525,'وكان مولده فى ليلة الثلاثاء التاسع والعشرين من شعبان سنة أربع وستين وأربعمائة، وتوفى بكرة يوم الاثنين الثامن والعشرين من ربيع الآخر من سنة إحدى وأربعين وخمسمائة، ودفن من الغد بباب حرب عند جدّه على دكّة الإمام أحمد بن حنبل، وصلّى عليه فى جامع القصر، فى جامع المنصور، وكان الجمع كثيرا جدا يفوت الإحصاء، وأغلق أكثر البلد فى ذلك اليوم؛ فمن شعره:
أأنصحكم على أوفى يقينى ... وسوء الظن منكم يعتريني
إذا ما جئتكم لأداء نصح ... أتانى الغشّ منكم فى الكمين
سأصبر ما حييت على أذاكم ... وأحفظ ودّكم فى كلّ حين
وله أيضا:
أيها الزائرون بعد وفاتى ... جدثا ضمنّى ولحدا عميقا
سترون الذى رأيت من المو ... ت عيانا وتسلكون الطريقا

<span data-type="title" id=toc-400>٣٣٣ - عبد الله بن على بن إسحاق الصّيمرىّ النحوىّ أبو محمد [١]</span>
قدم مصر، وحفظ عنه شىء من اللغة وغيرها. وكان فهما عاقلا. وصنف كتابا فى النحو؛ سماه التبصرة «١»، وأحسن فيه التعليل على مذهب البصريين.
ولأهل المغرب باستعماله عناية تامة، ولا يوجد به نسخة إلا من جهتهم. وقد ذكرته فى غير موضع من هذا الكتاب.
_________
[١] ترجمته فى بغية الوعاة ٢٨٥، وتلخيص ابن مكتوم ٩٤، وكشف الظنون ٣٣٩.
والصيمرىّ، بفتح الصاد وسكون الياء وفتح الميم: منسوب إلى صيمرة؛ موضع بالبصرة، أو بلد بين ديار الجبل وديار خوزستان.','2','123',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (526,'<span data-type="title" id=toc-401>٣٣٤ - عبد الله بن عيسى بن عبد الله بن أحمد بن سعيد بن سليمان ابن محمد بن أبى حبيب الأنصارىّ الخزرجىّ أبو محمد ابن أبى بكر الأندلسىّ [١]</span>
ولد بشلب، «١» ونشأ بإشبيلية من بيت العلم والوزارة، وصرف وجهه إلى طلب العلم حتى حصل له ما لم يحصل لغيره؛ وولى القضاء بالأندلس مدة، ثم خرج منها على عزم الحج، ودخل مصر وتوجّه إلى مكة فحج وجاور بها سنة، ثم قدم العراق وأقام ببغداذ مدّة، ثم سافر إلى خراسان فنزل هراة مدّة ومرو مدّة. وكان خبيرا بالحديث والفقه والأدب والنحو، وسمع بخراسان وسمع منه، وأفاد واستفاد؛ وشهد له علماؤها بالفضل والأدب والنبل. وكان مولده بشلب، إحدى مدن الأندلس فى ربيع الأوّل فى سنة أربع وثمانين وأربعمائة «٢».
أنبأنا أبو الضياء شهاب بن محمود الشاذمانى فى كتابه من هراة قال: أخبرنا عبد الكريم بن محمد المروزىّ من كتابه الجامع القديم بهراة بقراءة أبى النّصر الفامىّ قال: حدّثنا أبو محمد بن أبى حبيب الحافظ من لفظه بجامع هراة، حدّثنا أبو عمرو عثمان بن محمد بن أحمد البلخى إملاء فى جامع بلخ، أخبرنا القاضى أبو على الحسن ابن محمد الوحشىّ سنة إحدى وأربعين وأربعمائة، أخبرنا أبو سعيد محمد بن موسى الصيرفىّ، أخبرنا أبو عبد الله محمد بن عبد الله الأصبهانىّ، أخبرنا أبو جعفر أحمد ابن مهران بن خالد الأصبهانىّ، حدّثنا محمد بن الصباح، حدّثنا إسماعيل
_________
[١] ترجمته فى بغية الوعاة ٢٨٦، وتاريخ الإسلام الذهبىّ (وفيات سنة ٥٤٦) وتلخيص ابن مكتوم ٩٤ - ٩٥، وطبقات ابن قاضى شهبة ١: ٢١.','2','124',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (527,'ابن زكريا عن الحسن بن الحكم النخعىّ عن عادى بن ثابت عن أبى حازم عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: «من بدا جفا، ومن اتبع الصيد غفل، ومن أتى أبواب السلطان افتتن، وما ازداد عبد من سلطان قربا إلا ازداد من الله بعدا».
توفى- رحمه الله- بهراة فى شعبان سنة ثمان وأربعين وخمسمائة.

<span data-type="title" id=toc-402>٣٣٥ - عبد الله بن عمرو بن صبح المعروف بابن أبى صبح المرّىّ [١]</span>
أعرابى بدوىّ لغوىّ، فصيح. دخل من البادية، ونزل بغداذ، ولم يزل مقيما بها حتى مات وأخذ منه. وكان شاعرا فصيحا، وله مع الفقسىّ «١» أخبار ظريفة.
قال دعبل: حضر الفقعسىّ دارا فيها وليمة، وحضرها ابن أبى صبح الأعرابىّ فازدحما على الباب، فغلب ابن أبى صبح، ودخل قبل الفقعسىّ، وقال:
ألا يا ليت أنّك أمّ عمرو ... شهدت مقامتى كى تعذرينى
ودفعى منكب الأسدىّ عنى ... على عجل بناجية زبون «٢»
بمنزلة كأن الأسد فيها ... رمتنى بالحواجب والعيون «٣»
وكنت إذا سمعت بحق خصم ... منعت الخصم أن يتقدّمونى

<span data-type="title" id=toc-403>٣٣٦ - عبد الله بن فزارة النحوى [٢]</span>
بصرى تصدّر بها لإفادة هذا النوع، وتوفى بها سنة اثنتين وثمانين ومائتين.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٩٥، والفهرست ٤٩.
[٢] ترجمته فى بغية الوعاة ٢٨٦، وتلخيص ابن مكتوم ٩٥، وطبقات الزبيدىّ ١٤٧، وطبقات ابن قاضى شهبة ٢: ٤٥.','2','125',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (528,'<span data-type="title" id=toc-404>٣٣٧ - عبد الله بن القاسم بن على بن محمد بن عثمان الحريرىّ أبو القاسم [١]</span>
من أهل البصرة، سكن بغداذ، وهو ولد صاحب المقامات، وكان يسكن باب المراتب. شاب فاضل متميز، له حظ من الأدب واللغة، مليح الخط، قليل الحظ. ولد سنة تسعين وأربعمائة.

<span data-type="title" id=toc-405>٣٣٨ - عبد الله بن محمد بن هارون أبو محمد التوّزىّ [٢]</span>
مولى قريش. وكان يدعى بالقرشىّ. وقال المبرّد: قرأ التّوّزىّ كتاب سيبويه على أبى عمر الجرمىّ. قال: ما رأيت أعلم بالشعر منه. وكان أعلم من الرياشىّ والمازنىّ وأكثرهم رواية عن أبى عبيدة. وقد قرأ على الأصمعى وغيره.
وتزوّج التوّزىّ أم أبى ذكوان النحوىّ، فكان أبو ذكوان إذا قيل له:
من التوّزى منك؟ يقول: كان أبا إخوتى.
فمن تصانيفه كتاب الأمثال. كتاب الأضداد. كتاب الخيل وأسنانها وعيوبها وإضمارها ومن نسب إلى فرسه وسبقها. كتاب فعلت وأفعلت.
كتاب النوادر.
وهو منسوب إلى موضع من بلاد فارس اسمه توّز، وهم يسمّونه اليوم توّج.
توفى- رحمه الله- سنة ثلاثين ومائتين.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٩٥، والمستفاد من ذيل تاريخ بغداد الورقة ٤٢.
[٢] ترجمته فى أخبار النحويين البصريين للسيرافى ٨٥ - ٨٧، وبغية الوعاة ٢٩٠، وتلخيص ابن مكتوم ٩٥، وطبقات الزبيدىّ ٦٩، وطبقات ابن قاضى شهبة ٢: ٥١، والفهرست ٥٧ - ٥٨، ومراتب النحويين ١٢٢، والمزهر ٢: ٤٠٨، ٤٤٥، ٤٦٤، ونزهة الألباء ٢٣٢ - ٢٣٣. والتوزى، بفتح التاء وتشديد الواو: منسوب إلى توّز، وهى موضع عند بلاد الهند مما يلى فارس.','2','126',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (529,'<span data-type="title" id=toc-406>٣٣٩ - عبد الله بن محمد بن هانئ أبو عبد الرحمن النيسابورىّ اللغوىّ [١]</span>
عالم بهذا الشأن. أدرك الصدر الأوّل، وروى عن أبى زيد سعيد بن أوس الأنصارىّ، وروى عنه كتاب النوادر، وأشعار العرب، وأكثر عنهم الرواية لهذا النوع، وكان فى طبقة أبى عبيد القاسم بن سلّام وأبى حاتم سهل ابن محمد السّجستانى.
قال عبد الله بن محمد بن هانئ النيسابورىّ هذا: أنفق أبى على الأخفش اثنى عشر ألف دينار.
وكان جمّاعة للكتب، كثير الحفظ لها إلى أن صارت جملة عظيمة، وأبيعت بأربعمائة ألف درهم، وكان قد أعدّ فى حياته دارا لكل من يقدم من المستفيدين، فيأمر بإنزاله فيها، ويزيح علّته فى النسخ والورق، ويوسّع النّفقة عليه.
وله كتاب كبير يوفى على ألفى ورقة، فى نوادر العرب وغرائب ألفاظها، وفى المعانى والأمثال.

<span data-type="title" id=toc-407>٣٤٠ - عبد الله بن محمد بن عيسى بن وليد النحوىّ الأندلسىّ [٢]</span>
من أهل مدينة الفرج «١»؛ أبو محمد. كان من أهل العلم بالعربية واللغة، متحقّقا ربما، بارعا فيهما، مع وقار مجلس ونزاهة نفس. وكان قد شرع فى شرح كتاب
_________
[١] ترجمته فى بغية الوعاة ٢٩٠، وتاريخ بغداد ٧٢ - ٧٣، وتلخيص ابن مكتوم ٩٥ - ٩٦.
[٢] ترجمته فى بغية الوعاة ٢٩١، وتلخيص ابن مكتوم ٩٦، وكشف الظنون ٤٦٣.','2','127',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (530,'الواضح للزّبيدىّ، فبلغ منه النصف، ومات قبل إكماله. وله كلام على أصول «١» النحو. وكان يختم كتاب سيبويه فى كل خمسة عشر يوما- رحمه الله. «٢»

<span data-type="title" id=toc-408>٣٤١ - عبد الله بن محمد أبو العباس المعروف بابن شرشير الناشى الكبير [١]</span>
الشاعر النحوى العروضى المتكلم. أصله من الأنبار، وأقام ببغداذ مدّة طويلة، ثم خرج إلى مصر فنزلها إلى آخر عمره. كان يعلّم العلوم ويتبحّر فيها، علم النحو وأحكمه، ونظر فى علله وهو متكلّم، فتبين له بقوّة الكلام نقض أصوله، فنقضها وصنّف فيها. وكذلك العروض أدخل على قواعده شبها ناقضة لها، ومثّله بأمثلة غير أمثلة الخليل، وأحسن والله فى كل ذلك، وأظهر قوّة. وكذلك فعل بالكتب المنطقية. وإذا وقف الواقف على تصانيفه وأنصف ظهر له أثر الاجتهاد والإمتاع، حتى إن الغير «٣» منصف ينسبه إلى التّهوّس. وليس الأمر كذلك، وإنما هى قوّة وفطنة.
_________
[١] ترجمته فى تاريخ ابن الأثير ٦: ١١٥، وتاريخ بغداد ١٠: ٩٢ - ٩٣، وتاريخ أبى الفدا ٢: ٦١، وتاريخ ابن كثير ١١: ١٠١، وتلخيص ابن مكتوم ٩٦، وحسن المحاضرة ١:
٢٤٠، وابن خلكان ١: ٢٦٣ - ٢٦٤، وشذرات الذهب ٢: ٢١٤ - ٢١٥، ومراتب النحويين ١٣٩، والمزهر ٢: ٤٠٩، والمنتظم (وفيات سنة ٢٩٧)، والنجوم الزاهرة ٣:
١٥٨ - ١٥٩. والناشى، بفتح النون وبعد الألف ياء: لقب غلب عليه. وشرشير (بكسر الشين الأولى والثانية) فى الأصل: اسم طائر يصل إلى الديار المصرية فى البحر زمن الشتاء؛ وهو أكبر من الحمام بقليل، كثير الوجود بساحل دمياط؛ وجعل اسما عليه.','2','128',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (531,'وله شعر كثير يتضمن فوائد، وله قصيدة مطوّلة فى فنون من العلم على روىّ واحد وقافية واحدة، تبلغ أربعة آلاف بيت. وله مصنّفات جميلة.
فمن شعره ما أنشده له محمد بن خلف بن المرزبان، وقد أحضرت له مغنيّة حسناء:
فديتك لو أنّهم أنصفوك ... لردّوا النواظر عن ناظريك
تردّين أعيننا عن سواك ... وهل تنظر العين إلّا إليك
وهم جعلوك رقيبا علينا ... فمن ذا يكون رقيبا عليك
ألم يقرءوا- ويحهم- ما يرو ... ن من وحى حسنك فى وجنتيك!
قال ابن المرزبان: فشغفت بالأبيات. فقال ابن أبى طاهر: أحسنت والله وأجملت! قد والله حسدتك على هذه الأبيات «١».
قال سليمان بن أحمد الطّبرانىّ: أنشدنا الناشى لنفسه بمصر سنة ثمانين- يعنى ومائتين:
ليس شىء أحرّ فى مهجة العا ... شق من هذه العيون المراض
والخدود المضرّجات اللّواتى ... شيب جريالها «٢» بحسن البياض
ورنوّ الجفون والغمز بالحا ... جب عند الصّدود والإعراض
وطروق الحبيب واللّيل داج ... حين همّ السّمّار بالإغماض «٣»
مات أبو العباس الناشى بمصر سنة ثلاث وتسعين ومائتين.','2','129',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (532,'<span data-type="title" id=toc-409>٣٤٢ - عبد الله بن محمد بن طاهر الطّريثيثىّ أبو بكر القاضى [١]</span>
من أهل طريثيث. أحد الأفاضل، وكانت له يد باسطة فى اللغة والأدب.
طاف البلاد، وخدم الأكابر، وورد العراق، ولقى بالإكرام والاحترام. وكان ذلك قبل سنة اثنتين وثمانين وأربعمائة. وصنف كتابا سماه الموازنة بين أبى طاهر وطاهر يمدح فيه أبا طاهر الخوارزمىّ، ويذم طاهرا الطّريثيثىّ. وهو كتاب كثير الفوائد من المنثور والمنظوم والحكايات المفيدة وأحوال الناس، وأودعه قطعة صالحة من شعره «١».

<span data-type="title" id=toc-410>٣٤٣ - عبد الله بن محمد بن رستم أبو محمد اللغوىّ [٢]</span>
مستملى يعقوب بن السّكّيت. كان مذكورا بالعلم والفضل، وروى عن يعقوب. حدّث عنه قاسم بن محمد الأنبارىّ، وكان ثقة.

<span data-type="title" id=toc-411>٣٤٤ - عبد الله بن محمد بن سفيان أبو الحسين الخزاز النحوىّ [٣]</span>
حدث عن أبى العباس المبرّد وأبى العباس ثعلب وغيرهما. روى عنه عيسى ابن على بن عيسى الوزير «٢»، وكان ثقة.
_________
[١] ترجمته فى بغية الوعاة ٢٨٨، وتلخيص ابن مكتوم ٩٦، والمستفاد من ذيل تاريخ بغداد الورقة ٤٣. والطريثيثىّ، بضم الطاء وفتح الراء وسكون الياء: منسوب إلى طريثيث، وهى ناحية كبيرة من نواحى نيسابور.
[٢] ترجمته فى بغية الوعاة ٢٨٢، وتلخيص ابن مكتوم ٩٣، وطبقات الزبيدىّ ١٣٤، والفهرست ٤٨؛ وهو مكرر ٣٢٧.
[٣] ترجمته فى بغية الوعاة ٢٨٧ - ٢٨٨، وتاريخ بغداد ١٠: ١٢٣، وتلخيص ابن مكتوم ٩٦ - ٩٨، وطبقات ابن قاضى شهبة ٢: ٣٤٤، وكشف الظنون ١٤٥٨، ١٧٣٠.','2','130',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (533,'وله مصنّفات فى علوم القرآن «١» غزيرة الفوائد. وكان صاحب إسماعيل القاضى «٢» وورّاقه. قرأ على المبرّد كتاب سيبويه؛ أى أسمعه إياه من لفظه. مات عبد الله بن محمد بن سفيان يوم الثلاثاء لليلة بقيت من شهر ربيع الأوّل سنة خمس وعشرين وثلاثمائة.

<span data-type="title" id=toc-412>٣٤٥ - عبد الله بن محمد بن هانئ أبو عبد الرحمن النحوىّ النيسابورىّ [١]</span>
صاحب الأخفش. ذكره بهذا أبو عبد الله بن البيّع فى تاريخه، وقال عنه:
«سمع محمد بن جعفر، وعبد الأعلى بن عبد الأعلى، ويحيى بن سعيد، ويوسف بن سعيد، ويوسف بن عطية، ومبارك بن سحيم وأقرانهم من البصريين».
روى عنه محمد بن عبد الوهاب الفرّاء، وعلى بن الحسين الهلالىّ ومن بعدهما، مثل إبراهيم بن أبى طالب وطبقته وأصحابه. ومسجده مسجد هانئ، وفيه كان يحدّث.
مات فى جمادى الآخرة سنة ست وثلاثين ومائتين.
_________
[١] ترجمته فى بغية الوعاة ٢٩٠، وتاريخ بغداد ١٠: ٧٢ - ٧٣، وتلخيص ابن مكتوم ٩٥ - ٩٦؛ وهو مكرر ٣٣٩.','2','131',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (534,'<span data-type="title" id=toc-413>٣٤٦ - عبد الله بن محمد البخارىّ النحوىّ الفقيه الشاعر المعروف بالبافى [١]</span>
كان من أفقه الناس فى وقته على مذهب الشافعىّ، وله معرفة بالنحو والأدب مع عارضة «١» وفصاحة. وكان حسن المحاضرة، حاضر البديهة، يقول الشعر المطبوع من غير تكلّف، ويعمل الخطب، ويكتب الكتب الطوال من غير رويّة.
قال أبو بكر البرقانىّ «٢» - رحمه الله-: قصد أبو محمد البافىّ صديقا له ليزوره فلم يجده فى داره، فاستدعى بياضا ودواة وكتب إليه:
كم حضرنا فليس يقضى التّلاقى ... نسأل الله خير هذا الفراق
إن أغب لم تغب وإن لم تغب غب ... ت كأنّ افتراقنا باتفاق
وله أيضا:
ثلاثة ما اجتمعن فى رجل «٣» ... إلّا وأسلمنه إلى الأجل
ذلّ اغتراب وفاقة وهوى ... وكلّها سائق على عجل
يا عاذل العاشقين إنّك لو ... أنصفت أعفيتهم «٤» عن العذل
فإنهم لو عرفت صورتهم ... عن شغل «٥» العاذلين فى شغل
_________
[١] ترجمته فى الأنساب ٦١ ا، وتاريخ بغداد ١٠ - ١٣٩ - ١٤٠، وتلخيص ابن مكتوم ٩٧، والجواهر المضية ١: ٢٨٣ - ٢٨٦، وشذرات الذهب ٣: ١٥٢، وطبقات الشافعية ٢: ٢٣٢ - ٢٣٤، والباب ١: ٩٠، ومعجم البلدان ٢: ٤٣، والمنتظم (وفيات سنة ٣٩٨)، والنجوم الزاهرة ٤: ٢١٩. والبافىّ، بفتح الباء وفاء مكسورة وياء مشدّدة. منسوب إلى باف، وهى إحدى قرى خوارزم.','2','132',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (535,'وله إلى صديق له يستنجزه وعدا:
توسّع مطلى والزمان يضيق ... وأنت بتقديم الجميل حقيق
فإمّا «نعم» يحيى الفؤاد نجاحها ... وإمّا إياس فالغريب رقيق
فإنّ مرجّى البرّ فى الأسر موثق ... وإن طليق اليأس منك طليق
مات فى النصف من محرّم سنة ثمان وتسعين وثلاثمائة ببغداذ.

<span data-type="title" id=toc-414>٣٤٧ - عبد الله بن محمد بن الحسين بن ناقيا أبو القاسم الأديب الشاعر اللّغوىّ [١]</span>
كان فاضلا. له ترسّل وشعر وأدب ومقامات وتصنيفات فى الأدب. شرح كتاب الوسيط شرحا متوسطا ممتعا. وله كتاب فى ملح الممالحة وهو كتاب حسن فى نوعه «١». كان يعرف بالبندار.
وتوفى ليلة الأحد رابع محرّم سنة خمس وثمانين وأربعمائة ببغداذ، ودفن فى مقابر باب الشام. ومولده فى ذى القعدة من سنة عشر وأربعمائة.
وله شعر سائر، فمن شعره:
أخلّاى ما صاحبت فى العيش لذّة ... ولا زال عن قلبى حنين التّذكّر
ولا طاب لى طعم الرقاد ولا اجتنت ... لحاظى مذ فارقتكم حسن منظر
ولا عبثت كفّى بكأس مدامة ... يطوف بها ساق، ولا جسّ مزهر «٢»
_________
[١] ترجمته فى بغية الوعاة ٢٩٢، وتلخيص ابن مكتوم ٩٧، وخريدة القصر ١: ١٤٢، وابن خلكان ١: ٢٦٦، وكشف الظنون ١٢٩، ٥٩٤، ١٢٧٣، ١٨١٧، والمستفاد من ذيل تاريخ بغداد الورقة ٤٢. وناقيا؛ ضبطه ابن خلكان، بفتح النون وبعد الألف قاف مكسورة ثم ياء.','2','133',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (536,'<span data-type="title" id=toc-415>٣٤٨ - أبو عبد الرحمن عبد الله بن محمد اليزيدىّ العدوىّ المعروف بابن اليزيدىّ [١]</span>
كان عالما بالنحو واللغة. أخذ عن أبى زكرياء يحيى بن زياد الفرّاء وغيره وصنّف كتابا فى غريب القرآن، وكتابا فى النحو مختصرا، وكتاب الوقف والابتداء، وكتاب إقامة اللسان على صواب المنطق. وأخذ عنه ابن أخيه الفضل بن محمد اليزيدىّ.
قال أبو العباس ثعلب: ما رأيت فى أصحاب الفرّاء أعلم من عبد الله بن محمد اليزيدىّ، فى القرآن خاصة. ذكره ابن الأنبارىّ- رحمه الله.

<span data-type="title" id=toc-416>٣٤٩ - عبد الله بن محمد بن وداع بن زياد بن هانئ الأزدىّ [٢]</span>
ويكنى أبا عبد الله. حسن المعرفة بالأدب، صحيح الخط؛ يرغب فيه الناس ويتغالون فى ثمنه لإتقانه، من زمانه وذلك فى حدود سنة ثلاثين ومائتين، وإلى يومنا هذا، وهو حدود ثلاثين وستمائة. وكان له دكان ببغداذ يورّق فيه، ويجتمع إليه عامة أهل الأدب، ويحصل فيه بينهم من المحاضرة والمذاكرة ما لا يحصل فى غيره من أندية الأدب، ولقد اقتنيت بخطه كتاب الأمثال لأبى عبيد، فرأيت من الإتقان والتحقيق ما لا شاهدته لغيره، واقتنيت بعد ذلك غيره من الكتب الأدبية بخطه. وقيل إن خطه فى زمانه كان يباع بالثمن الغالى، وكذلك اليوم عند من يعرفه.
_________
[١] وردت هذه الترجمة فى هامش الأصل ص ٣٩٦ من الجزء الأوّل. وترجمته فى الفهرست ٥٧ - ٥٨، ونزهة الألباء ٢٢٦ - ٢٢٧. وانظر نسب اليزيدىّ فى الجزء الأوّل ص ١٦١.
[٢] ترجمته فى تلخيص ابن مكتوم ٩٧ - ٩٨، والفهرست ٨٠.','2','134',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (537,'<span data-type="title" id=toc-417>٣٥٠ - عبد الله بن محمد بن سفيان أبو الحسين الخزاز النحوىّ [١]</span>
كان معلّما فى دار أبى الحسن على بن عيسى «١»، مليح الخط صحيحه، من النحويين الذين خلطوا المذهبين. وهو الذى عمل كتاب على بن عيسى فى القرآن ونحله إياه، ورأيت بخطه كتاب شعر أبى تمام، وهو فى غاية الإتقان والجودة.
وصنّف؛ فمن تصنيفه. كتاب المختصر فى علم العربية. كتاب معانى القرآن. كتاب المقصور والممدود. كتاب المذكر والمؤنث. كتاب فى علم اللغة ومنظومها. كتاب أخبار أعيان الحلم «٢»، ألفه لأبى الحسن «٣» عمر ابن محمد بن حماد بن أبى عمرو. كتاب السرارى الذهبيّات «٤» والمسكيات. كتاب أعياد النفوس فى ذكر المعلم «٥». كتاب رمضان وما قيل فيه.

<span data-type="title" id=toc-418>٣٥١ - عبد الله بن محمد بن شقير أبو بكر النحوى [٢]</span>
خلط المذهبين، وهو مشهور بين النحاة، مذكور. تصدّر فأفاد، وصنف.
وله من التصنيف. كتاب مختصر نحو. كتاب المقصور والممدود. كتاب المذكر والمؤنث.
_________
[١] ترجمته فى بغية الوعاة ٢٨٧ - ٢٨٨، وتاريخ بغداد ١٠: ١٢٣، وتلخيص ابن مكتوم ٩٦ - ٩٧، وطبقات ابن قاضى شهبة ١: ٤٦ - ٤٧، والفهرست ٨٢، وكشف الظنون ١٤٥٨، ١٤٦١. وذكر الخطيب وابن مكتوم وابن قاضى شهبة أن وفاته كانت سنة ٣٢٥، وهو مكرر ٣٤٤.
[٢] ترجمته فى تلخيص ابن مكتوم ٩٨.','2','135',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (538,'<span data-type="title" id=toc-419>٣٥٢ - عبد الله بن محمد الأزدىّ أبو القاسم [١]</span>
من أهل البصرة. نحوىّ مذكور مصنّف، فمن نصنيفه كتاب المنطق.

<span data-type="title" id=toc-420>٣٥٣ - عبد الله بن محمد بن على بن محمد أبو القاسم ابن أبى عبد الله الأديب الراقطائىّ [٢]</span>
ويعرف بابن الخوارزمىّ. وراقطا: إحدى بلاد البطائح «١». ووالده قدم من خوارزم، وسكن هذه الناحية، وولد منه عبد الله هذا بها. وطلب العلم وقرأ الأدب على أبيه وغيره، وروى عن مشايخ وقته، وأفاد بها بواسط فى سنة خمسمائة، وقدم بغداذ فى سنة عشر وخمسمائة، وروى بها شيئا من شعره وتصانيفه؛ فمن شعره:
ربّ ليل فريت فروته ... أحبّه وهو بارد بارد
على سناد سناد كلكلها ... عند الونى مثل ساعد ساعد
وما افتقرت المطىّ مفتقرا ... عمرى وما كل واجد واجد
إن تنكرى يا قتيل قتلك لى ... فلى على ذلك شاهد شاهد
تغيير لونى ولمّتى شهدا ... أن الذى طلّ عامد عامد
أقول إذ زارنى وودّعنى ... قل لى متى أنت عائد عائد
وعاد أبو القاسم بن الخوارزمىّ إلى بلده بعد قدومه بغداذ، وتوفى بعد ذلك بيسير. والله أعلم.
_________
[١] ترجمته فى تلخيص ابن مكتوم ٩٨.
[٢] ترجمته فى تلخيص ابن مكتوم ٩٨.','2','136',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (539,'<span data-type="title" id=toc-421>٣٥٤ - عبد الله بن محمد بن محمد بن هبة الله بن علىّ ابن أبى عيسى [١]</span>
من أهل شهرابان «١»، بلدة من أعمال طريق خراسان. من بيت عدالة وقضاء وأدب، وكانت له معرفة بالأدب حسنة. قرأ على أبى محمد عبد الله بن أحمد ابن أحمد بن أحمد بن الخشاب النحو واللغة [و] العربيّة، وحصل له من ذلك طرف صالح، وسمع الحديث من بعض مشايخ زمانه، وله شعر منه:
نحن قوم قد تولّى حظّنا ... وأتى قوم لهم حظّ جديد
وكذا الأيام فى أفعالها ... تخفض الهضب فتستعلى الوهود
إنما الموت حياة لامرىء ... حظّه ينقص والهمّ يزيد
وإذا قام لأمر مكثب ... قعد الحظ به فهو بعيد
ولد ليلة الخميس ثانى عشر شهر رمضان من سنة أربع وثلاثين وخمسمائة، ومرض فى بغداذ فى رجب من سنة ستمائة، فحمل مريضا إلى شهرابان، فمات قبل الوصول إليها بموضع يعرف بالحصن، فى ليلة السبت سادس عشر الشهر المذكور، فحمل ميّتا، ودفن بشهرابان- رحمه الله.

<span data-type="title" id=toc-422>٣٥٥ - عبد الله بن محمد بن عبد الله بن على الأشيرىّ أبو محمد المغربىّ [٢]</span>
أصله من أشير زيرى من برّ العدوة. «٢» وأشير زيرى مدينة قبالة بجاية «٣» وقبلتها،
_________
[١] ترجمته فى تلخيص ابن مكتوم ٩٨.
[٢] ترجمته فى تاريخ فى تاج العروس، ٣: ١٤، وتاريخ الإسلام للذهبىّ (وفيات سنة ٥٦١)، وتلخيص ابن مكتوم ٩٨ - ٩٩، وشذرات الذهب ٤: ١٩٨، وطبقات ابن قاضى شهبة ٢:
٤٨ - ٤٩، واللباب لابن الأثير ١: ٥٥، ومرآة الجنان ٣: ٣٤٧، ومعجم البلدان ١: ٢٦٤ - ٢٦٥، والنجوم الزاهرة ٥: ٣٧٢.','2','137',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (540,'بينهما ثلاثة أيام فى بلاد صنهاجة. «١». وزيرى الذى عمرها واختطّها هو زيرى بن مناد «٢»، أحد مقدّمى صنهاجة فى وقته، وقد بقى الأمر فى ولده وولد ولده مدّة مديدة.
والمعز بن باديس بن بلجّين «٣» من نسله، وهو الذى استولى على بلد إفريقية بعد الفرقة الشيعية المنتقلين إلى مصر عن إفريقية، وسلموها إلى جدّة نيابة، فانفرد بها.
وكان عبد الله بن محمد الأشيرىّ هذا يخدم فى بعض الأمور بدولة عبد المؤمن «٤» ابن على، ولما حصل مع القوم بالأندلس جرى له أمر خشى عاقبته، فانصرف عنهم منهزما منهم، ومعه أهله وكتبه وما أمكنه استصحابه، وقصد الشام، فخرج إلى اللاذقيّة «٥»، وبها الفرنج، وسلّمه الله إلى أن وصل إلى حلب، ونزل على العلاء','2','138',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (541,'محمود الغزنوىّ المدرّس بمدرسة الحلاويين ظاهر باب الجامع، وأقام عنده مدّة وسمع منه الفوائد المغربية، وروى لهم عن ابن العربى «١» والقاضى عياض بن موسى اليحصبى «٢» وأمثالهم، وأقام إلى سنة تسع وخمسين، واتفق أن يحيى بن هبيرة الوزير صنّف كتاب الإفصاح، وجمع له علماء المذاهب، وطلب فقيها مالكيا، فدلّوه على الأشيرىّ، فطلبه من نور الدين محمود بن زنكىّ «٣»، فسيّره إليه، فأكرمه وأنزله وأجرى له نزلا «٤»، وحضر قراءة كتاب الإفصاح «٥»، فمرّت مسألة- سأذكرها- واختلف كلامه وكلام ابن هبيرة، فسبقه عليه ابن هبيرة، وجرت بعد ما سأذكره بعد تمام ترجمته، إن شاء الله.
وحج من بغداذ سنة ستين وخمسمائة، وزار قبر رسول الله صلى الله عليه وسلم وعياله معه، وضاق بهم وبه الحال، فخرج من المدينة، وترك أهله هناك، وذلك فى وسط السنة، وقصد الشام، ولقى نور الدين بظاهر حمص، وذكر له حاله، فوعده بخير. واتفق أنه مرض ومات فى شهر رمضان من سنة إحدى وستين','2','139',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (542,'وخمسمائة، وقيل إنه دفن بظاهر سور حمص قريبا منه. وقال لى ابن الأستاذ عبد الرحمن: إنه دفن بقبر إلياس فى البقاع. والله أعلم.
وسيّر نور الدين إلى أهله نفقة، وخيّرهم فى المقام أو الحضور إلى الشام، فحضروا صحبة ولد له اسمه محمد، ونزلوا حلب وباعوا كتبه فى وفاء دين عليه، وكانت فى غاية الجودة والصحة، وخدم ولده جنديا مع الأمير عز الدين بن جرديك، ومات فى خدمته.
وإنما ذكرت الأشيرىّ فى اللّغويين لأنه صنف كتابا هذّب فيه الاشتقاق الذى صنفه المبرّد،- ورأيته- فأحسن فيه، وهو عندى بخطه- رحمه الله- وذكره الحافظ أبو القاسم «١» على فى كتابه فقال:
«عبد الله بن محمد بن عبد الله أبو محمد الصنهاجىّ المعروف بابن الأشيرىّ. كامل فاضل، سمع بالأندلس أبا جعفر بن غزلون. وأبا بكر محمد بن عبد الله بن محمد ابن العربى الإشبيلىّ وغيرهما، وحصلت له كتب حسان، وكان يكتب لصاحب المغرب، فلما مات صاحبه استشعر، فأخذ كتبه وأهله وتوجه إلى الشام، وقدم دمشق، وأقام بها مديدة، وحدّث بالموطأ وغيره، وسمع منّى وكتب عنّى، وعلّقت عنه شيئا. وكان أديبا، وله شعر جيّد. ثم توجه إلى حلب.
ذكره أبو الليث شاكر بن عبد الله التنوخىّ لنور الدين محمود بن زنكى، والأمير أبو يعقوب يوسف بن على الملثم وهما فى صحبته فى الزيارة بالبقاع، «٢» وأثنيا عليه خيرا كثيرا، ورغّباه فى ترتيبه بحلب لتقوية السّنة بها، ولحاجة أهلها إلى مثله، فنقله الملك العادل إلى حلب، وقرّب له كتابته، وأقام يروى الحديث سنتى ثمان وتسع، وسافر إلى الحج فجاور سنة ستين، ثم قدم فى سنة إحدى وستين، وخلف ولده وزوجته','2','140',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (543,'بمكة، وتوجه إلى حلب مستميحا، واجتمع بمحمود بن زنكى بحلب، وسار بمسيره إلى حمص، وتخلّف بالمرض، ثم تبعه فثقل فى مرضه، وتوفى باللبوة يوم الأربعاء خامس عشرين شوّال سنة إحدى [وستين وخمسمائة]. واستأذن رفقته نور الدين فى دفنه، فرسم لهم بحمله إلى بعلبك، ودفن بظاهر باب حمص شمالى بعلبك. وزار قبره. وخاطبه أبو اليسر «١» فى أمر عيال الأشيرىّ واجتذابهم إلى ظله بالشام شفقة عليهم من ضيقة المعيشة بالحجاز، فرسم لمتولى السبيل أن يجتمع بهم ويقول لهم:
إن شئتم حملتكم إلى الشام، ويقرّر الملك لكم كفايتكم، فإن أجابوا نقلهم. فقدموا فى قافلة الحاج، وبعثهم إلى حلب، وقرّر لهم كفايتهم.

<span data-type="title" id=toc-423>٣٥٦ - عبد الله بن محمد بن السّيد البطليوسىّ النحوىّ [١]</span>
من أهل بطليوس. مدينة من مدن الأندلس، أبو محمد. سكن بلنسية.
كان عالما بالآداب واللغات، متبحّرا فيها، مقدّما فى معرفتها، يجتمع الناس إليه، ويقرءون عليه، ويقتبسون منه. وكان حسن التعليم، جيّد التلقين، ثقة حافظا ضابطا.
_________
[١] ترجمته فى أزهار الرياض ٣: ١٠١ - ١٤٩، وبغية الوعاة ٢٨٨، وتلخيص ابن مكتوم ٩٩ - ١٠٠، وابن خلكان ١: ٢٦٥، والديباج المذهب ١٤٠ - ١٤١، وشذرات الذهب ٤: ٦٤ - ٦٥، والصلة لابن بشكوال ١: ٢٨٧، وطبقات ابن قاضى شهبة ١: ٤٧ - ٤٨، وطبقات القرّاء لابن الجزرى ١: ٤٤٩، وعيون التواريخ (وفيات سنة ٥٢١) وقلائد العقيان ١٩٣ - ٢٠٢، وكشف الظنون ٤٨، ٤٨٨، ٦٠٣، ٩٩٢، ١٥٨٧، ١٩٠٧، ومرآة الجنان ٣: ٢٢٨، ومسالك الأبصار ج ٤ مجلد ٣: ٤٠٤ - ٤٠٥، ومعجم البلدان ٢: ٢١٧. والسيد، بكسر السين وسكون الياء، من أسماء الذئب، سمى به جده. والبطليوسىّ، بفتح الباء والطاء وسكون اللام وفتح الياء وسكون الواو: منسوب إلى بطليوس، مدينة جليلة بالأندلس.','2','141',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (544,'وألف كتبا حسانا؛ فمن ذلك كتاب الاقتضاب فى شرح أدب الكتاب «١».
كتاب الأسباب الموجبة لاختلاف الأمة «٢» كتاب شرح الموطّأ. كتاب المثلث «٣» فى اللغة، كبير. كتاب شرح سقط الزّند «٤». كتاب إصلاح الخلل الواقع فى شرح الجمل «٥». كتاب شرح أبيات الجمل «٦». كتاب التذكرة الأدبية «٧».
وله شعر حسن منه:
أخو العلم حىّ خالد بعد موته ... وأوصاله تحت التراب رميم
وذو الجهل ميت وهو ماش على الثّرى ... يظنّ من الأحياء وهو عديم
وكان قد سكن قرطبة فى أيام محمد بن الحاج صاحب قرطبة، وكان كاتبه علىّ الكاتب، ومدار الأمور بقرطبة عليه، وكان له بنون ثلاثة؛ يسمى أحدهم','2','142',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (545,'عزون، والثانى رحمون، والثالث حسون، وكانوا صغارا فى حدّ الحلم، وكانوا من أجمل الناس صورا، وكان شكل شعورهم قطاطىّ مضفورة، وكانوا يقرءون القرآن على المقرئ، ويختلفون إلى الجامع إليه فى ذلك، وكان أبو محمد بن السّيد قد أولع بهم، ولم يمكنه صحبتهم إذ كان من غير صنفهم ولا منهم. وكان يجلس فى الجامع تحت شجرة يتعلّل فى كتاب يقرأ فيه، فقال فيهم بيتين وهما:
أخفيت سقمى حتى كاد يخفينى ... وهمت فى حبّ عزّون فعزّونى
ثم ارحمونى برحمون فإن ظمئت ... نفسى إلى ريق حسّون فأحسونى
وخاف على نفسه بسبب أبيهم، ففرّ من قرطبة وخرج إلى بلنسية، وأقرأ بها، وألّف بها تواليفه إلى أن توفّى- رحمه الله- منتصف رجب من سنة إحدى وعشرين وخمسمائة. ومولده سنة أربع وأربعين وأربعمائة.

<span data-type="title" id=toc-424>٣٥٧ - عبد الله بن مسلم بن قتيبة أبو محمد الكاتب الدينورىّ النحوىّ اللغوىّ العالم [١]</span>
صاحب التصانيف الحسان فى فنون العلوم. مروزىّ الأصل. ولد ببغداذ، ونشأ بها وتأدّب، وأقام بالدّينور مدّة فنسب إليها.
_________
[١] ترجمته فى الأنساب ٤٤٣ ا، وبغية الوعاة ٢٩١، وتاريخ ابن الأثير ٦: ٦٦، وتاريخ بغداد ١٠: ١٧٠ - ١٧١، وتاريخ أبى الفدا ٢: ٥٤، وتاريخ ابن كثير ١١: ٤٨، ٥٧، وتذكرة الحفاظ ٢: ١٨٧، وتفسير سورة الإخلاص لابن تميية ١٠٤، ١٢٠ - ٢٢١، ١٣٣ - ١٣٤، وتلخيص ابن مكتوم ١٠٠، وتهذيب الأسماء واللغات ٢: ٢٨١، وتهذيب اللغة للأزهرى ١: ١٥، وابن خلكان ١: ٢٥١، وذيل كشف الظنون ٢: ١٤٦، ٥٠٦، وروضات الجنات ٤٤٧، وشذرات الذهب ٢: ١٦٩ - ١٧٠، وطبقات الزبيدى ١٢٩، وطبقات ابن قاضى شهبة ٢: ٥٢ - ٥٤، وطبقات المفسرين للداودى ١٠٣ ا- ١٠٤ ب؛ والفهرست ٧٧ - ٧٨،','2','143',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (546,'روى عن العلماء أمثال إسحاق بن راهويه «١»، ومحمد «٢» بن زياد الزيادىّ، وأبى حاتم السّجستانىّ. روى عنه العلماء كولده أحمد «٣»، وأبى محمد عبد الله بن جعفر ابن درستويه الفارسىّ.
وكان عبد الله بن مسلم بن قتيبة ثقة ديّنا فاضلا. فمن تصانيفه: غريب القرآن «٤». غريب الحديث «٥». مشكل القرآن «٦». مشكل الحديث.
_________
- وكشف الظنون ٣٢، ٤٧، ١٠٨، ٤٧٠، ٧٦٠؛ ٨٠٧، ١١٠٢، ١١٨٤، ١٢٠٤، ١٣٩٢؛ ١٣٩٩، ١٤٦٩، ١٦٩٥، ١٧٢٤، واللباب لابن الأثير ٢:
٢٤٢، ولسان الميزان ٣: ٣٥٧ - ٣٥٩، ومراتب النحويين، ١٣٧ - ١٣٨، ومرآة الجنان ٢: ١٩١ - ١٩٢، والمزهر ٢: ٤٠٩، ٤٢٠، ٤٦٥، والمنتظم (وفيات سنة ٢٧٦)، وميزان الاعتدال ٢: ٧٠، والنجوم الزاهرة ٣: ٧٥ - ٧٦، ونزهة الألباء ٢٧٢ - ٧٤. قال ابن خلكان: «والدينورى، بكسر الدال (وقال السمعانى بفتحها وليس بصحيح) وبسكون الياء وفتح النون والواو، وهذه النسبة إلى دينور، وهى من بلاد الجبل عند قرميسين، خرج منها خلق كثير».','2','144',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (547,'أدب الكاتب «١». عيون الأخبار «٢». المعارف «٣». طبقات الشعراء «٤».
الأشربة «٥». إصلاح الغلط «٦». كتاب الفرس «٧». معانى الشعر «٨».','2','145',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (548,'كتاب التفقيه «١». كتاب الخيل. كتاب النحو «٢». كتاب إعراب القرآن «٣». كتاب الأنواء «٤». كتاب التسوية بين العرب والعجم. كتاب الفقه «٥». كتاب المسائل والجوابات «٦». كتاب العلم. كتاب الميسر والقداح «٧». كتاب النحو الصغير. كتاب الردّ على المشبهة «٨».
أكل- رحمه الله- هريسة فأصاب حرارة، ثم صاح صيحة شديدة ثم أغمى عليه إلى وقت صلاة الظهر، ثم اضطرب ساعة، فما زال يتشهد إلى وقت السحر، ثم مات، وذلك أوّل ليلة من رجب سنة ست وسبعين ومائتين.','2','146',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (549,'قال محمد بن إسحاق النديم فى كتابه: «إن ابن قتيبة كوفىّ؛ وإنما سمى الدّينورىّ لأنه كان قاضى الدّينور، وكان يغالى فى [مذهب] البصريين؛ إلا أنه خلط المذهبين، وحكى فى كتبه عن الكوفيين، وكان صادقا فيما يرويه، عالما باللغة والنحو وغريب القرآن ومعانيه [و] الشعر والفقه، كثير التصنيف والتأليف. وكتبه بالجبل مرغوب فيها. ومولده فى مستهلّ رجب، وتوفى سنة سبعين ومائتين».

<span data-type="title" id=toc-425>٣٥٨ - عبد الله بن مسلم القيروانىّ النحوىّ أبو محمد [١]</span>
كانت له معرفة بالنحو واللغة، وندب إلى درسها بدار الكتب بمدرسة النظامية ببغداذ، واستفاد منه قوم. وهو مستور الحال.

<span data-type="title" id=toc-426>٣٥٩ - عبد الله بن محمود أبو محمد المكفوف النحوىّ القيروانىّ [٢]</span>
كان من أعلم خلق الله تعالى بالعربية والغريب والشعر وتفسير المشروحات وأيام العرب وأخبارها ووقائعها.
وأدرك المهرىّ «١» وأخذ عنه، ثم صحب من بعده حمدون»
النعجة، فكان لا يبارحه، ولم يمت حمدون حتى علا المكفوف عليه، وفضله فى أشياء كثيرة.
_________
[١] ترجمته فى بغية الوعاة ٢٩١، وتلخيص ابن مكتوم ١٠٠. وذكر السيوطى أن وفاته كانت سنة ٤٨٨.
[٢] ترجمته فى بغية الوعاة ٢٩٠، وتلخيص ابن مكتوم ١٠٠ - ١٠١؛ وطبقات الزبيدىّ ١٥٩، ١٦٠، وطبقات ابن قاضى شهبة ٢: ٥٢، ومسالك الأبصار ج ٤ مجلد ٣: ٣٩٦، ونكت الهميان ١٨٤ - ١٨٥. وما ذكره المؤلف فى ترجمته يوافق ما فى طبقات الزبيدىّ.','2','147',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (550,'وله كتب كثيرة أملاها فى اللغة والعربية والغريب، وله كتاب فى العروض يفضّله أهل العلم على سائر الكتب المؤلّفة فيه، لما بيّن [فيه] «١» وقرّب. وعليه قرأ الناس المشروحات، وإليه كانت الرحلة من جميع إفريقية والمغرب، وكان يجلس مع حمدون فى مكتبه، فربما استعار من بعض الصبيان كتابا فيه شعر أو غريب أو شىء من أخبار العرب فيقتضيه صاحبه إياه، فإذ ألحّ عليه أعلم أبا محمد المكفوف بذلك فيقول: اقرأه علىّ، فإذا فعل قال: أعده ثانية، ثم يقول: ردّه على صاحبه، ومتى شئت تعال حتى أمليه عليك.
وقيل: أبطأ عنه أبو القاسم بن عثمان الوزان النحوىّ أياما كثيرة ثم أتاه، فلامه على تخلّفه عنه، فقال له أبو القاسم: نحن كنا سبب ما أنت فيه من العلم، وقد علمت كيف كنت أخصّك وأوثرك على غيرك، فلما صرت إلى هذه الحال قطعتنا عنك. فقال له: أصلحك الله! أعذر، فقد كان لى شغل، قال:
وما هو؟ قال: لى اليوم أكثر من شهر أختلف إلى رقّادة «٢» إلى دار فلان- وذكر بعض السلاطين- أشكل له كتبا وأصححها، فقال: سررتنى والله، قال: بماذا سررتك؟ قال: بما يكون من برّه ومكافأته على اختلافك إليه وتصحيحك لكتبه.
فضحك وقال: والله ما هو إلا أنّه أكترى دابة إذا مررت وإذا رجعت من مالى.
فعجب من ذلك، وقال: تدرى كم وصل إلىّ من ابن الصائغ صاحب البريد؟ قال: لا. قال: نحو خمسمائة دينار، سوى الخلع وقضاء الحاجات والبرّ والإكرام؛ وما كان يسألنى عن شىء إلا أنه إذا كان يوم الجمعة بعث فى طلبى ابنه ودابته وأحضر مائدته.','2','148',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (551,'وكان أبو محمد المكفوف أصله من سرت «١»، فهجاه إسحاق بن خنيس فقال:
ألا لعنت سرت وما جاء من سرت ... فقد حلّ من أكنافها جبل المقت
فى شعر طويل له، فقال المكفوف فيه مجيبا له:
إن الخنيسىّ يهجونى لأرفعه ... اخسأ خنيس فإنى غير هاجيكا
لم تبق مثلبة تحصى إذا جمعت ... من المثالب إلا كلّها فيكا
ولأبى محمد أشعار فصيحة وأراجيز غربية. وله كتاب فى شرح صفة أبى زبيد الطائىّ للأسد جوّد فيه وحسّنه. وتوفى سنة ثمان وثلاثمائة.

<span data-type="title" id=toc-427>٣٦٠ - عبد الله بن مخلد بن خالد بن عبد الله التميمى النيسابورىّ أبو محمد النحوىّ [١]</span>
ذكره الحافظ أبو عبد الله فى تاريخ نيسابور، وسماه «النحوى»، وقال:
«سمع بخراسان علىّ بن الحسين بن شقيق وعبدان وحفص بن عبد الرحمن ومكىّ ابن إبراهيم ويحيى بن يحيى وغيرهم، وبالبصرة من عفان وبشر بن محمد السكرىّ ومسلم وغيرهم، وبالكوفة من أبى نعيم وأبى غسّان وغيرهما، وبالحجاز من عبد العزيز الأويسى وإسماعيل بن أبى أويس وغيرهما».
«وهو راوى كتب أبى عبيد القاسم بن سلّام عنه بخراسان. روى عنه أبو بكر الجارودىّ ومحمد بن إسحاق بن خزيمة».
«قرأت فى بعض كتب أصحابنا: توفى عبد الله بن محمد سنة ستين ومائتين، ومسكنه بباب فراشة».
_________
[١] ترجمته فى بغية الوعاة ٢٩، وتلخيص ابن مكتوم ١٠١، وخلاصة تذهيب الكمال ١٨١.','2','149',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (552,'<span data-type="title" id=toc-428>٣٦١ - عبد الله بن مؤمن بن مؤمل بن عنافر التجيبىّ المزوكىّ النحوىّ الإشبيلىّ الأندلسىّ أبو محمد [١]</span>
عالم بالنحو والشعر والحساب والعروض، حافظ للقرآن، كثير التلاوة، مذهبه جميل، وطريقته قويمة. وله شعر كثير فى الزهد.

<span data-type="title" id=toc-429>٣٦٢ - عبد الله بن مهران بن الحسن أبو بكر النحوىّ [٢]</span>
سمع هوذة بن خليفة بن عفان بن مسلم، وعاصم بن علىّ، وعلى بن الجعد، ومعلى بن مهدىّ. روى عنه أبو عمرو بن السماك، ومحمد بن العباس بن نجيح وأحمد بن كامل القاضى، وأبو بكر الشافعىّ.
وكان ثقة يسكن سويقة نصر ببغداذ. وكان ضريرا. وذكر ابن كامل أنه سمع منه فى سنة سبع وتسعين ومائتين. وكان ثقة. وقال الدار قطنىّ: لا بأس به.

<span data-type="title" id=toc-430>٣٦٣ - عبد الله بن هارون بن يحيى النيسابورىّ [٣]</span>
ذكره الحافظ أبو عبد الله فى تاريخه وقال: «أبو بكر النحوىّ، سمع إسحاق ابن إبراهيم الحنظلىّ وعمرو بن فزارة. روى عنه أبو عبد الله بن دينار. توفى فى رجب سنة تسع وثمانين ومائتين».

<span data-type="title" id=toc-431>٣٦٤ - عبد الله بن يس أبو محمد التميمىّ النحوىّ الأديب [٤]</span>
من أهل الأدب. قرأ منه قطعة صالحة على أبى منصور الجواليقىّ وابن الشجرىّ ببغداذ، وقدم دمشق، ثم خرج منها، وعاد إليها، وكان يكتب خطا
_________
[١] ترجمته فى بغية الوعاة ٢٩١، وطبقات الزبيدى ٢٠١؛ ولم يذكره ابن مكتوم فى التلخيص.
[٢] ترجمته فى تاريخ بغداد ١٠: ١٧٨ - ١٧٩؛ ولم يذكره ابن مكتوم فى التلخيص.
[٣] لم أعثر له على ترجمة، ولم يذكره ابن مكتوم فى التلخيص.
[٤] ترجمته فى تلخيص ابن مكتوم ١٠١.','2','150',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (553,'حسنا، ويذهّب المصاحف. ثم توجه إلى بلاد العجم وقطن خوارزم، ونفق على صاحبها، وكسب من جهته مالا، ومات هناك.

<span data-type="title" id=toc-432>٣٦٥ - عبد الله بن يحيى بن المبارك بن المغيرة أبو عبد الرحمن ابن أبى محمد العدوىّ المعروف بابن اليزيدىّ [١]</span>
كان أديبا عارفا بالنحو واللغة. أخذ عن ابن زياد الفرّاء، وصنف كتابا فى غريب القرآن حسنا فى بابه، ورأيته فى ستّة مجلدات، يستشهد على كل كلمة من القرآن بأبيات من الشعر؛ ملكته بخطه، وقد كتب عليه أبو سيف القزوينى المعتزلىّ شيئا بخطه أخطأ فيه، وذلك أنه نسبه إلى أبى محمد أبيه.
وصنّف عبد الله أيضا كتابا فى النحو مختصرا، وكتاب الوقف والابتداء وكتاب إقامة اللسان على صواب المنطق. روى عنه أخوه الفضل بن محمد اليزيدىّ.
قال أحمد بن يحيى النحوىّ: ما رأيت فى أصحاب الفرّاء أعلم من عبد الله بن محمد اليزيدىّ وخاصّة فى القرآن ومسائله.
_________
[١] ترجمته فى تاريخ بغداد ١٠: ١٩٨ - ١٩٩، وتلخيص ابن مكتوم ١٠١ وطبقات القراء ١: ٤٦٣، والفهرست ٥٠ - ٥١، ونزهة الألباء ٢٢٦ - ٢٢٧، وما ذكره المؤلف يوافق ما فى تاريخ بغداد. وانظر نسبة اليزيدىّ فى حواشى الجزء الأوّل ص ١٦١.','2','151',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (554,'<span data-type="title" id=toc-433>٣٦٦ - عبد الله بن يوسف بن عبد الله بن يوسف بن محمد ابن حيّويه الجوينىّ ثم النيسابورىّ أبو محمد [١]</span>
الأديب النحوىّ المفسر؛ أوحد زمانه. تأدّب على أبيه. توفى فى ذى القعدة سنة ثمان وثلاثين وأربعمائة «١».

<span data-type="title" id=toc-434>٣٦٧ - عبيد الله بن أحمد بن محمد أبو الفتح [٢]</span>
يعرف بجحجح «٢» النحوىّ. سمع أبا القاسم البغوىّ وطبقته، وأبا بكر بن دريد ومن بعده، وحدّث بشىء يسير. سمع منه أبو الحسين بن الفرات، ومحمد ابن أبى الفوارس، وروى عنه إبراهيم بن مخلد، وكان ثقة صحيح الكتاب. قال محمد بن العباس بن الفرات: مولد أبى الفتح عبيد الله بن أحمد بن محمد النحوىّ
_________
[١] ترجمته فى الأنساب ١٤٤ ب، وتلخيص ابن مكتوم ١٠١، وابن خلكان ١: ٢٥٢ - ٢٥٣، وطبقات الشافعية ٣: ٢٠٨ - ٢١٩، وطبقات المفسرين للسيوطى ١٥، وطبقات المفسرين للداودىّ الورقة ١٠٦ ب- ١٠٧ ا، وكشف الظنون ٣٣٩، ٣٨٥، ٤٤٥، ٦١٠، ٩٩٦، ١٦٢٦، ١٩١٠، واللباب لابن الأثير ١: ٢٥٦، ٢٥٧. قال ابن خلكان: «وحيويه، بفتح الحاء المهملة وتشديد الياء المثناة من تحتها وضمها وسكون الواو وفتح الياء الثانية وبعدها هاء. والجوينى، بضم الجيم وفتح الواو وسكون الياء المثناة من تحتها وبعدها نون. هذه النسبة إلى جوين، وهى ناحية كبيرة من نواحى نيسابور، وتشتمل على قرى كثيرة مجتمعة».
[٢] ترجمته فى بغية الوعاة ٣١٩، وتلخيص ابن مكتوم ١٠١ - ١٠٢، وروضات الجنات ٤٦٦، وكشف الظنون ٢٦، ١٤٣٩، ١٥٩١، ونزهة الألباء ٣٧٨ - ٣٧٩.','2','152',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (555,'سنة ست وثمانين. وتوفى ليلة الجمعة، ودفن يوم الجمعة لعشر خلون من جمادى الآخرة سنة ثمان وخمسين وثلاثمائة «١».

<span data-type="title" id=toc-435>٣٦٨ - عبيد الله بن فرج الطوطالقى النحوىّ القرطبىّ أبو محمد [١]</span>
روى عن أبى على القالىّ وأبى عبد الله الرياحىّ وابن القوطية ونظرائهم، وتحقق بالأدب واللغة، وعنى بذلك؛ وألف كتابا مختصرا فى المدوّنة «٢»، استحسن؛ وتوفى يوم الاثنين النصف من رجب سنة ست وثمانين وثلاثمائة، ودفن صبحة يوم الثلاثاء بمقبرة مومرة.

<span data-type="title" id=toc-436>٣٦٩ - عبيد الله بن محمد بن يحيى بن المبارك بن المغيرة أبو القاسم العدوىّ المعروف بابن اليزيدىّ اللغوىّ [٢]</span>
سمع عبد الرحمن بن أخى الأصمعى، وروى عن عمه إبراهيم بن يحيى وأخيه أحمد بن محمد عن جده أبى محمد اليزيدىّ عن أبى عمرو بن العلاء حروفه فى القرآن.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٢، والصلة لابن بشكوال ١: ٢٩٤ - ٢٩٥، ومعجم البلدان ٦: ٧٢. والطوطالقى، بضم أوّله وسكون ثانيه: منسوب إلى طوطالقة، وهى بلدة بالأندلس من إقليم باجة.
[٢] ترجمته فى تاريخ بغداد ١٠: ٣٣٨، وتلخيص ابن مكتوم ١٠٢، وطبقات ابن قاضى شهبة ٢: ١٢١، وطبقات القراء لابن الجزرى ١: ٤٩٢ - ٤٩٣، ومعجم الأدباء ١٢:
٥٩ - ٦١.','2','153',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (556,'روى عنه ابن أخيه محمد بن العباس اليزيدىّ وغيره. وكان ثقة، وكان يعلّم النحو ويسمى النحوىّ. قال سليمان بن أحمد بن أيوب الطّبرانى: «١» حدّثنا عبيد الله بن محمد بن أبى محمد اليزيدىّ أبو القاسم البغداذىّ النحوىّ «٢». وسماه النحوىّ. وقال ابن المنادى «٣»: عبيد الله بن محمد بن يحيى أبو القاسم؛ كان اليزيدىّ جدّه، كتب عنه الحروف، وشيئا من اللغة، وأكثر من الحديث فى أصناف الكتب.
توفى فى المحرّم سنة أربع وثمانين- يعنى ومائتين.

<span data-type="title" id=toc-437>٣٧٠ - عبيد الله بن محمد بن جرو الأسدىّ الموصلىّ أبو القاسم النحوىّ [١]</span>
من أصحاب أبى علىّ «٤» وتلك الحلبة. قرأ وأكثر الأخذ عن النحاة، وشدا شيئا من اللغة، وتصدر لإقراء هذا الشأن.
_________
[١] ترجمته فى بغية الوعاة ٣٢٠، وتاج العروس ١٠: ٧١، وتلخيص ابن مكتوم ١٠٢، وطبقات ابن قاضى شهبة ١: ١٢٠ - ١٢١، وطبقات المفسرين للسيوطى ٢٢، وطبقات المفسرين للداودى الورقة ١٥٨، وكشف الظنون ١٧٧٤، ١٩٠٤، ومعجم الأدباء ١٢: ٦٢ - ٦٨.','2','154',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (557,'نقلت من خط ابن عياض النحوى الشامى الكفر طابىّ: أنشد أبو القاسم عبيد الله بن أحمد بن جرو الأسدىّ الموصلىّ فى مسألة ياءات الإضافة:
ويسقط «١» بينها المرئى «٢» لغوا ... كما أسقطت فى الدية الحوارا «٣»
وذكر هلال بن المحسن فى كتابه تاريخ بغداذ قال: «وفى يوم الثلاثاء لأربع بقين من رجب سنة سبع وثمانين وثلاثمائة توفى أبو القاسم عبيد الله بن محمد بن جرو الأسدى «٤»».

<span data-type="title" id=toc-438>٣٧١ - عبد الباقى بن محمد بن بانيس النحوىّ [١]</span>
عراقى، لقى الجماعة المذكورين: أبا سعيد، وأبا على، ويوسف بن أبى سعيد، وعلى بن عيسى بن علىّ الرمانى. وعاصر ابن جنى والربعىّ وأمثالهما.
وكان نحويا متصدّرا للإفادة.
قال هلال بن المحسن بن إبراهيم فى كتابه:
«ولعشرين بقين من ربيع الأوّل سنة أربعمائة مات عبد الباقى بن محمد بن بانيس النحوىّ».
_________
[١] ترجمته فى بغية الوعاة ٢٩٤، وتلخيص ابن مكتوم ١٠٢. واسمه فى بغية الوعاة: «عبد الباقى ابن الحسن بن عبد الله النحوىّ»، وذكر أنه مات سنة نيف وتسعين وثلاثمائة. وحكى أنه نقل ذلك عن الصفدىّ.','2','155',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (558,'<span data-type="title" id=toc-439>٣٧٢ - عبد الباقى بن محمد بن الحسين بن داود بن ناقيا البندار الشاعر [١]</span>
من أهل الحريم الطاهرىّ «١»، يسكن شارع التوفيق من درب العوج. شاعر مجوّد رقيق الشعر جواد الخاطر والطبع. ولشعره ديوان كبير، وله فى العربية يد باسطة.
وصنف كتبا جميلة منها: تفسير الفصيح لثعلب، وملح الممالحة.
وكتب بخطه كتبا كثيرة فى الأدب، وينسب إلى التعطيل وذهاب مذهب الأوائل، وصنف فى ذلك مقالة، وكان كثير المجون، روى شيئا من الحديث عن بعض مشايخ زمانه. روى عنه ابن السمرقندىّ ومحمد بن ناصر السلامى.
وقال غيره: كان قليل الدين، وكان يسمى عبد الله أيضا، وقد ورد ذكره فى تبيين من اسمه عبد الله. سئل عن مولده فقال: فى النصف من ذى القعدة سنة عشر وأربعمائة. ومن شعره:
خلعت التّصابى واستراح عذولى ... وصار سبيل الناسكين سبيلى
فياربّ لهو قد شهدت وفتية ... صحبتهم صرفا بكأس شمول
وقد يرد الحانات زقّى مقدّما ... ويكرم دون الطارقين رسولى
_________
[١] ترجمته فى تاريخ ابن كثير ١٢: ١٤١، وخريدة القصر، ١: ١٤٢، وابن خلكان ١: ٢٦٦، وطبقات المفسرين الداودى ١٠٧ ا- ١٠٧ ب، وطبقات ابن قاضى شهبة ٢:
٥٨ - ٥٩، وكشف الظنون ٧٦٩، ١١٧٣، ١٨١٧، ولسان الميزان ٣: ٣٨٤ - ٣٨٥، والمنتظم (وفيات سنة ٤٨٥)، وميزان الاعتدال ٢: ٨٢. وانظر ص ١٣٣ من هذا الجزء.
و «ناقيا» ضبطه ابن خلكان: «بفتح النون وبعد الألف قاف مكسورة ثم ياء مثناة من تحتها نون مفتوحة وبعدها ألف».','2','156',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (559,'وخمّارة لاذت برحلى تكرما ... فكان مبيتى عندها ومقيلى
أظلّ إذا فار الهجير ببيتها ... وصحبى فى ظلّ هناك ظليل
ندير أباريق الشّمول وللدّجى ... نجوم على الآفاق غير أفول
فيغنين عن ضوء المصابيح أكؤسا ... قناديلها تذكى بغير فتيل
ومحسنة أمّا إذا شئت غردّت ... فبين خفيف تارة وثقيل
أرى الذّكر بعد المال يخلد باقيا ... ولم أر ذكرا صالحا لبخيل
قال محمد بن ناصر: مات أبو القاسم بن ناقيا يوم الأحد رابع المحرم سنة خمس وثمانين وأربعمائة ودفن بباب الشام.
قال أبو الحسن على بن محمد بن أحمد الدهّان المرتّب بجامع المنصور:
دخلت على الشيخ أبى القاسم بن ناقيا بعد موته لأغسله، فوجدت يده اليسرى مضمومة، فاجتهدت حتى فتحتها، وفيها كتابة بعضها على بعض، فتمهلّت حتى قرأتها، فإذا فيها مكتوب:
نزلت بجار لا يخيّب ضيفه ... أرجّى نجاتى من عذاب جهنم
وإنّى على خوف من الله واثق ... بإنعامه والله أكرم منعم

<span data-type="title" id=toc-440>٣٧٣ - عبد الحميد بن عبد المجيد أبو الخطاب الأخفش الكبير النحوى [١]</span>
أخذ عنه يونس، وهو من أئمة اللغة والنحو، وله ألفاظ لغويّة انفرد بنقلها عن العرب. والأخافش المشهورون من النحاة ثلاثة، أكبرهم هذا، والأوسط
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٦، وبغية الوعاة ٢٩٦، وتلخيص ابن مكتوم ١٠٢، وطبقات الزبيدىّ ١٧، وطبقات ابن قاضى شهبة ٢: ٦١، ومرآة الجنان ٢: ٦١، ومسالك الأبصار ج ٤ مجلد ٢: ٢٧٢، ونزهة الألباء ٥٣ - ٥٤. ولم يعرف تاريخ وفاته.','2','157',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (560,'سعيد بن مسعدة الآخذ عن سيبويه، والأخير علىّ بن سليمان. وقال اليمنى:
«هو الأوسط» «١»، وغلط وقال: «هو مولى من أهل هجر، وكان نحويا لغويا أخذ عنه أبو عبيدة معمر بن المثنّى وسيبويه وغيرهما». روى ذلك عن يوسف ابن يعقوب السكيت عن الجماز». وقال: «هو فى طبقة عيسى بن عمر ويونس، وأخذ عنه سيبويه».

<span data-type="title" id=toc-441>٣٧٤ - عبد الدائم بن مرزوق بن جبير اللغوىّ [١]</span>
الأندلسى المنزل، القيروانىّ الأصل. يكنى أبا القاسم. نزل المريّة، وكان قد روى كثيرا من كتب الأدب واللغة. وكان قد رحل إلى المشرق، ودخل العراق، وأخذ عن علمائها فى سنة ست وعشرين وأربعمائة، ولقى أبا العلاء المعرّىّ وأخذ عنه شيئا من الأدب، وروى عنه شيئا من شعره سقط الزّند فى سنة ثلاث وعشرين وأربعمائة. وكان حيا «٢» فى سنة سبع وستين وأربعمائة؛ فإنه كتب شيئا بخطه فى هذا التاريخ.

<span data-type="title" id=toc-442>٣٧٥ - عبد الرحمن بن إسماعيل بن عبد الله بن سليمان الخولانىّ النحوىّ العروضىّ الخشاب المصرىّ أبو عيسى [٢]</span>
يروى عن النّسائى «٣» وغيره. كان أديبا فاضلا متصدّرا بمصر لإفادة هذا الشأن، وله شعر أجود من شعر النحاة، فمنه ما قاله يرثى به الحافظ عبد الرحمن بن يونس
_________
[١] ترجمته فى بغية الوعاة ٢٦٩، وتلخيص ابن مكتوم ١٠٣، وبغية الملتمس للضبىّ ٣٨٦؛ واسمه فيها: «عبد الدائم بن مرزوق بن جبر».
[٢] ترجمته فى حواشى الجزء الأوّل ص ١٣٩.','2','158',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (561,'ابن عبد الأعلى المصرى «١» المحدّث المؤرّخ- رحمه الله- وكان قد حضر جنازته فى يوم الاثنين لست وعشرين ليلة مضت من جمادى الآخرة من سنة سبع وأربعين وثلاثمائة، وصلى عليه أبو القاسم بن حجاج:
بثثت «٢» علمك تشريقا وتغريبا ... وعدت بعد لذيذ الأنس مندوبا
أبا سعيد وما نألوك إن نشرت ... عنك الدواوين تصديقا وتصويبا
ما زلت تلهج بالتاريخ تكتبه ... حتى رأيناك فى التاريخ مكتوبا
أرّخت موتك فى ذكرى وفى صحف ... لمن يؤرّخنى إذ كنت محسوبا
نشرت عن مصر من سكانها علما ... مبجّلا بجمال القوم منصوبا
كشفت عن فخرهم للناس ما سجعت ... ورق الحمام على الأغصان تطريبا
أعربت عن عرب نقّبت عن نجب ... سارت مناقبهم فى الناس تنقيبا
نشرت ميّتهم حيّا بنسبته ... حتى كأن لم يمت إذ كان منسوبا
إن المكارم للإحسان موجبة ... وفيك قد ركّبت يا عبد «٣» تركيبا
حجبت عنّا وما الدنيا بمظهرة ... شخصا وإن جلّ إلا عاد محجوبا
كذلك الموت لا يبقى على أحد ... مدى الليالى من الأحباب محبوبا
قال ابن الطّحان المصرى «٤» فى تاريخه: «توفى عبد الرحمن بن إسماعيل بن عبد الله ابن سليمان الخولانىّ النحوىّ العروضى الخشاب فى صفر سنة ست وستين وثلاثمائة».','2','159',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (562,'<span data-type="title" id=toc-443>٣٧٦ - عبد الرحمن بن إسحاق ويعرف بالزجّاجىّ أبو القاسم [١]</span>
نهاوندىّ، من أهل الصّيمرة «١» أصله، وانتقل إلى بغداذ، ولزم الزّجاج أبا إسحاق، وقرأ عليه النحو، وانتقل إلى الشام، فأقام بحلب مدّة، ثم انتقل إلى دمشق، وأقام بها وصنّف، وخرج مع ابن الحارث عامل الضّياع الإخشيدية، فمات بطبريّة «٢» فى شهر رمضان سنة أربعين وثلاثمائة- رحمه الله.
وكانت طريقته فى النحو متوسطة، وتصانيفه يقصد بها الإفادة. ولما وردت له مسائل إلى العراق مع بعض الطلبة وقف عليها أبو على الفارسىّ- وقد كان رفيقه- فقال: لو رآنا الزجاجى لاستحيا منا. وقد واخذه جماعة فى تصانيفه، فمنها كتاب فى شرح مقدّمة أدب الكاتب ردّ عليه فيها جماعة من العلماء، وكتابه فى النحو المسمى الجمل «٣» تعرض له البطليوسىّ، وصنف فيه كتابا سماه الحلل، فى إصلاح الخلل، الواقع فى كتاب الجمل، وقد نكت ابن بابشاذ فى شرحه نكتا فى الردّ عليه، والكتاب مبارك ما اشتغل به أحد إلا انتفع.
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٦ - ٢٧، والإكمال لابن ماكولا ٢: الورقة ١١ ا، والأنساب ٢٧٢ ا، وبغية الوعاة ٢٩٧، وتاريخ ابن عساكر ٢٢: ٣٥٤ - ٣٥٨، وتلخيص ابن مكتوم ١٠٤، وابن خلكان ١: ٢٨٨، وروضات الجنات ٤٢٥، وطبقات الزبيدى ٨٦، وطبقات ابن قاضى شهبة ٢: ٦٥ - ٦٦، وعيون التواريخ (وفيات سنة ٣٤٠)، والفهرست ٨٠، وكشف الظنون ٤٨، ١٦٤، ٢١٠، ٦٠٣، ١٦٢٥، واللباب ١: ٤٩٧، والمزهر ٢:
٤٢١، ٤٤٨، ٤٦٦، ونزهة الألباء ٣٧٩. والزجاجىّ، بفتح الزاى وتشديد الجيم: منسوب إلى الزجاج أبى اسحاق إبراهيم بن السرى؛ لملازمته له.','2','160',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (563,'وسمعت من لفظ الشيخ أبى البقاء صالح «١» بن عادى العذرىّ الأنماطىّ النحوىّ نزيل قفط أن الزّجاجىّ- رحمه الله- صنف الجمل بمكة، حماها الله.
وكان إذا فرغ من باب طاف به أسبوعا، ودعا الله أن يغفر له، وأن ينفع به قارئه؛ فلهذا انتفع به الطلبة. وهو كتاب المصريين وأهل المغرب وأهل الحجاز واليمن والشام إلى أن اشتغل الناس باللمع لابن جنّى، والإيضاح لأبى على الفارسى. «٢»

<span data-type="title" id=toc-444>٣٧٧ - عبد الرحمن بن أخى الأصمعىّ [١]</span>
ويكنى أبا محمد، وقيل يكنى أبا الحسن. وكان من الثقلاء؛ إلا أنه كان ثقة عمّا يرويه عن عمه وعن غيره من العلماء.
وكان عمه إذا أكثر أنكر عليه؛ وربما كذّبه. وقيل إن رجلا لقيه فى الطريق فقال: ما يصنع عمك؟ فقال: ها هو قاعد فى غرفته يكذب على العرب.
وصنف عبد الرحمن هذا كتاب معانى الشعر.

<span data-type="title" id=toc-445>٣٧٨ - عبد الرحمن بن بزرج اللغوىّ [٢]</span>
كان حافظا للغريب والنوادر. صنف كتابا فى النوادر. قال أبو منصور الأزهرىّ الهروىّ فى كتابه تهذيب اللغة، وذكره فقال:
_________
[١] ترجمته فى بغية الوعاة ٢٩٩، وتلخيص ابن مكتوم ١٠٤، وطبقات الزبيدىّ ١٢٧، والفهرست ٥٦. وذكر الزبيدىّ أن اسمه «عبد الرحمن بن عبد الله».
[٢] ترجمته فى تلخيص ابن مكتوم ١٠٤، وتهذيب اللغة للأزهرىّ ١: ٩.','2','161',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (564,'«وقرأت له كتابا بخط أبى الهيثم الرازىّ فى النوادر فاستحسنته، ووجدت فيه فوائد كثيرة، ورأيت له حروفا فى كتب شمر «١» التى قرأتها بخطه؛ فما وقع فى كتابى لابن بزرج فهو من هذه الجهات».

<span data-type="title" id=toc-446>٣٧٩ - عبد الرحمن بن عبيد الله بن أحمد بن أبى الحسن الخثعمىّ ثم السّهيلىّ الأندلسىّ النحوىّ اللغوىّ الأخبارىّ [١]</span>
فاضل كبير القدر فى علم العربية، كثير الاطلاع على هذا الشأن. سمعت أنه كان مكفوفا- والله أعلم. وتصنيفه فى شرح سيرة ابن هشام يدل على فضله ونبله وعظمته وسعة علمه. وكان قريبا من زماننا؛ فإنه كان حيا بالأندلس فى سنة تسع وستين وخمسمائة، وصنّف كتابه هذا، ووسمه باسم يوسف «٢» بن عبد المؤمن بن علىّ المستولى على أرض المغرب، وسمى كتابه هذا الروض الأنف «٣»
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٧، وبغية الوعاة ٢٩٨ - ٢٩٩، وتاريخ ابن كثير ١٢: ٣١٨ - ٣١٩، وتلخيص ابن مكتوم ١٠٤، وابن خلكان ١: ٢٨٠، والديباج المذهب ١٥٠ - ١٥١، وشذرات الذهب ٤: ٢٧١ - ٢٧٢، وطبقات ابن قاضى شهبة ٢: ٦٩ - ٧٠، وطبقات القرّاء ١: ٣٧١، وكشف الظنون ٤٢١، ٩١٧، ١٩٢٤، ومرآة الجنان ٣: ٤٢٢ - ٤٢٣، ومعجم البلدان ٥: ١٨٨، ونفح الطيب ٤: ٣٧٠ - ٣٧١، ونكت الهميان ١٨٧ - ١٨٨. قال ابن خلكان: «والخثعمىّ، بفتح الخاء المعجمة وسكون الثاء المثلثة وفتح العين المهملة وبعدها ميم، هذه النسبة إلى خثعم بن أثمار، وهى قبيلة كبيرة. والسهيلى، بضم السين المهملة وفتح الهاء وسكون الياء المثناة من تحتها، وبعدها لام، هذه النسبة إلى سهيل، وهى قرية بالقرب من مالقة».','2','162',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (565,'والمنهل الرّوى، «١» فى ذكر من حدّث عن رسول الله صلى الله عليه وسلم وروى «٢». قال فى صدره: «فإننى انتحيت فى هذا الإملاء بعد استخارة ذى الطّول، والاستعانة بمن له القدرة والحول، إلى إيضاح ما وقع فى سيرة رسول الله صلى الله عليه وسلم التى سبق إلى تأليفها أبو بكر محمد بن [إسحاق] «٣» المطّلبىّ، «٤» ولخصها عبد الملك بن «٥» هشام المعافرىّ المصرىّ النسّابة النحوىّ، مما بلغنى علمه ويسّر لى فهمه؛ من لفظ غريب، أو إعراب غامض، أو كلام مستغلق، أو نسب عويص، أو موضع فقه ينبغى التنبيه عليه، أو خبر ناقص وجد «٦» السبيل إلى تتمته». ثم قال: «وذلك مستخرج من نيّف على مائة وعشرين ديوانا؛ سوى ما لقّنته [عن] «٧» مشيختى، ونقّحه فكرى، ونتجه نظرى، من نكت علمية لم أسبق إليها، ولم أزحم عليها» «٨».','2','163',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (566,'روى عن أبى بكر محمد بن عبد الله بن العربىّ «١» - رحمه الله- وعن أبى مروان عبد الملك بن سعيد بن بونه القرشىّ العبدرىّ، وأبى بكر محمد بن طاهر الإشبيلىّ وطبقتههم «٢».

<span data-type="title" id=toc-447>٣٨٠ - عبد الرحمن بن عتيق بن خلف المقرئ الصّقلّىّ النحوىّ المعروف بابن الفحّام [١]</span>
من كبار القرّاء، وممّن رحل من المغرب إلى المشرق فى طلب القراءة على الشيوخ، فأدرك بمصر ابن «٣» الهاشمىّ وابن نفيس «٤» وعبد الباقى بن «٥» فارس،
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٥، وحسن المحاضرة ١: ٢١١، وشذرات الذهب ٤:
٤٩، وطبقات ابن قاضى شهبة ٢: ٧٤ - ٧٥، وطبقات القراء لابن الجزرى ١: ٣٧٤ - ٣٧٥، وعيون التواريخ، وكشف الظنون ٣٥٤، ومرآة الجنان ٣: ٢١٣، ومعجم السفر للسلفى ١: ١٥٧ - ١٥٨، والنجوم الزاهرة ٥: ٢٢٥.','2','164',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (567,'وأبا الحسين «١» الرازىّ وآخرين سنة ثمان وثلاثين وأربعمائة. وتلمذ لطاهر بن بابشاذ فى النحو، وأملى عليه شرح مقدّمته. وله تأليف حسن سماه التجريد «٢» فى بغية المريد.
وكان حافظا للقراءات، صدوقا متقنا، عالما كبير السن، أقام بالإسكندرية على قدم الإفادة.
قال أبو الربيع سليمان بن عبد العزيز المقرئ الحمصىّ، حمص الأندلس:
ما رأيت أعلم بالقراءات ووجوهها منه؛ لا بالمغرب ولا بالمشرق؛ وإنه ليحفظ القراءات كما نحفظ نحن القرآن. وكان قد بقى بمصر للقراءة وطلب العلم من سنة ثمان وثلاثين وأربعمائة إلى سنة أربع وخمسين. وتوفى- رحمه الله- فى ذى القعدة سنة ست عشر وخمسمائة.

<span data-type="title" id=toc-448>٣٨١ - عبد الرحمن بن عيسى بن حماد الكاتب اللغوىّ [١]</span>
صاحب ألفاظ عبد الرحمن، أبو الحسن الهمذانىّ «٣». ذكره شيرويه «٤» فى طبقة
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٥، والفهرست ١٣٧، والوافى بالوفيات ج ٦ مجلد ١: ٨٦؛ وذكر أن وفاته كانت سنة ٣٢٠، وله ترجمة أيضا فى مقدّمة كتابه الألفاظ الكتابية.','2','165',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (568,'الهمذانيين وقال: «كان أديبا فاضلا أخباريّا، صاحب ألفاظ عبد الرحمن «١»، قديم المولد». «٢»
وألفاظه هذه من الألفاظ اللغوية المختارة، وهى أحسن ما يستعمله الكتاب «٣».
وقد عنى جماعة بشرحها فى الآفاق، ففى مصر شرحها رجل من أهل الفضل فى المائة الخامسة يعرف بالعميدىّ، وقفت على الجزء الأوّل منها. وشرحها من فضلاء خراسان الإمام مهدى الخوافىّ «٤»، وهو فى المائة الخامسة أيضا، ووقفت على كتابه كاملا فى الشرح، وهو أجود كتاب فى فنه- رحمهم الله أجمعين.

<span data-type="title" id=toc-449>٣٨٢ - عبد الرحمن بن محمد بن معمر اللغوىّ الأندلسىّ أبو محمد وأبو الوليد [١]</span>
كان واسع الأدب، كثيّر التفنن فى اللغة وضبطها ونقلها وإتقانها؛ أفادها، وعرف فى قطره باللغوىّ، وألف كتاب تاريخ الدولة العامرية إلى آخرها.
توفى بجزائر الأندلس الشرقية فى شوّال سنة ثلاث وخمسين وأربعمائة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٥.','2','166',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (569,'<span data-type="title" id=toc-450>٣٨٣ - عبد الرحمن بن محمد بن محمد بن عزيز بن محمد بن يزيد ابن محمد أبو سعيد المعروف بابن دوست [١]</span>
أحد أئمة العصر فى الأدب ورواية كتبه، والمعتمد عليه، والمرجوع إليه.
صنّف فى ذلك الكتب «١» وصحح الأصول بنيسابور. ولد سنة سبع وخمسين وثلاثمائة، وتوفى فى ذى القعدة سنة إحدى وثلاثين وأربعمائة. ذكره عبد الغافر الفارسىّ «٢» فى سياق تاريخ نيسابور.

<span data-type="title" id=toc-451>٣٨٤ - عبد الرحمن بن محمد بن أحمد بن على بن عبد الغفار ابن الإخوة البيّع أبو الفتح بن أبى الغنائم [٢]</span>
له معرفة تامة بالأدب واللغة، وله خط مليح، وكان يحفظ أشعارا كثيرة وأحوالا للناس عجيبة من المنامات وغيرها. خرج من بغداذ وتغرب،
_________
[١] ترجمته فى بغية الوعاة ٣٠٢، وتلخيص ابن مكتوم ١٠٥، ودمية القصر ١٨٦، وفوات الوفيات ١: ٣٣٦، وعيون التواريخ (وفيات سنة ٤٣١)، والوافى بالوفيات ج ٦ مجلد ١: ١٠٠ - ١٠١، ويتيمة الدهر ٤: ٣٨٩ - ٣٩١. قال الصفدى: «ودوست لقب جده محمد».
[٢] ترجمته فى تلخيص ابن مكتوم ١٠٥، وطبقات ابن قاضى شهبة ٢: ٧٣، والوافى بالوفيات ج ٦ مجلد ١: ١٢١.','2','167',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (570,'وسافر وسكن أصبهان وأفاد الناس بها. وكان أبوه سبط الشاعر المعروف بأبى على ابن شبل «١».
قال أبو الفتح عبد الرحمن بن الإخوة هذا: رأيت فى المنام منشدا ينشدنى شعرا:
وأعجب من صبرى القلوص «٢» التى سرت ... بهودجك المزموم أنى استقلّت
وأطبق أحناء الضّلوع على جوى ... جميع وصبر مستحيل مشتّت
فلما انتبهت جعلت دأبى [البحث] عن قائل هذين البيتين مدّة، ولم أجد بهما مخبرا، فلما مضى على هذه القضية عدّة سنين اتفق نزول الرئيس أبى الحسن ابن مشهر الموصلىّ فى ضيافتى، فتجارينا فى بعض الليالى ذكر المنامات وما يراه الإنسان فى نومه، وما يسمعه من نظم ونثر، فذكرت له حال المنام، وأنشدته البيتين، فقال: أقسم إنهما لمن شعرى من جملة قطعة هى:
إذا ما أسال الدمع نمّ على الهوى ... فليس بسرّ ما الضلوع أجنت
فوالله ما أدرى عشية ودعت ... أناحت حمامات اللّوى أم تغنّت
وأعجب من صبرى القلوص التى سرت ... بهودجك المزموم أنى استقلّت
أعاتب فيك اليعملات «٣» على النوى «٤» ... وأسأل عنك الريح من حيث هبّت
وألصق أحناء الضّلوع على جوى ... جميع وصبر مستحيل مشتت','2','168',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (571,'وقال: وأخبرنى أبو الحسن بن مشهر الموصلىّ عن أبى الحسن بن العين زربىّ «١» أنه رأى فى منامه منشدا ينشد هذين البيتين، وهما:
وهموم الناس إن رقدت ... آض همّى وهو يقظان
كيف يرجى الصحو من ثمل ... كلّ عضو منه سكران
وعاد ابن الإخوة من تغربه إلى بغداذ، ومات بها ليلة السبت ثامن عشرين صفر، ودفن من الغد بباب حرب سنة تسع وخمسين وخمسمائة.

<span data-type="title" id=toc-452>٣٨٥ - عبد الرحمن بن محمد بن عبد الله بن أبى سعيد الأنبارىّ أبو البركات الملقب بالكمال النحوىّ [١]</span>
الشيخ الصالح، صاحب التصانيف الحسنة المفيدة فى النحو وغيره؛ وكان فاضلا عالما زاهدا. سكن ببغداذ من صباه إلى أن توفى بها، وتفقه على مذهب الشافعىّ على ابن الرّزاز «٢» بالمدرسة النظامية، وأعاد بها الدرس بمدرسها، وقرأ النحو على النقيب
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٧ - ٢٨، وتاريخ ابن الأثير ٩: ١٥٥، وتاريخ أبى الفدا ٣: ٦٣، وتاريخ ابن كثير ١٢: ٣١٠، وتلخيص ابن مكتوم ١٠٦ - ١٠٧، وابن خلكان ١: ٢٧٩، وروضات الجنات ٤٢٥ - ٤٢٦، وشذرات الذهب ٤: ٢٥٨ - ٢٥٩، وطبقات الشافعية ٤: ٢٤٨، وطبقات ابن قاضى شهبة ٢: ٧٦ - ٨٠، وفوات الوفيات ١: ٣٣٥، وكشف الظنون ١٣٠، ١٨٢، ٢١٢، ٢٢٨، ٢٨٥، ٥٠٠، ٦٠٥، ٦٢١، ٦٩٠، ٧٢٨، ١٨٥٨، ١٨٩٩، ١٩١٨، ١٩٤٠، ١٩٨٣، ٢٠٠٢، ٢٠٣٠، ومرآة الجنان ٣: ٤٠٨، والمزهر ٢: ٤٢١، ٤٦٨، والنجوم الزاهرة ٦: ٩٠، والوافى بالوفيات ج ٦ مجلد ١: ٧٠ - ٧٥.','2','169',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (572,'أبى السعادات ابن الشجرىّ وغيره، ولم يكن ينتمى فى النحو إلا إليه، وقرأ اللغة على الشيخ أبى منصور موهوب بن الخضر الجواليقىّ، وبرع فى الأدب حتى صار شيخ وقته، ودرس فى المدرسة النّظامية النحو مدّة، ثم انقطع فى منزله مشتغلا بالعلم والعبادة، وأقرأ الناس العلم على طريقة سديدة، وسيرة جميلة، من الورع والمجاهدة والتقلل والنسك وترك الدنيا ومحاسنة أهلها، واشتهرت تصانيفه، وظهرت مؤلفاته، «١» وتردّد الطلبة إليه، وأخذوا عنه، واستفادوا منه، وكان مقيما برباط له بشرقىّ بغداذ، فى الخاتونية الخارجة. وله شعر منه:
تدرّع بجلباب القناعة والياس ... وصنه عن الأطماع فى أكرم الناس
وكن راضيا بالله تحيا منعّما ... وتنجو من الضّراء والبؤس والباس','2','170',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (573,'فلا تنس ما أوصيته من وصيّة ... أخىّ، وأىّ الناس من ليس بالناسى
وله أيضا:
دع الفؤاد بما فيه من الحرق ... ليس التّصوّف بالتلبيس والخرق
بل التصوّف صفو القلب من كدر ... ورؤية الصّفو فيه أعظم الخرق
وصبر نفس على أدنى مطامعها ... وعن مطامعها فى الخلق بالخلق
وترك دعوى بمعنى فيه حقّقه ... فكيف دعوى بلا معنى ولا خلق
كان مولده فى شهر ربيع الآخر من سنة ثلاث عشرة وخمسمائة، وتوفى فى ليلة الجمعة تاسع شعبان من سنة سبع وسبعين وخمسمائة، ودفن يوم الجمعة بباب أبزر بتربة الشيخ أبى إسحاق الشيرازىّ «١».','2','171',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (574,'<span data-type="title" id=toc-453>٣٨٦ - عبد الرحمن بن هرمز بن ابى سعد المدنىّ المقرئ النحوىّ [١]</span>
قال أهل العلم: إنه أوّل من وضع علم العربية؛ والسبب فى هذا القول أنه أخذ عن أبى الأسود الدؤلىّ، وأظهر هذا العلم بالمدينة، وهو أوّل من أظهره وتكلّم فيه بالمدينة. وكان من أعلم الناس بالنحو وأنساب قريش، وما أخذ أهل المدينة النحو إلا منه، ولا نقلوه إلا عنه، وإليه أشار ابن برهان النحوىّ فى أوّل شرحه فى كتاب اللّمع «١» بأن قال: «النحاة جنس تحته ثلاثة أنواع: مدنيون، بصريون، كوفيون». أراد أن أصل النحو نتج من أوّل علماء هذه المدن.
ولقد رأيت نحوىّ «٢» حلب، المتصدر للإفادة، الشارح للكتب، وقد سأله سائل عن قول ابن برهان وقال: من المدنيون من النحاة؟ فسكت طويلا، وقال:
لا أدرى لأهل المدينة مقالة فى النحو. وسبق إلى خاطره أن المراد ذكر أرباب الخلاف من النحاة فى هذه الأماكن، وليس المراد إلا من نتج عنه هذا العلم من أوائل العلماء فى هذه البقاع المعينة.
ويروى أن مالك بن أنس إمام دار الهجرة رضى الله عنه اختلف إلى عبد الرحمن بن هرمز عدّة سنين فى علم لم يبثه فى الناس، فمنهم من قال: تردّد
_________
[١] ترجمته فى أخبار النحويين البصريين للسيرافى ٢١ - ٢٢، والأنساب ٤٤ ا، وبغية الوعاة ٣٠٣، وتاريخ ابن الأثير ٤: ٢٢٤، وتاريخ ابن عساكر ٢٣: ٤٦٣ - ٤٧٣، وتذكرة الحفاظ ١: ٩١ - ٩٢، وتقريب التهذيب ١٥٩، وتلخيص ابن مكتوم ١٠٧، وتهذيب الأسماء واللغات ١: ٣٠٥ - ٣٠٦، وتهذيب التهذيب ٦: ٢٩٠ - ٢٩١، وخلاصة تذهيب الكمال ٢٠٠، وشذرات الذهب ١: ١٥٣، وطبقات الزبيدى ٩، وطبقات ابن سعد ٥: ٢٠٩، وطبقات ابن قاضى شهبة ٢: ٨١ - ٨٢، وطبقات القراء لابن الجزرى ١: ٣٨١، والفهرست ٣٩، واللباب لابن الأثير ١: ٦٠، ومرآة الجنان ١: ٢٥٠، والنجوم الزاهرة ١: ٢٧٦، ونزهة الألباء ١٨ - ١٩.','2','172',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (575,'إليه لطلب النحو واللغة قبل إظهارهما، وقيل كان ذلك من علم أصول الدين، وما يردّ به مقالة أهل الزيغ والضلالة. والله أعلم.
وعبد الرحمن بن هرمز مدنىّ تابعىّ، أخذ عنه نافع بن أبى «١» نعيم القراءة فى جماعة من أهل المدينة، وكان عبد الرحمن أخذ القراءة عن عبد الله بن العباس وأبى هريرة.
قال ابن الجزار «٢» القيروانىّ فى تاريخه: «مات أبو داود عبد الرحمن بن هرمز الأعرج مولى محمد «٣» بن ربيعة بن الحارث بن عبد المطلب بالإسكندرية، ودفن بها فى سنة سبع عشرة ومائة».

<span data-type="title" id=toc-454>٣٨٧ - عبد الرءوف بن وهب الأندلسىّ السّناط أبو وهب [١]</span>
بصير بالعربية، حاذق فيها. طالع كتاب سيبويه، وله شعر حسن فى مدح السّناط، منه:
ليس بمن ليست له لحية ... بأس إذا حصلته ليسا
_________
[١] ترجمته فى بغية الوعاة ٣١٩، وتلخيص ابن مكتوم ١٠٧، وطبقات الزبيدى ٢٠٤ - ٢٠٦.
قال ابن مكتوم: «صوابه عبد الوهاب بن محمد بن عبد الوهاب بن عبد الرءوف، وزير الناصر عبد الرحمن ابن محمد؛ وما ذكره القفطى من أن اسمه عبد الرءوف خطأ، والصواب ما ذكرته». وتحقيق ابن مكتوم يوافق ما فى بغية الوعاة وطبقات الزبيدى. والسناط، بالضم والكسر: من لا لحية له أصلا، مثل الكوسج.','2','173',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (576,'وصاحب اللحية مستقبح ... يشبه فى طلعته التّيسا
إن هبّت الريح تلاهت بها ... وماست الريح بها ميسا
وكان ذا كبر عظيم، ويظهر مع ذلك زهدا، وولى الوزارة فى قطره، فكان يرمى المسائل النحوية على بوّابه وكتابه، حتى تبرموا منه، واستعفوا من ذلك.

<span data-type="title" id=toc-455>٣٨٨ - عبد الرازق بن علىّ القيروانىّ النحوىّ أبو القاسم [١]</span>
ذكره ابن رشيق فى كتابه «١»، وسماه النحوىّ، وقال: «هو شاعر مشهور، قادر لطلب الطباق والتجنيس طلبا شديدا، بالتصريف وتبديل الحروف، ويستعمل القوافى العويصة».
وقال: «كتب إلىّ لما صنفت هذا الكتاب صحبة نبذ أنفذها إلىّ لأثبتها:
يا مبرزا إبريز خير سبيكة ... ومكلّلا إكليل خير متوّج
ومميزا جنسى مقدّمة النّهى ... إن أشكلا من عاقر أو منتج
ومطرّزا حلل البلاغة معجزا ... كلّ الورى ببلاغة الأنموذج
فكأنه للسمع لفظ أحبّة ... وكأنه للعين روض بنفسج
وكأنه للقلب سحر علاقة ... فى مهجة تخشى الصدود وترتجى
خصّصت أهل الأرض منه بمشرق ... بأقرّ من شمس النهار وأبهج
رتّبت بين ذوى الفصاحة منهم ... وفصلت بين مرتّب ومثبّج «٢»
وكشفت عن شعرى لتلحقه به ... فاستر على خلّ لسترك محوج
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٧ - ١٠٨، ومسالك الأبصار ج ١١ مجلد ٢:
٣٦٢ - ٣٦٣.','2','174',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (577,'<span data-type="title" id=toc-456>٣٨٩ - عبد السلام بن إسماعيل النحوىّ اللغوىّ الخراسانىّ أبو مطيع الجمعىّ الرامىّ [١]</span>
قريب العهد. كان فى المائة السادسة. صاحب اللغة والنحو والإعراب والورع الموفور، والتقى المشكور. وله شعر كشعر النحاة:
أغالب بالصبر دهرى فعزّ ... وفى مثل قيل: «من عزّ بزّ» «١»
وقد دهمتنى صروف الزّمان ... فمن لى بصبر وقد كان عزّ
فقالوا فهل لك فيما دهاك ... مجير عليه فقلت الأعزّ
غدوت إلى بابه لائذا ... كما لاذ بالدّرّ والسّىء فزّ «٢»
علىّ علا فامتطى فى العلاء ... مناط الثريا إذا ماركز

<span data-type="title" id=toc-457>٣٩٠ - عبد السلام بن الحسين بن محمد أبو أحمد البصرىّ اللغوىّ [٢]</span>
سكن بغداذ، وحدث بها عن محمد بن إسحاق بن عباد التمار وجماعة من البصريين. حدّث عنه عبد العزيز الأزجىّ وغيره. وكان صدوقا عالما ديّنا قارئا للقرآن، عارفا بالقراءات. وكان يتولى ببغداذ النظر فى دار الكتب، وإليه حفظها والإشراف عليها.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٨.
[٢] ترجمته فى بغية الوعاة ٣٠٥ - ٣٠٦، وتاريخ ابن الأثير ٧: ٢٧٥، وتاريخ بغداد ١١: ٥٧ - ٥٨، وتلخيص ابن مكتوم ١٠٨، وطبقات ابن قاضى شهبة ٢: ٨٣ - ٨٤، وطبقات القراء ١: ٣٨٥، والمنتظم (وفيات سنة ٤٠٥)، والنجوم الزاهرة ٤: ٢٣٨، ونزهة الألبا ٤١٢ - ٤١٣.','2','175',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (578,'ذكره أبو العلاء بن سليمان فى كتاب شرحه للحماسة فقال: كان يلقب بالوجكا «١»، وقال أبو القاسم عبيد الله بن على الرّقى الأديب: كان عبد السلام البصرىّ من أحسن الناس تلاوة للقرآن، وإنشادا للشعر. قال: وكان سخيا، ربما جاءه السائل وليس معه شىء يعطيه فيدفع إليه بعض كتبه التى لها قيمة كبيرة وخطر كبير.
قال على بن المحسن التّنوخىّ: إن عبد السّلام البصرىّ توفى فى يوم الثلاثاء التاسع عشر من المحرم سنة خمس وأربعمائة. قال غيره: ودفن فى مقبرة الشّونيزىّ عند قبر أبى علىّ الفارسىّ. وكان مولده فى سنة تسع وعشرين وثلاثمائة «٢».

<span data-type="title" id=toc-458>٣٩١ - عبد الصمد بن عبد القاهر بن نصر بن عيسون السّنجارىّ النحوىّ [١]</span>
تصدر فى قراءة النحو بسنجار، وكانت عنده فنون، منها الفقه. وتولى حكم سنجار فى زمن محمود بن زنكى. وكان- حفظه الله- كثير التسلّط على العلوم بذكائه، ويقال إن فقيها قدم سنجار بطريقة غريبة فى الخلاف، وحضر عنده، وأغرب فى الدليل، فأعرض وسأله هل وقف على الطريقة قبل ذلك، فأنكر أن
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٠٨.','2','176',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (579,'يكون وقف عليها. وكان حسن الضبط لما يكتبه من العربية، وإذا أفاض فى شىء من العوامل استوفاه، وبسط القول فيه. وكان أهل سنجار قسمين:
قسم يتردّد فى طلب العربية إليه، وقسم يتردّد إلى الشيخ أبى الحسن على بن دبابا النحوىّ السّنجارىّ. وكان موجودا فى وسط المائة السادسة من الهجرة.

<span data-type="title" id=toc-459>٣٩٢ - عبد الصمد بن محمد بن حيّويه البخارىّ [١]</span>
ذكره الحافظ أبو عبد الله فى تاريخ نيسابور فقال: «أبو محمد الأديب الحافظ النحوىّ. وكان من أعيان الرّحالة فى طلب الحديث، وسمع فى بلده أبا حاتم سهل بن السرىّ الحافظ وأقرانه، وبمرو عمر بن علك وأقرانه».
«قدم علينا نيسابور سنة ثلاث وثلاثين وثلاثمائة، وأقام عندنا إلى سنة سبع، ثم خرج إلى العراق ودخل الشام ومصر، وجمع الحديث الكثير، وانصرف إلى بغداذ سنة أربعين، ودخلتها وهو بها سنة إحدى وأربعين؛ ثم اجتمعنا بعد ذلك بنيسابور، ثم كتبنا عنه ببخارى سنة خمس أو ست وخمسين. وكان قلّما يفارقنا بها سنين. وله عندى قصيدة مدح بها شيخنا أبا أحمد التميمىّ. ثم انصرفت إلى نيسابور. وتوفى ببخارى فى شهر رمضان سنة تسع وخمسين وثلاثمائة».
قال الحافظ أبو عبد الله: «سمعت «١» عبد الصمد بن محمد البخارىّ، سمعت أبا بكر ابن حرب شيخ أهل الرأى يقول: كثيرا ما أرى أصحابنا فى مدينتنا هذه يظلمون أهل الحديث. كنت عند حاتم [العتكىّ] «٢»، فدخل عليه شيخ من أصحابنا من أهل الرأى، فقال: أنت الذى تروى أن النبىّ صلى الله عليه وسلم أمر بقراءة فاتحة الكتاب خلف الإمام؟ فقال: قد صح الحديث عن النبى صلى الله عليه وسلم فى ذلك- يعنى قوله:
_________
[١] ترجمته فى الإكمال لابن ماكولا الورقة ١٨٤، وبغية الوعاة ٣٠٦، وتاريخ ابن عساكر ٢٤: ١٦١ - ١٦٣، وتلخيص ابن مكتوم ١٠٨ - ١٠٩.','2','177',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (580,'«لا صلاة إلا بفاتحة الكتاب» - فقال له: كذبت؛ إن فاتحة الكتاب لم تكن فى عهد رسول الله صلى الله عليه وسلم؛ وإنما نزلت فى عهد عمر بن الخطاب رضى الله عنه.

<span data-type="title" id=toc-460>٣٩٣ - عبد الصمد بن يوسف بن عيسى النحوىّ أبو محمد الضرير [١]</span>
من قرية من السّواد تعرف برقبينا «١». سكن بغداذ، وحفظ القرآن الكريم، وقرأ النحو على أبى محمد بن الخشاب، ثم صار إلى واسط، فسكنها إلى آخر وفاته.
وكان يقرأ النحو، وكان كثير التلاوة للقرآن المجيد.
له أوراد من الصلاة- رحمه الله- وأوقات من الذكر. توفّى بواسط فى شهر ربيع الأوّل من سنة ست وسبعين «٢» وخمسمائة، ودفن بسكة الأعراب.

<span data-type="title" id=toc-461>٣٩٤ - عبد العزيز بن أبى سهل الخشنىّ النحوىّ اللغوىّ القيروانىّ المعروف بابن البقّال الضرير [٢]</span>
ذكره ابن رشيق القيروانىّ فى كتابه فقال فى وصفه: «كان مشهورا باللغة والنحو جدا، مفتقرا إليه فيهما، بصيرا بغيرهما من العلوم، ولم ير ضرير «٣» أطيب منه نفسا، ولا أكثر حياء. أدركته وقد جاز السبعين «٤»، والتلاميذ يكلمونه فيحمرّ
_________
[١] ترجمته فى بغية الوعاة ٣٠٦ - ٣٠٧، وتلخيص ابن مكتوم ١٠٨ - ١٠٩، وطبقات ابن قاضى شهبة ٢: ٨٧، ونكت الهميان ١٩٤.
[٢] ترجمته فى بغية الوعاة ٣٠٨، وتلخيص ابن مكتوم ١٠٩ - ١١٠، وطبقات ابن قاضى شهبة ٢: ٩٠ - ٩١، ومسالك الأبصار ج ١١ مجلد ٢: ٣١١ - ٣١٢، ونكت الهميان ١٩٤ - ١٩٥، والوافى بالوفيات: ج ٥ مجلد ٢: ٢٢٤.','2','178',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (581,'خجلا. وكان شاعرا مطبوعا، يلقى كلامه إلقاء، ويسلك طريق أبى العتاهية فى سهولة الطبع، ولطف التركيب، وقرب مآخذ الكلام، ولا غنى لأحد من الشعراء الحذّاق عن العرض عليه، والجلوس بين يديه؛ أخذا للعلم عنه، واقتباسا للفائدة منه. وكان سيدنا نصير الدولة «١» عارفا بحقّه، مقرّبا له، مقبلا عليه، لزمه بالقيروان مغرم فترك بسببه ألوف دنانير تناهز العشرة، بل تجاوز البدرة».
ومن شعره لعبد الله بن محمد الكاتب وقد أراد إدخاله الدعوى:
لكم علىّ وفاء ما حييت ولا ... أعدو رضاكم ولا أرضى بكم أحدا
لا تسألونى من دينى فأسخطكم ... لا بعت دينى بدنياكم إذا أبدا
فأعرض عنه، ولم يعرض له بعدها. وله:
قال العواذل قد طوّلت حزنك إذ «٢» ... لو شئت إخراجه عن سلوة خرجا
ولن أطيق خروج الحزن من خلدى «٣» ... لأننى أنا لم آمره أن يلجا
ومن شعره:
لما تحمّل قطّان الحمى تركوا ... عندى وساوس قد فضّلن بالحرق
وفى هوادجهم سرب أوانس قد ... دخلن فى الوحش بالأجياد والحدق
من كل مطلعة شمسا بلا فلك ... حسنا ويهززن أغصانا بلا ورق
ومن شعره:
يا غصنا غضّا من الآس ... ودرّة وهى من الناس
صوّرك الله على صورة ... كانت بها أسباب وسواسى','2','179',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (582,'ترديد ذكرى لك فى خاطرى ... أكثر من ترديد أنفاسى
نسيت ودّى وتناسيتنى ... وليس قلبى لك بالناسى
وليس لى منك سوى حسرة ... تجول بين الشوق والياس
وله، وهو من رقيق شعره:
ولست كمن يجزى على الهجر مثله ... ولكننى أزداد وصلا على هجرى
وما ضرّنى إتلاف عمرى كلّه ... إذا نلت يوما من لقائك فى عمرى

<span data-type="title" id=toc-462>٣٩٥ - عبد العزيز بن أحمد بن أبى الحباب النحوىّ الأندلسىّ [١]</span>
قرطبىّ يكنى أبا الإصبع. روى عن أبيه أبى عمرو بن الحباب كتبا من روايته، ولم يكن بالضابط لها. وتوفى ودفن يوم الأربعاء لعشر خلون من ربيع الآخر سنة إحدى عشرة وأربعمائة. ذكره ابن حيان «١» مؤرّخ الأندلس.

<span data-type="title" id=toc-463>٣٩٦ - عبد العزيز بن خلوف النحوىّ المغربى [٢]</span>
من إفريقية فى أيام باديس، المستولى على إفريقية، وممن عاصر ابن رشيق وابن شرف وطبقتهما. تصدّر لإفادة هذا الشأن بمدينة القيروان، وتقدّم هناك فى عصره، وله شعر منه:
لقوم ببلدتنا شيمة ... نحاجى بها الناس أهل الذكاء
تماح «٢» الدّلاء بآبارهم ... وآبار غيرهم بالدلاء
_________
[١] ترجمته فى تلخيص ابن مكتوم ١١٠، والصلة لابن بشكوال ١: ٣٦٢.
[٢] ترجمته فى بغية الوعاة ٣٠٧، وتلخيص ابن مكتوم ١١٠، ومسالك الأبصار ج ١١ مجلد ٢: ٣٠٣ - ٣٠٤.','2','180',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (583,'وذكره الحسن بن رشيق فى كتابه فقال: «عبد العزيز بن خلوف النحوىّ الحرورى «١». شاعر متقن، ذو ألفاظ حسنة، ومعان متمكّنة، مثقّف نواحى الكلام رطبها، حلو مذاقة الطّبع عذبها؛ يشبّه فى المنظوم والمنثور بأبى على البصير، «٢» وله فى سائر العلوم حظوظ وافرة، وحقوق ظاهرة، أغلبها عليه علم النحو والقراءات، وما تعلق بها. وفيه ذكاء يخرج عن الحدّ المحدود».
وقوله من قصيدة يمدح بها سيدنا- أدام الله سلطانه- أوّلها (قلت: يعنى بسيدهم المعز بن باديس):
أبلحظ طرف هذه الأنضاء «٣» ... شقيت إذن بالأعين الأعضاء
تتمثّل الغيد الحسان ببعض ما ... جرّت عليه الغادة الحسناء
تصبو الجمادات الموات لوجهها ... طربا فكيف النّطّق الأحياء
منها:
سارت وقد بنت الأسنّة حولها ... سورا يجاز بحدّه الجوزاء
ولما مدح المعز بن باديس بها وأطال فى المدح ختمها بقوله:
فتحت لنا نعماك كلّ بلاغة ... فجرى اليراع وقالت الشعراء
وقال ابن رشيق فى وصف هذه القصيدة: «وما حسبت أنّ أحدا من أهل عصرنا يبلغ هذه البلاغة، أو يصوغ الكلام هذه الصياغة، وإن كثيرا من أشعار المتقدّمين فى هذا الوزن والروىّ ليضعف ويقصر دون بنيتها».','2','181',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (584,'قال: ومن جيد شعره قوله من نسيب قصيدة فى بعض الكتاب:
ومن دونها طود من السّمر شامخ ... إلى النجم أو بحر من البيض متأق «١»
وأسود لا تبدو به النار حالك ... وبيداء لا تجتازها «٢» الريح سملق»
وقال فى مدحتها:
ينام عن المال «٤» التّلاد وإنه ... إذا عرضت أكرومة لمؤرّق
أخو نظر أما لدفع ملمة ... فسام «٥» وأمّا من حياء فمطرق
رمى ثغر الحسّاد عن قوس همة ... تحدّث عن حيث السّماك فتصدق
ومنها- وذكر القلم- فقال:
به السّحب تزجى «٦» والصّواعق تتّقى ... وماء الحيا «٧» ينهلّ والنار تحرق
وله فى الغزل:
مروا أن يروّح هذا الأسي ... ر بالقتل إن كان لا يطلق
أيتلف ذا العبد لا رغبة ... يباع ولا حسبة يعتق
وإنى من فقره موته ... لأنى من كبدى أنفق
لقد فتقت يد سحر العيو ... ن فتقا على العقل لا يرتق
قال ابن رشيق واصفا له: «وفى شعره من القوّة والتصرف والتصنّع ما ليس فى شعر غيره من أصحابنا، وهو مع ذلك كثير».','2','182',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (585,'<span data-type="title" id=toc-464>٣٩٧ - عبد العزيز بن عبد الله بن ثعلبة أبو محمد السعدىّ الأندلسىّ الشاطبىّ [١]</span>
قدم دمشق طالب علم، وسمع بها الحسن بن أبى الحديد «١» وطبقته، ورحل إلى العراق، فسمع بها أبا محمد الصّريفينىّ «٢» وطبقته، وصنف غريب الحديث لأبى عبيد القاسم بن سلّام على حروف المعجم، وجعله أبوابا، وروى عنه جماعة من الدّمشقيين، ومات فى سنة خمس وستين وأربعمائة، فى شهر رمضان، فى حرّان «٣»

<span data-type="title" id=toc-465>٣٩٨ - عبد العزيز القارى الملقب ببشكست المدنىّ النحوىّ الشاعر [٢]</span>
أخذ عنه أهل المدينة النحو، وكان يذهب مذهب الشّراة «٤»، ويكتم ذلك؛ فلما ظهر أبو حمزة الشّارى «٥» بالمدينة خرج معه؛ فقتل فيمن قتل.
_________
[١] ترجمته فى تاريخ ابن عساكر ٢٤: ١٩٤ - ١٩٥، وتلخيص ابن مكتوم ١١٠، ونفح الطيب ٣: ٣٩١.
[٢] ترجمته فى تلخيص ابن مكتوم ١١٠، وتاريخ ابن عساكر ٢٤: ٢٧٣ - ٢٧٥.','2','183',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (586,'وكانت وقعة أبى حمزة الشارى فى سنة ثلاثين ومائة فى أيام مروان «١»، فقال أحد الشعراء فى بشكست:
لقد كان بشكست عبد العزيز ... من أهل القراءة بالمسجد
فبعدا لبشكست عبد العزيز ... وأمّا القران فلا يبعد

<span data-type="title" id=toc-466>٣٩٩ - عبد العزيز بن عبد الرحمن بن حسين بن مهذب النحوى اللغوىّ أبو العلاء [١]</span>
قدم هو وأبوه وعمه على الدولة المصرية العلوية؛ فأما عبد الرحمن أبوه فإنه توفى فى سنة ثلاث وثمانين وثلاثمائة، وصلى عليه عبد العزيز. وتوفى أبو جعفر محمد أخوه فى صدر سنة ثلاث وتسعين وثلاثمائة. وكان يتولى بيت المال.
وأما أبو العلاء عبد العزيز بن عبد الرحمن بن حسين بن مهذب هذا فإنه أخذ اللغة بمصر عن أبى حسين المهلبىّ اللغوىّ وأكثر عنه، وامتدحه شاكرا لما أولاه، مما أفاده إياه.
وصنف أبو العلاء هذا كتابا فى اللغة، هو موجود بالديار المصرية، وقرأ النحو على أبى محمد الحسن بن عبد الرحمن المنداسىّ النحوىّ بمصر وأكثر عنه، وله شعر جيد- أعنى عبد العزيز هذا- منه:
إنّ البخيل يعيش فى ... دنياه عيش الأشقياء
_________
[١] ترجمته فى تلخيص ابن مكتوم ١١١.','2','184',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (587,'وحسابه فى دار أخرا ... هـ حساب الأغنياء
فبلغتم قبل الثرى ال ... أنفاق أصحاب الثراء
فالمرء يرحل كلّ يو ... م رحلة نحو الفناء
وله فى سفرة طست:
لله درّ غلام جاء يخدمنا ... بسفرة من رفيع الصّوف قوراء
بفروز «١» أزرق من حول دارتها ... تحار فيه وفيها مقلة الرائى
كأنها روضة خضراء مزهرة ... من حولها جدول جار من الماء
وله أيضا:
وما طربت لمشروب ألذّ به ... ولا لعشق ظباء العجم والعرب
لكن طربت إلى دهر أنال به ... غنى فأبذله فى عصبة الأدب

<span data-type="title" id=toc-467>٤٠٠ - عبد القاهر بن طاهر بن محمد البغداذىّ أبو منصور [١]</span>
الأستاذ الكامل ذو الفنون، الفقيه الأصولىّ الأديب الشاعر النحوىّ، الماهر فى علم الحساب، العارف بالعروض.
ورد نيسابور مع أبيه أبى عبد الله طاهر «٢» بن محمد البغداذىّ التاجر. وكان ذا مال وثروة. أنفق عبد القاهر ماله على أهل العلم، ولم يكتسب بماله علما.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١١١، وابن خلكان ١: ٢٩٨ - ٢٩٩، وطبقات الشافعية للسبكى ٣: ٢٣٨ - ٢٤٢، وفوات الوفيات ١: ٣٧٩ - ٣٨٠، وكشف الظنون ٢٥٤، ٣٣٥، ٤٦٢، ٤٧١، ١٢٧٤، ١٤٣٢، ١٧٦٩، ١٨٢٠، ١٩٧٠.','2','185',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (588,'درس تسعة عشر نوعا من العلوم «١»، واستفاد منه الناس. خرج عن نيسابور فى أيام التّركمانية إلى أسفرايين «٢»، فمات بها سنة تسع وعشرين وأربعمائة، ودفن عند الأستاذ أبى إسحاق «٣» بها.

<span data-type="title" id=toc-468>٤٠١ - عبد القاهر بن عبد الله بن الحسين أبو الفرج الشيبانىّ الحلبىّ النحوىّ الشاعر المعروف بالوأواء [١]</span>
وليس بالوأواء المشهور. أصله من بزاعة «٤»، ونشأ بحلب، وتأدّب بها، وكانت بينه وبين أبى عبد الله الطّليطلىّ النحوىّ نزيل شيزر «٥» مكاتبات. وتردّد إلى دمشق
_________
[١] ترجمته فى إعلام النبلاء ٤: ٢٤٤ - ٢٤٧، وبغية الوعاة ٣١٠، وتاريخ الإسلام للذهبى (وفيات سنة ٥٥١)، وتاريخ ابن عساكر ٢٤: ٢٩٨ - ٣٠١، وشذرات الذهب ٤: ١٥٨، وطبقات ابن قاضى شهبة ٢: ٩٤، وكشف الظنون ٨١٢، والنجوم الزاهرة ٥: ٣٢٢ - ٣٢٣.','2','186',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (589,'غير مرة، وكان يقرئ بها النحو، ويشرح شعر المتنبىّ ويعربه، وله شعر، أنشد منه ابنه أبو محمد عبد الصمد قوله «١»:
أظنّوا أنهم بانوا ... وهم فى القلب سكان
تولّى النوم إذ ولّوا ... وكان العيش إذ كانوا
أناديهم وقد حثّوا ... ودمع العين هتّان
أحبّ البعد أحباب ... وخان العهد إخوان
وقالوا شفّك الدهر ... وهم للدهر أعوان
ويحيا المرء إن راعت ... هـ أسياف وخرصان
ولا يحيا إذا راعت ... هـ أحداق وأجفان
وأغيد فاتن الألحا ... ظ صاح وهو نشوان
وريان من الحسن ... إلى الأنفس ظمآن
إذا لاح فما البدر! ... وإن ماس فما البان!
وذكر أن والده توفى فى آخر شوّال سنة إحدى وخمسين وخمسمائة بحلب «٢».','2','187',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (590,'<span data-type="title" id=toc-469>٤٠٢ - عبد القاهر بن عبد الرحمن الجرجانىّ أبو بكر النحوىّ [١]</span>
فارسى الأصل، جرجانىّ «١» الدار، عالم بالنحو والبلاغة. أخذ النحو بجرجان عن الشيخ أبى الحسين محمد بن الحسن بن محمد بن عبد الوارث الفارسىّ «٢»، نزيل جرجان، ابن أخت الشيخ أبى على الفارسى، وأكثر عنه، وقرأ ونظر فى تصانيف النحاة والأدباء، وتصدّر بجرجان، وحثّت إليه الرّحال، وصنف التصانيف الجليلة.
وكان- رحمه الله- ضيّق العطن، لا يستوفى الكلام على ما يذكره مع قدرته على ذلك. فمن تصانيفه: كتاب المقتصد «٣» فى شرح الإيضاح «٤» وهو مقتصد من مثله على ما سماه، لم يأت فى الإيضاح بشىء له مقدار. ولما تبرع فى التكملة لم يقصّر بنسبته إلى ما عهد منه، فلو شاء لأطال.
_________
[١] ترجمته فى بغية الوعاة ٣١٠ - ٣١١، وتلخيص ابن مكتوم ١١٢ - ١١٣، وروضات الجنات ١٤٣، وشذرات الذهب ٣: ٣٤٠، وطبقات الشافعية: ٢٤٢، وطبقات ابن قاضى شهبة ٢: ٩٤ - ٩٥، وطبقات المفسرين للداودى ١٤٠ ب، وفوات الوفيات ١: ٣٧٨ - ٣٧٩، وكشف الظنون ٨٣، ١٢٠، ٢١٢، ٦٠٢، ١١٦٩، ١١٧٩، ١٧٦٩، ومرآة الجنان ٣: ١٠١، ونزهة الألباء ٤٣٤ - ٤٣٦.','2','188',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (591,'وله شرح كتاب العوامل «١»، سماه الجمل، ثم صنف شرحه، فجرى على عادته فى الإيجاز. وله إعجاز «٢» القرآن دل على معرفته بأصول البلاغات ومجاز الإيجاز. وله مسائل منثورة أثبتها فى مجلد، هو كالتذكرة له، لم يستوف القول حق الاستيفاء فى المسائل التى سطرها «٣». ومع هذا كله فإن كلامه وغوصه على جواهر هذا النوع يدل على تبحره وكثرة اطلاعه.
ولم يزل مقيما بجرجان يفيد الراحلين إليه، والوافدين عليه إلى أن توفى فى سنة إحدى وسبعين وأربعمائة «٤».
ومن تلاميذه المذكورين الواردين إلى العراق والمتصدّرين ببغداذ على بن زيد الفصيحىّ- رحمه الله- وقد تخرج به جماعة كثيرة، واستفادوا منه ما استفاده من عبد القاهر.
ولعبد القاهر شعر مدح به نظام الملك الحسن بن إسحاق:
لو جاود الغيث غدا ... بالجود منه أجدرا
أو قيس عرف عرفه ... بالمسك كان أعطرا
ذو شيم لو أنها ... فى الماء ما تغيرا
وهمة لو أنّها ... للنّجم ما تغوّرا
لو مس عودا يابسا ... أورق ثم أثمرا','2','189',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (592,'وله يشكو الزمان وأهله:
أىّ وقت هذا الذى نحن فيه ... قد دجا بالقياس والتشبيه
كلما سارت العقول لكى تق ... طع تيها توغّلت فى تيه
وأشعاره كثيرة فى ذم الزمان وأهله. وكان هذا الأمر هو السبب فى تقصيره إذا صنف؛ إذ لم يجد راحة ممن جمع لهم وألف.
قال ابن غياض الشامىّ الكفرطابىّ النحوىّ- ونقلته بخطه فى تذكرته فى آخر نسخة المقتصد لعبد القاهر الجرجانى بالرّى مكتوبا ما حكايته:
«قرأ علىّ الأخ الفقيه أبو نصر أحمد بن إبراهيم بن محمد الشجرىّ- أيده الله- هذا الكتاب من أوّله إلى آخره قراءة ضبط وتحصيل، وكتبه عبد القاهر بن عبد الرحمن بخطه فى شهر رمضان المبارك من سنة أربع وخمسين وأربعمائة، حامدا لربه، ومصليا على محمد رسوله وآله» «١».

<span data-type="title" id=toc-470>٤٠٣ - عبد الكريم بن إبراهيم بن محمد بن الحسن النحوىّ الرازى أبو سعيد [١]</span>
نحوىّ، أفاد ببلده، ورحل إلى العراق وسمع بها أبا طالب محمد بن محمد بن غيلان البزاز، ودخل الشام، ونزل بيت المقدس، وروى به عن ابن غيلان المذكور. قرأ عليه نصر بن إبراهيم القدسىّ «٢» الفقيه العالم الزاهد الورع بالمسجد الأقصى، وسمع جماعة بقراءته.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١١٣.','2','190',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (593,'<span data-type="title" id=toc-471>٤٠٤ - عبد الكريم بن الحسن بن المحسن بن الفضل بن المسلم ابن المؤمل بن سوّار المقرئ النحوى التّككىّ المصرىّ [١]</span>
مقرئ فاضل، من فضلاء القرّاء، ومن العارفين بالقرآن وعلومه وتفسيره، سمع أبا إسحاق الحبّال، وأبا الحسين الخلعىّ. وأستاذه فى القراءات أبو الحسن على بن محمد بن حميد الواعظ. أدركه أبو طاهر السلفىّ، واشتركا فى السماع على أبى صادق، وسمع عليه السلفىّ كتاب معانى القرآن لأبى جعفر النحاس بكماله، وكان يرويه عن الخلعىّ عن الحوفىّ عن ابن الأدفوىّ عن النحاس.
سئل عن مولده فى سنة سبع عشرة وخمسمائة، فقال: لى ستون سنة.
توفى- رحمه الله- فى شهر ربيع الآخر سنة خمس وعشرين وخمسمائة، وجلس ولده مكانه فى حلقته فى جامع عمرو بن العاص يقرئ.

<span data-type="title" id=toc-472>٤٠٥ - عبد الكريم بن على بن محمد بن الطفال أبو محمد القضاعىّ النحوىّ الإسكندرىّ المكفوف البارع [٢]</span>
كان نحويا متصدّرا، صاحب حلقة الجامع بالإسكندرية لإقراء النحو.
وله شعر حسن. أنبأنا أبو طاهر السّلفىّ فى إجازته العامة، أنشدنى أبو محمد عبد الكريم بن محمد بن الطّفال القضاعىّ بالثغر لنفسه ابتداء قصيدة:
ليس الوقوف على الأطلال من شغلى ... إنى وشغلى ذوات الأعين النّجل
عين أعنّ على قلبى فقلبه ... داعى الصّبا فصبا للهو والغزل
_________
[١] ترجمته فى تلخيص ابن مكتوم ١١٣، وحسن المحاضرة ١: ٢١١، وطبقات القرّاء ١:
٤٠٠، ومعجم السفر للسلفى ٢: ٢٤٩ - ٢٥٠. والتككى، بكسر التاء وفتح الكاف الأولى:
منسوب إلى التكك، جمع تكة، وهى رباط السراويل.
[٢] ترجمته فى تلخيص ابن مكتوم ١١٤، ومعجم السفر للسلفى ١: ٢٤٣ - ٢٤٤، ونكت الهميان ١٩٥.','2','191',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (594,'من كل فاترة الألحاظ فاتنة الأل ... فاظ تسحب ذيل الدّل والكسل
قيد القلوب تخال العقل صورتها ... مراد كل فؤاد فتنة المقل
قال السّلفىّ: عبد الكريم هذا كانت له حلقة فى الجامع للنحو، وكان مائلا إلى الخير، وله شعر فى غاية الجودة، وعندى منه مقطّعات أنشدنيها، وكان كفيف البصر.
وقال أيضا: أنشدنا أبو محمد عبد الكريم بن على بن محمد بن القضاعىّ النحوى لنفسه بالثغر:
من يكرم الله يصبح عرضة الألم ... كذا النبيون مذ كانوا على القدم
وذاك أن الرضا والسخط منزلة ... لم يحوها قطّ إلا أشرف الأمم
إن المصائب عنوان الأجور «١» فمن ... يصب يفز بنعيم غير منصرم
كذا الملوك إذا اختاروا لخدمتهم ... عبدا أصاروا إليه أجهد الخدم
فالحمد لله كلّ منه تكرمة ... فالبرء والسقم معدودان فى النّعم
ثم قال السّلفىّ: «عبد الكريم هذا يعرف بابن الطّفال، وينعت بالبارع، وكان عفيفا كفيفا، وله فى الجامع حلقة لإقراء النحو. وشعره كثير، وقد علّقت منه جملة- رحمه الله- وكان قرأ على أبى على الحضرمىّ، وقال لى على بن عبد الرحيم:
كان عبد الكريم فى ابتداء أمره على طريقة لو بقى عليها فاق أهل زمانه من الاشتغال بقراءة الحقائق؛ من كلام الحارث المحاسبى «٢» وغيره، ولزوم الصمت، وإعراضه عن الدنيا. ثم تزوّج ورزق أولادا فصار يمدح ويستميح ضرورة. وتغيرت عليه الأحوال».','2','192',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (595,'<span data-type="title" id=toc-473>٤٠٦ - عبد الكريم بن هوازن بن عبد الملك بن طلحة ابن محمد القشيرىّ أبو القاسم [١]</span>
الإمام مطلقا، المفسر الأديب النحوىّ الكاتب الشاعر. لسان عصره، وسيّد وقته فى كل فن. صنف التفسير الكبير «١» قبل العشر وأربعمائة.

<span data-type="title" id=toc-474>٤٠٧ - عبد اللطيف بن يوسف بن محمد بن على بن أبى سعد البغداذى [٢]</span>
الموصلىّ الأصل، البغداذىّ المولد، أبو محمد بن أخى سليمان الموصلىّ، المدعو بالموفّق الملقّب بالمطجّن «٢». كان يدّعى معرفة النحو واللغة والعربية وعلم الكلام
_________
[١] ترجمته فى الأنساب للسمعانى ٤٥٣ ب، وتاريخ ابن الأثير ٨: ١١٨، وتاريخ بغداد ١١: ٨٣، وتاريخ أبى الفدا ٢: ١٩٠، وتاريخ ابن كثير ١٢: ١٠٧ - ١٠٨، وتلخيص ابن مكتوم ١١٤، وابن خلكان ١: ٢٩٩ - ٣٠١، ودمية القصر ١٩٤ - ١٩٦، وروضات الجنات ٤٤٤، وشذرات الذهب ٣: ٣١٨ - ٣١٩، وطبقات الشافعية ٣: ٢٤٣ - ٢٤٨، وطبقات المفسرين للداودىّ ١٤٣ ب- ١٤٧ ب، وطبقات المفسرين للسيوطى ٢١ - ٢٢، وكشف الظنون ٥٢٠، ٨٨٢، واللباب فى الأنساب ٢: ٢٦٤، ومرآة الجنان ٣: ٩١ - ٩٣، ومسالك الأبصار ج ٥ مجلد ١: ٨٩ - ٩١، والمنتظم (وفيات ٤٦٥)، ومعجم السفر ١: ١٧، والنجوم الزاهرة ٥: ٩١. والقشيرىّ، بضم القاف وفتح الشين وسكون الياء: منسوب إلى قشير ابن كعب بن ربيعة بن عامر بن صعصعة؛ وهو أبو قبيلة كبيرة، ينسب إليها كثير من العلماء.
[٢] ترجمته فى بغية الوعاة ٣١١، وتاريخ الإسلام للذهبى (وفيات سنة ٦٢٩)، وتلخيص ابن مكتوم ١١٤ - ١١٧، وحسن المحاضرة ١: ٢٣٢ - ٢٣٣، وشذرات الذهب ٥: ١٣٢، وطبقات الشافعية ٥: ١٣٢، وطبقات ابن قاضى شهبة ١: ٩٨ - ٩٩، وعيون الأنباه ٢: ٢٠١ - ٢١٣، وفوات الوفيات ٢: ٩ - ١١، وكشف الظنون ٣٠، ٦٩٦، ٧١٤، ١١٦٩، ١٢٧٤، ١٣١٥، ١٣٦١، ١٣٩٧، ١٤٦٦، ١٧١٥، ١٩٣٧، ١٩٩٦، ومرآة الجنان ٤: ٦٨، والمستفاد من ذيل تاريخ بغداد الورقة ٥٠، والوافى بالوفيات ج ٦ مجلد ٢: ٣٠٠ - ٣٠٣.','2','193',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (596,'والعلوم القديمة والطب. أسمعه والده فى صباه من جماعة كأبى الفتح محمد بن عبد الباقى «١» بن البطىّ وأبى زرعة طاهر بن محمد بن طاهر المقدسىّ «٢».
خرج عن بغداذ إلى الشام، وقدم مصر بعد سنة ثمانين، ونزل فى مسجد باب زويلة، وتعرّف بالحاجب لؤلؤ، وادّعى ما ادعاه، فمشى طلبة المصريين إليه واختبروه، فقصّر فى كلّ ما ادعاه فجفوه، وأقام بها مدة لا يعبأ به. ثم نفق على شابين كوفيين بعيدى الخاطر يعرفان بولدى إسماعيل بن حجاج المقدسىّ كاتب الجيش، فنقلاه إليهما، وأخذا عنه من العربية ما زادهما يبسا وعمى قلب ولكنة لسان. ثم خرج بعد ذلك إلى دمشق، وادعى الرواية، فقرأ عليه بعض المبتدئين.
وكان دميم الخلقة نحيلها، قليل لحم الوجه قصير الخلقة. ولما رآه زيد ابن الحسن الكندى لقّبه المطّجن- والألقاب تنزل من السماء- فشاعت ولم يعرف بعد ذلك إلا بها. وكان يدّعى تصانيف كتب «٣» ما فيها مبتكر، وإنما يقف على تصانيف غيره، فإما أن يختصر أو يزيد ما لا حاجة إليه، وهى','2','194',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (597,'فى غاية البرودة والركاكة. وكان إذا اجتمع بصاحب علم فرّ من الكلام معه فى ذلك العلم، وتكلم فى غيره مغربا، ولم يكن محققا فى شىء مما يقوله ويدّعيه.','2','195',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (598,'ولقد اجتمعت به واختبرته فرأيته فيما يدّعيه كالأعمى الذى يتحسس ويدّعى حدّة النظر؛ وما وثقت من روحى بذلك حتى سألت جماعة من أهل علوم متفرقة قد كان يدّعيها، فذكروا من أمره بعد نظره وكلامه نظير ما علمته منه.
ومن أسوأ أوصافه قلة الغيرة- ونعوذ بالله من ذلك- وقطن حلب فى آخر عمره، وأجرى له بها رزق على الطب؛ وهو لا يعلمه.
وخطر له فى شهور سنة ثمان وعشرين وستمائة السفر إلى العراق ليحج، فمرض ببغداذ، وأخذ فى مداواة نفسه بطبه، فمات- كما شاء الله- فى شهور سنة تسع وعشرين وستمائة، وأبيعت كتبه بحلب، فوقعت على شىء منها، وهى فى غاية الانحطاط عن رتبة الكمال. ونعوذ بالله من فتنة الدعوى.
كان مولده سنة سبع وخمسين وخمسمائة «١».','2','196',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (599,'<span data-type="title" id=toc-475>٤٠٨ - عبد الملك بن قريب أبو سعيد الأصمعىّ [١]</span>
عبد الملك بن قريب «١» بن عبد الملك بن علىّ بن أصمع بن مظهّر «٢» بن رباح بن عمرو
_________
[١] ترجمته فى أخبار النحويين البصريين للسيرافى ٥٨ - ٦٧، وإشارة التعيين الورقة ١٢٩، والأنساب للسمعانى ٥١ ا- ٥٢ ب، وبغية الوعاة ٣١٣ - ٣١٤، وتاريخ ابن الأثير ٥: ٢٢٠، وتاريخ الإسلام للذهبى (وفيات سنة ٢١٦)، وتاريخ أصبهان لأبى نعيم ٢: ١٣٠، وتاريخ بغداد ١٠: ٤١٠ - ٤٢٠، وتاريخ ابن عساكر ٢٤: ٤١٤ - ٤٢٩، وتاريخ أبى الفدا ٢: ٣٠، والتصحيف والتحريف ٤٥ - ٦٤، وتقريب التهذيب ١٦٥، وتلخيص ابن مكتوم ١١٧ - ١١٨، وتهذيب التهذيب ٦: ٤١٥ - ٤١٧، وتهذيب اللغة للأزهرى ١: ٦ - ٧، وجمهرة الأنساب لابن حزم ٢٣٤، وخلاصة تذهيب الكمال ٢٠٧ - ٢٠٨، وابن خلكان ١: ٢٨٨ - ٢٩٠، وروضات الجنات ٤٥٨ - ٤٦٢، وشذرات الذهب ٢: ٣٦ - ٣٨، وطبقات الزبيدى ١١٧ - ١٢٤، وطبقات ابن قاضى شهبة ١: ١٠١ - ١٠٦، وطبقات القرّاء ١: ٤٧٠، وطبقات المفسرين للداودى الورقة ١٥١، وعيون التواريخ (وفيات سنة ٢١٦)، والفهرست ٥٥ - ٥٦، وكشف الظنون ١١، ١١٤، ١١٥، ٧٢٢، ٧٢٣، ١٢٤٠، ١٣٥٥، ١٣٨٨، ١٣٩٥، ١٣٩٦، ١٣٩٩، ١٤٣٢، ١٤٥٤، ١٤٦١، ١٤٦٦، ١٤٦٩، ١٤٧٢، ١٩١٦، ١٩٧٩، ١٩٨١، واللباب فى الأنساب لابن الأثير ١: ٥٦، ومرآة الجنان ٢: ٦٤، ومراتب النحويين ٧٤ - ١٠٥، والمزهر ٢: ٤٠٤ - ٤٠٥، ٤١٩، ٤٢٣، ٤٦٢، ومسالك الأبصار ج ٤ مجلد ٢: ٢٢٥ - ٢٢٧، والمعارف لابن قتيبة ٢٣٦ - ٢٣٧، والنجوم الزاهرة ٢: ١٩٠، ٢١٧، ونزهة الألباء ١٥٠ - ١٧٢، والوافى بالوفيات ج ٦ مجلد ٢: ٣٥٤ - ٣٥٩، والأصمعى: منسوب إلى جدّه أصمع.','2','197',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (600,'ابن عبد شمس بن أعيا بن سعيد بن عبد [بن] «١» غنم بن قتيبة بن معن بن مالك بن أعصر بن سعد بن قيس عيلان، أبو سعيد الأصمعىّ «٢»، صاحب اللغة والنحو والغريب والأخبار والملح.
سمع شعبة «٣» بن الحجاج والحمادين «٤» ومسعر بن «٥» كدام وغيرهم.
روى عنه ابن أخيه عبد الرحمن بن عبد الله، وأبو عبيد القاسم بن سلّام، وأبو حاتم السّجستانىّ، وأبو الفضل الرّياشىّ، وأحمد بن محمد اليزيدىّ «٦» وغيرهم.
وكان من أهل البصرة، وقدم بغداذ فى أيام هارون الرشيد. قال عمر بن شبّة «٧»:
سمعت الأصمعىّ يقول: أحفظ ست عشرة ألف أرجوزة.','2','198',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (601,'قال الأصمعى: بعث «١» إلىّ محمد الأمين- وهو ولىّ العهد يومئذ- وقال:
إن أمير المؤمنين قد استدعاك على دوابّ البريد- وبين يديه السندىّ بن شاهك- فقال: خذه «٢» وسر. فسرت، فلما وصلت إلى الرّقّة «٣» أحضرنى الفضل بن «٤» الربيع إلى الرشيد، وهو منفرد، وسلّمت، فردّ واستدنانى وقال: أهديت إلىّ جاريتان وأردت أن تختبرهما- وأمر بإحضارهما، وهما أحسن شىء- فسألت إحداهما عن كل فن من فنون الأدب، فأجابت بجواب حسن، فاستنشدتها «٥» فأنشدت:
يا غياث البلاد فى كل محل ... ما يريد العباد إلّا رضاك','2','199',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (602,'لا ومن شرّف البلاد وأعلى ... ما أطاع الإله عبد عصاك
واختبرت الأخرى فوجدتها دونها؛ فقلت: ما تبلغ منزلة هذه، وإذا روّضت بالتعليم جادت.
فأمر بتجهيز الموصوفة وتحسينها لينال منها «١»، ثم قال: أخبرنى «٢» بشىء من أعاجيب ما سمعت من أخبار الناس، فقلت: صاحب لنا فى بدو بنى فلان، قد أتت عليه ست وتسعون سنة، وهو أصحّ الناس ذهنا، وأجودهم أكلا، وأقواهم بدنا، غبت «٣» عنه مدّة وعدت إليه، فوجدته من سوء الحال على خلاف ما وصفت، فسألته:
ما الذى نزل به؟ فقال: لمحت جارية قد لاثت رأسها، وطلت بالورس ما بين قدميها إلى رأسها، وعليها قميص وقناع مصبوغان، وفى عنقها طبل توقّع عليه، وتنشد هذا الشعر:
محاسنها سهام للمنايا ... مريّشة بأنواع الخطوب
برى ريب المنون لهنّ سهما ... تصيب بنصله مهج القلوب
فأجبتها:
قفى شفتى فى موضع الطّبل ترتعى «٤» ... كما قد أبحت الطّبل فى جيدك الحسن','2','200',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (603,'هبينى عودا أجوفا تحت شنّة «١» ... تمتّع فيها بين نحرك والذّقن
فلما سمعت الشعر منى نزعت الطبل فرمت به فى وجهى، وبادرت إلى الخباء.
فدخلت، فلم أزل واقفا إلى أن حميت الشمس على مفرق «٢» رأسى؛ لا تخرج إلىّ، ولا ترجع جوابا، فقلت: أنا والله معها كما قال الشاعر:
فوالله يا سلمى لطال قيامتى ... على غير شىء يا سليمى أراقبه
ثم انصرفت قريح العين سخينها. فهذا الذى ترى من التغير لعشقى لها. فضحك الرشيد، ثم قال: يا عباسىّ، أعط عبد الملك مائة ألف درهم، وردّه إلى مدينة السلام «٣». فقبضتها وأتتنى صلة الجارية التى وصفتها ألف دينار مع خادم، وأمر لى الفضل بن الربيع من ماله بعشرة آلاف درهم.
وأخبار الأصمعى كثيرة مدوّنة. قال المبرّد: كان أبو زيد الأنصارىّ صاحب لغة وغريب ونحو، وكان أكثر من الأصمعى فى النحو، وكان أبو عبيدة أعلم من أبى زيد والأصمعىّ بالأنساب والأيام والأخبار، وكان الأصمعىّ بحرا فى اللغة لا يعرف مثله فيها وفى كثرة الرواية، وكان دون أبى زيد فى النحو.
وقيل لأبى نواس: قد أشخص أبو عبيدة والأصمعى إلى الرشيد. قال:
أما أبو عبيدة فإنهم إن أمكنوه من سفره «٤» قرأ عليهم أخبار الأوّلين والآخرين، وأما الأصمعى فبلبل يطربهم بنغماته.','2','201',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (604,'قال الأصمعى: حضرت أنا وأبو عبيدة عند الفضل بن الربيع، فقال لى:
كم كتابك فى الخيل؟ فقلت: مجلد واحد، فقال لأبى عبيدة عن كتابه فى الخيل فقال: خمسون مجلدا، فقال له: قم إلى هذا الفرس وأمسك عضوا عضوا منه واذكر، فقال: لست ببيطار، وإنما هذا شىء أخذته عن العرب، فقال لى:
قم يا أصمعىّ وافعل ذلك، قال: فقمت وأمسكت ناصية الفرس، وشرعت أذكر منه عضوا عضوا ويدى على ذلك العضو، وأنشد ما قالته العرب، إلى أن فرغت منه. فقال: خذه، فكنت إذا أردت أن أغيظ أبا عبيدة ركبته إليه.
قال محمد بن إسحاق النديم فى كتابه «١»: «مات الأصمعى فى سنة عشر «٢» ومائتين.
وله من الكتب: كتاب خلق «٣» الإنسان. كتاب الأجناس «٤». كتاب الأنواء. كتاب الهمز «٥». كتاب المقصور والممدود. كتاب الفرق «٦».
كتاب الصفات. كتاب الأبواب «٧». كتاب الميسر والقداح.
كتاب خلق الفرس. كتاب الخيل «٨». كتاب الإبل «٩». كتاب الشاء «١٠».','2','202',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (605,'كتاب الأخبية [والبيوت «١»]. كتاب الوحوش «٢». كتاب فعل وأفعل.
كتاب الأمثال. كتاب الأضداد «٣». كتاب الألفاظ. كتاب السلاح.
كتاب اللغات. كتاب مياه العرب. كتاب النوادر. كتاب أصول الكلام. كتاب القلب والإبدال «٤». كتاب جزيرة العرب. كتاب الدلو. كتاب الاشتقاق. كتاب الرحل. كتاب معانى الشعر.
كتاب المصادر «٥». كتاب الأراجيز. كتاب النحلة «٦». كتاب النبات «٧» [والشجر «٨»]. كتاب ما اختلف لفظه واتفق معناه. كتاب غريب الحديث، [نحو مائتى ورقة، رأيته بخط السكرىّ «٩»]. كتاب السرج واللجام [والشوى والنعال] «١٠» والترس والنبال. كتاب غريب «١١» الحديث. كتاب الكلام الوحشىّ. كتاب نوادر الأعراب. كتاب المذكر والمؤنث.
وعمل الأصمعىّ قطعة كبيرة من أشعار العرب ليست بالمرضية عند العلماء «١٢» لقلة غريبها واختصار روايتها».','2','203',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (606,'ذكره الحافظ أبو نعيم فى كتاب تاريخ أصبهان وقال: «توفى سنة اثنتى عشرة ومائتين».
قال الأصمعىّ: بعث إلىّ محمد بن هارون «١»، فدخلت عليه، وفى يده كتاب يديم النظر إليه، ويتعجب منه، ثم قال: يا عبد الملك، أما تعجب من هذا الشاب وما يجىء به! فقلت: من هو؟ فقال: عباس بن الأحنف، ثم رمى بالكتاب إلىّ فإذا فيه شعر قاله عباس «٢»:
إذا ما شئت أن تصن ... ع «٣» شيئا يعجب الناسا
فصوّر هاهنا فوزا ... وصوّر ثمّ عباسا «٤»
ودع «٥» بينهما شبرا ... وإن زدت فلا باسا
فإن لم يدنوا حتى ... ترى رأسيهما راسا
فكذّبها بما قاست ... وكذبه بما قاسى
قال الأصمعى: وكان بينى وبين عباس شىء، فقلت: مسترق يا أمير المؤمنين، فقال: ممن؟ قلت: من العرب والعجم، قال: ما كان من العرب؟ قلت: رجل يقال له عمر، هوى جارية يقال لها قمر، فقال:
إذا ما شئت أن تصن ... ع شيئا يعجب البشرا
فصوّر هاهنا قمرا ... وصوّر هاهنا عمرا','2','204',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (607,'فإن لم يدنوا حتّى ... ترى بشريهما بشرا
فكذبها بما ذكرت ... وكذّبه بما ذكرا
قال: فما كان من العجم؟ قلت: رجل يقال له «فلقاء» هوى جارية يقال لها «زورق»، فقال:
إذا ما شئت أن تصن ... ع شيئا يعجب الخلقا
فصوّر هاهنا زورق ... وصوّر هاهنا فلقا
فإن لم يدنوا حتى ... ترى خلقيهما خلقا
فكذبها بما لاقت ... وكذّبه بما يلقى
قال الأصمعىّ: فبينا نحن كذلك إذ جاء الحاجب، فقال: عباس بالباب، فدخل فقال: يا عباس، تسرق معانى الشعر وتدّعيه، فقال: ما سبقنى إليه أحد.
فقال محمد: هذا الأصمعىّ يحكيه عن العرب والعجم، ثم قال: يا غلام، ادفع الجائزة إلى الأصمعىّ «١».
فلما خرجا قال العباس: كذبتنى وأبطلت جائزتى! فقلت له: أتذكر يوم كذا! وأنشأت أقول:
إذا وترت امرأ فاحذر عداوته ... من يزرع الشوك لا يحصد به عنبا «٢»','2','205',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (608,'<span data-type="title" id=toc-476>٤٠٩ - عبد الملك بن حبيب السّلمىّ الأندلسىّ [١]</span>
كان قد جمع علم الفقه والحديث وعلم الإعراب واللغة والتصرف فى فنون الأدب، وله تصانيف جمة فى أكثر الفنون، منها كتابه فى إعراب القرآن، وكتابه فى شرح الحديث إلى غير ذلك «١».
وقيل لسحنون «٢» بن سعيد: مات عبد الملك بن حبيب، فقال: مات عالم الأندلس؛ بل والله عالم الدنيا.
ولم يكن من أهل السعة فى دنياه، بل كان من المقتّر عليهم رزقهم، وله فى ذلك:
صلاح أمرى والّذى أبتغى ... هين على الرّحمن فى قدرته
ألف من البيض فأقلل بها ... لعالم أزرى على بغيته
زرياب قد يأخذها قفلة «٣» ... وصنعتى أشرف من صنعته
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٩، وبغية الوعاة ٣١٢، وتاريخ الإسلام للذهبى (وفيات سنة ٢٣٨)، وتاريخ علماء الأندلس لابن الفرضى ١: ٢٢٥ - ٢٢٨، وتذكرة الحفاظ للذهبى ٢: ١٠٧ - ١٠٨، وتلخيص ابن مكتوم ١١٩، والديباج المذهب ١٥٤ - ١٥٦، وشذرات الذهب ٢: ٩٠، وطبقات الزبيدىّ ١٧٦ - ١٧٧، وطبقات ابن قاضى شهبة ٢: ١٠٠، وعيون التواريخ (وفيات سنة ٢٣٨). وكشف الظنون ١٢٠٥، ١٩٩٦، ولسان الميزان ٤: ٥٩ - ٦٠، ومرآة الجنان ٢: ١٢٢، ومطمح الأنفس ٣٦ - ٣٧، وميزان الاعتدال للذهبى ٢:
١٣٣، والنجوم الزاهرة ٢: ٢٩٣، ونفح الطيب ٢: ٢١٤ - ٢١٧، والوافى بالوفيات ج ٦ مجلد ١: ٢١.','2','206',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (609,'وزرياب «١» هذا رحل من المشرق إلى الأندلس، ونال بها أموالا من ولاة الأمر «٢».

<span data-type="title" id=toc-477>٤١٠ - عبد الملك بن سراج بن عبد الله بن محمد بن سراج [١]</span>
مولى بنى أمية، من أهل قرطبة، يكنى أبا مروان. أقام اللغة بالأندلس غير مدافع. روى عن أبيه «٣» وابن الإفليلىّ «٤» ومكىّ بن أبى طالب القيروانىّ «٥» وأبى مروان ابن حيان «٦» وغيرهما.
_________
[١] ترجمته فى بغية الملتمس للضبى ٣٦٧ - ٣٦٨، وبغية الوعاة ٣١٢، وتلخيص ابن مكتوم ١١٩، والديباج المذهب ١٥٧، والصلة لابن بشكوال ١: ٣٥٧ - ٣٥٨، والوافى بالوفيات ج ٦ مجلد ٢: ٣٥١.','2','207',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (610,'كان عالما بالأدب ومعانى القرآن والحديث، وقرئت عليه كتب اللغة والغريب والأدب، وقيّد ذلك كلّه عنه. وكانت الرحلة فى ذلك الوقت إليه، ومدار أصحاب اللغة والآداب عليه، وكان وقور المجلس مهيبا. وأكثر مؤرّخو الأندلس من وصفه فى كتبهم.
ولد لاثنتى عشرة ليلة خلت من ربيع الأوّل سنة أربعمائة، وتوفى- رحمه الله- ليلة عرفة، ودفن يوم عرفة سنة تسع وثمانين وأربعمائة، ودفن بالرّبض «١».

<span data-type="title" id=toc-478>٤١١ - عبد الملك بن طريف اللغوىّ الأندلسىّ [١]</span>
من أهل قرطبة، يكنى أبا مروان، أخذ عن أبى بكر بن القوطيّة «٢» وغيره، وكان حسن التصرف فى اللغة، أصلا فى تثقيفها.
وله كتاب حسن فى الأفعال؛ وهو كثير بأيدى الناس، هذّب فيه أفعال أبى بكر ابن القوطية شيخه. وتوفى نحو الأربعمائة، وقد ذكر فى الكنى فى آخر الكتاب لشهرته بابن طريف.
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٩، وبغية الوعاة ٣١٣، وتلخيص ابن مكتوم ١١٩ - ١٢٠، والصلة لابن بشكوال ١: ٣٥٧، وكشف الظنون ١٣٩٤، والوافى بالوفيات ج ٦ مجلد ١: ١٢.','2','208',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (611,'<span data-type="title" id=toc-479>٤١٢ - عبد الملك بن قطن المهرىّ القيروانى النحوىّ [١]</span>
شيخ أهل اللغة والعربية هناك، وراوى القوم وعميدهم ورئيسهم، والمقدّم فى بلده وزمانه. وكان من أحفظ الناس لأنساب العرب وأشعارهم ووقائعهم وأيامهم. وكانت الأشعار المشروحة تقرأ عليه مجرّدة من الشرح فيشرحها ويفسّر معانيها، فلما دخلت المشروحة إلى إفريقية نظر طلبة العلم من العربية والنحو فيها، وفيما كانوا رووا عنه فيها، فلم يجدوا فى شرحه خلافا لما قال أصحاب الشرح، ولا وجدوا عليه فى روايته وتفسيره شيئا من الخطأ.
وكان لقى جماعة من العلماء بالعربية والمعروفين بالرواية؛ منهم ابن الطرمّاح «١» الأعرابىّ وأبو المنيع الأعرابىّ. وله كتب كثيرة ألّفها؛ من ذلك كتاب فى تفسير مغازى الواقدىّ، وكتاب يسمى كتاب الألفاظ، وكتاب فى اشتقاق الأسماء؛ مما لم يأت به قطرب.
وكان شاعرا خطيبا بليغا، وكان من عقلاء العلماء. وقام بخطبة- بين يدى زيادة الله بن محمد بن الأغلب «٢» - وهو أمير إفريقية يومئذ- طويلة فصيحة؛ ذهب فيها إلى تقريظه، ووصلها بشعر فيه. وكان نهما لا يقصد فى مطاعمه؛ فلا يمسك درهما ولا دينارا؛ على كثرة ما يوصل ويحبى. واستمر على حاله هذه حتى مات.
وكان بليغا؛ كتب إليه رجل كتابا وأطاله، ولم يأت بطائل، فكتب إليه:
«خير من الإطالة السكوت؛ وفى القصد إلى الحاجة قطع لمسافة الإطالة».
_________
[١] ترجمته فى إشارة التعيين الورقة ٢٩، وبغية الوعاة ٣١٤، وتلخيص ابن مكتوم ١٢٠، وطبقات الزبيدىّ ١٥٤ - ١٥٧، وطبقات ابن قاضى شهبة ١: ١٠٧ - ١٠٨. وكشف الظنون ١٠٢. وما ذكره المؤلف يوافق ما فى طبقات الزبيدىّ.','2','209',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (612,'وقال حمدون النحوىّ الملقب بالنعجة «١»: كنا عند المهرىّ يوما، فقال: اخرجوا بنا إلى مأجل «٢» مهريّة نتفرّج، وكانت داره بالقرب من سوق الأحد، فخرجنا فجلسنا حوله؛ إلى أن مرّ بنا نحو عشرين بغلا أو أكثر، ومعها رجل راكب؛ فلما رأى المهرىّ عدل إليه ونزل، ثم قال: يقرأ مولاى عليك السلام، وقد وجّه إليك بهذه الدوابّ وهى محمّلة طعاما وعسلا وخلّا وزيتا، وبهذه العشرين دينارا.
فقبضها منه تكرّها؛ ثم دمع وقال: ذهب الناس! (إنّا لله وإنّا إليه راجعون)! أبو علىّ ابن حميد يوجّه إلىّ بهذا! قال حمدون: فقلت له احمد الله واشكره، فإنّ هذا كثير.
قال: فنظر إلىّ وهو مغضب، ثم قال: هو كثير لك ولأمثالك، فأمّا لى فلا! وقال أبو عبد الله الدارونىّ: مرّ المهرىّ بناحية القيسارية عند الصيارفة، فقام إليه فنى كان يختلف إليه، ويستمع منه، فقال له: إلى أين أصلحك الله يا أبا الوليد؟
قال: إلى سوق الطعام، أشترى بهذين الدينارين قمحا. فمدّ الفتى يده إلى صرّة.
وكانت فى كمة، فدفعها إليه وقال: استعن بها- أصلحك الله- على شرائك القمح.
فأخذها ثم مضى غير بعيد، وهو يظن أنها دراهم، ففتحها فإذا فيها خمسون دينارا، فانصرف إليه، فلما رآه الرجل تلقاه، فأخرج المهرىّ الصرّة وقال: أخاف أن تكون قد غلطت؛ إنها دنانير، فقال: ما غلطت- أصلحك الله- وإنى لمحتشم من التقصير.
وقال الدارونىّ: مشيت يوما مع أبى الوليد المهرىّ، إلى أن مررنا بالجزارين، فقام إليه رجل منهم، فقال: يا أبا الوليد، أضررت بى؛ لأن بضاعتى كلّها عندك، ولا بدّ من قبض مالى قبلك، فاعتذر إليه وسأله الصبر فأبى. فمرّ بنا رجل فقال: كم لك على الشيخ؟ فقال: عشرة دنانير، فقال: هى علىّ، مرّ حتّى','2','210',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (613,'أدفعها إليك. فمضى معه، فظننت أنه من إخوان المهرىّ، [وظنّ المهرىّ أنه «١»] من أجلى فعل به ذلك. فلما صرنا إلى داره، قال: الرجل الذى أدّى عنى الدنانير من هو؟ قلت: ما أعرفه، وما كنت أظنّ إلا أنك عارف به. قال: فسل عنه، فسألت، فإذا هو رومىّ من أهل العطارين. وكان الناس من تعظم العلم والأدب على خلاف ما هم عليه اليوم.
وعمّر المهرىّ عمرا طويلا، وتوفى فى يوم الجمعة لعشر خلون من شهر رمضان سنة ست وخمسين ومائتين. «٢»

<span data-type="title" id=toc-480>٤١٣ - عبد الملك بن هشام بن أيوب الذّهلىّ النحوىّ [١]</span>
يكنى أبا محمد. صاحب المغازى، مغازى «٣» ابن إسحاق «٤». بصرىّ، قدم مصر وحدّث بها بالمغازى وغيرها. روى المغازى عن زياد بن عبد الله البكّائى «٥» عن محمد ابن إسحاق. وكان ثقة.
_________
[١] ترجمته فى بغية الوعاة ٣١٥، وتاريخ أبى الفدا ٢: ٢٩ - ٣٠، وتلخيص ابن مكتوم ١٢٠ - ١٢١، وحسن المحاضرة ٢: ٢٢٨، وابن خلكان ١: ٢٩٠، والروض الأنف ٥، وشذرات الذهب ٢: ٤٥، وطبقات ابن قاضى شهبة ٢: ١١١ - ١١٢، وعيون التواريخ (وفيات سنة ٢١٣)، وكشف الظنون ١٧٩، ١٠١٢، والوافى بالوفيات ج ٦ مجلد ١: ٢٦. والذهلىّ، بضم الذال وسكون الهاء: منسوب إلى ذهل بن معاوية بن الحارث بن معاوية بن ثور بن مرتع، وهو بطن من كندة.','2','211',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (614,'توفّى بمصر لثلاث عشرة ليلة خلت من ربيع الآخر سنة ثمان عشرة ومائتين.
وهذه السيرة «١» التى يرويها عن ابن إسحاق قد هذّب منها أماكن مرّة بالزيادة، ومرّة بالنقصان، وصارت لا تعرف إلا بسيرة ابن هشام. وللمصريّين بها فرط غرام وكثرة رواية، وعن المصريّين نقلت إلى سائر الآفاق.
وذكر السّهيلىّ «٢» الأندلسىّ ابن هشام هذا فقال: «وأما عبد الملك بن هشام فمشهور بحمل العلم، متقدّم فى علم النّسب والنحو؛ وهو حميرىّ معافرىّ «٣» من مصر.
وأصله من البصرة، وتوفّى بمصر سنة ثلاث عشرة ومائتين».
وله كتاب فى شرح أنساب حمير وملوكها «٤»، وكتاب ما وقع فى أشعار السّير من الغريب فيما ذكر لى والحمد لله [كثيرا وصلواته على نبيه محمد وسلامه]».
قلت: هذا الذى ذكره السّهيلىّ على سبيل الحدس، والمعوّل على نسبه الأوّل ووفاته الأولى؛ فإن الناقل لذلك هو أبو سعيد عبد الرحمن بن يونس المصرىّ «٥» إمام مصر فى الحديث والتاريخ، ذكره فى تاريخ الغرباء القادمين على مصر حسب ما ذكرته أوّلا، والله أعلم. «٦»','2','212',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (615,'<span data-type="title" id=toc-481>٤١٤ - عبد الواحد بن الحسين بن أحمد بن عثمان بن شيطى أبو الفتح المقرئ النحوىّ [١]</span>
من أهل الجانب الشرقى من بغداذ، ناحية الرّصافة «١». سمع أبا بكر بن إسماعيل الورّاق وأبا محمد بن معروف القاضى وعيسى بن على بن عيسى وإسماعيل بن سعد بن سويد.
كان ثقة عالما بوجوه القراءات «٢» بصيرا بالعربية؛ حافظا لمذاهب القرّاء.
سئل عن مولده فقال: ولدت يوم الاثنين السادس عشر من رجب سنة سبعين وثلاثمائة. ومات- رحمه الله- فى يوم الأربعاء الخامس والعشرين من صفر سنة خمس وأربعمائة، ودفن من يومه فى مقبرة الخيزران.

<span data-type="title" id=toc-482>٤١٥ - عبد الواحد بن على بن برهان أبو القاسم العكبرىّ النحوىّ [٢]</span>
كان من العلماء القائمين بعلوم كثيرة، منها النحو واللغة ومعرفة النّسب والحفظ لأيام العرب وأخبار المتقدّمين، وله أنس شديد بعلم الحديث، ولم يرو شيئا من الحديث.
_________
[١] ترجمته فى تاريخ بغداد ١١: ١٧، وتلخيص ابن مكتوم ١٢١، وشذرات الذهب ٣:
٢٨٥، وطبقات القراء لابن الجزرى ١: ٤٧٣ - ٤٧٤، وكشف الظنون ٣٨٣، ونزهة الألباء ٤٢٧ - ٤٢٨.
[٢] ترجمته فى إشارة التعيين الورقة ٢٩، والإكمال لابن ماكولا الورقة ٥٢، وبغية الوعاة ٣١٧، وتاريخ ابن الأثير ٨: ١٠٠، وتاريخ الإسلام للذهبى (وفيات سنة ٤٥٦)، وتاريخ أبى الفدا ٢:
١٨٥، وتاريخ ابن كثير ١٢: ٩٢، وتلخيص ابن مكتوم ١٢١ - ١٢٢، والجواهر المضية ١:','2','213',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (616,'مات فى يوم الأربعاء ودفن فى مقبرة الشّونيزىّ يوم الخميس سلخ جمادى الأولى من سنة ست وخمسين وأربعمائة.
ذكره الباخرزىّ فى كتابه وسجع له فقال: «هو أبو القاسم عبد الواحد بن الحسين بن برهان النحوىّ. رأيته ببغداذ سنة خمس وخمسين وأربعمائة شيخا بادّ «١» الهيئة، رثّ الكسوة، يمشى وقد شمل العرى [طرفيه] «٢»، ونظم رأسه وقدميه، وقصدته زائرا- ولم أكن عهدته- فإذا أنا فى باب المراتب بشيخ على ما وصفت، فلم أشكّ فى أنه ضالّتى المنشودة- وفراسة المؤمن لا تخطئ- فاقتفيت أثره إلى مسجد اجتمعت فيه تلاميذه ينتظرونه، وكمه أعجر بأجزاء النحو، فدخل عليهم وقاموا إليه، واستند إلى المحراب، وتكلم فى العلم الذى لقّب فيه، والفن الذى عقد بنواصيه، والضّرب الذى أحاط به من جميع نواحيه، فقل فى القرم «٣» الهائج هادرا، أو البحر المائج زاخرا. وكان فى نفسى أن أختلف إليه، وأغترف ممّا لديه؛ فقامت العوائق تدفع فى صدر الأمانى، والأسفار تسيّرنى سير السّوانى «٤»، وما كان عندى أن له شعرا تتعاطاه الأفواه، وتتهاداه الشفاه؛ حتى نسب إليه أبو الفرج الغندجانىّ هذه الأبيات:','2','214',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (617,'أحبّتنا بأبى أنتم ... وسقيا لكم أينما كنتم
أطلتم عذابى بميعادكم ... وقلتم نزور وما زرتم
فان لم تجودوا على عبدكم «١» ... فإنّ المعزّى به أنتم
وذكره محمد بن هلال فى كتابه فقال: «فى يوم الأربعاء لليلة بقيت من جمادى الأولى سنة ست وخمسين وأربعمائة توفى أبو القاسم عبد الواحد بن على بن عمر بن برهان النحوىّ، وقد أناف على الثمانين، ولولا شراسة خلق كانت فيه على من يقرأ عليه ويستمليه لكانت له آثار باقية وكتب مروية؛ لما كان فيه من الفضائل القوية. ولم يك يلبس سراويل، ولا يترك على رأسه غطاء، ولا يقبل لأحد عطاء».

<span data-type="title" id=toc-483>٤١٦ - عبد الواحد بن عمر بن محمد بن أبى هاشم أبو طاهر المقرئ النحوىّ [١]</span>
من مدينة أبى جعفر. قرأ على ابن درستويه بعض كتاب سيبويه، ولم ير بعد ابن مجاهد «٢» مثله. وكان يقرئ فى سكّة عبد الصمد بن على بن عبد الرحمن ابن العباس ببغداذ، وكان كوفىّ المذهب، وتوفى سنة أربع وأربعين «٣» وثلاثمائة.
_________
[١] ترجمته فى بغية الوعاة ٣١٧، وتاريخ بغداد ١١: ٧ - ٨، وتلخيص ابن مكتوم ١٢٢.
وطبقات القراء لابن الجزرى ١: ٤٧٥ - ٤٧٧، والوافى بالوفيات ح ٦ مجلد ٢: ٤١١.','2','215',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (618,'<span data-type="title" id=toc-484>٤١٧ - عبد الواحد بن محمد الكرمانىّ النحوىّ أبو القاسم [١]</span>
روى عن ابن حبّان «١» وابن المقرئ «٢» وأبى بكر محمد بن عبد الله بن الأسقاطىّ وأحمد ابن عبيد الله السهرديرىّ وعمر بن سيف البغداذىّ وغيرهم. روى عنه ابن المأمون، وذكره شيرويه بن شهمردار فى طبقات الهمذانيين، وسماه «النحوىّ».

<span data-type="title" id=toc-485>٤١٨ - عبد الوارث بن عبد المنعم الأبهرىّ النحوىّ اللغوىّ الأديب أبو المكارم [٢]</span>
صاحب أبى العلاء بن سليمان المعرّى. رحل من أبهر إلى أبى العلاء بمعرّة النعمان من أرض الشام، ولازمه وأخذ عنه جميع فنون الأدب، وبرع واستقلّ، ورجع إلى بلده، وتصدّر للإقراء والإفادة، وأخذ عنه أهل تلك الناحية أدبا كثيرا وبرع عليه جماعة؛ منهم فرامرز بن ميشة الأبهرىّ الأديب المشهور المذكور.
وكان لعبد الوارث شعر منه:
مراغ «٣» بالمراغة «٤» فى ثراها ... أحبّ إلىّ من رىّ برىّ
وأوشال «٥» بها أجدى وأندى ... على الأزمان من جىّ «٦» بجىّ «٧»
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٢.
[٢] ترجمته فى تلخيص ابن مكتوم ١٢٢.','2','216',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (619,'<span data-type="title" id=toc-486>٤١٩ - عبد الودود بن عبد الملك بن عيسى النحوىّ المغربىّ [١]</span>
نحوىّ مذكور مشهور، انتقل إلى المشرق، ودخل مدن الشام وتصدّر بها، وأقام بحلب مدّة، وجرى له بحلب قضية، وذلك أنه نظر إلى صبىّ مستحسن بها، فذهب رشده، وسقط إلى الأرض، وأفاق خجلا مما جرى عليه «١»، وخرج إلى العراق، وقرأ عليه الناس ببغداذ.
أنبأنا أبو طاهر السّلفىّ فى الإجازة العامة «٢»: «قرأت على أبى الحسن عبد الودود ابن عبد الله بن عيسى النحوىّ اللغوىّ المغربىّ ببغداد ياقوتة التصريف للأستاذ أبى عبد الله محمد بن أحمد الأردستانىّ، ومن جملة ما أورده فيه قال:
ليس فى الكلام على فعل (بضم الفاء وكسر العين) إلا واحد، وهو اسم «دئل»، وهى دويّبة، وبها سميت قبيلة أبى الأسود الدّؤلىّ».
وقال أيضا: «قرأت على أبى الحسن عبد الودود بن عبد الملك بن عيسى النحوىّ المغربىّ ببغداذ لما قدمها شيئا من التصريف، وكان متفننا، ولم أستنشده شيئا من شعره، وكان من المجيدين، وهو الذى له القصيدة السائرة يهجو فيها أحد الرؤساء، وأوّلها:
تسلّ فللأيام بشر وتعبيس ... وأيقن فلا النّعمى تدوم ولا البوس
_________
[١] ترجمته فى بغية الوعاة ٣١٨، وتلخيص ابن مكتوم ١٢٢ - ١٢٣، ومعجم السفر للسلفىّ ١: ٢١٦.','2','217',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (620,'<span data-type="title" id=toc-487>٤٢٠ - عبد الوهاب بن أصبغ النحوىّ اللغوىّ الأندلسى [١]</span>
معروف بهذا الشأن، صحب أبا على القالىّ وكتب عنه الكثير، وسمع عليه من تصانيفه كتاب المقصور والممدود. وكتب له أبو علىّ خطّه بذلك على نسخة الأصل التى بخطه، وهو يجرى مجرى من صحبه؛ كمحمد بن أبان بن سيّد، ومحمد بن الحسن الزّبيدىّ، ومحمد بن إبراهيم بن معاوية القرشىّ.
وكان متن هذا الكتاب بخط عبد الوهاب بن أصبغ؛ كذا ذكر أبو على القالىّ بخطه، وإنما أشار إلى أن المتن بخطه لسكونه إلى إتقانه وضبطه.

<span data-type="title" id=toc-488>٤٢١ - عبد الوهاب بن حريش أبو مسحل الهمذانىّ النحوىّ اللغوىّ [٢]</span>
كان من أهل العلم بالقرآن ووجوه إعرابه، عارفا بالعربية. وحدّث عن ابن حمزة الكسائىّ، روى عنه محمد بن يحيى الكسائىّ المقرئ، ويقال: إنه كان يكنى أبا محمد، ويلقّب أبا مسحل، وكان أعرابيا قدم بغداذ وافدا على الحسن بن سهل.

<span data-type="title" id=toc-489>٤٢٢ - عبد الوهاب بن هبة الله بن عبد الله بن محمد بن على بن الحسن ابن يحيى بن السّيبىّ أبو الفرج [٣]</span>
له معرفة بالأدب واللّغة، وكان يؤدّب أولاد الخليفة، وكان مولده فى سنة سبع عشرة وأربعمائة، وأدّب المقتفى «١»، وروى المقتفى عنه عن أبى محمد عبد الله
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٣.
[٢] ترجمته فى بغية الوعاة ٣١٨، وتاريخ بغداذ ١١: ٢٥، وتلخيص ابن مكتوم ١٢٣، وطبقات القراء لابن الجزرى ١: ٤٧٨. وفى بغية الوعاة «عبد الوهاب بن أحمد».
[٣] ترجمته فى تلخيص ابن مكتوم ١٢٣. والسيبى، بكسر السين: منسوب إلى سيب.
قال السمعانى: وظنى أنها قرية بنواحى قصر ابن هبيرة، نسب إليها جماعة.','2','218',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (621,'ابن محمد بن هزارمرد الصّريفينىّ «١». وروى أبو منصور موهوب بن الخضر الجواليقىّ عن المقتفى عنه عن الصّريفينىّ خبرا.
مات أبو الفرج عبد الوهاب السّيبىّ فى يوم السبت ثالث المحرّم سنة أربع وخمسمائة بالحجاز عند عوده من الحج وقبل وصوله إلى المدينة بيوم واحد، وحمل إلى المدينة، وصلّى عليه بها، ودفن بالبقيع- رحمه الله.

<span data-type="title" id=toc-490>٤٢٣ - على بن إبراهيم بن سعيد أبو الحسن النحوىّ الحوفىّ المصرىّ [١]</span>
فاضل عالم بالنحو والتفسير، قيّم بعلل العربية أتم قيام، من أهل ضيعة من حوف مصر، واسمها شبرا اللّنجة «٢».
دخل إلى مصر فطلب العربية، وقرأ على أبى بكر الأدفوىّ، وأخذ عنه وأكثر، وطالع الكتب، ولقى جماعة من علماء المغرب القادمين على مصر وغيرهم،
_________
[١] ترجمته فى إشارة التعيين الورقة ٣١، والأنساب للسمعانى ١٨١ ا، وبغية الوعاة ٣٢٨، وتلخيص ابن مكتوم ١٢٤، وحسن المحاضرة ٢: ٢٢٨، وابن خلكان ١: ٣٣٢، وشذرات الذهب ٣: ٢٤٧، وطبقات المفسرين للداودى ١٦٢ ب، وطبقات المفسرين للسيوطى ٢٥، وطبقات ابن قاضى شهبة ٢: ١٣٢، وكشف الظنون ٢٤١، ١٩٠٥، واللباب فى الأنساب ١: ٢٣٩، ومعجم الأدباء ١٢: ٢٢١ - ٢٢٢، ومعجم البلدان ٣: ٣٦٧، ومعجم السفر للسلفى ٢: ٣٠١ والحوفى، بفتح الحاء وسكون الواو: منسوب إلى حوف مصر. قال ياقوت: «والحوف بمصر حوفان؛ الشرقى والغربى، وهما متصلان، أول الشرقى من جهة الشام، وآخر الغربى قرب دمياط، يشتملان على بلدان وقرى كثيرة».','2','219',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (622,'وتصدّر لإفادة هذا الشأن، وصنّف فى النحو مصنفا كبيرا عنى «١» [به] النحويون، استوفى فيه العلل والأصول، وصنّف مصنفات أصغر منه، رأيت المصريين يشتغلون بها، وصنف تصنيفا كبيرا فى إعراب القرآن، أبدع فيه، يتنافس العلماء هناك فى تحصيله. وسمعت أن أحد المشتهرين بهذا النوع ابتاع منه نسخة بمصر فى عشرة مجلدات، وأحضرها إلى مدينته بالشام، وهو غير عالم بقدرها، ولا عارف بمصنفها؛ ولما تنبّه على جلالتها اشتدّ حفظه لها، وضنّه بها تقليدا، وادّخرها لولده إن طلع من أهل هذا الشأن. وعاش الحوفىّ- رحمه الله- إلى بعد الأربعمائة «٢».
أنبأنا أبو طاهر السّلفىّ الأصبهانىّ نزيل الإسكندرية، أخبرنا الشيخ أبو بكر عتيق بن على بن مكى السّمسطاوىّ النيدىّ «٣» بالإسكندرية، أخبرنا أبو العباس أحمد بن إبراهيم الرازىّ، أخبرنا على بن إبراهيم بن سعيد النحوىّ حدّثنا محمد بن عبد الله النيسابورىّ، حدّثنا أحمد بن شعيب الشيبانى، أخبرنا إسحاق بن منصور، أخبرنا عبد الرحمن عن مالك عن ابن شهاب عن أبى إدريس الخولانى عن أبى هريرة أن النبى صلى الله عليه وسلم قال: «من توضأ فلينثر، ومن استجمر فليوتر».

<span data-type="title" id=toc-491>٤٢٤ - على بن إبراهيم بن الحسن بن علىّ النحوىّ الصّقلىّ المعروف بابن المعلم [١]</span>
أجاد النحو واللغة، وتصدّر للإفادة، وقرأ الطب وتعبير الرؤيا. وكان له حظ حسن، وأبوه صقلّىّ وجدّه أصبهانى، واستوطن علىّ هذا مصر إلى أن مات بها.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٤، ومعجم السفر للسانى ٢: ٢٦١ - ٢٦٢، ٣٠٠ والمكتبة الصقلية ٦٤٥ - ٦٤٦، والصقلىّ: ضبطه السمعانى بفتح الصاد والقاف؛ منسوب إلى جزيرة صقلية فى بحر الروم.','2','220',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (623,'وذكر أبو الحسين بن الموفقى الكتبىّ أنه توفى فى أواخر شهور سنة اثنتين وثلاثين وخمسمائة، وكان دمث الأخلاق.
أنبأنا أبو طاهر السّلفىّ فى إجازته العامة: «قلت لأبى الحسين على بن إبراهيم ابن على النحوىّ المعروف بابن المعلم الصّقلىّ: رأيت فى المنام كأنى أطعم والدتى حلواء، ثم ألعق أصابعى فلا أجد لها الحلاوة الصادقة. فقال: هو خير يصل منك إليها، وهى المخصوصة به، فقلت: صدقت، فإنى بعد صلاة المغرب أصلّى ركعتين أقرأ فى كل ركعة الفاتحة وسورة الإخلاص ست مرات والمعوذتين مرة وأهب ثوابها لوالدتى، فقال: هو ذلك «١»».

<span data-type="title" id=toc-492>٤٢٥ - على بن إبراهيم بن على التّبريزىّ المعروف بابن الخازن أبو الحسن [١]</span>
طاف البلاد، وتقدّم فى علم العربية، وروى عن علماء زمانه، ورحل إلى الأندلس، وأسمع أهلها. وكان من أعلم الناس بالأدب واللّغات، حسن الخطّ عالما بفنون العربية، ثقة فيما يرويه. وكانت عنده غرائب، وكان شافعىّ المذهب. مولده سنة إحدى وسبعين وثلاثمائة.

<span data-type="title" id=toc-493>٤٢٦ - على بن إسماعيل بن سعيد بن أحمد بن لبّ بن حزم الخزرجى الشّارقىّ الأندلسىّ النحوىّ [٢]</span>
وشارقة «٢» حصن بقرب سرقسطة من مدن الأندلس. قرأ النحو على [ابن] «٣» طراوة المالقىّ. وكان أبوه إسماعيل مقرئا نحويا. وكان على هذا حفظة. رحل إلى المشرق
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٤.
[٢] ترجمته فى تلخيص ابن مكتوم ١٢٤، ومعجم السفر للسلفى ١: ١٩٠.','2','221',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (624,'وسمع منه الحافظ أبو طاهر السّلفىّ الأصبهانى. وقد كان سمع على ابن عطية الغرناطى «١» الحديث، وسمع أيضا من السلفىّ.

<span data-type="title" id=toc-494>٤٢٧ - على بن أحمد المهلّبىّ أبو الحسن [١]</span>
نزيل مصر. كان أديبا نحويا لغويا فاضلا كاملا، أحد علماء هذا النوع، روى عنه المصريون وأكثروا، وتنافسوا فى خطه والرواية عنه إلى زماننا هذا، ووصل لهم رواية كتب كثيرة من كتب الأدب.
قال عبد الرحمن بن إسماعيل العروضىّ أبو عيسى نزيل مصر: حدّثنى أبو الحسين على بن أحمد المهلّبىّ عن أبى الحسن محمد بن عبد الرحمن الروذبارىّ حدّثنى أبو بكر محمد بن عبد الملك التاريخىّ، قال: حدّثنى يوسف بن يعقوب بن السكيت، حدّثنى أبو عبد الله محمد بن عمرو الحمار التيمىّ بالبصرة سنة إحدى وأربعين ومائتين وله تسع وتسعون سنة قال: الخليل بن أحمد من الفراهيد، من الأزد، ولد سنة مائة، وتوفى سنة خمس وسبعين ومائة.

<span data-type="title" id=toc-495>٤٢٨ - على بن أحمد الدّريدىّ [٢]</span>
صاحب أبى بكر بن دريد، وأكثر من صحبته حتى عرف به. أصله من فارس، وكان ابن دريد يحبه ويريده، وأوصى بكتبه له، فصارت إليه.
_________
[١] ترجمته فى بغية الوعاة ٣٢٨، وتلخيص ابن مكتوم ١٢٥، وطبقات ابن قاضى شهبة ٢:
١٣٨، ومعجم الأدباء ١٢: ٢٢٤ - ٢٢٦.
[٢] ترجمته فى بغية الوعاة ٣٢٨، وتلخيص ابن مكتوم ١٢٥، وطبقات الزبيدى ١٣٠، وطبقات ابن قاضى شهبة ٢: ١٣٨، ومعجم الأدباء ١٢: ٢٢٣.','2','222',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (625,'<span data-type="title" id=toc-496>٤٢٩ - على بن أحمد الواحدىّ أبو الحسين [١]</span>
الإمام المصنف، المفسر النحوىّ. أستاذ عصره. قرأ الحديث على المشايخ وأدرك الإسناد العالى، وسار الناس إلى علمه، واستفادوا من فوائده.
وصنف التفسير الكبير، وسماه البسيط، وأكثر فيه من الإعراب والشواهد واللغة، ومن رآه علم مقدار ما عنده من علم العربية. وصنف الوسيط فى التفسير أيضا، وهو مختار من البسيط أيضا، غاية فى بابه. وصنف الوجيز «١» وهو عجيب، «٢» وصنف شرح ديوان المتنبى «٣» وهو غاية فى بابه «٤».
_________
[١] ترجمته فى إشارة التعيين الورقة ٣١، وبغية الوعاة ٣٢٧ - ٣٢٨، وتاريخ ابن الأثير ٨: ١٢٣، وتاريخ أبى الفدا ٢: ١٩٢، وتاريخ ابن كثير ١٢: ١١٤، وتلخيص ابن مكتوم ١٢٥، وابن خلكان ١: ٣٣٣، ودمية القصر للباخرزى ٢٠٣ - ٢٠٤، وروضات الجنات ٤٨٤، وشذرات الذهب ٣: ٣٣٠، وطبقات الشافعية ٣: ٢٨٩ - ٢٩٠، وطبقات ابن قاضى شهبة ٢: ١٣٥ - ١٣٨، وطبقات القرّاء لابن الجزرى ١: ٥٢٣، وطبقات المفسرين للداودى الورقة ١٦٥ ا- ١٦٦ ا، وطبقات المفسرين للسيوطى ٢٣، والفلاكة والمفلوكين ١١٧، وكشف الظنون ٧٦، ٢٤٥، ٣٥٥، ٨٠٩، ٢٠٠٢، ومرآة الجنان ٢: ٩٦ - ٩٧، ومسالك الأبصار ج ٤ م ٢: ٣٠٧ - ٣٠٩، ومعجم الأدباء ١٢: ٢٥٧ - ٢٧٠، والنجوم الزاهرة ٦: ١٠٤. والواحدى، بفتح الواو وبعد الألف حاء مكسورة. قال ابن خلكان: «لم أعرف هذه النسبة إلى أى شىء هى، ولا ذكرها السمعانى. ثم وجدت هذه النسبة إلى الواحد بن الديل بن مهرة.
ذكره أبو أحمد العسكرى».','2','223',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (626,'ومرض مرضة غير طويلة، ومات بنيسابور فى سنة ثمان وستين وأربعمائة.
وقد ذكره الباخرزىّ وسجع له فقال: «الشيخ «١» أبو الحسين «٢» على بن أحمد الواحدىّ، مشتغل بما يعنيه، وإن كان استهدافه للمختلفة يعنّيه، ولقد خبط ما عند أئمة الأدب، من أصول كلام العرب، خبط عصا الراعى فروع الغرب، «٣» وألقى الدلاء فى بحارهم حتى نزفها، ومدّ البنان إلى ثمارهم إلى أن قطفها. وله فى علم القرآن وشرح غوامض الأشعار تصنيفات، بيده لأعنتها تصريفات، وقل «٤» ما يعرض على الرواة ما يصوغه من الأشعار، وبلأى تتفتح أكمامها عن النوّار، فمما أنشدنى لنفسه، وقد دخل على الشيخ الإمام أبى عمر سعيد بن هبة الله الموفق وهو فى كتابه يتعلّم الخط ويكتب:
إن الرّبيع بحسنه وبهائه ... يحكيهما خطّ الرئيس أبى عمر
خطّ غدا ملء العيون ملاحة ... متنزّها للحظّ قيدا للبصر
فكأنه فى الدّرج «٥» يرقم كاتبا ... أولى لطاف بنانه فتق الزّهر
أخزت نقوش الصين بدعة صنعه ... فتعطلت ورقوم موشىّ الحبر
وسأله عبد الكريم الجيلىّ أبياتا يصف فيها خطه، فقال:
لعبد الكريم خطوط أنيقه ... يجيز لهن بحذق ونيقه «٦»
يطرّز بالخطّ قرطاسه ... كما طرّز السّحب لمع العقيقه «٧»
سطورا إذا ما تأملتها ... تخيّلت منها غصونا وريقه
وغارسها مرهف ناحل ... يمجّ عليها بسنّيه ريقه','2','224',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (627,'وبنيسابور نوع من الخوخ يقال له مزورة، أهدى منه شيئا إلى بعض أصدقائه، وكتب معه إليه:
الخوخ أرسل رائدا متقدّما ... ما مثله فى طيبه باكوره
هو زائر فى كل عام مرّة ... عند المصيف فلم يقال مزوره

<span data-type="title" id=toc-497>٤٣٠ - على بن أحمد، وقيل ابن إسماعيل أبو الحسن النحوىّ اللغوىّ المعروف بابن سيده الضّرير الأندلسىّ [١]</span>
إمام فى اللّغة والعربية. جمع فى اللغة كتاب المحكم، يقارب عشرين مجلدا «١»، لم ير مثله فى فنه، ولا يعرف قدره إلا من وقف عليه، وهو فى وقف التاج البندهىّ بدمشق فى رباط الصوفية؛ لو حلف الحالف أنه لم يصنّف مثله لم يحنث. وله غير ذلك من الكتب الأدبية.
وكان نادرة وقته، وله شعر جيد، وكان منقطعا إلى الأمير أبى الجيش مجاهد ابن عبد الله العامرىّ. «٢» ولما مات حدثت له نبوة ممّن خلفه، فرحل عن مستقرّه
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٢، وبغية الملتمس للضبى ٤٠٥ - ٤٠٦، وبغية الوعاة ٣٢٧، وتاريخ أبى الفدا ٢: ١٨٦، وتاريخ ابن كثير ١٢: ٩٥، وتلخيص ابن مكتوم ١٢٥، وجذوة المقتبس للحميدى الورقة ١٣٣ - ١٣٤، وابن خلكان ١: ٣٤٢، والديباج المذهب ٢٠٤ - ٢٠٥، وشذرات الذهب ٣: ٣٠٥ - ٣٠٦، والصلة لابن بشكوال ٢: ٤١٠ - ٤١١، وطبقات ابن قاضى شهبة ٢: ١٣٨ - ١٤٠، وكشف الظنون ٦٩١، ١٦١٦، ١٦١٧، ١٦٣٩؛ ١٩٩٧، ومرآة الجنان ٣: ٨٣، ولسان الميزان ٤: ٢٠٥ - ٢٠٦، ومسالك الأبصار ج ٤ مجلد ٢: ٢٥٩ - ٢٦٠، ومطمح الأنفس ٦٠، ومعجم الأدباء ١٢:
٢٣١ - ٢٣٥، ونفح الطيب ٤: ٣٥١، ونكت الهميان ٢٠٤ - ٢٠٥، و «سيده» ضبطه ابن خلكان بكسر السين وسكون الياء وفتح الدال وبعدها هاء ساكنة.','2','225',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (628,'إلى بعض الأعمال المجاورة، ثم استعطفه بقصيدة طويلة، «١» صرّف القول فيها.
فعطف له ورجع، ومات قريبا من سنة ستين وأربعمائة.
وذكره ابن بشكوال فقال: «على «٢» بن إسماعيل، يعرف بابن سيده. من أهل مرسية؛ يكنى أبا الحسن. روى عن أبيه «٣» وأبى عمر الطّلمنكىّ «٤» وصاعد اللغوىّ «٥» وغيرهم. وله تواليف حسان، منها كتاب المحكم فى اللغة، وكتاب المخصص «٦»، وكتاب الأنيق فى شرح الحماسة، وغير ذلك «٧»».
وذكر الوقّشىّ «٨» عن أبى عمر الطّلمنكىّ قال: «دخلت مرسية، فتشبث بى أهلها ليسمعوا علىّ غريب المصنف، فقلت لهم: انظروا من يقرأ لكم،','2','226',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (629,'وأمسك أنا كتابى، فأتونى برجل أعمى يعرف بابن سيده، فقرأه علىّ من أوّله إلى آخره، فعجبت من حفظه».
وكان أعمى ابن أعمى. وتوفى سنة ثمان وأربعين وأربعمائة. وقال القاضى صاعد: توفى سنة ثمان وخمسين وأربعمائة، وقد بلغ ستين سنة أو نحوها.

<span data-type="title" id=toc-498>٤٣١ - على بن أحمد بن خلف الأنصارىّ النحوىّ الأندلسىّ الغرناطىّ [١]</span>
كان من أهل المعرفة بالأدب واللغة والتقدم فى علم القراءات والضبط بالروايات. «١» وكان حسن الخط، جيّد التّقييد، أفاد النّاس هذا الشأن، فاستفادوا وسمعوا منه كثيرا.
وتوفّى- رحمه الله- ليلة الاثنين لثلاث عشرة ليلة خلت من المحرم، ودفن يوم الاثنين صلاة العصر من سنة ثمان وعشرين وخمسمائة. ومولده فى شوّال سنة أربع وأربعين وأربعمائة.
_________
[١] ترجمته فى بغية الملتمس للضبى ٤٠٦ - ٤٠٧، وبغية الوعاة ٣٢٦ - ٣٢٧، وتلخيص ابن مكتوم ١٢٥، والديباج المذهب ٢٠٥ - ٢٠٦، وطبقات ابن قاضى شهبة ٢: ١٣٣، وطبقات القرّاء لابن الجزرىّ ١: ٥١٨ - ٥١٩، وكشف الظنون ١١١، ١٣٧٩، ومعجم السفر للسلفى ١: ٢ - ٣.','2','227',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (630,'أنبأنا أبو طاهر السّلفىّ، أنشدنا أبو جعفر أحمد بن محمد بن كوثر «١» البخارىّ الغرناطىّ بديار مصر، قال: أنشدنا أبو الحسن علىّ بن أحمد بن خلف النحوىّ لنفسه بالأندلس فى كتاب الإيضاح لأبى علىّ الفارسىّ:
أضع الكرى لتحفّظ الإيضاح ... وصل الغدوّ لفهمه برواح
هو بغية المتعلمين ومن بغى ... حمل الكتاب يلجه بالمفتاح
لأبى علىّ فى الكتاب إمامه ... شهد الرواة لها بفوز قداح
يقضى على أسراره بنوافذ ... من علمه بهرت قوى الأمداح
فيخاطب المتعلمين «٢» بلفظه ... ويحلّ مشكله بومضة «٣» واح
مضت العصور وكلّ نحو ظلمة ... وأتى فكان النحو ضوء صباح
أوصى ذوى الإعراب أن يتذاكروا ... بحروفه فى الصّحف والألواح
وإذا همو سمعوا النصيحة أنجحوا «٤» ... إن النّصيحة غبّها لنجاح

<span data-type="title" id=toc-499>٤٣٢ - على بن أحمد بن محمد بن محمد المقرئ المؤدّب أبو الحسن [١]</span>
البغداذىّ الدار، الأحدب. شيخ صالح فاضل، له معرفة بالأدب، يعلّم الصبيان اللغة بالمقتدية.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٦.','2','228',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (631,'وسئل عن مولده فقال: ولدت ليلة الجمعة رابع عشر صفر سنة أربع وسبعين وأربعمائة بالجانب الشرقىّ.
وقال- رحمه الله: رأيت فى النوم عجوزا صفراء زرقاء معرقة تقول لى:
أنشدنى أبى المختار قال: كتب جدّى الأشرف بن فخر الملك إلى أخيه الأعز بأصبهان كتابا فيه هذه الأبيات:
إنّ الذى قسم الوراثة بيننا ... جعل الحلاوة والمرارة فينا
لكن أراك وردت ماء صافيا ... ووردت من جون الحوادث طينا
إن كنت أنت أخى فقل لى يا أخى ... لم بتّ جذلانا وبتّ حزينا!
ألّا اقتسمنا بيننا الفرح الذى ... كنّا اقتسمنا فى حياة أبينا!
وكان لهذا الشيخ شعر، فمنه ما قال: أنشدت بيتا وهو:
وإن لم يكن بينى وبينكم هوى ... ولم يك موصولا بحبلكم حبلى
قال: فأجزته:
ولم يجتمع فى الدهر يوما وليلة ... بشملكم يا بثن فى مجمع شملى
قال: وأنشدت أبياتا وهى:
إذا أبقت الدّنيا على المرء دينه ... فما فاته منها فليس بضائر
إذا أنت لم تؤثر رضا الله وحده ... على كل ما تهوى فلست بصابر
إذا أنت لم تحدث على كل نعمة ... لموليكها شكرا فلست بشاكر
إذا كنت بالدّنيا بصيرا فإنّما ... بلاغك منها مثل زاد المسافر
قال: فأجزته ببيت واحد فقلت:
ولا تفرحن منها بعيش وطيبه ... فإنّ قصاراه سكون المقابر','2','229',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (632,'<span data-type="title" id=toc-500>٤٣٣ - على بن أحمد بن عبد العزيز بن طنيز أبو الحسن الأنصارىّ الميورقىّ الأندلسىّ الفقيه اللغوىّ [١]</span>
رحل عن بلده إلى المشرق، ودخل الشام. روى بدمشق عن غانم «١» بن وليد المالقىّ النحوىّ المخزومىّ، وأبى عمر بن عبد البر النمرىّ، «٢» وأبى الحسن على «٣» ابن عبد الغنى القيروانىّ الضرير، وجماعة من أهل بلاده.
روى عنه عبد العزيز الكتانىّ «٤»، وأبو بكر الخطيب، وأبو محمد الأكفانىّ «٥»، وكان ثقة، وله شعر، منه:
وسائلة لتعرف كيف حالى ... فقلت لها بحال لا تسرّ
دفعت إلى زمان ليس فيه ... - إذا فتّشت عن أهليه- حرّ
_________
[١] ترجمته فى تاج العروس ٤: ٨٤، وتاريخ ابن عساكر ٢٨: ٤٣٣، وتلخيص ابن مكتوم ١٢٦، والمستفاد من ذيل تاريخ بغداد الورقة ٥٢، ومعجم البلدان ٨: ٢٣١. و «طنيز»، كزبير؛ هكذا ضبطه صاحب تاج العروس. ونقل عن ابن النجار أنه «طنز» بالطاء وتشديد النون والراء.
والميورقى، بالفتح ثم الضم وسكون الواو والراء: منسوب إلى ميورقة، وهى جزيرة فى شرقى الأندلس.','2','230',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (633,'وصفه ابن الأكفانىّ فقال: كان عالما باللغة. سافر إلى بغداذ من دمشق فى أواخر شهور سنة ثلاث وستين وأربعمائة، وأقام بها إلى أن توفّى هناك فى سنة سبع وسبعين وأربعمائة، وكان من أهل مدينة ميورقة.

<span data-type="title" id=toc-501>٤٣٤ - على بن أحمد بن على أبو الحسن البغداذىّ [١]</span>
يعرف بابن هبل. الأديب الطبيب. ولد ببغداذ، ونشأ بها، وقرأ الأدب والطبّ، وسمع وروى عن مشايخ وقته، منهم ابن السّمرقندىّ، ثم صار إلى الموصل، وخرج إلى أذربيجان، وأقام بخلاط «١» عند صاحبها شاه «٢» أرمن يطبّه، وقرأ الناس عليه هناك الحكمة والأدب، ثم عاد إلى الموصل- وقد تموّل- فأقام بها إلى حين وفاته.
وحدّث بها وأفاد وعمّر حتى كبر وعجز عن الحركة، فلزم منزله بسكّة أبى نجيح قبل وفاته بسنتين.
وكان فاضلا. سئل عن مولده فقال: ولدت ببغداذ بباب الأزج، بدرب ثمل فى ثالث عشرين ذى القعدة سنة خمس عشرة وخمسمائة. وتوفّى بالموصل ليلة الأربعاء ثالث عشر المحرم سنة عشر وستمائة، ودفن بها بمقبرة المعافى بن عمران.
وصنف كتابا حسنا كبيرا فى الطب، سماه المختار «٣».
_________
[١] ترجمته فى أخبار الحكماء للقفطى ١٥٩ - ١٦٠، وتاريخ الإسلام للذهبى (وفيات سنة ٦١٠)، وتلخيص ابن مكتوم ١٢٧، وشذرات الذهب ٥: ٤٢، وكشف الظنون ١٦٢٢، والنجوم الزاهرة، ٦: ٢٠٩، ونكت الهميان ٢٠٥ - ٢٠٦. و «هبل»، ضبطه الصفدى بفتح الهاء والباء وبعدها لام.','2','231',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (634,'<span data-type="title" id=toc-502>٤٣٥ - على بن أحمد بن منصور بن محمد بن عبد الله بن محمد أبو الحسن بن أبى العباس الغسّانىّ المعروف بابن قبيس [١]</span>
الفقيه المالكى الزاهد. دمشقىّ، سمع أباه وأبا بكر الخطيب وطبقتهما.
وكان ثقة، متحرّزا منقطعا عن الناس، ملازما لبيته فى درب النقاشة، ومتخليا فى بيته فى المنارة الشرقية. وكان يفتى على مذهب مالك، ويقرئ النحو، ويعرف الفرائض والحساب.
ولد- رحمه الله- ليلة الأحد لتسع خلون من شوّال سنة اثنتين وأربعين وأربعمائة، وتوفّى- قدس الله روحه- يوم عرفة تاسع ذى الحجة سنة ثلاثين وخمسمائة، ودفن بعد صلاة العصر من يومه بباب الصغير.

<span data-type="title" id=toc-503>٤٣٦ - على بن الأخضر النحوىّ الحمصىّ (حمص الأندلس) المغربىّ التّنوخىّ أبو الحسن [٢]</span>
كان فى المائة الخامسة من الهجرة، وله تقدّم وتصدّر فى إقليمه. روى أبو طاهر السّلفىّ عن واحد، عنه.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٧ - ١٢٨، وتاريخ ابن عساكر ٢٦: ٤٥٠، وشذرات الذهب ٤: ٩٥، ومرآة الجنان ٣: ٢٥٧ - ٢٥٨، والنجوم الزاهرة ٥:
٢٥٩.
[٢] ترجمته فى تلخيص ابن مكتوم ١٢٧، وبغية الوعاة ٣٤١، والصلة لابن بشكوال ١: ٤١٨.
وهو مكرر ٤٦٩. قال ابن مكتوم: «هو على بن عبد الرحمن بن محمد بن مهدى بن عمران التنوخى الإشبيلىّ. روى عن أبى الحجاج الأعلم، وعنه أخذ علم العربية، وعن أبى على الغسانى. ذكرهما أحد الناس عنه، وتوفى يوم الخميس سلخ سنة أربع عشرة وخمسمائة. وقد ذكره القفطى بعد ذلك فى هذا الكتاب مكررا، وذكره أبو القاسم بن بشكوال وغيره.','2','232',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (635,'أنبأنا أبو طاهر السّلفىّ فى إجازته العامة، سمعت أبا عبد الله محمد بن عبد الرزاق ابن يوسف الحمصىّ «١» (حمص «٢» الأندلس) - وكان ثقة من أهل المعرفة بالحديث- قال: أنشدنى أبو الحسن على بن الأخضر التنوخىّ النحوىّ بحمص الأندلس، قال: أنشدنى أبو محمد على بن أحمد بن سعيد «٣» [بن حزم] الحافظ لنفسه:
من لم ير العلم أغلى ... من كلّ شىء يصاب
فليس يفلح حتّى ... يحثى عليه التّراب
قال السّلفىّ: «وبعد أن أنشدنى ابن عبد الرزاق هذين البيتين كتب إلىّ شريح بن محمد بن شريح الرّعينىّ «٤» من الأندلس قال: أنبأناه أبو محمد على بن أحمد [بن سعيد] ابن حزم الظاهرىّ لنفسه».

<span data-type="title" id=toc-504>٤٣٧ - على أبو الحسين الطبرونىّ الضرير النحوىّ الأديب [١]</span>
نزيل المراغة «٥»، من أذربيجان؛ كان يشبّه فى وقته بأبى العلاء المعرىّ لتبحّره فى النحو والأدب وعلومه. أدركه أبو طاهر السّلفىّ بالمراغة، وروى عنه ووصفه.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٨، ومعجم السفر للسلفى ٢: ٢٨١ - ٢٨٢.','2','233',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (636,'<span data-type="title" id=toc-505>٤٣٨ - على السّنجارىّ [١]</span>
من آل أبى جحش. سنجارىّ نحوىّ، انحدر إلى بغداذ، وأخذ النحو عن الكمال عبد الرحمن بن الأنبارىّ، وقرأ اللغة على علىّ بن عبد الرحيم بن العصار، وكان كثير الحفظ لكلام المعرىّ؛ النثردون النظم. وكان لطيف الأخلاق، تصدّر بجامع سنجار لإفادة العربية، وقدّر له من الرزق ستون درهما فى كل شهر، وكان كثير الحفظ، حسن المحاضرة والمذاكرة، ولقى من ضيق الرزق بسنجار شدّة من نكد أهلها، وكان فى زماننا هذا.

<span data-type="title" id=toc-506>٤٣٩ - على بن بشرى اللغوىّ الكاتب الصّقلىّ [٢]</span>
من أهلها المقيمين بها. كان فى النظم والنثر سابقا لا يجارى، وفى اللغة والإعراب لا يبارى، وله من الشعر قوله:
وتعجبنى الغصون إذا تثنّت ... ولا سيما وفيهنّ الثّمار
إذا ارتجت نهود فى قدود ... فقل للحلم قد ذهب الوقار
وقوله أيضا:
ملكتنى المدامة الخندريس «١» ... وغزال يرنو وطرف يميس
إنما يملك النفوس فتعصى «٢» ... ناصحيها ما تشتهيه النفوس
قد ألفت الصّبا وإن لحظتنى ... فيه من عاذلى لواحظ شوس «٣»
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٢٨
[٢] ترجمته فى تلخيص ابن مكتوم ١٢٩، ومختصر الدرة الخطيرة الورقة ٧.','2','234',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (637,'ربّ يوم لهوت فيه بأبكا ... رحسان كأنهنّ شموس
حضرتنا السّعود فيه وغابت ... عن ذرانا فلم تطرنا «١» النّحوس
للقمارى به غناء وللرو ... ض ابتسام وللغيوم عبوس

<span data-type="title" id=toc-507>٤٤٠ - على بن ثروان بن زيد بن الحسن الكندىّ أبو الحسن [١]</span>
ابن عم أبى اليمن زيد بن الحسن الكندىّ. كانت له معرفة حسنة بالأدب وبقول الشعر، وهو الذى أفاد زيد «٢» بن الحسن ابن عمه، وأحضره مجالس مشايخ الأدب والرواية، ورغّبه فى ذلك، وحثّه عليه من صغره.
وأصلهم من بلد الخابور «٣». قدم بغداذ وأقام بها، وقرأ الأدب على أبى منصور ابن الجواليقىّ اللّغوى وعلى غيره، وسمع الحديث، وانتقل بعد ذلك إلى دمشق وسكنها، واستفاد الناس منه، وتقدّم عند أمرائها.
وتوفّى بدمشق قريبا من سنة خمس وستين وخمسمائة. وكان يكتب خطا صحيحا يشبه خط أبى منصور بن الجواليقى فى الجودة والصحة. رأيت بخطه كتاب الحماسة، وهو فى غاية الحسن والإتقان.
_________
[١] ترجمته فى بغية الوعاة ٣٣١، وتلخيص ابن مكتوم ١٢٩ - ١٣٠، وروضات الجنات ٤٨٥ - ٤٨٦، وطبقات ابن قاضى شهبة ٢: ١٤٢، ومعجم الأدباء ١٢: ٢٧٥ - ٢٧٧.','2','235',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (638,'<span data-type="title" id=toc-508>٤٤١ - على بن جعفر بن علىّ السعدىّ الصّقلّىّ المعروف بابن القطاع اللغوىّ النحوىّ الكاتب [١]</span>
مولده بصقلّية، فاضل ابن فاضل. ولد بصقلّية فى سنة ثلاث وثلاثين وأربعمائة، وقرأ الأدب على فضلائها كابن البرّ «١» اللغوىّ وأمثاله.
وأجاد النّحو غاية الإجادة، وصنّف التصانيف الجميلة، ورحل عن صقلّية لما أشرف على تملّكها الفرنج، ووصل إلى مصر فى حدود سنة خمسمائة. وأكرم فى الدولة المصرية. وتصدّر للإفادة والاستفادة. وقد كان نقدة المصريين يسمونه بالتساهل فى الرواية، فمن ذلك أنه لما دخل إلى مصر سئل عن كتاب الصّحاح فى اللغة للجوهرىّ، فذكر أنه لم يصل إليهم، ثم لما رأى اشتغال الطلبة به، ورغبة الناس فيه ركّب فيه طريقا فى روايته، وأخذ الناس عنه مقلدين له؛ إلا الأقل من محققى النقل فى ذلك الوقت.
وكان ذكيا، قال الشعر صبيا سنة ست وأربعين وأربعمائة، فمن شعره ما قاله فى الغزل، وأضمر اسم حمزة:
يا من «٢» رمى النار فى فؤادى ... وأنبط العين بالبكاء
_________
[١] ترجمته فى إشارة التعيين الورقة ٣١، وبغية الوعاة ٣٣١ - ٣٣٢، وتلخيص ابن مكتوم ١٣٠، وحسن المحاضرة ١: ٢٢٨، وخريدة القصر ١١: ٣٣ - ٣٦، وابن خلكان ١: ٣٣٩ - ٣٤٠، وروضات الجنات ٤٨٤ - ٤٨٥، وشذرات الذهب ٤: ٤٥ - ٤٦، وطبقات ابن قاضى شهبة ٢: ١٤٣ - ١٤٤، وكشف الظنون ١٣٣، ٧٣٩، ومسالك الأبصار ج ٤ مجلد ٢:
٢٥٩، ومرآة الجنان ٢: ٢١٢ - ٢١٣، ومعجم الأدباء ١٢: ٢٧٩ - ٢٨٣، والمكتبة الصقلية ٤١٥، ٥٨٩، ٦٢٧، ٦٤٦، ٦٧٦، ٧٠٠.','2','236',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (639,'اسمك تصحيفه بقلبى ... وفى ثناياك برء دائى
أردد سلامى فإنّ نفسى ... لم يبق منها سوى ذماء «١»
وارفق بصبّ أتى ذليلا ... قد مزج اليأس بالرّجاء
أنهكه فى الهوى التجنّى ... فصار فى رقّة الهواء
أقام بمصر على الإفادة والتصنيف إلى أن مات بها فى حدود سنة خمس عشرة وخمسمائة.
فمن تصانيفه: كتاب تهذيب أفعال ابن القوطيّة «٢» فى اللغة. كتاب شرح الأمثلة. كتاب الدّرة الخطيرة فى شعر أهل الجزيرة. كتاب المجموع الأدبى «٣» له.
أنبأنا أبو طاهر السّلفىّ الأصبهانىّ نزيل الإسكندرية فى إجازته العامة؛ سمعت أبا الحسين هبة الله بن على بن الحسن الكاتب الفرضى بمصر يقول:
سمعت أبا القاسم على بن جعفر بن علىّ اللغوىّ الصّقلىّ يقول: كتب إلىّ أبو الفضل يوسف بن حسداى «٤» الوزير الهارونىّ بسرقسطة من مدن الأندلس حين دخلها:
أعيذك بالله من فاضل ... أديب تداهى على صحبه
فأعرض محتقرا بزّهم ... وكلّ ينافس فى جلبه','2','237',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (640,'فلما أذاع لدينا سرائ ... ر ما كان أودع فى قلبه
جلا كل معجزة من نظيم ... لآلئه وحلى عصبه
فهل جاز سمعا ولم يلهه ... ومرّ بقلب ولم يصبه!
فأجبته مرتجلا:
بدأت بفضل أتاه الكريم ... ولا غرو منك ابتداء به
لأنك مغرى بفعل الجميل ... مهين لما عزّ فى كسبه
أتتنى أبياتك الرائقات ... بشأو بعيد على قربه
ونظم جلا النّظم فى أنقه ... وحلّى له الجدى فى قطبه
فأنطقنى حسنه واجترأت ... وقلت من الشعر فى ضربه
وعوّلت فيه على فضله ... وما خصّه الله من إربه
وذكر القاضى الموفق يوسف بن «١» الخلال كاتب الإنشاء فى الدولة القصرية بالديار المصرية أبا القاسم على بن جعفر بن على السعدىّ المعروف بابن القطاع هذا، قال:
مولده بجزيرة صقلّية سنة ثلاث وثلاثين وأربعمائة، ووفاته بمصر سنة خمس عشرة وخمسمائة.
نقل من خط الشيخ أبى القاسم على بن جعفر بن القطاع حكاية هذا معناها:
رأيت فى المنام كأنى جالس مع الفقيه عبد الرحمن بن أبى بكر السّرقوسىّ إذ دخل علينا شاب ومعه غلام أسود طوال، فسلّم وجلس، فقال له الفقيه: ما هذا العبد الأسود؟ فقال: اشتريته للخدمة، فقال له الفقيه: ما يصلح هذا للخدمة، فقال له الشاب: هذا هو المال، فقال الفقيه ارتجالا:
قد جاء عبّاد بعبد له','2','238',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (641,'ثم قال: أجز، فقلت:
فقال لى هذا هو المال
فقال الفقيه:
فقلت إن العذر فى مثله
ثم قال: أجز، فقلت:
يصعب والإنسان يحتال
فقال الفقيه:
والناس قد قالوا ومن ذا الّذى
ثم قال: أجز، فقلت:
يردّ قيل الناس إن قالوا
وانتبهت.

<span data-type="title" id=toc-509>٤٤٢ - علىّ بن جعفر الكاتب أبو الحسن الفارسىّ النحوىّ الشاعر [١]</span>
ذكره الحافظ أبو عبد الله فى تاريخ نيسابور قال: «وكان من أعيان الأدباء من أهل العلم، علّقت عنه من كلامه، ولم أعرفه بالرواية. سكن نيسابور».

<span data-type="title" id=toc-510>٤٤٣ - علىّ بن الحسن التّنوخىّ النحوىّ القيروانىّ المعروف بالحروفى [٢]</span>
كان معلما يؤدّب بعض أولاد السلاطين هناك. وكان حافظا للأشعار شاعرا مقتدرا.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٣١، وبغية الوعاة ٣٣٢، ومعجم الأدباء ١٢: ٢٧٧ - ٢٧٩.
[٢] ترجمته فى تلخيص ابن مكتوم ١٣١، وبغية الوعاة ٣٣٢، وطبقات الزبيدىّ ١٦٥.','2','239',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (642,'<span data-type="title" id=toc-511>٤٤٤ - علىّ بن الحسن المعروف بعلّان النحوىّ المصرىّ [١]</span>
كان من ذوى النظر والتدقيق فى المعانى، وكان قليل الحفظ لأصول النحو؛ فإذا حفظ الأصل تكلم عليه، وأحسن وجوّد فى التعليل، ودقّق القول ما شاء.
توفى بمصر فى شوّال سنة سبع وثلاثين وثلاثمائة.

<span data-type="title" id=toc-512>٤٤٥ - علىّ بن الحسن أبو الحسن الهنائىّ الأزدىّ [٢]</span>
ويعرف بكراع النمل؛ فإنه كان دميم الحلقة. كان لغويا نحويا من علماء مصر، خلط المذهبين، وأخذ عن النحويين البصريين والكوفيين، وكان إلى قول البصريين أميل، وصنّف كتبا فى اللغة، روى فيها عن أبى يوسف الأصبهانىّ عن أبى عبيد القاسم بن سلّام «١». وكتبه فى مصر مرغوب فيها، وكذلك فى المغرب، وكان خطه حسنا صحيحا قليل الخطأ، وكان يورّق تصانيفه، لم أر له خطا فى غيرها، ورأيت جزءا من كتابه المنضد من خطه، وقد كتب فى آخره أنه أكمل وراقة وتصنيفا فى سنة تسع وثلاثمائة.
فمن تصنيفه كتاب المنضّد فى اللغة، كبير، على الحروف، ملكته. كتاب المجرّد بغير استشهاد، ملكته. كتاب المنجد فيما اتفق لفظه واختلف معناه، ملكته. كتاب الأوزان، أتى فيه باللغة على وزن الأفعال، ملكته والحمد لله.
_________
[١] ترجمته فى بغية الوعاة ٣٣٣، وتلخيص ابن مكتوم ١٣١، وطبقات الزبيدىّ ١٥١، وطبقات ابن قاضى شهبة ٢: ١٤٧، ومعجم الأدباء ١٣: ١٨.
[٢] ترجمته فى إشارة التعيين الورقة ٣٣، وبغية الوعاة ٣٣٣ - ٣٣٤، وتلخيص ابن مكتوم ١٣١، وطبقات ابن قاضى شهبة ٢: ١٤٦ - ١٤٧، والفهرست ٨٣، وكشف الظنون ١٨٦٢، ومعجم الأدباء ١٣: ١٢ - ١٣. والهنائى، بضم الهاء وفتح النون: منسوب إلى هناءة بن مالك بن فهم ابن غنم بن درس. قال ابن مكتوم: «كنية كراع أبو الحسن. ولقب كراع النمل لقصره».','2','240',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (643,'<span data-type="title" id=toc-513>٤٤٦ - على بن الحسن بن الحسن بن أحمد أبو أهتم بن أبى الفضل الكلابىّ الفقيه الشافعىّ المقرئ النحوىّ الفرضىّ الدمشقىّ المعروف بابن الماسح [١]</span>
ولد سنة ثمان وثمانين وأربعمائة، وقرأ القرآن بحرف ابن عامر «١» على أبى الوحش سبيع بن المسلم، «٢» وقرأ على غيره بحروف كثيرة، وسمع من مشايخ زمانه، وتفقه على أبى الحسن على بن «٣» المسلم ونصر الله «٤» بن محمد الفقيهين، وحلّق فى المسجد الجامع بدمشق قديما.
_________
[١] ترجمته فى بغية الوعاة ٣٣٢، وتاريخ الإسلام للذهبى (وفيات سنة ٥٦٢)، وتلخيص ابن مكتوم ١٣٢، والدارس ٢٠٣، وطبقات الشافعية ٤: ٢٧٢، وطبقات ابن قاضى شهبة ٢:
١٦١ - ١٦٢، وطبقات القراء لابن الجزرىّ ١: ٥٣٠. وفى الدارس أن شهرته «ابن المانح»، وذكره فى مكان آخر: باسم «ابن الماصح».','2','241',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (644,'وكان يقرئ القرآن، ويذكر دروسا من الفقه والتفسير والنحو، وصار معيدا للفقيه أبى الحسن فى المدرسة الأمينية «١»، ثم درس بعد فى الجامع «٢» مدّة، وتولّى التدريس فى المدرسة المجاهدية «٣» مدّة مديدة، وكان حريصا على الإفادة، ذا عصبية ومروءة. وكان يعرف الفرائض والمناسخات، وحدّث.
مات يوم الأحد، مستهلّ ذى الحجة سنة اثنتين وستين وخمسمائة، ودفن فى مقبرة باب الفراديس.

<span data-type="title" id=toc-514>٤٤٧ - على بن الحسن بن إسماعيل بن الحسن العبدىّ المعروف بابن العلماء [١]</span>
على بن الحسن بن إسماعيل بن الحسن بن أحمد بن معروف بن جعفر بن محمد ابن صالح بن حسان بن خضر بن معلّى بن أسد بن عمرو بن مالك بن عامر بن معاوية ابن عبد الله بن مالك بن عامر بن الحارث بن أنمار بن عمرو بن وديعة بن لكيز ابن أفصى بن عبد القيس بن أفصى بن دعمىّ بن جديلة بن أسد بن ربيعة بن نزار ابن معدّ بن عدنان. أبو الحسن العبدى، من أهل البصرة. يعرف بابن العلماء «٤».
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٣٢ - ١٣٣، ومعجم الأدباء ١٣: ٨٨ - ٩٠.','2','242',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (645,'شيخ فاضل، له معرفة بالأدب والعروض، وله فى ذلك مصنّفات، ويقول الشعر ويترسّل. قرأ الأدب بالبصرة على أبى على بن الأحمر وأبى العباس بن الحريرىّ، وأبى المعزّ بن أبى الدنيا، وتصدّر ببلده البصرة، وأقرأ الناس الأدب والحديث والعروض؛ ونعم الشيخ كان فضلا وثقة. ومن شعره:
شميتى أن أغضّ طرفى فى الدّا ... ر إذا ما دخلتها لصديق
وأصون الحديث أودعه صو ... نى وسرّى ولا أخون رفيقى
وله أيضا:
لا تسلك الطرق إذا أخطرت ... لو أنّها تفضى إلى المملكة
قد أنزل الله تعالى: «ولا ... تلقوا بأيديكم إلى التّهلكه»
سئل عن مولده فقال: ولدت فى شهر ربيع الأوّل من سنة أربع وعشرين وخمسمائة بالبصرة، وتوفى بها فى اليوم الرابع والعشرين من شعبان سنة تسع وتسعين وخمسمائة.

<span data-type="title" id=toc-515>٤٤٨ - على بن الحسن بن عنتر بن ثابت أبو الحسن الحلّى الأديب [١]</span>
يلقب شميما «١». قدم بغداذ، وأقام مدّة يقرأ النحو على أبى محمد بن الخشاب وغيره من الأدباء؛ حتى حصل طرفا من النحو واللغة والعربية، وحفظ جملا من أشعار العرب، وقال شعرا جيدا.
_________
[١] ترجمته فى بغية الوعاة ٣٢٣، وتاريخ الإسلام للذهبى (وفيات سنة ٦٠١)، وتاريخ ابن كثير ١٣: ٤١ - ٤٢، وتلخيص ابن مكتوم ١٣٣، وابن خلكان ١: ٣٤٤ - ٣٤٥، والذيل على الروضتين ٥٢، وشذرات الذهب ٥: ٤ - ٦، وطبقات ابن قاضى شهبة ٢: ١٦٢، والفلاكة والمفلوكين ٩٠ - ٩١، وكشف الظنون ١٩٧، ١٥٦٣، ١٧٨٨، والنجوم الزاهرة ٦: ١٨٨، ومعجم الأدباء ١٣: ٥٠ - ٧٢.','2','243',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (646,'سافر إلى الشام ومدح أمراءها، وديار بكر ومدح أكابرها، وجمع من شعره كتابا سماه الحماسة، وكان مهوّسا «١»، ناقص الحركات، سيّئ العقيدة، يتحرّك فى مجلسه بحركات يضحك منها وهو لا يضحك، فلا يغضب من ضحك الجماعة، ويصرف ضحكهم إلى أنه يعجب منه ومن جودة ما يأتى به، إلى أمثال ذلك من السّخف فى الفعل والقول.
أخبرنى أبو البركات سعيد بن أبى جعفر الهاشمىّ الحلبىّ قال: جاءنا الشّميم إلى حلب، فدخلنا عليه مستفيدين، قال: فرأيته يوما وقد أنشد لنفسه شعرا كثّرنا الاستحسان له، فقام إلى أحد أركان المنزل، ونام على ظهره، ورفع رجليه إلى الحائط، ولم يزل يرتفع حتى صار واقفا على رأسه، ثم جاءنا وقال: هكذا يشكر الله على النعمة، وهو أن يقف الإنسان على رأسه لا على رجليه.
وقال لى ابن الحيرانىّ النحوىّ الحلبىّ: اختبرت الشّميم الحلىّ عند وروده علينا فى النحو فلم أجده قيّما به. قال: ورأيته يكتب فى خطه «الحلوىّ»، فسألته عن ذلك، فقال: أليس تقول فى تصريفها: «حلّ حلولا»؟ قال: فلم أردّ عليه لحمقه وخرقه، أو قال كلاما هذا معناه؛ فإننى كتبته من حفظى.
وكان قد اكتسب مالا من عطاء المرفدين له، وكان لا ينفق منه ولا يفارقه، [يضعه] فى جمدان «٢» كبير له لا يزاوله.
وحكى لى ياقوت الحموىّ عتيق عسكر التاجر؛ قال لى الشّميم الحلىّ يوما- وقد خلوت به: قد أنست بفضلك وعقلك، ومعى فى هذا الجمدان بين ثيابى ستة آلاف دينار مصرية- أو قال ثلاثة آلاف دينار مصرية (الشك منى) - وقد عزمت على أنى أعطيك منها جزءا متوفرا تتجّر فيه لتجد به مرفقا، ومتى غنيت أعد إلىّ رأس المال. قال: فامتنعت من ذلك.','2','244',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (647,'وذكر لى أبو البركات سعيد الهاشمىّ قال: رأيته يوما ونحن عنده وقد جرى ذكر نصيبين ووخمها، فقال: حضرتها فى بعض أسفارى سنة، وقد وخمت واشتد وخمها، ومات أهلها، فكنت كثيرا ما أرى الجنائز وخلفها النساء ينحن؛ فأصغيت إليهن، فلم يعجبنى قولهن، فصنّفت لهن نواحا ينحن به. ثم قام على قدميه وأمرنا بالقيام، ووقف على صفّة ونحن فى وسط القاعة وقال: قولوا كما أقول، والطموا على خدودكم كما ألطم، فأجبناه إلى ذلك، فقال:
بسّى نقوعك وبسّى حبّ رمّانك ... كم تحملين الدوا قد كلّت اقدامك
بسّى نقوعك وبسّى تمر هنديك ... كم تعملين الدوا قد كلّت ايديك
قال: وأخذ يلطم على خدّيه، ونحن نشير إلى خدودنا بمثل ذلك.
وأخبرنى العماد بن السابق الكتبىّ بحلب قال: أخبرنى أبو الخطاب بن دحية المغربىّ قال: ما رأيت أكفر من شميم؛ فإننى اجتمعت به وذاكرته، فقال: قد قيل فى «الدهده» كذا، وتلا آية من القرآن؛ فقلت: ما معنى قولك الدهده؟
فقال: الدهده فى كلام العرب: الهذيان (تعالى الله عما يقول علوا كبيرا) ومن شعره:
لا تسرحنّ الطرف فى بقر المها ... فمصارع الآجال فى الآجال «١»
كم نظرة أردت وما أخذت يد ال ... مصمى «٢» لمن قتلت أداة قتال
سنحت وما سمحت بتسليم، وإق ... لال التحية فعلة المغتال
أضللت قلبى عندهنّ ورحت أن ... شده بذات الضّال «٣» ضلّ «٤» ضلالى','2','245',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (648,'ألوى بألوية العقيق على الطّلو ... ل مسائلا من لا يجيب سؤالى «١»
تربت يدى فى مقصدى من لا يدى ... قودى وأولى لى بها أولى لى «٢»
يا قاتل الله الدّمى»
كم من دم ... أجرين حلّا كان غير حلال
أثّلن ذلّ اليتم فى الأشبال ... وفتكن بالآساد فى الأغيال «٤»
ونفرن حين ذكرت إقبالى ولو ... أنى نفرت لكان من إقبالى
لكن أبى رعيى ذمام الحبّ أن ... أولى الوفاء قطيعة من قال «٥»
وكان إذا حصل له من يقوم به أقام عنده، وسكن إلى ذلك، حافظا لما معه من المال، غير منفق منه بخلا به.
واتفق أنه دخل الموصل، وعلم به رجل ورّاق يعرف بابن الحدوس البقال، وتحقق ما معه من المال، وأنزله فى مسجد له، وقام به إلى أن توفى وفاز بموجوده، وغفلت عنه الظلمة فى المطالبة به. وقيل: إنه ظهر ذلك فى ثروته.
وكانت وفاته بالموصل فى العشر الأخير من شهر ربيع الآخر سنة إحدى وستمائة «٦».','2','246',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (649,'<span data-type="title" id=toc-516>٤٤٩ - على بن الحسن بن الوحشىّ الموصلىّ النحوىّ [١]</span>
أنبأنا أبو طاهر السلفىّ فى إجازته العامة، أنشدنى أبو الفرج هبة الله بن محمد ابن المظفر بن الحدّاد الكاتب بثغر آمد، قال: أنشدنى أبو الفتح على بن الحسن ابن الوحشىّ الموصلىّ النحوىّ لنفسه فى بكائه على الربع «١»:
لا تلحنى فى بكائيه فساكنه ... لم ألفه هاجرى يوما فأهجره

<span data-type="title" id=toc-517>٤٥٠ - على بن الحسين الضرير النحوىّ الأصبهانىّ المعروف بجامع العلوم [٢]</span>
سجع له بعض الفضلاء «٢» فقال فى وصفه: «هو فى النحو والإعراب كعبة لها أفاضل العصر سدنة، وللفضل فيه بعد خفائه أسوة حسنة».
«قال لى عمر بن قشام الحلبىّ: أخبرنى الصفىّ الحنفىّ الأصبهانىّ نزيل همذان وصاحب الطريقين أنه والده- يعنى جامع العلوم- ولا عجب أن يكون فضل الصفىّ، من ذلك المنهل الروىّ».
_________
[١] ترجمته فى بغية الوعاة ٣٣٣، وتلخيص ابن مكتوم ١٣٢، ومعجم الأدباء ١٣: ٣٢ - ٣٢،
[٢] ترجمته فى إشارة التعيين الورقة ٣٣، وتلخيص ابن مكتوم ١٣٣، وكشف الظنون ٢٦٣، ومعجم الأدباء ١٣: ١٦٤ - ١٦٦، ونكت الهميان ٢١١.','2','247',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (650,'وكان جامع العلوم هذا قد سيّر إلى خراسان يسأل عن معنى بيت شعر من شعر الفرزدق «١» وهو:
وليست خراسان التى كان خالد ... بها أسد «٢» إذ كان سيفا أميرها
فلم يبق فاضل من فضلاء خراسان إلا وكتب لهذا البيت شرحا.
وكان تسيير هذا البيت إلى خراسان من جهة جامع العلوم فى شهور سنة خمس وثلاثين وخمسمائة. وهذا البيت قد اختلف النحاة فى معناه وإعرابه، فذكره ابن جنّى فى خصائصه «٣»، وابن فضّال المجاشعىّ فى السيرة، وسأذكر له آخر الترجمة ما قاله جمهور النحاة فيه، وما يقتضيه التحقيق من معناه إن شاء الله.
فأما هذا الإمام جامع العلوم؛ فإنه استدرك على أبى علىّ الفارسىّ، وعلى عبد القاهر الجرجانىّ. وله شرح اللمع، عجيب المأخذ، قد حصر فيه الأصول','2','248',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (651,'وما تفرّع عليها، وهو غاية فى الإفادة والإيجاز. وله غير ذلك من التصانيف «١» فى العربية؛ من وقف عليها علم فضله. وله شعر منه:
أحبب النحو من العلم فقد ... يدرك المرء به أعلى الشّرف
إنّما النحوىّ فى مجلسه ... كشهاب ثاقب بين السدف
يخرج القرآن من فيه كما ... تخرج الدّرة من بين الصّدف

<span data-type="title" id=toc-518>٤٥١ - على بن الحسين بن موسى بن محمد بن إبراهيم بن موسى ابن جعفر بن محمد بن على بن الحسين بن على بن أبى طالب أبو القاسم العلوىّ [١]</span>
يلقب المرتضى ذا المجدين. وكانت إليه نقابة الطالبيين، وكان شاعرا مشتهرا كثير الشعر، يعرف النحو واللغة، وله تصانيف فى علم الكلام على مذهب الشيعة.
روى عن جماعة من النحاة العلماء، وروى عنه. وكتابه المسمى بالغرر والدّرر «٢» - وهى مجالس أملاها، تشتمل على فنون من معانى الأدب، تكلم فيها على النحو
_________
[١] ترجمته فى بغية الوعاة ٣٣٥ - ٣٣٦، وتاريخ ابن الأثير ٨: ٤٠ - ٤١، وتاريخ الإسلام للذهبى (وفيات سنة ٤٣٦)، وتاريخ بغداد ١٢: ٤٠٢ - ٤٠٣، وتاريخ أبى الفداء ٢:
٢: ١٦٧، وتاريخ ابن كثير ١٢: ٥٣، وتتمة اليتيمة ١: ٥٣ - ٥٦، وتلخيص ابن مكتوم ١٣٤ - ١٣٥، وجمهرة الأنساب لابن حزم ٥٦، ٥٧، وابن خلكان ١: ٣٣٦ - ٣٣٨، ودمية القصر ٧٥ - ٧٦، وروضات الجنات ٣٨٧، وشذرات الذهب ٣: ٢٥٦ - ٢٥٨، وكشف الظنون ٧٤٨، ٧٩٤، ١٩٩١ - ٩١٢، ولسان الميزان ٤: ٢٢٣ - ٢٢٤، ومرآة الجنان ٣: ٥٥ - ٥٧، ومعجم الأدباء ١٣: ١٤٦ - ١٥٧، والمنتظم (وفيات سنة ٤٣٦)، والنجوم الزاهرة ٥: ٣٩.','2','249',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (652,'واللغة وغير ذلك- كتاب ممتع، يدلّ على فضل كثير، وتوسع فى الاطلاع على العلوم. وشعره عدّة مجلدات «١».
مولده سنة خمس وخمسين وثلاثمائة، ومات فى يوم الأحد الخامس والعشرين من شهر ربيع الأوّل سنة ست وثلاثين وأربعمائة، ودفن فى داره عشية ذلك اليوم «٢».','2','250',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (653,'<span data-type="title" id=toc-519>٤٥٢ - على بن الحسين بن محمد بن أحمد بن الهيثم أبو الفرج الأصبهانى [١]</span>
على بن الحسين بن محمد بن أحمد بن الهيثم بن عبد الرحمن بن مروان بن عبد الله ابن مروان بن محمد بن مروان بن الحكم بن أبى العاص؛ أبو الفرج الأموىّ الكاتب المعروف بالأصبهانىّ الأخبارىّ النحوىّ اللغوىّ الشاعر.
روى عن عالم من العلماء يطول تعدادهم، وكان عالما بأيام الناس والأنساب والسيرة، وكان شاعرا محسنا.
قال التّنوخىّ «١»: ومن الرواة المتشيعين الذين شاهدناهم أبو الفرج على بن الحسين الأصبهانىّ؛ فإنه كان يحفظ من الشعر والأغانى والأخبار والآثار والأحاديث المسندة والنسب ما لم أر قط من يحفظ مثله. وكان شديد الاختصاص بهذه الأشياء، ويحفظ دون ما يحفظ منها من علوم أخر؛ منها اللغة والنحو والخرافات والسيرة والمغازى، ومن آلة المنادمة شيئا كثيرا؛ مثل علم الجوارح والبيطرة، ونتفا من الطب والنجوم والأشربة وغير ذلك.
_________
[١] ترجمته فى تاريخ ابن الأثير ٧: ٢٥، وتاريخ أصبهان ١: ٢٢، وتاريخ بغداد ١١:
٣٩٨ - ٤٠٠، وتاريخ أبى الفدا ٢: ١٠٨، وتاريخ ابن كثير ١١: ٢٦٣، وتلخيص ابن مكتوم ١٣٥، وجمهرة الأنساب لابن حزم ٩٨ - ٩٩، وابن خلكان ١: ٣٣٤ - ٣٣٥، وروضات الجنات ٤٨٧، وشذرات الذهب ٣: ١٩ - ٢٠، وعيون التواريخ (وفيات سنة ٣٥٦)، والفهرست ١١٥، وكشف الظنون ٢٦، ١٢٩، ١٣٠، ١٦١، ٢٠٤، ٤١٩؛ ٧٥٦، ١٩٥١، ولسان الميزان ٤: ٢٢١ - ٢٢٢، ومرآة الجنان ٢: ٣٥٩ - ٣٦٠، ومعجم الأدباء ١٣:
٩٤ - ١٤٦، والمنتظم (وفيات سنة ٣٥٦)، وميزان الاعتدال ٢: ٢٠٠ - ٢٠١، والنجوم الزاهرة ٤: ١٥ - ١٦، ويتيمة الدهر ٣: ٩٦ - ١٠٠.','2','251',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (654,'وصنف كتبا كثيرة؛ منها كتاب الأغانى الكبير «١»، ومقاتل الطالبيّين «٢»، وأخبار الإماء الشواعر، وكتاب الحانات، وكتاب الديارات، وآداب الغرباء، وكتاب القيان.
وحصل له ببلاد الأندلس كتب قد صنفها لبنى أمية المقيمين بها هناك، وسيّرها إليهم سرا، وجاءه الإنعام والعطاء سرّا أيضا، منها كتاب نسب بنى عبد شمس. كتاب أيام العرب؛ فيه ألف وسبعمائة يوم. كتاب التعديل والانتصاف فى مآثر العرب ومثالبها. كتاب جمهرة النسب. كتاب نسب بنى شيبان. كتاب نسب المهالبة. كتاب نسب بنى تغلب. كتاب نسب بنى كلاب. كتاب الغلمان المغنين. كتاب مجرّد الأغانى «٣».
قال أبو الفرج على بن الحسين بن محمد الأصبهانى: بلغ أبا الحسن «٤» جحظة أن مدرك بن محمد الشيبانى الشاعر ذكره بسوء فى مجلس كنت حاضره، فكتب إلىّ:','2','252',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (655,'أبا فرج أهجى لديك ويعتدى ... علىّ فلا تحمى لذاك وتغضب!
لعمرك ما أنصفتنى من مودّتى ... فكن معتبا إن الأكارم تعتب «١»
فكتب إليه:
عجبت لما بلّغت عنّى باطلا ... وظنّك بى فيه لعمرك أعجب
ثكلت إذا نفسى وعزّى وأسرتى ... بفقدى ولا أدركت ما كنت أطلب
فكيف بمن لا حظّ لى فى لقائه ... وسيّان عندى وصله والتجنّب
فثق بأخ أصفاك محض مودّة ... تشاكل منها ما بدا والمغيّب
وكان أبو الحسن البستىّ يقول: لم يكن أحد أوثق من أبى الفرج.
قال أبو نعيم الحافظ الأصبهانىّ: «توفّى أبو الفرج على بن الحسين الاصبهانىّ الكاتب ببغداذ فى سنة سبع وخمسين وثلاثمائة».
وقال محمد بن أبى الفوارس «٢»: «توفى أبو الفرج الأصبهانىّ الكاتب يوم الأربعاء لأربع عشرة خلون من ذى الحجة سنة ست وخمسين وثلاثمائة. ومولده سنة أربع وثمانين ومائتين. وكان قبل أن يموت خلط. وكان أمويا، وكان يتشيّع، وهذا القول هو الصحيح فى وفاته، والله أعلم.','2','253',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (656,'<span data-type="title" id=toc-520>٤٥٣ - على بن الحسين بن بلبل النحوىّ العسقلانى أبو الحسن [١]</span>
أستاذ كبير الشأن فى علم العربية، أخذ النحو عن علىّ بن عيسى بن الفرج النحوىّ صاحب أبى علىّ الفارسىّ، وتصدّر للإقراء بعسقلان «١»، فاستفاد منه الطلبة، ونبغ له عدّة أصحاب، أهل فضل وأدب، وله شعر أجود من شعر النحاة، منه قوله فى محبوب أزرق العينين:
قدّك كالذابل حسنا وفى ... طرفك ما فى طرف الذّابل
أزرق كالأزرق يوم الوغى ... كلاهما يوصف بالقاتل
ومن شعره:
شعر الذؤابة والعذار ... قاما بعذرى واعتذارى
بأبى الذى فى خدّه ... ماء الصّبا «٢» ولهيب نار
سكرت لواحظه وقل ... بى ما يفيق من الخمار «٣»
عابوا امتهانى «٤» فى الهوى ... حتى كأنّى باختيارى
ومن الصواب- وها عذا ... رى شائن- خلع العذار
ومن شعره:
أيا راحتى ما إن أرى لك راحة ... فلا «ليتنى» تجدى علىّ ولا «علّى»
وله فى أسنانه، وقد شرعت تتقلع أوّل أوّل:
كلّ يوم لى سنّ ... آذنتنا برحيل
_________
[١] ترجمته فى بغية الوعاة ٣٣٥، وتلخيص ابن مكتوم ١٣٤ - ١٣٥.','2','254',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (657,'لى فم أصبح بعد ال ... أنس كالرّبع المحيل
طال عمرى والذى أك ... ره فى العمر الطويل
وله فى ابن حباب:
تعرف فى وجهه إذا ما ... رأيته نضرة النعيم
كأنما خدّه حباب «١» ... بتّ له ليلة السليم
ولى غريم لوى ديونى ... ليت غرامى على غريمى

<span data-type="title" id=toc-521>٤٥٤ - على بن حازم اللّحيانى [١]</span>
وقيل على بن المبارك. لغوىّ مذكور، وأخذ عنه العلماء. عاصر الفرّاء وتصدّر فى أيامه، وكان إذا دخل على الفرّاء وهو يملى كتابه النوادر أمسك الفرّاء عن الإملاء حتى يخرج اللّحيانى، فإذا خرج قال: هذا أحفظ الناس للنوادر.
وللحيانىّ كتاب فى النوادر حسن جليل، وأخذ عنه القاسم بن سلّام.

<span data-type="title" id=toc-522>٤٥٥ - على بن حبيب اللغوىّ الصّقلّىّ أبو الحسن [٢]</span>
من أهلها المقيمين بها، أحد رجال اللغة المعدودين والعلماء بها المبرزّين، وممن تناول المرمى البعيد بقريب فهمه، وأوضح المهمات بنور علمه، وكان مضطلعا بنقد الشعر ومعانيه، ناهضا بأعباء الغريب ومبانيه؛ فمن شعره:
أهاب الكأس أشربها وإنّى ... لأجرأ من أسامة فى النّزال
أراوغها مراوغة كأنّى ... ألاقى عند ذاك شبا العوالى
_________
[١] ترجمته فى بغية الوعاة ٣٤٦، وتلخيص ابن مكتوم ١٣٦، وتهذيب اللغة للأزهرى ١٠، وطبقات الزبيدى ١٣٥، وطبقات ابن قاضى شهبة ٢: ١٤٤، ومراتب النحويين ١٤٤، والمزهر ٢: ٤١٠، ومعجم الأدباء ١٤: ١٠٦ - ١٠٨، ونزهة الألباء ٢٣٥ - ٢٣٧، واللحيانى: منسوب إلى بنى لحيان بن هذيل. وقيل سمى اللحيانى لعظم لحيته.
[٢] ترجمته فى تلخيص ابن مكتوم ١٣٦.','2','255',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (658,'<span data-type="title" id=toc-523>٤٥٦ - على بن حمزة أبو الحسن الأسدىّ المعروف بالكسائىّ النحوىّ [١]</span>
أحد الأئمة القراء من أهل الكوفة. استوطن بغداذ. كان الكسائى من أهل باحمشا «١»، ودخل الكوفة وهو غلام، وكان يعلّم بها الرشيد ثم الأمين من بعده.
وكان قد قرأ على حمزة الزيات «٢»، فأقرأ زمانا بقراءة حمزة، ثم اختار لنفسه قراءة فأقرأ الناس بها، وقرأ عليه بها خلق كثير ببغداذ وبالرّقة وغيرهما من البلاد وحفظت عنه.
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٣ - ٣٤، والأنساب ٤٨٢ ا- ٤٨٢ ب، وبغية الوعاة ٣٣٦ - ٣٣٧، وتاريخ بغداد ١١: ٤٠٣ - ٤١٥، وتاريخ أبى الفدا ٢: ١٧، وتاريخ ابن كثير ١١: ٢٠١ - ٢٠٢، وتلخيص ابن مكتوم ١٣٧ - ١٣٩، وتهذيب اللغة للأزهرى ١: ٧ - ٨، وابن خلكان ١: ٣٣٠ - ٣٣١، وروضات الجنات ٤٧١، وشذرات الذهب ١: ٣٢١، وطبقات الزبيدى ٨٨ - ٩١، وطبقات ابن قاضى شهبة ٢: ١٤٧ - ١٥٤، وطبقات القرّاء ١: ٥٣٥ - ٥٤٠، وطبقات المفسرين للداودى ١٦٩ ب، ١٧١ ا، وعيون التواريخ (وفيات ١٨٩)، والفهرست ٢٩، ٣٠، ٦٥، ٦٦، وكشف الظنون ١٧٣٠، واللباب فى الأنساب ٣: ٤٠، ومراتب النحويين ١٢٠ - ١٢٢، ومرآة الجنان ١: ٤٢١ - ٤٢٢، والمزهر ٢: ٤٠٧، ٤١٩، ٤٢٣، ٤٦٣، والمعارف لابن قتيبة ٢٣٧، ومعجم الأدباء ١٣: ١٦٧ - ٢٠٣، ومعجم البلدان ٢: ٢٨، ٤: ٢٩٣، والنجوم الزاهرة ٢: ١٣٠، ونزهة الألباء ٨١ - ٩٤.','2','256',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (659,'وصنف معانى القرآن، والآثار فى القراءات. وكان قد سمع من سليمان بن أرقم «١» وأبى بكر بن عياش «٢» ومحمد بن عبيد الله العرزمىّ «٣» وسفيان «٤» بن عيينة، وغيرهم.
روى عنه أبو توبة ميمون بن حفص «٥» وأبو زكريا الفرّاء وأبو عبيد القاسم ابن سلّام وأبو عمر حفص بن عمر «٦» الدّورىّ، وجماعة.
قال أبو بكر محمد بن يحيى الصولىّ: على بن حمزة الكسائىّ، هو على بن حمزة ابن عبد الله بن بهمن بن فيروز، مولى بنى أسد.
قال الفرّاء: إنما تعلّم الكسائىّ النحو على الكبر؛ وكان سبب تعلّمه أنه جاء يوما وقد مشى حتى أعيا، فجلس إلى الهبّاريّين- وكان يجالسهم كثيرا- فقال:
قد عيّيت، فقالوا له: تجالسنا وأنت تلحن! قال: كيف لحنت؟ قالوا له:
إن كنت أردت من التعب، فقل: «أعييت»، وإن كنت تريد من انقطاع','2','257',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (660,'الحيلة فقل: «عييت» (مخفّفة). فأنت من هذه الكلمة لحنت «١». ثم قام من فوره ذلك يسأل عمن يعلّم النحو، فأرشدوه إلى معاذ الهراء «٢»، فلزمه حتى أنفد ما عنده.
ثم خرج إلى البصرة، فلقى الخليل وجلس فى حلقته، فقال له رجل من الأعراب: تركت أسد الكوفة وتميمها وعندهما الفصاحة، وجئت إلى البصرة! فقال للخليل: من أين أخذت علمك هذا؟ فقال: من بوادى الحجاز ونجد وتهامة.
فخرج [ورجع] «٣» وقد أنفذ خمس عشرة قنّينة حبر فى الكتابة عن العرب سوى ما حفظ. فلم يكن له همّ غير البصرة والخليل، فوجد الخليل قد مات، وقد جلس موضعه يونس النحوىّ، فمرت بينهم مسائل أقرّ له يونس فيها موضعه وصدّره.
وسئل: لم سميت الكسائىّ؟ فقال: لأنى أحرمت فى كساء. وقد قيل:
إنه دخل الكوفة، فجاء إلى مسجد السّبيع- وكان حمزة بن حبيب الزيات يقرئ فيه- فتقدّم الكسائىّ مع أذان الفجر؛ فجلس وهو ملتفّ بكساء من البرّكان «٤» الأسود، فلما صلّى حمزة قال: من تقدّم فى الوقت يقرأ؟ قيل له: الكسائىّ أوّل من تقدّم- يعنون صاحب الكساء- فرمقه القوم بأبصارهم، وقالوا: إن كان حائكا فسيقرأ «سورة يوسف»، وإن كان ملّاحا فسيقرأ «سورة طه»، فسمعهم فابتدأ بسورة يوسف، فلما بلغ إلى قصّة الذئب، قرأ: (فَأَكَلَهُ الذِّئْبُ)
«٥» بغير همز، فقال له حمزة الزيّات: [الذّئب] «٦» بالهمز، فقال له الكسائىّ: وكذلك أهمز الحوت (فَالْتَقَمَهُ الْحُوتُ)
«٧»؟ قال: لا. قال: فلم همزت «الذّئب» ولم تهمز','2','258',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (661,'«الحوت» وهذا (فَأَكَلَهُ الذِّئْبُ)
وهذا (فَالْتَقَمَهُ الْحُوتُ)
؟ فرفع حمزة بصره إلى خلّاد الأحول «١» - وكان أجمل غلمانه- فتقدّم إليه فى جماعة من أهل المجلس فناظروه، فلم يصنعوا شيئا. فقالوا: أفدنا- رحمك الله! فقال لهم الكسائىّ: تفهّموا عن الحائك؛ تقول إذا نسبت الرجل إلى الذّئب:
قد استذأب الرجل، ولو قلت: قد استذاب- بغير همز- لكنت إنما نسبته إلى الهزال، تقول: قد استذاب الرجل إذا استذاب شحمه (بغير همز)، فإذا نسبته إلى الحوت [تقول: قد استحات الرجل أى كثر أكله، لأن الحوت «٢»] يأكل كثيرا، ولا يجوز فيه الهمز. فلهذه العلة همز الذئب، ولم يهمز الحوت. وفيه معنى آخر: لا يسقط الهمز من مفرده ولا من جمعه، وأنشدهم:
أيها الذئب وابنه وأبوه ... أنت عندى من أذؤب ضاريات
قيل: فسمّى الكسائىّ من ذلك اليوم.
وكان السبب فى اتصاله بالرشيد أنه كان عند المهدىّ مؤدّب يؤدّب الرشيد.
فدعا المهدىّ به يوما وهو يستاك، فقال له: كيف تأمر من السّواك؟ فقال:
«استك» يا أمير المؤمنين. فقال المهدىّ: (إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ راجِعُونَ)
«٣»! ثم قال:
التمسوا لنا من هو أفهم من ذا. فقالوا: رجل يقال له علىّ بن حمزة الكسائىّ من أهل الكوفة، قدم من البادية قريبا. فكتب بإشخاصه من الكوفة. فساعة دخل عليه قال: يا علىّ بن حمزة، ما تأمر من السواك؟ قال: سك «٤» يا أمير المؤمنين.
قال: أحسنت وأصبت. وأمر له بعشرة آلاف درهم.','2','259',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (662,'وذكر أن أبا يوسف القاضى «١» كان يقع فى الكسائىّ ويقول: أيش يحسن! إنما يحسن شيئا من كلام العرب. فبلغ الكسائى ذلك. فالتقيا عند الرشيد- وكان الرشيد يعظّم الكسائىّ لتأديبه إياه- فقال لأبى يوسف يا يعقوب: بأيش تقول فى رجل قال لامرأته: أنت طالق طالق طالق؟ قال: واحدة. قال: فإن قال لها: أنت طالق أو طالق أو طالق. قال: واحدة. قال: فإن قال لها: أنت طالق ثم طالق ثم طالق. قال: واحدة. قال: فإن قال لها: أنت طالق وطالق وطالق. قال: واحدة. قال [الكسائى] «٢»: يا أمير المؤمنين، أخطأ يعقوب فى اثنتين وأصاب فى اثنتين.
أما قوله: طالق طالق طالق، فواحدة؛ لأن الثانيتين تأكيد؛ كما تقول: أنت قائم قائم قائم، وأنت كريم كريم كريم. وأما قوله: أنت طالق أو طالق أو طالق فهذا شكّ، وقعت فى الأولى التى تتيقّن. وأما قوله: طالق ثم طالق ثم طالق، فثلاث؛ لأنها نسق، وكذلك طالق وطالق وطالق.
وقال الشافعىّ رضى الله عنه: من أراد أن يتبحّر فى النحو فهو عيال على الكسائىّ.
وقال أبو حاتم سهل بن محمد السّجستانىّ: ورد علينا عامل من أهل الكوفة لم أر فى عمّال السلطان بالبصرة أبرع منه، فدخلت مسلّما عليه، فسألنى: من','2','260',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (663,'علماؤكم بالبصرة؟ قلت: الزّيادىّ «١» أعلمنا بعلم الأصمعىّ، والمازنىّ «٢» أعلمنا بالنحو، وهلال «٣» الرأى أفقهنا، والشاذكونىّ «٤» من أعلمنا بالحديث، وأنا- رحمك الله- أنسب إلى علم القرآن، وابن الكلبىّ «٥» من أكتبنا للشروط. قال: فقال لكاتبه: إذا كان الغداة فاجمعهم إلىّ. قال: فجمعنا إليه، فقال: أيكم المازنىّ؟
قال أبو عثمان: هأنذا- يرحمك الله- قال: هل يجزئ فى كفّارة الظّهار «٦» عتق عبد أعور؟ فقال المازنىّ: فلست صاحب فقه- يرحمك الله- إنما أنا صاحب عربية.
فقال: يا زيادىّ، كيف تكتب بين رجل وامرأة خالعها «٧» على الثلث من صداقها؟ قال: ليس هذا من علمى، هذا من علم هلال الرأى.','2','261',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (664,'قال: يا هلال، كم أسند «١» ابن عون «٢» عن الحسن؟ «٣» قال: ليس هذا من علمى، هذا من علم الشاذكونىّ.
قال: يا شاذكونىّ من قرأ: (تثنونى صدورهم) «٤»؟ قال: ليس هذا من علمى، هذا من علم أبى حاتم.
قال: يا أبا حاتم، كيف تكتب كتابا إلى أمير المؤمنين [تصف] «٥» فيه خصاصة «٦» أهل البصرة وما أصابهم فى الثمرة، وتسأله لهم النّظر والنّظرة؟ فقال:
لست- يرحمك الله- صاحب بلاغة وكتابة، أنا صاحب قرآن.
فقال: ما أقبح الرجل يتعاطى العلم خمسين سنة ولا يعرف إلّا فنّا واحدا، حتى إذا سئل عن غيره لم يحل فيه ولم يمرّ! ولكنّ عالمنا بالكوفة الكسائىّ لو سئل عن كلّ هذا لأجاب.
قال الكسائىّ: صلّيت بهارون الرشيد فأعجبتنى قراءتى، فغلطت فى آية ما أخطأ فيها صبىّ قط؛ أردت أن أقول: (لَعَلَّهُمْ يَرْجِعُونَ)
«٧»، فقلت: لعلهم «يرجعين». قال: فو الله ما اجترأ هارون أن يقول لى: أخطأت؛ ولكنه لما','2','262',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (665,'سلّمت قال لى: يا كسائىّ، أىّ لغة هذه؟ قلت: يا أمير المؤمنين، قد يعثر الجواد. فقال: أمّا هذا فنعم! قال خلف «١»: كان الكسائىّ إذا كان شعبان وضع له منبر فقرأ هو على الناس فى كلّ يوم نصف سبع «٢»؛ يختم ختمتين فى شعبان، وكنت أجلس أسفل المنبر، فقرأ يوما فى سورة الكهف: (أنا أكثر) «٣» [فنصب «أكثر» «٤»] فعلمت أنه قد وقع فيه، فلما فرغ أقبل الناس يسألونه عن العلة فى (أكثر) لم نصبه؟ فثرت «٥» فى وجوههم: إنه أراد فى فتحه أقلّ (إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مالًا)
«٦». فقال الكسائىّ (أَكْثَرُ)
، فمحوه من كتبهم. ثم قال لى: يا خلف، يكون أحد من بعدى يسلم من اللحن؟ قال: قلت: لا؛ إنما إذا لم تسلم منه أنت، فلم يسلم منه أحد بعدك، قرأت القرآن صغيرا، وأقرأت الناس كبيرا، وطلبت الآثار فيه والنحو.
وقال الفرّاء: سمعت الكسائىّ يقول: ربما سبقنى لسانى باللحن فلا يمكننى أن أردّه. أو كلاما نحو هذا.
واجتمع الكسائىّ واليزيدىّ «٧» عند الرشيد فحضرت صلاة يجهر فيها، فقدّموا الكسائىّ يصلّى، فأرتج عليه قوله: (قُلْ يا أَيُّهَا الْكافِرُونَ)
فلما سلّم قال اليزيدىّ:
قارئ أهل الكوفة يرتج عليه (قُلْ يا أَيُّهَا الْكافِرُونَ)
! فحضرت صلاة يجهر فيها فقدّموا اليزيدىّ فأرتج عليه فى سورة «الحمد» فلما سلّم قال:
احفظ لسانك لا تقول فتبتلى ... «إن البلاء موكّل بالمنطق» «٨»','2','263',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (666,'قال الفرّاء: قال لى قوم: ما اختلافك إلى الكسائىّ وأنت مثله فى العلم؟
فأعجبتنى نفسى فناظرته وزدت؛ فكأنى كنت طائرا أشرب من بحره.
قال خلف: أولمت وليمة، فدعوت الكسائىّ واليزيدىّ، فقال اليزيدىّ للكسائىّ: يا أبا الحسن، أمور تبلغنا وحكايات تتصل بنا، ننكر بعضها. فقال الكسائىّ: أمثلى يخاطب بهذا! وهل مع العالم من العربية إلا فضل بصاقى هذا! ثم بصق. فسكت اليزيدىّ.
قال أبو بكر الأنبارىّ: اجتمعت للكسائىّ أمور لم تجتمع لغيره؛ فكان واحد الناس فى القرآن يكثرون الأخذ عنه؛ حتى لا يضبط الأخذ عليهم. فيجمعهم ويجلس على كرسى، ويتلو القرآن من أوّله إلى آخره وهم يسمعون؛ حتى كان بعضهم ينقط المصاحف على قراءته، وآخرون يتبعون مقاطعه ومبادئه فيرسمونها فى ألواحهم وكتبهم. وكان من أعلم الناس بالنحو وواحدهم فى الغريب.
قال الكسائىّ: بعد ما قرأت القرآن على الناس رأيت النبىّ صلى الله عليه وسلم فى المنام، فقال لى: أنت الكسائىّ؟ قلت: نعم يا رسول الله. قال: علىّ ابن حمزة؟ قلت: نعم يا رسول الله. قال: الذى أقرأت أمتى بالأمس القرآن؟
قلت: نعم يا رسول الله. قال: فاقرأ علىّ، قال: فلم يتأتّ على لسانى إلا:
(وَالصَّافَّاتِ)
، فقرأت عليه: (وَالصَّافَّاتِ صَفًّا فَالزَّاجِراتِ زَجْراً فَالتَّالِياتِ ذِكْراً).
فقال: أحسنت، ولا تقل (وَالصَّافَّاتِ صَفًّا)
نهانى عن الإدغام، ثم قال لى:
اقرأ، فقرأت حتى انتهيت إلى قوله تعالى: (فَأَقْبَلُوا إِلَيْهِ يَزِفُّونَ)
فقال: أحسنت ولا تقل (يَزِفُّونَ)
«١» ثم قال: فلأباهيّن بك- شكّ الكسائىّ- القرّاء أو الملائكة.','2','264',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (667,'واجتاز الكسائىّ بحلقة يونس بالبصرة- وكان شخص مع المهدىّ إليها- فاستند إلى أسطوانة تقرب من حلقته، فعرف يونس مكانه، فقال: ما تقول فى قول الفرزدق «١»:
غداة أحلّت لابن أصرم طعنة ... حصين عبيطات «٢» السّدائف والخمر «٣»
على أىّ شئ رفع «الخمر»؟ فأجاب الكسائىّ. فقال يونس: أشهد أن الذين رأسوا رأّسوك باستحقاق.
وقال القعقاع المقرئ: كنت عند الكسائىّ، فأتاه أعرابىّ فقال: أنت الكسائىّ؟ قال: نعم، قال (كَوْكَبٌ)
«٤» ماذا؟ قال: (دُرِّيٌّ)
، و (درّىّ) و (درّئ). فالدّرىّ يشبّه بالدّرّ، والدّرىّ جار، والدّرّئ «٥» يلمع، قال:
ما فى العرب أعلم منك.
قال أبو عمر الدّورىّ «٦»: قرأت هذا الكتاب- معانى الكسائىّ- فى مسجد السوّاقين ببغداذ على أبى مسحل وعلى الطّوال وعلى سلمة وجماعة، فقال أبو مسحل: لو قرئ هذا الكتاب عشر مرات لاحتاج من يقرؤه أن يقرأه.','2','265',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (668,'قال الفرّاء: لقيت الكسائىّ يوما فرأيته كالباكى، فقلت له: ما يبكيك؟
فقال: هذا الملك يحيى «١» بن خالد، يوجّه إلىّ فيحضرنى، فيسألنى عن الشئ؛ فإن أبطأت فى الجواب لحقنى منه عتب، وإن بادرت لم آمن الزلل. قال:
فقلت له ممتحنا: يا أبا الحسن، من يعترض عليك! قل ما شئت، فأنت الكسائى.
فأخذ لسانه بيده وقال: قطعه الله إذا إن قلت ما لا أعلم! قال أبو عمر الدّورىّ: لم يغيّر الكسائىّ شيئا من حاله مع السلطان إلا لباسه قال: فرآه بعض علماء الكوفيين وعليه جربّانات «٢» عظام، فقال له: يا أبا الحسن، ما هذا الزّىّ؟ فقال: أدب من أدب السلطان، لا يثلم دينا، ولا يدخل فى بدعة، ولا يخرج عن سنّة.
وذكر ابن أبى طاهر أنّ الكسائىّ النحوىّ كتب إلى الرشيد بهذه الأبيات،- وهو يؤدّب ولده محمدا- واحتاج إلى التزويج:
قل للخليفة ما تقول لمن ... أمسى إليك بحرمة يدلى
ما زلت مذ صار الأمين معى ... عبدى يدى ومطيّتى رجلى
وعلى فراشى من ينّبهنى ... من نومتى وقيامه قبلى
أسعى برجل منه ثالثة ... موقورة منّى بلا رحل
وإذا ركبت أكون مرتدفا ... قدام سرجى راكبا مثلى
فامنن علىّ بما يسكّنه ... عنى وأهد الغمد للنّصل
فأمر له الرشيد بعشرة آلاف درهم وجارية حسناء بآلتها وخادم معه برذون بسرجه ولجامه.','2','266',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (669,'قلت: وهذا من الكسائىّ قبيح من وجوه: أحدها: «يدلى» لفظة قبيحة ولا سيّما فى هذه الحالة التى تعرّض لوصفها، ثم كونه ناط هذا الأمر بكون الأمين معه تغفّل، وقبيح معناه المفهوم منه: إذا رأى الأمين تحرّكت جوارحه؛ وهذا فى غاية الشّناعة. ووصف نفسه بالشّبق ردئ جدّا لمن يروم التعليم أو مقابلة الخليفة، ووصف كبر قمدّه وشدّة انتصابه أردأ وأقبح، ثم سؤاله عمّن يسكّنه عنه؛ إنما يسأل مثل هذا العرّ من يقود العاهرات. فسبحان من أذهب رشده فى هذه الصورة «١»! ومن شعر الكسائىّ فى وصف النحو:
إنما النحو قياس يتّبع ... وبه فى كلّ أمر ينتفع
فإذا ما أبصر النحو الفتى ... مرّ فى المنطق مرّا فاتسع
فاتقاه كلّ من جالسه ... من جليس ناطق أو مستمع
وإذا لم يبصر النحو الفتى ... هاب أن ينطق جبنا فانقطع
فتراه ينصب الرفع وما ... كان من نصب ومن خفض رفع
يقرأ القرآن لا يعرف ما ... صرّف الإعراب فيه وصنع
والذى يعرفه يقرؤه ... وإذا ما شكّ فى حرف رجع
ناظرا فيه وفى إعرابه ... فإذا ما عرف اللحن صدع
فهما فيه سواء عندكم ... ليست السّنّة منا كالبدع
كم وضيع رفع النحو وكم ... من شريف قد رأيناه وضع
قال الكسائىّ: وقفت على نجار فقلت: بكم هذان البابان؟ فقال:
بسلحتان، فحلفت ألّا أكلم عامّيا إلا بما يصلح.','2','267',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (670,'مات الكسائىّ- رحمه الله- فى صحبة الرشيد ببلد الرّىّ فى سنة ثمانين ومائة.
وقيل فى سنة ثلاث وثمانين ومائة. وفيها مات محمد بن الحسن «١». وقال ثعلب:
ماتا فى يوم واحد، ودفنهما الرشيد بقرية اسمها رنبويه «٢». وقال: اليوم دفنت الفقه والنحو؛ فرثاهما اليزيدىّ «٣» فقال فيهما:
تصرّمت الدنيا فليس خلود ... وما قد ترى من بهجة سيبيد
سيفنيك ما أفنى القرون التى مضت ... فكن مستعدا فالفناء عتيد
أسيت على قاضى القضاة محمد ... فأذريت دمعى والفؤاد عميد
وقلت إذا ما الخطب أشكل من لنا ... بإيضاحه يوما وأنت فقيد!
وأوجعنى موت الكسائىّ بعده ... وكادت بى الأرض الفضاء تميد
وأذهلنى عن كل عيش ولذّة ... وأرّق عينى والعيون هجود
هما عالمان أوديا وتخرّما ... وما لهما فى العالمين نديد
قال الفراء: لما صار الكسائى إلى رنبويه، وهو مع الرشيد فى سفره إلى خراسان اعتلّ فتمثّل «٤»:','2','268',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (671,'قدر أحلّك ذا النّجيل «١» وقد أرى ... - وأبىّ «٢» - مالك ذو النّجيل بدار «٣»
إلّا كداركما بذى بقر «٤» الحمى ... هيهات ذو بقر من المزدار «٥»
وبها مات. ويقال: بل مات بطوس هو ومحمد بن الحسن. ولما رجع الرشيد إلى العراق قال: خلفت الفقه والنحو برنبويه. وقيل: إنهما توفيا فى سنة تسع وثمانين، وبلغ عمره سبعين سنة.
قال أبو مسحل عبد الوهاب بن حريش: رأيت الكسائىّ فى النوم فقلت:
ما فعل الله بك؟ قال: غفر لى بالقرآن. قلت: ما فعل حمزة الزيات وسفيان الثورىّ؟ قال: فوقنا، ما نراهم إلا كالكوكب الدّرّى. قال محمد بن يحيى:
فلم يدع قراءته حيا ولا ميتا.
وحضر الكسائىّ حلقة يونس بالبصرة؛ فقال الكسائىّ ليونس: لم نصبت «حتّى» الفعل المستقبل؟ فقال له يونس: هذا حالها من يوم خلقت. فضحك منه الكسائىّ.
ولقى الرشيد الكسائىّ يوما فى بعض طرقه، فوقف عليه وسأله عن حاله فقال له الكسائىّ: لو لم أجتن من ثمرة الأدب إلا ما وهبه الله لى من وقوف أمير المؤمنين علىّ لكان كافيا.','2','269',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (672,'وذكر أن الكسائىّ والفراء لم يقولا شعرا قطّ. وكان الكسائىّ فصيح اللسان، يتكلّم ولا يخيّل إليه أن يعرب عبارته، وهو يعرب.
وذكر محمد بن إسحاق النديم الكسائى فقال:
«هو أبو الحسن على بن حمزة بن عبد الله بن عثمان- وقيل بهمن- بن فيروز. وقيل يكنى بأبى عبد الله. كوفىّ أخذ عن الرّؤاسىّ وعن جماعة. وقدم بغداد، فضمه الرشيد إلى ولديه المأمون والأمين «١»».
«ولما اشتدّت علة الكسائىّ بالرّى جعل الرشيد يدخل إليه يعوده دائما.
فسمعه يوما ينشد هذين البيتين:
قدر أحلّك ذا النّجيل وقد أرى ... - وأبيك- مالك ذو النّجيل بدار
إلا كداركم بذى بقر الحمى ... هيهات ذو بقر من المزدار
فقال «٢» الرشيد بعد خروجه: مات الكسائى والله. قيل: وكيف يا أمير المؤمنين؟
قال: لأنه حدّثنى أن أعرابيا كان ينزل عليه فاعتل، فتمثّل شعرا قد أنشده الآن، ومات عنده. قال: فمات «٣» الكسائىّ من يومه».
«وسمى الكسائىّ لأنه كان يحضر مجلس معاذ الهرّاء، والناس عليهم الحلل، وعليه كساء روذبارىّ».','2','270',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (673,'«وله من التصانيف والكتب: كتاب معانى القرآن «١». كتاب مختصر النحو. كتاب «القراءات». كتاب مقطوع القرآن وموصوله. كتاب اختلاف العدد. كتاب الهجاء. كتاب النوادر الأوسط. كتاب النوادر الكبير. كتاب هاءات الكناية «٢» فى القرآن. كتاب الحدود فى النحو. كتاب العدد «٣»».
ذكره المرزبانىّ فقال: «أبو الحسن على بن حمزة بن عبد الله بن بهمن ابن فيروز مولى بنى أسد. روى أنه قيل للكسائىّ: لم سميت الكسائىّ؟ قال:
لأنى أحرمت فى كساء».
قال محمد بن داود بن الجراح: «ورد علىّ بن حمزة الكسائىّ بغداذ، وأدّب محمد بن الرشيد. وهو إمام أهل الكوفة فى النحو وفى القراءة، وأستاذ الفراء وعلى ابن المبارك الأحمر».
وجمع الرشيد بينه وبين سيبويه البصرىّ فخطأه الكسائىّ وغلاماه «٤»، فأمر الرشيد بصرف سيبويه، ووصله بعشرة آلاف درهم. فلم يدخل البصرة، واستحيا مما وقع عليه، ومضى إلى فارس، فمات بها.
وقال الجاحظ: تعلّم الكسائىّ النحو بعد الكبر، فلم يمنعه ذلك من أن برع فيه. ولقى أعراب الحطمة «٥»، وكثر سماعه منهم، وقرأ القرآن وبرع فيه؛ حتى','2','271',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (674,'قوى عليه وعرف إعرابه، واختار حرفا «١» فقرأ به. وكتب فى النحو كتبا مفهومة حسنة الشرح. وكان أثيرا عند الخليفة؛ حتى أخرجه من طبقة المؤدّبين إلى طبقة الجلساء والمؤانسين.
وقال يحيى الفرّاء: مدحنى رجل من النحوييّن وقال لى: ما اختلافك إلى الكسائىّ فأنت أعلم منه، أو مثله فى العلم!. قال: فأعجبتنى نفسى، فناظرته وسألته؛ فكأننى كنت طائرا يغرف من البحر.
وقال ابن قادم: قلت للفراء: قد بقى فى نفسك شىء من النحو؟ قال:
أشياء كثيرة. قال: فمن تحب أن تلقى فيها؟ قال: كنت أحبّ لو بقى الكسائىّ- وكان قد مات- رحمه الله.
وكان أبو زيد سعيد بن أوس الأنصارىّ يقول: كان الكسائىّ إذا أخذ معى فى اللغة والشعر هوى، وإذا أخذ فى النحو علا.
وقال الأصمعىّ: أرسل إلىّ الكسائىّ بأبى نصر، وقال: لست أعرض لك فى الشعر والغريب والمعانى، فدعنى والنحو. فوجّهت إليه: ما كلمتك قطّ فى النحو إلا بحجة أصحابى، وقد تركت ذلك لك.
وقال إسحاق بن إبراهيم الموصلىّ: ما رأيت فى الصّنعة أحذق من أربعة:
الأصمعىّ بالشعر، والكسائىّ بالنحو، ومنصور زلزل بضرب العود، وبرصوما «٢»','2','272',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (675,'بالزمر. قيل له: وما بلغ من حذقهم؟ قال: كنت إذا رأيت كتاب إنسان منهم فى صناعته لم تنازعك نفسك إلى أن تكون فى تلك الصناعة على أكثر مما سمعت.
وقال الأخفش سعيد بن مسعدة: قدم الكسائىّ إلينا البصرة مرتين؛ كان فى الأولى كذا وكذا؛ فأما فى الثانية فلم يتعلّق عليه بشىء.
وقال أحمد بن الحارث الخزّاز: كان الكسائىّ ممّن وسم بالتعليم، واكتسب به مالا كثيرا، وكان سخيا جميل الأخلاق.
وقال أبو حاتم: سمعت الكسائىّ يقول: رأيت بالبادية أعرابيين؛ أحدهما أسود والآخر أحمر، فسألت الأسود فلم أجد عنده شيئا، وسألت الأحمر فكأنما يأخذ العلم من شاربه. فقال لى الأحمر: ما رأيت رجلا أعلم بكلمة إلى جنبها كلمة أشبه شىء بها، أبعد شىء منها منك. قال: فكتبت هذا الكلام عنه.
وروى الفرّاء عن الكسائىّ قال: كنت أسأل أعرابيا عن كلمة صواب، وأسأله عن كلمة خطأ يقارب لفظها؛ أمتحنه بذلك، فقال لى: ما رأيت رجلا يأتى بكلمة إلى جنبها كلمة، أشبه شىء بها، أبعد شىء منها منك.
وروى إبراهيم بن إسماعيل الكاتب قال: قال أبو زياد الكلابىّ: ما رأيت أحدا أوقع على كلمة إلى جنبها كلمة أقرب شىء بها أبعد شىء منها منك.
وروى سلمة «١» عن الفراء عن الكسائىّ: قال: كنت بالبادية، فرآنى أعرابىّ وأنا أكتب فقال لى: ما رأيت رجلا يكتب الكلمة ومعها أخرى تشبهها كأنها أختها أو أمها مثلك.
وروى سلمة عن الأخفش قال: كان الكسائىّ جاءنا البصرة، فسألنى أن أقرأ عليه، أو أقرئه كتاب سيبويه ففعلت. فوجه إلىّ خمسين دينارا وجبة وشى.','2','273',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (676,'وقال أبو زيد الأنصارىّ: قدم الكسائىّ البصرة، فأخذ عن أبى عمرو ابن العلاء وعن يونس بن حبيب وعيسى بن عمر علما كثيرا صحيحا، ثم خرج إلى بغداذ، وقد قدم أعراب الحطمة، «١» وأخذ عنهم شيئا فاسدا، فخلط هذا بذاك فأفسده.
ولما أتى أبا زيد موت الكسائىّ قال: يرحمه الله! مات بموته علم كثير.

<span data-type="title" id=toc-524>٤٥٧ - على بن الحضرمىّ النحوىّ [١]</span>
كان من سواحل إفريقية. فيه نباهة وفضل، وكان ربّما علّم فى بعض الأوقات. وكان بقربه رجل قد نظر فى النحو أيضا؛ فكانا يتراسلان بالمسائل فى النحو.
ومما كتب به إليه علىّ بن الحضرمىّ:
لما أتانى كتاب واضح حسن ... فى النحو منك أبا إسحاق قد صنعا
كيما تغلّطنى فيه وتفحمنى ... ولست فى النّحو ممّن يبتغى الشّنعا
أمسكت خوف مراء لست تحمله «٢» ... حلما ولم أك عنه ممسكا فزعا

<span data-type="title" id=toc-525>٤٥٨ - على بن الحارث البيارىّ الخراسانىّ [٢]</span>
ذكره الباخرزىّ وسجع له فقال «٣»: «عنده مفصّل الفضل ومجموعه، ومرئىّ الأدب ومسموعه، ومعدن العلم وينبوعه. والذى تشدّ إليه الرحال، وتزمّ «٤» نحوه الجمال، ويقصد محلّه القصّاد، وينثال على مناهله الروّاد».
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٠، وبغية الوعاة ٣٥٩، وطبقات الزبيدىّ ١٦٢.
[٢] ترجمته فى تلخيص ابن مكتوم ١٤٠، ودمية القصر ٣٠٢. والبيارىّ فى الأصل منسوب إلى بيار، وهى مدينة من أعمال قومس.','2','274',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (677,'«حدّثنى تلميذه أبو العباس محمد بن على البادغوسىّ «١» قال: كتب إليه الوزير الحسن المصعبىّ مهيبا به إلى جنابه، ليجنى من الأدب ألذّ الجنى به، فترفّع عن إجابته؛ إذ لم يكن قصد ذلك الباب من بابته، وصدّر كتاب المصعبىّ بهذه الأبيات:
قد تدبرت ما أشرت إليه ... وهو الخير لا غبار عليه
غير أن المشيب من برد المو ... ت وخيط الرقاب فى كفّيه
فلماذا تريد ما لم أرده ... فى شبابى ولم أحنّ إليه
قال: وأنشدنى أيضا له، قال: أنشدنيه لنفسه:
ماذا أقول لربّى حين يسألنى ... فيم ابتغيت حراما بعد سبعين
لا همّ إن طمعت نفسى فلا طعمت ... فيما ابتغت غير زقّوم وغسلين
من تصنيفه: كتاب شرح الحماسة. كتاب صناعة الشعر.

<span data-type="title" id=toc-526>٤٥٩ - على بن دبيس النحوىّ الموصلىّ الشيخ أبو الحسن [١]</span>
قرأ على ابن وحشىّ «٢»، وابن وحشىّ قرأ على أبى الفتح بن جنّىّ. تصدّر ببلده لإفادة هذا الشأن.
وله شعر، منه قوله فى قوّاد:
يسهّل كلّ ممتنع شديد ... ويأتى بالمراد على اقتصاد
فلو كلّفته تحصيل طيف ال ... خيال ضحى لزار بلا رقاد
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٠، وبغية الوعاة ٣٣٧، ومعجم الأدباء ٣: ٢١٨.','2','275',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (678,'ومن شعره:
ما ساعفتك بطيفها هند ... إلّا لكى يتضاعف الوجد
ومنها فى مدح سعد الدولة أخى شرف الدولة مسلم «١» بن قريش:
والوجد ينمى فى الفؤاد كما ... ينمى لسعد الدولة السّعد

<span data-type="title" id=toc-527>٤٦٠ - على بن سليمان بن الفضل أبو الحسن الأخفش الصغير النحوىّ [١]</span>
سمع أبوى العباس ثعلبا، والمبرّد، وفضلا اليزيدىّ، وأبا العيناء الضرير. «٢» روى عنه علىّ بن هارون القرميسينىّ، وأبو عبيد الله المرزبانى، والمعافى بن زكريا الجريرىّ. وكان ثقة.
توفى أبو الحسن علىّ بن سليمان الأخفش فى ذى القعدة سنة خمس عشرة وثلاثمائة.
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٣، والأنساب ٢١ ب- ٢٢ ا، وبغية الوعاة ٢٣٨، وتاريخ الإسلام للذهبى (وفيات سنة ٣١٥)، وتاريخ بغداد ١٢: ٤٣٣، وتاريخ ابن عساكر ٢٩: ١٨٨ - ١٨٩، وتاريخ ابن كثير ١١: ١٥٧، وتلخيص ابن مكتوم ١٤٠، وابن خلكان ١: ٣٣٢ - ٣٣٤، وشذرات الذهب ٢: ٢٧٠، وطبقات الزبيدىّ ٨٤ - ٨٥، وطبقات ابن قاضى شهبة ٢: ١٥٦ - ١٥٧، والفلاكة والمفلوكين ٦٥، والفهرست ٨٣، وكشف الظنون ١٤٢٧، واللباب فى الأنساب ١: ٢٦ - ٢٧، ومرآة الجنان ٢: ٢٦٧ - ٢٦٨، ومعجم الأدباء ١٣: ٢٤٦ - ٢٥٧، والمنتظم (وفيات سنة ٣١٥)، والنجوم الزاهرة ٣: ٢١٩، ونزهة الألباء ٣١٢ - ٣١٣. وانظر حواشى ص ٣٦ من هذا الجزء.','2','276',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (679,'قال الأخفش: طلب إبراهيم «١» بن المدبّر من المبرّد محمد بن يزيد جليسا يجمع له بين تأديب ولده وإمتاعه بمؤانسته، فندبنى المبرّد لذاك، وكتب إليه معى كتابا:
قد أنفذت إليك- أعزك الله-[فلانا] «٢»، وجملة أمره كما قال الشاعر:
إذا زرت الملوك فإنّ حسبى ... شفيعا عندهم أن يخبرونى
وكان علىّ بن سليمان يتعرّض لابن الرومى الشاعر، ويباكر داره، ويقول عند بابه كلاما يتطيّر به فلا يخرج- وكان كثير التطير- فهجاه ابن الرومىّ بأهاج هى مثبتة فى ديوانه «٣». وكان علىّ بن سليمان الأخفش يتحفّظها ويوردها فى جملة ما يرويه استحسانا لها، وافتخارا بأنه نوّه بذكره إذ هجاه. ولما علم ابن الرومىّ ذلك أقصر عنه.
وقدم الأخفش مصر سنة سبع وثمانين ومائتين، وخرج منها سنة ست وثلاثمائة إلى حلب مع على بن أحمد بن بسطام صاحب الخراج، ولم يعد إلى مصر.
وتوفّى ببغداذ سنة خمس عشرة وثلاثمائة. وقيل سنة ست عشرة وثلاثمائة، وهو ابن [ثمانين سنة] «٤»، ودفن فى مقبرة قنطرة البردان.
وذكر هلال بن المحسّن «٥» فى كتابه، قال: «حكى أبو الحسن ثابت بن سنان قال: كان أبو الحسن على بن سليمان الأخفش يواصل المقام عند [أبى] «٦» على','2','277',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (680,'ابن مقلة «١»، ويراعيه أبو على ويبرّه، فشكا إليه فى بعض الأيام ما هو فيه من شدّة الفاقة، وزيادة الإضاقة، وسأله أن يكلّم أبا الحسن على بن عيسى- وهو يومئذ وزير- فى أمره، ويسأله إقرار رزق عليه فى جملة من يرتزق من أمثاله، فخاطبه أبو علىّ فى ذلك، وعرّفه اختلال حاله، وتعذّر القوت عليه فى أكثر أيامه، وسأله أن يجرى عليه رزقا برسم الفقهاء. فانتهره علىّ بن عيسى انتهارا شديدا، وأجابه جوابا غليظا- وكان ذلك فى مجلس حافل ومجمع كامل- فشقّ على أبى علىّ ما عامله [به]، وقام من مجلسه، وقد اسودّت الدنيا فى عينه، وصار إلى منزله لائما نفسه على سؤاله علىّ بن عيسى ما سأله، وحلف أن يتجرّد فى السعى عليه. ووقّف الأخفش على الصّورة، فاغتم بها، وانتهت به الحال إلى أكل السّلجم «٢» النّىء. فقيل إنه قبض على فؤاده: قلبه، فمات فجأة- رحمه الله- وكان موته فى شعبان سنة خمس عشرة وثلاثمائة».
وذكره المرزبانىّ فقال: «لم يكن بالمتّسع فى الرواية للأخبار والعلم بالنحو وما علمته صنّف «٣» شيئا البتة، ولا قال شعرا. وكان إذا سئل عن مسئلة فى النحو ضجر وانتهر من يواصل مساءلته. وشاهدته يوما وصار إليه رجل من أهل حلوان كان يكرمه، فحين رآه قال له:
حيّاك ربك أيها الحلوانى ... وكفاك ما يأتى من الأزمان
ثم التفت إلينا، وما يحسن من الشعر إلا هذا وما جرى مجراه.
ودفع كتابا له إلى بعض من فى مجلسه عليه اسمه، فقال له: أبو الحسن خفش خفش.','2','278',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (681,'<span data-type="title" id=toc-528>٤٦١ - على بن سعيد بن عثمان بن جار الخير بن دبابا [١] السّنجارىّ</span>
بسم الله الرحمن الرحيم. كتبت فى هذه الكراسة ما وجدته من شعر الشيخ أبى الحسن علىّ بن سعيد بن عثمان بن جار الخير- رضى الله عنهم أجمعين- وحصل لى ذلك من أوراق من كتاب العين فى اللغة، وجدتها بخطه منذ زمان قديم.
فمن ذلك أبيات دالية، وهى:
لمن طلل أضحت قفارا معاهده ... أكاتمه برح «١» الهوى وأناشده
وقفت به صحبى طويلا أبثّه ... لفرط الأسى والشوق ما أنا واجده
فأعيا جوابا واحتملت وللهوى ... بقلبى ولوع ما يزال يعاوده
ولست بناس يا أميمة عهدكم ... ولا خائنا إن خان عهدا معاهده
ولا هائبا صرف الزّمان إذا سطت ... بأحداثه أحداثه وشدائده
وكيف أخاف الدهر من بعد ما غدا ... كفيلا بنجح السّعى والقصد ماجده
هو السّيّد المرجوّ والمورد الذى ... بوادره محمودة وموارده
جواد إذا حدّثت عنه بمدحة ... تضافرنى فيما أقول حواسده
سحاب على الأعداء فيه صواعق ... وبحر نوال ما تغبّ «٢» عوائده
فتى شرفت همّاته وسما به ... إلى ذروة المجد الأصيل محامده
تعالى علوّا إن يساجل فما يرى ... له فى الّذى يأتيه ندّ يعانده
أنار سبيل العرف بعد ظلامه ... وقام به بعد التّثاقل قاعده
وأضحى به شمل الرّعية بعد ما ... تشعّب «٣» مجموعا وأرست «٤» قواعده
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤١.','2','279',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (682,'جرىء على الخصم الجرىء مساعد ... إذا قصرت عن نصرة الكفّ ساعده
بصير بوجه القصد والأمر مظلم ... إذا خفيت يوما عليك مقاصده
له شيمة تعلو على كلّ شيمة ... ومورد جود لا يخيّب وارده
إليك ابن منصور زجرت ركائبى ... وقد شردت بى عن مسيرى شوارده
وما خاب مسعى من غدوت رجاءه ... ولا ضلّ ركب أمّ بابك قاصده
وله رحمة الله عليه:
نمّت دموعى بما أكاتمه ... وعاد جسمى لبينهم سقم
وظلت فى الدّار بعد بعدهم ... ذا لوعة فى الفؤاد تضطرم
وعاذل بات فيك يعذلنى ... وبى عن العذل فى الهوى صمم
أذقته حرّ لومه فغدا ... ذا كبد ما تكاد تلتئم
يا جائرا فى هواه محتكما ... أسرفت فى الحكم أيّها الحكم
أطعت قول الواشى ولم أك فى ... حبّك ممن يطيع قولهم
فلا سقوا حيث حلّ سربهم ... ولا استهلّت «١» عليهم الدّيم»
ولا غدا بالنجاح طائرهم ... ولا سعى بالعلا لهم قدم
وله رضى الله عنه ورحمه:
أمغنى «٣» الهوى أصبحت مغنى النّوائب ... وقد كنت مغنى للحسان الكواعب
وأمسيت من بعد الأحبّة موحشا ... وكنت أنيسا فيك مسرى الحبائب
أبعد مشيب الرأس يعتادنى الصّبا ... وأمسى زميلا للخليع المصاحب!
وبعد خليلىّ اللذين تحمّلا ... أبيت قرير العين عذب المشارب!','2','280',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (683,'من مدحها:
وكنت قبيل الإلتقاء معظّما ... لما سار عنكم من جزيل المناقب
فلما تلاقينا رأيت مخبّرى ... لتقصيره فى الوصف فى زىّ كاذب
وله رحمة الله عليه:
روحى الفداء لزائر متفضّل ... شفعت مكارمه إليه فأنعما
سمحت به نفس الزّمان وطالما ... بخلت علىّ بأن أراه مسلّما
فطفقت أحمده وأشكر سعيه ... وأعدّ زورته الحميدة مغنما
وعلمت أن الدهر يعقب شهده ... فى الكأس من بعد الحلاوة علقما
أين الّذين علوا على أحداثه ... وتوقّلوا «١» خوف المنيّة سلّما
أخنى بكلكله فأفنى جمعهم ... وغدا مشيد بنائهم متهدّما
فاعمل لنفسك إن قدرت ولا تكن ... فرقا وكن حيث الأمور مسلّما
سمعت من أخوال الشيخ أبى الحسن على بن سعيد بن دبابا- رحمه الله- أنه كان رجلا متدينا كثير العقل. فمن دينه أنه سمع إنسانا يفضله على بعض نحاة سنجار «٢»، وهو عبد الصمد بن عيسون، فلما حضر للقراءة عليه أقسم أنه لا يقرئه وهو على هذه الحالة فى تفضيله والمغالاة فيه.
وأيضا فإنه كان يتّجر ويختلف إلى دمشق، فباع فى بعض سفراته على نوّاب أسد الدين شيركوه «٣» متاعا، غلط أصحابه بمائتى دينار صوريّة. فعمل حسابه فوجد الغلط، فحمل الذهب إليهم، فجزوه خيرا وشكروه.','2','281',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (684,'وطلبه عماد الدين صاحب سنجار- رحمه الله- وألحّ فى طلبه، فلم يمض إليه، وقال: هو يرانى بعين، وأخشى أن أنقص من عينه إذا اجتمعنا.
وتحيّل مجاهد الدين النائب بسنجار عليه فى الاجتماع به؛ حتى كان يقدم فى الوقت يوم الجمعة لأجل الاجتماع به.
وروى أنه اجتاز بسوق سنجار، فرآه بعض البغداذيين، وقال: من هذا الرجل؟ فقيل: هذا يقال له علىّ بن دبابا، فقال: يحتاج هذا الرجل إلى قلّة عقل.
وذكر أن رجلا من أهل سنجار يقال له ابن جبلة، خرج من مقصورة من مقاصير جامع سنجار يوم جمعة إلى صحن الجامع، فقال: سبحان الله! ما فى هذا الجمع مسلم! ثم نظر فإذا الشيخ أبو الحسن، فقال: ما كان لى أن أقول هذا والشيخ أبو الحسن حىّ.
وكان يناظر مع الفقهاء فيجيد بقوته فى علوم الفقه على وجه التسلّط. وكانت استراحته فى كلامه مثلا. وعلى الجملة، لأهل سنجار فيه من العقيدة ما لم يسمع لها مثال. وكان رجلا طويلا ضخما آدم اللون جهورىّ الصوت حسن التسميع، [ذا] نفس «١» كبيرة- رحمه الله.
وحكى أنّ مماته كان بريح الخمرة، فقال يوما: اليوم أشتمّ شيئا وأموت، فجاء الطبيب فقال: قوّوه، فشويت عنده شريحة لحم، فشمّها. وتوفى- رحمه الله- فى حدود سنة ستين وخمسمائة تقريبا.','2','282',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (685,'وكان يحضر مع جماعة من معتبرى سنجار فى وادى سنجار، على بيت بجانب بستان لرجل يقال له ابن الخردل، فاطلع عليه ابن الخردل يوما فقال: قد حضرنى شىء؛ أقوله أم لا؟ فقال له الشيخ: قل، فقال:
يا علىّ بن دبابا ... ليس ذا شيئا صوابا
تأخذ الدّر من البح ... ر وتلقيه الخرابا
هذا الشيخ- رحمه الله- كان نحويا بمدينة سنجار، يفيد النّحو بغير أجر.
وكان نزه النفس، مشتغلا بأمره، يرتزق من صنعة يده، ويصبر على المأكل الخشن والملبس المتوسط. وكان يصنع الجفنات «١» الحمر بيده ويرتزق بها. وكان ذا دين ومروءة.
قرأ النحو ببلده على البر «٢» النحوىّ القرقيسىّ نزيل سنجار، وعلى أبى جحش السنجارىّ النحوىّ. ولم يزل ببلده يفيدها إلى أن مات- رحمه الله.

<span data-type="title" id=toc-529>٤٦٢ - علىّ بن طاهر بن جعفر بن عبد الله أبو الحسن القيسىّ السّلمىّ النحوىّ الدّمشقىّ [١]</span>
سمع من مشايخ زمانه، وكان ثقة، وكانت له حلقة بجامع دمشق يفيد فيها العربية. ووقف فى موضع حلقته خزانة كتب له.
وسأله أبو محمد بن صابر عن مولده، فقال: فى سنة إحدى وثلاثين وأربعمائة.
وذكر ابن الأكفانى «٣» أن أبا الحسن [بن] طاهر النحوىّ توفى يوم الثلاثاء الحادى والعشرين من ربيع الأوّل سنة خمسمائة.
_________
[١] ترجمته فى بغية الوعاة ٣٣٩، وتلخيص ابن مكتوم ١٤٢، ومعجم الأدباء ١٣:
٢٥٧ - ٢٥٩.','2','283',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (686,'<span data-type="title" id=toc-530>٤٦٣ - على بن طاهر بن الرّقبانىّ أبو الفضل اللغوىّ الصّقلىّ [١]</span>
من أهلها المقيمين بها. حافظ للّغة وأيّام العرب، جامع لأدوات الأدب.
فمن شعره يمدح الأمير صمصام الدولة، وقد وصلت إليه ألقاب كثيرة، وخلع شريفة من مصر:
من قبل ذى الألقاب كنت شريفا ... إذ لم تزدك بكثرة تعريفا
لكنها عذبت فنحن بذكرها ... نرتاح لو كانت تعدّ ألوفا
يا سيّد الأملاك والعلم الذى ... ترك القوىّ من العصاة ضعيفا
لا زلت مسعودا وجدّك صاعدا ... حتى ترى فوق النجوم منيفا

<span data-type="title" id=toc-531>٤٦٤ - على بن طلحة بن كردان النحوىّ أبو القاسم [٢]</span>
صاحب أبى علىّ الفارسىّ وعلىّ بن عيسى الرمّانى. قرأ عليهما كتاب سيبويه.
والواسطيّون يفضّلونه على ابن جنّى والرّبعىّ.
صنّف كتابا كبيرا فى إعراب القرآن؛ قال لى شيخنا أبو الفتح: كان يقارب خمسة عشر مجلدا، ثم بدا له فيه فغسله قبل موته.
مات سنة أربع وعشرين [وأربعمائة «١»]، وكان متنزها [متصوّنا] ركب إليه [فخر الملك] أبو غالب محمد بن علىّ بن خلف وزير بهاء الدولة وهو سلطان الوقت، وبذل له فلم يقبل. وكان قد جرت بينه وبين القاضى أبى تغلب أحمد بن عبيد الله العاقولىّ [صديق الوزير المغربىّ وخليفة السلطان والحكام على واسط فى وقته «٢»] خصومة- وكان معظما [مفخما] «٣» - فقال له ابن كردان: إن صلت علينا بمالك صلنا عليك بقناعتنا.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٢، والمكتبة الصقلية ٦٤٦. والرقبانى فى الأصل:
العظيم الرقبة.
[٢] هذه الترجمة مذكورة فى هامش الأصل ص ٥١٥، ولم يذكره ابن مكتوم فى التلخيص، وله ترجمة أخرى فى بغية الوعاة ٣٣٩، ومعجم الأدباء ١٣: ٢٥٩ - ٢٦٤.','2','284',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (687,'آخر من حدّث عنه أبو المعالى محمد بن عبد السلام بن شاندة.
ذكر ذلك كله خميس الحوزىّ جوابا للسّلفىّ.

<span data-type="title" id=toc-532>٤٦٥ - على بن عبد الله بن سنان التيمىّ الطّوسىّ اللغوىّ [١]</span>
من أصحاب أبى عبيد القاسم بن سلّام. وكان من أعلم أصحابه وأكثرهم أخذا عنه أبو الحسن. عالم راوية لأخبار القبائل وأشعار الفحول، ولقى مشايخ الكوفيين والبصريّين. وكان أكثر مجالسته وأخذه عن ابن الأعرابىّ. وله ولد سلك طريقته فى العلم والحفظ. وكان الطّوسىّ عدوّا لابن السكّيت، لأنهما أخذا عن نصران «١» الخراسانىّ. واختلفا فى كتبه بعد موته. ولم يكن له مصنّف «٢».

<span data-type="title" id=toc-533>٤٦٦ - على بن عبد الله بن محمد بن عبد الباقى بن أبى جرادة العقيلىّ أبو الحسن [٢]</span>
شيخ العلماء فى وقته بحلب. له خطّ حسن ويد فى الحساب والهندسة على ما شاهدته بخطّه. وكان يميل إلى علم الأوائل، ويكتب منه الكثير، ولم يكن من أهل العربية على التحقيق؛ وإنما ذكرته هاهنا لأنه تعرّض إلى غريب الحديث لأبى عبيد القاسم بن سلّام- رضى الله عنه- فقفّاه على حروف.
_________
[١] ترجمته فى بغية الوعاة ٣٤٠، وتلخيص ابن مكتوم ١٤٢، وطبقات الزبيدىّ ١٤٤، والفهرست ٨١، ومعجم الأدباء ١٣: ٢٦٨ - ٢٧١، ونزهة الألباء ٢٤١ - ٢٤٢.
[٢] ترجمته فى تلخيص ابن مكتوم ١٤٢ - ١٤٣، ومعجم الأدباء ١٤: ٥ - ٨.','2','285',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (688,'فشارك بهذا التّصنيف أهل اللّغة، فذكرته فى هذا المصنف، وملكت هذا التّصنيف وفيه ما فيه.
وكان جدّه المدعوّ بأبى جرادة من أهل الفضل، وكان ورّاقا بحلب. ورأيت مجموعا على سبيل التذكرة لابن خالويه بخطّه. وقد كتب فيه نسخة «١» كتاب منه [إلى] «٢» الخالديّين «٣» [يسألهما انتساخ كتابه المبتدأ «٤»] فى النحو يقول فيه: وقد كنت عند إملائى كتاب المبتدأ فى النحو لم أحصّل به نسخة وعند كما نسخة منه فأسألكما انتساخها؛ وليكن الناسخ لها أبو جرادة الورّاق الحلبىّ؛ فإنّ خطّه حسن صحيح، وكذلك ضبطه، وكان حاضر الإملاء.
وكان أبو الحسن هذا- رحمه الله- محبّا للعلوم، جامعا للكتب الحسان.
وسألت عنه ابن الحرّانى نحوىّ بلده، فقال: لم يكن عالما بالنحو. وكان علمه بغير العربية أبلغ من علمه بها. ثم قال لى: رأيت شهادته فى بعض الكتب، وقد قال فيها: أشهدنى الموقف «٥» على نفسه. وسمع من مشايخ بلده المقيمين بها، والقادمين عليها. ورحل إلى بغداد حاجا، فسمع بها وبطريقها. وكان مولده فى المحرّم سنة إحدى وستين وأربعمائة بحلب.','2','286',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (689,'وله شعر قليل، منه:
يا ظباء البان «١» قولا بيّنا ... من لنا منكم بظبى من لنا
مشبه البدر بعادا وسنا ... من نفى عن مقلتىّ الوسنا
فتكت ألحاظه فى مهجتى ... فتك بيض الهند أو سمر القنا
يصرع الأبطال فى نجدته ... إن رمى عن قوسه أو إن رنا
دان أهل الدّلّ والحسن له ... مثل ما دانت لمولانا الدّنا
ذكر أنه توفّى بحلب سنة ست وأربعين وخمسمائة. وقيل سنة ثمان وأربعين والله أعلم.

<span data-type="title" id=toc-534>٤٦٧ - على بن عبد الله أبو الحسن الآمدىّ النحوىّ الفقيه [١]</span>
نزيل خلاط. نحوىّ مجوّد، وفقيه مسدّد. أدركه بخلاط «٢» أبو طاهر السّلفىّ الأصبهانىّ، وأخذ عنه.
أنبأنا أبو طاهر السّلفىّ قال: «سمعت أبا الحسن على بن عبد الله الآمدىّ النحوىّ الفقيه بثغر خلاط فى مجلس القاضى أبى معاذ يقول: «لم يذكر الشيخ أبو إسحاق الشيرازىّ «٣» فى المهذّب [فى المذهب «٤»] مسألة إلا بعد أن صلّى ركعتين
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٣، ومعجم السفر ١: ٢٠١. والآمدى بمد الألف وكسر الميم: منسوب إلى آمد، وهى مدينة من ديار بكر.','2','287',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (690,'واستخار الله تعالى فيها؛ كما فعل البخارىّ فى الصحيح. فلم ينكر ذلك أبو معاذ قاضى خلاط، وكان من أخلّاء أبى إسحاق وأئمّة تلامذته».

<span data-type="title" id=toc-535>٤٦٨ - علىّ بن عبيد الله بن عبد الغفار أبو الحسن اللّغوى السّمسمانىّ [١]</span>
سمع أبا بكر بن شاذان وأبا الفضل بن المأمون. وكان صدوقا، صاحب خطّ متقن فى الصّحة، مرغوب فيه لتحقيقه. كتب الكثير، وتصدّر ببغداذ للرواية، وأقرأ الأدب. وأكثر كتبه بخطّه. حصلت عند ابن دينار الواسطىّ الأديب وأدركها عرق ففسد أكثرها. وكان صدوقا ثقة فى الرواية- رحمه الله.
توفى يوم الأربعاء لأربع خلون من المحرم سنة خمس عشرة وأربعمائة.

<span data-type="title" id=toc-536>٤٦٩ - على بن عبد الرحمن بن محمد بن مهدىّ بن عمران التنوخىّ الإشبيلىّ النحوىّ اللغوىّ أبو الحسن المعروف بابن الأخضر [٢]</span>
كان من أهل اللغة والأدب والعربية، حافظا لذلك مقدّما. روى ذلك عن أبى الحجاج يوسف بن سليمان الأعلم- وعليه عوّل- وعن أبى علىّ القالى «١» وغيرهما.
_________
[١] ترجمته فى بغية الوعاة ٣٤٣، وتاريخ بغداد ١٢: ١٠، وتلخيص ابن مكتوم ١٤٣، وابن خلكان ١: ٣٣٦، وطبقات ابن قاضى شهبة ٢: ١٥٨، ١٦٧، ومعجم الأدباء ١٤:
٥٨ - ٦١. والسمسمانى، قال ابن خلكان: «ولا أعرف نسبته إلى ماذا هى، وهى بكسر السينين المهملتين، وسكون الميم الأولى وفتح الثانية وبالنون، ثم وجدت فى درّة الغواص للحريرى ما مثاله:
ويقولون فى النسبة إلى الفاكهة والباقلاء والسمسم فاكهانى وباقلانى وسمسمانى، فيخطئون فيه- وبين وجه الخطأ- ثم قال بعد ذلك: ووجه الكلام أن يقال فى المنسوب إلى السمسم سمسمى. وتمم الكلام إلى آخره. فلما وقفت على هذا علمت أن نسبة أبى الحسن المذكور إلى السمسم، وأنه استعمل على اصطلاح الناس».
[٢] ترجمته فى تلخيص ابن مكتوم ١٢٨، وبغية الوعاة ٣٤١، والصلة لابن بشكوال ١:
٤١٨، وهو مكرر ٤٣٦.','2','288',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (691,'وأخذ عنه جماعة الطلبة فى زمانه وأثنوا عليه، ووصفوه بالمعرفة واليقظة والدّين والفضل.
توفّى يوم الخميس سلخ سنة أربع عشرة وخمسمائة.

<span data-type="title" id=toc-537>٤٧٠ - على بن عبد الرحمن بن هارون بن عيسى بن هارون ابن الجراح الرئيس أبو الخطّاب المقرئ النحوىّ اللغوىّ [١]</span>
حسن الإقراء. وأخذ الناس عنه، وختم عليه الجمع الكثير ببغداذ. وكان يقول الشعر، ويفيد علم النحو واللغة. وسمع من مشايخ وقته، كأبى القاسم عبد الملك ابن محمد بن بشران الواعظ «١»، وأبى بكر محمد بن عمر بن بكر النجار «٢»، وغيرهما.
روى عنه عبد الوهاب بن المبارك الأنماطىّ وطبقته. اجتمع هو وأبو إسحاق الشيرازىّ، وأتا [هـ] بثلجيّة فيها ماء بارد، فأنشأ الشيخ أبو إسحاق الشيرازىّ يقول:
ممتّع وهو فى الثلاج ... فكيف لو كان فى الزّجاج
فأجابه الرئيس أبو خطاب:
ماء صفا رقّة وطيبا ... ليس بملح ولا أجاج
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٢، وطبقات ابن قاضى شهبة ٢: ١٥٩ - ١٦٠، وطبقات القراء ١: ٥٤٨ - ٥٤٩.','2','289',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (692,'سئل عن مولده فقال: فى سنة عشر وأربعمائة. ومات سحرة يوم الثلاثاء العشرين من ذى الحجّة سنة سبع وتسعين وأربعمائة. ودفن من يومه فى مقبرة باب برز، وصلّى عليه فى جامع القصر.

<span data-type="title" id=toc-538>٤٧١ - علىّ بن عبد الرحمن الصّقلىّ النحوىّ العروضىّ [١]</span>
نزيل الإسكندرية. عالم بعلمى النحو والعروض، قيّم بهما، بليغ فيهما، مشارك فى جميع الأنواع الأدبية، متصدّر لإفادة هذا النوع، وله شعر.
أنبأنا أبو طاهر السّلفىّ الأصبهانى فى إجازته العامة قال: «قال لى أبو الحسن على بن الحسن بن يوسف الدمراوىّ اللخمىّ بالإسكندرية: كنت أقرأ على أبى الحسن على بن عبد الرحمن الصّقلىّ العروضىّ النحوىّ، فعملت أبياتا وعرضتها عليه، فأضاف إليها بيتا واحدا. فالتى لى:
قالت سعاد وقد جدّ الوداع بنا ... ودمعها واكف ينهلّ كالبرد
كم من شجاع بلا سيف ولا ترس ... ومن جبان بآلات من العدد
ومن كريم بلا مال يجود به ... ومن لئيم كثير المال والصّفد «١»
والذى له:
جاد الزمان على هذا وضنّ على ... هذا فأصبح لا يخلو من الكمد
إن الأمور على الأقدار جارية ... وكل ذى أمل يسعى إلى أمد
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٤.','2','290',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (693,'<span data-type="title" id=toc-539>٤٧٢ - على بن عبد الرحيم بن الحسن السّلمىّ أبو الحسين ابن أبى الحسين المعروف بابن العصّار [١]</span>
الرّقىّ الأصل، البغداذى المولد والدار، المعروف بابن العصّار اللغوىّ. من ساكنى دار الخلافة المعظمة بالمطبق.
شيخ فاضل، له معرفة تامة باللغة [و] العربية. قرأ على أبى منصور بن الجواليقىّ، وعلى الشريف أبى السعادات بن الشجرىّ، ولازمهما حتى برع فى فنه، وأشير إليه فى ذلك. وسمع الحديث من مشايخ زمانه وروى عنهم. وأقرأ الناس زمانا، وتخرّج عليه فى الأدب جماعة، وسافر إلى مصر، واجتمع بها بأبى محمد بن برىّ النحوىّ والقاضى الموفق يوسف بن الخلال كاتب الإنشاء، وروى عنه.
وقال له ابن برّىّ: ما رأيت فى طريقك؟ قال: رأيت ما بين بغداذ ومصر سوادا. وكان يتّجر ويذكر بالبخل والإمساك، وكتب بخطه الكثير من كتب اللغة وشعر العرب. وقد كان يقع فى خطّه الغلط مع كثرة ضبطه واحترازه.
وقيل: إنه لم يكن ذكيّا، وإن النحو لم يتهيّأ له معرفة ما قرأ منه على الوجه. ورأيت فى خطّه وهما فى الإعراب يدلّ على ذلك. وكانت طريقته فى النسخ حسنة، والناس يتنافسون فى خطّه ويغالون به. وقد كان- رحمه الله- حريصا على الفوائد وطلبها، ويسطّرها على كتبه المنتسخة بخطّه.
وكانت ولادته فى سنة ثمان وخمسمائة. ومات- رحمه الله- فى يوم السبت بعد صلاة الظهر ثالث المحرّم سنة ست وسبعين وخمسمائة. وصلى عليه الخلق
_________
[١] ترجمته فى بغية الوعاة ٣٤١، وتلخيص ابن مكتوم ١٤٤ - ١٤٥، وشذرات الذهب ٥: ٢٥٧، ومرآة الجنان ٣: ٤٠٥، ومعجم الأدباء ١٤: ١٠ - ١١، وطبقات ابن قاضى شهبة ٢: ١٦٤ - ١٦٥، والعصار: منسوب إلى عصر الدهن.','2','291',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (694,'الكثير يوم الأحد رابع المحرم بجامع القصر، ومرة أخرى بالمدرسة النظاميّة. ودفن فى الجانب الغربىّ بمقبرة الشونيزىّ إلى جنب قبر أبيه «١».

<span data-type="title" id=toc-540>٤٧٣ - على بن عبد العزيز [١]</span>
صاحب أبى عبيد القاسم بن سلام، والراوى عنه كتبه هو وأخوه «٢». وأصلهم من باغ شور «٣» من خراسان.
ومات سنة سبع وثمانين ومائتين.

<span data-type="title" id=toc-541>٤٧٤ - على بن عبد الجبار بن سلامة بن عيذون الهذلىّ اللغوىّ التونسىّ المغربىّ [٢]</span>
من أهل تونس. إمام فى اللغة كامل فاضل حافظ لها؛ حتى لو قيل: لم يكن فى زمانه ألغى منه لما استبعد. وكانت له قدرة على نظم الشعر؛ خرج عن بلده
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٥، وطبقات الزّبيدىّ ١٤٤، وطبقات ابن قاضى شهبة ٢: ١٦٦، ومعجم الأدباء ١٤: ١١ - ١٤.
[٢] ترجمته فى بغية الوعاة ٣٤٠ - ٣٤١، وتلخيص ابن مكتوم ١٤٥، وطبقات ابن قاضى شهبة ٢: ١٥٨، ومعجم السفر ٢ - ٢٨٦ - ٢٨٧، ومعجم الأدباء ١٤ - ٨ - ١٠.
و «عيذون»، ضبطه ابن قاضى شهبة: «بفتح العين المهملة والمثناة من تحت، الساكنة، والذال المعجمة المضمومة»، وقال: «قيده كذلك ابن نقطة وغيره».','2','292',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (695,'إلى صقلّيّة، ولقى بها ابن رشيق الشاعر الفاضل متغرّبا عن القيروان، مقيما فى كنف ابن مدكود «١» بمدينة مازر «٢».
ورأى ابن البر «٣» اللغوىّ، ولم يأخذ عنه تعفّفا، لما كان عليه ابن البر من التخلّى والتبدّد فى أمر دينه؛ على ما ورد فى خبره. وأخذ عن أبى القاسم بن القطاع الصّقلّى، وقال: لم أرقط أحفظ للعربية واللغة منه.
وكان مولد على بن عبد الجبار بن سلامة بتونس يوم عيد النحر من سنة ثمان وعشرين وأربعمائة. وتوفى بالإسكندرية فى أواخر ذى الحجة سنة تسع عشرة وخمسمائة.
روى لنا عنه أبو طاهر السّلفىّ الأصبهانىّ نزيل الإسكندرية، ووصفه وذكره بالعلم فى اللغة والإتقان لها. وذكر عنه أن له قصيدة فى الرد على المرتدّ البغداذىّ- لعنه الله- فيها أحد عشر ألف بيت على قافية واحدة.

<span data-type="title" id=toc-542>٤٧٥ - على بن عمر بن أحمد بن عبد الباقى بن بكرىّ أبو الحسن [١]</span>
خازن دار الكتب بالمدرسة النظامية. من أهل باب الأزج. كانت له معرفة حسنة بالأدب. قرأ النحو على الشريف أبى السعادات بن الشجرىّ، واللغة [و] العربية على أبى منصور بن الجواليقىّ وغيرهما. وكان يكتب خطا جيدا. تولى الخزن سنين كثيرة. ورأيت بخطه أجزاء متعدّدة من كتاب الأزهرىّ، وفيها وهم وغلط. ولا شكّ فى موته قبل إتمامه ومقابلته.
وتوفّى يوم الثلاثاء عشرين من شهر رمضان سنة خمس وسبعين وخمسمائة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٤٥، وطبقات ابن قاضى شهبة ٢: ١٧٤.','2','293',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (696,'<span data-type="title" id=toc-543>٤٧٦ - علىّ بن عيسى بن على بن عبد الله أبو الحسن النحوىّ المعروف بالرمانىّ [١]</span>
حدث عن أبى بكر بن دريد وأبى بكر بن السراج. روى عنه التّنوخىّ «١» والجوهرىّ «٢» وهلال «٣» بن المحسّن الكاتب.
وكان من أهل المعرفة، مفتنا فى علوم كثيرة من الفقه والقرآن والنحو واللغة والكلام على مذهب المعتزلة. وله التصانيف المشهورة فى التفسير والنحو واللغة.
وكان مولده فى سنة ست وتسعين ومائتين. ومات- رحمه الله- فى ليلة الأحد الحادى عشر من جمادى الأولى سنة أربع وثمانين وثلاثمائة.
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٤، والأنساب ٢٥٨ ب، وبغية الوعاة ٣٤٤، وتاريخ ابن الأثير ٧: ١٦٦، وتاريخ بغداد ١٢: ١٦ - ١٧، وتاريخ أبى الفدا ٢: ١٢٩، وتاريخ ابن كثير ١١: ٣١٤، وتلخيص ابن مكتوم ١٤٥ - ١٤٦، وابن خلكان ١: ٣٣١ - ٣٣٢، وروضات الجنات، ٤٨٠ - ١٨١، وشذرات الذهب ٣: ١٠٩، وطبقات الزبيدى ٨٦، وطبقات بن قاضى شهبة ٢: ١٧٤ - ١٧٥، وطبقات المفسرين للداودى الورقة ١٧٦ ب- ١٧٧ ا، وطبقات المفسرين للسيوطىّ ٢٤، وعيون التواريخ (وفيات سنة ٣٨٤)، والفهرست ٦٣ - ٦٤، وكشف الظنون ١١١، ١٢٠، ١٣٩٧، ١٤٢٧، واللباب لابن الأثير ١: ٤٧٥، ومرآة الجنان ٢: ٤٢٠ - ٤٢١، ومسالك الأبصار ج ٤ مجلد ٣٠٢: ٣ - ٣٠٤، ومعجم الأدباء ١٤:
٧٣ - ٧٨، والمنتظم (وفيات سنة ٣٨٤)، والنجوم الزاهرة ٤: ١٦٨، ونزهة الألباء ٣٨٩ - ٣٩٢. قال ابن خلكان: «والرمانىّ، بضم الراء وتشديد الميم وبعد الألف نون، هذه النسبة يجوز أن تكون إلى الرمان وبيعة، ويمكن أن تكون إلى قصر الرمان، وهو قصر بواسط معروف. وقد نسب إلى هذا وهذا خلق كثيرون، ولم يذكر السمعانى أن نسبة أبى الحسن المذكور إلى أيهما. والله أعلم».','2','294',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (697,'ومن تصانيفه فى كل فن: كتاب شرح سيبويه كبير. كتاب شرح الأصول لأبى بكر بن السراج. كتاب شرح الموجز، له عدّة نسخ. كتاب شرح الجمل لابن السراج. كتاب التصريف. كتاب شرح الألف واللام للمازنىّ. كتاب الاشتقاق الكبير. كتاب الاشتقاق المستخرج.
كتاب شرح الهجاء لابن السراج. كتاب شرح المدخل للمبرّد. كتاب شرح المقتضب للمبرّد. كتاب الحروف. كتاب الألفات. كتاب الإيجاز فى النحو. كتاب شرح مختصر الجرمىّ. كتاب المبتدأ فى النحو. كتاب الخلاف بين النحويين. كتاب شرح مسائل الأخفش الكبير والصغير، مصنفان. كتاب الخلاف بين سيبويه والمبرّد. كتاب نكت سيبويه. كتاب أغراض سيبويه. كتاب المخزومات. كتاب التّصريف. كتاب الجامع فى علم القرآن. كتاب النّكت فى إعجاز القرآن.
كتاب شرح معانى الزجاج. كتاب المختصر فى علم السور القصار. كتاب المتشابه فى علم القرآن. كتاب جواب ابن الإخشيد فى علم القرآن. كتاب شرح الشكل والنقط لابن السراج. كتاب غريب القرآن. كتاب جواب مسائل طلحة فى علم القرآن «١». كتاب المسائل والجواب من كتاب سيبويه.
كتاب فى تهذيب أبواب كتاب سيبويه.
ومن كتب الكلام: كتاب صنعة الاستدلال، يشتمل على سبعة كتب.
كتاب نكت المعونة بالزيادات لابن الإخشيد «٢». كتاب شرح المعونة، لم يتم.
كتاب الأسماء والصفات لله عز وجل. كتاب ما يجوز على الأنبياء وما لا يجوز. كتاب الرويّة فى النقض على الأشعرىّ. كتاب نقض التثليث على يحيى بن عادى. كتاب تجانس الأفعال. كتاب استحقاق الذم.','2','295',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (698,'كتاب الإمامة. كتاب الرؤية. كتاب السؤال والجواب، غير الذى تقدّم. كتاب الأكوان. كتاب نقض استحقاق الذم فى الرد على أبى هاشم. كتاب تحريم المكاسب. كتاب الحظر والإباحة.
كتاب مسائل أحمد بن إبراهيم البصرىّ. كتاب مسائل ابن جابى. كتاب جوامع العلم فى التوحيد. كتاب صفات النفس. كتاب شرح الأسماء والصفات لأبى علىّ. كتاب الإرادة. كتاب نكت الإرادة. كتاب المعلوم والمجهول والنفى والإثبات. كتاب الأسباب. كتاب الحقيقة والمجاز. كتاب نقدات الاجتهاد. كتاب المجالس فى استحقاق الذم. كتاب مجالس ابن الناصر. كتاب مسائل أبى على بن الناصر فى علم القرآن. كتاب نكت الأصول. كتاب الأصلح الكبير. كتاب الأصلح الصغير. كتاب تهذيب الأصلح. كتاب المسائل والجواب فى الأصلح الواردة من مصر. كتاب المسائل فى اللطيف من الكلام. كتاب أدب الجدل. كتاب أصول الجدل. كتاب أصول الفقه. كتاب الرد على الدّهرية. كتاب المنطق.
كتاب الرسائل فى الكلام. كتاب القياس. كتاب مسائل أبى العلاء.
كتاب مبادئ العلوم. كتاب المباحث. كتاب المعرفة. كتاب صغير فى الصفات. كتاب العلوم. كتاب الأوامر. كتاب الأسماء والصفات.
كتاب العلل. كتاب العوض. كتاب أدلة التوحيد. كتاب التوبة.
كتاب مقالة المعتزلة. كتاب الأخبار والتمييز. كتاب تفضيل علىّ.
كتاب الردّ على من قال بالأحوال. كتاب الرد على المسائل البغداذيات لأبى هاشم. كتاب التعليق. كتاب فى الطبائع. كتب أماليه.
كان أصله من سرّ من رأى. ومولده ببغداذ سنة ست وتسعين ومائتين.
وكان أكثر ما يصنفه يؤخذ عنه إملاء.','2','296',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (699,'<span data-type="title" id=toc-544>٤٧٧ - على بن عيسى بن الفرج بن صالح أبو الحسن الرّبعىّ النحوىّ [١]</span>
صاحب أبى على الفارسىّ. بغداذىّ المنزل، شيرازىّ الأصل. درس ببغداذ الأدب على أبى سعيد السّيرافىّ، وخرج إلى شيراز، فدرس بها على أبى على الفارسىّ مدة طويلة، ثم عاد إلى بغداذ فلم يزل مقيما بها إلى آخر عمره.
قال على بن محمد بن الحسن المالكىّ: خرج على بن عيسى الرّبعىّ إلى فارس، وأقام على أبى علىّ النحوى عشرين سنة يدرس النحو. فقال أبو علىّ: ما بقى له شىء يحتاج أن يسأل عنه.
قال التّنوخىّ: سمعت من أبى زيد- وكان ابن أخت أبى على الفارسىّ النحوىّ- يقول: كان أبو علىّ يقول: قولوا لعلى البغداذىّ: لو سرت من الشرق إلى الغرب لم تجد أنحى منك.
كان مولد على بن عيسى سنة ثمان وعشرين وثلاثمائة. ومات فى ليلة السبت لعشر بقين من المحرم سنة عشرين وأربعمائة.
وتصانيفه: شرح مختصر الجرمىّ «١».
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٤ - ٣٥، وبغية الوعاة ٣٤٤ - ٣٤٥، وتاريخ بغداد ١٢: ١٧ - ١٨، وتاريخ ابن كثير ١٢: ٢٧، وتلخيص ابن مكتوم ١٤٦، وابن خلكان ١:
٣٤٣ - ٣٤٤، وروضات ٤٨٣، وشذرات الذهب ٣: ٢١٦، وطبقات ابن قاضى شهبة ٢: ١٧٥ - ١٧٧، وعيون التواريخ (وفيات سنة ٤٢٠)، والفلاكة والمفلوكين ١١٣ - ١١٤، وكشف الظنون ٢١٢، ومعجم الأدباء ١٤: ٧٨ - ٨٥، والنجوم الزاهرة ٤: ٢٧١.','2','297',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (700,'<span data-type="title" id=toc-545>٤٧٨ - على بن عساكر بن المرجّب بن العوّام أبو الحسن المقرئ النحوىّ الضرير [١]</span>
من أهل البطائح. والبطائح «١» ما بين واسط والبصرة. وكان نسبه فى عبد القيس، وولد بقرية تعرف بالمحمّدية، «٢» قريبة من الصّليق «٣» بالبطائح.
قدم بغداذ، وخفظ القرآن الكريم بالقراءات الكثيرة على أبى العز القلانسىّ الواسطىّ «٤»، وعلى البارع «٥» بن الدّبّاس، وعلى المزرقىّ «٦»، وسبط أبى منصور «٧» الخياط.
وسمع الحديث من مشايخ زمانه، وحدّث الكثير، وأجاز للإمام الناصر «٨» أحمد، وكانت له جماعة بجامع القصر، وأفاد الناس فى علوم القرآن والنحو، وكانت له معرفة حسنة.
_________
[١] ترجمته فى بغية الوعاة ٣٤٣، تاريخ الإسلام للذهبى (وفيات سنة ٥٧٢)، وتاريخ ابن كثير ١٢: ٢٩٦، وتلخيص ابن مكتوم ١٤٦، وشذرات الذهب ٤: ٢٤٢، وطبقات ابن قاضى شهبة ٢: ١٦٩، وطبقات القراء لابن الجزرىّ ١: ٥٥٦، ومعجم الأدباء ١٤: ٦١ - ٦٢، والمنتظم (وفيات سنة ٥٧٢)، والنجوم الزاهرة ٦: ٨٠، ونكت العميان ٢١٤ - ٢١٥.','2','298',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (701,'كان مولده فى سنة تسعين وأربعمائة، أو سنة تسع وثمانين. وتوفى ليلة الثلاثاء ثامن عشرين شعبان سنة اثنتين وسبعين وخمسمائة، ودفن فى باب حرب.

<span data-type="title" id=toc-546>٤٧٩ - على بن فضّال أبو الحسن المجاشعىّ [١]</span>
علىّ بن فضّال بن على بن غالب بن جابر بن عبد الرحمن بن محمد بن عمرو بن عيسى بن حسن بن زمعة بن هميم «١» بن غالب بن صعصعة بن ناجية بن عقال بن محمد ابن سفيان بن مجاشع القيروانىّ النحوىّ أبو الحسن المجاشعىّ.
هجر مسقط رأسه، ودوّخ الأرض، ذات الطول والعرض، مصر وشأما، وعراقا وعجما؛ حتى وصل إلى مدينة المشرق غزنة «٢»، فتقدّم بها، وأنعم عليه أماثلها، واختاروا عليه التّصانيف، وشرع فى ذلك، وصنّف لكل رئيس منهم ما اقتضاه، ثم انكفأ راجعا إلى العراق، وانخرط فى جماعة نظام الملك الحسن بن إسحاق الطوسىّ «٣» الوزير، ولم تطل أيامه بعد ذلك، حتى ناداه اللطيف الخبير فأجاب.
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٤ - ٣٥، وبغية الوعاة ٣٤٥، وتاريخ ابن كثير ١٢:
١٣٢، وتلخيص ابن مكتوم ١٤٦ - ١٤٨، وروضات الجنات ٤٨٥، وشذرات الذهب ٣:
٣٦٣، وطبقات ابن قاضى شهبة ٢: ١٧٧ - ١٧٨، وطبقات المفسرين للداودىّ الورقة ١٧٦ ب- ١٧٧ ا، وطبقات المفسرين للسيوطى ٢٤ - ٢٥، وكشف الظنون ١٠٢٧، ١١٧٩، ومرآة الجنان ٣: ١٣٢، والمستفاد من ذيل تاريخ بغداد ... ، ومعجم الأدباء ١٤: ٩٠ - ٩٨، والمنتظم (وفيات سنة ٤٧٩)، والنجوم الزاهرة (٥: ١٢٤). وفضال، ضبطه ابن قاضى شهبة بفتح الفاء وتشديد الضاد المعجمة.','2','299',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (702,'كان- رحمه الله- إماما فى النحو واللغة والتصريف والتفسير، موفّقا فى التصنيف. صنّف التواليف المفيدة.
صنف التفسير المسمى البرهان العميدىّ فى عشرين مجلدا، وصنف النّكت فى القرآن، وصنف كتابا فى شرح بسم الله الرحمن الرحيم.
وصنف فى النحو: إكسير الذهب فى صناعة الأدب، كبير فى عدّة مجلدات، وكتاب العوامل والهوامل فى النحو، وصنف الفصول فى معرفة الأصول، وكتاب الإشارة إلى تحسين العبارة، وشرح عنوان الإعراب، والمقدّمة فى النحو، وكتاب العروض، وشرح معانى الحروف، وغير ذلك من الكتب النحوية المحتوية على الفوائد.
وصنف فى التفسير كتابا آخر غير الأوّل سماه الإكسير فى علم التفسير خمسة وثلاثون مجلدا «١».
أقام- رحمه الله- ببغداذ مدّة، ودرس عليه النحو اللغة.
مات علىّ بن فضّال المجاشعىّ ببغداذ فى يوم الثلاثاء ثانى عشرين شهر ربيع الأوّل سنة تسع وسبعين وأربعمائة، ودفن فى مقبرة باب برز.
قال محمد بن طاهر المقدسىّ «٢»: سمعت إبراهيم بن عثمان، الأديب العربىّ بنيسابور يقول: لما دخل أبو الحسن على بن فضّال النحوىّ نيسابور اقترح عليه الأستاذ أبو المعالى الجوينىّ «٣» أن يصنف باسمه كتابا فى النحو، فصنفه وسماه','2','300',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (703,'الإكسير، ووعده بأن يدفع إليه ألف دينار، فلما صنفه وفرغ منه ابتدأ بقراءته عليه، فلما فرغ من القراءة انتظره أياما أن يدفع إليه ما وعده أو بعضه، فلم يدفع إليه شيئا، فأنفذ إليه يقول: إن لم تف بما وعدت، وإلا هجوتك. فأنفذ الأستاذ إليه رسالة على يد الرسول كتب فيها: «عرضى «١» فداك». ولم يدفع إليه حبة واحدة «٢».','2','301',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (704,'<span data-type="title" id=toc-547>٤٨٠ - على بن قاسم السّنجانىّ الخراسانىّ [١]</span>
وسنجان قصبة خواف «١». أبو الحسن صاحب مختصر العين، ومحله من الأدباء محل العين من الإنسان [والإنسان «٢»] من العين، وقد سهل طريقة اللغة على طالبيها، وأدنى قطوفها من متناوليها باختصاره، ولا تكاد ترى حجور المتأدّبين منه خالية؛ لا بل تراها أبدا به حالية.
وله شعر الزهاد، وقد جرى فيه «٣» على سمت العبّاد، ونسج فيه على منوال أولى الاجتهاد، فمنه قوله:
_________
[١] ترجمته فى الأنساب ٢١٠ ب، وبغية الوعاة ٣٤٦، وتلخيص ابن مكتوم ١٤٩ - ١٥٠، واللباب فى الأنساب ١: ٣٩٢، ومعجم الأدباء ١٤: ١٠٤ - ١٠٦، ومعجم البلدان ٣: ٤٨٠.','2','302',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (705,'خليلىّ قوما فاحملا لى رسالة ... وقولا لدنيانا التى تتصنع
عرفناك يا خدّاعة الخلق فاغربى ... ألسنا «١» نرى ما تصنعين ونسمع
فلا تتحلّى للعيون بزينة ... فإنّا متى ما تسفرى نتقنع
نغطى بثوب اليأس منك عيوننا ... إذا لاح يوما من مخازيك مطمع
وهل أنت إلا متعة مستعارة ... وهل طاب يوما بالعوارى تمتع
رتعنا وجلنا فى مراعيك كلّها ... فلم يهننا مما رعيناه مرتع
وأنت خلوب كالغمامة كلّما ... رجاها مرجّى الغيث ظلّت تقشّع
طلوع قبوع «٢» كالمغازلة التى ... تطلّع أحيانا وحينا تقبّع
وهذا لعمرى كلام لو دعى به الصخر لأجاب، ولو قرع به سمع عفريت لتاب وله أيضا يرثى نفسه:
دبت إلىّ بنات الأرض مسرعة ... حتى تمشّين فى قلبى وفى كبدى
قد وسّد الترب خدّى فهو مضطجعى ... وصار فيه مهادى أوعر المهد «٣»
والعين منّى فويق الخدّ سائلة ... وطالما كنت أحميها من الرّمد
وله أيضا:
عن قليل سرائر الخلق تفشو» ... فى مقام يشيب فيه الوليد
أىّ يوم هناك يومى إذا ما ... جمع الخلق موقف مشهود','2','303',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (706,'<span data-type="title" id=toc-548>٤٨١ - على بن قاسم بن يونش الإشبيلىّ المقرئ المعروف بابن الزقاق [١]</span>
قرأ القرآن على أبيه، وأخذ طرفا من العربية على شيوخ بلاده، وانتقل إلى الجزيرة، وخطب برأس عين «١» الخابور مدة. وسكن دمشق هو وأخ له، ثم انتقل إلى حلب، وأقام بها، وتصدّر بها لإقراء القرآن بجامعها برزق قرّر له. وابتاع له دارا بها واستوطنها، وأولد بها عقبا غير صالح. وكان عسر الخلق كثير الدعوى، بعيدا من الخير، شحيحا على جمع الدنيا، قليل الحياء فى ذلك، أغلف اللسان، يخطىء فيما يعانيه، ولا يرجع إذا ردّ عليه.
صنف فى النحو شرحا لكتاب الجمل للزجاجىّ فى أربع مجلدات كبار، ملكته بخطه. وله مفردات فى القراءات.
وكان أبوه قاسم من المقربين المذكورين فى قطره. أخبرنى أبو الخطاب بن دحية الكلبىّ قال: قاسم الزقاق، كان أبوه عبدا روميا لبعض أهل إشبيليّة اسمه يونش، وكان قد قرأ على شريح «٢» وصحبه المدة الطويلة، وكان شريح مجاب الدعوة، فدعا عليه يوما يبليه الله بالفقر والغربة، فاستجيبت دعوته. وذلك أنه كان يركب حمارا له، وينتقل من بلد إلى بلد من أرض الأندلس يقرئ الناس، لا يستقر
_________
[١] ترجمته فى بغية الوعاة ٣٤٦، وتلخيص ابن مكتوم ١٥٠، وطبقات ابن قاضى شهبة ٢:
١٨١ - ١٨٢، وكشف الظنون ٦٠٤. و «يونش»، ضبطه السيوطى بالشين المعجمة.','2','304',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (707,'ببقعة، ولم يزل فقيرا مدقعا. وقال: إنما سمى الزقاق لأنه كان سمينا كبير البطن، وكان الطلبة يسمونه زق الخراء، ثم أنفوا من التصريح بذلك، فدعوه بالزقاق، وصار علما له، ولم يزل علىّ ولده هذا على ما هو عليه من الإقراء بحلب؛ إلى أن حج فى حدود سنة خمس وستمائة، ومات عائدا بطريق مكة.

<span data-type="title" id=toc-549>٤٨٢ - علىّ بن محمد السّمسمانىّ الأديب البغداذىّ [١]</span>
كان فاضلا عالما متوفرا على إفادة علم الأدب، متصدرا ببغداذ. كتب بخطه الكثير، وكان فى غاية الضبط والإتقان. وله شهرة عند أهل هذا الشأن.
مات ببغداذ فيما ذكره هلال بن المحسّن فى يوم الأربعاء الخامس من المحرم سنة خمس عشرة وأربعمائة.

<span data-type="title" id=toc-550>٤٨٣ - على بن محمد بن الزّبير الأسدىّ المعروف بابن الكوفىّ النحوىّ اللغوىّ [٢]</span>
عالم، صحيح الخطّ، راوية، جماعة للكتب، صادق الرواية، منقّر بحاث.
من أصحاب أبى العباس ثعلب المختصّين به.
وكان أبوه من أهل ذوى اليسار من أهل الكوفة، واشتغل ولده هذا بطلب العلم من يومه. ولما مات أبوه خلّف له- فيما يقال- زائدا عن خمسين ألف دينار، فصرفها كلّها فى طلب العلم وتحصيل الكتب اشتراء واستنساخا وكتابة، وصرف
_________
[١] ترجمته فى بغية الوعاة ٣٤٣، وتاريخ بغداد ١٢: ١٠، وتلخيص ابن مكتوم ١٥١، وابن خلكان ١: ٣٣٦، وطبقات ابن قاضى شهبة ٢: ١٥٨، ١٦٧، ومعجم الأدباء ١٤:
٥٨ - ٦١. وانظر ترجمة أخرى له فى هذا الجزء ص ٢٨٨ وحواشيها.
[٢] ترجمته فى بغية الوعاة ٣٥٠، وتاريخ بغداد ١٢: ٨١، وتلخيص ابن مكتوم ١٥١، وشذرات الذهب ٢: ٣٧٩، والفهرست ٧٩، وكشف الظنون ١٧٢٩، ومعجم الأدباء ١٤:
١٥٣ - ١٥٦. وذكر الخطيب وياقوت والسيوطى وابن العماد أن وفاته كانت سنة ٣٤٨.','2','305',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (708,'من ذلك جزءا صالحا لفقراء طلبة العلم، وكان منزله مغشيا منهم، ونفقاته عليهم واسعة.
فأما كتبه ففى غاية الجودة والإتقان، والموجود منها فى زماننا هذا إذا تؤمّل دلّ على تيقظ وبحث ورغبة. وقد كانت لكثرتها يعيّن لكلّ نوع منها موضعا مخصوصا من خزائنه، ويكتبه على أوّل الكتاب ليجده إذا طلبه، ويعيده إلى موضعه المعلوم إذا غنى عنه- رحمه الله، فما كان أسنى فعاله! وشغله طلبه الفوائد عن التصنيف، فلم ير له إلا تصنيف واحد فى معانى الشعر واختلاف العلماء فى ذلك «١».

<span data-type="title" id=toc-551>٤٨٤ - على بن محمد السّعيدىّ الأستاذ الأديب أبو الحسن البيارىّ [١]</span>
رجل فاضل. من أهل بيت الفضل والأدب. وله شرح الحماسة، جميل، أحسن فيه غاية إمكانه.

<span data-type="title" id=toc-552>٤٨٥ - علىّ بن محمد بن علىّ أبو الحسن بن أبى زيد النحوىّ المعروف بالفصيحىّ [٢]</span>
من أهل أستراباذ، بلدة من أطراف خراسان. قرأ النحو على عبد القاهر الجرجانىّ، وبرع فيه حتى صار من أعرف أهل زمانه به.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٥٢، ومعجم الأدباء ١٥: ٥٨. والبيارىّ، بالكسر:
منسوب إلى بيار، وهى مدينة من أعمال قومس، خرج منها جماعة من العلماء.
[٢] ترجمته فى إشارة التعيين الورقة ٣٥، وبغية الوعاة ٣٥١ - ٣٥٢، وتلخيص ابن مكتوم ١٥٢، وابن خلكان ١: ٣٤٤، وطبقات ابن قاضى شهبة ٢: ١٨٧ - ١٨٨، ومعجم الأدباء ١٥: ٦٦ - ٧٥. قال ياقوت: «سمى الفصيحىّ لكثرة دراسته كتاب الفصيح لثعلب».','2','306',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (709,'قدم بغداذ، واستوطنها إلى أن توفى بها. ودرس النحو بالمدرسة النظامية مدّة، وأخذ عنه الناس، وتخرّج به جماعة. سمع منه أبو طاهر أحمد بن محمد بن سلفة الأصبهانىّ ببغداذ، وقال: جالسته وسألته عن أحرف من العربية.
توفى الفصيحىّ يوم الأربعاء ثالث عشر ذى الحجة من سنة ست عشرة وخمسمائة ببغداذ.
وكان- رحمه الله- يكتب خطا صحيحا، رأيت بخطه شرح الحماسة للبيارىّ، وهى فى غاية الجودة والصحة.

<span data-type="title" id=toc-553>٤٨٦ - علىّ بن محمد بن السّيد النحوىّ [١]</span>
من أهل بطليوس. أبو الحسن، ويعرف بالخيطال، وهو أخو الشيخ أبى محمد عبد الله بن السّيد البطليوسىّ «١».
روى عن أبى بكر بن الفرات. أخذ عنه أبو محمد كثيرا من كتب الأدب وغيرها.
وتوفى بقلعة رباح «٢» معتقلا من قبل ابن عكاشة قائدها سنة ثمان وثمانين وأربعمائة أو نحوها.
وكان مقدّما فى علم اللغة وحفظها والضبط لها.
_________
[١] ترجمته فى بغية الوعاة ٣٤٨، وتلخيص ابن مكتوم ١٥٢، والصلة لابن بشكوال ٢: ٤١٤ - ٤١٥، ومعجم الأدباء ١٥: ٥٦. والسيد، بكسر السين وسكون الياء، من أسماء الذئب، سمى به جدّه.','2','307',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (710,'<span data-type="title" id=toc-554>٤٨٧ - على بن محمد بن أحمد بن إسحاق بن البهلول بن حسان أبو الحسن التنوخىّ القاضى المعرىّ المقرئ الفقيه اللغوىّ النحوىّ [١]</span>
ولد علىّ أبو الحسن بن أبى طالب محمد بن أحمد بن إسحاق ببغداذ فى شوّال سنة إحدى وثلاثمائة، وتوفى بها فى شهر ربيع الأوّل سنة ثمان وخمسين وثلاثمائة.
وكان حافظا للقرآن، قرأ على أبى بكر بن مقسم بحرف حمزة «١»، ولقى أبا بكر بن مجاهد وقرأ عليه بعض القرآن، وسمع منه حديثا، وتفقّه على مذهب أبى حنيفة.
وحمل من النحو واللغة والأخبار والأشعار عن جدّه القاضى جعفر بن البهلول وعن أبى بكر بن الأنبارىّ ونفطويه والصولىّ وغيرهم.
وقال الشعر، وتقلد القضاء بالأنبار وهيت «٢» من قبل أبيه فى سنة عشرين وثلاثمائة أو قبلها، ثم تنقلت به الأحوال إلى أن تولى القضاء فى عدة أماكن «٣».

<span data-type="title" id=toc-555>٤٨٨ - على بن محمد بن إسماعيل بن محمد بن بشر أبو الحسن الأنطاكىّ المقرئ النحوىّ الفقيه [٢]</span>
قرأ القرآن بأنطاكيّة «٤» على أبى إسحاق إبراهيم بن عبد الرزاق المقرئ «٥»، ورحل إلى الأندلس، فأدخل إليها علما كثيرا من القراءات والرواية لحديث كثير عن الشاميين والبصريين. وكان بصيرا بالعربية والحساب، وله حظ من الفقه على مذهب الشافعىّ، قرأ الناس عليه بالأندلس، وكتبوا عنه، وسمعوا منه.
_________
[١] ترجمته فى تاريخ بغداد ١٢: ٨٢، وتلخيص ابن مكتوم ١٥٢، والجواهر المضية ١: ٣٦٩ - ٣٧٠. وما ذكره المؤلف يوافق ما فى تاريخ بغداد.
[٢] ترجمته فى تلخيص ابن مكتوم ١٥٣، وطبقات القراء لابن الجزرىّ ١: ٥٦٤ - ٥٦٥.','2','308',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (711,'وكان مولده بأنطاكيّة فى سنة تسع وسبعين ومائتين. وتوفى بقرطبة يوم الجمعة يوم تسعة وعشرين من ربيع الأوّل سنة سبع وسبعين وثلاثمائة، ودفن فى مقبرة الرّبض «١».

<span data-type="title" id=toc-556>٤٨٩ - على بن محمد الجزرىّ النحوىّ الأديب [١]</span>
نزيل باخرز «٢» من أعمال نيسابور. فاضل وقع من بعض أقطار الجزيرة إلى باخرز، وعلم فضله، فارتبطه أهلها للتأديب. وبقى بين كبرائها موفور النصيب.
وكان غاليا «٣» فى التشيّع، ومقت لذلك. فخرج عن باخرز، وقصد الشام ونزل دمشق، ولازم قبر معاوية بن سفيان، وهو فى القبّة الخضراء، وفى ذلك اليوم فيما قيل دكان لفقّاعىّ، فأقام ملازما للقبر مدّة ليزيل عنه اسم التشيع. ثم غلبه الطبع فلم يزل ينتهز الفرصة فى أن يخلو بالقبر. فلمّا خلا به فى بعض الأيام أسال عليه ميزابه، ونفض عليه عيابه، وألقى عليه جنينه، وخلط بذى بطنه طينه. وخرج عنه خائفا يترقب، قال: رب نجنى من القوم الظالمين «٤». وفى هذا المعنى يقول:
رأيت بنى الطوامث «٥» والزّوانى ... بمقت ينظرون إلىّ شزرا «٦»
لأنى بالشآم أقمت حولا ... على قبر ابن هند كنت أخرا
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٥٣، ودمية القصر ٥٢ - ٥٣.','2','309',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (712,'<span data-type="title" id=toc-557>٤٩٠ - على بن محمد بن إبراهيم بن عبد الله أبو الحسن الضّرير النحوىّ القهندزىّ النيسابورىّ [١]</span>
من أصحاب أبى عبد الله «١». شيخ فاضل من الأدباء، سمع الحديث الكثير، وسمع منه الناس، وقرأ عليه الأئمة، وتخرّجوا به.

<span data-type="title" id=toc-558>٤٩١ - على بن محمد بن عبد الله بن الهيثم بن بختيار بن خرزاد ابن سنين بن سينات بن الهيثم المعروف بأبى القاسم بن أبى جعفر الأديب الأصبهانىّ المدينىّ [٢]</span>
راوية لكتب اللغة. يروى كتب أبى عبيد القاسم بن سلام، سمعها من الطبرانىّ. ومات بأصبهان فى ذى القعدة سنة سبع وعشرين وأربعمائة.

<span data-type="title" id=toc-559>٤٩٢ - على بن محمد بن عبدوس الكوفىّ [٣]</span>
نحوىّ. له ذكر هناك، وصنف كتبا، منها كتاب ميزان الشعر بالعروض «٢».
كتاب البرهان فى علل النحو. كتاب معانى الشعر.
_________
[١] ترجمته فى بغية الوعاة ٣٤٦، وتلخيص ابن مكتوم ١٥٣ - ١٥٤، ومعجم الأدباء ١٥: ٥٧ - ٥٨، ونكت الهميان ٢١٥. والقهندزىّ، بضم القاف والهاء وسكون النون وضم الدال المهملة: منسوب إلى قهندز نيسابور. وقهندز: اسم الحصن أو القلعة فى وسط المدينة.
[٢] ترجمته فى تلخيص ابن مكتوم ١٥٤.
[٣] ترجمته فى بغية الوعاة ٣٥٠، وتلخيص ابن مكتوم ١٥٤، والفهرست ٨٦، وكشف الظنون ٢٤٢، ١٧٢٩، ١٩١٨، ومعجم الأدباء ١٤: ١٥٧.','2','310',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (713,'<span data-type="title" id=toc-560>٤٩٣ - على بن محمد الهروىّ النحوىّ [١]</span>
من أهل هراة. قدم مصر واستوطنها روى عن الأزهرىّ. وهو أوّل من أدخل نسخة من كتاب الصحاح للجوهرىّ مصر- فيما قيل- ووجد فيها خللا ونقصا، فهذبه وأصلحه. وصنف كتابا كبيرا فى النحو، عدّة مجلدات، وهو موجود بمصر.
وصنف كتابا فى معانى العوامل سماه الأزهية «١» رأيته بخط ولده أبى سهل، وملكته والحمد لله. وله مختصر فى النحو سماه المرشد، رأيته وملكته وعليه خطه «٢».

<span data-type="title" id=toc-561>٤٩٤ - على بن محمد السّخاوىّ المصرىّ المقرئ النحوىّ [٢]</span>
نزيل دمشق. من أهل سخا؛ إحدى قرى الناحية الشمالية من مصر «٣». قرأ القرآن العزيز بمصر على أبى القاسم بن فيّره «٤» الشاطبىّ المقرئ المشهور، ولازمه مدّة طويلة،
_________
[١] ترجمته فى بغية الوعاة ٣٥٥، وتلخيص ابن مكتوم ١٥٥، وكشف الظنون ٧٣، ٨٢٢، ومعجم الأدباء ١٤: ٢٤٩. والهروى، بفتح الهاء والراء: منسوب إلى هراة، وهى إحدى مدن خراسان المشهورة.
[٢] ترجمته فى إشارة التعيين الورقة ٣٦، وبغية الوعاة ٣٤٩ - ٣٥٠، وتاريخ الإسلام للذهبى (وفيات سنة ٦٤٣)، وتاريخ أبى الفدا ٤: ١٧٤، وتاريخ ابن كثير ١٣: ١٧، وتلخيص ابن مكتوم ١٥٤ - ١٥٥، وحسن المحاضرة ١: ١٧٣، وابن خلكان ١: ٣٤٥، وروضات الجنات ٤٩٢ - ٤٩٣، وشذرات الذهب ٥: ٢٢٢ - ٢٢٣، وطبقات الشافعية ٥: ١٢٦، وطبقات ابن قاضى شهبة ٢: ١٨٣ - ١٨٧، وطبقات القراء لابن الجزرىّ ١: ٥٦٨ - ٥٧١، وطبقات المفسرين للداودى الورقة ١١٧٩، وطبقات المفسرين للسيوطى ٢٥ - ٢٦، وكشف الظنون ٥٩٣، ٦٤٧، ١٢٣٦، ١٣٢٧، ١٧٧٥، ومرآة الجنان ٤: ١١٠،- ١١١، ومعجم الأدباء ١٥: ٦٥ - ٦٦، ومعجم البلدان ٥: ٤٦ - ٤٧، والنجوم الزاهرة ٦: ٣٥٤.','2','311',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (714,'واستفاد منه، وقرأ النحو على نحاة زمانه من الشاطبىّ وغيره، وخرج عن مصر، واستوطن دمشق، وتصدّر بجامعها للإقراء والإفادة، فاستفاد الناس منه، وأخذوا عنه. وصنف فى علم القراءات «١»، وشرح قصيدة «٢» شيخه فى القراءات شرحا كافيا، ونقل عنه. وشرح المفصل «٣» للزمخشرى شرحا حسنا، وطيئ الألفاظ، أراد به وجه الله تعالى، فالنفوس تقبله؛ إذ لم يعتمد فيه القعقعة الأعجمية، ولا التقاسيم المنطقية «٤». وهو مقيم على حالته فى الإفادة بدمشق فى زماننا هذا، وهو سنة اثنتين وثلاثين وستمائة «٥».','2','312',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (715,'<span data-type="title" id=toc-562>٤٩٥ - على بن المبارك الأحمر النحوىّ [١]</span>
صاحب على بن حمزة الكسائىّ. كان مؤدب الأمين، وهو أحد من اشتهر بالتقدّم فى النحو واتساع الحفظ. وجرت بينه وبين سيبويه مناظرة لما قدم بغداذ.
_________
[١] ترجمته فى الأنساب للسمعانى ٢٠ ا- ٢١ ب، وبغية الوعاة ٣٣٤، وتاريخ بغداد ١٢:
١٠٤ - ١٠٥، وتلخيص ابن مكتوم ١٥٥ - ١٥٧، وطبقات الزبيدى ٩٥، وطبقات ابن قاضى شهبة ٢: ١٨٠، ومراتب النحويين، والمزهر ٢: ٤١٠، ومعجم الأدباء ١٣:
٥ - ١١. واسمه فى معجم الأدباء: «على بن الحسن». والأحمر فى الأصل: صفة للرجل الذى فيه الحمرة. قال السيوطى فى البغية ص ٤٣٦: «الأحمر أربعة؛ أشهرهم اثنان: خلف البصرىّ وعلى بن الحسن الكوفى. والثالث أبان بن عثمان الطولونىّ والرابع أبو عمرو الشيبانىّ إسحاق ابن مرار».','2','313',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (716,'قال أبو العباس أحمد بن يحيى: كان علىّ بن المبارك الأحمر مؤدب «١» الأمين يحفظ أربعين ألف بيت شاهد فى النحو سوى ما كان يحفظ من القصائد وأبيات الغريب. ولما أحضر سيبويه فى دار يحيى بن خالد البرمكىّ لمناظرة الكسائىّ حضر الأحمر قبل حضور الكسائىّ، فألقى الأحمر على سيبويه مسألة. فأجاب فيها. فقال له الأحمر: أخطأت. وألقى عليه أخرى فأجاب، فقال له: أخطأت،- وكان الأحمر حادا حافظا- فغضب سيبويه، فقال له الفراء: [إن «٢»] معه عجلة. وأخذ الفراء فى الكلام مع سيبويه «٣».
وقال على بن المبارك الأحمر هذا: قعدت مع الأمين ساعة من نهار، فوصل إلىّ فيها ثلاثمائة ألف درهم. فانصرفت وقد استغنيت. ولم يصر إلى أحد قط من التأديب ما صار إليه. وقد ذكر أن اسمه على بن الحسن.
قال عبد الصمد بن المعذّل: رأيت الأصمعىّ بمكة، وقد جاءه الأحمر؛ فألقى إليه مسائل من الغريب، فجعل يجيبه. وكان الأحمر كأنه مجنون فى سؤاله وحركته.
ولما انقضت المسائل تمثل بشعر ابن مقبل «٤»:
وقد بريت قداحا أنت مرسلها ... ونحن راموك فانظر كيف ترمينا
ثم سأله الأصمعىّ عن بيت فلم يجبه، فسأله عن ثان فلم يجبه، ثم سأله عن ثالث فلم يجبه وتلجلج، فقال الأصمعىّ «٥»:','2','314',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (717,'تلجلج مضغة فيها أنيض ... أصلّت فهى فوق الكشح داء «١»
غصصت بنيئها فبشمت عنها ... وعندى لو طلبت «٢» لها دواء
فقال الأحمر للأصمعىّ: ما يتعرض لك فى اللغة إلّا مجنون.
وكان الأحمر هذا فى أوّل أمره من الجند، من رجالة النوبة على باب الرشيد، وكان يحب علم العربية ولا يقدر على مجالس الكسائى إلا فى أيام غير نوبته، وكان يرصد مصير الكسائىّ إلى دار الرشيد، ويعرض له فى طريقه كل يوم؛ فإذا أقبل تلقّاه وأخذ بردائه حتى ينزل، ثم أخذ بيده وماشاه إلى أن يبلغ إلى الستر، وسأله فى طريقه عن المسألة بعد المسألة، فإذا دخل الكسائىّ رجع إلى موضعه، فإذا خرج الكسائى من الدار تلقاه إلى الستر، وأخذ بيده فماشاه، وسأله حتى يركب ويتجاوز الموضع، ثم ينصرف إلى مكانه. ولم يزل كذلك يتعلم المسألة بعد المسألة حتى قوى وتمكن.
وكان فطنا حريصا، فلما أصاب الكسائىّ الوصح «٣» فى وجهه وبدنه كره الرشيد ملازمته أولاده، وأمره أن يرتاد لهم من ينوب عنه ممن يرتضى به. وقال له:
إنك قد كبرت، ونحن نحبّ أن نريحك؛ لسنا نقطع عنك جاريك «٤»، فجعل يدافع بذلك، وينوى أن يأتيهم برجل فيغلب على موضعه. إلى أن ضيق عليه الأمر وشدد وقيل له: إن لم تأتنا أنت من أصحابك برجل ارتدنا لهم من يصلح- وكان قد بلغه أن سيبويه يريد الشخوص إلى بغداذ والأخفش- فقلق لذلك، وأراد أن يدخل إليهم من لا يخشى عاقبته، فقال للأحمر: هل فيك خير؟ قال: نعم،','2','315',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (718,'قال: قد عزمت على أن أستخلفك على أولاد الرشيد، فقال الأحمر: لعلى لا أفى بما يحتاجون إليه. فقال له الكسائىّ: إنما يحتاجون فى كل يوم إلى مسألتين فى النحو، وبيتين من معانى الشعر وأحرف من اللغة، وأنا ألقّنك فى كل يوم قبل أن تأتيهم ذلك، فتحفظه وتعلّمهم، فقال: نعم. فلما ألحوا عليه قال: قد وجدت لكم من أرضاه، وإنما أخّرت ذلك حتى وجدته- وأسماه لهم- فقالوا له:
اخترت لنا رجلا من رجال النوبة، ولم تأت بأحد متقدّم فى العلم. فقال:
ما أعرف فى أصحابى فى الفهم والصيانة مثله، ولست أرضى غيره لكم. فأدخل الأحمر إلى دار، وفرش له البيت الذى فيه بفرش وخيش.
وكان الخلفاء إذا أدخلوا مؤدّبا إلى أولادهم فجلس أوّل يوم أمروا عند قيامه بحمل كل ما فى المجلس إلى منزله مع ما يوصل به، ويوهب له. فلما أراد الأحمر الانصراف إلى منزله دعى له بحمّالين، فحمل معه ذلك كله مع بزّ كثير.
فقال الأحمر: والله ما يسع بيتى هذا، وما أنا إلا فى غرفة ضيّقة فى بعض الخانات ليس فيها من يحفظه غيرى، وإنما يصلح هذا كلّه لمن له دار وأهل وكلّ شىء يشاكله. فأمر بشراء دار له وجارية، وحمل على دابة، ووهب له غلام، وأقيم له جار له ولمن عنده. وجعل يختلف إلى الكسائىّ كل عشيّة، فيتلقّن ما يحتاج إليه أولاد الرشيد ويغدو عليهم فيلقّنهم. وكان الكسائىّ يأتيهم فى الشهر مرّة أو مرّتين، فيعرضون عليه بحضرة الرشيد ما علّمهم الأحمر. وكان الكسائىّ لا يسألهم إلّا عمّا لقّنهم الأحمر، فيجيبوه عنه، فيثنى على الأحمر بذلك ويرضاه.
ولم يزل الأحمر كذلك حتى صار نحويّا، وحسنت حاله، وعرف بالأدب حتى قدّم على سائر أصحاب الكسائى. ولم يكن قبل ذلك له ذكر، ولا يعرف. ولما تمكّن','2','316',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (719,'فى الرياسة صارت له الهيئة الجميلة، والتجمّل التام، والجماعة المتوقّرة، والطعام السرىّ. وإذا حضر الطلبة إلى منزله رأوا منزلا كمنازل الملوك ينفح منه الطيب، ويوسّع لهم فى المأكل والورق والأقلام والمداد، ويريهم بشرا وسرورا؛ فلا ينفصل أحد عنه إلّا شاكرا.
وكان ينصرف من مكتبه يوم الثّلاثاء فينقطع فى ذلك اليوم عن الخروج، ويجمع إليه إخوانه وأصحابه، ويوسعهم فضلا وإفضالا، فلذلك قال أبو فقعس أو أبو الجراح:
قالوا: ثلاثاؤه خصب [ومكرمة] ... وكلّ أيّامه يوم الثلاثاء
والأحمرىّ إذا لاذوا فملوذه ... من الطّريق ندى فى رأس ميثاء
وجاءته قريبة الدّبيريّة تسأله فلم [يفهم] ما أرادت، فقالت:
الأحمرىّ الأحمق الطّرماذ «١» ... أحمق شخص ضمّه بغداذ
ليس له من خزيه ملاذ
وكان بين الفرّاء والأحمر وحشة؛ وذلك أن الأحمر كان قد اقترض من الفرّاء عشرة آلاف درهم، وردّها عليه مقطّعة، فاستوحشا لذلك.
ولما مات الأحمر بطريق مكّة نعى إلى الفراء، فذكره بخير وأثنى عليه. فقال أهل زمانه: لم يذكره لمحبّته له، وإنما ذكره ليكاثر أهل البصرة بأهل الكوفة.
قال الطّوال: ومات الأحمر قبل الفرّاء بمدّة. قال: أحسبه سنة أربع وتسعين ومائة، ومات الفراء سنة أربع ومائتين.','2','317',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (720,'<span data-type="title" id=toc-563>٤٩٦ - على بن المبارك بن عبد الباقى بن بانويه أبو الحسن النحوىّ [١]</span>
يعرف بابن الزاهدة «١». من ساكنى الظّفرية «٢». قرأ النحو على الشريف أبى السعادات بن الشجرىّ العلوى، [و] «٣» أبى جعفر «٤» المعروف بالتّكريتى، ثم على أبى محمد بن الخشّاب. وصارت له به معرفة جيدة، وأقرأ الناس مدّة، وتخرج به فيه جماعة؛ منهم أبو البركات محمد بن محمد الشهرستانىّ ثم البغداذىّ وغيره.
وكان قد انقطع قبل وفاته بمنزله، وسمع الناس منه فى حال انقطاعه.
وتوفّى يوم الثلاثاء ثالث ذى الحجة من سنة أربع وتسعين وخمسمائة ودفن عند والدته برباط لهم بدرب البقر بالظّفرية.
_________
[١] ترجمته فى بغية الوعاة ٣٦٤، وتلخيص ابن مكتوم ١٥٧، وطبقات ابن قاضى شهبة ٢: ٢٧٩، ومعجم الأدباء ١٤: ١٠٨ - ١١٠. وبانويه، ضبطه ابن قاضى شهبة بالموحدة وبعد الألف نون مفتوحة.','2','318',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (721,'<span data-type="title" id=toc-564>٤٩٧ - على بن المغيرة أبو الحسن الأثرم [١]</span>
صاحب النحو والغريب واللغة. سمع أبا عبيدة معمر بن المثنى وأبا سعيد الأصمعىّ. روى عنه الزبير بن بكار «١»، والحسن «٢» بن مكرم، وأحمد بن أبى خيثمة «٣»، وأبو العباس ثعلب وغيرهم. روى الأثرم هذا عن أبى عبيدة البصرىّ قال: مرّ أبو عمرو بن العلاء [بالبصرة] «٤»، فإذا أعدال مطروحة مكتوب عليها: «لأبو فلان» فقال أبو عمرو: يا ربّ، يلحنون ويرزقون! قال أبو بكر بن الأنبارىّ: وكان ببغداذ من رواة اللّغة اللّحيانى والأصمعىّ وعلى بن المغيرة الأثرم.
قال أبو مسحل: كان إسماعيل بن صبيح أقدم أبا عبيدة فى أيام الرشيد من البصرة إلى بغداذ، وأحضر الأثرم- وكان ورّاقا فى ذلك الوقت- وجعله فى دار من دوره، وأغلق عليه الباب، ودفع إليه كتب أبى عبيدة، وأمره بنسخها.
قال: فكنت أنا وجماعة من أصحابنا نصير إلى الأثرم، فيدفع إلينا الكتاب من
_________
[١] ترجمته فى الأنساب للسمعانى ١١٩، وبغية الوعاة ٣٥٥، وتاريخ الإسلام للذهبى (وفيات سنة ٢٣٢)، وتاريخ بغداد ١٢: ١٠٧ - ١٠٨، وتلخيص ابن مكتوم ١٥٧ - ١٥٨، وطبقات ابن قاضى شهبة ٢: ١٩٠، والفهرست ٥٦، واللباب لابن الأثير ١: ٢١ - ٢٢، والمزهر ٢، ١٢، ومعجم الأدباء ١٥: ٧٧ - ٧٩، والنجوم الزاهرة ٢: ٢٦٣، ٢٦٥، ونزهة الألباء ٢١٨ - ٢٢١. والأثرم: من كانت سنه متفتتة.','2','319',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (722,'تحت الباب، ويفرّقه علينا أوراقا، ويدفع إلينا ورقا أبيض من عنده، ويسألنا نسخه وتعجيله، ويوافقنا على الوقت الذى نردّه عليه فيه، فكنا نفعل ذلك.
وكان الأثرم يقرأ على أبى عبيدة، ويسمعها. قال: وكان أبو عبيدة من أضنّ الناس بكتبه، ولو علم بما فعله الأثرم لمنعه منه، ولم يسامحه.
مات الأثرم فى سنة اثنتين وثلاثين ومائتين فى جمادى الأولى. قال محمد ابن إسحاق النّديم فى كتابه: «أبو الحسن علىّ بن المغيرة الأثرم. روى عن جماعة من العلماء، وعن فصحاء الأعراب، وروى كتب أبى عبيدة والأصمعىّ- وكان لا يفارقها».
«قال ثعلب: كنا عند الأثرم صاحب الأصمعىّ، وهو يملّ «١» شعر الراعى.
قال: فلما استتم المجلس وضع الكتاب من يده- وكان معى يعقوب بن السّكيت- فقال: لا بد من أن أسأله عن أبيات. قال: فقلت: لا تفعل، فلعله لا يحضره جواب، فتكون قد هجنته على رءوس الملأ. قال: لا بدّ من ذلك.
فقال: ما تقول فى قول الراعى «٢»:','2','320',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (723,'وأفضن بعد كظومهنّ بجرّة ... من ذى الأبارق إذ رعين حقيلا «١»
قال: فلجلج الشيخ [وتنحنح] «٢»، ولم يجب بشىء. فقال: ما تقول فى بيته:
كدخان مرتجل بأعلى تلعة ... غرثان ضرّم عرفجا مبلولا «٣»
قال: فعاد إلى تلك الصورة، ورأيت فى وجهه الكراهة والإنكار».
وتوفّى الأثرم سنة ثلاثين ومائتين. وله من الكتب: كتاب النوادر.
كتاب غريب الحديث.

<span data-type="title" id=toc-565>٤٩٨ - على بن منصور بن عبيد الله بن علىّ الخطيبىّ أبو الحسن [١]</span>
الأصبهانىّ الأصل، البغداذىّ المولد والدار، اللغوىّ. فاضل له معرفة تامة بالأدب. قرأ على أبى الحسن على بن عبد الرحيم السّلمىّ المعروف بابن العصّار، وعلى أبى البركات عبد الرحمن بن محمد الأنبارىّ. وبرع فى ذلك حتى صار يشار
_________
[١] ترجمته فى بغية الوعاة ٣٥٦، وتلخيص ابن مكتوم ١٥٨، وطبقات ابن قاضى شهبة ١: ١٩٠ - ١٩١، ومعجم الأدباء ١٥: ٨١ - ٨٣. والخطيبىّ بفتح الخاء وكسر الطاء: منسوب إلى الخطيب. قال السمعانىّ عند ذكر هذه النسبة: «ولعل بعض أجداد المنتسب إليه كان خطيبا».','2','321',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (724,'إليه فى معرفة اللّغة العربيّة، ونقلها حفظا «١» وعلما، مع حفظ القرآن المجيد ومعرفة الفقه على مذهب الشافعىّ.
ولد فى سنة سبع وأربعين وخمسمائة فى شوّال «٢».

<span data-type="title" id=toc-566>٤٩٩ - على بن المغربى النحوىّ [١]</span>
المقيم بقلعة جعبر «٣»، من أرض الجزيرة. كان متصدّرا بها لإفادة هذا الشأن.
وكان أديبا فاضلا فى المائة السادسة من الهجرة، وله شعر جيّد منه:
ما كنت لولا كلفى بالعذار ... أصبو إلى الشرب بكأس العقار «٤»
سال كذوب المسك فى وجنة ... ورديّة تجمع ماء ونار
هذا وما تمّ غرامى به ... فكيف لو تمّ بها واستدار
وفاتن الألحاظ ما زلت من ... نواظر الناس عليه أغار
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٥٨.','2','322',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (725,'ملّكته رقّى على أنّه ... يجير قلبى فتعدّى وجار
ويلاه من صحة أجفانه ... وما بها من مرض واحورار

<span data-type="title" id=toc-567>٥٠٠ - على بن نصر بن سليمان أبو الحسن البرنيقىّ النحوىّ [١]</span>
نزيل مصر. وبرنيق مدينة على ساحل البحر المالح المغربىّ بين الإسكندرية وبرقة. وهى إحدى المراسى للمراكب الواردة من المغرب على رأس الجون المعروف بجون زنديق.
كان نحويا لغويا فاضلا، مشهورا بالأدب. وكتب بخطه الكثير. وكان الناس يتنافسون فى خطه وتحصيله، وذلك مستمرّ إلى زماننا هذا. ولقد رأيت نسخة بخطه من كتاب الجمهرة لابن دريد. وقد أبيع فى تركة الجمال البجلىّ البغداذى المعروف بابن الفضل الكرخى مدرّس المدرسة الحنفية بالقاهرة المعزّيّة بما مبلغه أربعة وعشرون دينارا مصريا. ولولا الحياء ممن تعرّض له، وهو مبارك ابن منقذ التبريزىّ أحد أمراء الدولة الصلاحيّة- وكان يتولّى الدواوين، وتحت يديه أرزاق المرتزقين بها من جهة السلطان- لكان ثمنها قد زاد على ذلك. وكان خطه خطا قاعدا عاقلا بين الخطوط، كثير الضبط، فى غاية التحقيق والتنقيب والتصحيح.
حدّث البرنيقىّ عن سعيد بن السكن الحافظ.
_________
[١] ترجمته فى بغية الوعاة ٣٥٧، وتلخيص ابن مكتوم ١٥٨، ومعجم الأدباء ١٥: ٩٧، ومعجم البلدان ٢: ١٥٥.','2','323',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (726,'<span data-type="title" id=toc-568>٥٠١ - على بن هارون بن نصر أبو الحسن النحوىّ المعروف بالقرميسينىّ [١]</span>
حدّث عن علىّ بن سليمان الأخفش. روى عنه عبد السّلام بن الحسين البصرىّ. وكان عنده عن أبى الحسن الأخفش أشياء كثيرة، وكان ثقة جميل الأمر.
وكان مولده سنة تسعين ومائتين، وكان يسكن الرحبة ببغداذ، وتوفى فى جمادى الآخرة سنة إحدى وسبعين وثلاثمائة.

<span data-type="title" id=toc-569>٥٠٢ - عمر بن إبراهيم بن محمد العلوىّ الزيدىّ أبو البركات [٢]</span>
عمر بن إبراهيم بن محمد بن محمد بن أحمد بن علىّ بن الحسين بن على بن حمزة بن يحيى بن الحسين ذى الدمعة بن زيد الإمام الشهيد بن على زين العابدين بن السّبط أبى عبد الله الحسين بن على بن أبى طالب، عليهم السلام. أبو البركات.
من أهل الكوفة. يسكن محلّة يقال لها السّبيع «١»، ويصلّى بالناس فى مسجد أبى إسحاق السّبيعىّ «٢». شيخ مسنّ كبير فاضل، له معرفة بالفقه والحديث والتفسير
_________
[١] ترجمته فى بغية الوعاة ٣٥٨، وتاريخ بغداد ١٢: ١٢٠ - ١٢١، وتلخيص ابن مكتوم ١٥٩، ومعجم الأدباء ١٥: ١١١. والقرميسينىّ، بكسر القاف وسكون الراء وكسر الميم:
منسوب إلى قرميسين؛ وهى مدينة بجبال العراق.
[٢] ترجمته فى الأنساب للسمعانى ٢٨٣ ب، وبغية الوعاة ٣٥٩، وتاريخ الإسلام للذهبى (وفيات سنة ٥٣٩) وتاريخ ابن عساكر ٣٠: ٤٨٣ - ٤٨٤، وتاريخ ابن كثير ١٢: ٢١٩، وتلخيص ابن مكتوم ١٥٩، وشذرات الذهب ٤: ١٢٢ - ١٢٣، وطبقات ابن قاضى شهبة ٢:
١٩٤، واللباب فى الأنساب ١: ٥١٧، ومعجم الأدباء ١٥: ٢٥٧ - ٢٦٢، والمنتظم (وفيات سنة ٥٣٩)، والنجوم الزاهرة ٥: ٢٧٦.','2','324',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (727,'والنحو واللغة والأدب. وله التصانيف الحسنة السائرة فى النحو. وهو خشن العيش، صابر على الفقر والقلّة، قانع باليسير. وكان يقول: أنا زيدىّ المذهب، وأفتى على مذهب أبى حنيفة. واسع الرواية، أدرك المشايخ الجلّة، كأبى بكر الخطيب وطبقته.
وسافر إلى الشام، وأقام بدمشق مدّة، ثم بحلب مدّة، وقرا بها الإيضاح لأبى على الفارسىّ فى سنة خمس وخمسين وأربعمائة، على رجل يقال له أبو القاسم زيد بن علىّ الفارسىّ عن خاله أبى علىّ الفارسىّ. وروى هذا الشريف الكتاب- أعنى الإيضاح- بهذا الطريق بالكوفة المدّة الطويلة، وأخذه عنه بهذا السّبيل الجمّ الغفير من علماء الرواة والنحاة. وكان هذا الشريف عمر متيقّظا حسن الاستماع، يكتب خطّا جميلا. وكان حافظا للسانه، تكرّر إليه المحدّثون ونقلوا عنه الأحاديث والأخبار لسعة روايته، ولم يسمعوا منه شيئا مما يتعلّق باعتقاد الشيعة.
قال المسلم بن نجم بن علىّ الرّسىّ الكوفىّ: كان الشريف عمر بن إبراهيم الكوفىّ يغرس فسيل «١» النخل فى أجمة له، وهو شيخ كبير، ومعه جماعة من شبّان محلّته يعينونه على ذلك كما جرت العادة. فوقف رجلان من طيّئ شيبان من بعيد من أبناء السبيل ينظران إلى العمل، فقال أحدهما لصاحبه: ترى من يغرس هذا الفسيل؟ فقال له: ذلك الشيخ الكبير. فقال البدوىّ: أذلّه الله! أيرجو هذا الشيخ أن يأكل من جناه! فسمع الشريف ما قال، وأحزنه ذلك، وقال له: يا بنىّ، كم من كبش فى المرعى وخروف فى التّنور! ففهم أحدهما دون الآخر كلام الشريف. فقال الذى لم يفهمه لصاحبه الذى فهم: أيش قال الشيخ؟ فقال','2','325',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (728,'البدوىّ: قال الشيخ: كم من ناب يسقى فى جلد حوار «١»! ففهم البدوىّ ما قال وأعجبه ذلك.
قال أبو الغنائم: وعاش الشريف إلى أن أدرك الفسيل وأكل من تمره سنين كثيرة.
وذاكر الشريف عمر هذا يوما بعض أصحاب الحديث الآخذين عنه، وقال:
دخل أبو عبد الله الصّورىّ «٢» الكوفة، وكتب عن أربعمائة شيخ. وقدم علينا هبة الله بن المبارك السّقطىّ «٣»، فأفدته عن سبعين شيخا من الكوفيّين، وما فى الكوفة اليوم أحد يروى الحديث غيرى. ثم أنشد:
لما دخلت اليمنا ... لم أر فيه حسنا
قلت: حرام بلدة ... أعلم من فيها أنا
وكان أبو محمد عبد الله بن على بن أحمد المقرئ سبط أبى منصور الخياط قد قرأ على الشّريف عمر النحو؛ لأن الشريف كان علامة فى النحو، وقرأ عليه جماعة من مشايخ العراق النحو أيضا. ومدحه أبو محمد عبد الله بأبيات، منها:
أحيا بكوفان علما كان مدروسا ... وقام بالحق فيها وهو خاطبه
فما له فى الورى شكل يماثله ... وما له فى التّقى عدل يناسبه
سئل عن مولده فقال: ولدت فى سنة اثنتين وأربعين وأربعمائة بالكوفة.','2','326',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (729,'وتوفّى رحمه الله يوم الجمعة السابع من شعبان سنة تسع وثلاثين وخمسمائة.
ودفن يوم السبت فى المسبلة المعروفة بالعلويين، وصلّى عليه كل من فى الكوفة.
وقدّر الجمع بثلاثين ألفا.
أنبأنا أبو طالب السّلفىّ فى إجازته العامّة- لمن يقول فى وقت الإجازة:
«لا إله إلا الله محمد رسول الله»، وذلك فى سنة ست وتسعين وخمسمائة- وكنت فى ذلك الحين ابن ثمان سنين: أخبرنى أبو البركات عمر بن إبراهيم بن محمد العلوىّ الزيدىّ بالكوفة وروى عنه حديثا. وقال: الشريف عمر هذا أديب نحوىّ، وفى المذهب زيدىّ، وكان يفتى بالكوفة على مذهبه، وسمع معنا على جماعة من شيوخنا الكوفيين. وكان من عقلاء الرجال، حسن الرأى فى الصحابة، مثنيا عليهم، متبرئا ممن يتبرّأ منهم. والزيديون فى تشيّعهم القديم يقولون بخلافة أبى بكر، ثم عمر، ثم عثمان، ثم علىّ، ويرون أن عليا أفضل، ويجوز تقديم المفضول على الفاضل».

<span data-type="title" id=toc-570>٥٠٣ - عمر بن أحمد بن محمد بن الحسن الكشانىّ الأديب أبو حفص [١]</span>
شيخ معروف مشهور بالتأديب، له تلامذة. ولد سنة ثلاث وسبعين وأربعمائة وتوفى يوم السبت سادس شعبان سنة ست وخمسين وأربعمائة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٦٠، والكشانى، بالفتح ثم التخفيف: منسوب إلى كشانية، وهى بلدة بنواحى سمرقند.','2','327',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (730,'<span data-type="title" id=toc-571>٥٠٤ - عمر بن حسن النحوىّ الصّقلىّ أبو حفص [١]</span>
شيخ فى اللغة والنحو، طويل الباع فيهما؛ أخذا ورويا عنه. وتصدّر للإفادة ببلرم «١»، وهى مدينة جزيرة صقلّيّة فى الأيام الفرنجية، وأصيب من الفرنج بما قضى بسجنه.
وقال يمدح رجّار «٢» ملك صقلّية، وهو فى حبسه:
طلب السلوّ لو أنّ غير سعاده ... حلّت سويدا قلبه وفؤاده
ورجا زيارة طيفها فى صدّها ... وغرامه يأبى لذيذ رقاده
والله لولا الملك رجّار الذى ... أهدى «٣» لحبّيه عظيم وداده
ما عاف كأس المجد يوم فراقها ... ورأى محيّا المجد فى ميلاده
منها فى المديح:
يهتزّ للجدوى اهتزاز مهنّد ... يهتزّ فى كفّيه يوم جلاده
ويضىء فى الدّيجور ضوء جبينه ... فتخال ضوء الشمس من حسّاده
ومطالع الجوزاء أرض خيامه ... والنجم والقمران من أوتاده
وإذا الأمور تشابهت فلعضبه ... خطّ يبيّض سودها بمداده
يأيّها الملك الذى ثنيت به ... قدما الفظاظة فى صفا أصلاده
ودعته أرواح العدى فرمى بها ... لعبا تلقتها ظبى أغماده
والله يغفر لهذا الشاعر فى مدحه الملك الكافر؛ ولكنه معذور؛ إذ هو مأسور.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٦٠، وخريدة القصر ١١: ٣٢، والمكتبة الصقلية ٥٨٧ - ٥٨٨، ٦٤٦.','2','328',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (731,'<span data-type="title" id=toc-572>٥٠٥ - عمر بن خلف بن مكّىّ الصّقلى [١]</span>
فقيه محدّث لغوىّ عالم بالعربيّة، مصنّف فى اللغة. صنّف فى اللغة كتابا سمّاه تلقيح الجنان وتثقيف اللسان فى نهاية الملاحة والبيان، يدل على وفور حظّه من هذا الشأن.
رحل إلى تونس من برّ العدوة، فاستوطنها، وولى قضاءها. وكان يجيد الخطب، يخطب فى كل جمعة بخطبة من إنشائه؛ تفوق خطب ابن نباته. وله شعر يروق، منه ما قاله فى القناعة:
يا حريصا قطع الأيّام فى ... بؤس عيش وعناء وتعب
ليس يعدوك من الرزق الّذى ... قسم الله فأجمل فى الطلب
وقال:
أتطمع فى ودّ امرئ وهو قاطع ... لأرحامه هيهات قد فاتك الرّشد
إذا لم يكن فى المرء خير لوالد ... ولا ولد لم يرجه أحد بعد

<span data-type="title" id=toc-573>٥٠٦ - عمر بن عثمان بن شعيب الجنزى [٢]</span>
من ثغر جنزة. قرأ على الأبيوردىّ «١». وهو أحد أئمة الأدب، وله باع طويل فى النحو ومعرفة كلام العرب. ورد بغداذ والبصرة وخوزستان؛ وذاكر الفضلاء
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٦٠، وخريدة القصر ١١: ٧٤ - ٧٦، والمكتبة الصقلية ٥٩٧، ٦٤٦.
[٢] ترجمته فى الأنساب ١٣٧ ب، وبغية الوعاة ٢٦٢، وتلخيص ابن مكتوم ١٦١ - ١٦٢، واللباب فى الأنساب ١: ٢٤١ - ٢٤٢، ومعجم البلدان ٣: ١٥١. والجنزى، بفتح الجيم وسكون النون وبعدها الزاى: منسوب إلى جنزة؛ وهى من قرى أذربيجان.','2','329',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (732,'حتى صار علّامة زمانه وواحد عصره. وشرع فى إملاء تفسير لو تمّ لم يوجد مثله.
وتوفى بمرو سنة خمسين وخمسمائة فى شهر ربيع الأول. ومن شعره:
بدا ونسيم صبحى برق عيد ... خيال زائر من برقعيد «١»
وقرّعنى على طول التنائى ... وجرّعنى عتاب المستزيد
ففضّ الدرّ فوق الورد نثرا ... وغصّ الورد بالدرّ النضيد
وبات وبتّ معتنقين شوقا ... يبدّد منه ضمّى سقط جيد
فلى طوقان لكن من لجين ... وطوقاه من التّبر الجديد

<span data-type="title" id=toc-574>٥٠٧ - عمر بن عثمان بن محمد بن عمير بن حبيب الأندلسىّ النحوىّ المعروف بابن الجرار [١]</span>
كان من أهل البلاغة والشعر، وكان ذا حظ من اللغة والنحو، وله رسالة ناقض فيها عبد الله بن المقفّع فى اليتيمة، وظهر فضله فيها. وكان يرمى بالزندقة. وكان ضئيل الخلقة؛ فلأجل ذلك كتب إلى ربّ الأمر فى زمانه، وقد كان مبعدا غير مقرّب:
يا لباب اللّباب من عبد شمس ... ومحلّ الحياة من كلّ نفس
إن يكن مبعدى قماءة شخصى ... وروائى ففى حديثى أنسى
_________
[١] ترجمته فى بغية الملتمس للضبى ٤١٥ - ٤١٦، وتلخيص ابن مكتوم ١٦١. واسمه فى بغية الملتمس: عمرو بن عثمان بن سعيد بن الجرز، (بالجيم والراء قبل الزاى).','2','330',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (733,'<span data-type="title" id=toc-575>٥٠٨ - عمر بن محمد بن عمر أبو حفص الفرغانى [١]</span>
من فرغانة تركستان ممّا وراء النهر، وإنما [ذكرت] بلده؛ خشية اللبس؛ وذلك [أن] فى قرى أصبهان فرغانة- وربما قيل فرغان- ينسب إليها جماعة من المحدّثين.
وعمر هذا قرأ النحو العربىّ فى بلاد العجم على عدّة مشايخ، وعرف منه طرقا. وقرأ المنطق اليونانى أيضا على الفخر الرازىّ «١» وطبقته، وأجاد النوعين، وشارك فيما سواهما مشاركة بليغ. وهو حسن النّقل فى الألفاظ البليغة، وربما خرج فى المؤاخذة إلى حدّ يرتفع به مجاز الكلام والاتساع فى العبارة والاستعارة.
رماه المقدار إلى مدينة سنجار، ورزق بها على تدريس ما يعلّمه، فتصدّر وأفاد الطلبة بجامعها علم النحو، والفقه على مذهب النعمان بن ثابت، والمنطق. وفيه كبر وعسر فى الإفادة، واطّراح لجانب الجهلة المتكبرين. واتفق أن جرى على رسمه السائر، فى قطع وصل ابن مهاجر. [و] لما استمر الفرغانى هذا على إهمال جانبه، وألقى حبله على غاربه، توسط له فى أمر رزقه بما هو أهله، وحمله على اطّراح علمه جهله، وأشار على صاحب البلد بإبعاده، ورماه عنده بكفره وإلحاده، فتقدم إليه بالرحلة عن سنجار؛ فتركها غير مكترث بها وسار. ولما حصل ببغداذ نال بها المآرب والملاذ، وتصدر للتدريس والإفادة، وبذلت له الحسنى وزيادة.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٦١، والجواهر المضية ١: ٣٩٦.','2','331',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (734,'وهو فى وقتنا هذا مرتّب فى المدرسة الجديدة المستنصرية «١»، يلقى الدروس، وتطأطأ نحوه الرءوس، وذلك فى سنة اثنتين وثلاثين وستمائة. وأصبح الناقص ابن مهاجر يقلّب من الندم على فعله فى حقّه كفّيه، ويتميّز غيظا إذ بلغه ما انتهى أمره إليه.
ولما توفرت لديه السعادات، وساغ [له] أن يعيش مات، فى أوائل سنة اثنتين وثلاثين وستمائة، ولم يخلف ولدا «٢».

<span data-type="title" id=toc-576>٥٠٩ - عمر بن محمد بن عمر بن عبد الله أبو على الشّلوبينىّ الأندلسىّ [١]</span>
نزيل إشبيلّية والمتصدّر بها. نحوىّ فاضل كامل، من قرية من قرى إشبيلية، اسمها شلوبينية «٣».
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٧ - ٣٨، وبغية الوعاة ٣٦٤، وتاريخ الإسلام للذهبىّ (وفيات سنة ٦٤٥)، وتاريخ ابن كثير ١٣: ١٧٣، وتلخيص ابن مكتوم ١٦٢ - ١٦٥، وابن خلكان ١: ٣٨٢، وروضات الجنات ٥٠١، وشذرات الذهب ٥: ٢٣٢ - ٢٣٣، وطبقات ابن قاضى شهبة ١: ٢٠٠، وكشف الظنون ٥٠٨، ١٤٢٨، ١٨٠٠، ومرآة الجنان ٤:
١١٣: ١١٤، ومعجم البلدان ٥: ٢٩٠، والنجوم الزاهرة ٦: ٣٥٨. وفى ابن خلكان: «هذه النسبة إلى الشلوبين، وهو بلغة أهل الأندلس الأبيض الأشقر». وحكى ابن مكتوم عن شيخه ابى حيان: «لا يقال له الشلوبينىّ؛ إنما هو الشلوبين (بالشين المشوبة) غير منسوب، وذلك لقب عليه».
ثم قال: «وليس قول من قال إنه منسوب إلى شلوبينية بشىء. والقول ما قالت حزام».','2','332',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (735,'قال لى مخلص بن الظّل الغرناطىّ لما قدم علينا حلب: خرجت من إشبيلية أنا وعمر الشّلوبينىّ النحوىّ، وكنت قاصدا مالقة لأركب منها البحر إلى بر العدوة، وكان الشلوبينىّ راكبا على حمار قصير تكاد رجلاه تلمس الأرض، وعليه برنس يغطّيه ويغطى الحمار، فلما كنا ببعض الطريق عرّج إلى ناحية قريته ومضيت إلى مالقة.
وهذا الشّلوبينىّ له فى بلاده ذكر كثير، وهو متصدّر هناك، وسألت عنه من رآه من أهل النحو فقال لى: لم تكن عبارته بليغة، وإن قلمه فى التصنيف لأجود من عبارته.
وقيل إنه صنف شرحا لكتاب سيبويه لم يظهر بعد، وصنف شرحا «١» للجزولية «٢»، رأيت منه فصولا قد أوردها الجيّانىّ «٣» النحوى فى شرحها منسوبة إليه، لم يكن فيها كبير أمر.
والذى وقع لى أنه غير عاشق فى هذه الصناعة، وإنما يريدها للارتزاق؛ وذلك أنه لما قدم علينا أبو العباس أحمد بن مفرج بن الرومية العشّاب «٤» الإشبيلىّ «٥»','2','333',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (736,'وهو أثبت من رأيت وأسكن، وهو أحد القائلين بمذهب ابن حزم الظاهرىّ الأندلسىّ.
أخبرنى أنه لما عزم على الخروج إلى المشرق للحج ابتاع من عمر الشلوبينىّ الأندلسى كتاب العالم فى اللغة لأحمد بن أبان بن سيّد الأشبيلىّ الأندلسىّ فى اللغة فى أربعين مجلدا، وهو كتاب غريب عجيب لا يسوغ لعالم عاشق فى علم العربية أن يخرج عن يده، واستدللت بهذا على ما قلت «١».','2','334',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (737,'وهو حى فى زماننا هذا بإشبيليّة يفيد هذا الشأن، ويقرأ عليه السّوقة والأعيان؛ لم تبلغنا وفاته، وذلك فى سنة اثنتين وثلاثين وستمائة «١».

<span data-type="title" id=toc-577>٥١٠ - عثمان بن جنى أبو الفتح الموصلىّ النحوىّ اللغوىّ [١]</span>
المشهور المذكور، صاحب التصانيف البديعة فى علم الأدب. وأبوه جنّى مملوك رومىّ لسليمان بن فهد بن أحمد الأزدىّ الموصلىّ. وفى ذلك يقول عثمان ابن جنى:
فإن أصبح بلا نسب ... فعلمى فى الورى نسبى
_________
[١] ترجمته فى إشارة التعيين ٣٠ ا، وبغية الوعاة ٣٢٢، وتاريخ ابن الأثير ٧:
٢١٩، وتاريخ بغداد ١١: ٣١١ - ٣١٢، وتاريخ أبى الفدا ٢: ١٣٦، وتاريخ ابن كثير ١١: ٣٣١، وتلخيص ابن مكتوم ١٦٥ - ١٦٦، وابن خلكان ١: ٣١٣ - ٣١٤، ودمية القصر ٢٩٧ - ٢٩٨، وروضات الجنات ٤٦٦، وشذرات الذهب ٣: ١٤٠ - ١٤١، والشعور بالعور ١٣١ - ١٣٧، وطبقات ابن قاضى شهبة ٢: ١٢٣ - ١٢٦، وعيون التواريخ (وفيات سنة ٣٩٢)، وكشف الظنون ٣٨٤، ٤١١، ٤١٢، ٤١٦، ٤٨١، ٤٩٣، ٦٩١، ٦٩٢، ٧٠٦، ٨١٠، ٩٨٨، ١٢٧٢، ١٤٥٧، ١٤٦٢، ١٥٦٢، ١٦١٢، ١٧١٢، ١٧٩٣، ١٨٥٠، ١٨٨٢، ١٩١٤، ومرآة الجنان ٢: ٤٤٥، ومسالك الأبصار ج ٤ مجلد ٢: ٣٠٧، ومعجم الأدباء ١٢: ٨١ - ١١٥، والمنتظم (وفيات سنة ٣٩٢)، والنجوم الزاهرة ٤: ٢٠٥، ونزهة الألباء ٤٠٦ - ٤٠٩، ويتيمة الدهر ١: ٨٩. قال ابن خلكان: «وجنى، بكسر الجيم وتشديد النون، وبعدها ياء».','2','335',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (738,'على أنى أؤول إلى ... قروم سادة نجب
قياصرة إذا نطقوا ... أرمّ «١» الدهر «٢» ذو الخطب «٣»
أولاك دعا النبى لهم ... كفى شرفا دعاء نبى
صحب أبا على الفارسىّ وتبعه فى أسفاره، وخلا به فى مقامه، واستملى منه، وأخذ عنه، وصنّف فى زمانه، ووقف أبو علىّ على تصانيفه واستجادها.
واستوطن أبو الفتح دار السلام، ودرس بها العلم إلى أن مات. وكانت وفاته ببغداد على ما ذكره أحمد بن علىّ التّوّزىّ «٤» فى يوم الجمعة لليلتين بقيتا من صفر سنة اثنتين وسبعين وثلاثمائة.
فمن تصانيفه: كتاب اللمع «٥». كتاب سر الصناعة «٦». كتاب المنصف «٧» فى شرح كتاب المازنى فى التصريف. كتاب الخصائص «٨». كتاب التلقين فى النحو. كتاب التعاقب. كتاب الكافى فى شرح قوافى الأخفش.
كتاب المذكّر والمؤنث. كتاب المقصور والممدود. كتاب التمام فى شعر','2','336',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (739,'الهذليين. كتاب إعراب الحماسة. كتاب المنهج «١» فى اشتقاق أسماء شعراء الحماسة «٢». كتاب الصبر «٣» فى شرح شعر المتنبى. مختصر العروض. مختصر فى القوافى. كتاب هذا القدّ، وهو ما استملاه من أبى علىّ. كتاب المسائل الخاطريات. كتاب التذكرة الأصبهانية. مختار تذكرة أبى على وتهذيبها.
كتاب المقتضب فى المعتل العين «٤». وذكره الباخرزىّ فى كتابه فقال:','2','337',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (740,'ابن جنّى» هو أبو الفتح عثمان، ليس لأحد من أئمة الأدب فى فتح المقفلات وشرح المشكلات ما له؛ ولا سيّما فى علم الإعراب، فقد وقع منها على ثمرة الغراب «١». ومن وقف «٢» على مصنّفاته وقف «٣» على بعض صفاته. فوربّى إنه كشف الغطاء عن شعر المتنبى. وما كنت أعلم أنه ينظم القريض، أو يسيغ ذلك الجريض «٤»؛ حتى قرأت له مرثية فى المتنبى، أوّلها:
غاض القريض وأودت نضرة الأدب ... وصوّحت «٥» بعد رىّ دوحة الكتب
منها:
سلبت ثوب بهاء كنت تلبسه ... لما تخطّفت بالخطّية السلب «٦»
ما زلت تصحب فى الجلّى إذا نزلت ... قلبا جميعا وعزما غير منشعب
وقد حلبت لعمرى الدهر أشطره «٧» ... تمطو بهمّة لا وان ولا نصب
من للهواجل تحيى ميت أرسمها ... بكل جائلة التصدير والحقب «٨»
قبّاء خوصاء محمود علالتها ... تنبو عريكتها بالحلس والقتب «٩»','2','338',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (741,'أم من لسرحانها تقريه فضلته ... وقد تضوّر بين البأس والسّغب «١»
أم من لبيض الظّبا توكافهنّ دم ... أم من لسمر القنا والزّغف واليلب «٢»
أم للجحافل تذكى جمر جاحمها «٣» ... حتى يقرّبها من ساطع اللهب
أم للمحافل إذ تبدو فتعمرها ... بالنّظم والنّثر والأمثال والخطب
أم للصواهل محمرّا سرابلها ... من بعد ما غربت معروفة الشّهب
أم للمناهل والظلماء عاكفة ... تواصل الكرّ بين الورد والقرب «٤»
أم للقساطل تعتمّ الحزون بها ... أم من لضغم الهزبر الضّيغم الحرب «٥»
أم للضّراب إذا الأحساب دافع عن ... تدنيسها شفرات الوكّف القضب
أم للملوك تحلّيها وتلبسها ... حتى تمايس فى أبرادها القشب
نابت وسادى أطراب تؤرّقنى ... لما غدوت لقى «٦» فى قبضة النوب
عمرت خدن المساعى غير مضطهد ... ومتّ كالنصل لم يدنس ولم يعب
فاذهب عليك سلام الله ما قلقت ... خوص الركائب بالأكوار والشّعب
- الشّعب: جمع شعبة، وهى المزادة الضخمة. قاله أبو حاتم السّجزىّ-
موفّق لسبيل الرشد متّبع ... يزينه كلّ ما يأتى ويجتنب
تسمو العلوم إليه كلما انفرجت ... للناس عن وجهه الأبواب والحجب
له خلائق بيض لا يغيّرها ... صرف الزمان كمالا يصدأ الذهب','2','339',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (742,'وخدم أبو الفتح بن جنّى البيت البويهىّ: عضد الدولة «١» وولده صمصام «٢» الدولة، وولده شرف «٣» الدولة، وولده بهاء «٤» الدولة، وفى زمانه مات. وكان يلازمهم فى دورهم وييايتهم.
وحكى أبو غالب بن بشران النحوىّ الواسطىّ محمد بن أحمد بن سهل قال:
ورد أبو الفتح بن جنى عثمان إلى واسط، ونزل فى دار الشريف أبى على الجوّانىّ نقيب العلويين، وكنا نتردّد إليه ونسائله، ويملى علينا مسائل سماها الواسطية.
وورد بعد ذلك أبو الحسن على بن عيسى الربعىّ إلى واسط، ونزل حجرة فى جوار شيخنا أبى إسحاق إبراهيم بن سعيد الرفاعىّ، وكنت أتردّد إليه، وأسأله، فقال لى يوما أبو إسحاق: قد انعكفت على هذا المجنون! فقلت له: إنه يحكى عن أبى علىّ النحو كما أنزل. فقال: صدقت «٥»!.','2','340',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (743,'<span data-type="title" id=toc-578>٥١١ - عثمان بن سعيد بن عثمان بن سعيد الأموىّ المقرئ الدانىّ المعروف بابن الصيرفىّ [١]</span>
من أهل قرطبة؛ أبو عمرو. سكن دانية «١»؛ المقرئ شيخ زمانه، وعلّامة أوانه وصدر عصره ومكانه.
روى عن علماء بلاده فأكثر، ورحل إلى المشرق، فسمع بمصر ومكة. وكان أحد الأئمة فى علم القرآن ورواياته وتفسيره ومعانيه وطرقه وإعرابه. وجمع فى معنى ذلك كله تواليف حسانا مفيدة؛ يكثر تعدادها، ويطول إيرادها «٢». وله معرفة بالحديث وطرقه وأسماء رجاله ونقلته. وكان حسن الخط جيّد الضبط، من أهل الفضل
_________
[١] ترجمته فى بغية الملتمس للضبى ٣٩٩ - ٤٠٠، وتاريخ الإسلام للذهبى (وفيات سنة ٤٤٤) وتذكرة الحفاظ ٣: ٢٩٨ - ٣٠٠، وتلخيص ابن مكتوم ١٦٦ - ١٦٧، والديباج المذهب ١٨٨، وشذرات الذهب ٣: ٢٧٢، والصلة لابن بشكوال ١: ٣٩٨ - ٤٠٠، وطبقات ابن قاضى شهبة ٢: ١٢٧، وطبقات القراء ١: ٥٠٣ - ٥٠٥، وطبقات المفسرين للداودى الورقة ١٥٩ ا- ١٦٠ ب، وطبقات المفسرين للسيوطى ١٥٩، وعيون التواريخ (وفيات سنة ٤٤٤)، وكشف الظنون ١٣٥، ٣٥٥، ٥٢٠، ٥٣٨، ١١٠٥، ١٤٧١.
١٦١٢، ١٦١٧، ١٨٠٩، ومعجم البلدان ٤: ٢٨، ومرآة الجنان ٣: ٦٢، ومعجم الأدباء ١٢: ١٢١ - ١٢٤، والنجوم الزاهرة ٥: ٥٣، ونفح الطيب ٢: ٣٣٥ - ٣٣٧.','2','341',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (744,'والعلم والذكاء والفهم، متفننا فى العلوم، جامعا لها، معتنيا بها. وكان ديّنا فاضلا ورعا مجاب الدعوة، مالكىّ المذهب. وقال رحمه الله: «ولدت سنة إحدى وسبعين وثلاثمائة، وابتدأت بطلب العلم بعد سنة خمس وثمانين وأنا ابن أربع عشرة سنة، وتوجهت إلى المشرق لأداء فريضة الحج يوم الأحد الثانى من المحرم سنة سبع وتسعين، وحججت سنة ثمان. وقرأت القرآن وكتبت الحديث وغير ذلك فى هذين العامين، وهى ابتداء الفتنة الكبرى التى كانت بالأندلس فى ذى القعدة سنة تسع وتسعين، وانصرفت إلى الأندلس سنة تسع وتسعين؛ والحمد لله على كل حال «١»».
وتوفى- رحمه الله- بدانية يوم الاثنين فى النصف من شوّال سنة أربع وأربعين وأربعمائة. وكان دفنه بعد صلاة العصر فى اليوم الذى توفى فيه، ومشى السلطان أمام نعشه، وكان الجمع فى جنازته عظيما.

<span data-type="title" id=toc-579>٥١٢ - عثمان بن على بن عمر السّرقوسىّ الصّقلىّ النحوىّ [١]</span>
كان عالما نحويا لغويا مقرئا. قرأ القرآن على ابن الفحام «٢» وابن بليّمة «٣» وغيرهما.
وله تواليف فى القراءات والنحو والعروض. وكانت له فى جامع مصر حلقة للإقراء وانتفع به الناس، ونقلوا كلامه، وكتبوا تصانيفه، وتنافس فيها أهل العلم. وكان
_________
[١] ترجمته فى بغية الوعاة ٣٢٣، وتلخيص ابن مكتوم ١٦٧، ومعجم الأدباء ١٢:
١٣٠ - ١٣٥، ومعجم السفر للسلفىّ ١: ١٨٤. والسرقوسى: منسوب إلى سرقوسة، وهى من مدن صقلية المشهورة.','2','342',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (745,'قريبا من زماننا هذا فى المائة السادسة للهجرة. لقيه الحافظ السّلفىّ بمصر، وشاركه فى السماع على أبى صادق وابن بركات والفرّاء الموصلّى.
ومن مصنفاته التى شاهدتها: الحاشية على كتاب الإيضاح. وهى فى غاية الجودة، ومختصر عمدة «١» ابن رشيق؛ وشاهدت هذا المختصر بحلب بخطه عند ابن القيسرانىّ، وقد زاد فيه أبوابا أخلّ بها ابن رشيق، وهى واقعة موقعها من التصنيف. وله شعر.
أنبأنا أبو طاهر السّلفىّ فى إجازته العامة قال: أنشدنى أبو عمر عثمان بن على ابن عمر السّرقوسىّ النحوىّ لنفسه بالثغر- يعنى الإسكندرية- وكتب لى بخطه:
إنّ المشيب من الخطوب خطيب ... ألّا هوى بعد المشيب يطيب!
خطب الخضاب على قضيبك خطبة ... لا غصن من بعد الخضاب رطيب
فدع الصّبا فمن المصيبة أن ترى ... صبّا وصيّب مقلتيك يصوب
إنّ الخضاب لعين عين ضدّه ... ببنانهنّ وكفّهن خضيب
ضحك المشيب بلمّتى فبكت له ... عينى فمنى ضاحك وقطوب
ضدّان مجتمعان فى وقت معا ... فى ذات مرء إن ذا لعجيب

<span data-type="title" id=toc-580>٥١٣ - عثمان البتىّ [١]</span>
ذكره أبو أحمد الحسن بن عبد الله بن سعيد العسكرى فى كتاب شرح ما يقع فيه التصحيف والتحريف فيما وهم فيه أبو عثمان عمرو بن بحر الجاحظ فقال:
_________
[١] ترجمته فى الأنساب للسمعانى ٦٥ ب، وتهذيب التهذيب ١٢: ١٥٤ - ١٥٥، وخلاصة تذهيب الكمال ٢٢٣، واللباب فى الأنساب ١: ٩٦ - ٩٧، والوافى بالوفيات ج ٥ مجلد ٢: ٣٠٣.
واسمه عثمان بن مسلم أبو عمرو. والبتى، بفتح الباء وكسر التاء مشددة: منسوب إلى البت. وهو موضع.
قال السمعانى: «أظنه بنواحى بالبصرة. وقد ذكر ابن حجر فى تهذيب التهذيب أن وفاته كانت سنة ١٤٣. وهذه الترجمة وردت فى هامش الأصل ص ٥٥٩.','2','343',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (746,'«سمعت من يحكى عن ابن دريد ولم أسمع هذه الحكاية منه [أنه] «١» قال: وجدت للجاحظ فى كتاب البيان والتبيين تصحيفا شنعا فى الموضع الذى يقول فيه: حدثنى محمد بن سلّام قال: سمعت يونس يقول: ما جاءنا عن أحد من روائع الكلام ما جاءنا عن النبىّ صلى الله عليه وسلم. قال أبو بكر «٢»: وإنما هو عن البتّى؛ أى عن عثمان البتّى، وكان فصيحا، وأما النبىّ صلى الله عليه وسلم فلا شك عند الملىّ والذمى أنه كان أفصح الناس. أخبرنا ابن دريد حدثنا أبو حاتم عن الأصمعىّ قال: كان عثمان البتّى نحويا؛ وكان يسمى عثمان العربىّ من فصاحته، فسمعه ابن أبى إسحاق ينشد:
[كورهاء] «٣» مشنىّ إليها حليلها
فقال: أخطأ عربيّكم؛ إنما هو «مشنوء» «٤».

<span data-type="title" id=toc-581>٥١٤ - عثمان بن عيسى بن منصور النتاج البلطىّ النحوىّ الموصلىّ [١]</span>
أصله من بلد، إحدى قرى الموصل، ويقال لها بلط بلغة النّبط. مولده فى بنى مائدة بالموصل فى سنة أربع وعشرين وخمسمائة، وانتقل إلى الشام وأقام بدمشق
_________
[١] ترجمته فى بغية الوعاة ٣٢٣، وتاريخ الإسلام للذهبى (وفيات سنة ٥٩٩)، وتلخيص ابن مكتوم ١٦٧ - ١٦٨، وطبقات ابن قاضى شهبة ٢: ١٢٩ - ١٣١، وفوات الوفيات ٢: ٤٠ - ٤٢، وكشف الظنون ١١٤٢، ١٣٣٧، ومعجم الأدباء ١٢:
١٤١ - ١٦٧.','2','344',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (747,'برهة من عمره يتردّد إلى الزبدانى للتعليم. ولما ملك العزيز مصر انتقل إليها، ورتب له صلاح الدين على جامعها كل شهر جاريا لإقراء النحو. رأيته بمصر وهو يفيد الطلبة علمى النحو والعروض؛ فإنه كان بهما قيّما، ولم أسمع أحدا يذكر صيانته.
وكان متّهم الخلوة؛ لا يردّه ملام عن رشف المدام، ولا يسمع الكلام فى ذمّ الغلام. ولم يزل عزبا قذر الهيئة، خشن الملبوس، مبدّد الأطراف، فى تصرفه ما يدل على نقص مروءته. وكان شريف النفس فى أمر واحد، وهو قلة الاكتراث بأهل المناصب، وترك السعى إليهم. وبلغنى أنه كان حلو المحاضرة مفيد المخاطبة والمناظرة. وله شعر مذكور مشهور، منه قوله:
حكّمته ظالما فى مهجتى فسطا ... وكان ذلك جهلا شبته بخطا
هلّا تجنبته والظلم شيمته ... ولا أسام به خسفا ولا شططا
ويلاه من تائه أفعاله صلف ... ملوّن كلما أرضيته سخطا
أبثّه ولهى صدقا ويكذبنى ... وعدا وأقسط عدلا كلما قسطا
واختصر كتاب الأغانى اختصارا جميلا أحسن فيه. «١» ومات فى حدود سنة ستمائة بالقاهرة المعزية «٢».','2','345',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (748,'<span data-type="title" id=toc-582>٥١٥ - عمرو بن عثمان بن قنبر المعروف بسيبويه [١]</span>
عمرو بن عثمان بن قنبر،»
مولى بنى الحارث بن كعب بن عمرو بن علة بن جلد بن مالك بن أدد- ويكنى أبا بشر وأبا الحسن. ومعنى سيبويه «٢» بالفارسية رائحة التفاح.
أخذ النحو عن الخليل بن أحمد الفرهودىّ الأزدىّ، ولازمه، وتتلمذ له.
وقد كان أخذ شيئا من النحو عن عيسى بن عمر الثقفىّ وعن يونس، وأخذ عن غيرهما. وأخذ اللغة عن أبى الخطاب الأخفش الكبير وغيره، وعمل كتابه المنسوب
_________
[١] ترجمته فى أخبار النحويين البصريين ٤٨ - ٥٠، وإشارة التعيين الورقة ٣٨ - ٣٩، وبغية الوغاة ٣٦٦ - ٣٦٧، وتاج العروس ١: ٣٠٥، وتاريخ ابن الأثير ٥: ١٤٢، وتاريخ الإسلام للذهبى (وفيات سنة ١٨٠)، وتاريخ بغداد ١٢: ١٩٥ - ١٩٩، وتاريخ أبى الفدا ٢: ١٥، وتاريخ ابن كثير ١٠ - ١٧٦ - ١٧٧، وتلخيص ابن مكتوم ١٦٨ - ١٧٣، وتهذيب اللغة للأزهرى ١: ٩، وابن خلكان ١: ٣٨٥ - ٣٨٦، وروضات الجنات ٥٠٣ - ٥٠٣، وشذرات الذهب ١: ٢٥٢ - ٢٥٥، وطبقات الزبيدىّ ٣٨ - ٤٥، وطبقات ابن قاضى شهبة ٢: ٢٠٦ - ٢١١، وطبقات القراء لابن الجزرى ١: ٦٠٢، والفلاكة والمفلوكين ٨٣، والفهرست لابن النديم ٥١ - ٥٢، وكشف الظنون ١٤٢٦ - ١٤٢٨، ومراتب النحويين ١٠٥ - ١٧، ومرآة الجنان ١: ٣٤٨، والمزهر للسيوطى ٢: ٤٠٥، ٤٢٦، ٤٥٤، ٤٦٢، ومسالك الأبصار ج ٤ مجلد ٢: ٢٧٧ - ٢٨٠، والمعارف لابن قتيبة ٢٣٧، ومعجم الأدباء ١٦: ١١٤ - ١٢٧، والنجوم الزاهرة ٢: ٩٩ - ١٠٠، ونزهة الألباء ٧١ - ٨١، والوافى بالوفيات ج ٥ مجلد ٣: ٥٣٠ - ٥٣٧.','2','346',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (749,'إليه فى النحو، وهو مما لم يسبقه إليه أحد. وقد قيل إنه أخذ كتاب عيسى بن عمر المسمى بالجامع، وبسطه وحشّى عليه من كلام الخليل وغيره، وأنه كان كتابه الذى اشتغل به، فلما استكمل بالبحث والتّحشية نسب إليه.
ويستدلّ القائل بهذه المقالة بما نقل أن سيبويه لمّا فارق عيسى بن عمر ولازم الخليل سأله الخليل عن مصنّفات عيسى بن عمر، فقال له سيبويه: قد صنف نيّفا وسبعين مصنفا فى النحو، وأن بعض أهل اليسار جمعها وأتت عليها عنده آفة فذهبت، ولم يبق منها فى الوجود سوى تصنيفين؛ أحدهما اسمه الكامل وهو بأرض فارس عند فلان، والجامع، وهو هذا الكتاب الذى أشتغل فيه عليك، وأسألك عن غوامضه. فأطرق الخليل ساعة ثم رفع رأسه، وقال: رحم الله عيسى! ثم أنشد ارتجالا:
ذهب النحو جميعا كلّه ... غير ما أحدث عيسى بن عمر
ذاك إكمال وهذا جامع ... فهما للناس شمس وقمر
فأشار إلى الإكمال بالإشارة إلى الغائب فى قوله: «ذاك»، وأشار [إلى الجامع] بالإشارة إلى الحاضر بقوله: «وهذا».
وذكر ابن إسحاق النديم فى كتابه قال:
«قرأت بخط أبى العباس ثعلب: اجتمع على صنعة كتاب سيبويه اثنان وأربعون إنسانا؛ منهم سيبويه. والأصول والمسائل للخليل». «١»','2','347',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (750,'وقدم سيبويه أيام الرشيد إلى العراق، وهو ابن اثنتين وثلاثين سنة، وتوفى وله نيّف وأربعون سنة بفارس. وكان وروده العراق لقصده يحيى بن خالد «١» البرمكىّ. ولما قيل ليحيى بن خالد: هذا فاضل نحاة البصرة اشتاقت نفسه إلى سماع كلامه. فقيل له: اجمع بينه وبين نحوىّ الكوفة الكسائى. فجمع بينهما، وحضر نحاة الكوفة، وحضر الأخفش الأوسط سعيد، وحضر الفرّاء والأحمر صاحبا الكسائىّ، وسألاه عن مسائل تلجلج فى جوابها. فقال يحيى بن خالد: من يحكم بين هؤلاء؟ فتراضوا بالأعراب، فأحضر من فصحائهم من قدم على باب السلطان، وهم أبو فقعس وأبو دماذ وأبو الجراح وأبو ثروان، فحكموا بما قاله الكسائىّ، فقال الكسائى ليحيى بن خالد: هذا رجل قدم عليك يريد من دنياك. فأجازه بعشرة آلاف درهم، فأخذها وعاد إلى البصرة، وخرج منها إلى فارس، فمات هناك فى سنة تسع وسبعين ومائة.
وكان المبرّد إذا أراد أحد أن يقرأ عليه كتاب سيبويه يقول له: هل ركبت البحر! تعظيما له، واستعظاما لما فيه.
وكان المدينىّ يقول: من أراد أن يعمل كتابا كبيرا فى النحو بعد كتاب سيبويه فليستحى.','2','348',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (751,'أخبرنى الشريف النقيب النّسابة محمد بن أبى البركات الحسين بن أسعد الحسينى الجوانىّ إجازة شافهنى بها بداره؛ بقرافة «١» مصر فى شهور سنة ثلاث وثمانين وخمسمائة، قال أخبرنى عبد السلام بن مختار اللغوى قال أخبرنى ابن بركات السعيدىّ قال أخبرنى أبو سهل محمد بن علىّ بن محمد الهروىّ قال أخبرنى أبو عبد الله محمد ابن الحسين اليمنىّ فى كتابه، قال:
«أخبرنا أبو العباس أحمد بن محمد بن الوليد قال حدّثنا إبراهيم بن السرىّ الزّجاج قال: قال محمد بن يزيد المبرّد: سيبويه يكنى أبا بشر، واسمه عمرو بن عثمان بن قنبر، مولى لبنى الحارث بن كعب بن عمرو بن علة بن جلد بن مالك بن أدد، وقيل: كان يكنى أبا الحسين وأبا عثمان. والأوّل أشبه وأثبت».
وقال أبو حاتم: هو عمرو بن عثمان. وسيبويه بالفارسية «رائحة التفّاح».
وهو لقب. وكان فى لسانه حبسة، وقلمه أبلغ من لسانه. وهو أثبت من أخذ عن الخليل بن أحمد، وهو أستاذه، وأخذ أيضا عن يونس بن حبيب، وعيسى بن عمر وغيرهم. وأخذ اللغة عن أبى الخطاب الأخفش وغيره. وسمع الحديث، وكان شديد الأخذ، وكان يستملى على حماد بن سلمة.
كتبت من خط محمد بن عبد الملك «٢»: حدّثنى محمد بن موسى بن حماد قال:
حدّثنا محمد بن منصور الطوسىّ قال: سمعت عفان بن مسلم يقول: قال سيبويه','2','349',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (752,'لشعبة- ورادّه فى حديث- فقال شعبة: لأن أخرّ من السماء أحبّ إلى من أن أدلّس.
قال نصر بن علىّ: كان سيبويه يستملى من حماد بن سلمة يوما: قال رسول الله صلى الله عليه وسلم: ما أحد من أصحابى إلا وقد أخذت عليه، ليس أبا الدرداء.
فقال سيبويه: «ليس أبو الدرداء»، فقال: لحنت يا سيبويه، فقال سيبويه:
لا جرم! لأطلبنّ علما لا تلحّننى فيه أبدا، فطلب النحو ولم يزل يلازم الخليل.
كتبت من خط محمد بن عبد الملك: حدّثنا إبراهيم بن مهدىّ قال حدّثنا سهل بن محمد قال سمعت أبا زيد الأنصارىّ يقول: كان سيبويه غلاما يأتى مجلسى وله ذؤابتان. قال: وإذا سمعته يقول: حدّثنى من أثق بعربيّته- فإنّما يعنينى.
وكتبت من خط محمد بن عبد الملك: حدّثنى محمد بن على بن حمزة قال حدّثنا الرياشىّ قال سمعت الأخفش يقول: كان سيبويه إذا وضع شيئا من كتابه عرضه علىّ وهو يرى أنى أعلم منه- وكان أعلم منى- وأنا اليوم أعلم منه.
وكتبت من خطه: حدّثنى أحمد بن محمد النحوىّ قال حدّثنى محمد بن سلام قال حدّثنى الأخفش: أنه قرأ كتاب سيبويه على الكسائىّ فى جمعة، فوهب له سبعين دينارا. قال: وكان الكسائىّ يقول لى: هذا الحرف لم أسمعه فاكتبه لى، فأفعل.
قال أبو العباس: وكان الأخفش يؤدّب ولد الكسائى، وكان الجاحظ قد سمع هذا الخبر فقال فيما يعدّده من فخر أهل البصرة على أهل الكوفة:
هؤلاء يأتونكم بفلان وفلان، وسيبويه الذى اعتمدتم على كتبه وجحدتم فضله-','2','350',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (753,'وذكر الجاحظ كتاب سيبويه- لم يكتب الناس فى النحو كتابا مثله، وجميع كتب الناس عليه عيال. وكان سيبويه لشهرته وفضله علما عند النحويّين، وكان يقال بالبصرة: قرأ فلان الكتاب؛ فيعلم أنه كتاب سيبويه، ولا يشك أنه كتاب سيبويه.
كتبت من خط محمد بن عبد الملك: حدّثنى المروزىّ عن الجاحظ قال:
«أردت الخروج إلى محمد بن عبد الملك الزيات، ففكّرت فى شىء أهديه إليه فلم أجد شيئا أشرف من كتاب سيبويه. فقلت له: أردت أن أهدى إليك شيئا، ففكرت فإذا كل شىء عندك دونه، فلم أر أشرف من كتاب سيبويه. وهذا كتاب سيبويه اشتريته من ميراث الفرّاء. فقال: والله ما أهديت إلىّ شيئا أحبّ إلىّ منه «١»».
وشاهدت بخط السلالى النحوىّ القرشىّ الكوفىّ الورّاق أن الجاحظ لما قدم من البصرة فى بعض قدماته أهدى إلى محمد بن عبد الملك الزيات فى وزارته نسخة من كتاب سيبويه، وأعلم بإحضارها صحبته قبل أن يحضرها مجلسه، فقال له ابن الزيات: أو ظننت أن خزائننا خالية من هذا الكتاب؟ فقال: ما ظننت ذاك؛ ولكنها بخط الفرّاء ومقابلة الكسائىّ وتهذيب عمرو بن بحر الجاحظ. فقال له ابن الزيات: هذه أجلّ نسخة توجد وأغربها. فأحضرها إليه، فسرّ بها، ووقعت منه أجمل موقع.
وكتبت من خط محمد بن عبد الملك التاريخىّ: حدّثنى ابن الأعلم قال حدّثنا محمد بن سلّام قال: كان سيبويه النحوىّ جالسا فى حلقته بالبصرة، فتذاكرنا شيئا من حديث قتادة، فذكر حديثا غريبا فقال: لم يرو هذا إلا سعيد بن','2','351',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (754,'أبى العروبة، فقال بعض ولد جعفر بن سليمان: ما هاتان الزائدتان يا أبا بشر؟
فقال: هكذا يقال؛ لأن العروبة هى الجمعة، ومن قال: عروبة فقد أخطأ. قال ابن سلام: فذكرت ذلك ليونس فقال: أصاب، لله درّه!. «١»
وكتبت من خط محمد بن عبد الملك: حدّثنى إبراهيم بن إسحاق الحربىّ قال:
سمعت ابن عائشة يقول: كنا نجلس مع سيبويه النحوىّ فى المسجد- وكان شابّا جميلا نظيفا قد تعلّق من كل علم بسبب، وضرب فى كل أدب بسهم، مع حداثة سنه وبراعته فى النحو- فبينا نحن عنده ذات يوم إذ هبّت ريح أطارت الورق، فقال لبعض أهل الحلقة: انظر أىّ ريح هذه؟ وكان على منارة، تمثال فرس «٢» من صفر، فنظر ثم عاد فقال ما يثبت الفرس على شىء. فقال سيبويه: العرب تقول فى مثل هذا: قد [تذاءبت الريح و «٣»] تدأّبت الريح؛ أى فعلت فعل الذئب، وذلك أنه يجىء من هاهنا وهاهنا ليختل، فيتخيل للناظر أنه عدّة ذئاب.
وكتبت من خطّه: حدّثنا بشر بن موسى، حدثنا ابن النّطّاح قال: كنت عند الخليل بن أحمد فأقبل سيبويه، فقال: مرحبا مرحبا بزائر لا يملّ. فقال أبو عمر المخزومى- وكان كثير المجالسة للخليل: ما سمعت الخليل يقولها لأحد إلا لسيبويه.
كتبت من خط ابن عبد الملك: سمعت أبا العباس أحمد بن يحيى يقول: كان سيبويه يخطئ فى اسمه؛ يقول: سيبويه وسيبويه آخر؛ والكسائى يقول سيبويه وسيبويه آخر؛ لأنه أعجمىّ فلا يجرى، وزيلويه وزيلويه آخر، ويثنى زيلويهان','2','352',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (755,'و [يجمع] زيلوهات، لأن الجمع بالواو والنون للحيوان الذى يعقل من الذّكران، والألف والتاء لما يعقل من الإناث ولما لا يعقل، ولا يعرف باللام. وقد قيل زيلويه وذو زيلويه وذوات زيلويه ورأيت زيلويه وذوى زيلويه وذوات زيلويه.
ومن خطه: حدثنى أبو أحمد التّبريزىّ قال: حدثنا الفضل بن الحسن قال حدثنا عبيد الله بن معاذ العنبرىّ قال: جاء سيبويه إلى حمّاد بن سلمة فقال له:
أحدّثك هشام بن عروة عن أبيه فى رجل رعف فى الصلاة؟ فقال: أخطأت يا سيبويه! إنما هو رعف. «١» قال: فانصرف إلى الخليل فشكا إليه ما لقيه به حمّاد، فقال: صدق، ومثل حماد يقول هذا. ورعف يجوز إلا أنها ضعيفة، والكلام رعف.
قال أبو العباس المبرّد: كان الأخفش أكبر سنا من سيبويه؛ وكانا جميعا يطلبان، فجاءه الأخفش يناظره بعد أن برع، فقال له الأخفش: إنما ناظرتك لأستفيد لا غير، قال: أترانى أشك فى هذا! ومات سيبويه قبل جماعة قد كان أخذ عنهم كيونس وغيره؛ فأما يونس فمات فى سنة ثلاث وثمانين ومائة، ومات أبو زيد بعد موت سيبويه بنيّف وثلاثين سنة.
وكانت وفاة سيبويه- على ما ذكر محمد بن عمرو الجماز- فى سنة ثمانين ومائة «٢» بفارس، فى أيام الرشيد، وقبره بشيراز قصبة فارس. وكان قدم بغداذ، وجمع له النحويون فناظروه، فاستزلّ، فعاد إلى فارس ومات هناك.','2','353',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (756,'ونجم من أصحاب سيبويه أبو الحسن الأخفش سعيد بن مسعدة، وأبو علىّ محمد بن المستنير قطرب.
أنبأنا زيد بن الحسن بن زيد النحوىّ فيما كتب به إلى: أخبرنا أبو منصور عبد الرحمن القزاز، حدثنا أحمد بن علىّ من كتابه قال: «عمرو بن عثمان بن قنبر أبو بشر المعروف بسيبويه النحوىّ من أهل البصرة. كان يطلب الآثار والفقه، ثم صحب الخليل بن أحمد، فبرع فى النحو، وورد بغداذ، وجرت بينه وبين الكسائىّ وأصحابه مناظرة». «١»
أنبأنى الكندىّ أخبرنا القزاز حدثنا أحمد بن على فى كتابه: «أخبرنا محمد ابن عبد الواحد بن على البزاز، أنبأنا أبو عبيد الله محمد بن عمران المرزبانىّ، أخبرنى الصّولىّ وعبد الله بن جعفر قالا: حدثنا محمد بن يزيد النحوىّ قال: أبو بشر عمرو بن عثمان بن قنبر مولى بنى الحارث بن كعب بن عمرو بن جلد بن مالك بن أدد بن علة. قال المرزبانىّ: وحدثنا محمد بن يحيى، حدثنا محمد بن يزيد المبرّد قال: سيبويه يكنى أبا بشر وأبا الحسن، وهو من مولى بنى الحارث بن كعب.
قال المرزبانىّ: ويقال: وهو مولى آل الربيع بن زياد الحارثى. وتفسير سيبويه بالفارسية رائحة التفاح». «٢»
أنبأ الكندىّ إجازة قال: أخبرنا عبد الرحمن القزاز سماعا عليه قال حدثنا أحمد بن علىّ من كتابه: «أخبرنا العتيقىّ، حدثنا محمد بن العباس، أخبرنا سليمان ابن إسحاق الجلاب قال: وسمعته- يعنى إبراهيم الحربىّ- يقول: سمى سيبويه لأن وجنتيه كانتا كأنهما تفاح «٣»».','2','354',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (757,'وبالإسناد قال أحمد بن على: «أخبرنا القاضى أبو العلاء الواسطىّ قال: قال:
محمد بن جعفر بن هارون التميمىّ: كان سيبويه فى أول أيامه صحب الفقهاء وأهل الحديث، وكان يستملى على حمّاد بن سلمة، فلحن فى حرف، فعاتبه حمّاد، فأنف من ذلك، ولزم الخليل. وكان من أهل فارس، من البيضاء «١» ومنشؤه بالبصرة، واسمه عمرو بن عثمان بن قنبر، وكنيته أبو بشر، ولقبه سيبويه، وتفسيره ريح التفاح لأن «سيب»: التفاحة، و «ويه»: الريح. وكانت والدته ترقّصه وهو صغير بذلك». «٢»
وبالإسناد قال أحمد بن علىّ فى كتابه: «أخبرنى التّنوخىّ، حدثنا أبو الحسن أحمد بن يوسف بن يعقوب بن إسحاق بن البهلول التّنوخىّ، حدثنا أبو سعد داود ابن الهيثم بن إسحاق بن البهلول، حدثنا حماد بن إسحاق بن إسماعيل بن حماد بن زيد عن نصر بن على قال: برز من أصحاب الخليل أربعة: عمرو بن عثمان أبو بشر المعروف بسيبويه، والنّضر بن شميل، وعلى بن نصر، ومؤرّج السدوسىّ «٣»».
وبالإسناد قال أحمد بن على: «أخبرنا محمد بن عبد الواحد بن على، أخبرنا المرزبانىّ، أخبرنا أبو بكر الجرجانىّ، حدثنا محمد بن يزيد قال: كان سيبويه وحماد بن سلمة فى النحو أكثر من النّضر بن شميل والأخفش، وكان النّضر أعلم الأربعة باللغة والحديث «٤»».
وبالإسناد قال أحمد بن علىّ: «قرأت بخط القاضى أبى بكر بن الجعابىّ، وأخبرناه الصيمرىّ، حدثنا أحمد بن علىّ الصيرفىّ، حدثنا ابن الجعابىّ، حدثنا الفضل (هو ابن الحباب) عن ابن سلّام قال: كان سيبويه النحوىّ مولى بنى الحارث','2','355',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (758,'ابن كعب غاية الخلق فى النحو، وكتابه هو الإمام فيه، وكان الأخفش أخذ عنه، وكان أفهم الناس فى النحو». «١»
وبالإسناد قال أحمد بن علىّ فى كتابه: «أنبأنى القاضى أبو عبد الله محمد بن سلامة بن جعفر القضاعىّ المصرىّ؛ أخبرنا أبو يعقوب يوسف بن يعقوب بن إسماعيل ابن خرّوزاد النّجيرمىّ، أخبرنا أبو الحسين على بن أحمد المهلبىّ، أخبرنا أبو الحسين محمد بن عبد الرحمن الروذبارىّ قال: أخبرنا أبو بكر محمد بن عبد الملك التاريخىّ قال: حدّثنى المروزىّ (يعنى محمد بن يحيى بن سلمة) «٢»، وذكر حكاية الجاحظ التى تقدّمت فى إهداء الكتاب إلى محمد بن عبد الملك الزيات «٣».
وبالإسناد قال أحمد بن على فى كتابه: «أخبرنا القاضى أبو الطيب الطبرى وأحمد ابن عمر بن روح قالا: أخبرنا المعافى بن زكريا، حدّثنا محمد بن عبد الواحد، أخبرنا أبو الحسن بن كيسان قال: سهرت ليلة أدرس، قال: ثم نمت فرأيت جماعة من الجن يتذاكرون بالفقه والحديث والحساب والنحو والشعر، قلت:
أفيكم علماء؟ قالوا: نعم، قال: فقلت- من همّى بالنحو-: إلى من تميلون من النحويين؟ قالوا: إلى سيبويه. قال أبو عمر: فحدّثت بها أبا موسى- وكان يغيظه لحسد كان بينهما- فقال لى أبو موسى: إنما مالوا إليه لأن سيبويه من الجن» «٤»».
وبالإسناد قال أحمد بن على: «أخبرنا القاضى أبو العلاء الواسطى، أخبرنا محمد بن جعفر التميمى حدّثنا أبو العباس محمد بن الحسن، حدّثنا ثعلب عن سلمة قال: لما دخل من البصرة إلى مدينة السّلام أتى «٥» حلقة الكسائىّ وفيها غلمانه:','2','356',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (759,'الفرّاء وهشام ونحوهما، فقال الفراء للكسائى: لا تكلّمه ودعنا وإياه، فلما جلس سيبويه سأل عن مسائل والفرّاء يجيب، ثم قال له الفراء: ما تقول فى قول الشاعر:
نمتّ بقربى الزينبين كلاهما ... إليك وقربى خالد وسعيد
فلحق سيبويه حيرة «١» السؤال وقال: أريد أمضى لحاجة وأدخل. فلما خرج قال الفراء لأهل الحلقة: قد جاء وقت الانصراف فقوموا بنا، فقاموا. فخرج سيبويه فذكر علة «٢» البيت فرجع، فوجدهم قد انصرفوا «٣».
أنبأنا زيد بن الحسن أخبرنا عبد الرحمن، حدّثنا الخطيب من كتابه: «أخبرنا هلال بن المحسّن الكاتب، أخبرنا أحمد بن محمد بن الجراح الخزاز، وأخبرنا محمد ابن محمد بن على الوراق؛ حدّثنا المعافى بن زكريا، حدّثنا أبو بكر محمد بن القاسم الأنبارىّ، أخبرنا أبو بكر مؤدّب ولد الكيّس بن المتوكل، حدّثنا أبو بكر العبدىّ النحوىّ قال: لما قدم سيبويه إلى بغداد فناظر الكسائى وأصحابه فلم يظهر عليهم سأل: من يبذل [من] «٤» الملوك ويرغب فى النحو؟ فقيل له طلحة بن طاهر، فشخص إليه إلى خراسان، فلما انتهى إلى ساوة «٥» مرض مرضه الذى مات فيه، فتمثل عند الموت:
يؤمل دنيا لتبقى له ... فوافى المنية دون الأمل
حثيثا يروّى أصول الفسيل ... فعاش الفسيل ومات الرجل
وبالإسناد قال أحمد بن علىّ «أخبرنا «٦» عبد الله بن يحيى السكرىّ، أخبرنا جعفر ابن محمد بن أحمد بن الحكم الواسطىّ، حدّثنا أبو محمد الحسن بن على بن المتوكّل،','2','357',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (760,'أخبرنا أبو الحسن المدائنىّ قال: قال أبو عمرو بن يزيد: احتضر سيبويه النحوىّ فوضع رأسه فى حجر أخيه فأغمى عليه. قال: فدمعت عين أخيه، فأفاق، فرآه يبكى فقال:
وكنّا جميعا فرّق الدهر بيننا ... إلى الأمد الأقصى فمن يأمن الدهرا!
قال أبو إسحاق إبراهيم بن السرىّ الزجاج: إذا تأملت الأمثلة من كتاب سيبويه تبينت أنه أعلم الناس باللغة.
وقال أبو الحسن سعيد بن مسعدة الأخفش والمبرّد وثعلب: إن سيبويه لما قدم العراق على أبى على يحيى بن خالد البرمكىّ سأله عن خبره والحال التى ورد لها.
فقال: جئت لتجمع بينى وبين الكسائىّ، فقال له: لا تفعل، فإنه شيخ مدينة السّلام وقارئها ومؤدّب أمير المؤمنين، وكلّ من فى المصر له ومعه. فأبى إلا الجمع بينهما. فعرف الرشيد خبره، فأمره بالجمع بينهما، فوعده بيوم. فلما كان ذلك اليوم غدا سيبويه وحده إلى دار الرشيد، فوجد الفرّاء وهشاما والأحمر ومحمد بن سعدان قد سبقوه، فسأله الأحمر عن مائة مسألة، فأجابه عنها، فما أجابه بجواب إلا قال: أخطأت يا بصرىّ: فوجم [لذلك] «١» سيبويه وقال:
هذا سوء أدب. ووافى الكسائىّ- وقد شق أمره عليه- ومعه خلق كثير من العرب، فلما جلس قال له: يا بصرىّ، كيف تقول: خرجت فإذا زيد قائم؟
فقال: خرجت فإذا زيد قائم، فقال الكسائىّ: أيجوز: فإذا زيد قائما؟ قال:
لا. قال الكسائىّ: كيف تقول: قد كنت أظن أن العقرب أشدّ لسعة من الزنبور، فإذا هو هى، أو فإذا هو إياها؟. فقال سيبويه: فإذا هو هى؛ ولا يجوز النصب. فقال الكسائىّ: لحنت! وخطّأه الجميع. وقال الكسائىّ: العرب','2','358',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (761,'نرفع ذلك كلّه وتنصبه. فدفع سيبويه قوله. فقال يحيى بن خالد: قد اختلفتما وأنتما رئيسا بلديكما، فمن يحكم بينكما وهذا موضع مشكل؟ فقال الكسائىّ: هذه العرب ببابك؛ قد جمعتهم من كل أوب، ووفدت عليك من كل صقع، وهم فصحاء العرب، وقد قنع بهم أهل المصريين، وسمع منهم أهل الكوفة والبصرة، فيحضرون ويسألون. فقال يحيى وجعفر: قد أنصفت، وأمر بإحضارهم، فدخلوا، وفيهم أبو فقعس، وأبو ثروان، وأبو دثار، فسئلوا عن المسائل التى جرت، فتابعوا الكسائى. فأقبل يحيى على سيبويه فقال: قد تسمع أيها الرجل. فانصرف المجلس عن سيبويه، وأعطاه يحيى عشرة آلاف درهم وصرفه. فخرج وصرف وجهه إلى فارس، ولم يعد إلى البصرة، وأقام هناك إلى أن مات غما بالذّرب «١»، ولم يلبث إلا يسيرا.
قال أبو الحسن على بن سليمان الأخفش: وأصحاب سيبويه إلى هذه الغاية لا اختلاف بينهم أن الجواب كما قال سيبويه، وهو: «فإذا هو هى»؛ أى فإذا هو مثلها، وهذا موضع رفع، وليس هو موضع نصب. فإن قال قائل: فأنت تقول:
خرجت فإذا زيد قائم، وقائما، فتنصب «قائما»، فلم لم يجز: فإذا هو إياها؛ لأن «إيّا» للمنصوب و «هو» للمرفوع؟ والجواب فى هذا أن «قائما» انتصب على الحال وهو نكرة، و «إيّا» مع ما بعدها مما أضيفت إليه معرفة؛ والحال لا يكون إلا نكرة، فبطل «إيّاها» والحال لا يكون إلا نكرة، فكيف تقع «إياها» وهى معرفة فى موضع ما لا يكون إلا نكرة، وهذا موضع الرفع! وقال أصحاب سيبويه: الأعراب الذين شهدوا من أعراب الحطمة الذين كان الكسائى يقوم بهم ويأخذ عنهم. وقد حكى أبو زيد الأنصارىّ: قد كنت أظنّ أن العقرب أشدّ لسعة من الزنبور فإذا هو إيّاها.','2','359',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (762,'قال أبو سعيد الطّوال: رايت على قبر سيبويه رحمه الله هذه الأبيات مكتوبة، وهى لسليمان بن يزيد العدوىّ:
ذهب الأحبّة بعد طول تزاور ... ونأى المزار فأسلموك وأقشعوا
تركوك أوحش ما تكون بقفرة ... لم يؤنسوك وكربة لم يدفعوا
قضى القضاء وصرت صاحب حفرة ... عنك الأحبة أعرضوا وتصدّعوا
قال أبو عبد الله بن طاهر العسكرىّ: سيبويه اسم فارسىّ كأنه فى المعنى ثلاثون رائحة، وكان- فيما يقال- طيّب الرائحة

<span data-type="title" id=toc-583>٥١٦ - عمرو بن أبى عمرو الشّيبانىّ اللغوىّ [١]</span>
روى عن أبيه وغيره من أهل العلم، وأملى فى حياة أبيه سنين متعدّدة. وكان ثبتا، واسع الرواية مفيدا. وتوفّى سنة إحدى وثلاثين ومائتين.
سمع منه أحمد بن يحيى ثعلب كتاب النوادر لأبيه، وسمع منه أبو إسحاق الحربى الشيخ الصالح الزاهد رحمه الله ووثّقه كل واحد منهما.

<span data-type="title" id=toc-584>٥١٧ - عمرو بن كركرة أبو مالك الأعرابىّ [٢]</span>
دخل الحاضرة، وأخذ الناس اللغة عنه، وله كتاب فى خلق الإنسان جيد، وكان يعلّم فى البادية ويورّق فى الحضر. وكان مولى لبنى سعد؛ ويقال:
إنه كان يحفظ اللغة كلّها. وكان بصرىّ المذهب.
_________
[١] ترجمته فى بغية الوعاة ٣٦٥، تلخيص ابن مكتوم ١٩٤، وتهذيب اللغة للأزهرىّ ١: ١٠ وطبقات الزبيدىّ ١٤٣، ومعجم الأدباء ١٦: ٧٣ - ٧٤.
[٢] ترجمته فى بغية الوعاة ٣٦٧، وتلخيص ابن مكتوم ١٧٤، وطبقات الزبيدىّ ١١٢ - ١١٣، والفهرست ٤٤، وكشف الظنون ٧٢٢، ومعجم الأدباء ١٦: ١٣١ - ١٣٢.','2','360',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (763,'وقال الجاحظ: كان عمرو بن كركرة أبو مالك أحد الطّياب، «١» يزعم أن الأغنياء عند الله عز وجل أكرم من الفقراء. ويقول: إن فرعون عند الله أكرم من موسى. وكان يلتقم الحارّ الممتنع فلا يؤلمه. ومن تصنيفه: كتاب خلق الإنسان.
كتاب الخيل. قال أبو سعيد السكرىّ يوما: إن عمرو بن كركرة سمع من أبى عمرو بن العلاء وغيره.

<span data-type="title" id=toc-585>٥١٨ - عياض بن عوانة بن الحكم بن عوانة الكلبىّ النحوىّ [١]</span>
نزيل القيروان. وكان جدّه الحكم بن عوانة عالما بأيام العرب وأنسابها. وكان له قدر وحال. وولى ولايات كثيرة. وكان أبو عوانة عالما أديبا، وكان من أهل الكوفة، وعنه أخذ المهدى القيروانىّ كثيرا من النحو والشعر. وكانت المهالبة «٢» ولاة القيروان يكرّمونه. وروى عنه أنه قال: أقمت زمانا لا عهد لى بصلة روح ابن حاتم «٣»؛ حتى أرملت وأملقت، فركبت يوما بغلة وخرجت حتى رقيت على الكدية «٤» السوداء المطلّة على القنطرة- وكانت العرب تضع أثقالها فى دخول إفريقية
_________
[١] ترجمته فى بغية الوعاة ٣٦٨، وتلخيص ابن مكتوم ١٧٤، وطبقات الزبيدىّ ١٥٢ - ١٥٣','2','361',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (764,'بالقيروان «١»؛ فسمّيت القيروان؛ لأنها الأثقال فى كلام العرب- فإنى لعلى الكدية إذ أتى رسوله يشتد إلىّ؛ فقال: أجب يابن عوانة. فمضيت وما أحسب أن بعثته إلىّ ابتداء من غير أن أكون توسلت للوصول إليه إلا لأمر نمى إليه عنى من القول، فلما أتيت نزلت على بابه؛ فاستؤذن لى، فصعدت، وإنه لمع جاريته طلّة الهندية، فسلمت فأحسن الرد، فكأن روعى سكن. ثم قال: ما حالك؟ فقلت: مقلّ معدم أبو عيال، ولا مال. فقال: قد بلغت الغيث، فتخيّم؛ أى ألق خيمنك، فقلت:
الحمد لله؛ ذاك والله المأمول المرجوّ من الأمير. قال: مالك من العيال؟ قلت:
ثلاثون- قال: وكان أبو هريرة قهرمانه أكرم حضير ومشير- فقال: هم أكثر من ذلك إلى السبعين؛ بين قرابة وأصهار، يأملون كلهم رأى الأمير ويرجونه، وما هو بذى ماشية ولا غاشية ولا بتاجر. قال: قد أمرت لك بخمسمائة دينار، فادفعها إليه يا أبا هريرة، ومن القمح والشعير والتبن والطلاء والزيت والخلّ ما قال إنه يقوم به إلى رأس الحول. قال: فنزلنا، فوزن لى المال، وقال لأصحاب الخراج: احسبوا كم له فى هذه السنة ممّا أمر له، فجعلوا يعدون ويعقدون،- وكان السعر قد نزا «٢» - فقال لى أبو هريرة: هل لك إلى ما هو أقرب من هذا؟ تأخذ ثمنا.
قلت: ما أكره ذلك؛ فأعطانى خمسمائة دينار أخرى ومضيت، [وما أنسى طلّة] «٣» يومئذ وقولها: عالم البلد أهل لكل ما أسدى إليه، فانصرفت بأحسن حال.
وكان عياض يقرض الشعر ويجيده.
وذكر المرزبانى فى كتابه: «أن عوانة «٤» بن الحكم كان يقول لأخ له يقال له عياض:
نحوىّ، لا تعمّق فى النحو؛ فإنه لم يتعمق أحد فيه الإصار معلّما». قال: «فصار','2','362',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (765,'عياض ذلك معلّما بإفريقية لولد المهلّب «١». فعلى هذا الخبر يكون عياض أخا عوانة ابن الحكم، لا ولده. والله أعلم.

<span data-type="title" id=toc-586>٥١٩ - عياض بن موسى بن عياض اليحصبىّ المغربىّ [١]</span>
من أهل سبته، «٢» يكنى أبا الفضل، إمام عالم فاضل كامل مصنّف. أخذ عن مشايخ المغرب بالأندلس والعدوة «٣»، وجمع من الحديث كثيرا.
_________
[١] ترجمته فى الإحاطة فى تاريخ غرناطة ٢: ١٦٧، وبغية الملتمس للضبى ٤٢٥، وتاج العروس (حصب) وتاريخ الإسلام للذهبى (وفيات سنة ٥٤٤)، وتاريخ أبى الفدا ٣: ٢٢، وتاريخ قضاة الأندلس للنباهى ١٠١، وتاريخ ابن كثير ١٢: ٢٢٥، وتذكرة الحفاظ للذهبى ٤: ٩٦ - ٩٩، وتلخيص ابن مكتوم ١٧٥ - ١٧٧، وابن خلكان ١: ٣٩٢ - ٣٩٣، والديباج المذهب ١٦٨ - ١٧٢، وروضات الجنات ٥٠٦ - ٥٠٧، وشذرات الذهب ٤: ١٣٨ - ١٣٩، والصلة لابن بشكوال ١: ٤٤٦ - ٤٤٧، وكشف الظنون ١٢٧، ١٥٨، ٢٤٨، ٣٩٥، ٥٩٣، ٥٧٧، ١٠٥٢، ١١٨٦، ١٢١١، ١٧٧٩، ١٩٦١، والمعجم لابن أبار ٢٩٤ - ٢٩٨، والنجوم الزاهرة ٥: ٢٨٥ - ٢٨٦، والوافى بالوفيات ج ٥ مجلد ٣: ٥٩٦ - ٥٩٧. ووضع أحمد بن محمد المقرى كتابا كبيرا فى سيرته أسماه: أزهار الرياض فى أخبار القاضى عياض أداره حول هذه الأبواب: روضة الورد فى أوّلية هذا العالم الفرد، وروضة الأقحوان فى ذكر حاله فى المنشأ والعنفوان.
وروضة البهار فى ذكر جملة من شيوخه الذين فضلهم أظهر من شمس النهار، وروضة المنثور فى بعض ما له من منظوم ومنثور، وروضة النسرين فى تصانيفه العديمة النظير والقرين، وروضة الآس فى وفاته وما قابله به الدهر الذى ليس لجرحه من آس، وروضة الشقيق فى جمل من فوائده ولمع من فرائده المنظومة نظم الدرّ والعقيق، وروضة النيلوفر فى ثناء الناس عليه وذكر بعض مناقبه التى هى أعطر من المسك الأذفر؛ (وقد طبع منه ثلاثة أجزاء فى مطبعة لجنة التأليف والترجمة بمصر سنة ١٣٥٨). واليحصبىّ، ضبطه ابن خلكان بفتح الياء وضم الصاد وفتحها وكسرها: منسوب إلى يحصب بن مالك بن زيد بن سهل بن عمرو بن قيس، قبيلة من حمير.','2','363',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (766,'وهو من أهل التّفنّن فى العلم والذكاء والفطنة والفهم. واستقضى ببلده مدّة طويلة؛ حمدت سيرته فيها. ثم نقل عنها إلى قضاء غرناطة «١»؛ فلم يطل مقاسه بها، ورحل عنها إلى قرطبة فى ربيع الآخر سنة إحدى وثلاثين وخمسمائة. وأخذ الناس بها عنه بعض ما عنده. وكانت ولادته فى نصف شعبان من سنة ست وعشرين وأربعمائة، وتوفى بمرّاكش مغرّبا عن قرطبة وسط سنة أربع وأربعين وخمسمائة.
وله من التصانيف شرح خبر أم زرع «٢» مجلد. كتاب مشارق الأنوار «٣» كبير.
تمام المعلم «٤» فى شرح كتاب مسلم كبير «٥».','2','364',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (767,'<span data-type="title" id=toc-587>٥٢٠ - عبّاس بن ناصح الأندلسىّ النحوى [١]</span>
ساكن الجزيرة الخضراء «١». كان من أهل العلم والعربيّة، ومن ذوى الفصاحة فى شعره ولسانه، ويذهب فى شعره مذاهب العرب، وولى قضاء شذونة «٢» والجزيرة ووليها ابنه عبد الوهاب بن عباس، ثم ابنه محمد بن عبد الوهاب.
قال عبد الرحمن بن عباس بن ناصح: كان أبى لا يقدم من المشرق قادم إلا كشفه عمّن نجم من الشعراء بعد ابن هرمة «٣»؛ حتى أتاه رجل من التجار، فأعلمه بظهور الحسن بن هانئ وارتحاله من البصرة إلى بغداذ، والمحلّ الذى ناله من الأمين وبنى برمك، وأتاه من شعره بقصيدتين؛ إحداهما قوله «٤»:
جريت مع الصّبا طلق الجموح «٥»
والثانية «٦»:
أما ترى الشّمس حلّت الحملا «٧»
فقال: إنّ هذا أشعر الجنّ والإنس؛ لا يحبسنى عنه حابس، وتجهّز نحو المشرق.
قال: فلما دخلت بغداذ سألت عن منزل الحسن بن هانئ، فأرشدت إليه، فإذا
_________
[١] ترجمته فى بغية الوعاة ٢٧٦، وتاريخ علماء الأندلس ١: ٢٤٥، وتلخيص ابن مكتوم ١٧٧، وطبقات الزبيدىّ ١: ٢٤٥، ١٧٧ - ١٧٩ وطبقات ابن قاضى شهبة ٢٠: ١٦ - ١٧ ...','2','365',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (768,'قصر عليه حفدة وخدّام وحوله أكثر متأدّبى بغداذ، فسلّمت وجلست حيث انتهى بى المجلس، وأنا فى هيئة السّفر، فلما كاد المجلس ينقضى قال: من الرجل؟ قلت:
باغى أدب، قال: أتروى من شعر أبى المخشى «١» الذى قاله عندكم؟ قلت: نعم، قال: أنشدنى، فأنشدته: «كنت الذرى إلى الذرى» «٢» قال: هذا الذى طلبته الشعراء فأضلّته. ثم قال: أنشدنى لأبى الأجرب، «٣» فأنشدته، ثم قال أنشدنى لبكر الكنانىّ فأنشدته، ثم قال: شاعر البلد اليوم عباس بن ناصح؟ قلت: نعم، قال: أنشدنى له، فأنشدته:
فأدت القريض ومن ذا فأد
فقال لى: عبّاس! قلت: نعم، فنهض إلىّ فتلقيته، فعانقنى وضمّنى إلى نفسه، وانحرف لى عن مجلسه، فقال له من حضر المجلس: من أين عرفته أصلحك الله فى قسم بيت؟ قال: إنى رأيته عند إنشاده لغيره، فرأيته لا يبالى ما حدث من استحسان واستقباح، فلما أنشدنى لنفسه استبنت عليه وجمة، فقلت إنه صاحب','2','366',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (769,'الشعر. قال عباس: ثم أتممت الشعر، فقال: هذا أشعر العرب، ثم نقلنى إلى نفسه فى ضيافته عاما. ثم قدم عبّاس الأندلسى، فتكرر على الحكم بن هشام بالمدح، ثم تعرض للخدمة فاستقضاه على بلده.

<span data-type="title" id=toc-588>٥٢١ - العباس بن الفرج أبو الفضل الرياشىّ [١]</span>
مولى محمد بن سليمان بن على بن عبد الله بن العباس بن عبد المطلب. من أهل البصرة. سمع الأصمعىّ وأبا معمر المقعد «١» وعمرو بن مرزوق، «٢» وروى عنه أبو إسحاق إبراهيم بن إسحاق الحربىّ وأبو بكر بن أبى الدنيا وأبو بكر محمد بن أبى الأزهر النحوىّ وأبو بكر بن دريد وأبو روق الهزانىّ «٣» وغيرهم.
_________
[١] ترجمته فى أخبار النحويين البصريين للسيرافى ٨٩ - ٩٣، وإشارة التعيين الورقة ٢٣، والأنساب ٢٦٤ ب، وبغية الوعاة ٢٧٥ - ٢٧٦، وتاريخ ابن الأثير ٥: ٣٦٤، وتاريخ بغداد ١٢: ١٣٨ - ١٤٠، وتاريخ أبى الفدا ٢: ٤٨، وتاريخ ابن كثير ١١: ٢٩ - ٣٠، وتلخيص ابن مكتوم ١٧٨، وابن خلكان ١: ٢٤٦، وشذرات الذهب ٢: ١٣٦، وطبقات الزبيدىّ ٦٧ - ٦٩، وطبقات ابن قاضى شهبة ٢: ١٤ - ١٥، والفلاكة والمفلوكين ١١٦، والفهرست ٥٨، واللباب فى الأنساب ١: ٤٨٤، ومعجم الأدباء ١٢: ٤٤ - ٤٦، والمنتظم (وفيات سنة ٢٥٧)، والنجوم الزاهرة ٣: ٢٧ - ٢٨، ونزهة الألباء ٢٦٢ - ٢٦٤. والرياشىّ بكسر الراء وفتح الياء: منسوب إلى رياش: وهو اسم رجل من جذام، كان والد المنتسب إليه عبدا له فنسب اليه.','2','367',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (770,'وكان من أهل الأدب وعلم النحو بمحل عال. وكان يحفظ كتب أبى زيد والأصمعىّ كلها، وقرأ على أبى عثمان المازنىّ كتاب سيبويه. وكان المازنىّ يقول: قرأ علىّ الرياشىّ الكتاب وهو أعلم به منى. وقدم بغداذ، وحدّث بها، وكان ثقة.
جاء أبو شراعة»
إلى الرّياشىّ فقال له: إن أبا العباس الأعرج قد هجاك، فقال:
إن الرياشىّ عباسا تعلّم بى ... حوك القصيد وهذا أعجب العجب
يهدى لى الشّعر جبنا من سفاهته ... كالتمر يهدى لذات الليف والكرب «٢»
فقال الرياشىّ: هلا ردد ثم عنى، أما سمعتم قول أبى نواس «٣»:
لا أعير الدهر سمعى ... أن يعيبوا لى حبيبا
لا ولا أحفظ عندى ... للأخلّاء العيوبا
فإذا ما كان كون ... قمت بالغيب خطيبا
احفظ الإخوان يوما ... يحفظوا منك المغيبا «٤»
قال أبو سعيد السيرافىّ: «الرّياشىّ أبو الفضل عبّاس بن الفرج، مولى محمد بن سليمان بن على الهاشمىّ. ورياش رجل من جذام؛ كان أبو العباس عبدا له فبقى عليه نسبه إلى رياش. وكان عالما باللّغة والشعر، كثير الرواية عن الأصمعىّ. وروى أيضا عن غيره، وقد أخذ عنه أبو العباس محمد بن يزيد- يعنى المبرّد- وأبو بكر ابن دريد» «٥». وقال «٦» أبو بكر بن أبى الأزهر- وكان عنده أخبار الرياشىّ- كنا','2','368',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (771,'نراه يجيء إلى [أبى] العباس المبرّد فى قدمة قدمها من البصرة، وقد لقيه أبو العباس ثعلب، وكان يقدّمه ويفضّله.
قال أبو سعيد: «ومات الرّياشىّ- فيما حدّثنى به أبو بكر بن دريد- سنة سبع وخمسين ومائتين بالبصرة، قتلته الزّنج «١»». دخلوا عليه المسجد بأسيافهم والرّياشىّ قائم يصلى الضّحا، فضربوه بأسيافهم، وقالوا: هات المال، فجعل يقول: أىّ مال، أىّ مال! حتى مات. فلما خرج الزّنج عن البصرة، ودخلها من فرّ عنها، فمرّ منهم من مرّ ببنى مازن الطحّانين- وهناك كان ينزل الرّياشىّ- فدخلوا مسجده، فإذا به ملقى وهو مستقبل القبلة، كأنما وجّه إليها وإذا شملته تحرّكها الريح قد مزّقت، وإذا جميع خلقه صحيح سوىّ، لم تنشقّ له بطن، ولم يتغير له حال، إلّا أن جلده قد لصق بعظمه ويبس. وذلك بعد مقتله بسنين- يرحمنا الله وإياه.
وذكر محمد بن موسى الحمّادى قال: رأيت الرياشىّ فى مسجده هذا، فرأيت رجلا طوالا آدم اللون، عليه قلنسوة، وحين قدم بغداذ نزل على يعقوب بن شيبة، فأقام على ابن السرىّ صاحب الكلبىّ شهرا، حتى كتب عنه ما أراد، ثم رجع إلى البصرة.
قال المازنىّ: قرأ الرياشىّ علىّ كتاب سيبويه فما بلغ النصف منه حتى كان أعلم به منى.','2','369',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (772,'قال مروان بن عبد الملك: سمعت العباس بن الفرج الرياشىّ يقول:
حفظت كتب أبى زيد ودرستها إلا أنى لم أجالسه مجالستى للأصمعىّ، وأما كتب الأصمعىّ فإنى حفظتها لكثرة ما كانت تتردّد على سمعى لطول مجالستى له. قال:
وكنت أقرأ على أبى زيد، ولعلّ حفظى قريب من حفظه. قال: وقال لى يوما:
عمن تأخذ؟ قلت له: عن فلان، فاجتمعنا عنده يوما أنا وذلك، فتناظرنا، فقال لى: تقول لى إنك تأخذ عن هذا وأنت أعلم منه!.
وقال الخشنىّ: كان المازنىّ فى الإعراب، وأبو حاتم فى الشعر والرواية، وكان الرياشىّ فى الجميع. وكان أهل البصرة إذا اختلفوا فى شىء قالوا ما قال فيه أبو الفضل الرياشىّ، انقيادا لفضله وروايته. وكان من أهل الفضل، لا تخرج البصرة مثل الرياشىّ.
وحمل الرياشىّ إلى سرّ من رأى فى أيام المتوكل، لتولّى قضاء البصرة فاستعفى، وقال شعرا مدح به المتوكل، وذكر فيه خلوّ مسجده منه، فأعفاه وأعطاه ووسع له وأعاده. وقرأ عليه الفتح بن خاقان الوزير، وأعطاه مالا جسيما، وعاد إلى البصرة.
وقال يحيى: رأيت أبا حاتم يعظم الرّياشىّ تعظيما كثيرا، وأبو حاتم أسنّ منه.
وسئل الرّياشىّ فى عقيب ذى الحجة سنة أربع وخمسين ومائتين: كم تعد؟ قال:
أظنّ سبعا وسبعين. وكانت قتلته فى شوّال سنة سبع وخمسين ومائتين- رحمه الله.
قال أبو الفتح محمد بن جعفر النحوى: قرأ الرّياشىّ النصف الأوّل من كتاب سيبويه على المازنى. وقال أبو بكر بن دريد: رأيت رجلا من الورّاقين بالبصرة يقرأ كتاب إصلاح المنطق لابن السكّيت، ويقدّم الكوفيين، فقلت للرّياشىّ-','2','370',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (773,'وكان قاعدا فى الوراقين- بما قال، فقال: إنما أخذنا اللغة من حرشة «١» الضّباب وأكلة اليرابيع «٢»، وأخذوا اللغة من أهل السواد، وأكلة الكواميخ «٣» والشواريز. «٤»
وله من الكتب: كتاب الخيل. كتاب الإبل. كتاب ما اختلفت أسماؤه من كلام العرب.
قال أحمد بن يحيى ثعلب: كنت أصير إلى الرّياشىّ لأسمع ما كان يرويه، فقال لى يوما: كيف تروى هذا البيت: «بازل عامين»، «أو بازل»، أو [بازل]؟
يعنى قول الشاعر «٥»:
ما تنقم الحرب العوان «٦» منّى ... بازل «٧» عامين حديث سنّى
لمثل هذا ولدتنى أمّى
قال ثعلب: تقول لى هذا فى العربيّة؛ إنما أصير إليك لهذه المقطّعات والخرافات! وقلت له: يروى «بازل عامين»، «وبازل عامين»، [وبازل عامين]، فأمسك.
الرفع على الاستئناف، والخفض على الإتباع، والنصب على الحال.','2','371',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (774,'وقال ثعلب أيضا: قدم الرياشى بغداذ فى سنة ثلاثين ومائتين، فأتيته لأكتب عنه، فقال: أسألك عن مسألة؟ فقلت: قل، قال: «نعم الرجل يقوم»؛ قلت:
الكسائىّ يضمر «رجلا يقوم» والفرّاء لا يضمر، لأن «نعم» عنده اسم، وعند الكسائىّ فعل، و «يقوم» من صلة الرجل، وسيبويه يقول: إنه ترجمة. قال:
صدقت، قلت: فتقول: «يقوم نعم الرجل»؟ قال: نعم، قلت: هذا مخالف لقول صاحبك «١»، والكسائىّ والفراء يجيزانه؛ لأن الترجمة إذا تقدّمت فسد «٢» الكلام؛ لأنه إنما أتى بها فى آخره، ليظهر معنى الكلام؛ فاقصد لما أتيت له.
ثم قال: إنى أسألك عن مسألة سألنا عنها الأخفش: لم قالت العرب:
«نعم الرجلان أخواك»، فثنّوا «الرجل» وهو جنس من الرجال على «أخواك»؟
والمعبّر «٣» عن الجنس لا يثنّى ولا يجمع؟ فقلت له: لمّا صرف الفعل إلى الرجل جرى مجرى الفاعل، فثنّى وجمع لذلك. فقال: هكذا قال الأخفش. قلت له: وجالست الأخفش؟ قال: نعم، وأنا أرى أنّى أعلم منه. فما أعجبتنى هذه الكلمة من الرّياشىّ؛ لأنى وجدته أفرط فيها. فجاريته الأخبار والأشعار وأيام الناس، ففجّرت به ثبج بحر.
وجرت مناظرة فى مجلس فيه الأخفش سعيد والعبّاس بن الفرج الرّياشىّ، فقال الأخفش: إن «منذ» إذا رفعت بها كان اسما مبتدأ وما بعده خبره، وإذا جررت بها كان حرفا جاء لمعنى. فقال له الرياشى: فلم لا تكون فى حال ما يرفع ويجر جميعا اسما؛ كما تقول: «ضارب زيدا، [وضارب زيد]»، فقد رأينا الاسم ينصب الاسم ويجره. فلم يأت الأخفش بمقنع. فلما سمع المازنىّ هذا الكلام [قال:]','2','372',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (775,'أقول أنا: إنه لا يشبه الأسماء، وذلك لأنى لم أر الأسماء على هذه الهيئة، فقد رأينا الأسماء المبتدأة تزول عمّا هى عليه، ولا تلزم موضعا واحدا، لا يغيّر عن مكانه الذى هو فيه؛ وإنما هو الحرف الذى جاء لمعنى؛ فهو حرف جاء لمعنى؛ مثل: أين، وكيف ألزم شيئا واحدا.
قال أبو يعلى بن أبى زرعة: فقلت لأبى عثمان: حرف جاء لمعنى، هل رأيته قط يعمل عملين: جرّا ورفعا؟ فقال: قد رأيته يعمل عملين؛ ينصب ويجر؛ مثل قولك: «أتانى القوم خلا زيد، وخلا زيدا».
قال أبو عثمان: أقول العوامل [و] هى الأفعال؛ إنما ترفع الشىء الواحد؛ ولم أرها رفعت شيئين إلا بحرف عطف؛ مثل: «قام زيد وعمرو»؛ ولا يجوز أن ترفع بالابتداء المبتدأ وخبره. قلنا له: فإنّ الصّفة هى مرتفعة أيضا إذا قلت:
«قام زيد العاقل»، فقد رفعت شيئا بغير حرف عطف، فقال: الموصوف قد اشتمل على الصفة. قال أبو عثمان: ألا [ترى] أنك لو حملت كوزا فيه ماء كنت قد حملت الماء؟ وأهل بغداذ يقولون: «إن زيدا منطلق» إنّ ناصب «زيد» «إنّ» و «منطلق» لم تعمل فيه «إن»، والحجة عليهم فى ذلك أن تقول: «إن زيدا لمنطلق»، وهذه اللام لا تدخل إلا على ما تعمل فيه «إن».
قال أبو عثمان المازنىّ: سألنى الرياشىّ: «الله». ما أنكرت أن تكون «الإله» فخفف فقيل: «أللاه»، ثم أدغمت اللام فى اللام الساكنة؛ كما أجزت فى «الناس» أن يكون تخفيفا من «الأناس»، ثم أدغمت؟ فقلت له: من قبل أن «الناس» على معنى «الأناس»، وكذلك كل شىء خففت من الهمزة فهو على معناه مخففا، وأنت إذا قلت «اللاه»، فليس يعلم الله عزّ وجلّ، فلو كان «الله» هو «الإله» مخففا لبقى على معناه، فلما جاء «الله» على غير معنى «إلاه» علمنا أن هذا ليس مخففا.','2','373',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (776,'<span data-type="title" id=toc-589>٥٢٢ - العباس بن رداد بن عمر البندنيجىّ أبو الفضل النحوىّ [١]</span>
كانت له معرفة حسنة بالنحو. قرأ على أبى الغنائم حبشى بن محمد الواسطىّ الضرير النحوىّ، ثم على أبى محمد عبد الله بن أحمد بن أحمد بن الخشاب، وأقرأ الناس، وتخرّج به جماعة، ومما أنشدنيه العباس لبعضهم:
أقبلت فى غلالة زرقاء ... لازرديّة كلون السماء
فتأملت فى الغلالة منها ... قمر الصيف فى ليالى الشتاء

<span data-type="title" id=toc-590>٥٢٣ - عيسى بن عمر البصرىّ الثقفىّ المقرئ النحوىّ [٢]</span>
اختلف فى نسبه، فقيل هو مولى لبنى مخزوم، وهو من ولد الحكم بن عبد الله الأعرج «١» الذى روى الحديث. وقيل كان من ثقيف، لخالد بن الوليد، وقيل هو مولى خالد بن الوليد المخزومىّ ونزل فى ثقيف.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٧٩. والبندنيجىّ، بفتح الياء وسكون النون وفتح الدال وكسر النون: منسوب إلى بندنيجين، وهى بلدة قريبة من بغداد.
[٢] ترجمته فى أخبار النحويين البصريين للسيرافى ٣١ - ٣٣، وإشارة التعيين الورقة ٣٩ - ٤٠، وبغية الوعاة ٢٧٠، وتاريخ ابن الأثير ٥: ٢٨، وتاريخ أبى الفدا ٢: ٥، وتاريخ ابن كثير ١٠:
١٠٥ - ١٠٦، وتلخيص ابن مكتوم ١٧٩ - ١٨٠، وابن خلكان ١: ٣٩٣ - ٣٩٤، وروضات الجنات ٥٥٧ - ٥٥٨، وشذرات الذهب ١: ٢٢٤ - ٢٢٥، وطبقات الزبيدىّ ١٧ - ٢١، وطبقات ابن قاضى شهبة ٢: ٢١٢ - ٢١٥، وطبقات القراء لابن الجزرىّ ١: ٦١٣، وعيون التواريخ (وفيات ١٤٩) والفهرست ٤١ - ٤٢، ومراتب النحويين ٣٢، ومرآة الجنان ١: ٣٠٧ - ٣٠٩، ومسالك الأبصار ج ٤ مجلد ٢: ٢٧٠ - ٢٧١، والمعارف لابن قتيبة ٢٣٥، ومعجم الأدباء ١٦: ١٤٦ - ١٥٠، والنجوم الزاهرة ٢: ١١، ونزهة الألباء ٢٥ - ٢٩، والوافى بالوفيات ج ٥ مجلد ٣: ٦٤٣ - ٦٤٥.','2','374',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (777,'وكان من قرّاء أهل البصرة ونحاتها. وكان عالما، أخذ عن ابن إسحاق.
وكان عيسى بن عمر فى طبقة أبى عمرو بن العلاء، وعنه أخذ الخليل بن أحمد.
وله فى النحو نيّف وسبعون تصنيفا، عدمت؛ ومنها تصنيفان كبيران؛ اسم أحدهما الإكمال والآخر الجامع. ويقال إن الجامع هو كتاب سيبويه، زاد فيه وحشاه. وسأل مشايخه عن مسائل منه أشكلت عليه فذكرت له فأضافها، وإنه لما أحضره إلى الخليل بن أحمد ليقرأه عليه عرفه الخليل، وأنشد:
بطل النحو جميعا كلّه ... غير ما أحدث «١» عيسى بن عمر
ذاك (إكمال) وهذا (جامع) ... فيهما للناس شمس وقمر
فأشار إلى الجامع بما يشار به إلى الحاضر، وهى لفظة هذا.
وقال أحد العلماء لعيسى بن عمر: أخبرنى عن هذا الذى وضعته فى كتابك؛ يدخل فيه كلام العرب كلّه؟ قال: لا، قلت: فمن تكلم خلافك واحتذى ما كانت العرب تتكلم به تراه مخطئا؟ قال: لا، قلت: فما ينفع كتابك! وقال محمد بن سلام الجمحىّ: «كان عيسى بن عمر ينزع إلى النصب إذا اختلفت العرب». «٢» ويقال: إنّ أبا الأسود لم يضع من النحو إلا باب الفاعل والمفعول فقط، وإن عيسى بن عمر وضع كتابه على الأكثر وبوّبه وهذّبه، وسمى ما شدّ عن الأكثر لغات. وكان يطعن على العرب، ويخطّئ المشاهير منهم؛ مثل النابغة فى بعض أشعاره «٣» وغيره.','2','375',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (778,'وكان صاحب تقعير فى كلامه، واستعمال للغريب فيه. وكان بعض جلساء خالد بن عبد الله القسرىّ «١» قد استودعه وديعة، فنمى ذلك إلى يوسف «٢» بن عمر، فكتب إلى واليه بالبصرة يأمره أن يحمله إليه مقيّدا، فدعى به، ودعى بالحداد وأمره بتقييده، فلما قيّد قال له الوالى: لا بأس عليك؛ إنما أرادك الأمير لتأديب ولده. قال: فما بال القيد إذن! فبقيت هذه الكلمة مثلا بالبصرة. فلما أتى به يوسف «٣» بن عمر سأله عن الوديعة فأنكر، فأمر به فضرب بالسياط؛ فلما أخذه السوط جزع، فقال: أيها الأمير؛ إنما كانت ثيابا فى أسيفاط؛ «٤» فتسلّمها عشّارك «٥»، فرفع السوط عنه، ووكل به حتى أخذت الوديعة منه. وأدركه بعد ذلك ضيق النفس؛ فكان يداويه بإجّاص يابس وسكّر يضعه فى فيه فيسكّن ما به، وكان دقيق الصوت.
قال يحيى بن معين: عيسى بن عمر بصرىّ ثقة. وجمع الحسن بن قحطبة «٦» عند مقدمه مدينة السلام الكسائىّ والأصمعىّ وعيسى بن عمر؛ فألقى عيسى بن عمر على','2','376',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (779,'الكسائىّ مسألة، فذهب الكسائىّ يوجّه احتمالاتها، فقال له عيسى: عافاك الله! إنما أريد كلام العرب، وليس هذا الذى تأتى به بكلامها «١».
قال الأصمعىّ: وجاء عيسى بن عمر يوما إلى أبى عمرو بن العلاء فقال له:
مررت بقنطرة قرّة، فلقينى بعيران مقرونان فى قرن، فما شعرت شعرة حتى وقع قرانهما فى عنقى، فلبج «٢» بى، فافرنقع عنى والناس قيام ينظرون. قال: فكاد أبو عمرو ينشقّ غيظا من فصاحته.
وذكر عنه أن ضيق النفس الذى كان به أدركه يوما وهو فى السوق فوقع، ودار الناس حوله يقولون: مصروع، مصروع! فبين قارئ ومعوّذ من الجانّ. فلما أفاق من غشيته نظر إلى ازدحامهم فقال لهم: ما لى أراكم تتكأكئون علىّ تكأكؤكم على ذى جنّة! افرنقعوا. فسمع أحد الجمع وهو يقول: إن جنّيّه هذا يتكلم بالهندية.
توفى عيسى بن عمر سنة تسع وأربعين ومائة قبل أبى عمرو بن العلاء بخمس سنين أو ست.

<span data-type="title" id=toc-591>٥٢٤ - عيسى بن أبى جرثومة أبو الأصبغ الخولانىّ الأندلسىّ النحوىّ [١]</span>
كان عالما بالنحو واللغة والحساب والعروض والقراءات، مفيدا لكل ذلك، متصدّرا للإفادة. وكان فاضلا خيّرا ديّنا شاعرا محسنا.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٨٠.','2','377',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (780,'<span data-type="title" id=toc-592>٥٢٥ - عيسى بن يللبخت الجزولىّ المغربىّ [١]</span>
البربرىّ النسب. وجزولة قبيلة من قبائل البربر مشهورة الذكر هناك.
وربما قالوا كزولة (بالكاف). أبو موسى.
رجل فاضل كامل ديّن خيّر. رحل من المغرب إلى المشرق وحج وعاد إلى مصر، وقرأ مذهب مالك والأصول على الفقيه أبى المنصور ظافر المالكىّ الأصولىّ «١». وقرأ النحو على الشيخ أبى محمد عبد الله بن برىّ النحوى المصرىّ الدار، إمام وقته، ولما قرأ عليه كتاب الجمل للزجّاجى سأله عن مسائل على أبواب الكتاب، فأجابه عنها، وجرى بحث فيها بين الطلبة أنتج قولة علقها الجزولىّ مفردة، فجاءت كالمقدّمة، فيها كلام غامض، وعقود لطيفة، وإشارات إلى أصول صناعة النحو غريبة. ولما عاد إلى المغرب نقلها الناس عنه، واستفادوها منه. وبلغنى أنه كان إذا سئل عنها: هل هى من تصنيفك؟ قال: لا؛ لأنه كان متورّعا. ولما كانت هذه من نتائج خواطر الجماعة عند البحث فى مجلس الشيخ أبى محمد بن برىّ، ومن كلام ابن برىّ لم يسغ له أن يقول: هى من كلامى وتصنيفى، وإنّما هى منسوبة إليه؛ لأنها من استفادته ومساجلته وكونه أثبتها دون الجماعة.
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٩، وبغية الوعاة ٣٦٩ - ٣٧٠، وتلخيص ابن مكتوم ١٨٠ - ١٨١، وابن خلكان ١: ٣٩٤ - ٣٩٥، وشذرات الذهب ٥: ٢٦، وطبقات ابن قاضى شهبة ٢: ٢١١ - ٢١٢، والفلاكة والمفلوكين ٩١ - ٩٣، وكشف الظنون ١٨٠٠ - ١٨٠١، والوافى بالوفيات ج ٥ مجلد ٣: ٦٣٣. ويللبخت، ضبطه ابن خلكان:
«بفتح الياء واللام الأولى وسكون الثانية وفتح الباء وسكون الخاء». وهو اسم بربرىّ.','2','378',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (781,'وأخبرنى صديقنا النحوىّ اللورقىّ الأندلسىّ قال: اجتزت به فى طريقى ببعض مدن العدوة- وأسماها لى وأنسيتها أنا- قال: وقد كان مقيما بها، فأرشدت إلى منزله، فدققت عليه بابه، فخرج إلىّ فرأيته فى هيئة متألّه، فسألته عن مسألة فى مقدّمته، أظنه قال فى باب التعجب أو فى باب الحكاية- السهو منّى- قال: فأجابنى عنها، وتركته وانصرفت.
وقد عنى الناس بشرح هذه المقدّمة؛ فممن شرحها «١» صديقنا هذا المعلّم وأجاد.
وشرحها أبو علىّ عمر الشّلوبينىّ،»
نزيل إشبيليّة ونحويّها، ولم يطل، وشرحها نحوىّ من أهل العدوة من أرض المغرب، ووصل شرحه إلى الشأم وقال: من وقف عليها: لم يأت بطائل. وشرحها شاب نحوىّ من أهل جيّان من الأندلس متصدّر بحلب لإفادة هذا الشأن، فجمع فيه بعض أقوال هؤلاء المقدم ذكرهم وأحسن فى الإيجاز «٣».
ومات الجزولىّ- رحمه الله- بالمغرب، فى حدود سنة خمس وستمائة؛ قبلها أو بعدها بقليل؛ والله أعلم.
واجتمعت بالمعلّم أبى القاسم بن الموفّق النحوىّ اللورقىّ الأندلسىّ المقدّم ذكره، وسألته عنه ثانية فقال: كان اجتماعى به بتونس، وقدم فى صحبة صاحب المغرب لقصد المهدىّ وقال: كان الجزولىّ مزوارا، ومعنى المزوار بالبربرية مقدّم جماعة. وسألته عن المسألة التى سأله عنها، فقال: هى فى التعجب من مقدّمته، وهى المثلية أو المثليّة «بالتحريك» قال: فقال له: هى المثليّة. وسألته عن سنة اجتماعه به','2','379',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (782,'فقال: كان ذلك فى أوائل سنة إحدى وستمائة. ثم سألته: هل حقّقت وفاته فقال: لم أحقق السنة؛ ولكن أخبرت أنه لم يعش بعدى إلا سنيّات قلائل، ما بين الثلاث إلى الأربع، فيكون تقدير وفاته ما ذكرناه «١».

<span data-type="title" id=toc-593>٥٢٦ - عيسى بن المعلّى الحجة بن مسلمة الرافقى اللغوىّ النحوىّ الشاعر [١]</span>
عربىّ الأصل. كان كثير الشعر، يمدح أمراء الدولة الأتابكية والنورية، ومدح مظفّر الدين «٢» بن زين الدين، وهو ممن فات العماد ذكره فى الخريدة، وصنّف كتبا فى اللغة لطافا، وصنف فى النحو كتابا لطيفا سماه المعونة، وشرحه بكتاب سماه القرينة فى شرح المعونة «٣».
وكان يحضر إلى حلب، ويمدح المعمّمين والمشربشين؛ فممن مدح الرئيس صفىّ الدين طارق بن أبى غانم بن الطربزة بقصائد شهد بها ديوانه. نقلت من خط ولده الصارم إبراهيم بن الحجة هذا على ظهر كتاب القرينة فى شرح المعونة:
«توفى والدى- رحمه الله- ليلة الجمعة ثالث ربيع الآخر سنة خمس وستمائة».

<span data-type="title" id=toc-594>٥٢٧ - عطاء بن أبى الأسود الدؤلىّ النحوىّ [٢]</span>
عالم بالنحو والعربية، وهو الذى اتفق بعد موت أبيه هو ويحيى بن يعمر على بسط النحو وتعيين أبوابه، وبعج مقاييسه. ولما تولى أبوه البصرة من قبل علىّ
_________
[١] ترجمته فى بغية الوعاة ٢٧٠، وتلخيص ابن مكتوم ١٨١، ومعجم الأدباء ١٦: ١٥١.
[٢] ترجمته فى تلخيص ابن مكتوم ١٨١.','2','380',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (783,'وابن عباس كان على شرط أبيه عطاء، ولم يعقب. ولما استوفى هو ويحيى بن يعمر جزءا متوفرا من أبواب النحو نسب بعض الرواة إليهما أنهما أول من وضع هذا النوع.

<span data-type="title" id=toc-595>٥٢٨ - عنبسة بن معدان الفيل [١]</span>
من بنى أبى بكر بن كلاب. وقيل إنه ينتهى إلى مهرة بن حيدان. قال المبرّد قال عنبسة: اختلف الناس إلى أبى الأسود يتعلّمون منه العربية؛ فكان أبرع أصحابه عنبسة بن معدان المهرىّ. واختلف الناس إلى عنبسة فكان أبرع أصحابه ميمون الأقرن. وكان عنبسة بن معدان يعرف بالفيل؛ وذلك أن زياد بن أبيه كانت له فيلة ينفق عليها فى كل يوم عشرة دراهم، فأقبل رجل من ميسان «١» يقال له معدان، فقال: ادفعوها إلىّ وأكفيكم المئونة وأعطيكم عشرة دراهم فى كل يوم، فدفعوها إليه فأثرى وابتنى قصرا، ونشأ له ولد يقال له عنبسة وفصح، وهو هذا الذى نحن فى ذكره. فروى الأشعار، وروى شعر جرير والفرزدق وانتمى إلى بنى بكر بن كلاب.
وقيل للفرزدق: هاهنا رجل من بنى كلاب يروى شعر جرير ويفضّله عليك، ووصفوه له. فقال: رجل من بنى كلاب على هذه الصفة لا أعرفه، فأرونى داره، فأروه، فقال: هذا ابن معدان الميسانىّ، ثم قصّ عليهم قصته، وقال:
لقد كان فى معدان والفيل زاجر ... لعنبسة الراوى علىّ القصائدا
فروى البيت بالبصرة.
_________
[١] ترجمته فى إشارة التعيين الورقة ٣٩ - ٤٠، وأخبار النحويين البصريين للسيرافى ٢٣ - ٢٤، وبغية الوعاة ٣٦٨، وتلخيص ابن مكتوم ١٨١ وطبقات الزبيدىّ ١١، وطبقات ابن قاضى شهبة ٢: ٢١١، ومراتب النحويين ١٩، والمزهر ٢: ٣٩٨، ٤٢٦، ومعجم الأدباء ١٦: ١٣٣ - ١٣٥.','2','381',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (784,'ولقى أبو عيينة بن المهلّب عنبسة، فقال له عنبسة: ما أراد الفرزدق بقوله:
لقد كان فى معدان والفيل زاجر
فقال: إنما قال:
لقد كان فى معدان واللؤم زاجر
فقال أبو عيينة: وأبيك إن شيئا فررت منه إلى اللؤم لعظيم! وقد اختلف الناس فى تقديم ميمون على عنبسة، وفى تقديم عنبسة على ميمون الأقرن فى الفضل والعلم وسعة الرواية. وهو من الطبقة الثالثة، فإنه يروى عن أبى الأسود، وأبو الأسود عن علىّ كرم الله وجهه. وهذه الطبقة حسب ما حصر الرواة، ممن أخذ عن أبى الأسود، عنبسة بن معدان هذا، وميمون المعروف بالأقرن، وعطاء بن أبى الأسود، وأبو نوفل بن أبى عقرب «١»، ويحيى بن يعمر، وقتادة بن دعامة السّدوسى، وعبد الرحمن بن هرمز، ونصر بن عاصم، كل هؤلاء أخذوا عن أبى الأسود، وتتفاوت مقاديرهم فى العلم بهذا النوع من العربية.

<span data-type="title" id=toc-596>٥٢٩ - عمار بن إبراهيم بن محمد بن حمزة العلوىّ الكوفىّ النحوىّ [١]</span>
أخو عمر بن إبراهيم «٢»؛ وهما زيديّا المذهب، وعمر أكبرهما سنا، وأظهرهما معرفة؛ كان يدرس النحو ببلده. أدركه أبو طاهر السّلفىّ وروى عنه. وقال:
أفادنا أخوا ابن المعمر الحبال وغيره.
_________
[١] ترجمته فى تلخيص ابن مكتوم ١٨٢، وقال: «كذا فى الأصل، والصواب تقديم عامر على عمار؛ فاعرفه».','2','382',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (785,'<span data-type="title" id=toc-597>٥٣٠ - عسل بن ذكوان النحوىّ اللغوىّ [١]</span>
فى طبقة المبرد وفى زمانه، ولم يشتهر شهرته. وكان مقيما للإفادة بعسكر مكرم، وأخذ عنه الناس ورووا عنه. وهو مذكور هناك. وله تصانيف؛ منها كتاب الجواب المسكت. كتاب أقسام العربية. وكان من أصحاب المازنىّ، وقرأ عليه كتاب سيبويه.

<span data-type="title" id=toc-598>٥٣١ - عامر بن إبراهيم بن العباس الفزارىّ النحوىّ الشاعر اللغوىّ القيروانىّ الإفريقىّ [٢]</span>
كان شاعرا بصيرا باللغة مع خبث وإقدام ورأى ومكر. وكان يلى أمور الأموال لملوك تلك الجهات، وجبى خراجا فى بعض سواحل إفريقية، فلما استكمله أخذه وهرب إلى مصر. وفى ذلك يقول محمد التونسىّ فى ولده أبى القاسم:
دعىّ فزارة من لؤمه ... إلى طلعة اللؤم ما أسبقه
أب هارب بخراج الإمام ... وجدّ قتيل على الزندقه
وكان ينتسب إلى حمل بن بدر؛ حتى أعلمه أبو بكر الحسن بن أحمد بن نافذ أن حمل ابن بدر لم يعقب، وأراه ذلك فى بعض الكتب، فحلّى عن ذلك وقال: نحن من ولد عيينه بن حصن. وكان أبو القاسم ولده بصيرا بالآداب، وله أشعار كثيرة، يغلب عليها الهجاء، وكان يزعم أنه من [ولد] «١» أسماء بن خارجة «٢».
_________
[١] ترجمته فى بغية الوعاة ٣٢٤، ومراتب النحويين ١٣٧، ومعجم الأدباء ١٢: ١٦٨ - ١٦٩.
[٢] ترجمته فى بغية الوعاة ٢٧٤، وتلخيص ابن مكتوم ١٨٢، وطبقات الزبيدى ١٧٠ - ١٧١، وطبقات ابن قاضى شهبة ٢: ١٣ وما ذكره المؤلف يوافق ما فى الطبقات.','2','383',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (786,'<span data-type="title" id=toc-599>٥٣٢ - عرام [١]</span>
لقب مشهور له. وهو المفضّل بن العباس بن محمد. وكان خفيف العقل مزلزله. وكان يتعاطى بعد تسميته بالنحوىّ المنادمة وأسبابا تجرّ الطنز واللهو.

<span data-type="title" id=toc-600>٥٣٣ - عاصم بن أيوب الأديب البطليوسىّ الأندلسىّ أبو بكر [٢]</span>
روى عن أبى بكر محمد بن الغراب، ومكى بن أبى طالب القيروانىّ. وكان من أهل الآداب والمعرفة باللغات، ضابطا لذلك؛ مع خير وفضل وفقه.
توفى فى سنة أربع وتسعين وأربعمائة.

<span data-type="title" id=toc-601>٥٣٤ - عيينة بن عبد الرحمن أبو المنهال المهلبى النحوىّ اللغوىّ [٣]</span>
ذكره الحافظ بن البيّع النيسابورىّ فى تاريخ نيسابور فقال: «صاحب العربية وتلميذ الخليل بن أحمد، مؤدب الأمير أبى العباس عبد الله بن طاهر بن الحسين «١»، ورد معه نيسابور، وتوفى بها.
_________
[١] لم يذكره ابن مكتوم فى التلخيص، ولم أعثر له على ترجمة أخرى.
[٢] ترجمته فى بغية الوعاة ٢٧٤، وتلخيص ابن مكتوم ١٨٢، والصلة لابن بشكوال ٢: ٤٤٤ - ٤٤٥.
[٣] ترجمته فى بغية الوعاة ٢٧٠، ومعجم الأدباء ١٦: ١٦٥ - ١٦٧؛ ولم يذكره ابن مكتوم فى التلخيص.','2','384',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (787,'روى عن داود «١» بن أبى هند، وسفيان بن عيينة، وسعيد بن أبى عروبة «٢» ويحيى بن سليم. «٣» وكان من قوله: لا تتصدر إلى فائق أو مائق»: «٤»

<span data-type="title" id=toc-602>٥٣٥ - عالى بن عثمان بن جنّى أبو سعد بن أبى الفتح النحوىّ [١]</span>
أخذ العربية عن أبيه وعن أبى علىّ الفارسىّ. وأكثر عن أبيه، وتقدم وأفاد هذا العلم، وانتقل إلى مدينة صور، «٥» الثغر الشامىّ، فنزله، وأفاد الناس به وروى عن مشايخ وقته العراقيّين والشاميّين، وكتب بخطه كثيرا، وكان محقّقا لما يكتبه.
كان حيّا بصور فى سنة اثنتين وخمسين وأربعمائة «٦».
ونقلت من على ظهر جزء بخط أحمد بن علىّ بن ثابت: أنشدنى الشيخ أبو محمد جعفر بن عبد الله بن على بن المفيد، قال: أنشدنى أبو سعد عالى بن عثمان بن جنّى ولد أبى الفتح بن جنى بصور لنفسه:
_________
[١] ترجمته فى بغية الوعاة ٢٧٤، وتلخيص ابن مكتوم ١٨٢ - ١٨٣، ومعجم الأدباء ١٢: ٣٩.','2','385',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (788,'ألا لله
ما أشقى حياتى ... فشيب مفارقى ممّا أقاسى
كأن طوالعى شربت دواء ... فطول الدهر تسلح فوق راسى
قال: وأنشدنى أيضا لنفسه بمنزله بصيداء «١»:
منزل لا أرى بعينى أدنى ... منه قدرا فى سائر الأمصار
فرشى فيه فقحة «٢» ووطائى ... حين أمسى غرائب الأفكار
وإذا لم أجد أنيسا من النا ... س تفيهقت «٣» فى عتاب الفار

<span data-type="title" id=toc-603>٥٣٦ - العماد المغربىّ [١]</span>
شاب من أهل المغرب، رحل إلى المشرق، وكان يعلّم العربية علما جيدا.
وقرأ على ابن يونس الموصلىّ شيئا من علوم الأوائل؛ ودخل سنجار، وأقام بها مدّة، وقرئ عليه بها. وكان نزيل قاضيها الكردىّ المعروف بالبدر.
واجتمع بيعمر الفرغانىّ النحوىّ المنطيقىّ، وتناظرا فى شىء من ذلك.
وكان ذكيّا حسن المباحثة، وهو فى زماننا هذا.

<span data-type="title" id=toc-604>٥٣٧ - العبدىّ النحوىّ [٢]</span>
نسبته أشهر من اسمه، وقد ذكرته فى باب «أحمد بن بكر» «٤»، وكنيته أبو طالب.
فاضل فى هذا الشأن، صحب أبا علىّ الفارسىّ النحوىّ وأخذ عنه، وحضر مجلس أبى سعيد السّيرافىّ، واستفاد منه. وكان اختصاصه بأبى علىّ وانتسابه إليه أكثر، وتعصّبه له أوفر. أخذ عن أبى علىّ جل ما عنده.
_________
[١] لم يذكره ابن مكتوم فى التلخيص، ولم أعثر له على ترجمة.
[٢] ترجمته فى بغية الوعاة ١٢٩، وكشف الظنون ٢١٢، ومعجم الأدباء ٢: ٢٣٦ - ٢٣٨، ونزهة الألباء ٤١٠ - ٤١١، ولم يذكره ابن مكتوم فى التلخيص.','2','386',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (789,'وكان وطئ العبارة، حسن الغوص، جميل التصنيف، اعتنى بكتاب شيخه أبى علىّ، وهو الكتاب المسمى بالعضدىّ «١»، وهو: الإيضاح، والتكملة، وشرحه شرحا كافيا شافيا، أتى فيه بغرائب من أصول هذه الصّناعة، وحقّق أماكن؛ حتى يقال: إنه شرح كتاب أبى علىّ بكلام أبى علىّ؛ لكثرة اطّلاعه على كتبه وفوائده. وإذا أنصف المنصف، وأجمل النظر، واطّرح الهوى رأى أن كلّ من تعرّض لشرح هذا الكتاب إنما اقتدى بالعبدىّ وأخذ منه، وإن غيّر الألفاظ فيما خرج عن القصد الذى قصده.
وكنت قد سألت عالمين بهذا الشأن عن كتاب العبدىّ وكتاب الجرجانىّ «٢» فى شرح الإيضاح، فسكتا مليّا وقال أحدهما: قد سمّى الجرجانىّ كتابه المقتصد، وهو كما سمّاه؛ فإنّ فوائده مختصرة. وقال الآخر: أحسن العبدىّ فى الكلام على العوامل، وقصّر فيها الجرجانىّ، وأحسنا فى التصريف، وكلام الجرجانىّ أبلغ وأبسط.
وكان العبدىّ رحمه الله قد أدركه خمول الأدب، ولم يحصل [له] من السمعة ما حصل لابن جنّى والرّبعىّ. وكان كثير الشكوى لكساد سوقه وسوق الأدب فى زمانه.
قال العبدىّ: وعهدى بنفسى حاضرا مجلس هذا الشيخ- يعنى أبا علىّ- وهناك من يقرأ كتاب سيبويه، دون غيره من المتوسّطات ثلاثون رجلا وأكثر؛ ما فيهم إلا من يطلق عليه اسم العامل، ثم ما يحسنونه من اللغة والشعر غير','2','387',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (790,'مذكور، ومجلس الشيخ أبى سعيد الحسن بن عبد الله السيرافىّ وعدد أهل المجلس ومن معنا السبّق للقراءة يزيدون على المائة، ومجلس الشيخ أبى الحسن على بن عيسى النحوىّ جميع أهل السنة رحمه الله، رحمنا [الله] وأعاننا على إصلاح ديننا، وجعل ذلك خالصا لوجهه. وهأنذا فى زمان لا أفتح عينى على طالب علم تحقيقا سوى اثنين أو ثلاثة.
ولم يكن للعبدىّ رحمه الله أنسة بشىء من العلوم القديمة؛ ودليل ذلك أنه لما عاب كتاب الأصول لابن السراج «١» قال: أفسده بالتّقسيمات الهندسية، والهندسة لا تقسيمات فيها، وإنما التّقسيم والترتيب وتعريف الأجناس والأنواع والخاصة والفصل والعرض ... إلى أمثال ذلك من ألفاظ أهل المنطق فيما فوق الهندسة والمنطق. وإنما كان متعب الخاطر فى معرفة العبارة العربية؛ غير مشتغل بسواها، فلا جرم أنه أجادها.
ومن العجب أنه كان يحضر مجلس أبى الحسن على بن عيسى الرمانى، وكان عالما بالمنطق مستعملا له فى عبارته النحوية والكلامية وما استفاد منه ما يفرق به بين التقسيم المنطقىّ والهندسىّ.
عاش العبدىّ إلى قريب سنة عشرين وأربعمائة- فيما قيل- والله أعلم.

<span data-type="title" id=toc-605>٥٣٨ - عباد بن كسيب [١]</span>
لغوىّ فيمن دخل البادية. أخذا الناس عنه طرفا من اللغة الفصحى. وهو قديم العهد؛ قد يرد اسمه فى كتب اللغويين، وأسندوا إليه جملة من الغريب.
_________
[١] لم أعثر له على ترجمة، ولم يذكره ابن مكتوم فى التلخيص.','2','388',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (791,'<span data-type="title" id=toc-606>(حرف الغين)</span>
<span data-type="title" id=toc-607>٥٣٩ - غانم بن وليد المخزومىّ المالقىّ النحوىّ الأستاذ أبو محمد الأديب [١]</span>
فاضل نحوىّ شاعر متصدّر ببلده مالقة من ساحل الأندلس، له نباهة وذكر هناك، متصدّر للإفادة، عالم بالعربية «١»، له شعر، فمن شعره:
ثلاثة يجهل مقدارها ... الأمن والصحة والقوت
فلا تثق بالمال من غيرها ... لو أنه درّ وياقوت

<span data-type="title" id=toc-608>٥٤٠ - الغورىّ [٢]</span>
منسوب إلى الغور، وهو عمل إلى جانب مدينة غزنة، فيه عدّة مدن وقرى.
لا أعرف من حال هذا المذكور شيئا، وإنما ذكر لى ياقوت الحموىّ مولى عسكر الحموىّ التاجر، نزيل بغداذ؛ قال: رأيت بمرو فى بعض خزائن وقفها- فلا أدرى:
أقال لى فى خزانة المشرف المستوفى أو فى خزانة الفقّاعىّ- كتابا كبيرا فى اللغة
_________
[١] ترجمته فى بغية الملتمس للضبى ٤٤٧ - ٤٢٨، وبغية الوعاة ٣٧١، وتلخيص ابن مكتوم ١٨٣، وجذوة المقتبس للحميدى الورقة ١٤٠ والصلة لابن بشكوال ٢: ٤٥٠ - ٤٥١، ومطمع الأنفس للفتح ٦٠ - ٦١، ومعجم الأدباء ١٦: ١٦٦ - ١٦٧.
وقال ابن مكتوم: «هو غانم بن وليد بن عمر بن عبد الرحمن. روى عن أبى عمر يوسف بن عبد الله ابن خيرون، وعن أبى عبد الله بن السراج. وتوفى رحمه الله سنة سبعين وأربعمائة. وذكره الحميدى وابن بشكوال، وأثنيا عليه».
[٢] ترجمته فى تلخيص ابن مكتوم ١٨٣.','2','389',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (792,'فى عدّة مجلدات من تصنيف الغورىّ. قال: وتأملت الكتاب، فرأيته أجمع كتاب، كثير الألفاظ، قليل الشواهد. وأظنه قال: هو على الأوزان «١». والله أعلم.
وهذا كتاب لم يظهر له ذكر؛ لا بالعراق ولا بالشام ومصر. وأظن أن مصنفه قريب العهد، والله أعلم «٢».','2','390',NULL,NULL,'0');
INSERT INTO [b10525] VALUES (793,'<span data-type="title" id=toc-609>[الفهارس]</span>
