INSERT INTO [b10538] VALUES (598,'بقعا: أبو عاصم ١١٩ الصلعا: ابن أبي كريمة ٥١٣ ظلّعا:- ٢٨١، ٤٦٨ تابع: (حميد بن ثور) ٤٨٧ أربع: بلعاء بن قيس ٦٤ راتع: النابغة ٤٢٠ هاجع: حميد ٥٢٩ متعجع: أبو ذؤيب الهذلي ٢٢٥ فدع: أبو زبيد ٢١٧ مصرع: عبد الله بن عبد الأعلى ١٣١ أوسع: السيد الحميري ٥١٠ وتمصع: ذو الرمة ٤٧٤ يقطع:- ٤٩٢ يافع: الفرزدق ٣٣٠ مرفّع: ابن عنقاء الفزاري ١١٩ بلقع: طفيل الغنوي ٢٨٠ موقّع: محرز بن المكعب الضبي ٧٤ ظالع: بلعاء بن قيس ٦٤، ٢٣٧، ظالع: قيسر بن العيزارة ٢٥٣ جائع: ابن عنقاء الفزاري ٢٩٨ والأسلع: مساور بن هند ١٠٣ الأسلع: جرير ١٠٢، ١٦٤ الصلع: ٥٠٨ تظلع: محرز بن المكعبر ٢٨١ تدمع:- ٤٥٦ أجمع: (نعيم بن شقيق التميمي) ٤١ تسمع:- ٥١٢ وأمنع: البلتع العنبريّ ٦٠ قعاقع:- ٤٧٨ كتيع: عمرو بن معد يكرب ٣٠٣ صليع: عمرو بن معد يكرب ٥١٤ بظلّع: طفيل العنوي ٢٩٨ خماع:- ٢٤٦ الضباع:- ٢٥١ (ف) فخضف:- ١١٧ أكلفا:- ٥٠٨ نزاحف: مسكين الدارمي ٤٨٥ النوادف: مسكين الدارمي ٤٧٥ النّزف: قيس بن الخطيم ٣١٣ محلف:- ٤٧٨ جانف: مزرد بن ضرار ٤٣٤ واف: عباس بن مرداس ١٨٤ لجف: أبو نواس ٢٢٨، ٢٣٠ الروادف:- ٥٠٠ عارف: فضالة بن شريك ٤٩٣ بالوظيف:- إسحاق الخريمي ٢٩٤ (ق) الزرقا: ٤٦٣ تندقّا: الأحنف بن قيس ٣١٥',NULL,'609',NULL,NULL,'0');
INSERT INTO [b10538] VALUES (599,'أخوفا: ابن عنمة الضبي ٤١٦ رفيقا: شتيم بن خويلد ٥٥١ أزرق: ذو الرمة ٤٧٥ ويورق:- ٤١٨ الأبلق:- ٦١ الأبلق: السمؤل بن عاديا ٥٦ أبلق:- ٥٦ يألق: خالد بن يزيد بن معاوية ٥٧ خرنق:- ٤١٤ العوق: المغيرة بن حيناء ٥٤ يفوق: المفضل النكري ٢٥١ الخندق: الفارس السلمي ٦٠ والخندق: ٦٠ الأبلق: السمؤل بن عاديا ٥٦ للأبلق: العلبان ٥٩ السّلق: أبو نواس ٣٣٤ خلقي: مطيع بن إياس ٥٠٢ السّاقي: أبو نواس ٥٠٠ الخوافق:- ٥٣٢ الخوق: اللعين ٤٦١ مغلوق: أبو الأسود ٤٣٧ الشقائق:- ٢٩٥ طريقي: أبو الشيص: ٤٢٥ الحليق: أمية بن الأسكر ٥١٣ (ك) منهوك: ذو الركبة العوجاء ٣٢٥، ٤٠٥ شريك: مالك بن المنتفق ١٨٣ مليكها:- ٥٣٢ (ل) الإبل:- ٢٣٣ عقالا:- ٤٥٨ ضلالا:- النابغة ٦١ عيالها: الكميت بن زيد ٢٥٢ فضلا: كثير ٣٦٤ كلكلا: حابس بن خبيس الأعسر ٥٢٣ أولا: الأعرج الطائي ٣٤٩ النزولا: مهلهل ٢٦٤ يطولا: الأخطل ٢٣٣ مشكولا:- ٢٣٦ وأحولا: بشار بن برد ٤٨ بخيلا: قعنب ابن أم صاحب ٤٧١ فحيلا: الراعي ٤١٩ الجديلا: بشامة بن الغدير ٣٤٥ قيلا: المنذر ٩٣ قليلا: هميم بن صعصعة ١٨٦ سبالها:- ٥٥٢ خالها: ابن الدمينة ٣٦٣ احولالها:- ٩٠ شمال:- ٥٥٣ قبل: أبو خراش الهذلي ٣٤٥ محجل: زفر بن الحارث ٥٢',NULL,'610',NULL,NULL,'0');
INSERT INTO [b10538] VALUES (600,'المحجّل:- ٥١ بخل: (المتنخل الهذلي) ٢٢٢.
ولا عزل: ابن مقروم الضبي ٤٦٤ أقزل: كعب بن زهير ٢٥٤ فضل: المسيب بن علس ٩٩، ٤١٠ كلكل: الكميت بن زيد ٤٢٢ الأمل: ابن أحمر ٣٠٠ تحملوا: أبو عمران الأعمى ٢١٤ مجاهله: (الحكم بن أيوب) ٣٥٠ وتبول: جرير ٤٧٣ حجول: محرز بن مكعبر الضبي ٥٧ حجول:- ٥٢ فيحول: جرير ١٤٢ الفحول: جرير ٤٢٩ معدول: عبدة بن الطبيب ٥٣٢ طفول:- ٥٣٢ يقول:- ٤٦٦ مأكول: طفيل الغنوي ٢٦٣ فحيل: جرير ٤٢٠ فحيل: ٤١٩ تزيل: خفاف بن ندبة ٣٣٩ تأسيل: جران العود ٢٥٤ قليل: الهذلي (ساعدة بن جؤية) ٢٤٧ عفشليل (ساعدة بن جؤية) ٢٣٢ قليل: الهذلي سائله: الأشتر بن عمارة ٤٢٦ نقلها: قطران العشمي ٢٢٣ قيلها: أبو الرديني العكلي ٣٥٤ طويل: مبشر بن الهذيل الفزاري ٤٨ مجال: عنترة ٢٥١ وأوصالي: امرؤ القيس ٥٤٧ النصال: الكميت ٢٤١، ٤٣٣ النصال: القحيف ٢٦٥ هطّال: امرؤ القيس ٢٨٦ فعالى: أبو طالب ٤٦ ثفال: مسكين الدرامي ٢٦١ الأكفال: الكميت بن زيد ٢٢٠ الأنفال: معدان الأعمى ٣٥٥ عقال: جرير ١٤١ النعال: مرار الأسدي ٣٦٢ الشّمال: معدان الأعمى ٥٦٠ حابل: أبو راشد الضبي ١٩٥ وخابل: لبيد ٤١ نابل: امرؤ القيس ٥٢٧ الوابل: عمرو بن الاطنابة ٢١٦ الرّجل: الأعرج المعنى الطائي ٤٥، ٣٤٩ النّصل: جعيفران ٢٤١ تتفل: امرؤ القيس ٢٤٠ عقل: رجل من بني عجل ٣٨٦ جحفل: الخريمي ٤٨٩ عل: النمر بن تولب ٢٩٣ المآكل: أبو راشد الضبي ١٩٨',NULL,'611',NULL,NULL,'0');
INSERT INTO [b10538] VALUES (601,'هيكل: ربيعة بن مقروم ٢٦٦ صاهل: ابن هرمة ٢٦٦ أهلي: حميد بن ثور الهلالي ٣٠٨ الأول: حسان بن ثابت ٤٦٣ كالأحول: زهير بن مسعود ٤٢٧ فحيل:- ٤١٩ بطويل: الفرزدق ١٤٢ الطويل:- ١٣١ السراويل: أبو نواس ٣٣٣ (م) وهام: الطرماح ٣٠٢ عدم: عامر بن حوط ١٠٧ مصطلم:- ٤٦٩ ألاما:- ١٧٩ العظاما: أبو عباد النميري ٣٢٩ المظالما: أبو مالك الأعرج ٣٣٧ أضجما: لقيط بن زرارة ٤٩٧ والأقدما: أوس بن حجر ٩٤، ١٥٠ تبسّما:- ٢٢٦ توسّما: ذو الرمة ٥٨ تحطما: (أبو دلامة) ٤٩١ تلطما: قيس بن زهير ٢٤٢، ٤٣٣ غنما: كعب بن زهير ٣٠٣ مريما: الأقيشر ٩٢، ١٦٨ عظام: إسحاق الموصلي ٥٠٠ والسنام:- ٤٦٥ المقادم: النعمان بن بشير ٥٠ هاشم: ابن أبي ربيعة المخزومي ٥٠١ مزكوم:- ٣٥٨ لجسيم: أوفى بن موءلة ٤٨ قهطم: معاوية بن أوس ٨٠ وأستقيم: حكيم بن جبلة ٢٦٠، ٣٧٦ ومستقيم: قيس بن زهير ٢٦٠ حميم: (ابن ميادة أو مزاحم العقيلي) ٢٨٦ الحامي:- ٤١٩ بشام: الفرزدق ١٦٥ الإعصام: (الجحاف بن حكيم السلمي) ٢٧٢ بسطام: الرشيد بن رميض ٥١٩ رؤام: قيس بن عاصم ١٨٠ الطوامى: الهذلي (معقل بن خويلد) ٢٨٧ كأيام:- ٤٦٥ للجعاثم: ابن عنمة الضبي ٤٨٩ تلأثم: النابغة ٣١١ الاضجم: ٤٩٧ الأسحم: معاوية بن أوس ١١٥ الصوارم:- ٤٦٨ جرمى: أبو خراش الهذلي ٢١٢ ضرم: (النابغة الجعدي) ٤٤٦',NULL,'612',NULL,NULL,'0');
INSERT INTO [b10538] VALUES (602,'الأكرم: عوف بن الخزع التميمي ٩٩ العرمرم: ١٦٤ خازم: ابن قنبر ٤٦٤ والمعصم: قيس بن عاصم ١٨٠ أضجم: ٤٩٨ الأقصم: الحارث بن حلزة ٤٩٨ الضّراغم:- ١٨٨ والحكم: أم حكيم بن جبلة ٣٧٦ تكلمى: ضمرة بن ضمرة النهشلي ٩٦ المترنّم: عنترة ٢٩٣، ٣٦٧ للمغنم ٣٣٠ بأرمام ٢٨٩ جهم: زهير ٢١٦ الوشوم: معاوية بن حزن المحجل ٥٠ الخرطوم: خليفة الأقطع ٤٦٧ كريم: كعب بن سعد الغنوي ١٢٧ (ن) الحزن: (أبو العتاهية) ١٩٩ تفنين: حميد بن مالك الأرقط ٣٤٥ عرينها: مدرك بن حصن ٢٥٠ صفوان: امرؤ القيس ٢٧٧ واغتديين: (عمرو بن لأي) ٤٦٦ ومين: الحارث بن الوليد ٥٤٦ تزيدونا:- ٢٩٥ الحزونا: عمرو بن كلثوم ٤٩٦ مجنونا: عبد الرحمن بن حسان ٢٣٧، ٤٣٢ بالبنينا:- (السليك بن السلكة) ٤١٤ يأتينا: ٢١٥ المحزون: أبو طالب ٨٧ يشينها:- ٣٧٢ الحدثان:- ١٩٨ العرجان: الحكم بن عبدل ٣٢٣ التيجان: ابن النطاح ٤٥٥ المدان: شريكا بن الأعور ٤٧٤ البعران:- ٤٢٠ ملتبسان: عوانة بن الحكم ٤٣٠ عينان: بلعاء بن قيس ٤٩٣ الألوان (جرير) ٥٢٠ الزمن:- ٣٨ برذون: ٤٥٥ مظعون: جميل: جميل ٥٤٨ (ي) حماريا: ٢٠٢ غازيا: مقاس العائذي ١٧٧ قاليا: ٢٨ مآليا:
العشي:- ٢٤٠ قاربّه: عمرو الأعور الخاركي ١٦٣ جافيه: (عيسى بن زينب المراكبي) ١٣٧',NULL,'613',NULL,NULL,'0');
INSERT INTO [b10538] VALUES (603,'ثمانيه:- ٣٣٢ «الألف المقصورة» المدى:- ٤٢٦ سعى:- ٣٦٨.',NULL,'614',NULL,NULL,'0');
INSERT INTO [b10538] VALUES (604,'<span data-type="title" id=toc-271>٨- فهرس انصاف الأبيات</span>
نصف البيت/ قائله/ الصفحة
ويمشي إن أريد به المشاء
/ الحطيئة/ ٢٤٠
آذنتنا بينها أسماء
/ الحارث بن حلزة/ ٥٢
حتّى يحكمهم إلى جوّاب
/ لبيد/ ١٤٧
إذا ما استيأس الرّيق عاصبه
/-/ ٣٠١
مجرّجيوش غانمين وخيّب
/ امرؤ القيس/ ٢٨٨
إنّ الجيّاد الضّابعات
/-/ ٢٩٥
إهابه مثل إهاب العثّ
/-/ ٢٩٧ ... حبّذا الوضح/ المنتخل الهذلي/ ١٠٥
فإن بليت فذاك الفالج الذّكر
/ سعد المطر/ ٤٤٠
وأنت ابن صغرى لم تتمّ شهورها
/ الفرزدق/ ٧٩
ورأس كقبر المرء من آل تبّع
/ ذو الرمة/ ٤٩٥
وأبو قبيصة والرّئيس الأول
/ الفرزدق/ ٥٢٠ وصلهما جميل/ أبو خراش/ ٢٩٦
كالسّاق ساق الحمام
/ الطرماح/ ٢٧٨
قرقمه العزّ وأضواه الكرام
/-/ ٤٣
غراب شمال ينفض الريش جاثما
/-/ ٥٥١
فسيّره الدّهر تعويج وتقويم
/ ابن مقبل/ ٢٦٢
حمراء لا حبشيّة الإتمام
/-/ ٥٩
قتيل الرّعد بالبلد التّهام
/-/ ٤٠٠
تمكو فريصته كشدق الأعلم
/ عنترة/ ١١٠
ألاهبيّ بصحنك فاصبحنا
/ عمرو بن كلثوم/ ٥٢
أمبدّ سؤالك العالمينا
/ عمر بن أبي ربيعة/ ٢٢٦.',NULL,'615',NULL,NULL,'0');
