١٦٧ وهل تطيق الرجل بسيط ٢
١٦٧ ودع مرتحل بسيط ٢
١٣٨ أحيا ما قتلا بسيط ٣
٩٩ إنا محيوك الطلل بسيط ٣
١٥٠ أهلا المقبل كامل ٢
٧٠ إن لم تكن قتال كامل ٢
٢٨٤ في الخد إن رحيلا كامل ٣
١٥٠ فعل يفعل كامل ٢','4','456',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (440,'١٠ أينفع العذل متقارب ٢
١٠ وتشمل يشمل متقارب ٢
١٧٠ ذاك قليلا خفيف ٣
م-
١٣٤ على قدر العزائم طويل ١
١٠٦ يمد يديه ضيغم طويل ٢
١٠٦ وعينيه أرقم طويل ٢
٢٠٩ كأن خبائنا طويل ٣
١٠ ألا يا اسلمي اسلمي طويل ٣
١٠ ثلاث تكلمي طويل ٣
١٦٥ على قدر العزائم طويل ٣
٦٨ واحر قلباه شم بسيط ٣
١٢٧ نصيب وتسجام بسيط ٣
١٠٣ كفى أراني ألوما كامل ٣
٦٧ أرض منجم كامل ٣
١٠٦ ماذا يزيدك القستم بسيط ٢
١٠٦ عقبى اليمين ندم بسيط ٢
٥٨ أم هل توهم كامل ٢
٥٨ هل غادر متردم كامل ٢','4','457',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (441,'٣٥١ فعجبت تظلم كامل ٢
١٠٤ أن تناما أو تنيما خفيف ٢
١٠٤ إن عهدا زميما خفيف ٢
ن-
٦٨ أخذنا بيننا طويل ٢
١٤١ يا كثير الدمن مديد ٢
٢٧٥ وألفى قولها ومينا وافر ٢
٢٧٠ متى أضع تعرفوني وافر ٢
٢٧ مغاني المغاني وافر ٢
٣٣٢ درس المنا فأبان كامل ٢
٨٧ الرأي قبل الشجعان كامل ٣
١٤٦ ذكر الكرخ الأوطان خفيف ٢
هـ-
٢٥٤ مني النفس تستطيعها طويل ٢
٢٥٤ بها وجدها وولوعها طويل ٢
٣٥٨ فعزما فقدما صاحبه طويل ١
١٣٦ ألا هكذا كاسبه طويل ١
٣٥٨ أهن عوادي وصواحبه طويل ١
١٦٠ في حمرة تلهبها بسيط ٢','4','458',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (442,'١٢٧ ميلوا إلى الدار نحميها بسيط ٣
١٢٥ سرب ذواتها كامل ٣
٤٠٧ ما بال جرعائه جرده منسرح ١
٤٠٧ ما لكثيب عقده منسرح ١
٨٥ لم نأت ذاكرها منسرح ١
٨٥ أوه بديل قولتي واها منسرح ١
ي-
٢٧٠ أنا الثنايا وافر ٢','4','459',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (443,'فهرس الأمثال:
إن البغاث بأرضنا يستنسر ج١ ص١٩٢
إن يبغ عليك قومك لا يبغ عليك القمر ج١ ص٦٢، ٦٣
أهل مكة أخبر بشعابها ج١ ص١٩١
أهلك والليل ج٢ ص٢٩٧
سبق السيف العذل ج١ ص١٠
قلب له ظهر المجن ج٣ ص٦٦
كل الصيد في جوف الفرا ج٢ ص٣٧٦
لا تعر الأحمق شيئا فيظنه له ج٢ ص٦٤
لا تنال المكارم إلا بالمكارم ج١ ص٣٥٠
الليل جنة الهارب ج٢ ص١٣٥
من أطاع غضبه أضاع أدبه ج١ ص٣٦٣
نار الحباحب ج٢ ص٣٣٢','4','460',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (444,'فهرس الحكم:
ظلام الليل يهديني إلى باب من أوده، وضوء النهار يضل بي عن باب من لا أود ج١ ص١٠٢
قد ظهر الصبح إلا أنه لم يملك الإنسان بصره ج١ ص١٠١
الماء أروى لشدوق النيب ج٢ ص١٠١
من تروى فترت عظامه ج١ ص١٠١
الموت طعام لا تجشه المعدة ج١ ص١٠٣','4','461',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (445,'فهرس الأيام:
بيعة الرضوان ج١ ص٦٤
عام الفتح ج١ ص٩
عمرة القضاء ج١ ص٩
غزوة الفتح ج٣ ص٢٥
فتنة الجمل ج٣ ص٢٦
يوم أحد ج٣ ص١٠٧
يوم بدر ج١ ص٤٧
يوم حنين ج١ ص٩٧، ١٠٩
يوم الفجار الثاني ج٢ ص٣١٠
يوم القادسية ج٢ ص٣٢٨
يوم اليمامة ج١ ص٣٨١، ج٢ ص١٠٠','4','462',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (446,'فهرس الآيات:
أ-
{أأتخذ من دونه آلهة يردن الرحمن بضر لا تغني عن شفاعتهم شيئا ولا ينقذون} ج٢ ص٢٨٢
{أئذا متنا وكنا ترابا وعظما أننا لمدينون} ج١ ص٢٨
{أأنتم أنزلتموه من المزن أم نحن المنزلون} ج٢ ص٢٤٤
{أأنتم تزرعونه أم نحن الزارعون} ج٢ ص٢٤٤
{أتأمرون الناس بالبر وتنسون أنفسكم وأنتم تتلون الكتاب أفلا تعقلون} ج٢ ص٢٨٩، ٣٥٠
{اتقوا الله وكونوا مع الصادقين} ج١ ص٣٠٤
{إذ أوحينا إلى أمك ما يوحى} ج٢ ص٢٠٣
{إذ تلقونه بألسنتكم وتقولون بأفواهكم ما ليس لكم به علم، وتحسبونه هينا، وهو عند الله عظيم} ج١ ص٣٦٠
{إذ دخلوا على داود ففزع منهم قالوا لا تخف خصمان بغى بعضنا على بعض} ج٣ ص١٦٠
{إذ قال لأبيه وقومه ما تعبدون} ج٣ ص١٢٨
{إذ قال لأبيه يا أبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا} ج٢ ص٢٦٢
{إذ قال لهم أخوهم نوح ألا تتقون} ج٣ ص٧
{إذ قال يوسف لأبيه يا أبت إني رأيت أحد عشر كوكبا والشمس والقمر رأيتم لي ساجدين} ج٣ ص١٨','4','463',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (447,'{إذ قالوا ليوسف وأخوه أحب إلى أبينا منا} ج٢ ص٢٤٥
{إذ نسويكم برب العالمين} ج٣ ص١٢٩
{إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد} ج٢ ص٣٣٦
{إذ بربكهم الله في منامك قليلا ولو أراكهم كثيرا لفشلتم ولتنازعتم في الأمر ولكن الله سلم إنه عليم بذات الصدور} ج١ ص٣٣٧
{إذ يلقون أقلامهم أيهم يكفل مريم} ج١ ص١٧٣
{وإذ تتلى عليهم آياتنا بينات قالوا ما هذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم} ج٢ ص٢٠١
{إذ جاءك المنافقون قالوا نشهد إنك لرسول الله والله يعلم إنك لرسوله والله يشهد إن المنافقين لكاذبين} ج٢ ص٢٤٣
{إذا رأتهم من مكان يعبد سمعوا لها تغيظا وزفيرا} ج١ ص٣٣٣
{إذا قمتم إلى الصلاة فاغسلوا وجوهكم} ج٢ ص٢٨٦
{اذهب بكتابي هذا فألقه إليهم ثم تول عنهم فانظر ما يرجعون} ج٢ ص٢٩١
{أرأيت إذ أوينا إلى الصخرة} ج١ ص١٨٧
{أرسله معنا غدا يرتع ويلعب وإنا له لحافظون} ج٢ ص٢٤٤، ٣٠٣
{إرم ذات العماد} ج٢ ص٣١٩
{أسباب السموات فأطلع إلى إله موسى} ج٢ ص٣٠٥
{أشداء على الكفار رحماء بينهم} ج٣ ص١٥٢
{اصبر على ما يقولون واذكر عبدنا داود ذا الأيد إنه أواب} ج٢ ص٢٩١
{أفرأيت إن متعناهم سنين} ج٣ ٢٨، ٢٠٢
{أفرأيتم اللات والعزى} ج٢ ص٣٦٢،','4','464',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (448,'{أفرأيتم الماء الذي تشربون} ج٢ ص٢٤٤
{أفرأيتم ما تحرثون} ج٢ ص٢٤٤
{أفسحر هذا أم أنتم لا تبصرون} ج١ ص١٤٩
{أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض إن نشأ تخسف بهم الأرض أو نسقط عليهم كسفا من السماء إن في ذلك لآيات لكل عبد منيب} ج٣ ص١٧٣
{أفلم ينتظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج} . ج١ ص٢٧٢
{أفمن شرح الله صدره للإسلام فهو على نور من ربه فويل للقاسية قلوبهم من ذكر الله أولئك في ضلال مبين} ج٢ ص٢٨٧
{اقتربت الساعة وانشق القمر} ج١ ص٣٣٦
{اقرأ باسم ربك الذي خلق} ج١ ص٣٧٦
{ألا إلى الله تصير الأمور} ج٢ ص٢٢٥
{ألا إنهم هم المفسدون ولكن لا يشعرون} ج٣ ص١٦٣
{ألا تتبعني أفعصيت أمري} ج٢ ص٢٩٣
{إلا تذكرة لمن يخشى} ج١ ص٢٧٢
{إلا من أتى الله بقلب سليم} ج٣ ص١٢٩
{إلا من هو صال الجحيم} ج٣ ص٢٨، ٢٠١
{التي لم يخلق مثلها في البلاد} ج٢ ص٣١٩
{الذي خلقني فهو يهدين} ج٣ ص١٢٨
{الذين إذا اكتالوا على الناس يستوفون} ج١ ص٣١٩
{الذين يتبعون الرسول النبي الأمي الذي يجدونه مكتوبا عندهم في التوراة والإنجيل يأمرهم بالمعروف وينهاهم عن المنكر} ج٣ ص١٣١.','4','465',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (449,'{الذي يؤمنون بالغيب ويقيمون الصلاة ومما رزقناهم ينفقون} ج٢ ص٢٨١
{الر كتاب أنزلناه إليك لتخرج الناس من الظلمات إلى النور} ج٢ ص٩٦
{ألكم الذكر وله الأنثى} ج١ ص٢٢٩
{الله الذي أنزل الكتاب بالحق والميزان وما يدريك لعل الساعة قريب} ج١ ص١٧٩
{الله الذي خلق سبع سموات ومن الأرض مثلهن} ج١ ص٣٨٧
{الله الذي رفع السموات بغير عمد ترونها} ج١ ص١٨١
{الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فتردى الودق يخرج من خلاله فإذا أصاب به من يشاء من عباده إذا هم يسبشرون} ج٣ ص١١
{الله لا إله إلا هو الحي القيوم} ج٢
{الله لا إله إلا هو له الأسماء الحسنى} ج١ ص٢٧٢
{الله لطيف بعباده يرزق من يشاء وهو القوي العزيز} ج١ ص٣٧٩
{الله نور السموات والأرض مثل نوره كمشكاة فيها مصباح} ج٢ ص٢٤، ١٢٧، ١٤٨
{الله يتوفى الأنفس حين موتها والتي لم تمت في منامها} ج٣ ص١٨٧
{ألم تر أن الله أنزل من السماء ماء فتصبح الأرض مخضرة إن الله لطيف خبير} ج٣ ص١٨٩، ١٩٣
{ألم تر أن الله سخر لكم ما في الأرض والفلك تجري في البحر بأمر ويمسك السماء أن تقع على الأرض إلا بإذنه إن الله بالناس لرءوف رحيم} ج٣ ص١٦٤
{ألم تر أن الله يعلم ما في السموات','4','466',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (450,'وما في الأرض ما يكون من نجوى ثلاثة إلا هو رابعهم ولا خمسة إلا هو سادسهم} ج٣ ص١٨٣
{ألم تر أنا أرسلنا الشياطين على الكافرين تؤزهم أزا} ج١ ص٣٧٨
{ألم تر أنهم في كل واد يهيمون} ج٢ ص٩٧
{ألم تر كيف فعل ربك بعاد} ج٢ ص٣١٩
{ألم. ذلك الكتاب لا ريب فيه} ج٢ ص٢٢٥، ٢٨١
{ألم. غلبت الروم في أدنى الأرض وهم من بعد غلبهم سيغلبون} ج٢ ص٣٦٧
{ألم يروا أنا جعلنا الليل ليكنوا فيه والنهار مبصرا} ج١ ص٨٦
{إلى ربك يومئذ المساق} ج١ ص٣٥١ ج٢ ص٢٢٥
{إلى ربك يومئذ المستقر} ج٢ ص٢٢٥
{إلى ربها ناظرة} ج٢ ص٣٥٠، ٢٢٤، ٢٢٥
{أم لهم شركاء شرعوا لهم من الدين ما لم يأذن به الله} ج١ ص٣٧٩
{أم يقولون شاعر نتربص به ريب المنون} ج١ ص٢٧٦
{أما السفينة فكانت لمساكين يعملون في البحر فأردت أن أعيبها وكان وراءهم ملك يأخذ كل سفينة غصبا} ج٢ ص٣١٥
{أمر من عندنا إنا كنا مرسلين} ج٢ ص١٧٨
{إن الأبرار لفي نعيم} ج١ ص٣٦١
{إن إبراهيم كان أمة قانتا لله حنيفا ولم يك من المشركين} ج٣ ص١٨٧
{أن اقذفيه في التابوت فاقذفيه في اليم} ج٢ ص٢٠٢','4','467',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (451,'{إن أكرمكم عند الله أتقاكم} ج١ ص١٨٨
{إن الذين كفروا ويصدون عن سبيل الله} ج٢ ص١٨٨
{إن الذين يحبون أن تشيع الفاحشة في الذين آمنوا لهم عذاب أليم في الدنيا والآخرة} ج٢ ص٣٢٥
{إن الله لا يحب الفرحين} ج١ ص٣٩٤
{إن الله لا يهدي القوم الظالمين} ج٢ ص٣١٩
{إن الله لعن الكافرين وأعد لهم سعيرا} ج١ ص٢٧١
{إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهي عن الفحشاء والمنكر والبغي يعظكم لعلكم تذكرون} ج٢ ص٣٣٥
{إن الله يأمركم أن تؤدوا الأمانات إلى أهلها} ج١ ص٣١٢
{إن الله يحب التوابين ويحب المتطهرين} ج٢ ص٢٥١
{إن إلينا إيابهم} ج٢ ص٢٢٤
{إن أولى الناس بإبراهيم للذين اتبعوه وهذا النبي والذين آمنوا} ج٣ ص١١٩
{إن تصبك حسنة تسؤهم وإن تصبك مصيبة يقولوا قد أخذنا أمرنا من قبل} ج٣ ص١٥٢
{إن الصلاة كانت على المؤمنين كتابا موقوتا} ج١ ص٣٠٦
{إن في ذلك لآية لمن خاف عذاب الآخرة ذلك يوم مجموع له الناس وذلك يوم مشهود} ج٢ ص١٩١
{إن في ذلك لذكرى لأولي الألباب} ج١ ص٣٨٥
{إن في ذلك لذكرى لمن كان له قلب أو ألقى السمع وهو شهيد} ج١ ص٢١٢
{إن قارون كان من قوم موسى فبغى عليهم، وآتينا من الكنوز ما إن مفاتحه لتنوء بالعصبة أولي القوة} ج١ ص١٨٩
{إن كل إلا كذب الرسل فحق عقاب} ج٣ ص٩','4','468',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (452,'{إن المتقين في جناب ونعيم} ج١ ص٣٧٦
{إن المجرمين في ضلال وسعر} ج١ ص٢٦٩
{إن الملأ يأتمرون بك ليقتلوك} ج٣ ص٩٣
{إن تقول إلا اعتراك بعض آلهتنا بسوء} ج٢ ص١٨٣
{إن هذا أخي له تسع وتسعون نعجة ولي نعجة واحدة} ج٣ ص٥٣، ٢١٦
{إن هذا القرآن يهدي للتي هي أقوم} ج٢ ص٢٠٥
{إن هذه أمتكم أمة وأحدة وأنا ربكم فاعبدون} ج٢ ص١٨٢
{إن هم إلا كالأنعام} ج٣ ص٢١
{إن يسرق فقد سرق أخ له من قبل} ج١ ص١٧٥
{إنا أخلصناهم بخالصة ذكرى الدار} ج٣ ص١٣٩
{إنا أنزلناه في ليلة مباركة إنا كنا منذرين} ج٢ ص١٧٧
{إنا أنشأناهن أنشاء} ج٢ ص٣٧
{إنا عرضنا الأمانة على السموات والأرض والجبال فأبين أن يحملنها} ج٣ ص٢٨
{إنا كذلك نجزي المحسنين} ج٢ ص٣٢٦
{إنا كما لكم تبعا} ج١ ص١٨٧
{أنتم وآباؤكم الأقدمون} ج٣ ص١٢٨
{أنزلت من السماء ماء فسالت أودية بقدرها فاحتمل السيل زبدا رابيا} ج٣ ص٦٣
{إنك أنت الأعلى} ج٢ ص١٩٥
{إنما جزاء الذين يحاربون الله ورسوله ويسعون في الأرض فإذا أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف} ج١ ص٣١٢
{إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل} ج٢ ص٢٤١','4','469',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (453,'{إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب} ج١ ص٣١٦
{إنما مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض مما يأكل الناس والأنعام} ج٢ ص١٣٩
{إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا} ج٣ ص٨
{إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم يذهبوا حتى يستأذنوه} ج١ ص٩٥، ج٣ ص٦
{إنما يخشى الله من عباده العلماء} ج١ ص٤٥
{إنما يزيد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا} ج١ ص٢٩١
{إنما يستأذنك الذين لا يؤمنون بالله واليوم الآخرة، وارتابت قلوبهم، فهم في ريبهم يترددون} ج٢ ص٣٦٦
{إنما يعمر مساجد الله من آمن بالله واليوم الآخر} ج١ ص٢٩٣، ٣٠٧
{إنه لقرآن كريم} ج٣ ص٤١
{إنها كلمة هو قائلها} ج١ ص١٨٤
{إني آمنت بربكم فاسمون} ج٢ ص١٧٧، ٢٨٢
{إني إذا لفي ضلال مبين} ج٢ ص٢٨٢
{إني أراني أعصر خمرا} ج٢ ص٨٩
{إني لكم رسول أمين} ج٣ ص٧
{اهدنا الصراط المستقيم} ج٢ ص١٧٣، ٢٠٣
{أو جاء أحد منكم من الغائط} ج١ ص١٠٨
{أو لا مستم النساء} ج٣ ص٥١، ٥٣، ٥٤
{أو لم يروا إلى ما خلق الله من شيء يتفيأ ظلاله عن اليمين والشمائل} ج٣ ص٤٨
{أو لم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم} ج١ ص١٩٨','4','470',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (454,'{أو لم يروا كيف يبديء الله الخلق ثم يعيده إن ذلك على الله يسير}
ج٢ ص١٩٩
{أولى لك فأولىؤ ج٣ ص١٠
{أولئك الذين طبع الله على قلوبهم وسمعهم وأبصارهم وأولئك هم الغافلون} ج٣ ص١٥٨
{أولئك الذين لهم سوء العذاب وهم في الآخرة هم الأخسرون} ج٣ ص١٢
{أولئك على هدى من ربهم وأولئك هم المفلحون} ج٢ ص٢٨١
{أولئك لهم الأمن} ج٢ ص٣٤٩
{أو يزوجهم ذكرانا وإناثا ويجعل من يشاء عقيما إنه عليم قدير} ج٢ ص٢٣٣
{أو ينفعونكم أو يضرون} ج٣ ص١٢٨
{إياك نعبد وإياك نستعين} ج٢ ص١٧٣، ٢١٨، ٢٣٠ ج٣ ص١٨٧
{ائتوني به أستخلصه لنفسي فلما كلمه قال إنك اليوم لينا مكين أمين} ج٢ ص٣٠٤
{أيحب أحدكم أن يأكل لحم أخيه ميتا} ج٣ ص٦٢
{أئذا متنا وكنا ترابا ذلك رجع بعيد} ج٢ ص٣٢٠
{أئذا متنا وكنا ترابا وعظما أئنا لمدينون} ج٣ ص٢٠١
ب-
{بدت لهما سوءاتهما وطفقا يخصفان عليهما من ورق الجنة} ج١ ص١٧٩
{بسم الله الرحمن الرحيم} ج٣ ص٧
{بل الله فاعبد وكن من الشاكرين} ج٢ ص٢١٨
{بل عجبوا أن جاءهم منذر منهم فقال الكافرون هذا شيء عجيب} ج٢ ص٣٢٠
{بل كذبوا بالحق لما جاءهم فهم في أمر مريج} ج١ ص٢٧٢','4','471',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (455,'{بل كذبوا بالساعة وأعتدنا لمن كذب بالساعة سعيرا} ج١ ص٣٣٣
{بما غفر لي ربي وجعلني من المكرمين} ج٢ ص٢٨٢
{بنصر الله ينصر من يشاء وهو العزيز الرحيم} ج٢ ص٣٦٧
ت-
{تالله إن كنا لفي ضلال مبين} ج٣ ص١٢٩
{تتبعها الرادفة} ج٢ ص٣٢٠
{ترى الظالمين مشفقين مما كسبوا وهو واقع بهم} ج١ ص٢٧٩
{تكاد السموات يتفطرن منه وتنشق الأرض وتخر الجبال هدا} ج١ ص٣٣٤
{تلك إذا قسمة ضيزى} ج١ ص٢٢٩
{تلك الدار الآخرة يجعلها للذين لا يريدون علوا في الأرض ولا فسادا} ج١ ص٣٤١
{تلك عشرة كاملة} ج٣ ص٣٠
{تنزيلا ممن خلق الأرض والسموات العلى} ج١ ص٢٧٢
ث-
{ثم إذا شاء أنشره} ج٢ ص٣٣٦، ٢٣٧، ٣٣٣
{ثم استوى إلى السماء وهي دخان فقال لها وللأرض ائتيا طوعا أو كرها قالتا أتينا طائعين} ج٢ ص٨١، ١٧٦
{ثم أماته فأقبره} ج٢ ص٢٣٦، ٢٣٧، ٣٣٣
{ثم إن ربك للذين عملو السوء بجهالة ثم تابوا من بعد ذلك وأصلحوا إن ربك من بعدها لغفور رحيم} ج٣ ص١٦
{ثم إن علينا حسابهم} ج٢ ص٢٢٤
{ثم أنزل الله سكينة على رسوله وعلى المؤمنين} ج٢ ص٢٠٠
{ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات} ج٢ ص٢٣١
{ثم أولى لك فأولى} ج٣ ص١٠','4','472',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (456,'{ثم جاءهم ما كانوا يوعدون} ج١ ص٢٨، ج٣ ص٢٠٢
{ثم الجحيم صلوه} ج٢ ص٢١٩
{ثم جعلناه نطفة في قرار مكين} ج٢ ص٢٣٨
{ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظاما فكسونا العظام لحما ثم أنشأناه خلقا آخر} ج٢ ص٢٣٨
{ثم السبيل يسره} ج٢ ص٢٣٦، ٢٣٧، ٣٣٣
{ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه} ج٢ ص٢٢٠
{ثم قتل كيف قدر} ج٢ ص٩
{ثم يأتي من بعد ذلك سبع شداد يأكلن ما قدمتم لهن إلا قليلا مما تحصنون} ج٢ ص٢٨٩
{ثم يأتي من بعد ذلك عام فيه يغاث الناس وفيه يعصرون} ج٢ ص٢٨٩
{ثم يجزا الجزاء الأوفى} ج١ ص٢٩٤، ٣٢٥
ح-
{حافظوا على الصلوات والصلاة والوسطى} ج٣ ص٢٨، ٣٥
{حتى إذا فتحت يأجوج ومأجوج وهم من كل حدب ينسلون} ج٢ ص٣٠٩
{حتى إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعلمون} ج٣ ص١٥٨
{حتى توارت بالحجاب} ج٢ ص٢٩٦
{حم والكتاب المبين} ج٢ ص١٧٧
{الحمد لله رب العالمين} ج٢ ص١٧٣، ٢١٩، ج٣ ص٧
{حنفاء لله غير مشركين به ومن يشرك بالله فكأنما خر من السماء فتخطفه الطير أو تهوى به الريح في مكان سحيق} ج٢ ص١٨٨
خ-
{خالدين فيه وساء لهم يوم القيامة حملا} ج١ ص٣٧٩','4','473',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (457,'{خالدين فيها لا يجدون وليا ولا نصيرا ج١ ص٢٧١
{خالدين فيها ما دامت السموات والأرض إلا ما شاء ربك إن ربك فعال لما يريد ج٣ ص١٧٤
{خذ العفو وأمر بالعرف وأعرض عن الجاهلين ج٢ ص٣٤٩
{حذوه قتلوه ج٢ ص٢١٩
{خلق الإنسان من علق ج١ ص٣٧٦
ذ-
{ذرهم يأكلوا ويتمتعوا ج١ ص٣٨٣
{ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم ج١ ص١٢٣
{ذلك ومن يعظم حرمات الله فهو خير له عند ربه ج٢ ص١٨٨
{ذلكم بما كنتم تفرحون في الأرض بغير الحق وبما كنتم تمرحون ج١ ص٣٥٠
{ذلكم قولكم بأفواهكم ج٢ ص٣٦٠
ر-
{رب إني نذرت لك ما في بطني محررا ج١ ص٢١١
{رب هب لي حكما وألحقني بالصالحين ج٣ ص١٢٨
{الرحمن الرحيم ج٢، ١٧٣، ٢١٩، ج٣ ص٧
{الرحمن على العرش استوى ج١ ص٢٧٢
{رحمة من ربك إنه هو السميع العليم ج٢ ص١٧٨
س-
{سأصليه سقر ج١ ص٣٨٣
{سبحان الذي أسرى بعبده ليلا من المسجد الحرام إلى المسجد الأقصى الذي باركنا حوله} ج٢ ص١٧٥
ش-
{شاكرا لأنعمه اجتباه وهداه إلى صراط مستقيم} ج٣ ص١٨٧','4','474',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (458,'ص-
{ص والقرآن ذي الذكر} ج٢ ص٢٠١
{صراط الذين أنعمت عليهم} ج٢ ص١٧٣، ٢٠٣
{صفراء فاقع لونها تسر الناظرين} ج١ ص٦٠
ط-
{طه} ج١ ص٢٧٢
ع-
{عربا أترابا} ج١ ص٣٧
غ-
{غير المغضوب عليهم} ج٢ ص١٧٤
ف-
{فأتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم} ج٢ ص٣٤٨
{فأتت به قومها تحمله} ج١ ص١٨٣
{فاتقوا الله وأطيعون} ج٣ ص٧
{فأثرن به نقعا} ج١ ص٢٧٢، ٣٣٣
{فأجاءها المخاض إلى جذع النخلة قالت ياليتني مت قبل هذا وكنت نسيا منسيا} ج٢ ص٢٢٧
{فأجمعوا أمركم وشركاءكم} ج٢ ص٣٠١
{فأخذناهم أخذ عزيز مقتدر} ج٢ ص٢٥٠
{فأدخلناه في رحمتنا} ج٢ ص٨٥
{فإذا انسلخ الأشهر الحرام فاقتلوا المشركين حيث وجدتموهم} ج١ ص٤٠٦
{فإذا طعمتم فانتشروا ولا مستأنسين لحديث} ج١ ص٢١٥
{فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم} ج٢ ص٢٨٥
{فإذا لقيتم الذين كفروا فضرب الرقاب} ج٢ ص٣٠٢
{فإذا نفخ في الصور نفخة واحدة} ج٢ ص٣٦٢
{فإذ نقر في الناقور فذلك يومئذ يوم عسير على الكافرين غير يسير} ج٣ ص٣٣
{فأرسلنا عليهم الطوفان والقمل والضفادع والدم آيات مفصلات} ج١ ص٢١٧','4','475',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (459,'{فالسابقات سبقا} ج٢ ص٣٢٠
{فاستفتهم أهم أشد خلقا أم من خلقنا إنا خلقناهم من طين لازب} ج١ ص١٤٩
{فأصبح في المدينة خائفا يترقب فإذا الذي استنصره بالأمس يستصرخه قال له موسى إنك لغوي مبين} ج٣ ص١٢
{فاعبدوا ما شئتم من دونه} ج٣ ص٥
{فأقبل بعضهم على بعض يتساءلون} ج٣ ص٢٨، ٢٠١
{فاكهة ونخل ورمان} ج١ ص٨٨
{فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم} ج١ ص٣٧٦
{فالذين آمنوا به وعزروه ونصروه واتبعوا النور الذي أنزل معه أولئك هم المفلحون} ج١ ص٢٦١
{فالعاصفات عصفا} ج١ ص٣٣٦
{فالمدبرات أمرا} ج٢ ص٣٢٠
{فالمغيرات صبحا} ج١ ص٢٧٢، ٣٣٣
{فالموريات قدحا} ج١ ص٣٧٢، ٣٣٣
{فأما الذين اسودت وجوههم أكفرتم بعد إيمانكم} ج٢ ص٤٢٦
{فأما الذين شقوا ففي النار لهم فيها زفير زشهيق} ج٢ ص٢٣٢ ج٣ ص١٧٤
{فأما من أوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه} ج١ ص٢١٦
{فأما اليتيم فلا تقهر} ج٢ ص٢٢١
{فإن تولوا فقل حسبي الله لا إله إلا هو عليه توكلت وهو رب العرش العظيم} ج١ ص٣٣٧
{فانطلقا حتى إذا ركبا في السفينة خرقها} ج١ ص١٨٨
{فانطلقا حتى إذا لقيا غلاما فقتله قال أقتلت نفسا زكية بغير نفس لقد جئت شيئا نكرا} ج٢ ص١٩٣
{فانظر إلى آثار رحمة الله كيف يحيى الأرض بعد موتها} ج١ ص١٧٢','4','476',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (460,'{فإنكم وما بعبدون} ج٣ ص٢٨، ٢٠١
{فإنها لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور} ج٢ ص٣٦٤، ٤٠٠
{فإنهم عدو لي إلى رب العالمين} ج٣ ص١٢٨
{فأوجس في نفسه خيفة موسى} ج٢ ص١٩٤، ٢١٩
{فبأي آلاء ربكما تكذبان} ج٣ ص٢٠
{فبما رحمة من الله لنت لهم} ج٢ ص٩٣
{فجعلناهن أبكارا} ج٢ ص٣٧
{فحملته فانتبذت به مكانا قصيا} ج٢ ص٢٣٧
{فخر عليهم السقف من فوقهم} ج٢ ص٣٩١
{فخرج على قومه في زينته قال الذين يريدون الحياة الدنيا ياليت لنا مثل ما أوتي قارون إنه لذو حظ عظيم} ج١ ص١٩٠
{فخرج على قومه من المحراب فأوحى إليهم أن سبحوا بكرة وعشيا} ج٢ ص٢٩٣
{فخلف من بعدهم خلف أضاعوا الصلاة واتبعوا الشهوات فسوف يلقون غيا} ج١ ص٣١١
{فذرهم يخوضوا ويلعبوا} ج٢ ص٣٠٢
{فذكر فما أنت بنعمة ربك بكاهن ولا محنون} ج١ ص٣٧٦
{فذوقوا عذابي ونذر} ج٣ ص١٩
{فرددناه إلى أمه كي تقر عينها} ج٢ ص٢٩٠
{فسقى لهما ثم تولى إلى الظل فقال رب إني لما أنزلت إلي من خير فقير} ج٢ ص٣٠٥
{فسيكفيكهم الله} ج١ ص٢٦٥
{فغشاها ما غشي} ج٢ ص٢٠٦
{فقال لهم رسول الله ناقة الله وسقياها} ج٢ ص٢٩٨','4','477',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (461,'{فقال الملأ الذين كفروا من قومه ما نراك إلا بشر مثلنا وما نراك اتبعك إلا الذين هم أراذلنا بادي الرأي وما نرى لكم علينا من فضل} ج٣ ص٧٢
{فقبضت قبضة من أثر الرسول} ج٢ ص٣١٠
{فقتل كيف قدر} ج٣ ص٩
{فقد صنت قلوبكما} ج١ ص٥٥
{فقضاهن سبع سموات في يومين وأوحى في كل سماء أمرها} ج٢ ص١٧٦
{فقلت استغفروا ربكم إنه كان غفارا} ج٢ ص٢٥١
{فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا فدمرناهم تدميرا} ج٢ ص٣٠٢
{فقلنا اضرب بعصاك الججر فانفجرت منه اثنتا عشرة عينا} ج٢ ص٢٨٧
{فقلنا اضربوه ببعضها كذلك يحيى الله الموتى ويريكم آياته لعلكم تعقلون} ج٣ ص٤٣
{فكان عاقبتهما أيهما في النار خالدين فيها} ج٢ ص١٢
{فكبكبوا فيها هم والغاوون} ج٢ ص٢٥٢، ج٢ ص١٢٩
{فلا أقسم بمواقع النجوم} ج٣ ص٤١
{فلا تعجل عليهم إنما نعد لهم عدا} ج١ ص٢٧٨
{فلا تقل لهما ألف ولا تنهرهما} ج٢ ص٢١٤
{فلما أتاهم نودي من شاطئ الواد الأيمن في البقعة المباركة من الشجرة أن يا موسى إني أنا الله} ج١ ص٣٨٨
{فلما أسلما وتله للجبين} ج٢ ص٥
{فلما أن أراد أن يبطش بالذي هو عدو لهما قال يا موسى أتريد أن تقتلني كما قتلت نفسا بالأمس} ج٣ ص١٢
{فلما أن جاء البشير ألقاه على وجهه فارتد بصيرا قال ألم أقل لكم إني أعلم من الله ما لا تعلمون} ج٢ ص٢٩٠، ج٣ ص١٣','4','478',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (462,'{فلما جاءتهم آياتنا مبصرة قالوا هذا سحر مبين} ج٣ ص١٨٧
{فلما دخلوا على يوسف آوى إليه أبويه وقال ادخلوا مصر إن شاء الله آمنين} ج٢ ص٢٩٠
{فلما ذهبوا به وأجمعوا أن يجعلوه في غيابة الجب} ج٢ ص٣٠٣
فله ما سلف} ج٢ ص٣٣٧
{فلو أن لنا كرة فتكون من المؤمنين} ج٣ ص١٢٩
{فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم} ج١ ص٣٥٢
{فليضحكو قليلا وليبكوا كثيرا} ج٣ ص١٤٤
{فما بكت عليه السماء والأرض وما كانوا منظرين} ج٢ ص٨١
{فما لنا من شافعين} ج٣ ص١٢٩
{فمن جاءه موعظة من ربه فانتهى فله ما سلف} ج٢ ص٢٣٥
{فمن كان منكم مريضا أو به أذى من رأسه ففدية} ج١ ص٣١٧
{فمن كان منكم مريضا أو على سفر فعدة من أيام أخر} ج٢ ص٣١٨
{فمن نكث فإنما ينكث على نفسه فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات} ج١ ص٣٣
{فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الارض ومنهم من أغرقنا وما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون} ج٣ ص٢٠٧
{فورب السماء والأرض إنه الحق مثل ما أنكم تنطقون} ج٣ ص٢٠٤
{فوسطن به جمعا} ج١ ص٢٧٢، ٣٣٣
{في بضع سنين لله الأمر من قبل ومن بعد ويؤمئذ يفرح المؤمنون} ج٢ ص٣٩٧','4','479',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (463,'{في سدر مخضود} ج١ ص٣٣٥، ٣٧٧
{فيها يفرق كل أمر حكيم} ج٢ ص١٧٨
{فيها فاكهة ونخل ورمان} ج٣ ص٢٨
{فيهما من كل فاكهة زوجان} ج٢ ص٣٦٩
ق-
{ق. والقرآن المجيد} ج٢ ص٣٢٠
{قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق} ج٣ ص١١، ٩
{قال أراغب أنت عن آلهتني يا إبراهيم لئن لم تنته لأرجمنك واهجرني مليا} ج٢ ص٣٧٧، ٢٢٢
{قال الذي عنده علم من الكتاب أنا آتيك من قبل أن يرتد إليك طرفك} ج٢ ص٢٩٤
{قال أفرأيتم ما كنتم تعبدون} ج٣ ص١٢٨
{قل ألم أقل لك إنك لن تستطيع معي صبرا} ج٢ ص١٩٣
{قال إنما أشكو بثي وحزني إلى الله وأعلم من الله ما لا تعلمون} ج١ ص٨٦
{قال إني ليحزنني إن تذهبوا به وأخاف أن يأكله الذئب وأنتم عنه غافلون} ج٢ ص٣٠٣
{قال تزرعون سبع سنين دأبا فما حصدتم فذروه في سنبله إلا قليلا مما تأكلون} ج٢ ص٢٨٦
{قال رب اجعل لي آية قال آيتك ألا تكلم الناس ثلاث ليال سويا} ج٢ ص٢٩٢
{قال رب أني يكون لي غلام وكانت امرأتي عاقرا وقد بلغت من الكبر عتيا} ج٢ ص٢٩٢
{قال سننظر أصدقت أم كنت من الكاذبين} ج٢ ص٢٩١
{قال سوف استغفر لكم ربي إنه هو الغفور الرحيم} ج٢ ص٢٩٠','4','480',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (464,'{قال عفريت من الجن أنا آتيك به قبل أن تقوم من مقامك وإني عليك لقوي أمين} ج٢ ص٢٩٣
{قال عيسى بن مريم اللهم ربنا أنزل علينا مائدة من السماء} ج١ ص١٨٢
{قال قائل منهم إني كان لي قرين} ج٣ ص٢٠١
{قال قد أوتيت سؤلك يا موسى} ج٢ ص٢٠٢
{قال قرينه ربنا ما أطغيته ولكن كان في ضلال بعيد} ج١ ص٣٧٧
{قال كذلك قال ربك هو على هين وقد خلقتك من قبل ولم تك شيئا} ج٢ ص٢٩٢
{قال كذلك قال ربك هو على هين ولنجعله آية للناس ورحمة منا وكان أمرا مقضيا} ج٢ ص٢٨٤
{قال لا تختصموا لدي وقد قدمت إليكم بالوعيد} ج١ ص٣٧٧
{قال ما خطبكن إذ راودتن يوسف عن نفسه} ج٢ ص٣٠٤
{قال الملأ من قومه إنا لنراك في ضلال مبين} ج٢ ص٢١١
{قال نكروا لها عرشها ننظر أتهتدي أم تكون من الذين لا يهتدون} ج٢ ص٢٩٤
{قال هل يسمعونكم إذ تدعون} ج٢ ص١٢٨
{قال يابن أم لا تأخذ بلحيتي ولا برأسي} ج٣ ص١٨١
{قال يا أيها الملأ يكم يأتيني بعرشها قبل أن يأتوني مسلمين} ج٢ ص٢٩٣
{قال يا قوم ليس بي ضلالة ولكني رسول الله من رب العالمين} ج٢ ص٢١١
{قال يا هارون ما معنك إذ رأيتهم ضلوا} ج٢ ص٢٩٣
{قالت أنى يكون لي لغلام ولم يمسسني بشر ولم أك بغيا} ج٢ ص٢٨٤','4','481',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (465,'{قالت يا أيها الملأ إني ألقي إلي كتاب كريم} ج٢ ص٢٩١
{قالوا أأنت فعلت هذا بآلهتنا يا إبراهيم قال بل فعله كبيرهم هذا فاسألوهم إن كانوا ينطقون} ج٣ ص٧٢
{قالوا بل وجدنا آباءنا كذلك يفعلون} ج٣ ص١٢٨
{قالوا تالله إنك لفي ضلالك القديم} ج٢ ص٢٤١
{قالوا تالله تفتأ تذكر يوسف} ج٢ ص٣٢٧
{قالوا بالله لقد علمتم ما جئتنا لنفسد في الأرض وما كنا سارقين} ج٣ ص٤٢
{قالوا لن نبرح عليه عاكفين حتى يرجع إلينا موسى} ج٢ ص٢٩٣
{قالوا لئن أكله الذئب ونحن عصبة إنا إذا لخاسرون} ج١ ص٣٠٣
{قالوا نعبد أصناما فنظل لها عاكفين} ج٣ ص١٢٨
{قالوا نفقد صواع الملك ولمن جاء به حمل بعير وأنا به زعيم} ج٣ ص٤٢
{قالوا وهم فيها يختصمون} ج٢ ص١٢٩
{قالوا يا أبانا استغفر لنا ذنوبنا إنا كنا خاطئين} ج٢ ص٢٩٠
{قالوا يا أبانا مالك لا تأمنا على يوسف وإنا له لناصحون} ج٢ ص٢٤٤، ٣٠٣
{قالوا يا مريم لقد جئت شيئا فريا} ج١ ص١٢٧
{قالوا يا موسى إما أن تلقى وإما أن نكون نحن الملقين} ج٢ ص١٩٢
{قتل الإنسان ما أكفره} ج٢ ص٢٣٩، ٢٣٧، ٣٣٣
{قد جعل ربك تحتك سريا} ج١ ص١٢٧
{قد صدقت الرؤيا إنا كذلك نجزي المحسنين} ج٢ ص٣٢٥','4','482',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (466,'{قد كانت لكم أسوة حسنة في إبراهيم والذين معه إذ قالوا لقومهم إنا براء منكم وما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء أبدا حتى تؤمنوا بالله وحده} ج٣ ص٣٤
{قد مكر الذين من قبلهم فأتى الله بنيانهم من القواعد فخر عليهم السقف من فوقهم وأتاهم العذاب من حيث لا يشعرون} ج٢ ص٣٦١
{قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل على مثله} ج٢ ص٣١٩
{قل أفغير الله تأمروني أعبد أيها الجاهلون} ج٢ ص٢١٨
{قل الله أعبد مخلصا له ديني} ج٣ ص٥
{قل اللهم مالك الملك تؤتي الملك من تشاء وتنزع الملك ممن تشاء وتعز من تشاء وتذل من تشاء بيدك الخير} ج٢ ص١٩٥
{قل أمر ربي بالقسط وأقيموا وجوهكم عند كل مسجد وادعوه مخلصين له الدين} ج٢ ص١٨٤
{قل إن ضللت فإنما أضل على نفسي وإن اهتديت فبما يوحى إلي ربي} ج٣ ص١٦٢
{قل إنما أعظكم بواحدة أن تقوموا لله مثنى وفرادى ثم تتفكروا ما بصاحبكم من جنة} ج٢ ص٢٠٥
{قل إنما أمرت أن أعبد الله مخلصا له الدين} ج٣ ص٥
{قل إني أخاف أن عصيت ربي عذاب يوم عظيم} ج٣ ص٥
{قال الروح من أمر ربي} ج١ ص١٢٣
{قل سيروا في الأرض فانظروا كيف بدا الخلق ثم الله ينشئ النشأة الآخرة} ج٢ ص١٩٩
{قل لا أسألكم عليه أجرا إلا المودة في القربى} ج٢ ص٢٩٠','4','483',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (467,'{قل من يرزقكم من السموات والأرض قل الله وإنا أو إياكم لعلى هدى أو في ضلال مبين} ج٢ ص٢٤٠
{قل يا قوم اعملوا على مكانتكم أني عامل فسوف تعملون} ج٢ ص٢٨٣
{قل يا أيها الكافرون} ج٣ ص٧
{قلنا لا تخف إنك أنت الأعلى} ج٢ ص١٩٤، ١٩٦، ٢١٩
{قم فأنذر} ج١ ص٣٣٦، ٤١٢
{قيل أدخل الجنة قال يا ليت قومي يعلمون} ج٢ ص٢٨٢
{قيل ادخلوا أبواب جهنم خالدين فيها فبئس مثوى المتكبرين} ج١ ص٢٤١
ك-
{كأنهن بيض مكنون} ج٢ ص١٣٠
{كذبت قبلهم قوم نوح وعاد وفرعون ذو الأوتاد} ج٣ ص٩
{كل حزب بما لديهم فرحون} ج١ ص٣٩٤
{كلا إذا بلغت التراقي} ج٢ ص٢٦٦
{كلا سيكفرون بعبادتهم ويكونون عليهم ضدا} ج١ ص٣٧٨
{كلا لما يقض ما أمره} ج٢ ص٣٣٣
{كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص} ج٢ ص٢٠١
{كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر وتؤمنون بالله} ج١ ص٣١١
ل-
{لا أعبد ما تعبدون} ج٣ ص٧
{لا تبقي ولا تذر} ج١ ص٣٨٣
{لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا} ج١ ص٩٣
{لا تحسبن الذين يفرحون بما أتوا ويحبون أن يحمدوا بما لم يفعلوا فلا تحسبنهم بمفازة من العذاب} ج٣ ص١٦، ١٨','4','484',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (468,'{لا تصاحبني قد بلغت من لدني عذرا} ج١ ص١٨٧
{لأصحاب اليمين} ج٢ ص٣٧
{لا علم لنا إلا ما علمتنا أنك أنت العليم الحكيم} ج١ ص٣٢٤
{لا فيها غول ولا هم عنها ينزفون} ج٢ ص٢٢٥
{لا يأتيه الباطل من بين يديه لا من خلفه تنزيل من حكيم حميد} ج١ ص٢٨٨، ٣٠٥
{لا يستأذنك الذين يؤمنون بالله واليوم الآخر أن يجاهدوا بأموالهم، وأنفسهم، والله عليم بالمتقين} ج٢ ص٢٦٦
{لا يستوى أصحاب النار وأصحاب الجنة أصحاب الجنة هم الفائزون} ج٣ ص١٦٨
{لا يستوي منكم من أنفق من قبل الفتح وقاتل أولئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا} ج٢ ص٢٠٧
{لقالوا إنما سكرت أبصارنا بل نحن قوم مسحورون} ج٢ ص٣٢٤
{لقد جاءكم رسول من أنفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم} ج١ ص٣٣٧
{لقد جئتم شيئا إدا} ج١ ص٣٣٤، ٤٨١
{لقد كنت في غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد} ج٢ ص٣٣٦
{لكل أجل كتاب} ج١ ص٢٠٧
{لكم دينكم ولي دين} ج٣ ص٧
{لكيلا تأسوا على ما فاتكم ولا تفرحوا بما آتاكم} ج٣ ص١٤٤
{لله الأمر من قبل ومن بعد} ج٢ ص٣١١
{لله ملك السموات والأرض يخلق ما يشاء يهب لمن يشاء إناثا ويهب لمن يشاء الذكور} ج٢ ص٢٣٣','4','485',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (469,'{له الحكم وإليه ترجعون} ج٢ ص٢٢٥
{له ما في السموات وما في الأرض وما بينهما وما تحت الثرى} ج١ ص٢٧٢
{لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء} ج٢ ص٣٠٧
{لو أن لي بكم قوة أو آوى إلى ركن شديد} ج٢ ص٣٢٣
{لو نشاء جعلناه أجاجا فلولا تشكرون} ج٢ ص٢٤٤
{لو نشاء لجعلناه حطاما فظلتم تفكهون} ج٢ ص٢٤٤
{لو يعلم الذين كفروا حين لا يكفون عن وجوههم لنار ولا عن ظهورهم ولا هم ينصرون} ج٢ ص٣٢٢
{ليبلوكم أيكم أحسن عملا} ج١ ص٣٥٨
{ليحق الحق ويبطل الباطل ولو كره المجرمون} ج٣ ص٥
{ليس كمثله شيء وهو السميع البصير} ج٣ ص٦١
{ليستخلفنهم في الأرض} ج١ ص٢٦٥
م-
{ما اتخذ الله من ولد وما كان معه من إله إذا لذهب كل إله بما خلق ولعلا بعضهم على بعض} ج٢ ص٣٢٠
{ما أغنى عنهم ما كانوا يمتعون} ج٣ ص٢٨، ٢٠٢
{ما أغنى عني ماليه} ج١ ص٢١٦
{ما إن مفاتحه لتنوء بالعصبة} ج١ ص١٨٩
{ما أنتم عليه بفاتنين} ج٣ ص٢٨، ٢٠١
{ما أنزلنا عليك القرآن لتشقى} ج١ ص٢٧٢
{ما جعل الله لرجل من قلبين في جوفه} ج٢ ص٢١١، ٣٦٠، ٣٦٤
{ما ضل صاحبكم وما غوى} ج١ ص٢٢٩، ٣٣٦
{ما فرطنا في الكتاب من شيء} ج٣ ص١٧٨
{ما كذب الفؤاد ما رأى} ج١ ص٢١٢','4','486',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (470,'{مالك يوم الدين} ج٢ ص١٧٣، ٣١٩ ج٣ ص٧
{ما لهذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها} ج٢ ص٢١٣
{ما ودعك ربك وما قلى} ج١ ص٢٤٢
{ما يلفظ من قول إلا لديه رقيب عتيد} ج٢ ص٣٣٦
{مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا وإن أوهن البيوت لبيت العنكبوت} ج٣ ص٢٠٧
{مثل الذين كفروا بربهم أعمالهم كرماد اشتدت به الريح في يوم عاصف} ج٢ ص١٤٩
{مثلهم كمثل الذي استوقد نارا فلما أضاءت ما حوله ذهب الله بنورهم وتركهم في ظلمات لا يبصرون} ج٢ ص١٣٩، ٢١٠
{من أعرض عنه فإنه يحمل يوم القيامة وزرا} ج١ ص٣٧٩
{من أي شيء خلقه} ج٢ ص٢٢٦، ٢٣٧، ٣٣٣
{من دون الله هل ينصرونكم وينتصرون} ج٣ ص١٢٩
{من عمل سيئة فلا يجزي إلا مثلها ومن عمل صالحا من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة} ج٢ ص٢٠٤
{من كان يريد حرث الآخرة نزد له في حرثه ومن كان يريد حرث الدنيا نؤته منها وما له في الآخرة من نصيب} ج١ ص٣٧٩
{من كفر فعليه كفره} ج٢ ص٣٣٥، ج٣ ص١٥٩
{من نطفة خلقه فقدره} ج٢ ص٢٣٦، ٢٣٧، ٢٣٨، ٢٣٣
{من يأتيه عذاب يخزيه ومن هو كاذب وارتقبوا إني معكم رقيب} ج٢ ص٢٨٢
{من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم} ج٢ ص٢٨٣
ن-
{النار يعرضون عليها غدوا وعشيا ويوم تقوم الساعة ادخلوا آل فرعون أشد العذاب} ج١ ص١٨٧','4','487',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (471,'{نساؤكم حرث لكم} ج٢ ص١٣٣
{نسوا الله فنسيهم ج٣ ص١٥٩
هـ-
{هذا خلق الله فأروني ماذا خلق الذين من دونه} ج٢ ص٣٧١
{هذا ذكر وإن للمتقين لحسن مآب} ج٣ ص١٣٩
{هل في ذلك قسم لذي حجر} ج٢ ص٣١٩
{هلك عني سلطانيه} ج٢ ص٢١٦
{هن لباس لكم وأنتم لباس لهن} ج٢ ص١٣٣
{هو الذي جعل الشمس ضياء والقمر نورا} ج٢ ص٢١٠
{هو الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا} ج٣ ص١٧٤
{هو الذي يسيركم في البر والبحر حتى إذا كنتم في الفلك وجرين بهم بريح طيبة وفرحوا بها جاءتها ريح عاصف} ج٢ ص١٨١
و {وآتينا ثمود الناقة مبصرة} ج٢ ص٣١٣
{وآتيناه الحكم صبيا} ج١ ص١٨٠
{وآتيناهما الكتاب المستبين} ج١ ص٣٧٨
{وآية لهم الليل نسلخ منه النهار فإذا هم مظلمون} ج٢ ص١٣٤
{واتخذوا من دون الله آلهة ليكونوا لهم عزا} ج١ ص٣٧٨
{واتل عليهم نبأ إبراهيم} ج٣ ص١٢٨
{واجعل لي لسان صدق في الآخرين} ج٣ ص١٢٩
{واجعلني من ورثة جنة النعيم} ج٣ ص١٢٩
{واختار موسى قومه سبعين رجلا لميقاتنا فلما أخذتهم الرجفة قال رب لو شئت أهلكتهم من قبل وإياي أتهلكنا من فعل السفهاء منا} ج٣ ص١٣١','4','488',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (472,'{واخفض لها جناح الذل} ج٢ ص٨٧
{وادخل يدك في جيبك تخرج بيضاء من غير سوء في تسع آيات إلى فرعون وقومه إنهم كانوا قوما فاسقين} ج٣ ص١٨٧
{وإذ غدوت من أهلك تبوئ المؤمنين مقاعد للقتال} ج١ ص٢٦٢
{وإذ قال الله يا عيسى بن مريم أأنت قلت للناس اتخذوني وأمي آلهين من دون الله قال سبحانك ما يكون لي أن أقول ما ليس لي بحق إن كنت قلته فقد علمته} ج٢ ص١٩٦، ج٣ ص١٨٢
{وإذا قتلتم نفسا فادارأتم فيها والله مخرج ما كنتم تكتمون} ج٣ ص٣٣
{وإذ يرفع إبراهيم القواعد من البيت وإسماعيل} ج٢ ص٢٠٥
{وإذ بربكموهم إذ التقيتم في أعينكم قليلا ويقللكم في أعينهم ليقضي الله أمرا كان مفعولا وإلى الله ترجع الأمور} ج١ ص
٣٣٧
{وإذ يعدكم الله إحدى الطائفتين أنها لكم وتودون أن غير ذات الشوكة تكون لكم ويريد الله أن يحق الحق بكلماته ويقطع دابر الكافرين} ج٣ ص٥
{وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا} ج١ ص٣٣٣
{وإذا بدلنا آية مكان آية والله أعلم بما ينزل قالوا إنما أنت مفتر بل أكثرهم لا يعلمون} ج٣ ص٤٢
{وإذا سألك عبادي عني فإني قريب أجيب دعوة الداع إذا دعان} ج١ ص٢٤٢
{وإذا قيل لهم آمنوا كما آمن الناس قالوا أنؤمن كما آمن السفهاء ألا إنهم هم السفهاء ولكن لا يعلمون} ج٣ ص١٦٣
{وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون} ج٢ ص٣٢٩
{وإذا قيل لهم لا تفسدوا في الأرض قالوا إنما نحن مصلحون} ج٣ ص١٦٣','4','489',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (473,'{وإذا كالوهم أو وزنوهم يخسرون} ج١ ص٣١٩
{وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلوا إلى شياطينهم قالوا إنا معكم} ج٢ ص٢٤٢
{وإذا مرضت فهو يشفين} ج٢ ص٢٣٦، ج٣ ص١٢٨
{واذكر إسماعيل واليسع وذا الكفل وكل من الأخيار} ج٣ ص١٣٩
{واذكر عبادنا إبراهيم وإسحاق ويعقوب أولى الأيدي والأبصار} ج٣ ص١٣٩
{واذكر في الكتاب إبراهيم إنه كان صديقا نبيا} ج٢ ص٢٦٢
{واذكر في الكتاب إسماعيل إنه كان صادق الوعد وكان رسولا نبيا} ج١ ص٢٨٥
{والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج} ج١ ص٢٧٢
{وأزلفت الجنة للمتقين} ج٣ ص١٢٩
{واسأل القرية} ج١ ص٨٥، ١١٠،} ج٢ ص٨٢، ٩٥، ٣٠٩
{واشتعل الرأس شيبا} ج٢ ص١٣٤
{وأشرقت الأرض بنور ربها ووضع الكتاب وجيء بالنبيين والشهداء} ج١ ص٢٤١
{وأصحاب اليمين ما أصحاب اليمين} ج١ ص٣٣٥، ٣٧٧
{وأضل فرعون قومه وما هدى} ج٣ ص٣٤٨
{وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو لله وعدوكم} ج١ ص٣١٨
{واغفر لأبي إنه كان من الضالين} ج٣ ص١٢٩
{واقترب الوعد الحق فإذا هي شاخصة أبصار الذين كفروا} ج٢ ص٢٢٢
{واكتب لنا في هذه الدنيا حسنة وفي الآخرة إنا هدنا إليك وقال عذابي أصيب به من أشاء ورحمتي وسعت كل شيء} ج٣ ص١٣١','4','490',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (474,'{والتفت الساق بالساق} ج١ ص٣٥١، ج٢ ص٢٢٥
{والذي أطمع أن يغفر لي خطيئتي يوم الدين} ج٣ ص١٢٨
{والذي هو يطعمني ويسقين} ج٢ ص٢٣٥، ج٣ ص١٢٨
{والذي يميتني ثم يحيين} ج٢ ص٢٣٦ ج٣ ص١٢٨
{والذين إذا انفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما} ج٣ ص١٧٨
{والذين تبوءوا الدار والإيمان من قبلهم} ج٢ ص١١٩
{والذين سعوا في آياتنا معاجزين أولئك لهم عذاب من رجز اليم} ج٣ ص١٥
{والذين كسبوا السيئات جزاء سيئة بمثلها} ج٣ ص١٦٠
{والذين كفروا أعمالهم كسراب بقيمة} ج٢ ص١٣٠
{والذين يحاجون في الله من بعد ما استجيب له حجهم داحضة عند ربهم} ج١ ص٣٧٩
{والذين يرمون أزواجهم ولم يكن والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم فشهادة أحدهم أربع شهادات بالله إنه لمن الصادقين} ج٢ ص٣٢٤، ج٣ ص١٦٤
{والذين يؤتون ما آتوا وقلوبهم وجلة} ج٢ ص٢٨٨
{والذين يؤمنون بما أنزل إليك وما أنزل من قبلك وبالآخرة هم يوقنون} ج٢ ص٢٨١
{والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت فأحيينا به الأرض بعد موتها} ج٢ ص١٨٥
{والله خلق كل دابة من ماء فمنهم من يمشي على بطنه ومنهم من يمشي على رجلين ومنهم من يمشي على أربع} ج٢ ص٢٣٢
{وأما الذين أبيضت وجوههم ففي رحمة الله هم فيها خالدون} ج٢ ص١٧٣
{وأما الذين سعدوا ففي الجنة خالدين','4','491',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (475,'فيها ما دامت السموات والأرض إلا ما شاء ربك عطاء غير مجذوذ} ج٢ ص٢١٢، ج٣ ص١٧
{وأما السائل فلا تنهر} ج١ ص٣٢٣،} ج٢ ص٢٢١
{وامرأة مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها} ج٢ ص٩٥
{وأمرت لأن أكون أول المسلمين} ج٣ ص٥
{وإن تجهر بالقول فإنه يعلم السر وأخفى} ج١ ص٢٧٢
{وإن تعجب فعجب قولهم أئذا كنا ترابا أئنا لفي خلق جديد أولئك الذين كفروا بربهم وأولئك الأغلال في أعناقهم وأولئك أصحاب النار هم فيها خالدون} ج٣ ص١١
{وإن تولوا فاعلموا أن الله مولاكم نعم المولى ونعم النصير} ج١ ص٣٧٧
{وأن سعيه سوف يرى} ج١ ص٢٩٤، ٣١٥
{وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين} ج٣ ص١١
{وإن الفجار لفي جحيم} ج١ ص٣٦١
{وأن ليس للإنسان إلا ما سعى} ج١ ص١٩٤، ٣١٥
{وإن يروا آية يعرضوا ويقولوا سحر مستمر} ج١ ص٣٣٦
{وإنا إذا أذقنا الإنسان منا رحمة فرح بها وإن تصبهم سيئة بما قدمت أيديهم فإن الإنسان كفور} ج٢ ص٢٢٣
{وأنا كنا نقعد منها مقاعد للسمع فمن يستمع الآن يجد له شهابا رصدا} ج١ ص٢٦٣
{وأنا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا} ج١ ص٢٦٣
{وإنا لنحن نحيى ويميت ونحن الوارثون} ج٢ ص٢٤٥
{وأنا منا الصالحون ومنا دون ذلك} ج٢ ص٣١٤
{وأنزلنا من السماء ماء طهورا لنحيي به بلدة ميتا ونسقيه مما خلقنا أنعاما وأناسى كثيرا} ج٢ ص٢٣٠
{وإنه لقسم لو تعلمون عظيم} ج٣ ص٤١','4','492',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (476,'{وإنه لكتاب عزيز} ج١ ص٢٨٨،
{وأنه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا} ج٣ ص٣٠٤
{وأنه هو أمات وأحيا} ج٢ ص٣٠٤
{وإنهم عندنا لمن المصطفين الأخيار} ج٣ ص١٣٩
{وأنهم يقولون ما لا يفعلون} ج٢ ص٩٧
{وأوحينا إلى موسى وأخيه أن تبوآ لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة وأقيموا الصلاة وبشر المؤمنين} ج٢ ص١٨١
{وأورثكم أرضهم وديارهم وأموالهم وأرضا لم تطئوها ج٣ ص٦٢
{وأوفوا بالعهد إن العهد كان مسئولا} ج١ ص٣١٧
{وبرزت الجحيم للغاوين} ج٣ ص١٢٩
{والبلد الطيب يخرج نباته بإذن ربه والذي خبث لا يخرج إلا نكدا} ج١ ص٣١٥
{وتعاونوا على البر والتقوى ولا تعاونوا على الإثم والعدوان} ج١ ص٣١٤
{وتقطعوا أمرهم بينهم كل إلينا راجعون} ج٢ ص١٨٢
{والتين والزيتون} ج١ ص٩٥
{وثمود وقوم لوط وأصحاب الأيكة أولئك الأحزاب} ج٣ ص٩
{وثيابك فطهر} ج١ ص٣٣٦، ٤١٢، ج٣ ص٥٣
{وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد} ج١ ص٣٣٦
{وجاءت كل نفس معها سائق وشهيد} ج٢ ص٣٣٦
{وجحدوا بها واستيقنتها أنفسهم ظلما وعلوا فانظر كيف كان عاقبة المفسدين} ج٣ ص١٨٧
{وجزاء سيئة سيئة مثلها} ج٣ ص١٥٩
{وجزاهم بما صبروا جنة وحريرا} ج٣ ص٣٧
{وجعل لكم من جلود الأنعام بيوتا تستخفونها يوم ظعنكم ويوم إقامتكم ومن أصوافها وأوبارها','4','493',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (477,'{وأشعارها أثاثا ومتاعا} إلى حين ج١ ص٣٨٧
{وجعلنا لكم فيها معايش ومن لستم له برازقين} ج١ ص٥٠
{وجعلنا الليل لباسا} ج٢ ص١٣٣
{وجعلنا الليل والنهار آيتين فمحونا آية الليل وجعلنا آية النهار مبصرة} ج٣ ص١٧٤
{وجنود إبليس أجمعون} ج٢ ص١١٩
{وجوه يومئذ ناضرة} ج١ ص٣٥٠، ج٢ ص٢٢٤، ٢٢٥
{وحرمنا عليه المراضع من قبل فقالت هل أدلكم على أهل بيت يكفلونه لكم وهم له ناصحون:} ج٢ ص٢٩٠
{وحملت الأرض والجبال فدكتا دكة واحدة} ج٢ ص٣٩٢
{وحنانا من لدنا وزكاة وكان تقيا} ج١ ص١٨١
{وحيل بينهم وبين ما يشتهون} ج١ ص٩٤
{والخامسة أن غضب الله عليها إن كان من الصادقين} ج٢ ص٣٢٤، ج٣ ص١٦٤
{والخامسة أن لعنة الله عليه إن كان من الكاذبين} ج٢ ص٣٢٤، ج٣ ص١٦٤
{ودخل جنته وهو ظالم لنفسه قال ما أظن أن تبيد هذه أبدا} ج٢ ص٢٧١
{وربك فكبر} ج١ ص٣٣٦، ٤١٢
{ورتل القرآن ترتيلا} ج٢ ص٢٥٦
{والرجز فاهجر} ج١ ص٣٣٦
{والسابحات سبحا} ج٢ ص٣٢٠
{وسارعوا إلى مغفرة من ربكم وجنة عرضها السموات والأرض} ج٢ ص٢١١
{وسيق الذين اتقوا ربهم إلى الجنة زمرا} ج١ ص٢٤١
{وسيق الذين كفروا إلى جهنم زمرا} ج١ ص٢٤١
{وشارهم في الأمر فإذا عزمت فتوكل على الله} ج١ ص٣٠٨','4','494',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (478,'{والشعراء يتبعهم الغاوون} ج٢ ص٩٧
{والشفع والوتر} ج٢ ص٣١٩
{والشمس تجري لمستقر لها ذلك تقدير العزيز العليم} ج٢ ص٢٢١
{والشمس وضحاها} ج١ ص١٠٨
{والضحى} ج١ ص٢٤٢
{وضرب الله مثلا قرية كانت آمنة مطمئة يأتيها رزقها رغدا من كل مكان} ج٢ ص١١٤
{وطلح منضود} ج١ ص٣٣٥، ٢٧٧
{والطور} ج١ ص٢٧٦
{وظل ممدود} ج١ ص٣٣٥
{وظنوا أنهم ما نعتهم حصونهم من الله} ج٢ ص٢٢١
{والعاديات ضبحا} ج١ ص٢٧٢، ٣٣٣
{والعاقبة للتقوى} ج١ ص١٨٨
{وعجبوا أن جاءهم منذر منهم قال الكافرون هذا ساحر كذاب} ج٢ ص٢٠١
{وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفهم في الأرض كما استخلف الذين من قبلهم} ج٢ ص٢٤٥، ٢٤٧
{وعد الله لا يخلف الله وعده ولكن أكثر الناس لا يعلمون} ج٢ ص٣٦٧
{وعرضوا على ربك صفا لقد جئتمونا كما خلقناكم أول مرة} ج٢ ص٣٠٠
{وعندهم قاصرات الطرف عين} ج٢ ص١٣٠
{والفجر} ج٢ ص٣١٩
{وفعلت فعلتك التي فعلت} ج٢ ص٢٠٥
{وفي السماء رزقكم وما توعدون} ج١ ص١٨١
{وفيها ما تشهيه الأنفس وتلذ الأعين} ج١ ص٩٤
{وقاتلوهم حتى لا تكون فتنة ويكون الدين كله لله} ج١ ص٣٧٧
{وقال الذي آمن يا قوم اتبعون أهدكم سبيل الرشاد} ج٢ ص٢٠٤، ج٣ ص٩','4','495',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (479,'{وقال الذي نجا منهما وادكر بعد أمة أنا أنبئكم بتأويله فأرسلون} ج٢ ص٣٠٣
{وقال الذين أوتوا العلم والإيمان لقد لبثتم في كتاب الله إلى يوم البعث فهذا يوم البعث ولكنكم كنتم لا تعلمون} ج٢ ص٣١٧
{وقال إني ذاهب إلى ربي سيهدين} ج١ ص٨٠
{وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم} ج٢ ص٢٦١
{وقال فرعون يأيها الملأ ما علمت لكم من إنه غيري فأوقد لي يا هامان على الطين} ج١ ص٢٥٩
{وقال فرعون يا هامان ابن لي صرحا لعلى أبلغ الأسباب} ج٢ ص٢٠٥
{وقال الملك أئتوني به فلما جاءا الرسول قال ارجع إلى ربك فأسأله ما بال النسوة اللاتي قطعن أيديهن إن ربي بكيدهن عليم} ج٢ ص٢٨٩، ٣٠٣
{وقالوا تخد الرحمن ولدا} ج٢ ص٣٣٤، ١٧٤
{وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء فنعم أجر العاملين} ج١ ص٢٤١
{وقالوا ربنا عجل لنا قطنا قيل يوم الحساب} ج٢ ص٢٩١
{وقد مكروا مكرهم وعند الله مكرهم وإن كان مكرهم لتزول منه الجبال} ج٢ ص٩٦
{وقضينا إليه ذلك لأمر أن دابر هؤلاء مقطوع مصبحين} ج٢ ص٢٠٢
{والقمر إذا تلاها} ج١ ص٢٠٨
{والقمر قدرناه منازل حتى عاد كالعرجون القديم} ج١ ص٢٢١، ج٢ ص١٢٧
{وقيل لهم أين ما كنتم تعبدون} ج٣ ص١٢٩','4','496',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (480,'{وقيل من راق} ج٢ ص٢٩٦
{وقيل يا أرض ابلعي ماءك ويا سماء أقلعي وغيض الماء وقضى الأمر} ج١ ص٢١٤
{وكتاب مسطور} ج١ ص٣٧٦
{وكذبوا واتبعوا أهواءهم وكل أمر مستقر} ج١ ص٣٣٦
{وكل شيء عنده بمقدار} ج١ ص١٨٢
{وكل شيء فعلوه في الزبر} ج١ ص٢٦٩
{وكلم الله موسى تكليما} ج٢ ص٢٥٥
{ولا أنا عابد ما عبدتم} ج٣ ص٧
{ولا أنتم عابدونما أعبد} ج٣ ص٧
{ولا تخزني يوم يبعثون} ج٣ ص١٢٩
{ولا تطع من أغفلنا قلبه عن ذكرنا واتبع هواه} ج٢ ص٢٣٩
{ولا تقتلوا أنفسكم} ج١ ص٧٩
{ولا تمش في الأرض مرحا إنك لن تخرق الأرض وأن تبلغ الجبال طولا} ج٢ ص٣٩٤
{ولا صديق حميم} ج٣ ص١٢٩
{واللائي يئسن من المحيض من نسائكم إن ارتبتم فعدتهن ثلاثة أشهر
واللائي لم يحضن وأولات الأحمال أجلهن أن يضعن حملهن} ج٢ ص٣٢٦
{ولتكن منكم أمة يدعون إلى الخير ويأمرون بالمعروف وينهون عن المنكر} ج٣ ص٢٧، ٣٥، ١١٨
{ولقد آتينا موسى الكتاب وجعلنا معه أخاه هارون وزيرا} ج٢ ص٣٠٢
{ولقد أرسلنا نوحا إلى قومه فلبث فيهم ألف سنة إلا خمسين عاما} ج٢ ص٢٠٨
{ولقد أنذرهم بطشتنا فماروا بالنذر} ج١ ص٢١٩
{ولقد أوحى إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك} ج٢ ص٢١٨، ج٣ ص٤٨
{ولقد أوحينا إلى موسى أن أسر بعبادي فاضرب لهم طريقا في','4','497',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (481,'{البحر يبسا لا تخاف دركا ولا تخشى} ج٢ ص٣٤٨
{ولقد جئتمونا فرادى كما خلقناكم أول مرة وتركتم ما خولناكم وراء ظهوركم وما نرى معكم شفعاءكم {ج١ ص٢٤٢
{ولقد خلقنا الإنسان من سلالة من طين} ج٢ ص٢٣٨
{ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ونحن أقرب إليه من حبل الوريد} ج٢ ص٣٣٦
{ولقد علموا لمن اشتراه} ج٣ ص٤٨
{ولقد قال لهم هارون من قبل يا قوم إنما قتنتم به وإن ربكم الرحمن فاتبعوني وأطيعوا أمري} ج٢ ص٢٩٣
{ولقد مكناكم في الأرض وجعلنا لكم فيها معايش قليلا ما تشكرون} ج١ ص٥٠
{ولقد مننا عليك مرة أخرى} ج٢ ص٢٠٢
{ولقد يسرنا القرآن للذكر فهل من مدكر} ج٣ ص١٩
{ولكم في القصاص حياة} ج٢ ص٣٥٢، ٣٥٤
{ولكن البر من اتقى} ج٢ ص٣٠٩
{ولكنا أنشأنا قرونا فتطاول عليهم العمر} ج٢ ص٢٨٣
{ولله على الناس حج البيت من استطاع إليه سبيلا} ج١ ص٢٩٣
{ولما ورد ماء مدين وجد عليه أمة من الناس يسقون} ج٢ ص٣٠٥
{وله الجوار المنشآت في البحر كالأعلام} ج٢ ص١٢٩
{ولو أن قرآنا سيرت به الجبال} ج٢ ص٢٢٣
{ولو ترى إذ فزعوا فلا فوت وأخذوا من مكان قريب} ج٢ ص٣٢٢
{ولو شاء الله لجمعهم على الهدى} ج٢ ص٣٠٦، ٣٠٨
{ولو شاء الله لذهب بسمعهم وأبصارهم} ج٢ ص٢٠٦','4','498',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (482,'{ولو فتحنا عليهم بابا من السماء فظلوا فيه يعرجون} ج٢ ص٣٢٤
{ولو كان من عند غير الله لوجدوا فيه اختلافا كثير} ج٢ ص٢٩٣
{ولولا فضل الله عليكم ورحمته وأن الله تواب حكيم} ج٢ ص٣٢٥، ج٣ ص١٦٤
{ولولا فضل الله عليكم ورحمته وأن الله رءوف رحيم} ج٢ ص٣٢٥
{ولو نشاء لأريناكهم فلعرفتهم بسيماهم ولتعرفهم في لحن القول والله يعلم أعمالكم} ج٢ ص٣٨٧
{ولو يؤاخذ الله الناس بما كسبوا ما ترك على ظهرها من دابة} ج٢ ص٣١١
{وليال عشر} ج٢ ص٣١٩
{وليتذكر أولو الألباب} ج١ ص٣٨٥
{والليل إذا سجى} ج١ ص٢٤٢
{والليل إذا يسر} ج٢ ص٣١٩
{ولئن أذقنا الإنسان منا رحمة ثم نزعناه منه إنه ليئوس كفور} ج١ ص٣٢٧
{ولئن أذقناه نعماء بعد ضراء مسته ليقولن ذهب السيئات عني إنه لفرح فخور} ج١ ص٣٣٧
{ولئن سألتهم ليقولن إنما كنا نخوض ونلعب قل أبالله وآياته ورسوله كنتم تستهزئون} ج٣ ص١٦٠
{وما أدراك ما سقر} ج١ ٣٨٣
{وما أسألكم عليه من أجر إن أجري إلا على رب العالمين ج١ ص٧
{وما أضلنا إلا المجرمون} ج٣ ص١٢٩
{وما أهلكنا من قرية إلا ولها كتاب معلوم} ج٢ ص٣٣٠
{وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين} ج٢ ص٣٢٦
{وما تكون في شأن وما تتلو منه من قرآن ولا تعملون من عمل إلا كنا عليكم شهود} ج٢ ص٢٣٤','4','499',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (483,'{وما كان الناس إلا أمة واحدة فاختلفوا ولولا كلمة سبقت من ربك لقضي بينهم فيما فيه يختلفون ج٣ ص٢٠٧
{وما كنت بجانب الطور إذ نادينا ولكن رحمة من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يتذكرون} ج٢ ص٢٨٤
{وما كنت بجانب الغربي إذ قضينا إلى موسى الأمر وما كنت من الشاهدين} ج٢ ص٢٨٣
{وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك إذا لارتاب المبطلون} ج٢ ص٣٢١
{ومالي لا أعبد الذي فطرني وإليه ترجعون} ج٢ ص١٧٧، ٢٨٢
{وما نؤخره إلا لأجل ممدود} ج٢ ص٢٢٢، ج٣ ص١٧٤
{وما ينطق عن الهوى} ج١ ص٣٣٦
{وما ينظر هؤلاء إلا صيحة واحدة مالها من فواق} ج٢ ص٢٩١
{ومثل الذين ينفقون أموالهم ابتغاء مرضاة الله وتثبيتا من أنفسهم كمثل جنة بربوة} ج٢ ص٣٨
{والمرسلات عرفا} ج١ ص٣٣٦
{ومكروا مكرا ومكرنا مكرا} ج٣ ص١٥٩
{والملك على أرجائها ويحمل عرش ربك فوقهم يومئذ ثمانية} ج١ ص١٨٦
{ومن الأرض مثلهن} ج١ ص٣٨٧
{ومن تاب وعمل صالحا فإن يتوب إلى الله متابا} ج٢ ص٣١٤
{ومن شر النفاثات في العقد} ج٢ ص٤٢
{ومن الناس من يشتري لهو الحديث} ج٢ ص٣٩٦
{ومن يتعد حدود الله فأولئك هم الظالمون} ج١ ص٣١٠، ٢٩١
{ومن يتق الله يجعل له مخرجا ويرزقه من حيث لا يحتسب} ج١ ص٣٠٤
{ومن يكسب خطيئة أو إثما ثم يرم به بريئا} ج١ ص٩٥
{ومناة الثالثة الأخرى} ج٢ ص٣٦٢
{والمؤتفكة أهوى} ج٢ ص٢٠٦
{وناديناه أن يا إبراهيم} ج٢ ص٣٢٥','4','500',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (484,'{والنازعات غرقا} ج٢ ص٣٢٠
{والناشطات نشطا} ج٢ ص٣٢٠
{والنجم إذا هوى} ج١ ص٢٢٩، ٣٣٦، ج٢، ٣٦٣
{ونفخ في الصور ذلك يوم الوعيد} ج٢ ص٣٣٦
{ونفخ في الصور فصعق من في السموات ومن في الأرض إلا من شاء الله} ج١ ص٢٤١
{وهديناه الصراط المستقيم} ج١ ص٣٧٨
{وهل أتاك نبؤ الخصم إذ تسوروا المحراب} ج١ ص١٨٨، ج٣ ص١٦٠
{وهم يحسبون أنهم يحسنون صنعا} ج١ ص٣٥١
{وهم ينهون عنه وينأون عنه} ج١ ص٣٥٠
{وهو الله في السموات وفي الأرض} ج١ ص٩٦
{ووصينا الإنسان بوالديه حسنا، وإن جاهدك لتشرك بي ما ليس لك به علم فلا تطعهما} ج٢ ص٣٠١
{ووصينا الإنسان بوالديه حملته أمه وهنا على وهن وفصاله في عامين أن أشكر لي ولوالديك} ج٣ ص٤٣
{ووفيت كل نفس ما عملت وهو أعلم بما يفعلون} ج١ ص٢٤١ ج٣ ص١٦٠
{ووهبنا لدواد سليمان نعم العبد أنه أواب} ج١ ص١٧٦.
{ويجعلون لله البنات سبحانه ولهم ما يشتهون} ج٣ ص٤٢
{ويدرأ عنها العذاب أن تشهد أربع شهادت بالله إنه لمن الكاذبين} ج٢ ص٣٤، ج٣ ص١٦٤
{ويقولون خمسة سادسهم كلبهم} ج٣ ص١٨٣.
{ويقولون متى هذا الوعد إن كنتم صادقين} ج٢، ٣٢٢
{ويل للمطففين} ج١ ص٣١٩','4','501',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (485,'{ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة} ج١ ص٢٤٣، ج١، ص٢١٨
{ويوم حنين إذ أعجبتكم كثرتكم فلم تغن عنكم شيئا وضاقت عليكم الأرض بما رحبت} ج٢ ص٢٠٠
{ويوم نسير الجبال وترى الأرض بارزة وحشرناهم فلم نغادر منهم أحدا} ج٢ ص١٩٠
{ويوم يحشر أعداء الله إلى النار فهم يوزعون} ج١ ص٨٧
{ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا} ج٢ ص٣٠٠
{ويوم ينفخ في الصور ففزع من في السموات ومن في الأرض} ج٢ ص١٩٠
ي-
{يا أبت إني أخاف أن يمسك عذاب من الرحمن فتكون للشيطان وليا} ج١ ص٣٧٧
{يا أبت إني أخاف أن يمسك عذاب من الرحمن فتكون للشيطان وليا} ج٢ ص٢٦٢
{يا ابت إن قد جاءني من العلم ما لم يأتك فاتبعني أهدك صراطا سويا} ج٢ ص٢٦٢
{يا أبت لا تعبد الشيطان إن الشيطان كان للرحمن عصيا} ج٢ ص٢٦٢
{يا أيها الذين آمنوا} ج٢ ص٣١٤
{يا أيها الذين آمنوا اتقوا الله حق تقاته ولا تموتن إلا وأنتم مسلمون} ج١ ص٣٠٤
{يا أيها الذين آمنوا اتقوا الله وكونوا مع الصادقين} ج١ ص٢٨٨
{يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله ذروا البيع} ج١ ص٣٠٧
{يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوا لكم فاحذرهم وأن تعفوا وتصفحوا وتغفروا فإن الله غفور رحيم} ج٣ ص٢٩','4','502',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (486,'{يا أيها الذين آمنوا أوفوا بالعقود} ج١ ص٢٠٩
{يا أيها الذين آمنوا لا تتخذوا بطانة من دونكم لا يألونكم خبالا ودوا ما عنتم} ج٢ ص٣٢٩
{يا أيها الساحر} ج٢ ص٣١٤
{يا أيها المدثر} ج١ ص٣٣٦، ج١ ص٤١٢
{يا أيها الملأ إني ألقي إلي كتاب كريم} ج١ ص١٨٥
{يا أيها الناس اتقوا ربكم الذي خلقكم من نفس واحدة} ج٣ ص٩٨
{يا أيها الناس اتقوا ربكم إن زلزلة الساعة شيء عظيم} ج٣ ص٩٨
{يا أيها الناس إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة} ج٢ ص٢٣٩
{يا أيها الناس إني رسول الله إليكم جميعا، الذي له ملك السموات والأرض لا إله إلا هو يحيى ويميت} ج٢ ص١٨٢
{يا داود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق} ج١ ص٣١٣
{يا زكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا} ج٢ ص٢٩٢
{يا عبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون} ج٢ ص٣١٧
{يا قوم اعملوا على مكانتكم إني عامل سوف تعلمون} ج٢ ص٢٨٢
{يا قوم إنما هذه الحياة الدنيا متاع وإن الآخرة هي دار القرار} ج١ ص٢٠٤ ج٣ ص٩
{يا هود ما جئتنا ببينة وما نحن بتاركي آلهتنا عن قولك وما نحن لك بمؤمنين} ج٢ ص١٨٣
{يا يحيى خذ الكتاب بقوة وآتيناه','4','503',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (487,'{الحكم صبيا} ج٢ ص٢٩٣
{يخرج الحي من الميت ويخرج الميت من الحي} ج١ ص٣٥٧
{يخوضوا ويلعبوا} ج١ ص٣٨٢
{يسبح لله ما في السموات وما في الأرض له الملك وله الحمد} ج٢ ص٢١٤
{يستعجل بها الذين لا يؤمنون بها والذين آمنوا مشفقون منها ويعلمون أنها الحق} ج١ ص٣٧٩
{يعلمون ظاهرا من الحياة الدنيا، وهم عن الآخرة هم غافلون} ج٢ ص٣٦٧
{يقول إنك لمن المصدقين} ج١ ص٢٨، ج٣ ص٢٠١
{يكاد البرق يخطف أبصارهم} ج٣ ص١٩٤ البقرة
{يوسف أيها الصديق أفتنا في سبع بقرات سمان} ج٢ ص٣٠٣
{يوقد من شجرة مباركة زيتونة لا شرقية ولا غربية} ج٢ ص١٢٧
{يوم تبيض وجوه وتسود وجوه فأما الذين اسودت وجوههم أكفرتم بعد إيمانكم فذوقوا العذاب بما كنتم تكفرون} ج٣ ص١٧٣
{يوم تجد كل نفس ما عملت من خير محضر وما عملت من سوء تود لو أن بينها وبينه أمدا بعيدا} ج٣ ص٢٠٥
{يوم ترجف الراجفة} ج٢ ص٣٢٠
{يوم لا ينفع مال ولا بنون} ج٣ ص١٢٩
{يوم يأت لا تكلم نفس إلا بإذنه فمنهم شقي وسعيد} ج٢ ص٢٣٢، ج٣ ص١٧٤
{يوم يجمعكم ليوم الجمع} ج٢ ص١٩١
{يوم يقوم الروح والملائكة صفا لا يتكلمون إلا من أذن له الرحمن وقال صوابا} ج٣ ص٢٠٥','4','504',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (488,'فهرس الأحاديث النبوية:
الآن حمي الوطيس ج١ ص٩٧، ١٠٩
آية الكرسي سيدة آي القرآن ج٢ ص٢٦٨
إذا ابتلت النعال فالصلاة في الرحال ج١ ص٨٩
إذا أتاك أحد الخصمين، وقد فقئت عينه، فلا تحكم له، فربما أتى خصمه وقد فقئت عيناه ج١ ص٢٠١
إذا التقى المتهاجران، فأعرض هذا، فخيرها الذي يبدأ بالسلام ج١ ص٢٠٢
إذا بلغ الماء قلتين لم يحمل خبثا ج٢ ص١٨
إذا قام أحدكم إلى الصلاة فليتوضأ ج٢ ص٢٨٦
إذا لم تستح فأصنع ما شئت ج٢ ص٧٧
إذا لم تستح فافعل ما شئت ج٣ ص٥١
ارجعن مأزورات غير مأجورات ج١ ص٢٧٤
استحيوا من الله حق الحياء ج١ ص٢٧٢
أسجعا كسجع الكهان ج١ ص٢٧٣، ٢٧٤، ٢٧٥
اسق يا زبير، ثم احبس الماء حتى يرجع إلى الجدر ج٢ ص٢٨٥
"أسلم سالمها الله"، "وغفار غفر الله لها"، "وعصية عصت الله" ج٣ ص١٩٧
أطع ولو عبدا حبشيا مجدعا ما أقام عليك كتاب الله ج٢ ص٤٥
أطولكن بدا أسرعكن لحوقا بي ج٢ ص٨١
أعطيت خمسا لم يعطهن أحد قبلي كان كل نبي يبعث في قومه وبعثت إلى كل أحمر وأسود وأحلت لي الغنائم جعلت الأرض','4','505',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (489,'طيبة وطهورا ونصرت بالرعب بين يدي مسيرة شهر وأوتيت جوامع الكلم ج٤ ص٤، ٥
الأعمال بالنيات ولكل امرئ ما نوى ج٢ ص١٨٤، ٣٤٠
أعيذه من الهامة والسامة، وكل عين لامة ج١ ص٣٧٣
أقبل وأدبر واتق الدبر والحيضة ج٣ ص٦٥، ١٣
اقرأ وارق ورتل كما كنت ترتل في الدنيا فإن منزلتك عند آخر آية نقرؤها ج٣ ص١٢٠
اقرءوا القرآن بلحون العرب وأصواتها، وإياكم ولحون أهل الفسق ولحون أهل الكتابين، وسيجيء بعدي قوم يرجعون القرآن ترجيع الغناء والنوح ج٢ ص٣٩٥
ألا يكفيك آية الصيف ج٣ ص٣٥٥
التمسوا الرزق في خبايا الأرض ج١ ص٨٩
اللهم ارفع درجته في المهتدين، واخلفه في عقبه في الغابرين لنا وله يا رب العالمين ج٢ ص٣٣٦
اللهم اقطع أثره ج١ ص٨١
اللهم بارك لهم في محضها، ومخضها ومذقها وقرقها، وابعث راعيها في الدثر بيانع الثمر، وافجر له الثمد ج١ ص٢٣٢
اللهم حببها إلينا كما حببت إلينا مكة ج١ ص١٩٨
الله كما أحسنت خَلقي حسن خُلقي ج١ ص٣٤٩
أما إنكم لو أكثرتم من ذكر هاذم اللذات لشغلكم عما أرى ج٢ ص٩٨
أمسك عليك هذا ج٢ ص١٣٨
أمكم النخلة ج٢ ص٣٩
إن إبليس له عرض على البحر، فيبث بنية في آفاق الأرض فيأتي أحدهم، فيقول: فعلت كذا، وفعلت كذا فيقول: ما فعلت شيئا، ويأتي أحدهم فيقول: زيلت بينه وبين أخيه، أو بينه وبين زوجته، فيقول: نعم الولد أنت ج٢ ص٢٠٣','4','506',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (490,'إن الأعمال تعرض على الله يوم الاثنين ويوم الخميس، فيغفر لكل امرئ لا يشرك بالله شيئا إلا امرأ كانت بينه وبين أخيه شحناء ج١ ص
إن الله خلق آدم من قبضة قبضها من جميع الأرض، فجاء بنو آدم على قدر الأرض، منهم الأحمر والأبيض والأسود، وبين ذلك، والحزن والسهل والخبيث والطيب ج١ ص٢٠٦
إن الله لا ينظر يوم القيامة إلى من جر ثوبه خيلاء ج٢ ص٢٩٤
"إن بني هشام بن المغيرة استأذنوني أن ينكحوا ابنتهم عليا فلا آذن ثم لا آذن إلا أن أطلق علي ابنتي وينكح ابنتهم" ج٣ ص١٠
إن جبريل عليه السلام عرض علي صورتك في سرقة وقال هذه زوجتك في الدنيا والآخرة، فقلت: إن يكن ذلك من عند الله يمضه ج١ ص٢٠٥
إن سورة الإخلاص تعدل ثلث القرآن ج٢ ص٢٦٨
إن قريشا قد نهتكم الحرب، فإن شاءوا ماددناهم مدة، ويدعو بيني وبين الناس، فإن أظهر عليهم وأحبوا أن يدخلوا فيما دخل فيه الناس ج٢ ص٣٤٢
أنا ابن الذبيحين ج١ ص٨٠
أنا أفصح من نطق بالضاد ج٤ ص٤، ٥
أنا أولى بالمؤمنين من أنفسهم فمن مات وترك مالا فلورثته ومن ترك دينا أو كلا أو ضياعا فإلي وعلي ج٣ ص١٢٠
انتقلنا عن الجهاد الأصغر إلى الجهاد الأكبر ج١ ص٩٤
الأنصار كرشي وعيبتي ١٩٤
إنكم تحشرون على أرض بيضاء كقرصة النقي ج٢ ص١٩
إنكم ترون أهل الدرجات العلا في الجنة كما ترون الكواكب في أفق السماء ج١ ص٢٠٩','4','507',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (491,'إنما تنكح المرأة لأربع لحسبها، أو لدينها، أو لمالها، أو لجمالها ج١ ص٢٠٥
إنه إذا كذب الكاذب تباعد الملك عنه ميلا لنتن كذبه ج١ ص٢٠٤
إنه كانت امرأة فيمن كان من قبلنا وكان لها ابن عم يحبها فراودها عن نفسها فامتنعت عليه حتى إذا أصابتها شدة فجاءت إليه تسأله فراودها فمكنته من نفسها فلما قعد منها مقعد الرجل من المرأة قالت لا لا يحل لك أن تفض الخاتم إلا بحقه ج٣ ص٦٤
إنه ليس منه وقد لعن الواصلة والمتوصلة، والواشمة والمتوشمة، والواشرة والمستوشرة ج٢ ص٣٩٦
إني تارك فيكم الثقلين كتاب الله وعترتي ج١ ص١٩٥
أوتيت جوامع الكلم ج١ ص٩٦، ج٢ ص٣٣٧
ب-
بعثت أنا والساعة كهاتين ج١ ص٩٧
بعثت في نفس الساعة ج١ ص٩٧
ث-
ثكلتك أمك يا معاذ! وهل يكب الناس على مناخرهم في نار جهنم إلا حصائد ألسنتهم ج٢ ص١٣٨
ثلاثة لا ترد: الطيب، والريحان، والدهن ج٢ ص٤٤
ج-
جاءني جبريل عليه السلام ومعه سرقة من حرير وفيها صورة عائشة وقال: هذه زوجتك في الدنيا والآخرة ج٢ ص٤٣
جار الدار أحق بدار الجار ج١ ص٣٥٧
ح-
حاج آدم موسى ج١ ص١٩٦
حرمت علي الصدقة وأحلت لي الهدية ج٢ ص٤٣
الحلال بين والحرام بين وبينهما أمور متشابهات ج٢ ص٣٤٠
خ-
خذي فرصة من مسك فتطهري بها ج٣ ص٦٠
الخراج بالضمان ج٢ ص٣٥٠','4','508',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (492,'خلوا بين جرير والجرير ج١ ص٣٤٣
خير المال عين ساهرة لعين قاتمة ج٣ ص١٤٤
الخيل معقود بنواصيها الخير ج٢
ذ-
ذاك من مدد السماء الثالثة ج١ ص١٩٦
ذو الوجهين لا يكون وجيها ج٢ ص٤٦
ر-
رب أشعث أغبر مدفوع بالأبواب لو أقسم على الله لأبره ج١ ص٢٠٤
رب أشعث أغير ذي طمرين لو أقسم على الله لأبره ج١ ص٢٠٩
رويدك سوقك بالقوارير ج٣ ص٦٤
س-
سباب المؤمن فسوق، وقتاله كفر ج١ ص٢٠١
سيروا بسير أضعفكم ج٢ ص٣٤٠، ٣٤١
ش-
شاهت الوجوه ج١ ص١٩٥
ص-
صلاة في مسجدي هذا خير من ألف صلاة في غيره من المساجد إلا المسجد الحرام ج١ ص٧٦
صومكم يوم تصومون، وفطركم يوم تفطرون، وأضحاكم يوم تضحون ج١ ص١١٧
ض-
ضالة المؤن حرق النار ج١ ص٣١٢
ع-
عرضت عليَّ الجنة والنار في عرض هذا الجدار، فلم أر كاليوم في الخير والشر ج١ ص٢٠٣
غ-
غربوا لا تضووا ج١ ص٢٠٠
ف-
فحج آدم موسى ج١ ص١٩٦
فهلا جارية تلاعبها وتلاعبك ج٢ ص٢٩٨
في سائمة الضم زكاة ج٢ ص٩٨
ق-
القدرية مجوس هذه الأمة ج٢ ص٣٨٨
قوم يخضبون بالسواد كحواصل الحمام، لا يريحون رائحة الجنة ج٢ ص٣٩٦
ك-
كانوا إذا خرجوا من عنده لا يتفرقون إلا عن ذواق ج٢ ص٢٧٨','4','509',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (493,'الكريم ابن الكريم يوسف بن يعقوب بن إسحاق بن إبراهيم ج٣ ص٢٢: ٤
الكلمة الحكمة ضالة المؤمن ج١ ص١٠٠
الكمأة جدري الأرض ج٢ ص١١٧، ١٣٢
ل-
لا تبيعوا القينات المغنيات، ولا تشتروهن، ولا تعلموهن، ولا خير في تجارة فيهن، وثمنهن حرام ج٢ ص٣٩٦
لا تدخل الملائكة بيتا فيه صورة ولا تمثال ج١ ص١٩٣
لا تستضيئوا بنار المشركين ج٢ ص٩٧
لا صلاة لجار المسجد إلا في المسجد ج٢ ص٣١٧
لا مد ولا تجريد ج١ ص٢٠٧، ١٦٢
لا يتوسد القرآن ج١ ص٧٧
لا يحل للمؤمن أن يهجر أخاه فوق ثلاث ج١ ص٢٠٢
لعن الله اليهود، حرمت عليهم الشحوم فجملوها، وباعوها وأكلوا أثمانها ج٢ ص٣٩١
لو مد لنا الشهر لواصلنا وصالا يدع له المتعمقون تعمقهم ج١ ص٣٨٢
ليأتين على أمتي ما أتى على بني إسرائيل، تفرق بنو إسرائيل على اثنتين وسبعين ملة، وستفرق أمتي على ثلاث وسبعين ملة ج٢ ص٣٨٧
ليهنك العلم أبا المنذر ج٢ ص٢٦٨
م-
ما الإحسان؟ قال: أن تعبد الله كأنك تراه فإن لم تكن تراه فإنه يراك ج٢ ص٣٤١
ما أنزل الله في التوراة ولا في الأنجيل مثل أم القرآن، وهي سبع المثاني ج١ ص٢٣١
ما دخلت هذه دار قوم إلا ذلوا ج٢ ص٣٣
ما رأيت ناقصات عقل ودين وأذهب للب الحازم من أحداكن يا معشر النساء ج١ ص٣٨٥','4','510',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (494,'ما فضلكم أبو بكر بصلاة وصيام، ولكن فضلكم بسر وقر في صدره ج١ ص٢٠٩
مثل الجليس الصالح مثل حامل المسك إما أن يحذيك وإما أن تبتاع منه، وإما أن تجد منه عرفا طيبا ومثل جليس السوء مثل نافخ الكير إما أن يحرق ثوبك وإما أن تجد منه رائحة كريهة ج٢ ص٤٤
مثل المؤمن الذي يقرأ القرآن كمثل الأترجة، طعمها طيب وريحها طيب ج٢ ص١٤٠
المسلم من سلم الناس من لسانه ويده ج١ ص٣٥١
المضعف أمير الركب ج٢ ص٣٤٠
من أتى الجحفة فليغتسل ج٣ ص٧٣
من أحق الناس بصاحبتي؟ قال: أمك قال: ثم من؟ قال: أمك قال: ثم من؟ قال: أمك قال: ثم من؟ قال: أبوك ج٣ ص٤٣، ١١
من جعل قاضيا بين الناس فقد ذبح بغير سكين ج١ ص٩٣
من شاء يرقع في الرياض الأنائق فعليه بآل حم ج٢ ص٣٥٠
من صبر على حر مكة ولأواء المدينة ضمنت له على الله الجنة ج١ ص١٩٨
من غش أمتي فليس مني ج٢ ص٣٩٦
من غشنا فليس منا ج٢ ص٣٩٦
منهومان لا يشبعان طالب علم وطالب مال ج١ ص١٨٨
المؤمن لا يلسع من حجر مرتين ج١ ص٢٦٣
هـ-
هذا جبل يحبنا ونحبه ج٢ ص٨٢
هذا هو البلاغة ج٢ ص٣٤٩
هذه كانت تأتينا في زمن خديجة، وحسن العهد من الإيمان ج١ ص١٩٩
هل يكب الناس على مناخرهم في نار جهنم إلا حصائد ألسنتهم ج٢ ص١٣٢
هو الطهور ماؤه، الحل ميتته ج٢ ص٢٢٣
هو الوأد الخفي ج٢ ص١٥١','4','511',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (495,'هو الوأدة الصغرى ج٢ ص١٥١
ووالله إنكم لتجينون وتبخلون وتجهلون وإنكم من ريحان الله، وإن آخر وطأة وطئها الله بوج ج٣ ص٧٤، ٤
الولد للفراش وللعاهر الحجر ج١
وهل يكب الناس على مناخرهم في نار جهنم إلا حصائد ألسنتهم ج٢ ص١١٨
ي-
يا أبا ذر، إني أحب لك ما أحب لنفسي، لا تأمرن على اثنين، ولا تولين مال يتيم ج١ ص٣٢٥
يقال لصاحب القرآن اقرأ وارق ورتل كما كنت ترتل في الدنيا فإن منزلتك عند آخر آية تقرأ ج١ ص٣٦١','4','512',NULL,NULL,'0');
