INSERT INTO [b10550] VALUES (508,'فهرس القوافي وأنصاف الأبيات:
أ-
ص صدر عجز بحر
٢٠١ وما العيش وماء طويل
٢٩١ إني وإن كان وورائه كامل
١١١ لا تعذل أحشائه كامل
١١١ إن القتيل بدمائه كامل
١٠٨ ما الخل بسوائه كامل
١٠٧ القلب أعلم وبمائه كامل
١٠٧ لا تعذل أحشائه كامل
١٠٢ عذل العواذل سودائه كامل
٢٧٧ وبلد سماؤه رجز
١٥٦ فإذا شوفي شفاء رمل
ب-
٥٨ وأظلم يتقلب طويل
٥٨ أغالب فيك أعجب طويل
٥٩ تريد بك المذرب طويل
١١٧ فديناك والغربا طويل
١١٨ ولم تفترق حبا طويل
١٢٢ وإن قصرت نضارب طويل','4','527',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (509,'١٢٢ إذا قصرت فنضارب طويل
١٦٠ ولست المهذب طويل
١٨٨ أناس الكتائب طويل
١٨٨ على مثلها السواكب طويل
١٩٠ إن يقتلوك شهاب طويل
٤٣ كأن صدري الذهب بسيط
١٥٦ وأزرق ينسكب بسيط
١٢٧ فلا تنلك بالغرب بسيط
١٣٩ وأزرق الفجر ينسكب بسيط
١٨٦ كم أحرزت كثب بسيط
١٨٧ السيف أصدق واللعب بسيط
٢٤٨ أن يعد الحطب بسيط
٢٤٨ السيف أصدق واللعب بسيط
١٨٢ أقفر فالذنوب مخلع البسيط
١٨٢ وكل ذي لا يئوب مخلع البسيط
١٢٨ يهز الجيش العقاب وافر
١٢٨ بغيرك الضراب وافر
١٦٨ وشيخ المشيبا وافر
١٦٨ ضروب الناس حبيبا وافر
١٤١ فاضمم قواصيهم شعاب وافر
٣٤ تقد السلوقي الحباحب كامل','4','528',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (510,'١٤١ ولو أن دهرا عتاب وافر
٢٧٤ وثنية الأحقب وافر
١٢٦ أحسن والغضب منسرح
٣٠٥ والشعر خطبه منسرح
٣٠٥ كلفتمونا كذبه منسرح
١٤٩ لا تذيلن قضيب خفيف
١٤٩ أي مرعى ملحوب خفيف
ت-
١١٧ هنيئا حزتا طويل
١٧٦ إن الكرام سويداواتها كامل
ح-
١٨٥ جللا الشيح كامل
د-
٢٠ ولولا ثلاث عودي طويل
٥٩ فإن نلت ورده طويل
٥٩ أود جنده طويل
٩٧ هنيئا لك وعيدا طويل
١٠٠ وما قتل اليدا طويل
١٠٠، ١٠٧ لكل امريء في العدا طويل
١٠٧ سيوفك الندا طويل','4','529',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (511,'١١١ فلم يبك النواهد طويل
١١١ عواذل لما جد طويل
١١٣ تنكسهم المكايد طويل
١١٤ لخولة أطلال اليد طويل
١٤٠ ووضع الندى طويل
١٤٥ ما ابيض البيد طويل
١٤٧ وقيدت نفسي تقيدا طويل
