INSERT INTO [b10550] VALUES (523,'فهرس الآيات:
أ-
{أتأمرون الناس بالبر وتنسون أنفسكم} ص١٦٧
{إذ نفشت فيه غم القوم} ص٢٥٨
{إذا السماء انشقت} ص٢٥٦
{أراغب أنت عن آلهتي يا إبراهيم} ص١٥٣
{أعوذ برب الناس ملك الناس إله الناس} ص١٧٩
{أفي الله شك} ص٢٥٢
{اقتلوا المشركين} ص٢٠٦
{الله نزل أحسن الحديث} ص٥٠
{ألم أقل لك إنك لن تستطيع معي صبرا} ص٢٣٠
{إما أن تلقي وإما أن تكون نحن الملقين} ص٢٢٩
{إن الله مع الذين اتقوا} ص١٦٢
{إن الله يغفر الذنوب جميعا} ص٤٤
{إن إلينا إيابهم ثم إن علينا حسابهم} ص٢٥٧
{إن رحمة الله قريب من المحسنين} ص٢٧١
{إن السمع والبصر والفؤاد كل أولئك كان عنه مسئولا} ص٧٦
{إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة، ثم من مضغة} ص٢٩٥
{إن لك ألا تجوع فيها} ص٢٥٨
{إنك أنت علام الغيوب} ص٢٢٩
{إنما أشكو بثي وحزني إلى الله} ص٢٨٨
{إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم {يذهبوا حتى يستأذنوه} ص٧٩
{إنما يخشى الله من عباده العلماء} ص٤٢
{إنه مصيبها ما أصابهم} ص٢٥٥
{إنها ترمي بشرر كالقصر} ص١٠٩
{إنهم كانوا لا يرجون حسابا} ص١٨٠','4','542',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (524,'{إني أراني أعصر خمرا} ص١٩٩
{أهدك صراطا سويا} ١٦٦
{أو لا مستم النساء} ص٢٩٢
{أو لم يروا إلى الطير فوقهم} ص٢٣٢
{أو لم يروا إلى ما خلق الله} ص٢٣٢
{إياك نعبد} ص٢٢٨
{إياك نعبد وإياك نستعين} ص٢٤٧
{بسلام منا وبركات عليك وعلى أمم من معك وأمم سنمتعهم ثم يمسهم منا عذاب أليم} ص١٧٧
{بل الله فاعبد وكن من الشاكرين} ص٢٤٦
{تلك عشرة كاملة} ص٢٨٧
{ثم أنشأناه خلقا آخر} ص٢٦٤، ٢٦٦
{ثم بدا لهم من بعد ما رأوا الآيات ليسجننه} ص٢٨١
{ثم الجحيم صلوه} ص٢٤٨
{ثم جعلناه نطفة في قرار مكين} ص٢٦٤
{ثم خلقنا النطفة علقة} ص٢٦٤
{ثم يرم به بريئا} ص٢١٣
{حتى إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون} ص٧٣
{حتى توارت بالحجاب} ص٢٧٩، ٢٨١
{حتى عاد كالعرجون القديم} ص٢١٥، ٢١٦، ٢٧٢
{الحمد لله رب العالمين} ص٢٢٨
{ذلك الكتاب لا ريب فيه} ص٢٥٩
{ذهب الله بنورهم} ص٢٣٤، ٢٣٥
{رب هب لي من الصالحين} ص٦٦
{سنفرغ لكم أيها الثقلان} ص١٩٦
{شهد عليهم سمعهم وأبصارهم وجلودهم} ص٧٣
{صراطك المستقيم} ص١٦٧
{عاليهم ثياب سندس} ص٢٥٧
{فأجاءها المخاض إلى جذع النخلة} ص٢٦٣
{فإذا هي شاخصة أبصار الذين كفروا} ص٢٥٣
{فاكهة ونخل ورمان} ص٧٤
{فإن الله هو مولاه وجبريل وصالح المؤمنين والملائكة بعد ذلك ظهير} ص٧٥','4','543',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (525,'{فانتبذت به مكانا قصيا فأجاءها المخاض} ص٢٦٤
{فإنهم عدو لي إلا رب العالمين} ص٢٧١، ٢٧٧
{فأولئك لهم جزاء الضعف} ص٢٥٢
{فبأي آلاء ربكما تكذبان} ص٢٢٦
{فبشرناه بإسحاق نبيا من الصالحين} ص٦٥
{فبشرناه بغلام حليم} ص٦٥، ٦٦
{فبما رحمة من الله لنت لهم} ص٢١٠
{فجعلناها نكالا لما بين يديها وما خلفها وموعظة للمتقين} ص١٣٤
{فحملته فانتبذت به مكانا قصيا} ص٢٩٣
{فخلقنا العلقة مضغة} ص٢٦٤، ٢٦٥
{فخلقنا المضغة عظاما} ص٢٦٤، ٢٦٥
{فسيكفيكهم الله} ص١٧٧
{فشرد بهم من خلفهم} ص١٦٦
{فصيام ثلاثة أيام في الحج وسبعة إذا رجعتم، تلك عشرة كاملة ص٢٨٥
{فكسونا العظام لحما} ص٢٦٤
{فكفارته إطعام عشرة مساكين من أوسط ما تطعمون أهليكم أو كسوتهم، أو تحرير رقبة} ص٢٩٠
{فلا يصدنك عنها من لا يؤمن بها واتبع هواه فتردى} ص٢٦٤
{فلما بلغ مع السعي قال يا بني إني أرى في المنام أني أذبحك} ص٦٥
{فلما ذهبوا به وأجمعوا} ص٢٣٤
{فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة} ص٧٩
{فمثله كمثل الكلب إن تحمل عليه يلهث} ص٢١٦
{فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم} ص٢٠٦
{فمنهم شقي وسعيد، فأما الذين شقوا ففي النار} ص٣٠١
{في جيدها حبل من مسد} ص٢٤٩
{فيها من برد} ص٤٤','4','544',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (526,'{قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك، فلما رآه مستقرا عنده قال هذا من فضل ربي} ص٢٧٨
{قال نكروا لها عرشها} ص٢٧٨
{قل كل يعمل على شاكلته} ص٣١
{كالتي نقضت غزلها من بعد قوة أنكاثا} ص١٤٠
{كزرع أخرج شطأه} ص٢١٥
{كلا إذا بلغت التراقي} ص٢٧٩
{كلما نضجت جلودهم بدلناهم جلودا غيرها} ص٧٤
{لا تتخذوا بطانة من دونكم لا يألونكم خبالا ودوا ما عنتم قد بدت البغضائ من أفواههم} ص٢٧٨
{لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا} ص٧٨
{لا تخف إنك أنت الأعلى} ص٢٢٨
{لا ترى فيها عوجا ولا أمتا} ص١٦٦
{لا تفتروا على الله كذبا فيستحكم بعذاب} ص٢٦٣
{لا فيها غول} ص٢٥٩، ٢٦١
{لا لغو فيها} ص٢٦١
{لا يحزنهم الفزع الأكبر وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون} ص٥٢
{لتركبن طبقا عن طبق} ص٣٠٠
{لكل جعلنا منكم شرعة ومنهاجا} ص٢٣٣
{لنخرج به حبا ونباتا} ص١٧٩
{ليس كمثله شيء} ص٢١١
{ليستخلفنهم في الأرض} ص١٧٧
{ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض} ص١٦٧
{ما لهذا الكتاب لايغادر صغيرة ولا كبيرها إلا أحصاها} ص٢٣٨
{ما وعدنا الله ورسوله} ص٢٨
{ما يأتيهم من ذكر من ربهم محدث إلا استمعوه وهم يلعبون} ص٢٣٢
{مثل نوره كمشكاة فيها مصباح} ص٢١٥، ٢١٦
{مثلهم كمثل الذي استوقد نارا، فلما أضاءت ما حوله ذهب الله بنورهم} ص٢٣١','4','545',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (527,'{مثنى وثلاث ورباع} ص٢٩٠
{مع الذين اتقوا} ص١٦٦
{من ورائه جهنم} ص٢٩١
{هذا بلاغ للناس} ص٨٧
{هذا يومكم الذي كنتم توعدون} ص٥١
{هل من خالق غير الله يرزقكم} ص٤٤
{هو الذي جعل الشمس ضياء والقمر نورا} ص٢٣١
{وآتوا حقه يوم حصاده} ص١٣٧
{واخفض لهما جناح الذل} ص١٩٧، ٢١٢
{وأدخلناه في رحمتنا} ص١٩٣، ١٩٥
{وإذ واعدنا موسى} ص٢٨٠
{واسأل القرية} ص٨٣، ٨٤
{واعبدوه واشكروا له إليه ترجعون} ص٢٢٨
{واقعدوا لهم كل مرصد} ص١٦٧
{والذين اهتدوا زادهم هدى} ص١٤٠
{والذين هم لفروجهم حافظون} ص٧٦
{والذين يؤتون ما آتوا وقلوبهم وجلة} ص٢٧٥
{وإما تخافن من قوم خيانة فانبذ إليهم على سواء إن الله لا يحب الخائنين} ص١٣٤
{وامرأة مؤمنة إن وهبت نفسها للنبي} ص٢١٢
{وإن تعفوا وتصفحوا وتغفر فإن الله غفور رحيم} ص٢٨٨
{وإن كان مكرهم لتزول منه الجبال} ص٢٩٤
{وإن لكم في الأنعام لعبرة نسقيكم مما في بطونه من بين فرث ودم لبنا خالصا سائغا للشاربين} ص١٣٦
{وأنزل من السماء ماء فأخرجنا به أزواجا من نبات شتى} ص١٦٤
{وإني كلما دعوتهم لتغفر لهم} ص٧٩
{والبلد الطيب يخرج نباته بإذن ربه، والذي خبث لا يخرج إلا نكدا} ص١٢٦
{وتركهم في ظلمات لا يبصرون} ص٢٣٥','4','546',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (528,'{وجزاء سيئة سيئة مثلها} ص٢٠٦
{وجعلنا آية النهار مبصرة} ص٣٠٠
{وجعلناه في الأرض رواسي} ص٢٥٧
{وجنات ألفافا} ص١٧٩
{وظنوا أنهم مانعتهم حصونهم من الله} ص٢٥١
{والعاقبة للمتقين} ص١٦٦
{وعرضوا على ربك صفا، لقد جئتمونا كما خلقناكم} ص٢٨٣
{وفوق كل ذي علم عليم} ص١٦٧
{وقال الملأ من قومه إنا لنراك في ضلال مبين} ص٢٣٦
{وقدمنا إلى ما عملوا من عمل فجعلناه هباء منثورا} ص١٩٧
{وقل للمؤمنات يغضضن من أبصارهن ويحفظن فروجهن} ص٧٦
{وقودها الناس والحجارة
} ص١٠٩
{وقولوا للناس حسنا} ص١٦٦
{وكان وراءهم ملك يأخذ كل سفينة غصبا} ص٢٩١
{وكذبوا بآياتنا كذابا} ص١٨٠
{وكنا لحكمهم شاهدين} ص٢٥٨
{ولا تتبع الهوى فيضلك عن سبيل الله} ص١٦١
{ولا تكونوا كالتي نقضت غزلها من بعد قوة أنكاثا} ص١٣٧
{ولا تمدن عينيك إلى ما متعنا به أزواجا منهم} ص١٦٦
{ولا تمدن عينيك إلى ما متعنا به أزواجا منهم زهرة الحياة الدنيا لنفتنهم فيه ورزق ربك خير وأبقى} ص١٦٣
{ولدان مخلدون} ص٢٥٧
{ولقد أوحى إليك وإلى الذين من قبلك لئن أشركت فليحبطن عملك ولتكونن من الخاسرين} ص٢٤٧
{ولقد عهدنا إلى آدم من قبل فنسي} ص٢٦٤
{ولما سكت عن موسى الغضب} ص١٩٧
{ولو كنت فظا غليظ القلب لا نفضوا من حولك} ص١٦٦
{وليبدلنهم من بعد خوفهم أمنا} ص١٦٦','4','547',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (529,'{وما ربك بظلام للعبيد} ص٢٦٧
{ومن عنده علم الكتاب} ص٢٥٢
{ومن يشاق الله} ص٢٣٢
{ومن يشاقق الرسول من بعد ما تبين له الهدى} ص٢٣٢
{وهو الذي جعل الشمس ضياء والقمر نورا} ص٢٣٣
{ووصينا الإنسان بوالديه حسنا، وإن جاهدك} ص٢٨٣
{ووضع الكتاب فترى المجرمين مشفقين مما فيه، ويقولون يا ويلتنا ما لهذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها} ص٢٣٩
{ووهبنا له إسحاق ويعقوب كلا هدينا ونوحا هدينا من قبل} ص٢٤٦
{ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا} ص٢١٧
{ويعلم ما في الأرحام} ص٧٦
{ويغفر لكم من ذنوبكم} ص٤٤
{ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة} ص٥١
{ويوم يعرض الذي كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا} ص٢٨٣
{يا بني آدم لا يفتتنكم الشيطان كما أخرج أبويكم من الجنة وينزع عنهما لباسهما ليريهما سوآتهما} ص١٣٣
{يأخذون عرض هذا الأدنى} ص١٦٦
{يا أيها الذين آمنوا استجيبوا لله وللرسول إذا دعاكم لما يحييكم} ص٧٩
{يتنازعون فيها كأسا لا لغو فيها ولا تأثيم} ص٢٦٠
{يخرج من بطونها شرابا مختلف ألوانه} ص٢٥٤
{يريد الله أن يحق الحق بكلماته ويقطع دابر الكافرين ليحق الحق ويبطل الباطل ولو كره المجرمون} ص٢٨٤
{يوم تبيض وجوه وتسود وجوه} ص١٣٥
{يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم وبأيمانهم} ص١٢٥','4','548',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (530,'فهرس الأحاديث النبوية:
أعيذكما من عين العائن ونفس النافس ص١٠٣
اللهم اشدد وطأتك على مضر ص١٧١
رفقا بالقوارير ص٢٩٤
صلاة في مسجدي هذا خير من ألف صلاة في غيره من المساجد إلا المسجد الحرام ص٥٦
ليس الصيد لمن أثاره، بل لمن حصله ص١٤٥
من جعل نفسه قاضيا للمسلمين فقد ذبح بغير سكين ص٧٦
هو الطهور ماؤه الحل ميتته ص٢٥٤
وإنكم لتقدمون على ما قدمتم ص١٤٩','4','549',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (531,'فهرس الأمثال:
الأعمال بخواتيمها ص١٤٥
أول الغيث طل ص١٣٩
سبق السيف العذل ص١٢١
عسى الغوير أبؤسا ص١٥٤
عند جهينة الخبر اليقين ص١٤٤
لو ذات سوار لطمتني ص٢٥٦
ملكت فأسجح ص١٤٧
من أشبه أباه فما ظلم ص١٥٠
هما رضيعا لبان ص١٥٠
هما شريكان عنان ص١٥٠','4','550',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (532,'مراجع التحقيق:
١- القرآن الكريم.
٢- أخبار أبي تمام. الصولي. تحقيق الأساتذة خليل عساكر ومحمد عبده عزام ونظير الإسلام الهندي. مطبعة لجنة التأليف والترجمة والنشر ١٣٥٦ - ١٩٣٧
٣- الأغاني. الأصفهاني. طبعة الساسي وطبعة دار الكتب.
٤- الأصمعيات. تحقيق الأستاذين أحمد شاكر وعبد السلام هارون. دار المعارف بمصر.
٥- الأمالي. القالي. طبعة دار الكتب ١٣٤٤هـ.
٦- أمالي الشريف المرتضى. مطبعة السعادة ١٣٢٥هـ.
٧- البداية والنهاية. ابن كثير. مطبعة السعادة بمصر ١٣٥١ - ١٩٣٢
٨- البيان والتبيين. الجاحظ. تحقيق الأستاذ عبد السلام هارون.
٩- تاريخ بغداد. الخطيب البغدادي. مطبعة السعادة بمصر ١٣٤٩ - ١٩٣١
١٠- تاريخ الرسل والملوك. الطبري المطبعة الحسينية بالقاهرة.
١١- تأويل مشكل القرآن. ابن قتيبة. تحقيق الأستاذ سيد أحمد صقر.
١٢- الجامع الكبير في صناعة المنظوم من الكلام والمنثور. ابن الأثير. تحقيق الدكتور مصطفى جواد والدكتور جميل سعيد. مطبعة المجمع العلمي العراقي.
١٣- جواهر الألفاظ. قدامة بن جعفر. تحقيق محمد محيي الدين عبد الحميد. مطبعة السعادة بمصر، ١٩٣٢
١٤- خزانة الأدب. البغدادي. تحقيق الأستاذ محمد محيي الدين عبد الحميد.','4','551',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (533,'١٥- الخصائص. ابن جني. مطبعة الهلال بمصر ١٣٣١، ١٩١٣
١٦- دلائل الإعجاز. عبد القاهر الجرجاني. مطبعة المنار بمصر ١٢٣١هـ.
١٧- ديوان ابن الرومي:
١- شرح الشيخ محمد شريف سليم. مطبعة الهلال ١٣٣٥ - ١٩٠٧.
٢- طبعة الأستاذ كامل كيلاني والشيخ عبد الرحمن خليفة.
١٨- ديوان ابن نباته: نشر الأستاذ محمد القلقيلي.
١٩- ديوان ابي تمام.
١- بشرح الخطيب التبريزي تحقيق الدكتور محمد عبده عزام مطبعة المعارف.
٢- طبعة صبيح.
٣- طبعة محمد جمال.
٢٠- ديوان أبي نواس.
١- تحقيق الأستاذ أحمد الغزالي مطبعة مصر ١٩٥٣
٢- طبعة المطبعة العمومية بالقاهرة ١٨٩٨.
٢١- ديوان الأخطل. تحقيق الأب أنطون صالحاني.
٢٢- ديوان الأعشى الكبير. تحقيق محمد حسين. النموذجية بالقاهرة ١٩٥٠.
٢٣- ديوان امرئ القيس. تحقيق الأستاذ محمد أبو الفضل إبراهيم. دار المعارف.
٢٤- ديوان أمية بن أبي الصلت. المطبعة الوطنية ببيروت.
٢٥- ديوان البحتري. مطبعة هندية بمصر ١٢٢٩ - ١٩١١.
٢٦- ديوان جرير نشر الأستاذ عبد الله الصاوي. مطبعة الصاوي بمصر.
٢٧- ديوان جميل. تحقيق الأستاذ البستاني. مكتبة صادر بيروت.
٢٨- ديوان حسان بن ثابت. تحقيق الأستاذ عبد الرحمن البرقوقي. المطبعة الرحمانية بمصر ١٣٤٧ - ١٩٢٩.','4','552',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (534,'٢٩- ديوان الحطيئة بشرح ابن السكيت والسكري والسجستاني. تحقيق الأستاذ نعمان طه.
٣٠- ديوان الحماسة لأبي تمام: شرح المرزوقي. تحقيق الأستاذ أحمد أمين. وعبد السلام هارون. مطبعة لجنة التأليف ١٣٧٢هـ - ١٩٥٣م.
٣١- ديوان ذي الرمة. المكتبة الأهلية ١٩٣٤.
٣٢- ديوان سقط الزند. بشرح التنوير. مطبعة مصطفى محمد.
٣٣- ديوان الشريف الرضي.
١- المطبعة الأدبية ببيروت ١٣٠٩هـ
٢- مطبعة الحلبي بمصر.
٣٤- ديوان الصولي.
٣٥- ديوان العباس بن الأحنف. طبعة الجوانب ١٢٩٧هـ.
٣٦- ديوان عمر بن أبي ربيعة. تحقيق الأستاذين عبد المنعم شلبي وإبراهيم الإبياري.
٣٧- ديوان الفرزدق. نشره الأستاذ عبد الله الصاوي. مطبعة الصاوي بمصر.
٣٨- ديوان القاضي الأرجاني. طبعة بيروت.
٤٠- ديوان كثير عزة. طبعة الجزائر.
٤١- ديوان المتنبي. بشرح الأستاذ عبد الرحمن البرقوقي. الطبعة الثانية.
٤٢- ديوان مسلم بن الوليد. تحقيق الدكتور سامي الدهان. دار المعارف بمصر.
٤٣- ديوان المعاني. أبو هلال العسكري، القاهرة حسام الدين القدسي، ١٣٥٢هـ.
٤٤- ديوان مهيار الديلمي. دار الكتب بمصر ١٣٤٩هـ - ١٩٣٠م.','4','553',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (535,'٤٥- ديوان النابغة الذبياني.
١- طبعة مصر ١٩٠٠
٢- ضمن مجموعة دواوين طبعة المطبعة الأهلية ببيروت.
٤٦- رسائل ابن الأثير. تحقيق الأستاذ أنيس المقدسي بيروت ١٩٥٩.
٤٧- زهر الآداب للحصري. تحقيق الأستاذ علي البجاوي. مطبعة الحلبي ١٣٧٢هـ - ١٩٥٣م.
٤٨- سر الفصاحة لابن سنان الخفاجي. تحقيق الأستاذ على فودة. المطبعة الرحمانية ١٣٥٠ - ١٩٣٢.
٤٩- سيرة ابن هشام. تحقيق الأساتذة مصطفى السقا وإبراهيم الإبياري وعبد الحفيظ شلبي.
٥٠- شذرات الذهب في أخبار من ذهب. ابن العماد الحنبلي. القدسي ١٣٥٠.
٥١- الشعر والشعراء. ابن قتيبة.
١- تحقيق الأستاذ أحمد شاكر. مطبعة الحلبي ١٣٧٠هـ.
٢- طبعة الحلوجي ١٣٣٢هـ.
٥٢- صبح الأعشى. القلقشندي. مطبعة دار الكتب بمصر.
٥٣- صحيح البخاري. مطبعة الحلبي ١٣٧٢هـ - ١٩٥٣م.
٥٤- الصناعيتن. أبو هلال العسكري. تحقيق الأستاذين علي البجاوي ومحمد أبو الفضل إبراهيم.
٥٥- طبقات الشعراء. ابن سلام. تحقيق الأستاذ محمود محمد شاكر. مطبعة دار المعارف بمصر.
٥٦- طبقات الشعراء ابن المعتز. تحقيق الأستاذ عبد الستار فراج. دار المعارف.','4','554',NULL,NULL,'0');
INSERT INTO [b10550] VALUES (536,'٥٧- العقد الفريد. ابن عبد ربه. تحقيق أحمد أمين وأحمد الزين إبراهيم الإبياري. مطبعة لجنة التأليف والترجمة النشر، ١٩٤١ - ١٩٤٩.
