INSERT INTO [b10720] VALUES (159,'<span data-type="title" id=toc-27>ثبت المصادر والمراجع المخطوطة والمطبوعة:</span>
١- القرآن الكريم.
٢- "آداب الشافعي ومناقبه": لابن أبي حاتم الرازي "ت٣٢٧هـ" تحقيق عبد الغني عبد الخالق، مطبعة السعادة.
٣- "آداب الفتيا": لجلال الدين بن أبي بكر السيوطي "ت٩١١هـ"، مخطوط بمكتبة برستن بأمريكا، مجموعة يهودا ضمن مجموع تحت رقم: "٨٣١".
٤- "الإبهاج في شرح المنهاج على الوصول إلى علم الأصول": للقاضي البيضاوي "ت٦٨٥هـ": تأليف تقي الدين علي بن عبد الكافي السبكي "ت٧٥٦هـ"، وولده تاج الدين عبد الوهاب بن علي السبكي "ت٧٧١هـ"، دار الكتب العلمية، بيروت "١٤٠٤هـ-١٩٨٤م".
٥- الاجتهاد: للسيوطي = "الرد على من أخلد إلى الأرض وجهل أن الاجتهاد في كل عصر فرض".
٦- "الإحكام في أصول الأحكام": لسيف الدين أبي الحسن علي بن محمد الآمدي "ت٤٦٧هـ"، دار الكتب العلمية، بيروت "١٤٠٠هـ-١٩٨٠م".
٧- "الإحكام في تمييز الفتاوى عن الأحكام وتصرفات القاضي والإمام": للإمام شهاب الدين أبي العباس أحمد بن إدريس المصري المالكي "ت٦٨٤هـ"،',NULL,'186',NULL,NULL,'0');
INSERT INTO [b10720] VALUES (160,'حققه وعلق عليه عبد الفتاح أبو غدة، مكتب المطبوعات الإسلامية، حلب "١٣٨٧هـ-١٩٦٧م".
٨- "أحكام القرآن": لأبي بكر محمد بن عبد الله المعروف بابن العربي "ت٥٤٣هـ"، تحقيق علي محمد البجاوي، طبع بمطبعة عيسى البابي الحلبي وشركاه "١٣٧٦هـ-١٩٧٥م".
٩- "أحكام القرآن": لأبي بكر أحمد بن علي الرازي الجصاص "ت٣٧٠هـ"، المطبعة البهية، مصر ١٣٤٧هـ.
١٠- "إحياء علوم الدين": لأبي حامد محمد بن محمد بن محمد الغزالي "ت٥٠٥هـ" دار المعرفة، بيروت.
١١- "أدب الدنيا والدين": لأبي الحسن علي بن محمد بن حبيب البصري الماوردي "ت٤٠٥هـ" تحقيق مصطفى السقا، دار الفكر للطباعة والنشر والتوزيع.
١٢- "إرشاد الفحول إلى تحقيق الحق من علم الأصول": لمحمد علي الشوكاني "ت١١٢٥هـ"، مطبعة محمد علي صبيح، ١٣٤٩هـ.
١٣- "إرشاد الفحول إلى تحقيق الحق من علم الأصول": لمحمد علي الشوكاني، دار الفكر.
١٤- "أسد الغابة": لعز الدين أبي الحسن علي بن محمد الجزري "ت٦٣٠هـ"، كتاب الشعب، القاهرة.
١٥- "الاشتقاق": لأبي بكر محمد بن الحسن بن دريد "ت٣٢١هـ"، تحقيق عبد السلام هارون، مؤسسة الخانجي، القاهرة.
١٦- "الإصابة في تمييز الصحابة": لأبي الفضل أحمد بن علي حجر العسقلاني "ت٨٥٢هـ" تحقيق علي محمد البجاوي، دار نهضة مصر.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b10720] VALUES (161,'١٧- "أصول الدعوة": تأليف عبد الكريم زيدان، مكتبة المنار الإسلامية، الطبعة الثالثة، "١٣٩٦هـ-١٩٧٦م".
١٨- "الاعتبار في الناسخ والمنسوخ": لأبي بكر محمد بن موسى الحازمي "ت٥٨٤هـ". حققه محمد أحمد عبد العزيز، مكتبة عاطف، القاهرة.
١٩- "الإعلام": لخير الدين بن محمود بن محمد الزركلي "ت١٣٩٦هـ"، دار العلم للملايين، بيروت، الطبعة الرابعة ١٩٧٩م.
٢٠- "إعلام الموقعين عن رب العالمين": لشمس الدين أبي عبد الله محمد بن أبي بكر المعروف بابن قيم الجوزية "ت٧٥١هـ" تعليق محمد محيي الدين عبد الحميد، توزيع دار الباز، مكة المكرمة.
٢١- "الإكمال في رفع عارض الارتياب عن المؤتلف والمختلف من الأسماء والكنى والأنساب": لأبي نصر علي بن هبة الله المعروف بابن ماكولا "ت٤٧٥هـ" تحقيق المعملي اليماني، نشر أمين دمج، بيروت، لبنان.
٢٢- "الأم": للإمام محمد بن إدريس الشافعي المطلبي "ت٢٠٤هـ" دار الشعب، القاهرة.
٢٣- "الانتقاء في فصائل الثلاثة الفقهاء": لأبي عمر يوسف بن عبد البر النمري القرطبي "ت٤٦٣هـ"، دار الكتب العلمية بيروت.
٢٤- "الأنساب" لأبي سعد عبد الكريم بن محمد بن منصور التميمي السمعاني "ت٥٦٢هـ"، من المجلد "١-١٠" نشر أمين دمج بيروت، ومن "١١- ١٣" مطبعة المعارف الهندية.
٢٥- "إيضاح المكنون في الذيل على كشف الظنون": لإسماعيل بن محمد بن مير سليم الباباني البغدادي "ت١٣٣٩هـ"، طبع إستانبول.
٢٦- "البحر المحيط": للإمام بدر الدين محمد بن بهادر الزركشي "ت٧٩٤هـ"،',NULL,'188',NULL,NULL,'0');
INSERT INTO [b10720] VALUES (162,'مخطوطة بمكتبة الأزهر تحت رقم "٢٠".
٢٧- بدائع الصنائع في ترتيب الشرائع: لعلاء الدين أبي بكر بن مسعود الكاساني الحنفي "ت٥٨٧"، مطبعة الإمام، قلعة مصر.
٢٨- البرهان في أصول الفقه: لإمام الحرمين أبي المعالي عبد الملك بن عبد الله الجويني "٤٧٨هـ" تحقيق عبد العظيم الديب، دار الأنصار، القاهرة، الطبعة الثانية، "١٤٠٠هـ".
٢٩- بغية الملتمس في تاريخ رجال الأندلس: لابن عميرة الضبي "مجريط، ١٨٨٤م".
٣٠- بغية الوعاة في طبقات اللغويين والنحاة: للحافظ جلال الدين عبد الرحمن السيوطي "ت٩١١هـ" تحقيق محمد أبو الفضل إبراهيم، الطبعة الأولى "١٣٨٤هـ-١٩٦٤م", عيسى البابي الحلبي، القاهرة.
٣١- تاج العروس من جواهر القاموس: لأبي الفيض محمد مرتضى الحسيني الزبيدي "١٢٠٥هـ" دار الحياة بيروت.
