-- Insert data for b10910
INSERT INTO [b10910] VALUES (1,'<span data-type="title" id=toc-1>بسم الله الرحمن الرحيم</span>
عن أبى سعيد الخدرى - رضى الله عنه - أن النبى - صلى الله عليه وسلم - قال: (لاَ ضَررَ ولا ضِرار) حديث حسن.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (2,'رواه ابن ماجه والدارقطنى، وغيرهما مسندا.
ورواه مالك في (الموطأ) عن عمرو بن يحيى عن أبيه عن النبى صلى الله عليه وسلم مرسلا، فأسقط أبا سعيد.
وله طرق يقوى بعضها ببعض.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (3,'<span data-type="title" id=toc-2>حديث أبى سعيد الخدري</span>
حديث أبى سعيد لم يخرجه ابن ماجه.
إنما خرجه الدارقطنى والحاكم والبيهقى من رواية عثمان بن محمد بن عثمان بن ربيعة حدثنا الدراوردى عن عمرو بن يحيى المازنى عن أبيه عن أبى سعيد الخدرى عن النبى - صلى الله عليه وسلم - قال: (لا ضَررَ ولا ضِرار، مَن ضَارَّ، ضَرَّه اللهُ، وَمَن شَاقَّ، شقَّ اللهُ عليه) .',NULL,'15',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (4,'وقال الحاكم: (صحيح الإسناد على شرط مسلم) .
وقال البيهقى. (تفرد به عثمان عن الدراوردي) .
وخرجه مالك في (الموطأ) عن عمرو بن يحيى عن أبيه مرسلا.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (5,'قال ابن عبد البر: (لم يختلف عن مالك في إرسال هذا الحديث) .
قال: (ولا يسند من وجه صحيح) .
ثم خرجه من رواية عبد الملك بن معاذ النصيبى عن الدراوردى موصولا.
والدراوردى كان الإمام أحمد يضعف ما حدث به من حفظه ولا يعبأ به، ولا شك في تقديم قول مالك على قوله.
وقال خالد بن سعد الأندلسى الحافظ: (لم يصح حديث) لا ضرر ولا ضرار (مسند) .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (6,'<span data-type="title" id=toc-3>حديث عبادة بن الصامت</span>
وأما ابن ماجه فخرجه من رواية فضيل بن سليمان. حدثنا موسى بن عقبة حدثنا إسحاق بن يحيى بن الوليد، عن عبادة بن الصامت، أن رسول الله صلى الله عليه وسلم قضى أن: (لا ضَررَ ولا ضِرَار) .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (7,'وهذا من جملة صحيفة تروى بهذا الإسناد، وهى منقطعة مأخوذة من كتاب، قاله ابن المدينى، وأبو زرعة، وغيرهما.
وإسحاق بن يحيى قيل: هو أبن طلحة، وهو ضعيف، لم يسمع من',NULL,'19',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (8,'عبادة، قاله أبو زرعة، وابن أبى حاتم، والدارقطني في موضع.
وقيل: إنه إسحاق بن يحيى بن الوليد بن عبادة، ولم يسمع أيضا. من عبادة قاله الدارقطنى - أيضا.
وذكره ابن عدى في كتابه (الضعفاء) وقال: عامة أحاديثه غير محفوظة.
وقيل: إن موسى بن عقبة لم يسمع منه، وإنما روى هذه الأحاديث عن أبى عياش الأسدى عنه، وأبو عياش لا يعرف.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (9,'<span data-type="title" id=toc-4>حديث ابن عباس</span>
وخرجه ابن ماجه - أيضا - من وجه آخر من رواية جابر الجعفى عن عكرمة عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم: (لا ضررَ ولا ضِرارَ) .
وجابر الجعفى ضعفه الأكثرون.
وخرجه الدارقطنى من رواية إبراهيم بن إسماعيل عن داود بن الحصين',NULL,'22',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (10,'عن عكرمة به وإبراهيم ضعفه. جماعة.
وروايات داود عن عكرمة مناكير.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (11,'<span data-type="title" id=toc-5>حديث عائشة أم المؤمنين</span>
وخرج الدارقطني من حديث الواقدى حدثنا خارجة بن عبد الله بن سليمان بن زيد بن ثابت عن أبى الرجال عن عمرة عن عائشة رضى الله عنها عن النبى صلى الله عليه وسلم قال: (لا ضررَ ولا ضِرَار) .
والواقدي متروك، وشيخه مختلف في تضعيفه.
وخرجه الطبراني من وجهين ضعيفين - أيضا - عن القاسم عن عائشة رضي الله عنها.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (12,'<span data-type="title" id=toc-6>حديث جابر بن عبد الله</span>
وخرج الطبرانى - أيضا - من رواية محمد بن سلمة عن ابن إسحاق عن محمد بن يحيى بن حبان عن عمه واسع بن حبان عن جابر عن النبي صلى الله عليه وسلم قال: (لا ضَررَ وَلا ضِرار في',NULL,'26',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (13,'الإِسلام) .
وهذا إسناد مقارب، وهو غريب.
لكن خرجه أبو داود في (المراسيل) من رواية عبد الرحمن بن مغراء عن ابن إسحاق عن محمد بن يحيى بن حبان عن عمه واسع مرسلا، وهو أصح.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (14,'<span data-type="title" id=toc-7>حديث أبي هريرة</span>
وخرج الدارقطني من رواية أبي بكر بن عياش قال: أراه عن ابن عطاء عن أبيه عن أبي هريرة أن النبي صلى الله عليه وسلم - قال: (لا ضَرَر ولا ضَروُرةَ، ولا يَمنَعَنَّ أَحَدُكم جَارَه أَن يَضَعَ خَشَبَهُ عَلَى حَائِطِه) .
وهذا الإسناد فيه شك.
وابن عطاء، هو يعقوب، وهو ضعيف.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (15,'<span data-type="title" id=toc-8>حديث عمرو بن عوف المزنى</span>
وروى كثير بن عبد الله بن عمرو بن عوف المزني عن أبيه عن جده عن النبي صلى الله عليه وسلم قال: (لا ضَررَ ولا ضِرَارَ) .
قال ابن عبد البر: (إسناده غير صحيح) .
قلت كثير هذا، يصحح حديثه الترمذي ويقول',NULL,'29',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (16,'البخاري في بعض حديثه: (هو أصح حديث في الباب) .
وحسن حديثه إبراهيم بن المنذر الحزامى، وقال: (هو خير من مراسيل ابن المسيب) .
وكذلك حسنه ابن أبي عاصم.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (17,'وترك حديثه آخرون، منهم الإمام أحمد، وغيره.
فهذا ما حضرنا من ذكر طرق أحاديث هذا الباب.
وقد ذكر الشيخ - رحمه الله - أن بعض طرقه يقوى ببعض، وهو كما قال.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (18,'وقد قال البيهقي في بعض أحاديث كثير بن عبد الله المزنى: (إذا انضمت إلى غيرها من الأسانيد التي فيها ضعف قويت) .
وقال الشافعي في المرسل: (إنه إذا أسند من وجه آخر أو أرسله من يأخذ العلم عن غير من يأخذ عنه المرسل الأول فإنه يقبل) .',NULL,'32',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (19,'وقال الجوزجانى: (إذا كان الحديث المسند من رجل غير مقنع) .
يعني لا يقنع برواياته، وشد أركانه المراسيل بالطرق المقبولة عند ذوي الاختيار، استعمل، واكتفى به.
هذا إذا لم يعارض بالمسند الذي هو أقوى منه.
وقد استدل الإمام أحمد بهذا الحديث وقال: قال النبي صلى الله عليه وسلم: (لاَ ضَررَ ولاَ ضِرَارَ) .',NULL,'33',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (20,'وقال أبو عمرو بن الصلاح: (هذا الحديث أسنده الدارقطني من وجوه، ومجموعها يقوى، الحديث، ويحسنه، وقد تقبله جماهير أهل العلم واحتجوا به) .
وقول أبي داود: (إنه من الأحاديث التي يدور الفقه عليها، يشعر بكونه غير ضعيف. والله أعلم.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (21,'<span data-type="title" id=toc-9>شواهد الحديث</span>
وفي المعنى - أيضا - حديث أبي صرمة عن النبي صلى الله عليه وسلم قال: (مَن ضارَّ، ضَارَّ الله به، وَمَن شَاقَّ، شَاقَّ الله عليه) .
خرجه أبو داود والترمذي وابن ماجه.
وقال الترمذى: (حسن غريب) .',NULL,'35',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (22,'وخرج الترمذي بإسناد فيه ضعف عن أبي بكر الصديق - رضي الله عنه - عن النبي صلى الله عليه وسلم قال: (مَلعُونٌ مَن ضَارَّ مُؤمِناً أَو مَكَر بِه) .',NULL,'36',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (23,'<span data-type="title" id=toc-10>الفرق بين الضرر والضرار في اللغة</span>
وقوله صلى الله عليه وآله وسلم: (لا ضَرَرَ ولا ضِرار) هذه الرواية الصحيحة: (ضرار) بغير همزة وروى: (إضرار) بالهمزة، ووقع ذلك في بعض روايات ابن ماجه، والدارقطنى، بل وفي بعض نسخ (الموطأ) .
وقد أثبت بعضهم هذه الرواية وقال: يقال: (ضر وأضر بمعنى، وأنكرها آخرون، وقالوا: (لا صحة لها) .',NULL,'37',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (24,'واختلفوا هل بين اللفظين - أعني الضرر والضرار - فرق أم لا؟ ١ - فمنهم من قال: (هما بمعنى واحد، على وجه التأكيد) .
والمشهور أن بينهما فرقا.
٢ - ثم قيل: (إن الضرر: هو الاسم، والضرار: الفعل) ، فالمعنى: (أن الضرر نفسه، منتف في الشرع، وإدخال الضرر بغير حق، كذلك) .
٣ - وقيل: (الضرر: أن يدخل على غيره ضررا، بما ينتفع هو به، والضرار: أن يدخل على غيره ضررا، (بما لا ينتفع هو به) ، كمن منع مالا يضره، ويتضرر به الممنوع) .
ورجح هذا القول، طائفة منهم، ابن عبد البر، وابن الصلاح.
٤ - وقيل: (الضرر: أن يضر بمن لا يضره، والضرار. أن يضر بمن قد أضر به، على وجه غير جائز) .',NULL,'38',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (25,'<span data-type="title" id=toc-11>احتراز واجب</span>
وبكل حال، فالنبى - صلى الله عليه وسلم - إنما نفى الضرر والضرار، بغير حق.
فأما إدخال الضرر على أحد بحق: أ - إما لكونه تعدى حدود الله، فيعاقب بقدر جريمته.
ب - أو كونه ظلم غيره، فيطلب المظلوم، مقابلته بالعدل، فهذا غير مراد قطعا.
وإنما المراد، إلحاق الضرر بغير حق، وهذا على نوعين: أحدهما: أن لا يكون في ذلك غرض سوى الضرر بذلك الغير، فهذا لا ريب في قبحه وتحريمه.
وقد ورد في القرآن النهى عن المضارة في مواضع:',NULL,'39',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (26,'<span data-type="title" id=toc-12>الضرار في الوصية من الكبائر</span>
منها في الوصية قال تعالى: (مِن بَعدِ وَصَيَّةٍ يُوصَى بِهَا أَو دَينٍ غَيرَ مُضَارّ) .
وفى حديث أبى هريرة - رضى الله عنه - المرفوع: (إِنَّ العَبدَ لَيَعمَلُ بِطَاعَةِ الله - عَزّ وجَلّ - سِتّينَ سنَة ثمَّ يَحضُرُهُ المَوتُ فيضارّ في',NULL,'40',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (27,'الوَصِيَّةِ، فَيَدخُل النَّارَ، ثُمَّ تلا: (تِلكَ حُدُودُ الله - إلى قوله - وَمَن يَعصِ الله وَرَسُولَهُ وَيتَعَدَّ حُدُودَهُ يُدخِلهُ ناراً خالِداً فِيهَا)) .
وخرجه الترمذى وغيره بمعناه.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (28,'وقال ابن عباس: (الإضرار في الوصية من الكبائر، ثم تلا هذه الآية) .
والإضرار في الوصية:',NULL,'42',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (29,'أ - تارة يكون بأن يخص بعض الورثة بزيادة على فرضه الذى فرضه الله له، فيتضرر بقية الورثة بتخصيصه.
ولهذا قال النبى - صلى الله عليه وسلم -: (إِنَّ الله قَد أَعطى كُلَّ ذِى حَقًّ، حقّه، فَلا وَصَّيةَ لِوارثٍ) .
ب - وتارة بأن يوصى لأجنبى بزيادة على الثلث، فتنقص حقوق الورثة، ولهذا قال النبى - صلى الله عليه وسلم -: (الثُلُثُ، والثلثُ كَثِير) .',NULL,'43',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (30,'ومتى وصى لوارث أو لأجنبى بزيادة على الثلث، لم ينفذ ما وصى به إلا بإجازة الورثة، وسواء قصد المضارة أو لم يقصد.
وأما إن قصد المضارة بالوصية، لأجنبى بالثلث، فإنه يأثم بقصده المضارة.
وهل ترد وصيته، إذا ثبت ذلك بإقراره أم لا؟',NULL,'44',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (31,'حكى ابن عطية رواية عن مالك أنها ترد، وقيل: إنه قياس مذهب أحمد.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (32,'<span data-type="title" id=toc-13>المرأة بين إهانة الجاهلية وتكريم الإسلام</span>
ومنها الرجعة في النكاح.
قال تعالى (فَأَمسِكُوهُنَّ بَمَعرُوف أَو سَرَّحُوهُنَّ بِمعرُوٍف، وَلاَ تُمسِكُوهُنَّ ضِرارَاً لِتَعتَدُوا، وَمَن يَفعل ذَلِكَ فَقد ظَلَمَ نَفسَهُ) .
وقال تعالى: (وَبُعُولَتُهنَّ أَحَقُّ بِرَدَّهِنَّ في ذَلِك، إِن أَرَادُوا إِصلاَحاً) .
فدل ذلك على أن من كان قصده بالرجعة المضارة، فإنه يأثم بذلك.
وهذا كما كانوا في أول الإسلام قبل حصر الطلاق في ثلاث، يطلق الرجل امرأته ثم يتركها حتى تقارب انقضاء عدتها ثم يراجعها ثم يطلقها، ويفعل ذلك',NULL,'46',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (33,'أبدا بغير نهاية، فيدع المرأة لا مطلقة ولا ممسكة، فأبطل الله ذلك، وحصر الطلاق في ثلاث مرات.
وذهب مالك إلى أن من راجع امرأته قبل انقضاء عدتها ثم طلقها من غير مسيس، إن قصد بذلك مضارتها، بتطويل العدة، لم تستأنف العدة، وبنت على ما مضى منها، وإن لم يقصد ذلك، استأنفت عدة جديدة.
وقيل: تبنى مطلقا، وهو قول عطاء وقتادة والشافعى في القديم وأحمد في رواية.
وقيل: تستأنف مطلقا، وهو قول الأكثرين منهم أبو قلابة، والزهرى، والثورى، وأبو حنيفة، والشافعى، في الجديد وأحمد في رواية، وإسحاق، وأبو عبيد، وغيرهم.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (34,'<span data-type="title" id=toc-14>الإيلاء تسكين لنوازع الشر</span>
<span data-type="title" id=toc-15>وفرصة لمراجعة النفس</span>
ومنها في الإيلاء، فإن الله جعل مدة الإيلاء للمولى أربعة أشهر، إذا حلف الرجل على امتناع وطء زوجته، فإنه يضرب له مدة أربعة أشهر، فإن فاء ورجع إلى الوطء كان ذلك توبته، وإن أصر على الامتناع لم يمكن من ذلك.
ثم فيه قولان للسلف والخلف: ١ - أحدهما: أنها تطلق علية بمضى هذه المدة.
٢ - والثانى: أن يوقف، فإن فاء وإلا أمر بالطلاق.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (35,'ولو ترك الوطء لقصد إضرار بغير يمين مدة أربعة أشهر، فقال كثير من أصحابنا: (حكمه حكم المولى في ذلك) ، وقالوا هو ظاهر كلام أحمد.
وكذا قال جماعة منهم: (إنه إذا ترك الوطء أربعة أشهر لغير عذر، ثم طلبت الفرقة، فرق بينهما، بناء على أن الوطء عندنا في هذه المدة واجب.
واختلفوا هل يعتبر لذلك قصد الإضرار أم لا يعتبر؟، ومذهب مالك وأصحابه إذا ترك الوطء من غير عذر، فإنه يفسخ نكاحه مع اختلافهم في تقدير المدة.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (36,'ولو طال السفر من غير عذر، وطلبت امرأته قدومه، فأبى، فقال مالك وأحمد وإسحاق: (يفرق الحاكم بينهما) .
وقدره أحمد بستة أشهر، وإسحاق بمضى سنتين.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (37,'<span data-type="title" id=toc-16>الحقوق الثلاثة</span>
ومنها في الرضاع.
قال تعالى: (لاَ تُضَارَّ وَالِدةٌ بِولِدهَا، وَلا مَولُودٌ لَهُ بِولَدِه) .

<span data-type="title" id=toc-17>حق الأم في حضانة وليدها</span>
قال مجاهد في قوله - تعالى -: (لا تُضَارَّ وَالِدةٌ بِوَلِدهَا) : (لا يمنع أمه أن ترضعه، ليحزنها بذلك) .',NULL,'51',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (38,'وقال عطاء، وقتادة، والزهرى، وسفيان، والسدى، وغيرهم: (إذا رضيت بما يرضى به غيرها، فهى أحق به) .
وهذا هو المنصوص عن أحمد، ولو كانت الأم في حبال الزوج.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (39,'<span data-type="title" id=toc-18>حق الزوج في الاستمتاع بزوجته</span>
وقيل: إن كانت في حبال الزوج، فله منعها من إرضاعه إلا أن لا يمكن ارتضاعه من غيرها، وهو قول الشافعى، وبعض أصحابنا.
لكن إنما يجوز ذلك، إذا كان قصد الزوج به توفير الزوجة للاستمتاع لا مجرد إدخال الضرر عليها.

<span data-type="title" id=toc-19>حق الطفل في حسن التربية</span>
وقوله تعالى: (وَلاَ مَولودٌ لَهُ بِوَلَدِه) يدخل فيه أن المطلقة إذا',NULL,'53',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (40,'طلبت إرضاع ولدها بأجرة مثلها، لزم الأب إجابتها إلى ذلك.
وسواء وجد غيرها أو لم يوجد، هذا منصوص الإمام أحمد.
فإن طلبت زيادة على أجرة مثلها زيادة كثيرة، ووجد الأب من يرضعه بأجرة المثل، لم يلزم الأب إجابتها إلى ما طلبت لأنها تقصد المضارة، وقد نص عليه الإمام أحمد - أيضا.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (41,'<span data-type="title" id=toc-20>بيع المضطر</span>
ومنها في البيع وقد ورد النهى عن بيع المضطر، خرجه أبو داود من حديث على بن أبى طالب أنه خطب الناس فقال: (سيأتى على الناس زمان عضوض، يعض الموسر على ما في يديه، ولم يؤمر بذلك، قال الله: (ولاَ تَنسَوُا الفَضلَ بَينكُم) ، ويبايع المضطرون، وقد نهى رسول الله صلى الله عليه وسلم عن بيع المضطر) .',NULL,'55',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (42,'وخرجه الإسماعيلى وزاد فيه: (قال رسول الله صلى الله عليه وسلم: (إِن كَانَ عِندكَ خَيرٌ تعودُ به عَلَى أَخيِكَ وَإلاّ فلا تزيده هلاَكاً إِلى هلاَكِه) .
وخرجه أبو يعلى الموصلى بمعناه من حديث حذيفة مرفوعا - أيضا وقال عبد الله بن معقل: (بيع الضرورة ربا) .',NULL,'56',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (43,'<span data-type="title" id=toc-21>خيار الغبن</span>
قال حرب: سئل أحمد عن بيع المضطر فكرهه، فقيل له: كيف هو؟ قال يجيئك وهو محتاج، فتبيعه ما يساوى عشرة بعشرين.
وقال أبو طالب: قيل لأحمد: إن ربح بالعشرة خمسة؟ فكره ذلك.
وإن كان المشترى مسترسلا لا يحسن أن يماكس - فباعه بغبن كثير لم يجز أيضا.
قال أحمد: الخلابة: الخداع، وهو أن يغبنه فيما لا يتغابن الناس في مثله، ببيعه ما يساوى درهما بخمسة.
ومذهب مالك وأحمد أنه يثبت له خيار الفسخ بذلك.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (44,'<span data-type="title" id=toc-22>مسألة التورق</span>
ولو كان محتاجا إلى نقد، فلم يجد من يقرضه، فاشترى سلعة بثمن إلى أجل في ذمته، ومقصوده بيع تلك السلعة، ليأخذ ثمنها فهذا فيه قولان للسلف.
ورخص أحمد فيه في رواية، وقال في رواية: أخشى أن يكون مضطرا.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (45,'<span data-type="title" id=toc-23>مسألة العينة</span>
فإن باع السلعة من بائعها له، فأكثر السلف على تحريم ذلك، وهو مذهب مالك وأبى حنيفة وأحمد وغيرهم.
ومن أنواع الضرر في البيوع التفريق بين الوالدة وولدها في البيع، فإن كان صغيرا حرم بالاتفاق.
وقد روى عن النبى صلى الله عليه وسلم أنه قال: (مَن فَرَّقَ بين الوالدةِ وَوَلِدهَا، فَرَّقَ اللهُ بينه وبين أَحَبَّتِه يومَ القِيامة) .',NULL,'59',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (46,'فإن رضيت الأم بذلك، ففى جوازه اختلاف.
ومسائل الضرر في الأحكام كثيرة جدا، وإنما ذكرنا هذا على وجه المثال.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (47,'<span data-type="title" id=toc-24>كل ابن آدم خطاء</span>
والنوع الثانى، أن يكون له غرض آخر صحيح.
أ - مثل أن يتصرف في ملكه، بما فيه مصلحة له، فيتعدى ذلك إلى ضرر غيره ... أو.
ب - يمنع غيره من الانتفاع بملكه توفيرا له، فيتضرر الممنوع بذلك.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (48,'<span data-type="title" id=toc-25>من حقوق الجار في دين الله</span>
فأما الأول، وهو التصرف في ملكه بما يتعدى ضرره إلى غيره.
أ - فإن كان على غير الوجه المعتاد: مثل أن يؤجج في أرضه نارا في يوم عاصف، فيحترق ما يليه، فإنه متعد بذلك، وعليه الضمان.
ب - وإن كان على الوجه المعتاد، ففيه للعلماء قولان مشهوران: أحدهما: لا يمنع من ذلك، وهو قول الشافعى وأبى حنيفة وغيرهما.
والثانى: المنع، وهو قول أحمد، ووافقه مالك في بعض الصور.
فمن صور ذلك: ١ - أن يفتح كوة في بنائه العالى مشرفة على جاره.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (49,'٢ - أو يبنى بناء عاليا يشرف على جاره ولا يستره، فإنه يلزم بستره.
نص عليه أحمد، ووافقه طائفة من أصحاب الشافعى.
قال الرويانى منهم في كتاب: (الحلية) : (يجتهد الحاكم في ذلك، ويمنع إذا ظهر له التعنت، وقصد الفساد) .
قال: (وكذلك القول في إطالة البناء، ومنع الشمس والقمر) .',NULL,'63',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (50,'وقد خرج الخرائطى وابن عدى بإسناد ضعيف عن عمرو بن شعيب عن أبيه عن جده مرفوعا حديثا طويلا في حق الجار، وفيه: (ولا يستطيل بالبناء، فيحجب عنه الريح إلا بإذنه) .',NULL,'64',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (51,'٣ - ومنها أن يحفر بئرا بالقرب من بئر جاره، فيذهب مأوها، فإنها تطم في ظاهر مذهب مالك وأحمد.
٤ - وخرج أبو داود في (المراسيل) من حديث أبى قلابة قال: قال رسول الله صلى الله عليه وسلم: (لا تُضارُّوا في الحَفر) ، وذلك أن يحفر الرجل إلى جنب الرجل ليذهب بمائة.
٤ - ومنها أن يحدث بملكه ما يضر بملك جاره من هز أو دق ونحوهما.
فإنه يمنع منه في ظاهر مذهب مالك وأحمد، وهو أحد الوجوه للشافعية.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (52,'وكذا إذا كان يضر بالسكان، كما له رائحة خبيثة ونحو ذلك.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (53,'<span data-type="title" id=toc-26>لا تعسف في استعمال الحق</span>
٥ - ومنها أن يكون له ملك في أرض غيره، ويتضرر صاحب الأرض بدخوله إلى أرضه، فإنه يجبر على إزالته ليندفع به ضرر الدخول.
خرج أبو داود في (سننه) من حديث أبى جعفر محمد بن على أنه حدث عن سمرة بن جندب أنه كانت له عضد من نخل في حائط رجل من الأنصار، ومع الرجل أهله، فكان سمرة يدخل إلى نخله، فيتأذى به، ويشق عليه، فطلب إليه أن يبيعه، فأبى فطلب إليه أن يناقله فأبى، فأتى النبى صلى الله عليه وسلم فذكر ذلك له، فطلب إليه النبى صلى الله عليه وسلم أن يبيعه فأبى، فطلب إليه أن يناقله فأبى، قال: (فَهبه لَه، وَلكَ كَذا وكَذا) أمر رغبه فيه فأبى، فقال: (أَنتَ مُضَارُّ) ، فقال النبى صلى الله عليه وسلم للأنصارى: (اذهَب',NULL,'67',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (54,'فَاقلع نَخلَهُ) .
وقد روى عن أبى جعفر مرسلا.
قال أحمد - في رواية حنبل بعد أن ذكر له هذا الحديث -: كل ما كان على هذه الجهة، وفيه ضرر يمنع من ذلك، فإن أجاب وإلا أجبره السلطان، ولا يضر بأخيه في ذلك، وفيه مرفق له.
وخرج أبو بكر الخلال من رواية عبد الله بن محمد بن عقيل عن عبد الله بن سليط بن قيس عن أبيه: (أن رجلا من الأنصار كانت في حائطه',NULL,'68',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (55,'نخلة لرجل آخر، فكان صاحب النخلة لا يريمها غدوة وعشية، فشق ذلك على صاحب الحائط، فأتى النبى صلى الله عليه وسلم فذكر ذلك له، فقال النبى صلى الله عليه وسلم لصاحب النخلة: (خُذ مِنه نخلةً مِمَّا يلى الحائِطَ مكَان نخلتِك، قال: لا واللهِ، قال: فَخُذ منه ثِنتين، قَال: لاَ واللهِ.
قَالَ فهبها له قَالَ لاَ واللهِ، قالَ: فردد عليه رسول الله صلّى الله عليه وسلّم، فَأَبَى، فَأَمَر رسولُ الله صلّى الله عليه وسلّم أَن يُعطيَه نخلةً مَكَانَ نخلتِه) .
وخرج أبو داود في (المراسيل) من رواية ابن إسحاق عن محمد بن يحيى بن حبان عن عمه واسع بن حبان قال: (كَانَت لأبى لُبابة عَذقٌ في حائِط رَجُلٍ، فَكَلَّمه، فقال: إِنَّك تطأُ حَائِطى إلى عذقك، فَأَنا أُعطِيك مِثله في حَائِطِك، وَأَخرِجه عَنىَّ، فَأَبَى عَلَيه، فَكَلّم النَّبى صلّى الله عليه وسلّم فيه فَقَال: يا أَبا لُبابة! خُذ مِثلَ عَذقِك، فَحزهَا إلى مَالِك، واكفُف عن',NULL,'69',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (56,'صَاحبِكَ مَا يَكرَهُ) ، فَقَال: مَا أَنَا بِفَاعِل، فَقَال: (اذهَب فاَخرج لَهُ مِثلَ عَذقِه إلى حَائِطه، ثُمَّ اضرب فوقَ ذَلك بِجِدَارٍ، فإنَّهُ لا ضَرَرَ في الإِسلامِ وَلا ضِرارَ) .
ففى هذا الحديث والذى قبله إجباره على المعاوضة حيث كان على شريكه أو جاره ضرر في شركه، وهذا مثل إيجاب الشفعة لدفع ضرر الشريك الطارئ.
ويستدل بذلك - أيضا - على وجوب العمارة على الشريك الممتنع من العمارة، وعلى إيجاب البيع إذا تعذرت القسمة.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (57,'وقد ورد من حديث محمد بن أبى بكر عن أبيه مرفوعا: (لا تعضية في الميراث إلا ما احتمل القسم) .
وأبو بكر هو ابن عمرو بن حزم، قال الإمام أحمد: والحديث حينئذ مرسل، والتعضية هى القسمة.
ومتى تعذرت القسمة لكون المقسوم يتضرر بقسمته، وطلب أحد',NULL,'71',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (58,'الشريكين البيع، أجبر الآخر وقسم الثمن، نص عليه أحمد، وأبو عبيد وغيرهما من الأئمة.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (59,'<span data-type="title" id=toc-27>المسلم بين كرم الإيمان والحرص على ملكه</span>
وأما الثانى، وهو منع الجار من الانتفاع بملكه والارتفاق به.
فإن كان ذلك يضر بمن انتفع بملكه، فله المنع، كمن له جدار واه، لا يحتمل أن يطرح عليه خشب.
وأما إن لم يضر به، فهل يجب عليه التمكين ويحرم عليه الامتناع أم لا؟.
فمن قال في القسم الأول: لا يمنع المالك من التصرف في ملكه وإن أضر بجاره، قال هنا: للجار المنع من التصرف في ملكه بغير إذنه.
ومن قال هناك بالمنع، فاختلفوا ههنا على قولين: I - أحدهما: المنع ههنا، وهو قول مالك.
ب - والثانى: أنه لا يجوز المنع، وهو مذهب أحمد في طرح الخشب على جدار جاره، ووافقه الشافعى في القديم، وإسحاق وأبو ثور وداود وابن المنذر',NULL,'73',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (60,'وعبد الملك بن حبيب المالكى، وحكاه مالك عن بعض قضاة المدينة.
وفى الصحيحين عن أبى هريرة - رضى الله عنه - عن النبى صلى الله عليه وسلم قال: (لا يمنعنَّ أَحَدُكُم جَارَه أَن يغرِز خَشَبه علَى جِدَارِه) قال أبو هريرة: (مَالِى أَرَاكُم عَنها مُعرضِين؟ وَاللهِ! لأرمِينَّ بِها بَينَ أَكتَافِكُم) .
وقضى عمر بن خطاب - رضى الله عنه - على محمد بن مسلمة أن يجرى ماء جاره في أرضه، وقال: (لتمرنَّ بِه وَلو علَى بَطِنك) .',NULL,'74',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (61,'وفى الإجبار على ذلك روايتان عن الإمام أحمد، ومذهب أبى ثور الإجبار على إجراء الماء في أرض جاره إذا أجراه في قنى في باطن أرضه، نقله عنه حرب الكرمانى.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (62,'<span data-type="title" id=toc-28>لا اشتراكية في الإسلام</span>
ومما ينهى عن منعه للضرر، منع الماء والكلأ.
وفى (الصحيحين) عن أبى هريرة: رضى الله عنه - عن النبى صلى الله عليه وسلم قال: (لا تَمنعُوا فَضلَ المَاءِ، لِتَمنعُوا بِه الكَلأَ) .',NULL,'76',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (63,'وفى (سنن) أبى داود أن رجلا قال: يا نبى الله، ما الشئ الذى لا يحل منعه؟ قال: (الماء) قال: يا نبى الله، ما الشئ الذى لا يحل منعه؟ قال: (المِلحُ) قال: يا نبى الله، ما الشئ الذى لا يحل منعه؟ قال: (أَن تَفعلَ الخيرَ خَيرٌ لَك) .
وفيها - أيضا - أن النبى صلى الله عليه وسلم - قال: (النَّاسُ شُرَكَاءُ في ثَلاثةٍ، في المَاءِ وَالنَّارِ وَالكَلأِ) .',NULL,'77',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (64,'وذهب أكثر العلماء إلى أنه لا يمنع فضل الماء الجارى والنابع مطلقا،',NULL,'78',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (65,'سواء قيل: إن الماء ملك لمالك أرضه أم لا.
وهذا قول أبى حنيفة والشافعى وأحمد وإسحاق وأبى عبيد وغيرهم.
٢ - والمنصوص عن أحمد وجوب بذله مجانا، بغير عوض للشرب، وسقى البهائم، وسقى الزرع.
ومذهب أبى حنيفة والشافعى: لا يجب بذله للزرع.
٣ - واختلفوا: هل يجب بذله - مطلقا - وإذا كان بقرب الكلأ، وكان منعه مفضيا إلى منع الكلأ؟.
على قولين لأصحابنا وأصحاب الشافعى، وفى كلام أحمد ما يدل على اختصاص المنع بالقرب من الكلأ.
٤ - وأما مالك فلا يجب عنده، بذل فضل الماء، الذى يملك منبعه ومجراه إلا للمضطر، كالمحاز في الأوعية.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (66,'وإنما يجب - عنده - بذل فضل الماء الذى لا يملك.
٥ - وعند الشافعى حكم الكلأ - كذلك - يجوز منع فضله إلا في أرض الموات، ومذهب أبى حنيفة وأحمد وأبى عبيد: أنه لا يمنع فضل الكلأ مطلقا.
٦ - ومنهم من قال: لا يمنع أحد الماء والكلأ إلا أهل الثغور خاصة، وهو قول الأوزاعى.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (67,'لأن أهل الثغور، إذا ذهب ماؤهم وكلؤهم، لم يقدروا أن يتحولوا من مكانه، من وراء بيضة الإسلام وأهله.
٧ - وأما النهى عن منع النار: I - فحمله طائفة من الفقهاء، على النهى عن الاقتباس منها، دون أعيان الجمر.
ب - ومنهم من حمله على منع الحجارة المورية للنار، وهو بعيد.
ج - ولو حمل على منع الاستضاءة بالنار، وبذل ما فضل عن حاجة صاحبها بها، لمن يستدفئ بها، أو ينضج عليها طعاما ونحوه، لم يبعد.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (68,'<span data-type="title" id=toc-29>لا إقطاعية في الإسلام</span>
٨ - وأما الملح، فلعله يحمل على منع أخذه من المعادن المباحة، فإن الملح من المعادن الظاهرة، لا يملك بالإحياء ولا بالإقطاع، نص عليه أحمد.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (69,'وفى: (سنن) أبى داود، أن النبى - صلى الله عليه وسلم - أقطع رجلا الملح، فقيل له: يَا رَسولَ اللهِ: إِنَّه بِمَنزِلةِ المَاءِ العِدِّ، فانتَزَعهُ مِنه) .',NULL,'83',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (70,'<span data-type="title" id=toc-30>من مقاصد الشريعة رفع الحرج والمشقة عن العباد</span>
ومما يدخل في عموم قوله - صلى الله عليه وسلم - (لا ضَررَ) : أن الله عز وجل لم يكلف عباده فعل ما يضرهم البتة.
فإن ما يأمرهم به، هو عين الصلاح لدينهم ودنياهم، ومانهاهم عنه، هو عين فساد دينهم ودنياهم.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (71,'لكنه لم يأمر عباده بشئ، هو ضار لهم في أبدانهم - أيضا -، ولهذا - أ - أسقط الطهارة بالماء عن المريض، وقال - تعالى -: (مَا يُرِيدُ اللهُ لِيجعلَ عَليكُم مِن حَرَج) .
ب - وأسقط الصيام عن المريض والمسافر، وقال - تعالى -: (يريد الله بكم اليسر، ولا يريد بكم العسر) .
ت - وأسقط اجتناب محظورات الإحرام، كالحلق ونحوه، عمن كان مريضا، أو أذى من راسه، وأمر بالفدية.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (72,'وفى (المسند) عن ابن عباس قال: (قِيلَ لِرسُولِ الله صلى الله عليه وسلم: أَىُّ الأَديَانِ أَحَبُّ إِلى الله؟ قاَل: الحنيفيَّةُ السَّمحةُ) .
ومن حديث عائشة رضى الله عنها - عن النبى صلى الله عليه وسلم',NULL,'87',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (73,'قال: (إِنِّى أُرسِلتُ بِحنيِفَّيٍة سَمحة) .
ومن هذا المعنى ما في (الصحيحين) عن أنس أن النبى - صلى الله عليه وسلم -',NULL,'88',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (74,'رأى رجلا يمشى، قيل له: (إِنّه نَذَر أن يحجّ مَاشِياً) ، فقال: (إنَّ الله لغنى عن مشيه فليركب) .
وفى رواية: (إِنَّ اللهَ لَغَنى، عَن تَعذِيبِ هَذَا نفسَهُ) .',NULL,'89',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (75,'وفى: (السنن) عن عقبة بن عامر أن أخته نذرت أن تمشى إلى البيت فقال رسول الله صلى الله عليه وسلم -: (إِنَّ الله لاَ يَصنعُ بِشقاءِ أخُتِكَ شَيئاً فَلتركَب) .
وقد اختلف العلماء في حكم من نذر أن يحج ما شيا: I - فمنهم من قال: لا يلزمه المشى، وله الركوب بكل حال.
وهو رواية عن الأوزاعى وأحمد.
وقال أحمد: يصوم ثلاثة أيام.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (76,'وقال الأوزاعى: عليه كفارة يمين.
ب - والمشهور أنه يلزمه ذلك، إن أطاقه.
فإن عجز عنه: ١ - فقيل: يركب عند العجز، ولا شئ عليه، وهو أحد قولى الشافعى.
٢ - وقيل: عليه مع ذلك كفارة يمين، وهو قول الثورى وأحمد في رواية.
٣ - وقيل: عليه دم، قاله طائفة من السلف منهم عطاء ومجاهد والليث والحسن وأحمد في رواية.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (77,'٤ - وقيل: يتصدق بكراء ما ركب، روى عن الأوزاعى وحكاه عن عطاء.
٥ - وروى عن عطاء: يتصدق بقدر نفقته عند البيت.
٦ - وقالت طائفة من الصحابة وغيرهم: لا يجزيه الركوب بل يحج من قابل، فيمشى ما ركب، ويركب ما مشى.
وزاد بعضهم: وعليه هدى، وهو قول مالك، إذا كان ما ركبه كثيرا.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (78,'<span data-type="title" id=toc-31>سماحة الإسلام</span>
ومما يدخل في عمومه - أيضا - أن من عليه دين، لا يطالب به مع إعساره، بل ينظر إلى حال إيساره، قال تعالى: (وإن كَانَ ذُو عُسرَةٍ، فَنَظِرةٌ إِلى ميسَرَة) .',NULL,'93',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (79,'وعلى هذا قول جمهور العلماء خلافا لشريح في قوله: (إنَّ الآيةَ مُختَصةٌ بِديُونِ الرِّبَا في الجَاهِليّة) .
والجمهور أخذوا باللفظ العام.
ولا يكلف المدين أن يقضى، مما عليه في خروجه من ملكه ضرر، كثيابه ومسكنه المحتاج إليه، وخادمه كذلك.
ولا ما يحتاج إلى التجارة به، لنفقته ونفقة عياله، هذا مذهب الإمام',NULL,'94',NULL,NULL,'0');
INSERT INTO [b10910] VALUES (80,'أحمد - رحمة الله تعالى.',NULL,'95',NULL,NULL,'0');
