INSERT INTO [b10921] VALUES (257,'وتوفي الشيخ الفقيه الصالح أبو الحسن علي بن حرزهم بمدينة فاس سنة تسع وخمسين وخمسمائة وعنه أخذ الشيخ أبو مدين رضي الله عنهما آمين.',NULL,'283',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (258,'<span data-type="title" id=toc-72>العشرة السابعة من المائة السادسة</span>
INSERT INTO [b10921] VALUES (259,'وتوفي أبو عبد الله محمد بن ظفر سنة خمس وستين وخمسمائة.
INSERT INTO [b10921] VALUES (260,'أبو محمد عبد الله بن الخشاب وفيها توفي محمد بن البروي صاحب الجدل.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (261,'وتوفي الكاتب الشهير أبو القاسم محمد بن إبراهيم بن خيرة صاحب ريحان الألباب والوشاح المفصل وغيرهما في سنة سبعين وخمسمائة.',NULL,'287',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (262,'<span data-type="title" id=toc-73>العشرة الثامنة من المائة السادسة</span>
INSERT INTO [b10921] VALUES (263,'وفي التي تليها توفي الشيخ المحدث أبو الطاهر أحمد بن محمد السلفي الأصبهاني وعمره مائة وأربع سنين وكان أجاز لكل من',NULL,'289',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (264,'أدركته حياته. وسلفة - بكسر السين المهملة - قرية بالمشرق.
INSERT INTO [b10921] VALUES (265,'وفي هذه السنة توفي أبو بكر محمد بن محمد الأبهري من أشياخ ابن زرقون.
INSERT INTO [b10921] VALUES (266,'<span data-type="title" id=toc-74>العشرة التاسعة من المائة السادسة</span>
INSERT INTO [b10921] VALUES (267,'وتوفي الشيخ الفقيه القاضي أبو محمد بن بري النحوي وعنه قيد الجزولي الجزولية في العربية. وفي هذه السنة توفي الشيخ الفقيه الخطيب القاضي المحدث أبو محمد عبد الحق بن عبد الرحمن الأزدي الأشبيلي المشهور بابن الخراط ببجاية، وله الأحكام في الحديث، والعاقبة في الوعظ والتذكير، وغير ذلك.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (268,'وتوفي الفقيه أبو عبد الله بن سعدون صاحب الجبر سنة خمس وثمانين وخمسمائة.',NULL,'294',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (269,'وفي التي تليها توفي الفقيه أبو عبد الله محمد بن سعيد بن زرقون الأندلسي صاحب الأنوار في الجمع بين المنتقى والاستذكار وهو من تلامذة القاضي عياض وكانت وفاته بإشبيلية. وفي هذه السنة توفي الفقيه الراوية أبو محمد عبد الحق بن بونه العبدري المالقي.
INSERT INTO [b10921] VALUES (270,'بيده وكان الأمير يقوم بأمر بغلته ولم يزد ثوباً على مرقعته.
INSERT INTO [b10921] VALUES (271,'<span data-type="title" id=toc-75>العشرة العاشرة من المائة السادسة</span>
INSERT INTO [b10921] VALUES (272,'أشياخ الشيخ سيدي أبي محمد عبد العزيز المهدوي وأبي البقاء عبد الله.
INSERT INTO [b10921] VALUES (273,'والنهاية وغيرهما ودفن بمراكش ثم نقل إلى مقبرة سلفه بقرطبة.
INSERT INTO [b10921] VALUES (274,'وفي سنة ثمان وتسعين وخمسمائة توفي الفقيه الصالح أبو محمد يسكر صاحب الحواشي على المدونة بمدينة فاس.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (275,'وفي سنة تسع وتسعين وخمسمائة توفي الإمام أبو الفرج عبد الرحمن بن علي بن الجوزي الواعظ وهو ابن مائة وخمس عشرة سنة وابتدأ التأليف وهو ابن عشر سنين.',NULL,'301',NULL,NULL,'0');
