INSERT INTO [b10921] VALUES (283,'<span data-type="title" id=toc-79>العشرة الثالثة من المائة السابعة</span>
INSERT INTO [b10921] VALUES (284,'وفي سنة خمس وعشرين وستمائة توفي بتلمسان الفقيه المحدث المحقق القاضي أبو عبد الله محمد بن عبد الحق التلمساني صاحب المختار في الجمع بين المنتقى والاستذكار.
INSERT INTO [b10921] VALUES (285,'وفي التي تليها توفي القاضي الراوية أبو عبد الله محمد بن علي ابن حَمادُه - بتخفيف الميم - وله النبذة المحتاجة في أخبار صنهاجة.',NULL,'311',NULL,NULL,'0');
