INSERT INTO [b10921] VALUES (295,'وتوفي تاج الدين محمد بن الحسين الأرموي مؤلف الحاصل في مختصر المحصول في سنة ثلاث وخمسين وستمائة.
INSERT INTO [b10921] VALUES (296,'وتوفي الشيخ الولي الصالح أبو الحسن علي الشاذلي بأرض الحجاز سنة ست وخمسين وستمائة.
INSERT INTO [b10921] VALUES (297,'وفي سنة ثمان وخمسين وستمائة توفي شهيداً بتونس الفقيه المحصل الكاتب المحدث الكامل أبو عبد الله محمد بن الأبار البلنسي',NULL,'324',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (298,'وعرف في بعض تواليفه بأبيه ووصفه بالعلم والخير وقال: ولا أزكيه بما ليس فيه من ذلك.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (299,'وفي سنة تسع وخمسين وستمائة توفي الفقيه الحافظ المحدث أبو عبد الله محمد بن أحمد بن سيد الناس اليعمري الإشبيلي وحدث عن أبيه',NULL,'326',NULL,NULL,'0');
INSERT INTO [b10921] VALUES (300,'عن أبي عبد الله ابن زرقون عن أبي محمد بن عتاب.
INSERT INTO [b10921] VALUES (301,'القرافي وابن دقيق العيد وغيرهما.',NULL,'328',NULL,NULL,'0');
