INSERT INTO [b11402] VALUES (99,'٢١٨- عبد الله بن الحسن، الهسنجاني، أبو محمد، الرازي (١) .
٢١٩- (دث ق) عبد الله بن الحكم ابن أبي زياد القطواني، أبو عبد الرحمن الكوفي، الدهقان ت ٢٥٥هـ (٢) .
٢٢٠- (خ مق دث س فق) عبد الله بن الزبير بن عيسى بن عبيد الله الحميدي، المكي، القرشي ت٢١٩هـ (٣) .
٢٢١- (دعس ق) عبد الله بن سالم، ويقال ابن محمد بن سالم الزبيدي، أبو محمد الكوفي، القزاز المفلوج ت٢٣٥هـ (٤) .
٢٢٢- (خ) عبد الله بن سعد بن إبراهيم بن سعد بن إبراهيم بن عبد الرحمن أخو عبيد الله الزهري ت٢٣٨هـ (٥) .
٢٢٣- (ع) عبد الله بن سعيد بن حصين، الكندي، أبو سعيد الأشج، الكوفي ت٢٥٧هـ (٦) .
٢٢٤- (س) عبد الله بن سوار بن عبد الله بن قدامة بن عنزة، العنبري، أبو السوار البصري ت٢٢٨هـ (٧) .
٢٢٥- عبد الله بن شبيب، أبو سعيد الربعي، بصري نزل مكة (٨) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/٣٤، وأجوبة أبي زرعة على أسئلة البرذعي مخطوط ورقة (١٣-أ-) .
(٢) انظر: الجرح والتعديل ج٢/ق٢/٣٨، وتهذيب التهذيب ج ٥ ص١٩٠.
