INSERT INTO [b11402] VALUES (129,'٤٨٣- (خ دت س) مسدد بن مسرهد بن مسربل البصري، الأسدي، أبو الحسن الحافظ ت ٢٢٨ هـ (١) .
٤٨٤- (ق) مسروق بن المزربان بن مسروق بن معدان الكندي الكوفي ت٢٤٠هـ (٢) .
٤٧٥- (ع) مسلم بن إبراهيم الأزدي الفراهيدي، مولاهم أبو عمرو البصري الحافظ ت ٢٢٢ هـ (٣) .
٤٨٦- المسيب بن واضح حمصي الأصل روى عن أبي إسحاق الفزاري وغيره (٤) .
٤٨٧- (د) مصرف بن عمرو بن كعب، اليامي، أبو القاسم ت ٢٤٠ هـ (٥) .
٤٨٨- (س ق) مصعب بن عبد الله بن مصعب بن ثابت بن عبد الله بن الزبير بن العوام، الأسدي، أبو عبد الله الزبيري، المدني، نزيل بغداد ت ٢٣٦ هـ (٦) .
٤٨٩- مصر بن غسان بن مضر، الأزدي، النمري، أبو عيينة (٧) .
٤٩٠- (خ ت ق) مطرف بن عبد الله بن مطرف بن سليمان الباري الهلالي، أبو مصعب المدني ت ٢٢٠ هـ (٨) .
_________
(١) انظر: الجرح والتعديل ج ٤/ ق ١/٤٣٨، وتهذيب التهذيب ج ١٠ ص ١٠٨، ويقال: إنه أول من صنف المسند بالبصرة.
(٢) انظر: الجرح والتعديل ج ٤/ ق ١/٣٩٧، وتهذيب التهذيب ج ١٠ ص ١١٢.
(٣) انظر: تهذيب التهذيب ج ١٠ ص ١٢٢.
(٤) انظر: الجرح والتعديل ج ٤/ ق ١/ ٢٩٤.
(٥) انظر: الجرح والتعديل ج ٤/ ق ١/ ٤٢٠.
(٦) انظر: الجرح والتعديل ج ٤/ ق ١/ ٣٠٩.
(٧) انظر: الجرح والتعديل ج ٤/ ق ١/ ٤٤٢.
(٨) انظر: الجرح والتعديل ج ٤/ ق ١/٣١٥، وتهذيب التهذيب ج ١٠ ص ١٧٥،
والإنتفاء ص٥٨، وميزان الاعتدال ج٤ ص١٢٤.','1','140',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (130,'٤٩١- (خ د) معاذ بن أسد المروزي، كاتب ابن المبارك، أبو عبد الله، نزل البصرة ت ٢٢٣ أو ٢٢٨ هـ (١) .
٤٩٢- (س) المعافي بن سليمان، الجزري، أبو محمد، الرسعني ت٢٣٤ هـ (٢) .
٤٩٣- (ع) معاوية بن عمر بن المهلب بن عمرو، الأزدي، المعني، أبو عمرو، البغدادي ت ٢١٤ هـ (٣) .
٤٩٤- (عب) معاوية بن عبد الله بن معاوية بن عاصم بن المنذر بن الزبير (٤) .
٤٩٥- معدان بن عبد الجبار بن محمد بن عمر بن معدان الأزدي (٥) .
٤٩٦- مغيرة بن معمر، البصري، روى عن معافى بن عمران، الموصلي (٦) .
٤٩٧- مقاتل بن محمد النصر أباذي، الرازي (٧) .
٤٩٨- مكرم بن محرز، الكعبي، الخزاعي (٨) .
٤٩٩- مليح بن وكيع بن الجراح روى عن أبيه وعن جرير بن عبد الحميد وغيرهما (٩) .
_________
(١) انظر: الجرح والتعديل ج ٤/ ق ١/ ٢٥١، وتهذيب التهذيب ج ١٠ ص ١٨٥.
(٢) انظر: الجرح والتعديل ج ٤/ ق ١/ ٤٠٠، وتهذيب التهذيب ج ١٠ ص ١٩٨.
(٣) انظر: الجرح والتعديل ج٤/ق١/٣٨٦، روى عن أبي إسحاق الفزاري في كتاب السير، وقال أبو حاتم: "كان سير أبي إسحاق الفزاري عند ثلاثة أنفس، عند معاوية بن عمرو وهو أحبهم إلي، وعند محبوب بن موسى وعند المسيب بن واضح".
(٤) انظر: الجرح والتعديل ج ٤/ ق ١/٣٨٧، وتعجيل المنفعة ص ٢٦٦.
(٥) انظر: الجرح والتعديل ج ٤/ ق ١/٤٠٤.
(٦) انظر: الجرح والتعديل ج ٤/ ق ١/ ٢٣١.
(٧) انظر: الجرح والتعديل ج ٤/ ق١/ ٣٥٥.
(٨) انظر: الجرح والتعديل ج ٤/ ق ١/٤٤٣.
(٩) انظر: الجرح والتعديل ج ٤/ ق ١/٣٦٨.','1','141',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (131,'٥٠٠- (م فق) منجاب بن الحارث بن عبد الرحمن، التميمي، أبو محمد الكوفي ت ٢٣١ هـ (١) .
٥٠١- (م دس) منصور بن أبي مزاحم بشير التركي، أبو نصر البغدادي ت ٢٣٥ هـ (٢) .
٥٠٢- مهدي بن جعفر بن حيان، الرملي، الزاهد، أبو محمد ت ٢٣٠ هـ (٣) .
٥٠٣- مهدي بن عيسى الواسطي، أبو الحسن (٤) .
٥٠٤- (ع) موسى بن إسماعيل، المنقري مولاهم أبو سلمة التبوذكي البصري ت ٢٢٣ هـ (٥) .
٥٠٥- (دس) موسى بن أيوب بن عيسى، النصيبي، أبو عمران الأنطاكي (٦) .
٥٠٦- موسى بن الحكم، أبو عمران روى عن محمد بن زياد، الراسبي (٧) .
٥٠٧- موسى بن حماد، النخمي، أبو الحسن، روكب عن شعبة (٨) .
٥٠٨- نافع بن خالد، الطاحي، البصري (٩) .
_________
(١) انظر: الجرح والتعديل ج ٤/ ق ١/٤٤٣.
(٢) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٧٠، وتهذيب التهذيب ج ١٠ ص ٣١١.
(٣) انظر: الجرح والتعديل ج ٤/ ق ١/٣٣٨، وتهذيب التهذيب ج ١٠ ص ٣٢٥، وميزان الاعتدال ج ٤ ص ١٩٤.
(٤) انظر: الجرح والتعديل ج ٤/ ق ١/٣٣٧.
(٥) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٣٦، وتهذيب التهذيب ج ١٠ ص ٣٣٤.
(٦) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٣٥، وتهذيب التهذيب ج ١٠ ص ٣٣٧.
(٧) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٤٠.
(٨) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٤٠ وفيه قال ابن أبي حاتم: "كتب عنه أبو زرعة بمكة وروى عنه".
(٩) انظر: الجرح والتعديل ج ٤/ ق ١/٤٥٧.','1','142',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (132,'٥٠٩- (ع) نصر بن علي بن نصر بن علي بن صهبان، الأزدي، الجهضمي، أبو عمرو البصري ت ٢٥٠ هـ (١) .
٥١٠- نصر بن فديد، أبو صفوان، الليثي، بصري كناني (٢) .
٥١١- (دس) نصير بن الفرج، الأسلمي، أبو حمزة الثغري ت ٢٤٥ هـ (٣) .
٥١٢- (زت س ق) هارون بن إسحاق بن محمد الهمداني، أبو القاسم الكوفي الحافظ ت ٢٥٨ هـ (٤) .
٥١٣- (م ٤) هارون بن عبد الله بن مروان البغدادي، أبو موسى البزاز الحافظ الجمال ت ٢٤٣ هـ (٥) .
٥١٤- (خ م د) هارون بن معروف المروزي، أبو علي الخزاز الضرير نزيل بغداد ت٢٣١ هـ (٦) .
٥١٥- (ق) هارون بن موسى بن حيان التميمي، أبو موسى القزويني ت ٢٤٨ هـ (٧) .
٥١٦- هاشم بن عبد الواحد، الجشاش، أبو بشر، القيسي، كوفي (٨) .
٥١٧- (خ م د) هدبة بن خالد بن الأسود القيسي، أبو خالد البصري ويقال له هداب، الثوباني ت سنة بضع وثلاثين ومائتين (٩)
_________
(١) انظر: الجرح والتعديل ج ٤/ ق ١/ ٤٧١، وتهذيب التهذيب ج١٠ ص ٤٣٠.
(٢) انظر: الجرح والتعديل ج ٤/ ق ١/٤٧٢.
(٣) انظر: تهذيب التهذيب ج ١٠ ص ٤٣٤.
(٤) انظر: الجرح والتعديل ج ٤/ ق ٢/٨٨، وتهذيب التهذيب ج ١١ ص ٣.
(٥) انظر: الجرح والتعديل ج ٤/ ق ٢/٩٢، وتهذيب التهذيب ج ١١ ص ٩.
(٦) انظر: الجرح والتعديل ج ٤/ ق ٢/٩٦، وتهذيب التهذيب ج ١١ ص ١٢.
(٧) انظر: تهذيب التهذيب ج ١١ ص ١٣، والإرشاد في معرفة علماء الحديث ج ٦، في ترجمته.
(٨) انظر: الجرح والتعديل ج ٤/ ق ٢/ ١٠٦.
(٩) انظر: الجرح والتعديل ج ٤/ ق ٢/ ١٢٤.','1','143',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (133,'٥١٨- (ق) هدية بن عبد الوهاب المروزي، أبو صالح ت ٢٤١ هـ (١) .
٥١٩- هريم بن عثمان، أبو المهلب، الطفاوي، البصري (٢) .
٥٢٠- (دق) هشام بن خالد بن يزيد بن مروان أزرق، أبو مروان ت٢٤٩ هـ (٣) .
٥٢١- (دس ق) هشام بن عبد الملك بن عمران اليزني، أبو تقى الحمصي ت٢٥١ هـ (٤) .
٥٢٢- (ع) هشام بن عبد الملك الباهلي مولاهم، أبو الوليد الطيالسي البصري الحافظ الإمام ت ٢٢٧ هـ (٥) .
٥٢٣- (خ ٤) هشام بن عماربن نصير بن ميسرة بن أبان السلمي، أبو الوليد الدمشقي ت ٢٤٥ هـ (٦) .
٥٢٤- (عخ م د) هناد بن السري بن مصعب بن أبي بكر التميمي، الدارمي، الكوفي ت ٢٤٣ هـ (٧) .
٥٢٥- (خ س ق) الهيثم بن خارجة الخراساني الحافظ، أبو أحمد المروزي، البغدادي ت ٢٢٧ هـ (٨) .
٥٢٦- واصل بن عبد الله بن بدر بن واصل الجهني، أبو الحسن (٩) .
_________
(١) انظر: الجرح والتعديل ج ٤/ ق ٢/ ١٢٤، وتهذيب التهذيب ج ١١ ص ٢٥.
(٢) انظر: الجرح والتعديل ج ٤/ ق ٢/١١٨.
(٣) انظر: تهذيب التهذيب ج ١١ ص ٣٨.
(٤) انظر: الجرح والتعديل ج ٤/ ق ٢/٦٦، وتهذيب التهذيب ج ١ ص ٤٥.
(٥) انظر: الجرح والتعديل ج ٤/ ق ٢/٦٥، وتهذيب التهذيب ج١١ ص ٤٦.
(٦) انظر: الجرح والتعديل ج ٤/ ق ٢/٦٦، وتهذيب التهذيب ج ١١ ص ٥١.
(٧) انظر: الجرح والتعديل ج ٤/ ق ٢/١١٩، وتهذيب التهذيب ج ١١ ص٧١، وتذكرة الحفاظ ص ٥٠٧.
(٨) انظر: الجرح والتعديل ج ٤/ ق ٢/٨٦، وتهذيب التهذيب ج ١١ ص ٩٤، والإرشاد ج ٥، في ترجمته، وتذكرة الحفاظ ص ٤٦٩.
(٩) الجرح والتعديل ج ٤/ ق ٢/٣١، قال أبو زرعة عنه: "صدوق لم أكتب عنه إلا حديثاً واحداً".','1','144',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (134,'٥٢٧- (م ٤) واصل بن عبد الأعلى بن هلال، الأسدي، أبو القاسم، ويقال أبو محمد الكوفي ت ٢٤٤ هـ (١) .
٥٢٨- الوليد بن عبد الملك بن عبيد الله بن مسرح الحراني، أبو وهب (٢) .
٥٢٩- (د) الوليد بن عتبة، الأشجعي، أبو العباس الدمشقي، المقرىء ت٢٤٠هـ (٣) .
٥٣٠- (دس) الوليد بن مزيد، العذري، أبو العباس، البيروتي ت ٢٨٣ هـ (٤) .
٥٣١- (م دس) وهب بن بقية بن عثمان بن شابور، الواسطي، أبو محمد، وهبان ت ٢٣٩ هـ (٥) .
٥٣٢- وهب الفامي عن محمد بن خالد بن عبد الله الواسطي (٦) .
٥٣٣- (عخ م د عس) يحيى بن أيوب المقابري، أبو زكرياء البغدادي ت٢٣٤ هـ (٧) .
٥٣٤- يحيى بن أبي الخصيب زياد، الرازي قاضي عكبرا (٨) .
٥٣٥- (خ ت) يحيى بن سليمان بن يحيى بن سعيد، الجعفي، أبو سعيد الكوفي المقرىء ت ٢٣٧ أو ٢٣٨ هـ (٩) .
_________
(١) انظر: الجرح والتعديل ج ٤/ ق ٢/٣٢، وتهذيب التهذيب ج ١١ ص ١٠٤.
(٢) انظر: الجرح والتعديل ج ٤/ ق ٢/ ١٠.
(٣) انظر: الجرح والتعديل ج ٤/ ق ٢/١٢.
(٤) انظر: الإرشاد في معرفة علماء الحديث في ترجمته.
(٥) انظر: الجرح والتعديل ج ٤/ ق ٢/٢٨، وتهذيب التهذيب ج ١١ ص ١٥٩.
