INSERT INTO [b11402] VALUES (794,'عنه: "صدوق كوفي إلا أنه كان في رأيه غلو" (١) .
٤٢- (بخ م دس) إسماعيل بن سالم الأسدي أبو يحيى الكوفي، قال عنه: "ثقة" (٢) .
٤٣- (س) إسماعيل بن عبد الله بن أبي طلحة زيد بن سهل الأنصاري، قال عنه: "ثقة" (٣) .
٤٤- (س) إسماعيل بن عبد الرحمن بن ذؤيب، وقيل ابن أبي ذؤيب الأسدي، قال عنه: "مدني ثقة" (٤) .
٤٥- (خ ت عس) إسماعيل بن مجالد بن سعيد بن عمير ذي مران، الكوفي الهمداني، أبو عمر قال عنه: "ليس ممن يكذب بمرة هو وسط" (٥) .
٤٦- (م ت س) إسماعيل بن مسلم العبدي، أبو محمد، البصري قاضي قيس (٦) ، قال عنه: "ثقة وليس هو بالمكي" (٧) .
٤٧- إسماعيل بن مسلم المخزومي مولاهم المكي، قال عنه: "المخزومي، لم يلق الحسن لا بأس به" (٨) .
_________
(١) انظر: الجرح والتعديل ج١/ق١/١٦٧، وتهذيب التهذيب ج١/٢٩٣، والترغيب والترهيب ج ٤/ ٥٨١، وميزان الاعتدال ج ٤/ ٤٩٠.
(٢) انظر: الجرح والتعديل ج١/ق١/١٧٢، وتهذيب التهذيب ج١/٣٠٢.
(٣) انظر: الجرح والتعديل ج١/ق١/١٧٩، وتهذيب التهذيب ج١/٣١٠.
(٤) انظر: الجرح والتعديل ج١/ق١/١٨٣ وتهذيب التهذيب ج١/٣١٢.
(٥) انظر: الجرح والتعديل ج١/ق١/٢٠٠، وتهذيب التهذيب ج١/٣٢٨، واكتفى في ميزان الاعتدال ج١/٢٤٦، بقوله: (هو وسط) .
(٦) قيس: مدينة بالبطائح غرقها الماء كان إسماعيل بن مسلم قاضيها يقال له الهوني كذا قال أبو داود في أجوبته على أسئلة الآجري ونقله عنه ابن نقطة. انظر: الجرح والتعديل ج١/ق١/١٩٦.
(٧) انظر: الجرح والتعديل ج١/ق١/١٩٧، وتهذيب التهذيب ج١/٣٣١.
(٨) انظر: الجرح والتعديل ج١/ق١/١٩٨، وتهذيب التهذيب ج١/٣٣٣.','3','846',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (795,'٤٨- (م س) الأسود بن العلاء بن جارية الثقفي، قال عنه: "شيخ ليس بالمشهور" (١) .
٤٩- (ى ٤) إسماعيل بن عياش الحمص أبو عتبة، العنسي ت ١٨١ أو ١٨٢- هـ، قال عنه: "صدوق إلا أنه غلط في حديث الحجازيين، والعراقيين" (٢) .
٥٠- إسماعيل بن نشيط العامري، قال عنه: "هو صدوق حدثنا عنه أبو نعيم" (٣) .
٥١- إسماعيل بن يحيى بن كيسان الرازي رفيق أبي مسعود أحمد بن الفرات، قال ابن أبي حاتم: سئل أبو زرعة عنه؟ فأثنى عليه، ونسبه إلى الصدق (٤) .
٥٢- (خت ٤) أشعث بن عبد الملك الحمراني، أبو هانئ البصري مولى حمران ت ١٤٢ هـ، قال عنه: "صالح" (٥) .
٥٣- أشعث بن عطاف الأسدي، أبو النضر، قال عنه: "كوفي، كان ههنا بالري، وكان شيخاً صالحاً" (٦) .
٥٤- (ل ت س ق) أصبغ بن زيد بن علي الجهني مولاهم أبو عبد الله الواسطي الوراق ت ١٥٧ هـ، قال عنه: "شيخ" (٧) .
٥٥- (قد) أهيّ بن ربيعة المرادي الصيرفي، أبو عبد الرحمن الكوفي قال ابن
_________
(١) انظر: الجرح والتعديل ج١/ق١/٢٩٣، وتهذيب التهذيب ج١/٣٤١.
(٢) انظر: الجرح والتعديل ج١/ق١/١٩٢.
(٣) انظر: الجرح والتعديل ج١/ق١/٢٠٢.
(٤) انظر: الجرح والتعديل ج١/ق١/٢٠٤.
(٥) انظر: الجرح والتعديل ج١/ق١/٢٧٥، وتهذيب التهذيب ج١/٣٥٨.
(٦) انظر: الجرح والتعديل ج١/ق١/٢٧٦.
(٧) انظر: الجرح والتعديل ج١/ق١/٣٢١، وتهذيب التهذيب ج١/٣٦١.','3','847',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (796,'أبي حاتم: "سألت أبا زرعة عن أمي بن ربيعة، عن طاوس أحب إليك أو شعيب السمان عن طاووس؟ قال: أمي أشهر" (١) .
٥٦- (م دت س) أمية بن خالد بن الأسود بن هدية، وقيل ابن خالد بن هدية بن عتبة الأسدي الأزدي الثوباني أبو عبد الله البصري ت ٢٥١ هـ، قال عنه: "ثقة" (٢) .
٥٧- (ع) أنس بن عياض بن ضمرة وقيل جعدبة، وقيل عبد الرحمن أبوضمرة الليثي المدني، ت ٢٠٠ هـ، قال عنه: "لا بأس به" (٣) .
٥٨- أنس بن سليم الهجيمي، قال عنه: "بصري ثقة" (٤) .
٥٩- (ع) أوس بن عبد الله الربعي، أبو الجوزاء البصري ت ٨٣ هـ روى عن عائشة وابن عباس وعبد الله بن عمرو، قال عنه: "بصري ثقة" (٥) .
٦٠- (د) إياس بن دغفل الحارثي أبو دغفل، قال عنه: "ثقة" (٦) .
٦١- (خ صد) أيمن الحبشي المكي والد عبد الواحد بن أيمن مولى ابن أبي عمرو المخزومي، وقيل مولى ابن أبي عمرة، قال عنه: "مكي ثقة" (٧) .
٦٢- (ع) أيوب بن موسى بن عمرو بن سعيد بن العاص بن سعيد ابن العاص بن أمية، أبو موسى المكي، ت ١٣٢ هـ، قال عنه: "ثقة" (٨) .
_________
(١) انظر: الجرح والتعديل ج١/ق١/٣٤٧، وتهذيب التهذيب ج١/٣٧٠.
(٢) انظر: الجرح والتعديل ج١/ق١/٣٠٣، وتهذيب التهذيب ج١/٣٧١، وتذكرة الحفاظ ص ٣٢٤.
(٣) انظر: الجرح والتعديل ج١/ق١/٣٨٩، وتهذيب التهذيب ج١/٣٧٥.
(٤) انظر: الجرح والتعديل ج١/ق١/٢٨٨.
(٥) انظر: الجرح والتعديل ج١/ق١/٣٠٥.
(٦) انظر: الجرح والتعديل ج١/ق١/٢٧٨، وتهذيب التهذيب ج١/٣٨٨.
(٧) انظر: الجرح والتعديل ج١/ق١/٣١٨، وتهذيب التهذيب ج١/٣٩٤، وقال الذهبي في ميزان الاعتدال ج١/٣٨٤: (وثقه أبو زرعة) .
(٨) انظر: الجرح والتعديل ج١/ق١/٣٥٨، وتهذيب التهذيب ج١/٤١٢.','3','848',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (797,'٦٣- (خ م س) أيوب بن النجار بن زياد بن النجار، الحنفي، أبو إسماعيل اليمامي، قال عنه: "ثقة" (١) .
٦٤- (خ ت دس) بَجالة بن عبدة، التميمي، العنبري، البصري، كاتب جزىء ابن معاوية عم الأحنف، قال عنه: "مكي ثقة" (٢) .
٦٥- (خ ٤) بدل بن المحبر بن المنبه، التميمي، اليربوعي، أبو المنير البصري ت ٢١٥ هـ، قال عنه: "ثقة" (٣) .
٦٦- (بخ ٤) برد بن سنان الشامي، أبو العلاء، الدمشقي مولى قريش ت ١٣٥ هـ، قال عنه: "لا بأس به بصري" (٤) . وقال أيضاً: "كان صدوقاً في الحديث" (٥) .
٦٧- (دق) بركة المجاشعي أبو الوليد البصري، قال عنه: "بصري ثقة" (٦) .
٦٨- (بخ ٤) بريد بن أبي مريم مالك بن ربيعة السلولي البصري ت ١٤٤ هـ، قال عنه: "ثقة" (٧) .
٦٩- (بخ ل س ق) بسطام بن مسلم بن نمير العوذي البصري، قال عنه: "بصري عوذي ثقة" (٨) .
٧٠- (خ دس ق) بشر بن بكر التنيسي أبو عبد الله البجلي دمشقي الأصل ت ٢٥٥ هـ أو ٢٠٥هـ، قال عنه: "ثقة" (٩) .
_________
(١) انظر: الجرح والتعديل ج١/ق١/٢٦٠، وتهذيب التهذيب ج١/٤١٤.
(٢) انظر: الجرح والتعديل ج١/ق١/٣٧، وتهذيب التهذيب ج١/٤١٧.
(٣) انظر: الجرح والتعديل ج١/ق١/٤٣٩، وتهذيب التهذيب ج١/٤٢٤، وميزان الاعتدال ج١/٣٠٠.
(٤) انظر: الجرح والتعديل ج١/ق١/٤٢٢، وتهذيب التهذيب ج١/٤٢٩، وميزان الاعتدال ج١/٣٠٢.
(٥) انظر: تهذيب التهذيب ج١/٤٢.
(٦) انظر: الجرح والتعديل ج١/ق١/٤٣٢، وتهذيب التهذيب ج١/٤٣٠.
