INSERT INTO [b11402] VALUES (814,'١٨٥- (د) داود بن عمرو الأودي الدمشقي عامل واسط، قال عنه: "لا بأس به" (١) .
١٨٦- (خت م ٤) داود بن قيس، الغراء، الدباغ، أبو سليمان القرشي مولاهم المدني، قال عنه: "ثقة" (٢) .
١٨٧- دينار، أبو عمر البصري روى عن الحسن وروى عنه وكيع، قال عنه: "صدوق" (٣) .
١٨٨- (ع) ذكوان، أبو صالح السمان الزيات المدني مولى جويرية
بنت الأحمس الغطفاني، ت١٠١ هـ، قال عنه: "ثقة مستقيم الحديث" (٤) .
١٨٩- (خ م دس) ذكوان، أبو عمرو المدني مولى عائشة، ت ٦٣ هـ، قال عنه: "ثقة" (٥) .
١٩٠- (بخ م ت س) رباح بن أبي معروف بن أبي سارة المكي، قال عنه: "صالح الحديث" (٦) .
١٩١- (دتم ق) ربيع بن عبد الرحمن بن أبي سعيد الخدري المدني أخو سعيد قال عنه: "شيخ" (٧) .
_________
(١) انظر: الجرح والتعديل ج١/ق٢/٤٢٠، وتهذيب التهذيب ج٣/١٩٦، وميزان الاعتدال ج٢/١٨.
(٢) انظر: الجرح والتعديل ج١/ق٢/٤٢٣، وتهذيب التهذيب ج٣/١٩٨.
(٣) انظر: الجرح والتعديل ج١/ق ٢/ ٤٣٤.
(٤) انظر: الجرح والتعديل ج١/ق٢/٤٥١، وتهذيب التهذيب ج٣/٢١٩.
(٥) انظر: الجرح والتعديل ج١/ق٢/٤٥١، وتهذيب التهذيب ج٣/٢٢٠.
(٦) انظر: أسماء الضعفاء لابن الجوزي فيمن اسمه رباح والذي نقله ابن أبي حاتم في الجرح والتعديل ج١/ق٢/٤٨٩، قوله: (صالح) انظر كذلك تهذيب التهذيب ج٣/٢٣٥، وميزان الاعتدال ج٢/٣٨.
(٧) انظر: الجرح والتعديل ج١/ق٢/٥١٩، وتهذيب التهذب ج٣/٦٣٨، والترغيب والترهيب ج٤/٥٧٠.','3','866',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (815,'١٩٢- (خت ت ق) ربيع بن صبيح، السعدي، أبو بكر، ويقال أبو حفص البصري مولى بني سعد بن زيد مناة، ت ١٦٠ هـ، قال عنه: "شيخ صالح صدوق" (١) .
١٩٣- (م س ق) ربيعة بن عثمان بن ربيعة بن عبد الله بن الهدير
التيمي، أبو عثمان المدني، ت ١٥٤ هـ، قال عنه: "إلى الصدق ما هو، وليس بذاك القوى" (٢) .
١٩٤- (ق) رزيق، أبو عبد الله الألهاني، الحمصي، قال عنه: "لا بأس به" (٣) .
١٩٥- (ع) رفيع بن مهران أبو العالية الرياحي مولاهم البصري، ت ٩٣ هـ، قال عنه: "ثقة" (٤) .
١٩٦- (عس) رفاعة بن إياس بن نذير، الضبي الكوفي، ت بعد سنة ١٨٥ هـ، قال عنه: "شيخ" (٥) .
١٩٧- (خ م دت س) روح بن القاسم التميمي العنبري أبو غياث، ت ١٤١ هـ، قال عنه: "ثقة" (٦) .
١٩٨- (خد) رياح بن عبيدة الباهلي، مولاهم بصري، قال عنه: "ثقة" (٧) .
_________
(١) انظر: الجرح والتعديل ج١/ق٢/٤٦٥، وتهذيب التهذيب ج٣/٢٤٨.
(٢) انظر: الجرح والتعديل ج١/ق٢/٤٧٧، وتهذيب التهذيب ج٣/٢٦٠، وفي ميزان الاعتدال ج٢/٤٤، اكتفى بقوله: "ليس بذاك القوي".
(٣) انظر: الجرح والتعديل ج١/ق٢/٥٠٥، وتهذيب التهذيب ج٣/٢٧٥، وميزان الاعتدال ج٢/٤٨.
(٤) انظر: الجرح والعديل ج١/ق ٢/ ٥١٠، وتهذيب التهذيب ج٣/٢٨٤، وميزان الاعتدال ج٤/٥٤٣.
(٥) انظر: الجرح والتعديل ج١/ق٢/ ٤٩٤، وتهذيب التهذيب ج٣/ ٢٨٠.
(٦) انظر: الجرح والتعديل ج١/ق٢/٤٩٥، وتهذيب التهذيب ج٣/٢٩٨.
(٧) انظر: الجرح والتعديل ج١/ق ٢/٥١١، وتهذيب التهذيب ج٣/٣٠٠.','3','867',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (816,'١٩٩- رياح بن عمرو القيسي، أبو المهاجر، الزاهد، الكوفي قال عنه: "صدوق" (١) .
٢٠٠- زاجر بن الصلت، الطاحي من اليمن قال عنه: "لا بأس به" (٢) .
٢٠١- (خ) زائدة بن قدامة الثقفي، أبو الصلت الكوفي، ت ٧٦ هـ، قال عنه: "صدوق من أهل العلم" (٣) .
٢٠٢- (دت ق) الزبيربن سعيد بن سليمان بن سعيد بن نوفل بن الحارث بن عبد المطلب بن هاشم الهاشمي، أبو القاسم، ويقال أبو هاشم المديني نزل المدائن، ت بضع وخمسين ومائة، قال عنه: "شيخ" (٤) .
٢٠٣- (ع) زكرياء بن إسحاق المكي، قال عنه: "لا بأس به" (٥) .
٢٠٤- (ع) زكرياء بن أبي زائدة خالد بن ميمون بن فيروز، وقال بحشل اسم أبي زائدة هبيرة الهمداني الوادعي مولاهم، أبويحيى الكوفي، ت ١٤٩ هـ، أوقبلها قال عنه: "صويلح، يدلس كثيراً عن الشعبي" (٦) .
٢٠٥- (بخ دس ق) زكرياء بن يحيى بن عمارة الأنصاري، أبو يحيى الذراع البصري، ت ١٨٩ أو ١٨٧ هـ، قال ابن أبي حاتم: سئل أبو زرعة عنه "فحسن القول فيه" (٧) .
_________
(١) انظر: الجرح والتعديل ج١/ق ٢/ ٥١٢، وميزان الاعتدال ج٢/٦٢.
(٢) انظر: الجرح والتعديل ج١/ق ٢/ ٦٢١.
(٣) انظر: تهذيب التهذيب ج٣/٣٠٦.
(٤) انظر: تهذيب التهذيب ج٣/٥ ٣١، وميزان الاعتدال ج٢/٦٧.
(٥) انظر: الجرح والتعديل ج١/ق٢/٥٩٣، وتهذيب التهذيب ج٣/٣٢٨.
(٦) انظر: الجرح والتعديل ج١/ق٢/٥٩٤، وتهذيب التهذيب ج٣/٣٣٠، وميزان الاعتدال ج٢/٧٣.
(٧) انظر: الجرح والتعديل ج١/ق٢/٦٠١، وتهذيب التهذيب ج٣/٢٣٧، وميزان الاعتدال ج٢/٧٥.','3','868',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (817,'٢٠٦- زهير بن أبي ثابت، وهو ابن حبيب العبسي، ويقال الأسدي أبو الأزهر الأعمى، قال عنه: "لا بأس به" (١) .
٢٠٧- (ع) زهير بن معاوية بن حديج بن الرحيل بن زهير بن خيثمة الجعفي، أبو خيثمة الكوفي، ت ١٧٢ أو ١٧٣ أو ١٧٧ هـ، قال عنه: "ثقة إلا أنه سمع من أبي إسحاق بعد الاختلاط" (٢) .
٢٠٨- (ع) زياد بن جبير بن حبة بن مسعود بن معتب الثقفي البصري، قال عنه: "ثقة" (٣) .
٢٠٩- (خ دس) زياد بن حسان بن قرة الباهلي البصري نسيب ابن عون وهو زياد الأعلم، قال عنه: "شيخ" (٤) .
٢١٠- (م ٤) زياد بن خيثمة الجعفي الكوفي، قال عنه: "ثقة" (٥) .
٢١١- (ع) زياد بن سعد بن عبد الرحمن الخراساني، أبو عبد الرحمن قال عنه: "ثقة" (٦) .
٢١٢- (خ م ت ق) زياد بن عبد الله بن فياض الخزاير، أبو الحسن الكوفي، ت ١٢٩ هـ، قال عنه: "شيخ" (٧) .
٢١٣- (م دس) زياد بن فياض الخزاعي، أبو الحسن الكوفي، ت ١٢٩ هـ، قال عنه: "شيخ" (٨) .
٢١٤- (مد) زياد بن أبي مسلم، ويقال ابن مسلم أبو عمر الغراء، ويقال الصغار البصري، قال عنه: "لا بأس به" (٩) .
_________
(١) انظر: الجرح والتعديل ج١/ق٢/٥٨٧.
(٢) انظر: الجرح والتعديل ج١/ق٢/٥٨٩، وتهذيب التهذيب ج٣/٣٥٢، وميزان الاعتدال ج٢/٨٦.
(٣) انظر: الجرح والتعديل ج١/ق٢/٥٢٧ وتهذيب التهذيب ج٣/٣٥٨.
(٤) انظر: الجرح والتعديل ج١/ق٢/٥٥٢ وتهذيب التهذيب ج ٣/ ٣٦٢.
(٥) انظر: الجرح والتعديل ج١/ق ٢/٥٣٠ وتهذيب التهذيب ج٣/٣٦٤.
(٦) انظر: الجرح والتعديل ج١/ق٢/٥٣٤، وتهذيب التهذيب ج٣/٠ ٣٧.
(٧) انظر: الجرح والتعديل ج١/ق٢/٥٣٨، وتهذيب التهذيب ج٣/٣٧٦، وأسماء الضعفاء لابن الجوزي فيمن اسمه زياد وميزان الاعتدال ج٢/٩١.
(٨) انظر: تهذيب التهذيب ج٣/٣٨١.
(٩) انظر: الجرح والتعديل ج١/ق٢/٥٤٧، وتهذيب التهذيب ج٣/ ٣٨٥.','3','869',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (818,'٢١٥- (خ م س) زياد بن فيروز، أبو العالية البراء، بالتشديد، البصري ويقال ان اسم أبي العالية البراء، وقيل كلثوم، وقيل أدينة، وقيل ابن أدينة، ت ٩٠ هـ، سمي البراء، لأنه كان يبري النبل، قال عنه: "بصري ثقة" (١) .
٢١٦- (دس) زياد أبو يحيى المكي ويقال الكوفي، الأعرج مولى قيس بن مخرمة، ويقال مولى الأنصار، قال عنه: "أبو يحيى زياد مولى ابن عفراء ثقة" (٢) .
٢١٧- (ع) زيد بن أسلم العدوي، أبو أسامة، ويقال أبو عبد الله المدني الفقيه مولى عمر، ت ١٣٦ هـ، قال عنه: "ثقة" (٣) .
٢١٨- زيد بن بشر الحضرمي، المصري، أبو بشر، قال عنه: "ثقة رجل صالح، عاقل، خرج إلى المغرب فمات هناك" (٤) .
٢١٩- (٤) سالم بن أبي الجعد رافع الأشجعي، مولاهم الكوفي، ت ١٠٠ أو١٠١ هـ، قال عنه: "ثقة" (٥) .
٢٢٠- (بخ م دت س) سالم بن أبي عطاء البصري، الجزري، أبو سعد العطار، ت ٢٠٠ هـ، قال عنه: "لا بأس به، صدوق، ثقة" (٦) .
٢٢١- (بخ س) السري بن يحيى بن إياس بن حرملة بن إياس الشيباني، أبو الهيثم، ويقال أبو يحيى البصري، ت ١٦٧ هـ، قال عنه "من الثقات" (٧) .
_________
(١) انظر: الجرح والتعديل ج١/ق٢/٥٤٢، وتهذيب التهذيب ج٢/١٤٣.
(٢) انظر: الجرح والتعديل ج١/ق٢/٠ ٥٥، وتهذيب التهذيب ج٣/ ٣٩٢.
(٣) انظر: الجرح والتعديل ج١/ق٢/ ٥٥٥، وتهذيب التهذيب ج٣/٣٩٧.
(٤) انظر: الجرح والتعديل ج١/ق٢/٥٥٧.
(٥) انظر: الجرح والتعديل ج ٢/ق١/١٨١، وتهذيب التهذيب ج٣/ ٤٣٢.
(٦) انظر: الجرح والتعديل ج٢/ق١/١٨٨، وتهذيب التهذيب ج٣/٤٤٣، وفي ميزان الاعتدال ج٢/١١٣، اكتفى بقوله: "صدوق ثقة".
(٧) انظر: الجرح والتعديل ج ٢/ق١/٢٨٤، وتهذيب التهذيب ج٣/٤٦١، وفي ميزان الاعتدال ج٢/١١٨، قال: (وثقه أبو زرعة) .','3','870',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (819,'٢٢٢- سري بن عبد الله البصري قال عنه: "لا بأس به" (١) .
٢٢٣- سعد بن طالب، أبو غيلان، الشيباني قال عنه: " لا بأس به" (٢) .
٢٢٤- (٤) سعيد بن بشير الأزدي، ويقال البصري مولاهم أبو عبد الرحمن ويقال أبو سلمة الشامي، أصله من البصرة ويقال من واسط، ت ١٦٨ هـ، قال ابن أبي حاتم: أسمعت أبي وأبا زرعة يقولان محله الصدق عندنا، قلت لهما: يحتج بحديثه؟ قالا: يحتج بحديث ابن أبي عروبة والدستوائي هذا شيخ يكتب حديثه" (٣) .
٢٢٥- سعيد بن بزيع روى عن محمد بن إسحاق، وعنه عبد الرحيم بن مطرف، قال عنه: "صدوق" (٤) .
٢٢٦- (ع) سعيد بن جبيربن هشام الأسدي الوالبي مولاهم، أبو محمد، ويقال أبو عبد الله الكوفي الفقيه قتل بين يدي الحجاج، سنة ٩٥ هـ، قال عنه: "ثقة" (٥) .
٢٢٧- (ع) سعيد بن أبي الحسن، واسمه يسار الأنصاري مولاهم البصري، ت ١٠٨ هـ، قال عنه: "ثقة" (٦) .
٢٢٨- (م تم س) سعيد بن الحويرث، ويقال ابن أبي الحويرث، المكي مولى السائب، قال عنه: "ثقة" (٧) .
٢٢٩- (ت س) سعيد بن خيثم بن رشد، الهلالي، أبو معمر الكوفي وقيل أنه من بني سليط قال عنه: "لا بأس به" (٨) .
_________
(١) انظر: الجرح والتعديل ج ٢/ق ١/ ٢٨٤.
(٢) انظر: الجرح والتعديل ج ٢/ق١/٨٨، وميزان الاعتدال ج٢/٢٢.
(٣) انظر: الجرح والتعديل ج٢/ق١/٧، وتهذيب التهذيب ج ٤/١٠.
(٤) انظر: الجرح والتعديل ج ٢/ق١/٨٠.
(٥) انظر: الجرح والتعديل ج٢/ق١/ ١٠.
(٦) انظر: الجرح والتعديل ج ٢/ق١/٧٣، وتهذيب التهذيب ج٤/١٦.
(٧) انظر: الجرح والتعديل ج٢/ق١/١١، وتهذيب التهذيب ج٤/١٩.
(٨) انظر: الجرح والتعديل ج٢/ق١/٧ ١، وتهذيب التهذيب ج٤/٢٣.','3','871',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (820,'٢٣٠- (خت م دت ق) سعيد بن زيد بن درهم الأزدي الجهضي أبو الحسن البصري، قال أبو زرعة: "سمعت سليمان بن حرب يقول: ثنا سعيد بن زيد وكان ثقة" (١) .
٢٣١- (دس) سعيد بن سالم القداح أبو عثمان المكي، خراساني الأصل، ويقال كوفي سكن مكة، قال عنه: "هو عندي إلى الصدق ما هو" (٢) .
٢٣٢- (أ) سعيد بن شقي، كوفي، همداني، قال عنه: "ثقة" (٣) .
٢٣٣- سعيد بن صالح القزويني، قال عنه: (سمعت يحيى بن معين يذكر سعيد بن صالح هذا بخير وعرفه (٤) "، قال أيضاً: "هو شيخ لنا رازي سكن قزوين، وكان يتفقه، وكان صحيح الكتب صدوقاً في الحديث كتبت عنه بالري" (٥) .
٢٣٤- سعيد بن عبد الله وهو ابن مرجانه، القريشي، ومرجانه أمه، يقال مولى بني عامر بن لؤي، قال عنه: "ثقة" (٦) .
٢٣٥- (د) سعيد بن عبد الرحمن بن يزيد بن رقيشي بن رباب الأسدي، المدني من حلفاء بني شمس، قال عنه: "شيخ، مدني، ثقة" (٧) .
٢٣٦- (خ ت س ق) سعيد بن عبيد الله بن جبير بن حية الثقفي الجبيري، البصري، قال عنه: "ثقة" (٨) .
٢٣٧- (ع) سعيد بن أبي عروبة، واسمه مهران العدوي مولى بني عدي بن
_________
(١) انظر: الجرح والتعديل ج ٢/ق١/١ ٢-٢٢، تهذيب التهذيب ج٤/٣٣.
(٢) انظر: الجرح والتعديل ج٢/ق١/٣١، وتهذيب التهذيب ج٤/٣٥، وميزان الاعتدال ج٢/ ١٣٩.
(٣) انظر: الجرح والتعديل ج٢/ق١/٣٣، وتعجيل المنفعة ص ١٠٤.
(٤) انظر: الجرح والتعديل ج٢/ق١/٣٥، وتاريخ قزوين في ترجمته ورقة (٢١٣-ب) .
(٥) انظر: الجرح والتعديل ج ٢/ق ١/٣٥، وتاريخ قزوين في ترجمته ورقة (٢١٣- ب) .
(٦) انظر: الجرح والتعديل ج٢/ق١/٣٦.
(٧) انظر: الجرح والتعديل ج٢/ق١/٣٩، وتهذيب التهذيب ج٤/٥٨.
(٨) انظر: الجرح والتعديل ج٢/ق١/٣٩، وتهذيب التهذيب ج٤/٦١.','3','872',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (821,'يشكر أبو النضر البصري، ت ١٥٥هـ، قَال عنه: "ثقة مأمون" (١) وقَال ابن أبي حاتم: "قلت لأبي زرعة: سعيد بن أبي عروبة أحفظ أو أبان العطار؟ فقال: سعيد أحفظ، وأثبت أصحاب قتادة هشام وسعيد" (٢) .
٢٣٨- (خ م دس ق) سعيد بن عمرو بن سعيد بن العاص بن أمية أبو عثمان ويقال عنبسة الأموي، قَال عنه: "ثقة" (٣) .
٢٣٩- (م س) سعيد بن عمرو بن سهل بن إسحاق بن محمد بن الأشعث بن قيس الكندي أبو عثمان، ت ٢٣٠هـ، قَال عنه "ثقة" (٤) .
٢٤٠- (ع) سعيد بن فيروز وهو ابن أبي عمران أبو البَختري الطائي مولاهم الكوفي، ت٣٨هـ، قال عنه: "ثقة" (٥) .
٢٤١- (ق) سعيد بن أبي كريب الهمداني، قَال عنه: "ثقة" (٦) .
٢٤٢- (ع) سعيد بن أبي سعيد، واسمه كيسان المقبري، أبو سعد المدني، ت ١١٧ أو ١٢٣هـ، قَال عنه: "ثقة" (٧) .
٢٤٣- (خ م د ق) سعيد بن سعيد الجرمي أبو محمد، وقيل أبو عبيد الله الكوفي، قَال عنه: سألت ابن نمير وابن أبي شيبة عنه؟ "فأثنيا عليه، وذاكرت أحمد بن حنبل بأحاديث عنه فعرفه وأثنى
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٦٦؛ وتهذيب التهذيب ج٤/٦٣.
(٢) انظر: الجرح والتعديل ج٢/ق١/٦٦؛ وتهذيب التهذيب ج٤/٦٤.
(٣) انظر: الجرح والتعديل ج٢/ق١/٤٩؛ وتهذيب التهذيب ج٤/٦٨.
(٤) انظر: الجرح والتعديل ج٢/ق١/٥١؛ وتهذيب التهذيب ج٤/٦٩.
(٥) انظر: الجرح والتعديل ج٢/ق١/٥٥؛ وتهذيب التهذيب ج٤/٧٣.
(٦) انظر: الجرح والتعديل ج٢/ق١/٥٧؛ وتهذيب التهذيب ج٤/٧٥، وقال في تقريب التهذيب ج١/٣٠٤ "وثقه أبو زرعة" وكذا في ميزان الاعتدال ج٢/١٥٦.
(٧) انظر: الجرح والتعديل ج٢/ق١/٥٧؛ وتهذيب التهذيب ج٤/٣٨؛ وميزان الاعتدال ج٢/١٣٩.','3','873',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (822,'عليه" (١) ، وقال أيضاً "سألت أحمد بن حنبل عنه؟ فقال: ثقة (٢) كان يطلب معنا الحديث" (٣) .
٢٤٤- (ع) سعيد بن المسيب بن حزن بن أبي وهب بن عمرو بن عائذ بن عمران بن مخزوم القرشي المخزومي، ت ١٠٠ هـ قال عنه: "مدني قرشي، ثقة إمام" (٤) .
٢٤٥- (بخ ت ق) سعيد المزربان العبسي مولاهم، أبو سعد البقال الكوفي الأعور مولى حذيفة، ت بعد ١٤٠هـ، قال عنه: "صدوق، مدلس" (٥) ، وسئل أبو زرعة عنه؟ فقال: "لين الحديث مدلس، قيل: هو صدوق؟ قال: نعم كان لا يكذب" (٦) .
٢٤٦- (خ م د س ق) سعيد بن مينا مولى البختري بن أبي ذباب الحجازي، مكي أو مدني، يكنى أبا الوليد قال عنه: "ثقة" (٧) .
٢٤٧- (خ ت) سعيد بن يحيى بن مهدي بن عبد الرحمن، أبو سفيان الحميري الحذاء الواسطي، ت ٢٠٢ هـ، قال عنه: "صدوق" (٨) .
٢٤٨ - (م د ت س) سعيد بن يزيد الحميري، القتباني، أبو شجاع الإسكندراني، ت ١٥٤ هـ، قال عنه: "وثقة" (٩) .
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٥٩؛ وتهذيب التهذيب ج٤/٧٦- ٧٧.
(٢) انظر: وفي حاشية ج٢/ ق١ /٥٩ من الجرح والتعديل قال المحقق: "هكذا في الأصلين ووقع في التهذيب بدل هذه الكلمة "صدوق" وانظر: تهذيب التهذيب ج٤/ ٧٧.
(٣) انظر: الجرح والتعديل ج٢/ق١/٥٩؛ وتهذيب التهذيب ج٤/٧٧
(٤) انظر: الجرح والتعديل ج٢/ق١/٦١؛ وتهذيب التهذيب ج٤/٨٦
(٥) انظر: الترغيب والترهيب ج٤ /٥٧١، وكذا أسماء الضعفاء لابن الجوزي فيمن اسمه سعيد، وكذا في التبين لأسماء المدلسين لبرهان الدين سبط ابن العجمي ص٩، وميزان الاعتدال ج٢/ ١٥٨.
(٦) انظر: الجرح والتعديل ج٢/ق١/٦٣؛ وتهذيب التهذيب ج٤/٧٩ - ٨٠.
(٧) انظر: الجرح والتعديل ج٢/ق١/٦٢.
(٨) انظر: الجرح والتعديل ج٢/ق١/٧٤.
(٩) انظر: الجرح والتعديل ج٢/ق١/٧٤؛ وتهذيب التهذيب ج٤/١٠١.','3','874',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (823,'٢٤٩- (ع) سعيد بن يسار أبو الحباب المدني مولى ميمونة، وقيل مولى شقران أو مولى الحسن بن علي، وقيل مولى بني النجار، ت١١٧هـ، قَال عنه: "وثقة" (١) .
٢٥٠- (د ت س) سعيد بن يعقوب الطالقاني أبو بكر، ت٢٤٤هـ، قَال عنه: "ثقة" (٢) .
٢٥١- (بخ ق) سفيان بن حمزة بن فروة الأسلمي، أبو طلحة المدني، قَال عنه: "صدوق" (٣) .
٢٥٢- (خ س) سفيان بن دينار، التمار، أبو سعيد الكوفي، قَال عنه: "ثقة" (٤) .
٢٥٣- (خ م) سفيان بن زياد العصفري، أبو الورقاء الأحمري، ويقال الأسدي، الكوفي، قَال عنه: "ثقة" (٥) .
٢٥٤- (ع) سفيان بن سعيد بن مسروق الثوري أبو عبد الله ت١٦١هـ، قَال ابن أبي حاتم: "سئل أبو زرعة من أحفظ أصحاب أبي إسحاق؟ فقال: أحفظ الناس عن أبي إسحاق سفيان وشعبة وإسرائيل" (٦) وقَال أيضاً: "أثبت أصحاب أبي إسحاق الثوري وشعبة وإسرائيل ومن بينهم الثوري أحب إلي، كان الثوري أحفظ من شعبة في إسناد الحديث وفي متنه (٧) ".
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٧٢؛ وتهذيب التهذيب ج٤/١٠٢.
(٢) انظر: الجرح والتعديل ج٢/ق١/٧٥؛ وتهذيب التهذيب ج٤/١٠٣؛ وتاريخ بغداد ج٩/٨٩؛ وتذكرة الحفاظ، ص ٤٦١.
(٣) انظر: الجرح والتعديل ج٢/ق١/٢٣٠؛ وتهذيب التهذيب ج٤/١٠٩.
(٤) انظر: الجرح والتعديل ج٢/ق١/٢٢١؛ وتهذيب التهذيب ج٤/١٠٩.
(٥) انظر: الجرح والتعديل ج٢/ق١/٢٢١؛ وتهذيب التهذيب ج٤/١١١.
(٦) انظر: الجرح والتعديل ج٢/ق١/٢٢٥؛ واكتفى في تهذيب التهذيب ج٤/١١٥ بقوله: "هو أحفظ من شعبة".
(٧) انظر: الجرح والتعديل ج٢/ق١/٢٢٥ وتقدمة الجرح والتعديل، ص ٦٦ وكذا ابن رجب في شرح العلل ص١٦٣ باختصار.','3','875',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (824,'٢٥٥- (خ م س) سلم بن زرير العطاردي، أبو يونس، البصري قَال عنه: "بصري صدوق" (١) .
٢٥٦- (خ٤) سلم بن قتيبة الشعيري، أبو قتيبة الخراساني الفريابي نزيل البصرة، ت٢٠١هـ، قَال عنه: "ثقة" (٢) .
٢٥٧- (بخ د تم س) سلم بن قيس العلوي البصري، قَال ابن أبي حاتم: "قلت لأبي زرعة سلم أحب إليك أو يزيد الرقاشي؟ قَال: سلم لأنه روى عن أنس حديثين أو ثلاثة ويزيد أكثر" (٣) .
٢٥٨- (خ ت ق) سلمة بن رجاء التميمي، أبو عبد الرحمن، الكوفي، قَال عنه: "صدوق" (٤) .
٢٥٩- (ع) سلمة بن كهيل بن حصين الحضرمي التنعي، أبو يحيى الكوفي، ت١٢٣هـ، قَال عنه: "ثقة، مأمون، ذكي" (٥) .
٢٦٠- (ت ق) سلمة بن وهرام اليماني، قَال عنه: "ثقة" (٦) .
٢٦١- سلمة بن الحجاج، أبو بشر، قَال عنه: "ثقة" (٧) .
٢٦٢- (بخ خد س) سليم المكي أبو عبد الله مولى أم علي، قَال عنه: "صدوق" (٨) .
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٢٦٤؛ وتهذيب التهذيب ج٤/١٣٠ وأسماء الضعفاء لابن الجوزي فيمن اسمه سلم.
(٢) انظر: الجرح والتعديل ج٢/ق١/٢٢٦؛ وتهذيب التهذيب ج٤/١٣٣؛ وميزان الاعتدال ج٢/١٨٦.
(٣) انظر: الجرح والتعديل ج٢/ق١/٢٦٣؛ وتهذيب التهذيب ج٤/١٣٥.
(٤) انظر: الجرح والتعديل ج٢/ق١/١٦١؛ وتهذيب التهذيب ج٤/١٤٥؛ وأسماء الضعفاء لابن الجوزي فيمن اسمه سلمة؛ وميزان الاعتدال ج٢/١٨٩.
(٥) انظر: الجرح والتعديل، ج٢/ق١/١٧١؛ وتهذيب التهذيب: ج٤/١٥٦.
(٦) انظر: الجرح والتعديل: ج٢/ق١/١٧٥؛وتهذيب التهذيب: ج٤/١٦١؛ وميزان الاعتدال: ج٢/١٩٤.
(٧) انظر: الجرح والتعديل ج٢/ق١/١٥٨.
(٨) انظر: الجرح والتعديل ج٢/ق١/٢١٤؛ وتهذيب التهذيب ج٤/١٦٧.','3','876',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (825,'٢٦٣- (م دت س) سليم بن أخضر، البصري، ت١٨٠هـ، قَال عنه: "ثقة" (١) .
٢٦٤- (ع) سلمان بن بلال التيمي القرشي، مولاهم أبو محمد، ويقال أبو أيوب المدني، ت١٧٢هـ، قال عنه: "سليمان بن بلال أحب إلى هشام بن سعد (٢) ".
٢٦٥- (مدس) سليمان بن داود الخولاني الدمشقي الداراني "أثنى عليه" (٣) .
٢٦٦- (خ م د س) سليمان بن داود العتكي، أبو ربيع الزهراني البصري الحافظ، ت ٢٣٤هـ، قال عنه "ثقة" (٤) .
٢٦٧- (م س) سليمان بن داود، ويقال ابن محمد بن سليمان، أبو داود المباركي، ت ٢٣١ هـ قال عنه: "سألت يحيى بن معين عنه؟ فقال لا بأس به. قيل لأبي زرعة ما قولك فيه؟ قال هو ثقة شيخ كان يكون ببغداد" (٥) .
٢٦٨- سليمان بن سليمان روى عن عاصم الجحدري، قال عنه: "شيخ" (٦) .
٢٦٩- (خ٤) سليمان بن عبد الرحمن الدمشقي الحافظ ابن بنت شرحبيل بن مسلم الولاني، ت٢٣٣هـ، قال أبو زرعة: "حدثني سليمان ابن عبد الرحمن فقيه أهل دمشق" (٧) .
_________
(١) انظر: الجرح والتعديل ج٢ /ق١/ ٢١٥؛ وتهذيب التهذيب ج٤/ ١٦٤.
(٢) انظر: الجرح والتعديل ج٢/ق١/١٠٣؛ وتهذيب التهذيب ج٤/١٧٦.
(٣) انظر: تهذيب التهذيب ج٤/١٩٠.
(٤) انظر: تهذيب التهذيب ج٤/١٩١؛ وتاريخ بغداد ٩/٣٩؛ وتذكرة الحفاظ ص٤٦٩.
(٥) انظر: الجرح والتعديل ج٢/ق١/١١٤؛ ١٤٠، وتهذيب التهذيب ج٤/١٩٢، وتاريخ بغداد ج٩/٣٨.
(٦) انظر: الجرح والتعديل ج٢/ق١/١٢١.
(٧) انظر: ميزان الاعتدال ج٢/٢١٣.','3','877',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (826,'٢٧٠- (قد ق) سليمان بن عتبة بن ثور بن زيد بن الأخنس السلمي، ويقال الغساني أبو ربيع الداراني، ١٨٥هـ، "قال أبو زرعة عن أبي مسهر ثقة" (١) .
٢٧١- (ق) سليمان بن قيس اليشكري، البصري، قال عنه: "ثقة" (٢) .
٢٧٢- (هب) سليمان بن محمد بن كعب بن كعب بن عجرة الأنصاري السلمي، المدني، قال عنه: "ثقة" (٣) .
٢٧٣- (ع) سليمان بن مهران الأسدي الكاهلي، أبو محمد الكوفي الأعمشي، ت١٤٧ أو ١٤٨هـ، قال عنه: "إمام" (٤) .
٢٧٤- (ق) سليمان بن أبي المغيرة العبسي أبو عبد الله الكوفي، قال عنه: "شيخ" (٥) .
٢٧٥- سليمان بن أبي هوذة قال عنه: "صدوق لا بأس به" (٦) .
٢٧٦- (ع) سليمان بن يسار، الهلالي، أبو أيوب ويقال أبو عبد الرحمن ويقال أبو عبد الله المدني، مولى, ميمونه، ت١٠٩هـ، قال عنه: "ثقة مأمون فاضل عابد" (٧) .
٢٧٧- (بخ م ٤) سماك بن الوليد الحنفي، أبو زميل، مصغرا،
اليمامي الكوفي، قال عنه: "ثقة" (٨) .
_________
(١) انظر: تهذيب التهذيب ج٤/٢١٠.
(٢) انظر: الجرح والتعديل ج٢/ق١/١٣٦؛ وتهذيب التهذيب ج ٤/٢١٥.
(٣) انظر: الجرح والتعديل ج٢/ق١/١٣٨؛ وتعجيل المنفعة ص ١١٣.
(٤) انظر: الجرح والتعديل ج٢/ق١/١٤٧.
(٥) انظر: الجرح والتعديل ج٢/ق١/١٤٦؛ وتهذيب التهذيب ج ٤/٢٢١.
(٦) انظر: الجرح والتعديل ج٢/ق١/١٤٨.
(٧) انظر: الجرح والتعديل ج٢/ق١/١٤٩؛ وتهذيب التهذيب ج٤/ ٢٢٩.
(٨) انظر: الجرح والتعديل ج٢/ق١/٢٨٠.','3','878',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (827,'٢٧٨- سماك بن موسى، الضبي أخو مسحاج بن موسى، قال عنه: "لا بأس به وبأخيه مسحاج بن موسى" (١) .
٢٧٩- (أ) سميع الزيات، الكوفي أبو صالح مولى ابن عباس الحنفي، قال عنه: "ثقة" (٢) .
٢٨٠- (م ٤) سهل بن حماد العنقزي، أبو عتاب الدلال البصري، ت ٢٠٨ هـ، قال عنه: "صالح الحديث شيخ" (٣) .
٢٨١- (دس) سهل بن محمد بن الزبير العسكري، أبو سعيد، وقيل أبو داود، ت٢٢٧ هـ، قال عنه: "كان أكيس من سهل بن عثمان" (٤) .
٢٨٢- (س) سهل أبو الأسد، الكوفي، الحنفي، القراري، قال عنه: "الذي يحدث عنه الأعمشي صدوق" (٥) .
٢٨٣- (٤) سهيل بن أبي صالح، واسمه ذكوان السمان، أبو يزيد المدني، قال ابن أبي حاتم: "سألت أبا زرعة عن سهيل بن أبي صالح قال: قلت هو أحب إليك أو العلاء بن عبد الرحمن؟ فقال: سهيل أشبه وأشهر وأبوه أشهر قليلا (٦) ".
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٣٢١.
(٢) انظر: الجرح والتعديل ج٢/ق١/٣٠٦؛ تعجيل المنفعة ص ١١٤.
(٣) انظر: الجرح والتعديل ج٢/ق١/١٩٦؛ وتهذيب التهذيب ج٤/٢٤٩، وميزان الاعتدال ج٢/ ٢٣٧.
(٤) انظر: الجرح والتعديل ج٢/ق١/٢٠٤؛ وتهذيب التهذيب ج٤/٢٥٧
(٥) انظر: الجرح والتعديل ج٢/ق١/٢٠٧؛ وتهذيب التهذيب ج٧/٣٩٧؛ قال ابن حجر في تهذيب التهذيب ج٧/٣٩٨: "جزم الدارقطني وجماعة قبله أن شعبة وهم فيه إذ سماه عليا وإنما هو سهل وكناه أبا الأسود وإنما هو أبو الأسد، وقال الحنفي: "وهو القراري برائين مهملتين قبلهما قاف قال: وروى عنه الأعمش ومسعر والمسعودي على الصحة".
(٦) انظر: الجرح والتعديل ج٢/ق١/٢٤٧؛ وتهذيب التهذيب ج٤/٢٦٣، وفي ميزان الاعتدال ج٢/٢٤٣ "سهيل أشبه من العلاء".','3','879',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (828,'٢٨٤- (ع) سلام بن سليم الحنفي مولاهم أبو الأحوص الكوفي الحافظ، ت١٧٩هـ، قَال عنه: "ثقة" (١) .
٢٨٥- (د ق) سيار بن عبد الرحمن الصدفي المصري قَال عنه: "ليس به بأس" (٢) .
٢٨٦- (خ خد س) شبيب بن سعيد التميمي الحبطي، أبو سعيد البصري، ت١٨٦هـ، قَال عنه: "لا بأس به بصري كتب عنه ابن وهب بمصر" (٣) .
٢٨٧- (د س) شبيب بن عبد الملك التيمي البصري، قَال عنه: "صدوق روى عنه معتمر بن سليمان" (٤) .
٢٨٨- (م ق) شجاع بن مخلد، الفلاس، أبو الفضل البغوي، ت٢٣٥هـ، قَال عنه: "ثقة" (٥) .
٢٨٩- (ع) شجاع بن الوليد بن قيس السكوني، أبو بدر، الكوفي ت٢٠٤هـ، قَال عنه: "لا بأس به" (٦) .
٢٩٠- شجاع بن أشرس أبو العباس قَال عنه: "ثقة" (٧) .
٢٩١- (ع) شعيب بن أبي حمزة، واسمه دينار الأموي مولاهم، أبو بشر
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٢٦٠، تهذيب التهذيب ج٤/٢٨٣.
(٢) انظر: الجرح والتعديل ج٢/ق١/٢٥٦، وفي تهذيب التهذيب ج٤/٢٩١. "لا بأس به".
(٣) انظر: الجرح والتعديل ج٢/ق١/٣٥٩؛ وفي تهذيب التهذيب ج٤/٣٠٧، اكتفى
بقوله: "لا بأس به".
(٤) انظر: الجرح والتعديل ج٢/ق١/٣٥٩ وفي تهذيب التهذيب ج٤/٣٠٨، اكتفى بقوله: "صدوق" وكذا في الميزان الاعتدال ج٢/٣٦٣.
(٥) انظر: الجرح والتعديل ج٢/ق١/٣٧٩؛ وتهذيب التهذيب ج٤/٣١٢.
(٦) انظر: الجرح والتعديل ج٢/ق١/٣٧٩؛ وتهذيب التهذيب ج٤/٣١٣؛ وميزان الاعتدال ج٢/٢٦٤.
(٧) انظر: الجرح والتعديل ج٢/ق١/٣٧٩.','3','880',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (829,'الحمصي، قَال ابن أبي حاتم: "سألت أبا زرعة عن شعيب وابن أبي الزناد؟ فقال: شعيب أشبه حديثاً، وأصح من ابن أبي الزناد" (١) .
٢٩٢- (س) شعيب بن يوسف النسائي أبو عمرو، قَال عنه: "ثقة قدم علينا وكان صاحب حديث" (٢) .
٢٩٣- (د) شعيب صاحب الطيالسة، وقال ابن حبان بياع الأنماط، قَال عنه: "لا بأس به" (٣) .
٢٩٤- (ع) شعبة بن الحجاج بن الورد العتكي مولاهم، أبو بسطام الواسطي، البصري، ت١٦٠هـ، قَال عنه: "أثبت أصحاب أبي إسحاق الثوري وشعبة وإسرائيل، وشعبة أحب إلي من إسرائيل" (٤) .
٢٩٥- (د) شهاب بن خراش بن حوشب بن يزيد بن الحارث الشيباني الحوشبي، أبو الصلت الواسطي ابن أخي العوام، قَال عنه: "لا بأس به" (٥) وقال مرة: "كوفي ثقة نزل الرملة" (٦) وقال أيضاً: "كان صاحب سنة" (٧) .
٢٩٦- (أ) شهاب بن مدلج العنبري والد حبيب بن شهاب، قَال عنه: "ثقة" (٨) .
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٣٤٥؛ وتهذيب التهذيب ج٤/٣٥٢.
(٢) انظر: الجرح والتعديل ج٢/ق١/٣٥٣؛ وتهذيب التهذيب ج٤/٣٥٨.
(٣) انظر: الجرح والتعديل ج٢/ق١/٣٥٤؛ وتهذيب التهذيب ج٤/٣٥٨.
(٤) انظر: الجرح والتعديل ج٢/ق١/٣٧٠- ٣٧١.
(٥) انظر: الجرح والتعديل ج٢/ق١/٣٦٢؛ وتهذيب التهذيب ج٤/٣٦٧.
(٦) انظر: تهذيب التهذيب ج٤/٣٦٧.
(٧) انظر: تهذيب التهذيب ج٤/٣٦٧ وفي ميزان الاعتدال ج٢/٢٨١ قَال: "ثقة صاحب سنة".
(٨) انظر: الجرح والتعديل ج٢/ق١/٣٦١؛ وتعجيل المنفعة.','3','881',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (830,'٢٩٧- (بخ م ٤) شهر بن حوشب الأشعري، أبو سعيد، ويقال، أبو عبد الله، ويقال أبو عبد، ت١١١أو ١١٢هـ، قَال عنه: "لا بأس به" (١) .
٢٩٨- (م د س) شيبان بن فروخ وهو شيبان بن أبي شيبة، الحبطي مولاهم، أبو محمد الأيلي، ت٢٣٥هـ، قَال عنه: "صدوق" (٢) .
٢٩٩- شيبان بن زهير بن شقيق بن ثور السدوسي أبو العوام وهو ابن عم قتادة بصري، قَال عنه: "صالح الحديث" (٣) .
٣٠٠- (بخ م) صالح بن عمر الواسطي نزل حلوان ت١٨٧هـ، قَال عنه: "ثقة" (٤) .
٣٠١- (ق) صباح بن محارب التيمي الكوفي، قَال عنه: "صدوق" (٥) .
٣٠٢- (خ م دت س) صغر بن جويرية أبو نافع مولى تميم، ويقال مولى بني هلال، قَال عنه: "لا بأس به" (٦) .
٣٠٣- (خ د س ق) صدقة بن خالد، الأموي العباسي الدمشقي مولى أم البنين أخت معاوية، ت١٧١هـ، قَال عنه: "ثقة" (٧) .
٣٠٤- صدقة بن المنتصر، أبو شعبة الشعباني، قَال عنه: "لا بأس به" (٨) .
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٣٨٣؛ وتهذيب التهذيب ج٤/٣٧١؛ والترغيب والترهيب ج٤/٥٧١- ٥٧٢؛ وميزان الاعتدال ج٢/٢٨٣.
(٢) انظر: الجرح والتعديل ج٢/ق١/٣٥٧ وتهذيب التهذيب ج٤/٣٧٥؛ وميزان الاعتدال ج٢/٢٨٥ وقَال الذهبي في تذكرة الحفاظ ص٤٤٣: "وثقه أبو زرعة".
(٣) انظر: الجرح والتعديل ج٢/ق٢/٣٥٥.
(٤) انظر: الجرح والتعديل ج٢/ق١/٤٠٩؛ وتهذيب التهذيب ج٤/٣٩٨.
(٥) انظر: الجرح والتعديل ج٢/ق١/٤٤٣؛ وتهذيب التهذيب ج٤/٤٠٨، في ميزان الاعتدال ج٢/٣٠٦ قَال الذهبي: "أثنى عليه أبو زرعة".
(٦) انظر: الجرح والتعديل ج٢/ق١/٤٢٧؛ وتهذيب التهذيب ج٤/٤١١.
(٧) انظر: الجرح والتعديل ج٢/ق١/٤٣١؛ وتهذيب التهذيب ج٤/٤١٤.
(٨) انظر: الجرح والتعديل ج٢/ق١/٤٣٤.','3','882',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (831,'٣٠٥- (بخ م مد س) الصعق بن حَزْن بن قيس البكري، ثم العيش أبو عبد الله البصري، قَال عنه: "ثقة" (١) .
٣٠٦- (بخ) الصّعقب بن زهير بن عبد الله بن زهير بن سليم الأزدي الكوفي، قَال عنه: "ثقة" (٢) .
٣٠٧- (م د س) صهيب أبو الصهباء الكبرى البصري، قَال عنه: "ثقة" (٣) .
٣٠٨- (د ت ق) الضحاك بن شرحبيل بن عبد الله بن نوف الغافقي أبو عبد الله المصري، قَال عنه: "لا بأس به صدوق" (٤) .
٣٠٩- (٤) الضحاك بن مزاحم الهلالي، أبو القاسم، ويقال أبو محمد الخراساني، ت١٠٦هـ، قَال عنه: "ثقة" (٥) .
٣١٠- (م د) طارق بن عمر المكي الأموي مولاهم القاضي، قال عنه: "ثقة" (٦) .
٣١١- (بخ ت) طالب بن الحجير العبدي أبو حجير البصري، قال عنه: "شيخ" (٧) .
٣١٢- (ع) طاوس بن كيسان اليماني أبو عبد الرحمن الحميري الجندي مولى نجير بن ريسان، ١٠٦هـ، قال عنه "ثقة" (٨) .
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٤٥٥؛ وتهذيب التهذيب ج٤/٤٢٤؛ وميزان الاعتدال ج٢/٣١٥.
(٢) انظر: الجرح والتعديل ج٢/ق١/٤٥٥؛ وتهذيب التهذيب ج ٤/٤٣٢.
(٣) انظر: الجرح والتعديل ج٢/ق١/٤٤٤؛ وتهذيب التهذيب ج٤/٤٣٩؛ ميزان الاعتدال ج٢/٣٢١.
(٤) انظر: الجرح والتعديل ج٢/ق١/٤٥٩؛ وتهذيب التهذيب ج٤/٤٤٥.
(٥) انظر: الجرح والتعديل ج٢/ق١/٤٥٩وتهذيب التهذيب ج٤/٤٥٣؛ وأسماء الضعفاء لا بن الجوزي فيمن أسمه الضحاك، وميزان الاعتدال ج٢/٣٢٦.
(٦) انظر: الجرح والتعديل ج٢/ق١/٤٨٧؛ وتهذيب التهذيب ج٥/٦وتقريب التهذيب ج١/٣٧٦.
(٧) انظر: الجرح والتعديل ج٢/ق١/٤٩٦؛ وتهذيب التهذيب ج٥/٩.
(٨) انظر: الجرح والتعديل ج٢/ق١/٥٠١؛ وتهذيب التهذيب ج٥/٩.','3','883',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (832,'٣١٣- (خ س) طلحة بن أبي سعيد الإسكندراني، أبو مالك مولى قريش، ت١٥٧هـ، قال عنه: "ثقة" (١) .
٣١٤- (خ ٤) طلحة بن عبد الله بن عوف الزهري المدني القاضي ابن اخي عبد الرحمن بن عوف، أبو عبد الله، ويقال أبو محمد ت٩٧هـ قال عنه: "ثقة" (٢) .
٣١٥- (ع) طلحة بن نافع القرشي، مولاهم أبو سفيان القاسمي، ويقال المكي الأسكاف قال عنه: "روى عنه الناس قيل أبو زبير أحب أحب إليك أو قال أبو زهير أشهر فعاوده بعض من حضر فيه فقال تريد أن أقول هو ثقة؟ الثقة سفيان وشعبة" (٣) .
٣١٦- (م ٤) طلحة بن يحيى بن طلحة بن عبيد الله التيمي المدني، ت١٠٦هـ، قال عنه: "صالح" (٤) .
٣١٧- (بخ م ٤) طلق بن حبيب العنزي، البصري، قال عنه: "كوفي سمع ابن عباس وهو ثقة لكن كان يرى الإرجاء" (٥) .
٣١٨- (بخ د ت س ق) طليق بن قيس، الحنفي الكوفي، قَال عنه: "ثقة" (٦) .
٣١٩- طيب بن زيان، أبو زبان، الفلسطيني، العسقلاني من أهل قرية سناجية قَال ابن أبي حاتم: "هل تحل الرواية عنه؟ قَال: نعم هو عندي صدوق" (٧) .
_________
(١) انظر: الجرح والتعديل ج٢/ق١/٤٧٦؛ وتهذيب التهذيب ج٥/١٦.
(٢) انظر: الجرح والتعديل ج٢/ق١/٤٧٣؛ وتهذيب التهذيب ج٥/١٩.
(٣) انظر: الجرح والتعديل ج٢/ق١/٤٧٥؛ وتهذيب التهذيب ج٥/٢٧؛ ميزان الاعتدال ج٢/٣٤٢.
(٤) انظر: الجرح والتعديل ج٢/ق١/٤٧٧؛ وتهذيب التهذيب ج٥/٢٨وفي ميزان الاعتدال ج٢/٣٤٣ قَال: "صالح الحديث".
(٥) انظر: الجرح والتعديل ج٢/ق١/٤٩١؛ وتهذيب التهذيب ج٥/٣١ وانظر: ميزان الاعتدال ج٢/ ٣٤٥.
(٦) انظر: الجرح والتعديل ج٢/ق١/٤٩٨، وتهذيب التهذيب ج٥/٣٥.
(٧) انظر: الجرح والتعديل ج٢/ق١/٤٩٨، ومعجم البلدان في مادة سناجية.','3','884',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (833,'٣٢٠- (ع) عاصم بن بهدلة وهو ابن أبي النجود الأسدي مولاهم الكوفي أبو بكر المقرئ، ت١٢٨هـ، قَال عنه: "ثقة" (١) .
٣٢١- عاصم بن حميد الكوفي الحناط، قَال عنه: "ثقة" (٢) .
٣٢٢- (دزق) عاصم بن رجاء بن حيوة، الكندي الفلسطيني، قَال عنه: "لا بأس به" (٣) .
٣٢٣- (ع) عاصم بن سليمان الأحول، أبو عبد الرحمن، ت بعد ١٤٠هـ، قَال عنه: "ثقة" (٤) .
٣٢٤- (ع) عاصم بن ابن عمر بن قتادة بن النعمان بن زيد بن عامر بن سواد بن كعب وهو ظفر بن الخزرج بن عمرو بن مالك بن الأوس الأنصاري الظفري، أبو عمر المدني، ت بعد ١٢٠هـ، قَال عنه: "ثقة" (٥) .
٣٢٥- (ع) عاصم بن محمد بن زيد بن عبد الله بن عبد الله بن عمر بن الخطاب العمري المدني، قَال عنه: "صدوق في الحديث" (٦) .
٣٢٦- (دق) عاصم بن المنذر بن الزبير بن العوام، الأسدي، المدني، قَال عنه: "صدوق" (٧) ، ونقل عنه أنه قَال عنه: "ثقة" (٨) .
_________
(١) انظر: الجرح والتعديل ج٣/ق١/٣٤١، وتهذيب التهذيب ج٥/٣٩؛ والترغيب والترهيب ج٤/٥٧٢؛ وميزان الاعتدال ج٢/٣٥٧.
(٢) انظر: الجرح والتعديل ج٣/ق١/٣٤٢، وتهذيب التهذيب ج٥/٤١.
(٣) انظر: الجرح والتعديل ج٣/ق١/٣٤٣، وتهذيب التهذيب ج٥/٤١؛ وميزان الاعتدال ج٢/٣٥٠.
(٤) انظر: الجرح والتعديل ج٣/ق١/٣٤٣، وتهذيب التهذيب ج٥/٤٣.
(٥) انظر: الجرح والتعديل ج٣/ق١/٣٤٦، وفي تهذيب التهذيب ج٥/٥٤ "وقال عبد الحق في الإحكام: هو ثقة عند أبي زرعة" وقَال الذهبي في ميزان الاعتدال ج٢/٣٥٥: "وثقه أبو زرعة".
(٦) انظر: تهذيب التهذيب ج٥/٥٧.
(٧) انظر: الجرح والتعديل ج٣/ق١/٣٥٠.
(٨) انظر: تهذيب التهذيب ج٥/٧٥.','3','885',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (834,'٣٢٧- عاصم بن مهجع روى عن مسلمة بن سالم الجهني قال عنه: "ثقة" (١) .
٣٢٨- عافية بن أيوب روى عن أسامة بن زيد بن أسلم، أبو عبيدة مصري قال عنه: "ليس به بأس" (٢) .
٣٢٩- (ع) عامر بن شراحيل بن عبد، وقيل عامر بن عبد الله بن شراحيل الشعبي الحميري، ت بعد ١٠٠، وقال عنه: "كوفي ثقة" (٣) .
٣٣٠- (ع) عامر بن أسامة بن عمير، أبو المريح، الهزلي، البصري قيل إسمه عامر، وقيل زيد بن أسامة عمير، وقيل بن عامر بن عمير، ت ٩٨ أو ١٠٨هـ، أو بعدها، قال عنه: "ثقة" (٤) .
٣٣١- عامر بن حجير بن أخي قزعة بن سويد، وهو ابن حجير ابن سويد بن حجير، الباهلي، البصري، أبو الحسن، قال عنه: "ثقة" (٥) .
٣٣٢- (س ق) عائذ بن حبيب بن الملاح العبسي، ويقال القرشي مولاهم أبو أحمد، ويقال أبو هشام الكوفي بياع الهروي، ت١٩٠ هـ، قال عنه: "صدوق في الحديث" (٦) .
٣٣٣- (خ م د س) عباد بن موسى الختلي، أبو محمد الأنباري، ت ١٣٠هـ، قال عنه "ثقة" (٧) .
٣٣٤- (خ م د س ق) عبادة بن الوليد بن عبادة بن الصامت الأنصاري، ويقال له عبد الله، قال عنه: "مديني ثقة" (٨) .
_________
(١) انظر: الجرح والتعديل ج٣/ق١/٣٥٠.
(٢) انظر: الجرح والتعديل ج٣/ق٢/٤٤.
(٣) انظر: الجرح والتعديل ج٣/ق١/٣٢٤، وتهذيب التهذيب ج٥/٦٧.
(٤) انظر: الجرح والتعديل ج٣/ق١/٣١٩.
(٥) انظر: الجرح والتعديل ج٣/ق١/٣٢٠
(٦) انظر: تهذيب التهذيب ج٥/٨٨.
(٧) انظر: الجرح والتعديل ج٣/ق١/٧، وتهذيب التهذيب ج٥/١٠٦.
(٨) انظر: الجرح والتعديل ج٣/ق١/٩٦.','3','886',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (835,'٣٣٥- (دت) عباس بن جليد الحجري المصري، بحدود ١٠٠ هـ، قَال عنه: "ثقة" (١) .
٣٣٦- عباس بن غالب، الوراق، بغدادي، ت ببغداد سنة٢٣٣هـ، قَال عنه: "شيخ ثقة، لا بأس به" (٢) .
٣٣٧- (س) عبد الله بن أحمد بن محمد بن حنبل بن هلال بن أسد الشيباني أبو عبد الرحمن البغدادي، ت ٢٩٠هـ، قَال عنه: "قال لي أحمد ابني عبد الله محفوظ من علماء الحديث لا يكاد يذاكر إسماعيل بن علي إلا بما لا أحفظ" (٣) .
٣٣٨- (ع) عبد الله بن إدريس بن يزيد بن عبد الرحمن الأودي الكوفي، أبو محمد، ت١٩٢ هـ، قَال ابن أبي حاتم: "سئل أبي وأبو زرعة عن يونس بن بكير وعبده بن سليمان وسلمة بن الفضل في ابن إسحاق أيهم أحب إليكما؟ قَالا: "ابن إدريس أحبهم إلينا" (٤) .
٣٣٩- (٤) عبد الله بن بدر بن عميرة بن الحارث بن شمر ويقال: سمرة الحنفي، اليمامي، قَال عنه: "ثقة" (٥) .
٣٤٠- (س ق) عبد الله بن بشر بن التيهان الرقي، مولى بني يربوع قاضي الرقة، قَال عنه: "لا بأس به" (٦) .
٣٤١- (كن ق) عبد الله بن الجراح بن سعد التيمي أبو محمد القهستاني، ت٢٣٢ هـ، قال عنه: "صدوق" (٧) .
_________
(١) انظر: الجرح والتعديل ج٣/ق١/٢١١، وتهذيب التهذيب ج ٥/١١٦.
(٢) انظر: الجرح والتعديل ج٣/ق١/٢١٧، وتاريخ بغداد ج ١٢/١٣٦.
(٣) انظر: تهذيب التهذيب ج٥/١٤٢.
(٤) انظر: الجرح والتعديل ج٢/ق٢/٩.
(٥) انظر: الجرح والتعديل ج٢/ق٢/١٢، وتهذيب التهذيب ج ٥/١٥٤.
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٤، وتهذيب التهذيب ج٥/١٦٠، وأسماء الضعفاء لابن الجوزي وكذا في تقريب التهذيب ج١/٤٠٤؛ وفي ميزان الاعتدال ج٢/٣٩٧ سماه "نبهان" بدل "التيهان" ونقل قول أبي زرعة فيه في ص٣٩٨.
(٧) انظر: الجرح والتعديل ج٢/ق٢/٢٨، وتهذيب التهذيب ج٥/١٦٩.','3','887',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (836,'٣٤٢- (خت م٤) عبد الله بن جعفر بن عبد الرحمن بن المسور بن مخرمة بن نوفل بن أهيب بن عبد مناف الزهري المخرمي، أبو محمد المدني، ت١٧٠هـ، قَال عنه: "هو أحب إلي من يزيد بن عبد الملك النوفلي" (١) .
٣٤٣- (د) عبد الله بن أبي جعفر عيسى بن ماهان الرازي قَال عنه: "ثقة صدوق" (٢) .
٣٤٤- (د) عبد الله بن الجهم الرازي أبو عبد الرحمن قَال عنه: "رأيته ولم أكتب عنه، وكان صدوقاً" (٣) .
٣٤٥- (ع) عبد الله بن الحارث بن نوفل بن الحارث بن عبد المطلب بن هاشم الهاشمي، أبو محمد المدني، ت٧٩هـ، قَال عنه: "ثقة" (٤) .
٣٤٦- (ع) عبد الله بن الحارث انصاري، أبو الوليد البصري نسيب ابن سيرين وختنه، قَال عنه: "ثقة" (٥) .
٣٤٧- (خت٤) عبد الله بن الحسين الأزدي أبو حريز البصري قاضي سجستان قَال عنه: "ثقة" (٦) .
٣٤٨- (خ٤) عبد الله بن داود بن عامر بن الربيع الهمداني ثم الشعبي أبو عبد الرحمن المعروف بالخريبي ت ٢١٣ هـ، قَال عنه: "ثقة" (٧) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/٢٢، وتهذيب التهذيب ج٥/١٧٢.
(٢) انظر: تهذيب التهذيب ج٥/١٧٧، وقال المنذري في الترغيب والترهيب ج٤/٥٧٣ "وثقه أبو زرعة" واكتفى ابن أبي حاتم في الجرح والتعديل ج٢/ق٢/١٢٧ بقوله: "صدوق" وكذا في ميزان الاعتدال ج٢/٤٠٤.
(٣) انظر: الجرح والتعديل ج٢/ق٢/٢٧، وتهذيب التهذيب ج٥/١٧٨واكتفى الذهبي
في ميزان الاعتدال ج٢/٤٠٥ بقوله: "صدوق".
(٤) انظر: الجرح والتعديل ج٢/ق٢/٣١، وتهذيب التهذيب ج٥/١٨١.
(٥) انظر: الجرح والتعديل ج٢/ق٢/٣١، وتهذيب التهذيب ج٥/١٨١.
(٦) انظر: الجرح والتعديل ج٢/ق٢/٣٥، وتهذيب التهذيب ج٥/١٨٨، وأسماء الضعفاء لابن الجوزي وميزان الاعتدال ج٢/٤٠٧.
(٧) انظر: الجرح والتعديل ج٢/ق٢/٤٧، وتهذيب التهذيب ج٥/٢٠٠.','3','888',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (837,'٣٤٩- (ع) عبد الله بن دينار العدوي أبو عبد الرحمن المدني، ت١٢٧هـ، قَال عنه: "مديني ثقة مولى ابن عمر" (١) .
٣٥٠- (بخ) عبد الله بن رافع الحضرمي أبو سلمة المصري، ت في خلافة هشام بن عبد الملك، قَال عنه: "مصري ثقة" (٢) .
٣٥١- (م٤) عبد الله بن رافع المخزومي، أبو رافع المدني مولى أم سلمة زوج النبي صلى الله عليه وسلم، قَال عنه: "مديني ثقة" (٣) .
٣٥٢- (خ خد س ق) عبد الله بن رجاء بن عمر، ويقال المثنى أبو عمر، ويقال أبو عمرو الغداني البصري، ت ٢١٩أو ٢٢٠هـ، قَال ابن أبي حاتم: "سئل أبو زرعة عنه فجعل يثني عليه وقال: حسن الحديث عن إسرائيل" (٤) .
٣٥٣- (زم دس ق) عبد الله بن رجاء المكي، أبو عمران البصري، نزيل مكة، ت في حدود ١٩٠هـ، قَال عنه: "شيخ صالح" (٥) .
٣٥٤- (خ م دق س) عبد الله بن سعيد بن عبد الملك بن مروان ابن الحكم بن أبي العاص الأموي، الدمشقي، أبو صفوان، ت على رأس ٢٠٠هـ، قَال عنه: "لا بأس به صدوق" (٦) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/٤٧؛ وفي تهذيب التهذيب ج٥/٢٠٢ اكتفى بقوله: "ثقة".
(٢) انظر: الجرح والتعديل ج٢/ق٢/٥٤؛ وتهذيب التهذيب ج٥/٢٠٦ وفي تقريب التهذيب ج١/٤١٤، قَال: "وثقه أبو زرعة".
(٣) انظر: الجرح والتعديل ج٢/ق٢/٥٣؛ وتهذيب التهذيب ج٥/٢٠٦.
(٤) انظر: الجرح والتعديل ج٢/ق٢/٥٥؛ وتهذيب التهذيب ج٥/٢١٠.
(٥) انظر: الجرح والتعديل ج٢/ق٢/٥٤- ٥٥؛ وتهذيب التهذيب ج٥/٢١١، وفي ميزان الاعتدال ج٢/٤٢١ نقل عن أبي زرعة أنه قَال عنه: "صدوق".
(٦) انظر: الجرح والتعديل ج٢/ق٢/٧٢؛ وتهذيب التهذيب ج٥/٢٣٨ واكتفى الذهبي في ميزان الاعتدال ج٢/٤٢٩ بقوله: "صدوق".','3','889',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (838,'٣٥٥- (ز) عبد الله بن سويد بن حيان المصري، أبو سليمان، ت ١٠٢هـ، قَال عنه: "صدوق" (١) .
٣٥٦- (ع) عبد الله بن شداد بن الهاد الليثي، أبو وليد المدني، قَال عنه: "ثقة" (٢) .
٣٥٧- (ص) عبد الله بن شريك العامري الكوفي قَال عنه: "ثقة" (٣) .
٣٥٨- (بخ م ٤) عبد الله بن شقيق العقيلي، أبو عبد الرحمن، ويقال أبو محمد البصري، ت ١٠٨هـ، قَال عنه: "ثقة" (٤) .
٣٥٩- (خت دت ق) عبد الله بن صالح بن محمد بن مسلم الجهني مولاهم أبو صالح المصري كاتب الليث، ت ٢٢٢هـ، قَال عنه: "لم يكن عندي ممن يتعمد الكذب، وكان حسن الحديث" (٥) وقَال أيضاً: "سمعت
عبد العزيز بن عمران المصري يقول، كنا نحضر شعيب بن الليث وأبو صالح يقرأ عليه حديث الليث، فإذا فرغنا قلنا يا أبا صالح نحدث بهذا عنك؟ فيقول: نعم" (٦) ، وقال سعيد البرذعي: "قلت لأبي زرعة أبو صالح كاتب الليث؟ فضحك وقَال ذاك الرجل حسن الحديث. قلت: أحمد يحمل عليه؟ قَال: وشيء آخر سمعت عبد العزيز بن عمران يقول: قرأ علينا أبو صالح كتاب عقيل فإذا في أوله حدثني أبي عن جدي فإذا هو كتاب عبد الملك بن شعيب بن الليث. قلت: فأي شيء حاله في يحيى بن أيوب ومعاوية بن صالح
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/٦٦؛ وتهذيب التهذيب ج٥/٢٤٨.
(٢) انظر: الجرح والتعديل ج٢/ق٢/٨٠؛ وتهذيب التهذيب ج٥/٢٥٢.
(٣) انظر: الجرح والتعديل ج٢/ق٢/٨١؛ وتهذيب التهذيب ج٥/٢٥٣.
(٤) انظر: الجرح والتعديل ج٢/ق٢/٨١؛ وتهذيب التهذيب ج٥/٢٥٤، وميزان الاعتدال ج٢/٤٤٠ حيث قَال: "وثقه أبو زرعة".
(٥) انظر: الجرح والتعديل ج٢/ق٢/٨٧؛ وتهذيب التهذيب ج٥/٢٥٩؛ وميزان
الاعتدال ج٢/٤٤١.
(٦) انظر: الجرح والتعديل ج٢/ق٢/٨٧.','3','890',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (839,'والمشيخة؟ قَال: كان يكتب لليث والله أعلم وفي نسخة واثنى عليه بدل والله أعلم" (١) وقال البرذعي أيضاً: "قلت لأبي زرعة، رأيت بمصر أحاديث لعثمان بن صالح عن ابن لهيعة يعني منكرة، فقال: لم يكن عثمان عندي ممن يكذب ولكنه كان يسمع الحديث مع خالد بن نجيح وكان خالد إذا سمعوا من الشيخ، أملى عليهم ما لم يسمعوا فبلوا به. وبلى به ابو صالح أيضاً في حديث زهرة بن معبد، عن سعيد بن المسيب عن جابر ليس له أصل وإنما هو من خالد بن نجيح، وكذا قَال أحمد بن يحيى التستري (٢) عن أبي زرعة في حديث الفضائل وزاد وكان
خالد يضع في كتب الشيوخ ما لم يسمعوا ويدلس لهم، وله غير هذا قلت لأبي زرعة فمن رواه عن ابن أبي مريم؟ قَال: هذا كذب. قَال التستري: وقد كان محمد بن الحارث العسكري حدثني به عن الكاتب الليث وابن أبي مريم" (٣) .
_________
(١) انظر: تهذيب التهذيب ج٥/٢٥٨.
(٢) والنص الذي نقله الذهبي عن التستري بالصيغة التالية: "قَال أحمد بن محمد
التستري: سألت أبا زرعة عن حديث زهرة في الفضائل، فقال: باطل، وضعه خالد المصري، ودلسه في كتاب أبي صالح. فقلت: فمن رواه عن سعيد بن ابي مريم؟ قَال: هذا كذاب، قد كان محمد بن الحارث العسكري حدثني به عن أبي صالح وسعيد. قلت: قد رواه ثقة عن الشيخين، فلعله مما أدخل على نافع، مع أن نافع بن يزيد صدوق يقظ، فالله أعلم"، انظر: ميزان الاعتدال ج٢/٤٤٣، والتستري هو الحافظ الحجة العلامة الزاهد تاج المتحدثين أبو جعفر بن أحمد بن يحيى بن زهير أحد الأعلام، سمع أبا كريب ومحمد بن عمار الرازي وأبا زرعة وغيرهم، وقَال عن أبي زرعة: "ما رأيت في الدنيا أحفظ من أبي زرعة"، ت ٣١٠هـ، انظر: تذكرة الحفاظ ج٢/٧٥٧- ٧٥٨.
(٣) انظر: تهذيب التهذيب ج٥/٢٥٩ وقد قَال المزي بعد أن نقل قول أبي زرعة، "رواه الحاكم وقَال قد شفي أبو زرعة في علة هذا الحديث فكل ما أتى أبو صالح من أجل هذا الحديث، فإذا وضعه غيره وكتبه في كتاب الليث كان المذنب فيه غير أبي صالح" قَال الذهبي في ميزان الاعتدال ج٢/٤٤٢ "وقد قامت القيامة على عبد الله بن صالح بهذا الخبر الذي قَال فيه: حدثنا نافع بن يزيد، عن زهرة بن معبد، عن سعيد بن المسيب، عن جابر- مرفوعاً: إن الله اختار أصحابي على العالمين سوى النبيين والمرسلين، واختار من أصحابه أربعة: أبا بكر، وعمر، وعثمان، وعلياً، فجعلهم خير أصحابي، وفي أصحابي كلهم خير" ثم نقل عن أبي زرعة، أنه قَال: "بلى أبو صالح بخالد بن نجيح في حديث زهرة بن معبد عن سعيد، وليس له أصل "ثم ذكر الذهب ما رواه الحاكم بسنده إلى علان بن عبد الرحمن أنه قَال: "قدم علينا محمد بن يحي ومعه مائتا دينا، فرأيته يوماً جاء إلى أبي صالح، ومعه أحمد بن صالح، فقال محمد بن يحيى: يا أبا صالح والله ثم والله، ما كانت رحلتي، إلا إليك، أخرج إلى حديث زهرة بن معبد عن ابن المسيب، عن جابر، فقال أبو صالح: والله لو كان في يدي ما فتحتها لك".','3','891',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (840,'٣٦٠- (ق) عبد الله بن عاصم الحماني، أبو سعيد البصري قَال عنه: "صدوق" (١) .
٣٦١- (ع) عبد الله بن عامر بن ربيعة العنزي أبو محمد المدني قَال عنه "أدرك النبي صلى الله عليه وسلم وهو ثقة" (٢) .
٣٦٢- (م ٤) عبد الله بن عباس بن أويس بن مالك بن أبي عامر الأصبحي أبو أويس المدني، ت ١٦٧هـ، قَال عنه: "صالح صدوق كأنه لين" (٣) .
٣٦٣- (م س) عبد الله بن أبي طلحة الأنصاري، أبو يحيى المدني، ت ١٣٤هـ قَال عنه: "ثقة" (٤) .
٣٦٤- (خ م د ت س) عبد الله بن عبد الله بن عمر بن الخطاب العدوي أبو عبد الرحمن، ت ١٠٥هـ، قَال عنه: "ثقة" (٥) .
٣٦٥- (س) عبد الله بن عبد الحكم بن أعين بن ليث أبو محمد المصري، ت ٢١٤هـ، قَال عنه: "مصري ثقة" (٦) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/١٣٤؛ وتهذيب التهذيب ج٥/٢٧٠.
(٢) انظر: الجرح والتعديل ج٢/ق٢/١٢٢؛ وتهذيب التهذيب ج٥/٢٧١؛ والإصابة ج٤/١٣٩.
(٣) انظر: الجرح والتعديل ج٢/ق٢/٩٢؛ وتهذيب التهذيب ج٥/٢٨١.
(٤) انظر: الجرح والتعديل ج٢/ق٢/٩١؛ وتهذيب التهذيب ج٥/٢٨٥.
(٥) انظر: الجرح والتعديل ج٢/ق٢/٩٠؛ وتهذيب التهذيب ج٥/٢٨٦.
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٠٦؛ وتهذيب التهذيب ج٥/٢٨٩.','3','892',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (841,'٣٦٦- (ع) عبد الله بن عبد الرحمن بن أبي حسين بن الحارث بن عامر بن نوفل بن عبد مناف المكي، قَال عنه: "ثقة" (١) .
٣٦٧- عبد الله بن عبد الرحمن بن عوف أبو سلمة، ويقال إن اسمه وكنيته واحد، القرشي ثم الزهري، المديني، قَال عنه: "مديني ثقة إمام" (٢) .
٣٦٨- عبد الله بن عبد الرحمن، الغافقي من أهل مصر، قَال عنه: "مصري لا بأس به" (٣) .
٣٦٩- (٤) عبد الله بن عبيد الله بن العباس بن عبد المطلب قَال عنه: "شيخ" (٤) .
٣٧٠- عبد الله بن عبيد الله، أبو عاصم العباداني، بصري، قَال عنه: "شيخ" (٥) .
٣٧١- (ع) عبد الله بن عبيد بن أبي مليكة زهير بن عبد الله بن جدعان بن عمرو بن كعب بن سعد بن تيم التيمي المكي كان قاضيا على عهد ابن الزبير، ت١١٧هـ، قَال عنه: "مكي ثقة" (٦) .
٣٧٢- (م٤) عبد الله بن عبيد بن عمير بن قتادة بن سعد بن عامر أبو هاشم المكي، ت١١٣ هـ، قَال عنه: "مكي ثقة" (٧) .
٣٧٣- (دت ف) عبد الله بن عاصم، ويقال ابن عصمة أبو علوان الحنفي العجلي، قَال عنه: "كوفي ليس به بأس" (٨) .
_________
(١) انظر: الجرح والتعديل ج ٢/ق٢/٩٧؛ وتهذيب التهذيب ج ٥/٢٩٣، والانتفاء ص٥٣.
(٢) انظر: الجرح والتعديل ج٢/ق٢/٩٤.
(٣) انظر: الجرح والتعديل ج٢/ق٢/٩٥.
(٤) انظر: الجرح والتعديل ج٢/ق٢/١٠٠؛ وتهذيب التهذيب ج٥/٣٠٦.
(٥) انظر: الجرح والتعديل ج٢/ق٢/١٠١.
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٠٠؛ وتهذيب التهذيب ج٥/٣٠٧.
(٧) انظر: الجرح والتعديل ج٢/ق٢/١٠١؛ وتهذيب التهذيب ج٥/٣٠٨.
(٨) انظر: الجرح والتعديل ج٢/ق٢/١٢٦؛ وتهذيب التهذيب ج٥/٣٢١.','3','893',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (842,'٣٧٤- (م ق) عبد الله بن عمير، أبو محمد، مولى أم الفضل، ت١١٧هـ، قَال عنه: "مدني ثقة" (١) .
٣٧٥- (ع) عبد الله بن عمرو بن أبي الحجاج، ميسرة، التميمي، المنقري مولاهم، أبو معمر المقعد، البصري، ت٢٢٤هـ، قَال ابن أبي حاتم: "سئل أبو زرعة عن أبي معمر، فقال: كان حافظا ثقة - يعني إنه كان متقنا" (٢) .
٣٧٦- (م س) عبد الله بن عون بن أبي عون عبد الملك بن يزيد الهلالي أبو محمد البغدادي الآدمي، ت٢٣٢هـ، قَال عنه: "ثقة" (٣) .
٣٧٧- (خ م دس ق) عبد الله بن فيروز الداناج البصري، قَال عنه: "بصري ثقة" (٤) .
٣٧٨- (عس) عبد الله بن كثير الدمشقي الطويل القارئ إمام الجامع قيل اسم جده ميمون الأنصاري، ت١٦٩هـ، قَال عنه: "دمشقي لا بأس به" (٥) .
٣٧٩- (خ م دس ق) عبد الله بن كعب بن مالك الأنصاري السلمي، ت٩٧ أو ٩٨ هـ، قَال عنه: "مديني ثقة" (٦) .
٣٨٠- (دس ق) عبد الله بن لحي الحميدي، أبو عامر، الهوزني الحمصي، قَال عنه: "لا بأس به" (٧) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/١٢٤؛ وتهذيب التهذيب ج٥/٣٤٤.
(٢) انظر: الجرح والتعديل ج٢/ق٢/١١٩؛ وفي نسخة أخرى منه "كان حافظا وكان متقنا" وانظر تذكرة الحفاظ ص٤٩٤ وفي تهذيب التهذيب ج٥/٣٣٦" وقَال ابن أبي حاتم عن أبي ذر- والصواب زرعة- كان ثقة حافظا قَال عبد الغني يعني أنه كان متقنا".
(٣) انظر: الجرح والتعديل ج٢/ق٢/١٣١؛ وتهذيب التهذيب ج٥/٣٥٩.
(٤) انظر: الجرح والتعديل ج٢/ق٢/١٣٦؛ وتهذيب التهذيب ج٥/٣٥٩.
(٥) انظر: الجرح والتعديل ج٢/ق٢/١٤٤؛ وتهذيب التهذيب ج٥/٣٦٨.
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٤٢؛ وتهذيب التهذيب ج٥/٣٦٩.
(٧) انظر: تهذيب التهذيب ج ٥/٣٧٣؛ والإصابة ج٥/٩٧؛ والجرح والتعديل ج٢/ق٢/١٤٥.','3','894',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (843,'٣٨١- (ع) عبد الله بن المبارك المروزي، أبو عبد الرحمن مولى بني حنظلة ت١٨١هـ، قَال عنه: "اجتمع فيه فقه، وسخاء، وشجاعة، وغزو، وأشياء" (١) .
٣٨٢- (خ ت ق) عبد الله بن المثنى بن عبد الله بن أنس بن مالك الأنصاري أبو المثنى الأنصاري البصري، قَال عنه: "صالح" (٢) .
٣٨٣- (خ دس ق) عبد الله بن أبي المجالد، ويقال محمد بن أبي المجالد الكوفي مولى عبد الله بن أبي أوفى، قَال عنه "ثقة" (٣) .
٣٨٤- (خ م دس) عبد الله بن محمد بن أسماء بن عبد بن مخارق الضبعي أبو عبد الرحمن البصري، ت٢٣١ هـ، قَال عنه: "لا بأس به شيخ صالح" (٤) .
٣٨٥- (خ م دس ق) عبد الله بن محمد بن أبي شيبة إبراهيم بن عثمان بن خواستي العبسي مولاهم أبو بكر الحافظ الكوفي، ت٢٣٥هـ، قَال ابن خراشي: "سمعت أبا زرعة الرازي يقول: ما رأيت أحفظ من أبي بكر بن أبي شيبة فقلت له: يا أبا زرعة وأصحاب البغداديين؟ فقال: دع أصحابك أصحاب مخاريق" (٥) .
٣٨٦- (ع) عبد الله بن مرة الهمداني الكوفي، ت١٠٠هـ، قَال عنه: "ثقة" (٦) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/١٨١.
(٢) انظر: الجرح والتعديل ج٢/ق٢/١٧٧؛ وتهذيب التهذيب ج٥/٣٨٨، ونقل الذهبي عنه في ميزان الاعتدال ج٢/٤٩٩، قوله: "صالح الحديث".
(٣) انظر: الجرح والتعديل ج ٢/ق٢/١٨٢؛ وذكره في محمد بن أبي مجالد ج ٤/ ق١/١٠٧، وقَال: "كوفي ثقة" وانظر: تهذيب التهذيب ج٥/٣٨٨.
(٤) انظر: الجرح والتعديل ج٢/ق٢/١٥٩؛ وتهذيب التهذيب ج٦/٥.
(٥) انظر: تهذيب التهذيب ج٦/٤.
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٦٦؛ وتهذيب التهذيب ج٦/٢٤.','3','895',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (844,'٣٨٧- (ت) عبد الله بن مسلم بن جندب الهذلي المدني المقريء، قَال عنه: "مديني لا بأس به" (١) .
٣٨٨- (خ م د ت س) عبد الله بن مسلمة بن قعنب القعنبي الحارني أبو عبد الرحمن المدني نزيل البصرة، ت١٢٣هـ، قَال عنه: "ما كتبت عن أحد أجل في عيني منه" (٢) .
٣٨٩- (ت ق) عبد الله بن معاذ بن نشيط الصنعاني مولى خالد بن غلاب، ت١٨١هـ قَال عنه: "قال ابن معين كان عبد الرزاق يكذبه، قَال أبو زرعة: وأنا أقول هو أوثق من عبد الرزاق" (٣) .
٣٩٠- عبد الله بن أبي موسى التستري نزيل الشام، قَال عنه: "وهو رجل من تستر قدم عليهم الشام فكتبوا عنه مستقيم الحديث" (٤) .
٣٩١- (م دس ق) عبد الله بن معبد بن العباس بن عبد المطلب الهاشمي المدني، قَال عنه: "مديني ثقة" (٥) .
٣٩٢- (بخ م٤) عبد الله بن نافع بن أبي نافع الصائغ المخزومي مولاهم أبو محمد المدني، ت٢٠٦هـ، قَال عنه: "لا بأس به" (٦) .
٣٩٣- (ع) عبد الله بن أبي نجيح يسار الثقفي أبو يسار المكي مولى الأخنس بن شريق، ت١٣١هـ، قَال عنه: "مكي ثقة" (٧) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/١٦٥؛ وتهذيب التهذيب ج٦/٢٨؛ وميزان الاعتدال ج٢/٥٠٣.
(٢) انظر: الجرح والتعديل ج٢/ق٢/١٨١؛ وتهذيب التهذيب ج٦/٣٢؛ والانتقاء ص٦١-٦٢، تذكرة الحفاظ ص٣٨٣.
(٣) انظر: تهذيب التهذيب ج٦/٣٨.
(٤) انظر: الجرح والتعديل ج٢/ق٢/١٦٧..
(٥) انظر: الجرح والتعديل ج٢/ق٢/١٧٣؛ وتهذيب التهذيب ج٦/٣٩.
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٨٤؛ وتهذيب التهذيب ج٦/٥١، والانتقاء ص٥٦، وميزان الاعتدال ج٢/٥١٣.
(٧) انظر: الجرح والتعديل ج٢/ق٢/٢٠٣؛ وتهذيب التهذيب ج٦/٥٤.','3','896',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (845,'٣٩٤- (ق) عبد الله بن واقد بن الحارث بن أرقم بن زياد بن مطرف النعمان بن سلمة بن ثعلبة بن الدؤل بن خليفة الحنفي أبو رجاء الهروي الخراساني، ت بعد ١٦٠ هـ، قال عنه: "لم يكن به بأس" (١) .
٣٩٥- (خت دت س) عبد الله بن الوليد بن ميمون الأموي مولاهم، أبو محمد المكي المعروف بالعدني، قال عنه: "صدوق" (٢) .
٣٩٦- (ع) عبد الله بن وهب بن مسلم القرشي مولاهم، أبو محمد المصري الفقيه، ت ١٩٧ هـ، قال عنه: "نظرت في نحو ثمانين ألف حديث من حديث ابن وهب بمصر فلا أعلم إني رأيت حديثا له، لا أصل له، وهو ثقة" (٣) وقال أيضا "سمعت ابن بكير يقول: ابن وهب أفقه من ابن القاسم" (٤) .
٣٩٧- (خ د) عبد الله بن يحيى المعافري، ويقال الكلاعي، أبو يحيى المصري المعروف بالبرلسي، ت ٢١٠هـ، قال عنه: "أحاديثه مستقيمة لا بأس به" (٥) .
٣٩٨- عبد الله بن هارون بن أبي هارون الصيرفي، قال عنه: "لا بأس به" (٦) .
٣٩٩- عبد الله بن يزيد بن تميم، قال عنه: "لا بأس به" (٧) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/١٩١؛ وتهذيب التهذيب ج٦/٦٥؛ وميزان الاعتدال ج٢/٥٢٠.
(٢) انظر: الجرح والتعديل ج٢/ق٢/١٨٨؛ وتهذيب التهذيب ج٦/٧٠؛ وميزان الاعتدال ج٢/٥٢٠.
(٣) انظر: الجرح والتعديل ج٢/ق٢/١٩٠؛ وفي الانتقاء ص٤٩ "نظرت في حديث ابن وهب نحو ثمانين ألف حديث من حديثه عن المصريين وغيرهم فما أعلم إني رأيت له حديثا لا أصل له" وفي تهذيب التهذيب ج٦/٧٢ "نظرت في نحو ثلاثين ألفا من حديث ابن وهب بمصر وغير مصر إني رأيت حديثا لا أصل له" وانظر: تذكرة الحفاظ ص ٣٠٥.
(٤) انظر: الجرح والتعديل ج٢/ق٢/١٩٠؛وتهذيب التهذيب ج٦/٧٢؛ والانتقاء ص ٤٩.
(٥) انظر: الجرح والتعديل ج٢/ق٢/٢٠٤؛ وتهذيب التهذيب ج ٦/٧٧ "لا بأس به".
(٦) انظر: الجرح والتعديل ج٢/ق٢/١٩٤.
(٧) انظر: الجرح والتعديل ج٢/ق٢/٢٠٠؛ وميزان الاعتدال ج٢/٥٢٥.','3','897',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (846,'٤٠٠- عبد الله بن يزيد بن فنطس الهذلي مديني، قَال عنه: "قَال لي عبد الرحمن بن عبد الملك بن شيبة لا أعلم إلا إني سمعت أبا بكر بن أويس، يقول: عبد الله بن يزيد الهذلي ما بحديثه بأس" (١) .
٤٠١- (ع) عبد الأعلى بن عبد الأعلى بن محمد وقيل ابن شراحيل القرشي البصري السامي من بني سامة أبو محمد، ت١٩٨هـ، قَال عنه: "ثقة" (٢) .
٤٠٢- (خت ت ق) عبد الحميد بن حبيب بن أبي العشرين الدمشقي أبو سعيد البيروتي كاتب الأوزاعي، قَال عنه: "دمشقي ثقة حديثه مستقيم وهو من المعدودين في أصحاب الأوزاعي" (٣) .
٤٠٣- عبد الحكيم بن عبد الله بن أبي فروة، مولى عثمان بن عفان أبو عبد الله، قَال عنه: "لا بأس به" (٤) .
٤٠٤-عبد الرحمن بن إبراهيم، القاري القاص نزيل كرمان، قَال عنه: "لا بأس به أحاديثه مستقيمة" (٥) .
٤٠٥- عبد الرحمن بن بكر، الطبري، الآملي، قَال عنه: "هو من أهل آمل جالس أبا يوسف وهو صدوق" (٦) .
٤٠٦- (بخ دت س ق) عبد الرحمن بن ثابت بن ثوبان شامي، قَال عنه: "شامي لا بأس به" (٧) وقال أيضا "لين" (٨) .
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/١٩٨.
(٢) انظر: الجرح والتعديل ج٣/ق١/٢٨؛ وتهذيب التهذيب ج٦/٩٦.
(٣) انظر: الجرح والتعديل ج٣/ق١/١١؛ واكتفى ابن حجر في تهذيب التهذيب ج٦/ ١١٣بقوله: "ثقة مستقيم الحديث".
(٤) انظر: الجرح والتعديل ج٣/ق١/٣٥.
(٥) انظر: الجرح والتعديل ج٢/ق٢/٢١١؛ وتعجيل المنفعة ص١٦٥.
(٦) انظر: الجرح والتعديل ج٢/ق٢/٢١٧.
(٧) انظر: الجرح والتعديل ج٢/ق٢/٢١٩.
(٨) انظر: تهذيب التهذيب ج٦/١٥١.','3','898',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (847,'٤٠٧- (بخ م٤) عبد الرحمن بن جبير بن نفير الحضرمي، أبو حميد، ويقال أبو حمير الحمص، ت١١٨هـ، قَال عنه: "شامي ثقة" (١) .
٤٠٨- (بخ٤) عبد الرحمن بن جوشن الغطفاني البصري، قال عنه: "بصري غطفاني ثقة" (٢) .
٤٠٩- عبد الرحمن بن الحارث بن أبي عبيد الغفاري، قَال عنه: "لا بأس به" (٣) .
٤١٠- (خ ت) عبد الرحمن بن حماد بن شعيب، ويقال ابن عمارة الشعيثي أبو سلمة العنبري، ت ٢١٢، قال عنه: "بصري لا بأس به" (٤) .
٤١١- عبد الرحمن بن زياد الرصاص روى عن شعبه قَال عنه: "لا بأس به حدثنا عنه الحميدي" (٥) .
٤١٢- (م دت س ق) عبد الرحمن بن سابط، ويقال ابن عبد الله ابن سابط وهو الصحيح، ويقال ابن عبد الله بن عبد الرحمن الجمحي المكي، ت ١١٨ هـ، قال عنه: "مكي ثقة" (٦) .
٤١٣- عبد الرحمن بن أبي سبرة، كوفي والد خيثمة بن عبد الرحمن قَال عنه: "كوفي ثقة" (٧) .
٤١٤- (خ م دتم ق) عبد الرحمن بن سليمان بن عبد الله بن حنظلة
_________
(١) انظر: الجرح والتعديل ج٢/ق٢/٢٢١؛ وتهذيب التهذيب ج٦/١٥٤.
(٢) انظر: الجرح والتعديل ج٢/ق٢/٢٢٠؛ وتهذيب التهذيب ج٦/١٥٥.
(٣) انظر: الجرح والتعديل ج٢/ق٢/٢٢٤.
(٤) انظر: الجرح والتعديل ج٢/ق٢/٢٢٦؛ وتهذيب التهذيب ج٦/١٦٤؛ وميزان الاعتدال ج٢/٥٥٧.
(٥) انظر: الجرح والتعديل ج٢/ق٢/٢٣٥؛ وتهذيب التهذيب ج٦/١٩٠.
