(٨) انظر: تهذيب التهذيب ج٨/ ٢٢٨.','3','921',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (870,'٥٦٣- (بخ) عيسى بن المغيرة بن الضحاك بن عبد الله بن خالد بن حزام الأسدي الخزامي، حجازي، قال عنه: "لا بأس به" (١) .
٥٦٤- (ع) عيسى بن يونس بن أبي إسحاق السبيعي أبو عمر ويقال أبو محمد الكوفي، ت ١٨٧ أو ١٩١ هـ، قال عنه: "حافظ" (٢) .
٥٦٥- (س) غسان بن مضر الأزدي النمري أبو مضر البصري المكفوف ت ١٨٤ هـ، قال عنه: "صدوق" (٣) .
٥٦٦- (دق) فرج بن سعيد بن علقمة بن سعيد بن أبيض بن حمال السبائي المحاربي، أبو روح اليماني، قال عنه: "لا بأس به" (٤) .
٥٦٧- (ع) الفضل بن دكين وهو لقب واسمه عمرو بن حماد بن زهير بن درهم التيمي مولى آل طلحة أبو نعيم الملائي الكوفي الأحول، ت ٢١٨ هـ، قال ابن أبي حاتم "سئل أبو زرعة عن أبي نعيم وقبيصة؟ فقال أبو نعيم أتفن الرجلين" (٥) .
٥٦٨- الفضل بن زياد، الطساس، البغدادي، قال عنه: "كتبت عنه كان يبيع الطساس، شيخ ثقة" (٦) .
٥٦٩- (س ق) الفضل بن عطية بن عمرو بن خالد، المروزي، مولى بني عبس، قال عنه: "لا بأس به" (٧) .
_________
(١) انظر: الجرح والتعديل ج ٣/ ق ١/٢٨٧، وتهذيب التهذيب ج ٨/ ٢٣١.
(٢) انظر: الجرح والتعديل ج ٣/ ق ١/٢٩٢، وتذكرة الحفاظ ص ٢٨٠، وفي تهذبب التهذيب ج ٨/ ٢٣٩ "كان حافظا".
(٣) انظر: الجرح والتعديل ج ٣/ ق ٢/ ٥١؛ وتهذيب التهذيب ج٨/ ٢٤٨.
(٤) انظر: الجرح والتعديل ج ٣/ ق ٢/ ٦٢؛ وتهذيب التهذيب ج ٨/ ٢٦٠.
(٥) انظر: الجرح والتعديل ٣/ ق٣/٢٦٢، وتهذيب التهذيب ٨/ ٢٧٣؛ وقبيصة هو ابن عقبة أبو عامر الذي قال عنه النووي: "كان ثقة صدوقا كثير الحديث عن سفيان الثوري"، روى عنه البخاري ٤٤ حديثا انظر: تهذيب التهذيب ج ٨/ ٣٤٩.
(٦) انظر: الجرح والتعديل ج ٣/ ق ٢/ ٦٢، وفي ميزان الاعتال ج ٣/ ٣٥١، قال: "وثقه أبو زرعة".
(٧) انظر: الجرح والتعديل ج ٣/ ق٢/ ٦٤، وتهذيب التهذيب ج ٨/ ٢٨١، وميزان الاعتدال ج٣/ ٣٥٤.','3','922',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (871,'٥٧٠- (ت) الفضل بن يزيد الثمالي، ويقال البجلي الكوفي، قال عنه: "كوفي ثقة" (١) .
٥٧١- (عب) فطر بن حماد بن واقد، البصري، قال عنه: "ثقة" (٢) .
٥٧٢- فهد من سلام أبو همام المنقري، البصري، قال عنه: "لا بأس به" (٣) .
٥٧٣- (ت) القاسم بن أمية الحذاء العقدي، أبو محمد البصري قال عنه: "كان صدوقا" (٤) .
٥٧٤- (بخ ت) القاسم بن الحكم بن كثيربن جندب بن ربيع بن عمرو بن عبد الله بن إبراهيم العرني أبو أحمد الكوفي قاضي حمدان، ت ٢٠٨ هـ، قال عنه: "صدوق" (٥) .
٥٧٥- القاسم بن سلام بن مسكين الأزدي، أبو محمد، البصري، ت ٢٢٨ هـ، قال عنه: "صدوق" (٦) .
٥٧٦- (بخ م ٤) القاسم بن الفضل بن معدان بن قريط الحداني الأزدي أبو المغيرة البصري، ت ١٦٧ هـ، قال عنه: "أحفظ من أبي هلال الراسبي" (٧) .
_________
(١) انظر: الجرح والتعديل ج ٣/ ق ٢/ ٦٩، وتهذيب التهذيب ج٨/ ٢٨٨.
(٢) انظر: الجرح والتعديل ج ٣/ ق ٢/ ٩٠، وتعجيل المنفعة ص ٢٢٠.
(٣) انظر: الجرح والتعديل ج ٣/ ق ٢/ ٨٩.
(٤) انظر: الجرح والتعديل ج ٣/ ق٢/ ١٠٧؛ وتهذيب التهذيب ج٨/ ٣٠٨.
(٥) انظر: الجرح والتعديل ج ٣/ ق٢/ ١٠٩ وتهذيب التهذيب ج٨/ ٣١٢، وميزان
الاعتدال ج ٣/ ٣٧٠.
(٦) انظر: الجرح والتعديل ج ٣/ ق ٢/ ١١٠، وتهذيب التهذيب ج٨/ ٣١٨.
(٧) انظر: الجرح والتعديل ج ٣/ ق٢/٢١٧، وتهذيب التهذيب ج ٨/ ٣٢٩ و (أبو هلال الراسبي) هو (خت ٤) محمد بن سليم أبو هلال الراسبي البصري المتوفى في آخر ١٦٧ هـ، وقيل قبل ذلك وهو "صدوق، فيه لين"، انظر: تقريب التهذيب ج٢/ ١٦٦، وتهذيب التهذيب ج٨/ ١٩٦.','3','923',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (872,'٥٧٧- القاسم بن محمد، أبو نهيك الأسدي الضبي، الكوفي، قال عنه: "كوفي ثقة" (١) .
٥٧٨- القاسم بن مقلاص، قال عنه: "شيخ" (٢) .
٥٧٩- (ع) قبيصة بن عقبة بن محمد بن سفيان بن عقبة السوائي أبو عامر الكوفي، ت ٢١٥ هـ، قال ابن أبي حاتم "سئل أبو زرعة عن قبيصة وأبي نعيم؟ فقال: كان قبيصة أفضل الرجلين وأبو نعيم أتقن الرجلين" (٣) .
٥٨٠- (ع) قتادة بن دعامة بن قتادة بن عزيز، أبو الخطاب السدوسي البصري، ت ١١٧ هـ، قال عنه: "قتادة من أعلى أصحاب الحسن، قيل له يونس بن عبيد؟ قال ثم يونس" (٤) .
٥٨١- (س) قدامة بن شهاب المازني البصري، قال عنه: "ليس به بأس" (٥) .
٥٨٢- (س) قدامة بن محمد بن خشرم بن يسار الأشجعي المدني، قال عنه: "لا بأس به" (٦) .
٥٨٣- (خت م دت ق) قدامة بن موسى بن عمر بن قدامة بن مظعون الجمحي المكي، ت ١٥٣ هـ، قال عنه: "مكي، جمحي، ثقة" (٧) .
_________
(١) انظر: الجرح والتعدبل ج ٣/ ق٢/ ١١٩.
(٢) انظر: الجرح والتعديل ج ٣/ ق ٢/ ١٢١.
(٣) انظر: الجرح والتعديل ج ٣/ ق٢/١٢٧، وتهذيب التهذيب ج٨/ ٣٤٨، وفي ميزان الاعتدال ج٣/ ٣٨٣ (وأبو نعيم أوثقهما) بدل (اتقن) .
(٤) انظر: الجرح والتعديل ج ٣/ ق٢/ ١٣٥، وفي تهذيب التهذيب ج٨/ ٣٥٥ (قتادة من أعلم أصحاب الحسن) .
(٥) انظر: الجرح والتعديل ج ٣/ ق٢/١٢٨، وتهذيب التهذيب ج ٨/ ٣٦٤.
(٦) انظر: الجرح والتعديل ج ٣/ ق ٢/ ١٢٩، وتهذيب التهذيب ج ٨/ ٣٦٥.
(٧) انظر: الجرح والتعديل ج ٣/ ق ٢/ ١٢٩، وتهذيب التهذيب ج ٨/ ٣٦٦.','3','924',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (873,'٥٨٤- (س) قزعة المكي مولى لعبد القيس، قال عنه: "مكي ثقة" (١) .
٥٨٥- (خ قدس) قطن بن كعب القطعي الزبيدي أبو الهيثم البصري، قال عنه: "بصري ثقة" (٢) .
٥٨٦- (د) قيس بن حبتر التميمي ويقال الربعي، الكوفي، ويقال الأسدي قال عنه "أصله كوفي وكان يكون بالجزيرة ثقة" (٣) .
٥٨٧- (خت م دس ق) قيس بن سعد المكي، أبو عبد الملك، ويقال أبو عبد الله الحبشي، ت ١١٩ هـ، قال عنه: "ثقة" (٤) .
٥٨٨- (ي م س) قيس بن سليم التميمي العنبري الكوفي، قال عنه: "ثقة" (٥) .
٥٨٩- (زدت ق) كثير بن زيد الأسلمي ثم السهمي مولاهم أبو محمد المدني، ت ١٥٨ هـ، قال عنه: "صدوق فيه لين" (٦) .
٥٩٠- (عب) كثير بن يحيى بن كثير، أبو مالك البصري الحنفي، قال عنه: "صدوق" (٧) .
٥٩١- (ي ٤) كليب بن شهاب بن المجنون الجرمي، كوفي قال عنه: "كوفي ثقة" (٨) .
_________
(١) انظر: الجرح والتعديل ج ٣/ ق٢/١٣٩، وتهذيب التهذيب ج٨/٣٧٧؛ وميزان الاعتدال ج ٣/ ٣٩٠.
