INSERT INTO [b11402] VALUES (882,'٦٤٥- محمد بن كليب بن جابر، قال عنه: "مديني ثقة" (١) .
٦٤٦- (ع) محمد بن مسلم بن عبيد الله بن عبد الله بن شهاب القرشي الزهري أبو بكر الفقيه الحافظ، ت١٢٥ أو ١٢٤ هـ، قال ابن أبي حاتم: "سئل أبو زرعة عن الزهري وعمرو بن دينار؟ فقال: الزهري أحفظ الرجلين" (٢) .
٦٤٧- (س) محمد بن مسلم بن عثمان بن عبد الله الرازي أبو عبد الله بن وارة، الحافظ، ت٢٦٥ أو ٢٧٠ هـ، قال ابن أبي حاتم "سمعت منه وهو صدوق ثقة، ووجدت في كتب أبي زرعة بخطه قد كتب عنه ورأيت أبا زرعة يبجله ويكرمه" (٣) .
٦٤٨- (خت م٤) محمد بن مسلم بن أبي الوضاح واسمه المثنى القضاعي أبو سعيد المؤدب الجزري، قال عنه: "بصري ثقة" (٤) .
٦٤٩- (فق) محمد بن مسلم المدني قال عنه: "مدني قدم عليهم البصرة أحاديثه مستقيمة" (٥) .
٦٥٠- (ت ق) محمد بن مصعب بن صدقة القرقساني أبو عبد الله، وقيل أبو الحسن نزيل بغداد، ت ٢٨٠ هـ، قال ابن أبي حاتم: "سئلت أبا زرعة عنه؟ فقال: صدوق في الحديث ولكنه حدث بأحاديث منكرة، قلت: فليس هذا مما يضعفه؟ قال: نظن أنه غلط فيها. قال: وسئلت أبي عنه؟ فقال: ضعيف الحديث ليس بقوي. قلت له: إن أبا زرعة قال كذا. وحكيت له كلامه. فقال: ليس هو عندي كذا، ضعف لما
_________
(١) انظر: الجرح والتعديل ج ٤/ ق١/٦٨.
(٢) انظر: الجرح والتعديل ج ٤/ق١/٧٤.
(٣) انظر: الجرح والتعديل ج٤/ ق١/٧٩ - ٨٠ وتهذيب التهذيب ج ٩/٤٥٢.
(٤) انظر: الجرح والتعديل ج ٤/ق١/٧٧، وتهذيب التهذيب ج٩/٤٥٤.
(٥) انظر: الجرح والتعديل ج ٤/ق١/٧٩ وتهذيب التهذيب ج ٩/٤٥٤.','3','934',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (883,'حدث بهذه المناكير. قال: وقلت لأبي زرعة محمد بن مصعب أحب إليك، أو علي بن عاصم؟ فقال: محمد بن مصعب" (١) .
٦٥١- (ت) محمد بن المعلى بن عبد الكريم الهمداني اليمامي الكوفي سكن بعض قرى الري، قال عنه: "صدوق في الحديث" (٢) .
٦٥٢- (خ م دس) محمد بن المنهال التميمي المجاشعي أبو جعفر، ويقال أبو عبد الله البصري الضرير الحافظ، ت ٢٣١هـ، قال ابن أبي حاتم: "سمعت أبا زرعة يقول: سألت محمد بن المنهال أن يقرأ على تفسير أبي رجاء ليزيد بن زريع فأملى على من حفظه نصفه ثم أتيته يوما آخر بعدكم- وفي نسخة بعد ذلك- فأملى علي من حيث انتهى فقال خذ، فتعجبت من ذلك وكان يحفظ حديث يزيد بن زريع" (٣) .
٦٥٣- (خ م دس ق) محمد بن الوليد بن عامر الزبيدي أبو الهذيل، الحمصي، القاضي، ت ١٤٦ أو ١٤٧ أو ١٤٩هـ، قال عنه: "حمصي قاضي حمص ثقة" (٤) .
٦٥٤- محمد بن يحيى بن زكرياء الإسكندراني الحميري، قال عنه: "مصري، ثقة" (٥) .
٦٥٥- (خ ٤) محمد بن يحيى بن عبد الله بن خالد بن فارس بن ذؤيب
_________
(١) انظر: الجرح والتعديل ج ٤/ ق١/ ١٠٣ وتهذيب التهذيب ج٩/ ٤٥٩ وعلى هو (دت ق) ابن عاصم بن صهيب الواسطي التميمي مولاهم، ت ٢٠١ هـ وهو "صدوق يخطىء ويصرا وقال عنه الفلاس: "علي بن عاصم فيه ضعف وكان إن شاء الله من أهل الصدق" انظر: تقريب التهذيب ج ٢/ ٣٩ وميزان الاعتدل ج ٣/ ١٣٦.
(٢) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٠١ وتهذيب التهذيب ج٩/ ٤٦٦، وفي ميزان الاعتدال ج٤/ ٤٥ (صدوق) وكذا في لسان الميزان ج٦/ ٧٠٧.
(٣) انظر: الجرح والتعديل ج ٤/ ق ١/ ٩٢ وتهذيب ج٩/ ٧٤٦.
(٤) انظر: الجرح والتعديل ج ٤/ ق١/ ١١٢ وتهذيب التهذيب ج٩/ ٥٠٣.
(٥) انظر: الجرح والتعديل ج ٤/ ق١/ ١٢٣.','3','935',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (884,'الذهلي النيسابوري، ت ٢٥٨ هـ، قال عنه "هو إمام من أئمة المسلمين" (١) .
٦٥٦- (س ق) محمد بن يوسف القرشي مولى عثمان وقيل عمرو ابن عثمان مدني، قال عنه "مديني ثقة" (٢) .
٦٥٧- (ع) محمد بن يوسف بن واقد بن عثمان الضبي مولاهم الفريابي، قال أبو زرعة: "نعي إلينا سنة ٢١٢ هـ"، قال ابن أبي حاتم: "سألت أبا زرعة عن الفريابي ويحيى بن يمان؟ فقال: الفريابي أحب إلي" (٣) .
٦٥٨- مالك بن سعد التجيمي، قال عنه: "مصري لا بأس به" (٤) .
٦٥٩- (خ قدت س ق) مالك بن سعير بن الخمس، التميمي، أبو محمد ويقال أبو الأحوص الكوفي، ت ٢٠٠ هـ، قال عنه: "صدوق" (٥) .
٦٦٠- مثنى بن بكر، العبيدي، قال عنه: بصري لا بأس به" (٦) .
٦٦١- (ع) المثنى بن سعيد الضبعي، أبو سعيد البصري القسام الذراع القصير، قال عنه: "ثقة" (٧) .
٦٦٢- المثنى بن عوف، العنزي، أبو منصور البصري، قال عنه: "ليس به بأس" (٨) .
_________
(١) انظر: الجرح والتعديل ج ٤/ ق١/١٢٥.
(٢) انظر: الجرح والتعديل ج ٤/ ق١/ ١١٨.
(٣) انظر: الجرح والتعديل ج ٤/ ق ١/ ١٢٠ وتهذيب التهذيب ج٩/٥٣٦، ويحيى هو (بخ م ٤) ابن يمان العجلي الكوفي المتوفى سنة ١٨٩ هـ، "صدوق عابد يخطىء كثيراً وقد تغير" وقال ابن المديني "صدوق، فلج فتغير حفظه" انظر: تقريب التهذيب ج ٢/ ٣٦١ وميزان الاعتدال ج٤/٤١٦، وفيه في ص ٧١ قال الذهبي عن الفريابي "أحد الأثبات".
(٤) انظر: الجرح والتعديل ج ٤/ ق١/٢٠٩ وتعجيل المنفعة ص ٢٥٣.
(٥) انظر: الجرح والتعديل ج ٤/ق١/٢١٠ وتهذيب التهذيب ج١٠/١٧ وميزان الاعتدال ج٣/٤٢٧.
(٦) انظر: الجرح والتعديل ج٤/ق١/٣٢٦.
(٧) انظر: الجرح والتعديل ج ٤/ ق١/٣٢٤ وتهذيب التهذيب ج١٠/٣٥.
(٨) انظر: الجرح والتعديل ج٤/ق١/٣٢٥ وتعجيل المنفعة ص ٢٥٧. ?','3','936',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (885,'٦٦٣- (ع) مجاهد بن جبر المكي، أبو الحجاج المخزومي المقرئ، مولى السائب بن أبي السائب، ت ١٠١ أو ١٠٢ أو ١٠٣ أو ١٠٤هـ، قال عنه: "مكي ثقة" (١) .
٦٦٤- (ع) محارب بن دثار بن كردوس بن قرواش بن جعونة السدوسي أبو دثار، ويقال أبو مطرف ويقال أبوكردوس ويقال أبو النضر الوفي القاضي وقيل إنه ذهلي، ت ١١٦هـ، قال عنه: "ثقة مأمون" (٢) .
٦٦٥- (خت م دس) محاضر بن الموزع الهمداني اليامي، ويقال السلولي، ويقال السكوني الكوفي، ت ٢٠٦ هـ، قال عنه: "صدوق صدوق" (٣) .
٦٦٦- محرر بن قعنب، الباهلي، قال عنه: "بصري ثقة" (٤) .
٦٦٧- (دعس ق) محفوظ بن علقمة الحضرمي، أبو جنادة، الحمصي، قال عنه: "لا بأس به" (٥) .
٦٦٨- (بخ م ٤) محمود بن لبيد بن عقبة بن رافع الأوسي، الأشهلي، أبو نعيم المدني، ت ٩٦ أو ٩٧ هـ، قال عنه: "مديني أنصاري ثقة" (٦) .
٦٦٩- (عس) مخلد بن مالك بن شيبان القرشي، وقيل السكسكي، أبو محمد الحراني السلميني، ت ٢٤٢ هـ، قال عنه: "لا بأس به" (٧) .
٦٧٠- (دخ ق) مرار بن حموية بن منصور الثقفي أبو أحمد الهمذاني الفقيه
_________
(١) انظر: الجرح والتعديل ج٤/ق١/٣١٩ وتهذيب التهذيب ج١٠/٤٣- ٤٤.
(٢) انظر: الجرح والتعديل ج٤/ق١/٤١٧ وتهذيب التهذيب ج١٠/ ٥٠.
(٣) انظر: الجرح والتعديل ج٤/ق١/٤٣٧ وتهذيب التهذيب ج١٠/٥٢. واكتفى الذهبي في ميزان الاعتدال ج٣/٤٤١ بقوله: (صدوق) .
(٤) انظر: الجرح والتعديل ج ٤/ ق ١/ ٤٠٩.
(٥) انظر: الجرح والتعديل ج ٤/ ق١/٤٢٢ وتهذيب التهذيب ج١٠/ ٥٩.
(٦) انظر: الجرح والتعديل ج ٤/ ق ١/ ٢٩٠.
(٧) انظر: الجرح والتعديل ج ٤/ ق ١/ ٣٤٩ وتهذيب التهذيب ج١٠/٧٦.','3','937',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (886,'الحافظ، ت ٢٥٤ هـ، قال فضلان بن صالح: "قلت لأبي زرعة أنت أحفظ أم المرار؟ فقال: أنا أحفظ والمرار أفقه" (١) .
٦٧١- (خت) مرجى بن رجاء اليشكري، ويقال العدوي أبو رجاء البصري، قال عنه: "بصري ثقة" (٢) .
٦٧٢- (ت) مرزوق أبو بكر الباهلي البصري، مولى طلحة بن عبد الرحمن، قال عنه: "بصري ثقة" (٣) .
٦٧٣- (د) مسحاج بن موسى الضبي، أبو موسى الكوفي، قال عنه: "لا بأس به" (٤) .
٦٧٤- (خ دت س) مسدد بن مسرهد بن مسربل البصري الأسدي أبو الحسن الحافظ، قال عنه: "قال لي أحمد بن حنبل مسدد صدوق فيما كتبت عنه فلا تعده" (٥) .
٦٧٥- (ع) مسعر بن كدام بن ظهيربن عبيدة بن الحارث بن هلال ابن عامر الهلالي أبو سلمة الكوفي العامري، ت ١٥٣ أو ١٥٥هـ، قال أبو زرعة: "سمعت أبا نعيم يقول: مسعر أثبت، ثم سفيان، ثم شعبة" (٦) وقال عنه: "كوفي ثقة" (٧) وقال: "سمعت أبا نعيم يقول كان مسعر شكاكا في حديثه، وليس يخطىء في شيء من حديثه إلا في حديث واحد" (٨) .
_________
(١) انظر: تهذيب التهذيب ج ١٠/ ٨١.
(٢) انظر: الجرح والتعديل ج ٤/ ق ٢/١ ١ ٤ تهذيب التهذيب ج ١٠/٨٣ وأسماء الضعفاء لابن الجوزي وميزان الاعتدال ج ٤/٨٧.
(٣) انظر: الجرح والتعديل ج٤/ق١/ ٢٦٤ وتهذيب التهذيب ج١٠/٨٧ وميزان الاعتدال ج٤/ ٨٨.
