INSERT INTO [b11402] VALUES (896,'الظفري، أبو الوليد الدمشقي، ت ٢٤٥ هـ، قال عنه: "من فاته هشام بن عمار يحتاج أن ينزل في عشرة الآف حديث" (١) .
٧٤٠- (خ ٤) هشام بن يوسف الصنعاني، أبو عبد الرحمن، الأنباري، قاضي صنعاء، ت ١٩٧ هـ، قال عنه: "كان هشام أصح اليمانيين كتابا.
وقال مرة أخرى كان أكبرهم، وأحفظهم، وأتقنهم" (٢) ، وروى أبو زرعة بسنده، إلى عبد الرزاق إنه قال: "إن حدثكم القاضي يعني هشام بن يوسف فلا عليكم أن لا تكتبوا عن غيره" (٣) . وروى بسنده أيضا إليه إنه قال: "قدم الثوري اليمن فقال اطلبوا لي كاتباً سريع الخط، فارتادوني فكنت أكتب" (٤) .
٧٤١- (ع) هشيم بن بشير بن القاسم بن دينار السلمي، أبو معاوية بن أبي خازم الواسطي، ت ١٨٣ هـ، قال ابن أبي حاتم: "سئل أبو زرعة عن جرير وهشيم؟ فقال: هشيم أحفظ" (٥) .
٧٤٢- (م ٤) الهقل بن زياد بن عبيد الله، ويقال ابن عبيد السكسكي، مولاهم أبو عبد الله الدمشقي كاتب الأوزاعي، سكن بيروت، وهقل لقب واسمه محمد، وقيل عبد الله، ت ١٧٩ هـ، قال عنه: "ثقة" (٦) .
٧٤٣- (ع) همام بن يحيى بن دينار الأزدي العوذي، المحلمي، مولاهم أبو عبد الله، ويقال أبو بكر البصري، ت ١٦٣ أو ١٦٤ هـ، قال عنه: "لا بأس به" (٧) .
_________
(١) انظر: تهذيب التهذيب ج١١/٥٤؛ وميزان الاعتدال ج٤/٣٠٣، وانظر: تذكرة الحفاظ ص ٤٥١.
(٢) انظر: الجرح والتعديل ج٤/ق٢/٧١؛ وتهذيب التهذيب ج١١/٥٧، وفي تذكرة الحفاظ ص ٣٤٦، نقل عنه أنه قال: (هشام أصح الناس كتابا) .
(٣) انظر: الجرح والتعديل ج٤/ق٢/٧١.
(٤) انظر: الجرح والتعديل ج٤/ق٢/٧١.
(٥) انظر: الجرح والتعديل ج٤/ق٢/١١٦؛ وتهذيب التهذيب ج١١/٦١.
(٦) انظر: الجرح والتعديل ج٤/ق٢/١٢٣؛ وتهذيب التهذيب ج١١/٦٥.
(٧) انظر: الجرح والتعديل ج٤/ق٢/١٠٩؛ وتهذيب التهذيب ج١١/٦٩؛ وميزان الاعتدال ج ٤/ ٣١٠.','3','948',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (897,'٧٤٤- الهيثم بن حبيب، وهو الهيثم بن أبي الهيثم الصيرفي، الكوفي، أخو عبد الخالق، قال عنه: "ثقة في الحديث، صدوق" (١) .
٧٤٥- (ع) واسع بن حبان بن منقذ بن عمرو بن مالك بن خنساء الأنصاري المدني، قال عنه: "ثقة" (٢) .
٧٤٦- واصل بن عبد الله بن بدر بن واصل بن عبد الله بن سعيد ابن الأطول، الجهني صاحب النبي صلى الله عليه وسلم، أبو الحسن، قال عنه: "صدوق لم أكتب عنه إلا حديثا واحدا" (٣) .
٧٤٧- (م دت س) واقد بن عمرو بن سعد بن معاذ الأنصاري الأشهلي أبو عبد الله المدني، ت ١٢٠ هـ، قال عنه: "ثقة" (٤) .
٧٤٨- (خ م دس) وبرة بن عبد الرحمن المسلي، أبو خزيمة، ويقال أبو العباس الكوفي، ويقال إنه حارثي، ت ١١٦ هـ، قال عنه: "ثقة" (٥) .
٧٤٩- (بخ) ورقاء بن عمر بن كليب اليشكري، ويقال الشيباني أبو بشر الكوفي، نزيل المدائن. يقال أصله من مرو. قال ابن أبي حاتم: "سألت أبازرعة فقلت: ورقاء أحب إليك أو شعيب بن أبي حمزة أو عبد الرحمن بن أبي الزناد أو المغيرة بن عبد الرحمن؟ فقال: ورقاء أحب إلي منهم" (٦) .
٧٥٠- (ع) الوضاح بن عبد الله اليشكري مولى يزيد بن عطاء أبو عوانة الواسطي البزاز، ت ١٧٦ هـ، قال عنه: "ثقة إذا حدث من كتابه" (٧) .
_________
(١) انظر: الجرح والتعديل ج٤/ق٢/٨١؛ وتهذيب التهذيب ج١١/٩٢.
(٢) انظر: الجرح والتعديل ج٤/ق٢/٤٨ وتهذيب التهذيب ج١١/١٠٢.
(٣) انظر: الجرح والتعديل ج٤/ق٢/٣١.
(٤) انظر: الجرح والتعديل ج٤/ق٢/٣٢؛ وتهذيب التهذيب ج١١/١٠٧.
(٥) انظر: الجرح والتعديل ج٤/ق٢/٤٢ وتهذيب التهذيب ج١١/١١١.
(٦) انظر: الجرح والتعديل ج٤/ق٢/٥١؛ وتهذيب التهذيب ج١١/١١٥.
(٧) انظر: الجرح والتعديل ج٤/ق٢/٤١؛ وتهذيب التهذيب ج١١/١١٨.','3','949',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (898,'٧٥١- (ق) وكيع بن محرز بن وكيع الناجي السامي النبال البصري، قال عنه: "لا بأس به" (١) .
٧٥٢- (بخ م دت س) الوليد بن عبد الله بن جميع الزهري، المكي، الكوفي، قال عنه: "لا بأس به" (٢) .
٧٥٣- (د) الوليد بن عقبة بن المغيرة ويقال ابن كثير الشيباني، أبو الحسن، ويقال أبو عبد الله الكوفي، الطحان، قال عنه: "لا بأس به" (٣) .
٧٥٤- (ع) الوليد بن مسلم القرشي، مولى بني أمية وقيل مولى بني العباس، أبو العباس الدمشقي، ت ١٩٥ هـ، قال عنه: "كان الوليد أعلم من وكيع بأمر المغازي" (٤) .
٧٥٥- (عخ م ٤) الوليد بن أبي الوليد عثمان وقيل ابن الوليد مولى
عثمان أو ابن عمر المدني، أبو عثمان، قال عنه: "ثقة" (٥) .
٧٥٦- (خ م دت س فق) وهب بن منبه بن كامل بن سيح اليماني الصنعاني، الذماري، أبو عبد الله الأنباري، ت ١١١ أو ١١٣ أو ١١٤ هـ، قال عنه: "ثقة" (٦) .
٧٥٧- (ع) لاحق بن حميد بن سعيد ويقال شعبة بن خالد بن كثير السدوسي، أبو مجلز البصري، ت ١٠٦ أو ١٠٩ هـ، قال عنه: "ثقة" (٧) .
_________
(١) انظر: الجرح والتعديل ج٤/ق٢/٣٧ وتهذيب التهذيب ج١١/١٣١.
(٢) انظر: الجرح والتعديل ج٤/ق٢/٨ وتهذيب التهذيب ج١١/١٣٨؛ وميزان الاعتدال ج٤/ ٣٣٧.
(٣) انظر: الجرح والتعديل ج٤/ق٢/١٢ وتهذيب التهذيب ج١١/١٤٤.
(٤) انظر: تهذيب التهذيب ج١١/١٥٣.
(٥) انظر: الجرح والتعديل ج٤/ق ٢/ ٢٥.
(٦) انظر: الجرح والتعديل ج٤/ق٢/٢٤ وتهذيب التهذيب ج١١/١٦٧.
(٧) انظر: الجرح والتعديل ج ٤/ق٢/١٢٤ وتهذيب التهذيب ج ١١/١٧٢ وميزان الاعتدال ج٤/ ٣٥٦.','3','950',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (899,'٧٥٨- (ق) ياسين بن شيبان ويقال ابن سنان العجلي، الكوفي، قال عنه: "لا بأس به" (١) .
٧٥٩- (م ٤) يحيى بن الجزار العرني، الكوفي، لقبه زيان، قال عنه: "ثقة" (٢) .
٧٦٠- (دت ق) يحيى بن أبي حية، أبو جناب الكلبي، الكوفي، واسم أبي حية حي، ت ١٥٥ هـ، أو قبلها، قال عنه: "صدوق، غير إنه كان يدلس" (٣) .
٧٦١- يحيى بن أبي الخصيب وهو يحيى بن زياد الرازي. قاضي عكبرا، قال عنه: "ثقة، كان مشهوراً يعرفه أحمد بن حنبل وعلي بن المديني، وأصحابنا" (٤) .
٧٦٢- (د) يحيى بن راشد بن مسلم، ويقال ابن كنانة الليثي، أبو هاشم الدمشقي الطويل، قال عنه: "ثقة" (٥) .
٧٦٣- (ع) يحيى بن سعيد بن القطان التميمي أبو سعيد البصري الأحول، ت ١٩٨ هـ، قال عنه: "كان من الثقات الحفاظ" (٦) .
٧٦٤- (ع) يحيى بن سعيد بن قيس بن عمرو بن سهل بن ثعلبة الأنصاري، البخاري أبو سعيد المدني القاضي، ت ١٤٤ هـ، أو بعدها، قال عنه: "ثقة" (٧) .
_________
(١) انظر: الجرح والتعديل ج٤/ق٢/٣١٢؛ وتهذيب التهذيب ج١١/١٧٣.
(٢) انظر: الجرح والتعديل ج٤/ق٢/١٣٣؛ وتهذيب التهذيب ج١١/١٩٢.
(٣) انظر: الجرح والتعديل ج٤/ق٢/١٣٩؛ وتهذيب التهذيب ج١١/٢٠٢، والتبين لأسماء المدلسين ص ١٩؛ وميزان الاعتدال ج٤/٣٧١.
(٤) انظر: الجرح والتعديل ج٤/ق٢/١٤٧.
(٥) انظر: الجرح والتعديل ج٤/ق٢/١٤٢؛ وتهذيب التهذيب ج١١/٢٥٦؛ وميزان الاعتدال ج٤/٣٧٣.
(٦) انظر: الجرح والتعديل ج٤/ق٢/١٥١؛ وتهذبب التهذيب ج١١/٢١٩.
(٧) انظر: الجرح والتعديل ج٤/ق٢/١٤٩؛ وتهذيب التهذيب ج١١/٢٢٣.','3','951',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (900,'٧٦٥- (م دس ق) يحيى بن عبيد البهراني الكوفي، قال عنه: "ليس به بأس" (١) .
٧٦٦- (عب) يحيى بن عثمان أبو زكرياء الحربي، البغدادي، أصله من سجستان، ت ٢٣٨ هـ، قال عنه: "ثقة كتبنا عنه ببغداد، كتب عنه أحمد بن حنبل، ويحيى بن معين" (٢) .
٧٦٧- (بخ م مد ت س ق) يحيى بن محمد بن قيس المحاربب، أبو زكير البصري، الضرير، مدني كنيته أبو محمد، وأبو زكير لقب، قال عنه: "أحاديثه متقاربة إلا حديثين حدث بهما" (٣) .
٧٦٨- يحيى بن مسلم الهمداني، أبو الضحاك الكوفي، قال عنه: "لا بأس" (٤) .
٧٦٩- يحيى بن مصعب، أبو زكرياء الكلبي الكوفي جار الأعمش، قال عنه: "صدوق" (٥) .
٧٧٠- (خ دت س) يحيى بن موسى بن عبد ربه بن سالم الحداني، أبو زكرياء البلخي السختياني، المعروف بخت، ت ٢٤٠ هـ، قال عنه: "ثقة" (٦) .
_________
(١) انظر: الجرح والتعديل ج٤/ق٢/١٧٢؛ وتهذيب التهذيب ج١١/ ٢٥٤.
(٢) انظر: الجرح والتعديل ج٤/ق٢/١٧٤ وتعجيل المنفعة ص ٢٩٢، وفي تهذيب التهذيب ج١١/٢٥٦، اكتفى بقوله (ثقة) وقال الذهبي في ميزان الاعتدال ج٤/٣٩٦ (وثقه أبو زرعة) .
(٣) انظر: الجرح والتعديل ج ٤/ق٢/١٨٥؛ وتهذيب التهذيب ج ١١/٢٧٥؛ وميزان الاعتدال ج٤/٤٠٥.
(٤) انظر: الجرح والتعدبل ج٤/ق٢/١٨٧ وتهذيب التهذيب ج١١/ ٢٧٩؛ وميزان الاعتدال ج٤/٤٠٩.
(٥) انظر: الجرح والتعديل ج٤/ق٢/١٩٠.
(٦) انظر: الجرح والتعديل ج٤/ق٢/١٨٨؛ وتهذيب التهذيب ج١١/٢٩٠؛ وتذكرة الحفاظ ص ٤٧٧.','3','952',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (901,'٧٧١- (خ م ت س ق) يحيى بن وثاب الأسدي مولاهم الكوفي، المقرئ، ت ١٠٣ هـ، قال عنه: "ثقة" (١) .
٧٧٢- يحيى بن يحيى النيسابوري، روى عن الليث بن سعد، قال عنه: "هو ثقة عندي كما يكون" (٢) ، وقال أيضأ "سمعت أحمد بن حنبل، وذكر يحيى بن يحيى النيسابور فذكر من فضله وإتقانه أمرا عظيما" (٣) .
٧٧٣- (أ) يحيى بن يزيد بن عبد الملك النوفلي، قال عنه "لا بأس به، إنما الشأن في أبيه، بلغني عن أحمد بن حنبل إنه قال: يحيى بن يزيد لا بأس به، ولم يكن عنده إلا حديث أبيه، ولو كان عنده غير حديث أبيه لتبين أمره" (٤) .
٧٧٤- (ع) يحيى بن يعمر البصري، أبو سليمان، ويقال أبو سعيد، ويقال أبو عدي القيسي الجدلي قاضي مرو، ت ١٢٠ أو ١٢٩ هـ، قال عنه: "ثقة" (٥) .
٧٧٥- (خ ق) يحيى بن يوسف بن أبي كريمة الزمي، أبو يوسف، ويقال أبو زكرياء الخراساني، ت ١٢٩ هـ، قال عنه: "ثقة" (٦) .
_________
(١) انظر: الجرح والتعديل ج٤/ق٢/١٩٣؛ وتهذيب التهذيب ج١١/٢٩٥.
(٢) انظر: الجرح والتعديل ج٤/ق٢/١٩٧؛ والانتقاء ص ٦٣.
(٣) انظر: الجرح والتعديل ج٤/ق٢/١٩٧؛ والانتقاء ص ٦٣.
(٤) انظر: الجرح والتعديل ج٤/ق٢/١٩٨ وتعجيل المنفعة ص ٢٩٤، وقال ابن حجر (وقد وجدنا له حديثا آخر رواه عن أبي عبادة الزرقي عن سهل بن عبيد في الدعاء في صلاة الجنازة أخرجه الطبراني وابن مندة في الصحابة.
(٥) انظر: الجرح والتعديل ج٤/ق٢/١٩٦؛ وتهذيب التهذيب ج١١/٣٠٥؛ وميزان الاعتدال ج٤/٤١٦.
(٦) انظر: الجرح والتعديل ج٤/ق٢/١٩٦؛ وتهذيب التهذيب ج١١/٣٠٨؛ وتاريخ بغداد ج١٤/١٦٧.','3','953',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (902,'٧٧٦- (ع) يزيد بن إبراهيم التستري أبو سعيد نزيل البصرة، ت١٦٣هـ، أوقبلها، قال عنه: "ثقة" (١) .
٧٧٧- (بخ م ٤) يزيد بن الأصم بن عبيد بن معاوية أبو عوف البكائي الكوفي، ت ١٠٣ هـ، قال عنه: "ثقة" (٢) .
٧٧٨- (دس ق) يزيد بن أمية أبو سنان الد! ؤلي المدني، ت بين ٨٥-٩٠ هـ، قال عنه: "ثقة" (٣) .
٧٧٩- يزيد بن بريد العوذي، قال عنه: "هو شيخ متعبد محله الصدق" (٤) .
٧٨٠- (ع) يزيد بن أبي حبيب واسمه سويد الأزدي مولاهم أبو رجاء المصري، ت ١٢٨ هـ، قال عنه: "مصري ثقة" (٥) .
٧٨١- (ع) يزيد بن حميد أبو التياح الضبعي البصري، ت ١٢٨ هـ، قاق عنه: "ثقة" (٦) .
٧٨٢- (عخ س ق) يزيد بن زياد بن أبي الجعد الأشجعي الغطفاني الكوفي، قال عنه: "شيخ" (٧) .
_________
(١) انظر: الجرح والتعديل ج٤/ق٢/٢٥٣؛ وتهذيب التهذيب ج١١/٣١٢ وهدي الساري ص ٤٥٢.
(٢) انظر: الجرح والتعديل ج٤/ق٢/٢٥٢؛ وتهذيب التهذيب ج١١/ ٣١٤.
(٣) انظر: الجرح والتعديل ج٤/ق٢/٢٥١؛ وتهذيب التهذيب ج١١/ ٣١٤.
(٤) انظر: الجرح والتعديل ج٤/ق٢/٢٥٤.
(٥) انظر الجرح والتعديل ج٤/ق٢/٢٦٧؛ وتهذيب التهذيب ج١١/٣١٩.
(٦) انظر: الجرح والتعديل ج٤/ق٢/٢٥٦؛ وتهذيب التهذيب ج١١/٣٢٠.
(٧) انظر: الجرح والتعديل ج٤/ق٢/٢٥٦؛ وتهذيب التهذيب ج١١/٣٢٠.','3','954',NULL,NULL,'0');
INSERT INTO [b11402] VALUES (903,'٧٨٣- (بخ ٤) يزيد بن أبي سعيد النحوي، أبو الحسن القرشي مولاهم المروزي ت ١٣١ هـ، قال عنه: "يزيد النحوي ثقة" (١) .
٧٨٤- (د) يزيد بن أبي سمية، أبو صخر الأيلي قال عنه: "روى حديثين، وهو ثقة" (٢) .
٧٨٥- يزيد بن أبي صالح، أبو حبيب الدباغ، قال عنه: "لا بأس به" (٣) .
٧٨٦- (خ م دس ق) يزيد بن صهيب الفقير، أبو عثمان الكوفي، قال عنه- "كوفي ثقة" (٤) وقال أيضا "يكتب حديثه" (٥) .
٧٨٧- (دس ق) يزيد بن عبد الرحمن بن أبي مالك اسمه هانىء الهمداني، ت ٩٣٥ هـ، أو بعدها، قال ابن أبي حاتم: "سئل أبو زرعة عنه فأثنى عليه خيرا" (٦) .
٧٨٨- (خ ٤) يزيد بن أبي مريم ويقال يزيد بن ثابت بن أبي مريم ابن أبي عطاء، أبو عبد الله الدمشقي، ت ١٤٤ هـ، قال عنه: "لا بأس به" (٧) .
٧٨٩- يزيد بن معاوية أبو شيبة، كوفي، قال عنه: "صالح" (٨) .
_________
