نقيوس ٢٠٢، ٢٠٤
نهر البلاء ٢٢٨
النوبة ١٩٧، ٢١٥، ٢١٦
النيل ٢٥، ٢٩، ٣٥، ٣٦، ٤٢، ٤٧، ٥٢، ٨٦، ٨٧، ١١٥، ١٥٤، ١٥٥، ١٧٥، ١٧٦، ١٧٧، ١٨٥، ١٩٠، ١٩١، ١٩٧، ٢٠٣، ٢١٧
حرف الهاء
الهند ١٩، ٢٨
حرف الواو
وادى أم حكيم ٢٣٤
وادى السباع ٢٩٢
وادى هبيب ١١٨',NULL,'485',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (479,'واسط ١٦٠
واق ١٩
واق واق ١٩
ودان ٢٢٢
وسيم ١٦٨، ٢٦٦، ٣٥٠
حرف الياء
ياق ٢٣
يثرب ١٨٧
اليحموم ١٨٤، ١٨٥
أبو يحنس ٩٥، ٩٦
اليدقون ١٦٨
اليمن ١٩، ٨٦، ١٢٧، ١٥٤، ١٥٥، ٢٨٦، ٣١١، ٣٤٩
اليهود ٢١، ١٤١، ١٨٧، ٣١٣، ٣٢٨',NULL,'486',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (480,'<span data-type="title" id=toc-138>(٤) فهرس الآيات القرآنية</span>
مرتبة حسب ورودها في الكتاب
الصفحة اسم السورة
٢٤ آمَنَّا بِرَبِّ الْعالَمِينَ، رَبِّ مُوسى وَهارُونَ
(سورة الأعراف ١٢١، ١٢٢)
٢٥ أَلَيْسَ لِي مُلْكُ مِصْرَ وَهذِهِ الْأَنْهارُ تَجْرِي مِنْ تَحْتِي
(سورة الزخرف ٥١)
٢٦ كَمْ تَرَكُوا مِنْ جَنَّاتٍ وَعُيُونٍ
(سورة الدخان ٢٥)
٤١ يا هامانُ ابْنِ لِي صَرْحاً
(سورة غافر ٦٠)
٤٣ و ٤٦ إِنَّ هؤُلاءِ لَشِرْذِمَةٌ قَلِيلُونَ
(سورة الشعراء ٥٤)
٤٣ و ٤٦ فَاسْتَخَفَّ قَوْمَهُ فَأَطاعُوهُ
(سورة الزخرف ٢٤)
٤٤ فَأَتْبَعُوهُمْ مُشْرِقِينَ
(سورة الشعراء ٦٠)
٤٤ اضْرِبْ بِعَصاكَ الْبَحْرَ
(سورة الشعراء ٦٣)
٤٤ وَاتْرُكِ الْبَحْرَ رَهْواً
(سورة الدخان ٢٤)
٥٤ و ٦٥ الم غُلِبَتِ الرُّومُ
(سورة الروم ١، ٢)
٦٧ يا أَهْلَ الْكِتابِ تَعالَوْا
(سورة آل عمران ٦٤)
٩٠ و ٢١٨ كَمْ مِنْ فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً
(سورة البقرة ٢٤٩)
١٨٣ قاتِلُوا الَّذِينَ لا يُؤْمِنُونَ بِاللَّهِ
(سورة التوبة ٢٩)
١٨٣ فَإِنْ تابُوا وَأَقامُوا الصَّلاةَ
(سورة التوبة ٥)
٢٩٧ فَاذْهَبْ أَنْتَ وَرَبُّكَ فَقاتِلا
(سورة المائدة ٢٤)
٢٩٧ كَما أَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِ
(سورة الأنفال ٥)
٢٩٧ وَإِذْ يَعِدُكُمُ اللَّهُ إِحْدَى الطَّائِفَتَيْنِ
(سورة الأنفال ٧)',NULL,'487',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (481,'٢٩٨ وَما رَمَيْتَ إِذْ رَمَيْتَ
(سورة الأنفال ١٧)
٢٩٨ ما كانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرى
(سورة الأنفال ٦٧)
٢٩٩ وَأَنْفِقُوا فِي سَبِيلِ اللَّهِ
(سورة البقرة ١٩٥)
٣١١ رِجالٌ لا تُلْهِيهِمْ تِجارَةٌ
(سورة النور ٣٧)
٣١١ يَضْرِبُونَ فِي الْأَرْضِ يَبْتَغُونَ مِنْ فَضْلِ اللَّهِ
(سورة المزمل ٢٠)
٣٥٢ يا أَيُّهَا الَّذِينَ آمَنُوا لا تَسْئَلُوا عَنْ أَشْياءَ
(سورة المائدة ١٠١)',NULL,'488',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (482,'<span data-type="title" id=toc-139>(٥) فهرس الأحاديث الشريفة</span>
مرتبة علي الأحرف حسب ورودها في الكتاب
أ
الصفحة
إذا افتتحتم مصر فاستوصوا بالقبط خيرا ١٩ و ١٣٥
إنكم ستفتحون أرضا يذكر فيها القيرط ٢٠ و ١٨٠ و ٣١٦
إن الله سيفتح عليكم بعدى مصر ٢٠ و ١٦٧
إنكم ستكونون أجناد او إن خير أجنادكم أهل الغرب منكم ٢١
الله الله فى قبط مصر ٢١
استوصوا بالقبط خيرا ٢١
الله الله فى أهل الذمة أهل المدرة السوداء ٢٢
استوصوا بالأدم الجعد ٢٢
إنكم ستقدمون على قوم جعد رءوسهم ٢٢
الله الله فى أهل الذمة ٢٢
إن إبراهيم قدم أرض جبار ومعه سارة ٣٠
إن شئتم أخبرتكم عما أردتم أن تسألونى قبل أن تتكلموا ٥٩
إن أول أمره أنه غلام من الروم ٥٩
إن جبريل أتانى فأخبرنى أن الله قد برأها ٧٠
إنها رحمة وأتبعها بالأخرى ٧١
أعتقها ولدها ٧٢
إن له ظئرا فى الجنة ٧٢',NULL,'489',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (483,'أما إنما لا تضر ولا تنفع ٧٢
إن الشمس والقمر آيتان من آيات الله ٧٢
إن له مرضعا فى الجنة ٧٣
إن الله قد زادكم صلاة فصلوها فيما بين صلاة العشاء إلى صلاة الصبح ١٢١ و ٣١٤ و ٣١٥
إن الله قد أمدكم بصلاة هى خير لكم من حمر النعم ١٣٠ و ٢٨٨
اسكن حراء ١٣٧ و ٢٩٠
إنا ركبون غدا إلى يهود ١٤١ و ٣١٣ و ٣٢٨
إن هذه الصلاة عرضت على من كان قبلكم ١٤١
إذا توضأت وأنا جنب أكلت وشربت ١٤٨
أكثر القبائل فى الجنة مذحج ١٥٣
ألا أخبركم بخير قبائل؟ قلوا: بلى ١٥٥
اللهم اغفر للسكاسك ١٥٥
أوصى بك كل مسلم ١٦٤ و ٣٣٦
أوصى بك كل مؤمن ١٦٤
أيما مملوك مثل به فهو حر ١٦٤
أسلم سالمها الله ١٦٥ و ٣٣٥
إذا فتح الله عليكم مصرفا تخذوا فيها جندا كثيفا ١٦٧
إذا حكم الحاكم فاجتهد ٢٥٥
إذا قضى القاضى فاجتهد ٢٥٦
اقض بينهما ١٥٦
إذا لقيتم عشارا فاقتلوه ٢٥٩ و ٣٤٢',NULL,'490',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (484,'إن الإسلام يجب ما كان قبله ٢٨٠
إن ربى حرم علىّ الخمر والميسر ٢٨٦ و ٣٠٣
اللهم إنهم حفاة فاحملهم ٢٨٧
إن الذي يمر بين يدى أخيه وهو يصلى متعمدا ٢٨٧
اقرأ ثلاثا من ذات حم ٢٨٧
اقرأ ثلاثا من ذات الراء ٢٨٧
إن أشد الناس عليكم ٢٨٩
إن الله لعن الخمر وشاربها ٢٩٣
ائتونى بشفرة أو مدية ٢٩٣
اتركوا الترك ما تركوكم ٢٩٥
إن للمسلم على أخيه المسلم ست خصال واجبة ٢٩٩
إيمان بالله وتصديق وجهاد فى سبيله ٣٠٠
إنه لا يقام لى إنما يقام لله ٣٠٢
إن الله حرم الخمر والكوبة والقنين وإياكم والغبيراء ٣٠٣
إن الجود من شيمة أهل ذلك البيت ٣٠٣
اللهم لا يدركنى زمان ولا أدركه لا يتّبع فيه العليم ٣٠٥
اللهم علمه الكتاب ومكن له فى البلاد ٣٠٦
ألا أخبركم بالمؤمن؟ ٣٠٧
أنا الزعيم لمن آمن بى وأسلم بيت فى ربض الجنة ٣٠٨
إنه بلغنى أنكم تتبايعون المثقال بالنصف ٣٠٩
الإيمان يمان والفقه يمان ٣١١
إياكم والخيل المنفلة ٣١١',NULL,'491',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (485,'إذا صلى أحدكم فلا يفترش يديه افتراش الكلب ٣١٢
إن هذه الصلاة عرضت على من كان قبلكم فتوانوا عنها وتركوها ٣١٣
ائتنى بفلانة ٣١٤
إن الكافر يأكل فى سبعة أمعاء ٣١٤
إذا أحب أحدكم صاحبه فليأته فى منزله ٣١٥
اعقل ما أقول لك ٣١٥
الأئمة المضلين ٣١٦
إنى أراك ضعيفا وإنى أحب لك ما أحب لنفسى ٣١٦
إن نزلتم بقوم فأمروا لكم بما ينبغى للضيف فاقبلوا ٣١٩
إن كنتم تحبون حلية الجنة وحريرها فلا تلبسوهما فى الدنيا ٣٢٢
إنه أواه ٣٢٣
إنى قد علمت أنه قد رابكم طول قيامى ٣٢٣
الله أكثر وأطيب ٣٢٨
اركبوا هذه الدواب سالمة وايتدعوها سالمة ٣٢٨
أفضل الفضائل أن تصل من قطعك ٣٢٨
أن تحب لله وتبغض لله ٣٣٠
إن لله عبادا لا يكلمهم الله يوم القيامة ولا يزكيهم ٣٣٠
أتدرى بكم سبقك أصحابك؟ ٣٣١
أكثرهم لله ذكرا ٣٣١
أطابت برمتك؟ ٣٣٣
إن فى النار لحيات أمثال أعناق البخت ١٣٤
إن الله أعد لعباده الصلحين ما لا عين رأت ٣١٤',NULL,'492',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (486,'أوفاهما وأبرهما ٣٣٧
إن موسى لما أراد فراق شعيب ٣٣٧
إن كان شفاء ففى شربة من عسل ٣٤١
إنما أخاف على أمتى من ثلاثة أشياء ٣٤٣
أكذلك؟ ٣٤٥
إن الصدقة صداع وحريق فى البطن ٣٤٥
اذهب فردهم
(ب)
بايع يا عمرو، فإن الإسلام يجب ما كان قبله ٢٨١
بلى: ولكنى قئت ٣٠٨
بع سرّقا ٣٢٧
(ت)
تمنّ ما شئت فإنك لن تتمنى اليوم شيئا إلا أعطيتكه ٤٢
تخرج ناس يمرقون من الدين كما يمرق السهم من الرمية ١٣٤ و ٣٣٧
تعلموا القرآن واقتنوه ٣٢٣
(ث)
ثلاثة إذا أنا فعلتهن فما أبالى ما ركبت ٢٨٣
ثلاثة لا تسأل عنهم: رجل فارق الجماعة ٣٠٩
(ج)
جئتم تسألوننى عن ذى القرنين ٥٩
(ح)
الحكام ثلاثة ٢٥٤',NULL,'493',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (487,'(خ)
خذ عليك ثيابك وسلاحك ٢٧٨
الخبث سبعون جزءا ٣١٨
(ذ)
الذهب بالذهب وزنا بوزن ٣٠٧
(ر)
رب الدابة أحق بصدر دابته ١٢٣
رباط يوم فى سبيل الله خير من صيام شهر وقيامه ٢٨٥
رأيت فى منامى أن عمود الكتاب حمل من تحت رأسى ٢٩٦
رويفع، لعله سيطول بك العمر ٣١٠
رحم الله عمرا ٣٣٥
روحة أو غدوة فى سبيل الله خير من الدنيا وما فيها ٣٤٠ و ٣٤١
(س)
السلام عليكم ٤١
سيصاح برجل من أمتى على رءوس الخلائق ٢٨٣
سوّوا قبوركم بالأرض ٣٠٨
سبحان الله! لا من الله استحيوا، ولا من رسوله استتروا ٣٣٤
(ش)
شقّوها على ما فيها من غضب الله ٢٩٣
الشهداء أربعة ٣٠٦
شرّ ما فى الرجل شح هالع ٣١١
(ص)
صلاة الخمس ٢٨٧',NULL,'494',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (488,'صاحب الدابة أولى بصدرها ٣٠٢
(ض)
ضيف ٤١
ضحّ به أنت ٣١٩
الضاحك فى الصلاة والملتفت والمفقّع أصابعه بمنزلة واحدة ٣٢٩
(ط)
طوبى لمن هدى إلى الإسلام ٣٠٧
(ع)
العلم ثلاثة وما سوى ذلك فضل ٢٨٤
عشر حسنات ٣٢٨
العاص! أنتم عبد الله، انزلوا ١١٨، ٣٣٢
عليكم بالقرآن ٣٣٩
(غ)
غير الدّجّال أتخوّف على أمتى ٣١٦
(ف)
فكيف أم فلان؟ ٤١
فصل ما بين صيامنا وصيام أهل الكتاب ١٢٢، ٢٧٨
فلعلك بلغت معهم الكدى ٢٨٨
الفأر من الطاعون ٣٠٤
(ق)
قبط مصر فإنهم أخوال وأصهار ٢٢
قولا نشهد أن لا إله إلا الله ٦٩',NULL,'495',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (489,'القضاة ثلاثة ٢٥٤
قص شاربك ٢٨٧
قد علمت لم نظر بعضكم إلى بعض ٢٩٤
قتل رجل تسعة وتسعين ٣٣٨
(ك)
كيف رأيت الإمارة أبا معبد؟ ١٢٦
الكافر يأكل فى سبعة أمعاء ١٤٢
كتب الله مقادير الخلائق قبل أن يخلق السماوات والأرض ٢٨٤
كلوا وأبى أن يأكله ٢٩٩
كيف ترى جعيلا؟ ٣١٦
كفارة النذر كفارة اليمين ٣١٩
(ل)
لما أغرق الله آل فرعون قال فرعون: آمنت بالذى آمنت به بنو إسرائيل ٤٥
لم فعلت؟ فكل ما دون العشر بضع ٥٥
لو بقى إبراهيم ما تركت قبطيا إلا وضعت عنه الجزية ٧٤
لو كنت مكانك فعلت مثل الذي فعلت ٢٧٧
لعمل شعيرة اليوم خير من مثقال قيراط بعد اليوم ٢٧٨
ليؤيدن الله الإسلام برجال ما هم من أهله ٢٨٤
لله أضنّ بدم المؤمن من أحدكم ٢٨٥
لو بلغت معهم الكدى ما رأيت الجنة ٢٨٨
لكل أمة أجل ٢٩٠
لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا ٣١٢',NULL,'496',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (490,'لو كان بعدى نبى لكان عمر بن الخطاب ٣٢٠
لو جعل القرآن فى إهاب ثم ألقى فى النار ما احترق ٣٢٠
لتحج راكبة مختمرة ولتصم ٣٢٦
لن تقرأ أبلغ عند الله من قل أعوذ برب الفلق ٣٢٦
لو ددت أنّ بينى وبين أهل نجران حجابا ٣٣٤
(م)
ما حاجتك؟ ٤١
مالى ولهم يسألوننى عما لا أدرى ٥٩
ملك مسح الأرض من تحتها ٦٠
من استنجى برجع دابته ٨٥
ما اسمك؟ ١١٨، ٣٣٢
من افترى علىّ كذبا فليتبوأ بيتا أو مقعدا من النار ١٤٨
من جعل قاضيا فقضى بين الناس فقد ذبح بغير سكين ٢٥٤
من طلب القضاء واستعان عليه وكل إليه ٢٥٦
ما من قوم يظهر فيهم الربا إلا أخذوا بالفناء ٢٧٧
من أطعم أخاه من الخبز حتى يشبعه ٢٨٢
من علم علما فكتمه ٢٨٤
ما من غازية تغزو فى سبيل الله ٢٨٥
مدينة هرقل ٢٨٥
من صمت نجا ٢٨٦
من مات وهو مشرك فلا تسل عنه ٢٩٠
من ولى لنا عملا ولم يكن له خادم فليكتسب خادما ٢٩٠',NULL,'497',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (491,'من كان عنده منها شىء فليؤذنىّ به ٢٩٣
من لم يقبل رخصة الله ٢٩٤ و ٣٢٤
من فرّق بين والدة وولدها ٢٩٩
ما من نفس تموت ٣٠٠
من كذب علىّ كذبة متعمدا فليتبوأ بيتا من النار ٣٠٣
من صام رمضان وأتبعه ستا من شوال ٣٠٤
من ستر مسلما ستره الله ٣٠٤
من كان فى المسجد ينتظر الصلاة فهو فى صلاة ٣٠٥
من وجد مسلما على عورة فستره ٣٠٥
من مات على مرتبة من هذه المراتب بعث عليها يوم القيامة ٣٠٨
المجاهد من جاهد نفسه ٣٠٨
من كان يؤمن بالله واليوم الآخر فلا يسق ماءه ولد غيره ٣٠٩
من كان يؤمن بالله واليوم الآخر فلا يركب دابة من المغانم ٣٠٩
من ردّته الطّيرة عن شىء فقد فارق الشرك ٣١٠
من صلى على محمد ٣١٠
مثل الذي يتعلّم ولا يعلّم ٣١٢
من القوم؟ ٣١٤
من تقرب إلى الله شبرا ٣١٥
ما أحبّ أن لى هذا الجبل ذهبا ٣١٧
من جرّ إزاره خيلاء وطئه فى النار ٣١٧
من علّق تميمة فلا أتمّ الله له ٣٢١
من كان له ثلاث بنات فصبر عليهن ٣٢١',NULL,'498',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (492,'من توضأ فجمع عليه ثيابه ٣٢١
من كان هاهنا من معدّ فليقم ٣٢١
من قال علىّ ما لم أقل فليتبوّأ بيتا فى جهنم ٣٢٢
من توضأ فأحسن وضوءه ٣٢٣
المؤمن أخو المؤمن ٣٢٤
الميّت من ذات الجنب شهيد ٣٢٤
من علم الرمى ثم تركه فليس منا ٣٢٤
من كذب علىّ كذبة متعمدا فليتبوأ مقعده من النار ٣٢٦
من قرأ قل هو الله أحد عشر مرات ٣٢٨
من ثبت فى مصلاه ٣٢٩
من كان صائما وعاد مريضا ٣٢٩
من حرّس ليله فى سبيل الله ٣٢٩
من أكل طعاما فقال: الحمد لله ٣٣٠
من بنى بنيانا فى غير ظلم ٣٣١
من تخطّى رقاب الناس يوم الجمعة ٣٣١
من غلبت عليه فاقتلوه ٣٣٦
ما من نبى إلّا وقد حذّر أمته الدجال ٣٤٠
من نجا من ثلاث فقد نجا ٣٤٤
من أراد منكم أن يتوضأ فليتوضأ ٣٤٥
(ن)
النيل وسيحان وجيحان والفرات من أنهار الجنة ١٧٦
نعم أهل البيت أبو عبد الله ٣١٩',NULL,'499',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (493,'نعم ومن لم يسجدهما فلا يقرأ بها ٣٢٠
نعم إذا توضأت أكلت وشربت ولا أصلى ولا أقرأ حتى أغتسل ٣٣٨
(هـ)
هذه الضربة يفتح الله بها كنوز الروم ٢٨٦
هل لكم أن نخرج فنتلقى هذه العير لعل الله يغنمناها؟ ٢٩٧
هم، هم ٢٩٧
هلاك أمتى فى الكتاب واللبن ٣٢٥
هل علم أحد منكم أنى صليت العصر؟ ٣٤١
(و)
وإنك لفلان ٤١
والذي نفسى بيده إنه ليختصم كل شىء يوم القيامة ٣١٢
والذي نفسى بيده ما ممّا وعدتم به فى الآخرة إلا وقد عرض علىّ ٣٢٣
ويل للأعقاب وبطون الأقدام من النار ٣٣٢، ٣٤٣
(لا)
لا يدخل الجنة عاص ١٠٠، ٢٩١
لا تأتى المائة وعلى ظهرها أحد باق ١١٩، ٣٤٠
لا تحملوهم ما لا تطيقون ١٦٤
لا. ولكن أحببت أن يرى الناس مكانك منى ٢١٤
لا يدخل صاحب مكس الجنة ٢٥٨، ٣٢٥
لا تقوم الساعة حتى يرفع القرآن والذكر ٢٨٤
لا طائر ولا عدوى ولا هامة ولا جدّ والعين حق ٢٨٤
لا تقطع الأيدى فى الغزو ٢٨٩',NULL,'500',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (494,'لا تحل الصدقة لغنى ٢٩٦
لا تشركوا بالله شيئا ٣٠٠
لا تسبّوا تبّعا فإنه قد أسلم ٣٠٥
لا تباع حتى تفصّل ٣٠٧
لا ينبغى هذا للمتقين ٣١٩
لا تخيفوا أنفسكم بعد أمنها ٣٢٥
لا تزال هذه الأمة على شريعة من الحق ٣٣٠
لا يبولن أحدكم مستقبل القبلة ٣٣٢
لا تلعنهم فإنهم منى وأنا منهم ٣٣٦
لا تنقطع الهجرة ما كان الجهاد ٣٣٩
لا خير فى الإمارة لمسلم ٣٤٥
(ى)
يكفونكم أعمال الدنيا وتتفرغون للعبادة ٢٢
يا أبا فاطمة، أكثر من السجود ١٣٦ و ٣٤٢
يا معاذ، انطلق حتى تأتى الجند ١٥٤
يا عمرو، إن الإسلام يجبّ ما قبله ٢٠٨ و ٢٧٩
يا عثمان، إذا ابتعت فكل ٢٦٣
يا فاطمة، من أين جئت ٢٨٨
يسلم الراكب على الماشى ٣٠٧
يا رويفع، لعل الحياة ستطول بك بعدى ٣١٠
يا موهبة، بيّتى هذا الرجل فى بيت ولا توثقى عليه الباب ٣١٤
يا أبا بصرة، إن الكافر يأكل فى سبعة أمعاء ٣١٤',NULL,'501',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (495,'يوم النحر ويوم عرفة وأيام التشريق عيدنا أهل الإسلام ٣٢٢
يكون فتنة يكون أسلم الناس فيها ٣٤٢
يا أخا صداء، إنك؛ لمطاع فى قومك ٣٤٥',NULL,'502',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (496,'<span data-type="title" id=toc-140>(٦) فهرس الأشعار والأراجيز</span>
مرتبة حسب ورودها في الكتاب
قد كان ذو القرنين جدّى مسلما ... ملكا تدين له الملوك وتحشد
٥٨
حسرت عقول أولى النهى الأهرام ... واستصغرت لعظيمها الأحلام
٦٤
قل لرسل النبي صاح إلى النا ... س شجاع ودحية بن خليفه
٧٤
يوم لهمدان ويوم للصدف ... والمنجنيق فى لىّ تختلف
٨٤
أقول إذا ما جاشت النفس اصبرى ... فعما قليل تحمدى أو تلامى
٩٧
هل توفين بنو أمية ذمة ... عهدا كما أوفى جواد هشام
١٣٣ و ٢٦٠
من كان فى نفسه للبيض منزلة ... فليأت أبيض فى حمّام زبان
١٤٠
وظلت أنادى اللكعاء قيسا ... لتدخلنى وقد حضر الغداء
١٥٠
من مبلغ خولان عنى رسالة ... يربّضها أبنا فراس بن مالك
١٥٢
عجبا ما عجبت حين أتانا ... أن قد امّرت قرة بن شريك
١٥٨
عين فابكى لعقبة بن أبان ... فرع فهر وفارس الفرسان
١٦٠
من سره شحم ولحم راكد ... فليأت جفنة عقبة بن أبان
١٦٠
بنو آكل السقب الذين كأنهم ... نجوم بآفاق السماء تنور
١٦٢
سبق الأقوام عجلى ... سبقتهم وهى حبلى
١٧٠
أبلغ أمير المؤمنين رسالة ... فأنت ولى الله فى المال والأمر
١٧٣
ألم تر أن الدهر أخنت ريوبه ... على عمرو السهمى تجبى له مصر
٢١٠
يا بنة جرجير تمشّى عقبتك ... إن عليك بالحجاز ربّتك
٢١٢',NULL,'503',NULL,NULL,'0');
INSERT INTO [b11404] VALUES (497,'لم تر عينى مثل يوم دمقله ... والنخيل تعدو بالدروع مثقله
٢١٥
أحن إلى الإسكندرية إنّ لى ... بها إخوة فى الدين أهل تنافس
٢٦١
ونزور سيدنا وسيد غيرنا ... ليت التشكى كان بالعواد
٢٦٥
يا أيها المتمنى أن يكون فتى ... مثل ابن ليلى فقد خلّى لك السبلا
٢٦٥
لن تجد الفهمى إلا محافظا ... على الخلق الأعلى وبالحق عالما
٢٦٧',NULL,'504',NULL,NULL,'0');
