و٢/ ١٦
{لا تضار والدة بولدها} ٢٣٣ ٢/ ١٦
{وَالَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ أَزْوَاجًا يَتَرَبَّصْنَ
بِأَنْفُسِهِنَّ أربعة أشهر وعشرا} ٢٣٤ ١/ ٣٨٦
{وَمَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُ} ٢٣٦ ٢/ ٢٢٥','2','292',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (886,'الآية رقمها الجزء/ الصفحة
{أو يعفو الذي بيده عقدة النكاح} ٢٣٧ ٢/ ١٦، ٢٢٥
{حافظوا على الصلوات والصلاة والوسطى} ٢٣٨ ١/ ٣٤٣ و٢/ ٧٩
{وأحل الله البيع} ٢٧٥ ١/ ٣٤٩
{يا أيها الذين آمنوا تقوا الله وذروا ما
بقي من الربا} ٢٧٨ ١/ ٣٢١
{ولا يضار كاتب ولا شهيد} ٢٨٢ ٢/ ١٦
{واستشهدوا} ٢٨٢ ١/ ٢٥٤
{واتقوا لله ويعلمكم الله} ٢٨٢ ٢/ ١٩٩
{والله على كل شيء قدير} ٢٨٤ ٢/ ٤٢
{لا يكلف الله نفسا إلا وسعها} ٢٨٦ ١/ ٣٢
{رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا به} ٢٨٦ ١/ ٣٢
سورة آل عمران
{مِنْهُ آيَاتٌ مُحْكَمَاتٌ هُنَّ أُمُّ الْكِتَابِ
وَأُخَرُ متشابهات} ٧ ١/ ٩٠
{فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا
تَشَابَهَ مِنْهُ ابْتِغَاءَ الْفِتْنَةِ وَابْتِغَاءَ تَأْوِيلِهِ وَمَا
يَعْلَمُ تَأْوِيلَهُ إِلَّا اللَّهُ وَالرَّاسِخُونَ فِي الْعِلْمِ
يقولون آمنا به} ٧ ١/ ٩٠
{وما يعلم تأويله إلا الله} ٧ ٢/ ٣٣
{ربنا لا تزغ قلوبنا} ٨ ١/٢٧٩
{قُلْ إِنْ كُنْتُمْ تُحِبُّونَ اللَّهَ فَاتَّبِعُونِي يُحْبِبْكُمُ الله} ٣١ ١/ ٩٧، ١٠٦،
٣١٤
{واسجدي واركعي مع الراكعين} ٤٣ ١/ ٨٢
{ومكروا ومكر الله} ٥٤ ١/ ٧٣
{فإن أَوْلَى النَّاسِ بِإِبْرَاهِيمَ لَلَّذِينَ اتَّبَعُوهُ
وَهَذَا النَّبِيُّ} ٦٨ ٢/ ١٧٨
{كُلُّ الطَّعَامِ كَانَ حِلًّا لِبَنِي إِسْرَائِيلَ إِلَّا ما
حرم إسرائيل على نفسه} ٩٣ ٢/ ٢٣٨
{وَلِلَّهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ
إليه سبيلا} ٩٧ ١/ ٣٨٣، ٣٨٥
و٢/ ٨٢
{فيه آيات بينات مقام إبراهيم} ٩٧ ١/ ٦٩
{كُنْتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ
بِالْمَعْرُوفِ وتنهون عن المنكر} ١١٠ ١/ ١٨٥، ٢٠٥
{قل موتوا بغيظكم} ١١٩ ١/ ٢٥٥
{لا تأكلوا الربا أضعافا مضاعفة} ١٣٠ ٢/ ٤١
{وسارعوا إلى مغفرة من ربكم} ١٣٣ ١/ ٢٦٢
{الذين قال لهم الناس} ١٧٣ ١/ ٣٩٧','2','293',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (887,'الآية رقمها الجزء/ الصفحة
{كل نفس ذائقة الموت} ١٨٥ ١/ ٣٥٥
{والله على كل شيء قدير} ١٨٩ ١/ ٣٥٥
سورة النساء
{أو ما ملكت أيمانكم} ٣ ٢/ ٢٧٢
{الذين يأكلون أموال اليتامى ظلما} ١٠ ١/ ٣٠٥
{يوصيكم الله في أولادكم} ١١ ١/ ٣٨٨
{فإن كان له إخوة} ١١ ١/ ٣١١
{مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِهَا أَوْ دَيْنٍ} ١٢ ٢/ ٩
{فأمسكوهن في البيوت حتى يتوافهن
الموت أو يجعل الله لهن سبيلا} ١٥ ٢/ ٦٤
{حرمت عليكم أمهاتكم} ٢٣ ١/ ٣٢٨، ٣٣٢،
٣٥٥ و٢/ ١٦، ١٧
{وأن تجمعوا بين الأختين} ٢٣ ٢/ ٢٧٢
{ربائبكم اللاتي في حجوركم} ٢٣ ٢/ ٤٢
{ولا تقتلوا أنفسكم} ٢٩ ١/ ٢٩٢
و٢/ ٢٢٣
{إِنْ تَجْتَنِبُوا كَبَائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عنكم
سيئاتكم} ٣١ ١/ ١٤٤
{لَا تَقْرَبُوا الصَّلَاةَ وَأَنْتُمْ سُكَارَى حَتَّى تَعْلَمُوا
ما تقولون} ٤٣ ١/ ٣٨
{طيعوا الله وأطيعوا الرسول} ٥٩ ١ /١٠٦
في شيء فردوه إلى الله ورسوله} ٥٩ ١/ ٢٠٨
و٢/ ٢٤٥
{وأقيموا الصلاة} ٧٧ ٢/ ٢٨
{من يطع الرسول فقد أطاع الله} ٨٠ ١/ ٩٧
{وَلَوْ رَدُّوهُ إِلَى الرَّسُولِ وَإِلَى أُولِي الْأَمْرِ
منهم لعلمه الذين يستنبطونه منهم} ٨٣ ٢/ ٩٨
{فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَدِيَةٌ مُسَلَّمَةٌ إِلَى أَهْلِهِ} ٩٢ ١/ ٨١
{وَمَنْ يُشَاقِقِ الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُ
الْهُدَى وَيَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ نُوَلِّهِ ما
تولى ونصله جهنم وساءت مصيرا} ١١٥ ١/ ١٩٨
{وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ مِنْ ذَكَرٍ أَوْ أنثى} ١٢٤ ١/ ٣١٨
{فبظلم من الذين هادوا حرمنا عليهم} ١٦٠ ٢/ ١٢٠','2','294',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (888,'الآية رقمها الجزء/ الصفحة
{لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اللَّهِ حُجَّةٌ بَعْدَ الرسل} ١٦٥ ١/ ٣١
{يبين الله لكم أن تضلوا} ١٧٦ ١/ ٧٠
سورة المائدة
{وأيديكم إلى المرافق} ٢ ١/ ٣٩١
{حرمت عليكم الميتة} ٣ ١/ ٢٩٠، ٣٢٨
و٢/ ١٦، ١٧، ٧٠
{اليوم أكملت لكم دينكم} ٣ ٢/ ١٠٠، ٢٠٢
{أحل لكم الطيبات} ٥ ٢/ ٢٨٤
{يا أيها الذين آمنوا إذا قمتم إلى الصلاة
فاغسلوا وجوهكم وأيديكم إلى المرافق
وامسحوا برءوسكم وأرجلكم إلى الكعبين} ٦ ٢/ ٢٧
إذا قمتم إلى الصلاة فاغسلوا} ٦ ٢/ ١٢١
{وأيديكم إلى المرافق} ٦ ١/ ٣٧٨، ٣٧٩
{إلى المرافق} ٦ ١/ ٣٦٣
{وامسحوا برءوسكم} ٦ ٢/ ١٧
{وإن كنتم جنبا فاطهروا} ٦ ١/ ٢٥٧
{ادخلوا عليهم الباب} ٢٣ ١/ ٧٠
{مِنْ أَجْلِ ذَلِكَ كَتَبْنَا عَلَى بَنِي إِسْرَائِيلَ} ٣٢ ٢/ ١١٨
{أو تقطع أيديهم وأرجلهم} ٣٣ ١/ ٨١
{والسارق} ٣٨ ١/ ٢٨٩
{والسارق والسارقة فاقطعوا أيديهما} ٣٨ ١/ ٨١، ٢٩٢،
٣٤٢، ٣٩٧
و٢/ ١٩، ٢٨، ٢٩
١٢٠، ١٢١
{يا أيها الرسول} ٤٠ ١/ ٣٢٣
{وكتبنا عليهم فيها أن النفس بالنفس} ٤٥ ٢/ ١٨٠
{والجروح قصاص} ٤٥ ٢/ ١٦٠
{لكل جعلنا منكم شرعة ومنهاجا} ٤٨ ٢/ ١٨٠
{فاستبقوا الخيرات} ٤٨ ١/ ٢٦٢
{والله يعصمك من الناس} ٦٧ ١/ ١١٧
{ثم عموا وصموا كثير منهم} ٧١ ١/ ٣٨٠','2','295',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (889,'الآية رقمها الجزء/ الصفحة
{لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ ثَالِثُ ثلاثة
وما من إله إلا الله واحد} ٧٣ ١/ ١٢٥
{إنما الخمر والميسر} ٩٠ ٢/ ٧٢
{وأطيعوا الله وأطيعوا الرسول} ٩٢ ١/ ٩٧
{فجزاء مثل ما قتل من النعم} ٩٥ ٢/ ٩٧
{يحكم به ذوا عدل منكم} ٩٥ ٢/ ٩٧
{لا تسألوا عن أشياء} ١٠١ ١/ ٢٧٩
سورة الأنعام
{لأنذركم به ومن بلغ} ١٩ ١/ ٣٢٢
{ما فرطنا في الكتاب من شيء} ٣٨ ٢/ ١٠٠
{وَلَا رَطْبٍ وَلَا يَابِسٍ إِلَّا فِي كِتَابٍ مبين} ٥٩ ١/ ٨٦ و٢/ ١٠٠
{أولئك الذي هدى الله فبهداهم اقتده} ٩٠ ٢/ ١٨٠
{انظروا إلى ثمره إذا أثمر} ٩٩ ١/ ٢٥٥
{وَلَا تَسُبُّوا الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ
فيسبوا الله} ١٠٨ ٢/ ١٩٥
{وقد فصل لكم ما حرم عليكم} ١١٩ ٢/ ٢٨٦
{وقالوا ما في بطون هذه الأنعام}
إلى قوله: {حكيم عليم} ١٣٩ ٢/ ١٢٦
{وآتوا حقه يوم حصاده} ١٤١ ٢/ ٢٣
{ثمانية أزواج} إلى قوله:
{الظالمين} ١٤٢-١٤٣ ٢/ ١٢٦
{قُلْ لَا أَجِدُ فِي مَا أُوحِيَ إليَّ محرما
على طاعم يطعمه} ١٤٥ ٢/ ٦٨، ٧٠،
٢٨٤
سورة الأعراف
{ما منعك أن تسجد إذ أمرتك} ١٢ ١/ ٢٤٩، ٢٦١
قُلْ مَنْ حَرَّمَ زِينَةَ اللَّهِ الَّتِي أَخْرَجَ لعباده
والطيبات من الرزق} ٣٢ ٢/ ٢٨٤
{فهل وجدتم ما وعبد ربكم حقا قالوا نعم} ٤٤ ١/ ٣٣٣
{مسخرات بأمره} ٥٤ ١/ ٢٤٢، ٢٤٣
{قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَغَضَبٌ} ٧١ ١/ ٢٢٢
{فماذا تأمرون} ١١٠ ١/ ٢٤٥
{قالوا يا موسى اجعل لنا إلها كما لهم آلهة} ١٣٨ ١/ ٣١١
{اخلفني في قومي} ١٤٢ ١/ ٢٥٠
{واختار موسى قومه سبعين رجلا} ١٥٥ ١/ ١٣٢
{ويحرم عليهم الخبائث} ١٥٧ ٢/ ١٣١
{وقولوا حطة وادخلوا الباب سجدا} ١٦١ ١/ ٨١','2','296',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (890,'الآية رقمها الجزء/ الصفحة
{وَاسْأَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كَانَتْ حَاضِرَةَ الْبَحْرِ} ١٦٣ ١/ ٣٩٧ و٢/ ١٩٣
سورة الأنفال
{ذلك بأنهم شاقوا الله ورسوله} ١٣ ٢/ ١٠٨، ١٢٠
{يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَتَّقُوا اللَّهَ يجعل لكم فرقانا} ٢٩ ٢/ ١٩٩
{إِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مَا قَدْ سَلَفَ} ٣٨ ١/ ٣٥
{فأن لله خُمُسَه} ٤١ ٢/ ٢٧
{إن يكن منكم عشرون صابرون} ٦٥ ١/ ١٣١
{الآن خفف الله عنكم} ٦٦ ٢/ ٨٤
{مَا كَانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرَى حتى يثخن
في الأرض} ٦٧ ٢/ ٢٣٣
سورة التوبة
{فاقتلوا المشركين} ٥ ١/ ٣٤٢، ٣٦٢،
٣٨٨، ٣٩٤
{حتى يعطوا الجزية} ٢٩ ١/ ٣٧٩، ٣٩٤
و٢/ ١٢١
{اتَّخَذُوا أَحْبَارَهُمْ وَرُهْبَانَهُمْ أَرْبَابًا مِنْ دُونِ اللَّهِ} ٣١ ٢/ ٢٤٥
{والذين يكنزون الذهب والفضة} ٣٤ ١/ ٣٣٢
{فبشرهم بعذاب أليم} ٣٤ ١/ ٦٨
{وقاتلوا المشركين كافة} ٣٦ ١/ ٣٠١
{عفا الله عنك لم أذنت لهم} ٤٣ ٢/ ٢٢٠
{والمؤمنون والمؤمنات بعضهم أولياء بعض} ٧١ ٢/ ١٢٣
{فليضحكوا قليلا وليبكوا كثيرا} ٨٢ ١/ ٢٥٥
{والسابقون الأولون} ١٠٠ ١/ ١٨٥
{خذ من أموالهم صدقة} ١٠٣ ١/ ٣١٦
{فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طَائِفَةٌ} ١٢٢ ١/ ١٣٦
سورة يونس
{فأجمعوا أمركم} ٧ ١/ ١٩٣
{قُلْ مَا يَكُونُ لِي أَنْ أُبَدِّلَهُ مِنْ تلقاء نفسي} ١٥ ٢/ ٧٠
{والله يدعو إلى دار السلام} ٢٥ ١/ ٣٣١
{بَلْ كَذَّبُوا بِمَا لَمْ يُحِيطُوا بِعِلْمِهِ وَلَمَّا يأتهم تأويله} ٣٩ ٢/ ١٩١','2','297',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (891,'الآية رقمها الجزء/ الصفحة
{ألقوا ما أنتم ملقون} ٨٠ ١/ ٢٥٥
سورة هود
{وَمَا مِنْ دَابَّةٍ فِي الْأَرْضِ إِلَّا عَلَى الله رزقها} ٦ ١/ ٣٥٥
{حتى إذا جاء أمرنا وفار التنور} ٤٠ ١/ ٢٤٢
{وأهلك} ٤٠ ٢/ ٢٧
{أتعجبين من أمر الله} ٧٣ ١/ ٢٤٢
{وما أمر فرعون برشيد} ٩٧ ١/ ٢٤٢
سورة يوسف
{إنا أنزلناه قرآنا عربيا} ٢ ١/ ٦٥
{وفوق كل ذي علم عليم} ٧٦ ٢/ ٢٣١
{واسأل القرية} ٨٢ ١/ ٦٩
{وما أكثر الناس ولو حرصت بمؤمنين} ١٠٣ ١/ ٣٦٨
سورة الرعد
{الله خالق كل شيء} ١٦ ١/ ٣٨٣
سورة إبراهيم
{وَمَا أَرْسَلْنَا مِنْ رَسُولٍ إِلَّا بِلِسَانِ قَوْمِهِ} ٤ ١/ ٤٣، ٦٥
{قل تمتعوا} ٣٠ ١/ ٢٥٤
{وإن تعدوا نعمة الله لا تحصوها} ٣٤ ١/ ٣٠٤
{ولا تحسبن الله غافلا} ٤٢ ١/ ٢٧٩
سورة الحجر
{إِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَإِنَّا لَهُ لَحَافِظُونَ} ٩ ٢/ ٧٥
{وإنا له لحافظون} ٩ ١/ ٣١٣
{فسجد الملائكة كلهم أجمعون} ٣٠ ١/ ٣٠٣
{إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ إِلَّا من
اتبعك من الغاوين} ٤٢ ١/ ٣٦٨
{ادخلوها بسلام آمنين} ٤٦ ١/ ٢٥٤
{وما خلقنا السموات والأرض وما
بينهما إلا بالحق} ٨٥ ٢/ ١٢٣
{ولا تمدن عينيك} ٨٨ ١/ ٢٧٩','2','298',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (892,'الآية رقمها الجزء/ الصفحة
سورة النحل
{والخيل والبغال والحمير لتركبوها وزينة} ٨ ٢/ ١٩٧
{لتأكلوا منه لحما طريا} ١٤ ٢/ ٤١
{وعلامات وبالنجم هم يهتدون} ١٦ ٢/ ٢٢٦
{وليعلم الذين كفروا أنهم كانوا كاذبين} ٣٩ ١/ ١٢٥
{إِنَّمَا قَوْلُنَا لِشَيْءٍ إِذَا أَرَدْنَاهُ أَنْ نَقُولَ له
كن فيكون} ٤٠ ١/ ٢٥١
{وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ إِلَّا رِجَالًا نُوحِي
إِلَيْهِمْ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لَا
تعلمون بالبينات والزبر} ٤٣-٤٤ ٢/ ٢٤٥
{لتبين للناس ما نزل إليهم} ٤٤ ١/ ٣٨٥، ٣٨٧
{لا جرم أن لهم النار} ٦٢ ٢/ ١٣١
{وأوحى ربك إلى النحل} ٦٨ ٢/ ٢٠٠
{ونزلنا عليك الكتاب تبيانا لكل شيء} ٨٩ ١/ ٢٠٨، ٣٨٦
{إن الله يأمر بالعدل والإحسان} ٩٠ ٢/ ٩٩
{إنما يعلمه بشر} ١٠٣ ٢/ ٢١٩
{فكلوا مما رزقكم الله} ١١٤ ١/ ٢٥٤
{وَلَا تَقُولُوا لِمَا تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ هَذَا
حلال وهذا حرام} ١١٦ ٢/ ٢٨٦
{إن إبراهيم كان أمة} ١٢٠ ١/ ٢٣٦
{ثُمَّ أَوْحَيْنَا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْرَاهِيمَ حنيفا} ١٢٣ ٢/ ١٧٨، ١٨٠
سورة الإسراء
{وما كنا معذبين حتى نبعث رسولا} ١٥ ١/ ٣١
{فلا تقل لهما أف} ٢٣ ١/ ٣٩٢، ٣٩٤
{وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ} ٣٦ ١/ ١٤٨
{واستفزز من استطعت} ٦٤ ١/ ٧١، ٢٥٤
{أيا ما تدعوا فله الأسماء الحسنى} ١١٠ ١/ ٢٩٨
سورة الكهف
{أيكم أحسن عملا} ٧ ١/ ٢٩٩
{وإذا اعْتَزَلْتُمُوهُمْ وَمَا يَعْبُدُونَ إِلَّا اللَّهَ
فَأْوُوا إِلَى الكهف} ١٦ ٢/ ١٢٠
{وَلَا تَقُولَنَّ لِشَيْءٍ إِنِّي فَاعِلٌ ذَلِكَ غَدًا إِلَّا
أَنْ يَشَاءَ اللَّهُ وَاذْكُرْ رَبَّكَ إِذَا نسيت} ٢٣-٢٤ ١/ ٣٦٤','2','299',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (893,'الآية رقمها الجزء/ الصفحة
{فمن شاء فليؤمن ... } ٢٩ ١/٧١
سورة طه
{الرحمن على العرش استوى} ٥ ٢/ ٣٣
{وأقم الصلاة لذكري} ١٤ ٢/ ١٨٠
{فاقض ما أنت قاض} ٧٢ ١/ ٢٥٥
{أفعصيت أمري} ٩٣ ١/ ٢٥٠
{وعصى آدم ربه فغوى} ١٢١ ١/ ١٠٠
{وَلَوْ أَنَّا أَهْلَكْنَاهُمْ بِعَذَابٍ مِنْ قَبْلِهِ لَقَالُوا
رَبَّنَا لَوْلَا أَرْسَلْتَ إِلَيْنَا رَسُولًا فَنَتَّبِعَ آيَاتِكَ
من قبل أن نذل ونخزى} ١٣٤ ١/ ٣١
سورة الأنبياء
{فسألوا أهل الذكر} ٧ ٢/ ٢٤٤
{بل فعله كبيرهم هذا} ٦٣ ١/ ١٠١
{ففهمناها سليمان} ٧٩ ٢/ ٢٣٤
{وعلمناه صنعة لبوس لكم} ٨٠ ١/ ٤٢
{إِذْ ذَهَبَ مُغَاضِبًا فَظَنَّ أَنْ لَنْ نَقْدِرَ عليه} ٨٧ ١/ ١٠١
{إِنَّكُمْ وَمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ حَصَبُ جهنم} ٩٨ ٢/ ٢٧
{إن الذين سبقت لهم منا الحسنى} ١٠١ ١/ ٣٠٥و ٢/ ٢٧
سورة الحج
{وترى الناس سكارى وما هم بسكارى} ٢ ٢/ ٢٦١
{أَلَمْ تَرَ أَنَّ اللَّهَ يَسْجُدُ لَهُ مَنْ في السموات
ومن في الأرض} ١٨ ١/ ٦١
{وكثير من الناس} ٨١ ١/ ٦١
{تجري في البحر بأمره} ٦٥ ١/ ٢٤٢، ٢٤٣
{وَمَا جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حَرَجٍ} ٧٨ ٢/ ١٩٠
سورة المؤمنون
{وَالَّذِينَ هُمْ لِفُرُوجِهِمْ، حَافِظُونَ إِلَّا عَلَى
أَزْوَاجِهِمْ أو ما ملكت أيمانهم} ٥-٦ ١/ ٣٣٢
{وإن لكم في الأنعام لعبرة} ٢١ ٢/ ٩٥
{كلوا من الطيبات} ٥١ ١/ ٢٥٥
{ولو اتبع الحق أهواءهم لفسدت
السموات والأرض} ٧١ ٢/ ٢٤٩','2','300',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (894,'الآية رقمها الجزء/ الصفحة
{قال رب ارجعون} ٩٩ ١/٣١٣
{أفحسبتم إنما خلقناكم عبثا} ١١٥ ٢/١٢٣، ٢٨٥
سورة النور
{الزَّانِيَةُ وَالزَّانِي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا
مِائَةَ جلدة} ٢ ١/ ٢٩٢
و٢/ ١٢٠
{الزانية والزاني} ٢ ١/ ٨١
{والذين يرمون المحصنات} ٤ ١/ ٣٧٣
{سبحانك هذا بهتان عظيم} ١٦ ٢/ ٢١٥
{فكاتبوهم إن علمتم فيهم خيرا} ٣٣ ١/ ٢٥٣
{إن في ذلك لعبرة لأولي الأبصار} ٤٤ ٢/ ٩٥
{فليحذر الذين يخالفون عن أمره} ٦٣ ١/ ١٠٦، ٢٥٠
سورة الفرقان
{وَمَنْ يَفْعَلْ ذَلِكَ يَلْقَ أَثَامًا، يُضَاعَفْ لَهُ العذاب
يوم القيامة ويخلد فيه مهانا} ٦٨-٦٩ ١/ ٣٤
{وَالَّذِينَ لَا يَدْعُونَ مَعَ اللَّهِ إِلَهًا آخَرَ وَلَا يَقْتُلُونَ
النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بالحق} ٦٨-٦٩-٧٠ ١/ ٣٤
سورة الشعراء
{إنا معكم مستمعون} ١٥ ١/ ٣١١
{بل ألقوا ما أنتم ملقون} ٤٣ ١/ ٢٥٥
{كذبت قوم نوح المرسلين} ١٠٥ ١/ ٣١٢
سورة النمل
{وأوتيت من كل شيء} ٢٣ ١/ ٣٨٥
{فناظرة بم يرجع المرسلون} ٣٥ ١/ ٣١٢
{فلما جاء سليمان} ٣٦ ١/ ٣١٢
{أيكم يأتيني بعرشها} ٣٨ ١/ ٢٩٨، ٢٩٩
سورة القصص
{وأوحينا إلى أم موسى أن أرضعيه} ٧ ٢/ ٢٠٠
{يجبى إليه ثمرات كل شيء} ٥٧ ١/ ٣٨٥','2','301',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (895,'الآية رقمها الجزء/ الصفحة
سورة العنكبوت
{فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلَّا خَمْسِينَ عَامًا} ١٤ ١/ ٣٦١
سورة الروم
{ومن آياته خلق السموات والأرض واختلاف
ألسنتكم وألوانكم} ٢٢ ١/ ٤٢
سورة الأحزاب
{لَقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ
لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ} ٢١ ١/ ١٠٦، ١٠٧،
١١٦
{إِنَّمَا يُرِيدُ اللَّهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ أَهْلَ
البيت ويطهركم تطهيرا} ٣٣ ١/ ٢٢٢
{إن المسلمين والمسلمات} ٣٥ ١/ ٣١٩
{وَمَا كَانَ لِمُؤْمِنٍ وَلَا مُؤْمِنَةٍ إِذَا قَضَى اللَّهُ
وَرَسُولُهُ أَمْرًا أَنْ يَكُونَ لَهُمُ الْخِيَرَةُ} ٣٦ ١/ ٢٥٠
{وَإِذْ تَقُولُ لِلَّذِي أَنْعَمَ اللَّهُ عَلَيْهِ وَأَنْعَمْتَ
عليه أمسك عليك زوجك} ٣٧ ٢/ ٢٢٠
{فما لكم عليهن من عدة تعتدونها} ٤٩ ١/ ٣٨٦
{إن الله وملائكته يصلون على النبي} ٥٦ ١/ ٦٠
{إنا أطعنا سادتنا وكبرائنا فأضلونا السبيلا} ٦٧ ٢/ ٢٤٥
سورة سبأ
{لا يعزب عنه مثقال ذرة} ٣ ١/ ٣٠٠
{من رجز أليم} ٥ ١/ ٢٢٢
{افترى على الله كذبا أم به جنة} ٨ ١/ ١٢٤
{وقليل من عبادي الشكور} ١٣ ١/ ٣٦٨
سورة فاطر
{إليه يصعد الكلم الطيب} ١٠ ٢/ ٣٣
سورة يس
{قَالَ مَنْ يُحْيِي الْعِظَامَ وَهِيَ رَمِيمٌ} ٧٨ ٢/ ٩٩
{قُلْ يحييها الذي أنشأها أول مرة} ٧٩ ٢/ ٩٩
{كن فيكون} ٨٢ ١/ ٢٥٥','2','302',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (896,'الآية رقمها الجزء/ الصفحة
سورة الصافات
{إني سقيم} ٨٩ ١/ ١٠١
{فانظر ماذا ترى} ١٠٢ ١/ ٢٥٥
سورة ص
{فَإِذَا سَوَّيْتُهُ وَنُفِخَتْ فِيهِ مِنْ رُوحِي
فَقَعُوا له ساجدين} ٧٢ ١/ ٢٥٧
ر
{قرآنا عربيا} ٢٨ ١/ ٦٥
{يا عبادي} ٥٣ ١/ ٣٢٣
{الله خالق كل شيء} ٦٢ ١/ ٣٠٦، ٣٨٣،
٣٨٥
سورة فصلت
{وويل للمشركين، الذين لا يؤتون الزكاة} ٦-٧ ١/ ٣٤
{فقضاهن سبع سموات} ١١ ١/ ٢٥٠
{اعلموا ما شئتم} ٤٠ ١/ ٢٥٤
{وَلَوْ جَعَلْنَاهُ قُرْآنًا أَعْجَمِيًّا لَقَالُوا لَوْلَا
فُصِّلَتْ آياته} ٤٤ ٢/ ١٢٣
سورة الشورى
{ليس كمثله شيء} ١١ ١/ ٧٠و ٢/٣٣
{شَرَعَ لَكُمْ مِنَ الدِّينِ مَا وَصَّى بِهِ نوحا} ١٣ ٢/ ١٧٢
{قُلْ لَا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا إِلَّا الْمَوَدَّةَ في
القربى} ٢٣ ١/٢٢٢
{فإن يشأ الله يختم على قلبك ويمحو الله
الباطل} ٢٤ ٢/ ١٩٨
{وَلَوْ بَسَطَ اللَّهُ الرِّزْقَ لِعِبَادِهِ لَبَغَوْا فِي الأرض} ٢٧ ٢/ ١٢٣
سورة الزخرف
{إنا وجدنا آباءنا} ٢٣ ٢/ ٢٤٥
{وَلَوْلَا أَنْ يَكُونَ النَّاسُ أُمَّةً وَاحِدَةً لَجَعَلْنَا لمن
يكفر بالرحمن} ٣٣ ٢/ ١٢٣
سورة الدخان
{وما خلقنا السموات والأرض وما بينهما
لاعبين} ٣٨ ٢/ ٢٨٥
{ذق إنك أنت العزيز الكريم} ٤٩ ١/ ٢٥٤','2','303',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (897,'الآية رقمها الجزء/ الصفحة
سورة الجاثية
{وسخر لكم ما في السموات وما في
الأرض جميعا منه} ١٣ ٢/ ٢٨٤
{إنا كنا نستنسخ ما كنتم تعملون} ٢٩ ٢/ ٤٩
سورة الأحقاف
{جزاء بما كانوا يعملون} ١٤ ٢/ ١٢٠
{تدمر كل شيء بأمر ربها} ٢٥ ١/ ٣٨٥
سورة محمد
{حتى نعلم المجاهدين منكم} ٣١ ٢/١٢١
سورة الفتح
{لقد رضي الله عن المؤمنين} ١٨ ١/ ١٨٥
{مُحَمَّدٌ رَسُولُ اللَّهِ وَالَّذِينَ مَعَهُ أَشِدَّاءُ
عَلَى الكفار} ٢٩ ١/١٨٥
و٢/١٩٧
سورة الحجرات
{إن جاءكم فاسق بنبأ فتبينوا} ٦ ١/ ١٣٦، ١٤٠
{وكره إليكم الكفر والفسوق والعصيان} ٧ ١/ ١٤٤
{والله بكل شيء عليم} ١٦ ١/ ٣٥٤، ٣٥٥
سورة الذاريات
{وما خلقت الجن والأنس إلا ليعبدون} ٥٦ ٢/ ١١٨
سورة الطور
{فاصبروا أو لا تصبروا} ١٦ ١/ ٢٥٤
سورة النجم
{وَمَا يَنْطِقُ عَنِ الْهَوَى} ٣ ٢/ ٢١٨
{إِنْ هُوَ إِلَّا وحي يوحى} ٤ ٢/ ٧٠، ١٠١،
٢١٨
{إِنْ هِيَ إِلَّا أَسْمَاءٌ سَمَّيْتُمُوهَا أَنْتُمْ وَآبَاؤُكُمْ
ما أنزل الله بها من سلطان} ٢٣ ١/ ٤٢، ٤٣','2','304',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (898,'الآية رقمها الجزء/ الصفحة
{وَإِنَّ الظَّنَّ لَا يُغْنِي مِنَ الْحَقِّ شَيْئًا} ٢٨ ١/ ١٤٨
سورة القمر
{وما أمرنا إلا واحدة كلمح بالبصر} ٥٠ ١/ ٢٤٢
سورة الرحمن
{كل من عليها فان} ٢٦ ١/ ٣٥٥
{يا معشر الجن والإنس} ٣٣ ١/ ٣٠١
سورة المجادلة
{فصيام شهرين متتابعين} ٤ ٢/ ١١
{يرفع الله الذين آمنوا منكم والذين أتوا العلم
درجات} ١١ ٢/ ٢٣١
{أَأَشْفَقْتُمْ أَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَاتٍ} ١٣ ٢/ ٨٤
سورة الحشر
{يخربون بيوتهم بأيديهم وأيدي المؤمنين} ٢ ٢/ ٩٦
{فاعتبروا يا أولي الأبصار} ٢ ٢/ ٩٥، ٢٧٣
{فاعتبروا} ٢ ٢/ ١٣٨
{ذلك بأنهم شاقوا الله ورسوله} ٤ ٢/ ١١٨، ١٣٠
{مَا قَطَعْتُمْ مِنْ لِينَةٍ أَوْ تَرَكْتُمُوهَا قَائِمَةً على
أصولها فبإذن اللَّهِ} ٥ ٢/ ٢٣٤
{وَمَا آتَاكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهَاكُمْ عنه
فانتهوا} ٧ ١/ ٩٧، ١٠٦،
٣١٤، ٣٢٤
و٢/ ٢٤، ٧٢،
١٠١
{لا يستوي أصحاب النار وأصحاب الجنة} ٢٠ ١/ ٣٠٥
{أصحاب الجنة هم الفائزون} ٢٠ ١/ ٣٠٦
سورة الممتحنة
{فلا ترجعوهن إلى الكفار} ١٠ ٢/ ٧١
{وَإِنْ فَاتَكُمْ شَيْءٌ مِنْ أَزْوَاجِكُمْ إِلَى الْكُفَّارِ} ١١ ٢/ ٧٠
سورة الجمعة
{هو الذي بعث في الأمين رسولا منهم} ٢ ١/ ٣٢٢
{وآخرين منهم لما يلحقوا بهم} ٣ ١/ ٣٢٢','2','305',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (899,'الآية رقمها الجزء/ الصفحة
{إِذَا نُودِيَ لِلصَّلَاةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا
إلى ذكر الله} ٩ ١/ ٣٩٤ و٢/٤٨
{وذروا البيع} ٩ ٢/ ١٢٣
سورة المنافقون
{إِذَا جَاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ إِنَّكَ
لَرَسُولُ اللَّهِ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ
وَاللَّهُ يَشْهَدُ إن المنافقين لكاذبون} ١ ١/ ١٢٤
{لَئِنْ رَجَعْنَا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ
مِنْهَا الأذل} ٨ ١/ ١٢٦
سورة الطلاق
{يا أيها النبي} ١ ١/ ٣٢٣
{ومن يتق الله يجعل له مخرجا} ٢ ٢/ ١٢٣، ١٩٩
{ومن يتوكل على الله فهو حسبه} ٣ ٢/ ١٢٣
{وأولات الأحمال أجلهن أن يضعن حملهن} ٤ ١/ ٣٨٦
{أسكنوهن} ٦ ١/٣٨٨
{لينفق ذو سعة من سعته} ٧ ٢/ ٢٢٥
{لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا مَا آتَاهَا} ٧ ١/٣٢
سورة التحريم
{فقد صنعت قلوبكما} ٤ ١/ ٣١٠
{لا تعتذروا اليوم} ٧ ١/ ٢٧٩
سورة القلم
{أفنجعل المسلمين كالمجرمين} ٣٥ ٢/ ١٢٣
سورة الحاقة
{كلوا واشربوا} ٢٤ ١/ ٢٥٤
سورة المعارج
{فذرهم يخوضوا ويلعبوا} ٢٨ ١/ ٢٥٤
سورة نوح
{رب اغفر لي} ٢٨ ١/ ٢٥٤
سورة الجن
{وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ فَإِنَّ لَهُ نَارَ جهنم} ٢٣ ١/ ٢٥٠','2','306',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (900,'الآية رقمها الجزء/ الصفحة
سورة المزمل
{قُمِ اللَّيْلَ إِلَّا قَلِيلًا، نِصْفَهُ أَوِ انْقُصْ منه قليلا} ٢-٣ ١/ ٣٦٨
{فعصى فرعون الرسول} ١٦ ٢/ ٥
سورة المدثر
{ما سلككم من سقر، قالوا لم نك من المصلين} ٤٢-٤٣ ١/ ٣٤
سورة القيامة
{فَإِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْآنَهُ} ١٨ ٢/٢٧
{ثُمَّ إِنَّ عَلَيْنَا بيانه} ١٩ ٢/ ٢٧
{أيحسب الإنسان أن يترك سدى} ٣٦ ٢/ ١٢٣
سورة المرسلات
{وإذا قيل لهم اركعوا لا يركعون} ٤٨ ١/ ٢٤٩
{ويل يومئذ للمكذبين} ٤٩ ١/ ٢٤٩
سورة التكوير
{علمت نفس ما أحضرت} ١٤ ١/ ٧٠
{والليل إذا عسعس} ١٧ ١/ ٥٩
سورة الانفطار
{إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ، وَإِنَّ الْفُجَّارَ لَفِي جحيم} ١٣-١٤ ١/ ٣٣٢
سورة الشمس
{ونفس وما سواها فألهمها فجورها وتقواها} ٧-٨ ٢/ ٢٠٠
سورة البينة
{لم يكن الذين كفروا} ١ ٢/ ٦٦
سورة الزلزلة
{فَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَرَهُ، وَمَنْ يعمل
مثقال ذرة شرا يره} ٧-٨ ١/ ٢٩٢ و٢/ ٢١٨','2','307',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (901,'٢-<span data-type="title" id=toc-382> فهرس أطراف الأحاديث النبوية:</span>
طرف الحديث الجزء والصفحة
"أ"
ابدءوا بما بدأ الله ١/ ٨٢
أبو عبيدة بن الجراح أمين هذه الأمة ١/ ٢٢١
أتأمرني بذلك "ألا تفارق زوجها" ١/ ٢٥١
أتي بسارق فقعيه من المفصل ١/ ١٠٥
أتاهم واحد "أهل قباء" فأخبرهم أن القبلة تحولت ١/ ١٣٦ و٢/ ٦٧
أجاركم الله من ثلاث خلال ١/ ٢٠٦
اجتهد رأيي ولا آلو "قول معاذ" ١/ ١٥٢
آخِرَ الْأَمْرَيْنِ تَرْكُ الْوُضُوءِ مِمَّا مَسَّتِ النَّارُ ٢/ ٢٥
ادرءوا الحدود بالشبهات ١/ ١٥٣
ادْعُهُمْ إِلَى شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا الله ٢/ ١٤
إذا اختلف البيعان ٢/ ٤٣
إذا أمرتكم بأمر فأتوا منه ما استطعتم ١/ ٢٨٢
إِذَا حَلَفَ الرَّجُلُ عَلَى يَمِينٍ فَلَهُ أَنْ يستثني ١/ ٣٦٤
إذا سَهَا أَحَدُكُمْ عَنْ صَلَاةٍ فَلْيُصَلِّهَا حِينَ يَذْكُرُهَا ٢/ ٧٥
إذا شرب سكر ٢/ ١٤٥
إذا قعد بين شعبها الأربع ٢/ ٧٧
إذا لاقى الختان الختان ٢/ ٧٧، ٢٥٨
إذا وضعها في الحلال كان له بها أجر ٢/ ١٠١
أذن رسول الله صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ بلبس الحرير "للحكة" ١/ ٣٩٨
أرأيت لو تمضمضت بماء ٢/ ٢٥، ١٠١، ٢١٩
أرأيت لو كان على أبيك دين ٢/ ١٠٠، ١٢٢، ٢١٩
أرأيت لو وضعها في حرام ٢/ ١٠١','2','308',NULL,NULL,'0');
INSERT INTO [b11437] VALUES (902,'ارتدت العرب قاطبة ١/ ٣٠١
استفت قلبك ٢/ ١٩٦، ٢٠٠
أشار بأصابعه إلى أيام الشهر ثلاث مرات ١/ ١١٨
أصحابي كالنجوم ١/ ١٨٦، ٢٢١، ٢/ ١٨٨
أعتق رقبة ٢/ ١١٢
أفينفعه إن حججت عنه ٢/ ١١٢
