INSERT INTO [b11686] VALUES (664,'١٤٤٢/٥٢٣، ١٤٤٣/٥٢٤، ١٤٤٥/٥٢٤.
* فتنة الأمين والمأمون: ١١١٩/٤١٨.
* فتنة حفص بن الوليد: ٦٦٩/٢٤٣.
* فتنة القراء بمصر سنة ٢١٧ هـ: ١٩١/٧٣.
* الفتوح أيام أبى بكر: ٧٢٦/٢٦٤.
* فتوح المغرب: ٤٧٤/١٧٧.
(حرف القاف) * قتال غرفة بن الحارث أهل النّجير فى الردة: ١٠٥٩/٣٨٩.
* قتل البجة عثمان بن كليب الحرسى: ٩٢٨/٣٤٠.
* قتل ابن أبى حذيفة بفلسطين سنة ٣٦ هـ بعد إخراجه من مصر: ١١٨٦/٤٤١.
* قتل حسان بن عتاهية على يد رجل، يدعى (شعبة) بأمر من صالح بن علىّ العباسى:
٣٠٤/١١٥.
* قتل حوثرة بن سهيل الباهلى عقبة بن نعيم الرعينى سنة ١٢٨ هـ: ٩٥٤/٣٥١.
* قتل خارجة بن حذافة على يد أحد الخوارج فى مصر سنة ٤٠ هـ، والقصاص من القاتل: ٣٨٢/١٤٦.
* قتل الروم جناب بن مرثد الرعينى صاحب حرس عبد العزيز بن مروان بالإسكندرية:
٢٥٠/٩٤.
* قتل الروم دخين بن عامر فى تنيس سنة ١٠٠ هـ: ٤٣٧/١٦١.
* قتل الروم عائذ بن ثعلبة بالبرلس سنة ٥٣ هـ: ٦٩٥/٢٥٥.
* قتل عاصم بن أبى بكر بن عبد العزيز بن مروان، وبعض بنى أمية فى مصر سنة ١٣٣ هـ: ٦٨٠/٢٥١.
* قتل عبد الله بن عثمان الثقفى أحد المحرضين على قتل عثمان، وهو (عمرو بن الحمق الخزاعى) سنة ٥٠ هـ: ١٠١٩/٣٧٢.
* قتل عبد العزيز بن جزىّ مع مروان بن محمد: ٨٥٦/٣٢٠.
* قتل عثمان بن عفان على يد (عروة بن شييم الليثى) : ٩٣٥/٣٤١.
* قدوم أبى الأعور السلمى مصر مع مروان بن الحكم: ١٤٢٠/٥١٧.
* قدوم أبى أيوب الأنصارى مصر بحرا سنة ٤٦ هـ: ١٤٢٢/٥١٧.
* قدوم شرحبيل بن حسنة رسولا من النبي إلى حاكم مصر: ٦٢٩/٢٣١.','1','667',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (665,'* قدوم عبد الرحمن بن غنم على الرسول فى السفينة من اليمن: ٨٣٥/٣١١.
* قدوم كعب بن عدى الإسكندرية رسولا من عمر إلى المقوقس سنة ١٥ هـ: ١١٠٦/٤١١.
