INSERT INTO [b11686] VALUES (701,'<span data-type="title" id=toc-504>(٧) فهرست الكتب المذكورة فى المتن</span>
(حرف الهمزة) * (الأخبار) ، لأحمد بن يحيى بن وزير: ٣٨٧/١٤٩.
* (الأخبار) ، لابن عفير: ٦٣/٢٥، ٩١/٣٤، ١٢٣/٤٢، ١٢٩/٤٣، ١٣٠/٤٤، ١٥٩/٥٤، ٣٤٢/١٣٤، ٣٥٩/١٤٠، ٤٠٠/١٥١، ٤٤٣/١٦٤، ٥٠٤/١٩١، ٥٥٠/٢٠٧، ٦٠٥/٢٢٣، ٦٣٢/٢٣٢، ٦٥٧/٢٤٠، ٧٩٧/٢٩٤، ٨٢٧/٣٠٨، ٨٦٠/٣٢٠، ٩٥٩/٣٥٢، ١٠٢٩/٣٧٦، ١١١٥/٤١٧، ١١٣٧/٤٢٨، ١٢٣٠/٤٥٣، ١٢٨٧/٤٦٨، ١٣٧٠/٥٠٢، ١٤١٠/٥١٢، ١٤١٨/٥١٦.
* (الأخبار) ، ليحيى بن عثمان بن صالح: ٢٨٧/١٠٩.
* (أخبار مصر) : ١١٩/٤١، ١٥٠/٥١.
* (أشراف جذام) : ١٢٩٨/٤٧٤.
* (أشراف لخم بمصر) : ٢٠٧/٧٩.
(حرف الباء) * بعض الكتب القديمة: ٥٠٣/١٩١، ٩٤٤/٣٤٤.
* بعض الكتب القديمة فى النسب القديم بخط ابن لهيعة: ١٧٢/٦٢.
(حرف التاء) * (التاريخ) ، لأحمد بن عبد الرحيم البرقى: ٤٠/١٧.
* (تاريخ العدّاس) : ٣٥٠/١٣٦.
* (تاريخ عمر بن الخطاب بن حليلة الإسكندرانى) : ٩٩٤/٣٦٤.
* (تاريخ ابن فضال) : ٣١٢/١١٩.
* (تاريخ الكباش) : ١٠٣/٣٧.
* (تاريخ مصر) ، لعلى بن قديد: ٩٧٣/٣٥٧، ١٠٤٩/٣٨٤.
(حرف الفاء) * (الفتوح) : ٦٢٢/٢٢٩.
* (فتح الإسكندرية) ، للواقدى: ٧٥٨/٢٧٩.','1','704',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (702,'(حرف الكاف) * كتاب (رايات مصر) : ٢٨٦/١٠٨.
* كتاب (ربيعة الأعرج) : ٩٤٣/٣٤٣.
* كتاب (عمرو بن الحارث) : ٢٠٩/٧٩، ١١٠٦/٤١١- ٤١٢.
* كتاب (محمد بن سحنون) : ١٤٤٣/٥٢٤.
* كتاب بخط (ابن مفرّج القاضى) : ١٨٣/٧٠- ٧١.
(حرف الميم) * (مختصر المزنى) : ١١٥٣/٤٣٢.
* (مغازى ابن إسحاق) : ١٢٨٠/٤٦٦.
* (المغازى) ، لمحمد بن عبد الله بن عبد الرحيم بن سعية بن أبى زرعة المصرى:
١٢٢٩/٤٥٢.
* (موطأ ابن وهب الكبير) ، كتاب (الزكاة) : ٣٨٥/١٤٨.
* (الملاحم) : ١٣٤٦/٤٩٣.
(حرف النون) * (نسب حضرموت) فى الرواية القديمة: ٤٦٩/١٧٤.
* (نسب حمير) ، لهانئ بن المنذر الكلاعى: ٦٣٦/٢٣٣.','1','705',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (703,'<span data-type="title" id=toc-505>(٨) فهرست أوائل الآيات القرآنية</span>
(حرف الهمزة) * اجْعَلْنِي عَلى خَزائِنِ الْأَرْضِ
: ١٤٢٤/٥١٨.
* أَفَرَأَيْتَ إِنْ مَتَّعْناهُمْ سِنِينَ
: ٦٨٠/٢٥١.
* أَلَيْسَ لِي مُلْكُ مِصْرَ وَهذِهِ الْأَنْهارُ تَجْرِي مِنْ تَحْتِي
: ١٤٢٩/٥١٩- ٥٢٠.
(حرف الفاء) * فَأَخْرَجْناهُمْ مِنْ جَنَّاتٍ وَعُيُونٍ وَكُنُوزٍ وَمَقامٍ كَرِيمٍ
: ١٤٢٩/٥٢٠.
(حرف الواو) * وَكَذلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ يَتَبَوَّأُ مِنْها حَيْثُ يَشاءُ
: ١٤٢٤/٥١٨.
(حرف الياء) * يا أَيُّهَا الَّذِينَ آمَنُوا إِذا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفاً فَلا تُوَلُّوهُمُ الْأَدْبارَ
: ٤٢٤/١٥٦.
* يا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ
: ٧٢٦/٢٦٥.
* يَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفاصِلِينَ
: ١٢٠٢/٤٤٥.','1','706',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (704,'<span data-type="title" id=toc-506>(٩) فهرست أوائل الأحاديث والآثار</span>
(حرف الهمزة) * إذا أراد الله بعبد هوانا: ١١٧٢/٤٣٧- ٤٣٨.
* إذا كان على رأس مائتين: ١٨٣/٧٢.
* اشتريت يوم خيبر قلادة: ٥٦٧/٢١٢.
* اطلبوا له وارثا مسلما: ١٣٤٣/٤٩٣.
* اللهم إنى أسألك من خير ما فيها: ٦٠٣/٢٢٢.
* ألم أكن نهيتكم عن أكل هذه الشجرة؟! ٨٤٦/٣١٦.
* إن ابن الزرقاء لمنافق خبيث: ٤٩٤/١٨٦- ١٨٧.
* إن الفئة رسول الله: ٤٢٤/١٥٦.
* إن الذي رأيت منى أنى رأيت الصراط: ١١١٧/٤١٧.
* إن مصر ستفتح بعدى: ٤٥٥/١٦٩.
* إنى لأعلم السنة التى تخرجون فيها من مصر: ٧٥٦/٢٧٨ (حرف الباء) * بالجد الأسعد، والطائر الأيمن: ٧٨٥/٢٨٩.
(حرف التاء) * تعوذوا بالله من الفقر، والقلة، والذلة: ١٣٣٥/٤٨٨.
(حرف الجيم) * جدّد رازح: ٧١٦/٢٦٠.
(حرف الدال) * دعا النبي لثمامة جد (بكر بن سوادة) : ٢٠٩/٧٩.
(حرف الراء) * رأيت النبي يمسح على الخفّين: ١٧٠/٦٠.
* رحم الله عمرا: ٥٠٢/١٨٩- ١٩٠، ٩٦٤/٣٥٣.
(حرف السين) * ستر العورة: ٧١٧/٢٦٠.','1','707',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (705,'* سجد النبي فى القرآن خمس عشرة سجدة: ٧٨٠/٢٨٧.
* سجود القرآن: ١٢٠١/٤٤٤.
* سيكون بمصر رجل من بنى أمية: ٣٠٦/١١٧.
* سيكون فى آخر أمتى قوم: ١٢٩٦/٤٧٣.
(حرف الصاد) * صلاة الخوف بكل طائفة ركعة وسجدتان: ١١٠٤/٤١٠.
(حرف الطاء) * طلّق أيتهما شئت: ١٤٢٦/٥١٨.
(حرف الغين) * غير النبي اسم رجل، وكان اسمه أسود، فسمّاه أبيض: ٨٨/٣٢.
(حرف الفاء) * فى الحج سجدتان: ٧٦٦/٢٨٢.
(حرف القاف) * قتل رجل من بنى إسرائيل تسعة وتسعين نفسا: ١٤٣١/٥٢١.
(حرف الكاف) * كانوا إذا كانوا فى الغزو: ٤٧٧/١٧٩.
* كنا عند النبي: ١٠٩٨/٤٠٦.
(حرف اللام) * لأنّا حين أكلنا: ٤٥٠/١٦٧.
(حرف الميم) * ما أشبع جوفك، وستر عورتك: ١٣٥٩/٤٩٨.
* ما الخيرات الثلاث؟: ٦٥٢/٢٣٨.
* من احتبس فرسا فى سبيل الله: ٦٧٥/٢٤٦.
* من جر إزاره خيلاء: ١٢٤٥/٤٥٧.
* من حمى مؤمنا من منافق: ١٣١/٤٤.
* من نجا من ثلاث: ٧٢٨/٢٦٦.
(حرف النون) * النهى عن استقبال البائل إلى القبلة: ١٠٦٤/٣٩١.','1','708',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (706,'* النهى عن الوشر، والوشم: ١٣٨١/٥٠٦.
(حرف الهاء) * هم خدم أهل الجنة: ١٤٤٤/٥٢٤.
(حرف الواو) * والله ما تركنا عهد نبينا: ٦٦٨/٢٤٣.
* والله ما كنت أبالى: ٤٦٣/١٧٢.
* وفد شريح اليافعى على النبي: ٦٣٦/٢٣٣.
(حرف اللام ألف) * لا تأتى المائة وعلى ظهرها أحد باق: ٥٧٥/٢١٥.
* لا تحمّلوهم ما لا يطيقون: ١٢٩٢/٤٧١.
* لا تقوم الساعة حتى يسود كل قبيلة منافقوها: ١٢٨/٤٣.
* لا تنقطع الهجرة ما كان الجهاد: ٢٥٢/٩٥.
* لا ربا فى الحيوان: ٢٤٩/٩٣.
* لا هجرة بعد الفتح: ١٠٦٠/٣٩٠.
(حرف الياء) * يا أبا فاطمة، أكثر من السجود: ١١٠٠/٤٠٧.
* يخرج ناس يمرقون من الدين: ٨٣٠/٣٠٩.
* يسير حىّ حتى يأتوا: ٤٦٧/١٧٤.
* ينهى عن أن توطأ الحبالى: ٤٨٤/١٨٢.','1','709',NULL,NULL,'0');
INSERT INTO [b11686] VALUES (707,'<span data-type="title" id=toc-507>(١٠) فهرست أوائل الأشعار</span>
(حرف الهمزة) * أنحى على ودجى: ١٧٤/٦٥.
(حرف الحاء) * حدّثت بسرا: ١٧٤/٦٥.
(حرف الفاء) * فإياك لا تجنى من الشر غلطة: ٣٤٠/١٣٣.
* فلا خير فى الدنيا ولا العيش بعدهم: ٣٤٠/١٣٣.
(حرف اللام) * الليث من يمنع حافات الدار: ١٧٤/٦٦.
(حرف الميم) * من ذا لوالهة: ١٧٤/٦٥.
(حرف الهاء) * ها من أحسّ: ١٧٤/٦٤.
(حرف الواو) * وأبوك سلّم داره وأباحها: ١٠٩٦/٤٠٥.
* وإن أمير المؤمنين مسلط: ٣٤٠/١٣٣.
* وجدّى الذي عاطى: ١٢٩٠/٤٦٩.','1','710',NULL,NULL,'0');
