INSERT INTO [b1176] VALUES (1,'بسم الله الرحمن الرحيم
INSERT INTO [b1176] VALUES (2,'الشافعي وصفه أحمد بن حنبل بالتدليس.
INSERT INTO [b1176] VALUES (3,'الحاكم وغيره أنه مدلس وحكى خلف بي سالم عن عدة من مشايخه أن تدليسه من أعمض شئ وكانوا يتعجبون منه.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (4,'٣ ـ ع:<span data-type="title" id=toc-5> إسماعيل بن أبي خالد</span>، ذكره النسائي وغيره بالتدليس.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (5,'٤ ـ م عه:<span data-type="title" id=toc-7> بقية بن الوليد </span>مشهور بالتدليس مكثر له عن الضعفاء يعاني تدليس التسوية وهو أفحش أنواع التدليس',NULL,'37',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (6,'٥ ـ ت:<span data-type="title" id=toc-9> تليد بن سليمان </span>المحاربي الكوفي قال العجلي: كان يدلس',NULL,'38',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (7,'٦ ـ د ت ق:<span data-type="title" id=toc-11> جابر الجعفي </span>قال',NULL,'38',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (8,'أبو نعيم: قال سفيان الثوري: كلما قال فيه جابر سمعت أو حدثنا فاشدد يديك به، وما كان سوى ذلك فتوقه.
INSERT INTO [b1176] VALUES (9,'حبان: كان مدلساً وروى أبو بكر بن عياش عن الأعمش قال لي حبيب بن أبي ثابت: لو أن رجلاً حدثني عنك ما باليت أن أرويه عنك.
INSERT INTO [b1176] VALUES (10,'وغيرهم.
INSERT INTO [b1176] VALUES (11,'١٠ ـ خ د ت ق:<span data-type="title" id=toc-16> الحسن بن ذكوان </span>قال محمد بن نصر المروزي في حديثه عن حبيب بن أبي ثابت عن عاصم بن ضمرة عن عمرو بن خالد عن حبيب بن أبي ثابت فدلسه بإسقاط عمرو بن خالد لأنه منكر الحديث، وكذلك قال يحيى بن معين في كل ما رواه الحسن بن ذكوان عن حبيب بن أبي ثابت أن بينه وبين حبيب رجلاً ليس بثقه.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (12,'قلت: وروى العقيلي في الضعفاء من رواية يحيى القطان عن الحسن بن ذكوان عن الحسن عن عبد الله بن مغفل في النهي عن البول في المغتسل قال يحيى: قيل له: سمعته من الحسن؟ قال: لا قال العقيلي: ولعل الحسن بن ذكوان أخذه عن أشعث الحداني، انتهى',NULL,'43',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (13,'١١ ـ ز:<span data-type="title" id=toc-17> الحسن بن مسعود </span>بن الحسن أبو علي بن الوزير الدمشقي المحدث متأخر ما ت سنة ٥٤٣ هـ
INSERT INTO [b1176] VALUES (14,'١٢ ـ خت م عه:<span data-type="title" id=toc-18> الحسين بن واقد </span>المروزي ذكره أبو يعلى',NULL,'44',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (15,'الخليلي فيمن يدلس
INSERT INTO [b1176] VALUES (16,'١٤ ـ<span data-type="title" id=toc-20> الحكم بن عتيبة </span>وصفه بالتدليس غير واحد',NULL,'46',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (17,'١٥ ـ زع:<span data-type="title" id=toc-21> حماد بن أسامة </span>أبو أسامة الكوفي الحافظ قال',NULL,'46',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (18,'الأزدي: قال المعيطي: كان كثير التدليس ثم بعد تركه.
INSERT INTO [b1176] VALUES (19,'١٧ ـ ز:<span data-type="title" id=toc-23> حميد بن الربيع </span>اللخمي الكوفي قال محمد بن عثمان',NULL,'48',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (20,'ابن أبي شيبة: قال أبي: أنا أعلم الناس بحميد بن الربيع هو ثقة لكنه شره يدلس
INSERT INTO [b1176] VALUES (21,'يدلس وكذلك أبو داود، انتهى.
INSERT INTO [b1176] VALUES (22,'الذهبي يدلس.
INSERT INTO [b1176] VALUES (23,'واحد.
INSERT INTO [b1176] VALUES (24,'٢٢ ـ ع:<span data-type="title" id=toc-30> سفيان بن عيينة </span>مشهور بالتدليس أيضاً.
INSERT INTO [b1176] VALUES (25,'واحد انتهى.
INSERT INTO [b1176] VALUES (26,'المرسلين وقال: كأنه مدلس فينبغي أن يذكر في المدلسين
INSERT INTO [b1176] VALUES (27,'٢٥ ـ ع:<span data-type="title" id=toc-33> سليمان الأعمش </span>مشهور بالتدليس أيضاً',NULL,'55',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (28,'٢٦ ـ م ق:<span data-type="title" id=toc-34> سويد بن سعيد </span>الحدثاني قال غير واحد كان كثير',NULL,'56',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (29,'التدليس
INSERT INTO [b1176] VALUES (30,'فيمن كان يدلس.
INSERT INTO [b1176] VALUES (31,'٢٩ ـ د:<span data-type="title" id=toc-38> شعيب بن أيوب </span>الصريفيني قال فيه ابن',NULL,'59',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (32,'حبان: كان يدلس',NULL,'59',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (33,'٣٠ ـ ع:<span data-type="title" id=toc-40> طاوس بن كيسان </span>أحد الأعلام ذكر حسين الكرابيسي في أثناء كلام له أنه أخذ عن عكرمة كثيراُ من علم ابن عباس وكان يرسله بعد ذلك عنه وهذا يقتضي أن يكون مدلساً ولم أر أحداً وصفه بذلك.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (34,'٣١ ـ ع:<span data-type="title" id=toc-41> طلحة بن نافع </span>أبو سفيان ذكره الحاكم ممن كان يدلس من التابعين',NULL,'61',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (35,'٣٢ ـ خت عه:<span data-type="title" id=toc-43> عباد بن منصور </span>الناجي قال',NULL,'61',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (36,'مهنا: سألت أحمد عنه فقال: كان يدلس
INSERT INTO [b1176] VALUES (37,'الذهبي في الميزان أنه كان يدلس عمن لحقهم ومن لم يلحقهم وكان له صحف يحدث منها ويدلس
INSERT INTO [b1176] VALUES (38,'٣٥ ـ<span data-type="title" id=toc-46> عبد الله بن أبي نجيح </span>المكي ذكره النسائي فيمن',NULL,'64',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (39,'كان يدلس رواه عنه أبو بكر بن الحداد الفقيه
INSERT INTO [b1176] VALUES (40,'٣٧ ـ د ت ق: عبد الرحمن بن زياد بن أنعم الإفريقي قال ابن حبان كان يدلس',NULL,'66',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (41,'٣٨ ـ ع: عبد الرحمن بن محمد بن زياد المحاربي قال عبد الله بن أحمد: بلغنا أنه كان يدلس',NULL,'67',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (42,'٣٩ ـ م عه: عبد المجيد بن عبد العزيز بن أبي رواد ذكر أحمد في حديث رواه عن عبيد الله بن عمر فقال: ينبغي أن',NULL,'68',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (43,'يكون عبد المجيد دلسه، أخذه من إنسان فحدث به ذكره الخلال في كتاب العلل
INSERT INTO [b1176] VALUES (44,'من التدليس
INSERT INTO [b1176] VALUES (45,'واحد
INSERT INTO [b1176] VALUES (46,'قلت: وقال صالح بن جزرة: أنكروا عليه حديث ثور في فضل العباس ما أنكروا عليه غيره كان ابن معين يقول: هذا موضوع فلعل الخفاف دلسه فإنه بلفظ عن، انتهى
INSERT INTO [b1176] VALUES (47,'خالد ذكره الذهبي في أرجوزة سمى فيها غالب المدلسين
INSERT INTO [b1176] VALUES (48,'الرازي بالتدليس
INSERT INTO [b1176] VALUES (49,'أحمد بن حنبل كان يدلس
INSERT INTO [b1176] VALUES (50,'أحمد أيضاً بذلك فيما رواه الأثرام عنه.
INSERT INTO [b1176] VALUES (51,'٤٧ - عمرو بن عبد الله أبو إسحاق السبيعي مشهور',NULL,'77',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (52,'بالتدليس
INSERT INTO [b1176] VALUES (53,'حبان: كان يدلس عن الثقات ما سمع من الضعفاء عنهم.
INSERT INTO [b1176] VALUES (54,'٥٠ ـ خت د ت ق:<span data-type="title" id=toc-63> المبارك بن فضالة </span>قال فيه أبو زرعة يدلس كثيراً وقال أبو داود: شديد التدليس',NULL,'80',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (55,'٥١ ـ خت م عه:<span data-type="title" id=toc-64> محمد بن إسحاق بن يسار </span>ممن أكثر من التدليس خصوصاً عن الضعفاء',NULL,'81',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (56,'٥٢ ـ ز ت: محمد بن إسماعيل بن إبراهيم البخاري صاحب الصحيح قال أبو عبد الله بن منده: قوله في صحيحه',NULL,'82',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (57,'قال فلان تدليس، وما علمنا لابن مندة موافقاً على ذلك، ولم ينسب أحد البخاري إلى شئ من التدليس
INSERT INTO [b1176] VALUES (58,'٥٤ ـ<span data-type="title" id=toc-67> محمد بن صدقة الفدكي </span>أبو عبد الله سمع مالك بن أنس وعنه إبراهيم بن المنذر الخزامي ذكره ابن الأثير في اختصار كتاب الأنساب للسمعاني أنه كان مدلسا.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (59,'٥٥ ـ خ د ت س:<span data-type="title" id=toc-68> محمد بن عبد الرحمن الطفاوي </span>سئل عنه أحمد فقال: كان يدلس رواه البرقاني في الثالث من كتاب اللفظ له.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (60,'٥٦ - خت م عه:<span data-type="title" id=toc-69> محمد بن عجلان المدني</span>، ذكره ابن أبي حاتم حديثه عن الأعرج عن أبي هريرة: " المؤمن القوي خير وأحب',NULL,'85',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (61,'إلى الله من المؤمن الضعيف"، فقال: إنما سمعه من ربيعة بن عثمان عن الأعرج.
INSERT INTO [b1176] VALUES (62,'ابن حبان أنه روى حديث مقتل عثمان عن ابن أبي ذئب قال: ولم يسمعه منه، إنما سمعه من إسماعيل بن يحيى أحد الضعفاء عنه فدلس وكذلك قال صالح بن محمد وغيره.
INSERT INTO [b1176] VALUES (63,'الطباع ذكره أبو داود بالتدليس وذلك في الخامس من سؤالات أبي عبيد الآخري.
INSERT INTO [b1176] VALUES (64,'قال: جئت أبا الزبير فدفع لي كتابين فانقلبت بهما ثم قلت في نفسي لو أني عاودته فسألته: أسمع هذا كله من جابر؟ فقلت له: أعلم لي على ما سمعت منه، فأعلم لي على هذا الذي عندي.
INSERT INTO [b1176] VALUES (65,'الزهري مشهور بالتدليس وقد قبل الأئمة قوله عن.
INSERT INTO [b1176] VALUES (66,'٦١ - ز ق:<span data-type="title" id=toc-74> محرز بن عبد الله </span>أبو رجاء الجزري مولى هشام بن عبد الملك قال ابن حبان في الثقات: كان يدلس عن مكحول يعتبر بحديثه ما بين فيه السماع عن مكحول وغيره.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (67,'٦٢ -<span data-type="title" id=toc-75> مروان بن معاوية الفزاري </span>قال يحيى بن معين: ما رأيت',NULL,'91',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (68,'أحيل للتدليس منه. قلت: عبارته وقد سأله عباس الدوري عن حديث مروان بن معاوية عن علي بن أبي الوليد هذا علي بن غراب والله ما رأيت أحيل للتدليس منه وهذا يقتضي أنه أراد تدليس الشيوخ، والذي نحن بصدده الآن من يدلس تدليس الإسناد، انتهى.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (69,'٦٣ - ع:<span data-type="title" id=toc-76> مغيرة بن مقسم الضبي </span>قال محمد بن فضيل كان يدلس وكنا لا نكتب عنه إلا ما قال: حدثنا إبراهيم، وقال أحمد بن حنبل: عامة حديثه عن إبراهيم مدخول إنما سمعه من حماد ومن يزيد بن الوليد والحارث العكلي وعبيدة وغيرهم وجعل يضعف حديثه عن إبراهيم وحده.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (70,'قلت: وقال العجلي: كان يرسل الحديث عن إبراهيم وإذا وقف أخبرهم ممن سمعه، وقال أبو داود: مغيرة لا يدلس، سمع من إبراهيم ـ مائة وثمانين حديثاً، انتهى.
INSERT INTO [b1176] VALUES (71,'قلت: وقال ابن حبان: ربما دلس، انتهى.
INSERT INTO [b1176] VALUES (72,'٦٦ - ت ق:<span data-type="title" id=toc-79> ميمون بن موسى المرائي </span>البصري قال فيه',NULL,'95',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (73,'أحمد بن حنبل: كان يدلس ولا يقول حدثنا الحسن. قلت: وقال أبو عبيد الآجري: عن ابي داود روى عن الحسن ثلاثة أشياء يعني سماعا. انتهى.
INSERT INTO [b1176] VALUES (74,'أمرين، وما ضرب بيده شيئا الحديث، فلما سألته قال: أخبرني أبي عن عائشة، قالت: ما خير رسول الله صلى الله عليه وسلم بين أمرين، لم أسمع من أبي إلا هذا، والباقي لم أسمعه إنما هو عن الزهري',NULL,'97',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (75,'رواه الحاكم في علومه عن ابن المديني.
INSERT INTO [b1176] VALUES (76,'٦٩-<span data-type="title" id=toc-84> الوليد بن مسلم </span>الدمشقي كذلك ويعاني تدليس التسوية أيضاً.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (77,'٧٠- ز ع:<span data-type="title" id=toc-86> لاحق بن حميد </span>السدوسي أبو مجلز البصري قال الذهبي في الميزان: يدلس',NULL,'100',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (78,'٧١ - د ت ق:<span data-type="title" id=toc-88> يحيى بن أبي حية</span>، أبو جناب الكلبي، ضعفوه،',NULL,'100',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (79,'وقال أبو زرعة: صدوق يدلس
INSERT INTO [b1176] VALUES (80,'ترجمة محمد بن عمرو بن علقمة.
INSERT INTO [b1176] VALUES (81,'النسائي وغيره
INSERT INTO [b1176] VALUES (82,'الحديث فيمن كان يدلس.
INSERT INTO [b1176] VALUES (83,'٧٦ - ع:<span data-type="title" id=toc-93> يونس بن عبيد: </span>ذكره بالتدليس',NULL,'104',NULL,NULL,'0');
INSERT INTO [b1176] VALUES (84,'النسائي وغيره.
INSERT INTO [b1176] VALUES (85,'متكلم فيه، وخرج الترمذي من طريقه عن الحكم عن عبد الرحمن بن أبي ليلى عن بلال حديث: " لا تثوبن في شيء من الصلوات إلا في صلاة الفجر" ثم قال الترمذي: لم يسمع أبو إسرائيل هذا الحديث من لحكم، يقال أنه رواه عن الحسن بن عمارة عن الحكم.
INSERT INTO [b1176] VALUES (86,'ابن عبد الرحمن، قال أحمد بن حنبل: صاحب تدليس عن الحسن إلا أن يحيى بن سعيد روى عنه ثلاثة أحاديث يقول في بعضها حدثنا الحسن، وقال البخاري: يتكلمون في روايته عن الحسن.
INSERT INTO [b1176] VALUES (87,'تعرف أبا سعد البقال؟ قال: إني والله أعرفه، عالي الإسناد، أنا حدثته عن عبد الكريم الجزري عن زياد بن أبي مريم عن عبد الله بن معقل عن ابن مسعود حديث (الندم توبة) فتركني وترك عبد الكريم بن أبي مريم، وروى عن عبد الله بن معقل عن ابن مسعود الحديث.
INSERT INTO [b1176] VALUES (88,'هؤلاء كلهم ليسوا على حد واحد بحيث أنه يتوقف في كل ما قال فيه واحد منهم عن ولم يصرح بالسماع بل هم على طبقات:
INSERT INTO [b1176] VALUES (89,'صرحوا فيه بالسماع وقبلهم آخرون مطلقاً كالطبقة التي قبلها لأحد الأسباب المتقدمة كالحسن وقتادة وأبي إسحاق السبيعي، وأبي الزبير المكي، وأبي سفيان وعبد الملك بن عمير.
INSERT INTO [b1176] VALUES (90,'وأكثر المحدثين تدليساً أهل الكوفة ونفر يسير من أهل البصرة.
INSERT INTO [b1176] VALUES (91,'يملي علي فقال لي: أبو بريد عمرو بن بريد الجرمي فأمسكت عن الكتابة ثم أعاد ثانيا، ثم قال حديث سرار بن محشر فقلت: قد أغناك الله عنه يا أبا بكر، فقد حدثناه أبو عبد الرحمن النيسابوري حدثنا أبو بريد فإن أخذ أحد من أهل بغداد التدليس فعن الباغندي وحده. انتهى كلام الحاكم ـ رحمه الله ـ وبانتهائه انتهى كلامنا. والله الموفق.
