...
٤- كشكشة أسد أو ربيعة: هي إبدال شين من كاف المخاطبة نحو رأيتش.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12046] VALUES (121,'٥-<span data-type="title" id=toc-48> فحفحة هذيل: </span>وهي قلب الحاء عينا مثل: عتى أي حتى.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12046] VALUES (122,'٦-<span data-type="title" id=toc-49> وكم ربيعة: </span>وهي كسر كاف الخطاب بعد الياء الساكنة أو الكسرة نحو عليكم، وبكم.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12046] VALUES (123,'٧-<span data-type="title" id=toc-50> وهم بني كلب: </span>وهي كسر هاء الغيبة إذا لم يكن قبلها ياء ساكنة ولا كسرة نحو بينهم، وعنهم.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12046] VALUES (124,'٨-<span data-type="title" id=toc-51> جمجمة قضاعة: </span>وهي قلب الياء الأخيرة جيما نحو الساعج بدل الساعي.',NULL,'103',NULL,NULL,'0');
