INSERT INTO [b12046] VALUES (126,'١٠-<span data-type="title" id=toc-53> الاستنطاء: </span>في لغة سعد والأزد وقيس، وهو قلب العين الساكنة نونًا قبل الطاء نحو أنطى في أعطى.',NULL,'103',NULL,NULL,'0');
