INSERT INTO [b12047] VALUES (188,'"يا عثمان أفطر عندنا" ١ فأصبح
_________
١ ابن سعد، الطبقات (٣/ ٧٥) ، وأبو يعلى، المقصد العلي (ق١٦٣ب -ق١٦٤أ) ، والبزار، كشف الأستار (٣/ ١٨١) ، وابن عساكر، تاريخ دمشق، ترجمة عثمان (٣٩٠) ، وذكره الهيثمي في مجمع الزوائد (٧/ ٢٣٢) ؛ كلهم من رواية أبي علقمة، مولى عبد الرحمن بن عوف، عن كثير بن الصلت، قال الهيثمي: أبو علقمة لم أعرفه، وباقي رجاله ثقات، قلت: وهو كما قال، فلم أجد له ترجمة، وباقي رجاله ثقات، انظر الملحق الرواية رقم: [٩٧] . وبهذين الطريقين، يرتقي الخبر إلى درجة الحسن لغيره.
أبو يعلى، المقصد العلي (ق١٦٤أ) ، ومن طريقه ابن عساكر، تاريخ دمشق، ترجمة عثمان (٣٩٠) ، والبزار، كشف الأستار (٣/ ١٨١) ، واللالكائي، شرح أصول اعتقاد أهل السنة والجماعة (ق٢٥٧ب) ، وابن عساكر، تاريخ دمشق، ترجمة عثمان (٣٩١) ؛ كلهم من طريق نافع مولى ابن عمر، عن ابن عمر عن عثمان -رضي الله عنهم-، وفي الإسناد أبو جعفر الرازي: صدوق، سيء الحفظ، وابن سعد، الطبقات (٣/ ٧٤-٧٥) ، وابن عساكر، تاريخ دمشق، ترجمة عثمان؛ من طريق يعلى بن حكيم عن نافع عن عثمان رضي الله عنه، وإسناده صحيح إلى نافع، ونافع لم يدرك عثمان رضي الله عنه، فالإسناد منقطع، انظر الملحق الرواية رقم: [٩٧] .
وعبد الله بن أحمد، مسند أحمد، بتحقيق أحمد شاكر (١/ ٣٨٨-٣٨٩) ، ومن طريقه ابن الأثير، أسد الغابة (٣/٤٩٠) ، وابن عساكر، تاريخ دمشق، ترجمة عثمان (٣٩٣) ، وأبويعلى، المقصد العلي (ق١٦٤أ) ، وأبو عرب، المحن (٦٤) ، وذكره المحب الطبري، الرياض النضرة (٣/ ٦٧-٦٨) ، والهيثمي، مجمع الزوائد (٧/ ٢٣٢) ؛ كلهم من رواية مسلم أبي سعيد مولى عثمان بن عفان، عن عثمان رضي الله عنه، وقال عنه الهيثمي: رجاله ثقات، قلت: مسلم لم يوثقه غير ابن حبان، انظر الملحق الرواية رقم: [٩٨] .
وابن سعد، الطبقات (٣/٧٥) ، وعبد الله بن أحمد، مسند أحمد، بتحقيق أحمد شاكر (٢/٧) ، وذكره الهيثمي في مجمع الزوائد (٧/ ٢٣٢) ؛ من رواية أم هلال بنت وكيع عن نائلة بنت الفرافصة امرأة عثمان بن عفان عنه، وقال الهيثمي: "وفيه من لم أعرفهم"، وقال أحمد شاكر: "فيه نظر"، وأعله بزياد وبأم هلال، قلت: زياد ضعيف، وأم هلال مجهولة، فالإسناد ضعيف بهما، انظر الملحق الرواية رقم: [٩٩] .
والبخاري في التاريخ الكبير (١/٢٦٢) ، وخليفة بن خياط، المسند جمع الدكتور أكرم العمري (٤٦) ، من رواية عبد الله بن سلام عن كثير بن الصلت، وفيه شعيب بن صفوان، ومحمد بن يوسف، قال الحافظ عن كل منهما: "مقبول"، انظر الملحق الرواية رقم: [١٠٢] .','1','211',NULL,NULL,'0');
INSERT INTO [b12047] VALUES (189,'صائماً١
_________
١ ابن سعد، الطبقات (٣/٧٥) ، وأبو يعلى، المقصد العلي (ق١٦٣ب -ق١٦٤أ) ، والبزار، كشف الأستار (٣/ ١٨١) ، وابن عساكر، تاريخ دمشق، ترجمة عثمان (٣٩٠) ، وذكره الهيثمي في مجمع الزوائد (٧/ ٢٣٢) ؛ كلهم من رواية أبي علقمة مولى عبد الرحمن بن عوف، عن كثير بن الصلت، قال الهيثمي: أبو علقمة لم أعرفه، وباقي رجاله ثقات، قلت: وهو كما قال، فلم أجد له ترجمة، وباقي رجاله ثقات، انظر الملحق الرواية رقم: [٩٧] .
