INSERT INTO [b12102] VALUES (41,'- سورة إبراهيم:
الآية "٢٤". الدرر ص "١٢٦".
- سورة الحجر:
جميعها. الاستنباط ص "١٨٣- ١٩٧" الدرر ص "١٢٦- ١٢٩".
- سورة النحل:
جميعها. الاستنباط ص "١٩٩- ٢٣٦" الدرر ص "١٣٠- ١٣٨".
كلام في معنى السلطان المثبت في قوله تعالى: {إِنَّمَا سُلْطَانُهُ عَلَى الَّذِينَ يَتَوَلَّوْنَهُ وَالَّذِينَ هُمْ بِهِ مُشْرِكُونَ} الآية "١٠٠" النحل. والسلطان المنفي في قوله: {وَمَا كَانَ لِيَ عَلَيْكُمْ مِنْ سُلْطَانٍ إِلَّا أَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي..} الآية "٢٢" إبراهيم- الدرر "١٣٨".
الآيات "١٢٠- ١٢٣" مرة أخرى الاستنباط ص "٢٣٧" الدرر ص "١٣٨".
- سورة الكهف:
الآيات "١- ١٠" الاستنباط ص "٢٣٩- ٢٤٢" الدرر ص "١٣٩، ١٤٠".
الآيات "١٣- ١٦" الاستنباط ص "٢٤٢- ٢٤٤" الدرر ص "١٤١".
الآيات "١٩- ٢٩" الاستنباط ص "٢٤٥- ٢٥٠" الدرر ص "١٤١- ١٤٣".
الآية "٤٩". الاستنباط ص "٢٥٠" الدرر ص "١٤٣".
- قصة موسى والخضر. وهي الواردة في الآيات "٦٠- ٨٢" ٠ الاستنباط ص "٢٥١- ٢٦٠" الدرر ص "١٤٣- ١٤٥".
الآية "١١٠" الاستنباط ص "٢٦٠، ٢٦١" الدرر ص "١٤٥، ١٤٦".
- سورة طه:
الآيات "١٢٤- ١٢٧" الاستنباط ص "٢٦٣- ٢٦٨" الدرر ص "١٤٦- ١٤٧".
- سورة المؤمنون:
الآيات "٥١- ٥٣" الاستنباط ص "٢٦٩، ٢٧٠" الدرر ص "١٤٩، ١٥٠".
- سورة النور:
الآيات "١- ٣٧" الاستنباط ص "٢٧١- ٢٧٨" الدرر ص "١٥٠، ١٥١".',NULL,'33',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (42,'الآيتان "٣٩، ٤٠" الاستنباط ص "٢٧٨" الدرر ص "١٥١".
الآيات "٤٧- ٥٤" الاستنباط ص "٢٧٨، ٢٧٩" الدرر ص "١٥١".
- سورة القصص:
الآيات "١- ٤٢" الاستنباط ص "٣٨١- ٢٩٤" الدرر ص "١٥٢- ١٥٥".
- قصة "موسى وفرعون" في السور الأخرى، الاستنباط ص "٢٩٥- ٣١٦" الدرر ص "١٥٥- ١٥٩" والسور هي:
طه: الآيات "٩- ٧٥" الأعراف الآيات "١٠٤، ١٠٥، ١١٦، ١١٨، ١١٩". "١٢٣-
١٣٧" الشعراء: الآيات "١٨- ٣٩" "٤٤" "٥٠- ٦٨" النمل: الآيات "٨- ١٤" يونس: الآيات "٧٧- ٩٢" هود: الآيات "٩٦- ٩٨" الإسراء: الآيات "١٠١-١٠٤" الحج: الآيات "٤٢- ٤٤" الصافات: الآيتان "١١٤، ١١٥" غافر: الآيات "٢٣- ٤٦" الزخرف: الآيات "٤٦- ٥٦" الدخان: الآيات "١٧- ٣٠" المؤمنون: الآيات "٤٥- ٤٨" الذاريات: الآيتان "٣٨، ٣٩" القمر: الآيات "٤١- ٤٣". المزمل: الآيات "١٥- ١٧" النازعات: الآيات "١٥- ٢٦".
- الآيات "٥٨- ٦١" الدرر ص "١٥٩، ١٦٠".
- سورة الزمر:
جميعها الاستنباط ص "٣١٧- ٣٤٣" الدرر ص "١٦٢- ١٦٩".
كلام يتعلق بقوله: {فَوَيْلٌ لِلْقَاسِيَةِ قُلُوبُهُمْ} في الآية "٢٢" الدرر ص "١٦٩".
كلام يتعلق بقوله: {مَثَانِيَ} في الآية "٢٣" الدرر ص "١٦٩".
الآيات "٦٤- ٦٧" مرة أخرى. الاستنباط ص "٣٤٤ - ٣٤٨" الدرر ص "١٦٩- ١٧١".
استنباط من سورة "الفتح" وأغلبه من أحداث القصة ذاتها وهو أشبه بها يسمى "فقه السيرة" الدرر ص "١٧٢- ١٧٦".
- سورة الحجرات:-
الآيات "١- ٨" وذكرت الآيتان "٩، ١٠" ولم يذكر تفسيرهما. الاستنباط "٣٤٩- ٣٥٤" الدرر ص "١٧٦- ١٧٨".
- سورة الحديد:
الآيتان "٢٣، ٢٤" الدرر ص "١٨٣".',NULL,'34',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (43,'سورة المجادلة:
الآية "١١" الدرر ص "١٨٢، ١٨٣".
استنباطات عامه من السور الثلاث "نوح، والجن، والمزمل" الدرر ص "١٨٥".
- سورة الجن:
جميعها الاستنباط ص "٣٥٥- ٣٦٣" الدرر ص "١٨٥- ١٨٨".
الآية "١٨" مرة أخرى. الاستنباط ص "٣٦٢، ٣٦٣" الدرر ص "١٨٨".
- سورة المدثر:
الآيات "١- ٧" الاستنباط ص "٣٦٥، ٣٦٦" الدرر ص "١٩٠".
- مقارنات بين سورتي "اقرأ" "العلق" والمدثر الاستنباط ص "٣٦٦، ٣٦٧" الدرر ص "١٩٠، ١٩١".
- سورة العلق:
جميعها الاستنباط ص "٣٦٩- ٣٧٣" الدرر ص "١٨٩، ١٩٠".
- استنباطات من سورة: اقرأ. "العلق" إلى آخر القرآن. الاستنباط ص "٣٧٥- ٣٨٠" الدرر ص "١٩١، ١٩٢".
- ما في قصة سبب نزول "تبت" "المسد" إلى آخرها من المسائل الاستنباط ص "٣٨١، ٣٨٢".
- ذكر ما في سورة "التكاثر" إلى آخر القرآن من المسائل الدرر ص "١٩٣".
- استنباطات من بعض قصار السور. الدرر ص "١٩٣".
- استنباطات من بعض قصار السور. الدرر ص "١٩٤".
- سورة الإخلاص:
جميعها، الاستنباط ص "٣٨٣" الدرر ص "١٩٨، ١٩٩".
- سورة الفلق:
جميعها. الاستنباط ص "٣٨٥، ٣٨٦" الدرر ص "١٩٩، ٢٠٠".
- سورة الناس:
جميعها الاستنباط ص "٣٨٧- ٣٨٩" الدرر ص "٢٠٠".
وهذا عدا ما نقله عن شيخ الإسلام ابن تيمية في التفسير فلم أثبته هنا.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (44,'٣- مختصر تفسير سورة الأنفال:
ويقع ضمن مجموعة المؤلفات/ ملحق المصنفات.
٤- تفسير سورة الفلق:
وهو مختصر من تفسير الإمام ابن القيم لها.
وموجود بالمتحف العراقي ببغداد ومصورته بالجامعة الإسلامية بالمدينة المنورة رقم "٢٣٢٠". وقد حققه أخيراً الدكتور/ فهد بن عبد الرحمن الرومي.
كما تناسب للشيخ كتب في التفسير أخرى ومنها:
١- مختصر تفسير سورة الحجرات:
وهو مصور بجامعة الملك سعود بالرياض برقم "١٦٣٩" ضمن مجموع يشتمل على عشرين كتابا لعلماء السلف كابن تيمية ومحمد بن عبد الوهاب وغيرهما. وهو بقدر سبع صفحات ونصف تقريبا بمعدل "٢٥" سطراً في الصفحة الواحدة. وهو مختصر من تفسير الإمام ابن كثير وجار على منهج الشيخ في الاختصار بالمحافظة على اللفظ المختصر حتى كأنه منتخب منه انتخاباً.
٢- قطعة من مختصر تفسير ابن كثير:
وهي موجودة لدى الشيخ: عبد الرحمن الفارس١ بالرياض.
وهو جزء من المختصر، مبتور من أوله ومن آخره، إذ يبدأ من أواخر ما يتعلق بتفسير الآية "الثالثة والستين" من سورة الأنبياء. وينتهي بعد سطرين مما يتعلق بتفسير آخر سورة الحجرات. وقد أفادني الشيخ الفارس أنه كان في أوله ورقة فيها نسبته إلى الشيخ محمد "رحمه الله" ويقع هذا التفسير في حوالي "٢٨٠" ورقة تترواح عدد سطور صفحاتها من "٢٣" إلى "٣٣" سطرا، وأكثر صفحاتها إلى "٣٣" سطراً أقرب.
_________
١ قاضي تمييز متقاعد بالرياض وهو من تلاميذ الشيخ محمد بن إبراهيم "رحمه الله" انظر مشاهير علماء نجد ص "١٣٧".',NULL,'36',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (45,'وهذا التفسير جارٍ على منهج الشيخ في الاختصار كما في سابقه.
وقد ذكر للشيخ مختصرا التفسير ابن كثير صاحب تحفة المستفيد بتاريخ الأحساء القديم والجديد "١: ١٢٧" للشيخ محمد بن عبد الله بن عبد المحسن آل عبد القادر الأحسائي. فالله أعلم.
٢- تفسير سورة النصر.
وهي تالية لمختصر تفسير سورة الحجرات المتقدم ذكره وبقدر ثمان صفحات تقريبا معدل كل صفحة "٢٦" سطرا.
وقد تبين لي بالمقارنة أنها للحافظ ابن رجب الحنبلي وقد نسبت في المخطوط للشيخ محمد بن عبد الوهاب خطأ.
ثانياً: مؤلفاته فيما يتعلق بالحديث.
١- كتاب مجموع الحديث على أبواب الفقه.
وهو ضمن مجموعة المؤلفات/ قسم الحديث "الأجزاء ١، ٢، ٣، ٤".
٢- كتاب بعنوان: هذه أحاديث في الفتن والحوادث التي أخبر النبي صلى الله عليه وسلم أنها ستكون بعده جمعها الشيخ/ محمد ابن عبد الوهاب.
٣- مختصر فتح الباري:
ذكره ابن قاسم في الدرر السنية١،ولم أقف عليه.
_________
١ انظر "١٢: ١٩".',NULL,'37',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (46,'ثالثاً:- مؤلفاته فيما يتعلق بالعقيدة والآداب الإسلامية.
وسأبدأ بذكر ما جمع مجموعة المؤلفات/ القسم الأول/العقيدة والآداب الإسلامية وهو ما يلي:-
١- كتاب التوحيد الذي هو حق الله على العبيد. ص "٧- ١٥١".
٢- كشف الشبهات. ص "١٥٣- ١٨١".
٣- ثلاثة الأصول. ص "١٨٣- ١٩٦".
٤- القواعد الأربع. ص "١٩٧- ٢٠٢".
٥- فضل الإسلام. ص "٢٠٣- ٢٢٧".
٦- أصول الإيمان. ص "٢٢٩- ٢٧٧".
٧- كتاب مفيد المستفيد في كفر تارك التوحيد. ص "٢٧٩- ٣٢٩".
٧- مجموعة رسائل في التوحيد والإيمان ومنها:-
- الرسالة الأولى: "مسائل الجاهلية" ص "٣٣٢- ٣٥٢".
- الرسالة الثانية: "شرح ستة مواضع من السيرة" ص "٣٥٣- ٣٦٢".
- الرسالة الثالثة: "تفسير كلمة التوحيد" ص "٣٦٣- ٣٦٩".
- الرسالة الرابعة: "تلقين أصول العقيدة للعامة" "٣٧٠- ٣٧٣".
- الرسالة الخامسة: "ثلاث مسائل". ص "٣٧٤، ٣٧٥".
- الرسالة السادسة: "معنى الطاغوت ورؤوس أنواعه" "٣٧٦- ٣٧٨".
- الرسالة السابعة: "الأصل الجامع لعبادة الله وحده" "٣٧٩- ٣٨١".
- رسالة في "نواقض الإسلام" ص "٣٨٥- ٣٨٧".
- رسالة في "ستة أصول عظيمة مفيدة" في "٣٩٣- ٣٩٧".
- "رسالة في توحيد العبادة" ص "٣٩٨ -٣٩٩".
٩- كتاب الكبائر.
وهو ضمن مجموعة المؤلفات/القسم الأول/ العقيدة والآداب الإسلامية.
١- رسالة في الرد على الرافضة..
وهو ضمن مجموعة المؤلفات/ ملحق المصنفات.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (47,'كما تذكر له بعض المختصرات في العقيدة. لم أقف على شيء منها وهي:
١- مختصر الإيمان.
٢- مختصر العقل والنقل.
٣- مختصر الصواعق.
٤- مختصر المنهاج١.
رابعاً: مؤلفاته فيما يتعلق بالفقه والأحكام.
وهي ضمن مجموعة المؤلفات/ القسم الثاني / الفقه.
١- مختصر الإنصاف والشرح الكبير "ضمن المجلد الأول" وما بعده ضمن المجلد الثاني.
٢- أربع قواعد تدور الأحكام عليها.
٣- مبحث الاجتهاد والخلاف.
٤- كتاب الطهارة.
٥- شروط الصلاة وأركانها وواجباتها.
٦- كتاب آداب المشي إلى الصلاة.
٧- أحكام الصلاة.
خامساً: مؤلفاته في السيرة وفقهها.
١- مختصر سيرة الرسول "صلى الله عليه وسلم".
وهو ضمن مجموعة المؤلفات / القسم الثالث.
وقد ذكر له كل من ابن غنام وابن قاسم كتابا آخر في السيرة وهو "السيرة المطولة" ولم أقف عليه٢.
_________
١ ذكر هذه المختصرات وغيرها ابن قاسم في الدرر السنية "١٢: ١٨،١٩".
٢ انظر روضة الأفكار: "١: ٥٠" والدرر السنية"١٢: ١٨"، وانظر تعليق شخنا الدكتور صالح العبود في كتابه "عقيدة الشيخ محمد بن عبد الوهاب.." ص "١٢٣" حول ما ذكر.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (48,'٣- مختصر زاد المعاد.
وهو ضمن مجموعة المؤلفات/ القسم الرابع.
٣ - بعض فوائد صلح الحديبية.
وهو ضمن مجموعة المؤلفات/ ملحق المصنفات.
٤- شرح ستة مواضع من السميرة.
وهو ضمن مجموعة المؤلفات/ القسم الأول ص "٣٥٣- ٣٦٢"
وغير ذلك من الرسائل والمكاتبات والخطب التي جمع الكثير منها.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (49,'وفاته:
في سنة "١٢٠٦" ست ومائتين وألف من الهجرة النبوية توفي الشيخ: الإمام محمد بن عبد الوهاب بمدينة الدرعية عن عمر يناهز الثانية والتسعين١.
ذكر ابن قاسم عن يوم جنازته "وكان يوماً مشهوداً، تزاحم الناس على سريره وصلوا عليه في بلدة الدرعية، وخرج مع جنازته الكبير والصغير٢.
وقد رثاه الشعراء وأثنى عليه العلماء.
فرحمه الله رحمة واسعة، واسكنه فسيح جناته، وأتم له أجر من دعا إلى هدى، وحشرنا وإياه في زمرة الذين أنعم عليهم من النبيين والصديقين والشهداء والصالحين وحسن أولئك رفيقاً.
ثناء العلماء عليه مفسراً:
إن العظماء في التاريخ كالشيخ محمد "رحمه الله" لتثني عليهم جهودهم العظيمة وآثارهم الحميدة وقد أثنى العلماء والمنصفون على الشيخ ثناءً عطراً، فلا تكاد تعثر على ترجمة له إلا وتجدها تفوح بالثناء العاطر على هذا الإمام الجليل والمصلح النبيل.
وسأقتصر هنا على إيراد بعض ثناء العلماء في ما يناسب المقام وذلك في جانب الثناء عليه مفسراً.
فقد تبين من خلال ترجمة الشيخ ورحلاته العلمية أنه قرأ التفسير وتأمل فيه وبرز وقد صرح بعض العلماء بذكر منزلته فيه فمن ذلك:-
تلميذه ومعاصره ابن غنام حيث يقول: "كان رحمه الله تعالى رحمة واسعة.. قد أعطى في القرآن فهماً وقاداً حديداً، ومقولاً باهراً مصيباً سديداً موفقاً مجيداً، فكان إذا تكلم على الآيات ونزلها على الواقع بهر
_________
١ روضة الأفكار لابن غنام "٢: ١٥٤" وعنوان المجد "١: ٩٥".
٢ الدرر السنية "١٢: ٢٠".',NULL,'41',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (50,'السامع كلامه" ١.
وقال في الثناء عليه في هذا الجانب أيضاً " ... المستنبط من كتاب الله تعالى ما يقصر عن بعضه الفهم.. المتفنن في فهم القرآن والاستنباط.." ٢.
ولهذه الشهادة وزنها وقيمتها لصدورها ممن عاصر الشيخ وجالسه وأخذ عنه مع بروزه في علم اللغة والنحو٣.
وممن أثنى عليه بالتفسير ومعرفته ابن بشر في "عنوان المجد" حيث قال عند ذكر مؤلفاته:
وكلامه على القرآن أكثر من مجلد. أتى فيه بالعجب العجاب من تقرير التوحيد ومعرفة الشرك، وكل آية وقصة يأتي عليها بعدة مسائل حتى أتى من قصه موسى والخضر في سورة الكهف بقريب مائة مسألة.
وممن أثنى عليه بهذا أيضاً العلامة الشوكاني في رثائه له بقصيدته الطويلة الشهيرة وفيها:
فمن للبخاري بعده ولمسلم ... يبين المخبا منهما للمحاول
ومن ذا لتفسير الكتاب ومن ترى ... لأحكام فقه الدين من للرسائل٤
وممن أثنى عليه في هذا المجال أيضاً الشيخ ابن قاسم في "الدرر السنية" حيث قال: وله من المسائل المستنبطات من كتاب الله ما يقصر عنه فهم الفحول الأفاضل ولا يقدر على إبرازه ذوو التدقيق الأماثل. تكلم على غالب السور واستنبط منها من الفوائد ما لم يسبق إليه٥.
وكل ناظر في تفسير الشيخ بفكر وتعمق وروية مع تجرد عن الهوى لا يملك إلا أن يذعن لهذا الإمام بالمعرفة بهذا الفن، وما أوتيه من دقة فهم واستنباط وغوص لمعان عميقة ونكت بديعة مما يدل على تمكنه فيه.
_________
١ روضة الأفكار "١: ٢٢٢".
٢ المرجع السابق "٢: ١٥٤".
٣ انظر الثناء على ابن غنام بمعرفة علوم العربية في تحفة المستفيد "٢: ١٠٤" مع دلالة أسلوبه في "روضة الأفكار" على ذلك.
٤ الدرر السنية "١٢: ٢٣".
٥ المرجع السابق "١٢: ١٩".',NULL,'42',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (51,'<span data-type="title" id=toc-3>قسم الدراسة - الباب الأول: تفسير الشيخ القرآن بالمأثور</span>
<span data-type="title" id=toc-4>الفصل الأول: تفسيره القرآن بالقرآن</span>
...
الباب الأول: تفسير الشيخ القرآن بالمأثور
ويشتمل على ثلاثة فصول:
الفصل الأول: تفسيره القرآن بالقرآن.
الفصل الثاني: تفسيره القرآن بالسنة.
الفصل الثالث: تفسيره القرآن بأقوال السلف من الصحابة والتابعين.
ويشتمل على ثلاثة مباحث:-
المبحث الأول: تفسيره القرآن بأقوال الصحابة.
المبحث الثاني: تفسيره القرآن بأقوال التابعين.
المبحث الثالث: موقفه من اختلاف السلف في التفسير.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (52,'الفصل الأول: تفسيره القرآن بالقرآن.
إن المنهج الصحيح الذي ارتضاه المحققون لتفسير كتاب الله عز وجل يبدأ من القرآن نفسه، وذلك أن كثيراً من الآيات يفسر بعضها بعضا، ويوضح المراد منه؛ فما أجمل في موضع من القرآن الكريم قد يبين في موضع آخر، وما أطلق في موضع قد يلحقه التقييد في موضع آخر، وما جاء عاماً قد يدخله التخصيص في موضع آخر، وهكذا.
قال شيخ الإسلام ابن تيمية رحمه الله تعالى: فإن قال قائل: فما أحسن طرق التفسير؟
فالجواب: إن أصح الطرق في ذلك أن يفسر القرآن بالقرآن. فما أجمل في مكان فإنه قد فسر في موضع آخر، وما اختصر في مكان فقط بسط في موضع آخر١. ا. هـ.
فلهذا كان لا بد لمن يتعرض لتفسير كلام الله عز وجل أن ينظر أولا في القرآن ليفسر بعضه ببعض فإن صاحب الكلام أعلم بكلامه ومراده منه.
وفي أثناء دراستي لتفسير الشيخ محمد بن عبد الوهاب رحمه الله اتضح لي أنه لم يغفل هذا المسلك الرشيد، بل كان يسلكه كلما أمكن ذلك، ووجد من الآيات ما يساعده على تحقيقه. وذلك في سبيل إيضاح المعنى وبيانه، وهو الغرض الذي يرمي إليه الشيخ من تفسيره. وأي إيضاح وبيان أعظم من بيان القرآن للقرآن؟ وذلك غاية البيان. ومما يلاحظ أن للشيخ أغراضا من إيراد الآيات التي يفسر بعضها بعضا أو يتعلق بعضها ببعض وأهمها ما يلي:
أولاً: تفسير الآية بالآية، وبيان مبهم فيها، أو نحو ذلك، وهذا هو الأصل في تفسير القرآن بالقرآن، وأمثله ذلك كثيرة في تفسيره أورد منها ما يلي:
_________
١ انظر: "مقدمة في أصول التفسير" لشيخ الإسلام ابن تيمية ص "٩٣".
وقد أورد كل من الزركشي في "البرهان في علوم القرآن" وابن كثير في تفسيره هذا الكلام بنصه. فانظر البرهان "٢: ١٧٥" وتفسير ابن كثير "١: ٤".',NULL,'44',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (53,'١-قال في تفسير قوله تعالى: {مَالِكِ يَوْمِ الدِّينِ} من سورة الفاتحة ما نصه: وأما تفسير الملك فيأتي الكلام عليه، وذلك أن قوله: {مَالِكِ يَوْمِ الدِّينِ} وفي القراءة الأخرى: {مَالِكِ يَوْمِ الدِّينِ} ، فمعناه عند جميع المفسرين كلهم ما فسره الله به في قوله: {وَمَا أَدْرَاكَ مَا يَوْمُ الدِّينِ ثُمَّ مَا أَدْرَاكَ مَا يَوْمُ الدِّينِ يَوْمَ لا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْئاً وَالْأَمْرُ يَوْمَئِذٍ لِلَّهِ} ١.
١- وقال عند تفسير قوله تعالى: {وَلَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ} ٢ ما نصه:.. ومعنى ذلك أن الله فطر عباده على الفطرة، وهي الإسلام، كما قال تعالى: {فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفاً فِطْرَتَ اللَّهِ الَّتِي فَطَرَ النَّاسَ عَلَيْهَا} ٣ الآية.
٢- وقال عند تفسير قوله تعالى: {أَأَرْبَابٌ مُتَفَرِّقُونَ خَيْرٌ أَمِ اللَّهُ الْوَاحِدُ الْقَهَّارُ} ٤ ما نصه: فهذه حجة عقلية شرحها في قوله تعالى: {ضَرَبَ اللَّهُ مَثَلاً رَجُلاً فِيهِ شُرَكَاءُ مُتَشَاكِسُونَ وَرَجُلاً سَلَماً لِرَجُلٍ هَلْ يَسْتَوِيَانِ مَثَلاً} ٥ الآية.
٣- وقال عند تفسير قوله تعالى: {الْحَمْدُ لِلَّهِ الَّذِي أَنْزَلَ عَلَى عَبْدِهِ الْكِتَابَ وَلَمْ يَجْعَلْ لَهُ عِوَجَا} ٦ ما نصه:
أنه أنزله معتدلا لا عوج فيه، ففيه معنى قوله: {وَلَوِ اتَّبَعَ الْحَقُّ أَهْوَاءَهُمْ لَفَسَدَتِ السَّمَاوَاتُ وَالْأَرْضُ} ٧.
٤- وقال في أواخر تفسير سورة الناس ما نصه:
وقوله: {مِنَ الْجِنَّةِ وَالنَّاسِ} يعني: أن الوسواس نوعان: إنس وجن،
_________
١ سورة الانفطار آية "١٧-١٩" وانظر مؤلفات الشيخ / القسم الرابع / التفسير ص "١٣".
٢ سورة النساء آية "١١٩".
٣ سورة الروم آية "٣٠" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٩٠".
٤ سورة يوسف آية "٣٩".
٥ سورة الزمر آية "٢٩" وانظر مؤلفات الشيخ / القسم الرابع / التفسير "١٤٦".
٦ سورة الكهف آية "١".
٧ سورة المؤمنون آية "٧١" وانظر مؤلفات الشيخ / القسم الرابع / التفسير ص "٢٤٠".',NULL,'45',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (54,'فإن الوسوسة: الإلقاء الخفي. لكن إلقاء الإنس بواسطة الأذن، والجني لا يحتاج إليها، ونظير اشتراكهما في الوسوسة، اشتراكهما في الوحي الشيطاني في قوله: {وَكَذَلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوّاً شَيَاطِينَ الْأِنْسِ وَالْجِنِّ يُوحِي بَعْضُهُمْ إِلَى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوراً وَلَوْ شَاءَ رَبُّكَ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ} ١.
ثانياً: بيان بعض المعاني المتعلقة بالآية، عن طريق ذكر الآية المقابلة لها، أو التي بينها وبينها مناسبة لإيضاح وجه العلاقة بين تلك الآيات. ويتضح ذلك بالأمثلة التالية من تفسيره:
١- قال عند تفسير قوله تعالى: {وَإِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِلنَّاسِ وَأَمْناً وَاتَّخِذُوا مِنْ مَقَامِ إِبْرَاهِيمَ مُصَلّىً وَعَهِدْنَا إِلَى إِبْرَاهِيمَ وَإِسْمَاعِيلَ أَنْ طَهِّرَا بَيْتِيَ لِلطَّائِفِينَ وَالْعَاكِفِينَ وَالرُّكَّعِ السُّجُودِ} ٢.
ما نصه: ذكره أنه عهد إلى إبراهيم وإسماعيل أن يطهراه لهذه الطائفة، ولذلك أنزل الله: {إِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هَذَا} ٣.
٢- وقال عند تفسير قوله تعالى: {وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ} ٤ الآية.
ما نصه: ونظيره: {فَإِنَّا خَلَقْنَاكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ} ٥ والله سبحانه يخاطب الموجودين، والمراد آباؤهم، كقوله: {وَإِذْ قُلْتُمْ يَا مُوسَى لَنْ نُؤْمِنَ لَكَ حَتَّى نَرَى اللَّهَ جَهْرَةً} ٦. وقد يستطرد سبحانه من الشخص إلى النوع كقوله: {ثُمَّ جَعَلْنَاهُ نُطْفَةً فِي قَرَارٍ مَكِينٍ} ٧ إلى آخره.
_________
١ سورة الأنعام آية "١١٢" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٨٩".
٢ سورة البقرة آية "١٢٥".
٣ سورة التوبة آية "٢٨" وانظر مؤلفات الشيخ/ القسم الرابع / التفسير ص "٣١".
٤ سورة الأعراف آية "١١".
٥ سورة الحج آية "٥".
٦ سورة البقرة آية "٥٥".
٧ سورة المؤمنون آية "١٣".',NULL,'46',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (55,'فالمخلوق من سلالة آدم، ومن نطفة ذريته١.
٣- وقال عند تفسير قوله تعالى إخباراً عن مقالة يوسف: {مَا كَانَ لَنَا أَنْ نُشْرِكَ بِاللَّهِ مِنْ شَيْءٍ} ٢ ما نصه:
قوله: {مِنْ شَيْءٍ} عام كل ما سوى الله، وهذه المسألة هي التي غلط فيها أذكياء العالم، وعقلاء بني آدم، كما قال تعالى: {كَبُرَ عَلَى الْمُشْرِكِينَ مَا تَدْعُوهُمْ إِلَيْهِ} ٣.
٤- وقال عند تفسير قوله تعالى: {كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لا يَشْعُرُونَ} ٤. ما نصه:
الإخبار بعذابهم من حيث لا يشعرون، بضد من يرزقه من حيث لا يحتسب٥. أ. هـ فهو يريد أن يلفت الأنظار إلى نكتة عظيمة هنا وهي أنه كما أتى المكذبين عذاب من حيث لا يشعرون لتكذيبهم، أتى المتقين رزق من حيث لا يحتسبون لتقواهم. كما قال تعالى: {وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجاً وَيَرْزُقْهُ مِنْ حَيْثُ لا يَحْتَسِبُ} ٦.
ومما يدل على تمشية مع هذا المنهج – أعني تفسير القرآن بالقرآن – ما أمكن – ما نراه من جمع للآيات التي يبين بعضها بعضا ويظهر من مجموعها بيان معنى معين في بعض أبواب كتاب التوحيد كما يأتي الإشارة إليه في موضعه إن شاء الله تعالى٧.
مما به يتبين هذا الجانب في تفسيراته واستنباطاته.
_________
١ انظر: مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٨٢، ٨٣".
٢ سورة يوسف "٣٨".
٣ سورة الشورى آية "١٣" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٤٥" وانظر قسم التحقيق ص "٣٢٩".
٤ سورة الزمر آية "٢٥".
٥ انظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٢٧".
٦ سورة الطلاق آية "٢، ٣".
٧ انظر فيما يأتي ص "١٢٨".',NULL,'47',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (56,'<span data-type="title" id=toc-5>الفصل الثاني: تفسيره القرآن بالسنة</span>
يذهب الشيخ محمد رحمه الله إلى أن النبي "صلى الله عليه وسلم" فسر للصحابة رضوان الله عليهم ألفاظ القرآن كما بلغها. وقد مال إلى هذا القول من قبل الإمام ابن جرير الطبري١، وقرره شيخ الإسلام ابن تيمية رحمهم الله٢.
والذي يظهر أن مرادهم بذلك أن النبي صلى الله عليه وسلم بين ما يحتاج إلى بيان وإيضاح، كما تدل عليه عباراتهم، واستدلالاتهم، كما سيأتي، وأما ما سوى ذلك فمجرد إبلاغه بيان له، على ما سيأتي توضيحه.
فإن قول الشيخ محمد: "فسر" مشعر بذلك، حيث أن التفسير في اللغة: هو الكشف والبيان٣. والذي يحتاج إلى كشف وبيان هو ما كان مستوراً غامضاً، وهو ما أمر النبي صلى الله عليه وسلم ببيانه في قول الله تعالى له: {وَأَنْزَلْنَا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ} ٤ فتولى النبي صلى الله عليه وسلم ذلك بالبيان على أوجه كثيرة: كبيان المجمل، وتوضيح المشكل، وتخصيص العام، وتقييد المطلق، وغير ذلك، والأدلة على بيانه صلى الله عليه وسلم كثيرة منها:
١- قوله تعالى: {وَأَنْزَلْنَا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ} فالبيان في الآية يتناول بيان معاني القرآن، كما يتناول بيان ألفاظه بتبليغها.
٢- قوله تعالى: {كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ مُبَارَكٌ لِيَدَّبَّرُوا آيَاتِهِ} ٥ وقوله: {أَفَلا يَتَدَبَّرُونَ الْقُرْآنَ} ٦ وقوله: {أَفَلَمْ يَدَّبَّرُوا الْقَوْلَ} ٧، وتدبر الكلام بدون فهم معانيه لا يمكن.
_________
١ انظر ترجمته فيما يأتي ص "٢٨٦" من قسم التحقيق.
٢ انظر مؤلفات الشيخ/ التفسير ص"٢٥٤"، وتفسير الطبري "١: ٧٣ وما بعد ها" ومقدمة في أصول التفسير لشيخ الإسلام ابن تيمية ص "٣٥".
٣ انظر لسان العرب "٥: ٥٥". والمصباح المنير "٤٧٢" مادة: فسر.
٤ سورة النحل: آية "٤٤".
٥ سورة "ص": آية "٢٩".
٦ سورة النساء: آية "٨٢".
٧ سورة المؤمنون: آية "٦٨".',NULL,'48',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (57,'وقوله: {إِنَّا أَنْزَلْنَاهُ قُرْآناً عَرَبِيّاً لَعَلَّكُمْ تَعْقِلُونَ} ١. وعقل الكلام متضمن لفهمه. ومن المعلوم أن كل كلام فالمقصود منه فهم معانيه لا مجرد ألفاظه. فالقرآن أولى بذلك.
ولهذا كان الصحابة رضوان الله عليهم يبقون مدة في حفظ السورة.
قال أنس كان الرجل إذا قرأ البقرة وآل عمران جد فينا٢.
وأقام ابن عمر على حفظ البقرة عدة سنين. قيل: ثمان سنين. كما ذكره مالك "رحمه الله" ٣، وقال أبو عبد الرحمن السلمي: حدثنا الذين كانوا يقرئوننا القرآن كعثمان بن عفان وعبد الله بن مسعود وغيرهما أنهم كانوا إذا تعلموا من "النبي صلى الله عليه وسلم" عشر آيات لم يجاوزوها حتى يتعلموا ما فيها من العلم والعمل، قالوا: فتعلمنا القرآن، والعلم والعمل جميعا٤.
٣- ما أخرجه أحمد وابن ماجة عن عمر أنه قال: من آخر ما نزل آية الربا وأن الرسول "صلى الله عليه وسلم" قبض قبل أن يفسرها٥.
فقد دل فحوى الكلام أنه كان يفسر لهم كل ما نزل، وأنه إنما لم يفسر هذه الآية لسرعة موته بعد نزولها، وإلا لم يكن للتخصيص بها وجه٦.
٤- أن العادة تمنع أن يقرأ قوم كتاباً في فن من العلم كالطب والحساب ولا يستشرحوه، فكيف بكلام الله تعالى الذي هو عصمتهم وبه نجاتهم وسعادتهم وقيام دينهم ودنياهم٧؟!
_________
١ سورة يوسف: آية "٣"
٢ رواه الإمام أحمد في مسنده "٣: ١٢٠".
ومعنى قوله: جد فينا أي صار ذا حظوة وقدر عظيم بيننا.
٣ أخرجه مالك في الموطأ/ كتاب القرآن/ باب ما جاء في القرآن "١: ٢٠٥" اثر "١١".
٤ أخرجه الإمام أحمد في مسنده "٥: ٤١٠" والطبري في تفسيره "١: ٨٠" تحقيق: أحمد شاكر، وابن أبي شيبة في المصنف/ كتاب فضائل القرآن/ في تعليم القرآن كم آية "٦: ١١٧" أثر "٢٩٩٢٩".
٥ أحمد في مسنده "٣٦:١، ٥٠" وابن ماجة/ كتاب التجارات/ باب التغليظ في الربا "٢/٧٦٤" حديث "٢٢٧٦" قال الساعاتي في الفتح الرباني "١٨: ٥٤" وسنده ضعيف لانقطاعه لأن سعيد بن المسيب لم يدرك عمر لكن يعضده ما رواه البخاري عن ابن عباس قال: آخر ما نزل على رسول الله صلى الله عليه وسلم آية الربا.
٦ الإتقان "٢٠٥:٢".
٧ انظر مقدمة في أصول التفسير لابن تيمية "٣٧".',NULL,'49',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (58,'بيد أن بعض العلماء: "ومنهم الخويي١" و "السيوطي٢" قد ذهبوا إلى أن النبي "صلى الله عليه وسلم" لم يفسر من القرآن إلا آيات قلائل.
فيقول الخويي:- وأما القرآن فتفسيره على وجه القطع لا يعلم إلا بأن يسمع من الرسول "صلى الله عليه وسلم" وذلك متعذر إلا في آيات قلائل٣.
_________
١ هو شمس الدين أحمد بن الخليل بن سعادة المهلبي الخويي- نسبة إلى خوي مدينة بأذربيجان- الشافعي أبو العباس ولد سنة "ثلاث وثمانين وخمسمائة" دخل خراسان وقرأ بها الكلام والأصول على الفخرالرازي "سمع من ابن الصلاح وقرأ الفقه على الرافعي.
قال الذهبي:- كان فقيهاً، إماماً، مناظراً، خبيراً بعلم الكلام، أستاذاً في الطب والحكمة ديناً كثير الصلاة والعبادة.
وقال التاج السبكي: حفظ القرآن على كبره. وله مصنفات كثيرة ونظم كثير.
توفي في شعبان سنة "سبع وثلاثين وستمائة".
انظر: سير أعلام النبلاء "٣٣: ٦٤، ٦٥" وطبقات الشافعية للسبكي "٨: ١٦، ١٧"" وشذرات الذهب "١٨٣:٥".
٢ هو الإمام الحافظ جلال الدين عبد الرحمن بن أبي بكر بن محمد الخضيري السيوطي.
ولد سنة "تسع وأربعين وثمانمائة" وحفظ القرآن وهو دون الثامنة، كما حفظ كثيراً من المتون.
وقال عن نفسه إنه رزق التبحر في سبعة علوم وهي: التفسير، والحديث والفقه، والنحو، والمعاني والبيان، والبديع.
وله مؤلفات جمه، منتشرة في الآفاق، وكثير منها حسن مفيد.
توفي سنة إحدى عشرة وتسعمائة.
انظر: الضوء اللامع "٤: ٦٥" وحسن المحاضرة "١٨٨:١" والكواكب السائرة "١: ٢٢٦" وشذارت الذهب "٨: ٥١".
٣ نقله عنه السيوطي في الإتقان "١٧٤:٢".',NULL,'50',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (59,'ويقول السيوطي: الذي صح من ذلك- أي من النقل عن "النبي صلى الله عليه وسلم" في التفسير- قليل جداً بل أصل المرفوع منه في غاية القلة١.
وقد استدلوا على ما ذهبوا إليه بأدلة منها:
١- ما أخرجه البزار والطبري عن عائشة "رضي الله عنها" قالت:- ما كان رسول الله "صلى الله عليه وسلم" يفسر من كتاب الله إلا أيا بعدد علمه إياهن جبريل٢.
٢- ما أخرجه أحمد وابن حبان والحاكم وصححه، وغيرهم أن النبي "صلى الله عليه وسلم" دعا لابن عباس بقوله: "اللهم فقهه في الدين وعلمه التأويل" ٣.
فلو كان التأويل مسموعاً كالتنزيل لما كان لتخصيص ابن عباس بهذا الدعاء فائدة٤.
٣- إنه يتعذر أن يفسر النبي "صلى الله عليه وسلم" القرآن كله، ولا يمكن ذلك إلا في آيات قلائل، والعلم بالمراد من الآيات يستنبط بأمارات ودلائل، والحكمة فيه أن الله تعالى- أراد أن يتفكر عباده في كتابه، فلم يأمر نبيه بالتنصيص على المراد في جميع آياته٥.
والرأي الذي ارتضيه بعد هذا هو ما ذهب إليه الشيخ محمد رحمه الله ومن سبقه إليه، وهو أن النبي "صلى الله عليه وسلم" قد امتثل أمر ربه وبين كل ما يحتاج إلى بيان من معاني القرآن الكريم ويظهر الحق جلياً إن شاء الله تعالى إذا علمت الأمور التالية:
١- أن بيان السنة للكتاب ليس مقتصراً على تفسير كلمة قرآنية بكلمة نبوية، بل هو أعم من ذلك، فيشمل ما كان بياناً بالقول، أو الفعل، أو التقرير، أو الوصف، وكل ذلك سنة، وبها يكون البيان.
_________
١ المرجع السابق "٢: ١٧٩".
٢ تفسير الطبري "١: ٨٤" بتحقيق: أحمد شاكر.
٣ انظر مسند الإمام أحمد في مواضع منها "٢٢٦:١" والإحسان بترتيب صحيح ابن حبان "٩/٩٨" ح "٧٠١٥". والمستدرك "٣: ٥٣٤".
٤ الجامع لأحكام القرآن للقرطبي "٣٣:١".
٥ الإتقان "٣: ١٧٤".',NULL,'51',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (60,'ولهذا قالت عائشة رضي الله عنها عندما سئلت عن خلق رسول الله "صلى الله عليه وسلم" "كان خلقه القرآن"١ فما تحلى به "صلى الله عليه وسلم" من كمال العبودية لله تعالى، وكريم الشمائل هو من قبيل تفسير القرآن بالسنة، فتحليله لما أحل القرآن، وتحريمه لما حرم، والتزامه بشرائعه وآدابه، في جميع شؤونه، كل ذلك هو ما قصدته عائشة "رضي الله عنها". فهو داخل ضمن التفسير.
٢- أن تفسير القرآن الكريم على أربعة أوجه، كما ذكر ذلك ابن عباس بقوله "التفسير على أربعة أوجه: وجه تعرفه العرب من كلاهما، وتفسير لا يعذر أحد بجهالته، وتفسير تعرفه العلماء، وتفسير لا يعلمه إلا الله تعالى٢.
فمعلوم أن القسم الأول مجرد تبليغ لفظه كاف عن تفسيره، وذلك لظهور معناه، وبخاصة لدى الصدر الأول، أهل العربية والفصاحة، فإن تفسير الواضح قد يعميه، فمثل هذا لا يحتاج إلى أن يبين الرسول صلى الله عليه وسلم معناه.
وكذا ما لا يعذر أحد بجهالته، لظهور معناه أيضاً.
ولهذا كان الشيخ الإمام رحمه الله يشنع على الذين يقولون بأن القرآن لا يفهم معناه، وأنهم لا يستطيعون أن يستفيدوا منه مباشرة بل يأخذون من أقوال متبوعيهم دون النظر إلى نصوص الكتاب٣.
وأما ما استأثر الله بعلمه، كوقت قيام الساعة، وحقيقة الروح، ونحو ذلك، فهذا لم يطلع الله عليه نبيه "صلى الله عليه وسلم"، فمعلوم أنه غير مراد بالبيان، ولكن الغرض من ذكر ذلك في القرآن قد حصل وهو الابتلاء والامتحان،
_________
١ أخرجه أحمد في مسنده في مواضع منها "٦/ ٩١" ومسلم في صحيحه في حديث طويل / كتاب صلاة المسافرين وقصرها/ باب جامع صلاة الليل "١/ ٥١٢" ح "١٢٩" وغيرهما.
٢ أخرجه الطبري في تفسيره "١: ٧٥" بتحقيق: أحمد شاكر.
٣ انظر ما يأتي ص "٩٣-٩٥".',NULL,'52',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (61,'ليتميز من يؤمن ويسلم ويعمل، ممن يتبع المتشابه ويجادل.
فأما ما يحتاج إلى بيان وإيضاح من النبي "صلى الله عليه وسلم"، ولا يدرك علمه إلا ببيانه، من تفصيل المجمل، وتوضيح المشكل، وتخصيص العام، وتقييد المطلق، ونحو ذلك فقد بينه النبي صلى الله عليه وسلم أتم بيان.
وأما ما زاد بعد ذلك من تفاسير المفسرين فالأمرين:
الأمر الأول: لبعد الناس شيئاً فشيئاً عن اللغة العربية الصحيحة، والذوق السليم، والفهم الصافي، فاحتاجوا بقدر بعدهم إلى إيضاح ما كان واضحاً لمن قبلهم.
الأمر الثاني: ولعظمة كتاب الله تعالى وإعجازه في كل زمان ومكان، فيجد فيه كل ناظر حصيف حاجته وكفايته، فتستنبط المعاني، الكثيرة من الآية الواحدة في كافة العلوم من عقيدة، وفقه، ودعوة، ونحو ذلك.
وأما ما استدل به أصحاب القول الثاني فالذي يظهر لي أنه لا تنهض به حجة على المدعي. وتوضيح ذلك:
١- أن حديث عائشة متكلم فيه حيث روي من طريق جعفر بن محمد الزبيري. وقد قال فيه البخاري: لا يتابع في حديثه. وقال الأزدي: منكر الحديث١.
وعلى فرض صحته فهو محمول على بيان بعض مغيبات القرآن، وتفسير مجمله، ونحو ذلك مما لا سبيل إلى معرفة معناه إلا بتوقيف من الله تعالى، وذلك في أي ذوات عدد بلا شك٢.
وقد أجاب الإمام ابن جرير الطبري على الاستدلال بهذا الحديث بكلام حسن يحسن إثباته حيث قال:
ولو كان تأويل الخبر عن رسول الله صلى الله عليه وسلم – أنه لا يفسر من القرآن شيئاً إلا آيا بعدد٣ – وهو ما يسبق إليه أوهام أهل الغباء، من أنه لم
_________
١ انظر ميزان الاعتدال "١: ٤١٦" ولسان الميزان "٢: ١٢٤".
٢ انظر تفسير الطبري "١: ٨٧" بتحقيق: أحمد شاكر.
٣ سبق تخريجه ص "٥١".',NULL,'53',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (62,'يكن يفسر من القرآن إلا القليل من آية، واليسير من حروفه كان إنما أنزل إليه "صلى الله عليه وسلم" الذكر ليترك للناس بيان ما أنزل إليهم، لا ليبين لهم ما أنزل إليهم.
وفي أمر الله جل ثناؤه نبيه "صلى الله عليه وسلم" ببلاغ ما أنزل إليه، وإعلامه إياه أنه إنما نزل إليه ما أنزل ليبين للناس ما نزل إليهم، وقيام الحجة على أن النبي صلى الله عليه وسلم قد بلغ وأدى ما أمره الله ببلاغه وأدائه على ما أمر به، وصحة الخبر عن عبد الله بن مسعود بقيله: كان الرجل منا إذا تعلم عشر آيات لم يجاوزهن حتى يعلم معانيهن والعمل بهن١، ما ينبي عن جهل من ظن أو توهم أن معنى الخبر الذي ذكرنا عن عائشة عن رسول الله "صلى الله عليه وسلم": أنه لم يكن يفسر من القرآن شيئاً إلا آيا بعدد، هو أنه لم يكن يبين لأمته من تأويله إلا اليسير القليل منه.
هذا مع ما في الخبر الذي روي عن عائشة من العلة التي في إسناده، التي لا يجوز معها الاحتجاج به لأحد ممن علم صحيح سند الآثار وفاسدها في الدين، لأن راويه ممن لا يعرف في أهل الآثار وهو: جعفر بن محمد الزبيري٢. أ. هـ.
قلت: وقد تقدم ذكر بعض كلام العلماء في جعفر هذا٣.
٢- وأما دعاؤه "صلى الله عليه وسلم" لابن عباس فلا يلزم منه عدم بيانه فمعاني القرآن واسعة، وأغراضه كثيرة.
وأيضاً فقد دعا له بالفقه في الدين، ولا يلزم منه عدم بيان "صلى الله عليه وسلم" الأحكام الشرعية، كما أن غير ابن عباس قد شاركه في الفقه والتأويل، كعلي وابن مسعود وغيرهما فلا يدل على تخصيصه بالفقه والتأويل.
٣- وأما كون بيان النبي "صلى الله عليه وسلم" لكل معاني القرآن متعذر فلا وجه له. ولكن لعل أصحاب القول الثاني قد قصروا التفسير على معنى ضيق وهو بيان
_________
١ أخرجه الطبري في تفسيره "١: ٨٠" بتحقيق أحمد شاكر وتقدم نحوه ص "٤٩".
٢ انظر تفسير الطبري "١: ٣٩".
٣ انظر ما سبق ص "٥٣".',NULL,'54',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (63,'كلمة قرآنية بكلمة نبوية لفظاً فحسب والله أعلم.
فإذا ثبت ما تقدم من بيان النبي "صلى الله عليه وسلم" لأصحابه مع قوله: "ألا إني أوتيت الكتاب ومثله معه" ١ وقوله تعالى: {وَمَا يَنْطِقُ عَنِ الْهَوَى إِنْ هُوَ إِلَّا وَحْيٌ يُوحَى} ٢ علمت أهمية تفسير القرآن بالسنة.
فيجب على من أراد فهم كتاب الله عز وجل فهماً سليماً، وتفسيره تفسيراً صحيحاً، ألا يهمل هذا الجانب بل يهتدي بهداه، ويستنير بضياه.
وذلك هو ما سار عليه الشيخ من واقع سلفيته، فتجد في تفسيره ملامح كثيرة لهذا المسلك القويم، يتجلى فيها ما أسلفته من نظره شمولية لتفسير السنة للقرآن الكريم، سواء أكان تفسير كلمة بأخرى، أو ما هو أوسع من ذلك. وهذا هو الأكثر في تفسيره. ومن خلال استقرائي لمنهج الشيخ في هذا النوع من التفسير أتضح لي أن له طريقتين في تفسير القرآن بالسنة والاستفادة منها في بيان المعنى:-
الطريقة الأولى: إيراد نص الحديث أو بعضه مميزاً إياه عن أسلوبه بما يعرف به أنه حديث كقوله: قال النبي صلى الله عليه وسلم: كذا أو نحو ذلك ومثل هذا كثير ومنه:
- قوله عند قول الله تعالى في شأن إبليس لعنه الله: {..وَقَالَ لَأَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيباً مَفْرُوضاً وَلَأُضِلَّنَّهُمْ وَلَأُمَنِّيَنَّهُمْ وَلَآمُرَنَّهُمْ فَلَيُبَتِّكُنَّ آذَانَ الْأَنْعَامِ وَلَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ ... } الآية٣ ما نصه:
.... ومعنى ذلك أن الله فطر عباده على الفطرة وهي الإسلام كما قال تعالى: {فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفاً فِطْرَتَ اللَّهِ الَّتِي فَطَرَ النَّاسَ عَلَيْهَا} الآية٤.
_________
١ جزء من حديث رواه أحمد في مسنده "٤: ١٣١" وأبو داود في سننه/ كتاب السنة/ باب في لزوم السنة "٤: ٢٠٠" ح "٤٦٠٤".
٢ سورة النجم: الآيتان "٣، ٤".
٣ سورة النساء: آية "١١٨، ١١٩".
٤ سورة الروم: آية "٣٠".',NULL,'55',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (64,'وفي الصحيح "ما من مولود إلا يولد على الفطرة فأبواه يهودانه" الحديث١ فجمع "صلى الله عليه وسلم" بين الأمرين: تغيير الفطرة بالتهويد وغيره، وتغيير الخلقة بالجدع، وهما اللذان أخبر إبليس أنه لا بد أن يغيرهما٢. أ. هـ.
ويعني الشيخ بتغيير الخلقة بالجدع ما ورد في بعض ألفاظ الحديث في آخره ".... كما تنتجون البهيمة، هل تجدون فيها من جدعاء حتى تكونوا أنتم تجدعونها" ٣.
- وقوله عند قول الله تعالى: {إِذْ قَالَ يُوسُفُ لَأَبِيهِ يَا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَباً وَالشَّمْسَ وَالْقَمَرَ رَأَيْتُهُمْ لِي سَاجِدِينَ قَالَ يَا بُنَيَّ لا تَقْصُصْ رُؤْياكَ عَلَى إِخْوَتِكَ فَيَكِيدُوا لَكَ كَيْداً إِنَّ الشَّيْطَانَ لِلْإِنْسَانِ عَدُوٌّ مُبِينٌ} ٤ ما نصه:
"...... ولما كان تعبيرها- أي الرؤيا- خضوعهم له، خشي إن حدثهم أن يحسدوه فيبغون له الغوائل. وثبت أن رسول الله صلى الله عليه وسلم أمر من رأى ما يحب أن يحدث به، ولا يحدث إلا من يحب، وإذا رأى ما يكره فليتحول إلى جنبه الآخر ويتفل عن يساره ثلاثاً ويتعوذ بالله من شرها فإنها لا تضره٥؟ " أ. هـ.
فهذا من بيان التأكيد كما يأتي إيضاحه في الفصل التالي، إذ أن شرع من قبلنا شرع لنا ما لم يرد في شرعنا ما يخالفه.
الطريقة الثانية: تضمين تفسيره معاني واردة في بعض الأحاديث، ولا يصرح بنص الحديث بل يمتزج أسلوبه مع نص الحديث أو يرويه بالمعنى
_________
١ رواه البخاري في صحيحه في مواضع منها/ كتاب الجنائز/ باب إذا أسلم الصبي فمات هل يصلى عليه/ انظر الفتح "٣: ٢٦٠" ح "١٣٥٨، ١٣٥٩" ومسلم في صحيحه/ كتاب القدر/ باب معنى: "كل مولود يولد على الفطرة" "٢٠٤٧:٤- ٢٠٤٩" ح "٢٦٥٨".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص٩٠.
٣ رواه البخاري في صحيحه/ كتاب القدر/ باب: الله أعلم بما كانوا عاملين.
انظر الفتح "٥٠٢:١١" ح "٦٥٩٩" ومسلم في الموضع السابق في هامش رقم "١".
٤ سورة يوسف: آية "٤، ٥".
٥ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٢٩" وانظر تخريج هذه الأحاديث في موضعها من التحقيق ص "٢٩٣".',NULL,'56',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (65,'وذلك لإيضاح بعض المعاني أو تقرير بعض المسائل.
ومن أمثلة ذلك:
- قوله ضمن المسائل المستنبطة من قوله تعالى: {وَاصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ وَلا تَعْدُ عَيْنَاكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَيَاةِ الدُّنْيَا وَلا تُطِعْ مَنْ أَغْفَلْنَا قَلْبَهُ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوَاهُ وَكَانَ أَمْرُهُ فُرُطاً} ١ حيث يقول- أن صلاة البر دين بالإخلاص توصل إلى المراتب العالية٢.
فهو بهذا قد ضمن تفسيره معنى الحديث الشريف "من صلى البردين دخل الجنة" ٣، واستفاد منه في تأييد معان مستنبطة من هذه الآية بدون ذكره نص الحديث ومن هذه المعاني المرادة هنا والمستفادة من إيراد هذا الحديث:
أ- شمول الدعاء للصلاة أيضاً، وذلك أن الصلاة في اللغة: هي الدعاء٤.
ب- تفسير الغداة والعشي بأنهما وقت البردين أي أول النهار وآخره وهما الفجر والعصر٥.
جـ- فضل المحافظة على صلاة البردين.
- قوله ضمن المسائل المستنبطة من قوله تعالى: {فَلْيَدْعُ نَادِيَهُ سَنَدْعُ الزَّبَانِيَةَ} ٦ ما نصه:
أنه لو دعا ناديه، أودنا من النبي "صلى الله عليه وسلم" لعوجل، ولكن دفع عنه ذلك لكونه ترك ما في نفسه٧.
_________
١ سورة الكهف: آية "٢٨".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٢٤٩".
٣ رواه البخاري في صحيحه/ كتاب مواقيت الصلاة/ باب فضل صلاة الفجر انظر الفتح "٦٣:٢" ح "٥٧٤". ومسلم في صحيحه/ كتاب المساجد/ باب فضل صلاتي الصبح والعصر والمحافظة عليهما "١: ٤٤٠" ح "٦٣٥".
٤ انظر المصباح المنير "٣٤٦" مادة: صلى.
٥ انظر فتح الباري "٢: ٦٤" ومؤلفات الشيخ/ القسم الرابع/ التفسير ص "٥٧".
٦ سورة العلق: آية "١٧، ١٨".
٧ مؤلفات الشيخ/ التفسير ص ٣٧٣.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (66,'فقد ضمن الشيخ تفسيره هذا معنى مستفاداً من قوله "صلى الله عليه وسلم" في أبي جهل: "لو دنا منى لاختطفته الملائكة عضوا عضوا" ١.
فلم يورد نص الحديث بل اكتفى بالاستفادة من معناه، وإن كان في كلامه ما يشعر بأن هذا المعنى مبني على حديث، إذ أن هذا الاستنباط الذي ذكره لا مجال للعقل فيه، بل لا بد فيه من نص من الشرع.
- قوله فمن المسائل المستنبطة من قوله تعالى في سورة "المسد": {مَا أَغْنَى عَنْهُ مَالُهُ وَمَا كَسَبَ} ٢ ما نصه:- إن الولد من الكسب، ففيه دليل على أن أطيب ما أكلتم من كسبكم، وأن أولادكم من كسبكم٣.
فقد ضمن استنباطه هذا الحديث الشريف وهو قوله "صلى الله عليه وسلم" "إن أطيب ما أكلتم من كسبكم، وإن أولادكم من كسبكم" ٤.
والغرض هنا هو التمثيل والاستدلال لبيان المنهج لا استقصاء جميع ما ورد.
وحيث أن سنن رسول الله "صلى الله عليه وسلم" مع كتاب الله وجهان٥، فقد استفاد الشيخ من كلا الوجهين، وأوردهما في تفسيراته وهذان الوجهان هما:
الوجه الأول: نص من السنة موافق لنص الآية. فيعتبر نص السنة في هذه الحال من قبيل بيان التأكيد ومن أمثلة ذلك من تفسير الشيخ ما يلي:-
_________
١ أخرجه البخاري في صحيحه/ كتاب التفسير/ باب {كَلَّا لَئِنْ لَمْ يَنْتَهِ لَنَسْفَعاً بِالنَّاصِيَةِ} انظر الفتح "٨/ ٥٩٥" ح "٤٩٥٨". ومسلم في صحيحه كتاب صفات المنافقين وأحكامهم/ باب قوله: {كَلَّا إِنَّ الْأِنْسَانَ لَيَطْغَى أَنْ رَآهُ اسْتَغْنَى} "٥/٢١٥٤" ح "٢٧٩٧".
٢ سورة المسد: آية "٢".
٣ مؤلفات الشيخ/ التفسير ص "٣٨٢".
٤ أخرجه الإمام أحمد في مسنده في مواضع منها "٢: ١٧٩" وابن ماجة في سننه/ كتاب التجارات/ باب ما للرجل من مال ولده "٢: ٧٦٨" ح "٢٢٩٠" والترمذي في جامعه "كتاب الأحكام" باب ما جاء أن الوالد يأخذ من مال ولده "٣: ٦٣٠" ح "١٣٥٨".
٥ انظر الرسالة للشافعي ص "٩١".',NULL,'58',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (67,'١- قوله فهن المسائل المستنبطة من قوله تعالى: {كَلَّا لا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ} ١ ما نصه٢:- الأمر بالاقتراب. ففيه معنى "أقرب ما يكون العبد من ربه وهو ساجد" ٣
فيلاحظ في هذا الحديث أنه لم يزد على ما في الآية شيئاً بل ولم يوضح مبهماً فيها وإنما أكد معنى ظاهراً في الآية ظهوره في الحديث.
٢- قوله ضمن المسائل المستنبطة من قوله تعالى: {فَلَمَّا رَجَعُوا إِلَى أَبِيهِمْ قَالُوا يَا أَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَأَرْسِلْ مَعَنَا أَخَانَا نَكْتَلْ وَإِنَّا لَهُ لَحَافِظُونَ قَالَ هَلْ آمَنُكُمْ عَلَيْهِ إِلَّا كَمَا أَمِنْتُكُمْ عَلَى أَخِيهِ مِنْ قَبْلُ فَاللَّهُ خَيْرٌ حَافِظاً وَهُوَ أَرْحَمُ الرَّاحِمِينَ} ٤.
حيث يقول٥: جوابه عليه السلام لهم فيدل على قوله: "لا يلدغ المؤمن من جحر مرتين" ٦.
فالذي يظهر لي أن هذا أيضاً من قبيل بيان التأكيد وذلك أن يعقوب عليه السلام أراد من قوله هذا أنه لا يلدغ المؤمن من جحر مرتين، وهذا ما أكده حديث نبينا "صلى الله عليه وسلم".
الوجه الثاني:- نص من السنة مفسر أو مبين لمعاني مفردات أو معان متعلقة بالآية فمن أمثله ذلك:
١- ما أورده الشيخ رحمه الله في تفسيره لسورة الفاتحة حيث يقول٧: ومن أحسن ما يفتح لك الباب في فهم الفاتحة حديث أبي هريرة الذي
_________
١ سورة العلق: آية "١٩".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير "٣٧٣".
٣ رواه مسلم في صحيحه/ كتاب الصلاة/ باب ما يقال في الركوع والسجود "١: ٣٥٠" خ "٤٨٢".
٤ سورة يوسف: آية "٦٣، ٦٤".
٥ مؤلفات الشيخ/ القسم الرابع/ التفسير "١٦٠".
٦ رواه الشيخان ويأتي تخريجه في موضعه من التحقيق ص "٣٦١".
٧ مؤلفات الشيخ/ القسم الرابع/ التفسير "٨".',NULL,'59',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (68,'في صحيح مسلم قال: سمعت رسول الله "صلى الله عليه وسلم" يقول: "يقول الله تعالى: قسمت الصلاة بيني وبين عبدي نصفين ولعبدي ما سأل. فإذا قال العبد: {الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ} قال الله: حمدني عبدي. فإذا قال: {الرَّحْمَنِ الرَّحِيمِ} قال الله: أثنى علي عبدي، فإذا قال: {مَالِكِ يَوْمِ الدِّينِ} قال الله: مجدني عبدي. فإذا قال: {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} قال الله: هذا بيني وبين عبدي ولعبدي ما سأل. فإذا قال: {اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلا الضَّالِّينَ} قال الله: هذا لعبدي ولعبدي ما سأل" ١.
فبين في هذا الحديث القدسي معاني بعض المفردات، والجمل، وبعض متعلقاتها.
٢- قوله عند تفسير قوله تعالى: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيَامَةِ وَالسَّمَاوَاتُ مَطْوِيَّاتٌ بِيَمِينِهِ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ} ٢.
ما نصه٣: ... في الصحيح: أن رسول الله صلى الله عليه وسلم قرأها على المنبر وقال: "إن الله يقبض يوم القيامة الأرضين وتكون السموات بيمينه" ثم ذكر تمجيد الرب تبارك وتعالى نفسه، وأنه يقول: "أنا الجبار، أنا المتكبر، أنا الملك، أنا العزيز، أنا الكريم"، قال ابن عمر: فرجف برسول الله "صلى الله عليه وسلم" حتى قلنا ليخرن به٤.
فهناك نوع مناسبة وبيان بين التسبيح في آخر الآية، والإخبار عن الله أنه يمجد نفسه ويقول: "أنا الجبار، أنا المتكبر.. " الحديث.
والله أعلم.
_________
١ أخرجه مسلم في صحيحه/ كتاب الصلاة/ باب وجوب قراءة الفاتحة في كل ركعة "١: ٣٦٩" ح "٣٩٥".
٢ سورة الزمر آية "٦٧".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير "٣٤٥".
٤ رواه من حديث "ابن عمر" أحصد في مسنده "٧٣:٣" ومسلم في صحيحه/ كتاب صفة القيامة والجنة والنار. "٤: ٢١٤٧" ح "٢٧٨٨" واللفظ لأحمد.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (69,'ومما ورد من السنة في بيان هذه الآية ما أورده الشيخ في كتاب التوحيد "باب {مَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ} " الآية١ من الأحاديث المفسرة لمعناها حيث قال بعد سياق هذه الآية التي ترجم بها: عن ابن مسعود رضي الله عنه قال: جاء حبر من الأحبار إلى رسول الله صلى الله عليه وسلم فقال: يا محمد إنا نجد أن الله يجعل السماوات على أصبع، والأرضين على أصبع، والشجر على أصبع، والماء على أصبع، والثرى على أصبع وسائر الخلق على أصبع فيقول: أنا الملك, فضحك النبي صلى الله عليه وسلم حتى بدت نواجذه، تصديقاً لقول الحبر.
ثم قرأ: {وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة} .
وفي رواية لمسلم: "والجبال والشجر على أصبع، ثم يهزهن، فيقول: أنا الملك أنا الله".
وفي رواية للبخاري "يجعل السموات على أصبع، والماء والثرى على أصبع، وسائر الخلق على أصبع" أخرجاه٢.
ولمسلم عن ابن عمر مرفوعاً "يطوي الله السموات يوم القيامة ثم يأخذهن بيده اليمنى، ثم يقول: أنا الملك، أين الجبارون؟ أين المتكبرون؟ ثم يطوي الأرضين السبع، ثم يأخذهن بشماله، ثم يقول أنا الملك، أين الجبارون؟ أين المتكبرون؟ " ٣.
ففي هذه الأحاديث زيادة تفسير وبيان لمعنى الآية.
٣- ما أورده في كتاب التوحيد/ باب من أطاع العلماء والأمراء في تحريم ما أحل الله أو تحديد ما حرم الله فقد اتخذهم أرباباً من دون الله٤، من حديث عدي بن
_________
١ مؤلفات الشيخ/ القسم الأول/العقيدة/ كتاب التوحيد باب "٦٦" ص "١٤٨، ١٤٩".
٢ رواه البخاري في صحيحه في مواضع منها/ كتاب التفسير/ باب: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ} انظر الفتح "٨: ٤١٢، ٤١٣"" ح "٤٨١١" ومسلم في صحيحه/ كتاب صفة القيامة والجنة والنار "٤: ٢١٤٧، ٢١٤٨" ح "٢٧٨٦".
٣ رواه مسلم في صحيحه/ كتاب صفة القيامة والجنة والنار "٤: ٢١٤٨" ح "٢٧٨٨".
٤ مؤلفات الشيخ/ القسم الأول/ العقيدة كتاب التوحيد/ باب "٣٧" ص "١٠٢".',NULL,'61',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (70,'حاتم أنه سمع النبي صلى الله عليه وسلم يقرأ هذه الآية: {اتَّخَذُوا أَحْبَارَهُمْ وَرُهْبَانَهُمْ أَرْبَاباً مِنْ دُونِ اللَّهِ وَالْمَسِيحَ ابْنَ مَرْيَمَ وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا إِلَهاً وَاحِداً لا إِلَهَ إِلَّا هُوَ سُبْحَانَهُ عَمَّا يُشْرِكُونَ} ١ فقلت له: إنا لسنا نعبدهم. قال: "أليس يحرمون ما أحل الله، فتحرمونه، ويحلون ما حرم الله فتحلونه"، فقلت: بلى. قال: "فتلك عبادتهم" رواه أحمد والترمذي وحسنه٢.
فقول النبي صلى الله عليه وسلم لعدي هنا بيان وإيضاح لمعنى العبادة المذكورة في الآية.
٤- وقوله ضمن الفوائد المستنبطة من قصة آدم وإبليس٣: ومنها أن في القصة معنى قوله "صلى الله عليه وسلم": "كل مولود يولد على الفطرة، فأبواه يهودانه، أو ينصرانه، أو يمجسانه" ٤ إلى آخره.
ومن ذلك قوله حكاية عن إبليس: {وَلَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ} ٥. فإنهم ذكروا في معناه: أي آمرهم بتغيير خلق الله، وهي فطرته التي فطر عباده عليها، وهي الإسلام لله وحده لا شريك له٦.
فان قوله صلى الله عليه وسلم "فأبواه يهودانه.." الحديث هو كالتفسير للآية المذكورة.
وللشيخ "رحمه الله" غرض ثالث في إيراد نصوص السنة، وهو الاستشهاد بها على معان مستنبطة من الآيات فمن أمثلة ذلك:
_________
١ سورة التوبة: آية "٣١".
٢ رواه الترمذي في جامعه/ كتاب تفسير القرآن/ باب ومن سورة التوبة "٢٧٨:٥" ح "٣٠٩٥" والطبري في تفسيره "١٠: ١١٤" وغيرهما.
٣ مؤلفات الشيخ، القسم الرابع، التفسير "٩٦".
٤ تقدم تخريجه ص "٥٦".
٥ سورة النساء: آية "١١٩".
٦ مؤلفات الشيخ، القسم الرابع، التفسير "٩٦".',NULL,'62',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (71,'١- قوله ضمن المسائل المستنبطة من قوله تعالى: {وَقَالَ لِلَّذِي ظَنَّ أَنَّهُ نَاجٍ مِنْهُمَا اذْكُرْنِي عِنْدَ رَبِّكَ فَأَنْسَاهُ الشَّيْطَانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ} ١.
ما نصه٢: إن من المقامات ما يحسن من شخص، ويلام في تركه، ويذم من شخص آخر، كما نهى رسول الله "صلى الله عليه وسلم" من أراد الإقتداء به في الوصال بقوله: "إني لست كهيئتكم" ٣.
٢- قوله ضمن المسائل المستنبطة من قوله تعالى: {قَالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأَباً فَمَا حَصَدْتُمْ فَذَرُوهُ فِي سُنْبُلِهِ إِلَّا قَلِيلاً مِمَّا تَأْكُلُونَ} ٤ الآيات.
من نصه:- النصيحة ولو لغير المسلمين كما قال "صلى الله عليه وسلم": "في كل كبد رطبة أجر" ٥. وأما المسلم فنصحه من الفرائض٦.
٣- قوله ضمن المسائل المستنبطة من قوله تعالى: {قَالَ إِنَّمَا أَشْكُو بَثِّي وَحُزْنِي إِلَى اللَّهِ وَأَعْلَمُ مِنَ اللَّهِ مَا لا تَعْلَمُونَ} ٧- ما نصه٨: إن هذا في مثل هذا المقام ليس من الفخر كما قال النبي "صلى الله عليه وسلم": "أنا سيد ولد آدم ولا فخر" ٩.
٤- قوله ضمن المسائل المستنبطة من قوله تعالى: {قَالَ إِنِّي أُرِيدُ أَنْ أُنْكِحَكَ إِحْدَى ابْنَتَيَّ هَاتَيْنِ عَلَى أَنْ تَأْجُرَنِي ثَمَانِيَ حِجَجٍ فَإِنْ أَتْمَمْتَ عَشْراً
_________
١ سورة يوسف: آية "٤٢".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير "١٤٩".
٣ سيأتي تخريجه في موضعه من التحقيق ص "٣٣٧".
٤ سورة يوسف: آية "٤٧".
٥ سيأتي تخريجه في موضعه من التحقيق ص "٣٤٣".
٦ مؤلفات الشيخ "القسم الرابع، التفسير "١٥٢".
٧ سورة يوسف: آية "٨٦".
٨ مؤلفات الشيخ، القسم الرابع، التفسير "١٧٠".
٩ سيأتي تخريجه في موضعه من التحقيق ص "٣٨١".',NULL,'63',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (72,'فَمِنْ عِنْدِكَ وَمَا أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ مِنَ الصَّالِحِينَ} ١.
ما نصه٢:- إن هذه المهنة لا نقص فيها، كيف وقد قال رسول الله "صلى الله عليه وسلم": "ما بعث الله نبياً إلا رعى الغنم" ٣.
ويتضح من كل ما تقدم نظرة الشيخ الشمولية لتفسير القرآن بالسنة، حتى إنه يوضح بعض المعاني المستنبطة من الكتاب بفعل النبي "صلى الله عليه وسلم"، "وهو من السنة" ومن ذلك قوله ضمن المسائل المستنبطة من قوله تعالى للوط عليه السلام: {فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَاتَّبِعْ أَدْبَارَهُمْ وَلا يَلْتَفِتْ مِنْكُمْ أَحَدٌ وَامْضُوا حَيْثُ تُؤْمَرُونَ} ٤.
ما نصه٥: معرفة قدر كونه آخر الرفقة في السفر، كما كان النبي "صلى الله عليه وسلم" يتخلف في آخرهم٦.
ويوضح المعاني أحياناً بتقرير النبي "صلى الله عليه وسلم" مما يؤكد النظرة الشمولية لمعنى السنة وتفسير القرآن بها ويتضح هذا من إيراده الحديث الآتي بعد قوله في كتاب التوحيد "باب ما جاء في قول الله تعالى: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيَامَةِ وَالسَّمَاوَاتُ مَطْوِيَّاتٌ بِيَمِينِهِ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ} ٧.
ولحديث الذي أورده بعد هذه الآية مبيناً لها هو ما ورد عن أبن مسعود، رضي الله عنه قال جاء حبر من الأحبار إلى رسول الله "صلى الله عليه وسلم" فقال: يا محمد، إنا نجد أن الله يجعل السموات على أصبع والأرضين على أصبع والشجر على أصبع، والماء على إصبع، والثرى على أصبع وسائر الخلق على أصبع
_________
١ سورة القصص: آية "٢٧".
٢ مؤلفات الشيخ، القسم الرابع، التفسير "٢٩٠".
٣ رواه البخاري في صحيحه/ كتاب الإجارة/ باب رعي الغنم على قراريط "انظر البخاري مع الفتح "٤: ٥١٦" ح "٢٢٦٢" من حديث أبي هريرة ونحوه عند مسلم. كتاب الأشربة/باب فضيلة الأسود من الكباث "٣: ١٦٢١" ح "٢٠٥٠" من حديث جابر وفيه: وهل من نبي إلا وقد رعاها.
٤ سورة الحجر: آية "٦٥"
٥ مؤلفات الشيخ، القسم الرابع، التفسير "١٩٣".
٦ سيأتي تخريجه في موضعه من التحقيق ص "٤١٥"
٧ سورة الزمر: آية "٦٧".',NULL,'64',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (73,'فيقول: أنا الملك. فضحك النبي "صلى الله عليه وسلم" حتى بدت نواجذه تصديقاً لقول الحبر، ثم قرأ: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيَامَةِ} وقد تقدم الحديث برواياته١.
ومما يدل على أن الشيخ رحمه الله أورد هذا الحديث مورد التفسير للآية قوله ضمن المسائل المستنبطة من هذا الباب: الأولى: تفسير قوله تعالى: {وَالْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيَامَةِ} ٢ والله أعلم.
_________
١ انظر ما تقدم ص "٦١".
٢ مؤلفات الشيخ / القسم الأول/ التوحيد "١٥٠".',NULL,'65',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (74,'<span data-type="title" id=toc-6>الفصل الثالث: تفسيره القرآن بأقوال السلف من الصحابة والتابعين</span>
<span data-type="title" id=toc-7>المبحث الأول: تفسيره القرآن بأقوال الصحابة</span>
اهتم الشيخ "رحمه الله" بتفسير الصحابة، فكان كثيرا ًما يورد تفاسيرهم في الآية مكتفياً بها حيناً، ومستفيداً من فهمهم وتفسيراتهم حيناً آخر، وخصوصاً من اشتهر منهم بطول باعه، وتضلعه من علم التفسير كحبر الأمة ابن عباس وابن مسعود وغيرهما "رضي الله عنهم أجمعين".
وذلك أن تفسير الصحابة رضي الله عنهم له المنزلة السامية بين تفاسير السلف، فإذا لم نجد التفسير في كتاب الله، ولا في سنة رسول الله "صلى الله عليه وسلم" فإننا نرجع إلى تفسيراتهم، وأقوالهم، واستنباطاتهم من القرآن وذلك لما ورد من الآيات المتكاثرة، والأحاديث المتواترة الناصة على كمالهم، والمورثة العلم القطعي بفضلهم وسبقهم وعدالتهم١، كما قال تعالى: {مُحَمَّدٌ رَسُولُ اللَّهِ وَالَّذِينَ مَعَهُ أَشِدَّاءُ عَلَى الْكُفَّارِ رُحَمَاءُ بَيْنَهُمْ تَرَاهُمْ رُكَّعاً سُجَّداً يَبْتَغُونَ فَضْلاً مِنَ اللَّهِ وَرِضْوَاناً سِيمَاهُمْ فِي وُجُوهِهِمْ مِنْ أَثَرِ السُّجُودِ ذَلِكَ مَثَلُهُمْ فِي التَّوْرَاةِ وَمَثَلُهُمْ فِي الْأِنْجِيلِ كَزَرْعٍ أَخْرَجَ شَطْأَهُ فَآزَرَهُ فَاسْتَغْلَظَ فَاسْتَوَى عَلَى سُوقِهِ يُعْجِبُ الزُّرَّاعَ لِيَغِيظَ بِهِمُ الْكُفَّارَ وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْهُمْ مَغْفِرَةً وَأَجْراً عَظِيماً} ٢ وغير ذلك.
ولكونهم أدرى بالتفسير من غيرهم لما شاهدوه من القرائن والأحوال التى اختصوا بها، ولما لهم من الفهم التام والعلم الصحيح.
وللشيخ "رحمه الله" طريقتان في إيراد أقوال الصحابة والاستفادة من تفسيراتهم:
الطريقة الأولى: أن يصرح بذكر اسم الصحابي المروي عنه، كأن يقول: قال ابن عباس. أو قاله ابن عباس مثلا. أو نحو ذلك.
ومن أمثلة ذلك:
- قوله في تفسير قول الله تعالى: {الرَّحْمَنِ الرَّحِيمِ} :
_________
١ انظر مؤلفات الشيخ/ ملحق المصنفات/ رسالة في الرد على الرافضة ص "١٦- ١٩".
٢ سورة الفتح: آية "٢٩".',NULL,'66',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (75,'اسمان مشتقان من الرحمة، أحدهما أبلغ من الآخر مثل العلام والعليم، قال ابن عباس: هما اسمان رقيقان أحدهما أرق من الآخر١. أي أكثر من الآخر رحمة٢.
- وقوله في تفسير قول الله تعالى: {وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ} ٣: قال ابن القيم:- قال ابن عباس: {وَلَقَدْ خَلَقْنَاكُمْ} يعني آدم, {ثُمَّ صَوَّرْنَاكُمْ} لذريته٤.
- وقوله في تفسير قوله تعالى: {وَلَقَدْ خَلَقْنَا الْأِنْسَانَ مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍ} ٥:
قال ابن عباس في رواية الوالبي٦- الصلصال: الطين اليابس.
وفي رواية: الذي إذا نقر صوت.
- وقوله في تفسير قوله تعالى: {وَأَلَّوِ اسْتَقَامُوا عَلَى الطَّرِيقَةِ لَأَسْقَيْنَاهُمْ مَاءً غَدَقاً لِنَفْتِنَهُمْ فِيهِ وَمَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذَاباً صَعَداً} ٧.
_________
١ أخرجه البيهقي في الأسماء والصفات ص "٧١" من طريق السدي عن الكلبي عن أبي صالح عن ابن عباس. وهذه من أوهى الطرق عن ابن عباس.
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٠".
٣ سورة الأعراف: آية "١١".
٤ أخرجه الطبري في تفسيره "١٢٦:٨" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٨٢".
٥ سورة الحجر: آية "٢٦".
٦ لعل المراد بالوالبي هنا سعيد بن جبير، ويؤيده الرواية التي هي بنحو هذا عن سعيد بن جبير عن ابن عباس أخرجها الطبري في تفسيره "٢٧:١٤" ونحوها عن سعيد عن قتاده. وسعيد هو: ابن جبير بن هشام، الإمام، الحافظ، المقرئ، المفسر، الشهيد أبو محمد ويقال: أبو عبد الله الأسدي، الوالبي، مولاهم، الكوفي، أحد أعلام التابعين، صحب ابن عباس فأكثر عنه وجود، وأخذ عن غيره من الصحابة. وقتله الحجاج سنة "٩٥" خمس وتسعين من الهجرة، عن "٥٧" سبعة وخمسين عاماً. انظر طبقات ابن سعد "٦: ٢٥٦" سير أعلام النبلاء "٤: ٣٢١" تهذيب التهذيب "٤: ١١".
٧ سورة الجن: آية "١٦، ١٧".',NULL,'67',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (76,'أخرج ابن جرير عن عمر: حيث كان الماء كان المال، وحيث ما كان المال كانت الفتنة١.
وقوله: {يَسْلُكْهُ عَذَاباً صَعَداً} "قال ابن عباس: شاقاً٢.
الطريقة الثانية: أن لا يصرح بذكر اسم الصحابي المروي عنه، ولكن يذكر أن هذا قول لبعض الصحابة، أو يذكر أنه قول بعض السلف أو نحو ذلك، والمعني هو ذلك الصحابي القائل. ومن أمثلة ذلك:
- قوله عند الكلام على قول الله تعالى: {وَمَنْ أَعْرَضَ عَنْ ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنْكاً وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَى} ٣ بعد كلام له:
فذكر الله لمن أعرض عن القرآن وأراد الفقه من غيره عقوبتين:
أحداهما: المعيشة الضنك- وفسرها السلف بنوعين:
الأول: ضنك الدنيا. وهو أنه إن كان غنياً سلط الله عليه خوف الفقر، وتعب القلب والبدن في جمع الدنيا، حتى يأتيه الموت ولم يتهن بعيش.
الثاني: الضنك في البرزخ وعذاب القبر.
وفسر الضنك أيضاً بالجهل، فإن الشك والحيرة لها من القلق وضيق الصدر ما لها٤، فصار في هذا مصداق قوله في الحديث "ومن ابتغى الهدى من غيره أضله الله" ٥.
_________
١ أخرجه الطبري في تفسيره "٢٩: ١١٥" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٥٩".
٢ أخرجه الطبري في تفسيره "١١٥:٢٩" إلا أنه قال: مشقة من العذاب يصعد فيها. فلعل الشيخ رواه بالمعنى.
٣ سور طه: آية "١٢٤".
٤ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٢٦٧".
٥ جزء من حديث مرفوع رواه الدارمي في سننه/ كتاب فضائل القرآن/ باب فضل من قرأ القرآن "٤٣٥:٢" والترمذي في جامعه/ كتاب فضائل القرآن/ باب ما جاء في فضل القرآن "١٧٢:٥" ح "٢٩٠٦" ويصححه العلماء موقوفاً من كلام علي رضي الله عنه.
وانظر الكلام عليه في كتاب "فضائل القرآن" لابن كثير بذيل تفسيره "٤: ٥٨٢".',NULL,'68',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (77,'فالنوع الأول الذي ذكره الشيخ في تفسير المعيشة الضنك روي نحو معناه عن ابن عباس رضي الله عنهما؛ حيث روي عنه: {وَمَنْ أَعْرَضَ عَنْ ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنْكاً} يقول: كل مال أعطيته عبداً من عبادي قل أو كثر، لا يتقيني فيه، لا خير فيه، وهو الضنك في المعيشة. ويقال: إن قوماً ضلالاً أعرضوا عن الحق وكانوا أولي سعة من الدنيا مكثرين، فكانت معيشتهم ضنكاً، وذلك أنهم كانوا يرون أن الله عز وجل ليس بمخلف لهم معايشهم من سوء ظنهم بالله، والتكذيب به، فإذا كان العبد يكذب بالله ويسيء الظن به، اشتدت عليه معيشته فذلك الضنك١.
والنوع الثاني: مروي عن بعض الصحابة كأبي سعيد الخدري، وأبي هريرة وابن مسعود رضي الله عنهم٢.
_________
١ أخرجه البخاري في تفسيره عن ابن عباس "٢٢٧:١٦" من طريق واهيه؛ إذ هو من طريق العوفي عنه.
٢ أخرجه الطبري في تفسيره "١٦: ٢٢٧، ٢٢٨".',NULL,'69',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (78,'<span data-type="title" id=toc-8>المبحث الثاني: تفسيره القرآن بأقوال التابعين</span>.
حيث أن التابعين هم تلامذة الصحابة الكرام رضوان الله عليهم، وقد تلقوا عنهم علومهم ومعارفهم، ونهلوا من منهلهم الصافي. فقد برز منهم علماء أفذاذ في تلك الحقبة الفاضلة، كانوا غرة في جبين الأمة الإسلامية ومسيرتها.
وقد كان لعلومهم أثرها، ولقولهم وزنه، ومن هنا اهتم العلماء بإيراد أقوالهم ضمن إيرادهم للتفسير المأثور في كتبهم التي اهتمت بذلك كتفسير عبد الرزاق، والطبري، والبغوي، وغيرهم.
وكان الشيخ محمد رحمه الله من أولئك العلماء الذين اهتموا بالاستفادة من تفاسير التابعين، وإيراد أقوالهم، فأوردها مستعيناً بها على تفسير الآية وإيضاح معناها، ومستشهداً بها على ما يستنبطه من أحكام وفوائد، وإن لم يكن ذلك بالقدر الموجود عند من افردوا التفسير بتصنيف خاص لجميع القرآن، إلا أنه قد طبق منهج المفسرين في هذا الجانب، فلم يهمل تفسير التابعين، بلى إنه أنزله منزلة عالية حتى كأنه يميل إلى حجية تفسير التابعين، يتضح هذا من قوله ضمن كلام وارد في رسالة مبحث الاجتهاد والخلاف وهي مختصرة من "أعلام الموقعين" لابن القيم: ومن تأمل كتب السلف ومن بعدهم وجدها مشحونة بالاحتجاج بتفسير التابعي١.
ولم يعقب الشيخ على هذا الكلام بشيء، مما يوحي بميله إلى هذا القول. ومما يقوي القول بترجيحه لما اقتصر على ذكره أن ابن القيم ذكر في "إعلام الموقعين" قبل هذا الكلام مباشرة أن في الاحتجاج بتفسير التابعي عن الإمام أحمد روايتين٢. فأعرض الشيخ عن هذا الكلام وأورد ما ذكرت ففي هذا ما يوحي باختياره لإحدى الروايتين عن الإمام أحمد في هذا الموضوع.
وإذا كان الأمر كذلك فقد رجع كثير من الأئمة في تفسير القرآن إلى أقوال التابعين،
_________
١ انظر مجموع المؤلفات "القسم الثاني- الفقه- المجلد الثاني- مبحث الاجتهاد والخلاف ص ٣٥.
٢ إعلام الموقعين لابن القيم ١٥٦:٤.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (79,'إذا لم يجدوا التفسير في القرآن نفسه، أو السنة الصحيحة، أو أقوال الصحابة١، فلا يكاد يخلو كتاب من كتب التفسير من إيراد أقوال التابعين، كمجاهد، وقتادة، وسعيد بن جبير، وسعيد بن المسيب، والضحاك، والحسن البصري، وغيرهم.
وقد حكى الأئمة أقوالهم، لأن غالب تفسيراتهم متلقاة من قبل الصحابة، فقد تلقوا عنهم القرآن وتفسيره، وعايشوا الصحابة، ورأوا أحوالهم، وعرفوا عقيدتهم، ومنهجهم، وأقوالهم، فمجاهد مثلا يقول:- عرضت المصحف على ابن عباس ثلاث عرضات من فاتحته إلى خاتمته أوقفه عند كل آية منه، وأسأله عنها٢.
وابن أبي مليكة يقول: رأيت مجاهداً يسأل ابن عباس عن تفسير القرآن، ومعه ألواحه، فيقول له ابن عباس اكتب حتى سأله عن التفسير كله٣.
ولهذا كان سفيان الثوري يقول: إذا جاءك التفسير عن مجاهد فحسبك به٤.
وقتادة يقول: ما في القرآن آية إلا وقد سمعت فيها شيئاً٥.
قال شيخ الإسلام ابن تيمية في معرض كلامه في الحث على التفسير على منهج السلف والتحذير من الابتداع وفى الجملة: من عدل عن مذاهب الصحابة والتابعين وتفسيرهم إلى ما يخالف ذلك كان مخطئاً في ذلك، بل مبتدعاً، وإن كان مجتهداً مغفوراً له خطؤه.
فالمقصود بيان طرق العلم، وأدلته، وطرق الصواب، ونحن نعلم أن القرآن قرأه الصحابة، والتابعون، وتابعوهم، وأنهم كانوا أعلم بتفسيره ومعانيه، كما أنهم أعلم بالحق الذي بعث الله به رسوله "صلى الله عليه وسلم" فمن خالف قولهم، وفسر القرآن بخلاف تفسيرهم فقد أخطأ في الدليل والمدلول جميعاً، ومعلوم أن كل من خالف قولهم له شبهة يذكرها إما عقلية وإما سمعيه كما هو مبسوط في موضعه٦.
_________
١ مقدمة في أصول التفسير ص "١٠٢".
٢ أخرجه الطبراني في تفسيره "١: ٤٠".
٣ أخرجه الطبراني في تفسيره "١: ٤٠".
٤ أخرجه الطبراني في تفسيره "١: ٤٠".
٥ أخرجه الترمذي في جامعه/ كتاب التفسير/ باب ما جاء في الذي يفسر القرآن برأيه "٥: ٢٠٠".
٦ مقدمة في أصول التفسير ص "٩١".',NULL,'71',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (80,'بينما ذهبت طائفة أخرى من العلماء إلى عدم الاحتجاج بتفسير التابعين، وهو رواية عن الإمام أحمد وحكي عن شعبة١.
قال الزركشي: ولعل اختلاف الرواية عن أحمد إنما هو فيما كان من أقوالهم وآرائهم٢. قلت: ولا ريب عندي أن الأمر كذلك، إذ ما صرحوا بنقله عن غيرهم لا يكون من قولهم بل هم في هذه الحالة رواة.
أما شعبة فقد حكي عنه أنه قال: أقوال التابعين في الفروع ليست حجه فكيف تكون حجة في التفسير؟ قال شيخ الإسلام: يعني أنها لا تكون حجة على غيرهم ممن خالفهم٣. ولعل عدم احتجاج هؤلاء العلماء بتفسير التابعي لأنهم ليس لهم سماع من النبي صلى الله عليه وسلم، كما أنهم لم يشاهدوا القرائن والأحوال التي نزل عليهم القرآن. ثم إن عدالة كل تابعي غير منصوص عليها كما نص على عدالة الصحابة. كما أنه لا دليل على حجية قولهم لذاته. والله أعلم.
والذي أختاره بعد هذا أن النفس تطمئن إلى التفسير المأثور عن التابعين أكثر ممن بعدهم لقربهم من عمر النبوة وخير القرون.
وأما الاحتجاج بأقوالهم لمجردها، فالأمر عندي كما قال شيخ الإسلام: إن أقوالهم لا تكون حجة على غيرهم ممن خالفهم، أما إذا اجتمعوا على الشيء فلا يرتات في
_________
١ المرجع السابق ص "١٠٥" وأعلام الموقعين "٤: ١٥٦" والبرهان في علوم القرآن للزركشي "١٥٨:٢" وشعبة هو ابن الحجاج بن الورد يكنى بأبي بسطام، العتكي، مولاهم، الواسطي ثم البصري، الإمام، الحافظ، أمير المؤمنين في الحديث.
كال الذهبي:- كان إماماً، ثبتاً، حجة، ناقداً، جهبذاً، صالحاً، زاهداً، قانعاً بالقوت، رأساً في العلم والعمل منقطع القرين.
وتوفي سنة "١٦٠" وله "٧٧" سنة تقريباً.
انظر في ترجمته طبقات ابن سعد "٧: ٢٨٠، ٢٨١" سير أعلام النبلاء "٧: ٢٠٢-٢٢٨" تهذيب التهذيب "٣٣٨:٤- ٣٤٦".
٢ البرهان في علوم القرآن "١٥٨:٢".
٣ مقدمة في أصول التفسير "١٠٥".',NULL,'72',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (81,'كونه حجة، فإن اختلفوا فلا يكون قول بعضهم حجة على بعض، ولا على من بعدهم، ويرجع في ذلك إلى لغة القرآن، أو السنة، أو عموم لغة العرب أو أقوال الصحابة في ذلك١.
هذا وبعد إيضاح علو منزلة تفسير التابعين عند الشيخ رحمه الله، والإشارة إلى مذاهب العلماء في حجة تفسيرهم، أعود فأقول إن مجمل روايات التابعين التي يوردها الشيخ تدور على أعلامهم المشهورين منهم بالتفسير كمجاهدين جبر المخزومي، وقتادة بن دعامة السدوس، والحسن البصري.
وله رحمه الله طريقتان مشهورتان في إيراد أقوال التابعين والاستفادة من تفسيراتهم:
الطريقة الأولى: أن يصرح باسم التابعي المفسر أو المستشهد بكلامه كمجاهد وقتادة وغيرهما.
ومن أمثلة ذلك ما يلي:
- ما أورده من نقول عن السلف في تفسير بعض الآيات المتعلقة بقصة آدم وإبليس ومنها قوله تعالى إخباراً عن مقالة إبليس: {ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَانِهِمْ وَعَنْ شَمَائِلِهِمْ وَلا تَجِدُ أَكْثَرَهُمْ شَاكِرِينَ} ٢.
حيث قال في قوله: {وَعَنْ شَمَائِلِهِمْ} : الباطل أرغبهم فيه، قال الحسن: السيئات يحثهم عليها ويزينها في أعينهم٣.
فقد فسر الشيخ رحمه الله قوله: {عَنْ شَمَائِلِهِمْ} بأن المراد منه ترغيبهم في الباطل. ثم أورد تفسير التابعي – الحسن البصري – مستشهداً به على ما ذهب إليه من تفسير الآية.
_________
١ مقدمة في أصول التفسير ص "١٠٥".
٢ سورة الأعراف الآية "١٧".
٣ مؤلفات الشيخ، القسم الرابع، التفسير ص "٨٨" –وقول الحسن لم أجده عنه، وقد روي نحوه عن ابن عباس، كما ورد نحوه عن غير الحسن من التابعين كقتادة وإبراهيم وغيرهم.
انظر تفسير الطبري "٨: ١٣٦، ١٣٧".',NULL,'73',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (82,'- وعند قوله: {مِنْ بَيْنِ أَيْدِيهِمْ} أورد تفسير مجاهد، وهو قوله: من حيث يبصرون: {وَمِنْ خَلْفِهِمْ} من حيث لا يبصرون١.
- كما قال عند قوله تعالى: {لَأَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيباً مَفْرُوضاً} ٢ قال الضحاك مفروضا: معلوما٣.
وعند قوله: {وَلَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ} ٤ قال ابن عباس: دين الله. وقاله ابن المسيب والحسن وإبراهيم وغيرهم٥.
فقد أورد تفسير الصحابي هنا ثم من وافقه من التابعين.
- وعند تفسير قوله تعالى- في قصة يوسف: {وَيُعَلِّمُكَ مِنْ تَأْوِيلِ الْأَحَادِيث} ٦ قال: قال مجاهد وغيره، عبارة الرؤيا٧.
وقد قال بهذا القول مع مجاهد قتادة والسدي٨.
- وأورد خلال تفسير سورة الجن بعض تفسيرات التابعين حيث قال عند قوله تعالى: {وَأَنَّهُ كَانَ يَقُولُ سَفِيهُنَا عَلَى اللَّهِ شَطَطاً} ٩ سفيههم إبليس. قاله مجاهد١٠.
_________
١ مؤلفات الشيخ، القسم الرابع، التفسير ص٨٩ وقول مجاهد في هذا أخرجه الطبري ص "٨: ١٣٧".
٢ سورة النساء الآية "١١٨".
٣ مؤلفات الشيخ، القسم الرابع، التفسير ص "٩٠" وقول الضحاك أخرجه الطبري "٥: ٢٨١".
٤ سورة النساء: آية "١١٩".
٥ مؤلفات الشيخ،القسم الرابع، التفسير ص"٩٠" وانظر تفسير الطبري "٥: ٢٨٣، ٢٨٤".
٦ سورة يوسف: آية "٦".
٧ مؤلفات الشيخ، القسم الرابع، التفسير ص"١٢٩" وقول مجاهد أخرجه الطبري "١٥٣:١٢".
٨ ذكره ابن الجوزي عنهما في زاد المسير "٤: ١٨١".
٩ سورة الجن آية "٤".
١٠ أخرجه الطبري "٢٩: ١٠٧".',NULL,'74',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (83,'- وعند قوله تعالى: {وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلا تَدْعُو مَعَ اللَّهِ أَحَداً} ١ قال: قال قتادة: كان اليهود والنصارى إذا دخلوا بيعهم وكنائسهم أشركوا بالله فأمرنا أن نخلص لله الدعوة إذا دخلنا المساجد٢.
وقيل المساجد أعضاء السجود.
- وقال عند تفسير قوله تعالى: {وَأَنَّهُ لَمَّا قَامَ عَبْدُ اللَّهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَداً} ٣ ... وعن قتادة قال: لما قام عبد الله للدعوة تلبدت الإنس والجن وتظاهروا عليه ليبطلوا الحق الذي جاءهم به ويطفئوا نور الله: {وَيَأْبَى اللَّهُ} إلا أن يتم هذا الأمر وينصره على من ناوأه٤.
- وقال عند تفسير قوله تعالى: {وَأَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذَلِكَ} ٥.... قال الحسن: أمثالكم فمنهم قدرية ومرجئة ورافضة٦.
الطريقة الثانية:- أن لا يصرح بذكر اسم التابعي، ولكن يميزه عن قوله بما يتميز به، نحو "قال بعض السلف" أو "قيل" ويريد به قول بعض التابعين.
ومن أمثله ذلك:
- قوله عند تفسير قوله تعالى: {وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلا تَدْعُو مَعَ اللَّهِ أَحَداً} وقيل: المساجد أعضاء السجود ٧.أ. هـ.
وهذا القول مروي عن سعيد بن جبير ٨.
- قوله عند تفسير قوله تعالى: {وَأَوْحَيْنَا إِلَيْهِ لَتُنَبِّئَنَّهُمْ بِأَمْرِهِمْ هَذَا وَهُمْ لا يَشْعُرُونَ} ٩ وقيل- لا يشعرون بإيحائنا ذلك إليه١٠.أ. هـ.
وهذا القول مروي عن مجاهد١١.
_________
١ سورة الجن: آية "١٨".
٢ أخرجه الطبري "١١٧:٢٩".
٣ سورة الجن: آية "١٩".
٤ أخرجه الطبري عن قتادة "١١٨:٢٩" مع بعض الاختلاف ولعل الشيخ أورده بالمعنى.
٥ سورة الجن: آية "١١".
٦ ذكره البغوي في تفسيره "٤٠٣:٤".
٧ مؤلفات الشيخ، القسم الرابع، التفسير ص"٣٦٠".
٨ ذكره البغوي عن سعيد بن جبير "٤٠٤:٤".
٩ سورة يوسف: آية "١٥".
١٠ مؤلفات الشيخ، القسم الرابع، التفسير ص "١٣٢".
١١ سيأتي تخريجه عن مجاهد في موضعه من التحقيق ص "٣٠١".',NULL,'75',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (84,'- وقال عند قوله تعالى: {وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَى} [طه:١٢٤] ، قال بعض السلف: أعمى عن الحجة لا يقدر على المجادلة بالباطل كما كان يصنع في الدنيا١.
وهذا القول مروي عن مجاهد وأبي صالح.
حيث قال مجاهد: {وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَى} عن الحجة٢.
وقال أبو صالح: {وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَى} ليس له حجة٣.
والشيخ رحمه الله لا يقتصر على إيراد ما هو من قبيل التفسير بل يورد أحياناً من أقوال التابعين ما يناسب المقام وله نوع تعلق بالآية وإن لم يكن من قبيل التفسير فيهدف من ذلك إلى الحث على أمر، أو التحذير منه، أو الاستدلال لصحة استنباط أو نحو ذلك.
فمن ذلك قوله ضمن الفوائد المستنبطة من قصة آدم وإبليس٤: ومنها- أي من الفوائد- معرفة شدة عداوة عدو الله لنا وحرصه على إغوائنا بكل طريق فيعتد المؤمن لهذا الحرب عدته ويعلم قوة عدوه وضعفه عن محاربته إلا بمعونة الله كما قال قتادة: إن عدوا يرانا هو وقبيله من حيث لا نراهم أنه شديد المؤونة إلا من عصمه الله٥.
وقد ذكر الله عداوته في القرآن في غير موضع وأمرنا باتخاذه عدوا.
فقد استنبط الشيخ من هذه القصة شدة عداوة إبليس لت وخطورته وكيفية النجاة منه ثم دعم ذلك بإيراد قول التابعي رحمه الله.
ثم أورد الشيخ قول قتادة أيضاً بعد تفسير قوله تعالى حكاية عن إبليس: {ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ..} ٦ الآية حيث يقول: أتاك الشيطان يا ابن آدم من كل وجه إلا أنه لم يأتك من فوقك ولم يستطع أن يحول بينك وبين رحمة ربك٧.
ومن الاستشهاد بأقوال التابعين أيضاً قوله ضمن المسائل المستنبطة من قصة يوسف وإخوته وحال أبيه من الحزن: العبرة فيما ذكركما قال الحسن: لقد ابتلى بهذه المدة الطويلة وأنه لأكرم أهل الأرض على الله٨.
_________
١ مؤلفات الشيخ، القسم الرابع، التفسير ص "٢٦٨".
٢ أخرجه الطبري "١٦: ٢٢٩".
٣ المرجع السابق "١٦: ٢٢٩".
٤ مؤلفات الشيخ، القسم الرابع" التفسير "٨١".
٥ عزا السيوطي أخراجه عن قتادة إلى عبد بن حميد وأبى الشيخ انظر الدر المنثور "٣: ٤٣٦".
٦ س سورة الأعراف: آية "١٧".
٧ أخرجه الطبري "١٣٦:٨".
٨ سورة مؤلفات الشيخ، القسم الرابع، التفسير ص ١٦٩والأثر سيأتي تخريجه في موضعه من التحقيق ص "٣٧٩".',NULL,'76',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (85,'<span data-type="title" id=toc-9>المبحث الثالث: موقف الشيخ من اختلاف السلف في التفسير</span>
أورد الشيخ رحمه الله جملة صالحة من تفاسير السلف من الصحابة والتابعين وغيرهم كما سبقت الإشارة إلى ذلك في تفسير الصحابة والتابعين في المبحثين السابقين.
ومن المعلوم أن السلف كانوا يختلفون في التفسير أحياناً، فما موقف الشيخ وقد أورد شيئاً من ذلك الاختلاف؟.
لبيان هذا أقول: إن الاختلاف منه ما هو اختلاف تباين، ومنه ما هو من قبيل اختلاف التنوع والعبارة، فيذكر كل من المفسرين نوعاً مما هو مندرج تحت أصل عام، أو يعبر عن المعنى بخلاف ما يعبر عنه الآخر، والكل داخل تحت معنى الآية.
وقد بين الشيخ أن أكثر اختلاف السلف في التفسير من قبيل النوع الثاني وهو اختلاف التنوع والعبارة لا اختلاف التضاد والتباين حيث قال: عادة السلف يفسرون اللفظ العام ببعض أفراده، وقد يكون السامع يعتقد أن ذلك ليس من أفراده. وهذا كثير في كلامهم جداً، ينبغي التفطن له١.
ويقول عند قول الله تعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَرَسُولِهِ} ٢ الآية.: اختلاف كلام المفسرين، والمعنى واحد، لكن كل رجل يصف نوعاً من التقدم٣.أ. هـ.
وذلك أن المفسرين اختلفوا في تحديد معنى التقدم المنهي عنه في الآية-
فقال ابن عباس: لا تقولوا خلاف الكتاب والسنة.
وقال مجاهد: لا تفتاتوا على رسول الله صلى الله عليه وسلم بشيء حتى يقضي الله على لسانه.
وقال الضحاك: لا تقضوا أمراً دون الله ورسوله من شرائع دينكم.
وقال سفيان الثوري: {لا تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَرَسُولِهِ} بقول ولا فعل.
_________
١ مؤلفات الشيخ، القسم الثالث، الفتاوى ص "٥٤".
٢ سورة الحجرات: آية "١".
٣ مؤلفات الشيخ، القسم الرابع، التفسير ص "٣٤٩".',NULL,'77',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (86,'وقيل غير ذلك١.
فوضح الشيخ بعبارته السابقة أن المعنى واحد، وكلاً من المفسرين قد ذكر نوعاً من أنواع التقدم، والكل راجع إلى معنى واحد وهو ما عبر عنه ابن كثير بقوله- قبل إيراده الأقوال المذكورة وغيرها- أي: لا تسرعوا في الأشياء بين يديه- أي قبله- بل كونوا تبعاً له في جميع الأمور٢.
وإنما نبه الشيخ إلى هذا لعظيم فائدته. فينبغي التفطن له، لئلا يظن من لا فهم عنده للمعاني، أومن يقصر نظره عن المعنى العام الجامع فيغفل عنه، ويقصر نظره على أنواعه أن ذلك اختلاف تضاد فيحكي في معنى الآية أقوالاً عديدة، ومآلها إلى معنى واحد، أو هي جميعاً مندرجة تحت أصل عام. وهذه فائدة نفيسة كما ترى. فكثير من الأقاويل عند التدقيق والتحقيق ترجع إلى معنى واحد. وكما نبه الشيخ إلى هذا فإنا نجد في تفسيره منهجاً تطبيقياً لموقف المفسر من هذا الاختلاف، إذ يجمع بعض أقوال السلف إلى بعض ويحسن الربط بينها، وإيضاح وجه كل منها، وبيانه بياناً شافياً، ولربما دعم ذلك البيان بالكتاب والسنة وضرب الأمثلة.
ويتضح ذلك من كلامه على قصة آدم وإبليس عندما ذكر تفاسير السلف لقوله تعالى: {ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَانِهِمْ وَعَنْ شَمَائِلِهِمْ} ٣.
فقال: {مِنْ بَيْنِ أَيْدِيهِمْ} يعني الدنيا "أو" ٤ الآخرة: {وَمِنْ خَلْفِهِمْ} يعني
_________
١ انظر هذه الأقوال وغيرها في تفسير الطبري "٢٦: ١١٦، ١١٧" وتفسير ابن كثير "٣٤٥:٧".
٢ تفسير ابن كثير "٣٤٥:٧".
٣ سورة الأعراف: آية "١١".
٤ في النسخ المخطوطة والمطبوعة من التفسير بالواو، في هذا الموضع والذي بعده، والأظهر. ما أثبته كما هو في روضة. الأفكار والأفهام "١: ٤٢٥" والمطبوع المحرر "٢: ٣٩٦".',NULL,'78',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (87,'الآخرة "أو" الدنيا١: {وَعَنْ أَيْمَانِهِمْ} قال ابن عباس: أشبه عليهم أمر دينهم٢، وعنه أيضاً: من قبل الحسنات٣.
وقوله: {وَعَنْ شَمَائِلِهِمْ} الباطل أرغبهم فيه.
قال الحسن: السيئات يحثهم عليها ويزينها في أعينهم٤.
قال قتادة: أتاك الشيطان يا ابن آدم من كل وجه إلا أنه لم يأتك من فوقك، ولم يستطع أن يحول بينك وبين رحمة الله٥.
ثم قال الشيخ وهو يوافق قول من "قال" ٦: ذكر هذه الأوجه للمبالغة في التوكيد. أي أتصرف لهم في الإضلال من جميع جهاتهم.
ولا يناقض ما ذكر السلف. فإن ذلك على جهة التمثيل، فالسبل التي للإنسان أربعة فقط، فإنه تارة يأخذ على جهة شماله، وتارة على يمينه، وتارة أمامه، وتارة يرجع خلفه. فأي سبيل من هذه سلكها وجد الشيطان عليها راصداً له، فإن سلكها في طاعة ثبطه، وإن سلكها بالمعصية حداه. وأنا أمثل لك مثالاً واحداً لما ذكر السلف: وهو أن العدو الذي من بني آدم إذا أراد أن يمكر بك لم يستطع أن يمكر بك إلا في بعض الأشياء، وهي الأشياء الغامضة، والأشياء التى ليست بعالية، فلو أراد أن يمكر بك في أمر واضح بين مثل التردي من جبل أو بئر وأنت ترى ذلك لم يستطع، خصوصاً إذا عرفت أنه قد مكر بك مرات متعددة، ولو أراد ليمكر بك لتتزوج عجوزاً شوهاء وأنت تراها لم يستطع ذلك.
_________
١ أورده الطبري في تفسيره "٨: ١٣٦" عن ابن عباس من طريق ابن أبي طلحة في قوله: {مِنْ بَيْنِ أَيْدِيهِمْ} يقول: أشككهم في آخرتهم: {وَمِنْ خَلْفِهِمْ} أرغبهم في دنياهم.
وعنه أيضاً من الطريق نفسه: {مِنْ بَيْنِ أَيْدِيهِمْ} يعنى من الدنيا: {وَمِنْ خَلْفِهِمْ} من الآخرة. فكأن الشيخ قد اعتبر كلا الروايتين لورودهما بإسناد واحد متكرر.
والله أعلم.
٢ أخرجهما عنه الطبري في تفسيره "٨: ١٣٦" بالإسناد المتكرر الذي أشرت إليه في الهامش السابق.
٣ أخرجهما عنه الطبري في تفسيره "٨: ١٣٦" بالإسناد المتكرر الذي أشرت إليه في الهامش السابق.
٤ تقدم تخريجه ص "٧٣".
٥ تقدم تخريجه ص "٧٦".
٦ ليست في شيء من النسخ المطبوعة والمخطوطة "فيما اطلعت عليه" وإنما أتثبتها من روضة الأفكار والأفهام لابن غنام "١: ٢٤٦" والمطبوع المحرر "٣٩٧:٢" وهذه المقالة ذكرها ابن الجوزي في زاد المسير "١٧٧:٣".',NULL,'79',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (88,'وأنت ترى اللعين- أعاذنا الله منه- يأتي الآدمي في أشياء واضحة بينة أنها مما حرم الله ورسوله فيحمله عليها حتى "يفعلها"١، ويزينها في عينه حتى يفرح بها، ويزعم أن فيها مصلحة، ويذم من خالفه كما قال تعالى: {لا تَحْسَبَنَّ الَّذِينَ يَفْرَحُونَ بِمَا أَتَوْا} ٢ الآية وقوله: {وَلا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنْتُمْ تَعْلَمُونَ} ٣، وقوله: {وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرَاهُ مَا لَهُ فِي الْآخِرَةِ مِنْ خَلاقٍ} ٤.
وهذا معنى قول من قال: {مِنْ بَيْنِ أَيْدِيهِمْ} من قبل الدنيا، فإنهم يعرفونها وعيوبها، ومجمعون على ذمها، ثم مع هذا لأجلها قطعوا أرحامهم، وسفكوا دماءهم، وفعلوا ما فعلوا، وهذا معنى قول مجاهد: {مِنْ بَيْنِ أَيْدِيهِمْ} من حيث يبصرون٥. فهو لم يقنع بإتيانه إياهم من الجهة التي يجهلون أنها معصية مثل ما فسر به مجاهد: {وَمِنْ خَلْفِهِمْ} قال: من حيث لا يبصرون٦.
ولا من جهة الغيب كما قال فيها بعضهم: الآخرة أشككهم فيها، لم يقنع بذلك عدو الله حتى أتاهم في الأمور التي يعرفونها عياناً أنها النافعة وضدها الضار، وفي الأمور التي يعرفون أنها سيئات وضدها حسنات، ومع هذا أطاعوه في ذلك إلا من شاء الله منهم كما قال تعالى: {وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُ فَاتَّبَعُوهُ إِلَّا فَرِيقاً مِنَ الْمُؤْمِنِينَ} ٧.
فقد أوضح الشيخ بهذا البيان والربط البديع بين المعاني- بعد جمعها- أن اختلافها ليس اختلافاً متضاداً، وإنما هو من قبيل التعبير عن الشيء
_________
١ في المطبوعة. "يفعله" والتصحيح من المخطوطات.
٢ س سورة آل عمران: آية "١٨٨" وتكملتها: {وَيُحِبُّونَ أَنْ يُحْمَدُوا بِمَا لَمْ يَفْعَلُوا فَلا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِنَ الْعَذَابِ وَلَهُمْ عَذَابٌ أَلِيمٌ} .
٣ سورة البقرة: آية "٤٢".
٤ سورة البقرة: آية "١٠٢".
٥ تقدم تخريجهما ص "٧٤".
٦ تقدم تخريجهما ص "٧٤".
٧ سورة سبأ: آية "٢٠" وانظر مؤلفات الشيخ، القسم الرابع، التفسير ص "٨٧- ٨٩".',NULL,'80',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (89,'الواحد بألفاظ متقاربة بل مآلها عند التحقيق واحد. فهي متحدة في الغاية والمصب كما اتحدت في المأخذ والمشرب. ولعل الطبري رحمه الله قد شعر بهذا إذ قال بعد أن أورد أقوال السلف في معنى الآية:- وأولى هذه الأقوال عندي بالصواب قول من قال:- معناه: ثم لآتينهم من جميع وجوه الحق والباطل، فأصدهم عن الحق وأحسن لهم الباطل، وذلك أن ذلك عقيب قوله: {لَأَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيمَ} ١ فأخبر أنه يقعد لبني آدم على الطريق الذي أمرهم الله أن يسلكوه، وهو ما وصفنا من دين الله الحق، فيأتيهم في ذلك من كل وجوهه، من الوجه الذي، أمرهم الله به، فيصدهم عنه، وذلك من بين أيديهم وعن أيمانهم، ومن الوجه الذي نهاهم الله عنه فيزينه لهم، ويدعوهم إليه، وذلك من خلفهم وعن شمائلهم. وقيل: ولم يقل من فوقهم لأن رحمة الله تنزل على عباده من فوقهم٢.
وقد كان لجمع أقوال السلف فى تفسير هذه الآية أثر كبير في بيان المعنى وتحقيقه وقد نبه إلى أهمية هذا شيخ الإسلام ابن تيميه رحمه الله بقوله:- وجمع عبارات السلف في مثل هذا نافع جداً، فإن مجموع عباراتهم أدل على المقصود من عبارة أو عبارتين٣.
ولأمثل بمثال آخر يتأكد به ما ذكرت من سير الشيخ على هذا المنهج، وحرصه على متابعة السلف في أقوالهم وتتبع آثارهم، وإنزالها في مواضعها، وغاية الاستفادة منها، إذ يقول عند قوله تعالى: {مَنْ كَانَ يُرِيدُ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَالَهُمْ فِيهَا ... } الآيتين٤.
ما نصه: وقد ذكر عن السلف من أهل العلم فيها أنواع مما يفعل الناس اليوم ولا يعرفون معناه.
_________
١ سورة الأعراف: آية "١٦".
٢ ورد هذا عن ابن عباس كما رواه الطبري في تفسيره "١٣٧:٨" وانظر ما تقدم ص: "٧٦".
٣ مقدمه في أصول التفسير ص "٥٤" وقد ذكر كلاماً نفيساً في أوجه اختلاف السلف في التفسير فراجعه.
٤ سورة هود وتكملتهما: {فِيهَا وَهُمْ فِيهَا لا يُبْخَسُونَ أُولَئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الْآخِرَةِ إِلَّا النَّارُ وَحَبِطَ مَا صَنَعُوا فِيهَا وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ} "١٦".',NULL,'81',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (90,'الأول: من ذلك العمل الصالح الذي يفعل كثير من الناس ابتغاء وجه الله من صدقه وصلاة وإحسان إلى الناس ونحو ذلك، وكذلك شرك ظلم أو كلام في عرض ونحو ذلك مما يفعله الإنسان أو يتركه خالصاً لله لكنه لا يريد ثوابه في الآخرة، إنما يريدان يجازيه الله بحفظ ماله، وتنميته، وحفظ أهله وعياله، وإدامة النعمة عليهم، ونحو ذلك، ولا همة له في طلب الجنة ولا الهروب من النار فهذا يعطى ثواب عمله في الدنيا وليس له في الآخرة نصيب.
وهذا النوع ذكر عن ابن عباس في تفسير الآية١.
الثاني: وهو أكبر من الأول وأخوف وهو الذي ذكر مجاهد أن الآية نزلت فيه وهو أن يعمل أعمالاً صالحة ونيته رئاء الناس لا طلب ثواب الآخرة وهو يظهر أنه أراد وجه الله وإنما صلى أو صام أو تصدق أو طلب العلم لأجل أن الناس يمدحونه ويجل في أعينهم"فإن الجاه من أعظم أنواع الدنيا ولما ذكر معاوية حديث أبى هريرة في الثلاثة الذين هم أول من تسعر بهم النار وهم: الذي تعلم ليقال عالم حتى قيل، وتصدق ليقال جواد، وجاهد ليقال شجاع بكى معاوية بكاء شديداً ثم قرأ هذه الآية٢.
الثالث: أن يعمل الأعمال الصالحة ومقصده بها مالاً، مثل أن يحج لمال يأخذه لا لله، أو يهاجر لدنيا يصيبها أو امرأة ينكحها، أو يجاهد لأجل المغنم، فقد ذكر هذا النوع أيضاً في تفسير هذه الآية كما في الصحيح أن النبي صلى الله عليه وسلم قال: "تعس عبد الدينار وتعس عبد الدرهم تعس عبد الخميصة" ٣..إلخ.
_________
١ أخرجه عنه الطبري في تفسيره "١٢: ١١" من طريق العوفي وهو إسناد ضعيف جداً انظر كلام الشيخ أحمد شاكر في هامش تفسير الطبري "١: ٢٦١، ٢٦٢" بتحقيقه.
٢ انظر الجامع الصحيح للترمذي/ كتاب الزهد/ باب ما جاء في الرياء والسمعة "٤: ٥٩١" ح "٢٣٨٢" وتفسير الطبري "١٢: ١٣".
٣ أخرجه البخاري في صحيحه في مواضع منها/ كتاب الجهاد/ باب الحراسة في الغزو في سبيل الله، انظر الفتح "٦: ٩٥" ح "٢٨٨٦، ٢٨٨٧".',NULL,'82',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (91,'وكما يتعلم العلم لأجل مدرسة أهله أو مكسبهم أو رياستهم أو يقرأ القرآن ويواظب على الصلاة لأجل وظيفة المسجد كما هو واقع كثيراً، وهؤلاء اعقل من الذين قبلهم لأنهم عملوا لمصلحة يحصلونها، والذين قبلهم عملوا لأجل المدح والجلالة في أعين الناس ولا يحصل لهم طائل، والنوع الأول اعقل من هؤلاء لأنهم عملوا لله وحده لا شريك له لكن لم يطلبوا منه الخير العظيم وهو الجنة ولم يهربوا من الشر العظيم وهو العذاب في الآخرة١.
النوع الرابع: أن يعمل الإنسان بطاعة الله مخلصاً في ذلك لله وحده لا شريك له لكنه على عمل يكفره كفراً يخرجه عن الإسلام مثل اليهود والنصارى إذا عبدوا الله وتصدقوا أو صاموا ابتغاء وجه الله والدار الآخرة ومثل كثير من هذه الأمة الذين فيهم شرك أكبر أو كفر أكبر يخرجهم عن الإسلام بالكلية إذا أطاعوا الله طاعة خالصة يريدون بها ثواب الله في الدار الآخرة لكنهم على أعمال تخرجهم من الإسلام، وتمنع قبول أعمالهم، فهذا النوع أيضاً قد ذكر في الآية عن أنس بن مالك وغيره٢. وكان السلف يخافون منه كما قال بعضهم: لو أعلم أن الله تقبل مني سجدة واحدة لتمنيت الموت٣، لأن الله يقول: {إِنَّمَا يَتَقَبَّلُ اللَّهُ مِنَ الْمُتَّقِينَ} ٤ فهذا قصد وجه الله والدار الآخرة، لكن فيه من حب الدنيا والرياسة والمال ما حمله على ترك كثير من أمر الله ورسوله، أو أكثره، فصارت الدنيا أكبر قصده، فلذلك قيل قصد الدنيا. وصار ذلك القليل كأن لم يكن، كقوله صلى الله عليه وسلم: "صل فإنك لم تصل" ٥ والأول أطاع الله ابتغاء وجهه، لكن أراد من الله
_________
١ انظر تفسير الطبري "١٢: ١٢، ١٣".
٢ انظر تفسير الطبري "١٢:١٢".
٣ ذكر نحو ذلك عن ابن عمر "رض الله عنهما" انظر الدر المنثور "٣: ٥٧".
٤ سورة المائدة: آية "٢٧".
٥ هذا جزء من حديث المسيء صلاته، وقد أخرجه البخاري في صحيحه من مواضع "انظر البخاري مع الفتح/ كتاب الآذان باب وجوب القراءة للإمام والمأموم "٢: ٢٧٦" ح "٧٥٧".
ومسلم في صحيحه كتاب الصلاة/ باب وجوب قراءة الفاتحة في كل ركعة "٢٩٨:١" ح "٣٩٧".',NULL,'83',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (92,'الثواب في الدنيا، وخاف على الحظ والعيال، مثل ما يقول الفسقة. فصح أن يقال: قصد الدنيا، والثاني والثالث واضح١.
فتبين من خلال هذا أن اختلاف السلف هنا من قبيل اختلاف التنوع فكل منهم يصف نوعاً تشمله الآية. ومثل هذا هو ما أشار إليه شيخ الإسلام ابن تيمية بقوله بعد أن ذكر اختلافاً للسلف من هذا القبيل: فكل قول فيه ذكر نوع دخل في الآية، ذكر لتعريف المستمع بتناول الآية له. وتنبيهه به على نظيره، فإن التعريف بالمثال قد يسهل أكثر من التعريف بالحد المطابق.
والعقل السليم يتفطن للنوع كما يتفطن إذا أشير له إلى رغيف فقيل له: هذا هو الخبر٢.
فتبين بهذا هذا المنهج القويم وهذه النظرة الواعية لأقوال السلف وتفسيراتهم بفهمها والجمع بينها ما أمكن، والاستفادة من المعاني التي فسروا بها.
وبالله التوفيق.
_________
١ انظر مؤلفات الشيخ / القسم الرابع/ التفسير ص "١٢٠- ١٢٣".
٢ س مقدمة في أصول التفسير ص "٤٤".',NULL,'84',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (93,'<span data-type="title" id=toc-10>الباب الثاني: التفسير بالرأي أو بالمعلول</span>
<span data-type="title" id=toc-11>الفصل الأول: نظرته إلى العقل ومكانته من الشرع</span>
...
الباب الثاني: التفسير بالرأي أو بالمعلول
ويشتمل على خمسة فصول هي:
الفصل الأول: نظرته إلى العقل ومكانته من الشرع.
الفصل الثاني: نظرته إلى التفسير بالرأي.
الفصل الثالث: توفر أدوات التفسير بالرأي لدى الشيخ.
ويشتمل على ثلاثة مباحث:
المبحث الأول: معرفته باللغة ومدى اهتمامه بها في تفسيره.
المبحث الثاني: معرفته بالنحو والإعراب ومدى اهتمامه به في تفسيره.
المبحث الثالث: معرفته بالبلاغة ومدى اهتمامه بها في تفسيره.
الفصل الرابع: مظاهر التفسير بالرأي عند الشيخ.
ويشتمل على ثلاثة مباحث.
المبحث الأول: التفسير الافرادي.
المبحث الثاني: التفسير الإجمالي.
المبحث الثالث: الاستنباط المباشر من الآيات.
الفصل الخامس: السمات العامة لتفسيره بالرأي.
ويشتمل على مبحثين:
المبحث الأول: تمشيه مع روح الشريعة الإسلامية.
المبحث الثاني: تمشيه مع تفسيره بالسلف وأقوالهم.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (94,'الفصل الأول: نظرته إلى العقل ومكانته في الشرع
نص الشيخ مراراً على أهمية التفكر والتدبر امتثالاً لأمر الله عز وجل بذلك في كتابه في غير ما موضع، إذ من المعلوم أن الإسلام دين العقل والحكمة كما هو دين النقل، وقد حرر العقل من قيود كانت مضروبة عليه، ففك آساره وجعله مناط التكليف، وأنزله منزلته اللائقة به، فقرر به بعض دلائل التوحيد وجزئيات الشريعة، ووجهه الوجهة الصحيحة، قال تعالى: {كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ مُبَارَكٌ لِيَدَّبَّرُوا آيَاتِهِ وَلِيَتَذَكَّرَ أُولُو الْأَلْبَابِ} ١، وقال: {أَفَلا يَتَدَبَّرُونَ الْقُرْآنَ أَمْ عَلَى قُلُوبٍ أَقْفَالُهَا} ٢، وقال في غير ما آية: {لَعَلَّكُمْ تَعْقِلُونَ} ٣: {لِقَوْمٍ يَتَفَكَّرُونَ} ٤: {لِقَوْمٍ يَفْقَهُونَ} ٥ إلى غير ذلك مما به تتأكد أهمية استعمال العقل للنظر والتدبر والتأمل.
يقول الشيخ منبهاً على التفكر عند قول الله تعالى: {أَفَلا تَتَفَكَّرُونَ} ٦ حثه سبحانه على التفكر الذي هو باب العلم كما حث عليه سبحانه في غير موضع ٧.
ويقول مبيناً منزلة العقل، وتقرير الشرع به عند ذكر محاجة إبراهيم قومه كما أخبر الله عنه بذلك بقوله: {وَكَذَلِكَ نُرِي إِبْرَاهِيمَ مَلَكُوتَ السَّمَاوَاتِ وَالْأَرْضِ وَلِيَكُونَ مِنَ الْمُوقِنِينَ} الآيات ٨: إن ذلك من أعظم الأدلة على المسألة – أي مسألة التوحيد- ببديهة العقل لأن من رأى نخلاً كثيراً لا يتخالجه شك أن المدبر له ليس نخلة واحدة منه فكيف بملكوت السماوات والأرض٩؟!
_________
١ سورة ص آية "٢٩".
٢ سورة محمد آية"٢٤".
٣ سورة البقرة آية "٢٤٢" يوسف آية "٢" النور آية "٦١" الزخرف آية "٣".
٤ سورة يونس آية "٢٤" الرعد آية "٣" النحل الآيتان "١١، ٦٩" وغيرها.
٥ سورة الأنعام آية "٩٨".
٦ سورة الأنعام آية "٥٠".
٧ مؤلفات الشيخ/ القسم الرابع، التفسير ص "٥٧".
٨ سورة الأنعام الآيات "٧٥-٧٩".
٩ مؤلفات الشيخ/ القسم الرابع / التفسير ص "٦٤".',NULL,'86',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (95,'ويقول أيضاً عند ذكر مقالة نوح لقومه: {أَوَعَجِبْتُمْ أَنْ جَاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلَى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوا وَلَعَلَّكُمْ تُرْحَمُونَ} ١ تعريفهم أن هذا الذي استغربوا، ونسبوا من قاله إلى الجهالة والجنون، هو الواجب في العقل، وهو أيضاً حظهم ونصيبهم من الله لأنه سبب الرحمة ففي هذا الكلام من أوله إلى آخره من تحقيق الحق، وذكر أدلته العقلية على تحقيقه، وإبطال الباطل، وذكر الأدلة العقلية على بطلانه، ما لا يخفى على من له بصيرة٢.
وعند قوله تعالى: {وَأَنْزَلْنَا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ} ٣ يقول: ذكر الحكمة في إنزال القرآن على محمد وأنها لبيان المنزل ولتفكرهم ٤.
وعند قوله تعالى: {أَرَأَيْتَ الَّذِي يَنْهَى- عَبْداً إِذَا صَلَّى} ٥ الآيات يقول: تقرير الشرع بالعقل لقوله: {أَرَأَيْتَ} ٦.
وبهذا تتبين منزلة العقل من الشرع، وأهميته، وإيمان الشيخ بذلك وتركيزه عليه، واستنباطه له.
وفي المقابل، وضح الشيخ تمشياً مع القرآن أيضاً التحذير من زلل العقل، وتقديمه على النقل، وتجرده عنه، إذ أن في ذلك مهاوي الردي ومعاطب الهلاك، فقد جعل الله لكل شيء قدرا.
وكثير من الضلالات نتجت من جراء تجاوز العقل حدوده، وخروجه عن مجاله، ودلالة أسمه، إذ الشأن أن يعقل صاحبه لا أن يحمله على أن يجمح ويتعدى.
_________
١ سورة الأعراف آية "٦٣".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٠٢".
٣ سورة النحل آية: "٤٤".
٤ مؤلفات الشيخ / القسم الرابع/ التفسير ص "٢١٢".
٥ سورة العلق الآيات: "٩-١٤".
٦ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٧١".',NULL,'87',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (96,'فقال الشيخ عند قوله تعالى: {قَالَ مَا مَنَعَكَ أَلَّا تَسْجُدَ إِذْ أَمَرْتُكَ قَالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ طِينٍ} ١: تغليظ رد النص بالرأي٢.أ. هـ وذلك أن إبليس- عليه لعنة الله- اجتهد فيما لا مجال للاجتهاد فيه، فالمقام مقام امتثال وطاعة لأمر الله لا مقام اجتهاد.
ويقول مستنبطاً من قصة آدم وإبليس ونكوص إبليس عن السجود: ومنها أي من الفوائد المستنبطة –التحذير من معارضة القدر بالرأي لقوله: {أَرَأَيْتَكَ هَذَا الَّذِي كَرَّمْتَ عَلَيَّ} ٣ وهذه بلية عظيمة لا يتخلص منها إلا من عصمه الله لكل مقل ومكثر٤.
ومنها- وهي من أعظمها- تأدب المؤمن من معارضة أمر الله ورسوله بالرأي، كما استدل بها السلف على هذا الأمر، ولا يتخلص من هذا إلا من سبقت له من الله الحسنى٥.
ويقول عند قول الله تعالى: {وَاعْلَمُوا أَنَّ فِيكُمْ رَسُولَ اللَّهِ لَوْ يُطِيعُكُمْ فِي كَثِيرٍ مِنَ الْأَمْرِ لَعَنِتُّمْ} الآية٦: إن نتيجة هذا الدلالة على التمسك بالوحي والتحذير من الرأي المخالف ولو من أعلم الناس٧.
وغير ذلك من الاستنباطات في هذا المجال.
_________
١ سورة الأعراف: آية: "١٢".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٧٣".
٣ سورة الإسراء آية: "٦٢".
٤ مؤلفات، لشيخ/ القسم الرابع/ التفسير ص"٨٥".
٥ المرجع السابق بصحيفته.
٦ سورة الحجرات: آية "٧".
٧ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٥٣".',NULL,'88',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (97,'<span data-type="title" id=toc-12>الفصل الثاني: نظرته إلى التفسير بالرأي</span>
يرى الشيخ رحمه الله تحريم التفسير بالرأي ١.
كما يحث في الوقت نفسه على التفكر والتدبر في القرآن تبعاً لأمر الله بذلك كما تقدم٢ ومعلوم أن التدبر والتفكر قدر زائد عن مجرد النقل عن السابقين. فما وجه ذلك وما حقيقة رأي الشيخ في التفسير بالرأي؟
وجهه – والله أعلم – أن النظر والتدبر والتأمل ليس حجراً محجوراً على طائفة من الناس، وإنما هو عام للناس كلهم فمن حق كل قارئ للقرآن أن يتدبره ويتأمل معانيه، بل إنه مأمور بذلك.
ومعلوم أن من تفسير كلام الله ما تعرفه العرب من لغتها، ومنه ما لا يعذر أحد بجهالته، كما ورد ذلك عن حبر الأمة رضي الله عنه٣. ولهذا نجد أن عامة المسلمين يفهمون كثيراً من معانيه، وقد كان الرجل يدخل في الإسلام فيسمع آيات الله تتلى عليه فيتأثر بها، ويأتمر بأمرها، وينزجر عما تزجر عنه، والواقع شاهد بهذا حتى في زمننا الحاضر الذي عمت فيه العجمة وبعد فيه الناس عن اللغة العربية الفصحى.
ومن كلام الله ما لا يعلمه إلا العلماء، إذ يحتاج إلى علم ودراية لفهمه وشرحه واستنباط الأحكام منه.
ولعل مما يقرر هذا الأخير من كلام الشيخ، قوله ضمن المسائل التي لخصها من كلام شيخ الإسلام ابن تيمية رحمه الله: " ... وأما النظر في مسألة معينة لطلب حكمها والعبد لا يعرف ما يدله عليه فهذا النظر لا يفيد، بل قد يقع له تصديقات يحسبها حقاً، وهي من إلقاء الشيطان، وقد يقع له تصديقات من إلقاء الملك، وكذلك إذا كان النظر في دليل هادي وهو القرآن فقد يفهم مقصود الدليل فيهتدي، وقد لا يفهمه
_________
١ انظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٨٠"، وفضائل القرآن ص "٢٨".
٢ انظر الفصل السابق مباشرة.
٣ انظر ما تقدم ص "٥٢".',NULL,'89',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (98,'أو يحرفه عن مواضعه فيضل به، ويكون ذلك من إلقاء الشيطان، كما قال تعالى: {وَنُنَزِّلُ مِنَ الْقُرْآنِ مَا هُوَ شِفَاءٌ وَرَحْمَةٌ لِلْمُؤْمِنِينَ وَلا يَزِيدُ الظَّالِمِينَ إِلَّا خَسَاراً} ١ وقال: {يُضِلُّ بِهِ كَثِيراً وَيَهْدِي بِهِ كَثِيراً} ٢ وقال: {فَزَادَتْهُمْ رِجْساً إِلَى رِجْسِهِمْ} ٣ وقال: {وَالَّذِينَ لا يُؤْمِنُونَ فِي آذَانِهِمْ وَقْرٌ وَهُوَ عَلَيْهِمْ عَمىً} ٤ فالناظر في الدليل كالمترائي للهلال، قد يراه، وقد لا يراه لغشى في بصره. وكذلك عمى القلب ... " إلى آخر ما قال رحمه الله٥.
فإذا لم يكن لدى المرء أهلية للتفسير بل تجرأ وفسر القرآن برأيه المجرد فقد وقع في المذموم.. وهو ما عناه الشيخ بالتحريم حيث ترحم في كتاب فضائل القرآن بـ "باب وعيد من قال في القرآن برأيه وبما لا يعلم"٦ وقول الله تعالى: {قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ} إلى قوله: {وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لا تَعْلَمُونَ} ٧.
وعن ابن عباس أن رسول الله صلى الله عليه وسلم قال: "من قال في القرآن برأيه" وفي رواية: "من غير علم فليتبوأ مقعده من النار".. رواه الترمذي وحسنه٨.
وعن جندب قال: قال رسول الله صلى الله عليه وسلم: "من قال في القرآن برأيه فأصاب فقد أخطأ" رواه أبو داود والترمذي وقال:
_________
١ سورة الإسراء: آية "٨٢".
٢ سورة البقرة: آية "٢٦".
٣ سورة التوبة: آية "١٢٥".
٤ سورة فصلت: آية "٤٤".
٥ مؤلفات الشيخ/ ملحق المصنفات ص "١٩٦".
٦ مؤلفات الشيخ/ القسم الرابع/ كتاب فضائل القرآن ص "٢٨".
٧ سورة الأنفال: آية "٣٣".
٨ رواه الترمذي في جامعه في كتاب التفسير/ باب ما جاء في الذي يفسر القرآن برأيه "٥: ١٩٩" ح "٢٩٥٠، ٢٩٥١".',NULL,'90',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (99,'غريب١.
كما ترجم في كتاب الكبائر بـ "باب ما جاء في القول على الله بلا علم"٢ وقول الله تعالى: {قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ} الآية. قال أبو موسى: من علمه الله علماً فليعلمه الناس، وإياه أن يقول ما لا علم له به فيكون من المتكلفين، أو يمرق من الدين٣.
وفي الصحيح عن ابن عمرو رضي الله تعالى، عنهما مرفوعاً: "إن الله لا يقبض العلم انتزاعاً ينتزعه من قلوب الرجال، ولكن يقبض العلم بموت العلماء حتى إذا لم يبق أتخذ الناس رؤساء جهالاً فسئلوا فأفتوا بغير علم فضلوا وأضلوا" ٤.
وقال الشيخ عند قوله تعالى: {وَإِذَا فَعَلُوا فَاحِشَةً قَالُوا وَجَدْنَا عَلَيْهَا آبَاءَنَا وَاللَّهُ أَمَرَنَا بِهَا قُلْ إِنَّ اللَّهَ لا يَأْمُرُ بِالْفَحْشَاءِ أَتَقُولُونَ عَلَى اللَّهِ مَا لا تَعْلَمُونَ} ٥ إنكاره عليهم القول عليه بلا علم٦.
وقال عند قوله تعالى: {قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْأِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَأَنْ تُشْرِكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَاناً وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لا تَعْلَمُونَ} ٧: تحريم القول بلا علم٨.
_________
١ رواه أبو داود في سننه/ كتاب العلم/ باب الكلام في كتاب الله بغير علم "٣: ٣٢٠" ح "٣٦٥٢" والترمذي في جامعه في كتاب التفسير/ باب ما جاء في الذي يفسر القرآن برأيه "٥: ١٩٩" ح "٢٩٥٠، ٢٩٥١".
٢ مؤلفات الشيخ/ القسم الأول/ كتاب الكبائر ص "٢٨".
٣ لم أجده فيما وقعت عليه.
٤ رواه البخاري في صحيحه في مواضع منها/ كتاب العلم/ باب كيف يقبض العلم انظر الفتح "١: ٢٣٤" ح "١٠٠" ومسلم في صحيحه/ كتاب العلم/ باب رفع العلم وقبضه، وظهور الجهل والفتن في آخر الزمان "٤: ٢٠٥٨" ح "٢٦٧٣".
٥ سورة الأعراف: آية "٢٨".
٦ مؤلفات الشيخ/ القسم الرابع/ التفسير/ ص "٧٧".
٧ سورة الأعراف: آية "٣٣".
٨ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٨٠".',NULL,'91',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (100,'ولاشك أن أعظم القول على الله القول في تفسير كلامه.
ورأي الشيخ هذا في تحريم هذا النوع من التفسير بالرأي صرح به جمع من العلماء وهو مجمع عليه وممن صرح به الإمام الطبري "رحمه الله" حيث قال بعد أن ساق جملة من الأحاديث في تحريم التفسير بالرأي: "وهذه الأخبار شاهدة لنا على صحة ما قلنا من أن ما كان من تأويل آي القرآن الذي لا يدرك علمه إلا بنص بيان رسول الله صلى الله عليه وسلم، أو بنصبه الدلالة عليه، فغير جائز لأحد القيل فيه برأيه، بل القائل في ذلك برأيه وإن أصاب الحق فيه فمخطئ فيما كان من فعله بقيله فيه برأيه، لان إصابته ليست إصابة موقن أنه محق، وإنما هو إصابة خارص وظان ولقائل في دين الله بالظن قائل على الله ما لم يعلم، وقد حرم الله جل ثناؤه ذلك في كتابه على عباده فقال: {قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْأِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَأَنْ تُشْرِكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَاناً وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لا تَعْلَمُونَ} ، فالقائل في تأويل كتاب الله الذي لا يدرك علمه إلا ببيان رسول الله صلى الله عليه وسلم الذي جعل الله إليه بيانه قائل بما لا يعلم، وإن وافق قيله ذلك في تأويله ما أراد الله به من معناه، لأن القائل فيه بغير علم قائل على الله ما لا علم له به١".
وقد صرح بهذا أيضاً النووي وشيخ الإسلام ابن تيمية وغيرهما، قال النووي: "ويحرم تفسيره بغير علم، والكلام في معانيه لمن ليس من أهلها، والأحاديث في ذلك كثيرة، والإجماع منعقد عليه.
وأما تفسيره للعلماء فجائز حسن، والإجماع منعقد عليه" ٢.
وإذا كان من التفسير ما يحتاج إلى علم ودراية لفهمه فإن العلماء قد اشترطوا شروطاً للمفسر تؤهله للتفسير، كأن يكون ذا معرفة بعلوم الشريعة، وإلمام بوسائلها كاللغة العربية، وعلومها، من نحو وبلاغة
_________
١ تفسير الطبري ص "١: ٣٥".
٢ انظر التبيان في آداب حملة القرآن ص "٣٢" ومقدمة في أصول التفسير لشيخ الإسلام ابن تيمية ص "١٠٥".',NULL,'92',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (101,'ونحوها وذلك لفهم مناحي الكلام ومقاصده وقد توفر ذلك في الشيخ ولله الحمد حسبما دل عليه ما ظهر من خلال تفسيره كما سيأتي إن شاء الله تعالى.
إلا أن الشيخ رحمه الله قد نبه على أمر مهم وخطير، وهو أنه لا يشترط للنظر في كتاب الله والتدبر فيه وتفسيره بلوغ تلك الرتبة التي أسماها بعضهم "رتبة الاجتهاد" وشرطوا لبلوغها شروطا قد لا توجد في أجله العلماء، وزعموا أن من بلغها فله أن يجتهد وينظر في كتاب الله، ومن لم يبلغها فلا يسعه إلا التقليد.
وإنما يرى الشيخ أن على عموم المسلمين النظر والتأمل والدراسة لكتاب الله، ومن كان عنده من العلم ما يؤهله للتفسير والاستنباط فإن له ذلك وإن لم يبلغ تلك الرتبة.
وقد رد الشيخ مراراً على من يزعم أن التفسير وقف على المجتهدين من العلماء، كما ذهب إلى ذلك بعض الجهال من المتعصبين الجامدين الذين اعترفوا على أنفسهم بالقصور والجهل، وأرادوا أن يلزموا غيرهم بأن يسلك مسلكهم، حيث اشترطوا للتفسير شروطاً لعلها لا تتوفر في سادة المسلمين وأئمتهم كأبي بكر وعمر رضي الله عنهما وغيرها كما ألمحت قريباً.
فأبطل الشيخ هذه المقالة، وفند مزاعم أصحابها، وشبههم، بنصوص القرآن في كل مناسبة.
- ومن ذلك استنباطه من قوله تعالى: {وَكَيْفَ تَكْفُرُونَ وَأَنْتُمْ تُتْلَى عَلَيْكُمْ آيَاتُ اللَّهِ وَفِيكُمْ رَسُولُهُ} ١ الآية.: الرد على أعداء الله الذين زعموا أن القرآن لا يفهم معناه٢.
ووجه ذلك أن الله احتج عليهم بكون آياته تتلى عليهم ولا يحتج
_________
١ سورة آل عمران: آية "١٠١".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٤٩".',NULL,'93',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (102,'عليهم بما لا يفهمون.
وقوله عند قول الله تعالى: {قُلْ لا أَسْأَلُكُمْ عَلَيْهِ أَجْراً إِنْ هُوَ إِلَّا ذِكْرَى لِلْعَالَمِينَ} ١ قوله: {لِلْعَالَمِينَ} فيه تكذيب من قال لا يعرفه إلا المجتهد٢.
ووجه ذلك: أن العالمين كل من سوى الله تعالى.
وقوله عند قول الله تعالى: {قَالَ اهْبِطَا مِنْهَا جَمِيعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدىً} الآية٣:.... ومعلوم أن الهدى هو هذا القرآن، فمن زعم أن القرآن لا يقدر على الهدى منه إلا من بلغ رتبة الاجتهاد فقد كذب الله في خبره أنه هدى، فإنه على هذا القول الباطل لا يكون هدى إلا في حق الواحد من الآلاف المؤلفة، وأما أكثر الناس فليس هدى في حقهم، بل الهدى في حقهم أن كل فرقة تتبع مما وجدت عليه الآباء، فما أبطل هذا من قول! وكيف يصح لمن يدعي الإسلام أن يظن في الله وكتابه هذا الظن٤؟ ‍.
وقد بين الشيخ ستة أصول عظيمة مفيدة بينها الله تعالى بياناً واضحاً وجهلها الكثير من الناس، والأصل السادس رد به على أصحاب هذه المقالة فقال٥: رد الشبهة التي وضعها الشيطان في ترك القرآن والسنة، وإتباع الآراء والأهواء المتفرقة المختلفة، وهى أن القرآن والسنة لا يعرفهما إلا المجتهد المطلق، والمجتهد هو الموصوف بكذا وكذا، أوصاف لعلها لا توجد تامة في أبى بكر وعمر، فإن لم يكن الإنسان كذلك فليعرض عنهما حتما لا شك ولا إشكال فيه، ومن طلب الهدى منهما فهو إما زنديق، وإما مجنون لأجل صعوبة فهمهما، فسبحان الله وبحمده كم بين الله سبحانه شرعاً وقدراً وخلقاً وأمراً في رد هذه الشبهة الملعونة من وجوه شتى بلغت إلى حد
_________
١ سورة الأنعام: آية "٩٠".
٢ مؤلفات الشيح/ القسم الرابع/ التفسير ص "٦٨".
٣ سورة "طه": آية "١٢٣".
٤ مؤلفات الشيخ لم القسم الرابع/ التفسير ص "٢٦٥".
٥ مؤلفات الشيخ/ القسم الأول/ الرسالة الثانية عشرة "ستة أصول عظيمة" ص "٣٩٦"، "٣٩٧".',NULL,'94',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (103,'الضروريات العامة ولكن أكثر الناس لا يعلمون: {لَقَدْ حَقَّ الْقَوْلُ عَلَى أَكْثَرِهِمْ فَهُمْ لا يُؤْمِنُونَ إِنَّا جَعَلْنَا فِي أَعْنَاقِهِمْ أَغْلالاً فَهِيَ إِلَى الْأَذْقَانِ فَهُمْ مُقْمَحُونَ وَجَعَلْنَا مِنْ بَيْنِ أَيْدِيهِمْ سَدّاً وَمِنْ خَلْفِهِمْ سَدّاً فَأَغْشَيْنَاهُمْ فَهُمْ لا يُبْصِرُونَ وَسَوَاءٌ عَلَيْهِمْ أَأَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لا يُؤْمِنُونَ إِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمَنَ بِالْغَيْبِ فَبَشِّرْهُ بِمَغْفِرَةٍ وَأَجْرٍ كَرِيمٍ} ١.
_________
١ سورة "يس": الآيات "٧- ١١".',NULL,'95',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (104,'<span data-type="title" id=toc-13>الفصل الثالث: توفر أدوات التفسير بالرأي لدى الشيخ</span>
<span data-type="title" id=toc-14>مدخل</span>
...
الفصل الثالث: توفر أدوات التفسير بالرأي لدى الشيخ
تبين من خلال الفصل السابق انقسام التفسير إلى قسمين:
تفسير بالرأي المحمود، كالتفسير بالرأي المذموم.
وإذا نظرنا إلى تفسير الشيخ رحمه الله نجد أنه كثيراً ما يتعرض لتفسير كلام الله عز وجل بالرأي، ولكنه ليس بالرأي المجرد المذموم النابع أصلاً من التعصب والهوى والتجرؤ على كلام الله بغير علم.
وإنما هو حقيقة ما يفهم من كلام الله تعالى، ومتمش مع معاني الشريعة ومقاصدها، مع تحمل اللفظ لذلك المعنى، على ما سيأتي بيانه إن شاء الله تعالى١ وذلك أنه قد توفر في الشيخ الشروط اللازمة للتفسير إذ من المعلوم أن الشيخ رحمه الله قد تضلع من علوم الشريعة حيث طلب العلم وطالع كثيراً، ورحل من أجله، وأخذ عن أجله من العلماء في عصره، ودرس، وأفتى، وألف، في علوم شتى كالتفسير، والحديث والعقيدة، والفقه، والسيرة، والآداب الإسلامية.
فتدريسه التفسير، وتأليفه فيه- وكثير منه من قبيل التفسير بالرأي المحمود- دليل على أنه يعلم من نفسه أنه أهل لذلك، إذ لا يتصور أن يحث على طلب العلم، وينكر على من يقول بغير علم، تم بعد هذا يتكلم في أعظم شيء بلا علم.
ثم إن الدارس لتفسيره يلحظ اأنه قد ظهر من خلاله ما يدل على معرفته بعلوم أخرى اشترطوها للمفسر مع أنه لم يهدف إلى إظهار تلك العلوم لذاتها، وإنما لتحقيق غرض معين. ومنها: "معرفة اللغة والإعراب والبلاغة وغيرها فضلا عن أساس ذلك وملاكه وهو صحة المعتقد. وإليك بعض اللمحات الدالة على ذلك فيما يتعلق بعلوم العربية في مباحث وأما ما يتعلق بالتضلع في العقيدة ومعرفة الفقه وأصوله وعلم القصص فسأبرزها إن شاء الله في فصول خاصة من خلال تفسيره وسألمح أيضاً إلى علوم أخرى وردت في تفسيره وظهر اعتباره لها كورود بعض القراءات والإشارة للناسخ والمنسوخ وأسباب النزول في مواضعها إن شاء الله تعالى٢.
_________
١ انظر المبحث الأول من الفصل الخامس ض هذا الباب ص "١١٧".
٢ انظر ما يأتي ص "٢٥٦ وما بعدها".',NULL,'96',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (105,'<span data-type="title" id=toc-15>المبحث الأول: معرفته باللغة ومدى اهتمامه بها في تفسيره</span>.
لغة التأليف عند الشيخ لغة عربية فصيحة، ومن استعرض كتبه من تفسير وغيره مما أراد به التأليف تبين له ذلك، بل إنه ينص في تفسيره أحيانا على بعض المسائل أو المعاني اللغوية، إذا احتاج إليها، إذ ليس من منهجه الاهتمام بالتشقيقات اللغوية لذاتها، فليست هي الغرض من التفسير عنده، بل الغرض فهم كتاب الله، والاستنباط منه، والاهتداء بهديه، واللغة وسيلة لذلك، وتفسير الشيخ للقرآن، واستنباطه منه، وعمله بهداه دليل على فهمه ومعرفته، ولا يمكن ذلك إلا بمعرفة اللغة العربية الصحيحة، فأنى لمن لا يفهم العربية أن يفهم فضلا عن أن يستنبط! اللهم الآن ترجمت له المعاني.
ومن نص الشيخ على بعض المسائل اللغوية قوله عند قول الله تعالى: {وَلَمَّا بَلَغَ أَشُدَّهُ} الآية١: تقول العرب: بلغ أشده أي منتهى شبابه٢.
وقوله عند قول الله تعالى: {وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُونِي بِأَخٍ لَكُمْ مِنْ أَبِيكُمْ} الآية٣: يقال: "جهزت القوم" إذا هيأت لهم جهاز السفر.
والرحل: كل ما يعد للرحيل من وعاء للمتاع، ومركب للبعير، وحلس وغير ذلك٤.
وقوله عند قوله تعالى: {قَالُوا يَا أَبَانَا مَا نَبْغِي} ٥ الآية: قبل أي شيء نريد وقد ردت بضاعتنا: {وَنَمِيرُ أَهْلَنَا} أي نأتي لهم بالطعام، يقال: مار وأهله
_________
١ سورة سورة يوسف: آية "٢٢".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٣٥" وانظر قسم التحقيق ص "٣٠٦".
٣ سورة يوسف: آية "٥٩".
٤ مؤلفات الشيخ / القسم الرابع/ التفسير ص "١٥٩" وانظر قسم التحقيق ص "٣٥٨".
٥ سورة يوسف: آية "٦٥".',NULL,'97',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (106,'إذا أتاهم بطعام١.
قال: وقوله: {إِلَّا أَنْ يُحَاطَ بِكُمْ} ٢ أي يأتيكم أمر يهلككم كلكم٣.
ومن دلائل معرفته بلغة العرب تفسير المفردات على ما سيأتي بيانه في موضعه إن شاء الله تعالى٤.
ومن دلائلها أيضاً أنه ينص على الفروق بين الكلمات المتقاربة في المعنى التي يظن ترادفها، ولا يميز بينها إلا المتأمل في اللغة الخبير بها، إلا أنه وسيرا على منهجه في الاختصار يكتفي بالنص على وجود فرق ولا يبينه غالباً، وإنما يترك على القارئ مهمة البحث عنه ومن أمثلة ذلك:
قوله- الفرق بين الأمان والطمأنينة٥.
وقوله- الفرق بين العلم والحكم٦.
وقوله- الفرق بين التكذيب والتولي٧.
وقوله:- الفرق بين التكذيب والإباء٨.
وقوله:- الفرق بين العذاب المخزي والعذاب المقيم٩.
وقوله- الفرق بين المغفرة والرحمة١٠. وغير ذلك.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٦٤" وانظر قسم التحقيق ص "٣٦٠".
٢ سورة يوسف: آية "٦٦".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٦٤".
٤ انظر ما يأتي ص "١٠٩".
٥ مؤلفات الشيخ / القسم الرابع/ التفسير ص "٢٣١".
٦ المرجع السابق ص "٣٨٤".
٧ المرجع السابق ص "٢٩٨".
٨ المرجع السابق ص "٢٩٩".
٩ المرجع السابق ص "٣٣٢".
١٠ المرجع السابق ص "٣٣٦".',NULL,'98',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (107,'<span data-type="title" id=toc-16>المبحث الثاني: معرفته بالنحو والإعراب ومدى اهتمامه به في تفسيره</span>
الحال في النحو والإعراب في تفسير الشيخ كالحال في اللغة، إذ يلتزم في كتاباته العلمية وتأليفه ومنها التفسير بقواعد النحو، ويتجنب اللحن كيف وقد حفظ: ألفية ابن مالك في النحو١.
وأما بيان جزئيات النحو والإعراب فلا يهتم بالنص عليها غالباً لأن ذلك ليس من أغراض تفسيره كما تقدمت الإشارة إلى نحوه، ومع هذا فلم يخل تفسيره واستنباطاته من بعض اللمحات النحوية إذا دعت الحاجة إليها لبيان معنى أو فائدة من جراء تركيب نحوي معين ومن ذلك:
قوله مستنبطاً من قصة آدم وإبليس:- ومنها- أي من الفوائد- المستنبطة- وهي من أعظمها- معرفه الطرق التى يأتينا منها عدو الله كما ذكر الله تعالى عنه في القصة أنه قال: {لَأَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيمَ ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَانِهِمْ وَعَنْ شَمَائِلِهِمْ} ٢ وإنما تعرف عظمة هذه الفائدة بمعرفة شيء من معاني هذا الكلام.
قال جمهور المفسرين:- انتصب صراط بحذف على" التقدير: لأقعدن لهم على صراطك. قال ابن القيم: والظاهر أن الفعل "مضمن٣" فإن القاعد على الشيء ملازم له، فكأنه قال: لألزمنه ولأرصدنه ونحو ذلك٤.
فقد أورد هذا النقل المتعلق بالنحو لبيان حقيقة المعنى الذي به تتبين عظمة الفائدة كما أسلفت.
ومن ذلك أيضاً قوله عند تفسير قول الله تعالى: {قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقَالُوا إِنَّا سَمِعْنَا قُرْآناً عَجَباً} الآية٥ حيث قال:
وقوله: {عَجَباً} أي بليغاً في لفظه ومعناه "أنه استمع"- بالفتح- لأنه نائب فاعل "أوحي" و "إنا سمعنا"- بالكسر- أنه محكى بعد
_________
١ ذكر ذلك عنه حفيده الشيخ عبد الرحمن بن حسن كما في الدرر السنية "٩: ٢١٦".
٢ سورة الأعراف: آية "١٦".
٣ في المطبوعة "مضمر" والصواب ما أثبته كما هو في روضة الأفكار "المخطوط" بجامعة الإمام برقم "ف "٢٠٧٤" ورقة "٧٧/أ".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٨٧".
٥ سورة الجن: آية "١".',NULL,'99',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (108,'القول١.
ثم قال- والضمير في: {وَأَنَّهُمْ ظَنُّوا} للجن، والخطاب في: {ظَنَنْتُمْ} للإنس٢.
فنص على مرجع الضمائر ليتبين بها المعنى.
وقال عند قوله تعالى: {قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَداً} ٣ ومعنى الاستثناء قيل إنه من: {لَا أَمْلِكُ} أي: أملك إلا بلاغاً من الله،: {قُلْ إِنِّي لَنْ يُجِيرَنِي} جملة معترضة لتأكيد نفي الاستطاعة عن نفسه، على معنى أن الله إذا أراد به سوءا من مرض، أو موت، أو غيرهما، لم يصح آن يجيره منه أحد، أو يجد من دونه ملاذا يأوي إليه، والملتحد: الملتجأ.
وقيل: {بلاغاً} بدل من ملتحدا أي لن أجد من دون منجى إلا أن أبلغ ما أرسلني به٤.
وهذا البيان وإن كان اعتمد فيه على نواحٍ بلاغية إلا أن له علاقة بالنحو، ويظهر ذلك جليا في القول الثاني.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص ٣٥٥.
٢ المرجع السابق ص "٣٥٦".
٣ سورة الجن: آية "٢٢".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٦١".',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (109,'<span data-type="title" id=toc-17>المبحث الثالث: معرفته بالبلاغة ومدى اهتمامه بها في تفسيره</span>
من أظهر ما في تفسير الشيخ من النواحي المتعلقة بالعربية وعلومها ما يتعلق بفن البلاغة وضروبها، فقد استفاد الشيخ منها أيما استفادة بل نص على كثير من فنونها وأغراضها، وذلك لأنها تخدم كثيراً منهج الشيخ في إيضاح المعنى والاستنباط، كما أنه يظهر من خلال النص عليه في بيان عظمة أسلوب القرآن، ودقة ألفاظه وتراكيبه وبلاغته وبذلك يظهر للقارئ روعة هذا الكتاب وعظمته. وهو عرض من أغراض التفسير لدى الشيخ.
والناظر في تفسيره واستنباطاته يجد أنه يمتلك دقة في النظر والتأمل وتذوقاً للبلاغة، ويهتم من خلال ذلك ببيان بعض المعاني والنكت وتقريرها أولفت أنظار إليهم أو التحذير من بعض المخالفات وهو يسير في بيان اللمحات والأساليب البلاغية على منهجه العام في الاختصار.
فانظر إلى فهمه ودقته في الاستنباط عند قول الله تعالى لإبليس: {مَا مَنَعَكَ أَلَّا تَسْجُدَ إِذْ أَمَرْتُكَ} ١.
حيث قال تعظيم الفعل بقوله: {إِذْ أَمَرْتُكَ} ٢.
ووجه هذا التعظيم الذي أشار إليه أن المسألة أمرٌ وتكليف وليست إباحة أو ندباً، ثم إن الآمر هو الرب عز وجل، والمأمور به داخل في جمله العباد المأمورين، ففعله الذي هو النكوص عن السجود عظيم مع توفر هذه الدواعي للامتثال.
وقوله ضمن المسائل المستنبطة من قوله تعالى: {وَيَا آدَمُ اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلا مِنْ حَيْثُ شِئْتُمَا وَلا تَقْرَبَا هَذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ} ٣.
حيث قال تأكيد النهي٤.
_________
١ سورة الأعراف: آية "١٢".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٧٢".
٣ سورة الأعراف: آية "١٩".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٧٤".',NULL,'101',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (110,'ووجه هذا التأكيد- والله أعلم- أن الله تعالى نهاهما عن قربان الشجرة مبالغة في النهي عن الآكل منها، وفي هذا معنى تأكيد النهي عن الأكل.
كما أن في قوله: {فَتَكُونَا مِنَ الظَّالِمِينَ} ما يقوي هذا النهي أيضاً إذ يترتب على مخالفته كون المخالف ظالماً في فعله هذا.
- وقوله مستنبطاً من قول الله تعالى إخباراً عن مقالة يوسف عليه السلام....: {وَقَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَجَاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ أَنْ نَزَغَ الشَّيْطَانُ بَيْنِي وَبَيْنَ إِخْوَتِي} الآية١ حيث يقول: كرمه عليه السلام في قوله: {أَخْرَجَنِي مِنَ السِّجْنِ} ولم يقل: من الجب. وكرمه في قوله: {نَزَغَ} ولم يقل من بعدما ظلمونى٢.
وهذا تأمل عجيب، ودليل حس بلاغي، ونظر دقيق لدى الشيخ رحمه الله.
- وقوله عند قول الله تعالى: {نبِّئْ عِبَادِي أَنِّي أَنَا الْغَفُورُ الرَّحِيمُ. وَأَنَّ عَذَابِي هُوَ الْعَذَابُ الْأَلِيمُ} ٣ حيث يقول: ٤
إن المغفرة والرحمة وصف بها نفسه، وأما العذاب الأليم فوصف به عذابه. وقد اقتصر هاهنا على لفت الأنظار إلى هذه الدقيقة، ولم يعللها، ولعله أراد أن يتأمل الناظر ويبحث عن السبب، فإن ما يتوصل إليه بعد بحث وعناء تكون العناية به أشد.
وقد عُلِّل هذا الأسلوب في الآية بترجيح جانب الوعد على الوعيد، والرحمة على الغضب٥.
- وقوله مستنبطاً من قول الله تعالى في شأن قوم فرعون وطغيانهم: {فَلَمَّا
_________
١ سورة يوسف: آية "١٠٠".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٧٦".
٣ سورة الحجر: الآيتان "٤١، ٥٠".
٤ مؤلفات الشيخ/القسم الرابع/ التفسير ص "١٩٠" وانظر قسم التحقيق ص "٤١٣".
٥ انظر البحر المحيط لأبي حيان "٤٥٧:٥".',NULL,'102',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (111,'كَشَفْنَا عَنْهُمُ الرِّجْزَ إِلَى أَجَلٍ هُمْ بَالِغُوهُ إِذَا هُمْ يَنْكُثُونَ} ١ {فَانْتَقَمْنَا مِنْهُمْ ... } الآية٢. حيث قال: قوله: {فَانْتَقَمْنَا مِنْهُمْ} بالفاء.
ولم يذكر الغرض من ذلك ولعل ذلك لما سبق من إرادة تأمل القارئ وبحثه عن السبب، أو لظن عدم خفاء مثل ذلك على القارئ لشهرته.
والغرض من هذا- والله أعلم- أن الانتقام مترتب على طغيان قوم فرعون ومتسبب عن نكثهم، وذلك أن من أغراض العطف بالفاء السببية والتعقيب٣.
فظهر بما تقدم الحس البلاغي والنظر المتأمل عند الشيخ.
وقد وظف الشيخ البلاغة بشكل واضح لترسيخ بعض المعاني أو شرحها واستنباطها أو التحذير مما يخالفها.
فانظر إلى قوله- منبهاً على أعظم الأمور في حياة المسلم وهو التوحيد بدلالة أسلوب بلاغي بديع وهو الحصر بطريق النفي والإثبات الذي يذكره كثيراً حيث يقول: إن كلمة التوحيد "لا إله إلا الله" تتضمن النفي والإثبات، نفي الإلهية عما سوى الله سبحانه وتعالى، وإثباتها لله عز وجل٤.
ومما يستدل بها على هذا النفي والإثبات قوله تعالى: {وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ وَقَوْمِهِ إِنَّنِي بَرَاءٌ مِمَّا تَعْبُدُونَ} ٥ فهذا دليل النفي، ودليل الإثبات: {إِلَّا الَّذِي فَطَرَنِي} ٦.
_________
١ سورة الأعراف: الآيتان "١٣٥، ١٣٦".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٠٤".
٣ انظر مغني اللبيب لابن هشام "١٦١:١- ١٦٣".
٤ مؤلفات الشيخ/ القسم الخامس/ الرسائل الشخصية رقم "١٦" ص "١٠٥" "١٠٦"، ورقم "٣٥" ص "١٧٤" والقسم الأول/ العقيدة/ الثلاثة الأصول ص "١٩٠" وتلقين أصول العقيدة للعامة ص "٣٧١".
٥ سورة الزخرف: آية "٣٦".
٦ مؤلفات الشيخ/ القسم الأول/ العقيدة/ الثلاثة الأصول ص "١٩٠" وتلقين أصول العقيدة للعامة ص "٣٧١".',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (112,'ويقول أيضاً مقررا هذا المعنى عند قوله تعالى: {وَلَقَدْ بَعَثْنَا فِي كُلِّ أُمَّةٍ رَسُولاً أَنِ اعْبُدُوا اللَّهَ وَاجْتَنِبُوا الطَّاغُوتَ} ١: أنه لا بد مع الإثبات من النفي٢ كما يوضح هذا المعنى جليا فيقول في شرح كلمة التوحيد:
... فإن "لا" في قولك "لا إله إلا الله" هي النافية للجنس، تنفى جميع الآلهة و "إلا" حرف استثناء يفيد حصر جميع العبادة على الله عز وجل٣.
ويقول أيضاً مرسخا هذا المعنى من خلال هذا الأسلوب- أسلوب الحصر- ولكن بطريق تقديم ما حقه التأخير ما نصه: {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} فيها توحيد الألوهية وتوحيد الربوبية: {إِيَّاكَ نَعْبُدُ} فيها توحيد الألوهية: {وَإِيَّاكَ نَسْتَعِينُ} فيها توحيد الربوبية٤.
ويوضح هذا المعنى في موضع آخر بأبسط من هذا وبه يتبين جليا اعتباره لهذا المعنى البلاغي فيقول عند نفس الآية:
فالعبادة: كمال المحبة، وكمال الخضوع، والخوف والذل، قدم. المفعول وهواه {إِيَّاكَ} وكرر للاهتمام والحصر أي لا نعبد إلا إياك، ولا نتوكل إلا عليك، وهذا هو كمال الطاعة، والدين كله يرجع إلى هذين المعنيين، فالأول: التبرؤ من الشرك، والثاني: التبرؤ من الحول والقوة، فقوله: {إِيَّاكَ نَعْبُدُ} أي إياك نوحد، ومعناه أنك تعاهد ربك أن لا تشرك به في عبادته أحداً لا ملكاً ولا نبياً ولا غيرهما٥.
ويقول مرسخا هذا المعنى أيضاً بهذا الأسلوب- أسلوب الحصر- ولكن بطريق تقديم المسند إليه حيث يقول مستنبطاً من قوله تعالى: {هُوَ الَّذِي
_________
١ سورة النحل: آية: "٣٦".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٠٩" وقسم التحقيق ص "٢٧".
٣ مؤلفات الشيخ/ القسم الخامس/ الرسائل الشخصية رقم "١٦" ص "١٠٥، ١٠٦".
٤ مؤلفات الشيخ/ القسم الأول/ العقيدة والآداب الإسلامية/ بعض فوائد سورة الفاتحة.: ص "٣٨٣".
٥ مؤلفات الشيخ/القسم الربع/ التفسير ص "١٦".',NULL,'104',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (113,'أَنْزَلَ مِنَ السَّمَاءِ مَاءً لَكُمْ مِنْهُ شَرَابٌ وَمِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ} ١، فيها الاستدلال بإنزال المطر، وأن غيره لا يقدر عليه٢.
ونحوه أيضاً قوله عند قول الله تعالى: {وَهُوَ الَّذِي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْماً طَرِيّاً} الآية٣.
حيث يقول تسخير البحر، وأنه الذي فعله لا غيره ٤.
فقوله: أن الذي فعله لا غيره، هو تقرير لربوبية الله وتفرده بالتعرف والإنعام لم ومما يدل على التفرد هنا الحصر في الآيتين.
وقد نص الشيخ على طرق أخرى من أساليب الحصر مستفيداً منها كالحصر "بإنما" في قوله تعالى: {إِنَّمَا يَفْتَرِي الْكَذِبَ الَّذِينَ لا يُؤْمِنُونَ بِآياتِ اللَّهِ وَأُولَئِكَ هُمُ الْكَاذِبُونَ} ٥ حيث يقول: حصر الكذب فيمن لم يؤمن بآيات الله٦.
والحصر المستفاد من توسط الضمير كما في قوله تعالى: {مَنْ كَفَرَ بِاللَّهِ مِنْ بَعْدِ إِيمَانِهِ} الآيات إلى قوله: {وَأُولَئِكَ هُمُ الْغَافِلُونَ لا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْخَاسِرُونَ} ٧ حيث يقول:- ذكر حصر الغفلة فيهم.
- حصر الخسران في الآخرة فيهم٨.
ومن الأساليب التي نص الشيخ عليها مراراً مستفيداً من دلالتها الاستفهام بأنواعه من إنكار أو تقرير أو نحوه.
_________
١ سورة النحل: آية. "١٠".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٢٠٣".
٣ سورة النحل: آية "١٠".
٤ مؤلفات الشيخ/القسم الرابع/ التفسير ص"٣٠٣".
٥ سورة النحل: آية "١٠٥".
٦ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٢٩".
٧ سورة النحل: آية "١٠٦- ١٠٩".
٨ مؤلفات الشيخ/ القسم الرابع/ التفسير ص"٢٣٠".',NULL,'105',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (114,'ومن ذلك قوله: تقرير التوحيد بقوله: {أَفَأَنْتَ تُنْقِذُ مَنْ فِي النَّارِ} الآية١
وقوله: استفهاماً التقرير الدال على عظمه القصة في قوله تعالى: {وَهَلْ أَتَاكَ حَدِيثُ مُوسَى} ٢.
وقوله: استفهام الإنكار في هذا الأمر الباهر في قوله: {أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللَّهِ هُمْ يَكْفُرُونَ} ٣.
وقد يكتفي أحياناً بقوله: "استفهام التقرير" أو "استفهام الإنكار" مثلا٤. ومن توظيف البلاغة أيضاً لترسيخ بعض المعاني العقدية قوله مستنبطا من قول الله تعالى إخباراً عن قول يوسف "عليه السلام": {مَا كَانَ لَنَا أَنْ نُشْرِكَ بِاللَّهِ مِنْ شَيْءٍ} ٥.
حيث يقول: قوله: {مِنْ شَيْءٍ} عام كل ما سوى الله٦.
ووجه ذلك أن "شيء" نكرة في سياق النفي فتعم، "ومن" لتأكيد النفي فنبه من خلال الاستفادة من هذا الأسلوب، إلي تقرير التوحيد والنهي عن إشراك آي شيء مع الله تعالى.
هذا ولا أريد أن استقصي جميع ما نص عليه الشيخ، أو استفاد منه من أوجه البلاغة، وإنما الغرض هو الإشارة إلى معرفة الشيخ بهذا الفن، بل واهتمامه به في حدود ما يبين المعنى ويوضحه، أو يلفت الأنظار إليه مع السير على منهجه في الاختصار.
_________
١ المرجع السابق ص "٣٣٥" والآية في الزمر رقم "١٩".
٢ المرجع السابق ص "٣٩٥" والآية في طه رقم "٩".
٣ المرجع السابق ص "٣٣٠" والآية في النحل رقم "٧٣".
٤ انظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٩،٣٣٥،٣٣٦،٣٣٧،٣٣٠" وغيرها.
٥ سورة يوسف: آية. "٣٨".
٦ مؤلفات الشيخ/القسم الرابع/ التفسير ص "١٤٥" انظر قسم التحقيق ص "٣٢٩".',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (115,'وليس من غرض الشيخ بيان التشقيقات البلاغية وضروبها لذاتها، وللزهو بها، ولهذا لما ورده سؤال "ابن عفالق" ١ المتنطع عن أضرب.
البلاغة في "سورة العاديات"٢ لم يتكلف الجواب عليه، ولم أعلم ألف رد عليه في رسالته هذه، إذ لا يجب الجواب على المتنطعين الذين يقصدون من أسئلتهم إظهار ملكتهم أو إحراج المسئول أو نحو ذلك، إذ ليس هو بمسترشد فيرشد أو مستفيد فيفاد.
"وبالله التوفيق".
_________
١ هو محمد بن عبد الرحمن بن عفالق الأحسائي، من فقهاء الحنابلة، وكان من المناوئين لدعوة الشيخ محمد بن عبد الوهاب رحمه الله، وقد ألفا رسالة وجهها إلى الشيخ محمد وكان عنوانها "تهكم المقلدين في مدعى تجديد الدين" وضمنها أسئلة تهكميه تعجيزيه تحمل طابع التحدي والغرور.
وتوفي ابن عفالق عام "١١٦٤هـ" في الأحساء.
انظر السحب الوابلة ص "٣٨٢، ٣٨٣" ترجمة "٥٩٧" وعلماء، نجد "٣: ٨١٨" ترجمة"٢٨٨".
٢ انظر - إن شئت- طرفاً من هذه الأسئلة في كتاب دعاوى المناوئين لدعوة الشيخ محمد بن عبد الوهاب عرض ونقض لعبد العزيز بن محمد العبد اللطيف ص"٤٣" ذكرها نقلاً عن الرسالة الموسومة "بتهكم المقلدين".',NULL,'107',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (116,'<span data-type="title" id=toc-18>الفصل الرابع: مظاهر التفسير بالرأي عند الشيخ</span>
<span data-type="title" id=toc-19>مدخل</span>
...
الفصل الرابع: مظاهر التفسير بالرأي عند الشيخ
لم يسر الشيخ رحمه الله في تفسيره على نمط واحد من حيث الطريقة "الشكل" وإنما تنوعت أساليبه في ذلك، ولعل ذلك لتفاوت الزمن الذي ألف فيه تفسيره، ولاختلاف الأغراض الداعية لتفسير جزئية عن أخرى، فقد يُساَّل عن آية أو آيات فيفسرها بطريقة يراها مناسبة للسائل، وقد يختار آية أو آيات فيستنبط منها المسائل، وقد يستعرض سورة كاملة فيفسرها تفسيراً شاملاً ونحو ذلك.
وقد استوعب تفسيره ثلاثة مناهج في العرض، سأذكرها إن شاء الله في مباحث:-',NULL,'108',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (117,'<span data-type="title" id=toc-20>المبحث الأول: التفسير الإفرادي</span>
وأعني به تفسير المفردات تفصيلاً ببيان كلمة أو كلمات قرآنية بما يرادفها أو يقاربها أو يراد منها. وهذا غالباً فيما يرى أنه يحتاج إلى بيان وإيضاح، وهي طريقة من طرق المفسرين سلفاً وخلفاً. وتكثر أمثلة ذلك في بعض السور، وتقل في البعض الآخر.
ومما كثر فيه هذا النوع من التفسير تفسير سورة يوسف، فإنه كثيراً ما يبدأ بشرح مفرداتها ثم يردف باستنباط المسائل منها، ومن ذلك: قوله عند قول الله تعالى: {لَقَدْ كَانَ فِي يُوسُفَ وَإِخْوَتِهِ آيَاتٌ لِلسَّائِلِينَ. إِذْ قَالُوا لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَى أَبِينَا مِنَّا وَنَحْنُ عُصْبَةٌ إِنَّ أَبَانَا لَفِي ضَلالٍ مُبِينٍ} الآيات١.
{إِذْ قَالُوا لَيُوسُفُ وَأَخُوهُ} : شقيقه: {وَنَحْنُ عُصْبَةٌ} أي جماعة. وقوله: {لَفِي ضَلالٍ مُبِينٍ} آي في تقديمهما علينا. وقوله: {اطْرَحُوهُ أَرْضاً} أي ألقوه في أرض بعيدة....
وقوله: {فِي غَيَابَتِ الْجُبِّ} أي أسفله: {يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ} أي المارة من المسافرين: {إِنْ كُنْتُمْ فَاعِلِينَ} أي إن كنتم عازمين على ما تقولون٢.
وعند قوله تعالى: {وَجَاءُوا أَبَاهُمْ عِشَاءً يَبْكُونَ} ٣ الآيات. يقول: لما رجعوا إلى أبيهم باكين إظهاراً للحزن على يوسف، اعتذروا باستباقهم وهو الترامي، وقالوا: {إِنَّا ذَهَبْنَا نَسْتَبِقُ} .
وقوله: {عِنْدَ مَتَاعِنَا} أي ثيابنا وأمتعتنا.
وقوله: {وَمَا أَنْتَ بِمُؤْمِنٍ لَنَا} أي بمصدقنا: {وَلَوْ كُنَّا صَادِقِينَ} عندك، فكيف مع التهمة؟!
ثم قال: قوله: {سَوَّلَتْ} أي: زينت وسهلت، والصبر الجميل: الذي لا شكوى
_________
١ سورة يوسف: الآيات "٧- ١٠".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص"١٣٠" وانظر قسم التحقيق ص "٢٩٧".
٣ سورة يوسف: الآيات "١٦- ١٨".',NULL,'109',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (118,'معه وقوله: {تَصِفُونَ} أي تذكرون١.
ثم يقول عند قوله تعالى: {وَجَاءَتْ سَيَّارَةٌ فَأَرْسَلُوا وَارِدَهُمْ} الآيات٢:-
السيارة: الرفقة السائرون، والوارد: الذي يرد الماء ليستقي للقوم: {وَأَسَرُّوهُ بِضَاعَةً} : أي أظهروا أنهم أخذوه بضاعة من أهل الماء.
وقوله: {وَشَرَوْهُ بِثَمَنٍ بَخْسٍ} أي باعوه في مصر بثمن قليل٣.
ويقول في مواضع آخر من السورة استعصم امتنع وأبى٤.
والعجاف ضد السمان.
والملأ: كبار القوم ورؤساؤهم.
و {أَضْغَاثُ أَحْلامٍ} : أخلاط وأباطيل.
وادكر: تذكر شأن يوسف.
دأبا: متوالية
تحصنون: تخزنون٥.
التحسس: البحث والطلب.
وروح الله: رحمة الله٦.
{لا تَثْرِيبَ عَلَيْكُمُ} : لا تعيير عليكم٧.
لولا أن تفندون: والفند ذهاب العقل٨.
ويقول في سورة الحجرات عند قوله تعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ جَاءَكُمْ فَاسِقٌ بِنَبَأٍ فَتَبَيَّنُوا} الآية٩: معنى التبين وهو التثبت١٠.
_________
١ مؤلفات الشيخ /القسم الرابع /التفسير ص"١٣٣" وانظر قسم التحقيق ص"٣٠٣".
٢ سورة يوسف: الآيتان "١٩، ٢٠".
٣ مؤلفات الشيخ/القسم الرابع/التفسير ص"١٣٣،٣٤ا" وانظر قسم التحقيق ص"٣٠٤".
٤ المرجع السابق ص "١٤١" وقسم التحقيق ص "٣٢٠".
٥ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٥٢" وقسم التحقيق ص "٣٤٤".
٦ المرجع السابق ص "١٧١" وقسم التحقيق ص "٣٨١".
٧ المرجع السابق ص "١٧٣" وقسم التحقيق ص "٣٨٤".
٨ المرجع السابق ص "١٧٤" وقسم التحقيق ص "٣٨٧".
٩ سورة الحجرات: آية "٦".
١٠ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٥٢".',NULL,'110',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (119,'معنى العنت: الضيق١.
ويقول في مواضع من سورة الفلق:-
فمعنى أعوذ: أعتصم وألتجئ وأتحرز.
والفلق: هو بياض الصبح إذا انفلق من الليل.
والغاسق: الليل. {إِذَا وَقَبَ} أي أظلم ودخل في كل شيء٢.
...... وهكذا.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٥٣".
٢ المرجع السابق ص "٣٨٥، ٣٨٦"',NULL,'111',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (120,'<span data-type="title" id=toc-21>المبحث الثاني: التفسير الإجمالي</span>
وأعني به أن يبين معنى الآية ومقصدها إجمالا، دون آن ينص على معاني ألفاظها لفظة لفظة، وهذا منهج من مناهج التفسير، وقد كان أكثر ما يهتم به السلف من الصحابة وغيرهم أن يفهموا عن الله مراده، دون الاهتمام بالتشقيقات والتعريفات لكل كلمة، وأصلها، ومعناها الخاص، ولهذا وردعن أنس رضي الله عنه قال: قرأ عمر بن الخطاب رضي الله عنه: {عَبَسَ وَتَوَلَّى} فلما أتى على هذه الآية: {وَفَاكِهَةً وَأَبّاً} قال: قد عرفنا الفاكهة، فما الأب؟ قال: لعمرك يا ابن الخطاب إن هذا لهو التكلف١.
ولعله- والله أعلم- يعني بذلك أن المعنى العام وهو امتنان الله على عباده بالنعم هو المراد وقد حصل وكل واحد يعلم أن الأب نبات لقوله تعالى: {فَأَنْبَتْنَا فِيهَا حَبّاً. وَعِنَباً وَقَضْباً. وَزَيْتُوناً وَنَخْلاً. وَحَدَائِقَ غُلْباً. وَفَاكِهَةً وَأَبّاً. مَتَاعاً لَكُمْ وَلِأَنْعَامِكُمْ} ٢.
ومثل هذه الطريقة ينهجها الشيخ- غالباً- عندما يرى وضوح معاني المفردات، أو عندما يركز على جانب الدعوة والإصلاح من خلال الآيات. ومن أمثلة هذه الطريقة:
قوله عند قول الله تعالى: {قُلْ أَتُحَاجُّونَنَا فِي اللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ وَنَحْنُ لَهُ مُخْلِصُونَ} ٣: أمر الله لنا آن نحاجهم بهذه الحجة القاطعة:- فإذا كان الله رب الجميع، وأيضاً أنه بإقراركم أنه عدل لا يظلم بل كل عامل فعمله له، وافترقنا في كوننا قاصديه٤ مخلصين له الدين، وأنتم قصدتم غيره، فكيف يساوى بيننا وبينكم أو يخص بكرامته من أعرض عنه دون من قصده؟ هذا لا يدخل عقل عاقل٤.
_________
١ أخرجه الطبري في تفسيره "٣٠: ٥٩".
٢ سورة عبس الآيات "٢٧-٣٢" وانظر كلام ابن كثير رحمه الله في توجيه قول عمر "رضي لله عنه" في تفسيره "٣٤٨:٨".
٣ سورة البقرة: آية "١٣٩". في النسخ المخطوطة والمطبوعة "قاصدينه" وهو سهو.
٤ مؤلفات الشيخ/ القسم الرابع/التفسير ص"٤٠، ٤١" وقد فسرها بأبسط من هذا ص "٢٨، ٢٩".',NULL,'112',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (121,'- وقوله عند قول الله تعالى: {قَالَ اهْبِطَا مِنْهَا جَمِيعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدىً} ١ يقول تعالى: لما أجليتكم عن وطنكم فإن بعد هذا الكلام، وهو أني مرسل إليكم هدى من عندي، لا أكلكم إلى رأيكم، ولا رأي علمائكم، بل أنزل عليكم العلم الواضح الذي يبين الحق من الباطل والصحيح من الفاسد، والنافع من الضار، لئلا يكون للناس على الله حجة بعد الرسل، ومعلو أن الهدى هو هذا القرآن٢.
وقوله عند قول الله تعالى: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ} ٣ التنبيه على سبب الشرك وهو أن المشرك بان له شيء من جلالة الأنبياء والصالحين ولم يعرف الله سبحانه وتعالى، وإلا لو عرفه لكفاه وشفاه عن المخلوق، وهذا معنى قوله: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ} الآية٤.
وقوله عند قول الله تعالى: {وَأَنَّهُ لَمَّا قَامَ عَبْدُ اللَّهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَداً} ٥.
معناه: قام عبد الله يعبده كادوا يزدحمون عليه متراكمين، تعجباً مما رأوا من عبادته، وإعجاباً بما تلا من القرآن لأنهم رأوا منه ما لم يروا مثله، وعبادة عبد الله ليس بأمر مستبعد عن العقل، ولا مستنكر، حتى يكونوا عليه لبدا٦، ثم ذكر أقوالاً أخر في معنى الآية.
_________
١ سورة طه: آية "١٢٣".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٢٦٥".
٣ سورة الزمر: آية "٦٧".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٤٦".
٥ سورة الجن: آية "١٩".
٦ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٦٠".',NULL,'113',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (122,'<span data-type="title" id=toc-22>المبحث الثالث: الاستنباط المباشر من الآيات</span>
كثيرا ما يعمد الشيخ رحمه الله إلى الاستنباط المباشر من الآيات دون التعرض لتفسيرها قبل ذلك تفصيلاً أو إجمالاً.
ولاشك أنه يتبين من خلال استنباطه فهمه الآيات، ووجهها عنده، إذ الاستنباط ناتج عن ذلك الفهم فلا يتصور أن يستنبط من الآيات الاستنباطات الصحيحة من لم يفهمها.
وفهم الشيخ هذا يعتمد في كثير من الأحيان على النظر المتأمل في الآيات دون الاعتماد على أثر في معناها فهو يندرج حينئذ تحت التفسير بالرأي. وينحى الشيخ هذا المنحى غالبا فيما يرى وضوح معناه عموما، وإنما يحتاج إلى النص على ما يقرره من مسائل، وما ينطوي عليه من فوائد. وأمثله هذه الطريقة كثيرة جداً، إذ ينهج هذه الطريقة في أغلب تفسيره من ذلك:-
- استنباط من سورة الفاتحة حيث يقول:
{إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} : فيها التوحيد.
{اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ} فيها المتابعة١.
فهذه الاستنباطات المختصرة مبنية على فهم مسبق للآيات، ويتضح هذا الفهم من خلال تفسيره المبسوط لهذه الآيات فليراجع٢.
- وقوله مستنبطاً من أول سورة النور:- ٣
فيه مسائل:
الأولى: حد الزانية٤.
الثانية: النهي عن الرأفة ٥.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٨".
٢ المرجع السابق ص "١٦،١٧".
٣ المرجع السابق ص "٢٧٣، ٢٧٣".
٤ في قوله تعالى: {الزَّانِيَةُ وَالزَّانِي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍ} .
٥ في قوله تعالى: {وَلا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِي دِينِ اللَّهِ} .',NULL,'114',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (123,'الثالثة: قوله: {وَلْيَشْهَدْ عَذَابَهُمَا طَائِفَةٌ مِنَ الْمُؤْمِنِينَ} ١.
الرابعة: تحريم نكاح الزانية٢.
الخامسة: ما ذكر الله في رمي المحصنات ما لم يأتوا بالبينة٣.
السادسة: رد شهادتهم٤.
السابعة. كون الله استثنى التوبة والإصلاح٥.
الثامنة: ما ذكر الله في رمي الإنسان زوجته، وفيه من الأحكام أنها إذا لم تلاعن ترجم٦.
... إلى غير ذلك من الاستنباطات المتمشية مع ظاهر النص القرآني ومنها يتبين فهم الشيخ للآيات وإجراؤه إياها على ظاهرها والاكتفاء بالنص على ما فيها من أحكام.
ومن ذلك أيضاً قوله مستنبطاً من قول الله تعالى: {وَمَا يَأْتِيهِمْ مِنْ رَسُولٍ إِلَّا
_________
١ سورة النور: آية "٢".
٢ في قوله تعالى: {الزَّانِي لا يَنْكِحُ إِلَّا زَانِيَةً أَوْ مُشْرِكَةً وَالزَّانِيَةُ لا يَنْكِحُهَا إِلَّا زَانٍ أَوْ مُشْرِكٌ وَحُرِّمَ ذَلِكَ عَلَى الْمُؤْمِنِينَ} سورة النور آية "٣".
٣ في قوله تعالى: {وَالَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَدَاءَ فَاجْلِدُوهُمْ ثَمَانِينَ جَلْدَةً} .
٤ في قوله تعالى: {وَلا تَقْبَلُوا لَهُمْ شَهَادَةً أَبَداً وَأُولَئِكَ هُمُ الْفَاسِقُونَ} آية "٤".
٥ في قوله تعالى: {إِلَّا الَّذِينَ تَابُوا مِنْ بَعْدِ ذَلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ} آية "٥".
٦ في قوله تعالى: {وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ وَلَمْ يَكُنْ لَهُمْ شُهَدَاءُ إِلَّا أَنْفُسُهُمْ فَشَهَادَةُ أَحَدِهِمْ أَرْبَعُ شَهَادَاتٍ بِاللَّهِ إِنَّهُ لَمِنَ الصَّادِقِينَ. وَالْخَامِسَةُ أَنَّ لَعْنَتَ اللَّهِ عَلَيْهِ إِنْ كَانَ مِنَ الْكَاذِبِينَ. وَيَدْرَأُ عَنْهَا الْعَذَابَ أَنْ تَشْهَدَ أَرْبَعَ شَهَادَاتٍ بِاللَّهِ إِنَّهُ لَمِنَ الْكَاذِبِينَ. وَالْخَامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إِنْ كَانَ مِنَ الصَّادِقِينَ} الآيات "٦-٩".',NULL,'115',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (124,'كَانُوا بِهِ يَسْتَهْزِئُونَ كَذَلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ لا يُؤْمِنُونَ بِهِ وَقَدْ خَلَتْ سُنَّةُ الْأَوَّلِينَ} ١ الإيمان بالقدر٢.
وهذا مبين لوجه المعنى عند الشيخ، وأنه يرى عود الضمير في قوله: {نَسْلُكُهُ} إلى الاستهزاء المذكور والكفر، كما ذهب إليه جمع من السلف على ما سيأتي بيانه في موضع آخر إن شاء الله تعال٣.
والأمثلة على هذا القسم كثيرة والغرض هنا بيان هدا المنهج والاستشهاد له لا استقصاء كل ما يندرج تحته.
والله أعلم.
_________
١ سورة الحجر: الآيات "١١- ١٣".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٨٥".
٣ انظر ما يأتي ص "١٢٤" وقسم التحقيق ص "٤٠٥".',NULL,'116',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (125,'<span data-type="title" id=toc-23>الفصل الخامس: السمات العامة لتفسيره بالرأي</span>
<span data-type="title" id=toc-24>المبحث الأول: تمشيه مع روح الشريعة الإسلامية</span>
إن من أهم مميزات تفسير الشيخ بالرأي أنه يتمش مع روح الشريعة ومقاصدها، ويسير في إطار النصوص الشرعية، فهو إذا ليس تفسير اً مجرداً متحرراً، وإنما هو تفسير مقيد في معناه ومضمونه، ومحدود بحدود التمشي مع النصوص، قد أشرب معانيها، وقرر مقاصدها ومراميها. فقرر الشيخ في تفسيره كثيراً مما تقرر في الشرع من عقيدة، ومعاملات وآداب، ودروس، وعظات، وحذر مما حذر منه الشرع، من شر وفساد ورذيلة على وجه البسط تارة، والإيجاز تارة أخرى.
وما كان من هذا القبيل من التفسير هو ما يسميه الزركشي "بالتفسير بالمقتضى من معنى الكلام والمقتضب من قوة الشرع"١.
مع تمشيه مع دلالة اللفظ المفسر لغة على ما يقرره، من معان.
وشواهد ما ذكرته كثيرة ظاهرة، يتبين كثير منها من خلال ما سيأتي - إن شاء الله تعالى- من تركيزه على جانب العقيدة وترسيخها واهتمامه بما يتعلق بالاتجاه الإصلاحي وتحذيره من البدع كل ذلك من خلال التمشي مع النصوص٢.
ولأمثل هنا ببعض أمثلة القريبة التي تتضح من خلالها جلياً هذه السمة في تفسيره.
فمن ذلك-
قوله:- وأما البسملة فمعناها: أدخل في هذا الأمر من قراءة، أو دعاء/ أو غير ذلك "بسم الله" لا بحولي ولا بقوتي، بل أفعل هذا الأمر مستعيناً بالله، متبركاً باسمه تبارك وتعال٣ ...
فانظر إلى ما أشار إليه من خلال البسملة من الاستعانة بالله، والتبري من الحول والقوة، والتبرك باسم الله تعالى، مما هو من مسلمات العقيدة.
_________
١ البرهان في علوم القرآن للزركشي "٢: ١٦١".
٢ انظر ما يأتي من ص "١٢٦" إلى ص "٢١٣".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٩".',NULL,'117',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (126,'ومما يوضح هذه السمة أيضاً قوله ضمن المسائل المستنبطة من قوله تعالى: {وَاتَّبَعُوا مَا تَتْلُوا الشَّيَاطِينُ عَلَى مُلْكِ سُلَيْمَانَ ... } الآية١ حيث يقول:
- إن السحر نوعان.
- إن له تأثيرا، لقوله: {يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْجِهِ} .
الإرشاد إلى التوكل، بكونه لا يضر أحداً إلا بإذن الله٢.
فهذه المعاني التي استنبطها الشيخ كلها قد قررت في الشريعة، واعتقدها السلف.
حيث اعتقدوا أن السحر منه ما هو حقيقة، ومنه ما هو تخييل٣.
وأن له تأثيرا ولكن لا يكون إلا بإذن الله سبحانه وقدره ع وليس خارجاً عن قدره في ملكه، فمن توكل على الله كفاه ووقاه، ولهذا قال تعالى: {وَمَا هُمْ بِضَارِّينَ بِهِ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اللَّهِ} .
فانظر إلى تمشي الشيخ مع النصوص، وتقريره لمعانيها، وتسليمه لدلالتها والسلامة في التسليم لنصوص الشرع. ولله الحمد.
ومن تمشيه مع روح الشريعة ومقرراتها قوله:-
{كَمَا بَدَأَكُمْ تَعُودُونَ} ٤ أي لابد أن يخلقكم للبعث كما بدأ خلقكم من نطفة. ثم قال: {فَرِيقاً هَدَى وَفَرِيقاً حَقَّ عَلَيْهِمُ الضَّلالَةُ} ٥ فهذا القدر يهدي من يشاء، ويضل من يشاء، فجمع في هذه الآية الإيمان بالله، والإيمان باليوم الآخر، والإيمان بالشرع، والإيمان بالقدر.
وذكر فيها تفصيل الشرع الذي أمر به، وذكر حال من عكس الأمر فجعل المنكر معروفا، والمعروف منكراً، ثم ختم الآية بهذه المسألة
_________
١ سورة البقرة: آية "١٠٢".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٢٣".
٣ تيسير العزيز الحميد "٣٨٣".
٤ سورة الأعراف: آية "٢٩".
٥ سورة الأعراف: آية "٣٠".',NULL,'118',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (127,'العظيمة، وهى: {إِنَّهُمُ اتَّخَذُوا الشَّيَاطِينَ أَوْلِيَاءَ مِنْ دُونِ اللَّهِ وَيَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ} ١ فلا أجهل ممن هرب عن طاعة الله واختار طاعة الشيطان ومع هذا يحسب أنه مهتد مع هذا الضلال الذي لا ضلال فوقه٢.
ومن استضاءته بنور الشريعة أنه يذكر في الفروق الشرعية الدقيقة منبها عليها، ليميز بينها، فمن ذلك قوله عند قوله تعالى: {قُلْ يَا عِبَادِ الَّذِينَ آمَنُوا اتَّقُوا رَبَّكُمْ} ٣ الآية.
إن إضافة العباد إليه إضافة الخاصة لا العامة٤.
ويعني بالإضافة التي تكون معها الكفاية، كما ذكر في موضع آخر٥ وهى إضافة تكريم وتشريف، بخلاف الإضافة العامة التي تشمل البر والفاجر كما في قوله تعالى: {إِنْ كُلُّ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ إِلَّا آتِي الرَّحْمَنِ عَبْداً} ٦.
ومن ذلك أيضاً قوله عند قول الله تعالى: {أَمِ اتَّخَذُوا مِنْ دُونِ اللَّهِ شُفَعَاءَ قُلْ أَوَلَوْ كَانُوا لا يَمْلِكُونَ شَيْئاً وَلا يَعْقِلُونَ. قُلْ لِلَّهِ الشَّفَاعَةُ جَمِيعاً لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ثُمَّ إِلَيْهِ تُرْجَعُونَ} ٧ إن الشفاعة كلها له ومعرفة هذه بمعرفة صفة الشفاعتين٨. ويعني بالشفاعتين: الشفاعة المثبتة، وهي الشرعية، وتكون لمن أذن الله له ورضي عن المشفوع أن يشفع فيه.
والشفاعة المنفية، وهى خلاف ذلك وهي التي فيها شرك أولم تستوف شروطها٩.
"والله أعلم"
_________
١ سورة الأعراف: آية "٣٠".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٠٠".
٣ سورة الزمر: آية "١٠".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٢٣".
٥ انظر المرجع السابق ص "٣٣٠".
٦ سورة مريم آية: "٩٣" وانظر مؤلفات الشيخ/ القسم الرابع/التفسير ص "١٣٧" وقسم التحقيق ص "٣١٢".
٧ سورة الزمر: آية "٤٣، ٤٤".
٨ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٣٣".
٩ انظر مؤلفات الشيخ/القسم الأول/ العقيدة/ كتاب التوحيد ص "٥٢".',NULL,'119',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (128,'<span data-type="title" id=toc-25>المبحث الثاني: تمشيه مع تفسيرات السلف وأقوالهم</span>
يتمشى الشيخ- من واقع سلفيته- مع أقوال السلف وتفسير اتهم للآيات، ويسير على منهجهم. وقد أكثر "رحمه الله"- كما هو معلوم- من الإطلاع على كتبهم، وأقوالهم، حتى استقرت في ذهنه، وامتزجت بأسلوبه، فصار يقرر "رحمه الله" ما قرروه من معان، ناهجا نهجهم، وسالكاً سبيلهم فنجد في تفسيره أقوالا قد قالوها، أو فسروا بها أو بنحوها، أصبحت من معارفه ومسلماته، وجرى بها قلمه، وزخرت بها مؤلفاته.
فغالب اختياراته في التفسير تجد له فيها سلفاً إن لم يكن بنصها فبمعناها، وهو أثر من آثار إطلاعه على تفاسير السلف، وفهمه لمعاني القرآن من خلال النظر الصحيح، المستضيئ بما يثمر له إصابة المعنى، ومن أهمه معرفة أقوال السلف وآرائهم وتفسير اتهم التي اهتم بها، وحث على تعلمها، كما يظهر ذلك في غير موضع من كلامه.
فمما يدل على اطلاعه على أقوال المفسرين:-
- قوله في سورة الفاتحة: وذلك أن قوله: {مَالِكِ يَوْمِ الدِّينِ} وفى القراءة الأخرى "ملك يوم الدين" فمعناه عند جميع المفسرين كلهم ما فسره الله به في قوله: {وَمَا أَدْرَاكَ مَا يَوْمُ الدِّينِ. ثُمَّ مَا أَدْرَاكَ مَا يَوْمُ الدِّينِ. يَوْمَ لا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْئاً وَالْأَمْرُ يَوْمَئِذٍ لِلَّهِ} ١.
- وقوله:.... وأنا أذكر لكم آية من كتاب الله أجمع أهل العلم على تفسيرها، وأنها في المسلمين، وأن من فعل ذلك- أي الشرك- فهو كافر في أي زمان كان، قال تعالى: {مَنْ كَفَرَ بِاللَّهِ مِنْ بَعْدِ إِيمَانِهِ إِلَّا مَنْ أُكْرِهَ وَقَلْبُهُ مُطْمَئِنٌّ بِالْأِيمَانِ} إلى آخر الآية وفيها: {ذَلِكَ بِأَنَّهُمُ اسْتَحَبُّوا الْحَيَاةَ الدُّنْيَا عَلَى الْآخِرَةِ} ٢.
_________
١ سورة الانفطار: الآيات "١٧- ١٩" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٣".
٢ سورة النحل: الآيتان "١٠٦، ١٠٧" وانظر مؤلفات الشيخ/ القسم الخامس/ الرسائل الشخصية ص "٢٧٢".',NULL,'120',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (129,'وقوله منكراً طاعة الأئمة في مخالفة أمر الله تعالى بعد سياقه قول الله تعالى: {اتَّخَذُوا أَحْبَارَهُمْ وَرُهْبَانَهُمْ أَرْبَاباً مِنْ دُونِ اللَّهِ} ١ الآية. فقد فسرها رسول الله صلى الله عليه وسلم والأئمة بعده بهذا الذي تسمونه "الفقه" وهو الذي سماه الله شركا واتخاذهم أرباباً، لا أعلم بين المفسرين في ذلك اختلافاً٢.
ففي هذه الأقوال وغيرها دليل على ما ذكرت من اطلاعه على تفاسير السلف والمفسرين عموماً، وأنه لا يفسر القرآن بمعزل عن ذلك.
وكما أطلع على تفاسيرهم، وأستفاد منها، فقد حث على العناية بها، والاطلاع عليها، وفهمهما والاستفادة منها.
فأنظر إلى قوله بعد أن ذكر بعض العلماء وذكر منهم ابن جرير وابن قتيبة وأبا عبيد: فهؤلاء إليهم المرجع في كلام الله، وكلام رسوله، وكلام السلف، وإياك وتفاسير المحرفين للكلم عن مواضعه، وشروحهم، فإنها القاطعة عن الله وعن دينه٣.
وقوله بعد أن ذكر بعض الآيات: واطلب تفاسير هذه الآيات من كتب أهل العلم، وأعرف من نزلت فيه، وأعرف الأقوال والأفعال التي كانت سببا لنزول هذه الآيات٤.
وغير ذلك مما هو دال على ما ذكرت من عنايته بكلام السلف من المفسرين، ولا شك أنه قد ظهرت آثار هذه العناية في تفسيراته برأيه الذي استضاء فيه بهذه الآراء كما سيتضح ذلك في غير موضع من تفسيره.
ومن ذلك:
قوله ضمن المسائل المستنبطة من قوله تعالى: {وَالَّذِينَ اجْتَنَبُوا الطَّاغُوتَ أَنْ يَعْبُدُوهَا وَأَنَابُوا إِلَى اللَّهِ لَهُمُ الْبُشْرَى فَبَشِّرْ عِبَادِ الَّذِينَ يَسْتَمِعُونَ
_________
١ سورة التوبة: آية "٣١".
٢ مؤلفات الشيخ/ القسم الخامس/ الرسائل الشخصية/ ص "٢٧٧".
٣ مؤلفات الشيخ/ القسم الخامس/ الرسائل الشخصية/ ص "٢٥٩".
٤ المرجع السابق ص "٢٦١".',NULL,'121',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (130,'الْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُ أُولَئِكَ الَّذِينَ هَدَاهُمُ اللَّهُ وَأُولَئِكَ هُمْ أُولُو الْأَلْبَابِ} ١
التحذير من فتنة جدال منافق بالقرآن٢.
ووجه هذا الاستنباط أن الله أثنى على عباده الذين يستمعون القول فيتبعون أحسنه ولا يجادلون فيه فيضربون بعضه ببعض.
وقد ورد نحو هذا المعنى الذي ذكره الشيخ عن عمر رضي الله عنه حيث ورد عنه أنه قال: يهدم الإسلام زلة العالم، وجدال المنافق بالكتاب، وحكم اللائمة المضلي٣.
ومن ذلك قوله عند قول الله تعالى: {قَالَ اجْعَلْنِي عَلَى خَزَائِنِ الْأَرْضِ إِنِّي حَفِيظٌ عَلِيمٌ} ٤.
حيث قال: قوله: {إِنِّي حَفِيظٌ} أي أحفظ ما وليتني عليه: {عَلِيمٌ} بأمره وحسابه واستخراجه٥.
فتفسيره هذا موافق لما ورد عن السلف، بل كأنه مستقرأ من تفاسيرهم؛ فقد روى الطبري عن قتادة: أنه قال: {إِنِّي حَفِيظٌ عَلِيمٌ} يقول: حفيظ لما وليت عليه، عليم بأمره٦.
وروى عن ابن إسحاق أنه قال: {إِنِّي حَفِيظٌ عَلِيمٌ} ، أي حافظ لما استودعتني، عالم بما وليتني٧ ... ، ثم أورد الطبري قولا آخر عن الأشجعي٨ حيث قال: {إِنِّي
_________
١ سورة الزمر: الآيتان "١٧، ١٨".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٢٤".
٣ رواه الدارمي في سننه/المقدمة/باب في كراهة أخذ الرأي "١: ٧١".
٤ سوف يوسف: آية "٥٥".
٥ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٥٦".
٦ انظر تخريج الأقوال في موضعه من التحقيق ص"٣٥٤".
٧ انظر تخريج الأقوال في موضعه من التحقيق ص"٣٥٤".
٨ هو الإمام الحافظ، الثبت، عبيد الله بن عبيد الرحمن الأشجعي، الكوفي، قدم بغداد فلم يزل بها حتى توفي سنة "اثنتين وثمانين ومائة".
قال ابن حجر في التقريب: ثقة، مأمون، أثبت الناس كتابا في الثوري.
انظر تاريخ بغداد "١٠: ٣١١" وسير أعلام النبلاء "٥١٤:٨" وتهذيب التهذيب "٣٤:٧" وتقريب التهذيب "٣٧٣".',NULL,'122',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (131,'حَفِيظٌ عَلِيمٌ} حافظ للحساب، عليم بالألسن١.
ثم اختار القول الأول وهو قول قتادة وابن إسحاق.
بينما يظهر من كلام الشيخ استفادته من كلا القولين، وعدم قصره العلم على جانب دون جانب.
ومن ذلك أيضاً تعليله قول يعقوب عليه السلام: {يَا بَنِيَّ لا تَدْخُلُوا مِنْ بَابٍ وَاحِدٍ} الآية٢: أنه خاف عليهم من العين٣.
وهذا التعليل الذي ذكره وردعن ابن عباس ومجاهد وقتادة وجمهور المفسرين٤ ومن سيره على منهج السلف، أنه يبنى استنباطاته أحياناً على تفسيراتهم وأقوالهم فمن ذلك قوله مستنبطاً من قول الله تعالى: {فَوَرَبِّكَ لَنَسْأَلَنَّهُمْ أَجْمَعِينَ. عَمَّا كَانُوا يَعْمَلُونَ} ٥ معرفة أن لا إله إلا الله عمل٦.
ووجه ذلك أنه قد ورد عن بعض السلف كأنس ومجاهد أن المراد بقوله: {عَمَّا كَانُوا يَعْمَلُونَ} أي عن لا إله إلا الله٧.
فاستنبط الشيخ من ذلك أن لا اله إلا الله عمل، مشيراً بذلك إلى أن العقيدة كما هي اعتقاد وقول فهي عمل أيضاً.
ومن ذلك قوله ضمن المسائل المستنبطة من قوله تعالى: {كَذَلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ. لا يُؤْمِنُونَ بِهِ وَقَدْ خَلَتْ سُنَّةُ الْأَوَّلِينَ} ٨ الإيمان بالقدر٩.
_________
١ انظر تفسير الطبري "١٣: ٥".
٢ سورة يوسف: آية "٦٧".
٣ مؤلفات الشيخ/القسم الرابع/ التفسير ص "١٦٣".
٤ انظر تخريج الأقوال في موضعه من التحقيق ص "٣٦٣".
٥ سورة الحجر: الآيتان "٩٢، ٩٣".
٦ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٩٦".
٧ انظر تخريج الأقوال في موضعه من التحقيق ص "٤٢١".
٨ سورة الحجر: آية. "١٢".
٩ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٨٥".',NULL,'123',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (132,'ووجه ذلك أنه وردعن بعض السلف كابن جريح والحسن وابن زيد:- أن المراد كذلك نسلك الكفر والتكذيب في قلوب المجرمين١. وهذا هو القدر فنص الشيخ على هذا الركن من أركان الإيمان الذي دلت عليه الآية، وهو ما ذهب إليه من ذكر من السلف وغيرهم.
فتبين بما سقته من أمثله أن تفسير الشيخ واستنباطاته ليست بمعزل عن تفسيرات السلف واستنباطاتهم وإنما هو مقرر لها ومؤكد ودائر في إطارها. ولله الحمد والمنة.
_________
١ انظر تخريج الأقوال في موضعه من التحقيق ص "٤٠٥".',NULL,'124',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (133,'<span data-type="title" id=toc-26>الباب الثالث: أهم الأغراض التي تناولها الشيخ أو حققها من خلال تفسيره</span>
<span data-type="title" id=toc-27>الفصل الأول: التركيز على العقيدة من خلال تفسيره</span>
<span data-type="title" id=toc-28>المبحث الأول: اهتمامه بالعقيدة من خلال تفسيره بصفة عامة</span>
...
الباب الثالث: أهم الأغراض التي تناولها الشيخ أو حققها من خلال تفسيره
ويشتمل على ستة فصول:
الفصل الأول: التركيز على العقيدة من خلال تفسيره. ويشتمل على ثلاثة مباحث:-
المبحث الأول: اهتمامه بالعقيدة من خلال تفسيره بصفة عامة.
المبحث الثاني: أهم المباحث العقدية التي بينها من خلال تفسيره.
ويشتمل على مطلبين.
المطلب الأول: اهتمامه ببيان أنواع التوحيد من خلال تفسيره.
المطلب الثاني: اهتما عه بتقرير أركان الإيمان من خلال تفسيره.
المبحث الثالث: استنباطه جوانب حماية العقيدة من خلال تفسيره.
الفصل الثاني: اهتمام الشيخ في تفسيره بالردود على المخالفين.
الفصل الثالث: الاتجاه الإصلاحي في تفسيره.
الفصل الرابع: اهتمامه بالفقه والتأصيل في تفسيره.
ويشتمل على مبحثين:-
المبحث الأول:- اهتمامه بالفقه في تفسيره.
المبحث الثاني:- اهتمامه بالتأصيل في تفسيره.
ويشتمل على مطلبين:-
المطلب الأول:- اهتمامه بالتأصيل العام في تفسيره.
المطلب الثاني:-، اهتمامه بأصول الفقه في تفسيره.
الفصل الخامس: القصص في تفسير الشيخ ومدى اهتمامه به.
ويشتمل على أربعة مباحث:-
المبحث الأول: نظرته إلى القصص واهتمامه به.
المبحث الثاني: طريقته في دراسة القصص والاستنباط منها.
المبحث الثالث: المنهج الموضوعي في تفسير القصص عنده.
المبحث الرابع: موقفه من القصص الإسرائيلي.
الفصل السادس: ما ذكره الشيخ من علوم القرآن في تفسيره.
١- القراءات.
٢- فضائل القرآن.
٣- علم قصص القرآن.
٤- علم أسباب النزول.
٥- علم النسخ.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (134,'الفصل الأول: التركيز على العقيدة من خلال تفسيره.
المبحث الأول: اهتمامه بالعقيدة من خلال تفسيره بصفة عامة.
عقيدة الشيخ رحمه الله عقيدة سلفية، متمشية مع نصوص كتاب الله وسنة رسوله "صلى الله عليه وسلم".
وأهم الشروط لأي تفسير ليلاقي القبول، ويؤثر في القلوب التأثير المستقيم، المثمر للعمل الصالح، أن يكون مبنياً على أسس ثابتة قوية، يكون في مقدمتها النظرة العقدية الصحيحة لنصوص كتاب الله عز وجل، ووضعها في مواضعها، وإجراؤها في مجراها الصحيح.
وقد تميز تفسير الشيخ بهذه الميزة تميزاً واضحاً، على الرغم من منحى الاختصار الذي ينحاه الشيخ، ولذلك كان تفسيره من التفاسير الموثوق بها عند أهل الاعتقاد الصحيح، كبقية تفاسير السلف التي توافرت على السير على الأسس العقدية الصحيحة، المستمدة من كتاب الله وسنة رسوله "صلى الله عليه وسلم" وأسس قواعدها رسول الله "صلى الله عليه وسلم" على هدى وبصيرة من الله، كما قال تعالى: {قُلْ هَذِهِ سَبِيلِي أَدْعُو إِلَى اللَّهِ عَلَى بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي وَسُبْحَانَ اللَّهِ وَمَا أَنَا مِنَ الْمُشْرِكِينَ} ١.
وقد ركز الشيخ "رحمه الله" على العقيدة تركيزاً بالغاً، لأهميتها ومنزلتها العظيمة في الدين، ولما رأى من أحوال كثير من الناس الشركية والبدعية فكان لزاماً عليه، وقد صمم على القيام بمهمة الدعوة إلى الله على بصيرة أن يركز على هذا الأساس العظيم، الذي جهله فئام كثيرة من الناس وتجاهله آخرون فلم يعبؤوا به، ولم يرفعوا به رأساً، ولم يهتموا به، استكبارًا أو إعراضاً أو استهانة، أو غير ذلك: {وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ} ٢. ومن ألقى نظرة على مؤلفات الشيخ، أو فتاويه، أو رسائله ومكاتباته، يظهر له تركيز، على حذا الجانب جلياً واضحاً.
_________
١ سورة يوسف: آية "١٠٨".
٢ سورة الرعد: آية "٣٣" وسورة الزمر: آية. "٢٣".',NULL,'126',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (135,'وحيث إن أهم الأغراض التي وردت في كتاب الله تعالى بيان وحدانية الله عز وجل وتفرده، بالربوبية والألوهية والكمال المطلق في ذاته وأسمائه وصفاته، ترسيخاً للعقيدة، وحماية لجنابها، وذباً عنها، فلا تكاد تخلو سورة من سور الكتاب العزيز من مبحث عقدي أو إشارة عقدية، فقد اهتم الشيخ بهذا الجانب، فكان يركز في تفسيره واستنباطاته عليه، ويسعى من خلال ذلك لإيضاح العقيدة، وترسيخها، والذب عن حياضها وحماية جنابها، والتحذير مما يناقضها أو يقدح فيها شارحاً للآيات، ومستدلاً بها، ومستنبطاً منها.
وفي الحقيقة أنه لا يمكن للمنصف فصل العقيدة عن التفسير، إذ أن أساس معرفة تفصيلات العقيدة هو كتاب الله، والتفسير إيضاح لمعاني كلام الله عز وجل.
ومن هنا نجد آن الشيخ ربط بين العقيدة والتفسير كما هو المنهج الصحيح، ويتضح تمام هذا الربط جليا في كتابه المشهور "كتاب التوحيد الذي هو حق الله على العبيد"، ويجد ربي وأنا بصدد دراسة منهج الشيخ في الاهتمام بالعقيدة من خلال تفسيره آن أدلل على هذا الاتصال والربط بإلقاء نظرة سريعة على هذا الكتاب القيم، فإن عنوان هذا الكتاب دال على مضمونه، وهو أنه كتاب يبحث في إيضاح التوحيد وتحقيقه وتجريده.
ومع هذا ففي الكتاب ملامح تفسيرية واضحة أذكر منها ما يلي:
١- ترجمته بالآية القرآنية أو الآيات وجعلها عنوانا لما يندرج تحتها، ودليلا لما يراد إثباته في هذا الباب، ومثل هذا لا يكون إلا بعد فهم الآية المترجم بها، فإن تراجم الأبواب لها أهمية كبيرة، ولهذا اهتم العلماء بمثل هذه التراجم ومراد المترجم منها في هذا الموضع، وبهذه الصياغة سواء في هذا الكتاب أم في غيره من الكتب، وأفردوها بالمصنفات.
وقد ترجم الشيخ لاثني عشر بابا بآيات قرآنية، عدا ما أورده من آيات هي جزء من الترجمة ومن أمثلة ذلك:',NULL,'127',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (136,'قوله١: باب قول الله تعالى: {حَتَّى إِذَا فُزِّعَ عَنْ قُلُوبِهِمْ قَالُوا مَاذَا قَالَ رَبُّكُمْ قَالُوا الْحَقَّ وَهُوَ الْعَلِيُّ الْكَبِيرُ} ٢.
ويوضح الشيخ سليمان بن عبد الله بن محمد بن عبد الوهاب وجه إيراد المصنف لهذه الآية في هذا الموضع، وغرضه من ذلك- ويتضح منه مدى فهم الشيخ محمد لها- فيقول: أراد المصنف رحمه الله بهذه الترجمة بيان حال الملائكة الذين هم أقوى وأعظم من عبد من دون الله، فإذا كان هذا حالهم مع الله تعالى، وهيبتهم منه، وخشيتهم له، فكيف يدعوهم أحد من دون الله؟ وإذا كانوا لا يدعون مع الله تعالى، لا استقلالا ولا وساطة بالشفاعة فغيرهم ممن لا يقدر على شيء من الأموات والأصنام أولى أن لا يدعى ولا يعبد. ففي الرد على جميع فرق المشركين الذين يدعون مع الله من لا يداني الملائكة ولا يساويهم في صفة من صفاتهم ... إلى آخر ما قال "رحمه الله" ٣.
وفى هذا إيضاح تفسير لما يشاكل هذه الآية من الآيات المتعلقة بالدعاء. كم أن ما يورده الشيخ من آيات أخر، وأحاديث وغير ذلك في الباب دال على وجه فهمه لهذه الآية المترجم بها.
٢- إيراده لأكثر من آية في الباب الواحد. وهذه الآيات يبين بعضها بعضاً ويوضح معناه ووجهه فهو من قبيل تفسير القرآن بالقرآن كما أن هذا الإيراد أيضاً يمكن أن يلحق بالتفسير الموضوعي وذلك أنها آيات من مواضع متعددة في القرآن وقد جمعت لاشتراكها في إيضاح موضوع واحد وإن كان ذلك غير شامل لجميع الآيات في الموضوع أحياناً.
ومن أمثلة ذلك:-
قوله- باب الشفاعة٤. وقول الله عز وجل: {وَأَنْذِرْ بِهِ الَّذِينَ يَخَافُونَ
_________
١ مؤلفات الشيخ/القسم الأول/ العقيدة/ كتاب التوحيد باب "١٥" ص "٤٨".
٢ سورة سبأ: آية "٢٣".
٣ تيسير العزيز الحميد: ص "٢٦٣، ٣٦٤".
٤ مؤلفات الشيخ/ القسم الأول/ العقيدة كتاب التوحيد ص "٥١".',NULL,'128',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (137,'أَنْ يُحْشَرُوا إِلَى رَبِّهِمْ لَيْسَ لَهُمْ مِنْ دُونِهِ وَلِيٌّ وَلا شَفِيعٌ} ١.
وقوله: {قُلْ لِلَّهِ الشَّفَاعَةُ جَمِيعاً} ٢.
وقوله: {مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلَّا بِإِذْنِهِ} ٣.
وقوله: {وَكَمْ مِنْ مَلَكٍ فِي السَّمَاوَاتِ لا تُغْنِي شَفَاعَتُهُمْ شَيْئاً إِلَّا مِنْ بَعْدِ أَنْ يَأْذَنَ اللَّهُ لِمَنْ يَشَاءُ وَيَرْضَى} ٤.
وقوله: {قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِ اللَّهِ لا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِي السَّمَاوَاتِ وَلا فِي الْأَرْضِ} الآيتين٥.
فهذه الآيات قد تضافرت في بيان موضوع واحد وهو موضع الشفاعة فيتحمل من مجموعها المذهب الصحيح في الشفاعة نفياً وإثباتاً.
وقد أوضح ابن تيمية ذلك بقوله الذي أورده الشيخ تعقيباً على هذه الآيات فقال: قال أبو العباس: نفى الله عما سواه كل ما يتعلق به المشركون فنفى أن يكون لغيره ملك أو قسط منه، أو يكون عوناً لله، ولم يبق إلا الشفاعة، فبين أنها لا تنفع إلا لمن أذن له الرب كما قال: {لا يَشْفَعُونَ إِلَّا لِمَنِ ارْتَضَى} إلى آخره٦.
٣- إيراده الأحاديث المفسرة للآيات المتقدمة عليها، وهذا من قبيل تفسير القرآن بالسنة، وكثيرا ما يورد ما يكون سببا لنزول الآية، والعلم بالسبب يعين على فهم المسبب.
ومن أمثلة ذلك:
_________
١ سورة الأنعام: آية "٥١".
٢ سورة الزمر: آية "٤٤".
٣ سورة البقرة: آية "٢٥٥".
٤ سورة النجم: آية "٢٦".
٥ سورة سبأ: آية "٢٢، ٢٣".
٦ سورة الأنبياء: آية "٢٨".',NULL,'129',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (138,'قوله: باب ما جاء في الاستسقاء بالأنواء١.
وقول الله تعالى: {وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ} ٢.
وعن أبى مالك الأشعري "رضي الله عنه" أن رسول الله "صلى الله عليه وسلم" قال: "أربع في أمتي من أمر الجاهلية الفخر بالأحساب، والطعن في الأنساب والاستسقاء بالنجوم، والنياحة", وقال: "النائحة إذا لم تتب قبل موتها تقام يوم القيامة وعليها سربال من قطران، ودرع من جرب" رواه مسلم٣.
ولهما عن زيد بن خالد "رضي الله عنة" قال: صلى لنا رسول الله "صلى الله عليه وسلم" صلاة الصبح بالحديبية، على إثر سماء كانت من الليل، فلما انصرف أقبل على الناس فقال: "هل تدرون ماذا قال ربكم؟ ", قالوا: الله ورسوله أعلم. قال: قال: "أصبح من عبادي مؤمن بي، وكافر، فأما من قال: مطرنا بفضل الله ورحمته، فذلك مؤمن بي، وكافر بالكوكب، وأما من قال: مطرنا بنوء كذا وكذا فذلك كافر بي مؤمن بالكوكب" ٤.
ولهما من حديث ابن عباس معناه وفيه: قال بعضهم: لقد مطرنا بنوء كذا وكذا فأنزل الله هذه الآية {فَلا أُقْسِمُ بِمَوَاقِعِ النُّجُومِ} إلى قوله: {تُكَذِّبُونَ} ٥.أ. هـ.
فقد أوردهنا قول الله تعالى: {وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ} والمعنى: وتجعلون
_________
١ مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "٨٥".
٢ سورة الواقعة: آية "٨٢".
٣ رواه مسلم في صحيحه/كتاب الجنائز/باب التشديد في النياحة "٢: ٦٤٤"ح "٩٣٤".
٤ رواه البخاري في صحيحه/ في مواضع منها/ كتاب الاستسقاء، باب قول الله تعالى: {وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ} انظر الفتح "٢: ٦٠٦" ح "١٠٣٨" ومسلم في صحيحه/ كتاب الإيمان/ باب بيان كفر من قال مطرنا بنوء كذا وكذا "٨٣:١" ح "٧١".
٥ رواه مسلم في صحيحه/ كتاب الإيمان/ باب بيان كفر من قال مطرنا بنوء كذا وكذا "١:٨٤" ح "٧٣" وأخرجه في صحيح البخاري.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (139,'شكركم أنكم تكذبون١. ثم أورد سبب نزول هذه الآية، وهو معين على فهم شيء من ذلك التكذيب المقصود في الآية، وهو قول بعضهم: مطرنا بنوء كذا وكذا في مقابل هذه النعمة، فأتضح معنى الآية بإيراد الأحاديث المتعلقة بها تحتها.
٤- إيراده بعض أقوال السلف من الصحابة والتابعين وغيرهم، المبينة لمعنى في الآية، وهذا داخل ضمن التفسير بالمأثور.
فمن أمثله ذلك:
- أنه أورد في باب "قول الله تعالى": {وَمِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللَّهِ أَنْدَاداً يُحِبُّونَهُمْ كَحُبِّ اللَّهِ} ٢.
قول ابن عباس في قوله: {وَتَقَطَّعَتْ بِهِمُ الْأَسْبَابُ} ٣ قال: المودة٤.
- وأورد في باب "من الإيمان بالله الصبر على أقدار الله" ٥ وقول الله تعالي: {وَمَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ} ٦.
ثم قال: قال علقمة: هو الرجل تصيبه المصيبة، فيعلم أنها من عند الله فيرضى ويسلم ٧.
- وأورد في باب "قول الله تعالى: {يَعْرِفُونَ نِعْمَتَ اللَّهِ ثُمَّ يُنْكِرُونَهَا} الآية" ٨.
_________
١ ورد هذا التفسير في حديث مرفوع أخرجه الإمام أحمد في مسنده "١٠٨:١" والطبري في تفسيره "٢٠٨:٢٧" والترمذي في جامعه/ كتاب التفسير / باب "ومن سورة الواقعة" "٤٠١:٥" ح "٣٢٩٥"
٢ سورة البقرة: آية "١٦٥" وانظر مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "٨٨".
٣ سورة البقرة: آية "١٦٦".
٤ أخرجه الطبري في تفسيره "٧١:٢".
٥ مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "٩٦".
٦ سورة التغابن: آية "١١".
٧ أخرجه الطبري في تفسيره "١٢٣:٣٨".
٨ سورة النحل: الآية: "٨٣" وانظر مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "١٠٨".',NULL,'131',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (140,'قول مجاهد ما معناه: هو قول الرجل: هذا مالي ورثته عن آبائي١.
وقول عون بن عبد الله: لولا فلان لم يكن كذا٢.
ثم أردف بقول غير هؤلاء فقال وقال ابن قتيبة: يقولون: هذا بشفاعة آلهتنا٣.
وقال أبو العباس: بعد حديث زيد بن خالد الذي فيه وإن الله تعالى قال: "أصبح من عبادي مؤمن بي وكافر" الحديث- وقد تقدم٤- وهذا كثير في الكتاب والسنة يذم سبحانه من يضيف إنعامه إلى غيره ويشرك به. وقال بعض السلف: هو كقولهم: كانت الريح طيبة، والملاح حاذقا، ونحو ذلك مما هو جار على ألسنة كثيرة.
فقد أورد هنا بعض النصوص المفسرة للآية. من أقوال السلف.
٥- النص ضمن مسائل الباب الواحد على تفسير الآيات المذكورة. فإن كان تفسيرها قد أتضح فبها ونعمت، وإلا فيرجع إلى تفسيرها الصحيح لأنه معين على فهمها، واستنباط الشاهد للباب منها.
وأمثلته كثيرة فكل باب ترجم له بآية نص في المسائل المستنبطة على التفسير بقوله: فيه مسائل: الأولى: تفسير آية كذا وكذا أو نحو ذلك.
ومن أمثلته:
- قوله "باب من الشرك إرادة الإنسان عمله الدنيا" ٥.
وقول الله تعالى: {مَنْ كَانَ يُرِيدُ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَالَهُمْ فِيهَا} الآيتين٦.
ثم أورد حديث أبي هريرة المرفوع: "تعس عبد الدينار، تعس عبد الدرهم" الحديث٧.
_________
١ تفسير الطبري "١٤: ١٥٨".
٢ تفسير الطبري "١٤: ١٥٨".
٣ انظر غريب القرآن لابن قتيبة ص "٢٤٨".
٤ انظر ما تقدم ص "١٣٠".
٥ مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "١٠٠".
٦ سورة هود: آية "١٥، ١٦".
٧ تقدم تخريجه ص "٨٢".',NULL,'132',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (141,'ثم قال: فيه مسائل: الأولى: إرادة الإنسان الدنيا بعمل الآخرة.
الثانية: تفسير آية هود.
٦- الاستنباط من الآيات وهو داخل ضمن منهجه الاستنباطي من القرآن الكريم ومن أمثله ذلك قوله: "باب من الشرك آن يستغيث بغير الله أو يدعو غيره" ١ وقول الله: {وَلا تَدْعُ مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُكَ وَلا يَضُرُّكَ فَإِنْ فَعَلْتَ فَإِنَّكَ إِذاً مِنَ الظَّالِمِينَ. وَإِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلا كَاشِفَ لَهُ إِلَّا هُوَ} الآية٢.
وقوله: {فَابْتَغُوا عِنْدَ اللَّهِ الرِّزْقَ وَاعْبُدُوهُ} الآية٣.
وقوله: {وَمَنْ أَضَلُّ مِمَّنْ يَدْعُو مِنْ دُونِ اللَّهِ مَنْ لا يَسْتَجِيبُ لَهُ إِلَى يَوْمِ الْقِيَامَةِ} الآيتين٤.
وقوله: {أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذَا دَعَاهُ وَيَكْشِفُ السُّوءَ} ٥.
وروى الطبراني بإسناده أنه كان في زمن النبي "صلى الله عليه وسلم" منافق يؤذى المؤمنين فقال بعضهم: قوموا بنا نستغيث برسول الله "صلى الله عليه وسلم" من هذا المنافق، فقال النبي "صلى الله عليه وسلم": "أنه لا يستغاث بي وإنما يستغاث بالله" ٦.
فيه مسائل:
الأولى: أن عطف الدعاء على الاستقامة من عطف العام على الخاص.
الثانية: تفسير قوله: {وَلا تَدْعُ مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُكَ وَلا يَضُرُّكَ} .
الثالثة: أن هذا هو الشرك الأكبر.
الرابعة: أن أصلح الناس لو يفعله إرضاء لغيره صار من الظالمين.
الخامسة: تفسير الآية التي بعدها.
_________
١ مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "٤٢".
٢ سورة يونس: آية "١٠٧،١٠٦".
٣ سورة العنكبوت: آية "١٧".
٤ سورة الأحقاف: آية "٦،٥".
٥ سورة النمل: آية "٦٢".
٦ ذكره الهيثمي في مجمع الزوائد "١٥٩:١٠".',NULL,'133',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (142,'السادسة: كون ذلك لا ينفع في الدنيا مع كونه كفرا.
السابعة: تفسير الآية الثالثة.
الثامنة: أن طلب الرزق لا ينبغي إلا من الله، كما أن الجنة لا تطلب إلا منه.
التاسعة: تفسير الآية الرابعة.
العاشرة: أنه لا أضل ممن دعا غير الله.
الحادية عشرة: أنه غافل عن دعاء الداعي لا يدري عنه.
الثانية عشرة: أن تلك الدعوة سبب لبغض المدعو للداعي، وعداوته له.
الثالثة عشرة: تسمية تلك الدعوة عباده للمدعو.
الرابعة عشرة: كفر المدعو بتلك العبادة ... وهكذا.
وبهذا يتضح ما أردت إيضاحه من الملامح التفسيرية في هذا الكتاب العظيم. هذا وبعد إيضاح الملامح التفسيرية في كتاب التوحيد، والتي يتضح منها تمام ربط الشيخ بين العقيدة والتفسير أعود فأقول:
إن تركيز الشيخ على العقيدة في تفسيره يتجلى في اختياره السور والآيات ذات الملامح العقدية الهامة، حتى تكون أساسا لفهم ما يشاكلها في القرآن كله مما يتعلق بموضوع نفسه.
فمن السور التي قصرها "سورة الفاتحة" حيث توسع في تفسيرها أكثر من غيرها، وبرز فيها واضحاً تركيزه على العقيدة، وترسيخها، وحماية جنابها من خلال الفهم الصحيح، لآيات هذه السورة العظيمة، وهي سورة لا يخفى شأنها وأهميتها ويكفي أنها "أم الكتاب".
ومن تلك السور سورة "النحل" وهى مليئة بالمباحث العقدية.
فمنها ما يتعلق بالإشارة إلى اليوم الآخر وتنزيه الله عز وجل نفسه، وتعاليه عن الشرك: {أَتَى أَمْرُ اللَّهِ فَلا تَسْتَعْجِلُوهُ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ} ١.
وفيها ذكر تنزيله تعالى الملائكة بالأمر بوحدانية الله تعالى وتقواه {يُنَزِّلُ
_________
١ سورة النحل: آية "١".',NULL,'134',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (143,'الْمَلائِكَةَ بِالرُّوحِ مِنْ أَمْرِهِ عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ أَنْ أَنْذِرُوا أَنَّهُ لا إِلَهَ إِلَّا أَنَا فَاتَّقُونِ} ١.
كما أن السورة تعرضت لذكر ربوبية الله عز وجل، والآيات الدالة عليه، كخلقه السماوات والأرض، وخلقه الإنسان من نطفه فإذا هو خصيم مبين، وما خلقه عز وجل لعباده من النعم في البر والبحر {وَالْأَنْعَامَ خَلَقَهَا لَكُمْ فِيهَا دِفْءٌ وَمَنَافِعُ وَمِنْهَا تَأْكُلُونَ. وَلَكُمْ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسْرَحُونَ. وَتَحْمِلُ أَثْقَالَكُمْ إِلَى بَلَدٍ لَمْ تَكُونُوا بَالِغِيهِ إِلَّا بِشِقِّ الْأَنْفُسِ إِنَّ رَبَّكُمْ لَرَؤُوفٌ رَحِيمٌ. وَالْخَيْلَ وَالْبِغَالَ وَالْحَمِيرَ لِتَرْكَبُوهَا وَزِينَةً وَيَخْلُقُ مَا لا تَعْلَمُونَ} ٢. كما تعرضت السورة لبعض مباحث الرسالة: {وَلَقَدْ بَعَثْنَا فِي كُلِّ أُمَّةٍ رَسُولاً أَنِ اعْبُدُوا اللَّهَ وَاجْتَنِبُوا الطَّاغُوتَ} ٣. الآية.
إلى غير ذلك من المباحث التي نص الشيخ على كل جزئيه منها في استنباطاته ولست هنا بصدد ذكر تلك الاستنباطات، وإنما أكتفي بالإشارة إلى كثرة المباحث العقدية في هذه السورة المختارة.
ومن تلك السور المختارة سورة "الزمر" و"الجن" والمدثر" والإخلاص" والمعوذتين" وفي كل سورة من هذه السور تعلق وثيق بالعقيدة وشمول لمعظم المباحث العقدية. فلعله لهذا اختارها الشيخ للتفسير ليفسر ما يشاكلها من السور والآيات ذات الموضوع نفسه على ضوئها.
كما أن تركيز الشيخ على العقيدة في تفسيره كما يتجلى في اختيار السور المفسرة فكذلك الحال بالنسبة للآيات المفسرة، بل قد يكون ذلك أوضح.
فمن الآيات المختارة للتفسير قوله تعالى: {مَا كَانَ لِبَشَرٍ أَنْ يُؤْتِيَهُ اللَّهُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِبَاداً لِي مِنْ دُونِ اللَّهِ} الآيتين٤.
فقد أوضح الشيخ رحمه الله أهمية هذه الآيات، واتصالها المباشر بالعقيدة
_________
١ سورة النحل: آية "٢".
٢ سورة النحل: الآيات "٥- ٨".
٣ سوره النحل: آية "٣٦".
٤ سورة آل عمران: آية "٧٩، ٨٠".',NULL,'135',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (144,'بما فيها من تقرير الإخلاص لله، والبراءة من الشرك، وما فيها من بيان حال الدعاة المصلحين، من الأنبياء وأتباعهم مع أقوامهم، فقال رحمه الله بعد ذكر الآيتين السابقتين:- إذا عرفت أن سبب نزولها قول أهل الكتاب "نحن مسلمون نعبد الله إلا إن كنت تريد أن نعبدك"١ عرفت أنها من أوضح ما في القرآن من تقرير الإخلاص والبراءة من الشرك، ومن أعظم ما يبين لك طريق الأئمة المهديين من الأئمة المضلين، وذلك أن الله وصف أئمة الهدى بالنفي والإثبات، فنفى عنهم أن يأمروا أتباعهم بالشرك بهم، أو بالشرك بالملائكة والأنبياء وهم أصلح المخلوقات، وأثبت أنهم يأمرون أتباعهم أن يصيروا ربانيين، فإذا كان من أنزله الله بهذه المنزلة لا يتصور أن يأمر أتباعه بالشرك به ولا بغيره من الأنبياء والملائكة فغيرهم أظهر وأظهر.
وإذا كان الذي يأمرهم به كونهم ربانيين، تبين طريقة الأنبياء وأتباعهم من طريقه أئمة الضلال وأتباعهم.
ومعرفة الإخلاص والشرك، ومعرفة أئمة الهدى وأئمة الضلال أضل ما حصل المؤمن٢.
إلى آخر ما يتعلق بهذه الآية من استنباطات معظمها استنباطات عقدية هامة، نابعة من فهم صحيح للآيات المختارة.
ثم بعد الآيتين السابقتين تعرض لتفسير آيتين تاليتين لهما وهما قوله تعالى: {وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ النَّبِيِّينَ لَمَا آتَيْتُكُمْ مِنْ كِتَابٍ وَحِكْمَةٍ} الآيتين٣.
وفيهما تقرير نبوة محمد "صلى الله عليه وسلم" والبشارة به في الكتب السابقة.
يوضح الشيخ هذا وغيره بقوله:- فيه- أي فيما ذكر من الآيتين- ما هو من أبين الآيات للخاص والعام، وكونه "صلى الله عليه وسلم" مذكوراً مبشرا ًبه
_________
١ سيأتي تخريجه في موضعه ص "٢٦٣".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٤٥، ٤٦".
٣ سورة آل عمران: آية "٨١، ٨٢".',NULL,'136',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (145,'في كتب الأنبياء، وفيه حجة على أن دعوته عامة في الظاهر والباطن، وفيه أن الإيمان به لا يكفي عن نصرته، بل لا بد من هذا وهذا، وفيه أخذه تعالى الميثاق على الأنبياء بذلك، دليل على شدته إلا على من يسره الله عليه، وفيه أن من آتاه الله الكتاب والحكمة أحق بالانقياد للحق إذا جاء به من بعده، بخلاف ما عرف من حال الأكثر من ظنهم أنه لو اتبعه غيرهم فهو نقص في حقهم وفيه مزيد التأكيد بقوله: {أَأَقْرَرْتُمْ وَأَخَذْتُمْ عَلَى ذَلِكُمْ إِصْرِي} . وفيه إشهادهم مع شهادته سبحانه، وفيه أن من تولى بعد ذلك فجرمه أكبر، وفيه أن الآخر مصدق لما معهم لا مخالف له١.
ومن الآيات التي اختارها للتفسير قوله تعالى: {وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ آزَرَ أَتَتَّخِذُ أَصْنَاماً آلِهَةً إِنِّي أَرَاكَ وَقَوْمَكَ فِي ضَلالٍ مُبِينٍ} إلى قوله: {إِنْ هُوَ إِلَّا ذِكْرَى لِلْعَالَمِينَ} ٢. وفيها يتجلى الولاء والبراء، والالتجاء إلى الله والاعتصام به من قبل إبراهيم: {وَحَاجَّهُ قَوْمُهُ قَالَ أَتُحَاجُّونِّي فِي اللَّهِ وَقَدْ هَدَانِ وَلا أَخَافُ مَا تُشْرِكُونَ بِهِ إِلَّا أَنْ يَشَاءَ رَبِّي شَيْئاً وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْماً أَفَلا تَتَذَكَّرُونَ} ٣.
وما تفضل الله به على عبده الحنيف الموحد، ومن سار على نهجه في التوحيد،: {وَتِلْكَ حُجَّتُنَا آتَيْنَاهَا إِبْرَاهِيمَ عَلَى قَوْمِهِ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَاءُ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ كُلّاً هَدَيْنَا ... } الآيات٤.
فيستنبط الشيخ من هذه الآيات، التي قصت عليت ما جرى بين إبراهيم وقومه فيقول: القدح في حجتهم، لأن السواد الأعظم ليس لهم حجة إلا هي، فيدل على الرسوخ في مخالفتهم بالأدلة اليقينية، لقوله: {إِنِّي
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٤٦، ٤٧".
٢ سورة الأنعام: الآيات "٧٤- ٩٠".
٣ سورة الأنعام: الآية "٨٠".
٤ سورة الأنعام: آية "٨٣". وما بعدها.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (146,'أَرَاكَ وَقَوْمَكَ فِي ضَلالٍ مُبِينٍ} ١.
وينبه الشيخ في ما أشارت إليه الآيات من البراء فيقول: براءته من شركهم، نفي أولا كونها "تستحق" ٢، ونفى ثانيا عن نفسه الالتفات إليها.
- تصريحه بما ذكر، ولم يدار مع كثرتهم ووحدته.
- تصريحه بالبراءة منهم بقوله: {وَمَا أَنَا مِنَ الْمُشْرِكِينَ} ٣.
ويقول الشيخ مستنبطاً من الآيات إنعام الله على عباده الموحدين:-
- أن العلم بدلائل التوحيد، وبطلان الشبه فيه، يرفع الله به المؤمن درجات.
- معرفة آن الرب تبارك وتعالى حكيم يضع الأشياء في مواضعها.
- كونه عليم بمن هو أهل لها كما قال تعالى: {وَكَانُوا أَحَقَّ بِهَا وَأَهْلَهَا} .
- ذكر نعمة على إبراهيم بذريته التي أنعم عليهم بالهداية.
- أن العلم والهداية أفضل النعم لقوله: {وَنُوحاً هَدَيْنَا مِنْ قَبْلُ} .
- هدايته لمذكورين أصولهم وفروعهم ومن في درجتهم.
- ذكر الذي هداهم الله إليه وهو الصراط المستقيم. وهو المقصود من القصة٤.
ومن الآيات التي اختارها الشيخ للتفسير أيضاً قوله تعالى: {مَنْ كَانَ يُرِيدُ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَالَهُمْ فِيهَا وَهُمْ فِيهَا لا يُبْخَسُونَ. أُولَئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الْآخِرَةِ إِلَّا النَّارُ وَحَبِطَ مَا صَنَعُوا فِيهَا وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ} ٥.
وفى هاتين الآيتين يتضح حال من لم يرد بعمله وجه الله تعالى، بل أراد عرضاً زائلاً، وأن ذلك سبب لحبوط عمله وخسرانه في الآخرة، والعياذ بالله. وقد ذكرنا ما ذكره الشيخ من أقوال السلف في ذكر الأقسام المندرجة تحت هذه الآية٦.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٦٤".
٢ في جميع النسخ المخطوطة والمطبوعة التي أطلعت عليها "لا تستحق" وهو خطأ.
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٦٥".
٤ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٦٦،٦٧".
٥ سورة هود: آية "١٥،١٦".
٦ انظر ما تقدم ص "٨٤-٨١".',NULL,'138',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (147,'وللشيخ رحمة الله مقدرة فائقة في استنباط المسائل العقدية من الآيات، وكيف لا وهو حامل لواء تجديد العقيدة الإسلامية التي خالطت روحه وفكره وثقافته، فتراه حتى في الآيات المتعلقة بالقصص القرآني يركز على الجانب العقدي، وإيضاح ما يستفاد من دروس عقدية من القصة، فلا يقف عند مجرد سرد أحداث القصة القرآنية كما درج عليه كثير من المفسرين، وذلك أن العقيدة واحدة لم تتغير بتغير الأمم، ولاشك أن الهدف من القصص هو العظة والعبرة، لا مجرد سرد الأحداث وسيأتي- إن شاء الله- مزيد تفصيل لمنهج الشيخ في القصص القرآني. واكتفى هنا بذكر مثال واحد يتضح به ما ذكرت.
فقد قال الشيخ مستنبطاً من قصة آدم وإبليس١: وفي القصة فوائد عظيمة وعبر لمن اعتبر بها، منها: أن خلق آدم من تراب من أبين الأدلة على المعاد، كما استدل عليه سبحانه في غير موضع، وعلى قدرته سبحانه، وعظمته، ورحمته، وعقوبته، وإنعامه، وكرمه، وغير ذلك من صفاته.
ومنها: أنها من أدلة الرسل عامة، ومن أدلة نبوة محمد "صلى الله عليه وسلم" خاصة، ومنها: الدلالة على الملائكة وعلى بعض صفاتهم، ومنها: الدلالة على القدر خيره وشره، فقد اشتملت على أصول الإيمان الستة في حديث جبريل، ومنها: وهى أعظمها أنها تفيد الخوف العظيم الدائم في القلب، وأن المؤمن لا يأمن حتى تأتيه الملائكة عند الموت تبشره.
وذلك من قصة إبليس، وما كان فيه أولا من العبادة والطاعة، ففي ذلك شيء من تأويل قوله "صلى الله عليه وسلم": "إن أحدكم ليعمل بعمل أهل الجنة حتى ما يكون بينه وبينها إلا ذراع" إلى آخره٢.
والشيخ رحمه الله لم يكتف بالامتثال منه وكفى، بل دعا غيره إلى إتباع الحق والتمسك به ونبذ ما يخالفه من البدع والأهواء، وهذا وأضح تمام الوضوح في تفسيره. فقد كان يهتم كثيراً بإيضاح العقيدة، ويرى أنها روح الإسلام وجوهره، بل هي الدين كله.
فانظر إلى كلامه حيث يقول وأما قوله: {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} فالعبادة كمال المحبة وكمال الخضوع. والخوف والذل، وقدم المفعول وهو إياك، وكرر للاهتمام والحصر أي لا نعبد إلا إياك ولا نتوكل إلا عليك، وهذا هو كمال الطاعة والدين كله يرجع إلى هذين المعنيين، فالأول التبرؤ من الشرك والثاني لتبرؤ من الحول
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٨٤".
٢ رواه البخاري في صحيحه في مواضع منها/ كتاب القدر/ باب "١" انظر الفتح "١ ٤٨٦:١" ح "٦٥٩٤" ومسلم في صحيحه/ كتاب القدر/ باب كيفية خلق الآدمي في بطن أمه ... "٢٠٣٦:٤" ح "٢٦٤٣".',NULL,'139',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (148,'والقوة، فقوله: {إِيَّاكَ نَعْبُدُ} أي إياك نوحد، ومعناه أنك تعاهد ربك أن لا تشرك به في عبادته أحدا، لا ملكا، ولا نبياً ولا غيرهما كما قال للصحابة: {وَلا يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلائِكَةَ وَالنَّبِيِّينَ أَرْبَاباً أَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ إِذْ أَنْتُمْ مُسْلِمُونَ} ١. فتأمل هذه الآية.
واعرف ما ذكرت لك في الربوبية أنها التي نسبت إلى "تاج" و"معمد بن شمسان" ٢ فإذا كان الصحابة لو يفعلونها مع الرسل كفروا بعد إسلامهم، فكيف بمن فعلها في تاج وأمثاله؟
وقوله: {وَإِيَّاكَ نَسْتَعِينُ} هذا فيه أمران: أحدهما: سؤال الإعانة من الله، وهو التوكل والتبريء من الحول والقوة، وأيضاً طلب الإعانة من الله كما مر أنها من نصف العبد٣.أ. هـ.
فانظر إلى كلامه "رحمه الله" عند تفسير هذه الآية العظيمة التي تضمنت الدين كله كما قال، حيث إن العلاقة بين المخلوق والخالق عبادة من المخلوق للخالق، وتكرم بالعون من الخالق للمخلوق، فنصرف لله وحده ماله من العبادة: {إِيَّاكَ نَعْبُدُ} ، ونسأله التفضل علينا بالعون في جميع الأمور، ونتوكل عليه سبحانه، فلا حول ولا قوة لنا إلا بالله: {وَإِيَّاكَ نَسْتَعِينُ} ، ومن هنا كان الدين كله يرجع إلى هذين المعنيين كما قال الشيخ.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٦".
٢ هما رجلان سلك الناس فيهما- في ذلك الزمن - سبيل الطواغيت، ورفعوهما إلى مقام الألوهية بل الربوبية.
أما محمد بن شمسان فقد كان له أولاد يأمرون الناس ويندبونهم لينذروا اله ويعتقدوا فيه الولاية.
أما "تاج" فكان بعض الناس يعتقدون فيه الولاية، وكانوا يأتونه لقضاء حاجاتهم، وكان هو يأتيهم من بلده الخرج إلى الدرعية لتحصيل ما تجمع من النذور، وكان أهل البلاد المجاورة يعتقدون فيه اعتقاداً عظيماً، فخافه الحكام، وهاب الناس أعوانه وحاشيته. وزعموا أنه أعمى وأنه يأتي من بلده من غير قائد. وغير ذلك من الحكايات والاعتقادات التي ضلوا بسببها عن الصراط والمستقيم.
انظر: روضه الأفكار "٢٢٦،٢١٦،١٨٨،١٧٨،١٥٥،١٣٠:١" وتاريخ نجد "المحرر ١:١٢" والرسائل الشخصية ص "٥٢، ٧٥، ١٧٢، ٢٤٠".
وتحقيق الدكتور/ فهد بن عبد الرحمن الرومي لسورة الفاتحة ص "٤٥" الطبعة الخامسة.
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٦".',NULL,'140',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (149,'وقد أوضح الشيخ كلا المعنيين هنا بينما اختصر القول في موضع آخر في الاستنباط من هذه الآية واكتفى بقوله: فيها التوحيد. وهذا جري على منهجه الاستنباطي المعروف فاكتفى بهذه الكلمة الفذة الشاملة عن كلام كثير جداً لو سطره لوسع مجلدات كما فعل ابن القيم من قبل في كتابه "مدارج السالكين بين مراتب {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} ".
ولكن الشيخ "رحمه الله" كما هو معهود عنه من كتاباته ومؤلفاته يهدف إلى الاختصار، والنص على أصول المسائل المفيدة من أقصر طريق، وإن كان يرى أن الموضوع وأسع جدا، ومما يؤيد هذا أن الشيخ بعد أن استنبط من الفاتحة بعض المسائل المختصرة قال في آخرها: آيات الفاتحة كل آية منها لو يعلمها الإنسان صار فقيهاً، وكل آية قد أفرد معناها بالتصانيف١. وإضافة إلى بيان الشيخ للعقيدة وأسسها من خلال تفسيره، فإنه يبين أحياناً أهمية بعض المسائل بطريقة ما كأن ينص على أهميتها، أو يتعجب من عظمها، أو غفلة الناس عنها إن كانت حسنة أو انغماسهم فيها إن كانت خلاف ذلك.
ولاشك أن مثل هذا الأسلوب يحدو بالقارئ إلى آن يقف عند هذه المسألة وقفة ليتمعن فيها، ويتأثر بما تأثر به الشيخ منها. وأمثلة ذلك كثيرة جدا فمنها:
قوله عند تفسير قوله تعالى: {مَالِكِ يَوْمِ الدِّينِ} فمن عرف تفسير هذه الآية وعرف تخصيص الملك بذلك اليوم مع أنه سبحان مالك كل شيء ذلك اليوم وغيره عرف أن التخصيص لهذه المسألة الكبيرة العظيمة، التي بسبب معرفتها دخل الجنة من دخلها، ودخل النار من دخلها، فيالها من مسألة لو رحل الرجل فيها أكثر من عشرين سنة لم يوفها حقها! فأين هذا المعنى والإيمان بما صرح به القرآن مع قوله "صلى الله عليه وسلم": "يا فاطمة بنت محمد لا أغني عنك من الله شيئاً" ٢. من قول صاحب البردة:
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٩".
٢ انظر تخريجه فيما يأتي في قسم التحقيق ص "٣٣٨".',NULL,'141',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (150,'ولن يضيق رسول الله جاهك بي ... إذا الكريم تجلي باسم منتقم
فإن لي ذمة منه بتسميتي ... محمداً وهو أوفى الخلق بالذمم
إن لم تكن في معادي أخذاً بيدي ... فضلا وإلا فقل يازلة القدم
فليتأمل من نصح نفسه هذه الأبيات ومعناها، ومن فتن بها من العباد، ممن يدعي أنه من العلماء، واختاروا تلاوتها على تلاوة القرآن. هل يجتمع في قلب عبد التصديق بهذه الأبيات والتصديق بقوله: {يَوْمَ لا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْئاً وَالْأَمْرُ يَوْمَئِذٍ لِلَّهِ} ١ وقوله: "يا فاطمة بنت محمد لا أغنى عنك من الله شيئاً " ٢؟! لا والله. لا والله. لا والله ٣....
ومن ذلك قوله ضمن المسائل المستنبطة من قوله تعالى: {وَكَذَلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا: أَهَؤُلاءِ مَنَّ اللَّهُ عَلَيْهِمْ مِنْ بَيْنِنَا أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ} ٤.
حيث يقول:- المسألة العظيمة الكبيرة وهى الاستدلال بصفات الله على ما أشكل عليك من القدرة، لأنه سبحانه رد عليهم ما وقع في أنفسهم من استبعاد كون الله حرمهم، وخص هؤلاء بالكرامة٥.
ويقول: جلالة هذه المسألة، وهي مسألة علم الله، لأنه سبحانه رد بها على الملائكة لما قالوا: {أَتَجْعَلُ فِيهَا مَنْ يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ} ٦ الآية ورد بها على الكفار والجهال في هذه الآية كما ترى" ٧.
وقوله ضمن المسائل المستنبطة من قوله تعالى: {ذَلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ وَلَوْ أَشْرَكُوا لَحَبِطَ عَنْهُمْ مَا كَانُوا يَعْمَلُونَ} ٨ العظيمة التي لم يعرفها
_________
١ سورة الانفطار: آية "١٩".
٢ انظر تخريجه في قسم التحقيق ص "٣٣٨".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٤،١٥".
٤ سورة الأنعام: آية "٥٣".
٥ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٥٨".
٦ سورة البقرة: آية "٣٠".
٧ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٥٨".
٨ سورة الأنعام: آية "٨٨".',NULL,'142',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (151,'أكثر من يدعي الدين وهي مسألة تكفير من أشرك، وحبوط عمله، ولو كان من أعبد الناس، وأزهدهم١.
وقوله ضمن المسائل المستنبطة من قوله تعالى: {مِنْ دُونِهِ أَوْلِيَاءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَا إِلَى اللَّهِ زُلْفَى إِنَّ اللَّهَ يَحْكُمُ بَيْنَهُمْ فِي مَا هُمْ فِيهِ يَخْتَلِفُونَ إِنَّ اللَّهَ لا يَهْدِي مَنْ هُوَ كَاذِبٌ كَفَّارٌ} ٢حيث يقول:
إبطال ما غرهم به الشيطان أن قصدهم وجه الله لا غير، وما أجلها من مسألة! ٣
ومن نصه على أهمية بعض المسائل قوله في قصة يوسف: وأعظم ما فيها تقرير الشهادتين بالأدلة الواضحة٤.
ومن كمال نصح الشيخ "رحمه الله" أن يهتم بإيضاح المسائل التي قد يشكل أمرها على بعض الناس من خلال التفسير، لئلا يتوهم متوهم وجود اختلاف أو تعارض أو إشكال في أمر من الأمور.
وقد وجد مناسبات كثيرة لهذه التنبيهات في قصة يوسف فمنها:-
ما استنبطه من قوله تعالى حكاية عن يوسف قال: {قَالَ اجْعَلْنِي عَلَى خَزَائِنِ الْأَرْضِ إِنِّي حَفِيظٌ عَلِيمٌ} ٥ حيث قال الشيخ- قوله: {عَلَى خَزَائِنِ الْأَرْضِ} هذا فيه طلب الولاية كما قال عمر بن الخطاب لبعض الصحابة لما عرض عليه ولاية فأبى فقال: طلبها من هو خير منك، يعني يوسف "عليه السلام" ولا يخالف هذا ما ورد من النهي عن طلب الأمارة، لأن هذا في غير شدة الحاجة كما أن خالداً لما أخذ الراية يوم موته من غير إمرة مدح على ذلك٦.
- وقوله: {إِنِّي حَفِيظٌ عَلِيمٌ} فليس هذا مما نهي عنه من تزكية النفس
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٦٧".
٢ سورة الزمر: آية "٣".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣١٨".
٤ المرجع السابق ص "١٣١".
٥ سورة يوسف: آية "٥٥".
٦ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٥٦" وانظر هذه الأخبار في موضعها من قسم التحقيق فيما يأتي ص "٣٥٣".',NULL,'143',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (152,'بل يذكر الإنسان ما فيه من الفضائل عند الحاجة إذا لم يقصد التزكية كما ورد عن جماعة من الصحابة١.
وكذا قوله عند قول، الله تعالى: {وَكَذَلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ يَتَبَوَّأُ مِنْهَا حَيْثُ يَشَاءُ نُصِيبُ بِرَحْمَتِنَا مَنْ نَشَاءُ وَلا نُضِيعُ أَجْرَ الْمُحْسِنِينَ} ٢ حيث قال:- تسمية الله سبحانه ذلك رحمة في قوله: {نُصِيبُ بِرَحْمَتِنَا مَنْ نَشَاءُ} وهذه من أشكل المسائل على أكثر الناس، بعضهم يظن أن هذا كله نقص أو مذموم، وأن التجرد من المال مطلقا هو الصواب، وبعض يظن أن عطاء الدنيا يدل على رضا الله، وكلاهما على غير الصواب. وذلك أن من أنعم الله عليه بولاية أو مال فجعلها طريقا إلى طاعة الله فهو ممدوح وهو أحد الرجلين اللذين يغبطهما المؤمن، وإن كان غير هذا فلا٣.
كما استنبط من قوله تعالى حكاية عن يوسف: {أَلا تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ وَأَنَا خَيْرُ الْمُنْزِلِينَ} ٤.
إن هذا ليس من تزكية النفس المذموم.
وان هذا ليس من المن والأذى المذموم٥.
واستنبط مما حكاه الله عن يعقوب من قوله: {يَا أَسَفَى عَلَى يُوسُفَ} ٦ إن الكلام إذا لم يكن فيه جزع لم يناف الشكوى٧.
وقال عند قوله تعالى حكاية عن يعقوب أيضاً: {إِنَّمَا أَشْكُو بَثِّي وَحُزْنِي إِلَى اللَّهِ وَأَعْلَمُ مِنَ اللَّهِ مَا لا تَعْلَمُونَ} ٨.
_________
١ المرجع السابق بصحيفته.
٢ سورة يوسف: آية "٥٦".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٥٧" وانظر قسم التحقيق ص "٣٥٥".
٤ سورة يوسف: آية "٥٩".
٥ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٥٩"، "١٦٠". وانظر قسم التحقيق ص "٣٥٩".
٦ سورة يوسف: آية "٨٤".
٧ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٦٩" وانظر قسم التحقيق ص "٣٧٨".
٨ سورة يوسف: آية "٨٦".',NULL,'144',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (153,'جوابه لهم عليه السلام وهو يدل على أن الشكوى إلى الله لا تنافى الصبر بل هي ممدوحة كما ذكر عن أيوب.
- إخبار الرجل بنيته الصالحة إذا احتاج، أو انتفع السامع، ولا محذور في ذلك١ كما استنبط من قوله تعالى حكاية عن يوسف: {اذْهَبُوا بِقَمِيصِي هَذَا فَأَلْقُوهُ عَلَى وَجْهِ أَبِي يَأْتِ بَصِيراً وَأْتُونِي بِأَهْلِكُمْ أَجْمَعِينَ} ٢ أن التبرك بذلك وإمساكه والتداوي به ليس من الشرك كما كانوا يفعلون بآثار رسول الله "صلى الله عليه وسلم" بل ذلك حسن مطلوب٣.
ومن المعلوم أن الحق والباطل في صراع دائم، وعلى الصراط دعاة يصدون عنه من آمن ويبغونها عوجا. وفي كتاب الله عز وجل من الحجج ما يقذف به الباطل فيدمغه فإذا هو زاهق. ولحماية جناب العقيدة، والذب عن حياضها من خلال التفسير نجد آن الشيخ "رحمه الله" قد ركز على جانب الردود على أهل البدع كثيراً بنص الآيات أو بدلا لالتها وما يستنبط منها، كما سيأتي بيانه في مبحث خاص إن شاء الله تعالى٤.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٧٠" وانظر قسم التحقيق ص "٣٨٠".
٢ سورة يوسف: آية "٩٣".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٧٤" وانظر قسم التحقيق ص "٣٨٦".
٤ انظر ذلك فيما سيأتي ص "١٨٦-١٩٣".',NULL,'145',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (154,'<span data-type="title" id=toc-29>المبحث الثاني: أهم المباحث العقدية التي بين لها الشيخ في تفسيره</span>
<span data-type="title" id=toc-30>المطلب لأول: اهتمامه ببيان أقسام التوحيد من خلال تفسيره</span>
...
المبحث الثاني: أهم المباحث العقدية التي بين لها الشيخ في تفسيره
كما أهتم الشيخ "رحمه الله" بالجانب العقدي في تفسيره وركز عليه في الجملة فقد نبه على كثير من المسائل عندما وجد لذكرها مجالا ومناسبة.
وقد آن لي أن أبين أهم النواحي العقدية التي تعرض لها في تفسيره، وسيكون إيضاحي لهذه النواحي من خلال الاستنباط- إن شاء الله- على وجه التنبيه والإعلام والاستدلال مع الاختصار في كل ذلك ما أمكن.
وأما تفصيل القول في تلك النواحي العقدية وغيرها، ومعتقد الشيخ فيها، وتقريره لها فأحيل الراغب في معرفتها إلى كتاب "عقيدة الشيخ محمد بن عبد الوهاب السلفية وأثرها في العالم الإسلامي" تأليف شيخنا الدكتور: صالح بن عبد الله العبود. فقد أفاد وأجاد وفصل القول في جوانب مهمة من العقيدة، فجزاه الله خيرا، وأجزل مثوبته.
وليكن أيضاً حي لأهم المباحث العقدية التي بينها الشيخ في تفسيره في مطلبين:-
المطلب لأول: اهتمامه ببيان أقسام التوحيد من خلال تفسيره
لقد أهتم الشيخ بهذا الجانب كثيراً وبينه وقرره واستنبط من الآيات ما يدل عليه حتى كان أظهر ما في تفسيره "رحمه الله" واليك بيانه:
أولاً: اهتمامه بتوحيد المعرفة والإثبات في تفسيره.
أهتم الشيخ في تفسيره بتوضيح توحيد المعرفة والإثبات وهو المشتمل على توحيد الربوبية والأسماء والصفات، وهو التوحيد العلمي الخبري، وذلك لأنه الأصل، فلا يغلط في الإلهية إلا من لم يعطه حقه.
وقد سار الشيخ في إيضاح هذا التوحيد مع كتاب الله عز وجل، حيث الإشارة إلى وحدانية الله، وكماله، وتفرده بتعريف ملكوت السماوات والأرض، وذكر الدلائل الشرعية والعقلية لتقرير هذا التوحيد.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (155,'ففي مجال إيضاح توحيد الربوبية:
يوضح الشيخ معنى الرب في تفسير سورة الفاتحة بأنه المالك المتصرف ثم يقول:- وأما العالمين" فهو اسم لكل ما سوى الله تبارك وتعالى، فكل ما سواه من ملك ونبي وانسي وجني وغير ذلك مر بوب مقهور يتصرف فيه، فقير، محتاج، كلهم صامدون إلى واحد لا شريك له في ذلك، وهو الغني الصمد١.
ويستدل على توحيد الربوبية ببديهة العقل، لأن توحيد الربوبية ثابت مشهود، فيقول في قوله تعالى: {وَكَذَلِكَ نُرِي إِبْرَاهِيمَ مَلَكُوتَ السَّمَاوَاتِ وَالْأَرْضِ وَلِيَكُونَ مِنَ الْمُوقِنِينَ} ٢ الآيات:- فإن ذلك من أعظم الأدلة على المسألة- أي الربوبية- ببديهة العقل، أن من رأى نخلا لا يتخالجه شك أن المدبر له ليس نخلة واحدة منه، فكيف بملكوت السماوات والأرض٣؟!
ويقول: والله من فضله أعطى الفطر ثم العقول ثم بعث الرسل وأنزل الكتب٤، فمع ثبوت آيات الله الكونية الدالة عقلا على ربوبية الله تعالى إلا أن الله قد أقام الحجة على خلقه ببعث الرسل، وإنزال الكتب، لتنبيه الخلق على دلائل ربوبيته ووحدانيته وتقريرها.
وقد سار "رحمه الله" في إيضاح هذا التوحيد مع تلك الآيات القرآنية الدالة على ربوبية الله تعالى، وتفرده بخلق السماوات والأرض والملكوت كله، والتفرد بالضر والنفع والتصرف فيما شاء سبحانه.
فمن دلائل ربوبية الله تعالى خلق المخلوقات، والتصرف فيها، كما في قوله تعالى: {خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ يُكَوِّرُ اللَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمّىً أَلا هُوَ الْعَزِيزُ الْغَفَّارُ} ٥.
وقد ذكر الشيخ هذه الآية، ثم ذكر ما فيها من براهين دالة على الدين الحق وضده في مسائل مستنبطه وهي:
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١١".
٢ سورة الأنعام: آية "٧٥" وما بعدها.
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٦٤".
٤ المرجع السابق ص "١٤٥".
٥ سورة الزمر: آية "٥".',NULL,'147',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (156,'الأولى: خلق السماوات والأرض.
الثانية: أنه الحق.
الثالثة: تكوير المكورين.
الرابعة: تسخير النيرين.
الخامسة: ذكر عزته في هذا.
السادسة: ذكر مغفرته١.
ثم ذكر براهين أخرى في الآية التي تليها وهي قوله تعالى: {خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَأَنْزَلَ لَكُمْ مِنَ الْأَنْعَامِ ثَمَانِيَةَ أَزْوَاجٍ يَخْلُقُكُمْ فِي بُطُونِ أُمَّهَاتِكُمْ خَلْقاً مِنْ بَعْدِ خَلْقٍ فِي ظُلُمَاتٍ ثَلاثٍ ذَلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ لا إِلَهَ إِلَّا هُوَ فَأَنَّى تُصْرَفُونَ} ٢.
وقد ذكر هذه البراهين في مسائل تدعو إلى التفكر وهي:
الأولى: خلقنا من نفس واحدة مع هذه الكثرة.
الثانية: إنزاله لنا من الأنعام هذه النعم العظيمة.
الثالثة: خلقنا في البطون.
الرابعة: أنه خلق من بعد خلق.
السادسة: أنه في الظلمات الثلاث.
السابعة: كلمة الأخلاص.
الثامنة التعجب من الغلط في هذا مع كثرة هذه البراهين ووضوحها٣.
ومن دلائل ربوبية الله تعالى إنعامه على عباده، بالنعم، وتربيتهم بها، كما قال: {وَلَقَدْ مَكَّنَّاكُمْ فِي الْأَرْضِ وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ قَلِيلاً مَا تَشْكُرُونَ. وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ لَمْ يَكُنْ مِنَ السَّاجِدِينَ} ٤ فيض الشيخ على ما ذكرت هاتان الآيتان فيقول:
_________
١ مؤلفات الشيخ/القسم الرابع/ التفسير "٣١٩".
٢ سورة الزمر: آية "٦".
٣ مؤلفات الشيخ /القسم الرابع/ التفسير ص "٣٢٠".
٤ سورة الأعراف: آية "١٠، ١١".',NULL,'148',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (157,'الآية الأولى: فيها ذكر نعمته بالتمكين في الأرض.
الثانية: ذكر نعمته بما فيها من المعايش.
الثالثة: ذكر قلة شكرهم.
وفى الآية الثانية: ذكر نعمة الخلق.
الثانية: ذكر نعمة التصوير.
الثالثة: ذكر نعمة أمر الملائكة بالسجود لأبينا آدم١.
ويذكر الشيخ أيضاً بعض دلائل ربوبية الله تعالى من خلال الآيات كما في قوله تعالى: {وَلَقَدْ جَعَلْنَا فِي السَّمَاءِ بُرُوجاً وَزَيَّنَّاهَا لِلنَّاظِرِينَ. وَحَفِظْنَاهَا مِنْ كُلِّ شَيْطَانٍ رَجِيمٍ. إِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَأَتْبَعَهُ شِهَابٌ مُبِينٌ. وَالْأَرْضَ مَدَدْنَاهَا وَأَلْقَيْنَا فِيهَا رَوَاسِيَ وَأَنْبَتْنَا فِيهَا مِنْ كُلِّ شَيْءٍ مَوْزُونٍ. وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ وَمَنْ لَسْتُمْ لَهُ بِرَازِقِينَ. وَإِنْ مِنْ شَيْءٍ إِلَّا عِنْدَنَا خَزَائِنُهُ وَمَا نُنَزِّلُهُ إِلَّا بِقَدَرٍ مَعْلُومٍ. وَأَرْسَلْنَا الرِّيَاحَ لَوَاقِحَ فَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَسْقَيْنَاكُمُوهُ وَمَا أَنْتُمْ لَهُ بِخَازِنِينَ. وَإِنَّا لَنَحْنُ نُحْيِي وَنُمِيتُ وَنَحْنُ الْوَارِثُونَ. وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِينَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِينَ. وَإِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ} ٢.
فيذكر ما في الآية من دلائل ربوبية الله عز وجل فيقول:
فيها ما جعل الله في البروج من الآيات سواء قيل: أنها النجوم، أو الكبار منها.
الثانية: تزيين السماء.
الثالثة: حفظها من الشياطين.
الرابعة: ذكر الاستراق.
الخامسة: ذكر عقوبته.
السادسة: مد الأرض.
السابعة: الرواسي.
الثامنة: إنبات البنات.
_________
١ مؤلفات الشيخ/القسم الرابع/التفسير ص "٧١".
٢ سورة الحجر: الآيات "١٦- ٢٥".',NULL,'149',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (158,'التاسعة: كثرته، وكونه من كل شيء.
العاشرة: كونه موزونا.
الحادية عشرة: ذكر المعا يش.
الثانية عشرة: ذكر الأنعام.
الثالثة عشرة: كوننا لا نرزقهم مع كونهم لنا.
واستنبط من قوله: {وَإِنْ مِنْ شَيْءٍ إِلَّا عِنْدَنَا خَزَائِنُهُ} مسألتين:
الأولى: فيها أن كل شيء خزائنه عنده.
الثانية: إنزاله بقدر معلوم.
كما استنبط من قوله: {وَأَرْسَلْنَا الرِّيَاحَ لَوَاقِحَ ... } إلى آخر الآيات ما يلي:
الأولى: فيها ذكر إنعامه بإرسال الرياح.
الثانية: أنها تلقح السحاب والشجر.
الثالثة: إنزال الماء من السماء.
الرابعة: تسهيل تناوله.
الخامسة: عجزهم عن خزانته.
السادسة: تفرده بالإحياء والإماتة.
السابعة: أنه الو ارث.
الثامنة: علمه بالمستقدم والمتأخر في الزمان وفي الطاعة.
التاسعة: تفرده بحشر الجميع.
العاشرة: ذكر حكمه وعلمه مع ذلك١.
ويوضح الشيخ أيضاً أن من دلائل ربوبية الله تعالى، تفرده سبحانه بالضر والنفع فيقول عند قوله تعالى: {وَحَاجَّهُ قَوْمُهُ قَالَ أَتُحَاجُّونِّي فِي اللَّهِ وَقَدْ هَدَانِ وَلا أَخَافُ مَا تُشْرِكُونَ بِهِ إِلَّا أَنْ يَشَاءَ رَبِّي شَيْئاً وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْماً أَفَلا تَتَذَكَّرُونَ} ٢.
_________
١ مؤلفات الشيخ / القسم الرابع/ التفسير ص "١٨٦،١٨٧" وانظر قسم التحقيق ص "٠٨-٤٠٦".
٢ سورة الأنعام: آية "٨٠".',NULL,'150',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (159,'أنهم لما خصموا رجعوا إلى التخويف كفعل أمثالهم، فذكر أنه لا يخاف إلا الله المتفرد بالضر والنفع، بخلاف آلهتهم، فذكر النفي الإثبات١. ولله تعالى مقاليد السماوات والأرض ليس لأحد فيهما شرك بل الملك كله لله سبحانه.
يوضح الشيخ هذا مستنبطاً إياه من الآيات بقوله عند ذكر قصة نوح عليه السلام٢:
- التحقيق بكون المخلوق ليس له من الأمر شيء ولو كان نبياً مرسلاً بسبب ما فيها من قمة ابن نوح.
- تبرؤ الرسل من دعوى أن عندهم خزائن الله وعلم الغيب مع أن الطواغيت في زماننا ادعوا ذلك وصدقوا وعبدوا أجل ذلك.
- معرفة شيء من حكمة الله في تأديبه الرسل كما قال لنوح: {إِنِّي أَعِظُكَ أَنْ تَكُونَ مِنَ الْجَاهِلِينَ} ٣.
فالشيخ رحمه الله قد تمشى في إثبات ما تقدم من دلائل ربوبية الله "ووحدانيته مع كتاب الله عز وجل واكتفى بالله، والتنبيه على ما دلت عليه الآيات من الدلائل وذلك مدعاة ليجول الناظر بتفكيره في هذه الدلائل العظيمة ثم أن مجرد التنبيه على توحيد الربوبية والتذكير به كاف في إثباته لذوي العقول الإسلامية. كما أن هذا يوضح حقيقة هامة، وهي أن الشيخ يرى أن من كتاب الله ما لا يحتاج إلى شرح وتفسير بل هو واضح في نفسه، فلفظه دال دلالة ظاهرة على معناه، فالمرء بحاجة إلى لفت النظر، إلى نص كتاب الله تعالى فقط.
وفي مجال توحيد الربوبية أيضاً يوضح الشيخ أنه متقرر عند الكفار عبده الأوثان ربوبية الله عز وجل فهم يعرفون ذلك معرفة مجردة.
يقول الشيخ٤: فالله تعالى مالك كل شيء، وهو المتصرف فيه، وهذا حق.
_________
١ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٦٥".
٢ المرجح السابق ص "١٢٤، ١٢٥".
٣ سورة هود: آية "٤٦".
٤ مؤلفات الشيخ/القسم الرابع/ التفسير ص "١٣،١٢".',NULL,'151',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (160,'ولكن أقربه عباد الأصنام الذين قاتلهم رسول الله "صلى الله عليه وسلم"، كما ذكر الله عنهم في القرآن في غير موضع كقوله تعالى: {قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالْأَرْضِ أَمَّنْ يَمْلِكُ السَّمْعَ وَالْأَبْصَارَ وَمَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُدَبِّرُ الْأَمْرَ فَسَيَقُولُونَ اللَّهُ فَقُلْ أَفَلا تَتَّقُونَ} ١.
بل إن الكفار يعرفون بعض صفات الله عز وجل فقد استنبط الشيخ من قوله تعالى: {وَكَذَلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا أَهَؤُلاءِ مَنَّ اللَّهُ عَلَيْهِمْ مِنْ بَيْنِنَا أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ} ٢.
أنه متقرر عند الكفار، عبدة الأوثان، منكري البعث، أن الله سبحانه حكيم، يضع الأشياء في مواضعها "والاشاعرة يزعمون أنه لا يفعل شيئاً لشيء٣ ".
ولكن الكفار لم يعبدوا الله وحده كما هو مقتضى شهادتهم بالربوبية كما قال: {وَالَّذِينَ اتَّخَذُوا مِنْ دُونِهِ أَوْلِيَاءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَا إِلَى اللَّهِ زُلْفَى إِنَّ اللَّهَ يَحْكُمُ بَيْنَهُمْ فِي مَا هُمْ فِيهِ يَخْتَلِفُونَ إِنَّ اللَّهَ لا يَهْدِي مَنْ هُوَ كَاذِبٌ كَفَّارٌ} ٤.
وذلك لأنهم لم يعطوا هذا التوحيد حقه، وإلا فكيف يشرك بالله من يعلم أنه هو النافع الضار؟! وكيف يؤثر على الله شيئاً من يعلم أن الملك كله لله وحده؟! وكيف يشرك بالله من يعلم أن الله يعلم السر وأخفى؟! نسأل الله السلامة. وقد أشار الشيخ إلى نحو من هذه المعاني عند قوله تعالى: {قُلْ أَنَدْعُو مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُنَا وَلا يَضُرُّنَا} الآيات٥.
حيث ذكر بعض المسائل التي يجاوب بها من أشير عليه بشيء يصير به مرتداً ومنها:
- الجواب بقوله: {أَنَدْعُو مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُنَا وَلا يَضُرُّنَا} يعني كيف تدبر
_________
١ سورة يونس: آية "٣١".
٢ سورة الأنعام: آية "٥٢".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص"٥٨". وهو يشير هنا إلى أنهم ينفون الحكمة.
٤ سورة الزمر: آية "٣".
٥ سورة الأنعام الآيات "٧١-٧٣".',NULL,'152',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (161,'عن هذا وتقبل على هذا؟
أن الملك كله له يوم ينفخ في الصور، فكيف تؤثر عليه مالاً، أوحالاً أو جاهاً، أو غير ذلك؟!.
- أنه عالم السر وأخفى فكيف لي بفعل ما تأمرني به وهو لا يغفى عليه١؟! ولهذا يبين الشيخ أن من عرف الله معرفة حقة، وعرف عظمته لم يشرك به، فيقول عند قوله تعالى: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيَامَةِ وَالسَّمَاوَاتُ مَطْوِيَّاتٌ بِيَمِينِهِ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ} ٢.
ففي الصحيح أن رسول الله "صلى الله عليه وسلم" قرأها على المنبر وقال: "إن الله يقبض يوم القيامة الأرضين وتكون السماوات بيمينه"، ثم ذكر تمجيد الرب تبارك وتعالى نفسه، وأنه يقول:- "أنا الجبار أنا المتكبر أنا الملك أنا العزيز أنا الكريم" قال ابن عمر فرجف برسول الله صلى الله عليه وسلم حتى قلنا ليخرن به٣.
ويستنبط الشيخ من هذه الآية التنبيه على سبب الشرك، وهو أن المشرك بان له شيء من جلالة الأنبياء والصالحين، ولم يعرف الله سبحانه وتعالى، وإلا لو عرفه لكفاه وشفاه عن المخلوق، وهذا معنى قوله: {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ} الآية٤.
_________
١ مؤلفات شيخ/ القسم الرابع/ التفسير ص "٥٩، ٦٠".
٢ سورة الأنعام: آية "٩١".
٣ سبق تخريجه ص "٦٠".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٤٥، ٣٤٦".',NULL,'153',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (162,'وأما في مجال إيضاح توحيد الأسماء والصفات:
فقد اهتم به الشيخ في تفسيره، نظراً لكثرة وروده وتقريره وتأكيده في كتاب الله تعالى، فكان الشيخ تبعاً لذلك يبين هذا القسم من التوحيد، ويهتم به، ويرى أنه أصل العلوم١.
فلذا يجب تعلمه والقيام بحقوقه، لأنه لا يستقيم للمرء توحيد الربوبية، وتوحيد الألوهية إلا بالإقرار بالصفات.
وهو يسير في تفسير، لآيات الأسماء والصفات على منهج السلف الصالح، أهل السنة والجماعة.
فعند تفسير سورة الفاتحة يبين الشيخ شيئاً مما يتعلق بالأسماء والصفات، وهو أن الله تعرف إلى عباده بأسمائه وصفاته فيقول:
....فذكر الله في أول هذه السورة التي هي أول المصحف الألوهية والربوبية والملك في قوله: {الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ} وقوله: {مَالِكِ يَوْمِ الدِّينِ} كما ذكره في آخر سورة في المصحف: {قُلْ أَعُوذُ بِرَبِّ النَّاسِ. مَلِكِ النَّاسِ إِلَهِ النَّاسِ} فهذه ثلاثة أوصاف لربنا تبارك وتعالى ذكرها مجموعة في موضع واحد في أول القرآن ثم ذكرها مجموعة في موضع واحد في آخر ما يطرق سمعك من القرآن، فينبغي لمن نصح نفسه أن يعتني بهذا الموضع، ويبذل جهده في البحث عنه، ويعلم أن العليم الخبير لم يجمع بينهما في أول، القرآن ثم في آخره إلا لما يعلم من شدة حاجة العباد إلى معرفتها، ومعرفة الفرق بين هذه الصفات، فكل صفة لها معنى غير معنى الصفة الأخرى٢.
ويقول عند الكلام في موضع آخر على سورة الفاتحة أيضاً منبهاً على ما فيها مما يتعلق بالأسماء والصفات:
وفي سورة الفاتحة معرفة الله على التمام، ونفى النقائص عنه تبارك وتعالى، وفيها معرفة الإنسان ربه، ومعرفة نفسه، فإنه إذا كان هنا رب فلا بد من مربوب، و، إذا كان هنا راحم فلا بد من مرحوم، وإذا كان هنا
_________
١ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٣٧٢".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص"١١،١٢".',NULL,'154',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (163,'مالك فلا بد من مملوك، وإذا كان هنا عبد فلا بد من معبود، وإذا كان هنا هاد فلا بد من مهدى، وإذا كان هنا منعم فلا بدمن منعم عليه، وإذا كان هنا مغضوب عليه فلا بد من غاضب، و، إذا كان هنا ضال فلا بد من مضل.
فهذه السورة تضمنت الألوهية، والربوبية، ونفي النقائص عن الله عز وجل، وتضمنت معرفة العبادة وأركانها١.
فانظر- يرحمك الله- إلى هذا النظر المستنير إلى ما تضمنته هذه السورة من مباحث تتعلق بالأسماء والصفات وغيرها، إذ أن هذه الجمل الشرطية معتمدة على فهم آيات الفاتحة ومتمشية معها، فتأمل.
ومن اهتمامه باستنباط ما يتعلق بتوحيد الأسماء والصفات من الآيات أن ميزه عن غيره من العلوم وأفرده بالذكر وقدمه عند تصنيفه لصدر سورة هود، وذكر ما فيه من العلوم حيث قال:
ذكر ما في صدر سورة هود من العلوم:
الأول: علم معرفة الله:
الأولى٢: ذكر أنه حكيم.
الثانية: أنه خبير.
الثالثة أنه قدير.
الرابعة: أنه ذكر شيئاً من تفصيل العلم في قوله: {أَلا إِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُ أَلا حِينَ يَسْتَغْشُونَ ثِيَابَهُمْ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ} ٣.
الخامسة: ذكر شيء من تفصيل القدرة، في قوله: {وَمَا مِنْ دَابَّةٍ
_________
١ مؤلفات الشيخ/ القسم الأول/ بعض فوائد سورة الفاتحة ص "٣٨٤".
٢ ليست في الأصل وزدتها للبيان.
٣ سورة هود: آية "٥".',NULL,'155',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (164,'فِي الْأَرْضِ إِلَّا عَلَى اللَّهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا كُلٌّ فِي كِتَابٍ مُبِينٍ} ١
السادسة: خلق السماوات والأرض في ستة أيام.
السابعة: كون عرشه على الماء.
الثامنة: ذكر شيء من تفصيل الحكمة في قوله: {لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاً} ٢.
التاسعة- كونه وكيلاً على كل شيء٣.
والشيخ من واقع منهجه السلفي يوضح أحياناً معاني بعض الأسماء والصفات الواردة في القرآن الكريم، فإن معانيها معلومة لأنها بلسان عربي مبين، أما الكيفية فلا يعلمها إلا الله تبارك وتعالى.
يقول الشيخ:
"الرحمان الرحيم": اسمان مشتقان من الرحمة، أحدهما أبلغ من الآخر مثل العلام والعليم. قال ابن عباس: هما اسمان رقيقان أحدهما ارق من الآخر أي أكثر من الأخر رحمة.
ويقول: "وأما قوله: {لِلَّهِ رَبِّ الْعَالَمِينَ} فالله علم على ربنا تبارك وتعالى ومعناه: الإله أي المعبود لقوله: {وَهُوَ اللَّهُ فِي السَّمَاوَاتِ وَفِي الْأَرْضِ} أي المعبود في السماوات والمعبود في الأرض {إِنْ كُلُّ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ إِلَّا آتِي الرَّحْمَنِ عَبْداً} الآيتين٤.
وأما الرب فمعناه المالك المتصرف ٥.
ويقول: قوله: {إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٌ} ٦ أي "عليم" بمن يصلح للاجتباء "حكيم" يضع الأشياء في مواضعها٧.
_________
١ سورة هود: آية "٦".
٢ سورة هود: آية "٧".
٣ مؤلفات الشيخ/ القسم الرابع/ ص "١١٦".
٤ سورة مريم: آية "٩٣، ٩٤".
٥ مؤلفات الشيخ/القسم الرابع التفسير ص "١١".
٦ سورة يوسف: آية "٦".
٧ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٢٩".',NULL,'156',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (165,'ويقول: إنه الحكيم الخبير فلا يتصور أنه يشتبه عليه من يعصيه بمن يطيعه، ولا يتصور أنه يجعل من أطاعه كمن عصاه، لأنه الحكيم الخبير يضع الأشياء في مواضعها١.
ويقول في تفسير سورة "الإخلاص" وما تضمنته من الأسماء والصفات:
و"الأحد" الذي لا نظير له.
و"الصمد" الذي تصمد الخلائق كلها إليه في جميع الحاجات، وهو الكامل في صفات السؤدد، فقوله: "الصمد" إثبات صفات الكمال.
وفى قوله: {لَمْ يَلِدْ وَلَمْ يُولَدْ} نفي الصاحبة والعيال.
وفي قوله: {وَلَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ} نفي الشركاء لذي الجلال٢.
وقد وضح الشيخ في تفسيره من خلال الآيات مرارا أن العلم والقدرة هما أصل الأسماء والصفات لله تعالى.
فقال عند قوله تعالى"إخباراً عن إبراهيم": {إِنِّي وَجَّهْتُ وَجْهِيَ لِلَّذِي فَطَرَ السَّمَاوَاتِ وَالْأَرْضَ حَنِيفاً وَمَا أَنَا مِنَ الْمُشْرِكِينَ. وَحَاجَّهُ قَوْمُهُ قَالَ أَتُحَاجُّونِّي فِي اللَّهِ وَقَدْ هَدَانِ وَلا أَخَافُ مَا تُشْرِكُونَ بِهِ إِلَّا أَنْ يَشَاءَ رَبِّي شَيْئاً وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْماً أَفَلا تَتَذَكَّرُونَ} ٣ ما نصه:-
- سعة العلم وما قبله سعة القدرة، وهاتان هما اللتان خلق العالم العلوي والسفلي لأجل معرفتنا لهما.
- أن من ادعى معرفتهما وأشكل عليه التوحيد فعجب ولذلك قال: {أَفَلا تَتَذَكَّرُونَ} ٤.
ويركز من خلال الآيات أيضاً على الاستفادة العملية التي يثمرها علم الأسماء والصفات، وهي من لوازم هذا التوحيد لمن عرف معرفة حقه، فإن فائدة العلم العمل.
_________
١ المرجع السابق ص "٦١" ونحوه ص "٦٢".
٢ المرجع السابق ص "٣٨٧".
٣ سورة الأنعام: الآيتان "٧٩، ٨٠".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٦٥" وانظر ص "٣٦٦،٣٦٧".',NULL,'157',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (166,'فمن أوجه الاستفادة العملية:- ما تورثه هذه المعرفة من إجلال الله وتعظيمه ومحبته، فأسماء الله سبحانه دالة على صفاته، ومعرفة صفاته مستلزمة عبادته كما قال تعالى: {وَرَسُولِهِ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ} ١.
فاستنبط الشيخ من هذه الآية: الأمر بالتقوى في هذا الموضع أي النهي عن التقدم بين يدي الله ورسوله:- والاستدلال بالأسماء الحسنى على المسألة٢ وذلك أن من علم أن الله "سَمِيعٌ عَلِيمٌ" أثبت لله صفة "السمع والعلم"، فإذا آمن بهما حقا فذلك مستلزم لتقوى "السميع والعليم" ومراقبته لئلا يسمع منه ولا يعلم إلا خيرا.
ومن خلال، الفهم الصحيح للآيات، والاستنباط الدقيق منها، المهتدى بالمنهج السلفي في التفسير نجد أن الشيخ يرى أن الأسماء والصفات قواعد كليه يستدل بها على جزئيات كثيرة، وأنه يستدل بها على ما أشكل على الإنسان من القدرة ونحوها، والاستدلال بصفات الله على أفعاله.
فيقول مستنبطاً من قوله تعالى: {وَدَّ كَثِيرٌ مِنْ أَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ إِيمَانِكُمْ كُفَّاراً حَسَداً مِنْ عِنْدِ أَنْفُسِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ فَاعْفُوا وَاصْفَحُوا حَتَّى يَأْتِيَ اللَّهُ بِأَمْرِهِ إِنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ} ٣.
وقوله: {إِنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ} فيه:-
الاستدلال بالصفات على الأفعال.
والاستدلال بالقدرة على ما لا يظن وقوعه.
والاستدلال بها على جعل العفو سببا لعز العافي وذل المعفو عنه.
ولكن أحسن ما فيها تنبيه أعلم الناس على أشكل المسائل بقوله: {إِنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ} ٤.
_________
١ سورة الحجرات: آية "١".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٣٥٠".
٣ سورة البقرة: آية "١٠٩".
٤ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٢٧".',NULL,'158',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (167,'ويقول عند قوله تعالى: {وَكَذَلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا أَهَؤُلاءِ مَنَّ اللَّهُ عَلَيْهِمْ مِنْ بَيْنِنَا أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ} ١ ما نصه:-
- المسألة العظيمة الكبيرة، وهي الاستدلال بصفات الله على ما أشكل عليك من القدرة، لأنه سبحانه رد عليهم ما وقع في أنفسهم من استبعاد كون الله حرمهم، وخص هؤلاء بالكرامة.
- جلالة هذه المسألة، وهى مسألة علم الله، لأنه سبحانه رد بها على الملائكة لما قالوا: {أَتَجْعَلُ فِيهَا مَنْ يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ} الآية٢ ورد بها على الكفار الجهال في هذه الآية كما ترى٣.
ومن خلال الآيات أيضاً يركز الشيخ على جانب آخر من الاستفادة العملية من معرفة الأسماء والصفات وهو دعاء الله عز وجل بها، والتوسل إليه بأسمائه وصفاته، كما أمر الله عز وجل بقوله: {وَلِلَّهِ الْأَسْمَاءُ الْحُسْنَى فَادْعُوهُ بِهَا} ٤ ولهذا ترجم الشيخ بهذه الآية في "كتاب التوحيد" فقال باب قول الله تعالى: {وَلِلَّهِ الْأَسْمَاءُ الْحُسْنَى فَادْعُوهُ بِهَا وَذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمَائِهِ} الآية٥.
وكان أنبياء الله وأولياؤه يتوسلون إلى الله بأسمائه وصفاته كما أخبر عنهم فقال: {وَإِذْ يَرْفَعُ إِبْرَاهِيمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَإِسْمَاعِيلُ رَبَّنَا تَقَبَّلْ مِنَّا إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ. رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَا أُمَّةً مُسْلِمَةً لَكَ وَأَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا إِنَّكَ أَنْتَ التَّوَّابُ الرَّحِيمُ. رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولاً مِنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكِّيهِمْ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ} ٦.
_________
١ سورة الأنعام: آية "٥٣".
٢ سورة البقرة: آية "٣٠".
٣ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٥٨".
٤ سورة الأعراف: آية "١٨٠".
٥ مؤلفات الشيخ/ القسم الأول/ العقيدة/ كتاب التوحيد ص "١٢٤".
٦ سورة البقرة: الآيات "١٢٧-١٢٩".',NULL,'159',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (168,'وقد نبه الشيخ على هذا أيضاً عند قول الله تعالى إخباراً عن يوسف: {قَالَ رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِي إِلَيْهِ وَإِلَّا تَصْرِفْ عَنِّي كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ وَأَكُنْ مِنَ الْجَاهِلِينَ. فَاسْتَجَابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ} ١.
فقال- استفتاحه الدعاء بربه وقوله: {فَاسْتَجَابَ لَهُ رَبُّهُ} ٢.
فأشار بهذا إلى توسله إلى الله عز وجل بهذا الوصف وهو الربوبية "رب" ففيه معنى التربية والعناية، ومن كان بهذه الصفة فهو الذي يجأر إليه في الشدائد، ويدعى في الملمات، فكان من لطف هذا الرب المربي بالنعم التفضل على عبده بالإجابة، وصرف الكيد عنه وهذا من كمال عناية الرب تعالى بمربوبه. "والله أعلم ".
وهكذا يسير الشيخ في بيان آيات الصفات ومعانيها وما يستنبط منها على المنهج الصحيح فيقررها، ويبين معانيها، وأهميتها، وثمرتها، ولا يحرف معانيها أو يتاولها على غير تأويلها. "ولله الحمد والمنة".
_________
١ سورة يوسف: آية "٣٣، ٣٤".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٤٢".',NULL,'160',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (169,'ثانيا: اهتمامه بتوحيد القصد والطلب وهو توحيد الإلهية والعبادة.
وقد ركز عليه في تفسيره كثيرا مستنبطاً إياه من الآيات ومقررا له.
فعند قول الله تعالى: {الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ} يوضح الشيخ معنى الإله وأنه المعبود فيقول: إذا عرفت أن معنى الله هو الإله، وعرفت أن الإله هو المعبود، ثم دعوت الله أو ذبحت له أو نذرت له فقد زعمت أنه هو الله، فمن عرف أنه قد جعل "شمسان" أو "تاجا" ١. برهة من عمره هو الله عرف ما عرفت بنو إسرائيل لما عبدوا العجل، فلما تبين لهم ارتاعوا، وقالوا ما ذكر الله عنهم: {وَلَمَّا سُقِطَ فِي أَيْدِيهِمْ وَرَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قَالُوا لَئِنْ لَمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ} ٢.
فقد بين هنا معنى الإله وشيئاً من كيفية تحقيق العبودية لله تعالى. كما بين ضروبا من الشرك في العبودية تحذيرا منها وحماية لجناب، التوحيد. وعند قوله تعالى: {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} يوضح أن العبودية كمال المحبة، وكمال الخضوع والخوف والذل، فيقول: وأما قوله: {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} .
فالعبادة كمال المحبة، وكمال الخضوع، والخوف والذل، وقدم المفعول وهو إياك وكرر للاهتمام والحصر، أي لا نعبد إلا إياك، ولا نتوكل إلا عليك وهذا هو كمال الطاعة، والدين كله يرجع، إلى هذين المعنيين، فالأول: التبرؤ من الشرك والثاني: التبرؤ من الحول والقوة فقوله: {إِيَّاكَ نَعْبُدُ} أي، إياك نوحد ومعناه أنك تعاهد ربك أن لا تشرك به في عبادته أحدا ل املكا مقربا ولا نبيا ولا غيرهما كما قال للصحابة، {وَلا يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلائِكَةَ وَالنَّبِيِّينَ أَرْبَاباً أَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ إِذْ أَنْتُمْ مُسْلِمُونَ} ٣ فتأمل هذه الآية واعرف ما ذكرت لك في الربوبية أنها التي نسبت إلى "تاج ومحمد بن شمسان". فإذا كان الصحابة لو يفعلونها مع الرسل كفروا
_________
١ انظر ما تقدم ص "١٤٠" هامش "٢".
٢ سورة الأعراف: آية "١٤٩" وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص"١٢".
٣ سورة آل عمران: آية "٨٠".',NULL,'161',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (170,'بعد إسلامهم، فكيف بمن فعلها في تاج وأمثاله١؟.
وقوله: {وَإِيَّاكَ نَسْتَعِينُ} هذا فيه أمران أحدهما: سؤال الإعانة من الله وهو التوكل، "والثاني" التبريء من الحول والقوه، وأيضاً: طلب الإعانة من الله كما مرأنها من نصف العبد ٢.
فقد بين هنا العبادة، وأهميتها، وان الدين كله دائر بين معاني: {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ} ووضح معنى كل منهما.
ويوضح الشيخ عند قول الله تعالى إخبارا عن قول إبراهيم لأبيه: {أَتَتَّخِذُ أَصْنَاماً آلِهَةً} ٣ أن أعظم شيء عند المسلم آلهة ومعبودة فيقول: قوله: {أَتَتَّخِذُ أَصْنَاماً آلِهَةً} السؤال عن معنى الآلهه؟ فإنها جمع إله، وهو أعلى الغايات عند المسلم والكافر، فكيف يتغذ جمادا؟! وهذا أعجب وأبعد عن العقل من جعل الحمار قاضيا، لأن الحيوان أكمل من الجماد فإذا كان هذا من خشب أو حجر لم يعص الله فكيف بمن اتخذ فاسقا إلها مثل نمرود وفرعون؟! فإن كان اتخذه بعد موته فأعجب وأعجب٤.
ويوضح الشيخ في بداية تفسيره عند الكلام على الاستعاذة والبسملة بعض أنواع العبادة والإخلاص فيها فيقول: فمعنى "أعوذ بالله من الشيطان الرجيم" ألوذ بالله واعتصم بالله وأستجير بجنابه من شر هذا العدو أن يضرني في ديني أو دنياي، أو يصدني عن فعل ما أمرت به، أو يحثني على فعل ما نهيت عنه، لأنه أحرص ما يكون على العبد إذا أراد عمل الخير من صلاة أو قراءة وغير ذلك. وذلك أنه لا حيلة لك في دفعة إلا بالاستعاذة بالله لقوله تعالى: {إِنَّهُ يَرَاكُمْ هُوَ وَقَبِيلُهُ مِنْ حَيْثُ لا تَرَوْنَهُمْ} ٥ فإذا طلبت
_________
١ مؤلفات الشيخ/القسم الرابع/ التفسير ص "١٦".
٢ المرجع السابق ص "٨، ١٦".
٣ سورة الأنعام: آية "٧٤".
٤ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٦٤".
٥ سورة الأعراف: آية "٢٧".',NULL,'162',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (171,'من الله أن يعيذك منه، واعتصمت به، كان هذا سببا في حضور القلب، فاعرف معنى هذه الكلمة، ولا تقلها باللسان فقط كما عليه أكثر الناس.
وأما البسملة: فمعناها أدخل في هذا الأمر من قراءة أو دعاء، أو غير ذلك: "بِسْمِ اللَّهِ" لا بحولي ولا بقوتي، بل أفعل هذا الأمر مستعينا بالله متبركا باسمه تبارك وتعالى، هذا في كل أمر تسمى في أوله من أمر الدين أو أمر الدنيا فإذا أحضرت في نفسك أن دخولك في القراءة بالله مستعينا به، متبرئا من الحول والقوة، كان هذا أكبر الأسباب في حضور القلب وطرد الموانع من كل خير١.
فقد قرر رحمه الله خلال شرح معنى الاستعاذة والبسملة معنى الاستعاذة والاستعانة والالتجاء إلى الله عز وجل والإخلاص في ذلك وبين ما يترتب عليه من عون الله وتوفيقه وحمايته لعبده.
وعند قول الله تعالى: {الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ} يتعرض الشيخ لإيضاح أنواع الحمد المستغرق في الآية فيقول:- اعلم أن الحمد هو الثناء باللسان على الجميل الاختياري فأخرج بقوله: "الثناء باللسان" الثناء بالفعل الذي يسمى لسان الحال فذلك من نوع الشكر، وقوله: "على الجميل الاختياري" أي الذي يفعله الإنسان با رادته، وأما الجميل الذي لا صنع له فيه مثل الجمال ونحوه فالثناء به يسمى مدحا لا حمدا، والفرق بين الحمد والشكر: أن الحمد يتضمن المدح والثناء على المحمود بذكر محاسنه سواء كان إحسانا إلى الحامد أو لم يكن، والشكر لا يكون إلا على إحسان المشكور، فمن هذا الوجه الحمد أعم من الشكر، لأنه يكون على المحاسن والإحسان، فإن الله يحمد على ماله من الأسماء الحسنى، وما خلقه في الآخرة والأولي، ولهذا قال: {الْحَمْدُ لِلَّهِ الَّذِي لَمْ يَتَّخِذْ وَلَداً} ٢ الآية، وقال: {الْحَمْدُ لِلَّهِ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ} ٣ إلى غير ذلك من الآيات.
_________
١ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "٩".
٢ سورة الإسراء: آية "١١١".
٣ سورة الأنعام: آية "١".',NULL,'163',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (172,'وأما الشكر فإنه لا يكون إلا على الإنعام، فهو أخص من الحمد من هذا الوجه، لكنه يكون بالقلب واليد واللسان، ولهذا قال تعالى: {اعْمَلُوا آلَ دَاوُدَ شُكْراً} ١ والحمد إنما يكون بالقلب واللسان، فمن هذا الوجه الشكر أعم من جهة أنواعه، والحمد أعم من جهة أسبابه.
والألف واللام في قوله "الحمد" للاستغراق، آي جميع أنواع الحمد لله لا لغيره، فأما الذي لا صنع للخلق فيه مثل خلق الإنسان، وخلق السمع والبصر والسماء والأرض والأرزاق وغير ذلك فواضح، وأما ما يحمد عليه المخلوق مثل ما يثني به على الصالحين، والأنبياء، والمرسلين، وعلى من فعل معروفا، خصوصا إن أسداه إليك، فهذا كله لله أيضاً بمعنى أنه خلق ذلك الفاعل، وأعطاه ما فعل به ذلك، وحببه إليه، وقواه عليه، وغير ذلك من إفضال الله الذي لو يختل بعضها لم يحمد ذلك المحمود فصار الحمد لله كله بهذا الاعتبار٢.
فقد أوضح الشيخ أولا معنى كل من الحمد والشكر والعلاقة بينهما وفصل القول في ذلك تفصيلا وافيا بالغرض وذكر أنواع الحامد ودخولها في {الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ} . ثم بين أنها لله وحده، ووجه ذلك.
وخلال تفسير الشيخ لسورة الفاتحة يذكر بضرورة حضور الذهن عند قراءتها وذلك لما تثمره في قلب صاحبها من كمال العبودية لله تعالى، إذا قرأها بتدبر لمعانيها، وما تتضمنه من الدعاء فإنه لا يستجاب الدعاء من قلب غافل.
يقول الشيخ: فإذا علم العبد أنها- أي الفاتحة- نصفان: نصف لله، وهو أولها، إلى قوله: {إِيَّاكَ نَعْبُدُ} ونصف للعبد، دعاء يدعو به لنفسه وتأمل أن الذي علمه هذا هو الله تعالى، وأمره أن يدعو به، ويكرره في كل ركعة، وأنه سبحانه من فضله وكرمه ضمن إجابة هذا الدعاء إذا دعاه
_________
١ سورة سبأ: آية "١٣".
٢ مؤلفات الشيخ/ القسم الرابع/ التفسير ص "١٠،١١".',NULL,'164',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (173,'بإخلاص وحضور قلب تبين له ما أضاع أكثر الناس١.
ومن خلال القرآن ومحاجة المعاندين به يبين الشيخ أن تلك الآيات فيها ترسيخ توحيد الألوهية والتحذير من الشرك فيه فيقول عند قوله تعالى: {أَمْ تَقُولُونَ إِنَّ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطَ كَانُوا هُوداً أَوْ نَصَارَى قُلْ أَأَنْتُمْ أَعْلَمُ أَمِ اللَّهُ وَمَنْ أَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهُ مِنَ اللَّهِ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ} ٢.
إذا كان رسول الله "صلى الله عليه وسلم" وأصحابه والأئمة بعدهم قد أجمعنا أنهم ومن اتبعهم على الحق، ومن خالفهم فهو على الباطل، فنقول: هذه المسألة التي اختلفنا وإياكم فيها هل رسول الله "صلى الله عليه وسلم" وأصحابه على قولنا أو على قولكم؟ فإذا أقروا أن دعاء أهل القبور، والبناء عليها وجعل الأوقاف والسدنة عليها من دين الجاهلية، فلما بعث الله محمدا "صلى الله عليه وسلم" ونهى عن ذلك كله، وهدم البناء الذي جعلته الجاهلية على القبور، ونهي عن دعاء الصالحين، وعن التعلق بهم، وأمر بإخلاص الدعوة لله، وآمر بإخلاص الإستعانة لله، وبلغنا عن الله أنه يقول: {فَلا تَدْعُو مَعَ اللَّهِ أَحَداً} ٣ ومضى رسول الله "صلى الله عليه وسلم" وأصحابه والتابعون وأتباعهم والأئمة، وأصحابهم، على ذلك ولم يحدث هذا إلا بعد ذلك "أعني دعاء غير الله والبناء على القبور، وما يتبع ذلك من المنكرات، فكيف تقرون أن رسول الله "صلى الله عليه وسلم" وأصحابه والأئمة بعدهم على ما نحن عليه، ثم تنكرونه أعظم من إنكار دين اليهود والنصارى مع إقرار كم أنه الدين الذي عليه رسول الله صلى الله عليه وسلم وأصحابه والأئمة؟ أم كيف تنصرون الشرك وما يتبعه، وتبذلون في نصره النفس والمال مع إقراركم أنه دين الجاهلية المشركين؟!
_________
١ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٨".
٢ سورة البقرة- آية "١٤٠".
٣ سورة الجن: آية "١٨".',NULL,'165',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (174,'هذا هو الشيء العجاب لأجعل الآلهة إلهاً واحداً١.
فقد بين في هذه المحاجة للمبطلين أن النبي "صلى الله عليه وسلم" قد بعث بالحنيفية السمحة داعياً إلى التوحيد ومحذراً من الشرك وبين ذلك واستدل له ليهلك من هلك عن بينة ويحيى من حي عن بينة.
_________
١ مؤلفات الشيخ / القسم الرابع/ التفسير ص "٤٢، ٤٣".',NULL,'166',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (175,'<span data-type="title" id=toc-31>المطلب الثاني: اهتمامه بتقرير أركان الإيمان من خلال تفسيره</span>
إن الناظر في استنباطات الشيخ "رحمه الله" من الآيات القرآنية يرى أنه يهتم بالنص على ما تتضمنه تلك الآيات من ركائز الإيمان وأركانه، بل يجعل ذلك من أوليات ما ينظر إليه كي يثمر العلم العمل، فإن الأعمال داخلة في مسمي الإيمان كما ذكر الشيخ عند قوله تعالى: {وَاعْلَمُوا أَنَّ فِيكُمْ رَسُولَ اللَّهِ لَوْ يُطِيعُكُمْ فِي كَثِيرٍ مِنَ الْأَمْرِ لَعَنِتُّمْ وَلَكِنَّ اللَّهَ حَبَّبَ إِلَيْكُمُ الْأِيمَانَ وَزَيَّنَهُ فِي قُلُوبِكُمْ وَكَرَّهَ إِلَيْكُمُ الْكُفْرَ وَالْفُسُوقَ وَالْعِصْيَانَ أُولَئِكَ هُمُ الرَّاشِدُونَ} ١.
وقد ذكر الشيخ عند استنباطه من قصة آدم وإبليس أن هذه القصة قد اشتملت على أصول الإيمان الستة في حديث جبريل وذكر هذه الأصول في موضع واحد فقال ٢:
وفي القصة فوائد عظيمة وعبر لمن اعتبر بها، منها أن خلق آدم من تراب من أبين الأدلة على المعاد كما استدل به سبحانه في غير موضع "وهذه دلالة القصة على الإيمان باليوم الآخر".
ومنها أنها من أبين الأدلة على قدرته سبحانه وعظمته ورحمته وعقوبته و، إنعامه وكرمه وغير ذلك من صفاته "وهذه بعض دلالتها على الإيمان بالله".
ومنها: أنها من أدلة الرسل عامة، ومن أدلة نبوة محمد "صلى الله عليه وسلم" خاصة "وهذه دلالتها على الإيمان بالرسل".
ومنها: الدلالة على الملائكة وعلى بعض صفاتهم "وهذه دلالتها على الإيمان بالملائكة".
ومنها: الدلالة على القدر خيره، وشره "وهذه دلالتها على الإيمان بالقدر". أما دلالتها على الإيمان بالكتب فلعل ذلك لكون هذه القصة، إنما أتت عن طريق الكتب، ولم يأت بها البشر من عند أنفسهم ولم يشهدها أحد منهم فدل ذلك على أن الكتاب منزل من لدن حكيم خبير كما قال تعالى: {بَلْ هُوَ
_________
١ سورة الحجرات: آية "٧". وانظر مؤلفات الشيخ/ القسم الرابع/ التفسير ص"٣٥٣".
٢ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٨٤".',NULL,'167',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (176,'قُرْآنٌ مَجِيدٌ. فِي لَوْحٍ مَحْفُوظٍ} ١.
هذا وقد استنبط الشيخ في مواضع اخرى ما يتعلق بهذه الأركان الستة نصا عليها وتوضيحا لها. فأشير، إلى اهتمامه بترسيخ هذه الأركان من خلال القرآن وتفسيره.
أولا: الإيمان بالله:
أعظم ما نص عليه الشيخ في تفسيره، ونبه إليه، ووضحه الإيمان بالله تعالى "وقد أوضحت شيئاً من اهتمام الشيخ بهذا الجانب عند الكلام عن اهتمامه بتوحيد المعرفة والإثبات وتوحيد القصد والطلب من خلال تفسيره ففي ذلك الكفاية فليراجع ٢.
ثانيا: الإيمان بالملائكة:
من خلال الاستنباطات ينص الشيخ على ما يتعلق بالملائكة، وصفاتهم، ليرسخ الإيمان بهم في القلوب، وقد سبق أن ذكرت أنه ذكر في قصة آدم وإبليس الدلالة على الملائكة وعلى بعض صفاتهم٣، فهم عباد مكرمون، لا يعصون الله ما أمرهم، ويفعلون ما يؤمرون.
ومن خلال القرآن يوضح شيئاً من تلك الصفات التي أجملها هنا. فمن ذلك طاعتهم لله عز وجل وسجودهم لآدم، وتواضعهم طاعة لرب العالمين، كما قال: {إِذْ قَالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي خَالِقٌ بَشَراً مِنْ طِينٍ. فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ سَاجِدِينَ. فَسَجَدَ الْمَلائِكَةُ كُلُّهُمْ أَجْمَعُونَ} ٤.
وينص الشيخ على بعض صفاتهم فيقول: إن الملائكة لما أخبرهم الله تعالى بأنه جاعل في الأرض خليفة قالوا: {أَتَجْعَلُ فِيهَا مَنْ يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ} ٥ فقيل لهم ما قيل وعوتبوا بقوله تعالى: {إِنِّي أَعْلَمُ مَا لا تَعْلَمُونَ} ٦
_________
١ سورة البروج آية "٢١،٢٢".
٢ انظر ما تقدم ص "١٤٦" وما بعدها.
٣ انظر ما تقدم ص "١٣٩".
٤ سورة "ص" الآيات "٧١- ٧٣" وانظر مؤلفات الشيخ/القسم الرابع/التفسير ص"٨٣،٩١،١٨٨".
٥ سورة البقرة آية "٣٠".
٦ سورة البقرة آية "٣٠".',NULL,'168',NULL,NULL,'0');
INSERT INTO [b12102] VALUES (177,'فكانت توبتهم أن قالوا: {قَالُوا سُبْحَانَكَ لا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا} ١ فكان كما لهم، ورجوعهم عن العتب، وكمال علمهم أن أقروا على أنفسهم بالجهل إلا ما علمهم سبحانه فقالوا: {قَالُوا سُبْحَانَكَ لا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا إِنَّكَ أَنْتَ الْعَلِيمُ الْحَكِيمُ} ٢.
وعند قوله تعالى: {وَلِلَّهِ يَسْجُدُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ مِنْ دَابَّةٍ وَالْمَلائِكَةُ وَهُمْ لا يَسْتَكْبِرُونَ. يَخَافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ} ٣ مما فيه تفسير لقوله تعالى: {لا يَعْصُونَ اللَّهَ مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ} ٤ يستنبط ما دلت عليه هاتان الآيتان، من صفاتهم فيقول: سجود الملائكة، وعدم استكبارهم مع شرفهم، وخوفهم منه مع ذلك، وأنهم مع ذلك الخوف كاملوا الانقياد فيما أمروا ٥.
ويستنبط من قوله تعالى: {الَّذِينَ تَتَوَفَّاهُمُ الْمَلائِكَةُ ظَالِمِي أَنْفُسِهِمْ} ٦.
الآية: أن ملك الموت له أعوان يتوفون٧.
ويستنبط من قوله تعالى: {قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ} ٨ أن روح القدس هو جبرائيل٩.
ووجه هذا الاستنباط أنه من المعلوم أن الذي ينزل بالقرآن على النبي صلى الله عليه وسلم هو جبرائيل فلما ذكر سبحانه في هذه الآية أن الذي نزله هو روح القدس علم أن روح القدس هو جبرائيل.
_________
١ سورة البقرة: آية "٣٢".
٢ مؤلفات الشيخ/القسم الرابع/ التفسير ص "٩٤".
