INSERT INTO [b12318] VALUES (411,'عليه وسلامه قال [٧٠] أنا دابة الأرض أنا كذا أنا كذا والله أعلم وقيل عبد الله بن الزبير دابة الأرض،
<span data-type="title" id=toc-110>ذكر الدخان</span>
