INSERT INTO [b12404] VALUES (627,'٣٠٠، ٤٠٧، ٤٠٨، ٤٥٩، ٤٩٧، ٥٣٩ مضيعة ابن الحكم: ١٣٨ معدن النحلة: ٥٥٦ معلاة: ٣٣٩ مقبرة بني يشكر: ٢٧٧ مقابر الخيزران: ٣١٦ مقابر قريش: ٤١٧ مكة: ٤١، ٤٣، ١٢٦، ١٨٨، ١٩١، ٢٢٦، ٢٣٧، ٢٥٣، ٢٥٩، ٢٦٣، ٢٦٤، ٢٧٤، ٣٥١، ٣٦٥، ٣٦٧، ٣٧٧، ٣٨٢، ٣٨٩، ٤٢٣، ٤٣٥، ٤٣٩، ٤٤٠، ٤٤٧، ٤٧٩، ٤٨٤، ٥٢٤، ٥٢٦، ٥٣٢، ٥٤٧، ٥٥٢، ٥٦٣ منى: ٣٥٠ المنصورة: ٢٧٠ مهران: ٢٧١ الموبد: ٢٨٦ الموصل: ١٣٢، ٢٧٤ موضع السقاية: ٢٣١ ميطان (جبل) : ٢٦٧ (ن) النخيلة: ٦٩، ٧٠، ٧٦، ٧٧ نسا: ٤٦٤، ٤٦٧، ٤٦٩، ٥٥٤ النظيم: ٣٣٩ نهر صرصر: ٤٤١ نهر آبان: ٣٠٩، ٢٣١٧ النهروان: ٤٣، ٤٦، ٤٣٧، ٤٥٢ نهري كربلاء: ١٤٦ النهرين: ١٤٦، ٤٢٦، ٤٥٢ النوبة: ٥٥٧ نيسابور: ٢٧٥، ٤٦٤، ٤٦٨، ٤٦٩، ٤٩٠، ٥٣٦، ٥٤٠، ٥٥٨ النيل: ٢٧٤، ٤٣٣ نينوى: ١٤٦، ٤٢٧ (هـ) الهاشمية: ١٧١، ١٧٢، ١٧٤، ٢٠٢، ٣٧٣ هراة: ١٤٩، ١٥٨ الهند: ٢٦٩ (و) الوادي: ٥٥٣ وادي القرى: ٥٥٣، ٥٦١، ٥٦٤ الوازار: ٤٣١، ٥٠٧ واسط: ١٣٢، ٢٧٤، ٢٧٥، ٢٨٦، ٤٣٥، ٣٠٧، ٣١٠، ٤٧٢، ٤٩٧، ٤٩٨، ٥٢١ الوضاح: ٣١٠ وليلى: ٤٠٩ (ي) الياسرية: ٤٣٧ اليمن: ٧٣، ١٩٠، ٢٦٣، ٤٢٣، ٤٣٥، ٥٦٢، ٥٦٥ اليمامة: ٥٥٢ ينبع: ١٨٩، ٢٠٥، ٣٣٠، ٣٣٧، ٥٥٧',NULL,'641',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (628,'<span data-type="title" id=toc-220>فهرس الأيام</span>
غزوة بني المصطلق: ٦٤ غزوة مؤتة: ٣٠، ٣١ واقعة السوس: ٤٢٣ يوم بدر: ٣١٣ يوم الثنية: ٢٢٠ يوم الجمل: ٦٤، ٣٢٥ يوم الجوزجان: ٣٢١ يوم الحرة: ١٢٢ يوم حنين: ٢٢٥٠ يوم الزاب: ١٥٨، ٢٣٣ يوم السبخة: ١٢٩، ٣٢١ يوم الشعب: ٣٩٨ يوم شعب جبلة: ٣٢٢ يوم صفين: ٣١٥، ٣٢٠ يوم الطف: ٩٨، ٣٢٠ يوم قنطرة الكوفة: ٤٢٢ يوم المدار: ٩٢، ١٢٣ يوم المريسع: ٦٤',NULL,'642',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (629,'<span data-type="title" id=toc-221>فهرس الشعر</span>
(أ) أحق الناس.. بكربلاء ٨٩ فإن يك.. التراب ٥٥ لعمرك إنني.. والرباب الحسين: ٩٤ ما ذكرك.. أو قربوا ٢٠٣ قد علمت.. بطل مجرب مرحب اليهودي: ٢٤ بأي يد.. قاضب سعيد بن حميد: ٤٨٩ أوقر.. المحجبا ١١٩ أنا علي.. بالنبي علي بن الحسين: ١١٥ إن كان.. سيرة النبي سلمة بن أسلم الجهني: ٢١٥ ما زال.. كثرة الألقاب ٥٥ فإن الأولى.. وعمهم أبى موسى بن عبد الله: ٣٨٢ ألم تعلمي.. ينعم صاحبه إبراهيم بن عبد الله: ٢٧٣ (ت) مررت على.. يوم حلت سليمان بن قتة: ١٢١ (ج) أمامك فانظر.. وأعوج ابن الرومي: ٥١١ (ح) ان ابن.. شاكي السلاح ١٥٥',NULL,'643',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (630,'ألا يا لقوم.. ببلدح ٣٨٥ (خ) ألا ليت.. يوم فخ ٣٨٣ (د) يا أبا الحسين.. منها يكمد أبو ثميلة الأبار: ١٤٤ إذا سلكت.. لها الفرقد عمر بن أبي ربيعة: ٤٠٦ إن الذي.. تجردا سلمة بن أسلم الجهني: ٢١٥ نبئت أن.. لتقتل خالدا خالد بن جعفر بن كلاب: ٣٢٢ نظرت.. محسورة جدا محمد بن صالح بن عبد الله: ٤٨٧ ارحم صغار.. لا لفقد يزيد الحسن بن معاوية: ٢٦٥ بكر النعي.. والسؤدد أبو الجاج الجهني: ٢٦٦ ليهنكم.. مهتدي ٢١٧ منخرق الخفين.. مرو حداد ٢٠٥، ٢٧٠ وكيف يريد.. من الفؤاد عبد الرحمن بن مسعود: ١٦٥ فلعل.. خضم مزبد أبو ثميلة الأبار: ١٤٥ آلا يا عين.. الجمود فضل بن العباس: ١٤٣ وقل للذي.. فكأن قد ٦٣ أريد حياته.. من مراد ١٠٢، ١٦٤ والله ما أطعم.. عيون العباد عيسى بن زيد: ٣٤٨ شردني.. ذكر المعاد عيسى بن زيد: ٣٤٩ تعلم يابن.. من معد زينب بنت عبد الله: ٣٦٤ يا بقعة.. من سيد ٤٠٥ وقتيل.. كل شاهد غالب بن عثمان الهمداني: ٣٢٩ تفرقت.. ما يصيد ابن معاوية: ١٥٦ (ر) أبنت أبي.. احدى الكبائر الربيع بن سليمان: ٢٣٧ فألقت عصاها.. بالأياب المسافر معقر بن أوس: ٣٠٣',NULL,'644',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (631,'قومي اضربي.. إليه المفاخر محمد بن يسير الخارجي: ٢٠٨ إذا افتقرت.. أبدا فقر عبد الله بن معاوية: ١٥٤ عين جودي.. غزير سكينة بنت الحسين: ١٣٣ وعند غني.. وتذكر سليمان بن قتة: ٩٢ فو الله ما أدري.. أتعذر طارق الخزاعي: ٦٤ لعمرك إني.. حتقها تتحفر أمية بن الأسكر: ٦٣ يا قبر سيدنا.. يا قبر ٥٦ فألقت عصاها.. المسافر ٥٥ أتظن يا إدريس.. فرار ٤٠٨ تنكرت الدنيا.. طيبها وسرورها أبو مالك الخزاعي: ١٥٩ رأيت بسامرا.. فتورها محمد بن صالح بن عبد الله: ٤٨٢ سأبكيك.. الوترا إبراهيم بن عبد الله: ٢٦٨ يا دار هجت.. ودارا غالب بن عثمان الهمداني: ٢٦٥ أقسمت.. شيئا نكرا مسلم بن عقيل: ١٠٦ وأنت الجواد.. ملأن الصدورا أعشى بني قيس بن ثعلبة: ٦٣ ونحن ضربنا.. فتقطرا ابن أبي مياس الفزاري: ٤٩ وما العود.. أن يتقطرا ٤١٢ لا تتركيني.. والغدر موسى بن عبد الله: ٣٣٧ إني رعيم.. فراسة للضرائر موسى بن عبد الله: ٣٣٧ كيف بعد.. الفراش الوثير غالب بن عثمان الهمداني: ٣٣٠ تقول ألا.. على الصبر دريد بن الصمة: ٢٦١، ٣٢١ أبو عامر.. حجرة المتكبر علي بن إبراهيم بن عبد الله: ٢٦١ لئن طال.. بالنظيم قصائر موسى بن عبد الله: ٣٣٩ يا لك من قبرة.. واصفري ١١١ وما في آل.. الخطب الكبير محمد بن صالح بن عبد الله: ٤٨٩ تعودت مس.. إلى الصبر ٣٥٩ ألف التقى.. المحل الداثر محمد بن صالح بن عبد الله: ٤٨٦، ٤٨٧ أربع بطوس.. على وطر دعبل: ٤٥٨ أنا الذي.. قسورة علي بن أبي طالب: ٤٠ قل لذي الود.. بيننا قدره عبد الله بن معاوية: ١٥٥',NULL,'645',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (632,'ما كان إلّا ريث.. سيوفا باترة ٤٢٩ (س) لما تعرضت.. وسواسا إبراهيم بن هرمة: ١٧٩ يا صاحب.. العيس أشجع بن عمرو السلمي: ٤٥٨ (ص) ... يا ليت قومي كلهم حنابصنا ٣٢٨ (ض) ومارست.. من الأرض ٤٤٤ (ط) إن قيسا.. على شمطه عبد الله بن معاوية: ١٥٣ وله شرطة.. من شرطه مطيع بن إياس: ١٥٣ (ع) إذا ما اشتملت.. القوارع محمد بن صالح بن عبد الله: ٤٨٢ تضوع مسكا.. يتضوع ابن الرومي: ٥٢٠ يا هند إنك.. تتابعا عبد الله بن الحسن: ٢٠٩ أبا المنازل.. فقد فجعا ٢٩٤، ٣٢١ إنك إن.. وتنفعا هند بنت أبي عبيدة: ٣٣٣ (ف) وإني لمرتاد.. إحدى المقاذف عبد الله بن موسى: ٥٠١ (ق) يا دار دار.. تستبق ٤٤٩ أني أتيح له.. مرسلا ساقا ٣٩٦ سنغني بحمد.. واضح الحق محمد بن إبراهيم: ٤٢٦ مهلا بني عمنا.. من الغلق ضرار بن الخطاب: ٣٢٠',NULL,'646',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (633,'من لم يمت.. والمرء ذائقها ٤٣١ خطبت إلى عيسى.. وعتيقها محمد بن صالح بن عبد الله: ٤٨٣ (ك) اشدد حيازيمك.. لاقيك عبد الرحمن بن ملجم: ٤٥ (ل) تزور.. فيما يحاول ابن هرمة: ٢٣٥ يا دهر.. والأصيل الحسين بن علي: ١١٣ هدت العيون.. الضباب المخضل كعب بن مالك: ٣٢ وقالوا الطالقان.. الدهر المديل مروان بن أبي حفصة: ٣٩٤ تدعى حواري.. سليل ٤٠٠ نفسي فداء.. لا قافل منصور بن الزبرقان النمري: ٤٢٧ وسل عن.. نزلوا الهيثم بن عبد الله الخثعمي: ٤٥١ رموني وإيّاها.. فعجلا محمد بن صالح بن عبد الله: ٤٨٥ يا قتيلا.. قتيلا سعيد بن محمد الأنصاري: ٥٥٨ ويوما على جمل ٨٢ لم ترعين.. ومن ناعل ٨٦ تسود قوم.. ابن جندل ٩١ واندبي إن.. بخذول سليمان بن قتة: ٩٥ إذا كنت.. وابن عقيل عبد الله بن الزبير الأسدي: ١٠٩ وسمى النبي.. مصقول سليمان بن قتة: ٩٦ ليت أشياخي.. وقع الأسل عبد الله بن الزبعري: ١١٩ أليس بعين.. في السلاسل ١٤٧ إنا لنرجو.. الكتاب المنزل سلمة بن أسلم الجهني: ٢١٥ سنّ ظلم ... ذو عقال الشميطي: ٣٥٤ لعمرك ما لام.. يخذل ابن أخطب اليهودي: ٣٩٣ إيها أبا إسحاق.. وعيش طويل سديف بن ميمون: ٢٧٢ ألم تر حوشبا.. لبني نفيلة ١٦٤ ألا تزغ.. من أجله عبد الله بن معاوية: ١٥٤',NULL,'647',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (634,'(م) قتلت أعز ... الكلام ابن الرومي: ٥٢٠ ألا أيها.. أنت حالم عويف القوافي: ٣٢٢ ومن يطلب.. تخترمه المخارم عمرو بن براقة الهمداني: ١٢٩ فإن يك يحيى.. وهو كريم ابن الرومي: ٥٢٠ بني عمنا.. يلمنا اللوائم موسى بن عبد الله: ٣٨١ لعمرك إن المجد.. لمقيم ٤٠٦ ألمت خناس.. وأحلامها عويف القوافي: ٣٢٢ وأبذل لابن.. في الناس مكرما ٤٢٥ يا صاحبي.. بألوم منكما عبد الله بن مصعب: ٢٦٧ أبي قومنا.. الدما الحصين بن الحمام: ١١٩ نفلق هاما.. وأظلما ١١٩ ستة آباء.. صوب الغمام النابغة: ٤٥٦ إن بني.. من أخزم أبو أخزم: ٥٤٨ وأبو الفضل.. من أسقام الكميت بن زيد: ٩٠ فلم أر مهرا.. وأعجم ابن أبي مياس الفزاري: ٥٠ لعمر حمدونة.. السقام ٤٨٣ يعجبني ... أم سلمة وحشي الرياحي: ٤٩٨ (ن) قناع الشك.. الرأي الرصين ٤٣٦ وبدا له ... لمعانه ٤٨٦ على الكره.. ورزين دعبل: ٤٥٩ ألا يا عين.. المؤمنينا أم الهيثم النخعية: ٥٥ زعم ابن مسعدة.. وبيانا إبراهيم بن عبد الله بن الحسن: ٢٦٩ سالت دموعك.. الأحزانا عبد الله بن مصعب: ٢٦٧ يا كيف.. سلوانا سعيد بن محمد الأنصاري: ٥٥٨ يا ضرية من.. رضوانا عمران بن حطان: ٥١ ما ضرّ تغلب.. تناطح البحران الفرزدق: ٢٧١ فلأبكين.. وعلى الحسن موسى بن عبد الله بن محمد: ٣٨٤',NULL,'648',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (635,'إني من القوم.. شدة الحدثان موسى بن عبد الله بن محمد: ٣٣٦ يا كذّب الله.. نعيه ثمن سليمان بن قتة: ٨٤ إن الحمامة.. دائم الحزن عبد الله بن مصعب: ٣٩٨ يا عين ابكي.. بنو حسن ٣٨٥ روعت بالبين.. وجيران مؤرج السدوسي: ٥١٢ يا بني أمية.. مرعش فان ٢٠٠ لا والذي.. في آخر الزمن إبراهيم بن هرمة: ٢١٦ ألا ليت أمي.. ولا الحسن ٣٨٣ طرب الفؤاد.. أشجانه محمد بن صالح بن عبد الله: ٤٨١ قوم كرام ... من ٣٨٤ (هـ) ما الانتظار.. من يحييها ١٠١ (ي) أحب مدحا.. حصورا عييا إبراهيم بن هرمة: ١٥٢ ألم يحزنك.. حيا محمد بن صالح بن عبد الله: ٤٨٧ رحم الله.. يوم الثنية ٢٢٠ وإن أحد.. مت وافيا أعشى بني قيس بن ثعلبة: ٦٨',NULL,'649',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (636,'<span data-type="title" id=toc-222>فهرس المصادر</span>
أبصار العين في أنصار الحسين ولمحمد بن طاهر السماوي النجف ١٣٤١ هـ ابن أبي الحديد القاهرة ١٣٢٩ هـ ابن الأثير بولاق ١٢٩٠ هـ ابن خلدون بولاق ١٢٨٤ هـ ابن خلكان القاهرة ١٣١٠ هـ أبو الفداء القاهرة ١٢٨٦ هـ اتقال المقال في أحوال الرجال النجف ١٣٤٠ هـ الارشاد في أسماء أئمة الهدى، للشيخ المفيد طهران ١٣٣٠ هـ الاستقصاء لأخبار المغرب الأقصى، لأحمد بن خالد السلاوي القاهرة ١٣١٢ هـ الاستيعاب في معرفة الأصحاب، لابن عبد البر حيدر آباد ١٣١٨ هـ أسد الغابة في معرفة الصحابة، لابن الأثير الجزري الاصابة في تمييز أسماء الصحابة، لابن حجر القاهرة ١٣٢٣ هـ الأغاني، لأبي الفرج الأصبهاني بولاق والدار أمثال الميداني القاهرة ١٣٤٢ هـ الإمامة والسياسة القاهرة ١٣٢٢ هـ أمالي القالي القاهرة ١٣٤٤ هـ الأنساب للسمعاني ليدن ١٩١٢ م البداية والنهاية، لابن كثير القاهرة ١٣٤٨ هـ البدء والتاريخ، للمطهر المقدسي باريس ١٩١٩ م بغية الوعاة، للسيوطي القاهرة ١٣٢٦ هـ البيان والتبيين، للجاحظ القاهرة ١٣٥١ هـ تاج العروس، للزبيدي القاهرة ١٣٠٦ هـ',NULL,'650',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (637,'تاريخ ابن عساكر (مخطوط) تاريخ ابن الجوزي (مخطوط) تاريخ الإسلام، للذهبي (مخطوط) تاريخ أصبهان ليدن ١٩٣١ م تاريخ بغداد، للخطيب البغدادي السعادة ١٩٣١ م تاريخ الخلفاء، للسيوطي القاهرة ١٣٥١ هـ تاريخ الوزراء، للصابي بيروت ١٩٠٤ م تاريخ اليعقوبي ليدن ١٨٨٣ م تذكرة الحفاظ الهند ١٣٣٣ هـ التنبيه والإشراف القاهرة ١٣٥٧ هـ تهذيب الأسماء واللغات، للنووي القاهرة ١٣٤٤ هـ تهذيب تاريخ ابن عساكر دمشق ١٣٣٢ هـ تهذيب التهذيب الهند ١٣٢٥ هـ جذوة الاقتباس في تاريخ فاس، لابن القاضي فاس ١٣٠٩ هـ حسن المحاضرة، للسيوطي القاهرة ١٣٢١ هـ شرح الحماسة، للتبريزي القاهرة ١٣٠٧ هـ حلية الأولياء القاهرة ١٣٥١ هـ الحور العين القاهرة ١٣٦٨ هـ الحيوان، للجاحظ القاهرة ١٣٦٥ هـ خزانة الأدب، للبغدادي بولاق ١٢٩٩ هـ خلاصة تذهيب الكمال، للخزرجي القاهرة ١٣٢٢ هـ الدر النفيس في مناقب إدريس فاس ١٣١٤ هـ ديوان ابن الرومي القاهرة ١٩١٧ م ديوان الأخطل بيروت ١٩٠٧ م ديوان الفرزدق باريس ١٨٧٥ م ذيل الأمالي القاهرة ١٣٤٤ هـ الروض النضير (مخطوط) الرياض النضرة في مناقب العشرة القاهرة ١٣٢٧ هـ زهر الآداب، للحصري القاهرة ١٣٥٠ هـ سمط اللآلي القاهرة ١٣٥٤ هـ',NULL,'651',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (638,'سيرة ابن هشام القاهرة ١٣٥٦ هـ سيرة أحمد بن طولون، للبلوي دمشق ١٣٥٨ هـ السيرة الحلبية القاهرة ١٣٢٩ هـ شرح شافية أبي فراس الهند شرح مقصورة حازم القاهرة ١٣٤٤ هـ شرح المواهب القاهرة ١٢٧٨ هـ الشريشي القاهرة ١٣١٤ هـ صفة الصفوة، لابن الجوزي الهند ١٣٥٦ هـ طبقات ابن سعد ليدن ١٣٢٢ هـ الطبري القاهرة ١٣٢٣ هـ العقد الفريد القاهرة ١٣٤٦ هـ عمدة القارئ القاهرة ١٣٤٨ هـ عيون الأخبار القاهرة ١٣٤٣ هـ عيون أخبار الرضا (مخطوط) الفخري القاهرة ١٩٤٥ م الفرق بين الفرق القاهرة ١٣٢٨ هـ فهرست ابن النديم القاهرة ١٣٤٨ هـ فهرست الطوسي النجف ١٣٥٦ هـ فوات الوفيات، لابن شاكر بولاق ١٢٨٣ هـ القسطلاني بولاق ١٣٠٠ هـ كتاب صفين، لنصر بن مزاحم القاهرة ١٣٦٥ هـ لسان العرب القاهرة ١٣٠٠ هـ لسان الميزان، لابن حجر الهند ١٣٣٠ هـ لطائف المعارف، للثعالبي ليدن ١٨٦٧ م المؤتلف والمختلف، للآمدي القاهرة ١٣٥٤ هـ مجموعة المعاني الجوائب ١٣٠١ هـ المحبر، لابن حبيب الهند ١٣٦١ هـ مرآة الجنان لليافعي حيدرآباد مروج الذهب، للمسعودي القاهرة ١٣٠٣ هـ مسلم القاهرة ١٣٤٩ هـ',NULL,'652',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (639,'مسند أحمد القاهرة ١٣١٣ هـ مشارق الأنوار، للقاضي عياض القاهرة ١٣٣٢ هـ المعارف، لابن قتيبة القاهرة ١٣٥٣ هـ معجم الأدباء، لياقوت القاهرة ١٣٥٧ هـ معجم البلدان، لياقوت القاهرة ١٣٢٣ هـ المفضليات القاهرة ١٣٦٢ هـ مقتل الحسين، لأبي مخنف (مخطوط) الملل والنحل القاهرة ١٢٨٨ هـ الملهوف على قتلى الطفوف العرفان ١٢٧٩ هـ مناقب الأئمة الاثني عشرية (مخطوط) منتهى المقال في أحوال الرجال الهند ١٣٠٢ هـ ميزان الاعتدال السعادة ١٣٢٥ هـ نزهة الألباء القاهرة ١٢٩٤ هـ نوادر القالي القاهرة ١٣٤٤ هـ الوحشيات، لأبي تمام (مخطوط) الوزراء والكتاب، للجهشياري القاهرة ١٣٥٧ هـ',NULL,'653',NULL,NULL,'0');
INSERT INTO [b12404] VALUES (640,'<span data-type="title" id=toc-223>فهرس الكتاب</span>
مقدمة الكتاب ٥ خطبة المؤلف ٢٣ جعفر بن أبي طالب ٢٥ محمد بن جعفر بن أبي طالب ٣٥ علي بن أبي طالب ٣٩ الحسن بن علي بن أبي طالب ٥٧ الحسين بن علي ٨٤ مسلم بن عقيل بن أبي طالب ٨٦ علي بن الحسين (الأكبر) ٨٦ عبد الله بن علي بن أبي طالب ٨٧ جعفر بن علي بن أبي طالب ٨٨ عثمان بن علي بن أبي طالب ٨٩ العباس بن علي بن أبي طالب ٨٩ محمد بن علي بن أبي طالب (الأصغر) ٩٠ أبو بكر بن علي بن أبي طالب ٩١ أبو بكر بن الحسن بن علي بن أبي طالب ٩٢ القاسم بن الحسن بن علي بن أبي طالب ٩٢ عبد الله بن الحسن بن علي بن أبي طالب ٩٣ عبد الله بن الحسين بن علي بن أبي طالب ٩٤ عون بن عبد الله بن جعفر بن أبي طالب (الأكبر) ٩٥ محمد بن عبد الله بن جعفر بن أبي طالب ٩٥ عبيد الله بن عبد الله بن جعفر بن أبي طالب ٩٦',NULL,'654',NULL,NULL,'0');
