-- Insert data for b12527
INSERT INTO [b12527] VALUES (1,'مسائل حرب الكرماني

<span data-type="title" id=toc-1>١ - [باب]</span>
[...] وسلم قال ((أيما امرأة تزوَّجت من غيرِ وليها, فنكاحُها باطل)) , فإنْ كان دَخَلَ بها كان لها الصداق بما استحل مِنْ فَرْجِها, وفَرَّق بينهما, وإنْ كان لم يدخل بها فرّق بينهما, والسلطانُ ولي مِنْ لا ولي له.','1','171',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (2,'وسمعتُ إسحاقَ أيضاً يقول: وذكر عن مالك بن أنس أنه كان يُرخص في المرأة إذا لم يُعرف لها نسب أن يزوجها المسلمون, قال إسحاق: وربما ملتُ إلى ذلك في حالِ الضرورة, وذكر عن عمر أنه قال: كان يزوجها السلطان أو ذو الرأي من أهلها.
قلتُ لإسحاقَ رضي الله عنه: إن قبلنا ليس اليوم حاكم, وربما خُطبت المرأة وهي لقيط ليس لها أب, ولا يُعْرَف لها نسب, فكأنه رخَّص, ولكن لم يُصرح.
قلتُ لأحمدَ: فالأمير أحق, أو القاضي؟
قال: القاضي أحق؛ لأنه إليه الفروج والأحكام.
وسمعتُ أحمدَ مرةً أخرى يقول: القاضي يزوج, ولا يزوج الولي.
قيل لأحمد: فإن أهل الرساتق','1','172',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (3,'مثل المدائن, والأنبار, وليس لهم قاضي, كيف يصنعون؟
قال: واليهم لا يحكم بحكم القضاة, فلا يجوز إلا من ينظر في هذا.
قيل: فتبقى المرأة ليس لها حيلة؟
قال: كيف أصنع؟ الحديث هكذا! ولم يرخص فيه.
حدثنا أحمد, قال: حدثنا يحيى بن سعيد, عن التيمي, عن الحسن قال: لا','1','173',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (4,'نكاح إلا بولي.
قلتُ: يحرمها القاضي؟
قال: نعم
قلت لإسحاق: ليس عندنا قاضي, فدعا الوالى رجلاً, فقال قد أمرتك كلما جاءتك امرأة ليس لها ولي أن تُزوجها.
قال: هذا جائز، ورخَّص فيه.
قلتُ لأحمدَ: فامرأة لها ولي, ولم يزوجها, هل تأتي السلطانَ فيزوجها؟
قال: إذا كان كفؤاً.
سألت أحمدَ: قلت: امرأةٌ ليس لها ولي, ولكن إذا انتسبت أنا إلى خمسة آباء, ثم انتسبت هي إلى خمسة آباء التقينا في النسب, هل أزوجها؟
قال: نعم أنت','1','174',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (5,'عصبتها إذا لم يكن ولي أقرب منك.','1','175',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (6,'<span data-type="title" id=toc-2>٢ - باب: الأب أحق أم الابن</span>؟
قلتُ لأحمدَ: فالأب أحق أم الابن؟
قال: الأب أحب إلي.
قلتُ: ثم الابن؟
قال: نعم.
حدثنا المسيب بن واضح، قال، قال أبنا المبارك: سمعتُ سفيانَ يقول: الابن أحق بتزويج الأم من الأب؛ لأن الابن عصبتها.','1','176',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (7,'<span data-type="title" id=toc-3>٣ - باب المرأة تتزوج بغير ولي فأجاز الولي النكاح</span>.
سألتُ أحمدَ قلتُ: امرأة تزوجت بغير ولي, ثم أراد الولي أن يجيز النكاح؟
قال: بنكاح جديد ومهر وخِطبة جديدة, ولا يجوز أن يقول قد أجزت ذلك النكاح.
وسئل أحمد مرة أخرى: عن امرأة تزوجت بغير إذن ولي, ثم بلغ الولي فأجاز؟
قال: لا ولكن يجدد النكاح.
وسألت إسحاق قلتُ: امرأة ولت أمرها رجلاً, فزوجها من رجل وليها غائباً, ثم قدم الولي فأجاز /٢/ النكاح من غير أن يجدد النكاح, أو من','1','177',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (8,'غير شهود؟
قال: حيث أجاز الولي جاز ذلك النكاح حينئذ وإن لم يجدد النكاح, والتجديد كان أحب إلي, فأما إذا أجاز فعل الذي أنكح جازه.
حدثنا أبو معن، قال: ثنا عبد الرحمن، قال: حدثنا مالك بن أنس، قال: إذا أجاز الولي, فأحب إلى أن يستأنف النكاح.','1','178',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (9,'<span data-type="title" id=toc-4>٤ - باب المرأة يتزوجها وليها</span>.
قلتُ لأحمدَ: فولي امرأة أراد أن يتزوجها, كيف يصنع؟
قال: يولي رجلاً.
حدثنا محمد بن نصر، قال: حدثنا حسان، قال: قال سفيان: إذا خطب الرجل المرأة, وهو أولى الناس بها, فإنه يستحب أن يأمر رجلاً فيزوجه.','1','179',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (10,'حدثنا محمد بن إسحاق، قال: قال عبد الرحمن بن مهدي: ينظر إلى أولى الناس بها بعده, فيزوجها.
حدثنا محمد بن إسحاق، قال: حدثنا معاذ، عن الأشعث, عن الحسن, قال: إذا أراد ولي المرأة أن يتزوجها, استأذنها في نفسها، وأشهد عليها شاهدين ثم يتزوجها بشهادة شهود عدول.
وسئل إسحاق عن امرأة قالت لوليها: زوجني ممن شئت. فزوجها من نفسه؟
قال: يجوزُ عندنا, ولكن يُشهد شاهدين.','1','180',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (11,'<span data-type="title" id=toc-5>٥ - باب إذا زوجها وليان في يومٍ واحدٍ</span>
قيل لأحمد: امرأة زوجها وليان لها في يومٍ واحدٍ وساعة واحدة؛ لأنه لا يُدرى أيهما زوّج قبل, فهما بمنزلة واحدة.
قال: يفسخ النكاح إذا كانا في الولاية سواء.
قيل: فإن طلقها كل واحد منهما تطليقة؟
قال: هذا حسن, وكأنه ذهب إلى أنها واحدة؛ لمرامهم عنه القول الأخير حينئذ.
وسئل أحمد مرة أخري, قيل: وليان زوجا في يوم, وأشكل أيهما أول.
فذهب إلى أنه إذا كان أحدهما أولى من الآخر فهو أحق.','1','181',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (12,'حدثنا عباس النَّرسي، قال ثنا يزيد بن بزيع، قال: ثنا سعيد, عن قتادة, عن خلاس بن عمرو أَنَّ رجلا يُقال له عبيد الله بن الجر، تزوج امرأة بالجزيرة وزوجها أهلها بالكوفة, فدخل بها صاحب الكوفة, فرُفع ذلك إلى علي بن أبي طالب, فردها على عبد الله, وكان الأول مَنُهْما, وفَرَّق بينها وبين الزوج الآخر, وجعل لها صداقها لما أَصَاب منها, وقال لعبيد الله: لا تقربَها حتى تنقضي عدتُها.','1','182',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (13,'حدثنا المسيّب بن واضح، قال حدثنا ابن مبارك، عن يونس, عن الزُّهْرِيّ, قال: إذا أنكحت المرأة نكاحين مختلفين في خصومة ولَبْسٍ، فنرى أن ينتزع من/٣/الرجلين كليهما, ولا صداق لها إلا أن تكون مُسَّت, فإن كانت مُسَّتْ, فلها مهرها من الذي مسَّها, وتعتد منه حتى تنقضي عدتها, ثم تَنكح مَنْ شاءت.
سُئِلَ أحمدَ عَنْ جارية زوجها أبوها من رجلٍ, وأخوها من رجل, وولي آخر من رجل؟
قال: هي للذي زوّج الأب رضيت أم كرهت, نرى نكاح الأب جائزاً على الصغيرة.
وسألت أحمدَ مرة أخري.
قلتُ: امرأةٌ زوّجها جدها أبو أبيها من رجل, وزوجها أخوها من رجل آخر؟
قال: أيهما كان قبل, فهي امرأته.','1','183',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (14,'قلتُ: الجد والأخ سواء؟
قال: ما أقربهما, والجد أقرب قليلاً.
قلتُ لأحمدَ: فإذا كان لها أب؟
قال: إذا كان لها أب لم يجز لأحد أن يزوج إلا الأب.
حدثنا إسحاق، قال أخبرنا ابن علية, قال: أخبرنا يونس، عن الحسن، قال: تزويج الأب على البكر جائز رضيت أم كرهت.','1','184',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (15,'حدثنا محمد بن آدم، قال: ثنا معمر بن سليمان, عن حجاج, عن الحكم, عن إبراهيم, أنه كان يرى نكاح الوالد جائزاً على الثيب والبكر.','1','185',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (16,'<span data-type="title" id=toc-6>٦ - باب: وفيه الاخوة</span>.
سئل أحمد عن امرأة لها أخ من أب وأم وأخ من أب, من أحق بتزويجها؟
قال: هما في الولاية سواء, وليس هذا مثل الميراث.
وسألتُ إسحاق: قلتُ: امرأة لها عم وأخ, فأبى الأخ أن يزوجها, هل للعم أن يزوجها؟
قال: إذا كان الأخ مضارا بها, فإن العم يزوجها.
وسمعتُ إسحاق أيضاً, وسأله أحمد بن نصر عن امرأة لها أخ وعم, فلم يزوجها الأخ هل يزوجها العم؟
قال: يزوجها العم إذا كان ضرورة.','1','186',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (17,'حدثنا سعيد بن سليمان، قال: ثنا أبو هلال: قال: سألت الحسن عن امرأة خطبت ووليها غائب ولكن ولي وليها، أيزوجها ولي وليها؟
قال: ينتظر الكتاب إلى وليها.
قلتُ: إن الخاطب لا ينتظر حتى يذهب الكتاب ويرجع؟
قال: فليصبر.
قلت: إلى كم يصبر؟
قال: يصبركما صبر أصحاب الكهف.
قال: وسئل قتادة عن ذلك, فقال: يزوجها ولي الولي.','1','187',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (18,'<span data-type="title" id=toc-7>٧ - باب: تزويج الأخ والأب غائب</span>.
سألت إسحاق: عن رجل زوج أخته من رجل, والأب حاضر؟
قال: لا يجوز.
قلتُ: فإن أجاز الأب؟
قال: جائز أرجو.
قيل: فإن كان بغير شهود؟
قال: النكاح بغير شهود لا يجوز قاله مراراً الأب وغير الأب.
حدثنا المسيب: قال: ثنا ابن مبارك، عن سعيد، عن قتادة قال: إذا زوج الرجل/٤/أخته, وأبوها نائي الدار فنكاحها جائز, فإن كان شاهداً, فليس بنكاح إلا أن يأذن له الوالد.
حدثنا عبيد الله بن معاذ، قال: ثنا أبي قال: ثنا أشعث، عن الحسن أن الأخ لا يزوج أخته وأبوها غائب إلا أن يكون أذن له في ذلك, فإن زوّج لم يجز.','1','188',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (19,'<span data-type="title" id=toc-8>٨ - باب: تزويج اليتيمة</span>.
سمعت إسحاق يقول، وسأله أبو شداد المروزي عن اليتيمة تزوج؟
قال: أما اليتيمة فنختار أن لا يزوجها أحد، ولا يجوز تزويجها لأحد من الأولياء سوى الأب, والذين أجازوا لغير الأب جعلوا لها الخيار إذا أدركت, وهكذا هو عندنا لها الخيار إذا أدركت, فإن اختارت إذا أدركت, وأشهدت على ذلك, ثم ماتت لم يتوارثا فرق الحاكم بينهما, أو لم يفرق.
قال إسحاق: والعجبُ لهم, كيف غلطوا!
قالوا: نحتاج إلى فُرقة الحاكم, وكلهم قد أجمعوا في معنى خلاف هذا.
فقالوا: إذا أُعتقت الأمة, وكان زوجها حرا فاختارت نفسها, فلها ذلك, ولا تحتاج إلى فُرقة قاضي عندهم وهذا عجب! لأن عامة أصحاب النبي - صلى الله عليه وسلم - يقولون: خيرها النبي عليه السلام بما كان الزوج عبداً، ورأى هؤلاء حراً كان أو عبداً , وكأن فُرقة القاضي هاهنا أشبه لاختلاف العلماء.
حدثنا سعيد بن منصور: قال حدثنا هشيم: قال أخبرنا عمر بن أبي سلمة, عن أبيه, عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم - ((لا تنكح البكر حتى تستأمر','1','189',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (20,'ولا الثيب حتى تشاور)) , قيل: يا رسول الله إن البكر تستحي, قال: ((سكوتها إقرارها)).
حدثنا المسيب بن واضح: قال حدثنا ابن مبارك, عن معمر, عن الزُّهْرِيّ, عن عروة بن الزبير أنه أنكح ابنه صغيراً بنت','1','190',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (21,'المصعب صغيرة, فماتت فورثها.
حدثنا علي بن عثمان: قال حدثنا حماد، عن قتادة في رجل زوج ابنه صغيراً. قال: إذا مات ابنه ورثته الجارية, وإن ماتت لم يرثها الابن إلا أن يكون الأب ... (١) وارثها, أو زوجها وارثها.
_________
(١) بياض بالأصل بمقدار ثلاث حروف تقريبًا.','1','191',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (22,'<span data-type="title" id=toc-9>٩ - باب: تزويج الصغار بلا شهود</span>.
سألت إسحاق: قلت أبوين زوجا ولديهما صغيرين بلا شهود, أو بشهادة رجل, ثم مات أحدهما، فتقول الجارية وأمها للزوج: أقم شهودك ولا شهود؟
قال: لا يجوز نكاح صغير ولا كبير إلا بشاهدين كانا مجتمعين أو متفرقين بعد أن يكون إعلاناً.
قلت لإسحاق: فرجل خطب/٥/امرأة على ابنه فقال أبوها: هي له, وأحكموا الأمر بلا شهود, ثم جاء آخر فخطبها, فزوجها منه بشهود, أترى للأخير أن يفارقها لما خطب على خطبة أخيه؟
قال أبو يعقوب: الأول ليس بنكاح, لما لا يكون نكاح إلا بولي وشهود.
قلتُ: فلو أن رجلاً قال لرجل: زوجني بنتك, فقال قد زوجتك بغير شهود, ألم يكن هذا تزويجاً؛
قال: لا ولم يره, ومذهب أبي يعقوب أنه لا يكون نكاح إلا بشهود.','1','192',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (23,'<span data-type="title" id=toc-10>١٠ - باب المرأة لا يكون لها بينة بالمهر</span>.
قلت لإسحاق: امرأة زوجت, فقدم شأنها, ومات شهودها, ونسو ذلك؛ إذ وقعت في أرضٍ غربة, فمات زوجها وورثته, يسألونها البينة على مهرها.
قال: كلما عرفوا أنها امرأة الميت قديماً بالسماع, وإن لم يشهدوا عقدة النكاح, حتى تسكن قلوبهم أنها زوجته وسعهم أن يشهدوا لها بالمهر والميراث, إلا أن يقيم الورثة أنها قد استوفت مهرها, فحينئذ يسقط المهر ويثبت الميراث.
حدثنا سعيد بن منصور: قال حدثنا أخبرنا هشام: قال هشام: عن ابن سيرين أن قوماً شهدوا ماآل رجل, وكانوا في البيت، وقوم في الصفة دون البيت، فقال أن التزويج في البيت, فخرج الذين كانوا في البيت على الذين كانوا في الصفة, فأخبروهم بالصداق, وبما تزوج عليه الرجل, فتقادم أمرهم,','1','193',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (24,'فاحتاجت المرأة إلى شهادة الشهود, ومات الذين كانوا في البيت, فاختصموا إلى شريح, فجاءت المرأة بالذين كانوا في الصفة, فقبل شهادتهم, وقضى على الرجل بالصداق, فقال الرجل لشريح: أتقضى علي بالنبأ؛ فقال شريح: أليس القرآن نبأ؟ ثم قرأ: {قُلْ هُوَ نَبَأٌ عَظِيمٌ* أَنتُمْ عَنْهُ مُعْرِضُونَ} [ص: ٦٧ - ٦٨].','1','194',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (25,'<span data-type="title" id=toc-11>١١ - باب: الصغيرة يزوجها غير الأب</span>.
سئل إسحاق: عن رجلٍ تَزَوَّجَ امرأة, وهي غير مدركة إلا أنها رضيت, وطابت نفسها, وبنى بها الزوج, فحاضت عند الزوج, ولم يجدد النكاح, وأنها زوجها العم؟
قال: إن كانت بجد تعقل, وجاوزت التسع سنين وتفهم التزويج, فزوجها العم, فرضيت, جاز ذلك.
قيل لأحمد: امرأة لها ولي لا يعقل شبه المصاب, أو المجنون؟
قال: تأتي السلطان.','1','195',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (26,'<span data-type="title" id=toc-12>١٢ - باب: إذا أشهد شاهدين متفرقين</span>.
سمعت إسحاق: يقول إذا زوج الرجل بنته أو كريمته من القرابة, وأمكنه بشهادة/٦/رجل واحد, ثم طل، آخر في مجلسه, أو في موضع آخر وأشهده حتى صار الشهود اثنين فصاعداً جاز النكاح؛ وذلك أن يزيد بن هارون قال: أخبرنا الحجاج: عن حصين الحادي، عن الشعبي, عن الحارث, عن علي: قال: لا','1','196',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (27,'نكاح إلا بولي وشاهدين.
قال إسحاق: ولم يقل شاهدين مجتمعين ولا متفرقين, فإذا كان الشاهدان على نكاح وهما عدلان تم النكاح بعد أن لا يقال للشاهدين اكتما النكاح, وأعلنوا التزويج, ولو أشهد شاهدين مجتمعين, فقال الولي لهما اكتما النكاح, فكتما كان النكاح باطلا؛ لأنه حينئذ نكاح السر, وإن مات أحد الشاهدين, وولدت المرأة ولدًا ذهب النسب.
قال: والشاهدان إذا أخبرا الناس علموا ذلك من قبلهما, لأذاعوا الخبر, ثم مات أحد الزوجين, فإن لأولئك الذين لم يشهدوا عقدة النكاح أن يشهدوا, فلذلك لا بد من إعلان النكاح بشهود يخبرون الناس, أو بصوت دف؛ ولذلك استحبوا الدفوف عند النكاح؛ ليكون النكاح معلنا, وقد قال عبد الله بن عتبة: شر النكاح نكاح السر, وشر البيع بيع السر, أخبرنا ذلك عبد الرحمن بن مهدي، عن سفيان، عن عبد الله بن فياض، عن عبد الله بن عتبة،','1','197',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (28,'حدثنا يحيى الحمالي: قال: ثنا أبو زياد التيمي، قال: ثنا بن الحكم السجستاني، عن شريح أن امرأة زوجتها أمها, فرفع ذلك إلى شريح فقال: هل ضربوا بدف؟
قال: لا.
قال: هل نثروا ريحانًا؟
قالوا: لا.
قال: هل نثروا سكرًا؟
قالوا: لا.
قال: فإن الناس يقولون هذا زنا.
قلتُ: يا أبا أمية ما تقول أنت؟
قال: ما أنا إلا من الناس.
حدثنا محمد بن يزيد أبو بكر، قال: ثنا يزيد بن هارون، عن عيسى بن ميمون, عن القاسم بن محمد, عن عائشة قالت: قال رسول الله - صلى الله عليه وسلم -: ((أعلنوا هذا','1','198',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (29,'النكاح واجعلوه في المساجد واضربوا عليه بالدفوف وليولم أحدكم ولو بشاة)).','1','199',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (30,'<span data-type="title" id=toc-13>١٣ - باب: الشغار ونكاح الفاسد</span>.
سألت إسحاق قلتُ: رجلٌ طلَّقَ امرأتَه ثلاثًا, فتزوجها رجل بغير ولي, هل تحل لزوجها؟
قال: هذا ليس بنكاح ولا تحل للزوج.
وسألت إسحاق قلتُ: رجلٌ تزوج امرأة بغير ولي أو نكاح الشغار, فمات أحدهما, هل يتوارثان؟
قال: لا يتوارثان, وهو مذهبه.
وسألتُ إسْحاقَ مرة أخري/٧/: قلتُ: رجل تزوج امرأة على نكاح المتعة, فمات أحدهما, هل يتوارثان؟
قال: لا يتوارثان.
قلتُ: فإن تزوج امرأةً على نكاح شغار, فمات أحدهما, هل يتوارثان؟
قال: لا.','1','200',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (31,'حدثنا محمد بن قدامة: قال: حدثنا جرير: عن مغيرة, عن إبراهيم, قال: إذا كانت المرأة تحت الزوج بنكاح لا تحل له, فإنها تعتد عدة المطلقة, ولا ميراث بينهما.
سألت إسحاق: قلتُ: رجلٌ تَزَوَّجَ امرأةً على نكاح الشغار, هل يحللها لزوجها؟
قال: لا. قال: وكل نكاح فاسد.
قلتُ: فإن تزوجها بغير ولي؟
قال: لا يحللها.
حدثنا عبيد الله بن معاذ، قال ثنا أبي أشعث, نا الحسن، في رجل طلَّقَ','1','201',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (32,'امرأتَه ثلاثًا, فتزوجت رجلاً, فقال لها الأول: اختلعي من زوجك حتى أتزوجك, فاختلعت فليس للأول أن يتزوجها ولا نعمة عين.
سمعتُ أحمدَ يقولُ: من تزوج على نكاح الشغار, أو تزوج امرأة على عمتها أو خالتها؛ فإنه يفرق بينهما, ولها المهر إذا أصابها, وإن لم يكن دخل بها فلا شئ لها.
قلتُ: إن خلا بها, ولم يمسها؟
قال: إذا أغلق بابًا أو أرخى سترًا فلها لمهر.','1','202',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (33,'<span data-type="title" id=toc-14>١٤ - باب: المولى يتزوج بالعربية</span>.
سألت أحمدَ: عن المولى يتزوج العربية؟
قال: لا.
قلتُ: يفرق بينهما؟
قال: نعم.
وسألتُ إسحاق عن المولى يتزوج العربية؟
قال: لا.
قلت: يفرق بينهما؟
قال: نعم.','1','203',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (34,'قال إسحاق: وسألني عبد الله بن ظاهر أيفرق بينهما؟
فقلتُ: نعم يفرق بينهما بالمنشار.
حدثنا أحمد بن نصر، قال: حدثنا حبان بن موسى، قال: قال عبد الله بن المبارك: لا أرى أن يتزوج الرجل المولى عربية, ولا أرى له حراما, ولكن ما يصنع بأن يشهر نفسه.
حدثنا المسيب بن واضح: قال: حدثنا حجَّاج: عن السري بن يحيي, عن ابن سيرين أنه تزوَّج عربية, وابن عون تزوج عربية.','1','204',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (35,'سمعت أبا محمد حرب يقول: تزوج ابن عون عربية, فجلده بلال بن أبي بردة.','1','205',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (36,'<span data-type="title" id=toc-15>١٥ - باب: تزويج المرأة على عمتها أو خالتها</span>.
قلتُ لأحمدَ: هل تزوج امرأة على عمتها أو خالتها من النسب والرضاع سواء؟
قال: نعم.
ومذهبه أن يفرق بينهما.
حدثنا عمرو بن مرزوق، قال: حدثنا زهير: قال: حدثنا داود, عن عامر أن أبا هريرة [قال] أن رسول - صلى الله عليه وسلم - /٨/نهى أن تنكح المرأة على عمتها, أو العمة على ابنة أخيها, أو المرأة على خالتها, أو الخالة على ابنة أختها, لا تنكح الكبرى على الصغرى, ولا الصغرى على الكبرى.','1','206',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (37,'<span data-type="title" id=toc-16>١٦ - باب: ولاية الأب</span>
سألت أحمدَ, قلتُ: رجل زوج بنته, وهي صغيرة, فلما أدركت, قالت: لا أرضي.
قال: ليس لها ذلك.
قلتُ: فإن كانت مدركة, فزوجها ولم يستأمرها؟
قال: يستأمرها.
قلتُ: فكم غاية الصغر؟
وقال: تسع سنين.','1','207',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (38,'قلت: فإنها قالت لأبيها: زوجني فلانًا, فزوجها غير ذلك؟
قال: إذا كانت مدركة, فليس له ذلك, وإن كانت بكرًا جاز عليها.
وسئل أحمد مرة أخري: قيل: الرجل يزوج ابنته, وهي بكر, وقد بلغت ولم يستأمرها؟
قال: قد اختلف الناس في هذا.
قيل: أي شئ تختار أنت؟
قال: لا تزوج الثيب, ولا البكر إلا بإذنها ومشورتها.
وسمعتُ أحمد مرة أخري: يقول: الصغيرة لا يزوجها إلا أبوها، يجوز نكاح الأب على الصغيرة حتى تبلغ تسع سنين, فإذا بلغت تسع سنين, استأمرها.
قلتُ: فإن زوجها, وقد بغت تسع سنين, وهي بكر, ولم يستأمرها؟
قال: يستأمرها.
قلتُ: لم يفعل.
قال: أهل المدينة يقولون: يجوز النكاح عليها, يعني نكاح الأب مادامت بكرًا, ويقولون قول النبي - صلى الله عليه وسلم - في الاستئمار,','1','208',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (39,'إنما هو شبة المشورة يستشيرها, فإما أن يكون في يديها شئ, فلا هذا إذا كانت بكرًا, وكأنه ذهب إليه, ولكنه سكت.
قال: لا أقول فيها شيئا.
قيل: فزوجها أبوها, وهي صغيرة على ثوب, أو على دينار, فلما بلغت قالت: لا أرضى بهذا المهر.
قال: ليس لها ذلك إذا زوجها الأب.
قال أحمد: ويروى عن طاووس أنه قال: إذا زوجها الأب, وهي صغيرة, ثم بلغت, فإن لها الخيار.
قال: ولا نعرفه عن أحد إلا عن طاووس, ولم يذهب إليه أحمد, وقال قد زوجت عائشة من رسول الله - صلى الله عليه وسلم - , فبلغت, فلم يكن ثم خيار, ومذهب أبي عبد الله في البكر إذا زوجها غير أبيها من غير استئمار, فإن يُفسخ إن شاءت.
وسئل إسحاق عن رجل زوج بنته, وهي صغيرة بكر, فلم ترض, وصاحت وضجت حتى سمع الجيران صراخها؟
قال: إذا زوجها الأب, وهي بكر جاز عليها رضيت أم كرهت, وإن كانت ثيبًا, فزوجها الأب, /٩/ فإنه لا يجوز إلا برضاها واستئمارها,','1','209',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (40,'وسمعته مرةً أخرى يقول: تزويج الأب جائز على البكر رضيت أم كرهت؛ لأن الأب ينظر لابنته.
قال إسحاق: وأخبرنا ابن علية, قال: أخبرنا يونس, عن الحسن, قال: تزويج الأب على البكر جائز رضيت أم كرهت.
حدثنا أبو معن، قال: حدثنا عبد الكبير، قال: حدثنا الربيع بن صبيح، عن الحسن قال: إذا زوج الأب بنته, فهو جائز, وإن كرهت بكرًا كانت أم ثيبًا, صغيرة كانت أم كبيرة.','1','210',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (41,'<span data-type="title" id=toc-17>١٧ - باب: الثيب يزوجها أبوها بغير رضاها</span>.
سألت أحمدَ عن البنت يزوجها أبوها, وهي كارهة؟
قال: لا يجوز إلا برضاها.
قلتُ: يفسخ النكاح؟
قال: نعم، يرد النكاح.
وسألتُ إسحاق قلتُ: امرأة كانت ثيبًا, فزوجها أبوها وهي كارهة؟
قال: لها ألا ترضى.
قلتُ: أتذهب إلى حديث خنساء؟
قال: نعم.
قلتُ: فإن الأب زَوَّجها, وهي كارهة، فخطبها رجل ورغبت فيه، هل تحتاج إلى تفريق السلطان؟
قال: لا؛ لأن نكاح الأب إياها باطل.','1','211',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (42,'قلتُ لإسحاق: فإن كانت جارية صغيرة زوجها غير الأب, فلما أدركت قالت: لا أرضى, فخطبها رجل, هل لها أن تتزوج من غير تفريق الحاكم بينهما؟
قال: نعم؛ لأن ذلك ليس بنكاح. [تزويج اللقيط أمه] (١).
سألت إسحاق عن المنبوذ يزوج أمه؟
قال: هو يزوجها.
قلتُ: فيرث أُمَّه؟
قال: يرثُها.','1','212',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (43,'<span data-type="title" id=toc-18>١٨ - باب: الأب يزوج ابنه صغيرًا, على مَنْ الصداق</span>؟
سألتُ أحمدَ, قلتُ: رجل زوج ابنه صغيرًا, على من الصداق؟
قال: إذا تقبل به الأب فهو عليه, وإلا فهو على الابن.
قلتُ: إن الابن لما أدرك قال لا أرضى.
قال: ليس له ذلك.
وسئل إسحاق عن رجل زوَّج ابنه وصير المهر على نفسه وعلى ابنه بأمر أبيه.','1','213',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (44,'قال أبو يعقوب: يجب المهر على ورثة الأب وعلى الابن المتزوج, ويجب للورثة على الابن المتزوج يعني إذا مات الأب, وقال إن ضمن الابن, والأب بغير أمر الابن, فالمال عليهما نصفان.
وسألتُ إسْحاقَ أيضًا: قلتُ: رجل زوَّج ابنه صغيرًا, فضمن الأب المهر, فلما بلغ الابن, ودخل بالمرأة, وسألوه الصداق, وقال إنما الصداق على الأب, ما الحكم في ذلك؟
قال: إذا ضمنه الأب فهو عليه.
قلتُ: وإن مات الأب؟
قال: وإن مات الأب, فهو على ورثته. /١٠/
قال: /١٠/ وإن أخذ من الزوج رجع به على الورثة.
استفهمتُه هذه المسألة, وراجعتُه فيها, فثبت عليه وكان هذا مذهبه.
وسئل إسحاق مرةً أخرى عن غلام صغير زوجه أخوه؟
فقال: إذا أدرك فهو بالخيار, فإن رضي فهو جائز وإلا فلا؟
قلتُ: فإن زوجه الأب؟
قال: النكاح جائز, وليس له خيار.
حدثنا محمد بن نصر: قال: حدثنا حسان: عن سفيان قال في رجل زوج ابنه صغيرًا.
قال: يجيز نكاحه, ولا يجيز طلاقه, فإذا شب الغلام, فالأمر أمر الابن, ولا يجوز أمر الأب, فإن أدرك الابن, فقال لا حاجة لي بالصداق فهو عليه, وإلا فهو على الابن,','1','214',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (45,'وإن مات الأب وقد تقبل به يؤخذ من ميراثه, ويتبع الورثة الابن بما أدى عنه من مال أبيهم.
حدثنا أحمد: قال: حدثنا هشيم، قال أبنا يونس: عن الحسن أنه كان يقول إذا زَوَّج الرجلُ ابنه وهو صغير جاز عليه, وكان الصداق على الغلام إلا أن يضمنه والده عنه.
حدثنا أحمد: قال: ثنا هشيم، قال: أخبرنا مجالد، عن الشَّعبيِّ في رجل زوج ابنه وهو صغير، قال: الصداق على الأب.','1','215',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (46,'<span data-type="title" id=toc-19>١٩ - باب: النظر إلى المرأة التي يريد أن يتزوجها</span>.
قلتُ لأحمدَ: الرجل يريد أن يتزوج المرأة, هل ينظر إليها؟
قال: إذا خاف ريبة.
حدثنا يحيى بن عبد الحميد، قال: حدثنا شهريك، عن عبد الله بن عيسي، عن موسى بن عبد الله بن يزيد, عن أبي حميد, قال: قال النبي - صلى الله عليه وسلم - ((لا جناح','1','216',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (47,'على الرجل إذا أراد أن يتزوج المرأة أن ينظر إليها قبل أن يتزوجها)).
حدثنا أبو بكر محمد بن يزيد، قال أنا يونس بن محمد، قال ثنا عبد الواحد بن زياد، عن محمد بن إسحاق, عن داود بن الحصين, عن واقد بن عبد الرحمن بن سعد بن معاذ, عن جابر بن عبد الله, قال: قال رسول الله - صلى الله عليه وسلم - ((إذا أراد','1','217',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (48,'أحدكم أن ينكح امرأة فلا حرج عليه أن ينظر منها إلى ما يدعوه إلى نكاحها)) , قال: فخطبتُ امرأةً من بني سلمة, فكمنتُ لها في النخل, فلم أزل حتى رأيت منها ما دعاني إلى نكاحها.
قلتُ لأحمدَ: فيجب أن تكون خطبة النكاح مثل قول ابن مسعود. فوسع في ذلك.','1','218',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (49,'<span data-type="title" id=toc-20>٢٠ - باب: من فجر بأخت امرأته</span>.
سألتُ أحمدَ, قلتُ: رجل تزوج أخت امرأته, أو أصاب منها صبوة؟
قال: يمسك عن امرأته حتى/١١/تنقضي عدة هذه.
قلتُ: حيضة؟
قال: لا, ولكن عدتها.
حدثنا أحمد، قال: ثنا عبد الوهاب الثقفي، عن البتي، عن ابن أشوع, قال: سُئل','1','219',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (50,'عن الرجل يزني بأخت امرأته؟ قال: حسرت عليها وهابها إبراهيم وعامر رأيت أن الحرام لا يحرم الحلال.
حدثنا أحمد، قال: ثنا يحيى بن سعيد، قال ابن جريج: أخبرنا قال: أخبرني عطاء, عن ابن عباس في رجل زنى بأخت امرأته، قال تخطى حرمة إلى حرمة ولا تحرم عليه امرأته. قال: قال عطاء: إذا زنا بابنة امرأته، أو بأم امرأته حرمت عليه امرأته.','1','220',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (51,'حدثنا أحمد، قال: أبنا هشيم، قال: أخبرنا محمد بن سالم, عن الشعبي, عن علي أنه قال في رجل فجر بأخت امرأته لا تحرم عليه امرأته, ولكن يعتزلها حتى يستبرئ الأخرى، ثم يرجع إلى امرأته، ويستغفر الله ويتوب إليه.
حدثنا أحمد، قال: ثنا هشيم، قال أبنا يونس, عن الحسن مثل ذلك.','1','221',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (52,'<span data-type="title" id=toc-21>٢١ - باب: من فجر بامرأةٍ، أيتزوج أمها أو ابنتها</span>؟
سألت أحمدَ قلتُ: رجل فجر بامرأةٍ، هل يتزوج أمها أو ابنتها؟
قال: إذا كان وطئها، فلا.
قلتُ: فإن لم يطأها، ولكن قَبَّلَ أو باشر؟
قال: دَع هذه المسألة.','1','222',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (53,'وسئل إسحاق عن رجل جامع أم امرأته؟
قال: حَرُمت البنت.
قيل لإسحاق: فإن جامع أخت امرأته؟
قال: يتربص حتى تحيض حيضة.
وسئل إسحاق أيضًا عن رجل قبل أم امرأته من الرضاعة، أو لامسها من غير مجامعة؟
قال: لا يحرم عليه امرأته.
قيل، فإن جامعها في الفرج؟، فذهب إلى أنها تَحرم عليه، وقال أهل المدينة يقولون: لا تحرم بالحرام حتى يتزوج الأم، فيدخل بها، فحينئذ تحرم.
وسئل إسحاق مرة أخري عن الرجل التزمٍ أم امرأته من فوق الثياب فأمنى؟، فذهب إلى أنها لا تحرم عليه امرأته إلا بالجماع، وكتب لنا إسحاق مرة أخري، وقرأته أنا عليه، وإذا جامع الرجل امرأته في غير الفرج ثم بدا له يتزوج بنتها؟ فإن: الذي أعتمد عليه أن الجماع في غير الفرج إذا تعمد ذلك وأنزل، أنه كالجماع في الفرج يُحرِّم عليه أمها وابنتها، وذلك لما يروي قول الحسن بن أبي الحسن، وعطاء بن أبي رباح وغيرهما: أن المجامع في الفرج وغير الفرج في رمضان يقضي صومه وعليه الكفارة وكذلك إذا فعل ذلك في الحج أفسد الحج؟ فإن كان تَزَوَّج بنتَ هذه المرأة التي جامع فولدت منه ثم علم بما كان من صنيعه إلى الأم ينزه عنها، وصار فعله إلى بنت المرأة/١٢/أو أمها في التزويج فاسدًا ويجانبها.','1','223',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (54,'وأما أهل المدينة مالك بن أنس وأصحابه، فإنهم يروا أن لو جامع في الفرج حرامًا أمَّ امرأته أو بنت امرأته أن لا يحرم عليه امرأته؛ لما لا يحرم الحلالَ الحرامُ، وأما الذي نعتمد عليه أن لا تحرم عليه امرأته إذا مس، أو قبل، أو باشر في غير الفرج ولم ينزل؛ فإن امرأته لا تحرم عليه، فأما إذا وطأها بجماع، فإنها تحرم عليه حينئذ.
قال: وأما قول أهل الكوفة: فإنها تحرم عليه، وهو خطأ بين؛ لأنه لا حكم في ذلك عند الحكام حتى يكون جماعًا، ولذلك جعلنا الولد ولد الزوج لما وصفنا أن مالك بن أنس وأصحابه رأوا الجماع لا يحرم عليه امرأته، فنحن وإن كنا لا نراه، فإنا نجعل الولد ولده لما نراه لا يُحرّم دون الجماع وإنزالٍ دون الفرج.
قال: وإذا جامع في الفرج أنزل أو لم ينزل؟
قال: فإنَّ امرأته تحرم عليه.
حدثنا أحمد بن حنبل: قال: حدثنا محمد بن عبد الله بن الزبير، قال: ثنا سفيان: عن الأغر، عن خليفة بن حصين، عن أبي نصر، عن ابن عباس قال: جاء','1','224',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (55,'رجل فقال: امرأة قد ولدت مني سبعةً، كلهم قد أطاق السلاح، وهي أحب الناس إلي، وإني كنت أصبت من أمها صبوة؟
قال: هل لك من مال؟
قال: نعم ثلاثمائة ألف، ولوددت أني فديتها.
قال: هي عليك حرام.
حدثنا أحمد: قال: حدثنا معتمر بن سليمان، عن عوف، عن الحسن في رجل عبث بأم امرأته حتى أنزل، ولم يستخلطا، أتحرم عليه امرأته؟
قال: لا.','1','225',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (56,'<span data-type="title" id=toc-22>٢٢ - باب: إذا لم يدخل بالمرأة، أيتزوج أمها أو ابنتها</span>؟
سُئِلَ أحمدَ عَنْ الرجل يتزوج أم امرأته بعد ما ماتت امرأته؟
قال: لا
قيل: لقول الله (وأمهات نسائكم)؟
قال: نعم.
وسُئِلَ أحمدَ عَنْ رَجلٍ تَزَوَّجَ امرأة، فماتت قبل أن يدخل بها، أيتزوج أمها؟
قال: لا.
قيل: قد تزوج بنتها.
قال: نعم إذا لم يكن دخل بأمَّها.','1','226',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (57,'وسئل إسحاق عن رجلٍ تَزَوَّجَ امرأة ولها بنت، فطلقها من قبل أن يدخل بها، أيتزوج الابنة؟
قال: سديدا إذا لم يدخل بالأم تزوج البنت.','1','227',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (58,'<span data-type="title" id=toc-23>٢٣ - باب: من يجمع بين ابنتي عم</span>.
قلتُ لأحمدَ: أتكره أن يجمع الرجل بين ابنتي عم، أو ابنتي خال؟
قال: نعم للقطيعة.
حدثنا أبو معن، قال: حدثنا عبد الملك بن عمرو، قال: حدثنا سفيان الثوري، عن خالد بن سلمة، عن عيسى بن طلحة قال: نهى رسول الله - صلى الله عليه وسلم - أن تنكح المرأة على/١٣/قرابتها مخافة القطيعة.','1','228',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (59,'<span data-type="title" id=toc-24>٢٤ - باب: مَنْ طَلَّقَ امرأة فتزوجَ بأُختها</span>.
سألتُ أحمدَ قلتُ: رجلٌ طلَّقَ امرأتَه، ثم أراد أن يتزوج أختها قبل انقضاء العدة؟
قال: لا يجوز.
قلتُ: فلو كنّ أربعَ نسوة، فطلق واحدة منهن، هل له أن يتزوج أخرى حتى تنقضي عدة هذه التي طلَّق؟
قال: لا.
حدثنا أبو معن، قال ثنا معاذ: قال ثنا ابن عون: قال: أنبأنا أبو صادق، قال: ثلاثٌ يعتد منهن الرجل','1','229',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (60,'إذا كُنَّ له أربع نسوة، فطلق إحداهن لم يتزوج حتى تنقضي عدتها، وإذا طلَّقَ امرأتَه لم يتزوج أختها حتى تنقضي عدتها، وإذا كان لامرأته ولد من غيره، فمات أمسك عن امرأته حتى تنظر، هل لها حمل فيرثه.
قال ابن عون: فذكرت ذلك لمحمد فعرفه.','1','230',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (61,'<span data-type="title" id=toc-25>٢٥ - باب: إذا اشترى أخت جاريته</span>؟
قلتُ لأحمدَ: رجل عنده أمة لا يطؤها، ثم اشترى أختها فوطئها، هل له أن يمسكها؟
قال: إذا اعتزلها فلا بأس.
حدثنا أبو معن، قال: حدثنا أبو النعمان، قال: حدثنا ابن مبارك، عن يونس، عن الزُّهْرِيّ، قال: إذا أبانها فلا بأس أن يطأ الأخرى.','1','231',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (62,'حدثنا أبو هشام قال: ثنا حسان، في رجلٍ له أمتان أختان وقع على أحديهما، ثم اعتزلها وهو لا يحدِّثُ نفسه بأن يراجعها فوقع على الأخرى ثم كرهها فاعتزلها، أله أن يقع على أختها باع هذه أو أمسكها؟
قال: قال سفيان: بئس ما صنع لم ينبغي (١) له حين واقع إحدهما (٢) أن يقع على الأخرى، وهما جميعًا في ملكه حتى يُمَلِّك فرجَ إحديهما غيره، فإذا وقع على الأخرى، فلا يقربن واحدة منهما حتى يملك فرج إحديهما غيره، وإن زوَّجها عبدَه فلا بأس بأن يقيم على أختها.
سألت أحمدَ أيضًا، قلتُ: رجل له أمة يطأها، فأراد أن يتزوج أختها أو يتسراها؟
قال: لا يجمع بين الأختين الاثنتين.
قلتُ: فإن زوَّج أختها التي عنده من رجل، ثم تزوج أختها، فطلق الرجل هذه التي تزوج أختها، فرجعت في ملكه؟
قال: ينبغي أن يُخرج إحديهما من ملكه.
_________
(١) كذا في الأصل والصواب ينبغِ.
(٢) كذا في الأصل والصواب إحداهما.','1','232',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (63,'وسألت أبا الوليد الطيالسي عن رجل له أمة يطؤها، فأراد أن يتزوج أختها من الرضاعة؟
قال: لا يجوز.
قلتُ: فِإنه زوجها من رجل؟
قال: إذا لم يرد التدليس لم أعلم به باسًا.
حدثنا سعيد بن منصور: قال: ثنا هشيم، قال: أبنا حجاج، عن ميمون بن مهران، عن ابن عمر أنة سئل عن رجل عنده أختين/١٤/فوطئ إحدهما، ثم','1','233',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (64,'أراد أن يطأ الأخرى؟
قال: يخرج التي وطئ من ملكه.','1','234',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (65,'قيل: فإن زوَّجها؟
قال: لا حتى يخرجها من ملكه.

<span data-type="title" id=toc-26>٢٦ - باب: من تزوج بذات محرم وهو لا يعلم</span>.
سمعتُ أحمدَ يقولُ في رجل تزوج ذات محرم منه وهو لا يعلم، ثم علم؟
قال: إن ولدت له ألحق به الولد، وورث.
قيل: فالمهر؟
قال: أتوحش من ذلك إذا كانت أمه، أو بنته من الرضاعة أو غيره، فإني أتوحش من أخذ المهر، ولو كانت عمته، أو خالته، أو نحو هذا كان أهون.','1','235',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (66,'وسألت أحمدَ مرة أخري، قلتُ: فرجل تزوج امرأة، فإذا هي محرم منه؟
قال: إذا كانت أم أو نحو ذلك، فإني أتوحش أن يأخذ المهر، وإن كانت غير ذلك فهو أهون.
حدثنا الحماني، قال ثنا قيس: عن زكريا، عن الشعبي، قال: لا يكون لها من ابنها، ولا من أخيها المهر.','1','236',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (67,'<span data-type="title" id=toc-27>٢٧ - باب: من قبل امرأة، أتحل لابنه</span>؟
سألت أحمدَ قلتُ: رجل جرد امرأة وقبلها، أو لامسها هل تحل لابنه؟
قال: إذا كانت ملك يمينه، فلا.
قلتُ: فإن لم تكن ملك يمينه؟ فكره الجواب فيه.
وسألت إسحاق، قلتُ: رجل قبل امرأة أبيه، أو ملك يمينه؟
قال: بئس ما صنع.
قلتُ: تحرم على الأب؟
قال: لا.
وسألتُ إسْحاقَ مرة أخري، قلتُ: رجل قبل امرأة أبيه؟
قال: لا تحرم على الأب.
وُسئل إسحاق مرة أخري، عن رجل قبل امرأة ابنه؟
قال: لا تحرم على الابن امرأته، ويتوب إلى الله، وكذلك إن قبل الابن امرأة أبيه، وكذلك إن قبل أم امرأته لا تحرم عليه امرأته إلا بالوطء.
حدثنا إسحاق، قال أبنا معتمر بن سليمان، عن عوف الأعرابي، عن الحسن في الرجل يعبث بامرأة أبيه، أو امرأة ابنه، أو نحوهما.
قال: ما لم يستخلط، فإن امرأته لا تحرم عليه.','1','237',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (68,'حدثنا عبد الله بن الزبير الحميدي، قال: حدثنا سفيان، قال: سمعت يحيى بن سعيد يحدث عن القاسم، عن عبيد الله وعبد الرحمن ابني عامر بن ربيعة أن أباهما نال منها شيئا إلا أن يكون أطَّلع منها مطلعًا، فكره أن يطلعه أحد بعده.','1','238',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (69,'<span data-type="title" id=toc-28>٢٨ - باب: العبد يتزوج بسَيِّدته</span>.
قلتُ لأحمدَ: عبد تزوج سيدته؟
قال: لا يجوز بتة وأظنه. قال: يفرق بينهما.
حدثنا أحمد، قال: حدثنا هشيم، قال أبنا حصين، عن بكر بن عبد الله/١٥/قال: كتب عمر إلى الأمصار: أيما امرأة تزوجت عبدها أو تزوجت بغير ولي ولا بينة فاضربوهما، وفرقوا بينهما.
وسألتُ أحمدَ مرةً أخرى، قلتُ: عبد تحته حرة فملكت منه شيئا؟
قال: حرمت عليه.','1','239',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (70,'حدثنا محمد بن الوزير الدمشقى: قال: حدثنا الوليد بن مسلم، قال: حدثنا سالم، قال: سمعت الحسن سئل عن امرأة حرة تحت مملوك فاشترته من مولاه، فصار لها؟
قال: إذا ملكته فلا يحل له فإن هي أعتقته، فهي عنده على منزلتها الأولى، وإن هي باعت، أو تربصت، فقد بانت منه بتطليقة.
حدثنا يحيى: قال حدثنا شريك، عن عطاء بن السائب، عن ميسرة، عن علي قال: إذا ملكت المرأة من زوجها شيئا حرمت عليه، قال عطاء: فلم','1','240',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (71,'أدر ما الذي يحلها له؟
فسألت عبد الله بن عتبة قال: تعتقه وتزوَّجه.','1','241',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (72,'<span data-type="title" id=toc-29>٢٩ - باب: المتعة</span>.
قلتُ لأحمدَ: المتعة التي نُهي عنها، كيف هي؟
قال: هو الأجل أن يتزوج إلى أجل.
حدثنا أبو بكر الحميدي، قال: ثنا سفيان، قال: ثنا الزُّهْرِيّ، قال: وأخبرني الربيع بن سبره الحمدي، عن أبيه، قال: نهى رسول الله - صلى الله عليه وسلم - عن نكاح المتعة عام الفتح.','1','242',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (73,'حدثنا الحميدى، قال: ثنا سفيان، قال ثنا الزُّهْرِيّ، قال: ثنا حسن وعبد الله ابنا محمد بن علي، عن أبيهما أن علي بن أبي طالب قال لابن عباس: أن رسول الله - صلى الله عليه وسلم - نهى عن نكاح المتعة، وعن لحوم الحمر الأهلية زمن خيبر.
حدثنا أبو بكر محمد بن يزيد، قال: ثنا موسى بن داود، قال: ثنا عبد الله بن عمر، عن سالم بن عبد الله، عن أبيه، عن النبي - صلى الله عليه وسلم - أنه','1','243',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (74,'نهى عن نكاح المتعة.
حدثنا أبو بكر: قال: حدثنا أبو أسامة، عن عبد الرحمن بن يزيد بن جابر، عن القاسم ومكحول، عن أبي إمامة الباهلى:','1','244',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (75,'أن رسول الله - صلى الله عليه وسلم - نهى عن نكاح المتعة عام خيبر.','1','245',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (76,'<span data-type="title" id=toc-30>٣٠ - باب: من تزوج بالمرأة، ثم وجد بها داءً</span>؟
سألت أحمدَ، قلتُ: رجلٌ تَزَوَّجَ امرأةً، فدخل بها، فوجد بها داءً ولم يُخبَر بذلك؟
قال: عليه المهر، ويقال يرجع به على الولي.
وسألتُ إسحاق، قلتُ: رجلٌ تَزَوَّجَ امرأةً، وبها عيب لا يُعلم، ثم علم؟
قال: إذا كان الشيء ظاهرًا يعرف، فإن كان دخل بها فلها المهر، وإن لم يكن دخل بها فلا شئ.
حدثنا أبو بكر الحميدى، قال ثنا/١٦/سفيان قال: حدثنا يحيى بن سعيد، عن سعيد بن المسيب: قال: قال عمر بن الخطاب: أيما رجل تزوج امرأة، وبها','1','246',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (77,'جنون أو جزام أو برص، ولم يعلم ذلك حتى أصابها، فلها المهر بما استحل من فرجها، ويَغْرُم وليها لزوجها مثل ما ساق إليها (١). وربما قال سفيان: وذلك لزوجها غرم على وليها.
حدثنا سعيد ين منصور، قال: حدثنا هشيم، قال: ثنا مغيرة، عن إبراهيم في الرجل تفجر امرأته.
قال: إن لم يستحي لولده فلا بأس ولها من أن يمسكها (٢).
حدثنا سعيد، قال: أبنا هشيم، عن حجاج، عن عطاء، قال: يمسكها إن شاء.
_________
(١) رواه ابن أبي شيبة ٣/ ٤٨٦.
(٢) سنن سعيد)) ١/ ٢٤٤ (٨١٣) وفيه قال: في الرجل يتزوج المرأة وقد فجرت. قال: إن لم تستحي لولده أن يعير بذلك فليتزوجها إن شاء.','1','247',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (78,'حدثنا سعيد، قال: ثنا هشيم، قال ابنا منصور ويونس، عن الحسن قال: يفارقها ولا يمسكها (١).
_________
(١) سنن سعيد)) ١/ ٢٤٤ (٨١٦)، ((المحلى)) ١٠/ ١١١.','1','248',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (79,'<span data-type="title" id=toc-31>٣١ - باب: من فجر بامرأةٍ، ثم تَزَوَّجها</span>.
سألتُ أحمدَ، قلتُ: رجلٌ زنا بامرأةٍ، ثم تزوجها؟
قال: إذا تابت فلا بأس.
قلتُ لأحمدَ: رجلٌ وجد مع امرأته رجلا؟
قال: لا تحرم عليه، ولكن لا أرى أَنْ يمسكَ مثلَ هذه.','1','249',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (80,'حدثنا محمد بن معاوية، قال ثنا شريك، عن عروة بن عبد الله بن قشبر، عن أبي الأشعث، عن ابن عمر في الرجل يزني بالمرأة، ثم يتزوجها؟
قال: أوله سفاح وآخره نكاح.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن سلام بن مسكين، قال حدثني عمران الخزاعي، قال: سمعتُ سعيد بن المسيب سئل عن رجلٍ','1','250',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (81,'أصاب مِنْ امرأة حرامًا، أتحل له أن يتزوجها؟
قال: لا نعلم لهما من التوبة والإحسان شيئًا أفضلَ من ذلك.','1','251',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (82,'<span data-type="title" id=toc-32>٣٢ - باب: العنين</span>.
سألت أحمدَ قلتُ: رجلٌ تَزَوَّجَ امرأةً، فلم يقدر أن يقربها؟
قال: يؤجل سنة.
قلتُ: فإن لم ترافعه؟
قال: إذا سكتت فَمهْ.
وسألتُ إسحاقَ، قلتُ: أخبرني رضي الله عنك عن امرأةٍ استَعْدَتْ على زوجها أَنَّه عنين، وأنه لا يقربها، وقال الزوجُ: كذبتِ، كيف يُعْرَفُ ذلك؟ وما الحكم في ذلك؟
قال: يريهم ماءه هذا حكمه.','1','252',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (83,'حدثنا محمد بن نصر، قال: ثنا حسان بن إبراهيم، عن سفيان، عن ابن جريج، عن عطاء، قال في العنين: إذا قال إنَّى قد جامعتُ. قال: لا يُصدَّق حتى يُريهم النطفة.
حدثنا المسيب بن واضح، قال ثنا ابن مبارك، عن يحيى بن أيوب، قال: حدثني يزيد بن أبي حبيب، عن بكير بن الأشج، عن سليمان بن يسار أن ابن','1','253',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (84,'سندر من أصحاب النبي - صلى الله عليه وسلم - خصيا تزوج امرأةً، ولم يُعلم أنه خصي فنزعها منه عمر بن الخطاب، وأمره أَنْ يُعلِمَها/١٧/ثم يُخيرها.
حدثنا سعيد بن منصور، قال ثنا عوان، عن أبى بشر، عن عمرو بن هرم، عن جابر بن زيد في الخنثى.
قال: ألزقوه بالحائط، فإن بال عليه فهو ذكر، وإن بال في رجليه فهو أنثى.','1','254',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (85,'قال سعيد، وحدثنا أبو عوانة عن قتادة، قال: فذكرت قول جابر بن زيد لسعيد بن المسيب، فقال سعيد: أرأيت إن بال منهما جميعا؟
قلتُ: لا أدرى.
قال: من أيهما ما سبق.','1','255',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (86,'<span data-type="title" id=toc-33>٣٣ - باب: العقيم يتزوج</span>.
سألتُ إسْحاقَ أيضا: قلتُ: ما قولُك في العقيم يتزوج امرأةً، ولا تعلمُ المرأةَ، ثم علمت بعدُ، ألها الخيار؟
قال: كلما لم تعلم المرأة من ذلك العلم ما يعلم هو، فلها الخيار؛ لأنه يَقْدُمُ على علم.
حدثنا محمد بن نصر، قال: ثنا حسان، عن سفيان، عن خالد وهشام، عن محمد بن سيرين: أَنَّ رجلاً تزوَّج امرأةً وهو عقيم لا يُولدُ له، فقال عمر: أخبرتَهم؟
قال: لا.
قال: فأخبرهم، ثُم خيرهم.','1','256',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (87,'<span data-type="title" id=toc-34>٣٤ - باب: من تزوج، وشرط لها ألا يخرجها</span>
سألت أحمدَ، قلتُ: رجلٌ تَزَوَّجَ امرأةً، وشَرطَ لها أن لا يخرجها من قريتها، ثم بدا له أن يخرجها؟
قال: ليس له أن يخرجها.
وسألتُ إسحاق، قلتُ: رجلٌ تَزَوَّجَ امرأةً على ألا يخرجها من مصرها، ثُمَّ بدا له أن يخرجها؟
قال: يُحكم له بإخراجها؟
حدثنا عباس بن عبد العظيم، قال: حدثنا يحيى بن سعيد، عن عبد الحميد بن جعفر، قال: ثنا يزيد بن أبي حبيب، عن مرثد بن عبد الله اليزني، عن عقبة بن','1','257',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (88,'عامر، عن النبي - صلى الله عليه وسلم - ((أحق الشروطِ أَنْ يوفى بها ما استحللتم به الفُروج)).
قلتُ لأحمدَ: رجلٌ تَزَوَّجَ امرأةً، وشرط لها أَنْ يأتيها في كل عشرة أيام مرة؟
قال: نعم، هذا جائز.','1','258',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (89,'حدثنا عمرو بن عثمان: قال: حدثنا بقية بن الوليد: قال: سئل الزبيدي عن الرجل يشترط على المرأة عند عقدة نكاحها ليس لك عليّ أن آتيكِ إلا في اليومين المرة الواحدة، أو في الثلاث مرة، من السبب كذا وكذا؟
قال: له شرطه ذلك.
قلنا: فإن نكح؟
قال: إذا نكح فعليه أن يعدل في المبيت، وغير ذلك.
قلتُ: فإن غلب؟
قال: فهذا رجل عاصي، إنما ينبغي له أن يؤمر بذلك ويحث عليه، وليس عليه في ذلك شئ من أمر عقوبة إنما ينتظر العقوبة من الله، أو يعفو عنه.','1','259',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (90,'حدثنا بكر بن خلف، ثنا يزيد بن زريع، عن يونس، قال: كان الحسن لا يرى بأسًا أن يتزوج الرجلُ المرأةَ، ويشترط عليها أن يأتيها إن شاء ليلا، وإن شاء نهارا، وإن شاء في الأيام يومًا إذا كان ذلك علانيةً.
حدثنا سعيد ين منصور، قال: ثنا هشيم/١٨/قال ثنا يونس، عن الحسن أنه كان لا يرى بأسًا بتزويج النهاريات، وكان ابن سيرين يكره ذلك.','1','260',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (91,'<span data-type="title" id=toc-35>٣٥ - باب: إذا شرط لها بالنفقة</span>.
قلتُ لأحمدَ: الرجلُ يتزوج على أن ينفقَ عليها كلَّ شهرٍ خمسةَ دراهم، أو عشرةَ دراهم؟
قال: ما أدري، قال: والنكاح جائز، ولها أن ترجع عن هذا الشرط.
حدثنا علي بن عثمان، قال: ثنا حماد، عن يونس، عن الحسن أنه كان لا يرى بأسًا بالشرط في النكاح، وكان محمد بن سيرين يكرهه، ويقول: ليس هكذا نكاح المسلمين.','1','261',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (92,'<span data-type="title" id=toc-36>٣٦ - باب: إذا اشترط أن يعزل عنها</span>.
سألت إسحاق: قلتُ: الرجل يتزوج المرأة، ويشترط عليها أن يعزل عنها، فلما حولها أبت، ولم ترض بالعزل قال لها: ذلك الخيار إليها، فإن أذنت أيضًا بعد الملك أن يعزل عنها، ثم ندمت، فلها ذلك.
حدثنا محمود بن خلد، قال: ثنا عمر بن عبد الواحد: قال: سمعت الأوزاعى يقول: في رجل تزوج امرأة، وشرط عليها عند عقدة النكاح أن يعزل عنها، فقال: مضى النكاح، وهذا شرط فاسد.','1','262',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (93,'<span data-type="title" id=toc-37>٣٧ - باب: من تزوج اليهودية على المسلمة</span>.
سألت أحمدَ بن حنبل: قلتُ: رجل تزوج يهودية، أو نصرانية على مسلمة، كيف القسمة؟
قال: بالسوية.
قلتُ: فيتزوج أمة على يهودية أو نصرانية.
قال: فيه اختلاف.
حدثنا المسيب بن واضح: قال: ثنا ابن مبارك، عن سعيد، عن قتادة، عن ابن','1','263',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (94,'المسيب والحسن قالا: يقسم بالسوية بين المسلمة والنصرانية وقالا: لا يتزوج الأمة على النصرانية واليهودية، ولكن يتزوج اليهودية والنصرانية على الأمة إن شاء، ويقسم للحرة يومين، وللأمة يومًا، والنفقة كذلك.','1','264',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (95,'<span data-type="title" id=toc-38>٣٨ - باب: نكاح أهل الكتاب</span>.
قلتُ لأحمدَ: فلا يحل من نساء غير أهل الإسلام إلا اليهوديات والنصرانيات.
قال: لا تعجبني المجوسيات.
حدثنا أبو معن الرقاشي، قال: ثنا عبد الرحمن، قال: ثنا سفيان، عن حماد قال: سألت سعيد بن جبير، عن تزويج اليهودية والنصرانية، فقال: لا بأس بها،','1','265',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (96,'فقلتُ: يقول الله: {وَلاَ تَنكِحُواْ الْمُشْرِكَاتِ حَتَّى يُؤْمِنَّ} [البقرة: ٢٢١].
قال: أهل الأوثان والمجوس.','1','266',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (97,'<span data-type="title" id=toc-39>٣٩ - باب: من جمع بين الحرة والأمة</span>.
قلتُ لأحمدَ: مملوك تحبه أمة، هل يتزوج عليها حرة؟
قال: لا يعجبنا.
قلتُ: /١٩/فيتزوج المملوك أمة على حرة.
قال: لا.
حدثنا أبو معن، قال: ثنا الحسن بن حبيب قال: حدثنا زكريا عن الشعبى، عن مسروق قال: لا يصلح للحر أن يجمع بين حرة وأمة،','1','267',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (98,'ويصلح للمملوك.
حدثنا محمود، قال أبنا عمر: عن الأوزاعى: قال: سألت الزُّهْرِيّ عن رجلٍ تَزَوَّجَ أمة على حرة، أحرمت عليه الحرة، أم تنزع منه الأمة، ولا تخير الحرة؟
قال: لا تنزع منه الأمة، ويعاقب، ولا تخير الحرة.
قلت للأوزاعى: وكيف إن كانت تحته أمة، ثم تزوج عليها حرة، ولم يعلم الحرة أن تحته أمة؛ حتى دخل بها قال: تخير الحرة، فإن شاءت فارقته، وإن شاءت استقرت تحته، ولها الثلثان من نفسه وماله.
حدثنا المسيب: قال: ثنا ابن مبارك, قال: سمعت سفيان يقول قي عبد يتزوج حرة على أمة.
قال: يقسم كما يقسم الحر للحرة يومين، وللأمة يوما.','1','268',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (99,'<span data-type="title" id=toc-40>٤٠ - باب: كم يقيم عند البكر والثيب</span>؟
سألت أحمدَ: قلتُ: فإن تزوج بكرًا على امرأته، كم يقيم عندها؟
قال: سبعة، ثم يسوى.
قلتُ: فإن تزوج ثيبًا؟
قال: يقيم ثلاثا، ثم يسوى.
حدثنا محمود، قال: ثنا عمر، قال: سمعت الأوزاعى يحدث عن الرجل يتزوج البكر قال: مضت السنة بأن يجلس في مدته سبعًا وفي البيت أربعًا.
قال: وحدثنا عمر عن الأوزاعى، عن الرجل يتزوج المرأة البكر، وله امرأة أخرى، كم له أن يمكث عنده؟
أرأيت إن تزوج المرأة الثيب، كم له أن يمكث عندها؟
قال: البكر ثلاثا، ثم يقسم، وللثيب ليلتين.','1','269',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (100,'قلتُ لأحمدَ: رجل له امرأة، وهو يصوم النهار، ويقوم الليل، ولا يقرب امرأته، هل له ذلك؟
فكره ذلك كراهية شديدة.
حدثنا أبو الحسن اللاحقى: قال: ثنا الحسن بن أبي الجعد قال: سمعت الحسن يحدث وأنا قاعد قال لقنني صفوان، ونعم الصفوان كان في هذا المسجد، فقال: يا حسن.
قلتُ: يا حسن؟
قال: أني تزوجت امرأة، وأنا راغب في ولدها، فلبثت عندي لا ترى منى شيئا، أما النهار فأصومه، وإذا كان الليل أدركتنى فترة الصائم، فإذا كان آخر الليل قمت إلى طهورى وسحورى، فلما رأت ذلك، قالت: يا فلان إنى امرأة من هذه النساء، وإن لي حاجة كحاجتهن فانظر في ذلك، واعفنى رحمك الله، فما ترى؟
قال الحسن: لا أرى عليك في ما مضى بأسًا إن شاء الله، وأرى أن تعفيها.','1','270',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (101,'<span data-type="title" id=toc-41>٤١ - باب: المرأة تصوم تطوعًا بغير إذن زوجها</span>.
قلتُ لأحمدَ: فهل للمرأة أن تصوم تطوعًا/٢٠/، وزوجها شاهد إلا بإذنه؟
قال: لا.
قلتُ: فإن صامت، فوقع عليها زوجها، هل عليها قضاء؟
قال: لا بأس أن تقضى.
حدثنا أبو بكر الحميدى قال: حدثنا سفيان، قال: حدثنا أبو الزناد، عن موسى بن أبي عثمان، عن أبيه، عن أبي هريرة: أن رسول الله - صلى الله عليه وسلم - قال: ((لا تصوم','1','271',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (102,'المرأة يومًا من غير شهر رمضان، وزوجها شاهد إلا بإذنه)).','1','272',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (103,'<span data-type="title" id=toc-42>٤٢ - باب: الحر يتزوج المملوكة</span>.
وكره أحمد أن يتزوج الحر المملوكة، وسألت أحمدَ مرة أخرى: قلتُ: أيتزوج الرجل الأمة، وهو يقدر على الحرة؟ فكأنه كره ذلك.
قلتُ: فأم الولد؟
قال: أم الولد أمة ما دام سيدها حيًا.
قلتُ: وكذلك أولادها؟
قال: نعم.
وسألتُ إسْحاقَ عن الرجُل الحر يتزوج الأمة؟ فكرهه، وقال: لا ينبغي إلا أن يعشقها.','1','273',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (104,'حدثنا محمود قال: حدثنا عمر بن عبد الواحد.
قال: قلت للأوزاعي في الحر، هل ينكح الأمة، وهو يجد طولا ينكح به الحرة؟
قال: لا؟
قلتُ: فإن نكحها؟
قال: تنزع منه.
حدثنا عبد الله بن معاذ، قال: ثنا أبي، نا أشعث، عن الحسن أنه كان يكره أن يتزوج الحر المكاتبة، فإن فعل فرق بينهما.
حدثنا محمد بن نصر: قال: ثنا حسان بن إبراهيم، عن سعيد، عن أيوب، عن نافع، أنه قيل لابن عمر: إنَّ ابن الزبير رخص في بيع أمهات الأولاد، فقال','1','274',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (105,'ابن عمر: لكن عمر أتعرفونه؟ قال: أم الولد لا تباع، ولا توهب، ولا تورث، ولكن يستمتع بها صاحبها ما بدا له، فإذا مات، فهى حرة.
سألت إسحاق: قلتُ: رجل تزوج أمة، فأراد مواليها أن يسافروا بها، هل لهم ذلك؟
قال: لهم ذلك، وإن أراد أن يتبعهم، فليتبعهم.
حدثنا عبيد الله بن معاذ: قال: ثنا أبي، قال: ثنا أشعث، عن الحسن أن الرجل إذا زوج أمته عبدًا، أو حرًا، فلزوجها أن يخرجها من دار إلى دار، إلا أن يشترطوا أن لا يخرجها من دارهم، وليس له أن يخرجها من المصر.
مسألة في النكاح والعزوبة (١)
قلتُ لأحمدَ: التزويج أحب إليك في زماننا هذا، أم العزوبة؟
قال: التزويج أحب إلي.
_________
(١) من هامش الأصل.','1','275',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (106,'<span data-type="title" id=toc-43>٤٣ - باب: كم يتزوج العبد من النساء</span>؟
قلتُ لأحمدَ: العبد كم يحل له من النساء؟
قال: ثنتان، وسئل أحمد مرة أخرى عن العبد كم يتزوج من النساء؟ قال: ثنتين.
حدثنا المسيب، قال: ثنا المبارك، عن ابن عيينة، عن محمد بن عبد الرحمن مولى آل طلحة، عن سليمان بن يسار، عن عبد الله بن عتبة، عن عمر بن الخطاب، قال: ينكح العبد اثنتين، ويطلق اثنتين (١). /٢١/
_________
(١) في هامش الأصل عند هذا الموضع وجد مكتوبًا: عورضت الكراسة كلها فصمت إن شاء الله.','1','277',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (107,'حدثنا المسيب، قال ثنا ابن مبارك، عن سفيان، عن أبي أبي نجيح، عن مجاهد. قال: يتزوج العبد أربعًا.
حدثنا عمرو بن عثمان: قال: حدثنا بقية، عن الأوزاعى: قال: حدثنى الزُّهْرِيّ: قال: يتزوج العبد إن شاء أربع نسوة.','1','278',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (108,'<span data-type="title" id=toc-44>٤٤ - باب: المملوك يتزوج بغير إذن سيده</span>.
سألت أحمدَ: قلتُ: مملوك تزوج بغير إذن مولاه؟
قال: لا يجوز.
قلتُ: فإن أجازه المولى؟
قال: بنكاح جديد.
قلتُ: فإن زوجه المولى، بيد مَن الطلاق؟
قال: بيد المملوك،','1','279',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (109,'وكذلك المهر.
قلتُ: فإن تزوج بغير إذن المولى، فدخل بها، هل لها مهر؟
قال: فيه اختلاف.
قال: وقال عثمان بن عفان: لها خمس المهر.','1','280',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (110,'قال أحمد: وأنا أذهب إليه، وهو في رقبة العبد.
وسألت إسحاق: قلتُ: مملوك تزوج بغير إذن مولاه؟
قال: لا يجوز للعبد البتة أن يتزوج بغير إذن مولاه، لا اختلاف فيه، وسألت إسحاق أيضًا عن عبد تزوج بغير إذن سيده.
قال: ليس هذا نكاح.
قلتُ: لها المهر؟
قال: إذا عتق العبد، فلها المهر كاملاً؟
قلتُ: فمن يقول لها الخمسان؟
قال: فيه قول، وذكر عن أبي موسى، ولم يذهب إليه.','1','281',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (111,'حدثنا أحمد بن حنبل، قال: ثنا وكيع، قال: ثنا حسن بن صالح، عن عبد الله، عن محمد بن عقيل، عن جابر بن عبد الله قال: قال رسول الله - صلى الله عليه وسلم - ((أيما عبد تزوج بغير إذن مواليه، فهو عاهر)).
حدثنا أحمد: قال: ثنا محمد بن بشر وعبد الله بن نمير، قال: ثنا عبيد الله، عن نافع، عن عبد الله أنه كان يرى نكاح العبد بغير إذن سيده زنا، وكان','1','282',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (112,'يعاقب الذين زوجوه.
حدثنا أحمد، قال: حدثنا يزيد بن هارون، قال: أخبرنا سعيد، عن قتادة، عن خلاس بن عمرو أن غلامًا لأبى موسى الأشعرى تزوج مولاة التيجان أخى بنى عائش، فساق إليها خمسة أبعرة، فكتب في ذلك أبو موسى إلى عثمان، ففرق بينهما، وجعل لها الخمسين بعيرين، ورد سائره إلى أبي موسى.','1','283',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (113,'حدثنا أحمد، قال: ثنا عبد الرحمن بن مهدى، قال: ثنا همام، عن مطر، عن نافع أن ابن عمر كان إذا تزوج عبده بغير إذنه جلده خمسين، وقال للمرأة أبحت فرجك، فأبطل صداقها.
حدثنا أحمد، قال: ثنا وكيع، قال: ثنا سفيان: عن فراس، عن الشَّعبيِّ قال: يؤخذ منها ما استهلكت، وما لم تستهلك.','1','284',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (114,'حدثنا أحمد، قال: ثنا وكيع، قال: حدثنا سفيان، عن مغيرة، عن إبراهيم، يؤخذ منها ما لم تستهلك/٢٢/ وما استهلكت فلا شئ.
حدثنا أحمد، قال: بنااسماعيل بن إبراهيم، قال: ثنا أيوب، عن نافع، عن ابن عمر: قال: إذا نكح العبد بإذن سيده، فالطلاق بيد العبد.
حدثنا أحمد، قال: ثنا معاذ ابن معاذ، عن أشعث، عن الحسن، عن سعيد بن المسيب: قال: إذا تزوج العبد بإذن سيده، فالطلاق بيد العبد، وهو رأى الحسن.','1','285',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (115,'حدثنا أحمد: قال: حدثنا محمد بن جعفر: قال: ثنا سعيد: عن قتادة، عن أنس بن مالك وابن عباس وجابر بن عبد الله قالوا: الطلاق بيد السيد، يعنى إذا تزوج بغير إذنه.
حدثنا أبو معن الرقاشى، قال: ثنا أبو عمران الرازى، قال: حدثنا الحسن بن محمد الكوفى، قال: ثنا أبو جرير، عن الشعبى، عن على وابن مسعود قالا:','1','286',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (116,'إذا زوج الرجل غلامه، فالطلاق بيد العبد، والصداق في رقبة العبد، وإن أراد بيعه؛ لأن المولى أذن له، وإذا تزوج بغير إذن مواليه، فالطلاق بيد المولى والصداق عليه؛ لأن الله يقول: {ضَرَبَ اللهُ مَثَلاً عَبْدًا مَّمْلُوكًا لاَّ يَقْدِرُ عَلَى شَيْءٍ} [النحل: ٧٥].','1','287',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (117,'<span data-type="title" id=toc-45>٤٥ - باب: المرأة توفى عنها زوجها قبل أن يدخل بها</span>.
سألت أحمدَ قلتُ: امرأة توفى عنها زوجها قبل أن يدخل بها؟
قال: لها المهر، وعليها العدة، ولها الميراث.
قلتُ: وكذلك لو ماتت هي، كان يرثها؟
قال: نعم.
قلتُ: فإن كان لم يسم لها مهرًا؟
قال: لها مثل صداق نسائها.
قلتُ: فإن كان صداق نسائها مختلف مثل ألف وألفين.
قال: وسط من ذلك.','1','288',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (118,'وسألت أحمدَ مرة أخرى: قلتُ: الرجل يتزوج المرأة، فيموت قبل أن يدخل بها؟
قال: لها المهر والميراث، وعليها العدة، وإن ماتت هي فله الميراث.
حدثنا أبو الوليد الطيالسى، قال: حدثنا همام، عن قتادة، عن جلاس وأبي حسان، عن عبد الله بن عتبة: أنه اختلف إلى ابن مسعود في رجل تزوج امرأة، فمات عنها، ولم يفرض لها، ولم يدخل بها، فاختلفوا إليه شهرًا، فقال: لها صدق نسائها، ولها الميراث، وعليها العدة، فقام الحراح أبو سنان، فشهد: أن رسول الله - صلى الله عليه وسلم - قضى به في بروع بنت واشق','1','289',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (119,'الأشجعية، وكان زوجها هلال بن مروان.
حدثنا محمود بن خالد، قال: ثنا عمر بن عبد الواحد، قال: كتبت إلى الأوزاعى أسأله عن رجلٍ تَزَوَّجَ امرأة، فلم يدخل بها، ولم يفرض لها، ثم هلك أو هلكت هي، كيف القول فيهما؟ /٢٣/أسواء هما؟
قال: هما سواء، إن مات ورثته، ولا صداق لها، وإن ماتت ورثها، ولا صداق عليه.','1','290',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (120,'<span data-type="title" id=toc-46>٤٦ - باب: مَنْ تَزَوَّجَ امرأةً عَلَى حكمها</span>.
قلتُ لأحمدَ: رجلٌ تَزَوَّجَ امرأةً عَلَى حُكمها؟
قال: لها ذلك ما لم تُشْطِط.
حدثنا أبو معن الرقاشى قال: حدثنا عبد الوهاب الثقفى، قال: حدثنا أيوب، عن محمد، أن الأشعث بن قيس تزوَّج امرأةً على حكمها، ثم طلَّقها قَبْل أَنْ تَحْكُم.
فقال عمر: امرأةً من المسلمين؟','1','291',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (121,'<span data-type="title" id=toc-47>٤٧ - باب: من زوج ابنته، وأخذ شيئًا</span>.
سألتُ أحمدَ، قلتُ: رجل يزوج ابنته على أن يُعَطى شيئًا؟
قال: لا بأس بذلك.
حدثنا يحيى بن عبد الحميد قال: حدثنا شريك، عن أبى إسحاق، عن مسروق: أنه زَوّج بنته من','1','292',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (122,'السائب بن الأقرع، واشترط لنفسه عشرة ألاف، فقسمها في المساكين، وفي سبيل الله.','1','293',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (123,'<span data-type="title" id=toc-48>٤٨ - باب: من دخل بالمرأة قبل أن يعطيها شيئًا</span>.
قلتُ لأحمدَ: الرجل يتزوجُ المرأةَ، فَيدخل بها قبل أَنْ يعطيَها شيئًا؟
قال: إذا أعطاها فهو أحسن.
حدثنا أبو معن، قال: ثنا حماد بن مسعدة، عن أبي ديب عن الزُّهْرِيّ: قال: مضت السُّنة في الرَّجلِ إذا فرض للمرأة شيئًا، أَنَّ لها مثل فرض لها، غير أنه لا يدخل بها حتى يعطيها شيئًا إما كسوة وإما شيئًا، وإنْ لم يفرض لها شيئًا، فدخل بها، فلها مثل صداق نسائها.','1','294',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (124,'(طلاق المكره وكيفيته ...) (١).

<span data-type="title" id=toc-49>٤٩ - باب: من أكره على التزويج</span>.
قلتُ لأحمدَ: الرجل يضطهد، فيزوج؟
قال: إذا ضُرب، أو نحو ذلك، فلا يجوز.
قلتُ: وكذلك الطلاق؟
قال: نعم، إذا عذب رجوتُ.
حدثنا محمد بن إسماعيل، قال: ثنا حفص، قال: حدثنا عاصم، عن محمد بن سيرين، عن شريح أنه كان لا يجيز
_________
(١) من هامش الأصل وفيه كلمة غير واضحة.','1','295',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (125,'نكاح المضطهد.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: ثنا منصور ويونس، عن الحسن: أنه كان لا يرى طلاق المكره شيئًا.','1','296',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (126,'<span data-type="title" id=toc-50>٥٠ - باب: ولد الزنا</span>.
سألتُ أحمدَ: قلتُ: ولدُ الزنا ينكح أو يُنكح إليه؟
فكأنه لم يحب ذلك.
حدثنا سعيد بن منصور، قال: ثنا هشيم، عن يونس، عن الحسن قال: ليس لولد الرِّشدة فضل على الزنا إلا بالتقوى.','1','297',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (127,'<span data-type="title" id=toc-51>٥١ - باب: الرجل يكره أن يتزوج ختنه على ابنته</span>.
قلتُ لأحمدَ: الرُّجل يكره أن يتزوج ختنه على بنته، هل في هذا شئ؟
قال: ومن يملك ذلك من نفسه.
قال: حدثنا أحمد بن يونس، قال: حدثنا الليث بن سعد، قال: /٢٤/حدثني عبد الله بن عبيد الله بن أبي مليكة القرشي التيمي أن المسور بن مخرمة حدثه: أنه سمع رسول الله - صلى الله عليه وسلم - على المنبر وهو يقول: ((أن بنى هاشم بن المغيرة استأذنوني أن ينكحوا ابنتهم علي بن أبي طالب، فلا آذن، ثم لا إذن، ثم لا إذن، إلا أن يحب بن أبي طالب أن يطلق بنتي وينكح ابنتهم، فإنما ابنتي بضعة مني بريبني ما رابها، ويؤذينى ما آذاها)).','1','298',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (128,'<span data-type="title" id=toc-52>٥٢ - باب: من أصدق امرأته سرًا، ثم أعلن، بأكثر من ذلك</span>.
سألتُ أحمدَ بن حنبل: قلتُ: رجلٌ تَزَوَّجَ امرأةً في السر على ألفين، وفي العلانية على أكثر من ذلك؟
قال: هو ما أقره في العلانية.
سألتُ إسحاق، قلتُ: رجل يتزوج امرأةً، وأمهرها في السر ألفًا، وفي العلانية ألفين؟
قال: يؤخذ بالأكثر إلا أن يقيم بينة أن العلانية كانت سُمعة.
حدثنا محمد بن نصر، قال: حدثنا حسان، قال: قال سفيان: يؤخذ بالعلانية، إلا أن يقيم بينةً أنَّ العلانية كانت سمعة.','1','299',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (129,'حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أخبرنا خلد، عن ابن سيرين، عن شريح قال: إذا تزوج الرجل المرأةَ، وأصدق صداقًا سرًا، وأعلن أكثر من ذلك.','1','300',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (130,'قال: يؤخذ بالسِّر.

<span data-type="title" id=toc-53>٥٣ - باب: كم أقل المهر</span>؟
سألت أحمدَ بن حنبل: قلتُ: كم أقل المهر؟
قال: ما تراضوا عليه.
وسُئل إسحاق عن أقل المهر؟
قال: درهم.
حدثنا عمرو بن عثمان، قال: حدثنا بقية بن الوليد، قال: سمعتُ عبيد الله بن عمر يقول في المهر: على ما تراضيا عليه، وإن درهم.
حدثنا أبو بكر محمد بن يزيد، قال: ثنا الحسين الجعفى، عن ابن أبي لبيبة عبد الرحمن، عن جده، عن النبي - صلى الله عليه وسلم - قال: ((من استحل فرجًا','1','301',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (131,'بدرهم فقد استحل)).
ثنا عمرو بن عثمان، قال: حدثنا الوليد بن مسلم، عن زهير بن محمد المكى، عن المطلب بن السائب، قال: زوجني سعيد بن المسيب ابنته على درهمين ما لها صداق غيره.
حدثنا أبو أمية، قال: حدثنا موسى بن إسماعيل، قال: حدثنا صالح بن رومان،','1','302',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (132,'قال: ثنا أبو الزبير، عن جابر أن النبي - صلى الله عليه وسلم - قال: ((لو أَنَّ رجلاً أعطى امرأة تمرًا ملأ كف، فرضيت به، كان لها صداقًا)).
حدثنا أبو أمية، قال: حدثنا محمد بن داود، قال: ثنا إبراهيم بن الحكم بن أبان، قال: ثنا أبي، عن عكرمة، عن ابن عباس أنه كان لا يرى بأسًا بالنكاح على موزة.','1','303',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (133,'حدثنا أبو بكر محمد بن يزيد، قال: حدثنا الربيع بن نافع، قال: حدثنا ابن عباس، عن عتبة بن حميد، عن عبيد الله /٢٥/ بن بكر بن أنس، عن أنس بن مالك، قال: تزوج أبو طلحة أم سليم على إسلامه، أصدقها إياه، فذكر ذلك للنبي - صلى الله عليه وسلم - , فحسَّنه.','1','304',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (134,'<span data-type="title" id=toc-54>٥٤ - باب: هل للمشرك من ولاية</span>؟
قلتُ لأحمدَ: امرأة أبوها نصراني، وأخوها مسلم، من يزوجها؟
قال: الأخ.
قلتُ: فهل للمشرك من الولاية شئ؟
قال: لا بتةً.
وسألتُ إسْحاقَ قلتُ: رجل مجوسي له بنت مسلمة، هل يزوجها الأب؟
قال: لا يزوجها الأب ولكن يزوجها بعض قرابتها من المسلمين ممن يخبروا النكاح (بنو العم وغيرهم.
حدثنا أبو هشام (١)، نا حسان بن إبراهيم، قال: قال سفيان في رجل مشرك زوَّج بنته وهي صغيرة برضاء منها، وأبى أولياؤها من المسلمين ذلك) (٢).
قال: إذا زوجها أبوها، وقد بلغت برضا منها كفؤًا كان
_________
(١) هو محمد بن نصر الكرماني.
(٢) ما بين القوسين من الهامش.','1','305',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (135,'النكاح جائزًا، ولم يكن لأوليائها أن يردوا ذلك؛ لأن من رد من الأولياء الكفؤ، فهو مضار، ومن رد من القضاة، فهو جائز، وإن زوجها أبوها غير كفؤ برضا منها، أو بغير رضا كان للأولياء أن يردوا ذلك، وإذا زوجها وهي صغيرة جاز ذلك.
(
حدثنا محمد، نا حسان، نا سفيان في مشرك زوج بنته، ولم يكن برضا منها، وأبى أولياؤها من المسلمين أن يجيزوا النكاح.
قال: إذا زوجها أبوها، وقد بلغت برضا منها كفؤا كان النكاح جائزًا، ولم يكن للأولياء أن يردوا ذلك) (١).
حدثنا أبو معن: قال: ثنا أبو بكر الحنفى. قال حدثنا سعيد، عن قتادة قال: المسلم أحق ونكاحه أجوز، وإن كان النصرانى زوجها قبله.
سألت إسحاق، عن مشرك أراد أن يزوَّج ابنته، فجلس رجل مسلم، فخطب لهم، وزوج؟
قال: لا ينبغي أن يُعَانُوا في شهادة، ولا في شئ.
_________
(١) كذا بالأصل وهي نفس المسألة السابقة.','1','306',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (136,'<span data-type="title" id=toc-55>٥٥ - باب: من تزوج امرأة، فولدت لأقل من ستة أشهر</span>.
سألت أحمدَ قلتُ: امرأةٌ ولدت لأقل من ستة أشهر؟
قال: لا يلحق ثنا الولد إلا أن تلد لستة أشهر، أو أكثر، وقال: الولد للزوج الأول.
قلتُ: فإن اعتدت أربعة أشهر وعشرا، ثم ولدت ولدًا لأربعة أشهر، أو لخمسة أشهر؟
قال: الولد للزوج الأول.
حدثنا سعيد بن منصور: قال: ثنا عبد الله بن المبارك، عن علي بن المبارك، عن يحيى بن أبى كثير، عن يزيد بن نعيم، عن سعيد بن المسيب: أن رجلاً','1','307',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (137,'تزوج امرأة، فدخل بها، فوجدها حبلى، فرفع ذلك إلى رسول الله - صلى الله عليه وسلم -، ففرق بينهما، وجعل لها الصداق، وجلدها مائة.
حدثنا أبو معن، قال: ثنا خلد بن الحرث، قال: ثنا سعيد، عن قتادة، عن سعيد بن المسيب: أن رسول الله - صلى الله عليه وسلم - رجم امرأة ولدت لأربعة أشهر، وجعل لزوجها ما أدرك من متاعها.','1','308',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (138,'<span data-type="title" id=toc-56>٥٦ - باب: من تزوج امرأة في عدتها</span>.
سألت أحمدَ بن حنبل، قلتُ: /٢٦/ رجل تزوج امرأة في عدتها، ثم علم بها بعد ذلك؟
قال: يفرق بينهما، ولها المهر.
قلتُ: المهر لها، أو لبيت المال؟
قال: هو لها.
قلتُ: فإن ولدت ولدًا فأظنه قال: إن ولدت لأقل من ستة أشهر، فهو للأول، قال: وتعتد من هذا الثاني.
وسألت أحمدَ مرةً أخرى، قلتُ: الرجلُ يتزوج المرأة في عدتها؟
قال: إن كان دخل بها، فلها المهر بما استحل من فرجها، وتكمل عدتها من الأول، ثم تعتد من هذا عدةً جديدة، وإن لم يكن دخل بها فرق بينهما، ولا شئ لها.','1','309',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (139,'قلتُ: فإن دخل بها، ثم فُرِّق بينهما، هل يجتمعان أبدًا؟
قال: نعم، إن أراد أن يتزوجها تزوجها.
وسألت أبا ثور إبراهيم بن خالد، قلتُ: رجل طلَّقَ امرأتَه، فتزوجت في عدتها، فجاءت بولد، فاداعيا الولد؟
قال: إذا جاءت به لأقل من ستة أشهر، فهو للزوج الأول، وإن كان لأكثر من ستة أشهر دعى القافة.
قلتُ: إنهم في موضع لا يجدون القافة؟
قال: يحملون إلى موضع القافة.
وسمعت علي بن عبد الله يقول: في امرأة تزوجت في عدتها، قال: يفرق بينهما، ولا يجتمعان أبدًا.','1','310',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (140,'حدثنا أبو الأزهر: قال: حدثنا الوليد بن الوليد قال: سئل الأوزاعى عن رجلٍ تَزَوَّجَ في العدة؟
قال: عليهما الضرب دون الحد، ولها صداقها، ويفرق بينهما.
قال الأوزاعى: وسمعت الزُّهْرِيّ يقول ذلك، ولا يجتمعان إبدًا إن كان دخل بها.
حدثنا المسيب، قال: حدثنا ابن مبارك، عن أشعث، عن الشعبي، عن مسروق أنَّ عمر بن الخطاب بلغه أن امرأةً من قريش تزوجها رجلٌ من ثقيف في عدتها، فأرسل إليهما، وفرق بينهما، وعاقبهما، وقال: لا ينكُحها أبدًا، وجعل الصداق في بيت المال، قال: وفشى ذلك في الناس، فبلغ عليا، فقال رحم الله أمير المؤمنين ما بال الصداق في بيت المال إنهما جهلا، فينبغي','1','311',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (141,'للإمام أن يردهما إلى السنة.
قيل: فما تقول أنت فيها؟
قال: لها الصداق بما استحل من فرجها، ويفرق بينهما، ولا جلد عليهما، وتكمل عدتها من الأول، وتكمل عدتها من الآخر، ثم يكون خاطبا، فبلغ ذلك عمر بن الخطاب، فخطب الناس، فقال: يا أيها الناس ردوا الجهالات إلى السنة.
حدثنا المسيب، قال: حدثنا ابن مبارك، عن إسماعيل بن أبي خالد، عن الشَّعبيِّ في المرأة تنكح في عدتها.
قال: تعتد من الآخر، ثم تكمل عدتها من الأول، وقال إبراهيم: تعتد من الأول، ثم تعتد من الآخر. /٢٧/','1','312',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (142,'<span data-type="title" id=toc-57>٥٧ - باب: إذا أغلق بابًا، وأرخى سترًا</span>.
سمعتُ أحمدَ يقولُ: إذا أغلق بابًا، أو أرخى سترًا، فقد وجب الصداق.
وسألت أحمدَ مرةً أُخْرى قلتُ: رجلٌ تَزَوَّجَ امرأةً، فأرخَى عليه وعليها سِترًا، فقال: لم أمسها، وقالت هي: لم يمسني؟
قال: عليه المهر، لا يقبل قوله ولا قولها. وذهب إلى أن عليها العدة.','1','313',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (143,'قلتُ: فإنْ أخذها عنْدَ نسوةٍ، فمسَّها، وقبض عليها، ونحو ذلك من غير أن يخلوَ بها؟
قال: إذا نال منها شيئًا لا تحل لغيره، فعليه المهر.
وسمعت إسحاق، وسأله أبو شداد المروزي عن رجلٍ تَزَوَّجَ جاريةً بكرًا، فأخذها وسط جواري فجامعها دون الفرج؟
قال إسحاق: كلما كان الجماع منه دون الفرج، وهي مع جواري، فليس ذلك بخلوةٍ، ولا يجب المهر إن طلقها، إنما يجب نصف المهر، ولو كان هذا الجماع في الخلوة، أو ستر باب لكان يجب المهر أجمع إلا أن تكون ممتنعة في الستر أيضًا، أو كانت علةُ حيضٍ، وهي التي منعت الزوج من الوطء، أو هي محرمة بحجة، أو لأية علة كانت؛ لأن الخلوة توجب المهر بالسُّنَّة؛ لقول عمر وعلي وغيرهما من أصحاب النبي - صلى الله عليه وسلم -: إلا أن تجئ علته من المرأة، وهي التي تمنع الزوج من الوطء.','1','314',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (144,'حدثنا إسحاق، قال أبنا وكيع، قال: حدثنا العمري، عن نافع، عن ابن عمر، عن عمر بن الخطاب، قال: من أغلق بابًا، أو أرخى سترًا، فقد وجب الصداق.
حدثنا إسحاق، قال: أنبا وكيع، قال: أنبا الحسن بن صالح، عن فراس، عن الشعبي، عن ابن مسعود قال: لها نصف الصداق ما لم يجامعها، ولو جلس بين رجليها.','1','315',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (145,'حدثنا إسحاق قال: أخبرنا جرير، عن ليث، عن طاووس، عن ابن عباس قال: لها نصف الصداق ما لم يخلُ معها.','1','316',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (146,'<span data-type="title" id=toc-58>٥٨ - باب: التحليل</span>.
سُئِلَ أحمدَ عَنْ التحليل: إذا هَمَّ أحد الثلاثة بالتحليل؟
فقال أحمد: كان الحسن وإبراهيم والتابعون يشددون في ذلك، وقال أحمد الحديث عن النبي - صلى الله عليه وسلم - أنه قال: ((أتريدينَ أنْ ترجعي إلى رِفاعة؟)).
يقول أحمد: إنها قد كانت هَمَّت بالتحليل، ونية المرأة ليس بشئ إنما قال النبي - صلى الله عليه وسلم -: ((لعنَ الله المُحِل والمحلل له)) وليست نية المرأة بشيء.
حدثنا عبد الله بن الزبير الحميدى، قال: حدثنا سفيان، قال: حدثنا الزُّهْرِيّ، قال: أخبرني عروة بن الزبير، عن عائشة أنه سمعها تقول: جاءت/٢٨/امرأة','1','317',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (147,'رفاعة القرظي إلى النبي - صلى الله عليه وسلم -، فقالت: يا رسول الله إني كنت عند رفاعة القرظي، فطلقني، فبتَّ طلاقي، فتزوجتُ عبد الرحمن بن الزبير، وإنما معه مثل هُدبة الثوب.
فتبسم رسولُ الله - صلى الله عليه وسلم -، وقال: ((أتريدينَ أن ترجعي إلى رفاعة! لا حتى تذوقي عسيله، ويذوق عسيلتك))
حدثنا المسيب بن واضح، قال: ثنا ابن مبارك، عن حكيم بن زريق، عن أبيه، عن سعيد بن المسيب في رجلٍ تزوج ليحلها لزوجها، ولم يشعر بذلك زوجها الأول، ولا المرأة.
قال: إن كان إنما نكحها ليحلها، فلا يصلح ذلك لهما، ولا تحل.
قال ابن المبارك: وسألت سفيان يقول (١) في رجل طلَّقَ امرأتَه، ثم غاب عنها، ثم رجع، فقالت قد تزوجت زوجًا، فدخل بى وطلقنى؟
قال: يصدقها إلا أن يتهمها.
_________
(١) كذا الأصل لعل فيه كلمة (ما) ساقطة وربما كانت وسمعت.','1','318',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (148,'حدثنا المسيب، قال: حدثنا ابن مبارك، عن الأوزاعي، عن الزُّهْرِيّ، عن عبد الملك بن المغيرة، عن ابن عمر أنه سُئلَ عن تحليل المرأة لزوجها، فقال: ذلك السفاح.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: ثنا يونس، عن ابن سيرين أنَّ رجلاً طلَّقَ امرأتَه، فندم، وكان بالمدينة رجل من الأعراب عليه رقعتين، له رقعة يواري بها عورته، ورقعة يواري بها سوأته، فقالوا له: هل لك أن تزّوَّج امرأةً، فتبيت عندها ليلة، ويُجعل لك جُعلُ؟ قال: نعم، فزوجوها منه، فلما دخل بها، فبات عندها، قالت له: هل عندك من خير؟
قال: هو حيث تخشي جعله الله فداها.
فقالت: لا تطلقني، فإن عمر لن يجبرك على طلاقي. فلما أصبحوا لم تفتح لهم الباب حتى كادوا يكسرون الباب، فلما دخلوا قالوا له طلقها، قال: الأمر إليها، فقالوا لها، فقالت','1','319',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (149,'أني أكره أن لا يزال يدخل علىَّ الرجلُ بعد الرجل، فارتفعوا إلى عمر بن الخطاب أخبروه القصة، فرفع يده وقال: اللهم أنت رزقتَ ذا الرقعتين إذ نحل عليه عمر، فقال له: لئن طلقتَها، فأوعده.
حدثنا سعيد، قال: ثنا جرير وأبو معاوية، عن الأعمش، عن مسيب بن رافع، عن قبيصة بن جابر قال: قال عمر بن الخطاب: لا أوتى بمحل، ولا محلل إلا رجمتُهما.','1','320',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (150,'حدثنا سعيد، قال: ثنا جرير، عن مغيرة، عن إبراهيم، قال: إذا هَمَّ الزوجُ الأول، أو المرأةُ، أو الزوجُ الأخير بالتحليل، فالنكاح فاسد.
قلت لإسحاق: فإن تزوجها في عدتها، ففرق بينهما، هل له أن يتزوجها بعد ذلك؟
قال: نعم/٢٩/. وذكرتُ له قول عمر، فكأنه لم يذهب إليه، وذكر أنها تتم عدتها من الأول، ثم تعتد من الثاني.
ثنا محمد بن نصر، قال: ثنا حسان، عن سفيان، عن صالح، عن الشَّعبيِّ أن عليًا قال: يجتمعان.','1','321',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (151,'سألتُ إسْحاقَ بن إبراهيم عن رجل طلَّقَ امرأتَه ثلاثًا، فتزوَّجها غلام لم يحتلم، فدخل بها؟
قال: لا تحلُّ لزوجها.
قلتُ: فتزوجها عبد بدون إذنِ سيده؟
قال: لا يكون هذا نكاحًا. ولم يرخص فيه.
قلتُ: فإن تزوجها رجل بغير وليٍّ، هل تحل لزوجها؟
قال: هذا ليس بنكاح، ولا تحل للزوج.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أنا منصور، عن الحسن في رجل طلَّقَ امرأتَه ثلاثًا، فتزوجها غلام لم يحتلم، فدخل بها، قال: ليس بزوج. حدثنا سعيد، قال: حدثنا هشيم، قال: أنا منصور، عن الحسن في رجل طلق امرأته فيزوجها مملوك بعد إذن سيده فدخل بها، قال: ليس بزوج.
حدثنا سعيد، قال: حدثنا هُشيم، قال: أبنا منصور، عن الحسن في رجلٍ طلَّق امرأتَه فتزوَّجها مملوك بغير إذنِ سيده، فدخل بها؟
قال: ليس بزوجٍ.','1','322',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (152,'قلتُ لإسحاق: مسلم تحته نصرانية، فطلقها ثلاثا، فتزوجها نصراني أو عبد، ثم طلقها، هل يراجعها الأول؟
قال: لا يتزوجها؛ لأن المشرك لا يحلل.
قلتُ: والعبد؟
قال: ولا العبد لا يحلل؛ لأنه عبد، ولم يرخص فيه.
حدثنا المسيب بن واضح، قال حدثنا ابن مبارك، عن يونس، عن الزُّهْرِيّ وأبي الزناد في المسلم يطلق النصرانية، فينكحها نصراني، فطلقها بعد ما مسها، فرأياها تحل للأول المسلم.
حدثنا المسيب، قال: ثنا ابن مبارك، عن سفيان، عن جابر، عن الشَّعبيِّ والحكم','1','323',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (153,'في المملوك يتزوج المرأة قد طلقها زوجها ثلاثًا، فيدخل بها.
قالا: يتزوجها الأول إن شاء. وسُئلا عن النصراني، فقالا: لا تحل له أن يتزوجها.','1','324',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (154,'<span data-type="title" id=toc-59>٥٩ - باب: من ادعى ولد زنا</span>.
سُئِلَ أحمدَ عَنْ رَجلٍ فَجَرَ بامرأةٍ، فادعَى ولدها؟
قال: لا يكون ولده لا يورث، ولا يدخل على حرمته وأنكر ذلك.
قال أحمد: أول قضاء علم برده من قضاء رسول الله - صلى الله عليه وسلم - دعوة زياد.
حدثنا يحيى بن عبد الحميد، قال: ثنا شريك، عن جابر، عن عامر، عن عمر، قال: لا يجوز دعوةُ ولد زنا في الإسلام إلا ما كان من نكاحٍ','1','325',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (155,'أو رِق.
حدثنا محمد بن المصفى، قال: حدثنا المعافى بن عمران، عن إسماعيل، عن المثنى بن الصباح، عن عمرو بن شعيب، عن أبيه، عن جده، عن رسول الله - صلى الله عليه وسلم - قال: ((أيما رجل اعترف بولد حرة أو أمة لا يملكها، فهو ولد/٣٠/ زنا لا يرث، ولا يورث)).
وسألت إسحاق: رجل فجر بامرأةٍ، فجاءت بولد، وهو يعلم أنه منه من الفجور، فادعاه.
قال أبو يعقوب: إذا استيقن أن الولد ولده لما حصنها، فإن','1','326',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (156,'الناسَ اختلفوا فيه، والأكثرون على أنه لا يقبله.
قال: والذي اختار إذا كان حصنها حتى استيقن أنه منه، فقبوله أحب إليَّ.
وسألتُ إسْحاقَ مرةً أخرى، قلتُ: رجل زنا بامرأةٍ فحبلت من الزنا، ثم إن الرجل تزوج المرأة قبل أن تضع، ودخل بها وجامعها قبل أن تلد، ثم أنها ولدت غلامًا فقبله، ما حال هذا الغلام، هل يرثه؟ وهل يقبله؟ وهل هو محرم لبنات هذا الرجل؟
قال: أحب إلي أن يقبله من زنا أو من تزويج، ويشارِك سائر الورثة في الميراث.
وسئل إسحاق مرةً أُخرى عن رجل زنا بامرأةٍ، فحملت منه، ثم أراد أن يتزوجها، وهي حامل من الفجور؟
قال: إذا تابا تزوجها.
قلتُ: وهي حامل من الفجور!
قال: نعم. ورخَّصَ فيه إذا كان هو فجر بها، ثم تابا وندما.
وسألتُ إسْحاقَ مرةً أُخرى، قلتُ: رجلٌ كانت له امرأة، فطلقها ثلاثًا، فلما حاضت ثلاث حيض، وانقضت مدتها أصاب منها حرامًا، فظن بها حبلٌ بعد ذلك، ولا يدري كان الحبل قبل ذلك، أو مما أصاب منها من الحرام، كيف الحكم في ذلك؟
قال: يقبل الولد؛ لأن الأنساب ثبتت بالشبهات، وهذا لا يدري في النكاح حملت، أو في الحرام.
حدثنا عبيد الله بن معاذ، قال حدثنا أبي، قال حدثنا أشعث، قال: سألني رجل عن رجل زنا بامرأةٍ، فحملت، أيتزوجها، ويسترها ويتقبل ولدها؟
فسألت الحسن، فقال: نعم فليفعل.','1','327',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (157,'<span data-type="title" id=toc-60>٦٠ - باب: من تزوَّج الأمةَ على الحرة</span>.
سُئِلَ أحمدَ عَنْ الرجل يتزوَّج الأمة على الحرة؟
قال: يُفرق بينه وبين الأَمَة.
قيل: فإن تزوَّج حرة على أَمَة؟
قال: لا يُفرق بينه وبين الأَمَة، ويقسم لهما ليلتين للحرة وليلة للأمة.','1','328',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (158,'حدثنا الحميدي، قال: ثنا سفيان، قال: سمعتُ يحيى بن سعيد يحدث عن سعيد بن المسيب قال: تنكح الحرة على الأمة ولا تنكح الأمة على الحرة، فإن الحرة رضيت فلها الثلثان وللأمة الثلث.','1','329',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (159,'<span data-type="title" id=toc-61>٦١ - باب: من تزوج امرأةَ رجل، وابنته من غيرها</span>.
قلتُ لأحمدَ: الرجل يتزوج امرأة الرجل وابنته من غيرها؟
قال: لا بأس.
وسألتُ إسْحاقَ عن ذلك، فقال: لا بأس به.
حدثنا عبد الله/٣١/ بن عبد الوهاب قال: ثنا إسماعيل، قال: ثنا أيوب، قال: سئل محمد بن سيرين عن الرجل يزوج امرأة الرجل وابنته من غيرها، فلم ير به بأسًا،','1','330',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (160,'وسئل الحسن عن ذلك، فكرهه.','1','331',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (161,'<span data-type="title" id=toc-62>٦٢ - باب: من تزوج امرأة وشرط لها أن يطلق التي هي تحته</span>.
سألت أحمدَ، قلت له: رجل له امرأة، فتزوج أخرى على أن طلاق الأولى بيد هذه التي تزوج إلى شهر، أو إلى سنة، أو وقت معلوم، فجاء الوقت ولم يقض شيئًا؟
قال: رجع الأمر إليه له شرطه.
قلتُ: ويجوز مثل هذا الشرط في النكاح.
قال: نعم هو جائز.
حدثنا عبد الله بن عبد الوهاب، قال: حدثنا عطاف بن خلد المحرومى، قال: حدثنى أبي: أن رجلاً من قريش كان مؤاخيًا له، وكانت امرأته بنت عم له، فخطب امرأة أخرى، فقالت: ما كنت لأتزوجك على فلانة،','1','332',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (162,'قال: فقال: إني أكره أن يرى أهل بيتى أني طلقتها في نكاح امرأة، ولكن تزوجيني، ثم هي بعد شهرين طالق ألبتة، قال: فتزوجته وكان رجلاً عفيفًا، فكره أن يراها حاسرًا، فوجد في نفسه من ذلك.
قال عطاف: فقال لي: أن سعيد بن المسيب، فاسأله.
قال: إي فأتيتُ سعيد بن المسيب، فقصصت عليه قصة الرجل ففزع وجلس، فقال: فارق الأولى حين تزوج الآخرة.
سألت إسحاق، قلتُ: رجلٌ تَزَوَّجَ امرأةً وجعل مهرها طلاق امرأة عنده، فلم يطلقها؟
قال: النكاح جائز، ولها مهر مثلها، ولا نرى الطلاق مهرًا.
قلتُ: فإن جعل مهرها عتق عبد؟
قال: جائز.','1','333',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (163,'<span data-type="title" id=toc-63>٦٣ - باب: من تزوج أمة، فطلقها ثم اشتراها</span>.
سألت أحمدَ، قلتُ: رجل تزوج أمة، فطلقها ثلاثًا، ثم اشتراها، أيغشاها بملك اليمين؟
قال: لا تحل له حتى تنكح زوجًا غيره، وإن غشيها سيدها أيضًا لم تحل له؛ لأن السيد يغشاها بملك اليمين.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أبنا الأعمش، عن أبي الضحى، عن مسروق أنه قال: لا تحل له حتى تنكح زوجًا غيره، ولا تحل له إلا من الباب الذي حرمت عليه.','1','334',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (164,'حدثنا سعيد بن منصور، قال: ثنا حماد بن زيد، عن أبي عبد الله السعدي، عن عامر، عن مسروق في حر كانت تحته أمة، ثم فطلقها تطليقتين، ثم غشيها سيدها.
فقال: سمعت الله يقول: /٣٢/ ((حتى تنكح زوجًا غيره)) ليس هذا بزوج.
سألت أحمدَ مرةً أخرى، قلتُ: رجل عنده أمة، فطلقها تطليقين، ثم اشتراها، أيغشاها؟
فقال: نعم يغشاها على تطليقة؛ لأن الطلاق بالرجال.','1','335',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (165,'وسألت إسحاق، قلت رجل تحته أمة، فطلقها تطليقتين، ثم اشتراها، هل يطؤها بملك اليمين؟
قال: يطؤها شديدًا.
قلتُ: فإنه طلقها ثلاثًا، ثم اشتراها.
قال: يجوز أن يطأها.
حدثنا علي بن عثمان، قال: حدثنا حماد، عن قيس بن سعد أن عطاء قال في حر تحته أمة، فطلقها تطليقتين، ثم اشتراها.
قال: إن شاء نكحها بملك اليمين.','1','336',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (166,'<span data-type="title" id=toc-64>٦٤ - باب: من تزوج أمة، فولدت له، ثم اشتراها أيبيعها</span>؟
قلتُ لأحمدَ: رجل تزوج أمة، فولدت له، ثم اشتراها، أيبيعها؟
قال: ما أقل ما اختلف الناس في هذا أنه يبيعها إلا الحسن، فإنه قال لا يبيعها، ويروى عن عبيدة وشريح أنهما قالا: يبيعها. وكأن أبا عبد الله ذهب إلى بيعها.','1','337',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (167,'حدثنا علي بن عثمان، قال: حدثنا حماد، قال: أبنا الحجاج بن أرطأة، عن الفضيل بن عمرو، عن إبراهيم أن عبيدة السلماني قال في حر تحته أمة، فطلقها تطليقتين، ثم اشتراها؟
قال: لا تحل له حتى تنكح زوجًا غيره. وإن كان له منها ولد، ثم اشتراها؟
قال: إن شاء باعها.
حدثنا يحيى الحماني، قال: ثنا ابن مبارك، عن سفيان، عن حماد ويونس، عن الزُّهْرِيّ قالا: لا ليس له أن يبيعها.','1','338',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (168,'حدثنا علي بن عثمان، قال: ثنا حماد، عن حميد، عن الحسن في رجل كانت له تحته أمة، فولدت، ثم اشتراها؟
قال: لا يبيعها؛ لأنها أم ولده.','1','339',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (169,'<span data-type="title" id=toc-65>٦٥ - باب: رجل تحته أمة، فطلقها، فغشيها سيدها، أتحل له</span>؟
سألت أحمدَ، قلتُ: رجل تحته أمة، فطلقها، فوطئها سيدها، ثم أراد الزوج أن يتزوجها؟
قال: لا يحل له نكاح السيد إياها؛ لأن السيد إنما وطئ ملك يمينه، وليس بزوج، وقال الله: {فَلاَ تَحِلُّ لَهُ مِن بَعْدُ حَتَّىَ تَنكِحَ زَوْجًا غَيْرَهُ} [البقرة: ٢٣٠]، وكذلك الأحكام في السيد والزوج مختلفة، فلا يجوز هذا، وسمعت أحمد مرةً أخرى يقول: إن غشيها سيدها لم تحل له؛ لأن السيد يغشاها بملك اليمين.
حدثنا أبو معن الرقاشى، قال: حدثنا عبد الوهاب، قال: أخبرنا خالد الحذاء، عن مروان الأصفر، عن أبي رافع أن عليًا سُئل عن العبد يطلق /٣٣/ الأمة، فيقع','1','340',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (170,'عليها سيدها، يحل للعبد أن يتزوجها؟ فكره ذلك.
حدثنا سعيد بن منصور، قال: حدثنا هشيم، قال: أخبرنا مغيرة، عن إبراهيم والشعبى أنهما قالا: إذا طلقها، ثم غشيها سيدها، لا تحل له حتى تنكح زوجًا غيره.','1','341',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (171,'<span data-type="title" id=toc-66>٦٦ - باب: عبد تزوج حرة، وظنت أنه حر</span>؟
قلتُ لأحمدَ: امرأة تزوجها رجل، فظنت أنه حر، فمكثت معه، ثم ظهر عليه أنه عبد؟
قال: إذا علمت أنه عبد، فلها خمسا المهر، وإذا لم تعلم فلها المهر في رقبة العبد حتى تجاوز قيمة العبد، فإن شاء مولاه فداه، قال: وإن كان المولى أذن له في التزويج، فالمهر على المولى، وإلا فهو في رقبته.
حدثنا المسيب بن واضح، قال: ثنا ابن مبارك، عن يونس، عن الزُّهْرِيّ في عبد انطلق إلى حي من المسلمين، فحدثهم أنه حر، فزوجوه حرة، ثم عُلِم؟
قال: السنة أن يفرق بينهما، ولها في ثمن العبد مثل صداق بعض نسائها، فإن شاء أهل العبد فدوه فذلك، وإن شاءوا أسلموه إليها، وتعتد عدة المسلمة، ويجلد العبد ما دون الحد نكالاً.','1','342',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (172,'<span data-type="title" id=toc-67>٦٧ - باب: رجلٌ تَزَوَّجَ امرأةً، وظن أنها حرة</span>.
قلتُ لأحمدَ: فرجل تزوج امرأة، وظن أنها حرة، فأصاب منها أولادًا، فإذا هي أمة.
قال: يفرق بينهما، وأولاده أحرار، ولكن يفديهم، وإن كان غَرَّه إنسان فعلى الذي غرَّه أن يفدي ولده، وسألتُ إسحاق، قلتُ: رجلٌ تَزَوَّجَ امرأةً وظن أنها حرة، فأصاب منها أولادًا، فلما أتى لذلك سنتين، أقام رجل شاهدين أنها أمته، فأخذها؟
قال: الولد','1','343',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (173,'أحرار، وعليه فداء ولده، ويرجع به على الذي غره وعليه المهر بما استحل من فرجها.
قلتُ: وليس على المرأة شيء بإقرارها أنها حرة؟
قال: لا.
حدثنا علي بن عثمان، قال: حدثنا حماد، عن قتادة، عن خلاس أن أمة أتت طيئًا، فزعمت أنها حرة، فتزوجها رجل منهم، فولدت أولادًا، ثم إن مواليها قدروا عليها بعد، فخاصموه إلى عثمان فقضى بها لمولاها، وجعل في أولادها الملة (١)، وقال: ما عرفت من متاعك فخذه، قال خلاس: جاريتان بجارية، وغلام بغلام، وقال الحسن: جارية بجارية، وغلام بغلام.
_________
(١) في هامش الأصل: الملة الدية بمالها.','1','344',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (174,'<span data-type="title" id=toc-68>٦٨ - باب: نفقة المرأة</span>
سألت أحمدَ، قلتُ: على الرجل نفقة امرأته قبل أن يدخل بها.
قال: إذا جاء /٣٤/الحبس من قبله، وإن كان الحبس من قبلهم، فليس عليه نفقة.
وسألت إسحاق، قلتُ: رجل تزوج صبية صغيرة، هل لها نفقة؟
قال: كلما لم يستطاع (١) أن يُبْنَى بمثلها، فليس لها نفقة. فراجعته فيها، فقال: ليس لها نفقة.
_________
(١) كذا في الأصل والصواب لم يستطع.','1','345',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (175,'حدثنا محمد بن نصر: قال: ثنا حسان، عن سفيان في رجل تزوج صغيرة أو مريضة، قال: عليه النفقة.
حدثنا علي بن عثمان، قال: حدثنا حماد، عن يونس، عن الحسن، قال: إذا تزوج الرجل الجارية التي لم تدرك، فلا نفقة لها حتى تدرك، وإن تزوج امرأة، فتركها سنة أو سنتين لا يطلبون النفقة لا نفقة لها حتى تقول ابن بي أو أنفق عليَّ، فيقال له ابن بها، أو أنفق عليها، فإن أبى أن يبني بها آخذه بالنفقة.','1','346',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (176,'<span data-type="title" id=toc-69>٦٩ - باب: قدر نفقة المرأة</span>.
سألت أحمدَ، قلتُ: المرأة كيف يفرض لها النفق؟
قال: على قدر ما يحتمل الرجل.
وسألت إسحاق، قلتُ: كم تقدر من النفقة؟
قال: ما يحتمل المال.
حدثنا عبيد الله بن معاذ، قال عن أبيه، عن إسرائيل، قال حدثني المغيرة: قال: سئل إبراهيم، عن قوت المرأة؟
قال: نصف صاع مد لطعامها ومد لإدامها.','1','347',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (177,'حدثنا محمد بن آدم، قال: حدثنا معمر بن سليمان، عن حجاج أن الحسن البصري قال: يُحدِّث أن رسول الله - صلى الله عليه وسلم - فرض لامرأةٍ على زوجها مملولًا من طعام وجَرَّة من ماء.
قال الحجاج: والملول ربع صاع.
حدثنا المسيب، قال: ثنا ابن مبارك، عن ابن لهيعة، عن محمد بن عبد الرحمن بن نوفل، قال: حدثتني فاطمة بنت أبي دواد، وكانت يتيمة عند حكيم بن حزام، قالت: كان حكيم يكسو أهله وبيته في السنة مرة لا يزيدنا على ذلك وإن عرينا.','1','348',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (178,'حدثنا المسيب، قال: ثنا ابن مبارك، عن عنبسة بن سفيان، عن أبي إسحاق، عن مالك بن المنبه أنه زوَّج ابنه ابنت أخ له يتيمة في حجره لم ينفق عليها، فأتت به شريحًا، فقالت: إن هذا زوجني ابنه، وأنا يتيمة في حجره، وهو موسر وليس ينفق علي.
فقال شريح: ما يمنعك أن تنفق عليهما، وأنت موسر؟
ففرض عليه خمسة عشر درهماً كل شهر.','1','349',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (179,'<span data-type="title" id=toc-70>٧٠ - باب: المهر عاجل وآجل</span>.
سألت أحمدَ، قلتُ: الرجل يزوج المرأة على عاجل من المهر، وآجل، يقول لها أعطيك خمس مائة الآن وخمس مائة إلى سنة؟
قال: أرجو أن يجوز هذا، ولكن إن طلق، أو كانت بينهما فرقة، فقد وجب عليه قبل الأجل.
حدثنا /٣٥/محمود، قال أبنا عمر، قال: سمعت الأوزاعى يقول في رجل تزوج امرأة على عاجل وآجل من المهر، ثم دخل بها؟
قال: هدم الدخول العاجل.
قال: وحدثنا عمر، عن الأوزاعي، قال: سمعت مكحولاً.
يقول: يحل الأجل إلى سنة، وقد استكثر ذلك ناس من أهل العلم،','1','350',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (180,'هل وكيف إن كان أجل إلى ميسورة؟
قال: إن علم أنه استفاد مالاً بعد دخوله أخذ به.
وعن رجل تزوج على صداق مسمى منه عاجل وآجل، فكتب في كتاب صداقها، ثم ادعت المرأة بعد حين أنه لم يدفع إليها ما شرط لها من عاجل صداقها؟
قال: إذا دخل بها، فقد نرى من العاجل، وتُسأل البينة على الآجل.
حدثنا علي بن عثمان: قال حدثنا حماد بن سلمة، قال: قال إياس بن معاوية، وقتادة: لا يؤخذ بالآجل حتى يطلقها أو يخرجها من مصرها، أو يتزوج عليها فإذا فعل ذلك أخذته بالآجل أنى شاءت، وقال حماد بن أبي سليمان العاجل والآجل حال.','1','351',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (181,'حدثنا المسيب، قال: ثنا ابن مبارك، عن هشام، عن الحسن، قال: إذا دخل بالمرأة فقد حل الآجل.','1','352',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (182,'<span data-type="title" id=toc-71>٧١ - باب: كم يتزوج المسلم من أهل الكتاب</span>؟
قلتُ لأحمدَ: المسلم يتزوج امرأتين من أهل الكتاب؟
قال: لا بأس بذلك، قد رُوي عن سعيد بن المسيب أنه قال: لا بأس أن يتزوج الرجل أربع نسوة من أهل الكتاب.
حدثنا عبد الله بن معاذ، قال: ثنا أبي، قال: ثنا أشعث، عن الحسن أنه كان لا يرى بأسًا أن يتزوج الرجل أربع نسوة من النصارى أو اليهود.','1','353',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (183,'<span data-type="title" id=toc-72>٧٢ - باب: كم يتزوج الحر من الإماء</span>؟
سمعتُ أحمدَ يقولُ: حدثنا غندر، عن شعبة، عن أبي هاشم الرِّمانى، عن الحراث، قال: يتزوج الحر من الإماء أربعًا.
قال شعبة: وسألت حماد، قال: يتزوج اثنين، قال أحمد: ما أعجب رأي حماد لا أدرى قياس هذا.
حدثنا أحمد، قال: حدثنا وكيع، عن شريك، عن عطاء بن السائب، عن سعيد بن','1','354',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (184,'جبير، عن ابن عباس قال: لا يتزوج الحر من الإماء إلا واحدة.
قال أحمد: ولا يعجبني أن يتزوج إلا واحدة. يذهب إلى حديث ابن عباس.','1','355',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (185,'<span data-type="title" id=toc-73>٧٣ - باب: العبد تحته حرة فبيع</span>.
قلتُ لأحمدَ: المملوك إذا كانت تحته الحرة، فباعه مواليه، فعلى من مهر امرأته؟
قال: على سيده الذي باعه إذا كان أذن له في التزويج.
قلتُ/٣٦/: فإن أعتقه؟
قال: كذلك أيضًا؛ لأنه شئ كان على السيد.
حدثنا أبو هشام، قال: حدثنا حسان في عبد تزوج حرة بإذن مواليه، فبيع العبد، قال سفيان: صداقها على مولاه في ثمنه.','1','356',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (186,'<span data-type="title" id=toc-74>٧٤ - باب: من قال إذا كان وقت كذا، فقد أنكحتك فلانة</span>.
سمعتُ أحمدَ يقولُ: حدثنا عباد بن عوام، عن عمر بن عامر أن رجلاً سأل الشعبي عن رجل خطب إلى رجل ابنته، فقال: إذا مضى شوال، فقد أنكحتُك.
قال: ليس هذا نكاحًا.
حدثنا يحيى الجماني: قال حدثنا عباد بن العوام، عن سفيان، عن حسين، عن الشَّعبيِّ والحسن: إذا قال إذا جاء رمضان فقد أنكحتك فلانة.
قال الحسن: فهذا نكاح.
وقال الشعبي: ليس هذا بنكاح.','1','357',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (187,'حدثنا محمود قراءةً عليه، عن عمر بن عبد الواحد، عن الأوزاعي، عن رجل قال لرجل: إذا كان رأس الهلال فقد زوجتك ابنتي.
قال: أرى نكاحه حين لفظ به جائز؛ لأنه ليس لغيره فيها مطمع فتخطب.
قلت للأوزاعي: فإنه قد مات أحدهما، هل يتوارثان؟
قال: نعم.','1','358',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (188,'<span data-type="title" id=toc-75>٧٥ - باب من تزوج امرأة، وفي نفسه طلاقها</span>.
سُئِلَ أحمدَ عَنْ الرجل يتزوج المرأة في نفسه طلاقها، فكرهه.
حدثنا محمود، قال ثنا عمر، قال: سألت الأوزاعي، عن رجلٍ تَزَوَّجَ امرأة، ومن نيته أن يطلقها، وليس ثمة شرط؟
قال: لا خير في هذا، هذه متعة.','1','359',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (189,'حدثنا عمرو بن عثمان، قال: أخبرنا بقية بن الوليد: قال سمعت الزبيدي محمد بن الوليد يقول، وقلت له: إن أناسًا يقولون لا يصلح له أن ينكح امرأة على أن أضمر على طلاقها.
قال: لا أقول أنا هذا، قال بقية وسمعت الأوزاعي يقول: كل نكاح يكون فيه الرجل على أن يطلق، فلا يصلح.','1','360',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (190,'<span data-type="title" id=toc-76>٧٦ - باب: ضرب الدف</span>.
وسمعت أحمد يستحب ضرب الدف والصوت في الملاك.
قيل: الصوت، ما هو؟
فقال: الصوت أن يحدث به ويتكلم به ويظهر وينسم، وقال: لا أقول كما يقول قوم، قال: وأهل المدينة يسهلون فيه يعني الغناء.
حدثنا محمد بن الوزير، قال حدثنا مروان، قال: أخبرنا ابن لهيعة، قال حدثنا يزيد بن أبي حبيب أن عمر بن عبد العزيز كتب إلى أيوب بن','1','361',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (191,'شرحبيل أن مر من قِبلك أن يظهروا الدفاف على النكاح، وأنهاهم عن البرابط.
حدثنا يحيى بن عبد الحميد/٣٧/، قال: حدثنا شريك، عن أبي إسحاق، عن عامر بن سعد، قال: دخلت على قرظة بن كعب وعلى أبي مسعود الأنصارى،','1','362',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (192,'وثابت بن زيد، فقالوا إن رسول الله - صلى الله عليه وسلم - رخص لنا في اللهو عند العرس.
حدثنا علي بن عثمان، قال: حدثنا حماد، قال: ثنا أبو الحسن خلد بن ذكوان، قال: دخلنا على ربيع بنت معوذ بن عفراء، فقالت: دخل عليّ رسول الله - صلى الله عليه وسلم - في يوم عرسي، فقعد في موضع فراشي هذا، وعندنا جاريتان تضربان بالدف ويندبان أي: آبائي الذين قتلوا يوم بدر، وقالت في ما تقولان وفينا نبي يعلم ما يكون في عدٍ، فقال رسول الله - صلى الله عليه وسلم - أما هذا فلا تقولاه.','1','363',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (193,'حدثنا زيد بن يزيد، قال: حدثنا عبد الله بن بكر السَّهمى، قال: ثنا أبو محمد مولى لبني هاشم، عن ثور بن يزيد، عن خالد بن معدان، عن معاذ بن جبل، قال: شهد رسول الله - صلى الله عليه وسلم - ملاك رجل من الأنصار فزوجه، ثم قال على يد الخير والألفة والطائر الميمون والسعة في الرزق بارك الله لكم، ثم قال: دففوا على رأسه بالدف، فجئ بالدف فضُربَ على رأسه، وأقبلت الأطباق عليها فاكهة وسكر فنثر على رأسه، فكف الناس أيديهم، فقال: ما أزين الحلم، ما لكم لا تنتهبون؟
قالوا: أولم تنهنا عن النهبة: قال:','1','364',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (194,'إنما نهيتكم عن نهيبة العساكر فأما العرسان (١) فلا. قال: فجاذبهم رسول الله - صلى الله عليه وسلم - وجاذبوه.
_________
(١) في الأصل العرساب وهي خطأ.','1','365',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (195,'<span data-type="title" id=toc-77>٧٧ - باب: مناكحة الفساق وأهل الأهواء</span>.
وسُئِلَ أحمدَ عَنْ مناكحة الفساق والذي يشرب المسكر وأصحاب الأهواء، فكره ذلك شديدًا وسمعته يقول: إذا كان يتكلم بهذا الكلام الذي هو كفر فإنه لا ينكح.
وسألتُ إسْحاقَ قلتُ: يا أبا يعقوب ما تقول في الرجل يزوج ابنته وأخته ممن يشرب الخمر؟
قال: لا هذا فاسق، فإذا زوج كريمته من فاسق، فقد قطع رحمه.
قلتُ: فإن كان يشرب المسكر؟
قال: إذا كانت فيه خصلة صالحة، فلا بأس أن يزوجه وكان متأولاً.
حدثنا أحمد بن نصر، قال: ثنا رحبان بن موسى، قال: قال عبد الله بن المبارك: شر الأزواج إذا زوجت من شاربٍ لما يتخوف من طلاقه في','1','366',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (196,'سكره.
قال: وذكر عبد الله أن سعيد بن المسيب خطب إليه بعض آل مروان وأبى أن يزوج ابنته، وقال: أزوجها ممن يطغيها ويبغيها!
حدثنا عمرو بن العباس/٣٨/الأهوازى قال: أخبرني عبد الله بن أبي الأسود، قال: سمعت عبد الرحمن بن مهدى يقول: لو خطب إليّ رجل من الجهمية أَمَةً لم أزوجه.
حدثنا أحمد بن سعيد الدارمي، قال: سمعت أبي يقول: سمعت خارجة بن مصعب [يقول] (١): الجهمية كفار لا تنكحوا إليهم ولا تنكحوهم.
_________
(١) بياض بالأصل بمقدار كلمة ولعلها: يقول.','1','367',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (197,'قلت لأبي بكر محمد بن بشار: أزوِّج القدرية وأتزوج إليهم؟
قال: معاذ الله.
حدثنا علي بن عثمان، قال: حدثنا عون بن موسى، قال: كان معاوية بن قرة ينهانا أن نمشى مع الخاطب إذا لم يكن مرضيًا.
وسألتُ إسْحاقَ أيضًا: قلتُ: الرجل يشرب الخمر وتحية امرأة حرة، والمرأة لا تستطيع أن تمنع زوجها من شرب الخمر، هل لهذه المرأة أن تقيم مع هذا الرجل؟
قال أبو يعقوب: كلما امتنعت منه لهذه العلة، فإنه يسعها.','1','368',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (198,'<span data-type="title" id=toc-78>باب: من تزوج امرأة، ولم يدخل بها، فجاءت بولد</span>.
وقال أحمد في رجل تزوج امرأة، فلم يدخل بها فأولدت ولدًا أنه لا يلزمه.
وسألت إسحاق: قلتُ: رجلٌ تَزَوَّجَ امرأةً بكرًا، ولم يدخل بها، فجاءت بولد فانتفى منه؟
قال: يلاعن.
قلتُ: وإن لم يدخل بها؟
قال: نعم، وقال: بين الزوجين إنما هو اللعان.
وسألت أبا ثور: قلتُ: رجلٌ تَزَوَّجَ امرأةً بالبصرة وهو هاهنا ببغداد، ولم يرها ولم يدخل بها، فجاءت بولدٍ؟
قال: إذا كانت المسافة قدر ما يمكنه الذهاب إليها، ويأتيها ألزمناه الولد.','1','369',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (199,'حدثنا أبو الربيع الزهراني، قال: حدثنا عباد بن العوام، قال: أخبرنا الشيباني، عن الحكم وحماد، عن إبراهيم في رجل تزوج امرأة فلم يدخل بها، فادعت حملاً، وأنكر ذلك فطلقها، قال: تيجلد ويلزم الولد ويغرم الصداق. قال: وقال الشعبي يلاعن ولها نصف الصداق.
حدثنا عبيد الله بن معاذ، قال: ثنا أبي، قال: حدثنا أشعث، عن الحسن فيمن قذف امرأته قبل أن يدخل بها، قال: يلاعن فإن كان فرض لها فلها النصف ولا عدة عليها، وإن لم يكن فرض لها فلا شئ لها إلا المتاع، فإن كان دخل بها فلها المهر كاملاً، وإن كان دخل بها ولم يعرض لها، فلها صداق إحدى نسائها، فإن كانت حاملاً فولدت لوقت حبل /٣٩/منه فتبرأ من الولد لا عنها وإن ولدت في أقل من الوقت أقيم عليها الحد ويرئ من الولد واللعان، وأقل الوقت عنده ستة أشهر.','1','370',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (200,'<span data-type="title" id=toc-79>٧٨ - باب: من تزوج أخت أخته من الرضاع</span>.
سمعت إسحاق يقول: لا بأس أن يتزوج أخت أخته من الرضاعة.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: حدثنا أشعث، عن الحسن أنه كان يكره أن يتزوج الرجل بنت ظئر ابنه، ويقول أخت ابنه ولا يرى بأسًا أن يتزوج أمها.
حدثنا أبو صالح السرى بن محمد المصيصى، قال: ثنا الهيثم بن جميل، عن زهير، عن جابر، قال: سئل عامر: أيتزوج الرجل أخت ابنته من الرضاعة؟
قال:','1','371',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (201,'هي أحل من ماء فرس.
حدثنا المسيب بن واضح: قال: ثنا بن مبارك، عن حيوة بن شريح، قال: أخبرني عياش بن عباس أن عمار بن سعد التجيبي حدثه، عن أبي سلمة بن عبد الرحمن، قال: ابن امرأتك من الرضاعة، أو سريتك فامرأته لك حلال تنكحها بعد أن يطلقها زوجها.
حدثنا أبو معن، قال حدثنا معاذ بن هشام، قال: حدثني أبي، عن قتادة، قال:','1','372',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (202,'بنت الربيبة وبنت ابنتها، وإن كانت أسفل ببطون كبيرة، فإنها بمنزلة الربيبة.
قال معاذ: وحدثني أبي، عن قتادة، عن الحسن، قال: وإن كانت أسفل بعشرين بطنًا، فإنها لا تصلح.','1','373',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (203,'كتاب الطلاق','1','374',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (204,'<span data-type="title" id=toc-80>١ - باب: الطلاق قبل النكاح</span>.
سمعت أحمد بن حنبل يقول في الطلاق قبل النكاح إذا وقت، قال: إن تزوجها لم يفرق بينهما، وإن لم يتزوجها، فليتزوج غيرها، فهو أحب إليه.','1','375',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (205,'قلتُ: هل صح فيه عن النبي - صلى الله عليه وسلم - شئ؟
قال: حديث عمرو بن شعيب، عن أبيه، عن جده.
وسمعتُ أحمدَ أيضًا يقولُ في الطلاق قبل النكاح وقت أولم يوقت فهو واحد، ومذهبه إذا تزوج أن لا يفارقها، وإن لم يتزوج، فإن يتزوج غيرها هو أحب إليه، وإن خاف على نفسه فتزوجها فلا بأس.
وسمعتُ إسحاق بن إبراهيم يقول في الطلاق قبل النكاح، قال: لا يتزوجها فإن تزوجها لم آمره بفراقها ولا إمساكها.
وأبو يعقوب يجبن عن المنصوبة بعينها.
وسمعت إسحاق مرة أخرى سئل عن رجل قال: يوم أتزوج فلانة فهي طالق؟
قال: لا أرى أن يتزوجها.
قيل: فإن فعل.
قال: لا أفتي فيه.
وسمعت إسحاق مرة أخرى يقول: إن تزوجها فلا يفارقها.
حدثنا المسيب بن واضح: قال: سمعت أبا إسحاق/٤٠/ الفزاري سئل عن الطلاق قبل النكاح، فقال: لا أقول فيها شيئًا.
حدثنا محمد بن الوزير، قال حدثنا مروان بن محمد، قال: كأن الأوزاعي','1','376',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (206,'سئل عن هذا، فقال: إن كان الرجل لم يتزوج المرأة أن لا يتزوج، وإن كان قد تزوج لم يفرق بينه وبين امرأته. قال مروان: هذا كلام فقيه.
حدثنا محمد بن المنهال، قال: ثنا أبو بكر الحنفي، قال: ثنا ابن أبي ذئب، قال: حدثنا عطاء، عن جابر بن عبد الله، قال: قال رسول الله - صلى الله عليه وسلم - ((لا طلاق إلا بعد ملك ولا عتق إلا بعد ملك)).
حدثنا أبو بكر محمد بن عبد الرحمن الجعفي، قال: ثن سعيد بن شرحبيل الكندي، عن ابن لهيعة، عن محمد بن المنكدر، عن عمرو بن شعيب، عن طاوس، عن ابن','1','377',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (207,'عباس أن رسول الله - صلى الله عليه وسلم - قال: ((لا طلاق ولا عتق لمن لا يملك)).
سمعت إسحاق مرة أخرى سئل عن رجل قال لامرأته إن تزوجتك فأنت طالق ثلاثًا؟
قال: إذا نصبها فلا يتزوجها.
قيل: إن تزوجها؟
قال: اختلف الناس فيها، قال قوم يطلق، وقال قوم لا يطلق، وذكر عن علي بن أبي طالب وعبد الله ابن عباس إذا سماها لا','1','378',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (208,'تطلق، وذكر عن ابن مسعود إذا سماها تطلق.
حدثنا إسحاق: قال حدثنا عبد الله بن إدريس، عن ليث بن أبي سليم، عن عبد الملك بن ميسرة، عن النزال بن سبره، عن علي بن أبي طالب، قال: لا طلاق قبل نكاح.
حدثنا إسحاق: قال أخبرنا جرير، عن عاصم الأحوال، عن عكرمة، عن ابن عباس، قال: لا طلاق إلا بعد نكاح ولا عتق إلا بعد ملك.','1','379',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (209,'حدثنا إسحاق: قال أخبرنا يعلي بن عبيد، قال حدثنا محمد بن عون الخراساني، عن محمد بن زيد، عن سعيد بن جبير، قل: سئل ابن عباس عن رجل قال لئن تزوجت فلانة، فهي طالق، قال: ليس بشيء إنما الطلاق لمن يملك، قالوا: فإن ابن مسعود يقول: إذا وقت وقتًا فهو كما قال، قال: رحم الله أبا عبد الرحمن لو كان كما قال لقال الله يا أيها الذين آمنو إذا طلقتم النساء المؤمنات ثم نكحتموهن، وإنما قال الله: {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ طَلَّقْتُمُوهُنَّ} [الأحزاب: ٤٩].
وسمعت أيضًا إسحاق يقول: وسئل عن رجل قال كل امرأة يتزوجها إلى ثلاثين سنة، فهي طالق ثلاثًا وثلاثين مرة، قال: إذا لم ينصبها بعينها رجوت.','1','380',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (210,'وسئل إسحاق مرة أخرى يقول: وسئل عن رجل قال لامرأة إن تزوجها إلى ثلاثين سنة فهي طالق.
قال: إذا نصبها لم يتزوج.
قلتُ: لم ينصب، ولكن وقت وقتًا؟
قال: لا بأس ورخص فيه (١)، /٤١/ وسئل إسحاق أيضًا عن رجل قال: لئن دخلت هذه الدار، فكل امرأة يتزوجها إلى ثلاثين سنة فهي طالق، فدخل الدار؟
قال: يتزوجها إذا لم ينصبها.
حدثنا محمد بن الوزير قال: ثنا مروان بن محمد، قال سمعت مالك بن أنس وليث بن سعد يُسألان عن الرجل قال: كل امرأة أتزوجها فهي طالق؟
قال: إن سمَّى امرأةً بعينها أو قبيلة أو بلدة، فذلك له لازم، وأما سوى ذلك فلا بأس به.
حدثنا سهل بن بكار: قال حدثنا أبو عوانة، عن محمد بن قيس أن رجلاً قال لجارية صغيرة إن تزوجها فهي طالق فشبت، فرغب فيها فتزوجها فوقع في نفسه، فلقيت إبراهيم فسألته فذكر عن علقمة
_________
(١) بهامش الأصل: عورضت كلها بالأصل ... ولله الحمد.','1','381',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (211,'والأسود، عن عبد الله، قال: هي كما قال.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: ثنا سعيد، عن عاصم الأحول، عن الشَّعبيِّ في من قال إن تزوجتُ امرأةً مَنْ بنى فلان، أو أرض كذا، أو وَقَّت، فهي طالق، فهو كما قال، فإن قال: كل امرأة أتزوجها فهي طالق، فليس بشئ.
حدثنا إبراهيم بن محمد الشافعى، قال: ثنا ابن أبي حازم، عن يحيى بن سعيد، قال: سمعتُ سالم بن عبد الله يكره أن يرخص لأحد أن يحلف بطلاق','1','382',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (212,'امرأته قبل أن يتزوجها ثم يريد أن يتزوجها، كان يرى أن الطلاق قد وقع عليه، وفي من حلف بعتاق ما لا يملك أنه كان يرى إذا ملكه، فقد وقع عليه الحنث.
قرأتُ على إسحاق: غلام ابن عشر سنين أو نحو ذلك، فقيل له نزوجك فلانة، فقال: إن تزوجها، فهي طالق ثلاثًا، فقد أدرك وبد له أن يتزوجها. قال أبو يعقوب: لا بأس أن يتزوجها، وقرأت على إسحاق مرةً أخرى: رجل حلف بالطلاق، فقال: إن لم أقضك يا فلان هذه الدراهم التي لك علي إلى شهر، فكل امرأة يتزوجها إلى ثلاثين سنة، فهي طالق ثلاثًا، قال أبو يعقوب: لا طلاق قبل نكاح، وقَتَّ أو لم يُوقت بعد أن لا ينصبها بعينها، فإنه إذا نصبها لا أفتي فيها؛ لما اختلف علي وابن مسعود في ذلك، قال ابن مسعود: إنها تطلق. ورأى علي أنها لا تطلق، وهما إمامان وهو أشبه بالحق فإن تقدم عليها لم أعبه.
حدثنا إسحاق: قال أخبرنا معمر بن سليمان، عن مبارك بن فضالة، عن الحسن، عن علي بن أبي طالب، قال: إذا قال إن تزوجت فلانة، فهي','1','383',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (213,'طالق، فليس بشئ.
حدثنا إسحاق: قال أخبرنا حفص بن غياث، قال حدثنا سليمان بن المغيرة: قال: سألتُ علي بن الحسين عن رجل قال إن تزوجت فلانة، فهي طالق؟
قال: ليس بشئ، قال الله: {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ/٤٢/طَلَّقْتُمُوهُنَّ}،','1','384',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (214,'سألت علي بن عبد الله عن الطلاق قبل النكاح؟
قال: نحن نذهب فيه إلى الأثر ((لا طلاق إلا بعد نكاح)).
قلتُ: وَقَّتَ، أولم يُوقِّت، سمَّى أو لم يُسَّم؟
قال: نعم.
حدثنا عبد الرحمن بن نحر: قال حدثنا رشدين بن سعد، قال: حدثنى عياض الفهري، عن عبد الرحمن ين الحارث المحرومى، عن طاووس، عن معاذ بن جبل أن رسول الله - صلى الله عليه وسلم - كتب له حين وجهه إلى اليمن: أن لا طلاق لا عتاق إلا بعد ملك.','1','385',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (215,'حدثنا عبيد الله بن معاذ، عن أبيه، عن إسرائيل قال حدثنى عبد الكريم الجزري، قال: سمعت سعيد ابن المسيب وسعيد بن جبير وعطاء بن أبي رباح يقولون: في رجل قال: يوم أتزوج فلانة، فهي طالق، فتزوجها، قال: ليس بشئ.
قال عبيد الله: وكان هذا قول أبي وقضى به في ولايته الأولى سمَّى أولم يُسَّم.
قرأت على إسحاق: رجلٌ قال: إن كنت مسستُ حرامًا قط، فكل امرأة يتزوجها إلى ثلاثين سنة، فهي طالق ثلاثًا، وقد أتى الحرام قبل أن يزوَّج، وبعد ما تَزوَّج؟
قال: لا بأس أن يتزوج.
وسمعتُ إسحاق أيضًا وسأله رجلٌ، فقال: رجلٌ قال لامرأته متى طلقتك فكل امرأة أتزوجها إلى ثلاثين سنة، فهي طالق وكل ما له في المساكين وما أستقيد إلى ثلاثين سنة، وعليّ حجة، وقد طلق هذه المرأة التي حلف بطلاقها وتزوج أخرى؟
قال أبو يعقوب: هذه المرأة التي تزوج بعد طلاق امرأته جائز وأما ما حلف بالمال والمساكين والحج، فيكفر بيمينه.','1','386',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (216,'وسمعت إسحاق أيضًا وسأله رجل بالفارسية، فقال: رجل حلف لامرأته أن كل امرأة يتزوجها عليها، فهي طالق، أيتزوج؟
فقال بالفارسية: ينحت - يعنى: يتزوج.
حدثنا إسحاق، قال: أخبرنا وكيع، عن ابن أبي ذئب، عن عطاء، عن جابر بن عبد الله، عن النبي - صلى الله عليه وسلم - قال: ((لا طلاق قَبْلَ نكاح)).
حدثنا إسحاق، قال: ثنا عيسى بن يونس، قال: حدثنا الوليد بن كثير المخزومي، عن عمرو بن شعيب، عن أبيه، عن جده، عن النبي - صلى الله عليه وسلم - قال: ((لا','1','387',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (217,'طلاق قبل ملك)).
حدثنا أبو معن الرقاشي: قال: حدثنا أبو عامر: قال: حدثنا عبد العزيز بن المطلب بن عبد الله بن حنطب، عن عثمان بن عروة، عن يزيد بن قسيط: أنه سأل عروة بن الزبير وسعيد بن المسيب وأبا سلمة بن عبد الرحمن، وعبيد الله بن عبد الله بن عتبة، وأبا بكر بن عبد الرحمن بن الحارث /٤٣/ بن','1','388',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (218,'هشام، عن رجل حلف بطلاق امرأة إن نكَحَها، ثُمَّ نكحها؟
قالوا: لا بأس به.','1','389',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (219,'<span data-type="title" id=toc-81>٢ - باب: الظهار قبل النكاح</span>
قيل لأحمد: الظهار قبل النكاح؟
قال: نلزمه؛ لأنه يمين، وليس بمنزلة الطلاق، نذهب إلى حديث عمر بن الخطاب حدثنا الحميدي، قال أبنا سفيان، قال: حدثنا ابن عجلان: أنه سمع عكرمة','1','390',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (220,'يقول: قال ابن عباس: ((ليس الظهار قبل النكاح بشيء)).
حدثنا عبيد الله بن معاذ: قال: حدثنا أبي، قال: ثنا سعيد، عن قتادة، عن سعيد بن المسيب، قال: لا ظهار إلا بعد ملك.','1','391',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (221,'<span data-type="title" id=toc-82>٣ - باب: المجوسي تزوج أَمَّه، هل لها المهر</span>؟
سألتُ إسْحاقَ عن مجوسي تزوج أمه، ثم أسلم، هل لها المهر؟
قال: لا إذا رفع إلى حاكم من كحام المسلمين لم يحكم لها بالمهر؛ لأن النكاح كان حرامًا في أحكام المسلمين.
حدثنا يحيى بن عبد الحميد، قال: ثنا قيس، عن زكريا، عن الشعبي، قال: لا يكون لها من ابنها، ولا من أخيها المهر.
حدثنا يحي: قال: حدثنا شريك، عن مغيرة، عن إبراهيم، قال: إذا تزوج الرجل ذات محرم منه أمه، أو أخته، أو خالته فرق بينهما، فإن كان دخل بها، فلها المهر؛ بما استحل من فرجها، وإن لم يكن دخل بها، فلا مهر لها.','1','392',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (222,'<span data-type="title" id=toc-83>٤ - باب: إذا أنكر الدخول بامرأته</span>.
سألت إسحاق، قلتُ: رجلٌ تَزَوَّجَ امرأةً، فقالت المرأة: إنه قد دخل بها، وأنكر الزوج ذلك، كيف الأمر في ذلك؟ وكيف نعرف ذلك؟
قال: بالبينة، واليمين إذا قامت البينة أنه قد أقر بالوطء، وإلا حلف أنه لم يطأها.
حدثنا المسيب بن واضح: قال: حدثنا ابن المبارك، عن إسماعيل، عن الشَّعبيِّ أن عمر بن نافع جاء يخاصم إلى شريح امرأة له زعمت أنه قد دخل بها، وقال لم أدخل بها، فأصبره شريح بمينًا بالله ما مسها، ثم قال: أعطها نصف الصداق.','1','393',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (223,'سألت إسحاق، قلتُ: مشرك تزوج أمّه، أو أخته، هل يفرق بينهما؟
قال: شديدًا.
قلتُ: يفرق بينهم، وهم مشركون؟
قال: نعم.
قلت: فرجل مشرك زوج بنته من أخيه، وهو عَمُّ الجارية، ثم جحد الأب ذلك، فارتفعوا إلينا، كيف نحكم بينهم، وهل يجبر الأب أن يسلم الجارية لأخيه، وهو محرم؟
قال: لا، ولكن يفرق بينهما.
حدثنا محمود بن خالد: قال حدثنا عمر، قال: سئل الأوزاعي عن اليهودي يتزوج ابنة أخيه، أو بنت أخته؟
قال: إن جاءت المرأة تنكر نكاح العم، أو الخال إياها/٤٤/ فرق بينهما، وإن لم تأت الإمام أعرض عنهم في نكاحهم، حدثنا سعيد بن منصور، قال: ثنا هشيم: قال: أخبرنا عوف، عن عباد المازني، عن نحالة بن عبدة، قال: كتب عمر إلى أبي موسى الأشعري: ((أن فرقوا بين المجوس وحرمهم)).','1','394',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (224,'<span data-type="title" id=toc-84>٥ - باب: من أتى أم امرأته من الرضاعة</span>.
سئل إسحاق عن رجل وطئ أم امرأته من الرضاعة؟
قال: حرمت عليه امرأته ليس فيه شك.
حدثنا عبيد الله بن معاذ، قال: ابن محمد بن عمرو بن علقمة الليثي، عن الزُّهْرِيّ، عن عروة، عن عائشة قالت: قال رسول الله - صلى الله عليه وسلم -: ((إن الرضاعة تحرم ما تحرم الولادة)).','1','395',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (225,'<span data-type="title" id=toc-85>٦ - باب: من قال: إن لم أتزوج فلانة فأنت طالق</span>.
قلت لإسحاق: رجل قال لامرأته: إن لم أتزوج فلانة إلا أن لا تزوجني نفسها، فأنت طالق ثلاثًا، فذهب إلى تلك المرأة، فقال: تزوجي نفسك على مهر مائة درهم، فقالت: لا ولكن على مهر ألف ومهر مثلها ألف، ما يقول في ذلك؟
قال: هو على إرادته.
قلتُ: فإنه لم ينو في ذلك المهر شيئًا؟
قال: إذا زوجته نفسها على مهر مثلها، فلم يفعل، فقد ضبب فراجعته في ذلك، وقلتُ: غن مهر مثلها ألف، وتقول هي: لا أزوجك نفسي دون عشرة آلاف؟
فقال: على ما تزوج مثلها، حدثنا محمود: قراءة عليه، عن عمر بن عبد الواحد، قال: سئل الأوزاعى عن رجل جعل امرأته طالقًا إن لم يتزوج امرأة سماها لها زوج، قال: طلقت امرأته مكانه، وإن كان أهلاً أن تنال منه.','1','396',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (226,'<span data-type="title" id=toc-86>٧ - باب: من تزوج امرأة على أن يحج بها</span>.
سئل إسحاق عن امرأة زوجت نفسها من رجل بشرط أن يحملها إلى مكة ويقيم معها مجاورًا وتركت به المهر كذلك، فحملها إلى مكة، ثم ردها، ولم يدعها تقيم بمكة؟
قال: ينبغي للرجل أن يفي لها بالشرط، وذكر حديث النبي - صلى الله عليه وسلم -: ((إن أحق ما وفيتم به من الشروط ما استحللتم به الفروج))، ولها أن ترجع في المهر.
حدثنا المسيب بن واضح، قال: حدثنا بن المبارك، عن أبي عوانة، عن المغيرة، عن حماد: في رجل تزوج امرأة لي أن يحج بها، فماتت، أو مات.
قال: قيمة الحج المهر.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: ثنا عبد الحميد بن جعفر، عن يزيد بن أبي حبيب، عن أبي الخير مرثد بن عبد الله الثربي، قال: سمعت عقبة','1','397',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (227,'بن عامر يقول: قال رسول الله - صلى الله عليه وسلم -: ((إن أحق ما وفيتم به من الشروط ما استحللتم به الفروج)).
سمعت إسحاق قال: أخبرني سفيان، عن عبد الملك، /٤٥/ عن ابن المبارك أنه قال: لا نكاح إلا بولي، وأرى أن يفرق بينهما.
قيل له: فما تقول إن ماتا يتوارثان، أو طلقها، أيقع عليها طلاقه؟
قال: أما في القياس، فلا ميراث، ولا طلاق، ولكني أجبن.
وسألت سوار بن عبد الله، قلتُ: إن تزوجها بغير ولي، ثم طلقها ثلاثًا؟
قال: النكاح عندنا باطل، ولا يقع الطلاق هاهنا، ويلحق به الولد إن كان ولد','1','398',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (228,'ويدرأ عنه الحد.
حدثنا سعيد بن منصور، قال: حدثنا سفيان، عن عمرو بن دينار، عن عبد الرحمن بن معبد بن عمير بن أخي عبيد بن عمير أن عمر بن الخطاب رد نكاح امرأة تزوجت بغير ولي.','1','399',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (229,'<span data-type="title" id=toc-87>٨ - باب: من خطب على خطبة أخيه</span>.
سئل أحمد عن الخطبة على خطبة أخيه.
قال: هو شبيه بالسوم على السوم إذا ركن إليه، وارتضى كل واحد منهما صاحبه؛ وذلك أن مالكًا هكذا فسره.
وسألت إسحاق، قلتُ: رجل خطب على خطبة أخيه، فزوجوه، أنزاه له طيبًا؟
قال: لا، قلت: أفتحب له أن يفارقها؟
قال: أحب أن يتبع نهي النبي - صلى الله عليه وسلم -.
قلتُ: يفارقها؟
قال: نعم.
قلتُ: خطب الرجل امرأة، فلم تزوج، ولم ترد، هل ترى لهذا أن يخطبها على خطبة هذا الرجل؟
قال: لا يخطب حتى يُرد.
قلت لإسحاق: رجل حلف بطلاق امرأته ثلاثًا أن يتزوج فلانة، فتزوجها في','1','400',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (230,'عدة من زوجها؟
قال: ليس هذا تزويج، حدثنا عبيد الله بن معاذ، قال: حدثنا أبي: قال: ثنا أشعث، عن الحسن فيمن قال أنت طالق إن لم أتزوج عليك.
قال: إن تزوج تزويجًا ليس بجائز لم يبرّ.','1','401',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (231,'<span data-type="title" id=toc-88>٩ - باب: مناكحة الجن</span>.
قلتُ: لأسحاق رجل ركب البحر، فكسر به، نتزوج جنية؟
قال: مناكحة الجن مكروه.
حدثنا إسحاق، قال: ثنا أبو معاوية، عن الحجاج، عن الحكم: أنه كره نكاح الجن.
حدثنا محمد بن يحيى القطعي، قال: حدثنا بشر بن عمر، قال: ثنا ابن لهيعة، عن يونس بن يزيد، عن الزُّهْرِيّ قال: نهى رسول الله - صلى الله عليه وسلم - عن نكاح الجن.','1','402',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (232,'حدثنا إسحاق، قال: أخبرنى محرز شيخ من أهل مروثقة، قال: سمعت زيدًا لعمّى يقول: اللهم ارزقنى جنية أتزوجها، قيل له: يا أبا الحوارى وما تصنع بها: قال: تصحبني في أسفارى حيث ماكنت كانت معى.
حدثنا إبراهيم بن عرعرة، قال: حدثني سلم بن قتيبة، قال: حدثنى عقبة الرفاعي: قال: سألت قتادة عن تزويج الجن، فكرهه.
حدثنا إبراهيم بن عرعرة: حدثنى سلم بن قتبية، قال: حدثني عقبة الرفاعي: قال: سئل الحسن عن تزويج الجن فكرهه.','1','403',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (233,'<span data-type="title" id=toc-89>١٠ - /٤٦/باب: يتزوج الرجل على تحو سنّه</span>.
قلت لإسحاق: الرجل يتزوج البكر أحب إليك، أم الثيب؟
قال: للشباب البكر أحب إلىَّ، وللشيخ إذا تزوج المكتهل كان أحب إلىًّ، ثم قال: على نحو سن الرجل.
حدثنا عمرو بن عثمان، قال: حدثنا بقية بن الوليد، عن أبي بكر بن أبي مريم، عن ضمرة وحبيب بن عبيد أن شيخًا تزوج شابة على عهد عمر بن الخطاب، فلما كان عند الوقاع ضمته إليها، فقتلته، فبلغ ذلك عمر بن الخطاب','1','404',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (234,'فقال: أيها الناس ليتزوج الرجل منكم لمته، ولتتزوج المرأة لمتها من الرجال.
حدثنا أبو معن، قال: حدثنا سعيد الحريرى، عن أبي نصرة، عن جابر بن عبد الله، قال: قال لى رسول الله - صلى الله عليه وسلم -: ((يا جابر: ما استحلفت بعد أبيك؟))
قلتُ: يا رسول الله- امرأة ثيبًا، قال: ((فهلا بكرًا تلاعبك وتلاعبها، وتضاجعك وتضاجعها)).
قلت لإسحاق: رجل قال لامرأة إذا تزوجتك، ثم دخلت هذه الدار، فأنت طالق ثلاثًا
قال: هذا بحق الطلاق قبل النكاح، لا أجيب فيها، وقال في الطلاق قبل النكاح: إذا نصبها بعينها لم أقل فيها شيئًا، وإذا لم ينصبها،','1','405',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (235,'ولكن وقت وقتًا، أو سمى قبيلتها، أو مصرها، أو لم يسم، ولم يوقت، فهذا كله واحد وليس بشيء حدثنا نصير بن الفرج، قال: ثنا يزيد بن هارون، قال: أخبرنا سعيد بن أبي عون، عن قتادة، عن سعيد بن المسيب والحسن قالا في رجل قال: إن تزوجت فلانة إلى شهر، فدخلت دار فلان، فهىَّ طالق ثلاثًا، فتزوجها، فدخلت دار فلان، فقالا: ليس بشيء حتى تكون يوم، قال ما قال في ملكه.','1','406',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (236,'<span data-type="title" id=toc-90>١١ - باب: امرأة لها أمة، فزوجتها</span>
سئل إسحاق عن امرأة لها أمة، فأرادت أن تزوجها؟
قال: تأمر وليها، فيزوجها، فإن لم يكن لها ولي أمرت رجلاً، فزوجها.','1','407',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (237,'<span data-type="title" id=toc-91>١٢ - باب: لكل مطلقة متعة</span>.
قلت لإسحاق: رجلٌ تَزَوَّجَ امرأةً، ولم يسم لها مهرًا، فطلقها واحدة قبل أن يدخل بها؟
قال: لها المتعة لازمة.
قالت: وكم المتعة؟
قال: ثلاثة أثواب أحب إلينا، وأدناه ثوب واحد جامع.
حدثنا أحمد بن يونس، قال: حدثنا ليث بن سعد، عن نافع، عن عبد الله: أنه كان يقول لكل مطلقة متعة، والتى تطلق واحدة، أو ثنتين، أو ثلاثًا إلا أن تكون امرأة طلقها الرجل قبل أن يسمها، وقد كان فرض لها، فحسبها فريضتها، فإن لم يكن فرض لها، فليس لها إلا المتعة.
حدثنا المسيب بن واضح، ثنا بن المبارك، عن المثنى بن الصباح، عن عمرو بن شعيب، عن سعيد بن المسيب في المتعة، قال: أدناها كسوة، وأرفعها خادم.','1','408',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (238,'حدثنا المسيب /٤٧/بن واضح: قال: حدثنا ابن المبارك، قال: قال سفينان: سمعت حمادًا يقول في الرجل يطلق، ولم يدخل بها، ولم يكن فرض لها: تُمتّع نصف مهر مثلها.
حدثنا المسيب: قال حدثنا ابن المبارك، عن يونس، عن الزُّهْرِيّ في المختلعة تمتع؟
قال: نعم، من غير أن يكره زوجها على ذلك.
سألت إسحاق، قلتُ: رجل له امرأة، فتزوج صبية صغيرة بنت سنة، فذهبت الكبيرة، فأرضعت الصغيرة؟
قال: تحرم الصغيرة عليه صارت بنته؛ بسبب إرضاع الزوجة الكبيرة للزوجة.
وسألتُ إسْحاقَ مرة أخرى، قلتُ: رجلٌ تَزَوَّجَ امرأةً، ثم تزوج صبية صغيرة، فأرضعت الكبيرة الصغيرة؟
قال: حرمت الصبية، ويمسك الأم، ثم راجعته فيها، فثبت عليها.
قلتُ: ولا تحرمان جميعًا؟
قال: لا
قال أبو محمد: تحرمان جميعًا.','1','409',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (239,'<span data-type="title" id=toc-92>١٣ - باب: طلاق العبد امراته</span>.
سمعت إسحاق يقول: إذا أذن لعبده في التزويج، فالطلاق بيد العبد.
حدثنا أبو معن الرقاشي، قال: حدثنا معتمر بن سليمان، قال: سمعت عبيد الله بن عمر يحدث، عن نافع، عن ابن عمر، قال: إذا تزوج العبد بإذن مولاه، فالطلاق بيد العبد.','1','410',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (240,'<span data-type="title" id=toc-93>١٤ - باب: الاستثناء في الطلاق</span>.
سألت أحمدَ عن الاستثناء في الطلاق، وكيف هو؟
قال: لا أقول في هذا شيئًا.','1','411',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (241,'وسمعت أحمد مرة أخرى عن الاستثناء في الطلاق، قال: لا أقول فيه شيئًا في الطلاق والعتاق، وأخاف أن نلزمه الطلاق.
قلتُ: فإن قَدَّم الاستثناء، فقال: أنت إن شاء الله طالق؟
قال: هو واحد، وسئل إسحاق عن رجل، قال لامرأته أنت طالق إن شاء الله تعالى؟
قال: لا يقع الطلاق.
وسألتُ إسْحاقَ مرةً أخرى عن الاستثناء في الطلاق؟
قال: جائز؟
قلتُ: فالاستثناء في العتق؟
قال: جائز، وذكر ذلك عن أبي مجلز، وسئل إسحاق مرة أخرى عن الاستثناء في الطلاق قبل وبعد؟
قال: إذا كان متصلاً بالطلاق جاز.','1','412',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (242,'وسألت علي بن عبد الله، قلتُ: رجل قال لامرأته: أنت طالق إن شاء الله؟
قال: هى طالق إذا كان هذا قوله، إنما الاستثناء إذا قال الرجل لمرأته: أنت طالق إن لم أفعل كذا وكذا، فهذا ثنياه، وهذا معناه.
حدثنا سعيد بن عون، قال: حدثنا إسماعيل بن عياش، عن حميد بن مالك اللخمى، عن مكحول، عن معاذ بن جبل، قال: قال رسول الله - صلى الله عليه وسلم -: يا معاذ: ماخلق الله شيئًا على وجه الأرض أحب إليه من العتاق وما خلق الله شيئًا على وجه الأرض أبغض إليه من الطلاق، فإذا قال الرجل لمملوكه أنت /٤٨/ حر إن شاء الله، فهو حر ولا استثناء له، وإذا قال لامرأته أنت طالق إن شاء الله فله استثناؤه، ولا طلاق عليه.','1','413',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (243,'<span data-type="title" id=toc-94>١٥ - باب: طلاق السكران</span>.
سُئِلَ أحمدَ عَنْ طلاق السكران؟
قال: لا أقول فيه شيئًا،','1','414',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (244,'وسئل أحمد مرةً أخرى عن طلاق السكران؟
قال: لا أقول فيه شيئًا.
وسألت أحمدَ مرةً أخرى، قلتُ: السكران يتزوج؟
قال: ما أدرى كيف هذا! وكأنه ذهب إلى أن يقف.
وسمعت إسحاق يقول في طلاق السكران: لا يجوز إذا كان لا يعقل.
قلتُ: فيستحلف أنه لا يعقل أنه طلقها؟
قال: يستحلف أنه لم يطلق.
وسئل إسحاق مرةً أخرى عن طلاق السكران وبيعه، وشراه؟
قال: ليس بشيء، ولا بيعه، ولا شراه، وسألت علي بن عبد الله عن طلاق السكران، قلتُ: ما قولك فيه؟ قال: طلاقه جائز، وإن افترى على إنسان أقيم عليه الحد، وإن أبى حدًا ما كان أقيم عليه.
حدثنا سعيد بن منصور، قال: حدثنا أبو مرية، عن ابن أبي ذئب، عن الزُّهْرِيّ، عن أبان بن عثمان بن عفان: أنه كان لا يجيز طلاق','1','415',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (245,'السكران.
حدثنا سعيد بن منصور، قال: حدثنا هشيم: قال أخبرنا يحيى بن سعيد أن عمر بن عبد العزيز أتى بسكران طلق، فاستحلفه أنه طلق، وما يعقل، فحلف، فضربه الحد، ورد عليه امرأته.
حدثنا سعيد، قال: حدثنا هشيم، قال: أنبا يحيى بن سعيد، عن القاسم بن محمد أنه قال مثل ذلك.
حدثنا سعيد، قال: حدثا هشيم، قال: أنبا مغيرة، عن إبراهيم: أنه قال:','1','416',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (246,'طلاق السكران جائز وما أتي من حدٍ أقيم عليه، أنبا سعيد: قال: حدثنا سفيان، عن ابن أبي يحيى، عن مجاهد قال: طلاق السكران جائز.
حدثنا هشام بن عمار، قال: أبنا خالد بن يزيد بن أبي مالك، عن أبيه، قال: السكران إذا غلب سقط عنه كل شيء إلا العتق والطلاق، فإنهما يلزمان.','1','417',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (247,'<span data-type="title" id=toc-95>١٦ - باب: من أكره على الطلاق</span>
سألت أحمدَ بن حنبل، قلتُ: رجل أكره على الطلاق؟
قال: إذا عذب، أو ضرب، أو خاف على نفسه رجوت أن لا يلزمه.
وسئل أحمد أيضًا عن يمين المستكره؟
قال: لا يكون عندى مستكرهًا حتى ينال بضرب، أو بعذاب، وسئل أحمد مرةً أخرى، وقيل له: رجل أخذه اللصوص، فقالوا له: احلف بالطلاق أنه ليس معك مال، فحلف؟
قال: لا يجوز له أن يحلف إلا أن يضرب، أو يعذب/٤٩/، وإلا فلا.
قيل: فأوعد؟
قال: الإيعاد ليس بشيء إلا أن يضرب.
وسألتُ إسْحاقَ عن المكره على الأشياء، وقلت له: له أيكون مكرهًا من غير أن ينال بضرب أو نحو ذلك؟
قال: إذا أفزعوه، أو خاف على نفسه، فهو مكره وسئل إسحاق مرةً أخرى عن طلاق المكره؟
قال: كلما أكرهه اللصوص، أو سلطان ظالم، وهو ينوي غير ما يحلف فالنية نيته.
حدثنا عمرو بن عثمان، قال: حدثنا بقية بن الوليد، قال: سألت الأوزاعي','1','418',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (248,'عن الرجل يكرهه اللصوص ومعه امرأته شابه، فيقولون له طلقها وإلا قتلناك، ويشيرون إليه بالسيف، فيطلق؟ قال: ليس عليه شيء.
حدثنا سعيد بن منصور، قال: ثنا هشيم قال: أخبرنا عبد الله بن طلحة الخزاعي، قال: حدثنا أبو يزيد المزنى، عن ابن عباس، قال: ليس طلاق المكره، ولا المضطهد طلاق.
حدثنا سعيد بن منصور: قال: حدثنا هشيم، قال: أنبا منصور ويونس عن الحسن: أنه كان لا يرى طلاق المكره شيئًا.','1','419',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (249,'حدثنا عبيد الله بن معاذ، عن أبيه، عن أبي جعفر، عن مقاتل بن حبان، قال: شهدت عمر بن عبد العزيز، وقال له رجل من أهل خرسان من بنى تميم: أن قتيبة بن مسلم قال لى طلق امرأتك، وإلا فعلت بك، وفعلت، فطلقتها فرقًا منه، قال: فردها عليه، وقد كانت تزوجت بعده، وولدت لذلك الزوج أولادًا.','1','420',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (250,'حدثنا عمرو بن عثمان، قال: حدثنا الوليد بن مسلم، قال: حدثنا الأوزاعي، عن عطاء بن أبي رباح: أنه سأله عن طلاق المكره، فإن لم يجيه إلى ما يأخذ عليه لم ينج منه سالماً؟
فقال: لا شيء عليه، قال الله: {إِلاَّ أَن تَتَّقُواْ مِنْهُمْ تُقَاةً} [آل عمران: ٢٨] , قال لي الأوزاعي، وسألت عنه عبد الله بن عبيد بن عمير، فقال: لا شيء عليه، أولاءك قوم فتانون.
حدثنا محمد بن المصفاء: قال حدثنا عباس بن الوليد: قال: حدثنا شعبة، عن حماد، عن إبراهيم في الرجل يمر بالماشي، فيحلف له بالمشي إلى بيت الله','1','421',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (251,'يعني بيت نفسه؟
قال: ليس عليه شيء.
حدثنا أبو معن: قال: حدثنا عبد الرحمن بن مهدى: قال: حدثنا إسرائيل، عن إبراهيم بن عبد الأعلى، عن جدبة، عن أبيها سويد بن حنظلة، قال: خرجنا نريد النبى - صلى الله عليه وسلم -، ومعنا وائل بن حجر، فأخذه عدو له، فتحرج القوم أن يحلفوا وحلفت أنه أخي، فأتينا النبي - صلى الله عليه وسلم -، فذكرت ذلك له، فقال، ((صدقت، المسلم أخو المسلم)).','1','422',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (252,'<span data-type="title" id=toc-96>١٧ - /٥٠/باب: من قال لامرأته أنت طالق. أنت طالق. أنت طالق</span>.
سئل يعني أحمد عن رجل قال لامرأته: أنت طالق، أنت طالق، أنت طالق؟
قال: إن كانت غير مدخول بها، فهى واحدة؛ لأنها بانت بالأولى، وإن كانت مدخولاً بها فأراد أن يفهمها ويعلمها، فإنه مذهبه في ذلك أيضًا واحدة، وإلا فثلاث.
قلتُ: فإن طلق التى لم يدخل بها ثلاثًا؟
قال: لا تحل له حتى تنكح زوجًا غيره.','1','423',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (253,'وسئل إسحاق عن رجل طلَّقَ امرأتَه قبل أن يدخل بها؟ قال: إن طلقها ثلاثا بكلمة وقعت ثلاثًا، وإن طلقها واحدة، ثم ثانية، ثم ثالثة لم يقع إلا واحدة الأولى.
وسألتُ إسْحاقَ مرة أخرى، قلتُ: رجل قال لامرأة ولم يدخل بها أنت طالق طالق طالق إن دخلت هذه الدار، فدخلت؟
قال: يقع الطلاق.
قلتُ: كم يقع؟
قال: واحدة.
وسألتُ إسْحاقَ مرة أخرى، قلتُ: رجل قال لامرأته لم يدخل بها: أنت طالق واحدة، ثم قال: لا، بل ثلاثًا؟
قال: إن لم يكن دخل بها، لم يقع إلا واحدة.
قلتُ: فإن كان دخل بها؟
قال: يعق عليها الطلاق، يعني ثلاثًا.
حدثنا أبو هشام: قال: حدثنا حسان في رجل قال لامرأته: أنتِ طالق أفهمتك، أنت طالق أفهمتك، أنت طالق أفهمتك؟
قال: ذكر سفيان أن ابن أبي ليلى يجعلها ثلاثًا إلا أن يكون قوله ذلك جوابًا لقولها.
حدثا عبيد الله بن معاذ: قال: حدثنا أبي: قال: حدثنا أشعث، عن الحسن،','1','424',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (254,'فيمن قال لامرأته: أنت طالق، أنت طالق، أنت طالق؟
قال: ثلاث.
حدثنا عمرو بن عثمان، قال: حدثنا محمد بن حرب، عن الزبيدي قال: سئل الزُّهْرِيّ عن رجل قال لامرأته قبل أن يدخل بها أنت طالق، أنت طالق، أنت طالق، فحدثنا، عن محمد بن عبد الرحمن بن ثوبان: أنه أخبره أن محمد بن إياس بن بكير الليثي أخبره أن أبا هريرة وابن عباس، وعبد الله بن عمر سألهم رجل من بني تميم عن ذلك، فقالوا: لا تحل لك حتى تنكح زوجًا غيرك.','1','425',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (255,'حدثنا محمد بن رافع، قال: حدثنا بن أبي فديك، قال: أخبرنا بن أبي ذئب، عن ابن قسيط، عن أبي بكر بن عبد الرحمن بن الحارث: أنه قال في رجل قال لامرأته ولم يكن دخل بها- أنت طالق، ثم أنت طالق، ثم أنت طالق، قال أبو بكر: قد بانت منه حين طلقها التطليقة الأولى.
حدثنا علي بن عثمان: قال: حدثنا حماد: قال: أنبا عاصم بن بهدلة، عن أبي وائل: أن بن مسعود قال في رجلٍ طلَّقَ امرأتَه ثلاثًا قيل أن يدخل بها؟
قال: لا /٥١/تحل له حتى تنكح زوجًا غيره.','1','426',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (256,'<span data-type="title" id=toc-97>١٨ - باب: إذا قال: أنت طالق قبل أن يدخل بها، ثم وطئها</span>.
وسُئِلَ أحمدَ عَنْ رَجلٍ قال لامرأته- قبل أن يدخل بها- أنت طالق، ثم وطئها، وهو لا يعلم أنها لا تحل له؟
قال: لها صداق، ونصف.
حدثنا محمد بن نصر، قال: حدثنا حسان بن إبراهيم في رجل طلَّقَ امرأتَه قبل أن يدخل بها، ثم وقع عليها بعد الطلاق بجهالة.
قال سفيان: قول حماد: لها نصف الصداق حين طلقها قبل أن يدخل بها، ولها المهر كاملاً بوقوعه عليها.
وأما ابن أبي ليلى، فيقول: ليس لها إلا نصف الصداق وذلك لما نذكر عن عبد الله بن إياس بن بكير حين آلى من امرأته، فأتى ابن','1','427',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (257,'مسعود، فأمره أن يخطبها، ولم يجعل لها صداقًا بوقوعه عليها.
حدثنا سعيد بن منصور، قال: أنبا هشيم، قال: أنبا منصور ويونس، عن الحسن، قال: عليه مهر واحد.','1','428',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (258,'<span data-type="title" id=toc-98>١٩ - باب: النية في الطلاق</span>.
قيل لأحمد: رجل طلق ثلاثًا، وهو ينوي واحدة؟
قال: هى ثلاث، وسمعت أحمد مرة أخرى إذا طلق واحدة، وهو ينوي ثلاثًا، فهى واحدة.
قيل: فإن طلق واحدة ينوي ثلاثًا؟
قال: هي واحد؛ إنما النية فيما خفي، وليس فيما ظهر منه وقال: قال مالك: إذا نوى ثلاثًا، فهى ثلاث، وهو معنى قول سعيد بن المسيب،','1','429',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (259,'وسألت إسحاق: قلتُ: فإن قال: أنت طالق واحدة، ونوى ثلاثًا؟
قال: هى واحدة.
قال: أليس النية فيما خفى، وليس فيما ظهر؟
قال: نعم.
وسئل إسحاق مرةً أخرى عن رجل قال لامرأته: أنت طالق ثلاثًا، ونوى واحدة؟
قال: إذا تكلم بلسانه ثلاثًا، وكان في قلبه واحدة وقعت ثلاث.
وإذا قال: أنت طالق واحدة، وكان في قلبه ثلاث، فإنه تقع واحدة.
وسمعت إسحاق مرةًا أخرى يقول: إذا قال: أنت طالق ونوى ثلاًث، قال: هذا بين هو ثلاث.
وسمعت إسحاق مرةً أخرى يقول: إذا قال: أنت طالق ولم يقل واحدة، ولا ثلاثا، ونوى ثلاثًا، فإنه يكون على إرادته ثلاثًا، وجهل هؤلاء، ولا قالوا: إذا قال أنت طالق ونوى ثلاثًا، قالوا: لا تكون ثلاثًا، وقالوا: بأجمعهم: إذا قال لامرأته: أنت طالق طلاقًا، ونوى ثلاثًا كان ثلاثًا، وكذلك إذا قال لها أنت الطلاق، ونوى ثلاثًا فمن ها هنا جهلوا حيث قالوا في هاتين: يقع الثلاث إذا نوى، وقالوا: إذا قال أنت طالق ونوى ثلاثًا لا يكون ثلاثًا. /٥٢/
وسألت أبا ثور: قلتُ: إن قال: أنت طالق واحدة ونوى ثلاثًا؟
قال: هي واحدة، وإن قال أنت طالق ثلاثًا ونوى واحدة، فهي ثلاث، هو ما تكلم به، وكان مالك والشافعى يقولان: إذا طلق واحدة، ونوى ثلاثًا، فهى ثلاث، وإن طلق ثلاثًا، ونوى واحدة، فهى ثلاث، ولم يسئل عن نيته.','1','430',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (260,'حدثنا عبيد الله بن معاذ، قال: حدثنا أبي: قال: حدثنا أشعث، عن الحسن فيمن طلق واحدة وقال: نويت ثلاثًا، قال: هي واحدة.
حدثنا سعيد بن منصور، قال: حدثنا هشيم: قال: أخبرنا بن شبرمة، عن الشعبي، قال: إنما النية فيما خفى، فأما ما ظهر فلا نية فيه.','1','431',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (261,'<span data-type="title" id=toc-99>٢٠ - باب: طلاق البائن</span>.
سألت أحمدَ، قلتُ: الرجل يقول لامرأته: أنت طالق تطليقة بائنة؟
قال: هذه مسألة مشتبكة.
وسألت إسحاق، قلتُ: رجل قال لامرأته: أنت طالق تطليقة بائنة؟
قال: هى تطليقة بائنة كما قال لا يملك رجعتها، وجعل ينكر قول الشافعي، حدثنا عبيد الله بن معاذ، نا أبي: نا أشعث، عن الحسن فيمن قال لامرأته أنت طالق تطليقة بائنة، قال: هى واحدة بائنة فإن قال: أنت طالق طلاقًا بائنًا، فثلاث.','1','432',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (262,'<span data-type="title" id=toc-100>٢١ - باب: من قال كل امرأة في الدنيا طالق</span>.
قلتُ لأحمدَ: رجل قال: كل امرأة في الدنيا طالق؟
قال: إن كانت له امرأة دخل عليه الطلاق.
وسألت أبا ثور، قلتُ: رجل قال: كل امرأة في الدنيا عليًّ كظهر أمه؟
قال: إن كانت له امرأة لزمه الظهار، وإن لم يكن له امرأة، فلا شيء.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: حدثنا أبو عوانة، عن مغيرة، عن إبراهيم في رجل قال: كل امرأة في الدنيا عليه حرام.
قال: هدا رجل حرم المحصنات، وليس بشيء.','1','433',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (263,'<span data-type="title" id=toc-101>٢٢ - باب: طلاق السنة</span>.
سألت أحمدَ، قلتُ: طلاق السنة؟
قال: أن يطلقها، وهى طاهر من غير جماع تطليقة، ثم يدعها حتى تنقضي عدتها كلها، وأنكر قول من يقول يطليقها عند كل طهر.
وسألت إسحاق، قلتُ: رجل قال لامرأته: أنت طالق ثلاثًا كما أمر الله ورسوله؟
قال: هى طالق كلما حاضت ثم طهرت وقع عليها تطليقة.
قلتُ: وليس له عليها رجعة؟
قال: لا.
قلتُ: فإن قال: أنت طلاق للسنة ثلاثًا؟
قال: السنة لا تكون ثلاثًا يقع عليها ثلاث تطليقات الآن قلتُ: فإن قال: أنت طالق
طلاق السنة؟
قال: يقع عليها تطليقة.','1','434',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (264,'حدثنا سعيد بن منصور، قال: حدثنا شريك بن عبد الله، عن أبي إسحاق، عن أبي الأحوص: قال: قال عبد الله بن مسعود/٥٣/: إذا أراد الرجل أن يطلق للعدة، فليطلقها واحدة طاهرًا من غير جماع.','1','435',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (265,'<span data-type="title" id=toc-102>٢٣ - باب: إذا كتب بطلاق امرأته</span>.
قيل لأحمد: الرجل بكتب إلى امرأته بطلاقها، فيضيع الكتاب.
قال: إذا كتب إذا جاءك كتابي هذا، فإنى أرجو أن لا يكون عليه، وإذا كتب أنت طالق فكأنه أوقع عليه الطلاق.
قيل: فإن كتب إليها بالطلاق من غير أن يتكلم به؟
قال: ما أدرى، ثم قال: الكتاب عمل وكأنه أوقع عليه.
وسألتُ إسْحاقَ عن رجل كتب بالطلاق؟
قال: إذا أراد الطلاق.
وسألتُ إسْحاقَ مرة أخرى، قلتُ: رجل كتب طلاق امرأته على الأرض أو الحائط؟
قال: تكلم به؟
قلتُ: لا.
قال: ليس بشيء إلا أن يتكلم.
قلت لإسحاق أيضًا، إن كتب إليها: أنت طالق ثلاثًا، ثم ندم، ومزق الكتاب؟
قال: مثل الأول.','1','436',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (266,'حدثنا عبيد الله ين معاذ، قال: حدثنا أبي: قال: حدثنا أشعث، عن الحسن في الرجل يكتب بطلاق امرأته.
قال: إن خطه بيده، فخطه لسانه، فقد طلقت امرأته، وإن أمر غيره، فكتب، فإذا وصل الكتاب قال عبيد الله: قال أبي: ووجدت في مكان أخبر أن الحسن قال في هذا: له أن يرجع مالم يصل الكتاب إلى امرأته.
حدثنا أبو معن، قال: ثنا خالد بن الحارث، قال: ثنا عبد الملك العرومي، عن عطاء في الرجل يكتب إلى امرأته بطلاقها، إن نفّذ فهي طالق، وإن ندم فأمسك، فليس بشيء.','1','437',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (267,'<span data-type="title" id=toc-103>٢٤ - باب: إذا وصل إليها الكتاب، هل تعتد</span>؟
سألت أحمدَ، قلتُ: امرأة أتاها كتاب من زوجها بخطه وخاتمه بالطلاق، هل تزوج؟
قال: لا حتى يشهد عندها شهود عدول.
قيل: فإن شهد حامل الكتاب؟
قال: لا إلا شاهدين.
حدثنا عبيد الله بن معاذ قال، حدثنا أبي، قال: ثنا أشعث، عن الحسن في امرأة أتاها كتاب من زوجها بطلاق؟
قال: لا تعتد إلا بشهادة عدلين؟','1','438',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (268,'<span data-type="title" id=toc-104>٢٥ - باب</span>
قيل لأحمد: رجل طلَّقَ امرأتَه ثنتين، فتزوجت زوجًا غيره، فطلقها، ثم راجعها الأول على كم يكون عنده.
قال: على واحد على ما بقي من الطلاق يروى عن الأكابر من أصحاب رسول الله - صلى الله عليه وسلم -، وأنكر قول من يقول على ثلاث.','1','439',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (269,'حدثنا سعيد بن منصور: قال: حدثنا إسماعيل بن عياش: قال: حدثنا عبيد الله بمن عبيد الهلاعي، عن مكحول، عن أبي هريرة، عن عمر بن الخطاب، قال: إذا طلق الرجل امرأته /٥٤/تطليقتين أو تطليق، ثم تزوجها زوج غيره، ثم توفى عنها، أو طلقها رجعت إلى زوجها أنها عنده على ما بقي من الطلاق.
حدثنا المسيب بن واضح، قال: حدثنا بن المبارك، عن يحيى بن سعيد: أنه سمع سعيد بن المسيب يقول: سمعت أبا هريرة يقول: قال عمر بن الخطاب: من طلَّقَ امرأتَه تطليقة أو تطليقتين، ثم طلقها حتى يخلوا (١) أجلها، فتزوجت، ثم رجعت إليه، فهى عنده على ما بقى من الطلاق.
_________
(١) كذا في الأصل والصواب بدون الألف.','1','440',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (270,'حدثنا المسيب، قال: حدثنا ابن المبارك، عن شعبة، عن حماد، عن سعيد بن جبير، عن ابن عباس وابن عمر أنهما قالا: يهدم النكاح الطلاق، وكان سفيان يأخذ بقول عمر بن الخطاب: هى على ما بقى من الطلاق، وأبو حنيفة يأخذ بقول ابن عمر.','1','441',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (271,'<span data-type="title" id=toc-105>٢٦ - باب: إذا قال لامرأته: قد أعتقتك</span>.
سألت أحمدَ: قلتُ: رجل قال لامرأته: قد أعتقتك؟
قال: إذا كان في غضب، فإنى أخشى أن يكون مثل اعتدى ونحوها.
قلتُ: فإن كان في غير غضب، فلم يره شيئًا.
قلتُ: فإن قال لا سبيل عليك؟
قال: وهذا أيضًا مثله.
حدثنا عبيد الله بن معاذ: قال: حدثنا أبي، قال: حدثنا أشعث، عن الحسن، عمن قال لامرأه: قد أعتقتك.
قال: هو ما نوى،','1','442',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (272,'وفيمن قال لامرأته: لا سبيل لى عليك.
قال: واحدة، وهو أحق بها.
حدثنا سعيد بن منصور، قال: حدثنا بن المبارك، عن عبد الملك بن أبي سليمان، عن عطاء في رجل قال لامرأته: قد أعتقتك قال: إن كان نوى طلاقًا، وإلا فليس بشيء.','1','443',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (273,'<span data-type="title" id=toc-106>٢٧ - باب: إذا طلقها ثلاثًا، وهو صحيح، ثم مرض، فمات، هل ترثه</span>؟
سألت أحمدَ، قلتُ: رجل طلَّقَ امرأتَه ثلاثًا، وهو صحيح، ثم مرض، فمات، وهى في العدة؟
قال: لا ترثه إذا طلقها وهو صحيح.
وسألت إسحاق، قلتُ: رجل طلَّقَ امرأتَه ثلاثًا، وهو صحيح، ثم مرض، فمات، وهى في العدة؟
قال: لا ترثه.','1','444',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (274,'<span data-type="title" id=toc-107>٢٨ - باب: إذا طلقها ثلاثًا، وهو مريض</span>.
قلتُ لأحمدَ: فرجل طلَّقَ امرأتَه ثلاثًا، وهو مريض، فمات، وهى في العدة.
قال: أنا أقول إذا طلقها وهى مريض، ثم مات، فإنها ترثه ما كانت في العدة، وبعد انقضاء العدة مالم تزوج.
وسألت إسحاق، قلتُ: رجل طلَّقَ امرأتَه في مرضه، فأبت طلاقها؟
قال: ترثه في العدة، وبعد انقضاء العدة مالم تزوج/٥٥/، لأنه فرَّ من ميراثها.
قال: والأكثرون على أنها لا ترث بعد العدة، وأما عثمان بن عفان، فإنه ورثها من الميت بعد انقضاء العدة وهو الذى نعتمد عليه، لما كان أصل الطلاق فرارًا.','1','445',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (275,'وسألتُ إسْحاقَ مرةً أخرى، قلتُ: إن طلقها وهو مريض؟
قال: ترثه، وإن انقضت العدة.
قلتُ: ما لم تزوج؟
قال: نعم.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أنبا عمر بن أبي سلمة، عن أبيه أن عثمان بن عفان ورثها بعد انقضاء العدة.
حدثنا محمد بن معاوية، قال: حدثنا ابن لهيعة، عن الأعرج، عن السائب بن يزيد','1','446',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (276,'أن عبد الله بن محمد الزُّهْرِيّ أصابه فالج بالبحرين، فقدم المدينة، فطلق جويرية بنت مارط، فمكث سنتين قبل أن يموت، فورثها عثمان بن عفان منه.
قلت لإسحاق: رجل قال لامرأته إذا جاء وقت كذا وكذا، فأنت طالق ثلاثًا، فجاء ذلك الوقت والرجل مريض، فوقع عليها الطلاق، ثم مات وهى في العدة، هل ترثه؟
قال: لا ترثه، إنما ترثه إذا طلقها في المرض، راجته في هذه المسألة.
قلت لإسحاق: فإن قال لها في مرضه: أمرك بيدك، فقالت: قد طلقت نفسى ثلاثًا، فذهب إلى أنه إذا كان الطلاق من قبلها لم ترث، وإذا علم أنه يفرّ من الميراث ورثت.','1','447',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (277,'قلت لإسحاق: فإن قال لها في مرضه: إن دخلت دار فلان، فأنت طالق ثلاثًا، فدخلت، فوقع عليها الطلاق، ثم مات، هل ترثه؟
قال: إذا علم أنه فرّ من الميراث ورثته.
قلتُ: لا يعلم، فذهب إلى أنها ترث','1','448',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (278,'<span data-type="title" id=toc-108>٢٩ - باب: إذا طلقها، وهو مريض، قبل أن يدخل بها</span>.
سألت أحمدَ، قلتُ: رجل مريض طلَّقَ امرأتَه قبل أن يدخل بها، ثم مات؟
قال: قد اختلف الناس في هذا.
وسئل إسحاق عن رجل طلَّقَ امرأتَه في مرضه قبل أن يدخل بها؟
قال: هو فار من الميراث.
حدثنا علي بن عثمان، قال: حدثنا حماد بن سلمة، عن قتادة، عن عمر بن عبد العزيز في رجل طلَّقَ امرأتَه في مرضه قبل أن يدخل بها.
قال: لها','1','449',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (279,'نصف الصداق، ولا ميراث لها، ولا عدة عليها.
حدثنا علي، قال: حدثنا حماد، قال: أحدثنا قتادة، عن الحسن في رجل/٥٦/ طلق امرأته في مرضه ثلاثًا قبل أن يدخل بها، فمات.
قال: لها الصداق كاملاً، وعليها العدة، ولها الميراث وقال جابر بن زيد: لها الصداق كاملاً، ولا عدة عليها، ولا ميراث لها.','1','450',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (280,'<span data-type="title" id=toc-109>٣٠ - باب: إذا قال: أنت طالق ما شئت</span>.
قلت لإسحاق: الرجل يقول لامرأته: متى فعلت كذا وكذا، أو كلما فعلت كذا وكذا، أو إن فعلت، فهو كله عنده واحد.
قال: هو نيته، هو ما أراد.
حدثنا علي بن عثمان، قال: حدثنا حماد بن سلمة، قال: قال حماد بن أبي سليمان في رجل قال لامرأته: أنت طالق ما شئت، وكم شئت.
قال: هي طالق ما شاءت، وإذا قال: أنت طالق متى ما شئت، وإذا شئت، فهي واحدة إذا شاءت ذاك، وإذا قال: أنت طالق كلما شئت، فهي طالق كلما شاءت.
حدثنا أحمد بن محمد، قال ثنا غارم: قال: حدثنا خالد، قال: سمعت عبيد الله يقول: إذا قال الرجل لامرأته: ما شئت فهو على','1','451',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (281,'وجهين إن شئت، وكم شئت فإن شئت إن شاءت وكم شئت تطليقة لا بد منها فصاعدًا، قال: سمعت عبيد الله يقول في اليمين متى وإن لم يجعلهما بمنزلة كلما.','1','452',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (282,'<span data-type="title" id=toc-110>٣١ - باب: إذا قال أن أضرب غلامي فامرأته طالق</span>.
سألت أبا عبد الله أحمد بن حنبل، قلتُ: رجل قال: إن لم أضرب غلامي، فامرأته طالق؟
قال: يقول قوم: لا يفارق امرأته حتى يضرب، وإن مكث زمانًا، ولا أدرى ما هذا، أما أنا فأذهب إلى نيته، وما أراد من يومه، أو نحو ذلك وسألت أحمدَ مرةً أخرى، قلتُ: رجل حلف بطلاق امرأته ليضربن غلامه، فأخر ذلك سنة، أو نحو ذلك؟
قال: لا ولكن ما أراد من ذلك؛ لأن هذا بين لأي شيء أراد ضربه، فهو عاجلُ ما أراد من ذلك، وليس له أن يؤخر ذلك ويتركه.
وسألت أحمدَ مرةً أخرى، قلتُ: رجل قال إن لم أضرب غلامي، فامرأته طالق ثلاثًا؟
قال: هو نيّته متى أراد.
قيل: فإن أبق الغلام قيل أن يضربه؟
قال: إذا جاء الوقت الذى أراد ضربه، بانت امرأته.
قلتُ: فإن مات أحدهما قبل أن يضربه، هل يتوارثان؟
قال: لا يتوارثان إذا جاوز الوقت.','1','453',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (283,'وسألت إسحاق، قلتُ: رجل قال: إن لم أضرب مملوكي فلانًا فامرأته طالق ثلاثًا؟
قال: هو إرادته إن أراد من فوره، ولم يضربه طلقت امرأته.
قلتُ: فإن لم يكن له في ذلك نية، متى يضربه؟
قال: إن لم تكن له نية، فما دام العبد حيًا، فإن مات العبد قبل أن يضربه، فارق امرأته. /٥٧/
قلتُ: فإن ماتت المرأة، أو الزوج؟
قال: يتوارثان، فراجعته في ذلك أيضًا، فقال: لا يتوارثان؛ لأن الطلاق يقع.
وسألتُ إسْحاقَ مرةً أخرى، قلتُ: رجل قال: إن لم أضرب غلامي فامرأته طالق ثلاثًا كيف الأمر في ذلك؟
قال: إن ضربه على ما نوى حين حلف عليه، فحينئذ قد خرج من يمينه، وإن أاد ضربه على ما حلف، ونوى ولم يكن له تواري في ذلك فأعجزه ما حلف عليه، ولم يكن منه تراخي في ذلك، ولا احتيال، فليس عليه شيء.
قلتُ: وإن مات أحدهما، يتوارثان؟
قال: نعم.
حدثنا عبيد الله بن معاذ، قال: ثنا أبي، قال: ثنا أشعث، عن الحسن فيمن قال: امرأته طالق ثلاثًا إن لم أضرب غلامي، أو أفعل كذا وكذا ما كان.
قال: يغشاها حتى يعلم أنه لا يضربه، وإن طلقها واحدة، فبانت منه، ثم','1','454',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (284,'تزوجا فقد سقطت اليمين عنه، وإن لم يطلقها، فمات أحدهما قبل أن يضربه لم يتوارثا، فإن قال: هي طلق إن لم يضربه إلى سنة، فمات أحدهما قبل السنة توارثا وإن مضت السنة، ولم يضربه، فقد بانت منه، وإن لم يوقت سنة، فمات أحدهما لم يتوارثا، وإن ضربه بعد موت امرأته لم يبر في يمينه، ولم يرث.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أنبا منصور، عن الحسن في رجل قال: إن لم يأت البصره، فامرأته طالق.
قال: هى امرأته حتى يأتى البصره، فإن مات أحدهما، فلا ميراث بينهما.
حدثنا سعيد بن منصور: قال: ثنا خالد بن عبد الله، عن صالح بن مسلم، عن','1','455',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (285,'الشَّعبيِّ في رجل حلف أن يضرب غلامه بطلاق امرأته، فأبق الغلام.
قال: هي امرأته حتى يموت.
سئل إسحاق عن رجل قال لامرأته: إن لم أتزوج عليك، فأنت طالق ثلاثًا؟
قال: يقف لا تقريبًا حتى يفعل ما قال.
حدثنا المسيب، قال: حدثنا ابن المبارك، عن سفيان، عن غيلان بن جامع، عن الحكم بن عتيبة في الرجل يقول لامرأته: أنت طالق إن لم يفعل كذا وكذا، ثم يموت واحد منهما.
قال: يتوارثان، وقال سفيان: إنما وقع الحنث بعد الموت.
قلت لإسحاق: قال لامرأته: إن لم أضربك مائة، فأنت طالق ثلاثًا.
قال: إن نوى به جاز، وإن لم ينو فإنى أخاف، هكذا وقع في كتابي.','1','456',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (286,'حدثنا محمد بن أبي بكر، قال: ثنا يحيى بن سعيد، عن سفيان بن عيينة ومحمد بن/٥٨/ أبي بكر أيضًا، قال: حدثنا سفيان بن عيينة، عن ابن أبي بحيح، عن مجاهد {وَخُذْ بِيَدِكَ ضِغْثًا فَاضْرِب بِّهِ وَلَا تَحْنَثْ} [ص: ٤٤] قال: هي لأيوب خاصة، وقال عطاء: هى للناس عامة.
حدثنا سعيد بن منصور، قال: حدثنا أبو معاوية، قال: ثنا عبد الواحد بن أيمن، عن عطاء، قال: آتاه رجل، فقال: إني حلفت ألا أكسوا (١) امرأتي درعًا حتى تقف بعرفة.
فقال عطاء: احملها على حمار، ثم اذهب بها فقف بها عرفة.
فقال: إني إنما عنيت يوم عرفة.
فقال له عطاء: وأيوب حين حلف ليجلدن امرأته مائة جلدة نوى أن يضربها بالضغث؛ إنما أمره الله أن يأخذ
_________
(١) كذا في الأصل وهي بدون ألف.','1','457',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (287,'ضغثًا، فيضربها، ثم قال عطاء: إنما القرآن عبر، إنما القرآن عبر.','1','458',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (288,'<span data-type="title" id=toc-111>٣٢ - باب: من له امرأتان أو ثلاث فقال: إحداكن طالق</span>
قلت لأحمد: رجل، عنده امرأتان أو ثلاث فقال: إحداكن طالق. ولا يدرى أيتهن هي؟
قال: يقول قوم: يختار أيتهن شاء فيطلقها. ويقول قوم: يدخل عليهن الطلاق، ولا يقول أحمد في هذا شيئاً.
قال أحمد: ولو كان له نسوة فقال: إمرأته طالق. ذهبت إلى قول ابن عباس أنه يقع عليهن الطلاق، لأن هذا ليس مثل الأول مسألة مشتبكة، وهذه ليس من تلك.','1','459',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (289,'وسألتُ إسحاق: قلتُ: رجل له أربع نسوة فقال: إحداكن طالق، ولم ينوِ واحدةً منهن؟ قال: يقرع بينهن.
قلتُ لإسحاق: فإن مرت به واحدة فقال: أنت طالق ولا يدري أيتهن هي، ولم تقر واحدة منهن؟ قال: يقرع بينهن أيضاً.
وسُئلَ إسحاق مرة أخرى قِيل: فإن كان له ثلاث نسوة فقال: إحداكن طالق ثلاثًا. ثم مات. قال: تطلق واحدة، وثلثا الثمن، أو ثلثا الربع بينهن، وإن لم يمت أقرع بينهن.
وسألت إسحاق مرة أخرى قلت: رجل له امرأتان فقال: إحداكما طالق ولم ينو واحدة منهما، فماتت إحداهما قبل أن يقرع بينهما كيف حاله؟ وهل يرثها؟ قال: يوقف فيقال: هذه طلقت، أو هذه. فإن كان لا يدري لم يرثها شيئاً.
قلت لإسحاق: فإن مات الزوج قبل أن يقرع بينهما؟ قال: نصف الربع، أو نصف الثمن بينهما.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن فيمن طلق امرأة من نسائه لا يعلم أيتهن هي. قال: يقرع بينهن. فيمن كان','1','460',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (290,'له نسوة فقال: امرأته طالق. قال: هي أيتهن شاء إلا أن يكون نوى واحدة /٥٨/ بعينها.
وفيمن قال لامرأته: يا فلانة- باسم امرأة له أخرى - أنت طالق. قال: طلقت التي سمى ونوى.
حدثنا أبو معن قال: ثنا روح قال: حدثنا أشعث، عن الحسن في رجل له نسوة فحلفه السلطان فقال: امرأته طالق. ولم يسم منهن شيئًا. فقال: إن كان نوى شيئًا فهو ما نوى، وإن لم يكن نوى يختر أيتهن شاء فأوقع عليها الطلاق، فإن كان طلق تطليقة ثم نوى فلم يدر أيتهن هي قال: يقع عليهن تطليقة تطليقة.
حدثنا أبو معن قال: ثنا روح قال: ثنا أشعث، عن الحسن في رجل طلق إحدى نسائه لا يدري أيتهن طلق. قال: يطلق أيتهن شاء. قال الأشعث: يطلقهن جميعاً.','1','461',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (291,'حدثنا محمد بن الوزير قال: حدثنا مروان بن محمد قال: ثنا سليمان بن موسى قال: ثنا ابن جريج، عن خلاد بن عطاء أن ابن عباس سُئلَ، عن رجل له أربع نسوة فطلق إحداهن لا يدري أيتهن هي. فقال ابن عباس: إن كنت علمتها ثم نسيتها فإنهن يشتركن في الطلاق كما يشتركن في الميراث، وإن كنت لم تنو واحدة منهن فاختر أيتهن شئت. قال: مروان: وذلك الأمر، عندنا.
حدثنا أبو معن قال: ثنا روح، قال: ثنا ابن جريج قال: قال عطاء: إن حلف رجل على امرأته ألا تخرج فخرجت امرأة له أخرى فقِيل له: هذه امرأتك. فحسبها الأخرى فطلقها ثلاثًا. قال: ليس بشيء.','1','462',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (292,'حدثنا سعيد بن منصور قال: ثنا هشيم قال: ثنا إسماعيل بن سالم، عن الشعبي في رجل، عنده أربع نسوة فطلق واحدة، ثم تزوج خامسة، ثم مات ولا يدري أيتهن طلق. قال: للخامسة ربع الميراث وللأربع ثلاث أرباع الميراث بينهن
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: حدثنا سعيد، عن قتادة،، عن سعيد بن المسيب أنه قال في رجل كان له أربع نسوة فطلق إحداهن ثم تزوج أخرى، ثم مات ولا يدري أيتهن طلق. قال: يقرع بينهن بالسهام فتلغى إحداهن ويدخل الخامسة.','1','463',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (293,'<span data-type="title" id=toc-112>٣٣ - باب: من قال: بينكن تطليقة لأربع نسوة</span>
قلت لأحمد: فإن كان له أربع نسوة قال: بينكن تطليقة. قال: يقع على كل واحدة تطليقة. قلت: تذهب إلى الحديث؟ قال: نعم، يروى عن الحسن أنه قال: لا يكون بعض تطليقة نصف أو ربع، ولكنه تطليقة على كل واحدة.
حدثنا أبو عمر الحوطي قال: ثنا مبارك بن فضالة، عن الحسن أنه قال: إذا قال الرجل لامرأته: أنت طالق نصف تطليقة فهي تطليقة /٥٩/ فإذا زاد على واحدة فهما ثنتان.','1','464',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (294,'حدثنا محمد بن قدامة قال: حدثنا جرير، عن مغيرة، عن الحارث قال: إذا قال الرجل لأربع نسوة: قد طلقتكن ثلاث تطليقات. بانت كل واحدة منهن بثلاث تطليقات.
حدثنا عمرو بن عثمان قال: ثنا الوليد بن مسلم، عن الأوزاعي قال: أخبرني سليم بن حبيب المحاربي أن عمر بن عبد العزيز قال: إذا رفع إليك رجل طلق امرأته نصف تطليقة فاجبرها له، واجعلها تطليقة.','1','465',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (295,'<span data-type="title" id=toc-113>٣٤ - باب:</span>
سألت أحمد قلت: رجل قال لامرأته: أنت طالق ثلاثًا إن دخلت هذه الدار. فطلقها تطليقة وبانت منه، ثم دخلت الدار؟ قال: لا يقع عليها حينئذ طلاق، لأنها دخلت وليست هي امرأته، ولكن إذا رجعت إليه رجع عليه اليمين، وهو على يمينه. قلت: فإنه إنما أراد هذه المرأة. قال: لا أدري ومرض فيه.
قلت: فإنه حلف بطلاق امرأته ثلاثًا أن لا يضرب غلامه، فطلقها تطليقة، ثم ضربه؟ فقال فيه نحو ذلك، وقال: إنما هو ما أراد هو نيته.','1','466',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (296,'وسألتُ إسحاقَ قلتُ: الرجل قال: إن كلمت فلانًا ففلان- مملوكه- حر. فباع المملوك ثم كلم الرجل الذي حلف أن لا يكلمه. قال: ليس عليه شيء إذا باعه بيعًا باتاً. حدثنا محمود بن خالد قال: ثنا عمر بن عبد الواحد قال: سألت الأوزاعي، عن رجل جعل امرأته طالقاً، وكل مملوك له حر إن كلم أخاه، فطلق امرأته، وأعتق مماليكه، ثم كلم أخاه. قال: كان الزهري يلزمه الطلاق، وأما نحن فإنا نرى أنه إذا طلقها ثم تركها حتى انقضت عدتها، ثم كلم أخاه، ثم خطبها، فتزوجها فلا شيء عليه.
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: إن دخلت هذه الدار فأنت طالق ثلاثًا. فدخلت الدار، وحنث الزوج، فتزوجت زوجًا غيره فطلقها، ثم راجعها الأول هل تدخل الدار؟ قال: إن نوى ما دامت في ملكه - يعني هذه الدار - فبانت منه، ثم تزوجت بعد، ثم دخلت الدار لا يحنث إلا أن يكون نوى إن دخلت- يعني: ما دخلتِ في ملكي - وأما قوله: إن دخلت. فهو على مرة إلا أن ينوي بقوله: إن دخلت كلما دخلت.','1','467',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (297,'حدثنا محمد بن نصر قال: ثنا حسان في رجل قال لامرأته: إن دخلت دار فلان فأنت طالق ثلاثًا. فطلقها تطليقة، فلما انقضت العدة تزوجها وطلقها ثلاثًا، فتزوجها رجل فمات عنها، أو طلقها فتزوجها /٦٠/ الزوج الأول هل نقض الطلاق والتزويج اليمين؟ قال حسان: حدثنا سفيان، عن ابن أبي ليلى أن اليمين ثابتة إن دخلت وهي في ملكه بانت منه.
حدثنا محمود قال: حدثنا عمر بن عبد الواحد قال: سمعت الأوزاعي يقول في رجل قال لامرأته: إن دخلت دار فلان فأنت طالق البتة. ثم طلقها واحدة، فتركها حتى انقضت عدتها، ثم تزوجها فدخلت دار فلان. قال: متى ما دخلت وقع عليها الطلاق.
قلت لإسحاق: رجل قال: إن كلمتُ فلانًا فامرأته طالق. فمر الرجل فسلم عليه فرد عليه هذا الحالف السلام. فقال إسحاق: ما لم يعلم أنه في القوم، أو لم يرده بعينه لم يحنث، فإن تعمد لذلك، أو نواه، أو أراده فإن السلام كلام.','1','468',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (298,'<span data-type="title" id=toc-114>٣٥ - باب: إذا حلف بطلاق امرأته ألا يفعل كذا ففعله ناسياً</span>
سألت أحمد بن حنبل قلت: فإن حلف بطلاق امرأته أن لا يدخل هذه الدار، فدخلها ناسيًا. قال: أرجو أن لا يكون عليه الكفارة. يذهب أبو عبد الله في النسيان إلى الطلاق وحده.
وسُئلَ إسحاق، عن رجل قال لرجل: إن شربتُ أو أكلتُ من دارك فامرأته طالق ثلاثًا. فمر بباب: الدار فشرب ناسيا. قال: لا يقع عليه','1','469',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (299,'الطلاق إذا كانت يمينه معناه على التعمد.
وسمعت إسحاق مرة أخرى يقول في الرجل يحلف بالطلاق أن لا يدخل هذه الدار، فدخلها ناسيًا قال: اختلفوا فيها. قلت أخبرني عن مذهبك. قال: أنا أميل إلى الرخصة إذا دخلها ناسيًا. قلت: يستحلف أنه دخلها وهو ناسٍ ليمينه؟ قال: لا أقل من ذلك.
وسُئلَ أيضاً، عن رجل قال لامرأته: إن لبست من غزلك فأنت طالق. فانتبه من نومه وحضرت الصلاة فأخذ إزار المرأة فلبسه وخرج إلى الصلاة وهو لا يعلم. قال: لا يقع الطلاق؛ لأنه لم يتعمد لذلك، ولكنه نسي.
وسُئلَ إسحاق مرة أخرى، عن رجل حلف بطلاق امرأته ثلاثًا إن لبس من غزل امرأته فأخذ سراويل ابنه وهو من غزل امرأته ناسيًا ليمينه فلبسها، وصلى فيها. قال: لا يحنث- يعني إذا نسي-.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أنبأ منصور وعوف، عن الحسن قال: إن الله تجاوز لهذه الأمة عن الخطأ والنسيان وما أكرهوا عليه.','1','470',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (300,'حدثنا سعيد قال: حدثنا سفيان، عن سلمة بن نبيط قال: سمعت الضحاك بن مزاحم يقول جاء بها جبريل ومعه من /٦١/ الملائكة ما شاء الله {آمَنَ الرَّسُولُ بِمَا أُنزِلَ إِلَيْهِ مِن رَّبِّهِ} [البقرة: ٢٨٥] إلى قوله: {رَبَّنَا لاَ تُؤَاخِذْنَا إِن نَّسِينَا أَوْ أَخْطَأْنَا} [البقرة: ٢٨٦] قال: ذلك لك.
حدثنا عمرو بن عثمان قال: حدثنا بقية بن الوليد، عن علي الهمداني، عن أبي جمرة، عن ابن عباس، عن النبي - صلى الله عليه وسلم - قال: ((إن الله تجاوز لأمتي، عن الخطأ والنسيان)).','1','471',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (301,'حدثنا محمد بن مصفا، قال: حدثنا الوليد بن مسلم، عن الأوزاعي، عن عطاء، عن ابن عباس قال: قال رسول الله - صلى الله عليه وسلم -: ((تجاوز الله لي، عن أمتي الخطأ والنسيان وما استكرهوا عليه)).','1','472',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (302,'<span data-type="title" id=toc-115>٣٦ - باب: من حلف أن لا يدخل دارًا فحمل كرهاً</span>
سألتُ إسحاقَ قلتُ: رجل حلف بالطلاق أن لا يدخل داراً، فحمله قوم كرها فادخلوه الدار. قال إسحاق: إن كان نوى أن لا يدخل طوعًا من ذات نفسه فأُدخِلَ كرهًا وهو يقدر على أن يمتنع فتركهم حتى حملوه كرها لما هوى ذلك فهو كالداخل من ذات نفسه.','1','473',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (303,'<span data-type="title" id=toc-116>٣٧ - باب: من حلف بالطلاق على شيء ولم يدر أهو كما قال</span>
قلت لأحمد: رجل حلف بطلاق امرأته أن هذا الشيء - أراد كذا وكذا- فلم يدر أهو كما قال أو لا، فسكت فيها وذكر حديث الشعبي في الرجلين اللذين حلفا على الطير وقال: لا يؤمن أن يكون أحدهما صادقًا. قلت: فأحب اليك أن لا تقول فيها شيئاً؟ قال: نعم. وأمسك عنها.
وسألتُ إسحاقَ قلتُ: رجلان يستأجرا فقال أحدهما لصاحبه: امرأته طالق ثلاثًا إن لم أكن خيرًا منك، أو أعز منك، أو نحو هذا، وكذلك قال الآخر. قال: يقفان حتى يُعرف ذلك. وقال: هذا مثل الرجلين اللذين حلفا على الطير. قلت: وكذلك الطير إذا حلف؟ قال: نعم يوقفان - يعني يقفان-، عن امرأتيهما. قلت: فإن ماتا يتوارثان؟ قال: نعم، لأنه لا يعرف.
وسئلَ إسحاق أيضاً، عن رجل قال لامرأته: إن لم تكوني جنبًا فأنت طالق. قال: إذا تحقق أنها ليست جنبًا وقع. ثم ذكر حديث الشعبي في الطير، وحلف أحدهما أن هذا غراب وحلف الآخر أنه غير ذلك، ولم يدر ما','1','474',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (304,'هو، فذهب في ذلك أيضًا إلى التحقيق، عنده، وقال: يعتزلان امرأتيهما. قلت: ولا يتوارثان؟ قال: إذا كان الأغلب على ذلك لا يتوارثان.
حدثنا أبو معن، قال: حدثنا قتيبة، قال: حدثنا عمر بن بشير قال: سُئلَ الشعبي، عن رجلين كانا في مجلس فمر بهما طائر متحلق فقال: أحدهما إن لم يكن حمامًا فامرأته /٦٢/ طالق. وقال الآخر: إن لم يكن غرابًا فامرأته طالق. فذهب الطير لا يدرى ما هو. قال الشعبي: يعتزلان نسائهما.
حدثنا عمرو بن عثمان قال: حدثنا بقية بن الوليد قال: وحدثني عتبة بن أبي حكيم، عن مكحول أنه قال في الرجلين اللذين اختلفا في الحمامة واليمامة، فطار قبل أن يستبين لهما ما هي أنه حمل الطلاق عليهما جميعا. قال أبو محمد: اليمامة حمامة مكة.','1','475',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (305,'قال نصر: الحمام ما يلقط، واليمام ما لا يلقط.
حدثنا أبو معن قال: حدثنا عبد الله بن عمرو قال: ثنا خالد بن وردان قال: سألت عطاء، عن رجل قال: امرأته طالق إن لم يكن كذا وكذا. وقال الآخر: امرأته طالق إن لم يكن كذا وكذا. قال عطاء: يدينان.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن وسُئلَ، عن رجلين قال كل واحد منهما لصاحبه: امرأته طالق إن لم تكن أنت أحسد مني. قال: هو بهما.
حدثنا محمد بن بشار قال: حدثنا عبد الرحمن قال: سألت سفيان، عن رجل قال لامرأته: أنت طالق إن مسستك. فمسها بعود. قال: له نيته.','1','476',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (306,'<span data-type="title" id=toc-117>٣٨ - باب: إذا أشار بالطلاق</span>
سألت أحمد قلت: رجل أراد طلاق امرأته ثلاثًا، فقام إليه رجل فوضع يده على فيه، فأشار بأصابعه الثلاث. قال: إذا لم يتكلم بلسانه فأرجو أن لا يدخل عليه. قلت: فإن عقد قلبه على الطلاق؟ قال: وإن عقد عليه قلبه، أرأيت لو طلق في نفسه أكان يكون طلاقًا؟!
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: أنت. وأراد أن يطلقها، فقام رجل فأخذ يده على فيه، فأومأ بيده ثلاثًا بأصابعه الثلاث ولم يتكلم بلسانه. قال: يحلف أنه لم يرد طلاقًا، وليس عليه شيء إذا لم ينطق به.
حدثنا يحيى بن عبد الحميد قال: حدثنا قيس بن أبي الهيثم قال: سألت إبراهيم، عن رجل قال لامرأته: أنت أنت. فقام إليه ابنه فأخذ بفيه فجعل','1','477',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (307,'يقول بيده هكذا بثلاث أصابعه- يعني ثلاثًا- فقال إبراهيم: ليس بشيء حتى يتكلم به.
حدثنا محمد بن آدم المصيصي قال: حدثنا سيف بن محمد، عن بيان، عن الشعبي في رجل أشار بثلاث أصابع إلى امرأته. فقال: أنت هكذا. قال الشعبي: إن نوى طلاقًا فهو ما نوى.
حدثنا يحيى بن عبد الحميد قال: ثنا عبد الله بن محمد الفروي، عن إسحاق بن عبد الله بن أبي فروة قال: قال عثمان: ليس الطلاق على /٦٣/ ما أضمرت ولكن الطلاق على ما تكلمت.','1','478',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (308,'حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن زياد بن عبيد الله قال: قلت للحسن: رجل حدث نفسه بشيء ظن أنه قد فارق أهله في ذلك. قال الحسن: ليس بشيء إلا أن يكون تكلم به كلاما. وسألتُ قتادة فقال: أن كان أسمع أذنيه.
حدثنا محمود قال: ثنا عمر قال: سمعت الأوزاعي يقول في رجل قِيل له: أطلقت امرأتك؟ فأومأ برأسه. قِيل له كم؟ فأمسك ثلاثًا. قال: لاشيء إلا أن يتكلم.','1','479',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (309,'<span data-type="title" id=toc-118>٣٩ - باب: طلاق الأخرس</span>
سألت أحمد قلت: الأخرس إذا كتب طلاق امرأته على الأرض. قال: إذا علم منه أنه يريد الطلاق فهو طلاق.
حدثنا محمد بن آدم قال: ثنا سيف بن محمد، عن ليث، عن مجاهد قال: طلاق الأخرس إذا كتب.','1','480',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (310,'<span data-type="title" id=toc-119>٤٠ - باب: إذا طلق امرأته ثم أنكر</span>
قلت لأحمد: امرأة طلقها زوجها فأنكر وليس لها بينة. قال: إذا علمت ذلك فإنها لا تقيم، عنده. قِيل: فلها أن تتزوج؟ قال: لا، لا تتزوج، لأنه يقول أنها امرأته.
قِيل: فتأخذ ميراثه؟ قال: لا يحل لها أن تأخذ من ميراثه شيئاً؛ لأنها قد علمت أنه قد طلقها.
حدثنا علي بن عثمان قال: ثنا حماد، عن أبي حمزة، عن إبراهيم وحميد، عن الحسن أنهما قالا: في رجل طلق امرأته ثلاثًا، ثم جحدها وليست لها بينة.','1','481',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (311,'قالا: ترفعه إلى السلطان، فإن استحلفه صبرت وأقامت، وليس عليها حرج إنما الحرج على الزوج.','1','482',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (312,'<span data-type="title" id=toc-120>٤١ - باب: إذا قال: أنت طالق إلى سنة</span>
سألت أحمد قلت: الرجل يقول لامرأته: أنت طالق إلى سنة. قال: هو كما قال: إذا جاءت السنة فهي طالق. قلت: فإن مات أحدهما قبل السنة هل يتوارثان؟ قال: نعم يتوارثان. قال: وكان الحسن وسعيد بن المسيب والزهري يقولون: إذا قال: أنت طالق إلى سنة، أو إلى شهر. فإنها تطلق من ساعتها. وهو قول أهل المدينة.
قِيل: تذهب إليه؟ قال: لا. ولم يعجبه.
وسألتُ إسحاقَ قلتُ: رجل قال لامرأته: أنت طالق إلى شهر أو إلى سنة. قال: هو إلى سنة إذا جاءت السنة فهى طالق إذاوقت وقتًا، فإذا جاء الوقت فهى طالق.','1','483',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (313,'حدثنا إسحاق، قال: حدثنا الفضل بن موسى، عن حماد بن سلمة، عن عبد الكريم، عن عكرمة، عن ابن عباس قال: إذا قال: أنت طالق إلى سنة. فإذا جاءت السنة فهي طالق.
قال إسحاق: وأهل المدينة يقولون: يقع الطلاق من ساعته، وكذلك يقولون: إذا قال: أنت طالق إن شاء الله. قال: يقع الطلاق وليس له استناه. قلت /٦٤/ فما تقول أنت في الاستثناء في الطلاق؟ قال: له استثناه. ولا يقع الطلاق.
وسألت إسحاق مرة أخرى قلت: رجل قال لامرأته: أنت طالق إلى شهر. فماتت قبل الشهر هل يرثها؟ قال: يتوارثان.
حدثنا علي بن عثمان قال: ثنا حماد، عن قتادة، عن الحسن وسعيد بن المسيب، ويحيى بن سعيد، عن سعيد بن المسيب أنهما قالا: إذا قال الرجل لامرأته:','1','484',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (314,'أنت طالق إلى سنة. قال: هي طالق ساعتئذٍ.
حدثنا محمد بن رافع قال: حدثنا ابن أبي فديك قال: أخبرنا ابن أبي ذئب، عن ابن شهاب أنه قال: في رجل قال لامرأته: أنت طالق إذا هل الهلال. قال: هي طالق الآن.
وسألت إسحاق مرة أخرى قلت: رجل قال لامرأته: إذا جاءت السنة فأنت طالق. قال: تطلق إذا جاءت السنة. قلت: فإن مات أحدهما قبل السنة هل يتوارثان؟ قال: نعم.
حدثنا أبو معن قال: ثنا حماد بن مسعدة قال: قال أشعث بن عبد الملك: لا تطلق حتى يجيء ما قال.
حدثنا محمد بن مصفا قال: حدثنا بقية بن الوليد، عن سعد بن إبراهيم،','1','485',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (315,'عن جده، عن الحكم، عن مقسم، عن ابن عباس في رجل قال لامرأته: إذا جاء رمضان فأنت طالق ثلاثًا. وبينه وبين رمضان ستة أشهر، فندم. قال ابن عباس: تطلق واحدة فتنقضي عدتها قبل أن يجيء رمضان، فإذا مضى خطبها إن شاءت.
قلت لإسحاق: رجل قال لامرأته: أنت طالق في رمضان. ولم ينو أي رمضان هو. قال: سئلَ ما نوى به رمضانيًا هذا أو غيره، وهو نيته لابد من إرادته.
حدثنا عمرو بن عثمان قال: حدثنا بقية بن الوليد، قال: سألت الزبيدي،','1','486',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (316,'عن قول الزهري: كيف كان يقول في الرجل يقول لامرأته: إذا كان هلال شهر رمضان فأنت طالق؟ قال: كان يلحق به الطلاق يوم تكلم به. قال الزبيدي: فكلمته في ذلك، فقلت: انقطع عنها الميراث قبل أن يأتي الأجل وكيف بذلك؟ قال: فترك قوله في ذلك فكنت نقلته عنه.
قال الزبيدي: فكان الزهري يقول: نرى أن يوقف عنها ويتوارثان حتى يأتي ذلك الوقت الذي وقت إليه إن مات أحدهما قبل أن يبلغه توارثا.
قلت للزبيدي: فكيف تقول أنت في هذا؟ قال: أقول: إنها امرأته يصيبها حتى يبلغ ذلك الوقت، فإذا بلغ ذلك الوقت فهي طالق.
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: أنت طالق قبل موتك بشهر. فعاشت /٦٥/ من يوم قال لها ذلك عشرة أيام، ثم ماتت. قال: ليس بينهما عصمة، لأن قبل أن يتكلم به كان طلاقًا. قلت: وقبل أن يتكلم به يكون طلاقاً؟ قال: نعم.
حدثنا عمرو بن عثمان، قال: ثنا بقية، قال: سألت الزبيدي، عن رجل قال لامرأته: أنت طالق قبل موتي بشهر؟ قال الزبيدي: أما أنا فأقول فيها وأرى ذلك أن يوقف عنها فلا يصل إليها أبدًا حتى تصيبه ضرورة فيطلق أو يموت أحدهما، ولا أمكنه من فرج أخاف أن يطأها حراما.
قلت للزبيدي: رجل قال لعبده: أنت حر قبل موتي بسنة، فافترى العبد على رجل فضربه الإمام أربعين، ثم مات السيد بعد ذلك بشهر. قال: أضربه أربعين أخرى.','1','487',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (317,'حدثنا أبو معن قال: حدثنا مؤمل، حدثنا سفيان، عن ابن جريج، عن عطاء في رجل قال لامرأته: طلقتك منذ كذا وكذا. قالت: فإني حضت منذ ذاك ثلاث حيض. قال: تعتد من يوم يخبرها، وترثه إن مات ولا يرثها.
قلت لإسحاق: رجل قال لامرأته: أنت طالق إلى حين. قال: يكون الحين ستة أشهر، ويكون سنة، ويكون يومًا إلى الليل.
حدثنا سعيد بن منصور قال: حدثنا خالد بن عبد الله قال: أنبأ الشيباني، عن عكرمة قال: الحين ستة أشهر.','1','488',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (318,'حدثنا سعيد قال: ثنا أبو معاوية، عن الأعمش، عن أبي ظبيان، عن ابن عباس قال: الحين قد يكون غدوة وعشية.
قلت لإسحاق: رجل قال لامرأته: أنت طالق اليوم، أو غدًا قال: لا تسألني عن هذه المسائل. قلت: لا أسألك.','1','489',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (319,'<span data-type="title" id=toc-121>٤٢ - باب: من باع امرأته </span>...
سألت أحمد قلت: رجل باع امرأته؟ قال: لا يدخل عليه الطلاق، ولكن يؤدب. قلت فإن باع ولده؟ قال: كذلك. قلت: ويرجع عليه بالثمن؟ قال: شديدًا.
حدثنا المسيب قال: حدثنا ابن مبارك، عن سعيد، عن قتادة، عن الحسن في الذي يبيع امرأته. قال: يعاقبان وينكلان.','2','490',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (320,'<span data-type="title" id=toc-122>٤٣ - باب: إذا قال لامرأته أنت طالق إذا حبلت</span>
قلت لأحمد: شيء يروى عن أهل المدينة في الرجل يقول لامرأته: أنت طالق إذا حبلت. فذهب إلى أنه يدعها حتى تحيض فإذا طهرت جامعها. قلت: إلى الحيضة الثانية؟ قال: لا، ولكن يجامعها مرة واحدة، ويواقعها وقعة واحدة، ثم يدعها أيضًا حتى تحيض يواقعها في كل طهر مرة، وهو قول أهل المدينة. وقال: لم يوقع /٦٦/ أهل المدينة الطلاق في هذا؛ لأن الحبل قد يكون ولا يكون، ولو كان هذا شيء يكون بتة كان يقع الطلاق ساعة يقوله في قولهم لو قال: أنت طالق إلى شهر. كان يقع الطلاق في قولهم؛ لأن الشهر لا بد أن يجيء.','2','491',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (321,'سألتُ إسحاقَ قلتُ: رجل قال لامرأته: إذا حبلت فأنت طالق. قال: يطؤها ما لم يتبين حبلها. قلت: فمن يقول يطؤها، عند كل طهر مرة؟ فلم يذهب إلى ذلك.
حدثنا أبو الأزهر قال: حدثنا الوليد بن الوليد الدمشقي، عن الأوزاعي وسئل عن رجل قال لامرأته: إن لم تحبلي فأنت طالق. وقد أيست من المحيض. قال: هي طالق حين تكلم.
قال: وسئل عن رجل قال لامرأته: إن حبلت فأنت طالق. قال: يطؤها، عند كل طهر، فإن حبلت طلقت ولا يزال على ذلك حتى تأيس من المحيض.
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ثنا أشعث، عن الحسن فيمن قال: أنت طالق إن لم أحبلك. قال: يغشاها حتى تأيس من الحبل، وإن مات أحدهما قبل أن تحبل فلا ميراث للآخر.
وفيمن قال لامرأته: إذا حبلت فأنت طالق. قال: يغشاها إذا اغتسلت من المحيض، ثم يمسك عنها إلى مثل ذلك.','2','492',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (322,'وقال محمد: يغشاها حتى تحبل.
قلت لإسحاق: رجل قال لامرأته: إذا حضت فأنت طالق. فولدت هل يكون دم النفاس حيضاً؟ قال: تطلق، لأن دم النفاس حيض إلا أن يقصد حين يحلف قصد الحيض، وذكر حديث عائشة أن النبي - صلى الله عليه وسلم - قال لها في الحج: ((ما لك أنفست؟)).
قلت لإسحاق: رجل قال لامرأته: إن ولدت ولدًا فأنت طالق ثلاثًا. فأسقطت سقطًا قد استبان خلقه. قال: هو ولد ويقع الطلاق.','2','493',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (323,'<span data-type="title" id=toc-123>٤٤ - باب: إذا قال يا مطلقة</span>
سألت أحمد قلت: رجل قال لامرأته: يا مطلقة. قال: إن كان أراد من الزوج الأول رجوت، وإن كان يريد منه خفت عليه، أو كما قال.','2','494',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (324,'حدثنا عبيد الله بن معاذ قال: ثنا أبي، ثنا أشعث، عن الحسن وسأله رجل فقال: مررت بصبيان وهم يقولون: فلانة مطلقة وبرجل معلقة باسم امرأته. فقلت مثل قولهم. قال: أردت طلاق امرأتك؟ قال: لا. قال: هذا ليس بشيء ائت امرأتك.','2','495',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (325,'<span data-type="title" id=toc-124>٤٥ - باب: إذا قِيل له: ألك امرأة؟ قال: لا</span>.
قلت لأحمد: رجل قِيل له: ألك امرأة؟ قال: لا. وله امرأة. قال: إن كان أراد الكذب رجوت أن لا يدخل عليه.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أخبرنا /٦٧/ يونس، عن الحسن، وأخبرنا مغيرة، عن إبراهيم، وأخبرنا سيار عن الشعبي في رجل قِيل له: ألك امرأة؟ فيقول: لا. قالوا: كذبة كذبها.','2','496',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (326,'<span data-type="title" id=toc-125>٤٦ - باب: إذا قِيل له أطلقت امرأتك؟ فقال: نعم</span>.
قلت لأحمد: رجل قِيل له: أطلقت امرأتك؟ فقال: نعم. ولم يكن طلق. قال: يلزمه الطلاق. قلت: تطليقة؟ قال: نعم.
وسألت إسحاق قلت: رجل قِيل له: أطلقت امرأتك؟ فقال: نعم. قال: كلما نوى بإجابته طلاقًا وقع الطلاق، وإن لم يرد ذلك فلا يقع.
قلت لإسحاق: فرجل قِيل له: ألك امرأة؟ فقال: لا. وله امرأة. قال: وكذلك أيضًا.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، قال: سمعت سفيان يقول في رجل قِيل له: أطلقت امرأتك عام الأول؟ قال: نعم. قال: أما في القضاء فيلزمه، وأما فيما بينه وبين الله فكذبة.','2','497',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (327,'حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ثنا أشعث، عن الحسن فيمن طلق واحدة فسئل مرارًا أطلقت فلانة؟ فيقول: نعم. قال: هي تلك الواحدة وصدق.','2','498',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (328,'<span data-type="title" id=toc-126>٤٧ - باب: إذا قال لامرأته ما أنت امرأتي</span>
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: ما أنت بامرأتي البتة، أو قال لها: قد كنت طلقتك منذ سنة. ولم يكن طلقها، ولكنه كذب؟ قال: إذا أراد الكذب لم تحرم عليه امرأته ويسعه أن يمسكها فيما بينه وبين الله، وأما المرأة فتستحلفه وتستعدي عليه.
وسألت إسحاق مرة أخرى قلت: رجل قال لامرأته: لست لي بامرأة. وشهدت الشهود عليه بهذا القول. قال: نيته. قلت: فأنه مات قبل أن تعرف نيته ما هي؟ قال: لا يتوارثان.
وسألت إسحاق مرة أخرى قلت: رجل قال لامرأته: قد كنت طلقتك. ولم يكن فعل. قال: إذا أراد به الطلاق وقع الطلاق، وإذا أراد تعمد الكذب ليخوفها وما أشبه ذلك لم يقع.
حدثنا عبيد الله بن معاذ، قال: ثنا أبي، قال: حدثنا سعيد، عن قتادة، عن سعيد بن أبي الحسن، عن الحكم بن أيوب، عن الحجاج بن يوسف، عن أبيه يوسف','2','499',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (329,'في رجل قال لامرأته: ما أنت لي بامرأة. ما أنت لي بامرأة. ما أنت لي بامرأة. قضاها تطليقة. قال: قتادة: وسألتُ سعيد بن المسيب، عن ذلك فقال: ما أبعد.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي، ثنا /٦٨/ أشعث، عن الحسن فيمن قال لامرأته: لست لي بامرأة، أو لست لك بزوج. قال: واحدة، وهو أحق بها.','2','500',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (330,'حدثنا المسيب قال: ثنا ابن مبارك، عن سعيد بن أبي عروبة، عن قتادة، عن الحسن وعطاء في رجل قال لامرأته: ما أنت بامرأتي. قالا: فإنها كذبة. وقال قتادة: إذا واجهها وأراد الطلاق فهى واحدة.','2','501',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (331,'<span data-type="title" id=toc-127>٤٨ - باب: من قال: حلف ثم قال: كذبت</span>
قلت لإسحاق: فرجل قال لامرأته: إني قد كنت حلفت بطلاقك ثلاثًا إن دخلت هذه الدار. فلما دخلت وعلم الزوج أنه قد حنث قال لها: إني كذبت, إنما كنت أخوفك ولم أحلف. ما القول في ذلك؟ قال: ليس قوله بشيء إذا شهدت الشهود أنه قد أقر بالطلاق فليس إنكاره بشيء.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: ثنا أشعث، عن الحسن في رجل قال: امرأته طالق إن فعلت كذا وكذا، أو إن لم أفعل كذا وكذا. ثم قال: قد أبررت يميني ولا يدري أصدق أم كذب. قال: يدين ذلك ويصدق.','2','502',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (332,'<span data-type="title" id=toc-128>٤٩ - باب: إذا قال: إذا طلقتك فأنت طالق</span>
قلت لإسحاق: فرجل قال لامرأته: إذا طلقتك فأنت طالق. قال: نيته إن أراد أنه إذا طلقها فهي طالق وإرادته الأولى فهي واحدة، وإذا أراد إذا طلقتك فأنت طالق أخرى فهما ثنتان.','2','503',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (333,'<span data-type="title" id=toc-129>٥٠ - باب: إذا قال: أنت طالق إن شئت</span>
سألت أحمد قلت: رجل قال لامرأته: أنت طالق إن شئت قالت: قد شئت؟
قال: هي طالق.
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: إذا شئت فأنت طالق. فلما كان بعد ذلك بشهر قالت: قد شئت الطلاق. قال: إذا شاءت فهي طالق؛ لأن الأمر إليها. قال: أجعله إلى الأبد إذا لم يؤقت.
حدثنا عبيد الله بن معاذ قال: ثنا أبي، ثنا أشعث، عن الحسن فيمن قال لامرأته: أنت طالق إن شئت. قال: إن قضت شيئًا فهو ما قضت، فإن قالت: لا أشاء شيئاً. فليس بشيء.','2','504',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (334,'<span data-type="title" id=toc-130>٥١ - باب: من حلف بالطلاق أن لا يمس الدراهم فمس الكيس</span>
سُئلَ إسحاق، عن رجل حلف بطلاق امرأته ثلاثًا أن لا يمس هذه الدراهم فمس الكيس، فزبر الرجل وقال: مسألة حيلة.
حدثنا أبو هشام قال: ثنا حسان في رجل قال لامرأته: إن دخلت دار فلان فأنت طالق ثلاثًا. فأدخلت رجلها ذلك البيت قال: قال سفيان: لا يقع الطلاق، إنها لم تدخل /٦٩/.
وقال: سفيان في رجل قال لجاريته: إن لم أفتضها الليلة فامرأته طالق ثلاثًا. فلما وطئها لم يجدها عذراء. قال: إن لم يجدها عذراء فقد وقع الطلاق.
قال: وحدثنا حسان في رجل قال لامرأته: إن صمتُ رمضان بهذه الأرض فأنت طالق ثلاثًا وبينه وبين رمضان خمسة أيام خرج من تلك الأرض إلى أرض أخرى فصام بها، ثم رجع إلى أرضه قبل أن ينسلخ رمضان فصام بها بقيته. قال: قال سفيان: يُسأل، عن نيته فإن كان نوى رمضان كله لم يكن طلاقًا، وإن كان نوى شيئًا من رمضان فهي طالق.','2','505',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (335,'حدثنا يحيى قال: حدثنا شريك، عن ربيع بن عبد الرحمن النخعي ابن وبرة قال: كان لي على رجل دراهم فجعل امرأته طالقًا إن فارقه حتى يستوفي منه، فأعطاني فوجدت فيه درهما زائفًا أو ستوقًا، فسألت إبراهيم فقال: مر امرأتك فلتعتد.
قِيل لإسحاق: رجل أخذت له امرأته عشرة دراهم فحلف بطلاقها لتردن درهمين، فأمرت أختها فردت الدرهمين قال: إذ لم تكن نيته أنها تعطيه من يديها إلى يديه لم يحنث.','2','506',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (336,'<span data-type="title" id=toc-131>٥٢ - باب: من طلق امرأته في نفسه</span>
سألتُ إسحاقَ قلتُ: رجل طلق امرأته في نفسه ثلاثًا من غير أن يلفظ به. قال: ليس بشيء حتى يلفظ به، أو يتكلم به بكلام يشبه الطلاق فيقول: نويت به الطلاق. فأما الطلاق في القلب والتفكر فليس بشيء.
حدثنا محمد بن آدم المصيصي قال: حدثنا وكيع بن الجراح، عن سفيان، عن ابن جريج، عن عطاء، وسفيان، عن ابن جريج، عن عمرو بن دينار، عن أبي الشعثاء، قالا: إذا طلق في نفسه فليس بشيء حتى يتكلم.','2','507',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (337,'<span data-type="title" id=toc-132>٥٣ - باب: إذا قال: إن خرجت من الدار إلا بإذني فأنت طالق</span>
قلت لإسحاق: رجل قال لامرأته: إن خرجت من الدار إلا بإذني فأنت طالق. فقال لها: قد أذنت لك إلى سنة. قال: هو على نيته. قال: تجيء النية على كل مرة، وتجيء النية على مرة فهو نيته.
حدثنا محمود قال: ثنا عمر قال: سمعت الأوزاعي يقول في رجل قال لامرأته: إن خرجت حتى آذن لك فأنت طالق البتة. فحضرها أمر علمت أنه لا يكره خروجها إليه، فلبست ثيابها وهو ينظر، ثم خرجت فلم يغيِّر عليها، قال: لا طلاق عليها.
وعن الأوزاعي، عن رجل قال لامرأته: إن خرجت حتى آمرك، أو حتى أقول لك فأنت طالق. فغاب عنها ثم كتب إليها يأمرها بالخروج فخرجت. قال: لا يلحقها الطلاق في خروجها /٧٠/.
وسمعت إسحاق أيضًا وسأله رجل فقال: حلفت بالطلاق أن لا أواكل فلاناً، ولا أشار به، ولا أصالحه فوقع بيني وبينه فشتمته وواثبته حتى اعتنقنا ما تقول في ذلك؟ قال: على ما نويت.
وسألتُ إسحاقَ قلتُ: امرأة كان بينها وبين زوجها كلام فقالت للزوج: لا تبرح ولا أبرح حتى تطلقني فقال لها الزوج أبرءيني من المهر حتى أطلقك. فقالت: كل حق لي عليك فقد جعلته لك فصير أمري بيدي لا بيد غيري. فقال: أمرك بيدك. فقالت قد طلقتُ مرة. طلقتُ مرتين. طلقت ثلاثًا. طلقت أربعًا. طلقت طلقت. قال أبو يعقوب: كلما جعل أمرها بيدها في كل الطلاق فطلقت نفسها مرة ومرتين وثلاثًا فقد','2','508',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (338,'بانت منه، لأنه مع الثلاث تطليقات كان أصل الطلاق على جعل، قلت: وقد برئت من المهر؟ قال: نعم.
سمعت إسحاق وسئلَ، عن رجل قال لامرأته بالفارسية: هيشتاي. هيشتاي. هيشتاي. ولا يدري نوى واحدة أو ثلاثًا. قال: إذا قال لها ثلاث مرات بالفارسية: هيشتاي. فقد طلقت ثلاثًا.
حدثنا سعيد بن منصور، قال: نا خالد، عن مطرف، عن الشعبي قال: إذا قال العجمي: بشتم. فهو طلاق.','2','509',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (339,'<span data-type="title" id=toc-133>٥٤ - باب: إذا قال: أنت طالق كذا إلا كذا</span>
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: أنت طالق واحدة غير واحدة، أو أنت طالق واحدة إلا واحدة. قال: إرادته هو ما أراد من ذلك.
حدثنا أبو هشام قال: حدثنا حسان في رجل قال لامرأته: أنت طالق ثلاثًا إلا واحدة. قال: قال سفيان: هي طالق ثنتين هو كما قال.
قرأت على إسحاق رجل حلّفه السلطان بأيمان مختلفة إن شرب، أو سرق وأشباهها مما كرهه الله وهو لا يدري كان فيه طلاقًا أو لم يكن ولم يكن عنده يوم حلف امرأة وتزوج بعدما ركب بعض الأمور التي حلف عليها. قال أبو يعقوب: إذا لم تكن امرأة في ملكه يومئذ فله أن يتزوج بعد ولا يضره، وإن وقت وقتًا فلا يضره أيضًا.
حدثنا إسحاق قال: أنبأ أبو علقمة الفروي قال: حدثني عبد الكريم بن أبي فروة قال: حدثني عمرو بن شعيب قال: عرض عليّ أبي امرأة فقلت:','2','510',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (340,'يوم أتزوجها فهي طالق البتة، ثم بدا لي أن أتزوجها فسألت سعيد بن المسيب فقال: لي: أما علمت أن النبي - صلى الله عليه وسلم - قال: ((لا طلاق قبل نكاح)) /٧١/ ثم سألت أبا بكر بن عبد الرحمن بن الحارث بن هشام فقال لي: أما علمت أن النبي - صلى الله عليه وسلم - قال: ((لا طلاق قبل نكاح)). ثم سألت: أبان بن عثمان فقال لي: لا طلاق قبل نكاح. ولم يرفعه.','2','511',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (341,'<span data-type="title" id=toc-134>٥٥ - باب: إذا حلف على الغضب ولا يدري طلق واحدة أو ثلاثًا</span>
قرأت على إسحاق: رجل قال لامرأته: إذا دخلت دار فلان اليوم أو غدًا أو بعد غد فأنت طالق. ووقف سويعة، ثم قال في غضبه: وإلى شهر. وهو لا يدري من الغضب طلق واحدة، أو ثنتين، أو ثلاثًا، فدخلت المرأة تلك الدار بعد الخمسة عشر يوما. قال أبو يعقوب: هو على ما ظهر من الطلاق، وينظر إلى أكثر نيته، فإن استيقن أنه كان على أقل من ثلاث جاز له أن يتزوجها، فإن كان أكثر ظنه على ثلاث لم تحل له حتى تنكح زوجًا غيره.
حدثنا أبو الأزهر قال: ثنا أبو العباس الدمشقي، عن الأوزاعي وسُئلَ، عن رجل قال: والله لقد طلقت امرأتي، عند غضبة أغضبتها فلا أدري واحدة أو ثنتين، أو ثلاثًا كيف يصنع به؟ قال: تكون تطليقة، لأنه لا شك في واحدة، وأما ما شك فيه من تطليقتين فلا شيء عليه إلا أن يستيقن.','2','512',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (342,'<span data-type="title" id=toc-135>٥٦ - باب: من حلف بالطلاق على أمر ففعله ناسيًا</span>
قرأت على إسحاق: رجل حلف بالطلاق إن كلم فلاناً، فكلمه ناسيًا؟ قال إسحاق: فإن أهل العلم اختلفوا في ذلك، منهم من لا يرى إيقاع الطلاق بالنسيان، ومنهم من يوقع يقول: إنه قد كلمه وأرجو أن لا يقع إذا كانت نيته يوم حلف على التعمد؟
وقرأت على إسحاق أيضًا رجل كان بينه وبين آخر كلام وبينهما كرم ولهما في ذلك شركاء من الورثة فقال الرجل: متى ما أكلت من هذا الكرم فامرأته طالق ثلاثًا. وكان نية الرجل حين حلف أن لا يأكل عمدًا، فأكل منه ولا يعلم أن هذا العنب من هذا الكرم. قال أبو يعقوب: لا شيء عليه.
قرأت على إسحاق رجل قال لامرأته: لا تحلين لي فإني قد طلقتك منذ كذا ألف مرة. قال أبو يعقوب: لا يصدق بعد إقراره بالطلاق: أني كذبت؛ لأن قوله على نفسه من غير كره يصدق.','2','513',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (343,'<span data-type="title" id=toc-136>٥٧ - باب: الخلع</span>
قرأت على إسحاق: امرأة اشترت نفسها من زوجها على تطليقة بالنفقة والعدة، ثم حلف الرجل بطلاق هذه المرأة التي اشترت من زوجها التطليقة هل يقع عليها الطلاق /٧٢/ قال أبو يعقوب: كلما نوى أنه قد طلقها قبل أن يحلف حين بانت منه بتطليقة فلذلك حلف، لأنها ليست بامرأته فلا حنث عليه.
حدثنا علي بن عثمان قال: ثنا حماد قال: أخبرنا هشام بن أبي عبد الله، عن يحيى بن أبي كثير أن أبا سلمة بن عبد الرحمن قال: الخلع تطليقة بائنة، فإن أتبعها طلاقًا في مقعده ذلك لحقها، وإن طلقها بعد ذلك لم يلحقها.','2','514',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (344,'<span data-type="title" id=toc-137>٥٨ - باب: من حلف أن لا يطعم فأخذ برة في فيه</span>
قرأت على إسحاق: رجل حلف أن لا يطعم من منزل أم امرأته شيئاً، فأخذ برة فوضعها في فيه؟ قال أبو يعقوب: كلما فعل ذلك ناسيًا وإرادته حين حلف أن لا يأكل ما يكون من الأطعمة التي يأكلها الناس فلا حنث عليه.
قلت لأبي يعقوب: ويحنث الإنسان في برة إذا حلف أن لا يأكل؟ قال: نعم.
سُئلَ إسحاق، عن رجل حلف فقال لرجل: لا آكل من طعامك إلى سنة في هذه الدار. فأخذ من طعام هذا فحمله إلى موضع آخر فأكله. فلم يرخص له، وقال: لا يأكله، لأنه من طعام هذا، وهو حيلة.','2','515',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (345,'<span data-type="title" id=toc-138>٥٩ - باب: إذا قال الطلاق لازم ونوى اليمين</span>
سُئلَ إسحاق، عن رجل قال: الطلاق لازم. ونوى اليمين. قال: هو له لازم.
حدثنا أحمد بن محمد: حدثنا عارم قال: ثنا خالد قال: سمعت عبيد الله يقول: إذا قال الرجل لامرأته أنا منك طالق ثلاثًا. جعلها طلاقًا. قال: وسمعته يقول: إذا قال الرجل لامرأته أنا منك طالق ثلاثًا. وقال: لم أنو طلاقًا. قال: لا أصدق الذي يقول ذلك.','2','516',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (346,'<span data-type="title" id=toc-139>٦٠ - باب: إذا قال: لا أريدك أو تبرأت منك</span>
سألتُ إسحاقَ قلتُ: رجل قال لامرأته: لا أريدك وتبرأت منك إن فعلت ما تفعلين. يريد به إن كنت تطاولين عليّ بلسانك وتعصيني هذا العصيان، والرجل يقول ويحلف: أنه لم يرد بهذا القول طلاقًا. قال: إذا حلف على ذلك بين يدي حاكم، فإن لم يكن حاكم فعدول يحلف بين أيديهم أنه لم يرد بقوله طلاقًا جاز ذلك.
حدثنا محمد بن نصر قال: حدثنا حسان قال: قال سفيان في رجل قال لامرأته: برئت مني، أو برئت منك؟ قال: يجعل ذلك به. قال: فإن قال: إنما قلت: برئت مني أن أظلمك، أو أعتدي عليك وأشباه هذا استحلف ولم يكن طلاقًا.','2','517',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (347,'<span data-type="title" id=toc-140>٦١ - باب: إذا حلف أن يشرب /٧٣/ ما في الحب من الخمر</span>
قلت لإسحاق: رجل عنده حب من خمر فحلف بطلاق امرأته ثلاثًا أن يشرب ما في هذا الحب، فسقط الحب فانصب ولم يهو الرجل ذلك. قال أبو يعقوب: أما شرب الخمر فلا يحل له ولو لم ينصب، ووقعت اليمين على المعصية، فأخشى أن يكون الطلاق قد وقع، لأنه لو لم ينصب لم يحل له شربه، ولم يأذن له عالم في شربه.
حدثنا عمرو بن عثمان قال: حدثنا عبد الملك بن محمد قال: سألت الأوزاعي، عن رجل قال: إن لم أشرب ما في هذا الكوز فامرأته طالق، فوقع الكوز، فذهب الماء. قال: لا شيء عليه.
حدثنا يحيى الحماني قال: حدثنا شريك، عن عطاء بن السائب، عن الشعبي','2','518',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (348,'في رجل قال لامرأته: أنت طالق إذا لم تأكلي هذا العرق. فجاءت السنور فأخذت العرق. قال: لم يجعل الله له مخرجًا فأجعل له مخرجًا يقع عليها؟!','2','519',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (349,'<span data-type="title" id=toc-141>٦٢ - باب: إذا قال لامرأته إن لم تخرجي الشيء الذي في البيت فأنت طالق</span>
سُئلَ إسحاق: عن رجل قال لامرأته: إن لم تخرجي الفانيد الذي في البيت فأنت طالق ثلاثًا. قال: إن أخرجته من فورتها ذلك الذي حلف عليه وإلا فهي طالق. قِيل: فإن كان من نيته متى ما أخرجته؟ قال: لا تطلق.
قلت لإسحاق: رجل قال لامرأته إذا ولدت غلامًا فأنت طالق واحدة، وإذا ولدت جارية فأنت كطالق ثنتين. فولدت غلامًا وجارية لا يدري أيهما قبل؟ قال أبو يعقوب: إن أراد به الأمرين جميعًا الذكر والأنثى فولدت ذكرًا وأنثى في بطن وكان حين حلف أراد أن يقع إذا ولدت الأنثى والذكر جميعًا وقعت ثلاثًا بالذكر واحدة وبالأنثى ثنتين. قلت: فإذا ولدت أحدًا وطلقت، ثم ولدت الثاني أيقع عليها الطلاق؟ قال: نعم ما دامت في العدة يقع عليها.
سُئلَ إسحاق عن رجل قال لامرأته: كلما خرجت من باب: الدار فأنت طالق. ولم يقل واحدة ولا ثنتين، ولا ثلاثة. قال أبو يعقوب: يسأل ماذا أردت بقولك: كلما خرجت. على مرة أردت، أو على مرار؛ لأنها إذا خرجت مرارًا يقع تطليقة كل مرة، فإذا تم خروجها ثلاث مرات فقد وقع التطليقات الثلاث فبانت منه، وإن احتالت فخرجت إلى السطح أو ما أشبهه تريد بذلك أن لا يقع الطلاق فإنه يقع مثل ما يقع إذا خرجت من باب: الدار.','2','520',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (350,'<span data-type="title" id=toc-142>٦٣ - باب: طلاق أهل الشرك</span>
سألت إسحاق /٧٤/ عن طلاق أهل الشرك فقال: جائز.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن سعيد، عن الحكم وحماد في المشرك يطلق امرأته في شركه. قال: جائز.','2','521',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (351,'<span data-type="title" id=toc-143>٦٤ - باب: إذا حلف أن لا يأكل من كسب امرأته</span>
قلت لإسحاق: رجل حلف بالطلاق أن لا يأكل من كسبها، فأهدي لها أترى ذلك من كسبها؟ فهل يحل للزوج أن يأكل من ذلك؟ قال: كلما نوى أن يأكل من الشيء الذي لها مما لم تكسبه بنفسها فأهدي لها أن ذلك ليس من كسبها، وإن نوى من الشيء الذي يحل لها كلما كان كسبها، أو أهدى، أو صار لها بوجه من الوجوه فهو كسبها إذا صار الشيء لها، وإن نوى ما تكسب بيديها فهو أهون.
قلت لإسحاق: رجل قال لامرأته: إن كنت تبغضينني فأنت طالق. فقالت: أبغضك. قال أبو يعقوب: لا يتبين البغض إلا بها، فإن أبغضته من ذات نفسها كان كما وصف من الطلاق، وتستحلف المرأة على ذلك، ثم يتنزه الرجل عنها بعدما تحلف أنها تبغضه. قلت: فإن مات أحدهما هل يتوارثان؟ قال: لا.
قلت لأحمد بن حنبل: رجل قال لامرأته: إن دخلت دار فلان فأنت طالق. فإذا الدار لغير الرجل الذي حلف عليه. قال: إذا نوى تلك الدار فهي طالق.
وسئلَ أحمد مرة أخرى، عن رجل كانت امرأته على غرفة في داره فقال لها: إن نزلت إلى هذه الدار ولكنها نزلت مرورًا إلى دار أبيها، ثم رجعت إلى تلك الغرفة، فقامت حتى انتقلوا متاعها ولم تدخل الدار. فقال: أبو عبد الله: هذه الغرفة من هذه الدار التي حلف عليها؟ قِيل: نعم. قال: يا أخي سل غيري.','2','522',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (352,'<span data-type="title" id=toc-144>٦٥ - باب: إذا قال لامرأته اذهبي فتزوجي من شئت</span>
قِيل لأحمد: رجل قال لامرأته: إذهبي فتزوجي من شئت. قال: إذا كان في غضب فإني أخاف أن تكون ثلاثًا، ولكني لا أفتي به أخاف أن يكون هذا مثل الخلية، والبرية، والبتة، والبائنة، وكذلك حبلك على غاربك.
حدثنا أبو معن قال: ثنا غندر قال: حدثنا شعبة، عن هشيم، عن مغيرة، عن إبراهيم قال: إذا قال الرجل لامرأته: اذهبي فتزوجي قال: ليس بشيء.','2','523',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (353,'قال الشعبي: إن أهون من هذا يعد طلاقًا.
حدثنا أبو معن قال: ثنا حماد بن مسعدة، عن أشعث، عن الحسن أنه جاءته امرأة فقالت: إن زوجها قال لها: اذهبي فتزوجي من شئت. قال: فجعلها الحسن ثلاثًا.','2','524',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (354,'<span data-type="title" id=toc-145>٦٦ - باب: طلاق المبرسم والمجنون</span>
سألت أحمد بن حنبل قلت: طلاق المبرسم والمجنون /٧٥/. قال: إذا كان لا يعقل فلا. ولم يذهب إليه.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن سعيد بن أبي أيوب، عن يزيد بن أبي حبيب، عن أبي الخير، عن عقبة بن عامر قال: لا يجوز طلاقالموسوس.','2','525',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (355,'<span data-type="title" id=toc-146>٦٧ - باب: طلاق الغلام</span>
سألت أحمد عن طلاق الغلام قال: إذا كان يعقل الطلاق جاز.
سألت إسحاق، عن طلاق الصبيان وعتاقهم قال: إذا كانوا لعشر سنين أو أقل، قال: لا يجوز حتى يبلغ ثنتي عشرة سنة، ووصيتهم جائزة حينئذ، وإن كان يعقل الشري والبيع فهو جائز أيضاً. وذهب إلى أنه لا يجوز دون ثنتي عشرة سنة.','2','526',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (356,'حدثنا أبو معن قال: ثنا روح، قال: حدثنا أشعث، عن الحسن في طلاق الصبي إذا عقل الصلاة وحفظ فهو جائز.
حدثنا أبو معن قال: حدثنا خالد بن الحارث قال: ثنا سعيد، عن قتادة، عن إسماعيل، عن سعيد بن المسيب أنه قال: في الغلام إذا أحصى الصلاة وصام رمضان فطلاقه جائز، وكان إسماعيل ابتُلي به.','2','527',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (357,'قلت لأحمد: الرجل يطلق امرأته وهو مشرك تطليقة أو تطليقتين، ثم أسلما فتزوجها. قال: نحن نقول إن طلاق أهل الشرك طلاق.
حدثنا محمود قال: ثنا عمر بن عبد الواحد قال: سمعت الأوزاعي يقول في المشرك ليس من أهل الكتاب يطلق امرأته هل يلزمه طلاق الشرك؟ قال: نعم.
قلت لأحمد: رجل قال لأمته: أنت طالق. قال: ليس بشيء.','2','528',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (358,'قلت: فإن قال لأم ولده: أنت طالق؟ قال: ليس بشيء.
قلت لإسحاق بن إبراهيم: رجل طلق امرأته، فراجعها في العدة ولم يعلمها، فلما انقضت العدة قال لها: إني قد كنت راجعتك. فقالت له المرأة: كذبت. ما الحكم في ذلك؟ قال: إن كانت له بينة صدق، وإلا لم يصدق. قلت لإسحاق: رجل طلق امرأته ثلاثًا، فادعت حملاً، وأنكر الزوج ذلك. قال: إلى سنة يجوز قولها، وهو الذي أختار أنا وقال قوم: سنتين. قال: ولا يجوز بعد سنة.
حدثنا أبو معن قال: ثنا يزيد بن هارون قال: أنبأ يحيى بن سعيد، عن سعيد بن المسيب أن عمربن الخطاب قال في المرأة تُطلق فتحيض حيضة أو حيضتين، ثم يرتفع حيضها فلم تدر ما الذي رفعها، فإنها تربص بنفسها تسعة أشهر، فإن استبان بها حمل فهي حامل، وإلا اعتدت بعد التسعة ثلاثة أشهر فتلك سنة.
حدثنا /٧٦/ المسيب بن واضح قال: حدثنا ابن مبارك، عن عبد الملك بن أبي سليمان، عن عطاء ابن أبي رباح في الرجل يقول لامرأته: قد أعتقتك.','2','529',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (359,'قال: إن كان نوى طلاقها فهي طالق، وإن لم يكن نوى طلاقًا فليس بشيء.
حدثنا المسيب قال: حدثنا ابن مبارك، عن يونس، عن الزهري في رجل يكون به مرض لا يعاد منه رمد، أو جرب، أو ريح، أو لقوة، أو فتق أيجوز طلاقه؟ أو ترثه امرأته؟ قال: إن بت الطلاق فيما ذكرت من الوجع، فإنها لا ترثه. وفي رجل قُطِعَ منه عرق، أو جرح به جراح أو بطن قال: إن كان كذلك وهو مريض، فإنها ترثه.','2','530',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (360,'<span data-type="title" id=toc-147>٦٨ - باب: إذا قال: أنت عليّ حرام</span>
سألت أحمد بن حنبل قلت: رجل قال لامرأته: أنت عليّ حرام ونوى الطلاق؟ قال: لا يكون طلاقًا نوى أو لم ينو. قلت فيه كفارة الظهار؟ قال: نعم. قلت فإن قال: الحل عليه حرام؟ قال: كذلك أيضاً. قلت: إنه لم ينو الطلاق؟ قال: نوى أو لم ينو فهو سواء.','2','531',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (361,'سألت أحمد أيضًا قلت: الرجل يقول لامرأته: وجهي من وجهك حرام؟ قال: إذا قال: أنت عليّ حرام. فهو أشد من هذا.
حدثنا أبو أمية محمد بن إبراهيم قال: ثنا محمد بن عبد الله الأنصاري قال: ثنا محمد بن فضاء، عن أبيه، عن جده أن عثمان بن عفان قال: في الرجل يقول لامرأته أنت عليّ حرام. فقال عثمان: عليه ما على المظاهر.
وسألت إسحاق، عن الحرام قال: أذهب فيه إلى قول ابن مسعود. نوى شيئًا فهو ما نوى، إن نوى ثلاثًا فثلاث، وإن نوى واحدة','2','532',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (362,'فواحدة بائنة لا تكون إلا بائنة في هذا وإن لم ينو طلاقًا فعليه كفارة مغلظة عتق رقبة أو صيام شهرين متتابعين، أو إطعام ستين مسكينًا وهو كفارة الظهار.
قال علي: إذا قال: أنت عليّ حرام، أو أنت عليّ كظهر أمي، فهو سواء.
حدثنا أحمد بن حنبل قال: ثنا عباد بن العوام قال: أخبرني عمر بن عامر، عن أبي رجاء، عن العلاء بن زياد، عن أبيه، عن زياد بن مطر قال: سمعت عمر بن الخطاب يقول: في الحرام يمين.
حدثنا ابن المسيب قال: حدثنا ابن مبارك، عن خالد الحذاء، عن عكرمة، عن ابن','2','533',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (363,'عباس أن عمربن الخطاب كان يقول في الرجل يقول: امرأته حرام عليه. قال: يمين يكفرها.
حدثنا محمد بن إسماعيل قال: ثنا الحزن بن عطية قال: ثنا الأوزاعي، عن يحيى بن أبي كثير قال: كتب إليّ يعلى، عن سعيد بن جبير، عن ابن عباس','2','534',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (364,'في الحرام يمين.
حدثنا أبو معن قال: ثنا حماد بن مسعدة، عن صخر بن جويرية /٧٧/، عن نافع، عن ابن عمر قال: إذا قال الرجل: امرأته عليه حرام. ثم أمسكها فليعتق، عن يمينه أو ليتصدق. حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن وهيب البصري، عن ابن طاوس، عن أبيه قال: في الحرام واحدة وهو أحق بها.','2','535',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (365,'حدثنا هدبة بن خالد قال: ثنا أبو الأشهب، عن الشعبي قال: ما أبالي حرمها أو قصعة من ثريد.
سألت أحمد قلت: رجل قال: الحل عليه حرام، وهو الطلاق؟ قال: طلقت امرأته ثلاثًا. قلت: ثلاثًا؟ قال: نعم، ولكني لا أفتي به.','2','536',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (366,'وسألتُ إسحاقَ قلتُ: إن قال: الحل عليه حرام وهو الطلاق. قال: يقع عليه.
وسألت إسحاق أيضًا قلت: الرجل يقول: الحل عليه حرام، ولم ينو به الطلاق، قال: كفارة يمين.
حدثنا أبو معن قال: ثنا بهز بن أسد قال: ثنا همام قال: سئل قتادة، عن رجل حلف، قال: كل حل عليه حرام؟ قال: كان الحسن وجابر بن زيد يقولان: هي يمين. قال: قتادة: إن قال: كل حلال عليه حرام من أهل أو مال. حرمت عليه امرأته.','2','537',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (367,'حدثنا المسيب بن واضح قال: حدثنا ابن مبارك، عن عمر بن ذر قال: سألت الشعبي، عن قول الرجل: كل حل عليه حرام؟ قال: لا يوجب عليه طلاقًا، ولا يحرم حلالاً، إنما هي يمين يكفرها.
حدثنا المسيب قال: ثنا ابن مبارك، عن معمر، عن قتادة، عن الحسن في الرجل يقول: الحلال عليه حرام؟ قال: إن نوى الطلاق فهي ثلاث، وإن نوى يمينًا فهي يمين، وإن لم ينو شيئًا فهي يمين.','2','538',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (368,'سألت أحمد قلت: فإن قال: بعض جسدك علي حرام: يدك، أو رجلك، أو نحو ذلك قال: هي حرام كلها. قلت: فإن قال: يدك، أو رجلك، أو بعض جسدك طالق؟ قال: طالق إلا الشعر. قلت: وما بال الشعر؟ قال: لأن الشعر هو شيء يفنى ويذهب من جسدها.
وسألت إسحاق قلت فإن قال لامرأته: يدك، أو رجلك طالق كلما طلق منها عضوًا طلقت. قلت: فإن قال: شعرك طالق؟ قال: لا تطلق في الشعر والظفر. حدثنا أحمد بن عبيد الله قال: ثنا خالد بن يزيد، عن معمر، عن قتادة قال: من حرم شعرة من امرأته حرمت عليه.','2','539',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (369,'حدثنا أحمد بن عبيد الله قال: ثنا أبو معاوية، عن اسماعيل بن مسلم، عن الحسن مثله.
حدثنا بشر بن معاذ قال: حدثنا أبو قتيبة، عن العلاء بن سليم قال: سمعت الحسن وسأله رجل فقال: رجل قال لامرأته: ذكري عليك حرام. قال: حرمت عليه. سُئلَ أحمد، عن رجل قال: حل الله عليه حرام وليست له امرأة ولم ينو؟ قال: أرجو أن يجزيه كفارة اليمين.','2','540',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (370,'وسألتُ إسحاقَ قلتُ: رجل قال: كل حل على المسلمين حلال فهو عليّ حرام ولم ينو به الطلاق، ولا كان في قلبه طلاق؟ قال: كلما لم ينو به الطلاق ونوى به اليمين فعليه كفارة يمين مغلظة صوم شهرين، أو إطعام ستين مسكينًا.
وسُئلَ إسحاق مرة أخرى، عن رجل قال لامرأته: حل الله عليه حرام. مراراً، ولم ينو به طلاقًا فحنث. قال: عليه يمين يكفرها إما أن يعتق رقبة وإما إن يصوم شهرين متتابعين، وإما إن يطعم ستين مسكيناً، لكل مسكين مد من بر، يجزيه من الثلاث أيهن شاء.
سألت علي بن عبد الله قلت: فإن قال: الحل عليه حرام. فذهب إلى أن عليه كفارة يمين. قلت لعلي فإن قال: الحل عليه حرام وهو الطلاق قال: واحدة وما نوى.
قِيل لأحمد: رجل قال: حل الله عليه حرام وهو الطلاق إن سكن هذه الدار، أو دخلها. قال: يتحول عنها ساعة يحلف. قِيل: فحنث؟ قال: أخشى أن يكون ثلاثًا، ولكن لا أفتي به.
وسألتُ إسحاقَ قلتُ: فإن قال: الحل عليه حرام وهو الطلاق؟ قال: يقع عليه.
وسئلَ إسحاق مرة أخرى، عن رجل قال: كل حل عليه حرام. ونوى طلاقًا؟ قال: هو طلاق، وإن نوى واحدة فواحدة، وإن نوى ثنتين فثنتين، وإن نوى ثلاثًا فثلاث.','2','541',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (371,'<span data-type="title" id=toc-148>٦٩ - باب: إذا قال: لجاريته أنت عليّ حرام</span>
قيل لأحمد: رجل قال لجارية يطؤها: أنت عليّ حرام قال: يكفر يمينه، وليس هذا مثل الذي يقول لزوجته: أنت عليّ حرام. لأن هذه ملك يمين.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أنبأ داود، عن الشعبي، عن مسروق أنه قال: إن رسول الله - صلى الله عليه وسلم - حلف لحفصة أن لا يقرب أمته، وأنها حرام عليه، فنزلت الكفارة ليمينه، وأمر أن لا يحرم ما أحل الله.','2','542',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (372,'<span data-type="title" id=toc-149>٧٠ - باب: الخلية والبرية والبائنة والبتة</span>
قِيل لأحمد: رجل قال لامرأته: أنت طالق البتة. قال: لا أقول في هذا شيئاً، وأخاف أن يكون ثلاثًا.
وسألتُ إسحاقَ قلتُ: رجل قال لامرأته: أنت طالق البتة. قال: أجعله نيته، فإن أراد واحدة يملك الرجعة فله ذلك، وإن أراد ثنتين، وإن أراد ثلاثًا، وإن أراد واحدة لا يملك الرجعة فله ذلك. جعلها نيته.
قِيل لأحمد: رجل قال لامرأته: أنت خلية، أو برية، أو بائنة، أو بتة. قال: هو مثل الأول، أخاف أن يكون هذا كله ثلاثًا.','2','543',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (373,'سألت إسحاق، عن الخلية، والبرية، والبائنة، والبتة، وقلت له: ما مذهبك فيها؟ قال: كل هذا واحدة بائنة، عندي. ثم قلت له: إن قال: أنت طالق تطليقة بائنة. ولم ينو شيئاً؟ قال: هي بائنة، لأنه قد تكلم بالطلاق، وقال: بائنة، أو برية، فلا يكون يملك الرجعة، وإن نوى أن يملك الرجعة فله ذلك. وسألت إسحاق مرة أخرى، قلت: رجل قال لامرأته: أنت طالق تطليقة بائنة. قال: هي تطليقة بائنة كما قال لا يملك رجعتها. وجعل ينكر قول الشافعي. قلت لإسحاق: فإن قال: أنت بائنة. قال: نوى الطلاق؟ قلت: نعم. قال: هو كما قال:. قلت: لا يملك رجعتها؟ قال: لا.
وسألتُ علي بن عبد الله قلت: الرجل يقول لامرأته: أنت خلية، أو برية، أو بائنة، أو حبلك على غاربك؟ قال: إن لم يكن نوى طلاقًا فليس بشيء، وإن أراد الطلاق فواحدة واحدة.
وسألتُ أبا ثور قلت: الرجل يقول لامرأته: أنت خلية، أو برية، أو بائنة، أو بتة؟ قال: كل هذا واحدة يملك الرجعة. قال: وكان الشافعي يقول:','2','544',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (374,'أسأله ماذا أراد واحدة، أو ثنتين، أو ثلاثًا أجعله به قال: وكان مالك يقول: أما خلية وبرية وبائن، فإنه إن كان دخل بها فهي ثلاث ثلاث، وإن لم يكن دخل بها فهي واحدة، وأما بتة فإنها ثلاث دخل بها أو لم يدخل بها.
قلت لأبي ثور: فإن قال: حبلك على غاربك؟ قال: أقول واحدة يملك الرجعة.
حدثنا محمد بن عبد الاعلى قال: حدثنا معتمر بن سليمان قال: سمعت بردًا يحدث، عن مكحول والزهري وسليمان بن موسى أنهم قالوا: في قول الرجل','2','545',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (375,'لامرأته: إنها برية، أو خلية، أو بائنة، أو طالق البتة: فإنها بمنزلة الثلاث لا تحل له حتى تنكح زوجًا غيره.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أخبرنا منصور - وهو ابن زاذان، عن عطاء بن أبي رباح أن رجلًا قال لامرأته: حبلك على غاربك. قال: ذلك مرارًا، فأتى عمر بن الخطاب فاستحلفه بين الركن والمقام ما أردت بذلك. قال: الطلاق. ففرق بينهما.
قال أبو محمد: سمعت أحمد بن حنبل يقول: ليس أحد يسمى منصورًا أفضل من منصور بن زاذان.','2','546',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (376,'<span data-type="title" id=toc-150>٧١ - باب: إذا قال لامرأته اعتدي</span>
قِيل لأحمد: رجل قال لامرأته: اعتدي. قال: إذا كان في غضب وأراد الطلاق فإني أخاف إن يكون كذلك أيضا- يعني مثل الخلية، والبرية ونحوها-.
حدثنا محمد بن المصفى قال: ثنا بقية بن الوليد، عن أبي الهيثم، عن أبي سلمة، عن أبي هريرة أن رسول الله - صلى الله عليه وسلم - قال: لسودة بنت زمعة:','2','547',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (377,'((اعتدي)) فجعلها تطليقة، وهو أملك بها.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنبأ يونس، عن الحسن وعبيدة، عن إبراهيم وقالا: إذا قال الرجل لامرأته: اعتدي. وهو ينوي فواحدة يملك الرجعة، وإن كان لا ينوي الطلاق فليس بشيء.
سألتُ أحمد أيضا قلتُ: حديث الحسن في رجل قال لامرأته أنت طالق فاعتدي، أو أنت طالق واعتدي. قال أحمد: إذا قال: أنت طالق','2','548',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (378,'فاعتدي, أو واعتدي. وأراد به القول الأول أنها تعتد فهي واحدة، وإن أراد غير ذلك حسبت أن تلزمه.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنا أبو حرة، عن الحسن قال: إذا قال: أنت طالق فاعتدي. فهي واحدة، وإذا قال: أنت طالق واعتدي. فهما ثنتان.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن ابن جريج قال: قلت','2','549',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (379,'لعطاء إذا قال: أنت طالق واعتدي ثم قال: أردت ثلاثًا. ثم ندم قال: إنما هي ما خرج من فيه.
قلت لعمرو بن دينار قال: أنت طالق. ثم قال: أردت ثلاثًا. قال: هي واحدة.','2','550',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (380,'<span data-type="title" id=toc-151>٧٢ - باب: إذا قال لامرأته أمرك بيدك</span>
قِيل لأحمد: رجل قال لامرأته: أمرك بيدك. قال: القضاء ما قضت. قلت: فإن قالت: قد طلقت نفسي ثلاثًا، أو واحدة فهو قولها؟ قال: نعم.
وسُئلَ أحمد مرة أخرى، عن الرجل يجعل أمر امرأته بيدها. قال: القضاء ما قضت. قلت: فإن قالت: قد طلقت نفسي ثلاثًا، أو قالت: قد طلقتك ثلاثًا. قال: إذا قالت: قد طلقت نفسي ثلاثًا. فهذا طلاق، وإذا قالت: قد طلقتك ثلاثًا. فإن ابن عباس قال: خطأ أنه نواها. كأنه لا يراه','2','551',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (381,'شيئًا.
قلت لأبي عبد الله تذهب إلى قول ابن عباس؟ قال: نعم. أرجو أن لا يكون طلاقًا. إذا قالت: قد طلقتك ثلاثًا لزوجها. وسألتُ إسحاقَ قلتُ: رجل قال لامرأته: أمرك بيدك. فقالت: قد طلقتك. قال: ما أرادت به الطلاق نفسه فذاك لها.
وسمعت إسحاق مرة أخرى يقول في: أمرك بيدك رأيته ينكر قول من يقول القضاء ما قضت.
وقال: إسحاق: القضاء ما قضت إذا فوض ذلك إليها.
قال إسحاق مرة أخرى في قوله: أمرك بيدك. ونوى واحدة. قال: هو نيته، وإن لم ينو شيئًا فطلقت نفسها ثلاثًا فهو ثلاث.
حدثنا أبو معن الرقاشي قال: حدثنا عبد الرحمن بن مهدي، قال: حدثنا همام، عن قتادة، عن سعيد بن المسيب، عن فضالة بن عبيد في:','2','552',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (382,'((أمرك بيدك)) قال: القضاء ما قضت.
حدثنا أحمد بن يونس قال: ثنا ليث بن سعد، عن نافع، عن عبد الله أنه كان إذا سُئلَ عن تمليك الرجل امرأته أمرها يقول: إذا ملكها أمرها فطلقت نفسها ثلاثًا فقد حرمت عليه. حدثنا المسيب قال: حدثنا ابن مبارك، عن ابن لهيعة، عن يزيد بن أبي حبيب أنه حدثه عن سلمة بن أبي سلمة بن عبد الرحمن بن عوف، عن أبيه أن رميثة','2','553',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (383,'القرشية كانت تحت محمد بن عبد الرحمن بن أبي بكر فملكها أمرها فقالت: أنت الطلاق ثلاثًا. فقال: عثمان: أخطأت، فلا طلاق لها إن المرأة لا تطلق الرجل.
وسألتُ أبا ثور قلت: رجل جعل أمر امرأته بيد رجل، ولم يبين، فطلقها ثلاثًا قال: يُسأل الزوج كم أراد، فإن قال: أردت واحدة. يستحلف ما أردت إلا واحدة، وهي امرأته، وهو قول الشافعي، وإذا بين فهو ما بين.','2','554',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (384,'<span data-type="title" id=toc-152>٧٣ - باب: إذا جعل امرأته بيد رجل أن يطلقها</span>
قِيل لأحمد: رجل جعل أمر امرأته بيد رجل أن يطلقها واحدة فطلقها ثلاثًا قال: هي واحدة هو ما شرط.
حدثنا المسيب قال: حدثنا ابن مبارك، عن وزكريا، عن الشعبي أنه سُئلَ، عن الرجل جعل أمر امرأته بيد رجل فطلقها ثلاثًا قال: هي تطليقة، إنما جعل بيده مرة واحدة.
سألتُ إسحاقَ قلتُ: رجل أراد أن يغيب عن أهله فقالت له أهله: إنك تغيب ولا أدري متى تقدم فاجعل أمري بيد رجل فقال لها إذا جاءت السنة فإن قدمت وإلا أمرك بيد فلان، وجعل الأمر بيد رجل قال: يُسئَل، عن إرادته يكون هذا على معنيين معنى أنه يريد إذا جاءت السنة فطلقها متي ما شئت، ومعنى آخر إذا جاءت فإن طلقها من ساعتها، وإلا فلا شيء لك. قال أبو يعقوب: يستحلف الزوج على ذلك ما أراد به.','2','555',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (385,'حدثنا محمد بن رافع قال: حدثنا ابن أبي فديك قال: قال أبو الحارث في الرجل يقول لامرأته: إذا كان الهلال فأمرك بيدك. قال: يقع عليها حتى يأتي الأجل، فإن شاءت فارقت، وإن شاءت ردت وليس هو كالذي يقول لامرأته: أنت طالق إذا كان الهلال.
قلت لإسحاق: رجل قال لامرأته: إذا قدم فلان فأمرك بيدك؟ قال: قال لها: كم هو بيدك؟ قلت: لا. قال: إذا قدم فلان فأمرها بيديها فإن قامت من مجلسها ولم تقض شيئًا رجع الأمر إلى الزوج. قلت: فرجل قال لامرأته قد خيرتك إلى شهر. قال: إلى تمام ما جعل لها الخيار فلها الخيار.
حدثنا عمرو بن عثمان، قال: حدثنا بقية بن الوليد: سألت الزبيدي، عن الرجل يجعل أمر امرأته في يديها حتى تنطق. قال: وكان غيره يقول: إذا قامت من مجلسها فلا شيء لها. قال الزبيدي: ولا أقول أنا قولهم، ولكن أقول ذلك بيديها ما لم يجامعها زوجها فذلك الرضا منها، وقد ذهب منها ما جعل في يديها من الخيار والتمليك. قال بقية: والناس على قول عمر بن','2','556',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (386,'الخطاب وعبد الله بن مسعود صاحبي النبي - صلى الله عليه وسلم -، وذلك أنها إن قامت من مجلسها ذلك فلا خيار لها، وإن نزع ذلك منها قبل أن تنطق من خيار أو تمليك فذلك له، وسقط قولها على هذا أكثر العلماء بالشام وعامة أهل الحجاز.
حدثنا محمود، عن عمر قال: سمعت الأوزاعي يقول في رجل ملك امرأته أمرها وهما قائمان فمشت أو قعدت قبل أن تنطق ثم نطقت. قال: لا أمر لها.
سألتُ إسحاقَ قلتُ: رجل جعل أمر امرأته بيد رجل إلى سنة، أو إلى شهر متى ما شاء طلقها إلى سنة أو إلى شهر وأشهد على ذلك، فلما مضى بعض السنة أشهد شهودًا أني كنت جعلت أمر امرأتي بيد فلان، وإني قد رجعت في ذلك واسترددت الأمر إليّ. هل له ذلك؟ ورخص فيه أن يرجع في ذلك.','2','557',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (387,'قلت لإسحاق رجل جعل أمر امرأته بيد رجلين، فطلق أحدهما دون الآخر؟ قال: لا يجوز حتى يجتمعا إذا جعل الأمر إليهما.
حدثنا عبيد الله بن معاذ، ثنا أبي، ثنا أشعث، عن الحسن فيمن جعل أمر امرأته بيد رجلين فطلق أحدهما قال: ليس بشيء حتى يجتمعا.
قلت لإسحاق: رجل قال لامرأته: إن خرجتُ من هذه الدار إلى شهرين فأمرك بيدك. فخرج سرًا من المرأة، ولم تعلم المرأة فلما كان بعد ذلك بأيام علمت؟ قال: الأمر بيدها، لأنه لم يقل أمرك بيدك يومئذ.
سألت أحمد بن حنبل قلت: رجل غاب، عن أهله فقال: إن لم آتك إلى كذا وكذا فأمرك بيدك. قال: هذا لا يضبط، لأن الغائب لعله يرجع، عن قوله في سفره.','2','558',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (388,'<span data-type="title" id=toc-153>٧٤ - باب: الخيار</span>
قِيل لأحمد: الرجل يقول لامرأته: اختاري. قال: إن اختارت نفسها فواحدة، وإن اختارت زوجها فلا شيء. قِيل هي واحدة بائنة. قال: وأي شيء البائنة لا.
وسألت أحمد أيضاً عن رجل خيّر امرأته فاختارت نفسها. قال: هي واحدة، وهو أحق بها، وقال: أشهد شاهدين على رجعتها بغير مهر.
وسئلَ إسحاق، عن رجل خيّر امرأته. قال: إذا خيرها فاختارت زوجها لم يقع الطلاق، وإن اختارت نفسها وقعت واحدة يملك الرجعة مثل طلاق السنة.','2','559',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (389,'حدثنا المسيب بن واضح قال: حدثنا ابن مبارك، عن سفيان، عن أبي الزناد، عن خارجة بن زيد، عن أبان بن عثمان، عن زيد بن ثابت أنه كان يقول في الرجل يملِك امرأته أمرها، فتختار نفسها. قال: هي واحدة، وهو أحق بها.
حدثنا المسيب قال: حدثنا ابن مبارك، عن سفيان، عن عمرو بن دينار، عن جابر بن زيد قال: إذا خيرها فقامت من مجلسها فليس لها خيار.','2','560',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (390,'قلت لإسحاق: رجل قال لامرأته: اختاري، اختاري، اختاري: فقالت: قد اخترت، قد اخترت، قد اخترت. قال: إن أراد أن يخيرها ثلاثًا فخيرها ثلاث مرات فلم تختر إلا مرة واحدة تريد بذلك جواب ما خيرها فإنها تبين منه بثلاث. قلت مرة اختارت أو ثلاثًا؟ قال: نعم.
حدثنا سعيد بن منصور قال: حدثنا خالد بن عبد الله، عن مغيرة، عن إبراهيم والشعبي وعن بيان، عن الشعبي وقالا: إذا قال الرجل لامرأته: اختاري، اختاري، اختاري. فاختارت واحدة فهي ثلاث، وإن قال لها: اختاري. فاختارت ثلاثًا فهي واحدة. حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن فيمن','2','561',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (391,'قال لامرأته: اختاري، اختاري. قال: هو بمنزلة قوله مرة واحدة.
قلت لإسحاق: رجل قال لامرأته: إذا كان غدًا فاختاري. قال: جائز يقع ذلك عليها.
قلت: فإن بدا له أن يرجع في ذلك قبل الغد؟ قال: ذلك له، وله إن يرجع.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن في الرجل قال لآخر: اذهب إلى امرأتي فخيرها. قال: هو بمنزلة قوله اختاري.','2','562',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (392,'<span data-type="title" id=toc-154>٧٥ - باب: الرجل يهب امرأته لأهلها</span>
سمعت أحمد بن حنبل يقول: إذا وهب الرجل امرأته لأهلها فقالوا: قد قبلناها. فهي واحدة يملك الرجعة، وإن لم يقبلوها فليس بشيء، وقال: اجعله بمنزلة الخيار. وسُئلَ أحمد، عن رجل قال لامرأته: قد وهبتك لأهلك ثم قال: لم أرد الطلاق. قال: يحلف على ذلك.','2','563',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (393,'حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن أشعث، عن الشعبي، عن مسروق، عن عبد الله قال: إذا وهب الرجل امرأته لأهلها فإن قبلوها فهي تطليقة وهو أحق بها، وإن لم يقبلوها فلا شيء.','2','564',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (394,'<span data-type="title" id=toc-155>٧٦ - باب: المفقود</span>
سمعت أحمد يقول: امرأة المفقود تتربص أربع سنين وأربعة أشهر وعشرة أيام، ثم تتزوج قال: وكذلك ما له. قال: والمفقود أن يفقد الرجل في الحرب، أو يكسر به البحر أو يكون','2','565',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (395,'نائمًا على فراشه فلا يرى، أو نحو ذلك. قلت: فالرجل يغيب عن أهله ولا يدرى مكانه؟ قال: هذا ليس بمفقود.
وسئل إسحاق عن المفقود وأنا قرأت عليه المسألة فاملى عليّ قال: إن المفقود هو الذي يفقد من موضع منزله، أو في كورة أخرى، أو في طريق سفر، أو غيره يكون معهم ثم يفقدونه فيقولون: أين فلان وأين ذهب','2','566',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (396,'فلان. فلا يدرى الجن ذهبت به أم مات، أم غاب حيث لا يدري في برٍ أو بحر فهذا المفقود فأما إذا غاب من منزله إلى سفر، أو قصد كورة فيها تجارة، أو حاجة من الحاجات، ثم انقطع علمه عن منزله وأهله سنين فلم يأتهم خبر، فإن هذا لا يسمى مفقودًا هذا غائب ولا يحكم حكم المفقود قال: والسنة مضت في المفقود أن تتربص امرأته أربع سنين من يوم فقدته تعتد امرأته بعد الأربع سنين أربعة أشهر وعشرًا ثم تتزوج، ويقسم ماله أيضاً، وهو الذي بينا عند الضرورة، وإذا لم يكن هناك سلطان، أو كان سلطانه فلم ير للمفقود هذا لوقت، وإن كانت في أرض بها حاكم فعليها أن ترفع أمرها إلى الحاكم، فينبغي للحاكم أن يأمر وليه أن يطلقها، وهو ولي الزوج، ثم تعتد ثلاث حيض، ثم تعتد بعد ذلك أربعة أشهر وعشراً، ثم تتزوج، وإن لم يأمر الحاكم ولي الزوج أن يطلقها وأمرها أن تعتد أربعة أشهر وعشرًا ثم تتزوج جاز ذلك أيضاً، لما صح عن عمر ابن الخطاب من غير طلاق الولي. حدثنا سعيد بن منصور قال: ثنا هشيم قال: ثنا عبد الملك، عن عطاء، عن','2','567',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (397,'عبيد بن عمير أن رجلًا غاب، عن امرأته، فطالت الغيبة، فأتت امرأته عمر بن الخطاب فأمرها أن تتربص أربع سنين، ففعلت، ثم أتته فأمر وليه أن يطلقها واحدة، ثم أمرها أن تعتد ثلاثة قروء، ثم أمرها أن تعتد أربعة أشهر وعشرًا ثم تُزَوَّج.','2','568',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (398,'<span data-type="title" id=toc-156>٧٧ - باب: المفقود يقدم وقد تزوجت امرأته</span>
قِيل لأحمد: المفقود إذا قدم وقد تزوجت امرأته وقُسِمَ ماله؟ قال: يرد عليه ماله، ويخير بين امرأته وبين الصداق. قال: صداقه الذي كان ساق إليها؟','2','569',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (399,'قلت: إن اختار الصداق دفع إليه؟ قال: نعم. قال: وإن اختار امرأته اعتدت من زوجها الأخير ثم ردت على الأول.
وسمعت إسحاق يقول في المفقود إذا قدم وقد تزوجت امرأته. قال: يخير، فإن شاء اختار امرأته، وإن شاء اختار الصداق الذي كان أعطاها.
وسمعت إسحاق مرة أخرى قِيل له: فإن تزوجت هذه المرأة؟ قال: إن كانت تزوجت لما بلغها موت زوجها، أو ظنت أن زوجها فقد فأنه يُفَرق بينها وبين زوجها، وعليه المهر والولد ثابت النسب؛ لأنها تزوجت على شبهة والأنساب تثبت للشبهة، والمهر يجب للوطء إذا كان وطء شبهة.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أخبرنا إسماعيل بن أبي خالد والشيباني، عن الشعبي في امرأة المفقود إذا تزوجت ثم جاءها خبر زوجها وهي حامل من الآخر ومات زوجها الأول. قال: تعتد من الأخير حتى','2','570',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (400,'تضع ثم تعتد من الأول أربعة أشهر وعشرا، ثم ترثه.
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ثنا أشعث بن عبد الملك الحداني، عن الحسن في رجل قدم من غيبة، وقد تزوجت امرأته، وولدت ولدًا ولا يدرى لأيهما هو، وكلاهما يدعيه. قال: هو للأول وقال الحسن إن تزوجت أم ولد المفقود فولدت فهو أحق بها وولدها بمنزلتها، ولا تزوج حتى يمضي لها أربع سنين.','2','571',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (401,'<span data-type="title" id=toc-157>٧٨ - باب: امرأة المفقود أترث من الزوج الثاني إذا مات وقدم الأول</span>
سألتُ إسحاقَ قلتُ: امرأة المفقود تزوجت رجلًا فورثته: ثم قدم زوجها الأول قال: إذا كان تزوجها بعد الأربع سنين فالميراث لها لا ترده قلت فإن ماتت المرأة فورثها الزوج الثاني ثم قدم الزوج؟ الأول قال: كذلك له الميراث. حدثنا سعيد بن منصور قال: ثنا هشيم، ثنا مغيرة، عن إبراهيم في امرأة نعي لها زوجها، فتزوجت زوجًا ثم مات زوجها فورثته ثم قدم زوجها. قال: تُرد إليه امرأته وترد ما ورثت من زوجها.','2','572',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (402,'<span data-type="title" id=toc-158>٧٩ - باب: الرجل يطيل الغيبة فأصابت امرأته ولدًا</span>
سألت أحمد قلت: رجل أطال الغيبة، عن امرأته فأصابت ولداً. قال: ألزمه الولد إلى أربع سنين، وإن كان أكثر من أربع سنين فكان سفرًا يمكنه أن يقدم على أهله ويُعرف ذلك ألزمته، وإن كان بعيدًا فلا.
سألت أحمد مرة أخرى قلت: الرجل يطيل الغيبة، عن أهله فولدت أولادًا بعد أربع سنين. قال: يلزمه الولد الولد للفراش، وأهل المدينة يقولون: يلحق به الولد إلى أربع سنين. ثم قال أحمد إذا كان الزوج والياً، أو قاضيًا على بلدة من البلدان بعيدة، عن المرأة نحو مصر، وكرمان ونحوه ولا يمكن الرجل أن يدع عمله ويأتي أهله فإني أرجو أن لا تلزمه، وإذا كان أمر يمكنه أن يأتي أهله ويمكث فيهم ثم يرجع لحق به الولد.','2','573',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (403,'وسألتُ إسحاقَ قلتُ: رجل غاب عن امرأته سنين، فجاءت بولد؟ قال: كلما استيقن أنه لم يطأها في السنين التي غاب وأتى لذلك أكثر من سنتين فجاءت بولد لم يقبله.
حدثنا محمد بن بشار، قال: حدثنا أبو عاصم، قال: حدثنا شيخ من أهل المدينة، قال: وضعت أم محمد بن عجلان بعد أربع سنين قال: أبو بكر فكان مالك بن أنس يحتج بهذا الحديث يقول: يلزم الولد الزوج الأول بعد أربع سنين.
حدثنا أبو بكر الحميدي قال: حدثنا سفيان قال: ثنا الزهري قال: أخبرني عروة بن الزبير، عن عائشة قالت: قال: رسول الله - صلى الله عليه وسلم -:','2','574',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (404,'((الولد للفراش)).
حدثنا أحمد بن سعيد قال: ثنا أحمد بن سليمان قال: حثنا محمد بن حرب قال: ثنا محمد بن الوليد، عن جبير بن نفير، عن أبي الدرداء أن رسول الله - صلى الله عليه وسلم - قال: ((ما ولد على فراش رجل فهو له وحسابهم على الله)). حدثنا إسحاق قال: ثنا يحيى بن اليمان قال: أخبرنا سفيان، عن ابن عجلان','2','575',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (405,'قال: ولدت امرأتي مرة لثلاث سنين.
وسألتُ أبا ثور قلت: رجل أقر بولد ثم أنكره؟ قال: ليس بين الناس اختلاف أنه إذا أقر به مرة ألزم على كل حال. قلت هل يورث لإنكاره؟ قال: أدبًا هكذا.','2','576',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (406,'<span data-type="title" id=toc-159>٨٠ - باب: هل تتزوج امرأة الغائب إذا بلغها موته</span>
قلت لأحمد: امرأة غاب عنها زوجها فجاءها كتاب من رجل أن الزوج قد مات هل تتزوج؟ قال: لا، إلا أن تقوم بينة به.
قلت لأحمد: فبلغها أنه مات الزوج، فتزوجت، ثم جاء الزوج الأول قال: يفرق بينها وبين الثاني وترد على زوجها الأول.
قلت: فإن كان أصاب منها أولاداً؟ قال: الولد للزوج الثاني إذا كان بينهما نكاح.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا الأشعث، عن الحسن في امرأة أُخبرت بوفاة زوجها فتزوجت، فجاء الأول قال: هي امرأته، ولا يقر بها حتى تعتد من الآخر إن كان دخل بها عدة المطلقة، وإن كانت حاملًا من الآخر فإذا وضعت فقد انقضت عدتها وحلت للأول ولها','2','577',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (407,'الصداق من الآخر كاملًا وإن كان لا يدرى من أيهما الحمل فهو للأول وقال الحسن في امرأة تزوجت ولها زوج يعلم أنه حي مثل هذا.','2','578',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (408,'<span data-type="title" id=toc-160>٨١ - باب: المرأة تلد بعد العدة</span>
قلت لإسحاق: امرأة طلقها زوجها فاعتدت ثلاث حيض فلما كان بعد ذلك بسنة ولدت ولداً، فزعمت أنه من الزوج؟. قال: إذا كان بعد ستة أشهر لم يلزم الزوج. وأظنه ذهب إلى أنه إنما يشاء الزوج. قلت فامرأة مات عنها زوجها فاعتدت أربعة أشهر وعشرًا فلما كان بعد ذلك جاءت بولد؟ قال: إذا كان إلى سنتين اُلزم الولدُ الزوجَ. قلت: ويفرق بين المطلقة وبين المتوفى عنها؟ قال: نعم، لأن المتوفى قد مات والذي طلق حي قائم.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن الحسن بن يحيى قال: أخبرنا الضحاك بن مزاحم أن أمه حملته سنتين قال: وما تغيض الأرحام؟ قال: تغيض من تسعة أشهر وما تزداد؟ ما فوق التسعة.','2','579',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (409,'وسمعت أحمد بن حنبل يقول: حدثنا يحيى بن سعيد الأموي قال: أخبرنا ابن جريج، عن جميلة بنت سعد قال: سمعت عائشة تقول: لا تزيد المرأة في الحمل عن سنتين قدر ظل معرك.','2','580',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (410,'<span data-type="title" id=toc-161>٨٢ - باب: الحكم في مال المفقود</span>
سُئلَ إسحاق عن رجل غاب زمانًا وله مال وترك أخوته وأخواته، وليس يأتي له خبر ولا يدرى أين هو كيف يصنع بماله؟ فقال له أبو يعقوب حين رخص له قال: يقسم المال على الورثة ويُضمنون ويُشهد عليهم، فإن جاء الغائب رد إليه ماله، وإن لم يجئ فهو لهم؛ لأنه كان حقًا لهم.
حدثنا محمد بن نصر قال: ثنا حسان قال: قال سفيان في المفقود وكيف الأمر في امرأته وماله؟ قال: أما ما أُنفق عليها وهو حي فلا تؤخذ به وما أُنفق عليها بعد موته أُخذت به وهو قول الشعبي وبه يأخذ ابن أبي ليلى.
حدثنا عبيد الله قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن إن المفقود لا يقسم ميراثه حتى تمضي أربع سنين.','2','581',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (411,'سألتُ إسحاقَ قلتُ: رجل غاب إلى بلدة بعيدة ووضع عندي مالاً، وله امرأة وأبوان وهم محتاجون. فسألوني أن أدفع إليهم من ماله شيئًا ينفقونه هل يجوز لي إن أدفع إليهم؟ قال: تبين موته؟ قلت: لا. قال: فُقد؟ قلت: لا، ولكنه غاب ولا يدرى أين هو. قال: لا تعطيهم إلا إن تقرضهم قرضًا. قلت: ويُشهَد عليهم، قال: نعم. قلت: فإن أمرني السلطان أن أعطيهم؟ قال: نعم شديدا.','2','582',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (412,'<span data-type="title" id=toc-162>٨٣ - باب: نفقة المطلقة</span>
سُئلَ أحمد وأنا أسمع عن المطلقة ثلاثًا هل لها السكنى والنفقة؟ قال: لا أنا أذهب مذهب حديث فاطمة بنت قيس.
وسألت أحمد مرة أخرى قلت المطلقة ثلاثًا قال: ليس لها سكنى ولا نفقة إلا الحامل فذهب إلى أن الحامل ينفق عليها حتى تضع.
قلت: فالمتوفى عنها زوجها؟ قال: لا سكنى ولا نفقة إلا أن تكون حاملا.
قلت: ينفق عليها من نصيبها؟ فأظنه قال: نعم.
وسمعت أحمد مرة أخرى يقول: المطلقة ثلاثًا ليس لها سكنى ولا','2','583',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (413,'نفقة.
حدثنا يحيى بن عبد الحميد قال: حدثنا شريك، عن أبي بكر بن صخير قال: دخلت أنا وأبو سلمة بن عبد الرحمن على فاطمة بنت قيس فسألها أبو سلمة فقالت طلقني زوجي فلم يجعل لي النبي - صلى الله عليه وسلم - السكنى ولا النفقة.','2','584',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (414,'حدثنا أبو الربيع الزهراني قال: حدثنا جرير، عن منصور، عن مجاهد، عن عمر أبي سلمة، عن فاطمة بنت قيس إن النبي - صلى الله عليه وسلم - لم يجعل لها سكنى ولا نفقة.
حدثنا عمرو بن عمير قال: ثنا بقية بن الوليد قال: حدثني حبيب بن صالح قال: حدثني محمد بن عباد قال: قعدت إلى ابن عباس فسأله رجل هل للمطلقة ثلاثًا نفقة؟ فقلت وكنت أحدث القوم: لا شيء لها، فقال ابن عباس أصبت أو قال: أحسنت ها يا ابن أخي أنا معك.','2','585',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (415,'حدثنا سعيد بن منصور قال: حدثنا أبو عوانة، عن منصور، عن إبراهيم، عن شريح قال: ينفق على الحامل المتوفى عنها من جميع المال.
قال: إبراهيم: وكان أصحابنا يقولون إذا كان المال كثيرًا ينفق عليها من نصيبها، وإذا كان المال قليلًا أُنفق عليها من جميع المال.
حدثنا سعيد بن منصور قال: حدثنا حماد بن زيد، عن عمرو بن دينار، عن إبن عباس قال: ينفق عليها من نصيبها.','2','586',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (416,'حدثنا سعيد قال: ثنا سفيان، عن عمرو بن دينار، عن عطاء، عن ابن عباس في المتوفى عنها الحامل قال: لا نفقة لها.','2','587',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (417,'<span data-type="title" id=toc-163>٨٤ - باب: المرأة المتوفى عنها زوجها وهو غائب كيف تعتد</span>؟
سألت أحمد قلت: إلى أي شيء تذهب في الطلاق والموت إذا كان الرجل غائبًا من أي يوم العدة؟ قال: إذا قامت البينة فمن يوم مات أو طلق. وسمعت إسحاق يقول: المرأة تعتد من يوم يأتيها الخبر.
وسمعت إسحاق مرة أخرى يقول: العدة من يوم يأتيها الخبر، إلا أن تقوم بينة أنه مات يوم كذا وكذا.
وسألت إسحاق مرة أخرى. عن العدة من يوم مات أو من يوم يأتيها الخبر؟ قال: من يوم يأتيها الخبر، إلا إن تقوم بينة على موت أو طلاق.','2','588',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (418,'حدثنا محمد بن عبد الأعلى قال: حدثنا معتمر بن سليمان قال: سمعت بردًا قال: كان مكحول والزهري يقولان: تعتد المتوفى عنها زوجها والمطلقة من يوم يموت زوجها أو يطلقها إذا قامت على ذلك بينة.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أخبرنا داود بن أبي هند، عن عمر بن عبد العزيز أنه قال: إن جاءت ببينة فهو من يوم مات أو طلق، فإن لم تأتي ببينة فمن يوم يأتيها الخبر.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: ثنا أشعث بن سوار، عن الحكم، عن أبي صادق، عن ربيعة بن ناجد، عن علي أنه قال:','2','589',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (419,'العدة من يوم يأتيها الخبر.','2','590',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (420,'<span data-type="title" id=toc-164>٨٥ - باب: الطلاق بالرجال</span>
سمعت أحمد يقول: الطلاق بالرجال، والعدة بالنساء
سألت إسحاق عن طلاق العبد الحرة والأمة قال: الطلاق بالرجال والعدة بالنساءوسألت إسحاق مرة أخرى، عن عدة الأمة قال: العدة بالنساء قلت: فإن عتقت قبل انقضاء العدة؟ قال: تتم عدتها عدة الحرة.
حدثنا سعيد بن منصور قال: ثنا خالد بن عبد الله، عن خالد، عن أبي قلابة، عن زيد بن ثابت قال:: الطلاق بالرجال، والعدة','2','591',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (421,'بالنساء.
حدثنا سعيد قال: ثنا سفيان، عن أيوب،، عن سليمان بن يسار أن نفيعا فتى أم سلمة طلق حرة تحته تطليقتين، فأرادوا أن يجمعوا بينهما فأبى ذلك عثمان وزيد بن ثابت.
حدثنا سعيد بن منصور قال: ثنا سفيان، عن يحيى بن سعيد، عن سعيد بن','2','592',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (422,'المسيب قال: الطلاق بالرجال، والعدة بالنساء.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنبأ يونس، عن الحسن وإسماعيل بن أبي خالد، عن الشعبي وعبيدة، عن إبراهيم قالوا: إذا طلق الرجل امرأته واحدة وهي أمة، وأعتقت في العدة فعدتها عدة الحرة، وإن طلقها ثنتين ثم أعتقت في العدة فعدتها عدة الأمة، ولا رجعة له إليها.
حدثنا أبو بكر محمد بن يزيد قال: أنبأ عمر بن شبيب المسلي، عن عبد الله بن عيسى بن أبي ليلى، عن عطية، عن ابن عمر، عن النبي - صلى الله عليه وسلم - قال: ((تطلق','2','593',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (423,'الأمة تطليقتين، وتعتد حيضتين))','2','594',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (424,'<span data-type="title" id=toc-165>٨٦ - باب: النفساء تطلق كم تعتد</span>؟
سألت أحمد بن حنبل قلت: النفساء تطلق كم تعتد؟ قال: تعتد. ثلاث حيض سوى دم النفاس.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن جرير بن حازم، عن قيس بن سعد، عن بكير بن عبد الله بن الأشج، عن سليمان بن يسار أن زيد','2','595',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (425,'بن ثابت سُئلَ عن النفساء قال: لا تعتد بدم نفاسها من حيضها.','2','596',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (426,'<span data-type="title" id=toc-166>٨٧ - باب: الأمة يتوفى عنها سيدها وهي حامل</span>
قلت لأحمد أم الولد إذا مات عنها سيدها هل ينفق عليها؟ قال: لا، وإن كانت حاملًا أيضًا لم ينفق عليها.
وسألت إسحاق قلت ما تقول في أمة مات عنها سيدها وهي حبلى؟ قال: ينفق عليها من نصيب ولدها. وسألت إسحاق مرة أخرى عن نفقة الأمة الحامل إذا مات عنها سيدها. قال: ينفق عليها من جميع المال.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أخبرنا يونس، عن ابن سيرين أنه كان يقول: نفقتها من جميع المال حتى توفي أخ له وأوصى إليه','2','597',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (427,'وترك أم ولده حاملًا فكره أن يعمل برأيه، فأرسل إلى عبد الملك بن يعلى قاضي البصرة فسأله فقال: لا نفقة لها.
حدثنا سعيد قال: أنا هشيم قال: أنبأ يونس، عن الحسن في أم الولد إذا توفي عنها سيدها وهي حامل قال: إن ولدته حيًا فنفقتها من نصيبه، وإن ولدته ميتًا فنفقتها من جميع المال.','2','598',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (428,'<span data-type="title" id=toc-167>٨٨ - باب: إن العذراء قد تحمل</span>
سمعت أحمد يقول: الجارية العذراء قد تحبل وتشق - يعني - تشق عذرتها إذا مات زوجها، أو طلقها وهي بعد بكر.
وسمعت إسحاق يقول: في حديث النبي - صلى الله عليه وسلم - حيث قال: ((لا يطأن أحد جاريةً ثيبًا من السبي حتى يستبرئها بحيضة)) ويعجبني أن لا يطأ العذراء أيضًا لما قِيل أنها تحمل مع أن العذر من أمرهم أن لا يحملن، وإنما الحكم في عامة الأشياء بأغلب المعاني.','2','599',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (429,'<span data-type="title" id=toc-168>٨٩ - باب: نفقة الحامل المتوفى عنها</span>
وسُئلَ أحمد عن الحامل المتوفى عنها زوجها قال: ليس لها نفقة قال: وإن كانت مطلقة وهي حامل فلها نفقة، وإن لم تكن حاملًا فلا سكنى ولا نفقة.
وقال أحمد مرة أخرى في الحامل: ينفق عليها من نصيبها.
وسألتُ أبا ثور قلت: الحامل هل لها نفقة؟ قال: لا، إنما ينفق الرجل على امرأته ما كان حياً، فإذا مات فلا شيء لها. قلت: حرًا كان الروح أو عبداً؟ قال: نعم.
حدثنا أبو هشام قال: حدثنا حسان، عن هشام بن عروة، عن أبيه أنه قال:','2','600',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (430,'النفقة لها إلا أن تكون حاملًا- يعني: المطلقة ثلاثًا.
قال: حسان وقال ابن أبي ليلى: لا نفقة لها إذا طلقها ثلاثًا، ولها السكنى.
سُئلَ أحمد عن رجل طلق امرأته تطليقة، ثم مات وهي في عدتها هل لها نفقة؟ قال: لا من أين يكون لها نفقة!؟
وسألتُ إسحاقَ قلتُ: رجل طلق امرأته تطليقة، ثم مات وهي في عدتها هل لها نفقة؟ قال: إذا طلقها طلاقًا يملك الرجعة فلها نفقة. قلت: فإن طلقها طلاقًا لا يملك الرجعة؟ قال: ليس لها سكنى ولا نفقة.','2','601',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (431,'<span data-type="title" id=toc-169>٩٠ - باب: القافة</span>
سألت أحمد عن القافة إذا ألحقوا الولد برجلين قال: هما أبواه، وهو ابنهما يرثهما ويرثانه. قال: وقوم يقولون إذا مات أحدهما انقطعت أبوته وهو ابن الباقي منهما. فجعل ينكر هذا القول.
وسألتُ إسحاقَ قلتُ: رجلان ادعيا ولداً. قال: القافة، قال إسحاق: وإذا كان الغلام مدركًا فادعاه رجلان واستوت الدعوتان اتبع الغلام أيهما شاء. قلت: وكم حد الإدراك في هذا؟ قال: إذا كان ابن ثنتي عشرة سنة، وأما حديث عائشة أنها كانت بنت تسع سنين حين بنى بها النبي - صلى الله عليه وسلم - فذاك في الدخول.','2','602',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (432,'قلت لإسحاق مرة أخرى: إذا ألحق برجلين فهو ابنهما يرثهما ويرثانه وهو ابن الباقي منهما؟ قال: نعم.
حدثنا محمد بن نصر قال: حدثنا حسان قال: قال سفيان بن سعيد في عشرة أخوة مشركين تزوجوا امرأة في عقدة واحدة، فولدت ولداً، ثم أسلموا جميعًا قال: الولد بينهم. وقال سفيان في أمة اجتمع عليها ثلاثة في طهر فاستبان حملها عند آخر الثلاثة قال سفيان: إن ادعاه الذي هي عنده، وادعاه الآخرون كان للذي في يده الأمة، وإن قالوا جميعاً: لا ندري من أينا حبلت كان بينهم يرثهم ويرثونه، والأمة لا يقع عليها أحد منهم، فإن مات أحدهم عتقت وعليها خدمة الآخرين، وإن تبروا جميعا كان الولد مملوكًا للذي هي عنده. حدثنا أبو بكر الحميدي، قال: ثنا سفيان، قال: ثنا يحيى بن سعيد، عن سليمان بن يسار قال: كان عمر يربط أولاد الجاهلية باب: ائهم فأتاه رجلان قد','2','603',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (433,'وقعا على جارية لهما في طهر واحد فدعا لهما القافة فقال: إن هما قد اشتركا فتناوله بالدرة فقال: يا أمير المؤمنين سلها. قال: فسألها فقالت: إن هذا غشيني فغبرت ما شاء الله، ثم غشيني هذا، فقال عمر: والي لإيهما شئت. حدثنا أبو عمرو عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا عوف، عن أبي المهلب عمر أبي قلابة قال: قضى عمر بن الخطاب في رجل ادعاه رجلان كل واحد منهما يزعم أنه ابنه وولده في الجاهلية، وأنهما اختصما فيه إلى عمر بن الخطاب، وأن عمردعا أمه فقال لها: أذكرك بالذي هداك الإسلام فلأيهما هو؟ فقالت: لا والذي هداني للإسلام لا أدري لأيهما هو أتاني هذا أول الليل، وأتاني هذا آخر الليل، ولا أدري لأيهما هو فدعا عمر بقافة أربعة ودعا ببطحاء فبسطها فأمر الرجلين المدعيين والمدعى فأوطأ كل واحد منهم بقدم في البطحاء، ثم وارى القوم ودعا القافة قال: انظروا، فإذا أثبتم فلا يتكلمن أحد حتى أسأله. فنظروا فقالوا: قد أثبتنا.','2','604',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (434,'ثم فرق بينهم فدعا بهم واحدًا واحدًا فتتابعوا جميعًا كلهم يشهد أن هذا لهذين فقال عمر: ياعجباه لما يقول هؤلاء، والله إني لأرى الذي ترون، وقد كنت أعلم أن الكلبة تلقح للكلاب ذوي العدد، ولم أكن أشعر أن النساء تفعلن ذلك قبل اليوم، فاذهب فإنهما أبواك وأنت ابنهما.','2','605',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (435,'<span data-type="title" id=toc-170>٩١ - باب: النفساء تُزوَج في نفاسها</span>
سألت أحمد قلت: النفساء تُزوَج قبل أن تخرج من نفاسها؟ قال: نعم تزوج إذا وضعت ما في بطنها من غير أن يقربها.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال:: أنبأ يحيى بن سعيد، عن نافع، عن ابن عمر قال: إذا وضعت فقد حلت فقال له رجل من الأنصار: سمعت عمر بن الخطاب يقول: إذا وضعت ذا بطنها وزوجها على السرير قبل أن يدلى في حفرته فقد حلت.','2','606',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (436,'<span data-type="title" id=toc-171>٩٢ - باب: الذي بيده عقدة النكاح</span>
قلت لأحمد: يا أبا عبد الله الذي بيده عقدة النكاح أهو الزوج، أو المرأة، أو الولي؟ قال: هو الزوج يروى عن رجلين من أصحاب النبي - صلى الله عليه وسلم - جبير بن مطعم تزوج من امرأة فأكمل لها الصداق قال: أنا أحق بالعفو وعلي بن أبي طالب حين قال: لشريح في ذلك.
حدثنا محمد بن الوزير الدمشقي قال: ثنا يحيى بن حسان قال: ثنا عبد الله','2','607',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (437,'بن جعفر، عن واصل بن أبي سعيد، عن محمد بن جبير بن مطعم أن جبير بن مطعم تزوج امرأة ثم فارقها قبل أن يدخل بها وأرسل إليها الصداق كاملًا فقِيل له في ذلك فقال: أنا أحق بالعفو.
حدثنا عيسى بن محمد قال: ثنا وهب بن جرير، عن أبيه، عن عيسى بن عاصم قال: سمعت شريحًا يقول: سألني علي بن أبي طالب عن الذي بيده عقدة','2','608',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (438,'النكاح قال: قلت: هو ولي المرأة. فقال: لا، بل هو الزوج.','2','609',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (439,'<span data-type="title" id=toc-172>٩٣ - باب: ما تلبس المتوفى عنها في عدتها</span>
سألت أحمد قلت: المتوفى عنها زوجها والمطلقة هل يلبسان البرد ليس بحرير؟ فقال: لا تتطيب المتوفى عنها زوجها، ولا تتزين بزينة. وشدد في الطيب إلا أن يكون قليلًا عند طهرها، ثم قال: وشبهت المطلقة ثلاثًا بالمتوفى عنها لأنه ليس لزوجها عليها رجعة.
حدثنا عباس بن عبد العظيم قال: ثنا عبد الملك بن عمرو، عن إبراهيم بن طهمان أنه حدثهم، عن بديل العقِيلي، عن الحسن بن مسلم، عن صفية بنت شيبة، عن أم عثمان، عن أم سلمة قالت: المتوفى عنها زوجها لا تلبس المعصفر من','2','610',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (440,'الثياب، ولا الحلي ولا تختضب، ولا تكتحل ولا تطيب، ولا تمتشط بطيب.','2','611',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (441,'حدثنا محمود بن خالد قال: حدثنا عمر بن عبد الواحد، عن الأوزاعي قال: حدثني يحيى بن أبي كثير قال: حدثني محمد بن عبد الرحمن بن ثوبان، عن عمر بن الخطاب وزيد بن ثابت أنهما استفتيا في امرأة توفي عنها زوجها وبها فاقة شديدة ألها أن تخرج إلى أهلها فتصيب من طعامهم فلم يرخصا لها، إلا أن تخرج في بياض يومها فتصيب من طعامهم، ثم ترجع إلى بيتها.
حدثنا المسيبب بن واضح قال: ثنا ابن مبارك، عن عبيد الله، عن نافع، عن ابن عمر قال في المطلقة ثلاثًا: تخرج نهارًا ولا تبيت عن بيتها.
قلت لأحمد: فتنتقل المتوفى عنها زوجها؟ قال: لا، ولكنها تعتد حيث مات زوجها، ولا تنتقل إلا في حال ضرورة. قِيل: فإن كانت في دار كذا','2','612',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (442,'فأخرجوها؟ قال: تتحول إلى موضع آخر وتمكث فيه.
حدثنا عمرو بن عثمان قال: ثنا بقية بن الوليد قال: سألت الزبيدي عن المطلقة والمتوفى عنها زوجها أسواء هما في أن لا يخرجا من بيوتهما حتى يفرغا من العدة؟ قال: نعم لا يحل لها الخروج إلا من عذر.
حدثنا المسيب بن واضح قال: حدثنا ابن مبارك، عن سعيد بن يزيد بن أبي حبيب، عن يزيد بن محمد القرشي قال: تزوج عبد الله بن عباس','2','613',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (443,'أختي، فخرج بها من المدينة إلى الروحاء فتوفى، فأتيت سالم بن عبد الله والقاسم بن محمد فقلت إن أختي توفى عنها زوجها وتركها بمكان وحش لا أنيس فيه. فقال: أما إليك فلا تنقلها، ولكن إلى منزل زوجها بالمدينة، أو أقرها في مكأنها حتى تنقضي عدتها.
حدثنا سعيد بن منصور قال: ثنا حمادبن زيد، عن سعد بن إسحاق بن كعب بن عجرة، عن عمته زينب، عن فريعة بنت مالك أخت أبي سعيد أن زوجها خرج في طلب أعلاج له فقُتل بطرف القدوم فأتت رسول الله - صلى الله عليه وسلم - تسأله النقلة إلى إخوتها، فذكرت وحشة من بيتها،','2','614',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (444,'فأذن لها، فلما ولت ناداها فقال لها: ((أمسكي في بيتك حتى يبلغ الكتاب أجله أربعة أشهر وعشرًا)).
حدثنا المسيب بن واضح قال: حدثنا ابن مبارك، عن أسامة بن زيد قال: حدثني محمد بن عبد الرحمن الدؤلي أنه سأل عروة بن الزبير وسعيد بن المسيب وأبا بكر بن عبد الرحمن بن الحارث وسليمان بن يسار عن المتوفى عنها زوجها فكلهم قالوا: لا تخرج حتى تنقضي عدتها.','2','615',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (445,'<span data-type="title" id=toc-173>٩٤ - باب: المختلعة ألها نفقة</span>
سألت أحمد قلت المختلعة هل له نفقة؟ قال: كيف يكون لها نفقة وقد ذهبت بنفسها، وكذلك إن كانت حاملا!؟
حدثنا المسيب قال: ثنا ابن مبارك، عن يحيى بن بشر أنه سمع عكرمة يقول: لا سكنى ولا قوت للمختلعة، إنما افتدت منه افتداء، فلا شيء لأحدهما على الآخر.','2','616',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (446,'حدثنا المسيب قال: ثنا ابن مبارك، عن سفيان، عن حماد في المفتدية الحبلى: لها النفقة والسكنى ما لم يشترط أن لا نفقة لك. قال سعيد: وإن اشترط أن لا نفقة لك، ولا سكنى فإن شرطه يجوز في النفقة ولا يجوز في السكنى.','2','617',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (447,'<span data-type="title" id=toc-174>٩٥ - باب: المطلقة ثلاثًا حاضت حيضتين ثم ارتفع حيضها</span>
سألت أحمد قلت: رجل طلق امرأته ثلاثًا فحاضت حيضة، أو حيضتين، ثم ارتفع حيضها؟ قال: إن كان ارتفع حيضها من مرض، أو رضاع، أو علة فإنها تمكث في العدة حتى يذهب مرضها ويعود إليها الحيض، أو تفرغ من الرضاع فيعود الحيض فتعتد بالحيض، فإن كان ارتفع حيضها من غير علة ولا مرض فإنها تعتد سنة تسعة أشهر للحمل وثلاثة أشهر للعدة وهو مذهب أحمد. قلت: فتعتد سنة من يوم طلق، أو من يوم ارتفع حيضها؟ قال: من يوم ارتفع حيضها. قال: ويقول قوم: تجلس أبدًا حتى يعاودها الدم ورأيته يقبح هذا القول.
وسألتُ إسحاقَ قلتُ: رجل طلق امرأته ثلاثًا فحاضت حيضة، أو حيضتين ثم ارتفع حيضها؟ قال أبو يعقوب: كلما لم تدر مما ارتفع حيضها فإنها','2','618',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (448,'تعتد سنة.
أخبرنا النفعي قال: سمعت يحيى بن سعيد قال: سمعت سعيد بن المسيب يقول: قال عمر بن الخطاب: أية امرأة طلقت تطليقة أو تطليقتين ثم حاضت حيضة أو حيضتين ثم ارتفعت حيضتها فلا تدري ما الذي رفعها فإنها تربص ما بينها وبين تسعة أشهر، فإن استبان حملها فذاك، وإلا اعتدت ثلاثة أشهر، ثم تزوجت.
حدثنا عبد الله بن الزبير الحميدي قال: ثنا سفيان قال: حدثنا يحيى بن سعيد أنه سمع سعيد بن المسيب يقول: قضى عمر بن الخطاب في امرأة طُلِقت فحاضت حيضة أو حيضتين، ثم رفعتها حيضتها لا يُعلم من أي شيء ذلك. فقال: تمكث تسعة أشهر، فإن بان بها حمل، وإلا اعتدت ثلاثة أشهر أخرى فتلك سنة.','2','619',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (449,'قلت لإسحاق: فامرأة طلقها زوجها وأوجب عليه النفقة في قول من يقول لها النفقة، واعتدت، فزعمت أن حيضها ارتفع وأنكر الزوج ذلك كيف الحكم في ذلك؟، وإنما تريد المرأة بذلك أن تأخذ النفقة؟ قال: تحلف على ذلك، لأنها أؤتمنت على فرجها. قلت فإن حلفت؟ قال: ينفق عليها إلى تمام ما تحيض مثلها. قلت: توقت في ذلك شيئاً؟ قال: سنة. وسمعت أحمد يقول: العجوز تعتد ثلاثة أشهر.
وسُئلَ إسحاق عن امرأة بلغت ثلاثين سنة ولم تحض، فطلقها زوجها كيف تعتد؟ قال: إن كان تبين أنها عاقر تربص حتى تيئس من المحيض. قيل: فإن كانت حاضت مرة ثم انقطع عنها؟ قال: تعتد سنة تسعة أشهر للحمل، وثلاثة أشهر للعدة.
سمعت أحمد يقول: لم يختلف أحد في المرأة إذا اعتدت بالشهور، ثم حاضت أنها تعتد بالحيض ما أعلم أحدًا اختلف في هذا.','2','620',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (450,'حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنبأ يونس، عن الحسن ومغيرة، عن إبراهيم ومحمد بن سالم، عن الشعبي قالوا: إذا طلق الرجل امرأته ولم تبلغ المحيض فعدتها ثلاثة أشهر، فإن حاضت قبل أن تنقضي الشهور بيوم أو يومين فعدتها ثلاث حيض، وإن مضت الشهور، ثم حاضت بعد ذلك بيوم أو يومين فقد انقضت عدتها.
قلت لأحمد: رجل قال لامرأته: أنت طالق. فصارت في عدتها، ثم طلقها ثنتين قال: بانت بثلاث يلحقها الطلاق إذا كان يملك الرجعة. قلت: فإن انقضت عدتها مرة واحدة ثم طلقها؟ قال: لا يلحقها الطلاق.','2','621',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (451,'حدثنا سعيد بن منصور قال: ثنا سفيان، عن عبد الكريم البصري، عن إبراهيم قال: كل امرأة ماء الرجل في رحمها وهي تعتد منه وهي تحل له ولا تحل لغيره حسب عليه الطلاق. سألت أحمد قلت: رجل طلق امرأته فولدت ولدًا وفي بطنها آخر؟ قال: لا تنقضي عدتها حتى تضعهما جميعًا. حدثنا المسيب بن واضح قال: حدثنا ابن مبارك، عن سعيد، عن','2','622',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (452,'ابن المسيب وسليمان بن يسار، والحسن، وعطاء بن أبي رباح في التي يطلقها زوجها وفي بطنها ابنان أنه أحق بها ما لم تضع الآخر. قال: وقال قتادة: قال عكرمة: إذا وضعت الأول منهما فقد بانت. فقال: سليمان بن يسار: فسألوه أتتزوج إذا وضعت أحدهما؟ فقال: لا. فقال: خصم العبد.
سألت إسحاق بن إبراهيم قلت: رجل قال لامرأته وهي في العدة: قد راجعتك فقالت له امرأته مجيبة له: قد انقضت عدتي. هل تُصدَق؟ قال: إذا ادعت ذلك فيما تنقضي به عدة النساء صدقت. قلت: فتستحلف؟ قال: نعم. قلت: إن ادعت أن عدتها في شهر هل تُصَدق؟ قال: في أربعين يومًا. فراجعته في الشهر فكأنه مال إلى ذلك ولم يره في أقل من شهر. وقال: أيضاً: إذا شهدت العدول من النساء على انقضاء العدة جاز ذلك راجعته فيها.','2','623',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (453,'حدثنا أبو معن قال: ثنا أبو عامر قال: ثنا إبراهيم بن طهمان، عن مغيرة، عن إبراهيم قال: إذا حاضت ثلاث حيض في أربعين ليلة وقامت بها البينة من نسائها أنها حرمت عليها الصلاة عند كل قرء فقد حلت.','2','624',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (454,'<span data-type="title" id=toc-175>٩٦ - باب: الخلع</span>
قِيل لأبي عبد الله أحمد: كيف الخلع؟ قال: إذا افتدت خلعها. قلت: وهو تطليقة؟ فلا أحفظ ما قال.
وسُئلَ أحمد مرة أخرى عن الخلع أتطليقة هو؟ قال: اختلف الناس فيه.
وسألت أحمد أيضًا قلت: الخلع تطليقة بائنة؟ قال: فيه اختلاف. وكأنه ذهب إليه.','2','625',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (455,'وسمعت إسحاق يقول الخلع مفارقة بغير طلاق إذا خلعها بمال أو اشترت نفسها منه. قلت: فإن تزوجها بعد ذلك فهي عنده على ثلاث؟ قال: نعم. وسألتُ أبا ثور عن الخلع قال: الخلع إنما هو فسخ. قلت: فيلحقها الطلاق ما دامت في العدة؟ قال: لا.
حدثنا أبو بكر الحميدي قال: حدثنا سفيان قال: حدثنا هشام بن عروة، عن أبيه، عن جمهان الأسلمي أن بكرة الأسلمية اختلعت من زوجها','2','626',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (456,'على عهد عثمان، ثم ندم وندمت، فجاء إلى عثمان فأخبره فقال عثمان: هي تطليقة، إلا إن تكونا سميتما شيئًا فهو كما سميتما.
حدثنا سعيد بن منصور. قال: حدثنا هشيم قال: أنبأ حجاج، عن حصين الحارثي، عن الشعبي، عن الحارث، عن علي قال: من أخذ مالًا على طلاق فهو بائن لا رجعة له عليها.
حدثنا سعيد بن منصور قال: ثنا أبو عوانة، عن ليث، عن طاوس أن ابن عباس جمع بين الرجل وامرأته بعد تطليقتين وخلع.
قلت لإسحاق: الرجل يضيق على امرأته فيظلمها حتى تختلع منه أيحكم لها بالمهر؟. قال: إذا كان الظلم من قبله لم يحل له أن يأخد منها، فإن كانت هي الظالمة جاز له أن يأخذ منها قدر ما أعطاها من مهر أو غير ذلك.','2','627',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (457,'حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنبأ إسماعيل بن سالم، عن الشعبي قال: إذا كان البدء من قبله لم يحل له أن يأخذ منها شيئاً، وإن كان من قبلها حل له أن يأخذ منها.
حدثنا سعيد قال: ثنا هشيم قال: أنبأ عبيدة، عن الشعبي قال: إذا كان البدء من قبله فما أخذ منهما فهو كالميتة والدم ولحم الخنزير.
سألت أحمد قلت: المختلعة يطلقها زوجها وهي في عدتها؟ قال: لا يلحقها الطلاق. قال: وقال: المختلعة تعتد عدة المطلقة.
وسُئلَ عن قول ابن عباس وعمر فلم يعجبه. وقِيل لأحمد مرة أخرى: الرجل يخلع امرأته ثم يطلقها؟ قال: لا يلحقها الطلاق ما دامت في العدة.
وسمعت أحمد مرة أخرى يقول: المختلعة لا يلحقها الطلاق.','2','628',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (458,'ما
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أنبا منصور، عن عطاء قال: لا يلحقها الطلاق ما دامت في العدة.
حدثنا عمرو بن عثمان قال: ثنا عبد الملك بن محمد، عن الأوزاعي قال: أنبا الزهري، وعطاء، وعمرو بن شعيب، ومكحول قالوا: المختلعة يلحقها الطلاق كانت في العدة.
سُئلَ أحمد عن رجل كان بينه وبين امرأته كلام، ودخل بينهما رجل فطلبت المرأة المباراة، فأعطاها الرجل حقها، وأشهد عليها أنها قد أخذت جميع','2','629',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (459,'حقها عليه، ثم قال: الرجل الذي بينهما للمرأة: اذهبي أنت ها هنا. وقال للرجل: اذهب أنت هاهنا فتفرقوا على ذلك من غير طلاق. قال: أخشى أن يكون هذا خلع، وقال للرجل: تزوجها بصداق جديد ونكاح جديد، وتكون عندك على ثنتين، قلت: يكون الخلع عند غير ذي سلطان؟ قال: نعم.
وسألتُ أبا ثور قلت: الخلع يكون، عند غير ذي سلطان؟ قال: نعم متى ما أخذ منها أو لم يأخذ منها إذا خلعها، عند سلطان أو غير سلطان فهو خلع. قلت: وهو تطليقة؟ قال: لا، ولكنه فسخ.
حدثنا يحيى الحماني قال: ثنا شريك، عن قيس بن وهب أجاز شريح الخلع دون السلطان.','2','630',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (460,'<span data-type="title" id=toc-176>٩٧ - باب: الحكمين</span>
وسألت أحمد بن حنبل عن تفسير الحكمين؟ قال: على حديث علي يبعث رجل من أهل المرأة ورجل من أهل الرجل فيريان رأيهما إن رأيا إن يفرقا فرقا، وإن رأيا أن يجمعا جمعا. وقال: رجلين عدلين. قلت: فإن قال الزوج: لا أطلق قال: ليس له ذلك بتة لا يترك.
وسألتُ أبا ثور عن الحكمين قال: إذا وقع بين الرجل والمرأة بعُث رجل من قبل المرأة ورجل من قبل الرجل، فيجعل الزوج أمره بيد الرجل إن رأى فرق وإن رأى جمع. قلت: فإن لم يجعل الزوج أمره في يد الرجل؟. قال: الأمر إلى الحاكم يفعل ما يرى.
حدثنا سعيد بن منصور قال: ثنا حماد بن زيد، عن أيوب، عن ابن سيرين، عن','2','631',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (461,'عبيدة السلماني قال: أتى عليا رجل وامرأة مع كل واحد منهما قيام من الناس، فبعث حكمًا من أهلها وحكما من أهله، ثم قال: للحكمين تدريان ما عليكما عندي؟ إن رأيتما إن تفرقا ففرقتما وإن رأيتما إن تجمعا جمعتما، فقالت المرأة: رضيت بكتاب الله عليّ ولي. وقال: الزوج أما الفرقة فلا. فقال علي كلا والله لا تنقلب حتى تقر بمثل ما أقرت به.
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ثنا صالح بن مسلم قال: سألني الشعبي، عن رجل وامرأة جعلا أمرهما إلى حكمين إن شاءا فرقا وإن شاءا','2','632',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (462,'جمعا، ثم بدا للرجل وامرأته إن يصطلحا وأن يرجعا فأبا الحكمان وقالا: ليس إليكما من ذلك شيء إنما الأمر إلينا، فقلت: ما أدري ما أقول فما تقول أنت؟ فقال: الرجل وامرأته على رءوس آخرهما ما لم يتكلم الحكمان بشيء، فإذا تكلما فلا شيء.
قلت لإسحاق: رجل يخلع ابنته من زوجها وهي مدركة، أو غير مدركة، أو الأخ هل يجوز ذلك الخلع؟ قال أبو يعقوب: الأب على البكر- يعني: يجوز أن يخلعها برضاها إن كانت مدركة، وإن كانت صغيرة جاز؛ لأن الخلع كالبيع يجوز بيع الأب على الصغار. قال: وأما المهر فإذا أقر الأب بالقبض فهو جائز. قلت: فإذا أدركت الصغيرة وطلبت المهر فأقام الزوج شاهدين على إقرار الأب بالقبض ذهب إلى أنه جائز. قلت لإسحاق: امرأة اختلعت من زوجها وهو مريض فمات، أو ماتت هل يتوارثان؟ قال: لا يتوارثان هي فرت من الميراث، وكذلك الزوج لا يرثها. حدثنا أبو هشام قال: حدثنا حسان، عن يونس بن يزيد، عن الزهري في امرأة اختلعت من زوجها وهي مريضة، قال: يكون ذلك في ثلثها لا يعدوه إن أجاز الورثة.','2','633',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (463,'<span data-type="title" id=toc-177>٩٨ - باب: تخيير الغلام بين أبويه</span>
قلت لأحمد بن حنبل: إن النبي - صلى الله عليه وسلم - خيّر غلامًا بين أبويه. كيف التخيير؟ قال: إذا بلغ سبع سنين خير، فإن شاء كان مع أبيه وإن شاء كان مع أمه. قلت: وقِيل ذلك؟. قال: يكون مع الأم إلى سبع سنين قلت: فالجارية؟ قال: والجارية تكون مع الأم حتى تبلغ ما يجوز يزوجها، ثم تكون مع الأب. قلت: ست سنين قال: ست وسبع.','2','634',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (464,'وسألتُ إسحاقَ قلتُ: إلى متى يكون الصبي والصبية مع الأم إذا طلقت؟ قال: أحب إلىّ أن يكون مع الأم إلى سبع سنين ثم يخير. قلت له: أترى التخيير؟ قال: شديدًا، قلت: فأقل من سبع سنين، قال: قد قال بعضهم: خمس. ولكن أنا أحب إلى سبع.','2','635',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (465,'حدثنا يحيى الحماني قال: ثنا ابن عيينة، عن يزيد بن يزيد بن جابر، عن إسماعيل بن عبيد الله، عن عبد الرحمن بن غنم قال: شهدت عمر بن الخطاب خير غلامًا بين أبويه. حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنبأ خالد الحذاء، عن الوليد بن مسلم قال: اختصموا إلى عمر بن الخطاب في يتيم، فخيره','2','636',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (466,'فاختار أمه على عمه فقال عمر: إن لطف أمك خير من خصب عمك.
حدثنا يحيى بن عبد الحميد وسعيد بن منصور قالا: ثنا سفيان بن عيينة، عن زياد بن سعد، عن هلال بن أبي ميمونة، عن أبي ميمونة، عن أبي هريرة أن رسول الله - صلى الله عليه وسلم - خير غلامًا بين أبيه وأمه.
حدثنا أبو معن، نا يحيى بن سعيد القطان قال: ثنا يونس بن عبد الله الجرميّ','2','637',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (467,'قال: حدثني عمارة بن ربيعة أنه خاصم فيه عمه إلى علي بن أبي طالب، قال: فخيرني عليٌ ثلاثًا كلهن أختار أمي، وشهد أخ لي صغير فقال: عليٌ إذا بلغ هذا مبلغ هذا خير. حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ثنا أشعث، عن الحسن أن الأب أحق بالولد من الأم، إلا أن تكون مرضعا، والأم أحق به من العصبة حتى تتزوج.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أنبأ يونس وهشام، عن ابن','2','638',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (468,'سيرين، عن شريح قال: الصبية مع أمهم ما كانت الدار واحدة ومعهم من الأموال ما يسعهم، فإذا تفرقوا فالأولياء أحق.
حدثنا هشام بن عمار قال: حدثنا محمد بن شعيب، عن ابن لهيعة، عن عمر مولى غفرة أنه أخبره، عن زيد بن إسحاق بن جارية الأنصاري أن عمر بن الخطاب حين خاصم إلى أبي بكر في ابنه عاصم فقضى أبو بكر لأمه، ثم قال: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((لا والدة عن ولدها)).','2','639',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (469,'حدثنا سعيد بن منصور قال: حدثنا أبو عوانة، عن أشعث بن سليم أن جدة وأمًا اختصما إلى شريح فقالت الجدة:
أبا أمية أتيناك ... وأنت المرء يأتيه
أتاك ابن وأماه ... وكلتانا تفديه
تزوجت فهاتيه ... ولا يذهب بك التيه
فلو كنت تأيمت ... ما نازعتكم فيه
ألا أيها القاضـ ... ـي هذه قضيتي فيه

فقالت الأم:

ألا أيها القاضي ... قد قالت لك الجدة
قولًا فاستمع مني ... ولا تنُظرني ردَّه
أعزي النفس عن ابني ... وكبدي حملت كره
فلما كان في حجري ... يتيما ضائعًا وجده
تزوجت رجا الخير ... من يكفيني فقده
ومن يكفل لي رفده ... ومن يظهر لي وده

قال شريح:

قد سمع القاضي ما قلتما ... فقضى بينكما ثم فصل
بقضاء بيَّن وعلى ... القاضي جهد إن عقل
ثم قال: للجدة بيني بالصبي ... وخذي ابنك من ذات العلل
إنها لو صبرت كان لها ... قبل دعواها تبغيها البدل','2','640',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (470,'<span data-type="title" id=toc-178>٩٩ - باب: رضاع الصبي</span>
وسألت إسحاق قلت: رجل طلق امرأته ثلاثًا ولها صبي رضيع، والأب فقير، والأم تأبا أن ترضع الصبي هل تجبر الأم على رضاع الصبي؟ قال: لا. قلت: إن الأب فقير؟ قال: يحتال، وقال: ينظر إلى ما ترضع به غيرها، فتدفع إلى الأم فترضع الصبي.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن المثنى، عن عمرو بن شعيب قال: أخبرني سعيد بن المسيب وسليمان بن يسار أن عمربن الخطاب قضى في غلام توفي أبوه وهو مسترضع ولم يترك أبوه مالًا فقضى أن رضاعه على ورثته، ثم قال: لو لم أجد ورثة لجعلته على عاقلته.','2','641',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (471,'قال: وسمعت سفيان يقول في صبي له عم وأم وهي ترضعه قال: يكون رضاعه عليهما يوضع على العم بقدر ما ترث، لأن الأم تجبر على النفقة على ولدها. قال سفيان: وكان بعض الفقهاء يقول: يكون عليهم نفقته بقدر مواريثهم منه.','2','642',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (472,'<span data-type="title" id=toc-179>١٠٠ - باب: كسب المرأة في بيت زوجها</span>
سألت أحمد عن كسب المرأة فقال لها.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك قال: سمعت سفيان يقول: لا يجبر الرجل امرأته على شيء من عمل البيت إنما عليها إلى فراشه، ولا تخرج إلا بإذنه، ولا تُدخل عليها من يكره زوجها، وإن شاء دفع إليها زوجها طعامًا دقيقا قال: وكان ابن أبي ليلى يقول: إن ترضع ولدها من جبرت على ذلك. حدثنا مسدد قال: ثنا عيسى بن يونس قال: حدثنا أبو بكر بن عبد الله بن أبي مريم، عن ضمرة بن حبيب قال: قضى رسول الله - صلى الله عليه وسلم - على ابنته فاطمة بخدمة البيت وقضى على علي بما كان خارجًا من البيت.','2','643',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (473,'<span data-type="title" id=toc-180>١٠١ - باب: إذا استدانت المرأة على زوجها وهو غائب</span>
سألت أحمد قلت: رجل غاب عن أهله، فاستدانت بقدر نفقتها؟ قال: يجبر الزوج على قضاء ذلك. قلت: فإن كان أكثر من نفقتها؟ قال: لا، إلا ما تكتفي به. قلت: وكذلك إن كان له ولد صغار فاستدانت لهم؟ قال: نعم كل هذا على الزوج. حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن سفيان، عن منصور، عن إبراهيم، قال: إذا غاب الرجل، عن امرأته فأكلت من مالها لم يحسب عليه، وإن أدانت بالقصد كان عليه.
حدثنا المسيب قال: حدثنا ابن مبارك، عن حماد بن زيد قال: سألت خالد الحذاء عن رجل غاب ووكل وكيلًا أن ينفق على امرأته فجعل ينفق بعد','2','644',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (474,'موته فكان أبو العالية وأبو قلابة ومحمد يقولون: إذا مات الميت فماله ميراث من أخذ منه شيئًا فمن نصيبه.
قال المسيب: وحدثنا ابن مبارك، عن سفيان، عن محمد بن علي، عن الحكم، عن إبراهيم في رجل قال لرجل: أنفق على امرأتي، فأنفق وهو ميت وهو لا يشعر. قال: ما أنفق في حياة الزوج فهو عليه، وما أنفق بعد موته فهو عليها، وقول إبراهيم الأول أحب إليّ.
وقال سفيان: وأرى أن يلزم المأمور ما أنفق عليها بعد موته؛ لأنه ضيع.','2','645',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (475,'<span data-type="title" id=toc-181>١٠٢ - باب: من عجز عن نفقة امرأته</span>
سُئلَ أحمد، عن الرجل يعجز، عن نفقة امرأته. قال: يفرق بينهما تجلس على الخسف بغير شيء. قلت: فإن قِيل له: طلقها فقال: لا. أفعل بطلاق القاضي عنه. قال: فيه اختلاف وذهب إلى أنه يجيز على الطلاق.
حدثنا عبد الله بن الزبير الحميدي، قال: حدثنا سفيان، قال: حدثنا أبو الزناد، قال: سألت سعيد بن المسيب قلت الرجل لا يجد ما ينفق على','2','646',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (476,'امرأته قال: يفرق بينهما قلت: سُنّة قال: سُنّة.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أبنأ يونس، عن الحسن قال: ينفق عليها أو يطلقها.
حدثنا سعيد، قال: ثنا هشيم، عن مطرف، عن الشعبي قال: يُنفَق عليها إن وجد فلا يُكلف ما لا يطيق.
حدثنا المسيب قال: ثنا ابن مبارك، عن سفيان، عن يحيى بن سعيد، عن سعيد','2','647',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (477,'بن المسيب قال: يفرق بينهما إذا لم يجد نفقة. قال: وكتب عمر بن عبد العزيز فيها: {لا يكلف الله نفسًا إلا ما أتاها} وبه يأخذ سفيان.','2','648',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (478,'<span data-type="title" id=toc-182>١٠٣ - باب: المرأة تتزوج ولها ولد ترضعه أله إن يمنعها</span>؟
قال: نعم له يمنعها؛ لأن حجرها له وهو أحق بها. قلت: وهذا ولدها. قال: يسترضع له أبواه. حدثنا محمد بن بشار، قال: حدثنا يحيى بن سعيد، قال: ثنا سفيان، قال: حدثنا الشيباني، عن رجل، عن عبد الله بن عتبة في رجل تزوج امرأة ومعها ولد. قال: لا ترضعه إلا بإذنه.','2','649',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (479,'<span data-type="title" id=toc-183>١٠٤ - باب: المرأة تعصي زوجها</span>
قلت لأحمد: المرأة تعصي زوجها هل لها نفقة؟ قال: لا.
حدثنا محمد بن مرزوق قال: ثنا أبو قتيبة، عن أبي هلال، عن هارون قال: قلت للحسن امرأة خرجت مراغمةً لزوجها ألها نفقة؟ قال: نعم. قلت: وما نفقتها؟ قال: جوالق من تراب.','2','650',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (480,'حدثنا محمد بن بشار قال: أخبرنا يحيى بن سعيد قال: حدثنا سفيان، عن طارق، عن الشعبي في المرأة تستعصي على زوجها قال: ليس لها نفقة وإن مكثت عشرين سنة.','2','651',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (481,'<span data-type="title" id=toc-184>١٠٥ - باب: من يجبر على نفقة المرضع</span>
قلت لأحمد: فأجر الصبي المرضع؟ قال: من نصيب الصبي إن كان له مال، وإلا فعلى الورثة بقدر سهامهم.
وسألت إسحاق عن أجر المرضع قال: من نصيب الصبي إن كان له مال، وإلا فعلى الورثة بقدر سهامهم.
حدثنا سعيد بن منصور قال: حدثنا سفيان، عن ابن جريج، عن عمرو بن شعيب، عن سعيد بن المسيب أن عمر بن الخطاب جبر عصبة الصبي أن','2','652',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (482,'ينفقوا عليه الرجال دون النساء.
حدثنا سعيد بن منصور قال: ثنا جرير بن عبد الحميد، عن منصور ومغيرة، عن إبراهيم في قوله: {وَعَلَى الْوَارِثِ مِثْلُ ذَلِكَ} [البقرة: ٢٣٣] قال: على الوارث إذا لم يكن للصبي أب ولا مال رضاع الصبي.','2','653',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (483,'<span data-type="title" id=toc-185>١٠٦ - باب: قول النبي - صلى الله عليه وسلم - في مذمة الرضاع</span>
قلت لأحمد: قول النبي - صلى الله عليه وسلم - في مذمة الرضاع غرة عبد أو أمة قال: هذا إذا كان للرجل ولد فأرضعته امرأة، فإنها إذا فطمته وفرغت من رضاعه فينبغي لأبي الصبي أن يعطي الظئر غرة عبد أو أمة.
حدثنا أبو بكر الحميدي قال: ثنا سفيان، عن هشام بن عروة، عن أبيه، عن الحجاج الأسلمي، عن أبيه قال: قلت: يا رسول الله، ما يذهب عني مذمة الرضاع؟ قال: ((الغرة العبد أو الأمة)).','2','654',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (484,'حدثنا المسيب قال: حدثنا ابن مبارك، عن سفيان، عن منصور، عن إبراهيم قال: كانوا يستحبون أن يرضخوا للظئر عند الفطام. قِيل لإسحاق: صبي له أم وخالة على من نفقته؟ قال: على الأم. قِيل: فخال وعم؟ قال: النفقة على العم.
حدثنا محمد بن عبد الأعلى قال: حدثنا معتمر بن سليمان قال: سألت يونس عن يتيم له أم وعم، ولأمه ميسرة قال: كان الحسن يقول: النفقة على العم.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن أنه كان يأخذ كل وارث بنفقة من يرث أبًا كان أو غيره، وإن كان المأخوذ يتيمًا وكان يأخذ إلا العصبة،','2','655',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (485,'وكان يأخذ ممن يرث الثلث ثلاثًا، وممن يرث النصف نصفاً، وكان الرجل بنفقة مولاه إذا كان فقيراً؛ وكذلك ابن أخيه إذا كان فقيراً؛ لأنه يرثه، وكان يأخذ الجد أبا الأب بالنفقة على بني ابنه وأبوهم حتى إذا لم يكن للأب ما يغنيهم وكان عند الجد غناء وكان يأخذ الرجل بنفقة امرأته إذا طلقها ثلاثًا وهي مرضع فإذا فطمت فالأب أحق بالصبي من أمه، وأن الوالد يأخذ من مال ولده ما شاء، وأن الأم تأخذ كما يأخذ الآباء، وأنه كان لا يُرى على العبد نفقة ابنه إذا كان حراً، ولا على مولى الأب وإن كان أذن له في التزويج إلا الرضاع.
حدثنا أبو معن قال: حدثنا وهب بن جرير قال: حدثنا شعبة، عن مغيرة، عن إبراهيم في جارية لها أم ولها موالِ وليس لها مال قال: نفقتها على أمها.','2','656',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (486,'<span data-type="title" id=toc-186>١٠٧ - باب: إذا دفع المهر أيحمل امرأته حيث شاء</span>؟
قلت لإسحاق: فإذا دفع مهر امرأته، فله أن يحملها حيث شاء؟ قال: ليس له أن يحملها حيث شاء على المضرة. قال: وللزوج أن يقول للأب سلم إلى المرأة حتى أدفع إليك المهر.
حدثنا المسيب قال حدثنا إبن مبارك، عن الحارث بن عبد الرحمن قال: أخبرني مسلم بن يسار قال: سألت سعيد بن المسيب عن الرجل يتزوج المرأة فيشترط له أن لا يخرجها؟ قال: إذا جمع الرجل امرأته فهي أولى بها إن شاء خرج بها إذا أوفاها.','2','657',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (487,'<span data-type="title" id=toc-187>١٠٨ - باب: ولد أهل الذمة يسلم أحد أبويه</span>
سألت أحمد عن النصرانية تسلم قبل زوجها ولها ولد صغار. قال: ولدها معها ويجبر الأب على النفقة عليهم.','2','658',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (488,'حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن في الصغير يكون بين أبويه وهما كافران ثم يسلم أحدهما. قال: هو مع المسلم منهما.
حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أخبرنا عثمان البتي. قال: أخبرني عبد الحميد بن سلمة الأنصاري أن جده أسلم على عهد النبي - صلى الله عليه وسلم - وأبت امرأته أن تسلم فاختصما إلى رسول الله - صلى الله عليه وسلم - في الولد فقال رسول الله - صلى الله عليه وسلم -: ((إن شئتما خيرتماه)) فقعد الأب في ناحية والأم في','2','659',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (489,'ناحية فتوجه الصبي نحو أمه فقال رسول الله - صلى الله عليه وسلم -: ((اللهم اهده)) فرجع إلى أبيه.','2','660',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (490,'<span data-type="title" id=toc-188>١٠٩ - باب: من كان تحته نصرانية مع من يكون الولد</span>
قلت لأحمد: رجل تحته نصرانية وهو مسلم، فطلقها مع من يكون الولد؟ قال: مع الأب المسلم قلت وإن كان صغيراً؟ قال: نعم.
حدثنا عمران بن يزيد بن خالد قال: حدثنا الوليد بن مسلم، عن أبي عمرو الأوزاعي أن العم والد، فإذا أسلم العم النصراني وبنو أخيه أطفال جبروا على الإسلام إذا كان أبوهم ميت، فإن كان حيًا لم يعرض لهم.
إن كانت أمهم حية فهم على دين أمهم، وإن كانت قد ماتت ألحقوا بجدهم مسلمين. قال الوليد: قال الأوزاعي: والجد أبو الأب إذا أسلم فهو أولى بولد ولده الصغار من أمهم إذا كان أبوهم قد مات. قال عمرو: والأخ يجرهم إذا كان أبوهم قد مات، والعم يجرهم والخال بتلك المنزلة.','2','661',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (491,'قال الوليد: ويتأول أبو عمرو القرآن ولم أسمع منه أمر الخال.
قِيل لأبي عمرو: فنصراني تزوج أمة نصرانية، ثم أسلم زوجها قال: لا يلحق به ولده.','2','662',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (492,'<span data-type="title" id=toc-189>١١٠ - باب: المرأة تسلم قبل زوجها هل ترجع إليه</span>
سألت أحمد قلت: امرأة تسلم قبل زوجها في دار الإسلام؟ فقال: اختلف الناس في ذلك. قِيل: فلا تقف منه على شيء؟ قال: هذه مسألة مشتبكة. قال قوم: إن أسلم زوجها قبل أن تنقضي عدتها رجعت إليه. وقال قوم قد انقطع الذي بينهما. ولم يقف منهما شيء.','2','663',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (493,'وقال في امرأة المرتد نحو ذلك.','2','664',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (494,'وسُئلَ أحمد مرة أخرى عن المرأة تسلم قبل زوجها، والرجل يسلم قبل امرأته قال: اختلف الناس في هذا. ولم يجب فيه، وقال مرة: هذه مسألة مشتبكة.
حدثنا أحمد، قال: حدثنا عباد، قال: حدثنا خالد، عن عكرمة، عن ابن عباس','2','665',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (495,'قال: إذا أسلمت اليهودية والنصرانية قبل زوجها فهي أملك بنفسها. وسألتُ أبا ثور قلت: المرأة تسلم قبل زوجها؟ قال: قال الشافعي، ومالك: إن أسلم الرجل قبل أن تنقضي عدتها فهي امرأته ويقول هؤلاء: يدعوه الإمام فيعرض عليه الإسلام، فإذا فعل ذلك لم تحل له بعد ذلك.
قلت: فما تقول أنت؟ قال: أقول إذا أسلمت قبله انفسخ النكاح بينهما لا ترجع إليه البتة قلت: فإن أسلم الزوج قبلها؟ قال: إن كانا يهوديين أو نصرانيين فهما على النكاح، وإن كانا مجوسيين فقد وقعت الفرقة بينهما.
قلت لأبي ثور: المرتد كيف تصنع امرأته؟ قال: كان الشافعي يقول: إن عاد إلى الإسلام قبل أن تنقضي عدة امرأته فهي امرأته. وقال مالك: انفسخ النكاح قلت: فما تختار أنت؟ قال: انفسخ النكاح بينهما.
حدثنا أبو هشام قال: ثنا حسان، عن سفيان، عن عمرو بن ميمون، عن','2','666',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (496,'عمر بن عبد العزيز قال: إذا أسلم في العدة فهو أحق بها. قال حسان: قال سفيان: ونحن نقول إذا أسلمت عرض عليه الإسلام، فإن أبي أن يسلم فرق بينهما نقول هي امرأته حتى يعرض عليه فإن أسلم وإلا فرق بينهما فإن أسلم بعد ذلك فلا شيء إلا بنكاح جديد. حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن أن المشركين إذا أسلما جميعًا في يوم واحد فهما على نكاحهما من يهودي أو نصراني أو مجوسي، فإن أسلمت المرأة قبل زوجها فقد فرق الإسلام بينهما وليس بطلاق، وعدتها عدة المطلقة، ولا تزوج غيره حتى تعتد، وإن أسلم زوجها فله أن يتزوجها في عدتها منه، فإن كان دخل بها فلها الصداق كاملاً، وإن لم يكن دخل بها فلا صداق لها ولا عدة','2','667',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (497,'عليها، ونرى إسلامهما فرقة بائنة منه، ولا نراه طلاقًا، ونقول فرق الإسلام بينهما، فإن أسلم فتزوجها فهي على ثلاث تطليقات، وكذلك إن أسلم المجوسي ولم تسلم امرأته، وإن أسلم اليهودي أو النصراني ولم تسلم امرأته فهما على نكاحهما.
حدثنا أبو هشام قال: حدثنا حسان قال: قال سفيان الثوري في رجل ارتد عن الإسلام أو ارتدت امرأته قال: قال سفيان: إذا ارتد واحد منهما انقطعت العصمة. قلت: فإن قتل واحد به منهما هل بينهما ميراث؟ قال: ما كانت المرأة في العدة فلها الميراث وعليها عدة المتوفى عنها زوجها. قلت: فإن لم يكن دخل بامرأته؟ قال لها نصف الصداق إذا كان هو المرتد، وإن ارتدت هي فلا شيء لها، لأن الفرقة جاءت من قبلها، وإن كان دخل بها فلها الصداق كاملاً.
قلت: إن تاب زوجها، ثم تزوجها على كم تكون، عنده؟ قال: على تطليقتين ومضت واحدة؛ لأن كل فرقة تجيء من قبل الرجل فهي تطليقة، وما كان من قبل النساء فلا يكون طلاقًا.','2','668',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (498,'قلت: من يرث المرتد؟ قال: ورثته، وهو بمنزلة المريض إن قتل وامرأته في العدة ورثته. قِيل لأحمد: فتسلم المرأة، ثم يسلم الرجل وهي في العدة، أو قبل أن تزوج أو ما اختلف الناس فيه ما تختار من هذا؟ قال: ما أدري. وسألت إسحاق، عن امرأة من أهل الذمة أسلمت قبل زوجها قال: إن أسلمت قبل زوجها ثم أسلم الزوج في العدة فأنه يراجعها، وإن كان بعد انقضاء العدة لم يراجعها. حدثنا محمود قال: حدثنا عمر، عن الأوزاعي قال: سألت الزهري عن نصراني تحته نصرانية فأسلمت، فاعتدت حيضة، أو حيضتين، أو لم تحض حتى أسلم أهو أحق بها ما لم تنقض/١٠٧/.','2','669',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (499,'قال حدثنا الوليد بن مسلم قال: ثنا عبد الرحمن بن يزيد بن جابر، عن مكحول أنه قال في متاع البيت إذا مات الرجل عن امرأته، أو طلقها: فما كان في بيتها من متاع النساء فهو لها، وما كان من متاع الرجال فهو له، وما كان نسبته متاع الرجال والنساء فهو لمن أقام البينة، فإن لم يكن بينة فهو لمن حلف عليه، وإلا فهو بينهما.
حدثنا هشام بن عمار قال: حدثنا خالد بن يزيد بن أبي مالك، عن أبيه قال: كل شيء من متاع البيت مما يكون للنساء فهو للمرأة، وكل شيء','2','677',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (500,'مما يكون للرجال من المتاع فهو للرجل، ومن متاع الرجل اللحف، والأردية، والخدم، والدور، والأرضين، والسلاح، وآنية الطعام والشراب، والطعام والشراب، والتوابيت، وقدور الطلاء إن كان له كرم، وإن كان الكرم للمرأة فالقدور للمرأة، وسائر النحاس كله للمرأة، والفرش، والثياب، والوسايد والأنماط، والستور، والحلي كله للمرأة.
حدثنا أحمد بن عبدة بن موسى قال: أنبأ سعيد ابن عمرو الزبيري قال: ثنا عبد الرحمن بن أبي الزناد أن أبا الزناد عبد الله بن ذكوان قال: كان سعيد بن المسيب، وعروة بن الزبير، والقاسم بن محمد، وأبو بكر بن عبد الرحمن بن الحارث بن هشام وخارجة بن زيد بن ثابت، وعبيد الله بن عبد الله بن','2','678',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (501,'عتبة بن مسعود، وسليمان بن يسار يقولون في الرجل والمرأة يتجاحدان المتاع بينهما وهما مجتمعان، أو بعد أن يتفرقا بموت أو طلاق: أن للمرأة ما كان من متاع البيت الذي هو من متاع النساء، ويجعل مع ذلك يمين، وأن للرجل ما كان من متاع الرجل، ويجعل مع ذلك يمينه. حدثنا سعيد بن منصور قال: حدثنا هشيم قال: أخبرني منصور، عن الحسن قال: للمرأة ما أغلقت عليه باب: ها، إلا مصحف الرجل وسلاحه. وقال ابن سيرين: ما كان من مهر فهو لها، وما كان من غير ذلك فهو ميراث.','2','679',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (502,'كتاب: الإيلاء','2','680',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (503,'سُئلَ أحمد عن الإيلاء فلم يره تطليقة وقال: إذا مضت أربعة أشهر فإما أن يفئ وإما أن يطلق وسمعت أحمد مرة أخرى يقول في الإيلاء: إذا حلف على أكثر من أربعة أشهر','2','681',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (504,'فإنه إذا مضت أربعة أشهر فإنه يوقف فإما أن يفئ إليها وإما أن يطلق. قلت: فإن سكتت المرأة عنه؟ قال: لا يوقف إلا أن تطلب هي ذاك إنما الأمر إليها. حدثنا أحمد قال: حدثنا سفيان بن عيينة قال: ثنا الشيباني، عن الشعبي، عن عمرو بن سلمة بن (حارث) (١) قال: قال علي في الإيلاء: إذا آلى من امرأته وقفه حتى تبين رجعة أو طلاقًا.
_________
(١) كذا بالأصل.','2','682',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (505,'حدثنا أبو بكر الحميدي، قال: حدثنا سفيان، قال: حدثنا يحيى بن سعيد، عن سليمان بن يسار قال: أدركت بضعة عشر رجلًا من أصحاب النبي - صلى الله عليه وسلم - من الأنصار كلهم يقول في المولي يوقف بعد الأربعة الأشهر فإما أن يفئ، وإما أن يطلق. حدثنا أحمد بن حنبل، قال: ثنا بهز بن أسيد، قال: حدثنا همام، قال: سمعت قتادة يرويه، عن سعيد بن المسيب، عن أبي الدرداء قال في','2','683',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (506,'الإيلاء يوقف فإما أن يمسك، وإما أن يطلق وكان سعيد يأخذ به·حدثنا أحمد بن يونس، قال: ثنا ليث بن سعد، عن نافع أن عبد الله بن عمر كان يقول في الإيلاء الذي الله لا يحل لأحد بعد الأجل إلا أن يمسك بمعروف أو يعزم الطلاق كما أمره اللهحدثنا أحمد، قال: حدثنا وكيع، قال: ثنا مسعر، عن حبيب بن أبي ثابت،','2','684',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (507,'عن طاوس، عن عثمان أنه كان يقول في المولى بقول أهل المدينة يوقفه. حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أخبرنا ابن أبي ليلى، عن الحكم، عن مقسم، عن ابن عباس قال: عزم الطلاق انقضاء الأربعة أشهر والفيء الجماع.','2','685',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (508,'<span data-type="title" id=toc-190>باب: كيف الإيلاء</span>
سمعت أحمد يقول: إذا حلف على أقل من أربعة أشهر فإنه ليس بإيلاء، وكذلك إن حلف على أربعة أشهر سواء، لأنه إذا مضت أربعة أشهر فليس يحجره عن تحليفها شيء، فالإيلاء لا يكون إلا أكثر من أربعة أشهر، ولا يذهب إلى قول من يقول إذا مضت أربعة أشهر فهي تطليقة، وقال: يذهب أهل الكوفة إلى قول ابن مسعود·حدثنا أحمد، قال: ثنا سفيان، عن عمرو سمعت سعيد بن جبير يقول: جاء رجل إلى علي فقال: حلفت إن لا آتي امرأتي سنتين؟ قال: ما أراك إلا قد دخل عليك إيلاء· قال: إنما قلت من أجل الرضاع قال: فلا إذًا.','2','686',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (509,'<span data-type="title" id=toc-191>باب: من ترك امرأته فلم يقربها أشهرًا كثيرة</span>
قلت لأحمد: رجل ترك امرأته منذ أشهر ولم يقربها؟ قال: قد أساء، ولا يدخل عليه إيلاء.
حدثنا أحمد قال: ثنا معاذ بن معاذ قال: حدثنا أشعث، عن الحسن قال: كانت لأنس بن مالك امرأة في خلقها شيء، فكان يهجرها الأربعة الأشهر وأكثر من ذلك فيمر بها فتعلق بثوبه، وتقول يا ابن مالك الله الله يابن مالك، فما يكلمها.','2','687',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (510,'<span data-type="title" id=toc-192>باب: المولى يوقف فلم يفء/١٠٩/ولم يطلق</span>
قلت لأحمد: المولى يوقف فلم يفء ولم يطلق؟ قال: يطلق السلطان عنه، لا يترك·وسألت أحمد مرة أخرى قلت: الرجل يولي ثم يوقف بعد أربعة أشهر كيف يراجع؟ قال: إذا كان له عذر من مرض أو غيره تكلم وقال: قد راجعت، وإن لم يكن له عذر فمذهبه الجماع. حدثنا أحمد قال: ثنا هشيم، عن حصين، عن الشعبي، عن مسروق قال: الفئ: الجماع.','2','688',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (511,'حدثنا أحمد قال: ثنا هشيم، عن يونس وعوف وأبي حرة، عن الحسن قال: الفئ: الإشهاد إذا كان عذر من مرض، أو حبس، أو نفاس.
حدثنا أحمد قال: ثنا أبو معاوية قال: ثنا الأعمش، عن إبراهيم، عن أبي الشعثاء قال: نزل بي ضيف فآلا من امرأته، فنفست، فأراد أن يفيء فلم','2','689',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (512,'تستطيع أن يقربها من أجل نفاسها، فأتى علقمة فذكر ذلك له فقال له علقمة: أليس قد فئت بقلبك ورضيت؟ قال: قد فئت من امرأتك.
حدثنا أحمد، قال: حدثنا جرير، عن منصور، عن إبراهيم، عن أبي الشعثاء، قال: آلا رجل من الحي من امرأته فنفست، وأراد أن يفيء فخشي أن تمضي الأربعة الأشهر قبل أن يفيء فسألني عن ذلك فقلت: لا أدري، ولكن سأسأل لك، قال: وعلقمة، والأسود، ومسروق يومئذ أحياء فسألتهم','2','690',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (513,'فقالوا: إذا هو فاء بلسانه أجزأه ذلك من الوقوع عليها.','2','691',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (514,'<span data-type="title" id=toc-193>باب: إذا حلف بالطلاق أن لا يقر بها سنة</span>
سألتُ أحمدَ قلتُ: فإن حلف بالطلاق على سنة؟ قال: إن قربتك إليَّ سنة فأنت طالق. قال: هذا لا يكون إيلاء، وليس هو الإيلاء، إنما هو يمين قال الله: {لِّلَّذِينَ يُؤْلُونَ مِن نِّسَآئِهِمْ} [البقرة: ٢٢٦] يقسمون وهذا ليس يمين.
حدثنا سعيد بن منصور، قال: حدثنا سفيان، عن عمرو قال: كان ابن','2','692',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (515,'عباس يقر الذين يقسمون من نسائهم تربص أربعة أشهر وإن عزموا السراح.
حدثنا المسيب بن واضح، ثنا ابن مبارك، عن سعيد، عن قتادة عن الحسن في رجل قال لامرأته: إن قربتك إليَّ سنة فأنت طالق ثلاثًا. قال: إذا مضت أربعة أشهر فهي تطليقة بائنة ويتزوجها إن شاء.
قلتُ لإسحاق: رجل طلق امرأته تطليقة وآلا منها في قول من يرى الإيلاء طلاقًا، فاعتدت، فتزوجها في عدتها، ثم طلقها قبل أن يدخل بها، قال في: مهر ونصف في قول من يقول تبين بالإيلاء.
حدثنا سعيد بن منصور قال: حدثنا حماد بن زيد، عن منصور، عن إبراهيم أنه قال في ذلك: لها المهر كاملًا وتستأنف العدة.','2','693',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (516,'قال: وقال الحسن وعامر: لها نصف الصداق، وتكمل مابقي من عدتها· قال حماد: قلت لمنصور: أي القولين أحب/١١٠/إليك؟ قال: قول الحسن وعامر.','2','694',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (517,'<span data-type="title" id=toc-194>باب: تفسير قول عليَّ الإيلاء والطلاق أنهما كفرسي رهان</span>
قلتُ لإسحاق: قول علي بن أبي طالب في الايلاء والطلاق: إنهما كفرسي رهان، فسره لي قال: هذا لمن لا يرى أن يوقف، ويرى الطلاق إذا طلق قبل الأربعة الأشهر وقع الطلاق، وإن مضت أربعة أشهر وقع الإيلاء.
حدثنا سعيد بن منصور، قال: أخبرنا هشيم قال: أخبرنا محمد بن سالم، عن الشعبي في رجل ألا من امرأته ثم طلقها، قال: قال علي: يستبقان كفرسي رهان قال: قال ابن مسعود: الطلاق يهدم الإيلاء.','2','695',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (518,'<span data-type="title" id=toc-195>باب: إيلاء العبد</span>
سألتُ أحمدَ قلتُ: العبد يولي من امرأته قال: أنا أقول إن الطلاق بالرجال والعدة بالنساء فمن قال: هذا فإذا مضى شهران وقف، ثم قال: أنا ربما ذهبت إلى إن أجعله مثل الحر لا يوقف إلى أربعة أشهر. وكأنه اختاره بعد.
وسألتُ إسحاق عن إيلاء العبد من الحرة والأمة قال: شهرين به يقع الطلاق وغيره.','2','696',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (519,'حدثنا أحمد قال: ثنا هشيم، عن يونس، عن الحسن ومغيرة، عن إبراهيم أنهما قالا: إيلاء العبد من الحرة أربعة أشهر، ومن الأمة شهران.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن سعيد، عن أيوب، عن نافع أن الربيع بنت مسعود بن عفراء أتت ابن عمر فأخبرته: إن أخاها خلعها بمالها وكان زوجها فاجرًا يشرب الشراب، فرفع ذلك إلى عثمان فأجازه، وأمرهاأن تتحول من بيت زوجها، وأن تعتد حيضة· فقال ابن عمر رحمه الله: عثمان كان من خيارنا وأفقهنا. وكان لا يعجبه قوله في العدة.','2','697',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (520,'كتاب الظهار','2','698',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (521,'سألت أحمد قلت: الظهار من الحرة والأمة سواء؟ قال: إذا كانت الأمة امرأته تزوجها بمهر فهو سواء، وإذا كانت ملك يمينه أو أم ولده فليس منها ظهار.
وسمعت أحمد مرة أخرى يقول: الظهار من الحرة والأمة سواء إذا كانت الأمة امرأته، فإذا كانت سريةً يطأها فليس منها ظهار.','2','699',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (522,'وسألت أحمد مرة أخرى، عن الظهار من الأمة إذا كانت ملك يمينه فليس هو ظهار يجزيه كفارة يمين.
حدثنا أبو معن قال: حدثنا معاذ قال: ثنا أشعث قال: سألت عطاء عن رجل ظاهر من امرأته وهي أمة قال: يكفر ما يكفر من الحرة.
حدثنا محمد بن معاوية/١١١/قال: حدثنا ابن لهيعة، عن عمرو بن شعيب، عن أبيه، عن جده قال: لا ظهار من الأمة.
حدثنا محمد بن معاوية قال: حدثنا ابن لهيعة، عن ابن هبيرة قال: سمعت قبيصة ابن ذؤيب يقول: ليس في التظاهر من الأمة','2','700',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (523,'إلا كفارة يمين.
سُئلَ أحمد عن رجل قال لامرأته أنت علي كأمي إن حلفت بالطلاق فحلف بالطلاق قال: يعتق رقبة أو يصوم شهرين.
سُئلَ أحمد، عن المظاهر متى تلزمه الكفارة؟ قال: إذا نوى إن يغشى.','2','701',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (524,'وسمعته مرة أخرى يقول المظاهر إذا نوى إن يجامع فعليه الكفارة، فإن طلق قبل إن يجامع سقطت عنه الكفارة· ومذهبه نوى إن يجامع أو لم ينو إذا طلقها سقطت عنه الكفارة.
قلت لأحمد: ولد الزنا يجوز في كفارة الظهار؟ فمرض عليّ فيه، ولم يجبني جوابًا صحيحًا.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن أن الأعور، والمدبر، وولد الزنا يجزون في كفارة الظهار، والشيخ الكبير،','2','702',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (525,'والصبي الصغير، وكان يكره الأشل، والأعرج، والكفارة في ذلك، ولا يجزون، ولا تجزيء أم الولد، ولا المكاتب في كفارة الظهار، وولد الزنا وولد الرشدة في الكفارات سواء.
حدثنا سعيد بن منصور، قال: حدثنا خالد بن عبد الله، عن مغيرة، عن إبراهيم قال: يعتق في الرقبة المؤمنة من عقل دينه.
سُئلَ أحمد عن الرجل يصوم من كفارة الظهار شهرًا أو أكثر من شهر غير أنه لم يتم شهرين، ثم أيسر قال: يمضي في صيامه.
قِيل: يمضي في صيامه؟ قال: نعم إذا دخل في شيء أتمه ولم ير عليه الكفارة.
وسُئلَ أحمد مرة أخرى، عن الرجل يصوم من كفارة الظهار ثم أيسر؟ قال: يمضي في صومه· قال: وكذلك جميع الكفارات إذا دخل في شيء مضى فيه.','2','703',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (526,'وسألتُ إسحاق عن رجل صام من كفارة الظهار بعضًا ثم أيسر؟ قال: الذي أختار أن يلزمه العتق· وذكر عن مالك وأصحابه قال: يمضي في صيامه.
حدثنا سعيد بن منصور، قال: حدثنا هشيم، قال: أنبأ منصور، عن الحسن قال: يمضي في صيامه.
قلت لأحمد: صام من كفارة الظهار أيامًا ثم مرض؟ قال: إذا صح مضى في صيامه ولا يستأنف.','2','704',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (527,'حدثنا أبو معن قال: حدثنا معتمر، عن إسماعيل بن أبي خالد، عن الشعبي في رجل كان عليه صيام شهرين متتابعين فقضاهما إلا يومين، ثم مرض قال: /١١٢/يقضي (ذلك اليومين) (١).
سألتُ أحمدَ قلتُ: الرجل يقول لامرأته أنت عليّ كعمتي، أو كخالتي؟ قال: هو ظهار. قال: الظهار من كل ذات محرم يحرم عليه نكاحها فهو ظهار.
_________
(١) كذا بالأصل.','2','705',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (528,'وسألت إسحاق، عن رجل قال لامرأته: أنت عليّ كأمك، أو كأختك، أو كجدتك· هل يكون هذا ظهار؟ قال: نعم هو ظهار.
حدثنا سعيد بن منصور، قال: حدثنا هشيم، قال: أنبأ يونس، عن الحسن قال: الظهار من كل ذات محرم.
قلت لأحمد: فإن قال: أنت عليّ كظهر أبي أو ابني أو كظهر رجل؟ قال: قد اختلفوا في هذا· قال بعضهم: هو ظهار. وذهب أحمد إلى أنه ليس بشيء.
حدثنا الحماني قال: حدثنا ابن مبارك، عن سعيد، عن مطر، عن حيان، عن جابر بن زيد قال: الظهار من كل ذات محرم ظهار، وإذا قال: أنت','2','706',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (529,'عليّ كظهر رجل فهو ظهار.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، قال: ثنا ابن جريج قال: قلت لعطاء: ظاهر بذات محرم؟ قال: سواء.
وسُئلَ عطاء، عن الرجل يقول لامرأته: أنت عليّ كأبي، أو كأختي؟ قال: ما هذا؟، ومن يقول هذا؟ فقال: أنت عليّ كابنة خالي· قال: ليس ذلك بظهار، إنما الظهار من ذوات المحرم.
قال ابن المبارك: وسمعت سفيان يقول: إن قال: أنت علي كفخذ أمي، أو ثدييها، أو بطنها· فهو ظهار· قال: وإن قال أنت عليّ كظهر أبي· فليس بظهار.
وعن ابن جريج، عن عطاء قال: إن قال لامرأته: أنت عليّ كأبي. فهو ظهار.','2','707',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (530,'قلت لعطاء: إن قال: أنت عليّ كالدم، أو كلحم الخنزير؟ قال: يمين يكفرها.
سألتُ أحمدَ قلتُ: المظاهر يواقع قبل إن يكفر؟ قال: عليه كفارة واحدة· يذهب إلى حديث سلمة بن صخر، عن النبي - صلى الله عليه وسلم -.
حدثنا يحيى بن عبد الحميد، قال: حدثنا عبد السلام، عن أبي إسحاق بن عبد الله، عن بكير بن الأشج، عن سليمان بن يسار، عن سلمة بن صخر قال:','2','708',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (531,'ظاهرت من امرأتي فوقعت بها قبل أن أكفر فسألت النبي - صلى الله عليه وسلم - فقال: ((عليك كفارة واحدة)).
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أخبرنا يونس، عن الحسن أنه (على) (١) يقول: إذا وقع المظاهر قبل إن يكفر فليمسك عن غشيانها، وليستغفر الله ويتوب، وعليه كفارة واحدة.
سُئلَ أحمد عن رجل ظاهر من أربع نسوة· قال: عليه كفارة واحدة.
_________
(١) هكذا في الأصل ولعلها زائدة.','2','709',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (532,'حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: حدثنا أشعث/١١٣/، عن الحسن في من ظاهر من أربع نسوة بقول واحد. قال: عليه أربع كفارات ويكفر عن أيتهن شاء فيغشاها، ثم أخرى، ثم أخرى حتى يأتي عليهن، ولا يدخل عليه الإيلاء في تركه إياهن حتى يكفر إن جاوز أربعة أشهر.','2','710',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (533,'سألتُ أحمدَ قلتُ: يجوز الصبي في كفارة الظهار؟ قال: فيه اختلاف، وأما في القتل فلا يجوز إلا من قد صلى وصام.
حدثنا أحمد بن حنبل قال: حدثنا هشيم، قال: أنبأ يونس، عن الحسن وأبو حيان، عن الشعبي ومغيرة، عن إبراهيم أنهم كانوا يقولون فيمن قتل مؤمنًا فعليه عتق رقبة قد بلغت، ويجزئ عتق الصغير في كفارة الظهار واليمين.','2','711',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (534,'سُئلَ أحمد عن العبد يظاهر من امرأته؟ قال: فيه اختلاف وذكر - أظنه- عن إبراهيم قال: يصوم شهر، وذهب أبو عبد الله إلى شهرين، وقال: ليس في شهرين اختلاف· وقال: في إيلاء العبد أربعة أشهر.
وسألتُ إسحاق عن ظهار العبد ماذا عليه؟ قال: عليه الصوم شهرين· قلت: وإن كانت امرأته أمة؟ قال: كذلك أيضًا. ذهب إلى أن الحرة والأمة في ذلك سواء.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن في العبد يظاهر؟ قال: إن أعتق عنه مولاه، وإلا صام شهرين حرة كانت امرأته أو أمة.','2','712',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (535,'يذهب أحمد إلى أن العبد يصوم في كفارة القتل والظهار شهرين كما يصوم الحر، وفي كفارة اليمين قِيل: فللسيد أن يحول بينه وبين الصيام؟ قال: لا، لأن الصيام عليه في بدنه.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا أبو عوانة، عن مغيرة، عن إبراهيم قال إذا ظاهر العبد من امرأته وهي أمة صام شهرًا.
حدثنا المسيب بن واضح، قال: ثنا ابن مبارك، عن سفيان، عن يونس، عن الحسن في العبد يظاهر. قال: إذا أذن له سيده في الطعام أجزأه.
قال سفيان: لا يعجبنا إلا الصوم.','2','713',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (536,'قلت لأحمد: الرجل يظاهر من امرأته مرارًا في مجلس؟ قال: يجزئه كفارة واحدة. قلت: فإن كان في مجالس؟ قال: أرجوا أن يجزئه كفارة واحدة ما لم يكفر.','2','714',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (537,'حدثنا أبو معن قال: حدثنا معتمر، عن ليث، عن طاوس والشعبي قالا: إذا ظاهر الرجل من امرأته خمسين مرة قالا: يكفيه كفارة واحدة.
قلت لأحمد: فشيخ ظاهر من امرأته ولا يقدر على العتق، ولا على الطعام، ولا على الصيام· قال: لا بد/١١٤/من واحدة من هذه الثلاث.
سألتُ أحمدَ قلتُ: المظاهر إذا قال: لا أكفر· يجبر على الكفارة؟ قال: نعم.
قلت: يروى عن سعيد بن المسيب في هذا شيء؟ قال: لا أعلم، ولكن يروى عن الحسن وابن سيرين حدثنا عباد بن العوام، عن سفيان بن حسين، عن الحسن وابن سيرين قالا: يستعدي عليه.','2','715',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (538,'وسألت أحمد مرة أخرى فذكر نحوه. وسألتُ إسحاق قلت: رجل ظاهر ثم أمسك عن الكفارة وقال: لا أكفر· قال: يجبر على ذلك.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن ابن جريج قال: أخبرني ابن طاوس، عن أبيه قال: إن قال المظاهر: لا حاجة لي بها. لم يترك حتى يطلق إن كان لا حاجة له بها.
سمعت أحمد يقول: المرأة إذا ظاهرت من زوجها لزمتها الكفارة كما يلزم الرجل إذا قالت لزوجها: أنت كأبي، أو كأخي، أو نحو ذلك· كفَّرت كما يُكفِّر الرجل؛ لأن الظهار ليس بمنزلة الطلاق؛ لأن الظهار إنما هي يمين.','2','716',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (539,'قِيل: فيطؤها (١) الزوج قبل أن تُكفِّر هي؟ قال: الزوج إيش عليه من ذلك، إنما هو عليها.
حدثنا عمرو بن عثمان قال: ثنا بقية بن الوليد، عن الوزير بن عبد الله، عن ابن شبرمة، عن عبيد الله بن عن عبد الله بن مسعود قال:
_________
(١) في الأصل (فيطأها) ولعل الصحيح ما أثبتناه.','2','717',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (540,'ظهار الرجل والمرأة سواء.
حدثنا عمرو بن عثمان قال: حدثنا الوليد بن مسلم، عن سعيد بن عبد العزيز، عن الزهري قال: على النساء من الظهار مثل ما على الرجال.
حدثنا أبو معن قال: حدثنا وهب بن جرير قال: حدثنا شعبة، عن حصين والشيباني، عن الشعبي أن عائشة بنت طلحة جعلت مصعبًا عليها','2','718',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (541,'كظهر ابنها إن تزوجته، ثم أرادت أن تزوجه فسألت فأُمِرَتْ أن تعتق رقبة.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن ابن لهيعة، عن خالد ابن أبي عمران قال: سألت القاسم وسالم عن المرأة يخطبها رجل، فتظاهر منه، ثم أرادت بعد ذلك نكاحه؟ قالا: ليس بشيء.
سمعت أحمد يقول: يعجبني في الكفارات كلها أن تكون رقبة مؤمنة.','2','719',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (542,'حدثنا المسيب قال: حدثنا ابن مبارك، عن سفيان، عن ابن أبي نجيح، عن عطاء قال: يجزئ اليهودي والنصراني في كفارة الظهار.
قال: وحدثنا ابن مبارك، عن ابن جريج، عن عطاء أنه كره الأشل','2','720',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (543,'والأعرج يعني في الرقبة. وقال: حدثنا ابن/١١٥/مبارك، عن ابن جريج، عن عطاء بن أبي رباح قال: يجزئ في الرقبة الأقطع والأبرص ولا يجزئ الأجذم.
وقال أحمد في المظاهر إذا جامع قبل أن يتم الصيام استأنف.
وسُئلَ أحمد عن المظاهر يقبل قبل أن يكفر فكأنه رخص في القبلة وحدها وذُكر عن ابن عباس أنه كره القبلة والمباشرة وغير ذلك وقال: إنه يراد','2','721',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (544,'بذلك كله الجماع.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن في قوله: {ثُمَّ يَعُودُونَ لِمَا قَالُوا} [المجادلة: ٣] قال: هو الغشيان في الفرج، وكان لا يرى بأسًا أن ينكح المظاهر في مادون الفرج قبل أن يُكَّفر.
حدثنا المسيب، قال: حدثنا ابن مبارك، عن يونس، عن الزهري قال: ليس','2','722',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (545,'له أن يقبلها ولا يتلذذها حتى يُكَّفر ولا نرى أن (.....) (١) حتى يؤمر بالتكفير والرجوع إلى امرأته· قلت لأحمد: المظاهر إذا ماتت امرأته؟ قال: يرثها· قلت: يكفر؟ قال: لا· حدثنا عبيد الله ابن معاذ، قال: حدثنا أبي، قال: حدثنا أشعث، عن الحسن فيمن ظاهر من امرأته، فمات أحدهما قبل أن يُكفر· قال: يتوارثان، ويستحب للرجل إذا ماتت امرأته أن يكفر وإن لم يفعل فله الميراث على كل حال.
حدثنا المسيب قال: حدثنا ابن مبارك، قال: حدثنا سعيد، عن قتادة. قال:
_________
(١) كلمت غير واضحة بالأصل.','2','723',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (546,'ترثه ولا يصل إلى ميراثها حتى يُكَّفر.
سألت أبا ثور قلت: المظاهر إذا ماتت امرأته قبل ـن يكفر؟ قال: إن كان وطئها قبل أن تموت كفر على كل حال، وإن لم يكن وطئها فلا كفارة عليه، ويرثها على كل حال.
حدثنا أبو معن قال: حدثنا حماد بن مسعدة، عن أشعث، عن الحسن فيمن ظاهر من امرأته في مقاعد شتى قال: عليه لكل ظهار منها كفارة، إلا أن يكون عنى شيئًا واحدًا· قال: وقال مالك بن أنس: في هذا كله كفارة واحدة.
سألت أبا ثور قلت: رجل ظاهر من امرأته ثم طلقها ثلاثًا، فتزوجت زوجًا، فطلقها، ثم تزوجها الأول كيف حاله في الظهار؟ قال: سقط الظهار وبطلت أحرامه كلها· قلت فرجل طلق امرأته تطليقة، فتزوجت رجلًا، فطلقها ثم رجعت إلى الأول؟ قال: هي عنده على ما بقي من الطلاق.','2','724',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (547,'قلت: فهل يطعم الصبيان في كفارة الظهار؟ قال: إذا كانوا يأكلون/١١٦/فلا بأس· قلت وإن لم يكونوا صَلُّوا؟ قال: وإن لم يكونوا صَلُّوا· قال: وإن لم يكونوا يأكلون فلا يطعمهم· قلت فيطعم الكفار؟ قال: لا· قلت: وكذلك القتل؟ قال: نعم كل كفارة واجبة فأنه لا يطعم الكفار ويطعم الصبيان إذا كانوا يأكلون· قلت فالمظاهر لا يجد إلا مسكينًا أو مسكينين؟ قال: لا يقربها حتى يكون ستين مسكينًا، ولا يطعم واحدًا طعام خمسة أو ستة لأن الله قال: {سِتِّينَ مِسْكِينًا} قلت: وكذلك جمع الكفارات؟ قال: نعم· قلت: فالظهار قبل الملك؟ قال: ليس بشيء، وكذلك الطلاق.','2','725',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (548,'كتاب اللعان','2','726',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (549,'سمعت أحمد يقول في اليهودية والنصرانية تكون تحت المسلم فيقذفها؟ قال: يلاعنها· قِيل: فبين الحر والأمة؟ قال: نعم· قال أحمد: وإن أكذب نفسه ألحق به الولد، ولا يجتمعان أبدًا على','2','727',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (550,'حال، وفي ضربه اختلاف.','2','728',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (551,'وسمعت أحمد أيضًا يقول: الرجل المسلم يلاعن اليهودية والنصرانية.
وسألت أبا ثور قلت إن قذف امرأته وهي يهودية؟ قال: يلاعنها.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي، نا أشعث، عن الحسن أنه كان يرى اللعان بين كل زوجين، ويراه بين المسلم والذمية، والحرة والعبد، والأمة والعبد، والحر والأمة، واللعان من الكافرة والأمة مثل الحرين المسلمين، إلا إن روح الأمة إن أكذب نفسه لم يجلد وكانا على حالهما وفيمن قذف امرأته وهي صغيرة ولم تحض قال: لا حد، ولا لعان.','2','729',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (552,'وقال: إذا تزوج الرجل امرأة في عدة من غيره، أو ذات محرم منه، أو خامسة، أو أخت امرأته، أو تزوج تزويجًا لا يصلح، أو ليس بصحيح، أو تزوج أختين بكلام واحد فقذفهما، أو إحديهما، أو واحدة من هؤلاء النسوة، ثم علم بفساد التزويج جلد ولا يلاعن، إلا أن تكون حاملًا فينتفي من الولد فيلاعن، ولا يجلد، وإن المتلاعنين يفرق بينهما ولا يجتمعان أبدًا.
حدثنا محمد بن أبي بكر قال: ثنا عمر بن هارون، عن الأوزاعي، عن عمرو بن شعيب، عن أبيه، عن جده أن النبي - صلى الله عليه وسلم - قال: ((أربع ليس بينهن وبين أزواجهن لعان اليهودية تحت المسلم، والنصرانية تحت المسلم، والحرة تحت العبد، والأمة تحت الحر)).','2','730',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (553,'قلت لأحمد: فيهودي قذف يهودية يتلاعنان؟ قال: إذا ارتفعا إلى حكام المسلمين حكم فيهم بحكم المسلمين· ثم قال أحمد: ليس لهذا وجه، لأنه ليس عدل واللعان إنما هو الشهادة، وهو ليس بعدل فتجوز شهادته/١١٧/. كأنه لم ير بينهما لعانًا.
حدثنا محمود، عن عمر بن عبد الواحد، قال: سمعت الأوزاعي يقول في نصراني قذف امرأته ثم ترافعا إلى إمام المسلمين ورضوا به هل بينهما لعان؟ قال: لا؛ لأنه لا حرمة لهم، ولا حد، ولو أن بعضهم قذف بعضًا ثم ارتفعوا إلى حكام المسلمين لم يضرب بعضهم لبعض.
وقال أحمد: الملاعن إذا أكذب نفسه بعد اللعان لم ترد عليه امرأته.
وسألت أحمد مرة أخرى قلت: الرجل يكذب نفسه بعد اللعان؟ قال: يلحق به الولد ولا يجتمعان أبدًا، وقد اختلفوا في الضرب.','2','731',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (554,'حدثنا سعيد بن منصور، قال: حدثنا خالد بن عبد الله، عن داود بن أبي هند، عن سعيد بن المسيب قال: إذا أكذب الملاعن نفسه جلد وتزوجها إن شاء.
حدثنا المسيب، قال: ثنا ابن مبارك، عن الفضل بن دلهم، عن الحسن قال: إذا قال: كذبت عليها جُلد الحد، ولم ترجع إليه.','2','732',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (555,'حدثنا سعيد بن منصور قال: ثنا أبو معاوية، عن الأعمش، عن إبراهيم قال: قال عمر بن الخطاب: المتلاعنان يفرق بينهما ثم لا يجتمعان أبدًا.
قِيل لأحمد: رجل لاعن امرأته، فشهد أربع شهادات، فلما كان عند الخامسة وقف ولم يشهد؟ فقال: هذا لم يتم اللعان يُضرب وترد عليه امرأته.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: حدثنا أشعث، عن الحسن قال: إذا أكذب القاذف لامرأته نفسه قبل أن يتم اللعان جُلد وهي امرأته ولحقه الولد والحبل أيهما كان، وإن كان قد تم اللعان لم يجلد، وبرئ من الولد والحبل، ولم تُرّد إليه امرأته أبدًا، ولم يلتفت إلى قوله.
قِيل لأحمد إذا قال الرجل لامرأته: ليس ما في بطنك مني· قال: هذا ليس','2','733',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (556,'نفيًا ولم يره شيئًا وقال: هذا مجهول؛ لأنه لا يدرى ما في بطنها لعله ريح أو وجع.
حدثنا محمد بن رافع قال: حدثنا ابن أبي بكر فديك قال: حدثنا ابن أبي ذئب، عن ابن شهاب أن رجلًا نكح امرأة ولم يدخل بها حتى حملت قال: ليس مني· وقالت: هو منه· ولا يعلم أنه دخل عليها؟ قال: يتلاعنان، ولا صداق لها؛ لأنه لم يدخل بها.','2','734',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (557,'سمعت أحمد يقول: إذا قذف الرجل امرأته، ثم طلقها ثلاثًا فأنه يلاعن، وإذا طلق ثلاثًا، ثم قذف مكانه فإنه لا يلاعن، لأنها ليست امرأته.
قِيل: فإن كانت حاملًا فقذفها، وقال: ولدك ليس مني· قال: فيه اختلاف.
قِيل: فإن طلقها واحدة وهي في العدة/١١٨/ثم قذفها· قال: هي امرأته يلاعن.
وقال أحمد مرة أخرى: إذا قذف الرجل امرأته ثم طلقها فإنه يلاعن، لأن اللعان وجب عليه قبل أن يطلق فهو عليه.','2','735',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (558,'سألت أحمد مرة أخرى قلت: رجل طلق امرأته ثم قذفها في العد. قال: إن كان طلاقًا يملك الرجعة فإنه يلاعن؛ لأنه يملكها، وإن كان طلقها طلاقًا لا يملك الرجعة فإنه لا يلاعن هذا إذا قذف وهي في العدة.
حدثنا سعيد بن منصور، (قال: حدثنا هشيم)، قال: أنبأ هشام بن حسان، عن حبان الأزدي، عن جابر بن زيد، عن ابن عمر في رجل طلق امرأته ثم قذفها· قال: إن كان طلقها ثلاثًا جُلد وأُلحِقَ به الولد ولم يلاعن، وإن كان طلقها واحدة لاعن.
حدثنا سعيد بن منصور، قال: حدثنا هشيم، قال: أنبأ يونس، عن الحسن قال: إذا قذفها ثم طلقها ثلاثًا لاعن حاملًا كانت أو غير حامل، وإذا','2','736',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (559,'طلقها ثلاثًا ثم قذفها، فإن كانت حاملًا لاعنها، وإن لم تكن حاملًا جُلِِد ولم يلاعن.
حدثنا سعيد بن منصور، قال: حدثنا هشيم، قال: أنبأ مغيرة، عن الشعبي في رجل طلق امرأته ثلاثًا ثم قذفها في العدةقال: يلاعن· فقال له الحارث العكلي: قال الله: {وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ} [النور: ٦] أفتراها زوجته وقد طلقها ثلاثًا!، ثم قال: إني لأستحي إذا رأيت الحق أن أرجع.
سألت إسحاق قلت: رجل قذف امرأته فماتت أو مات قبل أن يلاعنها؟ قال: سقط اللعان ويتوارثان.','2','737',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (560,'حدثنا المسيب بن واضح، عن ابن مبارك قال: ثنا ابن جريج قال: قلت لعطاء إن مات أحدهما ولم يتلاعنا؟ قال: يرثه الآخر ولا يلاعن.
سمعت أحمد يقول في المتلاعنين لا تقع الفرقة بينهما إلا أن يفرق الحاكم بينهما، لأن ابن عمر قال: فرق رسول الله - صلى الله عليه وسلم - بينهما، قِيل: فإن لم يُفرِق الحاكم أهما على حالهما؟ قال: لا يكونان على حالهما، ولكن بينهما أن يفرق بينهما.
حدثنا سعيد بن منصور، قال: حدثنا مالك بن أنس، عن نافع، عن ابن عمر قال: فرق رسول الله - صلى الله عليه وسلم - بين المتلاعنين وألحق الولد بأمه.','2','738',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (561,'حدثنا سعيد قال: حدثنا سفيان، عن الزهري سمع سهل بن سعد يقول: شهدت رسول الله - صلى الله عليه وسلم - فرق بين المتلاعنين وأنا ابن خمس عشرة سنة.
سألت إسحاق: أربعة شهدوا على امرأة بالزنا أحدهم زوجها· قال: إذا جاء ليشهد/١١٩/ عليها بالزنا ومعه ثلاثة نفر جازت شهادتهم وترجم وإذا رماها بالزنا وجاء ثلاثة فشهدوا مع الزوج جلد الثلاثة ويلاعن الزوج.
حدثنا أبو معن قال: حدثنا أبو بكر الحنفي قال: حدثنا الحسن بن عمارة، عن الزهري، عن سعيد بن المسيب قال: قال عمر بن الخطاب: إذا قذف','2','739',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (562,'الرجل امرأته وجاء ثلاثة يشهدون جلدوا ولاعنها الزوج، وإذا جاءوا أربعة جميعًا أحدهم زوجها (١) أقيم عليها الحد.
حدثنا محمد بن معاوية قال: حدثنا ابن أبي الزناد، عن أبيه، عن عبيد الله بن عبد الله، عن ابن عباس في أربعة شهدوا على امرأة بالزنا أحدهم زوجها يجلد الثلاثة ويلاعن الزوج·سألت أبا ثور قلت: أربعة شهدوا على امرأة بالزنا أحدهم زوجها؟ قال: كان الشافعى يقول: يجلد الثلاثة ويلاعنها الزوج، وقال غيره: هو شاهد يقام عليها الحد· قلت لأبي ثور: ما تقول أنت؟ قال: أجيز شهادتهم وأحدها، لأن الله تعالى قال: {وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ وَلَمْ يَكُن لَّهُمْ شُهَدَاء إِلَّا أَنفُسُهُمْ} [النور: ٦] وهذا معه شهداء.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: أنبأ أشعث، عن الحسن في من
_________
(١) في الأصل: زوجهم، ولعل الصواب ما أثبتناه.','2','740',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (563,'قذف امرأته وهي صغيرة ولم تحض قال: لا حد ولا لعان.
حدثنا أبو معن قال: حدثنا ابن رجاء، نا جرير بن أيوب، عن الشعبي أن رجلًا قذف امرأته بالزنا فقال: زنا بك فلان· فلاعنته امرأته، ثم إن الرجل الذي قذف بالمرأة جاء بعد فقال: افتريت علىّ فإنه لا يجلد له زوج المرأة، لأن ملاعنته المرأة أبطلت عنه الحد.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن فيمن لاعن امرأته ثم قذفها· قال: إن كان قال: يازانية· جُلِد، وإن قال: الذي قلت حق· لم يجلد.','2','741',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (564,'حدثنا أبو معن قال: حدثنا محمد بن جعفر قال: حدثنا شعبة، عن مغيرة، عن إبراهيم والشعبي قالا: إذا لاعن الرجل امرأته ثم قال: الولد ليس مني فإنه يُجلد حدًا.
حدثنا عبيد الله قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن قال: إذا التعن الرجل وأبت المرأة أن تلتعن حُبِسَت حتى تلتعن أو تقر بالزنا.
حدثنا المسيب قال: حدثنا ابن مبارك، عن يحيى بن أيوب البجلي قال: سمعت الشعبي سأله عن: رجل قذف امرأته وهي صماء بكماء عمياء؟ قال: هي مثل الميتة يضرب ثمانين وهي امرأته، أو تقر بالزنا. /١٢٠/','2','742',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (565,'<span data-type="title" id=toc-196>باب: عدة أم الولد</span>
سُئلَ أحمد، عن عدة أم الولد؟ قال: حيضة نذهب إلى أنها أمه وقال: لو كان عدتها ثلاث حيض ورثت.','2','743',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (566,'وسألت أحمد مرة أخرى قلت: عدة أم الولد إذا طلقها زوجها وهو حر أو عبد؟ قال: هي أمه حتى يموت سيدها.
قلت: فإن مات عنها سيدها كم تعتد من السيد؟ قال: عدة أم الولد؛ لأنها لو كانت حرة كانت ترث منه· وقال: أم الولد، والمكاتب، والمدبر عبيد.
وسألت إسحاق عن عدة أم الولد فقال: أربعة أشهر وعشرا.
حدثنا أحمد قال: حدثنا يحيى بن سعيد، عن عبيد الله قال: أخبرني نافع، عن ابن عمر قال: استبرأ أم الولد إذا مات عنها سيدها أو أعتقها حيضة.
حدثنا أحمد قال: حدثنا يعقوب بن إبراهيم قال: ثنا عبد العزيز بن المطلب،','2','744',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (567,'عن عبد الله بن أبي بكر، عن ابن شهاب، عن عبادة بن الصامت أنه قال: عدة أم الولد حيضة.
حدثنا أحمد قال: حدثنا محمد بن جعفر قال: حدثنا سعيد، عن مطر، عن رجاء بن حيوة، عن قبيصة بن ذؤيب، عن عمرو بن العاص أنه قال:','2','745',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (568,'لا تلبسوا علينا سنة نبينا - صلى الله عليه وسلم - عدتها عدة المتوفى عنها زوجها أربعة أشهر وعشرا.','2','746',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (569,'<span data-type="title" id=toc-197>باب: من باع جارية ثم ظهر بها حمل</span>
قِيل لأحمد: رجل له أمة فباعها من قوم فلما مكثت عندهم أيامًا ظهر بها حمل فأقر البائع أنه منه· قال: ترد عليه الجارية لأنه لا يجوز له أن يبيع مالا يملك· قِيل: فإنه قد أنفق الثمن وليس له مال؟ قال: يصير الثمن دينًا عليه.
قِيل لأحمد: زوج بريرة حرًا كان أو عبد؟ قال: الأحاديث الصحاح أنه كان عبدًا.
وسمعت إسحاق يقول: تخير الأمة من العبد، ولا تخير من الحر، وذكر عن أهل المدينة أن زوج بريرة كان عبدًا.
حدثنا إسحاق قال: أنا المغيرة بن سلمة المخزومي قال: حدثنا وهب، عن عبيد الله بن عمر، عن عروة، عن عائشة قالت:','2','747',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (570,'كان زوج بريرة عبدًا.
حدثنا إسحاق قال: أخبر المخزومي قال: حدثنا وهب، عن عبيد الله، عن نافع، عن صفية بنت أبي عبيد أنها قالت: كان زوجها عبدًا.','2','748',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (571,'حدثنا إسحاق، قال: أخبرنا جرير، قال: ثنا هشام بن عروة، عن أبيه، عن عائشة قالت: كاتبت بريرة على نفسها بتسع أواقٍ كل سنة أوقية، فخيرها رسول الله - صلى الله عليه وسلم - من زوجها/١٢١/وكان عبدًا فاختارت نفسها· قال عروة: ولو كان حرًا ما خيرها رسول الله - صلى الله عليه وسلم -.
حدثنا علي بن عثمان، قال: ثنا حماد، قال: أخبرنا قتادة، عن عكرمة، عن ابن عباس أنه قال: كان زوج بريرة عبدًا أسودًا كأني أنظر إليه في سكك المدينة يبكي.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن موسى بن عقبة، عن','2','749',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (572,'نافع، عن ابن عمر أنه كان يقول: إذا كانت المرأة تحت العبد فأصابتها عتاقة فأنها تخير ما لم يمسها زوجها إن شاءت كانت امرأته وإن شاءت فارقته فإن أقرت حتى يجامعها فلا تستطيع أن تنتزع منه.
قال أحمد في الأمة إذا عتقت وزوجها عبد فوطئها بعد العتق: فليس لها خيار علمت أن لها الخيار أو لم تعلم. مذهبه مذهب حديث حفصة.','2','750',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (573,'قال أبو محمد: في كتابي إذا بيعت، وإنما هو إذا عتقت.
حدثنا أبو هشام قال: حدثنا حسان قال: قال سفيان: إن وقع عليها وهي لا تعلم أن لها الخيار فإن حلفت خُيرت، وإن كانت تعلم فلا خيار لها.
وقال سفيان: إذا أعتقت تحت من كانت حر أو عبد فخُيرت فاختارت نفسها ولم يكن دخل بها فلا صداق لها؛ لأن الفرقة جاءت من قبلها، وإذا اختارته قبل أن يدخل بها فدخل بها فالصداق للسيد، لأن أصل المهر وقع للسيد حين نُكِحت، وإن كان الخيار بعد الدخول فذلك أوجب أن يكون للسيد، وإذا أُعتقت فعلمت في مجلسها أن لها الخيار فلم تخير فلا خيار لها.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن يونس، عن الزهري قال: أخبرني عروة بن الزبير أن مولاة لبني عدي بن كعب أخبرته أنها لما','2','751',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (574,'أعتقت دعتني حفصة زوج النبي - صلى الله عليه وسلم - فقالت: إني مخبرتك خبرًا والله ما أحب أن تفعليه ولكني تحرجت أن أكتمك، تعلمين أمرك بيدك إن شئت استقررت عند زوجك، (وإن شئت فارقتيه) (١) ما لم يمسسك فإن مسّك قبل أن تفارقيه فليس لك من أمرك شيء· قالت: فقلت: أشهدكم أني فارقته. وكان عبدًا.
_________
(١) مكررة بالأصل.','2','752',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (575,'<span data-type="title" id=toc-198>باب: العزل</span>
سُئلَ أحمد عن العزل فقال: أما الحرة فلا إلا بإذنها. وقال: إذا أذنت فلا بأس.
وسمعت إسحاق يقول لا بأس بالعزل يستأمر من الحرة ولا يستأمر الأمة، إلا أن تكون أمة لها زوج فلا يعزل عنها زوجها إلا بأمرها، فأما السرية فلا/١٢٢/يستأمرها سيدها، ولا تستأمر مملوكتك.
حدثنا سعيد بن منصور، قال: ثنا سفيان، عن ابن أبي نجيح، عن مجاهد، عن قرعة، عن أبي سعيد الخدري أن رسول الله - صلى الله عليه وسلم - سُئلَ عن العزل فقال:','2','753',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (576,'((لما تفعلونه - ولم يقل فلا تفعلوه - فإنه ليس نفس مخلوقة إلا الله خالقها)).','2','754',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (577,'<span data-type="title" id=toc-199>باب: من اشترى جارية فقبلها</span>
قِيل لأحمد: رجل اشترى جارية حاملًا أيقبلها؟ قال: لا· سبحان الله أتقبل امرأة لعلها أم ولد رجل· قِيل إنه لا يدرى ممن الولد· فكرهه كراهية شديدة.
وقِيل لأحمد أيضًا: إن الرجل يشتري الأمة أيقبلها أو (يطأها) (١) دون الفرج؟ فكرهه.
قِيل: والصغيرة؟ فلم يجبه فيها، وكرهه أيضًا.
_________
(١) كذا بالأصل، ولعل الصواب: يطؤها.','2','755',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (578,'حدثنا علي بن عثمان قال: ثنا حماد، عن علي بن زيد، عن أيوب بن عبد الله اللخمي أن ابن عمر قال: وقع في سهمي يوم جلولاء جارية جميلة كأن عنقها إبريق فضة فقال ابن عمر: فما ملكت نفسي حتى وثبت إليها فجعلت أقبلها والناس ينظرون.','2','756',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (579,'حدثنا أبو هشام قال: حدثنا حسان، عن سفيان، عن هشام، عن ابن سيرين كره أن يقبل ويباشر وهو أحب القولين إلى سفيان.','2','757',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (580,'<span data-type="title" id=toc-200>باب: استبراء الأمة</span>
قلت لأحمد: رجل بعث وكيلًا له من بغداد إلى البصرة ليشتري له جارية وهو رجل ثقة، فاشتراها فوصلت إليه بعد شهر أيستبرئها من يوم تصل إليه، أو من يوم أُشتريت له وقد مضى الاستبراء؟ قال: لا يقربها حتى يستبرئها من يوم تصل إليه.
قلت: يستبرئها من يوم تصل إليه؟ قال: نعم.
وسألتُ إسحاقَ قلت: رجل أُشتريت له جارية من البصرة فحُملت إلى خراسان أيستبرئها من يوم تصل إليه أو من يوم أُشتريت له وقد مضى الاستبراء؟ قال: يستبرئها من يوم ملكها إذا حاضت فهي مستبرأة.
حدثنا أبو معن، قال: ثنا أبو بكر الحنفي، قال: قال سفيان الثوري في الرجل يشتري الجارية فيتركها عند أهلها حتى تحيض. (قال: لا حتى تحيض) (١) عنده.
_________
(١) مكررة بالأصل.','2','758',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (581,'<span data-type="title" id=toc-201>باب: إذا اشترى جارية وهي حائض</span>
سألتُ أحمدَ قلتُ رجل اشترى أمة وهي حائض أيستقبل بها حيضة أخرى أو تجزئ هذه من الاستبراء؟ قال: لا ولكن يستقبل بها حيضة جديدة.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: حدثنا أشعث، عن الحسن قال: إن الاستبراء حيضة/١٢٣/، فإن اشتراها وهي حائض اجتزأ بتلك الحيضة المشتري والبائع.
حدثنا محمد بن الوزير قال: حدثنا مروان بن محمد قال: حدثنا ابن عياش قال: حدثنا حجاج بن أرطاه، عن الزهري، عن أنس بن مالك أن','2','759',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (582,'رسول الله - صلى الله عليه وسلم - استبرأ صفية بحيضة.
سألت إسحاق قلت: رجل اشترى أمة فحاضت حيضة ثم ارتفع (١) حيضتها قال: يجزيها حيضة الاستبراء· قلت: فإنها كانت تحيض قبل الشراء فلما اشتراها هذا انقطع حيضها· قال: إن كان لا يدرى مما انقطع حيضتها من كبر أو علة واشتبه عليهم فإني اختار أن يستبرئها بثلاثة أشهر، وقال بعضهم: سنتين، وذكر سفيان.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، قال: سمعت سفيان يقول في الرجل يشتري الجارية وهي ممن تحيض فترفع حيضتها· قال: لا يقربها سنتين، لأنه لا يكون الحمل أكثر من سنتين·حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ثنا أشعث، عن الحسن أن
_________
(١) كذا بالأصل.','2','760',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (583,'الاستبراء حيضة، فإن كانت لا تحيض من صغر أو انقطاع حيض فثلاثة أشهر فإن استبرأها وهي حائض اجتزأ بتلك الحيضة، وكان يرى أن يستبرئ البائع إذا باع والمشتري إذا اشترى، وإن اشتراها من امرأة، وإن كانت بكرًا.
حدثنا يحيى الحماني قال: حدثنا شريك، عن قيس بن وهب، عن أبي الوداك جبر بن نوف، عن أبي سعيد الخدري قال: أصبنا سبايا يوم أوطاس','2','761',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (584,'فأمر النبي - صلى الله عليه وسلم - أن نستبرئهن بحيضة حيضة.
حدثنا محمد بن معاوية، عن شريك بإسناده نحوه.
حدثنا أحمد قال: ثنا عباد بن عوام قال: ثنا محمد بن إسحاق، عن مكحول قال: قلت للزهري: أما بلغك أن عمربن الخطاب حتى انقضاء أجله كان يستبرئ الأمة بحيضة وعبد الله بن مسعود بالعراق قال: تستبرأ الأمة بحيضة وأن عثمان بن عفان كان يستبرئها بحيضة حتى كان معاوية فقال: حيضتين. قال الزهري: وأنا أزيدكم عبادة بن الصامت، أي موافقًا لذلك.
قلت لأحمد: فالتي لا تحيض بكم تستبرأ؟ قال: ثلاثة أشهر· قال: وكذلك الصغيرة.','2','762',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (585,'حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن خالد، عن ابن سيرين وأبي قلابة قالا: ثلاثة أشهر.
حدثنا المسيب، قال: حدثنا ابن مبارك، عن عبد الملك، عن عطاء في رجل','2','763',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (586,'اشترى جارية عذراء بين أبويها. قال: يستبرئ رحمها بحيضتين/١٢٤/، فإن لم تكن تحيض فخمسة وأربعين يومًا·حدثنا المسيب، قال: ثنا ابن مبارك، عن يحيى بن بشر، عن عكرمة قال: عدتها شهر·سألت إسحاق، عن رجل له جارية يطؤها، فزوجها من رجل قبل أن يستبرئها· قال: لا يحل له أن يزوجها حتى يستبرئها بحيضة· وذكر نافع، عن ابن عمر ذلك أنه لا يزوجها ولا يهبها حتى يستبرئها· قلت: فإنه زوجها ولم يستبرئها؟ قال: النكاح جائز وقد أجرم قال: وإن جاءت بولد لأقل من ستة أشهر فهو للبائع وإن كان لأكثر من ستة أشهر فادعياه جميعًا فهو للمشتري·حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن معمر، عن الزهري في رجل أنكح أمته وقد كان قبل ذلك يطؤها','2','764',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (587,'قال: تستبرأ بحيضة·قال ابن المبارك: وسمعت سفيان يقول إذا اشترى جارية فزوجها، أو أعتقها فمسها زوجها قبل إن يستبرئها· قال: لا بأس، لأنه في النكاح عهده.
وقال أحمد: تخير الأمة من العبد ولا تخير من الحر· قال: وأهل المدينة يقولون كان زوج بريرة عبدًا.
وسمعت أحمد مرة أخرى يقول في الأمة إذا بيعت وزوجها حر· قال: ليس لها خيار بتة، وإن كان الزوج عبدًا خيرت.
قلت: كيف','2','765',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (588,'تخير؟ قال: تخير من نفسها ومن زوجها.
قلت: فإن قالت: قد اخترت نفسي· قال: يختلف الناس في هذا يقول قوم: تطليقة· ويقول قوم: هو قطع ما بينهما، ويقول قوم هو فسخ النكاح.
قلت: فأي شيء تختار أنت؟ قال: ما أدري·
قال: حدثنا أحمد بن يونس، قال: ثنا ليث، عن نافع أن عبد الله كان يقول: إذا كان العبد متزوجًا الأمة فأصابها عتق وهي عنده فإنها تخير ما لم يمسها إن شاءت كانت معه وإن شاءت لم تكن معه.
قلت لأحمد: رجل يعتق امته ولها زوج لم يدخل بها، فتختار نفسها هل لها صداق؟ قال: ليس لها صداق؛ لأنه لم يدخل بها.','2','766',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (589,'قلت: فإن دخل بها فاختارت نفسها لمن الصداق لها أو لسيدها؟ قال: لسيدها· قلت: كيف يكون لسيدها وقد عتقت؟ · قال: لأن الأصل كان له.
وسألت أحمد مرة أخرى قلت: صداق الأمة لها أو لسيدها؟ قال: لسيدها وأظنه قال: وكذلك إن أعتقت.
وسُئلَ أحمد عن رجل باع عبدًا وله سرية؟ قال: هي لسيده.
قِيل: أيفرق بينهما؟ قال: لا هي امرأته وهي ملك سيده.','2','767',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (590,'وقِيل لأحمد مرة أخرى الرجل يأخذ سرية عبده؟ قال: إذا تسرى بإذن مولاه فليس له أن يأخذها منه. /١٢٥/
قيل: فإن تسرى بغير إذنه؟ قال: إن شاء أخذها.
قال: حدثنا أحمد بن يونس، قال: حدثنا ليث، عن نافع، عن عبد الله قال: قضى عمر بن الخطاب في العبد يباع وله مال فإن ماله لسيده الذي باعه إلا أن يشترط المبتاع ماله.','2','768',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (591,'حدثنا أحمد بن يونس، قال: حدثنا ليث بن سعد، عن نافع، عن عبد الله أنه كان يقول: من أخذ من غلامه أمته، أو من وليدته أمتها فلا بأس، فإنما الأمة والعبد لسيده.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أشعث، عن الحسن أنه كان لا يرى بأسًا أن يأخذ الرجل سرية عبده فيستبرئها بحيضة ثم يطأها وإن كان تسراها بإذن سيده.','2','769',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (592,'<span data-type="title" id=toc-202>باب: الأمة تباع ولها زوج</span>
قلت لأحمد: بيع الأمة طلاقها؟ قال: لا أقول ذلك: نذهب إلى حديث عبد الرحمن بن سعد وعمر بن الخطاب.
وسمعت أحمد مرة أخرى قال في الأمة تباع ولها زوج: ليس بيعها طلاقها.
وسمعت إسحاق وسألته قلت: رجل اشترى أمة ولها زوج؟ قال: لا يكون البيع طلاقًا.
قلت: يشتري بضعها من زوجها؟ قال: نعم.
قلت: من غير طلاق؟ قال: نعم يشتري بضعها.
وسألتُ إسحاق مرة أخرى قلت: رجل اشترى أمة ولها زوج ولم يعلم إن لها زوج هل يردها؟ قال: يردها، لأنه عيب كبيرقال: ويشتري بضعها من زوجها.
حدثنا سعيد بن منصور، قال: حدثنا هشيم، قال: أخبرنا عبد الرحمن بن','2','770',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (593,'إسحاق، عن أبيه أن عمربن الخطاب كتب إلى يسار بن نمير إن يشتري له جارية، فاشتراها، فلما قدمت عليه أخبرته أن لها زوجًا في أهلها، فكتب عمر إلى يسار أن يشتري بضعها من زوجها.
حدثنا عبيد الله بن معاذ، عن أبيه، عن عمران - أبي العوام القطان-، عن عاصم بن بهدلة، عن مصعب بن سعد بن مالك قال: اشتريت لأبي جارية فسألها فأخبرت أن لها زوجًا فأتيت زوجها فاشتريت منه بضعها، ثم أتيته بها','2','771',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (594,'فقبلها.
حدثنا عبيد الله قال: حدثنا أبي قال: حدثنا المنذر بن ثعلبة قال: حدثنا علباء بن أحمر قال: إشتريت لعلي بن أبي طالب جارية فسألها: ألك زوج قالت: نعم فأمسك عنها واشترى طلاقها من زوجها ثمان مائة درهم.
حدثنا سعيد بن منصور، قال: حدثنا أبو عوانة، عن عمر بن أبي سلمة، عن أبيه إن عبد الرحمن بن عوف اشترى جارية، فأخبر أن لها زوجًا فقال لزوجها: طلقها يا بني، فأبى، فردها. /١٢٦/','2','772',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (595,'حدثنا أبو بكر الحميدي، قال: ثنا سفيان، قال: ثنا الزهري، عن أبي سلمة بن عبد الرحمن إن عبد الرحمن بن عوف اشترى من عاصم بن عدي جارية، فأخبر أن لها زوجًا فردها.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا المنذر بن ثعلبة قال: حدثني عمرو بن سالم الأنصاري قال: اشتريت لعثمان ابن عفان جارية فقال لها: ألك زوج؟ قالت: نعم· فاحترمها عثمان.','2','773',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (596,'قلت لأحمد: عبد تحته حرة فحُملَ إلى خراسان وامرأته ها هنا؟ قال: هي امرأته على كل حال ولا يكون بيعه طلاقًا إلا في قول من يقول: بيعه طلاقًا.
حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أخبرنا يحيى بن سعيد، عن سعيد بن المسيب قال: إذا بيع العبد فليس بطلاق.
سُئلَ أحمد، عن العبد يتسرى قال: لا أعلم بأسًا أن يتسرى بإذن مولاه.','2','774',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (597,'حدثنا سعيد بن منصور، قال: ثنا هشيم، قال: أخبرنا الحجاج، عن نافع، عن ابن عمر أنه كان لا يرى بأسًا أن يتسرى العبد.
حدثنا سعيد قال: ثنا هشيم، قال: أخبرنا الحجاج، عن العباس بن عبيد الله ابن عباس، عن عمه عبد الله بن عباس أنه أذن لغلام له أن يتسرى فاشترى ثلاث جوار أثمان ألفين ألفين.
قلت لأحمد: الرجل يزوج عبده أمته بغير مهر· قال: أحب إلي إن يكون مهرًا مسمى وشهود.','2','775',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (598,'حدثني عباس العنبري، قال: سمعت أبا الوليد يقول: يزوج الرجل عبده أمته وهما كارهان، وهما صغيران، ويزوجها بغير صداق، ولا يزوج عبده أمة قوم آخرين بغير صداق.
حدثنا المسيب، قال: سمعت ابن مبارك سُئلَ عن الرجل يزوج غلامه أمته· قال: لا بد من شاهدين.','2','776',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (599,'حدثنا أبو معن، قال: ثنا أبو عاصم قال: حدثنا أشعث، عن الحسن قال: لابد من أن يسمي صداقًا ويدعو شاهدين· قِيل لأحمد: رجل زوج عبده أمته ثم أعتقهما جميعًا، فمكثت معه أيامًا؟ قال: لا يجوز إلا أن يجدد النكاح. قال: وأظنه قال: وكذلك إن اشترى لعبده سرية ثم أعتقها قال: يجدد النكاح.','2','777',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (600,'حدثنا أبو معن قال: ثنا عبد الأعلى قال: ثنا يونس، عن نافع، عن ابن عمر أن عبدًا له كانت له سريتان، فأعتقهما، وأعتقه ونهاه أن يقربهما إلا بنكاح.
سُئلَ إسحاق عن رجل له عبد وأمة، فوهب الأمة للعبد هبة، ثم عتق العبد، ثم باع الأمة من رجل هل ترى للمشتري أن يطأ هذه الأمة؟ قال: (من الناس) (١) /١٢٧/من رأى للعبد أن يتسّرى وهذا مثله· فكأنه كره أن يطأها.
حدثنا محمود قال: ثنا عمر، عن الأوزاعي، عن رجل يزوج عبده أمته ثم وهبها له، ثم أعتقها ما سبيلها؟ قال: هي حرة، وحرمت على زوجها إن شاء أن يخطبها خطبها.
_________
(١) مكررة بالأصل.','2','778',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (601,'<span data-type="title" id=toc-203>باب: الرضاع</span>
قِيل لأحمد: ما تقول في لبن الفحل؟ قال: يحّرم.
وسمعت أحمد مرة أخرى سُئلَ عن لبن الفحل فكرهه.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، عن موسى بن الوليد قال: حدثني عمي إياس بن عامر قال: سمعت علي بن أبي طالب يقول:','2','779',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (602,'لا تنكح من أرضعتها امرأة أبيك، ولا امرأة ابنك، ولا امرأة أخيك.
قِيل لأحمد ما تقول في الحقنة باللبن· قال: وما الحقنة؟ قِيل: يحقن الصبي باللبن· قال: ما تكلم في هذا أحد.
قلت لأحمد: ما تقول في لبن الضرة أليس لا يحرم كما يحرم غيره؟ قال: نعم. يعني إن المرأة سقت جارية رجل.','2','780',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (603,'حدثنا الحميدي، قال: ثنا سفيان قال: حدثنا عبد الله بن دينار كما تسمعه الآن· قال: سمعت ابن عمر يقول: جاء رجل إلى عمر بن الخطاب وهو عند دار القضاء فقال:: يا أمير المؤمنين إنه كانت لي جارية أغشاها، وإني خرجت من عندها فخالفتني امرأتي إلى جاريتي فأرضعتها لكي تحرمها عليّ فقال عمر: عزمت عليك إلا أوجعت رأس امرأتك وأتيت جاريتك، فإنما الرضاع ما كان في الصغر.','2','781',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (604,'سُئلَ أحمد، عن رضاع الكبير وذكر له حديث سالم· فقال: إن أم سلمة قالت: إن هذا كان لسالم خاصة· وهذا عندي أقوى من قول عائشة.
حدثنا عبد الله بن الزبير قال: حدثنا سفيان قال: حدثني عبد الرحمن بن القاسم، عن أبيه، عن عائشة قالت جاءت سهلة بنت سهيل إلى رسول الله - صلى الله عليه وسلم - فقالت: يارسول الله إني أرى في وجه أبي حذيفة من دخول سالم عليّ كراهية فقال النبي - صلى الله عليه وسلم -: ((أرضعيه)) قال: وكيف أرضعه وهو رجل كبير· فتبسم رسول الله - صلى الله عليه وسلم - وقال: ((قد علمت أنه رجل كبير فأرضعيه)) · ثم جاءت إلى النبي - صلى الله عليه وسلم - فقالت: ما رأيت في وجه أبي حذيفة شيئًا أكرهه مذ أرضعته· قال عبد الرحمن: فقد شهد بدرًا قال سفيان: وهذا منسوخ إن ذلك كان خاصة لسالم.','2','782',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (605,'حدثنا إسحاق قال: أخبرنا الفضل بن موسى، (عن عبيد الله) (١) بن أبي زياد، عن القاسم بن محمد قال: إنما كان ذلك رخصة من/١٢٨/رسول الله - صلى الله عليه وسلم - لسالم.
سألتُ أحمدَ قلتُ: امرأة أرضعت غلامًا بعد الحولين بيوم أو يومين هل يحرم؟ قال: ما أدري يوم أو يومين· قلت: فإن أفطم قبل الحولين فأرضعته امرأة بعد الفطام هل يحرم ذلك؟ قال: نعم ما كان في الحولين فأنه يحرم ومذهب أبي عبد الله الحولين.
حدثنا المسيب بن واضح، قال: حدثنا ابن مبارك، قال: سمعت سفيان سُئلَ، عن امرأة أرضعت أكثر من سنتين رضاعًا متصلًا قال: ليس ما فوق السنتين برضاع قِيل: فإنها أرضعته سنة ثم فطمته ثم عادت له امرأة بأشهر فأرضعته· قال: هو رضاع.
_________
(١) مكررة بالأصل.','2','783',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (606,'حدثنا المسيب قال: حدثنا ابن مبارك، قال: حدثنا عمر بن بشير، عن الشعبي أنه سُئلَ عن الرضاع فقال: ما كان في السنتين فهو رضاع، وما كان فوق السنتين فليس برضاع·حدثنا علي بن عثمان قال: حدثنا حماد، قال: أنبأ هشام بن عروة، عن يحيى بن عبد الرحمن بن خاطب، عن أم سلمة زوج النبي - صلى الله عليه وسلم - قالت: لا رضاع بعد فطام.
قلت لأحمد: فحديث النبي - صلى الله عليه وسلم - إنما الرضاعة من المجاعة أليس يريد','2','784',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (607,'ما كان في الصغر قبل أن يفطم؟ قال: نعم الكبير إذا لم يجوع ما يصنع باللبن.
حدثنا محمد بن رافع قال: حدثنا أبو المنذر إسماعيل بن عمر البزاز قال: حدثنا داود بن قيس الفراء، عن زيد بن أسلم، عن عبد الله بن علقمة بن الفعواء، عن أبيه قال: قال عمر: إنما الرضاعة الحضانة.','2','785',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (608,'سألتُ أحمدَ قلتُ: ما تقول في الرضاع بعد الحولين؟ قال: أما أنا فأقول أنه لا يكون الرضاع بعد الحولين. قال الله: {وَالْوَالِدَاتُ يُرْضِعْنَ أَوْلاَدَهُنَّ حَوْلَيْنِ كَامِلَيْنِ} [البقرة: ٢٣٣]. فإذا مضى حولين فقد تمت الرضاعة إلا من ذهب إلى حديث سهلة بنت سهيل· قلت: فإن كان في الحولين مصة أو مصتين· فكأنه سهل فيه أنه ليس برضاع واحتج بحديث النبي - صلى الله عليه وسلم -: ((لا تحرم المصة والمصتان)) إلا أنه لم يصرح.','2','786',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (609,'وسمعت إسحاق يقول بعد الحولين إذا أرضعت لم يكن شيئًا.
سألت إسحاق قلت: امرأة أرضعت غلامًا رضاعًا كبيرًا، وأرضعت جارية رضعةً واحدةً وأروتها ونامت الصبية· فلما أدركت زوجت الجارية من هذا الغلام وأهلها لا يعلمون، فجاءت المرأة المرضعة فأخبرت بما كان ولم يكن الرجل دخل بالجارية هل تحرم هذه الرضعة؟ قال أبو يعقوب/١٢٩/: إن كانت هذه الرضعة فيها تمام خمس مصات كل مصة يرجع الصبي بعدما يمص مصة فمه عن الثدي، ثم يعود فيمص أيضًا حتى تم خمس مصات فإنه يحرم ولا أحب أن يتزوج أحدهما الآخر لما صارا أخوة، وجاء عن النبي - صلى الله عليه وسلم -: ((لا تحرم المصة والمصتان، ولا تحرم دون خمس رضعات)) وذلك أن الرضعة يكون فيها مصات، وربما كانت مصة واحدة وهي رضعة لما يرد الصبي فمه عن المرضعة.
حدثنا عبد الله بن الزبير الحميدي قال: ثنا سفيان، حدثنا هشام بن عروة، عن أبيه، عن عبد الله بن الزبير، عن النبي - صلى الله عليه وسلم - أنه قال: ((لا تحرم المصة ولا المصتان، ولا الرضعة ولا الرضعتان)).
قلتُ لإسحاق: رجل زنا بامرأة فجاءت بولد من الزنا، فأرضعت هذا المرأة صبية هل تحرم على والد الذي زنا بها؟ قال: نعم تحرم· قلت: مجرى الحلال والحرام في اللبن سواء؟ قال: نعم.
سمعت إسحاق بن إبراهيم قال: حدثنا سفيان بن عيينة، عن الزهري، عن عروة، عن عائشة قالت: اختصم سعد بن أبي وقاص وعبد بن زمعة في ابن','2','787',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (610,'أمة زمعة فقال سعد: أوصاني أخي عتبة إذا قدمت مكة فانظر ابن أمة زمعة فهو ابني· وقال عبد ابن زمعة: هو ابن امة أبي ولد على فراش أبي· فرأى رسول الله - صلى الله عليه وسلم - شبهًا بينًا بعتبة فقال رسول الله - صلى الله عليه وسلم -: ((الولد للفراش، واحتجبي منه ياسودة)).','2','788',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (611,'كتاب الأدوية','2','789',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (612,'سمعت أحمد بن حنبل رخص في شرب أبوال الإبل.
وسمعت أحمد مرة أخرى يقول: أبوال الإبل لا بأس بشربها لدواء.
قِيل: فإن صار بول بعير في بئر؟ قال: أكرهه·.
حدثنا يحيى بن عثمان قال: حدثنا محمد بن كثير، عن عثمان بن عطاء، عن أبيه أنه كان لا يرى','2','790',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (613,'بالترياق وألبان الإبل وأبوال الإبل للمضطر بأسًا.
حدثنا أبو معن قال: ثنا عبد الرحمن قال: ثنا سفيان، عن منصور، عن إبراهيم قال: كانوا لا يرون باب: وال الإبل بأسًا، وقد كان بعضهم يأتي الناقة فيستنشق بولها.
قلت لأحمد: فتكره الحجامة في شيء من الأيام؟ قال: قد جاء في الأربعاء والسبت، وذكر حديث الزهري وكرهها في هذين اليومين.
قلت: فالجمعة والثلاثاء فيه شيء؟ قال: لا.','2','791',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (614,'وسألتُ إسحاقَ قلت: أتكره الحجامة في شيء من الأيام؟ قال: نعم يوم السبت والثلاثاء والأربعاء/١٣٠/، وقال يقال: إن في الثلاثاء ساعة لا يراق فيها الدم فلعله يوافق تلك الساعة· وقال: الحديث الذي روي، عن النبي - صلى الله عليه وسلم -: ((من احتجم يوم الثلاثاء لسبع عشرة من الشهر)) · قال أبو يعقوب: لعل هذا يوم مخصوص من السنة.
وسمعت إسحاق مرة أخرى يقول: أكره الحجامة يوم الثلاثاء والأربعاء والسبت· قِيل: أفتكره الإطلاء يوم الأربعاء والسبت؟ قال: شديدًا· ورخص الحجامة يوم الثلاثاء إذا كان يوم سبع عشرة من الشهر أو تسع عشرة أو إحدى وعشرين.','2','792',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (615,'قلتُ لإسحاقَ مرة أخرى: أتكره الحجامة يوم الجمعة؟ قال: لا أكرهه.
قال: وقال ابن المبارك: أكرهه للضعف.
وذكر إسحاق، عن ابن عون، عن ابن سيرين أنه كان يحتجم لسبع عشرة، وتسع عشرة، وإحدى وعشرين لا يبالي أي يوم كان· قلت: من ذكره عن ابن عون؟ قال: أخبرنا يحيى بن حفص عن ابن عون.
حدثنا عبدة بن عبد الرحيم بن حسان، قال: حدثنا محمد بن مزاحم قال: سُئلَ عبد الله بن المبارك: هل تكره الحجامة في شيء من الأيام؟ قال: يحتجم متى شاء· وذكر عن سفيان أنه كان يحتجم ولا ينظر في ذلك.
حدثنا عبيد الله بن معاذ، قال: حدثنا أبي قال: حدثنا عبد العزيز بن عبد الله، عن عون مولى أم حكيم، عن الزهري قال: قال رسول الله - صلى الله عليه وسلم -: ((من','2','793',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (616,'احتجم يوم السبت، أو الأربعاء أو اطلا فلا يلومن إلا نفسه في الوضيح)) ·
قال أبو محمد حرب: سمعت أبا بشار محمد بن نصر قال: رأيت رجلًا احتجم يوم الإربعاء فخرس.
حدثنا عمرو بن عثمان قال: حدثنا الوليد بن مسلم، عن ابن لهيعة، عن رجل، عن الزهري قال: قال رسول الله - صلى الله عليه وسلم -: ((من احتجم يوم الأحد دخل فيه شفاء وخرج منه داء، ومن احتجم يوم الأحد','2','794',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (617,'لسبع عشرة فهو دواء لداء سنة)). وقال رسول الله - صلى الله عليه وسلم - ((من احتجم يوم السبت أو يوم الأربعاء فأصابه بياض أو وضح فلا يلومن إلا نفسه والنورة يوم السبت ويوم الأربعاء كذلك)).
حدثنا أحمد بن يونس، قال: ثنا سلام الطويل، عن زيد العمى، عن معاوية بن قرة، عن معقل بن يسار قال: قال رسول الله - صلى الله عليه وسلم -: ((الحجامة يوم الثلاثاء لسبع عشرة من الشهر دواء لداء سنة)).
حدثنا جبارة بن مغلس قال: حدثنا عثمان بن مطر الشيباني قال: حدثنا','2','795',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (618,'الحسن بن أبي جعفر، عن محمد بن جحادة/١٣١/، عن نافع قال: قال لي ابن عمر: يا نافع ابتغى حجامًا واجعله رفيقًا، ولا تجعله كبيرًا، ولا غلامًا صغيرًا وسطًا بين ذلك فإني سمعت رسول الله - صلى الله عليه وسلم - يقول: ((الحجامة على الريق فيها شفاء وبركة، وتزيد في العقل والحفظ، وتزيد الحافظ حفظًا، فاحتجموا على اسم الله الاثنين والثلاثاء والخميس، واجتنبوا الحجامة يوم الجمعة والسبت والأربعاء فإن اليوم الذي عافى الله فيه أيوب من البلاء يوم الثلاثاء واليوم الذي ضربه فيه البلاء يوم الأربعاء، وأنه لا يبدو جذام ولا برص إلا في الأربعاء أو ليلة الأربعاء)).','2','796',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (619,'حدثنا محمد بن مصفا قال: ثنا عثمان بن عبد الرحمن قال: حدثني عبد الله بن عصمة، عن سعيد بن ميمون، عن نافع قال: قال ابن عمر: يا نافع تبيغ بي الدم، فأثنى بحجام واجعله شابًا، ولا تجعله شيخًا ولا صبيًا· قال: وقال ابن عمر: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((الحجامة على الريق أمثل، وهي تزيد في العقل، وتزيد في الحفظ، وتزيد الحافظ حفظًا فمن كان محتجمًا فيوم الخميس على اسم الله، واجتنبوا الحجامة يوم الجمعة ويوم السبت ويوم الأحد، واحتجموا يوم الاثنين ويوم الثلاثاء، واجتنبوا الحجامة يوم الأربعاء فإنه اليوم الذي أصيب فيه أيوب بالبلاء، وما يبدو جذام ولا برص إلا في يوم الأربعاء)).','2','797',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (620,'حدثنا أبو بكر محمد بن يزيد، قال: حدثنا عمرو بن عاصم، عن همام، عن قتادة، عن أنس قال: كان النبي - صلى الله عليه وسلم - يحتجم لسبع عشرة، وتسع عشرة، وإحدى وعشرين.
حدثنا (....) (١) بن سلام، قال: حدثنا معتمر بن سليمان، عن السري بن يحيى، قال: سمعت ابن سيرين يقول لرجل: إن أردت أن تفعل الحجامة فعليك بآخر الشهر.
_________
(١) كلمة غير واضحة بالأصل.','2','798',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (621,'<span data-type="title" id=toc-204>باب: الحقنة</span>
سألت أحمد عن الحقنة قلت: أتكره الحقنة للدواء وغير الدواء؟ قال: نعم.
وسألتُ إسحاق قلت: أتكره الحقنة؟ قال: شديدًا· ولم يرخص فيها.
وسُئلَ إسحاق مرة أخرى عن الحقنة قال: أما إذا كان لدواء فلا بأس، وأما للسمن فلا.
حدثنا إسحاق قال أخبرنا حفص بن غياث قال: حدثنا ليث، عن زيد','2','799',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (622,'أن علياً كان يكره الحقنة. قال: وحدثنا ليث، عن مجاهد أنه كرهها.
حدثنا إسحاق، قال: أنا عيسى بن يونس، عن أبي بكر بن أبي مريم، عن حكيم بن عمير وضمرة بن حبيب وحبيب بن عبيد أنهم كانوا يكرهون الحقنة.
حدثنا محمد بن معاوية/١٣٢/قال: ثنا ابن لهيعة، عن يزيد بن أبي حبيب، عن سعيد بن أيمن قال: اشتكى رجل فنعتت له الحقنة، فأتى عمر فسأله','2','800',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (623,'فنهاه فلما غلب احتقن فبرأ فأتى عمر فأخبره فقال: إن عاد فعد.
قلت لأحمد: رجل اشتكى بطنه فجاءت امرأة لا تحل له فوضعت يدها على بطنه· قال: لا ينبغي· قلت: أنها عجوز· قال: وإن كانت عجوزًا إلا أن يكون موضع ضرورة.
قال: وسألت أحمد أيضًا قلت: المرأة ينكسر فخذها أيجبرها الرجل؟ قال: نعم إذا اضطرت· قلت: فيضع يده على جسدها· قال: نعم إذا اضطرت.
حدثنا أبو سعيد هارون بن موسى الأيلي، قال: حدثنا عبد الوارث بن سعيد، عن ثابت بن ذروة قال: قلت لجابر بن زيد: امرأة وقعت فكسرت','2','801',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (624,'فخذها فكرهت أن أجبرها أو تهيبت أن أجبرها· قال: أفلا جبرتها، فإن المضطر كاسمه.
حدثنا عيسى بن محمد قال: حدثنا وهب بن جرير، عن أبيه، عن ثابت بن ذروة قال: جاءتني امرأة قد انكسرت فخذها فجبرتها، فسألت جابر بن زيد· فقال: لعلك تركتها يا أعرابي. قلت: لا قد جبرتها· قال: أحسنت.
سُئلَ إسحاق، عن: غلام به الحصاة هل يشق تحت مثانته فتخرج؟ قال: لا أحسن هذه المسألة؛ لأن هذا (قضاء على الله) (١) والكلام فيه جره (٢)، ثم قال: أن اجتمع الأطباء والناس فأشاروا على أبي الصبي إن يفعله فعله رجاء المنفعة فمات رجوت أن لا يكون عليهم شيء.
قال إسحاق: وقد رأيت أنا صبيًا فعل به ذلك فمات.
قلتُ لإسحاقَ: فإن كانت (....) (٣) في حلقه هل تبط عنه؟ قال: كلما كان الغالب عليه السلامة إذا فعلوه فبطوهُ لم أر به بأسًا.
_________
(١) هكذا بالأصل.
(٢) هكذا بالأصل وهي في ((لسان العرب)) ١/ ٦٠٩. ((جَرْهُ)) بمعنى: الشر الشديد.
(٣) كلمة غير واضحة بالأصل.','2','802',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (625,'حدثنا عبد الرحمن بن محمد بن سلام قال: حدثنا محمد بن كثير، عن مخلد بن حسين، عن هشام بن حسان، عن محمد بن سيرين قال: كان (ابن أبحر أطب) (١) العرب فرأى غلامًا يلعب مع الصبيان فيقع عليه البهر فيتنحى إلى الظل، ثم يعود فيلعب· فقال: أين أبو هذا الغلام؟ فدل عليه· فقال: في رأس ابنك هذا قطنة. فسُئِلت أمه، فقالت: إن أباه كان يعزل فتلقفت نطفته في قطنة فقِيل له: أما إذا علمت منه هذا فعالجه. فشق رأسه فاستخرج القطنة فذهب البهر. وكان له عم يغمضه ويقول: ليس يحسن
_________
(١) غير واضحة بالأصل، ولعله الصواب ما أثبتناه.','2','803',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (626,'شيئًا، فخرجت في رجله قرحة، فأرسل إليه فأبى أن يأتيه· فقِيل له: إنه عمك ولا بد لك منه.
فقال: /١٣٣/

من الناس من يغشى الأباعد نفعه ... ويُشقى به حتى الممات أقاربه
أما إذا استغنيتموا فعدوكم ... وادعا إذا ما غص بالماء شاربه
ثم قال: خذوا جروي كلب لم تفتق عيونهما وأدخلوه في بيت مظلم أثم صبوا على قرحته لبن حليب وأرسلوا الجروين حتى يشربا فإذا تركاه فوجهوهما قبل الضوء فإن خرجا مات الرجل، وإن ماتا عاش فُوجها فدبّا فتحترا فماتا فبرئ الرجل.
قلتُ لإسحاقَ: صبي لا يتكلم فشقوا تحت لسانه ليتكلم أتكره ذلك؟ قال إذا كان دواؤه ذلك فليس عليه شيء.
حدثنا عيسى، قال: حدثنا أبو نعيم، قال: حدثنا المسعودي، عن سهل أبي أسد، عن عبد الله بن عتبة قال:','2','804',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (627,'جاء طبي إلى عبد الله فقال: يا أبا عبد الرحمن هذا ابنك قد سقطت لهاته فنهى عن قطع اللهاة.
سألت إسحاق قلت: رجل وقع في عينيه الماء ففجره، وصلى على قفاه سبعة أيام؟ قال: أكره ذلك، وإن فعل فلا بأس.
حدثنا عمرو بن عثمان، قال: حدثنا الوليد بن مسلم، قال: قِيل لأبي عمرو: فمن عالج بصره أربعين يومًا مستلقيًا على ظهره ولا يصلي إلا إيماءً على ظهره؟ قال: قد كنت أشير عليه أن يصلي المكتوبة ثم يستلقي.','2','805',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (628,'حدثنا محمد بن الوزير الدمشقي، قال: حدثنا مروان بن محمد، قال: ثنا ليث بن سعد، قال: سألت يحيى بن سعيد، عن الرجل يقع في عينيه الماء فيريد أن يتعالج ويقيم أيامًا لا يصلي إلا وهو مستلقٍ؟ فقال يحيى: كنا نخلي بين الناس وبين ما أرادوا من ذلك.
حدثنا علي بن عثمان، قال: حدثنا أبو عوانة، عن الأعمش، عن المسيب بن رافع قال: أرسل ابن عباس إلى أبي صفيه لينزع الماء من عينيه، فقال: تستلقي سبعًا لا تصلي إلا وأنت مستلقٍ فقال: أرأيت إن كان الأجل في تلك السبعة الأيام· قال: وأرسل إلى عائشة يقرئها السلام ويقول أني أريد أن أنزع الماء من عيني. فقال: تستلقي سبعًا لا أصلي إلا مستلقيًا. فقالت:','2','806',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (629,'أرأيت إن كان الأجل في تلك السبعة الأيام؟
حدثنا محمد بن بشار، قال: حدثنا عبد الرحمن، قال: حدثنا سفيان، عن عاصم أن أبا وائل وقع في عينيه الماء فقِيل له: تستلقي سبعًا، فكره ذلك.
سألت إسحاق· عن قطع البواسير؟ فقال: ابن سيرين يكرهه جملةً، وقال: يطلي/١٣٤/عليه مردا سنج ودهن خل. قال أبو يعقوب: إن كان لمنفعة فلا بأس، ورخص في هذه الأشياء إذا كانت لمنفعة.','2','807',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (630,'حدثنا أبو حاتم قال: حدثنا مسلم، عن أبي عقِيل، عن ابن سيرين أنه كره قطع البواسير، ووصف له المرداسنج بدهن الخل.
سألت إسحاق: قلت: الرجل يخرج على جسده الثواليل فيحرقها بالنار؟ فقال: إن كان ذلك دواؤه فلا بأس. وقال: هو كالكي إن كان منفعته ذاك فأرجو إن لا يضره.
قلتُ لإسحاق: الوشم يكون في يد الرجل أنقلعه أم نتركه؟ قال: إذا خشى في قلعه الضرر تركه·حدثنا إسحاق قال: حدثنا وكيع قال: ثنا إسماعيل بن أبي خالد، عن قيس بن أبي حازم قال: دخلت أنا وأبي على أبي بكر الصديق نعوده فإذا','2','808',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (631,'عنده أسماء تذب عنه موشومة اليدين.
سُئلَ أحمد، عن الترياق فكأنه ذهب إلى الكراهة، وذكر عن ابن سيرين قال: لو علم ابن عمر ما تُجعل في الترياق ما شربه.
حدثنا عمرو بن عثمان قال: حدثنا بقية بن الوليد قال: سألت محمد بن الوليد الزبيري، عن الترياق ما ترى في شربه؟ فقال: لا يصلح شربه، لأنه من الحيات وهي تقطع باثنين بمسح بطن الحية حتى إذا صرف السم إلى جانبها قطع باثنين، ثم قال: لو كانت الحية أكلها','2','809',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (632,'حلال، ثم صنع بها ذلك لم يحل شيء منها، ثم قال: أما المضطر الملدوغ يُخاف عليه الهلاك فلم أكن أرى به بأسًا إذا كان على ذلك.
حدثنا عمرو بن عثمان قال: حدثنا الوليد بن مسلم، عن الأوزاعي قال: لا بأس بالترياق كله إذا ما عجن بالخمر.
حدثنا عمرو بن عثمان قال: ثنا بقية بن الوليد، عن شعبة، عن ابن عون، عن ابن سيرين، عن ابن عمر أنه كان يسقي الترياق.
سألت أحمد، عن الاستمشاء وخبث الحديد فقال: وهل يترك الناس هذا؟ وكأنه سهَّل فيه قليلًا·حدثنا أبو بكر محمد بن يزيد قال: حدثنا محمد بن إسماعيل، عن زكريا بن إبراهيم بن عبد الله بن مطيع، عن ركيع بن أبي عبيدة، عن أبيه، عن أم سلمة قالت: شربت دواء أستمشي به، فدخل عليّ النبي - صلى الله عليه وسلم - فقال لي: ((مالي أراك','2','810',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (633,'مرتثة)). قلت شربت دواء أستمشيء به. فقال: ((ما هو؟)) · فقلت: الشبرم. قال: ((حار حار عليك بالسنا والسنوت فإن فيه دواء من كل داء)).','2','811',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (634,'حدثنا أبو معن الرقاشي قال: حدثنا أبو بكر الحنفي قال: حدثنا عبد الحميد بن جعفر قال: ثنا عبيد بن عبيد الله/١٣٥/التيمي، عن أسماء بنت عميس أن رسول الله - صلى الله عليه وسلم - سألها: ((بما كنت تستمشين؟)) قالت: كنت أستمشي بالشبرم فقال ((حار حار)). قالت: ثم استمشيت بالسنا، فقال رسول الله - صلى الله عليه وسلم -: ((لو أن شيئًا فيه شفاء من الموت لكان في السنا)).
حدثنا محمد بن معاوية، قال: حدثنا عبد المؤمن البصري، قال: سمعت رجلًا سأل الحسن، عن دواء المشي، فقال ما أدري غير أن أنس بن مالك كان إذا وجد شيئًا من ذلك خلط من هذه الأطعمة ثم استسقى أو تقيأ فكان يزعم أنه يجد لذلك راحة.','2','812',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (635,'حدثنا محمد بن فراس قال: حدثنا أبو قتيبة، عن سلام بن أبي مطيع قال: رأيت أيوب السختياني يشرب الظريقون حتى تتحجر عينه.
حدثنا أبو معن قال: حدثنا أبو عامر قال: حدثنا إبراهيم بن طهمان، عن المغيرة، عن إبراهيم: أنه كان يكره جندبادستر، ويكره كثيرًا من الدواء من أجله.','2','813',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (636,'سألت إسحاق عن شرب خبث الحديد باللبن قال: شديدًا. ورخص فيه قال: وقال ابن عون شرب محمد الخبث.
حدثنا إسحاق قال: أخبرنا أزهر، عن ابن عون، عن ابن سيرين وسُئلَ عن: شرب خبث الحديد فلم ير به بأسًا، ووصف أنه يشربه باللبن قال: وأراه من المنافع التي قال الله عز وجل: {وَأَنزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَافِعُ لِلنَّاسِ} [الحديد: ٢٥].
حدثنا أبو موسى محمد بن المثنى، قال: حدثنا شريح بن سراج، عن عمته أنها دخلت على أبي قلابة وعنده جرة من رصاص فيها نبيذ، وفي النبيذ','2','814',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (637,'صرة معلقة قال: قلت: يا أبا قلابة ما هذه الصرة في نبيذك؟ قال: خبث حديد لرياح أجدها في بطني.
سألتُ أحمدَ قلتُ: الكي ما تقول فيه؟ قال: لا يعجبني.
قلت: فالبط بالنار؟ قال: كيف هو؟ قلت: يصيب الإنسان الريح في بعض جسده فيمتد فيحمى مسل ثم يدخل فيه ليخرج المدة. قال: هذا ليس كي. وسهَّل فيه.
حدثنا أبو حفص، قال: حدثنا سهل بن يوسف، قال: حدثنا حميد، عن ثابت، عن أبي رافع قال: رأى عمر في يدي جرحا، فأخذ بيدي فذهب بي إلى','2','815',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (638,'الطبيب فبطه، وقال: إن المدة إذا كانت بين العظم واللحم أكلته. قال حميد: وكان الحسن يكره البط.
حدثنا أبو حفص قال: ثنا أبو معاوية قال: ثنا الأعمش، عن أبي سفيان، عن جابر أن رسول الله - صلى الله عليه وسلم - عاد أبي بن كعب فبعث إليه طبيبًا فقطع منه عرقًا ثم كواه.
قلت/١٣٦/ لأحمد: فالقراءة في الماء للتعويذ؟ فكأنه سهّل.','2','816',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (639,'حدثنا يحيى قال: ثنا شريك، عن ليث، عن مجاهد قال: لا بأس أن يكتب القرآن في إناء ثم يغسل ويستشفى به.
قلت لأحمد: فتعليق التعاويذ فيه القرآن أو غيره· قال: كان ابن مسعود يكرهه كراهية شديدة جداً وذكر أحمد عن عائشة وغيرها أنهم سهَّلوا في ذلك ولم يشدد فيه أحمد (....) (١)
_________
(١) كلمة غير واضحة بالأصل.','2','817',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (640,'حدثنا زيد بن يزيد، قال: حدثنا عبد الرحمن بن مهدي، قال: حدثنا عبد الله بن المبارك، عن طلحة بن أبي سعيد، عن بكير بن عبد الله، عن القاسم بن محمد، عن عائشة قالت: التميمةليست مما تعلق بعد البلاء، إنما التميمة ما عُلِق قبل البلاء لدفع المقادير.','2','818',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (641,'حدثنا سعيد بن منصور، قال: حدثنا عيسى بن يونس، عن الأعمش، عن أبي ظبيان قال: دخل حذيفة على مريض يعوده فلمسه بيده فرأى تعويذًا على عضده؛ فقام غضبانًا وقال: لو مت وهذه عليك ما صليت عليك.
حدثنا سعيد قال: حدثنا أبو عوانة، عن مغيرة قال: قلت لإبراهيم: أعلق على عضدي هذه الآية: {يَا نَارُ كُونِي بَرْدًا وَسَلَامًا عَلَى إِبْرَاهِيمَ} [الأنبياء: ٦٩]. فكأنه كرهه.
حدثنا سعيد، قال: حدثنا عبد العزيز بن محمد، عن علقمة بن أبي علقمة','2','819',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (642,'قال: سألت سعيد بن المسيب، عن القرآن يلبسه الحائض والجنب إذا كان في حرقرة فلا بأس.
سألت أحمد عن رقية العقرب فلم ير به بأس إذا كان يعرف أو من القرآن.
حدثنا يحيى بن عبد الحميد، قال: حدثنا عبد الواحد بن زياد، قال: حدثنا عثمان بن حكيم، قال: حدثني الرباب:، قال: سمعت سهل بن حنيف يقول: مررنا بسيل فدخلنا نغتسل، فخرجت وأنا محموم، فَنُمى ذلك إلى النبي - صلى الله عليه وسلم - فقال: ((مروا أبا ثابت فليتعوذ)) · فقلت له: ياسيدي إن الرقى','2','820',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (643,'صالحة قال: ((لا رقى إلا من ثلاث، من النظرة، والحمى، واللدغة)).
حدثنا أبو بكر محمد بن يزيد، قال: ثنا عبد الله بن صالح، قال: حدثني الليث بن سعد، قال: حدثني إسحاق بن رافع، عن سعد بن معاذ الأنصاري، عن الحسن بن أبي الحسن البصري عن زيد بن عبد الله أنه قال: عرضنا على رسول الله - صلى الله عليه وسلم - رقية من الحية فأذن لنا فيها وقال: ((إنما هي مواثيق،','2','821',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (644,'والرقية بسم الله تنجيه ملحة بحر قرنية قفطا)).
سألت إسحاق قلت: البيطار يضع الخبيص والطعام على دبر الدابة؟ قال: ليس به بأس ورخص فيه.
قلتُ لإسحاق فالخبز السخن يوضع على الجرح/١٣٧/؟ فكرهه، وكذلك كل الطعام.
حدثنا نصير بن الفرج، قال: ثنا يزيد بن هارون، قال: أنبأ حبيب بن أبي حبيب، عن عمرو بن هرم، عن جابر بن زيد أنه سُئِل عن الرجل يغسل يده من الغمر (١) بالدقيق والخبز؟ قال: لا بأس بذلك.
_________
(١) الغمر بالتحريك وهو: ريح اللحم وما يعلق باليد من دسمه. انظر: ((لسان العرب)) ٦/ ٣٢٩٥.','2','822',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (645,'حدثنا أبو معن قال: حدثنا معتمر، عن عمران بن حدير، عن أبي مجلز أنه كان يكره أن يغسل يده بدقيق.
حدثنا أبو معن، قال: ثنا عبد الرحمن، عن سفيان، عن أبي مسكين، عن إبراهيم أنه أتى بدقيق أو سويق فكره أن يغسل يده.
قلتُ لإسحاقَ: الرجل يطعم البهيمة الخبز؟ قال: عند الضرورة إذا أُمرت بذلك فلا بأس، فأما أن تتخذ طعام البهيمة ذلك فلا خير فيه.
سألت أبا ثور قلت: الرجل يداوي البهيمة بالمسكر؟ فكأنه كرهه.','2','823',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (646,'حدثنا أبو معن الرقاشي قال: حدثنا عبد الكبير قال: ثنا مسعر، عن سعد بن إبراهيم، عن عبد الله بن عمر أنه كان يكره أن يداوى الدبر بالخمر.
سمعت إسحاق، قال: حدثنا أبو بكر بن عياش قال: دخلت على حبيب بن أبي ثابت في مرضه وهو يقول: آه آه يعني يئن في مرضه.
حدثنا عبدة بن عبد الرحيم، قال: حدثنا أبو وهب أن عبد الله بن المبارك لم يعجبه إذا سُئلَ الرجل أن يقول: أنا بِشَرٍّ، وذكر فيه حديثًا· قال:','2','824',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (647,'يقول: أنا صالح، فإن كان مريضًا ينوي ما أصلح الله من أموره وقال: إذا سُئلَ عن مرضه فلا بأس أن يخبر صاحبه حتى يعرف أمره، إنما يكره من ذلك الشكاة.
حدثنا أحمد بن نصر، قال: حدثنا حبان بن موسى قال: سُئلَ عبد الله عن المريض يقال له كيف أنت فيقول: بشر؟ فلم أره يعجبه هذا الحديث، وذكر عن النخعي قال: كان يعجبهم إذا قِيل له: كيف أنت أن يقول: بخير ثم يخبر· قال عبد الله: إذا أخبر يريد الخبر فليس بشكوى أرأيت لو سألك رجل ما فعل أبوك؟ ألا تقول قد مات؟ وذكر عبد الله عن النبي - صلى الله عليه وسلم - أنه قال: ((أوعك كما يوعك رجلان منكم)) وذكر قول موسى: {لَقَدْ لَقِينَا مِن سَفَرِنَا هَذَا نَصَبًا} [الكهف: ٦٢].
حدثنا أبو معن، قال: ثنا معتمر بن سليمان، عن ليث، عن طاوس أنه','2','825',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (648,'كان يكره الأنين، قال: ليث فحدث به طلحة وكان يئن في مرضه فما سمعت له أنينًا بعد ذلك حتى مات.
حدثنا عمرو بن عثمان، قال: حدثنا بقية بن الوليد، قال: حدثني أبو سلمة، قال: حدثني عبد العزيز ابن أبي رواد قال: عاد رجل من الفقهاء أخًا له فقال له: كيف بتَّ البارحة؟ قال: بتُّ في كذا/١٣٨/وكذا ولقيت كذا وكذا قال: فشكى ما شاء الله من ذلك، فقال العائد: أترى عيني هذه لقد ذهبت منذ أكثر من ثلاثين سنة ما علم بها أحد قبلك، ولولا الذي شكوت إليّ ما أعلمتك، إن من علامة البر كتمان المصائب، والصدقة، وإن من بث لم يصبر.
سمعت إسحاق ذكر عن بقية بن الوليد في حديث أسنده قال: شكى نبي من الانبياءالضعف فأوحى الله إليه: أن كل اللحم باللبن فإن القوة فيهما، وقال: وشكى نبي جبن قومه فأوحى الله إليه أن مرهم','2','826',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (649,'فليستقوا الحرمل.
حدثنا المسيب بن واضح، حدثنا بقية، عن الوليد بن كامل، عن رجل حدثه إن نبيًا من الأنبياء شكا إلى الله الضعف فأوحى الله إليه أن كل اللحم باللبن فإن القوة فيهما.
حدثنا المسيب قال: حدثنا بقية، عن عمرو بن مجاشع، عن بعض أصحابه، وعثمان بن عبد الرحمن عن بعض أشياخه: إن نبيًا من الأنبياء شكا إلى الله جبن قومه فأوحى الله إليه أن اؤمرهم أن يستقوا الحرمل فإنه يزيد في الفروسية.','2','827',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (650,'حدثنا عبد الرحمن بن سلام، قال: حدثنا داود بن المجبر، قال: ثنا مرزوق أبو حسان، قال: حدثنا مطر الوراق أن نبيًا من الأنبياء شكا إلى ربه الضعف، فأوحى الله إليه أن اطبخ اللحم باللبن وكله فإن القوة فيهما.
حدثنا يحيى بن عثمان، قال: حدثنا ابن خمير، قال: حدثنا عبد الله بن ثابت، عن عبد الله بن محمد بن علي، عن أبيه، عن ابن عباس، عن رسول الله - صلى الله عليه وسلم - أنه','2','828',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (651,'قال: ((الثناء دواء لكل داء، ولم يداو الورم والضربان بمثله)).
حدثنا أبو بكر محمد بن يزيد، قال: ثنا أبو عاصم، عن عثمان بن عبد الملك، عن سالم بن عبد الله، عن أبيه: قال رسول الله - صلى الله عليه وسلم -: ((إن في الحبة السوداء شفاء من كل داء إلا السام)).
حدثنا محمد بن يزيد قال: حدثنا موسى بن أيوب، عن يحيى بن سعيد العطار،','2','829',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (652,'عن سعيد بن ميسرة، عن أنس بن مالك قال: كان النبي - صلى الله عليه وسلم - إذا اشتكى تقمح كف شونيز وشرب عليه ماءً وعسلا.
حدثنا أبو معن، قال: حدثنا عبد الكبير الحنفي، قال: حدثنا أيمن بن نابل، قال: سمعت أم كلثوم بنت عمرو، قالت: سمعت عائشة تقول: إنَّ ناسًا مرضى','2','830',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (653,'أتوا رسول الله - صلى الله عليه وسلم - فذكروا أنهم لا يأكلون التلبينة والذي نفسي بيده إنها تغسل بطن أحدكم كما يغسل وجهه الماء الوسخ· قال أيمن: والتلبينة يؤخذ شيء من دقيق فيصُب عليه الماء فيخاض ثم يصفى ثم يسخن حتى ينعقد ثم تحسوه.
حدثنا إسحاق، قال: أخبرنا النضر بن شميل، قال: حدثنا المسعودي، عن قيس بن مسلم، /١٣٩/عن طارق بن شهاب، عن عبد الله، عن رسول الله -','2','831',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (654,'صلى الله عليه وسلم - قال: ((إن الله لم ينزل داء إلا أنزل له شفاء إلا الهرم فعليكم بألبان البقر فإنها ترم من كل الشجر)).','2','832',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (655,'سمعت إسحاق، قال: أخبرنا سفيان بن عيينة، عن ابن أبي نجيح قال: سأل عمر بن الخطاب الحارث بن كلدة: ما الطب؟ قال: الأزم يعني الحمية.
حدثنا إسحاق قال: أخبرنا وكيع، عن ثابت بن أبي صفية، عن أبي جعفر محمد أن عليًا كان ناقهًا من مرض فرآه النبي - صلى الله عليه وسلم - وبين يديه تمرات، فأخذ عليّ يتناول، فقبض النبي - صلى الله عليه وسلم - على التمر ثم جعل يلقي إلى علي تمرة تمرة.','2','833',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (656,'قال وكيع: حماه بذلك.
حدثنا عباس بن عبد العظيم قال: حدثنا عبد الملك بن عمرو قال: ثنا فليح، عن أيوب بن عبد الرحمن بن صعصعة، عن يعقوب بن أبي يعقوب، عن أم المنذر بنت قيس الأنصارية قالت: دخل عليَّ رسول الله - صلى الله عليه وسلم - ومعه علي، وعلي ناقه من مرض، ولنا دوال معلقة، فقام رسول الله - صلى الله عليه وسلم - يأكل منها وقام علي يأكل منها، فطفق النبي - صلى الله عليه وسلم - يقول لعلي: ((مه إنك ناقِةٌ)) حتى كفَّ· قالت: وصنعت شعيرًا وسلقًا فلما جئت به قال النبي - صلى الله عليه وسلم - لعلي','2','834',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (657,'((من هذا أصب فأنه أنفع لك)).
حدثنا يحيى بن عبد الحميد قال: حدثنا ابن مبارك، عن عبد الحميد بن صيفى من ولد صهيب، عن أبيه، عن جده صهيب قال: دخل عليَّ النبي - صلى الله عليه وسلم - وهو يأكل خبزًا وتمرًا فقال: ((أدنه)) فدنوت، فأقبلت على التمر وتركت الخبز فقال: ((أقبلت على التمر وأنت رمد!)) قلت: إنما آكل على الجانب الآخر فتبسم في وجهي.','2','835',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (658,'حدثنا أبو معن قال: ثنا أبو عامر قال: ثنا عبد الله بن زيد بن أسلم، عن أبيه، عن جده أنه كان أصابه جرح في رجله قال: فأمر رسول الله - صلى الله عليه وسلم - أبي، فأمر عمران يحمى فقال: إني لأمص النوى من الجوع.
حدثنا عمرو بن عثمان، قال: حدثنا بقية بن الوليد، قال: حدثني أرطاء، قال: اجتمع رجال من أهل الطب عند ملك من الملوك فسألهم: ما رأس دواء المعدة؟ قال: فقال كل رجل قولًا· قال: وفيهم رجلٌ ساكت فلما فرغوا قال له: ما تقول أنت؟ قال: قد ذكروا أشياء وكلها ينفع بعض النفع، ولكن ملاك ذلك ثلاثة أشياء: لا تأكل طعامًا أبدًا إلا وأنت تشتهيه، ولا تأكل لحمًا يطبخ لك حتى ينعم بصاجة، ولا تبتلع لقمة أبدًا','2','836',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (659,'حتى تمضغها مضغًا/١٤٠/شديدًا لا يكون على المعدة منها مئونة.
حدثنا إسحاق، قال: أنا وكيع، عن رزام بن سعيد ثقة، عن أبي المعارك قال: سمعت ابن عمر يقول: لا يحمين أحدكم مريضه طعامًا يشتهيه، لعل الله يجعل شفاه فيه إن شاء الله يجعل الشفاء حيث شاء.
حدثنا عيسى بن سلميان قال: ثنا إسماعيل بن عياش قال: حدثنا ابن جريج، عن عطاء بن أبي رباح قال: لا بأس إن يأتي المؤخذ، عن امرأته والمسحور','2','837',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (660,'من يطلق عليه.
حدثنا أبو معن قال: حدثنا خالد بن الحارث قال: ثنا هشام، عن قتادة، عن سعيد بن المسيب في الرجل يؤخذ عن امرأته فيلتمس من يداويه قال: إنما نهى الله عما يضر ولم ينه، عما ينفع.','2','838',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (661,'كتاب اللباس','2','839',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (662,'سألتُ أحمدَ قلتُ: الرجل يشد أسنانه بالذهب؟ قال: لا بأس بذلك.
حدثنا أحمد بن عبده بن موسى الضبي، قال: أخبرنا أبو القاسم ابن أبي الزناد، قال: حدثنا واقد بن عبد الله بن أبي ياسر التميمي قال: أخبرني من رأى عثمان بن عفان قد شد أسنانه بالذهب.
حدثنا محمد بن سعيد قال: أخبرنا عنبسة بن الفضل قال: ثنا عبيد الله بن','2','840',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (663,'أبي بكر بن أنس، عن جده أنس بن مالك أنه كان لا يرى بأسًا بشد الأسنان بالذهب والفضة.
حدثنا عيسى بن محمد، قال: ثنا حجاج، عن ابن جريج قال: سمعت ابن شهاب يسأل عن ربط الأسنان بالذهب قال: لا بأس به ربط عبد الملك بن مروان أسنانه بالذهب.','2','841',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (664,'حدثنا أبو معن، قال: ثنا أبو عامر، حدثتنا أم عمر مولاة لآل طلحة قالت: رأيت موسى بن طلحة قد شد ثنتيه ورباعيته بالذهب.
حدثنا علي بن عثمان وسعيد بن سليمان ومحمد بن سعيد، قالوا: حدثنا أبو الأشهب، عن حماد بن أبي سليمان قال: رأيت المغيرة بن عبد الله عامل الكوفة قد شد أسنانه بالذهب فسألت إبراهيم فقال: لا بأس به.','2','842',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (665,'حدثنا عبد الرحمن بن مبارك قال: حدثنا يحيى بن ميسرة قال: حدثنا عون العقِيلي أن عبد الرحمن بن أبي بكرة كان قد بلغ سنًّا وكان يولد له فسقطت أسنانه فأعيدت بسلسلة من ذهب.
حدثنا عبد الرحمن بن مبارك، قال: حدثنا حماد بن زيد، قال: رأيت ثابتًا وأبا التياح مضببين الأسنان بالذهب.','2','843',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (666,'حدثنا يحيى بن عثمان، قال: حدثنا عمر بن صالح الدمشقي، قال: رأيت أبا حرة قد ضبب أسنانه بالذهب.
حدثنا يحيى بن عثمان قال: رأيت بقية بن الوليد قد ضبب أسنانه بالذهب.
سمعت إسحاق يقول: قد مضت السنة من النبي - صلى الله عليه وسلم - أنه لَعَن الواصلة والموصولة وأن ما قطع من الحي فهو ميت، وقد سن في عرفجة بن أسعد حين اتخذ يوم الكلاب أنفًا من ورق فأنتن عليه، فأمره حينئذ أيتخذه من ذهب، وقد ضبب غير واحد من أهل العلم سنَّة بذهب، والذهب والفضة محللان لم يكرهها للنجاسة، وأحل الرسول - صلى الله عليه وسلم - ذلك للنساء، وقال: ((هما محرمان على ذكور أمتي حل لإناثهم)).
وهذا من غير علة حادثة أا ترى أعرفجة أمره النبي - صلى الله عليه وسلم - أيتخذ أنفًا من ذهب، وترخص نفر من أصحاب النبي - صلى الله عليه وسلم - في خاتم الذهب حتى مات بعضهم وهو يلبسه،','2','844',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (667,'وفسر ابن عباس لبس الحرير أنه إنما نهى عن المصمت منه، ورخص عمر بن الخطاب فيه بقدر الكف، ورخص النبي - صلى الله عليه وسلم - لعبد الرحمن بن عوف، والزبير بن العوام في لبس قميص الحرير للحكة التي كانت بهما.
وسمعت إسحاق أيضًا يقول: إذا أراد الرجل الذي انكسرت سنه أو انكسر منه عظم أيعالجه بعظم أو غيره لم يفعل إلا بما يؤكل لحمه ولا يعالجه بسن غير ذكي، وإن أراد أن يعيد سنه بعد ما بانت منه لم يجز له، لأنها حين سقطت صارت ميتة، فإن صلى كذلك أعاد الصلاة قال: وكذلك كلما رقعه بعظم ميت، أو عظم حي لم يذبح، وإذا رقعة بعظم ميتة، أو ذكي لا يؤكل لحمه، أو عظم إنسان فهو كالميتة، وعليه قلعه ولا يعتد بما صلى كذلك، فإن أبا إن يقلعه فإن بعض أهل العلم قال: يجبره السلطان على قلعه، فإن مات ولم يقلعه لم يقلع بعد الموت لما صار الحكم واحد وإن خشي سقوط سنه فربطها قبل سقوطها فلا بأس لأنها لا تصير ميتة إلا بعد السقوط، وأحب الأشياء أن يضبب سنه بالذهب لقول النبي - صلى الله عليه وسلم - لعرفجة حين أمره إن يتخذ أنفًا من ذهب·حدثنا إسحاق، قال: ثنا وكيع، قال: ثنا طعمة الجعفري، قال: رأيت موسى بن طلحة قد شد أسنانه بالذهب.','2','845',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (668,'حدثنا محمد بن مصفى، قال: ثنا بقية بن الوليد، قال: حدثني أبو مسكين الجزري، عن نصر الباهلي، عن هشام بن عروة، عن أبيه، عن عائشة، عن عبد الله بن عبد الله بن أبي قال: نذرت ثنيتي، فأمرني النبي - صلى الله عليه وسلم - إن أتخذ ثنية من ذهب.','2','846',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (669,'حدثنا سعيد بن سليمان، قال: حدثنا أبو الأشهب، عن عبد الرحمن بن عرفجة، عن عرفجة بن أسعد قال: أصيب أنفي يوم الكلاب، فاتخذت أنفًا من ورق فأنتن عليَّ، فأمرني النبي - صلى الله عليه وسلم - أن أتخذ أنفًا من ذهب.
حدثنا يحيى الحماني، قال: ثنا عباد بن العوام، عن ابن أبي عروبة، قال: حدثنا ثابت بن زيد بن ثابت بن زيد بن أرقم قال: حدثتني عمتي أنيسة بنت زيد بن أرقم، عن أبيها قال: قال رسول الله - صلى الله عليه وسلم -: ((الذهب والحرير حل لإناث','2','847',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (670,'أمتي حرام على ذكورها)).
حدثنا أبو معن، قال: حدثنا أبو عامر، قال: حدثنا إسرائيل، عن حكيم بن جبير، عن عكرمة، عن ابن عباس قال: لابأس بالحرير إذا كان فيه خلط.','2','848',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (671,'سألت أحمد، عن الثوب ينسج بالحرير وهو الملحم فكرهه وقال: هو محدث.
وسألت أحمد أيضًا عن الثوب يكون سداه حرير ولحمته قطن؟ قال: هذا الملحم هذا محدث لم يكن على عهد النبي - صلى الله عليه وسلم -، وكرهه، ورخص في الخز إذا كان سداه حرير وقال: الخز قد لبسه أصحاب رسول الله - صلى الله عليه وسلم -.','2','849',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (672,'وسُئلَ أحمد مرة أخرى عن الثياب الملحمة فكرهها لأنها محدثة.
حدثنا عيسى بن محمد قال: حدثنا يزيد، عن ابن عون، عن الحسن قال: دخلنا على ابن عمر بالبطحاء فقال له: رجل: يا أبا عبد الرحمن إن ثيابًا قد خالطها الحرير وهو قليل· قال: دعوا قليله وكثيره.','2','850',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (673,'حدثنا إبراهيم بن محمد الشافعي، قال: حدثنا فضيل، عن منصور، عن إبراهيم قال: كانوا يكرهون من الثياب ماكان سداه أو لحمته حرير، وكانوا لا يرون بالعلم بأسًا.
حدثنا إسحاق، قال: أبنا عتاب بن بشير، عن خصيف، عن عكرمة، عن ابن عباس قال: إنما نهى النبي - صلى الله عليه وسلم -، عن المصمت منه يعني الحرير.','2','851',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (674,'حدثنا أبو معن قال: ثنا وهب بن جرير قال: حدثنا عبد الله بن عمر، عن وهب بن كيسان قال: كان سعد بن مالك وجابر بن عبد الله وأبو هريرة وأنس بن مالك يلبسون خز الحرير.
حدثنا محمد بن الوزير قال: ثنا الوليد بن مسلم قال: رأيت أبا عمرو','2','852',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (675,'الأوزاعي لا يلبس الخز، ولا يرى لبسه، ويلبس البياض مع رجال من كبراء أهل الشام منهم إبراهيم بن أبي عبلة، والأحوص بن حكيم، ويزيد بن أبي مريم، ورأيت مالك بن أنس لا يلبس الخز، ولا يرى لبسه، ويلبس البياض مع رجال من كبراء أهل المدينة/١٤١/، منهم: عمر بن محمد بن زيد بن عبد الله بن عمر، وعبيد الله وعبد الله ابني عمر بن حفص بن عاصم، وابن أبي ذئب، ومحمد بن عجلان،','2','853',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (676,'ورأيت من كبرا، أهل مكة عثمان بن الأسود، والمثنى بن الصباح، وعبد العزيز بن أبي رواد لا يلبسون الخز ويلبسون البياض ويكتفون به، ورأيت سفيان الثوري لا يرى لبسه في رجال من كبراء أهل الشرق منهم: هشام بن حسان، والربيع بن صبيح، وأشباه لهم، ورأيت أبا عمرو، ومالك بن أنس وغيرهما يلبسون السبحان ولا يكرهون لبسها.
قال الوليد: وأخبرني عبد الله بن العلاء قال: رأيتُ على عمر بن عبد العزيز ساجًا قومته ستة دنانير.','2','854',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (677,'وسمعت أبا عمرو لا يكره أن يغالى بالساج، لأنه ثوب جامع باقي يورث.
حدثنا أبو الربيع الزهراني، قال: ثنا عباد بن العوام، قال: أخبرنا حصين قال: كتب أن لا يلبسوا من الحرير إلا ما كان سداه القطن أو كتان.
سمعت أحمد يكره الذهب والحرير للصغير والكبير من الذكور قيل:','2','855',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (678,'والمرأة عليها ذهب كثير· قال: إذا لم تظهره.
وسألت أحمد، عن الأعلام للثياب فرخص فيه وقال: أكثر ما جاء إلى أربع أصابع، وكأنه سها فيه إلى أربع أصابع.
حدثنا أبو حفص، قال: حدثنا عبد الله بن داود، عن هشام بن عروة قال:','2','856',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (679,'كان أبي يلبس البزكان الذي عليه أربع أصابع ديباج.
قِيل لأحمد: الرجل إذا اعتم يدخل العمامة تحت ذقنه؟ قال: نعم.
سألت إسحاق عن العمامة كيف يعتم بها قال: إن أدخلها تحت ذقنه جاز، وإن لم يفعل فهو أحب إليّ.
حدثنا إسحاق قال: أخبرنا الوليد بن مسلم، عن الأوزاعي، عن يحيى بن أبي عمرو الشيباني قال: كان رسول الله - صلى الله عليه وسلم - يعتم عمة العرب لا يدخل','2','857',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (680,'تحت ذقنه، وكل حسن جميل.
سألت إسحاق عن حديث معاوية أن النبي - صلى الله عليه وسلم - نهى عن لبس الحرير إلا مقطعًا· قال: يقول: لا يلبسه باجًا واحدًا ولكن إذا كان تكة أوجبب، أو نحو ذلك، فأما قميص تام أو رداء تام فلا.
سُئلَ إسحاق عن رجل في فص خاتمه مسمار من ذهب دقيق فقال: لو كان الخاتم من ذهب لرجوت وقال: مات خمسة من أصحاب رسول الله - صلى الله عليه وسلم - وخواتيمهم من ذهب.
حدثنا عمرو بن خالد المصري قال: حدثنا عيسى بن يونس، عن الأعمش، عن موسى بن عبد الله الخطمي، عن أمه قالت: كان خاتم حذيفة من','2','858',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (681,'ذهب فصه ياقوتة سمانجونة فيه كركيان متقابلان/١٤٢/بينهما مكتوب: الحمد لله·قال أبو محمد: كتبت عن عمرو بن خالد سنة ست عشرة ومائتين.
حدثنا يحيى الحماني قال: حدثنا شريك، عن حزر بن بشير قال: رأيت','2','859',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (682,'على البراء خاتمًا من ذهب بحادي أحمر·حدثنا يحيى قال: ثنا قيس، عن عمران بن موسى بن طلحة، عن أبيه قال: رأيت في يد طلحة بن عبيد الله خاتمًا من ذهب.','2','860',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (683,'حدثنا يحيى قال: حدثنا عبد الرحمن بن سليمان بن الغَسْيل قال: حدثني حمزة بن أبي أسيد قال: رأيت أبي عليه خاتم من ذهب ولقد مات وغُسَّل وإنه لفي يده.
حدثنا يحيى قال: ثنا عبيد الله بن موسى، عن حسن بن صالح، عن سماك','2','861',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (684,'قال: رأيت على جابر بن سمرة خاتمًا من ذهب.
سألت إسحاق عن المرفقة واللحاف من حرير· قال: كلما لم يلي جلده· قلت: فإن اللحاف بطانته من قطن وظهارته وشي· قال: لا بأس كلما لم يلي جلده فلا بأس.
حدثنا عيسى بن محمد قال: ثنا يزيد، عن ابن عون، عن محمد قال: قلت لعبيدة: افتراش الديباج كلبسه؟ قال: نعم.
حدثنا عيسى قال: حدثنا الفريابي، عن سفيان، عن أبي إسحاق، عن عمرو بن نعجة قال: أتى على ببرذون صفة سرجه ديباج فلم يركبه.','2','862',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (685,'سمعت أحمد يكره أن يمشي الرجل في نعل واحدة كراهية شديدة.
حدثنا يحيى بن عبد الحميد، قال: حدثنا أبو معاوية، عن الأعمش، عن أبي رزين، عن أبي هريرة· رأيته آخذًا بيده على جبهته ويقول: أشهد لسمعت رسول الله - صلى الله عليه وسلم - يقول: ((إذا انقطع شسع أحدكم فلا يمش في الأخرى حتى يصلحها)).
حدثنا محمد بن الوزير الدمشقي قال: ثنا الوليد بن مسلم قال: أنا الليث ابن سعد، عن جعفر بن ربيعة، عن الأعرج، عن أبي هريرة، عن رسول الله - صلى الله عليه وسلم -','2','863',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (686,'أنه قال لأبي هريرة: ((لا تمش في نعل واحدة، فإن الشيطان يمشي في نعل واحدة)).
حدثنا محمد بن عوف قال: حدثنا موسى بن داود، عن مندل، عن ليث بن أبي سليم، عن عبد الرحمن بن القاسم، عن أبيه، عن عائشة إن النبي - صلى الله عليه وسلم - مشى في نعل واحدة قال القاسم: ورأيت عائشة بعرفات تمشي في خف واحد.','2','864',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (687,'قلت لأحمد: فهذه النعال الغلاظ؟ قال: هذه السندية قال: إذا كان للوضوء، أو للكنيف، أو موضع ضرورة فلا بأس· وكأنه كره أن يمشي فيهما في الأزقة.
قِيل: فالنعل من الخشب؟ قال: لا بأس بها/١٤٣/أيضًا إذا كان موضع ضرورة.
حدثنا أحمد بن نصر قال: ثنا حبان بن موسى قال: سُئلَ عبد الله بن','2','865',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (688,'المبارك، عن هذه النعال الكرمانية فلم يعجبه، وقال: أما في هذه غنية عن تلك·سُئلَ أحمد: هل بلغك في الفتل كراهية؟ قال: لا - يعني - فتل الثوب.
حدثنا أحمد قال: حدثنا يزيد بن الحباب: قال: حدثنا معاوية بن عبد الكريم الثقفي قال أحمد وكان شيخًا صالحًا قال: رأيت على بكر بن عبد الله رداءً مفتولًا.
قلت لأحمد: ما تقول في الهدب في الثوب؟ قال: لابأس به.
حدثنا أحمد بن نصر قال: ثنا حبان قال: رأيت على عبد الله ملحفة','2','866',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (689,'مهدبة فقال: إلا أراني مهدبا.
حدثنا عبد الرحمن بن المبارك قال: حدثنا الصعق بن حزن، عن يونس بن عبيد، عن جابر بن شريك الأسلمي قال: أتيت رسول الله - صلى الله عليه وسلم - وهو في حلقة من أصحابه قلت: أيكم رسول الله؟ قال: ((ها أنا ذا)) · وإذا هو محتبي ببردة يضرب هدابها ظهور قدميه.','2','867',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (690,'كتاب الآداب','2','868',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (691,'سُئلَ أحمد عن الرجل يقدم من سفر فيأتيه أخوانه فيقوم لهم ويسلم عليهم ويعانقهم· قال: أرجو ألا يكون به بأس، ثم قال أحمد: استقبل النبي - صلى الله عليه وسلم - جعفرًا فالتزمه. قيل الأجلح عن الشعبي؟ قال: نعم· قِيل فحديث روي عن مخلد بن يزيد، عن مسعر، عن عون بن أبي جحيفة، عن أبيه في','2','869',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (692,'هذا فأنكره، وقال مخلد أي: لم يكن بالحافظ كتبت عنه بمكة.
وسألتُ إسحاقَ عن الرجل يقوم للرجل إذا قدم من سفر قال: لابأس، ولم يكرهه البتة.
حدثنا عيسى بن محمد قال: حدثنا سفيان، عن الأجلح، عن الشعبي أن جعفر بن أبي طالب لما قدم من الحبشة عانقه النبي - صلى الله عليه وسلم - وقبل بين عينيه.','2','870',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (693,'قلتُ لإسحاقَ فقبلة اليد وغير ذلك؟ قال: إذا كان في ذات الله فلا بأس وكرهه إذا كان تعظيمًا·حدثنا إسحاق قال: أنا بقية بن الوليد، عن معاذ بن رفاعة، عن ابن نزار قال: قبلة اليد إحدى السجدتين يعني أنه كرهه.
حدثنا عمرو بن عثمان، قال: أنا عثمان بن عبد الرحمن، قال: ثنا حمزة بن أبي حمزة، عن مكحول قال: لما قدم عمر الشام لقيه أبو عبيدة بن الجراح فقبل يده، وأراد إن يقبل رجله فلم يفعل.','2','871',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (694,'حدثنا أبو أمية قال: حدثنا موسى بن داود قال: ثنا عبد الرحمن المليكي قال: رأيت المغيرة بن حكيم ومحمد بن المنكدر التقيا كل واحد منهما (قبل) (١) يد/١٤٤/صاحبه.
حدثنا أبو أمية، قال: ثنا يحيى بن راشد البصري، قال: حدثنا طالب بن حجير العبدي، قال: حدثنا هود بن عبد الله بن سعد، قال: سمعت مزيدة العبدي يقول: أتينا رسول الله - صلى الله عليه وسلم - قال: فنزلت إليه فقبلت يده.
_________
(١) وردت بهامش المخطوط.','2','872',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (695,'قال أبو محمد: عطست عند أحمد بن حنبل فقال: يرحمك الله· فقلت: يغفر الله لكم فلم يغير عليّ، ثم عطست ثانية فقال: يرحمك الله. فقلت: يهديكم الله ويصلح بالكم· قلت له: ما تختار في هذا يا أبا عبد الله؟ قال: إذا عطس فقل يرحمك الله، ولتجب بقول: يهديكم الله ويصلح بالكم. قال: وهذا عن النبي - صلى الله عليه وسلم - من وجوه، وجعل ينكر قول إبراهيم أنه قال: إنما أحدث هذا الخوارج، ويتعجب من ذلك، وقال: الخوارج مارقة.
وسألتُ إسحاق قلت: كيف أحب اليك إن تشمت العاطس؟ قال: يقال له يرحمنا الله وإياك· ويقول هو يغفر الله لنا ولكم.
وعطس أبو يعقوب يومًا فقلت: يرحمك الله فقال يغفر الله لنا ولكم.
حدثنا إسحاق، قال: أنا أبو عامر، قال: حدثنا أبو معشر المدني، عن عبد الله بن يحيى، عن عمرة بنت عبد الرحمن، عن عائشة قالت: عطس رجل عند','2','873',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (696,'رسول الله - صلى الله عليه وسلم - فقال ماذا أقول يا رسول الله؟ قال: ((قل الحمد لله)) · قالوا: ماذا نقول له؟ قال: ((قولوا: يرحمك الله)) · قال: فماذا أقول لهم؟ قال: ((قل: يهديكم الله ويصلح بالكم)).
حدثنا أبو معن قال: حدثنا خالد بن الحارث قال: ثنا هشام، عن حماد، عن إبراهيم قال: إذا شمت أخاك فقل: يرحمك الله فإن معه الحفظة.','2','874',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (697,'حدثنا أبو معن قال: حدثنا وكيع، عن الأعمش، عن إبراهيم قال: كانوا يقولون للعاطس: يرحمنا الله وإياكم. يعنون الملائكة.
قلت لأحمد: فالرجل يشمت المرأة إذا عطست. قال: إن أراد إن يستنطقها ليسمع كلامها فلا، لأن الكلام فتنة، وإن لم يرد ذلك فلا بأس أن يشمتهن.
حدثنا أبو معن قال: ثنا نايل قال: ثنا الربيع بن صبيح، عن الحسن أنه','2','875',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (698,'يكره أن يسلم الرجل على النساء.
قلت لأحمد: فالرجل يسلم على النساء· قال: إن كُن شوابًا فأراد أن يستنطقهن فلا وكرهه وإن كن عجائز فلا بأس.
حدثنا عمرو بن عثمان، قال: حدثنا عبد الملك بن محمد، قال: خرجت أنا والأوزاعي من مسجد بيروت فمررنا بامرأة كهلة فبدأها/١٤٥/بالسلام.
حدثنا عيسى بن محمد، قال: ثنا حجاج، عن شعبة قال: سألت الحكم: أسلم على النساء؟ قال: كان شريح يسلم على النساء.
حدثنا عيسى قال: ثنا عبد الصمد، عن أبيه، عن إسحاق بن سويد قال: كان','2','876',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (699,'خالد بن عمرو ينهانا إن نسلم على النساء، ويقول: تلك المواصلة.
سألتُ أحمدَ قلتُ: أهل الذمة يكنون؟ قال: نعم لا بأس به، وذكر أن عمر قد كنى قلت: فالصبي يكنى؟ قال: نعم أليس قد قال النبي - صلى الله عليه وسلم -: ((أبا عمير ما فعل النغير)).','2','877',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (700,'حدثنا المسيب، قال: حدثنا سفيان، عن أيوب، عن يحيى بن أبي أن عمر بن الخطاب كنَّى نصرانيًا باب: حسان، وكان اسمه فرافصة.
حدثنا عمرو بن عثمان، قال: حدثنا عبد الملك بن محمد، عن الأوزاعي قال: لا بأس بعيادة النصراني وكتاب وصيته، ولا يصافحه، ولا يكنيه.
قلت للأوزاعي: فإن قدم من سفر فصافحني؟ قال: لا بأس.
حدثنا عمرو قال: ثنا عبد الملك بن محمد، عن سعيد بن عبد العزيز، عن ربيعة بن يزيد إن أبا الدرداء وفضالة','2','878',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (701,'بن عبيد عادا أركون دمشق.
حدثنا عبد الرحمن بن سلام، قال: ثنا زيد بن حباب:، قال: أنا المنهال بن عيسى البصري، قال: أنبا غالب القطان قال: قلت للحسن: إن لنا جيرانًا نصارى ينيلوننا من معروفهم، ويشيعون جنائزنا فأكافئهم؟ قال: كافئهم إذا أتيت الباب: فقل من هاهنا أدخل؟ فإذا دخلت فقل: كيف مريضكم، كيف تجدونه، فإذا أردت أن تقوم فقل الشفاء والعافية من الله· قال زيد: وأخبرنا بعض البصريين عن الحسن أنه كان إذا عزى النصراني والذمي قال: لا أصابكم الله إلا بخير.','2','879',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (702,'قلتُ لإسحاقَ: مسلم قال لنصراني مرحبًا وأهلًا؟ قال: بئس ما صنع لا ينبغي إن يحيى بتحية الإسلام ولا يكنى.
حدثنا يحيى بن عبد الحميد، قال: حدثنا أبو بكر، عن أبي سنان، عن سعيد بن جبير قال: قلت لابن عباس إن هؤلاء العلوج يقولون لنا: بارك الله فيكم أفنرد عليهم؟ قال: لو إن فرعون قال لي: بارك الله فيك. قلت له: وفيك.
حدثنا يحيى، قال: حدثنا ابن مبارك، عن سلام بن مسكين، عن أبي يزيد المدني أن النبي عليه السلام لقي أبا جهل فأخذ بيده.','2','880',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (703,'حدثنا أبو معن، قال: حدثنا أبو قتيبة، قال: حدثنا إسرائيل، عن جابر، عن الشعبي قال: إذا عطس اليهودي فقل هداك الله.
حدثنا أبو موسى، قال: حدثنا أبو معاوية، قال: حدثنا الأعمش، عن إبراهيم قال: إذا كانت لك الحاجة إلى اليهودي أو النصراني فأبدأه بالسلام.
قلتُ لإسحاق: فالرجل يقول للمشرك: إنه رجل عاقل. قال: لا ينبغي أن يقال لأنه ليست لهم عقول.
حدثنا يحيى قال: حدثنا شريح، عن محمود بن حرب، عن عياض الأشعري','2','881',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (704,'قال: قال عمر بن الخطاب: لا تأمنوهم إذ خونهم الله، ولا تعزوهم إذ أذلهم الله، ولا تقربوهم إذ بعدهم الله.
سمعت أحمد يقول: الرجل إذا كان صاحب بدعة يظهر ذلك أومعلنًا بفسقه؟ فليست له غيبة.
سألت إسحاق عن غيبة أهل البدع؟ قال: ليست لهم حرمة، وذكرعن ابن المبارك قال: ليس لهم غيبة ولكن أكره إن يعود الرجل لسانه، وكذلك أهل الشرك وذكر، عن ابن سيرين كراهيته.
وسألت محمد بن بشار قلت: الرجل يغتاب أهل البدع؟ قال: ليست لهم غيبة ولا يعجبني، وأما قولك: إن فلانًا صاحب بدعة فليس هذا غيبة، وكذلك قولك في الحديث كان فلان يغلط وفلان كان أثبت من فلان ونحو هذا فليس هذا غيبة، ولكنه شيء تبينه للناس· قلت فإن قال رجل: كان عمرو بن عبيد رجل سوء، وكان عباد بن صهيب','2','882',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (705,'رجل سوء؟ فكأنه رخص فيه.
حدثنا يحيى بن عثمان قال: أخبرنا بقية قال: سألت الأوزاعي قلت: من الذي ينبغي لي أن أكف عنه أرى الرجل يسكر، ويزني، ويعمل عملًا لا يحل له؟ · قال: إن أسرها فليس لك أن تذكرها، وإن كان صاحبها لا يبالي من رآه ولا يستتر فلا غيبة له.
حدثنا أحمد بن نصر، قال حدثنا عبد الله بن غالب وعمر بن هارون، عن الربيع بن صبيح، عن الحسن قال: ليس لأهل البدع غيبة.
حدثنا عبد الله بن عبد الوهاب، قال: حدثنا زياد بن الربيع، عن عبد الرحمن','2','883',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (706,'بن أذينة قال: حدثنا أشياخنا قال: ثلاثة لا حرمة لهم ولا غيبة، الوالي الظالم الجائر، والفاسق المعلن بفسقه، وصاحب البدعة.
حدثنا عبد الله بن عبد الوهاب، قال: ثنا عليلة بن بدر الأعرجي، قال: حدثنا أبان، عن أنس بن مالك قال: قال رسول الله صلى اله عليه وسلم: ((من ألقى جلباب: الحياء فلا غيبة له)).','2','884',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (707,'حدثنا عبد الله، قال: ثنا أبو عوانة، قال: ثنا قتادة، عن الحسن قال: ليس بينك وبين الفاسق حرمة.
حدثنا أبو معن، قال: حدثنا/١٤٧/معاذ بن هشام، قال: حدثني أبي، عن قتادة، عن الحسن أنه كان يقول: لا حرمة للفاجر· قال قتادة: فكأنه يرى إذا اغتبت المصلي الفاجر أنه لا بأس به.
حدثنا محمد بن رافع، قال: ثنا الجارود بن يزيد، قال: حدثنا بهز بن حكيم، عن أبيه، عن جده قال: قال رسول الله - صلى الله عليه وسلم -:','2','885',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (708,'((أترعون عن ذكر الفاجر متى يعرفه الناس اذكروه بما فيه يحذره الناس)).
سألت إسحاق عن غيبة السلطان الجائر قال: لا يكون فيهم إلا ما يكره للإنسان أن يعود لسانه.
حدثنا الأخضر، قال: حدثنا عارم، قال: حدثنا خالد، سمعت عبيد الله يقول في غيبة الخوارج والسلطان الذي قد أعلن لم ير لهم غيبة، فأما من يُعلَم أنه مذنب وهو يحب إن يستتر فرأى ذلك منهم غيبة.','2','886',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (709,'سألت إسحاق عن غيبة أهل الشرك قال: ليس أكرهه ولكن أكره أن يعود لسانه. حدثنا أبو معن قال: حدثنا معاذ بن هشام قال: حدثني أبي، عن قتادة، عن محمد بن سيرين أنه كان يكره إن يُغتاب النصراني.
حدثنا عبد الله بن عبد الوهاب، قال: حدثنا حماد بن زيد، قال: حدثنا طوق بن وهب، قال: نقهت من مرض مرضته فدخلت على محمد بن سيرين فرأى من حالي فقال ما يمنعك من فلان الطبيب نصراني، ثم قال: فلان أطب منه، ثم قال: استغفر الله اغتبته.
سألت إسحاق عن لعن أهل البدع· قال: يستوجبون اللعنة.
حدثنا أحمد بن سلمان، قال: ثنا سفيان، عن عمرو بن دينار، قال: قال لنا','2','887',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (710,'طاوس: أخزوا معبدًا.
حدثنا أبو معن قال: حدثنا إبراهيم بن المبارك الخزامي قال: حدثني محمد ابن طلحة بن عبد الرحمن بن طلحة بن عثمان التيمي قال: حدثني عبد الرحمن بن سالم، عن أبيه، عن جده قال: قال رسول الله - صلى الله عليه وسلم -: ((إن الله اختارني واختار لي أصحابي، فجعل لي منهم وزراء وأنصارًا فمن سبهم','2','888',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (711,'فعليه لعنة الله والملائكة والناس أجمعين لا يقبل الله منهم صرفًا ولا عدلًا)).
قلت لأحمد: كيف يحب أن يكتب الرجل في صدر الكتاب؟ فسهل في ذلك، وقال: الأمر فيه واسع·حدثنا بشر بن هلال، قال: ثنا عبد الوارث بن سعيد، قال: ثنا عمرو، عن الحسن أنه كان لا يرى بأسًا إن يبدأ الرجل باسم صاحبه إذا كتب إليه، وكان الحسن يفعل/١٤٨/ذلك، وكان عمرو يكره إن يكتب بسم الله الرحمن الرحيم لفلان ولا يرى بأسًا أن يكتب إلى أبي فلان.','2','889',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (712,'حدثنا يحيى بن عبد الحميد، قال: حدثنا ابن مبارك، عن يونس، عن الزهري، عن عبيد الله بن عبد الله، عن ابن عباس قال: حدثني أبو سفيان بن حرب قال: دخلت على هرقل قال: فألقى إلي كتابًا، فقال: هل تعرف هذا؟ فقلت: نعم· فإذا فيه: من محمد رسول الله - صلى الله عليه وسلم - إلى هرقل عظيم الروم سلام على من اتبع الهدى أما بعد.
حدثنا علي بن عثمان، قال: حدثنا عبد الملك بن الوليد بن معدان، قال: حدثني أبي أن عمر بن الخطاب كتب إلى أبي موسى الأشعري: بسم','2','890',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (713,'الله الرحمن الرحيم من عبد الله عمر أمير المؤمنين إلى عبد الله بن قيس سلام عليك، فإني أحمد إليك الله الذي لا إله إلا هو، أما بعد.
حدثنا يحيى، قال: حدثنا شريك، عن ليث، عن نافع، عن ابن عمر أنه كان إذا كتب إلى معاوية بدأ بنفسه، فبدت له إليه حاجة فكلموه فبدأ بمعاوية.
حدثنا يحيى قال: ثنا شريك، عن عمار الدهني، عن كريب، عن ابن عباس','2','891',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (714,'أنه كتب إلى حبر تيماء فبدأه بالسلام فقِيل له في ذلك فقال: إن الله هو السلام.
قلت لأحمد: فكيف يُكتبْ في عنوان الكتاب؟ قال: يُكتبْ إلى أبي فلان ولا يكتب لأبي فلان، ليس له معنى إذا كُتب لأبي فلان.
قلت: فإن كتب إلى أبي فلان حفظه الله ونحو ذلك؟ فسهَّل في ذلك ولم ير به بأسًا. قلت: فإن كتب إلى أبي فلان في سطر وكتب تحته فلان بن فلان؟ فلم يعرف هذا كيف هو.
سألت إسحاق قلت كيف أحب إليك إن يكتب عنوان الكتاب؟ قال: يكتب من فلان إلى فلان، فإن كان الأب والابن فأنه يبدأ بالأب. ورأيت أبا يعقوب يكتب كثيرًا إلى أبي فلان.','2','892',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (715,'حدثنا عيسى بن محمد، قال: حدثنا الهيثم بن جميل، عن شريك، عن مغيرة، عن إبراهيم أنه كان يكره إن يكتب في جوف الكتاب بسم الله الرحمن الرحيم وكأنه لم ير به بأسًا إن يُكتب في عنوانه. حدثنا عيسى قال: حدثنا يزيد بن هارون، عن ابن عون، عن محمد إن رجلًا كتب بين يدي عمر: بسم الله الرحمن الرحيم لفلان فقال ابن عمر: مه، إن اسم الله هو له.
حدثنا عيسى، قال: ثنا أبو أسامة، عن ابن عون، عن نافع قال: كانت لابن','2','893',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (716,'عمر حاجة إلى معاوية فأراد إن يكتب إليه فقالوا: ابدأ به، فلم يزالوا به حتى كتب بسم الله الرحمن الرحيم إلى معاوية·حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا/١٤٩/شعبة، عن منصور بن زاذان، عن محمد بن سيرين قال: كتب العلاء بن الحضرمي إلى النبي - صلى الله عليه وسلم - فبدأ باسم نفسه من العلاء بن الحضرمي إلى رسول الله - صلى الله عليه وسلم - · قال شعبة: وكان الحكم يكره إن أسم غيره.','2','894',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (717,'حدثنا يحيى بن عثمان قال: حدثنا بقية بن الوليد، عن ثور بن يزيد قال: كتبت لخالد بن معدان من خالد بن معدان إلى الوليد بن عبد الملك. قال: وحدثنا بقية، عن ثور قال: كتبت لمكحول: إلى فلان بن فلان من مكحول.
حدثنا عباس بن عبد العظيم قال: سمعت أبا الوليد قال: حدثنا عثمان بن زائدة قال: كتب إليّ سفيان الثوري: إلى عثمان بن زائدة من سفيان بن سعيد، أما بعد.','2','895',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (718,'حدثنا محمد بن يحيى بن عبد الكريم، قال: حدثنا هاشم بن القاسم، عن الأشجعي قال: قال سفيان: يكره إن يكتب الرجل إلى الرجل فيبدأ به، ولكن اكتب إلى أبي فلان.
حدثنا محمد بن يحيى قال: ثنا عبد الله بن داود، عن جعفر بن برقان، عن ميمون بن مهران أن عبد الله بن عمر كتب: من عبد الله بن عمر إلى','2','896',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (719,'عمر بن الخطاب. سُئلَ إسحاق عن ختم الكتاب؟ فقال: نعم.
حدثنا أبو بشر حاتم بن سالم قال: حدثنا إبراهيم بن محمد قال: ثنا يحيى بن وهب الكلابي، عن أبيه، عن جده أن النبي - صلى الله عليه وسلم - كتب كتابًا إلى أكيدر لدومة الجندل فيه أمان لهم من الروم فلم يجد يومئذ خاتمًا فختم له','2','897',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (720,'بظفره.
قال إبراهيم بن محمد: فلقيت رجلًا من آل أكيدر فقلت: بأي ظفر ختم لكم النبي - صلى الله عليه وسلم -؟ قال: بهذه يعني الإبهام.
حدثنا يحيى بن عثمان، قال: ثنا ابن حمير، حدثنا: علي، عن ابن جريج، عن ابن أبي مليكة، عن ابن عمر قال: جواب الكتاب حق على كل مسلم، وخاتم الصحيفة مكرمة لصاحبها.','2','898',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (721,'حدثنا إسحاق، قال: أنا بقية بن الوليد، عن عمر بن موسى، عن أبي الزبير، عن جابر قال: قال رسول الله - صلى الله عليه وسلم -: ((إذا كتب أحدكم كتابًا فليترب الكتاب فأنه أنجح للحاجة)).','2','899',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (722,'حدثنا محمود بن خالد، قال: ثنا الوليد بن مسلم، قال: ثنا ابن جابر، عن أبي كبشة، قال: حدثني سهل بن الحنظلية أن عيينة بن بدر والأقرع بن حابس سألا رسول الله - صلى الله عليه وسلم - شيئًا، فأمر معاوية فكتب لهما وختم كتابهما، ثم رمى به إليهما.
سمعت أحمد يقول في حديث أنس إن رجلًا قال للنبي - صلى الله عليه وسلم -: يا خير/١٥٠/البرية· قال: ((ذاك أبي إبراهيم)) قال: قد روي غير هذا أنه قال: ((أنا أول من تنشق عنه الأرض)) وقال الله: {كُنتُمْ خَيْرَ أُمَّةٍ','2','900',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (723,'أُخْرِجَتْ لِلنَّاسِ} [آل عمران: ١١٠] وذهب فيه إلى أن النبي - صلى الله عليه وسلم - أراد به التواضع.
حدثنا يحيى بن عبد الحميد، قال: حدثنا ابن إدريس، عن المختار بن فلفل، عن أنس قال: قال رجل للنبي - صلى الله عليه وسلم - يا خير البرية· قال: ((ذاك أبي إبراهيم)).
حدثنا علي بن عثمان، قال: حدثنا حماد بن سلمة، قال: أخبرنا علي بن زيد، عن أبي نضرة قال: خطبنا ابن عباس على هذا المنبر منبر البصرة فقال قال نبي الله - صلى الله عليه وسلم -: ((ما من نبي إلا له دعوة ينجزها في الدنيا، وإني اختبأت دعوتي شفاعة لأمتي وأنا سيد ولد آدم يوم القيامة ولا فخر وأنا أول من تنشق عنه الأرض ولا فخر وبيدي لواء الحمد ولا فخر فآدم ومن دونه تحت لوائي ولا فخر)).','2','901',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (724,'حدثنا أبو جعفر محمد بن سليمان، نا حديج بن معاوية، عن أبي إسحاق، عن صلة بن زفر، عن حذيفة قال: قال أصحاب النبي - صلى الله عليه وسلم - يا رسول الله إبراهيم خليل الرحمن وموسى كلمه الله تكليما، وعيسى كلمة الله وروحه فماذا أعطيت أنت؟ قال: ((ولد آدم كلهم يوم القيامة تحت رايتي، وأنا أول من يفتح له باب: الجنة)).','2','902',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (725,'<span data-type="title" id=toc-205>باب: إخصاء الدواب</span>
سُئلَ أحمد بن حنبل، عن إخصاء الدواب والغنم فكره للسمن وغير ذلك، إلا أن يخاف عضاضه. حدثنا هدبة بن خالد، قال: حدثنا همام، قال: حدثنا نافع أن ابن عمر كان يكره إخصاء الذكور من الإبل، والذكور من الغنم، وإخصاء الذكور من البقر.','2','903',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (726,'حدثنا عمرو بن عثمان، قال: حدثنا الفاريابي، عن سفيان، عن عاصم بن عبيد الله، عن سالم بن عبد الله، عن ابن عمر، عن عمر أنه نهى، عن خصاء الغنم، وقال أيضًا: النماء في الذكور.
حدثنا عبد الله بن الزبير الحميدي قال: ثنا سفيان قال: حدثنا هشام بن عروة إن أباه أخصى بغلًا له.
حدثنا الحسين بن محمد السعدي، قال: ثنا سلم بن قتيبة، قال: حدثنا مالك ابن مغول، قال: سألت عطاء عن خصاء الفحل؟ قال: إذا خفت','2','904',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (727,'عضاضه وسوء خلقه فلا بأس.
حدثنا الحسين بن محمد، قال: ثنا سلم بن قتيبة، حدثنا سلام بن مسكين، قال: سألت قتادة عن خصاء الديك والسنور فكرهه.
حدثنا عمرو بن عثمان، قال: حدثنا بقية، عن الأوزاعي، عن يحيى قال: لا بأس بخصاء ما لا نسل له، وخصاء الفرس الصوول، وكذا بتر الذنب. /١٥١/','2','905',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (728,'<span data-type="title" id=toc-206>باب: استعمال المرعراء</span>
قلتُ لإسحاق بن إبراهيم: الرجل يأخذ المرعراء من ظهر الشاه بالمشط وهو يشتد عليها؟ قال: يأثمون شديدًا.','2','906',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (729,'<span data-type="title" id=toc-207>باب: استعمال بقر الحرث في غير الحرث</span>
قلتُ لإسحاق: بقر الحرث هل يكره أن يستعمل في غير الحرث؟ قال: لا بأس في استعمالها في غير الحرث.
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: حدثنا محمد بن عمرو، عن أبي سلمة، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((بينما رجل يسوق بقرة أعيا، فركبها، فالتفتت إليه فقالت: إنا لم نخلق لهذا، إنما خُلقنا لحراثة الأرض)).','2','907',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (730,'<span data-type="title" id=toc-208>باب: حرق كور الزنابير</span>
سألت إسحاق عن كور الزنابير تُحرق بالنار؟ قال: كل شيء يحرق بالنار عقرب أو حية فكل هذا مكروه.
قلتُ لإسحاق أيضًا: كور الزنابير تحرق بالنار؟ فكرهه، وكذلك غيره.
حدثنا سعيد بن منصور، قال: حدثنا أبو عوانة، عن الأعمش، عن إبراهيم أنه كره أن تحرق الحية والعقرب بالنار.
حدثنا يحيى الحماني قال: ثنا حفص وموسى بن محمد الأنصاري وأبو معاوية وعباد بن العوام، عن الشيباني، عن الحسن بن سعد، عن عبد الرحمن بن عبد الله، عن أبيه قال: كنا مع النبي - صلى الله عليه وسلم - في سفر فنزلنا على قرية','2','908',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (731,'نمل فأحرقناها، فذكرنا ذلك للنبي عليه السلام فقال: ((لا ينبغي أن يعذب بعذاب الله أحد)).','2','909',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (732,'<span data-type="title" id=toc-209>باب: تسمية البهيمة والطير</span>
قلتُ لإسحاق: البهيمة والطير يكون في الدار فيسميها باسم يدعوه بها؟ قال: كلما كان المسمى بأسامي سموها به في ما مضى فلا بأس.
حدثنا أحمد بن الأزهر بن منيع، قال: ثنا منصور بن أبي نويرة، قال: حدثنا العرزمي، عن الحكم، عن مقسم، عن ابن عباس قال: كان اسم فرس النبي عليه السلام المرتجن وبغلته الدلدل، وحماره اليعفور، ورايته العقاب، وناقته العضبا، ودرعه ذات الفضول.
حدثنا سعيد بن منصور، قال: حدثنا عبد الرحمن بن أبي الزناد، عن أبيه، عن','2','910',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (733,'عبيد الله بن عبد الله، عن ابن عباس قال: سلَّ رسول الله - صلى الله عليه وسلم - سيفه ذو الفقار يوم بدر.
حدثنا المسيب بن واضح، قال: حدثنا أبو إسحاق الفزاري، عن ابن جريج قال: أخبرني مرة إن اسم سيف رسول الله - صلى الله عليه وسلم - /١٥٢/ذو الفقار، ودرعه ذات الفضول.','2','911',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (734,'قال أخي عمران بن موسى، عن نافع بن جبير بن مطعم قال: كان لرسول الله - صلى الله عليه وسلم - جمل يقال له.
حدثنا المسيب بن واضح، قال: ثنا أبو إسحاق، عن جعفر بن الحارث، عن محمد بن إسحاق، عن يزيد بن أبي حبيب قال: كان اسم فرس رسول الله - صلى الله عليه وسلم - الشكب.
حدثنا عمرو بن عثمان قال: حدثنا عبد الملك بن محمد قال: نا زهير بن','2','912',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (735,'محمد قال: كانت للنبي عليه السلام عمامة سوداء يُقال لها السجاب، ودرع يُقال له ذو الفضول وكان له ثلاثة أنيق: الجدعاء، والقصواء والبهرة، وبغلته يقال لها دلدل، وكانت رايته يقال لها العقاب، وقدحه يقال له الغمر، ومرآته يقال لها: المدلة. قال: وسمعت عثمان بن عطاء يحدث عن أبيه وزهير بن محمد يقولان كان سيف النبي عليه السلام فيه حلية يسيرة كان يقال له ذو الفقار· قال: وحدثني زهير بن محمد قال: كان للنبي - صلى الله عليه وسلم - فرسان يقال لأحدهما: المرتجز، والآخر السكت.
قال: وحدثنا عثمان بن عطاء، عن أبيه قال: كان للنبي عليه السلام حمار يقال له يعفور، وجمل يقال له: الديباج.','2','913',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (736,'قال: وحدثنا عبد العزيز بن إسماعيل بن عبيد الله قال حدثني عروة [] أن عنز النبي - صلى الله عليه وسلم - كان يقال لها ثمرة.','2','914',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (737,'<span data-type="title" id=toc-210>باب: اتخاذ الطيور والحمامات في المنازل</span>
سمعت أحمد يقول: لا بأس أن يتخذ الرجل في منزله الطيور والحمامات المقصوصة ليستأنس إليها، فإن تلهى بها فإني أكرهه.
حدثنا الحسين بن مهدي بن مالك قال: حدثنا عبيد الله بن موسى قال: نا أبو عبيدة الناجي، عن الحسن قال: قال رسول الله - صلى الله عليه وسلم -: ((اتخذوا الحمامات المقصوصات في بيوتكم فإنها تلهي الشيطان عن صبيانكم)).','2','915',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (738,'قلت لأحمد: فإن اتخذ قطيعًا من الحمام يطير؟ فكره ذلك كراهية شديدة، ولم يرخص فيه إذا كان يطير، وذلك أنها تأكل من أموال الناس وزروعهم.
سُئلَ إسحاق عن الرجل يتخذ الكندوج للحمام؟ فأملى: أما سألت عنه من بناء بروج الحمام التي تتخذ في القرى وتضر أهل القرى وغيرهم فإن كان رجلًا زرع في القرية كما يزرعون فأرجوا إلا يكون به بأس، فإن لم يكن له في القرية شيء فلا. قال: وأما في الدور فإني أكرهها أيضًا بحال ما تختلط/١٥٣/حمامه بحمام غيره فتفرخ ولا يدري فراخها، فإن اتخذها ولم يستيقن بالاختلاط بحمام غيره فلا بأس. حدثنا إسحاق، قال: ثنا بقية بن الوليد، عن الوليد بن كامل البجلي، عن نصر بن علقمة قال: سُئلَ رسول الله - صلى الله عليه وسلم - عن الرجل يتخذ الحمام في القرية قال: ((إن كان يزرع كما يزرعون، وإلا فلا)).','2','916',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (739,'حدثنا أبو حفص قال: حدثنا أبو صالح قال: حدثني حرملة، عن يزيد بن أبي حبيب أنه كان يكره حمام بروج البر، وكان يقول: لا ترد ضالتها، ولا يخرج زكاتها، ولا تمنع من فساد. قال أبو صالح: البر ليس لأحد.
حدثنا سعيد بن منصور، قال: حدثنا مسلم بن خالد، عن ابن أبي نجيح، عن مجاهد في قوله {أَتَبْنُونَ بِكُلِّ رِيعٍ آيَةً تَعْبَثُونَ} [الشعراء: ١٢٦] قال: يعني: بروح الحمام.
حدثنا أحمد بن حنبل، قال: ثنا يزيد بن هارون، قال: رأيت أبا بلخ وكان جارًا لنا وكان يتخذ الحمام.','2','917',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (740,'حدثنا عبد الرحمن بن بحر قال: حدثنا بقية بن الوليد قال: حدثني أبو سفيان الأنباري، عن المثنى بن حبيب بن أبي كبشة الأنباري، عن أبيه، عن جده أبي كبشة صاحب النبي عليه السلام إن رسول الله - صلى الله عليه وسلم - كان','2','918',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (741,'يعجبه النظر إلى الأترج والحمام الأحمر.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا أشعث، عن الحسن في الرجل يصيد الحمام قال: يرده على أهله ولا يحل له.
حدثنا علي بن عثمان قال: حدثنا أبو عوانة، عن مغيرة، عن إبراهيم قال كان يكره صيد الحمام الأهلي أن يحالَّ الرجل صاحبه فيصيد كل واحد منهما حمام صاحبه وإن أحل بعضهم لبعض.','2','919',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (742,'<span data-type="title" id=toc-211>باب: قطع السدر</span>
سُئلَ أحمد عن قطع السدر فكرهه كراهة شديدة، وذهب إلى حديث النبي عليه السلام وقال أحمد: قل إنسان فعله إلا رأى ما يكره في الدنيا. يعني: قطع السدر.
حدثنا أبو بكر محمد بن عبد الرحمن، قال: حدثنا أبو أسامة، عن ابن جريج، عن عثمان بن أبي سليمان، عن محمد بن سعيد بن جبير بن مطعم، عن أبي حبشي، قال: قال رسول الله - صلى الله عليه وسلم -: ((من قطع سدرة صوب الله رأسه','2','920',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (743,'في النار)).
حدثنا محمد بن عبد الرحمن قال: حدثنا أبو أسامة، عن ابن جريج، عن عمرو بن دينار، عن عروة بن الزبير قال: من قطع سدرة صُبَّ عليه العذاب صبًّا.
حدثنا محمد بن عبد الأعلى قال: حدثنا هشام/١٥٤/بن يوسف قال: حدثنا عبد الملك الهمداني قال: سألت جعفر عن قطع السدر فقال: كانت','2','921',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (744,'لأبي سدرة فكان يقطعها.
حدثنا أبو بكر محمد بن عبد الرحمن الجعفي قال: حدثنا الحسن بن بلال، عن نصر، عن هشام بن عروة قال: كان أبي يقطع سدر أرضه يجعلها أبوابًا.','2','922',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (745,'<span data-type="title" id=toc-212>باب: قطع النخلة</span>
سُئلَ أحمد عن رجل في دار نخلة قد ضيقت عليه؟ قال: يقطعها إنما كره السدر· وذكر حديث ابن جريج، عن ابن حبشي.
حدثنا عمران بن موسى، قال: ثنا عبد الوارث، عن عمرو، عن الحسن أنه كان يكره أن يقطع النخلة وهي حية.
سألت إسحاق عن قطع الشجر المثمر؟ قال: إذا كان لعلة فلا بأس، فإنهم ربماقطعوا الخوخ ليعود أصله أيضًا. قلت: فقطعه لغير علة باعه من النجارين؟ فكرهه. قلت: فإن كان في الشتاء وليس عليها ثمرة أتكرهها؟ قال: شديدًا، إنه وإن لم يكن أيام الثمرة فأصلها مثبت.
حدثنا أحمد بن نصر قال: حدثنا محمد بن كثير، عن الأوزاعي أنه كره قطع الثمرة (١).
_________
(١) ورد بالهامش: لعله: المثمرة.','2','923',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (746,'<span data-type="title" id=toc-213>باب: الرجل يخوف الشجرة بالمنشار ليحمل الثمر</span>
قلتُ لإسحاق: الرجل يكون له الثمرة، الشجر الكمثرى أو التفاح أو غير ذلك فربما لم يحمل فيضع المنشار في أصله يزعم أن يخوفه ليحمل فيحمل تلك السنةما تقول في ذلك؟ قال: إن كان ذلك شيئًا قد جُرِّبَ فلا بأس، ولم يكرهه· قلت: فإن فعلوا بالشجر سوى النخل شبه التلقيح؟ قال: كل شيء يراد به منفعته فلا بأس.
حدثنا يحيى الحماني، قال: ثنا أبو عوانة، عن شمال، عن موسى بن طلحة، عن أبيه قال: مررت مع النبي - صلى الله عليه وسلم - على قوم في رءوس النخل فقال: ((ما يصنع هؤلاء؟)) · قلت: يجعلون الذكر في الأنثى - يعني: يلقحون -· فقال النبي - صلى الله عليه وسلم -: ((ما أظن ذلك يعني شيئًا)) فتركوه فلم يحمل تلك العام، فأُخبر بذلك النبي عليه السلام فقال: ((إن كان ينفعهم فليعودوا)).','2','924',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (747,'<span data-type="title" id=toc-214>باب: أكل الكراث والثوم والبصل</span>
سُئلَ أحمد عن أكل الكراث والثوم والبصل، فكرهه.
قيل: الكراث مثل الثوم؟ قال: إذا كان في موضع يوجد به.
حدثنا إسحاق بن إبراهيم، قال: أخبرنا معتمر بن سليمان، عن ليث، عن عطاء، عن جابر بن عبد الله، عن النبي عليه السلام قال: ((من أكل الثوم والبصل والكراث فلا يقربن مسجدنا)).
حدثنا إسحاق، قال: أخبرنا معتمر/١٥٥/، قال: حدثني مظهر بن أبي مجلز أنه رأى على مائدة عمر بن عبد العزيز كراثًا.','2','925',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (748,'<span data-type="title" id=toc-215>باب: الملازقة</span>
سألت إسحاق عن الملازقة، قلت: القوم يجتمعون فيخرج هذا درهمًا وهذا درهمًا حتى يجمعوا دراهم ثم يشترون بها شيئًا ويأكلون؟ قال: لا بأس بها في السفر، إنما هي رخص في السفر.
حدثنا عمرو بن عثمان، قال: ثنا الوليد بن مسلم، قال: قال أبو عمرو الأوزاعي: لا بأس بأن يتخارج القوم النفقة، ولا بأس أن يشتري كل رجل منهم يومًا علفًا سعر ذلك اليوم، فيأخذ قوت علف دابته، ثم يقسم بقيته بينهم، ثم يشتري الآخر من الغد كذلك فلا بأس، هم قوم يتخارجون نفقة دوابهم غير أنها غير مجموعة فذكرته لليث بن سعد فقال: يجعلونها مع رجل أحب إليّّ.
حدثنا الحماني قال: حدثنا شريك، عن أبي حمزة أن طلحة الأيامي','2','926',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (749,'وزبيدًا وسلمة بن كهيل دخلوا بستانًا فأعطوا صاحب البستان درهمًا ودخلوا فأكلوا حتى شبعوا، ثم خرجوا','2','927',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (750,'للعب بالشطرنج. قيل لإسحاق: أترى بلعب الشطرنج بأسًا؟ قال: البأس كله. قيل: فإن أهل الثغور يلعبون به للحرب؟ قال هو فجور.
حدثنا أبو أمية قال: حدثنا محمد بن الحجاج قال: حدثنا عذار بن يحيى، عن مكحول، عن واثلة بن الأسقع قال: قال رسول الله - صلى الله عليه وسلم -: ((إن لله في كل يوم ثلاثمائة نظرة وستين نظرة، ألا وليس لصاحب الشاة فيها شيء)).
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: ثنا أبي حدثنا عاصم بن محمد، عن','2','928',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (751,'عمرو الملائي قال: إن لله سبع عشرة لحظة في اليوم والليلة لا ينال أهل الشاهيين منها شيء. يعني: الشطرنج.
حدثنا الحسن بن عرفة حدثنا الحسن بن قرعة، قال: حدثنا السكن بن إسماعيل، قال: ثنا أبو الأشهب، عن ليث، عن عبد الرحمن بن خالد أنه سمع','2','929',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (752,'صوتًا أو مناديًا يقول إن الله يغفر لأهل الأرض إلا أصحاب الشاهجين، يقول قتلته والله وما قتله، ومات والله، وما مات.
حدثنا إبراهيم بن بشار، قال: حدثنا عبد المجيد، عن أبيه قال: قال النبي - صلى الله عليه وسلم -: ((أشد الناس عذابًا يوم القيامة صاحب الشاهيل، ألا تسمعه يقول: قتلته والله ومات والله. افتراء وكذبًا على الله)). حدثنا أبو إسحاق إبراهيم بن بشار، قال: حدثنا عبد الله بن ميمون القداح، عن شهاب، عن عبد الملك بن عمير قال: يغفر الله لكل مسلم في أيام','2','930',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (753,'العشر في كل يوم ثنتي عشرة مغفرة إلا صاحب الشطرنج فإنه لا يغفر له، ألا تسمعه يقول: مات والله، قتلته والله· افتراء وكذبًا على الله.
حدثنا سعيد بن منصور، قال: ثنا أبو معاوية، عن سعد بن طريف، عن أصبغ ابن نباتة قال: مر عليٌّ بقوم يلعبون بالشطرنج فقال: ما هذه التماثيل التي أنتم لها عاكفون.
حدثنا الحماني، قال: حدثنا رباح بن خالد، عن شريك، عن ابن أبي ليلى، عن','2','931',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (754,'الحكم، عن أبي البختري، عن علي قال: لا تسلموا على أصحاب الشطرنج.
حدثنا العلاء بن عمرو قال: حدثنا سعيد بن مسلمة، عن ليث قال: كان إبراهيم لا يسلم على أصحاب الشطرنج.','2','932',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (755,'حدثنا سعيد بن منصور، قال: حدثنا أبو معشر، عن محمد بن كعب أنه سُئلَ عن لعب الشطرنج فقال أدنى ما يكون يوم القيامة أنه يعرض مع أهل الباطل.
حدثنا عيسى بن محمد قال: حدثنا روح، عن هشام قال: ذكروا عند محمد بن سيرين الشطرنج فقال انما الشطرنج رفق.
حدثنا أبو معن قال: حدثنا وهب بن جرير قال: حدثنا أبي، عن أخيه يزيد بن حازم قال: رأيت محمد بن المنكدر يلعب بالشطرنج على باب: هـ.','2','933',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (756,'حدثنا أبو أمية قال: حدثنا أحمد بن سليمان قال: حدثنا محبوب بن الحسن، عن إسماعيل بن مسلم قال: رأيت الشعبي يلعب بالشطرنج.','2','934',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (757,'<span data-type="title" id=toc-216>باب: الأمر بالمعروف والنهي عن المنكر</span>
سمعت إسحاق يقول: الأمر بالمعروف والنهي عن المنكر واجب على كل مسلم إذا رآه قيل: فإن خشي؟ قال: هو واجب عليه حتى يخاف فإذا خشي على نفسه فلا يفعل.
حدثنا سعيد بن منصور، قال: ثنا أبو شهاب، عن الأعمش، عن عمرو بن مرة، عن أبي البحتري، عن أبي سعيد قال: لا يحقرن أحدكم نفسه إذا رأى منكرًا غيره، فإن الله عز وجل يقول يوم القيامة: ما منعك/١٥٧/يوم يكون كذا وكذا أن تقول بعلمك· فيقول: رب خفت الناس فيقول: إياي أحق إن تخاف.','2','935',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (758,'حدثنا سعيد، قال: ثنا معتمر بن سليمان، قال: سمعت الركين يحدث عن أبيه، قال: قال عبد الله بن مسعود: إن من بقى منكم سيرى منكرًا، وبحسب امرئ إذا رأى منكرًا لا يستطيع له غيرًا أن يعلم الله من قلبه أنه له كاره.
حدثنا سعيد، قال: حدثنا أبو معاوية، عن الأعمش، عن قيس بن راشد، عن أبي جحيفة قال: قال علي أول ما تغلبون عليه من أمر دينكم الجهاد بأيديكم، ثم الجهاد بألسنتكم، ثم الجهاد بقلوبكم، فأي قلب لم يعرف المعروف ولم ينكر المنكر نكس فجعل أعلاه أسفله.','2','936',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (759,'حدثنا شاذ بن فياض، قال: ثنا حماد بن سلمة، عن أبي غالب، عن أبي أمامة، قال: أتى رجل رسول الله صلى اله عليه وسلم فقال: يا رسول الله أي الجهاد أفضل؟ قال: ((أفضل الجهاد كلمة حق عند سلطان جائر)).
حدثنا أبو عمرة الحوضي، عن عبيدة بن الحدايطة قال: أخبرني معاوية بن إسحاق قال: جاء رجل إلى ابن عباس فقال: يا أبا عباس آمر أميري','2','937',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (760,'بتقوى الله؟ قال: إن خفت إن يقتلك فلا فإن كنت لابد فاعلًا فبينك وبينه.','2','938',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (761,'<span data-type="title" id=toc-217>باب: الرجل يدعى إلى طعام فيرى في البيت آنية الفضة</span>
سئلَ أحمد عن الرجل يدعى إلى الطعام فيرى في البيت آنية منصوبة من فضة؟ قال: لا يأكل ولا يجيب.
قيل: وهكذا إذا رأى شيئًا من زي العجم؟ فكأنه قال: نعم.
قيل: حديث ابن مسعود أنه دعي إلى طعام فرأى صورة فخرج؟ قال: نعم، وحديث حذيفة ثم قال أحمد: حديث عبد الله.','2','939',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (762,'قيل: حديث محمد بن كعب؟ قال: نعم حديث حماد بن سلمة.
قيل والزهري، عن سالم، عن ابن عمر أنه دعا أبا أيوب. قيل لأحمد: رواه غير عبد الرحمن بن إسحاق، عن الزهري؟ قال: لا أعلمه.
حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا سعيد، عن عدي بن ثابت، عن خالد بن سعد قال: دعي أبو مسعود لطعام فقال: أفي البيت صورة؟ فقيل له: نعم. فلم يذهب حتى كسرت.','2','940',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (763,'حدثنا إسحاق قال: أخبرنا معتمر بن سليمان قال: أخبرنا ليث قال: دخلت على سالم بن عبد الله وهو متكئ على وسادة فيها تصاوير وحش أوسباع فقلت: أما تكره هذا قال: يكره للذي يصورها، ثم قال/١٥٨/سالم: سمعت أبي يحدث ان نبي الله - صلى الله عليه وسلم - قال: ((من صور شيئًا كلف أن يحييه يوم القيامة)). وسمعت النبي - صلى الله عليه وسلم - يقول: ((الفتنة ها هنا)) وأشار بيده نحو المشرق· قال: قلت له: بما جهزك أبوك؟ قال: بتابوتين من خشب، وحجلة من','2','941',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (764,'صوف، ونبد وسائد، وزربية، وسرير عليه حشية قال: قلت إلى من أرسل؟ قال: أرسل إلى أبي أيوب صاحب رسول الله - صلى الله عليه وسلم - ليدعوا لنا بالبركة، فلما رأى ما في البيت أبا أن يدخل وقال: ما كنت أرى أن تدعوني إلى مثل هذا. أو كما قال.','2','942',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (765,'<span data-type="title" id=toc-218>باب: أكل الرمان بشحمه</span>
سمعت أحمد بن حنبل، قال: حدثنا سعيد بن خثيم، قال: حدثتني جدتي أم أبي ربيعة بنت عياض الكلابية قالت: كان علي بن أبي طالب - كرم الله وجهه -يقول: يا أيها الناس إذا أكلتم هذا الرمان فكلوه بشحمه فأنه دباغ المعدة. حدثنا الربيع بن يحيى قال: حدثنا مالك بن مغول قال: حدثتني مرجانة قالت: رأيت علي بن أبي طالب يأكل رمانًا ويلتقط الحب إذا وقع منه في الأرض فيأخذه فيأكله.','2','943',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (766,'<span data-type="title" id=toc-219>باب: تعبير الرؤيا</span>
قلت لأحمد: يا أبا عبد الله الرجل يعبر الرؤيا، قال: وما بأس بذلك. فرخص فيه، وقال: إنه ينزع من القرآن، وحسنه وذكر أن أبا بكر، وابن المسيب، وابن سيرين كانوا يفعلون ذلك.
قال أحمد: وقد كان عندكم بكرمان رجل عالم بهذا. قلت: نعم، وفسرت له حاله، فجعل يعجب من علمه وقال: لا بأس بالعبارة.
وسألتُ إسحاق عن الرجل ينظر في عبارة الرؤيا فرخص فيه، ثم قال: أخبرنا المرجى بن وداعة قال: حدثنا غالب القطان قال: قلت لمحمد بن سيرين: إنك تحسن من العبارة على ما يجبن عنه فقهاؤنا، وتجبن من الفتيا ما يجسر عليه فقهاؤنا. قال: يا ابن أخ ما أنفس عليك أن تعلم مثل ما أعلم إنما هو شيء فآخذه من القرآن، وليس كلما نقول كما نقول','2','944',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (767,'إذا رأيت الماء فهو فتنة، وإذا رأيت السفينة فهي نجاة، وإذا رأيت اللؤلؤ فهو القرآن وإذا رأيت النار فهي ثائرة، وإذا رأيت الخشب فهو نفاق وإذا رأيت العقد فهو حكمة، وإذا رأيت التاج فهو ملك، وإذا رأيت /١٥٩/ الحرب فهو الطاعون، والكسوة كلها تعجبنا وأحبها الينا البياض، وإذا رأيت الصعود فهو هم.
حدثنا أحمد بن بشر قال: حدثنا أبو قتيبة، عن مهدي بن ميمون قال: سمعت محمد بن سيرين: إنما الرؤيا ظن أظنه ليس بحلال ولا حرام.','2','945',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (768,'(مسائل حرب ٣) باب: العلم والحاجة إليه
سمعت أحمد بن حنبل يقول: الناس يحتاجون إلى العلم قبل الخبز والماء لأن العلم يحتاج إليه الانسان في كل ساعة، والخبز والماء في اليوم مرة أو مرتين.
حدثنا عبد الرحمن بن بحرقال: حدثنا رشد بن سعد قال: حدثنا معاوية بن صالح، عن إسحاق بن عبد الله بن أبي طلحة، عن أنس بن مالك قال: قال رسول الله - صلى الله عليه وسلم -: ((العلم واجب على كل مسلم)).','2','946',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (769,'حدثنا عمرو بن عثمان، قال: حدثنا الوليد بن مسلم، عن ابن جابر قال: سمعت بشر بن عبيد الله الحضرمي قال: إن كنت لأركب إلى المصر من الأمصار في الحديث الواحد لأسمعه.
حدثنا المسيب بن واضح قال: ثنا ابن مبارك، عن زائدة، عن هشام، عن الحسن قال: كان الرجل إذا طلب العلم لم يلبث أن يرى ذلك في تخشعه وبصره ولسانه ويده وصلاته وزهده،','2','947',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (770,'وإن كان الرجل ليطلب الباب من العلم فيعمل به فيكون خيرًا له من الدنيا وما فيها لو كانت له فجعلها في الآخرة. حدثنا محمد بن الوزير قال: حدثنا صبح بن مزيع الخراساني قال: قال: ثنا إسحاق بن نجيح، عن غالب بن عبيد الله، عن الحسن قال: قال رسول الله - صلى الله عليه وسلم -: ((إن من العلم مايكون كهيئة الشيء المدفون لا يعلمه إلا العلماء بالله، فإذا نطقوا به لم يجهله إلا أهل العزة بالله، هل تدرون ما قال لي جبريل؟)) قالوا: الله ورسوله أعلم. قال: ((لا تحقرن عبد اأتاه الله علمًا فإن الله لم يحقره حين أتاه إياه فإذا كان يوم القيامة جمع الله العلماء في صعيد واحد قال: إني لم أجمعكم إلا لخير أريده بكم اذهبوا به فقد غفرت لكم على ما كان منكم)).','2','948',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (771,'<span data-type="title" id=toc-220>باب: التدليس في الحديث</span>
سألتُ أحمد عن التدليس في الحديث فكرهه وقال: أقل شيء أنه يتزيد أو يتزين. قال حرب: أنا أشك. قال أبو محمد: قد فعله الناس.
حدثنا أبو جعفر الدارمي قال: حدثنا أبو داود قال: قال/١٦٠/شعبة لأن أخرَّ من السماء أحب إليّ من أن أفعله - يعني التدليس - ولقد كان يفعله يونس بن عبيد كنت أوقفه فأقول: سمعتَ من الحسن؟ فيقول إن لم أكن سمعته منه فقد حدثني من أثق به.','2','949',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (772,'<span data-type="title" id=toc-221>باب: لم سمي يوم القيامة الساعة</span>؟
قلت لإسحاق: لم سمي يوم القيامة الساعة؟ قال: لأنها تأتي على نفس كل إنسان. حدثنا سعيد بن منصور قال: أخبرنا أبو معشر، عن محمد بن كعب في قوله: {يَوْمُ التَّغَابُنِ} [التغابن: ٩]. قال: يوم القيامة تغبن أهل الجنة أهل النار.
حدثنا عمرو بن عثمان، قال: أخبرنا الوليد، عن الأوزاعي، عن بدول بن سعد في قوله: {لِيُنذِرَ يَوْمَ التَّلَاقِ} [غافر: ١٥] قال: يلتقي أهل السماء وأهل الأرض.','2','950',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (773,'سمعت إسحاق يقول في حديث النبي عليه السلام: ((من تقرب إلى الله شبرًا تقرب الله إليه باعًا)) قال: يعني من تقرب إلى الله شبرًا بالعمل تقرب الله إليه بالثواب باعًا.
حدثنا علي بن عثمان قال: حدثنا عبد الواحد بن زياد قال: حدثنا أبو سليمان قال: حدثنا أبو صالح قال: سمعت أبا هريرة يقول: قال النبي - صلى الله عليه وسلم - ((قال الله: أنا عند ظن عبدي بي، وأنا معه حيث يذكرني إن ذكرني في نفسه ذكرته في نفسي وإن ذكرني في ملأٍ ذكرته في ملأٍ خير منهم، ومن تقرب إليَّ شبرًا تقربت إليه ذراعًا ومن تقرب إليَّ ذراعًا تقربت إليه باعًا، ومن جاءني يمشي جئته هرولة)).','2','951',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (774,'<span data-type="title" id=toc-222>باب: في قول الله عز وجل {لَّا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ}</span>
سمعت إسحاق في قول الله: {لَّا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ} قال: النسخة التي في السماء {لَّا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ} قال: الملائكة.
حدثنا سعيد بن منصور قال حدثنا أبو الأحوص قال: حدثنا عاصم الأحول، عن أنس بن مالك في قوله: {لَّا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ} قال: المطهرون الملائكة.','2','952',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (775,'<span data-type="title" id=toc-223>باب معارضة الكتاب</span>
سمعت إسحاق يقول: سمعت من ابن المبارك وأنا ابن خمس عشرة فكنت أغتابه إذ ذاك قال: فكنا إذا قمنا من المجلس عارضنا فتركت السماع لحال المعارضة.
قال حرب: قد سمع أصحاب رسول الله - صلى الله عليه وسلم - من رسول الله - صلى الله عليه وسلم - وهم اثنا عشر وأقل وأكثر وقد جاز ذلك، وعمل به المسلمون في أمر دينهم مثل أنس بن مالك، وابن عباس، وابن الزبير، والحسن بن علي، وعبد الله بن جعفر، وغيرهم. وقرأ على إسحاق بقية بن الوليد، عن الأوزاعي، عن سليمان بن موسى أنه قال لرجل: كتبت؟ قال: نعم. قال: أعارضت؟ قال: لا. قال: فلم تكتب.','2','953',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (776,'قال الأوزاعي: والكتاب إذا لم يُعارض كان بمنزلة من يدخل الخلاء ولا يستنجي. قال: والعجم نور الكتاب.','2','954',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (777,'<span data-type="title" id=toc-224>باب الرجل يأمر أباه بالمعروف وينهاه عن المنكر</span>
قلت لإسحاق: الرجل يأمر أباه بالمعروف وينهاه عن المنكر؟ قال: في رفق عظه ولا تفعله على رءوس الناس.
حدثنا أبو معن قال: ثنا حجاج قال: ثنا سلام بن مسكين قال: قلت للحسن: آمر والديَّ بالمعروف وأنهاهما عن المنكر؟ قال: مرهما إن قبلا، فإن سكتا فاسكت عنهما.','2','955',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (778,'<span data-type="title" id=toc-225>باب: معنى لا يكونن أحدكم امعة</span>
قلت لإسحاق: ما معنى لا يكونن أحدكم إمعة؟ قال: يقول. إن ضل الناس ضللت، فإن اهتدوا اهتديت.
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: حدثنا عبد الرحمن المسعودي، عن سلمة بن كهيل، عن عبد الرحمن بن يزيد قال: قال عبد الله: لا يكونن أحدكم إمعة يقول: [إن] اهتدى الناس اهتديت، وإن ضل الناس ضللت ليوطّن أحدكم نفسه إن كفر الناس أن لا يكفر.','2','956',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (779,'<span data-type="title" id=toc-226>باب: أطفال المشركين</span>
سألتُ إسحاق عن أطفال المشركين فقال: خل أمرهم إلى الله، الله أعلم بما كانوا عاملين. قال: وأطفال المسلمين هم في الجنة.
قال إسحاق: ولا يشهد أحدكم لصبي يموت، إني أشهد أن هذا في الجنة. قال: وسُئِلَ ابن عباس عن الولدان أفي الجنة هم؟ قال حسبك ما اختصم فيه موسى والخضر.
حدثنا إسحاق قال أخبرنا بقية بن الوليد قال: حدثني محمد بن زياد قال: حدثني عبد الله بن أبي قيس قال: حدثتني عايشة زوج النبي - صلى الله عليه وسلم - وسألتُها','2','957',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (780,'عن دراري المشركين والمؤمنين فقالت سألتُ رسول الله - صلى الله عليه وسلم - عنهم فقال: ((مع آبائهم)). قالت: فقلت: يارسول الله بلا عمل؟ قال: ((الله أعلم بما كانوا عاملين)).
حدثنا إسحاق قال: أخبرنا جرير/١٦٢/ قال: أخبرنا العلاء بن المسيب، عن الفضيل بن عمرو الفقيمي، عن عائشة بنت طلحة، عن عائشة أم المؤمنين قالت توفي صبي من الأنصار فقلت: طوي له عصفور من عصافير الجنة. فقال رسول الله - صلى الله عليه وسلم -: ((يا عائشة أولا تدرين أن الله خلق الجنة وخلق النار، فخلق للجنة أهلًا وخلق للنار أهلًا)).','2','958',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (781,'<span data-type="title" id=toc-227>باب: وسوسة القلوب</span>
سمعت إسحاق يقول في حديث النبي - صلى الله عليه وسلم - وأصحابه والتابعين في الوسوسة أنه محض الإيمان أو صريح الايمان. قال إسحاق: إذا أنفى الوسوسة عن نفسه فنفيه محض الإيمان وليس الوسوسة محض الإيمان، ولكن نفيه، وأما الوسوسة إذا وقع في القلب فلم ينفه فهو الهلاك. قال: وما روي عن أصحاب النبي - صلى الله عليه وسلم - أنهم كانوا إذا فقدوا الوسوسة عدوه نقصًا فليس أن يكونوا عدوا فقد الوسوسة نقصًا ولكن كانوا إذا أصابهم ذلك نفوها عن أنفسهم فإذا لم يصبهم ذلك عدوه نقصًا، لأن نفي ذلك عندهم فضيلة عندهم أو كما قال.
حدثنا أبو سهل بشر بن معاذ قال: حدثنا يوسف بن عطية قال: حدثنا ثابت البناني، عن أنس بن مالك أن بعض أصحاب النبي - صلى الله عليه وسلم - شكوا إليه ما يجدون من هذه الوساوس في صدورهم. قال: فقال النبي - صلى الله عليه وسلم -: ((كيف أنتم وربكم؟)) قالوا: لا شك في ربنا وليقع أحدنا من السماء فيتقطع أحب إليه من أن يتكلم بما في صدره، فقال النبي - صلى الله عليه وسلم -: ((الله أكبر الله أكبر ذاك محض الإيمان)).','2','959',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (782,'قال أبو سهل: قلت للعتبي: إن عبد الرحمن وأثنا عليه خيرًا ما عنى بقوله ذلك محض الإيمان؟ فقال: عنى به الخوف الذي شكوه إلى النبي - صلى الله عليه وسلم - من الذي وجدوه في صدورهم ذلك محض الإيمان.','2','960',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (783,'<span data-type="title" id=toc-228>باب: الذي يستمد من محبره الرجل</span>
سُئِلَ إسحاق عن الرجل يستمد من محبرة الرجل؟ قال: لا يستمد إلا بإذنه.
حدثنا بشار بن موسى قال: حدثنا أبو عوانة، عن خالد بن أبي الصلت قال: أتى عمر بن عبد العزيز بماء قد سخن بفحم من فحم الإمارة فأبى أن يتوضأ به.','2','961',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (784,'<span data-type="title" id=toc-229>باب: الزنادقة الذين يضعون الأحاديث</span>
سمعت إسحاق يقول: قال ابن المبارك: إن رجلًا من الزنادقة تاب فقال: وضعنا أربعة ألف حديث في أيدي الناس الصالحون منكم يروونه.
حدثنا أبو جعفر أحمد بن سعيد قال: حدثنا أحمد بن سليمان قال: حدثنا حجاج بن محمد قال: أخبرني ابن لهيعة عن السكن بن أبي كريمة، عن ليث، عن مجاهد، عن ابن عباس قال: إذا كان خمس وثلاثون ومائة سنة خرج شياطين من البحر كان سليمان بن داود حبسهم في أشعار الناس وأبشارهم يحدثون الناس ليفتنونهم فاحذروهم.','2','962',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (785,'حدثنا أبو معن الرقاشي قال: حدثنا عمربن يونس قال: حدثنا سعيد الحمصي، عن هارون بن هارون، عن مجاهد، عن ابن عباس قال: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((هلاك أمتي في العصبية والقدرية والرواية من غير ثبت)).
حدثنا أحمد بن سعيد قال: ثنا علي بن الحسن بن شقيق قال: قال أبو','2','963',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (786,'حمزة: تدرون ما الأثر؟ قال: أن أفتي فيقال يوم القيامة: عمن أفتيت؟ فأقول: عن الاعمش، فيجاء بالأعمش وأترك أنا، فيقال له: عمن أفتيت فيقول: عن إبراهيم حتى ينتهي إلى مننتهاه.
حدثنا عبد الله بن معاذ، عن بشر بن مفضل قال: سألتُ فقهاء أهل المدينة عن إبراهيم بن أبي يحيى فكلهم يقول: كذاب.
حدثنا أبو معن قال: سمعت أبا عامر يقول: ما أعلم أني رأيت أحدًا يكذب في الحديث إلا إبراهيم بن أبي يحيى وصاحب هذا المسجد لجار له يقال له أبو زيد.','2','964',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (787,'<span data-type="title" id=toc-230>باب: تفسير حديث النبي - صلى الله عليه وسلم - ((من غشنا فليس منا))</span>
قيل لأحمد مامعنى حديث النبي - صلى الله عليه وسلم -: ((من غشنا فليس منا))؟ فلم يجب فيه. قيل: فإن قومًا قالوا تفسير من غشنا فليس مثلنا فأنكره، وقال: هذا تفسير مسعر وعبد الكريم أبي أمية كلام المرجئة. قال أحمد: وبلغ عبد الرحمن بن مهدي فأنكره وقال: ولو أن رجلًا عمل بكل حسنة أكان يكون مثل النبي - صلى الله عليه وسلم -.
حدثنا محمد بن جامع قال: حدثنا عبد العزيز بن محمد قال: حدثنا ثور بن يزيد، عن عكرمة، عن ابن عباس قال رسول اللهب - صلى الله عليه وسلم -: ((من','2','965',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (788,'رمانا بالنبل فليس منا، ومن غشنا فليس منا)).
حدثنا قيس بن جعفر قال: /٦٤/ حدثنا خالد بن الحارث قال: حدثنا عبد الرحمن بن حرملة، عن سعيد بن المسيب قال: إنما العين نطفة فإن دلكتها رنقت، وإن تركتها صفت. حدثنا قيس بن حفص قال: ثنا الضحاك بن مخلد قال: بلغني عن عطاء بن أبي رباح أنه قال: لا تكثر مسح عينيك فإنما هي ماء.','2','966',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (789,'<span data-type="title" id=toc-231>باب: القول بالمذهب</span>
قال أبو القاسم: حدثنا أبو محمد حرب بن إسماعيل قال: هذا مذهب أئمة العلم وأصحاب الأثر وأهل السنة المعروفين بها المقتدى بهم فيها، وأدركت من أدركت من علماء أهل العراق والحجاز والشام وغيرهم عليها فمن خالف شيئًا من هذه المذاهب، أو طعن فيها، أوعاب قائلها فهو مبتدع خارج من الجماعة زائل عن منهج السنة وسبيل الحق، وهو مذهب أحمد وإسحاق بن إبراهيم بن مخلد، وعبد الله بن الزبير الحميدي وسعيد بن منصور، وغيرهم ممن جالسنا وأخذنا عنهم العلم فكان من قولهم: الإيمان قول وعمل ونية وتمسك بالسنة، والإيمان يزيد وينقص وسنتنا في الايمان سنة ماضية عن العلماء، وإذا سُئِلَ الرجل أمؤمن أنت؟ فإنه يقول أنا مؤمن إن شاء الله، أو مؤمن أرجو، أو يقول: آمنت بالله وملائكته وكتبه ورسله،','3','967',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (790,'ومن زعم إن الإيمان قول بلا عمل فهو مرجي ومن زعم أن الإيمان هو القول والأعمال شرائع فهو مرجئ. وإن زعم أن الإيمان لا يزيد ولا ينقص فهو مرجئ وإن قال: إن الإيمان يزيد ولا ينقص فقد قال بقول المرجئة، ومن لم الإستثناء في الإيمان فهو مرجئ، ومن زعم أن إيمانه كإيمان جبريل أو الملائكة فهو مرجئ وأخبث من المرجئ فهو كاذب، ومن زعم أن الناس لا يتفاضلون في الإيمان فقد كذب ومن زعم إن المعرفة تنفع في القلب وإن لم يتكلم بها فهو جهمي، ومن زعم أنه مؤمن عند الله مستكمل الإيمان فهذا من أشنع قول المرجئة وأقبحه، والقدر خيره وشره، وقليله، وكثيره، وظاهره وباطنه، وحلوه ومره، ومحبوبه ومكروهه، وحسّنه وسيئه، وأوله وآخره من الله تبارك وتعالى قضاء قضاه على عباده، وقدر قدّره عليهم لا يعدو أحد منهم مشيئة الله، لا يجاوز قضاءه، بل هم كلهم صائرون إلى ما خلقهم له وواقعون في ما/١٦٥/ قدّر عليهم لا محالة، وهو عدل منه عز ربنا وجل.
والزنا، والسرقة، وشرب الخمر، وقتل النفس، وأكل مال الحرام، والشرك بالله، والذنوب والمعاصي كلها بقضاء وقدر من الله من غير إن يكون لأحد من الخلق على الله حجةٌ بل لله الحجة البالغة على خلقه ولا يُسأََل عما يفعل وهم يُسأَلون.','3','968',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (791,'وعلم الله ماض في خلقه بمشيئة منه، قد علم من إبليس ومن غيره ممن عصاه من لدن أن عصى ربنا تبارك وتعالى إلى أن تقوم الساعة المعصية وخلقهم لها. وعلم الطاعة من أهل طاعته وخلقهم لها، فكل يعمل بما يخلق له وصائر إلى ما قضى عليه وعلم منه ولا يعدو أحد منهم قدر الله ومشيئته والله الفعال لما يريد.
فمن زعم أن الله تبارك وتعالى شاء لعباده الذين عصوه الخير والطاعة، وأن العباد شاءوا لأنفسهم الشر والمعصية فعملوا على مشيئتهم، فقد زعم أن مشيئة العباد أغلب من مشيئة الله تبارك وتعالى ذكره فأي افتراء على الله أكثر من هذا؟!
ومن زعم أن أحدًا من الخلق صائر إلى غير ما خلق له فقد نفى قدرة الله عن خلقه، وهذا إفك على الله وكذب عليه.
ومن زعم أن الزنا ليس بقدر قيل له: أرأيت هذه المرأة التي حملت من الزنا وجاءت بولد، هل شاء الله أن يخلق هذا الولد، وهل مضى هذا في سابق علمه؟ فإن قال: لا. فقد زعم أن مع الله خالقًا، وهذا قول يضارع الشرك بل هو الشرك، ومن زعم أن السرقة، وشرب الخمر، وأكل المال الحرام ليس بقضاء وقدر من الله فقد زعم أن هذا الإنسان قادر على إن يأكل برزق غيره، وهذا القول يضارع قول المجوسية والنصرانية، بل أكل رزقه وقضى الله له أن يأكله من الوجه الذي أكله.
ومن زعم أن قتل النفس ليس بقدر من الله فقد زعم أن المقتول مات بغير أجله فأي كفر بالله أوضح من هذا، بل ذلك كله بقضاء من الله وقدر، وكل ذلك بمشيئته في خلقه وتدبيره فيه، وما جرى في سابق علمه لهم','3','969',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (792,'وهو الحق والعدل الحق بفعل ما يريد ومن أقر بالعلم لزمه الإقرار بالقدر والمشيئة على الصغر والعماة، والله الضار النافع المضل الهادي فتبارك الله أحسن الخالقين.
ولا نشهد على أحد/١٦٦/من أهل القبلة أنه في النار لذنب عمله ولكبيرة أتى بها، إلا أن يكون في ذلك حديث فيروي الحديث كما جاء على ماروى ويصدق به ويقبل ويعلم أنه كما جاء، ولا ينصب الشهادة ولا يشهد على أحد أنه في الجنة لصلاح عمله أو لخير أتى به إلا إن يكون في ذلك حديث فيروى الحديث كما جاء على ما روي، يصدق به، ويقبل يعلم أنه كما جاء ولا تنصب الشهادة.
والخلافة في قريش ما بقي من الناس اثنان، ليس لأحد من الناس إن ينازعهم فيها، ولا يخرج عليهم، ولا يقر لغيرهم بها إلى قيام الساعة.
والجهاد ماض قائم مع الأئمة بروا أو فجروا، ولا يبطله جور جائر، ولا عدل عادل. والجمعة والعيدين والحج مع السلطان وإن لم يكونوا بررة عدولًا، ولا أتقياء.
ودفع الخراج والصدقات والأعشار والفيء والغنيمة إلى الأمراء عدلوا فيها أم جاروا. والانقياد لمن ولاه الله أمرك لا تنزع يدك من طاعة، ولا تخرج عليه بسيفك حتى يجعل الله لك فرجًا ومخرجًا وأن لا تخرج على السلطان وتسمع وتطيع لا تنكث بيعه فمن فعل ذلك فهو مبتدع مخارق مفارق للجماعة.
وإن أمرك السلطان بأمر هو لله معصية فليس لك إن تطيعه البتة، وليس لك أن تخرج عليه ولا تمنعه حقه.','3','970',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (793,'والإمساك في الفتنة سنة ماضية واجب لزومها، فإن ابتليت فقدم نفسك ومالك دون دينك، ولا تعين على الفتنة بيد ولا لسان ولكن اكفف يدك ولسانك وهواك والله المفتن.
والكف عن أهل القبلة لا تكفر أحدًا منهم بذنب، ولا تخرجه من الإسلام بعمل، إلا إن يكون في ذلك حديث فيروى الحديث كما جاء وكما روي ويصدق به ويقبله ونعلم أنه كما روي نحو ترك الصلاة، وشرب الخمر، وما أشبه ذلك ولا بتبدع بدعة ينسب صاحبها إلى الكفر والخروج من الإسلام، واتبع الأثر في ذلك ولا تجاوزه.
ولا أحب الصلاة خلف أهل البدع، ولا الصلاة على من مات منهم. والأعور خارج لا شك في ذلك ولا ارتياب وهو أكذب الكاذبين.
وعذاب القبر حق، يسأل العبد عن ربه، وعن نبيه، وعن دينه، ويرى مقعده من الجنة أو النار.
ومنكر ونكير حق، وهما فتانا القبور نسأل الله الثبات.
وحوض محمد - صلى الله عليه وسلم - /١٦٧/حق، ترد عليه أمته وله آنية يشربون بها منه.
والصراط حق يوضع في سواء جهنم فيمر الناس عليه، والجنة من وراء ذلك نسأل الله السلامة والجواز.
والميزان حق يوزن به الحسنات والسيئات كما شاء الله أن يوزن به.
والصور حق ينفخ فيه إسرافيل فيموت الخلق، ثم ينفخ فيه فيقومون لرب العالمين للحساب والقضاء، والثواب والعقاب والجنة والنار.','3','971',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (794,'واللوح المحفوظ حق يستنسخ منه أعمال العباد لما سبقت فيه من المقادير والقضاء.
والقلم حق كتب الله به مقادير كل شيء وأحصاه في الذكر فتبارك ربنا وتعالى. والشفاعة يوم القيامة حق يُشَّفعُ قوم في قوم فلا يصيرون إلى النارويخرج قوم من النار، بعدما دخلوها بشفاعة الشافعين، ويخرج قوم من النار برحمة الله بعد ما يلبثهم فيها ما شاء الله، وقوم يخلدون في النار أبدًا وهم أهل الشرك والتكذيب والجحود والكفر بالله. ويذبح الموت يوم القيامة بين الجنة والنار، وقد خُلِقَت الجنة وما فيها، وخُلِقَت والنار وما فيها خلقهما الله ثم خلق الخلق لهما لا يفنيان ولا يفنى من فيهما أبدًا، فإن احتج مبتدع زنديق بقول الله تبارك وتعالى: {كُلُّ شَيْءٍ هَالِكٌ إِلَّا وَجْهَهُ} [القصص: ٨٨] وبنحو هذا فقل له: كل شيء ما كتب الله عليه الفناء والهلاك هالك، والجنة والنار خُلقتا للبقاء لا للفناء ولا للهلاك وهما من الآخرة لا من الدنيا، والحور العين لا يمتن عند قيام الساعة، ولا عند النفخة، ولا أبدًا لأن الله تبارك وتعالى خلقهن للبقاء لا للفناء ولم يكتب عليهن الموت، فمن قال بخلاف ذلك فهو مبتدع مخالف وقد ضل عن سواء السبيل.
وخلق الله سبع سماوات بعضها فوق بعض، وسبع أرضين بعضها أسفل من بعض وبين الأرض العليا والسماء الدنيا مسيرة خمس مائة عام، وبين كل سماء بين مسيرة خمس مائة عام،','3','972',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (795,'والماء فوق السماء السابعة، وعرش الرحمن فوق الماء، والله تبارك وتعالى على العرش، والكرسي موضع قدميه، وهو يعلم ما في السماوات السبع، وما في الأرضين السبع، وما بينهن، وما تحتهن، وما تحت الثرى، وما في قعر البحار، ومنبت كل شعرة، وكل شجرة، وكل زرع، وكل نبت، ومسقط/١٦٨/كل ورقة، وعدد ذلك كله، وعدد الحصا، والرمل والتراب، ومثاقيل الجبال، وقطر الأمطار، وأعمال العباد، وأثارهم، وكلامهم وأنفاسهم، وتمتمتهم، وما توسوس به صدورهم يعلم كل شيء لا يخفى عليه شيء من ذلك، وهو على العرش فوق السماء السابعة، ودونه حجب من نار ونور وظلمة وما هو أعلم بها، فإن احتج مبتدع أو مخالف أو زنديق بقول الله تبارك وتعالى اسمه: {وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ الْوَرِيدِ} [ق: ١٦] ونحن أقرب إليه من حبل الوريد وبقوله: {وَهُوَ مَعَكُمْ أَيْنَ مَا كُنتُمْ} [الحديد: ٤] وبقوله: {مَا يَكُونُ مِن نَّجْوَى ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ} [المجادلة: ٧] ونحو ذلك من متشابه القرآن فقل: إنما يعني بذلك العلم؛ لأن الله تبارك وتعالى على العرش فوق السماء السابعة العليا يعلم ذلك كله وهو بائن من خلقه لا يخلو من علمه مكان، ولله عرش، وللعرش حملة يحملونه، وله حدٌّ الله أعلم بحده، والله','3','973',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (796,'على عرشه عز ذكره، وتعالى جده، ولا إله غيره.
والله تبارك وتعالى سميع لا يشك، بصير لا يرتاب، عليم لا يجهل، جواد لا يبخل، حليم لا يعجل، حفيظ لا ينسى، يقظان لا يسهو، رقيب لا يغفل، يتكلم ويتحرك، ويسمع ويبصر وينظر، ويقبض ويبسط، ويفرح، ويحب ويكره ويبغض ويرضى، ويسخط ويغضب، ويرحم ويعفو ويغفر، ويعطي ويمنع، وينزل كل ليلة إلى السماء الدنيا كيف شاء وكما شاء ليس كمثله شيء وهو السميع البصير، وقلوب العباد بين أصابع الرحمن يقلبها كيف يشاء، ويرعيها ما أراد. وخلق آدم بيده على صورته، والسماوات والأرضون يوم القيامة في كفه وقبضته، ويضع قدمه في جهنم فتزوى، ويخرج قوم من النار بيده، وينظر أهل الجنة إلى وجهه يزورونه فيكرمهم، ويتجلى لهم فيعطيهم، ويُعرض عليه العباد يوم الفصل والدين فيتولى حسابهم بنفسه لا يولي','3','974',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (797,'ذلك غيره - عز ربنا وجل وهو على ما يشاء قدير -.
والقرآن كلام الله تكلم به ليس بمخلوق، فمن زعم أن القرآن مخلوق فهو جهمي كافر، ومن زعم أن القرآن كلام الله ووقف ولم يقل ليس بمخلوق فهو أكفر من الأول وأخبث قولًا، ومن زعم إن ألفاظنا بالقرآن وتلاوتنا له مخلوقة والقرآن كلام الله فهو جهمي خبيث مبتدع. /١٦٩/
ومن لم يكفرها ولا القوم ولا الجهمية كلهم فهو مثلهم، وكلم الله موسى وناوله التوراة من يده إلى يده، ولم يزل الله متكلمًا عالمًا فتبارك الله أحسن الخالقين.
والرؤيا من الله وهي حق إذا رأى صاحبها شيئًا في منامه مما ليس هو ضغث فقصها على عالم وصدق فيها وأولها العالم على أصل تأويلها الصحيح ولم يحرف فالرؤيا وتأويلها حينئذٍ حق، وقد كانت الرؤيا من النبيين وحيًا فأي جاهل بأجهل ممن يطعن في الرؤيا، ويزعم أنها ليست بشيء، وقد روي عن النبي - صلى الله عليه وسلم - أن رؤيا المؤمن كلام يكلم الرب عبد هـ وقال ((الرؤيا من الله)) وبالله التوفيق.','3','975',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (798,'ومن السنة الواضحة البينة الثابتة المعروفة ذكر محاسن أصحاب رسول الله - صلى الله عليه وسلم - كلهم أجمعين، والكف عن ذكر مساوئهم والذي شجر بينهم، فمن سب أصحاب رسول الله - صلى الله عليه وسلم -، أو أحدًا منهم، أو طعن عليهم، أو عرض بعيبهم أو عاب أحدًا منهم بقليل أوكثير، أو دق أو جل مما يتطرق إلى الوقيعة في أحد منهم فهو مبتدع رافضي خبيث مخالف لا قبل الله صرفه ولا عدله بل حبهم سنة، والدعاء لهم قربه، والاقتداء بهم وسيلة، والأخذ بآثارهم فضيلة.
وخير الأمة بعد النبي - صلى الله عليه وسلم - أبو بكر، وخيرهم بعد أبي بكر عمر، وخيرهم بعد عمر عثمان. وقال قوم من أهل العلم وأهل السنة: وخيرهم بعد عثمان علي ووقف قوم على عثمان وهم خلفاء راشدون مهديون، ثم أصحاب محمد - صلى الله عليه وسلم - بعد هؤلاء الأربعة خير الناس. لا يجوز لأحد إن يذكر شيئًا من مساوئهم، ولا يطعن على أحد منهم بعيب، ولا بنقص ولا وقيعة، فمن فعل ذلك فالواجب على السلطان تأديبه وعقوبته ليس له أن يعفو بل يعاقبه ثم يستتيبه، فإن تاب قبل منه، وإن لم يتب أعاد عليه العقوبة ثم خلده الحبس حتى يتوب ويراجع فهذا السنة في أصحاب محمد - صلى الله عليه وسلم -.
ويعرف للعرب حقها وفضلها وسابقتها ويحبهم لحديث رسول الله - صلى الله عليه وسلم -: ((حب العرب إيمان وبغضهم نفاق)) ولا يقول بقول/١٧٠/الشعوبية وأراذل','3','976',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (799,'السؤال الذين لا يحبون العرب ولا يقرون لها بالفضل، فإن قولهم بدعة وخلاف.
ومن حرم المكاسب والتجارات وطلب المال من وجوهها فقد جهل وأخطأ وخالف، بل المكاسب من وجوهها حلال قد أحله الله ورسوله والعلماء من الأمة، فالرجل ينبغي له أن يسعى على نفسه وعياله، ويبتغي من فضل ربه، فإن ترك ذلك على أنه لا يرى الكسب فهو مخالف، وكل أحد أحق بماله الذي ورثه أو استفادة، أو أصابه أو اكتسبه لا كما يقول المتكلمون المخالفون.
والدين إنما هو كتاب الله وآثار وسنن وروايات صحاح عن الثقات بالأخبار الصحيحة القوية المعروفة المشهورة، يرويها الثقة الأول المعروف عن الثاني الثقة المعروف، يصدق بعضهم بعضًا حتى ينتهي ذلك إلى النبي - صلى الله عليه وسلم -، أو أصحاب النبي، أو التابعين، أو تابع التابعين، أو من بعدهم من الأئمة المعروفين المقتدى بهم، المتمسكين بالسنة، والمتعلقين بالأثر، الذين لا يُعرفون ببدعة، ولا يُطعَن عليهم بكذب، ولا يُرمَون بخلاف، وليسوا أصحاب قياس ولا رأي؛ لأن القياس في الدين باطل، والرأي كذلك وأبطل منه، وأصحاب الرأي والقياس في الدين مبتدعة جهلة ضُّلّال، إلا أن يكون في ذلك أثر عمن سلف من الأئمة الثقات، فالأخذ بالأثر أولى.','3','977',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (800,'ومن زعم أنه لا يرى التقليد، ولا يقلد دينه أحدًا فهذا قول فاسق مبتدع عدوا لله ولرسوله - صلى الله عليه وسلم -، ولدينه، ولكتابه، ولسنة نبيه عليه السلام، إنما يريد بذلك إبطال الأثر، وتعطيل العلم، وإطفاء السنة، والتفرد بالرأي، والكلام، والبدعة والخلاف. فعلى قائل هذا القول لعنة الله والملائكة والناس أجمعين. فهذا من أخبث قول المبتدعة، وأقربها إلى الضلالة والردى، بل هو ضلالة زعم أنه لا يرى التقليد وقد قلد دينه أبا حنيفة وبشر المريسي، وأصحابه، فأي عدو لدين الله أعدى ممن يريد أن يطفئ السنن، ويبطل الآثار والروايات، ويزعم أنه لا يرى التقليد وقد قلد دينه من قد سميت لك، وهم أئمة الضلال، ورءوس البدع، وقادة المخالفين فعلى قائل هذا القول غضب الله/١٧١/فهذه الأقاويل التي وصفت مذاهب أهل السنة والجماعة، والأثر، والجماعة، وأصحاب الروايات، وحملة العلم الذين أدركناهم وأخذنا عنهم الحديث وتعلمنا منهم السنن، وكانوا أئمة معروفين ثقات أهل صدق وأمانة يقتدى بهم ويؤخذ عنهم، ولم يكونوا أصحاب بدع ولا خلاف، ولا تخليط وهو قول أئمتهم وعلمائهم الذين كانوا قبلهم، فتمسكوا بذلك رحمكم الله، وتعلموه وعلموه وبالله التوفيق.','3','978',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (801,'ولأصحاب البدع نبز وألقاب وأسماء لا تشبه أسماء الصالحين ولا الأئمة ولا العلماء من أمة محمد - صلى الله عليه وسلم -، فمن أسمائهم المرجئة: وهم الذين يزعمون أن الإيمان قول بلا عمل، وأن الإيمان هو القول، والأعمال شرائع، وأن الإيمان مجرد، وأن الناس لا يتفاضلون في الإيمان، وأن إيمانهم وإيمان الملائكة والأنبياء واحد، وأن الإيمان لا يزيد ولا ينقص، وأن الإيمان ليس فيه استثناء، وأن من آمن بلسانه ولما يعمل فهو مؤمن حقًا، وأنهم مؤمنون عند الله بلا استثناء. هذا كله قول المرجئة وهو أخبث الأقاويل وأضله وأبعده من الهدى. والقدرية هم الذين يزعمون أن إليهم الاستطاعة والمشيئة والقدرة، وأنهم يملكون لأنفسهم الخير والشر والضر والنفع والطاعة والمعصية، والهدى والضلالة، وأن العباد يعملون بدئًا من أنفسهم من غير أن يكون سبق لهم ذلك في علم الله، وقولهم يضارع قول المجوسية والنصرانية وهو أصل الزندقة.
والمعتزلة: وهم يقولون قول القدرية ويدينون بدينهم، ويكذبون بعذاب القبر، والشفاعة، والحوض، ولا يرون الصلاة خلف أحد من أهل القبلة، ولا الجمعة إلا من كان على مثل رأيهم وهوائهم ويزعمون أن أعمال العباد ليست في اللوح المحفوظ.','3','979',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (802,'والبكرية: وهم قدرية، وهم أصحاب الحبة والقيراط، والدانق يزعمون أن من أخذ حبة، أو قيراطًا، أو دانقًا حرامًا فهو كافر، وقولهم يضاهي قول الخوارج.
والجهمية أعداء الله: وهم الذين يزعمون أن القرآن مخلوق وأن الله لم يكلم موسى، وأن الله لا يتكلم، ولا يرى، ولا يعرف لله مكان، وليس لله عرش، ولا كرسي وكلام كثير أكره حكايته، وهم كفار زنادقة أعداء الله فاحذروهم. والواقفة: وهم الذين يزعمون أنا نقول إن القرآن كلام الله ولا نقول غير مخلوق وهم شر الأصناف وأخبثها.
واللفظية: وهم الذين يزعمون أنا نقول: إن القرآن كلام الله ولكن ألفاظنا بالقرآن وتلاوتنا وقراءتنا له مخلوقة، وهم جهمية فساق.
والرافضة: الذين يتبرأون من أصحاب النبي - صلى الله عليه وسلم - ويسبونهم وينقصونهم، ويكّفرون الأمة إلا نفرًا يسيرًا، وليست الرافضة من الإسلام في شيء.','3','980',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (803,'والمنصورية: وهم رافضة أخبث الروافض، وهم الذين يقولون من قتل أربعين رجلًا ممن خالفهم هواهم دخل الجنة، وهم الذين يخنقون الناس ويستحلون أموالهم، وهم الذين يقولون أخطأ جبريل الرسالة، وهذا الكفر الواضح الذي لا يشوبه إيمان فنعوذ بالله ونعوذ بالله.
والسبائية وهم رافضة كذابين، وهم قريب ممن ذكرت مخالفون للأئمة.
والرافضة أسوأ أثرًا في الإسلام من أهل الكفر من أهل الحرب، وصنف من الرافضة يقولون عليٌّ في السحاب، ويقولون عليٌّ يبعث قبل يوم القيامة وهذا كله كذب وزور وبهتان.
والزيدية: وهم رافضة، وهم الذين يتبرءون من عثمان وطلحة والزبير وعائشة، ويرون القتال مع كل من خرج من ولد علي برًا كان أو فاجرًا حتى يَغلب أو يُغلب.
والحسنية: وهم الذين يقولون قول الزيدية.
والشيعة: وهم في ما زعموا ينتحلون حب آل محمد دون الناس، وكذبوا، بل هم خاصة المبغضون لآل محمد دون الناس، إنما شيعة آل محمد المتقون أهل السنة والأثر، من كانوا وحيث كانوا الذين يحبون آل محمد وجميع أصحاب محمد، ولا يذكرون أحدًا منهم بسوء، ولا عيب،','3','981',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (804,'ولا منقصة، فمن ذكر أحدًا من أصحاب محمد عليه السلام بسوء أو طعن عليه بعيب أو تبرأ من أحد منهم، أو سبهم، أو عرض بسبهم وشتمهم فهو رافضي مخالف خبيث ضال.
وأما الخوارج فمرقوا من الدين، وفارقوا الملة، وشردوا على الإسلام، وشذوا عن الجماعة، وضلوا عن سبيل الهدى، وخرجوا على السلطان والأئمة، وسلوا السيف على الأمة، واستحلوا دماءهم وأموالهم، وكفروا من خالفهم إلا من قال بقولهم/١٧٣/ وكان على مثل رأيهم، وثبت معهم في دار ضلالتهم، وهم يشتمون أصحاب محمد عليه السلام وأصهاره وأحبائه، ويتبرءون منهم، ويرمونهم بالكفر والعظائم ويرون خلافهم في شرائع الدين وسنن الإسلام، ولا يؤمنون بعذاب القبر، ولا الحوض، ولا الشفاعة، ولا يخرجوا أحدًا من أهل النار، وهم يقولون من كذب كذبة، أو أتى صغيرة أو كبيرة من الذنوب فمات من غير توبة فهو كافر فهو في النار خالدًا مخلدًا فيها أبدًا، وهم يقولون بقول البكرية في الحبة والقيراط، وهم قدرية جهمية مرجئة رافضة، ولا يرون جماعة إلا خلف إمامهم، وهم يرون تأخير الصلاة عن وقتها، ويرون الصوم قبل رؤيته، والفطر قبل رؤيته، وهم يرون النكاح بغير ولي ولا سلطان، ويرون المتعة في دينهم، ويرون الدرهم بالدرهمين يدًا بيد حلالًا، وهم لا يرون الصلاة في الخفاف، ولا المسح عليها، وهم لا يرون للسلطان عليهم طاعة، ولا لقريش خلافة، وأشياء كبيرة يخالفون فيها الإسلام وأهله، فكفى بقوم ضلالة يكون هذا رأيهم ومذهبهم ودينهم وليسوا من الإسلام في شيء، وهم المارقة،','3','982',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (805,'ومن أسمائهم الخوارج الحرورية، وهم أهل حرور أو الأزارقة وهم أصحاب نافع بن الأزرق، وقولهم أخبث الأقاويل وأبعدها من الإسلام والسُّنة.
والنجدية، وهم أصحاب نجد بن عامر، والأباضية، وهم أصحاب عبد الله بن إباض، والصفرية، وهم أصحاب داود بن النعمان حين قيل له: إنك صفر من العلم،','3','983',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (806,'والبهيسية، والميمونية، والخازمية كل هؤلاء خوارج فساق مخالفين للسنة خارجين من الملّة أهل بدعة وضلالة، وهم لصوص قطاع قد عرفناهم بذلك.
والشعوبية: وهم أصحاب بدعة يقولون: العرب والموالي عندنا واحد لا يرون للعرب حقًا ولا يعرفون لهم فضلًا، ولا يحبونهم، بل يبغضون العرب، ويضمرون لهم الغل والحسد والبغضة في قلوبهم. هذا قول قبيح ابتدعه رجل من أهل العراق وتابعه نفر يسير فقُتِلَ عليه.
وأصحاب الرأي: وهم مبتدعة ضُّلّال أعداء السّنة والأثر يرون الدين رأيًا وقياسًا واستحسانًا، وهم يخالفون الآثار، ويبطلون الحديث، ويردون على الرسول، ويتخذون/١٧٤/أبا حنيفة ومن قال بقوله إمامًا يدينون بدينهم، ويقولون بقولهم فأي ضلالة بأبين ممن قال بهذا أوكان على مثل هذا، يترك قول الرسول وأصحابه ويتبع رأي أبي حنيفة وأصحابه، فكفى بهذا غيًا وطغيانًا وردًا.','3','984',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (807,'والولاية بدعة: والبراءة بدعة، وهم يقولون: نتولى فلان ونتبرأ من فلان. وهذا القول بدعة فاحذروه.
ومن قال بشيء من هذه الأقاويل، أو رآها، أو هويها، أو رضيها، أو أحبها فقد خالف السنة، وخرج من الجماعة، وترك الأثر، وقال بالخلاف، ودخل في البدعة، وزل عن الطريق، وما توفيقنا إلا بالله عليه توكلنا، وبه استعنا ولا حول ولا قوة إلا بالله.
وقد أحدث أهل الأهواء والبدع والخلاف أسماء شنيعة قبيحة فسموا بها أهل السنة يريدون بذلك عيبهم والطعن عليهم والوقيعة فيهم والازدراء بهم عند السفهاء والجهال، فأما المرجئة فأنهم يسمون أهل السنة شكاكًا وكذبت المرجئة بل هم أولى بالشك وبالتكذيب. وأما القدرية فأإنهم يسمون أهل السنة والإثبات مجبرة، وكذبت القدرية، بل هم أولى بالكذب والخلاف أنفوا قدرة الله عن خلقه، وقالوا له ما ليس بأهل له تبارك وتعالى.
وأما الجهمية: فأنهم يسمون أهل السنة مشبهة وكذبت الجهمية أعداء الله بل هم أولى بالتشبيه والتكذيب افتروا على الله الكذب وقالوا على الله الزور والإفك وكفروا في قولهم.
وأما الرافضة: فإنهم يسمون أهل السنة ناصبة، وكذبت الرافضة، بل هم أولى بهذا الاسم إذ ناصبوا أصحاب محمد - صلى الله عليه وسلم - الصب والشتم وقالوا','3','985',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (808,'فيهم غير الحق ونسبوهم إلى غير العدل كذبًا وظلمًا، وجرأة على الله واستخفافًا لحق الرسول، والله أولى بالتغيير والانتقام منهم.
وأما الخوارج فأنهم يسمون أهل السنة والجماعة مرجئة، وكذبت الخوارج، بل هم المرجئة يزعمون أنهم على إيمان دون الناس ومن خالفهم كفار. وأما أصحاب الرأي والقياس فأنهم يسمون أصحاب السنة نابتة وكذب أصحاب الرأي أعداء الله، بل هم النابتة تركوا أثر الرسول وحديثه وقالوا بالرأي، وقاسوا الدين بالاستحسان، وحكموا/١٧٥/بخلاف الكتاب والسنة، وهم أصحاب بدعة جهلة ضلال طلاب دنيا بالكذب والبهتان. فرحم الله عبد اقال بالحق، واتبع الأثر، وتمسك بالسنة، واقتدى بالصالحين، وجانب أهل البدع وترك مجالستهم ومحادثتهم احتسابًا وطلبًا للقربة من الله وإعزاز دينه، وما توفيقنا إلا بالله.','3','986',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (809,'<span data-type="title" id=toc-232>باب: في الإيمان</span>
سئِلَ أحمد عن الإيمان فقال: قول وعمل يزيد وينقص. قيل ويستثنى فيه؟. قال: نعم. قلت: من زعم أن الإيمان قول أليس هو مرجئ؟ قال: نعم.
وسألتُ إسحاق عن الإيمان فقال: قول وعمل، ويزيد وينقص.
وسألتُ علي بن عبد الله قلت: ما قولك في الإيمان؟ قال: الإيمان قول وعمل، ويزيد وينقص على هذا أدركنا العلماء.
وسألتُ يحيى بن عبد الحميد عن الإيمان فقال أدركت المشايخ فذكر شريك وأبا الأحوص، وحماد بن زيد، وقيس يقولون: الإيمان قول وعمل. قلت أنا له: ويزيد وينقص؟ قال: نعم ويزيد وينقص.','3','987',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (810,'وسمعت عباس بن عبد العظيم قال: سمعت عبد الله بن داود يقول: الإيمان قول وعمل ونية. وقال: على هذا أدركنا أهل العلم. قال عباس: الإيمان قول وعمل ويزيد وينقص، وأقول مؤمن إن شاء الله وليس بشك.
حدثنا عباس قال: سمعت أبا الوليد يقول: الإيمان قول وعمل ونية ويزيد وينقص.
وسمعت محمد بن أبي بكر المقدمي: وأنا أقول الإيمان قول وعمل ونية ويزيد وينقص.
حدثنا المسيب بن واضح قال: حدثنا يوسف بن أسباط قال: كان سفيان يقول: الإيمان يزيد وينقص. قلت: وكيف يزيد؟ وكيف ينقص؟ قال يزيد بأداء الفرائض وينقص بترك الفرائض.
حدثنا أحمد بن حنبل قال: حدثنا شريح بن النعمان قال: حدثنا عبد الله بن نافع قال: كان مالك بن أنس يقول: الإيمان قول وعمل ويزيد','3','988',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (811,'وينقص.
حدثنا أحمد قال: حدثنا أبو جعفر السويدي، عن يحيى بن سليم، عن هشام، عن الحسن قال: الإيمان قول وعمل.
وقال أحمد: بلغني أن مالك بن أنس، وابن جريج، وشريكًا،','3','989',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (812,'وفضيل بن عياض قالوا: الإيمان قول وعمل.
حدثنا عمران بن يزيد بن خالد قال: حدثنا عبد الملك بن محمد/١٧٦/قال: سمعت الأوزاعي يقول: أدركت من أدركت من صدر هذه الأمة ولا يفرقون بين الإيمان والعمل، ولا يعدون الذنوب كفرًا ولا شركًا.
قال: وسمعت الأوزاعي يقول: الإيمان والعمل كهاتين وقال بإصبعيه لا إيمان إلا بعمل، ولا عمل إلا بإيمان.
حدثنا أحمد بن سعيد قال: سمعت النضر بن شميل يقول: الإيمان قول وعمل، ويزيد وينقص.
حدثنا علي بن يزيد قال: ثنا يحيى بن سليم الطائفي قال: سألتُ هشام بن حسان ما كان قول الحسن في الإيمان؟ قال: كان يقول: قول','3','990',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (813,'وعمل. قلت فما قولك أنت؟ قال: قول وعمل. قال: وقال مالك بن أنس: الإيمان قولٌ وعمل. وقال محمد بن عبد الله بن عمرو بن عثمان بن عفان: الإيمان قول وعمل. قال: وحدثني رجل من أهل البصرة يقال له أبو حيان قال: سمعت الحسن يقول لا يصلح قول إلا بعمل، ولا يصلح قول وعمل إلا بنية، ولا يصلح قول وعمل ونية إلا بسنة. قال: وقال الفضيل بن عياض: الإيمان قول وعمل. قال: وكان المثنى بن الصباح يقول: الإيمان قول وعمل. قال: وكان سفيان الثوري يقول: الإيمان قول وعمل.','3','991',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (814,'حدثنا أحمد قال: حدثنا وكيع قال: ثنا سفيان، عن سهيل بن أبي صالح، عن عبد الله بن دينار، عن أبي صالح، عن أبي هريرة قال: وقال رسول الله - صلى الله عليه وسلم -: ((الإيمان بضع وسبعون بابًا أدناها إماطة الأذى عن الطريق، وأرفعها قول لا إله إلا الله)).
حدثنا أحمد قال: حدثنا هشيم قال: حدثنا عبد الرحمن بن عبد الله بن دينار،','3','992',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (815,'عن أبيه، عن أبي صالح، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((الإيمان تسع وتسعون شعبة أعظم ذلك قول لا اله إلا الله وأدنى ذلك كف الأذى عن الطريق، والحياء شعبة من الإيمان)).
حدثنا أحمد قال: سمعت وكيع يقول الإيمان يزيد وينقص. قال: وكذلك كان سفيان يقول.
سمعت إسحاق قال: سألتُ سفيان بن عيينة فقلت: ما تقول في الايمان أيزيد؟ قال سفيان: أو أحد يستطيع رد هذا وقد قال الله تبارك وتعالى: {وَزِدْنَاهُمْ هُدًى} [الكهف: ١٣]، {وزدناهم إيمانًا مع إيمانهم}، {لِيَزْدَادُوا إِيمَانًا} [الفتح: ٤] قيل آيات أحتج بها وتعجب ممن لم يقل به. فقلت له: ما الإيمان أهو قول وعمل؟ فقال: نعم هو قول وعمل ومن يشك في هذا.','3','993',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (816,'وسمعت أبا إسحاق الرمادي قال: سمعت سفيان بن عيينة يقول: الإيمان قول وعمل فقالوا: يا أبا محمد أيزيد وينقص؟ قال: ما زاد شيء قط إلا نقص ألا تسمع الله يقول {فَزَادَهُمْ إِيمَاناً} [آل عمران: ١٧٣] فما زاد شيء قط إلا نقص.
قال سفيان: وقال أبو الدرداء: ما الإيمان إلا كقميص أحدكم ينزعه مرة ويلبسه أخرى.
قال أبو إسحاق: وأنا أقول: الإيمان قول وعمل يزيد وينقص، وكذلك أدركت الناس الذين أثق بهم.
حدثنا أبو بكر محمد بن يزيد قال: ثنا عبد الوهاب بن بجدة عن إسماعيل بن عياش، عن بشر بن عبد الله بن يسار السلمي قال: الايمان يزداد وينقص في كذا وكذا أنه من كتاب الله {لِيَزْدَادُوا إِيمَانًا مَّعَ إِيمَانِهِمْ} [الفتح: ٤]، {وَإِذَا تُلِيَتْ عَلَيْهِمْ آيَاتُهُ زَادَتْهُمْ إِيمَانًا} [الأنفال: ٢].','3','994',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (817,'حدثنا عبد ة بن عبد الرحيم، قال: حدثنا موسى بن أعين، الجزري قال: سمعت عبد الكريم بن مالك الجرزي وخصيف بن عبد الرحمن الجرزي يقولان: الإيمان يزداد وينقص. حدثنا أحمد بن حنبل، قال: حدثنا يزيد بن هارون، قال: حدثنا محمد بن طلحة، عن زبيد، عن ذر قال: كان عمر بن الخطاب يقول لأصحابه: هلموا نزداد ايمانًا. فيذكرون الله.','3','995',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (818,'حدثنا أحمد، قال: ثنا محمد بن فضيل، قال: ثنا أبي، عن شباك، عن إبراهيم، عن علقمة، أنه قال لأصحابه: امشوا بنا نزداد ايمانًا يعني تفقهًا.
حدثنا أحمد قال: ثنا وكيع، عن شريك، عن هلال بن حميد، عن عبد الله بن عكيم قال: سمعت ابن مسعود يقول في دعائة اللهم زدنا ايمانًا ويقينًا وفقهًا.','3','996',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (819,'حدثنا أحمد قال: ثنا وكيع قال: ثنا سفيان، عن هشام بن عروة، عن أبيه قال: ما نقصت أمانة عبد قط إلا نقص إيمانه.
سمعت بشار بن موسى الحفاف يقول: الإيمان قول وعمل ونية يزيد وينقص حتى يكون أعظم من الجبل وينقص حتى لا يبقى منه شيء. قلت: ويستثنى فيه؟ قال: كل شيء بمشيئة الله.
حدثنا أحمد بن حنبل قال: سمعت يحيى بن سعيد يقول: ما أدركت أحدًا من أصحابنا ولا بلغني إلا على الاستثناء. وقال يحيى: الإيمان قول وعمل. قال يحيى: وكان سفيان ينكر أن يقول: أنا مؤمن، وحسن يحيى الزيادة والنقصان ورآه. وسُئِلَ أحمد بن يونس وأنا أسمع عن الإيمان فقال: قول، وعمل يزيد وينقص، وبعضه أفضل من بعض.','3','997',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (820,'<span data-type="title" id=toc-233>باب: الاستثناء في الإيمان</span>
سُئِلَ أحمد بن حنبل: ما تقول في الاستثناء في الإيمان؟ قال: نحن نذهب إليه. قيل: الرجل يقول: أنا مؤمن إن شاء الله؟ قال: نعم. قال أحمد: وسمعت سفيان يقول: /١٧٨/إذا سُئِلَ أمؤمن أنت؟ إن شاء لم يجبه، وسؤالك إياي بدعة، لا يعنف من قال: الإيمان ينقص إن قال إن شاء الله ليس يكره، وليس بداخل في الشك.
سألتُ إسحاق قلت أنت تقول أنا مؤمن إن شاء الله؟ قال: نعم.
وسألتُ علي بن عبد الله عن الاستثناء في الإيمان؟ فقال: يقول أنا مؤمن إن شاء الله من غير شك، أو يقول: أرجو. قلت: فيحفظ عن جرير بن عبد الحميد عن عدة ذكرهم في الاستثناء؟ قال: قد سمعته منه ولم أكتبه فأنا أهابه، فذكر يزيد بن أبي الزياد، ومنصور، ومغيرة وغيرهم.','3','998',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (821,'قلت لعلي: فتحفظ عن عائشة من حديث جرير؟ قال: نعم، حدثنا به جرير، عن مغيرة، عن سمار بن سلمة، عن عبد الرحمن بن خصمة قال: قالت عائشة: أنهم المؤمنون إن شاء الله في حديث ذكره.
سألتُ أبا ثورعن الاستثناء في الإيمان ما تقول فيه؟ قال: الاستثناء لا بأس به من غير شك. قلت لأبي ثور: فإن قال لي قائل قال الله: {وَتُوبُوا إِلَى اللَّهِ جَمِيعًا أَيُّهَا الْمُؤْمِنُونَ} [النور: ٣١] فقد سماهم المؤمنين ونحو ذلك في القرآن؟ قال: يلزمه اسم الإيمان ولا يكون مستكملًا إلا إن يوافق قوله فعله، وذلك أنا قد نقول للرجل إذا دخل في الصلاة مصلي ولا يكون مستكملًا للصلاة حتى يؤديها، ونقول صائم وقد دخل في الصوم فلا يكون مستكملًا لصومه حتى يمضي يومه.','3','999',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (822,'سمعت أحمد بن سعيد قال: سمعت النضر بن شميل يقول: إذا سئِلَ أمؤمن أنت؟ قال: آمنت بالله، أو مؤمن إن شاء الله، أو مؤمن أرجو.
قال النصر: أدركت عليه أهل البصرة ابن عون وهشام وعوف وحماد وهشام بن حسان وعمران كلهم يستثنون وكان الحسن، وابن سيرين، وقتادة، وأيوب، وأصحابنا كلهم يستثنون.
حدثنا علي بن يزيد، عن يحيى بن سعيد إن الثوري قال: من قال أنا مؤمن ولم يستثني فهو مرجئ.
حدثنا علي بن يزيد قلت لعبد الله بن داود: أتعيب على من يقول أنا مؤمن إن شاء الله، ومن يقول أرجو؟ قال: لا، كل هذا حسن.
حدثنا محمد بن يزيد قال: حدثنا محمد بن كبير، عن الأوزاعي قال: لا بأس إن يقول أنا مؤمن إن شاء الله.
حدثنا أحمد قال: حدثنا أبو معاوية قال: حدثنا الأعمش، عن إبراهيم، عن','3','1000',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (823,'علقمة قال: تكلم عنده رجل من الخوارج بكلام كرهه فقال علقمة {وَالَّذِينَ يُؤْذُونَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا} الآية [الأحزاب: ٥٨] فقال له الخارجي: أمنهم أنت؟ قال: أرجو.','3','1001',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (824,'حدثنا أحمد قال: ثنا عبد الرحمن قال: ثنا سفيان، عن الحسن بن عبيد الله، عن إبراهيم قال: إذا قيل لك أمؤمن أنت؟ فقل أرجو.
حدثنا أحمد قال: حدثنا عبد الرحمن، عن سفيان، عن عطاء بن السائب، عن سعيد بن جبير قال: سألتُ ابن عمر قلت: اغتسل من غسل الميت؟ قال: أمؤمن هو؟ قال: قلت: أرجوا. قال: فيمسح بالمؤمن ولا يغتسل منه.
حدثنا عبد الله بن خبيب قال: سمعت يوسف بن أسباط يقول: إن قال مؤمن إن شاء الله فحسن، وإن قال: أرجو إن أكون مؤمنًا فحسن.
حدثنا أحمد قال: حدثنا وكيع، عن سفيان، عن الحسن بن عمرو، عن فضيل ابن إبراهيم قال: إذا سُئِلتَ أمؤمن أنت؟ فقل: آمنت بالله وملائكته','3','1002',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (825,'ورسله فأنهم سيدعونك.
حدثنا أحمد قال: ثنا عبد الرحمن قال: حدثني حسن بن عياش، عن مغيرة، عن إبراهيم قال: سؤال الرجل للرجل أمؤمن أنت بدعة.
حدثنا أبو جعفر محمد بن عوف الحمصي قال: حدثنا عمر بن حفص بن شليلة الدمشقي قال: حدثنا ابن سأبو ر عن سعيد بن عبد الجبار، عن عمر بن المغيرة حدثهم، عن أيوب السختياني، عن أبي مليكة، عن عائشة قالت:','3','1003',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (826,'ما كان رسول الله - صلى الله عليه وسلم - يبوح بهذا الكلام أن يقول إيماني كإيمان جبريل وميكائيل.
حدثنا محمد بن إسماعيل قال: ثنا مخلد بن يزيد قال: ثنا جعفر بن برقان، عن ميمون في قوله: {عِندَ ذِي الْعَرْشِ مَكِينٍ} [التكوير: ٢٠] قال: ذاك جبريل، وخيبةً لمن يزعم أن إيمانه مثل إيمان جبريل.
حدثنا محمد بن يزيد قال: ثنا أبو الحارث السلمي يعني عبد الوهاب بن','3','1004',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (827,'الضحاك، عن الوليد بن مسلم قال: قلت لمالك والليث بن سعد: الرجل يقول أنا مؤمن كإيمان جبريل وميكائيل؟ قالا: إذا قال تلك المقالة فهو إلى إيمان إبليس أقرب منه إلى إيمان جبريل وميكائيل.
سئِلَ إسحاق عن الرجل قال أنا ممن كتب الله الإيمان في قلبي؟ قال: إذا قال لا أحتاج إلى النطق فهو جهمي أراه قال: وإن قال: أحتاج إلى النطق بلا عمل فهو مرجئ. حدثنا علي بن يزيد قال: حدثني إبراهيم بن سعيد أنه سمع وكيع بن الجراح يقول: من قال: أنا مؤمن عند الله فهو مرجئ، ومن قال: إيماني كإيمان جبريل وميكائيل فهو شر من المرجئ، ومن زعم أن المعرفة تنفع في القلب وإن لم يتكلم بها فهو مرجئ.
قال علي بن يزيد: قلت لعبد الله بن داود من المرجئة؟ قال: من قال','3','1005',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (828,'إيماني كإيمان جبريل وميكائيل فهو رجل سوء وهو مرجئ.
حدثنا عبد الله بن محمد بن إسحاق الجزري قال: سمعت وكيعًا قال: كانت المرجئة تقول الإيمان قول، فجاءت الجهمية فقالت الإيمان معرفة. قال عبد الله: وحدثني إسحاق بن حكيم أن وكيعًا قال: وهذا عندنا كفر.','3','1006',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (829,'<span data-type="title" id=toc-234>باب: فيمن يقول: أنا مؤمن حقًا</span>
سمعت إسحاق يقول: لا نقول لرجل: إنه مؤمن باسم الإيمان الذي عليه وذكر ذلك عن النضر بن شميل.
حدثنا أحمد بن حنبل قال: ثنا أبو سلمة الخزاعي قال: قال مالك بن أنس، وأبو بكر بن عياش وعبد العزيز بن أبي سلمة، وحماد بن سلمة، وحماد بن زيد: الإيمان المعرفة والإقرار والعمل.
حدثنا أحمد قال: ثنا وكيع قال: قال سفيان: الناس عندنا مؤمنون في الأحكام والمواريث، ونرجو أن نكون كذلك، ولا ندري ما حالنا عند الله.','3','1007',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (830,'سمعت إسحاق يقول وسأله رجل فقال الرجل: يقول أنا مؤمن حقًا نفي هو كافر حقًا. حدثنا إسحاق قال: أخبرنا معتمر، عن ليث، عن صاحب له، عن الحسن قال: قال رسول الله - صلى الله عليه وسلم -: ((من قال أنه مؤمن حقًا فهو منافق حقًا)).
حدثنا أبو بكر محمد بن يزيد قال: ثنا عبد الصمد بن أزهر، عن ابن مبارك قال: قلت لابن عون تزعم أنك مؤمن؟ قال: إني لأستحي الله أن أزعم أني مسلم.
حدثنا أبو معن قال: ثنا وهب بن جرير قال: ثنا الفضيل بن حسان قال: ذكروا عند أبي جعفر محمد بن علي قول النبي - صلى الله عليه وسلم -:','3','1008',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (831,'((لا يزني حين يزني وهو مؤمن، ولا يسرق حين يسرق وهو مؤمن)) فقال: قال محمد بن علي فأدار هكذا كبيرة، فقال: هذا الإسلام دارة أخرى صغيرة في جوفها فقال: هذا الإيمان مقصور في الإسلام فإذا زنا أو سرق خرج من الإيمان، فإذا تاب رجع إلى الإيمان، ولا يخرجه من الإسلام إلا الشرك والدارة هكذا.
حدثنا أحمد قال: ثنا زيد بن حباب قال: حدثنا حسين بن واقد قال: ثنا عبد الله بن بريدة، عن أبيه قال: قال رسول الله - صلى الله عليه وسلم -: ((بيننا وبينهم ترك','3','1009',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (832,'الصلاة/١٨١/فمن تركها فقد كفر)).
حدثنا إسحاق بن إبراهيم قال: أخبرني بقية بن الوليد، عن زياد بن أبي حميد، عن مكحول فيمن يقول: الصلاة من عند الله ولا أصليها، والزكاة من عند الله ولا أؤديها. قال: يُستتاب فإن تاب وإلا قتل.
قال إسحاق وقال ابن المبارك ووكيع في ترك الصلاة متعمدًا، فأحدهما يقول: هو أن يترك الطهر إلى وقت العصر متعمدًا. وقال الآخر: هو أن يترك الظهر إلى المغرب، والمغرب إلى الفجر.
حدثنا أحمد بن الأزهر قال: حدثنا مروان بن محمد قال: حدثنا أبو مسلم الفزاري قال: سمعت الأوزاعي وسئل عن رجل قال: أنا أعلم أن','3','1010',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (833,'الصلاة حق ولا أصلي. قال: يعرض على السيف، فإن صلى وإلا قتل. قال: وسمعت سعيد بن عبد العزيز سئل عنه قال: يحبس ويضرب حتى يصلي.
حدثنا أحمد، قال: ثنا عبد الله بن يزيد، قال: ثنا عبد الله بن لهيعة، قال: حدثني بكر بن عمرو المعافري، عن رجل قال: قال عقبة بن عامر: إن الرجل لينفضل الإيمان كما يفضل ثوب المرأة.
حدثنا علي بن يزيد، قال: حدثنا سليمان بن داود قال: ثنا الصلت بن دينار، قال: سمعت ابن أبي مليكة يقول: لقد أتى عليّ برهة من دهري وما أرى','3','1011',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (834,'أني أسمع رجلًا يقول: إني مؤمن، فوالله ما رضوا بذلك حتى قالوا: إنه لمؤمن وإن نكح أمه وأخته إنه لمؤمن كإيمان جبريل - عليه السلام - ما كان محمد ليتفوه بها، والله لقد أدركت أكثر من ثلاثمائة من أصحاب محمد ما منهم رجل يموت إلا وهو يخشى النفاق على نفسه.
حدثنا أحمد قال: حدثنا سفيان بن عيينة، عن الزهري، عن سالم، عن أبيه','3','1012',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (835,'سمع النبي صلي الله عليه وسلم رجلًا يعظ أخاه في الحياء، فقال النبي - عليه السلام -: ((الحياء من الإيمان)).
حدثنا أحمد قال: ثنا يحيى بن سعيد، عن حبيب بن الشهيد قال: حدثنا عطاء، قال: سمعت أباهريرة يقول: ((لا يزني حين يزني وهو مؤمن، ولا يسرق حين يسرق وهو مؤمن)) قال عطاء: يتنحى عنه الإيمان.
حدثنا أحمد، قال: حدثنا يحيى، عن عوف قال: قال الحسن: يجانبه الإيمان ما دام كذلك، فإن راجع راجعه.','3','1013',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (836,'حدثنا أحمد، قال: ثنا يحيى، قال: ثنا أشعث، عن الحسن، عن النبي عليه السلام قال: ((ينزع منه الإيمان، فإن تاب أعيد إليه الإيمان)).
وسمعت أحمد وقيل له: المرجئة من هم؟ قال: من زعم أن الإيمان قول.','3','1014',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (837,'<span data-type="title" id=toc-235>باب/١٨٢/: الصلاة خلف المرجئ</span>
وسمعت أحمد يقول: لا يصلي خلف من يزعم أن الإيمان قول إذا كان داعية.
وسمعت إسحاق يقول: من قال: أنا مؤمن. فهو مرجئ. قلت: الصلاة خلفه؟ قال: لا.
وسمعت إسحاق أيضًا يقول: أول من تكلم بالإرجاء زعموا أن الحسن بن محمد بن الحنفية، ثم غلت المرجئة حتى صار من قولهم: إن قومًا يقولون: من ترك المكتوبات، صوم رمضان، والزكاة، والحج، وعامة الفرائض من غير جحود بها إنا لا نكفره يرجأ أمره إلى الله بعد إذ هو مقر فهؤلاء المرجئة الذين لا شك فيهم، ثم هم أصناف، منهم من يقول: نحن مؤمنون البتة، ولا يقول عند الله، ويرون الإيمان قولًا وعملًا، وهؤلاء أمثلهم، وقوم يقولون: الإيمان قول ويصدقه العمل، وليس العمل من الإيمان، ولكن العمل فريضة والإيمان هو القول، ويقولون: حسناتنا متقبلة، ونحن مؤمنون عند الله، وإيماننا وإيمان جبريل واحد. فهؤلاء الذين جاء فيهم الحديث أنهم المرجئة التي لعنت على لسان الأنبياء.','3','1015',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (838,'أخبرنا بقية بن الوليد، عن زرعة بن عبد الله الزبيدي أن شيخًا حدثهم عن معاذ بن جبل قال: لعنت المرجئة والقدرية على لسان سبعين نبيًا آخرهم محمد - صلى الله عليه وسلم -.
حدثنا عبد الله بن حنبق قال: سمعت يوسف بن أسباط يقول: أما المرجئة فهم يقولون: الإيمان كلام بلا عمل، من شهد أن لا إله إلا الله وأن محمدًا رسول الله فه مستكمل الإيمان كإيمان جبريل وميكائيل، ون قتل كذا وكذا مؤمنًا، وترك الصلاة، والصيام، والغسل من الجنابة، وهم يرون السيف على أمة محمد - صلى الله عليه وسلم -.
حدثنا علي بن يزيد قال: حدثنا عصمة بن المتوكل قال: سألتُ سفيان بن عيينة عن المرجئة؟ قال: من زعم أن الصلاة والزكاة ليستا من الإيمان.','3','1016',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (839,'حدثنا أبو سليمان يحيى بن عثمان قال: حدثنا بقية بن الوليد قال: ثنا محمد بن عبد الرحمن قال: حدثني قطن بن خليفة، عن ابن سابط، عن أبي بكر الصديق قال: قال رسول الله - صلى الله عليه وسلم -: ((صنفان من أمتي لا يدخلون الجنة: القدرية المرجئة)).
حدثنا محمد بن مصفى قال: ثنا بقية قال: حدثنا المعافى بن عمران الموصلي عن القاسم بن حبيب، عن نزار بن حيان، عن عكرمة قال: قال ابن عباس:','3','1017',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (840,'((اتقوا هذا الإرجاء فإنه شعبة من النصرانية)). /١٨٣/
حدثنا عمرو بن عثمان قال: حدثنا بقية قال: حدثني أبو العلاء الدمشقي قال: حدثني محمد بن جحادة، عن يزيد بن خمير، عن معاذ بن جبل قال: قال رسول الله - صلى الله عليه وسلم -: ((ألا وإن الله لقد لعن المرجئة والقدرية على لسان سبعين نبيًا، ألا وإن صنفين من أمتي لا يدخلون الجنة المرجئة والقدرية)).','3','1018',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (841,'حدثنا أحمد قال: ثنا محمد بن بشر قال: حدثني سعيد بن صالح، عن حكيم بن جبير قال: قال إبراهيم: للمرجئة أخوف عندي على أهل الإسلام من عدتهم من الأزارقة.
حدثنا أحمد قال: ثنا وكيع قال: ثنا القاسم بن حبيب، عن رجل يقال له: نزار بن حيان، عن عكرمة، عن ابن عباس قال: صنفان من هذه الأمة ليس لهما في الإسلام نصيب: المرجئة والقدرية.','3','1019',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (842,'حدثنا أحمد قال: ثنا معاوية بن عمرو قال: ثنا أبو إسحاق قال: قال الأوزاعي: كان يحيى (١) وقتادة يقولان: ليس من الأهواء أخوف عندهما من الإرجاء.
حدثنا أحمد قال: ثنا مؤمل قال: سمعت سفيان يقول: قال إبراهيم: تركت المرجئة الدين أرق من ثوب سابري.
_________
(١) ذكر في الهامش: هو يحيى بن أبي كثير.','3','1020',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (843,'حدثنا محمد بن يزيد قال: ثنا أبو أحمد، عن زياد بن المنذر قال: سمعت الشعبي يقول: لو كانت المرجئة من الدواب كانوا حمرًا.
حدثنا أحمد، ثنا عبد الله بن ميمون أبو عبد الرحمن الرقي قال: أبو مليح، قال: سئل ميمون عن كلام المرجئة فقال: أنا أكبر من ذلك.
حدثنا أحمد قال: ثنا عبد الرحمن قال: حدثني محمد بن أبي الوضاح، عن العلاء بن عبد الله بن رافع أن زرًا','3','1021',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (844,'أبا عمر أتى ابن جبير يومًا في حاجة قال: فقال: لا حتى تخبرني على أي دين أنت اليوم؟ فإنك لا تزال تلتمس دينًا قد أضللته، ألا تستحي من رأي أنت أكبر منه.
حدثنا أبو الأزهر قال: ثنا سعيد بن عامر، عن سلام، عن أيوب قال: أنا أكبر من الإرجاء.
حدثنا أبو الأزهر قال: سمعت أبا ضمرة يقول: قال:','3','1022',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (845,'اتق مزاحمًا لعن الله دينًا أنا أكبر منه.
حدثنا أحمد قال: ثنا أبو عمر قال: ثنا حماد بن سلمة، عن عطاء بن السائب، عن زاذان وميسرة قالا: أتينا الحسن بن محمد فقلنا: ما هذا الكتاب الذي ضعت - وكان هو الذي أخرج كتاب المرجئة؟ قال زاذان: فقال لي: يا أبا عمر لوددت أني مت قبل أن أخرج هذا الكتاب.
قال أحمد: لا يعجبني للرجل أن يخالط المرجئة.','3','1023',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (846,'حدثنا بشار بن موسى قال: قيل لشريك/١٨٤/ونحن عنده: يا أبا عبد الله كانوا يتزاورون وأهواؤهم مختلفة؟ قال: لا. حدثنا مغيرة قال: سلم التيمي على النخعي فلم يرد عليه، وسلم ذر على سعيد جبير فلم يرد عليه، قيل له: لم يا أبا عبد الله؟ قال: لأنهم كانوا يرون الإرجاء، زعموا أن الصلاة ليس من الإيمان، إنما الإيمان قول، وقد حدثنا أبو إسحاق، عن البراء في قول الله تعالى: {وَمَا كَانَ اللهُ لِيُضِيعَ إِيمَانَكُمْ} [البقرة: ١٤٣] قال: صلاتكم نحو بيت المقدس.','3','1024',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (847,'حدثنا أحمد بن سليمان الباهلي قال: ثنا مرحوم العطار، قال: سمعت أبي وعمي سمعا الحسن ينهى عن مجالسة معبد الجهني، ويقول: لا تجالسوه، فإنه ضال مضل.','3','1025',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (848,'<span data-type="title" id=toc-236>باب: في القدر</span>
سمعت إسحاق يقول: الخير والشر من الله مقدور على عباده.
حدثنا أبو معن قال: ثنا أبو أحمد الزبيري قال: حدثنا سفيان الثوري، عن عمر بن محمد قال: كنت عند سالم بن عبد الله فقال له رجل: الرجل يزني كتبه الله عليه؟ قال: نعم. قال: ويعذبه عليه؟ قال: نعم.
حدثنا محمد بن الوزير الدمشقي، قال: حدثنا أبو سفيان السفر، قال: حدثنا الأوزاعي، قال: حدثني يونس بن يزيد الأيلي، عن ابن شهاب، عن عبد الرحمن بن كعب بن مالك، عن أبيه قال: قال رسول الله - صلى الله عليه وسلم -: ((إن الله لما وعد','3','1026',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (849,'موسى أن يكلمه خرج إلى الوقت الذي وعده الله، قال: فبينما هو يناجي ربه إذ سمع خلفه صوتًا، فقال: إلهي إني لأسمع خلفي صوتًا لعل قومي ضلوا؟ قال: نعم يا موسى. قال: إلهي فمن أضلهم؟ قال: أضلهم السامري. قال: إلهي فيما أضلهم؟ قال: صاغ لهم عجلًا جسدًا له خوار. قال: إلهي: هذا السامري صاغ لهم العجل فمن نفخ فيه الروح حتى صار له خوار؟ قال: أنا يا موسى. قال: فوعزتك إلهي ما أضل قومي أحد غيرك. قال: صدقت يا حكيم الحكماء، لا يتسنى لحكيم أن يكون أحكم منك)).
حدثنا إسحاق بن إبراهيم قال: قال أخبرنا بقية بن الوليد، عن أرطأة بن المنذر، عن بشر، عن مجاهد، عن ابن عمر أن النبي - صلى الله عليه وسلم - قال: إن الله أول شيء','3','1027',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (850,'أخذ القلم بيمينه وكلتا يديه يمين فكتب: الدنيا بما فيها من عمل معمول برٌ أو فاجر، رطبٌ أو يابس، وأحصاه في الذكر ثم قال: اقرءوا إن شئتم: {هَذَا كِتَابُنَا يَنطِقُ عَلَيْكُم بِالْحَقِّ إِنَّا كُنَّا نَسْتَنسِخُ مَا كُنتُمْ تَعْمَلُونَ} [الجاثية: ٢٩] فهل تكون النسخة إلا من شيء قد فرغ منه. /١٨٥/
حدثنا أحمد بن فرج قال: ثنا بقية بن الوليد، عن محمد بن الوليد الزبيدي، عن راشد بن سعد، عن عبد الرحمن الحمصي بن أبي قتادة البصري، عن أبيه، عن حكيم بن حزام أن رجلًا قال: يا رسول الله، أتبدأ','3','1028',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (851,'الأعمال أم قضي القضاء؟ فقال رسول الله - صلى الله عليه وسلم -: ((إن الله أخذ ذرية آدم من ظهره ثم أفاض بهم في كفيه، ثم أشهدهم على أنفسهم، ثم قال: هؤلاء في الجنة، وهؤلاء في النار، فأهل الجنة ميسرون لعمل أهل الجنة، أهل النار ميسرون لعمل أهل النار)).
حدثنا يحيى بن عثمان قال: ثنا محمد بن حميد قال: حدثني يزيد بن يوسف، عن أبي عبد الرحمن الأنصاري، عن عمرو بن دينار، عن عبد الرحمن بن سابط، عن ابن عباس، عن رسول الله - صلى الله عليه وسلم - أنه قال: ((لعلك أن تبقى بعدي حتى','3','1029',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (852,'تدرك قومًا يكذبون بقدر الله، ويحملون الذنوب على عباده، واستقوا كلامهم من النصارى، فإذا كان كذلك فابرأ إلى الله منهم)) فكان ابن عباس يرفع يديه فيقول: اللهم إني أبرأ إليك منهم كما أمرني رسول الله - صلى الله عليه وسلم -.
حدثنا هشام بن عمار قال: حدثنا سويد بن عبد العزيز قال: ثنا قرة بن عبد الرحمن بن حويل، عن أبي قبيل حيي بن هانئ المعافري، عن شقي بن ماتع الأصبحي، عن عبد الله بن عمرو بن العاص قال: دخلت على','3','1030',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (853,'رسول الله - صلى الله عليه وسلم - وفي يده كتاب فقال: ((هذا كتاب كتبه رب العالمين بعدد أهل الجنة فيه أسماؤهم وأسماء آباؤهم، ثم أجمل على آخرهم لا يزاد فيهم ولا ينتقص، وبعدد أهل النار فيه أسماؤهم وأسماء آبائهم، ثم اجمل على آخرهم لا يزاد فيهم ولا ينتقص)) فقال رجل: ففيما العمل يا رسول الله؟ قال: ((اعملوا وسددوا، فإن صاحب الجنة خاتم بعمل أهل الجنة، وإن عمل أي عمل، وإن صاحب النار خاتم بعمل أهل النار وإن عمل أي عمل، ثم قال: {فَرِيقٌ فِي الْجَنَّةِ وَفَرِيقٌ فِي السَّعِيرِ} [الشورى: ٧])).
حدثنا عثمان بن سلام الأهوازي، قال: حدثنا صفوان بن عيسى، عن عزرة بن ثابت، عن يحيى بن عقيل، عن يحيى بن يعمر، عن أبي الأسود الديلي قال:','3','1031',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (854,'غدوت على عمران بن حصين يومًا من الأيام فقال لي عمران: يا أبا الأسود أرأيت ما يعمل الناس اليوم ويكدحون فيه أشيء قضي عليهم ومضى عليهم في قدر قد سبق، أو شيء فيما يستقبلون مما أراهم نبيهم - صلى الله عليه وسلم - واتخذن به عليهم الحجة؟ قال: قلت: بل شي قضي/١٨٦/ عليهم. قال: فقال عمران: فهل يكون ذلك ظلمًا؟ قال: ففزعت من ذلك فزعًا شديدًا، ثم قلت: إنه ليس شيء إلا خلق الله وملك يده لا يسئل عمّا يفعل وهم يسئلون، قال عمران: سدد الله، والله ما سألتُك إلا لأحرر عقلك، إن رجلًا من مزينة أو جهينة أتى رسول الله - صلى الله عليه وسلم - فقال: يا رسول الله، أرأيت ما يعمل الناس اليوم ويكونون فيه أشيء قضي عليهم ومضى عليهم في قدر قد سبق، أو فيما يستقبلون مما أتاهم به نبيهم واتخذن به الحجة عليهم؟ قال: ((بل شيء قضى عليهم ومضى عليهم)) قالوا: يا رسول الله فلما يعملون إذًا؟ فقال رسول الله صلي الله عليه وسلم: ((من كان الله خلقه لواحدة من المنزلتين يهيئه الله لعملها، وتصديق ذلك في كتاب الله {وَنَفْسٍ وَمَا سَوَّاهَا * فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَاهَا} [الشمس: ٧ - ٨]. حدثنا المسيب بن واضح، قال: ثنا يوسف بن أسباط، عن يحيى السقا، عن أبي حازم، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((ما كانت زندقة','3','1032',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (855,'قط إلا كان أصلها التكذيب بالقدر)).
حدثنا أبو الفضل عباس بن الوليد، قال: حدثنا مروان بن محمد قال: حدثنا سليمان بن عتبة السلمي، قال: حدثنا يونس بن ميسرة بن حلبس، عن أبي إدريس الخولاني، عن أبي الدرداء أنهم قالوا: يا رسول الله، أرأيت','3','1033',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (856,'ما يعمل أفي شيء قد فرغ منه أم شيء نستأنفه؟ فقال رسول الله - عليه السلام -: ((في شيء قد فرغ منه)). قالوا: فكيف بالعمل بعد القضاء؟ فقال رسول الله - صلى الله عليه وسلم -: ((كل امرئ ميسر لما خلق له)).
حدثنا أبو الربيع الزهراني، قال: ثنا عبد الله بن يزيد المقري، قال: ثنا عبد الله بن لهيعة بن عقبة، قال: حدثني عمرو بن شعيب قال: كنت عند سعيد بن المسيب إذ جاءه رجل فقال: إن ناسًا يقولون: قدر الله كل شيء ما خلا الأعمال. فغضب سعيد غضبًا لم أره غضب مثله قط حتى هم بالقيام، ثم قال: فعلوها فعلوها، ويحهم لما يعملون، أما إني قد سمعت فيهم بحديث كفاهم به شرًا، فقلت: وما ذاك يا أبا محمد رحمك الله؟ قال: حدثني رافع بن خديج، عن النبي - صلى الله عليه وسلم - قال: ((سيكون في أمتي قوم يكذبون بالله وبالقرآن وهم لا يشعرون)) قال: فقلت يقولون كيف يا رسول الله؟ قال: ((يقرون ببعض القدرويكفرون ببعض)) قال: قلت: يقولون: ماذا يا رسول الله؟ قال: ((يقولون الخير من الله والشر من إبليس، ثم/١٨٧/يقرون على ذلك كتاب الله فيكفرون بالله وبالقرآن بعد الإيمان والمعرفة، فماذا تلقى أمتي','3','1034',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (857,'منهم من العداوة والبغضاء؟ ثم يكون المسخ، فيمسخ عامة أولئك قردة وخنازير، ثم يكون الخسف فقل من ينجو منهم، المؤمن يؤمئذ قليل فَرَجُه، شديد غمه))، ثم بكى النبي - صلى الله عليه وسلم - حتى بكينا لبكائه، فقيل: يا رسول الله، ما هذا البكاء؟ قال: ((رحمة لهم، الأشقياء لأن منهم المجتهد ومنهم المتعبد، أما إنهم ليسوا بأول من سبق إلى هذا القول وضاق بحمله ذرعًا، إن عامة من هلك من بني إسرائيل بالتكذيب بالقدر)) فقيل: يا رسول الله، فما الإيمان بالقدر؟ قال: ((أن تؤمن بالله وحده، وتؤمن بالجنة والنار، وتعلم أن الله تبارك وتعالى خلقهما قبل الخلق، ثم خلق الخلق لهما، فجعل من شاء منهم للجنة، ومن شاء منهم للنار، عدلًا منه، فكل يعمل لما فرغ منه وصائر إلى ما خلق له))، فقلت: صدق الله ورسوله.
حدثنا محمد بن مصفى قال: حدثنا بقية بن الوليد، عن حبيب بن عمر الأنصاري، عن أبيه، عن ابن عمر، عن رسول الله - صلى الله عليه وسلم - أنه قال:','3','1035',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (858,'((ينادي مناد يوم القيامة: ليقم خصماء الله، وهم القدرية)).
حدثنا زيد بن يزيد قال: حدثنا عبد الملك بن عمرو قال: حدثنا سليمان بن سفيان، عن عبد الله بن دينار، عن عبد الله بن عمر، عن عمر بن الخطاب قال: لما نزلت {فَمِنْهُمْ شَقِيٌّ وَسَعِيدٌ} [هود: ١٠٥] سألتُ النبي - صلى الله عليه وسلم - فقلت: يا رسول الله، فعلى ما يعمل، أعلى أمر قد فرغ منه، أم شيء لم يفرغ منه؟ قال: ((بل على شيء قد فرغ منه وجرت به الأقلام يا عمر، ولكن كل ميسر لما خلق)).','3','1036',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (859,'حدثنا محمد بن مصفى قال: حدثنا بقية قال: حدثنا ابن أبي جميله، عن نافع، عن ابن عمر، عن النبي - صلى الله عليه وسلم - قال: ((إن الله لو شاء أن لا يعصى ما خلق إبليس)).
حدثنا محمد بن مصفى، قال: حدثنا بقية، عن سعيد بن جميل، عن ثابت البناني، قال: سمعت ابن عمر يقول: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((يكونون مكذبين، ألا وهم مجوس هذه الأمة، ما هلكت أمة قط إلا بشركها، وكان بدو شركها بعد إيمانها إلا بتكذيب للقدر)).
حدثنا ابن مصفى قال: حدثنا أبو المغيرة قال: ثنا أبو يحيى/١٨٨/','3','1037',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (860,'الحبشي، قال: سمعت طاوسًا يحدث عن ابن عباس قال: قال رسول الله - صلى الله عليه وسلم -: ((لكل أمة مجوس وإن مجوس أمتي لأقوام يكذبون بمقادير الله، وإن أدنى تكذيب بالقدر كمثل من أشرك بالله بعد الإيمان)).
حدثنا محمد بن مصفى قال: ثنا بقية، عن عمر بن محمد، عن زيد بن أسلم، عن أبيه، عن أبي هريرة، عن رسول الله - صلى الله عليه وسلم - لما نزلت: {لِمَن شَاء مِنكُمْ أَن يَسْتَقِيمَ} [التكوير: ٢٨] قالوا: الأمر إلينا إن شئنا استقمنا وإن شئنا لم نستقم، فاهبط الله عليه جبريل يقول: كذبوا يا محمد: {وَمَا تَشَاؤُونَ إِلَّا أَن يَشَاء اللَّهُ رَبُّ الْعَالَمِينَ} [التكوير: ٢٩] ففرج ذلك عن رسول الله - صلى الله عليه وسلم -.','3','1038',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (861,'حدثنا الحميدي قال: حدثنا سفيان قال: ثنا أبو الزناد، عن الأعرج، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((احتج آدم وموسى فقال موسى: يا آدم أنت أبو نا وأخرجتنا من الجنة. فقال آدم: يا موسى، اصطفاك الله بكلامه، وخط لك في الألواح بيده، أتلومني على أمر قدره الله عليّ قبل أن يخلقني بأربعين عامًا؟)) قال رسول الله - صلى الله عليه وسلم -: ((فحج آدم موسى، فحج آدم موسى)).
حدثنا هشام بن عمّار قال: حدثنا سليمان بن عتبة قال: سمعت يونس بن ميسرة بن حلبس يحدث عن أبي إدريس الخولاني، عن أبي الدرداء، عن النبي - صلى الله عليه وسلم - قال: ((لا يبلغ العبد حقيقة الإيمان حتى يعلم أن ما أصابه لم يكن ليخطئه، وأن ما أخطأه لم يكن ليصيبه، ولا يدخل الجنة عاق، ولا مدمن','3','1039',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (862,'خمر، ولا مكذب بقدر)).
حدثنا محمود، قال: حدثنا مروان بن محمد، قال: ثنا رباح بن الوليد، قال: حدثني إبراهيم بن أبي عبلة، عن أبي يزيد الأزدي، عن عبادة بن الصامت قال: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((أول ما خلق الله القلم فقال له: اكتب، فقال: يا رب وما أكتب؟ قال: اكتب مقادير كل شيء)).','3','1040',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (863,'حدثنا محمد بن مصفى قال: ثنا بقية بن الوليد، عن الأوزاعي، عن ابن جريج، عن أبي الزبير، عن جابر قال: قال رسول الله - صلى الله عليه وسلم -: ((إن مجوس هذه الأمة قوم يكذبون بأقدار الله، إن مرضوا فلا تعودوهم، وإن لقيتمهم فلا تسلموا عليهم، وإن ماتوا فلا تشهدوهم)).
حدثنا سعيد بن عون، قال: ثنا جعفر بن سليمان، عن مرزوق أبي بكر، عن أبي الزبير عن جابر قال: سأل سراقة بن جعثم/١٨٩/رسول الله - صلى الله عليه وسلم -','3','1041',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (864,'قال: يا رسول الله، أيعمل فيما جرت به الأقلام وجفت به الكتب، أم يعمل فيما نستأنف؟ قال: ((فيما جرت به الأقلام، وجفت به الكتب)) قال: ففيما يعمل العاملون يا رسول الله؟ قال: ((كل ميسر للذي خُلِقَ له)).
حدثنا أبو معن قال: حدثنا أبو إسحاق قال: حدثنا الأزرق بن يحيى قال: ثنا عبيد الله بن زياد، عن أبان، عن أنس بن مالك أن رسول الله - صلى الله عليه وسلم - قال: ((القدري أوله مجوسي وآخره زنديق)).
حدثنا محمد بن نافع، قال: ثنا الحفري، قال: حدثنا سفيان، عن عمر بن محمد، عن عمر مولى عفرة، عن حذيفة، عن رسول الله - صلى الله عليه وسلم -: ((إن قومًا','3','1042',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (865,'يقولون: لا قدر. وهم مجوس هذه الأمة، فلا تشهدوا لهم جنازة، ولا تعودوا لهم مريضًا، فإنهم شيعة الدجال، وحق على الله أن يلحقهم بالدجال)).
حدثنا أبو معن، قال: ثنا يزيد بن هارون، قال: حدثنا بشر بن نمير، عن القاسم، عن أبي أمامة، عن النبي عليه السلام قال: ((خلق الله الخلق وقضى القضية، وأخذ ميثاق النبيين وعرشه على الماء، فأهل الجنة أهلها، وأهل النار أهلها)) قالوا: يا رسول الله ففيم العمل؟ قال: ((يعمل','3','1043',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (866,'قوم لمنازلهم)).
حدثنا محمد بن أبي بكر، قال: حدثنا حسان بن إبراهيم، قال: ثنا سعيد بن مسروق، عن يوسف بن أبي بردة، عن أبي بردة قال: أتيت عائشة فقلت: يا أمتاه حدثيني بشيء سمعتيه من رسول الله - صلى الله عليه وسلم - فقالت: قال رسول الله - صلى الله عليه وسلم -: ((الطير تجري بقدر)) وكان يعجبه الفال الحسن.','3','1044',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (867,'حدثنا محمد بن الوزير قال: ثنا مروان بن محمد قال: حدثنا بقية بن الوليد قال: ثنا حبيب بن عمر الأنصاري، عن أبيه قال: سمعت واثلة بن الأسقع يقول: أما أنا فلست أصلي خلف قدري.
حدثنا الحسين بن محمد السعدي قال: ثنا ميمون بن زيد قال: ثنا حرب بن شريح قال: قلت لأبي جعفر: إن لنا إمامًا قدريًّا؟ قال: أعد كل صلاة صليت خلفه.','3','1045',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (868,'حدثنا محمد بن الوزير الدمشقي قال: حدثنا سلام بن سليمان قال: ثنا عبيد الله بن أبي سفيان قال: سمعت ابن سيرين يقول: لا تأكلوا ذبائح القدرية.
حدثنا سعيد بن منصور، قال: ثنا عبد العزيز بن محمد، عن أبي سهيل قال: إذا سلم عليك القدري فقل: وعليك.
حدثنا محمد بن عمر بن علي، قال: حدثنا الحسن بن حبيب، عن وائل بن زريق، عن عبد العزيز بن عمر، عن عمر بن عبد العزيز قال: لا تغزوا مع','3','1046',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (869,'القدرية، فإنهم لا يبصرون.
حدثنا محمد بن عمر بن مقدم قال: ثنا أبو عصمة الحداء، عن أبي صالح، عن أبيه قال: خوصم إلى عبيد الله بن الحسن في غلام اشترى من رجل، فقال: إني اشتريت هذا وضمن لي لا داء ولا غائلة بيع المسلم المسلم، وإني وجدته قدريًّا. قال: وأي داء أدوى منه؟ قال: فرده عليه.
حدثنا بشر بن معاذ وعيسى بن سليمان قالا: حدثنا عبد الله بن جعفر قال: أخبرني أبو سبيل بن نافع بن مالك قال: شاورني عمر بن عبد العزيز قال:','3','1047',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (870,'ما ترى في القدرية؟ قال: قلت: أرى أن يستتأبو ا، فإن تأبو اوإلا ضربت أعناقهم. فقال عمر: أما إن تلك سيرة الحق.
حدثنا أبو سليمان يحيى بن عثمان قال: حدثنا اليمان بن عدي قال: سألتُ الضحاك بن حمزة عن القدري قال: يستتاب، فإن تاب وإلا قتل.
حدثنا محمد بن الوزير قال: ثنا مروان بن محمد قال: ثنا معاوية بن يحيى قال: ثنا أرطأة بن المنذر، عن حكيم بن عمير قال: ذكر عند عمر بن','3','1048',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (871,'عبد العزيز أهل القدر، فقال عمر: إن كان يتخذونه دينًا فهم أهل أن تسل ألسنتهم من أقفيتهم.
حدثنا بشر بن معاذ، قال: حدثنا معتمر، عن أبي مخزوم، عن سيار أبي الحكم أن عمر بن العزيز قال: ينبغي للمكذبة بالقدر أن يستتأبو ا، فإن تأبو اوإلا أخلوا من ديار المسلمين. حدثنا محمد بن الوزير قال: حدثنا مروان بن محمد قال: سئل مالك عن القدري الذي يستتاب؟ قال: الذي يقول: إن الله لا يعلم ما العباد عاملون حتى يعملوا. قال أبو عبد الله: هؤلاء الذين أخرجوا الله من علمه.
حدثنا أبو معن قال: حدثنا يحيى بن الفضل قال: شهدت مكيف الندبي','3','1049',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (872,'سأل أبا يوسف القاضي فقال: يا أبا يوسف ما الحكم في القدرية؟ قال: الحكم أنه من ججحد العلم استتبته، فإن تاب وإلا قتلته. حرب بن إسماعيل الحنظلي الكرماني قال: حدثنا محمد بن أبي بر قال: حدثنا حسان بن إبراهيم قال: حدثنا محمد بن الفضل، عن كرز بن وبرة، عن محمد بن كعب القرظي قال: لعنت القدرية على لسان سبعين نبيًا، منهم نبينا هذا، فإذا كان يوم القيام نادى منادٍ ليقم خصماء الله، فتقوم القدرية.','3','1050',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (873,'حدثنا محمد بن عمر بن علي قال: ثنا الحسن بن حبيب، عن الأصبغ/١٩١/بن زيد، عن أبي غياث قال: سمعت أنس بن مالك يقول: المكذبين بالقدر المشركين.
حدثنا محمد بن عمر قال: حدثنا ابن ندبة، عن عمر بن محمد قال: قال ابن عباس: إيمان بالقدر نظام للتوحيد، فمن وحد الله وكذب بالقدر كان تكذيبه بالقدر نقضًا للتوحيد.
حدثنا أبو معن قال: ثنا مؤمل بن إسماعيل قال: ثنا عمر بن محمد العمري','3','1051',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (874,'قال: ثنا سالم بن عبد الله قال: قال ابن عمر: من زعم أن مع الله بارئًا، أو خالقًا، أو رازقًا، أو قاضيًا، أو راضيًا، أو يملك لنفسه ضرًا أو نفعًا، أو موتًا أو حياة، أو نشورًا بعثه الله أخرس لسانه، وأعمى بصره، وجعل عمله هباءً منثورًا، وقطع به الأسباب، وأكبه في النار على وجهه.
حدثنا حيان بن عمار قال: ثنا عمر بن يونس قال: حدثنا أيوب بن النجار، عن ابن عون، عن محمد قال: حدثت أن القدرية يمسخون في قبورهم قردة وخنازير.
حدثنا سعيد بن منصور قال: ثنا حماد بن زيد، عن أبي محروم النهشلي قال: قال عمر بن عبد العزيز: يا أيها الناس اتقوا الله، فإنه والله لا بد','3','1052',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (875,'لأقوام أن يعملوا أعمالًا كتبها الله عليهم ووضعها في أعناقهم.
حدثنا المسيب بن واضح، قال: ثنا يوسف بن السفر، عن الأوزاعي، عن الزهري قال: القضاء هو القدر، والقدر هو العلم، والعلم نافذ في العباد فيما عملوا من خير أو شر مكتوب في رقابهم إلى أن يفارقوا الدنيا.
حدثنا سعيد بن منصور قال: ثنا أبو معشر، عن محمد بن كعب في قوله: {مَا أَنتُمْ عَلَيْهِ بِفَاتِنِينَ * إِلَّا مَنْ هُوَ صَالِ الْجَحِيمِ} [الصافات: ١٦٢ - ١٦٣]، قال: ما أنتم بمضلين أحدًا إلا من كتبت عليه أنه من أهل الجحيم.
حدثنا سعيد بن منصور قال: حدثنا عبد الحميد بن سليمان، عن أبي حازم قال: ذكر عند ابن عمر قوم يكذبون بالقدر فقال: لا تجالسوهم،','3','1053',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (876,'ولا تسلموا عليهم، ولا تعودوا مرضاهم، ولا تشهدوا جنائزهم، وأخبروهم أني بريء منهم، وأنهم مني براء، هم مجوس هذه الأمة.
حدثنا كثير بن يحيى بن كثير قال: حدثني منصور بن زيد العدوي قال: حدثنا عمر بن محمد بن زيد قال: سأل رجل سالم بن عبد الله قال: الربا بقدر؟ قال: نعم، قال: قضاء قضاه الله عليه؟ قال: نعم على رغم أنفك.','3','1054',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (877,'<span data-type="title" id=toc-237>باب: في الشهادة على قوم بالجنة</span>
سمعت إسحاق بن إبراهيم يقول: مضت السنّة من النبي - صلى الله عليه وسلم - /١٩٢/والخلفاء من بعده، واجتمع علماء الأمصار على ذلك أن لا يشهد أحد على أحد بعد النبي عليه السلام أنه في الجنة لصلاحه وفضله وسوابقه، ولا أحد أنه من أهل النار؛ لارتكاب المعاصي والذنوب، ونكل ذلك إلى الله، فإنه الذي يتولى السرائر، قال: ويحق عليك أن تعرف وتستيقن أن ما صح عن النبي - صلى الله عليه وسلم - أنه قال: في الجنة، فهو في الجنة، كذلك الأمر عند أهل العلم من غير أن ينصب الشهادة.
حدثنا مالك بن سعد بن أخي روح قال: ثنا محمد بن يعلي قال: حدثنا عمر بن الصبح، عن خالد بن ميمون، عن نفيع بن الحارث، عن زيد بن أرقم','3','1055',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (878,'قال: قال رسول الله - صلى الله عليه وسلم - فيما يروي عن ربه تبارك وتعالى أنه قال: ((لا ينزلوا عبادي العارفين الموحدين المقرين بالجنة ولا النار، حتى أكون أنا الذي أنزلهم بعلمي فيهم، ولا يكلفوا ذلك ما لم يكلفوا، ولا يحاسبوا العباد دون ربهم)).
حدثنا أبو معن قال: حدثنا معتمر عن ليث، عن جعفر العبد ي قال: قال رسول الله - صلى الله عليه وسلم -: ((ويل للقتالين من أمتي الذين يقولون: فلان في الجنة، وفلان في النار)).','3','1056',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (879,'<span data-type="title" id=toc-238>باب: الأمر في قريش ما بقي من الناس اثنان</span>
حدثنا أحمد بن عبد الله بن يونس، قال: حدثنا عاصم بن محمد، عن أبيه قال: قال عبد الله بن عمر: قال رسول الله - صلى الله عليه وسلم -: ((لا يزال هذا الأمر في قريش ما بقي من الناس اثنان))، وقال بيده هكذا.
حدثنا أحمد بن حنبل، قال: حدثنا محمد بن جعفر، قال: ثنا شعبة، عن حبيب بن الزبير قال: سمعت ابن أبي الهذيل قال: كان عمرو بن العاص','3','1057',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (880,'يتخولنا، فقال رجل من بكر بن وائل: لئن لم تنته قريش ليضعن هذا الأمر في جمهور من جماهير العرب سواهم، فقال عمرو بن العاص: كذبت، سمعت رسول الله - صلى الله عليه وسلم - يقول: ((قريش ولاة الناس في الخير والشر إلى يوم القيامة)).
حدثنا أبو معن قال: ثنا عبد الملك بن عمرو قال: حدثنا كثير بن عبد الله، عن أبيه، عن جده أن رسول الله - صلى الله عليه وسلم - قال: ((يا معشر قريش إنكم الولاة بعدي لهذا الأمر، فلا تموتن إلا وأنتم مسلمون)).','3','1058',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (881,'حدثنا يحيى بن عبد الحميد، قال: حدثنا إبراهيم بن سعد، عن صالح بن كبسان، عن ابن شهاب، عن عبيد الله بن عبد الله بن عتبة، عن ابن مسعود قال: قال النبي - صلى الله عليه وسلم -: ((يا معشر قريش إن هذا الأمر فيكم ما لم تعصوا الله، فإذا عصيتموه بعث الله عليكم من يلحاكم كما يلحى هذا القضيب)) والحا قضيبًا في يده أبيض. حدثنا سعيد بن منصور، قال: حدثنا أبو معاوية، قال: حدثنا جعفر بن برقان،','3','1059',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (882,'عن زيد بن أبي أنيسة، عن أنس بن مالك قال: قال رسول الله - صلى الله عليه وسلم -: ((الجهاد ماض منذ بعثني الله إلى أن يقاتل آخر أمتي الدجال، لا يبطله جور جائر، ولا عدل عادل)).
حدثنا سعيد بن منصور، قال: حدثنا جعفر بن برقان، عن هشام قال: أخبرنا مغيرة، عن إبراهيم قال: سئل عن الغزو مع بني مروان، وذكر ما يصنعون قال: إن عرض به إلا الشيطان لينبطهم عن جهاد عدوهم.','3','1060',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (883,'حدثنا المسيب بن واضح قال: حدثنا أبو إسحاق الفزاري، عن الأعمش قال: كان عبد الرحمن بن يزيد، وأبو حذيفة، وإبراهيم النخعي، وعمار بن عمير يغزون في أمرة الحجاج، قلت: أين كانوا يغزون؟ قال: خراسان، والديلم، وغير ذلك. فقال رجل من القوم: أكانوا يكرهون على ذلك؟ قال: لا، بل يخفون فيه ويعجبهم.
قال: حدثنا أبو إسحاق، قال: سألتُ هشام عن الغزو مع هؤلاء الأئمة وذكرت له ما طعن في الغزو ومعهم، فقال: كان الحسن وابن سيرين يقولان: لك أجره، وذخره، وشرفه، وفضيلته، وعليهم ما أثمهم.','3','1061',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (884,'قال: وكان الحسن يقول: بلغني أن النبي عليه السلام كان يقول: ((ليؤيدن الله هذا الدين بأقوام لا خلاق لهم)).
وكان الحسن يقول: أربع من أمر الإسلام إلى السلطان: الحكم، والفيء، والجهاد، والجمعة.
قلت لهشام: وإن بروا أو فجروا؟ قال: وإن بروا أو فجروا.
حدثنا المسيب قال: حدثنا أبو إسحاق، عن موسى بن عتبة قال: غزوت مع سالم بن عبد الله الروم مع إمرة الوليد بن عبد الملك.
حدثنا المسيب قال: ثنا أبو إسحاق، عن الربيع بن صبيح، عن قيس بن سعد قال: قيل لابن عمر: ما ترى في الغزو، فإن هؤلاء قد أحدثوا ما قد','3','1062',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (885,'رأيت؟ قال: أغز معهم، وليس عليك من إحداثهم شيء. حدثنا محمد بن بشار قال: ثنا عبد الرحمن قال: ثنا عبد العزيز بن مسلم، عن أبي سنان، عن عبد الله بن أبي الهذيل قال: تذاكرنا ليالي المختار الجمعة، فاجتمع رأيهم على أن يأتوه، فإنما كذبه على نفسه.
حدثنا محمد بن بشار قال: حدثنا سفيان، عن الأوزاعي، عن عمير بن هانئ، قال: كنت أسمع','3','1063',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (886,'ابن عمر يقول لعبد الملك/١٩٤/ بن مروان، ولابن الزبير، ولنجدة: ذباب النار، ثم تقام الصلاة فيصلي مع هؤلاء ومع هؤلاء.
حدثنا محمد قال: ثنا عبد الرحمن قال: ثنا سفيان، عن بسام الصيرفي، عن أبي جعفر قال: كان الحسن والحسين يسبان مروان، ثم تقام الصلاة، فيبتدران الصلاة خلفه.','3','1064',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (887,'حدثنا نصر بن علي قال: حدثنا عمر بن أبي خليفة قال: سألتُ عبد الكريم أبا أمية عن الصلاة خلف بني أمية، فقال: كان ابن عمر يجئ في الليلة الملمة فيصلي خلف الحجاج.
حدثنا إسماعيل بن عبد الحميد العجلي قال: حدثنا لماده بن المغيرة، عن عبد الرحيم بن زيد بن الحواري، عن عمه قال: قال رسول الله - صلى الله عليه وسلم -: ((سبع من الهدي وفيهن الجماعة، من خرج من شيء منهن خرج من الجماعة، ثم قال: لا تشهدوا على أهل ملتكم بكفر، ولا شرك، ولا نفاق،','3','1065',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (888,'وذروا سرائرهم إلى الله، وصلوا على من صلى القبلة إذا مات، وصلوا الصلوات الخمس والجماعات خلف كل إمام بر أو فاجر، وجاهدوا مع كل خليفة لكم جهادكم وعليهم إثمه، وادعوا لهم بالصلاح والعافية ولا تدعوا عليهم، ولا تخرجوا على الأئمة بالسيف وإن جاروا وجانبوا الأهواء كلها، فإن أولها وآخرها باطل)).
حدثنا أحمد بن يونس قال: حدثنا منذر، عن حماد بن عبد الرحمن قال: ثنا محمد بن عبد الله الشعباني قال: سمعت مكحولًا يقول في مرضه الذي مات فيه: أربع لم أحدثكموهن عن رسول الله - صلى الله عليه وسلم -، فأنا محدثكموهن: ((لا تكفروا أهل ملتكم وإن عملوا الكبائر، والصلاة على كل ميت، والصلاة خلف كل إمام، والجهاد مع كل أمير)).
قال مكحول: ثنتان من رأيي لم','3','1066',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (889,'أذكرهما عن النبي - صلى الله عليه وسلم -: لا تقولوا في علي وعثمان إلا خيرًا {تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَهَا مَا كَسَبَتْ وَلَكُم مَّا كَسَبْتُمْ وَلاَ تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ} [البقرة: ١٣٤].
حدثنا أبو معن، قال: حدثنا الضحاك، قال: حدثني زمعة بن صالح، قال: حدثني سلمة بن وهرام قال: قلت لطاوس: لا أودي عشور أرضي وأقسمها؟ فقال: ألولم تعشر أرضك؟ قلت: بلى، أما هؤلاء الأمراء فقد أخذوها ولكن يضعونها في غير حقها فلا أدري يقضى ذلك عني أم لا؟ قال: وما يدريك؟ بل يقضى عنك، إياك والبدع، وقم للقرد في زمانه.
حدثنا هارون بن موسى قال: حدثنا حماد بن زيد، عن كلثوم بن جبر، عن قزعة أن ابن عمر سئل عن الزكاة فقال: ادفعوها إليهم/١٩٥/','3','1067',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (890,'وإن تمزقوا بها لحوم الكلاب على موائدهم.
حدثنا هشام بن عمّار قال: ثنا أبو سعيد الأنصاري، عن أبيه، عن خالد بن معدان، عن عبد الرحمن بن عمرو السلمي، عن العرباض بن سارية قال: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((أوصيكم بتقوى الله والسمع والطاعة، وإن عبد احبشيًا، فإنه من يبق بعدي فسيرى اختلافًا كثيرًا، فعليكم بسنتي وسنّة الخلفاء الراشدين المهديين، وعضوا عليها بالنواجذ، وإياكم ومحدثات الأمور فإن كل بدعة ضلالة)).
حدثنا أبو معن قال: حدثنا وهب بن جرير قال: ثنا أبي قال: سمعت','3','1068',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (891,'عبد الله بن جرير يحدث عن أبي قلس بن رباح، عن أبي هريرة، عن النبي - صلى الله عليه وسلم - قال: ((من خرج من الطاعة وفارق الجماعة فمات ميتة جاهلية)).
حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: حدثنا شعبة، عن قتادة قال: سمعت أبا مراية قال: سمعت عمران بن حصين يحدث عن النبي - صلى الله عليه وسلم - أنه قال: ((لا طاعة لأحد في معصية الله)).','3','1069',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (892,'حدثنا عبد الوهاب بن الضحاك قال: حدثنا إسماعيل بن عياش، عن عبد العزيز بن عبيد الله، عن شهر بن حوشب، عن عبد الله بن عمرو بن العاص، عن رسول الله - صلى الله عليه وسلم - أنه قال: ((كائن بعدي أمراء يعرفونكم ما تنكرون، وينكرون عليكم ما تعرفون، فلا طاعة لهم عليكم فلا تقبلن بالله)) يعني: إن أمروكم بالمعصية فلا تطيعوهم.','3','1070',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (893,'حدثنا بشر بن هلال قال: ثنا جعفر بن سليمان، عن هشام، عن ابن سيرين قال: قال شريح: كانت الفتنة سبع سنين ما خبرت فيها ولا استخبرت، وما سلمت. قيل: وكيف ذاك يا أبا أمية؟ قال: ما التقت فئتان إلا وهواي مع إحداهما.
حدثنا عمرو بن عثمان، قال: حدثنا أبي، قال: حدثنا شهاب بن حراش، عن القاسم بن غزوان، عن إسحاق بن راشد الجزري، عن سالم، قال: حدثني عمرو بن وابصة الأسدي، عن أبيه وابصة، قال: حدثني ابن مسعود عن رسول الله','3','1071',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (894,'- صلى الله عليه وسلم - قال: سمعته يقول: ((إن فتنة مظلمة مضلة جائية، القاعد فيها خير من القائم، والقائم فيها خير من الماشي، والماشي فيها خير من الراكب، والراكب فيها خي رمن المجري، قتلاها/١٩٦/كلهم في النار))، قلت: ومتى ذاك يا ابن مسعود؟ قال: تلك أيام الهرج حيث لا يأمن الرجل جليسه. قلت: فما تأمرني إن أدركني ذلك الزمان؟ قال: تكف لسانك ويدك وتكن حلسًا من حلانس بيتك.
حدثنا سعيد بن سليمان قال: ثنا حماد بن سلمة، عن ليث، عن طاوس، عن زياد سيمين حوش (١)، عن عبد الله بن عمرو أن رسول الله - صلى الله عليه وسلم - قال:
_________
(١) كذا في الأصل، وفي ((مصنف ابن أبي شيبة)) و ((سنن ابن ماجة)) و ((سنن الترمذي)): سيمين كوشى.','3','1072',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (895,'((تكون فتنة تستنظف العرب، قتلاها في النار - قالها ثلاثًا- اللسان فيها أشد من وقع السيف)).
حدثنا عبد الله بن حنبق الأنطاكي قال: سمعت يوسف بن أسباط يقول: أماأهل السنّة فإنهم لا يرون السيف على أحد من أهل القبلة، وهم يرون الصلاة والجمعة خلف الأئمة، والجهاد معهم قائم تام إلى يوم القيامة، ولا ينقصه جورهم، ولا يزيده عدلهم، ولا يكفرون أحدًا من أهل القبلة بذنب، ولا يشهدون عليه بشرك، وهم يقولون: الإيمان قول وعمل، والإيمان يزيد وينقص. وهم يستثنون في إيمانهم مخافة أن يزكوا أنفسهم.
حدثنا أبو بكر محمد بن يزيد قال: حدثنا عبد الأعلى بن سليمان الزراد','3','1073',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (896,'قال: حدثنا غالب القطان قال: لقيني الأشياخ من عبد القيس فقالوا لي: ما شهادتك على مالك بن المنذر، وعلى يزيد بن المهلب، وعلى الحجاج بن يوسف إن لم نشهد عليهم أنهم منافقون براء من الإيمان، من أهل النار، فإنك شكاك في كتاب الله. فأتيت الحسن فأخبرته بمقالة الأشياخ، فقال الحسن: ابن أخي رويدك بالشهادة تجرئك المعرفة أنك من أهل دين لا يحل لأحد أن يشهد عليك أنك من أهل النار. فأتيت محمد بن سيرين فأخبرته بمقالة الأشياخ فقال لي: أما مالك بن المنذر فأقرب ما كان منك جوارًا، وأعظمه عليك حقًّا تشهد عليه؟! لا آمرك بالشهادة عليه، وأما يزيد بن المهلب فتعرف ركب الأزد، فإن شئت فتعرض له، وأما الحجاج بن يوسف، والمسكين الحجاج، المسكين','3','1074',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (897,'أبو محمد انتهك الحرمة، وركب المعصية، فإن يعذبه فبذنبه، وإن يغفر له فإنا لا نَنفِسُ عليه المغفرة. قال فأتيت بكر بن عبد الله المزني فأخبرته بمقالة الأشياخ قال: لو أن الناس اجتمعوا يوم الجمعة فقالوا لي: أتعرف أفضل هؤلاء رجلًا واحدًا؟ لقلت: أتعرفون أنصحهم لهم؟ فلو قيل له: إنه هذا. فعرفت أنه كذلك لقلت: هذا أفضلهم ولو قيل: /١٩٧/أتعرف أشرهم رجلًا واحدًا؟ لقلت: أتعرفون أغشهم لهم؟ فلو قيل له: هذا، فعرفت أنه كذلك لقلت: هذا أشرهم، ولو قيل لي: اشهد لأفضلهم أنه من أهل الجنة لم أشهد، ولو قيل لي: اشهد على أشرهم أنه من أهل النار لم أشهد، فإذا كان رجائي لشرهم فكيف رجائي لخيرهم؟ وإذا خشيتي على خيرهم فكيف خشيتي على شرهم؟
حدثنا أبو معن، قال: ثنا أبو داود، قال: ثنا القاسم بن الفضل، قال: حدثنا عبد الكريم بن المعلم، عن طاوس قال: كنت عند ابن عمر، فأتاه رجل فقال: يا أبا عبد الرحمن قوم يحكمون بالهوى، ويقتلون في المعصية، ويستأثرون بالفيء أكفارٌ هم؟ قال: لا. قال: قوم يشهدون علينا بالكفر ويسفكون دماءنا تقربًا إلى الله أكفار هم؟ قال: لا. قال: فما الكفر؟ قال: أن يجعل مع الله إلهين مثنى.','3','1075',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (898,'<span data-type="title" id=toc-239>باب: الصلاة خلف الجهمي والرافضي</span>
حدثنا إبراهيم بن عبد الله الأنصاري، عن أبي عبيد قال: ما أبالي صليت خلف الجهمي والرافضي أم صليت خلف اليهودي والنصراني، ولا يصلى خلف من لا يقدم أبا بكر على الخلق أجمعين بعد رسول الله - صلى الله عليه وسلم -، فأما الصلاة خلف القدري، والخارجي والمرجئ فلا أحبها، ولا أراها.
سمعت أحمد بن يونس قال: سمعت زائدة يقول: لو كان رافضيًّا ما صليت وراءه. حدثنا أحمد بن يونس قال: سمعت رجلًا يقول لسفيان الثوري: الرجل يكذب بالقدر أصلي وراءه؟ قال: لا تقدموه.
حدثنا محمد بن الوزير قال: حدثنا مروان قال: سألتُ مالكًا هل يصلى خلف القدري؟ قال: لا.','3','1076',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (899,'حدثنا عبيد الله بن يوسف قال: حدثني فطر بن حماد قال: سألتُ معتمر بن سليمان فقلت: إمام لقوم يقول: القرآن مخلوق، أصلي خلفه؟ قال: أصلي خلف مسلم أحب إليّ. قال فطر: فأتيت يزيد بن زريع فقلت: إمام لقوم يقول: القرآن مخلوق، أصلي خلفه؟ قال: لا، ولا كرامة.
حدثنا يحيى بن عثمان قال: حدثنا ابن حمير قال: حدثني بشر بن جبلة، عن أبي المقوم، عن عبد الله بن عمرو قال: إن لكل أمة مجوسًا، وإن مجوس هذه الأمة أهل القدر، فإن مرضوا فلا تعودوهم، وإن ماتوا فلا تصلوا عليهم، ولا تسلموا/١٩٨/عليهم.','3','1077',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (900,'حدثنا أبو معن قال: حدثنا معاذ قال: ثنا سليمان التيمي، عن مكحول، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((إن لكل أمة مجوس، وإن مجوس هذه الأمة القدرية، فإن مرضوا فلا تعودهم، وإن ماتوا فلا تشهدوهم)).','3','1078',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (901,'<span data-type="title" id=toc-240>باب: في الدجال</span>
حدثنا عبيد الله بن معاذ، عن أبيه، عن سليمان بن المغيرة قال: حدثني حميد بن هلال قال: قال هشام بن عامر: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((ما بين خلق آدم إلى أن تقوم الساعة فتنة أكبر من الدجال)).
حدثنا عبيد الله، قال: حدثنا أبي قال: حدثنا شعبة، عن حبيب بن الزبير، عن عبد الله بن أبي الهذيل، عن عبد الرحمن بن أبزى، عن عبد الله بن خباب، عن أُبي بن كعب، عن النبي - صلى الله عليه وسلم - قال: ((الدجال عينه خضراء كأنها زجاجة))','3','1079',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (902,'وتعوذ بالله من عذاب القبر.
حدثنا مسدد قال: حدثنا يحيى بن سعيد، عن هشام بن حسان، عن حميد بن هلال، عن أبي الدهماء، عن عمران بن حصين قال: قال رسول الله - صلى الله عليه وسلم -: ((من سمع بالدجال فلينأ منه - مرتين أو ثلاثًا - فإن الرجل يأتيه فيحسب أنه','3','1080',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (903,'مؤمن، فيرى ما معه من الشبهات فيتبعه)). حدثنا محمد بن أبي بكر، قال: حدثنا يحيى بن سعيد، عن عيينة بن عبد الرحمن، عن أبي بكرة أن النبي - صلى الله عليه وسلم - قال: ((الدجال مكتوب بين عينيه كافر يقرأه الأمي والكاتب)).
حدثنا هشام بن عمار، قال: حثنا يحيى بن حمزة، قال: حدثنا الأوزاعي، عن إسحاق بن عبد الله بن أبي طلحة، عن أنس بن مالك أن رسول الله - صلى الله عليه وسلم - قال:','3','1081',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (904,'((يتبع الدجال من يهود أصبهان سبعون ألفًا عليهم الطيالسة)).
حدثنا عبيد الله بن محمد بن حفص، قال: حدثنا عبد العزيز بن مسلم، عن الأعمش، عن عطية، عن أبي سعيد الخدري قال: قال رسول الله - صلى الله عليه وسلم -: ((حُدثنا عن الدجال أنه سيسلط على نفس فيقتلها ثم يحييها. - قال: - فيقول: ألست بربك؟ فيقول: كذبت ما كنت قط أكذب منك الساعة)) قال: فما كنا نراه إلا عن عمر بن الخطاب.','3','1082',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (905,'<span data-type="title" id=toc-241>باب: في فتنة القبر</span>
حدثنا أبو خالد يزيد بن مهران الخباز، قال: حدثنا أبو بكر بن عياش، عن الأعمش، عن أبي سفيان، عن جابر قال: قال لي رسول الله - صلى الله عليه وسلم -: ((إذا أدخل العبد قبره/١٩٩/أتاه ملكان فينتهرانه فيقوم يهب كما يهب النائم، فيقولان له: من ربك؟ فيجيب، فيقال: صدقت كذلك كنت، افترشوه من الجنة وألبسوه منها. فيقول: دعوني أخبر أهلي. فيقال له: اسكن)).
حدثنا يحيى الحماني، قال: حدثنا حماد بن سعيد، قال: أخبرني يونس بن حبان، قال: أخبرني من سمع ميتًا يسئل في القبر، فقيل له: من ربك','3','1083',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (906,'وما دينك؟
حدثنا أحمد بن يونس، قال: حدثنا ليث بن سعد، عن نافع، عن عبد الله، عن رسول الله - صلى الله عليه وسلم - أنه قال: ((ألا إن أحدكم إذا مات عرض عليه مقعده بالغداة والعشي، إن كان من الجنة فمن الجنة، وإن كان من النار فمن النار، حتى يبعثه الله يوم القيامة)).','3','1084',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (907,'<span data-type="title" id=toc-242>باب: في الحوض</span>
حدثنا محمود بن خالد قال: ثنا مروان بن محمد، قال: ثنا محمد بن مهاجر قال: حدثني العباس بن سالم، عن أبي سلام الخشني قال: ثنا ثوبان مولى رسول الله - صلى الله عليه وسلم - أن رسول الله - صلى الله عليه وسلم - قال: ((إن حوضي ما بين عدن إلى إيلة أشد بياضًا من اللبن، وأحلى من العسل، أكاويبه كعدد نجوم السماء، من شرب منه شربة لم يظمأ بعدها أبدًا)).','3','1085',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (908,'حدثنا يحيى بن عثمان قال: ثنا أبو المغيرة قال: ثنا (الأحموسي) (١) عمر بن عمرو قال: حدثنا المخارق بن أبي المخارق، عن عبد الله بن عمر أنه سمعه يقول: إن النبي - صلى الله عليه وسلم - قال: ((إن حوضي كما بين عدن وعمان أبرد من الثلج، وأحلى من العسل، وأطيب ريحًا من المسك، أكوابه مثل نجوم السماء، من شرب منه شربة لم يظمأ بعدها أبدًا)).
_________
(١) كذا بالأصل وفي ((التاريخ الكبير)) ٧/ ٤٣٠ الأعموش أو الأحموسي في ترجمة مخارق.','3','1086',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (909,'<span data-type="title" id=toc-243>باب: في الصراط</span>
حدثنا محمد بن الوزير الدمشقي، قال: ثنا الوليد بن مسلم، قال: ثنا ابن جابر، قال: حدثني أبو سعيد جليس لأبي هريرة قال: سمعت أباهريرة يقول: قال رسول الله - صلى الله عليه وسلم -: ((الصراط بين ظهري جهنم، دحض مزلة، والأنبياء والملائكة - عليهم السلام-، عليه/٢٠٠/يقولون: اللهم سلم سلم، والناس يمرون كلمح البرق، وكطرف العين، وكجياد الخيل والركاب والبغال وشد الأقدام، فناج مسلم، ومخدوش مرسل، ومطروح فيها، ولها سبعة أبواب لكل باب منهم جزء مقسوم)).
حدثنا بشر بن معاذ قال: حدثنا عبد الواحد بن زياد قال: ثنا عبد الرحمن بن إسحاق عن النعمان بن سعد قال: سمعت المغيرة بن شعبة يقول','3','1087',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (910,'على المنبر: عن النبي عليه السلام قال: ((شعار المسلمين يوم القيامة على الصراط: اللهم سلم سلم)).','3','1088',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (911,'<span data-type="title" id=toc-244>باب: في الميزان</span>
حدثنا زيد بن يزيد، قال: ثنا مؤمل بن إسماعيل، قال: ثنا عباد، قال: ثنا أبو الزبير، عن جابر بن عبد الله قال: قال رسول الله - صلى الله عليه وسلم -: ((يوضع الميزان يوم القيامة، فتوزن الحسنات والسيئات، فمن رجحت حسناته على سيئاته خردلة دخل الجنة، ومن رجحت سيئاته مثقال خردلة دخل النار))، فقال رجل: يا رسول الله، فمن استوت حسناته وسيئاته؟ قال: ((أولئك أصحاب الأعراف لم يدخلوها وهم يطعمون)).
حدثنا هشام بن عمار قال: ثنا سعيد بن يحيى، عن عبد الملك بن أبي سليمان، عن عمرو، عن الحسن، أنه سئل عن الميزان؟ فقال: نعم له لسان وكفتان.','3','1089',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (912,'<span data-type="title" id=toc-245>باب: في الصور</span>
حدثنا أبو الربيع الزهراني قال: حدثنا يحيى بن سعيد بن أبان بن سعيد بن العاص قال: حدثنا الأعمش، عن عطية العوفي، عن أبي سعيد الخدري قال: قال رسول الله - صلى الله عليه وسلم -: ((جبريل عن يمينه وميكائيل عن شماله، وميكائيل صاحب الصور)).
حدثنا محمد بن أبي بكر قال: ثنا بشر بن المفضل، عن عوف، عن أبي المنهال، عن أبي الوليد، عن ميمون الكندي قال: إن صاحب الصور قد دفع إليه','3','1090',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (913,'الصور، وقد قدم إحدى رجليه وأخر الأخرى، مستعد متى يؤمر فينفخ فيه.','3','1091',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (914,'<span data-type="title" id=toc-246>باب: في القلم</span>
حدثنا هشام بن عمار الدمشقي قال: حدثني الجراح بن مليح قال: ثنا أرطأة بن المنذر، عن جعفر بن أبي إياس، عن مجاهد، عن ابن عباس قال: أول ما خلق الله القلم، فأخذه بيمينه وكلتا يديه يمين، ثم خلق النون وهي الدواة، ثم خلق الألواح فكتب فيها الدنيا وما يكون فيها حتى تفنى من خلق مخلوق، أو عمل معمول بر أو فجور، أو رزق من حلال أو حرام، أو أثر أو رطب، أو يابس، ثم ألزم كل شيء من ذلك شأنه وبقاءه فيها وفناءه حتى يفنى، ثم جعل على ذلك الكتاب حفظة من الملائكة، وعلى الخلق حفظة، فتأتي ملائكة الخلق ملائكة ذلك الكتاب، فيلقون إليهم النسخ بما يكون في كل يوم وليلة من ذلك، فتهبط ملائكة الخلق إلى الخلق فيحفظونهم بأمر الله، ويسوقونهم إلى ما في أيديهم من تلك النسخ حتى إذا استكمل كل شيء من ذلك شأنه في كل يوم وليلة','3','1092',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (915,'انقطع، فلم يكن لها مقام ولا بقاء ثم تلا: {إِنَّا كُنَّا نَسْتَنسِخُ مَا كُنتُمْ تَعْمَلُونَ} فقال رجل: يا أبا عباس، ما كنا نرى النسخ إلا فيما تحفظ علينا الملائكة في كل يوم وليلة، قال: ألستم قومًا عربًا يكون نسخه إلا من كتاب قد سبق، ثم قرأ: {وَلاَ رَطْبٍ وَلاَ يَابِسٍ إِلاَّ فِي كِتَابٍ مُّبِينٍ} [الأنعام: ٥٩] فقال: جميع الرطب اليابس كل شيء.
حدثنا أبو معن الرقاشي، قال: حدثنا معتمر بن سليمان، قال: سمعت عصمة بن عاصم يحدث عن عطاء بن السائب، عن مقسم، عن ابن عباس قال:','3','1093',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (916,'أول ما خلق الله القلم من هجاء ق ل م قال: فتصور قلم من نور طوله كما بين السماء والأرض، فقال: اجري في اللوح المحفوظ قال: رب بماذا؟ قال: كل شيء يكون إلى قيام الساعة، فلما خلق الله الخلق، ووكل ملائكة يحفظون أعمالهم، فإذا كان يوم القيامة عرضت أعمالهم عليهم، قيل: هذا كتابنا ينطق عليكم بالحق إنا كنا نستنسخ ما كنتم تعملون من اللوح المحفوظ، فعرض بين الكتابين فإذا هما سواء.
حدثنا سعيد بن منصور قال: ثنا هشيم قال: أخبرنا منصور بن زاذان، عن الحكم بن عيينة، عن أبي ظبيان، عن ابن عباس قال: أول ما خلق الله القلم، فأمره أن يكتب ما هو كائن، فكتب فيما كتب: {تَبَّتْ يَدَا أَبِي لَهَبٍ وَتَبَّ} [المسد: ١].','3','1094',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (917,'<span data-type="title" id=toc-247>باب: في الشفاعة</span>
حدثنا محمد بن الوزير، قال: ثنا الوليد بن مسلم، قال: حدثنا ابن جابر أنه سمع سليم بن عامر الكلاعي يحدث عن عوف بن مالك الأشجعي أنه سمعه يقول: سمعت رسول الله - صلى الله عليه وسلم - ذكر ما أعطاه الله من الشفاعة، فقلت: نشدتك يا رسول الله والصحابة لما سألتُ الله أن يجعلني من أهلها. قال: ((يا عوف إن شفاعتي يوم القيامة لكل مسلم)).','3','1095',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (918,'<span data-type="title" id=toc-248>باب: في الموت</span>
حدثنا سعيد بن منصور، قال: حدثنا أبو معاوية، عن الأعمش، عن أبي صالح، عن أبي سعيد الخدري قال: قال رسول الله - صلى الله عليه وسلم -: ((يجاء بالموت يوم القيامة كأنه كبش أملح، فيوقف بين الجنة والنار، فيقال: يا أهل الجنة أتعرفون هذا؟ فيشرئبون وينظرون، فيقولون: نعم، ويقال: يا أهل النار أتعرفون هذا؟ فيشرئبون وينظرون، فيقول: هذا الموت. فيؤمر به فيذبح، ثم يقال: يا أهل الجنة خلود فلا موت، ويا أهل النار خلود فلا موت))، ثم قرأ رسول الله - صلى الله عليه وسلم -: {وَأَنذِرْهُمْ يَوْمَ الْحَسْرَةِ إِذْ قُضِيَ الْأَمْرُ وَهُمْ فِي غَفْلَةٍ} [مريم: ٣٩].
حدثنا محمد بن عبد الأعلى/٢٠٢/، قال: حدثنا معتمر بن سليمان، قال: سمعت محمد بن عمرو، عن أبي هريرة أن نبي الله - صلى الله عليه وسلم - قال:','3','1096',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (919,'((يؤتى بالموت يوم القيامة فيوقف على الصراط، فيقال: يا أهل الجنة، فيطلعون خائفين وجلين أن يخرجوا من المكان الذي هم به، ثم يقال: يا أهل النار، فيطلعون فرحين مستبشرين أن يخرجوا من المكان الذي هم فيه، فيقال: هل تعرفون هذا؟ فيقولون: نعم ربنا هذا الموت. فيؤمر به فيذبح على الصراط، ثم يقال للفريقين كلاهما: خلود فيما تجدون لا موت فيه أبدًا)).','3','1097',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (920,'<span data-type="title" id=toc-249>باب: في الجنة والحور العين</span>
حدثنا المسيب بن واضح قال: حدثنا الحكم بن محمد، عن عطاء - قال حرب: هو الحكمي - عن ليث، عن مجاهد قال: الحور العين خلقن من الزعفران. قال المسيب: فقلت للحكم: فيأكلن ويشربن اليوم؟ قال: لا ليس يأكلن ولا يشربن حتى يأتي اليوم الذي يأكلن فيه ويشربن وينعمن مع أهل الجنة، قال: ولا يموتون، ولا يصعقون يوم القيامة، ولا يموت شيء في الجنة، ولا يموت شيء مما خلق الله فيها، إنما يموت ما خُلِق في هذه الدار الفانية، وأما تلك الداران الجنة والنار، فإنه يزيد كل شيء فيهما ولا ينقص منهما شيء. حدثنا إسحاق بن إبراهيم قال: حدثنا جرير قال: حدثنا العلاء بن المسيب عن الفضيل بن عمرو، عن عائشة بنت طلحة، عن عائشة قالت: قال رسول','3','1098',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (921,'الله - صلى الله عليه وسلم -: ((إن الله خلق الجنة وخلق لها أهلًا، وخلق النار وخلق لها أهلًا)).
حدثنا أبو الربيع الزهراني، قال: ثنا يعقوب، قال: أخبرنا حفص بن حميد، عن شمر بن عطية قال: خلق الله جنة الفردوس بيده، فهو يفتحها في كل خميس يقول: إزدادي طيبًا لأوليائي.
حدثنا أبو معن، قال: ثنا مؤمل، قال: حدثنا سفيان في قوله: {كُلُّ شَيْءٍ هَالِكٌ إِلَّا وَجْهَهُ} [القصص: ٨٨] قال: ما أريد به وجهه.','3','1099',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (922,'حدثنا عبيد الله بن معاذ قال: حدثنا أبي قال: حدثنا محمد بن عمرو، عن أبي سلمة، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((لما خلق الله الجنة دعا جبريل فقال: اذهب إلى الجنة فانظر إليها وإلى ماأعددت لأهلها فيها، فجاء فنظر إليها وإلى ما أعد الله لأهلها فيها، فرجع إليه فقال: وعزتك لا يسمع بها أحد إلا دخلها، فأمر بها فحجبت بالمكاره، ثم قال: ارجع إليها فانظر إليها وإلى ما أعددت لأهلها فيها، فرجع إليها فإذا هي قد حجبت/٢٠٣/بالمكاره، فرجع إليه فقال: وعزتك لقد خشيت أن لا يدخلها أحد. قال: اذهب إلى النار فانظر إليها وإلى ما أعددت لأهلها فيها، فإذا هي تركب بعضها بعضًا، فرجع إليه فقال: وعزتك لا يسمع بها أحد فيدخلها. فأمر بها فحفت بالشهوات، ثم قال: ارجع إليها فانظر إليها وإلى ما أعددت لأهلها فيها، فرجع إليها فإذا هي قد حفت بالشهوات، فرجع إليه فقال: وعزتك لقد خفت أن لا ينجو منها أحد إلا دخلها)).','3','1100',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (923,'<span data-type="title" id=toc-250>باب: في كلام الله</span>
حدثنا سعيد بن منصور قال: حدثنا أبو معاوية، عن الأعمش، عن مسلم، عن مسروق قال: قال عبد الله: إذا تكلم الله بالوحي سمع أهل السموات للسموات صلصلة كمر السلسلة على الصفا فيصعقون، فلا يزالون كذلك حتى يأتيهم جبريل، فإذا أتاهم جبريل فزع عن قلبهم، فيقولن: يا جبريل ماذا قال ربنا؟ فيقول: الحق. فينادون: الحق الحق. حدثنا يحيى بن حبيب بن عدي قال: ثنا أبو محمد يحيى بن حبيب بن عدي - بصري ثقة - قال: ثنا موسى عن إبراهيم قال: سمعت طلحة بن حراش يقول: سمعت جابر بن عبد الله يقول: لقيني رسول الله - صلى الله عليه وسلم -','3','1101',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (924,'فقال: ((يا جابر مالي أراك منكسرًا؟)) قال: يا رسول الله استشهد أبي وترك عيالًا ودينًا. قال: ((أفلا أبشرك بما لقي الله به أباك؟)) قال: بلى يا رسول الله. قال: ((ما كلم الله أحدًا قط إلا من وراء حجاب، وأحيا أباك فكلمه كفاحًا وقال: يا عبد ي تمن عليَّ أعطيك. قال: يا رب تحييني فأقتل فيك الثانية. قال الرب تبارك تعالى: إنه قد سبق مني أنهم إليها لا يرجعون))، قال: ((وأنزل الله تبارك وتعالى هذه الآية: {وَلاَ تَحْسَبَنَّ الَّذِينَ قُتِلُواْ فِي سَبِيلِ اللهِ أَمْوَاتًا بَلْ أَحْيَاء عِندَ رَبِّهِمْ يُرْزَقُونَ} [آل عمران: ١٦٩].
حدثنا سعيد بن منصور، قال: ثنا أبو معاوية، عن الأعمش، عن عبد الله بن','3','1102',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (925,'مرة، عن مسروق قال: سئل عبد الله عن قوله: {وَلاَ تَحْسَبَنَّ الَّذِينَ قُتِلُواْ فِي سَبِيلِ اللهِ أَمْوَاتًا بَلْ أَحْيَاء عِندَ رَبِّهِمْ يُرْزَقُونَ} [آل عمران: ١٦٩] قال: أما إنا قد سألنا عن ذلك أرواحهم كطير خضر يسرح في الجنة في أيها شاءت، ثم تأوي إلى قناديل معلقة بالعرش فبيناهم كذلك إذا طلع عليهم ربك اطِّلاعة فقال: سلوني ما شئتم، قالوا: يا ربنا وماذا نسألك ونحن في الجنة نسرح في أيها شئنا. فبيناهم كذلك إذ اطلع عليهم ربك اطلاعة فقال: سلوني ما شئتم. قالوا: يا ربنا لماذا نسألك ونحن في الجنة نسرح في أيها شئنا، فلما رأوا أنهم لن يتركوا من أن يسألوا قالوا: نسألك أن ترد أرواحنا في أجسادنا حتى نقتل في سبيلك مرة أخرى، فلما رأى أنهم لا يسألون إلا هذا تركوا.
حدثنا محمد بن الوزير، قال: ثنا مروان بن محمد قال: ثنا ابن لهيعة، عن يزيد بن أبي حبيب، عن أبي الخير، عن عقبة بن عامر قال: سمعت النبي عليه','3','1103',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (926,'السلام يقرأ: {سميعًا بصيرا} قال: فوضع النبي عليه السلام إصبعيه على عينيه. حدثنا إسحاق بن إبراهيم، أخبرنا جرير، عن الأعمش، عن تميم بن سلمة، عن عروة، عن عائشة أنها قالت: الحمد لله الذي وسع سمعه الأصوات، لقد جاءت خولة إلى رسول الله - صلى الله عليه وسلم - تشكو زوجها فكان يخفى عليّ كلامها، فأنزل الله: {قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجَادِلُكَ فِي زَوْجِهَا وَتَشْتَكِي','3','1104',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (927,'إِلَى اللَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَا إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ} [المجادلة: ١].
حدثنا أحمد بن حنبل، قال: ثنا إسماعيل بن عبد الكريم، قال: حدثني عبد الصمد بن معقل، قال: سمعت وهب بن منبه يقول: قال الله لموسى: أدنيتك وقربتك حتى سمعت كلامي. وكنت بأقرب الأمكنة مني، فانطلق برسالتي، فإنك بعيني وسمعي، وإن معك أيدي وبصري.
حدثنا عبد الله بن الزبير الحميدي قال: حدثنا سفيان قال: ثنا الزهري، عن سعيد بن المسيب، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -:','3','1105',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (928,'((يؤذيني ابن آدم يسب الدهر، وأنا الدهر، بيدي الأمر أقلب الليل والنهار)).
حدثنا سعيد بن منصور قال: ثنا أبو معاوية، عن الأعمشق، عن سعيد بن جبير، عن أبي عبد الرحمن السلمي، عن أبي موسى الأشعري قال: قال رسول الله - صلى الله عليه وسلم -: ((ما أحد أصبر على أذى يسمعه من الله هو يشرك به، ويجعل له ولدًا وهو يعافيهم ويرزقهم)).
حدثنا يحيى بن عثمان، قال: حدثنا محمد بن كثير، عن الأوزاعي، عن حسان بن عطية قال: ثمانية مقتهم الله وقللهم نفسه وميزهم من خلقه:','3','1106',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (929,'السقارون وهم العتالون، والمستكبرون والذين إذا دعوا إلى الله وأمره كانوا بطاءً، وإذا دعوا إلى الشيطان وأمره كانوا سراعًا، والذين يستحقون بأيمانهم ما لم يحققه الله لهم، والذين يكنزون البغضة لإخوانهم في صدورهم، فإذا لقوهم يحلفوا لهم، والمشاءون بالنميمة، والمفرقون بين الأحبة، والباغون دحضة البري.
حدثنا شاذ بن فياض/٢٠٥/، قال: ثنا عمر بن إبراهيم، عن قتادة، عن أنس بن مالك قال: قال رسول الله - صلى الله عليه وسلم -: ((الله أشد فرحًا بتوبة عبد هـ من أحدكم','3','1107',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (930,'حين يسقط على بعيره قد أضله بأرض فلاة)).
حدثنا سعيد بن منصور قال: حدثنا مغيرة بن عبد الرحمن وعبد الرحمن بن أبي الزناد، عن أبي الزناد، عن الأعرج، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((من أحب لقاء الله أحب الله لقاءه، ومن كره لقاء الله كره الله لقاءه)).
حدثنا سعيد بن منصور قال: ثنا عبد الرحمن بن أبي الزناد ومغيرة بن عبد الرحمن، عن أبي الزناد، عن الأعرج، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((يمين الله ملئ لا تغيضها نفقة، سحا الليل النهار - وقال- أرأيتم ما أنفق من خلق السموات والأرض فإنه لم يغض ما في يمينه،','3','1108',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (931,'وعرشه على الماء، وبيده الأخرى الميزان يخفض ويرفع)).
حدثنا سعيد قال: ثنا مغيرة بن عبد الرحمن، عن أبي الزناد، عن الأعرج، عن أبي هريرة، عن النبي عليه السلام قال: ((لما خلق الله الخلق كتب في كتابه وهو عنده فوق العرش: إن رحمتي غلبت غضبي)).
حدثنا عيسى بن محمد قال: ثنا محاضر بن المورع، عن الأعمش، عن عمرو بن مرة، عن أبي نصر، عن أبي ذر قال رسول الله - صلى الله عليه وسلم -: ((كثف السماء مسيرة خمسمائة عام، وما بين الأرض العليا والسماء الدنيا خمسمائة عام،','3','1109',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (932,'وكثفها خمسمائة عام، وكثف الثانية مثل ذلك، وما بين كل أرضين مثل ذلك، وكثف السماء مسيرة خمسمائة عام وبين السماء الدنيا والثانية خمسمائة عام، وكثف السماء مسيرة خمسمائة عام، ثم كل سماء مثل ذلك حتى تبلغ السابعة، ثم ما بين السماء السابعة والعرش مسيرة ما بين ذلك كله)).','3','1110',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (933,'<span data-type="title" id=toc-251>باب في قول الله عز وجل: {مَا يَكُونُ مِن نَّجْوَى ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ}</span>
سألتُ إسحاق بن إبراهيم قلت: قول الله تبارك وتعالى: {مِن نَّجْوَى ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ} كيف تقول فيه؟ قال: حيث ما كنت هو أقرب إليك من حبل الوريد، وهو بائن من خلقه.
قلت لإسحاق: العرش بحد؟ قال: نعم بحد، وذكر عن ابن المبارك قال: هو على عرشه بئن من خلقه بحد.
حدثنا سعيد بن نوح قال: ثنا أبي نوح بن مضروب، قال: ثنا بكير بن معروف، عن مقاتل بن حيان، عن الضحاك بن مزاحم في قول الله:','3','1111',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (934,'{مِن نَّجْوَى ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ} قال: هو تبارك وتعالى على العرش وعلمه معهم.
مسألة العلو
حدثنا أبو بكر محمد يزيد، قال: أخبرنا علي بن الحسن، قال: قلت لابن المبارك: يا أبا عبد الرحمن كيف نعرف ربنا؟ قال: هو على العرش فوق سبع سموات وعلمه وأمره في كل موضع. قال: قلت: بحد؟ قال: بحد، ولا نقول كما تقول الجهيمة إنه هاهنا، وهاهنا في الأرض.
حدثنا أحمد بن حنبل، قال: حدثنا شريح بن النعمان، قال: حدثنا عبد الله بن نافع، قال: قال مالك: الله تبارك وتعالى في السماء وعلمه في كل مكان، لا يخلو من علمه مكان.','3','1112',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (935,'حدثنا أبو بكر محمد بن يزيد، قال: سمعت عبيد الله بن موسى قال: سئل سفيان عن قوله: {وَهُوَ مَعَكُمْ أَيْنَ مَا كُنتُمْ} [الحديد: ٤] قال: علمه.

<span data-type="title" id=toc-252>باب: في العرش</span>
حدثنا أبو عمرو عثمان بن طالوت، قال: حدثنا معاذ بن هشام، عن أبيه، عن قتادة، عن كثير بن أبي كثير، عن أبي عياض، عن عبد الله بن عمرو قال: إن العرش لمطوف بحية، وإن الوحي لينزل بالسلاسل.','3','1113',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (936,'حدثنا هشام بن عمار الدمشقي قال: حدثنا عبد الله بن يزيد قال: ثنا شعيب بن أبي حمزة، عن عبد الأعلى بن أبي عمرة، عن عبادة بن نسي، عن عبد الرحمن بن غنم، عن معاذ بن جبل، عن النبي عليه السلام قال: ((المجرة التي في السماء عرق الأفعى التي تحت العرش)).','3','1114',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (937,'حدثنا عمرو بن عثمان قال: ثنا بقية بن الوليد، عن يحيى بن سعد، عن خالد بن معدان قال: قال أبو عائشة: إن نفرًا من اليهود أتوا رسول الله - صلى الله عليه وسلم - فقالوا: من يحمل العرش؟ قال: ((تحمله الهوام بقرونها، والمجرة التي في السماء من عرقهم)) قالوا: نشهد إنك رسول الله.
حدثنا إسحاق قال: أخبإنا جرير، عن عطاء بن السائب، عن ميسرة في','3','1115',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (938,'قول الله: {وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَانِيَةٌ} [الحاقة: ١٧] قال: أرجلهم في النجوم لا يستطيعون أن يرفعوا أبصارهم من شعاع النور.
حدثنا عبد الرحمن بن محمد قال: ثنا أبو أسامة، عن إسماعيل بن أبي خالد، قال: سمعت سعد الطائي يقول: العرش ياقوتة حمراء.
حدثنا عمرو بن عثمان، قال: ثنا أبو المغيرة، عن أم عبد الله بنت خالد بن معدان. قال أبو القاسم: اسمها عبد ة - عن أبيها أنه كان يقول: إن الرب سبحانه ليثقل على حملة العرش من أول النهار إذا قام المشركون، فإذا','3','1116',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (939,'قام المسبحون/٢٠٧/خفف عن حملة العرش.

<span data-type="title" id=toc-253>باب: في الاستواء</span>
أملى إسحاق أن الله تبارك وتعالى وصف نفسه في كتابه بصفات استغنى الخلق أن يصفوه بغير ما وصف به نفسه، من ذلك قوله: {يَأْتِيَهُمُ اللهُ فِي ظُلَلٍ مِّنَ الْغَمَامِ وَالْمَلآئِكَةُ} [البقرة: ٢١٠] وقوله: {وَتَرَى الْمَلَائِكَةَ حَافِّينَ مِنْ حَوْلِ الْعَرْشِ} وآيات مثلها تصف العرش، وقد ثبتت الروايات في العرش، وأعلى شيء فيه وأثبته قول الله: {الرَّحْمَنُ عَلَى الْعَرْشِ اسْتَوَى} [طه: ٥].
سمعت أبا جعفر أحمد بن سعيد - قال أبو محمد: هذا أحمد بن سعيد الدارمي خراساني، وليس هذا الرباطي، وقد كتبت عن الرباطي أيضًا - يقول: سمعت أبي يقول: سمعت خارجة بن مصعب يقول: الجهيمة كفار، لا تنكحوا إليهم، ولا تنكحوهم، ولا تعودوا مرضاهم، ولا تشهدوا جنائزهم، وبلغوا نسائهم أنهن طالق، أنهن لا يحللن لأزواجهن،','3','1117',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (940,'ثم قرأ: {طه * مَا أَنزَلْنَا عَلَيْكَ الْقُرْآنَ لِتَشْقَى} [طه: ١ - ٢] إلى قوله: {الرَّحْمَنُ عَلَى الْعَرْشِ اسْتَوَى} [طه: ٥] وهل يكون الاستواء إلا الجلوس.
حدثنا محمد بن مصفا الحمصي، عن وهب بن جرير، عن أبيه، عن محمد بن إسحاق، عن يعقوب بن عتبة، عن محمد بن جبير بن مطعم، عن أبيه قال: قال رسول الله - صلى الله عليه وسلم -: ((العرش على السموات مثل القبة، والله على العرش، وللعرش أطيط)).','3','1118',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (941,'وقال إسحاق في حديث أبي رزين العقيلي قوله: ((في عماء، ما فوقه هواء، وما تحته هواء)) معناه: أنه كان في عماء قبل أن يخلق السموات والأرضين، وتفسيره عند أهل العلم: أنه كان في عماء يعني: سحابة.
حدثنا عبيد الله بن معاذ، قال: ثنا أبي قال: ثنا حماد بن سلمة، عن يعلى بن عطاء عن وكيع بن حدس، عن أبي رزين العقيلي قال: قلت: يا رسول الله، أين كان ربنا قبل أن يخلق السموات والأرض؟ قال: ((كان في عماء ما فوقه هواء، وما تحته هواء، ثم خلق عرشه على الماء)).','3','1119',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (942,'<span data-type="title" id=toc-254>باب: في الحجب</span>
حدثنا إسحاق بن إبراهيم قال: أخبرنا روح بن عبادة قال: ثنا موسى بن عبيدة، قال: ثنا أبو حازم، عن سهل بن سعد الساعدي قال: قال رسول الله - صلى الله عليه وسلم -: ((إن دون الله سبعين ألف حجاب من نور، ما من نفس تسمع حس شيء من تلك الحجب إلا زهقت نفسه)).
حدثنا سعيد بن منصور قال: ثنا عبد العزيز بن حازم قال: حدثني أبي، عن عمر بن الحكم بن ثوبان، عن عبد الله بن عمرو قال: والذي نفسي','3','1120',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (943,'بيده إن بين الناس يوم القيامة/٢٠٨/وبين الرب تبارك وتعالى لسبعين ألف حجاب، منها حجب من ظلمة لا ينفذها شيء، ومنها حجب من نور لا يستطيعها شيء، ومنها حجب من ماء لا يسمع حس ذلك الماء أحد فلا يربط على قلبه إلا انخلعت أفئدته.
حدثنا محمد بن أبي بكر قال: حدثنا مؤمل قال: ثنا حماد، عن أبي عمران الجوني، عن زرارة بن أوفى أن النبي - صلى الله عليه وسلم - قال لجبريل: ((هل رأيت ربك؟)) فانتفض جبريل وانتفض، وقال: إن بيني وبينه سبعين حجابًا من نور، لو أدنوا إلى أدناها لاحترقت.

<span data-type="title" id=toc-255>باب: في النزول</span>
سألتُ إسحاق بن إبراهيم قلت: حديث النبي عليه السلام: ((ينزل الله كل ليلة إلى السماء الدنيا؟)) قال: نعم، ينزل الله كل ليلة إلى السماء','3','1121',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (944,'الدنيا كما شاء وكيف شاء، وليس فيه صفة.
وقال إسحاق: لا يجوز الخوض في أمر الله كما يجوز الخوض في فعل المخلوقين، يقول الله تبارك وتعالى: {لَا يُسْأَلُ عَمَّا يَفْعَلُ وَهُمْ يُسْأَلُونَ} [الأنبياء: ٢٣]، ولا يجوز لأحد أن يتوهم على الخالق بصفاته وفعاله بوهم ما يجوز التفكر والنظر في أمر المخلوقين، وذلك أنه يمكن أن يكون موصوفًا بالنزول كل ليلة إذا مضى ثلثها إلى السماء الدنيا كما شاء، لا يسأل كيف نزوله؛ لأنه الخالق يصنع ما شاء كما شاء.
حدثنا محمد بن إسماعيل قال: حدثنا إسماعيل بن علية، عن هشام الدستوائي، عن يحيى بن أبي كثير، عن هلال بن أبي ميمونة، عن عطاء بن يسار، عن رفاعة الجهني قال: قال رسول الله - صلى الله عليه وسلم -: ((إذا مضى نصف الليل - أو قال-: ثلثا الليل نزل الله إلى السماء الدنيا يقول: لا أسأل عن عبادي أحدًا غيري،','3','1122',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (945,'من ذا الذي يستغفرني اغفر له، من ذا الذي يدعوني استجب له، من ذا الذي يسألني أعطه حتى ينفجر الصبح)).
حدثنا أبو الوليد الطيالسي قال: ثنا حماد بن سلمة، عن علي بن زيد، عن الحسن عن عثمان بن أبي العاص، عن النبي - صلى الله عليه وسلم - أنه قال: ((ينزل الله إلى السماء الدنيا كل ليلة، فيقول: هل من داعي فاستجب له، هل من سائل فأعطيه، هل من مستغفر فأغفر له)).','3','1123',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (946,'مسائل حرب (٤)

<span data-type="title" id=toc-256>باب في القرآن</span>
سمعت أحمد بن حنبل وذكر عنده كلام الناس في القرآن أنه مخلوق فقال: كفر ظاهر مرتين.
سألتُ إسحاق قلت: أليس تقول: القرآن كلام الله تكلم به ليس بمخلوق، ومن قال: إنه مخلوق، فهو كافر.
وسألتُه عن الرجل يقول: القرآن/٢٠٩/كلام الله ويقف قال: هو عندي شر من الذي يقول إنه مخلوق، لأنه يقتدي به غيره.
وسمعت أبا بكر محمد بن يزيد قال: القرآن كلام الله ليس بمخلوق من قال: إنه مخلوق فهو كافر، ومن وقف لا يدري أن الله يتكلم أو لا يتكلم فهو كافر، والواقفة عندي شر من الجهمية.
سمعت عبد ة بن عبد الرحيم بن حسان قال: من زعم أن القرآن مخلوق فهو كافر، ومن وقف فهو شر منه، قال عبد ة: ليس بين أحد من أهل العلم اختلا ف أن من قال: القرآن مخلوق، فهو كافر.
حدثنا إبراهيم بن الحارث، قال: سألتُ أبا عبد الله أحمد بن حنبل قلت:','3','1124',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (947,'يا أبا عبد الله يكون من أهل السنّة من قال: لا أقول القرآن مخلوق، ولا أقول: ليس بمخلوق؟ قال: ولا، لا كرامة، لا يكون من أهل السنّة، قد بلغني عن ذاك الخبيث ابن معول أنه يقول بهذا القول، وقد فتن به ومن كثير من أهل البصرة.
سمعت إسحاق يقول: من قال: إن القرآن محدث على معنى مخلوق، فهو كافر بالله العلي العظيم.
قلت: ما معنى قوله: {مَا يَأْتِيهِم مِّن ذِكْرٍ مَّن رَّبِّهِم مُّحْدَثٍ} [الأنبياء: ٢] قال: محدث من العرش آخر ما نزل من الكتب من العرش. ثم راجعته في ذلك فقال: أحدث الكتب عهدًا بالرحمن.
حدثنا إبراهيم بن الحارث، قال: ثنا زهير بن حرب، قال: كنا في طريق مكة ومعنا مثنى الأنماطي، فجعل يقول: القرآن مجعول مخلوق. فقلت: وبينك بيني وكيع، فأتيناه، فقلنا: يا أبا سفيان إن هذا يزعم أن القرآن مجعول مخلوق؟ فقال وكيع: سبحان الله هذا كفر هذا كفر، فقال له المثنى: أليس الله يقول: {مَا يَأْتِيهِم مِّن ذِكْرٍ مَّن رَّبِّهِم مُّحْدَثٍ} فقال وكيع: هذا كفر.','3','1125',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (948,'حدثنا إبراهيم بن الحارث، قال: حدثني القاسم بن يزيد، قال: سمعت وكيع بن الجراح يقول: من زعم أن القرآن مخلوق محدث، ومن زعم أن القرآن محدث، فقد كفر بما أنزل على محمد، يستتاب فإن تاب وإلا ضربت عنقه.
سمعت إسحاق يقول: ليس بين أهل العلم اختلا ف أن القرآن كلام الله، وليس بمخلوق، وكيف يكون شيء من الرب عز ذكره مخلوقًا، ولو كان ما قالوا لكان يلزمهم أن يقولوا: علمه، وقدرته، ومشيئته مخلوقة، فإن قالوا ذلك لزمهم أن يقولوا كان الله تبارك اسمه، ولا عِلم ولا قدرة ولا مشيئة، وهو الكفر المحض الواضح، لم يزل الله عالمًا متكلمًا، له المشيئة والقدرة في خلقه، والقرآن كلام الله وليس بمخلوق، فمن زعم/٢١٠/أنه مخلوق فهو كافر، ومن وقف فهو شر منه.
حدثنا إبراهيم بن الحارث، قال: قال أحمد بن حنبل: القرآن كلام الله، ومن قال: إنه مخلوق فهو كافر، والقرآن من علم الله، وفيه أسماؤه، وعلم الله ليس بمخلوق، وقال الله: {الرحمن * عَلَّمَ الْقُرْآنَ * خَلَقَ الإنسَانَ * عَلَّمَهُ الْبَيَانَ} [الرحمن: ١ - ٤] فالقرآن من علم الله، وعلم الله ليس بمخلوق فيه أسماؤه.','3','1126',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (949,'قال إبراهيم: وسألتُ إبراهيم بن بصير عن القرآن؟ فقال: القرآن كلام الله وليس بمخلوق، ولم يزل الله بكلام هـ تبارك وتعالى قبل أن يخلق خلقه، ومن قال: مخلوق، فهو كافر يقتل ولا يستتاب.
سمعت إسحاق بن إبراهيم قال: القرآن كلام الله تكلم به. من قال: إنه كعباد الله. فهو كافر. حدثنا أبو موسى هارون بن زياد، عن أبي بكر بن عياش قال: من زعم أن القرآن مخلوق فهو كافر، قال أبو موسى: وأنا أقول من زعم أن القرآن مخلوق فهو كافر.
حدثنا عبد ة بن عبد الرحيم، قال: سمعت وكيع بن الجراح يقول: من قال: القرآن مخلوق فهو كافر.
حدثنا أبو موسى هارون بن زياد، قال: سمعت الفريابي وسأله رجل عمن','3','1127',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (950,'قال: القرآن مخلوق؟ قال: سألتُ سفيان الثوري عمن قال: القرآن مخلوق؟ قال: كافر بالله العظيم. حدثنا أبو علي الحسن بن الصباح البزار قال: عن سعيد الضبعي قال: القرآن كلام الله، ومن الله، وليس من الله شيء مخلوق، ومن زعم أن القرآن مخلوق فهو كافر.
وقال أبو عاصم النبيل: القرآن كلام الله وليس بمخلوق ومن قال: مخلوق فهو كافر.
وقال يعلى بن عبيد: القرآن كلام الله وليس بمخلوق، ومن زعم أنه مخلوق فهو كافر، هذا قول الجهمية.
حدثنا حماد بن المبارك، قال: حدثنا يحيى بن خلف أبو محمد المقري قال: كنا عند مالك بن أنس فسأله رجل: ما تقول فيمن قال: القرآن مخلوق؟ قال: زنديق كافر، اقتلوه، ثم قال مالك: ما سمعت هذا الكلام من أحد غيرك.
قال يحيى: ثم أتيت مصرًا فلقيت الليث بن سعد','3','1128',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (951,'وابن لهيعة فقلت لهما: ما تقولان فيمن قال: القرآن مخلوق؟ قالا: كافر. قال: ثم أتيت الكوفة فلقيت أبا بكر بن عياش فسألتُه فقال: كافر؟ وكل من لم يقل إنه كافر فهو كافر، قال أبو بكر: أيشك في اليهودي والنصراني أنهما كافران؟ فمن شك في هؤلاء أنهم كفار فهو كافر، والذي يقول القرآن مخلوق مثلهما، قال: ثم لقيت حفص بن غياث، ويحيى بن زكريا بن أبي زائدة، وحسين الجعفي، وعبد السلام الملائي، ووكيع، وابن إدريس/٢١١/ فقلت لهم ما قال الرجل لمالك فقالوا كلهم: كافر. قال: ثم لقيت هشيم وعلي بن عصام، ويزيد بن هارون فسألتُهم فقالوا: كافر، ثم قدمت المصيصة فلقيت عبد الله بن المبارك وأبا إسحاق الفزاري، ومخلد بن حسين، وعلي','3','1129',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (952,'بن بكار، فسألتُهم وقالوا: كافر. قال: ثم أتيت الشام فلقيت الوليد بن مسلم فسألتُه فقال: كافر.
أخبرني محمد بن أبي غياث قال: سمعت الفريابي يقول: من قال القرآن مخلوق فهو كافر، وسألتُ ابن أبي أويس، وأبا مصعب الزهري، وإبراهيم بن حمزة بن مصعب بن الزبير بن العوام عمن قال القرآن مخلوق؟ فقالوا: كافر.','3','1130',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (953,'حدثنا أبو بكر محمد بن يزيد، قال: أخبرنا الهيثم بن جميل قال: القرآن كلام الله ليس بمخلوق.
وقال أبو الوليد: من لم يعتقد قلبه أن القرآن ليس بمخلوق فهو خارج عن ملة الإسلام. وقال أبو عبيد الله: من قال القرآن مخلوق فهو كافر. قلت: لا يصلى عليه؟ قال: لا. قلت: ولا تجوز الصلاة خلفه؟ قال: لا. قلت: فإن صلى خلفه يعيد الصلاة؟ قال: نعم.
قال أبو نعيم: من قال القرآن مخلوق فهي الزندقة الصلعاء.
وقال أحمد بن يونس: هذا الكفر، ثم قال: خليفة يدعو الناس إلى الكفر إن هذا لهو البلاء العظيم.
وقال النفيلي: من قال القرآن مخلوق، فقد كفر بالله وكذب بالقرآن.
وقال سليمان بن داود الهاشمي: من قال القرآن مخلوق فقد كفر، لا يصلى خلفه، ولا يصلى عليه.','3','1131',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (954,'وقال عبد الله بن داود: من زعم أن القرآن مخلوق ينبغي أن ينزع لسانه من قفاه.
وقال محمد بن عيسى: من قال القرآن مخلوق فقد كفر. حدثنا عباس بن عبد العظيم قال: أخبرني محمد بن يحيى بن سعيد قال: سمعت معاذ بن معاذ قال: من قال القرآن مخلوق فهو كافر بالله.
حدثنا الحسن بن الصباح، عن إبراهيم بن بصير قال: سمعت أبا النصر يقول: من زعم أن القرآن مخلوق فهو كافر بالله.
حدثنا إسحاق بن إبراهيم، عن سفيان بن عيينة، عن عمرو بن دينار، قال: أدركت الناس منذ سبعين سنة، أدركت أصحاب النبي فمن دونهم','3','1132',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (955,'يقولون: الله الخالق وما سواه مخلوق، إلا القرآن، فإنه كلام الله منه خرج وإليه يعود.
حدثنا أحمد بن حنبل وعمرو بن العباس قالا: حدثنا عبد الرحمن بن مهدي قال: ثنا معاوية بن صالح، عن العلاء بن الحارث، عن زيد بن أرطأة، عن جبير بن نفير قال: قال رسول الله - صلى الله عليه وسلم -: ((إنكم لن ترجعوا إلى الله بشيء أفضل مما خرج منه)) يعني القرآن.','3','1133',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (956,'حدثنا سعيد بن نوح، نا أبو النضر هاشم بن القاسم، ثنا بكر بن خنيس، عن ليث بن أبي سليم، عن زيد بن أرطأة، عن أبي أمامة قال: قال رسول الله - صلى الله عليه وسلم -: ((ما تقرب المتقربون إلى الله بشيء أفضل مما خرج منه)) يعني: القرآن.
حدثنا الحسن بن الصباح، قال: ثنا حاجب، عن بقية بن الوليد، عن أبي بكر بن أبي مريم، عن عطية بن قيس الكلابي قال: ما تقرب العباد إلى الله بشيء أحب إليه من كلام هـ، ولا ردوا إليه كلام اأحب إليه مما خرج منه.','3','1134',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (957,'سمعت أبا الربيع الزهراني، وأبا ثور، والعباس العنبري، ويحيى الحمالي، وإبراهيم بن بشار يقولون: القرآن كلام الله تكلم به.
قال إبراهيم: وسمعت سفيان بن عيينة يقول: القرآن كلام الله من قال غير ذلك فهو مبتدع.
سألتُ محمد بن يسار قلت: أيام يحيى بن سعيد، ومعتمر بن سليمان، أصحابك تكلم الناس في القرآن؟ قال: نعم، ولكن هارون أمير المؤمنين كان لا يؤتى بأحد زعم أن القرآن مخلوق إلا قتله.
قال أبو بكر: وأنا أقول: القرآن كلام الله تكلم به.
وسمعته يقول: من زعم أن الله جل وعز لم يكلم موسى فهو كافر. قلت: فالصلاة خلف من زعم أن القرآن مخلوق؟ قال: لا تصلي.','3','1135',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (958,'حدثنا أحمد بن حنبل، قال: ثنا أسود بن عامر قال: ثنا إسرائيل، عن عثمان بن المغيرة، عن سالم بن أبي الجعد، عن جابر بن عبد الله قال: كان رسول الله - صلى الله عليه وسلم - يعرض نفسه على الناس بالموقف، فيقول: هل من رجل يحملني إلى قومه، فإن قريشًا قد منعوني أن أبلغ كلام ربي.
حدثنا محمد بن مصفى قال: ثنا بقية بن الوليد قال: حدثنا عيسى بن إبراهيم، عن موسى بن أبي حبيب، عن الحكم بن عمير الثمالي قال: قال','3','1136',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (959,'رسول الله - صلى الله عليه وسلم -: ((ينزل القرآن وهو كلام الله)).
حدثنا إسحاق، قال: أخبرنا جرير، عن ليث بن أبي سليم، عن سلمة بن كهيل، عن أبي الزعراء، قال: قال عمر: إن هذا القرآن كلام الله فلا عرفتكم ما عطفتموه على أهوائكم.','3','1137',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (960,'حدثنا علي بن عبد الله، قال: حدثنا سفيان بن عيينة قال: قال عثمان بن عفان: لو أن قلوبنا طهرت ما شبعت من كلام الله.
حدثنا محمد بن مصفى قال: حدثنا عبد الله بن محمد، عن عمرو بن جميع، عن ميمون بن مهران، عن ابن عباس قال: لما حكم عليّ الحكمين قال له الخوارج: حكمت رجلين؟ قال: ما حكمت مخلقًا إنما حكمت القرآن. حدثنا عمرو بن زرارة قال: حدثنا محمد بن يزيد الواسطي، عن نافع بن عمر، عن ابن أبي مليكة قال: كانت أسماء بنت أبي بكر إذا سمعت القرآن','3','1138',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (961,'قالت/٢١٣/: كلام ربي كلام ربي. حدثنا العلاء بن عمرو الحنفي، قال: حدثنا يحيى بن زكريا، عن ابن أبي زائدة، قال: حدثنا مجالد، عن الشعبي، عن مسروق، عن عبد الله بن مسعود قال: القرآن كلام الله، فمن رد منه شيئًا فإنما يرده على الله.','3','1139',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (962,'حدثنا بشر بن حجر قال: حدثنا صالح المري قال: قال الحسن: القرآن كلام الله إلى القوه و (الصعا) (١) [] والأعمال أعمال بني آدم إلى الضعف والتقصير، فاعمل وأبشر.
حدثنا عبيد الله بن معاذ، قال: ثنا أبي، عن السري بن يحيى، عن محمد بن سيرين قال: قال الحجاج بن يوسف وهو يخطب: إن ابن الزبير كان يبدل كلام الله. فقال له عبد الرحمن بن عمر: كذبت لا تستطيع ولا ابن الزبير أن تبدلوا كلام الله.
_________
(١) كذا بالاصل، وفي المطبوع من ((مسائل حرب)).','3','1140',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (963,'حدثنا إبراهيم بن الحارث، قال: ثنا الحسن بن بشر، قال: ثنا سعدان بن الوليد، عن عطا، عن ابن عباس قال: دخل رسول الله - صلى الله عليه وسلم - على أم هاني بنت أبي طالب فقالت له: يا رسول الله إن لي أصهارًا قد لجأوا إليّ، وإن علي بن أبي طالب لا تأخذه في الله لومة لائم، وإني أخاف أن يعلم بهم فيقتلهم، فاجعل من دخل داري آمنا حتى يسمع كلام الله [] رسول الله - صلى الله عليه وسلم -. سمعت إسحاق بن إبراهيم وسئل عن الرجل قال: القرآن ليس بمخلوق، ولكن قراءتي أناله مخلوقة، لأني أحكيه وكلامنا مخلوق؟ فقال إسحاق: هذا بدعة، لا يقار على هذا حتى يرجع ويدع قوله هذا.','3','1141',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (964,'حدثنا سليمان بن الأشعث، قال: ثنا يعقوب بن إبراهيم أن أحمد بن حنبل قال: اللفظية إنما يدورون إن على كلام جهم، يزعمون أن جبريل مخلوق وأنه إنما جاء بشيء مخلوق إلى محمد وهو مخلوق. حدثنا إسحاق بن إبراهيم، قال: حدثنا محمد بن أعين أنه شهد ابن المبارك وقيل له: إن النضر بن محمد يقول من قال: {إِنَّنِي أَنَا اللَّهُ لا إِلَهَ إلا أَنَا فَاعْبُدْنِي} [طه: ١٤] مخلوق فهو كافر. فقال ابن المبارك: صدق النضر.','3','1142',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (965,'حدثنا أبو إسحاق إبراهيم بن الحارث، قال: حدثنا موسى بن إبراهيم، قال: حدثنا شريك، عن مجالد، عن الشعبي، عن عامر بن شهر، قال: وحدثني محمد بن هاشم، عن أبيه، عن أبي عقيل، عن مجالد، عن الشعبي، عن عامر بن شهر وكان من الوفد الذين وفدوا على النجاشي من همدان قال: كنا عند النجاشي وجاء ابن له عن الكتاب، فقرأ سورة من الزبور، قال:','3','1143',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (966,'فضحك، فقال لي النجاشي: أتضحك من كلام الرحمن.
حدثنا سعيد بن منصور قال: حدثنا خلف بن خليفة، عن حميد/٢١٤/الأعرج، عن عبد الله بن الحارث، عن ابن مسعود، عن النبي - صلى الله عليه وسلم - قال: ((يوم كلم الله موسى كان عليه جبة صوف، وكسا صوف، وسراويل صوف، وكمة صوف، ونعلين من جلد حمار غير ذكي)).','3','1144',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (967,'حدثنا الحسن بن الصباح وعبد الرحمن بن محمد بن سلام قالا: ثنا علي بن عاصم، قال: حدثنا الفضل بن عيسى الرقاشي قال: ثنا محمد بن المنكدر قال: سمعت جابر بن عبد الله قال: قال رسول الله - صلى الله عليه وسلم -: ((لما كلم الله موسى يوم الطور كلمه بغير الكلام الذي كلمه به يوم ناجاه، فقال له موسى: هذا كلامك الذي كلمتني به؟ قال: يا موسى لقد كلمتك بقوة عشرة آلاف لسان، ولي قوة الألسنة كلها، وأنا أقوى من ذلك، فلما رجع موسى إلى بني إسرائيل قالوا: يا موسى صف لنا كلام الرحمن؟ قال: لا أستطيع، قالوا: يشبه؟ قال: ألم تروا إلى أصوات الصواعق التي تقبل بأحلى حلا وة سمعتموه فإنه قريب منه، وليس به)).','3','1145',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (968,'حدثنا عباس بن عبد العظيم، قال: ثنا علي بن عبد الله وزيد بن مبارك قالا: حدثنا محمد بن عمرو بن مقسم، عن عطا بن مسلم، عن وهب بن منبه قال: كلم الله موسى في ألف مقام كلما كلمه رئى النور على وجهه ثلاثة أيام، وما قرب موسى امرأة منذ كلمه ربه. حدثنا عيسى محمد قال: ثنا يزيد بن هارون، عن الحسن الحريري، عن أبي عطاف قال: كتب الله التوراة لموسى بيده وهو مسند ظهره إلى الصخرة، سمع صرير القلم في ألواح من در ليس بينه وبينه إلا الحجاب.','3','1146',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (969,'حدثنا عمرو بن العباس الأهوازي، قال: سمعت عبد الرحمن بن مهدي وقيل له: إن أصحاب جهم يقولون: القرآن مخلوق؟ فقال عبد الرحمن: إنما أرادوا أن ينفوا أن يكون القرآن كلام الله، وأرادوا أن ينفوا أن يكون الرحمن على العرش استوى، وأرادوا أن ينفوا أن يكون الله كلم موسى، ولقد ذكرها الله في كتابه فقال: {وَكَلَّمَ اللهُ مُوسَى تَكْلِيمًا} [النساء: ١٦٤] أرى أن يستتأبوا، فإن تأبوا وإلا ضربت أعناقهم. قال: وسمعت عبد الرحمن يقول: أرى أن يعرض أصحاب جهم على السيف. حدثنا أبو علي الحسن بن الصباح، قال: ثنا قاسم المعمري، قال: ثنا عبد الرحمن بن محمد بن حبيب بن أبي حبيب، قال: حدثني أبي، عن جدي حبيب قال: شهدت خالد بن عبد الله القسري وخطب الناس بواسط','3','1147',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (970,'يوم النحر فقال: أيها الناس ارجعوا فضحوا تقبل الله منكم فإني مضحٍ بالجعد بن درهم/٢١٥/، فإنه زعم أن الله تبارك وتعالى لم يتخذ إبراهيم خليلا، ولم يكلم الله موسى تكليمًا سبحانه وتعالى عمّا يقول الجعد بن درهم، ثم نزل إليه فذبحه.
حدثنا أبو بكر محمد بن يزيد قال: ثنا أبو بكر بن أبي الأسود قال: سمعت عبد الرحمن بن مهدي يقول: لو كان لي قرابة أنا وارثه، ثم مات لم أرثه إذا كان ممن يقول: القرآن مخلوق. حدثنا أبو القاسم عبد الرحمن بن محمد بن سلام قال: سمعت رجلا سأل يزيد بن هارون فقال: يا أبا خالد ما تقول في الجهمية؟ قال يزيد: زنادقة، زنادقة، زنادقة ومد بها صوته في الثالثة.','3','1148',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (971,'حدثنا محمد بن مصفى، عن يزيد بن هارون قال: لقد أخبرت من كلام المريسى بشيء وجعت وجعة في صلبي بعد ثلاث. وسأله رجل من أهل بغداد فقال: يا أبا خالد سمعت بشر المريسي يقول في سجوده: سبحان ربي الأسفل. فقال يزيد: إن كنت صادقًا إن بشر المريسي كافر بالله العظيم، وقال: لقد حرضت ببغداد على قتل بشر المريسي بجهدي.
حدثنا محمد بن مصفى، عن الفريابي قال: ألا تستطيعون أن تقتلوا بشر المريسي؟! حدثنا إبراهيم بن الحارث، قال: سمعت سعيد بن الصباح قال: جاء رجل إلى سفيان بن عيينة فذكر له كلام المريسي فقال سفيان: لقد شرع في هذا الدين ما لم يأذن به الله، ثم قال: من زعم أن القرآن مخلوق فهو كافر.','3','1149',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (972,'حدثنا أحمد بن الحباب، قال: سمعت أبا الصلت يقول: قال لي علي بن موسى الرضا: إن هذا - يعني: المأمون - يدعوكم لتقولوا القرآن مخلوق، فلا تقله فإنه كفر. حدثنا عباس بن عبد العظيم، قال: حدثني أحمد بن يونس قال: قال أبو ضيفة عند عيسى بن موسى: القرآن مخلوق. فقال عيسى لابن أبي','3','1150',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (973,'ليلى: استتبه فإن تاب وإلا فاضرب عنقه. حدثنا أحمد بن الأزهر قال: حدثني عبد الله بن عون، عن سفيان الثوري قال: قال لي حماد بن أبي سليمان قال لذاك الكافر: أبي حنيفة إن كنت تقول القرآن مخلوق، فلا تقربن مجلسي. حدثنا سليمان بن الأشعث وإبراهيم بن الحارث، قالا: حدثنا أحمد بن إبراهيم قال: حدثني زهير بن نعيم أنه سأل سلام بن أبي مطيع عن','3','1151',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (974,'الجهمية فقال: كفار، فلا يصلى خلفهم، قال: وقال زهير بن نعيم: أما أنا فإذا تيقنت أنه جهمي أعدت الصلاة خلفه الجمعة وغيرها. حدثنا محمد بن مصفى، عن أبي ضمرة أنس بن عياض في الصلاة خلف/٢١٦/الجهمية قال: لا تصلي خلفهم {وَمَن يَبْتَغِ غَيْرَ الإسلام دِينًا فَلَن يُقْبَلَ مِنْهُ وَهُوَ فِي الأَخِرَةِ مِنَ الْخَاسِرِينَ} [آل عمران: ٨٥]. حدثنا سليمان بن الأشعث، قال: قلت لأحمد: الجمعة؟ قال: أنا أعيد، ومتى ما صليت خلف أحد ممن يقول القرآن مخلوق فأعد.','3','1152',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (975,'حدثنا أبو إسحاق العبادي، عن يحيى بن معين أنه كان يعيد صلا ة الجمعة منذ أظهر عبد الله بن هارون ما أظهر. سمعت علي بن عبد الله، قال: بلغني عن ابن المبارك أنه قال: استجيز أن أحكي كلام اليهود والنصارى ولا استجيز أن أحكي كلام أصحاب جهم. قال أبو محمد: وذكرت عند علي بعض كلام هم قلت: قوم يقولون كذا ثم كذا أترى هؤلاء مسلمين؟ فقال: لو ذكر هذا رجل عند حماد وغيره من المشايخ لطردوه وما حدثوه بشيء يكره أن يحكى كلام هم أشد الكراهية. قلت لعلي: ويكره أن يذكر رجل كلام أهل البدع؟ قال: نعم، لأني أخاف أن يذكره عند رجل ضعيف القلب فيقع في قلبه. حدثنا محمد بن مصفى، عن نعيم بن حماد قال: من قال: القرآن مخلوق','3','1153',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (976,'فهو كافر بالله أرى أن جهادهم عندي أفضل من جهاد الروم، وأرى أن أقتلهم بلا استتابة. حدثنا محمد بن مصفى قال: حدثنا بقية بن الوليد، عن عبد العزيز الماجشون قال: جهم وشيعته الجاحدون. حدثنا محمد بن أبي يحيى الحماني قال: جهم كافر بالله العظيم.
وقال إسحاق: لا يجوز التفكر في الخالق، ويجوز للعباد أن يتفكروا في المخلوقين بما سمعوا فيهم ولا يزيدون على ذلك؛ لأنهم إن فعلوا تاهوا. حدثنا أحمد بن الأزهر قال: حدثنا محمد بن عبيد، عن الأعمش، عن عمرو بن مرة قال: مر النبي - صلى الله عليه وسلم - على قوم يتفكرون قال: ((تفكروا في الخلق، ولا تفكروا في الخالق)).','3','1154',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (977,'قال إسحاق: فالأشياء عند الله على معنى إرادته وحكمه، وأظهر للعباد من العلم ما يكتفون به، فينبغي الانتهاء ما عُلِّمْنَا، وَحُدَّ لنا حتى نصيب سبيلا في التفكر في خلق الله مشغلة عن التفكر فيما لم نؤمر به. قال أبو يعقوب: وكيف يستوسع من يدعي العلم الخوض في الأشياء المنهية عنها قال الله: {وَإِن مِّن شَيْءٍ إلا يُسَبِّحُ بِحَمْدَهِ} [الإسراء: ٤٤] فكيف يجوز لخلق أن يخوض في التسبيح من الشجب والأشياء المعمولة فيخوضوا كيف يسبح القصاع والأجوبة والحبر المجنون. والثياب المنسوجة، وكل هذا قد صح فيه العلم أنهم يسبحون فذلك إلى الله أن يجعل تسبيحهم كيف شاء وكما/٢٢٩/شاء، وليس للناس أن يخوضوا في ذلك إلا بما علموا، ولا يتكلموا في هذا وشبهه إلا بما أمر الله ولا يزيدون على ذلك، والله الموفق، وعليه التوكل، فاتقوا الله، ولا تخوضوا في هذه الأشياء المتشابهة، فإنه يردكم الخوض فيه عن سنن الحق. حدثنا محمد بن جعفر الوركاني، قال: حدثنا عبد الله بن المبارك، عن عيسى بن عبيد، عن عكرمة أنه قال: لا يعيبن أحدكم دابته وثوبه، فإن كل','3','1155',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (978,'شيء يسبح بحمده. حدثنا أبو معن قال: ثنا عبد الكبير قال: ثنا سفيان، عن منصور، عن إبراهيم في قوله: {وَإِن مِّن شَيْءٍ إلا يُسَبِّحُ بِحَمْدَهِ} قال: الطعام يسبح. حدثنا العلاء بن عمرو، قال: ثنا الأشجعي عبيد الله، عن مسعر، عن الأعمش، عن ذكوان قال: سمع صرير باب فقال: هذا تسبيحه.','3','1156',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (979,'<span data-type="title" id=toc-257>باب: في قول الله: {خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأرْضُ} [هود: ١٠٧]</span>
سألتُ إسحاق قلت: قول الله: {خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأرْضُ إلا مَا شَاء رَبُّكَ} قال: أتت هذه الآية على كل وعيد في القرآن. حدثنا عبيد الله بن معاذ قال: حدثنا معتمر بن سليمان قال: قال أبي: ثنا أبو نضرة، عن جابر زو أبي سعيد زو بعض أصحاب النبي - صلى الله عليه وسلم - قال: هذه الآية على القرآن كله {إلا مَا شَاء رَبُّكَ إِنَّ رَبَّكَ فَعَّالٌ','3','1157',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (980,'لِّمَا يُرِيدُ} [هود: ١٠٧] قال المعتمر: قال أبي: كل وعيد في القرآن. قال أبو محمد: ومعناه عندي إن شاء الله أعلم أنها تأتي على كل وعيد في القرآن لا هل التوحيد، وكذلك قوله: {إلا مَا شَاء رَبُّكَ} إلا من استثنى من أهل القبلة الذي يخرجون من النار، والله أعلم باستثنائه. حدثنا عبيد الله بن معاذ، قال: حدثنا أبي، قال: ثنا شعبة، عن أبي بلج','3','1158',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (981,'سمع عمرو بن ميمون يحدث عن عبد الله بن عمر وقال: ليأتين على جهنم يوم يصطفق فيه أبو ابها ليس فيها أحد وذلك بعدما يلبثون فيها أحقابا. حدثنا عبيد الله، قال: ثنا أبي، قال: ثنا شعبة، عن يحيى بن أيوب، عن زرعة، عن أبي هريرة قال: ما أنا بالذي لا أقول إنه سيأتي على جهنم','3','1159',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (982,'يوم لا يبقى فيها أحد، وقرأ: {فأما الذين فسقوا ففي النار لهم فيها زفير وشهيق} الآية. قال عبيد الله: كان أصحابنا يقولون: يعني به: الموحدين. وسمعت أبا عبيد الله محمد بن نصر الفراء، قال: نازلت سليمان بن حرب وعبيد الله بن محمد التيمي، وأبا عبيد دخل كلام بعضهم/٢٣٠/في بعض واحد قالوا: إن للنار جواتي وبراني فلا يدخل أهل التوحيد مدخل أهل الكفر والنفاق، لأن من أدخل مدخل أهل الكفر والنفاق لا يخرج منه أبدًا، أما تسمع إلى قوله: {لَا يَصْلَاهَا إلا الْأَشْقَى * الَّذِي كَذَّبَ وَتَوَلَّى} [الليل: ١٦] والعرب لا تسمى الشاة المصلية إلا ما شوى في وسط الجمر يحفرون في الأرض حفيرة فيجمعون فيها جمرًا كثيرًا، ثم يعمدون إلى المسلوخة، فيدخلونها وسط ذلك الجمر حتى تغيب فيها فتشتوى فيها، فذاك المصلى عندهم، فأما الذي يشوى على ظهر الجمر أو على المقلى، أو في التنور فلا يسمونها مصليا، قالوا: فمعنى الحديث أنه لا','3','1160',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (983,'يدخل أهل التوحيد مدخل أهل الكفر والنفاق وهو جوف النار وأسفله، يقول الله: {إِنَّ الْمُنَافِقِينَ فِي الدَّرْكِ الأَسْفَلِ مِنَ النَّارِ} [النساء: ١٤٥]، وتلك النار أعدت للكافرين. وأما معنى حديث الشفاعة أنه يخرج من النار من كان في قلبه مثقال حبة خردل من إيمان إنما معناه أن يخرج من برانى النار. قال أبو عبد الله: فنرى أن حديث عبد الله بن عمرو إن كان له أصل أنه يأتي على جهنم أحايين ليس فيها أحد، إنما هو موضع أهل التوحيد.
وقال الله: {خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأَرْضُ إِلاَّ مَا شَاء رَبُّكَ} فإنما يقع الاستثناء عندنا على أهل التوحيد في الآيتين جميعًا، لما جاء عن النبي - صلى الله عليه وسلم - أن له شفاعة لا هل الذنوب، فهذا ما أولنا والله أعلم.','3','1161',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (984,'حدثنا أبو معن، قال: حدثنا وهب بن جرير، قال: حدثنا شعبة، عن سليمان التيمي، عن أبي نضرة، عن جابر بن عبد الله أو بعض أصحابه في قوله: {خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ','3','1162',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (985,'وَالأَرْضُ إِلاَّ مَا شَاء رَبُّكَ} قال: هذه الآية على القرآن كله.
حدثنا أبو معن، قال: حدثنا موسى، قال: حدثنا سفيان، عن رجل، عن الضحاك في قوله: {خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأَرْضُ إِلاَّ مَا شَاء رَبُّكَ}، قال: إلا من استثنى من أهل القبلة الذين خرجوا من النار، {وَأَمَّا الَّذِينَ سُعِدُواْ فَفِي الْجَنَّةِ خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأَرْضُ إِلاَّ مَا شَاء رَبُّكَ} إلا من استثنى من أهل القبلة الذين أخرجوا من النار.','3','1163',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (986,'<span data-type="title" id=toc-258>باب: ما قيل للنبي - صلى الله عليه وسلم -: متى كنت نبيًا؟ قلت لإسحاق: حديث ميسرة الفجر قال: قلت: يا رسول الله، متى كنت نبيًا؟ قال: ((وآدم بين الروح والجسد)) ما معناه؟ قال: قبل أن يفخ فيه الروح وقد خلق. حدثنا عمرو بن العباس قال: حدثنا عبد الرحمن بن مهدي، عن منصور بن سعد، عن بديل العقيلي، عن عبد الله بن شقيق، عن ميسرة العجر قال: قلت: يا رسول الله، متى كنت نبيًا؟ قال: ((وآدم ببن الروح والجسد))</span>.','3','1164',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (987,'حدثنا علي بن عثمان، قال: حدثنا عماد قال: أخبإنا خالد عن عبد الله بن شقيق، عن أبي الحد قال: قلت: يا رسول الله، متى جعلت نبيًا؟ قال: ((آدم بين الروح والجسد)).','3','1165',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (988,'<span data-type="title" id=toc-259>باب: في الرؤيا:</span>
حدثنا عمرو بن عثمان بن سعيد بن كثير بن دينار، قال: حدثنا أبي، قال: حدثنا محمد بن مهاجر، عن جنيد بن ميمون، عن حمزة بن الزبير، عن عبادة بن الصامت أن النبي - صلى الله عليه وسلم - قال: ((رؤيا المؤمن كلام يكلم به العبد ربه في المنام)). حدثنا عبيد الله بن معاذ، قال: ثنا أبي، قال: ثنا شعبة، عن قتادة، عن أنس بن مالك، عن عبادة بن الصامت، عن النبي عليه السلام قال: ((رؤيا المؤمن','3','1166',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (989,'جزء من ستة وأربعين جزءًا من النبوة)). حدثنا يحيى بن عبد الحميد، قال: حدثنا سليمان بن بلا ل، عن سهيل، عن أبيه، عن أبي هريرة، عن النبي - صلى الله عليه وسلم - قال: ((الرؤيا الصالحة من العبد الصالح جزء من ستة وأربعين جزءًا من النبوة)). حدثنا يحيى، قال: حدثنا هشيم، قال: أخبرنا يعلي بن عطاء عن، وكيع بن عدس، عن عمه أبي رزين قال النبي - صلى الله عليه وسلم -: ((الرؤيا برجل طائر مال تعبر،','3','1167',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (990,'فإذا عبرت وقعت، والرؤيا جزء من خمسة وعشرين جزءًا من النبوة)) وحسبته قال: ((لا تقصها إلا على وادٍ، أوذي رأي)). حدثنا عبيد الله بن معاذ قال: ثنا أبي قال: ُنا شعبة، عن يعلى بن عطا، قال: سمعت وكيع بن عدس يحدث عن عمه أبي رزين قال: سمعت رسول الله - صلى الله عليه وسلم - يقول: ((رؤيا المسلم جزء من أربعين جزءًا من النبوة،','3','1168',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (991,'وهي على رجل طائر ما لم يحدث بها، فإذا حدثت بها وقعت)) قال يعلى وأحسبه قال: ((لا تحدث بها إلا حبيبًا أو لبيبًا)).
حدثنا الحميد، قال: قنا سفيان، قال: حدثنا عبد ربه بن سعيد قال: سمعت أبا سلمة بن عبد الرحمن يحدث، عن أبي قتادة أن رسول الله عليه السلام قال: ((الرؤيا من الله، والحلم من الشيطان، فإذا حلم أحدكم حلمًا يكرهه فلينفث عن يساره ثلاثا وليستعذ بالله من شرما رأى فإنها لن تضره)). حدثنا أبو بكر الحميدي قال: حدثنا/٢١٨/سفيان قال: حدثنا يحيى بن سعيد، عن أبي سلمة بن عبد الرحمن، عن أبي قتادة قال: قال رسول الله - صلى الله عليه وسلم -:','3','1169',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (992,'((الرؤيا من الله والحلم من الشيطان، فإذا حلم أحدكم حلمًا يكرهه فلينفث عن يساره ثلاثا وليستعذ بالله من شر ما رأى، فإنها لن تضره)). حدثنا أبو عبد الله أحمد بن بشر، قال: حدثنا يزيد بن زريع، عن سعيد، عن قتادة عن محمد بن سيرين، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((الرؤيا ثلاث فرؤيا حق، ورؤيا يحدث بها الرجل نفسه، ورؤيا تخزين من الشيطان، فمن رأي ما يكره فليقم فليصل)) وكان يقول: يعجبني القيد وأكره الغل، والقيد ثبات في الدين، وكان يقول: ((من رأني فإني أنا هو، فإنه ليس للشيطان أن يتمثل بي)) وكان يقول: ((لا تقصن، أولا تقص الرؤيا إلا على عالم أو ناصح)). حدثنا محمد بن مصفى، قال: ثنا بقية بن الوليد، قال: حدثني نصر بن علقمة،','3','1170',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (993,'عن عبد الرحمن بن عابد الأزدي، عن رجل من أصحاب النبي - صلى الله عليه وسلم - قال: قال رسول الله - صلى الله عليه وسلم -: ((من لم يؤمن بالرؤيا الصالحة فإنه لن يؤمن بالله ورسوله)).','3','1171',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (994,'<span data-type="title" id=toc-260>باب: في الرافضة:</span>
حدثنا أبو علي الحسن بن الصباح البزار، قال: حدثنا حسين بن محمد، قال: حدثنا الفرات، عن ميمون بن مهران، عن ابن عباس، عن النبي - صلى الله عليه وسلم - أنه قال: ((سيكون في آخر الزمان قوم ينتحلون مودة أهل بيتي هم الرافضة، فمن أدركهم منكم، فيقاتلهم فإنهم مشركون)).','3','1172',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (995,'حدثنا محمد بن نصر بن سعيد، قال: حدثنا يحيى بن أبي بكير، عن الفضيل بن مرزوق، عن أبي جناب، عن أبي سليمان، عن علي بن أبي طالب قال: قال رسول الله - صلى الله عليه وسلم -: ((إنه سيكون قوم يكون لهم نبر يقال لهم الرافضة، فإن أدركتهم فاقتلهم فإنهم مشركون)). قال: وقال عليٌّ: يكون قوم يقولون حبنا يكذبون علينا، وآية ذلك أنهم يسبون أبا بكر وعمر.
حدثنا أبو جعفر محمد بن عوف، قال: حدثنا أبو بكر بن حبيش، قال: حدثنا سَوَّار بن مصعب، عن داود بن أبي عوف، عن فاطمة بنت علي، عن','3','1173',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (996,'فاطمة الكبرى، عن أسماء بنت عميس، عن أم سلمة قالت: قال رسول الله - صلى الله عليه وسلم -: ((يا علي إلا إن ممن يزعم أنت بحبك قوم يضفرون الإسلام، ثم ما يلفظونه يقال لهم الرافضة، فإنه أدركتهم فاقتلهم فإنهم مشركون)) /٢١٩/قال: قلت: يا رسول الله، فما العلامة فيهم؟ قال: ((لا يحضرون جمعة ولا جماعة، ويطعنون على السلف)).','3','1174',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (997,'حدثنا يحيى بن عبد الحميد، قال: حدثنا إبراهيم بن سعد، عن عبيدة بن أبي رائطة، عن عبد الله بن عبد الرحمن، عن عبد الله بن مغفل قال: قال النبي عليه السلام: ((الله الله في أصحابي، لا تتخذوهم غرضًا، فمن أحبهم فبحبي أحبهم، ومن أبغضهم فببغضي أبغضهم، ومن آذاهم فقد آذاني، ومن آذاني فقد آذى الله، ومن آذى الله فيوشك أن يأخذه)). حدثنا محمد بن نصر، قال: ثنا حسان بن إبراهيم، عن محمد بن الفضل، عن عمرو بن دينار، عن جابر بن عبد الله قال: سمعت النبي - صلى الله عليه وسلم - يقول: ((إن','3','1175',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (998,'الناس يكثرون وأصحابي يقلون، لا تسبوهم، لعن الله من سبهم)). حدثنا سعيد بن منصور، قال: حدثنا أبو معاوية، عن محمد بن خالد الضبي، عن عطاء قال: قال رسول الله - صلى الله عليه وسلم -: ((من حفظني في أصحابي كنت له شفيعًا وشهيدًا يوم القيامة، ومن سبهم فعليه لعنة الله)). حدثنا حمزة بن عبيد الله، قال: حدثنا حميد بن أبي حميد الدمشقي، عن خالد بن معدان، عن عمر بن الخطاب قال: قال رسول الله - صلى الله عليه وسلم -: ((أحب آل','3','1176',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (999,'محمد ولا تكن رافضيًا، وأرجئ الأمور إلى الله ولا تكن مرجئًا، واعلم أن ما أصابك فمن الله ولا تكن قدريًا، واسمع وأطع ولو عبدًا حبشيًا، ولا تكن خارجيًا)). حدثنا أحمد بن سعيد، قال: سمعت غياث بن واقد يقول: قال سفيان: أرج ما لم تعلم إلى الله، ولا تكن مرجئًا، وأحب صالحي عبد المطلب، ولا تكن سبئيًا، وأحب وإن كان عبدًا حبشيًا ولا تكن خارجيًا. حدثنا سعيد بن منصور، قال: حدثنا أبو الأحوص قال: ثنا أبو عبد الرحمن، عن جعفر بن برقان، عن ميمون بن مهران قال: قال ابن عباس: يا ميمون لا تسب السلف، وادخل الجنة بسلام.','3','1177',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1000,'حدثنا سعيد، قال: حدثنا شهاب بن حراش، عن عمه العوام بن حوشب قال: أدركت من أدركت من صدر هذه الأمة وهم يقولون: اذكروا محاسن أصحاب رسول الله تتئلف عليهم القلوب، ولا تذكروا الذي شجر بينهم فتحرشوا الناس عليهم.
حدثنا أبو خالد الحيائي، قال: ثنا عثمان بن زفر، عن أبي خالد البصري، عن علي بن زيد بن جدعان، عن سعيد بن/٢٢٠/المسيب قال: من شتم أصحاب النبي - صلى الله عليه وسلم - سلط الله عليه في قبره حيَّتان (واحدهن) (١) من قبل رأسه وأخرى من قبل رجليه تقرصانه حتى ينتهيا إلى وسطه، ثم يعاد ويعادان إلى يوم القيامة.
_________
(١) كذا بالأصل ولعلها (إحداهن).','3','1178',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1001,'حدثنا سعيد بن منصور، قال: حدثنا أبو معاوية، عن الأعمش، عن أبي صالح، عن أبي سعيد الخدري قال رسول الله عليه السلام: ((لا تسبوا أصحابي فلو أن أحدكم أنفق مثل أحد ذهبًا ما أدراك مد أحدهم ولا نصيفه)). حدثنا أبو معن الرقاشي، قال: حدثنا يحيى بن سعيد، قال: ثنا صدقة بن المثى، قال: حدثني جدي بن رباح بن الحارث سمع سعيد بن زيد يقول: لمشهد شهده أحدهم مع رسول الله - صلى الله عليه وسلم - أغبر فيه وجهه أفضل من عمل أحدكم ولو عَمَّر عُمْر نوح.','3','1179',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1002,'حدثنا سعيد بن منصور، قال: حدثنا أبو معاوية، عن محمد بن سوقة، عن حبيب بن أبي ثابت قال: قال عليٌ: تفترق هذه الأمة على بضع وسبعين فرقة، شرهم قوم ينتحلون حبنا أهل البيت. حدثنا سعيد قال: ثنا أبو معاوية، عن مجالد، عن الشعبي، عن زياد بن النضر وكان على مجنبة علي قال: قلت له: يا أبا النضر ما ردك عن رأي هذه الشيعة وكنت فيهم رأسًا؟ قال: رأيتهم يتعلقون بإعجاز ليس لها صدور.','3','1180',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1003,'حدثنا أحمد بن يوسف، قال: ثنا زهير قال: حدثنا أبو إسحاق، عن عمر بن الأصم، قال: قلت للحسن بن علي: إن هذه الشيعة يزعمون أن عليًّا مبعوث يوم القيام؟ قال: كذبوا والله ما هؤلاء بشيعة، لو علمنا أنه مبعوث ما زوجنا نساءه، ولا اقتسمنا ماله. حدثنا يحيى بن عبد الحميد، قال: حدثنا إدريس، عن حصين، عن عمار بن الحارث، قال: قيل لابن عباس: إن ناسًا يزعمون أن عليًا سيرجع بعد','3','1181',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1004,'الموت؟ قال: لو علمنا ذلك ما اقتسمنا ميراثه، ولأنكحنا نساءه. حدثنا يحيى بن عبد الحميد قال: حدثنا شريك، عن أبي اليقظان، عن زاذان، عن علي قال: مثلي فيكم كمثل عيسى بن مريم أحبته طائفة فأفرطوا في حبه فهلكوا، وأبغضته طائفة فأفرطوا في بغضه فهلكوا، واقتصدت فيه طائفة فنجت، فالناجي منكم فيّ المقتصد. حدثنا أحمد بن يونس قال: حدثنا ابن أبي ذئب، عن الزهري قال: ما رأيت قومًا أشبه بالنصارى من السبائية. قال أحمد: هم الرافضة.','3','1182',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1005,'حدثنا يحيى بن عبد الحميد قال: حدثنا الحكم بن ظهير، عن السدي، عن أبي مالك، عن ابن عباس [في قوله تعالى] (١): {وَسَلَامٌ عَلَى عِبَادِهِ الَّذِينَ اصْطَفَى} [النمل: ٥٩] قال: أصحاب محمد.
حدثنا يحيى، قال: ثنا يعقوب، عن زيد بن أبي أسامة، عن نافع، عن ابن عمر
_________
(١) ليست في الأصل والسياق يقتضيها.','3','1183',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1006,'في [قوله تعالى]: {يَا أَيُّهَا الَّذِينَ آمَنُواْ اتَّقُواْ اللهَ وَكُونُواْ مَعَ الصَّادِقِينَ} [التوبة: ١١٩] قال: مع محمد وأصحابه. حدثنا يحيى قال: ثنا جعفر بن سليمان، عن عبد الصمد بن معقل قال: سمعت عمي وهب بن منبه يقول في قوله: {بِأَيْدِي سَفَرَةٍ * كِرَامٍ بَرَرَةٍ} [١٥ - ١٦] قال: أصحاب النبي - صلى الله عليه وسلم -. حدثنا العلاء بن عمرو، قال: ثنا ابن عليه، عن ابن أبي نجيح، عن مجاهد في قوله: {يَا أَيُّهَا الَّذِينَ آمَنُواْ أَطِيعُواْ اللهَ وَأَطِيعُواْ الرَّسُولَ وَأُوْلِي الأَمْرِ مِنكُمْ} [النساء: ٥٩] قال: أصحاب محمد.','3','1184',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1007,'حدثنا أبو محمد عبد الله بن خبيق الأنطاكي، قال: سمعت يوسف بن أسباط يقول: أما الشيعة فهم أصناف، فمنهم المنصورية وهم الذين يقولون: من قتل أربعين نفسًا ممن خالف هواهم دخل الجنة، وهم الذين يخيفون الناس ويستحلون أموالنا، وهم الذين يقولون: أخطأ جبريل بالرسالة، وأصل الشيعة الزيدية وهم الخشبية، وهم الذين يتبرءون من عثمان بن عفان وطلحة والزبير وعائشة، ويرون القتال مع كل من خرج من ولد علي برًا كان أو فاجرًا حتى يغلب أو يُغلب، ومنهم الرافضة الذين يتبرءون من جميع أصحاب النبي عليه السلام، ويكفرون الأمة، الأربعة: علي وعمار والمقداد وسلمان. حدثنا أبو بكر حماد بن مبارك، قال: ثنا محمد بن هيم، قال: ثنا الوليد بن مسلم، عن ثور بن يزيد، عن خالد بن معدان، عن معاذ بن جبل قال: قال','3','1185',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1008,'رسول الله - صلى الله عليه وسلم -: ((إذا ظهرت البدع وسب أصحابي فعلى العالم أن يظهر علمه، فإن لم يفعل فعليه لعنة الله والملائكة والناس أجمعين)) قال: قلت للوليد: ما إظهار علمه؟ قال: السنة. حدثنا عمر بن عثمان، قال: حدثنا بقية بن الوليد، عن هشام بن عبيد الله، عن ابن جريح، عن عطا، عن ابن عباس قال: كلام الشيعة هلكة. حدثنا سعيد بن عون، قال: حدثنا إسماعيل بن عياش، قال: حدثني حميد بن','3','1186',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1009,'مالك اللخمي، عن مكحول، عن معاذ بن جبل قال: قال رسول الله - صلى الله عليه وسلم -: ((يا معاذ أطع كل أمير، ولا تسبن أحدًا من أصحابي)). حدثنا محمد بن عبد الرحمن الجعفي، قال: حدثنا حسين بن علي، عن هانئ بن أيوب قال: سألتُ محارب بن دثار عن غيبة الرافضة؟ فقال: إنهم إذًا لقوم صدق. قال حسبي، أي لم ير بغيبتهم باسًا. /٢٢٢/حدثنا محمد بن عبد الرحمن قال: حدثنا أبو أسامة، عن زائدة، قال: قلت','3','1187',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1010,'لمنصور: يا أبا عتاب يصوم أحدنا ينتقص الذين يبغضون أبا بكر وعمر؟ قال: نعم. حدثنا محمد بن قدامة، قال: ثنا ابن علية، عن ابن عون، قال: سمعت إبراهيم يقول: احذروا هؤلاء الكذابين.','3','1188',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1011,'<span data-type="title" id=toc-261>باب: تفضيل أصحاب محمد - صلى الله عليه وسلم </span>-
سألتُ أحمد بن حنبل عن أصحاب النبي عليه السلام فقال: خير الأمة بعد النبي أبو بكر وعمر ثم عثمان على حديث ابن عمر. قال أحمد: وعلي في الخلفاء. قلت: أليس تقول: علي خير من بقي بعد الثلاثة في الخلافة؟ قال: هو خليفة. قلت: ولا يدخل في ذلك على طلحة والزبير؟ قال: لا، إيش على طلحة والزبير، ألا ترى أن عليًا كان يقيم الحدود، ويقسم الفيء، ويجمع بالناس، فإن قلت: ليس خليفة ففيه شناعة شديدة. وسألتُ إسحاق عن أصحاب النبي - صلى الله عليه وسلم - فقال: خير هذه الأمة بعد نبيها أبو بكر ثم عمر، ثم عثمان، ثم علي وقال: هو أفضل الأمة يومئذ وهو خليفة عدل، يعني بعد عثمان. وسألتُ علي بن عبد الله فقال: أبو بكر وعمر وعثمان. وسألتُ أبا ثور قلت: كيف تقول في أصحاب النبي عليه السلام؟ قال: خير هذه الأمة بعد النبي أبو بكر، ثم عمر، ثم عثمان، ثم الخمسة، وهم: علي وطلحة والزبير وسعد وعبد الرحمن، ورحم الله أبا عبد الرحمن يعني معاوية.','3','1189',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1012,'وسمعت هدبة بن خالد يقول: خير هذه الأمة بعد نبيها أبو بكر وعمر وعثمان وعلي. وسمعت أبا الربيع الزهراني يقول: خير هذه الأمة بعد نبيها أبو بكر ثم عمر، ثم عثمان، ثم نسكت. حدثنا عبيد الله بن معاذ، عن أبيه، عن بسر بن المفضل: عثمان أفضل من علي. قلت لعبيد الله بن معاذ: ما كان مذهب أبيك في هذا؟ قال: كان مذهب أبي أن عثمان أفضل من علي، يقول: أبو بكر، ثم عمر، ثم عثمان. حدثنا محمد بن عبد الأعلى، قال: حدثنا معتمر بن سليمان، قال: كان أبي يقول: خير الأمة أبو بكر، ثم عمر، ثم عثمان.
حدثنا محمد بن عبد الأعلى، قال: حدثنا يزيد بن زريع، قال: حدثني أيوب، عن أبي قلابة وكان عثمانيًا.','3','1190',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1013,'وحدثنا خالد بن عبد الله بن شقيق وكان عثمانيًا. حدثنا أحمد بن حنبل، قال: حدثنا بشر بن شعيب بن أبي حمزة، قال: حدثني أبي، عن الزهري قال: أخبرني سالم بن عبد الله أن عبد الله بن عمر قال: كنا نقول ورسول الله حي أفضل أمته: أبو بكر، ثم عمر، ثم عثمان. حدثنا أحمد بن يونس، قال: حدثنا إسماعيل بن عياش، قال: حدثنا يحيى بن سعيد الأنصاري، عن نافع، عن عبد الله بن عمر قال: كنا نتحدث على','3','1191',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1014,'عهد رسول الله - صلى الله عليه وسلم - أن خير الأمة بعد نبيها أبو بكر، ثم عمر، ثم عثمان. حدثنا أحمد بن حنبل، قال: حدثنا أبو معاوية، قال: ثنا سهيل بن أبي صالح، عن أبيه، عن ابن عمر قال: كنا نعد ورسول الله - صلى الله عليه وسلم - حي وأصحابه متوافرون أبو بكر وعمر وعثمان، ثم نسكت. حدثنا يزيد بن عمرو بن البراء، قال: حدثني عبد الله بن يزيد المقرئ، قال: حدثني عمر بن عبيد القرني، عن سهيل بن أبي صالح، عن أبيه، عن أبي هريرة قال: كنا أصحاب رسول الله - صلى الله عليه وسلم - متوافرون ونحن نقول: خير','3','1192',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1015,'هذه الأمة بعد نبيها: أبو بكر الصديق، وعمر بن الخطاب، وعثمان بن عفان، ثم نسكت. حدثنا عمرو بن عثمان، قال: ثنا سلم بن عبد الرحمن، قال: حدثني جعفر الواسطي قال: حدثنا عبد الله بن داود قال: حدثني سويد وابن عمرو بن حريث قال: سمعت عمرو بن حريث يقول: سمعت عليًا يقول: إلا إن خير هذه الأمة بعد نبيها أبو بكر، ثم عمر، ثم عثمان. حدثنا عبد الرحمن بن محمد، قال: حدثنا عبد الحميد بن عبد الرحمن، قال: حدثني صالح بن موسى الطلحي، قال: حدثني عاصم بن أبي','3','1193',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1016,'النجود، قال: قلت لزر بن حبيش: من عنى عليّ بالثالث؟ فقال زر: كان عليّ خيرًا من ذلك وأقرأ لكتاب الله من ذلك، وأعلم من ذلك أن يقوم على منبر رسول الله، ويعني نفسه، ولكن عنى بالثالث عثمان. سمعت إسحاق بن إبراهيم يقول: من قدم عليًا على عثمان فهو مخطئ. حدثنا أحمد بن سعيد، قال: سمعت النضر بن شميل يقول: من قال في بيعة عثمان فقد أزرى على عشرة آلاف من أصحاب رسول الله عليه السلام اجتمعوا فقدموا عثمان.','3','1194',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1017,'حدثنا نصير بن الفرج، قال: حدثنا أبو داود الجعفري، قال: حدثنا مسعر، عن عبد الملك بن ميسرة، عن النزال بن سبرة قال: سمعت عبد الله بن مسعود يقول حين استخلف عثمان: أمِّرنا خير من بقي ولم نألوا. قلت لإسحاق: قول النبي - صلى الله عليه وسلم - لعلي: ((أنت عون لي على عقر حوضي)) قال: هو في الدنيا يزود عنه، ويدعو إليه، ويبين لهم، ونحو ذلك من الكلام إلا أنه في الدنيا.','3','1195',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1018,'حدثنا أحمد بن سعيد قال: حدثنا الطنافسي قال: ثنا أبي قال: أدركت الناس وإنما يختلفون في علي وعثمان/٢٢٤/فأما أبو بكر وعمر فليس فيهما اختلا ف. قال أحمد بن سعيد وكان الفقهاء مختلفين منهم من يقول: أبو بكر وعمر، ويقف منهم الشعبي، وإبراهيم والكوفيون، وسعيد بن جبير، وأبو البختري، وغيرهم، وعبيد بن عمير، وقوم من أهل البصرة وقفوا، وكان قوم يقولون: أبو بكر وعمر وعثمان وعلي، وكان قوم يقال لهم الشيعة وليسوا مخارجين ولا منسوبين إلى البدعة يقولون: أبو بكر وعمر وعلي، وكان قوم يقال لهم عثمانين يقولون: أبو بكر وعمر وعثمان ويسكتون منهم سعيد بن أبي عروبة، وحماد بن زيد، وهشام بن أبي عبد الله، وغيرهم، وكان قوم من أهل البصرة يقفون في علي وعثمان،','3','1196',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1019,'منهم يحيى بن سعيد، وسليمان التيمي، ومعتمر بن سليمان، وخالد بن الحارث. حدثنا أبو حفص قال: ثنا عباس بن طالب قال: حدثنا حماد بن زيد، عن أيوب قال: قدمت المدينة والناس بها متوافرون: القاسم بن محمد، وسليمان بن يسار، وغيرهم، فما اختلف على أحد منهم في تقدمة أبي بكر، ثم عمر، ثم عثمان، قال حماد بن زيد: وذاك رأي أيوب وهو رأينا. وسمعت عبد الله بن سوار العنبري قال: السنة عندنا وما أدركنا عليه حمادًا، وحمادًا، والناس الذين يقتدى بهم تقديم أبي بكر، ثم عمر، ثم عثمان، والحب لأصحاب رسول الله - صلى الله عليه وسلم - جميعًا والكف عن ذكر مساوئهم، وعظيم الرضا بصحبة رسول الله - صلى الله عليه وسلم -، والإيمان قول وعمل.','3','1197',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1020,'حدثنا يحيى بن عبد الحميد، قال: ثنا حصين بن عمر الأحمسي، قال: ثنا مخارق، عن طارق، عن عثمان قال: سمعت النبي - صلى الله عليه وسلم - يقول: ((من أحب العرب فبحبي أحبهم، ومن أبغض العرب فببغضي أبغضهم)). حدثنا أبو الربيع الزهراني، قال: ثنا شجاع بن الوليد، عن قأبوس بن أبي ظبيان، عن أبيه، عن سلمان قال: قال لي رسول الله - صلى الله عليه وسلم -: ((يا سلمان','3','1198',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1021,'لا تبغضني فتفارق دينك)) قلت: يا رسول الله كيف أبغضك وبك اهتديت؟ قال: ((تبغض العرب فتبغضني)). حدثنا أبو عبد الرحمن النيسابوري، قال: حدثنا عبد الرحمن بن قيس، عن محمد بن عمرو، عن أبي سلمة، عن أبي هريرة قال: قال رسول الله - صلى الله عليه وسلم -: ((من أحب العرب فقد أحبني، ومن أحبني فقد أحب الله، ومن أبغض العرب فقد أبغضني، ومن أبغضني فقد أبغض الله)). حدثنا العلاء بن عمرو الحنفي، قال: حدثنا يحيى بن يزيد الأشعري، عن ابن جريح، عن عطاء، عن ابن عباس قال: قال رسول الله عليه السلام/٢٢٥/: ((أحب','3','1199',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1022,'العرب لثلاث: لأني عربي، والقرآن عربي، وكلام أهل الجنة عربي)).
حدثنا أبو معن الرقاشي، قال: حدثنا عبد الله بكير السهمي، عن محمد بن ذكوان، عن عمرو بن دينار، عن ابن عمر أن رسول الله - صلى الله عليه وسلم - قال: ((إن الله اختار من السموات، فاختار العليا فسكنها، واختار من الأرضين العليا فأسكنها من شاء من خلقه، ثم اختار من بني آدم، فاختار العرب، ثم اختار قريشًا، فمن أحب العرب فبحبي أحبهم، ومن أبغض العرب فببغضي أبغضهم)).','3','1200',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1023,'حدثنا يحيى بن عبد الحميد، قال: حدثنا قيس، عن ثابت أبي المقدام، عن جبة العربي قال: سمعت عليًا قال: قال النبي - صلى الله عليه وسلم -: ((يا علي أوصيك بالعرب خيرًا، يا علي أوصيك بالعرب خيرًا، يا علي أوصيك بالعرب خيرًا)).','3','1201',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1024,'<span data-type="title" id=toc-262>باب: في فضل الموالي</span>
حدثنا يحيى بن عبد الحميد، قال: حدثنا سليمان - يعني: ابن بلال- عن عمرو بن أبي عمرو، عن عكرمة، عن ابن عباس قال: شهد بدرًا مع النبي عليه السلام عشرون رجلا من الموالي. حدثنا عبد الرحمن بن عبد الوهاب، قال: ثنا منصور بن زيد أبو عبد الرحمن الموصلي، قال: ثنا محمد بن فضيل بن غزوان، عن أبيه، عن الرجال بن سالم، عن عطا بن أبي رباح قال: قال رسول الله - صلى الله عليه وسلم -: ((لا يبغض الموالي إلا','3','1202',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1025,'منافق، ولا يكون الأبدال إلا من الموالي)). حدثنا الحسن بن قزعة، قال: ثنا مسلمة بن علقمة، قال: حدثنا داود بن أبي هند قال: لما أمر ابن الزبير ببناء البيت قال: استعينوا بأهل فارس، فإنهم من ولد إبراهيم.','3','1203',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1026,'<span data-type="title" id=toc-263>باب: في الأنبياء صلوات الله عليهم:</span>
حدثنا علي بن عثمان قال: ثنا هشيم قال: أخبرنا العوام بن حوشب، قال: أخبرنا القاسم ابن عوف قال: قال كعب: أما إدريس فإنه كان رجلا صالحًا، وكان خياطًا يتعبد لله، ويصوم، ويصلي، ويتصدق بكسبه ما فضل عن قوته. حدثنا علي، قال: ثنا حماد بن سلمة، قال: أخبرنا ثابت البناني، عن أبي رافع، عن أبي هريرة أن رسول الله صلي الله عليه وسلم قال: ((إن زكريا كان نجارًا)).','3','1204',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1027,'حدثنا أحمد، قال: حدثنا هارون، قال: حدثنا ضمرة، عن ابن عطاء، عن أبيه، قال: كان سليمان يعمل الخوص بيديه، ويأكل خبز الشعير. حدثنا محمد بن عبد الرحمن، قال: حدثنا أبو أسامة، عن هشام بن عروة، عن أبيه، قال: كان داود يخطب/٢٢٦/ الناس على المنبر، وإنه ليعمل الخوص بيديه، فيعمل منه القفة، أو الشيء، ثم يبعث به مع من يبيعه، ويأكل من ثمنه.','3','1205',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1028,'حدثنا بشار بن موسى، قال: ثنا عباد، قال: حدثنا يحيى بن سعيد، عن سعيد بن المسيب قال: لا خير فيمن لا يطلب المال فيقي به دينه، ويصون به عرضه، ويقضي به ذمامه، وإن مات تركه ميراثًا لمن بعده.
حدثنا سعيد بن منصور قال: حدثنا خالد بن عبد الله، عن بيان قال: سمعت عبد الرحمن بن هلا ل يقول: أوصى أبو الدرداء رجلا منا فقال له: صل ونم، وصم وأفطر، وأعط وامنع، واجمع المال ولا تأثم. حدثنا أبو معن قال: حدثنا ابن سنان قال: حدثنا إسماعيل بن عياش، عن','3','1206',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1029,'يعقوب بن محمد قال: كنا نعالج البز، فكان يمر بنا إسحاق بن بشار فيقول لنا: الزموا إجارتكم، فإن أباكم إبراهيم كان بزازًا. حدثنا أبو محمد عبد الله بن محمد بن يحيى قال: حدثنا سفيان بن عيينة، عن عياض، عن أبي دليل، عن جرير بن عبد الله، عن النبي - صلى الله عليه وسلم - قال: ((من سن سنّة حسنة فعمل بها من بعد كان له مثل أجر من عمل بها لا ينقص ذلك من أجره شيئًا، ومن سن سنّة سيئة فعمل بها من بعد كان عليه مثل وزر من عمل بها ولا','3','1207',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1030,'ينقص ذلك من أوزارهم شيئًا)). حدثنا عبد الله بن محمد قال: حدثنا أبو يعقوب الحنفي، عن كثير بن عبد الله، عن أبيه عن جده قال: قال رسول الله - صلى الله عليه وسلم -: ((إن هذا الدين بدأ غريبًا وسيعود كما بدأ، فطوبى للغرباء)) قيل: يا رسول الله، ومن الغرباء؟ قال: ((الذين يحيون سنتي من بعدي ويعلمونها الناس)). حدثنا عباس بن الوليد، قال: ثنا عبد الجبار بن مظاهر الجشمي، قال: حدثني معمر بن راشد قال: سمعت ابن شهاب الزهري يقول: تعليم سنّة','3','1208',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1031,'أفضل من عبادة مائتي سنة. قال عباس: سمعت مروان يسأله عن هذا الحديث فحدثه. حدثنا محمد بن حفص العطار، قال: حدثنا عبد الرحمن بن مهدي، قال: سمعت مالك بن أنس قال: قال عمر بن عبد العزيز: سنَّ رسول الله - صلى الله عليه وسلم - وولاة الأمر بعده سننًا الأخذ بها تصديق بكتاب الله، واستكمال لطاعته، وقوة على دين الله، من عمل بها مهتدى، ومن استنصر بها منصور، ومن خالفها اتبع غير سبيل المؤمنين وولا هـ الله ما تولى.','3','1209',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1032,'<span data-type="title" id=toc-264>باب: مسألة أصحاب الرأي</span>
قيل لأحمد: رجل نزلت/٢٢٧/ [] (١) لغير الدين. حدثنا الأزهر قال: سمعت حبيبًا كاتب مالك يقول: قال مالك: كانت فتنة أضر (أبي حنيفة) (٢) على هذه الأمة من فتنة إبليس في الوجهين جميعًا في الإرجاء، وما وضع من نقص السنن. حدثنا عبدة بن عبد الرحيم بن حسان قال: ثنا معروف بن حسان السمرقندي قال: كنت عند الأعمش وهو مريض، فأتاه (...) يعوده، فقال: (...) لولا أني أثقل عليك لعدتك كل يوم. فقال الأعمش:
_________
(١) سقط من الأصل.
(٢) كشطت بالأصل، والمثبت من ((تاريخ بغداد)) ١٣/ ٤١٦.','3','1210',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1033,'من هذا؟ قال أبو [حنيفة] فقال: والله إنك لتثقل عليّ وأنت في منزلك فكيف إذا عدتني. حدثنا محمد بن يحيى بن عبد الكريم، قال: حدثنا عبد الله بن داود، قال: حدثني أشعث - صاحب لي- قال: سمعت الأعمش يقول: إنما مثل (...) رجل خرج بالليل فرأى سوادًا فظن أنها تمرة، فإن أخطاه أن يكون غير ذلك كان خر وكلب. حدثنا عبد العزيز بن أبي سهل، قال: حدثنا منصور بن أبي مزاحم، قال: سمعت شريك بن عبد الله يقول: لأن يكون في كل ربع من أرباع الكوفة خمار يبيع الخمر خير من أن يكون فيه من يقول بقول أبي حنيفة.','3','1211',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1034,'بقية الباب في قول (...): حدثنا محمد بن يحيى، قال: حدثنا نعيم بن حماد، قال: سمعت ابن عيينة يقول: لما قدمت الكوفة حدثتهم فكان فيما حدثتهم من حديث عمرو، عن جابر بن زيد، فقالوا إن (...) يرويه عن عمرو، ويقول: جابر بن عبد الله. قال: قلت: لا، إنما هو جابر بن زيد، قال: فأتوه، فأخبروه، فقال (...): لا تبالوا إن شئتم فاجعلوه جابر بن عبد الله، وإن شئتم جابر بن زيد.','3','1213',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1035,'حدثنا عبدة بن عبد الرحيم قال: أخبرنا سلمة بن سليمان قال: قال ابن المبارك: كنت آتيه - يعني: (...) سرًا من سفيان وأصحابنا. حدثنا محمد بن يحيى، قال: سمعت إسحاق بن إبراهيم يقول: قيل (...) حديث علي بن أبي طالب: ((الوضوء نصف الإيمان)) فقال (...) حسن من هذا. قلت لإسحاق: من يحكيه (...)؟ قال الشيباني. حدثنا أبو الأزهر، قال: حدثنا منصور بن أبي مزاحم، عن أبي صالح الفراء، قال: سمعت أبا إسحاق الفزاري يقول: حدثت (...) (١) بحديث عن النبي
_________
(١) بين كل معقوفين مكشوط بالأصل، وهي كلمة (أبي حنيفة)، ويبدو أن صاحب المخطوطة كشطها لشيء في نفسه.','3','1214',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1036,'عليه السلام في رد السيف فقال: هذا حديث خرافة حدثنا عباس. /٢٢٨/ (...) []','3','1215',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1037,'قال بشر: ولما أقبل أبو جابر البياضي سألتُ عن هؤلاء فقال: ليسو بثقات فلا تروين عنهم. وسألتُ عن رجل نسيت اسمه، فقال: تراه في كتبي؟ قلت: لا. قال: لو كان ثقة لرأيته في كتبي، قال: ونهاني عن ابن أبي يحيى. قلت: لعلك لمكان رأيه؟ قال: لا، ولكن ليس في حديثه بذاك.
وسمعت مالكًا يقول وكأنه عرض بي: عجبًا للرجل يقوم البلد فلا يحسن أن يختار لنفسه معلمًا، ولا يسأل حتى يختار له. قال بشر: وذاك أنه بلغه أني اختلفت إلى ابن أبي يحيى. قلت لأحمد بن سعيد: مالك، لما ترك حديث عكرمة؟ قال: لرأيه؟ قلت: وما رأيه؟ قال: شبه كلام الخوارج، فأما الكذب فمعاذ الله.','3','1217',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1038,'وسمعت أحمد بن حنبل يقول: لم يكن أحد اسمه منصور أفضل من منصور بن زاذان. حدثنا أحمد قال: سمعت يزيد بن هارون قال: زعموا أن منصور بن زاذان كان يختم في الضحى، وإنما كان يعرف ذلك منه بسجود القرآن، وكان سريع القراءة، فإذا أراد أن يترسل فكان لا يستطيع فأرسل بمصحف له إلى فلأن يعني فنقطه. قلت لأحمد: قيس بن الربيع أي شيء ضعفه؟ قال: روى أحاديث منكرة، وقد كان يتشيع ولم يضعف للتشيع. قال أبو محمد: وحدثنا أحمد بن يونس قال: حدثنا قيس بن الربيع، عن أبي حصين، عن حبيب بن صهبان قال: سمعت عمر يقول: ظهر المسلم حمى، لا يحله إلا حد. قال: ولقد رأيت بياض إبطه هو مقيد رجلًا من نفسه. قال أحمد بن يونس: هذا الحديث رواه أبو بكر بن عياش','3','1218',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1039,'وشعبة عن قيس بن الربيع. سألتُه عن عثمان أبي اليقظان؟ قال: هو عثمان بن عمر، روى عنه شريك وسفيان وروى أحاديث منكرة، وكان فيه تشيع. وقال: أبو هبيرة بن مريم يُعْرَف. روى عنه أبو إسحاق قال: وهبيرة وعاصم بن ضمرة أعلى من الحارث. وسألتُه عن حكيم بن جبير؟ فضعفه وقال: هو قريب من عثمان بن عمير. قال أبو محمد: وقال علي بن عبد الله ولم أسمعه سألتُه - يعني:','3','1219',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1040,'يحيى بن سعيد عن حكيم بن جبير فقال: كم روى؟ إنما روى شيئًا يسيرًا. قال يحيى: وقد روى عنه زائدة. قلت ليحيى: من تركه)) قال: شعبة من أجل هذا الحديث. قلت: حديث الصدقة؟ قال: نعم، ثم قال يحيى: نحن يحدث عمن هو دون هؤلاء. قال أحمد: إبراهيم بن أبي يحيى المدني ضعيف/٢٣١/ لا يكتب حديثه. قلت: لأحمد؛ فإن بشر بن المفضل قال: إبراهيم بن أبي يحيى كذاب؟ قال: قد قال ذاك بشر وغيره. قال أبو محمد: سمعت عبيد الله بن معاذ يحدث عن بشر بن المفضل قال: سألتُ فقهاء المدينة عن إبراهيم بن أبي يحيى فكلهم يقول: كذاب، أو نحو هذا. وسمعت أحمد يقول: كان يزيد بن خمير كيسًا، ويمدحه، ويقول: حديثه حسن.','3','1220',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1041,'قيل: حديث عمرو بن شعيب، عن أبيه، عن جده كيف حديثه؟ قال: هو عمرو بن شعيب بن محمد بن عبد الله بن عمرو بن العاص، ويقال: إن شعيبًا حدث من كتاب جده ولم يسمعه منه. قال أبو محمد: حدثنا أبو جعفر قال: حدثنا أحمد بن سليمان قال: ثنا معتمر، عن ليث قال: قال لي مجاهد: لا تجلس إلى هذين المهتابين. يعني عمرو بن شعيب ووهب بن المنبه.
قال معتمر: وقال أبو عمرو بن العلاء: كان قتادة وعمرو بن شعيب لا يعب عليهما بشيء إلا أنهما كانا لا يسمعان بشيء إلا حدثا به. وسمعت أحمد يحسن الثناء على إبراهيم الشافعي الذي بمكة،','3','1221',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1042,'وسعيد بن منصور وقال: إن محمد بن معاوية ذو أحاديث منكرة. قلت لأبي عبد الله: عطاء بن السائب تغير في آخر أمره؟ قال: نعم تغيرًا شديدًا. قال: ومن روى عنه بآخره فهو ضعيف مثل: إسماعيل بن علية، وعلي بن عاصم، وخالد الطحان، وجرير، وعامة البصريين. قال أبو محمد: وقال علي بن عبد الله قال يحيى: ما سمعت أحدًا من الناس يقول في عطاء بن السائب شيئًا قط في حديثه القديم. قلت ليحيى: ما حدث شعبة وسفيان عن عطاء بن السائب أصحيح هو؟ قال: نعم، إلا حديثين كان شعبة يقول سمعتهما بآخره عن زاذان. قال أحمد: كان عبد الكريم أبو أمية مرجئًا. قال أحمد: روح، عن شعبة، عن ابن أبي بكر، عن أبيه أنه كان ينفر يوم الثاني. وقال عبد الرحمن، عن شعبة قال: سمعت أبا بكر، قال أبو عبد الله:','3','1222',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1043,'هذا خطأ، لأن شعبة لم يلق أبا بكر، ولم يرو شعبة عن مشايخ المدينة، إلا عن المقبري لقيه بعدما كبر. قال أبو محمد: هكذا هو في كتابي. قال أبو عبد الله: قال وهيب: أتيت عطا بن السائب فقلت له: كم سمعت من عبيدة؟ قال: ثلاثين حديثًا. قال: ولم يسمع من عبيدة/٢٣٢/شيء. قال: ويدل ذلك على أنه تغير. وسمعت أبا عبد الله يقول في ثور بن يزيد: إنه كان يرى القدر. قال: وأهل حمص أخرجوه من حمص، فصار إلى نحو بيت المقدس. قال: وبحير بن سعد أروى عن خالد بن معدان منه. قال أحمد: وقال يحيى بن سعيد: رأيت الأوزاعي وثورًا سنة خمسين. سئل أحمد عن قتادة فلم يصرح، ولكنه يذهب إلى أنه كان ممن يرى القدر. قال: وعامة أصحاب الحسن وهمام وهشام كل هؤلاء كانوا يضعفون في القدر.','3','1223',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1044,'قيل لأبي عبد الله: إن بعض الناس قال: إنك لا تروي عن أبي أسامة؟ فقال: بلى، نحن نروي عن أبي أسامة. وقال: ما صح من حديث علي بن عاصم فلا بأس به. وقال: أبو معاوية أثبت في حديث الأعمش منه في غيره، وقال: هو أثبت في الأعمش من جرير، وقال: كان يحيى بن سعيد يقدم منصور والحكم على الأعمش، وقال: ما أثبت أبا نعيم وأكسبه، ولأنقدمه على ابن مهدي. وقال: كان ابن مهدي صاحب دين وخير. وقال: عند أبي نعيم نصف ما عند ابن مهدي فكيف يستويان؟ قلت لأحمد: اكتب من كتب أبي عبيد شيئًا؟ فسكت كأنه يضعف. وسألتُه عن الواقدي وأبي البحتري، فزبرني، وقال: نحن بعد في أبي البختري. وقال: آه آه.','3','1224',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1045,'قال أبو محمد: بلغني أنهما كانا يلقنان المشايخ. وسمعت أحمد يقدم أشعث أظنه ابن أبي الشعثاء علي سماك. وسمعته يقول: كان رقبة بن مسقلة شيخًا ثقة وكان كوفيًا.
وسئل أحمد عن حديث أبي المليح، عن ميمون، عن ابن عباس أن آخر جنازة صلى عليها النبي - صلى الله عليه وسلم - كبر أربعًا. فقال: هذا كذب، ليس له أصل، صاحب هذا كان يضع الحديث،','3','1225',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1046,'إنما رواه محمد بن زياد الطحان وكان يضع الحديث. وسئل أحمد عن حديث الليث، عن عبد الله، عن أم كلثوم، عن العباس، عن النبي عليه السلام في البكاء من خشية الله، فأنكره. سمعت أبا عبد الله وسئل عن حديث حماد بن سلمة، عن عاصم، عن أبي وائل، عن مغيرة بن شعبة في المسح فقال شعبة: قال لي عاصم ليس كما قال سليمان الأعمش، إنما حدثناه أبو وائل عن المعرة قال شعبة: فذكرت ذلك لمنصور، فوافق الأعمش عن حذيفة. قيل لأحمد: من ذكره عن شعبة؟ قال: أبو داود. قال أحمد: والأعمش ومنصور أحفظ لهذا من حماد وعاصم، وقد رواه حماد بن سلمة عنهما جميعًا. قيل: فحديث/٢٣٣/سفيان، عن أبيه حديث أبي عبد الله الجدلي، عن خزيمة','3','1226',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1047,'بن ثابت؟ فقال: كان وكيع يدع منه إبراهيم النخعي. قال أحمد: ورواه وكيع أيضًا عن سفيان، عن منصور وحماد عن إبراهيم، عن أبي عبد الله الجدلي، عن خزيمة بن ثابت، ولم يقل منصور أحد غير وكيع، وكأن أحمد لم يحفظ وكيعًا في هذا، قال أحمد: فلما قال شعبة: الحكم وحماد علم أن الحديث حديث الحكم. قال أبو محمد: حدثنا محمد بن بشار قال: حدثنا محمد بن جعفر قال: حدثنا شعبة، عن الحكم، عن إبراهيم، عن أبي عبد الله الجدلي، عن خزيمة بن ثابت، عن النبي - صلى الله عليه وسلم - أنه قال في المسح على الخفين: ((يومًا وليلة للمقيم، وثلاثة أيام ولياليهن للمسافر)).','3','1227',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1048,'حدثنا محمد بن بشار، قال: ثنا محمد بن جعفر، قال: ثنا شعبة، عن حماد، عن إبراهيم، عن أبي عبد الله، عن خزيمة، عن النبي مثله. قلت لأحمد: أبو عبد الله الجدلي معروف؟ قال: نعم رجل معروف ووثقه، ولكن شعبة قال: إن إبراهيم لم يسمع من أبي عبد الله الجدلي. قال أحمد: لم يسمع الحسن من ابن عباس، إنما كان ابن عباس بالبصرة واليًا أيام علي. حدثنا أحمد قال: ثنا عفان قال: ثنا حماد بن سلمة، عن علي بن زيد قال: لم يسمع الحسن من أبي هريرة.','3','1228',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1049,'قال أحمد: وحدثنا عفان قال: ثنا وهيب قال: قال أيوب: لم يسمع الحسن من أبي هريرة. سمعت أبا عبد الله قال: كان عمر بن علي المقدمي كثير التدليس وكان عاقلا حسن الهيئة. قال: وكان حفص يدلس وهشيم وسفيان. قال: ولم يكن أحد أروى عن سفيان بن حسين من عباد بن العوام. قال: وما سمعت ابن ادريس حدث عن ابن شبرمة بشيء. قال: والزبير بن عدي شيخ ثقة حسن الحديث.','3','1229',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1050,'وسئل عن الزبير بن عربي كيف حديثه؟ قال: لا أعرفه، قد روى عنه حماد بن زيد. قال أبو عبد الله: جعفر بن سليمان لم يكن به بأس، وكأنه ذهب إلى أنه يتشيع. قال: ومعاوية بن عبد الكريم كان شيخًا ثقة.
قيل لأحمد: حديث أبي عوانة، عن عبد الله، عن أبي المعلى، عن أبيه من أبو المعلى؟ قال: رجل من الأنصار. قيل له: هذا متصل عن النبي؟ قال: نعم.','3','1230',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1051,'سمعت أحمد يقول: نعيم بن ميسرة لا بأس به. وسئل عن النضر بن عربي؟ فقال: ما علمت إلا خيرًا، وكذلك معقل بن عبيد الله. قال حرب: أرى هؤلاء من أهل الجزيرة. قال أحمد: إسرائيل كان شيخًا ثقة، وجعل/٢٣٤/يعجب من حفظه. قال حرب: وقال علي بن عبد الله ولم أسمعه منه: قيل ليحيى: إن إسرائيل روى عن إبراهيم بن مهاجر ثلاثمائة حديث فقال يحيى: إبراهيم بن المهاجر لم يكن بذاك القوي. قيل: وروى عن أبي يحيى','3','1231',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1052,'القتات ثلاثمائة؟ فقال: لم يؤت منه أولى منهما جميعًا. قيل ليحيى: فالسدي؟ قال: السدي عندي كأنه لا بأس به. قال أحمد: الأعمش لم يسمع من شمر. وذكر أبو عبد الله مخلد بن يزيد فقال: لم يكن. أي بالحافظ، وقد كتبت عنه بمكة قبل، فمسكين، كأنه قدمه على مخلد، وقد روى عن شعبة أحاديث منكرة. قال أحمد: روى وكيع عن سفيان عن عيينه {مِن صَيَاصِيهِمْ} [الأحزاب: ٢٦] فلم يحفظ سفيان. وروى أبو معاوية عن سفيان، عن ابن أبي نجيح، عن مجاهد {يَوْمَ تَمُورُ السَّمَاء مَوْرًا}، فلم يحفظ سفيان. قيل','3','1232',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1053,'لأحمد: وعمرو بن دينار عن عمر بن عبد العزيز في طلا ق المكره أو السكران، وابن علية يرويه عنه؟ قال: نعم، فلم يعرفه سفيان، ثم قال: وهذا كثير عن سفيان. قال أحمد: اسم أبي مرعه سويد بن حجر الباهلي، وكان ثقة صالحًا. سئل أحمد عن حديث النيسابوري، عن بهز، عن أبيه، عن جده، فقال: لا أدري لم يره غيره. قيل لأحمد: فحديث مكي، عن بهز الذي رواه في الصدقة؟ فقال: ذاك قد وجدناه عن غير واحد، وكان يحيى بن سعيد يقول فيها كلها: حدثني أبي.','3','1233',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1054,'وسئل أحمد: عثمان البتي ابن من؟ فقال: لا أدري، وكان هشيم لا يقول البتي يقول: عثمان أبو عمر. وسئل أحمد عن حديث خالد، عن رجل، عن أبي معشر، عن إبراهيم، عن عبد الله في بيع ده دوازده ما لم يأخذ للنفقة ربحًا. فقال: حدثناه محمد بن بشر، عن سعيد بن أبي عروبة قال: حدثنا خالد، عن أبي معشر، عن إبراهيم، عن عبد الله قال: وليس كلهم يذكر فيه خالد، ثم قال أحمد: وقد رفع خالد عن إبراهيم إلى عبد الله أحاديث يرسلها غيره، منها هذا، وحديث: ((إذا توالى عليه رمضانان))، وفي الجنب يقرأ بعض الآية.','3','1234',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1055,'قال أحمد: وقد جالس أبو معشر ابن سيرين. وروى أنه كان جالسًا عنده ذات يوم فقال أبو معشر: كان ابن مسعود يشرب نبيذ الجر. فرفع ابن سيرين رأسه وقال: قد جالسنا أصحاب عبد الله فأنكروا ذلك أو كلمة نحوها، وأنكره ابن سيرين. وسمعت أحمد يوثق حسان بن إبراهيم الكرماني قال: حدثنا عنه غير واحد، فأخبرته بحاله، فقال: أخبرك أن عندكم بكرمان علمًا.
/٢٣٥/قلت: مستلم بن سعيد يروي عنه يزيد بن هارون كيف هو؟ قال: شيخ ثقة، هو صالح من أهل واسط حديثه قليل. قال أحمد: كان الغالب على هشيم رأي التابعين، وكان إذا صح عنده عن الحسن شيء لم يعده، وكان كثير الصلاة. وقال: يزيد بن هارون كيس، قدمه على هشيم في جياد الأحاديث والمسندات. سمعت أبا عبد الله يقول: كان عبد الوارث التنوري صالحًا في الحديث، وكان رأيه رأي سوء كان مفتونًا بعمرو بن عبيد.','3','1235',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1056,'قال أبو محمد: وحدثني أبو معن قال: سمعت حجاج بن المنهال قال: كان حماد بن زيد، ويزيد بن زريع، وبشر بن المفضل، وعدة من أصحاب الحديث في جنازة، فقالوا لحماد: أمض بنا نسأل عبد الوارث عما بلغنا عنه، فقال حماد: لا أجئ. قال: فذهب عدة من هؤلاء فدخلوا عليه، فقالوا: يا أباعبيد بلغنا أنك قلت أن عمرو بن عبيد أفقه من علقمة والأسود. قال: لو كنت قلت: إنه أفقه من صاحبهما لصدقت. قال أبو معن: وسمعت الناس يقولون: كان عبد الوارث لا يجمع. قال: قال أبو معن: وحدثني من سمع عبد الصمد بن عبد الوارث يقول: أتيت هشامًا الدستوائي فقال: ابن من أنت؟ قلت: ابن عبد الوارث. قال: أنت معتزلي صغير. سمعت أبا معن يقول: كان عبد الوارث التنوري وأبو علي الأسواري،','3','1236',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1057,'وعباد بن صهيب الكلبي، وبسر بن إبراهيم الأنصاري، وعبد الواحد بن زيد، وحمزة بن نجيح، وسفيان بن حبيب، ومهدي بن هلا ل، وصالح بن عمرو، وعثمان البري، وخليل بن مرة، وعمر السمري كل هؤلاء يرون القدر ويتهمون. نسأل الله العافية.','3','1237',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1058,'سمعت أحمد وذكر شعيب بن سهل بغداد، فقال: أخزاه، وهو يرى رأي جهم. قال أبو محمد: حدثنا أحمد بن سليمان قال: ثنا سفيان، عن عمرو بن دينار قال: قال لنا طاوس: أخزوا معبدًا. قال أحمد: عمرو بن قيس وابن أبجر شيخان صالحان. قال أحمد: قال عبد الرزاق - وكان أراه الثوري - إذ ذكر عمرًا أفتن فيه، فأثنى عليه. ورأيت أحمد وذكر رياح الصنعاني قد دمعت عيناه، وذكر من فضله وزهده، وقال: كان ابن المبارك يقول: حدثني رياح ورياح رياح. قال أحمد: الشاميون يقولون: عبد الرحمن بن عرزب، وهو أصح.','3','1238',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1059,'قلت: حديث شعبة، عن فراس، عن الشعبي، عن أبي بردة، عن أبي موسى، عن النبي - صلى الله عليه وسلم -: ((ثلاثة يدعون الله فلا يستجيب لهم)) قال: ليس هو عندنا مسندًا، وحدثنا غندر غير مسند. قال أبو محمد: حدثنا عبيد الله بن معاذ، عن أبيه، عن شعبة مسند. وحدثنا أحمد قال: ثنا هاشم بن القاسم قال: ثنا محمد بن طلحة، عن سلمة بن كهيل قال: وصف ذر الإرجاء وهو أول من تكلم فيه،','3','1239',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1060,'ثم قال: إني أخاف أن يتخذ هذا دينًا.
فلما أتته الكتب من الآفاق قال: سمعته يقول بعد: وهل أمر غير هذا؟ قلت: عبيد الله بن الوليد الوصافي كيف حاله؟ قال: ما أدري كيف هو، وذكر أن وكيعًا قد روى عنه شيئًا وابن المبارك. وسئل عن أبي حريز، فذكر ان يحيى كان يحمل عليه، وقال: ولا أراه إلا كما قال يحيى. وسمعته يقول: كان أبو لبيد يرى القدر، وكان صالح الحديث. وكان أبو قرة رجلًا صالحًا هو من أهل اليمن. سمعت أبا عبد الله يقول: ليس أحد أصح سماعًا من حصين بن عبد الرحمن من هشيم، وقال: هو أصح من سفيان وكأنه قال: إن','3','1240',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1061,'حصينًا تغير بآخره. وسمعته يقول: همام صالح الحديث. قال: وكان يحيى بن سعيد يحمل على همام حتى قدم معاذ بن هشام فوافق همامًا في أحاديثه. قال أحمد: أصحاب قتادة: شعبة، وسعيد، وهشام، إلا أن شعبة لم يبلغ علم هؤلاء كان سعيد يكتب كل شيء. قال أحمد: وذكر عفان حدثنا، فقال: أصاب همام، وأخطأ هشام وسعيد، والحديث هو: همام، عن قتادة، عن شريك بن خليفة، عن عبد الله بن عمرو، قال سعيد: عن قتادة، عن أبي أيوب، عن عبد الله بن عمرو، قال هشام: عن قتادة عن شريك بن خليفة، عن ابن عمر، قال: فوافقه سعيد بن عبيد الله بن عمرو، ووافقه هشام في شريك بن خليفة. قال: وأصاب همام وأخطأ كلا هما. حدثنا أحمد، قال: ثنا عبد الرحمن بن مهدي، عن همام، عن قتادة، عن شريك','3','1241',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1062,'بن خليفة قال: سألتُ عبد الله بن عمرو: آكل وأنا جنب؟ قال: توضئ وضؤك للصلاة ثم كل إن شئت. حدثنا أحمد، قال: ثنا يحيى بن سعيد، قال: حدثنا ابن زبي عروبة، عن قتادة، عن أبي أيوب، عن عبد الله بن عمرو قال: الجنب إذا أراد أن يطعم توضأ. حدثنا أحمد، قال: حدثنا عبد الرحمن بن مهدي وبهز قالا: حدثنا همام، عن قتادة، عن شريك بن خليفة قال: سألتُ عبد الله بن عمرو: أغسل','3','1242',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1063,'رزسي ولحيتي بالطيب وأنا جنب ثم أغتسل؟ قال: أمسه الماء. حدثناأحمد قال: حدثني علي بن عبد الله قال: ثنا معاذ بن هشام قال: حدثني أبي، عن قتادة، عن شريك أنه سأل عبد الله بن عمر قال: أرأيت إن اغتسلت من الجنابة وأخرت رأسي حتى أغسله بالطيب أيجزئ ذلك عني؟ قال: لا حتى تغسله بالماء، قال معاذ: في كتاب أبي ابن عمر. قال أحمد: قال عفان: أصاب همام، وأخطأ هشام وسعيد. قال حرب: حدثنا أحمد عن علي قبل المحنة. قيل لأحمد: عطا بن السائب أحد إليك زو حصين بن عبد الرحمن؟ قال: كلا هما ثقتان مأمونان.','3','1243',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1064,'قلت: يحيى بن الحرار سمع من علي؟ قال: لا. حدثنا أحمد قال: ثنا يحيى قال: حدثنا شعبة، عن الحكم قال: كان يحيى بن الحرار يغلو. يعني في التشيع. وسئل عن أبي الزبير فقال: قد احتلمه الناس. قال: وأبو الزبير أحب إليّ من أبي سفيان. وسمعته يقول: كان القعنبي من خيار أهل الإسلام.
قيل لأحمد: إذا اختلف سالم ونافع في ابن عمر المعنى من أحب إليك؟','3','1244',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1065,'قال: ما أتقدم عليهما. قال أحمد: كان في كتاب محمد بن عبيد في حديث إسماعيل بن أبي خالد عشرة أحاديث خطأ، وكان يعلى أثبت منه، وكان محمد رجلا صدوقًا. ولم ندرك بالكوفة أحدًا يروي عن إبراهيم بن مهاجر إلا عمر بن عبيد. قال أبو محمد: وسمعت إسحاق يقول: كان ولد عبيد ثلاثة كلهم مختلفون كان محمد بن عبيد صاحب سنّة، وكان يعلي شيعيًا بحتًا، وكان عمر بن عبيد أكبرهم. قال أبو محمد: وسمعت إسحاق يقول: وكان عمر بن عبيد يقول: معاوية كان أعدل في قتل الناس من علي. قال أبو محمد: ولم أدخله في الكتاب؛ لأن هذا يدخل فيه تفضيل معاوية على علي. قال أحمد: هو محرس الكعبي بفتح الراء.','3','1245',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1066,'قلت لأحمد: هو ابن مزاحم، أو ابن أبي مزاحم؟ قال: لا أدري الساعة. وكأنه ذهب إلى أنه ابن أبي مزاحم، وسمعته قبل ذلك يقول: مزاحم بن أبي مزاحم. وقال في حديث يروى عن رجل يقال له عبد الله القرشي: ((مالك يا عائشة حشيًا رابية)) وكان عبد الرزاق يصحف فيه يقول: حسبي. أو كلمة نحو هذا. سئل أحمد: الزهري عن سالم عن أبيه أحب إليك. أو هشام بن عروة عن أبيه عن عائشة؟ قال: كلاهما.','3','1246',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1067,'وسمعته يقول: عبد الرحمن بن غنم قد أدرك النبي - صلى الله عليه وسلم - ولم يسمع منه. سئل عن معاوية بن حديج سمع من النبي؟ فسكت، وقال: بريدة بن ُحصيب الأسلمي من خزاعة. قال أحمد: الحسن لم يسمع من ابن عباس وابن سيرين أيضًا، لم نخبر عنه سماعًا من ابن عباس. وقال: الحسن ولد بالمدينة، ثم صار إلى البصرة بعد. قال أحمد: قال يحيى: أهل الكوفة سمعوا من عبيد الله بالكوفة، وأظنه قال: ويحيى بن سعيد سمع منه بالكوفة. قال أحمد: قال أبو عاصم: قدمت المدينة سنة ست وأربعين وقد مات عبيد الله، قال أحمد: مات قبل سنة خمس وأربعين. قال أبو عبد الله: يقال: إن قتادة لم يسمع من سليمان بن يسار، يقولون: بينهما أبو الخليل، ولم يسمع من مجاهد ويقال: بينهما','3','1247',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1068,'أبو الخليل، قال أبو عبد الله: إبراهيم بن طهمان صالح الحديث وأثنى عليه، ولكنه كان يتكلم في الإرجاء. وقال: بشير بن مهاجر يقولون: كان مرجئًا، ولكنه صالح الحديث. قال أبو محمد: وحدثنا أحمد بن سعيد الدارمي قال: يقولون: كان حماد وزر، وعمر بن ذر يقولون بالإرجاء، ويغلون فيه، وكان أبو حنيفة يقول به ويغلي، وكان مسعر يقول به وليس بذلك الغالي، وكان علقمة بن مرثد وقيس بن مسلم وعمرو بن مرة يقولون بالإرجاء ولا يفرطون، وكان عبد العزيز بن أبي رواد يقول به بمكة ويفرط، وكان سالم الأفطس يقول بالإرجاء وإبراهيم التيمي كان','3','1248',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1069,'يرى الإرجاء بالكوفة، وطلق بن حبيب بصري كان يرى الإرجاء.
قال أبو عبد الله: عبد الوارث أصح الناس حدثنا عن حسين المعلم قال: ويقولون: سليمان بن بريدة أثبت. حدثنا عن أخيه عبد الله بن بريدة. قال: سليمان بن بلال صالح الحديث جدًا، وهو أحب إلي من الدراوردي، وقال: روى الدراوردي منكرات. وسمعته يثني على أبي لبيد السرخسي ثناءً حسنًا جدًا.','3','1249',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1070,'قال أبو عبد الله: سهيل بن أبي صالح ما أصلح حديثه!، قال: وكان يحيى يقدم عليه محمد بن عمرو. وقال أحمد: ليس كما قال. قال أحمد: والعلاء بن عبد الرحمن عندي فوق سهيل وفوق محمد بن عمرو. قال: أحمد: لم يحدث - أراه يعني العلاء. حدثنا أبو بكر من حديث أبي هريرة عن النبي عليه السلام: ((إذا كان النصف من شعبان)) وأنكر أحمد الحديث وقال: كان عبد الرحمن بن مهدي لا يحدث بهذا الحديث. قال أبو عبد الله: وأبو الزناد فوقهم كلهم وأظنه قال: كان سفيان يسميه أمير المؤمنين.','3','1250',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1071,'قال أبو محمد: قال علي بن عبد الله: سمعت يحيى وسئل عن سهيل بن/٢٣٩/أبي صالح ومحمد بن عمرو بن علقمة فقال: محمد بن عمرو أعلى منه، قلت: حديث يرويه خالد الطحان، عن سهيل، عن الزهري، عن عروة عن أسماء بنت عميس في غسل المستحاضة؟ قال: هذا مقلوب. قال أبو محمد: وذكرت لعلي بن عبد الله حديث خالد الواسطي عن سهيل، عن الزهري، عن عروة، عن أسماء بنت عميس في غسل المستحاضة. فقال: نعم، أخاف أن يكون هذا وهم، لأن الناس رووه على غير هذا، قال: وقد وهم خالد في حديث جابر بن عبد الله في دخول المسجد أن الناس يروونه','3','1251',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1072,'عن أبي قتادة عن النبي - صلى الله عليه وسلم -. قلت لأحمد: مالك بن أنس أحسن حديثًا عن الزهري أو سفيان بن عيينة؟ قال: مالك أصح حديثًا. قلت: فمعمر أحسن حديثًا أو مالك؟ فقدم عليه مالكًا، إلا أن معمرًا أكثر. قال أحمد: ويونس أكثرهم؛ لأن يونس كتب عن الزهري كل شيء. قال: وسمعت عثمان بن عمر يقول: قال يونس: ليس أحد أروى عن الزهري من عقيل. قلت لأحمد: فصالح بن أبي الأخضر؟ قال: كان يحيى لا يسم به. قال أحمد: شعيب بن أبي حمزة أصح حديثًا عن الزهري من يونس.','3','1252',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1073,'وسئل عن صالح بن كيسان. فقال: بخ بخ. قال أبو محمد: وحدثنا أبو بكر الحميدي، قال: ثنا سفيان، قال: ثنا الزهري، قال: ثنا أحمد بن سليمان بن يسار وأبو سلمة بن عبد الرحمن، عن أبي هريرة أن رسول الله - صلى الله عليه وسلم - قال: ((إن اليهود والنصارى لا يصبغون مخالفوهم)).
قال سفيان: فلما خرجنا من عند الزهري جلس أيوب السختياني وإسماعيل بن أمية، وإسماعيل بن مسلم، وأشعث بن سوَّار، والهذلي في عدة من الفقهاء، فقالوا: تعالوا نتذاكر ما سمعنا من الزهري، فجلسوا وجلست معهم، فقال أيوب: يا هلي أنتم أما سمعتموه يقول: أخبرني سالم بن عبد الله، أخبرني','3','1253',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1074,'عبد الله بن عبد الله، أخبرني حمزة بن عبد الله، أخبرني أبو بكر بن عبيد الله بن عبد الله يدور على ولد عبد الله كأنه أعجبه ذلك. ثم تذاكروا ما سمعوه فذكروا هذا الحديث: ((إن اليهود والنصارى لا يصبغون)) فقال بعضهم: هو عن أبي سلمة، وقال بعضهم: هو عن سليمان. فلما أكثروا قلت وأنا أصغرهم: عن كلاهما، فضحكوا من لحنتي، فقال إسماعيل: هو كما قال الصغير أحفظكم: هو عن كلاهما. وقال في ذلك المجلس أشعث بن سوار: ما جاء/٢٤٠/الزهري بشيء إلا قد سمعناه من أصحابنا بالكوفة. فمقته القوم حتى استبان لي ذلك، فمن يومئذ مقته، فلم أرو عنه شيئًا. قلت لأبي عبد الله: الشكوك التي في حديث المعتمر عن أبيه ممن هي؟ قال: من التيمي. قال أبو محمد: قال علي: سمعت يحيى بن سعيد يقول: كان التيمي من أهل الحديث عندنا، وقال: ما جلست إلى رجل أخوف لله منه. يعني التيمي. لم أسمعه من علي.','3','1254',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1075,'قيل لأحمد: حديث جابر بن زيد بن الأسود، عن أبيه عن النبي - صلى الله عليه وسلم - صلى الفجر فانحرف؟ قال: رواه سفيان، ورواه هشيم، وسمعته من يعلي، ولكنه لم يسمع منه انحرف، فكان هشيم إذا قيل له: انحرف؟ قال: نعم، ولم يسمعه. قيل: حديث ابن عمر عن النبي - صلى الله عليه وسلم -: ((من حلف فقال إن شاء الله)) قال: رفعه أيوب وخالفه الناس عبيد الله وغيره.','3','1255',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1076,'قال: يزيد الفارسي روى عنه عوف، وهو يزيد بن هرمز وكان كاتب ابن عباس. حدثنا أحمد قال: ثنا عبد الصمد قال: ثنا سلام - يعني: ابن مسكين- قال: حدثنا عون بن ربيعة، عن يزيد الفارسي. قال: وكان كاتبًا لابن عباس. وسئل أحمد عن حماد بن شعيب؟ فقال: لا أدري كيف، هو علي بن علي اللهبي حدثني روى منكرات.','3','1256',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1077,'وقال أحمد: حماد بن يحيى الأبح هو بصري لا بأس به، وعلي بن علي الرفاعي لم يكن به بأس. قلت: عمر بن علي المقدم، كيف كان؟ قال: لا أعلم إلا خيرًا، لم يكن به بأس، وكان شيخًا عاقلا. أبو الربيع السمان اسمه أشعث .. قال أحمد: كان يحيى لا يروي عن إسرائيل، وقد سمع منه، قال أحمد: وحديث إسرائيل حديث أهل الصدق، وهو أحب إلي من شريك، ولكن كان شريك أقدم سماعًا عن أبي إسحاق منه. قال أبو عبد الله: صدقة بن خالد شيخ ثقة أراه شامي.','3','1257',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1078,'قيل لأحمد: حديث سليمان بن موسى، عن الزهري، عن عروة، عن عائشة، عن النبي عليه السلام: ((أيما امرأة نكحت بغير ولي فنكاحها باطل))، قال: هذا لا يصح؛ لأن الزهري سئل عنه فأنكره، وعائشة زوجت حفصة بنت عبد الرحمن بنت أخيها والحديث عنها فهذا لا يصح. قلت لأحمد: قد روى من غير هذا الوجه؟ قال: ما هو هشام بن سعد؟ قلت: نعم، فلم يرض هشام بن سعد. قلت: فأي شيء يصح في هذا: ((لا نكاح إلا بولي))؟ قال: لا أعلم','3','1258',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1079,'شيئًا يصح عن النبي - صلى الله عليه وسلم - حديث أبي موسى يضطربون فيه، شعبة يقول: عن أبي بردة، وإسرائيل يقول: عن أبي موسى. قلت: سفيان يقوله/٢٤١/عن أبي بردة؟ قال: نعم، فلم يصحه، قال: ولكنه يروى عن عمر بإسناد صحيح، وعن ابن عباس أنه لا يجوز النكاح إلا بولي، قال: فأنا أذهب إليه. سئل أبو عبد الله عن علي بن زيد قال: قد روى الناس عنه. قلت: يا أبا عبد الله أليس كان يتشيع؟ قال: نعم شديدًا كان يغلي، وكذلك عمار الدهني كان يغلي، ويونس بن خباب أشدهم. قيل لأحمد: الحكم سمع من أبي عبد الله الجدلي؟ قال: قد صلى خلفه، وأراه قد سمع. قيل: كثير بن فرقد روى عنه غير الليث؟ قال: نعم روى عنه مالك. قال أحمد: سعيد بن أبي عروبة تغير في آخره تغيرًا شديدًا وبقي بعد هشام.','3','1259',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1080,'قال أبو محمد: سمعت إسحاق يقول: كان عمرو بن خالد الواسطي يضع الحديث، وكان أبو حنيفة يروي عنه. قال: سمعت إسحاق يقول: وسمعت أبا وهب عن ابن المبارك أنه ذكر يومًا أبا حنيفة فقال: لقد كان يتيمًا في الحديث. حدثنا محمد بن يحيى قال: ثنا أبو قدامة قال: سمعت سلمة بن سليمان قال: قال رجل لابن المبارك: كان أبو حنيفة عالمًا بالحديث؟ قال: ماكان بخليق لذاك، ترك نافعًا وروى عن أبي العطوف.','3','1260',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1081,'قال أبو محمد: وحدثنا أبو حفص الدارمي قال: قال عارم: قال ابن المبارك: يا طالب العلم ائت حماد بن زيد تقتبس منه علمًا ثم قيده بقيد. قلت لأحمد: شيخ يقال له: عمر بن إبراهيم، تعرفه؟ قال: نعم ثقة، لا أعلم إلا خيرًا. قلت: فشيخ يقال له: هاشم بن سعيد، يروى عن كنانة، عن صفية. روى عنه شاذًا، قال: ما أعرفه.','3','1261',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1082,'قال أحمد: يحيى بن سعيد الذي روى عنه يزيد بن هارون وحماد وغيرهما هو شيخ ثقة مأمون من أهل العلم، وهو يحيى بن سعيد بن قيس بن قهد، وأخوه عبد ربه بن سعيد. قلت: حديث ابن أبي حثمة ورافع بن خديج في القسامة أليس الحديث حديث يحيى بن سعيد عن بير بن يسار، رواه حماد بن زيد؟ قال: نعم. قال أحمد: الشيباني لم يرو عن النخعي؛ لأنه غاب فقدم، وقد مات إبراهيم. قلت: يروى عن معاوية عن النبي - عليه السلام - في ليلة القدر شيء؟ قال: أما في كتاب غندر وغيره من أصحاب شعبة فليس هو مرفوعًا، وبلغنا أن معاذ بن معاذ رفعه.
قلت: نعم قد رفعه معاذ كتبته عن ابنه من','3','1262',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1083,'أصل كتابه فكأنه لم ينكره. قال أحمد: دخلنا الكوفة ولم نر بها أحدًا أفضل من ابن ادريس. قلت: حديثه/٢٤٢/طلحة بن مصرف عن أبيه عن جده؟ قال: فأي شيء يروى هذا. كأنه لم يره شيئًا، وذكر أن سفيان كان ينكر ذلك. قال أحمد: قد عددت عشرة رجال بين قتادة، وبين سعيد بن المسيب، يروي عنهم قتادة، عن سعيد أحاديث. كثير عن أبي عياض؟ قال: أبو عياض هو مكي.','3','1263',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1084,'سمعته يقول: مخرمة بن بكير رجل صدق، ولم يسمع الكتب من أبيه، أخرج إليهم الكتب فقال: لم أسمعها. قال: وأبو بكر: شيخ صالح ثقة. قال أحمد: معافا بن عمران كان شيخًا كبيرًا له قدر وحال، وجعل يعظم أمره، وكان رجلا صالحًا. قال أحمد: ما رأيت في هذا الشأن - يعني الحديث والأخبار والإسناد - مثل يحيى القطان قط. قال: أبو حازم مولى عياش، اسمه نبتل.','3','1264',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1085,'قال: وعمرو بن مرة تكلم في الإرجاء في آخر أمره. قال أبو عبد الله: قيس بن سعد الذي روى عنه حماد بن سلمة ثقة، ويقولون: إن حمادًا ضاع كتابه عن قيس. حدثنا أحمد قال: ثنا عفان قال: ثنا حماد بن سلمة قال: قدمت في رمضان، وعطاء بن أبي رباح حي، فقلت: إذا أفطرت ودخلت عليه، فمات في رمضان، وكان ابن أبي ليلى يدخل عليه، فقال لي عمارة بن ميمون: الزم قيس بن سعد فإنه أفقه من عطاء. قال أبو عبد الله: راشد بن سعد لم يسمع من ثوبان. ابن أبي ذئب رجل صالح ثقة.','3','1265',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1086,'قلت: شيخ يقال له: المعارك بن عباد تعرفه؟ قال: ما أعرفه. قلت لأحمد: الحسن بن صالح كيف حديثه؟ قال: ما له، ما أحسن حديثه، ووثقه. قلت: يقال: إنه يتشيع قال: قد كان. قلت: فأبو هـ؟ قال: ثقة ثقة. ولم يذكر عنه شيعًا. قلت: فأخوه؟ قال: ثقة، وذكر أنه قديم الموت واسمه علي بن صالح. قال أبو محمد: وحدثنا عبد العزيز، قال: ثنا يوسف بن موسى، قال: سمعت أبا داود يقول: كان هشام الدستوائي أمير المؤمنين في الحديث،','3','1266',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1087,'وسمعت أبا داود يقول: قال لنا شعبة: ارتحلوا إلى قيس قبل أن يموت، وسمعت أبا داود قال: سمعت شعبة قال: سمعت أبا حصين يثني على قيس. حدثنا أبو داود، قال: قال شعبة حين أردت الخروج من عند ورقاء: لا والله لا تلقى مثل ورقاء حتى ترجع. وسمعت أبا داود يقول: قال شعبة: إذا خالفني سفيان في الحديث فالحديث حديثه. قال أبو داود: وسمعت شعبة يقول: من كتبت عنده ثلاثة أحاديث فأنا أبدًا له عبد واخضع له. /٢٤٣/وحدثنا أحمد بن يونس، قال: ثنا زائدة، عن مغيرة، عن إبراهيم قال:','3','1267',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1088,'إنهم يعني: الحارث، قال أحمد: كذب الحارث ولذلك اتهم وسمي كذابًا. قال أبو محمد: لقوله: تعلمت الوحي في ثلاث سنين. فاتهم لذلك. قال أحمد بن يونس: ما رأيت أحدًا في الدنيا أفضل من يوسف بن أسباط وقد مات، وما أراه خلف في الدنيا مثله.
سمعت أبا عبد الله أحمد بن حنبل يقول: إياس بن دَغْفَل شيخ بصري ثقة. قال أحمد: وما أعلم قتادة روى عن أحد من أصحاب النبي - صلى الله عليه وسلم - إلا عن أنس. قيل: فأين سرجس؟ فكأنه لم يره سماعًا.','3','1268',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1089,'قلت لأحمد: فشيخ يقال له: دَغْفَل بن حنظلة له صحبة يروي عنه قتادة؟ قال: ما أعرفه. قال حرب: وقد حدثنا أحمد يومًا آخر قال: حدثنا معاذ بن هشام قال: حدثني أبي، عن قتادة، عن الحسن، عن دغفل بن حنظلة، أن النبي - صلى الله عليه وسلم - قبض وهو ابن خمس وستين. سئل أحمد عن الوليد بن جميع، قال: ما أعلم إلا خيرًا. قيل: فسعيد بن عبيد الطائي؟ قال: ثقة. قال: ومحمد','3','1269',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1090,'بن قيس أيضًا ثقة، وأبو عاصم الثقفي مقارب لهؤلاء. قلت: جميع بن عمير كيف حديثه؟ قال: لا أعلم إلا خيرًا، روى عنه الصلت بن بهرام. قلت: الصلت بن بهرام كيف هو؟ قال: شيخ ثقة. قلت: فعبد الحميد بن بهرام؟ قال: شيخ ثقة. قلت: أين كان يكون؟ قال: بالمدائن في بعض السواد. قلت: شهر بن حوشب؟ قال: ما أحسن حديثه، ووثقه، هو شامي من أهل حمص. وأظنه قال: كندي، وسمع من أم سلمة وابن عباس، وروى عن أسماء بنت يزيد أحاديث حسان.','3','1270',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1091,'وسألتُه عن أبي المهرم، قال: ما أقرب حديثه اسمه يزيد بن سفيان قال أبو محمد: حدثنا أحمد بن سعيد، قال: قال مسلم بن إبراهيم، قال شعبة: رأيت أبا المهرم مطروحًا في مسجد ثابت لو أعطاه إنسان فلسين حدثه بسبعين حديثًا. قال أبو عبد الله: كان بواسط مشايخ من أهل الشام: يزيد بن حمير، وأبو الجُودي، وشيخ يقال له: أبو إسرائيل يروي عن جعدة رجل له صحبة روى عنهم شعبة. قلت: بلغك اسم أبي الجُودي؟ قال: لا.','3','1271',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1092,'قال أبو محمد: وحدثنا أبو جعفر الدارمي قال: حدثنا أحمد بن سليمان قال: قال أبو بدر: قال سفيان: كان عند طلحة نحو من خمسمائة حديث، وعند زبيد مائة حديث. وحدثنا أبو جعفر قال: ثنا أحمد بن سليمان قال: ثنا/٢٤٤/ابن علية قال: ثنا ابن عون، عن محمد قال: لو يعلم أيوب من حميد بن هلا ل ما أعلم ما حدث عنه. قال إسماعيل: قال لي شعبة: كان حميد على السوق أو غيرها فأنشأ السيرة. قال إسماعيل: وحدثنا ابن عون قال: كان ربيعة الرأي إذا تكلم عند','3','1272',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1093,'القاسم بن محمد وصف إسماعيل أنه يضع يده على خده فيستمع لحديثه. قلت لأبي عبد الله: محمد بن فضيل كان يتشيع؟ قال: نعم كان يتشيع، وكان حسن الحديث. قلت: فأبو هـ؟ قال: أبو هـ ثقة. قلت: عمرو بن ثابت؟ قال: لا تكتبن حديثه. قلت: فأبو هـ؟ قال: أبوه ثقة، ثابت أبو المقدام روى عنه الحكم. حدثنا أحمد قال: ثنا مؤمل قال: حدثنا سفيان قال: ثنا عبد الملك بن أبي بشير قال أحمد: هو من أهل المدائن وكان شيخًا صالحًا. قال سفيان: وكان شيخ صدق.','3','1273',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1094,'ولواقد ولحيكم بن الديلم كان شيخًا صالحًا.
قلت لأحمد: حديث علقمة؟ قال: قلنا لابن مسعود هل صحب النبي - صلى الله عليه وسلم - أحد منكم ليلة الجن؟ قال: لا، ولكنا فقدناه. أليس يرد قول من قال: الوضوء بالنبيد جائز؟ قال: نعم، وذاك ليس له إسناد. قلت: فحديث ابن عباس حيث قال: وهن يمشين بنا هَمِيسًا.','3','1274',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1095,'يختلفون في إسناده، بعضهم يقول عوف، عن زياد بن حصين، عن أبيه، وبعضهم يقول: زياد بن حصين، عن أبي العالية قال: الناس يختلفون في إسناده. قلت: فأيها أصح؟ قال: يختلفون في إسناده ما أدري. قلت: شيء يرويه ابن المنهال من حديث يزيد بن زريع، عن معاوية بن أبي سفيان أن النبي - صلى الله عليه وسلم - قال: ((لا تعلموا الصبي والمرأة والعبد القرآن)) فأنكره وقال: ما أنكر هذا من حديث. قيل: فإذا كان الحديث عن ثابت، وأبان، عن أنس يجوز أن أسمي ثابتًا وأترك أبان؟ قال: لا تقل في حديث أبان شيء ليس في حديث ثابت، وقال: إذا كان هكذا فأحب إلي أن تسميهما.','3','1275',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1096,'قال أبو عبد الله: صالح بن أبي الأخضر كان من أهل اليمامة. وسمعته يثني على يحيى بن بكير وقال: ما أكيسه في الحديث. قلت لأبي عبد الله: أفترجوا إذا أصاب الرجل معنى الحديث أن يكون الأمر فيه سهلا؟ قال: نعم، ومن يضبط هذا؟ وذكر عن هشيم من هذا عجائب أنه كان يقول: مغيرة عن إبراهيم، ويونس عن الحسن، ونحو هذا فيأتي بلفظ واحد ولعل ألفاظهم قد اختلفت، ورخص فيه أبو عبد الله. /٢٤٥/
حدثنا محمد بن يحيى قال: ثنا عبد الرزاق، عن معمر، عن أيوب، عن ابن سيرين، قال: كنت أسمع الحديث من عشرة المعنى واحد، واللفظ مختلف. حدثنا محمد بن يحيى قال: ثنا أزهر السمان، عن ابن عون قال: كان','3','1276',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1097,'الحسن وإبراهيم والشعبي يحدثون عن المعاني، وكان القاسم ومحمد ورجاء يحدثون كما سمعوا. يعني محمد بن سيرين ورجاء بن حيوة. قال أحمد: لا يضم رجلا إلى معمر في العلم إلا وحدث معمرًا أكتب منه وأطلب للعلم منه. قال أبو محمد: وسمعت أبا بكر محمد بن بشار يقول: كان معاذ بن معاذ قد جمع خصال السنّة كلها. سمعت أبا بكر يقول: تركنا حديث عباد بن صهيب قبل أن يموت بعشرين سنة. حدثنا أبو جعفر الدارمي قال: حدثنا النضر بن شميل قال: حدثنا الخليل','3','1277',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1098,'بن أحمد قال: لحن أيوب مرة فقال: أستغفر الله. قال أبو جعفر: وسمعت النضر يقول: ما رأيت أحدًا بعد ابن عون أعلم بالسنّة من الخليل. قال أبو جعفر عبد الرحمن بن عسيلة: أبو عبد الله الصنابحي رجل من أهل اليمن قدم يريد المدينة فلما بلغ الجحفة أخبر بوفاة رسول الله - صلى الله عليه وسلم -، فهاجر إلى الشام ولم يلق النبي - صلى الله عليه وسلم -. قال أبو جعفر: والصنابحي بن الأعسر رجل من بجيلة قدم على النبي - صلى الله عليه وسلم -، وله منه صحبة، وكان بالكوفة روى عنه قيس بن أبي حازم قال: وروى عنه عطاء بن يسار، عن رجل يقال له عبد الله الصنابحي، عن النبي - صلى الله عليه وسلم - حديثين أحدهما في','3','1278',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1099,'الوضوء، والأخر في كسوف الشمس ليس عندنا له صحبة ثابتة ولا نعرفه. قال: وسألتُ المديني عنه، فقال: نحوًا من هذا القول، وليس في الحديث أنه صلى مع النبي - صلى الله عليه وسلم - أو رأى النبي يتوضأ، وهو من أهل المدينة. قلت لأبي عبد الله: أبو عقيل الذي يروي عنه بُهَّية واسمه يحيى بن المتوكل مديني كيف حديثه؟ قال: وأي شيء روى هذا من الحديث يعني من قلته، فكأنه ضعفه','3','1279',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1100,'وقال: بُهيَّة أيضًا لا تعرف من هي. وقال: أبو عقيل الدورقي ثقة. قلت: أبو الزناد صاحب عبد الرحمن بن عوف من هو؟ قال: رجل أظن من أصحاب النبي - صلى الله عليه وسلم -. قلت لأبي عبد الله: حديث رواه ابن عامر عن زمعة، عن هشام بن عروة، عن أبيه، عن عائشة، عن النبي - صلى الله عليه وسلم - /٢٤٦/في النكاح بغير ولي فكأنه ضعف زمعه في هذا المكان في هشام بن عروة. قلت: فحديث حجاج عن الزهري في هذا؟ قال: يقولون إن','3','1280',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1101,'حجاج لم يلق الزهري، وكان يروي عن رجال لم يلقهم، وكأنه ضعفه. قال أبو محمد حرب: حدثنا إسحاق قال: فكان المفضل قال لي الوليد بن جميع: ياتون. -يعني: الحجاج بن أرطأة- وهو جاري ما صلى منذ أربعة أشهر صلا ة في جماعة. قال أحمد: روى ابن عيينة عن جدته، وروى معتمر عن أمه عن أخته.
وذكر أحمد عن هشيم، عن أشعث، عن ابن سيرين في رجل اشترى متاعًا بنسيئة ثم باعه مرابحة. قال له مثل أجله. قال أحمد: قال هشيم: قال حجاج: ائت أشعث فإنه يحدث بحديث فسله. قلت: حديث عمير مولى آل أبي اللحم؟ فلم يقل فيه شيئًا قلت: هو أي اللحم، أو أبي اللحم؟ قال: كله قد قيل. قال أبو عبد الله محمد بن راشد هو مقارب الحديث، قال: وقال عبد الرزاق: ما رأيت أحدًا أورع في الحديث منه -يعني: محمد بن راشد.','3','1281',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1102,'قال: وأرطأة بن المنذر ثقة، وصدقة بن خالد ثقة، وصدقة بن يزيد يضعَّف، وصدقة بن عبد الله ضعيف كانا يكونون الثلاثة بالشام، إلا أن صدقة بن يزيد خراساني. قال أبو عبد الله: الحسن العري لم يسمع من ابن عباس. قال أحمد: حمزة الزيات ثقة في الحديث، وذكر منه صلاحًا، ولا تعجبه قراءته. قال حرب: وسمعت الحميدي يكره قراءة حمزة.','3','1282',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1103,'قلت لأبي حفص: عبد الرحمن بن يزيد أخو الأسود بن يزيد أبوه له صحبة؟ قال: لا، ولكن شيخ يقال له: عبد الرحمن بن يزيد يحدث عن أبيه عن النبي: ((أرقاكم أرقاكم)). قلت: فالقاسم بن عبد الرحمن لقي أحدًا من الصحابة؟ قال: لا، ولكنه يروي عن ابن عمر، ولا شك أنه قد لقيه.','3','1283',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1104,'قال أبو معن: بلغني عن سفيان الثوري أنه قال: لم أر في زمان مرحوم بن عبد العزيز بالبصرة أحدًا يشبهه. قال أبو محمد: وحدثنا أبو جعفر الدارمي قال: حدثنا أحمد بن سليمان قال: قال يزيد بن زريع: أتينا هشام بن حسان فسألناه أن يملي علينا، فأبى علينا، ثم تابعنا فقال: اطلبوا اطراف قال: فأتيته أنا وإسماعيل بن علية وهارون الشامي وأبو عوانة، وسلام بن أبي مطيع وأبو جزي فكان هارون كاتبنا وكان هشام علا علينا وهو يكتب وأنا يمينه، وإسماعيل يسرة يغير/٢٤١/عليه الحرف والشيء، وأبو عوانة وسلام بن مطيع، وأبو جزي ينامون نومًا جيدًا، ثم يقومون ينسخون معنا.','3','1284',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1105,'قال: وسأل سفيان الرأس يزيد بن زريع وأنا شاهد فقال: ما تقول في أبي هلا ل الراسبي؟ قال: لا شيء. قال: ما تقول في البُرى؟ قال: لا شيء. قال: فما تقول في الربيع بن صبيح؟ قال: سمعت منه أشياء لا أدري أين هي مطروحة. قال: ما تقول في حماد بن سلمة وحماد بن زيد أيهما أثبت؟ قال: حماد بن زيد أثبت.
قال: وحدثت عن يزيد بن زريع أنه قال: أفادني علي بن عاصم عن خالد الحداء أحاديث وخالد حي بالبصرة، فأتيت خالدًا فسألتُه عنها فأنكرها كلها ما عرف منها شيئًا، وأفادني ذات يوم حديثًا عن هشام بن حسان، فأتيته فسألتُه فأنكره','3','1285',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1106,'قال يزيد بن زريع: كنت آتي عمران القطان وأنا غلا م أتعلم منه، فلما أتيت ابن أبي عروبة تركته، قال: وكان يخالف عمران كثيرًا في قتادة، قال يزيد: قلت لابن أبي عروبة: يا أبا النصر إن عمران يخالفك كثيرًا؟ فقال: إن عمران بني حفظه على غلط، قال يزيد: ثم أتيت عمران فقلت: يا أبا العوام إن سعيدًا يخالفك كثيرًا؟ فقال عمران: ما كنا نراه عند قتادة. فدخل قلبي من ذاك شيء. فقلت لهشام الدستوائي: يا أبا عبد الله إني ذكرت لعمران سعيد بن أبي عروبة فقال: لم أره عند قتادة. فقال: صدق عمران إن سعيد أفرغ من قتادة من قبل أن يغشى عمران قتادة. سمعت أحمد بن حنبل يقول: كان المسيب بن شريك صدوقًا، وكان صاحب سنّة، وكان يسهل في النبيذ، وكأنه ذهب إلى أنه كان ربما أخطأ في الحديث، وهو خراساني. قال: كان مقاتل بن سليمان بلخي. قلت: مستقيم بن عبد الملك مكي؟ قال: نعم هو مكي، وكان يضعف في الحديث قال: ويقال له أيضًا عثمان.','3','1286',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1107,'قال: وكان ابن شوذب بلخي ونزل البصرة وتأدب بها، ونزل الشام، وكان ضمرة كثير الرواية عنه. قلت: هشام بن حسان أقدم أو هشام بن أبي عبد الله؟ قال: هشام بن حسان أقدم، وكان رجلا صالحًا ثقة. وسمعت أحمد يقول: كان يونس بن عبيد يأخذ عن أشعث الحمراني يقولون: أشياء. يقول يونس: نبئت عن الحسن أخذها عن أشعث، ووثقه. قيل: فأشعث الحمراني أحب إليك/٢٤٨/، أو أشعث بن سوار؟ قال: أشعث الحمراني وفضله. وسئل أحمد عن حسان بن إبراهيم الكرماني فقال: ما أقرب حديثه، وحديثه حديث أهل الصدق.','3','1287',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1108,'وسئل عن الوضين بن عطاء؟ قال: قد روى الناس عنه، وكأنه ضعفه. وسئل عن تمام بن نجيح؟ فأظنه قال: ما أعرفه، أو ما أعلم إلا خيرًا. وسئل عن مروان بن شجاع؟ فقال: لا بأس به هو جزري. وقال: نصر بن باب ثقة. قال أبو عبد الله: يقال: إنه ليس أحد أروى من حماد عن ثابت ولا أثبته. سئل أحمد عن داود بن صالح التمار؟ فقال: لا أعلم به بأسًا. قيل: فابن أبي الموال؟ قال: وكم حديث ابن أبي الموال، وقال:','3','1288',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1109,'روى حديثًا لم يروه أحد. يعني حديث الاستخارة عن جابر، وكان يضعفه. قلت: فابن أبي الرجال؟ فقال: لا أعلم به بأسًا. قلت: فأبو الرجال؟ قال: أبو الرجال ثقة. قال أحمد: كان ابن أبي الموال محبوسًا في المطبق ثم خلي عنه ورجع إلى المدينة. قال أحمد: قال ابن عيينة ولم أسمعه: رجلان يستسقي بهما ابن عجلان ويزيد بن يزيد بن جابر.','3','1289',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1110,'قيل لأحمد: ابن عيينة أكثر في عمرو بن دينار، أو ابن جريج، فقال: عند ابن عيينة عن عمرو ما ليس عند أحد كثرة. قال أحمد: وقال شعبة: لا قتادة ولا الحكم مثل عمرو بن دينار. قال أحمد: داود بن قيس ثقة، وهو فوق هشام بن سعد. قلت: الحكم بن ظهير كيف حديثه؟ فكأنه ضعفه. قلت: فالهيثم بن جمَّاز فكذلك قال: وهو بصري.','3','1290',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1111,'قلت: فعبد الله بن بجير؟ قال: ثقة. وحدثنا أبو بكر الحميدي، قال: قال سفيان: كان لفظ الزهري إذا حدثنا عن أنس سمعت. قال: وكان لفظ الزهري إذا حدثنا عن سهل - يعني: ابن سعد- سمعت. قال: وكان لفظ الزهري إذا حدثنا عن السائب - يعني: بن يزيد- سمعت وأخبرني. وذكر الحميدي حديث عائشة: ألم تر أن مجزرًا المدلجي فقال: قال سفيان: كان ابن حريج حدث به عن الزهري فقال: ألم تر أن محزرًا المدلجي فقلت: يا أبا الوليد إنما هو مجزز فانكسر ورجع، وذكر حديث أنس: فجحش شقه، فقال: قال سفيان سمعت ابن جريح يحدث به عن الزهري فقال فيه: فجحش ساقه الأيمن','3','1291',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1112,'فقلت له: أبا الوليد إنما هو فجحش شقه الأيمن فرجع. حدثنا الحميدي، قال: حدثنا يوسف بن الماجشون/٢٤٩/، عن ابن شهاب قال: كنت إذا حدثني عروة، ثم حدثني عمرة يصدق عندي حديث عروة حديث عمرة، فلما تبحرتهما إذا عروة بحر لا ينزف. سمعت أبا عبد الله محمد بن أبي خالد قال: رأيت فيما يرى النائم أن النبي - صلى الله عليه وسلم - مع عمر بن الخطاب وعلي بن أبي طالب، ورأيت أبا يعقوب الحصري - شاب من أهل الحديث ناسك- وقع في خده الأكلة فمات، فقلت: يا أبا يعقوب ما أنزلك هذه المنزلة أغبطه بموضعه، فقال النبي عليه السلام: غفر الله له بتوحيده وتجاوز عنه ديونه بصلا ته، وجعله رفيقي في الجنة مما ابتلاه. قلت: يا رسول الله حديث عبد الله بن مسعود حدث عنك قال: حدثنا رسول الله وهو الصادق المصدوق','3','1292',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1113,'أريد حديث القدر. قال: أنا والله الذي لا إله إلا هو حدثته به. أنا والله الذي لا إله إلا هو حدثته به. أنا والله الذي لا إله إلا هو حدثته به، غفر الله للأعمش كما حدث به، وغفر الله لمن حدث به قبل الأعمش، وغفر الله لمن حدث به بعد. قال أبو عبد الله: وسألني عنه غير واحد فذكر ابن داود وأبا عاصم النبيل وأبا الوليد وغير واحد، وأنا رأيت أبا إسحاق الرمادي. وحدثنا أبو حفص قال: ثنا أبو معاوية قال: ثنا الأعمش، عن زيد بن وهب قال: رأيت عمر بال قائمًا.','3','1293',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1114,'حدثنا أبو حفص قال: ثنا يحيى قال: ثنا سفيان قال: حدثني حبيب بن أبي ثابت، عن زيد بن وهب قال: رأيت عليًا بال قائمًا. قال أبو حفص: الحديث الصحيح حديث علي، وحديث زيد بن وهب عن عمر أخاف أن يكون وهمًا. وسمعت أبا حفص يقول: أبو الرداد حدثني قال: ولم أسمع أحدًا سماه باسمه.
قال: والمغيرة الذي روى عن إبراهيم هو مغيرة بن مقسم ضبي وكان ضريرًا. قال: وسمعت الأفطس يقول: سمعت مُحل يقول: كنا أربعة عميان نتعاود إلى إبراهيم أنا ومغيرة وشباك وعبيدة.','3','1294',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1115,'قال: ومنصور بن زاذان أصله واسطي، ومنصور بن المعتمر كوفي من بني سليم. قال أبو حفص كان للحسن ابن أبي الحسن أخوان: سعيد بن أبي الحسن، وعمّار بن أبي الحسن. حدثنا أبو معن، قال: حدثنا مؤمل، قال: حدثنا حماد بن سلمة، عن سماك بن حرب قال: لقيت ثمانين من أصحاب النبي - صلى الله عليه وسلم -. حدثنا أبو جعفر الدارمي، قال: حدثنا أحمد بن سليمان قال: ثنا سعيد بن محمد الوراق، عن إسماعيل بن أبي خالد قال: رأيت خمسة رجال','3','1295',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1116,'كلهم رأى النبي - صلى الله عليه وسلم -: أنس بن مالك، وعمرو بن حريث، وعبد الله بن أبي أوفى، وأبو جُحيفة، وقيس بن عائذ. حدثنا أبو جعفر قال: حدثنا أحمد بن سليمان قال: ثنا عبد الله بن إدريس قال: سمعت ليثًا يذكر عن طاوس قال: أدركت من أصحاب رسول الله - صلى الله عليه وسلم - سبعين شيخًا كانوا إذا تدارءوا في الأمر انتهوا فيه إلى قول ابن عباس. سمعت محمد بن أبي بكر يقول: كان سفيان بن حبيب يحب المعتزلة','3','1296',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1117,'قال: وكان عبد الوارث أشد وأشد. قلت: فعبد الصمد؟ قال: كان ينافق كان يظهر لا هل السنة أنه منهم، ولهؤلاء أنه منهم. وسمعت أبا معن قال: كان سفيان بن حبيب قدريًا. قال أبو محمد: وقال علي بن عبد الله - ولم أسمعه - قال يحيى كان سفيان بن حبيب عالمًا والله بحديث سعيد بن أبي عروبة. قال أبو حفص: روى عن النبي - صلى الله عليه وسلم - من ربيعة: صحار بن عباس العبدي، وأحمر بن جزء السدوسي، وبشير بن الحضاضية، وطلق بن علي الحنفي، وفرات بن حيان العجلي، وعلي بن شيبان','3','1297',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1118,'الحنفي، وقيس بن النعمان العبدي. وسمعت أحمد بن سعيد الدارمي سمى من روى عن النبي - صلى الله عليه وسلم - من ربيعة قال: الجارود بن إسماعيل العبدي، والأشج عائذ بن المنذر العصري، وعبد الله بن جابر العبد ي، وقطن بن قتادة السدوسي، وبشير بن خصاصية سدوسي، وقتادة بن ملحان قيسي، وصحار','3','1298',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1119,'عبد القيس، وعباد بن شرحبيل الغُبَري، وأحمر بن جزء السدوسي، وعمرو بن ثعلب. حدثنا محمد بن إسماعيل قال: سمعت ابن علية يقول: عمرو بن دينار قهرمان آل الزبير ضعيف. قال محمد: وأما مهاصر فقد روى عنه الناس. حدثنا حيان قال: حدثنا عمر بن يونس قال: حدثنا أيوب بن النجار وكان من أفضل أهل اليمامة. حدثنا عثمان بن طالوت قال: حدثني عبد الله بن أبي الأسود بن أخت عبد الرحمن بن مهدي قال: عاد عبد الرحمن بن مهدي','3','1299',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1120,'أبا سلمة وأنا معه قال: فقال له: يا أبا سلمة كيف حديث كذا وكذا؟ ثم قال أبو عمرو: وسمعت غارم يقول: ما كنا نأتي شيخًا أو ندخل زفافًا إلا رأيت أبا سلمة.
قال أبو عمر: وحدثنا أبو الوليد يحدث عن شيخ في ظهر/٢٥١/كراسة فقال لنا: أفادني أبو سلمة. قال أبو عمرو: وحدثنا إبراهيم صاحب البصري قال: كان أبو داود يقول لنا: ليس أراكم تذكرون قريب أبي عاصم - يعني أبا سلمة - هو موسى بن إسماعيل. سألتُ أبا عبد الله أحمد بن حنبل عن ابن سمعان فقال: هو متروك الحديث. وهو عبد الله بن يزيد بن سمعان، قال أحمد: وكان إبراهيم بن سعد يرميه بالكذب. قال: واجتمع هو وابن إسحاق فكان','3','1300',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1121,'يقول: قال مجاهد وسمعت مجاهدًا. فقال: ابن إسحاق: ما رأيت كاليوم، لأنا أكبر منه ما رأيت مجاهدًا ولا سمعت منه. حدثنا إسحاق، قال: حدثنا يحيى بن آدم، قال: حدثنا ابن إدريس قال: كنت عند مالك بن أنس فقيل له: إن محمد بن إسحاق يقول: اعرضوا على علم مالك بن أنس فأبى بيطارها. قال مالك: انظروا إلى هذا دجال الدجاجلة يقول: اعرضوا عليه علمه. قال ابن إدريس: وما سمعت أحدًا جمع الدجاجلة قبل مالك. قلت: حيوة بن شريح، قال: ثقة ثقة. قال ابن المبارك: ما وصف لي رجل إلا وجدته دون ما قيل إلا حيوة.','3','1301',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1122,'قال: بكر بن مضر ثقة، وهو مصري، وكان يشبه بفضيل بن عياض في صلا حه. قلت: يحيى بن أيوب المصري؟ قال: صالح وحيوة فوقه. قلت: عبيد الله بن زَحْر؟ فضعفه. قلت: فعلي بن يزيد؟ فقال: هو دمشقي، وكأنه ضعفه. قال: والقاسم هو مولى معاوية. وهذه الأحاديث التي رويت عن هؤلاء عن القاسم منكرة. قال: وأظن ذلك من القاسم جاء الغلط. قلت: فيحيى بن أيوب الكوفي؟ قال: صالح. قلت: مشرح بن هاعان معروف؟ قال: نعم هو معروف، فذكر','3','1302',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1123,'غير واحد من المصريين أنه روى عنه. وسألتُ أحمد عن ابن لهيعة ورشيد؟ فضعفهما، إلا أنه قدم ابن لهيعة. وقال: بكر بن عمرو وابن هبيرة يروى لهم. قلت: جعفر بن الزبير؟ قال: متروك الحديث. قلت: فإن حماد بن سلمة يروي له؟ فضحك، وقال: حماد يدع أحدًا. وقال: وبشير بن نمير متروك الحديث. قال أحمد: نعيم بن جمار يختلفون في اسم أبيه يقول بعضهم: خمار، وبعضهم: همار، وبعضهم: هبار، وأظنه قال: كان أبو نعيم يصحف فيه يقول: حمار.','3','1303',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1124,'قال أحمد: هو حبيب بن حمار قال: وأظن أبا عوانة كان يصحف/٢٥٢/من يقول: جماز. وسمعت أحمد يقول: قتادة لم يسمع حديث عياض بن حمار عن النبي - صلى الله عليه وسلم -، الذي يقول فيه: ((خلقت عبادي كلهم حنفاء)) لم يمسعه من مطرف، وذكر ثلاثة أنه سمعه منهم عن مطرف: أبو العلاء، وعقبه، وذكر رجلا آخر.','3','1304',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1125,'وسمعت أحمد يذكر عمرو بن مرزوق بخير. وسألتُه عن عبد الله بن خباب الذي قتلته الخوارج فقال: هو عبد الله بن خباب بن الأرت، وهو الذي يروي عن أبيِّ. قلت: فعبد الله بن خباب الذي يروي عن أبي سعيد؟ قال: هذا غير ذاك، وكأنه ليس بمشهور.
قال أبو عبد الله: أرى أن ابن عون أكبر من أيوب بسنتين قال: وما كان في زمانهما أحد يعدلهما. يعني أيوب وابن عون. قلت لأحمد: عبد الله بن الوليد العدني كيف حديثه؟ قال: قد سمع من سفيان، وجعل يصحح سماعه، ولكنه لم يكن صاحب حديث، وحديثه صحيح، وكان ربما أخطأ في الأشياء، وقال: قد كتبت أنا عنه كثيرًا.','3','1305',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1126,'وقال: الفريابي سمع من سفيان بالكوفة، وصحبه، وسمع منه، قال أحمد: وكتبت أنا عن الفريابي بمكة. قلت: أسباط بن نصر الكوفي الذي يروي عن السدي كيف حديثه؟ قال: ما أدري، وكأنه ضعفه. وقال: عبد الرحمن بن عمار بن عمار بن أبي زينب شيخ ثقة، روى عنه محمد بن إسحاق. أبو الأحوص الذي روى عنه الزهري حدثني. وقال: أبو الوازع رجل معروف روى عنه شريك وسفيان.','3','1306',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1127,'قال أحمد: يحيى بن عبيد الله الذي يروي عن أبيه عن أبي هريرة قال: ما أعرفه ولا أباه، وكان ابن المبارك يروي عنه، وكان هشيم يروي عنه. وذكر الصلت بن دينار فكأنه ضعفه، ويكنى أبا شعيب. وسمعت أحمد يقول: هبيرة بن يريم بن عبد. قلت: ورقاء أحب إليك في تفسير بن أبي نجيح أو شبل؟ قال: كلا هما ثقة إلا أن ورقاء يقولون: لم يسمع التفسير كله من ابن أبي نجيح يقولون: بعضه عرض، وورقاء أوثقهما. قال: وقد روى يحيى بن أبي بُكير عن شبل شيئًا من التفسير.','3','1307',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1128,'قلت: عباد بن كثير هو بصري سكن مكة، وهو ضعيف في الحديث، روى عجائب منكرات وذهب إلى أنه متروك، وقد روى عنه زهير. قال: وللشاميين شيخ يقال له: عباد بن كثير. وسئل عن حديج بن معاوية؟ فقال: ليس أنا بذاك الخبير به، جاءه ابن مبارك فكتب عنه. قال أحمد: وابن جريج هو عبد الملك بن عبد العزيز بن جريج قال: /٢٥٣/وأبوه يروي عن عائشة، وذهب أحمد إلى أنه لم يلق عائشة. قال: وكان سالم يعني الأفطس يرى الإرجاء. قال: إبراهيم بن ميسرة يثبت الحديث وكان أحد الثقات.','3','1308',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1129,'قال أحمد: عكرمة بن عمار ليس هو في يحيى بن أبي كثير بذاك، وقدم هشامًا عليه في يحيى، وقال: وهو في غير يحيى ثبت. قال: وسماك الحنفي ثقة. قال أحمد: هو أبو شيخ الهنائي، عن أخية حمان، - يعني: معاوية-. سمعت أبا عبد الله يقول: أقل من جلسنا إليه يشبه سفيان - يعني - ابن عيينة في العلم. وسئل عن القاسم الجرمي قال: ما علمت إلا خيرًا.','3','1309',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1130,'وسئل عن عبد الله بن الفضل؟ فقال: لا بأس به. وقال: ما أشبه أن يكون صالح بن كيسان أسن من الزهري. قلت: مصعب بن ثابت كيف هو؟ قال: هو رجل من قريش، وكأنه ضعفه، وهو من ولد ابن الزبير. وسمعته يقول: بلغ ابن أبي ذئب أن مالك بن أنس قال: ليس البيعان بالخيار. فقال ابن أبي ذئب: يستتاب مالك، فإن تاب وإلا ضربت عنقه.
قيل: سهيل، عن أبيه، عن أبي هريرة أحب إليك أو محمد بن عمرو عن أبي سلمة عن أبي هريرة؟ قال: أما أنا فأختار سهيل، وكان يحيى بن سعيد يختار محمد بن عمرو.','3','1310',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1131,'وقال أحمد: لم يرو شعبة عن محمد بن عمرو إلا حديثًا واحدًا. قال: ومحمد قدم البصرة فكتبوا عنه. قيل: سلمة بن وردان كيف هو؟ قال: سلمة بن نبيط شيخ ثقة، وكأنه ضعف بن وردان. وسئل عن خصيف؟ فقال: ثقة، وعبد الكريم يقدم عليه. قلت: أبو شهاب عبد ربه بن نافع؟ قال: لا بأس به. قلت: فشهاب بن خراش؟ قال: لا بأس به. وقال: محمد بن زياد صاحب أبي هريرة ثقة جدًا، وأجاد حماد بن سلمة عنه الرواية.','3','1311',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1132,'وقال: أبو العميس اسمه عتبة بن عبد الله، وهو من ولد عتبة بن عبد الله بن مسعود ليس من ولد عتبة بن مسعود. وسئل عن أصحاب الحسن؟ قال: لا يعدل أحد يونس. قال: وأيوب، وابن عون، وهشام هؤلاء أصحاب محمد. قيل: محمد بن كعب عمن روى من أصحاب النبي - صلى الله عليه وسلم -؟ قال: يقولون: لم يرو إلا عن معاوية. قال: وهو رجل قديم. حدثنا أحمد قال: حدثنا عثمان بن عمر قال: وسمعت البتي يقول: كان حماد إذا قال برأيه أصاب، وإذا قال: قال إبراهيم. أخطأ.','3','1312',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1133,'قال أحمد: ابن جريج روى عن ستة عجائز هن من عجائز المسجد الحرام. قال: وكان صاحب علم. قيل: خصيف أحب إليك أو سالم الأفطس؟ قال: سالم، وقدمه على خصيف قال: وخصيف وسالم وعبد الكريم هؤلاء الثلاثة من أهل حران من الجزيرة. قلت: عون بن كهمس تعرفه؟ قال: لا أعرفه. قال: وكهمس شيخ ثقة. وسئل عن عبيس بن ميمون؟ قال: لا أدري كيف هو زعموا أنه روى منكرات. وسئل عن عس بن كهمس؟ قال: لا أدري.','3','1313',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1134,'وسئل عن أحمد عن الرجل إذا سها في الإسناد فأخطأ فيه ولم يتعمد ذلك؟ قال: أرجو أن لا يكون عليه شيء. قال أبو عبد الله: عطاء قد رأى ابن عمر ولم يسمع منه، وسمع عطاء من عائشة دخل مع عبيد بن عمير. غياث بن إبراهيم متروك الحديث، وجعل أبو عبد الله غير مرة يتعجب من كثرة حديثه. قيل لأحمد: رأي الزهري أحب إليك أو رأي إبراهيم والشعبي؟ قال: كان ابن عيينة يختار رأي الزهري. قلت: فالحسن؟ قال: الحسن بخ. قال: أبو إسحاق الهمداني اسمه عمرو بن عبد الله. وسمعته يقول: هشام بن سعد ليس بمحكم الحديث. قال: محمد بن عبد الرحمن الذي روى عنه أبان بن تغلب هو محمد بن عبد الرحمن بن يزيد.','3','1314',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1135,'قال أبو عبد الله: الأغر الذي روى عنه أبو إسحاق مديني وكان قاصًا. حدثنا أحمد قال: حدثنا حجاج، عن شعبة قال: كان الأغر قاصًا من أهل المدينة وكان رضا، وكان قد لقي أبا هريرة، وأبا سعيد. وقال أحمد: حزم بن أبي حزم هو: حزم بن مهران قُطَعي إلى بني زبيد. قلت: قطيعة إلى زبيد؟ قال: نعم كذا يقال. قال: سهيل بن أبي حزم أخو حزم روى أحاديث منكرات عن ثابت.','3','1315',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1136,'قال: ليس أحد يروي عن عطاء أثبت من عمرو وابن جريج. وقال: عباد بن راشد صالح الحديث، ووثقه. وسئل أحمد عن الرجل يطلب الإسناد العالي؟ قال: طلب الإسناد العالي سنّة عمن سلف، لأن أصحاب عبد الله كانوا يرحلون من الكوفة إلى المدينة فيتعلمون من عمر ويسمعون منه. حدثنا أحمد قال: حدثنا أبو قطن قال: ثنا أبو خلدة، عن أبي العالية قال: كنا نسمع الرواية عن أصحاب رسول الله - عليه السلام - بالبصرة فلم نرض حتى ركبنا إلى المدينة فسمعناها من/٢٥٥/أفواههم.','3','1316',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1137,'وحدثنا إسحاق بن إبراهيم قال: ثنا عيسى بن يونس قال: قال لي شعبة: أبو إسحاق جدك لم يسمع من الحارث الأعور إلا أربعة أحاديث. حدثنا إسحاق قال: حدثنا يحيى بن آدم قال: سألتُ الكسائي قلت: سفيان الثوري يحكي الحروف؟ يعني حروف القرآن. قال: لا يضبطها. قلت: وأبو بكر بن عياش؟ قال: ذاك شيطان. قلت: زائدة؟ قال: يأتيك بها على وجهها. حدثنا أبو جعفر الدارمي، قال: ثنا أحمد بن سليمان، قال: قال ابن علية','3','1317',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1138,'قال لي شعبة: ما جمع لك عطاء بن السائب من رجاله عن زاذان وميسرة وأبي البختري فلا تكتبه، وما حدثك به عن رجل بعينه فاكتبه. قال أبو جعفر: يريد أنه لا يحفظ. حدثنا أبو جعفر، قال: حدثنا أحمد بن سليمان، عن سويد بن عبد العزيز، عن مغيرة قال: أبطأت على إبراهيم أيامًا فقال لي: ما خلفك؟ قلت: شيخ قدم علينا عنده أحاديث، فقال: إبراهيم: لقد رأيتنا وما نأخذ إلا الأحاديث إلا ممن نعرف حلا له من حرامه، وإنك لتجد الشيخ يحدث بالحديث يحرف حلا له من حرامه وما تعلم.','3','1318',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1139,'حدثنا أبو جعفر، قال: قال أبو داود قال شعبة: اجتمعت أنا والأعمش عند أبي إسحاق فقال الأعمش: يا شعبة أنت سيئ الخُلق، وأنا سيئ الخلق لا تشغب عليّ ودعني حتى أسأل. قلت: سل، فسأل عن قول عبد الله: من أراد العلم فليثور القرآن. فقال أبو داود: قال بحر السقا: أتيت الأعمش فخرج إليّ فسألتُه عن حديث أبي صالح عن أبي هريرة: ((من فرج عن مؤمن كربة)) فحدثني به، ثم قال: قم أخزاك الله فعجبت منه حين بدأني بهذا من غير أن يكون مني إليه شيء. قال: فصاحت به امرأته من داخل أيها الشيخ لا تلومنه فإنه خرج وهو مغضب، وذاك أنه كسر إنجانة جديدة.','3','1319',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1140,'وقال أبو داود: قال أبو عوانة: كنا عند الحكم فحدثنا بحديث مرسل فقال: ليس هذا من يابة شعبة. وقال أبو داود: قال شعبة: ما حرك قتادة شفتيه بشيء إلا عرفت ما سمع مما لم يسمع. قال: وقال أبو داود: قال شعبة: ما رأيت عنه الأعمش خمسة تُقِرُّقطُّ. قال أبو داود: قال شعبة: ما لا حد عليّ من المنة ما لعباد، وما رأيت عتكيًا أفضل منه. قال أبو داود: قال شعبة: لأن أخر من السماء أحب إليّ أن أفعله. /٢٥٦/يعني: التدليس.
قال أبو داود: قال شعبة: اسمعوا من سفيان الثوري من شيوخه الذين تعرفون. قال أبو داود: قال شعبة: إذا حدثكم هشام الدستوائي بشيء فاجتمعوا عليه.','3','1320',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1141,'قال أبو داود: قال شعبة: رأيت زكريا بن أبي زائدة يأتي جابر فيسمع منه. قال سفيان: هذا الشيخ يجئ إلى هذا فيسمع منه ونحن أحداث. قال أبو داود: قال خارجة: حديث شعبة عن أبان لحديث. فقال شعبة: إذا أذكر أبان غثيت نفسي. قال: فذكرت قوله لا بان. قال: فترحم على شعبة ودعا له. قال أبو داود: وسمعت شعبة سأل الحسن بن دينار وجاء الحسن يعزيه على أخيه حماد، فقال له شعبة: يا أبا سعيد كيف حديث حميد بن هلا ل حديث عمر؟ فعرضه الحسن، فقال: نعم يا أبا بسطام حدثنا حميد بن هلا ل، عن مجاهد - وليس بأبي الحجاج - قال: صلى بن عمر صلا ة العصر، فقال: على مكانكم، إن أخوف ما أخاف عليكم.','3','1321',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1142,'قال أبو داود: قال شعبة: قال لي حماد بن زيد: في نفسك شيء من حديث أبي هارون العبد ي؟ قال: قلت: يكفيني هذا منك. قال أبو داود: قال شعبة: سألتُ أبا إسحاق عن عبد الله بن عطاء الذي روى عن عقبة بن عامر قال: كنا نتناوب رعية الإبل فقال: هذا شيخ من أهل الطائف حدثنيه. قال شعبة: فلقيت عبد الله بن عطاء فسألتُه عن حديث عقبة وقلت له: سمعته من عقبة؟ قال: لا، حدثني سعد بن إبراهيم. قال: فلقيت سعدًا فسألتُه عنه فقال: حدثني زياد بن مخراق، فلقيت زياد بن مخراق فسألتُه عن حديث عقبة','3','1322',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1143,'فقال: حدثنيه رجل عن شهر بن حوشب. قال أبو داود: قال همام حديث أبان بن أبي عياش حديث عطاء بن السائب، عن عبد الله بن عبيد، عن أبيه قال: قلت لابن عمر: إنك لتزاحم على هذين الركنين؟ قال: فحدث به أبان ابن جريج، فرواه ابن جريج عن عبد الله بن عبيد نفسه وألقى همام وأبان قال همام: فسألتُه عن ذلك؟ فقال: هذا أتانا من قبلكم جاءنا به أبان من عندكم.','3','1323',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1144,'قال أبو داود: قال شعبة: ما كان أحد أحب إلي بقاء من أمي وأبي الزبير حتى رأيته وسمعت منه وإن حديثه ليتجلجل في صدري. قال أبو داود: قال شعبة: ما صح عندنا ولا ثبت أن النبي عليه السلام أحل النبيذ، إلا ما/٢٥٧/جاء به عبد الله بن بريدة من خراسان لم يجئ به ابن عمر وابن عباس الذين بحثوا الحديث بعد رسول الله. قال أبو داود: أوصى شعبة بكتبه أن تغسل وكذلك أوصى سفيان. قال أبو داود: قال شعبة: سمعت طلحة بن مصرف، ولو شئت لقلت سمعته من طلحة مائة مرة. قال أبو داود: رأي شعبة سليمان بن المغيرة يمشي فقال له شعبة: مالك تمشي؟ قال: نفق حماري، فاشترى له شعبة حمارًا بثلاثين درهمًا، فقال له غندر: أنت ليس عندك شيء كيف تشتري؟ قال شعبة:','3','1324',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1145,'ويحك سليمان بن المغيرة يمشي وهو خير أهل المصر وعندي فضل.
قال أبو داود: قال حماد بن سلمة: عامة ما يروي حميد عن أنس لم يسمعه منه إن عامتها سمعه من ثابت. قال أبو داود: قال شعبة: إنما روى حميد عن أنس ما سمعه منه خمسة أحاديث. قال أبو داود: رأيت شعبة يسمع من أبي مكين. قال أبو عبد الله أحمد بن حنبل: لا أعلم أحدًا يروي عن أبي إدريس - يعني: الأزدي - إلا إسماعيل بن سالم.','3','1325',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1146,'حدثنا أبو خالد الخبار قال: حدثنا أبو بكر بن عياش، عن عاصم قال: كان أبو وائل إذا جلس مجلسًا فيه زر لم ينطق أبو وائل حتى يقوم. حدثنا أو جعفر الدارمي قال: ثنا أحمد بن سليمان قال: قال يحيى بن يمان قال سفيان: يا يحيى إلزم زائدة فإنه من بقايا الناس. حدثنا ربيع بن يحيى قال: سمعت زائدة يقول: أنا أسأل عن أصحاب الحديث كما يسأل القاضي. حدثنا أبو جعفر، قال: ثنا أحمد بن سليمان، قال: ثنا حسين الجعفي، عن ابن أيجر قال: كان إذا ذكر طلحة بن مصرف يقول: وهل رأيت مثل طلحة.','3','1326',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1147,'حدثنا أبو جعفر الدارمي قال: ثنا أحمد قال: ثنا هارون أبو العلاء قال: سمعت ربيعة بن أبي عبد الرحمن يقول: قال رسول الله - صلى الله عليه وسلم -: ((هلاك أمتي من ثلاث: من قبل القدرية، والعصبية، والرواية من غير ثقة)) قال: وربما سمعته يقول: ((والرواية من غير ثبت)). حدثنا أبو الربيع الزهراني قال: حدثنا إسماعيل - يعني: بين زكريا- عن عاصم الأحول، عن محمد بن سيرين قال: لقد أتى عليّ','3','1327',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1148,'زمان وما نسأل عن إسناد الحديث حتى وقعت الفتنة، فسئل عن إسناد الحديث لينظر من كان من أهل السنّة أخذ بحديثه، ومن كان من أهل البدعة ترك حديثه. حدثنا أبو الربيع قال: ثنا حماد بن زيد، عن أيوب قال: رآني سعيد بن جبير مع طلق بن حبيب فقال: لما أراك مع طلق؟ لا تجالسنه. قال: ما أدركت بالبصرة رجلا كان أبر بوالديه منه، ولا أعبد منه. حدثنا أبو جعفر الدارمي قال: ثنا أحمد بن سليمان، عن ابن عيينة، عن يحيى بن سعيد، عن سليمان بن يسار قال: أدركت بضعة عشر من أصحاب النبي - صلى الله عليه وسلم -.','3','1328',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1149,'حدثنا أبو بكر الحميدي، قال: حدثنا سفيان قال: حدثنا يحيى بن سعيد قال: سئل ابن لعبد الله بن عمر عن مسألة فلم يقل فيها شيئًا، ولم يكن عنده فيها شيء. فقيل له: إنا لنعظم أن يكن مثلك ابن إمامي هدى يسئل عن أمر ليس عنده فيه علم. فقال: أعظم والله من ذاك عند الله وعند من عرف الله وعند من عقل عن الله أن أقول ما ليس لي به علم، أو أخبر عن غير ثقة. حدثنا أبو جعفر الدارمي قال: ثنا أحمد بن سليمان قال: ثنا ابن عيينة كنت أسأل أبا الزناد فأقول: يا أبا عبد الرحمن أي شيء بلغك في كذا وكذا؟ فيقول: الشأن فيه كذا وكذا. فأقول: هل سمعت ذلك من أحد؟ فيضحك، ويقول: هذا عندنا موطأ.
حدثنا أبو بكر الحميدي، قال: ثنا سفيان، قال: قال هشام بن عروة:','3','1329',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1150,'تركت المدينة منذ مات موسى بن عقبة، إنما كنت آتيها من أجله. قال سفيان: وكان موسى مولى لهم. حدثنا أحمد بن عبد الله بن يونس قال: ذكر سفيان عند زائدة قال: كان ذلك أعلم الناس في أنفسنا، وذكر عنده ابن أبي ليلى فقال: كان أفقه أهل الدنيا. حدثنا عبد الله بن محمد بن أسماء، قال: ثنا جويرية، قال: سأل رجل قتادة عن القدر؟ فقال: رأي العرب أحب إليك أم رأي العجم؟ فقال الرجل: لا بل رأي العرب. قال قتادة: فإن العرب لم تزل في جاهليتها وإسلامها تثبت القدر، ثم تمثل ببيت من الشعر:
ما كان قطعي هول كل تنوفة ... إلا في كتاب قد خلا مسطورا','3','1330',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1151,'سئل علي بن عبد الله وأنا عنده عن عيسى بن يونس؟ فقال: بخ بخ ثقة مأمون. سمعت إسحاق قال: قلت لوكيع: أريد أن أذهب إلى عيسى بن يونس، فقال: تأتي رجلا قد قهر العلم. حدثنا الحماني، قال: حدثنا شريك، عن جابر، عن عامر قال: رأيت الحسن والحسين يسألان الحارث الأعور عن حديث علي. حدثنا أبو بكر الحميدي/٢٥٩/قال: قال سفيان: كنت في منزلنا نتغدى ومعنا أبو حصين وعدة من الكوفيين، فذكروا قدوم صفوان بن سليم وحجه، فقمت، فذهبت وسألتُ عنه، فقال لي رجل: إذا دخلت','3','1331',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1152,'مسجد الخيف فأت المنارة، فانظر إمامها قليلا شيخًا إذا رأيته علمت أنه يخشى الله، فجلست إليه فقلت: أنت صفوان؟ فقال: نعم، فسألتُ عن هذه الأحاديث وحدي وليس معي ولا معه أحد وتركت أبا حصين وقلت: هو في يدي إذا قدمنا الكوفة سمعت منه السبعين التي عنده، وقالوا لي يومئذ: إنما عنده سبعين حديثًا فلم أسمع منه إلا ثلاث أحاديث، وكان أهل الحجاز يومئذ أحب إلي أن آخذ عنهم من أصحابنا. قال سفيان: وحج صفوان وليس معه إلا سبعة دنانير، فاشترى بها بدنة، فقيل له ذلك، فقال: إني سمعت الله قال: {لَكُمْ فِيهَا خَيْرٌ} [الحج: ٣٦].
وقال سفيان: ذهبت مع أبي إلي الجمار، فرأى رجلا عند الجمرة الوسطى، فأعجبه طول وقوفه، وحسن هيئته، فقال: اذهب فسل من هذا الشيخ، فأتيت الذين وراءه، فسألتُهم عنه، فقالوا: هذا عامر بن عبد الله بن الزبير، وكانت عليه عمامة بيضاء قد أرخاها، ورآه وعليه بردًا معافري وهو أبيض اللحية قال: وكن محلوقًا فلا أدري كان يحلق أم لا.','3','1332',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1153,'حدثنا الحميدي قال: سألتُ سفيان عن أحاديث الأعرج سمعها أبو الزناد من الأعرج، فقال سفيان: كان يقول لنا فيها أخبرني الأعرج أنه سمع أبا هريرة إلا حديث البقرة فإنه قال فيه أخبرني الأعرج أنه سمع أبا سلمة بن عبد الرحمن يقول: سمعت أبا هريرة يقول، ثم قال سفيان: كنت أسأل أبا الزناد عنها حديثًا حديثًا. قال سفيان: وكنت إذا سألتُ أبا الزناد عن من هذا؟ يقول هذا موطأ. فأقول: عمن هو؟ أي مشيختكم؟ قال: فيقول: أخبرني فلأن للحديث الذي أسأله عنه. قال الحميدي: وسمعت سفيان يقول: كان أبو الزناد أحسن شيء خُلقًا كنت أسأله عن الشيء فيقول: الشأن فيه كذا وكذا فأقول: يا أبا عبد الرحمن فأي علمائكم قال ذلك؟ فعجب مني، ويقول: انظروا انظروا ثم يجيبني. سمعت أحمد يقول: كان يحيى بن حماد رجلا صالحًا. سمعت أبا حفص قال: سمعت أبا داود يقول: /٢٦٠/سمعت شعبة يقول: يا عجبًا لهذا الأحول لا يرضى قيس بن الربيع. يعني يحيى بن سعيد','3','1333',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1154,'القطان. قال أبو حفص: قلت لأبي الوليد: إنك تثني على قيس بن الربيع كثيرًا؟ قال: لأنه والله كان يخاف الله، أو نحو هذا أنا أشك في اليمين. قال أبو حفص: لما قدم يزيد بن هارون البصرة فحدثنا عن قيس قلت أنا: حدثنا عن غيره، فبلغ أبا داود، فغضب عليّ حتى اعتذرت إليه. وقلت له: أكتب قيس عنك؟ واكتب عن يزيد المشايخ الكبار. حدثنا أبو الأزهر قال: حدثنا محمد بن يوسف، عن إبراهيم بن الأدهم قال: سألتُ ابن شبرمة عن شيء فأسرع الجواب قال: وكانت عندي مسألة شديدة، فقلت له: انظر فيها تأنى. قال: إذا وجدت الأثر ووضح الطريق لم أحبسك. حدثنا عباس بن عبد العظيم قال: قال ابن داود: كان الحسن بن','3','1334',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1155,'صالح إذا ذكر عثمان سكت. يعني لم يترحم عليه، وترك الحسن بن صالح الجمعة سبع سنين. حدثنا عباس بن عبد العظيم قال: ثنا يحيى بن يمان قال: قالوا لسفيان: إن أصحاب الحديث يطلبون الحديث بغير نية. قال: طلبهم له نية. حدثنا عباس قال: سمعت أبا عاصم قال: رأيت ابن عون وسفيان الثوري ومالك بن أنس وابن جريج يؤثرون الحديث. يعني يحدثون إنسانًا ويتركون آخر، ويفضلون بعضًا على بعض. حدثنا عباس قال: سمعت أبا عاصم قال: نعيت زفر بن الهذيل إلى سفيان، فقال: الحمد لله الذي عافانا مما ابتلى ناسًا كثيرًا به.
حدثنا عباس قال: سمعت أبا عاصم قال: ابن جريج يدلي في حفرته','3','1335',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1156,'وسفيان يحدثنا عنه بأحاديث لم نسمعها. حدثنا إبراهيم بن مستمر قال: سمعت أبا الوليد يقول قبل أن يقدم عمرو بن حكام: يقدم عليكم رجل من أروى أهل البصرة. عن شعبة قال إبراهيم: سألتُ علي بن عبد الله عن عمرو بن حكام؟ فقال: هو ثقة، ثم سألتُه بعد ذلك عنه بسنتين فقال: ما أنقم عليه شيئًا إلا حديث الزنجبيل. حدثنا محمد بن عبد الأعلى قال: سمعت يزيد بن زريع قال: حدثني','3','1336',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1157,'أيوب، عن أبي قلا بة وكان عثمانيًا، وحدثنا خالد عن عبد الله بن شقيق وكان عثمانيًا. حدثنا محمد بن عبد الأعلى قال: ثنا أبو علي الطهماني/٢٦١/قال: سمعت جعفر بن سليمان وقيل له: إنك تذكر أبا بكر وعمر؟ فقال: أما شتيمة فلا، ولكن بغضًا ما شئت حبًا ما شئت. حدثنا حماد بن المبارك أبو بكر قال: قال علي بن عاصم: كنت بمكة أنا وهشيم وشعبة فكنا بمنى، فمر بنا شيخ فعرفه هشيم ولم نعرفه نحن، فقال: يا أبا الحسن اذهب في حاجة فمضى وبقيت أنا وشعبة، فمر بنا شيخ آخر فعرفه شعبة ولم أعرفه، فقال: اذهب في','3','1337',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1158,'حاجة فمضى، ثم مربي شيخ راكب على حمار فأتبعته فإذا هو ابن طاوس فكتبت عنه ستين حديثًا، فلما انصرفنا راجعين إلى العراق وضربا بالنبل في الماء أخرج هشيم كتابه وجعل يقول: حدثنا الزهري، فقال له شعبة: وأين رأيت الزهري؟ قال: ذاك الشيخ الذي مر بنا. قال: قد عملت عملا يا أبا معاوية ناولني كتابك. فناوله، فخرقه ورما به في الماء، فحفظ منها هشيم تسعة، ثم أخرج شعبة كتابه، فقال: حدثني يعلي بن عطاء. فقلت له: يا أبا بسطام وأين لقيته؟ قال: ذاك الشيخ الذي مر بنا فلم يجسر عليه هشيم، قال: ثم أخرجت الواحي فقلت: حدثنا ابن طاوس. قال: وأين لقيته؟ قال: لما مضيت مر بي شيخ على حمار، فقلت: من هذا؟ فقيل: ابن طاوس، فقال: أنت أنت يا أبا الحسن، ناولني ألواحك. فظننت أنه لا يجسر عليّ فناولته فأخذها فرمى بها في الماء، فحفظ منها سبعة عشر حديثًا. حدثنا حماد بن المبارك، عن داود بن المحبر قال: رأيت النبي عليه السلام في المنام فقلت له: يا رسول الله إن علي بن عاصم روى عنك ((أنه من عزَّى','3','1338',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1159,'مصابًا كان له مثل أجره)) وإن الناس رووه عن ابن مسعود، فقال النبي - عليه السلام- صدق علي أنا قلته. قال: فلم نلبث أن أقبل علي فمال إليه النبي بكلتي ييديه وأخذ كفه، ثم قال: مرحبًا بمن أحيا سنتي من بعدي.
حدثنا عبد الرحمن بن عمرو البصري قال: ذاكرت عبد الرحمن بن إبراهيم: ميسرة بن معبد فقال: قد روى عنه وكيع حدثنا أخطأ فيه في أكل اللحم الني، فقلت له: إن سوار بن عمارة حدثنا عن مسرة بن معبد قال: حدثني الزهري وسليمان بن موسى','3','1339',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1160,'قالا: نهى رسول الله - صلى الله عليه وسلم - /٢٦٢/أن يؤكل اللحم حتى يمضي عليه ثالثة أو تمسه النار وكان وكيع قد رواه عن مسرة، عن سليمان بن موسى وكان المشهور بالشام ما حدثنا الوليد بن النضر قال: حدثنا مسرة بن معبد، عن الزهري، ثم أخرجه سوار فجمعهما جميعًا عن الزهري وسليمان بن موسى. فقال أبو سعيد - يعني عبد الرحمن بن إبراهيم -: قلت: وكيع. قال علي بن عبد الله: أملى عليّ يحيى بن سعيد هؤلاء المشيخة، فقال: هؤلاء مشيخة شعبة من أهل الكوفة الذين لم يلقهم سفيان بن سعيد، منهم إسماعيل بن رجاء، وعبيد بن حسن، والحكم بن عتيبة، وعبد الملك بن ميسرة، وعدي بن ثابت، وطلحة بن','3','1340',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1161,'مصرف، والمنهال بن عمرو، ويحيى أبو عمر النهراني، وعلي بن مدرك، وسماك الحنفي، وسعيد بن أبي بردة، وأبو بكر بن حفص، وعبد الله بن جبر، وأبو زياد الطحان، ومحل بن خليفة، وأبو السفر، وزائدة','3','1341',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1162,'بن عمير، وناجيه، والعلاء بن بدر، وحيان البارقي، وعبد الله بن أبي المجالد، والهيثم، وأبو الهيثم، وعقبة بن حريث، وعاصم بن عمرو البجلي، وأبو المختار، وعمار العبسي، وعائد','3','1342',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1163,'بن نصيب، وأبو معشر، وسيار أبو الحكم، وأبو بحر الهلا لي، ومحمد بن ذكوان. وهؤلاء لم يعدهم يحيى وكتبهم أبا الوليد بن العيزار، ويحيى بن حصين، ونعيم بن أبي هند، والجوزي بن الزبير، وسعيد بن عمرو بن العاص.','3','1343',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1164,'وسألتُ الحسن بن الصباح البزار قلت: سم لي السبعة الذين أجأبو افي القرآن؟ قال: إسماعيل بن داود الجزي، وابن الدورقي، ويحيى بن معين، وأبو خيثمة، وابن أبي مسعود، وأبو مسلم المستملي، وآخر لم يسمه. قلت: سعد وبه فيهم؟ وسماهم أبو إسحاق العبادي وذكر السابع محمد بن سعد كاتب الواقدي.','3','1344',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1165,'حدثنا محمد بن عمر بن علي المقدمي قال: حدثني عبد الملك بن مسى قال: رأيت سفيان الثوري جاء إلى يونس فأخذ يسأله ويملي عليه ومعه ألواح، فلما قام قالوا له: نسألك فلا تحدثنا وتحدث سفيان غريب. حدثنا سهل بن محمد قال: قال الأصمعي: خرج من فقهاء أهل البصرة مع ابن الأشعث: مسلم بن يسار، وعبد الله بن غالب، وعقبة بن عبد الغفار، وأبو الجوزاء.','3','1345',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1166,'حدثنا سهل بن محمد قال: حدثنا الأصمعي/٢٦٣/قال: حدثنا حماد بن زيد قال: مما من الله به على الخليل بن أحمد أنه جالس أيوب وكان يخشى عليه الإباضية. قال: وقال المعتمر: ومما من الله به على يزيد بن زريع أنه جالس أبا عوانة، أي فأثبت القدر.
حدثنا سهل به محمد قال: حدثنا الأصمعي قال: حدثنا سلام بن أبي مطيع قال: كان أيوب أفقههم، وابن عون أملكهم، ويونس أرضاهم في العامة، وسليمان أعبد هم. حدثنا أبو أمية محمد بن إبراهيم قال: ثنا أبو مسهر الدمشقي قال: حدثنا عيسى بن يونس قال: دخل سفيان الثوري على محمد بن سعيد بن أبي','3','1346',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1167,'قيس الأزدي فاحتبس عنده هنهه، ثم خرج إلينا فقال: إنه كذاب. قال أبو مسهر: وقتله أبو جعفر في الزندقة. حدثنا عبدة بن عبد الرحمن قال: حدثنا بقية بن الوليد قال: حدثنا عتبة بن أبي حكيم قال: جلس إسحاق بن عبد الله بن أبي فروة في مسجد المدينة يحدث والزهري إلى جانبه. فيقول: قال رسول الله فلما أكثر قال الزهري: قاتلك الله يابن أبي فروة ما أجرأك على الله ألا تسند حديثك تحدثنا بأحاديث ليست لها خطم ولا أزمة. حدثنا محمود بن خالد الدمشقي قال: حدثنا الوليد بن مسلم قال: حدثنا ابن جابر قال: حدثني عمير بن هانئ، عن كعب الأحبار فقال: يلي','3','1347',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1168,'اليمن رجل يقال له: بحير بن ريان عليه مثل أثم اربل ملكة صيدا قتلت تسعة وتسعين نبنًا. قال ابن جابر: فوليهم في زمان معاوية فعاث فيهم مفسدًا وسفك فيهم دماء. قال أبو محمد حرب: بلغني أن ابن المبارك ترك حديث عباد بن كثير والحسن بن دينار، والحسن بن عمارة، وروح بن مسافر، وابن سمعان وعمرو بن ثابت، وقال ابن المبارك: ما يستوي حديث عباد بن كثير عندي كفًا من تراب.','3','1348',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1169,'حدثنا أحمد بن سعيد قال: حدثنا أحمد بن سليمان قال: سمعت عبد الوارث بن سعيد يقول: كان أبو هارون العبدي أوثق ممن تقرح جبهته هاهنا. حدثنا أحمد بن سعيد، عن النضر بن شميل، قال: لم يرو شعبة، عن حماد إلا شيئًا لم يحده عند غيره من أصحابه وكان ابن عون لا يسلم على حماد.
حدثنا أحمد بن سعيد، عن سعيد بن عامر، قال: حدثنا حرب، عن خويل قال/٢٦٤/: دخلنا على يونس بن عبيد، قال: فجاء رجل فقال: يا أبا عبد الله تنهانا عن حلقة عمرو بن عبيد وابنك قد دخل عليه قبل. قال: ابني؟ قال: نعم. قال: فتغيظ يونس، قال: فبينا نحن كذلك إذ جاء ابن يونس قال: يا بني قد عرفت رأيي في عمرو وأنت تدخل','3','1349',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1170,'عليه، قال: يابه إنما كنت مع فلأن، فاعتذر إليه، فقال يونس: أنهاك عن الزنا، والسرقة، وشرب الخمر، والله لأن تلقي الله بهن أحب إلى من أن تلقاه يرأي عمرو بن عبيد وأصحاب عمرو. وقال أبو عبد الله أحمد بن حنبل: ولد سفيان بن عيينة سنة سبع قال: وحج الزهري سنة ثلاث وعشرين. يعني ومائة ولابن عيينة ستة عشر سنة. قال: ومات عطاء سنة أربعة عشر. قال أحمد: قال ابن أبي نجيح: لم يكن في بلدنا هذا أحد يشبه عمرو بن دينار. قال أبو عبد الله: قال علي: سألتُ يحيى فقال: ولدت في سنة عشرين في أولها. حدثنا عبد العزيز قال: حدثني يوسف بن موسى قال: سمعت وكيعًا','3','1350',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1171,'يقول: لما مات سفيان خرقوا كتبه ما كان في القراطيس وما كان في الطروس محوه فباعوه والباقي دفنوه. قال: وسمعت وكيعًا يقول: قال سفيان مرة: إن كان الرجل ليجالس الرجل مرة فيعرفه. أو كما قال. قال: فظننا أنه يعني نفسه. قال: وسمعت وكيعًا يقول: استعمل ابن هبيرة على الكوفة فدعا منصور بن المعتمر، فأراد أن يستقضيه، فأبى، فقال: إن فعلت وإلا صنعت بك وصنعت. فأقهره، فكانوا يجيئون إلى المسجد فيقعد، فإذا جاءوا إليه قالوا: أقض بيننا. سكت فلم يكلمهم حتى كان أيام، فقالوا لابن هبيرة: إن منصورًا ليس يقض بيننا. فهم به، فقالوا له: إن منصورًا ليس ممن يضرب، فلما أيس منه استعمل مكانه أخي. قال وكيع: وكان منصور يقوم الليل وكانت له أم أعجمية، قال: وكان هو مثل ظن، أو خشية الليل أجمع ويبكي فتقول له أمه: يا بني قتلت قتيلا، أشركت في دم، ما حالك؟','3','1351',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1172,'قال: وسمعت وكيعًا يقول: ولد الأعمش في سنة ستين ومات في سنة مائة وثمان وأربعين، وكان ابن تسعين إلا سنتين. قال وكيع: واختلفت إلى الأعمش في سنة/٢٦٥/خمس وأربعين ومائة، ولزمته ثلاث سنين فكان قد ضعف بصره ولم يكن يبصر ولم يكن. يعني بذلك أنه ليس يبصر. قال وكيع: وما رأيت الأعمش فاتته ركعة من الصلاة، ولا تكبيرة، وكنت أصلي الصلوات معه قال وكيع: وكان عليه خرقة لو أراد أن يبيع لم يبعها بدرهم، أو بدرهم ودانقين، وكذلك كان رداءه، وكان عليه في الشتاء قميص غليظ دروزها من خارج. قال وكيع: وكان يلبس جبة صوف ليس لها لبنة قرقر كان يقورها وزرها من خلف. قال وكيع: ورأيت عليه فروة مقلوبة صوفها من الخارج. قال وكيع: كان يفعل هذا جادًا منه؟ قال: نعم.
قال وكيع: كنت إذا رأيت الأعمش تقول: هذا جمال أو مكاري، فإذا نطق تكلم بكلام أهل البادية. قال: وسمعت الأعمش يقول: كان أبي جميلا. وكان أبوه فارسيًا. قال:','3','1352',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1173,'وكان له صديقًا. قال: وسمعت الأعمش يقول: كان أبي يقول لذاك الرجل: هذا أخي. قال: فمات ذاك الرجل. قال الأعمش: فورث مسروق أبي من ذاك الرجل. قال وكيع: وكان الأعمش غلامًا يكون مع البرازين، فأخذ بعد ذلك في قراءة القرآن حتى لم يكن بالكوفة أقرأ منه، إلا طلحة بن مصرف اليامي، فبلغ طلحة أن بعض الناس قال: ليس بالكوفة أقرأ من الأعمش إلا طلحة بن مصرف. قال: فجاء حتى قرأ على الأعمش، أو سأله عن أحرف لكي لا يذكر بذاك ولا يعرف به كي يذهب عنه ذاك الاسم إلى هذا انتهى زهده. قال وكيع: ثم أخذ الأعمش في العربية حتى كان يأتي الأعراب فيسألهم عن الحرف إذا أشكل عليه. قال وكيع: وسمعت الأعمش يقول: سمعت حديث ابن عمر حيث دعا لا صحابه فقالوا له: زدنا. فقال: أعوذ بالله أن أكون من المسهبين. قال: فلم أدر ما المسهب. قال: فخرجت فلقيت أعرابيًا فقلت: ما المسهب؟ قال: المكثار الذي لا يسكت. وسمعت وكيعًا يقول: كان الأعمش إذا خرج إلى مكة هابه إلا كرياء قال: فلما أحرم اجترى عليه الكري. قال: فأخذ عصاه وجعل يضربه فقال له: يا أبا محمد وأنت محرم. فقال الأعمش: هذا من مناسك الحج.','3','1353',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1174,'قال وكيع: قال الأعمش: أول ما رأيت زيد بن وهب كان قاعدًا في المسجد فسمعته/٢٦٦/يحدث عن عمر وعبد الله، فدنوت منه فحدثني بأحاديث، فلما قام قلت: والله لأتبعنه، فأتبعته حتى دخل منزله، قال: فجعلت اتحفظ ما حدثني به، قال: فحفظت كلها ما خلا رأس حديث ذهب عليّ فرجعت فقلت: والله لأتينه فلا سألنه، قال: فجعلت أتفكر فيه فقلت: لا آكل منه طعامًا ولا أشرب شرابًا حتى أعلم ما هذا قال: فذكرته. قال وكيع: قال الأعمش: رأيت أنس بن مالك يصلي عند الكعبة. حدثنا عباس بن عبد العظيم قال: سمعت أبا الوليد يقول: لا أعلم إلا أني سمعت شعبة يقول: أفادني سفيان عن عبد الله بن دينار أحاديث. فسألتُ عبد الله بن دينار عنها، فكان سفيان أحفظ لها منه. قال: وسمعت أبا الوليد يقول: قال عثمان بن زائدة قلت لسفيان: عمن أحمل بالكوفة؟ قال: عن زائدة. قال: وذكر أبا بكر بن عياش الخياط، فقال: التفسير والقرآن.','3','1354',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1175,'حدثنا عباس قال: حدثنا أبو نعيم، عن سفيان قال: قال حبيب بن أبي ثابت: ما أردت بشيء مما ترون وجه الله. حدثنا عباس قال: ثنا أبو نعيم قال: ثنا سفيان عن سلمة قال: ما رأيت أحدًا يريد بعلمه وجه الله والدار الآخرة إلا عطاء وطاوس ومجاهد. حدثنا عباس قال: حدثنا عبد الرزاق قال معمر: من أخذ العلم جملة ذهب منه جملة.
حدثنا عباس قال: ثنا يحيى بن يمان قال: قالوا لسفيان: إن أصحاب الحديث يطلبون الحديث بغير نية. قال: طلبهم له نية.
حدثنا أحمد بن أبان القرشي قراءة عليه قال: ثنا سفيان قال','3','1355',NULL,NULL,'0');
INSERT INTO [b12527] VALUES (1176,'أبو إسحاق: إن عطاء بن السائب لمن البقايا.
حدثنا عباس بن عبد العظيم قال: حدثنا عبد الرزاق قال: قال الثوري مرة: منصور عن إبراهيم، عن علقمة، عن عبد الله. هذا السند العزيز.
قال عبد الرزاق: والزهري، عن سالم، عن ابن عمر. ثم سكت يعارضه.
حدثني أبو يوسف، عن الوليد بن عتبة، عن بقية قال: رأيت النبي - عليه السلام- في المنام بزق في فمي.
تم الكتاب والحمد له الذي بنعمته تتم الصالحات وصلى الله على محمد خاتم النبيين وعلى آله وأهل بيته الطاهرين.
قوبل كله بالأصل فصح والحمد لله رب العالمين.','3','1356',NULL,NULL,'0');
