CREATE TABLE "b12698"(id INTEGER PRIMARY KEY,content TEXT, part TEXT, page TEXT, number TEXT, services TEXT, is_deleted TEXT);

-- Insert data for b12698
INSERT INTO [b12698] VALUES (1,'بسم الله الرحمن الرحيم
أخبرنا الشيخ أبو محمد عبد الوهاب بن ظافر بن علي الأزدي عرف بان رواج بقراءت عليه بثغر الاسكندرية المحروس في مسجده قال أخبرنا الحافظ أبو طاهر أحمد بن محمد بن سلفة الأصبهاني قراءة عليه وأنا أسمع من أصله العتيق وأجاز لي جميع رواياته أخبرنا أبو الحسن المبارك بن عبد الجبار بن أحمد بن القاسم الطيوري الصيرفي قراءة عليه ببغداد من أصل سماعه في ذي القعدة سنة أربع وتسعين وأربعمائة أخبرنا أبو محمد الحسن بن علي بن محمد الجوهري قراءة عليه أخبرنا أبو عمر محمد بن العباس بن محمد بن زكريا بن حيوية الخزاز حدثنا أبو الطيب محمد بن القاسم بن جعفر الكوكبي قراءة عليه وهو ينظر في أصله سنة ست عشرة وثلاثمائة حدثنا أبو اسحاق إبراهيم بن عبد الله بن الجنيد الختلي قال:
<span data-type="title" id=toc-1>(١) </span>سمعت أبا زكريا يحيى بن معين يقول: «أبو هارون العبدي غير ثقة، يكذب، واسمه: عمارة بن جوين» .
<span data-type="title" id=toc-2>(٢) </span>وسئل يحيى بن معين وأنا أسمع عن عبد الله بن نافع مولى ابن عمر، فقال: «ضعيف» .
<span data-type="title" id=toc-3>(٣) </span>وسئل عن أخويه عمر بن نافع وأبي بكر بن نافع فقال: «ثقتان» .',NULL,'271',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (2,'<span data-type="title" id=toc-4>(٤) </span>سئل يحيى بن معين عن عبيد الله بن الأخنس فقال: «ليس به بأس، بصري، روى عنه يحيى القطان، وسعيد بن أبي عروبة» . قال يحيى بن معين: «وكان خزازاً مولى الأزد، ويكنى أبا مالك» يعني: عبيد الله بن الأخنس. قال يحيى: «وقد سمع منه عبد الوهاب الخفاف» .
<span data-type="title" id=toc-5>(٥) </span>وسئل يحيى بن معين وأنا أسمع عن أبي النضر فقال: «أبو النضر سالم مولى عمر بن عبيد الله ثقة، كان يقاتل مع عمر بن عبيد الله الخوارج، قاتل أبي فديك لعنه الله،
<span data-type="title" id=toc-6>(٦) </span>ولأبي النضر ابن يقال له: إبراهيم بن سالم بن بردان، يحدث عنه سليمان بن بلال، وحاتم بن إسماعيل، وصفوان بن عيسى» ،',NULL,'272',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (3,'قلت ليحيى: (كيف هو؟) ، قال: «ليس به بأس» ، يعني: إبراهيم بن بردان.
<span data-type="title" id=toc-7>(٧) </span>سئل يحيى بن معين وأنا أسمع عن سلمة بن وردان، فقال: «ليس بشيء» .
<span data-type="title" id=toc-8>(٨) </span>سمعت يحيى بن معين يقول: «سلمة بن كهيل يكنى أبا يحيى» .
<span data-type="title" id=toc-9>(٩) </span>سئل يحيى وأنا أسمع عن أبي الضحى مسلم بن صبيح، فقال: «كان عطاراً» ، فقال ابن الغلابي: (كان من موالي سعيد بن العاص) ، فما أنكر ذلك يحيى.
<span data-type="title" id=toc-10>(١٠) </span>قال ابن الغلابي: (وكان يحيى بن أبي كثير عطاراً، مولى طي) .',NULL,'273',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (4,'<span data-type="title" id=toc-11><span data-type="title" id=toc-15>(١</span>١) </span>سئل يحيى بن معين عن الفضل بن غانم الذي يحدث عن سلمة بالمغازي، فقال: «ضعيف، ليس بشيء» .
<span data-type="title" id=toc-12>(١٢) </span>حدثنا يحيى بن معين، حدثنا عبد الله بن إدريس، قال: «ما رأيت أحداً يكتب عند أشعث بن سوار إلا حفص بن غياث» (١) .
<span data-type="title" id=toc-13>(١٣) </span>حدثنا يحيى بن معين، نا أبو قطن، عن شعبة قال: «نهاني أيوب أن أحدث: (زينوا القرآن بأصواتكم) » .
<span data-type="title" id=toc-14>(١٤)</span>
_________
(١) ورواه عباس الدوري عن ابن معين في التاريخ (٢٦٢٧) .',NULL,'274',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (5,'سئل يحيى بن معين / وأنا أسمع عن أبي سعيد الأشج، فقال: «ليس به بأس» .
<span data-type="title" id=toc-16>(١٥) </span>وسئل يحيى بن معين وأنا أسمع عن أبي بشر ختن المقرئ، فقال: «ليس به بأس» .
<span data-type="title" id=toc-17>(١٦) </span>وسئل يحيى عن علي بن حكيم الأودي، فقال: «ليس به بأس، ثقة» .
<span data-type="title" id=toc-18>(١٧) </span>ذُكر ليحيى بن معين وأنا أسمع حديث أبي هارون العبدي عن أبي سعيد الخدري أنه قال: (مرحباً بوصية رسول الله -صلى الله عليه وسلم-)',NULL,'275',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (6,'فقال يحيى: «قد رواه ليث بن أبي سليم، عن شهر بن حوشب، عن أبي سعيد مثله» ، فقال رجل ليحيى: (هذا أيضاً ضعيف مثل أبي هارون) ، قال: «لا، هذا أقوى من ذاك وأحسن» . حدثناه ابن أبي مريم عن يحيى بن أيوب عن ليث.
<span data-type="title" id=toc-19>(١٨) </span>سئل يحيى بن معين وأنا أسمع عن حصين بن عمر الأحمسي، فقال: «ليس بشيء» .
<span data-type="title" id=toc-20>(١٩) </span>وسئل يحيى بن معين عن بشر بن عمارة الذي يحدث عن أبي روق وأنا أسمع، فقال: «ضعيف الحديث» .',NULL,'276',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (7,'<span data-type="title" id=toc-21>(٢٠) </span>قلت ليحيى بن معين: ما تفسير قول سعيد بن المسيب في الرجل يعطي الشيء في سبيل الله، قال: «إذا بلغ رأس مغزاه فهو كبعض ماله» ، ما يعني رأس مغزاه؟ قال: «الموضع الذي يريد أن يغزو منه» ، قلت: مثل طرسوس؟ قال: «نعم» .
<span data-type="title" id=toc-22>(٢١) </span>سئل يحيى بن معين وأنا أسمع عن عبد الرحمن بن صفوان، هل رأي النبي -صلى الله عليه وسلم- وصحبه؟ فقال: «زعم أبو بكر بن عياش، عن حنظلة بن أبي سفيان الجمحي، أن عبد الرحمن بن صفوان الجمحي لم ير النبي -صلى الله عليه وسلم-» . فقال ابن الغلابي ليحيى: (حنظلة هذا ابن ابنه، هو حنظلة بن أبي سفيان عن عبد الرحمن بن صفوان بن أمية الجمحي) .
<span data-type="title" id=toc-23>(٢٢) </span>قال يحيى بن معين وأنا أسمع: «والذي يُروى عن عبد الرحمن',NULL,'277',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (8,'بن صفوان حديثٌ واحد، يرويه يزيد بن أبي زياد» ، كأنه يضعف الحديث.
<span data-type="title" id=toc-24>(٢٣) </span>سمعت يحيى بن معين يقول: «بريدة هو ابن حصيب الأسلمي، ومن قال: (خصيب) فقد صحَّف» .
<span data-type="title" id=toc-25>(٢٤) </span>سمعت يحيى بن معين يقول: «محرش الكعبي، وهو من خزاعة» .
<span data-type="title" id=toc-26>(٢٥) </span>سمعت يحيى بن معين يقول: «الناس يقولون: (أبو عسيب) حشرج وغيره، ما خلا حماد بن سلمة، فإنه يقول: (أبو عسيم) » .',NULL,'278',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (9,'قلت ليحيى: فأيما الصواب؟ فكأنه مال إلى (أبي عسيب) .
<span data-type="title" id=toc-27>(٢٦) </span>سألت يحيى بن معين عن أنيس بن أبي يحيى وأخيه محمد بن أبي يحيى وأخيه سحبل، فقال: «هؤلاء ثلاثة إخوة ثقات» . قال يحيى بن معين: «وإبراهيم بن أبي يحيى هو ابن محمد هذا، وليس بشيء» .
<span data-type="title" id=toc-28>(٢٧) </span>سئل يحيى بن معين عن زيد بن جبيرة، فقال: «شيخ يحدث عنه البصريون، ليس بشيء» .
<span data-type="title" id=toc-29>(٢٨) </span>سألت يحيى بن معين عن الزبير بن جنادة، فقال: «شيخ خراساني ثقة، يحدث عنه أبو تميلة وأبو الحسين العكلي» ،
<span data-type="title" id=toc-30>(٢٩) </span>قال يحيى: «وأبو تميلة ثقة» .',NULL,'279',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (10,'<span data-type="title" id=toc-31>(٣٠) </span>سئل يحيى بن معين عن عبد المؤمن بن خالد الذي يحدث عنه أبو تميلة، فقال: «ثقة» .
<span data-type="title" id=toc-32>(٣١) </span>قلت ليحيى بن معين: حديث الليث بن سعد، عن أبي الزبير عن طاوس، وعن سعيد بن جبير عن ابن عباس: كان النبي -صلى الله عليه وسلم- يعلمنا التشهد، قلت: ورواه معتمر بن سليمان، عن أيمن بن نابل، عن أبي الزبير، عن جابر، عن النبي -صلى الله عليه وسلم- مثله، قال يحيى: «هذا خطأ، الحديث حديث الليث بن سعد» .
<span data-type="title" id=toc-33>(٣٢) </span>قلت ليحيى بن معين: يحدث الوحاظي عن شيخ يقال له: إسحاق بن يحيى العوصي، يحدث عن الزهري، قال: «لا أعرفه» .',NULL,'280',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (11,'<span data-type="title" id=toc-34>(٣٣) </span>قلت ليحيى بن معين: حبيب بن أبي ثابت، عن أبي أرطأة، عن أبي سعيد الخدري. من أبو أرطأة؟ قال: «شيخ له» .
<span data-type="title" id=toc-35>(٣٤) </span>سئل يحيى بن معين عن سلم بن قادم، فقال: «ليس به بأس، ثقة» .
<span data-type="title" id=toc-36>(٣٥) </span>سمعت يحيى بن معين وذكر ابن الشاذكوني، فقال: «قد سمع، إلا أنه يكذب ويضع الحديث» .
<span data-type="title" id=toc-37>(٣٦) </span>سمعت يحيى بن معين وسئل غير مرة عن محمد بن عباد المكي، صاحب ابن عيينة، فقال: «لا أعرفه» .
<span data-type="title" id=toc-38>(٣٧) </span>سمعت يحيى بن معين يقول: «لم يكن لحماد بن زيد كتاب، إلا كتاب يحيى بن سعيد» .
<span data-type="title" id=toc-39>(٣٨) </span>سألت يحيى بن معين عن المشمعل بن إياس، فقال: «شيخ بصري، يحدث عنه يحيى القطان وابن مهدي، ليس به بأس» .
<span data-type="title" id=toc-40>(٣٩) </span>سألت يحيى قلت: يزيد بن هارون كتب عن الجريري؟ قال: «نعم» ، قال يحيى: «وكان كهمس بن الحسن يقول: إن الجريري',NULL,'281',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (12,'اختلط بعد ذلك بكثير» .
<span data-type="title" id=toc-41>(٤٠) </span>سألت يحيى عن خالد بن رباح، فقال: «بصري، ليس به بأس، يحدث عنه يحيى وغيره» .
<span data-type="title" id=toc-42>(٤١) </span>قلت ليحيى: إن يحيى القطان يحدث عن عمران أبي بكر، فقال: «هذا عمران القصير، ليس بشيء» .
<span data-type="title" id=toc-43>(٤٢) </span>سئل يحيى بن معين عن بشر بن رافع، يحدث عنه عبد الرزاق، قال: «كان من قريته -يعني: قرية عبد الرزاق-، ليس بشيء» .
<span data-type="title" id=toc-44>(٤٣) </span>قلت ليحيى بن معين: يحدث مالك بن أنس عن أيوب بن حبيب مولى أبي زهرة؟ قال: «نعم، هذا شيخ لمالك» .
<span data-type="title" id=toc-45>(٤٤) </span>سئل يحيى بن معين وأنا أسمع عن مؤثر بن عفازة، فقال: «مرثد بن عفازة كذا قال هشيم وغيره» ، ثم قال يحيى بن معين: «مؤثر ومرثد واحد» .
<span data-type="title" id=toc-46>(٤٥)</span>',NULL,'282',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (13,'سئل يحيى بن معين وأنا أسمع عن محمد بن عبد الله بن أبي رافع الذي يحدث عنه علي بن هاشم وحبان بن علي، قال: «ليس بثقة» ، قال ليحيى: فأبوه عبيد الله بن أبي رافع، قال: «ثقة» .
<span data-type="title" id=toc-47>(٤٦) </span>قيل ليحيى بن معين: أيما أمثل: محمد بن عبيد الله بن أبي رافع أو العرزمي؟ فقال: «ما فيهما ماثل» .
<span data-type="title" id=toc-48>(٤٧) </span>سمعت يحيى بن معين وسئل عن حوط العبدي: حوط ابن مَنْ؟ فقال: «حوط بن رافع، كذا نسبه عبيد الله بن عمر» .
<span data-type="title" id=toc-49>(٤٨) </span>سئل يحيى بن معين وأنا أسمع عن مجاهد قال: سمعت',NULL,'283',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (14,'عائشة. فقال: «كان يحيى / القطان ينكر ذلك، ويروى في حديث عن مجاهد قال: سمعت عائشة» .
<span data-type="title" id=toc-50>(٤٩) </span>وسمعت يحيى بن معين يقول: «الدنيا دار مكروه وحتوف» .
<span data-type="title" id=toc-51>(٥٠) </span>وسئل يحيى بن معين وأنا أسمع عن كنية تميم الداري، فقال: «أبو رقية» ، قال يحيى: «وهو يُروى في حديث» .
<span data-type="title" id=toc-52>(٥١) </span>سمعت يحيى بن معين وسئل عن عمر بن إسماعيل بن مجالد بن سعيد، فقال: «كذاب، يحدث أيضاً بحديث أبي',NULL,'284',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (15,'معاوية، عن الأعمش، عن مجاهد، عن ابن عباس، عن النبي -صلى الله عليه وسلم-: «أنا مدينة العلم وعلي بابها» ، وهذا حديث كذب، ليس له أصل» .
<span data-type="title" id=toc-53>(٥٢) </span>سمعت أحمد بن إبراهيم الدورقي يقول: «مات أبو النضر هاشم بن القاسم في سنة سبع ومائتين» .
<span data-type="title" id=toc-54>(٥٣) </span>قلت ليحيى بن معين: بلغك في تفسير حديث رسول الله -صلى الله عليه وسلم-: «لا يقتل مسلم بكافر، ولا ذو عهد في عهده» ؟ فقال لي يحيى بن معين: «ما سمعت فيه تفسيراً من ثقة» ، قلت: فإن أناساً يقولون: تفسيره: لا يقتل مسلم بكافر حربي، ولا يقتل ذو عهد في عهده بكافر حربي، فلم يرد عليَّ يحيى شيئاً.
<span data-type="title" id=toc-55>(٥٤)</span>',NULL,'285',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (16,'سمعت يحيى بن معين يقول: «حبيب بن أبي ثابت يكنى أبا يحيى، وهو حبيب بن قيس» .
<span data-type="title" id=toc-56>(٥٥) </span>سمعت يحيى بن معين يقول: «ما رأيت رجلاً أثبت في الحديث من عبد الرحمن بن مهدي، وأسن، يسأل عبد الرحمن عن الحديث ما يسأله إلا لينيله شيئاً» ، فقال ابن الغلابي ليحيى: (ينبغي أن يكون هذا بهز) ، فسكت يحيى.
<span data-type="title" id=toc-57>(٥٦) </span>سمعت يحيى بن معين يقول: «إذا وَلَدَت المرأة فبقيت البشيمة، يؤخذ مُرٌّ وزن درهم، وقدحٌ من نبيذ، وقدحٌ من',NULL,'286',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (17,'عسل، يطبخ حتى يصير قدحاً واحداً، ثم تشربه، نافع إن شاء الله تعالى» .
<span data-type="title" id=toc-58>(٥٧) </span>سمعت يحيى بن معين وسئل عن مخرمة بن سليمان، فقال: «مديني ثقة، روى عنه مالك بن أنس» .
<span data-type="title" id=toc-59>(٥٨) </span>سمعت يحيى بن معين وسئل عن مخرمة بن بكير، فكأنه ضعفه.
<span data-type="title" id=toc-60>(٥٩) </span>وسئل يحيى بن معين وأنا أسمع عن حسين بن عطاء بن يسار، روى عنه سعيد بن أبي هلال، قال: «هو ابن عطاء بن يسار، قد روى عنه أيضاً حاتم بن إسماعيل» قلت: كيف حديثه؟ قال: «وأيش روى شيئاً» يقلله، كأنه يعني: لا بأس به.
<span data-type="title" id=toc-61>(٦٠) </span>سألت يحيى بن معين عن عمر بن مجاشع، فقال: «شيخ مدائني لا بأس به» . قلت: حدثنا إبراهيم بن ناصح، عن شبابة، عن عمر بن مجاشع، عن تميم بن الحارث، عن أبيه قال:
(كان علي يكره أن يتزوج الرجل أو يسافر في المحاق، وإذا نزل القمر العقرب) ، فلم ينكر يحيى بن معين / هذا الحديث. قلت ليحيى: (ما المحاق؟) ، قال: «إذا بقي من الشهر يوم أو يومان» .',NULL,'287',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (18,'<span data-type="title" id=toc-62>(٦١) </span>سئل يحيى عن عبد العزيز بن سياه، فقال: «ليس به بأس ثقة» .
<span data-type="title" id=toc-63>(٦٢) </span>سمعت يحيى بن معين يقول: «ما أهلك الحديث أحد ما أهلكه أصحاب الإسناد» ، يعني: الذين يجمعون المسند، أي: يغمضون في الأخذ من الرجال.
<span data-type="title" id=toc-64>(٦٣) </span>قلت ليحيى بن معين: حدثنا إبراهيم بن عرعرة، عن أبي عبيدة معمر بن المثنى، عن رؤبة بن العجاج، عن أبيه قال: أنشدت أبا هريرة:
طاف الخيالان فهاجا سَقَما ... خيالُ تكنى وخيال تكتما
إلى:. . . . . . . . ... وكعباً أدرما
فقال أبو هريرة: (كان رسول الله -صلى الله عليه وسلم- يعجبه مثل هذا) . فأنكر هذا يحيى بن معين، ودفعه، ورَدَّه.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (19,'<span data-type="title" id=toc-65>(٦٤) </span>وقلت ليحيى بن معين: حدثنا نصر بن علي الجهضمي، حدثنا يوسف بن يعقوب الضبعي، عن الحجاج بن فرافصة، عن داود الوراق، عن عباد بن راشد قال: شهدت جنازة أم أبي هريرة، فلما خرجوا بها قال: (استغفروا لها غفر الله لكم) . فقال لي يحيى: «ما أعرف يوسف بن يعقوب هذا، ولا أعرف هذا الإسناد، وليس هذا الحديث بشيء» ، قال يحيى: «كان نصر بن علي قد جالس الأصمعي قديماً، وكان من أصحابه» ، وأظنه قال: «وأكثر عنه» .
<span data-type="title" id=toc-66>(٦٥) </span>سألت يحيى بن معين عن زفر بن الهذيل الذي حدثنا عنه أبو نعيم، فقال: «ثقة مأمون، كوفي» .
<span data-type="title" id=toc-67>(٦٦) </span>سألت يحيى عن حديث شريك، عن علي بن علي، عن إبراهيم قال: سمع علقمةُ مؤذناً يؤذن بليل. فقال: (خالف هذا أصحاب محمد -صلى الله عليه وسلم-، لو بات على فراشه كان خيراً له) .',NULL,'289',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (20,'قلت ليحيى بن معين: من علي بن علي هذا؟ قال: «هذا علي بن السائب، كوفي ثقة، يحدث عنه شريك» ، قلت: من يحدث عنه غير شريك؟ قال: «ما علمت أحداً يحدث عنه غير شريك» .
<span data-type="title" id=toc-68>(٦٧) </span>قلت ليحيى بن معين: غندر سمع من سعيد بن أبي عروبة في الاختلاط أو قبل؟ فقال لي يحيى: «زعموا أنه لم يسمع منه إلا في الصحة، وأن أول من عرف اختلاط سعيد بن أبي عروبة: غندر» .
<span data-type="title" id=toc-69>(٦٨) </span>سمعت يحيى بن معين يقول: «قال هشام بن عروة: رأيت ابن عمر وأنس بن مالك وسهل بن سعد وجابراً»
<span data-type="title" id=toc-70>(٦٩) </span>قال يحيى بن معين: «وقد روى يحيى بن سعيد الأنصاري عن هشام بن عروة» .
<span data-type="title" id=toc-71>(٧٠) </span>وسمعت يحيى بن معين يقول: «صالح بن كيسان أكبر سناً من الزهري، سمع ابن الزبير وابن عمر» .
<span data-type="title" id=toc-72>(٧١) </span>سمعت يحيى بن معين يقول: «ابن أبي سمينة البصري،',NULL,'290',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (21,'وشباب، وعبيد الله بن / معاذ بن معاذ العنبري، ليسوا أصحاب حديث، ليسوا بشيء» .
<span data-type="title" id=toc-73>(٧٢) </span>سمعت يحيى بن معين يقول: «مثنى بن معاذ لا بأس به» .
<span data-type="title" id=toc-74>(٧٣) </span>سمعت يحيى بن معين يقول: «مات منصور بن المعتمر وأيوب في سنة إحدى وثلاثين ومائة في الطاعون، أو اثنتين وثلاثثين، وأيوب أقدم موتاً من منصور، وقد أدرك منصور المُسَوِّدة» .
<span data-type="title" id=toc-75>(٧٤) </span>سئل يحيى بن معين وأنا أسمع: حماد بن سلمة، أروى عن هشام بن عروة، أو أبو أسامة؟ فقال: «أبو أسامة» .
<span data-type="title" id=toc-76>(٧٥) </span>سئل يحيى عن ابن أبي ليلى، هل كان ثبتاً في الحديث؟ فقال: «ما كان يثبت في الحديث» .
<span data-type="title" id=toc-77>(٧٦) </span>وسئل يحيى وأنا أسمع عن محمد بن بشر العبدي، فقال: «لم يكن به بأس» ،
<span data-type="title" id=toc-78>(٧٧) </span>قيل: فأبو أسامة أحب إليك أو محمد بن بشر؟ فقال: «أبو أسامة» .',NULL,'291',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (22,'<span data-type="title" id=toc-79>(٧٨) </span>سئل يحيى وأنا أسمع: أيما أحب إليك، محمد بن عبيد، أو يعلى بن عبيد؟ فقال: «يعلى أحب إلي» ، وأراه قال: «وأثبت» .
<span data-type="title" id=toc-80>(٧٩) </span>سألت يحيى عن أسيد بن زيد الجمال، فقال: «كذاب، قد أتيته ببغداد في الحذائين، فسمعته يحدث بأحاديث كذب» .
<span data-type="title" id=toc-81>(٨٠) </span>سمعت يحيى بن معين يقول: «أهل مكة يتقون اللبن والزبد والجبن، لأن الشاء للمجذمة» .
<span data-type="title" id=toc-82>(٨١) </span>سمعت يحيى بن معين يقول: «كان أبو معاوية الأسود بطرسوس يخرج فيلتقط أسفل جزرة أو شيئاً مطروحاً لقمة أو عوداً، فيجمع من هذا ثم يطبخه فيأكله، وكان رجل صدق، وكان يقول: (ما ضرهم ما أصابهم في الدنيا إذا جبر الله لهم بالجنة كل مصيبة) » . قال يحيى بن معين: «صدق والله، ما ضر رجلاً اتقى الله على ما أصبح وأمسى من أمر الدنيا، وما الدنيا إلا كحلم، لقد حججت وأنا ابن أربع وعشرين سنة، خرجت راجلاً من بغداد إلى مكة، هذا منذ خمسين سنة، كأنما كان أمس» .',NULL,'292',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (23,'<span data-type="title" id=toc-83>(٨٢) </span>سمعت يحيى بن معين وسئل عن عبد العزيز بن أبان، فقال: «كذاب خبيث يضع الحديث» .
<span data-type="title" id=toc-84>(٨٣) </span>سمعت يحيى بن معين وسئل: سمع غندر من عبد الله بن جعفر المخرمي؟ فقال: «نعم، سمع من عبد الله بن جعفر المخرمي ومالك بن أنس» .
<span data-type="title" id=toc-85>(٨٤) </span>سمعت يحيى بن معين يقول: «عمر بن قيس الماصر مولى لثقيف، ثقة» ، قلت ليحيى: لم سمي الماصر، لعله كان يكون بالماصر؟ قال: «لا أدري لم سمي الماصر» .
<span data-type="title" id=toc-86>(٨٥) </span>سمعت يحيى بن معين وذكر أبا بكر بن عفان ختن مهدي بن حفص، فقال: «كذاب، يكذب، رأيت له حديثًا حدث به عن أبي إسحاق الفزاري كذب» .
<span data-type="title" id=toc-87>(٨٦) </span>سئل يحيى بن معين وأنا أسمع، وقيل له: ابن جريج قال: حدثني أبو شداد، عن مجاهد، قال: (في اليربوع سخلة) ، مَنْ أبو شداد هذا؟ قال: «شيخ له ما أعلمه يعرف، / ولا أعرفه» .
<span data-type="title" id=toc-88>(٨٧)</span>',NULL,'293',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (24,'سمعت يحيى بن معين يقول: «حدثنا سفيان، حدثنا عباس بن عبد الله بن معبد بن عباس قارئُ بني العباس قبل أن يجيئوا في زمانهم» ، قلنا ليحيى: ما الحديث؟ قال: «لا أحفظه» ، فذكر أن له حديثين أو ثلاثة.
<span data-type="title" id=toc-89>(٨٨) </span>سئل يحيى بن معين وأنا أسمع عن يحيى بن أبي الحجاج الخاقاني بصري، فقال: «لم يكن بثقة، هو أخو خاقان بن الأهتم» ، قلت: إنه يحدث عنه سعيد بن عامر، قال: «كان سعيد بن عامر لا يبالي عمن حدث» .
<span data-type="title" id=toc-90>(٨٩) </span>سئل يحيى بن معين وأنا أسمع عن شبيب بن شيبة بصري، فقال: «لم يكن بثقة» .
<span data-type="title" id=toc-91>(٩٠) </span>سئل يحيى بن معين وأنا أسمع عن يوسف السمتي، فقال: «ليس بثقة، كذاب، لا يكتب حديثه» .',NULL,'294',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (25,'<span data-type="title" id=toc-92>(٩١) </span>سمعت يحيى بن معين يقول: «قال رجل لعبد الله بن نمير: نحن إنما نمر بوكيع ثم نجيئك -قال يحيى: وكان ابن نمير أكبر من وكيع بعشر سنين أو أكثر-، فقال له ابن نمير: إنما يقرأ من له عقل، ويطلب العلم من له عقل، قد كنت أظن أن لك عقلاً، لا والله لا حدثتك بحديث أبداً» ، وذكر يحيى ابن معين هذا عند سوء أدب بعض أصحاب الحديث.
<span data-type="title" id=toc-93>(٩٢) </span>قلت ليحيى بن معين: ترى أن ينظر الرجل في شيء من الرأي؟ فقال: «أي رأي؟» ، قلت: رأي الشافعي وأبي حنيفة، فقال: «ما أرى لمسلم أن ينظر في رأي الشافعي، ينظر في رأي أبي حنيفة أحب إلي من أن ينظر في رأي الشافعي» .
<span data-type="title" id=toc-94>(٩٣) </span>وسمعت رجلاً يقول ليحيى بن معين: سئل ابن داود الخريبي: شرب النبيذ خيرٌ أو تركه؟، فقال: (شربه) .
<span data-type="title" id=toc-95>(٩٤) </span>سمعت يحيى بن معين يقول: «ترك النبيذ خير من شربه، ومن رخص فيه فيما أسكر كثيره، شريك وسفيان وحسن بن حي ووكيع وابن نمير وهؤلاء، وهم مع ذلك ينهون عن الخليطين،',NULL,'295',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (26,'وعن المنادمة والمعاقرة والجلوس عليه، والنقيع عندهم خمر، والبصريون يرخصون في النقيع، ويقولون: هو حلال» ، قال يحيى: «وكل نبيذ يجوز ثلاثة أيام فلا خير فيه عندهم، وعند سفيان وشريك وابن حي وابن نمير ووكيع وأبي معاوية، كلهم يكرهه» . قال يحيى بن معين: «وحديث النبي -صلى الله عليه وسلم-: (كل مسكر حرام) عندي صحيح، أبو سلمة عن عائشة» . قال يحيى بن معين: «من عاقر على النبيذ وسكر وأداروا الأقداح، فهؤلاء لا مروءة لهم، ولا تقبل شهادتهم، ولا يصلى خلفهم» ، وأراه قال: «هم فُسَّاق» .
<span data-type="title" id=toc-96>(٩٥) </span>نعيت ليحيى بن معين محرز بن عون، فاستغفر له، وترحم عليه، وقال: «كان شيخ صدق، لا بأس به» .
<span data-type="title" id=toc-97>(٩٦) </span>سمعت يحيى بن معين وسئل عن ابن جريج، عن مظاهر بن مظاهر،',NULL,'296',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (27,'من مظاهر هذا؟
قال: «هذا مظاهر بن أسلم، شيخ له ليس بشيء، قد سمع منه أبو عاصم النبيل أيضًا» يعني: من مظاهر هذا.
<span data-type="title" id=toc-98>(٩٧) </span>سمعت يحيى بن معين يقول: «إسحاق بن عبد الله بن أبي طلحة، وإسماعيل بن عبد الله بن أبي طلحة، وعبد الله بن عبد الله بن أبي طلحة إخوة مدنيون» ، قلت ليحيى: ثقات هم؟ قال: «نعم، ثقات» .
<span data-type="title" id=toc-99>(٩٨) </span>سئل يحيى بن معين وأنا أسمع عن حديث مبارك، عن الحسن قال: أخبرني أبو الأحوص، عن عبد الله، فقال يحيى: «هو صحيح» .
<span data-type="title" id=toc-100>(٩٩) </span>وسئل يحيى بن معين وأنا أسمع عن حماد بن زيد، عن خطاب بن يعقوب، فقال: «هذا شيخ بصري» .
<span data-type="title" id=toc-101>(١٠٠) </span>سمعت يحيى بن معين يذكر أنه رأى عبد الله بن وهب بمكة ما يلتفت إليه أحد، فقال رجل ليحيى: كان ابن ستين سنة؟ قال: «لا، قد كان له سنّ» .
<span data-type="title" id=toc-102>(١٠١) </span>سمعت يحيى بن معين وذكر حديثاً عن سفيان بن عيينة، فقال: «قد حدث به البار أيضاً - يعني: شعبة -، عن محمد بن عبد الرحمن مولى',NULL,'297',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (28,'آل طلحة، عن سليمان بن يسار، عن عبد الله بن عتبة، عن عمر قال: (يتزوج العبد اثنتين، ويطلق تطليقتين، وتعتد حيضتين أو شهراً ونصفاً» .
<span data-type="title" id=toc-103>(١٠٢) </span>سمعت يحيى بن معين وسئل أيضاً عن يونس بن بكير، فقال: «كان ثقة صدوقاً، إلا أنه كان مع جعفر بن يحيى البرمكي، وكان موسراً» ، فقال له رجل: إنهم يرمونه بالزندقة بكذا وكذا، فقال: «كذب» ، ثم قال يحيى: «رأيت ابني أبي شيبة أتياه، فأقصاهما، وسألاه كتاباً، فلم يعطهما؛ فذهبا يتكلمان فيه» . قال يحيى بن معين: «قد كتبت عنه» ، قال أبو خيثمة: «قد كتبت عنه» .
<span data-type="title" id=toc-104>(١٠٣) </span>سمعت يحيى وسئل عن سعيد القداح، فقال: «ثقة» .
<span data-type="title" id=toc-105>(١٠٤) </span>وسئل يحيى بن معين وأنا أسمع عن جابر بن نوح الحماني،',NULL,'298',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (29,'فضعفه وقال: «رأيت حفص بن غياث يضحك منه يهزأ به» ، ثم قال يحيى: «ليس بشيء» .
<span data-type="title" id=toc-106>(١٠٥) </span>سمعت أبا حذيفة بن مروان بن معاوية قال: «كان مروان يكنى أبا عبد الله» .
<span data-type="title" id=toc-107>(١٠٦) </span>سمعت يحيى بن معين يحدث أبا حذيفة، فقال: «رأيت مروان جاء مرةً إلى سفيان بن عيينة يستأذن عليه، نادى: يا أبا محمد، يا أبا محمد» .
<span data-type="title" id=toc-108>(١٠٧) </span>وسمعت يحيى يقول: «لما قدم مروان قيل لي، فأتيته في خان منارة، فإذا عنده معلى بن منصور وهو يسأله في قرطاس، فلما رآني طوى القرطاس، ثم لم أره عنده بعد ذلك، ولزمناه فكتبنا عنه» .
<span data-type="title" id=toc-109>(١٠٨) </span>قلت ليحيى بن معين: حدثنا سعيد بن منصور، عن رواد بن الجراح، عن سفيان الثوري، عن الزبير بن عدي، عن أنس بن مالك',NULL,'299',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (30,'قال: قال رسول الله -صلى الله عليه وسلم-: «من اجتنب أربعاً دخل الجنة: الفروج، والدماء، والأموال، والأشربة» ، فقال لي يحيى: «هذا كذب، ليس للزبير بن عدي عن أنس إلا ذاك الحديث الواحد» ، أحسبه قال: «حدثناه حفص، عن سفيان ومالك بن مغول، عن الزبير بن عدي» .
<span data-type="title" id=toc-110>(١٠٩) </span>قلت ليحيى بن معين / حدثنا سعيد بن منصور، حدثنا أنس بن أبي القاسم الحضرمي، قال: حدثني عبد الرحمن بن الأسود، عن أبيه قال: سألت عائشة عن الطيب عند الإحرام، فقالت: (كأني أنظر) ، فلم ينكر يحيى الحديث، ولم يعرف الشيخ إسناد هذا الحديث.
<span data-type="title" id=toc-111>(١١٠) </span>سألت يحيى بن معين عن اسم أبي غالب صاحب أبي أمامة، فقال: «حزور» ، قلت: ثقة؟، قال: «ليس به بأس» .',NULL,'300',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (31,'<span data-type="title" id=toc-112>(١١١) </span>سألت يحيى بن معين عن اسم التميمي، فقال: «أربدة» ، قلت: من يقول ذلك؟ قال: «إسرائيل» .
<span data-type="title" id=toc-113>(١١٢) </span>وسألت يحيى بن معين عن اسم ابن الحكم الغفاري، فقال: «عبد الكبير» .
<span data-type="title" id=toc-114>(١١٣) </span>سألت يحيى بن معين عن وهب بن إسماعيل، حدثنا عنه سعدويه، فقال: «كوفي قد سمعت منه، ليس بشيء» ، قلت: ليس بشيء؟ قال: «لم يكن بثقة» .
<span data-type="title" id=toc-115>(١١٤) </span>وسئل يحيى بن معين وأنا أسمع عن حارث النقال وأحمد بن إبراهيم الموصلي، فقال: «ثقتان صدوقان» .
<span data-type="title" id=toc-116>(١١٥)</span>',NULL,'301',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (32,'سمعت يحيى بن معين وسئل عن عيسى بن المسيب البجلي الكوفي، فقال: «كان ضعيفاً، وكان خرج إلى خراسان، وولي القضاء، ولاه أسد بن عبد الله» .
<span data-type="title" id=toc-117>(١١٦) </span>سمعت يحيى بن معين وسئل عن عيسى بن أبي عيسى الحناط، فقال: «هو عيسى بن ميسرة» .
<span data-type="title" id=toc-118>(١١٧) </span>وسئل يحيى بن معين وأنا أسمع عن عيسى بن جارية صاحب يعقوب القمي، فقال: «ليس بشيء» .
<span data-type="title" id=toc-119>(١١٨) </span>وسئل يحيى وأنا أسمع عن عيسى بن ميمون الجرشي، قال: «لا بأس به» .
<span data-type="title" id=toc-120>(١١٩) </span>قلت ليحيى بن معين: تعرف ثعلبة بن بلال الأعمى شيخ بصري؟ قال: «لا» ، قلت: حدثنا عنه القواريري، عن عبيد الله بن أبي بكر، عن أنس بن مالك أن رسول الله -صلى الله عليه وسلم- كان يشرب اللبن فيصيب ثوبه، فيصلي ولا يغسله. فلم يعرف يحيى الحديث أيضاً.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (33,'<span data-type="title" id=toc-121>(١٢٠) </span>قلت ليحيى: ثعلبة الذي روى عنه جرير، قال: «هو ثعلبة بن سهيل، كوفي نزل الري، وقد روى عنه الكوفيون أيضاً» ، قلت: ثقة؟ قال: «لا بأس به» .
<span data-type="title" id=toc-122>(١٢١) </span>سألت يحيى عن حزام بن إسماعيل، يحدث عن العلاء بن المسيب، فقال: «كوفي» ، قلت: ثقة؟ قال: «ثقة» .
<span data-type="title" id=toc-123>(١٢٢) </span>وسمعت يحيى بن معين يقول: لم يبلغ هشيم الثمانين، مات سفيان الثوري وهو ابن نيف وستين سنة، ولم يبلغ شعبة الثمانين.
<span data-type="title" id=toc-124>(١٢٣) </span>سمعت يحيى بن معين يقول: «مات شعبة قبل سفيان بسنة» .
<span data-type="title" id=toc-125>(١٢٤) </span>قال يحيى بن معين: «ومات عبد الرحمن بن مهدي وهو ابن نيف وستين» .
<span data-type="title" id=toc-126>(١٢٥) </span>سمعت يحيى بن معين يقول: «عيسى بن ميمون الذي يحدث عن القاسم عن عائشة عن النبي -صلى الله عليه وسلم-: (أعظم النِّكاح بركةً أيسره',NULL,'303',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (34,'مؤنة) يقال له: ابن / تليدان، وهو من آل أبي قحافة، ليس به بأس، وهو الذي يحدث عنه حماد بن سلمة قال: حدثني ابن سخبرة، هو هذا، ولم يرو هذا عن محمد بن كعب شيئاً، والذي يحدث عن محمد بن كعب ليس بشيء» ، يعني: آخر يحدث عن محمد بن كعب، ليس بشيء.
<span data-type="title" id=toc-127>(١٢٦) </span>سألت يحيى بن معين عن محمد بن مناذر الشاعر، فقال: «لم يكن بثقة ولا مأمون، رجل سوء، نفي من البصرة» ، وذكر منه مجوناً وغير ذلك، قلت: إنما يكتب عنه شعر وحكايات عن الخليل بن أحمد، فقال: «هذا نعم» ، كأنه لم ير بهذا بأساً، ولم يره موضعاً للحديث.
<span data-type="title" id=toc-128>(١٢٧) </span>سألت يحيى بن معين قلت: عبد الرحمن بن مهدي قال: حدثني أبو يونس، قال: «هذا سلم بن زرير، ضعيف الحديث» .',NULL,'304',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (35,'<span data-type="title" id=toc-129>(١٢٨) </span>سألت يحيى عن عمرو بن أبي عمرو، فقال: «ليس بذاك القوي» .
<span data-type="title" id=toc-130>(١٢٩) </span>سألت يحيى بن معين عن أصرم بن غياث، فقال: «شيخ نيسابوري، قد سمعت منه هاهنا ببغداد، ليس بثقة» .
<span data-type="title" id=toc-131>(١٣٠) </span>سألت يحيى بن معين عن أبي يونس القوي، فقال: «كوفي ثقة» . وهو الذي يقال له: أبو يونس الطواف.
<span data-type="title" id=toc-132>(١٣١) </span>سألت يحيى بن معين عن عمرو بن أبي حكيم الذي روى عنه شعبة، فقال: «واسطي ثقة» .
<span data-type="title" id=toc-133>(١٣٢) </span>سألت يحيى بن معين عن شعيب بن صفوان، فقال: «كان هاهنا ببغداد، وليس حديثه بشيء، وأيش كان عنده؟! كان عنده سمر» ، لم يكتب عنه يحيى بن معين شيئاً. قلت ليحيى: حدثنا عنه منصور بن أبي مزاحم بتلك الرسائل الطوال، فقال: «نعم» .
<span data-type="title" id=toc-134>(١٣٣) </span>سئل يحيى بن معين وأنا أسمع عن ابن وهب والمقرئ، فقال: «ابن وهب أحب إلي من المقرئ، وأعلم بحديث المصريين، وأحفظ لأسامي مشايخهم، وأكثر حديثاً» .',NULL,'305',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (36,'<span data-type="title" id=toc-135>(١٣٤) </span>سمعت يحيى بن معين يقول: «قلت لأبي مسهر: ابن سماعة عرض على الأوزاعي؟ قال: (أحسن حاله إن كان عرض) ، ثم قال لي أبو مسهر: (لم يكن هاهنا بدمشق أثبت في الأوزاعي من هقل) » .
<span data-type="title" id=toc-136>(١٣٥) </span>سئل يحيى بن معين وأنا أسمع عن عبد الحميد بن أبي العشرين، فقال: «ليس به بأس» ، قال إبراهيم: كان عبد الحميد بن حبيب بن أبي العشرين كاتب الأوزاعي.
<span data-type="title" id=toc-137>(١٣٦) </span>سمعت يحيى بن معين وذكر أبا مسهر فقال: «كان يبغض الموالي، قال لي يوماً: (عندك حديث في الموالي في عيبهم؟) » ، قلت ليحيى: فمن كان أبو مسهر؟ قال: «كان عربياً غسانياً» .
<span data-type="title" id=toc-138>(١٣٧) </span>وسئل يحيى بن معين وأنا أسمع عن محمد بن راشد، فقال: «لم يكن به بأس، كان يقول بالقدر» .
<span data-type="title" id=toc-139>(١٣٨) </span>قلت ليحيى بن معين: عتي بن ضمرة الذي يحدث عن الحسين، روى عنه أحد غير الحسن؟ فقال: «يحدث قرة بن خالد عن عبد الله بن عتي / بن ضمرة عن أبيه» .',NULL,'306',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (37,'قال لي بعض أصحابنا: إن يحيى بن معين يرويه عن أبي قطن عن قرة.
<span data-type="title" id=toc-140>(١٣٩) </span>سمعت يحيى بن معين يقول: «يونس بن يزيد شهد الإملاء من الزهري للسلطان، وشعيب بن أبي حمزة شهد الإملاء» .
<span data-type="title" id=toc-141>(١٤٠) </span>سئل يحيى بن معين وأنا أسمع عن عبد الرحمن بن نمر عن الزهري، فقال: «شيخ من الدمشقيين، ضعيف الحديث، يحدث عنه الوليد بن مسلم» .
<span data-type="title" id=toc-142>(١٤١) </span>سئل يحيى بن معين عن المثنى بن الصباح، فقال: «ضعيف الحديث، هو أقوى من طلحة بن عمرو» .
<span data-type="title" id=toc-143>(١٤٢) </span>سألت يحيى بن معين عن الزبير بن سعيد الهاشمي، فقال: «ضعيف، كان ينزل المدائن، يحدث عنه جرير بن حازم وعبد الله بن المبارك وإسماعيل بن عياش وغيرهم» .',NULL,'307',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (38,'<span data-type="title" id=toc-144>(١٤٣) </span>سئل يحيى وأنا أسمع عن عبد الله بن مسلم بن هرمز، فقال: «مكي ضعيف الحديث» .
<span data-type="title" id=toc-145>(١٤٤) </span>سئل يحيى وأنا أسمع عن عبد العزيز بن عمر بن عبد العزيز، فقال: «ليس به بأس، ثقة» .
<span data-type="title" id=toc-146>(١٤٥) </span>سئل يحيى وأنا أسمع عن يزيد بن زياد بن أبي الجعد، فقال: «كوفي ثقة» .
<span data-type="title" id=toc-147>(١٤٦) </span>سئل يحيى بن معين عن عبد الحميد بن جعفر الأنصاري، فقال: «ليس به بأس، كان قدرياً يرى رأي أهل القدر» .
<span data-type="title" id=toc-148>(١٤٧) </span>سئل يحيى بن معين وأنا أسمع: من أثبت من روى عن الزهري؟ فقال: «مالك بن أنس، ثم معمر، ثم عقيل، ثم يونس، ثم شعيب والأوزاعي والزبيدي وسفيان بن عيينة، وكل هؤلاء ثقات» ، قلت ليحيى: أيما أثبت: سفيان أو الأوزاعي؟ فقال: «سفيان ليس به بأس، والأوزاعي أثبت منه، والزبيدي أثبت منه - يعني: من سفيان بن عيينة -» .',NULL,'308',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (39,'قلت ليحيى: أيما أكبر: الأوزاعي أو سفيان بن عيينة؟ قال: «الأوزاعي أكبر من سفيان بن عيينة» .
<span data-type="title" id=toc-149>(١٤٨) </span>سئل يحيى بن معين وأنا أسمع عن محمد بن أبي حفصة، فقال: «هو محمد بن ميسرة، كان بالبصرة يحدث عن الزهري، ضعيف الحديث» .
<span data-type="title" id=toc-150>(١٤٩) </span>سئل يحيى بن معين وأنا أسمع عن أبي جابر البياضي، فقال: «مدني، ليس بثقة» .
<span data-type="title" id=toc-151>(١٥٠) </span>سئل يحيى وأنا أسمع عن فرقد السبخي، فقال: «ثقة» .
<span data-type="title" id=toc-152>(١٥١) </span>سئل يحيى وأنا أسمع عن موسى بن عقبة، فقال: «مدني، ثقة» . قال يحيى بن معين: «ليس موسى بن عقبة في نافع مثل مالك وعبيد الله بن عمر» .
<span data-type="title" id=toc-153>(١٥٢) </span>سئل يحيى عن موسى بن علي بن رباح، فقال: «ثقة» .',NULL,'309',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (40,'<span data-type="title" id=toc-154>(١٥٣) </span>سألت يحيى عن الفضل بن حبيب السراج، فقال: «شيخ من أهل المدائن، كان هاهنا ببغداد في السراجين، لم يكن به بأس» . حدثنا يحيى بن معين عنه بحديث عبد الله بن العلاء، عن الضحاك بن عبد الرحمن بن عرزب، عن أبي هريرة، عن النبي -صلى الله عليه وسلم-: «إن أول ما يسأل عنه العبد يوم القيامة من النعيم: ألم أصح جسمك، وأرويك من الماء البارد» .
<span data-type="title" id=toc-155>(١٥٤) </span>قال إبراهيم بن الجنيد: خالد بن دينار النيلي يكنى أبا الوليد.
<span data-type="title" id=toc-156>(١٥٥) </span>ويزيد بن أبي زياد يكنى أبا عبد الله.
<span data-type="title" id=toc-157>(١٥٦) </span>سألت يحيى بن معين عن كثير بن حبيش، يحدث',NULL,'310',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (41,'عنه محمد بن عمرو عن أنس وعن عمرة عن عائشة، فقال: «هذا شيخ مديني، ثقة» ، قلت: حدث عنه غير محمد بن عمرو؟ قال: «ما سمعت» .
<span data-type="title" id=toc-158>(١٥٧) </span>سئل يحيى بن معين وأنا أسمع عن اسم أبي ريحانة، فقال: «عبد الله بن مطر» ، قلت ليحيى: بصري؟ قال: «نعم» ، قلت: ثقة؟ قال: «ليس به بأس، يحدث عنه شعبة وابن علية وغير واحد» .
<span data-type="title" id=toc-159>(١٥٨) </span>قيل ليحيى بن معين وأنا أسمع: شعبة، عن أبي ميمونة، عن أبي هريرة، أبو ميمونة هو سلمة بن المجنون الذي يحدث عنه شريك؟ قال: «يقولون ذاك» .',NULL,'311',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (42,'<span data-type="title" id=toc-160>(١٥٩) </span>سئل يحيى بن معين وأنا أسمع عن الأحوص بن حكيم، فقال: «ليس بشيء» .
<span data-type="title" id=toc-161>(١٦٠) </span>سئل عن أبي بكر بن أبي مريم، فقال: «ضعيف الحديث، وهو أقوى من الأحوص» .
<span data-type="title" id=toc-162>(١٦١) </span>سئل يحيى وأنا أسمع عن أبي أويس المدني، فقال: «ضعيف الحديث،
<span data-type="title" id=toc-163>(١٦٢) </span>وأبو بكر بن أبي أويس كان يقال له: أبو بكر الأعشى، ليس به بأس، وإسماعيل أخوه مخلط، يكذب، ليس بشيء» .
<span data-type="title" id=toc-164>(١٦٣) </span>سئل يحيى وأنا أسمع عن يزيد بن المقدام بن شريح، فقال: «ليس به بأس» .
<span data-type="title" id=toc-165>(١٦٤) </span>سئل يحيى بن معين وأنا أسمع عن اسم أبي المعلى العطار، فقال: «اسمه يحيى، بصري، يحدث عنه شعبة وابن علية وحماد بن زيد، وهؤلاء ليس بهم بأس» .',NULL,'312',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (43,'<span data-type="title" id=toc-166>(١٦٥) </span>قال رجل ليحيى بن معين وأنا أسمع: سمع الزهري من ابن عمر؟ قال: «لا» ، قال: فرآه رؤية؟ قال: «يشبه» .
<span data-type="title" id=toc-167>(١٦٦) </span>قال رجل ليحيى بن معين وأنا أسمع: النعمان بن بشير سمع من النبي -صلى الله عليه وسلم- شيئًا؟ قال: «أهل المدينة يقولون: لا، كان صغيراً، ونحن نروي كما قد علمتم: (سمعت النبي -صلى الله عليه وسلم-) » .
<span data-type="title" id=toc-168>(١٦٧) </span>سئل يحيى بن معين وأنا أسمع عن يحيى بن الجزار، فقال: «يحيى بن زبان» . /',NULL,'313',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (44,'الجزء الثاني
بسم الله الرحمن الرحيم. وصلى الله على سيدنا محمد وآله أجمعين.
حدثنا أبو إسحاق إبراهيم بن عبد الله بن الجنيد الختلي، قال:
<span data-type="title" id=toc-169>(١٦٨) </span>قال رجل ليحيى بن معين وأنا أسمع: سمع الحسن من ابن عمر؟ قال: «نعم، قال الحسن: (دخلنا على ابن عمر بالبطحاء) ، ولم يسمع الحسن من جابر ولا من ابن عباس» .
<span data-type="title" id=toc-170>(١٦٩) </span>سمعت يحيى قال: «والان بن قرفة صاحب حديث أبي بكر، وإسماعيل بن سميع عن والان هو غير هذا، قال: (ذبح أهلي شاة) » .
<span data-type="title" id=toc-171>(١٧٠) </span>سئل يحيى بن معين وأنا أسمع: أيما أثبت في عمرو',NULL,'315',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (45,'بن دينار: ابن عيينة أو محمد بن مسلم؟ فقال: «ابن عيينة أثبت في عمرو من محمد بن مسلم، ومن داود العطار، ومن حماد بن زيد. وسفيان أكثر حديثاً منهم عن عمرو وأسند» ، قيل: فابن جريج؟ قال: «جميعاً ثقة» ، كأنه سوى بينهما في عمرو.
<span data-type="title" id=toc-172>(١٧١) </span>سئل يحيى بن معين وأنا أسمع عن حماد بن سلمة أحب إليك أو حماد بن زيد؟ فقال يحيى: «حماد بن زيد أحفظ، وحماد بن سلمة ثقة» .
<span data-type="title" id=toc-173>(١٧٢) </span>قيل ليحيى بن معين: أيما أحب إليك في ثابت: سليمان بن المغيرة أو حماد بن سلمة؟ قال: «كلاهما ثقة ثبت، وحماد بن سلمة أعرف بحديث ثابت من سليمان، وسليمان ثقة» .
<span data-type="title" id=toc-174>(١٧٣) </span>قيل ليحيى بن معين وأنا أسمع: ابن عيينة أحسنهم حديثاً؟ فقال يحيى بن معين: «الثوري أحسن حديثاً من ابن عيينة وأسند، وسفيان بن عيينة أحسن حديثاً عن الكوفيين، وشعبة أسند من الثوري» .
<span data-type="title" id=toc-175>(١٧٤) </span>قال رجل ليحيى بن معين وأنا أسمع: كيف شعبة في الأعمش؟ فقال: «ثقة، إلا أنه يخطئ في أحاديث» .
<span data-type="title" id=toc-176>(١٧٥) </span>قال يحيى: «أثبت الناس في الأعمش سفيان» .
<span data-type="title" id=toc-177>(١٧٦) </span>قال أبو خيثمة وأنا أسمع: يحدث جرير عن منصور ألفًا ومائتي حديث، فقال يحيى: «قد روى عنه الثوري أكثر من ثمانمائة حديث عن منصور» .',NULL,'316',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (46,'<span data-type="title" id=toc-178>(١٧٧) </span>سمعت يحيى بن معين يقول: أبو عوانة أروى عن مغيرة من جرير» .
<span data-type="title" id=toc-179>(١٧٨) </span>سئل يحيى بن معين وأنا أسمع: تعرف عن مغيرة، عن إبراهيم أنه كره بيع القردة؟ فقال يحيى: «كان شيخ بالبصرة يقال له: أيوب بن واقد يحدث به عن مغيرة عن إبراهيم، ليس بثقة» .
<span data-type="title" id=toc-180>(١٧٩) </span>سمعت يحيى بن معين يقول: «قضى شريك بن عبد الله على عبد الله بن إدريس بقضية، وأمر بحبسه بحق، فقال له ابن إدريس: (القضاء فيه كذا وكذا) ، فقال له شريك: (اذهب فأفتِ بهذا حاكةَ الزعافر) » ، قال يحيى: «الزعافر من بني أود» .
<span data-type="title" id=toc-181>(١٨٠) </span>سئل يحيى بن معين وأنا أسمع: سمع قتادة من سعيد بن جبير؟ فقال يحيى بن معين: «لم يسمع من سعيد بن جبير، ولا من مجاهد، ولا من سليمان شيئًا، ربما أدخل بينهم رجلاً، وربما أرسل، وأكثر ذلك لا يدخل، يرسلها» .
<span data-type="title" id=toc-182>(١٨١) </span>قال أبو داود النحوي سليمان بن معبد ليحيى بن معين:',NULL,'317',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (47,'حدثنا مسلم بن إبراهيم، قال: سمعت حماد بن سلمة يقول: أعض الله أبا حنيفة بكذا وكذا، لا يكني، فقال ابن معين: «أساء أساء» .
<span data-type="title" id=toc-183>(١٨٢) </span>سمعت يحيى بن معين يقول: «عبد الوهاب بن الورد هو وهيب بن الورد، وهو أخو عبد الجبار بن الورد» .
<span data-type="title" id=toc-184>(١٨٣) </span>قلت ليحيى بن معين: تعلم محمد بن سيرين يدخل بينه وبين عقبة بن أوس أحداً، أو عقبة بن أوس يدخل بينه وبين عبد الله بن عمرو أحداً؟ فقال: «لا أعلمه، وعقبة بن أوس يقال له أيضاً: يعقوب بن أوس» ، قال ابن الغلابي: (يزعمون أن عقبة بن أوس السدوسي لم يسمع من عبد الله بن عمرو، إنما يقول: قال عبد الله بن عمرو) .
<span data-type="title" id=toc-185>(١٨٤) </span>ذكر ليحيى بن معين وأنا شاهد حديث عن عبد الحميد بن جعفر: (تخرج نار من حبس سيل) ، فقال: «رواه عثمان بن عمر، فقال كذا، ورواه أبو عاصم، ورواه علي بن ثابت» ،',NULL,'318',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (48,'فقال يحيى: «علي بن ثابت أثبت هؤلاء وأكيس» .
<span data-type="title" id=toc-186>(١٨٥) </span>قلت ليحيى بن معين: وحدثنا داود بن مهران الدباغ، حدثنا عبد الله بن داود الواسطي، عن عبد الرحمن ابن أخي محمد بن المنكدر، عن محمد بن المنكدر، عن جابر قال: قال عمر ذات يوم لأبي بكر: (يا خير الناس بعد رسول الله -صلى الله عليه وسلم-) ، فقال أبو بكر: (أما الآن قلت ذاك، لقد سمعت رسول الله -صلى الله عليه وسلم- يقول: «ما طلعت الشمس على رجل خير من عمر» ) . فقال يحيى بن معين: «ما أعرف عبد الرحمن ابن أخي محمد بن المنكدر» ، وأنكر الحديث، ولم يعرفه.
<span data-type="title" id=toc-187>(١٨٦) </span>قلت ليحيى بن معين: حدثنا عبد الله بن صالح العجلي، عن حماد بن سلمة، عن يوسف بن سعد، عن الحارث بن حاطب، أن رجلاً سرق على عهد رسول الله -صلى الله عليه وسلم-، فأتي به، فقال رسول الله -صلى الله عليه وسلم-: «اقتلوه» ،',NULL,'319',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (49,'في قصة. فقال لي يحيى بن معين: «هذا يوسف بن سعد شيخ بصري ثقة، روى عنه يونس بن عبيد ومنصور بن زاذان وحبيب بن الشهيد، وقد لقيه حماد بن سلمة وسمع منه أيضاً» .
<span data-type="title" id=toc-188>(١٨٧) </span>وسئل يحيى بن معين وأنا أسمع: أيما أكبر: سعيد أو الحسن؟ فقال: «سعيد» ، يعني: أخا الحسن البصري.
<span data-type="title" id=toc-189>(١٨٨) </span>قال يحيى بن معين: «عبد الرحمن بن إسحاق، يقال لنا أيضاً: عباد بن إسحاق، ثقة،
<span data-type="title" id=toc-190>(١٨٩) </span>وعبد الرحمن بن إسحاق الكوفي، هو أبو شيبة، ليس بشيء» .
<span data-type="title" id=toc-191>(١٩٠) </span>سألت يحيى بن معين عن المشمعل بن ملحان الطائي، فقال: «كان هاهنا، ما أرى كان به بأس» .
<span data-type="title" id=toc-192>(١٩١) </span>وسألت يحيى بن معين عن حكيم بن نافع القرشي الرقي، فقال: «لا بأس به، وأيش عنده؟» .
<span data-type="title" id=toc-193>(١٩٢) </span>سمعت يحيى بن معين وذكر حديثًا عن ابن عيينة، عن حميد / الأعرج، عن سليمان بن عتيق، عن جابر أن النبي -صلى الله عليه وسلم- أمر',NULL,'320',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (50,'بوضع الجوائح، قال: «كان سفيان لا يحدث به، كان عليه فيه قول، فجاءه إنسان بشفاعة، فحدثه ونسي، ثم اذّكر، فقال: (آه) » ، فقال رجل ليحيى بن معين: أكان ابن جريج يرويه؟ قال: «لا أعلمه» .
<span data-type="title" id=toc-194>(١٩٣) </span>سمعت يحيى بن معين يقول: «أبو علقمة الفروي اسمه عبد الله بن محمد بن أبي فروة، ثقة، قد كتبت عنه،
<span data-type="title" id=toc-195>(١٩٤) </span>وصالح بن عبد الله بن أبي فروة، وعبد الأعلى بن عبد الله بن أبي فروة، وعبد الحكيم بن أبي فروة، ليس بهم بأس،
<span data-type="title" id=toc-196>(١٩٥) </span>وإسحاق بن عبد الله بن أبي فروة ليس بشيء،
<span data-type="title" id=toc-197>(١٩٦) </span>وقد روى الزهري عن صالح بن عبد الله بن أبي فروة» .
<span data-type="title" id=toc-198>(١٩٧) </span>قال أبو داود النحوي ليحيى بن معين وأنا أسمع: سمعت أبا نعيم وذُكر له حديث، فقال: (من روى هذا؟) ، فقالوا: زكريا بن عدي،',NULL,'321',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (51,'فقال أبو نعيم: (ما له وللحديث، ذاك بالتوراة أعلم) ، فقال يحيى بن معين: «كان زكريا بن عدي لا بأس به، وكان أبوه يهودياً فأسلم» . قال يحيى: «وكان أبو نعيم صاحب مزاح، لقد قال له رجل يوماً وأنا شاهد من أهل خراسان: يا أبا نعيم، إني أريد الخروج فأخبرني باسمك، قال: (اسمي دعاك) » ، قال: «فودعه ومضى» . قال: «وسمعته يوماً عند منزل ابن أبي شيبة ضرب بيده على الأرض، وقال: (أنا أبو العجائب) » .
<span data-type="title" id=toc-199>(١٩٨) </span>سألت يحيى بن معين عن الربيع بن حبيب أبي سلمة، فقال: «شيخ بصري ثقة» ، فقلت: إن يحيى القطان سئل عنه، قال: «هو نحو عمر بن الوليد الشني» ، فقال يحيى: «الربيع بن حبيب ثقة، وعمر بن الوليد ثقة» ، ثم قال لي يحيى بن معين: «لم يكن عامة مشايخ البصريين يسوون عند يحيى بن سعيد شيئاً» ، فذكر هماماً وغيره.
<span data-type="title" id=toc-200>(١٩٩) </span>سألت يحيى بن معين عن جهير بن يزيد، فقال: «بصري ثقة» ، قلت: ممن هو؟ قال: «عبدي» ، قلت: فإن القطان قال: (حوشب بن عقيل',NULL,'322',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (52,'أثبت عندي من جهير) ، فقال يحيى بن معين: «جهير ثقة، وحوشب بن عقيل ثقة» .
<span data-type="title" id=toc-201>(٢٠٠) </span>قلت ليحيى بن معين: وسئل القطان: سالم أبو النضر عندك فوق سمي؟ فقال: (نعم) ، فقال يحيى بن معين: «سالم أبو النضر ثقة، وسمي ثقة، جميعاً سواء» .
<span data-type="title" id=toc-202>(٢٠١) </span>قال لي يحيى بن معين: «لقد رأيت رجلاً يسأل يحيى بن سعيد عن أشياء، فجعل يقول: (لا أدري، لا أدري) ، حتى رحمته، وكان يحيى بن سعيد رجل صدق» .
<span data-type="title" id=toc-203>(٢٠٢) </span>وسمعت يحيى بن معين يقول: «حبة بن سلمة من أصحاب عبد الله بن مسعود» .
<span data-type="title" id=toc-204>(٢٠٣) </span>سمعت يحيى بن معين وذكروا أبا عبيدة بن محمد بن عمار بن ياسر، فقال يحيى: «ثقة» .
<span data-type="title" id=toc-205>(٢٠٤) </span>سمعت يحيى بن معين يقول: «عبد الرزاق مولى لموالي قوم من اليمن من العرب» .
<span data-type="title" id=toc-206>(٢٠٥) </span>وسمعت رجلاً قال ليحيى بن معين: سمعت إسماعيل بن علية يقول: شهدت أيوب يكتب إلى يحيى بن أبي كثير، فقال: «لا، وإسماعيل قد أدرك يحيى بن أبي كثير» ، ثم قال: «والله ما روى أيوب عن يحيى',NULL,'323',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (53,'بن أبي كثير شيئاً فيه خير، ولكن هشام الدستوائي سمعته يقول: (حدثنا ابن عيينة، عن أيوب، عن يحيى بن أبي كثير، أن عمر بن الخطاب كنى نصرانياً بأبي حسان) » .
<span data-type="title" id=toc-207>(٢٠٦) </span>وسمعت يحيى بن معين يقول: «أبو الأشعث الصنعاني شامي» .
<span data-type="title" id=toc-208>(٢٠٧) </span>سمعت يحيى بن معين يقول: «حدثنا عمرو بن عون بن أوس الواسطي -وأطنب في الثناء عليه-،
<span data-type="title" id=toc-209>(٢٠٨) </span>أخبرنا خالد بن عبد الله الواسطي، عن حميد قال: بلغني عن أبي موسى أنه قال: (لو أن النعمة والصحة وكلا بامرئ كانتا له حتفاً قاضياً) » ، قيل ليحيى: فإن حماد بن سلمة يقول: عن حميد عن الحسن عن النبي -صلى الله عليه وسلم- بهذا.
<span data-type="title" id=toc-210>(٢٠٩) </span>سمعت يحيى وسئل عن عباس الأزرق، فقال: «كذاب خبيث» .
<span data-type="title" id=toc-211>(٢١٠) </span>قلت ليحيى بن معين: عبد الرحمن بن عبد الله الدشتكي؟ فقال: «رازي لا بأس به» .',NULL,'324',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (54,'قلت: عمرو بن قيس؟ قال: «لا بأس به» . قلت: ثقتان؟ قال: «ثقتان» .
<span data-type="title" id=toc-212>(٢١١) </span>قلت ليحيى: حديث سفيان، عن علي بن زيد، عن الحسن، عن عبد الله بن مغفل، عن النبي -صلى الله عليه وسلم-: «الدجال قد أكل الطعام ومشى في الأسواق» . قال ابن الجنيد: هكذا حدثنا إسحاق بن أبي إسرائيل، وحدثنا أبو موسى الهروي يقول: عن عمران بن حصين، فقال لي يحيى: «إسحاق أثبت» . قلت: فإن حماد بن سلمة يقول: عن علي بن زيد، عن الحسن، عن النبي -صلى الله عليه وسلم-، فأيما الصحيح؟ قال: «ما أدري» ، قلت: أما سبق إلى قلبك أن الحديث حديث حماد؟ قال: «ما أدري، إن علي بن زيد لم يكن بالحافظ» .',NULL,'325',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (55,'وقد قال لي مرةً أخرى في حديث سألته عنه ورواه حماد بن زيد وخالفه حماد بن سلمة عن علي ابن زيد، فقال لي يحيى: «حماد بن سلمة أعلم بحديث علي بن زيد من حماد بن زيد، لكثرة روايته عنه» .
<span data-type="title" id=toc-213>(٢١٢) </span>قلت ليحيى بن معين: حماد بن سلمة عن داود الوراق عن داود بن فراهج، هذا داود الوراق هو داود بن أبي هند؟ قال: «لا، هذا رجل آخر» .
<span data-type="title" id=toc-214>(٢١٣) </span>قلت: حماد عن أبي العوام جليس لثابت؟ قال: «هو أبو العوام الخزاز، وبعضهم يقول: الجزار، يبيع الجزور» .
<span data-type="title" id=toc-215>(٢١٤) </span>سمعت يحيى وذكر ضرار بن صرد، فقال: «ليس حديثه بشيء» .
<span data-type="title" id=toc-216>(٢١٥)</span>',NULL,'326',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (56,'سئل يحيى وأنا أسمع عن حيوة بن شريح والجرجسي يزيد بن عبد ربه، فقال: «ثقتان» ، فقال له ابن الغلابي: (كتبت عن حيوة؟) ، قال: «نعم» .
<span data-type="title" id=toc-217>(٢١٦) </span>قلت ليحيى: حدثنا ابن أبي سمينة البصري، عن الثقفي، عن جعفر، عن أبيه، عن جابر أن المشاة تعرضوا للنبي -صلى الله عليه وسلم-، فقال يحيى: «حدثناه الثقفي هاهنا ببغداد» .
<span data-type="title" id=toc-218>(٢١٧) </span>حدثنا يحيى بن معين، حدثنا معتمر، عن محمد بن فضاء، عن أبيه، عن علقمة بن عبد الله المزني، عن أبيه قال: نهى رسول الله -صلى الله عليه وسلم- عن كسر سكة المسلمين الجائزة عنهم إلا من بأس. قلت ليحيى: محمد بن فضاء كان يعبر الرؤيا؟ قال: «نعم، كان يعبر الرؤيا، وحديثه مثل تعبيره» ، أي: أنه ضعيف الحديث.
<span data-type="title" id=toc-219>(٢١٨) </span>سألت يحيى بن معين عن الهذيل بن الحكم، فقال: «قد',NULL,'327',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (57,'رأيته بالبصرة، وكتبت عنه، ولم يكن به بأس» ، قلت: ما روى عن عبد العزيز بن أبي رواد، عن عكرمة، عن ابن عباس، عن النبي -صلى الله عليه وسلم-: (موت الغريب شهادة) ؟ قال يحيى: «هذا حديثه الذي كان يُسأل عنه، ليس هذا الحديث بشيء، هذا حديث منكر» .
<span data-type="title" id=toc-220>(٢١٩) </span>قال رجل ليحيى بن معين وأنا أسمع: أبو أروى سماه أحد؟ قال: «لا» .
<span data-type="title" id=toc-221>(٢٢٠) </span>سألت يحيى بن معين عن أبي هاشم صاحب الزعفراني، عمار بن عمارة، فقال: «ثقة» .
<span data-type="title" id=toc-222>(٢٢١) </span>سئل يحيى وأنا أسمع عن غسان بن عبيد الموصلي، فقال: «قد رأيته، كان هاهنا -يعني: ببغداد-، ضعيف الحديث» .',NULL,'328',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (58,'قلت ليحيى: عمر بن أيوب أثبت من هذا؟، قال: «نعم» ، وأثنى على عمر بن أيوب خيراً. قلت ليحيى: كتبتَ عن عمر بن أيوب شيئاً؟ قال: «نعم» .
<span data-type="title" id=toc-223>(٢٢٢) </span>قلت ليحيى: عبد الملك بن قدامة الجمحي؟ قال: «صالح» .
<span data-type="title" id=toc-224>(٢٢٣) </span>قلت ليحيى: عبد الرحمن بن عبد الله بن دينار؟ قال: «قد روى عنه يحيى بن سعيد القطان» .
<span data-type="title" id=toc-225>(٢٢٤) </span>سألت يحيى عن أبي غرة الدباغ، فقال: «بصري، كان قدم عليهم الكوفة، وكتب عنه أبو أحمد الزبيري وغيره، ليس به بأس» ، قال يحيى: «حدثنا عنه أبو سلمة التبوذكي، وله كنيتان» ، يعني: أبا عزة.
<span data-type="title" id=toc-226>(٢٢٥) </span>سألت يحيى بن معين عن فرات بن السائب الجزري، فقال: «ليس بشيء» ،
<span data-type="title" id=toc-227>(٢٢٦) </span>قلت: ففرات بن سلمان؟ قال: «ثقة» .
<span data-type="title" id=toc-228>(٢٢٧)</span>',NULL,'329',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (59,'قال رجل ليحيى بن معين وأنا أسمع: كتبت حديث حسان بن إبراهيم الكرماني؟ قال: «ليس به بأس إذا حدث عن ثقة» . قلت ليحيى بن معين: فحديث حسان، حديث رافع بن خديج في القدر؟ قال: «ليس بشيء» .
<span data-type="title" id=toc-229>(٢٢٨) </span>قلت ليحيى بن معين: منصور بن أبي الأسود كوفي؟ قال: «ليس به بأس، كان من الشيعة الكبار» ، قلت ليحيى: ما اسم أبيه؟ قال: «لا أدري» .
<span data-type="title" id=toc-230>(٢٢٩)</span>',NULL,'330',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (60,'سأل رجل يحيى بن معين وأنا أسمع عن سويد بن عبد العزيز الدمشقي، فقال: «ليس بثقة» .
<span data-type="title" id=toc-231>(٢٣٠) </span>وسئل يحيى عن عبيد الله بن عمرو الرقي، قال: «ليس به بأس» .
<span data-type="title" id=toc-232>(٢٣١) </span>قال: «وأبو المليح الرقي ليس به بأس» .
<span data-type="title" id=toc-233>(٢٣٢) </span>وسمعت يحيى بن معين يقول: «كل من حدث يشتهي أن يجوز عنه كل شيء» ، / قال يحيى: «وهذا إسحاق بن أبي إسرائيل يحدث عن محمد بن جابر، وليس بثقة» .
<span data-type="title" id=toc-234>(٢٣٣) </span>سمعت يحيى بن معين يقول: «عافية القاضي كان ضعيفاً في الحديث، وكان ولي القضاء قبل أبي يوسف» .
<span data-type="title" id=toc-235>(٢٣٤) </span>قال رجل ليحيى: فالعوفي؟ قال: «كان ضعيفاً في القضاء ضعيفاً في الحديث» .',NULL,'331',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (61,'<span data-type="title" id=toc-236>(٢٣٥) </span>سألت يحيى بن معين عن إبراهيم بن عيينة، فقال: «كان مسلماً صدوقاً، لم يكن من أصحاب الحديث» .
<span data-type="title" id=toc-237>(٢٣٦) </span>سألت يحيى بن معين عن علي بن أبي علي اللهبي، قال: «ليس هو بشيء» .
<span data-type="title" id=toc-238>(٢٣٧) </span>قال لنا يحيى بن معين ابتداءً: «عيسى بن قرطاس ونصر أبو عمر الخزاز وسعيد بن طريف الإسكاف وعلي بن حزور، ليسوا بشيء، لا يحل لأحد أن يروي عن هؤلاء شيئاً» .
<span data-type="title" id=toc-239>(٢٣٨) </span>سمعت يحيى بن معين يقول: «سعد الإسكاف ليس بشيء» .
<span data-type="title" id=toc-240>(٢٣٩) </span>وسألت يحيى بن معين عن مسافر الجصاص، فقال: «ثقة» . قال ابن الجنيد: كوفي، يحدث عنه أبو نعيم وأحمد بن يونس، حدثنا عنه، وأبو أحمد روى عنه.
<span data-type="title" id=toc-241>(٢٤٠) </span>سمعت يحيى بن معين يقول: «كتبت عن شيخ في زهران يقال له: (زاجر بن الصلت) » .',NULL,'332',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (62,'<span data-type="title" id=toc-242>(٢٤١) </span>سمعت يحيى بن معين وسئل عن الحسن الجفري، فقال: «ضعيف الحديث» .
<span data-type="title" id=toc-243>(٢٤٢) </span>سمعت يحيى بن معين يقول: «حديث ابن جريج، عن محمد بن أبي عطاء، عن موسى بن وردان، عن أبي هريرة قال: قال رسول الله -صلى الله عليه وسلم-: (من مات مريضاً مات شهيداً) ، ليس هذا الحديث بشيء، محمد بن أبي عطاء هو محمد بن أبي يحيى» .
<span data-type="title" id=toc-244>(٢٤٣) </span>سألت يحيى بن معين عن حديث محمد بن عثمان بن صفوان بن أمية الجمحي، عن هشام بن عروة، عن أبيه، فذكر يحيى كلاماً لم أضبطه عن أبيه موقوف.
<span data-type="title" id=toc-245>(٢٤٤) </span>قلت ليحيى بن معين: جويبر بن سعيد، هو أبو بسطام؟',NULL,'333',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (63,'قال: «لا، جويبر بن سعيد أبو القاسم، وأبو بسطام يحيى بن عبد الرحمن التميمي» ، قلت: كيف حديث جويبر؟ قال: «ضعيف» .
<span data-type="title" id=toc-246>(٢٤٥) </span>سألت يحيى بن معين عن الوقاصي، قال: «لا تكتب حديثه، كان يكذب» ، ثم قال: «كان من ولد سعد بن بن أبي وقاص» .
<span data-type="title" id=toc-247>(٢٤٦) </span>سمعت يحيى بن معين يقول وذكر المشمس -يعني: النبيذ-، فقال: «خبيث رديء» .
<span data-type="title" id=toc-248>(٢٤٧) </span>سمعت يحيى بن معين يقول: «إسرائيل بن يونس يكنى أبا يوسف» .
<span data-type="title" id=toc-249>(٢٤٨) </span>وسمعت يحيى بن معين وقيل له: جنادة بن أبي أمية الذي روى عنه مجاهد، له صحبة؟ قال: «نعم، جنادة بن أبي أمية الأزدي» ، قلت ليحيى: هو الذي يروى عن عبادة بن الصامت؟ قال: «هو هو» .',NULL,'334',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (64,'<span data-type="title" id=toc-250>(٢٤٩) </span>سألت يحيى بن معين عن أبي حمزة السكري، فقال: «ثقة» .
قيل ليحيى بن معين: ما اسم أبي حمزة السكري؟ قال: «محمد بن ميمون» .
<span data-type="title" id=toc-251>(٢٥٠) </span>قلت ليحيى: فخارجة / بن مصعب؟ قال: «ليس بشيء» .
<span data-type="title" id=toc-252>(٢٥١) </span>سمعت يحيى يقول: «سوار بن مصعب كوفي ليس بشيء» .
<span data-type="title" id=toc-253>(٢٥٢) </span>سئل يحيى بن معين وأنا شاهد عن مصعب بن المقدام الخثعمي، فقال: «ما أرى به بأساً» .
<span data-type="title" id=toc-254>(٢٥٣) </span>قلت ليحيى: فمصعب بن سلام؟ قال: «صدوق، كان هاهنا -يعني: ببغداد-، فأعطوه كتاباً للحسن بن عمارة، فحدث به عن شعبة، ثم رجع عنه» ، فقال عباس الدوري ليحيى: كتبت عن مصعب بن سلام شيئاً؟ قال: «نعم، ليس به بأس» .',NULL,'335',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (65,'<span data-type="title" id=toc-255>(٢٥٤) </span>وسمعت يحيى بن معين يقول: «إبراهيم بن أبي حرة جزري، روى عنه ابن عيينة» .
<span data-type="title" id=toc-256>(٢٥٥) </span>وسمعت يحيى بن معين يقول: «أبو مطرف هو عبيد الله بن طلحة بن عبيد الله بن كريز» ، قلت ليحيى: من روى عنه؟ قال: «هارون المعلم صاحب القراءة، وحماد بن زيد» ، وسمعت يحيى يقول: «هارون المعلم ثقة» .
<span data-type="title" id=toc-257>(٢٥٦) </span>سمعت يحيى يقول: «الربيع بن سليم قد رأى أبا لبيد» .
<span data-type="title" id=toc-258>(٢٥٧) </span>قلت ليحيى بن معين: برد بن سنان كيف حديثه؟ قال: «ليس به بأس» .
<span data-type="title" id=toc-259>(٢٥٨) </span>سمعت يحيى يقول: «الربيع بن سليم بصري روى عنه وكيع، والربيع بن مسلم أكبر منه، جميعًا بصريان، وإنما كان عند الربيع بن سليم حديثان أوثلاثة» .',NULL,'336',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (66,'<span data-type="title" id=toc-260>(٢٥٩) </span>سألت يحيى بن معين عن صالح بن موسى الطلحي، فقال: «ليس بشيء» .
<span data-type="title" id=toc-261>(٢٦٠) </span>وسألته عن هشام أبي المقدام، فقال: «ليس بشيء» .
<span data-type="title" id=toc-262>(٢٦١) </span>سمعت يحيى بن معين وسأله أبو طالب عن محمد بن راشد الشامي، فقال: «صالح، كان بالبصرة، وكان ثقة صدوقاً، وقد دخل بغداد» .
<span data-type="title" id=toc-263>(٢٦٢) </span>وسأله عن ثوبان، فقال: «صالح» .
<span data-type="title" id=toc-264>(٢٦٣) </span>وسأله عن قيس بن الربيع، فقال: «لا شيء،
<span data-type="title" id=toc-265>(٢٦٤) </span>وعبد الوهاب بن مجاهد لا شيء،',NULL,'337',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (67,'<span data-type="title" id=toc-266>(٢٦٥) </span>وعدي بن الفضل لا شيء» .
<span data-type="title" id=toc-267>(٢٦٦) </span>وسمعت يحيى وسئل عن مُحِلّ الذي روى عن إبراهيم، فقال: «محل ثقة لا بأس به» .
<span data-type="title" id=toc-268>(٢٦٧) </span>وسئل عن عمرو بن قيس، فقال: «ليس بشيء» .
<span data-type="title" id=toc-269>(٢٦٨) </span>وسئل عن سعيد بن أبي أيوب -قال ابن الجنيد: الذي يقال له: (ابن مقلاص) -، فقال: «ثقة» .
<span data-type="title" id=toc-270>(٢٦٩) </span>وسمعت أبا طالب يسأل يحيى بن معين عن زاذان أبي عمر، فقال: «ثقة» .
<span data-type="title" id=toc-271>(٢٧٠) </span>وسأله عن حميد بن هلال، فقال: «ثقة، لا يسأل عن مثل هؤلاء» .
<span data-type="title" id=toc-272>(٢٧١) </span>سمعت يحيى وسئل عن إسماعيل بن عبد الملك، فقال: «ليس به بأس، كوفي» .
<span data-type="title" id=toc-273>(٢٧٢) </span>وسمعت يحيى وسئل عن عمر بن موسى، فقال: «ليس بشيء» .',NULL,'338',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (68,'<span data-type="title" id=toc-274>(٢٧٣) </span>وسئل يحيى عن أبي شهاب موسى بن نافع، فقال: «ثقة» .
<span data-type="title" id=toc-275>(٢٧٤) </span>وسمعت يحيى وسئل عن عبد العزيز بن حصين -يعني: الترجمان-، فقال: «ليس بشيء» .
<span data-type="title" id=toc-276>(٢٧٥) </span>وسئل يحيى عن موسى بن عبيدة، فقال: «صالح» .
<span data-type="title" id=toc-277>(٢٧٦) </span>وسئل عن حرام بن عثمان، فقال: «ليس بشيء» .
<span data-type="title" id=toc-278>(٢٧٧) </span>وسئل عن يحيى بن عبيد الله، فقال: «ضعيف الحديث» .
<span data-type="title" id=toc-279>(٢٧٨) </span>سألت يحيى عن نافع بن يزيد، فقال: «نافع بن يزيد الإسكندراني ثقة» .
<span data-type="title" id=toc-280>(٢٧٩) </span>سألت يحيى عن / الزنبري، فقال: «ما كان عندي ثقة» .',NULL,'339',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (69,'<span data-type="title" id=toc-281>(٢٨٠) </span>وسألت يحيى عن القاسم بن مالك المزني، فقال: «ما كان به بأس، صدوق» .
<span data-type="title" id=toc-282>(٢٨١) </span>سألت يحيى عن أبي مكين، فقال: «ثقة، روى عنه وكيع وأبو أسامة» .
<span data-type="title" id=toc-283>(٢٨٢) </span>سئل يحيى بن معين عن عوام بن عباد بن العوام، وعلي بن أبي طالب الذي كان بالبصرة، فقال: «ليسا بشيء» .
<span data-type="title" id=toc-284>(٢٨٣) </span>سمعت يحيى بن معين يقول: «أبو حفص العبدي لم يكن ثقة» .
<span data-type="title" id=toc-285>(٢٨٤) </span>قلت ليحيى بن معين: إن يحيى بن سعيد يزعم أن قتادة لم يسمع من سنان بن سلمة الهذلي حديث ذؤيب الخزاعي في البدن، فقال: «ومن شك في هذا؛ أن قتادة لم يسمع منه ولم يلقه؟!» .',NULL,'340',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (70,'<span data-type="title" id=toc-286>(٢٨٥) </span>قلت ليحيى بن معين: مغيرة أحب إليك أو حماد بن أبي سليمان؟ فقال يحيى بن معين: «أنا سمعت يحيى بن سعيد يقول: (حماد بن أبي سليمان أحب إلي من مغيرة) » ، فقلت ليحيى بن معين: (وأنت، مغيرة أحب إليك أو حماد؟) ، قال: «حماد أحب إلي، كما قال يحيى» ، قلت ليحيى بن معين: في إبراهيم؟ قال: «في إبراهيم وغيره» .
<span data-type="title" id=toc-287>(٢٨٦) </span>قال رجل ليحيى بن معين وأنا أسمع: حديث حماد بن سلمة، عن حماد عن إبراهيم، عن الأسود، عن عائشة، عن النبي -صلى الله عليه وسلم-: «رفع القلم عن ثلاثة» ، هو عندك واهي؟) ، فقال يحيى: «ليس يروي هذا أحد إلا حماد بن سلمة عن حماد» .
<span data-type="title" id=toc-288>(٢٨٧) </span>وسمعت يحيى بن معين يقول: «تحريم النبيذ صحيح، وأقف عنده لا أحرمه، قد شربه قوم صالحون بأحاديث صحاح، وحرمه قوم آخرون بأحاديث صحاح» ،',NULL,'341',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (71,'ثم قال يحيى بن معين: «أنا سمعت يحيى بن سعيد يقول: (حديث الطلاء وحديث عتبة بن فرقد جميعاً صحيحان) » ، قلت ليحيى بن معين: تعني حديث الزهري عن السائب بن يزيد عن عمر: (بلغني أن عبيد الله) ؟ قال: «نعم» ، قلت: وحديث عتبة بن فرقد، حديث إسماعيل عن قيس عن عتبة بن فرقد؟ قال: «نعم، مشهوران، جميعاً صحيحان» .
<span data-type="title" id=toc-289>(٢٨٨) </span>سألت يحيى بن معين عن هلال بن خباب، وقلت: إن يحيى القطان يزعم أنه تغير قبل أن يموت واختلط، فقال يحيى: «لا، ما اختلط ولا تغير» ، قلت ليحيى: فثقة هو؟ قال: «ثقة مأمون» .
<span data-type="title" id=toc-290>(٢٨٩)</span>',NULL,'342',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (72,'سألت يحيى عن عبد الأعلى الثعلبي، فقال: «صالح، ليس بذاك» .
<span data-type="title" id=toc-291>(٢٩٠) </span>سألت يحيى بن معين عن إبراهيم بن مهاجر، فقال: «ليس بذاك القوي» .
<span data-type="title" id=toc-292>(٢٩١) </span>سئل يحيى بن معين وأنا أسمع عن زهير السلولي، فقال: «شيخ بصري، ليس بشيء» . قلت ليحيى: من يحدث عنه؟ فقال يحيى: «سمعت معتمر بن سليمان يروي عن زهير شيخ من بني سلول، عن يونس، عن الحسن قال: (يجزئ من الصرم السلام) ، وليس يحدث بهذا عن يونس ثقة، وليس هذا الشيخ ثقة» ، قال يحيى: «وكان هشيم يدلسه عن يونس عن الحسن» ، ثم قال يحيى: «كان هشيم يأخذ الحديث من السحاب» .
<span data-type="title" id=toc-293>(٢٩٢) </span>قلت ليحيى بن معين: إن يحيى بن سعيد القطان يزعم أن ابن أبي نجيح لم يسمع التفسير من مجاهد، وإنما أخذه من القاسم بن أبي بزة، فقال يحيى بن معين: «كذا قال ابن عيينة، ولا أدري أحق ذلك أم باطل، زعم سفيان بن عيينة أن مجاهداً كتبه للقاسم بن أبي بزة ولم يسمعه من مجاهد أحد غير القاسم» ،',NULL,'343',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (73,'ثم قال يحيى: «ولا ندري ما هذا» ، ثم قال: «ورقاء وشبل وعيسى بن ميمون الجرشي كلهم سواء» . قال حسين بن حبان ليحيى وأنا أسمع: سمعت هذا من ابن عيينة؟ قال: «بلغني هذا عنه» .
<span data-type="title" id=toc-294>(٢٩٣) </span>سألت يحيى بن معين عن أيوب بن مدرك، فقال: «كذاب، كان ها هنا، يمامي، قد رأيته وكتبت عنه، ليس بشيء» .
<span data-type="title" id=toc-295>(٢٩٤) </span>قلت ليحيى بن معين: آدم بن علي وجبلة بن سحيم عندك واحد؟ قال: «آدم ثقة، وجبلة ثقة، وما أرى يُروى عن كليهما عشرون حديثاً» .
<span data-type="title" id=toc-296>(٢٩٥) </span>قلت ليحيى بن معين: قتادة عن أبي العالية عن ابن عباس: (أخبرني رجال مرضيون) ، ترى قتادة سمع هذا من أبي العالية؟ قال: «نعم، قد سمع هذا قتادة من أبي العالية» .
<span data-type="title" id=toc-297>(٢٩٦) </span>سمعت يحيى بن معين وسئل عن حماد بن دليل أبي زيد، قاضي المدائن، فقال: «ثقة» .',NULL,'344',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (74,'<span data-type="title" id=toc-298>(٢٩٧) </span>قلت ليحيى بن معين: إن يحيى بن سعيد سئل عن إسماعيل بن سميع، فقال: (إنما تركه زائدة لأنه صُفْري، وأما الحديث فلا بأس به) ، فقال لي يحيى بن معين: «إسماعيل بن سميع ثقة» .
<span data-type="title" id=toc-299>(٢٩٨) </span>سئل يحيى بن معين وأنا أسمع عن عويس، فقال: «لا أخبره» ، فقال رجل ليحيى: كتبت عنه شيئًا؟ فقال: «ما كتبت عنه شيئًا قط» .
<span data-type="title" id=toc-300>(٢٩٩) </span>سئل يحيى بن معين وأنا أسمع عن إسحاق بن إسماعيل، فقال: «كان عندي لا بأس به، صدوق، ولكنه يبلى من الناس، وقد كلمني أن أكلم أمه تأذن له في الخروج إلى جرير، فكلمتها، فأجابتني، فخرج مع اثني عشر رجلاً مشاة، ولم يكن له تلك الأيام شيء» . قلت ليحيى: فما بلي به من الناس؟ قال: «يكذبونه وهو صدوق» ، قلت: كان يتهم تلك الأيام بالكذب، أو الآن بعدما حدث؟ قال: «لا، الآن بعدما حدث» ، ثم قال يحيى: «ما كان به بأس» .
<span data-type="title" id=toc-301>(٣٠٠) </span>سئل يحيى بن معين وأنا أسمع: عمارة بن عبيد سمع منه أحد غير أبي إسحاق؟ فقال: «ما سمعناه» .',NULL,'345',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (75,'<span data-type="title" id=toc-302>(٣٠١) </span>قال لي يحيى بن معين: «ألك ولد؟» ، قلت: لا، ولا امرأة، قال: «فما مقامك هاهنا، الحق بالثغر، أو ألق نفسك في تلك السواحل، فإن الدنيا ليست بشيء» ، ثم قال يحيى: «مرَّ يحيى بن سليم الطائفي على سفيان بن عيينة، فقال له سفيان: (يا أبا زكريا، ما فعلت مولاتكم؟) ، قال: (ماتت رحمها الله تعالى، وإني لأغبطها أنها لم تترك شيئاً) » .
<span data-type="title" id=toc-303>(٣٠٢) </span>سمعت يحيى بن معين وذكر أحمد بن نصر بن مالك، فترحم عليه، قال: «قد ختم الله له بالشهادة» ، قلت: كتبت عنه شيئاً؟ قال: «نعم، نظرت له في مشايخ الجنديين وأحاديث عبد الصمد بن معقل وعبد الله بن عمرو بن مسلم الجندي» .
<span data-type="title" id=toc-304>(٣٠٣) </span>قلت ليحيى: من يحدث عن عبد الله بن عمرو بن مسلم؟ قال: «عبد الرزاق» ، قلت: ثقة؟ قال: «هو ثقة ليس به بأس» ، قلت: فأبوه عمرو بن مسلم الذي يحدث عن طاوس، كيف هو؟ قال: «هو وأبوه لا بأس به» ،
<span data-type="title" id=toc-305>(٣٠٤) </span>ثم قال يحيى: «كان عند أحمد بن نصر مصنفات هشيم كلها، وعن مالك أحاديث كبار» ، ثم قال يحيى: «كان أحمد يقول: (ما دخل عليه أحد يصدقه -يعني: الخليفة-) » ، ثم قال يحيى: «ما كان يحدث، كان يقول لست موضع ذاك» ، يعني: أحمد بن نصر بن مالك - رحمه الله -. وأحسن يحيى الثناء عليه.
<span data-type="title" id=toc-306>(٣٠٥)</span>',NULL,'346',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (76,'وسئل يحيى بن معين عن مالك بن عبد الله البردادي، فقال: «هذا شيخ يحدث عن أهل مصر» .
<span data-type="title" id=toc-307>(٣٠٦) </span>وسمعت أبا عبد الرحمن بن الغلابي يقول: (مات محمد بن أبي بكر الصديق وهو ابن ست وعشرين سنة، ومات أبو بكر ومحمد ابن سنتين، ولم يلق القاسم بن محمد أباه) .
<span data-type="title" id=toc-308>(٣٠٧) </span>سمعت يحيى بن وسئل عن رغبان، فقال: «هو مولى لحبيب بن مسلمة، من أهل الشام» ، قلت ليحيى: كتبنا عنه حديثًا بالشام عن رغبان جد هؤلاء، أهو هذا؟ فقال يحيى: «نعم» .
<span data-type="title" id=toc-309>(٣٠٨) </span>سمعت يحيى بن معين وذكر عبد المجيد بن عبد العزيز بن',NULL,'347',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (77,'أبي رواد، فذكر من نبله وهيئته، ثم قال: قال لي علي بن المديني: (ما ندع عبد المجيد إلا بحال الحميدي، كان الحميدي ينهى عنه) ، ثم قال يحيى: «وكان عبد المجيد صدوقاً، ما كان يرفع رأسه إلى السماء» ، قال: «وكانوا يعظمونه» ، ثم قال يحيى: «رفع عليه حارث النقال النعل» ، قال: «فبلغهم، فهرب، ولو قدروا عليه» ، قال ابن الجنيد: (يعني: يُرْدونه) .
<span data-type="title" id=toc-310>(٣٠٩) </span>قال أبو حذيفة بن مروان بن حذيفة ليحيى بن معين: حارث كان طالب حديث؟ قال: «كان يطلب الحديث» ، فقال أبو خيثمة: كان صاحب شغب -أي: يشغب في الحديث-.
<span data-type="title" id=toc-311>(٣١٠) </span>سألت يحيى بن معين عن كامل أبي العلاء، فقال: «كامل أبو العلاء ثقة» .
<span data-type="title" id=toc-312>(٣١١) </span>وسألت يحيى عن حفص بن ميسرة، فقال: «لا بأس به، سماعه من زيد بن أسلم عرض، أخبرني من سمع حفص بن ميسرة يقول: كان عباد بن منصور يعرض على زيد بن أسلم ونحن نسمع معه» ، قال يحيى بن معين: «ما أحسن حاله إن كان سماعه كله عرضاً» /كأنه يقول مناولة.
<span data-type="title" id=toc-313>(٣١٢) </span>سألت يحيى بن معين عن أبي سفيان المعمري محمد بن',NULL,'348',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (78,'حميد وتفسيره عن معمر، فقال: «كان ثقة، قال لي: (عرضنا بعضها على معمر، وبعضها كان يحدثنا والكتاب في البيت، ثم يجيء ويوقع عليه) ، قال: (ولو قلت: إني قد سمعته كله) » ، قلت ليحيى بن معين: فأيما أحب إليك: عبد الرزاق أو هو؟ قال: «عبد الرزاق أحب إليَّ،
<span data-type="title" id=toc-314>(٣١٣) </span>وسعيد بن أبي عروبة أثبت الناس في قتادة» .
<span data-type="title" id=toc-315>(٣١٤) </span>قلت ليحيى بن معين: تفسير ورقاء عمن حملته؟ قال: «كتبته عن شبابة وعن علي بن حفص، وكان شبابة أجرأ عليها، وجميعًا ثقة» .
<span data-type="title" id=toc-316>(٣١٥) </span>سمعت يحيى بن معين يقول: «القاسم بن أبي شيبة ثقة صدوق، ليس ممن يكذب» .
<span data-type="title" id=toc-317>(٣١٦) </span>سألت يحيى بن معين عن أشعث بن براز، فقال: «بصري ضعيف الحديث» .
<span data-type="title" id=toc-318>(٣١٧)</span>',NULL,'349',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (79,'وسألت يحيى بن معين عن محمد بن الحجاج الخراساني، فقال: «كان كيسًا، حار الرأس، كان صاحب حديث» .
<span data-type="title" id=toc-319>(٣١٨) </span>سألت يحيى عن حبان بن موسى الكشماهاني، فقال: «ليس صاحب حديث، وهو لا بأس به» .
<span data-type="title" id=toc-320>(٣١٩) </span>سألت يحيى عن أحمد بن جميل المروزي، فقال: «سمع من ابن المبارك وهو غلام، قال: (كنت أسمع منه وأنا أرفع رأسي أنظر إلى العصافير) » .
<span data-type="title" id=toc-321>(٣٢٠) </span>سمعت يحيى بن معين يقول: «صاحب الأشجعي كذاب خبيث، يسرق حديث الناس، حديث حريز بن عثمان كتبه لنا أبو الدرداء، وأما ما روى عن المحاربي عن عاصم فإنه كذب، قال لي يحيى بن آدم: (إن حديث عاصم عن أبي عثمان عن جرير ما رواه أحد إلا عمار بن سيف) » ،',NULL,'350',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (80,'ثم قال لي يحيى بن معين: «ومنهم من يرويه عنه عن سفيان عن عاصم، ومنهم من يرويه عنه عن عاصم، وليس للحديث أصل» .
<span data-type="title" id=toc-322>(٣٢١) </span>سمعت يحيى بن معين يقول: «أبو سفيان طريف بن شهاب السعدي بصري» .
<span data-type="title" id=toc-323>(٣٢٢) </span>وسألت يحيى عن عبد الله بن بكر بن عبد الله المزني، فقال: «ليس به بأس» .
<span data-type="title" id=toc-324>(٣٢٣) </span>سألت يحيى عن عطاء بن أبي ميمونة، فقال: «ليس به بأس» .
<span data-type="title" id=toc-325>(٣٢٤) </span>سمعت يحيى بن معين يقول: «أبو سعد البقال مثل سعد الإسكاف» ، أي: ضعيف.
<span data-type="title" id=toc-326>(٣٢٥) </span>قال لنا يحيى بن معين: «سعيد بن يسار أخو أبي مرثد»',NULL,'351',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (81,'في حديث الليث، وفي حديث سهيل بن أبي صالح: سعيد بن يسار مولى بني النجار، وفي حديث محمد بن إسحاق قال: سمعت سعيد بن يسار: مولى الحسن بن علي. قيل ليحيى: فهؤلاء كلهم واحد؟ قال: «لا، كيف يكونون واحداً؟!» .
قلت ليحيى: فسعيد بن يسار أبو الحباب، أفي هؤلاء هو؟ قال: «صاحب سهيل» . /',NULL,'352',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (82,'الجزء الثالث
بسم الله الرحمن الرحيم
وبالإسناد إلى أبي إسحاق إبراهيم بن عبد الله بن الجنيد الختلي، قال:
<span data-type="title" id=toc-327>(٣٢٦) </span>سمعت يحيى بن معين يقول: «الربيع بن حبيب أخو عائذ بن حبيب ثقة. حدثنا عارم، عن عائذ بن حبيب، عن أخيه الربيع بن حبيب» . قال أبو زكريا: «وعائذ بن حبيب ثقة» ، قال: «وكان الربيع بن حبيب هذا يقال له: ربيع بن الملاح، هو الذي روى عن نوفل بن عبد الملك، ونوفل ليس بشيء، عن علي، عن النبي -صلى الله عليه وسلم- في ذوات الدر» .
<span data-type="title" id=toc-328>(٣٢٧) </span>سمعت يحيى بن معين يقول: «محمد بن بشير القاص ليس بثقة» .
<span data-type="title" id=toc-329>(٣٢٨) </span>سألت يحيى بن معين عن محمد بن عبد الرحمن بن مجبر؟',NULL,'353',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (83,'فقال: «ليس بثقة» ، قال: «وأبوه عبد الرحمن بن مجبر ثقة» .
<span data-type="title" id=toc-330>(٣٢٩) </span>سألت يحيى عن يحيى بن سلمة بن كهيل، فقال: «يحيى بن سلمة بن كهيل، وأخوه محمد بن سلمة بن كهيل ليسا بشيء» .
<span data-type="title" id=toc-331>(٣٣٠) </span>وسمعت يحيى يقول: «القاسم بن عبد الله بن عمر، وعبد الرحمن بن عبد الله بن عمر ليسا بشيء» .
<span data-type="title" id=toc-332>(٣٣١) </span>سألت يحيى عن عمر بن شبيب المسلي، فقال: «قد سمعت منه، ولم يكن ثقة، روى مروان الفزاري عن أبيه شبيب المسلي» . قلت ليحيى: وكان شبيب ثقة؟ قال: «نعم» .
<span data-type="title" id=toc-333>(٣٣٢) </span>سمعت يحيى بن معين وسئل عن عامر بن سعيد أبي حفص، الذي ينزل عند درب علي الطويل، فقال: «أبو حفص البزار ثقة» ،',NULL,'354',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (84,'وأحسن القول فيه، هو الذي دخل على رباح بن زيد، روى عن عبد الصمد بن معقل.
<span data-type="title" id=toc-334>(٣٣٣) </span>وشهدت يحيى بن معين وسئل عن اسم أبي رافع مولى رسول الله -صلى الله عليه وسلم-، فقال: «قال لي معمر -هذا الذي كان من ولده-: (إن اسمه إبراهيم) » ، فقلت ليحيى: معمر هذا ثقة؟ قال: «ما كان بثقة ولا مأمون» .
<span data-type="title" id=toc-335>(٣٣٤) </span>سمعت يحيى بن معين يقول: «منصور عن إبراهيم عن الأسود عن عائشة، أحب إليَّ من هشام بن عروة عن أبيه عن عائشة» ، قيل له: فالزهري عن عروة عن عائشة؟ قال: «هما سواء، ومنصور أحب إليَّ، لأن الزهري كان سلطانياً» .
<span data-type="title" id=toc-336>(٣٣٥) </span>وسمعت يحيى بن معين يقول: «سفيان الثوري أعلم الناس بحديث الأعمش وغيره» ، وذلك أن يحيى سئل: أيما أكثر في الأعمش: أبو معاوية أو الثوري؟
<span data-type="title" id=toc-337>(٣٣٦) </span>سمعت يحيى بن معين يقول: «حدثنا بعض الشاميين، عن',NULL,'355',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (85,'سعيد بن بشير، عن قتادة، عن أنس قال: (كان أحب الألوان إلى رسول الله -صلى الله عليه وسلم- الخضرة) .
<span data-type="title" id=toc-338>(٣٣٧) </span>وسمعت يحيى بن معين يقول: «إنما كان يضع سليمان بن بلال عند أهل المدينة لأنه كان على السوق، وكان أروى الناس عن يحيى بن سعيد» .
<span data-type="title" id=toc-339>(٣٣٨) </span>سمعت يحيى بن معين يقول: «قال لي عبد الرحمن -يعني: ابن مهدي-: (منعني أن أكثر عنه -يعني: سليمان- أني كنت قد كتبت عن عبد الله بن جعفر المخرمي، ولقد ندمت بعد أن لا أكون أكثرت عنه) » .
<span data-type="title" id=toc-340>(٣٣٩) </span>سألت يحيى بن معين عن موسى بن مطير، قال: «لم يكن بثقة» .',NULL,'356',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (86,'<span data-type="title" id=toc-341>(٣٤٠) </span>سألت يحيى عن عبد الجبار بن عمر، فقال: «عبد الجبار الأيلي ضعيف» .
<span data-type="title" id=toc-342>(٣٤١) </span>سألت يحيى بن معين عن محمد بن سنان العوقي، فقال: «ثقة» .
<span data-type="title" id=toc-343>(٣٤٢) </span>وسئل عن محمد بن كثير المصيصي، فقال: «كان صدوقاً» .
<span data-type="title" id=toc-344>(٣٤٣) </span>وسئل يحيى عن محمد بن كثير العبدي البصري، فقال: «كان في حديثه ألفاظ، حدثنا أبو إسحاق» كأنه ضعفه.
<span data-type="title" id=toc-345>(٣٤٤) </span>سألت يحيى بن معين أنا بعد ذلك عن محمد بن كثير العبدي، فقال: «لم يكن يستأهل أن يكتب عنه» .
<span data-type="title" id=toc-346>(٣٤٥) </span>سئل يحيى عن محمد بن المنهال الضرير، صاحب يزيد بن زريع، فقال: «ثقة، لم أسمع منه شيئاً» .
<span data-type="title" id=toc-347>(٣٤٦) </span>سمعت يحيى بن معين وسئل عن عمرو بن مرزوق الباهلي، فقال: «ثقة» .',NULL,'357',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (87,'<span data-type="title" id=toc-348>(٣٤٧) </span>سمعت يحيى يسأل عن أبي بكر عبد الرحمن بن المبارك، فقال: «ثقة» .
<span data-type="title" id=toc-349>(٣٤٨) </span>وسمعته يسأل عن بكير بن أخي جويرية، فقال: «ثقة» .
<span data-type="title" id=toc-350>(٣٤٩) </span>قلت ليحيى: ما تقول في يونس بن بكير؟ فقال: «صدوق» .
<span data-type="title" id=toc-351>(٣٥٠) </span>قلت ليحيى بن معين: ما تقول في أبي بكر النهشلي؟ فقال: «أبو بكر النهشلي ثقة» .
<span data-type="title" id=toc-352>(٣٥١) </span>سمعت يحيى بن معين يقول: «هدبة بن خالد ثقة» .
<span data-type="title" id=toc-353>(٣٥٢) </span>وسمعته يقول: «النرسيان ثقتان» .
<span data-type="title" id=toc-354>(٣٥٣) </span>وسمعت يحيى يقول: «المقدمي الصغير أحبهما إليَّ، الآخر ليس بثقة» .',NULL,'358',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (88,'<span data-type="title" id=toc-355>(٣٥٤) </span>سمعت يحيى بن معين يقول: «صدقة بن خالد الدمشقي ثقة، وهو صدقة بن خالد مولى أم البنين» .
<span data-type="title" id=toc-356>(٣٥٥) </span>سمعت يحيى يقول: «صدقة بن عبد الله الدمشقي، وصدقة بن يزيد الدمشقي ضعيفان، ليسا بشيء، وأرفعهم صدقة بن خالد» .
<span data-type="title" id=toc-357>(٣٥٦) </span>وسمعت يحيى يقول: «الحسن بن يحيى الخشني، ومسلمة بن علي الخشني ضعيفان، ليسا بشيء، والحسن بن يحيى أحبهما إليَّ» .
<span data-type="title" id=toc-358>(٣٥٧) </span>سألت يحيى بن معين عن أبي عبد الرحيم، فقال: «ثقة، خالد بن يزيد، خال محمد بن سلمة الحراني، وقد كان قدم هاهنا» .
<span data-type="title" id=toc-359>(٣٥٨) </span>وسألت يحيى بن معين عن أبي الصلت الهروي، فقال: «قد سمع، وما أعرفه بالكذب» ، قلت: فحديث',NULL,'359',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (89,'الأعمش عن مجاهد عن ابن عباس، قال: «ما سمعت به قط، وما بلغني إلا عنه» .
<span data-type="title" id=toc-360>(٣٥٩) </span>سمعت يحيى بن معين يقول: «أبو حسان الأعرج اسمه: مسلم الأجرد» .
<span data-type="title" id=toc-361>(٣٦٠) </span>سئل يحيى بن معين وأنا أسمع عن أبي المتوكل الناجي، فقال: «علي بن داود» .
<span data-type="title" id=toc-362>(٣٦١) </span>وسمعت يحيى بن معين يقول: «اسم أبي الصديق الناجي: بكر» .
<span data-type="title" id=toc-363>(٣٦٢) </span>وسمعت يحيى بن معين يقول: «عدي بن ثابت عن أبيه عن جده، وهو عدي بن ثابت بن دينار، من الأنصار» .
قال إبراهيم بن / الجنيد: ويقولون: عدي بن ثابت بن عبيد بن عازب، ابن أخي البراء بن عازب.',NULL,'360',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (90,'<span data-type="title" id=toc-364>(٣٦٣) </span>سمعت يحيى بن معين يقول: «أبو رجاء العطاردي عمران بن تيم» .
<span data-type="title" id=toc-365>(٣٦٤) </span>سمعت يحيى بن معين يقول: «سمعان بن مشنج العمري حدث به سفيان عن فراس عن الشعبي عن سمعان بن مشنج العمري فخذ من بن تيم» .
<span data-type="title" id=toc-366>(٣٦٥) </span>سمعت يحيى بن معين يقول: «أبو الزعراء الذي روى عنه ابن عيينة يقولون: عمرو بن عمرو، ويقولون: عمرو بن عامر» .
<span data-type="title" id=toc-367>(٣٦٦) </span>وسمعت يحيى بن معين يقول: «أبو يحيى الأعرج اسمه زياد، وبعضهم يقول: أبو يحيى مصدع، وهذا باطل، وعبيدة يقول: عن منصور عن أبي يحيى زياد المكي» .
<span data-type="title" id=toc-368>(٣٦٧) </span>سألت يحيى عن سعيد بن كثير بن عفير المصري، فقال: «ثقة لا بأس به، وأيش عنده؟!» .',NULL,'361',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (91,'<span data-type="title" id=toc-369>(٣٦٨) </span>وسأله رجل وأنا أسمع عن عبد الله بن خيران، فقال: «لا أعرفه» .
<span data-type="title" id=toc-370>(٣٦٩) </span>وسألت يحيى عن صاحب مغازي إبراهيم بن سعد، فقال: «لا أعرفه» .
<span data-type="title" id=toc-371>(٣٧٠) </span>قلت ليحيى بن معين: يزيد بن عياض بن جعدبة هو أخو أنس بن عياض؟ قال: «لا» ، قلت: فما تقول في يزيد بن عياض؟ فضعفه.
<span data-type="title" id=toc-372>(٣٧١) </span>قلت ليحيى بن معين: الحسن لقي عتبة بن غزوان؟ قال: «لا» .
<span data-type="title" id=toc-373>(٣٧٢) </span>قلت: خالد بن عمير لقي عتبة بن غزوان؟ قال: «نعم» .
<span data-type="title" id=toc-374>(٣٧٣) </span>سألت يحيى بن معين قلت: قتادة لقي سعيد بن جبير؟ قال: «لا، لم يلق سعيد بن جبير، ولا مجاهد، ولا سليمان بن يسار» .',NULL,'362',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (92,'<span data-type="title" id=toc-375>(٣٧٤) </span>سألت يحيى بن معين عن أبي سعيد الرقاشي الذي روى عنه سليمان التيمي عن ابن عباس {وطلح منضود} قال: الموز؟ قال: «لا أعرفه» .
<span data-type="title" id=toc-376>(٣٧٥) </span>سألت يحيى عن عبد الله المدني الذي روى حديث عائشة عن النبي -صلى الله عليه وسلم-: «من رفق بأمتي فارفق به» ، قال: «لا أعرفه، وقد روى هذا الحديث» .
قال إبراهيم بن الجنيد: قال: حدثنيه الفضل بن دكين، عن جعفر بن برقان، عن عبد الله المديني، عن عائشة قالت: قال رسول الله -صلى الله عليه وسلم-: «اللهم من رفق بأمتي فارفق به، ومن شق عليهم فشق عليه» .
حدثنا أحمد بن جميل، أخبرنا عبد الله بن المبارك، عن سفيان، عن جعفر بن برقان، عن عبد الله بن دينار، عن عائشة، عن النبي -صلى الله عليه وسلم- مثله.',NULL,'363',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (93,'<span data-type="title" id=toc-377>(٣٧٦) </span>وسألت يحيى قلت: ابن جريج سمع من مجاهد شيئًا؟ قال: «حرفًا أو حرفين» . قلت: فمن بينهما؟ قال: «لا أدري» .
<span data-type="title" id=toc-378>(٣٧٧) </span>وسألت يحيى عن معقل بن عبيد الله الجزري، فقال: «ثقة» .
<span data-type="title" id=toc-379>(٣٧٨) </span>وسألته عن النضر بن عربي، فقال: «ثقة» .
<span data-type="title" id=toc-380>(٣٧٩) </span>سأل رجل يحيى وأنا شاهد عن ركن الشامي، فقال: «ليس بشيء» .
<span data-type="title" id=toc-381>(٣٨٠) </span>سألت يحيى عن أبي العطوف، فضعفه، فقلت له: يا أبا زكريا قد روى عنه يزيد بن هارون، فقال: «بارك الله لك وليزيد بن هارون فيه» .',NULL,'364',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (94,'<span data-type="title" id=toc-382>(٣٨١) </span>قلت ليحيى: حدثنا عمرو بن محمد الناقد، حدثنا إسحاق بن يوسف الأزرق، عن ابن عون، عن ابن سيرين، أن النبي -صلى الله عليه وسلم- رئي يصلي، فطبق وجعل يديه بين ركبتيه.
قال عمرو الناقد: وحدثنا إسحاق الأزرق، عن ابن عون، عن نافع، عن ابن عمر، أن النبي -صلى الله عليه وسلم- فعله مرة. فقال لي يحيى بن معين: «ليس هذا بشيء» .
<span data-type="title" id=toc-383>(٣٨٢) </span>قلت ليحيى بن معين: أبوحازم سلمة بن دينار مولى الليثيين مدني؟ قال: «نعم» .
<span data-type="title" id=toc-384>(٣٨٣) </span>قلت: فأبو حازم الذي روى عنه الأعمش؟ قال: «ذاك أبو حازم سلمان الأشجعي» . قلت: سلمان ابن من؟ قال: «لا أدري، كان مولى، روى عنه الأعمش ومنصور ويزيد بن كيسان» .
<span data-type="title" id=toc-385>(٣٨٤) </span>سألت يحيى عن عنبسة بن عبد الواحد القرشي، فقال: «ثقة، كان من ولد سعيد بن العاص، وقد سمعت منه، وكان أعور» .',NULL,'365',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (95,'<span data-type="title" id=toc-386>(٣٨٥) </span>سألت يحيى عن واصل بن حيان، فقال: «كوفي ضعيف الحديث» .
<span data-type="title" id=toc-387>(٣٨٦) </span>سألت يحيى عن منصور بن حيان، فقال: «كوفي أسدي ثقة، روى عنه مروان بن معاوية ويزيد بن هارون» .
<span data-type="title" id=toc-388>(٣٨٧) </span>سألت يحيى عن حمزة الزيات، فقال: «حمزة بن حبيب ثقة» .
<span data-type="title" id=toc-389>(٣٨٨) </span>قلت: فياسين الزيات؟ قال: «ليس بشيء» .
<span data-type="title" id=toc-390>(٣٨٩) </span>وسألت يحيى عن عبد العزيز بن المختار، فقال: «بصري ثقة» .
<span data-type="title" id=toc-391>(٣٩٠) </span>سألت يحيى عن أبي هارون الغنوي، فقال: «اسمه إبراهيم بن العلاء» ، قلت: كيف حديثه؟ قال: «ليس يسأل عنه» ، أي: هو ثقة.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (96,'<span data-type="title" id=toc-392>(٣٩١) </span>سمعت يحيى يقول: «حسان بن أبي يحيى الكندي ثقة كوفي» ، قلت: من روى عنه؟ قال: «يعلى بن عبيد ومروان وهؤلاء، روى عن عمر بن كثير القبي؛ قال: قلت لسعيد بن جبير ... » ، قلت ليحيى: ما القبي؟ قال: «يكون في القبة، أي: في الرحبة بالكوفة» .
<span data-type="title" id=toc-393>(٣٩٢) </span>حدثنا ابن الجنيد، حدثنا أحمد بن جناب، حدثنا عيسى بن يونس، حدثنا عمران بن سليم، حدثنا أبو جعفر',NULL,'367',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (97,'القبي -قبيل من مراد-، قال: خرجنا مع عبد الله بن مسعود.
<span data-type="title" id=toc-394>(٣٩٣) </span>سمعت يحيى بن معين وذكروا عبد الله بن المبارك -رحمه الله-، فقال رجل: إنه لم يكن حافظًا، فقال يحيى: «كان عبد الله بن المبارك كيسًا مستثبتًا ثقة، وكان عالمًا صحيح الحديث، وكانت كتبه التي حدث بها عشرين ألفًا أو واحدًا وعشرين ألفًا» .
<span data-type="title" id=toc-395>(٣٩٤) </span>وسمعت يحيى يقول: «عنبسة بن سعيد القرشي ثبت في ابن المبارك، وكان من أصحابه، والطالقاني أحفظ الرجلين» .
<span data-type="title" id=toc-396>(٣٩٥) </span>وسمعت يحيى يقول: «سمعت يحيى بن سعيد يقول: (أنا لا أكذب الله، ربما بلغنا الشيء من قول أبي حنيفة، فنستحسنه فنأخذ به» .
<span data-type="title" id=toc-397>(٣٩٦) </span>وسمعت يحيى بن معين يقول في حديث علي: «حدثني أبو بكر وصدق أبو بكر» : أسماء أو ابن اسماء، قال يحيى:',NULL,'368',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (98,'بن معين: «هذا رجل لا يعرف» .
<span data-type="title" id=toc-398>(٣٩٧) </span>سألت يحيى بن معين عن حديث حماد بن سلمة، عن عبد الله بن شداد، عن أبي عذرة، عن عائشة، أن النبي -صلى الله عليه وسلم- نهى الرجال أن يدخلوا الحمامات إلا في الميازر. قال أبو إسحاق: حدثنا أبو سلمة.
قلت ليحيى: من عبد الله بن شداد هذا؟ قال: «شيخ له واسطي» ، قلت: ثقة؟ قال: «ليس به بأس» .
<span data-type="title" id=toc-399>(٣٩٨) </span>سألت يحيى عن حديث حماد بن سلمة، عن عرار بن سويد، عن عميرة بن سعد، أن رجلاً مات وترك ابنًا له وترك خمسين بعيرًا، قلت: من هذا؟ قال: «هذا كوفي روى عنه حماد',NULL,'369',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (99,'ابن سلمة» ، قلت: روى عنه أحد غير حماد؟ قال: «نعم، أبو بدر شجاع بن الوليد» .
<span data-type="title" id=toc-400>(٣٩٩) </span>قلت ليحيى: ابن المبارك، عن عبد الله بن عمر، عن عبد الرحمن بن القاسم، عن أبيه، أن أبا بكر مر بعبد الرحمن بن أبي بكر وهو يماظ جارًا له، فقال: (لا تماظ جارك، فإن هذا يبقى ويذهب الناس) .
قلت ليحيى: يماظ بالظاء أو بالطاء؟ قال: «لا، بالظاء: لا تماظ، وسألت مصعبًا الزبيري، فقال لي مثل ذلك، وسألت يحيى بن أكثم، فقال لي مثل ذلك، وهذا في كتاب الزهد» .
<span data-type="title" id=toc-401>(٤٠٠) </span>حدثنا محمد بن مقاتل، وسمعت أبا خيثمة يقول: حدثنا وكيع، عن سفيان قال: (يغسل قدميه) ، قلت: وإن جف وضوؤه؟ قال: (وإن جف وضوؤه) ، يعني: إذا خلع خفيه وقد كان مسح.
<span data-type="title" id=toc-402>(٤٠١) </span>سألت يحيى عن مسلم بن عبيد أبي نصيرة الذي روى عنه يزيد بن هارون، فقال: «واسطي، روى عنه أيضًا هشيم» ، قلت: ثقة؟ قال: «ليس به بأس» .',NULL,'370',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (100,'<span data-type="title" id=toc-403>(٤٠٢) </span>قلت ليحيى بن معين: وروى يزيد بن هارون أيضًا عن سالم بن عبيد، عن عون بن عبد الله، فقال: «نعم، هذا سالم بن عبيد رجل من أهل البصرة، ضعيف» ، أو قال: «ليس بشيء» .
<span data-type="title" id=toc-404>(٤٠٣) </span>سأل رجل يحيى بن معين: أيما خير: أبو العطوف، أو يزيد بن عياض بن جعدبة؟ فقال: «ابن جعدبة أمثل، وكلاهما ضعيفان ليسا بشيء» .
<span data-type="title" id=toc-405>(٤٠٤) </span>قلت ليحيى: داود بن أبي هند، عن الشعبي، عن أبي هريرة، عن النبي -صلى الله عليه وسلم-: «لا تنكح المراة على عمتها ولا على خالتها» ، تعرفه عن محمد بن سيرين، عن أبي هريرة؟ قال: «نعم، حدثنا به منجاب» .
قلت ليحيى: حدثنا محرز بن عون، عن علي بن مسهر، قال: «حدثنا منجاب، عن علي بن مسهر، لا أعلم أحدًا يقول هذا غير علي، وأما ابن عون وغيره فيقولون: (نهى أن تنكح) » .',NULL,'371',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (101,'<span data-type="title" id=toc-406>(٤٠٥) </span>قلت ليحيى في حديث حماد بن سلمة، عن عاصم الأحول، عن أنس: «تفضل صلاة الجميع ... » ، حدثناه أبو سلمة موقوفًا، قال: «نعم، هو موقوف» ، قلت: فإن علي بن عثمان اللاحقي رفعه، وزعموا أن حجاجًا رفعه؟ قال: «ما أدري» .
<span data-type="title" id=toc-407>(٤٠٦) </span>قلت ليحيى: حدثنا معاوية بن عمرو، عن مسلمة بن جعفر، عن أرطاة بن حازم، قال: قال لي الشعبي -ورأى عليَّ ثيابًا بياضًا-: (غير بعضها) ، قلت: وحدثناه أبو داود المباركي، عن ابن أبي زائدة، عن مسلمة بن جعفر، عن أبي أرطاة، قال: «لا، الزم ما قال معاوية» .',NULL,'372',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (102,'<span data-type="title" id=toc-408>(٤٠٧) </span>قلت ليحيى -وذكر عنده علي بن المديني، فحملوا عليه-، فقلت ليحيى: يا أبا زكريا، ما علي عند الناس إلا مرتد! فقال: «ما هو بمرتد، هو على إسلامه، رجل خاف فقال، ما عليه؟!» .
<span data-type="title" id=toc-409>(٤٠٨) </span>قلت ليحيى: حجاج، عن ابن جريج، قال: أخبرني وهب، عن شعيب الجبائي، من وهب هذا؟ قال: «هذا وهب بن سليمان، وليس بوهب بن منبه، ولم يلق ابن جريج وهب بن منبه» .
<span data-type="title" id=toc-410>(٤٠٩) </span>سألت يحيى عن عبد الواحد بن أيمن، فقال: «مكي ثقة» ، قلت: ثقة؟ قال: «ليس به بأس» .
<span data-type="title" id=toc-411>(٤١٠) </span>سألت يحيى بن معين عن أبي كعب الحريري، فقال: «بصري مولى الأزد، ليس به بأس» .
<span data-type="title" id=toc-412>(٤١١) </span>سال ابن الغلأبي يحيى عن سلم بن زرير، فقال: «ضعيف،',NULL,'373',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (103,'يحيى بن سعيد يضعفه تضعيفًا شديدًا» .
<span data-type="title" id=toc-413>(٤١٢) </span>سألت يحيى عن حديث المعافى بن عمران، عن علي بن مالك الرؤاسي، فقال: «هذا شيخ ضعيف كوفي» ، قلت: من يحدث عنه غير المعافى؟ قال: «وكيع وهؤلاء» .
<span data-type="title" id=toc-414>(٤١٣) </span>سمعت يحيى بن معين يقول: «الربيع بن بدر الأعرجي عليلة ليس بشيء، بصري» .
<span data-type="title" id=toc-415>(٤١٤) </span>قال ابن الغلأبي ليحيى وأنا أسمع: أيما أحب اليك: محمد بن أبان أو أيوب بن عتبة؟ قال: «أيوب بن عتبة أحب إلي منه، وأيوب ضعيف ليس بذاك القوي» .
<span data-type="title" id=toc-416>(٤١٥) </span>قال لي يحيى: «هذا البصري أبو أيوب صاحب البصري ثقة صدوق حافظ معروف، اكتب عنه»
<span data-type="title" id=toc-417>(٤١٦) </span>سمعت يحيى بن معين -وذكروا عنده رجلاً بشر-، فقال رجل ليحيى: إنه لا يحافظ على الصلاة في جماعة والجمعة، فقال يحيى: «وما ينفعه إذا كان يعصيها، إنما تنفع الصلاة من أطاعها» .
<span data-type="title" id=toc-418>(٤١٧) </span>سمعت يحيى بن معين يقول: «عبد الأعلى بن',NULL,'374',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (104,'أبي المساور، أبو مسعود الجزار، ليس بشيء، كذاب» .
<span data-type="title" id=toc-419>(٤١٨) </span>وسمعت يحيى يقول: «الربيع بن حبيب بصري، روى عنه أبو داود وعفان وهؤلاء» .
<span data-type="title" id=toc-420>(٤١٩) </span>قلت ليحيى: حماد بن زيد، عن ميمون الكردي، فقال: «ميمون بصري، روى عنه يزيد بن هارون ووكيع وعبد الصمد ويحيى القطان» ، قلت: ثقة؟ قال: «ثقة» .
<span data-type="title" id=toc-421>(٤٢٠) </span>سمعت يحيى بن معين يقول: «كان شعبة يقول: (أبو إسحاق، عن زيد بن أثيل) ، والناس كلهم يقولون: (زيد بن يثيع) » . قال يحيى: «والصواب: زيد بن يثيع» .
<span data-type="title" id=toc-422>(٤٢١) </span>وسمعت يحيى يقول: «ابن أبي ذئب عن جبير بن صالح، هذا مديني» .
<span data-type="title" id=toc-423>(٤٢٢) </span>سئل يحيى عن جخدب بن جرعب، فقال:',NULL,'375',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (105,'ما روى أحد عن سفيان عن جخدب بن جرعب التيمي الا يحيى بن يمان» ، كأنه ضعف يحيى بن يمان.
قلت ليحيى: حدث قبيصة، عن قيس، عن جخذب، عن أبيه: (إني لأبيت ليلتي أفكر في كلمة أرضي بها أميري ولا أسخط ربي؛ فما أجدها) ، فسكت يحيى.
<span data-type="title" id=toc-424>(٤٢٣) </span>قلت ليحيى: ابن جريج، عن عطاء {وجعلت له مالاً ممدودًا} ، قال: (غلة شهر لشهر) ؟ فقال يحيى: «من روى هذا؟» فذكرت له الشيخ البصري ابن حلبس، فقال: «ليس هذا بشيء» .',NULL,'376',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (106,'<span data-type="title" id=toc-425>(٤٢٤) </span>قلت ليحيى بن معين: محمد بن جحادة لقي الحسن البصري؟ قال: «نعم، كان دخل البصرة، ثم توجه إلى الكوفة، فبلغه أن إبراهيم العلوي قد خرج، فرجع إلى البصرة» ، ثم قال يحيى: «ما أرى حديثه يكون ستين حديثًا» ، قلت ليحيى: إن عبد الوارث يحسن الرواية عنه؟ قال: «نعم» .
<span data-type="title" id=toc-426>(٤٢٥) </span>سمعت يحيى بن معين يقول: «تحملنا بالجارودي البصري على محمد ابن أبي عدي، فحدثنا بأحاديث ابن عون وأشعث وداود، أشعث أكثر من ألف، وابن عون ألف وأكثر، وداود نحو من مائة حديث» .',NULL,'377',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (107,'<span data-type="title" id=toc-427>(٤٢٦) </span>سألت يحيى في شهر ربيع الأول سنة تسع وعشرين ومائتين عن حشرج بن نباتة، فقال: «لا بأس به» .
<span data-type="title" id=toc-428>(٤٢٧) </span>وقيل ليحيى وأنا أسمع: شعبة، عن موسى، قال: أخبرني من سمع ابن عمر -واكترى-، فقال: (لا تحملني على جلال) ، من موسى هذا؟ هو موسى السيلاني، أو موسى بن عقبة؟ قال: «لا، هذا موسى مولى بني عامر» .
<span data-type="title" id=toc-429>(٤٢٨) </span>وقيل ليحيى: خالد الحذاء، عن بركة أبي الوليد، عن ابن عباس، عن النبي -صلى الله عليه وسلم-: «لعن الله اليهود، حرمت عليهم الشحوم» ، هذا رواه ابن علية، وروى عبد الوهاب الثقفي، عن خالد، عن أبي العريان المجاشعي، عن ابن عباس، عن النبي -صلى الله عليه وسلم-، أيما الصحيح؟ قال يحيى: «ابن علية أثبت» .
قال إبراهيم بن الجنيد: قال خالد الحذاء: حدثنا بركة بن الوليد أبو العريان المجاشعي.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (108,'<span data-type="title" id=toc-430>(٤٢٩) </span>سألت يحيى عن فطر بن خليفة، قال: «ثقة» .
<span data-type="title" id=toc-431>(٤٣٠) </span>سألت يحيى بن معين عن يونس بن أبي إسحاق، فقال: «ثقة» .
<span data-type="title" id=toc-432>(٤٣١) </span>قلت ليحيى: أيما أثبت شريك أو إسرائيل؟ قال: «إسرائيل أقرب حديثًا، وشريك أحفظ» .
<span data-type="title" id=toc-433>(٤٣٢) </span>سمعت يحيى بن معين يقول: «الزهري، عن صفوان بن عبد الله بن صفوان» ، قال يحيى: «هذا صفوان بن عبد الله بن صفوان بن أمية بن خلف الجمحي» .
<span data-type="title" id=toc-434>(٤٣٣) </span>سئل يحيى بن معين عن بلال بن سعد عن أبيه، كانت لأبيه صحبة؟ قال: «نعم» .
<span data-type="title" id=toc-435>(٤٣٤) </span>سألت يحيى بن معين عن عبد الملك بن الصباح الصنعاني الذي روى عن بكار عن وهب بن منبه، فقال: «ثقة صدوق، قد رأيته، لم أكتب عنه، من حدثكم عنه؟» ، قلت: حدثنا عنه نعيم بن حماد، قال: «ثقة» .',NULL,'379',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (109,'<span data-type="title" id=toc-436>(٤٣٥) </span>سئل يحيى عن أبي إسماعيل المؤدب، فقال: «ثقة» .
<span data-type="title" id=toc-437>(٤٣٦) </span>سمعت يحيى بن معين وقيل له: كرز بن علقمة روى عنه أحد غير عروة؟ قال: «لا» .
قال إبراهيم بن الجنيد: قد روى عن كرز بن علقمة ابنُ السلماني.
قال إبراهيم بن الجنيد: حدثنا عقبة بن مكرم، عن يونس بن بكير، جاء العاقب إلى النبي -صلى الله عليه وسلم- يعني حديث ابن السلماني، لم يتمه.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (110,'<span data-type="title" id=toc-438>(٤٣٧) </span>قلت ليحيى: أسامة بن زيد بن أسلم وعبد الله وعبد الرحمن؟ فقال: «ضعفاء كلهم» .
<span data-type="title" id=toc-439>(٤٣٨) </span>سمعت يحيى يقول: ميسرة الأشجعي الذي روى عن أسباط بن نصر وزائدة، هو كوفي ثقة» .
<span data-type="title" id=toc-440>(٤٣٩) </span>قيل ليحيى: حديث داود بن عبد الرحمن العطار، عن ابن جريج، عن عطاء، عن أبي هريرة، قال: قال رسول الله -صلى الله عليه وسلم-: «أفطر الحاجم والمحجوم» ؟ قال يحيى: «ليس هذا بشيء، إنما هو موقوف عن أبي هريرة» .
قلت ليحيى: من يوقفه عن أبي هريرة؟ قال: «من حدث به عن ابن جريج» .',NULL,'381',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (111,'<span data-type="title" id=toc-441>(٤٤٠) </span>قلت ليحيى بن معين: إسماعيل بن إبراهيم بن عقبة؟ فقال: «ثقة، وإبراهيم هو أخو موسى بن عقبة» .
<span data-type="title" id=toc-442>(٤٤١) </span>قلت: فإبراهيم بن أبي حبيبة؟ قال: «ليس به بأس» .
<span data-type="title" id=toc-443>(٤٤٢) </span>قلت ليحيى بن معين: أكان عند معن القزاز عن مالك شيئًا غير الموطأ؟ قال: «شيء قليل» ، قال يحيى: «وإنما قصدنا إليه في حديث مالك» . فقيل ليحيى: فكيف هو في غير مالك؟ قال: «ثقة» .
<span data-type="title" id=toc-444>(٤٤٣) </span>قلت ليحيى بن معين: عبد الله بن أبي بكر الذي روى عن الزهري، عن سالم، عن أبيه، عن حفصة، عن النبي -صلى الله عليه وسلم-: «من لم يجمع الصيام من الليل» ؟ قال: «ثقة» .
<span data-type="title" id=toc-445>(٤٤٤) </span>سمعت يحيى بن معين يقول: «الحكم بن عبد الله الأيلي ليس بشيء» .
<span data-type="title" id=toc-446>(٤٤٥) </span>سمعت يحيى بن معين يقول: «أبان بن يزيد العطار ثقة،',NULL,'382',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (112,'<span data-type="title" id=toc-447>(٤٤٦) </span>وهمام بن يحيى ثقة» .
<span data-type="title" id=toc-448>(٤٤٧) </span>قال لي يحيى بن معين يومًا ابتداءً ولم أسأله عنه: «عاصم ليس بشيء» ، يعني: عاصم بن علي.
<span data-type="title" id=toc-449>(٤٤٨) </span>سمعت يحيى بن معين يقول: «محمد بن أبان ليس بشيء، وقيس بن الربيع ليس بشيء، وحماد بن شعيب ليس بشيء، ولا محمد بن جابر، ليس هؤلاء ثقات» .
<span data-type="title" id=toc-450>(٤٤٩) </span>سمعت يحيى بن معين يقول: موسى بن عبيدة ضعيف الحديث، إلا أنه -قال ابن الجنيد- ليس بمتروك.
<span data-type="title" id=toc-451>(٤٥٠) </span>وسمعت يحيى بن معين يقول: «أبو إسحاق الكوفي اسمه عبد الله بن ميسرة، وهو أبو ليلى، ليس بشيء» .
قلت ليحيى: هو الذي روى عنه هشيم، عن أبي إسحاق الكوفي؟ قال: «نعم» ، قلت: حدثنا عنه سعدويه وأحمد بن يونس، قال: «نعم ليس بشيء، وليس حديثه شيء» .
<span data-type="title" id=toc-452>(٤٥١) </span>قلت ليحيى بن معين: الأحاديث التي رواها الحسين',NULL,'383',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (113,'ابن واقد، عن ابن بريدة، عن أبيه، هي صحاح؟ قال: «ليس به بأس ثقة» ، يعني: الحسين بن واقد.
<span data-type="title" id=toc-453>(٤٥٢) </span>سمعت يحيى يُسأل: رشدين بن سعد؟ قال: «ليس بشيء،
<span data-type="title" id=toc-454>(٤٥٣) </span>وابن لهيعة أمثل من رشدين، وقد كتبت حديث ابن لهيعة» .
<span data-type="title" id=toc-455>(٤٥٤) </span>وسمعت يحيى يقول: «عبد الله بن وهب ثقة ليس به بأس» .
<span data-type="title" id=toc-456>(٤٥٥) </span>سمعت يحيى بن معين يقول: «الحسن بن صالح ثقة مأمون، سمعت أبا نعيم يقول: رأيت الحسن بن صالح في الجمعة قبل أن يموت بجمعة» .
<span data-type="title" id=toc-457>(٤٥٦) </span>سمعت يحيى بن معين يقول: «الحكم بن ظهير ليس بثقة» .',NULL,'384',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (114,'<span data-type="title" id=toc-458>(٤٥٧) </span>سمعت يحيى بن معين يقول: «الحكم بن عبد الملك صاحب قتادة ضعيف الحديث» .
<span data-type="title" id=toc-459>(٤٥٨) </span>سألت يحيى عن اسم أبي حصين، فقال: «عثمان بن عاصم» .
<span data-type="title" id=toc-460>(٤٥٩) </span>وسمعت يحيى يقول: «الوليد بن محمد الموقري شامي ليس بشيء» .
<span data-type="title" id=toc-461>(٤٦٠) </span>سمعت يحيى يقول: «صالح بن أبي الاخضر ضعيف الحديث» .
<span data-type="title" id=toc-462>(٤٦١) </span>سمعت يحيى يقول: «جعفر بن برقان ثقة فيما روى عن غير الزهري، وأما ما روى عن الزهري فهو ضعيف، وكان أميًّا لا يكتب، وليس هو مستقيم الحديث عن الزهري، وهو في غير الزهري أصح حديثًا» .
<span data-type="title" id=toc-463>(٤٦٢) </span>سمعت يحيى بن معين يقول: «عبد الرحمن بن إسحاق عن الزهري أحب إلي من صالح بن أبي الأخضر» .
<span data-type="title" id=toc-464>(٤٦٣) </span>سمعت يحيى -وذكر أبا الصلت الهروي-، فقال: «لم يكن أبو الصلت عندنا من أهل الكذب، وهذه الأحاديث التي يرويها ما نعرفها» .',NULL,'385',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (115,'<span data-type="title" id=toc-465>(٤٦٤) </span>سمعت يحيى بن معين يقول: «الحارث بن عطية المصيصي ثقة،
<span data-type="title" id=toc-466>(٤٦٥) </span>ومخلد بن الحسين ثقة،
<span data-type="title" id=toc-467>(٤٦٦) </span>وعلي بن بكار كان رجلاً غزاء رجل صدق، ولم يكن من أصحاب الحديث» .
<span data-type="title" id=toc-468>(٤٦٧) </span>سمعت يحيى يقول: «ابن أم مكتوم اسمه عمرو بن أم مكتوم» .
<span data-type="title" id=toc-469>(٤٦٨) </span>سمعت يحيى بن معين يقول: «شيبان أحب إلي من حرب بن شداد في يحيى بن أبي كثير» .
<span data-type="title" id=toc-470>(٤٦٩) </span>قلت ليحيى: حدثنا أبو سلمة، حدثنا وهيب، حدثنا عنبسة الأعور، قال: كنت أكتب المعاذات، فسألت الحسن',NULL,'386',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (116,'عنها، فكرهها، فتركتها. فقال يحيى: «هذا ثقة، وقد روى عنه أيضًا عبد الوهاب الثقفي» ، يعني: عنبسة الأعور.
<span data-type="title" id=toc-471>(٤٧٠) </span>سمعت يحيى يقول: «وعنبسة بن عبد الواحد القرشي الأعور ثقة، قد كتبت عنه» .
<span data-type="title" id=toc-472>(٤٧١) </span>وسمعت يحيى بن معين يقول: «عنبسة بن عبد الرحمن ضعيف الحديث ليس بشيء» .
<span data-type="title" id=toc-473>(٤٧٢) </span>قال يحيى: «وعنبسة بن أزهر الجرجاني روى عن سماك لا بأس به،
<span data-type="title" id=toc-474>(٤٧٣) </span>وعنبسة بن سعيد صاحب عبد الله بن المبارك ليس به بأس، كان هاهنا، وكان قاضي الري» ، قلت ليحيى: كتبت عنه شيئًا؟ قال: لا، وكان راوية عن ابن المبارك.
<span data-type="title" id=toc-475>(٤٧٤) </span>قلت ليحيى: عنبسة الخواص، الذي روى',NULL,'387',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (117,'عن جعفر الضبعي؟ قال: «بصري ليس بشيء» .
<span data-type="title" id=toc-476>(٤٧٥) </span>سألت يحيى بن معين عن محمد بن هلال المدني، قال: «ثقة ليس به بأس» .
<span data-type="title" id=toc-477>(٤٧٦) </span>سمعت يحيى بن معين يقول: «عمر بن دينار، قال: قلت لسعيد بن جبير ... » قال يحيى: «هو كوفي» ، قلت ليحيى: من روى عنه؟ قال: «شريك بن عبد الله القاضي» .
<span data-type="title" id=toc-478>(٤٧٧) </span>وسمعت يحيى بن معين يقول: «قال المهدي لشريك: كأني أرى رأس زنديق يضرب الساعة! فقال شريك: يا أمير المؤمنين إن للزنادقة علامات: تركهم الجماعات، وشربهم القهوات، وتخلفهم عن الجمعات. فقال المهدي: يا أبا عبد الله لم نعنك بهذا» ، قال يحيى بن معين: «وجده حاضر الجواب» .
<span data-type="title" id=toc-479>(٤٧٨) </span>سمعت يحيى بن معين يقول: «سفيان بن دينار التمار ثقة، سفيان بن زياد العصفري ثقة جميعًا كوفيان» .
<span data-type="title" id=toc-480>(٤٧٩) </span>سمعت يحيى بن معين يقول: «سليمان بن سفيان المديني',NULL,'388',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (118,'الذي روى عنه أبو عامر العقدي حديث طلحة عن النبي -صلى الله عليه وسلم- في رؤية الهلال، ليس بشيء» .
<span data-type="title" id=toc-481>(٤٨٠) </span>سمعت يحيى بن معين يقول: «طلحة بن يحيى ثقة، هو أخو إسحاق بن يحيى بن طلحة،
<span data-type="title" id=toc-482>(٤٨١) </span>وإسحاق بن يحيى ليس بشيء،
<span data-type="title" id=toc-483>(٤٨٢) </span>وعيسى بن طلحة عم طلحة بن يحيى وعم إسحاق بن يحيى، وهو ثقة» .
<span data-type="title" id=toc-484>(٤٨٣) </span>سمعت يحيى بن معين يقول: «ومعاوية بن إسحاق بن طلحة ثقة، روى عنه شعبة وسفيان وإسرائيل» .',NULL,'389',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (119,'<span data-type="title" id=toc-485>(٤٨٤) </span>سمعت يحيى بن معين يقول: «محمد بن زياد الطحان ليس بشيء، كذاب، الذي يروى عن ميمون بن مهران ما يروي» .
<span data-type="title" id=toc-486>(٤٨٥) </span>وسمعت يحيى بن معين يقول: «محمد بن زياد صاحب أبي هريرة مديني كان بالبصرة، ثقة، روى عنه شعبة والربيع بن مسلم وحماد بن سلمة وحماد بن زيد ومعمر» .
<span data-type="title" id=toc-487>(٤٨٦) </span>وسمعت يحيى بن معين يقول: «محمد بن الحسن بن زبالة المديني ليس بشيء، روى عن مالك عن هشام بن عروة، عن أبيه، عن عائشة قالت: قال رسول الله -صلى الله عليه وسلم-: «فتحت المدينة بالقرآن وفتحت المدائن بالسيف» ، قال يحيى: هذا كذاب، ليس بشيء، أصحاب مالك يروونه من كلام مالك» .
<span data-type="title" id=toc-488>(٤٨٧) </span>سمعت يحيى بن معين يقول: «عروة بن الزبير قد سمع من معاوية» .
<span data-type="title" id=toc-489>(٤٨٨) </span>سألت يحيى عن اسم أبي حرة صاحب الحسن، فقال: «اسمه واصل بن عبد الرحمن» .',NULL,'390',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (120,'<span data-type="title" id=toc-490>(٤٨٩) </span>وسألت يحيى عن الجراح بن مليح البهراني، فقال: «ليس به بأس» .
<span data-type="title" id=toc-491>(٤٩٠) </span>وسألت يحيى عن عيسى بن ميمون الجرشي الذي روى عنه أبو عاصم التفسير، فقال: «ليس به بأس، يشبه ورقاء وشبلاً» ، فقال رجل ليحيى: أيما أحب إليك: ورقاء أو شبل؟ قال: «واحد» .
<span data-type="title" id=toc-492>(٤٩١) </span>سئل يحيى وأنا أسمع عن سالم بن نوح، فقال: «يضعف» .
<span data-type="title" id=toc-493>(٤٩٢) </span>سمعت يحيى وقيل له: رأيت علي بن عابس؟ قال: «لم أر',NULL,'391',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (121,'علي بن عابس، ولم أسمع منه شيئًا» ، قيل: كيف حديثه؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-494>(٤٩٣) </span>قلت ليحيى بن معين: ما تقول في سعيد بن زربى؟ فقال: «ليس بشيء» .
<span data-type="title" id=toc-495>(٤٩٤) </span>قلت ليحيى بن معين: ما تقول في أبي عاصم العباداني؟ قال: «ضعيف الحديث» ، قلت ليحيى: فإنهم يزعمون أن أبا عاصم العباداني هو سعيد بن زربى؟ قال: «لا» .
قال إبراهيم بن الجنيد: فوجدت في بعض سماعنا من البصريين: سعيد بن زربى أبو عبيدة القارئ.
<span data-type="title" id=toc-496>(٤٩٥) </span>سألت يحيى بن معين عن اليمان بن المغيرة، فقال: «ليس بشيء» .
<span data-type="title" id=toc-497>(٤٩٦) </span>سألت يحيى عن الضحاك بن نبراس، فقال: «بصري ضعيف الحديث» .
<span data-type="title" id=toc-498>(٤٩٧) </span>سألت يحيى بن معين عن أشعث بن براز، قال: «ضعيف الحديث» .',NULL,'392',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (122,'<span data-type="title" id=toc-499>(٤٩٨) </span>سألت يحيى بن معين عن عثمان بن عطاء الخراساني، فقال: «ضعيف الحديث» ، قلت: هو عطاء بن ميسرة الخراساني؟ قال: «نعم» .
<span data-type="title" id=toc-500>(٤٩٩) </span>قلت ليحيى: ابن لهيعة ورشدين سواء؟ قال: «لا، ابن لهيعة أحب إلي من رشدين، رشدين ليس بشيء» .
ثم قال لي يحيى بن معين: «قال لي أهل مصر: ما احترق لابن لهيعة كتاب قط، وما زال ابن وهب يكتب عنه حتى مات» .
<span data-type="title" id=toc-501>(٥٠٠) </span>قال يحيى: «وكان أبو الأسود النضر بن عبد الجبار راوية عنه، وكان شيخ صدق،
<span data-type="title" id=toc-502>(٥٠١) </span>وكان ابن أبي مريم يسيء الرأي في ابن لهيعة، فلما كتبوها عنه وسألوه عنها سكت عن ابن لهيعة» .
<span data-type="title" id=toc-503>(٥٠٢) </span>قلت ليحيى: فسماع القدماء والآخرين من ابن لهيعة سواء؟ قال: «نعم، سواء، واحد» .
<span data-type="title" id=toc-504>(٥٠٣) </span>وسمعت يحيى بن معين وسئل عن الفضيل بن بزوان، فقال: «كوفي روى عن ميمون بن مهران، لم يكن عنده حديث» .',NULL,'393',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (123,'<span data-type="title" id=toc-505>(٥٠٤) </span>وسمعت يحيى يقول: «غزوان الرقاشي وحمصة الرقاشي بصريان متعبدان، ليس عندهما حديث» .
<span data-type="title" id=toc-506>(٥٠٥) </span>سمعت يحيى بن معين يقول: رأيت عبد الله بن وهب بمكة وجاء إلى سفيان بن عيينة، فقال: (السماع الذي سمعوه منك أول أمس أجزه لي) ، قال: (قد أجزته لك) .
<span data-type="title" id=toc-507>(٥٠٦) </span>سمعت يحيى بن معين يقول: «سماع ابن وهب من يونس عرض عرضه عليه» .
<span data-type="title" id=toc-508>(٥٠٧) </span>سمعت يحيى يقول: «شعيب بن أبي حمزة من أثبت الناس في الزهري، كان كاتبًا له» ، قلت: شعيب ابن من؟ قال: «شعيب بن دينار» ، قلت ليحيى: اسم أبي حمزة: دينار؟ قال: «نعم» .
<span data-type="title" id=toc-509>(٥٠٨) </span>قال يحيى: «وأما بشر -يعني: ابن شعيب-، فلم يسمعها من أبيه، سألوه عنها فقال: (لم أسمعها من أبي، إنما أنا صاحب طب) ، فلم يزالوا به حتى حدثهم بها، قالوا: قل: أبي، فكتبوا عنه» .',NULL,'394',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (124,'<span data-type="title" id=toc-510>(٥٠٩) </span>وسمعت يحيى بن معين يقول: «وأصحاب الزهري: شعيب ومعمر وعقيل ويونس والأوزاعي» ، قال رجل ليحيى: فمالك بن أنس؟ قال: «ذاك من أرفعهم» .
<span data-type="title" id=toc-511>(٥١٠) </span>قال يحيى بن معين وأنا اسمع: «جعفر بن برقان ضعيف فيما روى عن الزهري، كان أميًّا،',NULL,'395',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (125,'<span data-type="title" id=toc-512>(٥١١) </span>ومحمد بن أبي حفصة ضعيف، إلا انه أقوى من صالح بن أبي الاخضر» .
<span data-type="title" id=toc-513>(٥١٢) </span>قال لي يحيى بن معين: «عفير بن معدان، وأبو مهدي سعيد بن سنان، وأبو حفص القاص عثمان بن أبي العاتكة؛ هؤلاء ليسوا بشيء» .
<span data-type="title" id=toc-514>(٥١٣) </span>قال لي يحيى: «عبيد الله بن زحر ومطرح بن يزيد ضعيفا الحديث» .
<span data-type="title" id=toc-515>(٥١٤) </span>وسمعت يحيى يقول: «القاسم أبو عبد الرحمن ثقة إذا روى عنه الثقات أرسلوا ما رفع هؤلاء» .
<span data-type="title" id=toc-516>(٥١٥) </span>وسألت يحيى عن مهدي بن جعفر الرملي، فقال: «ثقة لا بأس به» .
<span data-type="title" id=toc-517>(٥١٦) </span>سألت يحيى بن معين في شهر ربيع الأول سنة تسع وعشرين ومائتين عن سعيد بن أسد بن موسى، فقال: «لا بأس به، فتى صدق صدوق» .',NULL,'396',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (126,'<span data-type="title" id=toc-518>(٥١٧) </span>سألت يحيى عن أبي العباس الصيدلاني، فقال: «الرملي، صدوق لا بأس به» .
<span data-type="title" id=toc-519>(٥١٨) </span>وسألت يحيى عن ابن أبي السري العسقلاني، فقال: «ثقة» .
<span data-type="title" id=toc-520>(٥١٩) </span>وسألته عن هشام بن عمار الدمشقي، فقال: «ثقة» .
<span data-type="title" id=toc-521>(٥٢٠) </span>وسألت يحيى عن محمد بن عائذ الدمشقي، قال: «ثقة، الكاتب» .
<span data-type="title" id=toc-522>(٥٢١) </span>وسألت يحيى عن عمرو بن خالد الحراني، فقال: «ثقة صدوق» .
<span data-type="title" id=toc-523>(٥٢٢) </span>سألت يحيى عن يونس بن عبد الأعلى المصري، فقال: «لا أعرفه» .
<span data-type="title" id=toc-524>(٥٢٣) </span>وسئل يحيى وأنا أسمع عن أبي اليمان، فقال: «ثقة» .',NULL,'397',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (127,'<span data-type="title" id=toc-525>(٥٢٤) </span>وسئل يحيى عن أبي عمير بن النحاس، فقال: «ثقة، أحفظ الناس لحديث ضمرة» .
<span data-type="title" id=toc-526>(٥٢٥) </span>سألت يحيى قلت: هل كتبت بمصر عن المفضل بن فضالة؟ فقال: «لا لا، ما كتبت عنه شيئًا، كان رجل سوء شاطر خبيث، لم يكن موضع أن يكتب عنه» .
<span data-type="title" id=toc-527>(٥٢٦) </span>وسئل يحيى عن عثمان بن صالح، فقال: «ثقة، كان عنده عن ابن وهب وبكر بن مضر» .
<span data-type="title" id=toc-528>(٥٢٧) </span>وسألت يحيى عن يوسف بن عدي، فقال: «لا بأس به، وأيش عنده؟!» .
<span data-type="title" id=toc-529>(٥٢٨) </span>سمعت يحيى وسئل عن نعيم بن حماد، فقال: «ثقة» .',NULL,'398',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (128,'قلت: إن قومًا يزعمون أنه صحح كتبه من علي العسقلاني الخراساني، فقال لي يحيى: «أنا سألته، فقلت: أخذت كتب علي العسقلاني وصححت منها؟ فأنكر، وقال: (إنما كان شيء قد درس، فنظرت؛ فما عرفت ووافق كتابي غيرت) » ، قلت ليحيى: فما تقول في علي هذا العسقلاني؟ قال: «ليس بشيء، كان أيام ابن المبارك غلامًا» .
<span data-type="title" id=toc-530>(٥٢٩) </span>سمعت يحيى بن معين يقول: «كان نعيم بن حماد رفيقي في البصرة» .
<span data-type="title" id=toc-531>(٥٣٠) </span>سألت يحيى بن معين عن صدقة بن المنتصر أبي شعبة الشعباني، فقال: «ثقة» .
<span data-type="title" id=toc-532>(٥٣١) </span>سمعت يحيى بن معين يقول: «عبد الرحمن بن يزيد بن جابر وأبو بكر بن أبي مريم وحريز بن عثمان الرحبي، هؤلاء ثقات» .',NULL,'399',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (129,'<span data-type="title" id=toc-533>(٥٣٢) </span>وسمعت يحيى بن معين يقول: «ابن ثوبان ضعيف الحديث، كان هاهنا ببغداد» .
<span data-type="title" id=toc-534>(٥٣٣) </span>سمعت يحيى بن معين يقول: «إبراهيم بن أبي عبلة ثقة» .
<span data-type="title" id=toc-535>(٥٣٤) </span>سألت يحيى بن معين عن أبي إبراهيم محمد بن القاسم الأسدي، قال: «ليس بشيء» .
<span data-type="title" id=toc-536>(٥٣٥) </span>سمعت يحيى بن معين يقول: «عمر بن موسى الشامي الذي يحدث عنه بقية هو الوجيهي، كذاب ليس بشيء» .
<span data-type="title" id=toc-537>(٥٣٦) </span>سمعت يحيى بن معين يقول: «صدقة الرماني شيخ بصري ضعيف الحديث، روى عنه أبو داود وغيره» .',NULL,'400',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (130,'<span data-type="title" id=toc-538>(٥٣٧) </span>قيل ليحيى وأنا أسمع: قيس بن أبي غرزة؛ روى حبيب بن أبي ثابت عن أبي وائل عن قيس؟ قال: «نعم، ومنصور وحبيب» .
<span data-type="title" id=toc-539>(٥٣٨) </span>سأل ابن الغلابي يحيى بن معين وأنا أسمع عن محمد بن أبان، فقال: «ليس بشيء» .
<span data-type="title" id=toc-540>(٥٣٩) </span>وسأله عن أيوب بن عتبة، فقال: «ضعيف» ، فعاوده، فقال: «ليس بذاك القوي» .
<span data-type="title" id=toc-541>(٥٤٠) </span>سألت يحيى عن عبد الملك بن عقاب، فقال: «روى عنه أبو عوانة» ، قلت: وعبيد الله بن عمرو؟ قال: «نعم» ، قلت: كوفي؟ قال: «لا، هو موصلي» .
<span data-type="title" id=toc-542>(٥٤١) </span>قال إبراهيم بن الجنيد: قلت: حدثنا عبيد بن جناد، حدثنا عبيد الله بن عمرو، عن عبد الملك بن عقاب، عن حماد، عن',NULL,'401',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (131,'إبراهيم بن علقمة بحديث لم يحفظه إبراهيم بن الجنيد.
<span data-type="title" id=toc-543>(٥٤٢) </span>سألت يحيى عن شيخ روى عنه المعافى بن عمران يقال له: راشد بن معبد، قال: رأيت أنسًا يحلب لقحة له على العسل، فيشرب، فقال لي يحيى: «هذا شيخ واسطي ضعيف ليس بشيء، يحدث عنه أبو الحسين العكلي» .
قال إبراهيم بن الجنيد: حدثنا بعض الرقيين، عن معافى، عنه.
<span data-type="title" id=toc-544>(٥٤٣) </span>سمعت يحيى بن معين يقول: «عثمان بن عمير أبو اليقظان، ليس بذاك،
<span data-type="title" id=toc-545>(٥٤٤) </span>عثمان بن أبي زرعة هو عثمان بن المغيرة، ثقة،
<span data-type="title" id=toc-546>(٥٤٥) </span>أبو المهزم ليس بشيء، واسمه يزيد بن سفيان» .
<span data-type="title" id=toc-547>(٥٤٦) </span>سمعت يحيى بن معين يقول: «محمد بن طلحة صالح،
<span data-type="title" id=toc-548>(٥٤٧) </span>أسامة بن زيد الليثي مديني صالح ليس بذاك،',NULL,'402',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (132,'<span data-type="title" id=toc-549>(٥٤٨) </span>وأسامة بن زيد بن أسلم وعبد الله بن زيد بن أسلم وعبد الرحمن بن زيد بن أسلم ليسوا بشيء» .
<span data-type="title" id=toc-550>(٥٤٩) </span>سمعت يحيى بن معين يقول: «موسى بن خلف ضعيف الحديث» .
<span data-type="title" id=toc-551>(٥٥٠) </span>سمعت يحيى يقول: «عمر بن إبراهيم صالح،
<span data-type="title" id=toc-552>(٥٥١) </span>عمر بن عامر ليس به بأس، كان قاضي البصرة» .
<span data-type="title" id=toc-553>(٥٥٢) </span>سمعت يحيى يقول: «جابر الجعفي ليس عندهم بشيء، حجاج بن أرطاة خير منه» .
<span data-type="title" id=toc-554>(٥٥٣) </span>سمعت يحيى بن معين يقول: «ليث بن أبي سليم ليس بذاك القوي،
<span data-type="title" id=toc-555>(٥٥٤) </span>سالم بن أبي حفصة ليس به بأس، كان مغليًا من الشيعة» .',NULL,'403',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (133,'الجزء الرابع
وبالإسناد قال:
<span data-type="title" id=toc-556>(٥٥٥) </span>سمعت يحيى بن معين يقول: «أبو نعيم النخعي عبد الرحمن بن هانئ ليس بثقة، كان يكذب، يروي عن سفيان الثوري أحاديث موضوعة» .
<span data-type="title" id=toc-557>(٥٥٦) </span>سمعت يحيى يقول: «العلاء بن المسيب ثقة مأمون» .
<span data-type="title" id=toc-558>(٥٥٧) </span>سمعت يحيى يقول: «زياد البكاء ليس به بأس، قال لي عبد الله بن إدريس: (باع شقصًا من داره وكتبها) » ، يعني: البكاء عن محمد بن إسحاق.
<span data-type="title" id=toc-559>(٥٥٨) </span>وسمعت يحيى يقول: «سلمة بن الفضل الأبرش ليس به بأس» .
<span data-type="title" id=toc-560>(٥٥٩) </span>وسمعت يحيى بن معين يقول -وسئل عن يونس بن خباب-،',NULL,'405',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (134,'فقال: «ليس بثقة، كان يشتم أصحاب النبي -صلى الله عليه وسلم-، ومن شتم أصحاب النبي -صلى الله عليه وسلم- فليس بثقة» .
<span data-type="title" id=toc-561>(٥٦٠) </span>قلت ليحيى: حدثنا أبو داود المباركي، عن ابن أبي زائدة، عن مجالد، عن زياد بن علاقة، عن سعد قال: بعثنا رسول الله -صلى الله عليه وسلم- إلى جهينة، فقال يحيى: حدثناه ابن أبي زائدة.
<span data-type="title" id=toc-562>(٥٦١) </span>حدثني أبو بكر بن صالح بن الفضل بن صالح بن شيخ بن عميرة الأسدي، قال: تذاكرنا يومًا أمر محمد بن عباد المكي وقول يحيى بن معين فيه، ثم انصرفت إلى منزلي، فقلت نصف النهار، فأتاني آت في منامي، فقال لي: (ما أحد يسمه يحيى فيرتفع أو يجوز -أو نحو هذا-) ، ثم أنشدني بيت شعر، قال: أنشده -يعني: في النوم-:',NULL,'406',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (135,'ولقد وسمتك غير متعذر ... ... ... ... بمواسم تبقى على الابد
<span data-type="title" id=toc-563>(٥٦٢) </span>سألت أبا زكريا يحيى بن معين: ابن عون، عن أيوب بن سويد الرملي؟ فقال: «ليس بشيء، كان حدثهم بالرملة بأحاديث عن عبد الله بن المبارك، ثم جعلها بعد عن نفسه عن رجال ابن المبارك» .
<span data-type="title" id=toc-564>(٥٦٣) </span>سألت يحيى عن ثابت بن يزيد أبي يزيد القيسي، فقال: «بصري ثقة» .
<span data-type="title" id=toc-565>(٥٦٤) </span>سئل يحيى عن زهير بن محمد، فقال: «ليس به بأس» ، قلت ليحيى: مكي؟ قال: «كان خراسانيًّا، وكان بمكة» .
<span data-type="title" id=toc-566>(٥٦٥) </span>قلت ليحيى: بلغنا موت محمد بن معاوية النيسابوري، فقال: «الحمد لله الذي أماته، فإنه كان يكذب على رسول الله -صلى الله عليه وسلم-» . قال إبراهيم بن الجنيد: يعني يكذب من غير تعمد، وكان رجلاً صالحًا، وجاور، وما زال بمكة حتى مات.',NULL,'407',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (136,'<span data-type="title" id=toc-567>(٥٦٦) </span>سمعت يحيى بن معين وقد ذكرنا عنده أحاديث من ملاحم الروم، فقال: «ليس من حديث الشاميين شيء أصح من حديث صدقة بن خالد عن النبي -صلى الله عليه وسلم-: (معقل المسلمين أيام الملاحم دمشق) » .
<span data-type="title" id=toc-568>(٥٦٧) </span>سمعت يحيى بن معين يقول: «حرام بن عثمان، عن أبي عتيق، ليس بشيء» ، قال يحيى: «حرام مات قديمًا، مات أيام أبي العباس» .
<span data-type="title" id=toc-569>(٥٦٨) </span>وسمعت يحيى بن معين يقول: «عبيد الله بن زحر، عن علي بن يزيد، ليس بشيء،
<span data-type="title" id=toc-570>(٥٦٩) </span>وبشر بن نمير ليس بشيء،
<span data-type="title" id=toc-571>(٥٧٠) </span>وجعفر بن الزبير ليس بشيء» .',NULL,'408',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (137,'<span data-type="title" id=toc-572>(٥٧١) </span>سمعت يحيى بن معين يقول: «القاسم أبو عبد الرحمن ثقة، ليس يروون عنه -يعني: القاسم- هذه الأحاديث لا يرفعونها» ، ثم قال يحيى: «من المشايخ الضعفاء ما يدل حديثهم على ضعفهم» .
<span data-type="title" id=toc-573>(٥٧٢) </span>وسأل ابن الغلابي يحيى بن معين وأنا أسمع عن محمد بن دينار الطاحي، فقال: «ليس به بأس» ، فعاوده، فقال: «ليس بالقوي» .
<span data-type="title" id=toc-574>(٥٧٣) </span>وسأله عن مصدع أبي يحيى، فقال: «لا أعرفه» .
<span data-type="title" id=toc-575>(٥٧٤) </span>قلت ليحيى بن معين: سلم بن أبي الذيال، روى عنه أحد غير معتمر؟ قال: «نعم، روى عنه إسماعيل بن سلم العبدي» ، قلت: كيف حديث سلم؟ قال: «ليس به بأس» .',NULL,'409',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (138,'<span data-type="title" id=toc-576>(٥٧٥) </span>قلت ليحيى: عمران بن الدوار ثبت؟ قال: «لا» ، قلت: ما حاله؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-577>(٥٧٦) </span>قلت ليحيى: الحميدي صاحب ابن عيينة ثقة هو؟ قال: «ما أدري، ليس لي به علم» .
<span data-type="title" id=toc-578>(٥٧٧) </span>سألت يحيى بن معين عن سوادة بن أبي الأسود، قال: «ليس به بأس» .
<span data-type="title" id=toc-579>(٥٧٨) </span>ذكر يحيى وأنا أسمع حديث العلاء، عن أبيه، عن أبي هريرة، عن النبي -صلى الله عليه وسلم-: «إذا مضى النصف من شعبان فلا تصوموا» ، فقال: «رواه زهير بن محمد وعبد الرحمن بن إبراهيم والزنجي» ، قلت ليحيى: والدراوردي، قال: «الدراوردي ومحمد بن جعفر لا يرفعانه» ، قلت ليحيى: حدثنا غير واحد عن الدراوردي يرفعه،',NULL,'410',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (139,'ثم قلت ليحيى: عبد الرحمن بن إبراهيم ثقة؟ قال: «ما رأينا أحدًا',NULL,'411',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (140,'يروي عنه إلا عفان» قلت: بصري؟ قال: «بصري» . قال ابن الجنيد: وقد رواه عن عبد الرحمن بن إبراهيم زيد بن الحباب.
<span data-type="title" id=toc-580>(٥٧٩) </span>قال يحيى بن معين: «عمرو بن دينار، عن يزيد بن جعدبة، هو جد يزيد بن عياض بن جعدبة» .
<span data-type="title" id=toc-581>(٥٨٠) </span>سمعت أبا خيثمة يقول: (كان يحيى بن سعيد الأنصاري قاضي أبي جعفر بالهاشمية) ، قال: (حدثنا يزيد بن هارون، أخبرنا يحيى بن سعيد قاضي أبي جعفر) .
<span data-type="title" id=toc-582>(٥٨١) </span>سألت يحيى بن معين عن موسى بن ربيعة الجمحي، فقال: «لا أعرفه، من حدثكم عنه؟» قلت: أبو الطاهر بن السرح، قال: «عمن يحدث؟» قلت: عن يحيى بن سعيد، قال: «لا أعرفه» ، وكان مصعب الزبيري حاضرًا، فقال: «لا أعرفه» .',NULL,'412',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (141,'<span data-type="title" id=toc-583>(٥٨٢) </span>سمعت يحيى بن معين يقول: «ابن أبي ذئب، عن خاله الحارث بن عبد الرحمن، ليس أحد يحدث عنه إلا ابن أبي ذئب» .
<span data-type="title" id=toc-584>(٥٨٣) </span>سألت يحيى عن الصعق بن حزن، فقال: «ثقة» .
<span data-type="title" id=toc-585>(٥٨٤) </span>قلت: من فيل بن عرادة؟ قلت: أراه بدويًّا؟ قال: «ما أشبهه» .
<span data-type="title" id=toc-586>(٥٨٥) </span>حدثنا إبراهيم قال: سأل رجل يحيى بن معين وأنا أسمع: أيما أحب إليك: القعقاع بن حكيم أم سمي؟ قال: «جميعًا، والقعقاع أقدم، وسمي مولى أبي بكر لا بأس به» .
<span data-type="title" id=toc-587>(٥٨٦) </span>قال رجل ليحيى بن معين وأنا أسمع: قال يحيى بن سعيد القطان: (عاصم الأحول لم يكن بالحافظ) ، فقال يحيى: «عاصم الأحول ثقة» .
<span data-type="title" id=toc-588>(٥٨٧) </span>سمعت يحيى يقول: «زافر بن سليمان خراساني ينسب إلى سجستان» ، فقلت ليحيى: كيف حديثه؟ قال: «لم يكن به بأس، قد حملنا عنه، يروي عن مسلم، وعن الأصبغ، وعن أبي سنان الصغير» ، يعني: زافرًا.',NULL,'413',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (142,'<span data-type="title" id=toc-589>(٥٨٨) </span>سألت يحيى عن عباد بن منصور الناجي، فقال: «كان قدريًّا، وكان صديقًا لأيوب السختياني، وكان قد ولي قضاء البصرة» .
<span data-type="title" id=toc-590>(٥٨٩) </span>سمعت يحيى قال: سمعت وهب بن جرير يقول: (ولي عباد بن منصور قضاء البصرة، فأتاه أيوب براحلة وغلام، فقال: هذه راحلة وهذا غلام يخرج معك إلى مكة) ، قال يحيى: وقال وهب: (يذهب إلى قدري يعرض عليه، وما يبالي لو أهلكه الله) .
<span data-type="title" id=toc-591>(٥٩٠) </span>قلت ليحيى بن معين: كان وهب بن جرير يثبت القدر؟ قال: «نعم» .
<span data-type="title" id=toc-592>(٥٩١) </span>قلت: كيف حديث عباد بن منصور؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-593>(٥٩٢) </span>قلت ليحيى: حدثنا أبو سعيد الحداد، عن سرور بن المغيرة ابن أخي',NULL,'414',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (143,'منصور بن زاذان، عن عباد بن منصور، عن الحسن؟ قال: «نعم، كان يروي عنه التفسير» ، قلت: من مسرور هذا؟ قال: «زعموا أنه واسطي، لا أعرفه» ، قال: «رحم الله أبا سعيد» .
<span data-type="title" id=toc-594>(٥٩٣) </span>سمعت يحيى بن معين يقول: «أبو عاصم -يعني: النبيل- لم يكن فصيحًا» ، يعني: لم يكن يعرب.
<span data-type="title" id=toc-595>(٥٩٤) </span>حدثنا يحيى بن معين قال: «بلغني عن هشام الكلبي، عن أبي بكر بن عياش، قال: قلت لعبد الملك بن عمير: متى هلك لبيد بن ربيعة؟ قال: (يوم نزل معاوية النخيلة) » ، قلت ليحيى: كان الكلبي هشام ثقة؟ قال: «كان أعلم الناس بالنسب» ، وقلت ليحيى: أين النخيلة؟ قال: «بالكوفة» .
<span data-type="title" id=toc-596>(٥٩٥) </span>وسمعت يحيى بن معين يقول: «سمع ابن جريج من مجاهد حرفًا واحدًا في القراءة: {فإن الله لا يهدي من يضل} -قال: لا أدري كيف قرأه يحيى بن معين-، لم يسمع منه غيره، كان أتاه ليسمع منه، فأتاه فوجده قد مات» .',NULL,'415',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (144,'<span data-type="title" id=toc-597>(٥٩٦) </span>وسمعت يحيى بن معين مرة أخرى يقول: «لم يسمع ابن جريج من مجاهد إلا حرفًا أو حرفين» .
<span data-type="title" id=toc-598>(٥٩٧) </span>وسمعت يحيى بن معين -وذكروا الفضل بن سخيت أبا العباس المسندي-، فقال: «كذاب، ما سمع من عبد الرزاق شيئًا، كان يتصدق» قالوا: إنه يحدث، قال: «لعن الله من يكتب عنه من صغير أو كبير، إلا أن يكون لا يعرفه» .
<span data-type="title" id=toc-599>(٥٩٨) </span>قيل ليحيى بن معين وأنا أسمع: معمر بن راشد لم ير الحسن البصري؟ قال: «لا» .
<span data-type="title" id=toc-600>(٥٩٩) </span>قيل ليحيى بن معين: إسماعيل بن أبي خالد، عن محمول، من محمول هذا؟ قال: «محمول مولى عمارة بن عقبة» ، قيل ليحيى وأنا شاهد: تعرفه؟ قال: «لا» .
<span data-type="title" id=toc-601>(٦٠٠) </span>وسئل يحيى بن معين وأنا شاهد عن جعفر بن كيسان، فقال: «بصري، روى عنه يزيد بن هارون والبصريون» ، قلت ليحيى: كيف حديثه؟ قال: «ليس به بأس» .',NULL,'416',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (145,'قال: «ويحدث عن عمرة عن عائشة، ليس هي عمرة بنت عبد الرحمن، هي عمرة، ينسبونها» . قال إبراهيم بن الجنيد: هي عمرة بنت قيس العدوية، بصرية.
<span data-type="title" id=toc-602>(٦٠١) </span>سئل يحيى وأنا أسمع عن عبد الرحمن بن أبي الرجال أخي حارثة، فقال: «ليس به بأس» .
<span data-type="title" id=toc-603>(٦٠٢) </span>قلت ليحيى: كيف حديث حارثة بن أبي الرجال؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-604>(٦٠٣) </span>قلت ليحيى بن معين: أبو عبد الرحمن سمع من عبد الله شيئًا؟ قال: «لم يسمع من عثمان ولا من عبد الله، ولكن سمع من علي» ، وأظن يحيى بن معين ذكر هذا عن شعبة.
<span data-type="title" id=toc-605>(٦٠٤) </span>سألت يحيى عن محمد بن علي السلمي، الذي يحدث عنه علي بن هاشم، عن منصور، عن ربعي، عن عمران بن حصين: «لأعطين الراية» ، فقال يحيى: «هو أخو منصور بن المعتمر لأمه» ، قلت: ثقة؟ قال: «ليس به بأس» .',NULL,'417',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (146,'<span data-type="title" id=toc-606>(٦٠٥) </span>سألت يحيى عن حديث شعبة، عن أبي المؤمل، عن الزهري، عن عروة، عن عائشة: كان النبي -صلى الله عليه وسلم- إذا صلى الركعتين اضطجع، حدثناه عمرو بن مرزوق وغيره، قلت له: من أبو المؤمل؟ قال: «شيخ من أهل الشام» ، قلت: يسميه أحد؟ قال: «ما سمعت أحدًا يسميه» .
<span data-type="title" id=toc-607>(٦٠٦) </span>قلت ليحيى: عمر بن صهبان، يروي عنه عبد الله بن وهب ومحمد بن بكر البرساني، كيف حديثه؟ قال: «ليس بثقة» ، قلت: مديني؟ قال: «مديني» .
<span data-type="title" id=toc-608>(٦٠٧) </span>سألت يحيى عن عمرو بن سعيد الذي روى عن ابن عون وأيوب ويونس بن عبيد قال يحيى: «وداود بن أبي هند» ، قال: «هو شيخ بصري» .
<span data-type="title" id=toc-609>(٦٠٨) </span>سمعت يحيى يقول: «ثابت بن عمارة ثقة» .
<span data-type="title" id=toc-610>(٦٠٩) </span>سألت يحيى بن معين عن يونس بن أبي الفرات، فقال: «الذي روى عن قتادة؟» قلت: نعم، قال: «ليس به بأس بصري» .',NULL,'418',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (147,'<span data-type="title" id=toc-611>(٦١٠) </span>وسألت يحيى بن معين عن الصلت بن طريف، قال: «ليس به بأس» ، فقلت: بصري؟ قال: «نعم، معولي» .
<span data-type="title" id=toc-612>(٦١١) </span>سألت يحيى بن معين عن علي بن مسعدة الباهلي، فقال: «صالح» .
<span data-type="title" id=toc-613>(٦١٢) </span>سمعت يحيى يقول: «السكن البصري الذي يروي عن الحسن البصري، ليس به بأس، هو الذي يحدث حديث عبد الرحمن بن خباب في قصة عثمان» .',NULL,'419',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (148,'<span data-type="title" id=toc-614>(٦١٣) </span>سئل يحيى بن معين وأنا أسمع عن عيسى بن تليدان، فقال يحيى: «هو الذي يحدث عن القاسم، عن عائشة: «أعظم النكاح بركة أيسره مؤنة» ، وهو من ولد أبي بكر الصديق، ليس به بأس، حدث عنه حماد بن سلمة ووكيع وعثمان بن عمر» .
<span data-type="title" id=toc-615>(٦١٤) </span>وسمعت يحيى يقول: «المستمر بن الريان ثقة» .
<span data-type="title" id=toc-616>(٦١٥) </span>سألت يحيى بن معين عن أبي عامر صالح بن رستم، قال: «بصري» ، فقلت: فكيف حديثه؟ قال: «ليس بشيء» .
<span data-type="title" id=toc-617>(٦١٦) </span>سئل يحيى وأنا اسمع عن سعيد بن حسان، فقال: «هو مخزومي مكي ليس به بأس، هو الذي روى حديث أم حبيبة: (كل كلام ابن آدم عليه لا له، إلا كذا وكذا) » .',NULL,'420',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (149,'<span data-type="title" id=toc-618>(٦١٧) </span>سألت يحيى بن معين عن سعيد بن خثيم الهلالي، فقال: «شيخ كوفي ليس به بأس ثقة» ، فقال رجل ليحيى: شيعي؟ قال: «وشيعي ثقة، وقدري ثقة» .
<span data-type="title" id=toc-619>(٦١٨) </span>سئل يحيى وأنا أسمع عن عمر بن مزيد، فقال: «ثقة شيخ بصري» ، قلت ليحيى: من روى عنه؟ قال: «وكيع ومعتمر» .
<span data-type="title" id=toc-620>(٦١٩) </span>قيل ليحيى بن معين وأنا أسمع: يزيد الفارسي روى عنه أحد غير عوف؟ قال: «لا» ، قلت ليحيى: فإنهم يزعمون أن يزيد بن هرمز هو يزيد الفارسي الذي روى عنه الزهري وقيس بن سعد حديث نجدة، فقال: «باطل، كذب، شيء وضعوه، ليس هو ذاك» .',NULL,'421',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (150,'<span data-type="title" id=toc-621>(٦٢٠) </span>قيل ليحيى بن معين وأنا أسمع: حكيم الأثرم روى عنه أحد غير حماد بن سلمة؟ قال: «ما أعلم أحدًا روى عنه غير حماد بن سلمة، وزياد الأعلم أيضًا كذلك» .
<span data-type="title" id=toc-622>(٦٢١) </span>سألت يحيى بن معين عن معاوية النصري الذي يحدث عنه أبو معاوية، عن نهشل، عن الضحاك، عن الأسود، عن عبد الله: (لو أن أهل العلم صانوا العلم) ، فقال: «هو معاوية بن سلمة» ، قلت: كيف حديثه؟ فكأنه ضعفه.',NULL,'422',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (151,'<span data-type="title" id=toc-623>(٦٢٢) </span>سئل يحيى وأنا أسمع عن أبي أيوب سليمان بن شرحبيل الدمشقي، فقال: «ليس به بأس» .
<span data-type="title" id=toc-624>(٦٢٣) </span>سألت يحيى عن جابر بن نوح الحماني، فقال: «قد كان هاهنا» ، قلت: كتبت عنه شيئًا؟ قال: «لا» .
<span data-type="title" id=toc-625>(٦٢٤) </span>سئل يحيى بن معين وأنا أسمع عن الوهبي محمد بن خالد، فقال: «ثقة» ، قلت ليحيى: حمصي؟ قال: «نعم» .
قال إبراهيم بن الجنيد: هما أخوان: محمد بن خالد وأحمد بن خالد.',NULL,'423',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (152,'<span data-type="title" id=toc-626>(٦٢٥) </span>سألت يحيى عن عمر بن الدرفس، حدثنا عن هشام وابن بنت شرحبيل، فقال: «لا أعرفه» .
<span data-type="title" id=toc-627>(٦٢٦) </span>سألت يحيى عن بزيع بن حسان أبي الخليل، قال: «لا أعرفه» قلت أنا: حدثنا عبد الرحمن بن المبارك عنه، عن هشام بن عروة بمناكير.
<span data-type="title" id=toc-628>(٦٢٧) </span>سألت يحيى عن راشد بن داود الصنعاني، فقال: «ليس به بأس ثقة، روى عنه أبو مطيع معاوية بن يحيى وإسماعيل بن عياش» .
<span data-type="title" id=toc-629>(٦٢٨) </span>قلت ليحيى: كيف حديث أبي مطيع هذا الأطرابلسي؟ قال: «صالح ليس بذاك القوي» .
<span data-type="title" id=toc-630>(٦٢٩) </span>قال يحيى وأنا أسمع: «صنعاء قرية من قرى الشام، منها راشد ابن داود، وأبو الأشعث الصنعاني، وحنش، ليس صنعاء اليمن» .',NULL,'424',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (153,'<span data-type="title" id=toc-631>(٦٣٠) </span>سألت يحيى عن عبد الجبار بن الورد، فقال: «ثقة ليس به بأس» ، قلت: هو أخو وهيب بن الورد؟ قال: «نعم» ، قلت: ثقة؟ قال: «ثقة» .
<span data-type="title" id=toc-632>(٦٣١) </span>قلت ليحيى: عثمان بن أبي رواد أخو عبد العزيز بن أبي رواد؟ قال: «نعم» ، قلت: ثقة؟ قال: «ثقة، وعبد العزيز ثقة، وابنه عبد المجيد بن عبد العزيز بن أبي رواد» ، فقال ابن الغلابي ليحيى شيئًا، فقال يحيى: «إنما كان الحميدي وأولئك يقعون فيه، أراد أن يذل لهم، فلم يفعل، وهو ثقة في نفسه، إلا أنه كان يرى رأي الإرجاء، إلا أنه كان يروي عن قوم ضعفاء، وأما في نفسه فهو ثقة» ، يعني: عبد المجيد.
<span data-type="title" id=toc-633>(٦٣٢) </span>سئل يحيى عن بكار ابن أخي موسى بن عبيدة الربذي، فقال: «ضعيف» .
<span data-type="title" id=toc-634>(٦٣٣) </span>قال يحيى وأنا أسمع: «أبو الهيثم صاحب دراج الذي يروي عن أبي سعيد اسمه سليمان بن عمرو العتواري» .
<span data-type="title" id=toc-635>(٦٣٤) </span>قيل ليحيى وأنا أسمع: سعيد بن أبي عروبة، قال: «سمع من الحسن وابن سيرين» .',NULL,'425',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (154,'<span data-type="title" id=toc-636>(٦٣٥) </span>سألت يحيى عن سعد بن عبد الحميد بن جعفر، فقال: «ليس به بأس، قد كتبت عنه» .
<span data-type="title" id=toc-637>(٦٣٦) </span>وسألته عن أبي الحارث الوراق، فقال: «ليس بثقة» .
<span data-type="title" id=toc-638>(٦٣٧) </span>سألت يحيى بن معين عن جابر بن يزيد بن رفاعة، فقال: «موصلي ثقة صدوق» ، قلت ليحيى: من روى عنه؟ قال: «يحيى بن يمان وابن مهدي وعفان» .
قال إبراهيم بن الجنيد: حدثنا عنه أحمد بن يونس، وهو جد أبي هشام الرفاعي.',NULL,'426',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (155,'<span data-type="title" id=toc-639>(٦٣٨) </span>سئل يحيى عن محمد بن دينار الطاحي، فقال: «ليس بذاك القوي» .
<span data-type="title" id=toc-640>(٦٣٩) </span>وسألت يحيى عن منصور بن دينار الكوفي، فقال: «ليس بشيء» .
<span data-type="title" id=toc-641>(٦٤٠) </span>وسألته عن عيسى بن دينار مؤذن بني خليد، فقال: «ليس به بأس» .
<span data-type="title" id=toc-642>(٦٤١) </span>سألت يحيى عن يزيد بن يوسف الذي يروي عن الأوزاعي، فقال: «شامي كان هاهنا ببغداد، ليس بشيء» .
قال ابن الجنيد: حدث عنه منصور بن أبي مزاحم.
<span data-type="title" id=toc-643>(٦٤٢) </span>قلت ليحيى بن معين: قتادة سمع من عبد الله بن رباح؟ قال: «نعم، يشبه» .
<span data-type="title" id=toc-644>(٦٤٣) </span>سألت يحيى عن أبي هلال الراسبي؟ فقال: «صالح ليس بذاك القوي» ، فقال رجل ليحيى بن معين: إن يحيى بن سعيد قال: (لأن أحدث عن عمرو بن عبيد أحب إلي من أن أحدث عن أبي هلال الراسبي) ، فقال يحيى بن معين: «عمرو بن عبيد ليس بشيء، رجل سوء، وأبو هلال صدوق» .',NULL,'427',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (156,'<span data-type="title" id=toc-645>(٦٤٤) </span>سألت يحيى بن معين عن مسلمة بن علي الخشني والحسن بن يحيى الخشني، فقال: «ضعيفان» ، قلت ليحيى: كان الحسن بن يحيى أقوى؟ فقال: «ضعيفان» .
<span data-type="title" id=toc-646>(٦٤٥) </span>قال رجل ليحيى بن معين وأنا أسمع: زعم إبراهيم بن عرعرة أن محمد بن ذكوان والحسين بن ذكوان ليسا بشيء؟ فغضب يحيى، وقال: «أما الحسين بن ذكوان فحدثني عنه يحيى بن سعيد وعبد الله بن المبارك، ولكن كان قدريًّا، وأما محمد بن ذكوان فليس به بأس، أي شيء كان عنده؟! روى عنه حماد بن زيد وعبد الوارث وعبد الصمد، لا بأس به، قل لابن عرعرة: اذهب ازرع» .
<span data-type="title" id=toc-647>(٦٤٦) </span>قلت ليحيى: أبو سعيد الرقاشي الذي روى عنه سليمان',NULL,'428',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (157,'التيمي، اسمه قيس؟ قال: «نعم» ، قلت: روى عنه عاصم الأحول؟ قال: «نعم» .
<span data-type="title" id=toc-648>(٦٤٧) </span>قال رجل ليحيى بن معين وأنا أسمع: عبد الحميد بن جعفر، عن أبيه، عن سمرة؟ قال: «لم يلق سمرة» .
<span data-type="title" id=toc-649>(٦٤٨) </span>قال يحيى بن معين وأنا أسمع: «عمر بن الحكم بن ثوبان، ليس هو ثوبان، إنما ثوبان لقب، قال لي سعد بن عبد الحميد بن جعفر: (هو عمي) ، قال: (وليس فينا أحد يسمى ثوبان، إنما هو لقب) » .
<span data-type="title" id=toc-650>(٦٤٩) </span>قال رجل ليحيى بن معين وأنا أسمع: حديث سعد بن عبد الحميد؟ قال: «ليس به بأس، كان سماعه عرضًا» ، قلت ليحيى: عرض؟ قال: «أحسن حالاته أن يكون عرضًا» .
<span data-type="title" id=toc-651>(٦٥٠) </span>سئل يحيى وأنا أسمع عن عبيد بن أبي قرة، قال: «ما كان به بأس، كان من التجار في القطيعة، وكان من أهل الهيئة والكرم، وكان عنده كتاب عن عبد الجبار بن الورد، وكتاب سليمان بن بلال، ما سمعت منه عن الليث إلا ذاك الحديث الواحد، وكانوا رحلوا إلى ابن لهيعة» .',NULL,'429',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (158,'قال أبو إسحاق: سمعت علي بن المديني -وذكر عبيد بن أبي قرة-، فقال: (ما كان به بأس، وما أنكرت عليه إلا صحبته حسينًا الأشقر) .
قلت ليحيى: كان رحل مع معلى؟ قال: «كان أسن من معلى» .
<span data-type="title" id=toc-652>(٦٥١) </span>ذكر يحيى محمد بن معاوية، والحديث الذي رواه عن محمد بن سلمة، قال يحيى: «محمد بن سلمة مأمون يصدق، وهذا',NULL,'430',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (159,'الحديث إنما هو وضع» .
<span data-type="title" id=toc-653>(٦٥٢) </span>سألت يحيى عن إسماعيل بن أبان الوراق، فقال: «ليس به بأس، كان صديقًا لي، ما كتبت عنه شيئًا قط، وكان يحدث عن شيوخ ضعفاء» .
<span data-type="title" id=toc-654>(٦٥٣) </span>سألت يحيى عن يعقوب القمي، فقال: «ثقة» .
<span data-type="title" id=toc-655>(٦٥٤) </span>قلت ليحيى: عمرو بن شعيب، عن أبيه، عن جده، ضعيف؟ فقال: «كأنه ليس بذاك» ، قلت: فما روى عن سعيد بن المسيب وغيره؟ قال: «عمرو بن شعيب ثقة» .
<span data-type="title" id=toc-656>(٦٥٥) </span>سألت يحيى عن عبد القاهر بن السري السلمي، قال: «لم يكن به بأس» ، قال ابن الغلابي -وهو إلى جانبه-: (كان من أشراف بني سليم) ، فلم ينكر يحيى قوله.
<span data-type="title" id=toc-657>(٦٥٦) </span>سألت يحيى عن محمد بن عقبة الرفاعي، قال: «الذي يروي عن الحسن؟» ، قلت نعم، قال: «ليس به بأس، يحدث عنه عبد الرحمن بن مهدي» .
<span data-type="title" id=toc-658>(٦٥٧) </span>وسمعت يحيى يقول: «عباس النرسي والآخر -يعني:',NULL,'431',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (160,'عبد الأعلى بن حماد النرسي- لا بأس بهما، كانوا كتابًا من ولد نرس، قالوا: (نحب أن ننسب إليه) » ، قلت ليحيى: من نرس؟ قال: «بعض كتاب العجم» .
<span data-type="title" id=toc-659>(٦٥٨) </span>سمعت يحيى بن معين يقول: «فيض بن الوثيق كذاب خبيث» .
<span data-type="title" id=toc-660>(٦٥٩) </span>قلت ليحيى: أيما أحب إليك: أن أكتب عنه جامع سفيان، عن حكام الرازي، أو غسان بن عبيد أو المعافى بن عمران؟ فقال لي يحيى: «اكتب عن عشرة عن المعافى بن عمران» .
<span data-type="title" id=toc-661>(٦٦٠) </span>سمعت يحيى بن معين يقول: «اسم أبي جناب الكلبي: يحيى بن أبي حية» .
قلت ليحيى بن معين: كيف حديثه؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-662>(٦٦١) </span>قلت: وأبو جناب القصاب؟ قال: «ثقة» .
قال إبراهيم: اسم أبي جناب القصاب: عون بن ذكوان.',NULL,'432',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (161,'<span data-type="title" id=toc-663>(٦٦٢) </span>سألت يحيى عن قاسم الجرمي الموصلي، قال: «ليس به بأس، قاسم بن يزيد، ثقة» .
<span data-type="title" id=toc-664>(٦٦٣) </span>سألت يحيى عن حماد بن واقد أبي عمر الصفار، قال: «لا أعرفه» .
<span data-type="title" id=toc-665>(٦٦٤) </span>سألت يحيى بن معين عن عبد الرحمن بن القاسم المصري، فقال: «ثقة، رجل صدق» .
<span data-type="title" id=toc-666>(٦٦٥) </span>سئل يحيى وأنا أسمع عن الحارث بن مسكين المصري، فقال: «لا بأس به» .
<span data-type="title" id=toc-667>(٦٦٦) </span>سألت يحيى بن معين عن النهاس بن قهم، قال: «كان النهاس بن قهم قاصًّا، ليس بشيء» .
<span data-type="title" id=toc-668>(٦٦٧) </span>سمعت يحيى بن معين يقول: «محمد بن جحادة ثقة، رجل صدق» .',NULL,'433',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (162,'<span data-type="title" id=toc-669>(٦٦٨) </span>سألت يحيى عن عبد الصمد بن النعمان جار معاوية بن عمرو، قال: «ذاك الذي كان يعين» ، قلت: كتبت عنه شيئًا؟ قال: «لا» ، قلت: كيف حديثه؟ قال: «لا أراه كان ممن يكذب» .
<span data-type="title" id=toc-670>(٦٦٩) </span>سألت يحيى عن أبي كدينة يحيى بن المهلب، قال: «ثقة» .
<span data-type="title" id=toc-671>(٦٧٠) </span>سمعت يحيى يقول: «بهلول بن المؤرق الشامي ثقة، قد كتبت عنه» ، قلت ليحيى: سمعت حديث الزهري: «قلبت مشارق الأرض» ؟ قال: «نعم، سمعته منه» .
<span data-type="title" id=toc-672>(٦٧١) </span>سألت يحيى بن معين عن سلام أبي المنذر، أثقة هو؟ قال: «لا» .',NULL,'434',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (163,'<span data-type="title" id=toc-673>(٦٧٢) </span>وسألته عن حصين بن نمير أبي محصن، فقال: «صالح» .
<span data-type="title" id=toc-674>(٦٧٣) </span>سمعت يحيى يقول: «أبو معمر صاحب عبد الوارث ثقة نبيل عاقل، رحمه الله» .
<span data-type="title" id=toc-675>(٦٧٤) </span>سمعت يحيى بن معين ذكر حسينًا الأشقر، فقال: «كان من الشيعة المغلية الكبار» ، قلت: فكيف حديثه؟ قال: «لا بأس به» ، قلت: صدوق؟ قال: «نعم، كتبت عنه عن أبي كدينة ويعقوب القمي» .
<span data-type="title" id=toc-676>(٦٧٥) </span>قلت ليحيى: الفضل بن عيسى الرقاشي هو أخو يزيد الرقاشي؟ قال: «لا، يزيد بن أبان الرقاشي فضل الرقاشي ابن أخته» ، قلت: كيف حديثه؟ قال: «ضعيف شيخ قاص، حدث عنه حماد بن زيد ومعتمر بن سليمان» ، قلت ليحيى: الفضل بن الرقاشي تعلم أحدًا روى عنه غير عاصم الأحول؟ قال: «لا أعلمه» .
<span data-type="title" id=toc-677>(٦٧٦) </span>قلت ليحيى: حجاج بن محمد بن عقبة عن سنان، من عقبة هذا؟ قال: «هذا عقبة بن سنان، كان كاتبًا ببغداد، قال حجاج: (أعطاني عقبة بن سنان كتابًا أخذه من ابن شبث، عن عمر بن',NULL,'435',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (164,'عبد العزيز طويل) » ، ثم قال يحيى: «أيش عندك؟» ، قلت: حجاج، عن عقبة بن سنان، حديث طويل؛ كلام أكثم بن صيفي، قال: «من حدثكم به؟» ، قلت: حدثنا به سنيد.
<span data-type="title" id=toc-678>(٦٧٧) </span>سألت يحيى عن القاسم بن أبي بزة، هو ابن نافع؟ قال: «نعم» ، قلت: مكي؟، قال: «مكي ثقة» .
<span data-type="title" id=toc-679>(٦٧٨) </span>سمعت يحيى يقول: «أبو ظبيان حصين بن جندب الجنبي هو أبو قابوس، وهو الذي روى عنه الأعمش، ليس أبو ظبيان يحدث عنه إلا هذا. حديث مسعر أن عمر بن الخطاب قال لأبي ظبيان: (ما مالك غير هذا؟) ، وشعبة وسفيان -وأظنه',NULL,'436',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (165,'قال: المسعودي-، ومسعر يحدث عن ابن معقل» .
<span data-type="title" id=toc-680>(٦٧٩) </span>حدثنا يحيى بن معين، حدثنا علي بن عياش، حدثنا أبو ثوبان، عن حسان بن عطية، عن عبد الله بن أبي زكريا، عن رجل، عن سلمان، عن النبي -صلى الله عليه وسلم- قال: «رباط ليلة أو يوم وليلة أفضل من صيام شهر وقيامه، صائمًا لا يفطر، وقائمًا لا يفتر، إن مات مرابطًا جرى عليه كصالح عمله حتى يبعث، ووقي عذاب القبر» .
<span data-type="title" id=toc-681>(٦٨٠) </span>سمعت يحيى بن معين يقول: «عبد الرحمن بن مالك بن مغول كذاب» .
<span data-type="title" id=toc-682>(٦٨١) </span>سمعت يحيى بن معين يقول: «يحيى بن يمان ليس بثبت، قال وكيع: (هذه الأحاديث التي يحدث بها يحيى بن يمان ليست من أحاديث سفيان) » ،',NULL,'437',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (166,'قال يحيى: «لم يكن يبالي أي شيء حدث، كان يتوهم الحديث» .
<span data-type="title" id=toc-683>(٦٨٢) </span>سمعت يحيى بن معين يقول: «وهب بن إسماعيل الكوفي ليس بشيء، قد كتبت عنه» .
<span data-type="title" id=toc-684>(٦٨٣) </span>قلت ليحيى بن معين: العوام بن مزاحم روى عنه أحد غير شعبة؟ قال: «ما أعلمه» .
<span data-type="title" id=toc-685>(٦٨٤) </span>قال يحيى وأنا أسمع: «ولا أعلم أحدًا روى عن أبي عيسى يحيى بن رافع إلا إسماعيل بن أبي خالد» .
<span data-type="title" id=toc-686>(٦٨٥) </span>سألت يحيى عن الصهباني زكريا بن عبد الله بن يزيد النخعي، فقال: «كوفي ليس به بأس» .
<span data-type="title" id=toc-687>(٦٨٦) </span>سألت يحيى عن أبي الأسباط الذي روى عنه حاتم، فقال: «هو كوفي، لم أسمع من حاتم عن أبي الأسباط شيئًا» .',NULL,'438',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (167,'<span data-type="title" id=toc-688>(٦٨٧) </span>سألت يحيى عن عمر بن حمزة الذي روى عنه أبو أسامة، فقال: «هو عمري» ، قلت: هو مستقيم الحديث؟ قال: «صالح ليس بذاك، يحدث عنه مروان الفزاري» .
<span data-type="title" id=toc-689>(٦٨٨) </span>قلت ليحيى بن معين: حدث عبد الوارث، عن أبي الجلاس عقبة بن سيار، عن علي بن شماخ، سمعت مروان يسأل أبا هريرة عن الصلاة على الميت، وشعبة يقول: عن الجلاس، عن عثمان بن شماس، فقال يحيى: «شعبة يصحف فيه، وعبد الوراث أثبت فيه منه» .
قال ابن الجنيد: حدثنا مسدد وأبو معمر جميعًا، عن عبد الوارث ح
وحدثنا عمرو بن مرزوق، عن شعبة،',NULL,'439',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (168,'<span data-type="title" id=toc-690>(٦٨٩) </span>سألت يحيى بن معين عن الحارث بن عمير، فقال: «ثقة بصري، وكان بمكة» .
قال ابن الجنيد: حدثنا إبراهيم بن محمد بن العباس الشافعي، قال: سمعت فضيل بن عياض في جنازة الحارث بن عمير وهو يقول: (هل سمعتم أحدًا إلا وهو يقول: نعم الرجل -يعني: الحارث بن عمير؟) .
<span data-type="title" id=toc-691>(٦٩٠) </span>سمعت يحيى بن معين يقول: «الصباح بن ثابت ثقة كوفي بجلي، روى عنه ابن نمير وأبو نعيم وهؤلاء» ، قلت: لقي الشعبي؟ قال: «نعم» .
<span data-type="title" id=toc-692>(٦٩١) </span>سمعت يحيى بن معين يقول: «عصمة بن محمد الأنصاري إمام مسجد الأنصار ببغداد» ، قال: «كان كذابًا، يروي أحاديث كذب، قد رأيته، وكان شيخًا له هيئة ومنظر، من أكذب الناس» .
<span data-type="title" id=toc-693>(٦٩٢) </span>سمعت يحيى بن معين -وذكر سعيد بن عمرو الأشعثي-، فقال: «شيخ صدوق لا بأس به» .
<span data-type="title" id=toc-694>(٦٩٣) </span>وسمعت يحيى يقول: «قدمت من اليمن إلى ... سنة ثنتين وتسعين ومائة، ومات عبد الرزاق سنة إحدى عشرة ومائتين» .',NULL,'440',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (169,'<span data-type="title" id=toc-695>(٦٩٤) </span>سمعت يحيى بن معين يقول: «شداد أبو عمار، شيخ شامي، روى عنه الأوزاعي وعكرمة بن عمار والنهاس بن قهم» ، قلت ليحيى: كيف حديث شداد أبي عمار؟ قال: «ليس به بأس» .
<span data-type="title" id=toc-696>(٦٩٥) </span>قلت ليحيى: فأبو طلحة شداد بن سعيد الراسبي؟ قال: «شيخ بصري» ، قلت: كيف حديثه؟ قال: «ثقة لا بأس به» .
<span data-type="title" id=toc-697>(٦٩٦) </span>سأل ابن الغلابي عن يزيد الفقير، فقال يحيى: «يحدث عنه عبيدة بن معتب» .
<span data-type="title" id=toc-698>(٦٩٧) </span>قلت ليحيى: كيف حديث عبيدة؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-699>(٦٩٨) </span>سمعت يحيى بن معين يقول: «النعمان بن راشد ضعيف الحديث» ، قلت ليحيى: ضعيف فيما روى عن الزهري وحده؟ قال: «عن الزهري وغير الزهري، هو ضعيف الحديث» .
<span data-type="title" id=toc-700>(٦٩٩) </span>سمعت يحيى بن معين يقول: «كان مغيرة الضبي مكفوفًا،',NULL,'441',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (170,'وعبيد الضبي مكفوف، والقعقاع مكفوف، وسماك مكفوف، وهم من بني ضبة» .
<span data-type="title" id=toc-701>(٧٠٠) </span>سمعت يحيى يقول: «عبيد الله بن موسى رجل صدق، ليس به بأس، كان له هدى وعقل ووقار» .
<span data-type="title" id=toc-702>(٧٠١) </span>سئل يحيى وأنا أسمع عن مؤمل بن إهاب، فكأنه ضعفه.
<span data-type="title" id=toc-703>(٧٠٢) </span>وذكر يحيى بن معين محمد بن يحيى بن سعيد القطان، فقال: «ثقة، رجل صدق، ولا بأس به، صاحب غزو» .
<span data-type="title" id=toc-704>(٧٠٣) </span>سمعت يحيى بن معين يقول: «علي بن المديني من أروى الناس عن يحيى بن سعيد، إني أرى عنده أكثر من عشرة آلاف» ، قلت: ليحيى أكثر من مسدد؟ قال: «نعم، إن يحيى بن سعيد كان يكرمه ويدنيه، وكان صديقه -يعني عليًّا-، وكان علي يلزمه» .',NULL,'442',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (171,'<span data-type="title" id=toc-705>(٧٠٤) </span>قلت ليحيى بن معين: يحيى بن المختار الذي روى عنه معمر؟ قال: «شيخ بصري ليس به بأس» ، قلت: تعلم أحدًا روى عنه غير معمر؟ قال: «لا أعلمه» .
<span data-type="title" id=toc-706>(٧٠٥) </span>قلت: الحكم بن ظهير يحدث عنه، قال: «الحكم بن ظهير قد سمعت منه، ليس بشيء» .
<span data-type="title" id=toc-707>(٧٠٦) </span>سألت يحيى بن معين عن شداد بن سعيد الراسبي ويكنى أبا طلحة، فقال: «ثقة» ، قلت ليحيى: إن ابن عرعرة يزعم أنه ضعيف، فغضب، وقال: «هو ثقة» ، وتكلم يحيى بكلام وأبو خيثمة يسمع، فقال أبو خيثمة: «شداد بن سعيد ثقة» .
ثم قال لي يحيى: يزعم ابن عرعرة أن سالم بن رزين ثقة؟ قلت: كذلك يقول، قال: «هو ضعيف ضعيف» .
<span data-type="title" id=toc-708>(٧٠٧) </span>قلت ليحيى: حدث أبو سلمة التبوذكي، عن عصام بن أبي عصام، عن شعيب، عن أبي هريرة: (أكثر الناس خطايا يوم القيامة',NULL,'443',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (172,'أكثرهم خوضًا في الباطل) .
قلت: من عاصم هذا؟ قال: «لا أعرفه» .
قال أبو إسحاق: قلت: لعله عصام بن طليق؟ قال: «لا أدري» .
<span data-type="title" id=toc-709>(٧٠٨) </span>قلت ليحيى: المفضل بن فضالة البصري، ويكنى أبا مالك القرشي، مولى عبد الرحمن بن يزيد بن الخطاب؟ فقال: «شيخ، وأيش عنده؟!» .
<span data-type="title" id=toc-710>(٧٠٩) </span>قلت ليحيى: عبد الرزاق، عن معمر، عن ابن طاوس، عن أبيه، عن ابن عباس، أن النبي -صلى الله عليه وسلم- كان يقرأ في صلاة الغداة يوم الجمعة: تنزيل السجدة، وسورة من المفصل. فقال: «لا أعرفه، من حدثكم هذا؟ مؤمل؟» قلت: لم أسمعه منه، وقد رواه» ، قال: «ليس بشيء، هذا إنما',NULL,'444',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (173,'هو عن ابن طاوس، عن أبيه مرسل» ، وحمل على مؤمل، ثم قال يحيى: «يحدث من حفظه زيادة» .
<span data-type="title" id=toc-711>(٧١٠) </span>سألت يحيى عن عمرو بن مالك النكري، فقال: «ثقة» ، قلت ليحيى: ابن عمرو بن مالك؟ فقال: «ليس بشيء» ، قلت ليحيى: النكري ممن هو؟ قال: «من نكرة، من عبد القيس» .
<span data-type="title" id=toc-712>(٧١١) </span>قلت ليحيى: روح بن مسافر؟ قال: «ليس بثقة ولا مأمون» ، قلت: لم ترك حديثه؟ قال: «لعل ابن عرعرة يزعم أنه ثقة؟!» ،',NULL,'445',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (174,'قال: لا أعلمه. ثم قال لي يحيى: «إنما زوّجه ابنة المعطل بن روح بن مسافر ابنُ سماعة» ، يعني: زوّج ابن سماعة إبراهيم.
<span data-type="title" id=toc-713>(٧١٢) </span>قلت ليحيى: حماد بن زيد، عن المغيرة؟ قال: «هذا المغيرة ختن مالك بن دينار» .
<span data-type="title" id=toc-714>(٧١٣) </span>قال ابن الغلابي ليحيى: يزيد بن أسد جد خالد بن عبد الله القسري، له صحبة؟ قال: «قالوا: (لا) » ، قال: من يقول ذلك؛ ولده؟ قال يحيى: «نعم» .
<span data-type="title" id=toc-715>(٧١٤) </span>قال يحيى وأنا أسمع: «وطلحة بن مصرف، عن أبيه، عن جده، ليس له صحبة، قال ولد طلحة بن مصرف: (ما أدرك جدنا النبي -صلى الله عليه وسلم-) » .',NULL,'446',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (175,'<span data-type="title" id=toc-716>(٧١٥) </span>قلت ليحيى: محمد بن الحسن بن آتش الصنعاني، قال: «لم أكتب عنه شيئًا، ولا عن إبراهيم بن خالد المؤذن» .
<span data-type="title" id=toc-717>(٧١٦) </span>سألت يحيى عن إسماعيل بن عبد الكريم الصنعاني أبي هشام، فقال: «لم يكن به بأس» .',NULL,'447',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (176,'الجزء الخامس
بسم الله الرحمن الرحيم، وصلى الله على سيدنا محمد. وبالإسناد قال:
<span data-type="title" id=toc-718>(٧١٧) </span>سأل ابن الغلابي يحيى بن معين وأنا أسمع عن رباح بن خالد الكوفي، فقال: «لم يكن به بأس، كان يتشيع، وكان من أصحاب أبي حنيفة» ، أي: يقول بقولهم.
<span data-type="title" id=toc-719>(٧١٨) </span>قلت ليحيى: يزيد بن أبي حكيم العدني؟ قال: «كان ليس به بأس، لم أكتب عنه شيئًا» .
<span data-type="title" id=toc-720>(٧١٩) </span>وسألت يحيى عن قريش بن أنس، فقال: «ليس به بأس» . قال ابن الجنيد: وقد كتب عنه يحيى ين معين.
<span data-type="title" id=toc-721>(٧٢٠) </span>وقلت ليحيى: قريش بن حيان؟ قال: «ثقة» .
<span data-type="title" id=toc-722>(٧٢١) </span>قال ابن الغلابي ليحيى بن معين وأنا أسمع: روى شعبة عن بريد بن أبي بردة؟ قال: «نعم» .
<span data-type="title" id=toc-723>(٧٢٢) </span>قال: ومحمد بن عمرو، سمع من سالم بن عبد الله؟ قال: «نعم» .',NULL,'449',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (177,'<span data-type="title" id=toc-724>(٧٢٣) </span>قلت ليحيى بن معين: قراد أبو نوح ثقة؟ قال: «لم يكن به بأس» .
<span data-type="title" id=toc-725>(٧٢٤) </span>قلت ليحيى: عبد الله بن صالح العجلي؟ قال: «ما أرى كان به بأس» .
<span data-type="title" id=toc-726>(٧٢٥) </span>قلت ليحيى: أحوص بن جواب؟ قال: «ما أرى كان به بأس» .
<span data-type="title" id=toc-727>(٧٢٦) </span>قلت ليحيى: أحمد بن عمر الجلاب الوكيعي بباب المقير؟ قال: «ما أرى به بأسًا» .
<span data-type="title" id=toc-728>(٧٢٧) </span>سمعت يحيى بن معين يقول -وسئل عن المسعودي، عن الضحاك بن قيس-، قال يحيى: «هذا الضحاك بن قيس رجل كان على روابط السند» .',NULL,'450',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (178,'قلت ليحيى: وأيش الحديث؟ قال: «عن محمد بن المنتشر، أن قتيلاً وجد بين وداعة -شيء في القسامة-» .
قلت ليحيى: فتعرف أحدًا روى عن الضحاك بن قيس هذا غير المسعودي؟ قال: «لا أعلمه» .
<span data-type="title" id=toc-729>(٧٢٨) </span>قال يحيى: «والضحاك بن قيس السكوني، روى عنه أبو أبي بدر، قال: (عطس رجل عند ابن عمر) » .
<span data-type="title" id=toc-730>(٧٢٩) </span>قلت ليحيى: نهشل؟ قال: «نهشل الضبي، كوفي ثقة» .',NULL,'451',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (179,'قلت: من روى عنه؟ قال: «سفيان -يعني: الثوري-، ومحمد بن فضيل، وجرير بن عبد الحميد» .
<span data-type="title" id=toc-731>(٧٣٠) </span>قلت: فنهشل الذي يحدث عن الضحاك بن مزاحم، عن ابن عباس، تعرفه؟ قال: «لا» .
<span data-type="title" id=toc-732>(٧٣١) </span>قلت ليحيى: زياد أبو عمر الصفار؟ قال: «بصري، زياد أبو عمر العابد ثقة» .
<span data-type="title" id=toc-733>(٧٣٢) </span>قلت ليحيى: عمر بن أبي زائدة؟ قال: «هو أخو زكريا بن أبي زائدة، صدوق لا بأس به» .
<span data-type="title" id=toc-734>(٧٣٣) </span>وسمعت ابن الغلابي يقول: (مسلم القري، من قرة عبد القيس) .
<span data-type="title" id=toc-735>(٧٣٤) </span>سمعت يحيى بن معين يقول: «مات رباح بن زيد قبل أن أدخل أنا اليمن، ومحمد بن ثور» .',NULL,'452',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (180,'قلت ليحيى: أيهما أعلى؟ قال: «كل ثقة؛ رباح ومحمد بن ثور وهشام وعبد الرزاق» .
قلت ليحيى: أظن محمد بن ثور قليل الحديث؟ قال: «لا، كان كثير الحديث، وكان رباح بن زيد يصحف ويخطئ، كأنه لم يكن صاحب حديث، إلا أنه لا بأس به، رجل صدق» .
<span data-type="title" id=toc-736>(٧٣٥) </span>سألت يحيى عن أبي عبيدة الناجي، قال: «ليس بشيء» ، قلت: هو الذي روى مواعظ الحسن.
<span data-type="title" id=toc-737>(٧٣٦) </span>سألت يحيى عن نصير بن أبي الاشعث، فقال: «كوفي» ، قلت: كيف حديثه؟ قال: «لا بأس به» .
<span data-type="title" id=toc-738>(٧٣٧) </span>حدثنا يحيى بن معين، عن أبي نعيم، عن نصير بن أبي الأشعث، عن أبي الزبير، عن جابر قال: قال رسول الله -صلى الله عليه وسلم-: «تداووا من',NULL,'453',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (181,'العذرة بالقسط والورس» .
<span data-type="title" id=toc-739>(٧٣٨) </span>سمعت يحيى بن معين يقول: «محمد بن أبي عائشة كوفي، ليس هو أخا موسى بن أبي عائشة، ما أعلم أحدًا روى عنه إلا شعبة» .
<span data-type="title" id=toc-740>(٧٣٩) </span>وسمعت يحيى بن معين يقول: «النعمان بن راشد جزري، وإسحاق بن راشد جزري، ليس بأخيه، ولا بينهما قرابة ولا رحم» .',NULL,'454',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (182,'قلت ليحيى: أيهما أعجب إليك؟ قال: «ليس هما في الزهري بذاك» ، قلت: ففي غير الزهري؟ قال: «ليس بإسحاق بأس» .
<span data-type="title" id=toc-741>(٧٤٠) </span>حدثنا إبراهيم، حدثنا أبو خيثمة، حدثنا فضيل بن عبد الوهاب، حدثنا حماد بن زيد، عن عثمان بن غياث، عن أبي عثمان، قال: (الذي أراد إبراهيم -عليه السلام- يذبحه: إسحاق) . قال لي يحيى: «هذا خطأ، إنما هو عثمان بن غياث، عن عبد الله بن شقيق، قال: (الذبيح إسحاق) » . قلت ليحيى: من',NULL,'455',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (183,'حدثكم؟ قال: حدثنا أحمد بن إسحاق، حدثنا خالد بن الحارث، عن عثمان بن غياث، عن عبد الله بن شقيق، قال: (الذبيح إسحاق) .
قلت ليحيى بن معين: مبارك، عن الحسن، عن الأحنف، عن العباس، قال: قال: (الذبيح إسحاق) ، ح، وحماد بن سلمة، عن علي بن زيد، عن الحسن، عن الأحنف، عن النبي -صلى الله عليه وسلم- قال: «الذبيح إسحاق» ، قلت ليحيى: أيهما أصح عندك؟ قال: «لا تبالي أيهما كان» ، كأنه ضعفهما جميعًا.
<span data-type="title" id=toc-742>(٧٤١) </span>قلت ليحيى: مبارك مثل علي بن زيد؟ قال: «ما أقربه منه» .
<span data-type="title" id=toc-743>(٧٤٢) </span>سمعت يحيى بن معين يقول: «روى شعبة، عن جابر الجعفي، وعن الحسن بن عمارة -إلا أنه لا يسميه، يقول: (عن رجل) -، عن الحكم، عن مجاهد، وروى عن قيس بن الربيع» .
<span data-type="title" id=toc-744>(٧٤٣) </span>قال رجل ليحيى بن معين وأنا أسمع: علي بن زيد اختلط؟ قال: «ما اختلط علي بن زيد قط» ، ثم قال يحيى: «حماد بن سلمة أروى الناس عن علي بن زيد» .
<span data-type="title" id=toc-745>(٧٤٤) </span>قلت ليحيى: حدثنا عفان، عن مبارك، عن الحسن',NULL,'456',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (184,'ابن عبد الله بن قدامة، عن السعدي، أن النبي -صلى الله عليه وسلم- أتى على وادي ثمود. قلت: وحماد بن سلمة يقول عن علي بن زيد، يقول: عن أبي ذر، أيهما أصح عندك؟ قال: «حديث أبي ذر أشبه» .
<span data-type="title" id=toc-746>(٧٤٥) </span>سمعت يحيى بن معين يقول: «روى عن سليمان الأسود الناجي: سعيد بن أبي عروبة ووهيب وهشيم» ، قيل ليحيى: حديث الصلاة رواه هشيم عنه؟ قال: «لا» .
<span data-type="title" id=toc-747>(٧٤٦) </span>سمعت يحيى بن معين يقول: «العوام عن سليمان بن',NULL,'457',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (185,'أبي سليمان، روى عنه أيضًا عوف، وروى عنه قتادة: (يكون أمراء يغشاهم غواش) » .
<span data-type="title" id=toc-748>(٧٤٧) </span>سمعت يحيى بن معين يقول: «السكن بن نافع بصري ثقة، يحدث عن عمران بن حدير وهشام بن حسان وصالح بن أبي الاخضر» ، فقال رجل ليحيى: كتبت عنه شيئًا؟ قال: «لا» .
<span data-type="title" id=toc-749>(٧٤٨) </span>قلت ليحيى بن معين: يحيى بن عبيد البهراني؟ فقال: «كوفي، روى عنه شعبة والحجاج بن أرطاة والأعمش» .
قلت ليحيى: روى عنه ابن عون شيئًا؟ قال: «لا أعلمه» .',NULL,'458',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (186,'<span data-type="title" id=toc-750>(٧٤٩) </span>سئل يحيى بن معين وأنا أسمع عن عبيد بن يعيش، فقال: «ثقة» .
<span data-type="title" id=toc-751>(٧٥٠) </span>وعن حسين بن عبد الأول، فقال: «لم يكن بثقة» .
<span data-type="title" id=toc-752>(٧٥١) </span>قلت ليحيى بن معين: عتي بن ضمرة السلولي، روى عنه أحد غير الحسن؟ قال: «ما سمعت» .
<span data-type="title" id=toc-753>(٧٥٢) </span>وسئل يحيى بن معين وأنا أسمع عن حديث أبي معاوية، عن إسماعيل بن أبي خالد، عن قيس، قال: قالت عائشة -رضي الله عنها-: (وددت أني ثكلت عشرة) ، فقال يحيى: «هذا خطأ من أبي معاوية، ليس هو عن قيس، إنما هو إسماعيل عن رجل آخر غير قيس» .
<span data-type="title" id=toc-754>(٧٥٣) </span>سأل ابن أبي غالب يحيى بن معين وأنا شاهد عن المغيرة بن مسلم القسملي، فقال يحيى: «ما أنكر حديثه عن أبي الزبير» .
قلت ليحيى: هو أخو عبد العزيز بن مسلم القسملي؟ قال: «نعم» ، قال ابن أبي غالب ليحيى: هما جميعًا من أهل خراسان؟ قال: «نعم» .',NULL,'459',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (187,'<span data-type="title" id=toc-755>(٧٥٤) </span>قال ابن أبي غالب ليحيى وأنا أسمع: نبيح العنزي، روى عنه أحد غير الأسود بن قيس؟ قال: «ما سمعت» .
<span data-type="title" id=toc-756>(٧٥٥) </span>وقال ابن أبي غالب ليحيى: أبو الزعراء روى عنه أحد غير سلمة بن كهيل؟ قال: «ما سمعت به» .
<span data-type="title" id=toc-757>(٧٥٦) </span>سمعت يحيى بن معين يقول: «أبو الجمل يمامي يحدث عن عطاء، ليس بشيء، اسم أبي الجمل: أيوب بن جابر، أخو محمد بن جابر» .
<span data-type="title" id=toc-758>(٧٥٧) </span>سمعت يحيى بن معين يقول في حديث عبد الرزاق، عن معمر، عن الزهري، عن عمر بن سعد، عن أبيه: (من يرد هوان قريش) ، قال: «هذا خطأ، ما روى الزهري عن عمر بن سعد شيئًا قط، وهذا أيضًا معمر يقول: أخبرت عن الزهري» .',NULL,'460',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (188,'<span data-type="title" id=toc-759>(٧٥٨) </span>قال رجل ليحيى بن معين وأنا أسمع: عمر بن المنكدر ومحمد بن المنكدر وأبو بكر بن المنكدر إخوة؟ قال: «نعم» .
<span data-type="title" id=toc-760>(٧٥٩) </span>وشهدت يحيى بن معين وقال له عباس الدوري: معاذ بن هشام يقول: رفاعة بن عرادة، وغيره يقول: رفاعة بن عرابة الجهني؟ فقال يحيى: «الصواب: رفاعة بن عرابة» .
<span data-type="title" id=toc-761>(٧٦٠) </span>قلت ليحيى بن معين: حماد بن سلمة دخل الكوفة؟ قال: «لا أعلمه دخل الكوفة» ، قلت: فمن أين لقي هؤلاء؟ قال: «قدم عليهم، عاصم وحماد بن أبي سليمان والحجاج بن أرطاة» ، قلت: فأين لقي سماك بن حرب؟ قال: «عسى لقيه في بعض المواضع، ولو كان دخل الكوفة لأجاد عنهم» .
<span data-type="title" id=toc-762>(٧٦١) </span>قال رجل ليحيى بن معين وأنا أسمع: أيما أعجب إليك: إسماعيل بن عياش، أو فرج بن فضالة؟ قال: «لا، بل إسماعيل» ، ثم قال: «فرج ضعيف الحديث، وأيش عند فرج؟!» .
<span data-type="title" id=toc-763>(٧٦٢) </span>قال يحيى بن معين وأنا أسمع: «محمد بن جعفر',NULL,'461',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (189,'الوركاني شيخ صدق، لا بأس به، إني قد كتبت عنه» .
<span data-type="title" id=toc-764>(٧٦٣) </span>سألت يحيى عن زيد بن أبي الزرقاء، فقال: «ليس به بأس، كان عنده جامع سفيان، قد رأيته بمكة» ، قلت ليحيى: كتبت عنه شيئًا؟ قال: «لا» .
<span data-type="title" id=toc-765>(٧٦٤) </span>سئل يحيى وأنا أسمع عن جويرية بن بشير، كيف حديثه؟ قال: «ما كان به بأس» .
<span data-type="title" id=toc-766>(٧٦٥) </span>سألت يحيى عن الحكم بن عبد الله الرعيني، فقال: «ليس بشيء» .
<span data-type="title" id=toc-767>(٧٦٦) </span>قلت ليحيى: مندل وحبان جميعًا سواء؟ قال: «سواء» ، أي: ضعيفان.
<span data-type="title" id=toc-768>(٧٦٧) </span>سألت يحيى بن معين عن سليمان بن كثير، فقال: «لم يكن به بأس» .',NULL,'462',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (190,'قلت: فأخوه محمد بن كثير؟ قال: «لم يكن يستأهل أن يكتب عنه» .
<span data-type="title" id=toc-769>(٧٦٨) </span>قلت ليحيى بن معين: حديث يحيى بن أبي كثير، عن عبد الله بن أبي قتادة، عن أبيه، عن -النبي صلى الله عليه وسلم- في الصلاة على الميت، ويقول يحيى أيضًا في الصلاة عن أبي إبراهيم الأنصاري، عن أبيه، فأي الحديثين أصح؟ قال: «قد رواهما جميعًا» ، قلت يقولون: إن أبا إبراهيم هو عبد الله بن أبي قتادة؟ قال: «لا» .
<span data-type="title" id=toc-770>(٧٦٩) </span>قلت ليحيى: يوسف بن ميمون الصباغ؟ قال: «هو بصري يروي عن الكوفيين، ليس بشيء» .
<span data-type="title" id=toc-771>(٧٧٠) </span>قلت ليحيى: سلمة بن بخت؟ قال: «مدني ليس به بأس، روى عنه القعنبي وغيره» .
<span data-type="title" id=toc-772>(٧٧١) </span>قال يحيى وأنا أسمع: «الجعيد ثقة، الذي روى عنه مكي بن إبراهيم» .',NULL,'463',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (191,'<span data-type="title" id=toc-773>(٧٧٢) </span>سمعت يحيى يقول: «يزيد بن عياض بن جعدبة ليس بشيء» .
<span data-type="title" id=toc-774>(٧٧٣) </span>سئل يحيى وأنا أسمع عن حديث عمر: «من كذب علي» ، فقال: «الدجين مديني، كان يقال له العرني، حدث عنه ابن المبارك، شيخ ضعيف الحديث ليس بشيء» .
<span data-type="title" id=toc-775>(٧٧٤) </span>سألت يحيى عن إبراهيم بن المختار الرازي، قال: «قد رأيته ببغداد دهرًا من الدهور» ، قلت: كتبت عنه شيئًا؟ قال: «لا» ، قلت: فكيف حديثه؟ قال: «ليس بذاك» .
<span data-type="title" id=toc-776>(٧٧٥) </span>سئل يحيى وأنا أسمع عن بكر بن مضر، فقال: «ثقة» .',NULL,'464',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (192,'<span data-type="title" id=toc-777>(٧٧٦) </span>سألت يحيى عن بشير بن سلمان، فقال: «ثقة كوفي، الذي روى عن سيار، وليس هو سيار أبي الحكم؛ هو سيار أبو حمزة» .
<span data-type="title" id=toc-778>(٧٧٧) </span>سألت يحيى عن أسباط بن نصر، فقال: «ثقة» .
<span data-type="title" id=toc-779>(٧٧٨) </span>قلت: أسباط بن محمد؟ قال: «ثقة» .
<span data-type="title" id=toc-780>(٧٧٩) </span>سمعت يحيى بن معين يقول: «هانئ بن عثمان كوفي، روى عنه عبد الله بن داود وغيره حديث التسبيح بالأنامل (فإنها مستنطقات) » .
<span data-type="title" id=toc-781>(٧٨٠) </span>قال يحيى وأنا أسمع: «كثير بن أبي كثير الذي روى عنه مروان الفزاري كوفي، قد رأى جرير بن عبد الله» .',NULL,'465',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (193,'<span data-type="title" id=toc-782>(٧٨١) </span>قال يحيى وأنا أسمع: «هشام بن عروة قد رأى عبد الله بن عمر، ورأى جابر بن عبد الله» .
<span data-type="title" id=toc-783>(٧٨٢) </span>قلت ليحيى بن معين: كيف توتر؟ تسلم في الركعتين؟ قال: «لا، أنا أوتر بثلاث، أسلم في آخرها» .
<span data-type="title" id=toc-784>(٧٨٣) </span>قلت: فما تقول في الصلاة خلف القدرية والجهمية؟ قال: «إن كان له موضع غير ذلك المسجد فلا يصلي خلفه» ، قلت: فإن صلى؟ قال: «إن صلى وهو يعلم فليعد الصلاة» .
<span data-type="title" id=toc-785>(٧٨٤) </span>قال يحيى وأنا أسمع: «فضيل بن غزوان، عن أبي حازم: عبد الرحمن بن حازم» .',NULL,'466',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (194,'<span data-type="title" id=toc-786>(٧٨٥) </span>قال يحيى وأنا أسمع: «حنظلة السدوسي بصري ضعيف،
<span data-type="title" id=toc-787>(٧٨٦) </span>حنظلة بن أبي سفيان مكي ثقة،
<span data-type="title" id=toc-788>(٧٨٧) </span>حنظلة التيمي القاص كوفي، روى عنه وكيع وأبو معاوية وهؤلاء» ، قلت ليحيى: كيف حديثه؟ قال: «ليس بذاك» .
<span data-type="title" id=toc-789>(٧٨٨) </span>قلت ليحيى: موسى بن مسلم الصغير، هو الطحان؟ قال: «نعم» ، قلت: ثقة؟ قال: «ثقة» .
<span data-type="title" id=toc-790>(٧٨٩) </span>سمعت يحيى بن معين يقول: «حجاج بن أرطاة، قال: سمعت الشعبي. والحجاج، قال: حدثني أبو رشدين كريب» .
<span data-type="title" id=toc-791>(٧٩٠) </span>وأملى علينا يحيى بن معين من كتاب دواء ينفع للمرة والبلغم، ويهضم الطعام، ويصفي اللون: فلفل وزنجبيل وكراويا وكمون',NULL,'467',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (195,'ورازيانج شامي وأيهل وصعتر فارسي وملح أندراني وأصل الكبر وجوز نوا ودار صيني ونانخاه، من كل واحد وزن درهم، وسقمونيا وزن ستة الدراهم، يدق ويعجن بالماء، ويحبب، والشربة منه وزن درهمين بماء فاتر، نافع -بإذن الله-.
<span data-type="title" id=toc-792>(٧٩١) </span>وأملى علينا يحيى بن معين نسبة أبي جعفر الخطمي: «أبو جعفر بن يزيد بن عمير بن حبيب بن حباشة ابن أخي أنس بن صرمة العدوي» .
<span data-type="title" id=toc-793>(٧٩٢) </span>سألت يحيى بن معين عن يحيى بن العلاء الرازي، فقال: «ليس بشيء» .
<span data-type="title" id=toc-794>(٧٩٣) </span>قلت: عقبة الأصم؟ قال: «ليس بشيء» .',NULL,'468',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (196,'<span data-type="title" id=toc-795>(٧٩٤) </span>قلت: فضيل بن سليمان النميري؟ قال: «ليس بشيء» .
<span data-type="title" id=toc-796>(٧٩٥) </span>قلت: كثير بن عبد الله بن عمرو بن عوف المزني؟ قال: «ليس بشيء» .
<span data-type="title" id=toc-797>(٧٩٦) </span>قلت ليحيى: الحجاج بن فروخ؟ قال: «لا أعرفه» ، قلت: يروي عن العوام بن حوشب، عن ابن أبي أوفى، قال: كان النبي -صلى الله عليه وسلم- إذا قال بلال: (قد قامت الصلاة) نهض، فكبر. قال: «لا أعرفه، من حدثكم عنه؟» ، قلت: عاصم بن علي، قال: «عاصم بن علي ليس بشيء» .
<span data-type="title" id=toc-798>(٧٩٧) </span>سمعت يحيى بن معين يقول: «كان أبو نعيم إذا ذكر إنسانًا، فقال: (هو جيد) ، وأثنى عليه؛ فهو شيعي، وإذا قال: (فلان كان مرجئًا) ؛ فاعلم أنه صاحب سنة لا بأس به» .',NULL,'469',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (197,'<span data-type="title" id=toc-799>(٧٩٨) </span>سأل رجل يحيى وأنا أسمع: ثوير بن أبي فاختة؟ فقال: «ضعيف» ، قال: يقوم مقام ليث ويزيد بن أبي زياد؟ قال: «ما أشبهه» ، قال: هو مثل جابر؟ قال: «هو ضعيف» .
<span data-type="title" id=toc-800>(٧٩٩) </span>سألت يحيى عن عبيد بن القاسم، الذي حدثنا عنه بحديث جرير عن النبي -صلى الله عليه وسلم-: {وما كان ربك ليهلك القرى بظلم وأهلها مصلحون} ، فقال: «كذاب» .
<span data-type="title" id=toc-801>(٨٠٠) </span>حدثنا خليفة بن خياط شباب العصفري، حدثنا عبيد بن القاسم، حدثنا إسماعيل بن أبي خالد، عن قيس، عن جرير، عن النبي -صلى الله عليه وسلم- في قوله -عز وجل-: {وما كان ربك ليهلك القرى بظلم وأهلها مصلحون} ، قال: «وأهلها ينصف بعضهم بعضًا» .
<span data-type="title" id=toc-802>(٨٠١) </span>سألت يحيى عن الحكم بن فضيل الواسطي؟ فقال: «لم يكن به بأس» .
<span data-type="title" id=toc-803>(٨٠٢) </span>قلت: عبد الحكيم بن منصور الواسطي الخزاعي؟ قال: «ليس بشيء» .',NULL,'470',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (198,'<span data-type="title" id=toc-804>(٨٠٣) </span>قال لي يحيى بن معين: «عبيد بن إسحاق العطار كذاب، وكان صديقًا لي» .
<span data-type="title" id=toc-805>(٨٠٤) </span>سمعت يحيى بن معين يقول: «موسى بن مسلم الصغير هو الطحان، كان ثقة» .
<span data-type="title" id=toc-806>(٨٠٥) </span>وسئل يحيى بن معين وأنا أسمع عن أبي حزرة يعقوب بن مجاهد، فقال: «مدني ليس به بأس» .
<span data-type="title" id=toc-807>(٨٠٦) </span>سمعت يحيى بن معين يقول: «أبو العباس السندي يكذب» .
<span data-type="title" id=toc-808>(٨٠٧) </span>قلت ليحيى بن معين: بلغني أنك كتبت عن عبد المنعم الذي كان بمصر؟ فقال: «أتيته، فأخرج إلينا أحاديث أبي مودود، نحو مائتي حديث كذب» ، قال: «فقلت له: يا شيخ، أنت سمعت هذه من أبي مودود؟ قال: (نعم، ورأيت شيخًا له هيبة مدني) » ، قال: «فقلت له: اتق الله، فإن هذه كذب، وقمت ولم أكتب عنه شيئًا» .
<span data-type="title" id=toc-809>(٨٠٨) </span>سمعت يحيى بن معين يقول: «عمر بن راشد اليمامي ليس بشيء،',NULL,'471',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (199,'<span data-type="title" id=toc-810>(٨٠٩) </span>محمد بن راشد ليس به بأس» .
<span data-type="title" id=toc-811>(٨١٠) </span>قال رجل ليحيى بن معين وأنا أسمع: الزنجي بن خالد ثقة؟ قال: «ليس بذاك القوي» .
<span data-type="title" id=toc-812>(٨١١) </span>وسأل رجل يحيى بن معين وأنا أسمع عن مندل، فقال: «ليس بذاك القوي الشديد» ، فقال: ابن فضيل مثل مندل؟ فقال يحيى: «لو كان ابن فضيل مثل مندل كان قد هلك» ، قال: مندل دونه؟ قال: «نعم، دونه ودون جيرته أولئك البقالين» .
<span data-type="title" id=toc-813>(٨١٢) </span>وسمعت يحيى بن معين يقول: «زيد بن الحباب ليس به بأس،
<span data-type="title" id=toc-814>(٨١٣) </span>وعمر بن حفص بن غياث ليس به بأس» .
<span data-type="title" id=toc-815>(٨١٤) </span>قلت ليحيى بن معين: إسحاق بن أبي طلحة في حديثه شيء؟ قال: «ليس به بأس» ، قلت: كان ولي صدقات اليمامة فخبط وعمل، قال: «قد كان واليًا» ، قال ابن الغلابي: (كان خرج إلى اليمامة في زمن عمر بن عبد العزيز مع أبي الرجال أو ابن أبي الرجال، فبقي ثم حتى أدرك سلطان بني العباس) .',NULL,'472',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (200,'<span data-type="title" id=toc-816>(٨١٥) </span>سمعت يحيى بن معين يقول: «سلمة بن وهرام يمامي» ، قلت: ثقة؟ قال: «ثقة» .
<span data-type="title" id=toc-817>(٨١٦) </span>قال لنا هارون بن مرة ويحيى بن معين يسمع: (جاء كتاب البغدادين إلى أبي المليح وأنا حاضر يسألونه عن محمد بن زياد الطحان، فقال: جاءنا محمد بن زياد الطحان الأعور بعدما مات ميمون بن مهران) .
<span data-type="title" id=toc-818>(٨١٧) </span>سألت يحيى عن فليح بن سليمان، فقال: «ضعيف الحديث» .
<span data-type="title" id=toc-819>(٨١٨) </span>قلت: فأخوه عبد الحميد؟ قال: «ذاك لا يحل لأحد أن يروي عنه، كان لعنة» .
<span data-type="title" id=toc-820>(٨١٩) </span>قال رجل ليحيى بن معين وأنا أسمع: أبو عبيدة بن عبد الله سمع من أبيه شيئًا؟ قال يحيى: «قالوا: (لا، ولا عبد الرحمن بن عبد الله) » .',NULL,'473',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (201,'<span data-type="title" id=toc-821>(٨٢٠) </span>قال رجل ليحيى بن معين وأنا أسمع: كان شبابة بن سوار يرى رأي الإرجاء؟ قال «ما أشبهه» .
<span data-type="title" id=toc-822>(٨٢١) </span>سئل يحيى وأنا أسمع عن المغيرة بن مسلم؟ فقال: «ثقة، هو أخو عبد العزيز بن مسلم القسملي، ينزل القسامل، ثقة ليس به بأس، روى عنه وكيع وشبابة وغيرهما» .
<span data-type="title" id=toc-823>(٨٢٢) </span>سألت يحيى عن عبد الحميد بن بهرام، فقال: «ليس به بأس» .
<span data-type="title" id=toc-824>(٨٢٣) </span>سألت يحيى عن معرف بن واصل، فقال: «معرف ثقة،
<span data-type="title" id=toc-825>(٨٢٤) </span>ولا أعرف مطرف بن واصل، أعرف مطرف بن معقل، بصري، الشقري، ثقة، روى عنه وكيع، وروى عنه البصريون» .
<span data-type="title" id=toc-826>(٨٢٥) </span>قال يحيى وأنا أسمع: «أبو إسرائيل إسماعيل بن أبي إسحاق، ليس به بأس» .',NULL,'474',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (202,'<span data-type="title" id=toc-827>(٨٢٦) </span>سألت يحيى عن سلام بن سلم الطويل المدائني، فقال: «ليس بشيء» .
<span data-type="title" id=toc-828>(٨٢٧) </span>وسألته عن إسماعيل بن زكريا الخلقاني ويكنى أبا زياد، فقال: «صالح» .
<span data-type="title" id=toc-829>(٨٢٨) </span>قال يحيى بن معين وأنا أسمع: «سفيان بن عيينة، عن إسرائيل بن أبي موسى، بصري، روى عن الحسن ومحمد بن سيرين، ثقة» .
<span data-type="title" id=toc-830>(٨٢٩) </span>قلت ليحيى بن معين: حديث حرب بن شداد؟ فقال: «صالح» .
<span data-type="title" id=toc-831>(٨٣٠) </span>سئل يحيى وأنا أسمع عن محمد بن مهزم الشعاب؟ فقال: «ثقة ليس به بأس، محمد بن مهزم الرمام، روى عنه وكيع ويزيد بن هارون» .
حدثنا ابن الجنيد، حدثنا عنه أبو سلمة الحوضي.',NULL,'475',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (203,'<span data-type="title" id=toc-832>(٨٣١) </span>سئل يحيى عن الحارث بن وجيه، فقال: «بصري ليس بشيء» .
<span data-type="title" id=toc-833>(٨٣٢) </span>وسئل يحيى وأنا أسمع عن عبد الله بن عثمان بن خثيم، فقال: «ليس به بأس» .
<span data-type="title" id=toc-834>(٨٣٣) </span>سألت يحيى عن معاوية بن عمار الدهني، فقال: «صالح ليس بمتروك الحديث» .
<span data-type="title" id=toc-835>(٨٣٤) </span>قلت ليحيى: حدثنا الحماني، عن الفضل بن شهاب، عن ابن جريج، عن عطاء، عن ابن عباس -رضي الله عنهما- قال: (من لبس نعلاً صفراء لم يزل ينظر في سرور) ، ثم قرأ: {إنها بقرة صفراء فاقع لونها تسر الناظرين} ، فقال يحيى: «هذا كذاب» .',NULL,'476',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (204,'<span data-type="title" id=toc-836>(٨٣٥) </span>وقال يحيى بن معين وأنا أسمع: «أبو صخر حميد بن زياد الخراط مديني، ليس به بأس» .
<span data-type="title" id=toc-837>(٨٣٦) </span>سئل يحيى بن معين وأنا أسمع عن حماد بن أبي حميد، فقال: «ليس بشيء، وأخوه محمد بن أبي حميد ليس بشيء، مدنيان» .',NULL,'477',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (205,'<span data-type="title" id=toc-838>(٨٣٧) </span>قال رجل ليحيى وأنا أسمع: روى جرير، عن حبيب بن أبي عمرة والشيباني أحاديث، كأنه يقول: منكرة، فقال يحيى: «حبيب بن أبي عمرة والشيباني ثقتان، لعل هذا من جرير» ، قال: وروى عن عطاء بن السائب؟ فقال يحيى: «إن جريرًا وابن فضيل وهؤلاء سمعوا من عطاء بن السائب بآخرة» ، فقلت ليحيى: كان عطاء بن السائب قد خلط؟ قال: «نعم» ، قال يحيى: «وحماد بن سلمة سمع من عطاء بن السائب قديمًا قبل الاختلاط» .',NULL,'478',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (206,'<span data-type="title" id=toc-839>(٨٣٨) </span>سألت يحيى بن معين عن صخر بن جويرية، فقال: «ثقة ليس به بأس» .
<span data-type="title" id=toc-840>(٨٣٩) </span>سألت يحيى عن مسلم بن خالد الزنجي، فقال: «ليس به بأس» ، وقال ابن الغلابي ليحيى: (ما كنت أراه إلا متروك الحديث) ، قال: «لا» .
<span data-type="title" id=toc-841>(٨٤٠) </span>قلت ليحيى: حماد بن زيد، عن الحسن، عن الضحاك بن سفيان الكلابي، قال لي رسول الله -صلى الله عليه وسلم-: «ما طعامك يا ضحاك؟» ، وحماد بن سلمة، عن علي بن زيد، عن أبي عثمان، أن النبي -صلى الله عليه وسلم- قال للضحاك، فقال لي يحيى: «حماد بن سلمة أعرف بعلي بن زيد من حماد بن زيد» .',NULL,'479',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (207,'<span data-type="title" id=toc-842>(٨٤١) </span>قال يحيى بن معين وأنا أسمع: «حنان أبو عثمان بصري،
<span data-type="title" id=toc-843>(٨٤٢) </span>وحنان بن خارجة شامي» .
<span data-type="title" id=toc-844>(٨٤٣) </span>قال يحيى بن معين وأنا أسمع: «عبد الرحمن بن أبي بكر بن أبي مليكة، أبو بكر المليكي مديني ليس بشيء» .
قال ابن الجنيد: روى عنه يزيد بن هارون. قال ابن الجنيد: وحدثنا عنه أبو سلمة. هذا من قول إبراهيم بن الجنيد.
<span data-type="title" id=toc-845>(٨٤٤) </span>سألت يحيى عن الحسن بن ذكوان، قلت: بصري؟ قال: «نعم» ، قلت: روى عن الحسن ومحمد؟ قال: «نعم» ، قلت: ثقة؟ قال: «قد روى عنه يحيى بن سعيد القطان» ، ثم قال لي يحيى بن معين: «زعموا أنه كان قدريًّا، وكان الخفاف يحدث عنه» ، يعني: الحسن بن ذكوان.
<span data-type="title" id=toc-846>(٨٤٥) </span>سألت يحيى بن معين عن يحيى الجابر، كيف حديثه؟ فقال: «يحيى بن عبد الله الجابر ليس حديثه بشيء» ، قلت ليحيى: هو كوفي؟ قال: «نعم» ، يعني: يحيى الجابر.',NULL,'480',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (208,'<span data-type="title" id=toc-847>(٨٤٦) </span>سألت يحيى بن معين عن حميد الأعرج، فقال: «حميد بن قيس الأعرج المكي ثقة» ، قلت: هو أخو عمر بن قيس؟ قال: «نعم» ،
<span data-type="title" id=toc-848>(٨٤٧) </span>قال: «وعمر بن قيس ليس بشيء» .
<span data-type="title" id=toc-849>(٨٤٨) </span>قال يحيى وأنا أسمع: «موسى بن محمد بن إبراهيم ليس بشيء حديثه، وروى هشام بن عروة عن أبيه محمد بن إبراهيم» .
<span data-type="title" id=toc-850>(٨٤٩) </span>قال يحيى بن معين وأنا أسمع: «عجلان أبو محمد بن عجلان مولى فاطمة بنت عتبة بن ربيعة، وروى ابن أبي ذئب عن عجلان مولى المشمعل» ، قال يحيى: «وروى ابن أبي ذئب أيضًا عن رجل، عن عجلان» ، يعني: أبا محمد بن عجلان.
<span data-type="title" id=toc-851>(٨٥٠) </span>سألت يحيى عن يحيى بن يعلى الأسلمي، فقال: «كان عابدًا» ، فقال رجل ليحيى: هذا أبو المحياة؟ فقال: «لا، قد سمعت أنا من أبي المحياة» .
<span data-type="title" id=toc-852>(٨٥١) </span>قال يحيى بن معين وأنا أسمع: «عثمان بن عمير أبو اليقظان ليس بذاك» ، كأنه ضعفه.',NULL,'481',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (209,'<span data-type="title" id=toc-853>(٨٥٢) </span>قلت ليحيى: فعثمان بن أبي زرعة؟ قال: «ثقة، هو عثمان بن المغيرة» .
<span data-type="title" id=toc-854>(٨٥٣) </span>قلت ليحيى: أبو المهزم كيف حديثه؟ قال: «ليس بشيء» ، قلت: ما اسمه؟ قال: «يزيد بن سفيان» .
<span data-type="title" id=toc-855>(٨٥٤) </span>سئل يحيى وأنا أسمع: كيف حديث موسى بن خلف؟ قال: «ضعيف» .
<span data-type="title" id=toc-856>(٨٥٥) </span>قال رجل ليحيى وأنا أسمع: عثمان بن إبراهيم؟ قال: «صالح» .
<span data-type="title" id=toc-857>(٨٥٦) </span>قال: عمر بن عامر؟ قال: «ليس به بأس، كان قاضي البصرة، روى عنه عبد الوارث التنوري وسعيد بن أبي عروبة» ، قلت: وعباد بن العوام؟ قال: «نعم» ، قلت: هو بصري؟ قال: «نعم» ، يعني: عمر بن عامر.
<span data-type="title" id=toc-858>(٨٥٧) </span>سأل رجل يحيى وأنا أسمع عن جابر الجعفي، فقال: «ليس هو عندهم بشيء» .',NULL,'482',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (210,'<span data-type="title" id=toc-859>(٨٥٨) </span>قال له رجل: فحجاج بن أرطاة مثله؟ قال: «لا، حجاج بن أرطاة خير منه» .
<span data-type="title" id=toc-860>(٨٥٩) </span>سأل رجل يحيى بن معين وأنا أسمع عن ليث بن أبي سليم، قال: «ليس بذاك القوي» .
<span data-type="title" id=toc-861>(٨٦٠) </span>سألت يحيى عن سالم بن أبي حفصة، قال: «ليس به بأس، كان مغليًا في الشيعة» .
<span data-type="title" id=toc-862>(٨٦١) </span>سئل يحيى وأنا أسمع عن علي بن بذيمة، قال: «ليس به بأس» .
<span data-type="title" id=toc-863>(٨٦٢) </span>سئل يحيى وأنا أسمع عن أبي نعيم النخعي عبد الرحمن بن هانئ، فقال: «ليس بثقة، كان يكذب، يروي عن سفيان أحاديث موضوعة» .
<span data-type="title" id=toc-864>(٨٦٣) </span>سئل يحيى وأنا أسمع عن أحمد بن محمد بن أيوب صاحب مغازي إبراهيم بن سعد، فقال: «لص كذاب، ما سمع هذه الكتب قط» .
<span data-type="title" id=toc-865>(٨٦٤) </span>قلت ليحيى: كيف قال أبو حازم: (إذا آخيت رجلاً فأقل مخالطته) ، فقال: «حدثنا حجاج -يعني: ابن محمد-، عن',NULL,'483',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (211,'أبي معشر، قال: قال أبو حازم: (إذا آخيت رجلاً في الله فأقل مخالطته في دنياه) .
<span data-type="title" id=toc-866>(٨٦٥) </span>سئل يحيى وأنا أسمع عن محاضر، فقال: «ما أدري، لم يكن صاحب حديث» .
<span data-type="title" id=toc-867>(٨٦٦) </span>سئل يحيى وأنا أسمع عن العلاء بن المسيب، فقال: «ثقة مأمون» .
<span data-type="title" id=toc-868>(٨٦٧) </span>سئل يحيى وأنا أسمع عن زياد البكائي، فقال: «ليس به بأس في المغازي» ، قلت ليحيى: فما روى غير المغازي؟ قال: «لا يرغبون في حديثه» ، ثم قال لي يحيى: «قال لي عبد الله بن إدريس: (باع زياد بن عبد الله البكائي شقصًا من داره وكتبها) ، يعني: كتب ابن إسحاق» ، قلت ليحيى: كتبت عنه شيئًا؟ قال: «نعم» .
قال يحيى: «كان إبراهيم بن سعد يقول: (يحدث هؤلاء النبط بمعايب أصحاب النبي -صلى الله عليه وسلم-) ، يعني: البكائي» ، قال يحيى: «كان يقرؤها على رؤوس الناس، ما يدع منها شيئًا» .
<span data-type="title" id=toc-869>(٨٦٨) </span>قلت ليحيى: سلمة بن الفضل الأبرش؟ قال: «ليس به',NULL,'484',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (212,'بأس، هو آخر من سمعها من ابن إسحاق، سمعها الكوفيون، وسمعها الحرانيون، وسمعها سلمة بالري» .
<span data-type="title" id=toc-870>(٨٦٩) </span>سأل رجل يحيى وأنا أسمع عن يونس بن خباب، فقال: «ليس بذاك، كان يشتم أصحاب النبي -صلى الله عليه وسلم-، كان يشتم عثمان، ومن شتم أصحاب النبي -صلى الله عليه وسلم- فليس بثقة» .
<span data-type="title" id=toc-871>(٨٧٠) </span>قال يحيى وأنا أسمع: «حماد بن زيد أكبر من سفيان بن عيينة، وشعبة أكبر من سفيان الثوري» ، فقال أبو عبد الرحمن بن الغلابي ويحيى حاضر يسمع: (حماد بن زيد أكبر من سفيان بن عيينة بعشر أو تسع سنين وأشهر) .
<span data-type="title" id=toc-872>(٨٧١) </span>قلت ليحيى: ترى للرجل أن يأمر في جيرانه وينهى إذا رأى منكرًا؟ قال: «له، إن أمكنه ذلك فليفعل» .
<span data-type="title" id=toc-873>(٨٧٢) </span>قال رجل ليحيى بن معين وأنا أسمع: منصور بن زاذان روى عن زاذان أبي عمر شيئًا؟ قال: «نعم» .
<span data-type="title" id=toc-874>(٨٧٣) </span>سألت يحيى بن معين عن محمد بن الزبرقان الأهوازي ويكنى أبا هشام، فقال: «ليس به بأس» .',NULL,'485',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (213,'<span data-type="title" id=toc-875>(٨٧٤) </span>سألت يحيى عن إسماعيل بن رافع، فقال: «ضعيف الحديث» ، فقلت: هو مثل إسحاق بن أبي فروة في الضعف؟ فقال: «إسحاق ضعيف وإسماعيل بن رافع ضعيف» .
<span data-type="title" id=toc-876>(٨٧٥) </span>سألت يحيى عن الربيع بن سعد الذي روى عن ابن سابط، عن جابر، عن النبي -صلى الله عليه وسلم-: «حدثوا عن بني إسرائيل» ، فقال: «الربيع بن سعد كوفي ثقة» .
<span data-type="title" id=toc-877>(٨٧٦) </span>سألت يحيى عن أبي رجاء الهروي عبد الله بن واقد، فقال: «ليس به بأس» .
<span data-type="title" id=toc-878>(٨٧٧) </span>سألت يحيى عن غالب بن نجيح، قلت: حدثنا عنه عثمان بن زفر، فقال: «ثقة، روى عنه جرير بن عبد الحميد',NULL,'486',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (214,'وأبو أحمد الزبيري والكوفيون، هو كوفي» .
<span data-type="title" id=toc-879>(٨٧٨) </span>قلت ليحيى: حدثنا أحمد بن يونس، عن أبي زبيد، عن سفيان، عن طلحة المديني، قال: (شهدت ابن عباس وصلى على جنازة، فكبر عليها أربعًا) ، فقال لي يحيى بن معين: «إنما هذا زيد بن طلحة المديني، لم يكن بشيء» .
<span data-type="title" id=toc-880>(٨٧٩) </span>سألت يحيى عن يحيى بن المتوكل أبي بكر البصري، وكان قدم بغداد فحدثهم عن هشام بن حسان وغيره، ثم خرج إلى المصيصة فمات بها، فقال: «لا أعرفه» .
قال ابن الجنيد: أمرني خالد الختلي أن أسأله عن هذا الشيخ، فلم يعرفه أبو زكريا.
<span data-type="title" id=toc-881>(٨٨٠) </span>سألت يحيى عن يحيى بن المتوكل، صاحب بهية، أبو عقيل، فقال: «ليس به بأس» .',NULL,'487',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (215,'<span data-type="title" id=toc-882>(٨٨١) </span>قلت ليحيى بن معين: حدثنا يحيى بن بكير، عن ابن وهب، عن مالك، قال: دخلت على عائشة بنت سعد فسألتها عن بعض الحديث، فلم أرض أن آخذ عنها شيئًا لضعفها، فقال يحيى بن معين: «عنى ضعف بدنها، عائشة بنت سعد ثقة» .
<span data-type="title" id=toc-883>(٨٨٢) </span>قلت ليحيى: بلغني أن أبا قطن حدث فيما قرأ على مالك عن عائشة بنت سعد، أن سعدًا كان له مركن يتوضأ منه وأهل البيت، قال: «قد سمعت هذا من أبي قطن» .
<span data-type="title" id=toc-884>(٨٨٣) </span>قلت ليحيى: يزيد بن أبي زياد حجة؟ قال: «لا، ليس بحجة، ضعيف الحديث» .
<span data-type="title" id=toc-885>(٨٨٤) </span>سألت يحيى عن علي بن غراب، فقال: «ما أرى كان به بأس، كان من الشيعة، وما كان ممن يكذب» .
<span data-type="title" id=toc-886>(٨٨٥) </span>قلت ليحيى: محمد بن حمران القيسي البصري؟ قال: «ضعيف الحديث» .
<span data-type="title" id=toc-887>(٨٨٦) </span>قلت ليحيى: بحر السقاء؟ قال: «ليس بشيء» .',NULL,'488',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (216,'<span data-type="title" id=toc-888>(٨٨٧) </span>قلت ليحيى: محمد بن كثير الكوفي؟ قال: «ما كان به بأس، كان قدم فنزل ثم عند نهركم ذاك» ، فظننت أنا أنه يعني نهر كرخايا، قلت: إنه روى أحاديث منكرات، قال: «ما هي؟» ، قلت: عن إسماعيل بن أبي خالد، عن الشعبي، عن النعمان بن بشير يرفعه: «نضر الله امرءًا سمع مقالتي فبلغ بها» ، وبهذا الإسناد مرفوع: «اقرأ القرآن ما نهاك، فإذا لم ينهك فلست تقرؤه» ، فقال: «من روى عنه هذا؟» ، فقلت: رجل من أصحابنا -أعني له: محمد بن عبد الحميد الحميدي-، فقال: «هذا عسى سمعه من السندي ابن شاهك، وإن كان الشيخ روى هذا فهو كذاب، وإلا فإني رأيت حديث الشيخ مستقيمًا» .
<span data-type="title" id=toc-889>(٨٨٨) </span>سمعت يحيى بن معين يقول: «إذا جزت باب الأنبار إلى أقصى قرية بأفريقية، فأحسن حالاتهم العرض» ، ثم ذكر حبيبًا صاحب',NULL,'489',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (217,'مالك، فقال: «كذاب خبيث، رجل سوء، يخرطف، يضع الحديث، يقرأ على مالك فيخرطف الأحاديث؛ العشر ورقات وأكثر وأقل» .
<span data-type="title" id=toc-890>(٨٨٩) </span>ثم قال يحيى: «إبراهيم بن سعد قرئ عليه حديث ابن الهاد ذاك؛ حديث سعيد بن يسار، عن ابن عمر في التحميض، وإنما هو: إبرهيم، عن الليث بن سعد» ، قلت: الليث عن ابن الهاد؟ قال: «لا، عن الحارث بن يعقوب، فلم يفطن له، وحملوه عنه، وإنما سمعه من الليث بن سعد» .',NULL,'490',NULL,NULL,'0');
INSERT INTO [b12698] VALUES (218,'<span data-type="title" id=toc-891>(٨٩٠) </span>قلت ليحيى: يزيد بن أبي زياد ما اسم أبيه؟ قال: «لا أدري» ، قلت: هو ضعيف؟ قال: «نعم» .',NULL,'491',NULL,NULL,'0');
