INSERT INTO [b12751] VALUES (1,'نوادر الرسائل (١٧)
INSERT INTO [b12751] VALUES (2,'<span data-type="title" id=toc-2>[فضائله، ومقتله، رضي الله عنه]</span>
INSERT INTO [b12751] VALUES (3,'٢-.. .... ... أنبأنا أبو بكر بن أبي الدنيا أنبأنا عباس بن هشام عن أبيه قال بويع علي بن أبي طالب بن عبد المطلب بن هاشم بن عبد مناف بالمدينة يوم الجمعة حين قتل عثمان لاثنتي عشرة ليلة بقيت من ذي الحجة فاستقبل المحرم سنة ست وثلاثين قال غير عباس وكانت بيعته في دار عمرو بن محصن ⦗٢٤⦘ الأنصاري ثم أحد بني مبذول يوم الجمعة ثم بويع بيعة العامة من الغد يوم السبت في مسجد رسول الله صلى الله عليه وسلم ويكنى أبا الحسن.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (4,'٣- أنبأنا أبو علي بن صفوان أنبأنا ابن أبي الدنيا حدثني شريح بن يونس أنبأنا هشيم عن إسماعيل بن سالم عن عمار الحضرمي عن زاذان أبي عمر أن رجلا حدث عليا بحديث فقال ما أراك إلا قد كذبتني قال لم أفعل قال أدعو عليك إن كنت كذبت قال ادع فدعا فما برح حتى عمي.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (5,'٤- قال وأنبأنا ابن أبي الدنيا أنبأنا خلف بن سالم أنبأنا محمد بن بشر عن أبي مكين قال مررت أنا وخالي أبو أمية على دار في محل حي من مراد فقال ترى هذه الدار قلت نعم قال فإن عليا مر عليها وهم يبنونها فسقطت عليه قطعة فشجته فدعا الله أن لا يكمل بناؤها قال فما وضعت عليها لبنة قال فكنت تمر عليها لا تشبه الدور.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (6,'٥- قال وحدثنا ابن أبي الدنيا حدثني عبد الله بن يونس بن بكير الشيباني عن أبيه عن عبد الغفار بن القاسم الأنصاري عن أبي نمير الشيباني قال ⦗٢٥⦘ شهدت الجمل مع مولاي فما رأيت يوما قط أكثر ساعدا نادرا وقدما نادرة من يومئذ ولا مررت بدار الوليد قط إلا ذكرت يوم الجمل قال فحدثني الحكم بن عتيبة أن عليا دعا يوم الجمل فقال اللهم خذ بأيديهم وأقدامهم.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (7,'٦-.. .... .... ... أنبأنا أبو علي بن صفوان قال أنبأنا أبو بكر بن أبي الدنيا أنبأنا علي بن الجعد أخبرني عمرو بن شمر حدثني إسماعيل السدي قال سمعت أبا أراكة قال صليت مع علي بن أبي طالب صلاة الفجر فلما انفتل عن يمينه مكث كأن عليه كآبة حتى إذا كانت الشمس على حائط المسجد قيد رمح قال وحائط المسجد أقصر مما هو الآن ثم قلب يده فقال والله لقد رأيت أصحاب محمد صلى الله عليه وسلم فما أرى اليوم شيئا يشبههم لقد كانوا يصبحون شعثا غبرا بين أعينهم أمثال ركب المعزى قد باتوا لله سجدا وقياما يتلون كتاب الله يراوحون بين جباههم وأقدامهم فإذا أصبحوا فذكروا الله مادوا كما تميد الشجر في يوم الريح وهملت أعينهم حتى تبل ثيابهم والله لكأن القوم باتوا غافلين ثم نهض فما رئي بعد ذلك مفترا يضحك حتى ضربه ابن ملجم عدو الله الفاسق.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (8,'٧-.. .... .... ... أنبأنا أبو علي بن صفوان أنبأنا أبو بكر بن أبي الدنيا أنبأنا ⦗٢٦⦘ يوسف بن موسى أنبأنا جرير عن حمزة الزيات قال قال علي بن أبي طالب
INSERT INTO [b12751] VALUES (9,'٨- أنبأنا أبو علي بن صفوان أنبأنا أبو بكر بن أبي الدنيا حدثني عيسى بن عبد الله مولى بني تميم عن شيخ من بني هاشم قال رأيت رجلا بالشام قد اسود نصف وجهه وهو يغطيه فسألته عن سبب ذلك فقال نعم قد جعلت لله علي أن لا يسألني أحد عن ذلك إلا أخبرته كنت شديد الوقيعة في علي بن أبي طالب كثير الذكر له بالمكروه فبينا أنا ذات ليلة نائم أتاني آت في منامي فقال أنت صاحب الوقيعة في علي وضرب شق وجهي فأصبحت وشق وجهي أسود كما ترى.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (10,'٩-.. .... .... .... أنبأنا أبو علي بن صفوان أنبأنا أبو بكر بن أبي الدنبا حدثني عبد الرحمن بن صالح أنبأنا عمرو بن هاشم الجنبي عن أبي ⦗٢٧⦘ جناب عن أبي عون الثقفي عن أبي عبد الرحمن السلمي قال لي الحسن بن علي قال أبي علي إن رسول الله صلى الله عليه وسلم سنح لي الليلة في منامي فقلت يا رسول الله ما لقيت من أمتك من الأود واللدد قال ادع عليهم قلت اللهم أبدلني بهم من هو خير منهم وأبدلهم بي من هو شر مني.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (11,'١٠-.. .... .... .... .... .. أنبأنا عمر بن الحسن قال أنبأنا ابن أبي الدنيا أخبرني العباس بن هشام عن أبيه قال كانت خلافة علي أربع سنين وثمانية أشهر وثلاثة وعشرين يوما.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (12,'١١-.. .... .... .... .... ... أنبأنا الحسن بن كثير عن أبيه وكان أدرك عليا قال خرج علي إلى صلاة الفجر فاستقبله الوز يصحن في وجهه فجعلنا نطردهن عنه فقال دعوهن فإنهن نوائح.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (13,'١٢- حدثنا الحسين نا عبد الله نا محمد بن عمرو بن الحكم نا ⦗٢٨⦘ الضحاك بن شهر نا خارجة عن حصين عن هلال بن يساف قال كان علي بن أبي طالب يخرج إلى صلاة الفجر فيقول الصلاة الصلاة فبينا هو كذلك إذ ابتدره رجلان فضربه أحدهما ضربة بالسيف فسبقه ابن النباح راجعا وأخذ الآخر فقالوا ما نرى به بأسا فقال لقد سقيته السم شهرين ولو قسمتها بين العرب لأفنتهم وجعل النساء يبكين عليه وجعل آخرون يقولون ليس عليه بأس فقال ابن ملجم لعنه الله أفعلي تبكون.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (14,'١٣- حدثنا الحسين نا عبد الله نا يوسف بن موسى نا عبيد الله بن ⦗٢٩⦘ موسى قال أخبرنا الحسن بن دينار عن الحسن قال سهر علي عليه السلام في تلك الليلة فقال إني مقتول لو قد أصبحت قال فجاءه مؤذنه بالصلاة فقام فمشى قليلا ثم رجع فقالت له ابنته مر جعدة يصلي بالناس قال لا مفر من الأجل ثم قام فخرج فمر على صاحبه وقد سهر ليله ينتظره قد غلبته عينه فضربه برجله وقال الصلاة فقام فلما رأى عليا ضربه قال الحسن أنى علم هذا.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (15,'١٤- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي نا علي بن أبي فاطمة الغنوي قال حدثني شيخ من بني حنظلة قال ⦗٣٠⦘ لما كانت الليلة التي أصيب فيها علي رحمه الله أتاه ابن النباح حين طلع الفجر يؤذنه بالصلاة وهو مضطجع متثاقل فقال الثانية يؤذنه بالصلاة فسكت فجاءه الثالثة فقام علي يمشي بين الحسن والحسين وهو يقول
INSERT INTO [b12751] VALUES (16,'١٥- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد قال حدثني رجل من النخع عن صالح بن ميثم عن عمران بن ميثم عن أبيه أن عليا خرج فكبر في الصلاة ثم قرأ من سورة الأنبياء إحدى عشرة آية ثم ضربه ابن ملجم من الصف على قرنه فشد ⦗٣١⦘ عليه الناس وأخذوه وانتزعوا السيف من يده وهم قيام في الصلاة وركع علي ثم سجد فنظرت إليه ينقل رأسه من الدم إذا سجد من مكان إلى مكان ثم قام في الثانية فقام فخفف القراءة ثم جلس فتشهد ثم سلم وأسند ظهره إلى حائط المسجد.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (17,'١٦- حدثا الحسين نا عبد الله قال حدثني أبي عن هشام بن محمد قال حدثني عمر بن عبد الرحمن بن نفيع بن جعدة بن هبيرة أنه لما ضرب ابن ملجم عليا عليه السلام وهو في الصلاة تأخر فدفع في ظهر جعدة بن هبيرة فصلى بالناس ثم قال علي علي بالرجل فأتي فقال أي عدو الله ألم أحسن إليك وأصنع وأصنع قال بلى قال ما حملك على ما صنعت قال شحذت سيفي أربعين يوما ثم دعوت الله أن أقتل به شر خلقه فقال علي ما أراك إلا مقتولا به وما أرك إلا شر خلقه فقتل ابن ملجم بذاك السيف.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (18,'١٧- نا الحسين قال نا عبد الله قال نا سعيد بن يحيى الأموي ⦗٣٢⦘ قال نا عبد الله بن سعيد عن زياد بن عبد الله عن المجالد بن سعيد قال جاء ابن بجرة الأشجعي وابن ملجم معهما سيفان فجلسا بالباب فلما خرج علي رضي الله عنه نادى بالصلاة وابتدره الرجلان فضرباه فأخطأ أحدهما فأصاب الحائط وأصاب الآخر وخرجا هاربين فخرج ابن بجرة من ناحية كندة وخرج ابن ملجم من ناحية السوق فأدرك فأخذ فأتي به علي رضي الله عنه فقال احبسوه.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (19,'١٨- حدثا الحسين بن صفوان البرذعي قال نا عبد الله بن أبي الدنيا قال حدثنا سعيد بن يحيى الأموي قال حدثنا عبد الله بن سعيد عن زياد بن عبد الله عن عوانة بن الحكم أن ثلاثة تبايعوا على قتل علي ومعاوية وعمرو بن العاص ⦗٣٣⦘ فخرج إلى عمرو بن العاص وآخر إلى معاوية يقال له البرك بن عبد الله رجل من بني تميم من بني سعد ثم من بني صريم وآخر إلى علي وهو ابن ملجم فجاء ابن ملجم إلى الكوفة فخطب قطام وكانت من بني التيم وكانت ترى رأي المحكمة فقالت لا والله لا أتزوجك إلا على ثلاثة آلاف وقتل علي فأعطاها ذلك وبنى بها.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (20,'١٩- حدثنا الحسين بن صفوان البرذعي قال نا عبد الله بن أبي الدنيا قال حدثنا سعيد بن يحيى قال نا عبد الله بن سعيد الأموي عن زياد بن عبد الله البكائي عن عوانة بن الحكم الكلبي قال فحدثني مزاحم بن زفر التيمي عن وجيه أن ابن ملجم كان يجلس في قومه من صلاة الغداة إلى ارتفاع النهار والقوم يهضبون وهو لا يتكلم بكلمة وبلغني أنه كان يوما جالسا في السوق متقلدا السيف فمرت به جنازة فيها المسلمون والقسيسون فقال ويلكم ما هذا ⦗٣٤⦘ قالوا أبجر بن جابر أبو حجار العجلي وابنه سيد بكر بن وائل فاتبعه المسلمون لمكان ابنه وتبعه النصارى لنصرانيته فقال ابن ملجم أما والله لولا أني أستبقي نفسي لأمر هو أعظم من هذا أجرا عند الله لاستعرضته بالسيف.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (21,'٢٠- حدثا الحسين قال حدثنا عبد الله قال حدثنا سعيد بن يحيى قال حدثنا عبد الله بن سعيد قال حدثنا زياد بن عبد الله عن عوانة أن قطام قالت لابن ملجم قد فرغت فافرغ فخرج ابن ملجم حتى أتى المسجد وضربت قطام قبتها في المسجد وألبسته السلاح وخرج علي يقول الصلاة الصلاة أيها الناس فضربه ابن ملجم على جبهته بالسيف فأصاب السيف الحائط فثلم فيه ثم ألقى السيف وقال للناس اتقوا السيف فإنه مسموم وزعموا أنه كان سمه شهرا وأخذ ابن ملجم ودخل علي منزله.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (22,'٢١- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد قال حدثني رجل من النخع عن صالح بن ميثم قال بينا علي بن أبي طالب قبل تلك الليلة بليلة يوقظ الناس للفجر إذ أتاه ابن ملجم بصحيفة ملفوفة يدعوه فيها أو ينابذه ففتحها علي فلم ينظر فيها فأمسكها حتى صلى ثم فتحها فإذا فيها أدعوك إلى التوبة من الشرك وأنابذك وإن الله لا يهدي كيد الخائنين فقال علي من صاحب هذه الصحيفة فلم يكلمه أحد فبصق عليها فمحاه ثم رمى به وقال عليه لعنة الله.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (23,'٢٢- حدثنا الحسين بن صفوان البرذعي نا عبد الله نا أبي رحمه الله عن هشام بن محمد أن أبا عبد الله الجعفي حدثهم عن جابر عن أبي جعفر محمد بن علي بن حسين قال ⦗٣٦⦘ لما أراد الله تبارك وتعالى إكرام علي بهلاك ابن ملجم ظل ابن ملجم في مسجد لبني أسد حتى إذا جنه الليل صار إلى دار من دور كندة وقبل ذلك بجمعة ما قام علي على المنبر فقال إنه قضي فيما قضي على لسان النبي عليه السلام الأمي لا يبغضك مؤمن ولا يحبك كافر وقد خاب من حمل إثما وافترى أما إني رأيت في ليلتي هذه في منامي أن شيطانا ضريني ضربة فخضب لحيتي من رأسي بدم عبيط فما ساءني ذلك واعلم يا علي أنك مقتول إن شاء الله فماذا ينتظر أشقاها أن يخضب هذه من هذا ثم أمر يده اليمنى على لحيته ثم على رأسه ثم نزل عن المنبر فلما كانت الليلة التي أصيب فيها خرج يريد صلاة العشاء تصايحت الوز حوله فقال يشيهن صوائحا ونساء نوائحا قال وتحينه الفاسق حتى إذا كانت الساعة التي يخرج فيها أقبل حتى قام في جنح الباب وخرج أمير المؤمنين فضربه ضربة وكان محمد بن الحنفية قريبا منه فأخذه ووثب الناس إلى ابن ملجم ليقتلوه فقال لهم مهلا لا يهاجن ما بقيت فإن عشت اقتصصت من الرجل أو وهبت لله وإن أمت فالنفس بالنفس.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (24,'٢٣- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن ⦗٣٧⦘ يونس بن بكير قال حدثني أبي عن عبد الغفار بن القاسم الأنصاري قال سمعت غير واحد يذكر أن ابن ملجم بات عند الأشعث بن قيس فلما أسحر جعل يقول له أصبحت وكان حجر مؤذنهم فخرج حجر وأذن فلم يكن أسرع من أن سمع الواعية فجعل حجر ينادي فوق المنارة قتله الأعور وكان الرجل أعور وكان علي يسميه عرف النار.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (25,'٢٤- حدثنا الحسين نا عبد الله قال حدثني أبي عن هشام بن محمد نا عوانة بن الحكم أن حجر بن عدي لما انصرف الناس من صلاة الغداة من مسجد الأشعث وكان حجر بن عدي إمامهم فلما سلم قال الناس ضرب أمير المؤمنين الليلة فنظر حجر إلى الأشعث فقال ألم أر ابن ملجم معك وأنت تناجيه تقول له فضحك الصبح والله لو أعلم ذلك حقا لضربت أكثرك شعرا فقال إنك شيخ قد خرفت قال وبعث الأشعث إليه قيس بن الأشعث صبيحة ضرب ⦗٣٨⦘ علي قال أي بني أنظرت كيف أصبح أمير المؤمنين فذهب فنظر ثم رجع إليه فقال يا أبه رأيت عينه داخليتين في رأسه فقال الأشعث عيني دميغ ورب الكعبة.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (26,'٢٥- حدثا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي عن محمد بن ربيعة قال حدثني نافع بن عقبة المنبهي قال خرجت من أهلي في السحر فانتهيت إلى باب المسجد باب كندة فإذا رجل خارج من المسجد مخترط سيفه فطرحت طيلساني في وجهه ثم أخذته فانتزعت السيف من يده ثم قدته كما يقاد الجمل فأدخلته المسجد فسمعت الضوضاء والناس يقولون قتل أمير المؤمنين فجئت به فقلت هو ذا أخذته خارجا من المسجد مخترطا سيفه فأدخل على علي فقال احتبسوه فإن أمت من جرحتي هذه فهو في أيديكم نفس بنفس فاقتلوه وإن أعش وأبرأ أر فيه رأيي.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (27,'٢٦- حدثا الحسين نا عبد الله قال حدثني أبي رحمه الله عن ⦗٣٩⦘ هشام بن محمد قال حدثني رجل من النخع قال حدثني صالح بن ميثم عن أبيه قال نظرت إلى الناس حين انصرفوا من الفجر ينهشون ابن ملجم بأنيابهم ويثبون عليه وثبا كأنهم السباع ويقولون يا عدو الله ما صنعت أهلكت الأمة وقتلت خير الناس وإنه لساكت ما ينطق.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (28,'٢٧- حدثنا الحسين نا عبد الله قال حدثنا سعيد بن يحيى نا عبد الله بن سعيد عن زياد بن عبد الله قال قال محمد بن إسحاق أقبل ابن ملجم المرادي من الشام حتى ضرب عليا فقالت أم كلثوم بنت علي لابن ملجم يا عدو الله قتلت أمير المؤمنين قال لم أقتل إلا أباك قالت أما والله إني لأرجو أن لا يكون به بأس قال أفعلي تبكين إذا ثم قال لها والله لقد سممته شهرا فإن أخلفني فأبعده الله وأسحقه.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (29,'٢٨- حدثنا الحسين نا عبد الله قال وأخبرني العباس بن هشام بن ⦗٤٠⦘ محمد عن أبيه عن أبي المقوم يحيى بن ثعلبة الأنصاري عن عبد الملك بن عمير قال لما أدخل ابن ملجم على علي رحمه الله صبيحة ضربه وعنده ابنته أم كلثوم تبكي عند رأسه فلما نظرت إلى ابن ملجم سكتت ثم قالت يا عدوا لله والله ما على أمير المؤمنين بأس فقال أما والله لقد شحذت السيف وأنكرت الحيف ونفيت الوجل وحثثت العجل وضربته ضربة لو كانت بربيعة ومضر لأتت عليهم فعلي إذا تبكين.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (30,'٢٩- حدثنا الحسين نا عبد الله قال حدثنا المنذر بن عمار الكاهلي قال حدثني ابن أبي الحثحاث العجلي عن أبيه قال خرج علي بالسحر يوقظ الناس للصلاة فاستقبله ابن ملجم ومعه سيف صغير فقال {ومن الناس من يشري نفسه ابتغاء مرضات الله والله رؤوف بالعباد] . فظن علي أنه يستفتحه فقال {يا أيها الذين آمنوا ادخلوا في السلم كافة} . فضربه بالسيف على قرنه.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (31,'٣٠- حدثنا الحسين نا عبد الله قال حدثني هارون بن أبي يحيى عن شيخ من قريش أن عليا قال لما ضربه ابن ملجم فزت ورب الكعبة.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (32,'٣١- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي عن أبي إسحاق المختار التيمي عن أبي المطر أن ابن ملجم لما ضرب عليا وقع حد السيف برأس علي ووقع وسط السيف بالباب فقال علي خذوا الرجل فإن أمت فاقتلوه وإن أعش فالجروح قصاص.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (33,'٣٢- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس قال حدثني أبي قال حدثني أبان البجلي عن أبي بكر بن جعفر عن ابن عباس قال سمعت عليا بالكوفة وأتي فقيل يا أمير المؤمنين ما تقول في هذا الأسير قال أرى أن تحسنوا ضيافته حتى تنظروا على أي حال أكون فإن أهلك فلا تلبثوه بعدي ساعة.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (34,'٣٣- حدثنا الحسين نا عبد الله قال حدثنا عبد الرحمن بن ⦗٤٢⦘ صالح نا عمرو بن هاشم عن إسماعيل بن أبي خالد عن عامر قال لما ضرب علي تلك الضربة قال ما فعل ضاربي قالوا قد أخذناه قال أطعموه من طعامي واسقوه من شرابي فإن أنا عشت رأيت فيه رأيي وإن أنا مت فضربوه ضربة ولا تزيدوه عليها.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (35,'٣٤- حدثنا الحسين نا عبد الله نا أبو خيثمة نا يحيى بن سعيد عن سفيان عن عمران بن ظبيان عن حكيم بن سعد أبي تحيا قال ⦗٤٣⦘ قالوا لعلي لو أخذنا قاتلك أبرنا عترته فقال به به ذاكم الظلم النفس بالنفس.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (36,'٣٥- حدثنا الحسين نا عبد الله نا يوسف بن موسى نا الضحاك بن مخلد عن سفيان عن عمران بن ظبيان عن حكيم بن سعد قال قيل لعلي لو نعلم قاتلك أبرنا عترته فقال به به ذاكم الظلم ولكن اقتلوه ثم احرقوه.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (37,'٣٦- حدثنا الحسين نا عبد الله نا خلف بن سالم نا أبو نعيم ⦗٤٤⦘ نا فطر نا أبو الطفيل قال دعا علي الناس للبيعة فجاء عبد الرحمن بن ملجم المرادي فرده مرتين ثم بايعه ثم قال ما يحبس أشقاها ليخضبن أبو ليصبغن هذه من هذا للحيته من رأسه ثم تمثل
INSERT INTO [b12751] VALUES (38,'٣٧- حدثنا الحسين نا عبد الله نا خلف بن سالم نا عبد الرزاق قال أخبرنا معمر عن أيوب عن ابن سيرين عن عبيدة قال كان علي إذا رأى ابن ملجم قال
INSERT INTO [b12751] VALUES (39,'٣٨- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد عن أبيه قال لما ضرب ابن ملجم عليا دعي له ابن أثير الكندي وكان طبيبا فأخذ خرقة فأدخلها في رأسه فإذا دماغه قد خرج فيها فقال يا أمير المؤمنين اعهد عهدك وأمر أمرك فإنك ميت.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (40,'٣٩- حدثنا الحسين نا عبد الله نا سعيد بن يحيى القرشي نا عبد الله بن سعيد عن زياد بن عبد الله قال قال مجالد دعي لعلي الكندي وكان طبيبا فدعا برئة فأخذ منها قديدة لطيفة فيها عرقها ثم نفخها ودسها في جرحه ثم أخرجها فإذا عليها من دماغه فقال اعهد يا أمير المؤمنين لا يعالج مثلك فقال علي عند ذلك إن مت فاقتلوه فإنما النفس بالنفس وإن عشت فسأرى رأيي.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (41,'<span data-type="title" id=toc-3>وصية علي بن أبي طالب رحمه الله</span>
INSERT INTO [b12751] VALUES (42,'٤١- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد عن أبي عبد الله الجعفي عن جابر عن أبي جعفر محمد بن علي قال أوصى علي بن أبي طالب بهذه الوصية وكتبها كاتبه عبيد الله بن أبي رافع وعلي يملي عليه.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (43,'٤٢- ثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد عن أبي جناب الكلبي عن أبي عون الثقفي عن أبي عبد الرحمن السلمي قال أوصى علي بن أبي طالب ابنه الحسن بن علي حين حضره الموت قال يا بني أوصيك بتقوى الله وإقام الصلاة وإيتاء الزكاة عند محلها وحسن الوضوء والصبر عليه فإنه لا صلاة إلا بطهور ولا تقبل الصلاة ممن منع الزكاة وأوصيك بمغفرة الذنب وكظم الغيظ وصلة الرحم والحلم عن الجهل ⦗٤٩⦘ والتفقه في الدين والتثبت في الأمر والتعاهد للقرآن وحسن الجوار والأمر بالمعروف والنهي عن المنكر واجتناب الفواحش كلها في كل ما عصي الله فيه.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (44,'٤٣- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد عن شيخ من الأزد حدثهم عن عبد الرحمن بن جندب عن أبيه قال دخلت على علي أسأل به فقمت قائما لمكان ابنته أم كلثوم كانت مستترة فقلت يا أمير المؤمنين إن فقدناك ولا نفقدك نبايع للحسن فقال علي ما آمركم ولا أنهاكم فعدت فقلت مثلها فرد عليها مثلها ثم دعا ابنيه الحسن والحسين فقال لهما أوصيكما بتقوى الله ولا تبغيا الدنيا وإن بغتكما ولا تبكيا على شيء منها زوي عنكما قولا الحق وارحما اليتيم وأعينا الطائع واصنعا للآخرة كونا للظالم خصما وللمظلوم عونا واعملا بما في كتاب الله ولا يأخذكما في الله لومة لائم ثم نظر إلى ابنه محمد بن الحنفية فقال يا بني أفهمت ما أوصيت به أخويك قال نعم يا أبه قال يا بني أوصيك بمثله وأوصيك بتوقير أخويك وتعظيم حقهما وتزيين أمرهما ولا تقطع أمرا دونهما ⦗٥٠⦘ ثم قال للحسن والحسين وأوصيكما به فإنه شقيقكما وابن أبيكما وقد علمتما أن أباكم كان يحبه فأحباه.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (45,'٤٤- حدثنا الحسين نا عبد الله حدثني محمد بن عباد بن موسى نا يزيد بن هارون عن محمد بن عبيد الله عن أبي جعفر أن عليا لما احتضر جمع بنيه فقال يا بني يؤلف بعضكم بعضا يرأف كبيركم صغيركم ولا تكونا كبيض وضاح في داوية ويح الفراخ فراخ آل محمد من عتريف مترف يقتل خلفي وخلف الخلف أما والله لقد شهدت الدعوات وسمعت الرسالات وليتمن الله نعمته عليكم أهل البيت قال ابن عباد قوله لا تكونوا كبيض وضاح في داوية أن النعامة تبيض في الداوية فتحضنه حتى إذا فرخ البيض تفرقت رئالها يعني فراخها يقول لا تتفرقوا بعد موتي.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (46,'٤٥- حدثنا الحسين نا عبد الله نا علي بن الجعد نا أبو ⦗٥١⦘ يوسف القاضي نا عبيد الله بن محمد بن عمر بن علي عن أبيه وجده أنه كتب هذه الوصية هذا ما أمر به وقضى به في ماله علي بن أبي طالب تصدق بها مرضاة الله ووجهه تنفق في كل نفقة في سبيل الله في الحرب والسلم والجنود وذي الرحم والقريب والبعيد لا يباع ولا يورث كل مال بينبع غير أن رياحا وأبا نيزر وجبيرا إن حدث بي حدث فليس عليهم سبيل وهم.. موالي يعملون في المال خمس حجج وفيه نفقتهم ورزقهم ورزق أهليهم فذلك الذي أقضي بما كان لي بينبع واجبة حيا أنا أو ميت ومعهما ما كان لي بوادي القرى من مال يبقى حيا أنا أو ميت ومع ذلك الأذينبة وأهلها حيا أنا أو ميت ومع ذلك رعه وأهلها وأن رازما له مثل ما كتب لأبي نيزر ورياح وجبير هو يتقبلهم وهو يرتهن ذلك قضيت بيني ⦗٥٢⦘ وبين الله للغد يوم قدمت مسكن حي أو ميت وإن مالي في وادي القرى والأذينبة ورعه ابتغاء وجه الله وفي سبيل الله ووجهه يوم تسود وجوه وتبيض وجوه لا يبعن ولا يوهبن ولا يورثن إلا إلى.. هذا ما قضى به علي بن أبي طالب في ماله واجبة بتة يقوم على ذلك الحسن بن علي يليها مادام حيا فإن هلك سلمها إلى الحسين بن علي يليها مادام حيا فإن هلك فهي إلى الأول فالأول من ذوي السن والصلاح من الذي يعدل فيها ويطعم الناس بالمعروف غير المنكر ولا الإسراف يزرع ويغرس ويصلح كإصلاحهم مالهم ولا يباع من أولاد نخل هذه القرى الأربع ودية واحدة حتى تشكل أرضها غرسا فإنما عملتها للمؤمنين أولهم وآخرهم فمن وليها من الناس فأذكره الله ما قعد ونصح وحفظ أمانته ووسع هذا كتاب علي بن أبي طالب رحمة الله عليه بيده يوم قدم مسكن وقد علمتم أن الفقير.. في سبيل الله واجبة بتة ومال محمد النبي صلى الله عليه ينفق في كل نفقة في سبيل الله ووجهه وذوي الرحم والفقراء والمساكين وابن السبيل يقوم على ذلك أكبر بني فاطمة بالأمانة والإصلاح كإصلاحه ماله يزرع ويغرس وينصح ويجهد ⦗٥٣⦘ هذا ما أوصى به علي بن أبي طالب رحمه الله في هذه الأموال التي كتب في هذه الصحيفة والله المستعان على كل حال لا يحل لأحد وليها وحكم فيها أن يعمل فيها بغير عهدي أما بعد فإن ولائدي اللاتي أطوف عليهن تسع عشرة منها أمهات أولادي معهن أولادهن ومنهن حبالى ومنهن من لا ولد لها وقضيت إن حدث لي حدث في هذا الغزو أن من كان منهن ليس لها ولد وليست بحبلى عتيقة لوجه الله ليس لأحد عليها سبيل ومن كان منهن حبلى أو لها ولد فلتمسك على ولدها وهي من حظه فإن مات ولدها وهي حية فليس لأحد عليها سبيل هذا ما قضى به في ولائده التسع عشرة شهد عبيد الله بن أبي رافع وهياج بن أبي هياج وكتب علي بن أبي طالب أم الكتاب بيده لعشر خلون من جمادى الأولى سنة تسع وثلاثين قال عبيد الله وكان بين مقتله وبين كتابه هذا أربعة أشهر وثلاث عشرة ليلة.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (47,'٤٦- حدثنا الحسين نا عبد الله نا إسحاق بن إسماعيل نا سفيان عن عمرو بن دينار قال في صدقة علي بن أبي طالب هذا ما تصدق به علي تصدق بينبع ابتغاء وجه الله وهي جذاذ أربعين آلاف وسق سوى حنطتها وشعيرها وسلتها وحنائها وموزها وكل مال لي بينبع إنما عملتها للمؤمنين أولهم وآخرهم ليولجني به الله الجنة ويصرف به النار عن وجهي ويصرف بها وجهي عن النار يوم تبيض وجوه وتسود وجوه فهي واجبة في سبيل الله صدقة واجبة مثلا لا تباع ولا توهب ولا تورث وتصدق علي بثماني عشرة عينا.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (48,'٤٧- حدنا الحسين نا عبد الله نا إسحاق بن إسماعيل نا سفيان عن عمرو بن دينار قال من وصية علي وأن رياحا وجبيرا وأبا نيزر يعملون في المال خمس حجج منها نفقاتهم ونفقات أهاليهم ثم هم أحرار.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (49,'٤٨- حدثنا الحسين نا عبد الله نا إسحاق نا سفيان عن عمرو بن دينار قال في وصية علي أما بعد فإن ولائدي اللاتي أطوف عليهن تسع عشرة وليدة منهن أمهات أولاد معهن أولادهن أحياء معهن ومنهن حبالى ومنهن من لا ولد لها فقضيت إن حدث بي حدث في هذه الغزوات من كان منهن ليست بحبلى وليس لها ولد فهي عتيقة لوجه الله ليس لأحد عليها سبيل ومن كان منهن حبلى أو لها ولد فهي تمسك على ولدها وهي من حظه فإن مات ولدها وهي حية فهي عتيقة لوجه الله هذا ما قضيت به في ولائدي التسع عشرة والله المستعان على كل حال شهد أبو هياج وعبيد الله بن أبي رافع وكتب.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (50,'٤٩- حدثنا الحسين نا عبد الله قال حدثني أبو علي أحمد بن الحسين الضرير نا الحسن بن هارون عن ابن زبار الكلبي عن حكيم بن نافع عن العلاء بن عبد الرحمن قال لما ضرب عبد الرحمن بن ملجم عليا رحمه الله وحمل إلى ⦗٥٦⦘ منزله أتاه العواد فحمد الله وأثنى عليه وصلى على النبي صلى الله عليه وسلم ثم قال كل امرئ ملاق ما يفر منه والأجل مساق النفس والهرب منه موافاته كم أطردت الأيام أبحثها عن مكنون هذا الأمر فأبى الله إلا إخفاءه هيهات علم مخزون أما وصيتي إياكم الله لا تشركوا به شيئا ومحمدا صلى الله عليه وسلم فلا تضيعوا سنته أقيموا هذين العمودين وأوقدوا هذين المصباحين وخلاكم ذم ما لم تشردوا حمل كل امرئ منكم مجهوده وعفا عن الجهلة رب رحيم ودين قويم كنا في مهب رياح وعلى ذرى أغصان وتحت ظل غمامة اضمحل مركدها فمحطها من الأرض عار جاورتكم أياما تباعا وليالي دراكا ثم طحرة أو لقعة وستعقبون من بعدي جثة خواء ساكنة بعد حركة كاظمة بعد نطق ليعظكم هدئي وخفوت أطرافي إنه أوعظ للمعتبرين من نطق البليغ وداعيكم وداع مرصد للتلاقي غدا ترون آثامي ويكشف عن سرائري لن يحابيني الله إلا أن أتزلفه بتقوى الله فيعفو عن فرط موعود عليكم السلام إلى يوم اللزام إن أبق فأنا ولي دمي وإن أفن فالفناء ميعادي العفو لي قربة ولكم حسنة فاعفوا عفا الله عنكم ألا تحبون أن يغفر الله لكم.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (51,'٥٠- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير حدثني أبي عن أبي عبد الله الجعفي عن جابر عن محمد بن علي قال إن عليا لما ضربه ابن ملجم أوصى بنيه ثم لم ينطق إلا بلا إله إلا الله حتى قبضه الله.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (52,'<span data-type="title" id=toc-4>موت علي بن أبي طالب رحمة الله عليه</span>
INSERT INTO [b12751] VALUES (53,'٥٢- حدثنا الحسين نا عبد الله قال حدثني أبو عبد الله العجلي نا عمرو بن محمد عن أبي معشر قال قتل علي رحمه الله يوم الجمعة لثلاث عشرة بقيت من شهر رمضان سنة أربعين قتله عبد الرحمن بن ملجم المرادي بالكوفة.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (54,'٥٣- حدثنا الحسين نا عبد الله قال حدثني محمد بن عمرو بن الحكم عن أبي عبد الرحمن الطائي ⦗٥٩⦘ بمثل ذلك وقال قتله عبد الرحمن بن يحيى بن عمرو بن ملجم المرادي.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (55,'٥٤- حدثنا الحسين نا عبد الله نا خلف بن سالم نا أبو نعيم نا سليمان بن القاسم قال حدثتني أمي عن أم جعفر سرية علي قالت إني لأصب على يديه الماء أخذ بلحيته فرفعها إلى أنفه وقال واها لك لتخضبن يوم الجمعة بدم فما مضت الجمعة حتى أصيب وأصيب يوم الجمعة.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (56,'٥٥- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد عن شيخ من الأزد عن عبد الرحمن بن جندب عن أبيه قال قبض علي رحمه الله يوم الأحد لإحدى عشرة ليلة بقيت من شهر رمضان سنة أربعين.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (57,'٥٦- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي قال حدثني أبو عبد الله الجعفي عن جابر عن أبي الطفيل وزيد بن وهب ومحمد بن علي وغيرهم أن عليا ضرب لثمان عشرة خلت من شهر رمضان وتوفي في أول ليلة من العشر يعني الأواخر من شهر رمضان.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (58,'٥٧- حدثنا الحسين نا عبد الله نا هارون بن معروف نا جرير بن عبد الحميد عن الأعمش عن سالم بن أبي الجعد عن عبد الله بن سبيع قال قيل لعلي ألا تستخلف يا أمير المؤمنين قال لا ولكني أترككم إلى ما ترككم رسول الله قال فما تقول إذا لقيت الله قال أقول اللهم تركتني فيهم ما بدا لك أن تتركني وتوفيتني وتركتك فيهم فإن شئت أفسدتهم وإن شئت أصلحتهم.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (59,'<span data-type="title" id=toc-5>سن علي بن أبي طالب رحمه الله</span>
INSERT INTO [b12751] VALUES (60,'٥٩- حدثنا الحسين نا عبد الله نا الحسين بن علي العجلي نا الحسين بن علي الجعفي قال سمعت سفيان يسأل جعفر بن محمد كم كان لعلي يوم قتل قال ثمان وخمسون.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (61,'٦٠- حدثنا الحسين نا عبد الله قال حدثني محمد بن عمرو بن الحكم نا أبو عبد الرحمن الطائي عن جعفر بن محمد عن أبيه قال قتل علي وهو ابن سبع وخمسين سنة وولي خمس سنين وبعث النبي وهو ابن سبع سنين.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (62,'٦١- حدثنا الحسين نا عبد الله نا محمد بن سعد قال أخبرنا محمد بن عمر قال أخبرنا علي بن عمر بن علي بن حسين عن عبد الله بن محمد بن عقيل قال قلت لابن الحنفية كم كانت سن أبيك حين قتل قال ثلاثا وستين.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (63,'٦٢- حدثنا الحسين نا عبد الله نا محمد بن سعد نا محمد بن عمر نا علي بن عمر بن علي بن الحسين عن عبد الله بن محمد بن عقيل قال سمعت ابن الحنفية يقول سنة الجحاف حين دخلت إحدى وثمانون لي خمس وستون سنة قد جاوزت سن أبي قلت وكم كانت سنه يوم قتل قال ثلاث وستون.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (64,'٦٣- حدثنا الحسين نا عبد الله قال حدثني أبو بكر بن محمد بن هانئ نا أحمد بن حنبل نا عبد الرزاق قال أخبرنا ابن ⦗٦٣⦘ جريج قال أخبرني عمر بن محمد بن علي أن علي بن أبي طالب مات لثلاث أوأربع وستين سنة أونحو ذلك.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (65,'٦٤- حدثنا الحسين نا عبد الله قال حدثني أبي عن هشام بن محمد عن أبيه قال أخبرني محمد بن عمر بن علي بن أبي طالب أن عليا قبض وهو ابن ثنتين وستين سنة ونصف.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (66,'٦٥- حدثنا الحسين نا عبد الله قال حدثني أبي قال أخبرنا شبابة بن سوار قال عن قيس بن الربيع عن عمرو بن قيس عن أبي صادق أن عليا قال والله لقد نهضت في الحرب وأنا ابن عشرين فها أنذا اليوم قد نيفت على الستين.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (67,'٦٦- حدثنا الحسين نا عبد الله قال وحدثت عن يحيى بن ⦗٦٤⦘ عبد الله بن بكير قال أخبرني ليث بن سعد أن أبا الأسود حدثه عن عروة أن عليا أسلم وهو ابن ثمان سنين قال ابن بكير فإن كان رسول الله أقام بمكة ثلاث عشرة قبل هجرته إلى المدينة فسن علي إحدى وستون وإن كان مقام رسول الله عشر سنين فسن علي ثمان وخمسون سنة.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (68,'<span data-type="title" id=toc-6>صفة علي رحمة الله عليه</span>
INSERT INTO [b12751] VALUES (69,'٦٨- حدثني الحسين قال حدثني عبد الله قال حدثني ⦗٦٦⦘ إبراهيم بن سعيد نا عفان نا أبو عوانة عن مغيرة عن قدامة بن عتاب قال كان علي ضخم البطن ضخم مشاشة المنكب ضخم عظم الذراع دقيق مستدقها ضخم عضلة الساق دقيق مستدقها.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (70,'٦٩- حدثنا الحسين نا عبد الله قال حدثني أبو هريرة الصيرفي نا يزيد بن هارون قال أخبرنا إسماعيل بن سالم عن الشعبي قال رأيت عليا يخطب الناس أبيض الرأس واللحية عظيم البطن قد أخذت لحيته ما بين منكبيه أصلع على رأسه زغبات.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (71,'٧٠- حدثنا الحسين نا عبد الله قال حدثنا أبو خيثمة نا جرير عن عبد الملك بن عمير قال رأيت عليا أبيض اللحية.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (72,'٧١- حدثنا الحسين نا عبد الله نا عبد الرحمن بن صالح نا يونس بن بكير عن عنبسة بن الأزهر وكان على قضاء جرجان وكان من بني عامر بن ذهل قال إنما منع عليا أن يخضب قول رسول الله صلى الله عليه وسلم يخضب هذه من هذه ووضع يده على هامته.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (73,'٧٢- حدثنا الحسين نا عبد الله قال حدثنا أبو عبد الرحمن القرشي نا عبد الرحمن بن محمد البخاري عن محمد بن إسحاق عن سعد بن عبد الرحمن بن أبي أيوب قال كنت في حجر جدتي أم أبي ابنة سعد بن الربيع وكانت عند زيد بن ثابت فسمعتها تقول قد رأيتني وأنا جارية شابة في ⦗٦٨⦘ مال لنا بالأسواف ورسول الله صلى الله عليه وسلم عندنا في نفر من أصحابه إذ قال لنا رسول الله ليدخلن عيكم الآن رجل من أهل الجنة ثم ثنى رسول الله ظهره ثم قال كن عليا قالت فطلع علي يفرج له الجريد والذي نفس أم سعد بيده لكأن وجهه القمر ليلة البدر.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (74,'٧٣- حدثنا الحسين نا عبد الله قال حدثني محمد بن فراس الضبعي نا عبد الله بن داود نا مدرك أبو الحجاج قال رأيت علي بن أبي طالب يخطب وكان من أحسن الناس وجها.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (75,'٧٤- حدثنا الحسين نا عبد الله قال حدثني أحمد بن بجير قال حدثني بهلول الكندي عن أبي إسحاق قال كنت مع أبي يوم الجمعة فقال لي ألا أريك عليا أمير المؤمنين قلت بلى فحملني فرأيته على المنبر أصلع له بطن.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (76,'٧٥- حدثنا الحسين نا عبد الله قال حدثني العباس بن هشام بن محمد عن أبيه عن جده قال حدثتني أمي عائشة بنت عبيد قالت رأيت علي بن أبي طالب فرأيت رجلا ربعة عظيم البطن بعيد ما بين المنكبين عظيم الهامة أخفش العين أرسح.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (77,'٧٦- حدثنا الحسين نا عبد الله قال حدثني محمد بن عباد بن موسى نا زيد بن الحباب عن محمد بن جابر عن أبي إسحاق قال رأيت عليا أبيض الرأس واللحية وعليه قميص قهز وإزار ذبيني الرداء فوق القميص والقميص فوق الإزار.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (78,'<span data-type="title" id=toc-7>غسل علي وتكفينه والصلاة عليه ودفنه رضوان الله عليه</span>
INSERT INTO [b12751] VALUES (79,'٧٨- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي قال حدثني أبو عبد الله الجعفي عن جابر عن محمد بن علي وأبي.. .. أن الحسن بن علي غسل عليا بيده وكفن في قميص ولفافتين وأخذه من ناحية القبلة وأسنده بسبع لبنات.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (80,'٧٩- حدثنا الحسين نا عبد الله قال حدثني أبو عبد الرحمن ⦗٧١⦘ القرشي نا عبيدة بن الأسود الهمداني عن عبد السلام بن أبي المسلي عن بيان عن الشعبي أن الحسن بن علي صلى على علي فكبر عليه أربعا.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (81,'<span data-type="title" id=toc-8>موضع دفن علي رحمة الله عليه</span>
INSERT INTO [b12751] VALUES (82,'٨١- حدثنا الحسين نا عبد الله قال وحدثت عن إبراهيم بن المنذر ⦗٧٣⦘ الحزامي قال حدثني حسين بن زيد قال حدثني جعفر بن محمد عن أبيه قال صلى الحسن بن علي على علي ودفنه بالكوفة عند قصر الإمارة ليلا وغبي دفنه.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (83,'٨٢- حدثنا الحسين نا عبد الله قال حدثنا محمد بن سعد نا محمد بن عمر قال أخبرنا أبو بكر بن عبد الله عن إسحاق بن عبد الله قال قلت لأبي جعفر أين دفن علي قال بالكوفة ليلا وقد غبي دفنه.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (84,'٨٣- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي قال حدثني أبي قال حدثني أبو عبد الله الجعفي عن أبي الطفيل أن الحسن بن علي صلى على علي ودفنه في الرحبة.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (85,'٨٤- حدثنا الحسين نا عبد الله قال حدثني أبي رحمه الله عن هشام بن محمد عن شيخ من الأزد عن عبد الرحمن بن جندب عن أبيه أن الحسن بن علي صلى على علي ودفنه في الرحبة ⦗٧٤⦘ مما يلي أبواب كندة قبل أن ينصرف الناس من صلاة الفجر.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (86,'٨٥- حدثنا الحسين نا عبد الله قال حدثني الحارث بن محمد التميمي نا داود بن المحبر نا المحبر بن قحذم عن مجالد بن سعيد عن الشعبي قال أمر الحجاج بن يوسف ببناء القبة التي بين يدي المسجد بالكوفة فلما حفروا أساسها هجموا على جسد طري فإذا به ضربة على رأسه طرية فلما نظروا إليه قالوا هذا علي بن أبي طالب فأخبر الحجاج بذلك فقال من يخبرني عن هذا فجاءه عدة من مشيخة الكوفة فلما نظروا إليه قالوا هذا علي بن أبي طاب قال فقال الحجاج أبو تراب لأصلبنه قال فقال له ابن أم الحكم أذكرك الله أيها الأمير أن تلقي هذه النائرة بيننا وبين إخواننا من بني هاشم قال فقال له الحجاج فما تخشى أتخشى أن يؤتى جسدك بعد موتك فيستخرج مرهم أن يدفنوك حيث لا يعلم بك قال فقال له ابن أم الحكم والله ما أبالي إذا أتي جسدي فاستخرج جسدي ⦗٧٥⦘ كان أم جسد غيري إذا قيل هذا جسد فلان فأمر الحجاج بحفائر حفرت من النهار ثم أمر بجسد علي فحمل على بعير وأطرافه تدلى فخرج به ليلا فدفن في ناحية أخرى حيث لا يعلم به.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (87,'<span data-type="title" id=toc-9>أمر ابن ملجم وقتله</span>
INSERT INTO [b12751] VALUES (88,'٨٧- حدثنا الحسين نا عبد الله نا سعيد بن يحيى بن سعيد الأموي نا عبد الله بن سعيد عن زياد بن عبد الله نا ابن إسحاق قال حدثني زيد بن عبد الله بن سعد قال حدثني عبد الله بن أبي رافع قال عذبنا ابن ملجم بعد موت علي بكل عذاب خلقه الله فوالله ما تكلم حتى دخل غلام ابتاعه عبد الله بن أبي رافع قبل موت علي فدخل به على علي فقال ما هذا ألا خنيزير قال فألححنا عليه خنيزير فقال خلوا عني وعنه وكان اسم الغلام سعدا فأخذ بأنفه فعضه فصاح صياحا ما سمعنا بمثله قط فقلنا خلوا بينه وبين خنيزير وأخذ عبد الله بن جعفر ابن ملجم فقطع يده ورجله وكحل عينيه بمسمار من حديد فجعل ابن ملجم يقول لابن جعفر إنك لتكحل عمك بململول ممض ثم أمر به فعولج عن لسانه ليقطع فجزع وقبل ذلك ما لم يجزع فقالوا له يا عدو الله قطعنا يديك ورجليك وسملنا عينيك فلم تجزع فلما أن دنا قطع لسانك جزعت قال لا والله ما أجزع من قطع لساني ولكني أجزع أن أكون في الدنيا فواقا لا أذكر الله فيه فقطعوا لسانه ثم حرقوه بالنار وهو حي فقال ابن حطان في ذلك
INSERT INTO [b12751] VALUES (89,'٨٨- حدثنا الحسين نا عبد الله نا سعيد بن يحيى الأموي قال أنشدني أبي لابن حطان في ابن ملجم
INSERT INTO [b12751] VALUES (90,'٨٩- حدثنا الحسين نا عبد الله قال حدثني إبراهيم بن سعيد نا ⦗٧٩⦘ الفضل بن دكين نا حفص بن حمزة القرشي قال سمعت جدتي بكرة بنت كليب عن عبد الله جدي وكان مؤذنا لعلي أن الحسن بن علي أمر بقتل عبد الرحمن بن ملجم فقتل ثم أدرج في بورياء فأحرق.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (91,'٩٠- حدثنا الحسين نا عبد الله قال حدثني إبراهيم بن سعيد نا أبو أحمد نا فطر عن أبي إسحاق قال حدثني رجل دخل على ابن ملجم حين ضرب عليا وقد احترق فصار وجهه أسود.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (92,'٩١- حدثنا الحسين نا عبد الله قال حدثني أبي عن هشام بن محمد عن أبي عبد الله الجعفي عن جابر الجعفي قال حدثني من نظر إلى ابن ملجم حين قدم إلى علي بن أبي طالب فإذا رجل أشهر حسن الوجه أبلج شعره مع شحمة أذنيه مسجد يعني في وجهه أثر السجود.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (93,'٩٢- حدثنا الحسين نا عبدا لله قال أخبرني العباس بن هشام عن أبيه قال حدثني أبو بكر بن عياش قال قدم قوم من اليمن من مراد فيهم ابن ملجم فلما وقفوا بين يدي عمر بن الخطاب قال ممن أنتم قالوا من مراد قال ما رأيت كاليوم وجوها أنكر يعيدها مرارا الحقوا بمصر ⦗٨٠⦘ وكان فيهم سيدان بن حمران الذي ضرب عثمان بالسيف يوم دخل عليه.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (94,'٩٣- حدثنا الحسين نا عبد الله نا المنذر بن عمار بن حبيب بن حسان بن الأشرس الكاهلي قال أخبرني ابن أبي الحثحاث العجلي عن أبيه أبي الحثحاث قال أخبرت عليا بقدوم ابن ملجم فتغير وجهه ثم أتيته به فلما رآه علي قال
INSERT INTO [b12751] VALUES (95,'٩٤- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن ⦗٨١⦘ بكير قال حدثني أبي عن عبيد بن.. .. عن وهب بن عبد الله بن كعب بن سور قال دخل محمد بن الحنفية الحمام فإذا فيه عبد الرحمن بن ملجم جالس فنظر إليه فقال له محمد ممن الرجل قال من مصر قال من أيهم أنت قال من اليمن قال من أيها أنت قال ما أنا بمخبرك فتركه فلما كان من أمر علي ما كان وقتل أخذ عبد الرحمن فحبس في بيت فدخل عليه محمد فقال ألست صاحب الحمام قال بلى قال أما والله ما أنا اليوم بأعرف بك مني يومئذ ثم التفت محمد إلى قوم معه فقال أما إنا لا نعلم الغيب ولكنا علمنا شيئا فعلمنا.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (96,'٩٥- حدثنا الحسين نا عبد الله قال حدثني أبي عن هشام بن محمد عن عبد الله الجعفي عن جابر عن أبي جعفر محمد بن علي قال لما توفي علي رحمه الله أمر الحسن بن علي بابن ملجم فأتي به فضرب ضربة فأندروا أصابعه ثم ثنى فقتله فلما تخوف الحسن من عواقب الضربتين حج ماشيا وقاسم الله ماله ثلاث مرات.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (97,'٩٦- حدثنا الحسين نا عبد الله بن محمد نا إبراهيم بن عبد الله بن ⦗٨٢⦘ حاتم قال أخبرنا هشيم قال أخبرنا حصين عن الشعبي قال حدثني زحر بن قيس الجعفي قال لما كان غداة أصيب علي عليه السلام ركبت مطيتي ومضيت نحو المدائن فلما كنت قريبا منها تلقاني أهلها فقالوا من أين أقبل الرجل فقلت من الكوفة قالوا وما الخبر قلت جرح أمير المؤمنين بصلاة الغداة فتلقاه رجلان فضربه أحدهما فأخطأه وضربه الآخر فأصابه بشجة قد يموت الرجل مما هو أدنى منها ويعيش مما هو أكثر منها فتماروا فيما بينهم فقالوا والله لو جئتنا بدماغه في ستين صرة لعلمنا أنه لا يموت حتى يسوق العرب بعصاه قال فدخلت المدائن فمكثت في بعض بيوتها حتى جاء كتاب الحسن بن علي عليهما السلام بما كان من أمره فاتقوا الله وعليكم بالسمع والطاعة ⦗٨٣⦘ قال وكان اللذان ضرباه عبد الرحمن بن ملجم المرادي وشبيب بن بجرة الأشجعي ضربه شبيب فأخطأه وضربه ابن ملجم على رأسه فقتله وكان الذي ضرب معاوية رجل من بني الصريم يقال له البرك وأن معاوية حرم بني الصريم أعطياتهم حياته.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (98,'٩٧- حدثنا الحسين نا عبد الله نا سعيد بن يحيى القرشي نا عبد الله بن سعيد عن زياد بن عبد الله نا المجالد بن سعيد قال مات علي رضي الله عنه ولم يستخلف أحدا قال فحدثني الشعبي قال أخبرني زحر بن قيس الجعفي قال بعثني علي رضي الله عنه على أربعمئة من أهل العراق فأمرنا أن ننزل المدائن رابطة قال فوالله إنا لجلوس عند غروب الشمس على الطريق إذ جاءنا راكب قد أعرق دابته فقلنا من أين أقبلت قال من الكوفة قلنا متى خرجت قال اليوم قلنا فما الخبر قال خرج أمير المؤمنين إلى الصلاة صلاة الفجر فابتدره ابن بجرة وابن ملجم فضربه أحدهما ضربة إن الرجل ليعيش مما هو أشد منها ويموت مما هو أهون منها ثم ذهب فقال عبد الله بن وهب السبائي ورفع يديه إلى السماء الله أكبر الله أكبر قلت له ما شأنك قال لو أخبرنا هذا أنه نظر إلى دماغه قد خرج عرفت أن أمير المؤمنين لا يموت حتى يسوق العرب بعصاه ⦗٨٤⦘ قال فوالله ما مكثنا إلا تلك الليلة حتى جاءني كتب الحسن بن علي من عبد الله حسن أمير المؤمنين إلى زحر بن قيس أما بعد فخذ البيعة ممن قبلك فقلنا أين ما قلت قال ما كنت أراه يموت.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (99,'٩٨- حدثنا الحسين نا عبد الله قال نا سعيد نا عبد الله بن سعيد عن زياد بن عبد الله نا إسماعيل بن أبي خالد عن أبي إسحاق عن هبيرة بن يريم قال قام الحسن بن علي بعد قتل أبيه فحمد الله وأثنى عليه ثم قال أيها الناس إنه قد فارقكم أمس رجل سبق الأولين ولا يدركه الآخرون وكان رسول الله صلى الله عليه وسلم يبعثه المبعث ويعطيه الراية فما يرجع حتى يفتح الله عليه جبيريل عن يمينه وميكائيل عن شماله ما ترك صفراء ولا بيضاء إلا سبعمئة درهم فضلت من عطائه أراد أن يشتري بها خادما لأهله.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (100,'٩٩- حدثنا الحسين نا عبد الله قال حدثني عبد الله بن يونس بن بكير قال حدثني أبي قال حدثني أبو عبد الله الجعفي عن جابر الجعفي عن عامر الشعبي قال ⦗٨٥⦘ صلى الحسن بن علي صلاة الفجر يوم مات علي عليهما السلام فقال الحمد لله حمدا كثيرا على ما أحببنا وكرهنا إنا لله وإنا إليه راجعون والحمد لله رب العالمين يا قوم إني أحتسب عند الله عز وجل مصابي بأفضل الآباء رسول الله صلى الله عليه وسلم واعلمن يا معشر.. .. أنه قد قبض في هذه الليلة رجل لم يسبقه أحد كان قبله ولم يلحقه بعده مثله وهو علي حبيب رسول الله صلى الله عليه وسلم وأخوه فنحتسب عند الله ما دخل علينا أهل البيت خاصة وما دخل على جميع أمة محمد عامة فوالله لا أقول اليوم إلا حقا لقد دخلت مصيبته على جميع العباد والبلاد والشجر والدواب فنسأل البر الرحيم أن يرحم وجهه وأن يعذب قاتله وأن يحسن علينا الخلافة من بعده.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (101,'١٠٠- حدثنا الحسين نا عبد الله نا يوسف بن موسى نا عبيد الله بن موسى قال أخبرنا سكين بن عبد العزيز نا حفص بن خالد بن جابر عن أبيه عن جده قال لما قتل علي عليه السلام قام الحسن بن علي فحمد الله عز وجل وأثنى عليه ثم قال أما والله لقد قتلتم الليلة رجلا في ليلة نزل فيها القرآن و ⦗٨٦⦘ فيها رفع عيسى ابن مريم عليه السلام وفيها قتل يوشع بن نون فتى موسى عليهما السلام.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (102,'١٠١- حدثنا الحسين نا عبد الله نا علي بن الجعد قال أخبرنا شريك عن عاصم بن أبي النجود عن أبي رزين قال خطبنا الحسن بن علي بعد وفاة أبيه على منبر الكوفة في ثياب سودة.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (103,'١٠٢- حدثنا الحسين نا عبد الله نا أبو مسلم عبد الرحمن بن يونس نا عبد الله بن إدريس قال سمعت إسماعيل بن أبي خالد يذكر ذلك عن أبي إسحاق قال ابن إدريس لا أعلمه إلا عن هبيرة بن يريم أن عليا لما أصيب خطب الحسن بن علي فحمد الله عز وجل وأثنى عليه ثم قال لقد فارقكم بالأمس رجل ما سبقه ⦗٨٧⦘ الأولون ولا يدركه الآخرون إن كان رسول الله صلى الله عليه وسلم يدفع الراية إليه فيمضي وجبريل عن يمينه وميكائيل عن يساره فما يبرح حتى يفتح الله عز وجل عليه وما ترك صفراء ولا بيضاء غير سبعمئة درهم كان أرصدها في خادم.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (104,'١٠٣- حدثنا الحسين نا عبد الله قال حدثني أبي عن هشام بن محمد عن أبي عبد الله الجعفي قال نا عروة بن عبد الله عن زحر بن قيس قال بعثني الحسن بن علي عليهما السلام إلى المدائن وبها حسين بن علي فلما انتهيت إليه قال أي زحر ما لي أرى وجهك متغيرا قلت تركت أمير المؤمنين في آخر يوم من الدنيا وأول يوم من الآخرة وهذا كتاب الحسن إليك قال زحر فلما ذكرت له أمر علي ومصابه قال ويحك من قتله قلت رجل من مراد مارق فاسق يقال له عبد الرحمن بن ملجم قال أقتل الرجل قلت نعم فكبر ثم قال إنا لله وإنا إليه راجعون والحمد لله رب العالمين ما أعظمك من مصيبة مع أن رسول الله صلى الله عليه وسلم قال إذا أصيب أحدكم بمصيبة فليذكر مصابه بي فإنه لن يصاب بمثلها أبدا وصدق رسول الله صلى الله عليه وسلم وما أصيب بعد رسول الله صلى الله عليه وسلم بمثلها ولن نصاب بمثلها في بقية عمري إن البلاء إلينا أهل البيت سريع فالله المستعان ⦗٨٨⦘ فقال زحر إن هاهنا من لا يرى أنه يموت حتى يظهر وأنا أخافهم عليك فاجمعهم إلي حتى أقرأ كتاب الحسن عليهم فنودي في الناس فاجتمعوا وحضر حسين عليه السلام فقمت فقرأت على الناس الكتاب فقال رجل يقال له ابن السوداء من همدان يقال له عبد الله بن سبأ والله لو رأيت أمير المؤمنين في قبره لعلمت أنه لن يذهب حتى بظهر فارتج من عقل بالاسترجاع والبكاء والاستغفار لعلي والتعزية لحسين ثم انصرف راجعا إلى الكوفة في الناس.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (105,'<span data-type="title" id=toc-10>ندب علي ومراثيه صلوات الله عليه</span>
INSERT INTO [b12751] VALUES (106,'١٠٥- حدثنا الحسين نا عبد الله قال حدثني محمد بن أبي يحيى أن شيخا من ضبة يكنى أبا الوليد حدثهم قال حدثني عبد الواحد بن أبي عبد الله الأسدي أن معاوية قال لرجل من كنانة صف لي عليا قال أعفني قال لا أعفيك قال أما إذ لا بد فإنه كان والله بعيد المدى شديد القوى يقول فصلا ويحكم عدلا يتفجر العلم من جوانبه وتنطق الحكمة من نواحيه يستوحش من الدنيا وزهرتها ويأنس بالليل وظلمته كان والله غزير العبرة طويل الفكرة يقلب كفه ⦗٩٠⦘ ويخاطب نفسه يعجبه من اللباس ما قصر ومن الطعام ما جشب كان والله كأحدنا يجيبنا إذا سألناه ويبتدئنا إذا أتيناه ويلبينا إذا دوعناه ونحن والله مع تقريبه لنا وقربه منا لا نكلمه هيبة ولا تبتدئه تعظمة فإن تبسم فعن مثل اللؤلؤ المنظوم يعظم أهل الدين ويحب المساكين لا يطمع القوي في باطله ولا يأيس الضعيف من عدله وأشهد بالله لقد رأيته في بعض مواقفه وقد أرخى الليل سرباله وقد غارت نجومه وقد مثل في محرابه قابضا على لحيته يتململ تململ السليم ويبكي بكاء الحزين فكأني الآن أسمعه وهو يقول يا دنيا يا دنيا إياي أردت أم بي تشوقت هيهات هيهات غري غيري لا حان حينك قد بتتك ثلاثا لا رجعة لي فيك فعمرك قصير وعيشك حقير وخطرك كبير آه من قلة الزاد وبعد السفر ووحشة الطريق قال فبكى معاوية وبكى القوم ثم قال رحم الله أبا حسن كان والله كذلك وكيف حزنك عليه قال حزن والله من ذبح واحدها في حجرها فلا ترقأ عبرتها ولا يسكن حزنها.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (107,'١٠٦- حدثنا الحسين نا عبد الله نا يوسف بن موسى نا جرير عن مغيرة قال لما جيء معاوية بنعي علي بن أبي طالب عليه السلام وهو ⦗٩١⦘ قائل مع امرأته ابنة قرظة في يوم صائف فقال إنا لله وإنا إليه راجعون ماذا فقدوا من العلم والخير والفضل والفقه قالت امرأته بالأمس تطعن في عينيه وتسترجع اليوم عليه قال ويلك لا تدرين ما فقدنا من علمه وفضله وسوابقه.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (108,'١٠٧- حدثنا الحسين نا عبد الله نا يوسف بن موسى نا عمرو بن طلحة القناد نا أسباط بن نصر عن سماك عن حجار بن أبجر قال جاء رجل إلى معاوية فقال سرق ثوبي هذا فوجدته مع هذا فقال لو كان لهذا علي بن أبي طالب.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (109,'١٠٨- حدثنا الحسين نا عبد الله قال حدثني عبد الرحمن بن صالح نا يونس بن بكير عن عنبسة بن الأزهر عن سماك بن حرب قال كان عمر بن الخطاب رضي الله عنه يقول لعلي بن أبي طالب عندما يسأله من الأمر فيفرجه عنه لا أبقاني الله بعدك يا أبا الحسن.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (110,'١٠٩- حدثنا الحسين نا عبد الله قال حدثني مهدي بن حفص نا ⦗٩٢⦘ عبدة بن سليمان عن عبد الملك بن أبي سليمان قال قلت لعطاء أكان أحد من أصحاب رسول الله صلى لله عليه وسلم أفقه من علي قال لا والله ما علمته.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (111,'١١٠- حدثنا الحسين نا عبد الله نا أحمد بن حاتم الطويل نا محمد بن الحجاج عن مجالد عن الشعبي عن قبيصة بن جابر قال ما رأيت أزهد في النساء من علي بن أبي طالب عليه السلام.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (112,'١١١- حدثنا الحسين نا عبد الله نا علي بن الجعد قال سمعت الحسن بن حي قال تذاكروا زهاد أصحاب رسول الله صلى الله عليه وسلم عند عمر بن عبد العزيز فقال بعضهم عمر وقال بعضهم ⦗٩٣⦘ فلان فقال عمر بن عبد العزبز علي عليه السلام.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (113,'١١٢- حدثنا الحسين نا عبد الله قال حدثني أبو حفص الصيرفي نا يحيى بن سعيد القطان نا عبد العزيز بن سياه قال حدثني أبو راشد قال أتيت عليا عليه السلام بالكوفة فقلت يا أمير المؤمنين فأجابني يا لبيكاه يا لبيكاه.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (114,'١١٣- حدثنا الحسين نا عبد الله قال حدثني أبو زيد النميري قال حدثني أبو غسان محمد بن يحيى بن علي الكناني قال حدثني عبد العزيز بن عمران الزهري قال قال محمد بن علي عليه السلام ليزيد بن معاوية وذكر يزيد عليا عليه السلام يا يزيد بن معاوية بن صخر إن عليا كان ⦗٩٤⦘ بينهم من مرامي الله عز وجل على عدوه يهوعهم مآكلهم آخذا بحناجرهم يمنعهم مآكل السوء ويلج عنهم بشظف المعيشة قال أبو بكر بقسوة المعيشة حتى صار أصغر عند كبرائهم من أمة لكعاء فنبزوه بالضعيف يعني بقول العطية ورموه بفريقة الأباطيل فيجئ على ثبج من أمره ومرأى من القوم ومرقبا من أنجمه ينوء بجبهة من الأنصار والأعوان خوفا إن يكن لنا منكم دولة نبر عظامكم ونحسم أمركم فإن المقاتل بادية والأستار عارية وليس لنا دون مقادير الحتوف حيلة: {وسيعلم الذين ظلموا أي منقلب ينقلبون} .',NULL,'93',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (115,'١١٤- حدثنا الحسين نا عبد الله نا إبراهيم بن بشار نا نعيم بن مورع نا هشام بن حسان قال بينا نحن عند الحسن البصري إذ أتاه رجل فقال يا أبا سعيد إن الناس يزعمون أنك تنقص عليا عليه السلام فقال رحم الله عليا إن عليا كان سهما لله عز وجل في أعدائه وكان ⦗٩٥⦘ في محلة العلم أشرفها وأقربها من رسول الله صلى الله عليه وسلم وكان رهباني هذه الأمة لم يكن لمال الله عز وجل بالسروقة ولا في أمر الله عز وجل بالنؤومة أعطى القرآن عزائمه فيما عليه وله فكان منه في رياض مونقة وأعلام بينة ذلك علي يا لكع.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (116,'١١٥- حدثنا الحسين نا عبد الله قال حدثني أبو علي أحمد بن الحسن الضرير هشام بن محمد عن الوليد بن وهب الحارثي عن يزيد بن عمرو التميمي قال لما توفي علي بن أبي طالب عليه السلام قام رجل من بني تميم كان على حرسه في مسجد الكوفة بعد ما صلوا عليه فقال رحمك الله يا أمير المؤمنين فلئن كان حياتك مفتاح خير ومغلاق شر وكنت للناس علما منيرا يعرف به الهدى من الضلالة والخير من الشر إن وفاتك لمفتاح شر ومغلاق خير وإن فقدانك لحسرة وندامة ولو أن الناس قبلوك بقبولك لأكلوا من فوقهم ومن تحت أرجلهم ولكنهم اختاروا الدنيا على الآخرة فأصبحو بعدك حيارى في سبل المطالب قد غلب عليهم الشقاء والداء العياء فهم ينتقضونها كما ينقض الحبل مريرته فتبا لهم خلقا تقبلوا سحقا وباعوا كثيرا بقليل وجزلا بيسير فكرم الله ما بك وضعف ثوابك وعليك السلام ورحمة الله وبركاته.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (117,'١١٦- حدثنا الحسين نا عبد الله قال حدثني عبد الرحمن بن صالح نا إبراهيم بن هراسة عن محمد بن سلمة النصيبي قال قالت أم الهيثم بنت العريان حين قتل علي بن أبي طالب عليه السلام
INSERT INTO [b12751] VALUES (118,'١١٧- حدثنا الحسين نا عبد الله قال حدثني سليمان بن أبي شيخ قال أنشدني محمد بن الحكم لأبي زبيد الطائي يرثي عليا
INSERT INTO [b12751] VALUES (119,'١١٨- حدثنا الحسين نا عبد الله نا أحمد بن إبراهيم نا محمد بن ربيعة قال حدثني أبو طلق القرشي قال حدثتني جدتي قالت كنت أنوح أنا وأم كلثوم بنت علي على علي عليه السلام.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (120,'١١٩- حدثنا الحسين نا عبد الله قال حدثني.. . بن خليفة الخزاعي نا أبو يحيى التيمي عن عمر بن عبد الله عن الزهري قال بعث إلي عبد الملك بن مروان فقال لي ما كان آية قتل علي عليه السلام صبيحة قتل قلت كان آية قتله صبيحة قتل أنه لم يقلب حجر بالجابية إلا عن دم عبيط فقال لي صدقت أما إنه لم يبق أحد يعلم هذا غير وغيرك.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (121,'١٢٠- حدثنا الحسين نا عبد الله قال حدثني إبراهيم بن عبد الله ⦗٩٨⦘ قال أخبرنا هشيم قال أخبرنا أبو معشر عن محمد بن عبد الله بن سعيد بن العاص عن الزهري قال قال لي عبد الملك بن مروان أي علامة كانت يوم قتل علي عليه السلام قال قلت لم ترفع حصاة ببيت المقدس إلا وجد تحتها دم عبيط فقال إني وإياك في هذا لغريبان.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (122,'<span data-type="title" id=toc-11>ولد علي بن أبي طالب عليه وعليهم السلام</span>
INSERT INTO [b12751] VALUES (123,'١٢٢- حدثنا الحسين نا عبد الله نا إبراهيم بن عبد الله الهروي قال أخبرنا الفضل بن موسى عن فطر عن منذر عن محمد بن علي عن علي عليه السلام قال قال رسول الله صلى الله عليه وسلم لا تجمعوا بين اسمي وكنيتي فقلت يا رسول الله إن ولد لي بعدك ولد أسميه باسمك وأكنيه بكنيتك قال نعم فولد له فسماه محمدا وكناه أبا القاسم.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (124,'١٢٣- حدثنا الحسين نا عبد الله نا إبراهيم بن عبد الله قال أخبرنا هشيم قال أخبرنا مغيرة عن إبراهيم قال كان محمد بن الحنفية يكنى أبا القاسم وكان محمد بن الأشعث يكنى أبا القاسم وكان يدخل على عائشة قال وأحسبها كانت تكنيه.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (125,'١٢٤- حدثنا الحسين نا عبد الله نا إسماعيل بن زكريا عن يزيد يعني ابن أبي زياد قال قلت لمحمد بن الحنفية متى ولدت قال لثلاث سنين بقين من خلافة عمر رضي الله عنه.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (126,'١٢٥- حدثنا الحسين نا عبد الله نا محمد بن سعد قال أخبرنا ⦗١٠١⦘ محمد بن عمر قال أخبرنا علي بن عمر بن علي بن حسين عن عبد الله بن محمد بن عقيل قال سمعت محمد بن الحنفية يقول سنة الجحاف حين دخلت إحدى وثمانون هذه لي ست وستون سنة قد جاوزت سن أبي قال قلت وكم كانت سنه يوم قتل قال ثلاث وستون سنة ومات أبو القاسم محمد بن الحنفية في تلك السنة',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (127,'رَجعٌ إلى حديث الزبير:
INSERT INTO [b12751] VALUES (128,'١٢٧- حدثنا الحسين نا عبد الله قال قال زبير وحدثني محمد بن سلام قال قلت لعيسى بن عبد الله بن محمد بن عمر بن علي بن أبي طالب كيف سمى جدك علي عمر قال سألت عن ذلك أبي فأخبرني عن أبيه عن عمر بن علي قال ولدت لأبي بعد ما استخلف عمر بن الخطاب رضي الله عنه فقال له يا أمير المؤمنين ولد لي الليلة غلام قال هبه لي قال فقلت هو لك قال قد سميته عمر ونحلته غلامي مورق قال فله الآن ولد كثير بينبع.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (129,'* والعباس الأكبر بن علي:
INSERT INTO [b12751] VALUES (130,'١٢٩- حدثنا الحسين نا عبد الله نا خالد بن خداش نا حماد بن زيد عن أيوب عن محمد أن أسماء ولدت لجعفر محمدا ولأبي بكر محمدا ولعلي محمدا.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (131,'١٣٠- حدثنا الحسين نا عبد الله نا محمد بن سلام الجمحي قال سمعت عباد بن مسلم يحدث عن قتادة قال استبق بنو أسماء الثلاثة ابن جعفر وابن أبي بكر وابن علي فسبق الأكبران ابن جعفر وابن أبي بكر وثنى ابن علي فقالت أسماء لئن سبقاك ما سبق آباؤهما أباك قال ثم أخذ قتادة يقول لم يكن علي رضي الله عنه مثلهما وعنده رجل من أهل الكوفة فقال يا عمي حدثنا بما سمعت ودعنا من رأيك
INSERT INTO [b12751] VALUES (132,'١٣١- حدثنا الحسين نا عبد الله قال قال الزبير قال عمي وإخوتهما لأمهما بنو يزيد بن عتبة بن أبي سفيان بن حرب بن أمية وقال غير عمي أختهما.. بنت لعنبسة بن أبي سفيان بن حرب بن أمية
INSERT INTO [b12751] VALUES (133,'١٣٢- كانت رقية الكبرى بنت علي عند مسلم بن عقيل فولدت له عبد الله قتل بالطف وعليا ومحمد ابني مسلم بن عقيل وقد انقرض ولد مسلم بن عقيل.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (134,'١٣٣- وكانت زينب الصغرى بنت علي عند محمد بن عقيل بن أبي طالب فولدت له عبد الله الذي يحدث عنه وفيه العقب من ولد عقيل وعبد الرحمن والقاسم ابني محمد ثم خلف عليها كثير بن العباس فولدت له كلثم تزوجها جعفر بن تمام بن العباس وقد انقرض ولد كثير وتمام ابني العباس بن عبد المطلب.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (135,'١٣٤- وكانت أم هانئ بنت علي عند عبد الله الأكبر بن عقيل فولدت له محمدا قتل بالطف وعبد الرحمن ومسلما وأم كلثوم.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (136,'١٣٥- وكانت ميمونة بنت علي عند عبد الله الأكبر بن عقيل فولدت له عقيلا.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (137,'١٣٦- وكنت أم كلثوم الصغرى واسمها نفيسة عند عبد الله الأكبر بن عقيل فولدت له أم عقيل ثم خلف عليها كثير بن العباس بعد زينب الصغرى فولدت له الحسن ثم خلف عليها تمام بن العباس فولدت له نفيسة تزوجها عبد الله بن علي بن الحسين بن علي بن أبي طالب.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (138,'١٣٧- وكانت خديجة بنت علي عند عبد الرحمن بن عقيل فولدت له سعيدا وعقيلا ثم خلف عليها أبو السنابل عبد الرحمن بن عبد الله بن عامر بن كريز بن ربيعة بن حبيب بن عبد شمس.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (139,'١٣٨- وكانت فاطمة بنت علي عند محمد بن أبي سعيد بن عقيل فولدت له حميدة ثم خلف عليها سعيد بن الأسود بن أبي البختري فولدت له برة وخالدة ثم خلف عليها المنذر بن عبيدة بن الزبير بن العوام فولدت له عثمان وكثيرة درجا.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (140,'١٣٩- وكانت أمامة بنت علي عند الصلت بن عبد الله بن نوفل بن الحارث بن عبد المطلب فولدت له نفيسة وتوفيت عنده فهؤلاء ولد علي بن أبي طالب عليه السلام لصلبه.
INSERT INTO [b12751] VALUES (141,'* أنا أبو الحسين محمد بن عبد الله ابن أخي ميمي قال أنا الحسين بن صفوان البرذعي إملاء قال نا أبو بكر بن أبي الدنيا قال نا أبو بكر العمري قال حدثي إسماعيل بن أبي أويس عن ابن أبي فديك قال بلغني أن سليمان النبي صلى الله عليه كان جالسا فرأى عصفورا يريد عصفورة على السفاد وهي تمتنع فضرب بمنقاره إلى الأرض ثم رفعه إلى السماء قال سليمان هل تدرون ما قال لها قالوا الله ورسوله أعلم فقال قال لها ورب السماوات والأرض ما أريد سفدا لك ولكني أردت أن يكون من نسلي ونسلك من يسبح الله عز وجل في الأرض.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (142,'* نا الحسين قال نا أبو بكر بن أبي الدنيا قال نا محمد بن الحسين قال حدثني محمد بن حسان قال نا أحمد بن عمرو الكندي قال دخلنا على مجوسي نعوده فقال بعض أصحابنا كيف تجدك يا مجوسي قال ما ظنك بمن يصير إلى قبر موحش ليس فيه مؤنس ويريد سفرا بعيدا بغير زاد ويقدم على ملك عادل بغير حجة فلم يلبث أن مات.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b12751] VALUES (143,'<span data-type="title" id=toc-13>ولدُ علي بن أبي طالب</span>
