INSERT INTO [b127700] VALUES (469,'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ','2','2',NULL,NULL,'0');
INSERT INTO [b127700] VALUES (470,'الرَّصْفُ لِما رُويَ عَنِ النّبِيّ - صلى الله عليه وسلم - مِنَ الفِعْلَ وَالوَصْفِ
