INSERT INTO [b127710] VALUES (1,'المطلع على دقائق زاد المستقنع
«فقه القضاء والشهادات»

تأليف
أ. د/ عبد الكريم بن محمد اللاحم

[المجلد الأول]','1',NULL,NULL,NULL,'0');
INSERT INTO [b127710] VALUES (2,'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ
