INSERT INTO [b12774] VALUES (1,'ـ[وصايا الآباء للأبناء]ـ
المؤلف: وائل حافظ خلف
الناشر: طُبع بدار جنا بالإسكندرية - دار الدعوة بمحرم بك - دار ابن القيم بالإسكندرية
عدد الأجزاء: ١
[الكتاب مرقم آليا غير موافق للمطبوع]',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b12774] VALUES (2,'المنتخب مِن

وصايا الآباء للأبناء

بقلم

وائل بن حافظ بن خلف
