-- Insert data for b12824
INSERT INTO [b12824] VALUES (1,'أنموذج اللبيب في خصائص الحبيب
تأليف
الإمام العلامة جلال الدين السيوطي
المتوفي سنة٩١١ هـ
بسم الله الرحمن الرحيم
وبالله العظيم نستعين',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b12824] VALUES (2,'الحمدُ لله الذي أتقن بحكمته كل شيء فاحتبك، وبعث حبيبه محمداً صلى الله عليه وآله وسلم فأنار به كل حلك، وآتاه من المعجزات والخصائص ما لم يُؤتَه نبي ولا مَلَك،',NULL,'15',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (3,'وجعل جنده الملائكة تسير معه حيث سَلَك، صلى الله عليه وعلى آله وصحبه ما سار فُلك ودار فَلك.
هذا أنموذج لطيف، وعنوان شريف، لخصته من كتابي الكبير الذي جمعت فيه المعجزات والخصائص النبوية بدلائلها،',NULL,'16',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (4,'وتتبعت فيه الأحاديث الواردة في منصب النبوة وعظيم فضائلها،',NULL,'17',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (5,'قصرته على إيراد الخصائص سرداً وجيزاً، وميزت فيه كل نوع من أنواعها تمييزاً، وسميته:
أنموذج اللبيب في خصائص الحبيب صلى الله عليه وعلى آله وسلم
وما توفيقي إلا بالله عليه توكلت وإليه أنيب، وينحصر في بابين:

<span data-type="title" id=toc-1>الباب الأول
في الخصائص التي اختص بها عن جميع الأنبياء ولم يؤتها نبي قبله</span>
وفيه أربعة فصول',NULL,'18',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (6,'<span data-type="title" id=toc-2>الفصل الأول</span>
فيما اختص به صلى الله عليه وآله وسلم في ذاته من الدنيا

... اُختص صلى الله عليه وآله وسلم بأنه أول النبيين خلقاً، وبتقدم نبوته، فكان نبياً وآدم',NULL,'19',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (7,'بين الماء والطين، وبتقدم أخذ الميثاق عليه، وأنه أول من قال: بلى يوم ((ألست بربكم)) ،',NULL,'20',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (8,'وخلق آدم وجميع المخلوقات لأجله، وكتابة اسمه الشريف على العرش، وكل سماء وما فيها والجنان وسائر الملكوت، وذكر الملائكة له في كل ساعة،',NULL,'21',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (9,'وذكر اسمه في الأذان في عهد آدم وفي الملكوت الأعلى،',NULL,'22',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (10,'وأخذ الميثاق على النبيين آدم فمن بعده أن يؤمنوا به وينصروه،',NULL,'23',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (11,'والتبشير به في الكتب السابقة، ونعته فيها ونعت أصحابه',NULL,'24',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (12,'وخلفائه وأمته،',NULL,'25',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (13,'وحجب إبليس من السموات لمولده وشق صدره في أحد القولين، وهو الأصح،',NULL,'26',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (14,'وجعل خاتم النبوة بظهره بإزاء قلبه حيث يدخل الشيطان، وسائر الأنبياء كان الخاتم في يمينهم، وبأن له ألف إسم،',NULL,'27',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (15,'وباشتقاق اسمه من اسم الله، وبأنه سمي من أسماء الله بنحو سبعين اسماً، وبأنه سُمي أحمد ولم يُسم به أحد قبله،',NULL,'28',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (16,'وقد عُدَّت هذه الخصائص في حديث "مسلم".
وبإظلال الملائكة له في سفره، وبأنه أرجح الناس عقلاً، وبأنه أوتي كل الحسن',NULL,'29',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (17,'ولم يؤت يوسف إلا شطره، وبغطه ثلاثاً عند ابتداء الوحي، وبرؤيته جبريل في صورته التي خلق عليها، عد هذه "البيهقي".
وبانقطاع الكهانة لمبعثه،',NULL,'30',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (18,'وحراسة السماء من استراق السمع، والرمي بالشهب، عدّ هذه "ابن سبع".',NULL,'31',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (19,'وبإحياء أبويه له حتى آمنا به،',NULL,'32',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (20,'وبوعده بالعصمة من الناس، وبالإسراء وما تضمنه من اختراق السموات السبع والعلو إلى قاب قوسين،',NULL,'33',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (21,'ووطئه مكاناً ما وطئه نبي مرسل، ولا مَلَكٌ مُقرب، وإحياء الأنبياء له',NULL,'34',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (22,'وصلاته إماماً بهم وبالملائكة، وإطلاعه على الجنة والنار، عدَّ هذه "البيهقي".
ورؤيته من آيات ربه الكبرى،',NULL,'35',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (23,'وحفظه حتى ما زاغ البصر وما طغى، وبرؤيته للباري تعالى مرتين، وبركوب البراق',NULL,'36',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (24,'في أحد القولين، وقتال الملائكة معه، وسيرهم معه حيث سار يمشون خلف ظهره، وبإيتائه الكتاب وهو أمي',NULL,'37',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (25,'لا يقرأ ولا يكتب، وبأن كتابه مُعجِزٌ ومحفوظ من التبديل والتحريف على ممر الدهور، ومشتمل على ما اشتملت عليه جميع الكتب وزيادة، وجامع لكل شيء،',NULL,'38',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (26,'ومستغن عن غيره، ومُيسر للحفظ، ونزل منجماً، وعلى سبعة أحرف، ومن سبعة أبواب،',NULL,'39',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (27,'وبكل لغة، عدَّ هذه "ابن نقيب".
وقراءته بكل حرف عشر حسنات، عدَّ هذه "الزركشي".
وقال صاحب التحرير: فُضِّل القرآن على سائر الكتب المنزلة بثلاثين خصلة لم تكن في غيره.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (28,'وقال الحليمي في "المنهاج": ومن عظيم قدر القرآن أن الله خصه بأنه دعوةٌ وحُجةٌ ولم يكن هذا لنبي قط، إنما كان يكون لكل منهم دعوة ثم تكون له حُجةٌ غيرها، وقد جمعهما الله لرسوله صلى الله عليه وآله وسلم في القرآن، فهو دعوة بمعانيه، حُجةٌ بألفاظه،',NULL,'41',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (29,'وكفى الدعوة شرفاً أن تكون حُجتها معها، وكفى الحُجة شرفاً أن لا تنفصل الدعوة عنها، وأعطي من كنز العرش ولم يُعط منه أحدٌ، وخُصَّ بالبسملة والفاتحة وآية الكرسي وخواتيم سورة البقرة',NULL,'42',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (30,'والسبع الطوال والمفصل،',NULL,'43',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (31,'وبأن معجزته مستمرةإلى يوم القيامة وهي القرآن، ومعجزات سائر الأنبياء عليه وعليهم الصلاة والسلام انقرضت لوقتها، وبأنه أكثر الأنبياء معجزات، فقد قيل أنها تبلغ ألفاً، وقيل: ثلاثة آلاف سوى القرآن فإن فيه ستين ألف معجزة تقريباً.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (32,'قال الحليمي: وفيها مع كثرتها معنى آخر، وهو أنه ليس في شيء من معجزات غيره ما ينحو نحو اختراع الأجسام، وإنما ذلك في معجزات نبينا صلى الله عليه وآله وسلم خاصة، وبأنه جمع له كل ما أوتيه الأنبياء من معجزات وفضائل ولم يجمع ذلك لغيره، بل اختص كُلٍ بنوع.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (33,'وأوتي انشقاق القمر، وتسليم الحجر،',NULL,'46',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (34,'وحنين الجذع، ونبع الماء من بين الأصابع، ولم يثبت لواحد من الأنبياء مثل ذلك. ذكر هذه "ابن عبد السلام".
وقال بعضهم: خص الله تعالى بعضاً بالمعجزات في الأفعال كموسى، وبعضاً بالصفات كعيسى، ونبينا صلى الله عليه وآله وسلم بالمجموع ليميزه،',NULL,'47',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (35,'وبكلام الشجر وشهادتها له بالنبوة وإجابتها دعوته، وإحياء الموتى وكلامهم، وكلام الصبيان في',NULL,'48',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (36,'المراضع، وشهادتهم له بالنبوة. ذكر ذلك "البدر الدماميني".
وبأنه خاتم النبيين وآخرهم بعثاً فلا نبي بعده، وشرعه مؤبد إلى يوم القيامة لا يُنسخ، وناسِخٌ لجميع الشرائع قبله،',NULL,'49',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (37,'ولو أدركه الأنبياء لوجب عليهم الاتباع، وفي كتابه وشرعه الناسِخ والمنسوخ، وبعموم الدعوة للناس كافة، وأنه أكثر الأنبياء تابعاً.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (38,'وقال السبكي: أُرسل للخلق كافة من لدن آدم، والأنبياء نواب له بعثوا بشرائع له مُعْنِيَّات، فهو نبي الأنبياء، وأرسل إلى الجن بالإجماع، وإلى الملائكة في أحد القولين، ورجحه السبكي.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (39,'قال البارزي: وإلى الحيوانات والجمادات والحجر والشجر، وبعثه رحمة للعالمين حتى الكفار بتأخير العذاب، ولم يعاجلوا بالعقوبة كسائر الأمم المكذبة،',NULL,'52',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (40,'وبأن الله أقسم بحياته، وأقسم على رسالته، وتولى الرد على أعدائه عنه،',NULL,'53',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (41,'وخاطبه بألطف ما خاطب به الأنبياء، وقرن اسمه باسمه في كتابه،',NULL,'54',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (42,'وفرض على العالم طاعته، والتأسي به، فرضاً مطلقاً لا شرط فيه ولا استثناء، ووصفه في كتابه عضواً عضواً،',NULL,'55',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (43,'ولم يخاطبه في القرآن باسمه،',NULL,'56',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (44,'بل ((يا أيها النبي)) ((يا أيها الرسول)) وحرم على الأمة نداءه باسمه.
وكره الشافعي أن نقول في حقه: الرسول، بل: رسول الله، لأنه ليس فيه من التعظيم ما في الإضافة.
وفَرَض على من ناجاه أن يقدم بين يدي نجواه صدقة ثم نسخ بعد ذلك،',NULL,'57',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (45,'ولم يُرِه في أمته شيئاً يسوؤه حتى قبضه الله تعالى، بخلاف سائر الأنبياء، وبأنه حبيب الرحمن، وجمع له بين المحبة والخِلَّة، وبين الكلام والرؤية، وكلَّمه عند سدرة المنتهى وكلم موسى بالجبل، عدَّ هذه "ابن عبد السلام".
وجمع له بين القبلتين:',NULL,'58',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (46,'مكة وبيت المقدس، والهجرتين: بيت المقدس والمدينة، وجمع له بين الحكم بالظاهر والباطن، وجمعت له الشريعة والحقيقة، ولم يكن للأنبياء إلا أحدهما بدليل قصة موسى مع الخضر',NULL,'59',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (47,'وقوله: "إني على علم لا ينبغي لك أن تعلمه، وأنت على علم لا ينبغي لي أن أعلمه".',NULL,'60',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (48,'ونصر بالرعب من مسيرة شهرٍ أمامه وشهر خلفه، وأوتي جوامع الكلم، وأوتي مفاتيح خزائن الأرض على فرس أبلق، عليه قطيفة من سندس،',NULL,'61',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (49,'وكلمه بجميع أصناف الوحي. عدَّ هذه "ابن عبد السلام".
وهبط إسرافيل عليه ولم يهبط على نبي قبله. عدَّ هذه "ابن سبع".
وجمع له بين النبوة والسلطان، عدَّ هذه الغزالي في "الإحياء".
وأوتي علم كل شيء إلا الخمس التي في سورة لقمان، وهي قوله تعالى: (إن الله عنده علم الساعة',NULL,'62',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (50,'وينزل الغيث ويعلم ما في الأرحام وما تدري نفس ماذا تكسب غداً وما تدري نفس بأي أرض تموت) .
وقيل: إنه أوتيها أيضاً وأمر بكتمها، والخلاف جار في الروح أيضاً، وبُيّن له من أمر الدجال ما لم يُبيَّن لأحد.
وَوُعِد بالمغفرة وهو يمشي حياً صحيحاً،',NULL,'63',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (51,'ورُفِع ذِكره، فلا يذكر الله جل جلاله في أذان ولا خطبة ولا تشهد إلا ذُكِر معه.
قال ابن عباس: ما أمَّن الله أحداً من خلقه إلا محمداً صلى الله عليه وآله وسلم فقال: (ليغفر لك الله ما تقدم من ذنبك وما تأخر) .
وقال للملائكة: (ومن يقل منهم إني إله من دونه فذلك نجزيه جهنم) .
وقال عمر بن الخطاب: ما تدري نفس ماذا مفعول بها ليس هذا الرجل الذي قد بُين لنا أنه قد غفر له ما تقدم من ذنبه وما تأخر صلى الله عليه وآله وسلم أخرجه "الحاكم".
وعرض عليه أمته بأسرهم حتى رآهم،',NULL,'64',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (52,'وعرض عليه ما هو كائن في أمته حتى تقوم الساعة.
قال الإسفرائيني: وعرض عليه الخلق كلهم من آدم فمن بعده، كما علم آدم أسماء كل شيء.
وهو سيد ولد آدم، وأكرم الخلق على الله،',NULL,'65',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (53,'وأفضل من سائر المرسلين وجميع الملائكة المقربين، وكان أفرسَ العالمين، عدَّ هذه "ابن سراقة".
وأُيِّدَ بأربع وزراء: جبريل، وميكائيل، وأبي بكر، وعمر.
وأُعطي من أصحابه أربعة عشر نجيباً، وكل نبي أعطي سبعة، وأسلم قرينه.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (54,'وكان أزواجه عوناً له، وبناته وزوجاته أفضل نساء العالمين،',NULL,'67',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (55,'وثواب أزواجه وعقابهن مُضاعف.
وأصحابه أفضل العالمين إلا النبيين، ويقاربون عدد الأنبياء، وكلهم مجتهدون، ولهذا قال صلى الله عليه وآله وسلم: "أصحابي كالنجوم بأيهم اقتديتم اهتديتم".',NULL,'68',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (56,'ومسجده أفضل المساجد، وبلده أفضل البلاد بالإجماع فيما عدا مكة، وعلى أحد القولين فيها وهو المختار.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (57,'وتربتها مؤمنة، وغبارها يطفئ الجذام، ونصف أكراش الغنم فيها مثل مثليها في غيرها من البلاد.
ولا يدخلها الدجال ولا الطاعون، وصرف الحُمَّى عنها أول ما قدمها ونقلها إلى الجُحفة، ثم لما أتاه جبريل بالحمى والطاعون أمسك الحمى بالمدينة',NULL,'70',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (58,'وأرسل الطاعون إلى الشام، ولما عادت الحمى إلى المدينة باختياره إياها لم تستطع أن تأتي أحداً من أهلها حتى جاءت ووقفت ببابه واستأذنته فيمن يبعثها إليه، فأرسلها إلى الأنصار.
واُحِلت له مكة ساعة من نهار، وحُرِّم ما بين لابتي المدينة،',NULL,'71',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (59,'وقال الماوردي والقاضي عياض: لا تقتل حيّات مدينة النبي صلى الله عليه وآله وسلم إلا بالإنذار، والحديث الوارد في إنذار الحيات خاص بها.
ويسأل عنه الميت في قبره، واستأذن ملك الموت عليه ولم يستأذن على نبي قبله.
وحرم نكاح أزواجه من بعده،',NULL,'72',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (60,'وأَمَةٌ وَطِئَها، والبقعة التي دفن فيها أفضل من الكعبة ومن العرش.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (61,'ويُحرم التكني بكنيته، وقيل: التسمي باسمه محمداً صلى الله عليه وآله وسلم، قيل والتسمي بالقاسم، لئلا يُكنى أبوه أبا القاسم. حكاها النووي في "شرح مسلم".
ويجوز أن يقسم على الله به وليس ذلك لأحد. ذكر هذه "ابن عبد السلام".',NULL,'74',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (62,'ولم تُرَ عورته قط، ولو رآها أحدٌ طُمست عيناه، ولا يجوز عليه الخطأ. عدَّ هذه "ابن أبي هريرة" و "الماوردي".
قال قوم: ولا النسيان. حكاه النووي في "شرح مسلم".
وذكر البارزي في "توثيق عرى الإيمان":
من خصائصه أنه جامع لخواص الأنبياء، وأنه نبي الأنبياء، وأنه مامن نبي له خاصة نبوة في أمته، إلا وفي هذه الأمة عالم من علمائها يقوم',NULL,'75',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (63,'في قومه مقام ذلك النبي في أمته، ويَنحو منحاه في زمانه، ولهذا ورد: "علماء أمتي كأنبياء بني إسرائيل".
وورد أن العالم في قومه كالنبي في أمته.
قال: ومن خواصه أن سماه الله عبد الله، ولم يطلقها على أحد سواه، وإنما قال: (إنه كان عبداً شكوراً) ، (نعم العبد) .
ومن خواصه: أنه ليس في القرآن ولا غيره صلاة من الله تعالى على غيره فهي خصيصة اختصه الله بها دون سائر الأنبياء. انتهى.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (64,'وأسماؤه توقيفية، كأسماءالله تعالى، جزم به في "الأربعين الطائية". انتهى.

<span data-type="title" id=toc-3>الفصل الثاني</span>
فيما اختص به صلى الله عليه وآله وسلم في شرعه في أمته في الدنيا

اختَصَّ صلى الله عليه وآله وسلم بإحلال الغنائم، وجعل الأرض كلها مسجداً، ولم تكن الأمم تصلي إلا في البيَع والكنائس، والتراب طهوراً وهو التيمم، وبالوضوء في أحد القولين',NULL,'77',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (65,'وهو الأصح، فلم يكن إلا للأنبياء دون أممهم.
وعبارة ابن سراقة في "الأعداد": خص بكمال الوضوء والتيمم وبمسح الخف، وجعل الماء مزيلاً للنجاسة، وأن كثير الماء لا تؤثر فيه النجاسة، والاستنجاء بالجامد. ذكر ذلك أبو سعيد النيسابوري في "شرف المصطفى" وابن سراقة في "الأعداد".
وبالجمع فيه بين الماء',NULL,'78',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (66,'والحجر، وبمجموع الصلوات الخمس ولم تجمع لأحد وبأنهن كفارات لما بينهن، وبالعِشاءَ ولم يصلها أحد، وبالأذان والإقامة، وافتتاح الصلاة بالتكبير، وبالتأمين',NULL,'79',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (67,'وبالركوع فيما ذكره جماعة من المفسرين.
وبقول: اللهم ربنا لك الحمد، وبتحريم الكلام في الصلاة، وباستقبال الكعبة،',NULL,'80',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (68,'وبالصَفِ في الصلاة كصفوف الملائكة، وبالجماعة في الصلاة كما يفهم من كلام ابن فرشته في "شرح المجمع".
وبتحية السلام، وهي تحية الملائكة وأهل الجنة، وبيوم الجمعة عيداً له ولأمته، وبساعة الإجابة، وبعيد الأضحى.
وذكر أبو سعيد في "شرف المصطفى"، وابن سراقة، أنه خص بصلاة الجمعة وصلاة الجماعة،',NULL,'81',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (69,'وصلاة الليل، وصلاة العيدين والكسوفين والاستستقاء والوتر. انتهى.
وبقصر الصلاة في السفر، وبالجمع بين الصلاتين في السفر والمطر وفي المرض في أحد القولين وهو المختار.
وبصلاة الخوف، فلم تشرع لأحد من الأمم قبلنا، وبصلاة شدة الخوف عند شدة القتال، أينما وحيثما توجه، وبشهر رمضان، عدَّ هذه القونوي في "شرح التعرف".',NULL,'82',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (70,'وأن الشياطين تصفد فيه، وأن الجنة تزين فيه، وأن خَلُوفَ فم الصائم عند الله أطيب من ريح المسك، وتستغفر لهم الملائكة حتى يفطروا، ويغفر لهم في آخر ليلة منه، وبالسحور وتعجيل الفطر',NULL,'83',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (71,'وإباحة الأكل والشرب والجماع ليلاً إلى الفجر، وكان محرماً على من قبلنا بعد النوم، وكذا كان في صدر الإسلام ثم نُسِخ، وبتحريم الوصال في الصوم، وكان مباحاً لمن قبلنا، وبإباحة الكلام في الصوم، وكان محرماً على من قبلنا، عكس الصلاة. عدَّ هذه ابن العربي في "الأحوذي".
وبليلة القدر،',NULL,'84',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (72,'كما قاله النووي في "شرح المهذب". وبجعل صوم يوم عرفة بكفارة سنتين لأنه سُنَّته، وصوم عاشوراء كفارة سنة، لأنة سُنَّة موسى عليه السلام. ويوم عرفة ذكرها القونوي في "شرح التعرف".
وغسل اليدين بعد الطعام بحسنتين، لأنه شرعُه. وقبله بحسنة لأنه شرع في التوراة.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (73,'وبالاستغسال من العين وأنه يدفع ضررها. وبالاسترجاع عند المصيبة، وبالحوقلة، وباللحد (في القبر) ، ولأهل الكتاب الشق، وبالنحر، ولهم الذبح فيما قاله مجاهد وعكرمة.
وبفرق الشعر، ولهم',NULL,'86',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (74,'السدل، وبصبغ الشعر، وكانوا لا يغيرون الشيب، وبتوفير العثانين (اللحية) وتقصير السبال (طرف الشارب) ، وكانوا يقصرون عثانينهم، ويوفّرون سبالهم، وكانوا يعقون عن الذكر دون الأنثى، وشُرِعت لنا عنهما معا.
وبترك القيام للجنازة،',NULL,'87',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (75,'وبتعجيل المغرب والفجر، وبكراهة اشتمال الصماء، وبكراهة صوم يوم الجمعة منفرداً، وكان اليهود يصومون يوم عيدهم منفرداً، وبضم تاسوعاء إلى عاشوراء في الصوم،',NULL,'88',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (76,'وبالسجود على الجبهة وكانوا يسجدون على حرف، وكراهة التميل في الصلاة وكانوا يتميلون،',NULL,'89',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (77,'وبكراهة تغميض البصر فيها، والاختصار، والقيام بعدها للدعاء، وقراءة الإمام فيها في المصحف، والتعلق فيها بالحبال، وبالأكل يوم العيد قبل الصلاة، وكان أهل الكتاب لا يأكلون يوم عيدهم حتى يصلوا، وبالصلاة في النعال والخِفاف.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (78,'عن ابن عمر كانت بنو إسرائيل إذا قرأت أئمتهم جاوبوهم، فكره الله ذلك لهذه الأمة، فقال: (وإذا قرئ القرآن فاستمعوا له وأنصتوا) .
وفي "المستدرك": أنه صلى الله عليه وآله وسلم نهى رجلاً وهو جالس معتمد على يده اليسرى في الصلاة، وقال: "إنها صلاة اليهود".',NULL,'91',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (79,'وأذن لنسائنا في المساجد، ومنعت نساء بني إسرائيل، وكان في شرعهم نَسخُ الحكم إذا رفعه الخصم إلى حاكم آخر يرى خلافه.
وبالعذبة في العمامة وهي سيما الملائكة، وبالاتزار في الأوساط،',NULL,'92',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (80,'وبكراهة السدل في الصلاة والطيلسان المقور، وشد الوسط على القميص، والقزع، وبالأشهر الهلالية،',NULL,'93',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (81,'وبالوقف، وبالوصية بالثلث عند موتهم، وبالإسراع بالجنازة.
وأن أمته خير الأمم، وآخر الأمم، ففضحت الأمم عندهم',NULL,'94',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (82,'ولم يفضحوا، واشتق لهم اسمان من أسماء الله: المسلمون والمؤمنون، وسمي دينهم الإسلام. ولم يوصف بهذا الوصف إلا الأنبياء دون أممهم.
وقال عبد الله بن يزيد الأنصاري: تسموا باسمكم الذي سماكم الله: بالحنيفية والإسلام والإيمان.
ورفع عنهم الإصر الذي كان على الأمم قبلهم، وأبيح لهم الكنز إذا أرادوا زكاته، وأحل لهم كثير مما تشدد على من قبلهم،',NULL,'95',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (83,'ولم يجعل عليهم في الدين من حرج.
وأبيح لهم أكل الإبل، والنعام، وحمار الوحش، والأوز والبط، وجميع السمك، والشحوم، والدم الذي ليس بمسفوح، كالكبد والطحال',NULL,'96',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (84,'والعروق.
وفي الحديث: "أحلت لنا ميتتان ودمان: السمك والجراد، والكبد والطحال".
ورفع عنهم المؤاخذة بالخطأ والنسيان، وما استكرهوا عليه، وحديث النفس، وأنَّ من همَّ منهم بسيئة ولم يعملها لم تكتب سيئة، بل تكتب حسنة،',NULL,'97',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (85,'فإن عملها كتبت سيئة واحدة، ومَن همَّ بحسنة ولم يعملها كتبت حسنة، فإن عملها كتبت له عشراً إلى سبعمائة ضعف، ووضع عنهم قتل النفس في التوبة، وفقأ العين من النظر إلى ما لا يحل، وقرض موضع النجاسة، وربع المال',NULL,'98',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (86,'في الزكاة.
ونسخ عنهم تحرير الأولاد، والتحصر، والرهبانية، والسياحة.
وفي الحديث: "ليس في ديني ترك النساء، ولا اللحم، ولا اتخاذ الصوامع". وكان من عمل من اليهود شُغلاً يوم السبت يُصلَب، ولم يجعل علينا يوم الجمعة مثل ذلك.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (87,'وكانوا لا يطعمون طعاماً حتى يتوضؤا كوضوء الصلاة، وكان من سرق استُرِقَّ عبداً، ومن قتل نفسه حرمت عليه الجنة، وكان إذا ملك الملك عليهم، اشترط عليهم أنهم رقيقه، وأن أموالهم له، ما شاء أخذ منها وما شاء ترك.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (88,'وشُرِع لهم نكاح أربع، والطلاق ثلاثاً، ورُخِّصَ لهم في نكاح غير ملتهم، وفي نكاح الأمة، وفي مخالطة الحائض سوى الوطء، وفي إتيان المرأة على أي هيئة شاؤوا.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (89,'وشُرعَ لهم التخيير بين القصاص والدية، وشُرعَ لهم دفع الصائل وكانت بنو إسرائيل كتب عليهم إذا الرجل بسط يده إلى الرجل لا يمتنع منه حتى يقتله أو يدعه، قاله مجاهد وابن جريج.
وحرم عليهم كشف العورة، والنوح على الميت، والتصوير،',NULL,'102',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (90,'وشرب المسكر وآلات الملاهي، ونكاح الأخت [واستعمال] أواني الذهب والفضة، والحرير وحلي الذهب على رجالهم، والسجود لغير الله، وكان تحية من قبلنا، فأعطينا مكانه السلام.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (91,'وكرهت لهم المحاريب، وعصموا من الاجتماع على ضلالة. ومن أن يظهر أهل الباطل على أهل الحق، ومن أن يدعو عليهم نبيهم بدعوة فيهلكوا، وإجماعهم حُجَّة، وإختلافهم رَحمة، وكان اختلاف من قبلهم عذاباً،',NULL,'104',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (92,'والطاعون لهم شهادة ورحمة، وكان على الأمم عذاباً، وما دعوا به استُجيب لهم.
ويؤمنون بالكتاب الأول، والكتاب الآخر، ويحجون البيت الحرام لا ينأون عنه أبداً، ويغفر لهم الذنب بالوضوء، وتبقى الصلاة لهم نافلة،',NULL,'105',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (93,'ويأكلون صدقاتهم في بطونهم ويثابون عليها، ويعجل لهم الثواب في الدنيا مع ادخاره في الآخرة، وتتباشر',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (94,'الجبال والأشجار بمرهم عليها لتسبيحهم وتقديسهم، وتُفتّح أبواب السماء لأعمالهم وأرواحهم، وتتباشر بهم الملائكة، ويصلي عليهم الله وملائكته.
قال سفيان بن عيينة: أكرم الله أمة محمد صلى الله عليه وآله وسلم فصلى عليهم كما صلى على الأنبياء فقال: (هو الذي يصلي عليكم وملائكته) .
ويقبضون على فرشهم وهم شهداء عند الله، وتوضع المائدة بين أيديهم فمايرفعونها حتى يغفر لهم،',NULL,'107',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (95,'ويَلبَسُ أحدهم الثوب فما ينفضه حتى يغفر له.
وصدِّيقهم أفضل الصديقين، وهم علماء حكماء، كادوا لفقههم أن يكونوا كلهم أنبياء.
ولا يخافون في الله لومة لائم، وأذلة على المؤمنين أعزة على الكافرين، وقرباتهم الصلاة، وفربانهم دماؤهم،',NULL,'108',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (96,'وسُتِرَ على من لم يُتقَبَّل عمله منهم، وكان من قبلهم يَنفضِح إذا لم تأكل النار قربانه، وتغفر لهم الذنوب بالاستغفار، والندم لهم توبة، قاله رزين.
وروي أن آدم قال: الله أعطى أمة محمدٍ صلى الله عليه وآله وسلم أربع كرامات لم يعطينيها: كانت توبتي بمكة، وأحدهم يتوب في كل مكان، وسلبت ثوبي حين عصيت، وهم لا يسلبون، وفرق بيني وبين زوجتي، وأخرجت من الجنة.
قال: وكان بنو اسرائيل',NULL,'109',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (97,'إذا أخطاؤا حرم عليهم طيب الطعام، وتصبح خطيئته مكتوبة على باب داره، انتهى.
ووُعِدوا أن لا يهلكوا بجوع ولا بعَدوّ من غيرهم يستأصلهم، ولا بغرق، ولا يعذبوا بعذاب عذب به من قبلهم، وإذا شهد الاثنان منهم لعبد بخير وجبت له الجنة، وكانت الأمم السابقة إذا شهد منهم مئة.
وهم أقل الأمم عملاً، وأكثرهم أجراً، وأقصر أعماراً،',NULL,'110',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (98,'وكان الرجل من الأمم السابقة أعبَدَ منهم بثلاثين ضعفاً، وهم خير منه بثلاثين ووهب لهم عند المصيبة الصلاة والرحمة والهدى، وأوتوا العلم الأول والآخر، وفتح عليهم خزائن كل شيء حتى العلم، وأوتوا الإسناد',NULL,'111',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (99,'والأنساب والإعراب، وتصنيف الكتب، وحفظ سنة نببيهم.
قال أبو علي الجيّاني: خص الله هذه الأمة بثلاثة أشياء لم يعطها من قبلها: الإسناد، والأنساب، والإعراب.
وقال ابن العربي في "شرح الترمذي": لم يكن قط في الأمم من انتهى إلى حد هذه الأمة من التصرف في التصنيف والتحقيق، ولا جاراها في مداها من التفريع والتدقيق.
وقال القرافي في "شرح المحصول": من خصائصه صلى الله عليه وآله وسلم أن الواحد من أمته يحصل له في العمر القصير من العلوم والفهوم ما لا يحصل لأحد من الأمم السابفة في العمر الطويل.
قال: ولهذا تهيأ للمجتهدين',NULL,'112',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (100,'من هذه الأمة من العلوم والاستنباطات والمعارف ما تقصر عنه أعمارهم. انتهى.
وقال قتادة: أعطى الله هذه الأمة من الحفظ شيئاً لم يعطه أحداً من الأمم قبلها، خاصة خصهم بها، وكرامة أكرمهم بها، ولا تزال طائفة منهم على الحق حتى يأتي أمر الله، ولا تخلو الأرض من مجتهد فيهم قائم لله بالحجة، حتى يتداعى الزمان بتزلزل القواعد، وتأتي أشراط الساعة الكبرى.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (101,'ويبعث الله لهم على رأس كل مائة سنة من يجدد لهم أمر دينهم، حتى يكون في آخر مائة عيسى ابن مريم.
ومنهم أقطاب وأوتاد ونجباء وأبدال،',NULL,'114',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (102,'ومنهم من يصلي إماماً بعيسى ابن مريم، ومنهم من يجري مجرى الملائكة في الاستغناء عن الطعام بالتسبيح، ويقاتلون الدجال، وعلماؤهم كأنبياء بني إسرائيل،',NULL,'115',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (103,'وتسمع الملائكة في السماء أذانهم وتلبيتهم.
وهم الحامدون لله على كل حال، ويكبرون على كل شرف، ويسبحون عند كل هبوط، ويقولون عند إرادة الأمر: أفعله إن شاء الله، وإذا غضبوا هللوا، وإذا تنازعوا سبحوا، وإذا أرادوا استخاروا الله ثم ركبوا، وإذا ركبوا على ظهور دوابهم حمدواالله، ومصاحفهم في صدورهم،',NULL,'116',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (104,'وسابقهم سابق ويدخل الجنة بغير حساب، ومقتصدهم ناج يحاسب حساباً يسيراً، وظالمهم مغفور له، وليس منهم أحد إلا مرحوماً، ويلبسون ألوان ثياب أهل الجنة، ويراعون الشمس للصلاة، وهم أمة وسط، عدول بتزكية الله،',NULL,'117',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (105,'وتحضرهم الملائكة إذا قاتلوا، وافترض عليهم ما افترض على الأنبياء والرسل، وهو الوضوء والغسل من الجنابة، والج، والجهاد، وأعطوا من النوافل ما أعطي الأنبياء.
وقال الله في حق غيرهم: (ومن قوم موسى أمة يهدون بالحق وبه يعدلون) .
ونودوا بالقرآن بـ (يا أيها الذين آمنوا) ،',NULL,'118',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (106,'ونوديت الأمم في كتبها بـ (يا أيها المساكين) ، وشتان ما بين الخطابين.
وقال الدَّميري في "شرح المنهاج": قال بعض العلماء: خاطب الله هذه الأمة بقوله: (فاكروني أذكركم) ، فأمرهم أن يذكروه بغير واسطة، وخاطب بني اسرائيل بقوله: (اذكروا نعمتي) ، فإنهم لم يعرفوا الله إلا بآلائه، فأمرهم أن يقصدوا النِّعم ليصلوا بها إلى ذكر المُنعِم.
قال الزركشي في "الخادم": فما كان مجتمعاً فيه صلى الله عليه وآله وسلم من الأخلاق والمعجزات صار متفرقاً في أمته، بدليل: أنه كان معصوماً، وأمته إجماعها معصوم.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (107,'قال بعضهم: ولهذا لما أودع أسراره وخُيِّر بين الحياة والموت، اختار الموت. ولما لم يحصل لموسى ذلك، وجاءه ملك الموت لطمه. وهم أكثر الأمم أيامى ومملوكين، وفي "تفسير ابن أبي حاتم" عن عكرمة قال: لم تكن أمة دخل فيها من أصناف الناس غير هذه الأمة.
وفي الحديث:',NULL,'120',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (108,'لما أُنزِلت: (والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه) قال صلى الله عليه وآله وسلم: "هذه لأمتي كلها وليس بعد الرضى سخط".
وقال معاوية: ما اختلفت أمة قط إلا غلب أهل باطلها أهل حقها إلا هذه الأمة.
وفي "شرح الرسالة" للجزولي: قيل: أهل القبلة اسم خصت به أمة محمد صلى الله عليه وآله وسلم.
وفي سنن أبي داود حديث: "لن يجمع الله على هذه الأمة سيفين،',NULL,'121',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (109,'سيفاً منها وسيفاً من عدوها".
وقال ابن مسعود: ولا يحل في هذه الأمة التجريد، ولا مدٌّ، ولا غِلٌّ، ولا صفد، يعني: لا تجرد ثيابه ولا يمد عند إقامة الحدود، بل يضرب قاعداً وعليه ثوبه.
وفي الحديث: "لا ترث مِلَّة ملة، ولا تجوز شهادة ملة على',NULL,'122',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (110,'ملة إلا أمة محمد، فإن شهادتهم تجوز عل من سواهم".
وقال ابن الجوزي: بَدء الشرائع كان على التخفيف، ولا يعرف في شرع نوح وصالح وابراهيم تثقيل، ثم جاء موسى بالشدائد والأثقال، وجاء عيسى بنحو ذلك. وجاءت شريعة نبينا صلى الله عليه وآله وسلم بنسخ تشديد أهل الكتاب ولا يطلق بتسهيل من كان قبلهم فهي غاية الاعتدال. انتهى.

<span data-type="title" id=toc-4>الفصل الثالث</span>
فيما اختص به صلى الله عليه وآله وسلم
في ذاته في الآخرة

اختُص صلى الله عليه وآله وسلم بأنه أول من تنشق عنه الأرض،',NULL,'123',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (111,'وأول من يَفيق من الصعقة، وبأنه يحشر في سبعين ألف ملك، ويحشر على البراق، ويُؤذن باسمه في الموقف،',NULL,'124',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (112,'ويُكسى في الموقف أعظم الحلل من الجنة، وبأنه يقوم على يمين العرش، وبالمقام المحمود، وأن بيده لواء الحمد، وآدم فمن دونه تحت لوائه، وأنه إمام النبيين يومئذ، وقائدهم وخطيبهم، وأول من يؤذن له بالسجود، وأول من يرفع رأسه، وأول من ينظر إلى الله تعالى، وأول شافِعٍ وأول مُشَفَّع، ويسأل في غيره وكل الناس يسألون في أنفسهم.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (113,'وبالشفاعة العظمى في فصل القضاء، وبالشفاعة في إدخال قوم الجنة بغير حساب، وبالشفاعة فيمن استحق النار أن لا يدخلها، وبالشفاعة في رفع درجات ناسٍ في الجنة.
كما جوز النووي اختصاص هذه والتي قبلها به.
ووردت الأحاديث في التي قبل، وصرح به القاضي عياض وابن دحية.
وبالشفاعة في إخراج عموم أمته من النار، حتى لا يبقى منهم أحد. ذكره السبكي.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (114,'وبالشفاعة لجماعة من صُلَحاء المسلمين، لُيتَجاوز عنهم في تقصيرهم في الطاعات. ذكره القزويني في "العروةالوثقى".
وبالشفاعة في الموقف تخفيفاً عمن يحاسب، وبالشفاعة في أطفال المشركين أن لا يعذبوا، وسأل ربه أن لا يدخل النار أحداً من أهل بيته فأعطاه ذلك.
وبالشفاعة فيمن خُلِّد في النار من الكفار أن يُخفّفَ عنه العذاب، وأنه أول من يُجيزُ على الصراط، وأن له في كل شعرة من رأسه ووجهه',NULL,'127',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (115,'نوراً، وليس للأنبياء إلا نوران، ويُؤمَر أهل الجمع بغض أبصارهم حتى تَمُرَّ ابنته على الصراط، وأنه أول من يقرع باب الجنة، وأول من يدخلها وبعده ابنته، وبالكوثر، زاد أبو سعيد وابن سراقة، وبالحوض.
قلت: لكن ورد أن لكل نبي حوضاً.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (116,'وفي أثرٍ في خصائصه صلى الله عليه وآله وسلم: وحوضه أعرض الحياض وأكثرها وارداً، وبالوسيلة وهي أعلى درجة في الجنة.
قال عبد الجليل القصيري في "شعب الإيمان":
الوسيلة التي اختص بها هي التوسل، وذلك أن النبي صلى الله عليه وآله وسلم يكون في الجنة بمنزلة الوزير من الملك بغير تمثيل، لا يصل إلى أحد شيء إلا بواسطته،',NULL,'129',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (117,'وقوائم منبره رواتب في الجنة، ومنبره على تُرعة من تُرَع الجنة، ومابين قبره ومنبره روضة من رياض الجنة، ولا يطلب منه شهيدٌ على التبليغ، ويطلب من سائر الأنبياء، ويشهد لجميع الأنبياء بالبلاغ، وكل سبب ونسب منقطع يوم القيامة إلا نسبه وسببه.
فقيل: معناه أن أمته ينسبون إليه يوم القيامة، وأمم سائر الأنبياء لا ينسبون',NULL,'130',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (118,'إليهم.
وقيل: يُنتَفع يومئذ بالنسبة إليه، ولا يُنتَفع بسائر الأنساب، ويكنى آدم في الجنة به دون سائر ولده تكريماً له، فيقال له: أبو محمد.
ووردت أحاديث في أهل الفترة أنهم يمتحنون يوم القيامة فمن أطاع دخل الجنة، ومن عصى دخل النار.
قال بعضهم: والظن بآل بيته كلهم أن يطيعوا عند الامتحان، لِتَقرَّ بهم عينه.
وورد: أن درجات الجنة بعدد آي القرآن، وأنه يقال لصاحبه: اقرأ وارْقَ، فآخر منزلته عند آخر آية يقرؤها،',NULL,'131',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (119,'ولم يرد في آخر الكتب مثل ذلك.
ويخرج من ذلك خَصيصةٌ أخرى: وهو أنه لا يقرأ في الجنة إلا كتابه، ولا يتكلم في الجنة إلا بلسانه.
وفي "تفسير ابن أبي حاتم" عن سعيد بن أبي هلال: أنه بلغه أن المقام المحمود، أن رسول الله صلى الله عليه وآله وسلم يوم القيامة يكون بين الجبار وبين جبريل، فيغبطه بمقامه ذلك أهل الجمع.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (120,'وفي الحديث: "أناأول من يقرع باب الجنة، فيقوم الخازن فيقول: من أنت؟ فأقول: أنا محمد، فيقول: أقوم فأفتح لك، ولم أقم لأحد قبلك ولا أقوم لأحد بعدك".

<span data-type="title" id=toc-5>الفصل الرابع</span>
فيما اختص به صلى الله عليه وآله وسلم
في أمته في الآخرة
اختُص صلى الله عليه وآله وسلم بأن أمته أول من تنشق عنهم الأرض من الأمم، ويأتون يوم القيامة غُرّاً مُحجلين من آثار الوضوء، ويكونون في الموقف على كوم عال،',NULL,'133',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (121,'ولهم نوران كالأنبياء، وليس لغيرهم إلا نور واحد، ولهم سيماء في وجوههم من أثر السجود، ويسعى نورهم بين أيديهم، ويؤتون كتبهم بأيمانهم، ويمرون على الصراط كالبرق والريح، ويَشفع مُحسنهم في مُسيئهم، وعُجِّل عذابها في الدنيا والبرزخ لتوافي القيامة ممحصة، وتدخل قبورها بذنوبها ومنها تخرج بلا ذنوب، ويمحص عنها باستغفار المؤمنين',NULL,'134',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (122,'لها، ولها ما سعت وما سُعي لها، وليس لمن قبلهم إلا ما سعى.
قال عكرمة: ويقضى لهم قبل الخلائق، ويغفر لهم المقحمات، وهم أثقل الناس ميزاناً، ونزلوا منزلة العدول من الحكام، فيشهدون على الناس أن رسلهم بلغتهم،',NULL,'135',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (123,'ويعطى كل منهم يهودياً أو نصرانياً فيقال له: يا مسلم هذا فداؤك من النار، ويدخلون الجنة قبل سائر الأمم، ويدخل منهم الجنة سبعون ألفاً بغير حساب، وأطفالهم كلهم في الجنة، وليس ذلك في سائر الأمم في أحد احتمالين للسبكي',NULL,'136',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (124,'في "تفسيره"، وذكر الإمام فخر الدين: أن من كانت معجزته أظهر يكون ثواب أمته أقل.
قال السبكي: إلا هذه الأمة, فإن معجزات نبينا أظهر، وثوابنا أكثر من سائر الأمم، وأهل الجنة مائة وعشرون صفاً، وهذه الأمة منها ثمانون، وسائر الأمم أربعون.
ويتجلى الله عليهم فيرونه، ويسجدون له بإجماع أهل السُّنة، وفي الأمم السابقة احتمالان لابن أبي جمرة.
وفي "فوائد" القاضي أبي الحسين بن المهتدي من حديث ابن عمر مرفوعاً: "كل أمة يعضها في الجنة وبعضها في النار إلا هذه الأمة فإنها كلها في الجنة".',NULL,'137',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (125,'وفي "مصنف عبد الرزاق" عن الربعي: أنه قرأ في بعض الكتب أن ولد الزنا لا يدخل الجنة إلى سبعة آباء، فخفف الله عن هذه الأمة فجعلها إلى خمسة آباء".

<span data-type="title" id=toc-6>الباب الثاني
في الخصائص التي اختص بهاصلى الله عليه وآله وسلم عن أمته ومنها ما علم مشاركة الأنبياء له فيه ومنها ما لم يعلم</span>
وفيه أربعة فصول

<span data-type="title" id=toc-7>الفصل الأول</span>
فيما اختص به صلى الله عليه وآله وسلم من الواجبات والحكمة فيه زيادة الزلفى والدرجات',NULL,'138',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (126,'خُص صلى الله عليه وآله وسلم بوجوب صلاة الضحى، والوتر،',NULL,'139',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (127,'والتهجد، أي صلاة الليل، والسواك والأضحية، والمشاورة',NULL,'140',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (128,'على الأصح في السنة وركعتي الفجر، لحديث في "المستدرك" وغيره.
وغسل الجمعة، وورد في حديث واهٍ، وأربع عند الزوال، وورد عن سعيد بن المسيب.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (129,'قيل: وبالوضوء لكل صلاة ثم نُسخ، وبالوضوء كلما أحدث فلا يُكلِّم أحداً، ولا يَردُّ سلاماً حتى يتوضأ ثم نُسخ، قيل وبالاستعاذة عند القراءة ومصابرة العدو وإن كثُر عددهم، وإذا بارز رجلاً في الحرب لم ينكف عنه قبل قتله.
وتغيير المنكر, ووجه الخصوصية فيه من جهة أنه في فرائض الإيمان، وفي حق غيره من فرائض الكفايات، ذكره الجرجاني في "الشافي".
وأنه يجب عليه إظهار الإنكار،',NULL,'142',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (130,'ولا يجب الإظهار على أمته، ذكره صاحب "الذخائر".
وأنه لا يسقط عنه للخوف فإن الله وعده العصمة، بخلاف غيره، ذكره في "الروضة"، ولا إذا كان المرتكب يزيده الإنكار إغراء، لئلا يتوهم إباحته بخلاف سائر الأمة، ذكره السمعاني في "القواطع".
ووجوب الوفاء بوعده كضمان غيره، بخلاف سائر الأمة ذكره الجوزي وطائفة، وقضاء دَين من مات من المسلمين معسراً على الصحيح.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (131,'وتخيير نسائه في فراقه واختياره على الصحيح، وإمساكهن بعد أن اخترنه في أحد الوجهين. وترك التزوج عليهن، والتبدل بهن، مكافأة لهن، ثم نسخ ذلك',NULL,'144',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (132,'لتكون المنة له صلى الله عليه وآله وسلم، وأن يقول إذا رأى ما يعجبه: لبيك إن العيش عيش الآخرة، في وجه حكاه في "الروضة" وأصلها، وأن يؤدي فرض الصلاة كاملة لا خلل فيها، ذكره الماوردي وغيره، وإتمام كل تطوع شَرعَ فيه، حكاه في "الروضة" وأصلها.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (133,'وأن يدفع بالتي هي أحسن، وكُلّف من العلم وحده ما كلفه الناس بأجمعهم، وكان مطالباً برؤية مشاهدة الحق مع معاشرة الناس بالنفس والكلام، ذكر الثلاثة ابن سبع وابن القاص في "تلخيصه".
وقال أبو سعيد في "شرف المصطفى": كُلِّف من العمل بما كُلِّف به الناس أجمعين. وبين الأمرين فرق، وكان يؤخذ عن الدنيا حالة الوحي، ولا يسقط عنه الصوم والصلاة وسائر الأحكام ذكره',NULL,'146',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (134,'في "زوائد الروضة" عن ابن القاص، والقفال، وجزم به ابن سبع. وكان يُغان (١) على قلبه فيستغفر الله سبعين مرة، ذكره ابن القاص ونقله ابن الملقن في "الخصائص".
وعبارة أبي سعيد في "شرف المصطفى": ويستغفر كل يوم سبعين مرة ولا يذنب.
وعبارةرزين في "خصائصه": ومماوجب عليه أن يستغفر في كل يوم سبعين مرة,
_________
(١) قال الأهدل في فتح الكريم: تنبيه: هذا الغين ليس بغين أغيار، إنما هو غين أنوار كما قاله بعضهم، وقال السيد السمهودي: لا يعتقد أن الغين حالة نقص، بل هو الكمال أو تتمة الكمال، فهو كجفن العين حين يسيل لدفع القذى، فإنه يمنع العين من الرؤية، فهو من هذه الحيثية نقص، وفي الحقيقة كمال. انتهى.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (135,'وعد أيضاً في خصائصه: أن الركعتين بعد العصر كانت واجبة عليه، وأن جميع نوافله كانت فرضاً، لأن النفل إنما هو للجبر ولا نقص في صلاته حتى تُجبر، وأنه خص بصلاة خمسين صلاة في كل يوم وليلة على وفق ما كان ليلة الإسراء، وأورد الأحاديث في صلاته غير الخمس فبلغت مائة ركعة.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (136,'وأنه كان إذا مر بنائم في وقت الصلاة أيقظه، وهو امتثال قوله: (ادع إلى سبيل ربك) .
قال: وخُصَّ بوجوب العقيقة، والإثابة على الهدية، والإغلاظ على الكفار، وتحريض المؤمنين على القتال، وأوجب عليه التوكل، وحرم عليه الادخار. وكان يُموِّن عيال من مات مُعسراً،',NULL,'149',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (137,'ويؤدي الجنايات عمن لزمته وهو معسر، وكذلك الكفارات. ومما وجب عليه الصبر على مايكره، وصبر نفسه مع الذين يدعون ربهم بالغداة والعشي، والرفق وترك الغلظة، وإبلاغ كل ما أنزل إليه وخطاب الناس بما يعقلون، والدعاء لمن أدى صدقات ماله، وقيل: إن كان ما يتقرب به كان واجباً عليه، وأن لا يَعِدَ وعداً أو يعلق أمراً على غدٍ بغير استثناء.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (138,'انتهى ما أورده رزين.
وقال أبو سعيد: كان يجب عليه حفظ أموال المسلمين، وكانت الإمامة في حقه أفضل من الأذان، في وجهٍ حكاه الجرجاني في "الشافي"، لأنه لا يقر على السهو والغلط، بخلاف غيره، وهذا الوجه ينبغي أن يقطع به، ويجعل محل الخلاف في التفضيل بين الإمامة والأذان في غيره.
وذكر بعض الحنفية: أن في عهده لا يسقط فرض',NULL,'151',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (139,'الجنازة إلا بصلاته، فيؤول إلى أن صلاة الجنازة في حقه فرض عين، وفي حق غيره فرض كفاية.

<span data-type="title" id=toc-8>الفصل الثاني</span>
فيما اختص به صلى الله عليه وآله وسلم من المحرمات
خُص صلى الله عليه وآله وسلم بتحريم الزكاة والصدقة والكفارة عليه, وتحريم الزكاة على آله، قيل: والصدقة أيضاً، وعليه المالكية، وعلى موالي أهله في الأصح، وعلى زوجاته بالإجماع، حكاه ابن عبد البر.
والنذورات.
قال البلقيني: وخرجت على ذلك أنه كان يَحرم عليه أن يوقف عليه معيناً لأن الوقف صدقة تطوع.
قال: وفي "الجواهر" للقَمولي ما يؤيده، فإنه قال: صدقة التطوع كانت',NULL,'152',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (140,'حراماً عليه على الصحيح.
وعن ابن أبي هريرة: أن صدقات الأعيان كانت حراماً عليه دون صدقات العامة كالمساجد وبناء الآبار، وتحريم كون أهله عمالاً على الزكاة في الأصح.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (141,'وصرف النذر والكفارة إليهم، وأكل ثمن أحدٍ من ولد اسماعيل وَرَدَ به حديث في المسند، ولم أر من تعرض له، وأكل ما له رائحة كريهة، والأكل متكئاً في أحد الوجهين فيهما، والأصح في "الروضة" كراهتهما،',NULL,'154',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (142,'قال أبو سعيد في "شرف المصطفى": وكره الضب، وتحريم الكتابة والشعر، قال الماوردي: وكذا روايته،',NULL,'155',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (143,'والقراءة في الكتاب.
وقال البغوي في "التهذيب": قيل: كان يحسن الخط ولا يكتب، ويحسن الشعر ولا يقوله، والأصح أنه كان لا يحسنهما، ولكنه كان يميز بين جيد الشعر ورديئه.
ونزع لأْمته إذا لبسها حتى يقاتل أو يحكم الله بينه وبين عدوه، وكذلك الأنبياء.
قال أبوسعيد وابن سراقة: كان لا يرجع إذا خرج إلى',NULL,'156',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (144,'الحرب، ولا ينهزم إذا لقي العدو وإن كثر عليه العدد.
والمَنُّ ليستكثر- أي أن يهدي هدية ليثاب بأكثر منها-، ومد العين إلى ما مُتِّع به الناس من زهرة الحياة الدنيا، وخائنة الأعين، وهي الإيماء إلى مباح من قتل أو ضرب، على خلاف ما يظهر، وكذلك الأنبياء.
... وأن يخدع في الحرب، فيما ذكره ابن القاص وخالفه الجمهور.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (145,'والصلاة على من عليه دَين ثم نسخ. وإمساك كارِهته، وتحرم عليه مؤبداً في أحد الوجهين، ونكاح من لم تهاجر في أحد الوجهين، ونكاح الكتابية، قيل: والتسري بها، ونكاح الأمة المسلمة،',NULL,'158',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (146,'ولو قدّر نكاحه أمة فولده منها حر، ولا تلزمه قيمته، ولا يشترط في حقه حينئذ خوف العنت ولا فقد الطَول، وله الزيادة على واحدة.
قال إمام الحرمين: ولو قُدِّر نكاح غرور في حقه لم يلزمه قيمة الولد.
قال ابن الرفعة: وفي تصور ذلك في حقه نظر.
وقال البلقيني: لا يتصور في حقه اضطرار قط إلى نكاح الأمة، بل لو أعجبته أمة وجب على مالكها بذلها إليه هبة قياساً على الطعام، وكان إذا خطب فرُدَّ لم يعد، كذا في حديث مرسل،',NULL,'159',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (147,'فيحتمل التحريم والكراهة، قياساً على إمساك كارهته، ولم أر من تعرض له.
وعدَّ ابن سبع من خصائصه: تحريم الإغارة إذا سمع التكبير.
وعدَّ القضاعي وغيره من خصائصه: أنه لا يقبل هدية مشرك، ولا يستعين به،',NULL,'160',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (148,'ولا يشهد على جور.
وحُرِّم عليه الخمر من أول ما بعث من قبل أن تحرم على الناس بنحو عشرين سنة، فلم يبح له قط.
وفي الحديث: "أول ما نهاني عنه ربي بعد عبادة الأوثان: شرب الخمر وملاحاة الرجال"، ونُهي عن التعري وكشف العورة من قبل أن يبعث بخمسين سنة،',NULL,'161',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (149,'وقالت عائشة: ما رأيت منه ولا رأى مني.
ونهى علياً عن إنزاء الحُمُر على الخيل نهياً خاصاً، عدَّ هذه رزين.
وكان لا يصلِّ على ميتٍ غَلَّ، ولا على من قتل نفسه، وفي "المستدرك" عن أبي قتادة قال: كان النبي صلى الله عليه وآله وسلم إذا دُعي إلى جنازة سأل عنها، فإن أثني عليها خير صلى عليها، وإن أثني عليها غير ذلك قال لأهلها: "شأنكم بها" ولم يُصل عليها.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (150,'وفي "سنن أبي داود" حديث: "ما أبالي ما أتيت إن أنا شربت ترياقاً أو تعلقت تميمة أو قلت الشعر من قبل نفسي".
قال أبو داود هذا كان للنبي صلى الله عليه وآله وسلم خاصة، وقد رخص الترياق لغيره.
وقد رخص أيضاً في تعليق التمائم لغيره إذا كان بعد نزول البلاء.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (151,'<span data-type="title" id=toc-9>الفصل الثالث</span>
فيما اختص به صلى الله عليه وآله وسلم من المباحات
اختُص صلى الله عليه وآله وسلم بإباحة المكث في المسجد جنباً، والعبور فيه عند المالكية.
وأنه لا ينتقض وضوؤه بالنوم',NULL,'164',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (152,'ولا باللمس، في أحد الوجهين وهو الأصح.
قيل: وبإباحة استقبال القبلة واستدبارها حال قضاء الحاجة، حكاه ابن دقيق العيد في "شرح العمدة".
وإباحة الصلاة بعد العصر، وقضاء الراتبة بعد العصر عند قوم.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (153,'وحمل الصغيرة في الصلاة فيما ذكره بعضهم، وبالصلاة على الغائب عند أبي حنيفة، وعلى القبر عند المالكية، وبجواز صلاة الوتر على الراحلة مع وجوبه عليه، ذكره في "شرح المهذب"، وقاعداً ذكره في "الخادم". وكان يجهر فيه وغيره يُسِرّ، وبالإمامة جالساً فيما ذكره قوم بجواز استخلافه في الإمامة، كما وقع لأبي بكر حين تأخر وقدّمه، فيما قاله جماعة،',NULL,'166',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (154,'وبأنه يصلي الركعة الواحدة بعضها من قيام وبعضها من قعود فيما ذكره بعض السلف، وقال: إن ذلك ممنوع لغيره، والقُبلة في الصوم مع قوة شهوته للوصال، والسواك بعد الزوال وهو صائم. ذكره رزين.
قيل: والصوم جنباً حكاه الطحاوي، وبإباحة دخول مكة بغير إحرام، واستمرار الطيب في الإحرام فيما ذكر المالكية،',NULL,'167',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (155,'وقهر من شاء على طعامه وشرابه.
زاد رزين: ولباسه إذا احتاج، ويجب على المالك البذل وإن هلك، ويفدي بمُهجته مهجة رسول الله صلى الله عليه وآله وسلم، وإباحة النظر إلى الأجنبيات والخلوة بهن، وإردافهن، ونكاح أكثر من أربعة نسوة وكذلك الأنبياء،',NULL,'168',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (156,'والنكاح بلفظ الهبة، وبلا مهر ابتدءً وانتهاءً، وبصداق مجهول، ذكره الروياني في "البحر"، وبلا ولي وبلا شهود، وفي حال الإحرام، وبغير رضا المرأة.
فلو رغب في نكاح امرأة خلية، لزمتها الإجابة وحرم على غيره خطبتها بمجرد الرغبة، أو مزوجة لزم على زوجها طلاقها لينكحها.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (157,'قال الغزالي في "الخلاصة": له حينئذ نكاحها من غير انقضاءعدة، وكان له أن يخطب على خطبة غيره، وكان له تزويج المرأة ممن شاء بغير إذنها ولا إذن وليها، وله إجبار الصغيرة من غير بناته، وزوج ابنة حمزة مع وجود عمها العباس، فقدم على الأقرب.
وقال لأم سلمة: مُري ابنك أن يزوجك، فزوجها وهو يومئذ صغير لم يبلغ،',NULL,'170',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (158,'وزوَّجه الله بزينب فدخل عليها بتزويج الله بغير عقد من نفسه.
وعبَّر في "الروضة" عن هذه بقوله: وكانت المرأة تحل له بتحليل الله، قال أبو سعيد في "شرف المصطفى": وكان كفؤاً لكل أحد،',NULL,'171',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (159,'وإذا تزوج بولي فاسق أو أعمى أو أخرس جاز له. انتهى.
وله نكاح المُعتَدَّة من غيره، في وجه [ضعيف] حكاه الرافعي، والجمع بين المرأة وأختها [وبين] وعمتها وخالتها أحد الوجهين، وبين المرأة وابنتها في وجه حكاه الرافعي.
وقال رزين في "خصائصه": إذا وطئ جارية بملك اليمين لم تثبت الحرمة في أمها ولا بنتها ولا أختها حتى يمتنع الجمع بينهن. انتهى.
فيحتمل أن يكون هو',NULL,'172',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (160,'الوجه المحكي في "الشرح" و"الروضة"، ويحتمل أن يكون غيره، وأنه يفرق في ذلك بين الأمة والزوجة.
وعتق أمته، وجعل عتقها صداقها، وأصدق جويرية عتق أسرى قومها، ونكاح من لم تبلغ فيما ذكره ابن شبرمة، لكن الإجماع على خلافه, وترك القسم بين أزواجه في أحد الوجهين وهو المختار.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (161,'وقال ابن العربي في "شرح الترمذي": إن الله خَصَّ نبيه بأشياء في النكاح منها: أنه أعطاه ساعة لا يكون لأزواجه فيهاحقٌّ حتى يدخل فيها على جميع أزواجه فيفعل ما يريد بهن، ثم يدخل عند التي يكون الدور لها، ولا يجب عليه نفقتهن في وجه كالمهر، وعلى الوجوب لا يتقدر.
ولا ينحصر طلاقه في الثلاث في أحد الوجهين، وعلى الحصر قيل: تحل له من غير محلّل، وقيل: لا تحل له أبداً، وتخيير نسائه صريح في وجه،',NULL,'174',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (162,'وفي حق غيره كناية قطعاً، وعلى الصراحة تكون بائناً يوجب تحريم الأبد في وجه بخلاف غيره.
ومرجع هذه الخصائص إلى النكاح في حقه كالتسري في حقِّنا، وحرم أمته فلم تحرم عليه، ولم تلزمه كفارة، وكان له أن يستثني في كلامه بعد حين منفصلاً، واصطفى ما شاء من الغنيمة قبل القسمة من جارية وغيرها وكذا من الفيء،',NULL,'175',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (163,'ذكره ابن كج في "التجريد".
وخُمسُ خُمس الفيء والغنيمة وأربعة أخماس الفيء، وكان له الأنفال يفعل فيها ما يشاء.
وذكر مالك في "خصائصه": أنه لم يكن يملك الأموال، إنما كان له التصرف والأخذ بقدر كفايته.',NULL,'176',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (164,'وعند الشافعي وغيره يملك، وأن يحمي الموات لنفسه ولا ينقض ما حماه، ومن أخذ شيئاً مما حماه ضمن قيمته في الأصح، بخلاف ما حماه غيره من الأئمة، ولو رعاه ذو قوة فلا غرم عليه.
والقتال بمكة وحمل السلاح والقتل بها، والقتل بعد الأمان،',NULL,'177',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (165,'ويلعن من شاء بغير سبب، ويكون له رحمة، والقضاء بعلمه، وفي غيره خلاف ولنفسه ولولده، وأن يشهد لنفسه ولولده، وأن يقبل شهادة من يشهد له ولولده،',NULL,'178',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (166,'وقبول الهدية بخلاف غيره من الحكام.
ولا يكره له الفتوى والقضاء في حال الغضب، ذكره النووي في "شرح مسلم"، ولو قال: لفلان على فلان كذا، جاز لسامعه أن يشهد بذلك. ذكره شريح والروياني في "روضة الحكام".
وكان له قتل من اتهمه بالزنا من غير بينة، ولا يجوز ذلك لغيره، ذكره ابن دحية.
وكان له أن يدعو لمن شاء بلفظ الصلاة، وليس لنا أن نصلى إلا على نبي أو مَلَك، وضحى',NULL,'179',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (167,'عن أمته وليس لأحد أن يضحي عن الغير بغير إذنه، وأكل من طعام الفجأة مع نهيه عنه، ذكر هذه ابن القاص، وأنكرها البيهقي وقال: إنه مباح لأمته والنهي لم يثبت.
وله أن يجمع في الضمير بينه وبين الله تعالى بخلاف غيره، ذكره ابن عبد السلام وغيره،',NULL,'180',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (168,'وله قتل من سبه أو هجاه، عدّ هذه ابن سبع.
وكان يُقطع الأراضي قبل فتحها، لأن الله ملكه الأرض كلها.
وأفتى الغزالي بكفر من عارض أولاد تميم الداري فيما أقطعهم وقال: إنه صلى الله عليه وآله وسلم كان يقطع أرض الجنة، فأرض الدنيا أولى.
وذكر الشيخ تاج الدين بن عطاءالله في التنوير": أن الأنبياء لا تجب عليهم الزكاة، لأنهم لا ملك لهم مع الله، إنما كانوا يستهدون ما في أيديهم من ودائع الله لهم، يبذلونه في أوان بذله، ويمنعونه في غير محله،',NULL,'181',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (169,'لأن الزكاة إنماهي طهرة لما عساه أن يكون ممن وجبت عليه، والأنبياء مبرؤون من الدنس لعصمتهم.
وعقد المساقاة لأهل خيبر إلى مدة مبهمة بقوله: "أقركم ما أقركم الله" لأنه كان يجوز مجيء الوحي بالنسخ، ولا يكون ذلك بعده.
وحلَفَ لا يحمل الأشعريين ثم حملهم، وقال: "لست أنا حملتكم ولكن الله حملكم" ولم يترتب عليه حنث ولا كفارة.
وعانق جعفراً عند قدومه من السفر، فقال مالك: هو خاص به، وكرهها لغيره.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (170,'وقال الخطابي: زعم بعضهم أن المَنَّ على الأسرى الوارد في قوله تعالى: (فإما مَنّاً بعد وإما فداء) كان خاصاً بالنبي صلى الله عليه وآله وسلم دون غيره.

<span data-type="title" id=toc-10>الفصل الرابع</span>
فيما اختص به صلى الله عليه وآله وسلم من الكرامات والفضائل
اختُص صلى الله عليه وآله وسلم بمنصب الصلاة، وبأنه لا يُورَث، وكذلك الأنبياء، فلهم أن يوصوا بكل مالهم صدقة،',NULL,'183',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (171,'وبأن ماله باق بعد موته على ملكه يُنفق منه على أهله في أحد الوجهين وصححه إمام الحرمين.
وأنه لو قصده ظالم وجب على من حضره أن يبذل نفسه دونه، حكاه في "زوائد الروضة" عن جماعة من الأصحاب.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (172,'قال قتادة: وكان من خصائصه: أنه إذا غزا بنفسه يجب على كل أحد الخروج معه، لقوله تعالى: (ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله) ، ولم يبق هذا الحكم مع غيره من الخلفاء. انتهى.
وكان إذا حضر الصف يحرم على من معه أن يولوا عنه الدُبر لئلا ينهزموا ويتركوه، قاله قتادة والحسن، وذهبا إلى أن الفرار من الزحف بعده ليس من الكبائر، وكان الجهاد في عهده فرض عين في أحد الوجهين عندنا وهو من بعده من فروض الكفاية.
ورأيت في بعض "المجاميع" عن التكريتي: أن مهر المثل لا يتصور في ابنته',NULL,'185',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (173,'لأنه لا مثل لها وهو حسن بالغ، وتحرم رؤية أشخاص أزواجه في الأًزُر، كما صرح به القاضي عياض وغيره، وكشف وجههن وأكفهن لشهادة أو غيرها، وسؤالهن مشافهة، وصلاتهن على ظهور البيوت.
وقال معمر: إن أزواجه صلى الله عليه وآله وسلم إذا أرضعن الكبير دخل عليهن، فكان ذلك لهن خاصة، ولسائر الناس لا يكون إلا ما كان في الصغر.
وقال طاووس: كان لهن رضعات معلومات، ولسائر النساء رضعات معلومات، وورد أنها عشر رضعات لهن، ولغيرهن خمس، وأنهن أمهات المؤمنين،',NULL,'186',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (174,'ووجوب جلوسهن بعده في البيوت، وتحريم خروجهن ولو لحج أو عمرة في أحد القولين، وأباح لهن ولآله الجلوس في المسجد مع الحيض والجنابة،',NULL,'187',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (175,'وكذا العبور عند المالكية.
وأن تطوعه في الصلاة قاعداً كتطوعه قائماً، وأن عمله له نافلة.
ويخاطبه المصلي بقوله: السلام عليك أيها النبي، ولا يخاطب غيره، وكان يجب على من دعاه وهو في الصلاة أن يجيبه، ولا تبطل صلاته،',NULL,'188',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (176,'وكذلك الأنبياء، ومن تكلم وهو يخطب بطلت جمعته، وكان يجب الاستماع والإنصات لقراءته إذا قرأ في الصلاة الجهرية، وعند نزول الوحي.
وقال مجاهد في قوله تعالى: (إذا قيل لكم تفسحوا في المجالس فافسحوا) : مجلس النبي صلى الله عليه وآله وسلم خاصة.
وقال جابر بن عبد الله: ليس على من ضحك في الصلاة إعادة وضوء، إنما كان',NULL,'189',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (177,'ذلك لهم حين ضحكوا خلق رسول الله صلى الله عليه وآله وسلم.
والنكاح في حقه عبادة مطلقاً كما قال السبكي، وهو في حق غيره ليس عبادة عندنا، بل من المباحات، والعبادة عارضة له.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (178,'والكذب عليه كبيرة ليس كالكذب على غيره، وقال الجويني: رِدَّة، ومن كذب عليه لم تقبل روايته أبداً وإن تاب فيما ذكره خلائق من أهل الحديث.
ويحرم التقدم بين يديه ورفع الصوت فوق صوته،',NULL,'191',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (179,'والجهر له بالقول ونداؤه من وراء الحجرات، والصياح به من بعيد، وأن يُقال فيه: أبونا في أحد الوجهين، وأن يقولوا له راعِنا.
وطهارة دمه وبوله وغائطه، وسائر فضلاته',NULL,'192',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (180,'تشرب ويُستشفى بها، ولا خلاف في طهارة شعره، وفي غيره خلاف، وقد قسم شعره على أصحابه.
والعِصمة من كل ذنب ولو صغير أو سهواً، وكذلك الأنبياء،',NULL,'193',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (181,'وينزه عن فعل المكروه، ومحبته فرض، وتجب محبة أهل بيته وأصحابه، ومن استهان به كفر، قيل: أو زنى بحضرته، ومن تَمنّى موته كفر، وكذلك الأنبياء، ذكره المحاملي في "الأوسط"، ورتب عليه تحريم إرثهم،',NULL,'194',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (182,'لئلا يتمناه ورثتهم فيكفروا.
قال غيره: ولذا لم يشب شعره، لأن النساء يكرهن الشيب، ولو وقع ذلك في أنفسهن كفرن، فعُصم من ذلك رفقاً بهن.
ومن سبه قتل، وكذلك الأنبياء، والسَبُّ بالتعريض في حقه كالتصريح بخلاف غيره، نقله الرافعي عن الإمام، وقال النووي: لا خلاف فيه ولم تبغ امرأة نبي قط، وقال الحسن: امرأة النبي إذا زنت لم يغفر لها، ومن قذف أزواجه فلا توبة له البتة، كما قال ابن عباس وغيره،',NULL,'195',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (183,'ويقتل كما نقله القاضي عياض.
وفي قوله يختص القتل بمن سبَّ عائشة، ويُحذُّ في غيرها حدّين، وكذا من قذف أمَّ أحد من أصحابه.
وذهب بعض المالكية: إلى أن من سبَّ أصحابه قتل، وقال ابن قدامة في "المُقنع": من قذف أمَّ النبي صلى الله عليه وآله وسلم قتل، مسلماً كان أو كافراً،',NULL,'196',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (184,'وأولاد بناته ينسبون إليه، قيل وأولاد بنات بناته.
وفي حديث: "إن الله لم يبعث نبياً قط إلا جعل ذريته من صلبه، غيري، فإن الله جعل ذريتي من صلب علي". ولا يتزوج على بناته.
وذكر المحب الطبري ماهو أبلغ من ذلك، فإنه أورد حديث المِسْور بن مخرمة لما خطب إليه حسن بن حسن، فاعتذر إليه بقوله صلى الله عليه وآله وسلم: "فاطمة بضعة مني',NULL,'197',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (185,'يقبضني ما يقبضها ويبسطني ما يبسطها".
قال: وعندك ابنتها، ولو زوجتك لقبضها ذلك.
ثم قال: فيه دليل على أن الميت يراعى منه ما يراعى من الحي.
قال: وقد ذكر الشيخ أبو علي السنجي في: شرح التلخيص": قد يحرم التزويج على بنات النبي صلى الله عليه وآله وسلم، ولعله يريد من ينسب إليه بالبُنُوّة ويكون هذا دليله. انتهى.
فإن أخذ هذا على عمومه، فمقتضاه أنه يحرم التزويج على ذرية بناته وإن سَفُلن إلى يوم القيامة وفيه وقفة. ومن صاهر من الجانبين لم يدخل النار.',NULL,'198',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (186,'ولا يجتهد في محراب صلى إليه في يمنة ولا في يسرة، وتختص صلاة الخوف بعهده، في قول أبي يوسف والمزني، لأن إمامته لا عوض عنها بخلاف غيره، ويجل منصبه عن الدعاء له بالرحمة',NULL,'199',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (187,'فيما ذكره جماعة.
ويحرم النقش على نقش خاتمه، وليس لأحد أن ينقش على خاتمه محمد رسول الله، ولا ينطق عن الهوى، ولا يقول في الغضب والرضا إلا حقاً، ورؤياه',NULL,'200',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (188,'وحي، وكذا الأنبياء، ولا يجوز على الأنبياء الجنون ولا الإغماء الطويل الزمن، فيما ذكره الشيخ أبو حامد في "تعليقه"، وجزم به البلقيني في "حواش الروضة" ونبه السبكي على أن إغماءهم يخالف إغماء غيرهم، كما خالف نومهم نوم غيرهم.
ويخص من شاء بما شاء من الأحكام، كجعله شهادة خُزيمة بشهادة رجلين، ولا يجوز علهيم العمى فيما ذكره السبكي.
قال القاضي عياض في حديث قول بني اسرائيل عن موسى: آدر، وتبرئة الله له: الأنبياء منزهون عن النقائص في الخَلق والخُلق، سالمون من العاهات',NULL,'201',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (189,'والمعائب، ولا التفات إلى ما يقع في بعض التواريخ من إضافة بعض العاهات إلى بعضهم، بل نزههم الله من كل عيب، وكل ما ينقص من العيوب أو ينفر القلوب.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (190,'وترخيصه في إرضاع سالم وهو كبير، وفي النياحة لتلك المرأة',NULL,'203',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (191,'وهي خولة بنت حكيم، وفي تعجيل صدقة عامين للعباس، وفي ترك الإحداد لأسماء بنت عميس، وفي الجمع بين اسمه وكنيته للولد الذي يولد لعلي، وفي المُكث في المسجد جنباً لعلي، وفي فتح باب من داره في المسجد له، وفي فتح خوخة فيه لأبي بكر،',NULL,'204',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (192,'وفي أكل المُجامِع في رمضان من كفارة نفسه، وفي الأضحية بالعَناق لأبي بردة بن نيار، وبالعَتود لعقبة بن عامر ولزيد بن خالد، وفي نكاح ذلك الرجل بما معه من القرآن، فيما ذكره جماعة، وورد به حديث مرسل،',NULL,'205',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (193,'قال مكحول: ليس ذلك لأحد بعد النبي صلى الله عليه وآله وسلم.
وفي لبس الحرير للزبير وعبد الرحمن بن عوف، فيما قاله جماعة، وهو وجه عندنا، ولبس خاتم الذهب للبراء بن عازب، وفي اشتراط عائشة الولاء لموالي بَريرة، ولا يوفى به فيما ذكره بعضهم، وفي العرية لثعلبة بن يزيد الحارثي وذويه، فيما ذهب إليه الواقدي، وفي خيار الغبن لحبان بن منقذ، فيما ذكره النووي في "شرح مسلم".',NULL,'206',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (194,'وفي التحلل بالمرض لضباعة بنت الزبير في أحد القولين، وفي ترك مبيت مِنى لأجل السقاية لبني العباس في وجه، ولبني هاشم في آخر، ولعائشة في صلاة ركعتين بعد العصر،',NULL,'207',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (195,'ولمعاذ بن جبل في قبول الهدية حين بعثه إلى اليمن.
وفي "المستدرك" وغيره، عن أنس أن أم سليم تزوجت أبا طلحة على إسلامه، قال ثابت: ما سمعت بامرأة قط كانت أكرم مهراً من أم سليم وهو الإسلام. وأعاد امرأة أبي ركانة إليه بعد أن طلقها ثلاثاً من غير محلل،',NULL,'208',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (196,'وأسلم رجل على أن لا يصلي إلا صلاتين فقبل منه ذلك، وضرب لعثمان يوم بدر بسهم ولم يضرب لأحد غاب غيره، رواه أبو داود عن ابن عمر.
وقال الخطابي: هذا خاص بعثمان، لأنه كان يمرض ابنة رسول الله صلى الله عليه وآله وسلم.
وكان يؤاخي بين أصحابه ويثبت بينهم التوارث، وليس ذلك لغيره، قاله ابن زيد.
وخص نساء المهاجرين بأن يَرثن دون أزواجهن لكونهن غرائب',NULL,'209',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (197,'لا مأوى لهن، وكان أنس يصوم من طلوع الشمس لا من طلوع الفجر فالظاهر أنها له خصوصية، وأصام أطفال أهل بيته وهم رُضَّع.
وكان يحرم على الصحابة إذا كانوا معه على أمر جامع، لم يذهبوا حتى يستأذنوه،',NULL,'210',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (198,'وكانوا يقولون له بأبي أنت وأمي، ولا يقال لغيره، فيما ذكره بعضهم، وكان يرى من خلفه كما ينظر أمامه.
زاد رزين: وعن يمينه وعن شماله، ويرى بالليل وفي الظلمة كما يرى بالنهار والضوء.
وريقه يُعذب الماء المالح ويجزي الرضيع، وإبطه أبيض، غير متغير اللون ولا شعر عليه،',NULL,'211',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (199,'ويبلغ صوته وسمعه ما لا يبلغه غيره، وتنام عينه ولا ينام قلبه، وما تثاءب قط، ولا احتلم قط، وكذلك الأنبياء في الثلاثة.
وعرقه أطيب من المسك،',NULL,'212',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (200,'وكان إذا مشي مع الطويل طاله، وإذا جلس يكون كتفه أعلى من جميع الجالسين، ولم يقع ظله على الأرض، ولا رؤي له ظل في شمس ولا قمر.
قال ابن سبع: لأنه كان نوراً، وقال رزين: لغلبة أنواره.
ولم يقع على ثوبه ذباب قط، ولا آذاه القمل،',NULL,'213',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (201,'وكان إذا ركب دابته لا تروث ولا تبول وهو راكبها، نقل ذلك عن ابن اسحاق، وبني عليه بعض المتأخرين طوافه على بعيره صلى الله عليه وآله وسلم، فجعله من خصائصه، ولم يجز لغيره ذلك.
وكان وجهه كأن الشمس تجري فيه، ولم يكن لقدمه أخمص، وكانت خُنصُر رجله متظاهرة،',NULL,'214',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (202,'وكانت الأرض تطوى له إذا مشى، وأوتي قوة أربعين في الجماع والبطش.
وفي رواية مقاتل: أعطي قوة بضع وسبعين شاباً.
وعن مجاهد: أعطي قوة بضع وأربعين رجلاً كل رجل من أهل الجنة، وقوة الرجل من أهل الجنة كمائة من أهل الدنيا،',NULL,'215',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (203,'فيكون أوتي قوة أربعة آلاف. وبهذا يندفع ما يستشكله بعضهم.
فقال: كيف يؤتى قوة أربعين فقط وقد أوتي سليمان قوة مائة رجل أو ألف رجل على ماورد؟ واحتاج إلى تكلف الجواب عن ذلك.
وورد من طرق: أتاني جبريل بقدر فأكلت منها فأعطيت قوة أربعين رجلاً في الجماع، وفي لفظ: ما أريد أن آتي النساء ساعة إلا فعلت.',NULL,'216',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (204,'وقال القاضي أبوبكر بن العربي في "سراج المريدين": قد آتى الله رسوله خصيصة عظمى: وهي قلة الأكل والقدرة على الجماع، وكان أقنع الناس في الغذاء، تقنعه اللقمة، وتشبعه اللقمة والجرعة، وكان أقوى الناس على الوطء، ولم يُرَ له أثر قضاء حاجة، بل كانت الأرض تبتلعه، ويشم من مكانه رائحة المسلك، وكذلك الأنبياء.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (205,'ولم يقع في نسبه من لدن آدم سفاح قط، وتقلب في الساجدين حتى خرج نبياً، وما افترقت فرقة إلا كان في خيرها، ولم يلد أبواه غيره.
ونكست الأصنام لمولده، وولد مختوناً، ومقطوع السرة، ونظيفاً ما به قذر,',NULL,'218',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (206,'ووقع إلى الأرض ساجداً، رافعاً إصبعه كالمتضرع المبتهل.
ورأت أمه عند ولادته نوراً خرج منها أضاء له قصور الشام وكذلك أمهات النيبين يَرَين.',NULL,'219',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (207,'قال بعضهم: ولم تُرضعه مُرضعة إلاأسلمت، قال: ومرضعاته أربع، أمه، وقد ورد إحياؤها وإيمانها في حديث، وحليمة السعدية،',NULL,'220',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (208,'وثويبة، وأم أيمن. انتهى.
وكان مهده يتحرك بتحريك الملائكة، ذكر هذه ابن سبع.
وكان القمر يُناغيه وهو في مهده، ويميل حيث أشار إليه،',NULL,'221',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (209,'وتكلم في المهد، وتظله الغمامة في الحر، ويميل إليه فيء الشجرة إذا سبق إليه،',NULL,'222',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (210,'وكان يبيت جائعاً ويصبح طاعماً يطعمه ربه ويسقيه من الجنة، وكان يُوعَك كما يُوعَك رجلان لمضاعفة الأجر، وعصم من الأعلال الموجبة، ذكر هذه القضاعي في "تاريخه".
ورُدَّت إليه الروح بعد ما قبض، ثم خير بين البقاء في الدنيا والرجوع إلى الله تعالى، فاختار الرجوع إليه، وكذلك الأنبياء،',NULL,'223',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (211,'وأرسل إليه ربه جبريل ثلاثة أيام في مرضه يسأله عن حاله.
ولما نزل إليه ملك الموت نزل معه ملك يقال له: إسماعيل، يسكن الهواء لم يصعد إلى السماء قط، لم يهبط إلى الأرض قبل ذلك اليوم قط,',NULL,'224',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (212,'وسمع صوت ملك الموت باكياً عليه ينادي: وامحمداه، صلى عليه ربه والملائكة، وصلى عليه الناس أفواجاً بغير إمام، قالوا: هو إمامكم حياً وميتاً، وبغير دعاء الجنازة المعروفة،',NULL,'225',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (213,'وكررت الصلاة عليه حتى فرغت الرجال، ثم النساء، ثم الصبيان، ولا تكرر على غيره عند مالك وأبي حنيفة،',NULL,'226',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (214,'وعند طائفة من خصائصه أنه لم يصل عليه أصلا، وإنما كان الناس يدخلون أرسالاً فيدعون وينصرفون، وعلل بأنه لفضله غير محتاج لذلك، ترك بلا دفن ثلاثة أيام ودفن بالليل، وذلك في حق غيره مكروه عند الحسن، وخلاف الأولى عند سائر العلماء، ودفن في بيته',NULL,'227',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (215,'حيث قبض وكذلك الأنبياء، والأفضل في حق من عداهم الدفن في المقبرة، وفرش له في لحده قطيفة، والأمران في حقنا مكروهان.
قال وكيع: هذا للنبي صلى الله عليه وآله وسلم خاصة، ويكره ذلك لغيره بالاتفاق عند الحنفية والمالكية، ومن خصائصه أنه غسل في قميصه، وقالوا: يكره ذلك في حق غيره، وأظلمت الأرض يوم موته، ولا يضغط في قبره وكذلك الأنبياء، ولم يسلم من الضغط لا صالح ولا غيره سواهم.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (216,'وفي "التذكرة" للقرطبي: إلا فاطمة بنت أسد ببركته صلى الله عليه وآله وسلم، وتحرم الصلاة على قبره، واتخاذه مسجداً، قال الأذرعي: ويحرم البول عند قبور الأنبياء، ويكره عند غيرهم.
ولايبلى جسده وكذلك الأنبياء، ولا تأكل لحومهم الأرض ولا السباع ولا خلاف في طهارة ميتهم، وفي غيرهم خلاف.
ولا يجري في أطفالهم التوقف الذي لبعضهم في غيرهم.
ولا يجوز للمضطر',NULL,'229',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (217,'أكل ميتة نبي، وهو حي في قبره، يصلى فيه بأذان وإقامة وكذلك الأنبياء، ولهذا قيل: لا عدة على أزواجه، وأوكل بقبره ملك يبلغه صلاة المصلين عليه، وتعرض عليه أعمال أمته فيستغفر لهم،',NULL,'230',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (218,'والمصيبة بموته عامة لأمته إلى يوم القيامة، وجواز التضحية عنه بعد وفاته فيما ذكره البلقيني.
ومن رآه في المنام فقد رآه حقاً، وأن الشيطان لا يتمثل في صورته، ومن أمره في المنام وجب عليه امتثاله في أحد الوجهين، واستحب في الآخر،',NULL,'231',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (219,'وورد أن أول ما يرفع رؤيته صلى الله عليه وآله وسلم في المنام، والقرآن، والحجر الأسود، وقراءة أحاديثه عبادة يثاب عليها كقراءة القرآن في أحد الروايتين، ولا تأكل النار شيئاً مَسَّ وجهه وكذلك الأنبياء،',NULL,'232',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (220,'والتسمي باسمه ميمون ونافع في الدنيا والآخرة.
ويكره أن يحمل في الخلاء ما كتب عليه اسمه، ويستحب الغسل لقرآة حديثه، والتَطَيَّب، ولا ترفع عنده الأصوات ويقرأ على مكان عال،',NULL,'233',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (221,'ويكره لقارئه أن يقوم لأحد، وحملته لا تزال وجوههم نضرة لقوله صلى الله عليه وآله وسلم: "نضر الله امرءاً سمع مقالتي فوعاها، فأداها كما سمعها".
واختصوا بالتلقيب بالحفاظ، وأمراء المؤمنين، من بين سائر العلماء،',NULL,'234',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (222,'وتجعل كتبه على كرسي كالمصحف، وتثبت الصحبة لمن اجتمع به صلى الله عليه وآله وسلم لحظة، بخلاف التابعي مع الصحابة، فلا تثبت إلا بطول الإجتماع معه على الأصح عند أهل الأصول، والفرق عظم منصب النبوة ونورها، فبمجرد ما يقع بصره على الأعرابي الجِلْف يَنطِق بالحكمة.
وأصحابه كلهم عدول،',NULL,'235',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (223,'فلا يبحث عن عدالة أحد منهم كما يبحث عن سائر الرواة، ولا يفسقون بارتكاب ما يفسق به غيرهم، كما ذكره في "شرح جمع الجوامع".
وقال محمد بن كعب القرظي: أوجب الله لجميع الصحابة الجنة والرضوان في كتابه مُحسنهم ومُسيئهم، وشرط على من بعدهم أن يتبعوهم بإحسان.
ولا يكره للنساء زيارة قبره، كما يكره لهن سائر القبور، بل يستحب كما قال',NULL,'236',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (224,'العراقي في "نكته" أنه لا شك فيه، والمصلي في مسجده لا يبصق عن يساره كما هو السنة في سائر المساجد، ولو بُني مسجده إلى صنعاء كان مسجده، ولا يفتح فيه باب ولا خوخة ولا كوة بحال.
ووكل بشفتي كل انسان ملكان',NULL,'237',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (225,'ليس يحفظان إلا الصلاة عليه خاصة.
ومن خصائصه: وجوب الصلاة عليه في التشهد الأخير عندنا، عدها في "الخادم" أخذاً من "الحلبيات" للسبكي، وكلما ذكر عند الحليمي والطحاوي، لأنه ليس بأقل من تشميت العاطس، واختاره من المتأخرين القاضي تاج الدين السبكي.
ومن صلى عليه عند الأمر الذي يُستقذر أو يُضحك منه، أو جعل الصلاة عليه كفاية عن شتم الغير: كفر، ذكره الحليمي ونقله في "الخادم".
ومن حكم عليه وكان في قلبه حرج',NULL,'238',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (226,'من حكمه عليه كفر، بخلاف غيره من الحكام، ذكره الاصطرخي في "آداب القضاء".
ومن خصائصه: أن الإمام بعده لا يكون إلا واحداً، ولم تكن الأنبياء قبله كذلك، قاله ابن سراقة في "الأعداد".
وجواز الوصية لآله مطلقاً، وفي غيره وجه أنها لا تصح لإبهام اللفظ، وتردده بين القرابة والدّين، ذكروه في "باب الوصية".
وأن أهله لا يكافئهم في النكاح أحدٌ من الخَلْق، ذكروه',NULL,'239',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (227,'في "باب النكاح"، ويطلق عليهم أشراف، والواحد شريف، وهم ولد علي وعقيل وجعفر، والعباس.
كذا مصطلح السلف، وإنماحدث تخصيص الشريف بولد الحسن والحسين في مصر خاصة في عهد الخلفاءالفاطميين.
وذكر صاحب "الفتاوي الظهيرية" من الحنفية: أن من خصائصه صلى الله عليه وآله وسلم أن ابنته فاطمة رضي الله عنها لم تحض، ولما ولدت طهرت من نفاسها بعد ساعة حتى لا تفوتها صلاة، قال:',NULL,'240',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (228,'ولذلك سُميت الزهراء، وقد ذكره من أصحابنا المحب الطبري في "ذخائر العقبى". وأورد فيه حديث أنها حوراء آدمية طاهرة مطهرة لا تحيض ولا يرى لها دم في طمث ولا في ولادة.
وفي "الدلائل" للبيهقي: أنه صلى الله عليه وآله وسلم وضع يده على صدرها فرفع عنها الجوع فما جاعت بعد.
وفي "مسند أحمد" وغيره: أنها لما احتضرت غسلت نفسها وأوصت أن لا يكشفها أحد، فدفنها علي بغسلها ذلك.
وذكر الإمام علم الدين العراقي: أن فاطمة وأخاها إبراهيم أفضل من الخلفاء الأربعة باتفاق.',NULL,'241',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (229,'ونقل عن مالك أنه قال: لا أفَضل على بضعة من النبي صلى الله عليه وآله وسلم أحداً، وفي "معاني الآثار" للطحاوي قال أبو حنيفة: كان الناس لعائشة محرماً، فمع أيهم سافرت فقد سافرت مع مَحرم، وليس الناس لغيرها من النساء كذلك.
ومما أورده رزين في خصائصه: أن شيئاً من شَعْره وقع في النار فلم يحترق (*)
_________
(*) قال مُعِدُّ الكتاب للشاملة: الزيادة بعد هذا ليست في المطبوع',NULL,'242',NULL,NULL,'0');
INSERT INTO [b12824] VALUES (230,'وأنه مسح بيده رأس أقرع فنبت شعره في وقته، ووضع كفه على المريض فعقل من ساعته، وغرس نخلاً فأثمرت من عامها، وهز بيده عمر فأسلم من ساعته، وأنه كانت أصبعه المسبحة أطول أصابعه، وما أشار بها إلى شيء إلا أطاعه، ولا وطيء على صخر إلا وأثر فيه، أو في نَخلٍ إلا وبورك فيها، وأنه كان إذا تبسم في الليلة أضاء البيت، وأنه كان يسمع حفيف أجنحة جبريل وهو بَعدُ في سدرة المنتهى، ويشم رائحته إذا توجه بالوحي إليه، وأنه ما التصق ببدنه مسلم فتمسه النار، وكان فئة المسلمين يتحيزون إليه، وكان قليل الكلام، فإذا أمر بالقتال شمر، وحُرِّم على الناس دخول بيته بغير إذنه، وطول القعود فيه. انتهى.
وفي "نكت الحاوي" للناشري: روي عنه صلى الله عليه وآله وسلم أنه لم يصلّ على ابنه ابراهيم، قال بعض العلماء لأنه استغنى بنبوة أبيه عن قُربة الصلاة، كما استغنى الشهيد بقُربة الشهادة.
وفي "المستدرك" عن أنس: أنه صلى الله عليه وآله وسلم صلى على حمزة ولم يصل على أحد من الشهداء غيره، وفي حديث أنه كبر عليه سبعين تكبيرة، وفي آخر أنه صلى عليه سبعين صلاة.
وفي "الصحيحين" وغيرهما، من حديث عقبة بن عامر أنه خرج يوماً فصلى على أهل أحُد صلاته على الميت، وذلك قرب موته، بعد ثمان سنين من وفاتهم.
وفي "الصحيح" أنه خرج إلى أهل البقيع فصلى عليهم.
قال القاضي عياض عن بعضهم: محتمل أن تكون الصلاة المعلومة على الموتى، ويكون هذا خصوصاً بآله، ويكون أراد أن يعمهم بصلاته إذ فيهم من دفن وهو غائب، أو لم يعلم به فلم يصلّ عليه، فأراد أن تعمهم بركته.
ومن الخصائص: أنه يجوز أن يقال للنبي صلى الله عليه وآله وسلم: احكم بما تشاء فما حكمت به فهو صواب موافق لحكمي على ما صححه الأكثرون في الأصول، وليس ذلك للعالم على ما اختاره السمعاني لقصور رتبه.
وذهبت طائفة إلى أن من خصائصه: امتناع الاجتهاد له لقدرته على اليقين بالوحي، ولغيره في عصره لقدرته على اليقين بتلقينه منه، وأجمعوا على أنه لا ينعقد الإجماع في عصره.
وفي "شرح المنار" لسكاكي: الإلهام حُجة على المُلهم وغيره، إن كان المُلهم نبياً وعلم أنه من الله، لا إن كان ولياً.
وفي "تفسير ابن المنذر" عن عمرو بن دينار، أن رجلاً قال لعمر: احكم بما أراك الله، فقال: مه إنما هذه للنبي صلى الله عليه وآله وسلم خاصة.
وفي "سنن سعيد بن منصور" عن سعيد بن جبير قال: ما سمعنا قط أن نبياً قتل في القتال.
وفي "المبسوط" من كتب الحنفية عن بعضهم: أن الوقف إنما يلزم من الأنبياء خاصة دون غيرهم، وحمل عليه حديث "لا نورث ما تركناه صدقة" وجعله هذا القائل مستثنى من قول أبي حنيفة: إن الوقف لا يلزم.
وفي "تفسير ابن المنذر" عن ابن جريج: كانوا إذا دخلوا على النبي صلى الله عليه وآله وسلم بدأهم بالسلام، فقال: سلام عليكم، وإذا لقيهم فكذلك أيضاً، لقوله تعالى (وإذا جاءك الذين يؤمنون بآياتنا فقل سلام عليكم) وفي هذا خصيصتان: ابتداؤه بالسلام على الداخل والمار، والسُّنة في حقنا أن الداخل والمار هو الذي يبدأ، ووجوب الابتداء عليه للأمر به في الآية، وليس أحد من الأمة يجب عليه الابتداء.
ومن خصائصه: أنه يجوز له رؤية الله تعالى في المنام، ولا يجوز ذلك لغيره في أحد القولين، وهو اختياري وعليه أبو منصور الماتريدي.
وفي "الرسالة" للإمام الشافعي: لا يحيط باللغة إلا نبي.
وفي "المستدرك" حديث: ليس لنبي أن يدخل بيتاً مزَوَّقاً، وقال ابن عباس: ما تنور (١) نبي قط.
وقال قتادة: إنما عبارة الرؤيا بالظن فيحق الله منهاما يشاء ويبطل ما يشاء، قال ابن جرير: هو كذلك في غير الأنبياء، وأما الأنبياء فيما عبروه كائن لا محالة, وكذب ثعلبة بن حاطب فامتنع من أخذ الزكاة منه عقوبة له فلم يقبلها منه أبوبكر، ولا عمر، ولا عثمان حتى مات في خلافته.
وكذبت تميمة بنت وهب فامتنع من ردها إلى مُطلِّقها رفاعة فلم يرجعها إليه أبوبكر ولا عمر، وقال لها عمر: لئن أتينتني بعد هذه لأرجمنك.
_________
(١) يعني: أنه ما احتاج إلى إزالة شعر العانة بالنورة.',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b12824] VALUES (231,'وغل رجلٌ زماماً من شعر ثم أتى به فقال له: كفَّ، أنت تجيئ به يوم القيامة فلن أقيله عنك.
وقال ابن عباس: كل أحد يؤخذ من قوله ويترك إلا النبي صلى الله عليه وآله وسلم.
وقال ابن عباس في قوله تعالى: (له معقِّباتٌ من بين يديه ومن خلفه يحفظونه من أمر الله) : هذه للنبي صلى الله عليه وآله وسلم خاصة.
وفي "مسند الشافعي" حديث "نصرت بالصبا وكانت عذاباً على من قبلي".
وفي أثر: أن آله صلى الله عليه وآله وسلم في أعلا ذروة في الجنة.
وفي الحديث: "مثل أهل بيتي مثل سفينة نوح من ركبها نجا، ومن تخلف عنها غرق"، وأن من تمسك بهم وبالقرآن لم يضل، وأنهم أمان للأمة من الاختلاف، وأنهم سادة أهل الجنة، وأن الله وعد أن لا يعذبهم، وأن من أبغضهم أدخله الله النار، ولا يدخل قلب أحدٍ الإيمان حتى يحبهم لله ولقرابتهم منه صلى الله عليه وآله وسلم، وأن من قاتلهم كان كمن قاتل مع الدجال، وأن من صنع إلى أحدهم يداً كافأه صلى الله عليه وآله وسلم، يوم القيامة، وأنهم ما من أحد إلا وله شفاعة يوم القيامة، وأن الرجل يقوم لأخيه من مجلسه إلا بني هاشم لا يقومون لأحد.
وشرع في عهده أحكام ثم نُسخت، فعل بها أصحابه ولم يعمل بها أحدٌ بعدهم.
منها: فسخ الحج إلى العمرة عند الجمهور، ومتعة النساء عند أكثر الأمة، ومتعة الحج فيما ذهب إليه عمر وعثمان وأبو ذر.
وروى مسلم عن أبي ذر قال: "لاتصح المتعتان إلا لنا خاصة".
والخلع فيما ذهب إليه أبوبكر بن عبد الله المزني، وقراءة القرآن بالمعنى، ووجوب الضيافة، وإنفاق الفضل، واسترقاق المديون، وأن لا غُسل إلا من الإنزال، والتخيير بين صوم رمضان والفدية، وتحريم زيارة القبور، وادخار الأضحية فوق ثلاث، والانتباذ في الأوعية، ونكاح الزاني العفيفة والزانية العفيف، والقتال في الشهر الحرام، ووجوب الوصية للوالدين والأقربين، واعتداد المتوفّي عنها حولاً، ومصابرة العشرين مئتين، والقسمة من التركة لمن حضر، واستئذان الصبيان والأرقاء في الأوقات الثلاث، وقيام الليل إلا قليلاً، والإرث بالحِلْف وبالهجرة، والمحاسبة بحديث النفس، والحبس في الزنا، والتعزير بإتلاف المال، وشهادة الكافر، وصلاة المؤمنين جلوساً خلف الإمام الجالس، وإن لم يكن لهم عُذر، والخطبة للجمعة بعد الصلاة، والوضوء مما مست النار، وكراهة الحَبْوة وقت الخطبة، وتحريم تحلي النساء بالذهب، وتحريم المسألة لمن عنده غداء يومه وعشاؤه، وقتل شارب الخمر في الرابعة، والمنع من دفن الموتى في أوقات الكراهة.
وذهب المالكية إلى أن حديث "لا يجلد فوق عشرة أسواط إلا في حَد" كان مختصاً بزمنه صلى الله عليه وآله وسلم لأنه كان يكفي الجاني منه هذا القدر.
ومن خصائصه فيما حكى القاضي عياض: أنه لا يجوز لأحد أن يؤمه، لأنه لا يصح التقدم بين يديه في الصلاة ولا غيرها، لا لعذر ولا لغيره، وقد نهى الله المؤمنين عن ذلك، ولا يكون أحد شافعاً له، وقد قال صلى الله عليه وآله وسلم "أئمتكم شفعاؤكم"، ولذلك قال أبوبكر: ما كان لابن أبي قحافة أن يتقدم بين يدي رسول الله صلى الله عليه وآله وسلم.
وخص أهل بدر من أصحابه بأن زادوا في الجنازة على أربع تكبيرات، تمييزاً لهم لفضلهم.
ومن خصائصه: أن من أصحابه من اهتز العرش لموته فرحاً بلقاء روحه، وحضر جنازته سبعون ألف من الملائكة لم يطأوا الأرض قبل موته، ومن غسلته الملائكة، ومن يُشبَّه بجبريل وابراهيم، وبنوح وبموسى، وبعيسى وبيوسف، وبلقمان الحكيم، وبصاحب يس.
وفي "طبقات ابن سعد" عن عمر بن سليمان قال: الحسن والحسين اسمان من أسماء أهل الجنة لم يكونا في الجاهلية. وفيها عن سعيد بن المسيب أنه كان لا يستحب أن يُسمى ولده بأسماء الأنبياء.
وفي "جامع الثوري" و"مصنف عبد الرزاق" عن سعيد بن المسيب أنه رأى قوماً يسلمون على النبي صلى الله عليه وآله وسلم، فقال: "ما مكث نبي في قبره أكثر من أربعين يوماً حتى يرفع".',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b12824] VALUES (232,'وأورد إمام الحرمين في "النهاية"، والرافعي في "الشرح الصغير" حديث: أنه صلى الله عليه وآله وسلم قال: "أنا أكرم على ربي من أن يتركني في قبري بعد ثلاث".
وفي "كفاية المعتقد" لليافعي: قال بعضهم: اليقين: اسم ورسم، وعلم، وعين، وحق. فالاسم والرسم للعوام، والعلم علم اليقين للأولياء، وعين اليقين لخواص الأولياء، وحق اليقين للأنبياء، وحقيقة حق اليقين اختص بها نبينا صلى الله عليه وآله وسلم.
وقال الشيخ تاج الدين بن عطاء الله: الأنبياء يطالعون بحقائق الأمور، والأولياء يطالعون بتمثالها.
وقال اليافعي أيضاً: فرق الشيخ عبد القادر الكيلاني بين ما تسمعه الأنبياء وبين ما تسمعه الأولياء، بأن وحي الأنبياء يسمى كلاماً، وإلهام الأولياء يسمى حديثاً، فالكلام يلزم تصديقه، ومن رده كفر، والحديث من رده لم يكفر.
وقال أبو عمر الدمشقي الصوفي: فرض الله على الأنبياء إظهار المعجزات ليؤمنوا بها، وفرض على الأولياء كتمان الكرامات لئلا يفتتنوا بها.
وقال أبو العباس المروزي السياري: الخَطرة للأنبياء، والوسوسة للأولياء، والفكر للعوام.
وقال النسفي في "بحر الكلام": أرواح الأنبياء تخرج من جسدها وتصير مثل صورتها، مثل المسك والكافور، وأرواح الشهداء تخرج من جسدها وتكون في أجواف طيور خضر.
ومن خصائص الأنبياء: أنهم يُنصب لهم في الموقف منابر من ذهب يجلسون عليها، وليس ذلك لأحد سواهم.
وقال سعيد بن المسيب: لا اعتكاف إلا في مسجد نبي، أخرجه النسائي من حديث قتيبة.
وفي "كرامات الأولياء" لخال ابن السُنّي عن بشر ابن الحارث: أنه ذكر عنده هذه الأحاديث في إجابة الدعاء وغيره، فقال: لست أنكر من هذا إلا شيئين: الذهب والمشي على الماء، فإنه لم يعطه إلا الأنبياء.
وقال النووي في حديث: "مامن مولود يولد إلا نخسه الشيطان إلا مريم وابنها"، ظاهر الحديث اختصاص هذه الفضيلة بعيسى وأمه، وأشار القاضي عياض إلى أن جميع الأنبياء يشاركون فيها.
وفي "حاشية الكشاف" للطيبي في قوله تعالى: (الآن خفف الله عنكم) روى السلمي عن النصر آبادي: هذا التخفيف كان للأمة دون الرسول صلى الله عليه وآله وسلم، ومن يثقله حمل أمانة النبوة كيف يخاطب بتخفيف اللقاء للأضداد؟ وكيف يخاطب وهو الذي يقول: بك أصول، وبك أجول، ومن كان به كيف يخفف عنه أو يثقل عليه؟.
وفي "تاريخ ابن عساكر" عن أبي حاتم الرازي قال: لم يكن في أمة من الأمم منذ خلق الله آدم أمة يحفظون آثار نبيهم غير هذه الأمة.
فقال له رجل: يا أبا حاتم ربما رووا حديثاً لا أصل له.
فقال: علماؤهم يعرفون الصحيح من السقيم، فروايتهم الحديث الواهي ليتبين لمن بعدهم أنهم ميزوا الآثار وحفظوها.
وقال السبكي: إن من صلى مع النبي صلى الله عليه وآله وسلم، وقام معه إلى خامسة عامداً أو سلم من اثنين عامداً لم تبطل صلاته، لأنه يجوز أن يوحى إليه بالزيادة والنقصان، أما بعده صلى الله عليه وآله وسلم فمتى تابع المأموم الإمام في ذلك عامداً بطلت صلاته.
وذكر ابن العربي في "شرح السنن" من خصائصه: الانفراد في السفر وحده لأمنه من الشيطان، بخلاف غيره. وقال ابن دحية في "التنوير" خص الله نبيه صلى الله عليه وآله وسلم بألف خصلة:
منها: صلاة الله تعالى والملائكة عليه، ومنها الرؤية والقرب والدنو، والشفاعة، والوسيلة والفضيلة والدرجة الرفيعة، والبراق والمعراج، والصلاة بالأنبياء، والإسراء، وإعطاء الرضا والسؤال، والكوثر، وسماع القول وإتمام النعمة، والعفو عما تقدم وما تأخر، وشرح الصدر ووضع الوزر، ورفع الذكر وعزة النصر، ونزول السكينة وإيتاء الكتاب، والسبع المثاني، والقرآن العظيم، وأن بعثه رحمة للعالمين، والحكم بين الناس بما أراه الله، وليس ذلك لغيره من الأنبياء عليه وعليهم الصلاة والسلام، حسبما نطق به القرآن العظيم، والقسم بإسمه، وإجابته دعوته، والشهادة بين الأنبياء والأمم يوم القيامة، والمحبة والخلة، وغير ذلك مما لا يحصى كثرة، انتهى.',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b12824] VALUES (233,'ووقفت على كتاب "حسن الاقتصاص لما يتعلق بالاختصاص" للشيخ بدر الدين ابنالدماميني، فوجدته قال فيه: من خصائصه صلى الله عليه وآله وسلم: وجوب وقايته بالنفس.
قال ابن المُنيِّر: أوجب الله في حقه عليه الصلاة والسلام أن يُؤثَر على النفس، وأن يكون أحب إلى كل مؤمن من نفسه.
ولهذا قال سعد يوم أحُد: نحري دون نحرك، فهذا من خصائصه، لا خلاف أن هذا لا يجب لغيره، وهل يجوز أن يفعل لغيره؟ الظاهر أنه لا يجوز بالقياس على عدم جواز الإيثار بالماء في الطهارة، والشرب إذا أفضى إلى هلاك صاحب الماء.
قال: وانظر هل في منعه من نكاح الأمة وتعليلهم، لأن من تزوج أَمةً كان ولده منهارقيقاً، ومنصبه صلى الله عليه وآله وسلم يُنزَّه عن مثل ذلك فيه إشارة إلى منع الشريف الحسني والحسيني من تزويج الأمة، لأنه يفضي إلى أن يكون ولده منها رقيقاً، ويَجِل منصب سيد الخلق صلى الله عليه وآله وسلم من أين يسترق أحد من ذريته؟
ولما تكلم ابن المُنَيِّر في "شرح البخاري" على الحديث المذكور في باب "من ملك من العرب رقيقاً" وفيه قوله صلى الله عليه وآله وسلم: "اعتقها فانها من ولد اسماعيل".
قال: تملك العرب لا بد عندي فيه من تفصيل، ومن تخصيص الشرفاء من ولد فاطمة. فلو فرضنا أن حسنياً أو حسينياً تزوج أمَة، لاستبعدنا الخلاف في أن ولده منها لا يسترق.
دليله قوله عليه السلام: "أعتقها فإنها من ولد اسماعيل" فإذا كان كونها من ولد اسماعيل يقتضي الاستحباب، فكونها بالمثابة التي ذكرناها توجب الحُرمة حتماً، والخلاف فيه صعب عسر.
قال: ومن خصائصه: أنه لم يكن يَمُر في طريق فيتبعه فيه أحَد إلا عرف أنه سلكه من طيبه، ذكره البخاري في "تاريخه الكبير" عن جابر.
قال اسحاق بن راهويه: كانت تلك رائحته بلا طيب، وقد عدَّ بعضهم ذلك في خصائصه، انتهى.
وفي "تذكرة" الشيخ بدر الدين بن الصاحب ما نصه: كانت همم الأنبياء متوجهة إلى طلب رجل يقص عليهم أخبار الأولين والآخرين، فجاء النبي صلى الله عليه وآله وسلم عن تلك الهمم كلها، فقصَّ القَصَص، وملأ الوجود خيراً.
وقال ابن السبكي في "التوشيح": سمعت الوالد يقول وقد سئل عن العلقة السوداء التي أخرجت من قلب النبي صلى الله عليه وآله وسلم في صغره حين شُق فؤاده، وقول الملك: هذا حظ الشيطان منك:
إن تلك العلقة خلقها الله في قلوب البشر قابلة لما يلقيه الشيطان فيها، فأزيلت من قلب النبي صلى الله عليه وآله وسلم، فلم يبق فيه مكان قابل لأن يلقي الشيطان فيه شيئاً، قال هذا معنى الحديث. ولم يكن للشيطان فيه حظ قط، وإنما الذي نقاه الملك أمر هو في الجبلات البشرية فأزيل القابل الذي لم يكن يلزم من حصوله حصول القذف في القلب.
قلت له: فَلِمَ خلق الله هذا القابل في هذه الذات الشريفة، وكان يمكن أن لا يخلقه الله فيها؟
فقال: إنه من جملة الأجزاء الإنسانية، فخَلْقه تكملة للخلق الإنساني لابد منه، ونَزعه كرامة ربانية طرأت بعده.
وقد رأى الأخُ الوالدَ بعد موته، وعليه أنوار، ووقع في نفسه أنه ببركة هذا البحث.
وقال السبكي في "الطبقات": لم يثبت عندي أن ولياً أحيى له ميتاً أزماناً كثيرة بعدما صار عظماً رميماً، ثم عاش بعده يحيى زماناً كثيراً، فهذا القدر لم يبلغنا، ولا أعتقد أنه وقع لأحد من الأولياء، ولا شك في مثل وقوعه للأنبياء عليهم السلام، فمثل هذا يكون معجزة ولا تنتهي إليه الكرامة.

صورة ما جاء بآخر النسخة

هذه النسخة المباركة بحمد الله تعالى وعونه من نسخة نقلت من نسخة نقلت عن نسخة سيدنا ومولانا الشيخ الإمام العالم العامل الجمالي يوسف الشريف تلميذ سيدنا ومولانا شيخ الإسلام الجلال السيوطي، مؤلف هذا الكتاب وهي نسخة معتمدة مكتوبة أعني نسخة السيد الشريف من نسخة المؤلف التي ألحقها الزيادة بعد أن انتشرت النسخ التي تعتبر زيادة. انتهى. والله أعلم بالصواب وإليه المرجع والمآب.',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b12824] VALUES (234,'وكان التمام بتاريخ أوائل شهر شعبان المعظم من شهور سنة تسع بعد الألف من الهجرة النبوية، على صاحبها أفضل الصلاة والسلام، على يد أفقر عباد الله وأحوجهم إلى عفوه أحمد الغزي الشافعي.
وصل اللهم على جميع الأنبياء والمرسلين والصحابة والتابعين وتابعيهم بإحسان إلى يوم الدين، ولا حول ولا قوة إلا بالله العلي العظيم، سبحان ربك رب العزة عما يصفون وسلام على المرسلين والحمد لله رب العالمين.',NULL,NULL,NULL,NULL,'0');
