-- Insert data for b12836
INSERT INTO [b12836] VALUES (1,'بسم الله الرحمن الرحيم

<span data-type="title" id=toc-1>مقدمة المؤلف</span>
الحمد لله رب العالمين قيوم السموات والأرضين. مدبر الخلائق أجمعين. باعث الرسل - صلواته وسلامه عليهم- إلى المكلفين لهدايتهم وبيان شرائع الدين بالدلائل القطعية، وواضحات البراهين.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (2,'أحمده على جميع نعمه. وأسأله المزيد من فضله وكرمه.
وأشهد أن لا إله إلا الله الواحد القهار. الكريم الغفار وأشهد أن سيدنا محمداً عبده ورسوله وحبيبه وخليله أفضل المخلوقين، المكرم بالقرآن العزيز المعجزة المستمرة على تعاقب السنين، وبالسنن المستنيرة للمسترشدين المخصوص بجوامع الكلم وسماحة الدين صلوات الله وسلامه عليه وعلى سائر النبيين والمرسلين وآل كل وسائر الصالحين.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (3,'أما بعد: فقد روينا عن علي بن أبي طالب، وعبد الله بن مسعود، ومعاذ بن جبل، وأبي الدرداء، وابن عمر، وابن عباس، وأنس بن مالك، وأبي هريرة، وأبي سعيد الخدري رضي الله تعالى عنهم من طرق كثيرات بروايات متنوعات: أن رسول الله صلى الله عليه وسلم قال: "من حفظ على أمتي أربعين حديثاً من أمر دينها',NULL,'37',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (4,'بعثه الله يوم القيامة في زمرة الفقهاء والعلماء" وفي رواية: "بعثه الله فقيها عالما".
وفي رواية أبي الدرداء: "وكنت له يوم القيامة شافعا وشهيدا". وفي رواية ابن مسعود: قيل له: "ادخل من أي أبوب الجنة شئت" وفي رواية ابن عمر "كُتِب في زمرة العلماء وحشر في زمرة الشهداء". واتفق الحفاظ على أنه حديث ضعيف وإن كثرت طرقه.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (5,'وقد صنّف العلماء رضي الله تعالى عنهم في هذا الباب ما لا يُحصى من المصنّفات. فأول من علمته صنف فيه: عبد الله بن المبارك، ثم محمد بن أسلم الطوسي العالم الرباني، ثم الحسن بن سفيان النسائي، وأبو بكر',NULL,'39',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (6,'الآجري، وأبو بكر بن إبراهيم الأصفهاني، والدارقطني، والحاكم،',NULL,'40',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (7,'وأبو نعيم، وأبو عبد الرحمن السلميّ، وأبو سعيد الماليني، وأبو عثمان الصابوني، وعبد الله بن',NULL,'41',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (8,'محمد الأنصاري. وأبو بكر البيهقي، وخلائق لا يحصون من المتقدمين والمتأخرين، وقد استخرت الله تعالى في جمع أربعين حديثاً اقتداء بهؤلاء الأئمة الأعلام وحفاظ الإسلام. وقد اتفق العلماء على جواز العمل بالحديث الضعيف',NULL,'42',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (9,'في فضائل الأعمال.
ومع هذا فليس اعتمادي على هذا الحديث، بل على قوله صلى الله عليه وسلم في الأحاديث الصحيحة: "ليبلغ الشاهد منكم الغائب" وقوله صلى الله عليه وسلم: "نضر الله امرأً سمع مقالتي فوعاها فأدّاها كما سمعها".
ثم من العلماء من جمع الأربعين في أصول الدين، وبعضهم في الفروع، وبعضهم في الجهاد، وبعضهم في الزهد، وبعضهم في الآداب، وبعضهم في الخطب،',NULL,'43',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (10,'وكلها مقاصة صالحة رضي الله تعالى عن قاصديها.
قد رأيت جمع أربعين أهم من هذا كله، وهي أربعون حديثاً مشتملة على جميع ذلك، وكل حديث منها قاعدة عظيمة من قواعد الدين قد وصفه العلماء بأن مدار الإسلام عليه، أو هو نصف الإسلام أو ثلثه أو نحو ذلك.
ثم ألتزم في هذه الأربعين أن تكون صحيحة، ومعظمها في صحيحي البخاري ومسلم،',NULL,'44',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (11,'وأذكرها محذوفة الأسانيد، ليسهل حفظها، ويعم الانتفاع بها إن شاء الله تعالى، ثم أُتبعها بباب في ضبط خفي ألفاظها. وينبغي لكل راغب في الآخرة أن يعرف هذه الأحاديث، لما اشتملت عليه من المهمات، واحتوت عليه من التنبيه على جميع الطاعات وذلك ظاهر لمن تدبّره، وعلى الله اعتمادي، وإليه تفويضي واستنادي وله الحمد والنعمة، وبه التوفيق والعصمة.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (12,'<span data-type="title" id=toc-2>الحديث الأول</span>
«عن أمير المؤمنين أبي حفص عمر بن الخطاب رضي الله تعالى عنه قال: سمعت رسول الله صلى الله تعالى عليه وعلى آله وسلم يقول: إنما الأعمال بالنيات، وإنما لكل امرئ ما نوى، فمن كانت هجرته إلى الله ورسوله فهجرته إلى الله ورسوله، ومن كانت هجرته لدنيا يصيبها أو امرأة ينكحها فهجرته إلى ما هاجر إليه» .
رواه إماما المحدثين:',NULL,'46',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (13,'أبو عبد الله محمد ابن إسماعيل بن إبراهيم بن المغيرة بن بردزبه البخاري، وأبو الحسين مسلم ابن الحجاج بن مسلم القشيري النيسابوري: في صحيحيهما اللذين هما أصح الكتب المصنفة.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (14,'<span data-type="title" id=toc-3>الحديث الثاني</span>
«عن عمر رضي الله تعالى عنه أيضاً قال: بينما نحن جلوس عند رسول الله صلى الله عليه وآله وسلم ذات يوم إذ طلع علينا رجل شديد بياض الثياب شديد سواد الشعر لا يرى عليه أثر السفر ولا يعرفه منا أحد، حتى جلس إلى النبي صلى الله عليه وآله وسلم فأسند ركبتيه إلى ركبتيه ووضع كفيه على فخذيه وقال: يا محمد أخبرني عن الإسلام،',NULL,'48',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (15,'فقال رسول الله صلى الله عليه وآله وسلم: الإسلام أن تشهد أن لا إله إلا الله وأن محمداً رسول الله، وتقيم الصلاة، وتؤتي الزكاة، وتصوم رمضان، وتحج البيت إن استطعت إليه سبيلاً قال: صدقت، فعجبنا له يسأله ويصدقه.
قال: فأخبرني عن الإيمان، قال أن تؤمن بالله وملائكته وكتبه ورسله واليوم الآخر، وتؤمن بالقدر خيره وشره، قال: صدقت، قال: فأخبرني عن الإحسان، قال أن تعبد الله كأنك تراه، فإن لم تكن تراه فإنه يراك،',NULL,'49',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (16,'قال: فأخبرني عن الساعة، قال ما المسئول عنها بأعلم من السائل، قال: فأخبرني عن أماراتها، قال أن تلد الأمة ربتها، وأن ترى الحفاة العراة العالة رعاء الشاء يتطاولون في البنيان',NULL,'50',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (17,'ثم انطلق فلبثت ملياً ثم قال يا عمر أتدري من السائل؟ قلت: الله ورسوله أعلم، قال فإنه جبريل أتاكم يعلمكم دينكم» .
رواه مسلم.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (18,'<span data-type="title" id=toc-4>الحديث الثالث</span>
«عن أبي عبد الرحمن عبد الله بن عمر بن الخطاب رضي الله تعالى عنهما قال: سمعت رسول الله صلى الله عليه وآله وسلم يقول: بني الإسلام على خمس: شهادة أن لا إله إلا الله وأن محمداً رسول الله، وإقام الصلاة، وإيتاء الزكاة، وحج البيت، وصوم رمضان» .
رواه البخاري ومسلم.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (19,'<span data-type="title" id=toc-5>الحديث الرابع</span>
«عن أبي عبد الرحمن عبد الله بن مسعود رضي الله عنه قال: حدثنا رسول الله صلى الله عليه وآله وسلم وهو الصادق المصدوق: إن أحدكم يجمع خلقه في بطن أمه أربعين يوماً نطفة، ثم يكون علقة مثل ذلك، ثم يكون مضغة مثل ذلك، ثم يرسل إليه الملك فينفخ فيه الروح ويؤمر بأربع كلمات: بكتب رزقه، وأجله، وعمله، وشقي أو سعيد،',NULL,'53',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (20,'فو الله الذي لا إله غيره إن أحدكم ليعمل بعمل أهل الجنة حتى ما يكون بينه وبينها إلا ذراع فيسبق عليه الكتاب فيعمل بعمل أهل النار فيدخلها، وإن أحدكم ليعمل بعمل أهل النار حتى ما يكون بينه وبينها إلا ذراع فيسبق عليه الكتاب فيعمل بعمل أهل الجنة فيدخلها» رواه البخاري ومسلم.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (21,'<span data-type="title" id=toc-6>الحديث الخامس</span>
«عن أم المؤمنين أم عبد الله عائشة رضي الله عنها قالت: قال رسول الله صلى الله عليه وآله وسلم: من أحدث في أمرنا هذا ما ليس منه فهو رد» .
رواه البخاري ومسلم.
وفي رواية لمسلم «من عمل عملاً ليس عليه أمرنا فهو رد» .',NULL,'55',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (22,'<span data-type="title" id=toc-7>الحديث السادس</span>
«عن أبي عبد الله النعمان بن بشير رضي الله عنهما قال: سمعت رسول الله صلى الله عليه وآله وسلم يقول: إن الحلال بين وإن الحرام بين وبينهما أمور مشتبهات لا يعلمهن كثير من الناس، فمن اتقى الشبهات فقد استبرأ لدينه وعرضه، ومن وقع في الشبهات وقع في',NULL,'56',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (23,'الحرام كالراعي يرعى حول الحمى يوشك أن يرتع فيه، ألا وإن لكل ملك حمى، ألا وإن حمى الله محارمه، ألا وإن في الجسد مضغة إذا صلحت صلح الجسد كله وإذا فسدت فسد الجسد كله ألا وهي القلب» .
رواه البخاري ومسلم.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (24,'<span data-type="title" id=toc-8>الحديث السابع</span>
«عن أبي رقية تميم بن أوس الداري رضي الله تعالى عنه أن النبي صلى الله عليه وآله وسلم قال الدين النصيحة.
قلنا: لمن؟ قال لله، ولكتابه، ولرسوله، ولأئمة المسلمين وعامتهم» .
رواه مسلم.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (25,'<span data-type="title" id=toc-9>الحديث الثامن</span>
«عن ابن عمر رضي الله تعالى عنهما أن رسول الله صلى الله تعالى عليه وعلى آله وسلم قال أمرت أن أقاتل الناس حتى يشهدوا أن لا إله إلا الله وأن محمداً رسول الله، ويقيموا الصلاة، ويؤتوا الزكاة: فإذا فعلوا ذلك عصموا مني دماءهم وأموالهم إلا بحق الإسلام وحسابهم على الله تعالى» .
رواه البخاري ومسلم.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (26,'<span data-type="title" id=toc-10>الحديث التاسع</span>
«عن أبي هريرة عبد الرحمن بن صخر رضي الله تعالى عنه قال: سمعت رسول الله صلى الله عليه وآله وسلم يقول مانهيتكم عنه فاجتنبوه، وما أمرتكم به فأتوا منه ما استطعتم، فإنما أهلك الذين من قبلكم كثرة مسائلهم واختلافهم على أنبيائهم» .
رواه البخاري ومسلم.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (27,'<span data-type="title" id=toc-11>الحديث العاشر</span>
«عن أبي هريرة رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وآله وسلم: إن الله تعالى طيب لا يقبل إلا طيباً، وإن الله أمر المؤمنين بما أمر به المرسلين فقال تعالى {يا أيها الرسل كلوا من الطيبات واعملوا صالحا} وقال تعالى {يا أيها الذين آمنوا كلوا من طيبات ما رزقناكم} ثم ذكر الرجل يطيل السفر أشعث أغبر يمد يديه إلى السماء: يا رب يا رب، ومطعمه حرام',NULL,'61',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (28,'وملبسه حرام وغذي بالحرام فأنى يستجاب له» .
رواه مسلم.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (29,'<span data-type="title" id=toc-12>الحديث الحادي عشر</span>
«عن أبي محمد الحسن بن علي بن أبي طالب سبط رسول الله صلى الله عليه وآله وسلم وريحانته رضي الله عنهما قال: حفظت من رسول الله صلى الله عليه وآله وسلم دع ما يريبك إلى ما لا يريبك» .
رواه الترمذي والنسائي، وقال الترمذي: حديث حسن صحيح.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (30,'<span data-type="title" id=toc-13>الحديث الثانى عشر</span>
«عن أبي هريرة رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وآله وسلم من حسن إسلام المرء تركه ما لا يعنيه» .
حديث حسن، رواه الترمذي وغيره وهكذا.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (31,'<span data-type="title" id=toc-14>الحديث الثالث عشر</span>
«عن أبي حمزة أنس بن مالك رضي الله تعالى عنه خادم رسول الله صلى الله عليه وآله وسلم عن النبي صلى الله عليه وآله وسلم قال لا يؤمن أحدكم حتى يحب لأخيه ما يحب لنفسه» .
رواه البخاري ومسلم.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (32,'<span data-type="title" id=toc-15>الحديث الرابع عشر</span>
«عن ابن مسعود رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وآله وسلم لا يحل دم امرئ مسلم إلا بإحدى ثلاث: الثيب الزاني، والنفس بالنفس، والتارك لدينه المفارق للجماعة» .
رواه البخاري ومسلم.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (33,'<span data-type="title" id=toc-16>الحديث الخامس عشر</span>
«عن أبي هريرة رضي الله تعالى عنه أن رسول الله صلى الله عليه وآله وسلم قال: من كان يؤمن بالله واليوم الآخر فليقل خيراً أو ليصمت، ومن كان يؤمن بالله واليوم الآخر فليكرم جاره، ومن كان يؤمن بالله واليوم الآخر فليكرم ضيفه» .
رواه البخاري ومسلم.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (34,'<span data-type="title" id=toc-17>الحديث السادس عشر</span>
«عن أبي هريرة رضي الله تعالى عنه أن رجلاً قال للنبي صلى الله عليه وآله وسلم: أوصني، قال لا تغضب فردد مراراً، قال لا تغضب» .
رواه البخاري.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (35,'<span data-type="title" id=toc-18>الحديث السابع عشر</span>
«عن أبي يعلى شداد بن أوس رضي الله تعالى عنه عن رسول الله صلى الله عليه وآله وسلم قال إن الله كتب الإحسان على كل شئ، فإذا قتلتم فأحسنوا القتلة وإذا ذبحتم فأحسنوا الذبحة، وليحد أحدكم شفرته وليرح ذبيحته» .
رواه مسلم.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (36,'<span data-type="title" id=toc-19>الحديث الثامن عشر</span>
«عن أبي ذر جندب بن جنادة، وأبي عبد الرحمن معاذ بن جبل رضي الله تعالى عنهما عن رسول الله صلى الله عليه وآله وسلم قال: إتق الله حيثما كنت، وأتبع السيئة الحسنة تمحها، وخالق الناس بخلق حسن» .
رواه الترمذي وقال: حديث حسن، وفي بعض النسخ: حسن صحيح.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (37,'<span data-type="title" id=toc-20>الحديث التاسع عشر</span>
«عن أبي العباس عبد الله بن عباس رضي الله تعالى عنهما قال: كنت خلف النبي صلى الله عليه وآله وسلم يوماً فقال يا غلام، إني أعلمك كلمات: إحفظ الله يحفظك، إحفظ الله تجده تجاهك، إذا سألت فاسأل الله، وإذا استعنت فاستعن بالله، واعلم أن الأمة لو اجتمعت على أن ينفعوك بشئ لم ينفعوك إلا بشئ قد كتبه الله لك،',NULL,'71',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (38,'وإن اجتمعوا على أن يضروك بشئ لم يضروك إلا بشئ قد كتبه الله عليك، رفعت الأقلام وجفت الصحف» .
رواه الترمذي وقال حديث حسن صحيح.
وفي رواية غير الترمذي «إحفظ الله تجده أمامك، تعرف إلى الله في الرخاء يعرفك في الشدة، واعلم أن ما أخطأك لم يكن ليصيبك، وما أصابك لم يكن ليخطئك، واعلم أن النصر مع الصبر، وأن الفرج مع الكرب، وأن مع العسر يسراً» .',NULL,'72',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (39,'<span data-type="title" id=toc-21>الحديث العشرون</span>
«عن أبي مسعود عقبة بن عمرو الأنصاري البدري رضي الله عنه قال: قال رسول الله صلى الله عليه وآله وسلم: إن مما أدرك الناس من كلام النبوة الأولى: إذا لم تستح فاصنع ما شئت» .
رواه البخاري.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (40,'<span data-type="title" id=toc-22>الحديث الحادي والعشرون</span>
«عن أبي عمرو - وقيل أبي عمرة - سفيان ابن عبد الله رضي الله عنه قال: قلت يا رسول الله قل لي في الإسلام قولاً لا أسال عنه أحداً غيرك، قال قل آمنت بالله، ثم استقم» .
رواه مسلم.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (41,'<span data-type="title" id=toc-23>الحديث الثانى والعشرون</span>
«عن أبي عبد الله جابر بن عبد الله الأنصاري رضي الله عنهما أن رجلاً سأل رسول الله صلى الله عليه وآله وسلم فقال: أرأيت إذا صليت المكتوبات، وصمت رمضان، وأحللت الحلال، وحرمت الحرام، ولم أزد على ذلك شيئاً، أدخل الجنة؟ قال نعم» .
رواه مسلم.
ومعنى حرمت الحرام: اجتنبته،',NULL,'75',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (42,'ومعنى أحللت الحلال: فعلته معتقداً حله.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (43,'<span data-type="title" id=toc-24>الحديث الثالث والعشرون</span>
«عن أبي مالك الحارث بن الأشعري رضي الله عنه قال: قال رسول الله صلى الله عليه وآله وسلم الطهور شطر الإيمان، والحمد لله تملأ الميزان، وسبحان الله والحمد لله تملآن - أو تملأ - ما بين',NULL,'77',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (44,'السماء والأرض، والصلاة نور، والصدقة برهان، والصبر ضياء، والقرآن حجة لك أو عليك، كل الناس يغدو: فبائع نفسه فمعتقها أو موبقها» .
رواه مسلم.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (45,'<span data-type="title" id=toc-25>الحديث الرابع والعشرون</span>
«عن أبي ذر الغفاري رضي الله تعالى عنه عن النبي صلى الله تعالى عليه وآله وسلم فيما يرويه عن ربه عز وجل أنه قال يا عبادي، إني حرمت الظلم على نفسي وجعلته بينكم محرماً فلا تظالموا،',NULL,'79',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (46,'يا عبادي، كلكم ضال إلا من هديته فاستهدوني أهدكم، يا عبادي، كلكم جائع إلا من أطعمته فاستطعموني أطعمكم، يا عبادي، كلكم عار إلا من كسوته فاستكسوني أكسكم: يا عبادي، إنكم تخطئون بالليل والنهار وأنا أغفر الذنوب جميعاً فاستغفروني أغفر لكم، ياعبادي، إنكم لن تبلغوا ضري فتضروني ولن تبلغوا نفعي فتنفعوني، يا عبادي، لو أن أولكم وآخركم وإنسكم وجنكم كانوا على أتقى قلب رجل واحد منكم ما زاد ذلك في ملكي شيئاً:',NULL,'80',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (47,'يا عبادي، لو أن أولكم وآخركم وإنسكم وجنكم كانوا على أفجر قلب رجل واحد منكم ما نقص ذلك من ملكي شيئاً: يا عبادي، لو أن أولكم وآخركم وإنسكم وجنكم قاموا في صعيد واحد فسألوني فأعطيت كل واحد مسألته ما نقص ذلك مما عندي إلا كما ينقص المخيط إذا أدخل البحر، يا عبادي، إنما هي أعمالكم أحصيها لكم ثم أوفيكم إياها فمن وجد خيراً فليحمد الله ومن',NULL,'81',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (48,'وجد غير ذلك فلا يلومن إلا نفسه» .
رواه مسلم.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (49,'<span data-type="title" id=toc-26>الحديث الخامس والعشرون</span>
«عن أبي ذر رضي الله عنه أيضاً أن ناساً من أصحاب رسول الله صلى الله عليه وآله وسلم قالوا للنبي صلى الله تعالى وعليه وآله وسلم: يا رسول الله، ذهب أهل الدثور بالأجور: يصلون كما نصلي ويصومون كما نصوم، ويتصدقون بفضول أموالهم.
قال: أوليس قد جعل الله لكم ما تصدقون: إن بكل تسبيحة صدقة، وكل تكبيرة صدقة، وكل',NULL,'83',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (50,'تحميدة صدقة، وكل تهليلة صدقة، وأمر بمعروف صدقة، ونهي عن منكر صدقة، وفي بضع أحدكم صدقة، قالوا: يا رسول الله، أيأتي أحدنا شهوته ويكون له فيها أجر؟ قال أرأيتم لو وضعها في حرام أكان عليه وزر؟ فكذلك إذا وضعها في الحلال كان له أجر» .
رواه مسلم.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (51,'<span data-type="title" id=toc-27>الحديث السادس والعشرون</span>
«عن أبي هريرة رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وآله وسلم كل سلامى من الناس عليه صدقة كل يوم تطلع فيه الشمس: تعدل بين اثنين صدقة، وتعين الرجل في دابته فتحمله عليها أو ترفع',NULL,'85',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (52,'له عليها متاعه صدقة، والكلمة الطيبة صدقة، وبكل خطوة تمشيها إلى الصلاة صدقة، وتميط الأذى عن الطريق صدقة» .
رواه البخاري ومسلم.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (53,'<span data-type="title" id=toc-28>الحديث السابع والعشرون</span>
«عن النواس بن سمعان رضي الله تعالى عنه عن النبي صلى الله عليه وعلى آله وسلم قال البر حسن الخلق، والإثم ما حاك في نفسك وكرهت أن يطلع عليه الناس» .
رواه مسلم
وعن وابصة بن معبد رضي الله تعالى عنه قال: «أتيت رسول الله صلى الله عليه وآله وسلم فقال: جئت تسأل عن البر؟ قلت: نعم، وقال: إستفت قلبك، البر ما',NULL,'87',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (54,'اطمأنت إليه النفس واطمأن إليه القلب، والإثم ما حاك في النفس وتردد في الصدر وإن أفتاك الناس وأفتوك» .
حديث حسن.
رويناه في مسندي الإمامين أحمد بن حنبل والدرامي بإسناد حسن.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (55,'<span data-type="title" id=toc-29>الحديث الثامن والعشرون</span>
«عن أبي نجيح العرباض بن سارية رضي الله تعالى عنه قال: وعظنا رسول الله صلى الله عليه وآله وسلم موعظة وجلت منها القلوب وذرفت منها العيون، فقلنا: يا رسول الله كأنها موعظة مودع فأوصنا، قال أوصيكم بتقوى الله عز وجل والسمع والطاعة وإن تأمر عليكم عبد،',NULL,'89',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (56,'فإنه من يعش منكم فسيري اختلافاً كثيراً، فعليكم بسنتي وسنة الخلفاء الراشدين المهديين عضوا عليها بالنواجذ، وإياكم ومحدثات الأمور فإن كل بدعة ضلالة» .
رواه أبو داود والترمذي وقال: حديث حسن صحيح.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (57,'<span data-type="title" id=toc-30>الحديث التاسع والعشرون</span>
«عن معاذ بن جبل رضي الله عنه قال: قلت يا رسول الله أخبرني بعمل يدخلني الجنة ويباعدني عن النار، قال: لقد سألت عن عظيم وإنه ليسير على من يسره الله تعالى عليه: تعبد الله لا تشرك به شيئاً، وتقيم الصلاة، وتؤتي الزكاة، وتصوم رمضان، وتحج البيت ثم قال: ألا أدلك على أبواب الخير؟ : الصوم جنة، والصدقة تطفئ الخطيئة كما يطفئ الماء النار، وصلاة الرجل في جوف الليل',NULL,'91',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (58,'ثم تلا: {تتجافى جنوبهم عن المضاجع} {حتى إذا بلغ} {يعملون} ثم قال ألا أخبرك برأس الأمر وعموده وذروة سنامه؟ قلت: بلى يا رسول الله.
قال رأس الأمر الإسلام، وعموده الصلاة، وذروة سنامه الجهاد',NULL,'92',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (59,'ثم قال: ألا أخبرك بملاك ذلك كله؟ قلت: بلى يا رسول الله، فأخذ بلسانه وقال كف عليك هذا قلت: يا نبي الله، وإنا لمؤاخذون بما نتكلم به؟ فقال ثكلتك أمك، وهل يكب الناس في النار على وجوههم - أو قال على مناخرهم - إلا حصائد ألسنتهم؟» .
رواه الترمذي وقال: حديث حسن صحيح.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (60,'<span data-type="title" id=toc-31>الحديث الثلاثون</span>
«عن أبي ثعلبة الخشبي جرثوم بن ناشر رضي الله تعالى عنه عن رسول الله صلى الله عليه وسلم قال إن الله تعالى فرض فرائض فلا تضيعوها، وحد حدوداً فلا تعتدوها، وحرم أشياء فلا تنتهكوها،',NULL,'94',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (61,'وسكت عن أشياء رحمة لكم غير نسيان فلا تبحثوا عنها» .
حديث حسن رواه الدارقطني وغيره.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (62,'<span data-type="title" id=toc-32>الحديث الحادي والثلاثون</span>
«عن أبي العباس سهل بن سعد الساعدى رضي الله تعالى عنه قال جاء رجل إلى النبي صلى الله عليه وآله وسلم فقال: يا رسول الله، دلني على عمل إذا عملته أحبني الله وأحبني الناس: فقال إزهد في الدنيا يحبك الله، وازهد فيما عند الناس يحبك الناس» .
حديث حسن، رواه ابن ماجة وغيره بأسانيد حسنة.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (63,'<span data-type="title" id=toc-33>الحديث الثاني والثلاثون</span>
«عن أبي سعيد سعد بن مالك بن سنان الخدري رضي الله تعالى عنه أن رسول الله صلى الله وعليه وآله وسلم قال: لا ضرر ولا ضرار» .
حديث حسن، رواه ابن ماجة والدارقطني وغيرهما مسنداً.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (64,'ورواه مالك في الموطإ مرسلاً عن عمرو بن يحيى عن أبيه عن النبي صلى الله عليه وآله وسلم، فأسقط أبا سعيد، وله طرق يقوي بعضها بعضاً.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (65,'<span data-type="title" id=toc-34>الحديث الثالث والثلاثون</span>
«عن ابن عباس رضي الله عنهما أن رسول الله صلى الله عليه وآله وسلم قال: لو يعطى الناس بدعواهم لادعى رجال أموال قوم ودماءهم، لكن البينة على المدعى واليمين على من أنكر» .
حديث حسن، رواه البيهقي وغيره هكذا، وبعضه في الصحيحين.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (66,'<span data-type="title" id=toc-35>الحديث الرابع والثلاثون</span>
«عن أبي سعيد الخدري رضي الله عنه قال: سمعت رسول الله صلى الله عليه وآله وسلم يقوم: من رأى منكم منكراً فليغيره بيده، فإن لم يستطع فبلسانه، فإن لم يستطع فبقلبه، وذلك أضعف الإيمان» .
رواه مسلم.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (67,'<span data-type="title" id=toc-36>الحديث الخامس والثلاثون</span>
«عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وآله وسلم لا تحاسدوا، ولا تناجشوا ولا تباغضوا، ولا تدابروا، ولا يبع بعضكم على بيع بعض، وكونوا عباد الله إخواناً، المسلم أخو المسلم لا يظلمه ولا يخذله ولا يكذبه ولا يحقره، التقوى ههنا - ويشير',NULL,'101',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (68,'إلى صدور ثلاث مرات - بحسب امرئ من الشر أن يحقر أخاه المسلم، كل المسلم على المسلم حرام: دمه وماله وعرضه» .
رواه مسلم.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (69,'<span data-type="title" id=toc-37>الحديث السادس والثلاثون</span>
«عن أبي هريرة رضي الله عنه عن النبي صلى الله عليه وآله وسلم قال من نفس عن مؤمن كربة من كرب الدنيا نفس الله عنه كربة من كرب يوم القيامة، ومن يسر على معسر يسر الله عليه في الدنيا والآخرة، ومن ستر مسلماً ستره الله في الدنيا والآخرة، والله في عون العبد ما كان العبد في عون أخيه، ومن سلك طريقاً يلتمس فيه علماً سهل الله له به طريقاً إلى الجنة، وما اجتمع قوم في بيت من بيوت الله يتلون كتاب الله ويتدارسونه بينهم إلا نزلت عليهم السكينة',NULL,'103',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (70,'وغشيتهم الرحمة وحفتهم الملائكة وذكرهم الله فيمن عنده، ومن بطأ به عمله لم يسرع به نسبه» .
رواه مسلم بهذا اللفظ.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (71,'<span data-type="title" id=toc-38>الحديث السابع والثلاثون</span>
«عن ابن عباس رضي الله عنهما عن رسول الله صلى الله عليه وآله وسلم فيما يرويه عن ربه تبارك وتعالى قال إن الله كتب الحسنات والسيئات ثم بين ذلك، فمن هم بحسنة فلم يعملها كتبها الله عنده حسنة كاملة، وإن هم بها فعملها كتبها الله عنده عشر حسنات إلى سبعمائة ضعف إلى أضعاف كثيرة، وإن هم بسيئة فلم يعملها كتبها الله عنده حسنة كاملة، وإن هم بها فعملها كتبها الله سيئة واحدة» .',NULL,'105',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (72,'رواه البخاري ومسلم في صحيحيهما بهذه الحروف فانظر يا أخي وفقنا الله وإياك إلى عظيم لطف الله تعالى، وتأمل هذه الألفاظ، وقوله عنده إشارة إلى الإعتناء بها، وقوله كاملة للتأكيد وشدة الإعتناء بها، وقال في السيئة التي هم بها ثم تركها كتبها الله عنه حسنة كاملة فأكدها بـ (كاملة) وإن عملها كتبها سيئة واحدة، فأكد تقليلها',NULL,'106',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (73,'بـ (واحدة) ولم يؤكدها بـ (كاملة) فلله الحمد والمنة، سبحانه لا نحصي ثناء عليه، وبالله التوفيق.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (74,'<span data-type="title" id=toc-39>الحديث الثامن والثلاثون</span>
«عن أبي هريرة رضي الله عنه قال: قال رسول الله صلى الله عليه وآله وسلم: إن الله تعالى قال: من عادى لي ولياً فقد آذنته بالحرب، وما تقرب إلى عبدي بشئ أحب إلي مما افترضته عليه، ولا يزال عبدي يتقرب إلي',NULL,'108',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (75,'بالنوافل حتى أحبه، فإذا أحببته كنت سمعه الذي يسمع به وبصره الذي يبصر به ويده التي يبطش بها ورجله التي يمشي بها، ولئن سألني لأعطينه، ولئن استعاذني لأعيذنه» .
رواه البخاري.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (76,'<span data-type="title" id=toc-40>الحديث التاسع والثلاثون</span>
«عن ابن عباس رضي الله عنهما أن رسول الله صلى الله عليه وآله وسلم قال إن الله تجاوز لي عن أمتى الخطأ والنسيان وما اسكترهوا عليه» .
حديث حسن رواه ابن ماجة والبيهقي وغيرهما.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (77,'<span data-type="title" id=toc-41>الحديث الأربعون</span>
«عن ابن عمر رضي الله عنهما قال: أخذ رسول الله صلى الله عليه وآله وسلم بمنكبي فقال: كن في الدنيا كأنك غريب أو عابر سبيل» وكان ابن عمر رضي الله تعالى عنهما يقول: إذا أمسيت فلا تنتظر الصباح، وإذا أصبحت فلا تنتظر',NULL,'111',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (78,'المساء، وخذ من صحتك لمرضك، ومن حياتك لموتك.
رواه البخاري.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (79,'<span data-type="title" id=toc-42>الحديث الحادي والأربعون</span>
«عن أبي محمد عبد الله بن عمرو بن العاص رضي الله عنهما قال: قال رسول الله صلى الله عليه وآله وسلم: لا يؤمن أحدكم حتى يكون هواه تبعاً لما جئت به» .
حديث حسن صحيح، رويناه في كتاب الحجة بإسناد صحيح.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (80,'<span data-type="title" id=toc-43>الحديث الثاني والأربعون</span>
«عن أنس رضي الله عنه قال: سمعت رسول الله صلى الله عليه وآله وسلم يقول: قال الله تعالى يابن آدم، إنك ما دعوتني ورجوتني غفرت للك على ما كان منك ولا أبالي، يا ابن آدم لو بلغت ذنوبك عنان السماء ثم',NULL,'114',NULL,NULL,'0');
INSERT INTO [b12836] VALUES (81,'استغفرتني غفرت لك، يا ابن آدم لو أتيتني بقراب الأرض خطايا ثم لقيتني لا تشرك بي شيئاً لأتيتك بقرابها مغفرة» .
رواه الترمذي وقال: حديث حسن صحيح.',NULL,'115',NULL,NULL,'0');
