INSERT INTO [b12876] VALUES (5656,'{فِي سَمُوم} رِيح حَارَّة مِنْ النَّار تَنْفُذ فِي الْمَسَامّ {وَحَمِيم} مَاء شَدِيد الْحَرَارَة
