INSERT INTO [b12895] VALUES (2,'<span data-type="title" id=toc-2>مقدمة هذه الطبعة [الخامسة]</span>','1','2',NULL,NULL,'0');
INSERT INTO [b12895] VALUES (3,'بسم الله الرحمن الرحيم
الحمد لله رب العالمين والصلاة والسلام على سيدنا محمد وعلى آله وصحبه أجمعين وبعد هذه هي الطبعة الخامسة من كتاب فقه العبادات على المذهب الشافعي بعد أن كتب الله له القبول فنفدت طبعاته السابقة جميعها دون أن يطرأ عليها تعديل أو تغيير رغم توق النفس للارتقاء بالعمل نحو الكمال ورغم دافع تحري المزيد من النفع بالكتاب لكن لم ييسر الأمر قبل الآن وأذن الله بذلك ويسره في هذه الطبعة فكان ثمة عودة ممحصة لمحتوى الكتاب شملت المادة الفقهية من جوانب متعددة وطريقة العرض والتصنيف في بعض المواضع والاستدلال من حيث شموله وتوثيقه والشروح اللغوية للغة الكتاب ولغة الأدلة وضبط بعض المفردات بالشكل حيث يقتضي الأمر ذلك وتلا التمحيص جهد بذل وأدى إلى خطوة نحو الأفضل في الجوانب المذكورة فمن حيث المادة الفقهية شمل التعديل تجاوز بعض المسائل نادرة الحدوث وعدل حكم كراهة التحريم حيث ورد إلى كراهة فحسب أو حرمة إذ لم يرد عن الشافعي أصلا تدرج في حكم الكراهة إنما جاء ذلك عن المتأخرين من فقهاء المذهب المجتهدين فرأيت الانحياز إلى أصل الحكم في المذهب كما شمل التعديل في المادة الفقهية تجاوز بعض ما ورد في هوامش الطبعة الأولى من النص على حكم العديد من المسائل في المذاهب الأخرى فاقتصرت في هذه الطبعة على تثبيت الحكم في تلك المذاهب في المواضع التي قد تلجئ إلى التقليد فحسب وفي كتاب الحج أضيفت مصورات ومخططات جدية مشروحة لتجلية مواضع وأعمال النسك إلى جانب المادة المقروءة لتكون معينا في إدراك حرة الحاج خلال أداء المناسك ويكون الكتاب دليلا ومرشدا لمن يريد أداء هذا النسك للمرة الأولى أما من حيث طريقة العرض والتصنيف فقد نال هذا الجانب قسطاً وفيراً من العناية والاهتمام في الطبعة الأولى فما كان من التعديل في هذا الجانب في هذه الطبعة سوى تعديلات طفيفة في مواضع محدودة بهدف خدمة الغرض من العنونة والتقسيم والتصنيف وهو الحصول على مزيد من الفائدة بأقصى ما يمكن من اليسر والسهولة وأما من حيث الاستدلال فقد أضيفت بعض الأدلة لمواضع كان ينقصها الاستدلال واضطررت للعودة لطبعات حديثة لتخريج الأحاديث الشريفة من مصادرها لعدم توفر الطبعة القديمة في الأسواق وكان ثمة عودة لتخريج الأحاديث الواردة جميعها من الطبعات الحديثة لتسنى للراغب العودة إليها إن أراد ثم رأيت اتباع طريقة جديدة في التخريح تمكن من أراد من العودة إليه في مصدره بيسر وسهولة رغم تغير الطبعات وأرقام الصفحات حيث ذكرت إلى جانب اسم المصدر رقم الجزء أولاً (ج ١، ج ٢) يليه اسم الكتاب (كتاب الطهارة كتاب الصلاة ... ) ثم رقم الباب في الكتاب (باب ١٥ باب ٣٠) وأخيرا رقم الحديث في الباب أو الكتاب حسب طريقة المؤلف في ترقيم الأحاديث أما في المصادر التي لم يعتمد فيها هذا التصنيف فقد اكتفيت بذكر رقم الجزء والصفحة فقط (ج ١ ص ٢٥) بعد اسم المصدر أما من حيث الشروح اللغوية وضبط الكلمات الملتبسة بالشكل فقد تمت العودة للمعاجم المختصة وكتب شروح الحديث الشريف وكتاب النهاية في غريب الحديث وفي هذا الميدان أضيفت ثورة إلى الكتاب إلى جانب ما حوته الطبعة الأولى منها حيث حاولت في هذه الطبعة استقصاء كل ما غمض أو أشكل.
بعد هذا الجهد المتواضع أرى أن الكتاب أصبح بحلة أفضل وذا نفع أكبر. أسال الله أن يقبله عملا خالصا لوجهه وأن يحقق به المزيد من النفع لعباده وأسأل مسلماً انتفع به دعاء أخر في ظهر الغيب.
والحمد لله رب العالمين
- ٢٦ رمضان ١٤٠٩ هـ ١/٥/ ١٩٨٩','1','3',NULL,NULL,'0');
INSERT INTO [b12895] VALUES (4,'<span data-type="title" id=toc-3>مقدمة الطبعة الأولى</span>','1','4',NULL,NULL,'0');
INSERT INTO [b12895] VALUES (5,'بسم الله الرحمن الرحيم
الحمد لله رب العالمين وأطيب الصلاة والسلام على سيدنا محمد وعلى آله وصحبه أجمعين وبعد فهذا نتاج متواضع في فقه العبادات على المذهب الشافعي أضعه بين يدي الراغب في معرفة أحكام الفقه العملية الضرورية في أداء العبادات على الوجه الصحيح وقد قمت بجمع مادته مما تلقيته عن أستاذي في علم الفقه المرحوم الشيخ عبد الكريم الرفاعي كرم الله مثواه وجزاه عني كل خير ثم عدت إلى عدد من الكتب الفقهية في المذهب الشافعي فجمعت بين هذا وذاك مع الحرص على تجنب الخوض في المسائل النادر وقوعها والفروع المطولة التي قد يحتاجها المتخصص في علم الفقه دون الراغب في منهج عملي مبسط للتطبيق اليومي والذي قد تمله التفريعات فيرغب عن دراسة الفقه كلها من أجل ذلك لجأت إلى تبويب أبحاث الكتاب وتصنيفها بحيث يسهل على الدارس حفظها وتطبيقها بيسر فاستعنت بالترقيم وغيره من أساليب الكتب المدرسية وابتعدت عن الاستطرادات المملة ما أمكن إلى حيث دعت الحاجة وحرصا على دقة الأحكام الفقهية الواردة عدت فعرضت أبحاثه جميعا على الشيخ محي الدين الكردي حفظه الله فراجعها وعلق عليها شكر الله له كما حرصت على الاستدلال لمعظم الأحكام الواردة فيه بالقرآن الكريم والسنة المطهرة من مصادرها المعتمدة ابتداء بصحيحي البخاري ومسلم وانتهاء بكتب الدارقطني والبيهقي مع الإشارة إلى مصدر الدليل وأخيرا أقدم هذا الكتاب على استحياء ألا يكون قد قارب الكمال في الدقة والصحة ولكنه جهد المقل، الراغب في خدمة هذا الدين فأرجو المولى عز وجل أن يتقبل مني عملي وينفع به ويجزي عني عظيم الخير كل من أعانني على تأليف هذا الكتاب والله من وراء القصد','1','5',NULL,NULL,'0');
INSERT INTO [b12895] VALUES (6,'<span data-type="title" id=toc-4>محمد بن إدريس الشافعي رضي الله عنه</span>','1','6',NULL,NULL,'0');
