INSERT INTO [b12929] VALUES (3834,'<span data-type="title" id=toc-3782>٣٧٤٤- أَلأم مِنْ ماء عَادِيةَ، ومِنْ مَذاقِ الخمرِ ومَنْ نَوْمةِ الضُّحَى، ومِنْ قُبْلَةٍ عَلَى عَجَلٍ</span>','2','256',NULL,NULL,'0');
