INSERT INTO [b12978] VALUES (299,'٤- الاستنثار.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b12978] VALUES (300,'٥- مسح صماخ الأذنين، أي ثقبيهما، أما ظاهر الأذنين وباطنهما فمن ظاهر الجسد الذي يجب غسله.
وهذه السنن من سنن الغسل إن لم يتوضأ قبله، أما إذا توضأ قبل الغسل كانت من سنن الوضوء وأجزأت عن إعادتها في الغسل.
مندوبات الغسل:',NULL,'85',NULL,NULL,'0');
