INSERT INTO [b12978] VALUES (1274,'٦- يكره اتباع الجنازة بالمباخر والشموع، لما روي عن أبي هريرة رضي اللَّه عنه عن النبي أنه قال: (لا تتبع الجنازة بصوت ولا نار) (١) .',NULL,'258',NULL,NULL,'0');
INSERT INTO [b12978] VALUES (1275,'٧- يكره أن يقوم الناس عند مرور الجنازة عليهم وهم جلوس، لحديث علي رضي اللَّه عنه قال: (قام رسول اللَّه صلى اللَّه عليه وسلم ثم قعد) (٢) .',NULL,'258',NULL,NULL,'0');
INSERT INTO [b12978] VALUES (1276,'٨- يكره حملها بلا وضوء لأنه يؤدي إلى عدم الصلاة عليها.',NULL,'258',NULL,NULL,'0');
INSERT INTO [b12978] VALUES (1277,'٩- يكره الركوب للمشيعين في الذهاب ولا بأس به في العودة لانتهاء العبادة، لحديث جابر بن سمرة رضي اللَّه عنه المتقدم (أن النبي صلى اللَّه عليه وسلم اتبع جنازة أبي الدحداح ماشياً ورجع على فرس) .',NULL,'258',NULL,NULL,'0');
INSERT INTO [b12978] VALUES (1278,'١٠- يكره الانصراف بلا صلاة عليها ولو بإذن أهلها، أما بعد الصلاة عليها وقبل الدفن فيجوز بعد الإذن من أهلها.',NULL,'259',NULL,NULL,'0');
