CREATE TABLE "b13239"(id INTEGER PRIMARY KEY,content TEXT, part TEXT, page TEXT, number TEXT, services TEXT, is_deleted TEXT);

-- Insert data for b13239
INSERT INTO [b13239] VALUES (1,'بسم الله الرحمن الرحيم
الحمد لله مُوجد الخلق ومبدئه ومبقيه ما شاء ومفنيه، وصلى الله على سيدنا محمد وأقربيه.
قال ابن خالويه: ليس في كلام العرب إنما هو على ما أحاط به حفظي، وفوق كل ذي علم عليم.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (2,'<span data-type="title" id=toc-1>(١)
باب
ليس في كلام العرب: فعل يفعل مما ليس فيه حرف الحلق عينًا ولا لامًا إلا عشرة أحرف: </span>أبى يأبى،',NULL,'28',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (3,'وقَلى يقلَى، وجبى يجبى: جمع الماء في الحوض، وسلى يسلى، وخظا يخظى، إذا سمن، من قولهم: لحمه خظًا بظًا كظًا، وغضضت تغض، وبضضت تبض، وقنط يقنط، وغسى الليل يغسى، إذا أظلم، وركن يركن، ولم يحك سيبويه إلا حرفًا واحدًا، وهو أبى يأبى، لأنه بلا خلاف، والبواقي مختلف فيها.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (4,'<span data-type="title" id=toc-2>(٢)
باب
ليس في كلام العرب: واوٌ وياءٌ يجتمعان، والأول ساكن في غير التصغير والمُلين من الهمزة إلا مدغمًا </span>نحو قولهم: يوم وأيام، وأصله أيوامٌ، وكويت الدابة كيًّا، وأصله كويًا، إلا أربعة أحرف: خيوان: قبيلة، وحيوة: اسم رجل، وعوى الكلب عوية واحدة، وضَيونٌ؛ وهو الخيطل: ذكر السنانير.
فأما أُسيود في تصغير أسود فإنه يطرد في نظيره لعلة، وكذلك رويَا إذا لُينت همزتها، ومثله: رُوية.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (5,'<span data-type="title" id=toc-3>(٣)
باب
ليس في كلام العرب: فعل يفعل فِعلاً إلا سحر يسحر سحرًا</span>. والسحر يكون حلالًا وحرامًا، يقال: فلان ساحر العينين؛ أي فتان، وفلان يسحر الناس بظرفه، والساحر: العالم الفَهِمُ، كقوله تعالى: {يا أيها الساحر ادع لنا ربك} يعني: العالم الفهم.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (6,'<span data-type="title" id=toc-4>(٤)
باب
ليس في كلام العرب: اسمٌ على فعال ليس بمصدر إلا كلمة واحدة</span>، وهي قولهم: أدخل الفعال في خرت الحدثان، والحدثان: فاس له رأس واحد، والفعال: خشبة الفأس، فأما المصادر فإنها تطرد على الفعال في باب فاعل، نحو: ضارب مُضاربة وضِرابًا.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (7,'<span data-type="title" id=toc-5>(٥)
باب
ليس في كلام العرب: أصرفت إلا في موضع واحد</span>، وهو قولك: أصرفت القوافي، إذا أقويتها، ويُنشد لجرير:
قصائد غير مُصرفة القوافي ... فلا عِيًّا بهن ولا اجتلابا',NULL,'33',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (8,'فأما سائر الكلام فصرفت، قال الله تعالى: {ثم انصرفوا صرف الله قلوبهم} وصرف ناب البعير، والجمل يصرف نابه نشاطًا، والناقة كلالاً وإعياءً.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (9,'<span data-type="title" id=toc-6>(٦)
باب
ليس في كلام العرب: المصدر للمرة إلا على فعلة</span>، نحو: سجدت سجدة واحدة، وقمت قومة واحدة، إلا حرفين: حججت حجة واحدة، بالكسر ورأيته رُؤية واحدة، بالضم، وسائر الكلام بالفتح، فأما الحال فمكسور لا غير: ما أحسن عمته وركبته، وحدثني أبو عمر عن ثعلب عن ابن الأعرابي: رأيته رأية واحدة، بالفتح، فهذا على أصل ما يجب.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (10,'<span data-type="title" id=toc-7>(٧)
باب
ليس في كلام العرب: كلمة تامة حروفها كلها من جنس واحد فأدغم استقلالا، إلا حرفين</span>، غلام بَبَّةٌ، أي سمين، وأنشد:
لأنكحن ببه ... جارية خدبه
تَبُذُ أهل الكعبه',NULL,'36',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (11,'والحرف الثاني: قول عمر بن الخطاب رضي الله عنه: (لئن بقيت إلى قابل لأجعلن الناس بَبَّابًا واحدًا).',NULL,'37',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (12,'أساوي بينهم في الرزق والأعطيات.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (13,'<span data-type="title" id=toc-8>(٨)
باب
ليس في كلام العرب: فعَل يفعُل، مما فاؤه واو، إلا حرفًا واحدًا </span>ذكره سيبويه، وهو وَجَدَ يَجُد قال جرير:
لو شئت قد نقع الفؤاد بشربة ... تدع الصوادي لا يجدن غليلا',NULL,'39',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (14,'فقال: وَجَدَ يَجُدُ، وقياسه أن يجيء على يفعل مثل: وزن يَزِنُ، ووعد يَعِدُ.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (15,'<span data-type="title" id=toc-9>(٩)
باب
ليس في كلام العرب: واو وقعت بين ياء وفتحةٍ، وليس فيه حرف واحد من حروف الحلق فسقطت إلا حرفًا واحدًا </span>وهو يَذَرُ، والأصل يَوْذَرُ.
وقياس الواو إذا وقعت بين ياء وفتحة أن تثبت، مثل: يوحل، ويَوْجَل، فإن وقعت بين ياء وكسرة سقطت، مثل: يزن، ويعد، وأصله يوزن، ويوعد، وإنما جاز ذلك لأنهم بنوا يذر على يدع، إذ كان لا ينطق منهما بفعل، ولا فاعل، ولا مفعول، ولا مصدر، فاعرف ذلك.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (16,'<span data-type="title" id=toc-10>(١٠)
باب
ليس في كلام العرب: فَعِلَ يفعلُ بكسر العين في الماضي والمستقبل من الصحيح إلا </span>ثلاثة أحرف:',NULL,'44',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (17,'نَعِمَ ينعِمُ، ويبس ييبس، ويئس ييئس، وقد يجوز فيهن الفتح وسمع، فأما المعتل فيجيء كثيرًا، نحو: ورث يرث، وورم يرم، وومق يَمِقُ، ووفِق يفق، وولى يلى.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (18,'<span data-type="title" id=toc-11>(١١)
باب
ليس في كلام العرب: اسم جاء على ألفاظ الأفعال كلها إلا اسما واحدًا</span>، وهو قولنا: إصبع، مثل: اذهب، وإصبِعٌ، مثل: اضرب، وأَصْبَع، مثل: أكرم، وأَصْبِعٌ، مثل أكْرمْ، وأُصبَعٌ، مثل أُكْرَمُ، وأصْبِعٌ، مثل: أُكْرِمً، وزاد سيبويه، إِصْبُعٌ، وهذا غريب، لأنه ليس في كلامهم إفعُلٌ غيره، ولله على فلان إصبع حسنة، أي نعمة ضافية، وأنشد:
من يجعل الله عليه إصبعا ... في الشر أو في الخير يلقاه معا
وأما قولهم: إن العبد بين إصبعين من أصابع الرحمن فمعناه: نِعَمُهُ وحُسْنُ آثاره.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (19,'<span data-type="title" id=toc-12>(١٢)
باب
ليس في كلام العرب: اسم على مَفْعُلِ إلا أربعة: مَكْرُمٌ ومعْوُن وميسر ومألك</span>، وهي الرسالة.
قال عدي:
أبلغ النعمان عني مألكا ... أنه قد طال حبسي وانتظاري
لو بغير الماء حلقي شرق ... كنت كالغضبان بالماء اعتصاري',NULL,'47',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (20,'وزعم سيبويه أنه ليس في كلام العرب مَفْعُلٌ وقد حكيت هذه الأربعة، فلقائل أن يقول: ليست على مفعل، فمكرم جمع مُكْرُمَةٍ، ومعون جمع معونة، ومألك جمع مألكة، وميسر جمع ميسرة، ووجدت في القرآن حرفا، قرأ عطاء: (فنظرة إلى مَيْسُرِهِ) الهاء هاء كناية.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (21,'<span data-type="title" id=toc-13>(١٣)
باب
ليس في كلام العرب: أفعَلَ فهو مُفْعَلٌ إلا ثلاثة أحرف: </span>أحصن فهو محصن، وألفج فهو مُلْفَجٌ؛',NULL,'49',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (22,'أي أفلس، وفي الحديث: (ارحموا مُلْفَجيكُم) وأسهب فهو مسهب: بالغ.
هذا قول ابن دريد، وقال ثعلب: أسهب هو مسهَب في الكلام، وأسهب فهو مسهِبٌ إذا حفر بئرًا فبلغ الماء.
ووجدت حرفًا رابعًا: اجرأشت الإبل فهي مجْرَأشَّةٌ، بفتح الهمزة إذا سمنت وامتلأت بطونها.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (23,'<span data-type="title" id=toc-14>(١٤)
باب
ليس في كلام العرب: اسم على مفعول إلا </span>مغرود، وهي الكَمْأة، ومُعلُوقٌ: شجر، ومُنْخُورٌ: لغة في المنخر، ومغثور ومغفور من المغافير، صمغ حلو، والصعارير: الصمغ، وربما كانت صُعرورة مثل رأس الجمل.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (24,'<span data-type="title" id=toc-15>(١٥)
باب
ليس في كلام العرب: مصدر تفاعل إلا على التفاعل، بضم العين: </span>تغافل تغافُلا، وتكاثر تكاثُرا: (ألهاكم التكاثر) إلا في حرف واحد جاء مفتوحًا ومكسورًا ومضمومًا قالوا: تفاوت، تفاوَتًا، وتفاوِتًا، وتفاوُتًا. وهذا غريب مليح حكاه أبو زيد.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (25,'<span data-type="title" id=toc-16>(١٦)
باب
ليس في كلام العرب: فعل من المضاعف لم يُدغم وظهر التضعيف فيه إلا </span>قولهم: لَحِحَتْ عَيْنُهُ، وضَبِبَ البلد: كثر ضبابه، وأرض مضبة بفتح الميم، وأرض مضبة بضم الميم: كثر ضبابها، وأَلِلَ السقاء: أنتن، ويَلِلَتْ أسنانه: تسكرت، ورجل أَيَلُّ، وامراة يلاَّءُ، والجمه يُلٌّ، ومَشِشَتْ الدابة.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (26,'<span data-type="title" id=toc-17>(١٧)
باب
ليس في كلام العرب: أفعل فهو فاعل إلا </span>أعْشَبَتْ الأرض فهي عاشب، وأورس الرمث فهو وارس، وأيفع الغلام فهو يافع، وأبقلت الأرض فهي باقل، وأغضى الرجل فهو غاض، وأمحل البلد فهو مَاحِلٌ.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (27,'<span data-type="title" id=toc-18>(١٨)
باب
ليس في كلام العرب: تَمَفْعَلَ الرجل، إنما هو تفعل إلا </span>تَمَدْرَعَ: ليس المدرعة، وتمسكن: صار مسكينًا، وتمندل بالمنديل، وتمغفر وتمغثر: من المغافير والمغاثير، وتمنطق.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (28,'<span data-type="title" id=toc-19>(١٩)
باب
ليس في كلام العرب: اسم ممدود وجمعه ممدود إلا حرفًا واحدًا</span>، وهو: داءُ وأدواءٌ، وإنما صلح أن يكون ممدودًا في اللفظ وأصله القصر، لأنه في الأصل دَوَيٌ، فانقلبت الواو ألفا لتحركها وانفتاح ما قبلها، والألف متى أتى بعدها حرف لين همزه إذا كانت الألف زائدة ككساء، ورداء، فشبهوا وقوعها بعد الألف المنقلبة عن حرف أصلي بالألف الزائدة، فقلبوا الياء همزة فصار داءً.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (29,'<span data-type="title" id=toc-20>(٢٠)
باب
ليس في كلام العرب: مصدر على عشرة ألفاظ إلا مصدرًا واحدًا وهو لقيت زيدًا </span>لقاء، ولقاءه، ولُقى، ولِقْيَانة، وَلُقِيا، ولِقِيا، ولُقيَة، ولِقْيَانا، ولِقَايَة، ولا يقال: لقاه، فألقيتها على قرموطة الكبرتك بحضرة سيف الدولة فلم يدر، ففهمته لأن المرة الواحدة',NULL,'58',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (30,'إنما تكون على فعلة ساكنة العين، ولقاه فعلة، فانقلبت الياء ألفا فاعرف ذلك فإنه حسن.
وقد جاء مصدران على سبعة أحرف، وهما: مكث، مَكْثا، ومُكْثا، ومُكُوثا، ومُكْثانا، ومِكِّيثى، مقصور، ومِكِّيثاء ممدود، ومَكْثة، والحرف الآخر: تم الشيء تِما، وتَما، وتَمًاما، وتمَامة، وتمامًا، وتَتِمَّةً، وَتِمَّةً، ولَيْلُ التِمَام لا غير.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (31,'<span data-type="title" id=toc-21>(٢١)
باب
ليس في كلام العرب: مصدر على فَعْلَلِيلٍ إلا </span>قرقر القُمْرِي قرقريرا، لأن فعلل مصدره على ضربين، فعلل فعللة، وفعلالا: قرقر قرقرة وقرقارًا، وهذا جاء نادرًا، ودحرج دحرجة، ودحراجًا وأنشد:
سَرْهفتُه ما شئت من سرهاف ... حتى إذا ما آض ذا أعراف
يقال: سرهفته، وسرعفته، وسرهدته: حسنت غذاءه، وأجاز البصريون أن يجيء مصدر الرباعي بفتح',NULL,'60',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (32,'أوله أيضًا: زلزل زلزلة، وزلزالاً، وقد قيل: مرمر مرمريرا، وأنشد:
وطال في الجَدَّاء ِ مرمريرها
الجداء: أرض لا ماء بها، وناقة لا سنام لها، وشاة لا لبن لها، وكله من الجد وهو القطع.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (33,'<span data-type="title" id=toc-22>(٢٢)
باب
ليس في كلام العرب: مصدر على مفعول إلا </span>قولهم فلان لا معقول له ولا مجلود له: أي لا عقل له ولا جلد.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (34,'<span data-type="title" id=toc-23>(٢٣)
باب
ليس في كلام العرب: مصدر على فَيْعُولَةٍ إلا </span>كينونة، والأصل كَيَّنُونَةٌ فخفف؛ وصار صيرورة، وحاد حيدودة، وطار طيرورة.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (35,'<span data-type="title" id=toc-24>(٢٤)
باب
ليس في كلام العرب: اسم على فُعِل إلا </span>حرفًا واحدًا دُئِل: دُوَيبَّةٌ، قال الشاعر:
جاءوا بجمع لو قيس مُعْرسُهُ ... ما كان إلا كمعرس الدُئل
وهذا شيء غريب نادر، وما ذكره سيبويه في',NULL,'65',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (36,'الأبنية ولا غيره، وإنما يذكرون أن أبنية الثلاثي عشرة: فَعْلٌ مثل سَعْدٌ، وفُعْلٌ مثل قُفْلٌ، وفِعْلٌ مثل جذع، وفَعَل، مثل كمد، وفِعَلٌ مثل عنب، وفَعِل مثل ضحك، وفَعُلٌ مثل رجل، وفُعُل مثل طنب، وفِعِل مثل إبل، وفُعَل مثل نغر، وفُعِل مثل الدئل، وهذا الحادي عشر غريب، والدول والديل، قبيلتان، والدئل هذه الدابة، وإليها نسب أبو الأسود، ففتح لما نسب إليه استثقالاً، فقالوا: أبو الأسود الدؤلي.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (37,'<span data-type="title" id=toc-25>(٢٥)
باب
ليس في كلام العرب: صفة على فعلاء إلا </span>طور سيناء، والطور: الجبل، والسيناء والسينين: الحسن، وقد قرئ (وطور سيناء الأمين) وكل',NULL,'67',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (38,'جبل مثمر فهو سينين، وإذا لم ينبت فهو أقرع، وجبل أقرع، لا ثمر عليه، وأرض حرماء، لا ماء بها، وأرض جلحاء: لا شجر بها، وأرض جداء، لا ماء بها، وأرض عداء: بعيدة، وأرض يهماء: لا يهتدى بها، وأرض مسحاء: مستوية ذات حصا، وأرض خبراء: قاع تبت السدر، وأرض ميثاء: سهلة دمثة.
وليس في الصفات صفة على فعلانة إلا حرفا واحدًا: ضب حيكانة، أي عداء.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (39,'<span data-type="title" id=toc-26>(٢٦)
باب
ليس في كلام العرب: اسم على يُفَاعِلاء إلا </span>ينابعاء، وليس على أفعلاء إلا حرف واحد الأربعاء: عمود الخيمة، وجلس فلان الأربعاوي، أي متربعًا، فأما يوم الأربعاء فإنه بكسر الباء وفتحها، والأصمعي يفتح، وغيره يكسر، ويوم الأربعاء يوم من أيام العرب في مقاتل الفرسان، وهو اسم موضع، ذكره أبو عبيدة',NULL,'69',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (40,'<span data-type="title" id=toc-27>(٢٧)
باب
ليس في كلام العرب: فعل دخل عليه الألف واللام عند سيبويه والفراء إلا </span>قولهم: اليجدع، واليتقصع، واليتتبع، واليسع اسم نبي عليه السلام، واليحمد: قبيلة، وكأنهم أرادوا: الذي يجدع، والذي يتقصع، وإذا سموا رجلا بفعل نحو: يزيد، ويشكر، وتغلب، لم يقولوا: اليزيد، فأما قول الشاعر:',NULL,'70',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (41,'وجدنا الوليد بن اليزيد مباركًا ... شديدا بأعباء الخلافة كاهله

فإنه أزوج باليزيد الوليد للمجاورة، كما قالوا: يأتينا بالغدايا والعشايا، ولا تجمع غداة على غدايا، وإنما أزوج بها العشايا؛ وكما قال النبي عليه السلام في النساء إذا زرن القبور: وليرجعن مأزورات غير مأجورات، وإنما هو موزورات، ولكن أزوج به المأجورات، ومن غريب ما يسمى بالفعل قولهم: تركته بوادي إصمت، وبأطرقا، أي قفر وحش، كان',NULL,'71',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (42,'ثلاثة نفر، فلما بلغوا هذا الموضع، قال أحدهم لصاحبيه: أطرقا، أي اسكتا، فسمي الموضع: أطرقا، وتركته بوادى تضلل، ووادي تهبط، ووادي تخيب؛ إذا هلك، ولم يدر أين صقع وبقع، ولا أدري أي الجراد عاره، فأما قولهم: اذهب بذي تسلم، فمعناه: والله يسلمك، كما يقال: لعا لك، ودعدعا لك، ونقذا لك، كل ذلك معناه سلمك الله وحفظك، وزاد اللحياني: لعالعا.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (43,'<span data-type="title" id=toc-28>(٢٨)
باب
ليس في كلام العرب: ما جاء من المضاعف على فعلت إلا </span>قولهم: لبيت يا رجل، ذكره يونس',NULL,'73',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (44,'ولبب الرجل، كل ذلك من اللب، قولهم: عززت الشاة إذا قل لبنها، من قولهم شاة عزوز، إذا كانت ضيقة الأحاليل قليلة اللبن، وهي ضد الفتوح.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (45,'<span data-type="title" id=toc-29>(٢٩)
باب
ليس في كلام العرب: تصغير بألف إلا </span>حرفين، ذكرهما أبو عمرو الشيباني عن أبي عمرو الهذلي: دُوابة يريد دويبة، وهداهد تصغير هدهد. وأنشد:
كهداهد كسر الرماة جناحه ... يدعو بقارعة الطريق هديلا',NULL,'75',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (46,'والهديل: فرخ كان على عهد نوح عليه السلام فصاده رجل، فكل الطير تبكيه.
وأملح ما سمع في التصغير ما حدثني به أبو عمر الزاهد عن ثعلب عن ابن الأعرابي قال: تصغير جيران أجيار، لأن الجمع الكثير في التصغير يرد إلى الجمع القليل، فرد جيران إلى أجوار، فقال لما صغره: أجيوار، ثم قلب الواو ياء وأدغم، كما تقول في تصغير أثواب: أُثَيَّابٌ.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (47,'<span data-type="title" id=toc-30>(٣٠)
باب
ليس في كلام العرب: كلمة أولها واو وآخرها واو إلا </span>قولهم: واو، ولذلك يجب أن يكتب كل مقصور أوله واو بالياء، نحو الوجى والونى والوغى، لأنك تحكم على آخره بالياء، إذا لم يوجد كلمة أولها واو وآخرها واو، وكذلك ما كان ثانيه واوا من المقصور كتبته بالياء مثل النوي والثوي والجوي في الأعم الأكثر.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (48,'<span data-type="title" id=toc-31>(٣١)
باب
ليس في كلام العرب: صفة فيها ست لغات من أسماء الرجال إلا </span>قولهم: رجل زُمَّل: ضعيف، وزُمَّيْلَةٌ، وزُمالة، وزمال، وزُمَيْل، وزِمْل، ومثله الحبوكرى، وبغير ياء، وحبوكران بزيادة ألف ونون.
فأما في غير هذا فقد قالوا: رُبْوَة، وربوة،',NULL,'78',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (49,'وربوة، ورباوة، ورَبَاوَة، ورِبَاوَةٌ، ست لغات. وقالوا: رُغوة اللبن، ورَغوة اللبن، ورِغوة اللبن ورِغَاوة اللبن، ورغَاوَتُهُ، ورغَايتُهُ: خرشاؤُهُ.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (50,'<span data-type="title" id=toc-32>(٣٢)
باب
ليس في كلام العرب: إتباع بخمسة أحرف إلا </span>في كلمة واحدة: مال كثير بثير غمير مرير بجير بذير، وقيل: مجير.
فأما الثلاث والاثنان فكثير نحو قولهم: حس بسن قسن، وحار يار جار.
حدثنا أبو عمر عن ثعلب عن ابن الأعرابي قال: قيل الأعرابي: ما تريدون بقولكم: فلان كَزٌّ لَزٌّ؟ فقال: حرف نَتِدُ به كلامنا، أي نؤكده به.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (51,'<span data-type="title" id=toc-33>(٣٣)
باب
ليس في كلام العرب: فُعال على فواعل إلا </span>حرفان: دُخان ودواخن، وعثان وعواثن، والعُثان أيضًا: الدخان والغبار، ويقال للدخان أيضًا: النُحَاس، قال تعالى: (يرسل عليكما شواظ من نار ونحاس فلا تنتصران) والشواظ: النار المحضة.
ويقال: للخضرة التي بين النار والشمعة: الكَلْحَبة، ويقال للدخان: الدُّخ، وأنشد:
لا خير في الشيخ إذا ما اجلخا ... بين رواق البيت يغشى الدخا
وانثنت الرجل فصارت فخا',NULL,'81',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (52,'<span data-type="title" id=toc-34>(٣٤)
باب
ليس في كلام العرب: اسمٌ يجيء على فُعَلاء وفُعْلاَءٍ بتحريك العين وتسكينها إلا </span>حرفان: خششاء، وخشَّاء، للعظم الذي وراء الأذنين، وقوَباء، وقُوباء، فمن أسكن نون وصرف، ومن أظهر لم ينون ولم يصرف، وشبهه قُسَّاء: جبل، بالصرف، وقُسَاء لا ينصرف.
فسئل ثعلب عن ذلك فقال: لأن الأصل قُسواء فخفف، ونظيره قوم براء وبرءاء.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (53,'<span data-type="title" id=toc-35>(٣٥)
باب
ليس في كلام العرب: اسم أوله ياء مكسورة إلا </span>يسار، لليد اليسري لغة في اليسار، والفتح هي الفصحى، ويقال: لليسار الشمال والشؤمى.
وسألت نفطويه عن قول جرير:',NULL,'84',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (54,'وإني لعف الفقر مشترك الغنى ... سريع إذا لم أرض داري احتماليا
وباسط خير فيكم بيمينه ... وقابض شر عنكم بشماليا
فقال: العرب تنسب كل خير إلى اليمين، وكل شر إلى الشمال، وكذلك قال الله عز وجل: (فأما من أوتي كتابه بيمينه)، (وأما من أوتي كتابه بشماله)، فأما الفعل في مثل: يَيْجَل ويِيجَلُ وتِعْلَم وتِمَت لغة بني أسد، فمن كسر من أول المضارع النون والتاء والهمزة لم يكسر الياء، فيقول: يعلم، استثقالا للكسرة في الياء على أنها حكيت شاذة.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (55,'<span data-type="title" id=toc-36>(٣٦)
باب
ليس في كلام العرب: فعل فعلا إلا </span>طلب طلبا، ورفض رفضا، وطرد طردا، وحلب حلبا، وجلب جلبا، ورقص رقصا، ستة أحرف جاء المصدر والماضي مفتوحين.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (56,'<span data-type="title" id=toc-37>(٣٧)
باب
ليس في كلام العرب: كسرة بعدها ضمة إلا </span>حرفان زِئْبُر لغة في الزئبر، وإِصْبُع حكاه سيبويه، وضِئْبُل: الداهية، والنئدل والنأدل، والنيدل، والنِّيدِلان، والنيدُلان؛ وهو الكابوس يقع على الإنسان، وأنشد:
نفرجة القلب قليل النيل ... يلقى عليه النيدلان بالليل',NULL,'87',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (57,'ويقال له: الجاثوم أيضًا، والكابوس، والجُثم، لأن الساكن ليس بحاجز حصين، فلذلك قالوا: ادخل، واقتل، والأصل: ادخُل فأتبعوا الضم الضم كراهة الخروج من كسر إلى ضم.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (58,'<span data-type="title" id=toc-38>(٣٨)
باب
ليس في كلام العرب: أَلِفُ الوصل تدخل على متحرك إلا </span>ثلاثة مواضع، قولهم: اسل زيدًا لغة عبد القيس، حكاها أبو زيد والفراء، يريدون: اسأل، والثاني- أن العرب تقول زيد الأحمر، والحمر، ولحمر ثلاث لغات.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (59,'والثالث- قال سيبويه: لو سميت رجلا بالباء من اضرب قلت: هذا اب قد جاء، وخالفه سائر النحويين، فمنهم من يقول: رب، ومنهم من يقول: ضَبٌ، وآخرون: ضرب، يردون الحروف كلها.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (60,'<span data-type="title" id=toc-39>(٣٩)
باب
ليس في كلام العرب: ألف الوصل تدخل على الأفعال لسكون أوائلها، أو على نُبَذٍ من الأسماء.
ولا تدخل على ألف الوصل على الحروف إلا </span>على حرفين: اللام للتعريف: الجمل، الفرس، وعلى قولهم: ايم الله، في القسم، وهذا مفتوحان.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (61,'وليس في كلام العرب ألف وصل مفتوحة إلا في هذين، إنما تكون مكسورة ومضمومة، وإنما فتحوا هذين لأنها خالفت بدخولها موضعها فخالفوا بحركتها حركتَها، وقد حُكيت ايم اللهِ بالكسر.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (62,'<span data-type="title" id=toc-40>(٤٠)
باب
ليس في كلام العرب: مِفْعِلٌ إلا </span>حرفان: مِنْتِنٌ ومِنْخِرٌ. قال سيبويه: وزن منتن مُفْعِلٌ، لأنه أنتن فهو منتِن، مثل أكرم فهو مكرم، وإنما أتبعوا الكسر الكسر، كما قالوا: الأسود بن يُعفر، وإنما هو يَعفر، فأتبعوا الضم الضم، مثل: منتن ومنخر، والمِغِيرة يريدون المُغِيرة.
وقال أبو عبيد: أنتَن فهو مُنْتِن، ونتن فهو مِنْتِنٌ.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (63,'<span data-type="title" id=toc-41>(٤١)
باب
ليس في كلام العرب: ما عينه ياء مثل كِلْتُ، وبِعتُ، وكدت إلا </span>أوله مكسور، لتدل الكسرة على الياء الساقطة، لأنه من كاد يكيد، وكال يكيل، وباع يبيع، إلا حُدْنا وكُدْنَا، ووجه ذلك ما ذكره شيخنا ابن دريد: أن من العرب من يقول: كان يكون كودا، وحاد يَحُود حودًا، وقد جاء مفتوحًا في حرف واحد وهو قولهم: لست قائمًا، وقد حكى الفراء: لُسْنَا بضم اللام لأنها لا تتصرف لشبهها بما.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (64,'<span data-type="title" id=toc-42>(٤٢)
باب
ليس في كلام العرب: فَعِل يَفعُل إلا </span>خمسة أحرف: دِمْتُ أَدُومُ، ومِتُ أموت، وفضل يفضل، ونعم ينعم، وقنط يقنط، وقد حكى ابن الأعرابي: فضل ونعم، فمن ضم المضارع فعلى هذه اللغة.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (65,'<span data-type="title" id=toc-43>(٤٣)
باب
ليس في كلام العرب: اسم على فعل إلا </span>ثمانية أسماء: إبل، وإِطِلٌ، وبِأَسْنَانه حِبِرٌ، أي صفرة، ولعب الصبيان جِلِخَ طِلِبَ، ووِتِدٌ، عن أبي عمر، ولا أفعل ذاك أبد الإبد، حكاه ابن دريد، وامرأة بِلِزٌ: ضخمة، والبلص: طائر، ويقال له: البَلَصُوصُ، وينشد:',NULL,'96',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (66,'كالبَلَصُوصِ يَتْبَعُ البَلَنْصَى
ولم يحْكِ سيبويه إلا حرفًا واحدًا، إِبِلٌ وَحْدَهُ، لأنه بلا خلافٍ، والباقية مُختلفٌ فيهن، فيقال: إِطْلٌ وأَيْطَلٌ، وهي الخَاصِرةُ، فتسمى الخاصرة الإِطْلَ، والإِطِل، والأَيْطَلَ، والقُرْبَ، والكَشْحَ، والصُّقْلَ، والنَاطِقَةَ، والخَوْشان: الخاصرتان، وقد قيل: مِسِكٌ، وَسِلِمٌ، والحِجِلُ، يريد الخَلْخَال، وأنشد:
أرتني حجلاً على ساقها ... فهش الفؤاد لذاك الحِجل
وخطب نِكِحٌ',NULL,'97',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (67,'<span data-type="title" id=toc-44>(٤٤)
باب
ليس في كلام العرب: اسم على أفعْلٍ إلا </span>ستة أسماء: آنُكٌ جاء في الحديث: (من استمع إلى قَيْنَةِ صب في أذنيه الآنُك)، وهو الرصاص، وأبْهُلٌ: نبات، وأنعم وأذرح وأثمد: مواضع، وأسقف النصارى.
وسيبويه يقول: ليس في كلام العرب أفْعُلٌ واحد، وقال أَشُدٌّ، وأَوْجُسٌ، وأَجْمُعٌ، وأَنْعُمٌ، وأَثْمُدٌ: مواضع.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (68,'<span data-type="title" id=toc-45>(٤٥)
باب
ليس في كلام العرب: سِواءٌ بالكسر والمد إلا </span>في حرف واحدٍ، يقال: فُلانٌ في سِيِّ رَاسِهِ، وفي سِوَاءِ رَاسِهِ أي في نعمة سابغة ضافية، وكأنه مصدر من سَاوى رأسه يساويه سِوَاءً ومساواة، لأن جميع كلام العرب جاءني القوم سِوَى زيد بالكسر والقصر، وسُوى لغة، فإذا فتحت السين مددت: جاءَنِي القَوم سَوَاءَكَ.
وأنشد:
وما قصدت من أهلها لِسَوَائِكَا',NULL,'99',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (69,'ومثل قولهم: فلان في سِواءِ رَأسِه، فلان في عيش خُرم، ورافع، وضاف، وسابغ، وفي الطفش والرفشِ، أي في الأكل والنكاح، وقد وقع في الأَهْيَغَيْنِ، والشوسل، والبآم.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (70,'<span data-type="title" id=toc-46>(٤٦)
باب
ليس في كلام العرب: اسم على فُعْلُولٍ، وفِعلاَلٍ إلا </span>طُنبُور وطِنبار، وجُذمُورٌ وجِذمارٌ: أصل الشيء، وعُسلُوجٌ وعِسلاَجٌ: الغصن، والذُّعلُوق مثل الغُصنِ، وبُزعُونٌ وبِزعان، للشاب الطَّرِي، وللغزال، وشُمْرُوخٌ، وشِمْرَاخ، وعُثْكول وعِثكَال، لعنقود النخل، وعُنقود وعِنقاد، وحُذفور وحِذفار: نواحي الشيء، قال النبي صلى الله عليه وسلم: من أصبح معافى في بدنه، آمنا في سربه، يملك قوت ليلته فكأنما حيزت له الدنيا بحذافيرها). السرب بالفتح: الطريق، وبالكسر: النفس، وسرب ظباء ونساء بالكسر أيضًا.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (71,'<span data-type="title" id=toc-47>(٤٧)
باب
ليس في كلام العرب: فعل كُسر أول مُستقبله وماضيه مفتوح إلا </span>حرف واحد: أبيت تِئبى، وأنشد:
ماءٌ رَوء ونصي حواليه ... هذا بِأفواهك حتى تِئْبِيَه
وإنما كسروا هذا الحرف لما رأوا مستقبله مفتوحًا قدروا أن ماضيه مكسور مثل علمت تعلم، ونحن نعلم رب اغفر وارحم، واعف عما تعلم، إنك أنت الأعز',NULL,'102',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (72,'الأكرم، هذه لغة بني أسد، لما كان ماضيه مكسورًا على فعل أحبوا أن يعلموا أن الماضي مكسور بكسر أول المستقبل، ومن قال: أنا إعلم، وأنت تعلم، ونحن نعلم، لم يقل: زيد يعلم استثقالا للكسرة على الياء، وإنما فعلوا ذلك شاذا، فإذا كان ثانيه واوًا كسروا الياء لتنقلب الواو ياء نحو وجع زيد ييجع، قال الشاعر:
قعيدك ألا تُسمعيني ملامة ... ولا تنكئى قرح الفؤاد فَيِيجعا
وقد قالوا: وجلت تِيجَلُ، وتَوْجَل، وتَاجَلُ، وتَيْجَلُ أربع لغات، وخامسة تأجل بالهمز، وهذا غريب، وقد مضى هذا الفصل قبل.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (73,'<span data-type="title" id=toc-48>(٤٨)
باب
سيبويه وأبو زيد يزعمان أنه ليس في كلام العرب: اسْفَعْلَ إلا </span>حرفا واحدا وهو أسطاع يُسطِيع بمعنى أطاع يُطيع، السِين زائدة سماعًا عن العرب، والكوفيون يقولون: إنه ليس في كلام العرب سِين تُزاد وحدها، وإنما هو استطاع، فأسقطوا التاء، فإذا قيل لهم: فلم',NULL,'104',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (74,'ضممتم أول المضارع؟ قالوا: لما أسقطت التاء أشبهت أفعل يُفعِل.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (75,'<span data-type="title" id=toc-49>(٤٩)
باب
ليس في كلام العرب: فعل ثلاثي يستوعب الأبنية الثلاثة فعل، وفَعِل، وفَعَل إلا </span>كَمُل، وكَمِلَ، وكَمَلَ، وكدر الماء، وكَدِرَ، وكَدَرَ، وخَثُر العسل، وخَثِرَ وخَثَرَ، وسَخُو الرجل، وسَخِي وسَخَا، وسَرِي، وسَرَا، وسَرُوَ، وأنشد:
إن السَّرِيَّ إذا سرا فبنفسه ... وابن السَّرِيِّ إذا سرا هما',NULL,'106',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (76,'<span data-type="title" id=toc-50>(٥٠)
باب
ليس في كلام العرب: فعل زِيدَ على آخره حرفان، فصار ثلاثة أحرف من جنس واحدٍ، إلا </span>حرفًا واحدًا، وهو قول الشاعر:
فالزمي الخُصَّ واخفِضِي تبيضِضِّي
إنما هو البياض ضاد واحدة، ثم قال: ابيض فزادوا ضادا، مثل احمر واصفر، فزاد الشاعر على الضاد الأولى ضادين، فقال: ابيضضي، لأن المشدد حرفان.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (77,'<span data-type="title" id=toc-51>(٥١)
باب
ليس في كلام العرب: من ذَواتِ الياء والواو كلمة على مَفْعِلٍ إلا </span>مفتوح العين، ما خلا حرفين فإنهم كسروا فقالوا: مَأوِي الإبل، ومأقِي العين، على أن الأصمعي',NULL,'108',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (78,'وغيره قد حكوا مأقي: وماقِي بغير هَمْزٍ، ومُوقٌ، ومٌوْقٌ، ومُوْقِيء، ومُوقِي، ست لغات، قال: وكان أبو هريرة يكتحل كل ليلة من الماق إلى الموق، والجمع: آماق، وأمواق، ومَواق، ومواقي.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (79,'<span data-type="title" id=toc-52>(٥٢)
باب
ليس في كلام العرب: ما كُره التشديد فيه فقُلب ياء إلا </span>في دينار، وديباج، وديوان، وشيراز، وقيراط. والأصل: دِنار، وقراط، ودِباج، ودِوان، وشراز، ألا ترى أنك إذا جمعت رددت الحرف إلى أصله، فقلت: دنانير، وقراريط، وشراريز، ودواوين،',NULL,'110',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (80,'ودبابيج، وربما قالوا: دياوين فتركوه على القلب، وأنشد:
دياوين تنفق بالمداد.
وشبيه به تمطى، والأصل: تمطط أي تبختر، قال الله تعالى: (ثم ذهب إلى أهله يتمطى)، (وقد خاب من دساها): أي أخفاها، والأصل: دسسها، وربما ضاعفوا فقالوا في كبب: كُبكِبَ، وفي رقق: رقوق، وأنشد:
وتبرد برد رداء العرو ... سِ في الصيف رقرقت فيه العبيرا
أراد رققت، ومثل الأول:
تقضي البازي إذا إذا البازي كسرد
أراد: تَقَضَّضّ.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (81,'<span data-type="title" id=toc-53>(٥٣)
باب
ليس في كلام العرب: مثل هرقت الماء، والأصل: أرقت، إلا </span>ثلاثة أحرف: هرقته أهريقه، وهنرت الثوب أهنيره، وهرحت الدابة أهريحها، وأصل ذلك كله: أُأَريق، وأُأَنير، وأُأَريح، فأبدلوا من الهمزة الثانية هاء استثقالا، ومن قال: أريق، أسقط همزة واحدة.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (82,'<span data-type="title" id=toc-54>(٥٤)
باب
ليس في كلام العرب: فعل صح من المعتل ولم يعل إلا </span>استحوذ، وأغيمت السماء، واستنوق الجمل،',NULL,'113',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (83,'واستتيست الشاة، وأغيلت المرآة من الغيل، وهي أن تحمل على حيض، وذلك ردئ، وقد يجيء في الشعر كثيرًا ضرورة كما قال:
صددت فأطولت الصدود وقلما ... وصال على طول الصدود يدوم
وأطيبت يا رجل.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (84,'<span data-type="title" id=toc-55>(٥٥)
باب
ليس في كلام العرب: من ذوات الواو مفعول خرج على أصله إلا </span>في حرفين يقال: مسك مدووف، وثوب مصوون، وحرف ثالث قد ذكرته بعد، إنما وجب أن يكون مدوفا مثل مَقُول، فأما بنات الياء فجائز أن يجئ على أصله، بر مكيل ومكيول، وثوب مبيع ومبيوع، وبسرة مطيوبة، وأنشد:
قد كان قومك يحسبونك سيدا ... وإخال أنك سيد معيون',NULL,'115',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (85,'<span data-type="title" id=toc-56>(٥٦)
باب
ليس في كلام العرب: أفعل فهو فعول إلا </span>ثلاثة أحرف: أنتجت الناقة فهي نَتُوج، وأشصت فهي شَصُوص: قل لبنها، ومنه الشصاصاء؛ أي الجدب والقحط، وأعقت الفرس فهي عقوق، أي حملت، وحرف رابع قد ذكرته بعد.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (86,'<span data-type="title" id=toc-57>(٥٧)
باب
ليس في كلام العرب: أفعلت أنا وفعلت غيري إلا </span>حرفا جاء نادرًا، لأنه ضد العربية، وهو أكب زيد في نفسه، وكب غيره، قال الله تعالى: (فكبت وجوههم',NULL,'118',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (87,'في النار). وقال الله تعالى: (أفمن يمشي مكبا على وجهه) لأن كلام العرب: جلس وأجلس غيره، وذهب وأذهب غيره، وقد قيل: أقشعت الغيوم وقشعتها الريح، وقال رسول الله صلى الله عليه وسلم: (وهل يكب الناس في النار إلا حصائد ألسنتهم) فقال: يَكُب، ولم يقل: يُكِب.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (88,'<span data-type="title" id=toc-58>(٥٨)
باب
ليس في كلام العرب: فَعُلَ وهو فاعل إلا </span>حرفان: فره الحمار فهو فاره، وعقرت المرأة فهي عاقر، فأما طهر فهو طاهر، وحمض فهو حامض، ومثل فهو ماثل فبخلاف ذلك، يقال: حمض أيضًا، وطهر، ومثل.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (89,'<span data-type="title" id=toc-59>(٥٩)
باب
ليس في كلام العرب: أفعلتُه فهو مفعول إلا </span>أجَنَّهُ الله فهو مجنون، وأزكمه الله فهو مزكوم، وأحزنته فهو محزون، وأحببته فهو محبوب، وقيل محب، وأنشد:',NULL,'121',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (90,'ولقد نزلت- فلا تظني غيره- ... مني بمنزلة المحب المكرم
قد قالوا: حببته، وقرأ أبو رجاء (فاتبعوني يَحْبِبكم الله)',NULL,'122',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (91,'<span data-type="title" id=toc-60>(٦٠)
باب
ليس في كلام العرب: أفعل صفة والجمع على فعال إلا </span>ثلاثة أحرف من الصفات: أجرب وجِراب، وأعجَف وعجاف، وأبطح وبِطاح.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (92,'<span data-type="title" id=toc-61>(٦١)
باب
ليس في كلام العرب: مصدر على تفعلةٍ إلا </span>حرفًا واحدًا، قال الله تعالى: (ولا تلقوا بأيديكم إلى التهلكة) وقد جاء تُهْلُوك أيضًا، أنشدنا أبو عمر عن ثعلب عن ابن الأعرابي:
شبيب عادى الله من يقليكا ... وسبب الله له تهلوكا
يا بأبي أرواح نشر فيكا ... كأنه وهنأ لمن يدنيكا
ريح خزامى ولي الركيكا
الرك والركِيك والرُّكاك: المطر الضعيف، وبه شبه الرَّكِيك، والرُّكاكة من الناس: الضعفاء.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (93,'<span data-type="title" id=toc-62>(٦٢)
باب
ليس في كلام العرب: اسم على ستة أحرف، إنما أكثر ما يكون على خمسة بلا زيادة، إلا </span>اسما واحدا: قبعثرى، وهو الجمل الضخم، وقيل: الفصيل المهزول. وقد بلغ بالزوائد ثمانية: اشهاب الفرس اشهيبابا،',NULL,'125',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (94,'وأقل ما يكون الاسم على ثلاثة، والفعل أكثر ما يكون على أربعة، فمتى وجدتهما أقل من ثلاثة فقد نقص منه حرف، أو حرفان، وقد وجدت حرفًا آخر: في فلان عَفَنْجَجِيَّةٌ مُشنَّعة؛ أي حماقة، ثمانية أحرف.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (95,'<span data-type="title" id=toc-63>(٦٣)
باب
ليس في كلام العرب: رجل أفعل وفعل إلا </span>أرمد ورمد، وأحمق، وحمِق، وثوب أخشن وخشن، وأحدب وحدِب، وأبح وبحِح، ولا يقال باح، وأنكد ونكد، وأوجل ووجل، وأقعس وقعس، وأشعث وشعث، وأجرب وجرب، وأجدع وجدِع.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (96,'<span data-type="title" id=toc-64>(٦٤)
باب
ليس في كلام العرب: مفعول على فَعِل إلا </span>حرفًا واحدًا: غلام جدِع: مقرقم ومزلج، وسغل مثل جدع، فصار حرفين، فإذا أحسن غزاؤه، قيل: مسرهد، ومسرهف.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (97,'<span data-type="title" id=toc-65>(٦٥)
باب
لم نجد صفة على فاعلٍ للمبالغة إلا </span>في حرفين: رجل جامل بمعنى جميل، ورجل ظارف بمعنى ظريف، والجيد أن تقول: رجل ظريف في الحال، وظارف',NULL,'129',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (98,'عن قليل، وميت في الحال، ومائت عن قليل، وغضبان في الحال، وغاضب عن قليل، ويقال: رجل ظريف وظُراف، وَظُرَّاف كما تقول: رجل كبير وكبَار وكبَّار، وكل فعيل جائز فيه ثلاث لغات: فعيل، وفعال وفعَّال: رجل طويل، وإذا زاد طوله قلت: طُوال، وفي القرآن: (إن هذا لشيء عجاب) وعجاب، وفيه أيضًا: (ومكروا مكرًا كبارا) وكبارًا، قرأه ابن محيصن المكي.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (99,'<span data-type="title" id=toc-66>(٦٦)
باب
ليس في كلام العرب: اسم ممدود جمع مقصورا إلا </span>ثمانية أحرف: وهو صحراء وصحارى، وعذارء وعذارى، وصلفاء وصلافى: أرض غليظة، وخبراء وخبارى: أرض فيها ندوة، وسبتاء وسباتى: أرض فيها خشونة، ووحفاء ووحافى: أرض فيها حجارة، ونبخاء ونباخى، ونفخاء ونفاخى، لأن الممدود يجمع',NULL,'131',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (100,'على أفعلةٍ: رداء وأردية، والمقصور يجمع ممدودًا: رحى وأرحاء، وقفا وأقفاء، ويا غلام خذ بأقفائهم.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (101,'<span data-type="title" id=toc-67>(٦٧)
باب
ليس في كلام العرب: مقصور جمع على أفعلة كما يجمع الممدود إلا </span>قفا وأقفية، كما جمعوا:',NULL,'133',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (102,'بابا أبوبة، وندى أندية، وهذا شاذ كما شذ الرضا وهو مقصور، قالوا: رضاء فمدوه، قال الشاعر:
شهاد أندية ولاج أبوبه قوال محكمة فكاك أقياد
نقاض مبرمة فتاح مصمته ... فتاح غادية حباس أوراد
حلال ممرعة فراج معضلة ... سباق عادية طلاع أنجاد
وأنشد أبو عثمان المازني في مد القفا:
حتى إذا قلنا تيفع مالك ... سلقت رقية مالكًا لقفائه',NULL,'134',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (103,'<span data-type="title" id=toc-68>(٦٨)
باب
ليس في كلام العرب: كلمة فيها أربع لغات: لغتان بالهمز، ولغتان بغير همز، إلا </span>أربعة أحرف وهن: أومأت إليه وومأت، وأوميت ووميت، وضنأت المرأة وضنيت، وضنت: كثر ولدها، وأضنأت',NULL,'135',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (104,'وأضنت، ورمح يزني، وأزني ويزاني وأزاني، والحرف الرابع قلب، وهمزت اللغات الأربع، وهو: فلان ابن ثأداء، وثأداء، ودأثاء، ودأثاء، إذا كان ابن أمة، ويقال: للأمة، حمراء العجان، والبغية، والفرتنى، ومدينة، وقينة، وسرية، وكرينة، إذا كانت مغنية.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (105,'<span data-type="title" id=toc-69>(٦٩)
باب
ليس في كلام العرب: مصدر على فعلان بجزم العين إلا </span>حرفين: شنئته شنآنا، وزدته زيدانا، لأن المصادر على هذا تجئ على فعلان كالجولان والنزوان، على أنه قد قيل: شنئته إذا أبغضته شنأ، وشِناء، وشُنْأ، وشنانا، وشنآنا، وشَنَآنا، ومشْنئَة، قال الله تعالى: (إن شانئك هو الأبتر) أي لا ولد له منقطع الذكر، وقال تعالى: (ولا يجرمنكم شنآن قوم).',NULL,'137',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (106,'بياض',NULL,'138',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (107,'<span data-type="title" id=toc-70>(٧٠)
باب
ليس في كلام العرب: ما جاء على تِفِعَّالٍ وفِعِلاَّلٍ إلا </span>قولهم: تملقه تملاقًا، قال:
ثلاثة أحباب فحب خلابة ... وحب تِملاق وحب هو القتلُ
فقلت للأعرابي زدني فقال: البيت يتيم، أي فرد،',NULL,'139',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (108,'وإذا أُفرد الولد عن أبيه فهو يتيم، واليتم في البهائم من قبل الأمات، والأمات جمع أم مما لا يعقل، وأمهات مما يعقل، وقد يجوز أمات فيمن يعقل، أنشد أبو عبيد:
لقد آليت أغدر في جذاع ... ولو منيت أمات الرباع
ويجوز أن يكون اليتم في الطير من قبل الأب والأم لأنهما جميعًا يزُقان ويلقمان، وفي الجراد منهما أيضًا، لأنه يغرز البيض ويطير، ولا يتم بعد البلوغ، والعَجِي في البهائم مثل اليتيم، ورملة يتيم: أي منفردة، ودرة يتيمة: لا نظير لها منفردة، واليتم: الغفلة، لأن اليتيم مغفول عنه، وقد ذكرته بعد، ومثل التملاق: التقطاع، والتبتال، وتكلام، وتلقاع، وتنقام، وسِجِلاط: الياسِمون، وإن شئت الياسِمِين، وجهنام: البئر البعيدة القعر، وبذلك سميت جهنم،',NULL,'140',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (109,'والشاعر الذي كان يهاجي الأعشى أو تابعته يقال لهما: جُهُنَّام.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (110,'<span data-type="title" id=toc-71>(٧١)
باب
ليس في كلام العرب: اسم على فِعْتَلٍ إلا </span>كلتا عند الجرمي، وعند سيبويه: إنما هو كلوا فعلى فانقلبت الواو تاء كما يقال: تالله، والأصل والله، وعند الكوفيين كلتا تثنية كِلت، والدليل على أنه واحد أن العرب تقول: كلتا المرأتين قائمة، ولا يقال قائمتان',NULL,'142',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (111,'إلا في شذوذ، قال الله تعالى: (كلتا الجنتين آتت أكلها) ولم يقل: آتتا.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (112,'<span data-type="title" id=toc-72>(٧٢)
باب
ليس في كلام العرب: ثلاثة أسماء صيرن اسما واحدا إلا </span>حرفا واحدا، وهو قولهم: فُرات بادَقْلى، حكاه',NULL,'144',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (113,'الفرأُ، وكان ابن الخياط يتعجب من ذلك إنما يجعل الاسمان اسما واحدًا مثل: خمسة عشر، وحضرموت، وبعل بك، وهو جاري بيت بيت، ونحو ذلك.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (114,'<span data-type="title" id=toc-73>(٧٣)
باب
ليس في كلام العرب: اسم على فَعْلَة، ولا صفة جمعت على فواعل إلا </span>حرفا واحدا، يقال: ليلة طلقة:',NULL,'146',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (115,'لا حر فيها ولا قر ولا ظلمة، وليال طوالق على فواعل، وإنما فواعل جمع لفاعلة: طالقة وطوالق، وامرأة صالحة طالحة قانتة، فإذا جمعت جمع السلامة قيل: صالحات طالحات قانتات، فإذا جمعت جمع التكسير قلت: صوالح طوالح قوانت، قرأ عبد الله بن مسعود: (فالصوالح قوانت حوافظ للغيب بما حفظ الله)، وقرأ أبو جعفر يزيد بن القعقاع (بما حفظ الله) بالفتح، ومعناه والله أعلم على حذف المضاف، أي حفظ دين الله.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (116,'<span data-type="title" id=toc-74>(٧٤)
باب
ليس في كلام العرب: فعل وفِعلة إلا </span>تسعة أحرف: الذل والذلة، والحكم والحِكمة، والبغض والبِغضة، والعذر، والعذرة، والقل والقلة، والنعم والنعمة، والنحل والنحلة، والخبر والخبرة، والعز والعزة، وحرف عاشر وهو الشح والشحة، وهو غريب.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (117,'<span data-type="title" id=toc-75>(٧٥)
باب
ليس في كلام العرب: واحد يوصف بجمع إلا </span>قولهم: ثوب أسمال أي خلق، وإنما جاز ذلك لأنه يعنى به أنه قد تخرق من جوانبه حتى صار جمعًا، وثوب أكباش: غليظ، وبُرمة أكسار، وقدر أعشار، وقميص أخلاق، وأنشد:
جاء الشتاء وقميصي أخلاق ... شراذم يضحك مني التواق
التواق: ابنه، فأما الواحد يؤدي عن الجمع فكثير، مثل قوله تعالى: (ختم الله على قلوبهم وعلى سمعهم)، و (إن أنكر الأصوات لصوت الحمير) وكقوله: (أو الطفل الذين لم يظهروا على عورات النساء) يريد الأطفال، وقال: (والملك على أرجائها)، يريد الملائكة',NULL,'149',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (118,'والأرجاء: النواحي، والواحد رجأ، وقال أبو ذؤيب: فالعين بعدهم كأن حداقتها سُملت بشوك فهي عور تدمع.
فالعين واحد ثم جمع الحداق، وهو كثير في كلام العرب، ووجدت حرفا غريبا: قربة أشنان، مثل ثوب أسمال.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (119,'<span data-type="title" id=toc-76>(٧٦)
باب
ليس في كلام العرب: شيء جمع على فعال إلا </span>نحو عشرة أحرف: عراق جمع عرق، وهو اللحم على العظم، ورُخال جمع رِخل من أولاد الضأن، ورُباب جمع رُبى من الشاء أي نفساء، يقال: شاة رُبى، وبقرة رغوث، وفرس نتوج، وناقة عائذ، وامرأة نفساء، وتؤام جمع توأم، وغلامان توأمان، والجمع توأمون إذا جمعته جمع سلامة، وتؤام في التكسير، وأنشد:',NULL,'151',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (120,'قالت لنا ودمعها تُؤام ... كالدر إذ أسلمه النظام
على الذين ارتحلوا السلام
وفرير وفرار: ولد الظبية، ونذل ونذال، ورَذل ورُذال، وقد قيل: رَذيل ونذيل في الرذل والنذل، وثناء جمع ثني، والثني في الكلام ثلاثة أشياء: أن تؤخذ الصدقة في السنة مرتين، قال النبي صلى الله عليه وسلم: (لا ثني في الصدقة) والثني: أن تلد الشاة في السنة مرتين، والثني: الثاني، قال الشاعر:
ترى ثنانا إذا ما جاء بدوهم ... وبدوهم إن أتانا كان ثنيانا',NULL,'152',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (121,'والثني: أن تلد المرأة بكرها، والثِنْي: الثاني بعد البكر، فقد صاروا أربعة أحرف، والبُساط جمع ناقة بُسط، إذا كانت غزيرة اللبن، وأنشد:
خمسون بُسطًا في خلايا أربعِ',NULL,'153',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (122,'<span data-type="title" id=toc-77>(٧٧)
باب
ليس في كلام العرب: هاء التأنيث إلا </span>قبلها فتحة نحو عشرة، وبقرة، وقائمة، إلا هاء هذه، وقولهم في الحكاية إذا قالوا: رأيت امرأتين، قلت: من تين، فإن قبلها ساكن، وكذلك فعلت كيت وكيت، وقلت: ذيت وذيت، فأما قولهم: حصاة وقطاة، وفتاة،',NULL,'154',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (123,'فإنما جاز الإسكان قبلها لأن الألف قبلها في نية حركة، إنما شد من تان، وهذه.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (124,'<span data-type="title" id=toc-78>(٧٨)
باب
ليس في كلام العرب: أفعل الرجل بمعنى فعل غيره إلا </span>قولهم، أمات زيد: مات ولده، وأجرب الرجل: جربت إبله، وأمرت الناقة: مريتها أنا، وأقوى الرجل: قويت إبله، وأطلب الماء: أحوج إلى الطلب لبعده، وماء مطلب، قال ذو الرمة:
أضله راعيا كلبية صدرا ... عن مطلب وطلى الأعناق تضطرب
لأن جميع كلام العرب أن يقال: فعل الشيء وأفعله غيره، مثل: جلس زيد وأجلسه غيره.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (125,'<span data-type="title" id=toc-79>(٧٩)
باب
ليس في كلام العرب: اسم ولا صفة على فعلى إلا </span>لمؤنث مثل المرطى: الفرس السريعة، والحيدى، والبشكى: السريعة، إلا في حرف واحد، فإنه جاء لمذكر: وهو قوله:
كأني ورحلي إذا زعتها ... على جمزى جازىء بالرمال
فقال: جازئ يصف ثورًا، أو حمارًا، ولم يقل جازئة، وهو يجتزئ بالرطب عن الماء، وما كان',NULL,'157',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (126,'نحو ذلك، وجاء على الثلاثي نحو الخوزلي، والخيزلى، وقرقرى. فإذا ثنيته فالأجود عندي أن تحذف الألف لطول الاسم، فتقول: الحوزلان، والجمزان، ولا تقول: الجمزيان، فإن لم يطل أثبت فقلت: الحبليان والبشريان.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (127,'<span data-type="title" id=toc-80>(٨٠)
باب
ليس في كلام العرب: تثنية تشبه الجمع إلا </span>ثلاثة أسماء وإنما يفرق بينهما بكسرة وضمة، وهن الصنو، والقنو، والرئد- المثل. والتثنية: صنوان، وقنوان، ورئدان، وهذا نادر مليح، والصنو: النخلة تخرج من أصل أخرى، فلذلك قيل: العم صنو الأب، أي أصلهما واحد، قال الكميت:
ولن أعدو العباس صنو نبينا ... وصنوانه ممن أعد وأندب',NULL,'159',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (128,'وقال الله تعالى: (صنوان وغير صنوان) وصنيان، وقنيان، وقُنوان، وقِنوان. والرئد- المثل، هذه رئد هذه وتربها، وأنشد:
وقد ودعوها وهي ذات مؤصد ... مجوب ولما تلبس الإتب رئدها
الإتب: الصدرة، وهو الصدار أيضًا، فأما الريد بفتح الراء فحيد الجبل، قيل لأعرابي: ما حروف الجبل؟ قال ريوده. قيل: وما ريوده! قال: حرفته، جمع',NULL,'160',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (129,'حرف الجبل حرفة، وجمع الحرف من غيره حروف، ومثله أن أعرابيًا سأله رجل فقال: ما المتآزف؟ قال المتكأكئ، قال: فما المتكأكئ؟ قال: الحنزقرة، قال: أنت أحمق.
قال ابن خالويه عفا الله عنه: وفيه من العربية أن النون تخفى عند الواو ولا تظهر، وقد ظهرت في صنوان وقنوان، فيه جوابان، قال أهل البصرة: أظهر ولم يدغم لئلا يلتبس فعلال بفعال. وقال أهل الكوفة: ليس سكون النون لازما إذا كان يتحرك في صني إذا صغر، وهو في الجمع أصناء.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (130,'<span data-type="title" id=toc-81>(٨١)
باب
ليس في كلام العرب: مثل حلية وحلى وحُلى، إلا </span>ثلاثة أحرف: لحية ولِحى، ولُحى، وجزية وجزِى وجُزى، فجمع بالكسرة والضم هذه الأحرف الثلاثة وسائر الكلام يجمع على لفظ واحد: فرية وفرى، ومرية ومرى.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (131,'<span data-type="title" id=toc-82>(٨٢)
باب
أجمع أهل النحو على أنه ليس في كلام العرب: لقرية وقرى نظير، لأن ما كان على فعلة من ذوات الواو والياء جمع بالمد كركوة وركاء، وشكورة وشكاء، إلا </span>ثعلبا فإنه زاد حرفًا آخر: نزوة ونزى.
وهذان نادران لا ثالث لهما في كلام العرب. قال الفراء: فأما قولهم: كُوة وكواء، وكَوة وكوى، فعلى لغة من',NULL,'163',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (132,'قال كُوة كما قيل في قوة الحبل قِوى وقُوى، قرأ عبد الرحمن السلمي (شديد القِوى) وسائر الناس: القُوي، وكل طاقة من طاقات الحبل فهي قوة، وقوة الإنسان منه، فلما صرفوا الفعل بنوه على فعل، لينقلب أحد الواوين ياء، ولم يقولوا: قووت ولكن قَويت.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (133,'<span data-type="title" id=toc-83>(٨٣)
باب
ليس في كلام العرب: مفعول على فعل إلا </span>حرفًا واحدًا: رجل جد للعظيم الجد والبخت، وإنما مجدود محظوظ له جد وحظ في الدنيا، وفي دعاء رسول الله صلى الله عليه وسلم: اللهم لا مانع لما أعطيت ولا معطي لما منعت، ولا ينفع ذا الجد منك الجد، أي من كان له حظ في الدنيا لم ينفع ذاك في الآخرة، إنما ينفعه العمل الصالح، فالجد: الرجل المحظوظ، والجد: البئر الجيدة الموضع من الكلإ، والجد: جمع جمل أجد،',NULL,'165',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (134,'وناقة جداء، لا سنام لهما بمعنى واحد، والجد: أبو الأب والأم، والسلطان، والعظمة، قال تعالى: جد ربما، والقطع، مصدر جد الشيء قطعه، والجِد بالكسر: الانكماش في الأمر، وضد الهَزل: خذ في الجد ودع الهزال، والجِد: النطع، والوكف، وشاطئ النهر.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (135,'<span data-type="title" id=toc-84>(٨٤)
باب
ليس في كلام العرب: اسم ولا صفة على أفاعل إلا </span>أربعة أحرف: أحامر: جبل، وأجارد: جبل، وأباتر: قاطع لرحمه، وأدابر مثله. فإذا قالوا: رجل مدبر في نفسه: خسيس، وأبتر: لا ولد له، وأباتر: بتر أقاربه، وحمار أبتر: مقطوع الذنب، وحية أبتر: مقطوع الذنب، وكان العرب يسمون من لا ولد له أبتر، وصنبورا، فقال المنافقون وكفار قريش ذلك',NULL,'167',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (136,'للنبي عليه الصلاة والسلام: إن محمدًا صنور أبتر لا ولد له، فإذا مات انقطع ذكره، فقال الله عز وجل: (إن شانئك هو الأبتر) فأما أنت يا محمد فذكرك مقرون بذكري إلى يوم القيامة، إذا قال المؤذن: لا إله إلا الله، قال: أشهد أن محمدًا رسول الله، فذلك قوله تعالى: (ورفعنا لك ذكرك).',NULL,'168',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (137,'<span data-type="title" id=toc-85>(٨٥)
باب
ليس في كلام العرب: اسم ولا صفة على أفَنْعَل إلا </span>حرفين ألَندد وألنجج، والألندد: الرجل الشديد الخضومة، ويقال: يلندد بالياء، ورجل ألد وألندد، وجمع ألد: لد، قال الله عز وجل: (وتنذر به قومًا لدا)، وقال: (وهو ألد الخصام) وامرأة لداء، قال كثير:
وكوني على الواشين لداء شغبة ... كما أنا للواشي ألد شغوب
وأما الألنجج فالعود الذي يتبخر به، يقال: ألنجَج ويلنجج وألنجُوج، وألية وألوة،',NULL,'169',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (138,'وأُلُوَّةٌ، وعود، ورند، ومندل، ومجمرة، وقطر، قال النبي صلى الله عليه الصلاة والسلام في صفة أهل الجنة: (ومجامرهم الألوة) وكان عليه الصلاة والصلام يتبخر بالألوة مع الكافور، ونظر أعرابي إلى قبر رسول الله صلى الله عليه وسلم بعدما دفن فقال:
ألا دفنتم رسول الله في سفط ... من الألوة أحوى ملبسا ذهبا
وقال امرؤ القيس:
كأن المدام وصوب الغمام ... وريح الخزامى ونشر القطر
يعل به برد أنيابها ... إذا طرب الطائر المستحر',NULL,'170',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (139,'<span data-type="title" id=toc-86>(٨٦)
باب
ليس في كلام العرب: اسك على فعلل إلا </span>حرفا واحدًا: عرتُن: نبات، وذلك أنه لا يجمع أربع متحركات في اسم واحد استثقالا حتى يحجز بين المتحركات بالسكون مثل جعفر وهدهد، لا يقال: جاءني جعفر، وإنما جاز ذلك في عرتن، لأنه محذوف في عرنتن، فاستثقلوا النون الساكنة، وكذلك قولهم: علبط، وعثلط، وعجلط، وهدبد، وعكمس، ودلمص، وقدر خرخر، وأكل الذئب من الشاة الحدلقة، ودودم، وماء زمزم، كل ذلك الأصل فيه فعالل: علابط، وخراخر، فلما سقطت الألف تخفيفا اجتمعت',NULL,'171',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (140,'أربع متحركات.
تفسير هذه الحروف: ناقة علبطة: ضخمة، والعجلط: اللبن الثخين، وكذلك العثلط، والهدبد: الشبكرة في العين، ومن كلام العرب: دواء الهدبد شحمة ضب بكبد، ويقول آخرون: إن العُلُبِط والعُجَلِط والعُثَلِط والهُدَبِد كل ذلك اللبن الغليظ، قال: وتقدم نحوي بغيض كان يتكلم بالإعراب إلى لبان فقال: يا لبان، أعندك لبن عثلط علبط عجلط؟ فقال له اللبان: تنصرف أو تصفع، والعكمس: الإبل الكثيرة، والدُّلَمِص والدُّمَلِص جميعًا: الدرع البراق، والخرخر: القدر الكبيرة، والحدلق: العين، ودودم: شيء يجعله النساء في الطراز، وماء زمزم: بين الملح والعذب،',NULL,'172',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (141,'وصحف أبو الرِّياش عند أبي عمر فقال: ماءٌ زمزمٌ أنا قد شربته، ثم رأيته في بعض النسخ ماءُ زمزم، وماءٌ زمزمٌ.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (142,'<span data-type="title" id=toc-87>(٨٧)
باب
ليس في كلام سيبويه هذه الأبنية</span>، أغفلها: الزٍّيزَم: صوت الجن، والهزنبزان: الرجل السيء الخلق، وشمنصير: اسم أرض، والدرداقس: عظم في الرقبة، وأصرى، من الإصرار على الشيء، وفيه أربع لغات: صِرِّى، وإصرى، وصِرَّى، وأصري ومن ذلك أن رجلا كان يحمق، يقال له أبو السَّمَّال',NULL,'174',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (143,'العدوي، أضل بعيرا له فقال: والله يا رب إن لم تردده علي اليوم لا صليت، فوجده، فقال: علم ربي أنها مني أصرِّي. ومفعل وقد وجد مألك، وغيره أربعة أحرف مضت فيما سلف من الكتاب، والهندلق: بقلة.
ولا تكون صفة على فعل وقد وجد عدى، وزيم: ضيق، وأنشد:
باتت ثلاث ليال ثم واحدة ... بذي المجاز تراعى منزلا زيما
ودين قيم، كل هذا أغفله.
وأوزان ما مضى: زيزم فِيعَل. هزنبزان فعنللان، شمنصير فعنليل، ودرداقس، فعلالل، أصري فعلي،',NULL,'175',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (144,'وصرى فعلي، وأصري فعلى، مألك مفعل، هندلق فنعلل، عدى وقيم فعل، دئل فعل، عشرة أبنة.
وما ذكر تلقامة، وفرناسا وهو الأسد، والمهوأن وهو المتسع من الأرض، ودحيدح يقال لمن أقر بعد جحد، وليث عفرين، وترعاية، والصِنبر،',NULL,'176',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (145,'وخَرانق، وهيدَكُر، وسئل ابن دريد عن تفسيره فقال: لا أعرفه، ولكنى أعرف الهيدكور، وهو الشاب الناعم.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (146,'<span data-type="title" id=toc-88>(٨٨)
باب
ليس في كلام العرب: صفة على فَعَّال جمع على فعل إلا </span>حرفا واحدا، قالوا: ناقة خوارة، والجمع خور، غزار، ورجل خوار، ضعيف، والجمع خور.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (147,'<span data-type="title" id=toc-89>(٨٩)
باب
ليس في كلام العرب: جمع لأفعل وفعلاء صفة إلا </span>على فعل، مثل: أصفر وصفراء إلا في حرف واحد، فإنه جمع على فعل: درع، ليلة درعاء لاسوداد أول الليل، مأخوذ من شاة درعاء إذا اسود رأسها وابيض سائرها، وذلك لأنهم سموا كل ثلث ليال باسم، فقالوا: ثلاث غرر، وثلاث نُفل، وثلاث تُسَع، وثلاث عُشَر، وثلاث بيض، وثلاث دُرَع، وثلاث ظلم، وثلاث حنادِس، وثلاث دَآدِئ، وثلاث مُحَاق.
وفي الحديث: نهى رسول الله صلى الله عليه وسلم عن صوم الدأداء، فسألت ابن مجاهد عنه فقال: هو الشك، قال الشاعر:',NULL,'179',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (148,'تداركه في منصل الأل بعدما ... مضى غير دأداء وقد كاد يعطب
أي لقيه وقد بقي من الشهر الحرام ليلة، ولولا ذلك لقتله، لأنهم كانوا ينزعون أسنتهم فلا يحاربون، والأل: جمع ألَّة وهي الحربة والسنان، والدأدأة بالقصر والمد: العدو مثل الدأداء، ومثل ذلك الفأفأة بالقصر: ثقل اللسان، ورجل فأفاء بالقصر والمد، مثل رجل نأنإ ونأناء بالقصر والمد، وهو الضعيف.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (149,'<span data-type="title" id=toc-90>(٩٠)
باب
ليس في كلام العرب: كلمة على إفعل إلا </span>إشفى الخراز، والجمع الأشافي، وقالوا: عدن إبين، وأبين، ويبين، ثلاث لغات، فأما إمر وإمع ففعل لا إفعل. والأمر: الحدي، ورجل إمر: مبارك مغضور الناصية، والإمع: الفضولي، وزاد سيبويه:',NULL,'181',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (150,'إبرَم موضع.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (151,'<span data-type="title" id=toc-91>(٩١)
باب
ليس في كلام العرب: صفة على فعل جمعت على أفعلة إلا </span>حرفا واحدا: عبد قن، وهو العبد ابن العبدين، ويقال: رجل قِن، وقد يجوز أن يجمع على أقنان، وجمعه جرير على أقنة فقال:
أولاد سوء خلقوا أقنة
كأنه جمع قنا أقنانا ثم جمع أقنانا أقنة.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (152,'<span data-type="title" id=toc-92>(٩٢)
باب
ليس في كلام العرب: جمع جمع ست مرات إلا </span>الجمل، فإنهم جمعوا الجمل أجملا ثم أجمالا ثم جاملا ثم جمالا ثم جمالة ثم جمالات جمع الجمع، لأن أكثر ما يكون الجمع مرتين أو ثلاثا، وهذا ست مرات فهو نادر.
يقول: نَعَم، وأنعام، وأناعيم، قوم، وأقوام، وأقاوم، وأقاويم، لا يجاوزون ذلك.
وليس في كلام العرب اسم: على ألفاظ مختلفة إلا الناقة فإنهم قالوا: ناقة ثم جمعوها: ناقات، ونوقا،',NULL,'184',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (153,'وناقا، وأيانق، ونياقا، وأينقا، وأونقا سبع مرات، وسبعة ألفاظ، لأنهم يمارسون هذين النوعين كثيرا فينطقون بها على ألفاظ مختلفة.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (154,'<span data-type="title" id=toc-93>(٩٣)
باب
ليس في كلام العرب: جمع على لفظ سَوَاسِرة إلا </span>حرفا واحدا: المقاتوة جمع مقتوي، وتفسير ذلك أن العرب تقول: قوم سواء في الخير، وسواسية في الشر، وينشد:
سواسية كأسنان الحمار
هذا مثل وليس بشعر ولا رجز، وفيه ألفاظ:',NULL,'186',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (155,'قوم سواسية، وسواسوة، وسياسية.
ومما لا يكون إلا في الشر التتابع، تتابع القوم في الشر، لا يقال في الخير، قال النبي صلى الله عليه وسلم: ما يحملكم على أن تتايعوا في الكذب كام يتتايع الفراش في النار) التتايع: التهافت والوقوع فيها كما تقع الفراشة في الشمعة.
ومثله باء فلان بخزي وشر، قال الله تعالى: (فباءوا بغضب من الله).
ومثله: صار القوم أحاديث في الشر، لا يكون في غيره، ومثله أوعده بكذا بالألف والهاء لا يقال إلا في المذموم، يقال: وعده خيرًا على الإطلاق، وأوعده',NULL,'187',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (156,'شرًا على الإطلاق، فإذا وصلهما جازا في الخير والشر: وعده خيرا، وأوعده شرا وخيرا، فإذا قال: أوعده بكذا، لا يقال إلا في المذموم وأنشد:
أوعدني بالسجن والأداهم ... رجلي ورجلي شثنة المناسم
هذا الذي كتبته إجماع من البصريين والكوفيين، لا أعلم خلافا فيه، غير أني وجدت في القرآن حرفًا: يعد في الشر على الإطلاق وهو قوله: (فهل وجدتم ما وعد ربكم حقا) هذا قول أهل الجنة لأهل النار.
وأما تفسيره المقاتوة فهو جمع مقتوي، وهو الذي',NULL,'188',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (157,'يخدم الناس بطعام بطنه، قال عمرو بن كلثوم
متى كنا لأمك مقتوينا
رجل مقتوي، ورجال كذلك، وقال آخرون: رجل مُقتوي، والقتو الخدمة، وقد قتا يقتوا قتوا، وأنشد:
إني امرؤ من بني فزارة لا ... أحسن قتو الملوك والخببا
ويقال للذي يعمل بطعام بطنه: العُضْرُوط، واللَّعْمَظ واللُّعْمُوظ، واللُّعْمُوظَة.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (158,'فأما الصَّعفقي فالذي يدخل السوق بلا رأس مال، هؤلاء الصعافقة، وأنشد:
نحن قدرنا والعزيز من قدر ... وآبت الخيل وقضينا الوطر
من الصعافيق وأتباع أخر',NULL,'190',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (159,'<span data-type="title" id=toc-94>(٩٤)
باب
ليس في كلام العرب: ياء التصغير إلا </span>تدخل ثالثة، نحو بُكير وشقير، إلا في حرف واحد، فإنه دخل رابعًا، وهو قولهم: اللُّغيزي، لجحر من جحرة اليربوع، فلذلك قال النحويون ليس مصغرًا.
وأسماء جحرة اليربوع: الدأماء، والدممة، والقاصعاء، والقصعة، والنافقاء، والنفقة، والراهطاء، والرهطة، والسابياء، والحاثياء، والغابياء، واللغيزي، ومن ذلك أخذ اللغز في الكلام، لأنه يعمي كلامه كما يعمى اليربوع على صائده، يحفر جحرا وراء جحر يعميه، واللغيزى إحدى ما جاء عن العرب مصغرا، ولا مكبر له',NULL,'191',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (160,'مسموعا مثل الثريا، وحُميا الكأس، ومسيطر، ومبيقر ومهيمن، والحجيلاء: اسم ماءة، ونحو ذلك.
والتصغير جري في كلام العرب على ثلاثة أوجه:
تصغير التحقير، والتقريب، والمدح، فالتحقير رجيل، والتقريب دوين السماء، والمدح فلان صُديقي، وأنا جُذيلها المحكك، وعذيقها المُرجب، وحجيرها المأوم.',NULL,'192',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (161,'<span data-type="title" id=toc-95>(٩٥)
باب
ليس في كلام العرب: مؤنث غلبه المذكر إلا </span>في ثلاثة أحرف.
في التاريخ: صمت عشرا، يرد على الليالي، لئلا ينقص الشهر يوما، ولا تقل: عشرة، ومعلوم أن الصوم لا يكون إلا بالنهار، وتقول: سرت عشرا بين يوم وليلة.
والثاني_ أنك تقول: الضبع العرجاء المؤنث، والمذكر: ضبعان، فإذا جمعت بين الضبع والضبعان قلت: ضبعان، ولا تقل: ضبعانان، فكرهوا الزيادة.
والثالث- أن النفس مؤنثة، فيقال: ثلاثة أنفسٍ،',NULL,'194',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (162,'على لفظ الرجال: ولا يقولن: ثلاث أنفس إلا ذهبوا إلى لفظ نفس أو معنى نساء، فأما إذا عنيت رجالا قلت: عندي ثلاثة أنفس يفعلون، وينشد:
ثلاثة أنفس وثلاث ذود ... لقد جار الزمان على عيالي
وقال الله عز وجل: (خلقكم من نفس واحدة) رده إلى المعنى لا إلى اللفظ، وإنما عنى بالنفس ها هنا آدم صلى الله عليه وسلم، ولو رده إلى اللفظ لقال: من نفس واحد، فالنفس: الرجل، والنفس: الروح، والنفس: ما يكون به التمييز، والنفس: الدّم، والنفس: الماء،',NULL,'195',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (163,'والنفسُ: الأخ، قال الله عز وجل: (ولا تقتلوا أنفسكم) أي إخوانكم، والنفس: قدر دبغة: أعطني نفسا أو نفسين أدبغ بها منيئتي فإني أفدَةٌ، أي عجلة، والنفس: بمعنى عند، قال الله تعالى حكاية عن عيسى عليه السلام: (وتعلم ما في نفسي ولا أعلم ما في نفسك) أي تعلم ما عندي ولا أعلم ما عندك.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (164,'<span data-type="title" id=toc-96>(٩٦)
باب
ليس في كلام العرب: ما قيل في مذكره إلا </span>بالضم، نحو: العقربان: ذكر العقارب، والثعلبان: ذكر الثعالب، والأفعوان: ذكر الأفاعي إلا في حرف واحد،',NULL,'197',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (165,'قالوا: الضبعان: ذكر الضباع، ولم يقل أحد لم ذلك، وذلك أن الضبعان مشبه بالسرحان، وهو الذئب، والذئب، أيضا: ذكر الضباع، ويقال لوالدها منه: الفرعل، وصغر تصغيره، وجمع جمعه، فقالوا: ضبيعين، كما قالوا: سريحين، وقالوا: ضباعين، كما قالوا: سراحين، فلما كانا جميعا ذكري الضبع وفق بين لفظيهما.',NULL,'198',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (166,'<span data-type="title" id=toc-97>(٩٧)
باب
ليس في كلام العرب: ما زيد فيه حرف من جنس، لامه من غير الملحق إلا </span>السؤدد، زادوا فيه دالا وإنما هو من السيادة سيد بين السؤدد.
وقولهم: ناقة حولل وعُوطَط، زادوا طاء',NULL,'199',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (167,'ولاما، وإنما هي من اعتاطت الناقة رحمها أعواما، لم تحمل، فهي أقوى لها، وكذلك حائل، إذا لم تحمل وهذا يكون في النخل، والنوق جميعًا. أما قولهم: مَهْدَد في مهد، ورماد رمدد فإنما أحلقت بناء ببناء
ويقال للرماد: أرمداء على أنه مصدر، ورواه ثعلب أرمداء بالفتح على أنه جمع.',NULL,'200',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (168,'<span data-type="title" id=toc-98>(٩٨)
باب
ليس في كلام العرب: فعل يصغر إلا </span>فعل التعجب، تقول: ما أحسن زيدا، وما أملح بشرا، ثم تقول: ما أحيسن زيدا، وما أميلح بشرا، وإنما جاز ذلك لأنه لا يتصرف تصرف الأفعال فأشبه الاسم، قال الشاعر:
ياما أميلح غزلانا شدن لنا ... من هؤلياء بين البان والسمر',NULL,'201',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (169,'وكل فعل دخله معنى لا ينصرف، فليس أحد يعمل اسم الفاعل إذا صغره إلا الكسائي وحده أجاز: هذا ضويرب زيدا، وأباه سائر الناس، لأنه لما صغره صحت له الأسمية.
وحجة الكسائي أنهم أعملوا فعل التعجب مصغرا كما أعملوه مكبرا فأجمعوا على إعماله قبل التصغير، هذا ضارب زيدا، كما تقول: هذا يضرب زيدًا.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (170,'<span data-type="title" id=toc-99>(٩٩)
باب
ليس في كلام العرب: اسم على فُعْلُول إلا </span>قولهم: فُرعُون، لغة في فرعون حكاه الفراء، وهذا نادر لأن أصله تفرعن الرجل: صار خبيثا، وهم الفراعنة مثل الرهادنة جمع رهدن، وهو الرجل الأحمق، والعصفور الصغير، والرهدل مثل الرهدن، العرب تقلب اللام نونا، والنون لاما لقربهما من الفم واللسان، يقال: سكر طبرزن وطبرزل وطبرزد، ثلاث لغات، فمن قال بالذال فإنما هي فارسية معربة، أي ضرب جوانبه بالفأس، لأن الفأس بالفارسية طبر، وقوله: زذ، أي اضرب، وكذلك طبرستان كان لا يوصل إليه لكثرة.',NULL,'203',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (171,'شجره وأشبه فقال: استان أي خذ الفاس واضرب من جوانبه، فسمي طبرستان، ويقال: جبريل وجبرين، وإسرائيل وإسرائين، وأنشد:
يقول أهل السوق لما جينا ... هذا وروب البيت إسرائينا',NULL,'204',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (172,'<span data-type="title" id=toc-100>(١٠٠)
باب
ليس أحد يقول يستعور يفتعول إلا </span>ابن دريد، لأنه عند النحويين ليس ذلك في كلام العرب، وإنما هو عندهم فعللول مثل عضوفوط: ذكر العظاء، ويستعور، تفسيره البلد البعيد، وأنشد:
فطاروا في البلاد اليستعور',NULL,'205',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (173,'وقيل: اليستعور: الكساء، وقيل: اسم أرض بعينها بالمدينة، قال ابن دريد: وعيد شوق: دويبة.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (174,'<span data-type="title" id=toc-101>(١٠١)
باب
ليس أحد من أهل اللغة والنحو عرف تفسير عِزويت</span>، وهو في كتاب سيبويه، ما عرفه الجَرمي ولا المبرد، فسمعت أبا بكر بن الخياط يقول: سألت أبا العباس ثعلبا عن عزويت فقال: يروى بالعين عزويت، وهو القصير، وقال الطبري محمد بن رستم: قال لنا المازني: هو بالغين.
وكذلك اسم دويبة يقال لها عريقصان اختلفوا',NULL,'207',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (175,'فيه، فقال قوم: إنما هو عرنقصان، وقال آخرون: عرقصان.
قال ولا يعرف صفة على مفعل إلا منكبا، وهو عون العريف، ومنكب الإنسان معروف، وأربع ريشات من الطائر مناكب، فالمناكب: النواحي، قال الله تعالى (فامشوا في مناكبها وكلوا من رزقه) وذكر ابن مجاهد أن رجلا قال لجاريته وهي تقرأ: إن عرفت ما تفسير قوله تعالى (فامشوا في مناكبها) فأنت حرة، قالت: المناكب: الجبال، فسأل جماعة فاختلفوا، فقال أحد أصحاب رسول الله صلى الله عليه وسلم: دع ما لا تعرف إلى ما تعرف، فأعتقها، وقد قيل: الجبال، وقيل النواحي.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (176,'<span data-type="title" id=toc-102>(١٠٢)
باب
ليس في كلام العرب: إناء مفعوم إلا </span>في بيت واحد لكثير إنما يقال: إناء مفعم، قال كثير:
أتي ومفعوم حثيث كأنه ... غروب السواني أترعتها النواضح
وهو مفعم، قال الفرزدق:',NULL,'209',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (177,'تصرم عني ود بكر بن وائل ... وما خلت عني ودهم يتصرم
قوارص تأتيني ويحتقرونها ... وقد يملأ الشعف الإناء فيفعم
الشعف؛ جمع شعفة، وهي القطرة من الماء، ومن أمثالهم: ما تغني الشعفة في الوادي الرغب، ويقال: ملأت الإناء فأفعمته، وأترعته، وزندته، وزكته، وحصرمته، وحضجرته، وأدهقته، وأرهقته، قال الله تعالى: (وكأسا دهاقا) وأتأقته، ويا غلام اتئق العتاد واملأ الكوز.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (178,'<span data-type="title" id=toc-103>(١٠٣)
باب
ليس في كلام العرب: مثل الإرزب: القصير، إلا </span>إطمر: الثوب الخلق، وهو الطمر أيضا، والطمر بالفتح: الوثب، طمر الفرس على الحجر، وطامر ابن طامر: من لا يعرف، ولا يعرف أبوه، ومثله صلمعة ابن قلمعة، وهي بن بي، وهيان بن بيان، والبرغوث طامر لطموره، ومثله الضلال بن ثُهلُل وثُهلَل،',NULL,'211',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (179,'فأما الرجل النبيه العالي الذكر فابن إحداها كما تقول: واحد الناس، ونسيج وحده، وإنه لأحد الأحدين، والأحدين وإنه لشرود الذكر، وإنه لنبيه بين النباهة، وقال أبو نخيلة لمسلمة:
أمسلم يابن خير كل خليفة ... ويا سائس الدنيا ويا قمر الأرض
شكرتك إن الشكر صنف من التقى ... وما كل من أوليته حسنا يقضي
فألقيت لما أن أتيتك زائرا ... علي رداء سابغ الطول والعرض
ونوهت لي ذكري وما كان خاملا ... ولكن بعض الذكر أنبه من بعض',NULL,'212',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (180,'<span data-type="title" id=toc-104>(١٠٤)
باب
ليس في كلام العرب: مذكر جمع بالألف والتاء إلا </span>حرفا واحدا، وهو قولهم: رجل خلفنة، وامراة، وقالوا: نساء خلفنات، ورجال خلفنات، وهذا غريب نادر.',NULL,'213',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (181,'وفيه خلاف، لأن أصل هذا الباب أن يقال: نساء مسلمات، ورجال مسلمون، ورجال صالحون، ونساء صالحات.
ومما جعل فيه المذكر على لفظ المؤنث قول الشاعر:
وعنترة الفلحاء جاء ملأ ما ... كأنك فند من عماية أسود
الفند: القطعة من الجبل، وبه سمي الفند الزماني، فقال: الفلحاء، ولم يقل: الأفلح، لأن تأويله وعنترة صاحب الشفة الفلحاء كما قال بعض العرب: أتاكم العيناء، أي صاحب العين الكبيرة.
وليس في كلام العرب: جمع على فعلنات غير هذا.',NULL,'214',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (182,'<span data-type="title" id=toc-105>(١٠٥)
باب
ليس في كلام العرب: ضمة بعد كسرة إلا </span>في حرفين: إصبُع وزِئبُر، وقد ذكرت الآن حرفا ثالثا في كتاب سيويه: الجنذوة: شعبة من الجبل، قال: جنذوة، وقيل: جنذَوة، وقيل: جنذُوة، الجرمي ضمه وحعله فعلوة من جذوت، وشبيه به صفة على فِعللولٍ؛ قرطبون، والمبرد فتحه وقال: ما عرفت تفسيره أحد.',NULL,'215',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (183,'<span data-type="title" id=toc-106>(١٠٦)
باب
ليس في كلام العرب: حرف حذف وعوض منه إلا </span>حرفا واحدا وهو قول الفرزدق أو غيره:
هما نفثا في في من فمويهما ... على النابح العاوي أشد رجام
جمع بين الميم والواو، وإنما الأصل الواو، هذا',NULL,'216',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (184,'فُو زيد، فأبدل من الواو ميما لما أفرد، فقال: فما، لأنه لا يكون اسم على حرفين، الثاني حرف لين، لأن التنوين يسقطه، فبعد أن أبدلوا الميم من الواو وجب أن يقول: فمان، فقال: فموان.
وقال بعض العرب: رأيت فمويه، والصواب حذف الواو إذا جئت بالميم، ألا ترى أن العجاج لما أمن التنوين في القافية لم يبدل، فقال:
خالط من سلمى خياشيم وفا
ولم يقل: فاها، تقول: هذا فوك، ورأيت فاك، وأخرجته من فيك، والأصل في فم فوه، فأسقط الهاء تخفيفا، فبقي فو، فأبدلوا منه الميم، والدليل على ذلك قولهم في الجمع: أفواه، وفي التصغير فويه.',NULL,'217',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (185,'<span data-type="title" id=toc-107>(١٠٧)
باب
ليس في كلام العرب: (مَنْ) وقعت على اثنين إلا </span>في بيت الفرزدق، وهو قوله يخاطب ئبًا:
تعال فإن عاهدتني لا تخونني ... نكن مثل من يا ذئب يصطحبان',NULL,'218',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (186,'<span data-type="title" id=toc-108>(١٠٨)
باب
ليس في كلام العرب ولا في شيء من العربية: ما رجع من معناه إلى لفظه إلا </span>في حرف واحد استخرجه ابن مجاهد من القرآن، وهو قوله تعالى: (ومن يؤمن بالله) فوحد يؤمن وذكره، على لفظ (من) وكذلك (يدخله جنات) ثم قال: (خالدين فيها أبدا) فجمع خالدين على معنى من ثم قال: (قد أحسن الله له رزقا) فرجع بعد الجمع إلى التوحيد، ومن المذكر إلى المؤنث، ومن لفظه إلى معناه، ولا يرجع من معناه إلى لفظه إجماعا من النحويين، وكان ابن الخياط',NULL,'219',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (187,'يتعجب من ذكاء ابن مجاهد كيف استخرج هذا الخرف بفطنته وحدة أصغريه، قال الله عز وجل: (ومن يقنت منكن لله ورسوله) فذكر على لفظ (من) وهو يريد نساء النبي صلى الله عليه وسلم، ثم قال: (وتعمل صالحا) فأنث، ولو قال تقنت ويعمل صالحا لم يجز، وقال: (بلى من أسلم وجهه لله وهو محسن) فوحد وذكر على لفظ (من) ثم قال: (ولا خوف عليهم) فجمع ورجع من لفظ (من) إلى معناه، ولا يجوز بلى من أسلموا، ثم يقول: (وهو محسن) وهذا دقيق حسن.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (188,'<span data-type="title" id=toc-109>(١٠٩)
باب
ليس في كلام العرب: رباعي بني على الكسر مثل حذام وقطام في الثلاثي إلا </span>أربعة أحرف:
قالت ريح الصبا قرقار',NULL,'221',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (189,'وجرجار: صوت الرعد، والقابة: صوت الرعد أيضًا؛ ما سمعنا العام قابة، وهمهام: وهو أن تسأل إنسانا أن يعطيك شيئا، فيقول: همهام، أي ما بقي شيء، وهيهاتِ هيهاتِ؛ أي بعيد بعيد في لغة من كسر هيهات.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (190,'<span data-type="title" id=toc-110>(١١٠)
باب
ليس في كلام العرب: اسم على فعول إلا </span>أربعة أسماء: عروس لغة في العروس، والعروس: الرجل والمرأة جميعا، مأخوذ من قولهم: عرس الصبي بأمه، إذا انضم إليها ولزمها، وأُتُي: تهر/ وجزور: لغة في الجزور، وسُدُوسٌ: طَيلَسان، فأمان سَدُوس بالفتح فقبيلة، وينشد:
فإن تمنع سدوس درهميها ... فإن الريح طيبة قبول
وهذه الأربعة الأحرف شذت، لأن فعولا لا يكون',NULL,'223',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (191,'إلا على ضربين؛ إما مصدرا مثل: دخل دخولا، وجلس جلوسا، أو جمعا مثل: قوم جلوس، وقوم قعود، على أن أبا عمرو بنا لعلاء حكى: على وجهه القبول، والولوع، والسحور، والفطور.',NULL,'224',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (192,'<span data-type="title" id=toc-111>(١١١)
باب
ليس في كلام العرب: صفة على فاعل، والفعل منه أفعل واستفعل إلا </span>قولهم: استودقت الأتان، وأوْدَقت فهي وادِقٌ، إذا اشتهت الفحل، ولم يقولواك مُودِقٌ، ولا مستودِق، كما يقال: صرفت الكلبة فهي صارِف، واستجعلت الذئبة، والكلبة أيضا، وضبعت الناقة، وحنت النعجة، كل ذلك إذا أرادت الفحل.',NULL,'225',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (193,'<span data-type="title" id=toc-112>(١١٢)
باب
ليس في كلام العرب: مفعول على لفظ فاعل من أفعل إلا </span>حرفا واحدا، قول العرب: أسمت الماشية في المرعى فهي سائمة، ولم يقولوا: مُسامة، وهذا نادر، قال الله تعالى: (فيه يسيمون) من أسام يسم.
قال ابن خالويه: وأحسبهم أرادوا: أسمتُها أنا فسامت هي، فهي سائمة، كما يقال: أدخلته الدار فهو داخل، قال الله تعالى: (والله أنبتكم من الأرض نباتا)',NULL,'226',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (194,'ولم يقل إنباتا، والمعنى: والله أنبتكم فنبتم أنتم نباتا، ولم يجئ ثلاثي يصير مصدره رباعيا إلا قول امرئ القيس:
ورُضتُ فذلَّتْ صَعْبةٌ أيَّ إِذلالِ
ولم يقل: أي ذُل، والمصدر أذَل إذلالا، قالوا: والحجة في ذلك أنه لما قال: رُضتها، أي أذللتها كما تُراض الدابة إنما هو إذلالُها.
وقد يجئ المصدر على غير المصدر، عذبته عذابا، والوجه تعذيبا، وأعطيته عطاء، والوجه إعطاء، وأقرضته إقراضا وهو الوجه وقرضا، وفي الحرف ابن مسعود: (وأُنزِلتِ الملائكة إنزالا) ولم تنزيلا.',NULL,'227',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (195,'<span data-type="title" id=toc-113>(١١٣)
باب
ليس في كلام العرب: اسم رباعي مثل درهم إلا </span>إذا صغر كسر ما بعد ياء التصغير كما يكسر بعد الف الجمع، فيقال: دريهم، كما يقال: دراهم لأن الجمع والتصغير من واد واحد، إلا في حرف واحد فإنهم فتحوا ما بعد ياء التصغير، وهذا غريب.
قالوا في مثل: أخذه بأبدح دبيدح، أي بالظلم، وأراهم زواجوا بين اللفظين، فإذا ولي الحرف الذي بعد ياء التصغير حرفا مؤنثا هاء أو ألفا فتح فيقال: حبيلى وحميدى، لأن الألف لا يكون ما قبلها إلا مفتوحا فشبهت الهاء بالألف.',NULL,'228',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (196,'<span data-type="title" id=toc-114>(١١٤)
باب
ليس في كلام العرب: مثل نسيج وحده إلا </span>أربعة أحرف: حرفان للمدح، وحرفان للذم، فنسيج وحده، ورجيل وحده مدحل، وفلان عُيير وحده، وجحيش وحده ذم، ومر كأنه تصغير عير: وهو الحمار، وتصغير جحش.
وسائر كلام العرب مفتوح، جاء زيد وحده، مصدر، وجاءني القوم وحدهم، كأنه بمعنى منفردين،',NULL,'229',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (197,'وتوحد وحده، مصدر، وواحد لا يثنى ولا يجمع إلا الكميت فإنه قال:
كحي واحدينا
وقال آخر في التثنية:
فلما التقينا واحدين علوته ... بذي الكف إني للكماة ضروب',NULL,'230',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (198,'ويقال جلس فلان على وحده، وجلس وحده، وجلسا على وحدهما، فقد صار الآن خمسة أحرف بالخفض، ولم يُسمع تثنية وحده إلا في بيت لِعُمارة:
ناجى الضمير به وحديـ ... ـنِ أبرز ضحكه بالمحض',NULL,'231',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (199,'<span data-type="title" id=toc-115>(١١٥)
باب
ليس في كلام العرب: نسوة بمعنى النسيان إلا </span>في كتاب اللغات: نسيت الشيء أنساه نسيانا، ونسيا ونساوة، ونسوة.
قال: وكتبت امرأة إلى زوجها: فوالله ما أدري أصرمت، أو مللت، أم نسيت؟ فكتب لها:
فلست بصرام ولا ذي ملالة ... ولا نسوة للعهد يا أم جعفر
فأما جمع المرأة فزعم ثعلب أن النسوة عدد قليل، والنساء عدد كثير، فلذلك قال الله تعالى: (وقال نسوة',NULL,'232',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (200,'في المدينة) فذَكَّر، ولم يقل: قالت، لأن المذكر قبل المؤنث، والقليل قبل الكثير، وقال في الكثير: (فلا تحل لك النساء من بعد) كذلك يقرؤه أبو عمرو.',NULL,'233',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (201,'<span data-type="title" id=toc-116>(١١٦)
باب
ليس في كلام العرب: كلمة على فِعَّوْل إلا </span>أحرفا، سألت نفطويه عن الحِنَّوْت فلم يعرفه، فسألت أبا عمر فقال: الحِنَّوت: الخسيس، ولم يجئ في كلام العرب اسم على هذا إلا خِنَّوتٌ وقِنَّور، وهو العبد ابن العبد، مثل القن، وسِنَّوت وهو العسل، وقيل الكَمُّون، والخنَّوص ولد الخنزير، وسنور: الرسَّيِّد، والهر، وعظم حلق الفرس، وخنور: وهي الدنيا،',NULL,'234',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (202,'والضبع، والنعمة، ومصر، واست الكلبة، وعلوز: الأنين، وعلوش: ابن آوى، وهو العلوص، وهو أيضا داء في الجوف، فلزمت أبا عمر إلى أ، خرجت من بغداد، وقد ذكرته أبين من هذا فيما يجئ، القلوب: الذئب، والجلوز: ضرب من الفاكهة شبه المشمش، والهلوف: الطويل اللحية، والعجول: واحد العجاجيل، الإبول: واحد الأبابيل.',NULL,'235',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (203,'<span data-type="title" id=toc-117>(١١٧)
باب
ليس في كلام العرب: فعُول جمع على فُعُولٍ إلا </span>ثلاثة أحرف ليس بين الجمع والواحد إلا فتحة وضمة، إذا فتحت فهو واحد، وإذا ضممت كان جمعا، والأحرف الثلاثة: عذوب، وهو الجائع، بات فلان عذوبا مثل بات وحشا وجائعا، وقوم عذوب، وينشد:
بنتنا عذوبا وبات البق يأكلنا ... نشوي القراح كأن لا حي بالوادي',NULL,'236',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (204,'إني لمثلكم في سوء فعلكم ... إن جئتكم أبدًا إلا معي زادي
ومعنى نشوي القراح أي نسخن الماء، لأن الماء البارد يقتل على الجوع.
والحرف الثاني: زبور وزبور، وقرئ (ولقد كتبتا في الزَّبُور من بعد الذكر) والزُّبُور.
والحرف الثالث: تخوم الأرض، والجمع تخوم، وأنشد:
أبني التخوم لا تظلموها ... إن ظلم التخوم ذو عقال',NULL,'237',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (205,'هذا قول قومٍ، وقال آخرون: من قال تَخُومُ، جمعه: تخمًا، مثل: رسول ورسل، ومن قال: التُّخُوم بالضم فجمع، والواحد تَخْم.
ولم نجد فعولا جمع على خمسة ألفاظ إلا عمودا، فإنهم جمعوه على عمدٍ، وعُمْدٍ، وأعمدةٍ، وعماد، وقد قرئ: (في عمد ممددة)، وعُمُد وَعُمْدٍ.',NULL,'238',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (206,'<span data-type="title" id=toc-118>(١١٨)
باب
ليس في كلام العرب: بَعْدُ بمعنى قبل إلا </span>حرفا واحدا في القرآن، قال الله تعالى: (ولقد كتبنا في الزبور من بعد الذكر أن الأرض) والزبور ههنا القرآن، فالمعنى: ولقد كبتنا في الزبور من قبل الذكر، والأرض ها هنا: الجنة، ولا يدخلا إلا الصالحون.
فأما أرض الدنيا فيرثها الصالحون والطالحون، والأرض في غير هذا أشياء قد فسرت، منها: حافر الدابة، وينشد:',NULL,'239',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (207,'ولم يقَلِّب أرضها البيطار ... ولا لحبليه بها حبار
أي أثر.
وليس في كلام العرب: ثم إلا مهملة، وشيء بعد شيء، لقيت زيدا ثم عمرًا، وقد جاء ثم بمعنى قبل، وهذا غريب، قال الله تعالى: (ولقد خلقناكم ثم صورناكم ثم قلنا للملائكة اسجدوا لآدم) وقد قال الله تعالى للملائكة: اسجدوا قبل أن يخلقنا، فلذلك تأوله بعضهم قال: معنى خلقناكم، أي خلقنا آباءكم، كما',NULL,'240',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (208,'قال لليهود الذين بين ظهراني رسول الله صلى الله عليه وسلم في زمانه: (فلم تقتلون أنبياء الله) معناه فلم قتل آباؤكم الأنبياء.',NULL,'241',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (209,'<span data-type="title" id=toc-119>(١١٩)
باب
ليس في كلام العرب: اسم مما لا يعْمَل به على مِفْعَلٍ إلا </span>حرفا واحدا: إنما هو (فاذكروا الله عند المشعر الحرام) وشعائر الله: علاماته ومناسكه، واحدها شعيرة، وقد أشعرت البدنة، إذا جعلت لها علامة إما بشيء يعلق عليها أو توجأ في سنامها، وقالت امرأة للحسن: قد أشعرت ابني، أي صيرته علامة للناس.',NULL,'242',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (210,'<span data-type="title" id=toc-120>(١٢٠)
باب
ليس في كلام العرب: فِعِّلٌ إلا </span>حرفين حمص، وجِلق: موضع، وقد زادوا حرفا ثالثا: رجل حِلز، وحلزة للبخيل، مثل قولهم رجل حصرم، بخيل، تقول العرب: حصرم: بخِل، وحضرم: لحن،',NULL,'243',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (211,'وخضرم: خلط، ومنه المخضرم، الذي أدرك الجاهلية والإسلام.
وأهل الكوفة على حمَّص، وجلَِّق، وأهل البصرة على حمِّص وجِلِّق.',NULL,'244',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (212,'<span data-type="title" id=toc-121>(١٢١)
باب
ليس في كلام العرب: اسم على فعل مما لامه سين إلا </span>خمسة: الدمقس: الحرير، والمدقس: القز، والعبقس: الداهية، والحلفس: الضخم السمين الثقيل الروح، والدرفس: الجمل الغليط، وناقة درفسة، والدرفس أيضا: الراية، رأيت الجيس تحت الدرفس، فلو جعلت هذه السينات قوافي للشعر ما جاءت إلا هجوا لا مدحا.',NULL,'245',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (213,'<span data-type="title" id=toc-122>(١٢٢)
باب
ليس في كلام العرب: إذا عظموا الشيء وكبروه إلا </span>بالضم على فعالي: رجل رُوأسي: عظيم الرأس، وأذاني وأنافي، وأيادي، وامرأة حراحرية، وفخاذي، إلا حرفا واحدا، فإنهم قالوا: رجل عضادي بالكسر',NULL,'246',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (214,'وأما لحياني فمن أجل الياء، وقالوا: رقباني، ففتحوا.',NULL,'247',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (215,'<span data-type="title" id=toc-123>(١٢٣)
باب
ليس في كلام العرب: إفعلاء إلا </span>حرفين: إرمداء للرماد، وقال ثعلب: أرمداء بالفتح على أنه جمع، والأول مصدر، وإربعاء لغة في الأربعاء، والإربعاء: عمود من أعمدة الخباء، مثل البوان، والجمع: بون، مثل خوان وخون، وأنشد:
لم يبق هذا الدهر من آيائه ... غير أثافيه وإرمدائه',NULL,'248',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (216,'آيائه جمع آية، والآية: العلامة، قل له بآية كذا، أي بعلامة كذا، قال:
ألا من مبلغ عني تميما ... بآية ما يحبون الطعاما
والآية: الجماعة، خرج القوم بآيتهم: أي بجماعتهم.',NULL,'249',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (217,'<span data-type="title" id=toc-124>(١٢٤)
باب
ليس في كلام العرب: فعول بالضم إلا </span>حرفين: سبوح قدوس، ويفتحان سبوح قدوس، وحرف ثالث ذُرُّوح، وذَروح، وذرنوح، وذراح، وذرَّحرح، وذرَحرح، كل ذلك قد حكي، وينشد:',NULL,'250',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (218,'قالت له وريا إذا تنحنح ... يا ليته يسقى على الذرحرح
الوري: داء في الجوف، قال النبي صلى الله عليه وسلم: لأن يمتلئ جوف أحدكم قيحا حتى يريه خير له من أن يمتلئ شعرا. وقد رواه الداء يريه، قال عبد بني الحَسْحَاس:
وراهن ربي مثلما قد ورينني ... وأحمى على أكبادهن المكاويا
فلو كنت وردا لونه لعشقني ... ولكن ربي شانني بسواديا
وسائر كلام العرب فعول بفتح الفاء: كَلُّوب، وسمور، وشبوط، وسفود، وسَحور.',NULL,'251',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (219,'<span data-type="title" id=toc-125>(١٢٥)
باب
ليس في كلام العرب: فعيل إلا </span>حرفين: مريق، وهو أعجمي في الأصل، وكوكب درئ، وقال الفراء: إنه منسوب إلى الدر، فقد صح ما قال سيبويه: إنه ليس في الكلام فعِّيل، وقد قرئت هذه الآية على وجوه (كأنها كوكب دُرِّي) ودري بغير همز، قرأ به نصر بن عاصم، وليس من السبعة من قرأ به، ودِرئ، ودَدِّئ: مضئ ثاقب من الدرء يدفع الظل، وقد قرئ به، وجاء عنهم.',NULL,'252',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (220,'<span data-type="title" id=toc-126>(١٢٦)
باب
ليس في كلام العرب: فَعَلُولٌ إلا </span>نحو من بضعة عشر: سلعوس: بلد، وبرهوت: وادي جهنم، وطرسوس، وقربوس السرج، وثقفور النصارى، وبلصوص: طائر، وأسود حلكوك، وبعكوك، يقال: وقعوا في بعكوك، أي اختلاط وغبار، وقاع قرقوس: واسع وعربون، وزرجون، وكلمون، وعسطوس: شجر.',NULL,'253',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (221,'<span data-type="title" id=toc-127>(١٢٧)
باب
ليس في كلام العرب: فعلاء صفة إلا </span>ثأداء للأمة، ودأثاء، وقد يجئ في الأسماء جنفاء، وقرماء موضع، وينشد:
على قرماء عالية شواه ... كأن بياض غرته خمار',NULL,'254',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (222,'يصف فرسا قد نفق على هذه العقبة شائلا قوائمة، وغرته في جبهته، فلذلك قال: عالية شواه.',NULL,'255',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (223,'<span data-type="title" id=toc-128>(١٢٨)
باب
ليس في كلام العرب: صفة على فعلى إنما تكون على فُعلى مثل: حبلى إلا </span>في حرف واحد، قوله تعالى: (تلك إذا قسمة ضيزى) قال أهل النحو: أصله فعلى فكسروا الضاد لئلا ينقلب الياء واوا كما قيل:',NULL,'256',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (224,'أبيض وبيض، وعيناء وعين، وفيها لغة ثانية: ضئري بالهمز، ضازني حقي وضأزني، ومثل هذا: (طوبى لهم وحسن مآب) إنما هو من الطيب، فانقلبت الياء واوا لانضمام ما قبلها، فلذلك قرأها مكسورة الأعرابي (طيبى لهم) بكسر الطاء، ويقال: الغلام الأكيس، والمرأة الكيسى، ومن قال: طوبى، قال: الكوسى، وقال ابن دريد، طوبى أصله الواو، ويقال للراجع من السفر: أوبة وطوبة. وهذا غلط، إنما أزوجوا طوبة بأوبة، والحجة للياء قولهم:
طاب يطيب، ولو كان من الواو لقالوا: يطوب مثل يقول:
وليس مما جاء على فعلة إلا التِّولة، وهو السحر،',NULL,'257',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (225,'وسبي طيبة، ومحمد خيرة الله من خلقه، وإياك والطيرة، والشيرة: لغة في الشجر، فأما في الجمع فكثير مثل: ثِورة، وكوزة.
وليس في كلام العرب: جيم قلبت ياء إلا في حرفٍ واحد، وإنما تقلب الياء جيما، يقال في علي: علج، وفي إيل: إجل، وينشد
يارب إن كانت قبلت حجتج
فلا يزال بازل يأتيك بج
أقمر نهات ينزي وفرتج
والحرف الثاني قلب فيه الجيم ياء: الشيرة يريدون',NULL,'258',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (226,'الشجرة، فلما قلبوا الجيم ياء كسروا أولها لئلا ينقلب الياء ألفا، فتصير شاذة، وهذا حسن فاعرفه.
وقال الشاعر، ووقف تحت شجرات لا ورق عليها ولا ثمر
إذا لم يكن فيكن ظل ولا جنا ... فأبعدكن الله من شيرات',NULL,'259',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (227,'<span data-type="title" id=toc-129>(١٢٩)
باب
ليس في كلام العرب: اسم على إفعال إلا </span>إسحار: شجر، وكل ما في كلام العرب إفعال فهو مصدر مثل: أكرم إكراما، وأنفق إنفاقا، إلا إسكاف، وهو كل صانع عند العرب، وإسنام: شجر، وإشنان لغة في الأشنان، وكل ما كان في الكلام أفعال فهو جمع مثل: أجمال، وأجبال، وأحبال، وأغنام.',NULL,'260',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (228,'وقد وجدت في القرآن ثمانية أحرف تكون جميعا ومصدرا
الحرف الأول في آل عمران: (بالعَشي والإبكار) ذكره الأخفش.
والحرف الثاني في الأنعام: (فالق الإصباح) والأصباح، قرأ بالفتح الحسن.
والحرف الثالث في براءة: (إنهم لا إيمان لهم) ولا أيمان لهم، قرأها ابن عامر.
والحرف الرابع في هود: (فعلي إجرامي) ذكره الفراء.
والحرف الخامس في سورة محمد صلى الله عليه وسلم: (والله يعلم إسرارهم) وأسرارهم، قرأها حمزة والكسائي.
والحرف السادس في ق: (وإدبار السجود) وأدبار السجود، قرأها أبو عمرو.
والحرف السابع في والطور: (وإدبار النجوم) وأدبار النجوم، قرأها الأعمش.',NULL,'261',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (229,'والحرف الثامن: (اتخذوا إيمانهم جنة) وأيمانهم، ذكره الزجاج في كتابه.
هذه الحروف إذا كسرت فهي مصادر، وإذا فتحت فهي جمع.',NULL,'262',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (230,'<span data-type="title" id=toc-130>(١٣٠)
باب
ليس في كلام العرب: اسم على إفعلان إلا </span>أربعة أحرف: إسحمان: جبل، وإمدان: ماء، وقالوا: مِدان: كدر، وإربيان: سمك صغار، ونبات أيضا، وليلة إضحيان: مضيئة.
وليس في كلام العرب: أفعلان إلا حرفين: عجين',NULL,'263',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (231,'أنبجان: مسترخ، ويوم أرونان: شديد في الحرب والحر والبلاء، يقال: يوم أرونانُ، وأروناني، وأرونانِ، ثلاث لغات، وقال النابغة الجعدي:
جلبنا الخيل من تثليث حتى ... أتين على أوارة والعدان
يعارضهن أخضر ذو ظلال ... على حافاته فلق الونان
فظل لنسوة النعمان منا ... على سفوان يوم أرونان
فأعتقنا حليلته وجئنا ... بما قد كان دمع من هجان',NULL,'264',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (232,'كانوا أسروا امرأة النعمان ثم منوا عليها.
ليس في كلام العرب: كلمة على أفعلى إلا أجفلى، يقال: دعا الجفلى والأجفلى؛ إذا عم، ودعاهم النقري: إذا خص، وينشد:
نحن في المشتاة ندعو الجفلى ... لا ترى الآدب فينا ينتقر
الآدب: الداعي، أدب يأدب فهو آدب، وأدب يأدب فهو أديب.
فأما أجلى: اسم موضع، فوزنه فعلى لا أفعلى، الهمزة فاء الفعل، وأول من دعا النقري زياد بن أبيه، ويقال: دعا الغورى مثل الجفلى.',NULL,'265',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (233,'<span data-type="title" id=toc-131>(١٣١)
باب
ليس في كلام العرب: فعلاء من ذوات الواو تكلموا به بالياء إلا </span>قولهم: العلياء، وإنما هو العلواء مثل العشواء.
وليس في كلام العرب واو صحت رابعة إلا قولهم: المذروان وكان الواجب أن يقولوا: المذريان، لأن الواحد مذرى، ولكن لما لم ينطق بواحده صححوا الواو كما قالوا: عقلته بثنايين، أي بحبلين فلم يهمزوا، أنه لم يفرد له واحد، فلو أفرد فقيل: ثناء',NULL,'266',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (234,'لوجب أن يقولوا في الثنية: ثناءين، والمذروان ثلاثة أشياء: طرفا القوس، فودا الرأس، وطرفا الأليتين، وينشد:
أحولي تنفض استك مذرويها ... لتقتلني فها أنا ذا عمارا
متي ما تلقني فردين ترجف ... روانف أليتيك وتستطارا
روانف بالراء، ويقال للمذروين: الرانفتان، والصومعتان، والصوفقتان، وقد تصح الواو بعد الألف مثل الغباوة.',NULL,'267',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (235,'<span data-type="title" id=toc-132>(١٣٢)
باب
ليس في كلام العرب: جمع وواحجد بلفظ واحد. وحركة أوله في الجمع مثل حركته في الواحد إلا </span>الفلك يكون واحدا وجمعا، ومذكرا ومؤنثا بمعنى واحد، وكذلك: المنون والطاغوت، وقال الله تعالى: (في الفلك المشحون) فذكر، وقال الله تعالى: (حتى إذا كنتم في الفلك وجرين بهم بريح طيبة) وقال الله تعالى: (ألم تر أن الفلك تجري في البحر) فأنث، فزعم',NULL,'268',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (236,'سيبويه أن الفلك الواحد ويجمع على أفلاك، كما أن أسدا يجمع على آساد، ثم جمعوا أسدا على أسد، فوجب أن يجمع فلك على فلك.
وهذا شبيه بالسحر إذا تأمله الإنسان، ويحسن ما يفطن له، وقال أهل الكوفة: الفلك يكون واحدا وجمعا بلا علة، ومثله الهجان والدلاص، يكون واحدا وجمعا.',NULL,'269',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (237,'<span data-type="title" id=toc-133>(١٣٣)
باب
ليس في كلام العرب: أفعل إلا </span>ومؤنثه فعلاء إلا في أحرف قالوا: امرأة حسناء، ولم يقولوا: رجل أحسن، وقالوا: فرس شوهاء للرائعة، ولم يقولوا للمذكر: أشوه، وقالوا: ديمة هطلاء، ولم يقولوا: سحاب أهطل، وقالوا: شجرة مرداء: لا ورق لها، ولم يقولوا: عصن أمرد، وقالوا: غلام أمرد، ولم يقولوا: امرأة مرداء، ومرطاء، إذا لم يكن على ركبها شعر، ويقال:',NULL,'270',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (238,'امرأة عجزاء، ولا يقال: رجل أعجز، كما قالوا رجل آلى، ولم يقولوا امرأة ألياء.',NULL,'271',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (239,'<span data-type="title" id=toc-134>(١٣٤)
باب
ليس في كلام العرب: اسم على فعلان إلا </span>غمدان السيف، وجربانه، وخضمان: موضع، ورجل عمدان، وقمدان: طويل، وفركان: أرض، وعرقان: جبل: ودويبة أيضا.
وليس على اسم فعويل إلا سمويل: طائر وعزويت:',NULL,'272',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (240,'موضع، وغسويل: نبات، وأنشد:
لم يعدوا ريشة من ريش سمويلا
ورجل ممراق: دخان في الأمور، ومهزاق:
طياش، وممزاق: أهوج، وناقة مسهاف مهياف.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (241,'سريعة العطش، وناقة مشياط: سريعة السمن، ونخلة مبسار: لا ترطب وامرأة ميقاب: ضد الرصوف الضيقة الحر، والمدمالق، والرهوي مثل الميقاب، ورجل دعنكار: متدرئ على الناس بالخصومة، ورجل حبيبق أي أحمق، ورجل صَميان، وصِميان: ينصمي على الناس بالأذى.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (242,'<span data-type="title" id=toc-135>(١٣٥)
باب
ليس في الصفات مفعالة إلا </span>حرفا واحدا قالوا: رجل معزابة، إذا طالت عزبته، وإنما هي مفعالة من عزب عنه إذا بعد، وتقول: رجل عزب، وامرأة عزبة وإن شئت عزب بغير هاء وينشد:
هل عزب أدله على عزب ... على فتاة مثل تمثال الذهب
وقيل في قوله تعالى: (إنه من يتق ويصبر) قال: من يتق الزنا ويصبر على العزوبة فإن الله لا يضيع أجر المحسنين.
وقد قيل: رجل مجذامة مطرابة: أي يطرب',NULL,'275',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (243,'ويقطع، والأكثر مفعل ومفعال بغير هاء، امرأة معطير: كثيرة التعطر، ويقال: امرأة معطير ومعطار وعطرة.',NULL,'276',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (244,'<span data-type="title" id=toc-136>(١٣٦)
باب
ليس في كلام العرب: مصدر على فعفعيل، وإن شئت فعلليل إلا </span>قولهم: سمعت غطمطيط الماء والبحر، وقرقرير الطائر، ومرمر مرمريرا.
فأما سائر ما جاء على هذا فإنه اسم أو صفة لا مصدر، وذلك قولك: عجوز شفشليق، وشمشليق، وعفشليل، وجعفليق، كل ذلك إذا كانت مسترخية، وماء خمجرير، وماء خمطرير، كثير، وكمرة فتجليس فنطليس: عظيمة.',NULL,'277',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (245,'<span data-type="title" id=toc-137>(١٣٧)
باب
ليس في كلام العرب: اسم على تفعال بكسر التاء، ولا صفة إلا </span>نحو تسعة عشر حرفا وهي: تبراك: موضع، وتعشار: جبل، ورجل تكلام، ورجل تلقام: عظيم اللقم، وتلفاق: ثوبان يخاط أحدهما بالآخر، وهو اللفاق، وتجفاف الدابة معروف، والتمثال: معروف، ومضى تهواء من الليل، ورجل تمساح، كذاب، وناقة تضراب: قريبة العهد بقرع الفحل، وتمراد: برج الحمام، وتنبال: قصير، وتقصار: قلادة أو مخنقة، وتلعاب: كثير اللعب، فأما تلقاء وتبيان فمصدران في',NULL,'278',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (246,'القرآن، وجاء لتيفاق الهلال وميفاقه ولتوفاقه، كل ذلك بمعنى واحد.',NULL,'279',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (247,'<span data-type="title" id=toc-138>(١٣٨)
باب
ليس في كلام العرب: فعوال إلا </span>هذه التي أذكرها، قولهم: مضى سعواء من الليل مثل تهواء، ولساعات الليل مئة وخمسة وثلاثون اسما قد أفردنا لها كتابا، نحو هزيع من الليل، وطبيق من الليل، وبنك من الليل، وطبق من الليل، وناشئة، وجلواح: واد واسع، وصرواح: حصن بنته الجن لسليمان، وناقة قرواح: طويلة القوائم، وكذلك النخلة، وهلواع: شهمة الفؤاد، ورجل شرواط: طويل، وقرواش: اسم رجل أو قبيلة، ووقع في عصواد: أي في شر وبلاء.',NULL,'280',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (248,'<span data-type="title" id=toc-139>(١٣٩)
باب
ليس في كلام العرب فَعِّيلَة إلا </span>سكينة لغة في السكينة والوقار، قال الفراء: سمعت بعضهم يقرأ: (سكينة من ربكم) وقال أهل التفسير: كانت السكينة لها وجه كوجه الإنسان، ثم هي بعد ريح هفافة.
وكذلك فعيل ليس في كلامهم إلا شيء روي عن نصر بن عاصم أنه قرأ (كأنها كوكب درئ) فأما',NULL,'281',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (249,'فِعيل بالكسر فكثير نحو سكيت، وفسيق، ومن غرائب فعيل: رجل عبيث من العبث، وعميت: لا يهتدي لوجهته، وقليبـ: الذئب، وشعير: الأحمق، ويقال أيضا للذئب: القلوب.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (250,'<span data-type="title" id=toc-140>(١٤٠)
باب
ليس في كلام العرب: اسم على فعولل إلا </span>صلودح، وحلوبق: اسم، وحزوكل، وعكوكل: قصير، وحبوكر: داهية، وسلوطح، موضع، وحدولق: قصير، وبحر غطومط: كثير الماء.',NULL,'283',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (251,'<span data-type="title" id=toc-141>(١٤١)
باب
قال الخليل ليس في كلام العرب: شين بعد اللام إلا </span>العلوش، ورجل زبعبق: سيء الخلق، وليس أحد فسر لنا الزعبل: ملاح معز الدولة إلا الزاهد فقال: هو الذي يعظم بطنه من أسفل، ويدق أعلاه،',NULL,'284',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (252,'ويكبر رأسه، ويدق عنقه، فيصير شهرة، وصبي زعبل: كادئ الشباب، سيء الغذاء.',NULL,'285',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (253,'<span data-type="title" id=toc-142>(١٤٢)
باب
ليس في كلام العرب- على قول ابن دريد: فوعل إلا </span>غيث جور: كثير، وزور: لرئيس القوم وسيدهم، فلان زور قومه، وقال أبو عمرو: يقال لرئيس العسكر: زوير، وأهل النحو يزعمون أن زورا وجورا فعل لا فوعل.',NULL,'286',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (254,'<span data-type="title" id=toc-143>(١٤٣)
باب
ليس في كلام العرب: فعل إلا </span>جمع مثل قوم ركع وسجد في جمع راكع وساجد، وقد جاء للواحد قليل: صفر: موضع، وزرق، وتمر، وحمر، ودخل، وقبر، وزمج، كل ذلك طائر، وزمج: ضعيف، وغبر الحيض والمرض: البقية منه، والكرج فارسية معربة، والحلب: نبت، والخلب: برق لا مطر فيه، وزمت: ضعيف، ولحم دخل: متراكب متداخل غليظ، ورجل حول قلب: محتال، قال معاوية',NULL,'287',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (255,'لابنته في مرضه: هيا بنية، إنك لتقلبين حولا قلبا إن نجا من عذاب الله، والزرق: طائر، والقنب: لغة في القِنب، والجُمل: قَلس السفينة.
وليس من هذا لفظة تكون واحدا وجمعا بلفظ واحد إلا سخل: رجل سخل، ورجال سخل: وهم الضعفاء، ودمل، وسلح: نبات، والقمل، جراد صغار.',NULL,'288',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (256,'<span data-type="title" id=toc-144>(١٤٤)
باب
ليس في كلام العرب: اسم ولا صفة على فعل إلا </span>قليل، فلذلك لم يصرف الاسم إذا جاء على فعل لأنه يشبه الفعل قطع، وكلم، والذي جاء منه: عثر، وبذر موضعان، وينشد:
سقى الله أمواها عرفت مكانها ... جرابا وملكوما وبذر والغمرا
وقال زهير:
ليث بعثر يصطاد الرجال إذا ما الليث كذب عن أقرانه صدقا',NULL,'289',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (257,'وخَضَّم: قبيلة، وبقَّم: صبغ، وشلم: اسم بيت المقدس، وله سبعة عشر اسما قد ذكرتها بعد، وشمر، وخود: اسمان لفرسين، قال ذو الرمة في خود أنه اسم موضع:
يركلن ريط اليمين المعضدا ... أو أعين العين بأعلى خودا',NULL,'290',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (258,'<span data-type="title" id=toc-145>(١٤٥)
باب
ليس أحد جعل الطريم السحاب إلا </span>رؤبة، فإنه قال:
في مكفهر الطريم الشرنبث
وسائر الناس قال: الطريم: العسل، والطريم: الرجل الطويل.
ومن غريب فعيل: غريد: ناعم، وضريم: صمغ، وعليب: واد، قال أبو عمرو: إنما هو علبب بباءين،',NULL,'291',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (259,'وعصيد: لقب حصن بن حُذيفة، وهميغ: الموت الوحى بالغين معجمة عند الناس كلهم إلا الخليل فإنه يقول: هميع بالعين غير معجمة، والغطير: القصير.',NULL,'292',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (260,'<span data-type="title" id=toc-146>(١٤٦)
باب
ليس في كلام العرب: فَعْيَلٌ إلا </span>حرفين، ضَهْيدٌ: الرجل الصلب، وصَهْيَدٌ: موضع. وإنما يجيء فَيْعَلٌ الياء قبل العين مثل صَيْقَلٍ، وصَيْرَفٍ.
ومن غريب هذا الباب الفَيْخَر والقيجر: الجردان العظيم، والسيطلة: الطست، وريح نيرج: عاصف، وزيمر وجيفر اسمان، زيمر اسم فرس، وجيفر اسم رجل، روى عن ابن عقدة، وامرأة هينغ: ملا عبة،',NULL,'293',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (261,'وصيدح: ناقة ذي الرمة، وبيهس: الأسد، والضيطر: الضخم، وصيدان: الثعلب، والصيدان: الملك، وصيدان: دويبة تجمع عيدانا، وشبه الصيدلاني به في جمعه العقاقير، ويقال للصيداني: الصيدلاني، والصيدان: الثعلب، لم يجئ إلا في شعر كثير، قال الأصمعي: ليس بشئ، وهيرع: جبان، وجيهل: خشبة يحرك الرجل بها الجمر، والغيهقة: التبختر؛ ويقال: غيهق الرجل، وتبختر، وتبهنس، وماس يميس، وراس يريس، ويؤذف، وتمطى، وتخطرف،',NULL,'294',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (262,'ومشى القدامية؛ كل ذلك إذا تبختر في مشيته، وطيسع: واسع، وهو الحريص أيضا، والخيزب: اللحم الرخص.',NULL,'295',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (263,'<span data-type="title" id=toc-147>(١٤٧)
باب
ليس في الظروف شيء إلا </span>معرب نصبا، كقولك: سرت شهرا، وصمت يوما، وسهرت ليلة، وكذلك ضحوة، وبكرة، وعشية، ودهرا، وسنة، وساعة، كل ذلك منصوب بوقوع الفعل فيه إلا حرفين فإنهما بنيا؛ وهما: أمس، مبني على الكسر تقول: ركبت أمس، وصمت أمس، لأن أمس يقع قبل كل يوم أنت فيه لا يخص يوما بعينه، فصار مبهما فزال',NULL,'296',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (264,'الإعراب عنه، والتقى في آخره ساكنان الميم والسين فكسر لالتقاء الساكنين.
وقال آخرون: إنما بني أمس على الكسر لأن العرب لا تكاد تنطق به إلا مع الباء: كان فلان بالأمس، وفعل فلان بالأمس كذا، قال الله تعالى (وأصبح الذين تمنوا مكانه بالأمس) فلما نزعوا الباء تركوه على نية الباء، فإن أضفت أمس أو أدخلته الألف واللم أعرب وزال البناء، فتقول: ركبت الأمس الأحداث، وليس أمسك مثل أمسي، وقد تركه بعضهم مع الألف واللام مبنيا فقال:
وإني وقفت اليوم والأمس قبله ... ببابك حتى كادت الشمس تغرب
والحرف الآخر: الآن، تقول: قمت الآن، فهو مبني على الفتح مع الألف واللام، قال الله تعالى: (آلآن وقد عصيت قبل وكنت من المفسدين)',NULL,'297',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (265,'وقال الفراء: الأصل في آن أوان، وهو مأخوذ من قولهم: آن لك أن تفعل، فهو فعل ماض فدخلته الألف واللم فترك على بنائه.
وقال أهل البصرة: فتح الآن لالتقاء الساكنين، لأنه وجب فيه البناء، وفيه الألف واللام لأنهما عين الإشارة، فمعنى الآن فعلت قالوا: (الآن جئت بالحق) أي هذا الذي جئت الآن، فبني لذلك.',NULL,'298',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (266,'<span data-type="title" id=toc-148>(١٤٨)
باب
ليس في كلام العرب: ما بني وفيه الألف واللام إلا </span>الأمس والآن، وقد فسرتهما في الباب الذي قبله، والخاق باق: صوت الحوز عند العراك، والخاش ماش: قماش البيت، والحاث باث: التفرق، وتركت القوم حوثا بوثا: أي مشتتين، والخازباز،',NULL,'299',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (267,'ويقال: الخازُبَاز، والخِزبَاز، والخَازِباز، والخازباء بالمد، ويفسر أنه نبات، وأنه الذباب، أو صوته ويفسر أنه ورم في اللهزمة.
وجميع الظروف منصوب أو مخفوض، تقول: جئت قبلك، ومن قبلك، وكنت عندك، وخرجت من عندك، ومن بعدك، فإذا أفرد بني على الضم، كقوله تعالى: (لله الأمر من قبل ومن بعد) لأن الفتحة والكسرة كانتا فيه مادام مضافا، فلما أفرد وصار غاية بني على الضم الذي لا يكون فيه ليعلم أنه بناء لا إعراب.',NULL,'300',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (268,'ومثله قولك: لم يضرب جزما، ثم تقول: لم يضرب الرجل، فتكسر لالتقاء الساكنين، لأن الفعل يدخله الضم والفتح، ولا يدخله الكسر، فلما أرادوا حركته حرك بالحركة التي لا تكون في الفعل ليعلم أنه حركة بناء لا حركة إعراب.',NULL,'301',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (269,'<span data-type="title" id=toc-149>(١٤٩)
باب
ليس في كلام العرب: اسم ممدود وجمعه ممدود إلا </span>داء وأدواء، وآءة وآءٌ مثل عاعة وعاع: شجر، وإنما صلح أن يكون الجمع والواحد ممدودين، لأن الأصل في الواحد القصر على وزن حمل، فاء الفعل همزة، ولامه همزة، وعينه واو، فلما انقلبت الواو ألفا لتحركها وانفتاح ما قبلها، وكانت الهمزة بعد الألف المنقلبة مكنت بالهمز للمدة، فهذا مد حرفٍ لحرف.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (270,'وكل ألف أتت بعدها همزة أو حرف مشدد فلا بد من مده، مثال ذلك: دابة، وشابة، وكساء، ورداء.
وكذلك الداء أصله دوء، فانقلبت الواو ألفا، والعلة واحدة، فاء شجر، وراء: شجر، والآء، والألاء: شجر، واحدهما: آءة وألاءة.',NULL,'303',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (271,'<span data-type="title" id=toc-150>(١٥٠)
باب
ليس في كلام العرب: فعل فعلا إلا </span>خنقه خنقا، وضرط ضرطا، وحلف حلفا، وحبق حبقا، وسرق سرقا، ورضع رضعا، وهو ستة أحرف.
وليس أحد يقول: سرأت المرأة: ولدت أولادا كثيرة، إلا في كتاب الهمز، لأن سرأت هي من مازن الجراد، أي بيضها، يقال: سرأت الجرادة، إذاغرزت، وكل جرادة تسرأ تسعا وتسعين سرءا فيقال',NULL,'304',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (272,'ذلك للمرأة استعارة إذا كثر ولدها
ومثله: بقت المرأة، وأبقت، ومثله: ضنت وضنأت، وأضنت وأضنأت، كل ذلك إذا كثر ولدها.
ويقال: امرأة منتاق: كثيرة الأولاد، وقال النبي صلى الله عليه وسلم: عليكم بتزوج الأبكار، فإنهن أعذب أفواها، وأنتق أرحاما، وأرضى باليسير. فإن كانت قليلة الأولاد فهي نزور، وإن لم يعش لها ولد فهي رقوب، ومقلات وللجراد ستون اسما، قد بينته فيما بعد.',NULL,'305',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (273,'<span data-type="title" id=toc-151>(١٥١)
ليس يجيء فعل وفعيل إلا </span>قليل، قالوا: كلب وكليب، وضأن وضئين، ومعز ومعيز، وعبد وعبيد، وقد جمعوا عبدا على: أعبد، وعِبدان، وعُبدان، وعباد، ومعبوداء، وعِبدى، مقصور، وعبداء ممدود، وعبد كل ذلك قد جاء عنهم.
وحدثنا أحمد بن عبدان المقرئ قال: حدثنا علي ابن عبد العزيز المكي قال: قرأت بخط أبي عبيد على ظهر دفتر له.',NULL,'306',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (274,'وإني وإن سيق إلي المهر ... ألف وعبدان وذود عشر
أحب أصهاري إلى القبر',NULL,'307',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (275,'<span data-type="title" id=toc-152>(١٥٢)
باب
ليس في كلام العرب: مصدر على تفعال بكسر التاء إلا </span>ثلاثة أحرف: تلقاء وتبيان وتلفاق، وسائر ذلك يجئ بالفتح: التقضاء، والتمشاء، والترماء، والترداد.
ومعنى التلفاق: هو اللفاق، لفقت الشيء، إذا لأمته مثل الثوبين يخاطان وبلاءم بينهما، لفقت لفقا وتلفاقا، وتلافق القوم، إذا تلاءمت أمورهم.',NULL,'308',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (276,'<span data-type="title" id=toc-153>(١٥٣)
باب
ليس في كلام العرب: أفعل فهو فعول إلا </span>أربعة أحرف: أخفدت الناقة فهي خفود: أسقطت، مثل أخدجت، وأشصت فهي شصوص: قبل لبنها، وأنتجت فهي نتوج، وأعقت الفرس فهي عقوق، وقد مر هذا الباب قبل هذا، وإنما أعدته لزيادة خفود.',NULL,'309',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (277,'<span data-type="title" id=toc-154>(١٥٤)
باب
ليس في كلام العرب: مثل بِدل وبَدل إلا </span>شِبه وشَيه، ومِثل ومَثل، ونِكل ونَكل: الفارس البطل.',NULL,'310',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (278,'<span data-type="title" id=toc-155>(١٥٥)
باب
ليس في كلام العرب: مثل قولهم: شغل شاغل إلا </span>ويل وائل، وموت مائت. قرأ عيسى بن عمر: (إنك مائت وإنهم مائتون) ورجل ميت في الحال، ومائت بعد قليل، ومريض في الحال، ومارض بعد قليل، وغضبن في الحال، وغاضب عن قليل، وظريف في الحال، وظارف بعد قليل
ومثله شعر شاعر، وشيب شائب، وذأل ذائل، وهو الخزي والهوان، وصدق صادق، وجهد جاهد،',NULL,'311',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (279,'ووتد واتد، وأنشد:
لا قت على الماء جذيلا واتدا
وقال آخر:
يخضبن بالحناء شيبا شائبا ... يقلن كنا مرة شبائبا
وقال امرؤ القيس:
حلت لي الخمر وكنت امرأً ... عن شربها في شغل شاغل',NULL,'312',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (280,'<span data-type="title" id=toc-156>(١٥٦)
باب
ليس في كلام العرب: فعلة إلا </span>مفعول، ولا فعلة إلا فاعل، وذلك قولهم: رجل سبة: يسب، وسببة: يسب، إلا في حرف واحد، رجل نومة بالإسكان إذا كان خامل الذكر، ويكون عبدا صالحا، قال أمير المؤمنين علي عليه السلام: خير ذلك الزمان كل نومة، أولئك مصابيح الدجى ليسوا بالمساييح المذاييع البذر).',NULL,'313',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (281,'<span data-type="title" id=toc-157>(١٥٧)
باب
ليس في كلام العرب: ضدان بلفظ واحد على فعل غير مدغم إلا </span>حرفا واحدا، ماء ربب، أي كثير، وماء زنن، أي قليل، فلم يدغم، وهذا مليح
ومن الغرائب على هذا الوزن: في عظمه رقق، أي رقة، والعزز: الضيق، والشصص: اليبس، والعشش: القصر، والشنن: اضعف، والقضض: الحصا الصغار، والضنن: الشجاع، والفكاك: انكسار الفك، والفهه: العي، والمهه: الحسن والطلاوة،',NULL,'315',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (282,'والرمم: الحبال، والرجج: الاضطراب، والمجج: استرخاء الفكين، والحتت: داء يصيب الشجر، ويقال: الختت بخاء معجمة.',NULL,'316',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (283,'<span data-type="title" id=toc-158>(١٥٨)
باب
ليس في كلام العرب: فاعل بمعنى مفعول إلا </span>قولهم: تراب ساف، وإنما هو مسفي لأن الريح سفته، والريح سافية، والتراب مسفي، والرياح هي السوافي، والسافياء: التراب أيضا والرياح ومثله: (عيشة راضية) بمعنى مرضية، و (ماء دافق) بمعنى مدفوق، وسر كاتم بمعنى مكتوب، وليل نائم بمعنى ناموا فيه، وأنشد:',NULL,'317',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (284,'فنام ليلي وتجلى همي ... وقد تُجَلَّى كُرَبُ المُهْتَمِّ
نِعْمَ عَميد القوم وابن العم
وقد يجيء مفعول بمعنى فاعل، قال الله تعالى: (حجابا مستورا) أي ساترا، وهذه كلها مجاز محتمل في الكلام، قال تعالى: (بل مكر الليل والنهار) يريد بل مكركم في الليل والنهار، وقوله تعالى: (فما ربحت تجارتهم) والتجارة لا تربح وإنما يربح فيها، وقال تعالى: (فإذا عزم الأمر) تأويله فإذا عزمتم أنتم على الأمر، ومثله: (واشتعل الرأس شيبا) وإنما هو واشتعل الشيب في الرأس، ومثله أدخلت القلنسوة رأسي، وإنما هو أدخلت رأسي في القلنسوة.',NULL,'318',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (285,'<span data-type="title" id=toc-159>(١٥٩)
باب
ليس في كلام العرب: ما يُمد إذا خُفف، ويشدد إذا قصر، إلا </span>الشاصلى والشاصلاء: نبات، والحوصلاء مخفف ثم قالوا: حوصل وحوصلة وليس المشدد من هذا الحرف مقصورا، والقُبيطى، والقبيطاء، والمرعزى والمرعزاء، والباقلى والباقلاء، واللغيزى',NULL,'319',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (286,'واللغيزاء، هما بيتا اليربوع، والكمثرى والكمثراء، والمصطكى والمصطكاء، والخليطى والخليطاء.',NULL,'320',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (287,'<span data-type="title" id=toc-160>(١٦٠)
باب
ليس في كلام العرب: اسم على فُعْلُمَة إلا </span>حرفا واحداً، وهو جُلْهُمَة: اسم بطن من العرب، الميم زائدة، من الجلهة، وهو شاطئ النهر، يقال لشاطئي النهر: هما جلهتا الوادي، وجلهتماه، وحافتاه، وسيفاه، وضيفاه، وضفتاه، وحداه، وملطاطاه، وجيزاه، وعُدوتاه، وعِدوتاه، وشطاه، وشاطئاه.
وصفاة جيهل وجيحل، إذا كانت عظيمة، والمجهل: الخشبة التي يحرك بها الجمر، واستجهلت الريح الغصن: حركته، والمجهلة: الأمر أو الخصلة التي تحمل المرء على الجهل، وفي الحديث: الولد مبخلة مجبنة مجهلة.',NULL,'321',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (288,'<span data-type="title" id=toc-161>(١٦١)
باب
لم نجد في كلام العرب ياء متحركة قبلها فتحة صحت إلا </span>قولهم: ما بالدار عين، أي أحد، ورجل أعين: بيِّن العين.
ومال حير: أي كثير، قال ابن الأعرابي: حَيرٌ بكسر الحاء، ولا يقولون: عيب، إنما يقبلون قيقولون: عيب وعاب، لأن عابا الأصل فيه عيب، فلما تحرك قلب، هذا في الأسماء.
وفي الأفعال كمثل، تقول: كال، باع، ولا',NULL,'322',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (289,'تقول: كيل، بيع، وهو الأصل، وكانت امرأة ترقص ولدها وتقول:
يا رب من سره أن يكبرا ... فسق له يا رب مالا حيرا
ويروى: حيرا، بكسر الحاء.',NULL,'323',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (290,'<span data-type="title" id=toc-162>(١٦٢)
باب
ليس أحد من العلماء يقول لزئبر الثوب زُؤْبُر، وزَوبر إلا </span>ابن الأعرابي، ولغة غريبة: زِئبر بكسر الزاي وضم الباء، لأن كسرة بعدها ضمة لا توجد إلا في زئبُر، وإصبُع لغة في إصبَع، فأما الزبر، والزبور: فالشديد، وأنشد:
أكون ثم أسدا زبورا',NULL,'324',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (291,'<span data-type="title" id=toc-163>(١٦٣)
باب
ليس في كلام العرب: فعل من المعتل معدول من فاعل إلا </span>في حرف واحد، وهو طوى، فيما لم ينون، معدول عن طاو، قال الله عز وجل: (طوى، اذهب إلى فرعون إنه طغى) ويقرأ: طوى، كأنه قد بورك فيه مرتين، طوى مثل الثٍّنى والُّنى، وجاء في الحديث: لا ثنى في الصدقة، أي لا يؤخذ خراجان في عام واحد.',NULL,'325',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (292,'وقرأ عيسى بن عمر (طاو اذهب) فطاو وطوى مثل عامر وعمر، وقائم وقثم، لأن فعل في كلام العرب على ثلاثة أوجه: إن كان معدولا عن فاعل لم ينصرف في المعرفة، وانصرف في النكرة، فتقول: مررت بعمر وعمر آخر، يستدل على عدله وتعريفه، لأنه يحسن أن تقول: العُمر.
والثاني- أن يكون فعل اسما واحدا غير معدول مثل صُرد، ولُغز، وجرذ، والجمع: جرذان، ولغزان، وصردان، وهذا ينصرف في كل حال.
والثالث- أن يكون فعل جمعا لفعلة، مثل: زمر، وغُرف، وقبل، جمع قبلة، وزمرة، وغرفة.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (293,'<span data-type="title" id=toc-164>(١٦٤)
باب
ليس في كلام العرب: يضرب بضم الراء إلا </span>في موضع واحد، وهو باب المغالبة: ضربني زيد',NULL,'327',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (294,'فضربته، وما أحببت أن أضرُبه، وجالسني فجلسته، وما أحببت أن أجلُسه، وهذا باب مليح فاعرفه، وفي الحديث: (حاج آدم موسى فحَجَّهُ) فإن كان فيه حرف حلق جاز رفعه وفتحه، مثل قولك: وما أردت أن افخره، وإذا كان معتلا بالياء فليكسر: فاضاني فقضيته، وما أحببت أن أقضيه، ولا تقل: أقضوه، لئلا ينقلب الياء واوا، وأنشد:
ولا نموت على مضاجعنا ... بالليل بل أدواؤنا القتل
ندع الدنية أن تلم بنا ... ونشد حين تعاور النبل',NULL,'328',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (295,'<span data-type="title" id=toc-165>(١٦٥)
باب
مستقصي من غرائب الجمع
ليس في كلام العرب: مثل مُهاة ومهى إلا </span>ثلاثة أحرف، والمهاة: ماء الفحل في رحم الناقة، وطُلاة، وطُلى، وهي الأعناق، وحكاة وحكى، وهي دويبة، والنينان: جمع النون، وهو السمك، والمعاز بفتح الميم جمع معز، ولو كان معازا بالكسر لم يكن غريبا، والنسار جمع النسر، والكفار جمع كافر، والإبار جمع إبرة، والصقار جمع صقر، وكلابات جمع كلب مثل بيوتات العرب، وأعيذك بأسماوات الله، وبلغ أشده جمع',NULL,'329',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (296,'شدة، مثل أنعم جمع نعمة، وفحالة جمع فحل، وسلقان جمع سلق وهي الصحراء، ومتيوساء، ومعبوداء، ومعيوراء، ومشيوخاء جمع تيس، وعبد وعير، وشيخ، ونياق جمع ناقة، وحقق جمع حقة من الإبل: التي استحقت أن يحمل عليها، والرسلاء والأرل جمع الرسول، وذب جمع ذباب، وأقرية جمع قري: مجاري الماء إلى الرياض، ويجمع الفلك فلكا، والهجان هجانا، وهذا من مخبآت سيبويه، والهجان: كرام الناس، وخيار الإبل وبيضها، والهجين: الذي أحد أبويه غير عربي، وهو أن تكون أمه غير عربية، والمقرف الذي أبوه غير عربي، ومكان وأمكن، مثل زمان وأزمن، وضرس وأضرس، وجمل وأجمل، ودوام جمع الداماء: جحر من جحرة اليربوع، وأواطب جمع أوطاب اللبن جمع الجموع؛ وبون جمع بوان: عمود الخيمة، وقوم ودداء بالإظهار ولا نظير له، وقوم سِقام جمع سقيم مثل كرام جمع كريم.',NULL,'330',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (297,'<span data-type="title" id=toc-166>(١٦٦)
باب
ليس في كلام العرب: فاعل تجمع إلا </span>على ما جمعته لك في هذا الباب: فاعل وفاعلون: كاتب وكاتبون، وفاعل وفُعَّال، كاتب وكتاب، وفاعل وفعلة: كاتب وكتبة، وفاعل وفعل: كاتب وكتَّب، وفاعل وفعل: صاحب وصحب، وفاعل وأفعال: صاحب وأصحاب، وفاعل وفعلان: صاحب وصحبان، وفاعل وفعالة: صاحب وصحابة، وفاعل وفعل: ناقة جائل، والجمع حول، وفاعل وفعلل؛ نحو: ناقة حائل وحولل، وعوطط: تعتاط رحمها سنين لا تحمل، وفاعل وفعل: ناقة عائط ونوق عيط، بكسر أوله لئلا ينقلب الواو ياء، وفاعل وفعل: غائب وغيب، وفاعل وفعيل: عازب وعزيب،',NULL,'331',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (298,'وفاعل وفواعل: حاجب وحواجب، وفاعل وفواعيل: خاتم وخواتيم، وفاعل وفعول: جالس وجلوس، وفاعل وأفاعل وأفاعيل باطل وأباطل وأباطيل، ويكون أباطيل جمع أُبطُولة، وفاعل وفعلاء: شاعر وشعراء، فأما عالم وعلماء فإنك تجعل علماء جمعا لعليم، وفاعل وأفعلة: واد وأودية، وفاعل وفعلة: قاض وقضاة، والأصل قضية، فانقلبت الياء ألفا لانفتاح ما قبلها، وفاعل وفعلى: فاسد وفسدى، ورائب وروبى: خُثَّرُ الأنفس، وروبى: حمقى، وهالك وهلكى، وفاعل وفعل: شارف وشرف للناقة.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (299,'<span data-type="title" id=toc-167>(١٦٧)
باب
استقصاء التثنية
ليس في كلام العرب: أنواع التثنية إلا </span>ما ذكرت، وما أعلم أحدًا جمعه ولا فرعه نحو مائة وجه، فأول ذلك أن كل اسم إذا أردنا تثنيته- معرفة كان أو نكرة، مذكرا كان أو مؤنثا، عربيا أو أعجميا، جمادا، أو حيوانا- فإنه يكون بالرفع بألف ونون مزيدين في آخره، وبياء ونون في النصب والجر: هذان رجلان، ورأيت رجلين، وفرسان وفرسين، والزيدان والزيدين، وهذا معروف.',NULL,'333',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (300,'ومن التثنية: ما لا يفرد واحده، وهما المذروان: فودا الرأس، شاب مذرواه، والمذروان: طرفا الأليتين.
ومنها: تثنية واحدة، فإذا أفردت كان لها ستة ألفاظ وهي: هاتان المرأتان بالتاء، فإذا أفردت قلت: هذي المرأة، وذي المرأة، وهذه، وهاتا، وتا، وذه، كل ذلك محكي، وينشد:
فهذي سيوف يا صُدي بن مالك ... كثير ولكن أين للسيف ضارب
ومنها: أن تكون التثنية في الرفع والنصب والجر على حال واحدة لغة بلحارث بن كعب: جلست بين يداه، ورأيت الزيدان، كما قال:
تزود منا بين أذناه ضربة ... دعته إلى هابي التراب عقيم
ومنها: تثنية جاءت نونا مفتوحة، مررت بالزيدين',NULL,'334',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (301,'أنشد الفراء:
على أحوذيين استقلت عشية ... وما هي إلا لمحة فتغيب
وروي ابن مجاهد عن أبي عمرو: (أتعدانني أن أخرج) وأنشد:
أعرف منها الجيد والعينانا ... ومنخران أشبها ظبيانا
ومنها: نون تثنية تشبه الجمع، وذلك تثنية صنوان، وقنوان، الواحد صنو وقنو، والتثنية: قنوان وصنوان، والجمع صنوان وقنوان، لا فرق بين التثنية والجمع',NULL,'335',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (302,'إلا ضمة وكسرة في الدَّرج، فإذا وقفت استويا
ومنها: تثنية حذفت نونها، وهي:
أبني كليب إن عمي اللذا ... قتلا الملوك وفككا الأغلالا
يريد: اللذان.
ومنها نون تثنية مشددة، وذلك في المبهمات خاصة: هذان واللذان، وهاتين لغة أهل مكة.
ومنها: تثنية قد أفردتها العامة خطأ: الجلم، والمقراض، إنما هما الجلمان والمقراضان، وكذلك',NULL,'336',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (303,'الكلبتان، لأن الكلبة الواحدة والمقراض الواحد لا يقطع ولا الجلم.
ومنها تثنية هما فردان، وتتوهم العامة أنه جمع، وذلك زوجان وهما فردان، والعامة تقدر أن الزوج اثنان، قال الله عز وجل: (احمل فيها من كل زوجين اثنين) فالرجل زوج المرأة، والمرأة زوج الرجل، قال الله تعالى لآدم عليه السلام: (اسكن أنت وزوجك الجنة) وربما قيل للمرأة: زوجة، بالهاء توكيدا للتأنيث ورفعا للبس، كما قالوا: فرس للذكر والأنثى، وربما قالوا: فرسة.
ومنها: لفظ كلتا، قال أهل الكوفة: إنه تثنية، وقال أهل البصرة: هو واحد، وهو قولك: كلتا المرأتين قامت، قالوا: الواحد كلت، والتثنية كلتا، وقال',NULL,'337',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (304,'أهل البصرة: أخطأوا، لأنك تقول كلتا المرأتين قامت، ولا تقول قامتا، وقال الله تعالى: (كلتا الجنتين آتت) ولأن الشاعر قال:
في كلت رجليها سلامى واحده ... كلتاهما قد قرنت بزائده
وهذا الشاعر إنما اضطر فحذف الألف، ولأنهم رأوه مع المكني تصير ألفه ياء، تقول: جاءتني كلتاهما، ورأيتهما كلتيهما، وهذا إنما هو مثل لدى وعلى وإلى، يكون مع الظاهر ألفا، ومع المكني ياء، نحو قولك: عليك ولديك وإليك.
ومنها: ما يفرق بين المذكر والمؤنث في الواحد، ويستويان في التثنية، وهو قولك: هما قاما، ثم تقول: هي، وهو، كذلك أنتِ وأنتَ، ثم تقول: أنتُما لهُمَا.',NULL,'338',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (305,'ومنها: تثنية يكون لفظُها والجمع سواء، وذلك قولك: أنا، ثم تقول: نحن، للجمع والاثنين، وكذلك تقول: ضربت، ثم تقول: ضربنا ومر بنا، فيستوي الجمع والتثنية.
وكذلك يستوي المؤنث والمذكر في الأمر إذا ثنيته، فتقول: اضرب يا رجل، واضربي يا امرأة، فإذا ثنيت تقول فيهما: اضربا.
ومن ذلك: تثنية بلا جمع، وهو قولك: هذان بشران، ولا يجمع، والواحد بشر، وقال الله تعالى: (أنؤمن لبشرين مثلنا)
ومنه: ما يجمع وأنت تريد التثنية، وذلك إذا كان سيان من سِيين أو ما في البدن من جارحة واحدة، ضربت رأس زيد، وضربت رؤوس الزيدين، وبقرت بطنه وبطونهما، ولا تقل: بطنيهما، قال الله تعالى:',NULL,'339',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (306,'(إن تتوبا إلى الله فقد صت قلوبكما) ولم يقل: قلباكما، وربما ثناه الشاعر كما قال:
فتخالسا نفسيهما بنوافذ ... كنوافذ العبط التي لا ترقع
ونحو قوله:
هما نفثا في في من فمويهما ... على النابح العاوي أشد رجام
وأحسبه ذهب بالفموين إلى الشفتين، كما قالوا: مات حتف أنفيه، ذهب إلى المنخرين، فإن أضفت ذلك إلى واحد ثم ثنيته جاز، تقول: أخذت خاتميه، وما جعل الله لرجل رأسين، ولا تقول: رؤوسا هنا، لأنك أضفته إلى واحد، وقال الله تعالى: (ما جعل الله لرجل من قلبين في جوفه).
ومنها: ما ثني وهو جمع، تقول: مر بنا إبلان أسودان وغنمان، وقال الله تعالى: (أو لم ير الذين كفروا أن السموات والأرض كانتا رتقا ففتقناهما)',NULL,'340',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (307,'ولم يقل: كن، وهي سبع سماوات، وسبع أرضين.
ومنها: ما يثنى وهو واحد تقول: يا غلام اضربا زيدا، ويا زيد اسفعا بيده، ويا حرسي اضربا عنقه.
ومنها: ما يؤكد ولم يخافوا لبسا، وهو قولهم: مررت برجلين كليهما، وقال الله تعالى: (لا تتخذوا إلهين اثنين إنما هو إله واحد).
ومنها: ما لفظه كلفظ التثنية، واختلف النحويون فيه، وذلك قولك: لبيك، وحنانيك، وحواليك، وكذا بين ظهرانيهم، وظهريهم، فمن زعم أنه مثنى قال: أنا مقيم ملب إلبابا وإجابة بعد إجابة، وسعديك إسعادا بعد إسعاد، ومن زعم أنه غير مثنى قال: إنما هو لبيك، فاستثقلوا ثلاث باءات فقلبوا أخراهن ياء.
ومنها: ما تحذف الياء منه في التثنية لطول الاسم، فيقال في التثنية قرقرى: قرقران.
ومنها: ما يجمع لفظين مختلفين فيجعلان على لفظ واحد، نحو قولهم: سنة العمرين، يريدون أبا بكر',NULL,'341',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (308,'وعمر رضي الله عنهما، والخبيبان: يريدون أبا خبيب ومصعبا أخاه، وكذلك الزهدمان، يريدون زهدما وكردما أخاه، والقمران: الشمس والقمر، وهو كثير، وقد أفردنا له كتابًا.
ويقال: للأم والأب: الأبوان، وكذلك الأب والخالة، قال الله تعالى في قصة يوسف عليه السلام: (ورفع أبويه على العرش) يعني أباه وخالته، لأن أمه شراحيل كانت قد ماتت، وقولهم: شاور نفسيه، أي إرادتيه، أيفعل أم لا.
ومن التثنية: ما يذكر واحدا والمراد اثنان، نحو قوله تعالى: (سرابيل تقيكم الحر) يريد الحر والبرد، فاجتزأ بأحدهما لأنه معلوم أن ما وقى الحر وقى البرد، وقال الشاعر:',NULL,'342',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (309,'وما أدري إذا يممت أرضا ... أريد الخير أيهما يليني
يريد الخير والشر، وقد فسره بالبيت الذي بعده:
أألخير الذي أنا أبتغيه ... أم الشر الذي هو يبتغيني

أي لا يألو جهدا في طلبي.
ومن التثنية: ما يذكر اثنين ثم يعود الضمير إلى أربعة أوجه: إما عليهما، وإما على الأهم، وإما على الأقرب، وإما على الأشرف.
فأما ما عاد عليهما فقوله عز وجل: (قال رجلان من الذين يخافون أنعم الله عليهما).
وعلى الأهم قوله تعالى: (وإذا رأوا تجارة أو لهوا انفضوا إليها) لأن التجارة كانت أحب إليهم.
وعلى الأقرب: قوله تعالى: (واستعينوا بالصبر والصلاة وإنها لكبيرة).
وعلى الأشرف قوله جل اسمه: (والله ورسوله أحق أن يرضوه).',NULL,'343',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (310,'<span data-type="title" id=toc-168>(١٦٨)
باب
ليس في كلام العرب: اسم ممدود يجمع لي أفعال جمع المقصور إلا </span>حرفا واحدا في شعر الأفوه الأودي، لأن الممدود يجمع على أفعلة كرداء وأردية، والمقصور على أفعال: قفا وأقفاء، وذلك الحرف فناء وأفنية، فأتى به الأودي على افناء، وهذا عزيز، قال:
نقرع الأعداء في أفنائها ... قرعة فيها استباء وإسار',NULL,'344',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (311,'<span data-type="title" id=toc-169>(١٦٩)
باب
غرائب المصادر مجموعة
ليس في أحد ذكر: تفاوت الأمر تفاوُتا وتفاوَتا وتفاوِتا إلا </span>أبو زيد، وهاتان نادرتان، والمعروف تفاوتا.
وهلك الشيء تهلكة، وتهلوكا، هاتان نادرتان، والمعروف هلك هُلكا وهلاكا.
ولا مجلود لفلان، ولا معقول، أي لا جلد ولا عقل.
والكاذبة، والعافية، والعاقبة مصادر، قال الله تعالى: (إذا وقعت الواقعة، ليس لوقعتها كاذبة).',NULL,'345',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (312,'والتعونة، مصدر عونت المرأة، صارت عونا: إذا ولدت بطنا أو بطنين، والمشغورة مصدر شغرت، والكينونة مصدر كنت، وغلبه غلبة وغلبى، وعبق الطيب بمفرقه عباقية، وآب أُوبا بضم الهمزة وفتحها، وحبت في الأمر حابة من الحوب، وهو الإثم، وضعفت عن المشي ضعافة، والحبابة مصدر حبب زيد، والبَخل لغة في البُخل، وينشد:
تريدين أن نرضى وأنت بخيلة ... ومن ذا الذي يرضي الأخلاء بالبَخل',NULL,'346',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (313,'والخليفي مصدر الخلافة، قال عمر بن الخطاب رضي الله عنه: (لولا الخليفى لأذنت) يريد: لولا الخلافة لأحببت أن أؤذن.
المصدر إذا كان على فعول فهو بالضم جلس جلوسا، وقعد قعودا إلا أحرف جاءت مفتوحة، وقد يجوز الضم فيهن على الأصل.
ويقول آخرون: إن الوقود بالفتح: الحطب، والمصدر: الوقود بالضم، من وقدت النار وقودا، والوضوء بالفتح: الماء، وبالضم المصدر، وهذا قياس مطرد.
ويقال سرق زيد سرقا، وخنقه خنقا، وحنق عليه حنقا، ورضع رضعا، وعمل عملا.',NULL,'347',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (314,'<span data-type="title" id=toc-170>(١٧٠)
ليس في كلام العرب: ألف وصل دخلت على متحرك إلا </span>في حرف واحد، لأن من حكمها ألا تدخل إلا على ساكن ليتوصل بها إلى النطق بالساكن، وذلك لغة عبد القيس: اسل زيدا، فينقلون فتحة الهمزة إلى السين، ويبقون ألف الوصل على ما كان عليه، وحرف آخر',NULL,'348',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (315,'ذكره سيبويه أنك إذا سميت رجلا بالباء من اضرب قلت: اب، وخطأه سائر الناس، وقد ذكرته بأبين من هذا.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (316,'<span data-type="title" id=toc-171>(١٧١)
باب
ليس في كلام العرب: ألف استفهام حذفت ولا دلالة عليها إلا </span>في بيت واحد لابن أبي ربيعة:
ثم قالوا تحبها قلت بهرا ... عدد القطر والحصى والتراب',NULL,'350',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (317,'وقد جاء بيت آخر:
أفرح أن أرزأ الكرام وأن ... أورث ذودا شصائصا نبلا',NULL,'351',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (318,'أراد: أأفرح، لأنه إنما يجوز حذفها إذا كان بعدها أم لأن أم تدل عليها، كقول امرئ القيس:
تروح في الحي أم تبتكر ... وماذا يضيرك لو تنتظر
وعلى هذا تقول: قام زيد أم قعد؟ لأنك تريد أقام زيد أم قعد.',NULL,'352',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (319,'<span data-type="title" id=toc-172>(١٧٢)
باب
ليس في كلام العرب: ألف وصل دخلت على حرف إلا </span>حرفين، لام التعريف: الحمد، الرجل، الفرس، وفي القسم: ايم الله، فلذلك فتحت لأنها خالفت بدخولها موضعها، فخالفوا بحركتها حركتها، لأن ألف الوصل إنما تدخل على الأفعال، وعلى الأسماء، وهي فيها مكسورة، نحو: اسم، وابن اضرب، اجلس، أو مضمومة نحو: ادخل، اخرج، فإذا وصلت بشيء قبلها سقطت، كقولك: باسم الله، ويا زيد اركب. وكذلك إن دخل عليها ألف الاستفهام وهي مكسورة سقطت، نحو: أبنك هذا؟ (أصطفى البنات على البنين) وقال ذو الرمة:',NULL,'353',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (320,'أستحدث الركب عن أشياعهم خبرا أم راجع القلب من أطرابه طرب
وقال جرير:
حي المنازل من ذات اليعافير ... أستنكرتني أم خفت بتخبيري
فإذا دخلت ألف الاستفهام على ألف الوصل وهي مفتوحة لم تسقط، ولكن مدت لئلا يلتبس الاستفهام بالخبر، نحو آلرجل قال ذلك؟ (آلله أمركم بهذا) (آلذكرين حرم أم الأنثيين) وكذلك: آيم الله، ومن قال في أيم الله: إيم الله، وجب أن يقول: في الاستفهما أأيم الله فيقلب.',NULL,'354',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (321,'<span data-type="title" id=toc-173>(١٧٣)
باب
ليس في كلام العرب: جمع من المعتل على مثال آية وآي إلا </span>ثاية وثاي، وساية وساي: موضع الغنم، وعلم يرجع إليه، وراية وراي، وينشد:
راي إذا أورده الطعن صدر
وحاجة وحاج، وسامة وسام: عرق الذهب، وجامة وجام، وزارة وزار، وهامة وهام،',NULL,'355',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (322,'ولابة ولاب وقاحة وقاح، وبانة وبان، وشامة وشام، فأما دارة ودار فليس جمعا إنما هو إظهر التأنيث.
وقد يجيء على فُعْل كثيرا: قارة وقور: الجُبيل، وساحة وسوح، وحامة وحوم، ولابة ولوب، وعانة وعون: حمير الوحش الأناثي.',NULL,'356',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (323,'<span data-type="title" id=toc-174>(١٧٤)
باب
ليس في كلام العرب: فاعل وجمعه فعلاء إلا </span>شاعر وشعراء، قال: وإنما جاز أن يجمع شاعر على شعراء، وفعلاء جمع فعيل لا فاعل، لأن من العرب من يقول: شعر الرجل إذا قال شعرا، كما يقال: شعر.
ومن قال: شَعُر، فالقياس أن يجيء الوصف على فعيل، فتجنبوا ذلك لئلا يلتبس بشعير، ثم أتوا بالجمع على ذلك الأصل، وهذا دقيق جدا فاعرفه، لأني ما أعلم استخرجه أحد، وعاقل وعقلاء، وصالح وصلحاء، وأما علماء فليس جمعا لعالم، ولكنهم قالوا: رجل عالم وعليم وعلامة، فعلماء جمع عليم.',NULL,'357',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (324,'وليس في كلامهم فعلة جمعت على فُعْل إلا خشبة وخشب، وبدنة وبدن، وأجمة وأجم، وأكمة وأكم، ورخمة ورخم، وقد جمع كل ذلك على فعل أيضا فقيل: أكم ورخَم، وخشب، إلا بدنة لئلا يشبه البدن: السنخ، أو جمع بدنة: الدرع.
ليس في كلامهم فعلة جمع على فعائل إلا ضرة وضرائر، وكنة وكنائن، وجزة وجزائز من الصوف، وقد قيل: جزة، فأما حرة وحرائر فهي فعلة.',NULL,'358',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (325,'<span data-type="title" id=toc-175>(١٧٥)
باب
ليس في كلام العرب: من المضاعف فاعل وفعلة إلا </span>شاب وشببة، وبار وبررة، وعاق وعققة، وإن كان جمع فاعل على فعلة قياسا مطردا، حافد وحفدة: الخدمة، وظالم وظلمة، وكاتب وكتبة، إلا أنه في المضاعف عزيز نادر: بار وبررة، وواد ووددة، وغاش وغششة، والاختيار أن تقول: شاب وشبان، وقد أشب الرجل بنين، أي صار له بنون شببة، والشبب بغير هاء: الثور المسن، لأن الثور مسن، والغلمان أحداث، قال الشاعر:
رأيت عجوز الحي أسنان أمها ... لداتي وشبان الرجال لداتها',NULL,'359',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (326,'<span data-type="title" id=toc-176>(١٧٦)
باب
ليس في كلام العرب: فعل على أفعلة إلا </span>حرفان: خال وأخولة، حكاها أبو جعفر الرؤاسي: هؤلاء أخولتي، وحكى غيره: حال وأحولة.
ومن غريب هذا الباب أرض محتالة: لم يصبها مطر، واحلوليت الرجل، واحلولاني، وينشد:
فلو كانت تعطي حين تسأل سامحت ... لك النفس واحلولاك كل خليل
وقال الرؤاسي- وكان ثقة مأمونا أستاذ الفراء-: ما حلئت منه بطائل بالهمز، وهذا غريب، كما قالوا:',NULL,'360',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (327,'حلأت السويق، وإنما هو حليت السويق.
وحكى الأحمر: هو أحلأ من العسل بالهمز، وهذا غريب.
وأحل القوم: نزلت البأساء بهم، وإذا درت الناقة من غير حمل يقال: أحلت.',NULL,'361',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (328,'<span data-type="title" id=toc-177>(١٧٧)
باب
ليس في كلام العرب: من الجموع على فعولة إلا </span>قولهم: فحل وفحولة، وعم وعمومة، وأبو وأبوة، وذكور وذكورة، وخيوط وخيوطة، وبعول وبعولة، وعلوق وعلوقة، وعير وعيورة: الحمار، وخؤول وخؤولة.',NULL,'362',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (329,'<span data-type="title" id=toc-178>(١٧٨)
باب
ليس في كلام العرب: أحد من العرب يجمع ما كان مثل: غيب، وبيت، وكيل، على أفعال استثقالا للضمة على الياء، لا يقال: أبيت ولا أكيل، إنما يقال: أبيات، وأكيال، إلا </span>في حرفين: أعين في عين، وأعينات في أعين، وينشد:
بأعينات لم يخالطها القذى
وحرف آخر وهو غريب: دين وأدين في القليل، وديون في الكثير.',NULL,'363',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (330,'<span data-type="title" id=toc-179>(١٧٩)
باب
ليس في كلام العرب: اسم على فعلان إلا </span>عِرفان: اسم رجل، وهو صفة لكل ثقيل من الرجال [لؤوم]، وأنشد:
كفاني عرفان الكرى وكفيته ... كلوء النجوم والنعاس معانقه
فبات يريه عرسه وبناته ... وبت أريه النجم أين مخافقه
وفركان: اسم طفيل العرائس الجارود بن أبي سبرة،',NULL,'364',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (331,'وهو القارئ (وما يخدعون إلا أنفسم)
ومنه على فِعِلاَّن: سنمَّار اسم رجل، وله حديث، وكنيته أبو قرد، وسجلاط: النمط، والياسمون، قال ابن دريد: سنمار أعجمي تكلمت به العرب وأنشد:
جزاني جزاه الله شر جزائه ... جزاء سنمار بما كان قدما
ورجل زبعباق: سيء الخلق، وزبعبق مثله.',NULL,'365',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (332,'<span data-type="title" id=toc-180>(١٨٠)
باب
ليس في كلام العرب: همزة تقلب هاء إلا </span>هرقت، والأصل: أرقت، وهياك وإياك، وهيهات وأيهات، وها الذاكرين، وآ الذاكرين، وهيازيد وأيازيد، وها أنتم وآأنتم، وهرجت الدابة وأرجتها، وهنرت الثوب وأنرت الثوب، وها الرجل فعل ذلك، يريد آالرجل فعل، وهزيد فعل ذلك يريد أزيد فعل، وأما والله وايم الله وهما الله وهيم الله، وإيه وهيه حديثا، وأيا فلان، وهيا فلان، وأخذوا هداتهم يريد أداتهم، قال جميل:
وأتت صواحبها فقلن أذا الذي ... منح المودة غيرنا وجفانا',NULL,'366',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (333,'أراد هذا الذي، وأنشد عن الفراء:
يا خال هلا قلت إذ أعطيتني ... هِيَّاك هياك وحنواء العنق
وأردت أن أفعل ذلك، وهردت لغة، فأنا أهريد هرادة، وأهنير هنارة، وأهريج هراجة، وأهريق هراقة، وأما أهرقت فلغة بعيدة، وكأن الهاء زائدة مثل أمهات، وأنأت اللحم: أنضجته، وأنهأته، ويقال: هؤلاء فعلوا وهاهلاء فعلوا.',NULL,'367',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (334,'<span data-type="title" id=toc-181>(١٨١)
باب
ليس في كلام العرب: اسم على مفعل إلا </span>موكل، ومورق، وموهب، فأما الموهبة بالهاء فنقرة في صخرة يستقر فيها ماء السماء، وأما المورقة فإن العرب تقول: التجارة مورقة، أي من اتجر كثر ورقه، وأكل التمر موردة: أي محمة من ورد الحمى، ونوم الغداة مجفرة مجعرة مبخرة، والولد مبخلة مجبنة،',NULL,'368',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (335,'والحرب مأيمة، أي يقتل الرجل فتئيم امرأته، وصلة الرحم منمأة للمال منسأة للعمر، وهذا الباب أحكم في كتاب الأفق.',NULL,'369',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (336,'<span data-type="title" id=toc-182>(١٨٢)
باب
ليس في كلام العرب: مما جاء على فعلة إلا </span>درجة لغة في الدرجة، وحزقة وهو الضيق الخلق، ويقال: حزقة، قال النبي صلى الله عليه وسلم للحسين وقد أخذ بيديه يرقيه على صدر قدميه: (حزقة حزقه، ترق عين بقه)',NULL,'370',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (337,'وروى ابن دريد: حبقة حبقه، ورجل كبنة: متقبض، وقد اكبأن: انقبض، وينشد:
في القوم غير كبنة علفوف
والعلفوف: الجافي: والجمع كبنات، والكبنة: الخبزة اليابسة، ورجل غُضُبة وغَضَبة، وغُلُبة وغَلَبة، وحمار كدرة: الغليظ، وأنشد:
نجاء كدر من حمير أبيدة ... بفائله والصفحتين ندوب',NULL,'371',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (338,'والخصلة: المرأة الحسناء لينة ناعمة، قال: قيل لأعرابي: ما تشتهي؟ قال: خُضُلة ونعلين وحلة، والخضلة: النعيم، وينشد:
إذا قلت إن اليوم يوم خضلة ... ولا شرز لا قيت الأمور البجاريا
أي ضيقا، الشرز والشرزة: الشدة، والحظبة مثل الحزقة، يقال: إن في خلقه لحزقة وحظبة، والأفرة: الاختلاط، وأفرة أيضا، وعفرة، ويقال: حدرة وبدنة، أي حادر بادن.',NULL,'372',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (339,'<span data-type="title" id=toc-183>(١٨٣)
باب
ومما على فعلةٍ: </span>فلان عجزة أبويه؛ أي آخر ولد، ومثل العجزة: البضاضة، وأول ولد: بكره، والثني: الثاني، وفلان قدوة في الخير، ولا يثنى ولا يجمع ولا يؤنث، ورجل عزبة، إذا اشتد فلم يوضع جنبه إلى الأرض، ويقال: عرنة بالنون، والعربة سفاة البهمى، وجمعه عرب، وفلان عيمة قومه، أي من خيارهم، مثل طريقة قومه، ونظيرة قومه، ونظورة قومه: ورجل قرفة؛ أي محتال، وفلان صفوة ولد أبويه، وهو عينة قومه مثل عيمة، ولا يثنى ولا يجمع.',NULL,'373',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (340,'<span data-type="title" id=toc-184>(١٨٤)
باب
ليس في كلام العرب: ما جاء على تفعل إلا </span>ترتب، وتدرأ، وتتفل لغة في تتفل، وهو ولد الثعلب، ويقال: تِتفل، وتُتفل، فأما ترتب فالأمر الثابت، ما هذا الأمر بترتب، أي راتب ثابت، وينشد:
وللخيل أيام فمن يصطبر لها ... ويعرف لها أيامها الخير تعقب
وقد كان حيانا عدوين في الذي ... خلا فعلى ما كان في الدهر فارتبي
يخاطب الحال: ارتبي أيتها الحال واثبتي، واما تدرأ؛ أي ذو درء.',NULL,'374',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (341,'<span data-type="title" id=toc-185>(١٨٥)
باب
ليس في كلام العرب: اسم على يَفَنْعَل إلا </span>اسم واحد ذكره سيبويه في شعر الطفيل الغنوي، وزنه يفنعل، أو فعنلل، قال:
أشاقتك أظعان بجفن يبنبم ... نعم بكرا مثل الفنيق المكمم
ألم تر ما أبصرت أم كنت ساهيا ... فتشجى بشجو المستهام المتيم',NULL,'375',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (342,'فقال ألا لا لم تر العين شبحة ... وما شمت إلا لمح خُلَّب معين
غدوا فتاملت الحدوج فشاقني ... وقد رفعوا في السير إبراق معصم
فقلت لحراض وقد كدت أزهي ... من الشوق في إثر الخليط الميمم
الخلب: السحاب الذي قد هراق ماءه، ومثله: الهف، والسيق، والجهام، وفي شعر طفيل: بنيان: موضع.
وبنيان لم تورد وقد تم ظمؤها ... تراح إلى ماء الحياض وتنتمي',NULL,'376',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (343,'<span data-type="title" id=toc-186>(١٨٦)
باب
ليس في كلام العرب: فاعل صفة جمعت على فواعل إلا </span>أربعة أحرف: فارص وفوارس، وهالك وهوالك، وخاشع وخواشع، وناكس ونواكس، لأن فواعل إنما هي جمع فاعلة لا فاعل، مثل: ضاربة وضوارب، وأما فاعل إذا كان اسما فإنه يجيء على فواعل كثيرا: جاجب وحواجب، وخاتم وخواتم.',NULL,'377',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (344,'<span data-type="title" id=toc-187>(١٨٧)
باب
ليس في كلام العرب: جمع ناقة أنق إلا </span>في شيء رواه الأصمعي، وهو قول الطفيل الغنوي:
وحوافر صلب وُقِين من الوجى ... لا بالصغار ولا الكبار الجنب
وتخاله في مشيه متوجيا ... نقبا بحافره وإن لم تنقب
يدع الجياد إذا جرين كأنها ... أنُقٌ مُشكلة بأعلى سبسب',NULL,'378',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (345,'قوله: مشكلة؛ أي مقيدة، يريد كأنها نوق، وجائز أن يكون أراد جمع أنوق وهو الرخم الطائر، شبهها سرعتها بذلك الطائر، فأنوق وأنق مثل: رسول ورسل، وإن كان جمع الناقة فإنه غريب ما سمع بمثله، فعلى هذا تجمع الناقة: ناقات، ونوقا، وأنُقا، وأيانق وأينقا، ونياقات، وأنواقا، وأونقا، وناقا، ونِياقا، على عشرة أوجهٍ.',NULL,'379',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (346,'(١٨٨)
باب
ليس في كلام العرب: في جمع فيعال مثل: عيمان إلى اللبن، وعيام، يقال رجل عيمان أيمان، فعيمان: عطشان إلى اللبن، وأيمان: ماتت امرأته من العيمة والأيمة، وامرأة عيمة أيمى، وإنما جاء عيام في بيت واحد، وهو قياس على عطشان وعطاش:
أتترك معشرا قتلوا هذيلا ... وتوعدني بقتلى من جذام
كذلك يضرب الثور المعنى ... ليشرب وارد البقر العيام',NULL,'380',NULL,NULL,'0');
INSERT INTO [b13239] VALUES (347,'ولم تفعل كما فعل ابن قيس ... وعرْقُ الصِدْقِ في الأَقْوَامِ نَام
تم الكتاب والحمد لله وحده، وهو حسبنا ونعم الوكيل وصلى الله على سيدنا محمد النبي، وآله الطاهرين وسلم تسليمًا كثيرًا.',NULL,'381',NULL,NULL,'0');
