INSERT INTO [b132974] VALUES (843,'للشيباني: عبد الرحمن بن علي بن محمد الشافعي الأ<span data-type="title" id=toc-261>ث</span>ري - دار الكتاب العربي، بيروت، لبنان (د. ت).
