* غُنْدَر: محمد بن جعفر. تقدم رقم (٣٨٣١).
* * *','3','462',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1428,'<span data-type="title" id=toc-5596>حرف الفاء</span>
* الفأفاء: محمد بن زياد. تقدم رقم (٣٩١١).
* فافاه: أبو معاوية. تقدم رقم (٣٨٧٣).
* الفرخ: حفص بن عُمر. تقدم رقم (٩٥٠).
* فُلَيْت بن خليفة: هو أفلت. تقدم رقم (٤٢٥).
* فُليح بن سُليمان: في الأسماء. تقدم رقم (٣٥٣٨).
* * *

<span data-type="title" id=toc-5597>حرف القاف</span>
* قاضي الجن: محمد بن عبد الله بن عُلاثة. تقدم رقم (٣٩٩٢).
* قاضي المِصْرَين: شُريح. تقدم رقم (١٨٠٥).
* قرة بن عبد الرحمن: في الأسماء. تقدم رقم (٣٦٠٠).
* القصير: هو عمران. تقدم رقم (٣٣٢٤).
* القويّ: هو أبو يونس. تقدم رقم (٨٨٢).
* قيصر: هو أبو النضر. تقدم رقم (٤٦٩٨).
* * *

<span data-type="title" id=toc-5598>حرف الكاف</span>
* الكبير: موسى بن أبي كثير. تقدم رقم (٤٥٥٤).
* كُرْدوس: خلف بن محمد. تقدم رقم (١١٢٨).
* * *

<span data-type="title" id=toc-5599>حرف اللام</span>
* لُزَيْم: هو ملازم بن عَمرو. تقدم رقم (٤٥٧٣).
* لؤلؤ: إسحاق بن إبراهيم. تقدم رقم (٢٧٠).
* * *','3','463',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1429,'<span data-type="title" id=toc-5600>حرف الميم</span>
* الماجشون: عبد العزيز. تقدم رقم (٢٦٢٢)، وعبد الملك بن عبد العزيز. تقدم رقم (٢٦٧٥).
* محرق: هو جارية بن قدامة. تقدم رقم (٦٣٣).
* مَرْدُويه: محمد بن سعيد بن الوليد. تقدم رقم (٣٩٣٢).
* مُسَدّد: في الأسماء. تقدم رقم (٤٢٨٨).
* مشقر: يزيد بن رباح. تقدم رقم (٤٩٩٤).
* المُصْبِح: مسلم بن يسار. تقدم رقم (٤٣٢٥).
* المُعَرْقب: مِصْدَع. تقدم رقم (٤٣٤٤).
*المقعد: أبو معمر. تقدم رقم (٢٢٥٥)، وعبد الرحمن بن سعد. تقدم رقم (٢٤٩٥).
* المقوِّم: يحيى بن حكيم. تقدم رقم (٤٨٨٤).
* مِنْذل بن علي: اسمه عَمرو. تقدم رقم (٤٤٧٩).
* * *

<span data-type="title" id=toc-5601>حرف النون</span>
* النبيل: أبو عاصم. تقدم رقم (١٩٢٢).

٥٤٤٦ - نِفْطويه (١).
* قال السهمي: سألته (يعني الدارقطني) عن نِفْطُوْيه؟ فقال: لم يكن بالقوي في الحديث. "سؤالات السهمي" ٦١.
* * *
_________
(١) اسمه<span data-type="title" id=toc-5603> إبراهيم بن محمد بن عرفة النحوي</span>، وفاتنا أن نذكره في حرف الألف.','3','464',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1430,'<span data-type="title" id=toc-5604>حرف الهاء</span>
* هدّاب بن خالد: هو هُدبة. تقدم رقم (٤٧٠٨).
* هِقْل بن زياد: قيل اسمه محمد، وقيل عبد الله. تقدم رقم (٤٧٤٧).
* * *

<span data-type="title" id=toc-5605>حرف الواو</span>
* وقدان العبدي: قيل اسمه واقد. تقدم رقم (٤٨١٠).
* وهيب بن الوَرْد: هو عبد الوهاب بن الورد. تقدم رقم (٤٨٦٢).
* * *

<span data-type="title" id=toc-5606>حرف الياء</span>
* ياقوتة العلماء: هو المعافَى بن عمران. تقدم رقم (٤٣٧٧).
* * *','3','465',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1431,'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ

<span data-type="title" id=toc-5607>باب الكنى من الألقاب</span>
* أبو بطن: هو الطفيل بن أبي بن كعب. تقدم رقم (١٩٤٩).
* أبو الرِّجال: محمد بن عبد الرحمن الأنصاري، هو أبو عبد الرحمن. تقدم رقم (٤٠٠٩).
* أبو زكَّار: الخليل بن زكريا، هو أبو زكريا. تقدم رقم (١١٣٨).
* أبو الزناد: هو أبو عبد الرحمن. تقدم رقم (٢١٣١).
* أبو ساسان: حُضَين بن المنذر. تقدم رقم (٩٣٦).
* أبو قلابة الرَّقاشي: هو أبو محمد. تقدم رقم (٢٦٨٤).
* أبو المَليح الرَّقي: هو أبو عبد الله. تقدم رقم (٨٦٦).
* أبو مُنَين: يزيد بن كَيْسان، هو أبو إسماعيل. تقدم رقم (٥٠٢٩).
* أبو همّام: عبد الأعلى السامي، هو أبو محمد. تقدم رقم (٢٣٩٣).
* * *','3','466',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1432,'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ

<span data-type="title" id=toc-5608>باب الأنساب من الألقاب</span>
* التِّنّيسي: هو عبد الله بن يوسف. تقدم رقم (٢٣٨٣).
* التَّبُوذَكي: موسى بن إسماعيل، بصري. تقدم رقم (٤٥١٩).
* الجُرْجُسي: يزيد بن عبد ربه. تقدم رقم (٥٠١٢).
* الجُهني: أبو فروة، هو نَهْدي، نزل في جهينة. تقدم رقم (٤٣٠٩).
* الجُوْباري: يحيى بن خلف الباهلي. تقدم رقم (٤٨٩٠).
* الخُوْزي: إبراهيم بن يزيد، مكي، نزل شعب الخوْز. تقدم رقم (١١٠).
* الدَّالاني: أبو خالد، كان نزل فيهم. تقدم رقم (٥٢١٣).
* الزنْجي: مسلم بن خالد، قيل كان أبيض أشقر. تقدم رقم (٤٣٠٨).
* السَّبيعي: أبو إسحاق. تقدم رقم (٣٢٥٨).
* السُّدي: إسماعيل بن عبد الرحمن. تقدم رقم (٣٧٠).
* الشَّاذَكوني: سليمان بن داود. تقدم رقم (١٦٥٧).
* الشَّيباني: أبو إسحاق. تقدم رقم (١٦٦٩).
* العِجْلي: محمد بن مروان. تقدم رقم (٤١٥٥).
* العَرْزَمي: محمد بن عُبيد الله. تقدم رقم (٤٠٤٧).
* العَمِّي: زيد بن الحَوَاري. تقدم رقم (١٣٨٨).
* القِبْطي: عبد الملك بن عُمير. تقدم رقم (٢٦٧٩).
* القَطَوَاني: خالد بن مَخْلد. تقدم رقم (١٠٩٨).
* المَقبُري: أبو سعيد. تقدم رقم (٣٦٨٤)، وابنه سعيد. تقدم رقم (١٤٩٧).','3','467',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1433,'* المكي: جماعة من غير أهلها نزلوها، منهم إسماعيل بن مسلم. تقدم رقم (٣٨٨).
* المَنْجَنِيقي: إسحاق بن إبراهيم بن يونس. تقدم رقم (٢٧٨).
* المَيْموني: محمد بن زياد. تقدم رقم (٣٩١١).
* * *

<span data-type="title" id=toc-5609>باب</span>
<span data-type="title" id=toc-5610>٥٤٤٧ - مولى ابن سباع، عن عبد الله بن عمر، عنه موسى بن عُبيدة</span>.
* قال الترمذي: مجهول. "جامع الترمذي" ٣٠٣٩.
* * *','3','468',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1434,'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ

<span data-type="title" id=toc-5611>أبواب النساء</span>
* * *

<span data-type="title" id=toc-5612>حرف الألف</span>
<span data-type="title" id=toc-5615>٥٤٤٨ - أسماء بنت أبي بكر الصديق. ذات النطاقين</span>.
* قال العجلي: هي أم عبد الله، وعروة بن الزبير. "الثقات" ١٨٦٠.
* * *

<span data-type="title" id=toc-5613>حرف الباء</span>
<span data-type="title" id=toc-5616>٥٤٤٩ - بحرية بنت هانئ الأعور</span>.
* قال الدارقطني: مجهولةٌ. "السنن" ٣/ ٣٢٤.

<span data-type="title" id=toc-5617>٥٤٥٠ - بُقيرة امرأة سلمان</span>.
* قال العجلي: كوفيَّة ثقة. "الثقات" ١٨٦١.
* * *

<span data-type="title" id=toc-5614>حرف التاء</span>
<span data-type="title" id=toc-5618>٥٤٥١ - تميمة بنت سلمة، عن عائشة</span>.
* قال الدارقطني: لا بأس بها. "سؤالات البرقاني" ٦٢.
* * *','3','469',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1435,'<span data-type="title" id=toc-5619>حرف الجيم</span>
<span data-type="title" id=toc-5621>٥٤٥٢ - جسرة بنت دجاجة العامرية الكوفية</span>.
* قال البخاري: عند جسرة عجائب. "التاريخ الكبير" ٢/ ١٧١٠.
* العجلي: كوفية تابعية ثقة. "الثقات" ١٨٦٢.
* وقال البزار: جسرة ما نعلم روى عنها غير قدامة بن عبد الله. "كشف الأستار" ٧٣٠.
* وقال البرقاني: سألته (يعني الدارقطني) عن جرة بنت دجاجة؟ فقال: يُعتبر بحديثها، إلا أن يحدث عنها من يُترك. "سؤالات البرقاني" ٦٩.
* * *

<span data-type="title" id=toc-5620>حرف الحاء</span>
<span data-type="title" id=toc-5622>٥٤٥٣ - حسنة أم شرحبيل</span>.
* قال العجلي: لها صحبة. "الثقات" ١٨٦٣.

<span data-type="title" id=toc-5623>٥٤٥٤ - حفصة بنت سيرين أم الهذيل الأنصارية البصرية</span>.
* قال العجلي: بصريَّةٌ ثقةٌ تابعيَّةٌ، سَمِعَتْ أم عطية. "الثقات" ١٨٦٤.

<span data-type="title" id=toc-5624>٥٤٥٥ - حفصة بنت عبد الرحمن بن أبي بكر الصديق</span>.
* قال العجلي: مدنيَّةٌ تابعيَّة ثقة. "الثقات" ١٨٦٥.

<span data-type="title" id=toc-5625>٥٤٥٦ - حفصة بنت عبيد بن عازب</span>.
* قال البرقاني: سألته (يعني الدارقطني) عن حفصة بنت عازب؟ فقال: هي بنت عبيد بن عازب، عن البراء، لا يكاد يُحدث عنها غير ابن أبي ليلى، يُخرَّجُ حديثُها. "سؤالات البرقاني" ١٢٣.

<span data-type="title" id=toc-5626>٥٤٥٧ - حفصة بنت عمر أم المؤمنين</span>.
* قال العجلي: زوج النبي -صلى الله عليه وسلم-، ورَحِمَهَا. "الثقات" ١٨٦٦.
* * *','3','470',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1436,'<span data-type="title" id=toc-5627>حرف الخاء</span>
<span data-type="title" id=toc-5630>٥٤٥٨ - خديجة بنت خويلد</span>.
* قال العجلي: زوج النبي -صلى الله عليه وسلم-، عمة حكيم بن حزام. "الثقات" ١٨٦٧.

<span data-type="title" id=toc-5631>٥٤٥٩ - خلدة بنت طلق</span>.
* قال العجلي: يماميَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٦٨.
* * *

<span data-type="title" id=toc-5628>حرف الدال</span>
<span data-type="title" id=toc-5632>٥٤٦٠ - دقرة</span>.
* قال العجلي: تابعية ثقة. "الثقات" ١٨٦٩.
* * *

<span data-type="title" id=toc-5629>حرف الراء</span>
<span data-type="title" id=toc-5633>٥٤٦١ - ربيعة بنت عياض</span>.
* قال العجلي: كوفيَّةٌ تابعيَّةٌ ثقةٌ، سمعت من علي. "الثقات" ١٨٧٠.
قال الهيثمي: في الأصل مكان ربيعة رمثة، وقد ضبب عليها في نسخة أخرى رمثة على الصواب. وكذلك هي في ثقات ابن حبان رمثة بنت عياض: كوفية تابعية ثقة. والصواب: ربيعة.

<span data-type="title" id=toc-5634>٥٤٦٢ - ريطة الحنفية</span>.
* قال العجلي: كوفيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٧١.
* * *','3','471',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1437,'<span data-type="title" id=toc-5635>حرف الزاي</span>
<span data-type="title" id=toc-5638>٥٤٦٣ - زينب بنت أبي حازم، أخت قيس بن أبي حازم</span>.
* قال العجلي: كوفية تابعيَّة ثقةٌ. "الثقات" ١٨٧٢.

<span data-type="title" id=toc-5639>٥٤٦٤ - زينب بنت أبي سلمة بنت أم سلمة</span>.
* قال العجلي: مدنيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٧٣.

<span data-type="title" id=toc-5640>٥٤٦٥ - زينب السهمية هي بنت محمد بن عبد الله بن عمرو بن العاص</span>.
* قال الدارقطني: مجهولةٌ، ولا تقوم بها حُجَّةٌ. "السنن" ١/ ١٤٢.
وقال أيضًا: مجهولة. "العلل" ٥/ الورقة ١٥١.
* * *

<span data-type="title" id=toc-5636>حرف السين</span>
<span data-type="title" id=toc-5641>٥٤٦٦ - سلامة بنت الحر الفزارية</span>.
* قال الآجري: سمعت أبا داود يقول: خَرَشَة بن الحر له صحبة، وأخته سلامة بنت الحر لها صحبة، سمعت النبي -صلى الله عليه وسلم- يقول: يقوم الناس لا يجدون من يصلي بهم. "سؤالات الآجري" ٣/ ٢١٥.
* * *

<span data-type="title" id=toc-5637>حرف الصاد</span>
<span data-type="title" id=toc-5642>٥٤٦٧ - صفية بنت شيبة بن عثمان حاجب الكعبة</span>.
* قال العجلي: مَكِّيَّة، تابعيَّة ثقةٌ. "الثقات" ١٨٧٤.

<span data-type="title" id=toc-5643>٥٤٦٨ - صفية بنت أبي عبيد بن مسعود الثقفية</span>.
* قال العجلي: مدنيةٌ متفقهةٌ ثقةٌ، امرأة عبد الله بن عمر وهي أخت المختار بن أبي عبيد. "الثقات" ١٨٧٥.
* وقال الدارقطني: لم تُدرك النبيَّ -صلى الله عليه وسلم- "السنن" ٢/ ٣٨.
* * *','3','472',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1438,'<span data-type="title" id=toc-5644>حرف العين</span>
<span data-type="title" id=toc-5645>٥٤٦٩ - العالية بنت أنفع، عن أم محبة، وعنها ابنها يونس بن أبي إسحاق</span>.
* قال الدارقطني: أم محبة والعالية مجهولتان لا يحتج بهما. "السنن" ٣/ ٥٢.
* العالية بنت سبيع. انظر أم العالية بنت سبيع. رقم (٥٤٩٠).

<span data-type="title" id=toc-5646>٥٤٧٠ - عائشة بنت أبي بكر الصديق رضي الله عنهما</span>.
واسم أبيها عبد الله بن عثمان.
* قال العجلي: زوج النبي -صلى الله عليه وسلم-، ورضيَ عنها، وَرَحِمَهَا."الثقات" ١٨٧٨.
* وقال يعقوب بن سفيان: حدثنا سليمان بن حرب. قال: حدثنا حماد، عن هشام بن عروة، أن أباه ذكر عائشة فقال: كانت أعلم الناس بالحديث، وأعلم الناس بالقرآن، وأعلم الناس بالشعر. قال: ولقد قلت قبل أن تموت بأربع سنين: لو ماتت عائشة لما ندمت على شيء إلا كنت سألتها عنه. "المعرفة والتاريخ" ١/ ٤٨٩.
وقال أيضًا: حدثنا عمرو بن حفص. قال: حدثنا أبي. قال: حدثنا الأعمش. قال: حدثنا مسلم، عن مسروق، أنه سئل عن عائشة كانت تحسن الفرائض؟ فقال: لقد رأيت أصحاب محمد -صلى الله عليه وسلم- الأكابر يسألونها عن الفرائض. "المعرفة والتاريخ" ١/ ٤٨٩.

<span data-type="title" id=toc-5647>٥٤٧١ - عائشة بنت الزبير بن هشام بن عروة</span>.
* قال ابن أبي حاتم: قلت لأبي زرعة: ما حال عائشة؟ هل روى عنها أحد سوى معاوية (يعني ابن عبد الله الزبيري)؟ قال: نعم حدثنا عنها المدنيون. "علل الحديث" ٧٨٧ و ٢٦٠٥.

<span data-type="title" id=toc-5648>٥٤٧٢ - عائشة بنت سعد بن أبي وقاص الزهرية المدنية</span>.
* قال العجلي: تابعيَّة ثقةٌ مدنيَّةٌ. "الثقات" ١٨٧٦.','3','473',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1439,'* وقال يعقوب بن سفيان: حدثنا ابن بكير. قال: حدثني ابن وهب. قال: حدثني مالك قال: دخلت على عائشة بنت سعد بن أبي وقاص، فسألتها عن بعض الحديث، فلم أرض أن آخذ عنها شيئًا لضعفها. "المعرفة والتاريخ" ١/ ٦٩٩.

<span data-type="title" id=toc-5649>٥٤٧٣ - عائشة بنت طلحة ببن عبيد الله التيمية أم عمران</span>.
* قال العجلي: تابعيَّة ثقة مدنية. "الثقات" ١٨٧٧.

<span data-type="title" id=toc-5650>٥٤٧٤ - عائشة بنت عجرد</span>.
* قال الدارقطني: ليس لعائشة بنت عجرد الا هذا الحديث (١)، عائشة بنت عجرد لا تقوم بها حُجَّة. "السنن" ١/ ١١٥.

<span data-type="title" id=toc-5651>٥٤٧٥ - عمرة بنت عبد الرحمن بن سعد بن زرارة الأنصارية</span>.
* قال العجلي: مدنيَّة تابعيَّة ثقةٌ."الثقات" ١٨٧٩.

<span data-type="title" id=toc-5652>٥٤٧٦ - عمرة الغاضرية أم القلوص، عن عائشة، وعنها المتوكل بن الفضيل</span>.
* قال الدارقطني: لا تثبت بها حُجَّةٌ. "السنن" ١/ ١٢٥.

<span data-type="title" id=toc-5653>٥٤٧٧ - عمرة الهمدانية</span>.
* قال العجلي: كوفيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٨٠.
* * *
_________
(١) يعني حديثها عن ابن عباس. قال: إن كان من جنابة أعاد المضمضة والاستنشاق، واستأنف الصلاة.','3','474',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1440,'<span data-type="title" id=toc-5654>حرف الفاء</span>
<span data-type="title" id=toc-5656>٥٤٧٨ - فاطمة بنت الحسين بن علي بن أبي طالب الهاشمية المدنية</span>.
* قال الترمذيُّ: لم تدرك فاطمة الكبرى. "جامع الترمذي" ٣١٥ * وقال البرقاني: فقلت للدارقطني: روى عبد الله بن الحسن الهاشمي، عن أمه، عن جدته؟ فقال: أمه هي فاطمة بنت الحسين وجدته هي فاطمة الكبرى بنت النبي -صلى الله عليه وسلم- ورضي عنها. لم تسمع أمه منها، ويُخَرَّجُ الحديث. "سؤالات البرقاني" ٢٦٩.

<span data-type="title" id=toc-5657>٥٤٧٩ - فاطمة بنت علي بن أبي طالب</span>.
* قال العجلي: لم تسمع من أبيها شيئًا. "الثقات" ١٨٨١.

<span data-type="title" id=toc-5658>٥٤٨٠ - فاطمة بنت عمرو بن حرام</span>.
* قال العجلي: عمة جابر، من أصحاب النبي -صلى الله عليه وسلم-. "الثقات" ١٨٨٢.

<span data-type="title" id=toc-5659>٥٤٨١ - فاطمة بنت المنذر بن الزبير بن العوام الأسدية امرأة هشام بن عروة</span>.
* قال العجلي: تابعيَّةٌ مدنيَّةٌ ثقةٌ، سَمِعَتْ من أسماء بنت أبي بكر الصديق. "الثقات" ١٨٨٤.

<span data-type="title" id=toc-5660>٥٤٨٢ - فاطمة الخزاعية</span>.
* قال أبو زرعة الدمشقي: حدثنا أبو اليمان. قال: حدثنا شعيب، عن الزهري قال: كانت فاطمة الخزاعية قد أدركت عامة أصحاب رسول الله -صلى الله عليه وسلم- "تاريخه" ٩٧٥.
* * *

<span data-type="title" id=toc-5655>حرف القاف</span>
<span data-type="title" id=toc-5661>٥٤٨٣ - قمير بنت عمرو الكوفية امرأة مسروق</span>.
* قال العجلي: كوفيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٨٥.
* * *','3','475',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1441,'<span data-type="title" id=toc-5662>حرف الميم</span>
<span data-type="title" id=toc-5663>٥٤٨٤ - معاذة بنت عبد الله العدوية البصرية أم الصهباء</span>.
* قال البخاري: قال لي سعيد بن محمد: حدثنا يعقوب. حدثنا أبي، عن ابن إسحاق. حدثني الصلت بن مسلم، عن الحسن، عن أم الصهباء، امرأة من أهل البصرة ثقةٌ."التاريخ الكبير" ٤/ ٢٩٠٦.
* وقال أبو داود: معاذة العدوية امرأة صلة بن أشيم. "سؤالات الآجري" ٣/ ٢٩٣.
* * *','3','476',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1442,'بسم الله الرحمن الرحيم

<span data-type="title" id=toc-5664>باب الكنى من النساء</span>
* * *

<span data-type="title" id=toc-5665>حرف الألف</span>
<span data-type="title" id=toc-5668>٥٤٨٥ - أم الأسود الخزاعية. ويقال: الأسلمية مولاة أبي برزة</span>.
* قال النسائي: أم الأسود يروي عنها أحمد بن يونس، غير ثقة. "الضعفاء والمتروكون" ٧٠٦.
* * *

<span data-type="title" id=toc-5666>حرف الباء</span>
<span data-type="title" id=toc-5669>٥٤٨٦ - أم بلال بنت هلال بن أبي هلال الأسلمية</span>.
* قال العجلي: مدنيَّة تابعيَّة ثقةٌ. "الثقات" ١٨٨٦.
* * *

<span data-type="title" id=toc-5667>حرف الدال</span>
<span data-type="title" id=toc-5670>٥٤٨٧ - أم الدرداء الصغرى زوج أبي الدرداء</span>.
* قال الآجري: سمعت أبا داود يقول: أم الدرداء الكبرى لها صحبة، والصغرى روى عنها أبو قلابة، وميمون بن مهران، ويونس بن ميسرة بن حلبس، كانت تكون بدمشق. "سؤالات الآجري" ٥/ الورقة ١٨.
* * *','3','477',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1443,'<span data-type="title" id=toc-5671>حرف الذال</span>
<span data-type="title" id=toc-5675>٥٤٨٨ - أم ذرة المدنية مولاة عائشة</span>.
* قال العجلي: مدنيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٨٧.
* * *

<span data-type="title" id=toc-5672>حرف السين</span>
<span data-type="title" id=toc-5676>٥٤٨٩ - أم سعد الأنصارية، روى عنها محمد بن زاذان</span>.
* قال الدارقطني: لا تُعرف إلا به. "الضعفاء والمتروكون" ٤٦٩.
* * *

<span data-type="title" id=toc-5673>حرف العين</span>
<span data-type="title" id=toc-5677>٥٤٩٠ - أم العالية (١) بنت سُبيع</span>.
* قال العجلي: مدنيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٨٨.

<span data-type="title" id=toc-5678>٥٤٩١ - أم عبد الله سرية الربيع بن خيثم</span>.
* قال العجلي: كوفيَّةٌ ثقةٌ. "الثقات" ١٨٨٩.

<span data-type="title" id=toc-5679>٥٤٩٢ - أم علقمة</span>.
* قال العجلي: مدنيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٩٠.
* * *

<span data-type="title" id=toc-5674>حرف الميم</span>
<span data-type="title" id=toc-5680>٥٤٩٣ - أم محبة، عن عائشة، وعنها العالية</span>.
* قال الدارقطني: أم محبة والعالية، مجهولاتان، لا يُحتج بهما. "السنن " ٣/ ٥٢.
_________
(١) في "لسان الميزان" ٧/ الترجمة (٥٩٢٢): العالية.','3','478',NULL,NULL,'0');
INSERT INTO [b133397] VALUES (1444,'<span data-type="title" id=toc-5684>٥٤٩٤ - أم موسى سرية علي بن أبي طالب. قيل: اسمها فاختة. وقيل: حبيبة</span>.
* قال الدارقطني: أم موسى عن علي حديثها مستقيم، هي سرية لعلي، يخرج حديثها اعتبارًا. "سؤالات البرقاني" ٥٨٥.
* * *

<span data-type="title" id=toc-5681>حرف النون</span>
<span data-type="title" id=toc-5685>٥٤٩٥ - أم النعمان، روى عنها الحارث بن شبل</span>.
* قال الدارقطني: ليست بمعروفة. "الضعفاء والمتروكون" ١٥٦.
* * *

<span data-type="title" id=toc-5682>حرف الهاء</span>
* أم الهذيل: حفصة بنت سيرين. تقدمت رقم (٥٤٥٤).
* * *

<span data-type="title" id=toc-5683>باب</span>
<span data-type="title" id=toc-5686>٥٤٩٦ - أخت ربعي بن حراش، وهي امرأة حذيفة</span>.
* قال العجلي: كوفيَّةٌ تابعيَّةٌ ثقةٌ. "الثقات" ١٨٩١.
* * *
انتهى الكتاب بفضل الله وبرحمته، في مدينة بغداد، حيث نزلنا بها، مع فجر الثالث عشر من شهر رمضان المبارك، لعام ١٤١٠ هـ نسأل الله عز وجل أن يتقبله منا، وأن يتجاوز عن ذنوبنا ما خفي منها وما ظهر، ويجمعنا برسول الله صلى الله عليه وسلم في مقعد صِدْق عند مليك مقتدر.','3','479',NULL,NULL,'0');
