-- Insert data for b13791
INSERT INTO [b13791] VALUES (1,'بسم الله الرحمن الرحيم
[و] صلى الله على سيدنا محمد وآله وسلم
الحمد لله ذي الجلال والإكرام، والآلاء العظام، والإفضال والإنعام، والعفو والانتقام. خالق الأعراض والأجرام، ومخترع الأشباح والأجسام. الموجود الذي لا أول لوجوده، إذ لو كان محدثا، لافتقر إلى','1','227',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2,'محدث إلى غير أول، وذلك لا يتصور في الأوهام. فهو الباقي إلى غير نهاية، إذ ما يثبت له القدم، استحال عليه الانعدام. نبه العقول على قدرته باختراعه المخترعات على غير مثال وإمام. وأرشدها إلى علمه بعجائب مصنوعاته، بما فيه من الإتقان والإحكام، ودلها على إرادته المتعلقة بجميع المتجددات، من جهة تخصيصها مع تساويها بالتقدم والتأخر، والإيجاد','1','228',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (3,'والإعدام، المنزه عن كل ما قام بالحوادث، فدل على حدثها، فلا تجوز عليه المماسة، والمباينة، والمجاوزة، والمكان والزمان، ولا الساعات والأيام. فهو الواحد في ذاته، فلا يقبل الانقسام. المخصوص بصفاته،','1','229',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (4,'ليس كمثله شيء، وهو السميع العلام. المنفرد بخلق الخلائق وأعمالهم، فلا شريك له عند أهل الإسلام. خلق القدرة لعباده على بعض الأفعال التي اخترعها، فهي مقدورة لهم من غير تأثير له في خلقها، إذ يستحيل أن','1','230',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (5,'يتطرق إلى المتحد الانقسام. فلا يتصور خلق بين خالقين عند ذوي الأفهام. البصير الذي يتعلق بصره بجميع الموجودات، فلا يخفى عليه شيء','1','231',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (6,'في الأرض ولا في السموات، وإن غلظ الحجاب، واشتد الظلام. السميع بسمع، محيط بالموجودات، فيسمع دبيب النمل على الصخرة في قعر البحر على الدوام. المتكلم بكلام قديم أزلي، قائم به، ليس بأصوات تتقطع، وحروف تتوالى، موصوفة بالتقدم والانصرام.','1','232',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (7,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .','1','233',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (8,'بل كلامه واحد، [أمر] بالمأمورات، ونهي عن المنهيات، وخبر عن المخبرات، لا تتصور فيه حقيقة الاستفهام، إذ هو العالم بالمعلومات غير المتناهيات، فمن المحال من العالم الاستعلام. وخبره صدق، لا يتصور فيه كب، إذ يستحيل الكذب في كلام النفس على من يستحيل عليه الجهل والأوهام. وهذه الصفات قديمة، أزلية قائمة به، بدليل اتصافه بأحكامها على الدوام.','1','234',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (9,'ويستحيل أن يكون محلا للحوادث، ثبت ذلك بواضح الأعلام. وهو المريد لجميع المتجددات، وأعمال العباد خيرها وشرها نفعها وضرها. فلا مانع له مما أراد، ولا معارض له فيما فعل من صحة وإسقام.
لا جائز في صفاته، ولا واجب في أفعاله. فإن عفا فبفضله، وإن عذب','1','235',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (10,'فبعدله، لا للتشفي والانتقام.
رحم عباده بإسقاط التكليف عنهم قبل ورود الأنبياء، وبعثة الرسل الكرام. ثم أرسل الرسل، وأيدهم بالمعجزات الظاهرة، دلالة على صدقهم فيما يبلغونه عن الملك العلام. بعث محمدا - صلى الله عليه وسلم -، بشيرا ونذيرا إلى الأحمر','1','236',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (11,'والأسود، مبينا للأحكام، وموضحا للحلال والحرام، داعيا إلى الله بإذنه، بأوضح الدلائل والأعلام- وهو القرآن المجيد- فلا يشبهه شيء من الكلام.
بل لو اجتمعت الإنس على أن يأتوا بسورة من مثله لعجزوا، مدى الدهور والأعوام، مشتمل على الفصاحة والجزالة، (٢/ب) والبلاغة، قد تحيرت فيه عقول ذوي الأفهام. فيه نبأ من كان قبلنا من الأمم الخالية، والقرون البالية، على أكمل تفسير وأحسن نظام، عما سيكون من الأمور، فصودفت على حسب ما أخبر، من غير انقسام. والمخمن والمنجم لا تتفق له الإصابة في كل الأحكام، لا جرم انقادت العرب العاربة، وهم اللد الفصحاء، واللسن','1','237',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (12,'البلغاء، واعترفت بالعجز، مع استبدادهم بفصيح الكلام. فأوضح الحجة، وأظهر المحجة، ودعا إلى شريعة الإسلام. وأمر بالصلاة والصدقة الصيام، وحث على الجهاد والحج وصلة الأرحام. وجاهد في الله حق جهاده حتى أمات الكفر وأبطل عبادة الأصنام. ونهى عن الظلم، والبغي والفواحش، وجميع الآثام. حتى انقاد الناس لحكم الله - عز وجل -، وترك التحاكم إلى الأزلام. وزهد في الدنيا قولا وفعلا، فإنها أشبه شيء بالأحلام. ورغب في الآخرة التي هي دار الدوام. صلى الله عليه وعلى آله وأصحابه وأصهاره وأنصاره البررة الكرام.
أما بعد، فإنه لا أهم بعد العلم بالله - عز وجل -، وصفاته ورسله، من أحكامه ليحصل الامتثال لأمره، والانكفاف عما نهى عنه. قال الله تعالى: {وما خلقت [الجن والإنس] إلا ليعبدون}. وقال تعالى: {قل هذه سبيلي أدعوا إلى الله على بصيرة أنا ومن اتبعني}. وقال: {وأن هذا صراطي مستقيما فاتبعوه ولا تتبعوا السبل فتفرق بكم عن سبيله}. فلا نجاة إلا بسلوك طريق الله - عز وجل -. والعلم هو الدليل على الطريق، وهو الميراث الذي ورثه الأنبياء، قال - صلى الله عليه وسلم -: (إن الأنبياء لم يورثوا دينارا ولا درهما، وإنما ورثوا العلم).','1','238',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (13,'ولا تصح معرفة الفروع دون تحصيل علم الأصول.
وقد رأيت كتاب الشيخ الإمام، إمام الحرمين رحمة الله عليه الملقب (بالبرهان)، من أحل ما صنف في أصول الفقه، لمكان مصنفه من العلم، وحرصه على التحقيق، ميله عن التقليد، وإضرابه عن التطويل والتكرير، وانصرافه عن الاستدلال عن الخيالات البعيدة، والاستدلالات الركيكة، مع فصاحة في اللفظ واختصار، واعتناء بالمعنى وعدم انتشار. فاستخرت الله تعالى في الاعتناء بشرحه، وحل ما أشكل من ألفاظه، وعسر من معانيه، مع الحرص على إيضاح الحق، والانحراف عن التعصب، مستعينا بالله - عز وجل -، متبرئا من الحول والقوة إلا بالله، وهو الموفق للصواب.
قال الإمام رحمة الله عليه: (الحمد لله رب العالمين والصلاة) [على محمد خاتم النبيين]). قال الشيخ [- رضي الله عنه -]: الحمد: لفظة يراد بها الثناء','1','239',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (14,'بصفات الكمال ومحاسن الأمور والأفعال والأقوال. فقد يحمد الإنسان على الشجاعة والعلم والمعرفة، وإن كانت هذه الصفات مختصة به، قاصرة عليه. والشكر للمنعم بآلائه ونعمه، إنما يشكر على ما أولى من إحسانه. فالحمد أعم والشكر أخص. هذا هو الأمر المشهور عند أهل اللسان. وقد يوضع الحمد موضع الشكر، فيقال: حمدتك على ما صنعت إلي من خير، ولا يوضع الشكر موضع الحمد، فلا يقال: شكرتك على شجاعتك وغيرها. وعند أهل التصوف: يرجع الشكر إلى العمل، ويرجع الحمد إلى الثناء الجميل. وقد قال الله تعالى: {وقل الحمد لله}. وقال: {وآخر دعواهم أن الحمد لله رب العالمين}. (٣/أ) وقال: {اعملوا آل داود شكرا}. فالشكر عندهم: صرف النعم إلى جهات يرضى بها المشكور المنعم.
وقوله: (الله)، قد اختلف في أصل هذه الكلمة ومعناها. فذهب','1','240',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (15,'[البصريون] إلى أن أصلها (لاه)، فزيدت (اللام) للتعريف ساكنة، فأدغمت اللام في اللام، فإن [ابتدئ] بها، اجتلبت همزة الوصل لها، وإن كان في حال التدرج سقطت.
وقال أهل الكوفة: أصلها (إله) ثم ادخلت الألف واللام، فصار (الإلاه)، ثم حذفت الهمزة لكثرة الاستعمال، ثم أدغمت (اللام) في (اللام) فقيل: (الله). والقول الأول أصح لوجهين:
أحدهما- لزوم [التمسك] [بهاء] الاسم عليه. وما ذكروه دعوى من غير دليل.
الثاني- أنه قد سمع (لاه أبوك) بمعنى الله أبوك، فتحرك حرف الجر، وبقيت الكلمة على ما هي عليه.
واختلف الناس في هذا الاسم، هل هو مشتق أو جامد؟ فذهب أكثر أهل العلم إلى أنه مشتق. ثم اختلفوا في اشتقاقه، فقال النضر بن شميل: هو من','1','241',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (16,'التأله، وهو التعبد [والتنسك]. وقيل: هو من (الإلاه)، وهو الاعتماد، إذ الخلق معتمدون عليه. وقال أبو عمرو بن العلاء: هو من ألهت إلى الشيء، إذا تحيرت فيه. وقال المبرد: هو من قول العرب: ألهت إلى الشيء، إذا سكنت إليه. وقيل: هو من (الوله)، وهو فقدان العقل لفوات المحبوب، فأبدلت من (الواو) (همزة)، كما يقال: إشاح ووشاح. وقيل معناه: المحتجب، لأن العرب تقول: لاهت العروس، إذا احتجبت. وقيل: معناه المتعالي، ومنه قيل للشمس: [إلهة]. وقد قيل غير هذا. وهذه الأقاويل أجودها.','1','242',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (17,'وقوله: (رب العالمين)، أي خالق الخلق، وسيدهم، ومالكهم، والقائم بأمورهم. العالمين: جمع عالم، ولا واحد له من لفظه، كالرهط والناس، وقيل: إنه اسم للجمع. قاله النضر بن شميل. واختلفوا في مدلوله، فقيل: هم الملائكة، وقيل: هم [بنو] آدم، وقيل: هم الإنس والجن، وقيل: الروحانيون. قاله أبو عمرو بن العلاء. وهو معنى قول ابن عباس: كل ذي روح. وقال سفيان بن عيينة: هم الخلق أجمعون. وهذا قول أهل الكلام. وسبب الاختلاف أنه: هل أخذ من العلم أو العلامة؟
وقوله: (الصلاة على محمد)، الصلاة من الله الرحمة. ومحمد: اسم','1','243',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (18,'دال على كثرة المحامد، ومذمم بخلافه. وخاتم النبيين: بعموم المعنى.
قال الإمام: (حق على كل من يحاول الخوض في فن من فنون العلم) إلى قوله (حظ من العلم الجملي). قال الشيخ - رضي الله عنه -: الحق: خلاف الباطل. والحق بمعنى الثبوت، يقال: استحق عليه حقا، أي ثبت له ولزمه. أي لازم لكل من يحاول الخوض، يقال: حاولت الأمر، أي أردته. فعلى مريد علم من العلوم هذه الوظائف الثلاث، وهي: الإحاطة بالمقصود الذي يراد له الفن، لتجر الداعية إلى الطلب، غذ صرف الهمة لما لا يتعلق به غرض، لا يتأتى للعقلاء اعتيادا. عن، الباري سبحانه لا يقصد إلى الأمور، ولا يريدها لغرض، لاستحالة الغرض عليه.
وقوله: (بالمواد التي منها يستمد ذلك الفن). المادة: زيادة متصلة.','1','244',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (19,'قال الله تعالى: {والبحر يمده من بعده سبعة أبحر .. }.
وقوله: (وبحقيقته وحده)، الحقيقة: لفظ مشترك، وقد يراد بها الذات، فيقال: حقيقة الشيء ونفسه وعينه بمعنى واحد. وقد يراد بها خلاف المجاز، وهي من أقسام الألفاظ، فيقال: اللفظ ينقسم (٣/ب) إلى الحقيقة والمجاز. والمراد هنا بالحقيقة: الذات دون اللفظ. والضمير عائد على الفن، أي بحقيقة الفن، وليست هذه الإضافة إضافة الشيء إلى نفسه، فإن تلك ممنوعة، وهي كإضافة الأسماء إلى الكنى، والكنى إلى الأسماء، وذلك لا يسوغ عند أهل اللسان. نعم، الإضافة على وجهين: لفظية ومعنوية. فاللفظية في اللفظ دون المعنى، كالحسن الوجه، فلا تفيد تخصيصا ولا تعريفا. والإضافة المعنوية: [ما أفادت]','1','245',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (20,'أحدهما، ثم هي على وجهين: إضافة الشيء لغيره، كغلام زيد، وسرج الدابة. وقد تكون كإضافة النوع إلى الجنس، كخاتم حديد، ونفس زيد، إذ لفظ الخاتم لا يختص بالحديد، ولا النفس بزيد، فأفادت الإضافة التخصيص.
وقوله: (وحده). اعلم أن لفظ الحد ينطلق في اللغة على جهات: إذ يطلق على الحاجز بين شيئين. فيقال: بيني وبينه حد. وقد يطلق على منتهى الشيء، فحدود الدار منتهاها. ويطلق أيضا على المنع، ومنه تسمى المرأة حادا، لامتناعها من الزينة والأزواج. وحدود الشرع موانع من المحرمات. وسمي الحديد حديدا، لامتناعه لشدته. وسمي السجان حدادا، لمنعه من في السجن من الخروج.
وهو في عرف العلماء: مشترك بين اللذات واللفظ، فيقال فيه على الوجه الأول: حد الشيء نفسه. وعلى الثاني: حد الشيء هو اللفظ المفسر لمعناه. ثم إنه أيضا على هذا الوجه مشترك بين [ثلاثة] أمور:
فيطلق على اللفظ الحد، باعتبار دلالته على الأوصاف النفسية عند','1','246',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (21,'مثبتي الأحوال، ولابد أن يكون دالا على جميع الأوصاف، وإن كانت ألفا، ولا [تبال] بالتطويل، حتى يكون السامع للحد متصور للمحدود، بحيث إذا رآه، لم يستفد معلوما جديدا، بل لا يتجدد له سوى الرؤية، ولكن يحترز من التكرار. وإذا كان اللفظ يدل على الشيء بطريق الضمن، فلا يذكر أولا صريحا، ثم يذكر ثانيا ضمنا، فيحصل التكرار.
ومثاله: أن يقول في حد الشراب: إنه مسكر معتصر من العنب. ولا يقول: إنه جسم مائع. والأحسن أن يبدأ بالأعم ويختم بالأخص. فلا يقول: مسكر معتصر من العنب، بل العكس. والسبب فيه أنه لا يمكن معرفة الأخص مع الغفلة عن الأعم، فإذا ذكر الأخص أولا، تعذر الفهم حتى يذكر الأعم، ثم يفهم الأخص، [فيتراخى] الفهم عن الذكر، وليس كذلك إذا ذكر الأعم','1','247',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (22,'أولا، فإن الفهم يساوق الذكرن فكان [أولى].
واعلم أن المقصود بالحد: الإرشاد إلى المحدود وإيضاحه، فلا يصح أن يأتي بالألفاظ المجملة من غير قرينة، لتعذر البيان، فأما إذا اقترنت قرينة معرفة، ففيه خلاف. والصحيح القبول، والأحسن الترك. ولا حاجة في الحد النفسي إلى ذكر الاطراد والانعكاس، والجمع والمنع، فإنه إذا [أتى] به على شرطه، لا يكون إلا كذلك.
الاصطلاح الثاني: للحد باعتبار اللفظ: الحد الرسمي، والمقصود منه بيان من حيث الجملة، إما بذكر بعض صفات النفس المختصة، أو بذكر اللوازم والتوابع المشهورة، بحيث تكون مختصة به، ولا تثبت لغيره. والفرق بينه وبين الأول: أن هذا لا تتصور به الحقيقة في النفس، ولكن إذا صودفت عرف (٤/أ) عندها المحدود. ولابد في هذا من شرط الجمع والمنع، والاطراد','1','248',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (23,'والانعكاس. ونعني بالجمع: أنه لا يخرج من المحدود عن الحد شيء. وبالمنع: انه لا يدخل فيه غيره. ونعني بالاطراد: وجود كل واحد منهما مع وجود صاحبه. وبالانعكاس: انتفاؤه مع انتفائه.
وهل الطر والعكس شرط في الحصة أو دليلها؟ فإن كان شرطا، لم يلزم من وجوده صحة الحد، ويلزم من الانتفاء الفساد. وإن كان دليل الصحة، لزم من الوجود الصحة، ولم يلزم من الانتفاء الفساد. والصحيح أنه شرط لا دليل. لأنا نجد حدودا مطردة منعكسة، ولا يحصل منها مقصد صحيح.
وأما قول الأصوليين في المناظرة: عرفت صحته باطراده وانعكاسه، فهو','1','249',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (24,'كلام متجوز به. وإنما اكتفى بذلك في المناظرة، من جهة أنه لا يمكن سواه، إذ الحدود لا تقتنص بالبرهان، إذ البرهان إنما يقام على الجملة الخبرية بعد معرفة مفرداتها، ثم تق النسبة القابلة للصدق والكذب، فتطلب البرهان.
وأما الحد، فإنه وإن كان على صورة الجملة، فهو في التحقيق مفرد. مثاله: أنا إذا قلنا: الخمر شراب مسكر، فالشراب المسكر هو الخمر بعينه، ولم يعقل أولا خمرا ثم وصفناه بكونه شرابا مسكرا، بخلاف قولنا: زيد عالم، فإنا لما عقلنا زيدا، قضينا عليه بكونه عالما، فأمكن الصدق والكذب في','1','250',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (25,'النسبة، فافتقر إلى البرهان. فلما لم تتصور إقامة الدليل على الحد، وأمكن أيضا من الحاد العبد الكثير، كان أقرب الطرق البيان بالاطراد والانعكاس، ويفوض إلى الخصم الاعتراض.
الاصطلاح الثالث: لإطلاق الحد على اللفظ باعتبار كونه شارحا للفظ: وهو بمثابة قولنا: العلم معرفة. وهذا لا يتحصل منه بيان الحقيقة بأوصاف النفوس، ولا باللوازم، بل يكون المتكلم فهم عن السائل أنه فهم الحقيقة، وإنما جهل نسبة اللفظ إليها، فيبين له بلفظ واضح النسبة عند السائل، أن اللفظ المسؤول عن بيانه [وينسب] إلى ما ينسب إليه الآخر.','1','251',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (26,'وشرطه: أن يكون اللفظ الثاني أوضح عند السائل من الأول. وقد يجوز باعتبار سائل غيره أن ينعكس الأمر في حقه، فيبين له بما سأل عن بيانه غيره.
فإذا ثبت هذا، فقول الإمام: (بحقيقته وحده)، لم يرد بالحد الحقيقة لقوله: (إن أمكنت عبارة سديدة)، بل إنما أراد اللفظ باعتبار كونه يدل على الذات. وهذا أمر لا يحتاج إليه مريد الفن، لتصور المطلوب عند الإحاطة بالحقيقة، وإن لم يدر الطالب التحديد. ثم إنه هل يحد لنفسه، أو يطلب من غيره التحديد؟ فإن كان يحد لنفسه ليبين، فذلك محال، وإن طلب من غيره الحد ليتبين بعد درك الحقيقة، فهو أيضا باطل، إذ الحاصل لا','1','252',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (27,'[يبتغي]. والظاهر أن هذا خلل في العبارة. والمراد- والله أعلم- وبحقيقته بحده، ملتمسا ذلك من غيره. فلم يرد الحد لعينه، وإنما أريد ليتصور السائل المحدود ليمكنه طلبه.
ثم قال: (وإن [عسرت]، حاول المدرك بمسلك التقاسيم). والمدرك ههنا: اسم مصدر، كمالضرب، والمراد: حاول الدرك. وهذا الكلام قد تكرر من الإمام مرارا في غير موضع. ورأي أن التقسيم يتوصل به إلى درك الحقيقة. وهذا الكلام (٤/ب) فيه نظر. فإن الشيء على ما ذكرناه، إنما','1','253',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (28,'تتبين حقيقته بالوقوف على أوصافه النفسية. وسلب أمر عن أمر لا يبينه بوجه، إذ نفي الضد ليس بصفة لضده نفسية، ولا معنوية. ولو تصور إنسان لا يعرف البياض مثلا، ولا تصوره، وعرف كل لون سواه، لم يكن نفي الألوان في حقه كافيا في تصور البياض. نعم، إن انضبط المطلوب في النفس مع معان سواه، وحصلت الإحاطة بالجميع، والتبست نسبة الاسم إلى معين منها، وعرف الإنسان اسم ما سواه، كان التقسيم في هذه الصورة يفيد في معرفة التسمية. وسيأتي لهذا مزيد تقرير في مسألة حد العلم، إن شاء الله.
وقد بينا أن الإحاطة بالمقصود لابد منها ليتأتى الطل اعتيادا. وأما تصور المطلوب في النفس، فشرط تصور الطلب عقلا. وأما تحصيل المواد، فقد يكون الاستمداد عقليا، وقد يكون عاديا، وسنتكلم على ذلك عند ذكر أصول الفقه.
قال: (والغرض من ذلك أن يكون الإقدام على تعلمه مع حظ من العلم). قد بينا توقف الطلب على تصور المطلوب. فإن قيل: فإذا علمه، فكيف يتصور الطلب مع حصول العلم؟ قلنا: علم الجملة لا يمنع من علم','1','254',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (29,'التفصيل، فإن من علم أن الفقه: هو العلم بالأحكام الشرعية، لم يصر بذلك فقيها، وكذلك من علم أن النحو: معرفة جهات كلام العرب، لم يكن بذلك نحويا، وكذلك من علم أن أصول الفقه: أدلته، لم يكن بذلك أصوليا.
ثم قال: (والعلم الذي يحاول الخوض فيه) إلى قوله (وهو مستمد من الكلام والعربية والفقه). اعلم أن أصول الفقه يطلق لقبا، ويطلق مضافا على حد الإضافة. فإذا أطلق لقبا، كان عبارة عن فن من الفنون، مشتمل على جملة معلومات، وهي الأحكام الشرعية، وحقائقها وأقسامها، والمثمر لها، وهي أدلتها، وبيان أقسامها، وشرائط دلالتها، وهي كيفية الاستثمار من الأدلة، من جهة المنظوم والمفهوم، والمعنى المعقول، وعلى من له استنباط الأحكام من الأدلة. فهذا هو العلم المعبر عنه بأصول الفقه.
وإذا أطلق مضافا، كان عبارة عن الأدلة خاصة. وهذا هو الذي أراده','1','255',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (30,'الإمام [ههنا]. وإذا كان هذا هو المراد، فلا يصح أن يكون الكلام مادة لأدلة الأحكام، فإنه قد فسر الكلام بأنه (معرفة العالم) إلى آخره. ولا يصح أن تكون هذه المعرفة مادة لأنفس هذه الأدلة، لصحة ثبوت الكتاب والسنة والإجماع في أنفسها، وإن لم يحصل [إلباس] بعلم الكلام. وكذلك العربية، كيف تكون مادة، والمادة زيادة متصلة، والكتاب والسنة من جملة العربية؟ والفقه أيضا لا يصح أن يكون مادة للأصول، إذ العلم بالأحكام إنما يتلقى منها.
فلابد من تأويل في اللفظ. فالمراد: أن العلم بهذه الأدلة لا يحصل دون تقديم هذه المواد، على ما سنبين وجه ذلك.
ثم قال: (فالكلام نعني به معرفة العالم، وأقسامه، [وحقيقته]،','1','256',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (31,'[وحدثه]، والعلم بمحدثه، وما يجب له من الصفات، وما يستحيل عليه، وما يجوز في [حقه] والعلم بالنبوات، وتمييزها بالمعجزات عن دعاوى (٥/أ) المبطلين، وأحكام النبوات، والقول فيما يجوز ويمتنع من كليات الشرائع). الفصل واضح، وفيه العلم بما يجب له من الصفات. يعني بالواجب [ههنا]: ما لو قدر عدمه لزم منه محال. وسيأتي إن شاء الله تعالى الكلام على الواجب، واختلاف معانيه.
وصفاته سبحانه وتعالى لا جائز فيها، إذ الجائز لا وجود له بنفسه، وهو مفتقر إلى من يوجده، فتحقق فيه حقيقة الحدث. والقديم سبحانه يستحيل أن','1','257',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (32,'يكون محلا للحوادث، إذ لو قامت به لم يخل عنها، وما لا يعرى عن الحوادث حادث. ويستحيل عليه كل ما [يناقض] وصف القدم. وجميع أفعاله مما يجوز في حكمه، فلا تجب ولا تستحيل، ومن ذلك جواز بعثة الأنبياء وتأييدهم بالمعجزات. فإن ذلك يدرك من علم الكلام.
وقوله: ([فيما] يجوز ويمتنع من كليات الشرائع). يمكن أن يريد به ما يتعلق بالتحسن والتقبيح، ونفي الواجبات عقلا، وما يتعلق بجواز نسخ الأحكام، وجواز تكليف المحال.
وقوله: (ولا يندرج المطلوب من الكلام تحت حد). يعني بذلك المطلوب: الحد النفسي والرسمي دون اللفظي. فإن هذا الفن قد اشتمل على ذكر القديم والحادث، والصفة والموصوف، والنفي والإثبات، والعلم والاعتقاد، والبرهان والشبهة. وهذه الأمور لا يتصور أن تدخل تحت حقيقة.','1','258',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (33,'جنس، ولا خاصة نوع، ولا يتصور أيضا أن تشترك في لوازم جامعة مانعة، لتنافيها وتنافي كثير منها، وتضاد بعضها. وأما الحد اللفظي، فلا يمتنع، إذ هو يرجع إلى تبديل الأسامي، وتعدد الألفاظ، كعلم الكلام، وأصول الدين، وقواعد العقائد، وغير ذلك من الألفاظ. وسيأتي الكلام على بقية الفصل، إن شاء الله تعالى.
وأما المادة الثانية: وهي العربية، والعربية في العرف: عبارة عن معرفة النطق بالكلمات العربية، وإن لم يدر مدلولاتها. وهذا وإن كان يحتاج إليه، إلا أن الحاجة إليه قليلة، والحاجة إلى معرفة مدلولات الألفاظ في اللغة أهم من ذلك.
وقد ذكره الإمام بعد ذلك فقال: ([ولن] يكون المرء على ثقة من هذا','1','259',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (34,'الطرف حتى يكون محققا [في اللغة] والعربية]. وهذا التعداد أيضا إنما هو من جهة الاستعمال، وإلا باللغة تشتمل على معرفة المدلولات وكيفية النطق. وقد تقدم الكلام أن اللغة ليست مادة للأدلة، وإنما العلم على ثقة من هذا الطرف حتى بكون متحققا، مستقلا في [اللغة] العربية).
ثم قال رحمه الله: (ومن مواد أصول الفقه؛ الفقه، فإنه مدلول الأصول، ولا يتصور درك الدليل دون درك المدلول). وقد بينا أيضا أن الفقه لا يصح أن يكون مادة للأدلة، إذ الفقه في غرضنا: هو العلم بالأحكام الشرعية، وذلك لا يتلقى إلا من الأدلة، فيكف يتصور أن يكون مادة لها؟ نعم، العلم بكونها أصول فقه، لا يتصور إلا إذا تصور الفقه. ويدل على ذلك أمران:','1','260',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (35,'أحدهما- بالنظر إلى صناعة العربية. والثاني- بالنظر إلى البحث العقلي. (٥/ب)
فأما الأمر الأول: فهو أن المضاف إلى معرفة إضافة حقيقية، لابد أن [يتعرف] بها، وكذلك يشترط سلبه للتعريف إن كان معرفة، لتعذر أن يتعرف الاسم من وجهين مختلفين. فإذا اشترط في صحة الإضافة التنكير، لم تتعرف إلا على تقدير المضاف إليه. فغن لم يكن المضاف إليه معرفة، لم يتعرف المضاف، إلا أن النحوي يكتفي في التعريف بكون الفقه معرفة، وإن كان لا يدري معناه، والأصولي ينقل هذا الكلام بعينه إلى المعنى، ويقول: إن لم يكن الفقه متصورا عندنا، لم ندر خصوصية الدليل المرتبط به. وأما حصول العلم بثبوت الأحكام الشرعية، فلا يثمره إلا الأدلة، فلا يصح أن يكون مادة لها.
قال: (ثم يكتفي الأصولي بأمثلة من الفقه يتمثل بها [في كليات من] أصول الفقه). أما الأمثلة، فغير محتاج إليها، وإن كان ذكرها معينا على','1','261',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (36,'السلوك. ولكن لابد من تصور الأدلة في النفوس حتى تتقرر دلالتها، ومن أي جهة دلت. وإذا تصورت من جهة دلالتها، تصور مدلولها، وهذا القدر مكتفى به.
ولما حكم بأن من مواد الأصول: الفقه، افتقر إلى حد الفقه فقال: (فإن قيل: فما الفقه؟ قلنا: هو في اصطلاح علماء الشريعة: العلم بأحكام التكليف). اعلم أن الفقه في اللغة: هو العلم مطلقا. يقال: فقهت الشيء وعلمته بمعنى واحد. قال الله تعالى: {فمال هؤلاء القوم لا يكادون يفقهون حديثا}. وقال: {وعلم آدم الأسماء كلها}. فسمى ذلك علما، ولكن صار بعرف العلماء- علم الشريعة- مخصوصا بعلم الأحكام الشرعية، حتى لا يطلق بحكم العادة اسم [الفقيه] على متكلم ونحوي ومفسر.
وأما قول الإمام: (إنه العلم بأحكام التكليف)، فليس ذلك وضع','1','262',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (37,'اللغة، ولا عرف الاستعمال. أما وضع اللغة، فقد بينا عمومه، وأما عرف الاستعمال، فلا يوافقه هذا من وجهين مختلفين:
أحدهما: أن الإباحة ليست من التكليف عند المحقيين، ولا الندب والكراهة عند الإمام. والعلم بها من الفقه، فلم يجمع الحد.
الثاني أن إضافة الإحكام إلى التكليف، ليست من قبيل إضافة الشيء إلى غيره، كغلام زيد، ولا من إضافة النوع إلى جنسه، كباب ساج، بل إضافة الجنس إلى نوعه، وليس ذلك بمعروف عند أهل اللغة، إلا أن يكون أطلق هذا على رأي الأستاذ الذي عد الإباحة من التكليف، فيكون لهذا وجه، والإمام ينكر هذا أشد الإنكار','1','263',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (38,'ثم قال: (فإن قيل: فمعظم متضمن مسائل الشريعة ظنون. قلنا: ليست الظنون فقها، وإنما الفقه العلم بوجوب العمل عند قيام الظنون. ولذلك قال المحققون: أخبار الآحاد والأقسية لا توجب عملا، وإنما يجب العمل بما يجب به العلم بالعمل، وهي الأدلة القاطعة). في الكلام اختصار، ومقصد مورده الاعتراض على حد الفقه بالعلم بالأحكام الشرعية في العرف، فقال: كيف يقول ذلك، وأكثر مسائل الفقه مظنونة، وسموا فقهاء بسببها؟ فقال: إنما سموا فقهاء باعتبار (٦/أ) ما عملوا، وإنما الظنون أمارات على حصول العلم بالأدلة القاطعة، وهو الإجماع المنعقد على وجوب العمل عند أخبار الآحاد وإجراء الأقيسة. فأقول: هذا كلام غامض، وموضع ملتبس، فلابد فيه من فضل تحقيق','1','264',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (39,'فاعلم أن ظن الشيء والعلم به ضدان، فلا يتصور أن يكون الشيء مظنونا معلوما في وقت واحد، على وجه واحد. والمجتهد إذا ظن الحكم مستندا إلى خبر الواحد والقياس، كيف يصح أن ينتهض ظنه علامة على علم الحكم؟ وإنما يستديم الحكم، ما دام الظن قائما حتى لو زال ظنه بالصدق والإلحاق، لامتنع الحكم عليه. فلا يصح أن يكون استمرار ظن الحكم شرط دوام العلم به، فإن ذلك محال.
نعم، يجوز أن يحصل ظن الاستواء في الجامع في باب القياس، أو ظن صدق العدل شرطا في العلم بالحكم ابتداء، ويرتبط استمراره باستمراره، ويجوز خلافه. فيكون ما ذكره الإمام في هذا المقام على القول بتصويب المجتهدين.','1','265',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (40,'وأما على القول بان المصيب واحد، فلا يصح أن تجعل الظنون على الوجه المذكور شرطا في انتصاب الأدلة القاطعة. إذ لو كان كذلك، لقطع بتصويب المجتهدين. والذي نختاره خلاف ذلك. على ما سيأتي بيانه، إن شاء الله تعالى. فالصحيح عندنا في إطلاق اسم الفقيه في العرف على: من تجوز له الفتوى، إما لكونه مجتهدا، أو لمن يجوز له الفتوى من المقلدين.
ثم قال: (فإن قيل: فما أصول الفقه؟ قلنا: هي أدلته، وأدلة الفقه هي','1','266',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (41,'القواطع السمعية. وأقسامها: نص الكتاب، ونص السنة المتواترة، والإجماع، ومستند جميعها قول الله - عز وجل -، ومن هذه الجهة تستمد أصول الفقه من الكلام). قال الشيخ - رضي الله عنه -: قد ذكرت أن أصول الفقه قد تطلق لقبا، وقد تطلق مضافا. والإمام رحمه الله إنما أطلقه مضافا. وإذا كان كذلك، فلا يصح أن يكون الفقه مدلول القواطع السمعية لوجهين:
أحدهما: أن الأدلة إنما باشرت الأحكام، فالأحكام هي المدلولات على الحقيقة. ومن أحاط بالدليل علم مدلوله. وقد اختلف المتكلمون في مسألة، وهي أنا إذا أقمنا دليلا على حدث العالم، فهل المدلول حدث العالم، أو العلم بحدث العالم؟ والصحيح أن المدلول الحدث، بدليل أن حدث الأكوان دال على حدث الجواهر، سواء نظر الناظرون أو لم ينظروا، لأن حد الدليل: هو الذي يتوصل بصحيح النظر فيه إلى علم ما لا يعلم في مستقر العادة اضطرارا. والنظر','1','267',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (42,'الصحيح يتضمن العلم بالمدلول.
الثاني: أن الناظر لو أخطأ جهة النظر في الدليل، لم يعلم الحكم، ولا يخرج الدليل عن كونه دليلا. فثبت بهذا أنها أدلة الأحكام، لا أدلة العلم بالأحكام.
ومعنى قولنا أدلة الأحكام: أي أدلة نسبة الأحكام إلى أفعال المكلفين، إما على جهة إثبات أو نفي. إذ نفس الحكم باعتبار كونه مفردا لا يقبل الدليل. كما قررناه في امتناع إقامة الدليل على المفردات. نعم، إذا حصل النسبة، أمكن الصدق والكذب، فافتقر إلى دليل.','1','268',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (43,'فأما إحدى النسبتين، وهي نسبة الإناث، فلا تتلقى إلا من الأدلة السمعية. فأما النسبة الأخرى، وهي نسبة النفي، فتتلقى من الأدلة السمعية، ولا تقتصر عليها. فإنا نعلم انتفاء الأحكام قبل ورود الشرع. كما سنقرره، إن شاء الله تعالى.
وقوله: (وأقسامها: نص الكتاب، (٦/ب) ونص السنة المتواترة، والإجماع). وقد اختلفت عبارات الأصوليين في هذا، فمنهم من لا يقيد هذا التقييد، ويذكر الكتاب والسنة والإجماع. وإذا قيل لهم: فالظواهر وأخبار الآحاد؟ فيقولون: إنما أردنا بذلك ما تحقق اشتمال الكتاب عليه، ولم نتحقق اشتمال الكتاب على الصورة المعينة من صور العموم. وكذلك يقولون في أخبار الآحاد: لم نتحقق كونه سنة. ومنهم من يقيد لإزالة هذا اللبس.
ومنهم من يقول: ما دل على الحكم، ولو كان بوجه مظنون، ولو كان بوجه مظنون، فهو دليل. فهذا لا يفتقر إلى التقييد، فيعد من جملة الأدلة الظواهر وأخبار الآحاد. ويرى','1','269',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (44,'أن الحكم استند إليهما. وهذا هو واللائق بمذهب من يقول المصيب واحد، ويظن الإصابة بناء على ظن الدليل.
والإمام رحمه الله قيد في الدليلين الأولين، ولم يقيد في الإجماع، ويمكن أن يكون ترك التقييد فيه لأمرين:
أحدهما- أن يكون جعل (الألف) و (اللام) في الإجماع للعهد، يعني والإجماع الذي هو حجة في عرف الأصوليين على شرطه. والعهد في الكتاب والسنة مفقود.
الثاني- أن الشروط المعتبرة في كون الإجماع حجة كثيرة، لا يمكن ضبطها إلا بتفريغ المسائل وتمهيد الأبواب. والكلام يجمل في غير مقصوده، لاسيما إذا كان التفصيل يطول.
وإنما قصر الأدلة على ثلاثة، لإخراجه (شرع من قبلنا) عن كونه حجة. وأما من يرى ذلك حجة، فلا تقتصر الأدلة عنده على ذلك. وهذا هو المختار عندنا.
وقوله: (ومستمد جميعها قول الله - عز وجل -، ومن هذه الجهة تستمد أصول الفقه في الكلام). اعلم أنه لا حاكم إلا الله تعالى، وأما النبي والزوج والسيد والوالد وغيرهم، إذا أمروا أو أوجبوا، لم يجب شيء بإيجابهم، بل بإيجاب الله','1','270',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (45,'تعالى طاعتهم، فلا حكم ولا أمر إلا الله تعالى: {ألا له الخلق والأمر}.
وقول الرسول تستند إلى الأحكام، لأنه {وما ينطق عن الهوى. إن هو إلا وحي يوحى}. والإجماع إنما استند إلى قوله، إما لكونه يدل على نص بلغهم، أو بناء على أن الله عصمهم عن الخطأ في الأحكام، فيرجع الإجماع إلى قول الرسول، ويرجع وجوب ابتاع قول الرسول إلى قول الله - عز وجل -، فافتقر بذلك إلى إثبات قول الله - عز وجل -، وإثبات صدق الرسول بالمعجزة. وقد بينا أن الذي يستمد من ذلك ليس الأدلة، وإنما افتقر العالم بالأدلة إلى تقديم هذه المعرفة، ليصح كونه عالما بالأحكام على الحقيقة.
وقد اختلف الأصوليون في هذه المادة، هل هي شرط في نيل درجة الاجتهاد، أو لا تشترط؟ فذهب أبو حامد الغزالي إلى أنها غير مشروطة في الأصل. ولكنها مشروطة في الإحاطة بالعلوم الدينية، وإلا فيتصور عنده أن يكون مجتهدا، وإن كان مقلدا في العقائد.','1','271',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (46,'وذهب غيره من الأصوليين إلى الاشتراط، وهو الصحيح عندنا في حصول العلم بالحكم، فإنه لا سبيل إلى حصول العلم مترتبا على دليل لم يعلم بعض مقدماته. وإذا كان المستدل غير عالم ببعض مقدمات الدليل، أو غير عالم بما يترتب عليه، لم يتصور أن يكون عالما بالحكم. نعم، يصح أن يكون عالما بنسبة (٧/أ) الفعل إلى الخطاب، وإن لم يعلم المخاطب. وإذا سمعنا نحن شعرا، علمنا مدلوله، وإن لم نعلم قائله. وهذا هو الذي أراده من نفى الاشتراط. والمشترط قصد أن تكون الأحكام معلومة، فلابد من تحصيل [علم] ما يترتب عليه.
ثم قال: (فإن قيل: تفصيل أخبار الآحاد والأقيسة لا تلفى إلا في أصول الفقه، وليست قواطع. قلنا: حظ [الأصولي] إبانة القاطع في العمل عندها،','1','272',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (47,'ولكن لابد من ذكرها ليتبين المدلول ويرتبط الدليل به). وجه ورود السؤال أنه لما فسر أصول الفقه: بأنها القواطع في عرف الأصوليين، قيل له: هذه مذكورة في الأصول، وليست قواطع. فإن كانت من الأدلة، فلم يجمع الحد، فيكون مختلا، وإن لم تكن من الأدلة، فأي حاجة إلى ذكرها؟ فأجاب: بأنها ليست من الأدلة، ولكن الأصولي مفتقر إلى إقامة الدليل على وجوب العمل عندها، فإن لم يتصور حقائقها، تعذر الاستدلال عليها، فذكرت من هذه الجهة، لا لكونها أدلة.
ثم قال: (فصل- قد ذكرنا أن الفقه: هو العلم بالأحكام الشرعية، ونحن الآن نذكر معنى الأحكام ذكرا [كليا]، ثم نفصلها بعد ذلك) إلى قوله (وليس لما يتعلق به قول قائل على جهة صفة حقيقية من ذلك القول.','1','273',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (48,'وهو كتسميتنا الشيء معلوما، مع القطع بأنه ليس له من تعلق العلم به صفة حقيقية). أما قوله: ليس الحكم المضاف إلى متعلقه صفة، فالأمر على ذلك، ولكن لا يتبين بهذا الحكم، لا من جهة الجملة، ولا من جهة التفصيل، فإنا قد بينا أن سلب شيء عن شيء لا يبين حقيقته بوجه. وإذا لم يكن الحكم صفة في المحكوم فيه ثابتة، وج أن تبقى الذات التي [هي] متعلق الحكم بعد التعلق على ما كانت عليه من قبل. ولكن قصد بهذا الكلام بيان مخالفتنا للمعتزلة في رد الأحكام إلى صفات الأعيان. على ما سيأتي بيان','1','274',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (49,'مذهبهم، إن شاء الله تعالى. نعم، قوله: إن الحكم يرجع إلى القول، هو بيان الحكم من أعم الوجوه، ولا يتحصل من ذلك ضبط جنس الحكم. ولم يذكر الإمام حد الجنس، لا في هذا المكان، ولا في غيره، مما سيأتي. وإنما تعرض بعد ذلك لحد التفاصيل. ولكن قد يؤخذ من حد التفصيل ضمنا حد الجنس، على ما سنبين، إن شاء الله تعالى.
وقد اختلفت عبارات الأصوليين في حد الحكم، فقال قائلون: هو خطاب الشارع إذا تعلق بأفعال المكلفين. وفي ظاهر هذا الكلام ما يدل على تجدد التعلق، وهذا يلائم قول من يقول: إن الله تعالى ليس [آمرا] في الأزل،','1','275',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (50,'وهو القلانسي، وأبو الحسن يأباه.
وقال قائلون: هو خطاب الشارع المتعلق بأفعال المكلفين. وهذا لا يظهر فيه ما ظهر من الأول، لكن يلزم منه أن يكون قول الله تعالى: {وعصى آدم ربه فغوى} - إذا خوطبنا به- حكما.
وقال قائلون: الحكم يرجع إلى تعلق الخطاب. ويرد عليه ما يرد على الأول. ولكن الفرق بين القولين: أن الأول رد الحكم إلى الخطاب المنسوب، والثاني رد الحكم إلى النسبة. والأول أصح. لأن الحكم يرجع إلى الأمر','1','276',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (51,'والنهي (٧/ب)، وهو القول على جهة مخصوصة. وسيأتي حده بعد ذلك.
والصحيح في حده أنه: خطاب الشارع المتعلق بأفعال المكلفين على جهة ترجيح أو تخيير. وقولنا: بأفعال المكلفين، تجوز، فإنه لا يتعلق التكليف إلا بمعدوم يمكن حدوث. والمعدوم ليس بفعل على الحقيقة. وهذا التجوز مشهور عند أهل اللسان. وإن أردنا الاحتراز عن هذا، قلنا: هو خطاب الشارع للمخاطبين المتعلق بما يصح أن يكون فعلا على طريق ترجيح أو تخيير.
فإن قيل: فقد عد الأصوليون من جملة الأحكام: وضع الأسباب والشروط، وليست متعلقة بما يصح أن يكون فعلا للمكلفين، كزوال الشمس','1','277',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (52,'وغيره. قلنا: هذا متجوز به، ولكن الأحكام ترتبط بما يصح أن يكون فعلا عند حصول هذه الشروط، فسمي نصبها حكما، من جهة ثبوت الأحكام عندها.
وأما قوله: (وليس لما يتعلق به قول قائل على جهة مخصوصة صفة حقيقية من ذلك القول). فكلام واضح، غير مفتقر إلى شرح وإيضاح، ولذلك تعلق القول بما لا يقبل التأثير، كالواجب والمستحيل، فتعلق القول به كتعلق العلم.
ثم قال: (ومن الأحكام: التحسين والتقبيح، وهما راجعان إلى الأمر والنهي، فلا يحسن شيء في علم الله تعالى لعينه، ولا يقبح شيء لعينه).
اعلم أن هذه النسبة لا يرجع عندهم التحسين والتقبيح إلى صفات الأفعال بوجه. وإنما يرجع ذلك إلى النسب والإضافات. فقد يحسن شيء بالإضافة إلى زيد، وهو بعينه قبيح بالإضافة إلى عمرو. والاصطلاح في ذلك يختلف بحسب الإضافة، وهي على ثلاثة أوجه:','1','278',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (53,'الأول: وهو المشهور العام: إطلاقه بإزاء الأغراض، وميل الطباع ونفرتها، فيقال على ما يميل الطبع إليه وتستريح إليه النفس: إنه حسن، وعلى عكسه: غنه قبيح. وقد تميل نفس زيد إلى ما تنفر عنه نفس عمرو، كالألوان والأشكال، فمن مال طبعه إلى صورة أو صوت استحسنه، وقضى بحسنه، ومن نفر طبعه عنه استقبحه. وليس ذلك راجعا إلى الذات، بل إلى الإضافة.
ولو قيل: هل هذا عند الله حسن أو قبيح؟ قلنا: هو عند الله حسن باعتبار زيد، وقبيح باعتبار عمرو. ومن أطلق لفظ الحسن والقبيح بهذا الاعتبار، لم يمنع الإطلاق.
الثاني: إطلاق لفظ الحسن على ما ورد الشرع بالثناء على فاعله، فيكون فعل الله تعالى، حسنا، وافق الغرض أو خالف، لثنائه على فاعله، ويكن المأمور به من الشرع جسنا، سواء كان واجبا أو ندبا، ولا يكون المباح حسنا.
الثالث: إطلاق الحسن على: ما لفاعله أن يفعله، وإن لم يكن مطلوبا، فيكون فعل الله تعالى حسنا، ويكون المباح على هذا حسنا، ولا يكون المنهي عنه حسنا. والإمام إنما اختار هذا الثالث، وهو المشهور عند العلماء، ولا سبيل إلى منع القسمين الآخرين.','1','279',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (54,'وقد قال الكعبي: إن المباح حسن بالاعتبار الثالث، وهو غلط، على ما سيأتي، إن شاء الله تعالى.','1','280',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (55,'ثم قال: (وقسمت المعتزلة الأفعال قسمين: فقالوا: يثبت حكم القبح (٨/أ) والحسن في أحدهما، مستدركا بالعقول، غير متوقف على ورود الأمر والنهي، ثم قسموا هذا القسم قسمين) إلى قوله (فإنا ندرك بمبادئ العقول أنه لا يجوز في استمرار العرف مخالفة الجم الغفير فيه). قال الشيخ أيده الله: نقل المذاهب صحيح. والفرق بين ما يدرك ضرورة ونظرا، أن ما ظهرت مصلحته، ولم تقابله مفسدة، علم حسنه ضرورة، وما علمت مفسدته، ولم تقابله مصلحة، علم قبحه ضرورة. وإنما ينشأ النظر عند اختلاف الجهتين في المعارضة، كالكذب المفيد والصدق المضر.','1','281',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (56,'وأما اختلاف النقلة في الحسن والقبح، هل هما وصفان نفسيان أم لا؟ فيفتقر إلى بيان أقسام الصفات عند المعتزلة. فالصفات التي لا ترجع إلى السلب تنقسم إلى: معللة وإلى غير معللة. وما ليست بمعللة تنقسم إلى:','1','282',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (57,'ما يقال إنها صفة النفس، وإلى ما ليست كذلك. والتي ليست كذلك تنقسم إلى: ما يقتضي مقتضيا تستند إليه، وإلى ما لا يقتضي ذلك. وهذا الأخير قد سمي التابع للحدوث.
وهذا التقسيم إنما يصح عند إطلاق الصفة على الحكم، وفي ذلك تنازع. فمن الأصوليين من يطلق الصفات على الأحكام، ومنهم من يطلق الصفات على المعاني. والتقسيم إنما يصح على هذا التفسير.
فمثال الصفات المعللة: الأحكام الثابتة للذوات بمعاني قائمة بها.','1','283',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (58,'ككونه قادرا وعالما ومريدا، إلى ما يضاهي ذلك.
وأما صفات النفس عند المعتزلة الثابتة للذوات عدما ووجودا، فككون الجوهر جوهرا، والعرض عرضا. وكذلك القول في جميع الأوصاف التي قضوا بثبوتها في العدم.
وأما الصفات المتعلقة بالمقتضيات، كالحدوث والتخصيص والإحكام، فالذي يقتضي الحدوث القدرة، والذي يقتضي الإحكام العلم، والذي يقتضي التخصيص الإرادة.
وأما الصفات التابعة للحدوث، فكتحيز الجوهر، وقيام العرض بالمحل. إلى ما يضاهي ذلك. فمن النقلة من جعله من قبيل كون الجوهر جوهرا، ومنهم من جعله من قبيل تحيز الجواهر. ولا ذاهب يذهب إلى تنزيل الصفات من الحسن والقبح منزلة التخصيص والإحكام والحدوث.','1','284',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (59,'والصحيح أن الحسن والقبح لا يكون وصفا نفسيا، لأنه لا يقضى على الفعل بالحسن والقبح في الأزل، فامتنع لذلك أن يكون من الأوصاف النفسية.
وقول الإمام: (كل ذلك جهدا بمذهبهم، فمعنى قولهم: يقبح الشيء أو يحسن لعينه، فإن ذلك يدرك منه عقلا من غير إخبار مخبر). ليس في هذا الكلام مذهب من المذاهب، و [لا] الإرشاد إلى جهة مخصوصة، إلا أن يكون المراد بذلك التعرض لإظهار الخلاف بيننا وبينهم، فإنا نقول: يفتقر في التحسين والتقبيح إلى ورود الشرع، والمعتزلة [تقول: ] لا تفتقر إلى ذلك','1','285',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (60,'في بعض الصفات.
فأما تعميم القول بأن المعنى أنه يدرك عقلا من غير إخبار مخبر، فلا يصح، لقضاء المعتزلة بأن بعض هذه الأوصاف لا تدرك (٨/ب) إلا شرعا، إلا أن يريد أنه إذا ورد الشرع بذلك كان مخبرا لا مبتدأ لشر الحكم. والعبارة لا تحتمل ذلك.
ورد القاضي رحمه الله في مسلكه الأول صحيح. والأمر الذي يدرك','1','286',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (61,'بمبادئ العقول، لا يجوز في مطرد العرف مخالفة الجم الغفير فيه. أما الضروري المحض الذي نفس العقل كاف في الإدراك فيه، فلا يصح أن يجهله بعض العقلاء.
وهل تعذر جهله عقلي أو عادي؟ أما من ذهب إلى أن العقل علوم ضرورية، فيحيل أن يجهل العاقل هذه العلوم عقلا، لأنها نفس العقل، فكيف يكون عاقلا من لا عقل له؟
وأما من ذهب إلى أن العقل صفة يتأتى بها درك العلوم، وليست منها، فيجوز أن يخلق الله لشخص تلك الصفة، وإن لم يخلق له العلوم الضرورية، لأن تلك الصفة واقعة في باب الشروط دون العلل، فيصح وجدان الشرط دون المشروط، وإن لم يتصور وجدان المشروط دون الشرط.','1','287',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (62,'وإذا ثبت أنه لابد من اشتراك جميع العقلاء في العلوم، إما عقلا وإما اعتياديا، فالمخالفة من العقلاء إنما تكون باللسان دون القلب، فيكون ذلك [تواطؤ] على الكذب، والكثرة تحيل التواطؤ اعتيادا. على ما سنبينه في أبواب التواتر.
وإن تصور كذب وتواطؤ في الأخبار المتواترة، فذلك عند حامل يحمل عليه، وملجئ يلجئ إليه، ولكن لابد من ظهور القضية بعد زوال الحامل. والأشعريون زائدون على عدد التواتر أضعافا مضاعفة، وهم مصرون على المخالفة، مستمرون عليها على مر الآباد من غير نكر وعناد، فبطل بذلك ادعاء العلم الضروري.
فإن فيل: فكما استحال أن يتواطأ العقلاء الجم الغفير على الكتمان، فكذلك يستحيل أن يخبروا بخلاف ما يضمرون. ونحن أيضا الجم الغفير، والعدد الكثير، وقد أخبرنا عن العلم الضروري أنه قام بنا بحسن بعض الأشياء وقبح بعضها، ولم يستند على نظر حتى يصح الغلط فيه، فلنكن صادقين فيما أخبرنا به عن أنفسنا من العلم الضروري. قلنا: قد بينا استحالة كونه ضروريا بما فيه مقنع وكفاية.','1','288',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (63,'وأما ما ذكرتموه، فوهم وخيال، وذلك أنا لم ننسب المعتزلة إلى التواطؤ على الكذب والكتمان، وإظهار خلاف ما أضمروا، فإن ذلك هو الذي قررنا استحالته. وإنما جاءهم الغلط من جهة كون الاعتقاد علما. والعادة لا تؤمن من الغلط في ذلك.
وهذه الوهميات قد تصادف في النفس كالأوليات، ولا يشعر الإنسان بوقت حصولها، ولا يشعر باكتسابها، فتنغرس في النفس حكمها، ويفتقر العاقل إلى التصديق بها مطلقا. فلا تفارق الأوليات العقلية، إلا أن الأوليات لا يتصور التشكك فيها، وإن حصل إصغاء، وهذه يمكن التشكك فيها. فإن الصغير يلقى إليه منذ الصبا تقبيح بعض هذه الصور وتحسين بعضها، ويستمر عليه دهرا، فيجد التصديق به، فيعتقده ضروريا، وهو وهمي. فهذا سبب الغلط. وأما تعمد الكذب فمحال، مع الكثرة وطول الزمان وانتفاء الإيالات الحاملة. (٩/أ).
ثم قال القاضي - رضي الله عنه -: (وإنما منشأ الخلاف في النظريات) إلى قوله (بطل النظر المستند إليه). قال الشيخ - رضي الله عنه -: قد اختلف الناس في جواز الاتفاق على النظري العقلي في الاعتياد، هل وقع ذلك أم لا؟ وأما التجويز العقلي، فلا','1','289',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (64,'ذاهب إلى منعه، إذ هو من قبيل الممكنات، والقدرة الأولية ليست قاصرة عنه. وقد وضح ذلك من بعض العقلاء مع الاستواء في قبول خلق العلم، وما جاز على أحد المثلين، جاز على مثله.
أما الوقوع، فقد قال أبو حامد الغزالي: (يجوز الاتفاق على ما ليس بضروري- يعني في العادة- قال: وقد اتفق العلماء على إثبات الصانع وجواز بعثة الأنبياء، ولم يخالف إلا الشواذ). فهذه منه غفلة عظيمة، وقد رد على نفسه بقوله: (لم يخالف إلا الشواذ). فقد صرح بامتناع الاتفاق من الجميع وقوعا، والقاضي لم يحل اتفاق الكثير، وإنما منع اتفاق الكافة، والأمر على ما قاله رحمه الله.
ونقل [مصنفو] المقالات اتفاق العقلاء [على] استحالة عدم القديم. وجعل ذلك حجة على الاتفاق على النظري. وهذا أيضا غلط. والذين اتفقوا على هاذ إنما هم أرباب النظر. وفي الخلق من لم يخطر بباله هذا، وليس من أهل النظر، وإنما حصل الاتفاق من البعض.
وقول الإمام: (ثم إذا ظهر النزاع في الأصل، وظهر بطلان دعوى الضرورة، بطل النظر المستند إليه). وهذا الكلام مقنع في الجدال، وأما في','1','290',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (65,'التحقيق، فغير مفيد إلا على تقدير، وهو أن لا يكون للتقبيح والتحسين مستند إلا هذا الطريق. وأما إذا أمكن غيره، لم يحصل العلم بالنفي بإبطال طريق معين. ولكن نحن قد بينا أنهما لا يرجعان إلى الصفات على التحقيق، وإنما يغني الجواب عن شبه الخصوم. وهذا الكلام مقنع في الجواب.
قال الإمام رحمه الله: (فإن قالوا: أنتم [توافقوننا] في التقبيح ما نقبحه وتحسين ما نحسنه) إلى قوله (بضرورة العقل). قال الشيخ: غرض المعتزلة بهذا الكلام أنه قد يقع الاتفاق على الحكم، وإن وقع الخلاف في الطريق، ولم تمكنهم المنازعة في أن الضروري يمتنع الخلاف فيه من الجم الغفير، ولم تمكنهم المنازعة في مخالفتنا إياهم مع الكثرة، أن هذه القضية إذا ثبتت، منعت','1','291',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (66,'من دعوى الضرورة، فقصدوا دفعا لسؤال بصرف الخلاف إلى المأخذ. قالوا: والذي يقدح في دعوى الضرورة المخالفة في العلم، لا في الطريق.
واحتجوا على ذلك: بأن أهل النظر متفقون على أن الخرم التواتر يحصل العلم، ثم تنازعوا، هل هو ضروري أو نظري، ولم يقدح في حصول العلم؟ فقدر القاضي عليهم: أنا خالفنا في نفس المعرفة.
وبيان ذلك: أن القبيح عند المعتزلة ينقسم إلى ما يقبح من غير شرط، كالظلم والكذب والكفران والجهل، وإلى ما يقبح مشروطا كالإيلام. وإنما حملهم على ذلك أمران:
أحدهما- أنهم مطبقون على أن الله تعالى لا يفعل قبيحا، ولم يمكنهم أن يناكروا في أن الله تعالى [آلم] بعض الخلق، بل كلهم بالموت. فقالوا: بعض الألم حسن.
الثاني- أنهم إنما يستروحون إلى ما يحكمون به من التحسين والتقبيح','1','292',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (67,'(٩/ب) إلى العادات، والعقلاء يستحسنون بعض الآلام ويأمرون بها، وجاءت الشرائع بالحدود والتعزيزات، فلما ثبتت هذه الأمور، قضوا بتحسين بعض الآلام، وقبحوا بعضها، كالآلام التي يتجنبها العقلاء.
والذي قضى المعتزلة بتحسينه من الآلام، ما تقدمته جناية، فيسوغ الألم في حقه، عقوبة وزجرا له ولغيره في مستقبل الزمان، أو ما يرجو بسببه نفعا، كالفصد والحجامة، أو ليثاب عليه، كما يتألم الأجير، ويستحسن ذلك لما يرجو من العوض. ويرون أن البهائم إنما جسن إيلامها، لأنها تثاب في الدار الآخرة، ولو عريت عن الثواب، لكان إيلامها قبيحا. وقد حسنا نحن الألم، ولا نعتقد لها جريمة سابقة، ولا ثوابا لاحقا. والألم إذا عري عن هذين الوجهين كان قبيحا بضرورة العقل عندهم.
قال الإمام: (والمسلك الثاني للقاضي- أنه قال: نرى كذبة تنجي أمما) إلى قوله (فتبلد ولم [يجد] جوابا). قال الشيخ أيده الله: قد تقدم الكلام','1','293',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (68,'أن المعتزلة ينقسم القبيح عندهم إلى قبيح مطلقا، وإلى قبيح مشروط. والمشروط عندهم هو الألم. وإنما حملهم على ذلك ما قررناه من الوجهين السابقين. فلما صادفوا الله تعالى يؤلم، مع استحالة فعل القبيح منه، تعذر عليهم القضاء بالتعميم، ولم يصادفوا في قول الله تعالى كذبا، فعمموا التقبيح.
هذا هو سبب الفرق عند القوم. ونحن نساعد على استحالة الكذب على الله تعالى، وليس ذلك لما اعتقدوه من التقبيح، وإنما ذلك لاستحالة الكذب في كلام النفس على من يستحيل عليه الجهل. على ما يأتي بيانه، إن شاء الله تعالى. وإذا كان الألم إنما حسن في بعض الأحوال، لترتب المنافع المبرة عليه، تصور مثل ذلك في الكذب الذي ينجي الأمم من الأنبياء والأولياء والمسلمين، والإلزام صحيح.
وقد أجاب بعض الناس بأن قال: يتصور الإنجاء من الله تعالى من غير حاجة إلى الكذب، بصرف الظالم ومنعه، فلا حاجة تدعو إلى الكذب. قلنا: وهذا بعينه مطرد في الألم، فإن الله سبحانه قادر على أن ينعم عباده بلا ضرر سابق، فإن الثواب تفضل من الله تعالى، كيف وهو لا يتصور أن يستفيد','1','294',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (69,'بإيلامهم غرضا حتى يترتب عليه عوض؟
وأما الإيلام الذي هو جزاء الأعمال السيئة السابقة، فباطل لوجهين:
أحدهما- أن الله تعالى قادر على منعهم من ذلك. فلئن قالوا: القدرة على الطاعة قدرة على المعصية. فعنه جوابان:
أحدهما- أنا لا نسلم ذلك، والقدرة مقارنة للمقدور، ولا يصح تقدمها عليه. على ما سيأتي بيانه.
الثاني- أن الله تعالى قادر على منعهم جبرا، فليمنعهم قهرا. فكم من ممنوع من الفواحش لعجز أو عنة، فذلك [أولى] مما ذكروه.
والذي شبب به بعض متأخريهم هو اللازم على أصولهم. والإلزام الذي ألزمه صحيح، لأنه إذا انقسم الكذب إلى الحسن والقبيح، والله تعالى لا يمتنع عليه فعل الحسن، فليجز أن يخلق الله تعالى كذبا نافعا (١٠/أ) يكون كاذبا به.','1','295',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (70,'ومعنى قوله: (والكذب عندهم من صفات الفعل، إذ هو من أقسام الكلام). فالكلام عند المعتزلة: فعل المتكلم. والمتكلم عندنا: من قام به الكلام، كما أن المتحرك من قامت به الحركة، لا من فعلها. ولو كانت المعتزلة تقول ذلك وتعترف بقيام الكلام بالقديم، لما تصور منه الكذب، لأن الصفة القائمة لابد أن تكون قديمة، والصدق يضاد الكذب. وإذا ثبت له الصدق، استحال عليه الكذب، فلذلك كان الإلزام على قواعدهم أشد.
قال الإمام رحمه الله: (والمسلك الحق عندي في ذلك، الجامع لمحاسن المذاهب) إلى قوله (ولا يجب عليه أن يعاقب أو يثيب). قال الشيخ وفقه','1','296',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (71,'الله: قوله: والمسلك الحق عندي في ذلك الجامع لمحاسن المذاهب، الناقض لمساوئها. كلام موهم، وذلك أن المتقدم مذهبان: مذهب قوم يقولون: إن الحسن والقبح يرجع إلى وضع الشرع، مفتقر إلى وروده. وقوم يقولون: إن ذلك مدرك عقلا في شيء، ويتوقف إدراك شيء على ورود الشرع، ولكن ليس على الوجه الذي نريده نحن من الإثبات، ولكن يكون العقل قد قصر عن الإدراك، فنبه الشرع على ذلك.
قوله: (محاسن المذاهب)، في ظاهره يدل على أن كل واحد من المذهبين يشتمل على حسن. وكذلك قوله: الناقض لمساوئها. هذا هو ظاهر','1','297',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (72,'الكلام. ويحتمل أن [يريد] محاسن المذاهب، مذاهب الأشعرية، أي كلها حسنة. الناقض لمساوئها: يعني مذاهب خصومهم بجملتها. ولكن الأول أظهر، ويدل عليه قوله: (أن نقول: لسنا ننكر أن العقل يقضي من أربابه اجتناب المهالك وابتدار المصالح). وهذا أولا تجوز في اللفظ، ومسامحة عظيمة، وذلك أن العقول لا يتصور أن تكون مقتضية، لأن العقل، إما العلم','1','298',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (73,'على قول، وإما صفة تشترط في حصول العلم على قول. ولا يصح على المذهبين جميعا أن تكون العقول مقتضية، وإنما المقتضي دواعي النفوس وصوارفها.
والذي يدل على ذلك أن المريض يعلم أن الغذاء والدواء نافع له، ولكن لا يجد من نفسه داعية إلى تناوله، بل نفرة عنه. وإن كان العقل قد كشف له أنه نافع، وكذلك عكسه. فتبين بهذا أن إطلاق الداعي والمقتضي على العقل تجوز أو غلط.
على أن هذا ليس مسألة نزاع، فإنا قد قدمنا أن إطلاق الحسن والقبح باعتبار الأغراض والمنافع والمضار مما لا ينكره أحد. فلا معنى لقول الإمام: لسنا ننكر ذلك. فإن ذلك مما لم يتلكم العلماء فيه فيما بيننا وبين القوم، وإنما الكلام مدار على ما يتعلق بالصفات الثابتة، إما نفسيه، وإما تابعة للحدوث. ولما استقر ذلك عند المعتزلة قضوا مطلقا بالإضافة إلى الخالق والمخلوق.
والإمام إنما أدار الأمر بينهم على المنافع والمضار. وإذا امتنع العقلاء من إطلاق الحسن والقبح فيما بينهم على ما لا ينفع ولا يضر، لخلوه من الأغراض، امتنع إطلاق الحكمين على الله تعالى، لاستحالة الأغراض عليه.','1','299',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (74,'ولا يمتنع إجراء هذين الوصفين فينا إذا [تنجز] ضرر أو أمكن نفع، بشرط أن يكون (١٠/) ذلك إضافيا لا وصفا ثابتا. ولكن هذا بشرط ألا يضاف إلى الله - عز وجل -، لأنه لا يتضرر بنفعنا وضرنا. وتكون نسبة الأفعال بجملتها إليه نسبة الفعل الذي لا يضر ولا ينفع بالإضافة إلينا. فإن العقلاء في هذا لا يطلقون عليه حسنا ولا قبحا، والله تعالى لا يتأثر بنفعنا ولا ضرنا، فيستحيل أن يوجب عليه العقل إثابة أو عقابا.
قال الإمام رحمه الله: (وتتمة القول فيه أنه لو فرض ورود الأمر الجازم) إلى قوله (ولا يغمض معه في النفي والإثبات شيء على المتأمل في هذا الباب). قال الشيخ - رضي الله عنه -: قوله: لو فرض ورود الأمر الجازم من الله تعالى من غير وعيد على تركه، لما كان للحكم بالوجوب معنى معقول في حقوقنا. هذا موضع اختلف الناس فيه. فذهب القاضي أبو بكر إلى أن الله تعالى لو أوجب','1','300',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (75,'شيئا لوجب، وإن لم يتوعد بالعقاب على تركه.
وقد رجع الإمام إلى هذا فيما بعد، وذلك أنه لما تكلم على حد الواجب قال في حد قوم: قالوا: الواجب هو الذي توعد بالعقاب على تركه، هو فاسد، فإنه لو توعد، لوجب تحقيق الوعيد، فإن كلام الله - عز وجل - مستحيل أن يكون فيه الخلف، ويتصور أن يعفو ولا يعاقب. فقد اعترف الإمام أن الواجب لا يتوقف على الوعيد، وذلك الكلام صحيح. فإن أراد الإمام في هذا المكان أن الوجوب لا يعقل إلا بنوع من الترجيح، إما ذما أو غيره، فهذا موضع يجب التثبت فيه.
فاعلم أن الوجوب هو قسم من أقسام الحكم، وله نسبة خاصة إلى المكلفين، على ما مر في بيان معنى الحكم. وهذه النسبة تثبت وإن لم تقترن بوعيد ولا عقاب. فلا يكون اقتران الوعيد من نفس الحكم، ولا يجب اقترانه به اقترانا عقليا، وسنبين في مسألة الواجب الموسع تحقق الوجوب في أول الوقت، وسقوط الذم والعقاب والوعيد [عمن] مات في وسط الوقت.','1','301',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (76,'على أن الوعيد خبر، والوجوب طلب. فكيف تتوقف عقلية الطلب للشيء على الخبر عن غيره؟
فالصحيح أن عقلية الوجوب لا تتوقف على الاقتران بالوعيد ولا العقاب ولا الذم. نعم، يفتقر إلى أمر يبين الوجوب من الندب، وذلك قد يكون بقرائن الأحوال، وهي الغالبة، وقد يكون بصريح المقال، كقوله: أوجبت','1','302',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (77,'وحتمت وألزمت، فهذه الأمور تطلب لتعريف المكلفين خصوصيات الطلب، لا أنها مأخوذة في معقول الطلب.
قال الإمام: (شبه المعتزلة: قال أبو هاشم: من تصدى له أمر مرغوب فيه، وهو يناله بالصدق ويناله بالكذب) إلى قوله (فإن انتهى في التصوير إلى حقيقة الاستواء، لم يسلم له قضاء العقل بتعين الصدق). قال الشيخ أيده الله: اعلم أن المعتزلة حاولوا إثبات كون الحسن والقبح أوصافا ثابتة على الاستمرار، من غير التفات إلى الأغراض بطرق:
قالوا: نحن نعلم قطعا أن من استوى عنده (١١/أ) الصدق والكذب من العقلاء آثر الصدق ومال إليه، وإن لم يطمع بفائدة من أحدهما، ولا خشي من الآخر مضرة، ببل الملك يميل إلى إنقاذ [الغرقى] هلكى، وإن كان يناله في ذلك جهد وتعب به. وإن كان كافرا لا [يرجو] ثوابا في الآخرة، ولا نفعا في','1','303',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (78,'الدنيا، بل لو كان المشرف على الهلاك صغيرا لا يعقل، أو بهيمة لا تميز، لفعل ذلك، لكونه من مكارم الأخلاق، ولا [يرجو] فائدة عاجلة ولا آجل، ولا [يرجو] مدحا ولا ثناء. فلما آثر الإنقاذ على الإهمال من غير غرض على حال، دل على كونه حسنا.
وشبههم كلها ترجع إلى هذا المعنى. وهو محاولة إثبات هذه الأحكام مع انتفاء جميع الأغراض. وهذا الذي ذكره المعتزلة باطل من أوجه:
أقربها- أنه استدلال في مواضع الضرورات، والضروري غير قابل للاستدلال، واستحسان هذه الأمور عندهم واقعة في أقسام الضرورات.
الثاني- أنا لا نسلم استواء الأغراض مطلقا، بل ما انفك قسم من هذه الأقسام عن غرض، ولكن الأغراض قد تدق وتخفى.
وللغلط في هذه الأمور أسباب:
أحدها: أن الإنسان في غالب أمره إنما ينظر لنفسه في حالته الراهنة، ويغفل عن غيره، ولا يتلفت إليه من جهة أن كل طبع مشغوف بنفسه، غير','1','304',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (79,'ملتفت لغيره، بل يغفل عن غير حالته الراهنة من أحواله، فإذا وافقه شيء، قضى بحسنه مطلقا، وأضاف الحسن إلى ذاته، فيقول هو في نفسه حسن، ويغفل عن قبحه لموافقته غرضه، فيكون قد قضى بثلاثة أمور: أحدها- كونه حسنا. والثاني- إضافة الحسن إلى نفسه. والثالث- قضاؤه بذلك على العموم. وهو مصيب في واحد منها، وهو أصل الاستحسان. ومخطئ في اثنين وهما: الإضافة إلى النفس، والقضاء على العموم. فإنه قد يستقبح عين ما استحسن إذا اختلف الغرض.
السبب الثاني للغلط: إن ما هو مخالف للغرض في كثير من الأحوال إلا في حالة نادرة، لا يلتفت الذهن إلى تلك الحالة النادرة، بل لا يخطر بالبال، فيراه مخالفا للغرض في كثير من الأحوال، فيقضي بقبح الكذب مطلقا. فإنه [ألقي] ليه منذ الصبا على سبيل التأديب، أن الكذب قبيح لا ينبغي أن يقدم عليه أحد، ولم ينبه على حسنه في بعض الأحوال، خيفة من أن لا تستحكم نفرته عنه فيقدم عليه. فتقرر تقبيحه عنده مطلقا، فإن لم يكن على ذكره إلا أكثر الأحوال، وهو بالإضافة إليه كل الأحوال، فينفر عنه ويجد التصديق به مطلقا. فإذا عرضت عليه تلك الحال الخاصة، وجد في نفسه نفرة عنها، ويغلبه الوهم على القضاء به على العموم.
السبب الثالث للغلط: أن المقرون بالشيء في بعض الأحوال، قد يسبق الوهم إلى الاقتران على العموم، لاسيما إذا كان الاقتران كثيرا. ومن هذا الوجه (١١/ب) غلط الطاردون في ربط الأحكام بمجرد اقترانها بالأوصاف. على ما سيأتي بيانه، إن شاء الله تعالى. وذلك عمل الوهم.','1','305',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (80,'ومما يشاهد في الاعتياد أنه إذا حضر طعام في إناء يثق العاقل بكونه لم يصادف نجاسة ولا قذرا، ولكنه على شكل الأواني [المعدة] لذلك، فينفر عنه ويتقذر الأكل. وكذلك يسمع الثناء الحسن إن كان قاطعا بأنه [معرى] عن تلك المعاني، فيجد لذلك انشرحا وميلا. وذلك عمل الوهم.
فكذلك لما كانت هذه الأمور في أغلب الأحوال ترتبط بها [الأغراض] نفعا وضرا، إلا في نادر الأحوال، لم تنتبه النفس لتلك الأحوال النادرة، بل لو التفتت إليها، لم تنفر لمقتضى العلم فيها، [واستولى] الوهم عليها. والأمثلة في ذلك كثيرة. بل الإنسان إذا جالس من عشقه في مكان، فإذا انتهى إليه، أحس من نفسه تفرقة بين ذلك المكان وغيره. وذكر الشعر والأوطان والآثار والمنازل، كله من هذا القبيل.
فنرجع إلى الكلام على المثال فنقول: إنما يترجح الإنقاذ على الإهلاك، إما من متشرع [يرجو] الثواب، وإما ممن يقصد أن يعرف بالإنعام والإفضال، وإما أن يتألم برؤية العذاب بغيره، فيزيل الألم عن نفسه، وإما أن يقدر نفسه في تلك البلية، ويقدر غيره قادرا على إنقاذه، وهو معرض عنه، فيستقبح ذلك منه، ثم يعود ويقدر ذلك الاستقباح عائدا عليه، فيزيله عن نفسه. وهذا طبع بني آدم، وهو طبع يتعذر الانكفاف عنه. فإن قدر فقدان هذا كله، فقد وجد الإنقاذ مقرونا بالثناء، فيحسب أنه يقترن به على الإطلاق.','1','306',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (81,'فإن فرض شخص لم يستول عليه شيء من هذه الأوهام، لم يسلم أن عقله يرشده إلى الترجيح على حال.
(شبهة أخرى- فإن قالوا: البراهمة مع [إنكارها] الشرائع قبحت وحسنت) إلى قوله (وقد اشتمل كلامنا على تسليم ذلك). قال الشيخ - رضي الله عنه -: هذا الكلام واضح، لا يفتقر إلى زيادة، وبسطه المسلك السابق.
قال الإمام: (مسألة: ترسم بشكر المنعم: لا يدرك وجوب شكر المنعم','1','307',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (82,'بالعقل عندنا) إلى قوله (فكيف يقضي العقل بوجوب؟ ). قال الشيخ وفقه الله: قد تقدم [الكلام] على الشكر، وأنه يرجع إلى الثناء على المنعم بذكر آلاء المنعم وإحسانه. وقد قضى المعتزلة بوجوب ذلك عقلا، وجعلوه من مواقع الاستدلال.
وفي الشكر مسألتان: إحداهما-[حسنه]. وهذا عند القوم واقع في قسم الضروريات المدركة بضرورات العقل. والثانية- وجوبه عقلا، وهو الذي يتوصل إليه بالنظر العقلي. وإنما منعهم من القضاء بالوجوب ضرورة، تعذر كل حسن، فإن ذلك مناقض لأحكام العقلاء في العادات، وهو مستند القوم فيما يحسنون ويقبحون، فلذلك قضوا بأنه لا يدرك إلا نظرا.
وقد أشار الإمام إلى أصل الاستدلال، ولكن لم يأت بالكلام على وجه مضبوط.','1','308',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (83,'وتحريره أن نقول: لا [يخلو]، إما أن يوجب الشكر لفائدة، أو لا لفائدة، (١٢/أ) ومحال أن يوجبه لا لفائدة، فإن ذلك عبث وسفه. وإن كان لفائدة، فلا [يخلو]، إما أ، ترجع إلى الشاكر أو إلى المشكور. ومحال أن ترجع إلى المشكور، فإنه يتعالى ويتقدس عن الأغراض، وقبول المنافع والمضار، فلم يبق إلا أن ترجع إلى الشاكر، ومنفعة الشاكر إما نفع أو دفع، إما في العاجل وإما في الآجل، فلا منفعة له في الدنيا، بل يتعب نفسه، ويتعرض إلى الآلام والأسقام، ولا منفعة له في [الآجل] باعتبار العقول، لأن الثواب تفضل من الله تعالى يعرف بوعده، فإذا لم يخبر عنه، من أين يعلم أنه يثيبه؟
قال المعتزلة: نسلم أنه لا فائدة له في الدنيا، بل فائدته في الآخرة، فإنه يفيد الشاكر الثواب الجزيل في [الآجل]، والعقل قاض باحتمال التعب','1','309',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (84,'العاجل لارتقاب النفع [الآجل] المربي على التعب المحتمل.
أجاب الإمام بأن قال: (كيف يدرك [العاقل] ذلك بالعقل؟ ومن أين يعرف العاقل هذا؟ والمشكور يقول: لا يجب على نفعك ابتداء، وما نفعتني فأعوضك. فإن قيل: يدرأ الشاكر بالشكر العقاب المرتقب على ترك الشكر. قلنا: كيف يعلم ذلك؟ والكفر والشكر سيان في حق المشكور.
فإن قيل: إن لم يقطع بالعقاب، لم يأمنه. قلنا: إذا تحقق استواء الأمرين، فارتقاب العقاب على ترك الشكر كارتقابه على فعله، ولا يبقى بعد ذلك [مضطرب].
ومما ذكره الأستاذ في مفاوضة له [أن] قال: الشاكر متعب نفسه،','1','310',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (85,'وهو ملك خالقه، فقد يتوقع على تنقيص ملك المالك من غير إذنه فيما لا ينتفع [به] المالك عقابا). هذا من الأستاذ رحمه الله إلزام عظيم على أصول المعتزلة، فإنهم يبنون أمرهم على التحسين والتقبيح. والتصرف في ملك الغير قبيح، والله تعالى هو المالك، ولم يأذن في الشكر، فتعاطيه قبل الأمر به، تصرف في الملك من غير إذن المالك.
فلئن استروحوا إلى الشاهد في استحسان الشكر وقبح الكفر، فذلك مسلم، لأنهم يهتزون للشكر ويغتمون للكفران، والله تعالى يستوي في حقه الأمران، فالمعصية والطاعة في حقه سيان، بل يصح أن يكون الرب سبحانه وتعالى يمنع مما يرتاح به عباده، إجلالا وتعظيما.
وعبادة العباد لا يتأثر بها الباري سبحانه وتعالى على حال، فهو بمثابة من أراد التقرب إلى السلطان بتحريك أنملته في زاوية حجرته، فإنه مستهين بنفسه،','1','311',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (86,'مزر على عقله. وعبادة [الخلق] أجمعين كذلك بالإضافة إلى الله تعالى.
الثاني: أن شكر العباد بأجمعهم ناقص بالإضافة إلى جلال الله تعالى وعظمته وكبريائه، قال الله سبحانه: {ولله الأسماء الحسنى فادعوه بها}. ومع هذا، فكل من أثنى على الله، فثناؤه قاصر. قال رسول الله - صلى الله عليه وسلم -: (لا أحصي ثناء عليك أنت كما أثنيت على نفسك). لاسيما وقد منه في الشرع من إطلاق بعض كلمات المدح، كالعقل والشجاعة، وإن وصف بالعلم والكرم والقدرة.
قال الإمام: (وللخصوم مسلكان: أحدهما- التعلق بتعاقل العقلاء شاهدا) إلى قوله (والرب سبحانه [يتعالى] عن قبول النفع والضر كما سبق). قال','1','312',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (87,'الشيخ أيده الله: (١٢/ب) ما تمسك به المعتزلة في هذه المسائل هو قياس في المعقولات. وقد اختلف الناس، هل يجري القياس في المعقولات؟ على ما سيأتي الكلام فيه، إن شاء الله تعالى.
والمعتزلة ينكرون ذلك، ثم يتمسكون في المعقولات بأقيسة، مع عروها عما يعتبر في القياس العقلي من الجوامع العقلية. على ما سنبينه بعد ذلك، إن شاء الله تعالى. والذي يختص بهذا المكان أنه يرد عليه أوجه من الاعتراض:
أحدها- منع حكم الأصل. فإنا لا نسلم اتفاق العقلاء على وجوب الشكر لكل منعم، لا قبل ورود الشرع ولا بعد وروده.
الثاني- فساد وضع القياس. فإن هذا النوع من القياس لا يجير في المعقولات.','1','313',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (88,'الثالث- الفرق بما ذكره الإمام. وقد فرق بفرق صحيح وكلام بالغ.
الرابع-[النقض]، فإن الله تعالى قد تحسن منه أمور لا تحسن من الخلق لو فعلوها. وذلك أن السيد لو ترك عبيده [وإماءه] يزنون ويرتكبون الفواحش، وهو مطلع عليهم، عالم بهم، قادر على منعم، ولم يمنعهم، لعد ذلك قبحا. وقد فعل الله بعبيده، ولم يقبح منه.
فإن قيل: تركهم لينزجروا بأنفسهم، فيستحقون الثواب. قلنا: قد علم أنهم لا ينزجرون، فليمنعهم كرها، فكم من ممنوع من الفواحش بعنة وعجز، فذلك [أولى] من تمكينهم مع العلم بأنهم لا ينزجرون.
قال الإمام: (والمسلك الثاني- في توقع العقاب) إلى قوله (مع قيام البرهان القاطع الذي أقمناه على مخالفتهم). قال الشيخ - رضي الله عنه -: وجه تقرير','1','314',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (89,'الشبهة على أصولهم أنهم قالوا: ما أفضى إلى المحال، فهو محال. وهذا كلام صحيح.
ثم قالوا: حجج الأنبياء متوجهة على الخلق، وهي حق، وما أفضى إلى إبطالها وامتناع توجيهها، فهو باطل. وهذا أيضا كلام صحيح.
قالوا: والقصر في تلقي الوجوب على الشرع يفضي إلى إبطاله ورفعه بعد ثبوته، للزوم الدور، وتوقف وجوب النظر على تقدير الشرع، وتوقف تقدير الشرع على نظر الناظر، وتوقف نظر الناظر على وجوب النظر.
وأما قول الإمام: (وهذا أولا ليس برهانا [على] وجوب النظر عقلا).','1','315',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (90,'يعني أن هذا الكلام غنما يفيد مع من اعترف باستقرار الشرائع، وأما من جحد ذلك، فإن هذا الكلام لا ينتج له وجوب النظر عقلا.
وهذه الشبهة أجاب عنها بوجهين: أحدهما- قوله: (ثم نفس العقل لا يدرك به وجوب النظر). اعلم أنا قد قدمنا أن ما يدرك بالعقل من المعقولات ينقسم إلى أولي، وهو الذي يصادف في أنفس العقلاء، غير مفتقر لسبب، ولا مقيد بشرط، وهذا هو الذي يجب اشتراك العقلاء فيه. وليس وجوب النظر عند المعتزلة من هذا القبيل. إذ لو كان كذلك، لما خلا عاقل في مضطرب','1','316',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (91,'أحواله عن العلم بوجوب النظر.
فلابد- والحالة هذه- أن يكون من القسم الثاني الذي يفتقر إلى سبق نظر. وإذا افتقر إلى سبق نظر، تأتى للعاقل التوقف حتى يعلم وجوب النظر، كما (١٣/أ) تأتى له ذلك عند دعاء الرسول، فيقول: لا أنظر حتى أعلم وجوب النظر، ولا أعلم وجوب النظر حتى أنظر، فيؤدي أيضا إلى الدور. فما لزمنا في السمع المنقول، ينعكس عليهم في قضيات العقول.
فإذا رجعوا إلى دعوى الضرورة، لم يستقم لهم ذلك مع مخالفتنا لهم.','1','317',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (92,'أم كيف ينقدح ما قالوه مع قيام البرهان القاطع الذي أقمناه على مخالفتهم، فيرجعون إلى النظر؟
قال الإمام: (فإن قالوا: يبعث الله إلى كل مدعو ملكا) إلى قوله (عما هذوا به). قال الشيخ - رضي الله عنه -: وهذا كلام واضح، ولئن وقع الاكتفاء في الإقدام على فعل النظر بخطور الخواطر حتى يكون ذلك قاطعا للدور، فخطور هذه الخواطر عند بعثة النبي أولى. فليقع الاكتفاء بذلك.','1','318',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (93,'قال الإمام: (ثم التحقيق [في ذلك] أن النظر ممكن) إلى آخر المسألة. قال الشيخ وفقه الله: قوله: ثم التحقيق [في ذلك] أن النظر ممكن إلى آخره. اعلم أن هذا الخيال إنما يلزم على القول باستحالة تكليف ما لا يطاق عقلا. والصحيح عندنا جواز ذلك عقلا. وسنبينه في موضعه، إن شاء الله تعالى.
فإذا صح ذلك، لم يكن للإلزام ورود على حال. فإن تنزلنا على','1','319',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (94,'امتناعه، فليس بشرط في التكليف بالاتفاق علم المكلف به. إذ لو كان كذلك، لما تصورت معصية ممن يجهل الحكم، ولم يكن الكفار عصاة بتكذيب الشرائع واعتقاد نفي النبوات. وفي قواطع السمع ما يدل على خلاف ذلك والشرائع مستقرة ببعثة الأنبياء، وتأييدهم بالمعجزات، وحصول العقول، والتمكن من الاستدلال.
وقوله: لا أنظر حتى أعلم [وجوب] النظر، ولا أعلم وجوب النظر حتى أنظر، كلام صحيح، ولكن لا تتوجه له به حجة، إذ لا حجة للخلق بعد إرسال الرسل. ويحمل ذلك من المدعو على الجدال والعناد.
ومثاله: ما لو قال الأب لولده: التفت فإن رواءك سبعا عاديا يهجم عليك. فيقول: لا ألتفت حتى أعلم وجوب الالتفات، ولا أعلم وجوب الالتفات ما لم أعلم السبع، ولا أعلم السبع حتى ألتفت. فيقال له: لا جرم تهلك وأنت غير معذور.','1','320',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (95,'وكذلك النبي إذا بعث، وأيد بالمعجزة، ودعا الخلق وقال: تعلمون ذلك بأدنى نظر في معجزتي، إذ العقول حاضرة، والأدلة منصوبة، والبراهين موجودة، والشواهد مشهودة. فهذا كلام واضح، لا تناقض فيه، ولا مانع يمنع من التحصيل.
وأصل الشبهة غلط الخصوم واعتقادهم عنا أنا نقول: لا يستقر الشرع إلا بعد النظر. ولو كنا نقول ذلك، للزم السؤال. ولكنا نقول: الشرع مستقر ببعثة الرسل وتأييدهم بالمعجزات، نظر الناظر أو لم ينظر.
وقول الإمام: (ولا يشترط في وجوب الشيء علم المخاطب بوجوبه عليه، بل يشترط تمكنه من العلم [به]). يجب النظر فيما يرجع عليه هذا الضمير، وهو (الهاء)، في (به): والصحيح أنه يرجع على الشيء، لا على الوجوب. فمعنى الكلام: لا يشترط في وجوب الشيء علم المكلف بوجوب ذلك عليه، بل يشترط تمكنه من العلم بالشيء. وإنما قلنا (١٣/ب) ذلك، لأن العلم بوجوب النظر مستحيل قبل انتهائه نهايته، فإذا كان العلم مستحيلا،','1','321',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (96,'فكيف يكون إمكانه شرطا في الوجوب، وإمكان المستحيل مستحيل؟
ولو كان الممكن منه شرطا، لصح أن يكون حصوله شرطا، فلذلك حكمنا بأن الضمير إنما يعود على النظر، لا على العلم بوجوب النظر. ومعنى الكلام: صحة الوجوب على من يتمكن من النظر دون من يستحيل منه، كالأطفال والمجانين.
وقال: (والسر في ذلك أن النظر الأول لا يتصور إلا كذلك، سوا فرض أخذه من السمع المنقول، أو من مدارك العقول). هذا كلام صحيح، إذ لا يتصور أن يعلم بوجوب واجب من لم يعلم الوجوب، فمن لم يستقر عنده بعد الحاكم، كيف يتصور أن يعلم حكمه؟ ومن هذا قضينا بأنه لا يتصور أن يكون الناظر عالما بالأحكام الشرعية إلا أ، يكون عالما بقواعد العقائد. [ومن]','1','322',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (97,'هذا قيل: إن القربة التي لا يتصور التقرب بها إلى الله تعالى هي النظر الأول.
وأراد [ههنا] بالتقرب: قصد الفاعل إلى إيقاع العمل طاعة. وهذا القصد قد يستحيل اشتراطه في بعض الطاعات، وقد يجب تحصيله في بعضها. فإذا وقع له الإخلال به، لم يصح العمل. وقد يجوز أن يحصل، ولكن لا يوجب الشرع ذلك.
فأما الموضع الذي يستحيل القصد فيه، فهو النظر الأول، وكذلك القصد عند موجبه، وكذل المعرفة المترتبة على النظر الأول، فإنه لا يتصور إيقاعها طاعة، إذ الناظر لا يقصد إلى معرفة مخصوصة، إذ العلم يضاد النظر، وهو لو علم لم ينظر، لأنه لا يدري ما يفضي إليه النظر، فلا يتصور منه القصد إلى التقرب بالمعرفة التي لم تحصل له، ونظره ينافيها. وكذلك إدارة الطاعة أيضا لا يشترط أن يقصد بها التقرب، غذ لو افتقرت إلى إرادة، لتسلسل القول.','1','323',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (98,'وأما الموضع الذي يجب تحصيل هذا القصد فيه: فكل عبادة بدنية لم يظهر لها مقصد عاجل، فإنها لا تقع إلا إذا قصد بها التقرب. وسيأتي لهذا تقرير حسن في موضعه من هذا الكتاب.
وأما الموضع الذي يصح تحصيلها ويزداد ثواب المكلف بسببها، وإن كان لا يشترط ذلك في حقه: فاستصحاب النية بالتحقيق في أثناء الأعمال، فإن ذلك معفو عنه [تكرما] من الله تعالى على العباد. فلو اتفق حصول النيات، لكان [أعلى] وأفضل.

قال الإمام: <span data-type="title" id=toc-34>(مسألة: لا حكم على العقلاء قبل ورود الشرع، بناء على أن الأحكام هي الشرائع بأعيانها) </span>إلى قوله (على من لا ينتفع ولا يتضرر). قال الشيخ - رضي الله عنه -: قوله: لا حكم على العقلاء قبل ورود الشرع، نفي عام لجميع الأحكام،','1','324',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (99,'لكن المراد نفي الأحكام الشرعية بجملتها. على ما سيأتي بيان قسمتها.
واختلفت مذاهب المعتزلة في الأحكام قبل ورود (١٤/أ) الشرع على ثلاثة مذاهب: ذكر الإمام مذهبين، ولم يذكر الثالث. والذي أغفله هو مذهب الوقف. إلا أن يكون لم ير الوقف مذهبا. إذ الواقف لم يحكم لا بنفي ولا بإثبات. ولكنه رد على الفريقين جميعا.
ومن العجب أنهم حكموا بذلك فيما لا يقضي العقل فيه بتحسين ولا تقبيح، ثم حظر بعضهم مستندا إلى العقل، وتوقف بعضهم مستندا إلى العقل.','1','325',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (100,'وهذا غريب أن يحكم بالعقل في موضع لا يقضي العقل فيه بحسن ولا قبح. وإنما احتيج إلى فرض المسألة فيما لا يقضي العقل فيه بحسن ولا قبح، لأن القوم متفقون على أن ما أدرك العقل حسنه، فليس بمحظور. وما أدرك العقل بقبحه فليس مباح. فلذلك كانت المسألة مفروضة فيما لا يقضي العقل فيه بحسن ولا بقبح. ولذلك ورد الإشكال وظهر التناقض ظاهرا.
ولكن مراد القوم بهذا القسم الذي زعموا أنه يتلقى من الشارع، على حسب ما تقدم بيانه. هذا موضع الاختلاف. ومعنى قولهم: فيما لا يقضي العقل بحسنه ولا بقبحه، أي بالنظر إلى صفات نفسه على الاختلاف السابق أيضا. فإن الذي تستقل العقول بدركه لا يتوقف على ورود الشرائع، بلا لا يتصور أن يتصرف الشارع فيه بنسخ أو نفي أو إثبات، ولا يتصور أن يتغير حكمه.
فهذا القسم خاصة إذا لم يرد الشرع بحكم فيه، تختلف مذاهبهم فيه.','1','326',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (101,'والذي تقتضيه أصولهم هو الجاري عليها الوقف. وغنما كان هذا هو الجاري على أصولهم، لأن الشرع عندهم إذا ورد بحكم في هذا، فإنه إنما يدر به بناء على صفة تقتضي معنى أو طلبا أو إذنا. فالشرع في معنى الكاشف لمعقول الصفة المستورة، لا منشئ [حكم] على الحقيقة.
وإذا كان كذلك، فما من فعل لم يظهر حسنه ولا قبحه، إلا ويتصور أن يرد الشرع بطلبه، فيتبين العقلاء أنه كان على صفة الحسنـ أو يمنعه فيظهر للعقلاء أنه كان على صفة القبح، أو بالتخيير فيه فيتبين للعقلاء استواء الأمرين وامتناع ترجيحه. وهذا المذهب الذي ذكرناه أنه أجرى المذاهب على أصولهم، هو الذي أغفل الإمام نقله. ومذهبنا نحن أيضا أن الأفعال قبل ورود الشرع على الوقف. ولكن المراد بالوقف عندنا غير ما أراده المعتزلة من الوقف،','1','327',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (102,'لالتباس الأحكام بسبب التباس الصفات. بل المتكلمون قاطعون بانتفاء الأحكام قبل ورود الشرائع، بناء على استحالة تكليف ما لا يطاق عقلا.
ومن التكليف بالمحال عندهم التكليف من غير دليل. وأما نحن فنقف عن الحكم قبل ورود الشرائع، تقديرا لاحتمال أن يكون الله تعالى كلف الخلق وإن لم يجعل لهم إلى معرفة الحكم طريقا. لا لأن الأحكام تتبع الصفات، وهي ملتبسة، ولكن لما جاءت الشرائع أخبرت عن انتفاء حكم الله تعالى عن العباد قبل ورود الأنبياء. قال الله تعالى: {وما كنا معذبين حتى نبعث رسولا}. فمذهبنا يساوي مذهب المعتزلة في الحكم، ويفارقه في الطريق. وأما أصحاب الحظر، فيصح (١٤/ب) أن يتمسكوا بوجهين:','1','328',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (103,'أحدهما- أن الشيء إذا احتمل أن يكون حسنا، وأن يكون قبيحا، فيكف عنه، لما ألف عند العقلاء من قضية الاحتياط. ويشهد له من الشرائع تغليب أمر الحظر عند اختلاط الميتة والمذكاة.
الثاني- أن التصرف في ملك الغير بغير إذنه قبيح، والله تعالى هو المالك، ولم يأذن. والإلزام من الإمام صحيح في حظر الشيء وضده، مع استحالة الخلو عنهما جميعا، (فإن حظروا جميعها، كان ذلك تكليف ما لا يطاق، وإن خصصوا بالحظر شيئا عن شيء من غير تحسين العقل وتقبيحه، لم يخف سقوط هذا المذهب).
وقول الإمام: (وإن خصصوا الحظر بما يعتقدون جواز الخلو عنه أصلا','1','329',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (104,'لمستندهم أن التصرف في ملك الغير قبيح بغير إذنه). وهذا القسم الذي ذكره الإمام غير متصور عندي، بل ما من شيء يجوز العرو عنه أصلا، إلا وله ضد لابد من التلبس به. وتصوير شيء لا ضد له يصح العرو عنه محال.
وفرض أمر يجوز العرو عنه وعن ضده باطل. فما ذكره ليس بعذر، ولا يمكن تنزيل المذهب عليه.','1','330',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (105,'وقوله: (وأما أصحاب الإباحة، فلا خلاف على الحقيقة بيننا وبينهم) إلى آخره. ليس الأمر على ما قال. بل الخلاف قائم بيننا وبينهم، فإنا نقول: المباح اسم مفعول يستدعي مبيحا، فإن أسماء المفعولين تستدعي المصادر، وكذلك أسماء الفاعلين، والإباحة عندنا: هي التخيير من الله تعالى بين الفعل والترك، وذلك إنما يعرف بخبر الله تعالى، فإذا لم يخبر عنه، لم يتصور علم ذلك. والمعتزلة تزعم أنها توصلت بالعقول إلى الإذن، وذكروا وجوها نذكرها ثم نبين فسادها.
فمنها- أنهم قالوا: إن الله خلقها من غير غرض له فيها، مشتملة على منافع [علمناها]، فلا مضرة عليه في الانتفاع [بها]، إذ لا يتصور أن تنقص مملكته بسبب ذلك، فهذه أشياء كل واحد منها يدل على الإذن.','1','331',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (106,'فنقول: هذه أوهام باطلة وخيالات فاسدة.
وأما قولهم: إنه لما خلقها نافعة، كان ذلك دليل الإذن، ففاسد، فإن إعلام المالك إيانا أن طعامه نافع لا يكون إذنا، وكذلك يمكن أن يكون إعلام الباري إيانا أنها نافعة ليس إذنا، بل ليدرك ثواب الاجتناب.
فإن قالوا: الباري سبحانه وتعالى لا يتضرر، والواحد منا يتضرر بالتصرف في مخلوقاته، فإنه يجري مجرى التصرف في السراج بالاستضاءة، وفي الجدار بالاستظلال، ومنع ذلك قبيح. قلنا: إن صح ذلك، فليقبح من الله تعالى منع عباده من لذاتهم وقضاء أوطارهم، فإن ذلك لا يضره بوجه.
فإن قالوا: إنما منع الله سبحانه العباد مما يعلم أنه يضرهم، وإن كانوا لا يقفون على وجه ذلك. فلنا: فما من شيء إلا ويمكن أن تكون فيه مضرة خفية، لا جرم فتوقف الإذن على ورود الشرع.
فإن قالوا: فالله سبحانه قادر على أن يخلقها عرية عن الطعوم، وكان','1','332',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (107,'قادرا على أن لا يخلق فينا الذوق، فخلق الطعم فيها والذوق فينا، دليل على أ، هـ أراد انتفاعنا بها. قلنا: (١٥/أ) الأشعرية وأكثر المعتزلة مطبقون على استحالة عروها عن الطعوم. فمن سلم أن ذلك مقدور؟ وإن سلم، فلسنا نسلم أنه خلقها لذلك. فعله [خلقه] لا لعلة، أو لعلة خلقها لندرك ثواب اجتنابها.
ومن سلم أن أفعال الله تعالى كلها معللة؟ بل خلق العالم كله لا لعلة. ثم نقول: بما تنكرون على أصحاب الحظر إذا تمسكوا بأن التصرف في ملك الغير بغير إذنه قبيح؟ وبم تردون على أصحاب الوقف إذا توقفوا حتى يرد الشرع لا لالتباس الصفات؟ فلا يبقى مع القوم إلا محض التحكم.
والكلام في هذه المسألة والتي قبلها فرع من فروع التحسين والتقبيح. وفي بناء المسألتين على الأصل السابق كفاية في إبطال مذاهب القوم.','1','333',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (108,'وعلى الجملة، فالأوهام غالبة على القوم في اعتبارهم أفعال الله تعالى بأفعال عباده مع استيلاء الأغراض على البشر واستحالتها في حق الله - عز وجل - {وما قدروا الله حق قدره}. {لا يسئل عما يفعل وهم يسئلون}. نسأل الله العصمة من الخذلان.

قال الإمام: <span data-type="title" id=toc-35>(فصل- يجمع التكليف ومعناه، ومن يكلف، وما يجوز التكليف به) </span>إلى قوله (وذلك يستدعي قولا مقنعا في تكليف ما لا يطاق).
قال الشيخ - رضي الله عنه -: التكليف لا يعقل إلا باجتماع أربعة أمور: التكليف، وهو','1','334',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (109,'المصدر، والمكلف، وهو من يقوم به التكليف، والمكلف هو الذي استدعي منه الفعل، والمكلف به، وهو المطلوب. وإنما تشتق أسماء الفاعلين والمفعولين من المصادر.
فلابد من تقديم الكلام على التكليف، وهو اسم مشتق من الكلفة، وهذا هو المعنى الغالب في الأحكام. وإلا فقد يطلب الله تعالى من العباد فعل ما لا مشقة عليهم فيه، وينهاهم عما لا مشقة عليهم في تركه، كالأمر بتناول الطعام والشراب، والنهي عن تناول السموم والقاذورات. ولكن هذه صور نادرة، فأطلق الاسم باعتبار الأمر العام، والخيرة تناقض التكليف، والإلزام التكليف فيه ظاهر، لا جرم اتفق القاضي والإمام على صحة إطلاق اللفظ على الوجوب والحظر، وعلى منع الإطلاق على الإباحة، واختلفا في الإطلاق','1','335',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (110,'على الندب والكراهة.
والذي منع الإمام من الإطلاق، اعتقاده أن الندب والكراهة يقترنان بالتخيير بين الفعل والترك. فقال: التخيير مع التكليف متناقض. والصحيح','1','336',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (111,'عندنا ما قاله القاضي. والتخيير يضاد الكراهة والندب، كما يضاد الوجوب والحظر، غذ التخيير: عبارة عن الإذن في الفعل والترك والتساوي بينهما بتسوية الشرع. وهذا مفقود في الترجيح. وليس المراد في نفي الخيرة الطبيعية، فإن ذلك يتطرق إلى الواجب والحظر، فإذا تحقق الترجيح بالطلب، امتنع التخيير على التحقيق. فالتكليف محقق، والطلب جزم.
وأما قول الأستاذ، فلا يستقيم على مراعاة الاشتقاق بحال، إلا أن يقول: إن هذا الاسم صار لقبا على الأحكام بجملتها، فيكون هذا من باب التقليد المحض. وهذا يداني قول الإمام في أول الكتاب: (إن الفقه: هو العلم بأحكام التكليف).
وأما الركن الثاني: وهو المكلف، وهو الذي يقوم به التكليف، وأصله كل طالب وملزم. لكن قد حققنا أنه لا يجب إلا طاعة الله (١٥/ب) - عز وجل -، وطاعة من أوجب الله طاعته. فإن وقع النظر في التكليف، فهو عام، وإن وقع النظر فيمن تجب طاعته، فهو مختص بالله تعالى.','1','337',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (112,'وأما الركن الثالث: وهو ما يكلف به، قال الإمام في بيانه: (فقد نقل النقلة عن أبي الحسن أنه كان يجوز تكليف ما لا يطاق) إلى قوله (ولا ينجي من ذلك تمويه المموه بذكر الكسب). قال الشيخ أيده الله: قول الإمام: وهذا سوء معرة بمذهب الرجل. إن ظاهر النقل يتضمن انقسام التكاليف إلى ما يطاق، وإلى ما لا يطاق، لاختلافهم في وقوع المجوز مع القطع بوقوع التكاليف. فإن كان كل تكليف تكليفا بما لا يطاق، لم يتصور الاختلاف في الوقوع.','1','338',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (113,'وما ذكره الإمام من كون التكاليف- على مقتضى مذهبه- كلها على خلاف الاستطاعة، كلام يفتقر إلى بسط وشرح، وسنبينه في آخر هذا الفصل، إن شاء الله تعالى.
[وقوله]: وما اعتذر به القوم من أن الأمر بالشيء نهي عن ضده. فالرد عليهم ظاهر، كما ذكره الإمام.
وقولهم: (إن المكلف وإن لم يكن قادرا على المأمور به، فهو قادر على ضد من أضداده، وهو المنهي عنه). كلام باطل، وغفلة من المورد. والجواب أيضا حائد، فإن الضد الذي هو قادر عليه، ملابس له، ليس منهيا عنه، وإنما هو منهي عن مثله، مما لم تتعلق قدرته به. وأما الذي تعلقت به','1','339',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (114,'القدرة وقت توجه الأمر عليه، فليس منهيا عنه. فلا يتعلق التكليف أمرا ونهيا إلا بمعدوم يمكن حدوثه.
وأما الوجه الثاني: وهو أن العبد مطالب بما هو فعل ربه، فكلام صحيح. وقوله: (ولا ينجي من ذلك تمويه المموه بذكر الكسب). فإن المراد بالكسب: تعلق القدرة الحادثة بالمقدور من غير أن تكون فيه مؤثرة بحال. وهذا هو مذهب الشيخ أبي الحسن.','1','340',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (115,'وقد اختلف الناس في هذا: فذهب أبو الحسن إلى ما حكيناه عنه من أن الله تعالى خلق القدرة الحادثة تؤثر كسبا، وهو الجهة الخاصة للحركة، ككونها حركة وكتابة، فيضاف إلى الله تعالى الخلق، ويضاف إلى العبد الكسب فيقال: العبد كاتب.
وذهب المعتزلة إلى أن الأفعال الاختيارية تضاف إلى العبد إيجادا وإبداعا، وليس للقدرة الأزلية في الأفعال الاختيارية أثر على حال.
وذهبت الجبرية إلى أن الأفعال واقعة بقدرة الله تعالى، كما صرنا إليه، ولكنهم ينكرون القدرة التي قضينا نحن بكونها مقترنة بالأفعال الاختيارية. والدليل على بطلان ما ذهب إليه المعتزلة من استبداد العبد بأفعال عن ربه، ما يدل على استحالة [إلهين]، وثبوت القدرة لله تعالى على الأمثال، واستحالة تغير القديم على كل حال. والحركة الاختيارية مماثلة للحركة الضرورية على القطع. إذ حقيقة الحركة لا تختلف. فلو لم تصلح القدرة القديمة للاختيارية،','1','341',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (116,'لم تصلح للضرورية، فإن صلحت، ولكنها منعت، كان ذلك باطلا. إذ الصفة القديمة يستحيل منعها. ولئن كان العبد مانعا ربه بقدرة اخترعها الله له، فليكن الرب مانعا عبده لقدم قدرته أولى. وهذا قاطع في الرد. وتمام بسطه في الكلام (١٦/أ). وهذا بعينه يرد على القاضي في إضافة الكسب إلى القدرة الحادثة.
والرد على الجبرية بين، فإنا ندرك بالضرورة تفرقة بين الحركة الاختيارية والحركة الضرورية، مع القطع بتماثلهما، فاستحال أن يرجع الفرق إلى ذواتهما. فلم يبق إلا رجوعه لأمر مقترن بإحداهما دون الأخرى. وهذا المقترن مدرك حالة التحرك، فبطل المعنى الذي وقع به الاقتران متقدما على الحركة، فالمعنى مدرك ضرورة، وهو المعبر عنه بالقدرة الحادثة. ونحن نقتصر في هذا المجموع على الرمز إلى الأدلة الكلامية، إذ ليس هذا موضع استقصائها.
ثم قال الإمام: (فإن قيل: فما الصحيح عندكم في تكليف ما لا يطاق؟ قلنا: إن أريد بالتكليف طلب الفعل) إلى قوله (فلا يحتمله هذا الموضع).','1','342',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (117,'قال الشيخ - رضي الله عنه -: اقتصر الإمام في هذا المكان على مجرد الدعوى ونقض المذهب. فإنه لم يدل على ما قال. وإنما قال: إن أريد بالتكليف طلب الفعل، فهو محال. فكأنه قال: إن أريد بالتكليف التكليف، فهو محال. فاقتصر على الدعوى وأتى بها في صورة التقسيم. ثم هو أيضا تقسيم باطل؛ فإنه إنما صح القسمة بعد الاشتراك في جهة الافتراق في غيرها. ومجرد الصيغة لم تشارك التكليف في شيء، فما معنى الاستفسار في غير موضع الاحتمال وعدم الإجمال؟ وليس السؤال عن مجرد ورود صيغة (افعل) نحو قول الشاعر:
ألا أيها الليل الطويل ألا انجلي ... ..... .... ...','1','343',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (118,'أو ما يضارعه. نعم، إذا وقع النظر في محامل الصيغ، فإذ ذاك يحسن إيراد هذه الأقسام.
وقد احتج في منع تكليف المحال بخيال، وفي إبطاله إزالة إشكال. وذلك أنهم قالوا: التكليف على وفق العلم، إذ هو من جملة كلام النفس. وما لا يعقل، لا يقوم بالنفس خبر عنه، وإذا استحال قيام الخبر، كيف يعقل قيام الطلب؟ وما لا يطاق، لا يعقل، فلا يقوم بالنفس خبر عنه. فلا يتصور طلبه. وهذا كلام قوي، وهذا الكلام يورده المعتزلة وبعض أصحابنا.
أما من أورده من الأشعرية، فجوابه من ثلاثة أوجه:
أحدها- ما صودف في الشرائع من تكليف العاصي الطاعة، والكافر الإسلام، مع القضاء بأن القدرة الحادثة مقارنة للمقدور، غير مؤثرة فيه، والمطلوب منه فعل اختياري. وفعل اختياري من غير قدرة عليه، غير معقول. فإن ذهب ذاهب إلى أن العاصي مأمور بالطاعة، فقد خرج عن الدين، وجحد متواترات الشريعة. وإذا وقع الاعتراف بالتكليف، مع فوات القدرة، فقد تحقق تكليف ما لا يطاق. فإن تمسك بذلك المعتزلة، فالجواب عنه من وجهين:
أحدهما- أن ما لا يطاق لو كان بمثابة المذهول عنه، لاستحال التعبير عنه قصدا، ويصح للواحد منا أن يقول لعبده: اجمع بين الحركة والسكون، متكلما على الحقيقة، لا يعد ذلك من أبواب الذهول. نعم. يعلم أن الطالب لم يرد','1','344',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (119,'الجمع، لعلمه بالاستحالة، والطلب عندنا لا يتوقف على الإرادة.
الثاني- أن الجمع معقول، والضدان معقولان، وقد أمر أن يضيف الجمع المعقول للضدين المعقولين، فنبا المحل (١٦/ب) عن قبول ذلك، فقيل: إن ذلك غير معقول. وكذلك نعلم استحالة الجمع بينهما. ولو كان ذلك لا يقوم بالنفس من حيث الجملة، لم يتصور القضاء عليه بالاستحالة، كما لا يتصور ذلك في المذهول عنه بالكلية، فإن ذلك لا يمكن التعبير عنه قصدا.
وهذان الوجهان أيضا اللذان ذكرناهما في الرد على المعتزلة، نرد بهما مع الوجه السابق على أصحابنا.
فإن قيل: فما تقولون في تكليف ما لا يطاق؟ قلنا: نذكر أولا صوره، وما يدل عليه لفظه. فإنه يدل عند الأصوليين والمتكلمين على أربعة أوجه:
أحدها- ما لا يعقل على حال، كالجمع بين الضدين، وقلب الأجناس، وإعدام القديم، وإيجاد الموجود، إلى ما يضاهي ذلك مما لا يعقل.
الثاني- إطلاقه على ما لا يدخل تحت مقدور البشر، وإن كان ممكنا في نفسه، كخلق الجواهر والأعراض، فغن ذلك لا يدخل تحت القدرة الحادثة. وبرهانه: أنه لو كان مقدورا لهم، لأدركوا من أنفسهم عجزا عنه، إذ المحل القابل للشيء وضده. ألا ترى أن الحركة لما كانت من','1','345',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (120,'جنس المقدور، أدرك العاقل عجزه عنها، واقتداره عليها؟ ونحن لا ندرك من أنفسنا عجزا عن خلق الأجسام والألوان، ثبت بذلك أنها ليست من جنس مقدورنا.
الثالث- إطلاق ما لا يطاق على ما يقدر العباد عليه في الاعتياد، وإن كان من جنس مقدورهم، كالطيران في الهواء، والمشي على الماء، وجميع ما لم تجر العادة به، مع كونه من جنس مقدور العباد.
الرابع- إطلاق اللفظ على جنس المقدور في الاعتياد، ولكن لم يخلق الله تعالى للعبد قدرة عليه. ومن هذا القبيل جميع الطاعات التي لم تقع، والمعاصي الواقعة. فإن الله تعالى لم يقدر العاصي على ترك المعصية، ولا الممتنع من الطاعة على فعلها.
والذي نختاره أن التكليف بجميع ذلك جائز من جهة العقل، وقد دللنا على صحة ذلك فيما سبق. وأما الوقوع السمعي فلم يقع عندنا من هذه الأقسام إلا القسم [الأخير]. فأما ما سواه، فلم يقع. ودل على ذلك استقراء الشرائع ونصوص الكتاب.
وقد قال الله تعالى: {لا يكلف الله نفسا إلا وسعها}. وقال حكاية','1','346',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (121,'عن عبادة الصالحين: {ربنا ولا تحملنا ما لا طاقة لنا به}. وقال في الحديث الصحيح: (إن الله تعالى قال: قد فعلت). وذلك لسر فهم من الشرائع أنها جاءت على حسب ما ألفه الناس في مطالبهم، واستقر في أنفسهم من عوائدهم.
وليس يجري في الاعتياد أن يطلب السيد من العبد الأعمى حسن انتقاد الدراهم والدنانير. فالشرع من أوله إلى آخره جرى على هذا المذاق. وقد قال الله تعالى: {وجزاء سيئة سيئة مثلها}. وقال: {فمن يعمل مثقال ذرة خيرا يره. ومن يعمل مثقال ذرة شرا يره}. وقال: {[من] يعمل سوءا يجز به}. و {من جاء بالحسنة فله عشر أمثالها}. ونظائرها مما لا يمكن حصرها، مصرح بتعلق التكاليف بما ألف البعاد أنه مقدورهم. وبذلك يحسن الوصف بالطاعة والمعصية. (١٧/أ) وبالله التوفيق.
قال الإمام: (فإن قيل: [فقد] كلف الله أبا جهل أن يصدقه فيما','1','347',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (122,'يخبر به) إلى قوله (فأما تكليفه الجمع بين النقيضين في التصديق فلا). قال الشيخ - رضي الله عنه -: تقدير السؤال أن الله تعالى أمر أبا جهل أن يصدق رسول الله - صلى الله عليه وسلم - في كل ما أخبر به، وكان أخبر بأنه لا يصدقه، ووجب عليه تصديقه في هذا الخبر، وهو أنه يكذبه. فحاصل التكليف على هذا التقدير أن يكون مكلفا بالتصديق على الإطلاق، وفي ضمنه التكذيب. وهذا تخيل السائل. فأنكر الإمام هذا. وقال: يستحيل التكليف بالتصديق على هذا الوجه، بل كلفه أن يصدقه في أنه رسول الله، وذلك ممكن، فإن الأدلة منصوبة، والعقل حاضر، إذ لم يكن مجنونا، غير أن الله تعالى علم أنه سيترك ما كلف به حسدا وعنادا. والعناد لا يصير الممكن مستحيلا، وتعلق العلم بالمعلوم لا يغيره. ولو كان','1','348',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (123,'العلم يؤثر في المعلوم، لما تعلق العلم بالقديم سبحانه وتعالى.
وقد اختلف المتكلمون في هذه المسألة، وهو أن خلاف المعلوم، هل هو مستحيل أو ممكن؟ فقال قائلون: هو مستحيل، إذ حد المستحيل: ما لو قدر وجوده، للزم منه محال. والجائز: ما لو قدر وجوده، لم يلزم منه محال. وخلاف المعلوم لو قدر وجوده، لزم منه محال، وهو انقلاب العلم جهلا.
وقال قائلون: إنه جائز، وهو الصحيح، لصحة وقوع مثله. وما جاز على أحد المثلين، جاز على مثله. والعلم يستحيل أن يؤثر في المعلوم، لصحة تعلقه','1','349',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (124,'بالواجب والمستحيل، وهما لا يقبلا التأثير. وليس من شرط الممكن وقوعه، بل قبلوه للوقوع لو رجح. ويستحيل أن يقع بنفسه، إذ لو كان كذلك، لخرج عن كونه ممكنا، فلم يكن امتناع وقوعه لنفسه، لكن لأن المرجح لم يرجحه، فاستحال لذلك. وهذه الاستحالة لا ترجع إلى نفس الشيء، فلا تؤثر فيه. والمستحيل: ما لو قدر وجوده لنبا العقل عن قبوله، وليس خلاف المعلوم من هذا القبيل.
قال الإمام: (فهذا منتهى [الكلام] في [منع] تكليف ما لا يطاق. [ونعود] بعده إلى المقصود بالفصل في ذكر من يكلف، وما يقع التكليف به. [والقول] الوجيز [فيه] أنه يكلف المتمكن، ويقع التكليف بالممكن، ولا نظر إلى الاستصلاح ونقيضه). قال الشيخ - رضي الله عنه -: قوله: يكلف المتمكن، هذا إنما بناه على أصله في تقدم القدرة على المقدور. فإن مذهبه في هذا الكتاب صحة ذلك. وهو خلاف ما يراه في الكلام. فأما على [ما اخترناه] نحن','1','350',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (125,'من اقتران القدرة بالمقدور، فلا يشترط ذلك، على معنى أنه لا يكلف إلا قادر. وإن أطلقنا أنه لا يكلف إلا متمكن، [فإنا نريد] به أنه لا يكلف في الواقع إلا من لا يتحقق عجزه عن إيقاع المطلوب. فأما اشتراط تحقيق الإمكان الذي هو الاقتدار، فغير معتبر، بل لا سبيل إلا علمه أبدا في جريان العادة إلا بعد العمل. ومن المتعذر أن يشترط في توجيه التكليف علم ما لا يعلم إلا بعد الامتثال.
وقوله: يقع التكليف بالممكن. ليس يعني به الممكن على الإطلاق، فإن خلق الجواهر ممكن من حيث (١٧/ب) الجملة، وإن كان يمتنع به التكليف عقلا عنده، وشرعا عندنا. وإنما مراده: ويقع التكليف بالممكن لمن كلف به.
[وقوله]: ولا نظر إلى الاستصلاح ونقيضه. أراد بذلك مخالفة المعتزلة في بنائهم الأحكام على الاستصلاح والاستفساد.','1','351',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (126,'قال الإمام: <span data-type="title" id=toc-37>(مسألة: السكران يمتنع تكليفه، خلافا لطوائف من الفقهاء) </span>إلى آخر المسألة. قال الشيخ وفقه الله: هذا [الذي] ذكره الإمام في هذا الموضع، تعرض فيه لإثبات شرط زائد على تمكن الفاعل، وإمكان الفعل. فإن السكران قد يتمكن من الفعل، [ولكنه] غير متمكن من إيقاعه طاعة. وقد بينا أنه لا يشترط في التكليف التمكن من علم وجوب الفعل به. كما تقدم في وجوب النظر.','1','352',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (127,'ولكن مسألة السكران تخالف تلك، من جهة تصور القصد إلى النظر بحصول الفعل، والسكران لا يصح منه القصد إلى فعل. فبهذا تفترق المسألتان. والصحيح عندنا جواز ذلك. وقد بينا أن عدم فهم الخطاب لا يمنع التكليف، وقد استحال فهم الطلب في مسألة وجوب النظر قبل إكماله. والإمام رحمه الله إنما جعل الاعتماد في منع التكليف على استحالة فهم الخطاب. وقد بينا أن المكلف لا يتصور أن يفهم خطاب الوجوب في النظر الأول على حال، ولا يعلمه إلا بعد تقضيه.
وأيضا فإن الجاهل بالأحكام جملته قد توجه عليه التكليف، وإن كان لا يفهم. وقد قررنا أن التمكن يقارن بالفعل، فهو على الحقيقة لا يفهم، ولا يتمكن من الحال، والتكليف متوجه من غير إشكال. فثبت بذلك أن الفهم غير','1','353',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (128,'مشترط في توجه التكليف من جهة العقل. وهذا يحقق صحة تكليف السكران من جهة العقل.
وأما وقوع تكليف السكران، فغير مقطوع به نفيا ولا إثباتا، والظاهر عندنا تكليفه. وقد قال الله تعالى: {يا أيها الذين أمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون}. وهذا خطاب مع السكران, وقد تؤولت الآية على وجهين:
أحدهما- أنه قيل: هذا خطاب مع المنتشي الذي ظهرت منه مبادئ النشاط والطرب، فإنه قد يستحسن فيه من الانبساط ما لا يتم معه مقصود الخشوع، فهو تسمية ما يؤول إليه، وهو منساغ.
[الثاني-] وقيل: لم يرد بذلك النهي عن الصلاة مع السكر، وإنما أريد به الإفراط في الشرب عند وقت الصلاة، وكأنه قيل: لا تشرب فتمتنع عليك الصلاة، وأضيف النهي إلى الصلاة تجوزا. وهذا كما يقال: لا تقرب','1','354',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (129,'التهجد وأنت شبعان، أي لا تشبع فيثقل عليك التهجد.
وهذا الذي ذكره هؤلاء ممكن، ولكن لا معنى لترك الظاهر من غير ضرورة. [ولكن] حملهم على هذا التأويل اعتقادهم الاستحالة، وقد بينا أنه لا استحالة في ذلك. وحققنا توجه الخطاب على من لا يفهم التكليف. وهذا مقطوع به في الشرائع، فإن من لا يعلم أنه مكلف، فهو لا يفهم الخطاب قطعا.
وقوله: إن الناسي كالسكران في سقوط التكليف عنه. والكلام أيضا عليهما سواء بالنظر إلى (١٨/أ) مقتضى العقول.
وأما الواقع من التكليف في حق الناسي والغافل، ففي الشرع انقسام في أحوالهما: فرب موضع يسقط عنهما [فيه] التكليف والمؤاخذات. وهذا بمثابة [ما] إذا أفطر في رمضان ناسيا، أو تكلم في الصلاة ساهيا، أو كان نذر صلاة أو صوما فنسيه، فإنه لا مؤاخذة [عليهما] في هذه الجهات بإجماع من العلماء.','1','355',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (130,'وأما [المواضع التي] تثبت فيها المؤاخذات، فهي المواضع التي لا يكون [فيها] امتلاء القلب بحب المعصية، فيمنعه من فهم حكمها، كمن رأى امرأة جميلة، وهو يعلم تحريم النظر، فنظر إليها غافلا عن تحريم النظر، فإن ذلك لا يسقط التكليف عنه، ولا يوجب العفو عن هذه الجريمة. وكذلك في الغيبة والنميمة والكبر والعجب والحسد والرياء والكذب، وغيره من محرمات الشريعة. فإن نسيان الأحكام بسبب قوة الشهوات، لا يسقط التكليف. وقد حكم العلماء أجمعون بصحة صوم النائم، وإن كان لا يفهم ذلك الوقت، فإنما يكون صموه طاعة على شرط كونه مأمورا به. وكذلك صلاة الغافل مقطوع بكونها قربة. وقد اعترف الإمام بهذا بعد هذا. على ما','1','356',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (131,'سنذكره في مسألة الصلاة في الدار المغصوبة.
والعمدة في الباب على جواز التكليف بالنظر إلى مقتضى العقول، من غير اعتبار هذه الشروط، واستقراء الواقع في الشرع. فقد ثبت ذلك بوجه مقطوع به، كما في صوم النائم وصلاة الغافل في أثنائهما، وتوجه التكاليف على من استفزه الهوى وأغفلته شهوة المعصية. وقد ثبت ذلك بوجه مظنون، كما في السكران وصوم المغمى عليه، فإن هذه المسألة اختلف الفقهاء فيها، فيقضي كل مجتهد بما غلب على ظنه. والله الموفق للصواب.

قال الإمام: <span data-type="title" id=toc-40>(مسألة: المكره لا يمتنع تكليفه، لإمكان الفهم والامتثال) </span>إلى آخر المسألة. قال الشيخ - رضي الله عنه -: المكره يطلق على وجهين: يطلق على','1','357',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (132,'المضطر الذي لا قدرة له، ولا تمكن في حقه. وهذا غير مكلف بفعل مأمور، ولا بترك منهي، إما عقلا عند قوم، وإما شرعا عندنا. ويطلق على من لم يخل ودواعيه، بل حركت دواعيه من خارج. وهذا القسم هو الذي فيه الكلام.
ومذهب أهل الحق صحة تكليفه بفعل المأمورات وترك المنهيات. ومنعت المعتزلة التكليف على وفق الإكراه، وجوزوا التكليف على خلاف','1','358',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (133,'الإكراه، فإذا أكره على قتل مسلم، كلف ترك القتل. وإلزام القاضي صحيح على مقتضى الأصول. فإن المعتزلة والقاضي متفقون على استحالة تكليف ما لا يطاق عقلا. وأراد القاضي أن يبين أن الإكراه لا يصير الفعل واجبا من جهة العقل، بدليل عقلي، وآخر شرعي:
أما العقلي: فهو أنه يمكنه الانكفاف، ولو كان الإكراه يصير الفعل حتما، لكان الانكفاف مستحيلا.
الثاني: وهو الشرعي، أنه لو تحتم الإيقاع بسبب الإكراه، لاستحال التحريم في توجه التحريم بالاتفاق، [مما] يدل على بقاء الإمكان. وإذا تحقق الإمكان، صح التكليف.
والمعتزلة ثبتت على أصولها في اشتراط انضمام شرط آخر إلى الإمكان، وهو استحقاق الثواب [مع] [امتناع] التكليف. (١٨/ب) وهذا من','1','359',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (134,'تحكماتهم الباردة؛ ولا يمتنع التكليف من غير إثابة، ولا يمتنع أن يثيب لله المكره إذا فعل.
وقد وجه أبو احمد أشكالا على من يقول: يصح التكليف مع الإكراه. وتلخيص سؤاله أن قال: المكره لا يخلو: إما أن يقدم بباعث السيف، فلا يكون فعله طاعة، أو يقدم بباعث الأمر خاصة، بل كان يفعله لو أكره على تركه، فلا يكون مكرها. ثم قال: فلينتبه لهذه الدقيقة. ومقصوده أنه لا يصح أن يكون مكرها مطيعا، فيخرج منه أن لا يكون مكلفا مطيعا. وهذا زلل. وإذا تحقق الإمكان، تصور التكليف. فإن كانت النية شرطا، وجب الإتيان بها. وإن فقدت، بكل العمل، لا لاستحالة التكليف، بل للإخلال بشرطه. وإن لم تكن النية شرطا، حصل المطلوب، وإن لم يكن الفاعل مكرها. كما لو أكره على رد الغصب والودائع، وقضاء الديون بأنواع العذاب. فلا حاصل لما ذكره هذا الإنسان.','1','360',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (135,'قال الإمام: (مسألة: ذهب بعض أصحاب أبي حنيفة إلى أن الكفار غير مخاطبين بفروع الشريعة) إلى قوله (ما لم يقدم رفع الحدث عليها). قال الشيخ وفقه الله: ظاهر مذهب مالك رحمه الله كما حكي عن أصحاب أبي حنيفة، وعندنا قول ليس بمشهور: أنهم مخاطبون.','1','361',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (136,'وأما الذين فصلوا من العلماء، فالسبب عندهم في الفرق، أن المنهيات يكتفى فيها بمجرد الكف دون النية، وذلك متأت من الكفار. وأما المأمورات، فمفتقرة إلى نية التقرب، وذلك لا يتصور من الكافر. وهذا خيال ضعيف. فإنه وإن تصور منهم مجرد الانكفاف، تصور منهم أيضا صورة الطاعة. وكما استحال منهم القصد مع الكفر، استحال منهم فهم التحريم مع جحود المحرم، فلا وجه لهذه التفرقة.
وما ذكره الإمام من تقسيم الكلام إلى الجواز والوقوع، حق لا مزيد عليه. وتلخيص القول: أنه لو فرض الخطاب بإقامة الفروع، لكان خطابا بتصحيح','1','362',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (137,'الفروع، وذلك ممكن ممن لا يعتقد الصانع المختار. معناه: أن الفاسد غير مأمور به، وإنما يؤمر بالفعل الصحيح. والفعل الصحيح مع الكفر مستحيل، فلا يمكن إلا الفاسد، وهو غير مأمور به. فالممكن ليس مأمورا به. وما يقدر مأمورا به لا يمكن. فلا يتصور الأمر على هذا إلا بتقديم الإيمان. وإذا حصل، صح الأمر بالفروع. هذا تقرير الشبهة. والجواب بالنقض لازم.
ونحن نذكر حقيقة النقض، وما ينفع منه في الدين والجدل، وما يفيد في القواطع والمظنونات، وما لا ينفع في واحد منهما، وما ينفع منه في المظنون دون المقطوع به.','1','363',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (138,'فالنقض في وضع اللغة: مأخوذ من تفريق الأجزاء، وإفساد البنية، ومنه قوله تعالى: {كالتي نقضت غزلها}. ويقال: نقضت البناء. وهو عند الأصوليين يرجع إلى وجهين: انتقاض الأدلة، وانتقاض العلل، وكلاهما يرجع إلى بيان حال الربط الذي ادعاه المستدل أو المعلل ببيان الافتراق بينهما في الوجود. وهذا قد يكون قادحا، وقد لا يكون قادحا. والذي ينتفع به في القطعيات قسم واحد، (١٩/أ) وهو أن تكون المسألة الناقضة مقطوعا بحكمها. ويقطع [القاطع] بمساواة المنقوضة لها مع امتناع الاستثناء عليها. فإذا اجتمعت هذه الشروط، قطع بمساواة حكم المنقوضة لحكم الناقضة.
وهذه المسألة من هذا القبيل، فإن الأمة مجمعة على ثبوت التكليف بالإيمان بالله - عز وجل - وبرسول، وإن كان ذلك لا يمكن إلا بتقديم النظر. وكذلك المحدث مأمور بالصلاة، وإن كان لا يتأتى منه إقامتها دون تقديم رفع الحدث عليها. فعلم بذلك، وهو قاطع، أن تقديم الشرط غير مشروط في تحقيق التكليف بالمشروط قبل وجود شرطه. وأما بقية أقسام النقض، فسيأتي إن شاء الله تعالى الكلام عليها في باب النقض. وإنما ذكرنا [ههنا] ما تمس الحاجة إليه الآن.','1','364',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (139,'وجواب آخر: وهو أنا بينا أن الإمكان لا يتشرط عقلا في تصور التكليف، بدليل ما أسلفناه.
ولو سلم ذلك، فليس المراد بالإمكان اقتران القدرة بالمقدور، وإنما المراد بذلك أن يكون الفعل من قبيل الممكنات لمن كلف به في الاعتياد، بحيث تتأتى منه داعية الإقدام والإحجام، والمكلف يتأتى منه القصد إلى إقامة الفروع بتقديم شروطها. فإن امتنع من تقديم الشرط عد مخالفا. وهذا أمر بين عند العقلاء. والعبد المأمور بشراء حاجة من مكان بعيد، يعلم أن سيده أمره بالشراء، وإن كان لا يتأتى منه ذلك دون الوصول إلى مكانها. ولا يقال: إنه لا يكون مأمورا بالشراء إلا بعد الوصول، وهذا إنما اتخذ السير وسيلة إلى تحصيل الواجب، فإذا لم يجب شراء البتة. ولو استحال التكليف بالمشروط قبل حصول الشرط، لم يجب أيضا الإيمان بالله، ولا النظر في الأدلة، إذ شرط ذلك القصد، فلا يكون الخلق مطالبين إلا بالقصد خاصة. فأما الإيمان بالله ورسوله، فلا يتوجه على الكفار أمر به. وهذا خلاف دين الأمة قطعا.
وكذلك القول في الصلاة باعتبار الطهارة، ولا يكون الإنسان أيضا مأمورا','1','365',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (140,'بالطهارة عند من يشترط النية، بل بالنية على الخصوص. ثم لا يصح أن يقصد الإنسان إلى إيقاع علم على وجه الطاعة يعلم أنه لم يؤمر به. والنية قصد يتبع العلم. فإذا علم، انتفى التكليف بالطهارة. كيف يتصور أن يقصد إلى الامتثال من يعلم أنه لم يتوجه عليه أمر؟ هذا محال لا شك فيه.
قال الإمام رحمه الله: (التحقيق في ذلك كله عندي أن الكافر','1','366',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (141,'يستحيل أن يخاطب بإنشاء فرع على الصحة) إلى قوله (فهذا هو الكلام في طرف الجواز). قال الشيخ - رضي الله عنه -: ما ذكره الإمام في هذا الموضع، كلام فيه لبس، وهو قوله: والذي أراه أنه يستحيل أن يخاطب الكافر في حال كفره بإنشاء فرع على الصحة. وهذا الكلام يصح على وجه، وهو أن يقال له: أوقعه صحيحا مع كونك كافرا، ويجعل كفره شرطا في كونه مطلوبا بالعمل، فإن ذلك لا يعقل. فأما أن يصادفه التكليف بالصلاة الصحيحة مع كونه كافرا، فإن ذلك غير مستحيل، بدليل ما قررناه قبل من المعقولات والأمثلة المعتادات.','1','367',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (142,'ولكن هذه المقالة بناها الإمام على قاعدة له سيأتي بيانها، وهو أن الأمر بالشرط لا يتحقق (١٩/ب) أنه أمر في الحال. وإذا كان الشرط عنده مفقودا، علم نفي الأمر. وبنى على ذلك مسائل كثيرة في الأوامر ومسائل النسخ. وأنه إذا ورد المانع من الفعل، تبين أنه لم يكن مأمورا على حال. وهذا غلط عند أهل التحقيق، كما قررناه فيما مضى. وعلى ما سنبينه بعد ذلك، إن شاء الله تعالى.
ثم أفضى به الأمر إلى إثبات العقاب على ترك المشروطات من غير تكليف بها على حال. والأمة مجمعة على أن لا عقاب إلا على العاصي،','1','368',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (143,'وإن كان الله تعالى يتفضل بإثابة من لم يطعه، كالمظلوم يؤخذ ماله وعرضه. إلا أنه تعالى قد تفضل بأنه لا يعاقب إلا من عصاه. وقد يتفضل بترك العقاب بعد المعصية. ولا يشذ عن هذا الأصل إلا مسألة واحدة، اختلف الناس فيها على مذاهب كثيرة، وهم أولاد المشركين. فأما ما سوى هذه المسألة، فمجمع عليها بين علماء الأمة.
قال الإمام: (فإن قيل: إن ثبت لكم الجواز على تأويل التوصل وفرض العقاب) إلى قوله (وتقرر في أصل الدين ومستفيض الأخبار أن الله لا يعفو عن الكفار). قال الشيخ - رضي الله عنه -: ما ذكره القاضي رحمه الله من كون المسألة مظنونة،','1','369',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (144,'كلام صحيح، والأدلة القطعية مفقودة من الجانبين. والمسألة من مسائل الفروع، وليست من مسائل الأصول بحال. إذ ليس النظر فيها متعلقا بأدلة قطعية، [وإنما] فيما يصح أن يكون أمارة، فهي بمجال الفقه أجدر. ولكن الأغلب على الظن عندنا أنهم غير مكلفين بالفروع. ويدل عليه أمران: نقلي وفقهي.
أما النقلي: فلما روي في الحديث الصحيح: أن النبي - صلى الله عليه وسلم - أرسل بعض رسله إلى قوم من المشركين وقال له: (فليكن أول ما تدعوهم إليه شهادة أن لا إله إلا الله وأن محمدا رسول الله، فإن أجابوا، فأعلمهم أن الله أوجب عليهم خمس صلوات في اليوم والليلة). فجعل الإعلام بالفروع بعد الإجابة إلى الإسلام.','1','370',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (145,'وأما من جهة المعنى: فطلب الفروع [يكون لإيقاعها]. والإيقاع مع الكفر متعذر، وإذا قدم الإسلام، فإن كانت أوقات الفروع قائمة، استقبل المكلف الفعل، لتوجه الخطاب عليه في الحال. فإن فاتت أوقاتها، فالإسلام يمنع من التدارك. فأي حاجة إلى تحقيق التكليف ثم الحكم بالسقوط؟ فهذه الفروع لا تفعل في حال الكفر، ولا تقضى بعد الإسلام. فكان الظاهر أنهم لم يخاطبوا بها، إذ لا يحصل منها مقصود التكليف. وهذا واضح، فإنا لم نقطع بذلك، لاحتمال أن يكون التكليف تظهر فائدته في الآخرة عند الموت على','1','371',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (146,'الكفر في مضاعفة العذاب. وهذا بعيد، فإن المقصد الأصلي من التكليف الطاعة بالفعل. وقد بينا أن هذا ممتنع في [حق] الكفار في حال الكفر وبعد الإسلام. فكان الظاهر عندنا نفي التكليف بالفروع.
وقوله: (والذي أراه أن الكفار مأمورون بالتزام الشرع جملة، والقيام بمعالمه تفصيلا). [فيه] اقتصار على نفس المذهب، معرى عن الدليل. وأما وجوب التوصل، (٢٠/أ) فقد بينا صحة التكليف من جهة العقل، وحققنا أن مقتضى مذهبه، امتناع التكليف بالوسائل عند العلم بعدم وجوب المتسول إليه.
والإيمان وجب [لا] لاعتبار كونه وسيلة عندنا، بل لأنه مقصود في','1','372',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (147,'نفسه. حتى لو علم الإنسان بوجه من الوجوه أنه لا يمكنه أن يفعل [شيئا] من الفروع، لكان الإيمان عليه واجبا.
وما ادعاه من القطع بأنهم معذبون في الدار الآخرة، دعوى القطع من غير برهنا، ولا أمارة ظنية.
وقوله: (والموصل إليه أنه قد ثبت [قطعا] وجوب التوصل). فمن العجب أن يجب التوصل إلى ما ليس بواجب. [وقوله: ] (وتقرر في أصل الدين ومستفيض الأخبار أن الله تعالى لا يعفو عن الكفار فيما تعبدهم به).','1','373',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (148,'[ولكن] النزاع، هل تعبدوا بالفروع أم لا؟ ولا أرى لما ادعاه من القطع وجها.

قال الإمام: <span data-type="title" id=toc-42>(القول في العلوم ومداركها وأدلتها)</span>. قال الشيخ - رضي الله عنه -: قوله: العلوم: هو جمع علم، لا يصح على مقتضى العربية، فإن العلم مصدر، والمصدر لا يثنى ولا يجمع، لأنه يدل على القليل والكثير من جنسه، إلا أن تختلف أنواعه، نحو [الحلوم] والأشغال. والعلم لا يتصور أن تختلف أنواعه بوجه يرجع إلى انقسام العلم وتفاوت درجاته، وإن كانت تختلف متعلقاته، وليس اختلاف المتعلقات بالذي يوجب اختلاف العلم، من جهة كونه علما.
وقد ذهب بعض الناس إلى أن العلم يختلف، وزعموا أنه يتصور أن يكون علم أوضح من علم. وهذا غلط بين. واعتذر هذا القائل بمثال محسوس تمسك به، وقال: إذا رأى الرائي جسما أبيض في غلس، فإنه يرى بياضه، وكلما اشتد الضياء، علم البياض على وجه أوضح مما علمه أولا بالإبصار. قال: فكذلك العلم الذي لا يرجع إلى حاسة البصر.','1','374',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (149,'وربما تمسك بأن علم النبي بربه لا يساوي علم غيره به. وهذا غلط عندنا. وخياله فيما تمسك به من رؤية الأبيض على جهة رؤيته على أوضح من ذلك، ليس بصحيح، وإنما يرجع ذلك إلى رؤية أجسام لطيفة مسودة، فإذا قوى البياض ذهب اسودادها.
وأما ما شنع به من أن علم النبي بربه لا يساوي علم غيره، فليس الأمر في ذلك يرجع إلى تفاوت العلمين، وإنما يرجع ذلك إلى كثرة معلومات النبي وقلة معلومات غيره، أو لدوام علمه، وتوالي الغفلات على غيره.
وأما تفاوت العلم حقا وحظا، فمحال، إذ حقيقته الكشف. وكمال التقرير فيه أن العالم إذا اطلع على معلوم، فلا يخلو: إما أن يحيط به من كل وجه، أو من بعض الوجوه، فإن أحاط به علما من كل وجه، استحال أن يعلم ثانيا ما لم يعلم أولا، فلا يصادف العلم الثاني متعلقا. وإن أحاط ثانيا بما لم يحط به أولا،','1','375',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (150,'فهذا علم آخر، متعلق بمعلوم آخر، ولا يرجع ذلك إلى إيضاح في الأول بحال. (٢٠/ب).
وإن قال قائل: يصح جمعه بالنظر إلى متعلقاته، فيقال له: فجوز جمع الضرب باعتبار تعدد متعلقاته، فقل: ضروب. وكذلك يلزم جمع النظر، لاختلاف المنظور فيه، حتى يقال: أنظار. بل منع أهل اللغة من جمع المصدر، وإن اختلف قلة وكثرة، وقوة وشدة. فكيف يجمع ما لا يقبل الاختلاف بوجه؟ وقد قال سيبويه رحمه الله: لا يجمع العلم ولا النظر. لما قررناه. والكلام في الدليل يأتي بعد ذلك، إن شاء الله تعالى.
قال الإمام: (الوجه تصدير الباب بقول مقنع في العقل) إلى قوله (فهذا لباب كلامه بعد تطويل وإطناب). قال الشيخ - رضي الله عنه -: قد اختلف في إلحاق لفظ','1','376',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (151,'العقل [بالعلم]، واختلفوا في ماهيته: فذهب الخوارج إلى أن العاقل: من عقل عن الله. وقال الشيخ أبو الحسن: العقل هو العلم. وهو مختار الأستاذ أبي إسحاق.','1','377',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (152,'وذهب الحارث بن أسد المحاسبي إلى أن العقل: غريزة يتوصل بها إلى المعرفة. وقد أوضح ذلك في كتاب: (الرعاية) وبسطه بسطا كثيرا، وأتى له بمثال فقال: (مثل العقل مثل البصر، ومثل العلم مثل السراج، فمن لا بصر له، لا ينتفع بالسراج، ومن له بصر بلا سراج، لا يرى ما يحتاج إليه). فقد صرح بمخالفة العقل العلم.
وقال الجبائي: العقل هو الصارف عن القبيح، الداعي إلى الحسن. وعباراتهم في ذلك كثيرة. وكلها ترجع إلى هذا. فلم نر الاشتغال بنقلها والإكثار منها.
وقال بعض المحصلين: إن العقل هو الذي يصح معه الاستدلال. قال القاضي: لست أنكر تفسير العقل بالعلم في وضع اللغة، فإن العرب تقول:','1','378',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (153,'عقلت الشيء وعلمته وفهمته. فلا امتناع في تسمية العقل علما والعلم عقلا. ولكن غرضنا بالكلام أن نوضح العقل الذي هو شرط صحة التكليف.
وأما التطويل الذي ذكره الإمام في احتجاج القاضي، فلابد منه ليتبين مأخذه. فإن الرد على القائل قبل معرفة مأخذه فيه نظر. قال القاضي: العقل أمر وجودي، إذ لو كان عدما، لما اختص به بعض الجواهر دون بعض، إذ النفي لا اختصاص له.
فإذا ثبت وجوده، فلا يخلو: إما أن يكون قديما أو حادثا، ويستحيل الحكم بقدمه، لدلالة الأدلة على أن لا قديم إلا الله وصفاته، إذ لو كان قديما، لاستحال أن يتصف به المحدث، ولا معنى للإطناب في ذلك.
وأيضا فإن ذات القديم لا يختص بها بعض الجواهر دون بعض، فكان يجب أن يثبت العقل لجميع جواهر العالم. وقد صار كثير من الحشوية إلى أن العقل قديم. وهم أقل من أن يفردوا بكلام. وهذا كقولهم بقدم الروح.','1','379',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (154,'وإذا بطل كون العقل قديما، وجب القطع بحدثه. ثم الحادث لا يخلو: إما أن يكون جوهرا أو عرضا، وباطل أن يكون العقل جوهرا، لأدلة: منها- تماثل الجواهر. ومنها- أنه لا يوجب جوهر حكما لجوهر، مع اختصاص كل واحد بحيزه. ومنها- تجدد حكم العقل على الجوهر مع استمرار وجوده. ويتحقق ذلك بوجوب قيام الموجب بمن له الحكم، فوجب أن يكون من قبيل الأعراض. ولا يصح وقوعه على جميعها، لأنها مضادة. وأيضا فإنه (٢١/أ) يتصف بالعقل مع فقدان جملة من الأعراض.
فإذا ثبت أنه بعض الأعراض، فلا يخلو: إما أن يكون غير المعلوم، وإما أن يكون من العلوم. باطل أن يكون غير العلوم، إذ لو كان كذلك، لصح أن يتصف بالعقل خال عن العلوم كلها. فدل أنه من ضروب العلم.
والذي يحقق ذلك، أنه ما من ضرب من ضروب الأعراض زائد على الحياة، إلا ويصح تقدير العقل مع عدمه إلا العلوم. وإذا كان كذلك، وصح أن العقل من العلوم، ومحال أن يكون جميعها، لصحة الاتصاف بالعقل مع فقدان جملة من العلوم. ولا يصح أن يكون من العلوم النظري، لأن النظر لا يقع ابتداؤه إلا مسبوقا بالعقل. ولا يصح أن يقال: إنه جملة من العلوم الضرورية، فإن العلم بالمحسوسات من الضروريات. وقد يعقل من لا يحس أصلا. فثبت أنه بعض العلوم الضرورية.','1','380',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (155,'ثم سبيل التنصيص عليه أن [يقال]: كل علم لا يخلو العاقل منه عند الذكر، ولا يشاركه فيه من ليس بعاقل، فهو العقل. وتحصيل ذلك: أن العلم بالآلام واللذات والعلم بالنفس، لما اشترك فيه العاقل وغير العاقل، لم تكن هذه الضروب من العقل.
فأما العلم بأنه لا يخلو المعدوم عن النفي والإثبات، ولا يخلو الموجود عن قدم وحدوث، فلا يشارك العقل فيه غيره فهو العقل. وعد القاضي من','1','381',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (156,'ذلك، العلم بأن الخبر لا يكون إلا صدقا أو كذبا. وعد منه العلم باستحالة اجتماع المتضادات. فهذا هو التطويل الذي ترك الإمام ذكره.
والاعتراض عندي على هذه الطريقة أن يقال: العقل معنى واحد مفرد، فكيف يتصور أن يكون مركبا من علوم متعددة، وكل علم مخالف للعلم الآخر؟ هذا عندي لا يصح. وقد قال بعض أصحاب القاضي: إن العقل يرجع إلى علم المرء بأنه عالم، فإن المعلوم ينقسم إلى النفي والإثبات، والإثبات ينقسم إلى القديم والحادث، فيرجع العقل إلى علم واحد، حتى لا يلزم التركيب في العقل. ولكن هذا أيضا غير سديد، فإنا نختار في الكلام: أن كل معلومين يتصور العلم بأحدهما مع الغفلة عن الآخر، فغنما يعلمان بعلمين. والعلم يتعلق بالمعلوم وبنفسه، فلا يكون علم واحد يتعلق بهذه المعلومات. فلا يصح أن تصير العلوم المتعددة عقلا.
قال الإمام: (وهذا الذي ذكره فيه نظر) إلى قوله (وهذا سبيل كل شرط ومشروط). قال الشيخ: قول الإمام: إنه لا يمتنع أن يكون العقل مشروطا','1','382',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (157,'بعلوم. لا يصح، إذ لو كان كذلك، لأمكن وجود العلم دون العقل، إذ لا يمتنع وجود الشرط دون المشروط. ومن المحال أن يعلم العلوم الضرورية من لا عقل له.
ولكن يتوجه على القاضي فيما ذكره اعتراض، وهو أن يكون العاقل شرطا في العلوم الضرورية، فلا تحصل إلا لمن حصل له العقل. وهذا الذي قصده الإمام، وإن كانت العبارة غير محررة، ولكن يجاب عن هذا: بأنه لو كان العقل شرطا، لأمكن وجدانه دون العلوم.
وهذا الذي ادعاه الإمام من أن الذاهل عن الفكر في الجواز والاستحالة عاقل، فهذا الذي ذكره غير مسلم، فإنه إذا ذهل عن جميع العلوم، لم يكن عاقلا. فإن قال قائل: ما المانع من كون العقل شرطا في العلوم، والعلوم شرطا في العقل (٢١/ب)، فيكون الاشتراط من الجانبين، فيوجب ذلك الاقتران من الطرفين؟ وهذا السؤال غامض، وينقدح في دفعه طريقان:
أحدهما- أن التحكم بادعاء شرط لا يعلم ضرورة ولا نظرا باطل. فإن قيل: لسنا ندعيه، ولكن نقول: ما المانع منه؟ فعنه جوابان:','1','383',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (158,'أحدهما- أن هذا التقدير يفضي إلى ما لا نهاية له، فإنه يمتنع تقدير شرط للشرط المقدر، ولا يقف الأمر على حد.
الثاني- أنه لو كان كذلك، لوجب أن يثبت ذلك في حق الله سبحانه وتعالى، فإن الشرط العقلي يجب طرده شاهدا وغائبا، فيكون للباري تعالى عقل زائد على العلم، وذلك باطل بإجماع الأمة. وهذا هو الطريق الثاني في الجواب عن السؤال الأول.
فإن قيل: هذا تمسك بالإجماع في مسائل الكلام وقضايا العقول. فنقول: لا يمتنع أن يتلقى هذا الضرب من المعقولات من الإجماع. والضابط: أن كل ما يصح أن يتلقى من الرسول - عليه السلام - من قضايا العقول، جاز أن يتلقى من الإجماع. ولو أخبر الرسول - عليه السلام - أنه ليس لله تعالى صفة هي شرط في العلم إلا الحياة، لعلم ذلك منه. وسيأتي لهذا مزيد تقرير، إن شاء الله تعالى.','1','384',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (159,'قال الإمام: (فإن قيل: فما العقل؟ قلنا: ليس الكلام فيه بالهين. وما حوم عليه أحد من علمائنا غير الحارث بن أسد المحاسبي. فإنه قال: العقل: غريزة يتأتى بها درك العلوم وليست منها). قال الشيخ وفقه الله: وقد أنكر الإمام [هذه المقالة] في (الشامل)، ومنه كون المحاسبي يقول ذلك: (ولعمري إنه لبعيد من نفس ذلك الرجل، وغير لائق بمذهب أهل الحق). ثم تأوله الإمام هنالك، وقال: (لعله أراد بذلك معرفة الله - عز وجل - والتشمير للعمل، ولم يرد به مطلق العلم). وللتأويل مجال. ولكن قد ذكر المحاسبي هذه المسألة في (الرعاية)،','1','385',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (160,'ونص على الفرق بين العلم والعقل. على ما قررناه. والله أعلم بصحة ذلك.
قال الإمام: ([والقدر] الذي يتحمله [هذا الموضع]) إلى قوله (والأدلة عليها). قال الشيخ وفقه الله: ما ذكره الإمام، لا خلاف بينه وبين ما حكاه عن (المحاسبي). إلا أن لفظ الغريزة ليست عبارة المتكلمين. فقد يوهم إطلاقها باختلاف في الجواهر على ما ذهب إليه الفلاسفة، وليس هذا اعتقاد أحد من أهل الإسلام. ولفظة (الصفة) مشهورة عندهم.
وقوله: ليس الكلام في بالهين، يشير إلى اشتراك لفظه، وتعدد مدلولاته. والفلاسفة يقولون: العقل يرجع إلى العالم العلوي. في مذاهبهم في','1','386',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (161,'العقول والنفوس. وهذا الأصل لا سبيل للخوض فيه في هذا الفن. وليس النزاع الحقيقي بين المتكلمين وراء الألفاظ، إلا في إثبات هذه الصفة ونفيها.
والذي يصح عندنا في العقل ما حكيناه عن أبي الحسن أنه يرجع إلى العلم من غير زيادة. وهو مطابق للغة، وإن كان لفظ العقل قد يطلق على زاد على العلم، ولكن إنما [نريد] نحن بعض مسمياته، وهو ما يرادف العلم منها، إذ يقال: علمت وعقلت وفهمت بمعنى واحد. وقد اعترض على هذا، بأن الله سبحانه عالم، ولا يقال له عاقل. والجواب عنه: (٢٢/أ) أن إطلاق الأسماء على الله - عز وجل - موقوف على ورود السمع.
فأما وضع الاسم تلقيبا، فمجمع على منعه. وأما إطلاق الأسماء المشتقة من المعاني عند ثبوت اتصافها بالمعاني، فمختلف فيه. وللمسألة ثلاثة أحوال: إما أن يرد الإذن، كالعالم والقادر، وإما أن يثبت المنع، كالسخي، وإن أطلق عليه لفظ الكريم، وإما أن ينتفي الأمران. فهذا موضع خلاف، فمن مانع، ومن مجيز، ومن متوقف.
والظاهر عندي المنع، لحصول القصر، بالنظر إلى مفهوم العدد، حيث','1','387',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (162,'قال - عليه السلام -: (إن لله تسعة وتسعين اسما، مائة إلا واحدا). فانظر كيف قال: (إلا واحدا). وكأن المنع من إطلاق العقل عليه لذلك.
وأظن- والعلم عند الله- أن سبب المنع ما يتطرق إلى اللفظ من اشتراك. إذ قد يطلق العاقل على من عنده سكون وهدوء، وملازمة الطريق الحميدة عند العقلاء، لداع يدعوه إليها، والانصراف عن الرذائل لصارف يصرفه عنها.
فلما كان ذلك قد يفهم من اللفظ، منع من إطلاقه على الله سبحانه وتعالى. وهذا الاعتراض أيضا يتوجه على من قال في حد العلم: إنه المعرفة. فإن الله سبحانه وتعالى لا يسمى عارفا، كما لا يمسى عاقلا. والكلام في المعقولات غير الكلام في جواز الإطلاق على الله [في السمعيات].
قال الإمام: (فصل- لم ينكر من يبالي به من العقلاء أصل العلوم) إلى','1','388',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (163,'آخر الفصل. قال الشيخ: هذا الذي ذكره الإمام ظاهر، لا مزيد عليه،','1','389',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (164,'ولا يحتمل أكثر من هذا.

قال الإمام: <span data-type="title" id=toc-44>(فصل- في حد العلم وحقيقته) </span>إلى قوله (وهذا لا يرشد إليه','1','390',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (165,'تغاير العبارات). قال الشيخ - رضي الله عنه -: ننبه في ابتداء هذا الكلام على أصل في الحدود، وهو أن اللفظ إذا كان نصا، فهو أحسن ما استعمل في الحدود. وكذلك إن كان ظاهرا واحتماله بعيد، فاستعماله أيضا حسن. وإن كان مشتركا وملتبسا، فلا يصح استعماله في الإفهام مجردا عن القرينة بحال.
واختلف أصحاب الحدود فيما إذا بينته القرينة المقالية، كقولنا: العلم:','1','391',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (166,'الثقة بالمعلوم. فإن الثقة مشتركة بين الأمانة والعلم. ولكن ذكر المعلوم يقطع ذلك الاشتراك في المحل المخصوص، ويبين مقصود المتكلم منه. وكذلك إذا قلنا: العلم إدراك المعلوم. وقد حد به أيضا الشيخ أبو الحسن. من جهة أن الإيهام الذي فيه يزيله التقييد بالمعلوم والإضافة إليه.
وهل يكون اقتران القرينة الحالية فيما بين المتخاطبين تقوم مقام القرينة اللفظية؟ هذا أيضا متنازع فيه. وإذا رأينا أن ذلاك لا يبطل، فالمراد ههنا بالتبيين: مجرد الكشف. ويكون ذلك استعمال اللفظ في خصوص ما وضع له. نعم، إن كان لا يطلق لفظ التبيين أبدا لا حقيقة ولا مجازا، إلا على ما سبق فيه إيهام، امتنع التحديد، ولست أرى الأمر كذلك.','1','392',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (167,'وأما قول أبي الحسن: العلم ما يوجب لمن قام به كونه عالما. فهو ضعيف من أوجه: منها- أن إيجاب الأحكام إنما يكون على القول بإثبات الأحوال. وأما نفاة الأحوال، فلا معنى عندهم للإيجاب. ولا يرجع ذلك إلا إلى مجرد التسمية، فكأنه قال: العلم إذا قام بذات سميت عالمة. لا فائدة فيه بوجه من الوجوه. ومعرفة (٢٢/ب) المصادر عليها تترتب أسماء الفاعلين والمفعولين، إذ لا يتصور أن يفهم الضارب من لا يفهم الضرب. وأما على القول بإثبات الأحوال، فالمعنى الموجب للحكم، قد يكون أخفى من الحكم،','1','393',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (168,'فيصح بيانه به. وهذا بمثابة قولنا: الحركة ما يوجب لمن قامت به كونه متحركا. ولعل أبا الحسن إنما أتى بذلك ردا على المعتزلة، فإنهم يثبتون للقديم أحكام الصفات وينفون الصفات. فيمكن أن يكون هذا الكلام جرى منه في مكالمتهم.
وأما القسم الثاني: وهو إذا كانت الصفات واضحة جلية وأحكامها ملتبسة خفية، فلا يكون ذكر الحكم مبنيا للمعنى بحال. وهذا بمثابة كون الألوان إذا قامت بمحال، هل تحب لها أحكاما؟ وفيه نظر. فإذا قيل لنا: ما البياض؟ تعذر علينا أن نقول لمن التبس عليه البياض: هو الذي يوجب لمن قام به كونه أبيض.','1','394',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (169,'وأما حد الأستاذ، فقد قصد أوجها من التقريب، ووقع في أكثر مما منه فر. وذلك أن الحدود التي ذكرناها كلها ترجع إلى الألفاظ دون الحقائق واللوازم. وقد بينا أن التبيين على التكميل في التحديد، بذكر أوصاف النفوس، ويليه في التعيين، التعريف باللوازم.
وأما تبديل العبارات المترادفةـ، فبمعزل عن البيان. ورأى صحة الإتقان والإحكام من لوازم العلم، فبينه [وبينها] ملازمة، غير أنه لم يجمع ولم يمنع.','1','395',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (170,'أما كونه غير جامع، فإن العلم المتعلق بالواجب والمستحيل، والباقي والموجود، لا يتأتى به الإحكام والإتقان. وإنما يندرج تحت ما قاله ضرب واحد من العلوم، وهو العلم المتعلق بالممكن على الخصوص.
وأما كونه غير مانع، فلأنه لا يصح الإحكام والإتقان دون القدرة، فليجب أن تكون القدرة علما؟ هذا إذا سلم أن للإحكام والإتقان أثرا محققا. وفيه نزاع عند المتكلمين. فقال طائفة: ليس إلا الإيجاد، والتخصيص يدل على الإرادة، والإرادة تدل على العلم. وسيأتي تقريره بعد هذا.
وحد المعتزلة أيضا غير جامع وغير مانع. وأما كونه غير جامع، فلتقييدهم العلم بالشيء. فإن الشيء عند القوم: هو الموجود، أو المعدوم الذي يمكن وجوده، وأما ما لا يمكن وجوده من المنفيات، فليس شيئا، فهذه علوم وليست علوما بأشياء. وأيضا فإن أبا هاشم أثبت علوما ليست متعلقة بمعلومات،','1','396',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (171,'وهذا كالعلم بأن لا شريك لله، فقد خرج هذا الضرب عن الحد.
وأما كونه غير مانع، فالعقود المصممة إذا كانت صحيحة، فإنها ليست علوما. وقد زاد بعض المتأخرين منهم زيادة أخرجت هذين الضربين، فقالوا: اعتقاد المعتقد على ما هو به، إذا وقع ضرورة أو نظرا. فقالوا: المعتقد، ليتناول الحد جميع أنواع العلم. وقالوا: إذا وقع ضرورة أو نظرا، ليخرج عقود المقلدة عن الحد. لكن يبقى مع هذا كله أمران:
أحدهما- إثبات أبي هاشم علما (٢٣/أ) لا معلوم له، إذا توجه ذلك عليه، بطل الاعتماد على ذكر المعتقد، فيجب طرحه. وإذا أسقطت هذه اللفظة بقي: (العلم: [اعتقاد])، فإذا اقتصر على ذلك، بطل بالجهل. وعن هذه الدقيقة كان مذهبه أن العلم بالشيء والجهل به مثلان. على ما سيأتي بعد ذلك، إن شاء الله تعالى.
والأمر الثاني: أن الاعتقاد لفظ مشترك، والعلم لا اشتراك فيه، فكأنه بين الواضح بالخفي.','1','397',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (172,'وأما قول القاضي: العلم معرفة المعلوم على ما هو به. فهو عندي ضعيف، لأنه إن حاول بذلك بيان العلم- إن تصور افتقاره إلى بيان- فلا يصح هذا اللفظ لذلك، وإن بدل لفظا بلفظ، فيغتفر في هذا، إلا أن تكون الأسامي مترادفة، ويكون الثاني عند السائل أوضح من الأول. وليس لفظ المعرفة مرادفا للفظ العلم عند أهل اللسان. بل خص اسم العلم بما يتعلق بالجملة، واسم المعرفة بما يتعلق بالمفرد.
فإن قيل: هما مشتركان في حقيقة الكشف، وإن كثرت متعلقات أحدهما وانفرد متعلق الآخر. قلنا: غرضنا أن نبين أن هذا لا يجري على ذوق الحد اللفظي. وأيضا فإن علم الباري سبحانه وتعالى لا يطلق عليه اسم المعرفة.
وقد تنازع أصحابه في قوله: المعلوم، هل هو محتاج إليه في الحد؟ فقال قائلون: لا حاجة إليه، فإنه لو قال: العلم: المعرفة، واقتصر، لكان الكلام','1','398',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (173,'كافيا. وقال قائلون: لابد من ذكر المعلوم، حذارا من أن يظن بالقاضي أنه يقول بقول أبي هاشم في تصور علم لا معلوم له. وهذا ضعيف، فإن الحاد لا يلتزم في حده أن يحترز عن أن يضاف حده إلى حدود باطلة.
ولم يتكلم أصحاب القاضي في الزيادة الأخرى، وهي قوله: على ما هو به. وهذه الزيادة أبعد من الأولى. ويمكن أن يعتذر عنها بما اعتر به عن ذكر المعلوم. فإنه أيضا لو قال: العلم معرفة المعلوم، [لأمكن] أن يقال: لفظ العلم والمعرفة والاعتقاد عند المعتزلة يجري على جنس واحد. فعلى هذا يصير كأنه قال: العلم اعتقاد المعتقد. ومتى رد الكلام إلى هذا، وجب أن يصون بقول: على ما هو به. احترازا عن الاعتقاد الفاسد.
وذكر الإمام لفظة: (وبها تميزه الذاتي عما عداه). وهذه اللفظة لا تجوز في العربية، وذلك أن النسبة ترد الكلمات إلى أصولها، وأصل (ذات)','1','399',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (174,'(ذوية). وإنما قلنا ذلك، لأنه لا يوجد اسم متمكن على أقل من ثلاثة','1','400',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (175,'أحرف. و (الهاء) ليست أصلية، وإنما هي علامة التأنيث، فتبقى الكلمة على حرفين، وهي لا تكون كذلك. فحذف منها (لامها)، وهي (ياء). [وإنما] قلنا: إن (اللام) (ياء)، لأنه لا تكون كلمة (عينها) و (لامها) (واوين) إلا قليلا جدا، كباب (فوه). فقضينا بكون (اللام) (ياء). وإنما قلنا: إن (العين) (واو) في الأصل، لظهورها في التثنية والجمع، فيقال: ذواتا، وذوات، فهي إذا (ذوي). وإنما تحركت (الواو) وانفتح ما قبلها (ألفا)، ثم خففت، فحذفت (اللام) وصارت (هاء) التأنيث كالعوض. فإذا جاء النسب، حذفت (الهاء)، لأنها زيادة متصلة بآخر الاسم، فلم (٢٣/ب) يجمع بينهما وبين (ياء) النسب.
وأيضا فغنها قد تنسب بـ (هاء) مثل [مماليه] وصياقله وصيارفه، فلما حذفت (الهاء) التي كانت كالعوض من (اللام)، رجعت (لاما)، وكان حقها أن ترجع (ياء)، إذ هو أصلها.','1','401',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (176,'ولكن يلزم منه أن يقال: [ذويي]. فتكثر الياءات والكسرات، فأبدل من (الياء) (واوا)، كما في رحوي، وهو من رحيت الرحى. وهذا الموضع من العربية غلبت فيه (الواو) على (الياء)، وإن كانت (الياء) غالبة على (الواو) في غيره، لأنها أخف منها. وكذلك إذا اجتمعت هي و (الواو) وسبقت إحداهما بالسكون، فلبت (الواو) (ياء)، فيقال: [كي] من كويت، كما يقال [حي] من حييت، وإنما نسب إلى ذات ذووي، على ما تقتضيه اللغة العربية.
قال الإمام رحمه الله: (فإن قيل: فما المرتضى عندكم في حقيقة العلم؟ ) إلى قوله (وحذار مخالفة الماضين). قال الشيخ: قوله: الرأي عندنا أن','1','402',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (177,'نتوصل إلى درك حقيقة العلم بمباحثة نبتغي بها ميز مطلوبنا مما ليس منه. قد تقدم في أول الكتاب الإرشاد إلى بيان امتناع إدراك حقيقة ملتبسة بسلب أمر عنها. وحققنا أن المطلوب إنما يعرف بدرك جميع أوصافه النفسية، وحققنا ذلك أبلغ تحقيق.
والذي نريده الآن أن الذي جعله الإمام في [هذا المكان] يوصله إلى معرفة أضداد العلم، هو أيضا تبديل الألفاظ. فلئن كان تبديل لفظ العلم بالمعرفة لا يرشد إلى حقيقة العلم، فقوله: الجهل عقد يتعلق بالمعتقد على خلاف ما هو عليه، لا يكون مرشدا إلى حقيقة الجهل، إذ ليس إلا تبديل الألفاظ.','1','403',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (178,'وقوله: والظن والشك ترددان بين معتقدين. أما الظن فإنه يرجع إلى ميل النفس إلى أحد الأمرين أو الأمور، فليس هو على ذوق الشك، ولا على حقيقته. وأما الشك، فقد اختلف الناس فيه، هل هو معنى أو لا؟ والصحيح أنه معنى، ويدل عليه ما يدل على جملة المعاني المستدل عليها، لتجدد أحكامها.
وقد ذهب الجبائي في أحد قوليه إلى أن الجهل عبارة عن نفس العلم. وهذا خطأ بين، وهو يفضي إلى نفي الأعراض. فيصح أن يقال- على هذا','1','404',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (179,'الرأي- إن العلم يرجع إلى نفي الجهل. وقال في [موضع] آخر: إن الشك يرجع إلى توالي اعتقادين متناقضين على العاقل، فيكون في زمن معتقدا نفيا، وفي الثاني معتقدا إثباتا، فلا ينفك في زمن من الأزمان عن الاعتقاد. وهذا القول باطل، فإن العاقل يدرك من نفسه تردده وعدم تصميمه على جهة. [ولو] صح أن يكون الشرك يرجع إلى تعاقب معنيين متناقضين على القلب، لكان الإنسان إذا اعتقد قدم العالم دهرا، ثم اعتقد حدثه أن يكون شاكا، وهو في جميع أزمنته مصمم.
وأما قوله الإمام: إنه تردد بين معتقدين. فهو لفظ متجوز به، وهو يفهم منه ما حكيناه عن أبي هاشم. والأحوط أن يجتنب في الحدود الألفاظ الموهمة، فيقال: هو تردد بني أمرين من غير ترجيح في أحدهما. (٢٤/أ)','1','405',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (180,'وهذا الموضع غامض. [فإن قيل]: هل المتعلق بالأمرين معنى واحد أو معنيان؟ فنقول: المتعلق بالأمرين معنى [واحد]، إذ لا يتصور الشك مع اتحاد المتعلق، فلابد أن يكون متعلق الشك أمرين. والذي خالفوا في تعلق العلم الحادث بمعلومين، سلموا تعلق الواحد بأمرين.
فإن قيل: إذا شك الإنسان في ثلاثة أمور، فقد تعلق شك واحد بأمرين، ولا يبقى للشك الثاني إلا متعلق واحد. قلنا: قد استحال تطرق الشك إلى أمر واحد. فنقول: على هذا يصح أن يتعلق الشك الواحد بأكثر من اثنين، فهو متردد بين الجهات الكثيرة ترددا واحدا.
وما انحصر إليه الكلام من طلب الفرق بين الاعتقاد الصحيح وبين العلم، فهو لعمري مكان غامض، [ومتاهة] مظلمة. ومنها نشأ اختلاف الأولين والآخرين في المذاهب والمعتقدات، فإن كل معتقد مصمم على أنه عالم. ومن المستحيل أن يعرف المعتقد كونه معتقدا، سواء كان اعتقاده موافقا للمعتقد أو مخالفا، فإنه إنما يدري أن الحاصل ليس يعلم بتجويزه النقيض. وعند تجويزه النقيض يبطل الاعتقاد.','1','406',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (181,'والبحث عن الأسباب المرشدة للفرق بين الاعتقاد والعلم مهم جدا. وقد سلك الإمام في ذلك طرقا:
أحدها- أنه قال: لا يخفى الفرق بين العلم والجهل. والمماثل للمخالف مخالف. هذا مقصوده في قوله: إن الاعتقاد يماثل الجهل، والجهل مخالف للعلم. وصدق في قوله: المماثل للمخالف مخالف.
وأما قضاؤه بأن الاعتقاد الصحيح من جنس الجهل، فكلام مبهم، وقول مظلم، فإنه إن أراد به أنه من جنسه، باعتبار أعم أوصافه، فالعلم أيضا من جنس الجهل. فإنه معنى من المعاني. وأن أراد أنه من جنسه، من جهته الخاصة، وهو الذي قصدها- والله أعلم- ولذلك أتى بالمثال، وهو قوله: إذا اعتقد المعتقد أن زيدا في الدار، ولم يكن فيها، ثم استمر العقد إلى أن دخلها زيد، فحال المعتقد لا يختلف، وإن اختلف المعتقد. فقد قضى باستواء حال الجاهل والمعتقد اعتقادا صحيحا. ولو كانت المعاني القائمة مختلفة، لما تصور استواء حال من قامت به، وهذا القول خطأ بين. وبيانه من وجهين:
أحدهما- أن المثلين هما اللذان يقوم أحدهما مقام الآخر ويسد مسده، ولو كان الاعتقاد صحيح مماثلا للجهل، للزم أن يكون المعتقد اعتقادا صحيحا جاهلا، حتى يعتقد المعتقد على خلاف ما هو به، فيكون اعتقاده صحيحا وباطلان معا.','1','407',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (182,'الثاني- أنا قررنا أن ضرورة المعتقد أن لا يدري كونه معتقدا، لأنه إنما يدري كونه معتقدا إذا أدرك الفرق، وإنما يدرك الفرق على تقدير الإحاطة بالأمرين. فإذا لم يحط بالأمرين، كيف يتصور أن يدرك الفرق؟
وما نقله الإمام عن أبي هاشم من قوله: إن العلم بالشيء والجهل به مثلان، هو اللازم على قوله من وجهين:
أحدهما- أن العلم عنده يرجع إلى اعتقاد المعتقد على ما هو عليه بلا زيادة، وهذا هو المراد بالاعتقاد (٢٤/ب) الصحيح. وقد اعترف الإمام بأن هذا مماثل للجهل.
الثاني- أن أبا هاشم لما حد العلم بأنه اعتقاد المعتقد، وقد بينا أن الإضافة إلى المعتقد ليست لازمة عنده ولا شرطا، فوجب حذفها، والاكتفاء بأن العلم: اعتقاد، والجهل أيضا: اعتقاد، فلزم أن يكون الجهل علما. بل يلزم على قوله أن يكون الشاك علما، لأنه في كل زمان معتقد. وهذه جهالة لا تنسب إلى لبيب.','1','408',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (183,'وقول الإمام: الشاك يرتبط عقده بأن زيدا في الدار أم لا؟ . كلام فيه ضعف وإيهام، إذ كيف يكون معتقدا ولا اعتقاد عنده؟ بل شك محض.
وقوله: [المعتقد] سابق إلى أحد المعتقدين. يعني اللذين أضيفا إلى الشاك، والكلام فيه على ما تقدم.
قال الإمام: (ومن أحكام عقد المقلد) إلى قوله (وترى معارضه جدلا محجاجا). قال الشيخ: قصد الإمام في هذا المكان أن يفرق بين العلم والاعتقاد بطريقين:
أحدهما- تصور حقيقتهما مع الاختلاف.','1','409',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (184,'والثاني- النظر إلى الآثار والخواص. فأما الأول وهو قوله: الاعتقاد مأخوذ من الربط، والعلم يشعر بالانشراح والثلج. فهذا الأمر يرجع إلى تفسير اللغة ووضع الألفاظ، [ولا تتلقى من ذلك المعقولات]. وأيضا فإنه قد قررنا أن ضرورة المعتقد أن يعتقد كونه عالما. فلو كانت المعاني واضحة الاختلاف باعتبار حقائقها، لم يتصور اللبس. وأكثر الخلق حصل الالتباس في حقهم حتى حسبوا الاعتقاد علما.
وأما الكلام الثاني: وهو قبول التشكيك عند نهاية الإصغاء، ففيه عسر من','1','410',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (185,'جهة الاعتياد، فإن التصميم على الأمر يمنع من كمال الإصغاء إلى نقضيه. ولكن إذا اتفق ذلك، وحصل الإنصاف، ووقع الالتفات إلى التجويز في المعتقدات، أمكن الاضطراب.
لكن يبقى على هذا سؤال، فيقال: هل يحصل الشك عند الإصغاء في حالة بقاء الاعتقاد أو عند زواله؟ فإن كان مع بقاء الاعتقاد، فمحال، وإن كان بعد زواله، فقد خلفه ضد من أضداده، وذلك أيضا يصح في العالم إذا زال علمه، صح أن يخلفه الشك، فبماذا يفارق العلم الاعتقاد؟
فنقول والله المستعان: يمكن أن يشكك المعتقد وإن بقي ذاكرا لسبب اعتقاده، فإنا نبين له أنه لا ربط بينه وبين معتقده، وإلا لو استند إلى وجه صحيح يقتضيه، لكان علما، ولكان السبب دليلا، بخلاف العلم إذا استند إلى سبب يقتضيه، لم يتصور بيان بطلان السبب. فلذلك استحال أن يشكك العالم مع ذكر سبب العلم، وأمكن أن يتشكك المعتقد على ما قررناه. وهذا واضح فيما إذا استندت العلوم والاعتقادات إلى أسباب مختلفة، وإنما تغمض إذا صودفت الاعتقادات في النفوس مضاهية للأوليات من العلوم، فإنه قد يجد الإنسان نفسه مصممه على أمور وهمية لا يقدر على صرف نفسه عنها. وهذا كالاعتقاد أنه لابد من خلاء أو ملاء وراء العالم. هذا في النفس مضاه','1','411',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (186,'لكون الشخص لا يخلو من كونه متحركا أو ساكنا. (٢٥/أ) والأول عمل الوهم، والثاني ضروري.
ولكن طريق الفصل في ذلك أن الوهمي العقل يصد عنه، فإن الخلاء لا معنى له، والملاء باطل بانحصار العالم، [والأولي] العقلي لا يتصور أن يقدح فيه شيء. فإذا يدرك الميز إما: بالثمرات من قبول التشكيك وامتناعه، أو بالنظر إلى الطرق وقبولها للفساد وامتناع ذلك. هذا هو المعتمد في الفرق بين العلم والاعتقاد.
قال الإمام: (وقد يطرأ على العالم المحقق) إلى قوله (وسأتحفك إن ساعدت الأقدار بلباب هذه الفنون، مستعينا بالله، وهو خير معين). قال الشيخ - رضي الله عنه -: لما قال الإمام: إن العالم لا يتصور تشكيكه إلى آخره، وجه على نفسه هذا السؤال، يشير به إلى أنه قد يتوهم المتوهم [تشكيكا]. ومثال ما ذكر: أن المعتزلة إذا قضوا بأنه لا يصح قيام المعنى بنفسه، كان في ذلك على','1','412',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (187,'بصيرة وبرهان. ثم اعتقد أن القدم أخص أوصاف الباري تعالى، وأن الاشتراك في الأخص يوجب الاشتراك في الأعم. وهو في هذا مصمم غالط. فإذا جاءت مسألة الإرادة، فلم يمكنه أن يقول: الباري تعالى مريد لنفسه. فإن مقتضى مذهبه أن الحكم الثابت للنفس، إذا كان يقتضي تعلقا، وجب أن يعم تعلقه، كما قالوا في كونه تعالى عالما، فإنه عالم بكل معلوم.
قالوا: فلو كان مريدا لنفسه÷ لكان مريدا لكل أمر، حتى يكون مريدا للقبائح، وذلك عندهم محال. ولا يصح أن يكون مريدا بإرادة قديمة، لأن القدم عندهم أخص أوصاف الباري تعالى، فلو كانت إرادته قديمة لكانت إلها. ولا يصح أن يقال: غير مريد، لوجود التخصيص الدال على ذلك. ولا يصح أن يقال: إنه مريد لا لنفسه، ولا لمعنى، فإن ذلك محال، فلم يبق إلا أن يقال: إنه مريد بإرادة حادثة. ولا يصح أن يقال: إنها قائمة بذاته، فإنه','1','413',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (188,'يستحيل أن يكون محلا للحوادث. ولا يصح أن يقال: إنها قائمة بغيره، فإنه يكون من قامت به مريدا بها، ولا يرجع حكمها إلى الباري سبحانه، فلم يبق إلا أن يقال: إنه مريد بإرادة قائمة بنفسها، وهذا يناقض ما تقدم من استحالة قيام المعنى بنفسه. ولا يتصور قيام المتضادات بالقلب في حالة واحدة، فيصير الإنسان على هذا مترددا بين الاعتقادين، فيكون أشبه شيء بالشك على مذهب أبي هاشم.
قيل للإمام: فهذا عالم متردد. فقال: (ليس ذلك شكا، إن كان الحاصل له علما، وإنما هو إيثار ذهول عن الأول، ليستمر ما يحاوله من الاستقرار على العقد التقليدي). وأما ما هو عالم به، فلا يجد في نفسه ترددا ولا ميلا عنه.','1','414',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (189,'وقوله: (ولن يبالي بذلك، إلا من ضعفت غريزة عقله). كلام صحيح، فإنه لا يصح للعاقل أن يترك ما علمه، بناء على الوهم، وإنما يعتري ذلك من ضعف العقل. والله المستعان. هذا نهاية كلام الأصوليين والمتكلمين.
والذي عندي في العلم غير ذلك كله. فنقول والله المستعان: من أحاط بحقيقة الشيء، فهو مستغن عن حده، ليعرف به المحدود. وقد تقدم الكلام (٢٥/ب) على هذا في أول الكتاب. فإن طلب العالم حدا، فإنما يريد إرشاد غيره، لا ليتعرف هو في نفسه، وإذا كان كذلك، فالأمر ينقسم إلى ثلاثة أقسام:
قسم لا يتصور جهله بوجه، وهو العلم، إذ من ضرورة من قام به العلم أن يعلمه. فإن المذهب الصحيح أن العلم يتعلق بالمعلوم وبنفسه. ولو لم نقل','1','415',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (190,'ذلك، للزمنا أمور ممنوعة، وهو القضاء بكون العلم لا يتصور أن يعلم، وذلك محال، فإنا نعلم علم غيرنا وعلمنا. ويلزمنا أيضا أن يكون الواحد منا عالما بالعلوم الكثيرة، وهو لا يعلم ذلك من نفسه، وذلك معلوم بطلانه ضرورة. أو يقال: يعلم العلم بعلم آخر، وذلك باطل، لإفضائه إلى التسلسل، إذ لا ينتهي إلى حد يوقف عنده، فلم يبق إلا أن العلم يتعلق بالمعلوم ويتعلق بنفسه. وإذا كان كذلك، وجب أن يكون العالم عالما بحقيقة العلم، فكيف يتصور أن يطلب العاقل أن يكشف له حقيقة العلم؟
وظني بالأئمة - رضي الله عنهم - أنهم ما سلكوا في التحديد التعرض لتبديل الألفاظ، إلا لما قررته من كون الحقيقة مكشوفة. وإنما التبس على السامع نسبة اللفظ إلى المعنى، فأوضحوا له بلفظ أوضح دلالة ونسبة من الأول.
فإن قيل: فإذا كان من ضرورة العالم أن يحيط بحقيقة العلم، فكيف يتصور التباس العلم بالاعتقاد، حتى قلتم: إن ذلك من أغمض الأمور؟ فيقال: إنما جاء ذلك من خفاء الاعتقاد وشدة التباسه، إذ ضرورة المعتقد أن يعتقد كونه عالما، فلما كان ذلك من ضرورته، التبس أمره، فذكرنا تلك الطرق، ليتبين بها الاعتقاد، لا أن العلم مفتقر إلى البيان، إذ ليس بعد العلم كشف على حال.
وهذا الذي ذكرته في هذا المكان من جواهر الكلام. ولم أر أحدا من الأصوليين والمتكلمين ذكر ذلك، ولكنه شيء من الله سبحانه، فله الحمد وله المنة.','1','416',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (191,'قال الإمام: <span data-type="title" id=toc-45>(فصل- يحتوي على أقاويل في مدارك العلوم) </span>إلى قوله (لا مدرك للعلوم إلا الكتاب والسنة والإجماع). قال الشيخ: هذا المذهب عن الأوائل مشهور، وفي كتب الأئمة كلها مسطور، وهو أول ناقل له في كتاب الكلام. وقد ناظر القوم على إبطال كون النظر غير مفيد للعلم، [وأوسع]','1','417',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (192,'القول. وكيف ينكر ذلك، وينسب النقلة إلى الغلط، وهو الناقل عن السوفسطائية إنكار العلوم على الإطلاق؟ كيف وهؤلاء أقل شرا من أولئك؟
وأما الذين قالوا: إن مدارك العلوم الإلهام، فإن زعموا أن الله تعالى قد [يلهم] بعض أوليائه بمعرفة أمور من غير نظر، فنحن لا نمنع من ذلك،','1','418',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (193,'لكن بشرط أن لا تكون تلك العلوم متعلقة بالأحكام الشرعية، لانعقاد الإجماع على أنه لا طريق إلى معرفة أحكام الله تعالى إلا أدلتها. وقد كان رسول الله - صلى الله عليه وسلم - ينتظر الوحي في الأحكام.
وإن أراد القوم أنه لا طريق سوى الإلهام، فهؤلاء قد جحدوا العلوم الضرورية وأدلة العقول. وسنبين أنها موصلة إلى العلم. ويتصل القول بالرد على منكري النظر.
وما ذهب (٢٦/أ) إليه الحشوية من أن مدارك العلوم: الكتاب والسنة والإجماع، [لولا كمال العمى وتمام الجهل].
قال الإمام: (وقال المحققون: مدارك العلوم: الضروريات) إلى قوله (على ما سيأتي تفصيله). قال الشيخ - رضي الله عنه -: ذكر المحققون أن مدارك العلوم الضروريات التي تهجم مبادئ فكر العقلاء عليها. يريد بالمدارك: الطرق التي','1','419',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (194,'بها تدرك العلوم. وإذا كان كذلك، لم تكن النظريات طرقا، وإنما النظريات: العلوم التي يتوصل إليها بالنظر. وقول الأئمة صحيح بالنظر إلى العادات. وفي المقدور خرقها وإحداث علوم من غير تقدم نظر. أما وقوعها ضرورة، فمتفق عليه بين العقلاء.
وأما وقوعها كسبية من غير تقدم نظر، هل يجوز ذلك عقلا أم لا؟ فيه خلاف. ذهب القاضي إلى منع ذلك. وذهب الأستاذ إلى جوازه، وهو الصحيح عندنا.
وتمسك القاضي بأن قال: لو جاز أن يحصل العلم النظري من غير سبق نظر، لجاز أن يوجد النظر الصحيح، ولا يحصل العلم به. وهذا الذي ذكره وجعله أصلا، مختلف فيه أيضا. فذهب ذاهبون إلى أن ترتب العلم على النظر الصحيح معتاد، وفي المقدور أن يسبق النظر، ويتم على سداده، ولا يحصل العلم. وذهب الأكثرون إلى وجوب حصول العلم عند تمام النظر وانتفاء الآفات، وهذا هو الصحيح عندنا.
وتمسك الفريق الأول بأن قالوا: النظر يضاد العلم بالمنظور فيه، وإنما','1','420',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (195,'يحصل العلم عند تصرم النظر، فزمان عدم النظر هو زمان حصول النظر، وإنما ينحتم حصول أحد الضدين بعينه، إذا كانت القسمة محصورة في الضدين، كالحركة والسكون. فإنه إذا عدمت الحركة، ففي ذلك الزمان يخلق السكون. فإنه لو لم يكن كذلك، لخلا المحل عن الضدين، وذلك محال. وليس النظر مع العلم كذلك. إذ للنظر أضداد كثيرة، فهو بمثابة اللون، فلا يلزم إذا عدم البياض أن يخلفه السواد. وكذلك إذا عدم النظر، لم يلزم أن يخلفه العلم. وقد وقع الاتفاق على جواز خلق الضد العام من الغفلة والغشية والبهيمة.
ولا يقال: النظر بوجوب العلم إيجاب العلة معلولها، لما قررنا من التضاد الحاصل بين النظر والعلم. والعلة عند مثبتيها توجب معلولها لنفسها، ولا يتصور انفصال بينهما. وإنما اطردت العادة بأن النظر إذا تم على سداده، ولم تعقبه آفة، فالعلم يحصل.
وقال المحققون: لا يتصور عند تمام النظر، وانتفاء الآفات، أن لا','1','421',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (196,'يحصل العلم، لأن النظر إذا تم، فهو المفضي بالناظر إلى الإطلاع على الوجه الذي منه يدل الدليل، فكيف يتصور أن يكون محيطا بالوجه الذي منه يدل الدليل، مع ذكره له، ولا يحصل العلم بالمدلول؟
وإذا بينا على وجوب الحصول بمقتضى العقول- عند الإحاطة وعدم الذهول- من أين يلزم إذا انقضى النظر أن يستحيل خلق العلم (٢٦/ب) المقدور؟ والعلم الضروري مماثل للكسبي، باعتبار أنفسهما. وإنما يفترقان في خلق القدرة مقترنة بأحدهما دون الآخر. وإذا جاز أن يخلق الله القدرة والعلم جميعا، جاز أن يخلق العلم دون القدرة.
وقال القاضي أبو بكر: لو جاز ذلك، لبطل النظر، وسقطت احجج، وتمكن المتحكمون بإسقاط الأدلة. وهذا الذي قاله تؤمن منه العادة، فلا نرى ما قاله مستقيما على ما بيناه. فيكو تقسيم المدارك إلى الضروريات والنظريات عقليا عند القاضي، واعتياديا عندنا.
قال الإمام: (فأما الضروريات: فإنها تقع بقدرة الله تعالى غير مقدورة للعباد). قال الشيخ أيده الله: هذا الذي قاله متفق عليه باعتبار العادات، لكن اختلف العلماء، هل يجوز في العقل خلاف ذلك؟ فذهب ذاهبون إلى تجويزه، وأن تقع الضروريات نظريات على العموم. ومنع آخرون ذلك، وقالوا: لا يتصور في العقل أن تقع نظرية مكتسبة.
وقال القاضي أبو بكر: أما العلم الذي هو عقل، فلا يتصور أن يقع','1','422',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (197,'مستدلا عليه، إذ لا يصح أن يستدل إلا بعد كمال العقل. فكيف يدرك العقل بالنظر من لا عقل له، ولا ينظر إلا عاقل؟ وأما ما ليس عقلا من العلوم الضرورية، فلا بعد في أن يقع نظريا.
ومن جوز الاقتدار على الجمع، [تمسك] بتماثل العلوم، وعموم قدرة القديم. وإذا صح خلق القدرة على علم بعد سبق النظر، صح ذلك في [مثله].
وأما الذين منعوا ذلك، وهو الصحيح عندنا، فالمعتمد أن [يقال]: لو كانت العلوم الضرورية تقبل الاستدلال، لم يخل الدليل من أن يكون نفيا أو إثباتا، والنفي لا اختصاص له بمعلوم دون معلوم، والثابت: إما قديم أو حادث، والحادث: إما جوهر أو عرض. وهذه الأمور معقولة، ولا دلالة عليها عقلا على العلوم الضرورية. فكيف يتصور أن تغير أدلته؟ أم كيف يتصور عقلية غيرها؟ ولذلك قضى الأئمة بأنه لا يصح في المقدور دليل على صدق الرسول - صلى الله عليه وسلم - إلا المعجزات على الخصوص، فإن الدليل إما أن يكون معتادا، فلا اختصاص له، أو غير معتاد، فهو الخارق بعينه.
قال الإمام: (والنظريات في رأي معظم الأصحاب مقدورة بالقدرة الحادثة).','1','423',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (198,'قال الشيخ - رضي الله عنه -: الذي ذهب إليه أكثر المتكلمين هو الذي ذكره الإمام. وقد قال بعض الناس: إنها غير مقدورة، وهو الذي اختاره الإمام ههنا. والأستاذ أبو إسحاق يبدي توقفا في المسألة، ولم يحك هذا القول إلا [لقربه] من الصواب.
والذي اعتمده المتكلمون في كون العلوم النظرية مقدورة بالقدرة الحادثة: هو الدليل الدال على انقسام الحركات إلى الضرورية والاختيارية. وقد سبق في هذا الكتاب تقرير ذلك، فهو بعينه (٢٧/أ) جار ههنا.
وهل سبق النظر في العلم المكتسب شرط من جهة العقل، أو من جهة العادة؟ [فذهب] القاضي إلى أنه شرط عقلي. وذهب الأستاذ أبو إسحاق إلى أنه شرط من جهة العادة. وهذا هو الصحيح عندي. والدليل عليه أن العلم لا يجامع النظر، وإنما يخلق في حال نفيه، وهو من قبيل الممكنات. والقدرة الصالحة لا قصور فيها، فإذا صح خلقه والإقدار عليه من غير نظر، [نظرنا] إلى تماثل العلمين، وصلاحية القدرة، وكون قدرة العبد مقارنة غير مؤثرة.','1','424',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (199,'قال الإمام: (والمرتضى المقطوع به عندنا، أن العلوم كلها ضرورية. والدليل القاطع على ذلك، أن من استد نظره، وانتهى نهايته، ولم يستعقب النظر ضد من أضداد العلم المنظور فيه، فالعلم يحصل لا محالة من غير تقدير خيرة فيه). قال الشيخ: ما ذكره الإمام غير صالح للاستدلال على سلب الاقتدار، وذلك أنه جعل الدليل على كون الشيء مقدورا، التمكن من الانكفاف عنه. وهذا لا يصلح على مذهب أهل السنة، فإن القدرة الحادثة عندهم تقارن','1','425',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (200,'حدوث المقدور، ولا تتعلق إلا بمقدور واحد، ولا تصلح لغيره. فكيف يستدل بتحتم حصول العلم مقارنا للقدرة، على أنه غير مقدور؟ وما من فعل عندنا تتعلق القدرة به إلا يتحتم حصوله، ويمتنع الانكفاف عنه، إذ هما في وقت واحد. وسيأتي الكلام عليه في غير هذا المكان، إن شاء الله تعالى.
قال الإمام: (فأما المعتزلة فإنهم فهموا أن العلوم ليست مباشرة بالقدرة [الحادثة]، وعلموا أن النظر يستعقبها استعقابا لا دفع له، فزعموا أن النظر يولدها توليد الأسباب مسبباتها. والمقدور الذي هو مرتبط التكليف والثواب هو النظر عندي). قال الشيخ: ما ذهب إليه المعتزلة من كون العلوم ليست مباشرة بالقدرة الحادثة، من جهة أن القدرة عندهم تصلح للإقدام والإحجام، وبعد النظر لا يتصور الإحجام، فلزم ألا تكون مباشرة بالقدرة. وقد علموا أن النظر يستعقبها، مع تحققهم طلب الشرع العلوم، واستحالة تكليف ما لا يطاق، فقالوا: إن النظر يولدها.','1','426',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (201,'والمولد عندهم: هو فعل فاعل السبب، وأصل التوليد عندنا باطل. والصحيح عندنا أن القدرة الحادثة غير مؤثرة على حال، ولا في محلها، فكيف بالمنفصل عن محلها؟ وقد ساعدت المعتزلة على أن تذكر النظر لا يولد العلم، وإن كان يحصل عقيبه.
واتفقوا على أن أفعاله تعالى لا تقع مولدة. ويلزم على ما قالوه بدعة شنعاء، وهي من رمى سهما إلى رمية، واتصل السهم بالرمية بعد موت الرامي وعدمه، ثم أفضت الجراحة إلى هلاكه، فإن قتله مقدور للرامي، وإن كان عدما محضا. وأي جهالة تزيد على هذا في إضافة فعل على التحقيق إلى عدم محض؟ نعوذ بالله من الجهل والضلال.
قال الإمام: (والمقدور الذي هو مرتبط التكليف والثواب هو النظر عندي). قال الشيخ: وإنما حمله على ذلك أصلان:','1','427',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (202,'أحدهما- استحالة تكليف (٢٧/ب) ما لا يطاق.
والثاني- ما تخيله من أن العلم النظري [غير مقدور بالقدرة الحادثة]. فلهذين الأصلين ارتكب ما قال، وشذ عن قول أهل الإجماع. فإن الأمة مجمعة على تحريم الشك في الله - عز وجل -، وعلى تحصيل عقد يتعلق به على ما هو عليه، إما علما عند المتكلمين، وإما اعتقادا عند بعض الناس. وقد قال الله تعالى: {فاعلم أنه لا إله إلا الله}. {واعلموا أن الله يعلم ما في أنفسكم فاحذروه}. وكم من آية في كتاب الله تعالى مصرحة بالأمر بالعلم. وهذا ما لا خفاء به عند العلماء.','1','428',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (203,'قال الإمام: (ثم رتب أئمتنا [أدلة العقل] ترتيبا ننقله) إلى قوله (وهذا الفن لا يفيد علما قط). قال الشيخ: ما ذكره الأئمة في تقسيم أدلة العقول إلى أربعة أقسام، لا تصلح لضبط الأدلة، وليس فيه ما يقتضي القصر على ذلك.
وبعضها غير صالح للعد في الأدلة، كإنتاج المقدمات النتائج، على ما سنبينه. والاستدلال بالمتفق على المختلف يضاهي إلحاق الغائب','1','429',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (204,'بالشاهد، إذ حقيقته ترجع إلى القياس، فلا يبقى إلا السبر والتقسيم.
وليس السبر والتقسيم من الأدلة بحال، فإن السبر هو الاختبار، يقال للمرود الذي يختبر به الجرح مسبر. والتقسيم يرجع إلى حصر الأقسام، إما علما، كما تدور في النفي والإثبات. وإما علما، كما تدور في النفي والإثبات. وإما ظنا. وليس في الحصر على التعيين','1','430',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (205,'بحال، وقد يكون انحصار القسمة ضروريا، فلم يبق مما ذكروه إلا القياس خاصة. وما ذكروه من التحكم بالإلحاق، فصحيح، والجمع بالعلة وبقية الجوامع، إنما تصح عندي على القول بالأحوال، فإذ ذاك يتصور الالتفات إلى الجوامع. [وهذا لدقيقة، وهي] أن من أنكر الحال يمتنع عنده الاشتراك إلا في اللفظ، ولا يلزم من ثبوت حكم عقلي لمسمى، أن يثبت لما يشاركه في التسمية. والاشتراك في المعقول منتف عند هذا القائل.
وقد حاول القاضي أن يمشي [على] طريقة الجمع بالحقائق على القول بنفي الحال. وهذا عندي غير صحيح، فإنه إذا كانت حقيقة العلم الحادث تخالف حقيقة العلم القديم، فمن أين يلزم من القضاء على أحدهما القضاء على','1','431',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (206,'الآخر؟ وكذلك نطرد هذا في الشرط والدليل، فلا يلزم من ثبوت الاشتراط في شيء، أن يكون شرطا فيما يخالفه في الحقيقة عند مشاركته في الاسم. [إلا] إذا [بنى] الأمر على القول بالأحوال في صحة الجوامع، على ما سنبينه.
أما الجمع بالعلة: فاقتضاء العلة معلولها اقتضاء نفسيا. ولا يتصور في العقل وجود الموصوف دون وصفه النفسي، ولا ثبوت الوصف دون الموصوف. وكذلك إذا قضى باستواء الحقيقتين وتماثلهما، فمن المحال أن يختص أحدهما بحكم عقلي دون مماثله. وكذلك إذا افتقر شيء إلى شرط عقلا، فمن المحال ثبوته دون [مشروطه]. والدليل يدل بصفة هو عليها، فلا يتصور وجوده غير دال.','1','432',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (207,'وقول الإمام: إن الدليل الذي قام في الشاهد، إن قام في الغائب، أغناك عن الاستشهاد بالشاهد. فهو كذلك، ولكن لم ينتبه العقل للدليل (٢٨/أ) إلا لما نظر في الشاهد. بيانه: أن القائل لو قال: كون العالم عالما، وصف يرجع إلى نفس العالم، وثبت كون الباري تعالى عالما. وقال قائل: لعله عالم لنفسه. فإذا تجرد كون الشاهد عالما، استحال أن يرجع إلى نفسه، ولزم أن يكون الحكم معللا، على ما يفهم من العلة والمعلول، فلا استرابة في أن ذلك يرشد إلى [أن] الباري عالم بعلم.
فما ذكره الإمام لا يعترض على طريقة الأئمة. لكن بعد إثبات الحال.','1','433',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (208,'ولا يستغني متكلم في هذا الفن على القول بالأحوال، إما بأن يسميها أحوالا، أو وجوها واعتبارات. وقولهم: الشيء يعلم من وجه ويجهل من وجه، إشارة إلى الأحوال.
وأما بناء النتائج على المقدمات، فليس ذلك من أصناف أدلة العقول، وإنما هو راجع إلى التعبير عن الأدلة بصيغ مختلفة، وليس اختلاف العبارات بالذي يوجب اختلاف الأدلة.
فإنا إذا قلنا: [عن] النبيذ: إنه مسكر فيحرم، قياسا على الخمر، كان ذلك قياسا. فيغير هذا النظام، فيقال: كل مسكر حرام، والنبيذ مسكر، فكان حراما. فليس هذا دليل آخر من جهة الحقيقة، وإنما هو تغيير النظم خاصة.','1','434',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (209,'وأما قولهم: إن المقدمة الواحدة منتجة. فهذا الكلام غير صحيح، إلا أن يكونوا أهملوا ذكر المقدمة الثانية [لوضوحها]. فأما ترتب النتيجة على المقدمة، فلا يصح ذلك، إذ أل ما يتركب منه البرهان مقدمتان يحصل بينهما ازدواج بذكر أمر يكون موجودا في المقدمتين جميعا، وهو الرابط، ولولاه لم تحصل النتيجة أبدا. فإنا لو قلنا: النبيذ مسكر، فكان حراما، لم يلزم ذلك إلا','1','435',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (210,'إذا قلنا: وكل مسكر حرام.
وقوله تعالى: {لو كان فيهما آلهة إلا الله لفسدتا}. إنما حصل المقصود للعلم بالمقدمة الأخرى، إذ المشاهدة على أنهما لم تفسدا. وكذلك قولهم: تحرك الجوهر، ولم يكن متحركا، فلابد من فرض زائد الذات. وهذا لا يكون منتجا حتى ينضم إليه كل طارئ على الذات، فلابد من مقتض. وقد طرأ التحرك على الذات، فلابد له من مقتض. والقول في المقدمات وشروطها','1','436',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (211,'ونتائجها طويل، وله فن مخصوص به، فلم نر التطويل به.
وقولهم: قد تكون المقدمة نظرية والنتيجة ضرورية، إن أريد بذلك الضروري الذي يلازم أنفس العقلاء، فهذا محال. إذ كيف يكون هذا ملازما لأنفس لعقلاء، وهو لا يحصل إلا بعد النظر في تحصيل مقدمته؟ وإن أريد به أن المقدمتين إذا علمتا، وعلم الازدواج، وتنبه الذهن لاشتمال المقدمتين على النتيجة، فلابد من حصول العلم بها، فهذا صحيح. وتقدم في أثناء الكلام القول في الاستدلال بالمتفق على المختلف، وأنه يرجع إلى القياس، وشرط صحته الجوامع المقتضية للتسوية، على ما ذكرناه.
وأما السبر والتقسيم، فإذا لم ينحصر في نفي وإثبات، لم يصح لإثمار العلم. ويصح أن يستعمل في غلبات الظنون، إن كان الحصر مظنونا. وإن انحصر في النفي والإثبات، فلا يكون نفس الحصر دليلا، فإن الإنسان يعلم أن','1','437',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (212,'العالم لا يخلو من وصف الحدوث والقدم، (٢٨/ب) ولكن لا يكون هذا دليلا على معرفة وصفه. فعد السبر والتقسيم من الأدلة لا يصح، إلا أن يراد أن التقسيم إذا دل على بطلان أحد القسمين تعين الثاني، فهذا صحيح. والاعتماد على الدليل في التعيين، لا على نفس التقسيم.

قال الإمام: <span data-type="title" id=toc-46>(فصل- يجمع قول الأصحاب في مراتب العلوم) </span>إلى قوله','1','438',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (213,'(فيستحيل اعتقاد ترتيبها). قال الشيخ: الأمر على ما ذكره الإمام من استحالة تفاوت العلوم. وقد سبق الدليل على ذلك. والضروريات في سرعة الحصول والاستمرار، مقدمة على النظريات من غير إشكال. وما ذكره الكعبي من كون العلم بخبر التواتر نظريا، سيأتي الكلام عليه، إن شاء الله.
واختلف أصحابنا في العلوم بالحرف والصناعات، هل هي مكتسبة أم ضرورية؟ فقال قائلون: إنها مكتسبة، وهو الظاهر، ولذلك افتقر إلى تعلمها.','1','439',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (214,'وقال آخرون: هي ضرورية، وهو ظاهر هذا الكلام. والظاهر عندنا الأول.
وأما من قدم المعقول على المحسوس، من جهة أن الحواس عرضة للآفات والتخييلات، فخيال باطل، فإن الإدراك القائم بالحاسة لا يتصور الخطأ','1','440',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (215,'فيه. نعم، قد يتخيل للإنسان شيء على خلاف ما هو به، وليس بمدرك على الحقيقة، ما يتوهم المتوهم أمرا ويعتقد معقولا، ولا يكون كذلك.
وأما قول من قدم البصر لتعلقه بجميع الموجودات بزعمه، فهذا ليس هو الواقع، فلئن قال: كل موجود يجوز أن يرى، وما لم ير، إنما لم ير لمانع. فكذلك عندنا كل موجود يجوز أن يسمع، وما لم يسمع، إنما لم يسمع لمانع. وتقرير ذلك في فن الكلام.
وما اعتمده القتبي في التقديم من الترتيب في الذكر، فلا يخفى سقوطه.','1','441',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (216,'وما احتج به أيضا من أن الله تعالى لن يبعث أصم، وفي الأنبياء عميان، فذلك أن الأنبياء بعثوا لإرشاد الخلق، واستماع أقوالهم وجوابهم عن سؤالهم، والصمم يخل بذلك دون العمى.
وأما تفاوت طرق العلم، فمحال أيضا، فإنها كلها قصاراها العلم، فلا يتصور دليل أشد ارتباطا من دليل. وسننبه على وجه تفاوت النظر.
وما ذكره الإمام [من] منع التفاوت في العلم على كونه ضروريا، كلام صحيح، وسواء قلنا إن العلم ضروري أو مقدور، فلا تفاوت فيه، لما','1','442',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (217,'[مر] من البرهان.

قال الإمام: <span data-type="title" id=toc-47>(فصل- فيما يدرك بالعقل لا غير) </span>إلى قوله (لا يمتنع اشتراك السمع والعقل فيه). قال الشيخ: قوله: أما ما لا يدرك إلا بالعقل، فحقائق','1','443',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (218,'الأشياء. كلام ليس على عمومه، وقد تعرض لتخصيصه بعد ذلك بقوله: كل مدرك. إلى آخره. فالمراد من العمومات: ما يتقدم ثبوته على الكلام الصدق.
والضابط فيما يجوز إدراكه بهما جميعا هو: كل أمر من المعقولات يصح الكلام مع الغفلة عنه. فهذا هو الذي يجوز إدراكه بهما جميعا.
وأما قولهم: إن كلام الله تعالى لا يجوز أن يدرك بسمع، فهذا ينبني على خلاف المتكلمين في مدلول المعجزة، هل مدلولها كون الرسول (٢٩/أ) صادقا أو مصدقا؟ ذهب الإمام في هذا الكتاب إلى أن المدلول كونه صادقا.','1','444',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (219,'فيعلم صدق الرسول - صلى الله عليه وسلم - من هو غافل عن ثبوت الكلام لله سبحانه. وقد مال الإمام إلى هذا في (الإرشاد). وضرب في ذلك [مثالا] للملك المتصدي للرعية، المخالف للعادة، على حسب سؤال المدعي. قال: فيحصل للحاضرين العلم، مع الغفلة عن كلام النفس. بل لو كان في المجلس من يعتقد نفي كلام النفس، لحصل له العلم. وسيأتي لهذا مزيد تقرير، إن شاء الله تعالى.

قال الإمام: <span data-type="title" id=toc-48>(فصل- يشتمل على مقدار من مدارك العقول) </span>إلى قوله (على الألوان بحكم الأكوان من غير بصيرة). قال الشيخ: قوله: لا يجول العقل في كل شيء. الشيء: هو الموجود عندنا. فإن أراد أن بعض الموجودات لا','1','445',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (220,'يصح علمه، فهذا غير صحيح. ومن أصول أهل الحق أن كل موجود يجوز أن يرى. والرؤية إما علم خاص، وإما إدراك زائد على العلم، فالعلم أعم تعلقا. فإذا كان كذلك، صح أن يعلم كل موجود. وأيضا فإن العلم لا يصحح لتعلقه، فإنه يتعلق بالواجب والجائز والمستحيل.
وإن أراد [أن] بعض الموجودات لا يصح الاستدلال عليها، فهذا قد تقدم الكلام عليه عندما تكلمنا على أن الضروريات، هل يصح أن تكون مستدلا عليها؟
وقوله: (فالنظر عندنا: عبارة عن المباحثة في أنحاء العلوم الضرورية). وأراد بالمباحثة: الفكر، لكنه تجوز بإطلاق لفظ (المفاعلة) في غير موضعها، فإنها تستدعي تعددا في غالب الأمر. والعبارة السديدة ما قاله المتكلمون،','1','446',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (221,'فإن النظر عندهم: هو الفكر الذي يطلب به من قام به علما أو غلبة ظن.
وقوله: (في أنحاء الضروريات وأساليبها)، يعني الطرق، ثم العلوم الحاصلة على إثرها كلها ضرورية، يعني أنها ليست مقدورة على ما مر. وقد تقدم الكلام على السبر والتقسيم، وأنه غير مكتفى به في حصول العلم، بلا لابد من إقامة الدليل على التعيين.
وقوله: (وليس للدليل تحصيل إلا تجريد الفكر). كلام غير معقول، فإنه لابد للفكر من متعلق، فإذا لم يكن متفكر فيه، كيف يتصور الفكر لحصول','1','447',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (222,'العلم؟ وقوله: (إذا استد النظر) إلى قوله (فهو الذي يسمى نظرا ودليلا). فقد جعل في هذا المكان أن النظر هو الدليل، وليس الأمر كذلك؛ فإن الدليل قد','1','448',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (223,'قررنا: أنه مرتبط بالمدلول، سواء نظر الناظر أو لم ينظر. وههنا أربعة أمور متعددة: فالدليل مرتبط بالمدلول، والنظر في الدليل إذا تم على سداده يتضمن العلم. وعلى ما نراه ههنا يسقط من الأقسام، ولا يبقى إلا النظر والعلم والمعلوم. والعلم غير منظور فيه، وكذلك المعلوم، فلا يبقى للنظر مورد. فإذا بطل متعلق النظر، بطل النظر. فإما أن تصير الأمور كلها معلومة، وإما أن يتعذر علم شيء منها ليس بضروري. وهذا يشير (٢٩/ب) إلى مذهب منكري النظر.','1','449',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (224,'وما ذكره من الأمثلة لا [يدفع] هذا التحقيق. وقد بينا أن الأدلة ترجع إلى أمور مرتبطة بأمور على جهات من الارتباط، فمن اطلع على جهة ارتباط الدليل بمدلوله، حصل له العلم بالمدلول.
وحصره جولان العقول في التقسيم المنضبطة التي يهتدي العقل إلى تعيين بعضها، غير صحيح. فإن البرهان ينقسم إلى البرهان المستد وإلى برهان الخلف. فالبرهان المستد: هو الذي يفضي بالناظر إلى العثور على عين المطلوب، كالإحكام الدال على العلم، أو على كون العالم عالما عند المعتزلة. وكذلك القول في الإيجاد والتخصيص. وبرهان الخلف: هو الذي لا يهجم على المطلوب بنفسه، ولكن يريد الناظر المطلوب بين قسمين، نفي وإثبات، ثم يقوم البرهان على استحالة الثبوت، فيتعين النفي أو بالعكس من ذلك.','1','450',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (225,'والكلام في استحالة العرو عن الألوان مشكل، وهو مما استخير الله فيه. وطرق المتكلمين فيه مشهورة، فلم نر الإكثار بذكرها.
وأما ما ذكره في منع القياس إلى آخره. فقد سبق الكلام على مثله عند كلامنا على إلحاق الكاتب بالشاهد.
قال الإمام: (ومما يتعين على الطالب الاهتمام به في مضايق هذه الحقائق) إلى قوله (يستحيل أن يدرك حقيقة ما لا يتناهى). قال الشيخ: ما ذكره في هذا المكان من أن الأحكام الإلهية تستحيل الإحاطة بها، وإنما يتعلق العلم بأمور جميلة منها. اختلف المتكلمون في جواز تعلق العلم بمعلوم على الجملة دون التفصيل. فذهب الأكثرون إلى جواز ذلك.','1','451',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (226,'واختلف في ذلك قول ابن الجبائي، وقد نقل عن الشيخ أبي الحسن أنه منع من ذلك، وله أيضا كلام يقتضي التجويز. وقد تمسك المنكرون بنكتتين نذكرهما ونذكر الكلام عليهما:
إحداهما- أنهم قالوا: حقيقة العلم الكشف، ونحن لا ننكر أن يعلم المعلوم من وجه، ويجهل من وجه. والوجه الذي علم منه منكشف، ليس فيه إجمال بحال. بل ذلك الوجه مفصل عند العالم به.
[الثانية]: أن العلم بالجملة يناقض العلم بالتفصيل، فلا يتصور اجتماع العلم بالجملة مع العلم بالتفصيل، فلا يتصور أن يعلم الباري تعالى معلوما أو معلومات على الجملة. فاستحال تعلق العلم على الجملة، ولا يتصور أن يتعلق العلم الحادث به، فإن العلم الحادث مخلوق لله - عز وجل -، وتعلقه بالمعلوم لنفسه، فلابد أن يكون فاعل العلم عالما بمتعلقه، فيقتضي ذلك أن يكون الباري تعالى يعلم المعلومات على الجملة، وهو عالم بها على التفصيل، وذلك متناقض على ما قررناه.','1','452',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (227,'وأما المجوزون، فقد قالوا: لا ننكر العلم على الجملة، فإنا إذا علمنا أن معلومات الباري تعالى لا تتناهى، فقد علمنا أمرا لا محالة، إذ يستحيل علم لا معلوم له، ومعلومة المعلومات غير المتناهية، وهي غير مفصلة عندنا قطعا، لاستحالة تعلق العلم الحادث بما لا يتناهى على التفضيل، وأن كل معلومين يصح (٣٠/أ) العلم بأحدهما مع الغفلة عن الآخر، فإذا علما، فإنما يعلمان بعلمين مختلفين، فإنه لو تعلق بما علم واحد، لاستحال أن يعلم أحدهما مع الغفلة عن الآخر، وكان يجب عقلا أن يعلما معا.
وقد صورنا جواز العلم بأحدهما مع الغفلة عن الآخر، وكل ما أفضى إلى استحالة المجوز، فهو باطل وانقلاب الجائز محالا محال. وما أفضى إلى المحال فهو محال. وإذا كان كذلك، فلو قدرنا كون الحادث يصح منه أن يعلم ما لا يتناهى، للزم أن يعلمها بعلوم لا تتناهى، وتوجد تلك العلوم قائمة بالعلم، وما حصره الوجود، فهو متناهٍ ضرورة، فاستحال أن يعلم المخلوق ما لا يتناهى على التفصيل، فإذا لم يعلم المعلومات غير المتناهيات، إلا من حيث الجملة، لا من حيث التفصيل.','1','453',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (228,'وأما الاستدلال الثاني، وهو قولهم: لو علم الواحد منا معلوما من حيث الجملة، للزم أن يكون الباري تعالى عالما به مع علم بالتفصيل، والعلمان متناقضان. إذ قد بينا أن العلم بالجملة يناقض العلم بالتفصيل. فقد اختلف جواب أهل الحق عن هذا السؤال، فذهب القاضي أبو بكر إلى التزام ذلك، وقضى بأن الباري تعالى لا يتصور أن يعلم شيئا من حيث الجملة لإحاطته بالتفاصيل. والتزم أن قال: إذا خلق الله لعبده علما متعلقا من حيث الجملة، لم يكن عالما بمعلوم العبد، وإن كان عالما بعلمه، لأن خالقه ومبتدعه. قال: ولا بعد في خروج بعض المعلومات عن أن يتعلق بها العلم القديم. قال:
والمحقق هو الذي يدور مع الدليل ولا يجبن. وهذا يناظر ما ذهب إليه من أن القدرة الحادثة تؤثر في حال، والكسب، ولا تتعلق [بقدرة] القديم.
وهذا القول لا نراه ولا نرضاه، ومن المستحيل أن يخرج ممكن عن القدرة القديمة، مع عموم التعلق، وتحقق التماثل في المتجددات. وكذلك لا سبيل إلى أن يخرج معلوم عن العلم القديم، لما قررناه من استواء المعلومات بالإضافة إلى العلم، إذ ما تعلق بأحد المثلين، مع صحة تعلقه بالمعدودات التي يجوز افتراقها، وجب أن تستوي النسبة إلى الجميع. وقد أجمع أهل الإسلام على أن الله تعالى عالم بجميع المعلومات، لا يشذ عن علمه معلوم.','1','454',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (229,'وما ذكروه من أن العلم بالجملة يناقض العلم بالتفصيل، فلا نسلم المضادة في ذلك، ولو قدرنا أن الإنسان علم جواهر جسم وعددها، وعلم أن غيره يعلم الجسم من حيث جملة الجسم، [لما كان في ذلك مضادة]. وهذا هو الذي لا يصلح عندنا غيره.
وقوله: وإفضاء العقل إلى أمور جميلة منها، اقتصار على الدعوى. وما ذكره من أن المتناهي يستحيل أن يدرك حقيقة ما لا يتناهى، دعوى ثانية غير مقترنة ببرهان. نعم، الذي يستحيل أن يعلم [معلومات لا تتناهى عددا، هو الحادث]، لما قررناه من الدليل. أما المتعلق بمطلق لفظ ما لا يتناهى، فلا يفيد. والباري تعالى غير متناه وجوده، بمعنى أنه لا آخر له، وذلك غير مانع من علم ذاته. ويعتقد أهل السنة أن الله تعالى يرى في الآخرة، والرؤيا أخص من العلم. على ما سبق تقريره.
قال (٣٠/ب) الإمام: (وعبر الأوائل عن ذلك) إلى قوله (سلطنة الكل على الجزء). قال الشيخ: ماذكره الإمام حكاية عن الأوائل، مبني على غير','1','455',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (230,'أصول أهل الشريعة، فإنهم ينفون الصانع المختار، ويثبتون الفيض على الدوام. فلا منع ولا إعطاء، وإنما يرجع ذلك إلى اختلاف القوابل، وذلك يرجع إلى اختلاف التهيؤ للقبول. وهذه أصول باطلة كلها. وأدلة حدث العالم يسقط جميع هذا الهذيان.
ولو وقع التنزل على فاسد أصلهم، فقضاؤهم بأنه لا يبلغ النهى مبلغا يقبل من الفيض ما يحيط من الكل. فليس في هذا إلا اقتصار على محض الدعوى. وكذلك قولهم: وأما الاحتواء على الحقيقة، فهو سلطنة الكل على الجزء.
قال الإمام: (وأما ما يحمل على تبلد العقل) إلى قوله (في زمن ما مع تكرر المقتضيات). قال الشيخ: هذه الأمور كلها هي أصول الفلاسفة','1','456',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (231,'المنكرين للخلق والإيجاد، الذاهبين إلى العلل والمعلولات. وقواعد أهل الإسلام على خلافها.
ثم أيضا هم متحكمون في قولهم: إنه من الممكن أن يدرك بالعقل الخاصية الجاذبة للحديد في المغناطيس. فإن قالوا: يستحيل خرق العوائد، لاعتقادهم إياها عقليات، وليس في العقول صانع مختار ليخرق، فهو أيضا مبني على تلك الأصول الباطلة.
ثم نقول لهم: ما المانع من وصول بعض العقلاء إلى إدراك هذا؟ وبأي','1','457',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (232,'طريق علمتم نفيه؟ وما كل من أحاط بعلم يكشفه لأهل الأرض. فلا يرجعون إلا إلى محض الدعوى العرية عن البرهان.
فأما ما يتعلق بالخواص وتأثيرها، فهو باطل عندنا، ولا فاعل إلا الله. والجواهر متماثلة والأعراض لا تنتقل. وكل ذلك مدلول عليه في إثبات الإلهية والوحدانية.
وإنما الغامض في هذا الفصل قول الإمام: (ولكن ينقدح عندي في ذلك أمر يحمل التعذر عليه) إلى آخره. لا يتصور أن يكون ما ذكره عذرا عند أهل الإسلام، ولكن الظن به أنه إنما أراد: أنه ينقدح عندي لبعضهم على بعض. فإن الأوائل اختلفوا في ذلك. فكأنه يقول: وينقدح عندي لهم أمر يحمل التعذر عليه على مقتضى قواعدهم.','1','458',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (233,'ثم قوله بعد ذلك: (وعلى الجملة لا يقوم برهان على التحاق هذا القسم بالمواقف) إلى آخره. هذا أيضا كلام صعب، فإنه إذا جعل استقراء العوائد دليلا على استحالة ما لم تجر به العادة، أفضى ذلك إلى جحد النبوات، وإنكار المعجزات، واستحالة بعث الأموات. وهذا كفر بلا إشكال.
ويعتذر أيضا عن هذا بأن يقال: لا يقوم لبعضهم برهان على بعض في أن ذلك موقف بالنظر إلى عد صلاحية تهيؤ المفيض إلى آخره. وإذا بطل التلقي من هذه الجهة، رجع إلى استقراء العوائد على أصولهم، في اعتقاد وجوب الأطراد، واستحالة الخرق عقلا. هذا هو الذي أراده الإمام رحمه الله، ولا يصح غيره.
قال الإمام: (وأما الميز بين الجواز المحكوم به، ويبن الجواز بمعنى (٣١/أ) التردد والشك، فلائح. ومثاله: أن العقل يقضي بجواز تحرك جسم ساكن) إلى قوله (وإذا لاحت الحقائق، فليقل الأخرق بعدها ما شاء).','1','459',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (234,'قال الشيخ - رضي الله عنه -: الجواز كما ذكر، قد يعبر به عن: قبول المعلوم لأمري على البدل، كقبول الجوهر أن يكن متحركا أو ساكنا. وهذا الجواز نقيض الاستحالة. وقد يعبر به عن: التباس المر بحيث لا يدري الناظر كيف الحال فيه، فيقول: يجوز أن يكون العالم قديما، ويجوز أن يكون حادثا. هذا هو الجواز بمعنى التردد.
وما ذكره في المثال، وهو انحصار الأجناس، واستدلاله على ذلك بأنها معلومة على التفصيل، وذلك مستحيل في غير المتناهي، كلام باطل، وقول غير صحيح. والذي عليه أهل الإسلام أن الله تعالى عالم بالمعلومات','1','460',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (235,'على التفصيل. فاقتصر على الدعوى في مثل هذا الأمر العظيم. ولم يأت بدليل بحال.
وقوله: فإن ما يحيل دخول ما لا يتناهى في الوجود، يحيل وقوع تقديرات غير متناهية في العلم. دعوى، ما الدليل على ذلك؟ ومن أين يلزم من كون','1','461',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (236,'الموجود متناهي العدد أن يكون المعلوم متناهيا، وقد حصل موجوداً؟ فقد','1','462',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (237,'هجم على أمر عظيم، وخالف أدلة العقول، وراغم إجماع المسلمين.
وأقرب ما يدل على كون الباري تعالى عالما بما لا يتناهى على التفصيل، أن نقول: ما من معلوم إلا ويصح من الباري تعالى أن يخلق لعبده علما متعلقا به، ولا يختص هذا بموجود أو معدوم، محقق أو مقدور. وإذا جاز أن يخلق علما بمعلوم، وعلما آخر بمعلوم آخر، امتنع الانتهاء إلى حد يستحيل معه تقدير خلق علم آخر، وكذلك إلى غير نهاية. فوجب لذلك كونه عالما بالمعلومات غير المتناهية على التفصيل. وللمتكلمين في ذلك أدلة كثيرة. والذي ذكرناه مقدار غرضنا، وقواطع السمع على تأبيد نعيم أهل الجنان، وعذاب أهل النار','1','463',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (238,'إلى غير نهاية، والله تعالى عالم بتفصيل ذلك.

قال الإمام رحمه الله: <span data-type="title" id=toc-49>(فصل- مدارك العقول في الدين ثلاثة) </span>إلى قوله (العقل المحض في الديانات). قال الشيخ: قد سبق في أول الكتاب أن صفات الله تعالى واجبة لا جائز فيها، وأفعاله جائزة لا واجب فيها، فإنه يستحيل عليه كل ما قام بالحوادث، فدل على حدثها، وأفعاله دالة على صفاته. ولما ثبت للعالم الحدث، دل على جوازه. وهذا يستقصى في علم الكلام.
قال الإمام: (والمدرك الثاني- هو المرشد) إلى قوله (مقنع في غرض هذا الفصل). قال الشيخ أيده الله: أما المدرك الثاني، فسيأتي الكلام عليه [بعد','1','464',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (239,'هذا]. أما المدرك الثالث، وهو أدلة السمع، فقد تقدم الكلام عليها.
وقوله: (والتحقيق في ذلك يستدعي تقديم أصلين). إما يفتقر إلى تقديم، لأنه لا يرى أن المعجزة تدل من جهة نزولها منزلة التصديق بالقول، فيفتقر حينئذ إلى إثبات العلم بصدقه، ليعلم صدق من صدقه. وأما من رأى أن المعجزة يحصل بها العلم بصدق المدعي، فلا يفتقر إلى تقديم هذا الأصل. نعم، (٣١/ب) لابد من تقديم الكلام على الأصل الثاني، وهو بيان اقتضاء المعجزة صدق من ظهرت على يديه، فإنه إذا لم يثبت صدقه، لم نتحقق الأحكام المتوجهة علينا أنها من عند الله.','1','465',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (240,'قال الإمام: (ولكن القدر الذي يتفطن له العاقل أن [العالم] لا يخلو عن [نطق نفسي]، ثم النطق النفسي لا يكون إلا على حسب تعلق العلم. وإذا كان كذلك، لم يكن إلا صادقا). قال: (فإن فرض فارض إجراء شيء في النفس على خلاف العلم، فهو وسواس وتقديرات، ولا يتصور فرضها إلا حادثة. وهذا لقدر على إيجازه مقنع في غرض هذا الفصل). قال الشيخ: الباري تعالى متكلم بكلام أزلي، قائم به، ليس حرفا ولا صوتا، وخبره سبحانه صدق، لا يتصور الخلف فيه والكذب، ولابد من دليل على الأمرين جميعا. أما إثبات كونه متكلما، فقد ذكر الأئمة في ذلك طرقا:
منها- أن الحي إما أن يتصف بالكلام، وإما أن يتصف بأضداده، وأضداد الكلام آفات، وهي مستحيلة على القديم سبحانه وتعالى.','1','466',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (241,'الثاني- أن الكلام له، إما أن يكون مستحيلا، وإما أن يكون جائزا. ونعلم نفي الاستحالة ضرورة، إذ حاصله راجع إلى أن العالم بالشيء يستحيل أن يخبر عن معلومه. وهذا ظاهر البطلان. فإذا بطلت الاستحالة، فمن المحال أن يكون جائزا، إذ الجائز لا يتصور قيامه بالقديم، وهذا يفتقر إلى بيان أن المتكلم من قام به الكلام. وهذا واضح، كما أن العالم من قام به العلم. فإنا لو سمعنا متكلما يتكلم، لعلمنا كونه متكلما، من جهة قيام الكلام به، وإن لم يخطر ببالنا، ل هو خالق الكلام أم لا؟ ولو كان حقيقة المتكلم من فعل الكلام، لم يعلم كونه متكلما إلا من أحاط بذلك علما.
الثالث- أن الأمة مجمعة على أن الله تعالى متكلم بكلام حق. وهذه الدلالة لا تستمر إلا على [القول] بأن المعجزة تدل على الصدق.','1','467',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (242,'وأما الدليل على كون الكلام صدقا، فإن الصدق: هو الخبر على وفق العلم. والله تعالى عالم بالمعلومات على ما هي عليه. وكلامه قائم به وفق علمه، فوجب أن يكون حادثا. أما الكذب الذي هو كلام النفس على الحقيقة، فلا يتصور إلا من الجاهل الذي يعتقد على نقيض ما هو عليه، فيخبر عنه على ذلك.
قال الإمام: (والأصل الثاني- في اقتضاء المعجزة) إلى قوله (يكفي من','1','468',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (243,'غير حاجة إلى رابط). قال الشيخ وفقه الله: قد اختلف الناس في حد المعجزة، وجهة دلالتها، وفي مدلولها.
فأما أحدها: فقد ذكر الإمام حده. وقد ذكر غيره غيره. وقال أبو الحسن: فعل أو ما يقوم مقامه. وإنما افتقر إلى هذه الزيادة، لأنه يصح [عنده] أن تكون المعجزة عدما خارقا، كما لو تحدى بأن ينعدم جبل فينعدم، كان ذلك خارقا، وصح أن يكون معجزة، وإن كان العدم ليس بفعل. ولا فرق في التحقيق بين الإعدام والعدم، إن ذلك يرجع إلى نفي محض. ويعترض أيضا على الحد (٣٢/أ) [بما] إذا ظهر الخارق مكذبا، فإنه لا يكون معجزة. وهذا له صورتان:','1','469',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (244,'إحداهما- أن يدعي أن آيته أن تنطق يده، فتنطق مكذبا له.
والصورة الثانية- أن يتحدى بإحياء ميت، فيحيا قم يكذبه. فأما الصورة الأولى، فمتفق على أنها لا تكون معجزة، لأن الخارق خرق للصدق، فإذا كان الخارق مكذبا، لم يصح أن يكون مصدقا. وهذا في التمثيل بمثابة ما لو قال المدعي: أنه رسول الملك، وأن آية صدقه أن يكلم الملك رعيته على خلاف عادته، ثم يستدعي ذلك من الملك، فيقول: هو كاذب فيما قال، فلا يستريب الحاضرون في كونه لم يصدقه بنطقه، وإن كان خارقا لعادته.
وأما الصورة الثانية، ففيها نظر، إذ يمكن أن يحتج المدعي بأن الخارق قد وجد، ولما حيي هذا، صار من جملة المعاندين المكذبين. والذي نختاره','1','470',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (245,'أنه لا يكون صادقا في ذلك أيضا. فإن الحاضرين إذا شاهدوه حيي وكذب، ثم مات على الفور، لا يحصل لهم العلم بالصدق على حال.
فإذا الصحيح في حدها أن يقال: المعجزة معلوم خارق للعادة ظاهرا على حسب سؤال مدعي النبوة، غير مكذب، مع امتناع وقوعه في الاعتياد من غيره، إذا كان يبغي معارضة.
وقد نوزع في كون ما ظهر خارقا. وقالوا: ما المانع من كون ذلك ما','1','471',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (246,'وقع في العادة؟ واتفق لبعض الناس، ففرق بين النادر والخارق. وهذا هوس، فإن من تخيل أن انقلاب العصا حية، وانقلاب البحر أطوادا، وإحياء الموتى، مما جرى بصفة الندرة، فقد خرج عن حكم المعقول بالكلية.
وأما وجه دلالتها: فقد ذهب ذاهبون إلى أنها تجري مجرى أدلة العقول، فلا يتصور عقلا إظهارها على أيدي الكذابين. وهذا اختيار الأستاذ أبي إسحاق، والإمام في (الإرشاد) يشير إليه.
واستدل على ذلك بأن قال: لو دلت من جهة العادات، لأمكن خرقها، وأن توجد غير دالة، وإذا خرجت عن أن تدل لنفسها، لم يتوصل الباري سبحانه إلى تصديق رسله بالمعجزات، والعجز عليه محال. وهذا الكلام فيه نظر، وإنما يصح العجز، لو كان نصب دليل عقلي للصدق ممكنا،','1','472',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (247,'وعجز عنه الباري تعالى، فحينئذ يقضى بالاستحالة.
وأما الخصم فإنه يقول: ليس في الإمكان دليل يدل على الصدق لنفسه، لأن المقدور إنما يكون معتادا أو غير معتاد، فإن كان معتاداً، فلا اختصاص له ببعض المتحدين. وأما غير المعتاد، فهو الذي يقول الخصم: إنه يدل لنفسه.
وقال كثير من المتكلمين: إنها تدل على جهة العادات، ولا تتنزل منزلة أدلة العقول. وذا القول والصحيح عندنا.','1','473',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (248,'ودليله أنه لو [كانت] الدلالة على الصدق من صفة نفس الخارق، لما تصور خارق إلا دالا على الصدق، وهذا باطل بالاتفاق.
فإن قيل: الخارق المقترن بالدعوى هو الذي يدل لنفسه. قلنا: لا يصح ذلك، إذ ما يدل لنفسه، لا يتصور فيه الشرط، وليس الاقتران بالدعوى مما يغير صفة الخارق ويجعل له صفة نفسية لم تكن له.
قال الإمام: (فإن قيل: أيتصف (٣٢/ب) الرب تعالى [بالاقتدار] بأن','1','474',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (249,'يظهرها) إلى قوله: (لانسلت العلوم عن الصدور، كما سبق تمثيله في قرائن الأحوال). قال الشيخ: هذا هو الصحيح، على ما اختناه من دلالتها من جهة العادات. فإن العادات يصح خرقها، وتجويز الخرق لا يمنع من حصول العلم.
وما ذكره الإمام من كونه يعرض لسبب إنكار العلم بصدق الرسل تقدير','1','475',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (250,'اعتراض، فإنه لما قال: تتنزل منزلة قرائن الأحوال، قيل له: العلم المترتب على قرائن الأحوال ليس نظريا حتى يتصور اختلاف العقلاء فيه، بل الحاصل على إثرها ضروريا. فلو كانت المعجزات تدل من جهة العادات، لاشترك المطلعون عليها في حصول العلم، وقد وقع الاختلاف- بعد الاطلاع- من الخلق الكثير. وهذا سؤال مخيل.
فأجاب رحمه الله بأن قال: إنما يحصل العلم للمشتركين في كمال السبب، فلا يترتب العلم عليه. وهذا بمثابة ما لو سمع بعض الناس أخبار التواتر، فإن العلم يحصل لهم، ولا يدل ذلك','1','476',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (251,'على أن العلم المترتب على سبب التواتر ليس ضروريا.
وقوله: (وما أتى منكر لصدق نبي [محق]، إلا من جهات) إلى آخره. يشير إلى أن كل منكر إنما ينكر، لاختلاف بعض القيود في حقه. وهذا الذي ذكره ممكن، لكنه لم يدل عليه. ووجه تقرير الدليل فيه، الاستقراء للعوائد في المنكرين، فإن كلامهم قد أسند إنكاره إلى جحد القيود أو بعضها. فقال بعضهم: {إنما يعلمه بشر}. وقال بعضهم: {هذا ساحر كذاب}. وقال بعضهم: {أساطير الأولين اكتتبها}. فكل مكذب قد قدح في المعجزة ولم يعترف بها.
وأما الأمر الثالث: وهو مدلولها، فقد قال قائلون: مدلولها كونه صادقا.','1','477',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (252,'وهؤلاء لا يفتقرون إلى إثبات الكلام لله - عز وجل -. وقال قائلون: مدلولها كونه مصدقا، وهو الصحيح. فإنه قد ادعى واستدعى من الله تعالى ما يدل الخلق على صدقه، فيفعل فعلا مطابقا لدعواه، ليقوم مقام قوله: صدق. فلابد على هذا من إثبات الكلام الصدق.
وقوله: (ولو خرق الله تعالى العادة بإظهارها على أيدي الكذابين، لانسلت العلوم عن الصدور). قال الشيخ: هذا كلام حسن، وذلك أن الأدلة العقلية والسمعية يترتب العلم [فيها] على استتمام النظر في الدليل.
وأما العلم المترتب على قرائن الأحوال، فلا سبيل إلى ضبطها حتى يحصل من ضبطها ترتب العلم عليها. وهذا كالعلم المترتب على خبر التواتر، والشبع المترتب على الأكل، والري الترتب على الشرب. وكذلك كل علم','1','478',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (253,'مترتب على أساس غير مضبوطة. وإنما يستدل على جريان السبب بكماله بالعلم المترتب عليه. فإذا ظهرت المعجزة، وحصل العلم بصدق المدعي، أمنا من كونه كاذبا، لحصول العلم بصدقه.
فإذا أظهرها الله تعالى على أيدي الكذابين، لم يخلق العلم المترتب عليها. وكذلك القول في الخبر المتواتر الذي يترتب العلم عليه، فإن كانوا كذلك، أو كان فيهم (٣٣/أ) كاذب، لم يخلق الله تعالى له العلم بالصدق.
قال الإمام: (ونقول بعد هذين الأصلين: الأصل في السمعيات: كلام الله','1','479',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (254,'[تعالى]، وهو مستند قول [الرسول - عليه السلام -]) إلى قوله (فمنها تلقي الكتاب، والأصل الكتاب). قال الشيخ: قوله: الأصل في السمعيات كلام الله. قد تقدم الكلام قبل هذا على أنه لا حاكم على الحقيقة، باعتبار الشرع إلا الله - عز وجل - فالحكم يرجع إلى كلام الله القديم. وإذا قلنا: أمر الرسول، أو القاضي، فبمعنى أنهم يبلغون أحكام الله - عز وجل -، لا أنهم مستبدون بحكم من قبل أنفسهم. فإذا جردوا النظر، وميزوا الحكم، بان أن الحاكم هو الله تعالى خاصة، وإن نظروا إلى المبلغ المعرف، فهو الرسول خاصة، فإن الله تعالى إنما علمنا أحكامه بواسطة الرسل، ولا حاكم إلا الله، ولا مبلغ إلا الرسول - عليه السلام -.','1','480',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (255,'قال الإمام: (وأما الإجماع فقد أسنده معظم العلماء) إلى قوله (عنينا به المتواتر النص الذي ثبت أصله وفحواه قطعا). قال الشيخ - رضي الله عنه -: قوله: إن الآية ليست على مرتبة الظواهر. اقتصارا على الدعوى من غير أن يبين الوجه في ذلك. ولكن تقرير خروجها عن الظواهر من وجوه:
أحدها- أن (غير) لا تتعرف إلا بالإضافة إلى معرفة، إن تعددت الأغيار، باتفاق من أئمة اللسان.
وإن انضبطت القسمة، فقد قال بعضهم: إن الإضافة في هذه الصورة إلى المعرفة تقتضي تعريفا، كقوله تعالى: {غير المغضوب عليهم ولا الضالين}.','1','481',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (256,'وهذا هو اختيار ابن السراج. وأطلق آخرون القول بأن (غير) لا تتعرف بالإضافة. وهذا القول أقرب إلى العربية من جهة أن التعيين لم يأت من جهة الإضافة، وإنما جاء من الحصر، حتى أن من لا يعرف الحصر لا يدله اللفظ على التعيين. فإذا ثبت ذلك من المذهبين، فـ {غير سبيل المؤمنين}، لا تتعرف بهذه الإضافة، إذ الأغيار هاهنا كثيرة: من الكفر والشقاق، والخروج على الأئمة، والمخالفة في الأحكام، وارتكاب محرمات الشريعة، إلى غير ذلك.','1','482',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (257,'وقد جاء النبي - عليه السلام -: (من غشنا فليس منا). و (سباب المؤمن فسوق، وقتال كفر). و (من حمل علينا السلاح فليس منا). فهذه كلا غير سبيل المؤمنين.
الوجه الثاني- أنه قال: {ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين}. فهل الجملتان [شرطا جزاء]، أو كل واحدة شرط مستقل، ويكون الكلام اشتمل على ذكر أحد الجملتين، ولم يذكر جزاء الأخرى لدلالة الكلام عليه؟ فعلى التقدير الأول، لا يكون في [الآية] تعرض لمخالفة أهل الإجماع على انفرادها، وإنما يكون فيها ظهور على التقدير الثاني.
الثالث- هو أن سياق الآية يدل على أنه أريد بغير طريق المؤمنين، ما يرجع إلى المناصرة والمعاضدة والجهاد وإعلاء كلمة الإسلام. فإن الآية إنما سبقت لهذا الغرض، فإن لم يكن ظاهرا، فهو محتمل، على أن أقصى الممكن فيه تسليم ظاهره. ولا يصح (٣٣/ب) الاستدلال على القطعيات بالظواهر، فإن الظاهر ملتحق في محل طلب العلم بالمجملات.','1','483',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (258,'وأما ما ذكره الإمام هاهنا، فإن ذلك يتضمن استقصاء مسائل الإجماع، وهذا له كتاب تام، فليؤخر إلى موضعه. وبقية الكلام قد تقدم.
قال الإمام: (وما ذكرناه من الخبر عنينا به: التواتر النص). قد تقدم أيضا الكلام على ذلك، ووجه الاشتراط، واختلاف الأصوليين في الاحتراز بهذه القيود، والإضراب عنها.
قال الإمام: (وأما الخبر الواحد) إلى قوله (طريق نقله أو مستند إليه). قال الشيخ أيده الله: قد تقدم الكلام أيضا على هذا. وهل يقطع بالعمل عند خبر الواحد، أو يظن الحكم بمقتضاه؟ بما يغني عن الإعادة.
وقوله: (وما عداه طريق نقله). يعني الكتاب والسنة جميعا، فإنه إذا','1','484',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (259,'دل على كلام نفسه بألفاظ تتلى، سمي كتابا، وإن كان بلفظ لا يتلى، سمع من الرسول - عليه السلام - سمي سنة، والمستند إليه هو الإجماع.
قال الإمام: (فهذا بيان العقلي المحض) إلى قوله (وإلا فليس في حقيقة','1','485',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (260,'النظر العقلي المفضي إلى العلم تفاوت). قال الشيخ: هذا الكلام من الإمام أورده حكاية عن الأصوليين، وإلا فمذهبه أن العلوم ضرورية. والضروري لا يقبل الاستدلال.','1','486',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (261,'وقولهم: تدل لأنفسها. كلام متجوز به، فإن العدم قد يدل ولا نفس له، وإنما يعنون بذلك: وجوب الارتباط عقلا.
واعتذار الإمام عندما حكم بكون العلوم ضرورية في سبب التفاوت في الإدراك، فإن الشيء قد يحوج إلى مزيد تدبر، كيف يفتقر في الضروري إلى تدبر؟ فإن قال قائل: التدبر يرجع إلى النظر، فقد بينا أن النظر على أصله لا يتأتى.
وأما الوجه الثاني من تعدد الرتب، بكأنه يقول: لا بعد في اشتراط الترتيب في العلوم الضرورية، ويكون الترتيب فيها لابد منه. قلنا: يلزم على','1','487',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (262,'أن الرتبة الأولى إذا حصلت، فتحصل الرتبة الثانية ضرورة وبديهة، إذ العلوم ضرورية، والشرط- وهو الترتيب- قد وجد. فلا يستقيم هذا الكلام مع إنكار الاستدلال.
قال الإمام: (ثم البرهان ينقسم إلى البرهان المسند) إلى قوله (وقد رسمه الأصوليون وطولوا أنفاسهم فيه، فنبدأ به). قال الشيخ: انقسام البرهان على','1','488',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (263,'ما ذكره صحيح. ومذهبه أن الدليل لا يرجع إلى مجرد النظر، فكيف يستقيم البرهان إذ لا برهان؟ وقضاؤه بأن الأحكام الإلهية كلها ترجع إلى برهان الخلف. فقد بينا أن من الأدلة على الأحكام الإلهية ما يرجع إلى [المستد] الاستدلالي بالإيجاد والتخصيص.
وقوله: (إن البرهان المستد يجري في غير الإلهيات في جميع مطالب العقل، لا ما استثناه). دعوى مجردة عن البرهان. وأما أحد ما استثناه من','1','489',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (264,'أحكام الأزل ونفي الولية، فهو راجع إلى أحكام الإلهية.
وأما الثاني، وهو كون الجوهر الفرد لا ينقسم، فلا يتلقى إلا من برهان الخلف، فإن العقل لا يقف على حد، ولا يمنع من قسمته، ولكن يقول: لو انقسم إلى غير نهاية (٣٤/أ)، لدخ غير المتناهي الوجود، وبراهين من هذا الجنس. ودخول ما لا يتناهى الوجود محال.
قال الإمام: (القول في البيان) إلى قوله (إذا استقل كلامه بالإبانة','1','490',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (265,'والإشعار بالغرض. فهذا منتهى المقصود في هذا الفن). قال الشيخ - رضي الله عنه -: ظاهر كلامه أن أصحاب العبارة الأولى والثالثة متفقون، وإن كان أحد اللفظين أكثر احترازا، وليس الأمر كذلك. بل القائل الأول يرد البيان إلى الاستدلال بالدليل، والإرشاد إلى جهة دلالته. والثالث رد البيان إلى نفس الدليل، على أن لفظ الدليل مشترك عند المتكلمين، فإنه قد يطلق على الدال، وقد يطلق على الدلالة، وهذا الثاني هو اختيار القاضي رحمه الله. والأمر في ذلك قريب، وإنما ترجع المسألة فيه إلى اللفظ.','1','491',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (266,'قال الإمام: (فأما الكلام في مراتب البيان) إلى قوله (والاكتفاء بذكر الإجماع أولى، ولا دفع للسؤال). قال الشيخ: الكلام واضح كله. وما اعترض به أبو بكر على الشافعي غير لازم، وذلك أن الشافعي رحمه الله','1','492',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (267,'عد ما تستند إليه الأحكام مباشرة. والمراتب السابقة كلها كذلك. وأما','1','493',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (268,'الإجماع فهو على الحقيقة ليس دليل الحكم، وإنما هو دليل دليله. وهذا واضح إذا ظهر مستند الإجماع. وإن لم يظهر مستنده، فقد يظن الظان أن الحكم أسند إليه مباشرة، وليس كذلك. بل إنما يثبت الحكم، لصحة العصمة في المستند. والإجماع يدلنا على طريق لا نص فيه، ولا دليل سواه. وإن كنا نعلم نفي بقية الأدلة.
ولو كان الإجماع كذلك، لتصور أن ينعقد الإجماع ويكون حجة مع فقدان تلك الطرق، وفرض ذلك محال، لأنه يناقض العصمة؛ إذ [يكونوا] قد حكموا من غير دليل. وقد قال الشافعي رحمه الله ما أشرنا إليه، فقال في كتاب القياس: ([لا أرى] للصحابة في القراض [مستندا] إلا ما صح عن','1','494',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (269,'رسول الله - صلى الله عليه وسلم - في المساقاة). يقول: إنهم سلكوا فيه مسلك القياس. قيل: هذا منه قلب لمجاري القياس، فإن سبيل القياس الصحيح اعتبار المختلف فيه بالمتفق عليه. وهذا اعتبر المتفق عليه بالمختلف فيه.
قال الشافعي: الإجماع لا ينعقد [هزلا]. وتمام التقرير يقتضي الالتفات إلى الأدلة التوقيفية، نقلية أو قياسية. فإليها يستند أهل الإجماع. فتبين بهذا سقوط السؤال. وسيأتي مقررا بأبلغ من هذا في ذلك المكان، إن شاء الله تعالى، وهو حسبنا ونعم الوكيل.
قال الإمام: (وقد ذكر بعض الأصوليين صنفا آخر من الترتيب) إلى قوله','1','495',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (270,'(في لفظ الشارع له ذكر. ثم له مراتب، لسنا لها الآن). قال الشيخ: هذا الترتيب غير صحيح، فإنه قدم لفظ الشارع مطلقا على الفعل الواقع بيانا. وقد','1','496',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (271,'يكون اللفظ ظاهرا، وقد يعلم بالقرائن ما قصده بالفعل، [فكيف يصح التقديم مطلقا؟ والإشارة (٣٤/ب) أيضا فعل] اقترن به القول للبيان، فلم تأخرت عن قضية الصلاة الواقعة بيانا؟
وكذلك تأخير المفهوم عن العموم مع [أنه] يؤخر من جهة كونه من توابع اللفظ، لا من جهة مفهومه.
وكذلك القول في القياس، إذ لا يصح أن يقدم كل لفظ على كل قياس، إلا أن يكون المقصود أنه لولا شرعيته، لم تدل الألفاظ على أحكام الفروع، ولم تستند إليها، فيكون لهذا وجه. فأما أن يعتقد الترتيب في قوة الدليل وضعفه، فغير صحيح.','1','497',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (272,'قال الإمام: (والقول الحق عندي أن البيان: هو الدليل. وهو ينقسم إلى العقلي والسمعي. فأما العقلي، فلا ترتيب فيه على التحقيق في الجلاء والخفاء،','1','498',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (273,'وإنما يتباين من الوجهين) إلى آخر المسألة. هذا الكلام واضح، لا يحتمل أكثر من هذا.

قال الإمام: <span data-type="title" id=toc-56>(مسألة: في تأخير البيان) </span>إلى قوله (ولو بين لهم أولا لفسدوا). قال الشيخ: اختلفت مذاهب المعتزلة في تأخير البيان إلى وقت الحاجة. فمنهم من منع ذلك عموما، ومنهم من فرق بين العام والمجمل، فقال: يجوز تأخير بيان المجمل، إذ لا يحصل منه تجهيل، ولا يجوز تأخير بيان العموم، لما فيه من إلباس. ومنهم من عكس ذلك، وقال: يجوز تأخير بيان العموم، لما فيه من أصل الفائدة. بخلاف المجمل، فإن وروده لا فائدة فيه بحال. ومنهم من جوز في الأحكام، لقبولها النسخ، وهو عندهم يرجع إلى','1','499',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (274,'البيان، دون الوعد والوعيد.
والقول الحق يقتضي جواز تأخير [الجميع]. والقوم منعوه [بناء] على قواعد التحسين والتقبيح. [هذا] تقرير مأخذ [الخصم] والاعتراض عليه. ولا يحصل من مثل هذا علم، إلا أن يثبت بالبرهان انحصار المأخذ، ثم يبطل ذلك، فيحصل منه أن الحاكم متحكم، ولكن لا يحصل من هذا القول','1','500',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (275,'بطلان المذهب قطعا، إلا ببرهان يقوم على إبطاله. وليس في إبطال المآخذ المعينة، بطلان كل ما يقدر مأخذا. ولو قطعنا ببطلان كل مأخذ، لم يحصل العلم ببطلان المذهب، إذ يحتمل أن يكون صحيحا، ولم يتفق لقائله الوقوف على دليله، أو يكون مما لا دليل عليه. ومن أين يلزم أن يكون كل شيء مدلولا عليه؟ ومن أين يلزم أن يكون كل دليل يعرفه الحاكم؟ فلا يحصل من مثل هذا علم في العقليات. نعم، يكون هذا الطريق نافعا في السمعيات، من حيث تقرر أن الله تعالى لا يحكم على العباد بحكم حتى ينصب لهم عليه دليلا. فإذا علم انتفاء الأدلة، علم نفي الحكم، لاستحالة تكليف ما لا يطاق، إما عقلا، وإما سمعا.','1','501',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (276,'ولكن الدليل على أنه ممكن، صحة وجوده، فإن المستحيل لا يتصور وجوده بحال، ويصح للواحد منا أن يأمر عبده ووكيله بخطاب عام، وهو يقصد تخصيصه بعد ذلك، لمصلحة يراها، أو بخطاب مجمل، [وهو] يقصد بيانه بعد ذلك. ولو كان [ذلك] مستحيلا، لم يتصور بوجه.
قال الإمام: (الثاني: يتعلق بمناقضتهم مذهبهم هاهنا بأصلهم في النسخ، [فإنه] عندهم: بيان مدة التكليف. [وليس هذا] البيان مقترنا بمورد الخطاب الأول. وليس لهم عن هذا جواب). قال الشيخ وفقه الله: ما ذكره الإمام غير (٣٥/أ) لازم للقوم، فإن البيان هو الدليل على حسب ما مر. وإذا كان كذلك، لم يمنع ورود دليل، وإنما الممتنع أن يكون الدليل يتعرض للدليل الأول، إما بتخصيص أو تفسير. فأما إذا كان لا يتعرض له، وإنما يتضمن قضية أخرى، فلا مانع من ذلك. والنسخ عند المعتزلة لا يتعرض للفظ','1','502',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (277,'الأول بحال، فإن الذي دل عليه اللفظ الأول لا يرفع. نعم، [إذا] كانوا يجوزون [ورود] مثله، فالخطاب الثاني منع مثل الحكم الأول من الورود. يدل على ذلك ورود النسخ على النص الذي لا يقبل البيان بوجه. فإذا البيان يرجع إلى إزالة ظاهر أو اشتراك مجمل. وهذان النوعان هما اللذان منعهما القوم.
قال الإمام: (والمسلك [الثالث]: يتعلق بمطالبتهم) إلى قوله (ثم يفسر لهم في وقت الحاجة). قال الشيخ: هذا المسلك الشرعي مقطوع به تواترا','1','503',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (278,'واستفاضة، فإنا نعلم بالضرورة أن تفاصيل الصلاة، وما تفتقر إليه من شروط وبيان أركانها، وما تتوقف صحتها عليه، من قراءة، أو طهارة، وستر عورة، واستقبال قبلة، وما يقدح في صحتها من المفسدات، وكذلك الزكاة، والصيام، والحج، وما يتعلق بالبيع، والنكاح، وما يصح تزويجه، وما شروط العقد، مما لا يمكن أن يبين في وقت واحد. وهذه الطرق تجري على جميع المذاهب، ولا يفرق بين عام ومجمل، ولا بين حكم ووعيد.
تبقى المسألة مع المجوزين، اختلفت مذاهبهم في جواز التدريج في البيان. فقال قائلون: إذا شرع، وجب أن يبين الجميع، فإن اقتصاره على إخراج صورة من العموم، يوهم كون الباقي مقطوعا باستقراره. وهذا أيضا باطل من وجهين:
أحدهما- أن من توهم ذلك تاما، فقد أخطأ كمن توهمه أولا، والسابق','1','504',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (279,'من الأدلة يقضي عليه.
الثاني- أنه قد نقل في الشريعة أنه كان [يبين] ما تدعوا الحاجة إلى بيانه، فلما سئل عن الاستطاعة، فقال: (زاد وراحلة). ولم يتعرض لأمن الطريق في ذلك الوقت، وإن كان شرطا. بل كان - عليه السلام - لا يعتني بالبيان وقت توجه الخطاب اعتناءه بوظائف النقل، فضلا عن المقترنات.
وقد منع قوم تأخير المخصص عن اللفظ العام، بناء على غير هذا الأصل، مصيرا منهم إلى أنه لو تجرد اللفظ عن القرائن، لكان نصا في الاستغراق، فيرجع ذلك إلى استحالة بيان النصوص. وهذا الأصل سيأتي الكلام عليه بعد هذا، إن شاء الله تعالى.

قال الإمام: <span data-type="title" id=toc-57>(القول في اللغات ومأخذها) </span>إلى قوله (ونحن نذكر الآن','1','505',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (280,'مسائل على شرط هذه الترجمة، إن شاء الله تعالى). قال الشيخ: قوله: معظم الكلام في الأصول يتعلق بالألفاظ والمعاني. لم يرد بذلك عموم المعاني المعقولة، فإنه لو أراد ذلك، لم يبق من أصول الفقه شيء. وإنما أراد المعاني القياسية. وإذا كان كذلك، لم يكن معرفة وضع الفقه ومعرفة عرف الشريعة مكتفى به في علم الأصول، لتعلق الكلام فيه بطرف صالح من المعقولات. فلذلك قال: معظم الكلام.
وقوله: لما كان فنا مجموعا ينتحي ويقصد، لم يكثر منه الأصوليون.','1','506',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (281,'اكتفاء بما ذكره أهل اللسان، إلا أن تدعوه ضرورة إلى ذكر شيء مما ذكره أهل اللسان، ولا يجدون بدا من الكلام عليه، كالكلام على (من) الزائدة (٣٥/ب)، هل تكون مؤكدة للنفي؟ وغير ذلك. ثم صرفوا عنايتهم لما لم يذكره أئمة اللسان، وإن كان من اللغة، لأن أئمة اللسان لم يقصدوا إلى معرفة مدلولات الألفاظ، وإنما قصدوا النطق على حسب ما نطق [به] أهل اللسان، حتى إنهم يتكلمون على الكلمة باعتبار إعرابها، وغير ذلك، وإن كانوا لا يفهمون المدلول في اللغة. واللغوي المحض الباحث عن [معرفة] [مدلولات الألفاظ] لا يحسن صنعة الإعراب وعلم التصريف. وقصد الأصوليون إلى شرح المدلول، باعتبار الألفاظ التي ظهر مقصد الشرع فيها، ليحصل الغرض من فهم مقصود الشرع بألفاظه.','1','507',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (282,'قال الإمام: <span data-type="title" id=toc-58>(مسألة: اختلف أرباب الأصول في مأخذ اللغات. فذهب قوم إلى أنها توقيف من الله تعالى) </span>إلى قوله ([وإذا احتمل الكلام الأمرين، لم يكن فيه حجة]). قال الشيخ: قد تردد الناس في البحث عن هذه المسألة، هل لها تعلق بأصول الفقه، أو لا فائدة فيها؟ والصحيح عندنا أنه لا فائدة','1','508',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (283,'فيها، وذلك أن كل أمر يستوي العلم به وعدمه، بالإضافة إلى حصول أمر آخر، فلا حاجة إلى علمه في تحصيل مطلوبه. وقد بينا أن الاحتياج إلى معرفة اللغة العربية، إنما احتيج إليه بالإضافة إلى فهم الأحكام، وقد أمرنا الله تعالى بتنزيل أحكامه على ما نفهمه من اللغة العربية، إلا أن يثبت للشرع تصرف في بعضها، فيجب التنزيل على ما قرره الشارع من اللغة. على ما سيأتي مفصلا، إن شاء الله تعالى.
وإذا (٢/ب) كان كذلك، [فمفهومها لا يختلف، سواء كانت]','1','509',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (284,'اصطلاحا أو توقيفا. ولذلك قال بعض الأصوليين: الكلام عليها في الإطلاق وامتناعه. وهذا كإطلاق زيد قاتلا مثلا، وكون الخمر محرمة، مع قضاء العقول بأن مزهق الأرواح من الأشباح هو الله سبحانه. وأن الأعياد لا تكون متعلق التحريم. فإن كانت توقيفا من الله تعالى، استحال أن يكون الوضع لإزهاق الروح على التحقيق، بل إنما يكون معنى قتله، أي فعل عنده [فعلا] يحصل عنده الموت، أو يكون القتل عبارة عن زهوق النفس، فيكون قولنا: قتل زيد تجوزا. كما قال تعالى: {وسئل القرية}. فإنه لا يقال في هذا إنه جعل القرية اسما للأهل، بل للقرية، وأضمر الأهل. فإن كانت اللغة اصطلاحا،','1','510',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (285,'أمكن الغلط على الواضع في المعقولات، كما وقع للمعتزلة. وأمكن أيضا أن يكون عالما بالمدلولات، فيكون الاصطلاح على هذا التقرير كالتوقف.
قال الإمام: (مسألة: ذهب بعض من ينتمي إلى [أصحابنا]) إلى قوله (لا يجري هذا في محل النزاع قطعا). قال الشيخ: هذه المسألة يحتاج إليها في الأصول، فإنه إذا ثبت عموم الاسم بطريق القياس اللغوي، اندرجت','1','511',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (286,'المسميات تحت العموم، ولم يحتج إلى القياس وشرائطه، حتى يكون قوله: حرمت الخمر، يتناول النبيذ. وكذلك اسم السارق يتناول النباش، فدخل تحت قوله: {والسارق والسارقة فاقطعوا أيديهما}.
والأمر في التقدير على ما ذكره الإمام [بلا] مزيد. هذا إذا ثبت اختصاص اللغة في أصل الوضع، ولم يثبت طرد القياس في اللغات بحال، فإنا نعلم من مذهب العرب أنهم لا يلتزمون بطرد (٣٦/أ) الاشتقاق وإن كان الاسم مشتقا، لأنهم قد يأخذون المحل قيدا في التسمية، فيسمون الفرس أدهما،','1','512',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (287,'لسواده، وكميتا لحمرته. والآدمي المتلون بذلك اللون أو الثوب، لا يستحق هذا الاسم.
فإذا لم [يطرد] ذلك في لغتها، بل وجد الانقسام، امتنع القياس مضافا إليهم. وإن أطلق المطلق اللفظ، ولم يسنده إلى لغة العرب، لم يمتنع منه. وأما أسماء الفاعلين والمفعولين، فقد ثبت عموم الاشتقاق منهم فيها، فلا يكون ذلك قياسا. بل ذلك وضع منهم، فثبت أن اللغة نقل كلها.
قال الإمام: (مسألة: في ألفاظ استعملها العرب، وجرت في ألفاظ','1','513',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (288,'الشرع) إلى قوله (ومساق ما ذكره أن المسمى [بالصلاة]: الدعاء فحسب، وليس الأمر كذلك). قال الشيخ: رد على القاضي من غير أن يذكر له مستندا. وقد ذكر القاضي مستندين:
أحدهما- أن هذه الألفاظ لغوية، وقد تعبدنا بفهم لغة العرب، فلو نقلها الشارع وجعل لها مدلولات أخر، لم يكن بد من بيان التغير، وإلا [جر] ذلك تلبيسا وتجهيلا وتكليفا بمحال.
الثاني- أن هذه الألفاظ مذكورة في القرآن، والقرآن عربي كله، وإنما','1','514',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (289,'يكون اللفظ عربيا، إذا كان منطوقا به، دالا على مدلوله عندهم. فإذا جعل اللفظ عبارة عن غير مدلوله، أو عبارة [عن] مدلوله، أو عبارة عن بعض مدلوله]، فليس ذلك لغة العرب. ولو قال: أكرم العلماء، وأراد المجانين، لم يكن آتيا بلغة العرب، وإن كان لفظ العلماء لغويا.
وقد وجه القاضي على نفسه [أسئلة] فقال: قال الله تعالى: {وما كان الله ليضيع إيمانكم}. قال المفسرون: أراد به صلاتكم نحو بيت المقدس. وقال - عليه السلام -: (الإيمان بضع وسبعون شعبة، أعلاها شهادة أن [لا إله إلا الله]،','1','515',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (290,'وأدناها إماطة الأذى عن الطريق). فقال: هذا يجاب عنه بأن الصلاة دليل الإيمان، وكذلك إماطة الأذى عن الطريق أمارة للتصديق بالثواب، فيجوز تسميته إيمانا، وتسمية الشيء بما له تعلق به صحيح [لغة].
ووجه سؤالا فقال: لا يفهم واضع اللغة من لفظ الصلاة إلا الدعاء، ويفهم منه في الشرع غير ذلك. قال القاضي: وهو المفهوم منه أيضا في الشرع، ولكن شرط في الاعتداد به أمورا أخر من أقوال وأفعال. فالشرع تصرف بوضع الشرط، لا بتغيير الوضع. هذا تلخيص قوله.
أما ما احتج به من أن التغيير يفتقر إلى التعريف، فالأمر على ما قاله، وقد حصل التعريف بقرائن الأحوال، وبيان الأعمال، والتكريرات والإشارات، إلى حد لا يبقى معه ريب في المفهوم.','1','516',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (291,'وأما قوله: [القرآن] عربي كله، لا عجمة فيه بوجه، فهذا خلاف أئمة اللسان بالنظر إلى المدلول والإعراب. أما المدلول فإن: (الإستبرق) ليس في كلام العرب، وكذلك قال بعضهم: ليس (الأب) من كلام العرب. وأما ما يتلقى من الإعراب، فمنع بعض الأسماء [من] الصرف، بناء على العجمة والتعريف، وهذا مقطوع به عند أهل الفن. وفي العجمة المانعة من الصرف كلام طويل يتعلق بفن العربية، وفي الموضع الذي يمنع. ولكن ما ذكرنا مقدار غرضنا.
وأما قوله: (وإنما سميت إيمانا [لأنها] دليل الإيمان). فكلام [لا يصح]، إذ هو قياس في اللغة، (٣/ب) ونحن [قد] منعنا من إجراء','1','517',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (292,'القياس في اللفظ الدال حقيقة، [فكيف] يصح القياس في المجاز؟ فيفتقر القاضي (٣٦/ب) إلى أن ينقل عن أهل اللسان أنهم تجوزوا هذا النوع من التجوز.
قال الإمام: (وأما المختار عندنا، فيقتضي ببيانه تقديم أصل) إلى قوله ([والإمساك] في الصلاة والحج والصوم. فهذا حاصل هذه المسألة). قال الشيخ: ما ذكره الإمام من بيان العرف، وتسمية الأفعال عرفية، إنما هذا في عرف أهل اللغة، وأما عرف غيرهم، فلا التفات إليه. ولو التفتنا إلى عرف','1','518',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (293,'غيرهم، لكانت الأسماء كلها عرفية.
وأما تصرف [الشرع] بهذين الوجهين، [فلا يوجب] أن يكون هذا أمرا لغويا، أصليا ولا فرعيا، إذ لم ينطق أهل اللغة بذلك. ولو اعتبر غير الشارع مثل هذا المعنى، أو وضع اسما يقتضي ذلك، لم تكن تلك الأسماء عرفية عند الأصوليين، بل أسماء شرعية. وليس يلزم من إثبات هذه الأسماء لهذه المسميات شرعا، الإضراب عن اللغة أصلا. وهو معنى قول القاضي: ثم الشرع لا يزجر عن تسمية الدعاء المحض صلاة.','1','519',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (294,'ثم اختلف الأصوليين بعد هذا [فيما] إذا دار الاسم بين معناه اللغوي ومعناه الشرعي، فقال القاضي: مجمل. وهذا يناقض مذهبه في حجد الأسماء الشرعية، اللهم إلا أن يكون له قول آخر بإثباتها، وإلا فالإجمال مع اتحاد جهة الدلالة محال. أو يكون هذا منه تفريعا على قول من يثبتها. وهذا ضعيف، فإنه من أين له الحكم عليهم بأنهم يسوون بين [التسمية] إلى المسمين؟
وقال قائلون: هو لمعناه الشرعي، إلا أن يقوم دليل على أنه أراد المعنى اللغوي. وهذا هو الأظهر عندنا، لاستقراء عرف الشرع غالبا في إطلاق ألفاظ','1','520',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (295,'الصلاة والصوم وغيرها، مما ثبت له فيه عرف استعمال، فيصير هذا بمثابة المجاز المشهور، كالغائط والعذرة.
وقال أبو حامد: أما ما ورد في صيغ الأمر والإثبات، فهو للأمر الشرعي، وأما ما ورد في النهي، فهو مجمل. واحتج على ذلك بقوله - عليه السلام -: (دعي الصلاة إلى أقرائك). وأنه إنما أراد المعنى اللغوي، فإن الصلاة الشرعية غير ممكنة، وما لا يمكن، لا ينهى عنه. وهذا [الكلام] ضعيف، [فإنه لم يرد أيضا في الحديث الصلاة اللغوية، وهي الدعاء، فإنه غير منهي عنه. [وسيأتي] الكلام عليه] في مسألة الصلاة في الدار المغصوبة.
وقوله في تمثيل المجاز، إطلاق التحريم على الخمر، فإنه مستعار متجوز به. لا يلزم ذلك، ومن أين يلزم من كون العقل يحيل [إضافته] إليها أن يكون الإطلاق اللغوي مجازا، وهم يقولون: قتل زيد عمرا؟ وإن كانت','1','521',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (296,'القدرة الحادثة لا [تعدو] محلها، ولا ذاهب إلى كون هذا اللفظ مجازا.
وأما قوله: (إن من قال: إنها (٤/أ) نقلت نقلا كليا، فقد زل). [فصحيح] على وجه كلامهم، [فإن] الاسم إذا دل على [مدلول على صفة]، ولم تستعمله العرب إلا على تلك الجهة، فمن زاد فيه، أو نقص [منه]، أو تعداه إلى غيره، فقد غير الوضع بالكلية. وإن كان الشرع قد جعل اللفظ دالا على الدعاء والركوع والسجود، وغير ذلك. فهو نقل كلي. نعم، سبب النقل ما ذكر، من اعتبار أصل موضوع اللغة.

ومما يتصل بهذا الموضع<span data-type="title" id=toc-61> الكلام في الحقيقة والمجاز</span>. والحقيقة:','1','522',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (297,'عبارة عن اللفظ الدال بالإضافة على مدلوله، غير مقبول من غيره، ولا مستغنى [عنه]. وقد ذهب بعض الناس إلى إنكار المجاز في القرآن. وذهب المحققون إلى اشتمال (٣٧/أ) القرآن عليه.
ويمكن أن يكون المنكر، إنما أنكر المجاز في القرآن، على معنى الخلف والكذب، فقد يطلق لفظ المجاز على الباطل، فيقال: هذا القول مجاز لا حقيقة له. والظاهر خلاف ذلك.
وسبب خيالهم في ذلك، أن المجاز إنما يصار إليه لضرورة أو حاجة، وهو خلاف الفصاحة. وليس الأمر على ما تخيلوه. ورب مجاز لا تقوم الحقيقة مقامه. وقد عد من أقسام الكلام الفصيح البليغ، التجوز، كقوله تعالى: {واخفض لهما جناح الذل من الرحمة}. وكيف ينكر ذلك، وفي كتاب الله','1','523',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (298,'تعالى: {يريد أن ينقض}. {ويمكرون ويمكر الله}. {أحاط بهم سرادقها}. و {كلما أوقدوا نارا للحرب أطفأها الله}. إلى غير ذلك مما لا يحصى كثرة.
والمجاز: ما تجوز به عن أصل موضوعه. وذلك لأسباب: إما لمشاركته إياه في التعيين، وإما لملازمته إياه غالبا، كتسمية الغائط والعذرة. وإما لكونه من أثره، كقولهم: انظر إلى قدرة الله، يعني الثبات، لأنه من أثر قدرة الله، ودليل عليها.
وهو على ثلاثة أنواع:','1','524',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (299,'أحدها- إطلاق الاسم على غير ما سمي به في أصل الوضع، فتكثر المسميات بعد أن كانت قليلة، كتسمية الرجل الشجاع أسدا، والجواد بحرا.
الثاني- الزيادة التي لا تفيد، كقوله تعالى: {ليس كمثله شيء}. فإن (الكاف) وضع للإفادة، وهو ههنا غير مفيد، ولو جرى على حقيقته، لأثبت مثلا، ونفى التشبيه عنه، وهو محال.
الثالث- النقصان الذي لا يمنع فهم المعنى، ولا يلبس، كقوله تعالى: {وسئل القرية}. {والعير}. ولا يجوز: سل زيدا، وأنت تريد غلامه، إلا في الشعر. وهذا النقصان اعتادته العرب، فهو توسع وتجوز. فإن قيل: لم قضي على هذه الأضرب بكونها مجازا، وهي منطوق بها عند أهل اللسان؟ قلنا: هذا معلوم ضرورة، كما أن العرب سمت عمرا مخصوصا، وآخر زيدا. وكذلك نعلم الأسماء الدالة بالإضافة أو التجوز.','1','525',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (300,'وطريق معرفة ذلك: النقل المتواتر، إما صريحا، وإما استقراء، وهو طريق معرفة أوضاع اللغة، وليس [للقياس] في ذلك مجال. وقد ذكر بعض الأصوليين طرقا لمعرفة الفرق:
إحداها- أن الحقيقة جارية على العموم، إذ قولنا: عالم، لما صدق على عالم، صدق على ذي علم، {وسئل القرية} يصح في بعض الجمادات (٤/ب)، ولا يصح في الجميع. وهذا عندنا غير صحيح. فأما اطراد الحقيقة، فغير صحيح، فإنا قد بينا أن بعض الألفاظ الحقيقية مختصة. وأما أسماء الفاعلين والمفعولين، فقد تبين من العرب طردها. وقد يطرد بعض المجاز، فإن كل كريم [سمي] بحرا، وكل شجاع [سمي] أسدا.','1','526',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (301,'الطريق الثاني: أنه قال: يعرف المجاز بامتناع الاشتقاق منه، إذ (الأمر) إذا استعمل في الأمر حقيقة، اشتق منه (أمر)، فإذا استعمل في الشأن، لم يشتق منه (أمرا). وهذا أيضا ضعيف، لأن (الأمر) قد يكون مصدر (أمر) فليشتق منه لذلك، وقد يكون اسما للحال، كزيد وعمرو، فامتنع الاشتقاق لذلك.
الثالث: أن تختلف صيغ الجمع على الاسم، فيعلم أنه في أحدها [مجاز]، [إذ] الأمر إذا كان بمعنى القول، جمع على أوامر، وإذا كان بمعنى الشأن جمع على أمور. [وهذا] أيضا غير مطرد، فإن صيغة الجمع قد تختلف، فإنك قد تجمع فلا على فعال وفعول (٣٧/ب)، وكل ذلك حقيقة. وقد تجمعه على فعيل، ككليب وعبيد، في جمع كلب وعبد، ولا يدل على أن لفظ (فعل) مجاز في (فلس)، [ولا في (فعل)]، وإن كان (فلس) لا يجمع على فعيل، وإن صح جمع كلب على أكلب، وكذلك يجمع فعيل على فعلاء، كشريف وشرفاء، ويجمع فعيل على فعلان، كقضيب وقضبان، ولا يجمع شريف على شرفان. ولا يدل ذلك على أن اللفظ [مجاز] في أحدهما.','1','527',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (302,'الرابع: أنهم قالوا: إذا كان اللفظ له في الأصل الدلالة على تعلق بالغير، فإذا استعمل فيما لا يتعلق له [كان] مجازا. وهذا أيضا لا يفيد، لأنه يفتقر فيه إلى معرفة الوضع، وأنه وضع بالإضافة إلى ذلك المعنى دون غيره، فيثبت عدم الاشتراك والتجوز بمعرفة الوضع.
واعلم أن كل حقيقة لا يلزم أن يكون لها [مجاز]، وأن كل مجاز لابد له من حقيقة. بل ضربان من الكلام لا يدخلهما المجاز، وهما:
[الأول]-[أسماء] الأعلام، [لأنها] وضعت للفرق بين ذات وذات، فلو دخلها المجاز، لبطل هذا الغرض.
الثاني- الأسماء التي تستغرق كل مسمى بأصل الوضع، فلا [تقبل] هذا التجوز، إذ جميع المسميات دلت عليها حقيقة، فكيف يتجوز بها إلى غير مدلولها الأصلي؟
وقد توهم بعض الناس التجوز في الأعلام، واحتج بأنه يقال: قرأت','1','528',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (303,'(سيبويه)، وهو يريد الكتاب، وقد تجوز بإطلاق اسم صاحب الكتاب عليه. وهذا غلط، بل (سيبويه باق على الدلالة على الرجل، وإنما [جاء] التجوز من حذف الكتاب، لا من جهة إطلاق لفظ صاحب الكتاب. وكذلك قوله تعالى: {وسئل القرية}. لم يجعل القرية عبارة عن الأهل، بل القرية باقية على مدلولها. حذف المضاف وإقامة المضاف إليه مقامه، فيما يتعلق بالإعراب، أي يعرب المضاف إليه بما كان يعرب المضاف، لا أنه جعل الاسم للمحذوف.

قال الإمام: <span data-type="title" id=toc-63>(فصل- في ألفاظ جرى رسم الأصوليين (٥/أ) بالخوض فيها) </span>إلى قوله (وهي تنطلق على ما يفيد، وعلى ما يفيد). قال الشيخ: قوله: الكلام: هو المفيد. اختلف الناس [فيه]، فذهب الأكثرون إلى هذا،','1','529',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (304,'وهو اختيار سيبويه. وقال آخرون: القول: هو الكلام.
واحتج الأولون بأن الكلام: اسم لمصدر تكلم، وهذا البناء يقتضي مبالغة، فلابد فيه من الإشعار بالتركيب، والكلمة الواحدة لا يصح ذلك فيها، وليس المراد تركيب الحروف، وإنما يراد تركيب الكلمات.
الأمر الثاني- أن الكلام قد شرف في قلوبهم، وجل في صدورهم، وكان أعظم الأمور عندهم، وما ذاك إلا لفهم معناه، وحسن الدلالة به، والكلمة الواحدة لا تستحق ذلك، فلا يحصل منها مقصود التفاهم، ولا تستحق إعرابا، وسبيلها الأصوات التي ينعق بها.
وقوله: (والجملة معقودة من مبتدأ وخبر، [أو فعل] وفاعل). يصح على التأويل، وأم كلام أئمة العربية، فبخلاف ذلك، فإن الجمل عندهم أربع: جملة من مبتدأ وخبر، وجمل من فعل وفاعل، وجملة من جار','1','530',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (305,'ومجرور، وجملة من شرط ومشروط، [ولكنها] في الحقيقة [ترجع] إلى جملة اسمية، وأخرى فعلية. وهذا هو الذي قصده الإمام (٣٨/أ).
وقوله: ([والكلم]: جمع كلمة، كالنبق والنبقة، واللبن واللبنة). هذا محل غامض في علم العربية، فقد يكون الاسم [جمعا]، كما ذكر. وقد يكون مفردا يدل على الجنس. فليس قولنا: كلم، كقولنا: نبق. إذ يقال: نبق طيب أو حسن، ولا يقال: كلم حسن، لإشعار اللفظ بالجمع. وأكثر ما تصادف أسماء الأجناس الدالة على الجنس، مع كونها [لفظا مفردا] في المخلوقات دون المصنوعات. قال النحويون: الجنس أخو الجمع. ولم يجعلوه جمعا، لإفراد لفظه. وجعلوه أخاه، بالنظر إلى كثرة الداخل [تحتهما] جميعا. ولكن الفرق عندي [بينهما]، أن واضع لفظ الجنس قصد إلى وضع الاسم بإزاء معقول الجنس، فلم يتعرض في التسمية إلى الأعداد. وواضع لفظ الجمع تعرض للدلالة على الأعداد.','1','531',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (306,'وإنما كثر ذلك في المخلوقات، لأن الغالب فيها ما استوى المندرجات تحت مقتضى التسمية، [وإن] وقع الاختلاف في أمور عارضة. وليس المصنوعات كذلك، لكثرة الاختلاف ووجوه التباين.
وهذا بمثابة الصدر، فإنه لما وضع للقليل والكثير من جنسه، استغنى عن جمعه، لعدم تعلق غرض المسمى بالأعداد. فإن حاول التعرض للأعداد، زاد (هاء) فأحد وثنى وجمع، فقال: ضربة وضربتين وثلاث ضربات، وكذلك يقول: تمرة وتمرتان وتمرات.
وقوله: ([والكلم] الذي ينتظم منه الكلام: اسم وفعل وحرف). قلت: يفتقر في هذا المكان إلى معرفة الاسم ومدلوله واشتقاقه. أما الاسم: فإنه ينطلق على المسمى، وينطلق على التسمية، والأكثر عند المتكلمين دلالة على المسمى. قال الله تعال: {ما تعبدون من دونه إلا أسماء سميتموها}. [و] لم يكونوا يعبدون المسميات. قال الشاعر:','1','532',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (307,'إلى الحول ثم اسم السلام عليكما ... .... ... ...
وما أراد على القطع إلا السلام. فإنه لم يقصد (٥/ب): عليكم تسمية السلام.
وقالت المعتزلة: الاسم يرجع إلى قول المسمين. وهذا أيضا كثير في اللغة. ويدل عليه قوله تعالى: (ولله الأسماء الحسنى}. وقوله - عليه السلام -: (إن لله تسعة وتسعين اسما .. ). والإنصاف الاعتراف بالاشتراك في اللغة، وينزل الأمر على ما يقتضيه ظهور غرض المطلق. والكلام بين الفريقين في ذلك يطول، ولا يتعلق به غرض في الأصول.
وغرضنا في هذا المكان، الكلام على الاسم باعتبار المسمين التي هي أقوال المسمي، ثم يتصدى بعد ذلك بحث آخر في إشعار الألفاظ بما في النفس إذا أطلق الاسم، كالأمر والنهي عليهما جميعا. وهل هو حقيقة [فيهما]، أو مجاز في [أحدهما]؟ وفيه أيضا نظر. سيأتي الكلام عليه في أول كتاب الأوامر، إن شاء الله تعالى.','1','533',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (308,'وغرضنا الآن الكلام على الاسم الذي هو لفظ. قال الإمام: (كقولك: رجل وفرس ودار، وكل ما دل على [معنى] سمى به). قال الشيخ: الاسم مختلف في اشتقاقه وحده.
أما اشتقاقه: فقد ذهب البصريون إلى أنه من السمو، وهو الارتفاع، فكأنه سما بمسماه، أي رفعه عن اللبس حتى [عرف]. وذهب الكوفيون إلى أنه من الوسم، وهي العلامة. وكأن هذا القول أقرب من جهة المعنى، فإن الأسماء علامات تعرف بها المسميات، [ولكن] القول الأول أجرى (٣٨/ب) على قياس العربية، باعتبار التصغير والتصريف والتكسير. فإنك تقول: سمي: ولو كان من الوسم لقلت: وسيم. وأما التصريف، فإنك تقول: سميت تسمية، ولا تقول: [وسميت]. وأما التكسير. [فتقول]: أسماء، ولا تقول: أوسام.
وأما حده بأنه: الدال على معني سمي به. فغير مانع، إذ يدخل فيه الفعل، فإنه يدل على [معنى] [وضعه] المسمى للدلالة عليه. فلابد من زيادة تخرجه، فتقول: غير مقترن بزمان محصل.','1','534',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (309,'وقوله: (إنها ثلاثة: اسم وفعل وحرف). فدليل حصر القسمة: أن اللفظ إما أن يدل، وإما أن لا يدل. فإن لم يدل بوجه من الوجوه، فليس من أقسام الكلام. وإن دل، فلا [يخلو]: إما أن يدل على معنى في نفسه، أو على معنى في غيره. والقسمة صحيحة بعد ثبوت أصل الدلالة، فإن دل على معنى في الغير، فهو الحرف. وإن دل على معنى في النفس، فإما أن يتعرض في الدلالة لمعنى أو لذات، فإن تعرض للذات، فهو اسم، وإن تعرض للمعنى، فلا يخلو: إما أن يدل على [زمان] وجود ذلك المعنى مقرونا بالمعنى، أو لا يباشر بالدلالة الزمان. فإن دل مباشرة على الزمان، فهو الفعل، وإن لم يباشر الدلالة على الزمان، فهو المصدر، وهو من الأسماء.
قال الإمام: (ثم الأسماء تنقسم إلى: متمكن) إلى قوله (وما لا ينصرف','1','535',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (310,'يسمى متمكنا ولا ينون ولا يجر). قال الشيخ [- رضي الله عنه -]: تقسيم الأسماء كما ذكره، صحيح. وأما اقتصار البناء على [شبه] الحروف، فغير صحيح، فإن كثيرا من المبنيات لا تشبه الحروف والمنادى المضموم، ونزال بمعنى: أنزل، ودراك بمعنى: أدرك. وقد (٦/أ) أطبق أئمة العربية على أن هذا المعنى لا يقتصر البناء عليه. نعم، هذا أحد الأسباب في نحو (ما) ونظائرها. بل الصحيح أن السبب مناسبة ما لا تمكن [فيه]، كنزال أو ما [شاكله] الواقع موقعه، كفجار، أو وقوعه موقع: ما أشبهه، كالمنادى [المضموم]. أو إضافته إليه، كقوله تعالى: {من عذاب يومئذ}، و {هذا يوم لا ينطقون}.','1','536',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (311,'على قراءة من فتح.
قال الإمام: (والأفعال: صيغ دالة على أحداث الأسماء، مشعرة بالأزمان.','1','537',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (312,'والأحداث: هي المصادر، وهي أسماء ولكنها لصيغ الأفعال، كالتبر للصورة المصوغة. ثم الأفعال المبنية خلا المضارع) إلى قوله (ثم لا أجد بدا من ذكر معاني حروف كثيرة التدوار في الكتاب والسنة). قال الشيخ: قوله في الأفعال: إنها صيغ دالة على أحداث الأسماء، مشعرة بالأزمان، هو قول النحويين. وقد اعترض على ذلك بـ (كان) الناقصة، فإنها غير دالة على ما حدث، [وإنما] دلت على زمن الحدث خاصة. وهذا ضعيف، فإنها دلت على زمن ذلك الحدث، [فإنا] إذا قلنا: كان [زيد] قائما، فإنها دلت على زمن الحدث بـ (كان). فلأجل تعلق مدلولها [بالحدث]، جعلت دالة على الأحداث،','1','538',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (313,'ومدلولها [المباشر] المقصود [هو] الزمان.
قال الإمام: (مسألة: ذهب بعض الفقهاء إلى أن (الباء) إذا اتصلت بالكلام (٣٩/أ) إلى قوله (في مثل قولك: مررت بزيد). قال الشيخ: هذا الكلام كله ظاهر، إلا الإلصاق في: مررت بزيد، لأنه محمول على التوسع،','1','539',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (314,'أي: اتصل مروري بزيد.
قال الإمام: (ومن أحكامه: تعدية الفعل اللازم من: قمت [وقمت] به، وذهبت وذهبت به. وهذا قياس جار مطرد).
قال الإمام: (مسألة: مما خاض [فيه] الفقهاء) إلى قوله (فالقول الأخير بعد استكمال الكلام الأول في حكم البيان له، فكان الكلام بآخره). قال الشيخ [أيده الله]: الأمر كما ذكر في (الواو) لا تقتضي ترتيبا. وقد يصح','1','540',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (315,'في وضع اللغة أن يقول القائل: جاءني زيد اليوم، وعمرو أمس، ولا يكون بذلك متجوزا. وقد قال الله تعالى: {وادخلوا الباب سجدا وقولوا حطة}. وفي موضع آخر: {وقولوا حطة وادخلوا الباب سجدا}. ويقول القائل: ([وسيان] قيامك وقعودك)، ولا يدل على تقديم أحدهما على الآخر. واستعمالهم إياه في باب التفاعل، دليل واضح على بطلان الترتيب.','1','541',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (316,'فلئن قال قائل: هي ليست نصا في الترتيب ولكنها ظاهر، فإن دل دليل على تقدير الترتيب، بقي على اقتضائه في غير هذا المكان. قلنا: لو كان كذلك، لكان قول القائل: تقاتل زيد وعمرو مجازا، وليس الأمر كذلك.
وأما مسألة القائل: أنت طالق وطالق. أما من ذهب إلى أنهما يلزمان جميعا، فلا خفاء بعدم ورود السؤال عليه. فإن المذهب بعيد عن غرض الترتيب. وليس الأمر كذلك، فإن هذا إنما يتخيل في الإخبار عن الأمور التي تقبل التقديم والتأخير [والاجتماع]. وأما الأمور المنشأة التي ترتب وجودا، فلا يتصور فيها هذا. وإذا قال الحالف لامرأته: أنت طالق، فهو منشئ لذلك، وإنما تأتي الطلقة الثانية بعد البينونة، فلم تصادف (٦/ب) محلا لذلك.
وهذا ظاهر، ولكن مالكا رحمه الله لم ير ذلك.
وقول الإمام: (إن الكلام الأول تام، فبانت به). فنحن لا نسلم تمامه،','1','542',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (317,'ولو قال قائل: أنت طالق إن كان كذا، لاعتبر الشرط، ولم نقل: بانت بالكلام الأول. وكذلك لو قال: هذا المال لزيد وعمرو، فقد نزل العطف في عدم تمام الجملة منزلة الشرط. فلا يتم قولهم: أنت طالق، ويكون كلاما، إلا إذا اقتصر عليه. فأما إذا عطف عليه، صار الاسمان كالجملة الواحدة، وكذلك عطف جميع المفردات.
فإن قيل: يلزم على هذا إذا قال للمدخول بها: أنت طالق ثلاثا، وأنت علي كظهر أمي، أن [يلزماه] جميعا. قلنا: ينبغي أن يتنبه لأصل، وهو أن التثنية أصلها العطف، وإنما جعلت التثنية طلبا للاختصار، فإذا اضطر الشاعر إليها قال:
كأن بين فكها والفك ... ... ...
فنحن إنما نقيم العطف مقام التثنية في المفردات التي يصح أن تثنى أو','1','543',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (318,'تتجانس. فأما الجمل المتقاطعة، فلا يمكن ذلك فيها، والطلاق جنس خلاف الظهار، فلا يصح [جعلهما] جملة واحدة.
قال الإمام: (وأما من [قال] إنها للجمع، فهو أيضا متحكم، فإنا على القطع نعلم أن من قال: رأيت زيدا وعمرا، لم يقتض ذلك أنه رآهما [جميعا]. فإذا مقتضى (الواو) العطف والاشتراك، وليس فيه إشعار بجمع ولا ترتيب. نعم، قد [ترد] في غير غرض المسألة بمعنى الجمع، إذا قلت: لا تأكل السمك وتشرب اللبن، أي لا تجمع بينهما. ومنه قول الشاعر:
لا تنه عن خلق وتأتي مثله ... عار عليك إذا فعلت عظيم
فلا تكون (الواو) عاطفة في ذلك. فإن أردت العطف قلت: لا تأكل السمك وتشرب اللبن، وأنت تعني النهي عن كل واحد منهما. والمعنى: لا','1','544',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (319,'تأكل السمك ولا تشرب اللبن.
[وقد] ترد (الواو) في باب المفعول معه بمعنى (مع) تقول: استوى الماء والخشبة) إلى قوله (ولكنه للترتيب مع التراخي). قال الشيخ [أيده الله]: وقد قدمت الكلام على هذا بأنها تستعمل فيما [يعقل] [منه] الاجتماع، كما ذكرناه. وإذا قال: جاءني زيد أمس وعمرو اليوم، فقد صرح بضد الاجتماع، والكلام حقيقة لا مجاز فيه.
وقوله: إنها ترد في غبر غرض المسألة لغير العطف في قولك: لا تأكل السمك وتشرب اللبن. قال الشيخ: (الواو) لا تصلح لنصب الفعل بحال. وسنتكلم عليه في باب (ما) إن شاء الله. وإنما يقع الانتصاب بعدها بإضمار (أن) وهي تضمر بعد خمسة أحرف: حتى، واللام، [و] (أو)، بمعنى (إلى)، و (واو الجمع)، و (الفاء)، بعد الأمر والنهي، والنفي والاستفهام','1','545',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (320,'والتمني والعرض. والضابط لذلك أن لا يكون الكلام ثابتا، كقولك: مررت حتى أدخلها، وجئتك لتكرمني، ولألزمنك أو تقضيني حقي، ولا تأكل السمك وتشرب اللبن، واتيني فأكرمك، ولا تشتمني فأشتمك، وما تأتينا فتحدثنا، وهل لي من شفيع فيشفع لي. ولا يقع النصب إلا (بأن)، وإنما كان كذلك (٧/أ)، لأن الحروف الداخلة على الاسم والفعل لا تعمل في واحد منهما. والناصب أيضا للفعل لا يكون إلا ناصبا، وهذه قد توجد غير ناصبة. وإنما وجب في العوامل أن [تكون] على صفة واحدة، لأنها لو لم تعمل مرة، لم تعمل مطلقا. فلذلك قلنا: إن النصب لغيرها. وإنما اختص الإضمار بالنفي، لأن الكلام يحتمل معه جهات لا يحتملها الإثبات. وقد تتوسع العرب في النفي حتى قالوا: مررت برجل لا قائم ولا قاعد. ولا يقولون: مررت برجل قائم وقاعد. وربما جاء النصب بعد (أن) مضمرة في الإيجاب. قال الشاعر:
سأترك منزلي لبني تميم ... وألحق بالحجاز فأستريحا
وهو ضرورة. ولكن الذي حسنه عندي أنه غير ثابت من جهة المعنى، لقوله: سأترك، [وسألحق].','1','546',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (321,'وسننبه على لطيفة في العربية في هذا المكان، وذلك أنه لما تقدم في هذا الموضع الجملة الفعلية، وجهات هذه الحروف الصالحة للعطف، ولم يقصد الناطق العطف، تحول الكلام إلى الاسم، فلو بقى إعراب الثانية على حسب إعراب الأولى، لفهم العطف، فأرادت العرب إزالة اللبس بنصب الفعل الثاني، ليعلم أنه لم يقصد عطفا. وإنما يصير الكلام إلى الاسم عند وجدان الفعل، إذا رد إلى المصدر، وإنما يكون كذلك، إذا قدرت فيه (أن)، فيكون الفعل معها مقدرا بتأويل المصدر، فنصبوا لها الغرض.
ولكن يبقى في هذا نظر، وهو عطف الجملة الاسمية على الجملة الفعلية، وهو أيضا رديء، فيفتقر إلى تأويل الجملة الأولى. قال سيبويه: (لقولك: ما تأتينا فتحدثنا، معنيان:
أحدهما- ما تأتينا، فكيف (٤٠/أ) تحدثنا؟ أي لو أتيتنا لحدثتنا.
والآخر- ما تأتينا أبدا إلا لم تحدثنا، أي منك إتيان كثير، ولا حديث [معه]). فانظر كيف رد الجملتين جميعا إلى المصدر. وطريقه ما قررناه، ووجه الحاجة إليه ما بيناه.
ويمتنع إظهار (أن) مع كل هذه الأحرف إلا (اللام) إذا كانت لام (كي)، فإن الإضمار واجب تارة، وجائز تارة، وممتنع تارة. فأما الموضع الذي يجب فيه الإضمار، فإنه إذا كان الذي تدخل عليه [داخلة عليه] (لا)','1','547',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (322,'كقولك: كيلا [يعطيني]. ويجوز الإظهار إذا لم يكن الفعل كذلك.
وأما المؤكدة، فليس معها إلا التزام الإضمار، في مثل قوله {وما كان الله ليعذبهم وأنت فيهم}. {وما كان الله ليضيع إيمانكم}.
وقوله: ([وقد] ترد (الواو) في باب المفعول معه بمعنى (مع) تقول: استوى الماء والخشبة). كما قال، ولكن يشترط في انتصاب المذكور [بعد (الواو)] أن يكون الكلام الأول يشتمل على فعل أو على رائحة فعل. وأما إذا لم يكن كذلك، فليس إلا الرفع، كقولك: ما أنت وقصعة من ثريد. والنصب في الوجه الأول حسن، والرفع جائز، كقولك: استوى الماء والخشبة، أي تساويا. وإنما لم يجز النصب في الوجه الثاني، لأن الفعل تعدى إلى الاسم الثاني بواسطة (الواو). وهذا نظير الاستثناء في قولك: قام القوم إلا زيدا.','1','548',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (323,'وقوله: (فأما (الفاء) فإنها للترتيب والتعقيب والتسبيب (٧/ب)، وقد ترد بمعنى (الواو) للعطف). قال الشيخ: (للفاء) محلان عند النحويين: أحدهما- أن تكون عاطفة. والثاني- أن تكون جواب شرط صريح، كقوله: (من أحيا أرضا ميتة فهي له). أو مقدر، كقوله تعالى: {فأما اليتيم فلا تقهر}.','1','549',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (324,'وكذلك قوله تعالى: {الذين ينفقون أموالهم بالليل والنهار سرا وعلانية فلهم أجرهم}.
فأتى بالفاء في معنى الجواب. وهي في كلا حالتيها لابد [فيها] من الترتيب.
واختلف النحويون، هل تكون زائدة؟ أنكره سيبويه، وأثبته الأخفش. واحتج بقوله:
وقائلة: خولان فانكح فتاتهم ... ... ... ...
وللتأويل فيه مساغ، إذ يمكن أن تكون عاطفة، [ويكون التقدير]: هذه خولان فانكح فتاتهم. وقد اعترض على هذا بقوله تعالى: {وكم من قرية أهلكناها فجاءها بأسنا}.
والبأس قيل: الهلاك. وله تأويلان: أحدهما- أن يكون المراد: أردنا إهلاكها.','1','550',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (325,'والثاني- أن يكون المراد: أهلكناها، فحكمنا بأن البأس جاءها، أي أخبرنا بمجيء البأس.

قال الإمام: <span data-type="title" id=toc-72>(مسألة: تحوي مراسم الأصوليين في معاني الحروف) </span>إلى قوله {والسماء وما بناها}. معناه: [و (بنيناها)]. قال الشيخ: تنقسم (ما) إلى الحروف والاسم، كما ذكره.
و(ما) النافية لا يعلمها بنو تميم، والحجازيون يعلمونها بشرط أن لا يليها إلا الامس، وأن يبقى الخبر منفيا، فإن تقدم الخبر، كقولك: ما قائم زيد،','1','551',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (326,'أو [انتقض] النفي (بإلا)، كقولك: ما زيد إلا قائم، اتفقت المذاهب، وبطل العمل في القول الفرزدق:
فأصبحوا قد أعاد الله نعمتهم إذ هم قريش وإذ ما مثلهم بشر
كلام طويل يخرجنا عن الغرض. والنكتة فيه أن [بني] تميم لا تعملها وإن تقدم الاسم، (٤٠/ب) فكيف تعملها إذا عكس الترتيب؟ وإنما فعل بنو تميم هذا لأمرين:
أجدهما- أن كل حرف داخل على البابين، حقه أن لا يعمل في واحد منهما لأمرين:','1','552',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (327,'[أحدهما]- الاستقراء. والثاني- المعنى. وذلك أنا قد بينا أن عمل العامل لا يفارقه بحال. والأثر الذي تقبله الأسماء، لا تقبله الأفعال، والذي تقبله الأفعال، لا تقبله الأسماء، فلو علمت في الأسماء، لم يفارقها العمل عند الدخول على الأفعال، والمحل لا يقبل.
الوجه الثاني- أن الحروف اختص عملها بجهة واحدة، فحروف النصب لا تصلح لغيره، والجزم كذلك. وإنما يعمل العملين المختلفين الأفعال، لقوتها ك (كان) وأخواتها. وإنما خرج عن هذا، الحروف المشبهة بالأفعال (كإن) وأخواتها، ولكن [تلك] يمتنع دخولها على الأفعال بالكلية.
وأما ما يشبهها بالأفعال، فهي أبعد من تلك، [لأن] تلك أخذت شبه الأفعال لفظا ومعنى، و (ما) ليس [فيها] إلا شبه معنوي لفعل ضعيف. وقد قال سيبويه: ومذهب بني تميم هو القياس، ولكنا نختار مذهب الحجازيين لوجهين:
أحدهما- أن القرآن أفصح اللغات، وهو على ذلك، قال الله تعالى: {ما هذا بشرا}.
الثاني- أن الشبه الخاص مقدم على الشبه العام، وفي (ما) مشابهة عامة','1','553',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (328,'للحروف، [وللداخلة] على البابين في ظاهر الحال شبه خاص، بالإضافة إلى (ليس)، لنفي الحال، و (ما) كذلك، فكان الالتفات (٨/أ) إلى الشبه الخاص أولى.
وأمل قولهم: الداخل على الأسماء والأفعال لا يعمل في واحد منهما. فاعلم أنا حققنا أنه ليس [للحروف] أن تفعل رفعا ونصبا، وإنما عملت ذلك، حملا لها على غيرها، وهي (ليس). و (ليس) لا يصح دخولها على الفعل، فـ (ما) الداخلة على الفعل لا تشبه (ليس) التي عملت، فلم تعمل الأخرى.
وأما كونها إذا [نقض] النفي لم تعمل، فهي إنما شبهت (ليس)، من جهة اشتراكها في النفي. فإذا رجع الكلام إلى الإيجاب، بطلت المضارع. ولم تعمل (ليس) للشبه بشيء، بل بالإضافة، فاستوى في عملها النفي والإثبات.
وقوله: (وأما [ما] وقع اسما، فينقسم إلى منكور وموصول) ,','1','554',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (329,'القسمة صحيحة، إذ لا تخلو الأسماء: إما أن تكون معارف أو نكرات. فلنقدم الكلام على النكرات، إذ التنكير سابق على التعريف، ولذلك كان التعريف أحد الأسباب المانعة من الصرف، لأجل كونه فرعا.
أما المنكور، فهي: الاستفهام، والشرط، والتعجب. أما في الاستفهام والشرط، فلأجل إيهامه وعدم اختصاصه، كان التنكير أليق به. وأما التعجب، فقد اختلف أئمة اللسان فيه؛ فذهب سيبويه إلى أن (ما) فيه نكرة. وقال الأخفش: بل هي معرفة موصوفة. وإنما حمل سيبويه على المصير إلى التنكير أمران: أحدهما- أن الجملة تكون معه تامة غير مفتقرة إلى حذف ولا إضمار.','1','555',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (330,'والثاني- أن المعنى الذي حسن التنكير في الاستفهام، حسنه في التعجب. فإنه لا يتعجب إلا مما خفي سببه، والتنكير مناسب لذلك.
والذي حمل الأخفش على المصير إلى أنها معرفة، الفرار من الابتداء بالنكرة على غير شروطها، فإنما لزم في (٤١/أ) المبتدأ أن يكون معرفة أو قريبا منها، فأولى وأحرى ألا يعرف الخبر، فيخرج الكلام عن الفائدة. وقد يبتدأ بالنكرة إذا أفادت، وذلك في ستة مواضع:
[الأول] إذا وصفت كقولك: رجل عالم خير من جاهل.
الثاني: أن يتقدم الخبر، كقولك: في الدار رجل، وتحتك فرص، وهذا عجيب. والكلام بحالة والمفهوم سواء في التقديم والتأخير، ولكن السبب في ذلك: أن النكرة أحوج إلى الصفة منها إلى الخبر، فإذا وضعت صفتها متأخرة، أمكن تحصيل الغرض الأهم، فلزم أن تقع صفة، وأما إذا قدم قولك: في الدار، لم يتصور أن تكون صفة، لامتناع تقديم الصفة على الموصوف. فلما بطلت الصفة، وهي الأمر المهم عند التأخير، حمل الأمر على الخبر.
فإن قيل: ولم امتنع تقديم الصفة، وجاز تقديم الخبر، وإن كانا لا يقعان إلا تابعين؟ فنقول: الصفة تعد جزءا من الموصوف، وكأن الاسم لا يتم عند السامع إلا بها، والخبر لا يؤتى به إلا بعد أن يفهم السامع المبتدأ، فهو من هذه الجهة غير تابع، وإن كان لا يخبر إلا عن مبتدأ. (٨/ب)','1','556',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (331,'الموضع الثالث الذي يبدأ فيه بالنكرة: أن يكون دعاء، كقولك: سلام عليك. قال الله تعالى: {سلام على آل ياسين}. {وويل للكافرين}. وإنما صح الابتداء بها في هذا المكان، حملا على الفعل، كأنه قال: سلمهم الله، وأهلكهم الله.
الموضع الرابع: أن يكون الكلام معها غير موجب، كاستفهام أو نفي، كقولك: ما أحد في الدار، هل أحد في الدار؟ لما فيها من معنى العموم، وقد ارتفع بها الاسم كقولك: أقام الزيدان؟
الخامس: إذا كان جوابا، كما إذا قيل لك: من جاءك؟ قلت رجل، أي: رجل جاءني، وقد يصرح بذلك. وقولهم: (شر أهر ذا ناب). (وشيء ما جاء بك). كلام محمول على المعنى. والسبب فيه أن المتكلم سمع هرير الكلب فاستدل به على الشر، فكان الشر في موضع فاعل، لسبق الفعل عند المتكلم، فكأنه قال: أهر ذا ناب شر. وكذلك قوله: شيء ما جاء بك، فإنه سبق إليه مجيئه، وعلم أنه لا يجيء به إلا شيء. فاللفظ لفظ المبتدأ، والمعنى معنى الفاعل.','1','557',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (332,'ولما انحصرت هذه المواضع عند الأخفش، ورأى باب التعجب خارجا عنها، افتقر إلى أن يقدرها موصولة، ولكن على مذهبه، تنقص الجملة، وتفتقر إلى التكميل، إذ يصير التقدير: الذي حسن زيدا شيء، والإضمار على خلاف الأصل. وقد بينا أن السبب المحسن للابتداء بالنكرة في الشرط والاستفهام، موجود هاهنا.
وأما قوله: (والموصول: ما له بد من صلة). فالأمر كذلك، وحصر الصلة في الجملتين قد تقدم الكلام عليه، بل الجمل التي يصح أن تقع صفات، تقع صلات، ولابد من الصلة من راجع منطوق به أو مقدر. وقد قال الله تعالى: {أهذا الذي بعث الله رسولا}. أي بعثه، وقال: {الذي يتخبطه الشيطان من المس}.فأظهر الضمير، وإنما افتقر إليه، ليربط الكلام. وأما لو قلنا: جاءني الذي عمرو منطلق، لم يكن كلاما. وسمع الخليل رجلا يقول: ما أنا بالذي قائل له سوءا، يريد هو [قائل].
قال الإمام: (فأما (أو) فهي (٤١/أ) للتردد والشك) إلى قوله (أو','1','558',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (333,'تقضيني حقي. معناه: إلى أن تقضيني حقي). قال الشيخ [أيده الله]: النحويون يقولون: إنها على أربعة أوجه: الشك، والإباحة، والتخيير، والإبهام. فالشك في الأخبار، كقولك: جاءني زيد أو عمرو. والتخيير فيما كان المخاطب ممنوعا من تناوله. فيقال: خذ من مالي دينارا أو درهما. والإباحة فيما ليس أصله للحظر، كقولهم: (جالس الحسن أو ابن سيرين). والإبهام في مثل قول القائل: جاءني زيد أو عمرو، وهو يعلم من جاءه منهما. ويمكن أن يكون قوله تعالى: {مائة ألف أو يزيدون}. من هذا المعنى.','1','559',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (334,'وقوله: ((وأم) في معناه، إلا أنه قد يقع في موضع الكلام مقرونا بالاستفهام). هذا الذي ذكره، لا تقتصر (أم) عليه، بل قد تقع بعد استفهام، وقد تقع من غير تقدم استفهام، فإن وقعت بع استفهام، سميت متصلة، أي تربط آخر الكلام بأوله. فإذا قال القائل: أزيد عندك (٩/أ) أم عمرو؟ معناه: أيهما عندك؟ فهو سؤال عن التعيين، كقولك: أيهما عندك؟ ولا يصح في هذا المكان أن يقول المجيب: أحدهما، بل الجواب: زيد [أو] عمرو بالتعيين.
ويصح أن يقول: زيد عندك أم عمرو؟ لا على طريق الاستفهام، وتسمى المنقطعة، أي انقطع عن الكلام الأول، واستأنف قضية أخرى. قال الله تعالى: {أم يقولون افتراه}. ولم يتقدم استفهام. ومن قول العرب: (إنها لإبل أم شاء)؟ . معناه: (بل أهي شاء)؟ وأما إذا قال: رأيت زيدا أو عمرا،','1','560',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (335,'فيصح أن يقول: أحدهما، لتردد السائل في أصل الرؤية، فهو يسأل عن أصلها.
أما قول الإمام: (إنه لا يصح أن يقول: زيدا أم عمرا). فقد بينا أنه','1','561',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (336,'يصح على المنقطعة، وفي المتصلة على تقدير أن تزاد الهمزة. قال الشاعر:
لعمرك ما أدري وإن كنت داريا ... بسبع رمين الجمر أم بثمان
وقوله: (لا يصح أن يقال: أرأيت زيدا أو عمرا؟ ). فقد قال أهل العربية: إنه يصح ذلك، ولا أعرف فيه خلافا.
وقد نصوا على قولك: أرأيت زيدا أو عمرا؟ وردوا الفرق بين (أم) و (أو) إلى ما ذكرناه من أن (أم) لتحقيق الأصل والتباس التعيين، وليس (أو) كذلك. وبقية ما ذكرناه واضح.','1','562',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (337,'وقوله: (قد تكون (أو) بمعنى (إلى). قد تقدم كلامنا عليه، وكيف الوجه في النصب قبل هذا. وباقي الكلام مفهوم.
قال الإمام: (وأما (هل) فمعناه: الاستفهام) إلى قوله (وأنت تبغي الحث على المجيء). قال الشيخ [وفقه الله]: (هل)، [و (الهمزة)] [حرفا] استفهام، ولا يتحتم [لهما] ذلك في كل موضع، ولكن الهمزة أعم تصرفا من (هل). تقول: أبصرت زيدا وهو أخوك؟ قال الله تعالى: {ألست','1','563',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (338,'بربكم قالوا بلى}. ولا يصح دخول (هل) في هذا. وكذلك لمن قال مررت بزيد: أبزيد مررت؟ وتوقعها قبل (الفاء) و (الواو) و (ثم). قال الله تعالى: {أو كلما عاهدوا عهدا}. وقال: {افمن كان على بينة من ربه}. وقال: {أثم إذا ما وقع آمنتم}. فلا تقع (هل) في هذه المواضع.
وقال الشاعر:
أطربا وأنت قنسري؟ ... .... ... .... ... ....
ولا يجوز أن يقال: هل طربا وأنت قنسري؟
قال الإمام: (وأما (لا) فمعناه: النفي، وقد تكون (٤٢/أ) للتبرئة، فتتصل إذا باسم منكور) إلى قوله (وفي الشاذ: [{لأقسم}]. قال الشيخ: (لا)','1','564',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (339,'تكون للنفي خاصة إذا جاءت لمعنى، وقد تكون زائدة، كما ذكر.
وقوله: إذا كانت زائدة لا تكون إلا لتأكيد نفي اشتمل الكلام عليه، ليس بصحيح، وقد قال الله تعالى: {لئلا يعلم أهل الكتاب}. أي ليعلم أهل الكتاب. وليس هنا: نفي تؤكده (لا).
قوله: وفي الشاذ: [{لأقسم}]. ليس كذلك، بل في السبع.
قال الإمام: (وّأما (لو) فحرف يدل على امتناع الشيء لامتناع غيره، تقول: لو جئتني لجئتك، أي امتناع مجيئي لامتناع مجيئك. وقد تكون بمعنى','1','565',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (340,'(إن) قال الله تعالى: {ولأمة مؤمنة خير من مشركة ولو أعجبتكم}. معناه: وإن أعجبتكم. وقد تفيد معنى التقليل كقوله - عليه السلام -: (انقوا النار ولو بشق تمرة) إلى قوله (٩/ب) (هلا نفر). قال الشيخ: (لو) حرف يدل على امتناع الشيء لامتناع غيره، كما ذكر الإمام. والأقسام التي ذكرها صحيحة، ولكن بقي من مواضعها أغفله، وهو أنه قد تكون بمعنى التمني، تقول: لو تأتيني فتحدثني بالنصب، أي [أتمنى] ذلك، ويجوز الرفع. قال الله تعالى: {ودوا لو تدهن فيدهنون}. وفي بعض المصاحف: {فيدهنوا}.','1','566',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (341,'وأما (لولا)، فهي على ما ذكره في التقسيم، ولكن [تفسيره]: لولا زيد لأكرمتك، أي امتنع إكرامي إياك لوجود زيد. فإن هذا يوهم صحة دخول (لولا) على الفعل، إذا كانت للامتناع.
وبينهما أيضا فرق، وذلك أن (لو) لا تدخل إلا على جملتين كاملتين نطقا، كقولك: لو جئتني لأكرمتك. وأما (لولا) فقد التزم العرب حذف خبر الجملة الأولى، وقد سد الكلام مسد الخبر. ونظيره قولك: ظننت أن زيدا منطلق، فإن تقديره: ظننت انطلاق زيد، كائنا أو واقعا، ولكن استغنى عنه، لطول الكلام. والسبب في اختصاص (لو) بالفعل أنها للشرط، والشرط بالأفعال أليق، والسبب في اختصاص (لو) بالفعل أنها للشرط، والشرط بالأفعال أليق، فالاسم يرتفع بعدها بأنه فاعل. وقد امتنع في اللغة إضمار فعل الفاعل، إلا على شريطة التفسير.','1','567',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (342,'وخصت (لولا) بالاسم فرقا بينها وبين (لو)، وحذف خبر المبتدأ سائغ. وإذا وقع الاسم بعد (لو) أو حرف الشرط، فيرتفع بأنه فاعل بفعل مضمر، يفسره هذا الظاهر. تقديره في قوله: [(لو)] زيد قائم لو زيد قام. وكذلك متى وقع الاسم بعد حرف الشرط، أو ما في معناه، كقوله: {إذا [السماء] انشقت}. معناه: [إذا] انشقت السماء انشقت.
وأما إذا كانت للتخصيص، فلا تدخل إلا على الفعل، وتصير حينئذ كحروف الشرط في استدعاء الفعل، ولا تدخل إلا على فعل ماض أو مستقبل. فإن وقع بعدها اسم منصوب أو مرفوع، فبإضمار رافع أو ناصب. قال الشاعر:
تعدون عقر النيب أفضل مجدكم ... ني ضوطرى لولا الكمي المقنعا','1','568',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (343,'قال الإمام: (وأما (من) فحرف جار خافض) إلى قوله (تقول: أخذت من [على] الفرس جله).
قال الشيخ: النحويون يقولون: (عن) للمجاوزة والبعد، [وإذا] كانت','1','569',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (344,'(عن) لا تقتضي الفصل [والبعض]، وهذا معنى (من)، فكيف تكون في معناها؟ والعبارة الأولى التي عبر بها النحويون أولى.
قال الإمام: (٤٢/ب) (وأما (إلى) فحرف جار، وهو للغاية) إلى قوله (من الجمعة إلى الجمعة). قال الشيخ: (إلى) كما ذكره، لانتهاء الغاية. وقد ذهب بعض النحويين إلى أنها في قوله تعالى: {إلى المرافق} بمعنى (مع). فهذا موضع اختلف الفقهاء فيه: فمنهم من أوجبه، ومنهم من لم يوجبه.','1','570',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (345,'والسبب في هذا الاختلاف: تقابل أصلين عند اللفظ. فإن صح أنها ظاهرة في انتهاء الغاية، لزم من ذلك الانتهاء إلى المرافق. وإن كانت مترددة، فحينئذ يتقابل الأصلان. والالتفات إلى (١٠/أ) براءة الذمة يقتضي أن لا تشتغل إلا بلفظ دال، إما قطعا وإما ظنا. والالتفات إلى شغل الذمة بطلب شرط الصلاة، يقتضي أن لا يقدم عليها إلا [محقق] الشرط.
وأما قوله: (إن (من) تدخل الزمان والمكان). هذا مذهب الكوفيين، أما البصريون فإنهم يمنعون ذلك، ويقولون: كما اختصت (منذ) بالزمان، اختصت بالمكان. ويتأولون قوله تعالى: {من أول يوم}. أي من تأسيس أول يوم. وكذلك يتأولون قول الشاعر:
.... ... .... ... أقوين من حجج ومن دهر','1','571',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (346,'والظاهر ما ذهب إليه الكوفيون. والمصير إلى التأويل من غير دليل لا وجه له، إلا أن يستقرأ أن العرب لم تدخلها على الزمان، فيصح ما قالوه.
قال الإمام: (وأما (مذ) و (منذ)، فيختصان بالزمان. ولا يدخلان [على المكان]، واستعمالهما في الزمان، أفصح من استعمال (من). تقول: منذ أسبوع انتظره. [وهذا] أحسن من قولك: من أسبوع [انتظره]. وإذا استعملت (من) قرينة (إلى)، لم تقم [(عن)] مقامها أصلا. فإن قيل: زيد أفضل من [عمرو] من أي قبيل؟ هو لاقتضاء الغاية. والمعنى: [ساوى] زيد عمرا في فضله، وابتدأ زيد زيادة عليه في الفضل. كما تقول: سرت من البصرة إلى بغداد. ولهذا لا تستعمل (عن) في هذا الباب).','1','572',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (347,'قال الشيخ: قوله (مذ) و (منذ) يختصان بالزمان، كلام صحيح.
وقوله: واستعمالهما في الزمان أفصح من استعمال (من). هذا إنما هو على مذهب الكوفيين.
وأما قوله: زيد أفضل من عمرو، يعني (من) الداخلة على عمرو، من أي قبيل هي؟ قال: هي [لابتداء] الغاية، إذ التفضيل يشعر بأنه بلغ إليه وزاد عليه. والكلام في (أفضل) طويل. ولم يورده الإمام إلا لغرض، ولذلك تركنه الكلام عليه.
قال الإمام: (وأما (على) فلفظة تقع اسما وفعلا وحرفا) إلى قوله (حتى تقضيني حقي، أي إلى أن تقضيني حقي). قال الشيخ: (حتى) تكون على ثلاثة أوجه: تكون عاطفة، كالواو، ولكنها تفارقها في أن المعطوف بها لابد أن يكون مجانسا لما قبلها، إما لفضله، كقولك: مات الناس حتى الأنبياء، أو لدونه، كقولك: قدم [الحاج] حتى المشاة. وتكون للابتداء، كقوله:','1','573',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (348,'.. وحتى الجياد ما يقدن بأرسان
وتكون جارة، وهذه الجارة هي التي ينتصب الفعل بعدها بإضمار (أن)، لأنه إذا قال: لألزمنك حتى تقضيني حقي، فمعناه: إلى أن تقضيني حقي، لأنا قد بينا أن العامل في الاسم لا يعمل في الفعل. وهذه الجارة في معنى (إلى). ولكن الفرق بينهما أن (إلى) لا يقتضي وضعها أن يدخل ما بعدها (٤٣/أ) في حكم ما قبلها، إلا إذا كانت بمعنى (مع). و (حتى) توجب التشريك في ذلك، لأن معنى قولهم: حتى رأسها، أي تصرمت شيئا [فشيئا] حتى فرغ منها.
فقول الإمام: (إنك إذا قلت: [أكلت] السمكة حتى رأسها، فقد أنبأت','1','574',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (349,'أنك لم تأكل رأسها). غير صحيح، ومن العجب (١٠/أ) احتجاجه بالبيت على ذلك، وقد روي على الوجوه الثلاثة:
بالرفع والنصب والخفض. وإذا كان الخفض عنده يقتضي أن الثاني لم يدخل فيما دخل فيه الأوا، كيف يصح أن يقال: (حتى نعله ألقاها)، فيجمع بين النفي بالحرف وبين الإثبات بالفعل؟ نعم، الافتراق يرجع إلى الإعراب واستئناف الجملة، إذا عدم استئفافها.
قال الإمام: (فأما (أي) فمعناه: أجل) إلى قوله (فليكن جوابك إذا رأيته: نعم). قال الشيخ: الأمر على ما ذكر في (بلى). وأما (نعم) فيجاب بها','1','575',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (350,'في الإثبات والنفي جميعا، وهي حرف تصديق لما سبق من الجملة. فإذا قال القائل: أقام زيد أو لم يقم؟ فصدقته، قلت: نعم. أي: الأمر على ما تقول.
قال الإمام: (وأما (من) فلا تكون إلا اسما بخلاف (ما) إلى قوله (مع اعترافنا بأن حقائقها [تتلقى] من فن النحو). قال الشيخ: ما ذكره الإمام في هذه الحروف واضح، ولكن قوله: ((أي) إنها اسم معرب). فهذا موضع اختلف النحويون فيه إذا جاءت موصولة، وجاءت صلتها محذوفة الصدر. فقد ذهب سيبويه إلى أنها مبنية، وذهب غيره إلى أنها معربة في هذه الحالة. وقد تمسك سيبويه بالقرآن والشعر. قال الله تعالى: {ثم لننزعن من كل شيعة أيهم أشد على الرحمن عتيا}.','1','576',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (351,'وأنشد أبو عمرو الشيباني:
إذا ما أتيت بني مالك ... فسلم على أيهم أفضل','1','577',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (352,'فضم [أيهم]، وهذا شاعر. وأكثر النحويين على خلاف سيبويه في ذلك. والوجه في ذلك عنده: أنه إذا كان موصولا تنزل من صلته منزلة الاسم الواحد، فإذا حذفت بعض الصلة، نقص الاسم، فأشبه الحروف. ونظير ذلك (قبل) و (بعد)، فإنه إذا نطق بما يضافان إليه، وجب إعرابهما، وإذا حذف ما يضافان إليه، فإن كان المحذوف مقصودا، وجب البناء، وإن أعرض عنه، والتفت إلى الباقي، لزم الإعراب. قال الله تعالى: {لله الأمر من قبل ومن بعد}. وقد قرئ: (من قبل [ومن] بعد). فتكون (أي) مع صلتها كذلك. وهذا كلام واضح، والسماع يعضده.
قال الإمام: ([لما] قسم أهل العربية الكلام إلى الاسم والفعل والحرف) إلى قوله (يجوز فرض السكوت عليه). قال الشيخ: القول عندي','1','578',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (353,'في النداء أنه من أبواب الخير، فإن التقدير: أنادي زيدا، [أو ناديت زيدا]. ولو صرح بذلك، لكان مخبرا. ولكنه لا يجري على مقصود الخبر، والمراد به الإنشاء، كقول القائل: بعت واشتريت. وأما (يا) فحرف، كأنه يرجع إلى التنبيه، فكأنه يقول: (يا) لتنبيه من يقصد نداءه، ثم يقول: أنادي زيدا. واستغنى عن إظهار الفعل، لدلالة الحرف عليه، ولكثرة دوره في الكلام. وهذا الفعل التزمت العرب إضماره. قال الإمام: (١١/أ) (وقال الأستاذ: التلهف والترجي والتني من أقسام الخبر) إلى آخر الفصل. (٤٣/ب)','1','579',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (354,'قال الإمام: <span data-type="title" id=toc-87>(كتاب الأوامر)</span>. قال الشيخ [وفقه الله]: ينبغي أن ينظر، هذا جمع ماذا؟ والذي عليه الأصوليون أن الأوامر جمع أمر. وأهل العربية الذين وقفنا على أقوالهم كسيبويه وأبي علي والمتأخرين، لا يرو هذا الجمع أصلا. ويقولون: لا يصح أن يجمع فعل على فواعل، وقد ذكروا [أمثلة] جمعه، وليس منها هذا البناء. إلا أن الجوهري صاحب (الصحاح) قد قال: ويقول: (أمرته بكذا أمرا، والجمع الأوامر). وهذا شاذ غير معروف عند أئمة العربية.','1','581',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (355,'وقال بعض الناس: إن هذا جمع أمر، وفواعل لا يخلو: إما أن يكون [اسما] أو صفة لمذكر. فإن كان اسما، صح جمعه على فواعل، تقول: خاتم وخواتم، وتابل وتوابل. وإن كان صفة لمذكر، لم يجمع على فواعل. وقد شذ فارس وفوارس وهالك في الهوالك. فأما فارس وفوارس، فلعدم اللبس، إذ لا يكون هذا صفة للمؤنث. وأما هالك في الهوالك، فكأنهم نحوا به ناحية النفس، [إذا] قصد تأنيثها.
وقال بعض الناس: المراد الصيغة، فإنه قد تسمى الصيغة آمرة تجوزا. وإذا كان المراد (فاعلة) صح الجمع على فواعل، اسما كان المفرد، كفاطمة وفواطم، أو صفة، ككاتبة وكواتب. وهذا بعيد في التجوز، وليس هو المقصود ههنا، إذ الكلام في الأمر الحقيقي.
وأما الألفاظ، فلم يأت الكلام فيها إلى الآن، إلا أن يكون قصد الكلمة باعتبار كونها قائمة بالنفس، ويسمى الأمر آمرا. والظاهر عند أنه [إنما] قصد ما ذكره صاحب (الصحاح) من جمع الأمر على أوامر.
ثم لفظة الأمر مصدر، والمصدر لا يثنى ولا يجمع، إلا أن تختلف أنواعه. وقد بينا أنه لا التفات عند أهل اللسان إلى تعدد المحال، كما قلنا: إن سيبويه منع جمع العلم، ولم يلتفت إلى [تعدد] متعلقاته. فلا يصح','1','582',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (356,'جمع الأمر إلا إذا تحقق اختلاف لا يرجع إلى تعدد المحال. وهذا ثابت في الأمر، إذ أمر الوجوب يباين أمر الندب، باعتبار الذات، لا المتعلق، فصح جمعه لذلك. فإذا تقرر هذا، فلفظ الأمر يدل على ثلاثة أمور:
أحدها- إطلاق الأمر على الشأن والحال، كما يقول القائل: أمر فلا مستقيم، أي حاله مرضية. وق تقول عكس ذلك، قال الله تعالى: {وما أمر فرعون برشيد}.
[الثاني]- وينطلق الأمر على اللفظ الدال على الطلب القائم بالنفس، عند مثبتي كلام النفس. وعند نفاته، اللفظ: هو الأمر نفسه. أما مثبتوه، فيطلقون الاسم عليه، من جهة أنه دليل الطلب. وأما نفاته، فيقولون: إنه الأمر لاعتبار نسبة الدلالة. ولا يطلق عند هؤلاء على المعنى القائم بالنفس.','1','583',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (357,'قال الإمام: <span data-type="title" id=toc-88>(مسألة: الأمر: قسم من أقسام الكلام. والقول فيه </span>(١١/ب) وفيما بعده من معاني الصيغ واللفاظ، يستدعي [قولا] في إثبات كلام النفس) إلى قوله (مما يتفق التواطؤ [عليه] علما [لحله] محل [العبارة]). قال الشيخ [- رضي الله عنه -]: قد قدمت (٤٤/أ) أن لفظ الأمر ينطلق على ثلاث معان على حسب ما مر. وإنما تكلم الإمام هاهنا على أمرين:','1','584',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (358,'أحدهما- اللفظ. والثاني- المعنى القائم بالنفس.
فإذا أطلق اللفظ على المعنيين قيل: هل هو فيهما حقيقة، وفي الآخر مجاز؟ فيه ثلاثة مذاهب: قول بالاشتراك، وقولان متقابلانفي النفي والإثبات
فالمسألة لغوية محضة، والقطع بأحدهما، لم يثبت عندي. وأهل العربية مطبقون على إطلاق [الكلام] على الألفاظ. وقد سمى الله تعالى ما في النفس قولا، فقال: {ويقولون في أنفسهم لولا يعذبنا الله}. وقال الله تعال والفقهاء لا يكتفون في القراءة في الصلاة','1','585',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (359,'بكلام النفس. ولكن يمكن أن يكون ذلك لتعبد في اللفظ، وهو السبب، إذ باللفظ يتحقق الإعجاز.
وقال الأخطل:
إن الكلام لفي الفؤاد وإنما ... جعل اللسان على الفؤاد دليلا
فقد أثبت الكلام قائما بالنفس، وجعل اللفظ دليلا عليه.
وأما ما ذهب إليه الإمام [من] أن الكلام الحقيقي هو القائم بالنفس، فحيد عن المقصود، فإن النزاع ليس في كون المعنى القائم بالنفس على حقيقة لا يختلف، وأن الألفاظ اصطلاحية وضعية، فإن هذه أمور معقولة لا تتعلق باللغات والتسميات، وإنما النظر في إطلاق اللفظ حقيقة أو مجازا. وهذا البحث لا يرشد إلى ذلك.
قال الإمام: (وأما المعتزلة وكل من خالف عصبة أهل الحق) إلى قوله','1','586',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (360,'(ولا علما بكيفية الصيغة، فلم يبق إلا ما حاولناه). قال الشيخ: قول الإمام: ولا نجد بدا من ذكر ما يقع الاستقلال به إلى آخره. كلام فيه إشكال، وذلك أنه لم يبين في أول الكلام إلا ادعاء الضرورة، وعليه سؤلان:
أحدهما- أن الضروري لا يقبل الاستدلال.
والثاني- أنه لا يتصور فيه من العقلاء النزاع. وأكثر الخلق على جحد كلام النفس، فكيف يتصور دعوى الضروري فيه؟ لكن يجاب عن هذا: بأنه لم يقصد الاستدلال على ما ادعى الضرورة فيه، فإنه يقول: أصل المعنى الذي نحن [نثبته]، لا ينفيه خصومنا، ولكنهم يردونه إلى الإرادة تارة، وإلى العلم أخرى. وربما أثبت ابن الجبائي كلام النفس، وسماه الهواجس. والكلام','1','587',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (361,'على هذا الوجه صحيح. وإذا رده المعتزلة إلى الإرادة، وحققنا أمرا غير مريد، ثبت العدد.
وما ذكره الأئمة من الصورة المفروضة، وكون السيد لا يريد الامتثال، فكلام صحيح، لا نزاع فيه، وإنما النزاع في أنه آمر أو لا؟ فالمعتزلة منعت كونه آمرا، لتحقيق أنه غير مريد. وعند أصحابنا أنه آمر على الحقيقة. ولكن هذه دعوى برهانها (١٢/أ) عندهم علم العبد والحاضرين بحقيقة الأمر، مستندين إلى قرائن الأحوال, وهذا يمنعه الخصم، ويقول: بل الحاضرون يعلمون خلاف ذلك. وأما العبد، فمعتقد يمكن تشكيكه عند اطلاعه على باطن الأمر.','1','588',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (362,'فتنتهي المسألة إلى هذا الحد، ولا يثبت بذلك استدلال على (٤٤/ب) الحقيقة.
وقد التزمنا في هذا الكتاب الإنصاف، وترك التعصب، بحسب الإمكان، ولا حول ولا قوة إلا بالله. لكن الصواب عندي في المسألة بناؤها على ما يتعلق بعموم الخلق وشمول الإرادة للكائنات جميعا. وإذا تقرر ذلك، بنينا عليه غرضنا، وقلنا: لو كان الله تعالى أمر بالإيمان عموما، ولا يريد الإيمان ممن كفر، وقد قال تعالى: {ولو شئنا لأتينا كل نفس هداها}.','1','589',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (363,'{ولو شاء ربك ما فعلوه}. {ولو شاء الله لجمعهم على الهدى}.
ومما اجتمعت [الأمة عليه] كلمة متلقاة بالقبول غير معدودة من محتملات الألفاظ، وهي قولهم: (ما شاء الله كان، وما لم يشأ لم يكن).
ومعنى قول الإمام في الرد عليه: (الصيغة المنبئة عن العلم بالصيغة ليست هذه). يريد بذلك أنه لو اتحد المعنى، لم تختلف دلالة الألفاظ. والمفهوم','1','590',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (364,'من قول الإمام: قم، ليس هو المفهوم من قوله: صيغة (قاف) و (ميم) محذوفة العين، منقولة من (قوم) إلى (قوم)، لسكون آخر الفعل، فيحذف العين ويرد الفعل إلى بناء آخر. هذا طريق معرفة الصيغة، وليس مدلول (قم) هذا قطعا. وكلامه في هذا حسن جدا.
قال الإمام: (ثم أثبت المعتزلة النظر طلبا زائدا على الإرادة) إلى قوله (ونحن نذكر بعد ذلك القول في حقيقة الأمر). قال الشيخ: قصد الإمام بهذا أنهم أثبتوا الطلب الذي هو النظر، والنظر هو الفكر، وأنكروا فكر النفس، [فكأنه] نسبهم إلى [إثبات] شيء وجحد ما يضاهيه في الوضوح والقيام به. بل يظهر من كلامه أن الفكر الذي هو كلام النفس أجلى من النظر، إذ يتأتى للإنسان أن يبقى مدة هو غافل لا يتظر في دليل، ولا يتصور أن يكون عريا','1','591',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (365,'من كلام النفس. ولهذا قال: وجدان المرء [جولان] الفكر الذي هو كلام النفس من قبيل الضروريات.
وما ذكره ثانيا من (أن كل أمر جرى عليه كلام النفس، فالإنسان يخبر عنه بأنه يخبر عن كونه عالما بكيفية نظم الصيغة). هذا معنى كلامه.
وقوله: (إن العلم الحق لا يدركه الإنسان من نفسه إدراكه آلامه ولذاته). كلام مشكل، إن أراد أن العلم لا يعلم، فذلك محال، وقد بينا استحالته. وإن أراد أن إدراك الآلام [واللذات] يستمر، والعلم النظري لا يستمر، فهذا صحيح. ولكن العلم الضروري يضاهي الآلام واللذات، باعتبار','1','592',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (366,'الاستمرار وامتناع الغفلة. وإن أراد أن مدرك الآلام يتألم، والعالم ليس كذلك، ولكن لا يحصل منه مقصوده. وغرضه بهذا أن الإرادة كالعلم، وكلام (١٢/ب) النفس كالآلام، ليثبت التعدد والتغاير. وجنح في هذا الاستدلال','1','593',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (367,'بإدراك الأوائل في كونهم فرقوا بين الإرادة التي هي توقان وشهوة، [وبين] العلم، [و] بين الآلام واللذات، حتى جعلوا العلم والإرادة على [النفس والعقل] المباينين لعوالم الأفلاك، وليس الآلام واللذات كذلك. وهذا لعمري كلام ضعيف. وكيف (٤٥/أ) يتمسك بقول قوم نحن نعلم أنهم على ضلال؟ ولكنه يزعم أنهم على بصيرة في إدراك الفرق. والخصم لا يساعد على ذلك. فلا وجه لهذا الكلام على حال.

قال الإمام: <span data-type="title" id=toc-91>(مسألة: الأمر: هو [القول] المقتضي بنفسه طاعة المأمور) </span>إلى قوله (جزم في طلب الطاعة). قال الشيخ: قوله: الأمر هو القول، فعدل','1','594',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (368,'عن لفظ الكلام. وهذا عدول إلى القول الذي هو أعم. وقد كنا بينا أن الحاد يجتنب اللفظ البعيد إذا وجد القريب، بما فيه مقنع. ولكن قد قدمنا أن لفظ الكلام، هل يرادف لفظ القول، أمر لا ينطلق إلا على المفيد؟ فإذا قلنا لا ينطلق إلا على المفيد، وهو الظاهر، فهل نقول: الأمر جملة، حتى يكون كلاما؟ أو مفردا، حتى يطلق عليه قول دون كلام؟ والصحيح أنه مفرد. فإن الأمر هو الكلمة الواحدة، ولكنها متعلق بمأمور، فيكون من الأمر. والضمير كلام، وإنما أراد هاهنا حد الأمر على انفراده، وهو بمثابة ما لو سئلنا عن حد الفعل، لم يصح أن يقال: هو كلام يشعر بالزمان مقترنا بالحدث. بل يقول: هو لفظ، فيعدل عن الكلام إلى اللفظ. فهذا- والعلم عند الله- هو الذي قصد.','1','595',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (369,'قوله: (المقتضي). لفظ الاقتضاء فيه نظر وإلباس. فإنه قد يقال: اقتضى فلان حقه من فلان، ولا يكون مقتضيا على هذه الصفة. فمعنى قوله: المقتضي أنه [لا يعقل] إلا منسوبا إلى متعلقه.
وقوله: بنفسه، قد بين الغرض من ذلك.
وقوله: طاعة المأمور بفعل المأمور به. فقد بين أيضا أنه أراد بذكر الطاعة أن ينفصل الأمر عن الدعاء والرغبة. وهل يسمى الدعاء أمرا؟ أما النحويون فيأبونه، ويقولون: إنه يشاركه في الإعراب والبناء، وكذلك أكثر الأصوليين.
ومنهم من يقول: يصح أن يأمر الأدنى الأعلى. وهذا غير محقق في الجزم والإيجاب. ولكن يعترض على الحد عندي بأنه تعرض لبيان الأمر بأمور لا تعرف إلا بعد بيان الأمر، فإن الفعل إنما يكون طاعة على تقدير تعلق','1','596',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (370,'الأمر به، وكذلك [المأمور] إنما يكون مأمورا على تقدير تعلق الأمر بفعله وتوجهه عليه. فمن جهل الأمر، فهو جاهل بكل أمر يتلقى منه.
بل الصحيح في حده أن يقال: هو القول المقتضي تحصيل ما نسب إليه من المخاطب به [على وجه يكون به] الفاعل ممتثلا.
قال الإمام: (وأما المعتزلة فقد أوضحنا من مذهبهم أن الكلام ليس جنسا) إلى قوله (وأما في وقوع اللفظ أمرا، فصفة تلزم [اللفظ]، ولا حاجة في (١٣/أ) تحصيلها إلى الإرادة). قال الشيخ [أيده الله]: قوله: قول المعتزلة: الأمر: قول القائل لمن دونه: افعل، إنه منقوض بما إذا قال لمن في درجته: افعل، فإنه يكون أمرا. وهم لا يسلمون ذلك، بل يقولون: ذلك رغبة وسؤال. أما تحقيق الأمر، فليس كذلك.','1','597',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (371,'وقوله: وليست هذه اللفظة بعينها كل الأمر. هو كذلك، وإنما هذه أمثلة وضعها النحويون كالموازين توزن بها الصيغ، نحو قولهم: (يفعلان) وما ضاهى ذلك.
[وقوله]: (ولو [اهتدوا] لبناء الأمر على حقيقة أصلهم، لما التزموا تحديد الأمر، وهو قسم (٤٥/ب) لا حقيقة لأصله). إن أراد بذلك الحد النفسي، فهو صحيح. وإن أراد امتناع الحدود اللفظية، فليس كذلك. فإن الحدود اللفظية تتطرق إلى الألفاظ اللغوية. وإنما لم تتأت الحدود النفسية في الأمر على مذهب المعتزلة، لأنه أمر يرجع إلى الأوضاع، وليس [أمرا','1','598',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (372,'لنفسه]. ولذلك اشترط فيه الإرادات.
نعم، إنما يستقيم هذا على أصل البلخي الذي ذهب إلى [أن] قول القائل: افعل، أمر لنفسه من غير محال. فقيل له: فهذه الصيغة قد ترد للتهديد أو الإباحة. فقال: هذا جنس وذلك جنس. وهذه مناكرة حس، ومدافعة ضرورة. ولذلك أضرب بقية المعتزلة عن هذه المقالة، لركاكتها وفساد عقل موردها.
وقال بعض المعتزلة: تكفي إرادة واحدة، وهي إرادة الامتثال. وذهب إلى هذا المذهب أبو هاشم، [فقيل] له: فيلزم أن يكون الله تعالى آمرا لأهل','1','599',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (373,'الجنة بقوله: {كلوا واشربوا} و {ادخلوها بسلام آمنين}. فالتزم ذلك، وقد خرق إجماع المسلمين. فإن الآخرة ليست دار تكليف. وقد قال: إن الله تعالى يريد دخولهم الجنة ويكره امتناعهم، إذ لو لم يدخلوها، لما وجدوا ثواب أعمالهم، وذلك ظلم، والله تعالى يكره الظلم.
والأكثر شرطوا [ثلاث] إرادات، كما ذكره. وطائفة شرطوا إرادتين.
قال الإمام: (ولا يتبين مذهبهم إلا بذكر قواعدهم في الصفات) إلى قوله','1','600',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (374,'(ومن ظن أنه يتمكن من فصل بين مذهبهم وبين معتقد أصحاب الهيولي، فقد ظن محالاً). قال الشيخ: الأمر على ما قرر الإمام في هذا. وذلك أنه','1','601',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (375,'إذا حكم القوم بأن صفات النفوس ثابتة أزلية، فمن أنكر الحال، فلا يخفى أن المصير [إلى] قدم الذوات تصريح بنفي على الإطلاق. وأما إذا','1','602',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (376,'سلمت الحال، فهي عند أبي هاشم غير معلومة ولا مجهولة، فكيف يصح أن تتعلق بها القدرة؟ والوجود على هذا الرأي حال.
وتمام التحقيق فيه أن الذوات إذا دخلت الوجود، وقيل للمعتزلة: هذا الموجود بصفاته النفسية، هل تعلقت [بذاته] قدر القادر، أو أثر فيه كونه قادرا؟ فيلزم أن [يقولوا]: لا، بل قد [اعترفوا] به، فنفس (١٣/ب) العرض والجوهر قديمان. نعم، إنما تجدد عند القوم التسمية والإطلاق، دون الحقائق والمعقولات.
قال الإمام: (فأما الصفات التابعة للحدوث، فقولهم مختبط فيها) إلى قوله (فقد كفى هذا القدر، وأغنى عن التطويلات). قال الشيخ: الصفات','1','603',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (377,'التابعة للحدوث عند المعتزلة يبني أمرها على الأصل من اعتقاد صفات النفوس في الأزل. وإذا تبين بطلان ذلك، فإن العدم ليس على صفة من صفات الإثبات، والله تعالى مخترع الموصوف بجميع صفاته، إن سلم الحال، فإن منع، فليس إلا مفرد هو متعلق القدرة، فلا تبقى صفة قديمة وأخرى متجددة ليست من أثر القدرة. ولو صير إلى تعدد الصفات، فالمصير إلى وجوب بعضها وجواز الآخر، تحكم محض بعارضه عكسه.
قال الإمام: (فأما المصير إلى أن الحدوث من أثر القدرة، فباطل) (٤٦/أ) إلى قوله (نعم، الآمر يجد في نفسه إرادة وتجريد قصد). قال','1','604',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (378,'الشيخ: [أيده الله]: مذهب المعتزلة أن الأمر عندهم هو اللفظ إذا اقترنت به الإرادة المتقدمة. وإذا كان كذلك، لم يتصور عندهم إلا أن يكون أمرا، ولا يرجع إلى الأدلة الوضعية التي يتصور تغيرها وتبديلها. ويتنزل عندهم منزلة أدلة','1','605',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (379,'العقول، ولذلك التزموا تجديد الأمر. فنقول للقوم: إذا بطل الاعتماد على مجرد الصيغة عند الأكثر، ولم يكن بد من إرادة جعل اللفظ أمرا، فما الأمر؟ وما حقيقته حتى يقصد أن يجعل اللفظ أمرا؟ وهل للأمر معنى سوى الصيغة حتى يقصد إلى ذلك المعنى؟ فلا يبقى على التحقيق إلا أن يقصد التعبير باللفظ عما في النفس، وهو عين ما صرنا إليه.
ثم كيف يتصور القطع بتساوي الحروف والأصوات مع ادعاء الافتراق في','1','606',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (380,'صفات؟ وهذا تناقض بين، ومكابرة حسن. نعم، الأمر يجد في نفسه إرادة وتجريد قصد، للتعبير باللفظ عما في نفسه.
قال الإمام: (ثم التطم البصريون والبغداديون) إلى قوله (فلا فرق في أصل الصفة). قال الشيخ: معنى ذلك أن البصريين قالوا للكعبي: أنت إذا جعلت كون اللفظ أمرا من قبيل الصفات التابعة للحدوث، المستغنية عن الإرادة، فالصفة التابعة للحدوث [تتبع حقيقة] وجود الذات، ولا يتصور أن يمتاز بها بعض الذوات المتماثلات. ألا ترى أن التحيز لما كان عند القوم من الصفات التابعة للحدوث، لم يتصور أن يمتاز بذلك بعض الجواهر عن بعض؟ فلو كان كونه أمرا يتبع حقيقة اللفظ، وهو [قول القائل]: (١٤/أ) (افعل)، للزم أن يتبع كل قول على هذا الوجه، لاشتراكها في حقيقة الحروف والأصوات. والفرق بينهما مكابرة وجحد لإدراك حاسة السمع.','1','607',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (381,'وأجاب الكعبي بأن قال: قد استوينا في التحكم وادعاء ما لا دليل عليه، وألتزم الفرق بين ما قطع بتماثل، باعتبار إدراك السمع، إلا أني جعلت الصفة المتحكم بها من قبيل الصفات التابعة للحدوث. وإنما حمله على ذلك، السؤال الذي قدمناه، وهو المطالبة بمعقول الأمر حتى يقصد بجعل اللفظ أمرا. قال: وأنتم أثبتم الصفة من أثر الإرادة، مع القطع باستواء الصيغتين، فلا فرق على الحقيقة بين المذهبين في التحكم على نقيض الحس.
قال الإمام: (فإن قيل: ما أنكرتموه منهم يلزمكم مثله) إلى قوله (فهو [ملتحق] [بقرائن] الأحوال). قال الشيخ: تقرير هذا السؤال: أن','1','608',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (382,'المتماثلين لا سبيل إلى امتياز أحدهما بحكم عن مماثله.
قالوا: وقد حكمتم باستواء الحروف والأصوات من الحاكي والآمر، وإذا تحقق الاستواء من كل وجه، امتنع الاستدلال على المختلفين بأمرين متماثلين، إذا حاصله راجع إلى إثبات التماثل والاختلاف معا. وهذا إنما يلزم أن لو صرنا إلى نفس الاكتفاء بمجرد اللفظ. فأما إذا اشترطنا انضمام القرائن إلى اللفظ، فلا يتحقق التماثل، إذا القرائن التي تقترن بلفظ الحاكي، ليست كالقرائن المقترنة بلفظ (٤٦/ب) الطالب، فتحقق الاختلاف لهذا، فأمكن لذلك الاستدلال على المختلفات.','1','609',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (383,'قال الإمام: <span data-type="title" id=toc-92>(مسألة: في صيغة الأمر) </span>إلى قوله (فهذا هو التنبيه على سر مذهب أبي الحسن وطبقة الواقفية). قال الشيخ: قوله: الصيغة هي العبارة، هذا هو عرف الأصوليين. وأما أهل النحو، فعبارتهم غير هذا، وهو أن الصيغة ترجع إلى كيفية نظم اللفظ. ولذلك قالوا: إن الفعل يدل على الزمان بصيغته، وعلى المكان بضرورته، وعلى المصدر بلفظه. وكأنهم ردوا الصيغة','1','610',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (384,'إذا أضيفت إلى الأمر- على مذهب المعتزلة- لم تكن حقيقة، إذا الصيغة هي الأمر، وهي في مذهب قول القائل: نفس الشيء وذاته.
أما كون الصيغة هي الأمر مطلقا، فقد بينا أن هذا مذهب البلخي خاصة. وبعضهم يضيف إليه الشروط، على حسب ما تقدم.
وقوله: (إن الإضافة على هذا الوجه غير [حقيقة]). ليس الأمر كذلك عند أئمة العربية. فإن الإضافة غير الحقيقية هي التي يعني بها الانفصال، كإضافة اسم الفاعل إذا كان بمعنى الاستقبال. ولذلك لا يتعرف وإن أضيف','1','611',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (385,'إلى معرفة، كقوله: {هذا عارض ممطرنا}. فقد جرى (ممطرنا) نعتا لعارض، فلو لم يكن نكرة، لم ينعت به (١٤/ب) النكرة. وكذلك قول جرير:
يا رب غابطنا لو كان يطلبكم .... لاقى [مباعدة منكم] وحرمانا
فقال: (رب غابطنا)، و (رب) لا تدخل إلا على النكرات. وأما قولنا: (نفس زيد)، فقد أفادنا تعريفا باتفاق أهل اللسان، ولا أحد يقضي على النفس عند الإضافة إلى زيد بكونها نكرة. والإضافة الحقيقية هي التي تفيد تعريفا، كنفس زيد، أو تخصصا، كخاتم حديد.','1','612',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (386,'وقوله: (إن الناقلين الذين نقلوا أن أبا الحسن يستمر على القول بالوقف على فرض قرائن الأحوال). فإذا اقترنت باللفظ المجمل أو الملتبس يظهر أثرها، باعتبار غرض المتكلم. فأما اللفظ، فباق على إجماله أو التباسه، باعتبار وضع اللغة. وأبو الحسن وأصحابه إنما تكلموا باعتبار وضع اللغة. فإذا استقر فيه ظهور أو [نصوصية]، تمسك به على حسب ما يقتضيه الكلام، من طلب قطع، أو اكتفاء بغلبة ظن، أو يثبت إجمال، فلا يتمسك بمطلقه على حال.
فمصير الإمام إلى أن قرائن الحال توضح اللفظ، حيد عن المسألة،','1','613',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (387,'وذهاب عن المقصود، وغير مفيد باعتبار الأصول. وليس ينكر أحد أن لفظ الماء يصلح للعذب والأجاج. [والجالسين] على المائدة إذا استدعي الماء [عند الغص] بلقمة، فهم منه طلب العذب، ولا يوجب خروج اللفظ عن اشتراكه في الأصل.
وقوله أيضا: (إن أبا الحسن لا ينكر صيغة مشعرة بالوجوب القائم بالنفس، نحو قول القائل: أوجبت). هذا أيضا حيد عن المقصود، وليس الكلام إلا في الصيغة التي يقال فيها إنها صيغة الجزاء، ولا أحد منهم يذهب','1','614',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (388,'إلى أن قول القائل: أمرتك، أن هذا فعل أمر. ثم رجع إلى أنه لا يتوقف في هذه الصورة أصلا. وهذا [الذي] قاله لا ينكره أحد. فلا وجه لكونه رأى ذلك رأيا عن أبي الحسن، منفردا به. فإنه لا يتصور أن يتوقف في غرض المتكلم بلفظه عند قرائن (٤٧/أ) أحواله، أوت صريح عبارته. والوقف لازم باعتبار أصل الوضع. وهذا الذي يبنى عليه قواعد الأصول.
وقوله: (قد يتردد المتردد في الألفاظ التي ذكرناها إذا اقترنت بقول القائل) إلى آخره. هذا عندي لا يصح أن يتردد فيه، وكيف يتأتى ذلك، وقول القائل حتما تفسير لقوله (افعل)، لإزالة ما فيه من الإيهام والالتباس؟ فكيف يصير العمدة على المفسر دون المفسر؟ فلا وجه لحمل تردد المتردد على هذا أبدا.','1','615',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (389,'وقوله: (وهذا عندي- إن صح- محمول على قرائن المقال على ما فيها من الخبط). كلام ضعيف، وكيف يدعي الخبط في قرائن المقال على الإطلاق، وقد قال هو: إنها واضحة جلية، كقول القائل: افعل حتما أو واجبا؟ (١٥/أ)
قال الإمام: فأما المعتزلة، فلم يقف على حقيقة مذهبهم إلا خواص الأصوليين) إلى قوله (وإنما معناه الإرادة، [والوجوب] متلقى من الوعيد','1','616',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (390,'المقترن به). قال الشيخ: إنما بعد القول الأول عن مذهب المعتزلة، من جهة أن الأمر الحقيقي: هو قول القائل لمن دونه: افعل، وهو يرجع إلى [الإلزام] والتسخير. وإذا كان كذلك، فالإباحة لا إلزام فيها، ولا تسخير، ولا [اقتضاء]. فكيف يصح أن يكون قول القائل:
(افعل) للإباحة بالإضافة؟ ثم يلزم منه إذا استعمل في الأمر خروج عن حقيقته ولا يبقى أمر حقيقي بحال.
وليس عند القوم كلام نفس، حتى يكتفي بهن ويعبر عنه باللفظ المجازي، فيفوت الأمر باعتبار الكلام النفسي، ولا وجود له باعتبار الوضع الحقيقي. فهذا بعيد جدا عن مذاهبهم، إلا أن ينبني الأمر على ما قاله الكعبي، من كون المباح حسنا، ويجوز أن يطلبه الطالب، باعتبار كونه حسنا، ولهذا قال: المباح مأمور به، فهذا ربما يمشي على هذا المذهب، وهو رديء جدا عند المعتزلة. وسيأتي الكلام عليه بعد هذا، إن شاء الله تعالى.','1','617',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (391,'وأما من ذهب إلى أنه للندب، فهو أقرب من جهة ما في الندب من الاقتضاء، ولكن يلزم منه أن يكون طلب جازم معقول، على حسب ما مر في كونه للإباحة، إلا أن ينبني الأمر على أن الندب طلب جازم. وهذا الذي يختاره القاضي. وسيأتي أيضا الكلام عليه.
ولكن مع هذا إذا جعل الندب، باعتبار خصوصيته، خرج الوجوب عن قسم الطلب، وليس الأمر كذلك. لا جرم أن عبد الجبار من هذه الإلزامات فر، وقال: الصيغة تدل على إرادة مطلقها الامتثال، فهذا مقتضاها فحسب. وهذا الكلام أيضا غير صحيح، فإن إرادة الامتثال ليس مدلول الصيغة، وإنما إرادة الامتثال شرط في كون اللفظ أمرا، فكيف يصح أن تكون مدلول اللفظ؟ ولو كان هذا مدلول الصيغة، لم يكن معنى إلا الإرادة من غير طلبن فيخرج الطلب عن حقيقته. ولا يلزم أن يقال: كل مراد مأمور به. وكل هذا إنما هو','1','618',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (392,'تحير، وتغير لنفي كلام النفس. ويطلبون معقول الأمر، ويعرفون أن الألفاظ لا تدل لأعيانها، فتحيروا هذا التحير.
وقوله: (بيد أن المراد لا يكون إلا طاعة). ليس كما قال، بل إنما يكون طاعة على تقدير تعلق الأمر به، حتى يكون طاعة مرادا؟ وقصد عبد الجبار بما قاله أن يشمل حقيقة الأمر الوجوب والندب جميعا (٤٧/ب)، لا من جهة خصوصيتهما، بل من جهة اشتراكهما في كونهما مطلوبين مرادين، ثم يقع الافتراق من وجه توجه الذم وانتفائه. فهذا هو مقصوده، والاعتراض عليه ما (١٥/ب) سبق.','1','619',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (393,'قال الإمام: (وأما الفقهاء، فالمشهور من مذهب الجمهور أن الصيغة التي فيها الكلام للإيجاب، إذا تجردت عن القرائن. وهذا مذهب الشافعي. والمتكلمون من أصحابنا مجمعون على اتباع أبي الحسن في الوقف، ولم يساعد الشافعي غير الأستاذ أبي إسحاق). قال الشيخ: هذا الذي ذكره الإمام حكاية عن الشافعي، قد نقل أبو حامد عن الشافعي خلافه. فقال: صيغة الأمر مترددة بين الندب والوجوب، وصيغة النهي للتحريم. ووجه على نفسه اعتراضا، فقال: إنما أوجبنا تزويج الأمة، بقوله: {[فلا] تعضلوهن أن ينكحن أزواجهن}. ولم نوجب تزويج العبد، لأنه لم يرد فيه إلا قوله: {وأنكحوا الأيامى منكم والصالحين من عبادكم وإمائكم}. فيمكن أن يكون له في المسألة قولان.','1','620',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (394,'قال الإمام: (والذي يقتضيه الترتيب المفضي إلى درك الحق) إلى قوله (وقد يستحق بدون ذلك التأديب). قال الشيخ: الصحيح من مذهب القاضي التوقف في اللفظ، وامتناع القضاء عليه بجهة، وطريقته لا تقتضي إلا ذلك. وحاصله أنه مطالبة بالدليل، إن من لم يحكم لا يستدل، وهو لم يحكم على اللفظ بجهة حتى يدل عليها. وإنما غايته أنه يقول: الطرق التي يعرف بها الوضع مفقودة، إن أراد أنه لم يقف على شيء منها، فذلك مسلم له، لأنه أعلم','1','621',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (395,'بحاله، وإن أراد أنه لا سبيل لأحد إلى عملها، فهذا تحكم، فمن أين القضاء على الناس كلهم بذلك؟
وقوله: إن (النقل إما أن يعرف صريحا، أو استنباط، تواترا أو آحادا). هذا تقسيم من جهة النقل بلا ريب، ونحن نعلم من اللغة ضرورة أن لفظ البحر وضع للماء، وإن كان يطلق على الكريم، فلا يمنعنا وجدان اللفظ دالا على معنيين، إلا بعلم الحقيقة من المجاز.','1','622',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (396,'والذي يظهر من كلام القاضي أنه أراد نفي الطرق، بالإضافة إلى [أهل] الزمان بجملتهم، ولذلك تعرض لضبطها ونفيها.
وقوله: (فإن النقل المتواتر يحصل العلم الضروري، ويتضمن استواء طبقات جميع العقلاء فيه). [هو] كما قال، إذا وقع الاشتراك في','1','623',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (397,'السبب، وأما إذا لم يطلع بعض العقلاء على النقل، أو على كماله، لم يلزم أن يحصل العلم، ولا يتبين بذلك غلط المحيط عند حصول العلم له.
وأما رد الإمام على القاضي عندما [مال] إلى دعوى التشارك، فصحيح لا شك فيه، لأن تلك الطرق تفضي إلى اللبس، لا إلى تعيين جهة. فمن عين الاشتراك، كمن عين غيره. والإمام إنما أورد هذا الكلام، لاعتقاده أن يصلح لإبطال (١٦/أ) الاشتراك، فإنه يلزمه أن لا يعين جهة من الجهات. وإن','1','624',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (398,'أورده [على المعارضة المحضة، ومقابلة الفاسد بالفاسد، صلح للجدل دون التحقيق.
وأما رد عليه عندما ذهب إلى] التوقف بقوله: (لم يترك والركون إلى هذه العماية العمياء، والجهالة والجهلاء) إلى آخره. فكلام ضعيف.
وقول: (إن اللفظ التداور مع الجواز). أمر لا يفيد، وإن قدرنا أن اللفظ معروف الدلالة عند أهل اللغة وغيرهم، فما الذي يعين (٤٨/أ) ذلك في','1','625',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (399,'حق من جهل مدلول، والتبست عليه طرقه، والقاضي ما أحال علم هذا على الخلق، وإنما منع من معرفته في عصر، لما قرره من الطرق المنحسمة التي يرجع النقل إليها.
وقوله: (وتنسل بفرض هذا دعوى القطع من يده). نعم، إن قطع القاضي بأنه لا يعلم ذلك أحد، فهذا غير سديد. وكيف يمكن دعوى ذلك،','1','626',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (400,'وأهل اللغة يعرون مرادهم بألفاظهم من اشتراك أو غيره؟ وما ذكره القاضي من الاشتراك على الإيهام بالاستفهام، فغير مفيد، فإنه وإن اتفق أن يكون السامع بفهم المدلول على وجه مظنون، فقد يقصد قطع الاحتمال وإزالة الإشكال. ويمكن أيضا أن يكون المستفهم ممن يرى له إجمالا، فلا يكون- في رأيي- احتجاجا، إلا أن يثبت أنه من أهل اللسان، [وينفي] تعارض قرائن الأحوال، ويكون ممن يكتفي بالظن في فهم المقال، ولم يقصد الارتقاء إلى حد الاستيقان، فحينئذ يكون في ذلك متعلق. [واجتماع] هذه الشروط','1','627',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (401,'[عسير]، وقد يستحق التأديب على وجه الاستفصال من غير حاجة إليه.
قال الإمام: (وأما المعتزلة فقد بنوا حقيقة أصلهم في ذلك) إلى قوله (وكل ما كان كذلك لا يكون واجبا. فهذا منتهى المسألة). قال الشيخ: ما ذكره الإمام في هذا الموضع من التقسيم، قد نقضه على نفسه في صيغة','1','628',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (402,'(افعل) بعد الحظر، فإنه ذهب فيها إلى الوقف. ويمكن سلوك هذا الطريق نفسه. فيقال: من أنكر أن العرب ما فصلت بين قول القائل: إذا حللت فاصطد، وبين قوله: إذا حللت، فلا حرج عليك، اصطدت أو تركت؟ فليس من التحقيق على شيء. فإنا على اضطرار نعلم الفصل في ذلك، وهلم جرا إلى بقية الأقسام. والإمام قد توقف فيها، والتقسيم بعينه جار فيها.
ثم قوله: (من أنكر أن العرب ما فصلت). عبارة فيها وهم، وذلك أن من أنكر عدم الفصل، فقد اعترف بالفصل، وهو الذي يريده الإمام. وإنما المراد: من قال أو من زعم أن العرب ما فصلت؟ هذا مراده. وقصاراه فيما قال ادعاء الضرورة.','1','629',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (403,'وأما الفصل الذي ذكره، فمعترف به، ولن لا يحصل منه مقصود، وذلك أنه قابل بين المجمل وبين اللفظ النص، فظهر التفاوت بلا إشكال. ولا يوجب إدراك (١٦/ب) الفصل أن يكون اللفظ المجمل لا يحتمل تلك الجهة، وهو بمثابة ما لو قال القائل: لفظ العين لا يتناول الذهب. وقال: من زعم أن العرب ما فصلت بين قول القائل: العين، وبين قوله: الذهب، فليس من التحقيق على شيء، لكان مصيبا، ولم يلزم منه أن يكون لفظ العين لا يتناول الذهب مع غيره. كذلك الإمام قابل بين لفظ (افعل) المحتمل لجهات، وبين نص الإباحة ونص الندب، فظهر التفاوت، ولا يمتنع ذلك التناول.','1','630',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (404,'وقوله: (إن من ضرورة الندب تخيير في الترك). غير صحيح، وقد مر الكلام عليه في حد التكليف. والندب عندنا أمر محقق، والمندوب مأمور به. وسيأتي تحقيقه بعد هذا، إن شاء الله تعالى.
وقوله في الجواب عن سؤال: (إن هذا مذهب الشافعي: بأن الوجوب لا يعقل دون التقييد بالوعيد على الترك). فقد مر الكلام عليه. وهو نقض هذا بعينه (٤٨/ب)، وأفسد حد من حد الواجب: بأنه المتوعد بالعقاب على تركه. على ما سيأتي.
فإذا انتهى الكلام إلى هذا الحد، فلننبه على أصل كلي، لا بد من التنبه','1','631',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (405,'له، وذلك أنه إنما احتاج الأصوليون إلى معرفة الأوضاع اللغوية، لتفهم الأحكام الشرعية، وإلا فلا حاجة بالأصولي إلى معرفة ما لا يتعلق بالأحكام من الألفاظ. وإذا كان كذلك، افتقر إلى تقديم أمر آخر، وهو أن الشرع، هل تصرف في اللغة أم لا؟ فإن ثبت عدم التصرف، اكتفى الأصولي بمعرفة وضع الشرع في الاسم، ولا يحتاج معه إلى معرفة اللغة في ذلك اللفظ. وإن عرف وضع اللغة، والتبس عليه، هل الشرع تصرف في الاسم أو لا؟ لم يجز له الحكم بوضع اللغة، حتى يستقر عنده وضع الشرع فيه، ولهذا إن الفقهاء أول ما يتكلمون على الألفاظ باعتبار وضع اللغة، لأنهم يرون تصرف الشرع في الأسماء، فترام يجنحون إلى الإجماع وغيره، وهم في ذلك على بصيرة، إذ عرف الشرع مكتفى به، مصار إليه. وعرف اللغة على هذا التقدير عند احتمال التغيير لا يفيد.
والذي نراه في هذه المسألة أن (افعل) عند الإطلاق يقتضي طلبا لا محالة، وذلك ثابت عند أئمة العربية أجمعين، فإنهم فرقوا بين باب الأمر وباب النهي، فقالوا: باب الأمر (افعل) وباب النهي (لا تفعل). لا يبدي أحد','1','632',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (406,'في ذلك خلافا. وإنما اختلفوا في أن مبني عند عدم الزوائد أو معرب؟ فأما جحد كونه أمرا، فلا ذاهب إليه، [وإن] كان قد يستعمل اللفظ في الإباحة طويلا. نعم، يؤخذ من هذا أنه ليس نصبا في الطلب، وإنما هو ظاهر.
وكذلك الفقهاء مجمعون على أن هذا مقتضاه في الشريعة، لكنهم يترددون في الوجوب أو الندب.
ولعمري إنها مسألة غامضة. (١٧/أ) والظاهر عندي أنه متردد بينهما، وإنما تبين القرائن، فإنها صيغة طلب، والطلب محقق في الندب، كما هو في الوجوب، فلا سبيل إلى تعيين أحدهما بالتحكم. وكذلك القول في قول القائل (لا تفعل). وبالله أستعين.

قال الإمام: <span data-type="title" id=toc-93>(فصل- الصيغة التي تكلمنا على أصلها تفرض مطلقة ومقيدة) </span>إلى قوله (والسبب في ذلك أن ما يقع ضمنا، فإنه يتبع المتضمن في','1','633',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (407,'مقتضاه لا محالة). قال الشيخ [أيده الله]: أما إذا اعترف القوم بالتعدد، وسلكوا مسلك القياس، فهو ضعيف، وقد تقدم الكلام على القياس في','1','634',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (408,'اللغة. وإن سلك الناظر مسلكا آخر، وقال: إن لفظ الأمر بالشيء، يرادف لفظ النهي عن ضده، والألفاظ المترادفة لا يمكن اختلافها. وتقرير ذلك: أن القائل: (تحرك) مطلوبه عين مطلوب من قال: (لا تسكن)، فإن نفي السكون لا يصح أن يكون مطلوبا. على ما سيأتي الأمر بالشيء، هل هو نهي عن ضده؟','1','635',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (409,'ولو قال له: (لا تسكن) تضمن ذلك أمرا بالحركة مطلقا، فكذلك إذا قال له: (تحرك)، إذ قد تحقق بهذا التقدير ترادف اللفظين واتحاد المعنى. وهذا كلام مخيل، تحقيقه بعد هذا يأتي إن شاء الله.
ولكن القدر الذي ننبه عليه ههنا أقوله: (لا تسكن)، وإن رجع إلى طلب الحركة (٤٩/أ) -[ولا يمكن] الافتراق بحرف النهي الذي يضارع النفي- يكون قرينة [منضمة] إلى الطلب، يفهم منها عموم استرسال طلب الحركة. وإنما تكلمنا ههنا في الطلب المطلق دون المقيد.','1','636',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (410,'وإن لم يسلك الاتحاد، وسلك مسلك التضمن، لصح الجواب، وبطل الإلزام، [وظهر] ما قاله الإمام، فإن المتضمن يتبع المتضمن. والأمر كما قدره، مع أنه لو وقع التقييد في الأمر بالمرة الواحدة، لتضمن ذلك- على هذا الرأي المسلم جدلا- تباين الأضداد من غير استيعاب.
قال الإمام: (ومما تمسك به أصحاب التكرار) إلى قوله (وأما العزم فسأذكر فيه فصلا مقنعا في المسألة التي [تلي] هذه، إن شاء الله تعالى).
قال الشيخ [وفقه الله]: ما قاله الإمام في هذا الموضع، كلام صحيح، لا يفتقر إلى أكثر من هذا. ولكن خيال القوم من حيث الجملة، أن الأمور','1','637',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (411,'[الثلاثة] [وهي]: الامتثال والعزم واعتقاد الوجوب، استندت إلى اللفظ استنادا واحدا، فلا يصح تفاوتها بحال. وقد سلم عدم اختصاص الوجوب والعزم بالمرة الواحدة، فليكن كذلك الامتثال. [ومعنى] الإمام أن اعتقاد دوام [الوجوب] لا يرجع إلى أنا نعتقد أن الفعل دائم وجوبه، فهذا محال، وإنما أريد أن ما أوجبه الله تعالى، فهو واجب على الحقيقة، فهو كذلك، ولا يتلقى من هذا
(١٧/ب) وجوب الامتثال. وكذلك معتقدنا في الأمر المقيد بالمرة الواحدة.','1','638',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (412,'قال الإمام: (فأما الصائرون إلى أن الصيغة المطلقة تقتضى امتثال المأمور به مرة واحدة) إلى قوله (سيما مع العلم بتفاوت صيغ الأفعال، واختلاف مقتضياتها). قال الشيخ: أما إذا سلك أصحاب هذا المذهب مسلك القياس، فالأمر على ما قاله الإمام. وأما إذا سلكوا مسلكا آخر، وهو أن الفعل [إذا]','1','639',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (413,'تلقي من المصدر كان مطلوبا أو مخيرا. فمطلق المصدر لا يتعرض للأعداد بحال. فهذا الكلام له أوضح. وسنذكره في آخر المسألة
قال الإمام: (المسلك الثاني للقوم- أنهم قالوا: من امتثل الأمر مرة واحدة) إلى قوله (والنفي لا اختصاص له، فكان الجنس كالشخص في حقه). قال الشيخ: ما ذكره الإمام من أن الأمر استدعاء المصدر، والمصدر لا يتضمن استغراقا، ولا يشعر بالمرة الواحدة اقتصارا عليها. هو كما قال. وأما الوقف في الزائد على المرة الواحدة، من غير نفي ولا إثبات، فغير مستقيم،','1','640',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (414,'فإن المصدر لا يتعرض للأعداد بحال، لا من جهة الوضع، ولا من جهة التهيؤ والصلاح للدلالة. وإنما هو مطلق في الحدوث.
وهو بمثابة الدلالة على (رقبة) عند الطلب. ولو قال له: اعتق رقبة، وقع الاكتفاء بكل ما يسمى (رقبة). فكذلك يجب أن يقع الاكتفاء بكل ما يسمى','1','641',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (415,'صلاة عند الأمر بمطلق صلاة، ولا يتوقف في الزيادة عليها على حال. فالوقف غير صحيح مع المصير إلى أن المصدر لا تعرض له للعدد بحال.
فإن قيل: فهذا مذهب من قال إنه يتضمن الامتثال مرة واحدة. قلنا: هو في [الحكم] كذلك، ولكن إن قال صاحب هذه لمقالة: إن اللفظ له إشعار بالمرة، من جهة كونها مرة، فليس كذلك. وإنما حكمنا بثبوتها ضرورة. فإنه','1','642',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (416,'إذا أمر بإعتاق عبد، فأعتق [أسود]، حكمنا بإجزاء عتق الأسود (٤٩/ب). لا من جهة [كونه أسود، بل من جهة] كونه عبدا. كذلك إذا أمر بصلاة، فصلى صلاة واحدة، حكمنا بكونه مطيعا، من جهة كونه مصليا، لا من جهة كونه صلى صلاة واحدة. وكذلك الأمثلة التي ذكرت في الحالف [والمخبر]، [يبر] الحالف، ويصدق المخبر. من جهة حصول أصل المصدر. لا من جهة إفراده وجمعه. وهذا واضح للتأمل. وبالله التوفيق.
قال الإمام: (مسألة الصيغة التي فيها الكلام، إن قيل: إنها تقتضي','1','643',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (417,'استغراق الأوقات بالامتثال) إلى قوله (ونحرر بعد المباحثة ما اخترناه. فلتقع البداية بأصحاب الفور). قال الشيخ: المذاهب كما نقلها بينة، والمؤاخذة على من قال إنها على التراخي متوجهة. ولكنه لم يوجه الاعتراض على من قال إنها على الفور. فإن مقتضى هذا (١٨/أ) النقل إذا وقع التأخير لا','1','644',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (418,'يجدي. وهو ينكر أن يكون هذا مذهبهم، فلا تختص المؤاخذة بمن قال إنها على التراخي. نعم، قد [ذهب بعض الأصوليين] إلى أن من أخر، لا يعتد منه بما فعل مؤخرا. والترجمة على هذا المذهب لا مؤاخذة عليها.
وما قال أيضا من أن (مصير القاضي إلى الامتثال، من غير نظر إلى زمان ولا [وقت] على حال. [وهذا بعيد] من قياس مذهبه، مع [استمكانه] بالوقف، وتجهيله من لا يراه). فقد غفل الإمام عن كلام القاضي ووجهه، وألزمه ما لا يلزمه، فإنه لا خلاف في تعدد محامل الصيغة للجهات التي','1','645',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (419,'ذكرها القاضي، وإنما النظر في أن اللفظة حقيقة في الجمع، أو حقيقة في الجمع، أو حقيقة في بعضها، مجازا في البعض؟ والقاضي لم يثبت عنده التفاوت في الإطلاق، ولا التساوي، و [لا] الاشتراط، فلم يكن بد من الوقف، بخلاف تعرض لفظ الفعل والمصدر للأعداد، فإنه لم يوضع لها، ولا يصلح للدلالة عليها.
فإن أراد مريد أن يكون المصدر يدل على الأعداد، صرفه عن حكم إطلاقه، وزاد (هاء) فأحد وثنى وجمع. فمن أين يلزم من الوقف عند تعدد','1','646',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (420,'المدلولات، الوقف مع نفي التعرض لهذه الجهات- أعني الأعداد والمبادرة والتأخير؟ وقد سلك القاضي مسلكا واحدا في المسألتين. وكذلك فعل الإمام فيهما.
قال الإمام: (فما اعتمده [أصحاب الفور] أن الصيغة إذا وردت واقتضت إيجابا) إلى قوله (فاز بالأجر، وإن أخلى العمر منه، تعرض للمعصية، فلا استحالة فيه). قال الشيخ: الكلام في المسألة من وجهين: أحدهما- بالنظر إلى المعقول في تصور الوجوب مع المهلة، أو منع ذلك.
الثاني- فيما يقتضيه وضع اللغة وإشعار اللسان، وهو الذي ابتدأ به الإمام على المسألة، باعتبار معقول الوجوب. وما ذكره الإمام على الطريقة من النقض بجوار تأخير قضاء الصلوات التي فاتت بأعذار، مستدلا بالإجماع. وقوله: ثم العمر وقتها على الفسحة. أما القضاء، فمجمع عليه، وأما الفسحة في','1','647',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (421,'القضاء، فلا إجماع فيها، وقضاء المنسيات عندنا على الفور.
وأما المسلك الثاني- وهو تقدير عقلية الوجوب مع القسمة، فهذا محز الكلام. وما ذكره من: أن العقل لا يحيل اقتضاء وجوب شيء، ثم يكون العمر مهلة. فهذا ينازع فيه أصحاب أبي حنيفة (٥٠/أ) أشد نزاع. فكيف قال: ولا يخالف في ذلك مخالفة فنثبته؟ ولأجل استحالة هذا عند القوم، ذهبوا إلى أن المأمور به، إن ضبط آخر وقته، فإنما يتحقق الوجوب آخر الوقت، وإن أطلق مع تحقيق الوجوب، فلا بد من الفور. فمن العجب كونه ادعى الاتفاق على المعقول، والقوم ينكرون ذلك أشد إنكار. فلم يحصل برهان ولا إجماع.','1','648',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (422,'وأما الوجه الثاني الذي عينه وهو: (١٨/ب) أن المكلف إذا مات، ولم يكن امتثل، لقي الله عاصيا. فقد اقتصر على الدعوى من غير برهان. ولكن طريق التقريب أن نقول: إذا قال السيد لعبده: أوجبت عليك خياطة هذا الثوب في أول النهار أو آخره أو وسطهن فلا يخلو: إما أن يقال: إنه لم يوجب شيئا أصلا، وهو باطل، لأنه صرح بالإيجاب، أو يقال: أوجب مضيقا، وهو باطل، لأنه صرح [بالتوسيع]، فلم يبق إلا أن يقال: أوجب موسعا. ونحن نجد هذا من أنفسنا، فيطلب الطالب من غيره تحصيل أمر في يومه، ولا يرتبط غرضه بساعة من ساعاته، بل [متى] حصل [العمل] في ذلك اليوم، حصل المقصود. هذا مما لا شك فيه.
ويدل [عليه] أيضا الإجماع على وجوب الصلاة في أول الوقت، وأنه','1','649',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (423,'[مهما] فعل، كان ممتثلا مؤديا فرض الله، كما نواه [وأداه]، ولا تضييق. هذا ما درج عليه الأولون قطعا.
فإن قيل: ليس حكم العمل بالإضافة إلى آخر الوقت وأوله حكم واحد، بل الفعل في أول الوقت- إن أوقع- كان مندوبا إليه، إذ المندوب: ما','1','650',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (424,'يكون فعله خيرا من تركه، بالإضافة إلى مقصود الطالب، من غير ذم يلحق بالترك. والصلاة مثلا في أول الوقت كذلك، وإن أضيف إلى آخر الوقت، كانت واجبة. إذ يلحق الذم [تاركها] الآن. فهما فعلان في وقتين يختلف حكمهما، باختلاف الإضافة إلى الأوقات، كما يختلف حكم الصوم بالإضافة إلى الأزمنة، كصوم رمضان ويوم العيد.
فنقول: التحقيق في ذلك أن نبين الحقائق، ثم نجعل الألفاظ تابعة لها. فإن حق الأمور المختلفة، أن تختلف العبارات الدالة عليها، فهو أبعد عن الإيهام، وأجلب للأفهام. فنقوم: الفعل المطلوب قد يلحق الذم بتركه عند أول الخطاب وإمكان الامتثال، وهو الواجب المضيق. ومعنى التضييق فيه: أن المكلف لم يجعل له فسحة في التأخير من زمان إلى زمان آخر. وفعل مطلوب يلحق الذم بتركه، بالإضافة إلى مجموع الوقت، [ولا] يلحق بالإضافة إلى إخلاء بعض الأوقات. وما جاز تركه مطلقان يخالف ما لا يجوز','1','651',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (425,'تأخيره، وما يجوز تأخيره دون تركه، أو الواجب الموسع.
والعبارة الثاية أولى من وجهين: لأن الندب مع امتناع الترك، متناقض بالإضافة إلى [عرفهم]. والوجوب: [التحتيم] في تحصيل الفعل، دون التعرض لأزمنة الإيقاع. والإجماع أيضا منعقد على كون الصلاة واجبة عند دخول الوقت، (١٩/أ) وإن لم تجب المبادرة، والتضييق منتف. ويحقق ذلك أن المصلي في أول الوقت ينوي فرض الصلاة، ويثاب ثواب الفرض لا ثواب النفل. فقد تبين تعدد الأقسام عقلا وشرعا، نية ولفظا. (٥٠/ب)
وقوله: (والمختار تعيين قسم من الأقسام التي [ذكروها]، وهو التعصية إذا مات ولم يفعل). والكلام ههنا مفروض في الواجب الذي ضبط آخر وقته بغير العمر. واقتصر الإمام في هذا المكان على الدعوى من غير برهان ولم يزد على قوله: (ولا [مبالاة] بقول من يقول من الفقهاء: إنه مات غير','1','652',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (426,'عاص). وما ذكره القوم من امتناع إيهام الشرط، لم يدل على الجواز، بل قال: (فإن هذا النوع من الجهالة محتمل على الجملة). اقتصر في جميع هذه الأطراف على الدعاوى من غير برهان.
أما الأول، [فإنه] يقول: إذا مات في أثناء الوقت بحيث يبقى عليه من الوقت المختار ما يسع الفعل، فإنه لا يموت عاصيا بترك الصلاة، وإن كان عازما على أن لا يصلي، فإنه يأثم، لا لأجل إضاعة الواجب، بل لمجرد العزم على المخالفة. حتى لو عزم على ذلك قبل دخول الوقت وتاب، كان عاصيا لعزمه، وإن كان لا يتأتى منه إيقاع الفعل المطلوب قبل دخول وقته، [فمعصية] ترك العزم غير معصية ترك الفعل.
والذي يدل على ذلك: أنا نعلم أن الصحابة رضوان الله عليهم كانوا لا يعصون من مات فجأة بعد مضي مقدار ركعتين بعد الفجر، أو أربع ركعات بعد الزوال، وكانوا لا ينسبونه إلى التفريط. بل كيف يعصي وقد جوز له','1','653',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (427,'التأخير؟ ولما بين الرسول صلى الله عليه وسلم الأوقات قال: (الوقت بين هذين). في معرض التعليم. وهذا وقت الحاجة إلى البيان. فلو كان يتطرق إلى المؤخِر عصيان، لبينه عندما سأله، لإزالة الإشكال.
وأما قوله ثانيا: (فإن هذا النوع من الجهالة محتمل). فغير صحيح، فإن الشروط إنما هي أعلام منبهة للمشروطات، وشروط الساعة: أعلامها، وسمي الشرط شرطا لإعلامه نفسه بلباس يعرف به، فكيف يصح أن يكون الشرط الذي علق الشرط الحكم عليه لا يتصور للمكلف معرفته؟ وقد تقدم كلامه أنه لا يشترط في الوجوب علم المكلف به، بل يشترط تمكنه من العلم. وكما يشترط التمكن من العلم باعتبار الوجوب، فكذلك يشترط باعتبار التحريم والجواز جميعا. وهذا حكم لا يتصور علمه بحال، فيكون محالا عقلا عند قوم، وشرعا عند آخرين.','1','654',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (428,'وقوله: (وإنما الذي يستحيل جهالة تمنع فهم الخطاب، أو إمكان امتثال). راجع إلى قوله: (يكلف المتمكن، ويقع التكليف بالممكن). ولكننا نقول: هذه الجهالة تمنع فهم الخطاب، فإنه لا يدري، هل خوطب بجواز التأخير أو تحريمه؟ ولكنا نقول: تحقق الوجوب (١٩/ب) باعتبار الطلب. فإذا قيل: هل جاز التأخير أم لا؟ قلنا: لم يتعرض له الدليل، لا مطلقا ولا مشروطا. فإن قيل: إذا تحقق الوجوب ومات ولم يفعل، فقد ترك الواجب،','1','655',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (429,'فيكون مذموما، فإن حد الواجب: هو الذي يذم تاركه بوجه ما. وهذا الحد ليس باعتبار المعقول، لأنا قد بينا أن كون الفعل واجبا، إنما كان كذلك، لتعلق الإيجاب به. والإيجاب: قول في النفس يباين الندب. ولكن هذا أمر يكون لتعريف خصوصية الطلب.
وقد بينا صحة ما قال القاضي من أن الله تعالى لو أوجب شيئا لوجب، وإن لم يتوعد (٥١/أ) بعقاب على تركه. والمقصود إثبات مزيد معرفة، لفصل الواجب [عن] المندوب، ولحوق الذم بترك المطلوب، يميز بين القسمين، ونعني بقولنا: (بوجه ما)، أن تارك الصلاة حتى يخرج وقتها','1','656',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (430,'مذموم. فلحوق الذم في هذه الحالة، يبين أنها ليست من قبيل المندوبات، فيعقل الوجوب بالنظر إلى الطلب الجازم شرعا، ويتحقق كون الفعل واجبا في أول الوقت بدليل الإجماع، بالنظر إلى نية الوجوب، وحصول الامتثال، ويلحق الذم مخلي الوقت بكماله عن العمل، ويسقط الذم عن الميت في وسط الوقت، بناء على أن ما بقي منه يسمع الفعل، موافقة لأهل الإجماع. والتمييز بين الوجوب والندب قد حصل. وهذا كلام في غاية الحسن.
وقد لاحظ مالك رضي الله عنه [هذا] الأصل في الصلوات والأيمان. فأما الصلوات، فإن المرأة الطاهر إذا لم تصل في أول الوقت، وأصابها الحيض، وقد بقي ما يسع الصلاة، ولم تطهر حتى خرج وقت الصلاة، فإنه لا قضاء عليها. لأن الحيض أصابها في وقت الأداء، فنافى الوجوب، فتصير بمثابة ما إذا أصابها الحيض من أول الوقت.','1','657',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (431,'والشافعي أوجب القضاء، وكأنه نظر إلى أنها جاز [لها] التأخير، لتفعل فيما بعد. ومالك [رضي الله عنه] لم ير هذا شرطا، لحصول الإبهام فيه، وخروجه عن حقيقة الشرط، فإنه غير صالح لتعريف المشروط. وكذلك [قال] فيمن أخر قضاء رمضان مع التمكن منه، فإنه عندنا يوسع له في القضاء إلى شعبان، لقول عائشة رضي الله عنها: (كان يكون علي قضاء رمضان، فما أستطيع أن أقضيه إلا في شعبان، يمنعني الشغل برسول الله صلى الله عليه وسلم). فلو كان القضاء على الفور، لما أخرته. ولو كان يتسع أكثر، لما استمر على القضاء فيه، وأمكن في بعض الأوقات أن يؤخر عنه. فلو أخر القضاء إلى شعبان، واتفق عذر يمنعه من القضاء فيه، لم تكن عليه فدية المؤخر، حتى يدخل رمضان، لأنه أخره إلى بعض الوقت المتسع.','1','658',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (432,'وكذلك قال رحمه الله في الحالف ليفعلن: فإن لم يضرب أجلا، (٢٠/أ) فهو على حنث، كما نقول في الأمر المطلق. وإن ضرب أجلا، كان على بر حتى يحل الأجل، ويصير كالأمر إذا ضبط له وقت، ولم يأت آخر وقته. وفائدة كونه على بر أو حنث، فيما يتعلق بوطء الزوجة أو الأمة المحلوف بعتقها.
وقوله: (فأما تكليف المرء شيئا، مع تقدير عمر مهلة وفسحة، إن امتثله فاز بالأجر، وإن أخلى العمر منه، تعرض للمعصية). كلام صحيح، وهو ربط الطلب بالفعل، مع قطع النظر عن زمان مخصص.
قال الإمام: (ومما تمسك به هؤلاء، وهو قريب المأخذ مما سبق) إلى قوله (لا يتأتى للقوم التعلق به، لإثباتهم واجبا مقيدا بجواز التأخير). قال','1','659',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (433,'الشيخ: هذا السؤال مشكل، لأن الوجوب يضاد الجواز. وإذا كان كذلك، فكيف يصح أن يقال: الفعل واجب، والترك جائز؟ ومعقول الجواز يقتضي التخيير بين الأمرين، ولا يعقل الجواز باعتبار جهة واحدة. فإذا قلنا بجواز الترك، فقد خيرناه بين الفعل والترك، مع ترجيحنا الفعل على الترك، فهذا متناقض. وقد أجيب عن هذا، فقيل: الذي يتناقض وجوب الفعل جواز الترك. وأما جواز التأخير، فلا يناقض. قالوا: والتأخير ليس هو الترك. واعترض عليه: بأن التأخير ترك على (٥١/ب) الحقيقة، فإن الترك: هو التلبس بضد الفعل، فيكون بذلك تاركا للضد الآخر. فإذا لم يحتم الإقدام، فقد جوز الترك في ذلك الزمان. ويؤكد هذا الإلزام أن يقال: هل الفعل واجب أول وقت الفهم والإمكان أم لا؟ فإن كان واجبا فيه، فقد [جو إخلاؤه] عنه، فهو مناقض. وإلى هذا المعنى يرجع الإشكال، وإن اختلفت الصيغ في الإلزام. والنقض عليهم- إن سلموه- لازم، وإن منعوه، أثبت بالبراهين السابقة.','1','660',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (434,'قال الإمام: (ولكن الإشكال قائم في النفس في الصور المتفق عليها) إلى قوله (ولا ينبغي أن يظن بهذا الرجل العظيم غير هذا. غير أنا لا نرى ذلك رأيا). قال الشيخ: اقتصر الإمام على ذكر مذهب القاضي، واشتغل بالرد عليه. [وكثيرا] ما يفعل هذا في هذا الكتاب. وهو تقصير كثير، من أعظم الإعانة على تعقب المذاهب ببيان مستنداتها. ومستند القاضي [والرد]','1','661',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (435,'عليه، [من حيث] الجملة، أمور حصرته إلى هذا القول:
أحدها- ما استقر عنده من حد الواجب: أنه الذي يذم تاركه. وتقرر عنده أن التأخير ترك من جهة العقل، واستقر عنده بالإجماع وجوب الصلاة في أول الوقت، وتقرر أيضا بالإجماع أن المؤخر عند العزم غير مذموم. فتقرر بهذا أن الواجب ليس متحدا، إذ لو اتحد الواجب وترك، لم يكن من الذم، فتقدر واجبا ليس متحدا، إذ لو اتحد الواجب وترك، لم يكن بد من الذم، فقدر واجبا آخر. فإذا قيل له: فما الذي يدل على أن المقدر العزم؟ استدل (٢٠/ب) على ذلك: بأنه [[إن فعل الفعل]، لم يجب العزم].
وإن فعل العزم، لم يجب الفعل. فهذا الذي دل على تعيينه دون غيره. فإنه لو فعل كل واجب دون الصلاة، ولم يعزم على فعلها، لم يكن ذلك مسقطا عنه إثم','1','662',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (436,'الترك. هذا تقرير كلامه.
وقول الإمام: (وهذا خروج عظيم من مسلك التحقيق) إلى تمامه. والتشنيع عليه بأنه توقف في أصل الصيغة، من حيث لم يسنح له دليل على التعيين. فكيف أثبت وجوب العزم من غير أن تتعرض الصيغة له بحال؟ وهذا كلام ضعيف، فإن القاضي لم يثبت العزم تلقيا من الصيغة المتقاصرة عليه، بل يقطع القاضي بأن اللفظ لا يصلح لإيجاب العزم، وإنما يتلقى وجوب العزم من الطريق الذي قررناه. بل في الصيغة احتمال [الوجوب والندب،','1','663',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (437,'وأما وجوب العزم فلا تحتمله بحال. والعجب من الإمام كيف وجه هذا الإلزام]؟ وكيف غفل عن هذا المستند؟
فإن قيل: أنتم قد منعتم أن يتلقى حكم شرعي من العقول، واعترفتم بتقاعد الصيغة عن الدلالة على وجوب العزم، ثم قضيتم بأنه واجب، وهذا تصريح يتلقى الأحكام من العقل. قلنا: نحن ننبه ههنا على دقيقة يتكرر الاحتياج إليها في الكتاب.
أعلم أن الذي منعناه: أن تستقل العقول بالأحكام، غير مفتقرة إلى ورود الشرائع، فأما إذا وردت الشرائع، ودلت الأدلة على طلب، وكان لا يتمكن تحصيله إلا بتعاطي غيره، والانكفاف عنه، فإنه قد ثبت وجوب الوسيلة أو تحريم المانع، فلا تكون العقول مستقلة بأصل الحكم، دون ورود الشرع، ولكن لما طلب أحد الأمرين، تلقينا منه الآخر. (٥٢/أ)','1','664',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (438,'وقول الإمام: (وفيما صار إليه خصلة أخرى عظيمة الموقع). وهو أنه يلزم منه المصير إلى أن الصلاة ليست واجبة تعينا، وإنما تكون أحد الواجبين لا بعينه. فقد صرح القاضي بذلك، وقال: إنها كواجب المخير، بالإضافة إلى أول الزمان وإلى آخره أيضا، ولم يناقض أصله في الواجب المخير.
وقوله: (ثم إنما كان يستقيم ما ذكره، لو ساعده حملة الشريعة) إلى آخره، (وقد أجمع حملة الشريعة على أنه لو أضرب عن العزم، واتفق منه الفعل، فليس من العلماء من يعصيه). هذا الكلام ليس ببين، فيقال له: ما','1','665',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (439,'معنى قولك: لو أضرب عن العزم: أمع الذكر، أو مع النسيان؟ فإن قال: مع النسيان، فصحيح. وسببه أن الناسي غير مكلف. وإن قال مع الذكر، فغير صحيح، لأنه [لا] يضرب عن العزم على القيام بالواجب، إلا إذا تلبس بضده، وهو العزم على الترك، أو التردد في المعصية، وكلاهما حرام. هذا تقرير [دليل] الفريقين.
ولكنا نقول: ما ذكره القاضي غير صحيح عندنا. وإنما ألجأه إلى هذا المجال الضيق، ما اعتقده من أن الواجب: هو الذي يذم تاركه مطلقا. على ما','1','666',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (440,'قررناه: ونحن إذا رددنا (ما يذم تاركه بوجه ما)، وفسرناه على حسب ما ذكرناه، (٢١/أ) تحقق الوجوب، ولم يفتقر إلى عزم يكون بدلا عن الفعل.
[ثم ما ذكره متناقض في نفسه، فإنا نقول له: هل العزم عندك بدل من الفعل الواجب، بحيث إذا فعل العزم، سقط وجوب الفعل؟ أو يبقى الفعل على وجوبه، ويكون العزم بدلا عن المبادرة؟ فإن اختار القسم الأول، وهو الذي صرح به، فيقال له: إذا كان العزم بدلا عن الفعل الواجب]، فإن عزم على الفعل الواجب، سقط وجوب الفعل، ويستحيل بقاء العزم واجبا على [فعلٍ] نفي وجوبه. هذا متناقض لا شك فيه. وإن قال: ليس العزم بدلا عن الفعل، بل عن المبادرة، فقد رجع إلى [ما] منه فر. فإنه على هذا التقدير، قد عقل الوجوب، من غير مبادرة إلى الإيقاع، فلا حاجة مع عقلية هذا، إلى إثبات العزم بدلا على حال. ثم نقول: وجوب العزم على الطاعة الواجبة، [وترك] المحرمات، ليس بدلا في الشرع على حال، ولذلك يجب في','1','667',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (441,'الواجبات التي لم تدخل أوقاتها، ولم يمكن في الحال فعلها، وإنما يرجع ذلك إلى اعتقاد الشريعة وتصديق موردها. فلا وجه لقوله على حال.
قال الإمام: (فإن قيل: فما وجه الجواب عن السؤال) إلى قوله (فيؤول الأمر إلى الترهيب والخوف. وليس بعد هذا البيان بيان، وهذا نجاز الطريقة). قال الشيخ [أيده الله]: ما ذكره الإمام عن الشافعي، من كون الصلاة تجب في أول الوقت. كلام صحيح، ومصيره إلى أنه لا يأثم إذا مات في أثناء الوقت كلام المحققين. وقد [نقلنا] إجماع المتقدمين، واستدللنا على ذلك ببراءة الذمة وتحقيق النية. ومن تحقق أن الفعل لم يجب في الحال، كيف يتصور أن يقصد إلى إيقاعه واجبا؟ ولو كان كذلك، لم يكن بد','1','668',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (442,'من نية التقديم، كما في الزكاة، فإنه لو أخرج المال قبل حلول (٥٢/ب) [الحول]، ولم يقصد أنه قدم ما سيجب عليهن لم يكن كذلك مجزئا له. ونحن على بصيرة أنه ما نوى أحد من السلف في الصلاة في أول الوقت إلا ما نواه في آخره، ولم يفرقوا أصلا، وهو مقطوع به.
[وأما] كون الإمام بنى انتفاء الوجوب على سقوط الإثم، [فجنوح] إلى ما ذهب إليه القاضي، من أنه في ترك الواجب من الذم، ولكنه يفارقه من وجه آخر، وهو أن القاضي لا يفرق بين الترك والتأخير، [والتأخير] [ترك] على الحقيقة، وهما جمعا- إذا كان المكلف [متمكنا] منهما- ناقص ذلك الوجوب. والإمام يرى أن التأخير المسوغ غير مناقص، فصح عنده أن يجب الفعل، وإن لم تجب المبادرة إليه.','1','669',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (443,'والمذهب الثالث: وهو الذي اخترناه، أن مطلق الترك مع سقوط الذم عن فاعله، لا يناقض الوجوب في الشرع، إلا إذا انقضى الوقت بكماله، والمكلف متمكن من إدامة [ما كلف] به. ولم يذمه الشرع على تركه. فهذا يمنع تحقيق الوجوب فيه، بالنظر إلى المشروع دون المعقول.
وأما تعجيل الزكاة قبل حلول [الحول]، فمختلف فيه بين العلماء، فذهب ذاهبون إلى منعه مطلقا، وذهب ذاهبون إلى تجويزه مطلقا، بعد ملك النصاب. وذهب ذاهبون إلى جواز التقديم، إذا بقي [زمان قليل، والمنع إذا بقي] كثير من الزمان.
وسبب هذا التردد: دوران المسألة بين قضاء الديون (٢١/ب) ومحض العبادات. فالالتفات إلى مضاهاة الديون يصحح التقديم، والالتفات إلى محض العبادات، بدليل اشتراط النيات، يوجب الاقتران أن يتصرم الحول. وهذا هو أحد قولي مالك رضي الله عنه.','1','670',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (444,'فالالتفات إلى دوران المسألة بين [الأصلين]، وأخذ شبهين من الطرفين، رجح بعض شائبة الدين عن قرب الحول. وهذا هو اختيار ابن القاسم وروايته عن مالك. وقد فعل ذلك عبد الله بن عمر (فكان يخرج فطرة رمضان قبل يوم العيد باليوم واليومين). وليس [من] الإنصاف إلزام مسألة مقطوع بها، وهي إجزاء الصلاة في أول الوقت، على مسألة الزكاة المختلف فيها. فالصحيح وقوع الصلاة واجبة مجزئة، وسقوط الذم عن المؤخر إلى وسط الوقت. وإن اتفق عذر يمنعه الإيقاع، لم يكن عاصيا على حال.
وقوله: (وسبيل مكالمة أصحاب أبي حنيفة، إن استنكروا الوجوب على','1','671',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (445,'حكم الجواز التأخير، كسبيل مكالمة أصحاب الفور في الطريقة الأولى). يرجع إلى ما قررناه من أن التسويغ لا يمنع الوجوب.
وقوله: (وإن تفطنوا لنفي المأثم في الصلاة، وسلم لهم ذلك، لم يبق لادعاء الوجوب معنى). لأنه يرى أن الترك من غير خوف ذم يمنع الوجوب.
وقوله قبل هذا: (فإن الذي ذكرناه إظهار منا لخلاف [ما استبعدوه قطعا]. لا يصح إذا كانت (ما) بمعنى الذي، فإنه يكون تقدير الكلام: فإن الذي ذكرناه إظهار منا لخلاف] استبعادهم، أي اخترنا ما استبعدوه. وهذا الذي أراده. والعلم عند الله تعالى.
وقوله: (فإن أصر مصر على المخالفة، لم ينتظم له ذلك، إلا من تأثيم من يموت في أثناء الوقت، وقد ذهب شرذمة إلى ذلك من الأصحاب). هذا','1','672',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (446,'ما قررناه عنه من أن جواز التأخير، لا ينافي الوجوب (٥٣/أ) دون جواز الترك.
وأما قوله: (وأما الأمر المسترسل على العمر، فالذي أراه أن من أخره، فلا يقطع القول فيه بنفي الإثم عنه، ولا يطلق ذلك إلا مشروطا). فالأمر على ما ذكرناه عند من يرى الفور.
وقوله: (والذي يكشف الغطاء في ذلك، أن الواجب المضيق المحقق، لا','1','673',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (447,'ينفصل عما ليس بوقوع العقاب [بالتارك لا محالة] إلى آخر. تخيل توجه اعتراض وهو: كيف يتحقق الوجوب مع كونه لا يؤثم بالتأخير؟ فقال: تحقق الوجوب بأنه خائف من الانحراف، فيفوت الفعل، فإن الواجب على التضييق، لا يتحقق العقاب فيه. فأين هذا من قوله: لا يعقل وجوب من غير وعيد؟ فقد رجع إلى ما أنكره على القاضي فيما تقدم.
تنبيه على أصل: وهو أن الأوامر المطلقة إذا لم يصر إلى أنها على الفور، وجوز فيها التأخير، فالمجوزون افترقوا ثلاث فرق:
فذهب فرقة إلى أن التأخير مشروط بشرط سلامة العاقبة، والفعل فيما بعد. وهذا الذي فسره الإمام ههنا: (إن مات ولم يحج، انبسطت المعصية على جميع سني الإمكان).
وطائفة ذهبت إلى أن الوجوب متعلق بالفعل من غير نظر إلى زمان. وهذا (٢٢/أ) هو الذي نختاره. وإذا مات في هذه الصورة بعد الإمكان، تحققت المعصية، لا باعتبار التأخير، بل من جهة ترك الواجب. والقاضي','1','674',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (448,'إنما يجوز له التأخير بشرط العزم على الفعل. على حسب ما تقدم. وإنما يصح العزم إذا أخر إلى أمد يغلب على ظنه البقاء إليه، كتأخير الصلاة من وقت إلى وقت، وتأخير الصوم من يوم إلى يوم.
وأما تأخير الحج من سنة إلى سنة، فهل ذلك مما يغلب على الظن؟ قال أبوحنيفة: لا يغلب على الظن. وكذلك أيضا يقول القاضي في الواجب الذي انضبط إلى آخر وقته، لا يجوز التأخير إلا بشرط العزم. ولا يتصور العزم على الفعل مع التأخير، إلا إذا أخر إلى أمد لا يغلب على ظن الهلاك إليه. فلو ظن أنه يموت في وسط الوقت، لم يجز له التأخير- على هذا الرأي- ويكون [عاصيا] بذلك.','1','675',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (449,'فلو أخر ثم لم يمت، فإذا وقع الفعل، فهل يكون الفعل قضاء؟ [وإنما] قال القاضي: يكون قضاء، لأنه يقيد الوقت في حقه بغلبة ظنه، فيكون هذا كل الوقت في حقه، وإن كان بعض الوقت في حق غيره. وكذلك يلزم عليه إذا غلب على ظنه أنه يموت قبل سنة أخرى، ووجب عليه الفور، فلم يفعل، ولم يمت، ووفق للعمل، فالذي يقتضيه قول القاضي أنه قضاء، وكذلك يقول في كل واجب تعين البدار إليه، ثم فعل، فيجيء على أصل القاضي أنه قضاء. والرأي الحق عندنا أنه أداء.
ولننبه هنا دقيقة، وهي أن كل فعل يتلقى وجوبه من الدليل الأول، من غير حاجة إلى دليل جديد، ويقع الفعل موافقا لذلك الطلب، فهو أداء. وكل فعل افتقر المكلف في إيقاعه طاعة إلى خطاب جديد، تداركا لفوات متعلق الطلب الأول، فهو قضاء. فعلى هذا من غلب على ظنه أنه يموت قبل','1','676',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (450,'(٥٣/ب) عام آخر جديد، ولم يحج عصى، من جهة عزمه على ترك الواجب. فإذا لم يمت، فهو مطالب بالحج بخطاب الله تعالى: {والله على الناس حج البيت}، فلا يكون قاضيا بحال.
قال الإمام: (ومما تمسك به أصحاب الفور، النهي على النسق [المتقدم] في مسألة التكرار) إلى قوله (وشرطنا قبض الكلام بعد الوضوح، وإعداد جمام التقرير للمشكلات. فهذا منتهى مسلك أصحاب الفور). قال الشيخ: قوله واضح كله، غير مفتقر إلى زيادة إلا وجها واحدا، وهو قوله: (فالنهي بالاتفاق يقتضي الاستغراق). ليس هذا قول جميع الأصوليين. وقد خالف في ذلك بعض الناس، وقال: النهي أيضا الانكفاف مطلقا. ولو قال له: لا تفعل كذا. حسن أن يقول: انتهائي أبدا أم مرة واحدة؟ ولكن','1','677',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (451,'الصحيح أن النهي المطلق يقتضي (٢٢/ب) الاستغراق.
وقد أشار إليه الإمام فيما سبق، ولكنه لم يستوعب الكلام عليه. إلا أنه قال: ([والقول] الكافي فيه أن الإثبات يختص بثابت، والنفي لا اختصاص له، [فكان] الجنس كالشخص في حقه). ولنبسط القول فيه ههنا بعض البسط، فنقول: قد اختلف الناس في معنى قول القائل: لا تقم. فذهب كثير من الناس إلى أن معناه: لا يوجد منك قيام. فـ (لا) حرف نفي، فإن دخلت على اسم منكر، كانت نفيا، وإن دخلت على فعلن سميت حرف نهي. والمراد: نفي المصدر بواسطة إشعار الفعل به. فيكون التقدير: لا يوجد منك قيام.','1','678',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (452,'وهو لو قال ذلك، لدخل في باب النكرة المنفية بـ (لا) نحو: فلا رجل في الدار. وقد ذهب إلى هذا كثير من المعتزلة. وهو اختيار القاضي أنه يصح طلب النفي. ولعل هذا [بناه] على القول بصحة تعلق القدرة بالإعدام.
وقال قائلون: لا يصح أن يكون النفي مطلوبا لأنه يتعلق بشيء. و (لا تفعل) عدم، وليس بشيء. ولا يصح الإعدام بالقدرة. قالوا: والمنتهى بالنهي مثاب، ولا يثاب إلا على شيء.
والنظر في المسألة يتعلق بالبحث عن متعلق التكليف. والذي اختاره حذاق الأصوليين أن متعلق أفعال المكلفين.
وقد بينا أن هذا تجوز، والمراد ما يصح أن يكون فعلا، والنفي لا','1','679',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (453,'يصح أن يكون فعلا، وكذلك الانتفاء. فإذا [انبنى] الأمر على المذهب الأول، في أن النفي مطلوب، وهو متعلق النهي، واستقر أن أصل المصادر التنكير، اتضح جدا أن يكون النهي على العموم، إلا أن يدل دليل على الاختصاص، وضده الأمر بالظاهر عند الاستغراق، إلا أن يقوم دليل على خلاف ذلك.
وإن فرع على المذهب الثاني، وهو الصحيح، رجع النهي إلى طلب الترك، فيشق على هذا التقدير كونه مستغرقا. ولكن يقرر الكلام من وجه آخر، فيقال: لا يعدل العربي عن قوله (لا تسكن) طالبا لمطلق الحركة، فإنه يكون مطولا، من جهة دخول الحرف الزائد، ومن جهة أنه لم يذكر حقيقة المطلوب، فحمل الأمر على أنه طلب حركة يحصل عندها نفي السكون، ولا اختصاص في النفي، [فلزم] [بذلك] عموم النفي بهذا التقرير. والله المستعان. [وكلامه] في بقية الطريقة بين، لا يفتقر إلى زيادة. وقد تقدم الإرشاد إليه قبل هذا.
قال الإمام: (فأما من لم ير الفور، وجوز (٥٤/أ) التأخير: فمن مسالكهم','1','680',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (454,'ما ذكره القاضي معتمدا لنفسه) إلى قوله (فقد أبعد). قال الشيخ: الذي ذكره القاضي من [الاحتجاج]، صحيح، وما أتى به استشهادا، تقريب وتمثيل، من غير أن يقصد القياس والاستدلال. وإنما وسع المقال وضرب الأمثال.
والمقصود من الكلام: أن اللفظ لا تعرض له للازمان بحال. والتقديم والتأخير إنما هو بالنسبة إلى الزمان، وذلك من ضرورات الأفعال، (٢٣/أ) لا من معقول التكليف، والزمان كالمكان من غير إشكال. وكما يصح أن يربط التكليف [بالزمان، كذلك يصح أن يربط بالمكان. ولكن إذا قصد ذلك الطالب،','1','681',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (455,'فعليه التعريف بنصب الدليل، وإلا كان ذلك] من قبيل التكليف بالمستحيل. وكون القاصد يريد ذلكن لا ينكر على الجملة، وليس الحق إلا ما قاله القاضي - رضي الله عنه -.
وقوله: (ومن عد النظر في هذا الفن من قبيل النظر في الغيم والإصحاء) إلى آخر كلامه. ليس فيه تحقيق، فإن القاضي إنما يسوي بين هذه الأمور، بالنظر إلى قصور إشعار اللفظ عن هذه الجهات كلها. وهذا','1','682',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (456,'بالإضافة إليها دلالة على جهة واحدة في القصور وعدم التعرض لشيء منها، فيجب فهم مدلول اللفظ، وقطعه عن الأوقات كلها.
لكنا نقول للقاضي: إذا عقل عندك تعلق الطلب بالفعل من غير نظر إلى زمان، فأي حاجة مع هذا إلى تقدير عزم يكون بدلا من الفعل، وقد ربط الطالب طلبه بالفعل، معرضا عن جميع الأزمنة؟ وهذا غير مستنكر عرفا وعقلا. فالوجه كما قال: فهم الامتثال وقطعه عن الأزمنة على الإطلاق، إلا] أن يتعرض لها [الطالب]، وينصب عليها دليلا.
وقوله: (فالقول الحق في ذلك: أن الأمر اقتضاء ناجز، فليقتض الوفاء الناجز). كلام ضعيف، ومن العجب ادعاؤه كونه حقا مع اعترافه بعد هذا','1','683',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (457,'(بأنه لا يستقل [بإثبات] غرض، فإن الطلب ليس مجحودا). ومعنى قوله: (إنه طلب ناجز). أي توجه الطلب على المكلف في الحال. وهذا مسلم من غير إشكال. قال الشيخ: كلام القوم صحيح، وما ذكره الإمام ضعيف. وبيان صحته: التمسك [بأن] القائل إذا قال: (افعل)، فقد ألزم غيره الفعل، غير متعرض للأزمنة. وإذا التزم الفعل، فقد التزمه أيضا، مع قطع النظر عن أزمنة الفعل. وقد تقدم تقرير هذا في طريق القاضي. وإيراد الالتزام على الإلزام، لم يكن على جهة القياس، وإنما المراد أن الملتزم والملزم جميعا، لم يتعرضا','1','684',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (458,'للأزمنة، فإن [قصدا] إليها، فلينصبا الدليل الدال عليها. فيقول الملزم: (افعل غدا)، فإذا وقع التقييد بذلك، لم يثبت الامتثال، ولا الخروج عن الالتزام بالتأخير عن ذلك الزمان.
قال الإمام: (فأما من قال من أصحاب الوقف: إن من بادر إلى إيقاع [الفعل] المطلوب، لم يقطع بكونه ممتثلا) إلى قوله (في مظان الضروريات). قال الشيخ: [- رضي الله عنه -]: الأمر على ما قاله من لزوم القضاء بامتثال المبادر لاقتضاء الطلب للفعل، ودلالة الصيغة عليه، وعدم التعرض لأزمنة الفعل، وصحة نسبة الفعل الواقع في أول الوقت إليه، ولا معنى لوقوع الفعل طاعة إلا من جهة موافقته للطلب.
قال الإمام: (والذي يجب القطع به) إلى قوله (فإن اللفظ (٥٤/ب) لا','1','685',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (459,'اختصاص له بوقت). قال الشيخ [أيده الله]: أما قوله: (إن المطالب (٢٣/ب) [مهما] أتى بالفعل، فإنه بحكم الصيغة ممتثل). فالأمر على ذلك، وهو اختيارنا واختيار القاضي أيضا، وقد بينا وجهه. وأما التوقف في المؤخر، فلا وجه [له]، لما قررناه من وجوب الافتقار في الطلب [إلى] ما دل عليه اللفظ، واللفظ لم يفتقر في دلالته [إلى] زمان، ولم يتعرض له','1','686',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (460,'بحال. وإذا أمكن الطلب من غير نصب دليل عليه، لزم من ذلك التوقف في المبادر أيضا، لاحتمال أن يكون المقصود التأخير، وهو تمام هذا المذهب.
ثم أيضا يلزم منه التوقف في أصل امتثال، لأنه يمكن أن يقول، ويضمر مبادرا، ويصح أن يقال: (افعل وبادر). فعلى التقدير الأول، يخرج المؤخر عن كونه ممتثلا. وعلى التقدير الثاني، لا يخرج عن كونه ممتثلا. وإذا تحقق استواء الأمرين بالنظر إلى اللفظ، لم يكن سبيل إلى تعيين أحدهما بالتحكم.
وما اعتذر به الإمام من (أن اللفظ الدال على الطلب لا اختصاص له بزمان). يحقق إبطال الوقف في المؤخر على كل حال.','1','687',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (461,'[وأما قوله]: (أجمع المسلمون على أن كل مأمور به بأمر مطلق، إن لم يجز تأخيره، فإذا فرض تأخيره ثم إقامته، فليس ما أقيم مقضيا، وإنما هو','1','688',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (462,'مؤدى). فليس الأمر كذلك في ادعاء الإجماع. وقد ذهب بعض الناس إلى أنه لا يكون [ممتثلا] بحال، وهذا هو الذي يقتضيه قول من يعتقد أن الأمر على الفور، باعتبار وضع اللغة. فعلى هذا لو قال له: (افعل على الفور) وأخر، لم يكن ممتثلا. وكذلك إذا كان مقتضي اللفظ ذلك. نعم. لما غلب في الشرع قضاء المؤقتات بعد ذهاب الأوقات، ظن ذلك من مقتضي الطلب الأول.
وسيأتي هذا في مسألة أخرى، إن شاء الله.

قال الإمام [رحمه الله]: <span data-type="title" id=toc-99>(مسألة لفظية: ذهب القاضي في جماعة من الأصوليين إلى أن المندوب إليه مأمور به) </span>إلى آخر المسألة. قال الشيخ: الذي ذهب إليه القاضي هو الصحيح. والحد الذي ذكره الإمام [للأمر] يتناول الندب، لأن الأمر: هو القول المقتضي الطاعة بالفعل. هذا حده عنده. وفاعل','1','689',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (463,'المندوب مطيع. وإذا وجد الحد، فكيف يتخلف المحدود؟ وأيضا فإنه شاع في ألسنة العلماء أن الأمر ينقسم إلى أمر إيجاب وأمر استحباب. وما شاع أنه انقسم إلى أمر إباحة وأمر إيجاب. وقد قدمنا أن (افعل) الذي هو لفظ الأمر، إنما يقتضي الطلب من غير زيادة.
وأما قول الإمام: إنها مسألة لفظية. إن أراد أنه لا فائدة لها في الأصول، فليس بصحيح. وإن أراد أن البحث [فيها] يتعلق باللغة، فالأمر على ما قال. وبيان الحاجة [إليها] في الأصول أنه إذا قال: أمرتكم، أو أنتم مأمورون. إن قلنا: إن لفظ الأمر يختص بالوجوب، كان اللفظ ظاهرا في ذلك، حتى يقوم دليل (٢٤/أ) على خلافه. وإن قلنا: إنه متردد بينهما، لزم أن يكون','1','690',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (464,'مجملا. وهذه المسألة خولف فيها من وجهين:
أحدهما- البحث العقلي، هل وجد في الندب حقيقة الأمر؟
والثاني- هل يسمى الندب أمرا؟ وهذا بحث لغوي. وقد نوزع في الأمر الأول. فقال الإمام فيما سبق: (والندب من ضرورته تخيير في الترك يفوت جزم الطلب). وهذا هو الذي حمل من صار إلى أن المندوب إليه غير مأمور به. وهذا (٥٥/أ) عندنا غير صحيح، والمندوب إليه [مطلوب] طلبا','1','691',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (465,'محققا، لا تخيير فيه بوجه. فإن التخيير: عبارة عن استواء الفعل والترك، والتساوي بينهما بتسوية الشرع. على ما سيأتي في حد المباح. وهذا المعنى غير موجود في المندوب إليه.
فإن قيل: بقيت له خيرة. [قلنا: خيرة] من جهة الدواعي والصوارف، أو من جهة الشرع؟ الأول مسلم، والثاني ممنوع. وتسلم الأول [لا] يضر في تحقيق الطلب، فإنه يتطرق إلى الواجب. وقد قال الله سبحانه وتعالى في المحرمات: {فمن شاء فليؤمن ومن شاء فليكفر}. هذه خيرة باعتبار الطباع، لا باعتبار الشرع، فلا يمنع من تحقيق الطلب.
قال الإمام: (مسألة: ذهب بعض أئمتنا إلى أن الأمر بالشيء نهي عن','1','692',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (466,'أضداد المأمور به) إلى قوله (فلم [يستقم] الحكم بأن قيام الأمر بالنفس مشروط بقيام النهي). قال الشيخ: أكثر المتكلمين صاروا إلى أن الأمر بالشيء نهي عن ضده، وهو المنصور للقاضي وغيره من الأئمة. وهم إنما يقضون بذلك باعتبار الكلام القائم بالنفس. وما ذكره الإمام عن القاضي، فلم أره له صريحان وإنما قد يشتمل كلامه على ذلك تلويحا.','1','693',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (467,'وأما المعتزلة، فهم متفقون على نفي كلام النفس، صائرون إلى [أن] الكلام هو العبارات، فلم يكونوا أن يقولوا: الأمر بالشيء نهي عن ضده.
فإن الألفاظ مختلفة لا مراء في اختلافها، فقالوا: إنه يقتضيه ويتضمنه، وليس يعنون بذلك إشعارا لغويا، وأمرا لفظيا فقط، فإنه لو كان كذلك، لأمكن أن يأمر بالشيء من لا ينهي عن ضده. ولكن القوم قد قالوا: الأمر: قول القائل لمن دونه: (افعل) مع إدارات. على حسب ما قدمناه من مذاهبهم.
ومريد الشيء عندهم لا بد أن يكون كارها لضده، فلزم أن يكون الآمر بالشيء ناهيا عن ضده، فيكون في اللفظ إشعار بصيغة أخرى، وفي الشرط تلازم باعتبار المعقول. هذا تفصيل مذهب القوم وتحصيله.
ولم يذكر الإمام مستند من قال: إن الأمر بالشيء نهي عن ضده، واقتصر في الرد عليهم على دعوى الضرورة، ونسبة القوم إلى مراغمة البديهة، وأن من خالفه سقطت مكالمته. وزعم أن هذا كاف. وكيف يرد على فحول النظار','1','694',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (468,'وجماهير المتكلمين في أمر استمروا عليه خلفا عن سلف بمثل هذا القول الباطل، والنسبة إلى الجحد الضرورة؟ (٢٤/ب)
ثم إنه حمل عليهم غير ما قالوه، وهو أنه قال: (من زعم أن القول القائم بالنفس الذي يعبر عن بـ (افعل) هو القول الذي يعبر عنه بـ (لا تفعل) سقطت مكالمته). والقوم لا يقولون هذا، وإنما يقولون: القول القائم بالنفس الذي هو (تحرك)، هو القول الذي هو (لا تسكن)، لا أنه القول الذي هو (لا تتحرك).','1','695',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (469,'وقد استدل القاضي على ذلك بما سنذكره. وذلك أنه قال: إذا اتحد الفعل، لم يتصور تعدد الطلب، إذا المحل لا يقبل الامتثال. والقائل إذا قال: (تحرك) فله مطلوب، وهي الحركة. وإذا قال: (لا تسكن)، فله مطلوب، وقد امتنع أن يكون مطلوبه السكون، فإنه هو المنهي عنه. فإذا تعذر أن يكون السكون مطلوبا، وقد تحقق الطلب، وطلب لا مطلوب له محال. وإذا ثبت ذلك، وامتنع أن يكون السكون مطلوبا، وقد تحقق الطلب، وطلب لا مطلوب له محال. وإذا ثبت ذلك، وامتنع أن يكون السكون مطلوبا، ولا درجة بين الحركة والسكون، وجب أن تكون الحكة هي المطلوبة. ولا يصح أن يكون نفي السكون مطلوبا، لا بالنظر إلى حد الحكم، ولا بالنظر إلى نفي تكليف المحال، إما عقلا وإما شرعا، فلا بد أن يكون المطلوب مما يصح فعله. ونفي السكون لا يتصور أن يكون فعلا بحال. نعم، الذي يصح أن يكون فعلا هو الحركة والسكون. وإذا بطل أن يكون','1','696',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (470,'(لا تسكن) طلبا للسكون، وجب أن يكون طلبا للحركة، التي هي ترك السكون.
ولهذا صار المتكلمون إلى أن النهي طلب الترك، والترك فعل على الحقيقة. وقرره أيضا بوجه آخر، وذلك أنه قال: قرب الحركة إلى جهة المغرب، هي بعينها بعد عن المشرق، وشغل الجوهر بحيز انتقل إليه، [عين] تفريغه للحيز المنتقل عنه. كذلك ههنا طلب واحد، هو بالإضافة إلى السكون نهي، وبالإضافة إلى الحركة أمر. هذا تقرير كلامه، وهو في غاية الصعوبة، والانفصال عنه غامض. على ما سننبه عليه في آخر المسألة، إن شاء الله تعالى.
وأما الذي حكاه عن القاضي أخيرا، من أنه ليس عينه، ولكن يقتضيه ويتضمنه. فهذا المذهب ضعيف، ولا صبر له على مأخذ الأدلة، فإنه على هذا الرأي يعترف بتعدد المتعلق، ثم يدعي وجوب الاقتران من جهة العقل بلا برهان. ولكن يمكن أن يكون مستنده في ذلك، من حيث الجملة، الالتفات إلى وجوب الوسائل، وتحريم الموانع، وهو لا يصح منه فعل الحركة، مع مانع السكون، فحرم السكون لأجل ذلك.','1','697',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (471,'وسنتكلم على هذه القواعد بعد هذان إن شاء الله تعالى. وهذا يتنزل على هذا الرأي منزلة الجوهر والعرض، لا يمكن انفصالهما بحال.
وقوله: (إنه يتنزل منزلة الحياة والعلم). ليس كما قال، فإنه لو كان كذلك، لجاز وجود أحدهما دون الآخر. وهذا مما لا يصير إليه القاضي بحال، لأن له مذهبين:
أحدهما- اتحاد الطلب، فكيف على هذا أن يكون آمرا بالشيء من لا (٢٥/أ) ينهي عن ضده؟
والمذهب الثاني- التعدد مع التلازم عقلا، فلا يصح أيضا الانفصال بوجه، فكأن هذا [يشبه] افتقار الجواهر إلى جنس [الأعراض].','1','698',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (472,'[قوله]: (فإن قيام الأمر بالنفس مشروط بقيام النهي). قال الشيخ: إذا ثبت له أن الآمر بالشيء، قد لا تخطر بباله أضداده من وجه، بطل المصير إلى اتحاد الطلب المتعلق بالحركة، الذي [هو] بعينه طلب ترك السكون، وبطل أيضا التلازم من جهة العقل، بين الأمر بالشيء وبين النهي عن ضده.
ولكن [المتكلمين] لا يسلمون هذا، ويقولون: لا يتصور أن يأمر بالحركة من هو ذاهل عن السكون، فإن معقول الحركة: استقرار الجوهر في حيز بعد أن كان مستقرا في غيره، ونفس الاستقرار في الحيز الثاني سكون فيه.
فمن سلم أنه قد يأمر بالحركة من لا يخطر له السكون؟
قال الإمام: (فإذا لاح سقوط المذهبين انبنى عليه ما هو الحق المبين عندنا) إلى آخر المسألة. قال الشيخ: عمدته في المسألة على صحة طلب','1','699',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (473,'الحركة مع الذهول عن السكون. وقد قررنا أن ذلك ممتنع، وحاصله راجع إلى طلب الشيء مع الذهول عنه، وذلك مستحيل. نعم. قد لا تخطر بباله الجهات التي يقع السكون عليها، من نوم أو جلوس أو غيره. فأما الغفلة عن السكون (٥٦/أ) مع طلب الحركة، فمحال.
وأما قوله: (إنه إذا كان ذاكرا للأضداد عند طلب الفعل، فلا يكون قيام','1','700',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (474,'به). فهذا كلام مشكل، فإن هذا الكلام يفهم منه أن يكون الانكفاف عنها ذريعة إلى إيقاع حركة. وقد علم ضرورة أنه ليس بين السكون والحركة واسطة.
وما ذكره [من أن] (في تحقيق المسألة فرض مستحيل). لا يفيد،','1','701',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (475,'فإن القوم إنما وضعوا كلامهم على اتحاد المطلوب، ورد التعبد إلى العبارات. فإذا تحد المطلوب، كيف يتصور فرض التعدد؟ هذا باطل لا شك فيه.
فإن قيل: فما الذي ترونه في ذلك؟ قلنا: الذي نختاره أن الأمر بالشيء لا يكون نهيا عن ضده، ولا يتضمنه. والدليل على ذلك: أن الطالب لحركة القيام، [مقصوده] حصوله، وإن كان لا يتصور حصوله إلا والقعود ينتفي حالة حصول القيام. فالنظر إلى مقصود الطالبين يحقق هذا التقرير.
وما ذكرناه في الاعتراض من أن التكليف لا بد له من متعلق، وانحصار الأمر في فعل الحركة أو نفي السكون إلى آخره، فالقسم صحيحة، وتخيل واسطة بين السكون والحركة محال. وكون السكون مطلوبا، باطل لا شك فيه، ورجوع التكليف إلى نفي السكون غلط، وليس نفي السكون فعلا، حتى يتعلق به التكليف.','1','702',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (476,'ولكن وجه الجواب أن طالب الحركة (٢٥/ب) قد يتفق أن يطلبها من جهة كونها شغلا لحيز، غير الحيز الأول، وقد يطلبها من جهة كونها تفريغا للحيز الأول. فإن طلبها من جهة كونها شغلا لحيز آخر، فقد تضمن ذلك [إثباتا]. فيعبر عن ذلك الطلب بكونه أمرا. فإن طلبت من جهة كونها تفريغا للحيز الأول، عبر عن ذلك بأنه طلب ترك أو نهي، ويفرق بذلك حرف النهي، ليشير إلى أنها طلبت من جهة ما يقاربها من نفي السكون. هذا هو الفرق، فلا يتعلق التكليف إلا بأفعال المكلفين. وقد حد النهي بأنه: طلب [ترك]، والترك فعل على الحقيقة. ولا يتصورها غير هذا.
فأما أن يجعل الترك للسكون ذريعة إلى تحصيل الحركة، فهذا محال، إذا يوجب إثبات واسطة بين الحركة والسكون، كما تقدم تقريره. والالتفات إلى','1','703',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (477,'تعدد الجهات، يوجب التغاير بين متعلق الأمر والنهي، وإن اتحد الفعل. وستأتي مسائل الأمر والنهي على هذه القاعدة. ومن لم يتقن هذا الأصل، تناقضت عليه المسائل، واضطربت القواعد. وعليه تخرج مسألة الصلاة في الدار المغصوبة وغيرها. وبالله التوفيق.

قال الإمام: <span data-type="title" id=toc-101>(مسألة: إذا وقع المأمور به [المقتضى] على حسب الاقتضاء، [جزى] وكفى) </span>إلى آخر المسألة. قال الشيخ: اختلف الناس في أن موافقة الأمر، هل يتضمن الإجزاء؟ وهل لفظ الصحة والإجزاء [مترادفان] أو مختلفان؟ و (الصحيح) عند المتكلمين: عبارة عما وافق','1','704',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (478,'الأمر. والإجزاء: يرجع إلى سقوط القضاء وحصول الاكتفاء. وهذان أمران لا يتلازمان، حتى أن صلاة من ظن أنه متطهر صحيحة (٥٦/ب) عند المتكلم، وإن وجب القضاء.
وعند أكثر الفقهاء أن الصحيح: عبارة عما أجزأ وأسقط القضاء. فالصلاة في الدار المغصوبة صحيحة عند الفقيه، لأنها مجزئة، وفاسدة على رأي المتكلمين.','1','705',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (479,'فعلى ما يقوله المتكلم، تصح الموافقة، ولا يعلم من ذلك امتناع القضاء، ولا تحقق براءة الذمة. وهذا الذي ذكره المتكلمون ظاهر الفساد. فأما إطلاق لفظ الصحة على الفعل الموافق، فلا مشاحة فيه، ولكن المصير إلى أنه يكون موافقا للطلب، ولا تبرأ [به] الذمة، ويتوجه الأمر بالقضاء، فهذا باطل، فإنه إذا لم يتضمن الطلب إلا فعلا واحدا، ولا نسبة بينه وبين فعل آخر [بحال] ن فإذا فعل مقتضى هذا، فقد انقطعت النسبة بدخول المطلوب الوجود، فإنه إذا وجد، لم يبق مطلوبا، ولا نسبة للطلب إلى غيره بحال. فيستحيل أن يبقى على المكلف علقة من ذلك الخطاب.
أما ما تمسكوا به من أن صلاة من ظن أنه متطهر موافقة للطلب، والإجزاء غير حاصل، فهذا وهم وزلل. أما من يذهب إلى أن المصيب واحد، فإنه لا','1','706',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (480,'يقطع بأنه مأمور به عند ظن الطهارة، بل يظن أيضا أن الفعل إذا (٢٦/ب) أتي به، فإذا تبين له الحدث، تبين أنه لم يأت بالمطلوب.
وأما من ذهب إلى أن كل مجتهد نصيب، فهو عندما ظن الطهارة، قطع بوجوب الصلاة عليه، بضرورة حاله، فإذا تبين له الحدث بعد ذلك لم يخرج الفعل الواقع عنده عن أن يكون طاعة. ولكن يعلم في الحال أن الخطاب الذي توجه عليه بفعل الصلاة بالطهارة لم يمتثل، وكأ التكليف سقط عنه، لضرورة عجزه، فلا يكون ممتثلا له على وجهه، ثم وجب عليه تداركه بعد فعله على شرطه. وسنبين أن القضاء إنما يثبت لاستدراك مصلحة لم تحصل من الطلب الأول.','1','707',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (481,'وقد تمسكوا أيضا بالمضي في فاسد الحج، فإنه إذا أفسد [أمر] بالتمادي وبالقضاء جميعا. فقد امتثل ما أمر به على وجهه، وقد أمر بقضائه. وهذا زلل بين، ونحن نقول: ما حصل فيه الامتثال من غير إخلال، فلا سبيل إلى قضائه بحال. والحج الذي خاض فيه- إن [كان] [حجة] الإسلام- فالكلام المأتي به امتثالا لذلك الطلب، ويجب التمادي بخطاب جديد، ويبقى عليه حق القيام بالطلب الأول.
وإن كان تطوعان فقد تعين إتمامه بالشروع. فإذا أفسده، وجب التمادي والقضاء جميعا بخطاب جديد. ولا مانع من تقدير ذلك، وطلب دليلهما من فن الفقه لا من الأصول. (وحظ الأصول تقدير أمر جديد في كل ما لا يتلقى من الأمر الأول).
ومعنى قوله: (وقد يعتاص على الفقيه الفرق بين الفساد والفوات','1','708',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (482,'[والتحلل] بعد الإحصار). ومقصود هذا الكلام أن الجميع اشتركوا في عدم الإتيان بالعمل [على وجهه]، ثم اختلفت أحكامهم. فالمفسد متماد على العمل، ومن فاته الحج تحلل بعمرة، والمحصر بغير عمرة، والمفسد ومن فاته الحج يقضيان. والمتحلل بعذر الإحصار يتحلل، ولا قضاء عليه في حج التطوع عند مالك، ولا في الفرض عند عبد الملك.
فقيل الإمام: ما سبب اختلاف هذه (٥٧/أ) الأحكام في الأمر بالقضاء وسقوطه، مع التساوي في أن العبادة لم يؤت بها على وجهها؟ قال: هذا أمر [لا يتلقى من فن الأصول]، وإنما يطلب فيه أدله الشرع في الفروع.
قال الإمام: (الأمر بالشيء يتضمن اقتضاء ما يفتقر المأمور إليه في وقوعه','1','709',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (483,'شرعا) إلى الفصل. قال الشيخ: اختلف الناس فيما لا يتوصل إلى الواجب إلا به من الأمور الاعتيادية أو العقلية، هل يتصل بالوجوب؟ أما ما لا يتوصل إلى الواجب إلا به من [الشروط] الشرعية، فلا خلاف أن إيجاب [المشروط] عين إيجاب الشرط أو يتضمنه. فإذا (٢٦/ب) قلنا يتضمنه، فعلى أي وجه يقع الضمن؟ وما معناه؟ فهما مسألتان نتكلم على كل واحدة منهما.
أما المسألة الأولى: وهي الشرط من جهة العادة أو العقل، فقد قال قائلون: ما لا يتوصل إلى الواجب [إلا] به، فهو واجب. وزاد أبو حامد','1','710',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (484,'قيدًا احترز به عن نقوض توهمها فقال: ما لا يتوصل إلى الواجب إلا به، وهو فعل المكلف، فهو واجب. قال: لأنه لا يتوصل إلى الكتابة إلا باليد، ولا إلى المشي إلا بالرجل، ولا تكون اليد والرجل واجبتين، فافتقر إلى أن يقول: وهو فعل المكلف. وهذا وإن كان كذلك، فهو مقصود القوم، وإن كان في اللفظ نقص.
وقال قائلون: يجب التوصل إلى الواجب بما ليس واجبا، وهذا ضعيف، فإنه تناقض بين في قولهم: يجب ما ليس واجبا. فنضرب عن تعدد العبارات بعد فهم المقصود. ولنذكر عمدة من ذهب إلى وجوب الوسائل على الضبط المذكور.','1','711',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (485,'قالوا: إذا استحال تكليف المحال، فإنما يطلب الفعل بصفة الإمكان، فكأنه قال له: اغتسل غسلا ممكنا، والغسل الممكن في الوجه هو الذي يدخل فيه جزء من الرأس، فليكن ذلك القدر واجبا. وعلى هذا [التقدير] يثبت ذلك بمقتضى الطلب الأول، ولا يفتقر إلى طلب آخر.
قالوا: وهذا الوصف- وإن لم يكن مذكورا- فلا بد منه، حذرا من تكليف ما لا يطاق. ونحن نقول: وإن كان كذلك، إلا أنا نلتفت إلى تعدد الجهات ومقاصد [الطالبين]. كما سبق الكلام عليه في مسألة الأمر بالشيء، هل يكون نهيا عن ضده؟
وإذا تقرر ذلك الأصل، فنسلك هذا المسلك ههنا، ونقول: طالب غسل الوجه إنما طلب غسله، وقد يتيقن- في حال الأمر بغسله- أن لا يخطر له أخذ شيء من الرأس، ولا الحاجة إليه. وإذا أمكن ذلك، فكيف نقول: لا بد أن يطلب مع تصور غفلته عنه؟ وإن ذكره، فلا رأي له فيه بحال.','1','712',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (486,'فإن قيل: هذا الذي تمسك به الإمام في أن الأمر بالشيء لا يكون نهيا ع ضده، وقد أنكرتموه. قلنا: أنكره القاضي لإنكاره، ونحن إنما أنكرنا أن يكون ترك السكون وسيلة إلى فعل الحركة، لما فهم من هذا الكلام من واسطة بين الحركة والسكون. نعم، لو حرم الوسيلة العقلية أو العادية، لكان تكليف ما لا يستطاع، وذلك محال [عقلا عند قوم]، شرعا عند آخرين. فالمراتب إذا ثلاث:
أحدها- أن تتعرض الأدلة الشرعية للوسائل بالطلب. فهذا واضح أنه مطلوب.
والثانية- أن يقدر النهي عنها، فهذا يمنع التكليف.
والثالثة- أن تفقد الدلالة على الطلب والزجر جميعا. فهذا (٢٧/أ) موضع الخلاف. والذي نختاره أنه لا يثبت الطلب، نظرا إلى تعدد الجهات، ومقصود (٥٧/ب) الطالبين.
وقوله: (ويظهر أثر ذلك بأن الصائم يخص بنيته اليوم دون أخذ طرفين من الليلتين). قال الشيخ: بهذا تسمك الإمام، ولكن لا يقوى التمسك به،','1','713',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (487,'لأصل نمهده، وهذا كثير التداور، عظيم الفائدة. وهو أن ما طلبه الشرع، قد لا يفهم من طلبه غرض عاجل، لغلبة التعبد عليه، فهذا يقع طاعة بالنية المشروطة في هذا النوع من المأمورات.
وإن ظهر له غرض عاجل، فلا يفتقر إلى نية في الاجتزاء به، ولكنه إنما يثاب عليه إذا قصد التقرب به، إلا فيما شذ من مسائل، فإن الثواب حاصل، وإن كان القصد [مفقودا]، كمسألة النظر الأول. وسيأتي لهذا تقرير حسن في موضعه، إن شاء الله تعالى.
فإذا تحقق ذلك، فأخذ طرفين من الليلتين، قد ظهر له مقصد عاجل، [وهو] الإتيان بالمطلوب بكماله. فإذا ظهر هذا المقصود، استغنى ع النية، ويصير كرد الغصوب والودائع، وكإزالة النجاسة، عند بعض','1','714',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (488,'العلماء. فلا يقوى التمسك بهذا الطريق في نفي الوجوب، والمعتمد على ما ذكرنا.
[المسألة] الثانية: الشروط الشرعية التي يلفي كونها شرطا شرعا، وهذا بمثابة الطهارة في الصلاة وغيرها من الواجبات. وصورة المسألة: ما إذا قال الشارع: (لا صلاة إلا بطهور). ولم يوجب الطهارة إيجابا يخصها، [ويوجب] الصلاة، فهل يفهم من هذا إيجاب الطهارة أم لا؟ وإذا فهم إيجاب الطهارة، فهل إيجاب الصلاة عين إيجاب الطهارة، أو تجب الطهارة بإيجاب آخر؟ هذا مما اختلف فيه.','1','715',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (489,'أما الأول، وهو فهم وجوب الطهارة، فلا شك فيه، وطريق تقريره: أن الواجب: هو الذي يذم تاركا شرعا. على ما سيأتي تحريره بعد ذلك. وبعد هذا الخطاب لا تقبل الصلاة من المصلي إلا بالطهارة، فإذا لم يفعلها، كان ملوما، وما سبب ذلك إلا فقدان الطهارة، فتحقق فيها حد الواجب. وأما كونه بذلك الطلب أو بطلب آخر يقع ضمنا؟ فالصحيح أنه بطلب آخر.
وتحقيقه أن نقول: مفهوم الألفاظ يستقر في النفوس قبل النظر في الأمر والنهي، ولا يكون تلعق الطلب بالمسمى بغير التسمية على حال. وإذا كان كذلكن فمسمى الصلاة في لسان الشرع: أفعال مخصوصة، وليست الطهارة من','1','716',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (490,'مسمى الصلاة، فوجوب الصلاة لا يوجب اتساع التسمية وتغير مدلولها. فعقل من هذا وجوب الصلاة خاصة، ومن عدم اكتفاء الشرع بفعلها وجوب أمر آخر يتصل بها، فلا يكون ذلك الأمر من جملة مسمى الصلاة، ولا يكون أيضا من إشعار اللغة، ولكنه يكون في لسان الشرع، فشابه المفهوم اللغوي. فإن قاعدته أن [اللغوي] (٢٧/ب) أشعرنا بأنه [متى] خص شيئا بالذكر على جهة، فهو يبغي قصر الحكم عليه، ونفيه عما سواه، فيستدل بالمذكور نطقا على أنه قام بنفسه [مذكور] آخر، لم يعبر عنه بعبارة صريحة، فسمى لذلك مفهوما.
وكذلك إذا قال الشرع: الطهارة شرط في صحة الصلاة، فقد قال: لا أقبلها إلا بها. ولم يأمر الشرع بصلاة فاسدة، فلزم أن يكون ذلك يتضمن [طلبا] آخر [مشروطا] في هذه العبادة. (٥٨/أ)، فوجب الاعتراف بأن إيجاب المشروط يتضمن إيجاب شرطه الشرعي. والله المستعان.
قال الإمام: (ونحن الآن نذكر الصيغة المقيدة، [ووجوه] التقييد) إلى آخر المسألة. قال الشيخ: قول الأصوليين: صيغة مطلقة، يدل ظاهره على','1','717',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (491,'الإطلاق من سائر قيوم المقال والمحال، وذلك محال، فلا تتصور أبدا صيغة مطلقة بهذا الاعتبار. فإن الناطق لا بد أن يكون على حاله سهو أو ضده، فقرينة السهو والغفلة تمنع أن يكون اللفظ مقصود الدلالة. (فإذا لا تلفى صيغة أبدا على حقيقة الإطلاق). وإذا كانت القرائن لا بد منها، ولا يمكن أيضا النزاع في مقصود المتكلم بلفظه عند ظهور قرائن أحواله، فيفتقر إلى معرفة محل','1','718',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (492,'الخلاف بين الأصوليين. وما المقصود بقولهم: الصيغة المطلقة والمقيدة، مع استحالة الإطلاق العام ووجوب التقييد في كل حال؟ فنقول: مرادهم بذلك إطلاق من بعض الوجوه، وهو أن يبين، إما على وجه مقطوع به، أو على وجه مظنون، أن المتكلم أورد اللفظ قاصدا به الدلالة على الموضوع اللغوي بالإضافة، غير حاكٍ لذلك عن غيره، فهذه هي الصورة التي يعبر عنها الأصوليون بكونها (مطلقة) على ما بيناه. فيرجع البحث إلى المفهوم اللغوي [ما هو]؟','1','719',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (493,'وأما إذا التبست القرائن على الإطلاق، فلا يتصور إلا الوقف، كما ذهب إليه القاضي. وإذا ثبتت القرينة المعينة لمقصود المتكلم، فلا وقف في فهم كلامه، وإن بقي القاضي وأصحابه على الوقف في مدلول الصيغة، بالإضافة إلى أصل الوضع، كما تقدم الكلام عليه. والصورة الثالثة في هذه التي شرحناها، وهي الصورة المطلقة في عرف الأصوليين.
قال الإمام: (ما ثبت فيه الخطر ثم وردت فيه صيغة الأمر) إلى آخر','1','720',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (494,'المسألة. قال الشيخ: الصحيح أن (افعل) بعد تقدم الحظر باقية على الدلالة على الطلب، كما تقررناه، وهو اختيار القاضي. والوجه فيه ما قررناه من أن تقدم الحظر ليس من القيود الحالية، ولا المقالية، فإن القيود المقالية: ألفاظ تقترن بالصيغة تبين مقصود المتكلم بها، كقوله: (افعل حتما أو ندبا أو إباحة). وليس تقدم الحظر من هذا الجنس. وأيضا فإنا ما اعتمدنا في أنها تدل على الطلب عند أهل (٢٨/أ) اللغة، إلا من جهة كونها فعل أمر بالإضافة إلى أصل الصناعة. وهذه الطريقة موجودة، وإن تقدم الحظر، فلا ريب ولا إشكال. وأهل اللغة يتسامحون بإطلاق لفظ الطلب على الإباحة، ولكن عند قيام دليل يدل عليه.
وما ذكره من التقسيم السابق من كونها للطلب، موجود في هذا المكان','1','721',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (495,'بعينه، فلا حاجة إلى إعادته. هذا هو الصواب عندي، ولمذهب القاضي الثاني وجه، وهو كونها تبقى دالة على الطلب، لكن على وجه أضعف مما','1','722',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (496,'كانت عليه قبل ذلك. ولا يظهر لهذا المذهب مستند إلا الاستقراء، وهو أنها صودفت في الكتاب والسنة دالة على رفع الحظر السابق، كقوله: {وإذا حللتم فاصطادوا}. ولا يجب الاصطياد. وكذلك قوله: {[فإذا] قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله}. (٥٨/ب) وقوله - صلى الله عليه وسلم -: (قد كنت نهيتكم عن الانتباذ فانتبذوا، وكل مسكر حرام). وكذلك قوله: (كنت نهيتكم','1','723',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (497,'عن ادخار لحوم الأضاحي، فالآن ادخروها). فهذه المواضع وردت فيها الصيغة، وفهم منها رفع الحجر السابق. فهذا هو خيال من فرق بين أن يكون الحظر مطلقا، وبين أن يكون محدودا إلى غاية علق زواله على صيغة (افعل).
ولكن ينقدح في هذه الآي والأخبار أن فهم الإباحة لم يقتصر على محض سبق الحظر، ولكن دلت الدلالة على الإباحة، واللفظ ليس بنص، فترك ظاهر الدليل. والأدلة واضحة في نفي وجوب الاصطياد والبيع والانتشار وادخار اللحوم، فإلى هذه الأدلة استند في الوجوب، لا إلى سبق تقدم الحظر.
وما ذكره الأستاذ من اتفاق الأصوليين على مسألة سبق الطلب إذا ورد الحظر بعد ذلك، وادعاؤه الوفاق. له وجه ظاهر، فإن هؤلاء الفقهاء إنما منعهم من الجريان على ظاهر اللفظ، ما ذكرناه من آي الكتاب ومن السنة، الدالة على رفع الحظر والحرج السابق، وهذا مفقود فيما إذا وردت صيغة','1','724',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (498,'الحظر بعد سبق الطلب، فبقيت دالة عليه. وقد بينا أن الوجه في المسألة الأخرى أيكون الأمر كذلك.

قال الإمام: <span data-type="title" id=toc-104>(مسألة: الصيغة إذا تضمنت فعلا مؤقتا) </span>إلى آخر قوله (ولا سبيل إلى إثبات ذلك). قال الشيخ [أيده الله]: ما ذكره الإمام من أن بعض الفقهاء ذهب إلى أن القضاء متلقى من الأمر الأول، [هو] كما قال.','1','725',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (499,'والرد عليهم واضح، لا إشكال فيه.
ونزيده تقريرا وتقريبا، فنقول: إن [الفهم] متعلق الخطاب قبل الشروع في العمل، فإذا تعلق الطلب بفعل مخصوص، لم يكن له تعلق بغيره. فلو تعذر الفعل المطلوب، لم يصح للمكلف أن يفعل غير ذلك الفعل الذي لا يتعلق به','1','726',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (500,'الطلب، لأجل فوات المطلوب، وهو بمثابة ما لو طلبت منه الصلاة فتعذر عليه (٢٨/ب) فعلها، فلا يصح أن يأتي بالصوم بدلا منها، اقتصارا على الخطاب الأول. وكذلك إذا طلبت الصلاة على وجه مخصوص، كما إذا قال: ليصل قائما، فتعذر عليه القيام، فلا يتمكن بهذا الخطاب خاصة أن يصلي على غير تلك الجهة. وهذا أيضا واضح.
وكذلك إذا أمر بالصلاة إلى الكعبة، فتعذر عليه الاستقبال، وقدر الاقتصار على ذلك الخطاب، ولم يرد سواه، امتنع عليه أن يصلي إلى غير القبلة. وكذلك القول في الطهارة وستر العورة وغيره.
وكذلك إذا قال له: صم يوم الاثنين مثلا، فما تعلق الخطاب بالصوم إلا مضافا إلى يوم الاثنين، فليس له تعلق به، بالإضافة إلى يوم الأحد والثلاثاء. إذا تقاعد الخطاب عنهما جميعا تقاعدا واحدا. فإذا لم يرتبط الطلب بالفعل مطلقا، ولم يرد خطاب جديد يتضمن الفعل، امتنع','1','727',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (501,'إيقاعه امتثالا.
وإلى هذا الحرف أشار الأستاذ أبو إسحاق فإنه قال: إنما يملك من منافع الدار وغيرها بالإجارة ما كان بينه وبين العقد نسبة. وإذا استأجر الدار (٥٩/أ) شهرا بعينه، فإنما ملك من المنافع ما يكون الشهر المعين ظرفا لها ووعاء. وليس بين العقد وبين منافع تضاف إلى شهر آخر نسبة بحال. فإذا فات الشهر، فليس إلا الحكم بفوات متعلق العقد، فيجب فسخه.
وهذا كلام صحيح. والأمة مجمعة على امتناع تقديم العبادة على وقتها، إذا لم يقم دليل ذلك. فكذلك يجب القضاء في التأخير عن وقت الفعل الذي أضافه الشرع إليه. وهذا واضح لا خفاء به. والمسلك الأول لهم غير صحيح، والرد عليهم كما ذكره الإمام، [ونزيده] زيادة تحققها، وهو أنه إذا وقع الاعتراف بتقاعد اللفظ عن تناول الفعل في غير ذلك الزمان، وتحقق انتفاء طلب آخر، وسلم أن الفعل لا يكون طاعة إلا إذا كان مطلوبا، فكيف','1','728',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (502,'يتصور مع ذلك نزاع؟
وأما قول الإمام: ([وجه] تسميته قضاء عند [فرض] أمر جديد، من جهة مضاهاة الواقع آخرا لما استدعي أولا). فهذا الكلام ضعيف، ولو كان كذلك، لكانت كل عبادة سبق طلب مثلها قضاء، وليس الأمر كذلك،','1','729',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (503,'بل القضاء: عبارة عما فعل بعد خروج وقته، استدراكا لمصلحة أدائه الفائتة على المكلف. فلنذكر في هذا الباب حقيقة القضاء والأداء والإعادة، ووجه التسمية، وانقسام الأمر في الألفاظ إلى الحقيقة والمجاز، وبيان سبب القضاء.
فالأداء: عبارة عن الفعل في وقته ابتداء، على حكم الامتثال والموافقة.
والإعادة: عبارة ع الفعل المطلوب ثانيا في وقته، لجبر خلل في الفعل الأول. وذلك الخلل قد يخل بالإجزاء، وقد (٢٩/أ) يخلل بالكمال، فإن أخل بالإجزاء، وجبت الإعادة، [إن] كان مطلوبا [طلبا] على','1','730',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (504,'الوجوب. وإن أخل بالكمال، استحبت الإعادة. والاسم مقصور على تفاوت الفعل.
والقضاء: عبارة [عما] فعل بعد خروج وقته، استدراكا لمصلحة أدائه على المكلف، إما لعذر أو لغير عذر. وهل إذا غلب على ظن المكلف في الواجب الموسع المنضبط آخر وقته كالصلاة، أو غير المنضبط كالحج، والهلاك إلى وقت، وأمر بالفعل قبله فلم يفعل، ثم لم يمت ووفق للعمل، فهل ينوي الأداء أو الاقتضاء؟ فقد أشرنا فيما سبق إلى خلاف فيه، وحققنا أنه مؤدين نظرا إلى الاكتفاء بالطلب الأول، وافتقار القضاء إلى طلب جديد. وكذلك لزمه قضاء صلاة على الفور فلم يفعل، ثم فعل فيما بعد، فلا نقول إنه قضاء القضاء.
وإذا كان القضاء إنما يشرع استدراكا لمصلحة الأداء، فللأداء ثلاثة أحوال:
أحدها- أن يجب الأداء، ولا يكون كذلك، إلا إذا جرى السبب ووجد الشرط، ثم لم يتفق الفعل. فهذا إذا شرع في حقه القضاء لاستدراك مصلحة','1','731',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (505,'الأداء، فهذه حقيقة في أعلى [درجات] القضاء. وهذا كمن ترك الصلاة عمدا. قال أبو حامد: وكذلك إذا تركها سهوا، فإنه يقضي حقيقة. وهذا تناقض بين منه، فإنه يذهب إلى استحالة تكليف النائم والناسي. وهو يقول: لا يكون القضاء حقيقة إلا إذا كان الأداء لازمان فالنوم ينافي التكليف على زعمه عقلا، فما كان ينبغي أن يكون قاضيا إلا مجازا.
الحالة الثانية: أن لا يجب [الأداء، بل يمتنع منه، كالصوم في حق الحائض، فإنها لو صامت عصت، فتسمية صومها قضاء مجاز محض]، وحقيقته: (٥٩/ب) أنه فرض مبتدأ .. ووجه التحوز: أنه لما كان الأمر سببه حالة عرضت منعت من الصوم، حتى فات بسببها التكليف، سمي ذلك قضاء.','1','732',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (506,'والصحيح أنه أداء.
وقد توهم بعض الفقهاء من هذا أن صوم أيام الحيض واجب على الحائض، ولكن الحيض يمنع من الفعل. قالوا: بدليل وجوب القضاء عليها.
وهذا غلط عظيم. وكيف يصح أن يقال إن الصوم واجب عليها، وهي لو صامت عصت؟ وليس الحيض كالحدث، فإن إزالته ممكنة، فيكون المكلف متمكنا من فعل ما كلف به بعد تحصيل شرطه الداخل تحت قدرته. وليس الحيض كذلك، إذا إزالته غير ممكنة. وإنما اغتر هؤلاء بإطلاق كونها تقضي، وجعل هذا','1','733',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (507,'الاسم مجازا أولى من خلاف الإجماع، وتسويغ تكليف ما لا يطاق، وإيجاب الفعل مع تحريم إيقاعه.
فإن قيل: فلم تنوي قضاء رمضان؟ قلنا: بمعنى أن سبب وجوبه حالة عرضالبلوغ القضاءإن عنيت أنه قضاء، لما وجب عليها في حالة الحيض من صيام أيام الحيض، فخطأ أو محال.
فإن قيل: فيلزم أن ينوي الصبي بعد البلوغ القضاء، لما فات من الصيام بالصبا. قلنا: لم نجعل فوات العبادة بسبب (٢٩/ب) الصبا، سببا لوجوب القضاء لما فات في زمان الصبا بخلاف الحيض.
ولننبه على سر يعتمد عليه في الأصول والفروع جميعا، وهو أن فوات المخاطبة بالعبادة، إما أن يكون لفوات السبب، لم يشرع الشرع التدارك، كما في الصبي، فإن السبب مفقود. وإن فات التكليف لأجل فوات الشروط مع حصول السبب فههنا قد شرع الشرع القضاء، كما في حق الحائض. فإن السبب الطاقة مع شهود الشهر، وقد وجد ذلك في حق الحائض، لكن فات الشرط، وهو الطهر، فورد الشرع بالقضاء، استدراكا لمصلحة السبب الموجود، ولا سبب في حق الصبي، إذا الطاقة مفقودة.
فإن قيل: إذا كان السبب الطاقة ووجود الشهر، فينبغي إذا قارب البلوغ وأطاق، أن يؤمر بالقضاء بعد البلوغ من ذلك الوقت. فنقول لما كانت','1','734',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (508,'الطاقة والعقل حقيقتين تختلف فيهما الناس اختلافا ظاهرا، حجز الشرع الخلق عن مظنة في هذه الأمور المضطربات، وناط التكليف بالبلوغ، إما الاحتلام أو بالسن. كما ناط القصر بسفر طويل مقدر بمرحلتين، أو يوم وليلة. وكذلك فعل في نصاب السرقة ونصاب الزكاة. وسيأتي لهذا مزيد تقرير في موضعه، إن شاء الله تعالى.
وقد اختلف الفقهاء في مسائل تتعلق بالقضاء، لترددهم في أن اللفظ شرطا لقدان السبب أو لتخلف الشرط؟ فمن ذلك اختلافهم في قضاء المجنون الصوم. فقال بعضهم: يقضي الصوم، كما تقضي الحائض. وهو المشهور من مذهب مالك رضي الله عنه. وقال قائلون: لا يقضي اعتبارا بالصبي. وقال قائلون:','1','735',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (509,'إن بلغ مجنونا، لم يقض. وإن بلغ صحيحا ثم جن، قضى. وقال قائلون: إن قلت السنون، قضى اعتبارا بقضاء الحائض الصوم، وإن كثرت، لم يقض، اعتبارا بترك قضاء الحائض الصلاة. والقول الأول أشهر عندنا، لأن الطاقة في حقه موجودة، وشهود الشهر أيضا كذلك. فكان امتناع التكليف بفوات شرط التكليف، وهو عقل. وهذا هو الذي اختاره المتكلمون، فإنهم جعلوا العقل شرطا في توجه التكليف.
واعلم أن (٦٠/أ) الناس اختلفوا، هل من فرق بين السبب والشرط؟ فذهب الأستاذ أبو إسحاق إلى أنه لا فرق بينهما، وأن الحكم يتوقف على الجميع. وقال الأكثرون بفرق بينهما، وعلى ذلك ينبني إثبات الرخص في الشريعة ونفيها، وإثبات الموانع وغيره. والأستاذ ينكر ذلك كله، ويقول: جميع ما يتوقف الحكم عليه [يكون] جزءا من السبب. ولذلك منع مانعون من','1','736',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (510,'تخصيص العلة (٣٠/أ) على كل حال. وسيأتي مزيد بيان في باب النقض، إن شاء الله تعالى. فإذا اخترنا الفرق بين السبب والشرط، فطريق معرفة الفرق [ثبوت] مناسبة الاقتضاء، من غير ظهور مناسبة للشرط في الثبوت. والذي يناسب التكليف الطاقة.
وأما العقل، فيناسب معرفة الحكم حتى يتصور الامتثال. هذا هو السبب الذي لأجله ثبت القضاء عند فوات بعض الأعمال وانتفائه، وهو أصل حسن في الشريعة.
الحالة [الثالثة]: حالة المريض والمسافر إذا لم يجب تضييقا، ولكنهما إن صاما، فقد فعلا الواجب، ونعني إذا كان الصوم يضر بالمريض ولا يهلكه، فقد أبيح له الفطر، ولم يحتم عليه، فهو كالمسافر، فإنهما إن صاما امتثلا، فإن لم يفعلا، وقضيا بعد ذلك، فهل اسم القضاء يطلق عليهما حقيقة أو مجازا؟ والصحيح أنه مجاز، لثبوت التخيير، فلم يفت وقت الوجوب، بدليل أنهما غير مفتقرين إلى خطاب جديد، وهو ضابط القضاء الحقيقي.
وقوله تعالى: {فمن كان منكم مريضا أو على سفر فعدة من أيام أخر}. محتمل أن يقال: إنه [حقيقة]، لأنه لو فعله في وقته لصح منه، فإخراج عن مظنة إيجابه، يوهم كونه قضاء حقيقة، والصحيح أنه أداء. وقد','1','737',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (511,'خير الله المريض والمسافر بين صوم رمضان، [أو أيام من] غيره، فهو كالواجب المخير. ومن فعل إحدى خصال الكفارة، لم يكن قاضيا. وهذا أيضا يضعف إطلاق القضاء عليه مجازا. ولكن لما كان التدارك في هذه الصورة لا يمكن إلا بعد فوات الزمان الأول، [أشبه] ذلك العبادة بعد خروج وقتها. فلذلك أطلق عليها لفظ القضاء مجازا. ولكن مقتضى هذا أن تسمى الصلاة في آخر الوقت قضاء مجازا، لأنه لا يمكن إيقاعها آخر الوقت، إلا إذا لم تفعل في أوله. لكن [إيجاب] عليه بأن لرمضان خصوص نسبة إلى الصوم ليست شوال ولا لغيره، بدليل أن الصبي المسافر لو بلغ في أثناء رمضان، لزمه صوم ما بقي منه، ولو بلغ بعد مضيه، لم يلزمه شيء، دل ذلك على قبول اختصاص رمضان بالصيام، وليست السنة كلها للصيام، كوقت الصلاة المتسع. فإن الصبي إذا بلغ في أثناء وقت الصلاة لزمته. وإن بلغ بعد خروج رمضان، لم يلزمه. هذا إذا قلنا إن المسافر مخير بين الإفطار','1','738',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (512,'والصيام، فالأظهر أن تسمية صومه قضاء مجاز محض.
وقد قال قائلون: إن فرضه أيام أخر، ولكن إن صام رمضان، فهو معجل للواجب. فلا يتأتى على هذا أن يكون صومه قضاء بحال. وهذا ضعيف، لأن الآية لا تفهم الترخص للمشقة. ويظهر ذلك من قوله: {شهر رمضان الذي أنزل فيه القرآن هدى للناس وبينات من الهدى والفرقان}. فبين فيه شرفية الشهر أولا، ثم عقب ذلك بذكر صيامه، ففهم (٦٠/ب) أن ذلك لأجل شرفه، ورخص للمسافر لمشقته في تأخير الصوم عن وقته، لأجل عذره. وهذا الذي ذكرناه (٣٠ ـ/ب) مذهب الكرخي.
وذهب أهل الظاهر إلى أن المسافر لا يجزئه صوم رمضان، وهو مذهب بعض التابعين. وهو ضعيف، لأن مساق الآية يفهم الإضمار، بمثابة قوله: {وسئل القرية}. والتقدير: من غيرن منكم مريضا أو على سفر فأفطر. وأيضا فإن (أصحاب رسول الله صلى الله عليه وسلم كانوا يصومون ويفطرون في السفر من غير','1','739',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (513,'نكير من بعضهم على بعض).
الحالة الرابعة: حالة المريض الذي يخشى الهلاك على نفسه، لا يحل له الصوم، فإن صام وسلم فقد عصى. وهل يكون مؤديا للواجب؟ فيه نظر. ذهب بعض الفقهاء إلى أنه لا يجزئه الصوم، وجعلوه بمثابة صوم يوم النحر الذي لا ينعقد عند بعض الفقهاء، وهو مالك والشافعي رضي الله عنهما، وأبو حنيفة حكم بانعقاده. فلا يخفى حكمه بصحة صوم المريض.
ومن العلماء من فرق بينهما، فأبطل صوم يوم النحر، فإنه مأمور بالفطر لإجابة دعوة الله تعالى إلى أكل القرابين، فكيف يقال له أجب الدعوة، أي كل وصم؟ هذا متناقض. وفي المريض قيل له: لا تهلك نفسك، فيصح أن يقال له: صم مع ذلك. وقد يقال في المريض: لا تهلك فسك بالصوم، فكيف يقال له مع هذا: صم؟
قال أبو حامد: ويعسر الفرق بينهما جدا. والصحيح عندنا هو الفرق والقضاء ببطلان صوم يوم النحر، وصحة صوم المريض، فإنه لم يتعرض في','1','740',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (514,'حق المريض لخصوصية الصوم. وإنما تعلق النهي بجنس المضرة. حتى قد يمتنع الأكل إذا كان مضرا، فيصير بمنزلة النهي عن الصلاة في الدار المغصوبة، لانحراف النهي عن خصوصية الصلاة إلى التصرف في ملك الغير بغير إذنه، بخلاف صوم يوم النحر، فإن النهي تعرض له من جهة كونه صوما.
ولم يصح عندنا انصراف النهي إلى جهة غير الصوم. وهذا واضح. والله المستعان.

قال الإمام: <span data-type="title" id=toc-108>(مسألة: الأمر بالشيء من أشياء، إذا كان محمولا على الوجوب) </span>إلى آخر المسألة. قال الشيخ هذه المسألة خلاف المعتزلة فيها من جهة المعنى مشهور، وفي كتب الأئمة مسطور، وقواعدهم على منعها تدور.','1','741',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (515,'ورد الخيرة في التعيين إلى المكلف عندهم غير معقول. ونحن بعون الله وتأييده نتكلم على أًصل المسألة، ثم نبين قواعد المعتزلة، ثم نتعقبها بالأبطال.
ونوضح الغرض بثلاثة أمور: أحدها- تصور المسألة من جهة المعقول. والثاني- ثبوت وقوعها في الشرع المنقول. والثالث- توجه النقوض التي أجمع عليها أهل الإسلام، وساعد المعتزلة عليها من غير منع ولا إنكار.
أما الطريق العقلي: فهو أن الآمر إذا قال لمأموره: أوجبت عليك خياطة هذا الثوب، أو بناء هذا الحائط في هذا اليوم، كان هذا كلاماً (٣١/أ) معقولا، وأمرا متصورا، وصرح له مع ذلك: بأني لم أوجب عليك الجميع، ولا مكنتك (٦١/أ) من ترك الجميع، ومهما فعلت، فقد امتثلت إيجابي. فلا يخلو- بعد ورود هذا الخطاب، وتصور الطلب، وفهم المخاطب- إما أن يقال: لم يوجب شيئا أصلا، وهو محال، لأنه نقيض ما صرح به، أو يقال: أوجب الجميع، وهو أيضا باطل، لأنه صرح بضده، أو أوجب معينا، وهو قد صرح بالتخيير وعدم','1','742',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (516,'التعيين، فلم يبق إلا أن يقال: أوجب مخيرا. ويدل عليه من جهة الشرع ثلاثة أمور:
أحدها: النص الصريح، وهو قوله تعالى: {فكفارته إطعام عشرة مساكين من أوسط ما تطعمون أهليكم أو كسوتهم أو تحرير رقبة}. ولفظ (أو) لا يتحمل الجميع بحال عند أهل اللسان.
الثاني: الإجماع على أنه لا يجب على الحانث فعل الخصال الثلاث، وأن الواجب عليه [أحدها] لا بعينه.
الثالث: أنه كان يلزم من مساق ما ذكروه م منع الخيرة في الواجب، أن يجب إعتاق العبيد، وإطعام جميع مساكين العالم، جميع أنواع الأطعمة، وكسوة جميع [الثياب]، حذرا من تطرق الخيرة إلى الواجب. وإذا اعترف الخصم بتحقق الوجوب، مع ثبوت الخيرة في التعيين في بعض الأحايين، فقد اعترف بالمسألة، وكفى المؤنة.','1','743',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (517,'وقد تمسك المعتزلة بطرفين: أحدهما- يتلقى من تصور الطلب. والثاني- ينبي على قواعدهم.
أما الأول: فقد قالوا: الوجوب طلب محتم، والخيرة ضد ذلك، فكيف يتصور أن يكون واجبا مخيرا؟ وحاصله راجع إلى أنه مطلوب غير مطلوب، وذلك غير معقول. قالوا: فإذا تعلق الطلب بخصلة، فإما أن يتعلق بها من جهة خصوصيتها، فتجب على انفرادها، وإما أن يتعلق بها من جهة تشمل بقية الخصال، فينتفي وجوب الجميع. هذا تقرير هذا الطريق المتلقي من مضادة الوجوب التخيير. والجواب عنه من الأوجه المتقدمة العقلية والنقلية.
ونخص هذا الكلام بجواب يتعرض البيان تعدد الجهات. فنقول: تعلق الطلب بالخصلة من جهة كونها خصلة مفردة، فقيل له: افعل خصلة من هذه','1','744',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (518,'الخصال، كما تقول المرأة لوليها: زوجني من أحد الخاطبين، والخيرة في التعيين إليك. ويقال: بايع أحد الإمامين الصالحين للإمامة. ولا يتصور في هذه الصورة أن يقال: جعلت له تزويجا منهما جميعا، فإن ذلك لا يصح شرعا. [وكذلك] يمنع عقد الإمامة لإمامين في وقت واحد.
احتجوا بأن الخصال الثلاث إن كانت متساوية الصفات في أنفسها، فينبغي أن تجب جميعها، [تسوية] بين المتساويات، وإن انفرد بعضها بوجه يقتضي إيجابه، فينبغي أن يعين للمكلف، حتى لا يلتبس بغيره. وهذا مبني على أصول القوم؛ فإن الوجوب يتبع الصفات الثابتة التي (٣١/ب) لا تبديل فيها ولا تحويل. وقد مر الكلام على هذا الأصل، فلا نعيده.
ثم إنه باطل بالمسائل المسلمة الإجماعية، ويصح أن يكون مقصود الطالب أحد الفعلين. وقد بينا أن الإيجاب يرجع إلى تعلق الخطاب، أو إلى','1','745',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (519,'الخطاب المتعلق. وليس تعيين متعلق الذكر، كما إذا [قيل]: أكرم رجلا. وإذا لم يشترط تعيين متعلق الذكر، فلا يشترط تعيين متعلق الإيجاب. وإنما وقع هذا من حيث ضاهى الوصف (٦١/ب) بالحل والحرمة، الوصف بالعجز والقدرة والسواد والبياض، وذلك وهم نبهنا عليه.
فإن قيل: الله تعالى يعلم ما يأتي به المكلف، وما يتأدى به الواجب، فيكون معينا في علمه، وإنما هو متلبس علينا. قلنا: هذا باطل من أوجه:
أحدها- أن هذا تكليف ما لا يطاق، فإن الجوب على هذا متعلق بعين مخصوصة، وقد كلف فعلها بعينها، ولم ينصب له دليل عليها، وذلك محال.','1','746',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (520,'الثاني: أن نقول: العلم يتعلق بالمعلوم على ما هو عليه، ولا يغيره. وإذا كان كذلك، فيجب النظر إلى التكليف حتى يعلم، هل تعلق بمعين أو بمبهم، فيعلمه الله تعالى كذلك؟ فأما إذا [كان] متعلقا بمبهم، فكيف يعلمه الله تعالى متعلقا بمعين، وهو خلاف حقيقته؟ فيكون المتعلق جهلا [لا] علما.
الثالث: إنا وإن قدرنا أن الله تعالى يعلم ما سيوقعه العبد، إلا أن لا نسلم أنه متعلق [الوجوب]، من جهة [تعينه]، وإنما هو تعيين باعتبار الوجود، لا باعتبار تعيين التكليف، فإنه لو قال: اعتق رقبة، وكان الله سبحانه يعلم أنه يعتق زيدا مثلا، لم يكن زيد متعلق الخطاب، من جهة كونه زيدا، وإنما كان متعلق الخطاب من جهة كونه عبدا.','1','747',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (521,'الرابع: أن نقول لهم: ما قولكم لو علم الله أنه لا يوقع شيئا، أو علم أنه يوقع الجميع، فما الذي تقولون؟ إن قلتم: إنه لم يوجب شيئا، فهو محال. وإن قلتم: إنه أوجب واحدا من آحاد، ورد الخيرة في التعيين إلى خيرة المكلف، فمتعلق الوجوب خصلة، من جهة كونها خصلة، ولا خيرة في ذلك، ومتعلق التخيير التعيين، ولم يتعرض له التكليف. وإن كان من ضرورة دخول الشيء الوجود أن يكون متعينا.
فإن قيل: فإذا صح تعلق التكليف بأحد فعلين لا بعينه، فهل يصح تعلق التكليف بأحد شخصين لا بعينه؟ وما حقيقة فرض الكفاية، أهو فرض على الجميع ويسقط بفعل واحد، أو هو واجب على بعض لا بعينه؟ قلنا: هذا محل غامض، وأمر ملتبس، ونحن نكشف الحق في ذلك بعون الله وتوفيقه.
وأعلم أنه لا يعقل التكليف إلا عند اجتماع أربعة أمور: أحدها- التكليف، والثاني- المكلف، والثالث- المكلف، والرابع- ما يتعلق بالتكليف. (٣٢/أ) وقد مرت هذه القسمة في أول الكتاب.
وإذا كان كذلك، وجب عقلا تعيين المكلف، لقيام التكليف به، وتعيين التكليف أيضا، فإن ل في نفسه حقيقة معقولة، لا يعقل اللبس في ذاته.
الثالث: المكلف به، وهو الذي نازع فيه المعتزلة، هل يجب تعينه عقلا أم لا؟ وهم متفقون على استحالة الإبهام، ولكن لهم مذهبان: أحدهما-','1','748',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (522,'وجوب الجميع. والثاني- أن الواجب معيين، وهما جميعا يقضيان إلى نفي الخبرة، ومنع الفرق بين المتماثلات في الصفات.
وأما الركن الرابع: وهو المكلف، فقد ذهب أبو حامد إلى أنه يستحيل أن يكون مبهما. واحتج بأن الوجوب إنما يعقل على تقدير ذم التارك أو عقابه، ويستحيل أن يعاقب أحد الشخصين لا بعينه، فيستحيل توجه الوجوب على واحد لا بعينه. كما يستحيل خلق السواد في واحد لا بعينه، وكذلك الضرب وغيره. والذي نختاره خلاف ذلك، وندل عليه بالمعقول والمنقول.
أما (٦٢/أ) المعقول: فإن السيد إذا قال لعبيده: ليأتني أحدكم بشيء من الأشياء، وحتم ذلك وضيقه، وعلم منه بقرائن أحواله إيجابه لذلك الشيء. فلا يخلو: إما أن يقال: لم يطلب [منهم] شيئا، وهو ضد ما صرح به. أو يقال: طلب من جميعهم. وهو باطل، لأنه قال: ليأتني أحدكم. وإما أن يقال: أوجب الإتيان على واحد بعينه، فهو باطل من وجهين: أحدهما- تعيين من غير نصب دليل عليه.
الثاني: أنه قد يصرح، ويقول: أي واحد كان.
وأما المنقول: فقد قال الله تعالى: {فلولا نفر من كل فرقة}. ولم يعين تلك الطائفة. وكذلك قوله: {ولتكن منكم أمة يدعون إلى الخير ويأمرون بالمعروف}. ولم يعين تلك الأمة. وأيضا فإن الطلب قد يوجه على وجه لا يتصور أن يعم الجميع، كما إذا كان عند السيد مائة عبد مثلا، وقال: [ليأتني] أحدكم بكوز ماء، فيعلم على القطع أنه لم يأمر بجميعهم بذلك، وكيف يمكنهم','1','749',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (523,'[جميعا] فعل ما طلب، مع اتحاد المطلوب، وامتناع المشاركة فيه؟ لا سيما إذا كان للسيد أشغال أخر، يعلم أن ذهاب الجميع لتلك الحاجة يفوت مقاصد السيد من غيرها. فيعلم بالضرورة أنه لم يوجه الطلب على الجميع بصفة الاجتماع على العمل الذي قصد انفراد البعض به. هذا واضح، لا خفاء فيه.
فإذا تصور ترك الجميع ذلك المطلوب، صح أن يعاقبهم أجمعين، لما صح في حق كل واحد من استهانة وقلة احترام. هذا هو الذي يصح عندنا.
وقد حققنا وجوب شيء من أشياء. وبهذا التقرير أيضا نرد على من قال في مسألة الأمر بالشيء نهي عن ضده، أن الأمر نهي عن الأضداد، والنهي ليس أمراً بواحد من الأضداد، فإنه قد تناقض كلامه، فإنه كما يستحيل (٣٢/ ب) الإتيان بالمأمور دون الانكفاف عن جميع أضداده، فكذلك يستحيل الانكفاف عن المنهي عنه دون التلبس ببعض أضداده. وإنما حملهم على الفرق أن الانكفاف عن جميع أضداد المأمور به يتأتى، والإتيان بجميع أضداد المنهي عنه لا يتأتى. فلم يبق إلا أن يقال: إنه مأمور بواحد منها لا بعينه. وهذا منعه المعتزلة.
وقال الإمام: (لا يمتنع وجوب شيء من أشياء، وتعينه إلى خيرة','1','750',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (524,'المكلف). وهذا القرير جدير بأن يذكر في تلك المسألة، ولكنا أغفلنا ذكره هناك، فذكرناه في هذا المكان، لكونه يليق به. وبالله التوفيق.
قال الإمام: (مسألة: اشتهر من مذهب شيخنا أبي الحسن - رضي الله عنه - مصيره إلى أن المعدوم الذي سبق في العلم وجوده) إلى آخر المسألة. قال الشيخ: ذهب','1','751',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (525,'القلانسي إلى أنه لا يتصف الباري تعالى في الأزل بكونه آمرا ناهيا، إلى بقية الأوصاف. لكن اختلف الناس في تأويل لفظه: فذهب جماعة من الناس إلى أنه منع الوصف الذي هو الإطلاق من جهة النطق. وأما حقيقة الكلام فثابتة له. وهذا هو الأقرب.
وذهب ذاهبون إلى أنه منع حقيقة الصفة، فلا أمر ولا نهي له في الأزل، كما أنه لا يسمى خالقا في الأزل. وهذا غير صحيح على رأي أهل السنة،','1','752',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (526,'الذاهبين إلى إثبات كلام النفس أزلا. وليس للباري سبحانه وتعالى من كونه خالقا حكم حقيقي. ومعنى كونه خالقا: (٦٢/ب) وقوع الخلق بقدرته.
وإذا سلم القلانسي للشيخ أن الكلام قديم، فكونه آمرا، من حقيقته النفسية وصفته الأزلية. ويستحيل ثبوت الموصوف مع فقدان وصفه النفسي.
ووجه آخر: وهو أنه لم يثبت كونه آمرا في الأزل، لم يثبت كونه آمرا فيما لا يزال، لتجدد الحقائق وصفات النفوس، وذلك غير معقول.
وأيضا فإنه يقتضي تجدد حوادث قائمة بالقديم، وذلك مستحيل. ويلزم','1','753',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (527,'منه أيضا أن تكون الصفة ليست كلاما، ويتجدد كونها كلاما، وذلك غير معقول. وهذه وجوه قطعية في إبطال ما صار إليه، إن لم يرد النزاع إلى اللفظ.
وأما المسلك الأول للأصحاب: فإن الرسول في حكم من يبتدئ أمرا، إلى آخره. فكلام ضعيف، واعتراض الإمام عليه صحيح. وتحقيق سقوطه: أن الأمر لا يتصور قيامه بنفسه، فإنه من جملة المعاني. وإذا كان كذلك، فكيف يتصور أمرا لا آمر له، وهو بمثابة علم لا عالم به، وقدرة لا قادر بها، وذلك غير معقول. والإجماع أيضا على منعه. فكيف يسوغ الاستشهاد بممتنع وفاقا؟ وأدلة العقول تمتنع منه أيضا، كما قررناه. والرسول ليس مستقلا بأمر، وإنما هو مبلغ أمر الله تعالى. وإذا تم التبليغ، لم يؤثر موت المبلغ، ومن','1','754',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (528,'له الأمر حي، (٣٣/أ) لم يزل ولا يزال.
وأما المسلك الثاني لهم: وهو أن المأمور به، وهو الفعل، لا يمتنع أن يكون معدوما. العبارة فيها قصور، فإنه يوجب أن لا يتوجه الأمر إلا بمعدوم، إما عقلا عند من أحال تكليف المحال عقلا، أو شرعا عندنا. فلا بد إذا من تعلق الأمر بالمعدوم. وقيل يبقى مستمرا حتى يدخل الفعل الوجود، أو ينقطع تعلق الفعل حالة الوجود. كما سيأتي بيانه بعد هذا. وهذا لا يصح التمسك به، فإن مقصود الطلب تحصيل ما ليس حاصلا، ولو كان حاصلا، لم','1','755',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (529,'يطلب، فأين هذا من توجه الأمر على المعدوم، وتحقيق الطلب في حقه؟
لكن المعتزلة إذا ذهبت إلى تجدد الأمر، وامتناع بقائه، فقد التزموا أنه لا أمر لله - عز وجل - في زماننا [هذا] متوجه على عباده. وهذا أشد وأشنع من المصير إلى كون الباري تعالى [ليس] آمرا في الأزل، فإن هذا منع للشرائع أن تكون متوجهة علينا في الحال، ومخالفة لإجماع المسلمين، فإن الأمة مجمعة على أن أوامر الله تعالى متوجهة علينا في زماننا.
وقوله في تقرير طريقة الشيخ: (إنه لا يمتنع قيام الأمر [بالنفس] مع غيبة المأمور). كلام صحيح. ولو بقي ذلك الطلب حتى يحصل المأمور،','1','756',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (530,'لارتبط به، ولكنه لا يتصور بقاؤه لكونه عرضا. والواحد منا يتبينه في حق مأموره، فإنه يقول القائل: إذا لقيت فلانا فقل له: إني أمرته أن يفعل كذا، وإذا فعل، يقال فيه: امتثل، وإذا بعد التبليغ، يقال: إنه خالف، ويستحق التأديب. وهذا مقطوع به عند العقلاء، وعليه يبنون أوامرهم ونواهيهم، وجاحد ذلك مخالف للعقلاء في مطالبهم. وإذا لم يبعد ذلك في كلامنا، فهو المعني بكلام (٦٣/أ) النفس. والذي قلناه في حق الغائب، لا يختص به، بل يقوم بذات الأب طلب تعليم العلم من الولد الذي سيوجد، ويقول لوصيه: إذا بلغ الولد، فعرفه أني أمرته بكذا. وإذا فعل ذلك، يقال: أطاع أباه، وامتثل أمره، وإن كان الأب الآن معدوما، والولد كان وقت أمر الأب معدوما. هذا معقول عند العقلاء.
وقوله: ([فلست أرى] ذلك أمرا حاقا، [وإنما يقدر الأمر] لو كان، كيف يكون؟ ). ليس كما قال، فإنه كان تقدير أمر، لم يكن بعد ذلك، إذ','1','757',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (531,'لم يتجدد له غيره، ولا كان الولد أو الغائب ممتثلا أمره عند بلوغه.
وقوله: (وغرض المسألة إثبات الأمر أزلا، من غير مأمور، لا محاولة إثبات [المنفي] مأمورا). هو كما قال: وإذا صح قيام الأمر بالنفس من غير مأمور، ارتفع السؤال.
وما ذكره من أن: (من ظن أن المعدوم مأمور، فقد خرج عن حد المعقول). هو كما قال. ولا يتصور أن يكون المعدوم- وهو في نحض- مدعوا بأمر، ولا مصروفا بزجر، (فلا شك أن الوجود شرط في كون المأمور مأمورا).','1','758',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (532,'وقوله: (٣٣/ب) (وإذا لاح ذلك، بقي النظر في أمر بلا مأمور، وهذا [معضل] [أزب]). أشار إلى صعوبة الكلام، ثم صرح بالاستحالة.
فقوله: (الأمر [من الصفات] المتعقلة [بالنفس]، وفرض متعلق لا متعلق له محال). [هذا] تصريح بتجدد الأمر، بلا ريب، وقدم الكلام يمنع من هذا، فلا يتصور أن يكون الأمر متجددا، والكلام قديما، لما رددناه به على القلانسي. ثم هذا الإشكال لا يختص بالأمر، فإذا أحال الإمام متعلقا لا','1','759',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (533,'متعلق له، لزمه أن يحيل القدرة في الأزل، أو كونه قادرا.
وقد اعترفت المعتزلة بكونه قادرا أزلا. وإن كان قادرا حكما متعلقا، ولا فرق بين أن يكون المعلق حكما أو صفة، وكذلك الإرادة عندنا والرؤية. فليس يستمر للإمام ما ذكره، إلا بنفي الصفات والأحكام في الأزل، ثم يحكم بتجددها فيما لا يزال. كما ذكره في قضية الأمر. وإذا حكم بأن الباري تعالى ليس قادرا في الأزل، استحال أن يكون قادرا فيما لا يزال، لأنه لا يكون قادرا إلا بقدرة متجددة، ويستحيل القدرة إلا بسبق قدرة أخرى، وكذلك القول إلى ما لا يتناهى، وذلك محال. فوجب بهذا التقدير إثبات الصفات في الأزل من غير تجديد، ورجوع التجدد والطريان إلى المتعلقات.','1','760',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (534,'ولله تعالى رؤية قائمة به، وهو بها راءٍ. وليس المراد بكونه رائيا، أنه رأى المعدوم، وإنما كان باعتبار قيام الرؤية به، فإذا وجد الموجود، كان مرئيا بتلك الرؤية. وكذلك القول في السمع، فهو سميع في الأزل، باعتبار قيام السمع به، فإذا وجدت الأصوات، كان سامعا لها بسمعه القديم، وكذلك جميع صفاته إلا العلم، فإن العلم لا يخصص بوجود وعدم، وكذلك الخبر الذي هو من الكلام.
والأصل الذي إليه المرجوع، وجوب القدم لله سبحانه وصفاته، واستحالة الجواز والتجدد في ذلك. وإنما غمض هذا على الخلق بالنظر إلى معانيهم،','1','761',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (535,'فإنها متجددة غير باقية. فلو تقدمت الرؤية في حقنا على (٦٣/ب) المرئي، لعدمت ولم تتعلق بشيء. ولا تصلح أيضا للتعلق، فتخرج عن كونها رؤية. فلما استقر هذا في أوصاف الخلق، شق عليهم فهمه بالإضافة إلى صفات الله - عز وجل -.
وإذا دل الدليل على قدم الذات والصفات، واستحال التجدد على كل حال، لم يلتفت لعمل الوهم في اشتراط الاقتران. والله الموفق للصواب بحوله وقوته.

قال الإمام: <span data-type="title" id=toc-109>(مسألة: ذهب الأصوليون من أصحاب أبي الحسن - رضي الله عنه - إلى أن الفعل في حال الحدوث مأمور به) </span>إلى قوله (فهذا وجه هذه المسألة إن','1','762',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (536,'اتجه). قال الشيخ: (٣٤/أ) المسألة مبنية على الاستطاعة، كما ذكره الإمام. وهذا على رأي من يحيل تكليف المحال. فأما مجوزه، فلا يشترط من جهة العقل شيئا من ذلك، وإن كان يمنع من التكليف وقوعا فيما لا يعقل. ومذهب المعتزلة أن القدرة الحادثة لا تقارن حدوث [المقدور]،','1','763',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (537,'بل تتقدم عليه بزمان، فتتعلق في الزمان الأول، ويقع المقدور في الزمان الثاني. وإنما حملهم على ذلك أنهم قالوا: سبيل القادر على الشيء أن يكون قادرا على تركه، وحالة الحدوث لا يكون قادرا على الترك، فلا يكون قادرا على ترك الفعل. وقد نقضوا هذا بالمقيد المربوط، فإنهم قد حكموا بأنه قادر، وإن لم يكن متمكنا من','1','764',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (538,'الترك. وطريق الرد عليهم باستحالة بقاء الأعراض كلها. ودليله أنها لو بقيت لاستحال عدمها، وقد عدمت، فيستحيل بقاؤها، فإنها لو بقيتن لبقيت بنفسها، وصفة النفس لا يتصور تبدلها. وقد ساعدت المعتزلة على استحالة بقاء الأعراض، كالأصوات وغيرها. وأيضا فإنا نقول لهم: إذا كانت القدرة لا تتعلق بالمقدور حالة حدوثه، بل كانت القدرة غير مرتبطة به، لم يكن الفعل واقعا بها، بل يصح عند المعتزلة أن تتقدم في حال وجود المقدور، فكيف تكون مؤثرة وهي معدومة؟
وقوله: (لا حاصل لتلقي حكم تعلق الأمر من القدرة) إلى آخره. كلام [ضعيف]، فإن أبا الحسن إذا [جوز] تعلق الأمر بما [لا] يصح أن يكون مقدورا، فكيف لا يجوز أن يكون الأمر متعلق بالمقدور؟ نعم، إنما يكون هذا الكلام قادحا لو كان أبو الحسن يمنع تعلق الأمر بمقدور، فحينئذ يكون الدليل منقوضا.','1','765',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (539,'وقوله: (فإن القاعد في حال العقود مأمور بالقيام على رأي أهل الإسلام). وهو على ما قال، ولكنه نقضه في المسألة التي بعد هذه، وفي مسألة النسخ، فإنه ذهب فيهما جميعا إلى أنه لا يعلم توجه التكليف تحقيقا، ومنعه من ذلك الموت أو النسخ. فإذا أجمع المسلمون على كونه مأمورا بالقيام، وجب أن يكون عالما به، إذ الإجماع دليل قاطع.
وقوله: (ولو تنزلنا على اقتران القدرة بالمقدور، فيستحيل مع ذلك كونه مأمورا به). اقتصر على دعوى الاستحالة من غير برهان أكثر من قوله: (فإن','1','766',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (540,'اقتران القدرة الحادثة بالمقدور معناه أنه بها وقع). وهذا ليس ببرهان، وما المانع من تعلقهما جميعا- أعني الأمر والقدرة؟ ولا يزال (٦٤/أ) الأمر متعلقا مستمرا حتى ينقطع تعلق القدرة، هذا لا مانع يمنع منه.
وأما قوله: (لم يشترط أبو الحسن اقتران القدرة بالمقدور، بالنظر إلى خصوصية القدرة، وإنما كان كذلك من جهة كونها عرضا). فهو كلام صحيح. (٣٤/ب)
قال الإمام [رحمه الله]: (وإن تفطن ذكي لوجه الحق، خطر له في معارضة ذلك) إلى آخر المسألة. قال الشيخ: ما ذكره من أن القدرة لا توجب المقدور لعينها، كلام صعب، إن عم به القول في القدرة القديمة والحادثة، فإن القدرة توقع المقدور لعينها، ولكن لو لم تكن كذلك، لاستحال أن توقعه، إذ الصفة لا توصف بغير الأوصاف النفسية.
وقوله: (لو أوجبت لعينها، لكان العالم أزليا). هذه شبهة ابن','1','767',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (541,'سينا في إثبات قدم العالم، فإنه قال: إما أن يرجع العالم إلى نفس الذات، أو يرجع إلى صفة الذات، فإن رجع إلى الذات، لزم أن يكون قديما، وإن رجع إلى صفة، على ما يقوله أهل الإسلام، فالصفة قديمة، فيجب أن يكو قديما. وجوابنا أن نقول: يقع العالم بالقدرة القديمة عند إرادة الموقع، فإذا قال: الإرادة أيضا قديمة، فلم تأخر المراد؟ قلنا: معقول الإرادة أن يفعل أو يترك على حسب ما يريد من غير تحتيم. فالعلم عندنا يقع بالقدرة من غير زيادة. ولزوم القدرة باطل بما قررناه في الافتقار في التخصيص إلى الإرادة.
وقوله: (ومن أنصف من نفسه، علم أن معنى القدرة: التمكن من الفعل، وهذا إنما يعقل قبل الفعل). هذا عين مذهب المعتزلة في صحة تقديم القدرة','1','768',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (542,'على المقدور. وقد بينا بطلان ذلك. وقد بينا بطلان ذلك. وأما التمكن المدرك قبل الفعل، فذل يرجع إلى غلبة العادة في تسيير بعض الأفعال دون بعض. وكم من توهم ذلك، ثم منع منن العمل وقت الحاجة.
وقوله: (لو قدرنا اقتران القدرة بالمقدور، فيستحيل مع ذلك كونه مأمورا به، فإن الأمر طلب، فكيف يطلب كائن ويقتضي حاصل؟ ). أما أبو الحسن، فيجوز ذلك كله عقلا. وإنما يبقى النظر في [مواقعه] شرعا، والتكليف يستمر','1','769',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (543,'حتى يمتثله المكلف، أو يفوت التمكن. ومقتضى ما قاله الإمام أن لا تتصور طاعة من أحد من الخلق، فإنه لما كان مأمورا، لم يكن فاعلا، ولما كان فاعلا، لم يكن مأمورا على حال. والعبد عند شروعه في الفعل، إنما يقصد طاعة الله تعالى بفعله. ولو علم سقوط الطلب المتعلق بالفعل، لم يتصور أن يقصد إلى الطاعة به، وتصير حالة الفعل- على هذا الرأي- مضادة للتكليف، كما تكون حالة ورود النسخ مضادة لبقاء الطلب. فكما لا يتصور حالة ورود المنع أن يطيع بالفعل، فكذلك لا يتصور حالة سقوط التكليف أن يكون مطيعا بالفعل. فهذا باطل قطعا من جهة الشريعة، فإن مقتضاه أن أحدا لم يطع ربه بفعل يفعله.
قال الإمام: (مسألة: ذهب أصحابنا (٣٥/أ) إلى أن المخاطب إذا خص بالخطاب) إلى قوله (ونحن الآن نأخذ في النواهي، إن شاء الله تعالى). قال','1','770',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (544,'الشيخ: قوله: إنما منعت المعتزلة أن يعلم، لأنه جاهل ببقاء الإمكان له إلى وقت التكليف. فهذا غير صحيح، فإن المعتزلة مجمعون على أن الواحد منا إذا أمر غلامه أو مأموره بفعل من الأفعال، فهو يعلم قيام الأمر به، وتعلقه بمأموره، والمأمور يعلم ذلك من أمره، مستندا إلى قرائن أحواله، (٦٤/ب) ولا يخالجه ريب في أمره المتعلق به، وهو لا يعلم بقاء الإمكان له إلى حين امتثاله.
فلو كان علم الإمكان شرطا في حصول العلم بالطلب، لم يعلم على حال والأمر بخلافه، والمذهب على نقيضه.
وقوله: (الجاهل بالشرط جاهل بالمشروط). كلام صحيح، لكن هل','1','771',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (545,'الإمكان شرط في توجه التكليف، أو شرط في حصول الفعل المكلف به؟ فإن كان شرطا في التكليف، فممنوع بأدلة سبقت قبل هذا. وإن كان شرطا في الإيقاع، فهو كذلك، ولا يمنع جهله التكليف، كما ذكرنا في حق الشاهد.
وأما مسلك القاضي الأول، فهو صحيح. وقد نقله الإمام في المسألة المتقدمة، فإنه قال: (القاعد في حال القعود مأمور بقيام باتفاق أهل الإسلام). والإجماع دليل، فمن ثبت عنده الإجماع على الحكم، كيف لا','1','772',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (546,'يعلمه؟ ولا يصح أن يقال: إنما ذكره الإمام حكاية عن أبي الحسن، فإنه أورده محتجا عليه في أنه لا ربط بين القدرة والتكليف، ولذلك أنه قال: (ولا قدرة له عند أبي الحسن). فلم يتمسك أبو الحسن إلا بعين ما نقله هو. وكيف لا يعلم المكلف توجه الحكم عليه قبل الشروع فيه، وهو [لا] يشرع فيه إلا بنية؟ والنية [قصد] يتبع العلم، فلو لم يعلم لم يقصد. هذا تقرير القاضي،','1','773',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (547,'وهو جارٍ على أصله في أن كل مجتهد مصيب، وأنه إذا [انتهضت] الأمارات، حصل العلم بالحكم. أما نحن إذا بنينا على أن المصيب واحد، لم نشترط في نية العبادة العلم بوجوبها، بل يكتفي بالظن في ذلك.
وأما المسلك الثاني للقاضي، وهو الذي بنى النسخ عليه، أن الحكم يثبت قطعان ثم يرتفع بالنسخ. فالكلام فيه طويل. وسيأتي مقررا في كتاب (النسخ). إن شاء الله تعالى.
وقوله: (فلا يتوجه القطع بتوجه التكليف، إلا مع القطع بالإمكان، أو مع اعتقاد التكليف من غير إمكان. وهذه قسمة بديهية لا يتصور مزيد عليها). ونحن قررنا أن الإمكان شرط في إيقاع الفعل، وليس شرطا في توجه التكليف، فإن التكليف يتحقق من غير أن يعلم المكلف تمكنه (٣٥/ب) من','1','774',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (548,'الفعل في مستقبل الزمان. هذا تمام كلام الإمام، واعتراضه والرد عليه.
وللمسألة عندي طريق آخر، ليس هو شيئا من هذا، وذلك أنا نقول: إنما يتصور أن يعلم المأمور كونه مأمورا قبل التمكن من الامتثال، إذا تصور أن يؤمر قبل ذلك. فإن العلم يتبع المعلوم على ما هو به، فإذا تصور أن يؤمر قبل التمكن، أمكن أن يعلم ذلك، إذا نصب له عليه دليل، وإنما يكون مأمورا، إذا توجه الأمر عليه.
وهل يصح أن [يتوجه] الأمر عليه قبل أن يعلم تمكنه من الفعل المكلف [به]؟ وقد بينا أن ذلك يتصور، وبينا ثبوته في حقوق البشر وأوامرهم مع عبيدهم. ولو اتفق أن يكون مستحيلا، لما تصور شاهدا وغائبا. فإذا قال السيد لعبده: صم غدا، فقد أوجب وألزم في الحال صوم الغد، وإن كان الإيقاع مشروطا ببقاء العبد إلى الغد. فقد تنجز التكليف، وتحقق الخطاب، وإن لم يكن الفعل في الحال، ولا علم إمكانه في الغد، لاحتمال طريان موت أو عجز.','1','775',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (549,'فلما ثبت ذلك في الشاهد، أطبقت المعتزلة على أن الأمر بالشرط أمر في الحال، لكن يشترط أن تكون عاقبة الشرط ملتبسة على الآمر (٦٥/أ) الطالب.
أما إذا كانت منكشفة له، لم يكن أمر بشرط في حقه، بل إن كان عالما بالثبوت، فأمر مؤقت، وإن كان عالما بالانتفاء، فلا أمر على حال. وإنما حملهم على ذلك أصلان:
أحدهما- أن الشرط هو الذي يمكن أن يكون، وأن لا يكون، أما ما يتحقق ثبوته أو نفيه، فلا يصلح للشرطية.
الثاني- أن الأمر مريدا للفعل الذي علق طلبه على الشرط. فإن من قال لعبده: صم إن صعدت السماء، لم يرد صومه بحال. فلما تقرر ذلك عند القوم، قالوا: لا يتصور الأمر بالشرط في حق الله - عز وجل -، لعلمه بعواقب الأمور، ولامتناع الإرادة في الفعل. وأما نحن،
فلا نقول باقتران الأمر بالإرادة. وقد تقدم الدليل على ذلك، فيصح أن يأمر، وإن لم يكن مريدا للمأمور به. هذا حرف المسألة بين الفريقين.
ونحن نقول: متى كان الشرط منكشفا [للمأمور]، فالأمر على ما قالوه، لامتناع مقصد التكليف، من تجريد الرغبات، وانتفاء الابتلاء والامتحان. هذا','1','776',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (550,'عندنا ممتنع، باعتبار الواقع لا المعقول. فإذا اشتبهت عاقبة الأمر على المطلوب منه، صح أن يكلف، كما فعل في حق إبراهيم - عليه السلام -، وقد سماه الله تعالى مبينا.
والمعتزلة بنت ما قررناه من أصولها، وزادوا وجها آخر، فقالوا: (٣٦/أ) إذا كان الأمر بالشرط مشروطا بجهله، فليكن ذلك في حق الآمر، إذا هو الذي قام به الأمر، فالمؤثر فيه صفة، لا صفة غيره.
وقد قدمنا نحن من أصولنا ما [يحقق] وقوع التكليف بما لا يقدر المكلف عليه، وبينا ذلك بيانا شافيا، وحققنا أن العاصي غير قادر على الطاعة، ولا يخرجه ذلك عن كونه مأمورا منهيا. ولكن لما أمكن تحريك الرغبات في الاعتياد، والصوارف بالنهي، صح التكليف. ويكتفي في حق المكلف بأن لا يعلم عجزه عن الفعل. فأما أن يشترط علمه بالإمكان وقت العمل فلا. وهذا هو الثابت في الشرائع، فإن أحدا من المكلفين لا يعلم وقوع الفعل الذي يكلف به قبل وقوعه في جريان العادة، بل يتحقق التكيف علما، وإن أمكن الاخترام قبل ذلك. وإن جاء الاخترام أو العجز أو النسخ، لم يتبين أنه لم يكن مأمورا، بل نقول: أمر ثم انقطع التكليف عنه.','1','777',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (551,'وعلى هذا قضى مالك - رضي الله عنه - بأن المرأة لو أفطرت في أول النهار، ثم حاضت في آخره في رمضان، لوجبت عليها الكفارة، لأنها أفسدت الصوم المأمور به على الحقيقة. وليس طريان الحيض يبين سقوط التكيف بصوم اليوم من أوله، بل يقطعه بعد توجهه.
فإن قيل: فلو علمت المرأة بقول نبي صادق أنها تحيض في أثناء النهار، فهل تتمكن من الإفطار، من [جهة] أن صوم بعض اليوم غير مأمور به، وهي لا تستطيع صوم الجميع؟ قلنا: يجب عليها الصيام في الحال، فإن المرخص للإفطار لم يوجد بعد، والميسور لا يسقط بالمعسور. نعم. لو علمت أنها لا تتمكن من صوم اليوم بوجه، بالإخبار أنها تحيض قبل الفجر، لم يتأت منها اعتقاد الوجوب في هذه الصورة، لفوات مقصد التكليف [من الابتلاء] والعزم، والاهتمام بالعمل. هذا تقرير الكلام على المذهبين جميعا. والله المستعان وعليه أتوكل وإليه أنيب. (٦٥/ب)
قال الإمام [رحمه الله]: (القول في النواهي: النهي من أقسام الكلام','1','778',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (552,'القائم بالنفس، وهو في اقتضاء الانكفاف عن المنهي عنه) إلى آخر المسألة. قال الشيخ: قوله:<span data-type="title" id=toc-110> القول في النواهي</span>. وإيراد الجميع على هذه الصيغة، هو بمثابة قوله: الأوامر. والكلام على المفرد والجمع، واشتراك الاسم على ما سبقن فلا نعيده. وقد رد النهي في هذا المكان إلى طلب الانكفاف، لا إلى نفي المنفي عنه، فإن الكف: فعل يدخل تحت المقدور، والنفي ليس بمقدور. على حسب ما سبق، فلا نعيده.
قال الإمام: (مسألة: ذهب المحققون إلى أن الصيغة المطلقة في النهي) (٣٦/ب) إلى قوله (وأبو هاشم لا يسلم ذلك ولا أمثاله، ولا هو ممن يروعه','1','779',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (553,'[التهويل]). قال الشيخ: الكلام مع أبي هاشم في موضعين: أحدهما- في تصور الطاعة في الأرض المغصوبة وامتناع ذلك.
والثاني- وإذا لم نتصور الطاعة، فهل يبقى الأمر متوجها على المكلف، أم ينقطع عنه؟ أما البحث العقلي، فقد اتفق عليه القاضي وأبو هاشم. وأما الأمر الشرعي، وهو أن الأمر يبقى مستمرا أو ينقطع؟ فإن الظاهر ما قاله أبو','1','780',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (554,'هاشم، من أن الفعل إذا خرج عن كونه طاعة، وبقي العمل قائما، وجب الامتثال، تلقيا من الخطاب. هذا ظاهر إلا أن يقوم دليل على سقوط الطلب. [والمسائل التي أوردت نقوضا عليه] لازمة بغير إشكال، ولا قدرة له على منع جميعها بحال.','1','781',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (555,'وقول الإمام: (إنه لا يسلمها ولا شيئا منها، وليس هو ممن يروعه التهويل). تنويه من الإمام بقول خارق [الإجماع]، فإنه [إذا] ذهب إلى بطلان صلاة من توجه عليه قضاء دين، لم يختص ذلك ببطلان الصلاة، بل يتعدى إلى النكاح والبيع [وغيرهما]، [وكذلك لا يختص الأمر بقضاء الدين، بل التحلل من الحقوق التي يجب التنصل منها، كالغيبة وغيرها]، وجميع ما يتعلق بحقوق العباد. فيفضي ذلك إلى بطلان أعمال أكثر الخلق من صلاة وصوم وزكاة وحج وبيع وشراء وإعتاق، إلى غير ذلك من التصرفات التي','1','782',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (556,'هي ضروريات الخلق. وهذا معلوم بطلانه من أدلة الشريعة علما ضروريا.
وأما ما تمسك به من البحث العقلي في اتحاد الفعل فالرد عليه هو والقاضي يأتي في آخر المسألة، إن شاء الله تعالى.
قال الإمام: (ومما ألزمه القاضي من هذا الفن أنه قال: المصلي في حال غفلاته، ليس قائما بحقيقة العبادة) إلى قوله (فهذا م كلام المعترضين).
قال الشيخ: إلزام القاضي صحيح، وعذر الإمام ضعيف، والانفصال [عما] ذكره غير متجه.
وبيان تقرير الالتزام أن أبا هاشم اعتمد على بقاء الأمر متوجها على من لم يمتثل، ورأى أن المصلي في الدار المغصوبة غير ممتثل- على زعمه- فيبقى الأمر متوجها.','1','783',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (557,'قال القاضي: إنما يكو الفعل امتثالا، إذا كان الطلب متوجها، وإذا سقط تعلق الطلب بالفعل، كيف يتصور أن يكون امتثالا، وهو غير مطلوب، والغفلة حالة استمرار التكليف؟ فإذا صح أن يكون هذا الفعل مسقطا للخطاب، وإن لم يكن طاعة، لم يبعد أيضا في مسألة المصلي في الدار المغصوبة أن يسقط التكليف، وإن لم يكن مطيعا.
والفرق بأن المصلي في الدار المغصوبة (٦٦/أ) عاص بفعله، والغافل غير مكلف في حال غفلته، لا يقدح فيما قرراه. فإن أبا هاشم إنما اعتمد في بقاء التكليف مستمرا على كون الفاعل غير مطيع (٣٧/أ)، فإنه قال: إن المعصية لا يتصور أن تكو طاعة، وكذلك الفعل في [حال] الغفلة لا يتصور أن يكون طاعة، إذ لا تكليف. هذا هو التحقيق.','1','784',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (558,'وقول الإمام: (وإنما غائلة [كلام] أبي هاشم، أن المعصية لا يصلح أن تكون مأمورا بها على جهة العبادة، ولا على جهة أخرى). ومثله يطرد في الفعل في حال الغفلة من غير إشكال.
قال الإمام [رحمه الله]: (وأما القاضي فقد سلك مسلكا آخر،','1','785',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (559,'[وسلم] أن الصلاة في الدار المغصوبة) إلى قوله (ممن يدعي وفاق الماضي على إسقاط الأمر بسبب معصية). قال الشيخ: أما كون القاضي سلم أن الصلاة الواقعة في الأرض المغصوبة ليست طاعة. فسيأتي وجه الرد عليه. [وأما تمسكه] بالإجماع على سقوط القضاء، فلم يقل في ذلك إجماع مصرح به.
وقول الإمام: (هذا حائد عن التحصيل). وقوله: (فإن الأعذار التي تسقط التكليف محصورة، والمصير إلى سقوط التكليف بسبب معصية مع التمكن، لا أصل له في الشريعة). كلام كله ضعيف، ومن ساعده على انحصار الأعذار فيما تخيله؟','1','786',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (560,'وقوله: (السقوط في هذه الصورة لا أصل له). إن أراد أنه لا أصل له يشبهه بقياس عليه، [فقد] سلم ذلك. وإن أراد أنه لا دليل عليه، فالقاضي قد ادعى الإجماع في ذلك، فلا يبقى إلا المطالبة بتحقيق نقل الإجماع. وهذا لعمري نقله [صعب]، وطريقه مشكل، ولا يصح إجماع على التواتر، وإن قدرنا نقل الآحاد، صارت المسألة ظنية، وخرجت عن نظر الأصول.
وقد ذهب أبو حامد إلى أنها قطعية واحتج بأن: من أبطل أخذه من دليل العقل، وهو مقطوع به. ومن [صحح]، أخذه من الإجماع، وهو أيضا مقطوع به. فكأنه يقول: انعقد الإجماع على أنها قطعية، وإنما يبقى النظر في [تعيين] [مأخذه] لا طريقه.
والإجماع لم ينقله القاضي صريحا، وإنما تلقاه بمسلك استنباطي على','1','787',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (561,'زعمه. فقال: (لم يأمر أئمة السلف الغصاب بإعادة الصلوات التي أقاموها في الأراضي المغصوبة). ورد عليه الإمام بقوله: (قد كان في السلف متعمقون في [الفتوى] يأمرون بالقضاء بدون ما فرضه القاضي). وهذا الرد ضعيف، [وكأنه] راجع إلى نقل الإجماع بطريق القياس، إذ معنى الكلام: إذا كانوا يأمرون بالقضاء بدونه، فكيف لا يأمرون بالقضاء بهذا؟ وهذا لا يتصور أن يرد به على من نقل الإجماع. نعم، يطالب بتقرير صحة النقل. فأما وجه الإمام، فلا يتوجه.
والطريق الذي أسند إليه القاضي الإجماع، لا يصلح عندي للثبوت، فإنه قال: لم يأمر أئمة السلف الغصاب بإعادة الصلوات، وهذا يحتمل [أمورا] كثيرة، إما أن يكونوا أمروا ولم يتفق نقله تواترا، فإن هذا ليس من الأمور العظيمة التي تتوفر (٣٧/ب) الدواعي على نقلها على مرور [الآماد] وتمادي الزمان، فمن أي يحصل العلم بأنهم لم يأمروا بجملتهم حتى يكون','1','788',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (562,'ذلك إجماعا على سقوط القضاء؟
وقد يمكن أن يكون بعضهم يرى القضاء، ولا يرى المسألة قطعية، ولم يستفت فيها، واكتفى بجواب غيره، أو ما أراد أن يرد على المجتهد في محل الاجتهاد، أو لعل جميع العلماء ما اطلعوا على المصلي في الأرض المغصوبة من الأمراء بعد التوبة. وهذه احتمالات كثيرة تمنع من العلم بإضرابهم (٦٦/ب) عن الأمر بالقضاء.
وههنا مسألة عظيمة ستأتي إن شاء الله تعالى في كتاب الإجماع، وهي أن الأمة، هل [عصمت] عن [المعصية] على الإطلاق، أو عصمت عن الخطأ في الأحكام؟ فإذا قلنا: إنها لم تعصم إلا عن الخطأ في الأحكام، وهو اختيار القاضي أبي بكر، فغاية ما فيه لو ثبت النقل عن جميعهم أنهم لم يأمروا بالقضاء، فهل قضوا مع ذلك ببراءة الذمة وسقوط المطالبة، واقتصروا على الإضراب، فيكون ذلك من باب تعاطي الفعل، لا من أبواب الفتوى؟ وإن صرنا- وهو الصحيح- إلى العصمة على الإطلاق، فتبقى الاحتمالات السابقة، فيفوت العلم بسببها.','1','789',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (563,'وقوله: (ثم إن صح عندهم ما ذكره) إلى آخره. كلام ضعيف، مبني على تحريف، فإذا كان الإمام لم يثبت عنده الإجماع على عدم الأمر بالقضاء، كيف يتفق أن يرتب على ذلك نقل الإجماع على أن الموقع صلاة مجزئة؟ فهذا محل غفلة، إلا أن يصدق القاضي في نقل الإجماع، ثم يزعم أنهم أفتوا بكونها صحيحة، فيكون هذا ضدا لما نقله من الخلاف أولا. فإنه قال: (وتقدير الإجماع مع ظهور خلاف السلف عسر).
وقوله: (فلا ينبغي أن يجريه في عين [ما نقله]). القاضي لم ينقل أن الموقع صلاة حتى يكون قد [أجرى] المنقول، ولكنه أخذ من الإجماع سقوط القضاء، وتلقي من العقل استحالة كون الموقع طاعة، هذا طريقه','1','790',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (564,'قال الإمام: (فإذا لاح بطلان هذه الوجوه، فقد حان أن نذكر طريق التحقيق، ونبوح بالسر والغرض) إلى قوله (وهذا في [نهاية] الوضوح). قال الشيخ: ما ذكره الإمام في هذا المكان كلام حسن، وهو رأي الفقهاء. وحاصله راجع إلى تعدد الجهات، وإقامتها مقام متعدد الذوات، وينظر في ذلك إلى غرض الطالب فقد يطلب الشيء من جهة، وينهي عنه من جهة، ولكن يشترط في ذلك أمران: أحدهما- بيان تعدد الجهات. والثاني- صحة الانفصال.
أما إذا تعددت الجهات، ولم يمكن الفصل، استحال تعلق الأمر بجهة والنهي بأخرى، وكذلك يقال في الفعلين المتلازمين. وقد بينا ذلك في مسألة','1','791',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (565,'غسل الوجه، وأنه لو منع أخذ أطراف في الرأس، كان (٣٨/أ) ذلك يجر إلى تكليف ما لا يطاق.
فعلينا الآن أن نبين تعدد الجهات، وإمكان الانفصال، فنقول: الناهي عن الغصب إنما نهى عنه باعتبار معقول، وهو وضع اليد العادية على ملك الغير، وقد يكون الإنسان عاصيا، وإن لم يكن في المكان بالكلية، فضلا عن أن يكون مصليا، فإذا لم ينه عن الغضب باعتبار حركة أو سكون، فهذه جهة معقولة، غير متوقفة على تخيير في البقعة المغصوبة أو غير ذلك.
وقد يأمر بالصلاة من لا يخطر بباله تخير مكان مخصوص لها، وإن كانت الصلاة لا تقع إلا في مكان. لكن هذا من ضرورة الوجود، لا من مقصود الطلب، فليس من ضرورة مصادفتها للبقعة المغصوبة أو مباينتها لها، ولا تتوقف عقليه [إحداهما] على تصور الأخرى.
وإن كان كذلك، فقد قررنا فيما قدمنا أنه يجب فهم الخطاب قبل الشروع','1','792',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (566,'في العمل. فإذا فهم الخطاب على حقيقته، نسب الحاصل إلى ما فهم. فإذا وجد موافقا له، قضي بكونه امتثالا. فليفهم أولا معنى قوله (صل)، فإذا أحيط به فهما، نسبت الصلاة الواقعة في (٦٧/أ) الأرض المغصوبة إلى الطلب السابق، [ووجب] القضاء بالامتثال. وليفهم [أولا] النهي عن الغصب،','1','793',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (567,'ثم ينسب الفعل الواقع في الأرض المغصوبة إليه، فتصح النسبة، فيقضى عليه من هذه الجهة بكونه معصية، لا باعتبار خصوصية الصلاة.
والمثال المذكور فيه مزيد بيان، وتمام إيضاح، وهو: إذا أمره بالخياطة ونهاه عن دخول الدار مطلقا، فهو يفهم طلب الخياطة أولا، ويفهم النهي عن دخول الدار أيضا، فإذا خاط في تلك الدار، فقد أتى بالمطلوب، لأن معقول الخياطة قد حصل، والمخالفة بالدخول قد تحققت، ولا شك أن طلب الخياطة لا تعلق [له] بمكان مخصوص، بل تعلقه بالخياطة في تلك الدار، كتعلقه بها في غيرها. وهذا واضح.
ومعنى قول الإمام: (وليس للفعل من نسبة الخطاب إليه صفة، حتى يقال يتصف بكونه حسنا، باعتبار تعلق الأمر به، وينصف بكونه قبيحا، باعتبار تعلق النهي به). والأمر على ما قال الإمام من: (أن الفعل لا [يكتسب] من','1','794',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (568,'تعلق الحكم به صفة). ولو صرنا إلى صبوت الصفات، لم يتناقض، إذا صح تعدد الجهات، وتنزيلها منزلة تعدد الذوات. وسيأتي هذا في مسألة السجود بين يدي الصنم.
وقوله: (وما ذكرناه وما لم نذكره [تضبطه] الآن [أقسام] ثلاثة: [الأول]: وهو أنه لا يتصور أن يجتمع الأمر والنهي على فعل واحد من وجه','1','795',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (569,'واحد، بل هما يتعاقبان [ويتضادان]. [والثاني]: وهو أن يرد أمر بفعل، ثم يرد [نهي] عن إيقاع ذلك المأمور به على وجه، فهذا يتضمن إلحاق شرط (٣٨/ب) بالأمر الأول [ويكون] الإتيان به مع تفويت شرطه منهيا، كما لو باشر النهي عنه). هذا القسم مما اختلف الناس فيه. فذهب الشافعي ومالك إلى ما قاله الإمام من بطلان العمل عند [فوات] الوصف المطلوب. وخالف في ذلك أبو حنيفة، وزعم أن ذلك يتضمن فساد الوصف [لا انتفاء] الأصل. واقتصر الإمام على ذكر الحكم ولم يذكر الخلاف ولا الدليل.','1','796',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (570,'وهذه المسألة غامضة جدا، ولنصورها أولا، فنقول: إذا قال الله تعالى: {وليطوفوا بالبيت العتيق}. ثم نهى عن الطواف مع الحدث، أو أمر بالبيع ونهى عن إيقاعه مشتملا على زيادة في العوض في الربويات، وأمر بالصيام ونهى عن إيقاعه يوم النحر، وشرع الطلاق ونهى عن إيقاعه في حالة الحيض، فيقال: الصوم من حيث أنه صوم مشروع، ومن حيث وقوعه يوم النحر غير مشروع، وكذلك الطواف مشروع بقوله تعالى: {وليطوفوا بالبيت العتيق}. ولكن من جهة وقوعه مع الحدث غير مشروع، إلى بقية الأقسام. فالفساد عندنا يرجع إلى الأصل. وأبو حنيفة جعل هذا درجة بين الممنوع بأصله ووصفهن وبين المشروع بهما جميعا، ولهذا قضى بصحة صوم يوم النحر، ووقوعه طاعة، وإن [كان] المكلف عاصيا، وحيث أبطل صلاة المحدث','1','797',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (571,'دون طوافه. وزعم أن الدليل قد دل على الشرطية، لا من جهة النهي عن الوصف، فقال: قال صلى الله عليه وسلم: (لا صلاة إلا بطهور). والشافعي ومالك حين نفذ الطلاق في الحيض، بينا انصراف النهي عن الطلاق ووصفه، الذي [هو] أمر يجاوره، وهو تطويل العدة، أو لحوق الندم عند الشك في الولد.
وفي المسألة نظران يتعلق أحدهما بالأمر المعقول، وهو أنه هل (٦٧/ب) يتصور النهي عن الوصف مع بقاء الأصل مطلوبا، أو كل نهي رجع غلى الوصف [رجع] إلى الأصل؟ فإن كان كذلك، استحال أن يقضى بالطاعة والمعصية جميعا مع اتحاد الجهة، إذ من المحال أن يقول: أمرتك بالخياطة وأنهاك عنها. ويصح أن يقال: اجمع بين المطلوب والمكروه، إذ دخول الدار أمر زائد على الخياطة. كما سبق تقريره. وإذا قال: آمرك بالخياطة وأنهاك عن','1','798',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (572,'إيقاعها وقت الزوال، فهل يقال: جمع بين المكروه والمطلوب، أو ما أتى بالمطلوب؟ فالصحيح عندنا أنه ما أتى بالمطلوب. والدليل عليه أن إيقاع الخياطة هو عين الخياطة، وليس إيقاع الخياطة زائدا عليها. ولو قال: آمرك بالخياطة وأنهاك عنها وقت الزوال، تضمن ذلك تخصيص الطلب الأول ببعض الأوقات، إذ لو كانت مطلوبة في كل وقت، لأفضى أن تكون الخياطة مطلوبة وقت الزوال، ومنهيا عنها في ذلك (٣٩/أ) الوقت، وذلك محال.
ومن زعم أن الخياطة مطلوبة، فلا بد أن يبين للنهي مصرفا آخر، يمكن انفصاله عن الخياطة، وذلك غير ممكن. وكذلك إذا قال: {وأحل الله البيع} مطلقا، ثم نهى عن البيع المشتمل على الزيادة في الربويات، اقتضى ذلك تخصيصا وتقييدا في الخطاب الأول، فيكون البيع المشتمل على الزيادة ليس بمشروع على حال. فإن الشيء لا يصح أن يكون مشروعا ممنوعا، فيفتقر- على','1','799',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (573,'رأيهم- إلى أن يبين أن بيع الربا مندرج تحت آية الحل، ومندرج تحت آية التحريم، وذلك متناقض لا شك فيه.
وأما النظر الثاني: وهو أن مطلق النهي، هل ينصرف إلى العين، أو إلى الوصف، أو إلى المجاور؟ وسنتكلم عليه بعد هذا، إن شاء الله تعالى.
قال الإمام [رحمه الله]: (فإذا [تبين] ما ذكرناه، انعطفنا بعده على القول في الصلاة في الدار المغصوبة. وقلنا: لم يثبت النهي عن الكون في الدار المغصوبة) إلى قوله (لم تصح، كما لا تصح صلاة المحدث، لما صح النهي عن الصلاة مع الحدث). قال الشيخ: قوله: لم يثبت النهي عن الكون في الدار المغصوبة، باعتبار مقصد الصلاة. فقد تقدم الكلام عليه.','1','800',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (574,'وقوله: (وإن [ورد] نهي مقصود عن الصلاة في [البقعة] المغصوبة، فلا تصح). قصد [بهذا]، [أنه] قد يرد النهي عن الصلاة في مكان، ولكن يقوم الدليل على أن النهي لم يكن لأجل نفس الصلاة، بل لأمر يجاورها، فلا يمنع ذلك صحتها. وهذا بمثابة النهي عن الصلاة في المواضع السبعة. وكذلك النهي عن البيع وقت النداء يوم الجمعة. فالحكم في صحة الصلاة في بعض الصور [يكون] عندما يقوم الدليل على الصرف عن الأصل [إلى الوصف]. ولكن [ما] يقتضي مطلق النهي، هل يتناول الذات حتى','1','801',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (575,'يقوم الدليل على الانصراف إلى جهة أخرى، ويكون منصرفا حتى يفتقر إلى دليل المباشرة؟ فالذي ذهب إليه الجماهير أن الطاهرة مباشرة النهي لعين المنهي عنه، حتى يقوم دليل على خلاف ذلك.
وإنما أخذ هذا من قضائهما بأن النهي تضمن صحة المنهي عنه، وأنه استدل بالنهي عن صوم يوم النحر على صحة صومه، وأنه لو استحال انعقاده، لما نهي عنه، إذ المحال لا ينهى عنه، فإنه مندفع بنفسه، إذ لا يقال للأعمى: لا تبصر، كما لا يقال له: أبصر. وقالوا: إن النهي عن الزنا يدل على انعقاده. وهذا فاسد، بل النهي لا يدل إلا على طلب الترك. فأما أن يدل على كون الشيء مثمرا إن وقع، فهذا (٦٨/أ) محال.
وحجتهم في هذا من حيث الجملة، بعد التقرير الأول، هو امتناع النهي عن المحال، وطلب الإمكان في الأمر والنهي جميعا. وهذا مسلم. قالوا:','1','802',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (576,'والشرع قد تصرف في (٣٩/ ب) الأسماء، فيكون اسم البيع للبيع الشرعي. وهذا كم قررناه أولا، لأنه يجب فهم مدلول الأسماء، ثم ينسب إليها الأمر والنهي، ولا تتغير النسبة بالنظر إلى تعلق الأمر والنهي.
قالوا: وإذا كان كذلك، صار معنى الكلام: نهيتكم عن البيع الشرعي، والبيع الشرعي هو المستجمع لشرائط الصحة. وإذا كان كذلك، تعذر فساده والنهي عنه، ولزم من ذلك أن يكون الكلام مؤولا ولا منصرفا إلى جهة غير جهة البيع. وهذا باطل قطعا، فإنه صرف اللفظ عن مدلوله، ورده إلى جهة غير مذكورة، مع ادعاء أن هذا ظاهر اللفظ، وهذا محال. فيجب تنزيل اللفظ على ظاهره إلا أن يدل دليل على خلافه. هذا هو طريق التحقيق في مقتضى العقل واللغة والشرع. والله المستعان.
قال الإمام: (فهذا تمام المقصود في المقدمة الموعودة، [والآن يرجع بنا الكلام] إلى القول في أن النهي عن الشيء يدل على الفساد) إلى قوله','1','803',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (577,'([وهذا] بلاغ كامل). قال الشيخ: ينبغي أن يفهم معنى الفساد والصحة أولا، ثم ينظر في النهي، هل يتضمن الفساد أم لا؟ فيعبر عن الصحيح: بما أثمر الثمرة المقصودة منه، ويعبر بالفساد: عما تخلفت عنه ثمرته. والفاسد مرادف للباطل عندنا. وعند أبي حنيفة الفاسد: عبارة عن الممنوع بوصفه، المشروع بأصله، كصوم يوم النحر، والباطل: عبارة عما لم يشرع أصله. ورتب على ذلك أن البيع الفاسد ينقل الملك نقلا خبيثا مستحقا للنقض،','1','804',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (578,'بخلاف بيع الخمر، فإنه لا يفيد [شيئا [أصلا، وكذلك بيع الخمر، بخلاف الربا، فإنه قال: يصح إذا سقطت الزيادة. وكذلك البيع بشرط خيار طويل يزيد عنده ثلاثة أيام. قال: إذا سقط اليوم الرابع قبل حلوله، صح البيع.
فإذا تبين ذلك، فقد اختلف الناس في النهي، هل يدل على الفساد وتخلف الثمرات أم لا؟ فقال قائلون: إنه يدل. وقال آخرون: لا يدل. وفرق آخرون بين ما نهي عنه لعينه أو لوصفه.','1','805',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (579,'والصحيح عندنا التفصيل. وهو أن ينظر إلى ثمرة السبب ومقصوده، هل ثمرته الثواب؟ [أو] [المدح] شرعا؟ أو براءة الذمة وسقوط القضاء؟ أو ثمرته ترجع إلى حصول أملاك، واستباحة فروج، وما يرجع إلى فوائد العقود؟ فإن كانت الثمرة من الجنس الأول، فالنهي يقتضي تخلفها، فينبغي أن يقتضي فسادا، إذ لا يصلح أن يكون الفعل حراما طاعة، [أو] منهيا عنه امتثالا، أو مذموما على فعله ممدوحا، أو معاقبا عليه مثابا، هذا متناقض لا إشكال فيه، فيكون النهي في هذا الصنف يدل على الفساد إلا أن يتبين انصراف النهي عن ذات العمل ووصفه إلى أمر يجاوره ويلازمه، فلا يقتضي ذلك فسادا، لكون العمل على الحقيقة غير منهي عنه.
وأما ما يتعلق بالعقود (٤٠/ أ)، فهذا محل غامض. وقد صار كثير من المتكلمين والفقهاء إلى أنه لا يدل على الفساد، وقالوا: تخلف الثمرات عند كون الفعل منهيا عنه، إما أن يكون لتناقض من جهة العقل، [أو من جهة اللغة،','1','806',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (580,'أو من جهة تقدير (٦٨/ب) الشريعة. لا يصح أن يقال تناقض من جهة العقل] أن يكون البيع إذا وقع منهيا عنه [مفيدا] للملك، فإنه يجوز من جهة العقل أن نقول: هذا البيع محرم لعينه، ولكن إذا وقع أفاد الملك للفريقين.
وكذلك نقول: ينهى الأب أن يطأ جارية الابن، لكنه إن وطئ واستولد [تم] الملك، والتحق الولد. فليس في ذلك مناقضة عقلية. وكذلك لا يتناقض من جهة اللغة، فإنه يصح للعربي أن يقول مثل ذلك. على أن الأحكام الشرعية لا تترتب على اللسان ولابد، وإنما هذه أمور ترجع إلى وضع الشرع، فله وضعها كيف يشاء. فإن كان يقضي فسادا، فلابد [و] أن يسند هذا إلى الشرع، وإذا ورد الشرع به، تلقى بالقبول. وذلك إما أن يكون نقلا صريحا أو استنباطا. أما الإجماع فمفقود، والنقل الصريح: إما تواترا أو آحادا، ولم يثبت شيء من ذلك. فلزم منع الإطلاق بأن النهي يدل على الفساد في هذه الأسباب. وهذا كلام قوي، والإمام لم يدل على ذلك، ولم يتعرض لما قررناه. وإنما قال: (نحن إنما نعني بالفساد: الحيد عن سنن','1','807',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (581,'الامتثال). وهذا كلام ضعيف، لأن هذا لا يطرد في العقود المشروعة على جهة الإذن دون الطلب، كالبيع والإجارة وغيرهما.
وإذا كان هو إنما يريد بالفساد تحقيق المخالفة، وخروج الفعل عن كونه امتثالا، لم ينازع فيه، ولكن لا يحصل منه غرضه في كل الأبواب. فإن هذا إنما يجري في العبادات دون المعاملات. [وهو] [سيتكلم] بعد على أن البيع الفاسد لا ينقل الملك، ويحيل الدليل [على] هذا المكان. وهذا لا ينتفع به على حال.
وإذ تحقق ما قلناه، فالصحيح من مذهب مالك أن النهي يدل على الفساد. وتقريره هو أن هذه العقود ورد الشرع بها تحصيلا لمصالح الخلق منها، لما علم في ذلك من لطف واستصلاح. فإذا ورد النهي عنها، فقد منع من الإقدام عليها، فقد علم أن لا مصلحة له فيها، أو مفسدتها [تربو] على مصلحتها. فلو أفادت المقصود عند الإقدام عليها، كان ذلك محركا للنفوس لتعاطيها، ومفسدتها الراجحة تمنع من الإقدام عليها، فيتناقض من قبل الشارع','1','808',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (582,'الصارف والباعث، وحكم الشرع على خلاف ذلك. قال المغيرة من أصحاب مالك في النكاح الفاسد: إنه لا يحل المبتوتة، قال: ولا يكون ما حرم الله تعالى طريقا إلى ما أحل. يشير إلى ما قررناه.
ويظهر من كلام الشافعي أنه سلك بالعقود مسلك العبادات. ولهذا إنما تفيد إذا جرت على وفق الشرع، نظرا إلى ما تمهد فيها من الشروط، وقيدت به من القيود، ومنع الخلق من كثير مما يرضون به، فلما تمهدت هذا التمهيد، أشبهت العبادات، فيكون انتقال الأملاك متوقفا على جريان عقود مشروعة. ولهذا منع من انعقاد النكاح بلفظ (٤٠/أ) الهبة، إلى غير ذلك من مسائله. ولقد بنى على هذا أنه عد أبواب الخيار والأجل من الرخص.
فإذا تحقق ذلك، فكلام الإمام خارج عن كلام الفريقين، لأنه عمم القول بأن النهي يدل على الفساد، وخصص الدليل يكون المنهي عنه لا يكون امتثالا. وهذا إنما يجري في العبادات دون المعاملات.','1','809',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (583,'قال الإمام: (مسألة: مما يتعلق بالمناهي (٦٩/أ) الرد على الكعبي في مصيره إلى أنه لا مباح في الشريعة) إلى آخر المسألة. قال الشيخ: ما ذهب إليه من ر مباح في الشريعة، له مأخذان:
أحدهما- وهو الصحيح عنده- أن المباح به، ولكنه دون الندب، كما أن المندوب مأمور به، ولكنه دون الواجب. وهذا بناه على أن المباح حسن، ويصح أن يطلبه الطالب [لحسنه]. هذا هو الطريق الذي اعتمده الكعبي في الفتوى. وهذا الذي قاله غير معقول، فإن هذا المطلوب: إما أن يترجح فعله على تركه، أو لا يترجح، فإن لم يترجح، فهو المباح بعينه، وإن ترجح، فلا يخلو: أن يلحق الذم بتركه على وجه ما أو لا يلحق، فإن لحق الذم','1','810',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (584,'بتركه على وجه ما، فهو الواجب، وإلا فهو المندوب. ومن تخيل مرتبة في الطلب بعد هاتين، فلا عقل له بوجه.
وأما الطريق الثاني- وهو أن المباح يقع تركا لمحظور، فيقع من هذه الجهة واجبا. فهذا يمنع من أن يكون التخيير ثابتا في الشرع على حال، وهو فاسد لأدلة:
أحدها- أنه يفضي إلى تناقض، فإنه قد يترك بالفعل واجبا وحراما، قليكن حراما واجبا، ويمنع أيضا من إثبات المندوب والمكروه، فترجع الأحكام إلى قسمين، ثم يتأتى التناقض في القسمين، فإذا تعاطى محرما، فقد ينكف به عن حرام آخر، فليكن واجبا من جهة انكفافه به عن محرم، وليكن حراما. وكذلك يقال في الواجب، فإنه قد يترك بفعله واجبا.','1','811',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (585,'وأيضا فإن هذا مخالف لإجماع الأمة، فإن المباحات مقصودة منتحاة [بقصد] الإباحة، وليست مقصودة بإيجاب. وما قررناه في مسألة الصلاة في دار المغصوبة من الالتفاف إلى المقاصد يبين هذا كله. فلا ينبغي أن تقع الالغفلة عن مقصد الطالبين في مطالبتهم.','1','812',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (586,'قال الإمام: <span data-type="title" id=toc-113>(مسألة: المكروه لا يدخل تحت الأمر المطلق عند المحققين) </span>إلى آخر المسألة. قال الشيخ: الأمر على ما ذكره الإمام من حيث أن المكروه مطلوب تركه، فكيف يندرج تحت الأمر الذي هو طلب الفعل؟ والجمع بين طلب الفعل وطلب الترك في فعل واحد من وجه واحد متناقض.
فإذا تحقق ذلك، وجب أن يكون المأمور بفعل إذا فعله على وجه، كره الشرع إيقاعه عليه ألا يكون ذلك الفعل امتثالا، ولا يكون الفاعل ممتثلا. هذا','1','813',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (587,'لا إشكال فيه. وينعطف من كراهية (٤١/ أ) الإيقاع على هذا الوجه قيد على الأمر المطلق. وهذا بمثابة ما مثله في القسم الثاني في مسألة الصلاة في الدار المغصوبة. وكما قررناه فيما إذا أمر بالطواف ونهى عن إيقاعه مع الحدث. وإذا تقرر أن الوضوء المنكس مكروه عند الشرع، فلا يصح أن يكون مأمورا به بحال، ويكون قوله تعالى: {إذا قمتم إلى الصلاة}. الآية، يتضمن وضوءا ليس هو المنكس، فلا يبقى للآية محمل إلى الوضوء المرتب.','1','814',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (588,'وقول الإمام: (إنه لا يمتنع الإجزاء مع الحكم بالكراهة، ومن تتبع قواعد الشريعة ألفي من ذلك أمثلة تفوق الحصر). وليس الأمر على ما قاله بوجه. بل يستحيل أن يكون المكروه طاعة على حال. فكيف يصح اجتماع الحكمين وهما متضادان؟ نعم، إن تعددت الوجوه، أمكن ذلك. كما قررناه','1','815',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (589,'في الوجوب والتحريم في مسألة الصلاة في الدار المغصوبة.
والسر في ذلك (٦٩/ ب) أن الأحكام الخمسة متضادة، لا يصح اجتماعها، ولا اجتماع اثنين منها بحال. نعم، إن تعددت الجهات، وأمكن الفصل، صح التعدد. ومسألة الوضوء المنكس لم يكن [مكروها] من جهة كونه تنكيسا، وإنما كانت الكراهة بالنظر إلى مخالفة السلف الصالح، لا من جهة خصوصية التنكيس. فليفرض الأمر الأول عاما شاملا للمنكس والمرتب، من غير تعرض [لصفة] مخصوصة. كما قلنا في الصلاة في الأرض المغصوبة. فإنه لم يتعرض لمكان مخصوص [لما كان] الأمر','1','816',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (590,'مرسلا في تناول الصلاة، وقعت في تلك البقعة أو غيرها، ثم نهى عن غصب، لا باعتبار صلاة ولا غيرها. كذلك أمر بالوضوء، ونهى عن مخالفة السلف الصالح، أو نهى عن (وقوع العمل على وجه يخالف في صحته جماعة من حملة الشريعة، من غير عسر ولا عذر في ارتياد الموافقة). وهذا كلام واضح.

قال الإمام: <span data-type="title" id=toc-114>(مسألة: من توسط أرضا مغصوبة على علم، فهو معتد، مأمور بالخروج) </span>إلى قوله ([وكذلك] الذاهب إلى صوب الخروج ممتثل في وجه، عاص [ببقائه] من وجه). قال الشيخ: ما ذكره الإمام صحيح،','1','817',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (591,'وإذا تحقق الامتثال من كل وجه، استحالت المعصية. ولله حكم على هذا المتوسط، فإذا أمره بالخروج، وحرم عليه المكث، ولم يتوجب واجب آخر، وفعل ما طلب منه من كل وجه، فكيف يتصور أن يكون عاصيا؟ وذهاب أبي هاشم إلى (أنه إلى الانفصال عاص). وهو مقتضى قواعده الفاسدة، إذ تصرف التصرف في ملك الغير بغير إذنه قبيح، [والقبيح] لا يصح أن يكون مطلوبا.','1','818',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (592,'ولكن أوقعه في ذلك أمر عظيم، وهو تكليف ما لا يطاق، فإنه قال: لو خرج عصى، وحرم عليه الشيء وضده جميعا. وهذا أشد من القول في الصلاة في الدار المغصوبة. فإذا قلنا هناك: الواجب الصلاة، والمحرم الغصب. وهاهنا الواجب الخروج، وهو تصرف في ملك الغير، فكيف يصح أن (٤١/ ب) يكون واجبا محرما؟ ونحن نقول: ليس التصرف في ملك الغير حراما لصفة وهو عليها، وإنما حرم بتحريم الشرع. وهذا التصرف واجب، فلا يصح أن يكون معصية، فيؤمر بالخروج، لأنه تقليل الضرر، وفي المكث تكثيره، وأيسر الضررين يصار إليه، فرارا من ضرر أشد منه. ولذلك أنه لو على إتلاف ملك الغير، وجب أو جاز.
وقول الإمام في الغاصب: (إذا ندم وتاب، وأتى بنوبته على شرطها، فإن','1','819',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (593,'ما يتعلق بحق الله تعالى يتنجز سقوطه، إما مقطوعا به على رأي، أو مظنونا على رأي). هذه المسألة تتعلق بالكلام على التوبة وأحكامها وهي كثيرة، وإنما تتكلم هاهنا على حكم واحد، وهو كونها [مكفرة لما سبق من الذنوب. فا] لتوبة بهذا الاعتبار تنقسم قسمين: أحدهما- توبة عن الكفر. والثاني- توبة عن الذنوب سواه.
فأما التوبة عن الكفر، فمجمع على أنها تجب ما قبلها، وهذا مقطوع به.
وأما التوبة [عما] سواه، فلا يخلو: إما أن تكون توبة عن قتل المؤمن متعمدا، أو غير ذلك، فإن كانت توبة عن القتل، فأكثر أهل العلم يقول: هي [مفيدة]. وذهب بعض العلماء إلى أن القاتل لا توبة له، وهو ضعيف. وقد قال النبي - صلى الله عليه وسلم -: (التوبة تجب ما قبلها). (٧٠/أ)
والقسم الثاني من هذا القسم: التوبة عن الذنوب سوى القتل، فقد قال قائلون: إنها تجب قطعا، وقال آخرون: إنها تجب ظنا. والصحيح عندنا','1','820',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (594,'القطع بالمحو، ومستندنا انعقاد الإجماع على المحو، وإن اختلفوا في القطع والظن، فمن قال: إنها غير ماحية، فقد خرق الإجماع. وهذا سيأتي له مزيد تقرير في كتاب الإجماع، وفيه نبين أنه إذا اتفقت الأمة على قولين، لم يجز إحداث قول ثالث.
فإن قيل: فبعض الأمة ظانة، فكيف ينتج القطع مستندا إلى قولها، والقائل لم يقطع؟ قلنا: يلزم من هذا إذا اتفقت الأمة على قول مظنون، أن لا يكون حجة. وهذا يأتي تقريره. ونحن إنما نختار أن الإجماع حجة على كل حال، فظن بعضهم لا يزيد على ظن جميعهم.
وأما قول الإمام: (وأما ما يتعلق [بمطالبة] الآدمي، فالتوبة [لا تنجيه] إلى آخره. فقد تقدم الكلام على ذلك. وقول الإمام هو قول','1','821',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (595,'أبي هاشم حرفا حرفا، وهو يرى استمرار المعصية إلى وصول المال لربه، كما يرى أبو هاشم المعصية إلى انفصاله من البقعة المغصوبة. والرد عليه كالرد عليه.
وقوله: (وهذا يلتفت [عليه] الصلاة في الدار المغصوبة، وأنها تقع امتثالا من وجه، وغصبا واعتداء من وجه). فقد تقدم الكلام على الفرق بين المسألتين. ثم إن الإمام حكم بأن الأمر يتناول الصلاة، فهو مأمور بها على التحقيق، والنهي يتناول الغصب، وهو منهي عنه، وذلك (٤٢/ أ) الكلام','1','822',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (596,'صحيح، جار على الأصول. وهذه المسألة [متعددة] الجهات كتعددها هناك، فينبغي أن [يقول: ] إن النهي متوجه عليه [مع] الأمر، فلم افتقر إلى أن يقول العصيان مستمر، والنهي متوجه؟ فقد اعترف بالفرق بين المسألتين.
قال الإمام: (فإن قيل: إدامة حكم العصيان عليه [من] ارتكابه نهيا) إلى آخر المسألة. قال الشيخ: أما السؤال الذي وجهه الإمام على نفسه،','1','823',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (597,'فلازم، ولم يحصل عنه انفصال. وقد مر في ذلك ما فيه كفاية وبلاغ. ومسألة أبي هاشم التي ألقاها، تخرج عليها، والنظر إلى أرجح الضررين يدفعه، وإن قدر استواء الضرر في المقام الأول والانتقال، ففي هذه الصورة تردد. فقال قائلون: يتخير، لاستواء الأعمال.
وقال قائلون: يمكث، لأن الانتقال فعل مبتدأ، خلاف اللبث، فإنه لا يفتقر إلى استعمال قدرة. وهذا ضعيف، لأن مكثه اختياري كانتقاله، فلا فصل. ويحتمل أن يقال: لا حكم لله، ولا يبعد خلو واقعة عن الحكم، وهذه المسألة ليست منصوصة، ولا نظير لها في المنصوصات. ومذهب الإمام في','1','824',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (598,'هذه سقوط التكليف عن صاحب الواقعة، مع استمرار حكم سخط الله عليه. أما استمرار السخط مع سقوط التكليف، فقد تقدم الكلام عليه.
فأما قوله: (إن التكليف ساقط). فعجب، مع كونه يقول: لا تخلو واقعة عن الحكم. وقد سأل أبو حامد الغزالي الإمام أبا المعالي عن هذا، فقال له: كيف تقول: لا حكم وأنت ترى أنه لا تخلو واقعة عن حكم؟ فقال: حكم','1','825',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (599,'الله أن لا حكم. قال أبو حامد: فقلت له: لا أفهم هذا. وهذا أدب حسن منه، وتعظيم المشايخ. والقول بأن حكم الله أن لا حكم متناقض، وجمع بين النفي والإثبات. (لا حكم) نفي عام (٧٠/ ب)، كيف يتصور ثبوت الحكم مع نفيه على العموم؟ وقولنا: حكم الله، أي: حكم لله تعلق بفعل المكلف، فكيف يصير هذا عبارة عن عدم تعلق الخطاب بفعل المكلف؟ هذا لا يهم، لا لعجز السامع عن الفهم، بل لكونه غير مفهوم في نفسه.
وأما المسألة الأخرى: (وهو المواقع النازع مع أول الفجر، وتفرقته بين أن يكون تعمد أو لم يتعمد). فكلام ضعيف، وفرق لا يصح، وذلك أن مدار','1','826',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (600,'المسألة على حرف، وهو النزع، هل هو وطء [أم] لا؟ فإن كان وطأ، فإنه يبطل صوم النازع عند مقارنة الفجر، سواء كان متعمدا لذلك، أو غير متعمد، فإن لم يكن النزع وطأ، فلا يفسد الصوم، سواء كان تعمد أو لم يتعمد. وهذا الذي يقوله مالك والشافعي. فالتفصيل لا وجه له. والله أعلم.

قال الأمام: <span data-type="title" id=toc-116>(مسألة: السجود بين يدي الصنم مع قصد التقرب إليه محرم) </span>إلى آخر المسألة. قال الشيخ: (٤٢/ب) الأمر على ما ذكره من كون السجود بين يدي الصنم، تعظيما له، محرم بلا إشكال، والمحرم لا يكون طاعة بوجه، إذا اتحد الوجه، ولكن للواحد أربعة أحوال: إما أن يتحد الجنس [وتختلف الأنواع، وإما] أن يتحد النوع وتتعدد الصور، وإما أن تتحد الصورة وتتعدد الوجوه.','1','827',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (601,'أما اتحاد الجنس، فلا يمتنع باتفاق، لاختلاف الأحكام باعتبار اختلاف الأنواع، وهذا كإيجاب الصلاة، وتحريم [الربا]، وإن اشتركا في كونهما فعلين اختياريين.
وأما اتحاد الوجه، فيمنع تعدد الأحكام عند من يحيل تكليف المحال.
وأما اتحاد العين، فيمنع التعدد عند أبي هاشم والقاضي، نظرا إلى اتحاد الفعل. وقد تقدم الكلام عليه في مسألة الصلاة في الدار المغصوبة.
وأما اتحاد النوع، فلا يمنع عند علماء الشريعة، وجعله أبو هاشم كاتحاد العين. وهذا هو الذي يقتضيه أصله؛ فإن الأحكام تتبع الصفات، التي هي الحسن والقبح. وهذه الصفات تلحق في الوجود عند الاشتراك في حقيقة النوع، إذ لا يتعدد إلا الصور، فلا يصلح أن يتبع الحسن صورة، ولا يتبع مثلها، كما لا يصح أن يتحيز أحد الجوهرين دون الثاني. هذا أصله. ونحن نرد عليه بوجوه:
أحدهما- منازعته في الأصل الذي بنى عليه مذهبه.
الثاني- إلزام النقض عليه، فإنه قد حكم بأن القتل يحسن تارة، ويقبح أخرى، وحقيقته لا تختلف. فلئن قال: اختلف الأمر باختلاف سبق الجناية','1','828',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (602,'ولحوق العوض. قلنا: هذا لا يوجب اختلاف صفة القتل. ثم إن ساغ ذلك، ساغ أيضا أن يختلف وصف السجود بحسب اختلاف الإضافة، كما يجب صوم رمضان، ويحرم صوم يوم العيد.
الثالث- أنه لم تحكم بأن صفته الحسن؟ وبم تنكرون على من يزعم أن صفته القبح؟ وإنما [يطيع] الساجد لله بالقصد. فلئن قال: أمر الله بالسجود له. قلنا: ونهى عن السجود للصنم. بل النهي هو السابق.
الرابع-انعقاد الإجماع على أن الساجد للصنم عاص بفعله وقصده جميعا.
الخامس- أن هذا (يوجب الخروج الأفعال الظاهرة عن كونها قربا أو محرمات، وإنما يرجع الأمر إلى التعظيم والاستهانة. وهذا خروج عظيم عن الدين).','1','829',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (603,'قال الإمام: (مسألة: إذا اتصلت صيغة (لا) في النفي بجنس من الأجناس، فقد اضطرب فيها رأي أصحاب الأصول) إلى قوله (ورد معنى اللفظ (٧١/ أ) إلى الحكم). قال الشيخ: (لا) إذا دخلت على اسم منكور، إنما تكون لنفي الجنس، إذا كان الاسم معها مبنيا على الفتح، أما إذا وقع الاسم بعدها معربا، فإنها لا تقتضي نفي الجنس. هكذا ذكر أئمة العربية.
وظاهر كلام الأصوليين أنهم اكتفوا بالتنكير مع النفي. وقد تقدم تقرير هذا فيما سبق.
وقول الإمام: (إن النفي لا اختصاص [له]، إنما [انضم] للنكرة،','1','830',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (604,'فاقتضى اجتماعهما (٤٣/ أ) استغلاقا). والمصير إلى دعوى [الاجتماع] [بناء] على تعمد التمسك، إذ هو يتضمن نفي الصوم حسا ووجودا. وليس الأمر كذلك، فلم يدر ما المراد به بعد ذلك.
وهذا يرجع إلى أصل، وهو أن الأسامي الشرعية، [هل] بقيت ظاهرة على الوضع اللغوي؟ وإنما تصرف إلى غيرها بدليل، وإذا صير إلى ذلك، فقد','1','831',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (605,'تقرر إجراء اللفظ على ظاهره، ويرد عليه بوجهين:
أحدهما: أن القرينة من الرسول - عليه السلام - ترشد إلى أنه إنما قصد نفي الحكم، دون التعرض للحس نفيا وإثباتا. وهذا مقطوع به.
والثاني: أنا قد بينا فيما تقدم أن اللفظ إذا دار بين معناه اللغوي ومعناه الشرعي، فالظاهر هو الجريان على الوضع الشرعي، إذ هو غالب عرف الاستعمال.
وأما قول الإمام: (أنا إذا تحققنا وقوع الجنس الذي ذكروه، فقد اضطررنا إلى أن الرسول لم يرده). وهذا ليس ردا عليهم، بل هو عين ما قدروه، وهو يثبت كونه مجملا، إذ تعذر حمله على ظاهره، ولم يعرف مراد الناطق به.','1','832',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (606,'قال الإمام: (قيل: هذا [تشكك] في صدق الرواة. [قيل]: المسألة في اللفظ المقطوع به جارية، كقوله تعالى: {لا إكراه في الدين}، وغيره). قال الشيخ: معنى الكلام: أن القوم [كأنهم] صاروا إلى تقرير اللفظ على الوضع اللغوي، فلا يصح نفي ما هو موجود من الصادق، فلا يكون صادقا. ولكن هذا الطريق لا يفضي إلى التشكك في الرواة، بل يفضي إلى التكذيب.
وقول الإمام: (المسألة في اللفظ به جارية). يبطل هذا الكلام، إذ لا سبيل إلى الشك في الصدق فيه.
قال الإمام: (وذهب نازلون عن هذه المرتبة إلى صرف دعوى الإجمال','1','833',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (607,'إلى تردد اللفظة بين نفي الجواز ونفي الكمال) إلى قوله (والرسول - عليه السلام -[تعرض] لنفي الصوم). قال الشيخ: إنما صار القاضي إلى الإجمال، لأنه نفى الأسماء الشرعية. والذي دل ظاهر اللفظ على نفيه موجود، فافتقر إلى التقدير وتعدد المقدر.
ولننبه هاهنا على أصل، وهو إن تعدد مدلول اللفظ من جهة الحقيقة، واستوت النسبة، لزم الإجمال. وإن [تفاوتت]، ثبت الظهور، باعتبار الراجح، وكان الوجه الآخر خفيا، فإن دل دليل على وجوب الرد إلى جهة الاحتمال، فلا يخلو: إما أن يتحد، أو يتعدد على تساو، أو تفاوت. فإن اتحدت الجهة، صار اللفظ نصا في جهة احتماله عندما يتعذر على وجه مقطوع به حمله على جهة ظهوره. وهذا كقوله تعالى: {وسئل القرية}.','1','834',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (608,'وإن تعددت جهة الاحتمال واستوت، لزم الإجمال بين الجهات، كما لزم الإجمال في اللفظ المشترك. وإن تفاوتت النسبة، كان اللفظ ظاهرا في الاحتمال اللغوي (٤٥/ب) عند قيام الدليل الدال على منع التمسك بالظاهر، ولا يصار إلى الآخر إلا بدليل، وهذا كالعام (٧١/ب) إذا خصص، فإن الاحتمالات في الرد على أحد الأبعاض متعددة، ولكن الظاهر في عرف الشريعة تناول ما دون المخرج.
وكذلك قوله تعالى: {حرمت عليكم أمهاتكم}. فإنه لما استحال تعلق الأحكام بالأعيان، لزم الرد إلى بعض الأفعال، ولكن عرف أهل اللغة في مثل هذا متعين. فإن قال القائل: إذا قال حرمت عليك الطعام، أراد أكله، وإذا قال: حرمت امرأة، أراد وطأها. وقد ذهبت المعتزلة إلى أنه مجمل، وليس الأمر كما قالوا، لأن عرف الاستعمال يعرفنا المضمر، كصريح النطق في','1','835',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (609,'مسألة (لا صيام)، [لما تعذر نفي الصوم حسا، وجب] الرد إلى الحكم، وهو متعدد، لدوران الأمر بين الجواز والكمال. فلئن قال قائل: [ما] المانع من نفيها جميعا؟ فنقول: إنما وجب التقدير ضرورة طلب فائدة الخطاب، والفائدة تحصل بنفي أحدهما، فلا ضرورة تلجئ إلى نفي الثاني، ولا معنى للتحكم بالتعيين. ووجه يمنع نفيهما جميعا، سنذكره في آخر المسألة.
قال الإمام: ([فالمذهب] المختار: أن اللفظ ظاهر في نفي الجواز، مجاز في نفي الكمال، كما سنوضح مراتب التأويلات ومناصبها في كتاب التأويلات، إن شاء الله). قال الشيخ: هذا إنما يصح للإمام إذا أثبت الأسامي','1','836',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (610,'الشرعية، ويبين أن الغالب في الشرع إطلاق الألفاظ على المعنى الشرعي، وهو لم يدل على هذا فيما سبق، وإنما قرر أن الشرع تصرف، وجعل للألفاظ مفهوما آخر. وقد تقدم كلامنا على هذا. فإذا ثبت عليه عرف الشرع، صار معنى الكلام: لا صوم شرعي، والذي ليس بكامل صوم شرعي، فيجب على مقتضى ذلك، أن ينتفي الأجزاء، ويصير نفي الكمال تأويلا، وهذا كلام صحيح.
قال الإمام: (وذهب جماهير الفقهاء إلى أن اللفظ عامة تتناول نفي الوجود ونفي الحكم) إلى قوله (فكيف يفهم معه [نفي الحكم]؟ ). قال الشيخ: تردد الناس في اللفظ المجمل، كالمشترك إذا دخل عليه حرف نفي، هل يكون مشتركا في الأجناس، كما كان مشتركا في الآحاد عند الإثبات؟ فلا يعرف أي جنس نفي، فيتوقف في الأجناس؟ أو يكون ذلك يتضمن نفي الأجناس كلها؟ كما إذا قال: لا تمس النساء- إذا صرنا إلى أن المس يتناول المس باليد والوقاع- أو نقول: دخل النفي على واحد مشترك، فانتقل الواحد لأجل النفي، فصار جنسا، فيثبت الاشتراك باعتبار الأجناس؟
فمذهب هؤلاء القوم أن المشترك عام، وهو اختيار القاضي والشافعي.','1','837',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (611,'وستأتي المسألة (٤٦/ أ) بعد هذا. فعلى هذا يكون اللفظ يتناول الوجود والحكم، ثم يعلم أن الوجود غير مراد، فكان ذلك تخصيصا بمسلك الحس وقضية العقل.
ومعنى قول الإمام: (لأن العموم إنما يتناول [مسميين] يتصور اجتماعهما في النفي). وهذا كلام صحيح، وذلك أن العموم إنما وضع للاختصار، وللغنية عن تكرار اللفظ، فيكون معنى الكلام: لا وجود، ولا حكم. وهذا الكلام على هذا الوجه فاسد، فإنه إذا نفى الوجود، لم يتصور ثبوت حكم، حتى يفتقر إلى نفيه، (٧٢/ أ) وكذلك لا يتصور أن ينفي ذات زيد، ثم يفتقر إلى نفي علمه، مع تحقق نفي الذات.','1','838',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (612,'قال الإمام: (وذهب ذاهبون من الفقهاء إلى أن الوجود غير معني [بالنفي] إلى قوله (واستبان ظهور] اللفظ في نفي الجواز، وكونه مؤولا في نفي الكمال).قال الشيخ: هذا كلام واضح، وتقريره قد سبق. وفي هذا الموضع وجه آخر ذكره الإمام بعد، وهو أن نفي الكمال متضمن ثبوت الجواز، فعندما نقول ينفي الكمال، فقد أثبتنا الجواز، وقد نفيناه في تلك الحال، وهو محال.

قال الإمام: <span data-type="title" id=toc-117>(فصل- قد اشتمل [ما جرى] في أحكام الأوامر والنواهي)</span>','1','839',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (613,'إلى قوله (إذ لو لم يكن كذلك، لكن الوعيد خلفا تعالى الله عنه). قال الشيخ: اختلف الناس، هل يصح العفو في الوعيد؟ فذهب أكثر المتكلمين إلى أن ذلك غير جائز، وأن اللفظ الذي دل على الوعيد، إما أن يكون نصا، وإما أن يكون ظاهرا، فإذا لم يعرف، تبينا التخصيص، والتخصيص: بيان لا رفع. فتبين أنه لم يكن في جملة ما اندرج تحت اللفظ. وإن كان نصا، فهو يدل على ما في النفس دلالة مقطوعة بها، وكلام النفس على وفق العلم، وإذا علم الله تعالى أنه يعاقب شخصا، فلا يتصور العفو عنه، لئلا ينقلب العلم جهلا، ويصير القول خلفا، والله تعالى متقدس عن الأمرين.
وذهب ذاهبون إلى أنه يجوز العفو في الوعيد، ولا يجوز المنع عند الوعد، ويقولون: إن أهل اللغة لا يعدون العفو من باب الخلف، وينشدون قول الشاعر:
[وإني وإن] أوعدته [أو] وعدته ... لمخلف إيعادي [ومنجز] موعدي','1','840',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (614,'وهذا كلام ضعيف، ولا محمل لذلك عندي إلا وجه واحد؛ وهو أن يكون واضع اللغة جعل لفظ الوعيد محتملا إضمار الشريط، ولم يجعل لفظ الوعد كذلك، فيكون معنى الكلام عند الوعيد: أعاقبك إن شئت. ولم يروا ذلك من باب الوعد. وهذا حسن بالإضافة إلى مكارم الأخلاق، فإن لم يكن الأمر كذلك، فهو فاسد قطعا، وخلف في الكلام الأزلي، وبداء صراح، وذلك على الله تعالى محال.
وقد صار الإمام فيما سبق إلى هذا الحد، عندما تكلم القاضي على معنى الواجب وقال: (لو أوجب الله تعالى شيئا، لوجب، وإن لم يتوعد بعقاب على (٤٦/ ب) تركه) من غير وعيد على الترك، لما كان للحكم بالوجوب معنى','1','841',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (615,'معقول في حقوقنا). فقد التزم هناك أن عقلية الواجب [تتوقف] على التقييد بالوعيد.
قال الإمام: (وقال قائلون: الواجب: ما يخاف المكلف العقاب على تركه) إلى قوله (وقد لا يكون كذلك). قال الشيخ: ويفسد أيضا بالواجب','1','842',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (616,'الذي لا يعرفه المرء واجبا، فإنه لا يخاف العقاب على تركه، ولا يخرجه ذلك عن كونه واجبا فلم يجمع الحد، ولم يمنع.
قال الإمام: (فالمرضي في معنى الواجب: أنه الفعل المقتضي من الشارع الذي يلام تاركا شرعا) إلى آخر المسألة. قال الشيخ: قوله: وإنما [قلنا]: المقتضي من الشارع، لأنه معنى الإيجاب. كلام ضعيف، لأنه إذا كان الاقتضاء هو الإيجاب، لزم (٧٢/ب) أن يكون المندوب واجبا، لأنه مقتضي، بلا خلاف، ولم يكن أيضا لقوله: (الذي يذم تاركا شرعا). فائدة جديدة، لأنه قال ذلك لينفصل عن المندوب. فإذا كان لفظ الاقتضاء ينبئ عن الوجوب، فلا دخول في ذلك للمندوب.
ويفتقر إلى معرفة اللوم الذي يريده الأصوليون، وليس المراد به التوبيخ من قبلنا، ولا إطلاق ألسنة [باللعنة] في ذلك، فإن ذلك حرام، وإنما أرادوا به أنه منسوب إلى المعصية شرعا. وقد زال أبو حامد في هذا المكان، واعتقد أن اللوم من قبلنا، فقال: هذا الحد يبطل بمعاصي الأنبياء، فإنه قد دل على وقوعها','1','843',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (617,'منهم، [ولم] نؤمر بإهانتهم وذمهم، والمعصية محققة ولا لوم. وهذا ضعيف، وليس ما ذكره هو المراد، بل لا يجوز لنا أيضا في حق غيرهم. وإنما المراد ما ذكرناه من النسبة إلى المعصية شرعا.
وقول الإمام: (هو الذي [يذم] تاركه شرعا).جار على أصله، فإنه التزم في الواجب الموسع الذي يضبط آخر وقته أنه إذا مات في وسطه ولم يفعل، كان آثما. أما نحن فلا نرتضي بذلك، على حسب ما قررناه. فنفتقر إلى أن نزيد (بوجه ما). وقد بيناه فيما سبق.
وأما انفصاله عن السؤال (بأنه لا وجوب على من لا يعلم الوجوب). فيبطل من وجهين:
أحدهما- أنه قد أفسد هذا على نفسه فيما سبق، فإنه قال في مسألة','1','844',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (618,'وجوب النظر: (لا يشترط في وجوب الشيء علم المكلف بوجوبه عليه).
الثاني- أن الذي ذكره: أنه لا وجوب على من لم يعلم الوجوب، باطل بأدلة سمعية قطعية، وقد قال الله تعالى: {[الذين] ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعا}. وقال تعالى: {وإن الظن لا يغني من (٤٧/أ) الحق شيئا}. وقال: {[يومئذ] خاشعة. عاملة ناصبة. تصلى نارا حامية}. وقواطع السمع دلت على تعذيب الكفار، وإن كانوا يجهلون وجوب الإيمان، فلا يصح القول بأنه: لا وجوب على من لا يعلم الوجوب بحال. وإنما ينقل هذا المذهب عن الجاحظ والعنبري. وسيأتي الرد عليهما في كتاب','1','845',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (619,'الترجيح، إن شاء الله تعالى.
وكان الإمام قد قال: (ونحن نذكر حقيقة كل حكم من هذه الأحكام). أعني الوجوب والحظر والندب والكراهة والإباحة. ولم يذكر حد شيء منها، وإنما حد الأفعال التي تعلقت بها الأحكام. وهذا غلط بين، وذلك بمثابة من تعرض لحد العلم فقال في حده: المعلوم ما كشفه العلم. فليس هذا [حدا] للعلم بحال. [وإنما الأحكام الخمسة هي التي ذكرناها آنفا]. أما الواجب والمحظور والمباح والمكروه والمندوب، فهي الأفعال التي تعلقت بها الأحكام. فإن قيل: فما الصحيح عندكم في حد الوجوب الذي هو الحكم على الحقيقة؟ قلنا: الوجوب هو القول المقتضي حصول الفعل على وجه يكون المخالف مذموما بوجه ما.
قال الإمام: (مسألة: اضطرب الأصوليون في معنى المكروه) إلى آخر','1','846',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (620,'المسألة. قال الشيخ: المكروه أيضا: هو الفعل الذي تعلقت به الكراهة. والكراهة: هي القول المقتضي ترك الفعل، بحيث يمدح التارك، ولا يذم الفاعل. هذا حد الحكم. ولنتكلم ها هنا على وضع باعتبار اللغة والشريعة.','1','847',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (621,'أما من جهة اللغة، فالكراهة ضد الإرادة. (٧٣/أ) وترجع إلى صوارف تصرف عن الفعل، وليس المراد بها ذلك عند علماء الشريعة، بل تطلق الكراهة في حق الله تعالى على معنيين:
أحدهما- يرجع إلى الإرادة، وعليه نزل قوله تعالى: {ولكن كره الله انبعاثهم فثبطهم}. والمراد: أراد [التثبيط]، فمنعهم الانبعاث، فسميت','1','848',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (622,'إرادة الضد كراهة، باعتبار ضده. وإنما قلنا ذلك، لأن الباري سبحانه واحد، [فلا يصح] أن تقوم به المتضادات. وهي إذا أطلقت في الأحكام، مختلف فيها عند الأصوليين؛ فقد يطلق المكروه على المحظور، فيقال للمحرم مكروه، فكثيرا ما يقول الشافعي: أكره كذا، وهو يريد تحريمه. وكذلك يقول مالك أيضا.
الثاني: وهو المشهور- ما نهي عنه نهي تنزيه، وهذا هو الذي حددناه.
الثالث: ترك الأولى، كترك الصلاة، لا لنهي [ورد] عنها، ولكن','1','849',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (623,'لكثرة ثواب الفعل. فعلى هذا يصح أن يقال: ترك استيعاب الزمان على حسب الإمكان بالنوافل مكروه.
الرابع: ما وقعت الشبهة في تحريمه، كلحم السبع ويسير النبيذ، وهذا أضعفها، فإن من أراده اجتهاده إلى أنه حرام، فهو حرام عليه، ومن أداه (٤٧/ب) [اجتهاده] إلى الحل، فلا معنى للكراهة في حقه. وهذا متجه على قول من يقول كل مجتهد مصيب. أما من قال المصيب واحد، فقد يقول بالكراهية إذا بقي في نفسه حزازة من كلام الخصم.
وليس في مسائل الفقه مسألة أصعب من القضاء بالكراهة، لأجل مخالفة الخصم، ولاسيما إذا كان المجتهد يرى الحل، وغيره يرى التحريم. فإذا ذهب إلى الكراهة، فقد خالف الدليلين جميعا، وإن كان القولان متفقا عليهما، كان المصير إلى الكراهة خرقا للإجماع. ثم الذي يتأتى في هذا المكان التوقف عن','1','850',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (624,'الفعل، وإن كان يغلب على ظنه الحل، لاحتمال التحريم. أما حمل غيره عليه أو الفتوى بالكراهة، فلا وجه [له عندي].
قال الإمام: ([مسألة: تجمع] محامل الصيغ التي يقال [لها] صيغ','1','851',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (625,'[الأوامر]. أما [المطلق] فقد سبق الكلام في محمله، وإنما تتعدد المحامل بالقيود، ونحن نذكر منها جملا تشارف الاستيعاب). وهذا الذي ذكروه واضح، لا يفتقر إلى أكثر من هذا.','1','852',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (626,'وقد ترد بمعنى التعجيز، كقوله تعالى: {فأتوا بسورة من مثله}. وقوله تعالى: {قل كونوا حجارة أو حديدا}.
وترد بمعنى التحكيم والتفويض، كقوله تعالى: {فاقض ما أنت قاض}. ثم صيغة الأمر من جميع مل ذكرناه، ما يقتضي الإيجاب، وفيما يقتضي الندب خلاف، كما تقدم. واللوجوه الباقية ليست من معاني الأمر.
وأما صيغة النهي إذا تقيدت، فإنها ترد على وجوه على مناقضة الأمر، لا يعسر على الباحث طلبها. ومطلقها للحظر، [والمقيد] منها يرد على وجوه:
منها التنزيه، ومنها الوعيد، ومنها الدعاء، كقوله تعالى: {ربنا لا تزغ قلوبنا بعد إذ هديتنا}.
ومنها: الإرشاد، كقوله تعالى: {لا تسألوا عن أشياء إن تبد لكم تسؤكم}.
ومنها: بيان العاقبة، كقوله تعالى: {ولا تحسبن (٦٤/ب) الذين قتلوا في سبيل الله أمواتا [بل أحياء]}.
وترد بمعنى التحقير والتقليل، كقوله تعالى: {ولا تمدن عينيك إلى ما متعنا به أزواجا منهم [أزواجا منهم]}.
وترد بمعنى إثبات اليأس، كقوله تعالى: {لا تعتذروا اليوم}.','1','853',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (627,'قال الإمام: ([كتاب] العموم والخصوص. قال المحققون من أئمتنا: العام والخاص قولان قائمان بالنفس كالأمر والنهي، والعبارات تراجم [عنها]) إلى قوله (ونعود الآن إلى المقصود اللائق بما نحن فيه). قال الشيخ: مذهب أهل السنة أن الكلام: هو القائم بالنفس، والعبارات تراجم. على ما سبق في كتاب الأوامر. والطريق في إثباته واحد.
وما تخيله الإمام مشكلا، فهو أنه قال: (الاقتضاء يجده العاقل من نفسه ضرورة، ويترجم بالعبارة). على ما سبق متضحا. فلا معنى للإعادة.','1','854',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (628,'قال: (وأما العموم والخصوص، فما أراها كذلك في الوضوح). إذ ليس في العموم اقتضاء، والذي نقرر به العموم: (كل ما ثبت العلم به، ففي النفس كلام عنه، والعلم محيط بمعنى الجميع، وفي النفس فكر به، وحديث عنه). ومعنى ذلك أن الإنسان يعلم الجميع، ولا ينفك (٧٣/ب) عن كلام النفس. وقد تقدم بيان ذلك في المقدمة أن العالم لا يخلو عن النظر النفسي.
وقوله: (وليعلم طالب هذا الشأن أن معظم ما يحسبه من لم يعظم حظه في الحقائق علما فهو فكر). وإنما كان كذلك، من جهة أن الإنسان إذا علم أمرا، أخبر عنه، وأعلم به غيره. وما أخبر عنه، إلا من جهة قوله معلوما. فإن كلام النفس خبر عن المعلوم، لا خبر عن العلم. والذي يحقق ذلك أن من صار إلى أن العلم يقوم بالعالم ولا يعلمه، يستحيل منه في حالته تلك أن يخبر عنه،','1','855',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (629,'مع كونه لا يعلمه، ونحن نقول: كل من قام به العلم لابد أن يعلمه، فيكون معلوما (٤٨/أ) فيخبر عنه، من جهة كونه معلوما له.
وقوله: (ومن دقيق ما يتعلق بمدارك العقول، أن فكر النفس متعلقة بالمعلومات والمعتقدات). يريد بذلك إثبات الاختلاف بين كلام النفس وبين العلم، ف''ن العلم لا يتعلق بالمعتقدات، وكلام النفس يتعلق بها.
وقوله: (ولا تنطق النفس بالعلم الحق). الظاهر فيه أنه لا يصح الخبر عن العلم، باعتبار كونه علما، بل باعتبار كونه معلوما.
وقوله: (ومهما ظن ذو الفكر أنه ناطق بالعلم، فهو [متخيل] العلم معلوما). كلام محتمل، فإن أراد به لا ينطق بالعلم بوجه، فهذا محال، إذ','1','856',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (630,'يتضمن ذلك أن العلم لا يعلم. وإن أراد به لا يخبر عنه باعتبار كونه معلوما، فهذا صحيح.
وقوله: ([وهذا الذي] اختلج [في عقول المتكلمين وطيش] أحلامهم حتى اضطربوا في أن العلم بالشيء، هل هو علم بأنه علم به؟ وهذا الذي اختبطوا فيه اضطراب منهم في فكر النفس، لا في العلم نفسه). فيقول: إن العالم لا ينفك عن اقتران كلام النفس، فقد يظن التلازم [و] الاتحاد، وقد لا يظن ذلك، فيقع الاضطراب لذلك.
وقوله: (فنعود الآن إلى المقصود [و] اللائق بما نحن فيه). لم يتكلم الأصوليون على العموم والخصوص، إلا باعتبار الألفاظ اللغوية، إذ هي','1','857',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (631,'الأدلة، وهم إنما يتكلمون على أدلة الأحكام، التي هي الكتاب والسنة، وهي ألفاظ لغوية، فلذلك قال: نعود إلى المقصود، [و] اللائق بما نحن فيه.
قال الإمام: (اختلف الأصوليون في صيغة العموم اختلافهم في صيغة','1','858',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (632,'الأمر والنهي) إلى قوله (وما يقع منكرا منفيا، فهو كذلك [أيضا يتعين] القطع بوضع العرب إياه للعموم [كقولك]: لم أر رجلا). قال الشيخ: مذاهب','1','859',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (633,'الناس كما نقلها الإمام، وطرق الواقفية، على حسب ما مضى في صيغة الأمر، ومذاهبهم في الاشتراك والالتباس كذلك، بلا مزيد. وإنما تنازع الناس فيما ذكره في قسم واحد. وهو [ظرف] الزمان، [كقوله]: متى جئتني أكرمتك، هل يتضمن ذلك كل زمان أو يتضمن ذلك زمانا مطلقا؟ حتى إذا جاءه وأكرمه، لم يبق عليه عهدة من الخطاب؟ فظاهر كلام الأصوليين أنه يعم جميع الأزمنة، والفقهاء لا يرون هذا، وإنما يقولون: يتضمن إكراما واحدا، في أي','1','860',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (634,'زمان كان. قال مالك: إذا قال: متى دخلت الدار، فأنت طالق، فإنها لا تطلق عليه إلا طلقة واحدة، بخلاف قوله: كلما دخلت، فأنت طالق، فإنها تطلق متى دخلت. وهذا ظاهر، وكأنه قال: إن دخلت الدار (٧٤/أ) فأنت طالق. [وأتى بها] يشير إلى أنه لا يختص بعينه بزمان مخصوص.
[وأما] قوله: (وما يقع منكرا ومنفيا). ونحن بينا أن سيبويه وغيره من أئمة العربية أن ذلك إنما يكون على تقدير بناء النكرة (٤٨/ب) مع النفي على الفتح. فأما إذا جاءت معرفة، فلا يفهم منها العموم. والمسألة نقلية محضة، وأهل اللغة بذلك أقعد.
وأما قوله: (واسم مبهم يختص بما لا يعقل [في رأي]. فهذا إنما هو فيما إذا كانت بمعنى (الذي) فهي التي [فيها] نزاع أهل اللغة، كقوله:','1','861',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (635,'عرفت ما عندك، أي الذي عندك. ومذهب سيبويه أنها تعم من يعقل وما لا يعقل. وأما إذا وقع شرطا، فليس من هذا القبيل، فإن الشرط يستدعي الفعل، ولا يدخل على الأسماء على حال.
قال الإمام: (وأما صيغ الجموع، فلو قسمناها على مراسم صناعة النحو، لأطلنا أنفاسنا، ولكنا نذكر مراسم على قدر مسيس الحاجة) إلى قوله (ثم لم يفصلوا الجمع إلى جمع القلة وإلى جمع الكثرة). قال الشيخ: ما ذكره ينقسم إلى ما ليس للأصول به تعلق بوجه، كالقول في علامات الإعراب، واختصاصها بهذه الحروف، واختلاف أحوالها رفعا ونصبا، وامتناع جمع بعضها جمع سلامة. هذا محض إدخال علم النحو في الأصول، ولكنه اقتصر على ذكر الأحكام دون العلل والأسباب. ونحن بعون الله وتوفيقه [نتوجه] ما ذكره على شرط أئمة العربية.
فالجمع كما ذكر ينقسم إلى جمع سلامة وجمع تكسير، ومعنى كونه جمع سلامة: أنه يسلم فيها بناء الواحد، كقولك: زيد، ثم تقول: الزيدون، فقد سلم بناء الواحد. يقال فيه إنه الجمع الذي على حد التثنية. وإذا قلنا: موسيان وعيسيان وعصوان، فبناء الواحد مقدر، ولكنه على غير الإفراد، ولما جاءت','1','862',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (636,'التثنية، رجع الاسم [إلى] أصله. وكذلك إذا قلنا: مصطفون، فهو جمع سلامة، والتقدير: مصطفيون، ولكن استثقلت الضمة على الياء، فحذفت فسكنت، والواو ساكنة، فحذفت الياء، لالتقاء الساكنين، وبقيت الواو لتدل على الجمع والرفع. وكذلك يقال في المنصوب.
وقوله: (وهو ينقسم إلى جمع الذكور والإناث). وليس يعني بالذكور: ما له ذكر، ولا بالإناث، ما له فرج، بل المراد بكونه جمع مذكر: أن الاسم عري عن علامة التأنيث لفظا وتقديرا. ويراد بالاسم المؤنث: ما فيه العلامة لفظا أو تقديرا، واختص المؤنث بالعلامة، لأنه منقول عن الأصل، والأصل في الأسماء التذكير، ويدل على ذلك أمور:
منها: ما ذكره أئمة العربية أن لفظ الشيء ينطلق على كل موجود، والشيء مذكر.
ومنها: أن التأنيث صار إحدى الأسباب المانعة من الصرف، ومنع [الصرف] عارض على الكلام، لأنه يمنع الاسم بعض الحركات، ويوجب ضربا من الإلباس.','1','863',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (637,'الثالث: الافتقار في الإرشاد إلى التأنيث إلى الزيادة. فهذه الأسباب تبين الفرعية.
وقوله: ([فأما] جمع الذكور، فبزيادة (واو) (٤٩/أ) قبلها ضمة، و (نون) بعدها في محل الرفع، وبزيادة (ياء) قبلها كسرة (ونون) بعدها في محل النصب والجر). اعلم أن الإعراب أصله أن يكون بالحركات، والسبب فيه أن الاسم تختلف أحواله، وتختلف الأغراض بحسب (٧٤/ب) اختلاف الأحوال، كما تختلف الأغراض باختلاف المسميات. فوضعت العرب الأسماء المختلفة دالة على المسميات المختلفة.
فإذا اختلفت الاسم، فإما أن يرشدوا إلى اختلاف أحواله أو لا يرشدوا، فإن لم يرشدوا، فات غرض مهم، كمعرفة كون الشخص فاعلا أو مفعولا، فلابد من الإرشاد. وإذا أرشدوا، فإما أن يغيروا الأسماء على تقدير اختلاف الأحوال، أو لا يغيروا الأسماء، [فإن يغيروا الأسماء بالكلية، لم يحصل المقصود]، فإنا لا ندري أن الذي كان مفعولا هو الذي كان فاعلا، فلا يحصل المقصود.
وأيضا فإنه كانت الأسماء تكثر كثرة مفرطة، وأثبتوا والاسم الواحد تغييرا يرشد إلى اختلاف أحواله مع بقاء الاسم، فيكون الاسم واحدا، كما','1','864',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (638,'الذات واحدة، ويكون اللفظ متغير الحال، كما كانت الذات متغيرة الحال. وإذا كان كذلك, فحروف الكلمة كأجزائها، وحركاتها بمثابة الأعراض الواردة عليها، فخص الإعراب بالحركات، ليحصل المقصود مع بقاء الاسم، فما أعرب بالحركات، فجار على الأصل، وما أعرب من الأسماء بالحروف، فلعلة، وذلك ثلاثة أنواع: الأسماء الستة المضافة. الثاني: المثنى والمجموع. الثالث: ك (لا) إذا أضيفت للمضمر على اللغة الفصيحة.
فأما المثنى والمجموع، فأعرب بالحروف، فرقا بينه وبين الواحد، ولزيادة دلالة على المفرد.
وأما الأسماء الستة، فأعربت بالحروف، لاعتلالها، ولزوم إضافتها، فكأنها إنما أعربت بالحروف، عوضا لها مما سلبته، وخص ذلك بحال الإضافة، لتقرب من الجمع، باعتبار الزيادة. ووقع الاقتصار في علامات الإعراب على حروف العلة، لأنها قريبة من الحركات، إذ (الألف) عن الفتحة، و (الياء) عن الكسرة، و (الواو) عن الضمة.
وأما قوله: (وأما الاسم المؤنث: فهو ينقسم إلى ما في آخره علم التأنيث). يريد في اللفظ، وأما في المحل والموضع، فلابد من العلامة،','1','865',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (639,'يدلك على ذلك التصغير والتكسير. وكون جمع السلامة بالألف [والتاء]، مطرد في الإناث على الإطلاق، ولا يختص جمع السلامة في الإناث بمن يعقل، وإن جمع السلامة في الذكور يختص بمن يعقل، أو صفات من يعقل. فعلوا ذلك، إثباتا لشرفية الأعلام بالامتياز. وإنما دخلت التاء هاهنا، وإن لم تكن من حروف العلة، فإن التاء تعرف بتاء التأنيث المفرد، فدخلت في الجمع [مرشدة] إلى ذلك، والألف لابد منها. إذ بها يقع الفصل بين الواحد والجمع.
وأما ما في آخره علم التأنيث، فعلامات التأنيث [ثلاث]: (٤٩/ب) التاء والألف والياء. فالتاء في قولك: (فاطمة وقائمة)، والألف في قولك: (حبلى وخنساء)، والياء في قولك: (قومي واخرجي وتذهبين). هذا مذهب','1','866',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (640,'البصريين. وأما الهاء والهمزة، فليست علامة تأنيث عندهم. وإنما التاء إذا وقفت عليها، صارت هاء. وبعض العرب يقف بالتاء أيضا، فقال: (مثل ظهور الجحفت). يريد الجحفة. وإنما قالوا: مسلمات، ولم يقولوا: مسلمتات، حذرا من اجتماع متجانستين في اسم واحد. وإذا لم يكن بد من حذف واحدة، فالأولى تدل على التأنيث خاصة، والثانية تدل على الجمع والتأنيث، فكان حذف الأولى أولى.
وقوله: (وأما (٧٥/أ) ما يكون [علم] التأنيث فيه ألفا، فينقسم إلى ألف ممدودة). وقد يفهم من هذا الكلام أن الألف تمد وتتحرك، وهذا محال، بل لا تقبل الألف الحركة بحال، وإنما يعنون بكونها ممدودة أن [يتقدمها] ألف وتاء في الألف الآخرة، فلا يتصور اجتماعهما، لسكونهما، فتقلب الآخرة منهما همزة، [فيقال: لف ممدودة لهذا. وإنما تقلب همزة]،','1','867',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (641,'لقرب الهمزة من الألف. هذا هو الكلام في المفرد.
وإذا صرت إلى الجمع، قلبت الهمزة واوا، ويستوي في ذلك من يعقل وما لا يعقل، تقول: خنفساوات. ولو سميت امرأة صحراء، لقلت صحراوات، إلا الصفة على فعلاء إذا كان المذكر أفعل، فإن العرب لا تنطق بجمع السلامة.
أما قلب الهمزة واوا، فالهمزات بالإضافة إلى القلب والإقدار عند التثنية والجمع أربع همزات: أصلية، فهذه تستقر، كقولك: قراء ووضاء، فإنك تقول: قراءان و [قراؤون]. ومنقلبة عن ألف، كالتأنيث، فهذه تقلب، كما تقدم. وإنما قلبت هذه، لأن الهمزة قريبة من الألف، فلو استوت، لثبت قريب من ثلاث ألفات. وذلك مستكره، فعدل عنه إلى ما هو أخف، بشرط ألا تكون الهمزة أصلية. وأما الهمزة الأصلية، فاغتفر فيها [الاستثقال] حفظا للأصل.','1','868',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (642,'الهمزة [الثالثة] ما هي مبدلة عن حرف أصلي، كهمزة (كساء) و (رداء). والأكثر الإقرار، فيقال: [كساءان]، ورداءان، وقد جاء القلب.
والتي للإلحاق تتنزل هذه المنزلة. والهمزة في هذا المكان ليست أصلية، وإنما هي مبدلة عن حرف أصلي، والهمزة في هذا المكان ليست أصلية، وإنما هي مبدلة عن حرف أصلي، كهمزة (كساء) بدلا من الواو، وكقوله: كسوت، وفي رداء عن الياء، كقولك: رديت. والياء والواو إذا تطرفتا، ووقعت الألف قبلهما، قلبت ألفا، لكن بشرط ألا يكونا أصليين.
وشرط القلب في هذه الصورة، أن الألف لا تتحرك أبدا، فلما ماتت بالكلية، قدرت عدما. والياء والواو إذا تحركتا، وانفتح ما قبلهما ألفا، إذا لم يمنع من ذلك مانع، فقدرت الألف السابقة كالمعدومة، حتى كأن الفتحة باشرت الواو والياء، فقلبتهما. ولكن إنما يقال هذا في الألف الزائدة التي للمد.','1','869',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (643,'فأما الألف التي ليست زائدة، فلا يعد فيها ذلك، (٥٠/ أ) كألف (رأي) و (أرى).
وهذا من دقيق [علم] العربية أن يقع الالتفات في الحرف الواحد لجهتين، حتى يقوى باعتبار جهة، ويضعف باعتبار أخرى. وهذا فيما ذكرنا في الألف التي يأتي بعدها الياء والواو. والالتفات إلى بعضها يقتضي الإعراض عنها، وتقدر الحركة، وهي الفتحة باشرت حرف العلة. والالتفات إلى وجودها، يقتضي إقرار حروف العلة. والتفت أهل اللغة إلى ضعفها، إن كانت زائدة، وإلى وجودها، إن كانت أصلية، أو نازلة منزلتها.
زمن تصفح كلام أهل العربية، وجد من ذلك كثيرا، وإنما عدل أهل اللغة عن جمع المؤنث بالألف [والتاء] في الصفة، إذا كان المذكر فيه (أفعل) ليسر، وذلك أنهم يقصدون إلى طلب الخفة. [والثقل] تارة يحصل بكثرة الحروف، وتارة بإعمال الذهن في كثرة فهم المعاني، والصفات أثقل من','1','870',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (644,'الأسماء، ولذلك [وقعت] الصفة إحدى العلل المانعة من الصرف، فلم يريدوا إلا الإشعار بالصفة، [وهذه] الزيادة في المؤنث. فإن قيل: هذا يلزمكم أن تقولوا: مسلمات، فإن فيه الصفة والتأنيث والزيادة. قلنا: هذا (٧٥/ب) إنما خص بالصفة، إذا كان المذكر منها (أفعل) دون غيره.
فإن قيل: فأي مناسبة لهذا الشرط؟ قلنا: استعمال لفظ (أفعل) في المذكر، تدل على المبالغة في الوصف، بخلاف مسلم ومسلمة، فلا تظهر فيه المبالغة بحال. وإن جرى هذا اللفظ لقبا، لم يمنع بالألف والتاء، كما جاء: (ليس في الخضروات صدقة).
وأما ما ألفه مقصورة، كحبلى، وسكرى، فإنك تقلب فيه الألف ياء، لأن الياء أقرب إليها.
فإن قيل: فأنتم قد قلتم في جمع مسلمة: مسلمات، وإنما لم تقولوا: مسلمتات، حذرا من الجمع بين علامتي تأنيث، وقد صرتم إلى ذلك في حبليات وسكريات، فما وجه ذلك؟ قلنا: بينهما فرقان:
أحدهما- أن العلامتين هاهنا [متماثلتان]، بخلاف مسلمات،','1','871',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (645,'[وقيل]: ويكره من اجتماع الأمثال، ما لا يكره مع الاختلاف.
الثاني- أن الياء ليست علامة تأنيث في هذا المكان على الحقيقة، وإنما العلامة الألف، وقد زالت، وقد فرق بين ثبوت الشيء، وبين ثبوت بدل عنه.
وأما قوله: (وأما جمع التكسير، فهو الذي ينكسر فيه بناء الواحد). فهذا لعمري هو الغالب، ولكن لا يجري الباب كله على ذلك. وقد يأتي لفظ الجمع على إلف الواحد بلا فصل، وقد يكون بزيادة علامة تلحق المفرد، وقد تكون بالعكس من هذا. فالأول كالفلك، فإنه يكون للواحد والجمع بلفظ واحد.
قال الله تعالى: {في الفلك المشحون}. وقال: {والفلك تجري في البحر}. وكذلك الخلفاء والظرفاء، قال سيبويه: يكون للواحد والجمع بلفظ واحد.
وأما القسم الذي يأتي للجمع بلفظ المفرد بزيادة (الهاء)، كالكلم والكلمة، والتهم والتهمة، وعكس ذلك الكم والكمة (٥٠/ ب)، الكم للواحد، والكمة للجمع، فلم يستمر القول بأن جمع التكسير، هو الذي ينكسر فيه بناء الواحد. وحصر جمع القلة في أبنية التكسير، صحيح. وقد نظم ذلك في','1','872',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (646,'بيت أذكره، ليكون حفظه أسهل. قال الشاعر:
بأفعل وبأفعال وأفعلة ... وفعلة يعرف الأدنى من العدد
وقول الإمام: (إنه لما دون العشرة). يظهر منه أنه لا يتناول العشرة، وليس كذلك عند النحويين، بل للعشرة فما دون.
ووجه آخر: أنا إذا قلنا: هذا جمع قلة، فلا نعني أنه لا يتناول بوجه الكثرة، ولكن نعني أنه ظاهر في القلة، محتمل للكثرة. قال الشاعر:
بنا الجفنات [الغر] يلمعن بالضحى ... وأسيافنا يقطرن من نجدة دما
ولم يرد أن لهم عشر جفان.
وقوله: (ثم حظ الأصول أن الجمع ينقسم انقساما آخر، فمنه ما هو جمع','1','873',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (647,'القلة، ومنه ما هو جمع الكثرة). هذا هو المقصود في الأصول، ولكن يتعلق النظر لأجل هذا الغرض بالتعريف والتنكير والسلامة والتكسير. فإذا تكلمنا على الجمع باعتبار السلامة والتكسير، فإن ذلك من غرض الأصول، لما يترتب عليه من معرفة التقليل والتكثير. وإن تكلم على الجمع باعتبار التعريف، فعلى هذا الحد يدخل تحت قسم الأصول، لا من غير هذه الجهة.
قال الإمام: (ونحن نقول: أما [ما ذكرناه] (٧٦/أ) فعلى تقاسيم الجموع [من الشرط] والتنكير في النفي، فلا شك أنه لاقتضاء العموم) إلى قوله ([إلا أن يقيد] بقرينة حالية [ينزل] على حسبها). قال الشيخ:','1','874',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (648,'الأمر على ما ذكره في الشرط، فإن الشرط وضع على الإبهام، لا اختصاص له في وضع اللسان. وأما التنكير، فقد تقدم كلامنا عليه.
وأما قوله: (فأما جمع القلة، فلم يوضع للاستغراق قطعا). إن أراد أنه ليس ظاهرا فيه، فهو مسلم، وإن أراد أنه لا يحتمله قطعا، فهذا باطل على القطع. وقد قال أئمة العربية: قد يوضع القليل موضع الكثير، وكذلك عكسه.
وأما قوله: (وأما جمع الكثرة، فهو الاستغراق). هذا غلط، بل هو صالح له ولما دونه. ولا ينضبط مقداره، إلا أنه ظاهر فيما يزيد على العشرة، لاسيما في الجمع الذي ليس له جمع قلة، كرجال، [ودراهم]، تقول: ثلاثة رجال، وأربعة [دراهم].','1','875',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (649,'قال الإمام: (ونحن من هذا المنتهى نفرع ذروة في التحقيق لم يبلغ حضيضها، ونفترع معنى [بكرا] هو على التحقيق منشأ اختباط الناس في','1','876',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (650,'[عمايتهم] إلى قوله (فهذا بيان الطرفين). قال الشيخ الكلام كما ذكره واضح. فأما الرتبة الأولى، وهي رتبة النص في الأعداد، فليست مما نحن (٥١/ أ) فيه، فإن [أهل] العربية قالوا: إنه يقع على القليل والكثير من جنسه.','1','877',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (651,'ولذلك لا يثنى ولا يجمع، إذ في لفظه ما يغني عن ذلك. فهو يتخيل فيه عموم.
والصحيح أنه لم يوضع للأعداد بحال. وإن مطلق [(الضرب)] و (القيام)، إنما باعتبار معقول هذه الهيئة. كما أن مطلق لفظ الرجل غير متعرض لصفاته وهيآته، وإن كان الرجل يصلح للوصف بهذه الجهات.','1','878',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (652,'والقلة والكثرة أوصاف للمصدر، كما أن الطول والقصر أوصاف للرجل، والنعت ليس للغير اللفظ المنعوت، وإنما هو إلحاق زيادة يتقاعد اللفظ الأول عن الدلالة عليها. قال سيبويه: ولو كان لفظ الموصوف يشعر بالصفة، لاستغني عنها، ولجرت الصفة مجرى التوكيد. كلام حسن بالغ.
والفرق بين صلاحية الدلالة [وصلاحية الوصف]، أن يكون في طباع','1','879',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (653,'اللفظ لغة، ما يصلح للدلالة والإطلاق. [و] صلاحية الوصف ترجع إلى أنه يصح وصفه بما يذكر. هذا كلام التمام على الطرفين.
قال الإمام: ([وأما] القيمان المتوسطان، فعلى [ضربين]: نحن [نصفها]: أحد القسمين: [ما وضع في اللسان للعموم]) إلى قوله ([و] أمكن [أن يحمل الذين] جرى ذكرهم [تخصيصا]). قال الشيخ [أيده الله] اختلف الناس في صيغ العموم بعد المصير إلى [أنها] مستغرقة، هل تدل على الاستغراق قطعا أو ظنا؟ أو بعضها يستغرق قطعا، وبعضها','1','880',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (654,'يستغرق ظنا؟ فذهب الشافعي والمعتزلة إلى أنها إذا تجردت عن القرائن، دلت على الاستغراق قطعا، ولكن المعتزلة تلقته من مأخذ، والشافعي من (٧٦/ ب) غيره.
أما المعتزلة، فتلقوا ذلك من استحالة تأخير البيان عن مورد الخطاب، فلو كان المراد به غير ما هو ظاهر فيه، لكان تأخيرا للبيان، وذلك محال.
وأما الشافعي، فلم يتلق من هذا، وإنما كأنه يرى أن التخصيص، إنما يكون واردا على كلام المتكلم، لاقتران اللفظة المخصصة عند [الإطلاق]. وهذا إنما يكون نظرا إلى اللغة، فيفتقر فيه إلى النقل.','1','881',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (655,'وقال الفقهاء: الألفاظ ظواهر، لا تدل دلالة قطعية إلا بالقرائن، كما أنه لا تسقط دلالتها إلا بالقرائن. وهذا هو المختار عندنا. وفرق أبو المعالي هاهنا بين أدولت الشرط وغيرها، فرأى أن أدوات الشرط تدل دلالة قطعية، وإنما تقبل التخصيص، بناء على القرائن. وهذا مذهبه معرى عن الدليل.
ودليل التعميم عندنا: أنا وجدنا هذه الألفاظ تستعمل للاستغراق تارة، وللبعض أخرى، فامتنعنا من القطع، ولم نصر على الإجمال، لعلمنا من أهل اللغة والصحابة طلب دليل الخصوص.
والأمثلة في ذلك واضحة. فلو قال القائل: أكرموا من يزورنا، أو عبيدي (٥١/أ) أحرار، [ودوري محبسة]، وما لقيت اليوم أحدا، وكل رجل يأتيني فله درهم، أو {ما فرطنا في الكتاب من شيء}. لفهم العموم في هذه الأحوال، ولذلك ورد النقض على الخبر العام، قال الله تعالى: {قالوا ما أنزل الله على بشر من شيء قل من أنزل الكتاب الذي جاء به موسى}. ولما نزل قوله تعالى: {إنكم وما تعبدون من دون الله حصب جهنم}. قال ابن','1','882',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (656,'الزبعري: إذا أخصم لكم محمدا، فقال: يا محمد عبدت الملائكة، وعبد المسيح، فليكونوا في النار. فأنزل الله تعالى: {إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون}. وطلب دليل الخصوص، لا دليل العموم.
قال الإمام: (وأما القسم الثاني من القسمين المتوسطسن: فهو الجمع الذي ليس له جمع قلة) إلى قوله (ونذكر في كل مسألة ما يليق بها. [وبالله التوفيق]). كلامه واضح.','1','883',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (657,'قال الإمام: <span data-type="title" id=toc-124>(مسألة: ذكر سيبويه- وغيره من أئمة [العربية] أن جمع السلامة من أبنية جمع القلة) </span>إلى قوله (أنه لا يتعرف المثنى والمجموع إلا [بالألف] واللام، [وهما] يعرفان كل نكرة). قال الشيخ [أيده الله]: قوله: إن الأصوليين القائلين بالعموم، مجمعون على حمل جمع السلامة على الاستغراق، صائرون إلى تنزيله منزلة الكثرة من أبنية التكسير. ليس الأمر على ما قال من الإجماع، وقد قال بعض [أهل العموم] إنه','1','884',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (658,'مستغرق، كما ذهب إليه أئمة العرب.
وقوله: ([أهم] مقصود [في] المسألة محاولة الجمع بين مسالك الأئمة). هو كذلك إن قدر عليه.
وقوله: (الاسم العلمي إذا ثني أو جمع، خرج عن كونه، [معرفة] وتنكر). خلاف أهل اللسان أجمعين، فإنهم مطبقون على أن العلم لا يثنى ولا يجمع بالألف واللام.','1','885',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (659,'فقوله: (إذا قلت: زيدان فقد نكرت، باتفاق أئمة العرب). هذا لا يصح باتفاق أئمة العربية، إلا في مواضع مخصوصة، وهو أن تبقى العلمية [للفرق بيننا وبينه] (٧٧/ أ)، وبين الجنس، وما في معناهما. والسبب في امتناع التثنية والجمع بغير الألف واللام: أن المفرد علم، ونحن إنما أردنا تثنيته أو جمعه، فلو جئنا بالتثنية والجمع على التنكير، لحصل الشياع، ولم ترجع التثنية على العلم بعينه، فتكون تثنيته لا يحصل منها مقصود تثنية، فيفوت الغرض في التثنية، وكذلك الجمع. [فصح] أن قوله قول من لا يحيط بهذه القاعدة.','1','886',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (660,'قال الإمام: (فنعود بعد ذلك إلى [بيان] الكلام في المسألة) إلى قوله (إذ ذكر حكم التثنية والجمع على [التمحيض]). قال الشيخ: قوله: إن كل نكرة لا تقتضي استغراقا. قد قال بعض الأصوليين: إنه لا فرق بين قول القائل: اقتلوا رجالا، وبين قوله: اقتلوا الرجال. وإن كان الأكثرون قد ذهبوا إلى الفرق.
والصحيح عندي في ذلك أن جمع الكثرة، لم يوضع (٥٢/ أ) مختصا بالاستغراق، بل هو فيما يزيد على أقل الجمع المشترك، على أي عدد أطلق كان حقيقة، فإذا [لا] يعم الجميع]، وإن كان للكثرة لعينه، وإنما يعم بسبب القرائن. وأما إذا عرف، فينظر إلى الألف واللام، هل كانت للعهد؟ فينظر إلى مقدار ذلك المعهود أو الجنس، فينزل الأمر على ذلك.
وأما قوله تعالى: {ما لنا لا نرى رجالا كنا نعدهم من الأشرار}. فلا يدل','1','887',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (661,'على التبعيض ولابد، إذ يحتمل أن يكون لبيان الجنس، ويكون التقدير: كنا نعدهم أشرارا، لكن هذا على مذهب الأخفش، الذي يجوز زيادتها في الواحد. وإن قدرنا أنها للتبعيض، فليس فيه إلا أن جمع الكثرة في هذا المكان المخصوص، لم يقصد به الاستغراق، ونحن ما حتمنا أنه يراد للاستغراق، بل فلنا إنه مشترك بين الأعداد بعد مجاوزة الأول.
وأما قوله: ([وإذا] عرف، ولم يكن على بيان التقليل، فهو للاستغراق). واحتج على ذلك بقوله تعالى: {إن الأبرار لفي نعيم}.','1','888',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (662,'وهذا أمر عجيب، وما أدري، هل استدل به على أن جمع القلة لا يستغرق حتى يكون المراد بالقرائن أن دون العشرة من [الأبرار]؟ أو استدل به على أنه إذا عرف استغرق؟ وهذا هو الظاهر من عرضه. وغفل الرجل عن كونه جمع قلة.
ثم قال بعد ذلك: وإذا عرف، ولم يكن على بناء التقليل، فهو للاستغراق. فقد اشترط في الاستغراق شرطين: أحدهما- أن يكون الجمع جمع كثرة. والثاني- أن يكون معرفا بالألف واللام. وهب أن جمع السلامة عرف، فقد عدم الشرط الآخر، وهو أن يكون الجمع جمع كثرة، فلم يظهر لما قاله وجه أصلا. وأما التعريف، فلا يتلقى منه تخصيص ولا تعميم، وإنما هو مرشد إلى العهد والجنس.
قال الإمام: ([فينتظم] من ذلك: أن كل جمع في عالم الله تعالى، فإنه لا يقتضي الاستغراق بوضعه) إلى آخر المسألة. قال الشيخ: هذا الكلام الذي ذكره في هذا المكان، من أفسد شيء، وقد ناقض به كل ما تقدم، وهو فقد قال: (إن جمع القلة لما دون العشرة، وجمع الكثرة يزيد على ذلك). فقد صار في','1','889',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (663,'هذا إلى أن الجمع بجملته لأقل الجمع، وإن كان جمع كثرة. وإن كان كل جمع في حال التنكير لأقل الجمع، بطل انقسام الجمع إلى جمع قلة وجمع كثرة، فإن التعريف يجعل جمع (٧٧/ ب) القلة جمع كثرة.
وإن قدرنا محالا، وهو أن التعريف ينتقل فينتقل الجميع. وهذا الذي قاله خلاف إجماع أهل اللغة وأئمة العربية. وما حكاه عن سيبويه من (أن جمع السلامة من أبنية القلة). فهو يقول: إنه إنما يستغرق إذا لم يعرف، ولم يكن جمع قلة، فإذا حصل التعريف، فقد فات الشرط الآخر، وهو أن يكون جمع كثرة. فهذا كلام متناف كله. هذا نهاية الكلام على ما قال. ونحن نرى أن نذكر هاهنا متمسك أصحاب المذاهب المعممين والمخصصين، (٥٢/ب) ونرمز إلى مأخذ الموافقة فنطلبها.
أما المعممون، فقد تمسكوا بطرق:','1','890',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (664,'أحدها- أنهم قالوا: إن أهل اللغة، بل أهل جميع اللغات، كما فهموا الأعداد والأشخاص والأنواع، ووضعوا لها ألقابا تدل عليها، لدعاء حاجتهم إليها، فكذلك فهموا العموم واستغراق الجنس، فاحتاجوا إلى التعريف، فكيف لا [يضعون] عبارات تدل على ذلك؟ قلنا: هذا كلام ضعيف من أوجه:
أحدها- أن هذا قياس في اللغة، ومن أين يلزم إذا وضع عبارة عن بعض الأشياء، أن يضع عبارة عن كل ما علم؟
الثاني: أن هذا منقوض بالتعبير عن خصوصية الأرائح، فإنهم كانوا فهموا الألوان حتى أبيض وأسود، وبياض وسواد، فكذلك الأرائح، ولم يضعوا لها عبارة، حتى لزم تفريقها بالمحل حتى يقال: رائحة المسك.
والثالث: وإن سلمنا [أن] ذلك مقتضى الحكمة، فمن أين يلزم أن واضع اللغة راعي الحكمة؟ وكم من حكيم يترك ما لا تقتضي الحكمة تركه؟ .
الرابع: أنا وإن سلمنا أنه لابد من العبارة، فنقول: قد عبر عنه، ولكن باللفظ المشترك، ولا ينكر اشتمال اللغة على الألفاظ المشتركة. فإن قيل: ذلك يخل بالبيان. قلنا: إذا صح الوضع، اندفع السؤال، على أن البيان يحصل بقرائن الأحوال المزيلة للإجمال.','1','891',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (665,'الشبهة الثانية: أنهم قالوا: يحسن أن نقول: اقتلوا المشركين إلا زيدا، ومن دخل الدار فأكرمه إلا الفاسق، ومن عصاني فعاقبه إلا المعتذر، فيصح الاستثناء، والاستثناء لاستخراج ما كان يدخل تحت اللفظ أولا، فلو لم يكن اللفظ الأول مستغرقا، لما حسن الاستثناء. وهذا الكلام ضعيف من أوجه:
أحدها- أن الاستثناء قد يؤتى به لذلك، وقد يؤتى به لقطع صلاحية الدخول، وقد يجيء الاستثناء من غير الجنس، فيمكن أن يكون الآتي بالاستثناء في هذه الصورة جيء به لقطع الصلاحية، لا لإخراج ما يتناوله اللفظ ولابد، بل لقطع الصلاحية خاصة. وإذا أمكن ذلك سقط الاستدلال.
الشبهة الثالثة: قولهم: إن تأكيد الشيء ينبغي أن يكون موافقا لمعناه، فلا يؤكد العموم إلا بالعموم، ولا الخصوص إلا بالخصوص، ويصح أن يقال: جاءني القوم كلهم، (وكل) للاستغراق، فليكن لفظ القوم كذلك. ويقال: اضرب القوم أجمعين، ولا يقال: اضرب القوم نفسه، واضرب زيدا نفسه، ولا يقال: اضرب زيدا أجمعين. قلنا: هذا الاستدلال باطل، [فإن] النزاع في [التوابع] المؤكدة، كالنزاع في المؤكد. ومن سلم أنه إذا قال: اضرب القوم','1','892',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (666,'كلهم أنه مستغرق؟ بل أكد القوم الذين أرادهم بلفظه الأول، وفيه النزاع. فلا يزول [الإشكال] بقضية (٥٣/أ) التوكيد على الحال.
فإن قيل: [إذا قال]: أكرم (٧٨/أ) الناس أجمعين أكتعين أبصعين، صغيرهم وكبيرهم، ذكرهم وأنثاهم، حرهم وعبدهم، على أي وجه كانوا، وعلى أي حال، فلابد من فهم العموم بلا ريب، والتوكيدات توابع المؤكد [للعموم] الأول.
وهذا أيضا ضعيف، فإن الواقفية في هذا على [معتقدهم] في الوقف، وإن الجماعة التي أكدت، لم تبين، والتوكيد تابع. وقد اعترف بعضهم بالعموم، وتلقاه من قرائن الأحوال، وعرف الاستعمال في ذلك، فيكون زائدا على فهم مقتضى الألفاظ.
الشبهة الرابعة: أنهم قالوا: إن هذه الألفاظ إما أن تكون للاستغراق- وهو ما [يريدون]- وإما أن تكون لأقل الجمع، وهو باطل. كما سيأتي في الرد','1','893',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (667,'على أصحاب الخصوص. وإما أن تكون مشتركة، ولا يصح أن تبقى مجملة، ولا يحصل منها بيان، فيتعين أن تكون للعموم.
وهذا ضعيف، فإن للقائل أن يقول: لا تحمل على جهة من الجهات إلا بقرينة تبين المقصود، كالألفاظ المشتركة، ولا ينكر اشتمال اللغة على ذلك.
الشبهة الخامسة: تمسكهم بالإجماع، وأن الصحابة - رضي الله عنهم - تمسكوا بعمومات الكتاب والسنة في إثبات الأحكام وبنائها عليها، فكانوا يطلبون دليل الخصوص، لا دليل العموم. ففهموا من قوله: {يوصيكم الله في أولادكم}. ميراث فاطمة، حتى روى أبو بكر - رضي الله عنه - أن النبي - عليه السلام - قال: _نحن معاشر الأنبياء لا نورث).','1','894',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (668,'وتمسكوا بآية المواريث، حتى روي لهم: (القاتل [لا يرث]). وكذلك جميع الأحكام. وهذا من أظهر ما يتمسك به أصحاب العموم. ولكن يصح أن يقال: إنما فعلوا ذلك عند ظهور القرائن، وهي غالبة جدا في هذا الباب. وكل مسلك من الكلام تطرق إليه إمكان، لم يفض إلى القطع.
شبهة أرباب الخصوص: قالوا: هذه الألفاظ لابد أن تدل، وأقل الجمع لابد منه، والزيادة مشكوك [فيها]، فلا سبيل إلى إثباتها بالتوهم. وهذا فاسد من وجهين:
أحدهما- أنه لا نسلم أن أقل الجمع لابد منه، فقد ذهب أبو الحسن [إلى] أن اللفظ مشترك بين الواحد، اقتصارا عليه، وبين أقل الجمع فما فوقه.
الثاني: أنه ليس النزاع في ثبوت أقل الجمع، وإنما النزاع في اقتصار اللفظ عليه، أو تصح الزيادة في الدلالة، أو الأمر ملتبس؟ فإلى هذا يرجع','1','895',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (669,'الخلاف. فلا يخلو الخصم: إما أن يمنع الدلالة، أو يشك فيها؟ فإن منع وقال: لا يدل، افتقر إلى الاستدلال. فإن قال: لا أعرف، لم يأت بالدليل، ولكنه غير عارف بالوضع. وليس محل النزاع إثبات الأقل، فهذا النوع من الاستدلال لا يصلح في اللغات على حال.
وأما مستند أصحاب الوقف: فعلى حسب ما مضى في صيغة الأمر حرفا حرفا، فلا معنى للإعادة.
وقد تمسكوا أيضا في هذه المسألة بحسن الاستفهام، والجواب كما تقدم. وطريق الرد عليهم من (٥٣/ب) ثلاثة أوجه:
أحدها- التحاكم إلى أهل اللغة أجمعين، ومصيرهم إلى انقسام الجمع إلى جمع قلة وإلى جمع كثرة، وضبطوا جمع القلة بأبنية معدودة، وقالوا: إنه للعشرة فما دونها، وقالوا: جمع الكثرة للزيادة على ذلك، ولكنهم لم يثبتوه للعموم، بل للزيادة. وهذا هو الصحيح عندنا، ويفتقر في التعميم إلى دليل (٧٨/ب) آخر تبينه قرينة الحال، أو صريح المقال. وهذا التفصيل [عند أهل] اللغة إنما هو في الجموع. فأما الشرط والتنكير في النفي مع الفتح، فقد','1','896',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (670,'قضى أهل اللغة بكونه للعموم.
الثاني- إجماع الصحابة. كما سبق تقريره. وما ذكره القاضي والواقفية من أن ذلك يرجع إلى القرائن، كلام ضعيف، فإن القرينة يتعذر مصادفتها لجميع المعلومات اعتيادا. فلو كان التمسك بالقرينة [دليلا]، لوجب اختلاف الفهم، إلا أن تكون القرينة أمرا كليا لا تختلف باختلاف الصيغ، فيحصل [المقصود] من الاستغراق، وإن أمكن النزاع من جهة وضع اللسان. وكأن الخصم وافق في الحكم، وخالف في الطريق.
الوجه الثالث- أن نقول: غاية الواقفية أن يدعو الالتباس أو الاشتراك، ولا يمنعون صلاحية اللفظ على حال. وإذا استقر في الشرع أن الحكم في المتماثلات واحد، حتى لو ثبت اقتصار اللفظ لغة [عممنا] بطريق القياس. فإذا كان للفظ صلاحية الدلالة، وجب التمسك به، بعدما استقر في الشرع المقصد إلى التسوية في الأحكام. فإن ثبت أنه ظاهر اللغة، فلا إشكال، وإن أمكن الإجمال، فيجب التعميم، لما قررناه من قرينة الاستواء في غرض المتكلم. وإنما يقصد بيان مدلول اللفظ في الشريعة. وقد استبان ذلك بما','1','897',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (671,'قررناه، واندفع عنه الإشكال. والحمد لله رب العالمين.

قال الإمام: <span data-type="title" id=toc-126>(مسألة: قال أصحاب العموم: النكرة في النفي [تعم]، وفي الإثبات تخض. فلابد من تفصيل القول في الطرفين) </span>إلى قوله ([ولم يسغ] حملها من غير قرينة مخصصة على الخصوص). قال الشيخ: أما قولهم: النكرة في النفي تعم، قد تقدم الكلام أن (لا) إذا دخلت على اسم منكور، فإن بني معها على الفتح، فهي [محل] اتفاق بين النحويين والأصوليين. وإن بقي الاسم معربا، فقال أئمة العربية: إن دلالتها على نفي الجنس عند البناء. وظاهر كلام الأصوليين التسوية. كما تقدم الكلام.
وأما (ما) النافية إذا دخلت على فعل واقع على نكرة، فإنها تعم، كقولك: ما رأيت رجلا، والبناء في هذا لا يصح.
وأما قولهم وفي الإثبات تخص، فكلام صحيح، إذ النكرة: كل اسم شائع في جنسه، لا [يختص] به واحد دون آخر.','1','898',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (672,'وما ذكره الإمام من التفصيل في هذا القسم: وهو (أن النكرة الواقعة في سياق الشرط محمولة على العموم). فلا يصح عندي، ولو كان اللفظ في هذه الصورة للعموم، لما استحق الإكرام من أتى بمال واحد، بل كان يفتقر إلى الإتيان بالأموال، كما (٥٤/أ) لو قال: من أتاني بكل مال أجازه، فإنه لا يستحق الآتي بمال واحد الإكرام، بل النكرة باقية على إطلاقها، تعم الشرط، وتثبت عموما في الآتين بالمال، حتى لا [تختص] بآت واحد. ولو اتفق أن','1','899',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (673,'يأتي جماعة بمال، لاستحقوا بجملتهم الإكرام، فالعموم في الشرط، لا فيما تعلق الشرط به. فكلام الأمة صحيح، ومؤاخذة الإمام غير متوجهة.
وأما قولهم: إنها في النفي تعم، فلا نزاع فيه، ولا تقسيم يرد على أصل الحكم. نعم، التفاوت ظاهر، باعتبار ورود الحرف المؤكد، والأئمة لا ينازعون في ذلك.
قال الإمام: (مسألة: اللفظ الموضوع للإشعار بالجنس، الذي واحده','1','900',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (674,'بزيادة (الهاء) كالتمر والتمرة، والشجر والشجرة) إلى قوله (فهذا قولنا (٧٩/أ) في هذه [المسألة]). قال الشيخ أما الكلام على ألفاظ الجنس التي يميز فيها الواحد عن الجنس بالهاء، كالثمر والثمرة، وما يقضي فيه بكونه [جمعا]، أو جنسا مفردا، فقد تقدم الكلام عليه. وإنما نخص في هذا المكان الكلام بلفظ الجنس.
وإنما [قال] الإمام: (إنه يضم إلى المسألة مسألة أخرى). فجهة','1','901',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (675,'المقاربة بين المسألتين حتى جمعا في نظم واحد، أن لفظ الجنس هناك امتاز عن المفرد بالهاء، ولفظ الجنس هنا امتاز عن الواحد [بالألف] واللام للعهد، عند سبق التنكير. وإنما اختار الكلام على المسألة الثانية، لأنها أغمض، إذ لا امتياز من جهة اللفظ، وإنما الاعتماد على قرائن الأحوال، فإذا صح له فيها التعميم مع فقدان الفرق اللفظي، فلأن يثبت التعميم في باب التمر والتمرة أولى.
وقد اختلف الأصوليين في هاتين المسألتين على مذاهب: فقال قائلون: إنه للجنس، وقال قائلون: لا يثبت ذلك فيه، وقال قائلون: إنه مشترك، وفرق','1','902',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (676,'الإمام بين ما فيه (الهاء)، وبين ما لا (هاء) فيه. فذهب إلى [أن] ما فيه (الهاء) [أنه] للجنس عند فقدانها، وذهب في القسم الآخر إلى الوقف. وهذا هو الصحيح عندنا.','1','903',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (677,'أما من ذهب إلى أنه مفرد، فقد تمسك بإفراد لفظه وقبوله للتثنية، فيقال: الرجل والرجلان. قالوا: وأصل اللغة اختصاص دلالة الألفاظ المفردة على المفرد، والتثنية على التثنية، والجمع على الجمع. هذا هو الأصل، وإن كان يصح إطلاق الجمع على التثنية، ولكنه مجاز قليل. وكذلك إذا أطلق لفظ المفرد على الجمع، فمجاز.
وأما الذين ذهبوا إلى أنه الجنس، فقد تمسكوا بأمرين:
أحدهما- استواء الجنس في إطلاق اللفظ، فهم لو ذكره مرتين، لصح أن يريد به الاثنين، والاختصار في إسقاط التكرار، والإتيان بالإفراد.
وقد تمسكوا أيضا بأمثلة كثيرة جرى اللفظ فيها على الجنس، كقوله تعالى: {إن الإنسان لفي خسر}. وكقوله: {لقد خلقنا الإنسان في أحسن','1','904',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (678,'تقويم}. وغير ذلك، [كقولهم]: الدينار أشرف من الدرهم، والرجل أفضل من (٥٤/ب) المرأة.
وقالوا أيضا: [يكفي] في كونه للجنس، أن لا يتقدم منكر سابق، ولا يكفي في كونه لتعريف العهد، أن يسبق التنكير، بل يفتقر إلى قرينة زائدة تنضم إلى ذلك، وهو أن يقصد العهد، فلما كان عدم التنكير يحقق الجنس، ولم يكن تقدمه يوجب العهد، كان الظاهر أنه للجنس. هذا توجيه المذاهب.
فأما قولهم: إفراد اللفظ يقتضي إفراد المعنى، فغير مسلم على الإطلاق، وهل النزاع إلا فيه؟ وألفاظ العرب والكتاب والسنة تثبت أسماء الأجناس، بدليل صحة الاستثناء منها، ولا يستثنى من الواحد.
وقولهم: إنه مجاز، غير مسلم، وألفاظ الجنس [حقائق عند] أهل اللسان. وأما من قال: إنه للجنس، فقد غلط، ولا يلزم من الاشتراك في حقيقة الجنس، أن لا يعبر الإنسان إلا عن الجنس بكماله، ولا ذاهب يذهب إلى أن القائل إذا قال: أقبل الرجل، يكون متجوزا، بل مستعملا للفظ على حقيقته.','1','905',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (679,'وأما تمسكهم بأنه يكفي في الرد إلى الجنس، فقدان التنكير السابق، فصحيح، ولكنهم لم يفهموه على وجهه، وذلك أن اللفظ المجمل إذا انحسمت إحدى جهتيه، بقيت الأخرى. والإجمال إنما ينشأ من احتمال العهد والجنس، (٧٩/ب) فإذا انحسمت إحدى الجهتين، بقيت الأخرى. فالصحيح إذا في [هذا] القسم الإجمال، كما ذهب إليه الإمام.
قال الإمام [رحمه الله]: (وأما التمر والتمرة، فمطلق اللفظ الذي واحده بزيادة (الهاء) للعموم عند محققى المعممين) إلى آخر المسألة. قال الشيخ: قوله: مطلق اللفظ الذي واحده بزيادة (الهاء) للعموم عند المعممين. هذا إنما يكون كذلك، إذا كان معرفا بالألف و (اللام). [وأما إذا كان] منكرا، فإنه لا يشعر بالاستغراق بحال. أما على مذهبه، [فيصح] أن يكون كجمع الكثرة، وهو يرى جمع الكثرة لا يستغرق إلا إذا كان معرفة، فكيف','1','906',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (680,'باسم الجنس؟ وأما نحن فنرى الاشتراك في جمع الكثرة من فوق العشرة إلى غير نهاية. ويريد هاهنا أنه صالح أيضا مع ذلك لما دون العشرة. وإذا صح ذلك، بطل المصير إلى أنه بمطلقه للاستغراق، ويكون قول القائل: إذا أطلق لفظ (ثمر) على القليل والكثير حقيقة، ولا تجوز في ذلك بحال.
وإن كان كلامه منزلا على ما إذا كان نكرة، فباطل بالقطع، وإن كان إنما يتكلم عليه إذا كان معرفة، ولم يقصد عهدا، فكلام صحيح. ويصير الكلام في هذه المسألة كالتي سبقت من كل وجه، إلا في صورة واحدة، وهو أن المسألة السابقة يدور الأمر فيها بين الواحد وغيره، والإطلاق هاهنا لا يقبل الإفراد، فإن العرب في هذا المجال جعلت [في] المفرد حرفا زائدا، وهو (الهاء)، وإذا نزعت منه (الهاء)، بطلت الوحدة.
وأما (إنكار بعض أصحاب العموم الدلالة على العموم، من جهة أنه ليس','1','907',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (681,'بجمع ولا شرط). فكأنهم يقولون: إنما يكون العموم بناء على جمع، (٥٥/أ) من جهة اللفظ كالمجموع، أو النظر إلى الإبهام كالشروط، وإن لم يكن شيء من ذلك، فلا يثبت العموم. وهذا غلط من القوم في ادعاء حصر الأسباب. ومن جملة الأسباب: النكرة في النفي، كما تقدم. والقوم قد اقتصروا على محض الدعوى، وقالوا: لا مقتضى للتعميم إلا الإبهام والجمع.
وقولهم: (وربما يجمع في نفسه، فيقول: تمور)، واستدلوا بذلك على أنه مفرد، وعارضه بقوله: (يمتنع أن يقال: تمر واحد). هذا إنما يمتنع على تقدير أن يراد تمرة واحدة. وأما أن يراد نوع من التمر، فلا يمتنع ذلك.
وقوله: (هذا أظهر من متعلقهم). يريد إن دلالة الجمع على الإفراد','1','908',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (682,'ليست مستمرة، لصحة جمع الجمع، بخلاف دلالة الواحد على الواحدة، فإنها مستمرة. فإذا انتفى النعت بالواحد، انتفت الوحدة. فأما كون الجمع يجمع، فلا ريب فيه، وإنما يجمع على تقدير أن يتخيل الجمع مفردا، ثم يتخيل اجتماع أفراد من ذلك الجنس، فيجمع على هذا الجنس.
وقول الإمام: ([ومما ينبغي أن يتنبه له الناظر أن لفظ التمر] أحرى [باستيعاب] الجنس من التمور، فإن التمر يسترسل على الجنس لا بصيغة لفظية). معناه أن المطلق يطلق اللفظة بإزاء المعنى الشامل لآحاد. والتمور يلتفت فيه إلى الوحدان، ثم الاستغراق بعده بصيغة الجمع. وفي صيغة الجمع مضطرب عندهم. وسر هذا يتبين في مسألة أقل الجمع.
قال الإمام: (مسألة: اللفظ المشترك كالقرء والعين وما في معناها، إذا','1','909',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (683,'ورد مطلقا) إلى قوله (وهذا القول يجري في الحقائق وجهات المجاز). (٨٠/أ) قال الشيخ: اللفظ إذا دل على مسميين فصاعدا، إما أن تستوي نسبته إليهما جميعا، فيكون مشتركا، وإما أنة تتفاوت النسبة، فهو الظاهر باعتبار معناه السابق، والمؤول باعتبار المعنى الخفي.
وقد اختلف الناس في القسمين. والشافعي يذهب إلى حمله على المعنيين، ولم يفرق بين النسبة المستوية والمختلفة. ووافقه القاضي عند استواء النسبة أن يكون للعموم. واستدل القاضي بدليل لا يليق بمثله الاستدلال به، فقال: لو كرر اللفظ مرتين، وأراد المعنيين، لصح له ذلك، فما الذي يمنع من','1','910',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (684,'إرادتهما جميعا مرة واحدة؟ قلنا: لا مانع من ذلك من جهة العقل، وإنما يتبع في ذلك موجب اللغة، فكيف يتمسك في ذلك بالتجويز العقلي؟ والاعتماد على استواء النسبة [غير مفيد حتى يثبت، هل استوت النسبة] في الإرادة؟ وفي مصير القاضي إلى ذلك مع ذهابه إلى الوقف في صيغة العموم، [يمنع] من حمل اللفظ على جميع المسميات. وأقصى ما سلم له ثبوت الاشتراك، وهو قد ذهب إلى أن [المشترك] عام، أو لعله إنما تكلم على هذا (٥٥/ب) تفريعا على قول من يقول بالعموم.','1','911',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (685,'وأما المصير إلى إجمال اللفظ الذي يظهر منه العموم لكونه قد يدل على البعض، [وادعاء] العموم في اللفظ المشترك، فبعيد جدا. وكذلك وقفه في صيغة الأمر وادعاء الإجمال، فما الذي [منعه] من المصير إلى العموم؟ ولكن هذا لا يلزم لوجهين:
أحدهما- أن الفعل لا عموم له، بخلاف الاسم.
والثاني- أنه [إنما] يذهب إلى العموم إذا لم يمنع من ذلك مانع. وإذا نزلنا اللفظ على الندب، امتنع أن يحمل في تلك الصورة على الوجوب. والصحيح عندنا أن اللفظ المشترك لم يوضع للعموم [بحال]، فإن العرب لم تستعمله [ليفيد] الاحتواء على جميع المسميات. [وكل ما] ذكرناه في الرد على القاضي يتوجه على الشافعي وزيادة، وهو حمل اللفظ على الحقيقة والمجاز جميعا. وهو [الاعتراض] الذي وجهه القاضي. وتقريره: أن معنى','1','912',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (686,'كون اللفظ حقيقة، أنها اقتصرت في الدلالة على الوضع الأصلي بلا زيادة ولا نقصان، [كما تقدم الكلام عليه. وإذا أطلقت على زيادة أو نقصان]، فقد تغير الوضع اللغوي بلا إشكال.
قال الإمام: (فإن قيل: [أتجوزون] أن يراد به جميع محامله؟ ) [إلى قوله] (ويتبين من حاله أنه يريد [أن يطبق] ذلك على جميع ما جرى). قال الشيخ: [ما ذكره الإمام]، كلام لا يغني في هذا المكان، فإن [النطق] على هذا الوجه غير محرم، [وإنما] النظر: [هل] إذا نطق بذلك','1','913',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (687,'[أيكون] اللفظ لغويا، أو حائدا عن مقتضى اللغة؟ ولم يتعرض الإمام لهذا، وإنما قال: يصح. وإذا قررنا أنه يصح لغة، فهل جمع بين الحقيقة والمجاز، أو يكون مجازا محضا؟ وهذا أيضا لم يتعرض [له].
[والصحيح] عندنا أنه ثبت أنه نطق به على هذا الوجه، فإنه إذا كان الوضع الأصلي [للاقتصار]، فعند الزيادة [يفوت الوضع الأصلي، كما في جانب النقصان. وسيأتي تقرير ذلك في مسألة] العام إذا خص، هل يصير مجازا، أو يجتمع فيه موجب الحقيقة والمجاز؟ والصحيح عندنا أنه مجاز محض، إذ يؤخذ في حد الحقيقة: الانطباق على الوضع الأصلي. هذا تمام كلام الأصوليين.','1','914',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (688,'وعندي في المسألة نظر آخر، (٨٠/ب) وهو أن نقول: المصير إلى أن المشترك عام على الإطلاق غلط، [ولا] يتأتى أن تكون دلالة المشترك على آحاد كل جنس، بأعظم من دلالة النكرة [الشائعة] في الجنس الواحد. وكيف يصح أن يقال إذا قيل للمرأة: تربصي [قرءا] أن يكون هذا [أمرا] بتربص [كل] الأقراء؟ هذا محال لا شك فيه. ولما قال الله تعالى: {والمطلقات يتربصن بأنفسهن ثلاثة قروء}. أجمع الناس على أنه ليس المراد جميع الأقراء، ولا الطهر ولا الحيض [معا]، وإنما أريد ثلاثة من أحد النوعين، وإن اختلف الناس في التعيين. وهذا أيضا يدل على أنه وقع في قسم الإجمال، إلى البيان، وإلا كان يصح أن يقال: إذا تربصت ثلاثة بين طهر وحيض اكتفى بذلك.
والذي أظنه بالقاضي أنه يقصد أن تكون النكرة التي اختلفت الأجناس في دلالتها في شياعها في أجناسها، كالنكرة الشائعة في الجنس الواحد، ويشيع لفظ','1','915',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (689,'العين في كل ما يسمى عينا، [وإن] اختلفت الأجناس، كما يشيع لفظ الرجل في الجنس الواحد، (٥٦/أ) وفي الموضع الذي [يقضي] فيه بعموم هذه النكرة في آحاد جنسها، يقضي فيه بعموم المشترك في جميع أجناسه. وفي الموضع الذي تكون فيه تلك مطلقة- حتى يقع الاكتفاء بواحد من الجنس، أي واحد كان- يكون اللفظ المشترك في آحاد أجناسه.
مثاله إذا قال: رأيت رجلا، اقتضى ذلك واحدا بعينه، [مختصا] برؤيته، [لا بلفظه]. وإذا قال: أكرم رجلا، [اقتضى إكرام واحد من الجنس. وإذا قال: ما رأيت رجلا]، أو لا تكرم رجلا، اقتضى ذلك عموما واستغراقا، فقد صار للنكرة الشائعة في الجنس الواحد ثلاثة أحوال: الإجمال والإطلاق والعموم.
كذلك ينبغي أن يكون اللفظ المشترك مجملا تارة، كما إذا قال: رأيت عينا. ومطلقا تارة، [كما] إذا قال: تربصي قرءا. وعموما [تارة]، [كما] إذا قال: لا قرء، وما رأيت قرءا. هذا تحقيق المسألة عندي على مذهب القاضي وغيره، ممن يصير إلى [أن] المشترك عام.
فأما المصير إلى التعميم عند إفراد اللفظ في جانب الإثبات، فمحال، لا ينتحله لبيب. ويتعلق بهذا الغرض مسائل، لم يذكرها الإمام في كتابه، وهي','1','916',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (690,'[حرية] بأن تذكر في هذا المحل. وتلك المسائل:
مسألة المفهوم، هل له عموم أم لا؟ والفعل، هل له عموم [أم] لا؟ وكذلك الفعل المتعدي إلى مفعولاته، هل يكون عاما بالإضافة إلى مفعولاته؟ وكذلك قول الصحابي: نهى رسول الله - صلى الله عليه وسلم -[عن كذا]، وقضى بكذا. [هذه مسائل اختلف الأصوليون فيها]، ونحن [نذكرها] مسألة مسألة، ونبين في كل مسألة ما فيها [من الدقائق].
مسألة: من يقول بالمفهوم، [فقد] يظن له عموما. وفيه نظر، وإنما قيل: إن له عموما، من جهة أن القائل بالمفهوم يزعم أنه إذا قال: (في سائمة الغنم الزكاة). فقد تضمن ذلك قولا آخر، وهو: لا زكاة في المعلوفة. ولو قال صريحا (لا زكاة في المعلوفة)، لكان عاما يقبل التخصيص.','1','917',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (691,'[ومقصود] المسألة: أنا إذا وجدنا صورة من صور المفهوم موافقة للمنطوق به، فهل نقول: بطل المفهوم بالكلية، حتى لا يتمسك به في غير تلك الصورة؟ أو نقول: يتمسك به فيما وراء ذلك؟ هذا موضع فيه نظر، ونحن نقول: لا يخلو: إما أن يستند المفهوم [إلى] البحث عن طلب فوائد (٨١/أ) التخصيص، وهو اختيار الشافعي، أو نقول: استقر في اللغة أنه إذا خص [المذكور] بالذكر على جهات مخصوصة، [اقتضى] هذا قيام قول آخر به، يتضمن نفي الحكم عما عداه، انتفاء ظاهرا، فإن بني على الطريق الأول، لا يصح أن يثبت للمفهوم عموم، لأنا إنما [أثبتناه] طلبا لفائدة المخالفة.
فإنهم قالوا: [لو] استوى المنطوق به والمسكوت عنه في الحكم، لما كان للتخصيص [بالذكر] فائدة، فيظهر أن المخالفة هي الفائدة. فإذا وجدنا بعض صور المفهوم، بطل أن تكون الفائدة المخالفة، ولزم طلب فائدة أخرى. وأما إذا قلنا: إن المفهوم استند إلى عرف لغوي في قيام المعنيين','1','918',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (692,'بالنفس، والاكتفاء في الدلالة بالعبارة الواحدة، حتى كأنه عبر عن الأمرين، فقال: لا زكاة في المعلوفة، فيصح (٥٦/ب) على [هذا التقدير]، إذا دل دليل على وجوب الزكاة في بعض المعلوفة، أن يتمسك بالمفهوم في البقية. وأما نحن فلا نرى للمفهوم دلالة فضلا عن سقوطها بقيام دليل الموافقة في البعض.
وأما المسألة الثانية: وهو المقتضى، [هل له عموم أم] لا؟ وصورة المقتضى: [فما لابد منه] في فهم مقصود المتكلم، وهو غير منطوق به. إما أن يكون [الاضطرار] من جهة العقل، كقوله: {[وسئل] القرية}. فإنه لا يمكن للجماد. وإما من جهة وجوب صدق المتكلم، كقوله: ([رفع] عن أمتي الخطأ [والنسيان]). وإما بالإضافة إلى الشريعة،','1','919',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (693,'كقولك: اعتق عبدك عني فإنه يتضمن حصول الملك [للملتمس]، فإن شرط نفوذ العتق عنه ثبوت الملك [له]، عند بعض العلماء.
فإذا تقرر أن المقتضى لا لفظ له، وإنما ثبت [ضرورة]، فالعموم من عوارض الألفاظ، فما ليس متلفظا به، فلا عموم له. هذا كلام الأصوليين. والصحيح عندنا أنه لا يمكن دعوى العموم [فيه]، لأن الضرورة ألجأت إلى تقدير مصدر ليثبت الصدق للمتكلم، ولم تلجئ إلى أكثر من ذلك. والعموم لابد أن يتناول [مسميين] أو أكثر من ذلك. فإذا لم يتحقق التعدد، كيف','1','920',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (694,'يثبت العموم؟ وكذلك قوله: (لا صيام). عند من ينكر الأسماء الشرعية، يجب إضمار الحكم، وهو متعدد.
فإن قال قائل: ما بال هذا لا يكون من باب حذف المضاف وإقامة المضاف إليه مقامه؟ فلما قال: (لا صيام)، فكأنه [قال]: لا حكم للصيام. وهو لو قال: لا حكم للصيام، لتضمن ذلك نفي الإجزاء والكمال. قلنا: قد بينا قبل هذا أن اللفظ المشترك لا عموم له بحال. وقد حققنا أنه لا يتضمن أن يكون اللفظ عاما بين نفي الجواز ونفي الكمال. على ما بيناه فيما سبق.
مسألة: الفعل المتعدي إلى مفعولين، [اختلفوا] في أنه بالإضافة إلى مفعولاته، هل يجري مجرى العموم؟ فذهب أصحاب أبي حنيفة إلى أنه لا عموم له، حتى لو قال: والله لا آكل، ونوى طعاما بعينه، أو قال: إن أكلت فأنت طالق، ونوى طعاما بعينه، أو قال: إن خرجت فأنت طالق، ثم قال: أردت مكانا بعينه، لم يقبل. [وكذلك] إذا نوى بالضرب آلة بعينها.','1','921',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (695,'واستدل أصحاب أبي حنيفة بأن هذا من قبيل المقتضى، فلا عموم له. وألحقوا أيضا بذلك دلالة المصدر على العدد. [وإنما جعلوه من باب المقتضي من جهة أنا قد بينا المصدر عن الدلالة على العدد]، وإن كان [العدد] من ضرورته.
قالوا: فلو قال: أنت طالق، ونوى اثنين، لم تلزم إلا واحدة، ولم تنفعه نية العدد. وكذلك لا تنفعه (٨١/ب) نية بعض الأطعمة عند قوله: لا آكل. بل متى وجد معقول الأكل، وجب أن يكون [حانثا].
وكذلك القول في الزمان والمكان والآلة، وكل ما ليس في اللفظ تعرض له وإشعار به. [كما لو] قال: أنت [طالق]، وقال: أردت به: إن دخلت الدار، أو يوم الجمعة. وكذلك قالوا: لو نوى بقوله: أنت طالق غدا، [لم يجزه]، وجوز أصحاب [الشافعي] ذلك.
والصحيح عندنا قبول نية التخصيص، وقصد (٥٧/أ) الزيادة في التطليق على مقتضى المصدر، فيلزمه إذا قال: أنت طالق طلقتين، ويرجع ذلك عندنا','1','922',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (696,'إلى [أن] التطليق [الذي] نطق به، لم ينطق به على جهة الإطلاق، بل إنما قصده من جهة كونه مخصوصا.
ويدل عليه أنه لم نطق بطلقتين، لقبل ذلك منه، فلو لم يكن للإطلاق صلاحية ذلك، لم يقبل التقييد بالعدد بعد ذلك. ولما قبل العدد على جهة التفسير، دل على أنه قام بقلب المطلق [خاصا]. [ولا] نقول: إن هذه زيادة على ما تضمنه [اللفظ] الأول، بل تفسير له، فيصير بمثابة ما لو قال: زوجتي طالق، وأراد [زينب].
وكذلك التقييد بالزمان والمكان والآلة. فإذا لم يرد أن اللفظ [تعرض] لهذه الجهات، ولكن كان للفظ صلاحية الشمول والقصر على البعض، [فإذا] قصد المتكلم القصر، فقد حمل اللفظ على ما يسوغ حمله عليه، ويكون ما يذكره مبينا لما قصده، بالإضافة إلى من يخاطبه. وقد توهم بعض الأصوليين دلالة لفظ الأكل على المأكول، حتى أرادوا أن يوردوا التحصيل عن','1','923',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (697,'الطعام، والآلة في الضرب. فقالوا: إذا حلف أنه لا يأكل، حنث بأكل أي [طعام] كان، فيدل على أن اللفظ تعرض له.
قلنا: ليس كذلك، بل إنما حنث من جهة أن حقيقة الأكل موجودة، فحنث بذلك، وهو لو قال: أكرم رجلا، فأكرم زيدا، [لامتثل]، لا من جهة كونه زيدا، الذي ليس في اللفظ [تعرض له]، [بل] من جهة كونه رجلا.
مسألة: لا يمكن دعوى العموم في الفعل، لأن الفعل لا صيغة له، حتى يتمسك [بعمومه]، وإن استوت نسبة الفعل بالإضافة إلى أحكام كثيرة، لأنه إن وقع [نفلا]، فلا يقع فرضا، وإن وقع [أداء]، فلا يقع قضاء، وهو إنما يقع على جهة واحدة. ففرق بين استواء [النسبة] لأشياء يصح [اجتماعها] كالعموم، وبين استواء [النسبة] لأشياء لا يتصور اجتماعها.','1','924',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (698,'ومثاله: أن النبي - صلى الله عليه وسلم - (صلى في الكعبة). فقال قائلون: إنه صلى النافلة. وقال قائلون: يصح أن يكون صلى النفل، ويكون صلى الفرض، [فنحمله] عليهما جميعا، إذ لا يصح في الصلاة الواحدة أن تكون نفلا فرضا، [اللهم] إلا أن [يكون] حكم الشرع في اشتراط الكعبة في النفل والفرض على جهة واحدة، فيكون هذا إثبات الحكم بطريق القياس، لا بطريق العموم. وكذلك روي أنه [- صلى الله عليه وسلم -] (صلى بعد غيبوبة الشفق). فقال [قائلون]: [نحمله] على أنه صلى بعد الشفقين: الحمرة والبياض [جميعا]. وهذا تحكم، لأنه إذا صلى بعد غيبوبة [الحمرة]، لم يلتفت إلى البياض، وإن صلى (٨٢/أ) بعد غيبوبة البياض، فلا التفات إلى الحمرة، والمصير إلى التعميم، تحكم من غير دليل.
مسألة: [فعل رسول الله]- صلى الله عليه وسلم - كما لا عموم له بالإضافة إلى أحوال الفعل، فلا عموم له بالإضافة إلى غيره، بل يكون مختصا به، إلا أن يتبين أنه','1','925',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (699,'أراد به [بيان] [أحكامنا]، [كصلاته] مع قوله: (صلوا كما رأيتموني أصلي). و (خذوا عني مناسككم). لأن فعله (٥٧/ب) قاصر [عليه] لا يتعداه باعتبار حقيقته، ولم تجعل الأفعال أدلة حتى ينظر في [خصوصها] وعمومها. فإن استقر في الشرع قصد المساواة في الحكم، كما في حق الواحد منا، فهذا يتلقى من أمر زائد على الفعل، لا من جهة الفعل على الخصوص.
مسألة: لا يمكن دعوى العموم في قول الصحابي: أمر رسول الله - صلى الله عليه وسلم - بكذا، ونهى عن كذا، كقوله مثلا: (نهى رسول الله - صلى الله عليه وسلم - عن بيع الغرر). (ونكاح الشغار). لأن الحجة في المحكي. وما سمعه الراوي','1','926',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (700,'[ورآه] حتى روى، يمكن أن يكون قضية مخصوصة، وسؤال خاص حكم على وفقه، وابتدأ أمرا للعموم، أو خروج الكلام تعريفا لمنكر سابق، وإذا احتمل ذلك، فلا معنى إثبات العموم بالتوهم.
وكذلك إذا قال: (قضى رسول الله - صلى الله عليه وسلم - بالشفعة للجار). (وبالشفعة فيما لم يقسم). هو من هذا القبيل. وقد يقال: إن الراوي لو لم يفهم العموم. لم يرو على ذلك. وهذا ضعيف، لأنه صادق على الأوجه كلها، وإنما [يثبت] العموم في الموضع الذي [يثبت] النقل، فينظر فيه وفي دلالته على العموم، [وأما] أن [يثبت] بالتوهم فلا.','1','927',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (701,'قال الإمام: <span data-type="title" id=toc-128>(مسألة: في ألفاظ الشارع في حكايات الأحوال</span>، [قال الشافعي: ترك الاستفصال في حكايات الأحوال مع الاحتمال يتنزل منزلة [عموم] المقال] على قوله (وادعاء قصد ظهوره في حكايات الأحوال المرسلة). قال الشيخ: ما ذكره الشافعي من أن ترك الاستفصال إلى آخره، للمسألة صور:
أحدها- أن [يثبت] عند الشارع ثبوت الوصف الخاص الذي وقعت الواقعة عليه، وتنتفي جهات اللبس، بمثابة ما لو ظهر من قرائن الأحوال أنه يريد','2','5',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (702,'به الوجه الخاص، فهذا لا يتصور فيه خلاف أنه لا يثبت فيه مقتضى العموم.
[الثانية]- أن يتحقق الاستبهام من كل وجه، فهذا يتنزل منزلة العموم، وليس عمومًا [على الحقيقة]، لأن قوله: (عليك عتق رقبة). لا عموم فيه، وكذلك قوله: (واقعت أهلي في نهار رمضان). فلا عموم، لا في الفعل ولا في الجواب، وإنما قلنا إنه في معنى العموم، نعني باعتبار [الأحوال] التي يمكن أن [تقع] الواقعة على آحادها، وإنما ذلك من جهة أن الحكم لو كان يختلف باختلاف الأحوال، حتى يثبت تارة، وينتفي أخرى، لما صح لمن التبس [الحال عليه] أن يطلق الحكم، لاحتمال أن تكون الحال مما لا','2','6',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (703,'[يستقر] معها الحكم على حال، فلابد من التعميم على حال، ولابد من التعميم على هذا التقدير بالإضافة إلى أحوال صاحب الواقعة. (٨٢/ب).
الثالثة: أن يطلق السؤال عن الواقعة، فلا يخلو: إما أن يسأل عنها، [باعتبار] دخولها الوجود، [بأن] يقال: ما تقول فيمن واقع في نهار رمضان؟ [فيقول]: عليه كذا [وكذا]. فهذا عندنا يقتضي استرسال الحكم على جميع الأحوال، لأنه لما سئل عنها على الإبهام، لم يتعين [الحال] بحال.','2','7',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (704,'وكذلك إذا كان الفعل المسئول عنه غير داخل في الوجود، مثل أن يُسأل عمن يواقع في نهار رمضان. بل هذا أبعد من التخصيص، لأن الفعل لا يدخل الوجود إلا مخصصا. أما إذا لم (٥٨/أ) يدخل، [فالإطلاق] حاصل، باعتبار السؤال، ولا وجود يخصص الواقعة.
[فهذه الأوجه لا] يتأتى الخلاف في شيء منها، وإنما يتصور الخلاف في [نفي] وجه واحد، وهو إذا [كانت] الواقعة حاصلة في الوجود، وأطلق السؤال عنها، والالتفاف إلى القيد الوجودي، يمنع القضاء على الأحوال [كلها]، والالتفاف إلى الإطلاق في السؤال، يقتضي استواء [الأحوال] في غرض المجيب، [فالتفت] الشافعي [لهذا] الوجه. وهذا أقرب إلى مقصود الإرشاد، وإزالة الإشكال، وحصول تمام البيان، فإن الشارع، وإن قدر إحاطته بقيد الواقعة، لكن ظاهر الحال أنه رتب الجواب على مطلق السؤال.','2','8',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (705,'[وأبو حنيفة] يقول: هذا تعميم بالتحكم، وما المانع من ترتيب الجواب على ما [علم] المجيب من [قضية] حال الواقعة؟ فإذا قيل له: يحصل [التلبيس]. قال: لا لبس في ذلك، فإن الجواب لا عموم [له]، وإنما يتلقى العموم من الاستبهام، ولم يتحقق، [فمن اعتقد] [الاسترسال] على الأحوال، فقد [تحكم]. هذا القول أدق في نظر الأصول، وإن كان [للأول] [وجهٌ] بين في قصد تمام البيان.
قال الإمام: (ولكن [وجه] الدليل [في الخبر] واضح [في قصة','2','9',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (706,'غيلان، فإنه صلى الله عليه وسلم قال: (أمسك أربعا)). إلى آخر المسألة]. قال الشيخ: ما ذكره النقلة عن أبي حنيفة أنه عمم أمورًا لا يصير إلى تعميمها [شاد] في الأصول، [فقد صار الإمام] إلى مثل ذلك. قال في كتاب','2','10',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (707,'القياس: (قد يقول القائل: من باع ثوبًا، زال ملكه عنه، [وهو] يقصد جنس المال، ولكنه يريد [ضرب مثلٍ] لخفته عليه. وكذلك يقول من باع عبدا وهو يريد جنس الرقيق، فيكون اللفظ عامًا، ويكون المقيد غير مقصود بالتقييد. وكذلك قال بعض العلماء في قوله تعالى: {ومن قتله منكم متعمدًا}. هذا يتناول المخطئ. قال: والتقييد [بالعمد] ليس بمقصود، لكنه [ذكر] لكونه المعتاد، فكأنه قال: ومن قتله منكم- والغالب أنه لا يقتله إلا متعمدًا- فجزاء مثل ما قتل من النعم. وأبو حنيفة يمنع القياس في الكفارات، ويقول: إن الآكل يكفر، فليس ذلك بقياس عنده، ولكنه [قال: هو] من باب','2','11',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (708,'تنقيح المناط، فيرجع حاصل الكلام [إلى أنه تمسك] باللفظ المؤول الدال الظاهر. وسنذكر طريقه فيه.
فيقدر أن السائل قال: (هلكت وأهلكت، أفطرت في نهار رمضان). فذكر الجماع من جهة كونه إفطارًا، لا من (٨٣/أ) جهة كونه وقاعا. ولو قال: أفطرت في نهار رمضان، لكان في معنى العموم، [إذ] أطلق الجواب.
لكن هذا لا يمشي على أصل أبي حنيفة [الذي يقول]: تطرق الاحتمال إلى حكايات الأحوال يتنزل منزلة الإجمال في المقال، إلا أن يتبين','2','12',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (709,'استبهام الحال على المسؤول، أو يتبين من غرضه استواء الأحوال. فيقال له: إن كان تنقيحا، فتمسك بتأويل لفظ، وإن كان استنباطا، فقياس. [والقائس] أيضًا يبدي قضية عامة يتمسك بها في تعميم الحكم، ويعرض عن خصوصية اللفظ، والمنقح أيضًا كذلك يفعل، فلابد من الوجه الذي يقتضي الفرق بين البابين. فيقول: الفرق بينهما أن المنقح يرد اللفظ (٥٨/ب) [بعد التنقيح إلى] جهة تقبل من جهة اللغة، [يدل] اللفظ عليها. والقائس يبدي قضية لا يصلح [اللفظ من جهة] اللغة للدلالة عليها، فيعم الحكم، مع اعترافه بفقدان دلالة [اللفظ] لغة، ظهورًا [واحتمالا]. والمنقح إنما يبغي الدلالة من جهة [الظهور، لا من جهة] الاحتمال. وإذا تقرر ذلك، وجب الالتفات إلى اللغة، هل أطلق واضع اللغة لفظ الجماع، باعتبار كونه إفطارًا؟ [أو ما وضع] إلا للدلالة على الخصوصية، التي هي الوطء؟ والصحيح عندنا هذا، فإن الجماع لفظ معقول قبل ورود الشريعة، وكذلك الوقاع، فتصرف أبي حنيفة في اللفظ، باعتبار إبداء الاحتمال، ضعيف، وهو لو اعترف بقصور اللفظ، لم يعد الحكم بحال.','2','13',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (710,'قال الإمام: <span data-type="title" id=toc-130>(مسألة: في أقل الجمع، اضطرب رأي العلماء في ذلك) </span>إلى قوله (غير مستحسن في هذا الفن). قال الشيخ: أما قوله: إن مذهب ابن عباس لم يوجد إلا استنباطا. ليس كما قال، [فإنه قال] لعثمان رضي الله عنه: (ليس الأخوان إخوة في لسان قومك). [فقد صرح] بأن التثنية','2','14',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (711,'[ليست] بجمع. وأما أخذ [ابن مسعود] من الصورة المذكورة، [لا] يظهر [وجهه] ظهورًا جليًا، إذ يحتمل أن يكون قيام الرجلين عن يمينه وشماله من سنن الصلاة، ولم يبن الأمر [لا] على تثنية، ولا على جمع.
وأما الصورة التي تمسكوا بها، فلا متمسك فيها، فإن العرب كرهت الجمع بين مثنيين، وقد يجوز إطلاقها، وقد جمع الشاعر [بينهما] في بيت، فقال','2','15',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (712,'. .... .... ظهراهما مثل الترسين.
وإنما فعل [هذا] عند عدم اللبس، إذ قد عرف أنه ليس للشخص إلا قلب واحد، فلا يوهم إطلاق لفظ الجمع [لبسا]. [ويمتنع] أن يقال: [فقأت] أعينهما، إذا أراد عينين، للبس الحاصل. وقد','2','16',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (713,'[جاء]: وضعت [رحاليهما]، وهو شاذ.
فإن قيل: قد جاء في كتاب الله [عز وجل]: {والسارق والسارقة فاقطعوا أيديهما}. فذلك يوهم قطع الجميع، وليس الحكم كذلك. قلنا: لا جرم أن في حرف عبد الله [بن مسعود] (فاقطعوا أيمانهما). حذرًا من هذا اللبس.
ووجه الرواية المشهورة: أن الله [تعالى] [لما] بين على لسان','2','17',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (714,'رسول [صلى الله عليه وسلم] جميعا، وأنه تقطع له يد واحدة، صار هذا البيان مزيلا لذلك الإشكال، وإلا فالإشكال مانع من الجمع في محل اللبس. وفيها لغتان [أخريان].
إحداهما- إطلاق التثنية حرصًا على البيان، فيقال: ظهراهما.
وقد يقال: ظهرهما، وينوي الجنس في لغة الجمع، إذا كان لفظ الجمع (٨٣/ب) يصادف على [صيغته] [مفرد]، كالقلوب والظهور، فكأنما إنما نطقنا بمفرده وأضفناه.
قال الإمام: ([ومما] تمسكوا به الضمير الذي يعني به المتكلم نفسه وغيره [منفصلا] [ومتصلا] إلى قوله ([فلا يحكم] [بهذا اللقب الذي استحدثه] المتأخرون على موضع اللفظ). قال الشيخ [أيده الله]: ما','2','18',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (715,'ذكره الإمام في هذا واضح. وقد (٥٩/أ) صار القاضي إلى أن أقل الجمع اثنان.
وتمسك بما تقدم ذكره، وزاد أيضًا التمسك بآيات من كتاب الله تعالى جاء فيها لفظ الضمير مجموعًا، عائدا على اثنين. [فقال]: قال الله تعالى لموسى وهارون لما بعثهما إلى فرعون: {إنا معكم مستمعون}. وقال: {وهل أتئك نبؤا الخصم إذ تسوروا المحراب (٢١) إذ دخلوا على داود ففزع متهم [قالوا] لا تخف خصمان}. وقد أجيب عن هذا بأن قيل: إنما قال: {إنا معكم مستمعون} نستمع إليكما مع فرعون.
وقالوا: الخصم ينطلق على الواحد والاثنين والثلاثة، [فلسنا] نسلم','2','19',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (716,'[أنهما] كانا اثنين فقط، وهذا كقوله [تعالى]: {وإن طائفتان من المؤمنين اقتتلوا}. إذ كل طائفة جماعة. قال القاضي: هذه تكلفات وتعسفات لا حاجة [إليها].
ومما رد به عليه: أنه لو كان الاثنان جمعا، لصح أن يقال: رأيت اثني رجال، كما يقال: ثلاثة رجال، وهذا واقع، ولكن قال القاضي: لم نقل ذلك، لأن أهل اللغة لم يطلقوه. وهذا ضعيف. وحاصله تسليم الاستدلال، فإنهم استدلوا بذلك على أن التثنية ليست جمعا، إذ لا يمتنع في الأعداد إضاقة الجمع [إلى الجمع].
ولكن السبب في امتناع ذلك عندي: أن الاثنين في باب الأعداد لا تضاف على حال، [لا] إلى اثنين، ولا إلى أكثر من ذلك، فإنه [يمتنع]','2','20',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (717,'أن يقال [اثني] رجلين. [ولفظ] الواحد والاثنين في العدد لا يضاف بحال.
والسبب في افتقار الجمع إلى [ذكر] العدد والمعدود جميعا، ما في لفظ الجمع من اشتراك، ولما كان المفرد والمثنى لا اشتراك فيه، اقتصروا على لفظ واحد، [فقالوا]: رجل، ورجلان، وثلاثة رجال، إذ لو [قالوا]: رجال، لم يتبين المقصود من المعدود، [وافتقر] [إلى ذكر] [العدد].
والذي يصح [عندي] في هذه المسألة: أنه لا يمتنع إطلاق لفظ الجمع على الاثنين مجازا، لما فيهما من معنى [الاجتماع]، ولكن [لا','2','21',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (718,'يصار] إلى ذلك إلا بدليل. وقد قال النحويون: التثنية ضم شيء إلى مثله، والجمع: ضم [شيء] إلى أكثر منه. هذا هو الظاهر عندهم. وما وقع التمسك به من هذه الآيات، [فإن] وجد [فيه] الثلاثة، فقد جرى لفظ الجمع على الحقيقة. وإن وجد [فيه] الاثنان، فقد جرى اللفظ على جهة المجاز.
[وهل يصح] [رده] إلى الواحد؟ هذا فيه خلاف. والذي عليه الأكثرون أنه لا يصح الرد إلى الواحد، لبطلان حقيقة الجمع. ولذلك صار','2','22',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (719,'معظم الناس إلى أن ألفاظ العموم نصوص في [أقل] الجمع، [وإن] اختلفوا في أقل الجمع، هل هو اثنان أو ثلاثة؟
وقد نقل عن أبي الحسن وأصحابه أنهم قالوا: اللفظ مشترك بين الواحد، اقتصارًا عليه، وبين أقل الجمع فما فوقه. (٨٤/أ) والمصير إلى حمل لفظ المشترك على مشترك واحد مكابرة [للغة]، وإن كان الرجل العظيم قد يقول: فعلنا، فليس ذلك [بجمع]، وإنما هو للتعظيم، وكذلك لفظة نحن. هذا هو الذي يصح عندي في هذه المسألة. وبالله التوفيق.
قال الإمام [رحمه الله]: (فإن قيل: فما [المرتضى] [إذًا]؟','2','23',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (720,'[قلنا: هذه المسألة موضوعة على رأي المعممين، فمطلق اللفظ معناه في مختارنا ما سبق] إلى قوله ([على ما سنوضحه في كتاب (التأويلات)، إن شاء الله]). قال الشيخ رضي الله عنه: قول الإمام: هذه المسألة موضوعة على رأي العممين، [يرى] أنه لا حاجة إليها، إلا إذا قامت المخصصات، (٥٩/ب) وإلا [فالألفاظ للعموم] عند فقدان أدلة التخصيص، والذي قال غير صحيح، لا على أصله، ولا على أصل غيره.
أما أصله، فهو يرى أن الألفاظ عند التنكير لأقل الجمع، فإذا لم يعرف اقل الجمع، كيف يحكم بأن الألفاظ مقتصرة عليه؟ وكذلك يقول في جمع القلة. [وإن] عرف أنه لأقل الجمع، فلابد إذًا من بيان أقل الجمع، بالإضافة إلى جمع الكثرة المنكر، [وإلى] جمع القلة وإن عرف','2','24',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (721,'وأما على رأي الفقهاء، فإنهم مفتقرون إلى ذلك فيما يتعلق بالإقرار والإنكار والالتزام والإلزام والوصايا وغيرها.
[فقوله]: إنها موضوعة على رأي المعممين. لا يظهر له وجه. وأما الرد إلى [الاثنين]، فقد [بينا] صحة الرد إليه، فلا يمتنع ذلك. وإذا ثبت كون اللفظ ظاهرًا في الاستغراق عند وجدان صيغ العموم، فإذا طلب التخصيص، [فكلما] كثر المخرج، افتقر إلى قوة الدليل المخرج. فإذا انتهى إلى اثنين، [بطل [التخصيص، [إذ] انتهى اللفظ إلى ما هو نص فيه.','2','25',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (722,'قال الإمام [رحمه الله]. (والذي أراه أن الرد إلى الواحد ليس بدعًا [أيضًا]، ولكنه أبعد من الرد [اثنين] [بكثير]) [إلى قوله] (فهذا ما [أردنا] في هذه المرتبة). قال الشيخ: ما ذكره من صحة الرد إلى','2','26',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (723,'الواحد، فقد بينا بطلان ذلك، وأن اللفظ نص في أقل الجمع. وما ذكره من المثال، لا حجة فيه بحال، وقد قال هو: إنه ليس من مقتضى اللفظ. وإنما [صارت] رؤية الواحد [سببًا] للتوبيخ على التبرج للجنس.
والذي يحقق ذلك، أن [صيغة] الجمع في الصورة أحسن. ولو كان المراد التعبير عن الواحد. لم يختلف أهل اللغة [قي] أن الجمع لا يكون أحسن من الواحد. فلما اختير لفظ الجمع، دل على أن المراد النهي والتقريع في التبرج للجنس. وقد يصح أن يقصر الزجر على من رآها تبرجت له، والأول','2','27',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (724,'أحسن، لاستواء الحمية في التبرج للجنس آحادًا [أو] جمعًا.
ففرقٌ بين إطلاق لفظ الجمع على الواحد، وبين كون الواحد سببًا لإطلاق لفظ الجمع على حقيقته.
قال الإمام: ([وإما] الرد إلى اثنين، فيسوغ بما يسوغ (٦٠/ أ) به الرد إلى الواحد) إلى قوله (في صيغة العموم [نوضح] ذلك كله). قال الشيخ: قوله: أما الرد إلى اثنين فيسوغ بما ذكرناه. قد بينا في الرتبة السابقة أن لفظ الجمع لم يرجع إلى [الاثنين] بحال، وإنما كان رؤية الاثنين، سببًا لإطلاق لفظ الجمع على مائة دالًا على حقيقته. وأما الجنس الآخر، وهو إذا بدا [للرجل] رجلان، وكان لا يخاف الواحد منهما، وإنما خشي اجتماعهما، فإنه يحسن أن يقول: أقبل الرجال، فيطلق لفظ الجمع بإزاء [المتجمع]،','2','28',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (725,'(٨٤/ب)، لأنه إنما كره التجمع، والتجمع موجود. هذا قوله.
وأما نحن فنرى [صحة إطلاق] لفظ الجمع على الاثنين مجازًا، فلا حاجة إلى كل هذا [التكلف].
وقوله: (وإطلاق صيغة التثنية [ههنا] [أشكل] [وأوفق]). الأمر على ما قال، لأن من يتقي التجمع [يحذر] من الثلاثة ما لا [يحذر] من الاثنين. فلا يقال في هذه الرتبة: استوى في غرضه الاثنان والثلاثة، كما قيل رتبة التبرج، [فإن الغرض [هناك] يقتضي استواء غرض العقلاء في كراهية التبرج] على الإطلاق.','2','29',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (726,'قال الإمام: (فأما [القول في الرتبة الثالثة، وهو] الرد إلى الثلاثة، فلا يستدعي ذلك قرينة حاقة في جنس مخصوص، وبهذا تنفصل المسألة). قال الشيخ: مراده بهذا الكلام أنه يرى أن اللفظ في الزيادة على الواحد لا يتغير، [إلا] [بقرينة] واحدة، وهو نص في الزيادة [على] الاثنين، لا ينقص عن ذلك إلا بالقرينة السابقة، وبقرينة أخرى، وهي كراهة التجمع، ويرى أنه ليس بنص في الزيادة على الثلاثة بحال، بل إما أن يكون مقتصرًا، أو ظاهرًا في الزيادة. فإذا دل الدليل على إزالة الظاهر، ترك ولم يقتصر على نوع مخصوص، كسائر الظواهر. قال: وبهذا تنفصل المسألة. وهذا هو الذي أراده في أول المسألة.
وقوله: (وحق الناظر في هذه المسألة أن ييأس من [الوقوف عليها]، ما لم يستكملها). معناه: أن المقصود منها لا يحصل إلا على التدريج. [و] في بعض الكتب: (ألا ييأس من الوقوف عليها، ما لم يستكملها).','2','30',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (727,'[معنى] هذه الرواية: أن الناظر في أول أمره، لا يظهر له مقصودها، فلا ينبغي أن يكون ذلك سببا يمنعه من التكميل، فإن المقصد منها يظهر على التدرج.
قال الإمام [رحمه الله]: (نعم، إن لم يقم دليل هو مستند التأويل، فقد بان وجوب الجريان على الظاهر، [فجمع الكثرة] للاستغراق) إلى قوله (ولا أرى للنزاع في أقل الجمع معنى إلا ما ذكرته). قال الشيخ: قد تقدم الكلام أن فائدة المسألة معرفة ما يتيقن دخوله تحت اللفظ، بحيث لا يجوز التنقيص منه. وإذا تحقق ذلك، امتنع إخراج بطريق التخصيص، وامتنع أيضًا النزول عنه في جانب الإقرار والوصايا وغيرها.','2','31',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (728,'وأما مالك رحمه الله فإنه لم ير للمقر التفسير باثنين على حال. وكذلك يقول الشافعي. وهذا يدل على أنهم رأوا أقل الجمع ثلاثة.
وأما كون [مالك] يحجب الأم من الثلث إلى السدس باثنين من الإخوة، فلا يلزم أن يكون [يرى] الاثنين جمعا، ولكن استقر في قاعدة المواريث أن كل موضع فرق فيه بين الواحد والجمع، سلك بالاثنين مسلك (٦٠/ب) الجمع، لا مسلك الواحد. من ذلك للبنات الثلثان، [وللبنت النصف]، وللاثنتين الثلثان، وكذلك الأخوات. فيمكن أن يكون مالك سلك هذا المسلك في الحجب، لا أنه رأى الأخوين إخوة، وإلا فمسائله في الوصايا والإقرارات أنه لابد من ثلاثة).','2','32',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (729,'قال الإمام [رحمه الله]: ([وليعلم] الناظر أن معظم الخلاف سببه توسط النظار النظر من غير استتمام له. وقد ظهر في [المعقول] تباين الرتب [الثلاث]) (٨٥/أ) [إلى قوله] (لما كان لاختلافهم معنى). قال الشيخ: قوله: إن سبب الاختلاف توسط النظار النظر من غير استتمام له. كلام لا يستقيم، فإن فيه نسبة العلماء إلى التقصير في النظر وعدم الاستتمام، وذلك حرام، لاسيما على قول من يقول: كل مجتهد مصيب، فلو لم يستتم النظر، لم يكن مصيبا، وإن قيل: [المصيب] واحد، فإنما يكون المخطئ [مثابا] على تقدير أن يفعل ما يمكنه من الاجتهاد. فأما إذا قصر، فعاص','2','33',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (730,'بالاتفاق. فكيف يحل أن ينسب العلماء إلى توسط النظر من غير استتمام؟ وسيأتي له [أيضًا] مثل هذا.

قال الإمام: <span data-type="title" id=toc-131>(فصل-[الصيغة] الموضوعة للعموم تنقسم إلى المطلق والمقيد) </span>إلى آخر [الفصل]. قال الشيخ [وفقه الله]: ما ذ كره ههنا، كما مضى في صيغة الأمر، فالمراد بالإطلاق ههنا، هو المراد بالإطلاق هناك، والتجرد عن جميع القرائن محال. كما تقدم. وإذا قلنا: صيغة مطلقة في','2','34',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (731,'العموم، فإنما نعني بها صيغة تحقق قصد مطلقها [إلى وضعها] دالة على ما تدل عليه عند أهل اللسان في أصل الوضع، وعريت عما وراء ذلك من قرائن الحال والمقال.

قال الإمام: <span data-type="title" id=toc-132>(مسألة: إذا ورد [في] الشرع [لفظ] يتناول في اللغة الأحرار والعبيد، فهو عند المحققين محمول على الجنسين) </span>إلى آخر المسألة. (٦١/أ) قال الشيخ: الأمر على ما ذكره الإمام بلا مزيد عليه.','2','35',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (732,'والذي تخيله القوم من حيث الجملة أن متعلق التكليف أفعال المكلفين، وتلك الأفعال ملكها الشرع للسادات من العبيد قبل جريان [التكليف]، فلم تأت التكاليف إلا والأفعال مملوكة عن العبيد، ولا يندرجون تحت مطلق الخطاب، إلا أن يأتي خطاب خاص يقتضي اقتضاء حقوق السادات من منافع العبيد. وهو خطأ من وجهين:
أحدهما- أن بعض التكاليف [متعلق] [بما] لا يزاحم [حقوق السادات] من أعمال القلوب، وبعض أعمال الجوارح، فكان يجب اندراجهم تحت مطلق الخطاب في هذه الأفعال [التي] لا تتعلق بها حقوق السادات.
الوجه الثاني- أن تلك الأفعال إنما ملكها السادات بتمليك الشرع، والذي ملك بعض الأفعال جعل التكاليف متعلقة ببعضها. ويدل عليه: أن ورود الخطاب الخاص يقدم على ملك [السادات]. وإذا ورد الظاهر في محل العمل تنزل منزلة النص. نعم، إن اقتضى الظاهر اندراجًا، واقتضت سلطنة','2','36',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (733,'الملك إخراجًا، نظر في الترجيح. [وقد يندرج] كما في الصلاة والصوم، وقد يترجح الإخراج، كما في الجهاد والحج والزكاة. وقد تتعارض الجهات، فيجري الاختلاف، كما في الجمعة.
وقد اختلف أهل العلم هل على العبيد جمعة [أم لا]؟ واختلف فيه قول مالك، والأكثر [على] قوله أنه لا جمعة عليهم، لأنها شرعت لإظهار شرف الإسلام بإقامة ذوي الهيئات والأقدار، والعبيد ليسوا كذلك.
وأما ما [سقط] من الأحكام عنهم، لمزاحمة حقوق السادات، فالإقرار فيما يتعلق بالأموال، لتعلق حقوق السادات بها، فليس للعبد أن يقر في المال الذي بيده، ليفوت حق سيده، إما من ملكه، أو من حق التمليك (٨٥/ب)،','2','37',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (734,'عند من يرى العبد [مالكًا].
وهل يصح إقرار فيما يتعلق ببدنه؟ هذا مما اختلف العلماء فيه، فذهب مالك [رضي الله عنه] إلى قبوله والقضاء به، بشرط أن يقتص المقر [له]. وأما إن [عفا] على أخذه، بطل إقراراه، وعاد إلى الإقرار في المال. وذهب غيره [إلى رده]، لأنه [يتضمن] تفويت ملك سيده. ومحط النظر أن [قوله] دائر بين الإقرار والشهادة، لأنه من حيث [تناول] إقراره حق السيد شاهد، ومن حيث إنه يتصرف في نفسه مقر، [فرجح] مالك أمر الإقرار، باعتبار النفس، وأمر الشهادة، باعتبار المال، لانتفاء التهمة بالعقوبة الراجعة إلى البدن.','2','38',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (735,'[قال الإمام: <span data-type="title" id=toc-133>(مسألة: ] إذا ورد في لفظ الشارع صيغة جمع السلامة كالمسلمين والمؤمنين، [مما هو مرتب] على [تثنية] مؤمن [وأشباه ذلك] في الذكور</span>. ففي تناول هذه الصيغة عند الإطلاق للنساء خلاف. فذهب ذاهبون إلى أنه يتناول النساء، واستدلوا عليه: بأن العرب إذا حاولت التعبير عن الذكور والإناث بصيغ جمع السلامة، فمن [مذهبها] المطرد تغليب التذكير، وهذا مشهور عنهم، مسطور في كتب أئمة العربية، والرأي الحق عندنا خلاف','2','39',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (736,'ذلك، والذي (٦١/ب) تخيله هؤلاء وهم [وزلل]، [وذلك أنه] لا يخفى [على من] [شدا] طرفا من العربية [أن] قول القائل: [مسلمان]، [مبني على قول القائل: مسلم]، وقول القائل: [مسلمون] مبني على قوله: مسلم [ومسلمين]. وهذه التقاسيم أظهر [من] أن يحتاج [في] إثباتها إلى تكلف وإطناب. ثم [ميزت] العرب [باب] الإناث) [إلى','2','40',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (737,'قوله] (فلا شك في تناولها [لهما] كالناس والقوم [وما أشبههما]). قال الشيخ [أيده الله]: لا خلاف بين أهل العربية والأصول أن جمع المذكر السالم لا يتناول عند إطلاقه الإناث بحال، وقد قال الله تعالى: {إن المسلمين والمسلمات والمؤمنين والمؤمنات} [الآية]. وهذا مقطوع به','2','41',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (738,'على كل حال. ولم يذهب من ذهب من الأصوليين إلى أنه يتناول [الجنسين]، لأجل المنازعة في ذلك، ولا المناكرة فيه، ولكنه لما كثر في أحكام الشرع أن حكم [الذكور] والإناث واحد، وصار ذلك غالباً، [كان] تقدير هذه العادة الغالبة، تبين أن الشرع لا يقصد قصر الأحكام على الذكور، ولو ثبت بصريح اللفظ القصر على الذكور، لتعدي الحكم، بناء على القياس، إلا أن يثبت التخصيص [مقصودًا] بالقصر.
وإذا كان الحكم يتعدى، [بناء على القياس]، فإنما يصار إلى القياس، بنا على قصور اللفظ عن التناول. والحكم ههنا يصح استناده إلى اللفظ، وإن كان بطريق المجاز. ولا خلاف أن قول الله عز وجل: {يأيها الذين امنوا}.','2','42',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (739,'لا يختص بالذكور، فهذا مستند الأصوليين الذاهبين إلى تناول [الجنسين]. ولكن هذا أيضًا غير مطرد، فإنه قد ثبتت أحكام للذكور دون الإناث، وللإناث دون الذكور، ولكن الغلبة في جانب المساواة. وإذا حكمنا بتناول اللفظ للذكور والإناث، فهل نقول: اجتمع في اللفظ موجب الحقيقة والمجاز، أو يكون تناولها جميعًا [مجازًا]؟ هذا مما يختلف فيه، وقياس مذهب القاضي (٨٦/ب) أن يكون مجازًا [صرفًا]، وقياس قول الإمام أنه اجتمع في اللفظ موجب الحقيقة والمجاز. وسيأتي لهذا مزيد تقرير في العام إذا تطرق إليه التخصيص.
قال الإمام: ([مسألة]: (من) من الألفاظ المبهمة، [وهي] إحدى','2','43',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (740,'صيغ العموم [في اقتضاء الاستغراق، إذا وقع شرطًا، ويتناول الذكور والإناث]) [إلى قوله] (في قضية من القضايا). قال الشيخ: ما ذكره الإمام من كون [) من)] الشرطية غير مختصة بذكور وإناث، وجمع ووحدان. الأمر على ما ذكره، لا خلاف عند أئمة العربية في ذلك.','2','44',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (741,'ويثبت ذلك من (٦٢/أ) إبهام [الشرط] وعدم اختصاصه. إلا أنه [إن] كان مستند أصحاب أبي حنيفة ما ذكره من صورة جمع الذكور فيها على خلاف صورة [جمع] الإناث. فمتضمن هذا الطريق أن لا يتناول أيضًا جمع الذكور، نظرًا إلى أفراده، فلا يكون متناولًا واحدًا من الذكور، كالنكرة في الإثبات. وهذا لا يذهب إليه أحد، فلا يصح أن يكون هذا مستند القوم. نعم، إن التفت إلى [أن] لفظه لفظ الذكور، ثم يتمسك في التعميم بالإبهام، فهذا قد يكون وجهًا، وإن كان مريبا. أما التقرير الأول، فلا يتخيل له وجه بحال.
والذي يحقق أن لفظه لفظ الذكور، عود الضمير عليه في قوله:','2','45',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (742,'(من بدل دينه فاقتلوه). وكذلك قوله: (من أحيا أرضا ميتة فهي له). وأما قول الشاعر، ففيه شذوذ من أوجه:
أحدها- أنه استعمل اللفظة في الخبر دون الحكاية، وهي لا تستعمل إلا في الحكاية بالزيادة.
والثاني- أنه أثبت الزيادة في الوصل، وهي لا تثبت إلا في الوقف.
الثالث- أنه حرك، وهي لا تستعمل إلا ساكنة.
فلا التفات إلى [هذه الجهات] من الشذوذ].','2','46',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (743,'قال الإمام: (ثم للعرب مذهبان [سائغان]: [فمنهم من يكني عن معنى (من)، ومنهم من يرد الكناية إلى [اللفظ]]) إلى قوله ([والقانون المتفق عليه ما ذكرناه]). قال الشيخ: (من) [منفردة] اللفظ معناها','2','47',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (744,'معنى الجمع في كثرة [التناول]، فيجوز رد الكناية إلى اللفظ تارة، وإلى المعنى أخرى، وكذلك القول في التذكير والتأنيث. وقد قرئ قوله تعالى: {ومن يقنت}. و} [من] تقنت}. واتفقوا على قوله: {وتعمل صلحًا}.
وأما [قوله]: [إنهم] اعتبروا [بالبيت]، فقد بينا أنه لا يصلح أن يكون ذلك مستندًا.
وقوله أيضًا: (إن بعض العرب يقول .. ). ليس كما قال، وإنما [تقول] [العرب] للواحد من [الذكور]: [منه]، بشرط أن يكون','2','48',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (745,'نكرة، هو في حال الوقف، فإذا صار إلى [الدرج]، أسقط العلامة.
وقوله بعد ذلك: (إنه من شواذ اللغة). [إن أراد] أنه لا يستعمل إلا في هذا المكان، فصحيح، وإن [أراد] أن شاذ في باب الحكاية للنكرات، فليس كذلك، فلا شي في حكايات [النكرات] إلا هذا.
وقوله: (إن ما ذكروه-[إن ساغ]-[فالأفصح] غيره). لا وجه له، فإنه لا طريق في حكايات [النكرات] إلا هو، فكيف يصح أن يكون الأفصح غيره؟ وإن أراد ما ذكره أصحاب أبي حنيفة من الاختصاص بالذكور، فليس [ذلك] لغة العرب بحال.','2','49',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (746,'قال الإمام: <span data-type="title" id=toc-135>(مسألة: اختلف الأصوليون في دخول المخاطب تحت الخطاب [في مثل القائل لمأموره: ) إلى آخر المسألة]</span>. قال الشيخ:','2','50',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (747,'هذا قول [بعض] الأصوليين، إلا أن أهل اللغة يفرقون بين (٨٧/أ) ألفاظ الحضور وبين ألفاظ الغيبة، فجعلوا للحضور الضمائر: كأنا وأنت ونحن، وما أشبه ذلك، وجعلوا الأسماء الظاهرة كلا للغيبة، وكذلك بعض المبهمات، ك (من). والذي يدل على صحة ذلك، لحوق علامة الغيبة الفعل، كقولك: زيد يفعل، وعمر يقوم، ولا تقول: زيد تقوم، [والتاء] علامة الخطاب في [المذكر]. وإذا كان كذلك، فقوله: من دخل الدار فله درهم، هذا كلام لا يتناول إلا الغائب. فكون القائل يكون داخلا تحته [مع] كونه مخاطبا متعذر،','2','51',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (748,'إلا أن يعرض عن قضية الحضور والغيبة، ويلتفت إلى كونه داخلا. وهذا ضعيف، فإنه لو قال: من دخلها من جنس سماه، لم يتناول الخطاب غيره من الداخلين. وهذا أمر يفتقر إلى دليل، وليس ظاهر الخطاب يدل عليه، إلا أن يثبت أن واضع اللغة جعل ألفاظ الغيبة العامة تتناول المخاطب، وهذا لم يثبت.
فالظاهر عندي ما قالته الطائفة [الثانية] من الأصوليين.','2','52',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (749,'قال الإمام: (٦٣/ب) <span data-type="title" id=toc-136>([مسألة: إذا ورد] خطاب مطلق في الكتاب [يشمل الأمة] بصيغة تصلح في الوضع للرسول - عليه السلام </span>-) [إلى آخر المسألة]. قال الشيخ: الأمر [على] ما ذكره من شمول اللفظ من جهة','2','53',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (750,'اللسان، وقبول الرسول الحكم، ومصير المحققين إليه. ومعنى كلام من ذهب إلى أنه غير داخل، أنه على خصائص، لم يرد أنه ثبت [له أحكام] يخالف فيها [الأمة] فقط، بل ترجع تلك الخصائص إلى تشريف وتعظيم لا توجد في حق غيره.
وأما الأحكام التي [تثبت] للمسافرين من دون المقيمين، والحيض [من] دون الطاهرات، فليس ذلك لمقتضى التشريف، لا جرم اشترك الجنس','2','54',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (751,'في ذلك، [واللائق] بخطاب الإكرام [أن] لا يندرج [المكرم] مع عموم المخاطبين، وإن اتفق أن يكون هذا، فليس ذلك بضربة لازم، بل إن فعل ذلك، فإنه حسن، وقد قال الله تعالى: {لقد تاب الله على النبي والمهاجرين والأنصار}. وقال الله تعالى: {يأيها النبي إذا طلقتم النساء}. فخصه بالخطاب [وإن] أراد الأمة، وكذلك قال الله تعالى: {يأيها النبي حسبك الله ومن اتبعك من المؤمنين}. فخض بالخطاب وذكرت الأمة [بعده]. هذا نهاية التشريف والتكريم، فلما قاله القوم وجه بين، وليس [يرجع] [إلى]','2','55',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (752,'محض ثبوت أحكام امتاز بها عن الأمة، كما ذهب إليه الإمام.
والذي رد به الإمام على الحليمي والصيرفي، كلام بين، فإن الأمر بالتبليغ ثابت في جميع (٦٤/أ) آي القرآن، إما [مصرحًا به]، أو مقدرًا، والقائل هو الله عز وجل، ولم يؤمر الرسول بأن يقول: [إن القرآن ليس إليه وضعه، ولو كان المراد الأمر بأن يقول]، لأسقط [) قل)] من التلاوة','2','56',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (753,'وابتدأ: يا [أيها]، فهذا خيال باطل، لاشك فيه، والقائل هو الله تعالى، وليس للرسول إلا التبليغ، والخطاب العام لا يغيره أمر بالتبيلغ.

قال الإمام: <span data-type="title" id=toc-137>(مسألة: إذا وردت صيغة مختصة [في وضع اللسان برسول الله صلى الله عليه وسلم] إلى قوله (في طرد اعتقاد المشاركة)</span>. قال الشيخ: الأمر على ما (٨٧/ب) ذكره الإمام [من] قصور اللفظ، من جهة وضع اللسان، ولكن يبقى النظر فيما استقر الشرع عليه. والصحيح أنه واحد من المكلفين، يطرد عليه ما اطرد على الخلق إلا ما ثبتت الخاصية فيه. كاختصاصه بصفي','2','57',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (754,'المغنم، وتزويج تسع، وإسقاط فرض القسمة بين الزوجات عند بعض العلماء. فهذا [لا] سبيل إلى المشاركة فيه.
فإذا الأصول باعتبار غرضنا تنقسم: إلى أصل ظهرت المساواة فيه [بيننا] وبينه، فهذا [إذا] استقر فيه حكم له، ساويناه فيه. ولما (رأى ابن','2','58',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (755,'عمر رسول الله صلى الله عليه وسلم على لبنتين مستقبلا بيت المقدس، مستدبرا الكعبة لحاجت). استدل بذلك على عدم تحريم الاستقبال إذا كان في بناء. وكذلك ما يتعلق بالوضوء والغسل والصلاة [والبيع] وغيره. هذا مقطوع به.
[الأصل] [الثاني]: [أن تظهر] الخاصية في غير القضية، فهذا لا سبيل إلى التمسك به بالكلية، كتزويج تسع، ووجوب قيام الليل عند بعض الناس. وكذلك تفرقته بينه وبينهم في الوصال.','2','59',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (756,'الثالث: أن يكون [الحكم] في قاعدة ظهرت فيها الخواص، ولكن لم تثبت الخاصية في الحكم المعين، فهل يقع الاقتصار عليه، أو يحكم بتعدية الحكم إلى غيره؟ هذا في محل النظر. [فقد] يقال: إن خاصيته في القاعدة في القليل منها، والكثير جرى فيه على المساواة، فيقضى بالمساواة إلى كثرة الأحكام التي ساوى الأمة فيها. وقد يقال: [يقع] الاقتصار [لاقتضاء] الصيغة التخصيص، والعلم بخصائص الرسول في القاعدة.
والأغلب [على الظن] الالتفات إلى الكثرة في الاقتصار والمجاوزة. فهذا يقضى [فيه] بالقياس، نظرا منا إلى أكثر الأحكام المعللة. وإن أمكن الاستثناء والتعبد، فلا تكون الأمور النادرة مانعة لنا من إجراء الأمر على الغالب','2','60',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (757,'في مسائل الظنون. هذا هو الظاهر عندنا [في] هذه القاعدة.
قال الإمام: (ومما يتعين [له التنبيه] [الآن إلى أن يجيء تقريره في باب (التأويلات) أن كل ظهور متلقى من وضع اللسان]) إلى آخر المسألة. قال الشيخ: قول الإمام: (إنه نظر أن الصحابة كان يحتج بعضهم على بعض بالآيات الواردة في حق الرسول صلى الله عليه وسلم). وقال: (لا أقطع بهذا منهم في اعتقادهم طرد المشاركة). ورتب على ذلك أنه (لا يتمسك بهذا النوع من غلبة الظن). يقول: إنه لا يعمل في الأصول بشيء مظنون، إلا إذا انعقد عليه إجماع أو نص عن الرسول صلى الله عليه وسلم. وهذا الأصل فيه تنازع كثير، ولم','2','61',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (758,'[يف] الإمام [بطرده] في [ذلك]، وقد قال هو: إنه إذا نقل الإجماع على ألسنة الآحاد، تُلقي منه العمل، ونُزِّل منزلة أخبار الآحاد الثابتة عن الرسول [صلى الله عليه وسلم]. لأولم ينعقد على تنزيله منزلة أخبار الآحاد إجماع]، إذ العلماء في ذلك مختلفون.
وكذلك يقول القاضي في قياس الشبه: إنه باطل. وقال: لا أخطيء من','2','62',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (759,'قبله، وسلك به مسلك مسائل الإجماع، وإن كان الكلام في نوع من أنواع الأقيسة. وكذلك قال الإمام في كتاب الأخبار: ([إن أخبارًا كانت] تنقل عن الصحابة، فيعمل بها قوم ويردها آخرون. وكانوا ينزلون ذلك منزلة مسائل الظنون). وهذا هو الصحيح عندنا. وإذا ظن الإنسان إجماع الأمة بناء على نقل [العدل] أو غيره (٨٨/أ)، فكيف يتفق له ترك الحكم وهو يظن ثبوته؟ هذا هو الأصل، إلا في مواضع منعت الشريعة من العمل [بها]، وإن كانت غلبة الظن حاصلة، نظرًا للتعبُّد، كما في أبواب الشهادات.
ومن عجيب الأمر أنه إذا ظفر بالفعل منقولًا عن الرسول [صلى الله عليه وسلم]، [استند الفعل إليه قطعًا، لأنه ثبت] عند من سيرة الصحابة أنهم كانوا يعتقدون المشاركة في الفعل. [وإنما] يصير الفعل دليلًا (٦٢/ب) في حقه وفي حق غيره، لكونه مشروعًا [له]، وثبوت اعتقاد المشاركة بيننا وبينه، فإذا صودف دليل الحكم، كيف تنقطع المشاركة التي ثبتت عندما لا يوجد سوى الفعل؟ هذا ظاهر في التناقض.
وقد يعتذر له بعذر ضعيف، فيقال: إذا ثبت الفعل، أمكن أن يكون','2','63',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (760,'الدليل الذي دله على الحكم مسترسلًا، يتناوله وغيره، فاعتقدت المشاركة لإمكان ذلك، وأما الخطاب المختص به، فلا يمكن اعتقاد الاسترسال فيه. وهذا ضعيف، وما استدل القوم بالفعل إلا لاعتقادهم المشاركة في التكليف.
فإذًا الصحيح عندنا أن نتمسك بفعله المطلق، وبالخطاب المختص، إذا انتفت الأدلة الدالة على أنه من خاصيته. وكذلك فهمت الأمة أن الرسول صلى الله عليه وسلم والناس في التكاليف شرع، وقد قال صلى الله عليه وسلم: (صلوا كما رأيتموني أصلي).
وقال (خذوا عني مناسككم). وقال للذي سأل عن القبلة للصائم بأنه يفعله، ولم يقنع المستفتي بذلك وطلب صريح الفتوى، [فقال صلى الله عليه وسلم]: إني لأرجو أن أكون أخشاكم لله [عز وجل] وأعلمكم بما أتقي).
واستدل العلماء على جواز التخيير للزوجات بتخيير رسول صلى الله عليه وسلم نساءه. ولما ذهب بعض الناس [إلى] أن بنفس التخيير تطلق المخيرة، ردت عائشة رضي الله عنها على ذلك القائل، وقالت: (قد خير رسول الله صلى الله عليه وسلم أزواجه','2','64',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (761,'فاخترنه، فلم يعد ذلك طلاقًا). وإن كان التخيير ورد فيه خطاب مختص بالرسول صلى الله عليه وسلم. وهو أيضًا في قاعدة ظهرت [منها] خصائصه، ولم يمنعهم ذلك من اعتقاد المشاركة في الحكم.
فإذًا الصواب ثبوت الاشتراك على الإطلاق في المنقول، وفي الخطاب المختص، إلا أن يدل دليل على أنه خاصية. وبالله التوفيق.

قال الإمام: <span data-type="title" id=toc-138>(مسألة: إذا [خصص] رسول الله صلى الله عليه وسلم واحدًا من أمته بخطاب) </span>إلى آخر المسألة. قال الشيخ: إذا خُص واحد من الأمة.','2','65',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (762,'بخطاب، فالمسألة من جهة اللغة واضحة في الاختصاص. وإن تعدى الحكم عنه إلى غيره، فإنما يكون [ذلك على] تقدير مساواة المحكوم فيه لغيره، ولم يثبت كون المحكوم فيه مختصًا بذلك الحكم، وإن ثبت اختصاصه [به]، فلا سبيل إلى أن يتجاوزه الحكم، كما قال [صلى الله عليه وسلم] لأبي بردة بن نيار: (تجزئك ولا تجزئ عن [أحد] بعدك). وقال في شأن مكة: (إنما أحلت','2','66',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (763,'لي ساعة من نهار، وقد عادت حرمتها ليوم خلق [الله] السموات والأرض).
وكذلك قبول شهادة خزيمة وحده. فإذا ثبتت الخاصية، اقتصر الحكم.','2','67',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (764,'فإن قيل: إذا وجد في الشريعة التسوية بين المخاطب وغيره، ووجد اختصاصه بالحكم دون سواه، فكيف السبيل إلى التعميم مع ثبوت الانقسام؟ قلنا: لما قرر (٨٨/ب) الشرع أن الحكم على الواحد حكم على الجماعة، وقرر التسوية في الأحكام إلا في محل الخصائص، مع كونه صلى الله عليه وسلم كان لا يؤخر البيان عن وقت الحاجة، وهو يعلم أنه لما قرر تلك الأدلة، فالنا (٦٣/أ) يعممون الأحكام، على حسب ما قرره [لهم]، فلو كان الحكم يختص، لنبَّه على اختصاص المخاطب به، [كما قال] في حق أبي بردة [بن نيار]. هذا هو المعروف من الشريعة، لكن لا يثبت تعميم [الحكم] إلا بعد تحقيق المساواة بين المخاطب وغيره. وهذا يفتقر إلى تمام بحث وكشف عما يمكن أن يكون له [أثر] في الحكم.
قال الإمام: (مسألة: إذا ورد خطاب الشارع على سبب مخصوص','2','68',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (765,'وسؤال عن واقعة معينة) إلى قوله ([وهذا كافٍ في [مسلك] الكلام على هذه الآية]). قال الشيخ: ما ذكره الإمام في هذه المسألة مع التمسك بعموم اللفظ دون خصوص السبب واضح، لا إشكال فيه.
وينبغي أن ننبه على أصل ترجع إليه مسائل كثيرة، وهو أنه لما تقرر أن التمسك بالعموم لازم في مسائل الظنون، فلابد [من التمسك به، ولا سبيل]','2','69',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (766,'إلى تعطيله من غير معارض، [إذ] المعارض هو المناقض، بحيث لا يتأتى التمسك بالدليل على وجهه، والمعارض يدافعه.
[وأما] إذا كان الوارد على الدليل لا يعارضه، فكيف يسوغ ترك دلالته من غير (٦٤/ب) [معارض]؟ ويتبين انتفا المعارضة بالتمكن من التمسك بالدليل والاعتراف بصحة ما أورد عليه، فيتبين بذلك [أن لا] معارضة. إذ المتعارضان متضادان، ومن المحال اجتماع المتضادات. ويصح في هذه المسألة أن يعترف بخصوصية السؤال، وتحصيل جوابه، مع تمسكنا بعموم اللفظ فيما وراء ذلك، فلما تصور هذا، بطل التضاد.
وقول القائل: إنه [إنما] قصد جواب السؤال خاصة، غير مسلم. نعم، قصد جواب السؤال من غير إشكال، فأما الاقتصار عليه، فهو محل النزاع، واللفظ ظاهره دال، فلا معنى لترك دلالته بالوهم الصرف. وإن أمكن تخصيصه بالسؤال، أمكن أن تخصيصه بالسؤال، أمكن أن يقصد ابتداء التأسيس. وإذا لم يترجح أحدهما على الآخر، وجب التمسك باللفظ ومقتضاه العموم.','2','70',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (767,'وأما معنى كلام الشافعي على الآية، [فإنه] قال: حرمت الكفار ما أحل الله، وأحلوا ما حرم الله، فكانوا على المضادة والمحاداة، فجات الآية مناقضة لغرضهم، فكأنه قال: لا حلال إلا ما حرمتموه، ولا حرام إلا ما [أحللتموه]، نازلا منزلة قول القائل [لمن] يقول له: [لا] تأكل اليوم حلاوة، فقال: ما أكلت اليوم إلا حلاوة. والغرض المضادة، لا النفي والإثبات على الحقيقة، فكأنه تعالى قال: لا حرام إلا ما أحللتموه من الميتة والدم و [الخنزير]، وما أهل لغير الله به، ولم [يقصد] [حل] ما وراءه، إذ القصد إثبات التحريم لا إثبات الحل.
وأما قوله: (ولولا سبق الشافعي [إلى] ذلك، [لما] كنا','2','71',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (768,'نستجيز مخالفة مالك فيما ذكره الله تعالى). وقوله: (ومسألة الخلاف فيما إذا كان كلام الشارع مستقلا). ترجمة المسألة ترشد إلى ذلك، فإنها','2','72',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (769,'مترجمة: بورود العام على [سبب] خاص. [وأما] إذا كان الجواب لا يفرض له رتبة الاستقلال، لو قدر الابتداء [به]، فليس موضع خلاف، بل إنما يصير خلافا [في المستقبل] بارتباطه بالسؤال، فينتظم منهما جميعا كلام.','2','73',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (770,'وقوله: ([وما] ذكره (٨٩/أ) الشافعي من الكلام على الآية، فهو في غاية الحسن، ولولا [ما عهدناه]، لكانت الآية نصا). هذا عندنا غلط، [وليست] الآية نصا على حال. وتوهمه النص فيها، من جهة ما اشتملت عليه من نفي وإثبات، فاللفظ نص في النفي والإثبات جميعا. وهذا لا نزاع فيه، وإنما الكلام في عموم ما اشتمل النفي عليه في قوله: {لا أجد في ما أوحى إلى محرمًا [على طاعم يطعمه]}. والنكرة في النفي إحدى صيغ العموم. وقد تقدم أن المذهب الصحيح أن الألفاظ ظواهر في الاستغراق على الشروط التي تقدمت. فهي وإن كانت نصا في [أصل] النفي، إلا أنها ظاهرة في عموم النفي.','2','74',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (771,'[وقوله]: (ولم يدع أحد من حملة علوم القرآن النسخ فيها). ليس كما قال، بل للعلماء في هذه الآية مذاهب: [منهم] من يقول: [إنها منسوخة، ومنهم من يقول إنها مخصوصة، ومنهم من يقول: ] إنها مقرة على ما دل عليه ظاهرها، ومنهم من يقول: إنها مقرة، لكن ألحق بهذه [الأشياء] غيرها. وكأن هذا القائل سلك مسلك الخبر، أي: لا محرم ذلك الوقت إلا ما اشتملت عليه الآية، ثم تجددت بعد ذلك أحكام. فدعوى الإجماع على نفي النسخ [باطلة].
وقوله بعد ذلك: (وأنا أقول: مقتضى هذا السياق الذي هو','2','75',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (772,'[متمسك] إمام دار الهجرة مالك يقتضي تحليل الحشرات والقاذورات والعذرات). غفلة عن مقتضى (٦٥/أ) الآية، [ووضع اللغة العربية. أما مقتضى الآية]، فإنها لا تتضمن الحل، وإنما تضمنت نفي التحريم. فلو قال: مقتضى ما ذكره نفي تحريم هذه الأشياء، لأمكن قبوله. أم كونه يقول يقتضي تحليلها، فغلط وما إدخال العذرات في هذا الباب، فهو [خطأٌ] بين من الملزم، فإن أهل اللغة يطلقون الطعم على غير ما ذكره هذا الإنسان، ولما أطلق لفظ (طاعم)، أشعر ذلك بطعام، فليس العذرة مما يطلق عليه طعام بحال.
وأما كونه يدعي على السلف القطع بالتحريم، فيا لله ويا للمسلمين، [أيكون] الإمام أعرف من مالك بمواضع إجماع الصحابة واختلافهم، مع قرب عصره ومحل ولادته وتربيته، ودوام اشتغال بالبحث عن أحوالهم وأقوالهم؟ هذا والله محال. [غير] أنه قد نقل عن القوم خلاف ما ذكره، (سئل ابن عباس عن الحشرات، فأفتى بحلها، وتلا الآية).','2','76',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (773,'ثم إنه اقتصر في نسبة مالك [رحمه الله] إلى خرق الإجماع على التمسك بعادة الصحابة، ولم ينقل في ذلك شيئا أصلا، غير أنه قال: قطع السلف بالتحريم فيه).
ثم قال: ([ورُبَّ] شيء لما استقر عنده من مذهبه ومذهب أصحاب تحريم هذه الأشياء، قضي بأن [الآية] كذلك عند الصحابة. [وهذا] نقل إجماع بطريق القياس [على مذهبه]، وهذا [فيه] نهاية التمويه والإلباس.
وسننبه في هذا المكان على أمر ينبغي أن يعتني بحفظه، وذلك أن الأمور','2','77',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (774,'التي تعلم من أحوال الماضين، فيها ما كان لعادة فيها تصرف، [وقضي بأن] من تقدم كانوا يأكلون ويشربون ويفرحون ويحزنون، ولا ينفك عصرهم من مائل إلى الشهوات، وراكن إلى اللذات والراحات، لا جرم لا يخلو عصر من الأعصار عن ذلك. نعم، قد يتفق أن [يقل] في قوم، ويكثر في آخرين، كل ذلك [نظرًا] منا إلى العوائد، وتلقي الأحكام منها، فهذا [هو] (٨٩/ب) الذي [يُقْضَى] على أهل الأعصار [الخيالية] فيه، تمسكًا بالعادات، والأمن من انخراقها، وكذلك القول في عيافة الطبع المستقذرات، وميل النفوس إلى الثناء، ونفرتها عن الذم. كل ذلك العادة وأهلها [فيه] على التداني.
وأما ما يرجع إلى الأحكام الشرعية، فمن أين يلزم إذا ظن قوم شيئا، وليسوا [من] أهل الإجماع، أن يكون ظن الأولين بجملتهم على ما ظنه البعض في هذه الأعصار؟ ولو صير إلى التمسك بالعوائد، فما صح أن يختلف أهل هذا العصر [فيه، صح أن يختلف فيه أهل العصر] الأول، ولم ينقل عن الأولين تحريم الحشرات بحال.','2','78',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (775,'نقل أبو داود: (أن ابن عمر كرهها، وأبى ذلك البحر، يعني ابن عباس). ثم أطلق لسانه في مالك رحمه الله، ونسبه إلى [استيطاء مركب العقوق]. فيا ليت شعري ما الذي عنه بكونه عاقا؟ هل عقه؟ فهو بحمد الله غير ملتفت إلى أصحاب هذه المقالة [حتى يعقهم]، إذ هو [مجتهد] [يعمل] بما أراه لله تعالى، أم عق صاحب الشريعة؟ وهذا هو الذي أراده صاح الكتاب، لأنه نسبه إلى جحد الأدلة القاطعة. ومعاذ لله أن يكون الأمر كذلك، وما ينبغي أن تطلق الألسنة في الأئمة على هذا الوجه، فإن هذا يقبح (٦٥/ب) من العوام فيما بينهم.
رحمه الله ورضوانه على القاضي أبي بكر حيث يحسن العبارة في الرد على الشافعي، فإنه [لما قال] قال الشافعي: ([مراسيل] [ابن','2','79',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (776,'المسيب] حسنة). قال القاضي: لست أدري ما الذي يحسنها؟ ثم قال: بلغت عن هذا الحبر أنه قال كذا. فهذا طريق مكالمة الأئمة بعضهم بعضا. فأما هذا النوع من الكلام، فلا يليق بأهل العلم على حال.
وقوله: [ونحن [وإن كنا لا نرى تطرق التخصيص إلى اللفظ مقتضيا مصير اللفظ مجملا، كما سنذكره في مسائل الخصوص، فإنا [نرى] أن ذلك- إن [جرى]- يخرج اللفظ عن مراتب [النصوص] في العموم، ويلحقه بقبيل الظواهر، وهذا كافٍ في [مسلك] الكلام على هذه الآية). [فقد','2','80',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (777,'اقتصر] الإمام في هذا المكان على مجرد الدعوى في قوله: إن اللفظ يضعف ظهوره، وينحط عن مرتبة في الظهور. وهذا ما دليله؟ ومن أين يقوله؟ وكيف يصح في علم الأصول الاقتصار على محض الدعوى؟ أيريد أن يقلده الناس في ذلك؟ وعلم الفروع إذا جرى على هذا الوجه كان فيه تقصير، فما الظن بالأصول؟ [ولكن] [تقدير] [الضعف] فيما وراء محل السبب أن اللفظ قبل أن يرد على السبب، فيه جهة مقطوع بها، وأخرى مظنونة.
[وما] انحصر المقطوع به [متعينا] في شي مخصوص، فما من صورة من صور العموم إلا ويمكن أن تكون هي المقطوع [بها]، فتردد بين أن تكون هي التي اللفظ فيها نص أو ظاهر. وإذا تعين محل السبب للنصوصية انحصر الظاهر في البقية، فانحط ما وراء السبب رتبة، من جهة بطلان احتمال القطع [فيه].
وقد قال بعض الناس: سبب ضعفه تطرق الخلاف إليه، فإن من العلماء من يقول: إنه صار مجملا، وإذا قوبل ما وقع الإطباق على نفي إجماله.','2','81',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (778,'[كان] مقدما على ما اختلف في إجماله. وهذا ضعيف، لأنا نقطع بخطأ من صار إلى الإجمال، وكيف يصح بناء التضعيف على قول مقطوع ببطلانه؟
وقال قائلون: جهة الضعف فيه أن اللفظ إذا خص، صار مجازا في الباقي، وإذا لم يخص، فهو حقيقة، وجهة دلالة الحقيقة فوق [جهة] دلالة المجاز. (٩٠/أ) وهذا لعمري حسن، لكن على رأي من يذهب إلى أن إذا خص، صار مجازا.
وأما الإمام فإنه يراه حقيقة في تناوله على ما كان عليه [أولًا] فيما عدا المحل المخصوص. فلا يتصور على مذهبه الترجيح في ذلك [القدر] على حال، فتضعف دلالته على قاعدته، بالإضافة إلى محل [معارضته] ظاهر التناقض من غير إشكال.
[واعلم] أن الناس أطبقوا على أن [لتقدم] السبب على ورود','2','82',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (779,'العموم أثر، ولا التفات إلى ما يحكى عن أبي حنيفة من تجويز استخراج [محل] [السبب] [بطريق] التخصيص. فإن هذا لا يصح على حال. واتفقوا على أنه يفيد النصوصية في السبب، من جهة استحالة تأخير البيان عن وقت الحاجة، ويؤثر أيضًا فيما وراء السبب [أثرا]، وهو إبطال الدلالة على قول، وتضعيف الدلالة على قول. أما وجه التضعيف، فقد ذكرناه.
وأما وجه الإبطال، (٦٦/أ) [فلم يذكر الإمام لقائله مستندا. فقد [تمسكوا] بشبه ننقلها ثم نبين ضعفها.
منها: أنهم قالوا: لو لم يكن للسبب تأثير، وبقي العموم على ما كان عليه، فينبغي أن يجوز إخراج السبب] بطريق التخصيص، كما [لو] لم يكن سبب. وهذا فاسد، لأنا قد بينا أن تقدم السبب له أثر، وإنما النزاع فيه. فهؤلاء [يرون تأثيره] في [التعيين] والإسقاط. ونحن لا نرى الإسقاط. وقد تقدم بيانه.','2','83',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (780,'الشبهة الثانية: أنهم قالوا: لو لم يكن للسبب أثر، لما نقله الراوي، واعتناؤه بنقله يدل على فهمه قصر الحكم عليه. وهذا أيضًا ضعيف، وبيان ضعفه: أنه لو لم ينقل السبب، لصح للناظر إخراجه [بطريق] التخصيص، وذلك غلط، فاعتنى بنقله ليبين موضع القطع من الظهور. على أن الراوي يروي ما اطلع عليه، ولا يلزم أن يكون كل المنقول له أثر [في الحكم]. ألا ترى الراوي روى (أن الأعرابي جاء يلطم نحره، وينتف شعره، ويقول: هلكت وأهلكت، [واقعت] أهلي في نهار رمضان). فنقل ما للحكم به تعلق، وما لا تعلق له به.
الشبهة الثالثة: أنهم قالوا: لو كان المراد بيان الحكم دون اختصاص السبب، لما أخر إلى حين السؤال، فالتأخير يدل على الاختصاص. وهذا باطل، [فما] المانع من كون التأسيس مقصودًا، وذلك وقت ابتداء التكليف؟','2','84',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (781,'الشبهة الرابعة: أنهم قالوا: العموم هو الذي تستوي نسبته إلى المسميات، من غير تفاوت على حال، ولا خلاف أن النسبة في هذه غير مستوية، فإن حد العموم: هو الذي يدل من جهة [واحدة] على [مسميين] فصاعدًا، فلما اختلف [السبب]، بطلت حقيقة العموم. وهذا ضعيف، ونحن نسلم أن حد العموم ما ذكروه، ولكن لسنا نسلم [أن النسبة] [اختلفت]، نظرًا إلى مجرد اللفظ، وإنما جاء القطع من دليل من خارج، زائد على اللفظ، فلا يبطل ذلك حقيقة العموم. وهذا بمثابة ما لو اعتضد بعض صور العموم بمقتضى العقل، ولم يناف الدليل الباقي، فإنا نقطع بما وافقه [دليل العقل]، ونبقى على الظن فيما وراءه.
[قال الإمام]: (وقد حان الآن أن نذكر ما نقل [من] سرف','2','85',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (782,'أبي حنيفة [في] عدم الالتفات إلى السبب) [إلى قوله] (وإن لم يرد في اللعان عن النبي صلى الله عليه وسلم غير [قصة] العجلاني). قال (٦٦/ب) الشيخ: قوله: وإن لم يرد عن [المصطفى صلى الله عليه وسلم] غير [قصة] العجلاني. ليس الأمر فيه على ما قال، وقد رويت [قصة] العجلاني، وقد روي أيضًا حديث هلال بن أمية الواقفي، وهو أحد الثلاثة المخلفين عن غزوة تبوك، الذين','2','86',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (783,'تاب الله عليهم. [وقصته] [مشهورة] عند أهل الحديث.
وقد جاء [عن] النبي صلى الله عليه وسلم أنه قال: (الولد للفراش، وللعاهر الحجر). وهذا واضح جدًا في جانب القياس، أعني (٩٠/ب) مذهب أبي حنيفة؛ فإنه لو تحقق زناه، لم ينتف الولد، فكيف بالدعوى المحلوف على','2','87',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (784,'كذبها؟ ولكن السنة أولى بالإتباع من القياس. فمن سلم أنه لم يطلع على الحديثين [بكمالهما]؟ ولعله اطلع، ورأى التعارض، ورجح القياس. والخبر خبر واحد غير متواتر.
قال الإمام: (والحديث [الآخر] حديث عبد بن زمعة) [إلى قوله] (ولم يلحق ولد المملوكة بمولاها، وإن أقرَّ بالوطء والافتراش).','2','88',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (785,'قال الشيخ [أيده الله]: قوله: إن أبا حنيفة ألحق الولد بالزوج، وإن تيقن استحالة العلوق من مائه. غير صحيح، فإنه قد قال: لو أتت بولد في الحال، أو في [أمد] قريب، لا يكون [أقل] الحمل، [لم] يلحق بالزوج.
نعم، [قضى] في المشرقي يتزوج المغربية، وتأتي بالولد بعد مدة الحمل أن يلحق به، [لإمكان] الوصول بخرق العادة.
وأما كونه [لم] يلحق ولد الأمة بمولاها، فهذا شديد. ولكن معتمده من حيث الجملة، أنها [مدعية] للحرية، فلا [تعتق] بدعواها. وهذا','2','89',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (786,'ضعيف مع النص على لحوق الولد بالواطئ، وليس دعواها الاستيلاد ينبغي أن [تعارض] هذا الخبر، ولكن نسبته [إلى] أنه لم يحط بالحديث، غير مستقيم، مع أنه لم يرد الخبر [إلا] متصلا بزيادة.
قال الإمام: ([والذي] عندي أنه لا يجوز أن ينسب إلى متعاقل [تجويز استخراج [السبب] تخصيصا) إلى قوله (فهذا ما أردنا ذكره في القرائن الحالية. [وأما] القرائن [المقالية] التي ليست [أحوالا]، فهي','2','90',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (787,'تنقسم إلى الاستثناء والتخصيص، ونحن نبدأ بالاستثناء]، [مستعينين بالله، وهو خير معين]).

قال الإمام: <span data-type="title" id=toc-140>(مسائل الاستثناء: </span>الاستثناء: استفعال [من الثني، يقال: [ثنيت] الشيء [أثنيه]، إذا طويته]) إلى قوله ([فقالوا: ] جاء القوم إلا زيدًا). قال الشيخ: [رضي الله عنه]: الاستثناء عند أئمة العربية: إخراج بعض من كل (بإلا)، أو بكلمة تقوم مقام (إلا). [وهل] يشترط في البعض [المخرج] أن يكون أقل من المبقى؟ اشترط ذلك كثير من النحويين، وقالوا: إن الاستثناء في معنى الاستدراك، وجوز بعض البصريين إخراج','2','91',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (788,'النصف، فأما استثناء أكثر من النصف، فلم يجزه أحد من البصريين. وسيأتي الكلام على ذلك بعد هذا.
وقول الإمام هو: (مأخوذ من الثني). راجع إلى هذا، فكأنك [قلت]: ثنيت الكلام عن بعض ما كان يقتضيه إطلاقه لولا الثني (بإلا). ولكن هذا يوجب أن يكون الاستثناء من غير الجنس مجازًا. وسنتكلم عليه بعد هذا، إن شاء الله تعالى.
وقوله: (وله أدوات في اللسان). الأمر على ما [ذكر]. والاستثناء قد يكون بالحروف، وقد يكون بالأفعال، وقد يكون بالأسماء. فالحروف: ك (إلا) [و) حاشا). والأسماء: ] (٦٧/أ) ك (سيما) و (سوى) و (سوى).','2','92',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (789,'والأفعال ك (ليس)، [و) لا يكون)]. وأم الباب (إلا)، لأنها لا تنفك عن الاستثناء بحال.
[وقوله: (ثم لا يخلو الاستثناء: إما أن يتصل بكرم موجب أو منفي). القسمة صحيحة [في الغالب]. يظهر هذا في الخبر، فأما الأمر والنهي والاستفهام، فيرجع [إليه] بطريق التأويل. (فإن كان في [إيجاب]، فكم اللسان فيه اقتضاء النصب). [الأمر] على ما قال، كقولك: قام القوم إلا زيدًا.','2','93',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (790,'وقوله: (فهو حرف). يعني (إلا)، [دال] على فعل ناصب، والتقدير عند أهل الصناعة: أستثني [زيدًا]، هذا لم [أره] لأحد من [أئمة] العربية على هذا التقدير. [وللنحويين] في عامل النصب أربعة أقوال:
أحدها: مذهب الأكثرين من المتقدمين [والمتأخرين] من البصريين (٩١/أ) أن العامل الفعل بواسطة الحرف، لأن الفعل القاصر قد يعدى بالحروف، تقول: قام زيد، فلا [يتعدى]، ثم تقول: أقام زيد عمرًا. ونظير هذا الباب المفعول معه، في قولك: استوى الماء والخشبة، [فتنصب] الخشبة [بقولك]: استوى، بواسطة الحرف، فكذلك ينصب زيد ب (قام) بواسطة (إلا)، فإن لم يكن الفعل، [وكان] [في معناه] (فعل)، [وذلك] [كقولك]: في الدار القوم إلا زيدًا، ففي هذا الكلام رائحة الفعل.','2','94',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (791,'وذهب أبو العباس المبرد إلى أن الحرف هو العامل، لشبهه بالفعل، [لأنه دال على الفعل]، كما علمت الحروف [الشبيهة] [) بكان) و (إن)] و) لكن) و) كأن) و) ليت) و) لعل)، فهو يرى أن الحرف ناصب، ولم يقل إنه يدل على ناصب. وهذا الذي قاله ضعيف، فإنه لو عملت الحروف يتضمنها معاني الأفعال، لم يبق حرف إلا عاملًا، [كحروف النفي والاستفهام].
وأيضًا فإنه لو كان الحرف عاملًا، لجاز (إلا زيدًا قام القوم)، [كما يجوز (قام القوم إلا زيدا)]، ولو كان [النصب أيضًا]- لما في [) إلا) من] معنى الاستثناء- لاستوى الموجب والمنفي في النصب.
وذهب الفراء إلى أن [) إلا)] [مركبة] من (إن) الناصبة و (لا)','2','95',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (792,'النافية، فإن نصبت، اعتمدت على [) إن)]، [وإن رفعت، اعتمدت على (لا)]. وهذا أيضًا ضعيف، وهو تحكم من غير دليل، [مع] أن المعنى لا يساعد عليه، فإن هذا التضعيف لو فك، لبطل الفهم [من] قولك: قام القوم إلا زيدا.
وذهب الكسائي إلى أن الاسم ينتصب (بأن) مضمرة. والتقدير عنده: (قام القوم إلا أن زيدا لم يقم. وهذا رديء، فإنه أضمر الحرف وأبقى عمله، والحروف ضعيفة لا تعمل مضمرة، إلا على تقدير دلالة، أو ثرة استعمال، كما قيل في بعض الأوقات في الاسم [المخفوض] بعد [كم]. وفيه أيضًا وجه بعيد، وهو إثبات (إن) دون خبرها. فإذًا الصحيح هو المذهب الأول.
وما ذكره الإمام خارج عن الجميع، وإنما فرق بين الإثبات والنفي،','2','96',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (793,'[من حيث] أن النفي يمكن فيه البدل، [والإثبات] يتعذر ذلك فيه، فإنك في البدل تقدر طرح الأول، إذ [العمدة] على الآخر، وذلك يصح في قولك: ما قام [القوم] إلا زيدا، تريد: ما قام إلا زيد، فيلتئم الكلام. ولا يصح في قولك: [قام] القوم إلا [زيدا]، [أن تقدر] حذف القوم، [وتقول]: قام [إلا زيدا]، فلما لم يجز ذلك، لم يكن إلى الرفع على طريق البدل في الإيجاب سبيل.
وقول الإمام: (وقد يأتي [ما بعد] (إلا) في الإيجاب مرفوعا).','2','97',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (794,'كلام يوهم أن الباب بحاله في الاستثناء، (٦٧/ب) [وأن الرفع والنصب جائز فيه]، وليس الأمر كذلك، لا من جهة الإعراب، ولا من جهة المعنى. أما من جهة المعنى، فإنك إذا قلت: قام القوم إلا زيدا، [فهذا] يتضمن الإخبار أن زيدا لم يقم، وإذا قلت: قام القوم إلا زيدا، فأنت في هذه الصورة لم تتعرض لزيد بإخبار من عدم قيامه، بل أنت ساكت عنه، وكأنك قلت: قام القوم المغايرون لزيد، ولهذا قال الفقهاء: إذا قال المقر له: عندي درهم إلا قيراطا، [يلزمه] [بعض الدرهم، وهو الدرهم الذي ينقص قيراطا، وإذا قال: إلا قيراطا، لزمه الدرهم بكماله]، فيخرج الباب عن الاستثناء إلى الصفة، على تقدير الرفع في الإيجاب. [وعليه نزل] قول الشاعر، وهو [عمرو بن','2','98',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (795,'معدي كرب] في البيت الذي سبق، فكأنه لم يخبر عن الفرقدين بافتراق [ولا غيره]، ولكن أخرجهما من خبره. وعليه نزل [قوله تعالى]: {لو كان فيهما إلهه إلا الله لفسدتا}.
وإنما قال الإمام (٩١/ب): إن [) إلا)] محمولة على (غير)، لاشتراكهما في أصل المعنى، [ولكنهما] أيضًا مفترقان من وجه. فنذكر ما لكل واحدة بالإضافة، وموضع الاشتراك، [وخاصية] [الافتراق].
أما الذي لـ (إلا) بالإضافة، فالاستثناء، والذي (لغير) بالإضافة، الصفة، وإجراؤها على إعراب الموصوف، وحكم الاستثناء في غير موضع البدل','2','99',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (796,'النصب، فإذا جرت (غير) على إعراب ما قبلها، فهي على أصالتها، وإذا جرت (إلا) على النصب، فهو بابها.
ولكنهما متقاربان في وجه، وهو أن الكلام بعد ورودهما جميعا ينقص عما كان يقتضيه إطلاقه لولاهما، فإنه كان خبرا عن الجميع، إلا أن ورود [إحداهما] وهي (غير) تقتضي القصر على البعض، معرضة [عن] الدلالة على ما بعدها. [و) إلا)] تدل على ما قبلها وما بعدها، فلما حصل الاشتراك من هذه الجهة، حسن حمل إحداهما على الأخرى، فيما يتعلق بالعمل، إلا أن (إلا) لما كانت حرفا، لم يتصور أن تعمل المضارعة فيها، فانتقل الأمر إلى ما بعدها، (وغير) اسم يقبل الإعراب، فانتقل إعراب ما بعد (إلا) إلى (غير) فقيل: قام القوم غير زيد، وقيل: قام القوم إلا زيدا. وهذا كلام حسن بالغ في فن العربية، وحسن تصرف أهل اللغة.
قال الإمام: ([فإذا] [اتصل] الكلام [بكلام] مبني على النفي، فلا يخلو: إما [أن يتم] الكلام دونه، [وإما أن] لا يتم الكلام دونه) إلى','2','100',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (797,'قوله (وكل ما ذكرناه [من] الاستثناء من الجنس). قال الشيخ: [إذا جرى الاستثناء في جانب النفي، ولم يتم الكلام، لم تعمل (إلا) شيئا، وتسمى [المفرغة]، وإنما كان كذلك، لأن الفعل يطلب فاعله، وهو إليه','2','101',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (798,'أحوج من المفعول، فإذا لم يستوف الفعل فاعله، وسبق حرف النفي أُتي (بإلا) لنفي النفي، ويصير الكلام في المعنى موجبا، كأنك قلت: قام زيد، ورأيت زيدا، ومررت بزيد، أما إذا استوفى الفعل فاعله، وجيء بالمستثنى، أمكن البدل، وهو الأصل، لأن المراد إثبات القيان لزيد.
والقائل: لا إله إلا الله، الغرض الأصلي إثبات الإلهية لله تعالى، ونفي ما سواه، لتأكيد الإثبات، فعلى هذا يقوى جانب البدل. ويجوز الاستثناء تعرضًا للأمرين جميعا مقصودًا، والأول أكثر، وإنما جاز الأمران، لصحة معنى','2','102',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (799,'الاستثناء والبدل جميعا. وإذا دار الأمر بين شيئين، لا أكثر منهما، فعندما يدل دليل على سقوط أحدهما، يتعين الآخر.
وإذا بطل البدل، تعين الاستثناء، ويبطل البدل على تقدير تأخير المبدل منه، وتقدم البدل، فإن التوابع الأربعة لا يجوز أن يتقدم شي منها على متبوعه، كالتوكيد والنعت والعطف والبدل، وإنما كان كذلك، من جهة أنها أتباع ينصبُّ عمل العامل عليها انصبابة واحدة، فلذلك يمتنع أن تقول: [قام] وعمرو زيد، لأنك عطفت قبل ذكر المعطوف عليه، وكما امتنع ذلك في العطف، فيمتنع في الصفة، فلا يجوز أن يقال: ([قام] العاقل زيد) على إرادة النعت، وكذلك لا يصح أن يقال: [قام] كلهم الناس.

قال الإمام: ([وأما]<span data-type="title" id=toc-141> الاستثناء من غير الجنس</span>-[والكلام] مبني على النفي حيث انتهى الترتيب إليه) إلى آخر المسألة. قال الشيخ]: الاستثناء','2','103',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (800,'من غير الجنس ليس فيه (٩٢/أ) عند أهل اللغة إلا النصب، إذا لم يكن للمستثنى تعلق بالمستثنى منه بوجه، وإن كان له تعلق به، كالتوابع للمذكور والسابق، فهذا موضع خلاف أهل اللغة.
) فالحجازيون) التفتوا إلى [الظاهر] من عدم المجانسة (٦٨/أ)، [وأجروه] [مجرى] ما تحقق [فيه] [التقاطع] من كل وجه، فلم يروا البدل. (وبنو تميم) جوزوا البدل، ولمذهبهم ثلاثة أوجه:
أحدها- أن يكون غلب من يعقل، [فكأنه] قال: ما جاني شيء، وذكر الأحد تغليبا، فيكون استثني من المقدر في النطق، فلا يكون منقطعا بالكلية.
والثاني- أن يكون أجرى ذكر الأحد توكيدا، والمراد: ما جاءني إلا','2','104',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (801,'حمار، ولو [قال] كذلك، لفهم أن الأحدين لم يأتوه.
الثالث- أن يكون قال: ما جاءني [أحد]، ولا يتبع الأحدين من [إبلهم] ودوابهم. واستغنى عن ذكر التابع لفظا، للعلم بأن مجيء المتبوع يتضمن مجيء التابع.
وأما قول الإمام: (أقبل أحد الحمارين). فليس بظاهر، وإذا أطلق لفظ الأحد على من يعقل وما لا يعقل، خرج عن باب [الاستثناء من غير الجنس]. هذا ما يحتمله الكلام، وفيه في علم العربية كلام [طويل].
قال الإمام: (مسألة: صيغة الاستثناء إذا [انقطعت]، لم تعمل','2','105',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (802,'وألغيت) إلى آخر المسألة. قال الشيخ: ما ذكره الإمام في وجوب اتصال الاستثناء، هو الصحيح، والفصل به يفضي إلى ما ذكره الإمام من عدم الوثوق بالنصوص، وفقد الصدق [بالوعد والوعيد]، [إلى] غير ذلك من','2','106',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (803,'ضروب الفساد، وثبوت الاختلال بوضع اللغة، وامتناع الفهم من الألفاظ.
وأيضًا فإنه إذا فصل ما بعد (إلا) مما قبلها (بإلا)، [خرج] عن أن يكون كلاما مفهوما، فضلا عن أن يكون مخرجا شيئًا فهم. وكذلك الكلام في كل [جزء] لا يستقل بنفسه، كالشرط وخبر المبتدأ وغيره، وهذا أظهر من أن يفتقر إلى [تقريره]. وكلام ابن عباس غامض شديد، والله تعالى أعلم بصحته ومراده.','2','107',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (804,'وأما ما ذهب إليه بعض الفقهاء [من] تجويز ذلك في [كلام] الله [تعالى] دون غيره، فخطأ بين، وقول باطل، وما ذهبوا إليه من اتحاد الكلام القديم، [أمر] [لا] يفيد، ولا يغني، الأمر [على] ما ذكره الإمام، من الالتفات إلى [لغة] العرب، وهي متعددة [مرتبة]، لا خلاف في ذلك. وقد كان القرآن ينزل [شيئا] بعد شيء، فما الذي يفيد النظر إلى قدم الكلام واتحاده؟ هذا مقصود الأصول في الرد على جوز فصل الاستثناء في كلام الله تعالى دون غيره.','2','108',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (805,'وأما ما تكلم به الإمام من الإشكال المتوجه في اتحاد الكلام، باعتبار ثبوت الاستثناء، واشتمال الكلام على النفي والإثبات، فكيف تتحقق مع ذلك الوحدة؟ وهذا الذي قاله [ظاهره يشير] إلى تعدد الكلام، وتعدد الكلام باطل، بلا إشكال. والدليل على ذلك، هو الدليل على اتحاد العلم، وقد سبق وجه ذلك في مقدمة [الكتاب]. والذي نعيده [الآن] أن الكلام ليس لمتعلقه مصحح، لصحة تعلقه بالواجب والجائز والمستحيل، فلو تعدد الكلام بتعدد متعلقاته، لوجب أن يثبت للباري [سبحانه] كلمات لا تتناهى، ودخول مالا يتناهى الوجود محال، (٦٨/ب) والقصر على عدد باطل، للتحكم في ذلك.','2','109',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (806,'وقد ذهب القاضي في بعض مقالاته إلى أن اتحاد الصفات [لا يتلقى] من العقل، (٩٢/ب) وإنما هو متلقى من الإجماع. وهذا فيه نظر. وقد وجه عليه أنه، كيف يدعي الإجماع على [نفي] تعدد العلم، ومن الأمة من لا يثبت أصل العلم للقديم؟ فكيف يدعي الإجماع على اتحاده؟ [أجاب] القاضي عن هذا بأن قال: أجمعت على نفي علم ثان قديم، فلا ذاهب من الأمة [إلى] إثباته، وأصل العلم دلت الأدلة العقلية على ثبوته. [هذه طريقته]، والذي قاله صحيح، وفي أدلة العقل غنية وبلاغ.','2','110',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (807,'قال الإمام: <span data-type="title" id=toc-143>(مسألة: إذا اشتمل الكلام على جمل، واستعقب الجملة الأخيرة استثناء) [</span>إلى قوله] (وفصل الجمل إجمالا ووقفا). قال الشيخ: اختلف الأصوليون في هذه المسألة على ثلاثة مذاهب، وأثبت الإمام تفصيلا، فتصير المذاهب على هذا أربعة: ما في الكتاب، ومذهب ثالث،','2','111',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (808,'وهو مذهب الواقفية.
أما أصحاب الشمول، فقد ذهبوا إلى ما ذكره، محتجين بما قرره [لهم]، وبأمرين آخرين:
أحدهما- أنهم قالوا: الاستثناء كالشرط والصفة، والشرط اللاحق، والصفة أيضًا اللاحقة ترجع إلى الجميع، فينبغي أن يكون الاستثناء كذلك.','2','112',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (809,'والجواب عنه: أن هذا قياس في اللغة، واللغة تثبت نقلا لا قياسا. كما سبق.
الثاني- أن [لا نسلم] رجوع الصفة والشرط إلى الجميع، بل [يختص] عند المخصص، أو [يتوقف] فيه عند الواقف. نعم، سلم ذلك بعض أصحاب أبي حنيفة، وهو صعب على القوم، [ولكن] يمكن أن يكون مستندهم من حيث الجملة أن الشرط له صدر الكلام وإن تأخر. ولهذا يقدر','2','113',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (810,'في [قولك]: أكرمك إن جاء زيد، معناه: إن جاء زيد أكرمتك. هذا هو التقدير عند أهل اللسان. [فإن] كان كذلك، فالشرط متقدم في الحقيقة، والاستثناء [مؤخر]. وهذا الكلام [فيه ضعف]، فإنا لا ننازع في أن الشرط يجب تقديمه، [و] لكن على أي جملة؟ هل على الجمل كلها، أو على الأخيرة منها؟ فلا يرجع الأمر إلا إلى الدعوى.
وقالوا أيضًا: [تكرير] الاستثناء عقيب كل جملة قبيح، [وعيُّ]','2','114',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (811,'[من] القول، [لا] سبيل إلى النطق به في الكلام الفصيح. وهذا فاسد لا خفاء بفساده.
وقولهم: إنه قبيح، لا يسلم الواقف [قبحه]، بل يقول: [لا تحصل] [العبارة] إلا به. ثم [لو قدر] أنه قبيح، فمن أين يتحقق أن المتكلم قصد الجميع، [مع] صحة صرف الاستثناء إلى الأخير، وقصره عليه؟','2','115',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (812,'وأما المخصصة فقد تمسكوا باستقلال الجمل، وأن الآخذ في الجملة الثانية معرض [عن] الأولى، [فلا يرجع الاستثناء إليها، وهذا باطل من وجهين:
أحدهما- أنه لا نسلم انه معرض عن الأولى]، إلا بتقدير الاقتصار عليها، [وأما] إذا كان في [مساق] كلامه، لم يسكت عليه، ولا اقتصر، فمن أين يتحقق إضرابه [عن الأولى]؟ ويحقق هذا أيضًا اعترافهم برجوع الشرط والصفة إلى الجميع.','2','116',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (813,'وأما الواقفية فقد قالوا: (٦٩/أ) إذا بطل كلام المعممين، ووضح تحكم المخصصين، لم يبق سوى الوقف. ويسلكون في هذه المسألة مسلكهم في بطلان كون الأمر له صيغة، وهل للعموم صيغة؟ والكل على [حدِّ] واحد، فيما يتعلق بالقياس ووضع اللغات.
ولكن سلك القوم [مسلكا] عظيما في هذه المسألة. وذلك أن القاضي وأصحابه يقولون: إنه يصح أن ينفصل الاستثناء عن الجملة الأخيرة، فتبقى الجملة على (٩٣/أ) إطلاقها، ويرجع الاستثناء على ما قبلها. وهذا بعيد عن وضع اللغة بالكلية، [فهلا] جعلت الجمل كلها كجملة واحدة، لأجل','2','117',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (814,') الواو) المشتركة، ولا حكم [لكل جملة] [بالاستقلال] والتقاطع؟ فهذا مذهب غريب جدا.
وأما [مذهب] الإمام، فلم يبن على وضع اللغة، وإنما [بني كرمه] على قضية فقهية في تباين المقاصد [ومقاربتها]. [والظاهر] [عندنا] أيضًا خلاف ما ذهب إليه، والجمل [بالتشريك] [بالواو] في معنى الجملة الواحدة. هذا هو الظاهر في العطف، إلا أن تكون (الواو) للابتداء فتنفصل الجملة [الثانية] عن الأولى، وقد قال الله تعالى: {لنبين لكم ونقر','2','118',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (815,'في الأرحام ما نشاء}. وقال: {فإن يشا الله يختم على قلبك ويمح الله الباطل ويحق الحق}. [وإذا} ترددنا في العطف [والاستئناف]، حكمنا بأن الاستثناء ينعطف على الجملة الأخيرة، لاتصاله بها. وتوقفنا [عن] انعطافه على ما سبق. هذا هو الذي يصح عندنا. والله الموفق للصواب.
وقد [استدل] القاضي في صحة صرفه عن الأخيرة إلى غيرها بقوله تعالى: {ولو ردوه إلى الرسول وإلى أولى الأمر منهم لعلمه الذين يستنبطونه منهم ولولا فضل الله عليكم ورحمته لأتبعتم الشيطان إلا قليلًا}. قال: لا يصح أن يرجع هذا الاستثناء إلى الجملة الأخيرة، فإن من لا يصبه فضل الله، لابد أن يتبع الشيطان، فلزم صرفه إلى ما تقدم من الجمل، وهي قوله: {لعلمه الذين يستنبطونه منهم} إلا قليلًا ممن قصر في النظر، ولم يستوعب الفكر، ولم يكمل','2','119',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (816,'[السبر]، فيظن أنه من أهل الاستنباط، وليس منهم. وهذا الذي قاله ممكن، ويمكن خلافه.
والتقدير عندنا: ولولا فضل الله عليكم ببعثة محمد صلى الله عليه وسلم لاتبعتم الشيطان، إلا قليلًا ممن من الله عليه بالإسلام والتوحيد، كقس بن ساعدة، وأويس القرني. هذا [عندنا أولى] مما ذكره القاضي.
ومحط المسألة اللغة. والذي ذكرناه نقطع به، ولكنه يظهر من كلام أئمة العربية في التشريك بالواو.
قال الإمام: ([وما آية القذف]، فإنها خارجة عن القسمين جميعًا،','2','120',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (817,'[على] ما [سنوضحه] الآن) [إلى قوله] (مع استمساكنا بالحق [اليقين] في مأخذ الأصول). (٦٩/ب) قال الشيخ: ما ذكره الإمام في معنى [الآية] أمر عجيب، وتفسير غريب، وكون (الواو) من حروف التعليل، مما لم يقله أحد من أئمة العربية.
وللتعليل حروف صراح وظواهر [وإيماءات]. فصرائحه معروفة، [كاللام] و) كي) [و) لأجل)] و (من أجل) و) بأن ذلك). [وللإماءة] [صور] كثيرة، [و) الفاء) من جملتها]، كقوله: (سها فسجد)،','2','121',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (818,'(وزنى ماعز فرجمه رسول الله صلى الله عليه وسلم). [ومنها] ترتيب الحكم على الأسماء المشتقة، إما مطلقًا، وإما بشرط المناسبة، إلى غير ذلك من [كلمات الإيماء] [والتنبيه].
وأما عد [) الواو)] من هذا الباب، فلم يصر إليه [فيما] عرفناه من الأصوليين [والنحويين أحد]. وقد عد الناس محامل (الواو)، وهي إما أن تكون عاطفة أو للابتداء. [وكونه] يقول: ([فصار] كأنه قال: لأنهم','2','122',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (819,'فاسقون). هذا [إبداء] إمكان من غير استدلال. بل نقول: الجمل [مستقلة] بأنفسها.
وقوله: ([فإن] الشهادة في [هذه] المحال [بالفسق] (٩٣/ب) ترد). لا يسلم له ذلك، بل يصح أن يكون رد الشهادة زيادة عقوبة في حقه، حتى يعذب بالعذاب الحسَّى، وهو الحد، والعذاب المعنوي، وهو سلب منصب الشهادة، ويكون فاسقًا [بذلك]، وأما الحد، فلا ينعطف الاستثناء عليه، لأنه تعلق به حق المقذوف من أن تظهر راءته بحده له، فيصير من حقوق الآدميين التي لا تؤثر التوبة فيها. ولا خلاف بين الناس أن حقوق الآدميين لا تسقطها التوبة، بل لابد من التنصل من مظالم العباد، وذلك من جملة التوبة.','2','123',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (820,'وهل تسقط التوبة [الحدود] أم لا؟ مع اتفاقهم [على] أنها تسقط الآثام المختصة بالله [سبحانه]. ذهب مالك [رضي الله عنه] إلى أن التوبة لا تسقط الحدود، وهو أحد قولي الشافعي، والقول الآخر للشافعي أنها مسقطة. وهم متفقون على أن التوبة تسقط [حد] الحرابة، بقول الله تعالى: {إلا الذين تابوا من قبل أن تقدروا عليهم فأعلموا أن الله غفور رحيم}. والشافعي جعل هذا أصلا، وقاس عليه في أحد قوليه. ومالك تمسك بالخبر الصحيح: [رجم] رسول الله صلى الله عليه وسلم الغامدية، وقال في حقها: (لقد تابت توبة (٧٠/أ)','2','124',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (821,'لو تابها أهل بلدة لوسعتهم). وفي حديث آخر: (وهل وجدت أفضل [من] أن جادت بنفسها). وهذا تصريح بكمال التوبة مع إقامة الحد. وكذلك سائر الحدود، إلا حد الحرابة. وسبب خروجه عن قاعدة الحدود: شدة الشوكة، وعظيم المفسدة، بما تعاطوه من المغالبة، فجعل ذلك لاستنزالهم عن شوكتهم، كما فعل ذلك في الحربي. [فثبت بهذا] أن التوبة لا تتعدى إلى الحد بالوجهين المذكورين.
[وأما رد] الشهادة، فليس من أبواب الحدود، وإنما هو [زيادة سلب منصب، لتتحقق الصيانة عن الإقدام على القذف]. فصح أن تؤثر التوبة فيه.
قال الإمام: (فإن قيل: إذا حبس على فرق وطوائف، [وعقب الجملة] الأخيرة استثناء، فبم تفتون؟ ) إلى قوله (على جميع المذكورين).','2','125',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (822,'قال الشيخ: ما ذكره الإمام واضح، [وقد] يقول [قائل]: إن الأولين [قد] ثبت في حقهم الأخذ بالقول الأول، وإنما نحن على تردد في انعطاف الاستثناء المخرج، ولا يمنع بالشك والاحتمال. وللقائل أن يقول: إنما [يثبت] استحقاق الأولين على تقدير السكوت، فأما إذا لم يتفق السكوت عليهم، فنحن [نتردد] في الاستحقاق. هذا هو الظاهر في ذلك.

قال الإمام [رحمه الله]: <span data-type="title" id=toc-144>(مسألة: إذا استغرق الاستثناء الجميع كان باطلا [ولغوا] إلى آخر المسألة</span>. قال الشيخ: قد قدمت أن مذهب','2','126',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (823,'القاضي في الاستثناء هو مذهب البصريين من النحويين، وهم [ذوو] التحقيق في فن العربية، وإليهم المرجوع في فهم اللغة، لاسيما الخليل بن أحمد، والنضر بن شميل وسيبويه، وهم متفقون على ذلك. ولكن لم أظفر لهم بدليل واضح يدل على هذا الحكم، إلا أن يكون النقل عندهم [ثابتا]، فهذا إذا كان كذلك، فمسلم، وإن لم يثبت النقل، فلم يبق إلا الاستقراء، والاستقراء ثابت في اللغة أنه لن يستثن النصف، بل إنما يأتي الاستثناء على طريق الاستدراك [للقليل]، بالإضافة إلى الكثير. (٩٤/أ).
هذا هو المنقول، وهو الثابت في الكتاب، قال الله تعالى: {فلبث فيهم ألف سنة إلا خمسين عامًا}. وقال: {فسجد الملائكة كلهم أجمعون (٧٣) إلا','2','127',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (824,'إبليس}. وقال: {يوم لا ينفع مال ولا بنون (٨٨) إلا من أتى الله بقلب سليم}.
وهذا عدد قليل بالإضافة إلى غيره، فيجب الاقتصار على المسموع، ونمنع الزيادة، بناء على القياس، لامتناع القياس في اللغة.
وقد صار إلى مذهب القاضي من الفقهاء عبد الملك بن الماجشون من أصحاب مالك، وأكثر الفقهاء على خلاف ذلك، [ويجوزون] الاستثناء مطلقا. أما من يراعي المقاصد، فكرمه [واضح]، وأما من يراعي ظواهر اللغة، ويقتصر عليها، فيضعف كلامه [بما] قررناه. والظاهر عندي من جهة اللغة الوقوف على المسموع. والله المستعان.','2','128',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (825,'قال الإمام: (مسألة: ذكر الفقهاء اختلاف الشافعي وأبي حنيفة) [إلى قوله (يضاهي ذلك]). قال الشيخ: [الاستثناء من غير الجنس يسمى الاستثناء المنقطع، وهو ثابت في اللغة والقرآن العزيز، قال الله تعالى: {فإنهم عدو لي إلا رب العالمين}. وقال: {فسجد الملائكة كلهم أجمعون (٧٣) إلا إبليس}. وقال: {لا عاصم اليوم من أمر الله إلا من رحم}. والمرحوم ليس بعاصم، ولكنه معصوم.','2','129',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (826,'واختلف الناس بعد ذلك، هل الاستثناء [حقيقة] أم لا؟ واختلف فيه قول القاضي، والظاهر من كلام أهل العربية أنه استثناء حقيقة، بل لا وجه فيه إلا الاستثناء. وقد تقدم أن جانب النظر باطل فيه. وأما حقيقة الشيء، فيجوز أن يكون المراد به ههنا انعطاف الكلام على خلاف جهته، من نفي إثبات، أو من إثبات إلى نفي، فيسمى ذلك استثناء، وإن لم يكن فيه حقيقة الإخراج.
فإن قيل: فينبغي إذا نطق الإنسان بجملة إثباتية أو سلبية، ثم نطق بضها، أن يكون مستثنيا، وليس الأمر كذلك. قلنا: قد بينا أن الاستثناء يعد جزءا من الكلام الأول، ولا يقدر جملة مستقلة بحال، فإذا جيء به على الجملة [الثانية]، لم يكن استثناء، إذ خرج عن كونه جزءا. هذا هو الظاهر.
وأما إذا قال: عندي ألف درهم إلا ثوبا، فالظاهر ما قاله أبو حنيفة، إذ الاستثناء من غير الجنس غير [منقض] للكلام الأول، فبقي على ما كان عليه. وإذا قال الشافعي: يقبل ذلك، وينقص من المقر به مقدار القيمة، فهذا عدول عن الظاهر بالكلية.','2','130',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (827,'وقول الإمام: (فإن صح في [مسائل] الظنون التعبير بالثوب عن قيمته، فالقيمة هي المستثناة إذًا، إذ هي المجانسة). فكلام] غير صحيح، ولا سبيل أبدا إلى أن يجعل الثوب [عبارة] عن القيمة.
وإنما يكون هذا من باب حذف المضاف، وإقامة المضاف إليه مقامه، كما [قررناه] في أبواب المجاز، وهي كقوله تعالى: {وسئل القرية}. فليس نقول: إن القرية عبارة عن الأهل، ولكن المراد: اسأل أهل القرية.
هذا النوع من المجاز يفتقر إلى استعمال، فإنا قد قررنا أن هذا مقصور على السماع، فيقال: سل الطلل، والربع، والمنازل، والدار، ولا يقال: سل','2','131',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (828,'[البساط] والكوز، ويراد به صاحبه، فإذ تقرر ذلك، امتنع هذا النوع من التجوز، [لعدم] سماعه. [ولكن] [أبا حنيفة] نقض [قوله] [بتجويز] استثناء (٩٤/ب) المكيل من الموزون، والموزون من المكيل. وهذا لعمري نقض بين، والتعلق بالمناقضات غير مفيد، فيما يتعلق بالحقائق، إلا على جهات تقدم ذكرها، وسيأتي لها مزيد تحقيق في باب النقض.','2','132',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (829,'وأما مالك رحمه الله، فقد سلك بالمعاوضات في الاستثناء من غر الجنس مسلكا آخر، فقال: إذا قال الرجل: بعتك هذه السلعة بدينار إلا قفيز حنطة، كان القفيز مبيعا من السلعة. وهذا عجيب، فلم يلغ الاستثناء [بخروجه] عن الجنس، كما قال أبو حنيفة، ولم يجعله منقصًا، ردًا إلى التأويل الذي ذكره الشافعي، ولكنه في التحقيق راجع إلى قول الشافعي، [ولكنه] زاد زيادة من وجه آخر.
[وبيان] رجوعه إلى طريق الشافعي: أنه يقول: لما قال: (بدينار إلا قفيز حنطة) وأورد لفظة الاستثناء على الدينار، اقتضى ذلك تنقيصًا من جهة مقصد المتكلم، [فإنه] لم يجعل الدينار بكماله ثمنًا للسلعة، وأبقى منه مقدرًا يقابل القفيز، [فكأنه] قال: ذلك البعض من الدينار يجعل عوضًا للقفيز. فمن هذه الجهة يكونان مبيعين. وإنما فعل ذلك، لأنه [لو] استثنى','2','133',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (830,'من الدينار قيمة القفيز للإسقاط، للزم أن يكون (٧١/أ) البيع فاسدا في السلعة. [فإذا] جعلت ذلك ثمنًا للقفيز، لم يضر الجهل بما ينوب كل واحد من القفيز والسلعة، لما كان المالك واحدًا.
[فهذا] [وجه] هذا المذهب، وهو لعمري يمشي على أصل مالك، في أنه لا يراعي مناسبة الألفاظ، من جهة اللغة في صحة العقود، إذا فهم المقصود. وإنما يعسر الأمر على الشافعي، الذي يشترط مناسبة الألفاظ. هذه نهاية الكلام على الاستثناء وفصوله.

قال الإمام: <span data-type="title" id=toc-145>(فصل- قد ذكرنا ما يتعلق بالأصول من [حكم] الاستثناء) </span>إلى قوله (وأمثلة ذلك تكثر). قال الشيخ: أما التخصيص فهو:','2','134',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (831,'الإفراد في اللغة، من غير قصر على الذكر، يقال: خص المطر بني فلان، وخصه الله تعالى بأمر من فضله. ولكن غلب [في] عرف الأصوليين [إطلاقه على] ما يتعلق بالذكر. وهل يكون العموم والخصوص من عوارض الألفاظ، أو يرجعان إلى الكلام، [لا إلى] الألفاظ؟ وقد تقدم [من] كلام الأئمة أن العام والخاص قولان قائمان بالنفس.
وقال أبو حامد: إنهما من عوارض الألفاظ. وما يظن به إنكار كلام','2','135',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (832,'النفس، [وإنما] الظن به أنه أراد الصيغ، لانتشار الكلام [عليها]، والحاجة إلى معرفة وضع اللغة فيها.
وقول الإمام: (إن اللفظ باعتبار العموم والخصوص ينقسم ثلاثة أقسام: إما خاص مطلقًا، كزيد، وهذا الرجل، وإما عام مطلقًا، كالمعلوم والمذكور، وإما [خاص] من وجه، [وعام] من وجه، كالزَّيدَيْن [والزَّيدِين]). كلام صحيح، والمراد بالمذكور: ما يصح تعلق الذكر به، [أو ما] هو مذكور لله عز وجل. [وإذا] [أريد] ذلك، لم يتصور وجود لفظ أعم من هذا [بوجه]. ولهذا [كان] هذا النحو من الألفاظ لا يتصور التجوز فيه بحال. [إذ] كيف يتصور أن يكون مجازًا فيما هو حقيقة فيه؟ وقد اعترض أبو حامد','2','136',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (833,'على هذا، وقال: المذكور لا يتناول المسكوت عنه، والمعلوم لا يتناول المجهول. قال: ومن هذه الجهة يمكن أن يقال: ليس [يوجد] في الألفاظ عام مطلقًا. وقد (٩٥/أ) بينا نحن المراد الإطلاق، وأن المراد به: إما ما يصح أن يكون مذكورًا، او كونه مذكورًا لله [عز وجل].

قال الإمام: (ثم ذكر [أرباب الأصول] طرفًا [في]<span data-type="title" id=toc-147> الفرق بين التخصيص والاستثناء) </span>إلى قوله (والازدياد بعد البيان لا يفيد). قال الشيخ: [ما ذكره] الإمام هو حقيقة الفرق، [فإن] الاستثناء يعد','2','137',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (834,'جزءًا، والتخصيص بيان ودليل، ولا يشترط [المقارنة] في الأدلة، ولا القصر أيضًا على الألفاظ، ولا يشترط القاطع في المبين، ولا ورود البيان على النص. وإذا كان الاستثناء [جزءًا]، اقتصر على اللفظ واشترط الاتصال. [وهما] يشتركان في امتناع الاستغراق، فلا يصح أن (٧١/ب) يكون استثناء [مستغرقا]، ولا التخصيص مسقطًا.
[وقول] الإمام: (إن الاستثناء مع المستثنى [منه] في حكم الواحد [المسوق] لما يبقى بعد الاستثناء). كلام فيه إشكال، وذلك أن','2','138',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (835,'[الأصوليين] اختلفوا في قول القائل: عشرة إلا خمسة، هل هذا اللفظ عبارة عن خمسة، [أو العشرة] عبارة عن العشرة والخمسة للخمسة، و) إلا) للنفي بعد الثبوت؟
ذهب القاضي [رحمه الله] إلى أن اللفظ [بجملته] عبارة [عما] بقي بعد الاستثناء. قال: ويكون الكلام بسبب الزيادة شيئا آخر، موضوعًا لشيء','2','139',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (836,'آخر، كما تقول: زيد، فيكون للواحد، فتزيد (الواو) و) النون) فيصير للجمع. وقصد [بهذا] أن يفرق بين التخصيص بدليل متصل أو منفصل، فإن كان بدليل متصل، [كان] الباقي حقيقة، وإن كان بدليل منفصل، تناول اللفظ الباقي مجازًا. وكذلك قال في الاستثناء: [إن الكلام] بجملته يصير عبارة عن أمر آخر. [وذهب] غيره من الأصوليين إلى خلاف هذا، [وأن العشرة للعشرة، والخمسة للخمسة]، كما ذكرناه. ويظهر مذهب [القاضي] من وجهين:
أحدهما- أنه لو كان الأمر كذلك، للزم أن يكون العربي الذي لا يعرف الحساب إذا قال: عشرة إلا ثلاثة [أن] لا يفهم المدلول من لغته، وهذا غير صحيح.','2','140',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (837,'الثاني- أن الله تعالى إذا قال: {فلبث فيهم ألف سنة [إلا خمسين عامًا]}. فكلام السنين على وفق العلم، والله تعالى إنما علم لبثه تسع مائة وخمسين [عامً]، فجعل هذا عبارة عن ذلك. قال القاضي: فقد وضعت العرب لتسع مائة وخمسين عبارتين: إحداهما- ألف سنة [إلا خمسين]. والأخرى- تسع مائة وخمسين. [والذي] ذكره القاضي ظاهر، والفقهاء مجمعون على [أنه] [إذا] قال له: عندي عشرة إلا درهما، [لم يقر] إلا بتسعة. ولا يقال: أقر بعشرة ثم رفع درهما منها، [هذا لا يصح]. ولكن','2','141',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (838,'إطباق أهل اللسان يظهر منه خلاف هذا، فإنهم يقولون: الاستثناء من النفي إثبات، ومن الإثبات نفي، وحدوه: بأنه [المخرج] من الكلام ما لولاه لكان داخلا فيه.
فهذا كلام أهل العربية بجملتهم، ففيه إثبات ونفي. ولو كان عبارة عن [تسعة]، [أعني] في قوله: (عشرة إلا درهما)، لم يكن فيه إثبات ونفي، ولهذا قال الإمام في هذا المكان: (إن المستثنى مع المستثنى عنه في حكم الكلام [المسوق] لما يبقى بعد الاستثناء). ولم يقل هو كلام واحد، وإنما قال: في حكم الكلام الواحد، يعني باعتبار ما يلزم من الإقرار، [وما] يستقر في النفس من حال المخبر عنه.
والمسألة غامضة باعتبار اختلاف هذه [الجهات]. [والذي] يصح عندنا فيها الطريق الثاني، ومعنى قوله: له عندي عشرة إلا درهما، معناه: عشرة تنقص درهما، وكذلك الجواب عن الآية وهي قول: {ألف سنةٍ إلا خمسين','2','142',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (839,'(٩٥/ب) عامًا}. معناه: ألف سنة ناقصة خمسين [عاما، ولا نقول: ألف سنة إلا خمسين]، عبارة عن [واحدة] عن تسع مائة [وخمسين]. هذا هو الذب نراه في ذلك.
قال الإمام: ([وليعلم] [الناظر] أن ما (٧٢/أ) نذكره في العموم','2','143',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (840,'والخصوص إنما يختص بالأسماء دون الفعال والحروف) إلى آخر المسألة. قال الشيخ [أيده الله]: قوله: [إن ما] [نذكره] في العموم والخصوص. لا يريد أنه لا يثبت عموم الألفاظ، [لأنه] قد تقدم إثبات كلام النفس. وإنما [يعني] ههنا أنا إنما نتكلم على الصيغ الدالة على العموم والخصوص.
وإذا تكلمنا في الألفاظ في هذه المواضع، فقد [تقدم] أن الكلمات اللغوية تنحصر في ثلاثة أقسام: أسماء وأفعال وحروف، أما الحروف فكما ذكر، ليس لها معان في [نفسها]، وعموم المعنى بعد ثبوته، وكذلك خصوصه، وأما الأفعال، فلا يلحقها معنى التعميم، ولا التخصيص على','2','144',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (841,'حدهم، [فإنهم] قالوا: [العموم] الشمول، والعام: هو الذي يتناول مسميين فصاعدًا، [يجوز] إفراد كل واحد منهما عن الآخر. والخاص: هو اللفظ الذي ينبئ عن أمر يجوز إدراجه مع غيره تحت لفظ آخر. [وإنما] المشكل عدم عموم [الفعل] وخصوصه.
وتمسك الإمام في أنه لا يتصف بالخصوص، لأنه لا يقبل التثنية والجمع. وهذا يستدل به من يقول إنه خاص، ولكنه لما قال: إن الخصوص','2','145',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (842,'لا يتصور إلا حيث يمكن [التعميم]، [لزم أن يكون الفعل غير خاص، ولكن من أين يلزم أن لا يثبت الخصوص إلا حيث يمكن العموم]؟ [هذه] دعوى محضة، ولكن نحن إنما نعني [في] هذا المكان بالفعل (الفعل الصناعي)، نحو (قام) و) قعد)، وهذا إنما جيء به على الحقيقة للإشعار بزمان الحدث، والإرشاد إلى [مضيه] [أو] حلوله [أو] استقباله، ففهم مقصوده يمنع من شموله، إذ لا مقصود إلا الإرشاد إلى أحد هذه الأزمان، فلا يتحقق فيه شمول المسميات. إذ لا دلالة له على أصل المسميات، فكيف يكون عاما أو خاصا فيها، ولا دلالة عليها؟

قال الإمام: <span data-type="title" id=toc-150>(مسألة: لا يمتنع ورود اللفظ العام مع استئخار المخصص عنه إلى وقت الحاجة) </span>إلى آخر المسألة. قال الشيخ: هذه المسألة بعينها','2','146',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (843,'تقدمت، وأدلتها سبقت. والذي أراده الإمام في هذا المكان غير مفيد،','2','147',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (844,'وذلك أن الذين اشترطوا اقتران البيان، لم يشترطوا إلا اقتران الدليل المخصص، ولم [يلتزموا] أن يكون ذلك الدليل أوضح الأدلة، بل اشترطوا أن يكون الدليل غير متأخر فقط.
وقوله: (لو بين ذلك بالنص، [كان] أحرى. [وقد] يقول القائل: قد يتطرق إلى الألفاظ الاحتمال، [وأدلة] العقول لا تقبل ذلك بحال).','2','148',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (845,'[وحاصل] كلام الإمام [أن] البيان بالقول أصلح، ومن اوجب الصلاح لم يوجب الأصلح، فالذي قاله غير لازم للقول بحال. [فالاجتزاء] بما سبق هو الأصل في المسألة. وقد كنا بينا أن أدل الأدلة على الجواز العقلي الوقوع السمعي، ونحن نعلم ضرورة أن الآيات العامة، كالزكاة والسرقة، وحل البيع والنكاح، وغيره من قواعد الشريعة كثيرة مسائلها، وأشكال شروطها، وما يعتبر فيها، مما يستحيل أن يبين وقت الورود. وهذا معلوم بالضرورة والبديهة، وفيه [أبلغ] حجة، [وأوضح] دليل. (٧٢/ب).','2','149',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (846,'قال الإمام: (٩٦/أ) <span data-type="title" id=toc-151>(مسألة: إذا وردت الصيغة [الظاهرة] في اقتضاء العموم، ولم يدخل وقت العمل بموجبها) </span>إلى آخر المسألة. قال الشيخ: مذهب الصيرفي أنه يمتنع تأخير البيان عن مورد الخطاب إلى وقت الحاجة. هكذا [حكاه] [عنه أبو حامد]، [وتفريعه] لا يقتضي إلا ذلك. ومعنى قوله: إن [تبين] ذلك، أي إن لم يرد [ما يناقض].','2','150',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (847,'العموم، وإن ورد [مناقض، تغير] العقد. يعني- والله أعلم- أن الوارد [يعد] ناسخا، [فينتفي] الحكم الذي تيقنا ثبوته أولًا، لا وجه غير ذلك. فأما المصير إلى اعتقاد الظهور، وصحة ورود البيان، ثم القطع بالحكم، هذا محال [لا يلتئم] في الفكر على حال.
وقول الإمام: (إن أبا بكر يرد على هؤلاء في تصانيفه). هل يرد','2','151',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (848,'عليهم في [عين] المسألة، أو رد عليهم في غيرهم؟ فالرد [عليهم] في غيرها، لا يمنع الموافقة فيها، ولو رد عليهم في عينها، لوجب أن يكون له في المسألة قولان، [ولا وجه] غير ذلك. واشتمال [الفكر] على القطع بالعموم، [وتجويز] إرادة البعض باللفظ في حالة واحدة، [لا] يعقل بحال.
وقوله: (والذي نختاره أن [المكلف] قبل أن [يتنجز] العمل يتردد، وقد يغلب غلى ظنه العموم). عبارة ضعيفة، فإن (قد) إذا صحبت الأفعال [المستقبلية]، أفادت تقليلا. [والمفهوم] من هذه العبارة أن الغالب أن المكلف قبل دخول وقت العمل لا يظن، وليس الأمر كذلك إلا عند الواقفية.','2','152',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (849,'وقوله: ([إذا] دخل وقت العمل كان ذلك على وجهين: أحدهما- القطع بالعمل مستندا إلى قرائن الأحوال). وهذا [أيضًا كلام] ضعيف، لأنه إذا اعتمد في حصول العلم على قرائن الأحوال، فهي راجعة إلى حال المتكلم وقت نطقه، فلا [ينظر] في حصول العلم [ودخول] وقت العمل، [لتقديم دليل] الاستغراق قطعا.
وقوله: (والثاني-[إلا] تظهر قرائن [يعلم] بها العموم، فيبقى','2','153',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (850,'[الأمر] في العموم [مظنونا]، واعتذر بأن الأعمال لا يشترط فيها الأدلة القاطعة [في ظن] الاستغراق، وإن كنا نعلم العمل، كما نقول في خبر الواحد والقياس، وكل ما [ليس] مقطوعا به). هذا [كلام] ضعيف، وبيان ضعفه: هو أنا وإن قلنا: إن الأعمال تثبت من غير اشتراط أدلة قطعية في [أفراد] المسائل، [بل] يستند العمل عندها إلى الإجماع. لكن يبقى وراء ذلك أمر آخر، وهو أن العموم إنما لم يحصل القطع باستغراقه، لاحتمال أن يكون المتكلم إنما قصد به البعض دون الجميع.
هذا هو السبب في عدم حصول العلم. وإنما [هنا] علمنا أنه لم يرد جهة الاحتمال، مع القطع بأصل الدلالة، فيحصل (٧٣/أ) من هذه الجهة القطع','2','154',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (851,'بأنه أراد الاستغراق، إذ لو كان قصد بلفظه الدلالة على البعض، فهذا أمر يفتقر إلى البيان عند الحاجة، فلا يسوغ تأخيره بحال، عند من صوب كل مجتهد، فكيف يجوز أن يبقى يظن الاستغراق، وقد انفصل الزمان، وامتثل المكلف الجميع؟ هذا باطل، على من أحال تأخير البيان، وصوب كل مجتهد.
والإمام ممن أحال تأخير البيان عن وقت الحاجة. وإذا كان اللفظ لا يحتمل قطعا إلا إحدى جهتين بأعيانها، فإذا قام دليل قاطع على أنه لم يرد به إحداهما، [بقيت] الجهة الأخرى على قطع. فلا وجه عندي لما قاله الإمام. نعم، هذا يمشي على [مذهب] من يقول المصيب واحد، فإنه يعمل بما غلب على ظنه، وإن أمكن أن يكون الباطن خلافه. وعلى (٩٦/ب) هذا لا [يمتنع] تأخير البيان عن وقت الحاجة.
قال الإمام: (مسألة: [اللفظ الظاهر] في العموم إذا اقتضى العقل','2','155',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (852,'[تخصيصه] إلى آخر المسألة. قال الشيخ: ما ذهب إليه الأصوليون بين في هذه المسألة، ولم يذكر الإمام مستندا للقوم، ويمكن أن يكون مستندهم أن الواضع للغة إذا عقل الأمور، فإنه لا يضع اللفظ دالًا على ما [لا] يعقل، لاسيما إذا قلنا إنها [توقيف] من الله تعالى، فإنه يظهر ظهورًا جليًا أنها إنما تثبت على مقتضى المعقولات.
وقد تكلمنا على قريب من هذا في إظهار فائدة الخلاف في اللغة،','2','156',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (853,'[هل] هي اصطلاح أو توقيف؟ . وإذا كان كذلك، كان معنى قوله: {الله على كل شيء قدير}. [أي: على كل شيء] مقدور غيره [قدير]. ولا التفات لكون لفظ الشيء ينطلق على القديم والحادث، ويكون اقتران الاقتدار باللفظ يبين اقتصاره. هذا [وجه مسوغ بين في مقصوده. وأما الذي يلجئنا إلى أن اللفظ يتناول كل شيء، ثم] يفتقر إلى التخصيص بعد ذلك، فلا يقع بخطأ هؤلاء القوم في هذه المسألة.
قال الإمام: (مسألة: الصيغة الظاهرة في العموم إذا تطرق إليها','2','157',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (854,'التخصيص، فقد صار جماهير المعتزلة وطوائف من أصحاب [أبي حنيفة] [إلى] أنها صارت مجملة في بقية المسميات، لا يسوغ التمسك بها إلا أن يرد خطاب بتنزيلها على بقية المسميات، [وبالتعبد والعمل] بموجبها) إلى قوله (وفي تناول البقية مجاز في الاختصاص). قال الشيخ: الكلام في هذه المسألة [يتعلق بطرفين]:
[أحدهما-] في الحقيقة والمجاز والإجمال والبيان. [والثاني-] ما يتعلق بالعمل.
أما الأول- فقد اختلف الناس (٧٣/ب) فيه [على] مذهبين: الأول-','2','158',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (855,'ما ذهب إليه المعتزلة أنه صار مجملا. والثاني- مذهب الفقهاء والإمام والقاضي: أنه مبين. ثم هؤلاء اختلفوا، هل هو حقيقة، أو مجاز، أو حقيقة تارة، أو مجاز أخرى؟ فذهب أكثر الفقهاء إلى أنه حقيقة. وذهب القاضي إلى الفرق بين أن يكون التخصيص بدليل متصل لفظي، أو يكون على خلاف ذلك. فإن كان بدليل متصل لفظي، فهو حقيقة، وإن كان على خلاف ذلك، فهو مجاز. وذهب الإمام إلى [أنه] حقيقة في تناوله، مجاز في الاقتصار.
أما مأخذ المعتزلة، فهو أن اللفظ إذا تعددت دلالته، واستوت نسبته، ولم يقصد جميع المسميات، [وجب]، قالوا: وهذا محقق في العموم','2','159',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (856,'إذا [خصص]، فإن ظاهر اللغة يقتضي استغراقا، وجهة الاستغراق واحدة، وإذا ثبت الخصوص، بطل الاستغراق [المتحد] [الجهة]، ولزم رد اللفظ [إلى جهة المجاز]، وهو الدلالة على البعض. [وإذا] تعددت جهات المجاز، واستوت النسبة، كان ذلك بمثابة [ما لو] [تعددت] الحقائق على استواء النسبة، وامتناع قصد [الجميع]، فلابد في ذلك من الإجمال.
والكلام قوي، ونحن نقول: الأمر كذلك إذا لم تغلب إحدى جهات المجاز، أما إذا غلبت [بعرف] [لغة]، أو عادة شرع، لزم أن [تكون] الجهة','2','160',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (857,'الغالبة مصارًا إليها، بعد قيام دليل التخصيص. كما تقدم في قوله: {حرمت عليكم أمهاتكم}. و} حرمت عليكم الميتة}. فإن أهل اللغة كانوا يفقهون قول القائل: حرمت عليه الطعام، أنه إنما يريد أكله، (٩٧/أ) والمرأة إنما يريد وطأها والتلذذ بها، فيفتقر [هنا] إلى دليل يقتضي تغليب بعض الأبعاض على بعض، ولم يثبت ذلك عند المعتزلة.
وأما القاضي رحمه الله، فإنه قال: إن كان التخصيص بدليل متصل لفظي، وجب المصير إليه، وكان باقيا على حقيقته، وإن اقتصر على البعض، لأن الكلام باتصال الزيادة، كلام آخر، موضوع لشيء آخر. وقد تقدم كلامنا على هذا قبل ذلك في مسألة الاستثناء.
وإن كان التخصيص بدليل منفصل، كان اللفظ مجازًا، [إذ تجريد] النظر إليه يقتضي استغراقًا، باعتبار وضع اللغة، ولم يبق دالًا على ذلك، وكل لفظ دل على غير ما دل عليه عند أهل اللسان بالوضع الأصلي، فهو مجاز. هذا تقرير كلام القاضي. وهذا أصعب شيء على القاضي، فإنه ذهب إلى تنزيله','2','161',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (858,'على ما عدا المخصوص، مع تطرق احتمالات كثيرة.
واحتج في ذلك بأن الصحابة رضي الله عنهم كانوا لا يقفون [عن] العمل بالعمومات، إذا تطرق التخصيص [إليها]. وهذا لا يصح من القاضي في العموم، [لأنه] [لا ينكر] دلالته قبل أن يثبت تخصيصه، [وينكر] [الإجماع] على ذلك أشد الإنكار، فكيف يسوغ منه أن يدعي الإجماع على العمل بعد التخصيص، والعموم إذا ثبت خصوصه ضعيف في نفسه؟ [فهو] ينكر الإجماع عندما يكون العموم قويًا، فكيف حاله عندما (٧٤/أ) يضعف بالتخصيص؟
وحجته أنه وجده [يستعمل] تارة للاستغراق، وتارة للبعض، والمصير إلى الحوالة على [القرينة] في إحدى الحالتين دون الأخرى، تحكم من غير دليل، يعارضه عكسه من غير ترجيح. وهذا المسلك بعينه يجري فيه إذا','2','162',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (859,'خصص، فإنه قد يقصر تارة على الكثير، وتارة على القليل، وتارة على [المتوسط، فلا] يجري على قاعدته إلا [طرد] الوقف، أو يكون القاضي له قول آخر بإثبات صيغ العموم، ويكون هذا منه تفريعا على ذلك القول، أو يكون تكلم على التفريع على [مذهب] المعممين.
وهذا قد تأوله بعض الناس عليه، [وهو] ضعيف جدًا، إذ كيف يقول: أجمع المسلمون على العمل بالعموم بعد التخصيص، محتجًا به على إبطال الإجمال، وهو لا يرى ذلك على حال، ويغلط مدعي الإجماع على العمل قبل التخصيص، وينسبه إلى الغفلة والجهالة؟ فلا يصح في هذا عندي إلا وجه واحد، وهو أن يكون القاضي يرى القول بصيغ العموم، فيكون فيكون هذا طريقًا صحيحًا، وتفريعًا مستقيمًا، ويكون قد رجع إلى [قول] الفقهاء في معرفة أحوال الصحابة من العمل بالعمومات، إذا لم توجد الأدلة المخصصة، ويكون','2','163',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (860,'الإجماع عند قيام الدليل على التخصيص، اقتضى الاعتماد فيما وراء ذلك.
فأما قول الإمام إنه: ([اجتمع] في اللفظ موجب الحقيقة والمجاز). فكلام ضعيف، وقد نقضه على نفسه في كتاب التأويلات. وقال: لما قال أصحاب أبي حنيفة: قوله - عليه السلام -: (لا صيام لمن لم يبيت الصيام من الليل). هو عام في جميع أنواع الصوم، إلا أنه يحمل [في] بعضها على الأجزاء، وفي بعضها على الكمال، رد ذلك بقوله: (فإن الإنسان الفصيح [ذا الجد] لا [يطلق] لفظة [واحدة]، وهو يبغي حقيقتها من وجه، [ومجازها] من وجه). وقد التزم هو ذلك في الكتاب (٩٧/ب) والسنة [جميعًا، فكيف يصح أن يسلب الفصاحة عن الكتاب والسنة؟ ].','2','164',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (861,'وقوله: (لو أفنى] الطالب عمره مكبًا على الطلب [الحثيث]، لم [يظفر] [بعام] شرعي [لم] يتطرق إليه [التخصيص]). هذه مبالغة، وإلا [ففي] كتاب الله عز وجل: {فأعلم أنه لا إله إلا الله}. و} وما كنا معذبين حتى نبعث رسولا}. و} الله لا إله إلا هو الحي القيوم}. وغيره، ولكن المراد المبالغة في ذلك.
وكونه يقول: (إن تناوله لبقية المسميات لا تجوُّز فيه، واقتصاره عليها وانحصاره فيها، جهة في المجاز). هذا الكلام ضعيف، وذلك أنه إذا ثبت أن وضعه الاستغراق والشمول، فهذه جهة متحدة، لا تقبل التعدد بحال، فإذا لم يشمل، فقد خالف الوضع، وصير إلى جهة المجاز، وهي متعددة. وإنما وقع للإمام هذا الوهم، من جهة أنه قدر استرسال اللفظ على المسميات، استرسالًا لا يضاهي مدَّ الأجرام بعضها على بعض، فإذا أزيل الساتر عن بعضها، بقي','2','165',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (862,'ساترًا لما بقي تحته من غير تغيير. وليس تناول اللفظ للمسميات على هذا النحو بحال، بل الجهة متحدة، كما قررناه.

قال الإمام [رحمه الله]: <span data-type="title" id=toc-154>(فصل- قد ذكرنا أن الأولى [تقديم] كلام يحوي حقيقة النص والظاهر والمجمل [والمفسر] والمتشابه [والمحكم) إلى قوله () فإذا وجد فجوة نص)] </span>(٧٤/ب) وهذا الآن كاف في معنى النص). قال الشيخ: قد بينا أن الألفاظ لا تخلو من أن تكون مفهومة المعنى بوضع اللغة، أو لا تكون مفهومة المعنى، ونعني بذلك بعد ما تقرر كونه كلامًا. وقد قدمنا سؤالا على ذلك، إذ حددنا الكلام: بأنه المفيد، [ثم] قلنا:','2','166',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (863,'ومن [جملته] المجمل: وهو الذي لا يدرك معناه، وأجبنا عن ذلك.
فاللفظ إن لم يفهم معناه بوضع اللغة، فهو المجمل، وإن فهم، فلا يخلو: إما أن يقطع بالمفهوم منه، أو يظن، فإن قطع به، فقد اتحدت جهة الدلالة، وإن لم يقطع به، بل ظن ذلك ظنًا، [فهو] بالإضافة إلى المعنى المظنون يسمى ظاهرًا، وبالإضافة إلى المعنى الخفي يسمى مؤوَّلًا، فانحصرت الألفاظ اللغوية في النصوص والظواهر والمجملات.
فأما النص: فلفظ [مشترك] في عرف الأصوليين، يطلق على ما لا يتطرق إليه [تأويل، وهو الأشهر الأبعد عن الاشتراك. ويطلق [على] ما يتطرق إليه] احتمال، وسواء عضد بدليل، أو لم يعضد. هذا هو الذي ذكره [الشافعي]. وهو اختيار القاضي. وهذا هو طبق اللغة، فإن النص في اللغة','2','167',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (864,'معناه: الظهور، يقال: نصَّت الظبية رأسها، إذا رفعته. وقال بعض الأصوليين: النص [ينطلق] على ما لا يقبل التأويل، وعلى ما لا يتطرق إليه تأويل، غير مقصود بدليل، أما إذا عضد بدليل، خرج عن كونه نصًا. وهذه اصطلاحات قريبة، والمعنى المهم معرفة تطرق الاحتمال وانقطاعه. هذا [هو] الذي يحتاج إلى معرفته على الحقيقة. فاللفظ قد يكون نصًا بوضع اللغة، وقد يكون نصًا بالقرينة.
فأما ما ذكره الإمام: (من اعتراض [من اعترض] بالفحوى، فإنها تقع [نصًا]، وإن لم يكن لفظًا، وجوابه: بأنها ليست مستقلة [بنفسها]). فليس','2','168',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (865,'هذا جوابًا على الحقيقة، فإن الفحوى ليست لفظًا. [نعم]، لها استناد إليه كما يستند المفهوم، ولكن ليست متلفظًا بها.','2','169',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (866,'وقول بعضهم: (استوى [ظاهره] وباطنه). يشير بالباطن (٩٨/أ)','2','170',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (867,'إلى المفهوم، وإلا فليس للفظ ظاهر [ولا] باطن على الحقيقة.
قال الإمام: ([وأما] الظاهر، [فقد فقال] القاضي','2','171',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (868,'[أبو بكر]: [هو] لفظة معقولة [المعنى] إلى قوله ([فالاستمساك به] تعلق بالظاهر، وتركه في حكم التأويل). قال الشيخ: [ما ذكره','2','172',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (869,'الإمام] من [الاعتراض] على القاضي غير صحيح عندي، ولم يزل اللفظ على ظهوره في المسائل التي ذكرها، بالنظر إلى أصل [اللغة]، وإنما صير [إلى جهة] [المجاز] فيما ذكره، لأدلة ظاهرة جلية. وانصراف اللفظ عن أصل موضوعه إلى جهة مجازه بالدليل، لا يخرجه عن كونه [ظاهرًا]، لاسيما إذا كان الدليل جليًا واضحًا.
وقد تقدم للإمام ما يدل على هذا في الصيغة الظاهرة إذا اقتضى العقل تخصيصها، فإنها تكون مخصصة بدليل العقل، ولا يخرجها ذلك عن كونها ظاهرة، وإن كان العقلاء لا يفهمون منها جهة ظهورها، وكذلك يكون عرف استعمال أهل اللغة أو عرف الشريعة دليلًا يخرج اللفظ عن ظهوره، ويوجب المصير إلى جهة احتماله، ولا يسلبه ذلك اسم الظهور [عنه].','2','173',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (870,'قال الإمام: (ثم الظهور] (٧٥/أ) قد يقع في الأسماء) إلى قوله ([فهذه] معاقد تفصلها التأويلات). قال الشيخ: قد تقدم في معاني الحروف أن سيبويه قال: وإن استعملت (إلى) قرينة (من) كانت للحد، ولم يدخل الحد في المحدود، وإن لم تستعمل مع (من)، احتمل أن تكون للغاية، واحتمل أن تكون بمعنى (مع). ولم يقل إنها أظهر في أحدهما.
وقد قال الإمام: إنها في قوله تعالى: {وأيديكم إلى المرافق}. بمعنى (مع)، ولم يدل على ذلك. فإن كان ذلك لدليل خاص، فلا مناقضة بينه','2','174',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (871,'وبين هذا الكلام. وإن كان اقتصارًا على محض [ورود الآية، فهو هذا].
قال الإمام: ([فأما المجملات]، فقد يطلق المجمل على العموم [في قولك: أجملت الحساب، إذا جمعت آحاد [وأدرجتها] تحت صيغة جامعة لها) إلى فقوله] (والتفصيل محال [به] على باب','2','175',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (872,'التأويلات)
قال الشيخ: ما ذكره [من جهة بيان] الإجمال واضح، وحاصله أن ما للحكم به تعلق بوجه من الوجوه، قد [يتبين] ذلك الوجه، وقد يكون أمره ملتبسًا، فتتعدد جهات الإجمال، بالإضافة إلى تعدد متعلقات الحكم. غير أن الكلام جرى فيه نوع من التجوز، وهو قوله: قد يكون الحكم [مجملًا]. ومثله بقوله: {وءاتوا حقه يوم','2','176',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (873,'حصاده}. ليس الحق المجمل حكمًا، وإنما الحكم وجوب أداء الحق، فإن الحكم قد تحقق أنه يرجع [إلى] الخطاب، و) الحق) إنما هو العشر [أو] نصفه، وهذا لا يتصور أن يكون خطابًا، ولا يصح أيضًا أن يكون متعلق الحكم، فإن العشر أو نصفه ليس من أفعال المكلفين، فقد جرى في الكلام تجوز كثير. وكذلك أيضًا قوله لفلان: (في بعض مالي حق). جعل أيضًا [الحق] حكمًا.
وأما قوله: (إذا قال لنسائه: إحداكن طالق، أن الحكم الطلاق). فهذا الكلام [أيضًا] محتمل، [إن أراد بالطلاق قول الزوج: إحداكن طالق، فهو غير صحيح، إذ قول الزوج ليس بحكم]، وإن أراد الطلاق الذي حكم الشر عبه عند قول الزوج، فصحيح. وأما كون المشترك مجملًا. فقد تقدم الكلام عليه مبسوطًا.','2','177',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (874,'وقوله: (ومن وجوه [الإجمال]: أن يكون اللفظ لو فرض [الاقتصار] عليه، [كان مفهومًا]، ولكنه [وصل] باستثناء (٩٨/ب) مجهول)، ومثله بالآية. فهو كلام صحيح، لأنه إذا كان المخرج غير معلوم، لم يعلم المنفي. وهذا قول قد يتفق أن يكون المخرج نسبته إلى سائر المنفي نسبة واحدة، فهذا يصير جميع الباقي مجملًا، وقد يكون التردد في البعض، فيكون ذلك البعض مجملًا خاصة، ويبقى الأول على بيانه.
مثاله: ما لو قال: أكرم الناس إلا رجلًا من بني تميم، معينٍ عند الآمر، فمن سوى بني تميم [على] ظاهر التعميم في الإكرام، وإنما التوقف في بني تميم على الخصوص. والقسم المذكور بعده راجع [إلى هذا]، وهو أن يعلم أنه لابد من إخراج صورة من العموم، ولم تتعين، فيبقى على الإجمال في','2','178',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (875,'البقية، حتى [يتعين] ما اقتضى العقل إخراجه، كالقسم الذي قبله.
فأما قوله تعالى: {وأحل الله البيع [وحرم الربوا]}. فقد اختلف الناس أولًا: هل هو عام أو خاص؟ للتردد في أن (الألف) و) اللام) للعهد [أو للجنس]؟ [فإذا] جعلت للعهد، وهو البيع الذي عرفت شروطه، لم يكن قوله: {وحرم الربوا} لا استثناء ولا تخصيصًا، بل استئناف جملة أخرى. وإلى هذا ذهب كثير من أصحابنا. وقال قائلون: (الألف) و) اللام) للجنس، إذ لم يتقدم منكر ينعطف التعريف عليه. وعلى هذا ينظر نظرًا [آخر، ] وهو أن البيع، هل تصرف الشرع فيه، حتى يكون الاسم ينصرف إلى الجنس المشروع؟ على حسب ما مر في قوله: (لا صيام لمن لم يبيت الصيام من الليل). فيه للعلماء خلاف.','2','179',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (876,'ولقد اختلفوا فيمن حلف: [أن لا] يبيع، فباع الخمر، هل يحنث [أو لا يحنث]؟ فذهب قوم إلى أنه يُسمَّى بيعًا، وأنه يحنث. وقال قائلون: لا يحنث، إذ ليس ببيع، والبيع هو البيع الشرعي.
وقال مالك رحمه الله: يحنث إن باعها (٧٥/ب)، وإن حلف ليبيعن، لم يبر ببيعها. وهذا القول صعب، لأنه لم يتمسك بالإطلاق اللغوي، ولا [نزل] الأمر على البيع الشرعي. ولكنه بناه [على] أصله، [من الاحتياط] فيما يتعلق بالبر والحنث في الأيمان، فلا يبر إلا بما لا إشكال فيه، ويحنث عند إمكان الحنث احتياطًا. وكذلك جرى له فيمن حلف','2','180',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (877,'ليأكلن هذا الرغيف، لم يبر إلا بأكل جميعه، وإن حلف أنه لا يأكله، حنث [بأكل اليسير منه]. و [اعتمد أصحابه في ذلك طريقين:
أحدهما]- أنه إذا حلف ليأكلنه، فكل جزء محلوف على إيقاع الكل فيه، [فإذا] حلف أن لا يأكله، فكل جزء محلوف على أنه لا يوقع فيه أكلًا. وقال قائلون: إنما بنى الأمر فيهما جميعًا على الاحتياط، [برًا] وحنثًا.
ونظير هذا أن الله تعالى لما حرم ما نكح الآباء من النساء، حرمت من وطئها الأب على ابنه وطئًا صحيحًا أو فاسدًا، وكذلك بالعقد دون الوطء. ولما قال الله تعالى في المطلقة ثلاثًا: {فلا تحل له}. أي للزوج المطلق} حتى تنكح زوجًا غيره}، لم يحلها مجرد العقد دون الوطء، فإنه قال - عليه السلام - فيها:','2','181',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (878,'(لا حتى تذوق العسيلة). فاشترط عند الحل كمال الوطء، ولم يشترط ذلك في التحريم.
هذا [توجيه] ما قاله مالك رحمه الله. وإذا قلنا: إن الشرع ليس له تصرف في لفظ البيع، [وأنه] باقٍ على أصل الوضع اللغوي، فقد امتنع إبقاؤه على ظاهره بلا شك، فإن الله تعالى [قد] حرم بيوعا، منها بيع الربا، فقد استثنى [مجهولا]، فإن الربا: الزيادة، ولا تحرم كل زيادة. ولكن هل ينعكس هذا الإجمال على جميع ما تضمنه (٩٩/أ) أول المقال حتى يكون بمثابة قوله: {إلا ما يتلى عليكم}؟ أو ينعكس الإجمال على بعضه دون بعض، حتى يكون كقول القائل: أكرم الناس إلا رجلا من بني تميم أعينه لك؟
هذا موضع اختلاف الإمام والشافعي. ورأى الشافعي أنه من القبيل الأول، وقال الإمام: بل من الثاني. وحجته أن الربا: الزيادة، ولا تحرم','2','182',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (879,'[كل] زيادة، [فإذا] وقع البيع غير مشتمل على زيادة، فهو مندرج تحت الظاهر، ولم يرجع إليه إجمال بحال. ولمذهبه وجه بين.
ومعتمد القول الآخر: أن الزيادة في الشرع لا ترجع إلى المحسوسة، اقتصارا عليها، فإنه قد حرم في بيع الطعام بالطعام الأجل، وإن اختلف الجنس، وعُدَّ ذلك من أبواب الربا. فإذا كانت الزيادة، [فقد] تكون حسية، وقد تكون معنوية فالله تعالى أعلم بها.
ويدل على صحة هذا القول (أن النبي صلى الله عليه وسلم لما نزلت هذه الآية تلاها على المنبر، وحرم التجارة في الخمر). ويظن من التلاوة والتحريم عقيبها الاستدلال. وليس لكون بيع الخمر محرما، بنا على الربا الحسي، بل [لأنه] أخذ الثمن زيادة بلا مقابل، فيصح بهذا أن يكون كل بيع لا مقابل للعوض، أو المعوض فيه، من أبواب الربا، وله وجه بين. فعلى هذا ينعكس الإجمال على [أول] المقال.
قال الإمام: (فأما [المحكم والمتشابه]) إلى (٧٦/أ) قوله','2','183',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (880,'(والمتشابه: [هو] المجمل، وقد سبق معناه). قال الشيخ: الكلام في المحكم والمتشابه، [إما] أن يرجع إلى أصل وضع اللغة، [وإما] أن يكون على ما قصده الله [تعالى] في كتابه. فأما ما يتعلق منه بالكتاب، فيرجع إلى علم التفسير. [والمحكم] في اللغة: هو المتقن، [ومنه قوله تعالى: {منه ءايات محكمات}]، أي منه آيات [متقنات] مبينات مفصلات، } وأخر متشابهات}، أي يشبه بعضه بعضا. هذا هو الاشتقاق من حيث','2','184',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (881,'اللغة. واختلف علماء التفسير في المراد بهما في القرآن: فقال قتادة والربيع [والضحاك] والسدي: المحكم: الناسخ الذي يعمل به،','2','185',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (882,'والمتشابه: المنسوخ الذي [يؤمن] به، ولا يعمل به. و [هي رواية عطية عن ابن عباس]. وروي عن ابن عباس أيضًا أنه قال: (محكمات القرآن: ناسخه، وحلاله، وحرامه، وحدوده، وفرائضه، وما [يؤمن] به ويعمل به. ومتشابهه: منسوخه، ومقدمه، ومؤخره، [وأمثاله]، وأقسامه، وما [يؤمن] به، ولا [يعمل] به).
وروي عن ابن عباس أيضًا في قوله [تعالى]: {ءايات محكمات}.','2','186',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (883,'قال: هي الثلاث الآيات في سورة الأنعام: {قل تعالوا أتل ما حرم ربكم عليكم .. } إلى آخر [الثلاث] الآيات. ونظيرها في سورة بني إسرائيل: {وقضى ربك ألا تعبدوا إلا إياه}.
وقال مجاهد وعكرمة: المحكم: ما فيه من الحلال والحرام، وما سوى ذلك متشابه يصدق بعضه بعضا. وروي عن محمد بن جعفر بن الزبير أنه قال: المحكم: ما لا يحتمل من التأويل غير وجه واحد. والمتشابه: ما احتمل من التأويل أوجها. وهذا هو الذي اختاره الإمام.
وقال ابن زيد: المحكم: ما ذكره الله تعالى في كتابه من قصص','2','187',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (884,'الأنبياء، ففصله وبينه [لمحمد]- عليه السلام - وأمته، كما ذكر قصة نوح - عليه السلام - في أربع وعشرين آية، وقصة هود - عليه السلام - في [ثماني] آيات، وإبراهيم - عليه السلام - في [ثماني] آيات، ولوط - عليه السلام - في [ثماني] آيات، وشعيب - عليه السلام - في ثلاث [عشرة] آية، وموسى - عليه السلام - في آيات كثيرة، وذكر حديث رسول الله صلى الله عليه وسلم في أربع وعشرين (٩٩/ب) آية.
[والمتشابه: ] هو ما [اختلفت] فيه الألفاظ من [قصصهم] عند التكرار، كما قال [تعالى] في قصة نوح: {قلنا [أحمل]}. وقال في','2','188',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (885,'موضع آخر: {(فأسلك] فيها}. وقال في ذكر موسى: {فإذا هي حية تسعى}. وقال في [موضع] آخر: {[فإذا هي] ثعبان مبين}.
وقال بعضهم: المحكم: ما عرف العلماء تأويله وفهموا معناه. والمتشابه: ما ليس لأحد من العلماء [إلى] فهمه سبيل، [مما] استأثر الله تعالى بعلمه، [كالخبر] عن [وقت] خروج الدجال، ونزول عيسى - عليه السلام -، وطلوع الشمس من مغربها، وقيام الساعة، وفناء الدنيا ونحوها.
وقال [ابن فاتخة]: المحكم: أوئل السور، منها يستخرج القرآن.} آلم (١) ذلك الكتاب} منها استخرجت البقرة، و} آلم (١) الله .. }','2','189',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (886,'منها استخرجت آل عمران.
وقال ابن كيسان: المحكمات: حججها واضحة، ودلائلها لائحة لمن سمعها إلى طلب معانيها. والمتشابه: (٧٦/ب) هو الذي يدرك علمه بالنظر، ولا يعرف العوام تفصيل الحق فيه من الباطل. وقال بعضهم: المحكم: ما اجتمع على تأويله. والمتشابه: [ما] ليس فيه بيان قاطع.
وقال أبو عثمان: المحكم: فاتحة [الكتاب] التي لا تجزئ الصلاة [إلا] بها. وقال محمد بن الفضل: هي [سورة] الإخلاص، لأنه ليس فيها إلا التوحيد فقط. وقيل: المتشابه: [أمر] القدر. وهذا ضعيف، لأن أمر','2','190',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (887,'القدر منكشف عند العلماء بأدلة العقول.
واعلم أن القرآن كله محكم، بمعنى الإتقان والنظم، وحسن الترتيب، وثبوت الفصاحة والبلاغة، والانتهاء إلى أقصى درجات الجزالة. [ومتشابه] من وجه، أي يشبه بعضه بعضا، ويصدق بعضه بعضا.} ولو كان من عند غير الله لوجدوا فيه اختلافًا كثيًرا}. وقد روي [عن] ابن عباس أن المتشابه: حروف [التهجي] في أوائل السور. [وله] قصة مع حيي بن أخطب لم [أذكرها]. [هذه] أقاويل [أهل التفسير] من أهل العلم والسنة. وأما واصل بن عطاء ............................................','2','191',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (888,'والأصم، فأهل بدعة وضلال، فلا معنى للاشتغال بنقل مذاهبهم في التفسير، فإنهم فسروا بناء على قواعد فاسدة.
وأما إذا [جرى] الكلام في المحكم والمتشابه في غير القرآن، فالمحكم: المتقن، ويستحق اللفظ أن يكون متقنا بحسن نظمه ودلالته، وسواء كان ظاهرًا أو نصًا، إلا أنه إذا قطع بمعناه، كان أبلغ في بعده عن الاشتباه باعتبار المعنى. والمتشابه إما أن يرجع إلى المتلبس باعتبار معناه، أي اشتبهت نسبته إلى المعاني المتعددة التي لا تجتمع. ويصح أن يكون متشابها، أي مستوي النظم، حسن السياق. هذا معنى الفظ بالنظر إلى مطلق الوضع، وقد ذكرنا التفسير.
[قال الإمام]: (فإن قيل: هل بقي في كتاب الله [عز وجل] وقد استأثر','2','192',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (889,'الله برسوله [صلى الله عليه وسلم] [مجمل]؟ ) إلى آخر المسألة. قال الشيخ: وجه الإمام [سؤالا]، [وهو] أنه هل بقي في القرآن مجمل؟ فأجاب باستحالة ذلك في التكاليف، وهذا جواب حسن، مفرع على وجوب [البيان] في الأحكام. وأما الكلام الثاني، فليس فيه جواب عن السؤال على الحقيقة، لأنه أجاب بأن ذلك جائز في العقل، ولم يرد سمع [قاطع] بنفيه، فلم يبق (٧٧/أ) سوى التجويز. [والسائل] إنما [سأل] عن الوقوع،','2','193',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (890,'ولنتكلم نحن على الوقوع، وقد اختلف الناس فيه.
فذهب ذاهبون إلى أنه لم يمت الرسول صلى الله عليه وسلم وبقي في كتاب الله كلمة إلا [وقد فهم] معناها، [وجعلوا] في (١٠٠/أ) قوله تعالى: {والراسخون في العلم}. (واو) العطف، ومعناه: أن الله تعالى يعلم تأويله، ويعلمه الراسخون في العلم، ومع علمهم يقولون: آمنا به. [وهذا قول مجاهد، والربيع، محمد بن جعفر بن الزبير. ويكون قوله: {ويقولون ءامنا به}]، أي قائلين: آمنا به.
واحتجوا بأن الله تعالى لم ينزل كتابه إلا ليعمل به، ويفهم معناه. قال الله تعالى: {كتاب أنزلنه إليك مبارك ليدبروا ءايته}. وقال: {[بلسانٍ] عربي','2','194',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (891,'مبين}. والمبين: الظاهر. وقال: {[بكتب] فصلنه على علم}. فوصف جميعه بالتفصيل والتبيين. وقال تعالى: {لتبين للناس ما نزل إليهم}. ولا يجوز أن يبين ما لم يعلم. وإذا جاز أن يعرفه الرسول، [فالرسول]- عليه السلام - لم يكتم من العلم عن الأمة شيئا من فهم كتاب ربه تعالى.
ولأن المفسرين لم يتوقفوا عن شيء في القرآن لم يفسروه، ذهابًا منهم إلى [أنه] لا يعلم تأويله إلا الله. وكان ابن عباس يقول: (أنا ممن يعلم تأويله).
وقال آخرون: (الواو) في قوله تعالى: {والراسخون في العلم} للاستئناف وتم الكلام، والقطع عند قوله [تعالى]: {إلا الله}، [أي: ]} وما يعلم تأويله إلا الله}، واستأنف} والراسخون في العلم يقولون ءامنا [به]}.','2','195',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (892,'[وهذا] قول عائشة [رضي الله عنها] وعروة بن الزبير، ورواه طاووس عن ابن عباس. [واختار] الكسائي والفراء والفضل بن سلمة ومحمد بن جرير: أن الراسخين لا يعلمون تأويله، ولكنهم يؤمنون به. والآية راجعة
-','2','196',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (893,'على [هذا] التأويل- إلى العلم بمدة أجل الدنيا، ووقت القيامة وفناء الدنيا، ووقت طلوع الشمس من مغربها، ونزول عيسى [ابن مريم]- عليه السلام -، وخروج الدجال، ويأجوج ومأجوج، وعلم الروح ونحوها، مما استأثر الله تعالى بعلمه، ولم يطلع عليه [أحدا] من خلقه. وهذا القول أشبه. وقد وقف الخلق عن معرفة أجل الساعة وغيرها. قال الله تعالى: {ويسئلونك كأنك حفى عنها}. [والله تعالى أعلم].

قال الإمام: <span data-type="title" id=toc-158>(مسألة: عموم الكتاب، هل [يخصص] بالخبر الناص الذي [ينقله] الآحاد)</span>؟ إلى آخر المسألة. قال الشيخ: اختلف الناس','2','197',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (894,'في تخصيص عموم الكتاب بخبر الواحد على أربعة مذاهب: فقال بتقديم العموم قوم، وبتقديم الخبر قوم، وبتقابلهما والتوقف إلى دليل آخر قوم. وقال قوم: إن كان العموم مما تطرق [إليه] التخصيص بدليل قاطع، فالخبر أولى، لأنه قد ضعف بالتخصيص وصار مجازًا، وإلا فالعموم أولى، وإليه ذهب عيسى ابن أبان. [واحتجوا [بمسلكين:
أحدهما- ما ذكره الإمام، وعليه أوجه من الكلام: [أحدها]: أن دخول الصورة التي تضمنها الخبر لا يقطع بدخولها تحت العموم، بل يستند إلى','2','198',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (895,'ظهور صيغة العموم (٧٧/ب)، وقد أنكره الواقفية، وزعموا أنه [مجمل]، فكيف ينفع [كون] أصل العموم مقطوعا ب فيما لا يقطع بكونه مرادا بلفظ الكتاب؟
الثاني- أنه لو كان ثابتا قطعا، للزم تكذيب [الراوي] إذا رواه مخصصا، ولا شك في إمكان صدقه. فإن قيل: لو نقل النسخ، فصدقه أيضًا ممكن، ولا يقبل. قلنا: لا جرم لا يعلل بكون الآية مقطوعًا بها، لأن دوام [حكمها] إنما يقطع به، بشرط أن لا يرد ناسخ، فلا يبقى القطع مع وروده، لكن الإجماع منع من نسخ القرآن بخبر الواحد، [ولا مانع من التخصيص].
الثالث- أن براءة الذمة قبل ورود السمع [مقطوع] بها، ثم ترفع بخبر الواحد، [لأنها] مقطوع (١٠٠/ب) [بها]، بشرط أن لا يرد سمع مغير. وماء البحر مقطوع بطهارته، إذا جعل في كوز، لكن بشرط أن لا يخبر عدل','2','199',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (896,'بوقوع نجاسة فيه. كذلك العموم ظاهر في الاستغراق، بشرط أن لا يرد خاص.
الرابع- أن [وجوب] العمل بخبر الواحد مقطوع به بالإجماع، وإنما الاحتمال في صدق الراوي، ولا تكليف علينا في اعتقاد صدقه، فإن سفك الدم وتحليل البضع واجب بقول عدلين، مع أنا لا نقطع بصدقهما.
المسلك الثاني [لهم]: قولهم: إن خبر الواحد، إما أن يكون نسخا، وإما أن يكون بيانا، فإن كان نسخًا، فلاشك في رده، وإن كان بيانًا، فمحال، إذ البيان ما يقترن بالمبين، ويلقيه الشارع إلى [عدد] التواتر، حتى تقوم الحجة به. [قلنا: ] [هو للبيان]، ولا يجب اقترانه، كما قدمناه.
وأما كونه لابد أن يلقيه [لعدد] التواتر، فذلك غير لازم، وإنما يفتقر','2','200',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (897,'إلى القاطع في رفع القاطع. وأما [ما] يظن دخوله تحت اللفظ، فلا يجب ذلك في.
وأما من قدم الخبر، فقد ذكر الإمام حجتهم في ذلك، وهو أنهم استندوا إلى إجماع الصحابة على التقديم، فمنعوا نكاح المرأة على عمتها، وعلى خالتها، بما رواه أبو هريرة من النهي عن ذلك. [ورفعوا] عموم آية','2','201',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (898,'الوصية بقوله: (لا وصية لوارث). وهذا الذي ذكروه من إجماع الصحابة غير مسلم، وقد ردت فاطمة [قول] أبي بكر [رضي الله عنه] في منعها الميراث [حتى] روى عن النبي [صلى الله عليه وسلم] أنه قال: (لا يقسم ورثتي دينارا ولا درهما، ما تركت بعد نفقة نسائي ومؤنة عاملي، فهو صدقة).
وقوله: (إنهم كانوا يعملون بالخبر في تفسير مجملات القرآن). هذا مسلم، لأن المجمل لا يتلقى منه حكم، حتى يكون خبر الواحد يناقضه، بخلاف العموم، فإن الحكم ثابت، تلقيًا منه، قبل أن يروى الخبر. فهذا ضعيف.
وقد [تمسك] هؤلاء [أيضًا]: بأنا إذا أعملنا خبر الواحد في','2','202',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (899,'تخصيص عموم الكتاب، فقد جمعنا بين الدليلين، وإذا أعملنا العموم، عطلنا الخبر، ولا يجوز تعطيل الدليل مع إمكان إعماله بحال. وهذا وهم وزلل، وذلك أنهم إذا أعلموا الخبر، فقد أسقطوا عمل العموم في محل المعارضة، إذ التضاد حاصل، وإعمال المتضادين محال. فلو قدر على إعمال الدليلين، لم يجز التعطيل لأحدهما بوجه.
والذي نختاره (٧٨/أ) في هذه المسألة العمل بخبر الواحد النص، [لا] لأن الإجماع [ينقله] في ذلك، ولكن لحصول غلبة الظن في الحكم الثابت على المكلف، [فإن] الأمر منحصر في أن يكون الحكم على مقتضى العموم، أو على مقتضى الخبر، [فإذا] أنحسم القطع، وبقي الأمر مناطًا بغلبة الظن، فإنا على بصيرة من أن سكون النفس إلى قول العدل أغلب، من سكونها إلى اندراج الصورة المخصصة تحت العموم، فإن غلبة الظن تقوى على','2','203',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (900,'تقدير ضعف الاحتمال المقابل لها، ويقوى بضعف الظاهر. [والظن تطرق] إلى قول العدل، وتعذر القطع، بالنظر إلى احتمال الكذب عمدًا أو سهوًا. [فهذا هو الذي منع من القطع به، [والظن تطرق] إلى العموم باعتبار تطرق التخصيص إليه. وقل من العمومات ما بقي على عمومه. وقل من العدول من اطلع على كذبه سهوًا أو عمدًا].
فإذًا قد ضعف في جانب العدل الاحتمال المعارض للصدق، وقوي في جانب العموم وجدان التخصيص، فلا يخفى أن غلبة الظن بصدق الراوي، تزيد على غلبة الظن، باشتمال العموم على الصورة المعينة، لاسيما إذا كانت تلك الصورة نادرة قليلة، كميراث فاطمة، بالإضافة إلى آية المواريث. وكذلك مسألة [القاتل] (١٠١/أ) في المواريث أيضًا. ذا هو الصواب عندنا. [والله أعلم]. وهذا الكلام يأتي على تفصيل عيسى بن أبان الذي فرق بين أن يكون العموم [تطرق] إلي التخصيص [أم لا].
قال الإمام: (مسألة: اضطراب الناس في تخصيص عموم الكتاب','2','204',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (901,'بالقياس على النحو المتقدم) إلى آخر المسألة. قال الشيخ: اختلف الناس في تخصيص عموم الكتاب بالقياس على خمسة مذاب: فذهب قوم إلى تقديم العموم، وإلى تقديم القياس قوم، وإلى [تعارضهما] وطلب دليل آخر قوم، وفرق قوم بين أن يكون العموم مما تطرق إليه التخصيص، أو كان باقيًا على عمومه، فإن [تطرق] إليه التخصيص، قدم القياس عليه، وإلا قدم','2','205',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (902,'العموم. وهذا قول عيسى بن أبان. وفرق قوم بين جلي القياس وخفيه، فقدموا الجلي، وأخروا الخفي. واحتج القائلون بتقديم العموم بثلاثة أوجه:
أحدها- أن العموم أصل، والقياس فرع، فكيف يقدم الفرع على الأصل؟ وعليه سؤالان:
أحدهما: أن هذا القياس ليس هو فرع العموم الذي عارضه، وإنما هو فرع أصل آخر. والنص قد يخصص العموم بلفظه، وقد يخصصه بمعقول لفظه. فإذا خصصنا بقياس الأرز على البر عموم قوله: {وأحل الله لبيع وحرم الربوأ}، لم يخصص الأصل بفرعه، فإن الأرز فرع حديث البر، لا فرع [آية إحلال] البيع.
الاعتراض الثاني: أنه يلزم منه إلا يخصص عموم القرآن بخبر الواحد، لأن خبر الواحد فرع. وهذا يتوجه على من اعترف بتخصيص العموم بخبر الواحد، وإن منعه مانع أثبتناه بما سبق.
[والوجه الثاني] (٧٨/ب) لهم: أنهم ..............................','2','206',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (903,'[قالوا]: إنما يطلب بالقياس [حكم] ما ليس منطوقا به، [فما هو منطوق به، كيف يطلب حكمه بالقياس؟ والجواب: أنه ليس منطوقا به] نطقا صريحا، بل [يظن] على بعد. فإذا ورد القياس مخصصا، لم يبق عليه ظن من دخول الصورة المعينة تحت العموم. فما خصه من خصه، وهو [معترف] باندراجه تحت [عموم] الكتاب بحال. فإن العموم إذا أريد به الخصوص، كان نطقا بذلك القدر، اقتصارا عليه. والدليل على لك: جواز تخصيص العموم بالعقل. ودليل العقل لا يجوز أن يخالف النص بحال. لأن الأدلة لا تتعارض.
فإن قيل: فما أخرجه العقل، عرف أنه لم يدخل تحت العموم. قلنا: كذلك نقول: ما أخرجه القياس، يبين أنه لم يقصد بالعموم، ولا فرق.
الوجه الثالث [لهم]: قالوا: إن رسول الله صلى الله علي وسلم قال لمعاذ: (بم تحكم؟ قال: بكتاب الله. قال: فإن لم تجد؟ [قال]: بسنة رسول الله','2','207',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (904,'[صلى الله عليه وسلم]. قال: فإن لم تجد؟ قال: اجتهد رأيي). فجعل الاجتهاد مؤخرًا، فكيف يقدم على الكتاب؟ الجواب: أن كونه مذكورًا في الكتاب، ينبني على كونه مرادًا بالعموم، وهو محل التردد، بعد ورود القياس. ثم أنه كان يلزم- لو لزم هذا [الترتيب]- أن يقدم عموم الكتاب على الخبر المتواتر، ولا خلاف في كونه لا يتقدم عليه. وكذلك كان يلزم أن يقدم العموم على القياس المعلوم على الأصل المعلوم الحكم، وهو خلاف الإجماع.
[وللحديث معنى] خلاف هذا، [وهو أن المبدوء] بالنظر فيه الكتاب، لانضباطه وقلة انتشاره، فإذا ظفر بالحكم فيه، افتقر الناظر إلى تمام بحث عن نفي [معارضه] [وثبوته]، ولكن الغالب فقدان التعارض فيه، فجري الترتيب [منه]، بنا على هذا في الوجود. وسيأتي الكلام عليه','2','208',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (905,'بأشبع من هذا، إن شاء الله عز وجل في كتاب (١٠١/ب) الترجيح. واحتج القائلون بتقديم القياس بأمرين:
أحدهما-[أنهم] قالوا: العموم يحتمل المجاز والخصوص، والاستعمال في غير ما وضع له، والقياس [لا] يحتمل شيئًا من ذلك، [ولأنه يخصص] العموم [بالنص] الخاص، مع إمكان كونه مجازًا [ومؤولًا]، [فالقياس] أولى. [والجواب: ] أن ما ذكروه تعدد ألفاظ لنوع من الإيهام، ليبينوا تعدد الاحتمالات المتطرقة إلى العموم، ويقولون: [إن] القياس لا يتطرق إليه إلا احتمال واحد، وهو احتمال المخالفة. وإذا بينا أن العبارات المتعددة ترجع إلى معنى واحد، وهو احتمال إرادة البعض، استوى الأمران.
الوجه الثاني- أن نقول: ليس من ضرورة تعدد الاحتمالات في شيء وقلة الاحتمالات في غيره، أن يكون الذي قلت الاحتمالات فيه مقدما على الآخر،','2','209',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (906,'فإنه [قد] تكثر الاحتمالات، ولكنها ضعيفة، ويتطرق إلى [الآخر] احتمال واحد، ولكنه قوي جدا، [فتكون قوته] تضاهي كثرة الاحتمالات في الشيء الآخر، وربما زادت عليه. وهذا بمثابة تعارض العدول، فقد يكون واحد (٧٩/أ) تزيد عدالته على عدالة جماعة سواء، فلا [التفات إلى] محض العدد دون الوقوف على الاستواء والتفاوت.
على أن التحقيق يقتضي أنه لا يتطرق إلى العموم إلا احتمال واحد، والاحتمالات المتطرقة إلى القياس كثيرة جدا، فإنه يحتمل أن لا يكون الأصل معللا، [ولو كان معللا]، فيحتمل أن يكون [بغير العلة] التي جمع [بها] الجامع، وإن كان بها، [فيحتمل أن يكون أغفل قيدا أو وصفا، وإن لم يغفل]، فيحتمل أن يقع فرق خفي بين الأصل والفرع. فهذه الجهات كلها مختلفة على الحقيقة، فتقتضي تأخرا [عن] العموم الذي [لا] يتطرق إليه إلا احتمال واحد.','2','210',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (907,'الحجة الثانية لهم: أنهم قالوا: العمل بالقياس جمع بين العموم والقياس جميعا، وإذا [دار الأمر] بين [احتمالين]، أحدهما يقتضي إسقاط دليل، والآخر يقتضي إعماله، وجب الأخذ بالأمر الذي يقتضي الإعمال. ووجه الرد [عليه] قد سبق في الكلام على معارضة العموم الخبر.
وأما الذين ذهبوا إلى التوقف، وهو القاضي والإمام، فإنهم قالوا: إذا تطرق الظن إلى كل واحد من العموم والقياس، وبطل القطع في التقديم، فلابد من الوقف. وقد اعترض عليه: بأن الوقف خلال الإجماع، فإن الأمة على [فريقين]: فريق يقدم العموم، وفريق يقدم [القياس]. فمن صار إلى الوقف، فقد خالف الفريقين.
أجاب القاضي: بأنهم لم يصرحوا بإبطال الوقف قطعا، ولم يجمعوا عليه، ولكن كل واحد رأى ترجيحا، والإجماع لا يثبت بمثل [ذلك]، كيف','2','211',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (908,'ومن لا يقطع [ببطلان] [مذهب] مخالفة في ترجيح القياس، كيف يقطع [بخطئه] إن توقف؟ قال الشيخ: هذا الكلام ضعيف، فإن الأمة إذا اختلفت على قولين، فكل واحد لا يقطع بخطأ مخالفه، لكونه في محل الاجتهاد، ولكن إذا صار صائر إلى قول ثالث، قطع [بخطئه]، لأنه خرج عن القولين جميعا. [فنقول للقاضي]: ما هذا التوقف؟ أتوقف من التبس عليه الأمر، أم مصير منك إلى الاستواء وإبطال الترجيح؟ فإن قال: التبس على الراجح من المرجوح، [لم ينسب] إلى خرق إجماع، وإن حكم بالاستواء، وأبطل الترجيح، فقد خلف الفريقين جميعًا، كما [سيأتي] في مسألة: إحداث قول ثالث، في مسألة (اختلفت (١٠٢/أ) الأمة فيها على قولين).
ثم نقول لمن صار إلى الوقف: لا يكفي في الوقف انتفاء القاطع في التقديم، حيث يثبت الاستواء في غلبة الظن، فيجب النظر في هذه المسألة إلى أسباب غلبة الظن، هل هي [مستوية] .................','2','212',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (909,'[في] الجانبين، [أم] [ثبت] [التفاوت] في جانب؟ هذا أصل المسألة.
والمفرق بين جلي القياس وخفيه، والمفرق بين كون العموم تطرق إليه التخصيص، [أو لم] يتطرق، تشوف إلى بيان تفاوت غلبة الظن بالقوة والضعف. وهذا أمر لا يتبين ضبطه باعتبار الآحاد.
والصواب عندي في هذه المسألة: أن يرد المجتهد في ذلك إلى ما غلب على ظنه، فرب عموم ضعيف، والقياس الذي يقابله بالغ (٧٩/ب) قوي، [فالصواب] في [مثل هذه] الصورة الاعتماد على القياس، وقد يكون الأمر على العكس من ذلك، [أن] يكون العموم، مثلا مستندا إلى أدوات الشرط مع التوكيد، والقياس [من] أبواب الأشباه [أو المعاني] الضعيفة. فالحكم في هذه الصورة: أن يقدم [العموم]. وقد يضعف العموم، بأن لا يظهر منه قصد الاستغراق بكثرة المخصص منه، ولوروده مثلا على سبب','2','213',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (910,'خاص، وأن يظهر منه قصد الفصل، كما في قوله: (فيما سقت السماء العشر). فإذا ورد (ليس فيما دون خمسة أوسق من تمر صدقة). كان القياس على هذا في اشتراط النصاب أقوى في الدلالة من التمسك بقوله: (فيما سقت السماء العشر).
وقد تكون بعض [الصور] يقوى في [النفوس] اندراجها تحت العموم، ويضعف الظن في بعضها، ويشك في بعض، ويظن خروج البعض، فليس [كلها] يدخل تحت العموم، بالإضافة إلى غلبة الظن على حد واحد، وإن كانت [النسبة] من جهة اللفظ مستوية. [فلابد أن] ينظر المجتهد إلى آحاد المسائل نظرًا خاصًا، ولا يستمر في ذلك حكم عام أصلا. هذا هو الصحيح عندنا. والله أعلم.
لكن في ترجمة المسألة إطلاق لابد من تفسيره، [وتبيين] محل الخلاف فيه، فإن بعض أنواع القياس [يجب] تقديمه على العموم بلا شك، وهو إذا كان الأصل الذي يستند إليه حكم الفرع مقطوعًا به، [وكانت] نسبة','2','214',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (911,'الفرع إلى [الأصل] نسبة العلم، [كالقياس] الذي يسمى [في] معنى الأصل، والمنصوص [على] علته، مع [مصادفتهما] في [الفروع] من غير فارق قطعًا، فهذا النوع من القياس لا يتصور [الخلاف] في أنه مقدم، فيجب إخراج هذه الصورة عن ترجمة المسألة.
الصورة [الثانية]: أن يكون الأصل إلي استند الفرع إليه ثابتا بأخبار الآحاد، ونسبة الفرع إليه أيضًا نسبة العلم، على النحو [المتقدم]. فهذا يجري مجرى أصله، وهو خبر الواحد.
فإن فرض الكلام مع من يقدم الخبر على العموم، وجب أيضًا أن يقدم هذا القياس. ومن قدم العموم على الخبر، فكذلك يقول في قياسه المعلوم، ويتوقف أيضًا في هذا القياس المتوقف في العموم الذي عارضه خبر الواحد، وإنما يقوى الخلاف فيما إذا كان الأصل مقطوعا به، وكانت نسبة الفرع إليه ظنية. فهذه هي المسألة الأصولية على الحقيقة، [وكلامنا] فيها على حسب','2','215',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (912,'ما سبق، وهذه هي الصورة الثالثة.
الصورة الرابعة: أن يكون الأصل ثبت بخبر الواحد، ونسبة الفرع إليه غير معلومة، فهذه الصورة تضعف عن التي قبلها، من جهة أنه تطرق إليها الظن من جهتين: من جهة ثبوت حكم الأصل، ومن جهة ظن الإلحاق. ولا بعد في تقديم العموم في هذه (١٠٢/ب) الصورة، [فإنه] لما [تزايد] احتمال أن لا يكون الأصل ثابتًا، ضعف القياس.
الصورة الخامسة: أن يكون حكم القياس استند إلى ظاهر عموم من الكتاب، وتكون نسبة الفرع إلى الأصل مظنونة أيضًا. فهذا في محل (٨٠/أ) الاجتهاد. [وأضعف] الصور [التي] لا ينبغي أن تقف [للعموم] على حال، أن يكون حكم الأصل ثبت بظاهر خبر الواحد، وكانت نسبة الفرع إلى [الأصل] مظنونة، فهذا يبعد المصير إلى تقديم القياس في هذه الصورة. بل الصحيح أن ظاهر خبر الواحد لا يقف لعموم الكتاب، [وإن خالف القاضي فيه. كما سيأتي في كتاب الترجيح. فإذا كنا نرى تقديم الكتاب على ظاهر خبر الواحد]، فكيف [بنا في] الفرع المقيس على الأصل الثابت بظاهر خبر الواحد، مع كون الإلحاق مظنونا؟ هذه تقاسيم [صور] معارضة القياس','2','216',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (913,'العموم. وقد أطلق الأصوليون القول، ولم يفصلوا هذا التفصيل، ولابد منه في التحصيل. والله المستعان.
[وقول] الإمام: (ثم يحصل [من] الوقف ما [يحاوله]) إلى نجاز المسألة. [فنقول: الواقف يمنع التمسك بالعموم، ولا يعمل القياس، فإسقاط العموم في محل المعارضة متفق عليه بين المخصص والواقف، والمخصص يدعو إلى العمل بالقياس، والواقف يأباه، وفيه يختلف المسلكان].
قال الإمام: (مسألة: تخصيص الخبر العام المتواتر بالقياس، أو','2','217',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (914,'[بالخبر] الناص الذي [نقله] [الآحاد]، كتخصيص عموم الكتاب بهما) إلى آخر المسألة. قال الشيخ: ذهاب الإمام [إلى] الوقف في تخصيص عموم خبر الواحد بالقياس، كلام ضعيف، وذلك [أنه] قد حكم بأن القياس يساوي عموم الخبر المتواتر، ولا شك أن الوقف [يقتضي] [المساواة]، إذ لو ثبت الترجيح، لامتنع الوقف، [وهو] قد نقل','2','218',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (915,'الإجماع على أنه [لو تعارض] عموم خبر متواتر وعموم خبر واحد، لكان العموم المتواتر [مقدما]، فإذا كان القياس يساوي العموم المتواتر، والعموم المتواتر مقدم على عموم خبر الواحد، وجب أن يقدم القياس على عموم خبر الواحد، إذ قد تحقق مساواته للعموم المتواتر، بدليل امتناع تقدم العموم المتواتر عليه. هذا هو الصحيح، إلا أن يكون الإمام يلتفت إلى كون كل واحد منهما أمارة على تقدير الانفراد، [بدليل] الإجماع، فقد تحقق القطع بنصب الأمارة. ولقد ذهب إلى هذا المذهب [جعل] من المعتزلة، ولكنه يفضي إلى إنكار الترجيح بالكلية، فإنه إنما يطلب الترجيح على تقدير كون كل أمارة
-','2','219',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (916,'لو انفردت- لصح إسناد العمل بالإجماع، ولا ترجيح في محل الإجماع. هذا مستند هذا المذهب. ولكنه يرد عليه:
[أولا]: بإجماع الأمة على الترجيح، وإن كان لا يتصور إلا بعد استجماع كل علامة أسباب الصحة، إذ لا ترجيح بين فاسدين، ولا [بين] فاسد وصحيح.
الثاني: أنه لو كان [النصب [مقطوعا به مطلقا، لاستحال التعارض، إذ يفضي إلى تناقض القواطع.
الثالث: أنه إنما انعقد الإجماع على تقدير كون كل إمارة [منفردة]، أما إذا اجتمعتا وتناقضتا، فلا إجماع (٨٠/ب) على حال، [فيجب] النظر [إلى] الأقوى في النفوس. وقد تحققنا- على مقتضى مذهب الإمام- مساواة القياس لعموم الكتاب [والخبر المتواتر]، وتحققنا تأخر عموم خبر الواحد عن العمومين المتواترين، فكيف يكون فرعه (١٠٣/أ) يخصص به عموم الكتاب؟ هذا عندي لا وجه له. والله أعلم.','2','220',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (917,'قال الإمام: <span data-type="title" id=toc-161>(مسألة: في حمل المطلق على المقيد) </span>إلى قوله (ونحن ننبه الآن على ما تخيلوه أخذًا من النسخ). قال الشيخ: قد تقدم الكلام على معنى كون اللفظ مطلقا ومعنى التقييد. وأما تقسيم النقلة القول، وجعلهم إياه ثلاثة أقسام، فهذه عبارة الأصوليين. [وعندي] أن القسمة غير','2','221',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (918,'منحصرة فيما ذكروه، ويبقى قسم رابع، وهو أن يتحد الموجب، وتختلف صفة الموجب، وهو بمثابة ما إذا قيد الرقبة في كفارة الظهار بالإسلام مثلا، ثم أطلق في جانب الإطعام ذكر المساكين، فهل يتقيد به المسكين، بأن يكون مسلما كالرقبة المعتقة؟ فهذا اتحاد الموجب، واختلاف قبيل الموجب. فلابد','2','222',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (919,'من الكلام على هذه الصورة، فإن الأصوليين أغفلوها.
وأما العبارتان المنقولتان عن [الأصوليين] في ضبط صور الوفاق [والخلاف]، فإحدى العبارتين أضبط من الأخرى، وأدل على المقصود،','2','223',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (920,'فإن المعبر الأول عبر بلفظ التباعد والتقارب. وهذه ألفاظ نسبية. [فرب] قريب باعتبار جهة، بعيدٍ باعتبار أخرة، بخلاف ما عبر به الآخرون من الاتفاق في الموجب والموجب، والاختلاف فيهما، [أو الاتفاق] في أحدهما دون الآخر. ومن هذه الجهة تصير الأقسام أربعة.
وقول الإمام: (إن المسألة تتعلق بالنسخ، [وهي] مناسبة لأحكام','2','224',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (921,'العموم والخصوص). أما مناسبته للعموم، فإن العموم على الحقيقي: هو [الذي] يتناول مسميين فصاعدا، والمطلق لا يتناول إلا مسمى واحدا. ولكن لما كان يجزئ أي واحد، [فكأنه] صار في اللفظ استرسال على النوع بكماله. فمن هذه الجهة حصلت مناسبته للعموم، ويكون التقييد الوارد يمنع اللفظ من الشياع في بعض ما كان يظهر [فيه شياعه]، فقد أزيل اللفظ عن ظاهره.
و[أما] مناسبته لأحكام النسخ، فمن جهة أن الذي فهم من اللفظ [الأول]، أزيل [التكليف] عنه بعد ثبوته. والنسخ: هو رفع الحكم بعد [ثبوته]. على ما سيأتي في حده. فسلك أبو حنيفة بالمسألة مسلك النسخ، نظرًا للثبوت، وسلك الشافعي ومالك بها مسلك التأويل وإزالة الظاهر.','2','225',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (922,'قال الإمام: ([فقالوا] [في] قوله [تعالى] [في كفارة الظهار]: {فتحرير [رقبة] من قبل أن يتماسا}. [يقتضي أجزاء الرقبة المطلقة]) إلى قوله ([وإذا] وجهت عليهم سكتوا لها، مقرين [بالحق]، [وناطقين] بالصدق). قال الشيخ: [المسألة] مترجمة بأن الزيادة','2','226',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (923,'على النص، هل هي نسخ [أو] لا؟ [فقال قائلون: هي نسخ]. وقال آخرون: ليست نسخا. وللمسألة [ثلاث] صور:
إحداها-[أن] لا تتعلق الزيادة [بما] سبق بوجه. وهذا بمثابة (٨١/أ) ما لو وجبت الصلاة ثم وجبت الزكاة، [فلا] ذاهب إلى [أن] هذا','2','227',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (924,'نسخ، وإن كان قد فات نعت الكلية [للصلاة]، فإنه لما كانت الصلاة واجبة [وحدها]، كان من أتى بها، [فقد أتى] بكلية الواجب، [ولما] وجبت الزكاة، خرجت الصلاة عن كونها كلية الواجب.
فقد يظن أنه [لما] تغير هذا الوصف، أن ذلك نسخ، وليس كذلك، فإن النسخ إنما: [هو] رفع حكم شرعي. وكون الصلاة كانت كل الواجب، غنما معناه أنه لم يجب غيرها. وهذا مما لا خلاف فيه.
الصورة الثانية: (١٠٣/ب) وهي غي أقصى البعد من الأولى، أن تتصل الزيادة بالمزيد عليه اتصال اتحاد، يمنع التعدد والانفصال، وهذا بمثابة ما روي (أن الصلاة وجبت ركعتين ركعتين في الحضر والسفر، فأقرت صلاة السفر،','2','228',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (925,'وزيد في صلاة الحضر). [فهذا يجب أن يكون نسخًا، فإن الركعتين كانتا مجزئتين]، وهذا حكم شرعي، فلما وجبت الركعتان الآخريان، خرجت الركعتان الأولتان عن أن تكونا مجزئتين. فالصحيح أن تلك [العبادة] نسخت واستؤنفت عبادة أخرى. وقد يتوهم أن الركعتين قارتان وزيد عليهما ركعتان، وهذا غي صحيح.
والدليل عليه أن الركعتين كانتا مطلوبتين بصفة الاقتصار عليهما، [فقد صار الاقتصار عليهما] وصفا مقصودا، ولذلك أنه لو زاد عليهما متعمدًا','2','229',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (926,'بطلتا، وإذا كان القصر [مقصودًا]، فإذا جاءت الزيادة متصلة، فهذه عبادة أخرى. [فهو بمثابة ما لو أوجب عليه الصلاة جالسًا، ثم [أوجبها] قائما، فإن هذه عبادة] جديدة.
[الصورة] الثالثة: زيادة عشرين جلدة على الثمانين مثلا، وليس [انفصال] هذه كانفصال الزكاة عن الصلاة، ولا اتصالها كاتصال الركعتين بالركعتين، فقال أبو [حنيفة]: هو نسخ. وليس بصحيح، بل هو كالمنفصل، بدليل أنه لو ضرب القاذف تسعين، [لم يبطل الحد] بهذه الزيادة التي ليست مشروعة. فإذا الاقتصار عليها، لم يكن لقصد [القصر] وضعًا، بل لانقياد دليل وجوب الزيادة، فيكون بمثابة إيجاب الزكاة بعد الصلاة.
وإنما العمدة في الباب النظر إلى أن الاقتصار على الأول، هل كان','2','230',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (927,'مقصودًا، [أو صفة] للعبادة، أو كان الاقتصار لعذر دليل الزيادة؟ ففي الصلاة ثبت القصر على الركعتين وصفًا مقصودًا، وفي الثمانين اقتصر عليها، لعدم دليل الزيادة. ومقصود هذا الكلام أنه إذا لم يكن القصر مقصودًا وصفًا، وإنما [انتفى] لعدم دليل الوجوب، فإذا وجدنا دليل الوجوب، أثبتناه، وسواء [كان الدليل قطعيًا أو ظنيًا، وسواء] كان [ذلك] الدليل [الدال] على الحكم الأول أيضًا قطعيًا أو ظنيًا. لأنا إنما أثبتنا [حكمًا] ابتداء، وانتقلنا عن براءة أصلية، [فلا] يشترط في هذا أن يكون الدليل قطعيا. كما لو وجبت الصلاة بالتواتر، ثم جاء خبر [الواحد] يتضمن وجوب وتر أو غيره، فإنا لا نمنع من الإثبات، نظرًا [منا] إلى سبق واجب متواتر، وعلى هذا يثبت التغريب بخبر الواحد، ون كان القرآن لم يتضمن إلا الجلد، ويكون التغريب غير [متعرض] له، فهو مسكوت عنه، وأبو حنيفة يزعم (٨١/ب) أن التغريب منفي بالقرآن، فلا يصح إثباته بخبر الواحد. وهذا غير صحيح، لما قررناه.','2','231',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (928,'فإن قيل: [قد كانت الثمانون] حدا كاملا، وإذا وجب التغريب، خرجت الثمانون عن كونها كلية الواجب، والكلية ثابتة بالنص المتواتر، وإسقاطها بخبر الواحد ممتنع. قلنا: قد كانت الثمانون حدا كاملًا، على معنى [أننا لم نجد] دليلا يقتضي [زيادة عليها، كما مثلناه] في صورة الصلاة والزكاة، فإن أثبت مثبت [أن] الاقتصار على الثمانين حكم مقصود ثابت بنص القرآن، امتنعت الزيادة عليها بخبر الواحد.
فإن قيل: تحقق النسخ يوجب الاقتصار على الثمانين، فإنه لو أوجب [الثمانين]، كان ذلك مانعا من الزيادة. قلنا: لم يثبت منع الزيادة بطريق (١٠٤/أ) المنطوق، بل بالمفهوم، ولا يقول به أصحاب أبي حنيفة ولا نحن. وإن قلنا بالمفهوم، فترك المفهوم غايته أن يكون [كإزالة] ظاهر، فيكون [كتخصيص] العموم، ولا يكون نسخا بحال. هذا لو ثبت المفهوم.','2','232',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (929,'واستقر، ويحتمل أن يكون خبر التغريب ورد متصلا بيانًا، لا [ناسخًا] رافعا.
وقد سلك بعض أصحاب أبي حنيفة مسلكا آخر فقال: التفسيق ورد الشهادة يرتبط [بالحد] [الكامل]، فإذا زيدت العشرون على الثمانين، امتنع أن يكون التفسيق والرد مرتبطا بالثمانين، فيكون تغييرا لحكم شرعي، فيكون نسخا. وقد أجيب عن هذا: بأن الحكمين لم [يرتبطا] بالثمانين، بالنظر على كونها ثمانين، بل باعتبار كونها حدا كاملا، فكأن [الشارع] يقول: من كمل حده، ردت شهادته، وثبت فسقه. فتصرف الشرع في الجلد بالزيادة والنقصان، ليس تصرفًا في ارتباط الفسق ورد الشهادة [بالحد] الكامل. ونظير هذا أن الله تعالى أحل المرأة للأزواج بعد انقضاء العدة، وتصرف الشرع في العدة بالنقل من سنة إلى أربعة أشهر [وعشر]، [ليس] تصرفا في حل النكاح المرتبط بانقضاء العدة.','2','233',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (930,'وقد اختلف الفقهاء في رد شهادة القاذف متى ترد؟ فذهب مالك [رحمه الله] إلى أنها لا ترد حتى يحد. وقال بعض أصحابه والشافعي: ترد بنفس القذف، لأنه بالقذف فسق، إذا لم تقم البينة على ذلك. والذي اعتمده مالك في ذلك أن [سلب] منصب الشهادة في حق القاذف، إنما هو زيادة عذاب معنوي، فهو كالتتمة للعذاب الحسي، إذ الغالب [من القاذف أنه لا يكون صاحب منصب]، فكان العقاب الحسي في باب [زجر] القاذف هو المهم [الأصلي] وسلب المنصب في معنى التتمة. وإذا كان كذلك، فلا تثبت التتمة إلا لعد ثبوت الأصل. ونظير هذا [له] أنه- رحمه الله- يرى [الموالاة] في لطهارة مقصودة مع الذكر، والترتيب [فيها] مستحب. وإنما يستحب الترتيب، إذا كانت العبادة واحدة بإثبات موالاتها، فإذا [إذا] وقعت التفرقة الكثيرة المعفو عنها في حال النسيان، فلا يثبت الترتيب في هذه الحال.','2','234',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (931,'وصورة المسألة: ما إذا نسي مسح رأسه، وغسل رجليه، ثم ذكر ذلك، (٨٢/أ) قال مالك رحمه الله: يمسح رأسه ثم ينظر، فإن كان [ذكر] ذلك قريبًا، بحيث تبقى [البلة]، أعاد غسل رجليه، لتحصل الموالاة، [وإن] بعد الأمر، وجف وضوؤه، فقد فاتت الموالاة، فلا يستحب الترتيب في هذه الصورة، لأنا نستحب الترتيب عندما [تكون] العبادة بموالاتها.
وليس من هذا القبيل ما إذا [أمر] بالصلاة مطلقًا، ثم [إنه] شرط الطهارة، فإنا نرى ذلك نسخًا على الحقيقة، لكن بشرط أن يقطع [بأنه] طلب الصلاة من غير شرط [منضم]، فإذا فعلت الصلاة بغير طهارة- على مقتضى هذا- أجزأت، [وإذا زيد] شرط [الطهارة]، لم تجز الصلاة مع','2','235',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (932,'فقدانها، وقد كانت مجزئة قبل ذلك. وهذا نسخ محقق.
فإن قيل: فالطهارة كانت مسكوتًا عنها، [ثم] استؤنف إيجابها، [وقد] قلتم: إن استئناف الأحكام، وإن زالت به براءة الذمة، لا يكون نسخًا. قلنا: لم يثبت النسخ [بالإضافة إلى وجوب الطهارة التي لم تكن واجبة، لكن هذا نسخ]، من جهة سقوط الأجزاء الذي فهم من النص الأول.
فإن قيل: يلزمكم المصير إلى أن اشتراط الطهارة (١٠٤/ب) في الطواف نسخ، فإن الله تعالى قال: {[وليطوفوا] بالبيت العتيق}. ولم يشترط طهارة، وقد قضيتم [بأنها] شرط، ومنعتم الأجزاء بقوله - عليه السلام -: (الطواف بالبيت صلاة). وأبو حنيفة قال: هذا يوجب الوضوء، منضمًا إلى الطواف دون الاشتراط. قلنا: لو ثبت بالقرآن أجزاء الطواف على كل حال، بطهارة','2','236',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (933,'وبغير طهارة، لم يبطل طواف المحدث بخبر الواحد، ولكنه ليس فيه إلا مطلق يقبل التقييد. [وسنتكلم] عليه [بعد هذا].
فأما مصير أبي حنيفة إلى أنه يعصي بالطواف محدثًا، ويصح له الطواف. فهذا قد تكلمنا [عليه] وقلنا: إن النهي عن [الوصف] يرجع إلى الأصل، فلا [نعيده]. وكذلك يقال في سائر المطلقات والعمومات، إن تحقق الشياع [والشمول] قطعا، امتنع التخصيص والتقييد بأخبار الآحاد والمقاييس المظنونة.
فإن قيل: فقد قال الله تعالى في آية الوضوء: {وأرجلكم إلى الكعبين}. فأوجب الله تعالى بنص القرآن غسل الرجلين، فمن جوز المسح على الخفين، فقد رفع حكمًا ثبت بالقرآن وبأخبار الآحاد، وذلك ممتنع، [إذ] لا يصح نسخ القرآن بأخبار الآحاد، وهذا من أعظم ما تمسك به الخوارج في إبطال المسح على الخفين.','2','237',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (934,'وقد نقل عن مالك قريبا من هذا قال: (إني لأقولن اليوم [مقالة] ما قلتها قط في ملأ من الناس، لا يمسح على الخفين، وقد [أقام] رسول الله صلى الله عليه وسلم بالمدينة وأبو بكر [وعمر] وعثمان، فلم ينقل عنهم المسح، وإنما هي أخبار، وكتاب [الله] أحق أن يتبع). قال الراوي عنه: وكأنه [كره] المسح على الخفين مطلقا.
والصحيح من مذهبه [ومذهب] غيره من العلماء، جواز المسح على الخفين. وليس ذلك بنسخ للقرآن، فإن القرآن ليس بنص في وجوب غسل الرجلين على كل مصل، [بل] يحتمل أن يكون ذلك في حق من [لم] يلبس خفا. [وإذا] (٨٢/ب) كان ذلك ممكنا، [ورويت] الأخبار','2','238',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (935,'الصحاح فيه، وجب المصير إليها. على أنه لا يثبت دعوى العموم في خطاب المواجهة من نفس اللغة، [إذا ذلك] يصح عند [كون] المخاطب جمعا، قليلا أو كثيرًا، فلا صيغة يتمسك بعمومها، وإنما يعتمد في [فهم] التعميم على قرائن الأحوال. [فإذا] لم يكن للفظ ظهور في العموم، كيف يدعي النصوصية فيه؟ ومن هذا القبيل قوله تعالى: {[واستشهدوا] شهدين من رجالكم فإن لم يكونا رجلين فرجل وامرأتان}. وقد ذهب أبو حنيفة إلى رد الشاهد واليمين، بناء منه على أنه إثباته يناقض حكم القرآن. وليس كذلك، ولم يتضمن القرآن [إلا] ذكر بعض الحجج، ولا يلزم من ذكر بعض الحجج، استفاء [جميعها]، ولذلك أنه لم يذكر في هذا الموضع الحكم [بالإقرار].
وإن تمسكوا بقوله: {فإن لم يكونا رجلين}. من حيث مفهوم الشرط،','2','239',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (936,'فالقوم لا يقولون به، وكذلك نحن أيضًا على ما سيأتي. وإن قلنا به، فليس بنص، وقد سبق الجواب عن مثله، فلا [نعيده]. وبالله التوفيق.
وقد سلك من ذهب إلى [أن] تقييد المطلق من باب النسخ مسلكا آخر، فقال: إذا اقتضى [الكتاب] حكما، [وأورد] مريد أن يغير حكمه بإبداء ما لا يحتمله [اللفظ]، لم يكن ما جاء به واقعا في أواب التأويل، إذ التأويل: إبداء ما يحتمله اللفظ. [وإذا أبدى ما لا يحتمله اللفظ]، وتصور معه استقراء حكم اللفظ، لما بينهما من مناقضة، فلا محط لذلك، ولا محمل سوى النسخ.
قالوا: وذكر (١٠٥/أ) [الرقبة] لا يشعر بالإيمان، [ولا] يحتمله على حال. والذي يحقق ذلك، ما تقدم في مسألة المصدر، وكونه لا يشعر بالأعداد، وقد بينا ذلك أوضح بيان. وكذلك الموصوف [بالصفة] لا','2','240',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (937,'يشعر بها. وإذا كان كذلك، فالذي يبدي [الإيمان] شرطًا، مع كون اللفظ لا يحتمله أصلًا، فقد غير مدلول اللفظ الأول، بإبداء ما لا يحتمله. وهذا [هوي] التعطيل دون التأويل. [والظواهر والمقاييس في تأويلات المتواترات دون تعطيلها].
وهذا سؤال مشكل، وهو عمدة القوم. والجواب هو: أنا نسلم أن التأويل: إبداء ما يحتمله اللفظ، ونسلم أيضًا أن الموصوف لا يشعر بالصفة، ونسلم أن لفظ الرقبة لا يحتمل الدلالة على المؤمنة بوجه، ونسلم [أيضًا] أن من أبدى ما لا يحتمله اللفظ، وتغير به حكم اللفظ، أنه يقع في أبواب التعطيل جون التأويل. ولكنا نقول: هل قوله: {فتحرير رقبة] نص في أجزاء كل رقبة، أو يحتمل أن يشيع في البعض دون البعض؟ فإن قيل: إنه نص في أجزاء كل رقبة، قلنا: ذلك باطل من وجهين:
أحدهما- أن شياع النكرة في الجنس من أبواب الظواهر، لا من أبواب النصوص.
الثاني- أن القوم قد نقضوا هذا فيما [إذا] اتحد الموجب والموجب، وهي مسألة إجماع.
وصورتها: أن نقول في كفارة القتل: فتحرير رقبة، فإذا قطع بإجزاء كل','2','241',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (938,'رقبة، لزم إذا قال في موضع آخر: (مؤمنة) أن يكون نسخا، وأهل الإجماع على خلاف ذلك، [فإن] هذا لا يكون من أبواب النسخ (٨٣/أ) على حال.
وهذه [المسألة] واضحة جدا على [مذهب من] رأى بين الخاص والعام [مقابلة] الناسخ والمنسوخ [من الفقهاء] [والأصوليين]. وقد خالف في ذلك [القاضي أبو بكر، وقال] بالتعارض.
وقد [نقل] من مذهب أبي حنيفة أنه قدم العام على الخاص، [وأخذ هذا من حكمه] بوجوب الزكاة [في المال] دون اعتبار النصاب بقوله [- عليه السلام -]: (فيما سقت السماء العشر). وترك قوله (ليس فيما دون خمسة','2','242',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (939,'أوسق من تمر صدقة). ولكن القاضي مع مصيره إلى المعارضة بين [الخاص والعام] نقل الإجماع عن العلماء على أن المطلق محمول على المقيد. وفي ذلك أبلغ دليل على أن المطلق لا يشيع في الجنس على جهة القطع، بل على [وجه] [ظاهر]، فإزالة [الظاهر] ليست بدعا، ولا يقع في [أقسام] النسخ بحال.
[فلنجب] عن السؤال، ثم [ننعطف] على كلام القاضي، [فنقول]: إنما صح إبداء الوصف، وهو الإيمان، لا على [أن] لفظ الرقبة يحتمل الدلالة عليه، فإن الموصوف لا يشعر بالصفة، ولا يحتمل الدلالة عليها بحال، [وإنما] قول رقبة- من جهة استرساله على جميع الجنس-[يتنزل] منزلة العموم. وإذا كان كذلك، فاللفظ العام [يصح] أن يراد','2','243',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (940,'به البعض، ويفتقر من يرى [البعض] عند إرادة التبيين للمخاطب أن يبين له [مراده]، فيكون ذكر الإيمان بعد ذلك مبينا للبعض المراد، لا أنه محتمل اللفظ في الدلالة والإشعار. وهذا يتنزل منزلة ما إذا قال: {فاقتلوا المشركين}. ثم خص بالحربيين، [فإن ذلك يعد تخصيصا لا نسخا. [ففرق] بين أن تذكر الصفة لكونها] مدلول اللفظ، أو تذكر مبينة لما يحتمل اللفظ [الاقتصار] عليه. وهذا كلام حسن بالغ.
وأما ما ذكره القاضي من ادعاء الإجماع على [أن] المطلق محمول على المقيد عند اتحاد الواقعة، فهو صحيح. ولكنه مشكل على القاضي، (١٠٥/ب) فإنه يذهب إلى أن الخاص والعام إذا كانا في واقعة واحدة، فهما [متعارضان].
مثاله: قوله [- عليه السلام -]: (فيما سقت السماء العشر). فإنه يعم ما دون النصاب، مع قوله: (ليس فيما دون خمسة أوسق من حب صدقة). فإنه','2','244',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (941,'خاص يتضمن اشتراط النصاب. [وقوله تعالى]: {والسارق والسارقة فاقطعوا أيديهما}. [مع قوله: ] (لا قطع في [تمر] ولا كثر). قال القاضي: يتعارضان، فإنه يجوز أن يكون الخاص ورد سابقا، ثم ورد العام بعده [مستغرقا]، فيكون ناسخا، ويجوز أن يكون المراد بالعموم ما دون المحل الخاص المنصوص عليه. وإذا احتمل النسخ والبيان جميعا، [لم] [يحكم] بحمله [على] البيان دون النسخ، [ولم] يضرب عن احتمال أن يكون الخاص [مقدما] منسوخا.
[وهذا] هو الظاهر من مذهب أبي حنيفة. ولكن لا يصح هذا فيما','2','245',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (942,'إذا ثبت تقدم العام وتأخر الخاص فإنه إما أن يكون [بيانا، وإما أن يكون] [نسخا]، وكلا الوجهين يقتضي إعماله. نعم، يقال هذا فيما [إذا] [التبس التاريخ]، أو ثبت أن العموم متأخر. ونحن نختار الحمل على البيان دون النسخ، وذلك أن في المصير إلى التعارض إثبات [الحكم، ثم الحكم برفعه بطريق النسخ، وهذا إثبات ورفع بالتوهم. والبيان أغلب في] الشريعة [من] النسخ، [ولذلك] أنه لم يشتغل العلماء بمعرفة [طلب] التواريخ في العام (٨٣/ب) والخاص، بل نزلوا الأمر على البيان.
وهذا هو الأغلب من عادة الشريعة، إذ النسخ في معنى النادر، والبيان غالب معتاد، [بل] لا تكاد العمومات المتعلقة بالأحكام تنفك عن التخصيص بشروط في السبب والحل والشرط، كآية السرقة وغيرها. وإذا كان البيان أغلب في عادة الشرع، وجب التنزيل عليه.
قال القاضي: إذا أمكن الأمران جميعا، فلم يتحكم [بتعيين] أحدهما','2','246',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (943,'من غير دليل [عقلي] [أو سمعي]؟ قال: ولم يبق معكم سوى ادعاء الكثرة. وتارة [يقول: ] مجرد الكثرة لا [يقتضي] ترجيحًا. ولو اختلطت [أخته] بعشرة نسوة، فالأكثر حلال، ولا يجوز [ترجيح] واحدة، بناء على الكثرة، وكذلك ميتة [بعشر] ذكيات، وإناء [نجس بعشر أوان] طاهرة، فإنه يمتنع [مد اليد]، بناء على الغالب، والنسخ ممكن، والبيان كذلك. هذا [كلامه]. والصحيح عندنا خلافه.
[أما] قوله: إنه لا يجوز اعتماد الأغلب إلا بدليل [زائد]، واحتجاجه بما ذكره، فعنه جوابان:
أحدهما- أن نقول: ما يغلب على الظن أنه [الحاصل]، لزم التمسك به، إلا ما منعت الأدلة منه، والأصل إتباع غلبة الظن مطلقا، لأنه إذا تعذر حصول العلم بالحكم، ولم يبق سوى غلبة الظن مطلقا، لأنه إذا تعذر حصول العلم بالحكم، ولم يبق سوى غلبة الظن في موضع لا يشترط العلم فيه،','2','247',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (944,'فلو لم نقض بغلبة الظن، لكنا [تاركين] لحكم الله [تعالى] علينا على حسب ما ظننا، ونكون قد أقدمنا على المخالفة ظنًا، وذلك ممتنع، إلا أن يثبت تعبُّدٌ في الشريعة، كمنع القضاء بشهادة الواحد، وإن كان يغلب قوله على الظن، [ولكن هذه أمور نادرة، وأكثر الأحكام خرجت على أسباب مغلبة على الظن]، فلا وجه [لترك] المغلب على الظن في محل لا يطلب العلم فيه.
[الجواب] الثاني- أنا نعلم من تصرف الصحابة رضوان الله عليهم أنهم في العمومات [التي] [ثبت] [الانتقال] عنها لأدلة خاصة، أنهم ما صاروا على أنها منسوخة، فإن ذلك لو تطرق، للزم أن يكون القرآن من أوله إلى آخره منسوخًا، (١٠٦/أ) ولكن نزل الأمر على البيان، ولذلك لم يشتغلوا بطلب التواريخ. وهذا أمر معلوم. ويتأيد هذا بما في تخصيص العموم بخبر الواحد. [وهذا أيضًا مما يضعف قول القاضي، فإنه إذا توقف في تخصيص العموم بخبر الواحد]- والنص المتواتر مقدم على خبر الواحد قطعا- وإذا','2','248',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (945,'كان نص خبر الواحد عنده [يوقف] العموم ويقابله، وجب أن يتقدم على العموم [ما يسقط] نص خبر الواحد. وهذا واضح، وبنحو هذا رددنا على [الإمام في] العموم في قوله: إن القياس على أصل ثبت بخبر الواحد يعارض العموم. وقد تقدم هذا.
قال الإمام: [فمما يلزمهم [من ذلك] اشتراطهم سلامة الرقبة عن كثير من العيوب]) إلى قوله (فهذه أحد الوجوه الثلاثة). قال الشيخ: هذا الكلام واضح، والمناقضة كما ذكرها الإمام متوجهة. والذي يذكرونه إنما هو تعليل للنقض، واعتراف (٨٤/أ) بأن التقييد لا يغير النص، وهو المقصود. والذي تخيله [القوم أن] السلامة من كل عيب، قليلا كان أو كثيرا، متعذر،','2','249',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (946,'[فعلم] أن [بعض] العيوب [مغتفر، لتعذر] [الانكفاف] عنه في العرف، [فوجب] أن يكون [المعتبر] بذهاب ما تجب فيه [الدية من الحد، إذ] وجوب الدية في [الذاهب] تدل على أنه [يتنزل] منزلة الرقبة بكمالها. [وهذا] كلام ضعيف، واسم الرقبة مطلق، وإن كان الذاهب [تجب] فيه من الحر كل الدية.
[وأما] ما وجهه عليهم في تقييد [ذوي] القربى بالفقر والاستحقاق، فللقوم في هذا أصل ننبه عليه، وذلك أن المعنى المفهوم من','2','250',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (947,'العموم: إما أن يكون [مساوقا] للفهم عند استماع العموم بحيث لا يتراخى عنه بحال، وهذا بمثابة قوله صلى الله عليه وسلم: (لا يقضي القاضي وهو غضبان). وما يجري مجراه. فالمعنى السابق إلى [الفهم]، هو [المحكم] في التخصيص والإلحاق. لا جرم يجوز القضاء مع الغضب اليسير، ويمنع مع الجوع المفرط، والألم المبرح، ولا ينظر في هذا على كثرة المخرج، ولا إلى قلته، إذ اللفظ وإن كان [في] وضعه يصلح للتعميم، إلا أن هذا المعنى المساوق للفهم، أوجب التقييد، حتى كأنه قال: لا يقضي القاضي [الدهش] الذي لا يتأتى منه استيعاب الحجج. ولو صرح بهذا، لم يتناول من غضب يسيرا، لأن اللفظ [لا] يدل على المعنى لعينه، وإنما يدل باعتبار غرض مطلقه، فإذا كانت هذه القرينة مقترنة باللفظ، منعت التمسك بالعموم قطعا، فلا دلالة لهذا على','2','251',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (948,'الاستغراق، حتى يفتقر إلى دليل مخصص أو رافع، [لعدم] استقرار الحكم على التعميم.
الصور الثانية: أن يكون المعنى مستنبطا بالتأمل من مورد العموم، فهذا لا يخلو: إما أن يساوق العموم في جميع صوره، وإما أن يكون استنباطه يقتضي إخراج الكثير، أو يقتضي إخراج القليل، فإن ساوق، فصحيح، وإن أخرج الكثير، فباطل، وإن أخرج القليل، فمختلف فيه، ذهب أبو حنيفة إلى رده، وذهب ذاهبون إلى قبوله.
وغموض المسألة من جهة أن المعنى المستنبط من العموم أو الظاهر، [لا يتصور] أن يعارض بجهة طرده، إذ من المستحيل أن يستنبط من محل التحريم معنى يقتضي الحل، فلا يبقى إلا أن يستنبط معنى يقتضي [التحريم]، ويصادف ذلك المعنى في كثير من صور العموم، ويختلف عن بعضها، (١٠٦/ب) فيكون العموم موجودا، والمعنى مفقودا. فإما أن يقال: إن نفي المعنى لا دالة له بحال، وإما أن يقال يدل [على] العكس دلالة ضعيفة','2','252',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (949,'تضاهي مزية ترجيحه، فلم يعطل عمل العموم، بناء على أن المعنى لم يوجد في المحل المخصوص؟ وهذا غامض، والقول بمنع التخصيص قوي واضح. وتمام التقرير [فيه] يأتي في كتاب (التأويل).
والمقصود منه ههنا أن أبا حنيفة زعم أن [سد الخلة] فيما يتعلق [بمال] بيت المال، أمر معلوم من الشرع بنصوص [متفرقة]، وقرائن متعددة. [و] يتنزل هذا [المفهوم] (٨٤/ب) منزلة فهم قاعدة القضاء [المرتبة] على فصل الخصومات بفهم الحجج. وإذا كان [هذا] المعنى مساوقا للفهم، صار قيدا مقترنا، ويكون التقدير: (ولذي القربى المحتاجين).','2','253',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (950,'كما كان التقدير في قوله: (لا يقضي القاضي وهو غضبان). أي وهو متحير العقل، لا يهتدي لفهم الحجج.
والقاعدة الأصولية [لا] نزاع فيها، وهي أنه إذا كان المعنى [مساوقًا للفهم]، غير متأخر عنه، أنه المحكم في التخصيص والتعميم. والنظر في آحاد الأمثلة من [أي] قبيل، هو محض علم الفقه. [ولكنا] أردنا التنبيه على أن هذا ليس بقضاء على مقتضى أصول [القوم].
قال الإمام: ([الوجه الثاني]) - أن نقول: أتدعون أن ذكر الرقبة على','2','254',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (951,'الإطلاق نص في إجراء كل رقبة؟ ) [إلى قوله] (هذا هو الوجه الثاني من الكلام). قال الشيخ: قد قدمت الكلام على هذا الفصل بعينه بكلام شاف. [ولكن] يبقى في هذا الموضع شيء، [وهو] النظر إلى سياق الكلام في ذكر الرقبة في كفارة اليمين، فإنه إنما ذكر أصل الخصال، ولم يتعرض لتفصيل شيء منها على حال. وقد أطبقنا على أنه لا يقع الاكتفاء بما يسمى إطعاما، بل لابد من تقدير مخصوص، وكذلك مطلق ما يسمى كسوة، لا يكتفى [به]، فكما صح التقييد في الخصلتين، صح في الثالثة. وقد منع','2','255',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (952,'مانعون أن يكون للرقبة ظهور في أجزاء كل رقبة، وقال: لم [تسق] لغرض التعميم، وإنما سيقت لغرض التأصيل.
وهذا الكلام يناسب قول من يقول: إذا ظهر للعموم مقصد سوى التعميم، لم يتمسك بظاهره في العموم، في مثل قوله: (فيما سقت السماء العشر). فإن الإمام قال: (إذا تمسك [الحنفي] به في كل ما تسقيه السماء، لم يكن متمسكا بظاهر). وهذا مثله. وسيأتي الكلام عليه في كتاب (الترجيح). إن شاء الله [تعالى].
قال الإمام: ([والوجه] الثالث - أن الرقبة المطلقة تعم كل رقبة، [فحملها على خصوص من الرقاب [عين] التخصيص]) إلى قوله (ولا يطيب التصرف في [تفاصيل] ذلك إلا [في كتاب] التأويل). (٨٥/أ)','2','256',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (953,'قال الشيخ: إذا تقرر أن المطلق [يشيع] في الجنس على البدل شياعا ظاهر، وإنما يسقط الظاهر [بمعارض راجح، والمعارض] [المناقض]. فكل [أمرين] أمكن اجتماعهما، فلا معارضة بينهما على الحقيقة. [وإذا كان] كذلك، فليس [يتناقض] [اشتراط] الإيمان في القتل، وجواز الكافرة في [الظهار]، فإذا أمكن الجمع، وإقرار كل واحد على بابه، فبأي وجه [يزال] ظهور الظاهر في الرقبة [المطلقة]؟ هذا باطل، لا خفاء ببطلانه. وإذا [تقرر] ذلك، [فإن] وجد في الشرع دليل آخر يقتضي إزالة ظاهر الإطلاق، [وترجح] عليه، صح ذلك. ولا فرق بين كونه قياسا، أو خبرا، أو غيره، وسلك به [مسلك] المخصصات. هذا هو الحق، والمسلك الواضح. والله الموفق.','2','257',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (954,'وبقي علينا الكلام مع القاضي في كونه صار إلى أن العام والخاص يتعارضان، (١٠٧/أ) مع [نقله] الإجماع على أن المطلق محمول على المقيد عند اتحاد الموجب والموجب، وهو لم يبين الفرق. ولكن الذي [يمكن] أن يعتذر به عن الفرق بين البابين، أن الخاص [يناقض] [العام] في جهة مدلوله في محل التعارض، فإن العام ينفي الحكم مثلا في محل النزاع، والخاص [يثبته]، فعقل التعارض من هذه الجهة.
وأما المطلق، فقد [بينا] أنه لا [يتعرض] [للصفات] بنفي ولا إثبات. والإيمان [مسكوت] عنه، وإذا كان كذلك، وجب أن يثبت، إذا دل دليل عليه، لكونه لم يتعرض العام لنفيه، [ولا] دليل على النفي، وقد وجد دليل الإثبات، فلا وجه لتعطيله. وهذا كلام صحيح، [وهو] يقتضي أن [تثبت] الصفة بأي طريق دل على إثباتها، سواء كان ذلك','2','258',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (955,'[الطريق] قاطعًا أو ظنيًا.
[وأما] قول الإمام: (إن التخصيص يكون على وجهين: أحدهما: [قصره] على بعض المسميات، من غير فرض [تعيين] ما وقع القصر عليه','2','259',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (956,'[بصفة]، كحمل آية الفقراء على ثلاثة منهم). وهذا صحيح، إذ الصفة المعتبرة الفقر، وهو موجود في الجميع، وقد بطل الاستيعاب، لتعذره وخروجه عن الإمكان. [والاقتصار] على عدد دون عدد، تحكم من غير برهان، فلم يبق إلا أقل الجميع، [إذ لا يمكن التنقيص منه. وهذه إحدى الفوائد','2','260',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (957,'في معرفة أقل الجمع]. فهو على الحقيقة تقييد بعدد.
[والثانية]: تقييد بالصفات، والمطلق لا يتعرض لواحد [منها]. وقال أبو حنيفة: يقتضي منع التخصيص بأخبار الآحاد [على الإطلاق]، سواء كان بعدد أو صفات، إذ اللفظ المطلق، لا يشعر بعدد ولا صفة.
قال الإمام: (فإن قيل: فما معتمدكم في [اشتراط الإيمان] [في الرقبة في كفارة الظهار])؟ إلى آخر المسألة. قال الشيخ: [أما] الكلام على [اشتراط الإيمان] في كفارة الظهار، فخارج عن فن الأصول، بعد أن أوضحنا إمكان إجراء كل آية على حدتها، فيرجع الاشتراط إلى الفقه، ونظر المجتهدين في الفروع، [بناء على طريقين]:','2','261',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (958,'أحدهما - أنهم يتلقون ذلك من الخبر، فإن في الحديث: (إن الذي لطم جاريته في وجهها وعظم ذلك عليه (٨٥/ب) رسول الله صلى الله عليه وسلم، فقال له الرجل: إن علي رقبة، أفأعتقها؟ فدعاها [رسول] الله صلى الله عليه وسلم فسألها، [فاعترفت] بالله، وصدقت رسوله). [هذا] معنى الخبر دون لفظه، فقال - عليه السلام -: (اعتقها فإنها مؤمنة). فرتب الأمر بعتقها على إيمانهم [) بالفاء)، التي] هي [للسبب]. والرجل قال: (علي رقبة) مطلقة، [فأشعر] ذلك بأن الرقبة إذا وجب إعتاقها اشترط الإيمان فيها.
والطريق الثاني - القياس على كفارة القتل، والجامع أنها رقبة طلب عتقها','2','262',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (959,'على طريق التكفير الماحي، فوجب أن يكون الإيمان مطلوبا، [تحليلا] للعبادة، وإلحاقا [لها] بكفارة قتل النفس، هذا طريق الفقهاء في اشتراط الإيمان.
وأما الصورة الرابعة التي أثبتناها، وهي ما إذا اتحد الموجب واختلف [صنف] الموجب، ومثلناها باشتراط الإيمان في كفارة الظهار تقديرا، [وإطلاق] ذكر المساكين [في] كفارة اليمين بالله، أن لو ثبت النص على إيمان الرقبة، هل [كان يتقيد] به لفظ المساكين في الكفارة، حتى يشترط فيهم الإيمان؟ فنقول: ذلك يصح، ويمكن أن يسلك فيه مسلك القياس، كما سلكناه في مسألة تقييد الرقبة في الظهار على الرقبة في القتل على ذلك المنهاج، من غير (١٠٧/ب) فرق بحال. هذا تمام الكلام [في] المطلق.','2','263',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (960,'ومما يتعلق [به الكلام] <span data-type="title" id=toc-163>[القول] في دخول الشرط على الكلام</span>، [فإن] التقييد [اشتراط]. [وإذا استوفينا الكلام على التقييد، فلابد من الكلام على الشرط] فنقول: الشرط عبارة عما لا يوجد المشروط إلا عند وجوده، ولكن لا يلزم من وجوده وجوده، [بدلالة] في جانب نفيه، فإنه إذا انتفى، انتفى المشروط، [ولا] دلالة في جانب الوجود. [والشرط] [عقلي] وشرعي ولغوي.
فالعقلي: كالحياة للعلم، والعلم للإرادة، وكذلك الحياة شرط في السمع والبصر والكلام، ولا يلزم [من وجود الحياة]، وجود هذه الصفات، ويلزم من [عدمها] عدمها.','2','264',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (961,'والشرط الشرعي: كالطهارة للصلاة، وستر العورة وغير ذلك، فلا تصح صلاح المحدث، ولا يلزم إذا كان متطهرًا، أن تصح صلاته ولابد.
والشرط اللغوي: كقولك: إذا دخلت الدار فأنت طالق، فإنها لا تطلق إذا لم تدخل بمقتضى الشرط، وإن أمكن أن تطلق طلاقا آخر غير الطلاق المعلق على الدخول، ويكون هذا الاشتراط قد منع الطلاق من أن يكون مطلقا. [ولا] [نقول: ] إنه نطق بالطلاق مطلقًا، ثم خصصه بالدخول، فإنه لو كان كذلك، لم يقدر على التخصيص [بعد] الطلاق، بل إنما يكون بالطلاق مضافا إلى الدخول. وكذلك النعت [يقتضي] تخصيص لفظ المنعوت، الذي لولا النعت، لكان مسترسلا، [فإن] قال: ([اقتلوا] المشركين الحربيين)، كان الأمر بالقتل مختصا بسبب النعت، ولو لم يقتل (الحربيين)، لاقتضى اللفظ قتل كل مشرك، فكذلك لو قال: أنت طالق، لوقع الطلاق ناجزا، إذ لا اختصاص له بزمان أو مكان. (٨٦/أ) فإذا قال: أنت طالق إن دخلت الدار، فإذا لم يكن دخول، [فلا دلالة] للفظ على غير حالة [الدخول','2','265',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (962,'بحال]. [فهكذا] ينبغي أن تفهم حقيقة الشرط.
فإن قيل: [فإذا كان] الشرط لا دلالة له على [جانب الإثبات] بحال، وإنما يدل على أنها إذا لم تدخل الدار [لم تطلق]، فينبغي إذا دخلت أن [لا تطلق]، إذ اللفظ لا دلالة له على الإثبات بحال. [قلنا]: لم تطلق إذا [دخلت] الدار، بناء على كون دخول الدار شرطا، وأن الشرط اقتضى الطلاق، هذا باطل لاشك فيه، وإنما طلقت عند دخول الدار بوجود السبب، وهو تطليق الزوج، فإن دخول الدار لا يقتضي تطليقها على حال، وإنما هو شرط في وجود السبب، فإذا لم يكن دخول، لو يوجد السبب، [وإذا] وجد الدخول، وجد السبب، [فاستند] التطليق إلى جريان سببه عند وجود شرطه.
ولقد رتب على الفرق بين السبب والشرط أحكام في الفقه:
منها: صحة تقديم الزكاة قبل الحول، مع امتناع التقديم قبل النصاب.
ومنها: صحة التفكير [قبل الحنث في اليمين بالله]، مع امتناع التفكير قبل اليمين.
[والسند] في ذلك قوله - عليه السلام -: (من حلف على يمين فرأى غيرها','2','266',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (963,'خيرا منها، فليفكر عن يمينه، وليأت الذي هو خير). فاليمين سبب، والخنث شرط. وقد صح تقديم الكفارة، بناء على جريان السبب، [وإن] تأخر الشرط. وكذلك جوز العلماء عفو المجروح عن قاتله، بناء على [جريان] السبب، [وإن] تأخر الشرط، وهو الزهوق.
ولمعرفة الفرق بين السبب والشرط طرق، نذكرها إن شاء (١٠٨/أ) الله في كتاب القياس. ولقد بالغ [مالك] [رحمه الله] في الغوص على هذه القاعدة حتى قال: إذا أسقط الشريك في العقار حقه [من] الشفعة، لمن طلب [ذلك] قبل الشراء، لم يلزمه هذا الإسقاط، وكان له القيام بحقه.
وقال: إذا جعل الرجل أمر امرأة يتزوجها بيد الزوجة التي [له] في الطلاق [أو الإبقاء]، فاستأذنها في التزويج، فأذنت له، فلما تزوج، أرادت','2','267',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (964,'أن تطلق عليه. قال رحمه الله: ليس لها ذلك. فألزمها الإسقاط وإن كان التزويج لم يحصل، ولم يلزم الشريك الإسقاط، بناء على [أن] [الشرط] لم يحصل، [وكلاهما] قد أسقط حقه قبل تمكنه منه. ولهذا ذهب عبد الملك إلى [أنه] لا فرق بين المسألتين، [وألزم] الشريك الإسقاط.
ولكن مالكا رحمه الله رأى أن الزوجة أسقطت بعد جريان السبب، والشريك أسقط قبل جريانه، [وهو] غامض، ولكن فهمه يترتب على معرفة الفرق بين [السبب والشرط]. ففي [السبب] مناسبة للحكم.
[وتقريره] ههنا أن الذي يناسب أن تطلق المرأة ضرتها، تمكين الزوج لها من ذلك، إذ العصمة بيده، وأما التزويج [عليها]، فلا يصلح أن يكون سببًا لطلاق نفسها، ولا لطلاق ضرتها. وإذا كان السبب هو تمليك الزوج، فهو حاصل قبل [التزويج]، ولم يتأخر إلا الشرط، فصح الإسقاط، بناء على جريان السبب، كما صح في (٨٦/ب) تعجيل الكفارة قبل الحنث. [والسبب]','2','268',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (965,'في الاستشفاع إنما هو لحوق الضرر، والشركة شرط، فإن الشركة لا تناسب إزالة ملك الشريك، وإنما الذي يناسب دخول المشتري الجديد عليه، لما يلزمه من المضايقة في الربع، فكان إسقاط الشريك [للمنفعة] قبل شراء المشتري إسقاط قبل جريان السبب، فلم يلزم. هذا [تقرير] الفرق بين السبب والشرط، وبيان دلالة الشرط، ووجه كونها إذا دخلت الدار تطلق. [والله المستعان].

قال الإمام: <span data-type="title" id=toc-165>(مسألة: الصحابي إذا روى خبرًا وعمل بخلافه) </span>إلى آخر المسألة. قال الشيخ: ما ذكره الشافعي [رحمه الله] ظاهر في نظر','2','269',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (966,'الأصول، فإن الخبر حجة، وأعمال الصحابة إذا لم ينعقد عليها إجماع، ليست حججًا، وترك الحجة لما ليس بحجة صعب شديد. وستأتي المسألة بعينها في كتاب الترجيح. وما أحد من العلماء [يترك] الخبر بمحض [عمل] العامل، وإنما [يستدل] الناظر بالعمل على خبر على وفق العمل،','2','270',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (967,'[فيترك] الخبر للخبر، لا [للعمل]. وترك الخبر بخبر متأخر عنه، ولا إشكال فيه، وإنما الشأن في تلقي الخبر من العمل. هذا هو العسر، وفيه [غموض] كثير. والمسألة لها صور نفرد كل صورة بالكلام.
[فأعلاها: ] أن يكون الراوي [ذاكرًا] للخبر، والخبر يتضمن [تحريم] أمر، فيتعاطاه [الراوي]، [فلا شك] أنه لم يتركه إلا لمستند عنده اقتضى الترك، واحتمال الترك، بناء على الجرأة حرام، لا يحل المصير إليه بحال، مع إمكان مأخذ سواه، [فيتعين] المصير [إلى] أنه [تركه] لمستند','2','271',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (968,'عنده. ولكن انحصار المستند الذي [استند إليه] في نص ناسخ، اتفق بلوغه إليه ولم يبلغنا، صعب شديد. إذ للقائل أن يقول: ما المانع من إسناده العمل [والمخالفة] إلى أمر لا يصلح عندنا للإسقاط؟ بل [لو ظهر، فربما] كنا نحن نقدم الخبر عليه، وهو لو عين مستنده، وأسند [المخالفة] إليه، لم نكتف نحن برأيه، ولم نقلده، فإذا كان الأمر [مبهمًا] فهو أشد. وهذا كلام واقع، والانفصال عنه شديد.
ولكنا (١٠٨/ب) نقول: لا ننكر حصول غلبة الظن قبل الوقوف على سبب الترك، في أن [الخبر] ساقط متروك، [وأنه] إذا نقل للمجتهد حكم [المجتهدين] من حيث الجملة، فإنه يغلب على ظنه [الإصابة]، نظرًا إلى حصول درجة الاجتهاد، وإن أمكن عند التعيين أن [تفوت] غلبة الظن، إذ','2','272',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (969,'الأغلب على المجتهد الإصابة، والخطأ قليل، لاسيما إذا كانت المسألة نقلية محضة، ليس للرأي فيها مجال. وإنما كثر الاختلاف في المسائل الراجعة إلى الرأي. فأما المسائل التوقيفية، فالخلاف فيها من أهل العصر الأول قليل، فإذا غلب على الظن [عند ترك الراوي للخبر سقوط الخبر، امتنع التمسك بخبر يغلب على الظن] سقوطه.
فإن قيل: إذا سلكتم هذا المسلك، فجوزوا للمجتهد [تقليد] المجتهد، فإنه يغلب على ظنه [إصابته] (٨٧/أ) قبل بحثه، فليكتف بهذا النوع من غلبة الظن. [وإذا] لم تكتفوا [بذلك، فلا تكتفوا] بغلبة الظن الحاصلة بسقوط الخبر، بناء على ترك الراوي [له]. قلنا: [قد اختلف الناس] في جواز تقليد المجتهد للمجتهد. وليست المسألة قطعية، وهي في محل','2','273',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (970,'[الاجتهاد]. وإن كان الأغلب على ظننا منع ذلك. ولكن الفرق بين المسألتين أنا لا نكتفي بغلبة الظن من حيث الجملة، إذا [قدرنا] على تلقي الأمر من جهة البحث والتفصيل والتعيين. وقد قررنا - عند كون العالم [مجتهدًا]- أن يجتهد كاجتهاده، فلم يكتف بنظره [إذا] قدرنا على مثل ما قدر عليه، وليس كذلك إذا خالف الخبر، فإنه لم يخالفه إلا لخبر عنده اتفق أنه بلغه ولم يبلغنا، وإذا تعذر [علينا] [الإطلاع] على [غير] المعارض، تعذر علينا أن نعرف التفاوت على التعيين والتفصيل، فاكتفينا بغلبة الظن','2','274',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (971,'الحاصلة بسقوط الخبر تلقيًا من مخالفة [راوية] له.
وهذا الطريق الذي قلناه، يطرد فيما إذا غلب الظن بلوغ الخبر له ثم خالفه، وكذلك يطرد فيما إذا غلب على ظننا أنه لم [ينسه] وجوزنا ذلك، فإن هذه الصور كلها تجري على نحو واحد، [وإن] كان بعضها في غلبة الظن أقوى [من البعض]. و [لكن] غلبة الظن حاصلة في الجميع، [لسقوط] الدليل، وليس كذلك ما إذا [شككنا] في البلوغ، ولم يغلب على الظن بوجه، فلا يسقط الأصل بسبب التردد، ويصح [حينئذ] قول','2','275',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (972,'الإمام: ([إنا] على ثبت من أصول الشريعة -[أعني] الخبر [المنقول]-[ولا] يسقط الأصل بسبب التردد). وهذا الكلام صحيح عند الشك، وليس كذلك ما إذا غلب على الظن أنه بلغه.
وأما [نقله] عن مالك من كونه يرى تقديم عمل أهل المدينة على الأخبار، فهذا له صور:
أحدها - أن يكون الخبر بلغهم، فهذا قدر وافق الإمام على سقوط الخبر فيه.
الصورة الثانية - أن يثبت عندنا أن الخبر لم يبلغهم، فلا يحل لأحد في مثل هذه الصورة أن يترك الخبر، وهم [لو بلغهم] الخبر، لما خالفوه [أصلًا].','2','276',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (973,'الصورة الثالثة: أن نجد الأعمال على خلاف الأخبار، ولم نتحقق البلوغ، ولا انتفاء، [فالظاهر] من قول مالك- رحمه الله- أن الخبر متروك، بناء منه على أن الغالب أن الخبر لا يخفي [عنهم]، لقرب دراهم وزمانهم، وكثرة حثهم، وشدة اعتنائهم بحفظ أدلة الشريعة، فنقع المسألة في قسم ما إذا ظننا بلوغ الخبر، [ولم] نقطع به، وقد (١٠٩/أ) [اخترنا] في [هذه] الصورة سقوط التمسك بالخبر، ولزوم التمسك بالعمل.
هذا هو الرأي الظاهر في ذلك. وعلى هذا التقدير ما [نبقى] على','2','277',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (974,'شك، هل بلغهم الخبر أم لا؟ بل يغلب على الظن البلوغ مطلقا، نظرا للعادة. على أن هذه المسألة قد اختلف قول مالك فيها، فروى عن النبي صلى الله عليه وسلم أنه قال: (البينة على المدعي، واليمين على من أنكر). (٨٧/ب) فعم. وقال [الفقهاء] السبعة: لا تتوجه اليمين إلا بمعاملة [أو مخالطة] أو ظنه. والمشهور من مذهب مالك هذا. وله قول آخر في تعميم توجه اليمين، على','2','278',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (975,'حسب ما اقتضاه الظاهر من التعميم.
وأما إذا روى [الراوي، وأول وذكر] محامل، فهذا مما اختلف الناس فيه. أما من ذهب إلى أن قول الصحابي حجة، فيصير ذلك الاحتمال مرجحا، لوجدان الدليل الدال عليه. [وإذا] قلنا- وهو الصحيح- إنه لا يكون حجة، [فهل] يصلح مرجحا ومعينِّا، [وأنه] قد [ثبت] الترجيح بما [لا] يستقل [دليلا]؟ ذهب الشافعي [رحمه الله] إلى ذلك.
والذي يظهر لي- والله أعلم- أنه لا يثبت [بذلك] ترجيح، إلا أن يقول الصحابي: علمت ذلك من قرائن أحوا الرسول [صلى الله عليه وسلم]. فإن صير إلى','2','279',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (976,'[تعينه، فبناء] على أنه أعلم، لا باعتبار كونه راويا، [واطراد] ذلك في بقية الصحابة إذا [أولوا]- أعني كل واحد منهم على انفراد. وكل هذا إنما هو مع المصير إلى أن أقوال الصحابة ليست حججا.
وما ذكره الإمام آخر المسألة من أن المخالفة إنما تكون معتبرة إذا جرت في زمن العدالة. فكرم صحيح، لأن الدليل إنما [تم] [بقولنا: ] إن كان الخبر صحيحا معتمدًا، فالعامل فاسق، ولا يحل نسبة ذلك إليه. فإذا تحقق فسقه، لم ينضبط المأخذ في الدليل المخالف، [فأمكن] أن تحمل المخالفة على [الجرأة]، [فيرجع] هذا بمثابة ما إذا لم نتحقق المخالفة.
فإن قيل: فالأمر دائر بين أن يكون الخبر [متروكا]، أو [الراوي] فاسقا، وكلاهما [يقتضي] أن لا يتمسك بالخبر. قلنا: لهذا افتقر إلى أن يقدر تخلل زمان طويل، بحيث لا تنعطف [غوائل] الفسق على الخبر، فيبقى الخبر معمولًا به. هذا نهاية المسألة.','2','280',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (977,'قال الإمام: <span data-type="title" id=toc-167>(مسألة: إذا ورد [لفظ الشارع]، وله مقتضى في وضع اللسان، [ولكن عم في عرف أهل الزمان استعمال ذلك اللفظ على خصوص في بعض المسميات]) </span>إلى آخر المسألة. قال الشيخ: هذه المسألة، وهي تخصيص العموم بالعرف تنقسم أقساما، بعضها متفق عليه في النفي، [وبعضها متفق عليه في الإثبات]، وبعضها مختلف فيه. [والمتفق] عليه من ذلك أن يكون العرف أهل اللسان، كالدابة والغائط، وما يجري مجراه. هذا','2','281',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (978,'[إن] قلنا إن الشارع لم يتصرف في اللغة، وإن بني على أنه تصرف [في اللغة]، [تنزل] عرفه [منزلة] عرف أهل اللسان، ووجب التخصيص [به].
الثاني: أن يكون العرف لغير أهل اللغة، ولم يكن [الشارع] يعرف عرفهم في الاختصاص. فهذا يجب أن تنزل ألفاظ الشارع على مقتضاها [في اللغة]، [أو في] عرف الشرع. وهذا لا يتجه فيه خلاف، إذ كيف','2','282',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (979,'[يتصور أن] يكون قصد [مناطقتهم] على حسب عرفهم، ولا يعرفه؟
الثالث: أن يكون للمخاطبين، وليسوا أهل اللغة (١٠٩/ب)، والشارع يعرف عرفهم، ولكنه لم يظهر منه مناطقتهم على مقتضى عرفهم، ولا ظهر أيضًا منه الإضراب [عن] ذلك. فهذا موضع خلاف، فذهب ذاهبون إلى انه ينزل على مقتضى عرفهم، وهو تحكم، والدليل عليه ما ذكره الإمام، [وهو] كون الشارع خاطب الشريعة العربية الخلق أجمعين، على أن يفهموا من الألفاظ مقتضاها.
الرابع: أن يكون المخاطبون (٨٨/أ) اعتادوا تعاطي بعض ما يدل عليه العموم فعلا، وهذا بمثابة [ما لو] نهى عن أكل اللحم مثلا، وكان أولئك القوم اعتادوا أكل لحم مخصوص، [فهل] يكون النهي مقصورا على ما اعتادوا أكله أم لا؟ اختلف الفقهاء فيه، وكذلك الأصوليون.','2','283',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (980,'وعليه خرج تخصيص الأيمان بالعرف الفعلي، فإذا حلف أن لا يأكل لحمًا، حنث بأكل [لحم] السمك، وكذلك إذا حلف أنه لا يأكل بيضًا، حنث بأكل بيض العصافير.
وكذلك إذا حلف أن لا يدخل بيتًا، حنث بدخول بيت الشعر، [أو يقتصر] يمينه على [ما اعتاده]؟ هذا مما اختلف فيه. وعليه خرج الاختلاف بين أصحابنا، هل يجب على صاحب [السلس] الوضوء، أو على [من] أنزل من حكة أو ضرب غسل أم لا؟ والشهور من المذهب وجوب الوضوء على [صاحبه]، ..........................','2','284',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (981,'لأن الله تعالى [قال]: {أو جاء أحد منكم من الغائط}.
وهذا هو اختيار ابن القاسم. وذهب بعض أصحابنا إلى أنه لا يجب الوضوء على صاحب السلس، ونزل قوله [تعالى]: {أو جاء أحد منكم من الغائط} على المعتاد من الفعل، وخص به عموم الخطاب، وهذا ضعيف.
وقد قررنا أنه إذا تحقق الظهور لغة، ولم يمنع مانع [منه]، وجب التمسك بالعموم، ولا مناقضة بين حمل اللفظ [على] عمومه، وقصور','2','285',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (982,'[التعاطي] على شيء مخصوص. هذا [أمرُ] ما ذكره الإمام [في] المخصصات، وبقي من المخصصات شيئان:
أحدهما- فعل [الرسول صلى الله عليه وسلم]. والثاني- تقريره واحدا من أمته على خلاف مقتضى العموم. أما التقرير، فداخل تحت قسم [الناص] الخاص، إما تواترا، وإما آحادا. وأما الفعل فأخر الإمام الكلام فيه، لأنه طويل، وفيه تقسيم وتفصيل. ونحن نرى أن [نؤخر الكلام] فيه إلى موضعه. وإذا استقر أمره، نبهنا على كونه مخصصا أم لا؟ والله الموفق للصواب.

قال الأمام [رحمه الله]: <span data-type="title" id=toc-168>(القول في المفهوم</span>-[ما يستفاد] من','2','286',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (983,'اللفظ نوعان) إلى قوله (فلتقع البداية بالمسألة الأولى). قال الشيخ [أيده الله]: اللفظ عند أهل اللغة يدل على وجهين: أحدهما- من جهة نظمه [وصيغته]. [والآخر-] من جهة [اقتران] أمر آخر بالمتلفظ به، [قائما] بنفس المتكلم، استغني عن التعبير عنه بالتعبير [عن ملازمه]، وهذه الملازمة، قد تكون على وجه مقطوع به، وقد تكون على وجه مظنون، وقد تكون أيضًا على جهة موافقة، وقد تكون على جهة مناقضة، وقد تكون على جهة مخالفة، [لا مناقضة] ولا مماثلة. فهذه [خمسة] أمور لابد من النظر فيها.','2','287',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (984,'الأول: ما يسمى اقتضاء، وهو أمرٌ مفهوم [عند] اللفظ، ولا يكون منطوقا [به]، ولكن (٨٨/ب) يكون من ضرورة [المنطوق] به، إما من حيث [أنه] لا يمكن أن يكون المتكلم صادقا إلا به، أو من جهة (١١٠/أ) كونه لا يصح الملفوظ [به شرعا إلا به، أو من] حيث يمتنع ثبوته عقلا إلا به.
أما المقتضى الذي [هو ضرورة] صدق المتكلم، [فكقوله: (لا عمل] إلا بنية). و) رفع عن أمتي الخطأ والنسيان). فإن الخطأ والنسيان موجودان، وكذلك العمل بغير [نية] موجود أيضًا، فيفتقر إلى','2','288',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (985,'إضمار، ليصح صدق المتكلم، فيضمر الحكم [أو الإثم]، أو ما يشبه ذلك.
[وكذلك] قال القاضي هذا في قوله [صلى الله عليه وسلم]: (لا صيام لمن لم يبيت الصيام من الليل). يجب إضمار الحكم، وإنما لزم ذلك، من جهة أنه [ينفي] [الأسماء] الشرعية، ويرى أن الصوم لمحض الإمساك، وهو موجود، فيجب إضمار الحكم، على حسب ما [مر]. [وأما] من جعله عبارة عن الصوم الشرعي، فيمكن انتفاؤه بطريق النطق. ولهذا قال القاضي: إنه لا عموم له، لأنه [ثبت] اقتضاء.
وأما الاقتضاء الذي لا يتصور ثبوته شرعًا إلا به، فإذا أمر بالصلاة أو التزمها، [فإن] ذلك يتضمن [الطهارة لا محالة. وقد دللنا على أنه لابد من','2','289',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (986,'الطهارة. وإنما يثبت ضمنًا لا تصريحًا.
ومن هذا القبيل عند بعض العلماء، قول القائل لغيره: اعتق عبدك عني، فإن ذلك يتضمن] الملك للملتمس، [وإن لم يتلفظ به]، [لكنه] ضرورة المتلفظ به شرعًا.
هذا مذهب الشافعي [رحمه الله]، وأما مالك، فإنه لا يرى ذلك، ويرى أن الإنسان إذا أعتق عن غيره، أن المعتق عنه، لم يملك، وإنما يثبت له الولاء [بالسنة]، [يدل] على ذلك صحة العتق عن الميت، وإن كان لا يملك.
وأما المقتضي الذي يستحيل ثبوت [المذكور] عقلا إلا به، فكقوله:','2','290',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (987,'{حرمت عليكم أمهاتكم}. فلا يتصور إضافة الأحكام إلى الأعيان، فلابد من إضمار الحكم، فالمراد: وطء أمهاتكم. وهذا كله قد تقدم كلامنا عليه. وكذلك قوله: {أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم}. [ويقرب] منه قوله تعالى: {وسئل القرية}. إذ لا يمكن سؤال الجماد، فيفتقر إلى إضمار [الأهل]. وقد عبر بعضهم عن هذا بالإضمار دون الاقتضاء، والقول في ذلك قريب.
الضرب الثاني: ما يؤخذ من [إشارة] اللفظ، وإن لم تدع إليه ضرورة، بل يصح الاقتصار على المذكور، ولكن [تشير] الألفاظ إلى جهة أخرى.','2','291',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (988,'وكما أن [المتكلم] قد [يعرف] بحركاته أمورًا، لا يدل عليها صريح لفظه، فكذلك يؤخذ من إشارة الألفاظ أمور ليست هي المقصود الأصلي، الذي وقع التعبير عنه، ولكنها [تقع] من توابعه.
مثاله: استدلال العلماء على أن أقل الطهر خمسة عشر يوما، وأكثر الحيض خمسة عشر يوما [بقوله]- عليه السلام -: (إنكن ناقصات عقل ودين، قيل: ما','2','292',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (989,'نقصان [عقلهن] ودينهن؟ قال: تمكث إحداهن شطر [عمرها] لا تصلي). فالكلام [لم يسق] [لبيان] مدة الحيض، [وإنما] سيق لنقصان الدين، ولكن نعلم من جهة العادة أن النساء [لا يحضن كلهن] شطر الدهر، وإنما القليل منهن يحضن كذلك. فنعرف (٨٩/أ) أن النبي صلى الله عليه وسلم لم يقصد الغالب في ذلك، بل [قصد] القليل النادر عند مقصد التنقيص، فلو أيقن أن يكون في النساء من [تحيض] أكثر من ذلك، لارتقى إليه عند القصد إلى المبالغة في الذم.','2','293',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (990,'[ومن هذا القبيل] استدلال العلماء على أن أقل مدة الحمل ستة أشهر، لقوله تعالى: {وحمله وفصله ثلاثون شهرًا}. وقال: {وفصله في عامين}. فالباقي للحمل ستة أشهر، وبه استدل علي [رضي الله عنه] في المرأة التي وضعت في ستة أشهر، فأمر [عثمان] برجمها (١١٠/ب) فقال علي رضي الله عنه: (ليس ذلك عليها). وتمسك بهذا.
ومن هذا القبيل [الحكم] بأن من أصبح جنبا صح صومه، لقوله تعالى:','2','294',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (991,'{حتى يتبين لكم الحيط الأبيض من الحيط الأسود من الفجر}. فبمد الرخصة إلى الفجر يعرف أن من [وطئ] عند الفجر، [فغسله يقع] بعد طلوعه. ونظائر ذلك [مما] يكثر.

الضرب الثالث:<span data-type="title" id=toc-171> فهم التعليل من إضافة الحكم إلى الوصف المناسب</span>، كقوله: أكرم العالم، وأهن الفاسق، وامدح المطيع، فإن أهل اللغة يفهمون [من] ذلك التعليل، وإن لم يصرح به. وكذلك [كل] ما يخرج مخرج المدح والذم، والترغيب والترهيب. ومن هذا قوله تعالى: {إن الأبرار لفي نعيمٍ وإن الفجار لفي جحيم}. أي [لبرهم] وفجورهم، وهذا قد يسمى إيماء','2','295',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (992,'وإشارة، وإليك الخيرة في التعبير عنه، [بعد فهم حقيقته].

الضرب الرابع:<span data-type="title" id=toc-172> فهم غير المنطوق من المنطوق، بدلالة سياق الكلام </span>ومقصوده، كفهم تحريم الشتم والضرب من قوله تعالى: {فلا تقل لهما أفٍ}. وفهم تحريم الاتلاف من قوله [تعالى]: {إن الذين يأكلون أموال اليتامى ظلمًا}. وفهم ما وراء [الذرة] [والدينار] من قوله تعالى: {فمن يعمل مثقال ذرة خيرًا يره}. وقوله: {ومنهم من إن تأمنه','2','296',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (993,'بدينار لا يؤده إليك}. [وكذلك] قول القائل: ما أكلت له برة، ولا شربت له جرعة، ولا أخذت من ماله حبة، فإنه يدل على [ما وراءه].
فإن قيل: هذا من قبيل التنبيه بالأدنى على الأعلى. قلنا: إذا وقع [الاعتراف] بأن مجرد اللفظ لا يقتضي ذلك، وإنما هو مفهوم من أمر زائد على اللفظ، فلا حجر في [العبارة].
فإن قيل: هو ملتقى من القياس. قلنا: ليس كذلك، فإنه لو كان من القياس، لتوقف فيه منكرو القياس، [ولكنا] لا نفهمه قبل ورود الشرع بالقياس، وأهل اللغة، بل أهل [كل] [لغة] يفهمون من هذا السياق','2','297',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (994,'كمال التعظيم، وتمام الاحترام، ومجرد اللفظ لا يقتضيه، إذ قد يؤمر بقتل الرجل العظيم، وينهى عن مواجهته بالإهانة والقول القبيح. وقد يحلف الحالف ويقول: والله ما أكلت له لقمة، ويكون قد أحرق ماله أو ضيعه، فلا يحنث. وهذا قد يسمى مفهوم الخطاب، وقد يسمى فحوى الخطاب، [والخطب] في اللفظ [قريب] إذا [اعترف] أن [التأفيف] ليس عبارة عن نفي الأذية على الإطلاق.
وقد زعم بعض الأصوليين أن هذا من قبيل الألفاظ العرفية، فيصير بمثابة الغائط والبول (٨٩/ب)، الذي ألف في العرف انطلاقه على غير ما وضع له، فيكون على هذا منطوقًا به. وقد بينا خلاف ذلك.','2','298',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (995,'وقال قائلون: هو من القياس والاعتبار، فإن أراد القوم أن المنطوق به هو الذي [عرف] حكم المسكوت عنه، فهو [صحيح]. وإن أرادوا أنه من القياس [الذي] جاءت الشريعة بإثباته وشروطه، فليس كذلك، فإنا قد قلنا: إن العقلاء قبل ورود [الشرع]- عند فهم هذا السياق - يفهمون تحريم الأذية، ووجوب الاحترام.
الضرب [الخامس]: المفهوم: ومعناه الاستدلال بتخصيص الشيء بالذكر على نفي الحكم [عما] عداه.','2','299',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (996,'وسمي مفهوما، لا لأنه [يفهم غيره، بل] [المنطوق] به أيضًا مفهوم، [بل لما] فهم من غير تصريح بالتعبير عنه، سمي مفهومًا، وهذا أيضًا لا يقال على الإطلاق، فإنه لو سمي مفهوما لهذا، اقتصارًا عليه، لسميت الأقسام (١١١/أ) الأربعة السابقة [مفهومًا]، فهو مخصوص عند الأصوليين بما فهم عند النطق على وجه مناقض للمنطوق به.
أما إذا كان يوافقه، [فإن] انتهى إلى حد يقطع به، سمي فحوى، وإن كان على وجه مظنون، أطلق عليه إلحاق الأدنى بالأعلى. وقصر الشافعي [المقطوع به في] إلحاق الأدنى بالأعلى، وليس كذلك، فإنه قد يكون مساويًا، كقوله - عليه السلام -: (لا يبولن أحدكم في الماء الدائم). فإنه لو صب بولًا','2','300',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (997,'من إناء في الماء، [لكان] في معناه. وإنما عظم الأمر وطال النزاع في مفهوم المخالفة.
وأما مفهوم الموافقة، فإنه يرجع إلى فهم سياق، أو ثبوت استواء في نظر الشرع، فلا [تنازع] فيه، إذا كان جليًا. وقد يتنازع في المنطوق [منه]، [كما قال] الشافعي: إذا وجبت الكفارة في الخطأ، فلأن تجب في العمد أولى. وقد نازعه مالك في ذلك، ولم يوجب الكفارة في العمد، ورأى أنه أعظم من أن يكفر. وقد تعرض الشافعي [لبيان] [درجات] المفهوم، ولم يستوعب الأقسام، ولم يذكر مواضع الاختلاف منها. ونحن نذكرها، ونبين تفاوتها. إن شاء الله تعالى.','2','301',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (998,'[الرتبة] الأولى: وهي أبعدها، بحيث يجب أن يقر ببطلانها كل ذي عقل، تخصيص المسميات بألقابها، كتخصيص الأشياء الستة في الربا، ، فإن ذلك إنما قصد به تعريف المسميات، ولم يقصد به النفي. وسيأتي الدليل عليه.
الرتبة الثانية: تخصيص الأوصاف التي [لا] تطرأ ولا تزول، كأسماء الأجناس، كقوله: ([لا تبيعوا البر بالبر]، ولا تبيعوا الطعام بالطعام). [وهذا] فيه خلاف وتفصيل، ولا فرق بين أن يقول: (لا تبيعوا البر بالبر)، [وبين أن يقول: ] (لا تبيعوا الطعام بالطعام)، إذ هذا الاسم جعل لقبًا لجنس أوسع من الأول.
الرتبة الثالثة: تخصيص الأوصاف التي تطرأ وتزول، كقوله: (الثيب أحق','2','302',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (999,'بنفسها من وليها). و) السائمة تجب فيها الزكاة). [فلأجل] [أن] السوم يطرأ ويزول، قد يتوهم [أن] [لطيران] [الزكاة] [صلة] [بطريان] السوم، وأن ذلك يتضمن نفيًا عما سواه، إذ لما كانت غير سائمة، لم تذكر [الزكاة] [فيها]، وهذا عندنا ضعيف. وفرق الإمام بين الصفة المناسبة وغيرها، (٩٠/أ) وسنتكلم على ذلك.
الرتبة الرابعة: أن يُذكر الاسم العام، ثم [تذكر] الصفة الخاصة في','2','303',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1000,'معرض [الاستدراك]، كقوله: (من باع ثمرة مؤبرة فثمرتها للبائع). [وكما] لو قال: (أكرموا العلماء الصالحين، واقتلوا المشركين الحربيين)، فقد يقول القائل: لو كان الحكم [يعمها] لما أنشأ بعد ذلك استدراكًا، وهذا عندنا ضعيف. نعم، هذا التخصيص يفهم أن هذا [هو] المنطوق به، أما أن يعرفنا نفي الحكم عما عداه فلا. [فيقول] القائل: فما سبب الاستدراك؟ قلنا: سببه أن يبين المحل المحكوم فيه، أو لسر آخر، فلا يلزم من ذلك إشعار اللفظ بالنفي عما سواه بحال.
وسبب تفاوت هذه الدرجات: أن ذاكر [اللقب] [يحتمل] أن يقال لم يحضره سوا، فلذلك عبر عنه على الخصوص. فهذا سبب، وهو الغفلة عن غيره. [وتبعد] الغفلة عن الفكر عند ذكر [النعت]، لأن الصفة','2','304',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1001,'[بذكر] ضدها [يضعف] احتمال [النسيان]، فكأنه [يقوي] احتمال قصد القصر عند ذكر الضد، وإذا ذكر الجنس، ثم استدرك بالفصل، انقطع هذا (١١١/ب) الاحتمال بالكلية، فقوي احتمال المفهوم عند انقطاع [احتمال] الغفلة. [فهذا] بيان تفاوت هذه الجهات. والصحيح أن ما سوى هذه المذكورات مسكوت عنه على الإطلاق.
[الرتبة] الخامسة: الشرط وأبوابه، على حسب ما ذكر الإمام.
[الرتبة] السادسة: قوله - عليه السلام -: (إنما الماء من الماء). و) إنما الشفعة فيما لم يقسم). و) إنما الولاء لمن أعتق). و) إنما الربا في','2','305',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1002,'النسيئة). و (إنما الأعمال بالنيات). وقد أخذ على هذه [الرتبة] بعض المنكرين للمفهوم. وهذا عندنا خطأ، وسنبينه بعد ذلك، إن شاء الله تعالى.
[الرتبة] السابعة: [مد] الحكم [إلى غاية] [بصيغة] (إلى) و) حتى) كقوله [تعالى]: {[ولا] تقربوهن حتى يطهرن}. و} حتى يعطوا الجزية}. وقد جحد المفهوم فيه بعض المنكرين للمفهوم، وقالوا: هذا نطق بما قبل الغاية، وسكوت عما بعد الغاية، فيبقى على ما كان عليه. واعترف القاضي به، وهو الظاهر. ويقرب منه التحديد','2','306',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1003,'بالعدد، كقوله: (اضرب عشر ضربات). و} الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة}.
[الرتبة] الثامنة: الاستثناء، كقوله: (لا عالم في البلد إلا زيد)، و) لا إله إلا الله)، فقد ذهب قوم إلى أنه إنما نطق بالنفي، ولم ينطق بما [تثبت] له القضية، فكأنه قال: (ما سوى الله ليس بإله)، ولم يتعرض في كلامه لإثبات الآلهية [لله تعالى]. [وهذا] سرف عظيم، وجهل باللغة، وخروج عن مقصد العقلاء، وإنما غرهم في هذا، أن القائل [قد] يقول: (لا سيف إلا ذو الفقار)، وإن كان لا يقصد النفي. وهذا خطأ من وجهين: أحدهما- أن هذا اعتراض على النفي، لا على الإثبات.
الثاني- أنه لم يرد نفي ذات السيوف، وإنما أراد نفي الكمال والجودة، وقصرها على ذي الفقار، فهو يدل على ضد مقصودهم.
وتمسكوا أيضًا بقول [صلى الله عليه وسلم]: (لا صلاة إلا بطهور). و) لا عمل إلا بنية). قالوا: هذا إنما (٩٠/ب) نطق بالنفي [دون الإثبات]. وهذا كلام','2','307',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1004,'صحيح، ولكنه ليس من الباب لوجهين:
[أحدهما-] أن [) الباء)] التي هي [) باء)] السببية [موجودة، فهي مرشدة] إلى الاشتراط والاستعانة، فالمقصود بهذا: الإخبار عن امتناع [الصلاة بلا طهارة]، فيرجع [إلى]، باب الشرط الذي انحصرت دلالته على النفي.
الثاني- أن قوله: (لا صلاة) [لا] يتضمن نفي الطهارة حتى يستدركها ب) إلا)، فلما لم تكن داخلة تحت لفظ المنفي، لم يتصور فيها الاستثناء، بناء على الإثبات، فالمراد الاشتراط خاصة، بخلاف قوله: (لا إله إلا الله)، فإن من قال ذلك، [فقد نفى وأثبت] قطعا.

قال الإمام: <span data-type="title" id=toc-179>(مسألة: نذكر وجوه احتجاج القائلين بالمفهوم، [ونتتبع]</span>.','2','308',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1005,'ما لا نرضى فيها [بالإبطال]، ثم [نعقبها] بوجه الحق) إلى قوله ([ولا نجد في أنفسنا العلم الضروري [باعتقاد] الأولين] اقتضاء التخصيص نفي ما عدا الخصوص). قال الشيخ: جميع ما ذكره الإمام [مستقيم]، إلا في قضية واحدة، [وهي] إنكار صحة قوله - عليه السلام -: (لأزيدن على السبعين). فإن هذه الزيادة صحيحة، ثابتة في الصحاح، ذكرها [) مسلم)]','2','309',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1006,'في (صحيحه).
وما ذكره القاضي في إنكار استفاضة الأخبار، بالنظر إلى جملة (١١٢/أ) الرواة، حسن بالغ، وذلك أنه إذا افترقت الرواة في نقل الأقاصيص، فلا تخلو تلك الأقاصيص: إما أن تشترك في أصل واحد، وتختلف في التفصيل، أو لا تشترك بحال، او تشترك في [الجهة] الخاصة.
فإن اشتركت في الجهة الخاصة، مع [توافر] عدد التواتر، واجتماع الشرائط، فهو الخبر المتواتر، على ما يأتي بيانه. وإن لم تشترك الأقاصيص في أصل، ولم يحصل في كل قصة عدد التواتر، لم يحصل العلم بشيء منها، لا أصلا ولا تفصيلا.
وإن اشتركت في الأصل، وافترقت في التفصيل، فإن كان جملة الرواة للأقاصيص، لو اشتركوا في نقل جهة خاصة، لتواترت بنقلهم، وحصلت بقية','2','310',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1007,'[الشروط]، فإذا [أخبروا عن] التفصيل، حصل العلم بالأصل دون التفصيل، لاشتراك العدد في الأصل. وهذا كشجاعة علي [رضي الله عنه]، وكرم الطائي، فإن تفصيل القضية [تشترك] في أصل الشجاعة والكرم، لا جرم علم الكرم، وإن لم يعلم [آحاد] الوقائع. وليس كذلك تفصيل المفهوم، فإنه لم يحصر الرواة في [حد يتواتر] النقل بعددهم.
[وأيضًا] لو قدرنا أنهم قطعوا بالمفهوم، [فمستندين] إلى علم ضروري، على حسب ما يأتي في شرح التواتر. والأخبار المنقولة أيضًا لا يقطع','2','311',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1008,'بأنها استندت إلى المفهوم، فشرائط التواتر [مختلفة] كلها.
[قال الإمام]: (طريقة [أخرى لهم] ضعيفة) [إلى قوله] (بمثابة','2','312',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1009,'فرض التخصيص باللقب). (٩١/أ) قال الشيخ: الأمر على ما ذكره الإمام، ونزيده [تقريرًا] وتقريبًا، ونبين [وجه] الوهم فيه.
اعلم أن هذا الأصل، وهو إذا كان الحكم يتناول صورا كثيرة، فنص','2','313',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1010,'[على] بعضها، وخص بالذكر في خلاف ذلك الحكم، [فلاشك] أن الفرق يدرك بين المقتطع، وبين المنفي، ولم يكن الفرق قبل ذلك ثابتا، فلما وقع التخصيص في البعض [بالنقيض]، عقل الفرق حينئذ، وعماد الفرق أمران:','2','314',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1011,'أحدهما- ثبوت الحكم الأول في [المنفي].
والثاني- ثبوت الحكم الثاني في المقتطع، وإنما [تنبه] الذهن [للفرق]، بسبب النص على المقتطع، [فيظن] أن الحكم في القسمين [استند] إليه، وليس الأمر كذلك، بل الحكم في الأول مستمر، على ما','2','315',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1012,'يقتضيه الأصل السابق، والحكم الثاني هو المتلقى من الخطاب الجديد، فتوهم قوم [أن] الخطاب الجديد هو الذي أفهم الحكمين المتناقضين في [المحل]، وذلك غلط بين.
والاستشهاد على فساد هذا الوهم بالتنصيص على اللقب عند الاقتطاع، استدلال [غير] صحيح، إذ اللقب لا مفهوم له. وصورته: ما إذا قال الرجل [لوكيله]: بع سالما، [فإنه] لا يتمكن من بيع غانم، لا لأجل النص على','2','316',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1013,'بيع سالم، ولكن لأنه لا يبيع إلا إذن، والحجر سابق، والإذن قاصر، فبقي الحجر على ما كان عليه في غير محل الإذن. فليتنبه لهذا.
قال الإمام: ([وأما] الإمام الشافعي، فإنه احتج في إثبات القول بالمفهوم) إلى قوله (وليس في كلام الشافعي التزام ذلك، على ما ينبغي من اختصاص أثر التخصيص بالمواصفات [عنده]). قال الشيخ: هذا الذي','2','317',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1014,'ذكره الإمام هو تقرير الشافعي، وقال: إنه حسن بالغ، وإنما اعترض عليه بالنقض [باللقب خاصة، فإذا الطريق صالح، وإنما جاء الخلل من النقض]، [فيقتضي ذلك] أنه إذا اطرد الطريق، صلح [للاعتماد] والوفاق، [فقد] طرد هذا، فينبغي أن يكون مذهبه مستقيمًا، وذلك غير صحيح، فإذًا الطريق غير صالح (١١٢/ب) [ولا] حسن، سواء [اطرد أو انتقض].
فيا ليت شعري من أي [وجه] حسن [هذا] المأخذ؟ على أن النقض على الحقيقة لا يتوجه على الشافعي، فإنه لم يجع انحصار الفائدة في المخالفة قطعًا، إذ لو كان كذلك، لقطع بالمفهوم، وغايته أن يكون مظنونًا. وإذا','2','318',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1015,'كنا نظن أن الشارع قصد المخالفة عند التخصيص بالصفات- إذ هو الغرض في هذا الباب- فنحن على بصيرة أن التخصيص بالألقاب لا يقصد به المخالفة [على حال، لقيام دليل القياس. فهذه الصورة دلت الأدلة على أن] المخالفة في الحكم، ليست مقصودة. [ولا] يلزم من ترك الظاهر في صورة، [لدليل] دل عليه، [سقوطه] على الإطلاق. هذا [هو] مستند الشافعي في الفرق بين الصفات والألقاب. ولكن الطريق [الموثق به عندنا] غير صالح، لا باعتبار انتفاضه، ولكن لأمور أُخر:
منها- أن هذا القائل جعل [طلب] فوائد الألفاظ دليلًا على الوضع، (٩١/ب) والأمر بالضد من ذلك. ولو [صرنا] إلى ما قاله، للزم جحد','2','319',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1016,'المشتركات والمجملات من اللغات، بل يجب فهم اللفظ أولًا، ثم ينظر في [فائدته] وإجماله، وتعدد الفائدة وانفرادها. هذا هو الصحيح، [فأما] أن يجعل الوضع تابعًا [للفائدة] فلا.
الثاني-[أنا] نسلم أن التخصيص [لا يكون إلا لفائدة]، فمن أين يلزم أن تكون الفائدة اختصاص المذكور بالحكم واقتصاره عليه؟ والفوائد كثيرة، والبواعث على التخصيص متعددة، إما بالإضافة إلى كونه خطر بباله دون غيره، أو حاجة ناجزة، أو جوابا عن واقعة [خاصة].
فإن قيل: لو كان عليه باعث آخر، لعرفناه. قلنا: ومن اين يلزم أن يكون كل باعث معروفا لكم؟ وهذا الكلام [كثيرا] ما يستعمله بعض الأصوليين، فيجعل عدم علمه بالشيء دليلًا على عدم ذلك الشيء، وهذا غلط بين، وقد','2','320',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1017,'تقدم تقرير فساده قبل هذا.
الثالث- أن ما ذكره يبطل بتخصيص اللقب، على حسب ما مر، إذ لابد للتخصيص من فائدة عند القوم، إلا الاختصاص بالحكم، والقصر على المذكور.
الرابع- أنا [نبدي للنص] على الموصوف فوائد زائدة على قصر الحكم عليه.
منها- أن ينص عليه، ليخرج [عن] محل الاجتهاد، ويكون الحكم ثابتا فيه بطريق التوقف، حتى يتمكن المجتهد من إخراجه بطريق النظر والرأي.
وفائدة أخرى: وهي أن يحصل أصلا معتبرا في [الإلحاق] به [والاعتبار].','2','321',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1018,'[وثالثة]: أن يرد الخلق إلى القياس [والاستنباط]، ليؤجروا على ذلك، ولولا هذا [لذكر لكل] حكم رابطة كلية، ولكن علم الله تعالى [صلاحًا للخلق] في تحريك [دواعي] المجتهدين في بالبحث والاستنباط، وإبداء [سرائر الشريعة]. ولهذا السر [حسن] استنباط العلة القاصرة، للوقف على حكم الشريعة.
الرابعة: أن يكون الله تعالى إنما تعبده بالنص على هذا الخصوص بالصفة، وما كان له أن [يخالف] أمر الله تعالى، كما أمره [الله تعالى] بذكر الأشياء الستة، فذكرها للتعبد الوارد عليه، لا لقصر الحكم عليها، فكل ذلك ممكن. فكيف يدعي أنه لا فائدة في الشريعة إلا قصر الحكم على المذكور؟ وهذا وهم محض، وخيال ضعيف.
قال الإمام [رحمه الله]: (وقد [حان] أن [نبين مسلك] الحق','2','322',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1019,'على (١١٣/أ) وجه [يشتمل على بيان] المختار) إلى قوله (فهذا منتهى المراد في هذا الطرف). قال الشيخ: اقتصر في هذا [المكان] على التشنيع والتهويل، ولم يأت على المسألة بدليل، غير أنه وقع له الوهم في فهم المسألة. وذلك أنه لا خلاف أن باب الشرط يتضمن اختصاص الجواب به، وأن القائل إذا قال: من أتاني أكرمته، أن هذا الكلام لا يحتمل قط أن يكون وضعه أن يكرم [مكرمه]، ويكرم غيره.
[ومنكرو] المفهوم لا يقولون هذا، ولا يصيرون إليه، فكيف يصح أن يضاف إليهم أنهم يرون استرسال هذا الكلام على [إكرام] الآتي وغيره؟ هذا محال، بل الصحيح أن هذا الناطق لم يلتزم [إلا إكرام] الآتي خاصة. ولا','2','323',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1020,'يكون الإكرام [متلقى] من الشرط، ولكن من الالتزام عند الشرط.
[فالشرط [بين أنه لم [يلتزم] (٩٢/أ) الإكرام لغير الآتي. فإنا قد بينا أن [دلالة] الشرط انحصرت في النفي دون الإثبات، فلو قال: أنا أكرم الناس، لم يختص بآت من غيره، فلما قال: من أتاني، اقتضى هذا الكلام أن غير الآتي لا مدخل له في التزام الإكرام، [فأفاد] الشرط التخصيص خاصة، دون الإعطاء والإكرام. وقد تقدم تحقيق هذا في فصل الشرط، فيكون غير الآتي غير مذكور بحال.
وأصحاب المفهوم [يقولون: ] إنه [قال: ] لا أكرم من لا يأتي، وهذا تحكم محض، وخروج عن حقيقة الشرط. [وحمل] الإمام [على]','2','324',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1021,'منكري المفهوم أنهم يقولون: [إنا نكرم] الآتي وغيره، وليس كذلك. ففرق بين أن يتقاعد اللفظ عن التعرض لغير الآتي، وبين أن يتعرض [لمنعه] وإعطائه. أما إعطاؤه، فلا ذاهب إليه، وأما قصور اللفظ [عنه]، فهو الذي نختاره، وهو الجاري على ذوق الشرط.
وأما [كونه] تعرض فيه للمنع، فبيس كذلك، [ونظير] هذا لو قال القائل: أنا أكرم الصالحين، لم يكن في هذا الإخبار ما يدل على أنه لا يكرم غيرهم، فالتخصيص بالصفة، كالتخصيص بالشرط.
فإذا قال القائل: أنت طالق إن دخلت الدار، فالفقهاء متفقون على أن هذا الكلام لا يتضمن أنه لا يطلقها إذا لم تدخل الدار، بل هذا الكلام يتضمن أنه [لا يلزم] طلاقها بهذا اللفظ إلا إذا [دخلت]، وإذا دخلت، طلقت','2','325',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1022,'بالالتزام، [لا] بالشرط.
فإن قيل: [فقد] قلتم: إن الشرط لا دلالة [له] إلا على انتفاء المشروط، ولا يدل [على [ثبوته، [وإذا صرتم] إلى الكلام إلى أن الطلاق لا يفهم نفيه عند عدم الشرط، فقد نقضتم [ما قلتم]، ورجعتم عما أثبتم. قلنا: ليس الأمر كذلك، [فإنه إذا] انتفى الدخول، [انتفى الطلاق] المعلق عليه، وكذلك إذا قال: إن جئتني أكرمتك، فهذا القول لا تعلق له بغير [حالة] المجيء [بحال. فحالة عدم المجيء مسكوت عنها]، وحالة عدم دخول الدار [مسكوت عنها، باعتبار هذا الكلام، فإن أكرمه، أو طلقها من غير دخول الدار] [أو الإتيان]، لم يكن مناقضا','2','326',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1023,'لشرطه، ولا مخالفا لقوله، [ولا مؤوِّلًا]، بل تصرف في محل مسكوت عنه.
هذا هو [التحقيق] في المسألة، وفيه تقدير الشرط على بابه، وقصر دلالته على النفي عند انتفائه، أي لا تكون [غير] الصورة المشروطة مذكورة بحال. وهذا سر عجيب، فليقف الإنسان عنده ليقف عليه. وأبو المعالي إنما رد الصور كلها إلى هذه الصورة، وقد ظهر بطلانها، فجميع (١١٣/ب) ما يأتي يرجع إليها.
قال الإمام: (ومما نذكره التحديد بالزمان أو المكان أو العدد) [إلى قوله] (وهو من صور المفهوم). قال الشيخ: هذا أيضًا من الطراز الأول، ونحن نسلِّم أن الحدود تتضمن حصر [المحدودات]، وأن المتكلم','2','327',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1024,'[لم] يتكلم إلا بذلك المحدود، ولم يتكلم بغيره بحال.
[مثاله]: إذا قال: اضربه عشر ضربات، أو [اجلده] ثمانين جلدة، فاللفظ [لم يتناول] إلا الثمانين خاصة، (٩٢/ب) وما زاد عليها، فغير مذكور.
وتوهم [الإمام أيضًا] أن منكري المفهوم يزعمون أن اللفظ لا اختصاص له يالثمانين، وذلك خطأ قطعًا، ففرق بين كون اللفظ مقتصرًا على الثمانين، وبين أن يكون صالحًا لا، وللزيادة عليها، وبين أن يكون [مقتصرًا] عليها، لا تعلق له بغيرها. فأصحاب المفهوم يزعمون أنه [دال] على الثمانين إثباتًا، وعلى الزائد نفيًا.
[وإما إضافة] الإمام [إلى منكري] المفهوم يزعم أنه يحمل الدلالة على الزيادة إثباتًا، [فهذا] خطأ قطعًا. ونحن نرى أنه يدل على الثمانين [إثباتًا]، ولا دلالة له على ما زاد عليها، [فيبقى] المحل [مسكوتًا]','2','328',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1025,'عنه، فيثبت بدليله، إما موافقًا [له]، وإما مخالفًا.
قال الإمام: (ومن الصور تخصيص الموصوفات بالذكر) [إلى قوله] (فهذا ما أردناه). قال الشيخ: ما ذكره في الصيغة وتقسيمها غلى المناسب [وغيره]، كلام صحيح، وقد قدمنا (٩٣/أ) في القسم [الثالث] من أقسام المفهوم، فهم التعليل من تعليق الحكم على الصفات المناسبة، كقول القائل:','2','329',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1026,'ذم [الفاجر]، وأكرم المطيع. وقصارى ذلك الكلام، فهم التعليل، وأنه الباعث له على الحكم الخاص، بحيث لم يأمر إلا بذلك المعلول، أو لم يحكم إلا فيه.
[فأما] أن يفهم من هذا أنه يحكم بالنقيض في غيره فلا، إذ لا يستفاد من التعليل إلا معرفة الباعث. فلو قال القائل: أكرم زيدًا، لم يفهم من هذا الكلام أنه [ينهى] عن إكرام غيره، وكذلك في الخبر لو قال: أكرمت زيدًا، لم يتضمن هذا أنه لم يكرم غيره، فإذا كان اللفظ مقتصرًا على زيد [عند] عدم التعليل، فالتعليل لا يصير له مفهوما آخر، لم يكن له قبل ذلك. هذا أمر واضح، ومن زعم أن دلالة اللفظ تزيد عند تعليله، فليس على بصيرة من أمره. فالعام قبل التعليل لا يصير خاصاً بعد التعليل، بالإضافة إلى وضع اللغة، [هذا إذا كانت العلة] تطابق جميع الصور. وكذلك الخاص لا يصير عامًا بحال.
وقوله: (إن الحكم إذا ارتبط بعلة أشعر وضع اللسان بثبوته [بها]','2','330',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1027,'و [انتفائه] عند انتفائها). هذا قد يسلم، [ولكن] هذا كله فيما يتعلق بالمذكور [المعلل] إكرامه بالإتيان، [يعني] أنه إنما التزم إكرامه على تقدير إتيانه. وأما عدم الإتيان، فلم يجر له ذكر، لا بالإكرام، ولا بمنعه. وقد حققنا ذلك في باب الشرط تحقيقًا بالغًا، فلا نعيده.
[وأما] تشبيهه بقوله: (إنما أكرم الرجل لاختلافه إلى). فهذا [أيضًا فيه] خلاف، فإنه إنما تضمن إكرامه عند الاختلاف، ولم يتعرض لحاله إذا لم يختلف. فعلى هذا لا يحصل للإمام مقصود. وإن سلمنا - وهو الصحيح -[فـ) إنما)] كلمة حصر عند أهل اللغة تتنزل منزلة النفي والإثبات في أبواب الاستثناء، قال الله','2','331',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1028,'[سبحانه]: {إنما الله إله واحد}. وأراد قصر [الألوهية] عليه، واختصاصه [بها]، وينزل (١١٤/أ) ذلك منزلة قوله: {لا إله إلا الله}. فالاعتماد في [مسألة التمثيل] في صورة التعليل على حرف الحصر، وهي (إنما)، لا على مجرد لفظ التعليل. وإنما صورة المسألة أن يقول القائل: أكرمت زيدًا، لاختلافه إلي، فإن هذا لا يتضمن منعًا من إكرامه إذا لم يختلف، ولا تحقيق الإكرام، بل [هذه] حالة مسكوت عنها. على ما تقدم [فيما مضى من] الأبواب.','2','332',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1029,'قال الإمام: (فإن قيل: خصصتم [بالذكر] الصفات [المناسبة للأحكام]) إلى ذكره ([الدقاق]). قال الشيخ. [أما] تفرقة الإمام بين الصفة المناسبة وغيرها، فهو صحيح على أصله، فإنه لم يصر إلى القول','2','333',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1030,'بالمفهوم، بناء على طلب التخصيص، لانتقاضه بتخصيص اللقب، فسلك مسلكًا آخر، وهو الصفة المناسبة، لتفهمه التعليل، ويتلقى المفهوم من التعليل. على حسب ما [مضى].
وقوله: (ومن سر هذا الفصل) إلى آخره. [كلام] يفيد ظاهره الفرق بين العلة المستنبطة والمومأ إليها، وليس الأمر (٩٣/ب) كذلك، وإنما [اشترطت] تلك الشروط في المستنبطة، ليثبت كونها علة، فإن الناظر عند اطلاعه على الوصف المناسب مثلًا، يجوز كونه علة، ويغلب ذلك على ظنه، إن وجد بقية شروطه، فكانت الشروط في المستنبطة، ليثبت بها التعليل. أما النص فقد [أغنى] عن شروط الاستنباط، [وقد ثبت، فمالنا نطلب شروط ثبوته؟ فهذا فرق بين الاستنباط] والنص، [وفرق] بين المستنبطة والمنصوص عليها.','2','334',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1031,'هذا تمام ما اشتمل عليه الكتاب، وبقي علينا أن ندل على إبطال المفهوم، على حسب ما نختاره. وكلامنا مخصوص بما إذا لم يوجد سوى تخصيص الشيء بالذكر من غير دليل زائد، ويدل على بطلانه مسالك:
الأول: أن نقول: إثبات الحكم [للمنطوق] به ثابت، أما نفيه عما سواه، تلقيا من ذكره، واستنادًا إليه، [فلا] يعلم إلا بنقل من أهل اللغة، [متواتر] أو جار مجرى التواتر، [والجاري مجرى التواتر]: هو أن يطرد الأمر في لغتهم اطرادًا لا [يقتصر]، كأسماء الفاعلين والمفعولين، وكأمثلة المبالغة، [كضروب وقتولٍ]، والأعلم والأفضل، وإن كان هذا قد يستعمل بمعنى العالم، ولكن [الغالب] المبالغة والإشعار بالزيادة.
أما نقل الآحاد، [فلا يكتفي به] [عند كثير من الأصوليين. قالوا: إن إثبات لغة ينزل عليها كتاب الله وسنة نبيه بقول الآحاد، لا يكفي]. وهذا قد ذكره القاضي وغيره. وهو اقتصار منهم على مجرد الدعوى، معراة عن','2','335',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1032,'الدليل، وأئمة العربية متفقون على خلاف ذلك.
هذا سيبويه - على جلالة قدره في هذا الفن - يرجع إلى ما ينقله [الخليل] وأبو عمرو. فإن قيل: لا يصار إلى القوم فيما يتعلق بالأعمال. قلنا: لو نقل سيبويه أن أبنية السلامة من أبنية القلق، [وكذلك] غيره من أئمة العربية، ولم يبلغوا عدد التواتر، لصير إلى قولهم في ذلك، وكذلك أبو عبيدة وغيره. وأكثر تفسير ألفاظ الكتاب [وغيره] رجعت إلى قول طائفة من هؤلاء. [واشتراطه] النقل المتواتر في [تفسير] كل لفظة من كتاب الله عز وجل متعذر، لا شك فيه، لاسيما عند [بعد] الأعصار، وفساد لغات العرب، [فتكليف] [أهل] العلم بالنقل المتواتر في كل كلمة تكليف شطط.
فالصحيح عندي الاكتفاء بغلبات الظنون فيما يتعلق بفهم آحاد الكلمات، ولا ينفك أحد من الأئمة عن التمسك في آحاد كلمات العربية بقول بعض [أئمتها].','2','336',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1033,'المسلك الثاني: حسن الاستفهام، (١١٤/ب) فإن من قال: إن ضربك زيد عامدًا فاضربه، حسن أن يقال: فإن ضربني خطأ، فهل أضربه؟ وإذا قال: أخرج الزكاة من سائمتك، حسن أن يقال: وهل أخرجها من المعلوفة؟ وحسن الاستفهام [بذلك] يدل على أن ذلك غير مفهوم، فإنه لا يحسن في المنطوق، وحسن [في المفهوم] المسكوت عنه.
المسلك الثالث: أنا نجدهم [يعقلون] الحكم، تارة على الصفة، مع مساواة المنطوق به المسكوت عنه، وتارة يقصدون القصر عليه، فالثبوت للمنطوق به قد عرف، فأما الاقتصار عليه، [فدعوى] عرية عن [التحصيل، يعارضها عكسها من غير ترجيح.
المسلك الرابع: أن الخبر عن] ذي (٩٤/أ) الصفة، لا يتضمن نفي [غير] الموصوف؛ فإذا قال: قام الأبيض وخرج وقعد، لم [يدل] على نفيه عن [الأسود]، بل هو [سكوت] عن [الأسود]. وإن منع ذلك','2','337',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1034,'مانع - وقد قيل [به]-[لزمه] تخصيص اللقب [والاسم] العلم، حتى يكون قوله: رأيت زيدا، نفيا للرؤية عن غيره، وإذا قال: ركب زيد، اقتضى ذلك أنه لم يركب غيره.
وقد [التزم] هذا بعض أصحاب المفهوم، وهو بهت، واجتراء على أهل اللغة. ومتضمن هذا أن القائل إذا قال: محمد رسول الله، أن يكون كافرًا، لأنه نفى الرسالة [عن موسى] وعيسى [وغيرهما من الأنبياء [عليهم السلام].
فإن قيل: هذا إنما يلزم في اللقب بخلاف الصفة. قلنا: طلب فوائد التخصيص فيهما جميعًا على حد واحد، [فاللقب] لتعريف العين، [والصفة] لتعريف الجنس. وقوله: (في الغنم زكاة) [في] الاقتصار عليها، وعدم التعرض لغيرها، كقوله: (في السائمة الزكاة) في عدم التعرض للمعلوفة.','2','338',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1035,'[المسلك] الخامس: أنا نعلم أن العرب كما أخبرت عن الأشخاص والأعيان بأسمائها، فاقتضى [ذلك] قصرًا عليها، وكذلك أخبرت عن الموصوفات بصفاتها، [فليقتض] ذلك اقتصارًا عليها. ولو قال القائل: تزوجت امرأة ثيباً، ثم قال بعد ذلك: [تزوجت] بكرًا، يعني [غير] الأولى، لم يكن كاذبًا، ولا كان قوله متناقضًا. وكذلك لو قال: اشتريت سائمة، وكان قد اشترى معلوفة [أخرى]، لم يكن كاذبًا. وكذلك [في] النخل المؤبرة وغيرها من الموصوفات. ولو كان ذلك يقتضي حصرًا وقصرًا، لكان ذلك كذبًا منه.
فإن قيل: هل تدل على القصر دلالة ظاهرة وتقبل التأويل؟ قلنا: فإذا كان كذلك، فينبغي أن نظن به الكذب، حتى [تبين] جهة التأويل، وليس [الأمر] كذلك على حال. فتقرر بهذا كله أن ذكر الشيء [مختصًا]، لا يتضمن نفي غيره أصلًا، اللهم إلا أن تظهر قرينة حالية أو مقالية [والمقالية] كلفظ (إنما) المؤذن بالحصر، [أو لفظ] (إلا) كما في قولهم: قام القوم إلا زيدًا.','2','339',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1036,'وهل يكون [مد] الحكم إلى غاية بضيغة (إلى) و) حتى) من جملة القرائن اللفيظة [الدالة] على القصر والاختصاص، وثبوت النقيض [لما] بعد الغاية والحد؟ هذا مما اختلف فيه من رد المفهوم. ذهب القاضي إلى أن ذلك يدل، وقوله تعالى: {[ولا تقربوهن حتى يطهرن}. يدل عنده على تحريم الوطء قبل انقطاع الدم، وعلى جوازه بعد الانقطاع والغسل، حتى أنه لو لم يرد قوله: {فإذا تطهرن فأتوهن}، لكان ذلك يفهم من مجرد قوله: {حتى يطهرن}.
وقال أبو حنيفة وجماعة من المنكرين للمفهوم: [هذا] نطق بما قبل الغاية، وسكوت (١١٥/أ) عما بعد الغاية، فيبقى على ما كان عليه قبل النطق. واحتج بأن قوله تعالى: {فلا تحل له من بعد حتى تنكح زوجًا غيره}. و} حتى يطهرن}، ليس كاملا مستقلا، [فأنه] لم يتعلق بقوله} ولا تقربوهن}. فيكون لغوا، وإنما يصح لما فيه من إضمار، وهو قوله: (حتى يطهرن فاقربوهن)، و) حتى تنكح زوجا [غيره] فتحل).','2','340',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1037,'وبهذا يقبح (٩٤/ب) الاستفهام إذا قال: لا تعط زيدا حتى يقوم، فإذا قال: أفأعطيه إذا قام؟ فلا يحسن، إذ معناه: أعطه إذا قام. لأن الغاية نهاية، ونهاية [كل] شيء [مقطعه]. فإن لم يكن [مقطع]، فلا [يكون] نهاية. فإذا قال [له]: اضربه حتى [يتوب]، فلا يحسن معه أن يقول: وهل أضربه إذا تاب؟ هذا تمام كلام القاضي. وفيه [نظر].
أما قوله: {حتى يطهرن}، لا يكون كاملا إلا بارتباطه بقوله: {ولا تقربوهن}، فهو صحيح، لأن (حتى) حرف غاية، والغاية لا تتصور إلا بذي ابتداء، فكيف يكون قوله: {حتى يطهرن} كلاما مستقلا؟ هذا محال. فقال: إذا لم يكن كاملا إلا بانضمامه إلى قوله: {حتى يطهرن}} فأتوهن}. فلابد من إتمام، فإما أن يكون المراد: (حتى يطهرن فلا تقربوهن)، وذلك محال، أو يكون الحكم [مستمرا]، ولا يكون الطهر غاية، فلم يبق إلا أن يكون المراد: (حتى يطهرن] فأقربوهن).
وهذا الذي قاله غير لازم، وليست القسمة محصورة في أن [يكون]','2','341',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1038,'{[ولا] تقربوهن}، بل يكون المعنى: (فإذا تطهرن انتهى النهي). ولا يكون ما بعد الطهر منهيا عنه، [ولا] مأذونًا فيه، بل أفادت (حتى) انتهاء التحريم خاصة، كما [أفاد] الشرط قصر الحكم على حالة وجود الشرط.
وأما ما وراء الشرط، فليس فيه حكم، لا بالنفي، ولا بالإثبات. هذا يتوجه على القاضي، إلا أن ينقل أن أهل اللغة جعلوا [ما] بعد الغاية ضد الحكم المذكور قبلها، ولم يجعلوه مسكوتًا عنه على حال، فيسند الحكم إلى [النقل]، لا إلى فقه [حروف] الغاية.
وأما الأمثلة التي استشهد بها في قبح الاستفهام في الصورة المذكورة، فلعمري إنه يقبح الاستفهام فيها، [لا لمحض] الغاية، بل لأجل المناسبة [الحاصلة]، فإن الضرب وتعليقه على التوبة، يقتضي منعًا من الإقدام عليه عند وجودها. وإنما يكون الاحتجاج مستقيما، لو أتى بشيء لا مناسبة فيه، مع تسليم قبح الاستفهام، فحينئذ ينتفع بذلك. والمخالفون لا يسلمون ذلك بحال، فليست هذه المرتبة بمثابة ما تقدم من قوله: (إنما الماء من الماء). (وإنما العالم زيد). والله الموفق للصواب.','2','342',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1039,'قال الإمام [رحمه الله]: ([المسألة الثانية المعقودة على الدقاق]) إلى قوله (وحصر المفهوم فيما [يناسب]. [فهذا منتهى الكلام]). قال الشيخ: ما ذكره الأئمة من الرد على الدقاق صحيح، ومراغمته [للشريعة','2','343',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1040,'واللغة] ثابتة، والإلزامات عليه شديدة، واعتذار الإمام عنه بارد، فأن الأئمة لم ينكروا أن الإنسان لا يتكلم إلا لفائدة، ولا [يخصص] [إلا لغرض]، هذا ضرورة كون المتكلم [عاقلا]. [وإنما] الذي أنكره الأصوليون ذهاب','2','344',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1041,'الرجل إلى تعيين الغرض في مخالفة المسكوت عنه المنطوق به في الحكم، هذا هو الذي أنكروه، بل زاد الإمام عليهم زيادة، وقال: [بل] لا يجوز أن يكون قصر الاختصاص أحد الأغراض.
وهذا الذي قاله (١١٥/ب) الإمام [يصح] على وجه، ويبطل على وجه، فإن أراد أن لفظ التخصيص [بما يذكر] لقبا، لا يقتضي نطقا في خلافه [قصر] الحكم، [فهو] (٩٥/أ) صحيح، وإن أراد أن ذاكر اللقب لا يصح منه قصد قصر الحكم [على المذكور] فهذا غير صحيح، [والظاهر] [أنه] إنما أراد القسم [الأول].','2','345',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1042,'وأما بقية كلامه، فهو إعادة ما مر من [المذاهب] في المسألة، وقد تبين اختيارنا فيها، وأنا لا نرى محض التخصيص بالذكر يقتضي نفي الحكم عن غير المخصوص على حال. والمواضع التي اعترفنا فيها بالمخالفة، إنما كان ذلك لزيادات وضعت عند أهل اللسان، مشعرة [بالقصر]، [كـ) إنما) و) إلا)]. وقد أبدينا في (حتى) التي للغاية [احتمالا]. والله الموفق للصواب.
قال الإمام: (مسألة: [قد ذكرنا] أن المفهوم [ينقسم] [إلى ما يقع','2','346',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1043,'نصا، غير قابل للتأويل، ويغلب ذلك في مفهوم الموافقة]) إلى قوله (المفهومات). قال الشيخ: لا يكاد عند القائلين [بمفهوم] المخالفة أن [يرقى] الأمر [إلى] حد يحصل [العلم]. وإنما هو عند القائلين به ظاهر، ولكن كل ظاهر بالضرورة لابد فيه من جهة مقطوع بها، هي التي لا يتوصل [إلى] إزالتها بوجه، [والعموم] لا يسقط جميعه، حتى [يصير] اللفظ لغوًا، وكذلك [صيغة] الطلب، فكل ظاهر فلا بد فيه من','2','347',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1044,'ثلاث جهات: مقطوع بها، وهي أصل الدلالة، ومظنونة، [وهي] جهة الظهور، وبعيدة، وهي جهة التأويل، فبالإضافة إلى جهة القطع، يمنع التصرف بالإسقاط، وبالإضافة إلى جهة الظهور، [يكتفي] في مسائل الظنون بالسبق إلى الفهم. وبالإضافة إلى جهة الاحتمال، يصح التأويل، والعضد بالدليل الراجح، [ليصار] إلى تلك الجهة في مسائل الظنون. ومجرد الاحتمال يمنع التعلق بجهة الظهور [في] مطالب العلم. وإذا قضينا بأن المفهوم ظاهر عند مثبتيه، فهل يصح إسقاطه بجملته، حتى يكون كإزالة الظاهر، [أو إنما] يؤول، حتى يرد إلى البعض، كما في تخصيص العموم؟ قلنا: يصح إسقاطه بجملته.','2','348',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1045,'فإن قيل: هذا تعطيل، [وتناقض] لما سبق، فإنكم قلتم: كل ظاهر لابد فيه من جهة مقطوع بها، [وإذا] أسقط المفهوم بكماله، لزم أن تسقط الجهة المقطوع بها. قلنا: ليس كذلك، فإن المفهوم لا استقلال له بنفسه، وإنما هو مستند إلى لفظ آخر، فإذا دل دليل على إسقاط المفهوم بكماله، بقي اللفظ معتمدا فيما دل عليه نطقا، فخرج [اللفظ] عن كونه لغوًا، لبقائه دليلا على ما تحته.
وأما العموم، [فإذا] أخرج [كل] ما دخل [تحته]، بقي اللفظ','2','349',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1046,'لغوًا، وبطلت جهة النصوصية منه قطعا، فكان المفهوم [كصورة] من صور العموم، فكان ترك المفهوم بمثابة تخصيص العموم، وليس كترك جميع مقتضى [اللفظ]، فكان تركه من السائغ الذي [لا يستنكر].
والسبب فيه عند مثبتيه: أنهم رأوا العرب تخصص الشيء بالذكر، [وهي تقصد] مخالفة [المسكوت] عنه المنطوق به، [وتارة] لا [تقصد]','2','350',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1047,'ذلك، فلم يكن في ترك المفهوم خروج عن مقتضى اللسان. (٩٥/ب)] كما [أنها تستعمل لفظ العموم تارة مستغرقًا، وتارة غير مستغرق، فكان ترك المفهوم، وتخصيص العموم، من السائغ الذي لا يستنكر. وقد تقدم الكلام على أنه إذا دل دليل على إخراج صورة من صور المفهوم، فهل يسقط المفهوم] بالكلية [؟ أو يتمسك] به [(١١٦/أ)] في البقية [؟ وهذا على القول بأن العموم إذا خص لا يكون مجملا.
أما من ذهب إلى أن إخراج صورة من صور العموم، توجب الإجمال،','2','351',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1048,'[وتمنع] التمسك باللفظ فيما وراء المحل المخصوص، فلأن يقول ذلك في المفهوم، إذا تركت منه صورة بدليل أولى.
أما من لا يرى] أن [التخصيص في العموم يوجب إجمالا، فهو] لا يتردد [-] إذا دل [دليل على إسقاط المفهوم - في صورة] أن يتمسك بغيرها [.
أما الشافعي فمقتضى مأخذه] ترك [المفهوم بالكلية، لأنه إنما يتلقاه','2','352',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1049,'بالنظر إلى فوائد التخصيص، ] وأنه [لا فائدة إلا] مخالفة [المسكوت عنه] للمنطوق [به.] وإذا [ثبت أن بعض المسكوت عنه يوافق المنطوق به، بطل أن تكون تلك هي الفائدة، فتطلب فائدة أخرى. وإنما نحن لو كنا من القائلين بالمفهوم، لأثبتنا له] ظهورا [شاملا، فإذا دل دليل على سقوط المفهوم في بعض الصور، بقينا على التمسك بالمفهوم] في بقيتها [، ويكون ذلك بمثابة تخصيص العموم.] الأجرم [نقول بالمفهوم بعد التخصيص في المواضع] التي [اعترفنا بفهم المخالفة فيها، كما إذا قال: إنما العالم زيد،','2','353',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1050,'أو لا عالم إلا زيد.] فإذا [دل] الدليل [على إثبات عالم غير زيد، اقتصرنا في الإثبات على ما أثبته الدليل الجديد، وبقينا على النفي فيما سواه.
والدليل على ما اخترناه: أن اللفظ أشعر بنفي عام، ] وإنما [استثنى في الخطاب الأول صورة واحدة، فلم يبق اللفظ شاملا] إلا لنفي [ما سواها. واللفظ الشامل في النفي إذا أخرجت] منه [صورة، ] بقي على العموم [فيما سواها. هذا هو التحقيق عندي في ذلك.
وعلى هذا] تقبل [نية التخصيص فيما إذا حلف، وقال: لا أكلت إلا السمك مثلا، ونوى تخصيص] النفي [بغيره، لقبل] ذلك منه [في الفتيا.
نعم، قد لا يقبل منه ي القضاء فيما] يجيز [عليه، تعلقاً بظاهر اللفظ. هذا','2','354',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1051,'هو الحق، والمسلك الواضح.
] فأما [الأمر الثاني: وهو أن] الشافعي [يسقط] التعلق [بالمفهوم] إذا [جرى التخصيص على وفق العرف، ومعنى ذلك أن يكون الجاري] المعتاد [هو الحالة المخصوصة بالذكر، ] وما [سوى تلك الحالة، ] فنادر [، كالشقاق] المذكور [في الخلع، وكاستشهاد النساء في الدين، وكخوف المسافر، وكقصر الصلاة عند الخوف، وكتزويج المرأة نفسها، ] إذا [الغالب أنه لا تفعل ذلك إلا عند الالتفات إلى الأولياء. فهذه الجهات هي الغالبة المعتادة.
وكذلك القائلون] بمفهوم اللفظ [أقروا بأنه لا مفهوم لقوله: (صبوا','2','355',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1052,'عليه ذنوبا من ماء). ولا لقوله: (] فليستنج [بثلاثة أحجار). لأن هذا إنما ذكر، أنه الغالب المعتاد عندهم.) ٩٦/أ) وإنما صار الشافعي في القول المشهور إلى ترك المفهوم في هذه الصورة، ] جريا [على أصله، في كونه أثبت المفهوم، بناء على طلب الفائدة، وحصرها في مخالفة المكسوت عنه] المنطوق [به، فإذا ظهرت فائدة، وهي إجراء] الكلام [على مقتضى العرف، إذ قد يعرض عن النادر، ولا يخطر بالبال، وإن خطر، فقد يعبر عن الغالب، ويقصد بنفيه] النادر [.
وإنما] أنتج الكلام [القول بالمفهوم، عند حصر الفائدة في المخالفة، ] وعند إمكان فائدة أخرى، يفوت الحصر، وهو إحدى مقدمتي الدليل، فلا','2','356',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1053,'ينتج. وهل [تكون هذه الفائدة تقتضى إجمالًا، حتى لا يحكم بمخالفة ولا موافقة؟ أو تقتضي] تضعيفًا حتى يقل الظهور، مع بقاء الأصل، فتظهر (١١٦/ب) الفائدة عند] وجود [المعارض، حتى لا يطلب دليل بالغ في القوة؟ أو يكون هذا قرينة تقتضى أن يكون السكوت عنه مساوياً للمنطوق به؟ وهذا أضعف الأوجه. والمصير إلى الإجمال هو الجاري على قاعدة الشافعي، والضعف والنزول عن قوة الظهور، هو مقتضى قاعدة الإمام، وقد تبين وجهه.
وإنما قلنا: إن المصير إلى مساواة المسكوت عنه [المنطوق به] ضعيف، من جهة أنه إثبات حكم غير مستند [للفظ] عام، ولا [لطلب] فوائد التخصيص، أما اللفظ فقاصر عن الدلالة، موافقة على المسكوت عنه.
وأما طلب فوائد التخصيص، فلا ترشد إلى الاستواء بحال، بل ترشد إلى','2','357',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1054,'المخالفة. ويمكن أن يكون مستندهم من حيث الجملة، استقراء المخصصات على وفق العرف، [ومصادقة] موافقة المسكوت [عنه] للمنطوق به. وهذا كقصر الصلاة في السفر خوفًا وأمنًا، وجواز المفاداة حبًا وبغضًا، وثبوت الهلكة إلا ما وقى الله عز وجل حضرًا وسفرًا. فلما استقرت المساواة على الإطلاق في هذه المخصوصات، اعتقد أن ذلك مقتضى اللغات، وليس كذلك، فإنما تثبت المساواة في هذه [الجهات] [بدلالة] خاصة، دلت على التسوية.
وأما قول الإمام: (ومن [حسائك] الصدور ترك المفهوم في مسألة النكاح بلا ولى). [فإنما] صعبت المسألة عليه، لأنه يرى أن الصفة إذا','2','358',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1055,'كانت مناسبة، اقتضت اختصاصًا، ويرى أن تزويجها نفسها بغير إذن وليها، مناسب [لفسخ] النكاح، نظرًا إلى افتياتها على [أوليائها]، وهذه المناسبة ضعيفة، فيما يتعلق بفساد النكاح، فإنها لو وضعت نفسها في أفضل الناس وأحسنهم حالًا، لم يلحق الأولياء من ذلك نقص، إلا من وجهة كونها باشرت العقد بنفسها، فإنها تنسب بذلك إلى شهوة الرجال، وعدم الحياء، والخروج عن دأب الخفرات.
وإذا كان هذا هو السبب، فهو موجود، وإن [أذن الولي]، بل يزداد الأمر قبحًا، من جهة عدم غيرة الولي حتى أذن لوليته في مباشرة العقد، فهو [بالفسخ] أولى. وأما نحن فقد استقر من أصلنا أن لا نقول بالمفهوم على حال. فحالة إذن الولي مسكوت عنها على حكمها من دليلها، وقد أرشدنا إلى الدليل المقتضي لإبطال النكاح الآن. وهذا أيضًا (٩٦/ب) حجة واضحة على إبطال المفهوم، فإنه إذا ظهر مثل هذا الغرض، بطل القول بالمفهوم. فإذا لم يظهر، فمن أين [يعلم] انتفاؤه؟ إذ البواعث [على] التخصيص كثيرة، وقصد قصر الحكم أحدها، وقد سبق بيان ذلك، فلا نعيده.
فإن قيل: فلو قطعنا بانتفاء الفوائد سوى المخالفة في الحكم، فماذا تقولون: أتثبتون المفهوم أم [تنفونه]؟ [فإن] نفيتموه، فهو باطل، إذ [فيه','2','359',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1056,'إخراج] الكلام عن مقصد العقلاء، وهو بمثابة ما إذا قال القائل: السودان [إذا] عطشوا [لم] يروهم إلا الماء، مع القطع بأم من سواهم كذلك. وهذا لا يقوله القائل إلا تبين هزله وهزؤه أو جهله، [وفساد عقله]. وإن اعترفتم بالتخصيص عند عدم الفائدة، فإذا لم تظهر، فالأصل عدمها، فيجب القول بالمفهوم حتى يظهر أمر [آخر]، [غير] مقصد المخالفة. وأما ترك المفهوم [لمحض] الاحتمال، فلا وجه له. (١١٧/أ) قلنا: هذا خيال باطل.
وقول القائل: إذا لم تظهر الفائدة، فالأصل عدمها، كلام ضعيف، ومن سلم أن الأصل العدم؟ ولو صح هذا، لجاز للمجتهد عند مصادقة العموم [أو غيره، أن يبادر إلى الحكم به، بناء على أن الأصل عدم المخصصات، ففرق بين أن يثبت العدم]، [وبين] [أن لا] يعلم الثبوت. فإذا ثبت العدم في مسائل التقسيم، صح الاعتماد، وإن لم [يحصل] الثبوت، لم يجز [الإعدام].','2','360',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1057,'وأما قول [الإمام]: (القائل: السودان إذا عطشوا لم يروها إلا الماء فهو قبيح لا شك فيه). لا لأجل مساواة المسكوت عنه المنطوق به، ولكن لكونه أخبر عما لا فائدة فيه. هذا تمام الكلام في المسألة.
قال الإمام: (ما صار إليه المحققون [أن قوله صلى الله عليه وسلم: (تحريم الصلاة التكبير وتحليلها التسليم))] إلى قوله] (بكتاب التأويلات). قال الشيخ: ما ذكره الإمام في المثال الأول، وهو قوله [- عليه السلام -]: (تحريمها التكبير وتحليلها التسلم). من أن ذلك يتضمن حصر الصلاة وقصرها على','2','361',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1058,'هاتين [القضيتين]، حتى لا يقوم لفظ آخر مقامها، [وقواه] [بوجهين].
[أحدهما -] ما ادعاه من الفرق بين قول القائل: زيد صديقي، من أن ذلك [لا يتضمن] أنه لا صديق له غيره، بخلاف قوله: صديقي زيد، فإن ذلك يتضمن قصر الصداقة على زيد، [ولا] يصح [على] هذا أن يكون له صديق غيره. وقرره [بأن] المبتدأ [وضعه] أن يكون [معرفا]،','2','362',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1059,'حتى إذا فهم، [استند] إليه [الخبر الملتبس]، فوضع الكلام أن يقول: زيد صديقي، فإذا [قلب] الكلام وقال: صديقي زيد إلى آخره. [كلام عندي] في غاية الضعف؛ وذلك أن حق المبتدأ بالأصالة أن يكون معرفة أو قريبًا منها. وقد تكلمنا [على] المواضع التي [يحسن] الابتداء بالنكرة فيها. [ومن] حق الخبر أن يكون نكرة، [فإنه] محل [اللبس]','2','363',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1060,'والفائدة. وقد يجيء [المبتدأ] نكرة [والخبر] معرفة، وهذا قليل جدًا وقد قال حسان [بن ثابت]:
كأن سبيئة من بيت رأس *** يكون مزاجها عسل وماء
وشجعه (٩٧/أ) على ذلك [أمن] اللبس. [وقد تجيئان] [معرفتين]، وإذا [كانتا] كذلك، فالناطق [بالخيار]، يجعل [أيهما','2','364',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1061,'شاء] الاسم، والآخر بالخبر، إلا أن [الأحسن] أن يجعل الأعراف اسماً. وهذا حسن، وليس بلازم. وإذا كان [كذلك]، فقوله: صديقي زيد، هذان [معرفتان]، [والناطق] بالخيار يجعل [أيهما شاء] الاسم، والآخر الخبر. وقد قال أئمة العربية: إن حكم المضاف إلى [المعرفة] حكم ما يضاف إليه. والمضمر عند سيبويه أعرف من العلم. والمضاف إليه ينبغي أن يكون أعرف.
وإذا كان كذلك، فيكون الأحسن عندهم أن يقول القائل: صديقي زيد. وأبو المعالي يرى أنه [ما حسن] الابتداء به [إلا قصد] [قصر] الصداقة عليه. وهذا لا يقوله أحد من أهل الفن، أعني أئمة العربية.','2','365',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1062,'والصحيح أنه لا فرق في الفهم بين أن يقول: صديقي زيد، أو زيد صديقي. وقد قال [هو (إن] هذا المعنى لا يفضي إلى القطع بنفسه). [قلت]) [ولا إلى غلبة] الظن بحال.
وأما أبو حامد، فقد حكم بهذا الحكم، وسلك قريبًا من هذا الطريق، والتزم الفرق بين قول القائل: زيد صديقي، وبين قوله: صديقي زيد، وزعم أن إحدى الصيغتين تتضمن الحصر دون الأخرى. (١١٧/ب) لكنه قال: السبب [فيه] أن المبتدأ لا يصح أن يكون أعم من خبره، بل إنما يكون مساوياً له، [أو] زائدًا. وتمسك في ذلك بأمثلة لم يحط بمعناها، وهي كلام أصحاب','2','366',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1063,'المنطق، [الذين] لا يعرفون لغة العرب.
قال: يصح أن القول القائل: الإنسان حيوان، لأن الحيوان أعم من الإنسان، ولا يجوز أن يقول: الحيوان إنسان، لأن الإنسان أخص. فرتب على ذلك أنه إذا قال: صديقي زيد [وضع] زيدًا [موضع] الخبر، فلا يجوز أن يكون [له] صديق غير زيد، [إذا] يلزم منه أن تكون الصداقة أعم من زيد، وقد وقع الصديق موقع المبتدأ، [وذلك محال]. فأما إذا قال: زيد','2','367',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1064,'صديقي، فـ (زيد) مبتدأ، فلا يضر أن يكون له صديق غيره، إذ ليس فيه أكثر من [خصوص] المبتدأ، [وعموم] الخبر، وذلك صحيح.
[هذا] تقريره. وهو عندنا في نهاية السقوط، وليس هذا [قول] من [شدا] طرفا من العربية بحال. والخبر عند أئمة العربية هو المبتدأ بعينه، أو منزلًا [منزلته] على طريق التجوز والاستعارة:
[فالأول - قولنا: زيد قائم، والله ربنا، ومحمد نبينا. والثاني - كقولنا: الشافعي مالك]، أي يقوم مقامه [ويسدد مسده]. وإن أخبر [عن المبتدأ] بجملة، فإنها تؤول إلى مفرد، ليقع خبرًا عن المفرد، وكيف لا يكون كذلك؟ لأن الشخص إنما يخبر عنه بأحواله أو بشيء من سببه. فاعتقاد خصوص المبتدأ، أو عموم [الخبر] عين التخليط.
وأما المثال [الثاني] الذي ذكره [في] صحة قول القائل: الإنسان','2','368',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1065,'حيوان، [وأنه] أخبر بعام، فباطل قطعًا، وذلك أنه إن قصد الإخبار عن الإنسان [بعموم] الحيوان، فقد أخبر عنه بكونه حمارًا [أو فرسًا]، وذلك باطل، فالمراد: الإنسان حيوان مخصوص، لا عموم الحيوان ولا مطلقه. وإذا قال: [الحيوان إنسان]، إن أراد بالألف واللام العهد، ص (٩٧/ب) ذلك، إذ يكون المبتدأ هو الخبر، وإن أراد بالحيوان العموم، فهو باطل، إذ يصير التقدير: كل الحيوان إنسان، وذلك باطل ضرورة.
[والتحقيق] فيه أن الخبر هو المبتدأ مطلقًا، ويرجع [الأمر] في المثال إلى غرض الناطق في قصد الشمول فيهما، أو الاختصاص لهما، [أو الشمول] في أحدهما، والاختصاص في الآخر. [هذا] تحقيق المسألة.
ومن الضلال البين تفسير العربية على اصطلاح أصحاب المنطق، [وهو] محاولة تفسيرها بالعجمية. ولقد [تعجبت] ممن يريد أن يتكلم في حقائق الأصول على مقتضى اللغة العربية [بمثل هذا الوهم، والخيال الباطل.
وأما القسم] الثاني: وهو قوله - عليه السلام -: ([إنما] الشفعة فيما لم','2','369',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1066,'يقسم). فهو من الحصر بلا ريب. وقد تقدم هذا في مراتب ما نقول به من التخصيصات. [وقصر] [القاضي] [الاعتراف] على هذا. [وأما] قوله: (الشفعة فيما لم يقسم)، فأنكره.
والذي عندي فيه التفصيل، فإن قوله: (الشفعة فيما لم يقسم). هو اسم مفرد فيه الألف واللام، فإن ثبت أنه لتعريف معهود، فلا عموم له بحال، [وإن] ثبت أنه للجنس، فيكون المراد به الاستغراق، [فيصير] بمثابة قوله: (الشفعة في [كل] ما لم يقسم). وهو لو صرح بذلك، [لانحصرت] الشفعة فيما لم يقسم، فلا [تصادف] الشفعة في المقسم بحال، فيكون هذا حصرًا وقصرًا، بالنظر إلى العموم الثابت في [لفظ] الشفعة، لا من جهة التخصيص (١١٨/أ) بالذكر. هذا تمام ما أردناه في هذه الفنون.','2','370',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1067,'قال الإمام [رحمه الله]) (القول في أفعال رسول الله صلى الله عليه وسلم) إلى قوله (والظواهر [مشعرة بوقوعها منهم]). قال الشيخ [وفقه الله]: افتقر إلى الكلام في أفعال رسول الله صلى الله عليه وسلم، لأن من العلماء من قدرها [مأخذًا] للأحكام. فافتقر إلى الكلام عليها لذلك، ليتبين ما يصح من ذلك. [وإذا ثبت] أنها','2','371',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1068,'مستند الأحكام، فعلى أي وجه يصح الاستناد إليها؟ وقدم الكلام على العصمة لغرض له، وذلك أنه يقول: إذا لم [يبعد] وقوع الذنب من الرسول، فلا يتخيل عاقل وجوب المتابعة، وسنتكلم عليه.
وأما العصمة عما يناقض مدلول المعجزة، فلا إشكال فيه، ولو لم يعصم [الرسول] [عن] الكذب الذي دلت المعجزة على نفيه، لما كانت المعجزة دليلًا.','2','372',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1069,'وأما الكبائر، فمن ذهب إلى أنها مستحيلة عقلا، فليس على بصيرة. وغايته أن يتمسك [بأمر] ويحظر، ويقول: كيف يتصور مع كمال المعرفة وحصول [المراقبة] المجاهرة بالكبائر؟ وهذا وإن كان بعيدا، [إلا أن] العقل يجوزه، وقد يصادف في الوجود من يتجرأ على هواه، وإن كان السيف على رأسه. نعم، هي ممتنعة بالإجماع، على حسب ما قاله القاضي.
وأما الصغائر، فقوله: (ففي إثباتها كلام أولًا). قد ذهب بعض الناس إلى أن الذنوب (٩٨/أ) كلها كبائر، وإن كان بعضها أكبر من بعض، نظرا إلى','2','373',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1070,'عظمة المخالف. وكم من أمر يسير بين الاكتفاء، إذا جرى في حق المكل [تضرب] له الرقاب، فلا ينظر إلى صغر الذنب، ولكن ينظر إلى عظمة من يعصى به. وهذا هو اختيار الإمام في [) الإرشاد)]. وإلى هذا ذهب الخوارج، ولكم زادوا زيادة أخرى، وهي أنهم كفروا مرتكب الكبيرة. والذنوب عندهم كلها كبائر، فأفضى الكلام إلى تكفير كل عاص.
وهل هذا يرجع إلى كفران النعم، [أو] إلى الكفر [بالله] على الحقيقة؟ فقال بعضهم: هو كافر على الحقيقة، [مستوجب] للخلود في النار. وقال بعضهم: إنما هو من كفران النعم. فالذي عليه الأكثرون انقسام الذنوب إلى الصغائر والكبائر. وقد أرشد [القرآن] إلى هذا، حيث يقول: {إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم}. [فلو] كانت الذنوب كلها كبائر، لكان المعنى: إن تجتنبوا الكبائر، وهي كل الذنوب، نكفر عنكم سيئاتكم.','2','374',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1071,'وقد يعتذرون على هذا بأنه لما قال: {كبائر ما تنهون عنه}، قصد بذلك أكبر الكبائر، فكأنه [قال: (إن تجتبوا كبائر المحرمات). وهذا لعمري تأويل. وقد] قال صلى الله عليه وسلم: (الجمعة إلى الجمعة والصلوات الخمس مكفرات لما بينهن إذا اجتنبت الكبائر).
ثم اختلفوا بعد ذلك في العدد، فقيل: أربع، [وقيل: سبع]، وقيل: سبع عشرة. وقال ابن عباس: (هي إلى السبعين أقرب منها إلى السبع). وقال قائلون: ما اشتملت عليه [سورة] بني إسرائيل من أولها إلى قوله تعالى: {كل ذلك كان سيئة عند ربك [مكروهًا}. فهو كبيرة].
وقوله: (ولكنا نعني بالصغيرة: ما لا يتضمن صدوره فسق من ظهر عليه، وانسلاله من نعت [العدالة]). فنقول: إن جعل ذلك حدًا للصغيرة، فلا','2','375',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1072,'يصح، [إذ] بعض الصغائر [يسلب] العدالة. [وإن لم يرد الحد، وإنما أراد كل ما لا يسلب] العدالة، فهو [صغيرة]. (١١٨/ب) فهذا لا [يفيد] ضبط جميع الصغائر، وهم [ضبطها] السؤال. على أن بعض الكبائر أيضا لا تسلب العدالة في حق من لم يعلم أنها كبيرة، عند بعض العلماء. وقد [قيل] الشافعي شهادة أهل الأهواء، وإن كانوا أصحاب كبائر. إلا أن يكون المراد: أن الكبيرة إذا صدرت ممن يعلم أنها كبيرة سلبت العدالة. فهذا كلام صحيح.
وإنما قلنا: إن رد الشهادة لا يتوقف على الكبائر خاصة، من جهة أن قبول الشهادة مرتب على حصول غلبة الظن بتقوى الشخص، حذرًا من الله','2','376',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1073,'[تعالى] أن يتعاطى الكذب. ومقتضى هذا [السياق] [أن كل] معصية تعاطاها المكلف، عالما بها، [تقدح] في الشهادة، ولكن لو استمر هذا، لردت شهادة أكثر الخلق، لامتناع [الانفكاك] عن المعاصي من حيث الجملة. فطريان [ما] يتعذر الانفكاك عنه، لا يقدح إذا كان الغالب التمسك بطريق الخير. قال الشافعي رحمه الله: (لسنا نعلم إلا قليلًا [ممن] يفعل الطاعة [ولا] يشوبها بمعصية، [وآحاد] الزلات [لا يعرى] من مثلها (٩٨/ب) الصديقون).
وقوله: (إن الضمائر مختلف [في وقوعها] من الأنبياء). فهو كذلك. ومذهب مالك [رحمه الله] أنها واقعة من حيث الجملة. واستدل على ذلك بقول الله عز وجل: {ليغفر لك الله ما تقدم من ذنبك وما تأخر}.','2','377',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1074,'[الآية] ظاهرة في القطع بالنفي، والإثبات مفقود.
قال الإمام: ([ومما] نقدمه [قبل] الخوض في الغرض: [النسيان]) إلى قوله (في إيضاح المختار والدليل عليه). قال الشيخ: النسيان يكون على ثلاثة أوجه:
أحدها - أن ينسى أن يبلغ ما أمر بتبليغه، [فلا يبلغه].','2','378',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1075,'الثاني -[أن] ينسى، فيعتقد أنه كمل صلاته، ولا يكون كملها، أو يظن أنه بقي عليه شيء منها، [وينسى أن يكون كملها.
والثالث - أن ينسى]، فيبلغ خلاف ما أنزل الله.
أما القسم الأول: فليس في العقل ما يحيله بوجه، ولم يرد سمع دال على وقوعه بحال، فهو من المجوزات العقلية، وليس بواقع عندنا، ويعرف ذلك بالاستقراء.
وأما القسم الثاني: وهو السهو في العبادات، زيادة ونقصانا، فهو ثابت وقد نقل ذلك في أخبار كثيرة. و) لما سلم [النبي] صلى الله عليه وسلم من [اثنتين] وقال [له] الصاحب: (أقصرت الصلاة أم نسيت؟ قال: ما قصرت ولا نسيت. قال له: بل نسيت). وهذا من الراوي فهم [ثابت]، فإنه كان الأمر مترددا عنده بين [نقصان] يبينه الرسول بفعله - فإنه كان يبين بالفعل تارة وبالقول أخرى -وبين سهو)، فلما نفى رسول الله صلى الله عليه وسلم الأمرين، والراوي قاطع','2','379',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1076,'بجريان أحدهما، لكن علم أن أحد الأمرين يستحيل الغلط فيه، وهو قوله: (ما قصرت الصلاة). انحصر الجانب الآخر، وهو النسيان، [فهذا] مجوز عقلا، [وواقع] سمعا.
القسم الثالث: النسيان حتى ينقل على خلاف ما كان سمعه، [وأوحي] إليه سهوا. وهذا عندنا محال، لأن المعجزة دلت على أنه صادق في جميع [ما] يخبر به عن الله تعالى.
فلو جوزنا أن ينسى حتى يبلغ خلاف ما [بلغه]، لم تدل المعجزة على الصدق في التبليغ مطلقا.
ووجه آخر: وهو أنه كان يلزم منه أن لا [نثق] بكل ما يبلغنا، لاحتمال السهو والغلط فيه، وذلك محال. فيثبت (١١٩/أ) [بما] قررناه انقسام النسيان إلى واقع [ومستحيل] عقلا، ومجوز عقلا، [منتف] وقوعا.','2','380',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1077,'قال الإمام: (ونحن نقول بعد ذلك: [إذا] لم يبعد وقوع الذنب من الرسول) [إلى قوله] (في حكم فعله). قال الشيخ: هذا الذي ذكره الإمام ضعيف، وذلك أنا جوزنا أن يقع الذنب منه، ويصح أن ينهض فعله علامة على وجوب مثل الفعل في حق غيره، فلا مناقضة بين وجوب الاقتداء به في مثل فعله، [وبين] تجويز وقوع الذنب منه. ونحن نرى التعلق بالعموم، وإن أمكن أن يكون مخصصًا. [وكذلك] [نقول: ] الغالب امتناع وقوع الذنب، والأكثر الأعم فقدانه، فلا يضر (٩٩/أ) التجويز في أمر [القدوة] [بحال].
قال الإمام: (وأجمع تقسيم فيه أن نقول: فعله ينقسم) [إلى قوله]','2','381',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1078,'(والظهور مع تطرق فنون الظنون [لا يقتنع به] في القطعيات). قال الشيخ. قول أبي الحسن: [المراد] بالآية: (ما أمركم)، لا [يصلح]، لا باعتبار وضع اللغة، ولا باعتبار قول أهل التفسير.
أما أهل اللغة: فإنهم بقولون: (آتي) بمعنى: أعطى (٩٩/ب)، تقول:','2','382',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1079,'آتيته مالا، أي أعطيته، ولا [نطلق]: (آتى عمرو زيدًا)، بمعنى أمره بحال.
وأما أهل [التفسير: فإنهم يقولون: {ما ءاتاكم}، أي ما أعطاكم من] الغنائم، } وما نهاكم عنه}: [أي] من الغلول والسرقة.','2','383',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1080,'[وقوله: (إنه يدل على ذلك اقترانه بالنهي، والنهي] إنما يقارنه الأمر على طريق المضادة). فإنه من فصيح الكلام أن يقول: خذ ما أعطيتك، وانته عما نهيتك عنه من غير إشكال. وجميع ما ذكر غير هذا واضح صحيح. ومن العجب ممن ذهب إلى الوجوب أنه يجوز أن يكون الفعل في حق الرسول [صلى الله عليه وسلم] مندوبا، فكيف يكون في حق [غيره] واجبا؟ إلا أن يكون سلك به مسلك [المعاملات]، لا مسلك القدوة.','2','384',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1081,'وأما إذا كان الفعل واقعا بيانا، فلا خلاف فيه. ولكن [الشأن] في معرفة كونه وقع بيانا. وهذا قد يعرف بقرينة الحال، [أو] بصريح المقال. [أما] صريح المقال فواضح، وأما قرينة الحال، فإن [تنجز] وقت العمل ولا يقبل التأخر بوجه وفقد البيان بالقول، وفعل فعلا صالحا للبيان، [فيجب] تنزيله عليه.
فإن قال قائل: فما المانع من كون هذا حكما [مستفتحا]، ويبقى القرآن على الإجمال؟ قلنا: يمنع من ذلك تأخير البيان عن وقت الحاجة، ومثاله: قول الله (عز وجل): {وءاتوا حقه يوم حصاده}. ولم يرد منه قول يقتضي بيان قدر الحق، ولكنه [أحد]: العشر أو نصفه، فيدل ذلك على أنه مقدار الواجب. وكذلك (لما فرضت الصلاة على الجملة، ولم تبين صفاتها، أم جبريل','2','385',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1082,'[- عليه السلام -] رسول الله (صلى الله عليه وسلم) مبينًا له كيفية الصلاة).
قال الإمام: (ومما [تمسك] به هؤلاء أن قالوا: أجمع المسلمون قبل [اختلاف الآراء]) [إلى قوله] (إلا إذا أمر به). [قال الشيخ: تمسكوا] [بما] ذكره الإمام لهم، وقد تمسكوا بشبه غير هذا، وذلك أنهم قالوا: لا بد من وصف فعله بكونه حقا وصوابا ومصلحة، [ولولاه لما] أقدم عليه، ولما تعبد به، فليجب على الأمة مثل ذلك. وهذا غلط من وجهين:
أحدهما- أن هذه الأوصاف مسلمة، ولكنها لا تدل على الوجوب في','2','386',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1083,'حقه، فضلًا عن أن تدل في حق غيره، [إذ كل] هذا يثبت للفعل المندوب إليه.
الثاني-[أنا وإن] سلمنا أن الفعل واجب بالإضافة إليه، فمن أين يلزم وجوبه في حق غيره؟ والفعل لا دلالة [له]، (١١٩/ب) فليختص حكمه بفعله.
وقالوا أيضًا: أنه لو لم يتابع في أفعاله، [لصح أن] لا يتابع في أقواله، وذلك تصغير لقدره، وتنفير للقلوب (١٠٠/أ) عنه. وهذا هذيان، [فإن] المخالفة في القول عصيان، والطاعة واجبة، والتبليغ عن الله حق، [والتصديق] دليل الصدق، وقوله متعد إلى غيره، وفعله قاصر عليه.
وأما التنفير، فلا التفات إليه، وقد قال الله تعالى: {وإذا بدلنا ءاية مكان ءاية والله أعلم بما ينزل [قالوا إنما أنت مفتر]}. فقد صار النسخ [سببا للتكذيب] والتنفير، ولم [يمتنع]. وكيف يقال: إن','2','387',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1084,'المخالفة في الفعل تقتضي تنفيرا، وقد (واصل ومنع من الوصال). و) نكح تسعا)، ومنعت الأمة من ذلك؟ فاستبان أن هذه خيالات، وأن الفعل متردد، كاللفظ [المشترك].
وتمسكوا أيضا بقوله: {[فليحذر] الذين يخالفون [عن أمره]}. قالوا: والأمر يراد به الفعل والشأن، فلا بد من موافقته في شأنه كله، إلا ما دل الدليل على اختصاصه [به]. وهذا أيضًا فاسد، لأن إطلاق الأمر [على الشأن] تجوز بعيد، وإنما المراد الأمر الحقيقي الذي [نصب] لأجله من تبليغ الأحكام، ووجوب الطاعة في الأمر.
ومن أعظم ما تمسكوا به، وهو [أظهر] متمسك لهم، فعل الصحابة (رضي الله عنهم)، وهم أنهم (وصلوا الصيام لما واصل). و) خلعوا نعالهم في الصلاة لما خلع). [و) أمرهم] عام الحديبية [بالتحلل] والحلق فتوقفوا','2','388',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1085,'[فشكا] إلى أم سلمه فقالت: (اخرج إليهم، واذبح واحلق، ففعل، ف ١ بحوا وحلقوا [مسرعين]). وانه (خلع خاتمه فخلعوا). وكان عمر يقبل الحجر الأسود [ويقول]: (إني لأعلم أنك حجر لا تضر ولا تنفع، ولولا أني رأيت رسول الله (صلى الله عليه وسلم) يقبلك ما قبلتك). وقال في جواب [السائل عن] القبلة للصائم: ([ألا] [أخبرتها] أني أفعله. وكذلك الصحابة كلهم لما اختلفوا في الغسل من التقاء الختانين، فقالت عائشة [رضي الله عنها]:','2','389',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1086,'(فعلته أنا ورسول الله (صلى الله عليه وسلم) فاغتسلنا). فرجعوا إلى ذلك. والجواب عن ذلك من وجوه:
أحدها- أن [هذه] الأخبار تدل على أنهم رأوا التأسي، أما أن يكونوا رأوا ذلك واجبا، فغير مسلم، [هذه] أخبار آحاد لا يصح أن تستند القواطع إليها.
[والثاني]- أيضا فإنهم لم يتبعوه في جميع أفعاله وعبادته، فلم [صار] الأتباع في البعض [دون البعض] [واجبا]؟
الثالث- أن هذه الأخبار متعلقة بعبادات، كان قد أرشدهم إلى أنه مساوٍ لهم فيها، كالصلاة والصوم والحج، فلما ثبتت المساواة بقوله، رأى القوم الاقتداء بالأفعال في تلك القواعد. [وقد كان يلزمهم] مساواته لهم فيها،','2','390',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1087,'على حسب ما قدمناه، وقد قال في الضوء: (هذا وضوئي ووضوء [الأنبياء] من قبلي). يشير إلى عموم حكم المكلفين. فبهذه الأخبار أثبتوا القدوة، لا بمجرد الفعل.
فإن قيل: الفعل دائر بين [أن] يكون واجبا أو مندوبا إليه، والاحتياط في الحمل على الوجوب. وهذا تحكم، بل [لا] يحمل على جهة إلا بقرينة. وقد يقال: إن الأًصل انتقاء الأحكام، (١٠٠/ب) [فلا يثبت] حكم [إلا بيقين]، ولا يتيقن وجوب ولا ندب، [وهذا] طريق الواقفية.
قال الإمام: ([وأما من صار إلى أن الفعل يدل على ثبوت]','2','391',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1088,'[الاستحباب] [فيما يقع قربه، فهذا أقرب دليلا]) إلى قوله ([وكل ذلك] (١٢٠/أ) فيما ظهر وقوعه على [قصد القربة من الرسول (صلى الله عليه وسلم)]).
قال الشيخ: ما ذكره الإمام ظاهر، ولكن يبقى النظر في أمر، وهو أنهم، هل كانوا يقضون بالندب في الفعل الذي تعاطاه الرسول مندوبا إليه، أو كانوا يقضون بالندب والاستحباب عند ظهور مقصد التقرب منه خاصة؟ [فإن] كان القوم يعتقدون المشاركة في حكم الفعل على أنه واحد من المكلفين، افتقرنا إلى أن نعرف خصوصية حكم الفعل، بعد ظهور قصد التقرب، [به، هل] على جهة [الاستحباب أو الوجوب]؟ وإن سلك به مسلك العلامة على الندب،','2','392',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1089,'فلا حاجة إلى خصوصية حكم الفعل، بل يكون الفعل مندوبا إليه بالإضافة إلينا، وإن أمكن أن يكون في حقه واجبا.
والذي يصح عندي في هذه المسألة القسم الأول، فإنهم اعتقدوا أنه واحد من المكلفين، وأن حكم الله عليه وعليهم على جهة واحده، إلا في أمور يسيرة، [ولا تكاد] أن تبلغ في الشريعة عشرة أحكام، بعد أن أعلمهم اختصاصه بها [عنهم]، وبقي القوم على اعتقاد المشاركة [له] في الأحكام. فعلى هذا لا يقضي بكون الفعل مندوبا إليه، إلا بعد أن يثبت أنه مندوب إليه في حقه. وإن لم يتبين ذلك، [وثبت] التقرب خاصة، ثبت في حقهم التقرب، ونبقى على التردد في الندب والوجوب.','2','393',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1090,'ولكن يبقى ها هنا [أمر] آخر، وهو: لو علم أن الفعل [عليه] واجب، وأن قصد التقرب لا يرشد إليه، بل يبقى على التردد، للزم أن يكون في تأخير ذلك تأخير البيان عن وقت الحاجة. فعلى هذا ينتفي أمر الوجوب، ويبقى أصل الندب.
قال الإمام: (فأما فعله المرسل الذي لا يظهر [منه] وقوعه [منه]','2','394',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1091,'على قصد القربة) إلى قوله ([فهذا منتهى القول في أفعاله صلى الله عليه وسلم]). قال الشيخ: الأمر على ما ذكره الإمام في هذا القسم بلا زيادة، والنقل عن الصحابة ثابت فيه على [الضرورة]. وقد استدل فيه بهذا بعض الأصوليين على اعتقادهم عصمته عن الصغائر، والاستدلال وجهة واضح، وطريقه لائح.
وتقريره أنه لو انقسمت أفعاله إلى ما يصح أن يكون مباحا، وما يصح أن','2','395',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1092,'يكون معصية، لم يصلح الاستدلال بمطلق الفعل المعتاد على الإباحة، ولكن [يلزم] منه أيضًا [عصمته] عن المكروهات. [ولم يتكلم العلماء على العصمة عن المكروهات]، ولكن سياق هذا الاستدلال يقتضي العصمة أيضًا، [وأنه] لا يقع منه إلا مطلوب، أو مباح.
وهذا وإن كان ظاهرًا، فليس مقطوعا به، إذا يحتمل أن يكون القوم غلب على ظنهم من فعله أن الفعل مباح، [ويتنزل] فعله منزلة ظاهر من الكتاب','2','396',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1093,'يدل على الإباحة، (١٠١/أ) فإنهم [متعبدون] بالعمل، عندما يغلب على ظنهم. ولا شك في حصول غلبة الظن [بالشرعية]. وإن قلنا بتجويز المعصية، فذلك أقل شئ وأندره، فإذا [لا يدل] هذا على القطع بنفي الصغائر بحال.
[وإذا] تقرر أنهم كانوا يفهمون من فعله نفي الحرج عنهم، صح إذا ورد لفظ يتناول تحريم أمور، ثم نقل أن النبي (صلى الله عليه وسلم) تعاطي بعض ما دل العموم [عليه بالتحريم]، أن [يكون] ذلك مخصصا في حقهم.','2','397',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1094,'وقد كنا ذكرنا ما يخصص به العموم، أشرنا إلى أن الفعل يكون مخصصا، [ولم نر] الاستيعاب هناك، لأنه لم يتأب استيعابه [إلا بعد البسط].
ونحن نذكر وجه كونه مخصصا في هذا المكان. (١٢٠/ب) والنظر ها هنا يتعلق بطرفين:
أحدهما- التخصيص [بالإضافة]، حتى يكون الفعل منه يدل على خروجه [هو] عن قسم النهي، إذا كان اللفظ له شاملا.
الثاني- كونه [يكون] مخصصا بالإضافة إلى غيره من الأمة، إذا كان لفظ النهي يتناول الأمة متعلقًا بذلك الفعل وغيره.
أما القسم الأول: فمثاله: أنه - عليه السلام -: (نهى عن الوصال ثم واصل، فقيل له: نهيت عن الوصال، ونراك تواصل. فقال: (إني لست كأحدكم، إني أظل عند ربي يطمعني ويسقيني). فبين أنه [لم يرد] بفعله بيان الحكم في حق الأمة، [ولكنه] ذكر أنه [كان] يفارق الأمة [فيه].','2','398',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1095,'ثم لفظ [النهي]، إن كان يتناوله وغيره، [ففعله مخصص] للعموم، بالإضافة إليه، وإن ثبت بقوله: (لا تواصلوا)، و (نهيتكم عن الوصال)، فهذا الخطاب لا يصلح [لتناوله] بحال، فلا يكون فعله مخصصا له. وإذا لم يكن فعله [مخصصا] بالإضافة إليه، فهل يكون مخصصًا بالإضافة إلى [غيره]؟ هذا فيه نظر.
ومثاله: (أنه نهى عن استقبال القبلة في قضاء الحاجة، ثم رآه ابن عمر [رضي الله عنهما] مستقبلا بيت المقدس على سطح لقضاء حاجته). فيحتمل أن يكون تخصيصا، لأنه كان وراء سترة، والنهي كان مطلقًا، وأريد [به] ما إذا لم يكن ساتر. ويحتمل أنه كان [مستثني] ومخصوصًا، فهو دليل على خروجه عن العموم، إن كان لفظ [المحرم] [عامًا] له، ولا يصلح هذا [لأن] [ينسخ] [به] تحريم','2','399',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1096,'الاستقبال، لأنه [فعل] يكون في خفية وخلوة، [ولا] يصلح أيضًا [لأن] يراد به البيان، [فإن] ما يراد به البيان، لا بد من إظهاره. وأقل [ذلك] أن يظهر لعدل، أو لعدم التواتر، [إن] تعبد فيه بالعلم. ولم [يجز] شيء من ذلك، إلا على تقدير واحد، وهو أن يكون [النبي]- عليه السلام - علم إطلاع ابن عمر عليه، فاكتفى بذلك في البيان.
المثال الثالث: أنه (نهى عن كشف العورة). ثم كشف فخذه بحضرة أبي بكر وعمر ثم دخل عثمان فسترها، [فتعجبوا] منه، فقال: ([ألا أستحي] ممن استحت منه ملائكة السماء)؟ فهذا [لا يرفع]','2','400',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1097,'(١٠١/ ب) النهي، [لكونه] لم يكن داخلاً فيه، أو لعله [كشف] لعارض [وعذر]، [فإنه] حكاية حال. [أو أريد] [بالفخذ: ما يقرب منه، وليس داخلاً في حده]، أو إباحته [خاصة] [له]، أو نسخ تحريم كشف العورة. [وإذا تعارضت الاحتمالات، فلا] يرفع [التحريم] في حق غيره بالوهم.
وكأن [هذا] [يعترض] على قولنا: إن الصحابة كانوا يعتقدون إباحة الفعل المنقول لهم عنه، لكن ذلك في فعل لم يثبت عليهم في حكم مخصوص، فلا يلزم هذا فيما استقر عليهم فيه المنع.
وبقي في المسألة مذهب لم ينقله الإمام، لركاكته، ونزول قدر قائله، وأردنا نحن التنبيه [عليه] ليكون الإنسان خبيراً به، [و] بمأخذه، ذهب','2','401',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1098,'بعض الخائضين في الأصول إلى أن فعل الرسول [- عليه السلام -] على الحظر ومعناه بالإضافة إلينا لا بالإضافة [إليه]، إذ لا أحد من الأمة [يعين في فعله] المطلق [المعصية]، وإنما الناس يختلفون في الإمكان والاستحالة، فأما [تعين] المعصية، فلا ذاهب إليه، وهؤلاء القوم [سلكوا] في تقرير ذلك أصلين:
أحدهما- أن الأفعال قبل ورود الشرع على الحظر، وقد تقدم ذلك الرد عليه.
والثاني-[أن] فعله مختص به لا يتعداه، فيبقي الفعل على ما كان عليه في حقنا قبل أن يفعل الرسول [- عليه السلام -]. [وهذا طريق الواقفية، نظرًا منهم إلى قصور دلالة الفعل على حكم أفعالنا. وهذا لعمري عند الاقتصار على مجرد الفعل، [وأما] مع الطريق الذي قررناه بالإضافة إلى الصحابة، فلا يصح ذلك، لما سبق من اعتقادهم نفي الحرج في الأفعال التي ليست من قبيل القربات.','2','402',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1099,'قال الإمام: <span data-type="title" id=toc-193>(فصل- يحوي [مقاصد] من أحكام الأفعال) </span>إلى قوله (في محله) قال الشيخ: الظاهر عندي أيضاً في هذه المرتبة اعتقاد المشاركة إلا في عين الخاصية، فإن الذي استقر عند الصحابة - رضي الله عنهم - أنه واحد من المكلفين، [على عموم الأحوال، وحكم الله تعالي على الواحد حكم على الجماعة، وثبوت خاصية بفعل المكلفين] لا يمنع من مساواة [الناس] له في غير ذلك المكان، ألا تري خزيمة لما خص بقول شهادته وحده، لم يمنع المسلمين ذلك من إجراء أحكام الشهادات عليه في غير حكم الانفراد.
ويحقق ذلك أن الرسول [- صلى الله عليه وسلم -] قد ثبت له خاصية في الصيام بدليل [الوصال]، ولم يمنع [ذلك] من اعتقادهم مشاركته في غير ذلك. ولقد','2','403',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1100,'قال - صلى الله عليه وسلم - للذي سأل عن القبلة للصائم: [هلا] [أخبرتيها] أني أفعله"؟ وقد ثبتت لرسول الله - صلى الله عليه وسلم - خواص في النكاح، ولم يزل الناس يستدلون بأفعاله، كاستدلالهم [على] أن الزوجة الجديدة لها حق في أن يقيم الزوج عندها [قوله] لأم سلمة: "ليس بك على أهلك (١٠٢/ أ) هوان، إن شئت سبعت عندك وسبعت [عندهن]، وإن شئت ثلث ودرت. [قالت: بلى]).
إلى غير ذلك من الأخبار التي تمسكوا بها في أبواب النكاح والصوم وغيره. والصواب عندنا الاستدلال بأفعاله [مطلقًا، إلا] في محل [عين] الرخصة لجميع المكلفين.','2','404',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1101,'قال الإمام: (وهذه [غاية] ينبغي أن ينتبه [لها] من يبغي البحث عن المذاهب) [إلى قوله] (كدأبنا في المسائل)، قال الشيخ: هذا الذي ذكره الإمام إنما يفرض في حق العلماء، فإنهم يبعدون أن تستند أحكامهم إلى محض الأوهام، فلا بد من مستندات من حيث الجملة، فأما أن يجعل ذلك قضاء على كل الخلق، فهذا باطل، وكم من مذاهب الخلق الكثيرة استندت إلى محض الأوهام، بحيث لا يكون لهم مستند عقلي ولا سمعي، كعبادة النيران، والسجود للأصنام، وما يتعلق بتشبيه المشبهة، وتعطيل المعطلة، إلى غير ذلك من المذاهب الفاسدة، والأقوال المتهافتة.
[قال الإمام: (ومما نذكره في [حكم] الأفعال بعد ثبوت التأسي به','2','405',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1102,'[- صلى الله عليه وسلم -] على التفصيل [المتقدم] إلى قوله [كانوا يردون الأخير أفضل أحواله وأولى أفعاله] ٠، قال الشيخ: إذا نقل عن الرسول - صلى الله عليه وسلم - فعلان مؤرخان، فمذهب القاضي أن فعله مختص به، لا يتعدي حكمه لغيره، وإذا كان كذلك، ووقع الفعلان في زمانين، فلا تعارض في الحقيقة؛ إذا التعارض معناه التناقض، وشرط [التناقض] اتحاد الزمان والمكان [والفاعل].
وعند التعدد يفوت التناقض، ويبقي فعله مختصًا به، هذا إذا لم يقترن بالفعل','2','406',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1103,'قول يدل على ثبوت [الحكم]، [بالإضافة] إلى الأمة، [فإذا اقترن] به قول يقتضي أن يتعدي حكم الفعل إلى الأمة، كقوله: "صلوا كما رأيتموني أصلي"، ونقل عنه أيضًا أنه "صلي صلاة الخوف على جهات متعددة"، فهل نقول: يصح إيقاعها على كل وجه، [لأنا نتلقى] من فعله الشرعية؟ وكذلك يتلقي من الثاني؟
[ذهب] إلى ذلك بعض العلماء، [والصحيح] عندنا خلافه، وأن ذلك يتنزل منزله القولين المختلفين، فإنه إذا قال: "صلوا كما رأيتموني أصلي". [وصلى] أولاً على حالة، تضمن الأمر (١٢١/ ب) [الإيقاع','2','407',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1104,'على تلك الحالة]، وإذا صلي ثانيًا على غيرها، لزم الإيقاع على الوجه الآخر، ولا سبيل إلى الجمع بينه وبين الوجه الأول. [فمن] هذه الجهة يتناقضان، ويكون الآخر ناسخًا.
وقد ذهب بعض الناس إلى جوازهما جميعًا، واستحب رواية ابن خوات من طريق التفصيل، لأنها أقرب إلى مقصود الصلاة، وأبعد عن التشويش وعدم الخشوع، وما قررناه أولاً يرد هذا ويبطله، هذا إذا ثبت جريان الفعلين جميعًا، وأما إذا لم يثبت إلا فعل واحد، ووقع هذا الاختلاف فيه، فهذا تناقض بين، فإن استوت الرواة [في العدالة]، هل يتخير المجتهد، أو يتوقف، أوي بني على الاحتياط؟ فيه كلام يأتي في كتاب الترجيح، إن شاء الله.','2','408',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1105,'وما ذكره الإمام من [حمل] على أنهم "كانوا يرون الآخر أفضل [أحواله] "، لا يصح أن يكون ذلك في قسم المباحات، وقد قال هو: إن أفعاله فيما لا يتعلق بقبيل القربات تكون مباحة، فيتعين تنزيل ما ذكره ههنا على القربات، وإنما قصد في هذا المكان [الصلاة] وما يجري [مجراها]، مما يثبت فيه مساواة الأمة، وقد بينا أن الصحيح فيما يتعلق بالصلاة والحد والمواضع التي أمر الخلق فيها بالموافقة، أن يعتمد على الفعل الأخير، ويكون الأول منسوخاً.
فإذا قيل: كيف يتصور النسخ في الفعل [الثابت]؟ قلنا: (١٠٢/ ب) كذلك نفس القول الثابت لا يصير [بورود] النسخ عليه غير ثابت، بل المراد أنه لا يدوم حكمه، والنسخ يقطع الدلالة في مستقبل الزمان، ولا فرق بين أن يتلقي الحكم من قول أو فعل. وقد بينا أن الصلاة وغيرها مما يضاهيها، جعل','2','409',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1106,'الشرع أفعاله فيها تتبين بها أحكام الأمة، على حسب ما مر.

قال الإمام: <span data-type="title" id=toc-194>(مسألة مما يتعلق بالكلام في أفعال رسول الله - صلى الله عليه وسلم - حكم تقريره غيره على أمر) [وإلى قوله] (فهذا [التفصيل] لا بد منه في التقرير)</span>. قال الشيخ الكلام في مسألتين:
[إحداهما-] محض السكوت، والأخرى- السكوت تقريرًا.
أما إذا سكت [تقريرًا]، فهذا لا يتصور فيه خلاف أن يكون ذلك مشروعًا لفاعله، ويبقي النظر في كونه مباحًا أو مندوبًا أو واجبًا، وإنما المقصد خروج الفعل عن قسم الممنوع، فإنه لو كان فاعلاً منكرًا، لما سكت [عنه] على حال، لاسيما إذا كان مقرراً له.','2','410',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1107,'فإن قيل: هذا إنما يصح على قول [من يقول] بامتناع الصغائر عليه، [أما إذا] جوز، فما المانع [من] كون الفاعل عاصيًا، وهو يراه، وتمكن من الإنكار عليهن ولم يفعل؟ [فالجواب]: أن هذا باطل من وجهين:
أحدهما- أنا فرضنا أنه سكت مقرراً، والمفهوم من التقرير الرضا بما فعله الفاعل، فيصير [بمثابة] ما لو [أخبره] أنه مشروع له، فإن [المعصية] في ذلك محال.
الثاني- أن ذلك يفضي إلى كتمان الشرع، وذلك أيضًا باطل، فعندما يفهم','2','411',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1108,'التقرير، فلا يتصور أن يكون [الحكم] ممنوعًا بحال.
الصورة الثانية: هو أنه إذا لم يوجد إلى السكوت خاصة، فهذا هو الذي طرق إليه الإمام الاحتمال، على حسب ما قرره، والصحيح عندنا خلاف قوله، ونختار ما ذهب إليه الأصوليون من أن السكوت في هذه الصورة يوهم النسخ. وقد يستدل الناظر عند هذه الحالة على الإباحة برؤيته [ذلك]، اللهم إلا أن يظهر من قرائن أحواله بحيث يزول اللبس (١٢٢/ أ) عن [الناهي] أنه إنما سكت لكون الفاعل لا يقبل (١٠٣/ أ) النهي، فحينئذ يزول الإبهام في هذه الحال.
وهذا بمثابة كونه لم ينكر [صبيحة كل سبت وأحد، ولم يمض إلى]','2','412',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1109,'بيع [اليهود والنصارى] منكرًا عليهم، [لأن] المسلمين والكفار [قد علموا جميعًا] أنه على أمره في الإنكار عليهم، فزال اللبس، واندفع الإشكال، ولم يبق وجه لتفصيل الإمام.

قال الإمام: <span data-type="title" id=toc-196>(مسألة: استدل الشافعي في إثبات [القيافة] بتقرير رسول الله - صلى الله عليه وسلم </span>-) إلى قول (إذا لم يثبت في شرعنا [ناسخ له على التعيين]) ..','2','413',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1110,'قال الشيخ: الصحيح عندي في مسألة مجزر أنه لم يقل محرمًا في [قوله لأسامة] وزيد: (إن هذه الأقدام بعضها من بعض). إذا لو كان ممنوعًا من ذلك، لما صح سكوت [رسول الله - صلى الله عليه وسلم -]، [لاسيما مع] ما أظهر من','2','414',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1111,'[الاستبشار]، فلا وجه [للإعراض] عنه، مع كونه لم ينطلق بأمر مشروع [لأجل] مراغمة الكفار والرد عليهم، هذا لا سبيل إليه بحال.
ولكن لا يلزم [من كونه] لم يقل محرمًا أن تستند [الأنساب] إلى قوله، ولو شهد عدل بأن هذا ولد هذا، فلا تنكر عليه الشهادة، ولكن لا يثبت [النسب] بذلك عند إنكار الوالد فكيف يبني الشافعي [إلحاق] الأنساب بقول القائف على كونه النبي [- صلى الله عليه وسلم -] قرر مجززاً على قوله [واستبشر] به؟','2','415',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1112,'أما استبشاره، فلأجل أنه جاء من العرب من يكذبهم، وأما كونه لم ينكر عليه، [فلأنه] لم يقل إلا حقًا، فإنه أضاف الولد لأبيه الذي هو أبوه شرعًا.
وأما كونه جزم القول بأنه أبوه، [فيصح] أن يرشده الشبه إلى ذلك. [ولم] يقل ذلك مجازفًا. [ويمكن] أيضًا أن يكون عالمًا بأنهما أسامة وزيد، وقال هذا لأقول، لرد [دعاوي] المشركين خاصة، أي الشبه يدل على ما استقر في الشرع، وهذا لا منكر فيه حتى يتعين إنكاره، فالصواب إذاً ألا [تستند] الأنساب إلى القيافة بهذا الخبر.
والصحيح من مذهب مالك امتناع دخول [القيافة] في الحرائر، [وإنما] القافة عنده في الأمة يطأها السيدان في طهر واحد، إما الشريكان،','2','416',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1113,'وإما المشتري [والمالك]، ومعتمده في ذلك [كون] عمر - رضي الله عنه -[لط] أولاد الجاهلية بآبائهم من الزنا)، لاستواء النسبة إلى الفروج ذلك الوقت. وله قول ضعيف في [إلحاق] الأنساب بقول القافة في النكاح أيضًا، والأول هو المشهور.

قال الإمام [رحمه الله]: <span data-type="title" id=toc-197>(القول في [التعلق] بشرائع الماضين</span>.','2','417',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1114,'[مسألة]: اضطربت المذاهب) [إلى قوله] (فيما نحاوله). قال (١٠٣/ ب) الشيخ [أيده الله]: الذي نختاره في هذه المسألة [أن شرع] من','2','418',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1115,'قبلنا شرع لنا، إذا لم يثبت في شرعنا [ناسخ له] ولنتعقب استدلال الإمام، ثم ندل [على] [ما] نختاره.
أما من أحال ذلك عقلاً، فلا حاجة إلى [تكلف] رد عليه، والمسألة عندهم مبنية على التحسين والتقبيح، وهو أصل قد فرغ منه.','2','419',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1116,'وأما ما احتج به الإمام من (أن أصحاب الرسول [- صلى الله عليه وسلم -] لم [يبحثوا] عن أحكام [الملل] السابقة). [فالأمر] على ما قال، والسبب فيه كون البحث لا يجدي شيئًا، ولا يفيد علمًا ولا ظنًا، [لاختلاف الوسائط]، وامتناع صحة النقل، لا متواترًا ولا آحادا.
وقوله: (إن ذلك موافقة في الحكم، وإن رجعت المنازعة إلى الطريق)، ليس الأمر كذلك، فإنه يقول: لو ثبت بقول الرسول (١٢٢/ ب)','2','420',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1117,'[- صلى الله عليه وسلم -] أن حكم التوراة [كذا]، [لم يكن] ذلك حكمًا علينا، ونحن نقول: إذا ثبت الحكم السابق، كان ذلك حكمًا علينا، وتثبت أحكام [الملل] السابقة بإخبار الكتاب عنها، أو بإرشاد الرسول - صلى الله عليه وسلم - إليها، وقد قال الله تعالي: {وأخذهم الربا (١٠٤/ أ) وقد نهوا عنه وأكلهم أموال الناس بالباطل} وقال جل وعلا: {وكتبنا عليهم فيها أن النفس بالنفس .. } وقال تعالي: {إن الله يأمركم أن تذبحوا بقرة}. وقال: {فقلنا اضربوه ببعضها كذلك يحي الله الموتي}.
[واحتجاجه] الثاني: (بأنه لو كان شرع من قبلنا شرعًا [لنا]، لنبهنا الشارع على مواضع اللبس والتحريف حتى لا يتعطل علينا مدرك).
كلام ضعيف، وليس للعباد أن يتحكموا على الله تعالي في [دفع] الحاجة','2','421',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1118,'وتبين [الطريق]، بل الله يفعل من ذلك ما يشاء، ولقد كانت النصوص على الأحكام بذكر الروابط الكلية [أهون] على الخق من الاحتياج إلى القياس والاستنباط، فـ} لا يسئل عما يفعل وهم يسئلون}.
وأما [الوجه] [الثالث]: وهو أنه كان أسلم من أحبارهم جماعة تقوم بهم الحجة، فلم ينته القوم إلى [حد] التواتر، وقد اختلفت الفقهاء في نقل الإجماع على ألسنة الآحاد، هل يكتفي به، ويكون نقل هذين الرجلين الشرائع المتقدمة من هذا القبيل؟','2','422',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1119,'هذا إن صح [وقوفهم] [على الحكم] على التحقيق، على أن ذلك غير متأت، لأنه سبق التحريف والتبديل وجودهم، فلا نكون على يقين مما يصادفونه في التوراة، وإن تحقق [من أحدهم] العلم بحقيقة الحكم، ونقله إلينا، [تنزل] ذلك عندنا منزلة نقل الإجماع على ألسنة الآحاد، والصحيح عندنا الاكتفاء [به] في مسائل الظنون.
قال الإمام: (فإن تمسك فقهاؤنا بقوله تعالي: {إن أولى الناس بإبراهيم للذين اتبعوه} إلى قوله (والرد على عبدة الأوثان). قال الشيخ: هذا من الإمام اقتصار في التأويل على محض الدعوى، ولكنه إنما اكتفي بذلك، لما تخيل أن الإجماع منعقد على منع التعبد، فلذلك اقتصر ههنا على إبداء الاحتمال. ولو صح الإجماع، [لم] يكن للاستدلال بدليل سمعي وقع علي حال. لاسيما ظواهر بعيدة عن الدلالة على الغرض.','2','423',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1120,'فإن قيل: فما الذي يدل على أن شرع من قبلنا شرع لنا؟ قلنا: قد جاء في ذلك هذه الظواهر، مع أخبار صحيحة، منها: أنه - عليه السلام -، لما سئل عمن نام عن الصلاة، قال: "من نام عن صلاة أو نسيها فليصلها إذا ذكرها، [فإن الله تعالي يقول: {وأقم الصلاة لذكرى}، أورده رسول الله - صلى الله عليه وسلم - دليلاً على وجوب القضاء بعد الذكر، كما أمر موسي بذلك، ولما طلب من رسول الله - صلى الله عليه وسلم - القصاص في السن في قصة الربيع بنت معوذ قال أخوها أنس: "والله [لا تكسر] [ثنية] الربيع، فقال رسول الله - صلى الله عليه وسلم -: (كتاب الله القصاص).','2','424',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1121,'وإنما جرى القصاص في السن في كتاب الله - عز وجل -[حكاية] [عن التوراة].
وما ذهب إليه بعض الناس من [أنه] أشار إلى قوله} فمن اعتدى عليكم فاعتدوا عليه [بمثل ما اعتدى عليكم}]، [فعدول] عن الظاهر، وجنوح إلى التأويل.
[وأيضًا] فإن الاستقراء يرشدنا إلى أن أكثر [أحكام] الشرائع المتقدمة مستمرة [علينا]. [وإنما] الاختلاف (١٢٣/ أ) في فروع قليلة، نسبتها (١٠٤/ ب) إلى شريعتنا نسبة المنسوخ من شريعتنا إلى الثابت فيها، فيرشد ذلك إلى استواء الأحكام إلا في مواضع النسخ وتحقق المخالفة.
وأيضا فإن الثابت عندنا أن الشرائع، [إنما] ثبتت لمصالح الخلق لقول الله تعالى: {وما أرسلناك إلا رحمة للعالمين}. وكذلك سائر الرسل.
[وإذا] تقرر هذا، [فعوائد] الخلق في مصالحهم متقاربة،','2','425',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1122,'وحاجتهم إلى [دفع] المضار عنهم كذلك، فليكن [الحكم] الذي حكم به عليهم تحصيلاً لمصلحتهم، ودفعًا للمضرة عنهم مستمرًا علينا، لحصول المشاركة في حاجة النفع والدفع، هذا هو الظاهر، والمسألة ظنية، ولا سبيل إلى القطع فيها على حال.
قال الإمام: [مسألة: ] مما ذكره الأصوليون متصلاً بهذا الفن، القول فيما كان عليه [رسول الله]- صلى الله عليه وسلم - إلى قوله (مستعينين بالله). قال','2','426',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1123,'الشيخ: البحث عما كان عليه رسول الله - صلى الله عليه وسلم - قبل أن يبعثه الله تعالي، مما لا يتعلق بالأصول به غرض. وقد كنا قدمنا على مثل هذا في البحث عن اللغة: هل ثبتت اصطلاحًا أو توقيفًا؟ وقلنا: إن معرفة ذلك لا يترتب عليه أمر يتعلق بفهم الأحكام، [وقررنا] استواء حال العالم بذلك والجاهل به في فهم الأدلة العقلية والسمعية جميعًا، وكل ذلك استواء علمه وجهله بالإضافة إلى علم آخر، فلا تكون معرفته مادة، [ولا] وسيلة بالإضافة إلى ذلك العلم.
وكذلك من عرف دين الرسول [- صلى الله عليه وسلم -] قبل أن يبعثه الله [تعالي] أو جهل ذلك، لم يؤثر علمه ولا جهله في معرفة ما بعث به، ونحن إنما تعبدنا','2','427',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1124,'[بما] جاء بعد رسالته، لا ما كان عليه قبل ذلك. ولكن- وإن كانت المسألة غير محتاج إليها في أصول الفقه - فإذا وقع الكلام فيها، فلنتكلم عليها بما تقتضيه الأدلة، ونبين وجه الاقتصار على الدعوى.
أما ما ذهب إليه القاضي من أنه [لم يكن] على دين، وقطع بذلك، تلقيًا من اقتضاء العادة الإشاعة. فهذا الذي قاله إنما يصح في الأمور العظيمة التي يعتني الناس بالبحث عنها، وتتوافر الدواعي على نقلها، بعد أن تكون أيضًا','2','428',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1125,'منكشفة لعدد التواتر، وأما كون الشخص من الناس منفرداً بنفسه، فما الذي يعتني بمعرفة دينه، وما هو عليه وما هو عليه [من حاله]؟
[ولو] قدرنا أنه أطلع عليه، فقد لا يعتني بنقله، وإن قدر اعتناؤه بنقله، فلا يستمر النقل فيه على التواتر دائمًا، وإنما يدوم [النقل] متواترًا [في] الأمور العظيمة الدينية، ألا تري أنه لا شيء أعظم في طرد العادة من نقل القرآن، وما نسخ منه انصرفت الهمم عن نقله، فمنه ما لم ينقل، ومنه ما نقل آحادًا، ولم يكذب الناقل. وما سبب ذلك إلا أنه لم تتعلق [به] [أحكام]، انصرفت النفوس عن دوام الاعتناء بالنقل.','2','429',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1126,'[فبهذه] [الأوجه] يتبين أن عدم النقل فيه، لا يدل على كونه كان [منسلخًا] [من] كل [ملة] أصلًا، لاحتمال (١٠٥/ أ) ألا يعرف دينه، ولاحتمال أن يعرفه [آحاد]، فلا يصح التواتر، [ولاحتمال أن يعرفه عدد التواتر]، ولكن لا يكون في النفوس ما يقتضي دوام النقل متواترًا.
وأما إلزام الإمام للقاضي، فلا أراه لازمًا، وليس انصراف النفوس عن نقل كونه ليس على دين، كانصرافها عن نقل دينه الذي كان عليه، فإنه قد يتفق حصول غرض في [نقل] الدين الذي كان عليه من أولئك القوم الذين عهد','2','430',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1127,'متبعاً لهم. [وليس] الأمران عندي في عدم النقل على حد واحد، ولكن الوجه ما ذكرناه أولاً، فإذا الصحيح عندي أن أمر ما كان عليه من الدين قبل أن يبعث ملتبس، إلا أنه لم يشرك بالله - عز وجل - هو ولا غيره من الأنبياء أصلاً.

قال الإمام [رحمه الله]: <span data-type="title" id=toc-198>[كتاب] [التأويل]</span>- التأويل: رد اللفظ الظاهر إلى ما إليه مآله [في] دعوى المؤول [إلى قوله] ([على ما يأتي] في كتاب (الترجيح)، إن شاء الله تعالي). قال الشيخ: التأويل في وضع اللغة يرجع إلى تفسير اللفظ ببيان مرجعه ومآله، وهذا لا يختص بتأويل الظواهر، بل يرد على الظواهر والمجملات، فإنها تفسير [ببيان]. مراد','2','431',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1128,'المتكلم [بها]، ولكن [غلب] في عرف الأصوليين اختصاص التأويل بالظواهر.
وقد يطلق التأويل عند الفقهاء على محض الاستدلال [بالدليل الذي ليس بنص، فكأنه بين مراد المطلوب، وقد قال] مالك رحمه الله: يجوز تحريق قري العدو، وتأول قوله تعالي: {ما قطعتم من لينة أو تركتموها قائمة على أصولها}، أن ذلك نزل في تحريق نخل بني [النضير] [وفيها] يقول حسان بن ثابت:','2','432',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1129,'وهان على سراة بني لؤي ... حريق بالبويرة [مستطير]
وأما التأويل عند الأصوليين فهو مخصص بالظواهر، وقد اختلف في حده: فذهب الإمام إلى هذا: وهو صرف اللفظ الظاهر إلى ما إليه مآله في دعوى المؤول، مأخوذ من المآل والمرجع، وقال أبو حامد: التأويل: عبارة عن احتمال معضود بدليل، [يصير] به [أغلب] على الظن من المعني الذي دل عليه الظاهر. وهذا الحد ضعيف، وذلك أنه ليس من ضرورة التأويل أن يعضد بدليل راجح، ولا أن يعضد أولاً بدليل، ولهذا يقال: هذا تأويل،','2','433',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1130,'فما دليله؟ ولو كان العضد بالدليل [الراجح من جملة معقول التأويل]، لم يصح أن يطالب المتأول بالدليل. [وقد] يتأول ويعضد بدليل، ويكون التأويل يساوي الظاهر، فيثبت ذلك رتبة التوقف.
وقال بعضهم: التأويل: بيان انقداح احتمال في اللفظ معضود بدليل. وهذا الحد أيضًا غير مستقيم، أما إبداء الاحتمال، [فلا يكون] تأويلاً، فإن ضرورة الظاهر أن يكون محتملاً، وليس من ضرورة التأويل أن يعضد بدليل.
ويمكن (١٠٥/ ب) أن يكون [أبو حامد] إنما حد التأويل الذي يصار إليه ويحكم به، فيكون الحد على هذا مختصًا ببعض أنواع التأويل، [ولا يكون شاملًا للجميع، ويكون صاحب الحد الثاني إنما حد التأويل] الذي يقف لمعارضة الظاهر، فإنه إنما يعارض الظاهر على شرط العضد بالدليل، فينظر حينئذ للقوة والضعف.
والحد الجامع هو حد الإمام، فإنه يتناول كل تأويل. ولا خفاء على هذا','2','434',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1131,'[بأن] التأويل [لا يرد] على النصوص، ولا على المجملات في عرف الأصوليين، وإن كان له ورود عليها عند الفقهاء والمفسرين. وإذا تعارضت النصوص، بطل التأويل، وإن كانت متواترة، بطل الترجيح، ولا يبقي إلا النسخ خاصة.
وقول الإمام: (إن النصوص إذا تعارضت [نظر] إلى الترجيح) .. ينزل على ما إذا كان النقل آحاداً، وبقية الكلام مفهوم.
قال الإمام [رحمه الله]: (فأما الظاهر الذي يتطرق إمكان التأويل إليه) [إلى قوله] (وسبيل نقل الإجماع التواتر). قال الشيخ:','2','435',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1132,'الكلام ههنا في طرفين:
أحدهما- أن الشريعة اشتملت على مسائل يطلب العلم فيها.
والثاني- اشتمالها على مسائل يكتفي فيها بغلبة الظن. ولابد من بيان القول في الطرفين جميعا. لنقدم على ذلك أولا معني قول الإمام: [إن الجاهل بكون اللفظ نصا [أو ظاهرا] أحق بالعذر من الذي [يجهل] محل العلم [من] المواضع [التي] يكتفي فيها بغلبة الظن]. إنما قال ذلك من جهة أن اللفظة المعينة لا يبعد أن تخفي على الإنسان جهة نصوصيتها أو ظهورها، ولا يلزم من ذلك أن يكون جاهلا بمقتضي اللغات، وتفاوت دلالة العبارات. والجاهل بتمييز مواقع العلم عن المحال التي يكتفي [فيها] بغلبة الظن، جاهل بجميع الشرع، فذلك غلط يتعلق بصورة معينة، وهذا غلط يعم جميع الشريعة.','2','436',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1133,'[مثال الثاني]: أن يكون [لا يفرق] بين النصوص والظواهر في وجه من الوجوه، فهذا أيضا غلط عظيم، فيستوي على هذا التقرير الفريقان.
ثم أعلم أن الشريعة انقسمت أحكامها [إلى] قسمين: [قسم] طلب من الخلق فيه العلم [أو الاعتقاد الصحيح- عند بعض الناس]- ولم يكتف منهم فيه بغالب الظن على حال. [وقسم] اكتفي الشرع فيه بغلبة الظن، ولم يكلف الخلق العلم. والمسائل التي لا يكتفي فيها [بغلبة] الظن انقسمت إلى: كلامية وإلى أصولية وإلى فقيهة.
فالمسائل الكلامية: كحدث العالم، وإثبات الصانع وصفاته، وما يستحيل عليه، وجواز بعثة الرسل وتأييدهم بالمعجزات، إلى ما ضاهى ذلك مما [ذكرناه] في خطبة الكتاب. فهذا الفن لا يكتفي فيه بغالب الظن،','2','437',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1134,'[ولابد] من العقد الصحيح في ذلك. وبعض هذه المسائل [إذا] لم يحصل العقد الصحيح [فيها]، [لكفر بها]، وقد يضلل ويفسق، واستقصاء (١٠٦/ أ) ذلك ههنا غير ممكن.
وأما المسائل الأصولية: [فككون] الإجماع حجة، وخبر الواحد أمارة، والقياس والعمل بالظواهر. وقد ذهب بعض الناس إلى أن [المسائل] كلها قطعية، وليس [كذلك] عندنا، وسنبين ذلك في التفاصيل عند ذكر كل نوع منها. وهذه المسائل الصحيح فيها أنه يلزم التكفير يجحدها، وسيرد ذلك في الكلام على أن جاحد الإجماع، هل يكفر؟','2','438',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1135,'وأما المسائل الفقهية: [فهي] منقسمة إلى مقطوع [به] ومظنون، والمقطوع [به] [منها] على قسمين: قسم علم بالتواتر [والضرورة]، كوجوب الصلوات الخمس والصوم والزكاة والحج، فجاحد وجوبها كافر. [وقسم] يثبت [بالنظر]، كبيع أمهات الأولاد، ومنع نكاح المعتدة عند بعض [العلماء]. ومسائل الشريعة [القطعية] النظرية كثيرة، والمسائل [الفروعية الظنية] لا يتأتي حصرها. هذا التقسيم هو مذهب العلماء من [الفقهاء والأصوليين].','2','439',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1136,'فخرج من ذلك أن القطعيات قسمان: ضروريات ونظريات، فجاحد الضروري كافر، والمخطئ في [النظري] الظني منها، لا إثم [عليه] بحال. [وهل] يتصور فيها الخطأ أم لا؟ [أعني الفقهيات الظنية. هذا] ينبني على أن كل مجتهد مصيب، أو المصيب واحد، والمخطئ معذور؟ (١٢٤/ ب) [وهذا] مذهب الجماهير.
وقد اختلف أهل [الأهواء] في ذلك، فذهبت فرقة إلى إلحاق القطعيات بالظنيات، [فقالوا]: لا يجب على المكلفين العلم بحال، وإنما يجب على مكلف ما يمكنه. وإلى هذا ذهب الجاحظ والعنبري، فقالوا: لا إثم [إلا على من عاند، وأما المجتهد، فلا إثم عليه بحال، وإن كان [في] قواعد العقائد.
وذهب بشر المريسي إلى إلحاق الفروع بالأصول، وأثم','2','440',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1137,'[المخطئ] في المجتهدات. فلنرسم على هؤلاء [الثلاثة] [ثلاث] مسائل.
مسألة: ذهب الجاحظ [إلى] أن مخالف ملة الإسلام من اليهود والنصارى والدهرية وغيرهم، إن كان معاندا على اختلاف اعتقاده، فهو آثم، وإن نظر فعجز عن [درك] الحق، فهو معذور، وإن ترك النظر [لجهله] بوجوبه، فهو أيضا معذور، وإنما الآثم [المعذب]، المعاند خاصة، لأن الله تعالي لا يكلف نفسًا إلا وسعها، وهؤلاء قد لازموا عقائدهم، خوفا من الله تعالى، فلا يعذبهم، [إذ] لم يقدرهم على غير هذا.
وهذا الذي قاله يجوز من جهة العقل، ولكن دلت قواطع السمع على خلافه، وهي أمور معلومة ضرورة، فإنا كما نعلم أن رسول الله - صلى الله عليه وسلم - بعث إلى الخلق وشرع الصلاة والصوم، فكذلك نعلم أن كفر أهل الكفر من المشركين،','2','441',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1138,'واستباح [بيضتهم]، واسترق [نساءهم]، وسفك دمائهم، ووعدهم بالنار إن ماتوا على حالهم. وهذا معلوم ضرورة من الدين. وكذلك [قاتل] جميعهم. [وكان يكشف عن مؤتزر من بلغ منهم ويقتله]. (١٠٦/ب) ونعلم أيضا أن المعاند قليل، وإنما [الأكثرون] مصممون على معتقداتهم، وأمور تلقوها من آبائهم، وقد قال الله تعالي: {إن الظن لا يغني من الحق شيئا}. وقال [- عز وجل -]: {وقدمنا إلى ما عملوا من عمل فجعلنه هباء منثورا}. وقال: {[الذين] ضل سعيهم في الحيوة الدنيا وهم يحسبون أنهم يحسنون صنعا}. وقال: {وجوه يؤمئذ خشعة [٢] عاملة ناصبة}. والآي في ذلك [كثيرة].
فإن قيل: فقد قال الله تعالي: {لا يكلف الله نفسا إلا وسعها}. وهؤلاء لم يقدروا إلا على ما حصلوا. قلنا: نحن نعلم بالضرورة أنهم قد كلفوا خلاف ما هم عليه. [أما] كونهم يقدرون، فلا يشككنا في ذلك','2','442',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1139,'[بحال]. وقد نبه الله تعالي على كونهم قادرين، [بناء] على الاعتياد بإرسال الرسل، ونصب الأدلة، قال الله تعالي: {لئلا يكون للناس على الله حجة بعد الرسل}. وقال: {وما كنا معذبين حتى نبعث رسولا}.
مسألة: ذهب عبيد الله بن الحسن العنبري إلى أن كل مجتهد في العقليات [مصيب، كما في الفقهيات عند بعض الناس. فنقول: إن أردت أنه مصيب] على الحقيقة، من حيث أنه أحاط بالشيء على ما هو عليه، فكيف يتصور أن يكون معتقد قدم العالم مصيبا، ومعتقد حدثه كذلك، مع كون العقل يحيل أن يكون على الصفتين جميعا؟ وكذلك كون [الشخص] رسولا وغير رسول، إلى ما يضاهي ذلك، مما الحق فيه واحد؟ فإن [زعم] هذا القائل أن الإصابة محققة، فالعالم حادث باعتبار (١٢٥/ أ) من [اعتقد] الحدث، وقديم باعتبار من [اعتقد] القدم، فهذا خروج [عن المعقول] بالكلية، [وشر] من مذهب من نفي العلوم من [السوفسطائية]، فإن هذا القائل جعل الحقائق تتبع العقود، مع القطع بأن [الاعتيادات] لا تؤثر في المعتقدات.','2','443',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1140,'[وإن] أراد [أنه] إنما هو مصيب باعتبار التكليف، إذ لم يكلفوا إلا ذلك، فهذا هو مذهب الجاحظ بعينه، وليس ذلك مستحيلا عقلا، ولكنه باطل بأدلة سمعية قطعية ضرورية. وقد تقدم هذا الرد على الجاحظ. ونعلم أيضا بالإجماع [هجران] أهل البدع وذمهم ومقاطعتهم، والتشديد عليهم والإنكار وقطع الاعتذار. فهذه أدلة قطعية.
واعتقاد الشيء على خلاف ما هو عليه [جهل، والجهل بالله حرام. ومهما كان الحق في نفسه واحدا، كان أحدهما معتقدا للشيء على خلاف ما هو عليه].
فإن قيل: فكيف يكلف الخلق ما لا يطيقون، وتمييز المعجزة عن السحر مشكل، وكذلك صفات الله [سبحانه] غامضة، والأدلة فيها متعذرة؟ قلنا: أما تعذر الأدلة فممنوع، وأما كونها غامضة فمسلم، ولكن لم ينته الأمر إلى حد يحيل الإدراك، فإذا نصب الله تعالي الأدلة وخلق العقول، ونبه على الاستدلال، كان الإدراك في قسم الإمكان. وكذلك صادف بعض الناس، ولو كان مستحيلا، لم يصل إليه بحال. (١٠٧/ أ).
مسألة: ذهب بشر المريسي إلى [إلحاق] الفروع بالصول، وقال: إن الإثم غير محطوط عن المختلفين في الفروع، بل لابد أن يكون أحدهما آثما. وزعم أن في كل مسألة فرعية دليلا قطعيا، فمن [أخطأه] [فهو] مخطئ','2','444',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1141,'آثم، والإثم عنه غير محطوط، ولكن المخطئ قد يكفر، كما في أصل [الإلهيات والنبوات]، وقد يفسق، كما في مسألة الرؤية وخلق الأعمال، وقد يقتصر على الإثم، كما في الفقهيات. وإنما يتأتي له ذلك [إذا] كان [استنادا] العمل إلى [المظنون] في الشريعة.
وقد أنكر القياس [ووافقه] على [ذلك] من القائلين بالقياس [ابن علية] وأبو بكر الأصم، ووافقه جميع نفاة القياس، ومنهم الإمامية، فقالوا: لا مجال للنظر في الأحكام، فقالوا: والعقل قاض بالنفي الأصلي في جميع الأحكام، إلا ما استثناه دليل قاطع، [فما أثبته دليل] سمعي، فهو ثابت، وما لا يثبته، فهو باق على النفي الأصلي، ولا مجال [للنظر] فيه.
وقد أنكروا العمل بخبر الواحد، وربما أنكروا الحكم بالعمومات','2','445',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1142,'والظواهر [المحتملة]، حتى يستمر هذا المذهب. وهذا الذي قاله هؤلاء القوم هو [جائز] من جهة العقل، ولكنا نعلم بالإجماع المنقول تواترا، أن أصحاب رسول الله - صلى الله عليه وسلم - درجوا على [خلاف] ذلك، فإنهم كانوا يتشاورون ويختلفون- وهم على المخالفة والتوقير والإجلال والتعظيم- لا يفسق بعضهم بعضا، ولا ينقض [قضاءه]. ولو كان في المسائل دليل قطعي، وكان الخلق مكلفين إصابته، لأثم بعضهم بعضا، ولما صبر له أصلا، وتمتنع العامة من تقليده. [ألا ترى إلى] [أن] [مانعي] الزكاة والخوارج كيف اشتد النكير عليهم، وانتهض القوم لمجاهدتهم، وكفهم عما هو عليه [من خطئهم]؟ فيعلم بالضرورة أنهم لم يسلكوا بمسائل الفروع هذا المسلك، ولهذا قال زيد بن ثابت لابن عباس: [أقول برأيي [وتقول] برأيك]. وتكليف نقل [آحاد] الوقائع في هذا تكليف شطط.','2','446',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1143,'[وكان] من قام من الخلفاء لا يتعرض لأحكام من سبقه بحال، ولو اعتقد خطأه قطعا، لما أهمل [نقضه] أصلا.
فإن قيل: فقد خطأ بعضهم بعضا، (١٢٥/ ب) قال ابن عباس: (ألا لا يتقي الله زيد بن ثابت يجعل ابن الابن ابنا، ولا يجعل أبا الأب أبا)؟ وكذلك [قال] في مسألة العول: (من شاء باهلته إن الله تعالي لم يجعل في المال النصف [والثلثين]]. وقالت عائشة [رضي الله عنها]: (اخبروا زيد بن أرقم أنه أبطل جهاده مع رسول الله - صلى الله عليه وسلم - إلا أن يتوب) في مسألة العينة. وهذه مسائل فقيهة. قلنا: [ما] ذكرناه من [الإجلال] والتعظيم','2','447',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1144,'والإكرام والتوقير منقول تواترا، وهذه أخبار آحاد، فلا [تقف] لما ذكرناه.
على أنه قد يتفق أن يكون المغلط رأى في هذه المسائل الخاصة أدلة قطعية، فغلط بسببها، وقد لا يكون كذلك، فيكون هو غالطا في اعتقاد ما ليس [بقاطع] [قاطعا]، وهذا أدل دليل. [وعلى أنهم] [لو] اعتقدوا (١٠٧/ ب) في المسائل كلها أدلة قاطعة، لاشتد الإنكار، ولعظم الخطب، وارتفعت المجاملة، وثبتت الهجرة والمقاطعة.
فإن قيل: فلعلهم أنكروا ولم يبلغنا. قلنا: هذا مما تحيله العادة، [وكيف] يكون كذلك، ولما حصل الإنكار في مانعي الزكاة والخوارج نقل إلينا ولم يندرس؟ فما الحال في مسائل الفروع [مع] كثرتها؟ كيف يتفق اندراس التقاطع والإنكار فيها؟ هذا محال. فقد تقرر بما ذكرنا انقسام الأحكام إلى ما يكتفي بالظن فيه، وإلى ما لا يكتفي فيه [بالظن]، فليلتحق الظاهر في محل طلب العلم بالمجملات.
[قال الإمام]: (فإن قيل: أنتم تعلمون وجوب العمل [بالظواهر]،','2','448',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1145,'وربط العلم [بالمظنون] محال] [إلى قوله] [نبهنا [بعدها] على سر الكتاب). قال الشيخ: الجواب عن السؤال [بالفزع] إلى الإجماع على وجوب العمل عند الظواهر. وجعل الظواهر أعلاما، إنما يستقيم على مذهب من يقول كل مجتهد مصيب، فإنه إذا ظن الظهور، قطع بحكم الله عليه، وكذلك من [ظنه] من جهة التأويل.','2','449',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1146,'أما مع المصير [إلى أن] المصيب واحد، فما يتأتي [لنا] القطع بثبوت التحريم أو الحل مثلا. وكيف يقطع بذلك، وهو يجوز أن يكون الحكم (١٠٨/أ) لله - عز وجل -[غيره]؟ والإمام يظهر من كلامه أنه [يثبت] في المجتهدات حكما معينا، هو [شوف] الطالبين ومطمح نظر المجتهدين.','2','450',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1147,'وإذا كان كذلك، فهو يظن المصادفة للحكم، فكيف يصح مع ظن الإصابة وإمكان الخطأ القطع بالحكم؟ [وهذا] لا يتصور. وسيأتي تقريره في كتاب القياس. إن شاء الله تعالي.
وإذا ثبت بالإجماع صحة التمسك بالظاهر في محال الاجتهاد، فمحض تطرق الاحتمال إليه لا يمنع الاستدلال، إذ لو كان يمنع، لبطل التمسك بالظاهر، وقد دل الإجماع على صحة التمسك بالظاهر، فإذا [لا] يترك ظهوره إلا إذا عضد التأويل بدليل، [وإن] كان الدليل [مع الاحتمال مساويا] ظهور الظاهر، وجب الوقف. [وإن] كان التأويل مع دليله أظهر صير إليه، وإن كان أضعف، بقي التمسك بالظاهر مستمرا.

قال الإمام: <span data-type="title" id=toc-201>(مسألة: استدل الشافعي رحمه الله في اشتراط الولي في النكاح [بحديث عائشة رضي الله عنها] </span>إلى قوله (ومهر الأمة','2','451',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1148,'لمولاها). قال الشيخ: لا ينكر أبو حنيفة (١٢٦/ أ) ظهور هذا اللفظ في العموم، [وقوته] أيضا، ولكنه لم ينته إلى النص الذي [لا] يقبل التأويل، [بل هو] في أعلى درجات الظواهر، ولا يجوز تركه من غير دليل يمنع التمسك به.
وأبو حنيفة رأى القياس يمنع التمسك بالظهور، فإنه يفرض الكلام في العاقلة الرشيدة التي يصح تصرفها في مالها، فيرى أن التصرف في البضع، كالتصرف في المال. وقد [يقيس] الإناث على الذكور. وكل واحد من [القياسين] يقرب من وجه، [ويبعد] من وجه، فإذا اعتبر تصرفها في [البضع] بتصرفها في المال، قرب الأمر من جهة اعتبار [تصرفاتها]','2','452',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1149,'بعضها ببعض، إذ [المتصرف] واحد، ولكن يختلف [المتصرف] فيه، إذ أحد التصرفين تصرف [في مال]، والآخر تصرف في [بضع]، ولا يبعد اختلافهما. والقياس الآخر يعتبر التصرف في البضع [بالتصرف] في [المال]، ولكن يختلف مع الأصل، فإن الأصل الذكر، والفرع الأنثى، وقد يثور من ذلك فرق. فلأجل هذين القياسين، وهما [مناسبان]، [افتقر] إلى تأويل الظاهر وتخصيص العموم. ولابد في التخصيص من [بقاء] [أقل] ما ينطلق عليه الاسم، وإذا حمل على الصغيرة، لم [يترك للفظ] متناول، إذ الصغيرة لا تسمى امرأة، كما لا يسمي [الصغير] رجلا.','2','453',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1150,'وأما قوله: [ويبطل أيضا بقوله: [فنكاحها باطل]. ونكاح [الصغيرة] لا ينعقد باطلا، بل موقوف على الإجازة، [وقولهم]: [إن تسميته] باطلا، [أي] مصيره إلى البطلان].
وقول الإمام في الجواب عن هذا: [إنما يسوغ ذلك فيما يؤول إليه','2','454',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1151,'[الشيء] ولابد، كالموت الذي إليه مصير كل ذي روح]. فليس هذا الذي قاله مشترطا، وقد قال الله تعالي: {إني أرني أعصر خمرا}. وهو [عندما] يعصر، لا يكون خمرا، ولكنه يؤول إلى ذلك، وقد لا يؤول [إليه]. (١٠٨/ ب) [فلم] يصح الجواب.
ولكن يرد عليهم بأنه إنما [يعبر عما] سيكون بالكائن [في] أمر يصير إلى تلك الحالة، إما لازما، وإما غالبا، أما إذا أمكن أن يكون، أو لا يكون على سواء، فلا سبيل [للتعبير] عن إحدى الحالتين مع تجويز الأخرى.
[ووجه آخر: ] وهو أن التعبير عن الشيء [بما] يؤول إليه نوع من','2','455',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1152,'التجوز، وقد قدمنا أن باب التجوز [مسموع]، ولا سبيل [إلى القياس] فيه بحال. وقد بينا أنه يقال: سل الطلل والربع والقرية، ولا يقال: سل الحمير والكلاب، ويراد أصحابها. فيفتقر إلى النقل أن واضع اللغة يعبر عن النكاح [القابل] للإمضاء والفسخ بأنه باطل في الحال، لاحتمال [البطلان] في مستقبل الزمان.
وأما الحمل على الأمة، فيتجه فيه ما ذكرناه حرفا حرفا.
فأما الوجه الآخر: وهو قوله: [يرد عليهم قول الرسول [- صلى الله عليه وسلم -]: (فلها المهر). ومهر الأمة [لسيدها]. هذا كلام ضعيف، فإنه وإن كان مهرها لمولاها، فلا يبعد إضافته إليها، من جهة أنها السبب في [استحقاقه]، ويضاف الشيء إلى الشيء بأدنى ملابسة، كما يقال: سرج الدابة، وكما يقول أحد حاملي الخشبة [لصاحبه]: خذ طرفك. وقال - عليه السلام -: (من باع عبدا وله','2','456',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1153,'مال، فماله للبائع، إلا أن يشترطه المبتاع).
فإذا لا يفسد التأويل من هذه الجهة بحال، [لمنع تقييد هذا التأويل]، بالنظر إلى أن القوم لا يرون فساد نكاح الأمة، ويقولون: إنما يبطل إذا رده (١٢٦/ ب) [السيد]. وهذا كلام ضعيف، لأن التوكيد الحاصل من الرسول - عليه السلام - يشعر [بتحقيق] البطلان. أما أمكن أن يبطل بالرد، وان يجوز بالإمضاء، فلا يحسن بالكلية إطلاق القول بالبطلان، مع التوكيد. هذا بعيد عن الفصاحة.
[قال الإمام: [زعم] من يدعي التحقيق والتحذق من متأخيرهم: أن الحديث محمول على المكتبة، واستفادوا بالحمل عليها- على زعمهم- استحقاقها المهر، ثم الأمر عند هؤلاء قريب في حمل المرأة على الأمة، والولي على المولى]. قال الشيخ: [قصد هذا القائل] دفع [اعتراض] إضافة','2','457',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1154,'المهر إليها، فإن مهر المكاتبة لها، ولكنهم فروا من أمر سهل، وارتكبوا أمرا عظيما.
قال الإمام: [ومن هذا [المنتهي منشأ] مقصود المسألة] إلى قوله (ولا يراد على [هذا] الخصوص بالصيغة العامة). قال الشيخ: نقرر ههنا قاعدة قبل الخوض في المسائل، وتلك القاعدة: أن التأويل إنما هو: بيان المراد باللفظ في قصد المتكلم في غير جهة الظهور، [فمتى تبينا] أن المتكلم لم يقصد تلك الجهة، فلا سبيل إلى حمل لفظه عليها، وإن كان اللفظ [لو [جرد] النظر إليه- لاحتملها.
ولذلك نقول: إذا اقترنت قرائن باللفظ الظاهر، يصير بها ناصا]، [امتنع] تأويله على هذه الحالة. فإذا تقرر ذلك، لزم أن يعتبر حال المتكلم','2','458',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1155,'وقرائن أحواله، وصورة لفظه، وقوته وضعفه، وحال الواقعة من التأسيس والابتداء، أو غير ذلك من التعرض لبعض التفصيل، مع سبق تمهيد الأصل، وإلى المخاطب، وما يقرب من فهمه، وفصاحة المتكلم، وقصور عبارته، وقصده إلى كمال الإفهام وتمامه، [وإتعاب] المخاطب والتعمية عليه.
فإذا اجتمعت هذه الأمور في النفس، صح أن يكون التأويل منقطعا عن كلام بعض المتكلمين، متطرقا إلى كلام بعض. وكذلك باعتبار المخاطبين، (١٠٩/ أ) فقد يكون للإنسان مع غيره [عادة] في إطلاق [ألفاظ] يفهم منها ذلك الذي اعتادها أمورا، لو أطلقها ذلك المتكلم، لغير ذلك المخاطب، [لم يفهم] منها ذلك.
وكذلك القول الذي يؤتى به للتأسيس في أمر لم يعرف شروطه، ولم يدر سره، ولا مقصود الطالب به، قد يفهم منه الفاهم أمرا، اقتصارا على محض اللفظ، وقد لا يفهم ذلك. [إذا] جاء الكلام في تفصيل قاعدة سبق تقرير','2','459',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1156,'أصلها، كقوله: (فيما سقت السماء العشر). حتى ذهب بعض الناس في هذا إلى [نفي] التمسك بالعموم فيه.
فإذا تقررت هذه القاعدة، فالرسول - عليه السلام - أفصح من نطق بالضاد، وقصده من [ألفاظه] كمال البيان، وإزالة الإشكال، والمخاطبون في زمانه، وهم العرب العاربة، وقواعد القياس عند كثير منهم لم تتقرر، [فإذا ذاك] إنما [يدريه المجتهدون، وليس أكثر من خاطبه الرسول [بالأحكام ذلك] الزمان كانوا مجتهدين، ولا يكون قياس البضع على المال، أو الإناث على الذكور، بالغا في القوة في تخصيص العمومات، كالقرائن الحالية والمقالية، فإنا بالضرورة لو سمعنا رجلا يقول: أيما امرأة دخلت الدار فلها كذا، لم (١٢٧/ أ) يتأت لنا تنزيله على المكاتبة بحال. وقد اشتملت الواقعة على قيود كثيرة من جهة اللغة، تقوى أمر العموم:','2','460',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1157,'منها- الإتيان بأدوات الشرط مؤكدة. ومنها- ترتيب الحكم على الفعل بصيغة (الفاء) المسببة مع الجواب في الشرط، فهو يفهم التعليل، حتى ذهب بعض [أهل] الأصول [إلى أنه] إذا ظهر فهم التعليل امتنع التخصيص. [ومنها-] أنه أكد، والتأكيد بالغ في شدة الاعتناء بالقضية، وامتناع التجوز فيها. ومنها-[أنه] قال: (فإن مسها، فلها المهر بما استحل [من فرجها]). وإنما يطلق هذا اللفظ عند كون العقد فاسدا.
فإذا تقررت الجهات التي يتعذر قصد الاستغراق، امتنع الحمل على النوادر على الإطلاق. وقرائن الأحوال لا تنكر، ولا عند منكري صيغ العموم، كما صور في المريض القائل: لا تدخل على أحدا، على حسب ما صور.','2','461',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1158,'فإن قيل: [فقد] تقدم أن صيغ العموم نصوص في [الأقل]، ظواهر فيما زاد عليه، وقد قال الإمام: (فأما الرد إلى ثلاثة، فلا يستدعي ذلك قرينة [حاقة] في جنس مخصوص). يشير إلى اللفظ فيما زاد على الثلاثة [ظاهر] مطلقا، [وإذا حمل على المكاتبة، فقد بقي دالا على أكثر من الثلاثة، فليس في القضية إلا إزالة ظاهر مطلقا]، فينبغي أن نصغي إليه، ثم يقابل الظاهر بالاحتمال ودليله، ويصار إلى القوي منهما. قلنا: [هذا] الكلام منزل على ما إذا لم تظهر القرائن الدالة على قصد الشمول والاستغراق، ولا يبقي سوى التمسك بظاهر اللفظ، فحينئذ يفرق بين ما اللفظ [ظاهر] فيه أو نص.
فأما ما ذكرنا من القرائن الحالية والمقالية، فلا سبيل إلى القصر على النادر. فإن قيل: فما معنى قول الشافعي: ([النادر] (١٠٩/ ب) ينتحي','2','462',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1159,'بالنص [عليه]، ولا يراد على الخصوص بالصيغة العامة)؟ قلنا: يتنزل الكلام على مثل هذه الحالة، وقد يجوز أن يكون المراد بالنادر هو الذي لا ينتبه [الذهن] له عند إطلاق لفظ العموم، [كالثعلب] مثلا عند إطلاق لفظ الحيوان، فقد لا يخطر بالبال، وعدم تنبه الذهن [له]. وليس كذلك التنبه للثلاثة، وهو أقل الجمع عن إطلاق العموم، فإنه لا يكاد [يشذ عن] ذهنه [الجميع] عند إطلاق لفظ العموم. فهذا هو الفرق بين القليل والشاذ، فيراد بالشاذ: ما لا يتذكر بذكر الجملة.
وأما ما قاله الإمام: (من أن (ما) [و (أي)] [من أدوات] الشرط، غ''ذا فرض الجمع بينهما كان بالغا في القوة). فغفلة عظيمة، وليست (ما)','2','463',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1160,'في قوله: ([أيما] امرأة]). شرطية بحال، وإنما هي زائدة دخلت مؤكدة، والتوكيد حاصل مع الزيادة. فأما المصير إلى أن (ما) في هذا المكان شرطية، فباطل، فكيف تكون (ما) الشرطية مضافة إلى اسم بعدها؟ وضرورة الشرط أن يدخل على الفعل، ومتى دخل الشرط على اسم، فالتأويل يرده إلى الفعل، فلا يصح ما قاله بحال.
قال الإمام: (الطريقة الثانية- أن التعلق بالظاهر يقتضي ظهوره) إلى قوله (بالوجه الذي قدمناه). قال الشيخ: قد تقدم الجواب عنه، وإنما','2','464',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1161,'غمض أمره، من جهة مصير الأصوليين إلى أن ألفاظ العموم ظواهر فيما زاد على أقل الجمع، وإزالة الظاهر ليست منكرة، لكن ينزل ذلك على لفظ لم يظهر منه قصد العموم. وأما إذا ظهر قصد التعميم بقرائن المقال والحال، فيمتنع (١٢٧/ ب) القصر على الأقل، إلا إذا ظهرت أيضا قرائن التنزيل على الأقل، فإنا نعقل ذلك.
وما ذكره أصحاب أبي حنيفة من القياس، لا ينزل منزلة القرائن المقترنة باللفظ المخصصة له. على أنا لا نسلم أن القياس في جانبهم خاصة، بل الذين تمسكوا به قياس عام، وتمسك أصحابنا بقياس يختص بالنكاح،','2','465',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1162,'وهو أنه قصد الشرع بمنعها من تولي عقد النكاح على نفسها، أبقى منصب لها، وحفظها للمروءة، وإبعادًا لها عن الشهوة والشبق إلى النكاح.','2','466',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1163,'ولقد كان أهل الجاهلية على ذلك قيل الإسلام: (تخطب إلى الرجل وليته فيزوجها). كذا قالت عائشة رضي الله عنها. وقال - عليه السلام -: (بعثت لأتمم مكارم الأخلاق). فإلحاق البضع بالمال، أو الإناث بالذكور، غفلة عن هذا المقصود، وانصراف عن مقتضى العادات الحميدة. ولقد يستقل هذا الكلام بمنع توليها النكاح لو لم يصادف الخبر. وهذا الكلام يليق بفن الفقه، وغنما أوردناه ههنا، ليتبين قوة العموم حالا ومقال، فقها ونقلا.
قال الإمام: (فإن قالوا: التخصيص حال في تغيير عموم اللفظ محل','2','467',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1164,'الاستثناء] إلى قوله [فقد لاح الغرض من هذه المسألة]. قال الشيخ: أما قياس التخصيص على الاستثناء، فباطل من أوجه:
أحدها- أنه قياس في اللغات، واللغات تثبت نقلا وتوقيفا، لا قياسا [وتشبيها].
والثاني- أنا قد [نقلنا] عن البصريين أنهم لا يجوزون استثناء النصف بحال. وهو الصواب عندنا. فقد منع حكم [الأصل]، فلا يصح الإلحاق.
الثالث- الفرق بين التخصيص والاستثناء فإن الاستثناء في معني الكلام الواحد، المسوق لما يبقي بعد [هذا] الاستثناء، ولهذا أنه [يمتنع]','2','468',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1165,'تأخيره، وقد تقدم الكلام على هذا. وأما التخصيص، فلا يعد [جزءا] بحال.
وتحقيق القول فيه أن إطلاق القول العام مع إرادة القصر على النادر، [يقع] [في] أبواب [الألغاز]، مطلق لفظ الاستثناء [لا إلغاز] في حقه، ولا [إيهام]. نعم، إن استخرج الأكثر [عد] ذلك من ركاكة القول، والعدول عن الفصاحة. فأما [الإلغاز] والإيهام، فلا يتطرق إليه. فإذا جوزناه- مع اعترافنا بركاكته- منعنا أن يصدر من الرسول [- صلى الله عليه وسلم -]، [لكمال] الفصاحة [وعظيم] البلاغة.
قال الإمام: (مسألة: استدل الشافعي [رحمه الله] في اشتراط تبييت','2','469',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1166,'النية) إلى قوله (في المسألة الأولى). قال الشيخ: هذه المسألة لا تشبه المسألة الأولى، [لا] بالإضافة إلينا، ولا بالإضافة [إلى] أصحاب أبي حنيفة.
أما جهة المفارقة بالإضافة إلينا، فالعموم في تلك المسألة قوي من جهات:
منها- إسناد (١١٠/ أ) العموم إلى حرف الشرط، وهو في اقتضاء التعميم أبلغ من النكرة في النفي.
ومنها- التوكيد الحاصل بالحرف الزائد، ولا توكيد في هذا المكان.
ومنها- فهم التعليل من ترتيب الجزاء على الشرط بحرف (الفاء).
وقوله: ([لا] صيام). لم توجد فيه هذه [الزيادات]، فاللفظ ههنا دون ذلك اللفظ. والمقر تحته- على رأيهم- ليس في الندرة كندور','2','470',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1167,'المكاتبة، فإن [النذر] والقضاء [ليس] قليلا في الشرع، كقلة الكتابة في نساء العالم، فقد قوي في تلك المسألة العموم، [وقل] (١٢٨/ أ) المنفي].
وليست هذه المسألة كذلك في القسمين معا. فهذه جهة التفاوت على رأينا.
وأما التفاوت على رأي أبي حنيفة، فإن القياس الذي عضد به التأويل في تلك المسألة يرجع إلى المعاني المناسبة، واستعمال الأقيسة في جانب العبادات الجامدة بعيد، [واللفظ] ههنا، وإن لم يكن بالغا في القوة، إلا أن الذي يقع تخصيصه به ضعيف.
وما ذهبوا إليه من كونه [تعين] بنفسه]، [فيستغني] عن النية. لا','2','471',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1168,'يصح، [و] مقتضاه الاستغناء عن أصل النية. علي أن معني [قولنا]: تعيين، أي: لا يجوز أن يصام لغير هذا الشهر، فأما أن يستغني عن نية التقرب فلا.
وقد قالوا: لابد من نية في أثناء النهار. وهذا الذي ذهبوا إليه ضد القياس، فقد خالف القوم الحبر والقياس جميعا، فإن ما مضي، لا علي طريق التقرب، كيف يتصور انعطاف قصد التقرب عليه؟ والنية قصد، [ومتعلقة] الحال، أو عزم، ومتعلقة الاستقبال، ولا يتصور [ذلك] في ذاهب [منقض].
فإن قالوا: فقد جاء الشرع بذلك في التطوع: فإنه [لما سأل: هل [شيء يأكله]؟ قالوا: لا. قال: إني إذًا صائم]. فقد أحدث نية في أثناء النهار.
وعنه جوابان:','2','472',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1169,'أحدهما- لا نسلم أنه أحدث نية صوم. وهذا مذهب مالك رحمه الله، فإن التطوع أيضا لا يصح عنده إلا بالنية قبل الفجر أو معه. ثم لو سلمنا، فيصح أن يثبت الشرع للتطوع من [التراخيص] ما لا يثبته [للواجب]، كصحة صلاة النافلة في السفر علي الراحلة غير مستقبلي القبلة، ولا يجوز ذلك في الفريضة. وإذا كان القياس لا يقتضي ذلك، بل يقتضي نقيضه، فكيف [يصح] أن يترك العموم لقياس يقتضي ما يقتضيه العموم؟ فهذا يبطل مذهبهم، ويتعين التمسك بالعموم.
[الثاني-] [إنا لا] ندعي أنه نص في منع هذا التأويل، فامتنع التأويل، لفقدان الدليل [العاضد]، [لا لنبو] اللفظ عنه، بخلاف المسألة الأولي. فهكذا ينبغي أن يتنبه [للآحاد] في تتبع الألفاظ، وجهة تفاوتها.
[قال الإمام]: (وذكر أصحاب أبي حنيفة مسلكا آخر في التأويل).','2','473',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1170,'[إلى قوله] [في يوم قبله بسنة]. (١١٠/ ب) قال الشيخ: ما ذكره الإمام [من] الوجهين في الرد علي الطحاوي، لا يظهر شيء منهما عندي.
أما الوجه الأول: فلم يذكر فيه سوي ظهور اللفظ، والرجل لم ينازع في ذلك، وإنما هو ادعي أن الدليل قد صده عن التمسك بالظاهر، فاحتاج إلي أن يرجع إلي جهة التأويل، [ويعضده] بالدليل، فلا ينبغي أن يكون هذا واقعا في أقسام التأويلات الفاسدة، وإنما يرد النظر إلي أمر الفقه، وينظر في الدليل.','2','474',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1171,'العاضد الصاد عن الظاهر، هل يصح للتأويل أم لا؟ فيخرج الكلام عن غرض الأصول. ونحن [قد] بينا أن قياسهم الذي عضدوا به التأويل غير صحيح.
وأما الوجه الثاني: فلم يحقق الإمام فيه الوجه الذي قصد الرجل، ونحن نحققه، فنقول: قوله [- صلى الله عليه وسلم -]: [لا صيام لمن لم يبيت الصيام من الليل].
اشتمل الحديث علي ثلاثة أوجه من الظهور: أحدهما- نفي كل صوم، لكل من لم يبيت، بالإضافة إلي حكم الإجزاء، فهذه ثلاثة أوجه. [فمن] أصحاب أبي حنيفة من صرف التأويل إلي [عموم لفظ الصوم، فحمله علي البعض، ومنهم من صرف التأويل إلي] المنفي، فرده من الجواز إلي الكمال، ومنهم من قصد [الجمع] بين نفي الكمال والجواز، ولكن بالإضافة إلي صومين لا','2','475',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1172,'إلى صوم واحد، ومنهم من صرف التأويل إلي (١٢٨/ ب) عموم من لم [يبيت].
وتقريره: هو أن الذين [لا يبيتون] ثلاثة أقسام: قسم أضربوا عن النية بالكلية، وقسم قدموا النية علي الدليل، فنووا قبل غيبوبة الشمس، وقسم أخروها عن الليل، فأوقعوها في النهار. وظاهر العموم يقتضي بطلان صوم الجميع.
[فقال] الطحاوي: يستقر العموم في قسمين: وهو من لم [يبيت] أصلا، ومن قدم النية علي الليل، ويخرج منه من أخر النية عن الليل، وأوقعها في نهار الصوم، بناء علي القياس علي التطوع المنصوص عليه. هذا [تقرير] [ما أراده] الرجل، وهو كلام حسن، بالغ في الحسن.','2','476',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1173,'[وما ذكره] الإمام من التشنيع علي (١١١/ أ) الرجل وكونه [يحط من مرتبه- إن صح النقل عنه-]، [وأنه] [فهم ذلك من الفحوى التي لا ينكرها [كل محصل]]. كل هذا [دعاوي] صرفة، وأقوال من غير حجة.
وكونه يقول: المقصود [الأمر] بالتقديم، والنهي عن التأخير، يلزمه تجويز النية قبل الليل، علي حسب ما ذكر، وهو لا يجوز ذلك. فثبت أنه ليس مأخذ المسألة ما ذكره. وإنما المأخذ الصحيح أن الدليل الذي لا يصلح لإزالة الظاهر، وتخصيص العموم لم يوجد.
قال الإمام: [واعلم- هديت [لرشدك]- أن هذه الفنون] إلي قوله (نعوذ بالله منه). قال الشيخ: هذا الذي ذكره ههنا، غلط عظيم،','2','477',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1174,'وقول ممنوع، ونسبة العلماء [إلي] الجرأة علي الدين، وخرق حجاب الهيبة، [لا] يحل إطلاق هذا بحال. وقد قدمنا أن الذي قاله الرجل سائغ في اللغة، جائز عند أهل [البصر] بالعربية. وأما الاستجراء علي الدين، فلو فعلوا ذلك، لكان فسقًا عظيمًا، فكيف يجوز إضافة هذا لأهل العلم؟ ولو كانت لي قدرة علي إسقاط هذه اللفظة ونظائرها من الكتاب لأزلتها، فإنه قول قبيح، وأمر محذور، وتحقير لأهل العلم بالكلية. والله المستعان.
قال الإمام: ([ومما] وجه علي هذا الحديث من التأويل) إلي قوله (من غير [ذي] مذهب أولي). قال الشيخ: المصير إلي حمل الحديث علي نفي الكمال، تأويل لا نزاع في قبوله، [لكن] يبقي أنه غير [متأت] في القضاء والنذر، فإنهما لا يصحان بغير نية. [فإذا] قال','2','478',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1175,'أصحاب أبي حنيفة: يبقي في القضاء والنذر علي ظاهره، ويؤول بالإضافة إلي رمضان والتطوع.
[وقول] الإمام: [إذا تعين حمل [اللفظ علي حقيقته] في بعض المسميات، تعين ذلك في سائرها]. قلنا: هذا الذي قاله لا يصح علي مذهب الشافعي، فإنه يري اللفظة إذا أطلقت، [ولها] حقيقة ومجاز، عمت بالإضافة إليها جميعا. فإذًا لا يمتنع أن يطلق اللفظ ويراد به الحقيقة والمجاز. وقد أجاز الإمام [هذا مع قرينة دالة عليه، فلا يناقض ذلك اللغة، ولا ينافي الفصاحة.','2','479',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1176,'وأما الوجه الآخر، وهو أن القضاء والنذر غير مقصودين. فهذا لعمري فيه عسر، ولكن حملهم علي ذلك أمران: أحدهما- أن اللفظ لا يراد به الحقيقة والمجاز جميعا.
والثاني- أن الدليل عندهم دل علي صحة بعض الصوم بلا نية. وإن حمل علي نفي الإجزاء، لزم أن ينزل العموم علي القليل النادر، وهو القضاء والنذر. فإن حمل علي الغالب، وهو الصوم الذي هو ركن الإسلام، اقتضي (٢٩/ أ) نفي كماله دون إجزائه، كدلالة الدليل عندهم علي صحة الصوم [المعين] بلا نية تبييت، فيجب حمل العموم علي الغالب دون النادر.
وقول الإمام: (إنه لو حمل علي نوع واحد من الصوم، ثم حمل فيه علي نفي الكمال، لما كان عاما أصلا). إن أراد أنه لا يبقي علي عمومه، فصحيح، إذ هذا تأويل مخصوص، وإن أراد أنه لا يبقي له عموم النية، فليس بصحيح، فإنه قد بقي متناولًا للصوم الأعم الأغلب، فرضا ونفلا].','2','480',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1177,'وقوله في الرد عليهم: إن الذي [ذكروه] (ليس مذهبا لأحد). وإن أراد أن القضاء والنذر ليس صوما في الشرع، فيفتقر إلي نية، فهو كذلك، ولكن القوم لم ينازعوا في هذا، وإنما كلامهم، هل [قصدا] بهذا الخبر، [وتلقي حكمهما] منه، [أو] [تلقي حكمهما] من وضع آخر؟ هذا محل الكلام، [وليس] فيه إجماع، وللكلام وجه في الصحة.
قال الإمام: (مسألة: استدل الشافعي في نكاح المشركات) إلى','2','481',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1178,'قوله (بلفظ المصطفي [- صلى الله عليه وسلم -]. قال الشيخ: الكلام في هذه المسألة يقع في أبواب تأويل الظواهر، لا في أبواب تخصيص العموم، والمسألة يتعلق النظر فيها بجهات:
منها- النظر في إطلاق لفظ [أمسك]، هل يقتضي استمرارًا، أو يحمل علي [الإمساك] لتجديد العقود؟
الثاني- إن قوله: [أربع] مطلقٌ، ظاهره الاسترسال علي النسوة من غير تخصيص.
الثالث- إن (١١١/ ب) لفظة الأمر [ظاهرة] في الطلب، محتملة [للإباحة] والتخيير.','2','482',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1179,'الرابع- إن الحكم الثابت علي الصحابة، مطرد في حقنا، ولا يكون [للأزمان] تأثير في [التقييد] بحال. وقد تكلم أصحاب أبي حنيفة علي المسألة باعتبار هذه الجهات. والذي حملهم علي ذلك القياس، فإنهم قالوا: إذا وقع العقد [فاسدا]، لم يتأت أن يصح في استدامته، وإنما يستديم علي [حسب] ثبوته. قالوا: فأنكحة المشركين إذا وقعت فاسدة، كيف يصح أن','2','483',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1180,'تصح بالإسلام؟ فلما وردت هذه الأخبار، جنحوا إلي طرق التأويل.
[فأما] الوجه الذي أضرب الإمام عن تقريره، ورآه معاندة [للنص]، فإنه يرجع إلي تقييد المطلق، وهو قولهم: أراد الأوائل. ولقد صدق رحمه الله، فإن الرسول [- صلى الله عليه وسلم -] قد قال لصاحب [الأختين]: (اختر أيتهما شئت، وفارق الأخرى). وفهم صاحب الواقعة التخيير. وكذلك صاحب الخمس حتى قال: [فعمدت إلي أقدمهن صحبة عندي ففارقتها]. ثم هذا بعيد من مذهب أبي حنيفة، فإنه يذهب إلي أن تقييد المطلق يقع [في] أبواب النسخ،','2','484',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1181,'فكيف يصح أن يورده في أقسام التأويل؟
وأما الثاني- وهو حمل الإمساك علي ابتداء [التجديد] [للعقود]، فلعمري إنه لتأويل يقبله اللفظ، ولكن قد تجتمع قرائن تدفعه، وأن كان كل واحد [منها] علي [انفرادها] [لا يسقطه]. وقد اشتمل [الحديث] علي قرائن:
منها- أنه قابل لفظ [الإمساك] بلفظ المفارقة، وهذا إذا قوبل بالمفارقة، ظهر منه التمادي على ما كان.','2','485',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1182,'الثاني- أنه رد الخيرة إليه فقال: [[أيتهما] شئت]. وهو إذا أرجع الأمر إلي ابتداء النكاح، لا يختص بالخيرة، [بل] تثبت [لهن أيضًا] خيرة.
الثالث- أنه [كيف] كان ينتظم [دخولهن] تحت [ربقة الرضا] لو كان) ١٢٩/ ب (لهن القول؟
الرابع- أنه لعله ألا ينكحهن بعد أن قضي منهن وطرا، بل يكن كسائر نساء العالم.','2','486',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1183,'[الخامس]-[لأن] في هذا تأخير البيان عن وقت الحاجة، فما [أحوج] جديد [عهد] بالإسلام [إلي البيان].
السادس- أنا لا نشك في أنه لا يسبق إلي فهمه من هذا الحديث إلا التمادي علي الأنكحة السابقة، والقياس الذي يزعم أبو حنيفة أنه يرشد إلي ذلك، لا يفهمه من هو جديد عهد بالإسلام، مع [تنجيز] الحاجة إلي البيان.
هذا لعمري واضح جلي. وقد قررنا أن التأويل إنما هو مصير إلي ما يقصد المتكلم في غير جهة الظهور، فإذا ظهر أن المتكلم [لم] يقصد تلك','2','487',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1184,'الجهة، امتنع المصير إليها تأويلا، هذا هو الحق.
وأما ما ذهب إليه الإمام: [من أن المخالف إن عاند في كونه الخبر نصا، فإنه لا ينكر ظهوره، وغلبة الظن كافية في هذا الباب، وهي مقدمة علي ما يظن من جهة القياس] إلي آخره. فكلام في غاية الضعف، وهو أيضًا قد [نقضه] علي نفسه.
أما بيان ضعفه: فمن جهة أن المتأول لا يسلم بعد عضد التأويل بالقياس، ظهور مقصد المتكلم في جهة الظهور، بل [يظن] نقيض ذلك، [وينسب] المتمسك بجهة الظهور إلي مخالفة قصد الرسول. ولا يجري هذا مجري تقديم الخبر علي القياس علي ما تخيله، فإن المتمسك بالقياس في تلك الصورة، [معترف] بأنه مخالف للفظ [بالكلية]، وليس كذلك ما إذا تأول (١١٢/ أ) وعضد بالدليل، فإنه يقول: إنما [تمسكت] بلفظ الشارع خاصة، فأين هذا من تقديم الخبر على القياس؟','2','488',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1185,'[وأما] النقض الذي نقض به على نفسه، فهو أنه يقول: (إذا ورد القياس مخصصا للعموم، أو مزيلًا للظاهر إلى جهة الاحتمال، ليس الأمر في ذلك متروكًا سدى، بل على الناظر أن يزن [كفه] ظنه من الظاهر [بالباطن] [الملتقى] من القياس، [وأيهما] كان أغلب أتبعه، إذ ليس كون هذا ظاهرًا، [أو كون] هذا قياسًا بالذي يقتضي تقديم احدهما على صاحبه). ولعمري إن [هذا] القول منه لحق، وهو الصواب الذي لا ينبغي العدول عنه.
وأما الجهات التي [قررنا أنها] [تقوي] الظاهر، وتمنع من المصير إلى التأويل، فليست قطعية، وإن كانت جلية. وقد يختلف الأمر باختلاف أحوال المجتهدين. [وإنما] في الأصول، [لتذليل] الطرقن وكيفية التصرف في ألفاظ الشارع، [وتبيين] القواعد.','2','489',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1186,'[قال الإمام: ] [فإن قيل: كأنكم تبنون هذا علي تقديم الخبر علي القياس] إلي قوله [[فلم] يكن ذلك لغفلتهم عن أمثالها]. قال الشيخ: قد قدمت أن المتأول إذا عضد [التأويل] بقياس، لم يخالف لفظ الرسول بحال، وإنما هو [متمسك] باللفظ [في] جهة [يحتملها]. ولو كان اللفظ الظاهر يتقدم علي القياس مطلقا، لما صح تأويل ظاهر [معضد] بدليل.','2','490',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1187,'بقي أنه زاد ههنا زيادة: و [هي] إذ ظهر قصد الشارع. قلنا: قد يظهر قصد الشارع من مجرد لفظه، فهذا (١١٣/ ب) قد ساعد الإمام علي صحة تأويله بالقياس. وإن أراد أن يظهر قصده من أمر زائد علي عموم لفظه، فإن أراد ذلك، وقال: هذا هو الظاهر الذي [يمتنع] تأويله، وإن عضد التأويل بقياس، فنقول له: أردت أنه ظهر مقصود (١٣٠/ أ) الشارع علي وجه مقطوع به [أو مظنون؟ فإن كان علي وجه مقطوع به]، فصحيح، لاستحالة تصور التأويل في هذه الصورة، ولا يبقي إلا المناقضة والإسقاط.
وإن أراد أنه ظهر علي وجه مظنون، فهو كما لو ظهر اقتصارا على محض','2','491',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1188,'الظاهر. نعم، [تتظافر] الجهات اللفظية المغلبة علي الظن، فيفتقر إلي قوة الدليل العاضد للتأويل، وينظر في [تقابلهما]، ويجع الحكم تابعا للجهة القوية. هذا هو التحقيق.
[وأما] قوله: [إنه لا يعتضد القياس إلا بإجماعهم]. فهو كذلك.
وقوله: [[وإن] أنصف الخصم، علم أنهم ما كانوا يقيسون في مثل هذه [الحالة]]. ليس الأمر علي ما قال، فإنه قد يتفق أن يكون القياس العاضد للتأويل في معني الأصل، بحيث يصلح [للنسخ]، فكيف لا يصلح لإزالة ظاهر قوي عاضد لتأويل ضعيف؟ .
وقوله بعد ذلك: [[وإن] ركب رأسه، وطرد شماسه]. ألفاظ مستنكرة، مستهجنة عند أهل العلم، لا يليق ذكرها بالعلماء، وإنما تذكر هذه الألفاظ بالإضافة إلي البهائم، والغوغاء من الناس [يأخذ] المسألة من','2','492',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1189,'إطلاق الألفاظ المستهجنة الركيكة.
وقوله: [لم يمكنه أن [ينقل] قياسهم في هذه الصورة]. يتوجه مثله عليه، فإنه ليس معتمده [في] العمل بالظاهر إلا إجماعهم. ولهم أن يقولوا: من أنصف [علم] أنهم ما كانوا يتعلقون بالظاهر في المواضع التي تعارضها الأقيسة الجلية، وإن ركب الخصم رأسه، وطرد شماسه، لم يمكنه أن يعلم علمهم بالظواهر في مثل هذه الصورة. وكيف يتصور أن ينتفع المتكلم بكلام يتوجه عليه بعينه في القضية التي ينكرها علي خصمه؟ هذا [والله] محال، وغفلة عظيمة، نعوذ بالله من الحيرة.
وقوله: [ولا شك أنهم ما كانوا يقيسون في كل موضع]. كذلك يقول [له] الخصم: ولا شك أنهم [ما كانوا] يقضون بالظواهر في كل موضع.','2','493',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1190,'وقوله: [[ولو] تتبع [المتتبع الأخبار] التي رويت لهم، فعملوا بها، لوجدها ظواهر]. هذا الكلام بعيد، مع [إنكار] العمل بالظواهر.
[فأما] من اعترف بها، فما الذي يحوج إلي هذا الكلام؟ إلا أن يزيد فيه: أنه لو تتبعها لوجدها ظواهر مخالفة للأقيسة، ومع [هذا] أعملوها وأضربوا عن القياس. فهذا [أيضًا] [إذا] نقل علي هذا الوجه [الذي] نقل، إجماع علي تقديم الظواهر علي جميع الأقيسة. وليس الأمر كذلك، وهو أيضًا لا يدعيه، ولا يصير إليه، ولا يرتضيه.
قال الإمام: (مسألة: مما يجريه أبناء الزمان في [أدراج]','2','494',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1191,'[الاعتراضات]] إلى قوله [[يتنزل] منزلة [الإجمال] في صيغ الأقوال]. قال الشيخ: هذا الكلام من أصحاب أبي حنيفة بعد العجز عن التقييد [للمطلق]، والتأويل [بظاهر] لفظ [الإمساك]، (١١٤/ أ) مع كون القياس يقتضي أن العقد إذا وقع فاسدا، لم ينقلب صحيحا، فمنعوا أن تثبت أنكحة الكفار في زماننا، وسلموا أن الخيرة ثبتت لأولئك الناكحين في ذلك الزمان بعد الإسلام. فإذا قيل لهم: الأحكام لا تختص بالأشخاص، [ولا] بالأزمنة، فليطرد علي الكفار اليوم ذلك الحكم؟ قالوا: إنما [يلزم] طرد الحكم، إذا استوت الأحوال، [وأما]) ١٣٠/ ب (مع الاختلاف فلا','2','495',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1192,'وهذا [الكلام] صحيح.
وجهة الاختلاف: أن الأنكحة لم تكن محصورة في عددٍ في ابتداء الإسلام، [فكانت أنكحة] أولئك القوم الذين أسلموا وقت وقوعها صحيحة، [لعدم] الحصر، ووافق إسلامهم الحصر فخيروا، ولم تبطل أنكحتهم بجملتها لسبق الصحة، ولم [يقروا] عليها بالكلية، لوقوع [الحصر].
قالوا: فلا يحق بهذه الأنكحة أنكحة وقعت بعد الحصر، لتحقق الفساد فيها، واستقرار الصحة في تلك
وقد اختلف الأصوليون في الجواب عن هذا، فذهب ذاهبون إلي أن الاستدلال قد [استقل]، وما ادعاه القوم محض إمكان، والإمكان لا يدرأ','2','496',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1193,'الاستدلال. وقال المحققون: هذا مضر قادح، وفرق بين إمكان [يعارض] ظاهرا، [وبين إمكان لا يعارضه]، [فالإمكان] الذي يعارض الظاهر، لا يضر في التمسك بالظاهر في محل الأعمال، كما تقدم، وليس عندنا [ظاهر] يتمسك به في أن هذه الأنكحة وقعت بعد الحصر، فعلي تقدير [أن تكون بعد الحصر، يكون فيها متمسك. وعلي تقدير] أن تكون قبل الحصر، فلا يتمسك بها فتسقط، [ولا] يكون ذلك ترك دليل بإمكان، بل تمسك في الاستدلال بمجرد إمكان. وهذه [صورة].
[والصورة] الأخرى: أن ينقل أن هذه الأنكحة بأعيانها وقعت بعد','2','497',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1194,'الحصر، فهذا أعلي شيء في الاستدلال، ولا يبقي [للخصم] كلام علي حال.
الصورة الثالثة: أن ينقل أصحاب أبي حنيفة عدم الحصر في ابتداء الإسلام، وأن تلك الأنكحة وقعت في حال عدم الحصر، فلا يتصور استدلال علي هذا الوجه بتلك الأقاصيص.
الصورة الرابعة: أن لا ينقل ذلك ولا نقيضه، ولكن يقال: لعله لم يكن في ابتداء الإسلام حصر، [وقد] يمكن أن تكون تلك الأنكحة وقعت في ذلك الوقت. وهذا الاحتمال غير قادح، فإن الالتفات إلي [أصل] [هذا، يمنع] التمسك بكثير من الأدلة، وهو احتمال معرض [عنه] بالإجماع.
قال الإمام [رحمه الله]: [ولكن لو صح ما ذكروه، انقدح وراءه','2','498',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1195,'[وجهان] [من الكلام]] إلي قوله [فهذا وجه]. قال الشيخ: هذا الذي ذكره الإمام من أنه إن صح النقل، فهو في الحصر في العدد وهيهات. كلام صحيح، فإن الصحابة رضوان الله عليهم كانوا خلقا كثيرا، وكان منهم الموسر والمعسر، مع شهوة العرب في النكاح ورغبتهم فيه، ولو اتفق أن تكون الزيادة علي الأربع مشروعة، لفعل بعضهم ذلك، وفارقوا بعد نزول الحصر، ولم ينقل ذلك بحال.
أما قوله في الجمع بين الأختين: [فلم يعهد (١١٤/ ب) مسوغا في ابتداء الشرع]. فكذلك أيضا. وقوله: ({ما قد سلف} من الاستثناء من','2','499',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1196,'غير الجنس، أي من أمر الجاهلية]. ليس كذلك، بل قد عهد [ذلك] [مسوغا] في زمن يعقوب - عليه السلام -، وقد نقل المفسرون وأصحاب السير أن يعقوب [- عليه السلام -] تزوج أم يوسف وأختها.
وأما استدلاله علي ذلك بقوله تعالي: {إنه كان فاحشة ومقتًا وساء سبيلًا}. غفلة عظيمة عن نص القرآن، وهذه تكملة إنما هي لآية سبقت، وهي قوله تعالي: {ولا تنكحوا ما نكح ءابآؤكم}. (١٣١/ أ) الآية. وأما هذه فتكملتها: {وأن تجمعوا بين الأختين إلا ما قد سلف إن الله كان غفورًا رحيمًا}.
قال الإمام: (والوجه الآخر- أنا نقول: لو صح ما ادعوه من صحة [مناكح','2','500',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1197,'المشركين]) إلى قوله (بمجرد الاحتمال أصلا). قال الشيخ: هذا الكلام الذي أورده الإمام لازم، لا إشكال في لزومه، فإنهم حكموا ببطلان نكاح الرضيعتين، لتعذر الاستمرار في [الجمع]، ونسبة المحرم إليهما نسبة واحدة، ورد الفسخ إلي إحداهما بغير عينها متعذر، وتخصيص واحدة بالفسخ تحكم، فلم يبق إلا [إبطال] النكاحين جميعا. وهذا موجود في تلك الأنكحة إذا ادعي سبق صحتها، فلابد علي هذا من الاعتراف بالتخفيف والترخيص في أنكحة المشركين، [وأنها] جرت علي خلاف أنكحة المسلمين. وإذا ثبت التخفيف والترخيص، فلنجر الأخبار علي ظواهرها، ولا حاجة إلي هذا النوع من [التكلف] فيها.','2','501',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1198,'قال الإمام: (مسألة: [إن] صح عن رسول الله - صلى الله عليه وسلم -[قوله]: (من ملك ذا رحم محرم [عتق عليه)] إلي قوله [لأقيسة [تعن] لهم)','2','502',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1199,'قال الشيخ: الأمر علي ما ذكره الإمام من (أن الخبر لو صح، لم يصح تأويل متبعي الشافعي في حمله علي الأصول والفصول). لأن العموم قوي، والقصد إلي الاستيعاب واضح، لحصول الاستدراك بالفصل، فيدل علي تناهي المتكلم في التصور والبعد عن التجوز. وظهور التعليل أيضا يقوي ذلك، [فقصر] هذا علي الآباء والأبناء بعيدا جدا.
[ثم] للأب خاصية [تتقاضي] تلك الخاصية، [وهي]','2','503',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1200,'التنصيص [عليه]، حتى لو أطلق علي الأب كونه قريبا، لعد ذلك هجرا، [وحسب ذلك] لغوا. ولو عبر عن [النبي] بكونه رجلا مسلما، لكان المعبر جاهلا مختلا، وإن كان مسلما علي الحقيقة. ولكن له خاصية ليست [مجرد] الإسلام. ومن كانت عادته إكرام [أبيه] فقال: من عادتي إكرام أقاربي، عد [ملغزا].
والقياس الذي عضدوا به هذا التأويل ليس بالغا في القوة مبلغا يحوج إلي ٠ [تقدير] حتى يقول: إنه سئل عن: [من] ملك الأب، فقال هذا الجواب','2','504',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1201,'وقياسهم إنما هو [تخصيص] هؤلاء [بالنفقة]، بناء علي [البعضية]، فكأنه كما وجب [عليه] أن (١١٥/ أ) يحيي بعضه [بالإنفاق]، وجب عليه أن يخرجه من الإرقاق. وهذا قياس ضعيف، والعموم قوي، والعدول من لفظ يخص إلي لفظ يعم، قريب من الإلباس، ولكن الحديث لم يصح عند الشافعي، [فإن] الحديث موقوف علي الحسن ابن عمارة.
وأما ما ذكره الإمام بعد ذلك من تقديم الخبر علي القياس بإجماع الصحابة، فقد سبق كلامنا عليه، فلا معنى للإعادة.','2','505',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1202,'وأما قوله: [[كان الراوي] الموثوق به إذا روي [لهم] خبرا، [يعلمون] به، ولا يعرجون علي التأويلات البعيدة، لأقيسة [تعن] لهم]. هذا الكلام صحيح، إذا كان [التأويل] مع عاضده من القياس لا يبلغ قوة الظاهر. فأما إذا كان [يزيد] عليه [في غلبة الظن]، فلا معني لترك التمسك به.
وقوله: (إنه في حكم الراد لخبره). ليس كذلك، بل إنما هو تمسك [به].
وقوله: (لو رددنا إلي عقولنا، لما سفكنا الدماء) إلي آخره (بأقيسة).
قلنا: وكذلك قلنا لا نفعل ذلك، بناء علي أخبار الآحاد [والظواهر]، ولكن','2','506',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1203,'فهمنا أن [الشارع] اكتفي منا بغلبة الظن في [فروع] الشريعة عند فقدان القواطع، فلنتبع غلبة الظن، سواء كانت في تأويل [معضود] بدليل، (١٣١/ ب) أو في التمسك [بالظاهر]. هذا هو الحق في ذلك.
قال الإمام: (فإذًا الألفاظ [المأثورة] علي ثلاث مراتب) إلي قوله (ما في معناها). قال الشيخ: الكلام من جهة التأصيل حسن، والكلام على الأمثلة لا يقوى.','2','507',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1204,'أما حسن الكلام، باعتبار التأصيل، فهو أن اللفظ العام إنما تعلق به من جهة أنه يغلب على الظن أن مطلقه قصد به الاستغراق، إما من جهة قرينة حالية، أو من جهة [أن] [أكثر] ما يطلق هذا اللفظ [في اللغة] لإرادة الشمول والاستيعاب.
فإذًا لم يتمسك به إلا من جهة غلبة الظن، مع ظهورٍ [في قصد الاستغراق. فإذا ظهر أنه لم يقصد الاستغراق، فكيف يتمسك بغير لفظه مع ظهور] قصد المخالفة [له]؟ وقد قدمنا أن الألفاظ لا تدل لأعيانها، وحققنا ذلك في كتاب (الأوامر).
وأما ما ذكره من إجراء المثال على هذا الأصل، فلسنا نسلمه، وقد','2','508',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1205,'حققنا قريبا من هذا الطريق في العام الوارد على [سبب] خاص، وقلنا: إن العموم لا يخصص إلا بما يناقضه راجحا عليه. أما إذا كان الوارد يناقض العموم، فلا وجه لتخصيصه [به].
واقتصر الإمام في هذا المكان على أن الرسول [- صلى الله عليه وسلم -] لم يقصد التعميم، وإنما قصد الفصل بين ما يجب فيه العشر، [وبين] ما يجب فيه نصف العشر. قلنا: لا مناقضة بين ذلك، وبين قصد التعميم. وإذا أمكن ذلك من غير مناقضة، وفي اللفظ صلاحية الشمول والدلالة على العموم، فلم [يتحكمون] بإسقاط دلالة اللفظ من غير ضرورة تدعوا إلى ذلك؟','2','509',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1206,'[والصواب] عندنا في هذا أن يتمسك بعمومه، [ويطلب] دليل التخصيص، ولكنه قد لا يبلغ في قوته.
وأما قوله (١١٥/ ب) تعالى: {وثيابك فطهر}. فإن هذا ليس من الألفاظ العامة، لأنا قد قررنا أن العموم من عوارض الأسماء دون الأفعال والحروف. نعم، يقع هذا في أقسام المطلق، فإنه إذا قال: {وثيابك فطهر}.
فمتى وجد تطهير ما، وجب الاكتفاء [به] من غير نظر إلى الآلة، إلا أن يدل دليل على القصد إلى تعيين آلة، فيكون من أبواب المطلق، إذا دل دليل على تقييده، فمتى وجد التطهير بالخل وغيره، اكتفي به على مقتضى [الإطلاق].
قال الإمام: (المرتبة الثانية: أن يظهر قصد التعميم) إلى قوله (في','2','510',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1207,'مسائل الشرع). قال الشيخ: ما ذكره الإمام في المرتبة [الثانية] من غير قصد التعميم، أنه يمنع من التأويل والعضد بالقياس، [فقد] تكلمنا عليه، وأبدينا فيه [تفصيلا وتقسيما]. [وفرقنا] بين أن يظهر قصد التعميم قطعا، أو يكون الظهور مظنونا، وفرقنا أيضا بين رتب القياس العاضد للتأويل، فلا نعيده. فليطالعه المنتهي إلى هذا المقام.
وأما المرتبة الثالثة: وهي أن [يعرى] اللفظ العام عما يقويه وعما يضعفه، فهذا موضع التأويل عنده، قال: (وليس الأمر في ذلك أيضا متروكا [سدى، بل] على الناظر أن ينظر إلى ما دل عليه الظاهر، وإلى جهة التأويل','2','511',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1208,'[المعضودة] بالقياس، فأي الجهتين كانت [على] الظن أغلب، اتبع الحكم موجبه). هذا الكلام صحيح، ولكنه خلاف ما تقدم له من أنه (إذا تعارض العموم [والقياس] على صورة مخصوصة، فإنه يجب الوقف).
ولم يقف في هذا المكان. بل قال: يتبع الحكم الجهة القوية. وهذا كلام صحيح، وهو الذى كنا اخترناه فيما سبق.
وأما الصورة الأخرى: وهي إذا تساويا، فالصحيح ما ذهب إليه القاضي من وجوب الوقف. وتوجيهه ما قررناه، إذ ليس كون هذا قياسا، [وكون] هذا ظاهرا، (١٣٢/ أ) [مما] يقتضي تقديم أحدهما على صاحبه. وكيف لا يكون كذلك، وقد وافق الإمام على أنه لو كان الظن [أغلب] في جانب القياس [لعمل به؟ ولم يقل إن هذا [من قبيل] تقديم القياس على الخبر','2','512',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1209,'[فيمتنع]. فإذا صح أن يتقدم القياس] على العموم عند قوته عليه، كيف لا يصح أن يوقفه عند مساواته [له]؟ وهل التفرقة بينهما [إلا] محض الوهم؟
ثم إن الإمام قد قال فيما سبق: (إن القياس إذا ورد مخصصا للعموم، فلابد من الوقف). وقد ذهب ههنا إلى إبطال الوقف على الإطلاق، فإنه ([إذا] ظهر رجحان أحدهما، قضي به، وإن استويا، قدم الخبر). فإذًا لا وقف على حال. [وهذا] تناقض بين. فإذًا الصواب ما ذكره القاضي، وهو الذي تقدم [للإمام]، لكن على وجه التأويل، فإنه أطلق [هناك] الوقف، وقضى [هنا] بأن الأقوى في (١١٦/ أ) النفس يعتمد.','2','513',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1210,'قال الإمام: <span data-type="title" id=toc-206>(مسألة: مما رده المحققون من طرق التأويل) </span>إلى قوله ([في كلا] الوجهين باطل). قال الشيخ: ما ذكره الإمام من أن المحققين [ردوا] كل تأويل يفضي إلى حمل كلام الشارع على جهة ركيكة، تنأى عن قبولها اللغة الفصيحة، كلام [صحيح]، إذا وجد عن ذلك معدلا، ولم [ترهق] إليه ضرورة.
وأما المثال الذي ذكره، وهو حمل [الكسر] على [الجوار] في','2','514',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1211,'قوله [تعالى: ]} وأرجلكم .. }. من غير مشاركة [المعطوف] المعطوف عليه. ولعمري إنه [لكذلك]، إذ [للحمل] على الخفض، بناء على ظاهر الإعراب، وجه مستقيم. [وإذا] أمكن الوجه الفصيح، فالعدول إلى [الندور] ممنوع.
وأما قوله: ([في الكسر] على [الجوار] أنه لا يتسامح به، إلا في مضائق القوافي [وأوزان] الشعر). فليس كذلك، فإنه قد جاء الكسر','2','515',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1212,'[على] الجوار في [نثر الكلام]. ومن أمثال العرب: (جحر ضب خرب). بخفض (خرب). وإن كان نعتا للجحر، [لا للضب]، ولكنه شاذ. [والذي] [حمله] عليه [الأمن] من اللبس.
وقد اختلف النحويون في قوله تعالى: {قتل أصحاب الأخدود، النار ذات الوقود}. فقال البصريون: إنه بدل من الأخدود بدل الاشتمال. وقال الكوفيون: إنه خفض على [الجوار]. ومنعهم من البدل إنه لا ضمير يعود على المبدل منه، [وأنت] إنما تقول: سلب زيد ثوبه، ونفعني عبد الله','2','516',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1213,'[علمه]، فيأتي بالعائد، ولا عائد في النار يعود على الأخدود.
وتأول الأولون [وقالوا: ] التقدير: قتل أصحاب الأخدود نارها، [واستغنى] بالألف واللام عن ذكر الضمير، كما قال الله تعالى: {مفتحة لهم الأبواب}. يعني منها.
[ولكن] مع هذا كله، فليس [الكسر] على الجوار بمشهور معتاد، وإنما تدعو إليه الضرورة، وهو باب مسموع، وليس للقياس فيه مجال. [فإذا] كان كذلك، لم يصر إليه إلا لضرورة، ولا ضرورة في الآية، إذ يمكن أن يكتفي [بالمسح] في الرجلين، إلا أن يثبت بطريق يبين وجوب الغسل، فيرجع إلى التأويل، فإن وجدنا عن الكسر [على] الجوار معدلا، لم يصر','2','517',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1214,'إليه، وإلا صرنا إليه. وقد قررنا أن هذا جرى في القرآن، كما نطق أهل اللغة [بقولهم]: (هذا جحر ضب خرب).
[وقوله]: (وقال من أحاط بعلم هذا [الباب]: حمل قراءة من قرأ: {وأرجلكم} [بالنصب] [على المسح في الرجل]، [والمصير إلى أنه محمول] على محل [رؤوسكم أمثل] وأقرب إلى قياس الأصول، [من حمل قراءة الكسر على الجوار]، [فإن] كل مجرور اتصل [الفعل به بواسطة]، الجار [فحكمه] النصب). هذا اللفظ فيه [نظر]، فإنه','2','518',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1215,'قد يتصل الفعل بواسطة الجار، ويكون [المحل] رفعا، كقوله [تعالى]: {[وكفى] بالله شهيدًا}. ولكن إنما تكلم في الفعل المتعدي إلى المفعول بواسطة الحرف، (١٣٢/ ب) هذا إذا كان الحرف محتاجا إليه.
وأما في هذا المكان، فالأمر أجدر، فإنه يصح الاستغناء عن [(الباء)] في هذا المكان، فهي [زائدة]، فلا يخفى أن المحل (١١٦/ ب) محل نصب.
وقوله: (والعطف على المحل من فصيح الكلام). هو كما قال. ولكن لا يصح ذلك في كل موضع، [فإن] القائل إذا قال: إن زيدا قائما، فـ (زيد) في محل رفع بلا خلاف. ولو قال: إن زيدا وعمرو قائم أو قائمان، لم يجز،','2','519',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1216,'فقد امتنع العطف على المحل في هذا الوجه. ولو قال: إن زيدًا قائم وعمرو، جاز العطف على المحل. وقد يتحتم العطف على المحل، [ولا يجوز على اللفظ على حال، وهذا إذا قلت: جاءني هؤلاء وزيد، وهذا زيد، لم يجر العطف إلا على المحل. فإذا صار العطف على المحل ينقسم ثلاثة أقسام: قسم لا يجوز بحال، وقسم لا يجوز غيره، وقسم يجوز العطف على المحل واللفظ جميعا.
فأما الموضع الذي يتحتم العطف فيه على المحل]، فهو من الأسماء المتوغلة في البناء، [التي] لا يصل إليها الإعراب بوجه، كـ (هؤلاء)، و (هذا)، [وما يجري مجراهما].
وأما الموضع الذي لا يجوز فيه العطف على المحل، فكما مثلناه من قولنا: إن زيدا وعمرا قائم: لا يجوز: [وعمرو] أصلا. والسر فيه أن العامل اللفظي أقوى من المعنوي، والابتداء عامل [معنوي]، [فإذا لم] تتم الجملة [الابتدائية]، و [إنما] حصل أحد [جزءيها]، [امتنع]','2','520',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1217,'العطف على المحل، لعدم تكميل الجملة، [فإن] تكملت الجملة، جاز الوجهان.
وأما الموضع الذي يجوز فيه الأمران، فما عدا هذين المحلين، مما يكون اللفظ يعطى حكما، والمحل خلافه. وهذا ظاهر فيما إذا كان المعطوف عليه معرب اللفظ، ومحله على غير ذلك. وقد سلك به - ما إذا كان مبنيا، ولم يكن متوغلا في البناء - مسلك [المعرب]: يا زيد الطويل، ويا عمرو [الحدث]، فإذًا [العطف] على المحل ههنا حسن بالغ، إن ساعد على ذلك الحكم في الشريعة.
وأما ما ذكره سيبويه من قوله: (الكلام الجزل الفصيح) إلى آخره. فهو كلام حسن بالغ في الحسن، وسنبين وجهه مبسوطا، فنقول: من البلاغة في الكلام التعبير [عن] خواص الأمور والجهات التى يقع بها الافتراق. وقد قدمنا نحو هذا الطريق في التعبير عن الأب [بخاصية]، وكذلك جميع ما','2','521',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1218,'يعبر عنه. ومن حسن الكلام جريانه على وجه واحد في النظم، فإن حسن النظم من مقصود العرب، [ولذلك شرف] الشعر على غيره. والخطب والسجع بعده، والكلام المنثور مؤخر عند القوم.
وإذا كان كذلك، فإن قدر على التعبير [عن] الخواص مع تناسب اللفظ وتناسقه، فهو أحسن شئ وأبلغه. [وإن] كان الإعتناء [بإحدى] الجهتين يمنع الإتيان بالأخرى، فههنا قد يقصد القاصد حسن السياق، ويضرب عن دقائق التفاوت بين الرتب.
[فأما] الرتبة الأولى: فقول القائل: [أكرمت] زيدا [وعمرا]، فقد [تناسق] اللفظ، ووقع التعبير عن اشتراكهما في القضية المقصودة التي هي الإكرام.
ومثال الثاني: تقلدت سيفا ورمحا، فإن الفرق بين النقلين يسير، والمقصود المهم الحمل، وهو المقصود، وإن كان نقل السيف يسمى','2','522',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1219,'[تقليدا]، ونقل الرمح يسمى اعتقالا، وهما [يتقاربان]، فقد تؤثر العرب كمال البيان في المعطوف، فتقول: تقلدت سيفا، واعتقلت رمحا، وقد تؤثر الاختصار، [وتفر من] التطويل، فتقول: تقلدت سيفا ورمحا. (١١٧/ أ)
والأبيات التي [أنشدها] (١٣٣/ أ) دالة على ذلك. [وهو مشهور عندهم، ولو تباعدت المعاني كل التباعد، لا متنع ذلك].
وقول سيبويه: (وهو مسكوت عنه في المعطوف، فسهل احتماله). معناه: أنه لم يقل [معتقلا] سيفا ومتقلدا رمحا، [ولو] قال ذلك، لكان قبيحا، ورب شئ يقدر، ولو نطق به لا متنع.
وهذا من قبيل قولهم: ظننت أن زيدا [منطلق]، قالوا: تقديره: ظننت','2','523',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1220,'انطلاق زيد. ولو صرح بذلك واقتصر، [لبطل] الكلام، لأنه يكون مقتصرا في الذكر على أحد مفعولي (ظننت)، وذلك ممتنع.
[وهذا] معنى قوله: (وهو مسكوت عنه في المعطوف، فسهل احتماله). وأراد بقوله: [واسحنفاره]، تقول العرب: [اسحنفر] الرجل في [خطبته]، إذا مضى فيها [مسرعا] غير متوقف، ولا [اختبل] [عليه] شئ. هذا نهاية ما قرره [سيبويه] بالنظر إلى علم العربية.','2','524',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1221,'ثم تعرض لأمر آخر، ينحو نحو الفقه، فإن الذي ذكره [كأنه] تأويل، وصرف اللفظ عن ظاهره، إذ التقدير على ظاهر العطف لفظا [أو معنى] متأتٍ. وقد قلنا: [إنه] إذا أمكن، فهو أبلغ. فقد أشار- رحمه الله - إلى أن هذا تأويل.
ثم [أخذ] يتكلم على [ما] يعضده من الدليل فقال: (ذكر الله فرض اليدين محدودا مضبوطا، وتبين أن [حكمهما] الغسل، وذكر فرض الرأس مطلقا، وثبت أن حكمه المسح، وذكر فرض الرجلين مضبوطا محدودا، فنقول: حمل المحدود على حكم المحدود أولى من حمله على المطلق).
وهذا ضعيف بالنظر إلى الفقه، [ولا] مانع يمنع من الإطلاق في أحد','2','525',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1222,'[الممسوحين] والتقييد في الآخر. ثم عدل (إلى التمسك بالإجماع قبل ظهور الأهواء على وجوب غسل الرجلين).وهذا- إن صح _ أبلغ دليل يعضد به التأويل.
وقوله: (ولما أراد [النبي - صلى الله عليه وسلم -] أن يبين الوضوء غسل رجليه). [هذا] لعمري كذلك، فإن جميع من نقل وضوء رسول الله - صلى الله عليه وسلم - متفقون على أنه غسل رجليه. ولكن يقال: إنه فعل الواجب وغيره. والذي يحقق ذلك، أنه في كل وضوء [يتوضأ] تمضمض واستنشق، ولم يدل ذلك على','2','526',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1223,'الوجوب. فمن هذه الجهة ضعف التمسك بهذا الطريق. ولكن قد جاء: (أن النبي - صلى الله عليه وسلم - رأى قوما يصلون وعراقبهم لم يصبها الماء، فقال: (ويل للأعقاب من النار). و (رأى رجلا يصلي، وبقي على ظهر قدمه [مثل] الدرهم لم يصبه الماء، فأمره بإعادة الصلاة). ولو كان المسح مجزئا، لما تبين أثر ما لم يصبه الماء. فالصواب إذًا وجوب الغسل باجتماع هذه الأدلة من الكتاب والسنة.
[قال الإمام]: (فإن قيل: بناء (فعالل) و (فعاليل) [مما] لا ينصرف) [إلى قوله] (من غير غرض). (١١٧/ ب) قال الشيخ: بناء (فعالل) و (فعاليل)، مما لا ينصرف في معرفة، ولا نكرة في سعة الكلام.','2','527',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1224,'وقد قرأ جماعة من القراء كما قال الإمام.
وما ذكره عن بعض أصحاب المعاني من (أن الحصال (نون)، وليس [تنوين]، وإنما يقولون: [بدل] من (الألف)). هذا كلام ضعيف، وإنما [تقول] العرب هذا في آخر القافية كقوله:
....... ... .......... ... .......... ... يا أبتا علك أو [عساك]
فأما في أثناء الكلام فلا.
وأما ما ذكره الإمام [من] (أن الأصل صرف كل [اسم]، فإذا','2','528',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1225,'صرف ما لا ينصرف، كان ذلك [ردا] إلى الأصل، وليس فيه خروج عن القانون). [قلنا]: هذا الكلام ضعيف أيضًا، أما كون الأصل يقتضي صرف كل اسم، فهذا لعمري كان ينبغي أن يكون، لأنه أكمل في [البناء]، [وأوقع للاشتمال]، ولكن أضرب فصحاء العرب عن هذا الأصل، [لأسباب] طرأت منعت من الصرف، كما [طرأت] أسباب تمنع من أصل [الإعراب]. ولو أعرب الإنسان المبينات ردًّا إلى [الأصل]، (١٣٣/ ب) كان غالطا بلا خلاف.
فإذًا لا يصلح التمسك بالأصول المرفوضة. والذي اعتمده أهل هذه','2','529',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1226,'الصناعة في العذر عن الكلمات المذكورة، أنها مصروفة على الحقيقة، ولكن يكون القرآن جاء على لغة عربية. فإن من العرب من يصرف كل اسم، ولا يلتفت إلى العلل المانعة إلا [(أفعل)]، إذا كان صفة، فيكون القرآن قد جاء في هذه الكلمات عن هذه اللغة. وقيل: إنما صرفه من صرفه، لأنه [صودف] في المصحف مكتوبًا بالألف. قال أبو عبيد: (رأيت في المصحف الذي كتبه [عثمان]- رضي الله عنه -[(قواريرا)] الأولى بالألف ثابتة، والثانية [حكت ألفها، وأثر الحك] باقـ فلما [رأوها] كتبت بالألف، صرفوها).
[وقد] قيل: إن الجمع في [منع] الصرف ضعيف، ولذلك أن مطلق [الجمع] لا يمنع، حتى قال النحويون: الجمع الذي يمنع هو الجمع','2','530',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1227,'الذي لا نظير له في الآحاد، فرآه [هؤلاء] [جمعا]. ويدلك على ذلك صحة جمعه، قوله - صلى الله عليه وسلم -: (إنكن لأنتن صواحبات يوسف). فجمع صواحب وقدره واحدا، وإن كان على بناء [فواعل]. قال الفرزدق:
وإذا الرجال رأوا [يزيد] رأيتهم .... خضع الرقاب نواكس [الأذقان]
روي (١١٨/ أ) بنصب السين وكسرها. فمن روى بكسرها، جعله جمع (نواكسين)، [بـ (الياء)] و (النون)، فقد جمعه على نواكيس، لما سقطت النون للإضافة، [والياء] لالتقاء الساكنين، فبقي (نواكس)، ودلت الكسرة على المحذوف. فهذا هو الكلام على هذه الألفاظ.

قال الإمام: <span data-type="title" id=toc-208>(مسألة: مما غلط فيه الشافعي القول على [المتأولين])</span>','2','531',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1228,'إلى قوله (فهذا منتهى المراد في [هذه المسألة]). قال الشيخ: ما ذكره الإمام من أن تأويل أبي حنيفة [باطل] قطعا، من الجهة التي قدرها، من أن الإضافة باللام للاستحقاق، [فالعطف] بـ (الواو) يقتضي التشريك. أما كون (الواو) للتشريك فمقطوع به، إذا كانت للعطف، وإن كان قد [تأتي] غير ذلك على ما مر في معاني الحروف. و (الواو) [ههنا] للعطف، فلابد من التشريك. وكون اللام للاستحقاق، يقبل التأويل؛ [فإن] القائل يقول:','2','532',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1229,'سرج الدابة، وإن لم يتحتم أن يكون لها، [لكونها] تأهلت لذلك. [فهل] المراد بقوله: (الصدقات للفقراء)، أي ملكا، [أو يصح] الصرف إليهم وهم أهل ذلك؟ فإن كان المراد الملك، لزم ما قاله الشافعي. وإن كان المراد التأهل وصحة الصرف، وجب الاشتراك في صحة الصرف والتأهل، وهذا [هو] الذي نختاره نحن. فخرج الكلام بهذا التقدير عن مراتب النصوص.
[فأما] أن نقول: إنه مشترك بين الجهتين، فيفتقر إلى البيان في الجانبين، فيكون كل واحد [مفتقرا] إلى الدليل. وقد سلم ظهور ما قالوا، فتخرج المسألة عن تعطيل النصوص، وتقع في أقسام التأويلات [المقبولة] التي يفتقر من صار إليها إلى دليل يعضد به التأويل. وهذا عندنا راجع إلى ما','2','533',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1230,'سبق من قوله: {ومنهم من [يلمزك] فى الصدقات} إلى قوله} راغبون}. ذمهم على تعرضهم لها، مع [خلوهم] عن شرط استحقاقها، ثم بين الله تعالى من يستحق الصرف، ومن هو لها أهل فقال: {إنما الصدقات للفقراء والمساكين}. الآية.
[وإذا] تقرر هذا، فإذا قيل لنا: إذا كان هذا احتمالا [مساويا] على وجه، أو بعيدا على وجه، (١٣٤/ أ) فما الذي دلكم على تعيينه؟ ولم لم تقضوا بوجوب التعميم؟ [إذ التوقف] على ما يقتضيه كل واحد من الطريقين؟ [قلنا]: دلنا على ذلك أمر كلي، وهو علمنا بأن الشارع قصد بأخذ هذه الأموال من هذه الجهات، سد خلة ذوي الحاجات، والنظر في مصالح البشر، وهذا أمر معلوم من قواعد الشريعة بلا ريب.','2','534',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1231,'وقوله - عليه السلام -: ([أمرت أن] [آخذها] من أغنيائكم وأردها في فقرائكم). يشير إلى هذا المعنى. وإذا كان كذلك، [فلو تحتمت] القسمة بين الأصناف على التساوي، أفضى الأمر إلى تعبد في الصرف؛ فإنه قد يتفق [أن يكثر الفقراء ويقل] الماء، ولا يوجد الغارمون، ولا الرقاب التي تشترى للعتق، فلو صرف إلى الفقراء من ذلك المال تقديرا، وأخر الثمن للغارمين إلى وقت طريان الغارمين، والفقراء هالكون بالجوع، فلا يليق بحكمة الشريعة أن تفعل هذا، فإنهم (١١٨/ ب) معرضون للموت، أو [للحمل] على [الأغنياء]، وفي ذلك إضرار بذوي الأموال [لا محالة]. فالذي تقتضيه حكمة الشريعة النظر في مصالح الخليقة [على] الجهة المقدرة.
وإذا ثبت ذلك، خرج الأمر عن [تعطيل] النصوص، ووقع في','2','535',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1232,'[قسم] التأويل المقبول، أو التفسير المرضي.
وأما مسألة الوصية، فلا سبيل إلى تخصيص بعضهم بالإعطاء، [لظهور] مقصد التمليك، وثبوت حقيقة التشريك. وقول الشافعي: (إذا تعين اعتبار قول [الموصي]، [واعتقاده] نصا، فقول الشارع بذلك أولى).
[لعمري] إن النص بالوضع لا يختلف بالإضافة. أما [النص بالقرائن]، فيختلف باختلافها. وقد بينا أن [حرف] (اللام) لا يقتضي تمليكا قطعا، وإنما هو محتمل لذلك. فإذا دل دليل في اللفظ الدائر بين معنيين على','2','536',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1233,'انحسام أحدهما، تعين الثاني.
وقد دل الدليل على مسألة الموصي، لانحسام معنى الاستحقاق، وتحقق قصد التمليك، وفقدان القرينة. وإنما صور في مسألة الصدقات، أن يطلب [من الإنسان] أن يصرف شيئا من ماله لأصناف [لا ترتضي] أحوالهم، [فيقول]: ليس مالي لهذه الأصناف، وإنما مصرف مالي لأهل الفضل والدين [والعلم، فهذا لا يقتضي تشريكا ولا تمليكا. هذا هو الحق، والقول الصدق، والله] الموفق.

قال الإمام: <span data-type="title" id=toc-209>([مسألة]: مما عده الشافعي من القبيل [المتقدم] الكلام على قوله تعالى: {[فأن لله خمسه وللرسول ولذى القربى]}) </span>إلى','2','537',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1234,'قوله (فلا يبقى على هذا لمذهبهم وجه). قال الشيخ: الكلام على هذه الآية كالكلام على الآية السابقة، [وأن] الأمر راجع إلى انحصار الصرف في هذه الجهات المذكورة. ولكن يبقى في هذه المسألة زيادة، وهى [كون] أصحاب أبي حنيفة اعتبروا الحاجة مع القرابة، [ومطلق] الآية لا يقتضي ذلك، مع مصيرهم إلى أن اشتراط الإيمان [في الرقبة] زيادة تقتضي نسخا. وهذا الإلزام قد تقدم أيضًا الكلام عليه، [وأن] المعنى إذا كان [مساوقا] للفظ عند النطق، فهو المحكم في التعميم والتخصيص،','2','538',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1235,'والإطلاق والتقييد، كما ذكرناه في [مسألة]: (لا يقضي القاضي وهو غضبان). فإن ذلك المعنى مما أجمع على أنه محكم في العموم والخصوص [والإلحاق]. وقد بينا السنة في ذلك. وأن المعنى إذا لم يتراخ عن سماع اللفظ، فلم يستقر في النفس استغراق العموم؟ والنسخ هو الرفع.
فإذا ثبت أن اللفظ لا يدل على العموم عند سماعه لأجل اقتران المعنى به، لم يكن (١٣٤/ ب) [رفع]. وإذا كان [هذا محله ما سبق إلى الفهم، لم يكن تنزيل اللفظ عليه يقتضي نسخا بحال. نعم، إذا] سدت خلة ذوي الحاجات، وبقيت بقية من المال، نظر فيه الإمام بالمصالح، إن رأى أن يقسمه على الأغنياء بقدر اجتهاده فعل، وإن رأى أن يبقيه [لنائبه] [تنزل] فعل.
ولقد سلك الشافعي هذا المسلك بعينه في قوله تعالى: {واليتامى}، فإنه ضم في قولٍ الحاجة إلى اليتم. وإن كان لفظ القرآن لا يشعر بذلك،','2','539',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1236,'[فلئن] قال: لفظ اليتم [ينبئ] [عن] الحاجة، فهو ملتقى من القرآن. قلنا: ليس الأمر كذلك، فإن اليتيم عبارة [عمن] [لا أب] له، غنيا كان أو فقيرا، وقد قال الله تعالى في أمر اليتامى: {وابتلوا اليتامى حتى إذا بلغوا النكاح [فإن] ءانستم منهم رشدًا (١١٩/ أ) فادفعوا إليهم أموالهم}. فأطلق عليهم اسم اليتم، وإن كانت لهم أموال. ولما قال رسول الله - صلى الله عليه وسلم -: ([واليتيمة تستأذن]). لم ينزله الشافعي على [الفقيرة]، بل [أبقاه] على ما يقتضيه وضع اللسان، من كونها التي لا أب لها.','2','540',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1237,'وأما قول الإمام: (لو حتموا صرف شئ إلى القرابة، [واشترطوا] الحاجة لقرب [ما ذكروه] بعض القرب). يشير إلى أنه كان يقع في أبواب التخصيص، فأما [المنع] على الإطلاق، فواقع في أبواب التعطيل. وقد كنا أجبنا عن هذا، وبينا أن الآية إنما سيقت لبيان صحة الصرف في هذه الأقسام، وليس الصرف إلى جهة محتما على حال.

قال الإمام: <span data-type="title" id=toc-210>(مسألة: من فاسد تصرفات أصحاب أبي حنيفة) </span>إلى قوله','2','541',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1238,'(واكتفينا بإيرادها في مواضعها). قال الشيخ: ما ذكره الإمام في هذه المسألة من تقسيم الفعل المتعدي إلى مفعولين: أحدهما- ما ينتظم منهما مبتدأ وخبر. والآخر- ما لا ينتظم منهما مبتدأ وخبر. كلام صحيح، والمصير إلى أنه متى ذكر أحد المفعولين الذي [يلتئم] منهما مبتدأ وخبر، [تعين] ذكر الآخر، صحيح أيضًا، إلا أنه يفتقر إلى [تتمة] تتعلق بعلم العربية وهي مسألتان: إحداهما- أنه يصح أن يقول القائل: ظننته زيد قائم، فقد يخيل للناظر في هذه المسألة أنه ذكر أحد المفعولين دون الثاني، وليس الأمر كذلك، وإنما (الهاء) ههنا راجعة إلى المصدر، والتقدير: ظننت الظن زيد قائم، فلم يذكر في هذه (١١٩/ ب) المسألة [واحد] من المفعولين.','2','542',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1239,'والصورة الثانية: أن يقول القائل: ظننت أن زيدا قائم، وقد اختلف النحويون في هذه المسألة، مع اتفاقهم على أنه لم يذكر إلا أحد المفعولين، فإن التقدير: ظننت انطلاق زيد، والمعنى كذلك. فقال قائلون: - وهم الأكثرون - إن طول الكلام، قد سد مسد المفعول الثاني، والمفهوم منه بين واضح.
وقال قائلون: المفعول الثاني محذوف، والتقدير: ظننت انطلاق زيد واقعا أو كائنا. وهذا ضعيف، وفيه حذف الخبر من غير دليل [يدل عليه]، وليس المراد أيضًا الإخبار عن الوقوع والكينونة.
وقال قائلون: تكتفي ظننت في [هذا المكان] بمفعول واحد، وهذا غلط بين، ولا يتصور أن يفيد المبتدأ على انفراده بوجه. هذا على قوله: (فإذا ذكر الذاكر أحد المفعولين، [تعين] ذكر الثاني).
[فأما] قوله في القسم الآخر: (إنه لا [يتحتم] على الناطق الاعتناء','2','543',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1240,'بالمفعولين، ولا بأحدهما]. فكلام صحيح، لأن الاعتماد [هنا] علي المفعول، ويصح حذف المفعول. (١٣٥/ أ) وإنما [لزم] الذكر في القسم الأول، لافتقار المبتدأ إلى الخبر [والخبر] إليه. فلذلك وجب إذا ذكر أحدهما، أن يذكر الثاني. فتقول: أعطيت زيدا درهما، وأعطيت درهما، وأعطيت زيدا، وأعطيت، مقتصرا.
ولما كان الاعتماد في هذا الباب على الفعل، [فإنه متى] فرض حذفه، بطل الكلام، فهو إذًا [العمدة]، فلا يسوغ تعطيل عمله، وأن [يقدم] [لشدة] الاعتناء به، وإسقاط عمله، إعراض [عنه] مع استحالة الاعتماد على غيره. وليس كذلك [ظننت]. فإن [الكلام] جهتين: فعلية واسمية. فالاسمية باعتبار [المبتدأ] والخبر. والفعلية باعتبار (ظننت)،','2','544',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1241,'والعامل اللفظي أقوى من العامل [المعنوي].
فإذا تقدم الفعل، فقد يقوى من جهة كونه عاملا لفظيا، ومن جهة تقدمه والاعتناء به-أعني (ظننت) -فلا يجوز تعطيل عمله بوجه، إلا في مسألة واحدة، وهي إن [تعلق]، ومعنى كونه تعلق: أنه يؤتى بحرف يمنعه العمل بتقوية جانب [المبتدأ] فيه.
وحروف التعلق ثلاثة: (لام) الابتداء، [كقولك]: ظننت لزيد قائم.
وحرف الاستفهام، كقولك: ظننت [أزيد] قائم؟ وحرف النفي، كقولك: ظننت ما زيد قائم. وإنما علقت الفعل، لاستدعاء صدر الكلام وتحقيق جانب الابتداء فيها، ولا يكون التعليق لغير هذه الحروف في غير هذه الأفعال.','2','545',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1242,'وأما قوله: (إذا تأخر الفعل في هذه الحروف، بطل عمله، فتقول: زيد عالم ظننت، لا يتجه غيره). فليس على ما قال، بل قول النحويين إنه يصح الإعمال والإلغاء جميعا، سواء توسط أو تأخر. وسببه أنه [إذا] توسط أو تأخر، صار للجملة جهتان:
إحداهما-الابتداء، فإن [خبر] المبتدأ موجود في قولنا: زيد ظننت عالم، فإنا [قد] وضعناه في أول الكلام، لنحدث عنه.
وفيه جهة الفعل، فإنه قوي، يصح أن يعمل في المفعول، تقدم عليه أو تأخر عنه، ونظير هذا عند النحويين: زيد ضربته، فإن شئت قلت: زيد ضربته، فجعلت الاعتماد (١٢٠/ أ) على الابتداء، وإن شئت قلت: زيدا ضربته، فتجعل الاعتماد على الفعل، وتجعل الفعل المذكور [مفسرا] [له]. وأحسن','2','546',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1243,'التفسير أن يكون الفعل المذكور [يفسره] مثله، [وهو] [كقولك]: [زيدا] ضربته. وإن افتقر إلى تأويل، فهو أضعف.
وصورته أن تقول: [زيدا] ضربت أخاه، هذا أضعف من الأول، لأنه يفتقر [ههنا] إلى أن يقدر: أهنت زيدا ضربت أخاه.
الثالث: أن يكون تفسيره من جهة معناه على خلاف لفظه، كقولك: زيدًا مررت به، [و] التقدير: جاوزت زيدا مررت به]. فهذه ثلاث رتب [متفاوتات]. وتمام الكلام عليه [يتعلق] بفن العربية. هذا تمام الكلام على ما يتعلق بالعربية.','2','547',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1244,'وأما ما يتعلق بالأصول، وهو المقصود، فقد بنى الإمام الأمر على أنه (وقع الاعتناء بذكر المساكين، ووقع الاكتفاء بذكرهم عن ذكر مقدار الطعام، اكتفاء بأن [طعام] المسكين يشعر بقدر سداده). وهذا كلام صحيح، ولكن، هل ذكر عدد المساكين لنفس القصد إلى عددهم، من جهة كونه عددا؟ أو ذكر [العدد] مرشدا إلى تقدير الطعام؟ هذا محل الكلام. فإذًا هو يقول لم يذكر عدد المساكين إلا [لغرض]، وهذا لاشك فيه، فإن كل شيء يصح [ألا] يذكر، فإذا ذكر، أشعر ذكره بشدة اعتنائه، لغرض يترتب للذاكر على ذكره.
أما [انحصار] الغرض في تحتيم الصرف إلى العدد، فلا يلزم أن يكون','2','548',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1245,'ذلك مقطوعا به، بل يجوز أن يكون [للغرض] الذي ذكرناه. وهذا لو فسر رسول الله - صلى الله عليه وسلم - (١٣٥/ ب) الآية بهذا، [لم] [تنفر] الأسماع من [قبوله]، ولم [يعد] ذلك [خارقا] للنص، ولا مزيلا للفحوى المقطوع بها، وإنما [تشمئز] منه قلوب من لم يأنس [بتجوز] العرب [ومعرفة] [تخاطبها]، أو من غلب من غلب عليه التعصب للمذاهب، والركون إلى التقليد.
فإذًا لسنا نمنع أبا حنيفة من المصير إلى ما صار إليه، لاعتقادنا كون','2','549',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1246,'اللفظ نصا، وإنما نمنعه من ذلك، لعدم الدليل الذي يعضد التأويل به، فإنه إنما جدد نظره إلى سد الخلة، وقال: قصد الشارع إلى إطعام ستين مسكينا في يوم، [كقصده] إلى سد خلة مسكين في ستين يوما، إذ هو في كل [يوم] محتاج إلى ما يصلحه.
والشافعي ومالك يقولان: سد الخلة مقصود، [ولكن] لا [يصح] أنه كل المقصود، [إذا] يمكن أن يكون الشارع قصد إلى العدد، من حيث أنه لا يبعد أن يشتمل العدد الكثير على رجل صالح تستجاب دعوته، ويتبرك بسد خلته. وإذا أمكن ذلك، ففي مراعاة العدد تحصيل [المقصود] مع الخلاص من [عين] المخالفة.','2','550',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1247,'[وفي] الكفارات نوع من التعبد، بالنظر إلى العدد، فيكون التمسك بالظاهر المحصل للمقصود من كل وجه أولى. وهذا هو [الأغلب] على الظن عندنا، وإن كنا لا نقطع ببطلان مذهب أبي حنيفة.
ومن هذا القبيل أيضًا قوله - عليه السلام -: [في أربعين شاة شاة]. ذهب أبو حنيفة إلى أنه يجوز إخراج (١٢٠/ ب) الشاة، [أو قيمتها] من أي مال كان.
وقال أصحابنا: لا يجوز هذا بحال، فإنه تعطيل للنص، فإن النص يتضمن وجوب الشاة على التعيين، فمن جوز غير هذا، كان معطلا للنص. وهذا عندنا من جنس ما تقدم، [فيمكن] أن يكون تعيينها مقصودا، كما ذهب إليه مالك وغيره. ويمكن أن يكون المقصود بتعيينها أن يكون لتعريف المالية.
ويذكر: أنها الأسهل على الملاك، والأقرب في [العادات]، وعليه','2','551',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1248,'نزل قوله - عليه السلام -: [فليستنج] بثلاثة أحجار]. إنما [ذكر] ذلك، لأنه [الأسهل] في تلك البلاد، فتذكر [لتعريف] عدد ما يستنجى به، لا للقصر على العين.
ومالك رحمه الله وغيره يقولون: يمكن أن يكون الأمر كذلك، ويمكن أن يكون الشرع قصد مشاركة الفقير الغني في جنس ما يملك، ففي القصر على الشاة، [تحصيل] مقصود الشرع، مع الخلاص عن [ضرر] المخالفة.
وباب الزكاة [باب] تعبد، فالإعراض عن التأويل، لقصور الدليل العاضد للاحتمال، [لا] لكون اللفظ نصا.
قال الإمام: [مسألة: إذا [ورد عن] رسول الله - صلى الله عليه وسلم - لفظ يدل على','2','552',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1249,'تعليل حكم) إلى قوله (وسيأتي قول بالغ في التعارض، في كتاب (الأخبار). قال الشيخ: [هذه المسألة] [تقدمت] [لها] نظائر كثيرة في هذا الكتاب، وقد تبين اختيارنا فيها، وأن ظهور التعليل [بالإيماء] القابل للتأويل، [يتنزل] منزلة اللفظ في [التعميم].
وقد صح أنه من تخصيص العموم، وتقييد [المطلق بالقياس، إذا كان','2','553',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1250,'راجحا عليه، منضما إلى التأويل. وغاية ظهور التعليل] أن يحصل ظنا بذلك مع احتمال غيره، فإذا تأول المتأول ظاهر التعليل، وصرفه عن ظهوره، وذكر محملا متأتيا وعضده بدليله، وترجح التأويل بدليله على اللفظ في ظهوره، بحيث (١٣٦/ أ) أنه [لم] تبق جهة الظهور مظنونة، فكيف يصح التمسك بظاهر يظن خلافه؟ [هذا] باطل، لا خفاء ببطلانه.
وقوله: [هذا تقديم [للقياس] على الخبر]. فقد تكلمنا عليه، وبينا اختلاله. ولعمري إن [التمسك] بالظاهر عندما يظن أن مطلقه لم يرد جهة ظهوره، مخالف للخبر على الحقيقة. وهذا قد أشبعنا القول فيه، فلا معنى لإعادته.','2','554',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1251,'قال الإمام: <span data-type="title" id=toc-212>(مسألة: إذا وردت مناه [عن] رسول الله - صلى الله عليه وسلم - في عقود) </span>إلى قوله (تفصيلا وتأصيلا). قال الشيخ: ما ذكره الشافعي في هذه المسألة من منع قبول التأويل الذي يقبله اللفظ، لو كان مفردا، من جهة أن أمثاله منع أهل الإجماع من تأويلها.','2','555',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1252,'وقوله: ([نحن نعلم] [بمسلك النقل عنهم]، أن هذا لو نقل [إليهم]، لجروا فيه على [ما] ألفوه في أمثال]. كلام ضعيف، وذلك أن اللفظ إذا كان في نفسه قابلا للاحتمال الذي أبداه المؤول، فإنه يمتنع من قبوله اقتران قرائن تمنع منه، [وتصير] اللفظ نصا، غير قابل لذلك الاحتمال. [وإذا] كان كذلك، (١٢١/ أ) فليس [اقتران] المناهي بمنهي معين بالذي يصيره نصا في التحريم، وإن ثبت أن [المقترن] به محمول على التحريم والفساد. والعرب قد تجمع بين المختلفات في العطف فتقول: أقم الصلاة وآت الزكاة، وصل الوتر [وصل القريب]، وتصدق على الضعيف، [وصد] الصيد، [ولا] يوجب ذلك استواء أحكامها.','2','556',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1253,'وقول الإمام في هذا المكان: (وهو لا يبلغ عندي في السقوط مبلغ [ما تقدم]). يشير إلى أنه ساقط سقوطا دون ذلك. والصحيح عندي أنه لا سقوط فيه بحال، وإنما يرجع في ذلك إلى طلب الدليل العاضد للتأويل، والاقتران','2','557',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1254,'وعدمه في ذلك [سواء]. والله تعالى هو [الموفق] وبه الحول والقوة.
قال الإمام: (ونحن الآن نجدد العهد بترتيب [يشتمل على ما مضى من] [أبواب] الكتاب] إلى قوله [ونبتدئ] الآن [كتاب] الأخبار). قال الشيخ: ما ذكره الإمام في هذا المكان، إنما هو [عد] جملة أبواب الكتاب من حيث الجملة. [وأما] ما يتعلق بالترتيب، وسبب','2','558',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1255,'التقديم والتأخير، فلم يتعرض له على حال. والكتاب أيضاً غير [مشتمل] على الترتيب على التحقيق، وإنما الترتيب الحسن في هذا، ما ذكره أبو حامد، ولا مزيد عليه في الحسن. ونحن نرى أن نذكره ههنا على غره، إذ هو مستقيم، [لا اختلال] فيه، ولا اعتراض عليه.
وقد كنا قدمنا أن أصول الفقه تطلق لقبا على فن مجموع، وتطلق على الأدلة، ونحن [ههنا] نتكلم عليها باعتبار كونها أدلة الأحكام. [وإذا] كان كذلك، فالحاصل معنا ههنا أربعة أمور: دليل، ومدلول عليه، وجهة منها يدل الدليل، ومستدل، فاشتمل هذا العلم الذي هو أصول الفقه لقبا على أربعة أجزاء: دليل، ومدلول، ومستدل، وطريق يعرف كيفية الاستدلال.','2','559',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1256,'فالمقصود [كيفية] اقتباس الأحكام من الأدلة، فافتقر إلى معرفة الدليل والمدلول، وطريق الاستدلال، وصفات المستدل، فإن الأحكام ثمرات، وكل ثمرة فلها حقيقة في نفسها وانقسام، ولها تعلق بالمثمر [والمستثمر]، [وكيفية] الاستدلال، فافتقر إلى معرفة الثمرة في نفسها، وانقسامها وأركانها، [ثم] في الأدلة وانقسامها وأركانها، [ثم] في كيفية اقتباس الأحكام من أدلتها، [ثم] في صفات المقتبس الذي له أن يقتبس الأحكام. وإذا نظر في الثمرة عرفت حقيقة الحكم، وأنه يرجع إلى الخطاب، (١٣٦/ ب) وأنه يتعلق بالأفعال لا بالأعيان، وأنه ليس بصفة للأفعال على حال، ويندرج تحت ذلك إبطال التحسين والتقبيح، وامتناع وجوب شكر المنعم بالعقل، وأنه لا حكم على العقلاء قبل ورود الشرع.
وفي النظر في أقسام الحكم يتبين الوجوب والحظر والإباحة والكراهة والندب، وحقيقة الواجب الموسع والمخبر، وأن التوسعة لا تناقض الوجوب،','2','560',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1257,'وكذلك التخيير، وأن ما لا يتوصل إلى الواجب إلا به، هل هو واجب؟
(١٢١/ ب) وما حقيقة فرض العين [وفرض] الكفاية؟ إلى غير ذلك من المسائل التي [سبق] النظر فيها، [و] من تضاد الأحكام وامتناع اجتماعها، وفيه [يتبين] تعدد الجهات، والالتفات إلى مقاصد الآمرين والناهين.
وفي النظر في أركان الحكم يتبين أن لا حاكم على الحقيقة إلا الله [- عز وجل -]، وأنه لا حكم للسيد على العبد، ولا للنبي على الأمة، ولا للزوج على المرأة، على حسب ما قررناه.
وفي النظر في المحكوم عليه يتبين حكم خطاب الناسي والغافل، والسكران والمكره، ومخاطبة الكفار بفروع الشريعة. وفي النظر فيما يكلف به','2','561',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1258,'يتبين متعلق التكليف، [وهو] الأفعال دون [الأعيان]، وجواز تكليف ما لا يطاق واستحالته. والمقتضى [بالتكليف] [أهو] الإقدام والكف، [أو] الإقدام خاصة؟ وإذا نظر في متعلق الحكم، تبين حكم السبب، وصحة نصب الأعلام، [وجواز تعليلها، والعزيمة والرخصة، والقضاء والأداء، والصحة] والفساد والبطلان. هذا نهاية ما اشتمل عليه الركن الواحد، وهو ركن الحكم. وكل هذا النظر نظر عقلي يتعلق بتصور الأمور، والنظر في حقائق معقولة.
[وأما] الركن الثاني، وهو ركن الأدلة، فإذا استوعبنا النظر فيه، [تبينا] حقيقة الكتاب وحده، وما يصح أن يكون كتابا، وما لا يصح، والنظر في القراءة الشاذة، والمحكم والمحمل والمتشابه واشتمال القرآن على [عجمية] أم لا.','2','562',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1259,'[وإذا] نظر في السنة تبين [حد] الخبر وحقيقته، وانقسامه إلى صدق وكذب، [ومتواتر] وآحاد، ومسند ومرسل، وصفة [الراوي] من عدالة وترجيح، وستر وسلامة، وبيان طرق التعديل، وما يتعلق بذلك من الكلام على الأخبار.
[وإذا] نظر في الإجماع، وهو الدليل [الثالث]، تبين فيه حقيقة الإجماع وتصوره، وإمكان الإطلاع عليه، وكونه [حجة]، [وإجماع] الصحابة ومن بعدهم، [وصحة] [تصوره مستند القياس، وظن حكم إجماع الصحابة ومن بعدهم، إلى استتمام مسائل الإجماع].','2','563',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1260,'والنسخ لا يرد إلا على الكتاب والسنة، [ولا] ورود له على الإجماع بحال. فنذكر حقيقة النسخ وتصوره، وأنه يرجع إلى الرفع [أو البيان]، وجواز نسخ القرآن بالسنة، والسنة بالقرآن، [إلى] استتمام مسائل النسخ.
وأما ما يتعلق بشرع من قبلنا، فهو يندرج عندنا تحت أقسام الأدلة، مستندا إلى الكتاب، على حسب ما بيناه. وأما الإمام فلا يعده أصلا بحال. [هذه تفاصيل] الأدلة والأحكام.
وأما معرفة جهات الاستدلال، فإنه ينقسم إلى المنظوم والمفهوم، والمعنى المعقول، وهو القياس. أما المنظوم، فإنه يرجع أربعة أنواع: إلى الظاهر (١٣٧/ أ) والمؤول، والمجمل والمبين، والأمر والنهي، والعموم والخصوص. وكل هذه الجهات لغوية محضة، ليس للعقل فيها مجال؛ [فإن] أدلة الأحكام [وضعية، والوضعي] موقوف على الاختيار، [فهي] دلالة لغوية.
وأما دلالة [المفهوم] فتشمل على جملة من الفحوى والمفهوم، وإشارة اللفظ، والضرورة والاقتضاء، وقد مرت مستقصاة. ويتصل بذلك','2','564',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1261,'القياس، وفيه يتبين حقيقته، [وشروط] دلالته، وكيفية تلقي الأحكام منه.
(١٢٢/ أ)
والركن الرابع: المجتهد، [وفي] مقابلته المقلد، وفيه يتبين صفات المجتهدين، والموضع الذي يجري فيه الاجتهاد، والموضع الذي لا يجري، وما على المجتهد، والقول في تصويب المجتهدين، وجملة أحكام الاجتهاد.
هذا جملة ما ذكر في هذا الفن. فهذا هو الترتيب على مقتضى التحقيق، والله المستعان، [وهو ولي التوفيق].
قال الإمام [رحمه الله]: [كتاب] الأخبار-الخبر: [قسم من أقسام] الكلام [القائم بالنفس] عند [معتقدي] كلام النفس] إلى','2','565',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1262,'قوله [والقول في ذلك قريب]. قال الشيخ: ما ذكره الأصوليون في حد الخبر بأنه: ما يدخله الصدق والكذب، أو الصدق [أو الكذب] على مقتضى العبارتين السابقتين، عندي لا يصح، وبيانه: هو أن الصدق خبر خاص، والكذب كذلك، [فالصدق] والكذب نوعا الخبر، ومن التبس عليه حقيقة الجنس، كيف يعرف نوعيه؟ وكيف يتصور أن يكون النوع [مبينا] للجنس؟ ولا يتصور فهم النوع إلا بعد معرفة الجنس. وهذا بمثابة من يسأل عن حد الحيوان، فيقال له: هو المنقسم إلى الإنسان والفرس، فإن هذا لا يصلح للبيان أصلا.
والمقصود فهم الخبر، لا باعتبار كونه صدقا ولا كذبا، [فإن] الصدق والكذب اشتركا في حقيقة الخبرية، كما أن الإنسان والفرس اشتركا في قضية','2','566',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1263,'الحيوانية، فإن من فهم الحيوان، فهمه لا باعتبار كونه إنسانا أو فرسا. كذلك من فهم الخبر فهم حقيقته، لا باعتبار كونه صدقا وكذبا. [ولذلك] [ينتفي] الصدق، ولا [ينتفي] الخبر، [وينتفي] الكذب أيضًا، ولا ينتفي الخبر فثبت بما قررناه أن الخبر جنس، والصدق والكذب أنواع.
وقد اعترض بعض الناس على [الحد] بوجه آخر، أعني على قولهم: ما يدخله الصدق والكذب، زائد على [إيهام] [اجتماعهما] في خبر','2','567',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1264,'واحد. [والذي] اعترض به صاحب الكتاب، وقال: [مقتضى هذا أن يكون كل خبر يدخله الصدق والكذب]. ورب خبر لا يقبل الكذب بحال، كخبر الله سبحانه، ورب خبر لا يصح أن يكون صدقا، كالخبر عن حصول المستحيلات.
وهذا الاعتراض ساقط، ومراد [الحاد] أن [يبين] محض الخبر، من جهة كونه خبرا، لا يتعين له أحد الجانبين، وإنما يتعين الصدق [أو الكذب] فيه من أمر زائد على الإخبار. ولكن الاعتراض هو الذي قدمناه.
فإن قيل: فما الصحيح عندكم في حد الخبر؟ فنقول: الخبر [ما] يقوم بالنفس على وفق [العلم]. فهما متلازمان على حسب ما قدمناه.','2','568',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1265,'[وإذا] كان كذلك، فضرورة من قام بنفسه الخبر أن يكون [عالما به، ولا يتصور خلو العقل عن (١٣٧/ ب) العلوم الضرورية، باستحالة المستحيلات، وامتناع] اجتماع المتضادات. فإذا علم ذلك، [فلابد أن] يقوم بنفسه خبر عنه، معلوم له، [ولا] يتصور أن يطلب حد الخبر ليعلمه [عاقل]، لحصول العلم به له. نعم، قد يطلب العبارات (١٢٢/ ب) المحررة، وأما طلب الحد المرشد إلى تصور الحقيقة فمحال، على ما قررناه في العلم.
والعبارة عن الخبر على هذا الغرض أن يقول: هو إسناد معلوم إلى معلوم على جهة سلب أو إثبات. [فقولنا: إسناد معلوم إلى معلوم، قضية معقولة من غير نظر في صدق أو كذب، ولكن قد يأتي الإسناد فيما ليس بخبر، كقولنا: أقام زيد؟ وقم، ولا تفعل، فإنا قد أسندنا الفعل إلى ضمير الفاعل في الأمر والنهي، فافتقرنا إلى أن نقول: على جهة سلب أو إثبات].
وقد عبر سيبويه عن ذلك بأن قال في المبتدأ والخبر: [باب المسند والمسند إليه]. ولم [يزد] الزيادة التي ذكرناها، ولكنه أشار إلى الأصل،','2','569',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1266,'وهو الإسناد. وإنما لم يذكر الزيادة، [لأن أئمة] العربية [يقولون: ] إذا قال القائل: زيد هل [قام]؟ وعبد الله ما فعل؟ هذا عندهم مبتدأ والجملة خبر، وإن لم يكن هذا الكلام مشتملا على نفي وإثبات، ولا يحتمل أيضًا صدقا ولا كذبا. وقضاؤهم عليه بأنه خبر، عجيب، حتى قال بعضهم: معنى قولنا: زيد هل قام؟ [أي] استفهمت عنه، وما يضاهي ذلك. فقد قبل الكلام الصدق والكذب على هذا الوجه. وهذا عندنا غير صحيح، وقد تقدم قريب من هذا في بيان أقسام الكلام على رأي الأصوليين. والقائل: زيد هل قام؟ لم يثبت شيئا، ولم ينفه قطعا، وإنما هو مستفهم محض.
قال الإمام: [فإن قيل: [فلم] سمى الأصوليون [جميع] ما نقله الرواة عن رسول الله - صلى الله عليه وسلم -[أخبارا؟ ] إلى قوله [فلتقع البداية [بالمتواتر]. قال الشيخ: قد تقدم الكلام أن أصول الأحكام: الكتاب','2','570',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1267,'والسنة والإجماع. والنظر في هذه [الأصول]، تارة يتعلق بطريق إثباتها وتحقيق مناطها، وتارة يتعلق ببيان الطريق الذي منه تدل. أما بيان كونها أدلة، فقد تقدم بيانه في مقدمة الكتاب عند بيان مراتب الأدلة. [ولكن حقق هناك] أمر الكتاب والسنة، وأما الإجماع فأرجأ [أمر] كونه دليلًا إلى موضعه، لاتساع الكلام فيه، وتوجه الاعتراضات عليه. ولا خفاء بكون الكتاب دليلا، وكذلك السنة المتواترة. وأما ما يتعلق بفهم جهة [الدلالة]، فهو الذي قدمناه في أول كتاب [الأوامر] إلى هذا الحد. وإنما عقد هذا الكتاب لبيان أمرين:
أحدهما-[بيان] حقيقة التواتر وشرطه، وهو راجع إلى تحقيق مناط','2','571',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1268,'الخبر، هل وجد أم لا؟ ولعمري إن هذا أيضًا يتطرق إلى الكتاب، ولكن لما كان أمر الكتاب مكشوفًا جليًا، لحصول العلم الضروري به، باعتبار الأطراف والوسائط، استغنى الأصوليون بذلك عن بسط القول فيه، [وإلا] فما يفتقر إليه الخبر المتواتر من الشروط، يفتقر إليه الكتاب.
وقد [انجر] الكلام في كتاب الأخبار إلى [الكلام] عند ذكر الأصوليين القراءة الشاذة. وعندما يحصل النقل المتواتر في الخبر، استند الخبر إلى الله تعالى، من جهة دلالة المعجزة على [الصدق]، فيعلم ثبوت حكم الله تعالى [على] عباده، فلا يطلب أمر آخر وراءه. أما إذا فقد النقل المتواتر، فقد [فقد] (١٣٨/ أ) الدليل القاطع، ويبقى النظر مصروفًا إلى','2','572',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1269,'الأمارات، والنظر فيها يتعلق بأمرين:
أحدهما- تحقيق المناط، حتى (١٢٣/ أ) يثبت عندنا ظن [وجود] الخبر.
والثاني-إقامة الدليل بعد تحقيق المناط على كونه أمارة. [لا جرم] افتقرنا في هذا المكان إلى إقامة البرهان على وجوب العمل عند أخبار الآحاد، ولم نفتقر إلى ذلك في المتواتر. [وإنما] تكلم الأصوليون في المتواتر على أحد الطرفين: وهو تحقيق المناط، وإثبات الشروط [التي] [إذا] اجتمعت كان الخبر متواترًا. أما بعد ثبوت كونه متواترًا، فلام يفتقر [الأصولي] إلى إقامة دليل العمل بحال، بعد ثبوت الرسالة وتحقيق الإيمان.','2','573',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1270,'قال الإمام: [القول في الخبر المتواتر-الذي [يقتضيه] الترتيب [أن] [نصدر] الخبر [المتواتر] بذكر شرائطه] إلى قوله [مقتضيات] [العلوم] الضرورية، فهذا شرط]. قال الشيخ: ذهب','2','574',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1271,'الإمام إلى تعليل منع حصول العلم بخبر [التواتر]، [إذا] أسندوه إلى نظر العقول، وزعم أن ذلك إنما لم يحصل، لأن طلبات العقول قائمة، والخطأ ممكن، ولا يستقل بجميع وجوه النظر عاقل، فإذا كان كذلك، [لم يفد] الخبر عن النظريات علمًا. وهذا [الكلام] ضعيف، وما المانع من حصول العلم بإخبارهم، وإن أخبروا عن نظري؟
فلئن قال: إذا ثبت في نفس السامع إمكان الزلل، كان ذلك مانعًا يمنعه من حصول العلم، لتجويزه الخطأ على المخبر. قلنا: فينبغي أن يحصل العلم','2','575',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1272,'لمن لم يخطر له هذا التجويز. [وسامع الخبر المتواتر يحصل له العلم بالمخبر عنه، وإن لم يكن المخبر عنه مما يجوز] فيه الغلط. ويحصل أيضًا له العلم، وإن لم يتنبه لكمال العدد، وهذه أمور اعتيادية، لا سبيل إلى ضبطها، [ولا] الوقوف على أسرار [تقتضيها]، مع تجويز العقول [نقيضها]، بل نقول: كان يجوز في مقدور الله - عز وجل - أن يخلق [للسامع] العلم عند [استناد] الخبر إلى النظر، ولا [يخلقه] إذا استند الخبر إلى الضرورة، فلا [يتضح] في ذلك تعليل معقول، وإنما يرجع الأمر إلى العادات واطرادها.','2','576',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1273,'فأما ما قيده الأصوليون من اشترط الاستناد إلى الحواس، فلابد منه.
وما ذكره الإمام لا [يصح]. أما وجوب اشتراط [الاستناد] إلى الحواس، فهو أنه إذا تقرر أن العلم لا يحصل بخبر المخبرين من جهة العادات، إلا إذا استند إلى الضرورات، [والضروري] إما أن يكون عقليًا محضًا، فيجب اشتراك العقلاء فيه، فلا يكون الخبر قط في هذا الصنف محصلًا علمًا، [لحصول] العلم قبل الخبر. وإذا لم يكن الضروري عقليًا محضًا، [فلابد أن] يستند [إلى محسوس]، فقد وضح ما اشترطه الأصوليون.
فأما الصورة التي اعترض بها، فغير [لازمة]؛ فإن الأصوليين إنما','2','577',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1274,'اشترطوا أن يكون له استناد [لمحسوس] على الجملة، وقرائن الأحوال تستند إلى المحسوس، وإن كان فيها [أمور] دقيقة، يعسر التعبير عنها. والإنسان يحس الفرق بين حمرة الخجل والغضبان، وإن تعذر عليه (١١٣/ أ) التعبير. وهذا أيضًا إنما يكون على رأي [من يرى] أن الرؤية تتعلق بالمرئي على صفاته الخاصة والعامة، وأما من يرى أن الرؤية لا تتعلق إلا بالموجود، فهذا لا يقول: إن اختلاف الألوان يتعلق به [الحس]، وإنما العقل يميز ذلك عند تعلق الرؤية بالوجود، [وهذا] يحقق في علم الكلام.
[وأما] قوله: ([فيشترط] [استناد] الأخبار إلى البديهة والاضطرار). فكلام ضعيف، إذ فيه عزل [الحس] عن ذلك بالكلية،','2','578',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1275,'فإن المراد [بالعلوم] (١٣٨/ ب) البديهية: ما لا يتصور انفكاك العقلاء عنها، فأي فائدة للإخبار فيها؟
قال الإمام: (ومما يشترط في الخبر المتواتر [صدوره عن] عدد. وقد اضطرب الناس في ذلك]) إلى قوله (عارضنا القاضي بما ينقض عليه مذهبه). قال الشيخ: ما ذكره الإمام [من] تتبع أصحاب الأعداد صحيح، ولا مزيد عليه. وأما القاضي- رحمه الله- في كونه أخرج الأربعة','2','579',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1276,'عن عدد التواتر، لتوقف القاضي على طلب التزكية، ولا معنى لطلب التزكية فيما علم ضرورة.
هذه المسألة فيها نظر من جهة الفقه، وهو أنه، هل طلبت التزكية لتحصل غلبة الظن للحاكم، أو طلبت تعبدا؟ ومقصود هذه المسألة: لو شهد العدول عند الحاكم بأمر يعلم خلافه، هل يحرم عليه الحكم أو يجب؟ ذهب مالك رحمه الله إلى أنه يجب عليه الحكم في القول المشهور عنه. هذا إذا لم يعلم تعمد','2','580',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1277,'الكذب، أما إذا علم تعمدهم الكذب، فقد [فسقوا]، وهو في الترجيح والتعديل يطالب بعمله في ذلك، وقال: إن لم يقض بشهادتهم، كان ذلك إيقافا للحكم بشهادة العدول.
وقال غيره: لا يجوز الحكم [بالشهادة]، إذ علم القاضي النقيض، إذ','2','581',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1278,'كيف يحكم بما يعلم خلافه؟ ولكن هذا يفضي إلى أن يحكم الحاكم بعلمه، [وقد] ترك رسول الله - صلى الله عليه وسلم - الحكم بعلمه في المنافقين، وإن كانوا [منكشفين] له، وحكم [بقول] العدول، وإن لم يحصل له من ذلك إلا الظن، فدل ذلك على أنه لا التفات لعلم الحاكم وظنه، فيما يتعلق بالأحكام، وفصل الخصومات.','2','582',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1279,'وقد ساعد أهل العراق على أنه لا يحكم في الحدود بعلمه. فعلى هذا لا نسلم أن طلب التزكية لتحصيل غلبة الظن [للحاكم]، بل للتعبد الحاصل والذي يحقق ذلك أنه لو شهد من النساء عدد التواتر في غير المال، لم يقض بالشهادة، [إذا] كان ذلك المحل مما [لا] [ينفردن] [فيه بالاطلاع] عليه. وإن كان في المال، لم [تنفع شهادتهن] إلا مع اليمين عند بعض أهل','2','583',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1280,'العلم، [أو مع] رجل عند آخرين. وإن كان العلم حاصلا، بالأمر المشهود به.
فلا نسلم للقاضي [على هذا التقدير] أن التزكية المطلوبة دالة على نفي [العلم الضروري]، لتحقق العلم في مواضع، مع امتناع القضاء.
وأما إلزام الإمام له (في طلب الاستظهار، وما يفرض من استظهار لا يبلغ مبلغ العلم). هذا غير لازم، [والقاضي] توقف في هذه الصورة، ولم يحكم فيها بنفي، ولا إثبات.','2','584',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1281,'وكون الإمام يقول: (إن خرج الخامس عن مراتب (١١٢/ ب) البينات، فهو واقع في أبواب الاستظهار). إنما منع [القاضي ذلك] في الأربعة، لتوقف الأمر على طلب العدالة، وهذا في الاستظهار غير محقق، ولكن نقول: لا معنى لتوقف القاضي في الخامس، فإن العلم إذا قام بالعالم علمه، ولا يتصور أن يكون الإنسان عالما وهو لا يعلم. فلو كان الخامس يحصل علما، لعلم [المجتهد] ذلك. ولسنا نجد من أنفسنا العلم الضروري بخبر الخمسة. فلا معنى عندي لهذا التوقف. وكم من خبر نسمعه من خمسة وستة، ثم ينكشف [كذبا]. فالصحيح [إلحاق] الخامس بالرابع.
وقد ذهب القاضي في بعض كتبه [إلى] [أن الطريق إلى معرفة العدد، أن يمتحن اللبيب نفسه [عند] أول خبر يسمعه عن قضية، فإن النفس قد','2','585',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1282,'تميل [إليه]، (١٣٩/ أ) ثم الثاني والثالث- يريد [الأمر]- وكذلك الرابع والخامس، إلى أن ينتهي إلى حد يحصل له العلم، فإذا انضبط له ذلك العدد، الذي ترتب العلم عليه، وحفظ [حساب المخبرين] [عند] ذلك العدد- معتبرة في كل واقعة يخبر عنها-[قضى] بأن العدد [الذي] [يحصل] العلم في واقعة، [لابد وأن] يحصله في كل واقعة، ولشخص أنه لا بد أن يحصله لكل شخص].
وهذا [عندي] مشكل، وقرائن الأحوال مؤثرة في [الغالب]، وهي لا تنضبط على كل حال، وكذلك أحوال المخبرين، فقد يقل العدد من أشخاص معروفين بالصدق والضبط، فيحصل [العلم] للسامع بأخبارهم، [وإن] قل العدد.
وقد يكون الأمر [بالضد] من ذلك، وكذلك المخبر، فمن الناس من','2','586',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1283,'خلق على [طوية] [تحسين] الصدق بالمخبرين، فينضاف [قلة] العدد [لما في طبيعته] في التصديق، فيحصل له العدد بعدد، لو أخبروا غيره- ممن خلق على خلاف طبيعته- لم [يحصل] له العلم. وكذلك أحوال الوقائع من الإشهار، والوقع في النفس، وقرب العهد [بالواقعة]، فهذه الواقعة تحتاج في حصول [العلم] بها إلى الكثرة المفرطة، وليس كذلك الإخبار عن الأمور البعيدة.
وعلى الجملة هذه أمور معتادة، وقرائن الأحوال مؤثرة [بلا إشكال]، فلا سبيل إلى الضبط على حال.
[قال الإمام]: ([فأما] من قال [عدد] التواتر: لا يحويهم','2','587',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1284,'بلد ولا [يحصيهم] عدد) [إلى قوله] (إلى الخروج عن المعقول من غير غرض). (١٢٤/ ب) قال الشيخ: قد ذهب النظام إلى أن خبر الواحد','2','588',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1285,'يوجب العلم، وكذلك ذهب إلى هذا المذهب الحشوية. وإذا كان الرجل قد قال ذلك، فلا معنى لتكذيب النقلة. [لاسيما] وقد احتج لمذهبهم','2','589',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1286,'بحجج، فكيف يتفق التكذيب في ذلك؟
فأما ما [تأوله] [الإمام] عليه من (أنه أراد هذه الصورة من اجتماع هذه القرائن). [فهذا تنزيل] اللفظ العام على أندر الصور وأبعدها. على أنا لا نسلم أيضا أن هذه الصورة يتحتم فيها حصول العلم، وقد يتعاطى مثل ذلك تلبيسا لغرض من الأغراض. وقرائن الأحوال على انفرادها، قد يحصل منها علم من غير حاجة إلى إخباره.','2','590',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1287,'فإن دلت [القرائن على تعيين المصيبة، وانتهت] إلى حد يحصل (١٢٥/ أ) علما، فلا فائدة في الإخبار. وإن دلت القرائن على أصل المصيبة، ولم [تتعرض للتعيين]، ولم يكن [في] التعيين إلا محض إخباره، فلا يحصل العلم.
ويبقى النظر في صورة، وهي أن تكون القرائن لها تعلق بالتعيين، ولكن لم تنته إلى حد يحصل علما، ويبقى بينها وبين [تحصيل] العلم مثلا رتبة واحدة، فيخبر المخبر عن التعيين، فهل [يتنزل إخباره] منزلة ما بقى من القرائن، حتى يترتب العلم من القرائن والخبر؟ [هذا] محل [الخلاف].','2','591',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1288,'والقاضي منع ذلك، وقال: لابد من عدد كامل، أو قرائن أيضا كذلك. أما التلفيق من القسمين، فلا سبيل إليه.
ولعمري إن هذا الذي قاله القاضي هو المطرد في الأدلة العقلية والسمعية، فلا يتركب معلوم من [جزءي] دليلين، [لا عقلين] ولا [سمعيين]. أما الأسباب العادية، فلا [يتعذر] ذاك فيها، فإنها تجري على أمور من غير أن تكون أسبابها مقتضية لها اقتضاء عقليا. وهذا ممثل بالطعام، فقد يأكل الإنسان من صنف مقدارا مخصوصا، فيشبعه، ولا بعد في أن يتناول من أصناف مختلفة ذلك المقدار فيشبع. وقد قدمنا أن القرائن في هذا مؤثرة في الاعتياد.
[وأما] الانتهاء إلى حد لا يبقى [معه] من القرائن إلا قرينة واحدة، بحيث لو اقتصر عليها، لم يحصل العلم، فإذا أخبر [مخبر]','2','592',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1289,'(١٣٩/ ب) [حصل] العلم. فهذا إنما يعرف بالتجربة، ولم نجربه، ولكن [كثيرا] ما [انعقد] جزما، بناء على قرائن الأحوال [المنضمة] إلى الخبر، [ثم] صودف كذبا. وقد يقول القائل: إن القرائن في تلك المواضع نقصت، ولم بق خبر بما بقي منها. فهذا محل مشكل، وأمر ملتبس.
قال الإمام: ([وإذا] ذكرت [إمكان] حصول العلم [بصدق]','2','593',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1290,'[مخبر] واحد) إلى قوله (حاملة على الكذب، رابطة له). قال الشيخ: هذا الذي ذكره الإمام يشير به إلى أنه لابد في حصول العلم بأخبار التواتر من القرينة، ولو تحدد العدد، لم يلتفت إليه. وليس كذلك، ولا خلاف [في صحة] استقلال العدد الكامل بحصول العلم، إذا وجدت بقية الشرائط، وليس العدد على انفراده مكتفى به، بل العدد مع استناد الإخبار إلى المحسوس، على حسب ما اخترناه.
[وأما] إذا حملوا على الكذب، فالعلم لا يحصل، [لا] لنقصان العدد، ولا لكونه غير مكتفى به، إلا مع [القرينة]، لكن لفوات الشرط الثاني، وهو الإخبار عن المحسوس، أو [الضروري].','2','594',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1291,'ولو أخبر أهل الزمان عن حدث العالم، لم يحصل [العلم]، لا لافتقار العدد إلى القرينة، ولكن لفوات الشرط الثاني، فالمصير إلى ا، العدد غير مكتفى به حتى [ينضم] إليه ما يجرى مجرى القرينة، لم يصر إليه أحد من الأصوليين، ولا [يقتضيه] التحقيق. والصورة التي تمسك بها، قد [تبين] وجه الحق فيها، وسبب تخلف العلم عنها.
فإن قيل: فهل يتصور أن يحصل العلم بإخبار قوم إن (١٢٣/ ب) أخبروا عن اختيار، ولا يحصل إن أخبروا عن إكراه؟ قلنا: قال القاضي: لا يتصور ذلك؛ فإنه لا يشترط إلا [بشرطين]: أحدهما- كمال العدد','2','595',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1292,'والثاني- إسناد الإخبار إلى محسوس، فإذا حصلا، فلابد من حصول العلم.
وقد نازع في ذلك أبو حامد وقال: يصح [أن] يحصل العلم [إن] كانوا صادقين، وقرره بأن قال: يجوز أن يكون سبب العلم عند كثرة المخبرين ما يجده العاقل في نفسه [من] أن هؤلاء [مع] كثرتهم لا يحملهم على الكذب حامل إذا أخبروا، ثم يصدق أنهم أخبروا، فيحصل العلم، فإذا ظهر كون [السيف] حاملا مثلا، فقد فاتت إحدى مقدمتي السبر، [فيفوت] العلم. وهذا باطل، فإن سامع الخبر لا يخطر بباله ما ذكر من [اختصار] المقدمات، [وقد] يسمع ذلك من لا [يحسن] نظم المقدمات، ولا تلقي النتيجة منها، والعلم يحصل.
قال [جوابا] عن هذا: لابد من حصول هذه المقدمات في النفس معلومة، ولكن قد لا يشعر الإنسان بها، ولا بتوسطها، ولا بترتيب العلم عليها، فإن الشعور بالشيء، غير الشعور بالشعور. وهذا قريب من السفسطة، وهو أيضًا مفضي إلى الاسترسال [إلى] ما لا يتناهى.','2','596',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1293,'أما وجه السفسطة، فهو أن الواحد منا لا يمتنع أن يقوم به علم الأولين والآخرين، [وهو لا يعرف] ذلك من نفسه، وكذلك علم الهندسة والطلسمات [والنحو] وغيره.
الثاني- أنه لو قام به العلم، ولم يعلمه، مع قبول لأن يعلم، صح أن يعلم العلم [بعلم]، أو وجب أن يقوم به جهل، فإن العلم مما يصح أن يعلم [وأن يجهل، فإذا لم يعلم]، فإنما لم يعلم، لجهل يقوم [بالعالم] يمنعه من علم علمه. والجهل أيضا يصح أن يعلم وأن يجهل، فيقال فيه ما [يقال] في العلم، وذلك محال، فوجب أم كل من قام به العلم أن يعلمه بنفس العلم. وقد تقدم الكلام على هذا قبل هذا.
قال الإمام: ([وأنا] الآن [أنخل] للناظر جميع مصادر','2','597',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1294,'المذاهب إلى قوله (ما يجري مجرى القرينة [من الحالات] الجامعة) قال الشيخ: هذا الذي (١٤٠/ أ) ذكره الإمام للسمينة غلط بين، والقوم قصروا العلوم على [الحواس]، ونفوا ما عداها.
وأما عذره عن [أن] العدد غير مكتفي به، حتى ينضم [إليه] ما','2','598',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1295,'يجري مجرى القرينة، فقد تكلمنا عليه، وبينا أن العدد بمجرده لا خلاف في الاكتفاء به عند الشرط الآخر.
قال الإمام: (وذهب الكعبي إلى أن العلم [بصدق] [المخبرين تواترًا نظري]) إلى قوله (وليس ما ذكره [إلا] الحق). قال الشيخ: هذا الذي ذكره الكعبي قد أنكره المتكلمون والأصوليين من أهل السنة والمعتزلة، وإنما (١٢٤/ أ) انتصر [له] الإمام وأبو حامد.','2','599',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1296,'أما الإمام، فقد [بين] عذره عنده. وهذا الذي ذكره [له] باطل قطعا، والدليل عليه أنه لو توقف العلم بصدق المخبرين تواترا على النظر في الإيالة الحاملة وانتفائها، لوجب أن لا يحصل العلم إلا لمن أخطر ذلك بباله، فإن العلم إذا كان يتوقف على أسباب، فلا سبيل إلى حصوله عند فقدان بعض الأسباب. وقد يحصل العلم في الحال لم ن لا يخطر له النظر [في] السياسة على بال، والعلم يحصل لمن ليس من أهل النظر على حال.
وأما أبو حامد، فقد سلك [مسلكا] آخر قريبا من هذا، وذلك أنه قال: العلم [الأولي] أو البديهي: هو الذي يحصل في [النفس] القضاء بالنسبة من غير افتقار إلى واسطة، [فلا] يفتقر العاقل فيه إلا إلى معرفة [المفردين]، فإذا عرضا على العقل، [صدق] بالنسبة من غير توقف،','2','600',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1297,'كقولنا: الضدان لا يجتمعان، والشخص الواحد لا يصح أن يكون في حالة واحدة في مكانين، إلى ما يجري مجراه. [فكل] ذهن تصور المفردين، بادر إلى النسبة عند العرض عليه.
القسم الثاني: أن لا يتلقى في الذهن طرفا هذه الواسطة، كما إذا قلنا: العالم حادث، فمن فهم العالم والحادث، [ونسب] أحدهما إلى الآخر بالنفي والإثبات، وعرض على العقل، لم يبادر العقل إلى هذه النسبة، فيطلب أمرا آخر، [فنسب] إلى العالم [فيصدق]، وينسب إليه الحدث، فيصح، فيقال مثلا: العالم [متكثر]، [والمتكثر] حادث، فالعالم [حادث]. فقد افتقرنا إلى إدخال التكثير أو التجديد باعتبار أعراضه. فالموضع الذي يفتقر إلى وسط مستجلب، لا إشكال في كونه نظريا، والموضع الذي لا يفتقر إلى وسط مستجلب، لا إشكال في كونه نظريا، والموضع الذي لا يفتقر إلى الوسط بحال، هو المعبر عنه [بالأولي] [أو البديهي]. ورب واسطة','2','601',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1298,'مفتقر إليها، ولكنها حاصلة [جلية، فلا يفتقر إلى استجلاب لحصولها، وقد يظن ألا حاجة إليها، وليس كذلك، بل لم يطلب حصولها، لأجل أنها حاصلة]، فيحسب لذلك أن الشيء أولي، [وليس بأولي].
قال أبو حامد: وخبر [التواتر] مفتقر إلى واسطة، ولكنها حاصلة، فلم تطلب، [فيظن] الاستغناء عنها، وذلك غلط. قال: ورب واسطة [حاصلة] لا يتنبه [الإنسان] لتوسطها وحصولها، [ولا] لارتباط العلم بها. قال: ولا يتصور حصول العلم، بناء على خبر التواتر، حتى تشعر النفس بالكثرة مع الموافقة على الخبر، فيقال: [الكثير] إذا أخبروا لم يكذبوا، وهؤلاء قد كثروا وأخبروا، فلا يكذبون. هذا تمام كلامه، وتقريره [للكعبي]، واختياره له.
[فأقول]: [هذا] الذي قاله غير صحيح. أما قوله: إن الواسطة','2','602',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1299,'[حاصلة]، والنفس بها [غير] مشعرة، ولكنها لم تشعر بشعورها. فقد تكلمنا عليه بما فيه كفاية.
وأما [قوله]: إنه لا يحصل العلم أبدا إلا [إذا] [استشعرت] النفس الكثرة [التي] يستحيل عليها الكذب. [فأقول]: لو كان (١٢٥/ب) الأمر على ما يقول، لاستحال [ترتيب] العلم على خبر التواتر المحض (١٤٠/ب)، [فإن] ضبط الكثرة التي [يترتب] عليها العلم غير معروفة، وإذا شك الإنسان في الكثرة [التي] [يترتب] العلم عليها، هل حصلت أم لا، لم يتصور أن يحصل العلم، إذ شرط حصوله تحقيق العلم بوجدان الكثرة [المحيلة] [للكذب].','2','603',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1300,'[ثم إن استحالة الكذب] على العدد الكثير، إنما هو [أمر] متلقى من العادات، فأول سامع للخبر المتواتر لم تنضبط له عادة يعرف بها استحالة الكذب على العدد الكثير، فلا يحصل العلم لسامع أول الخبر المتواتر، ثم الثاني والثالث كذلك، فلا تستقر عادة محيلة [للكذب]، فلا يحصل علم بالخبر المتواتر على حال.
قال الإمام: (وظن ظانون [أن] العدد معتبر) إلى قوله (فهذا هو','2','604',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1301,'المنتهى الذي ليس بعده مطلب لمتشوف). قال الشيخ: [هذا] الذي ذكره الإمام في هذا الموضع هو المختار، وقد تقدم الكلام عليه قبل هذا. وإنما ننازعه في وجه واحد، وهو كونه يقول: إن العدد بمجرده لا التفات إليه، فإنا قد بينا خلاف هذا.
[قال]: (وإن العدد من جملة القرائن). [وفي كلامه إرشاد إلى ذلك، وهو قوله: (إن الكثرة من جملة القرائن)]. يعني [التي]','2','605',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1302,'[يترتب] العلم عليها. فقد اعترف بأن العدد على انفراده قرينة. وهذا هو الحق الذي لا شك فيه.
قال الإمام: (ونحن نذكر بعد ذلك مجامع من كلام الناس) إلى قوله (وليس من شرائط وقوع التواتر). قال الشيخ: الشرط قد تقدم الكلام على حقيقته ومعناه. ونزيد ههنا زيادة، وهي أنه لابد أن يكون [زائدا] على المشروط، إذ لا يتصور أن يكون الشيء شرطا في نفسه. وإذا كان كذلك، فمن المحال أن يكون تواتر النقل شرطا في التواتر، فكيف يقال: من شروط التواتر','2','606',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1303,'استواء الطرفين [والواسطة] بالإضافة [إلى] كثرة العدد؟
وقولهم أيضا: إن التواتر قد ينقلب [آحادا]. كلام لا يصح على حقيقته. [إذ] يكون معناه: أن [الكثير] بعينه قد يصير واحدا، وذلك باطل، وإنما أراد القوم أن الخبر المتواتر قد ينقل [آحادا]، [ويندرس] ما تواتر مدة. وهذا كلام صحيح، والعبارة مطلقة على التجوز والاستعارة، وهذا لا خفاء به.','2','607',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1304,'قال الإمام: ([وذكر] [بعض] أصحاب المقالات عن اليهود: أنها اشترطت في التواتر أن يكون في المخبرين [أهل] ذلة وصغار]) إلى قوله ([كالفضل] المستغنى [عنه]). قال الشيخ: بقي من مذاهب الناس في شروط التواتر مذهبان لا ينقلهما الإمام.
أحد المذهبين: شرط قوم في عدد التواتر أن تختلف أديانهم، [ولا يكونوا] أهل ملة واحدة، وتختلف مواضعهم، فلا يكونون أهل بلدة واحدة،','2','608',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1305,'وتختلف أنسابهم، فلا يكونون أبناء أبٍ واحد. [وكأن] هؤلاء القوم يزعمون أنه إذا اجتمعت هذه الأسباب من الاختلاف، [تعذر] التواطؤ من جهة العادات. وهذا باطل بوجهين:
أحدهما- أن العلم يحصل بقول (١٢٦/أ) الحجيج إذا أخبروا عن واقعة بعرفات منعت الناس، وهم أهل ملة واحدة، وقد جمعتهم بقعة واحدة. بل نقول: أهل الجمعة إذا أخبروا عن أمر حدث في المسجد منع الجمعة. بل نقول: الروم إذا أخبروا [عن موت ملكهم].
الوجه الثاني: أن الكثرة مع بقية الشروط تحيل ذلك وتؤمن من التواطؤ فيه.
وذهب قوم إلى [أنه] لابد أن يكونوا أولياء صالحين، مصيرا','2','609',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1306,'[منهم] إلى أن العدالة [تنافي] الكذب. [وهذا باطل (١٤١/أ) من وجهين:
أحدهما- أن العدالة إنما تنفي الكذب] ظنا لا قطعا، لتصور الكذب من العدل، سهوا [أو عمدا]، والمطلوب من الخبر المتواتر العلم، فأي فائدة لشرط لا يحصل إلا غلبة [الظن] في موضع [يطلب] فيه العلم؟
الثاني- أن العادة [بخلاف] ذلك، [فكم] من خبر علمناه','2','610',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1307,'مستندين إلى أخبار الروم، وما [ينزل] ببلادهم من موت سلطان، أو قيام حرب، [وما] يجري مجراه.
[وذهبت] الروافض إلى أنه يشترط أن يكون في المخبرين الإمام المعصوم. وهذا المذهب هو مذهب من ينفي حصول العلم [مترتبا] على خبر التواتر، وهم السمنية. وأما إذا كان في المخبرين الإمام المعصوم، [فالعلم] يحصل مستندا لقوله، فلا فائدة في انضمام غيره إليه.
الوجه الآخر: أنه كان ينبغي أن لا يحصل العلم [بقول] أهل بلدة، إذا لم يكن هو في المخبرين، ولا يحصل العلم [بقول] دعاته ورسله، ولا تتوجه له حجة على غير أهل محلته، إذ العلم مفقود، وكل ذلك قياس [مذهبهم] وهذيانهم.','2','611',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1308,'قال الإمام: <span data-type="title" id=toc-220>([فصل]- ذكر الأئمة [تقاسيم] [الأخبار]) </span>إلى قوله (وأهل الإجماع لا يجتمعون على باطل). قال الشيخ: ما ذهب إليه الأستاذ أبو إسحاق من أن المستفيض يحصل العلم. نظر باطل لا شك','2','612',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1309,'فيه، فإن النظر العقلي لا يقتضي العلم بصدق الخبر [المتواتر]، وقد بينا ذلك، فكيف بالمستفيض؟ وأراد أنه قد ينقص العدد، ولكن يكون النظر في أحوال المخبرين من الثقة [والتحرية] يحصل ذلك.
وقد مال إلى هذا أبو حامد، ولا وجه له. نعم، ما ذكروه مما يغلب','2','613',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1310,'على الظن، [فأما] العلم فلا. والعلم مع تجويز [النقيض] في حالة واحدة محال.
وأما ما ذكره الأستاذ أبو بكر، وهو ما اتفق عليه المحدثون، فهم يتفقون على العمل بالخبر ويطلقون عليه [لفظ] الصحة، على حسب ما رآه الإمام. وليس يعني الأصوليون ولا المحدثون بالصحة التصديق، ولا بالرد التكذيب. [بل] يقضي بقول خبر العدل، وإن أمكن أن يكون كاذبا. ولا','2','614',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1311,'يقضي [بقبول] خبر الفاسق، وإن أمكن أن يكون صادقا. وتصريحهم بأنه حق، لا شك فيه [بحال]، كما قرره القاضي.
فإن قيل: فلو كان كاذبا، [لكان] عمل الأمة بخبر باطل، ويكونون مخطئين، والعصمة لهم [ثابتة]. فلابد من أن يكون الخبر صدقا. قلنا: ليس كذلك، فإن الله تعالى [أمرهم] بالحكم بخبر من ظهرت لهم عدالته، سواء كان هو صادقا أو [كاذبا]، ولا تكليف على القوم بصدقه.','2','615',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1312,'وكذلك القاضي إذا حكم بشهادة الشاهد الذي ظاهره العدالة (١٢٦/ب)، وكان [مزورا] في الباطن، فهو مصيب، [وإن] كان الشاهد كاذبا. وقد قال رسول الله - صلى الله عليه وسلم -: (إنكم لتختصمون إلي، ولعل بعضكم أن يكون ألحن بحجته من بعض، فأقضي له على نحو ما أسمع، فمن [قضيت له بشيء] من حق أخيه، فلا يأخذه، فإنما أقطع له قطعة من النار). فهو مصيب فيما كلفه الله [به] من الحكم، على حسب الدعوى [ودليلها]. والمبطل مخاطب','2','616',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1313,'في حق نفسه بأن لا يأخذ ما يدري أنه لا يستحقه.
قال الإمام: ([وأما] القسم الثاني) إلى قوله ([وهذه] الأصول [قدمناها] وبينا ما يستند من أمور [الدين] إليها). قال الشيخ: جميع الأخبار إنما يعلم صدقها بأمر زائد على [خبر] المخبرين، إلا الخبر المتواتر خاصة، فإنه يحصل العلم بالصدق (١٤١/ب) فيه وإن لم [يقترن] به أمر آخر، بناء على اطراد العادات. وأما الخبر الكذب، فلا يعلم كذبه إلا [بأمر]','2','617',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1314,'زائد عليه، وحاصله راجع إلى أن كل خبر على نقيض ما علم، فهو كذب.
[وقد] يعلم المعلوم ضرورة. وقد يعلم نظرا. وقد يعلم بالحس أو التجربة [أو العادة]، أو من الأدلة الشرعية. والذي يقتضي العلم كثير، ولا حاجة إلى ضبطه في هذا المكان، إذ ليس ضبط [الطريق] هو المقصود، وليس في شيء من [ذلك] إشكال، إلا في قسم واحد، وهو ما للعادة فيه قضاء، مع جواز الخرق، ولكن قد تدرج مسائل في أحكام العادات، ولا تكون منها.
[فمنها] القطع [بعدم] النص على علي- كرم الله وجهه- حكم به الأئمة، بناء منهم على أن الخلافة من [المهمات]، وأمرها من أعظم الأمور','2','618',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1315,'في العادات، فلو كان فيها نص، لما خفي على حال. قالوا: فنحن نعلم عدم النص على علي [- رضي الله عنه -]. وهذا الذي قالوه لا يقوى [عندي، لاحتمال أن يكون الرسول - عليه السلام - لم يذكر ذلك إلا لواحد أو اثنين، أو دون عدد التواتر].','2','619',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1316,'وعندما ينقص العدد، يصح النسيان والكتمان، فلا [تشتهر] القضية، ولا يلزم من ذلك عدم النص بحال. وكذلك إذا حصل الإلقاء [لعدد] التواتر، ثم اتفق موت بعضهم أو نسيانه، فينقص العدد [ولا] تتواتر القضية بحال، ويجوز [عند] نقصان العدد الكتمان والنسيان جميعًا. نعم. الذي يقطع به أن القضية يوم السقيفة لم تكن مذكورة [لعدد] التواتر، [إذ] لو كان كذلك، لما تصور الكتمان مع الذكر مع العدد الكثير.','2','620',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1317,'هذا القسم خاصة هو الذي يقطع به. وكذلك أيضًا يطرد هذا فيما إذا اقتصر على عدم المعارضة بامتناع الإشاعة، وأن ذلك لو كان، لم يكن بد من إشاعته.','2','621',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1318,'هذا أيضًا لا يحصل منه علم بعدم المعارضة، [لاحتمال أن يكون المعارض عارض، ولم يظهر لمقصد، أو لموت قبل الإعلان، فلا يكون عدم الإشاعة دليل عدم المعارضة]. نعم، يعلم أن ظهور المعارضة لعدد التواتر منتفٍ قطعًا.
فإن قيل: فهل تقطعون بانتفاء المعارضة؟ قلنا: نعم، والمستند في ذلك حصول العلم الضروري بعجز العرب [العاربة، مع كمال الفصاحة، وتمام البلاغة، واعتقادهم أن هذا خارج عن مقدور البشر]. [ولذلك] أنهم حاربوا وجاهدوا مع التصريح (١٢٧/أ) بأنهم إن [عارضوا]، زالت هذه العبر، وارتفعت الحرب، وحصل الانقياد، فما أضربوا عن هذا الأمر الذي هو سهل عليهم، ومقتضى لغتهم، إلا وقد تحققوا الاستحالة، وعلموا العجز. وإذا عجز الفصيح، فالألكن أعجز.','2','622',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1319,'فبهذا علمنا أنه لم يتفق لأحد معارضة القرآن بحال. وكذلك [القول] في النص على أبي بكر وغيره. المقطوع به أنه لم يكن عند أهل التواتر نص تواطأوا على كتمانه. فأما عدم النص [على الإطلاق]، فلا يقطع به، [وهذه] نكتة [حسنة] ينبغي أن [يتنبه لها].
وما ذكره الإمام [من] أن (التواتر قد يتواتر عن قرب، ويفضي الأمر بعد ذلك إلى [الدروس]، وقد يستمر الأمر دهرا طويلا).','2','623',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1320,'[وهذا] صحيح، فإن النقل بحسب دواعي النفوس، فما كان من الغرائب والعجائب، تواتر وقت وقوعه، وكذلك ما [جل] في النفوس، وعظم في القلوب، كقتل الملك، [ونقل] الدول، ثم يستأنس [الناس] به. [وأما] الذي يدوم نقله، فالأمور الدينية، لما تعلق للخلق [من الغرض] بدوامها.
قال الإمام [رحمه الله]: (ونحن [الآن نوجه] أسئلة [يتعين','2','624',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1321,'(١٤٢/أ) الاعتناء بها]) إلى قوله (بسبب تخييلات وإلزامات). قال الشيخ: هذه القاعدة لابد من الإحاطة بها، وهي أنه إذا حصل العلم بأمر مستند إلى العرب، [فما] علم لا يتصور خلافه. وإذا كانت العادة تقتضي في شيء- إن وقع- أن ينقل تواترا، وعلم ذلك، بناء على العادة، فإذا لم يتواتر، علم أنه لم يكن، فإذا سلم الخصم ذلك، لم يتصور ورود النقض على المعلوم بحال. [فإذا] جرت أمور تخيل فيها أنها تنقض [القاعدة]، علم أن ذلك','2','625',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1322,'لا يكون، فإما أن تكون المسائل ليست من القاعدة، [وإما] أن تكون متواترة عند أصحاب ذلك الفن، فإن هذا أيضًا محل فيه انقسام: فمن المتواتر ما يتواتر عند الكافة، كتواتر الصلوات الخمس، وصوم رمضان، ومنه ما يتواتر عند بعض الناس، فكم من خبر يعد من أخبار الآحاد، وهو عند أهل الحديث من المتواترات.
ولا شيء في الدين أعظم تواترا من القرآن، ولكن عند القراء وحملته. وكم من رجل من العقلاء لا يدري كلمات القرآن، ولا سوره، فلا يكون عدم التواتر عند بعض الناس، دليلا على انتفاء التواتر على الإطلاق. وقد','2','626',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1323,'يكون الشيء مما يقتضي العرف التواتر فيه، ابتداء لا دواما. فهذا هو الجواب الكلي المغني عن التفصيل. وأما الجواب عن تفاصيل الأسئلة الواردة [فيهن].
أما الإفراد والقران، [فإنه] [لما] علم الصحابة أنهما جميعا مسوغان، [وإنما] يرجع الأمر [إلى الفضيلة]، لم يقع الاعتناء بالفعل في ذلك، كسائر النوافل والمندوبات. ويمكن أن يكون هذا من قبيل ما يتواتر أولًا، ثم [تناقص] الهمم في نقله، على ما بيناه.
قال الإمام: ([وأما] انشقاق القمر) إلى قوله (أن الأمر الضروري','2','627',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1324,'لا تخرمه التخييلات). قال الشيخ: أما المصير إلى أن معنى الآية: (سينشق القمر)، [فخلاف] مذهب أهل السنة. وقد روى الانشقاق طائفة من الصحابة، كابن مسعود وابن عمر [وغيرهما]. وقد قال العلماء: [إنكار] انشقاق القمر مذهب أهل البدع.
وأما عذر الإمام عن عدم التواتر، بأنها آية ليلية، جرت والناس نيام والمستيقظون في أكنان. فهذا كلام ضعيف، وكيف يتفق أن لا يحيط بانشقاق القمر ونزوله إلى الأرض عدد التواتر، ونسبته إلى الأرض نسبة','2','628',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1325,'واحدة؟ هذا محال في العادة، لا شك فيه.
وإنما السبب في عدم تواتر النقل على الدوام، أنه يقع في أبواب الكرامات، لا المعجزات، لاستقلال الشريعة بالقرآن دون غيره، وما تحدى الرسول - عليه السلام -[آحاد]، وأسندها الرواة إلى مشاهد فيها جمع كثير من الصحابة، كما رواه جابر بن عبد الله عام الحديبية، وكانوا ألفا وأربعمائة، أو ألفا وتسعمائة، في البئر التي جمع منها','2','629',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1326,'الماء اليسير، فكان ينبغ من بين أصابع رسول الله - صلى الله عليه وسلم -. وكذلك الطعام الذي صنعه أبو طلحة، فقال - عليه السلام -: (ائذن لعشرة، فأكلوا حتى شبعوا، وأكل القوم، وهم ثمانون رجلا). وآياته وعجائبه أكثر من أن تحصى. ولما كثرت هذه الخوارق (١٤٢/ب)، [لم] تتوفر النفوس على نقل الأفراد تواترا على الدوام.
وتعظم في عين الصغير صغارها .... وتصغر في عين العظيم العظائم
هذا طريق الكلام على انشقاق القمر، فأما منع الانشقاق، فخلاف مذهب أهل السنة.
قال الإمام: (وأما أمر الإقامة، [فإنها] من أغمض الأسئلة) إلى','2','630',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1327,'(١٢٧/ب) قوله] (كل خبر خالفه حكم العرف، فهو كذب). قال الشيخ: ما ذكره الإمام في أمر الإقامة من [تهوين] الصحابة أمرها. ليس الإشكال في عدم التواتر، لأن الأمر ليس بعظيم، وإنما الإقامة شعار','2','631',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1328,'مسنون، [فلا] معنى لاشتراط التواتر فيها، وليس كل ما يتكرر، يلزم أن يكون متواترا. وهذا الوتر، وركعتي الفجر مما يتكرر، والنقل فيها آحادًا. وإنما [الغامض] فيها اختلاف النقلة عن النبي - صلى الله عليه وسلم -، واقتصار أهل كل جهة على ضفة.
وما [أظن]- والعلم [عند الله]- إلا أن النبي - صلى الله عليه وسلم - علم كل قوم','2','632',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1329,'على جهة، فنقل كل قوم [على] ما علموا. وكذلك هذا في قراءة القرآن، وقد أرشد عمر - رضي الله عنه - إلى ذلك في حديث [هشام بن حكيم بن حزام]، حيث سمعه يقرأ سورة (الفرقان)، فاختلفا وجاءا إلى رسول الله - صلى الله عليه وسلم - فقرآ جميعا، فقال: ([كلكم] قد أصاب)، هذا معنى الحديث دون لفظة. والمقصود أن الاختلاف ينزل على أنه [علم] الجهات كلها.
وأما فتح مكة، فالصحيح عند العلماء أنه عنوة. وقد جاء: (أن النبي','2','633',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1330,'- صلى الله عليه وسلم - لما دنا من مكة [خشي العباس] أن يدخل النبي - صلى الله عليه وسلم - مكة فيهلك قريشا، فخرج في الليل يطلب أحدا يخبر أهل مكة، فوجد أبا سفيان بن حرب في جوف الليل). وفي الحديث قصة طويلة. إلا أن المقصود منها أن أبا سفيان أخذ من رسول الله - صلى الله عليه وسلم -[أمانا] لبعض الناس، فقال - عليه السلام -: (من دخل دار أي سفيان، فهو آمن، ومن ألقى سلاحه، فهو آمن، ومن تعلق بأستار الكعبة، فهو آمن، ومن [أغلق] بابه، فهو آمن). فأقبل الناس إلى بيوتهم وإلى','2','634',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1331,'المسجد، وإلى دار أبي سفيان. (ودخل رسول الله - صلى الله عليه وسلم - وعلى رأسه المغفر). وكذلك أصحابه مسلحون، وقال - عليه السلام -: ([إن] هذا البلد لم يحل لأحد قبلي، ولا يحل لأحد بعدي، وإنما أحلت لي ساعة من [نهار]).
فهذه أحاديث تدل دلالة واضحة على [أنه] دخلها عنوة. وإنما وقعت الشبهة لبعض الناس من جهة أن النبي - صلى الله عليه وسلم - ودى قوما [قتلهم خالد]. وهذه شبهة، [إلا] أن [تزال] بالنظر، [إذ يمكن] أن يكون ذلك لنهي خاص، وقضية معينة.
قال الإمام: (ومما [يذكر من] أقسام الكذب، أن يتنبأ متنبئ من غير','2','635',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1332,'معجزة) إلى قوله (فلينعم المنتهي إلى هذا الفصل نظره، وليتدبر غائلته). قال الشيخ: هذه المسألة عظيمة الموقع، شديدة الغموض. وقد اختلف الناس في خرق العادة كرامةً للأولياء، فذهبت المعتزلة إلى منع ذلك، مصيرا منهم إلى أنها لو انخرقت للأولياء، لم يتوصل إلى معرفة صدق [الرسل]، [إذ] يقع الخارق للنبي وغيره. والأستاذ أبو إسحاق يميل إلى قريب (١٤٣/أ) [من] مذاهبهم.','2','636',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1333,'وأهل (١٢٨/أ) السنة متفقون على جواز [خرق العادة للأولياء عقلا، وأن ذلك واقع سمعا، والآيات والأخبار الصحاح في ذلك] كثيرة. ففي كتاب الله - عز وجل -: {قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك}. وفيه: {وهزي إليك بجذع النخلة تساقط عليك [رطبًا جنيًا]}. وفيه: {كلما دخل عليها زكريا المحراب وجد عندها رزقًا}. وفيه قصة أصحاب الكهف. وفيه [قصة] صاحب يوسف. وفيه كلام عيسى - عليه السلام - في المهد قبل أن يبعثه الله، وقد قيل: إنه تكلم [براءة لمريم]، ثم عاد إلى السكوت، إلى حين كلام الصبيان في الاعتياد. والأخبار عن رسول الله - صلى الله عليه وسلم - صحيحة في','2','637',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1334,'حديث (جريج). وكلام الصغير، وكلام البقرة، وأصحاب الغار الذين دعوا فارتفعت الصخرة، وأصحاب الأخدود، وخرق العادات في زمان بني إسرائيل [كثيرة]. وما جرى لصالحي هذه الأمة أكثر من أن يحصى. وفي حديث أبي بكر أنه قال لعائشة في مرض موته بعد [قصة]: (إنما هما [أخواك] وأختاك. قالت: يا أبت إنما هي أسماء فمن الأخرى؟ قال:','2','638',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1335,'ذو بطن بنت خارجة، أراها جارية). [فكان] كذلك.
وقول عمر: (يا سارية الجبل). ومكاتبته للنيل من أعجب العجائب.
والنقل متواتر ضرورة عن صالحي هذه الأمة [لخوارق] العادات. فإن قيل: فهل تخصصون الخوارق بجنس دون جنس؟ قلنا: لا تخصيص بجنس،','2','639',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1336,'بل يصح أن تنخرق في الجميع، وما جاز أن يقع معجزة لنبي، جاز أن يكون كرامة لولي، كإحياء الموتى وغيره.
فإن قيل: [فبماذا] يقع الفرق بين الكرامة والمعجزة؟ قلنا: بالتحدي، ودعوى النبوة خاصة، وبهذا [تفارق] السحر. وقد ظن بعض الناس أن الفرق يرجع إلى تحدي [النبي] دون [الولي]، فإن [الكرامات] تجري على الولي، وهو غير [مريد] لها. وليس بصحيح، [وما] نقلناه من الأحاديث والحكايات المنقولة عن صالحي هذه الأمة يقضي على ذلك، فإنهم كانوا يطلبون، ويفعل الله لهم ذلك، مع أن العقل لا يحيله.','2','640',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1337,'فإن قيل: فكيف يصح مع هذا أن يعم القول بأن [ما] خالف العادة من الأخبار، يقطع بكذبه؟ قلنا: الصحيح عندنا منع هذا الإطلاق، وإنما يكون ذلك في خوارق يعلم أنها ليست من قبيل الكرامات، كدخول ملكٍ صعقا، أو مقتلة عظيمة، أو وقوع زلزلة في المسجد، وكذلك خفاء معارضة القرآن، وكتمان النص على إمام. فهذه أمور يعلم أنها ليست من قبيل الكرامات. فأما إذا أمكن أن يكون المخبر عنه من قبيل الكرامات، فلا سبيل إلى القطع بتكذيب المخبر على العموم، بل نقول فيه ما قلناه في المعجزة، حيث جوزنا صدورها على أيدي الكذابين. فقيل [لنا]: فتوقفوا عن الصدق [عند] ظهورها. قلنا: إذا وجدنا من أنفسنا العلم الضروري بصدق (١٢٨/ب) من ظهرت على يديه، علمنا أن العادة مطردة. ولو قلب الله سبحانه','2','641',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1338,'[العادات]، لسلب العقل مذاق العلم، ولا نسلت العلوم [من] الصدور. فكذلك إذا أخبرنا عن تقلع جبل، [لم نعمم القول] بأن المخبر (١٤٣/ب) كاذب، بل نراعي [أنفسنا]، فإن وجدنا العلم الضروري يكذبه، علمنا أن الله تعالى لم يخرق العادة، وإن لم نجد العلم الضروري، توقفنا عن التكذيب.
وأكثر ما ينقل من الكرامات لا [يجد] العاقل [من نفسه قطعا] بكذب المخبر. هذا هو القول في ذلك. والله المستعان، [وهو حسبنا ونعم الوكيل].
قال الإمام: (مسألة: ما ذهب إليه علماء الشريعة) إلى قوله (فهذا هو المعتمد في إثبات العمل [عند خبر] الواحد). قال الشيخ: المذاهب','2','642',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1339,'على حسب [ما ذكره] الإمام. [إلا أنه أغفل ذكر مذهب، وهو أن قوما أوجبوا العمل بخبر الواحد عقلا. واستدلوا عليه: بأن المجتهد إذا] لم','2','643',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1340,'يصادف دليلا قاطعا، وصادف خبر الواحد، فلو لم يعمل به، لتعطلت الوقائع عن أحكام الله [تعالى]. وأيضا فإن الرسول مأمور بتبليغ الأحكام إلى كافة الخلق، ولا يتصور ذلك إلا بأخبار الآحاد، فإنه لو [تكلف] أن يرسل إلى كل قطر عدد التواتر، لم يف أصحابه ببعض [الأقاليم]، [ولتعطلت] دار هجرته، ولخلت من أصحابه وأنصاره، ولتوصل إليه أعداؤه، فوجب لذلك الاكتفاء بخبر الواحد.
وقالوا أيضًا: الصدق ممكن، فيجب العمل، لإمكان مصادفة حكم الله','2','644',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1341,'تعالى. هذا كلام هؤلاء القوم، وإنما ذكرنا شبههم، لأن الإمام أسقط هذا المذهب.
وأما قولهم: إن المجتهد إذا لم يصادف دليلا قاطعا، [وصادف] خبر الواحد، فلو لم يقض به، [لتعطل] حكم الله تعالى. فهذا غير صحيح، فإن العقل لا يقضي بصدق [خبر] الواحد، حتى يكون المجتهد معطلا للحكم، [فقد يقابل] هذا بأنه [إذا لم] يعلم الصدق، فلو حكم به، [لخشي] أن يكون حاكما بغير حكم الله تعالى. وبهذا تمسك من أحال العمل بخبر','2','645',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1342,'الواحد. ويصلح أن يحرم علينا أن نعمل بظنون، فكيف توجب ذلك العقول؟
وأما قولهم: إنه [مكلف] بتبليغ الأحكام كافة الخلق. فالعقل لا يحتم ذلك. ولله تعالى أن يكلفه تبليغ من أمكنه مشافهته، [أو يرسل] [إلى] عدد التواتر. ولو سلمنا أنه كلف ذلك، فيصح أن لا [ينقله] عن [البراءة] الأصلية إلا بدليل قاطع، فإذا لم يصادف ذلك، بقي المكلف على الأصل.
وأما قولهم: إذا أمكن الصدق، وجب العمل، فهذا من [أفسد]','2','646',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1343,'شيء، [ويقابل] على [الفور] بقول من يقول: إذا أمكن الكذب، حرم العمل، ثم كان يلزم منه العمل [بخبر] الكافر والفاسق، فإن الصدق ممكن، وكذلك الحكم بقول المجرح، إذ الإمكان في الجميع [ثابت].
قال الإمام: (وأما الرد على من [زعم] أن تكليف العمل بخبر الواحد [مستحيل] في العقل) إلى قوله (استدلالا [وسؤالا وانفصالا]). قال الشيخ: هذا الذي ذكره الإمام (١٢٩/أ) هو [الصحيح] في المسألة. وقد','2','647',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1344,'سلك غيره من الأصوليين مسلكين [آخرين] بعد هذين المسلكين، [ونحن نذكرهما] ونبين وجه الاستدلال [بهما]، [ونعترض] على ما يتوجه عليه الاعتراض منهما.
الأول: أنهم قالوا: [العامي] بالإجماع مأمور باتباع قول المفتي، ويجب عليه ذلك، وهو ربما أفتى عن ظنه، فالذي يخبر عن السماع المحقق أولى. والكذب والغلط [جائز] على المفتي [كالراوي]، بل الغلط على','2','648',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1345,'الراوي أبعد، فإن المجتهد إنما يكون مصيبًا-[عند] من صوبه- إذا [بذل] المجهود في طلب الحكم، ولم [يقصر]، ومواضع الظنون [هي] مواضع الغلط (١٤٤/أ) غالبًا، [بخلاف] ما يستند إليه الحواس.
ويقوى الإلزام [على] مذهب من يجوز [تقليد] الناقل للمذهب، فإنه ينقل قول غيره، ويلزم [العامي] المصير إليه، فلم [لا] يروي قول غيره؟ هذا طريق. ولكنه غير صحيح عندنا، فإن خبر الواحد أصل','2','649',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1346,'[يجب] العمل عنده قطعًا، [ومنكر] العمل به مخطيء آثم، فلا يصح أن يستند إلى القياس.
وقد أجابوا عن ذلك: بأن هذا قياس معلوم، [والقياس] المعلوم يحصل علمًا بالحكم، فكذلك هذا. وهذا غلط بين، والقياس الذي يترتب [العلم] عليه، إنما يجري في جزيئات النوع التي علم من [الشرع] أنها تجري في الحكم عنده على قضية واحدة، كتسوية العتق، والبول في الماء الدائم، وتشطير الحد على الذكر، اعتبارا بالأنثى، [للقطع] [بأن]','2','650',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1347,'الشرع سلك بهذه المسائل مسلكا واحدا، ولولا ذلك، لم يثبت حكم بالقياس. وقد قال القاضي وغيره: إنه لا يجوز إثبات الإجماع بخبر الواحد، قياسا على [نقل] السنة، لأنا لم نتعبد بالقياس في أصول الشريعة. وإن كان نقل حجة في الموضعين، فأين فتوى [المفتي] من نقل الراوي؟ على أن [العامي] لا يتصور أن يكون له طريق إلى معرفة الحكم إلى [الاستفتاء]. هذا هو الصحيح [عندنا]، فإنا لم نؤمر بإثبات أصول الشريعة بالقياس.
ومما تمسكوا به أيضًا قوله تعالى: {فلولا نفر من كل فرقة ... }.','2','651',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1348,'[الآية]. قالوا: والطائفة عدد قليل، لاسيما إذا كانوا بعض الفرقة، فلا يحصل العلم بقولهم. وهذا ضعيف لوجهين:
أحدهما- أنه ليس فيه إلا الإنذار، [والإنذار قد يكون بالإخبار، وقد يكون بالفتوى، فلعل المراد أراد الإنذار] بالفتوى والإرشاد دون الرواية والإخبار.
وأيضًا فإنه [لو قدرنا أنه] أوجب الإنذار، أو أنه [أوجب العمل بالفتوى]، [إلا أن] الكلام في وجوب القبول عند انفراد المخبر، هذا','2','652',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1349,'ليس في القرآن تصريح [به]، كيف ويجب على الشاهد الواحد أن يشهد، وإن لم يجز الحكم بشهادته، لكن [الاحتمال] أن يشهد غيره؟ هذا تمام الكلام على أصل الاستدلال.
وقد اعترض على الطريق الأول، وهو التواتر، بأنهم إنما كانوا يبعثون حكاما ومفتين، لا رواة ومخبرين، ونحن نسلم أن فتوى المفتي واجب المصير إليها. قلنا: ليس الأمر كذلك، وقد أمر رسول الله - صلى الله عليه وسلم - معاذًا وغيره أن يخبروا الناس بما أوجب الله عليهم. وكان يكتب كتب [الصدقة والعقود]، وقال: (نضر الله امرءًا سمع مقالتي). وقال في خطبة الوداع: (فليبلغ الشاهد','2','653',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1350,'الغائب). ولو ذهبنا ننقل الأخبار التي نقلها الصحابة للناس، لطال الكلام، وجاوز حد الاقتصاد.
وقد اعترض على هذا بالممانعة فيه، عن كل واحد على انفراده، على ما سيأتي.
قال الإمام: (وقد [استدلوا] بظاهر قوله تعالى: {ولا تقف ما ليس لك','2','654',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1351,'به علم}). إلى آخر المسألة. قال الشيخ: ولا زائد على ما قاله الإمام في هذا المكان.
قال الإمام: (مسألة: ذهبت الحشوية من الحنابلة) إلى قوله (وقد','2','655',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1352,'تكلمنا عليه بما فيه مقنع).
قال الشيخ: الأمر على ما ذكره الإمام، والعادة بذلك شاهدة، والضرورة بعده حاصلة. وقد قال بعضهم: يورث العلم الظاهر. والعلم ليس فيه ظاهر ولا باطن، أو لعله يسمي الظن علما. وقد تردد الناس في هذا، هل يسمى الظن علمًا؟ وقد قال زهير:','2','656',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1353,'وأعلم علمًا ليس بالظن [إنه] ... .... ....
قالوا: فلو لم يكن الظن يسمى علمًا، لم يفتقر إلى أن يقول (ليس بالظن). وقيل: هذا على جهة التأكيد، لا لأجل الانقسام. أما العلم فيسمى ظنًا من غير خلاف. (١٤٤/ب) قال تعالى: {وظنوا أن لا ملجأ من الله إلا إليه}. وقال تعالى: {الذين يظنون أنهم ملاقوا ربهم}. والموت معلوم غير مظنون.
وربما استدلوا بقوله تعالى: {فإن علمتموهن مؤمنات}. ولا يعلم إيمانهن إلا بقولهن. قالوا: فدل أن قول الواحد يحصل علمًا. وهذا ضعيف، لأنه يحتمل أمرين:','2','657',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1354,'إما أن يكون يسمى إيمان اللسان إيمانًا، فإنه يصح أن يسمى الشيء باسم ما هو دليل عليه. أو يكون المراد: فإن علمتم نطقهن بالشهادة. وهذا معلوم لا شك فيه، وهو الذي علق الشرع الأحكام عليه، ولهذا قال لأسامة في قتله ذلك الذي تلفظ بكلمة الشهادة، قال - عليه السلام -: (أقتلته بعد أن قال لا إله إلا الله (؟ ولا معنى للتعلق بالمجملات في مدافعة الضرورات.
قال الإمام: (مسألة: ذهب الجبائي إلى أن خبر الواحد لا يقبل) إلى قوله (وآخر في كيفية الرواة). قال الشيخ: ينبغي أن يفهم مراد الأصوليين بخبر الواحد: وهو عندهم عبارة عما لم يحصل علمًا، وإن كثر رواته من خمسة وستة وغيرها، وخبر الرسول لما دلت المعجزة على صدقه، لا يسمى خبر الواحد.','2','658',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1355,'وهذا هو الذي دللنا على وجوب العمل عنده بالأدلة السابقة.
والمقصود أن العدد في الرواة غير مشترط، على حسب ما قررناه من المتواتر عن الرسول - صلى الله عليه وسلم -، والنقل عن أهل الإجماع.
وقد اعترض الجبائي على الأصلين جميعًا، فقال: أما الرسول - صلى الله عليه وسلم - فلم يقبل خبر ذي اليدين حتى سأل أبا بكر وعمر فصدقاه. ولم يقبل أبو بكر خبر المغيرة حتى روى معه محمد بن مسلمة. ولم يقبل أبو بكر وعمر حديث','2','659',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1356,'عثمان في رد [الحكم بن أبي العاص]، وكان عثمان مشهور بالفضل والدين. ولم يقبل عمر حديث أبي موسى في الاستئذان، حتى روى معه أبو سعيد.','2','660',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1357,'ولم يقبل عمر حديث فاطمة بنت قيس في السكنى. ولم يقبل علي حديث أبي سنان الأشجعي في حديث بروع بنت واشق. ومن ذلك رد عائشة','2','661',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1358,'حديث ابن عمر الذي رواه: (إن الميت ليعذب ببكاء أهله عليه). ونظائر ذلك مما يكثر. وطريق الجواب من ثلاثة أوجه:
أحدها- أن من قبل خبر الواحد لا يعمم القبول، ومن رده عمم الرد. ونحن قد نقلنا الأدلة القاطعة في قبول خبر الواحد من غير شرط زائد على العدالة. فإذا وجهت هذه الأسئلة، لم يلزم الجواب عنها، فإنا لا نعمم القبول في كل موضع.
الثاني: أن هذه الأقاصيص جرت نادرة على جهات خاصة، فيحتمل أن يطلع المتوقفون أو الرادون على أسباب اقتضت ذلك، لأن هؤلاء القوم الذين','2','662',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1359,'توقفوا، هم بأعيانهم قبلوا قول الأحاد، ورتبوا عليها الأحكام. فلا تنزل أمورهم على انتقاض. هذا أمر يكتفى به في الجواب، ثم إلينا الخيرة إن أحببنا اقتصرنا على ذلك، وإن شئنا تعرضنا للتفصيل، وهو أحسن الأجوبة.
أما كون رسول الله - صلى الله عليه وسلم - توقف في خبر ذي اليدين، فقد ذكر الأصوليون له ثلاثة أوجه:
أحدها- أنه جوز الغلط عليه، لكثرة الجمع، وانفراده بالإخبار عن الجماعة، وإذا ظهرت أمارات الوهم، لم يجز العمل.
الثاني- أنه قال قولا- لو صدق فيه- لاعتقد أنه لابد من تصديق الراوي عند كثرة الجمع، وسكوت الباقين من غير بحث ولا سؤال، فيصير التصديق مع (١٤٥/أ) السكوت سنة ماضية، فحسم سبيل ذلك.
الثالث: أنه قال قولا- لو صدق فيه- لاشتغلت ذمة القوم بالصلاة وهم يسمعون، فيصير ذلك من أبواب الشهادة، فيشترط فيه العدد. وهذه الأجوبة عندي ضعيفة.','2','663',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1360,'أما الأول: فلا يلزم من رواية عدل في جمع مع سكوتهم أن تحصل ريبة ولا توقف.
وأما الثاني: وهو أن خشي أن يصير التصديق مع السكوت سنة، فنقول: لا التفات إلى السكوت، وتصديق العدل لازم.
وأما التحاق المسألة بأبواب الشهادات، فهذا غير صحيح، بل الأخبار المنقولة عن الرسول - صلى الله عليه وسلم - يكتفى بنقل الواحد فيها، وإن روى لنفسه. وقد روى جابر بن عبد الله أن النبي - صلى الله عليه وسلم - قال: (لو جاء مال البحرين، لأعطيتك هكذا وهكذا). فلما توفى رسول الله - صلى الله عليه وسلم - واستخلف أبو بكر - رضي الله عنه - قال: (من كان له عند رسول الله - صلى الله عليه وسلم - شيء فليأتني. فقام جابر فقال: (قال لي رسول الله - صلى الله عليه وسلم -. فقال أبو بكر: (خذ)، فأخذ بيديه، ثم قال له: عدها، فعدها فكانت خمسمائة، فقال له: (خذ مثليها).
لكن المعتمد الصحيح عندي أنه أسند القضية إليه، وأخبره [بأنه] لم يكمل صلاته، فكان النبي - صلى الله عليه وسلم - على اعتقاد التكميل أو ظنه، فلم يحرك له قول ذي اليدين ظنا، وإنما يعمل بخبر الواحد عند ظن الصدق. ألا ترى أن النبي - صلى الله عليه وسلم - قال له: (كل ذلك لم يكن). فهذا هو السبب الذي منعه من قبول قوله ابتداء.','2','664',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1361,'فإن قيل: فلم عمل به انتهاء؟ قلنا: لما رآه مصمما على الإخبار، أثار ذلك شكا للرسول - صلى الله عليه وسلم -، فلما سأل القوم فصدقوه، لم يسع إلا الإتمام بعد ذلك.
وأما رد حديث عثمان في حق [الحكم بن أبي العاص]، فقد ذكر بعض الأصوليين فيه أوجها ضعيفة عندنا:
منها- أنهم قالوا: إنه خبر عن إثبات حق لشخص، فهو كالشهادة لا يثبت إلا بقول شاهدين [أو توقف]، [لأن] عثمان كان قريبا للحكم، وقد كان معروفا بأنه كلف [بأقاربه]، تنزيها لعرضه ومنصبه من أن [يقال] متعنت. وإنما قال ذلك لقرابته، حتى يثبت ذلك بقول غيره. أو توقفا','2','665',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1362,'[ليبينا] للناس التوقف في حق القريب الملاطف، [ليتعلم] [منهما] التثبت في مثله. وهذه الأوجه ضعيفة عندنا.
أما انتظار العدد، لكون الحق يتعلق بمعين، فقد تكلمنا عليه قبل هذا. وقلنا: إنه في الأخبار غير مانع.
وأما التوقف، لأنه حق يتعلق بالقريب، فهذا باطل، فإن الشهادة مع ضيق بابها، وكثرة التعبد فيها، لا تمنع من قبول شهادته، فكيف إذا روى خبرا يتعلق بحقه؟
وأما الرد صيانة لعرضه، فهذا بالضد، فإنهما إذا رداه لذلك، كان ذلك أعظم تهمة وتسليط الناس على عرضه على الحقيقة، فهذا سعي في صيانة العرض بهتكه. ولكن الصحيح عندنا في ذلك أن نفي الحكم من المدينة كان مشهورًا معلومًا، علمه الخاص والعام، وما علم من الأحكام لم ينسخ بقول الآحاد، ورواية عثمان لا تثير لهما إلا ظنًا، فلم يرجعا عما [علماه] بأخبار الواحد. ولما ولي عثمان رده، لأن الحكم بالرد من الرسول - عليه السلام - كان معلومًا','2','666',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1363,'عنده، فلم يرفع القاطع إلا بقاطع.
وأما رد [عمر] حديث أبي موسى، فيمكن أن يكون السبب في ذلك (١٤٥/ب) أنه لما رجع عن باب عمر كالمترفع عن الوقوف، وعز ذلك على عمر، وخشي أبو موسى من عقابه، فقد تنشأ تهمة من هذه الجهة، أو يكون توقف، لا لتردد، ولكن لطلب زيادة، ولهذا قال: (إني لم أتهمك، ولكن خشيت أن يتقول الناس على رسول الله - صلى الله عليه وسلم -).
والظاهر عندي أن عمر شك في الصدق، ويصح التوقف إذا لم يحصل للسامع ظن الصدق عند رواية الخبر، ولهذا قال لما شهد له أبو سعيد الخدري: (سبحان الله، خفي علي هذا من أمر رسول الله - صلى الله عليه وسلم -، شغلني عنه الصفق بالأسواق).','2','667',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1364,'وأما رد علي حديث أبي سنان الأشجعي، فقد ذكر علته، وقال: (كيف [نقبل] قول أعرابي بوال على عقبه). يشير إلى أنه ليس من أهل الرواية.
وأما رد عمر حديث فاطمة بنت قيس، فإنما أراد رده لعارض، فقال: (لا ندع كتاب ربنا وسنة نبينا لقول امرأة، لا ندري أحفظت أم نسيت (؟
وأما رد عائشة حديث ابن عمر، فإنها قال: (السمع يخطئ، وإنما قال رسول الله - صلى الله عليه وسلم -: (إنهم ليبكون عليه، وإنه ليعذب في قبره، يعني يهوديا). وقالت: (اقرأوا إن شئتم: {ولا تزر وازرة وزر أخرى}).
فهذا سبيل الكلام على ما ينقل من الأخبار، والأصل المرجوع إليه، إجماعهم على قبول الأخبار من حيث الجملة، والتعميم غير لازم، والرد في بعض الأحوال له أسباب. والتباس أحوال الوقائع ينزل منزلة الإجمال.

قال الإمام <span data-type="title" id=toc-226>(فصل- في صفة الرواة: العقل والإسلام، والعدالة معتبرة، وأصحاب أبي حنيفة- وإن قبلوا شهادة الفاسق</span>-) إلى قوله (والذي نراه القطع','2','668',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1365,'بالرد، كما تقدم [ذكره]). قال الشيخ: القاعدة التي بنيت الأخبار عليها: أنها ليس فيها تعبدات حكمية، كما في الشهادة، وإنما مدارها على غلبة الظن، فكل ما يخل بغلبة الظن، فإنه مانع، وما لا يخل بوجه، فإنه لا يمنع. وربما يختلف العلماء في أمور: هل تخل بغلبة الظن أم لا؟ فيرد كل مجتهد إلى ما غلب على ظنه. هذا فيما جاوز محل الإجماع.
والأمر المجمع عليه أنه لو حصلت غلبة الظن بقول الكافر، لم يعتمد على ذلك. وكذلك لا التفات إلى غلبة الظن التي تحصل عن قول الفاسق الذي يعلم فسق نفسه. فهذان الموضعان مما أجمع الناس عليهما.
فمما اختلف الناس فيه: الصبي، فذهب الأكثرون إلى أنه يمنع القبول، ثم ترددوا، هل ذلك مظنون، أو مقطوع به؟ فالذي عليه الأكثرون أن ذلك','2','669',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1366,'مظنون، والكلام مفروض في صبي مميز يدري ما يشهد به، مع ما عرف منه من صدق وصلاح.
[وهل] يلتفت إلى خصوصية الحال، [أو يلتفت] إلى غالب أحوال الصبيان؟ فالأكثرون على الإعراض عن الخصوصية، والالتفات إلى الأمر الكلي. وهذا لعمري هو الصواب عندنا، فإن الحكم يثبت بنص، أو قياس على منصوص، والنص والإجماع حاصلان في البالغ، والصبي بعيد التحاقه بالبالغ والتفصيل على حسب ما ذكره القاضي.
فلئن قيل: يقبل قياسًا على البالغ. قلنا: إذا أمكن الفرق، وظهر بين الفرع والأصل، (١٤٦/أ) امتنع الإلحاق، ولا يلزم من قبول الشرع قول من حرم عليه الكذب، ووعده عليه بالنار، أن يقبل قول من ليس الكذب عليه حراما.
فإن قيل: قد جرت من عادته أنه لا يكذب، والمقصود غلبة ظن الصدق. قلنا: لا التفات إلى الامتناع من الكذب من غير خوف المعصية، فإنا لو جربنا من فاسق أنه لا يكذب، تكرما وتمسكا بمكارم الأخلاق، لم تقبل روايته، إذا','2','670',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1367,'لم يكن الوازع عن الكذب خوف الله تعالى، والصبي بذلك أولى.
وأما ما ذكره الإمام في الرد، وادعى فيه القطع من أن المعتمد في القبول إرسال الرسل، ولم يرسل قط صبيا رسولا. فهذا الكلام ضعيف، فإنه كما لم يرسل الصبيان، فكذلك لم يرسل العامة من أصحابه، وإنما أرسل الخواص والعلماء، فعلى هذا الطريق ينبغي أن لا تقبل إلا رواية هذا الصنف من الناس، وليس الأمر كذلك.
وأما قوله: (إن أهل الإجماع ما راجعوا الصبيان). فهذا أيضًا لا يدل على الرد على القطع، وقد لا يحتاجون إلى مراجعتهم، وهذا هو الصحيح، وذلك أن أقوال رسول الله - صلى الله عليه وسلم - كانت في العلانية تارة، وفي الخلوة أخرى، فأما','2','671',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1368,'العلانية، فمنكشفة للناس، وأما ما كان منها في الخلوة، فقد نقلها أزواجه رضوان الله عليهن.
وأنس [- رضي الله عنه -] كان يخدم رسول الله - صلى الله عليه وسلم -، [وقد] روى عنه ما أدركه في الصغر والكبر جميعا. وكذلك محمود بن [الربيع]، روى أنه عقل مجة','2','672',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1369,'مجها رسول - صلى الله عليه وسلم - في وجهه، وهو ابن سبع سنين. وليس يحصل من هذا دليل على الرد، لا قطيعا ولا ظنيا.
وأما قوله: (إذا لم يوجد [قطع] بالرد). فقد تكلمنا عليه، ونزيده تقريرا ههنا فنقول: إذا لم يوجد دليل قاطع على القبول، قطع بالرد، إنما يكون هذا بشرط إقامة برهان على أن المسألة يطلب فيها العلم، وليس يسلم أن تفاصيل أخبار الآحاد يشترط العلم فيها، بل نقول: لما استقرئت الواقعة، وعرفت القاعدة، وجرت تفاصيلها على غلبات الظنون، تبين العلماء من هذا أن المراد حصول غلبة الظن من قول الراوي، فإذا حصل ذلك، وكانت المسألة ليس مجمع على ردها، وجب القبول.
وهذا نطرده في رواية المستور والمبتدع المتأول، وغير ذلك من تفاصيل الأنواع. وسنقرر هذا في أبواب المراسيل، إن شاء الله. ورد الشرع أقاريره،','2','673',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1370,'يدل على ما قلناه من أنه لا تثق النفس بقوله. فأما لو ثبت أنه أتلف مال غيره، لوجب عليه غرمه، وقوله لا يحصل ذلك منه.
فلئن قيل: ماله مصان عن قوله. قلنا: أما بعد ثبوت الإتلاف، فلا صيانة، فما بال الإتلاف لا يحصل بقوله؟ ولكن هذا منقوض بالسفيه، فإنه لا يقبل إقراره على نفسه، وتقبل روايته وشهادته. قلنا: اختلف الناس في قبول شهادة السفيه. فمن ردها، فقد اطرد عنده الباب، ومن قبلها، فإنه يفرق بين قبول شهادته، وقبول شهادة الصبي، للخوف الذي عند أحدهما، وفقدانه في حق الآخر. وهذا إذا كان سفيها من غير فسق، فأما مع الفسق، فلا سبيل إلى القبول بحال.
فإن قيل: فإذا اخترتم رد رواية الصبي، فالشهادة بابها أضيق، (١٤٦/ب) وشروطها أكثر، وقد تقبل رواية من لا تقبل شهادته، لاشتمال الشهادات على جهات من التعبدات، وقد قبلتم شهادة الصبيان فيما بينهم، فما وجه ذلك؟ قلنا: قد نقل قبولها عن بعض الصحابة والتابعين، لكن فيما بينهم على الخصوص، فلا [تقبل لصغير] على كبير، ولا لكبير على صغير، ولا حيث يحضر كبير. وإنما ذلك للضرورة، ولكثرة الجراح فيما بينهم، ولتعذر حضور','2','674',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1371,'من تقبل شهادته، بشرط أيضًا أن لا يفترقوا ولا يخببوا، فلأجل الضرورة، قضى بها من قضى.
ولا ينبغي أن تجعل مظان الضرورات أصولا ترجع إليها الأحكام الكلية. وقد اختلف في قبول شهادتهم في القتل في شهادة الإناث. وتفاصيل هذه المسائل ليس من فن الأصول.

قال الإمام: <span data-type="title" id=toc-231>(مسألة: في رواية المستور) </span>إلى قوله (وانقلبت الإباحة [كراهة]). قال الشيخ: الناس متفقون على أن الفاسق مردود الشهادة والرواية، وقد قال الله تعالى: {إن جاءكم فاسق بنبإٍ فتبينوا أن تصيبوا قومًا بجهالة}. وقال عمر - رضي الله عنه -: (لا يؤسر رجل في الإسلام بغير العدول).','2','675',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1372,'والعدالة: عبارة عن حالة راسخة في القلب تحمل على ملازمة التقوى والمروءة جميعا. وهذا أمر باطن لا يتأتى الوقوف عليه يقينا، وإنما يستدل عليه بأحواله وأفعاله.
فقال أبو حنيفة: إذا لم يأت الاطلاع على ذلك يقينا، وإنما يستدل عليه بالأحوال الظاهرة، فالإسلام يدل على ذلك دلالة ظاهرة، مع عدم الاطلاع على فسق ظاهر، فإنه يدل على أصل الخوف، إذ لولاه لم يكن مسلما. ونهاية الخوف الحاجز عن كل المعاصي لا يشترط.
فإذا تقرر اعتبار العصمة، ولم [يمكن] اجتناب الكبائر، وتعذر الوقوف على الحالة الباطنة، وجب الضبط بأصل الإسلام، مع عدم الفسق الظاهر. قالوا: ويشهد [لهذا] أن الصحابة رضوان الله عليهم قبلوا شهادة المسلمين مطلقا، إلا من عرفوه بفسق، كالخوارج وغيرهم.
(ولما شهد الأعرابي برؤية الهلال، أمر رسول الله - صلى الله عليه وسلم - بالصيام). ولم يعلم منه سوى الإسلام. هذا تقرير كلامهم.','2','676',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1373,'وربما تمسكوا بصحة الاستفتاء عند ظهور العلم والإسلام، وإن لم تعلم العدالة. هذا تمام كلام القوم. ونحن نقول: إذا ثبت رد رواية الفاسق، والمستور متردد العدالة والفسق، فلا ينبغي أن تقبل روايته بالشك في حصول الشرط.
وأما ما ذكروه من كون الإسلام مظنة. فلسنا نسلم ذلك، وأن العدالة غالبة على المسلمين، بل إما أن نقول: في الأمر انقسام، وإما أن نقول: ليس أكثر المسلمين عدولًا. والشك يكفي في التردد، لاسيما إذا كانت الكثرة في الشق الآخر.
وأما كون الصحابة لم يبحثوا عن أسباب العدالة، فإنما لم يفعلوا ذلك في أصحاب رسول الله - صلى الله عليه وسلم - وأزواجه، وكانت عدالتهم معروفة عندهم، وحيث جهلوا ردوا، كقول علي كرم الله وجهه: (كيف نقبل قول أعرابي بوال على عقبيه).','2','677',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1374,'وأما قبول رسول الله - صلى الله عليه وسلم - قول الأعرابي في رؤية الهلال، وكونه أعرابيًا، لا يمنع أن تكون عدالته معروفة عنده، إما بوحي أو إخبار. وقد كنا قلنا: إن مدار الرواية على غلبة الظن. ولا تحصل غلبة الظن (١٤٧/أ) من قول المستور. بل حصول غلبة الظن [بقول فاسق] جرب باجتناب الكذب، أقرب من غلبة الظن بقول المستور.
وأما جواز الاستفتاء، بناء على مجرد الإسلام وظهور العلم، ففيه منع، فلا يجوز الاستفتاء إلا بعد ظهور العدالة أيضا. وإن سلمنا، فذلك لأن الغالب من أحوال العلماء- لاسيما بعد بلوغ درجة الاجتهاد- العدالة، فالعلماء عدول إلا القليل، ولا يقال المسلمون عدول إلا القليل. فهذا هو الفرق بين البابين.
وتمسكوا أيضا ببناء الشرع أحكاما على مجرد الإسلام والستر، كما يقبل قول البائع: إن الجارية ملكه، حتى يجوز الشراء والوطء. وكذلك يقبل قول بائع اللحم: إنه لحم ذكية، وأن الماء طاهر. وكذلك إمام الصلاة يصح أن يقتدى به، بناء على أنه متطهر، وإن لم يظهر منه سوى الإسلام.
قلنا: أما البائع فيصح الاعتماد على قوله في كونه مالكًا، مع ظهور','2','678',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1375,'الفسق، بل مع الكفر، وذلك للضرورة الداعية إلى ذلك. فلو كلف الخلق أن لا يشتروا إلا بعد البينة بأن الشيء ملك زيد، لتعذر ذلك، وغاية ما يقدر عليه إقامة البينة على الملك سابقًا، فأما في الحال، فلا يقدر على ذلك على حال، فلابد من الرجوع إلى قول صاحب اليد، فهذه ضرورة، لا معدل عنها.
وأما قول إمام الصلاة: إنه متطهر، فهذا لعمري يفتقر فيه إلى سكون النفس بقوله، وإذا لم تسكن النفس، فلا يقتدى به. وما بين العبد وبين الله تعالى يجوز أن يرد فيه إلى غالب ظنه. وأما الرواية والشهادة، فأمرهما أعظم، وخطرهما أشد، فلابد من تحقيق الأسباب في ذلك.
قالوا: لو أسلم كافر فروى في الحال أو شهد، إن لم تقبلوا، فهو بعيد، فإن هذا مطهر من كل الذنوب، (والإسلام يجب ما قبله)، فإن قبلتم، فليس عندكم ما تعتمدون عليه من أحواله سوى الإسلام. قلنا: قد اختلف الناس في ذلك، والصواب عندنا التوقف حتى تعرف حاله، فإنه قد يسلم الكافر الكذاب، ولا يبقى على كذبه، فلا بد من بحث زائد على حاله. مع حدوث إسلامه. وإن جوزنا القبول، فذلك لأجل بدايته، وشتان بين من هو في طراوة البداية، وبين من استمر زمانه، وكثرت أيامه. وهذا معروف بالتجارب والعوائد. هذا تمام الكلام. وأما قطع الإمام بالرد، ففيه ضعف.','2','679',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1376,'وقوله: (إنهم كانوا يردون رواية المجان والفسقة وأصحاب الخلاعة). هذه المسائل ليست محل نزاع، وإنما أدرجت ههنا لغرض التعقيد.
وقوله: (ولو [باداهم] إنسان برواية، لم يبتدروا العمل بها، ما لم يبحثوا عن حاله). هذا أمر لم ينقل، وإنما قدره تقديرا. وكون المقدر يقع على حسب ما يقدره الخصم، فقد يقدر خصمه نقيضه. والعجب إسناد القطع إلى مثل هذا الوهم، والخيال الضعيف.
وقوله: (ومن ظن [بهم] أنهم [كانوا] يعملون برواية كل مجهول الحال، فقد ظن محالا). هذا هو نفس المذهب من غير عضد له بدليل.','2','680',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1377,'وأما قوله: (والذي [أراه] أنه [لا يطلق القول] برد رواية المستور، بل لابد من البحث عن حاله). هذا لا خلاف فيه، ولا ذاهب يذهب إلى أنا إذا كنا على حل شيء، فروى لنا مستور تحريمه، وذلك الشيء هو بين أيدينا، فلا يحل مد اليد إليه، لعدم انكشاف حال الراوي. وكذلك في شهادة المستور. وكذلك إذا صودف خبر لم يعرف (١٤٧/ب) كونه منسوخا أم لا. فلابد من التوقف حتى ينكشف الحال. وهذا مجمع عليه. فلا معنى لإضافته ذلك إلى نفسه رأيا.','2','681',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1378,'وأما قوله: (إنه إذا روى مجهول، ودخل في غمار الناس، فإنه تنقلب الإباحة كراهة). هذا أولًا كلام متجوز به، فلا تصير الإباحة كراهة أبدا، إذ هو قلب الحقائق. نعم، يجوز أن يصير المباح مكروها إذا زالت الإباحة.
ثم المصير إلى أنه يصير مكروهًا، تحكم من غير دليل، فإن العدالة إذا كانت شرطا، ولم تظهر، ولم تحقق، فلا حكم بالخبر على حال. وإن كان يقول: يمكن أن تكون حاصلة، فهذا يقتضي توقفا، وترددا في التحريم، والبقاء على الأصل.','2','682',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1379,'فأما المصير إلى الكراهة، فمخالف لمقتضى الرواية، وللدليل السابق. فلا شيء يتمسك به في هذه الصورة، وهذا من عجيب الأمور، فإنه خروج عن الدليل السابق، وعن الخبر اللاحق. ويظهر هذا عندي فيما إذا ظن المكلف الحل، وعورض بدليل يقتضي تحريما، ولم يقو المعارض على إسقاط ظنه بالكلية، فقد صار بعض الناس في مثل هذا النوع إلى الكراهة.
وهذا أصعب شيء في الفقه وأغمضه، ولا يكاد أن يكون لقائله مستند. بل كنا قد قررنا أنه لو اختلف الناس قبل هذا الناظر في الإباحة والحظر، وصار هو إلى الكراهة، لكان خارقا للإجماع. فهذه خيالات ضعيفة، لا مستند لها في الشريعة.
قال الإمام: (فإن قالوا: الأصل نقيض الفسق) إلى آخر المسألة. قال','2','683',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1380,'الشيخ: هذا الكلام صحيح، لا يحتمل زيادة بحال.

قال الإمام: <span data-type="title" id=toc-233>(فصل- في التعديل [والتجريح]: </span>إذا تقرر أن الفاسق','2','684',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1381,'مردود الرواية، [وصح] أن القبول متوقف على ظهور العدالة) إلى قوله (والكلام في التعديل والتجريح متفرع عليه). قال الشيخ: هذا الكلام كله واضح إلا قوله: ومن التعبدات في الشهادات اشتراط الحرية. هذا الكلام يوهم أن في الشرع نصا على رد شهادة العبد، وليس كذلك، فليس في القرآن ولا في السنة- فيما علمته- نص يتضمن رد شهادة العبد، وقد قبلها جماعة من','2','685',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1382,'الصحابة والتابعين. وإنما المسألة عند العلماء مجتهد فيها، محكوم فيها بالرأي المحض.
وقد يتمسك الرادون بأن العبد ناقص المنزلة، محطوط المرتبة، والشهادة منصب سني، وليس من المناسب إعطاء الخسيس المنصب النفيس. ونقصانه من جهة أن الرق من أثر الكفر، فسلب المنصب لذلك. وقد ينقض هذا بقبول فتواه وروايته، والمناسب قد ينقض إلا أن يعتذر عنه. وقد اعتذر عن ذلك بأن الفتوى مضطر إليها، إذ قد لا يوجد غيره. وأما الشهادة، فيمكن أن يشهد غير العبد. وهذا أيضًا قد لا يوجد غيره، إذ قد لا يسمع الخبر من الرسول غيره.
قال الإمام رحمه الله: (ونحن ننقل المذاهب فيهما ونؤثر المختار) إلى','2','686',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1383,'قوله (بالتعديل والجرح). قال الشيخ أيده الله: ما ذكره الإمام من كون التعديل والجرح على وجهين: صريح وضمن. هو كما ذكره، والمذاهب أيضًا كذلك. والخلاف في المسألة دائر على حرف واحد، وهو أن المعدل والمجرح، هل هو مخبر فيصدق، أو هو حاكم ومفت، فلا يقلد؟
ذهب ذاهبون إلى أنه مفت ومجتهد، فلا يقلده غيره. بل لابد أن يبدي مستنده، لينظر، هل يوافق (١٤٨/أ) أو يخالف؟ وهذا هو أصل مذهب','2','687',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1384,'الشافعي، إلا أنه لم يذكر أسباب التعديل، لعدم الضبط، فرجع إلى الإخبار فيه، وأبقى باب [التجريح] على الأصل، فيفتقر إلى ذلك السبب، ولا يقلد غيره فيه.
وطرد طاردون هذا في القسمين، ومنعوا التقليد، وقالوا: لابد من ذكر السبب فيهما جميعا. وقال قائلون: يستغنى عن ذكر السبب، وقد أخبر عن كونه عدلا أو مجرحا، وهو صادق، فيجب قبول قوله.','2','688',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1385,'وقال القاضي: إطلاق الجرح يخرج الثقة، وإطلاق التعديل لا يكفي. وهذا لعمري ضعيف، فإنه إن سلك [به] مسلك [المخبر]، فليكتف في الأمرين. وإن سلك به مسلك المفتي، فلا ينبغي أن يقلد في الأمرين.
وقوله: إن إطلاق الجرح يخرم الثقة، ليس كذلك في حق كل أحد وإن فرض في حق العارف بالتعديل والجرح، فغلبة الظن عند الإطلاق حاصلة فيهما جميعا.
وأما ما ذهب إليه الإمام من الفرق بين البصير وغيره. فلا خلاف في أن من ليس من أهل البصر بالتعديل والتجريح أنه لا يقبل منه واحد منهما، [وأن] حظه أن ينقل ما رآه، فيكون راويا محضا. وينظر الحاكم فيما ذكره، هل يقتضي تعديلا أو تجريحا، أم لا؟ فإضافته هذا إلى رأيه لا يختص هو بذلك.
ثم قوله: (وإن كان من أهل البصر، اكتفينا بإطلاقه هذا). والذي نراه','2','689',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1386,'في هذه القضية أن نسلك بالتعديل والتجريح مسلك الشهادات، لا مسلك الخبر والرواية، وعليه نرجح اعتبار الحرية والذكورية، ولا يكتفى بتعديل العبد ولا المرأة. وإنما قلنا ذلك، من جهة أنها قضي تتعلق بها منفعة المعدل والمجرح، فهي بالشهادة أليق، فلا يفتقر في حق الشاهد إلى أن يذكر السبب الذي لأجله يشهد، وكذلك إذا شهد بالملك لزيد، لم يفتقر إلى ذكر السبب، وإن كان العلماء يختلفون في الأسباب المملكة، صحة وفسادا، ولزومًا وجوازا، ولكن أعرض عن ذلك في أبواب الشهادات.
قال الإمام: (ثم قال المحققون: يكفي في التعديل والتجريح قول واحد) إلى قوله (أفاد جرحه ردًا أو توقفا). قال الشيخ: أما عدد مزكي الشاهد ومجرحه، فهو ثابت عند مالك، ولا أعرف له نصا في تعديل الراوي','2','690',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1387,'وتجريحه. والذي يقتضيه قياس مذهبه أن يشترط العدد فيهما جميعا. وإنما قلنا ذلك، لأن اشتراط العدد في تعديل الشاهد وتجريحه إنما سببه أنا سلكنا بالتعديل والتجريح مسلك الشهادة للشخص أو عليه، لثبوت الاختصاص، والعدد في الشهادة لازم. فلا يحسن أن يقال: إن التزكية في حق الشاهد شهادة، وفي حق المخبر خبر، لأن معقول الشهادة فيهما جميعا على حد واحد، وهو الإنباء عن أمر يختص به المشهود له أو عليه.
فالصواب عندي على هذا أن يشترط العدد فيهما جميعا، وأيضا فإنه إذا روى الحر المسلم الذكر، وطلبت تزكيته، فزكاة واحد، فقد ثبتت العدالة، وجميع شروط الشهادة، فإذا شهد هذا لعينه: إن ردت شهادته، فهو عدل حر ذكر، وإن قبلت شهادته، فقد قبلت شهادة من لم يزكه إلا واحد، فلا يصح عندي إلا تعميم القول باشتراط العدد.','2','691',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1388,'وقد قال بعض الناس إن التزكية شرط القبول في [الشهادة دون] الرواية، وإذا كان المشروط لا يعتبر العدد فيه، فالشرط بذلك أولى. وهذا هو الذي أشار إليه الإمام ههنا.
قالوا: (١٤٨/ب) وقد وجدنا في الشريعة الإحصان يثبت بقول شاهدين، وإن لم يثبت الزنا إلا بأربعة. وهذا ضعيف، فإن العدد في التزكية، لم يكن باعتبار كونها شرط القبول للرواية، وإنما كان من جهة أن التزكية شهادة للشخص، فيفتقر إلى العدد.
قال الإمام: (فأما التعديل والجرح الواقعان ضمنًا) إلى قوله (وهذا نجاز الكلام في هذا الفن). قال الشيخ: لا يختلف الناس فيما إذا انكشفت عادة الراوي، وتبينت حالته من كونه يقتصر على الرواية عن العدول، أو عرف منه','2','692',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1389,'الرواية عن كل أحد، أن رواية الأول تعديل، [ورواية] الآخر، ليست بتعديل.
وكلام الإمام يشير إلى أن هذا موضع خلاف. وذلك لا يتصور.','2','693',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1390,'وإنما محل الخلاف، إذا لم تعرف عادة الشخص في نفسه، هل يقال: الغالب على أهل الحديث أنهم لا يروون إلا عن العدول، فيكون هذا الراوي معدلا؟ أو يقال: قد صودف منهم من يروي عن العدل، وعن غير العدل، فلا يثبت التعديل بالتردد؟ هذا هو الموضع الذي يتصور فيه الخلاف.
والأظهر عندنا في هذا أنه تعديل عند التباس حال الراوي المخصوص، إذا الأغلب على المحدثين أنهم إذا رووا عن غير من هو من أهل الرواية، أن ينبهوا عن العلل.
هذا الترمذي وغيره كأبي داود والنسائي وغيرهم، إذا ذكروا','2','694',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1391,'الأحاديث الضعاف، ذكروا العلل. وقليل من المحدثين من يروي عن الضعفاء ولا يبين. وهذا [يكاد] أن يكون تلبيسا في الرواية. والصواب القضاء عند عدم معرفة عادة الراوي أنه معدل، حملًا على الأكثر.','2','695',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1392,'قال الإمام: (مسألة: قال القاضي - رضي الله عنه -: إذا لم نجد معتصما مقطوعا به) إلى قوله (أحلناها على هذا القانون). قال الشيخ: هذا الذي ذكره القاضي ضعيف، ومن أين يلزم إذا لم يقم دليل قاطع على القبول، قطع بالرد؟ وهذا إنما هو في أنواع الخبر. وأما الجنس، فقد دل الدليل عليه. ولم يقم القاضي على ذلك [برهانًا] أكثر من قوله: إن الأمة اجتمعت على العمل بخبر الواحد. وأين الدليل على أن كل نوع لابد من اشتراط العلم فيه؟ وقد قال القاضي في قياس الشبه، وهو نوع من أنواع القياس: (إن التمسك به باطل).
وكذلك قال في الطرد والعكس. قيل له: كيف تقول إنه باطل، وقد صوبتم','2','696',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1393,'المجتهدين؟ قال: هذا باطل عندنا إذا لم يغلب على ظننا، وهو صحيح عند من غلب على ظنه.
فهذا تصريح منه بأن الأنواع لا يشترط فيها العلم. ويتأتى أن يسلك طريقه في قياس الشبه، ويقال: إنما أجزنا العمل بالقياس من إجماع الصحابة، ولولا إجماعهم، لم نعمل به. فإذا لم نجد إجماعهم في قياس الشبه، قطعنا برده. هذا يلزم على طريقه.
وقد قال الإمام عنه فيما سبق: (إنه شبب بإلحاق قبول رواية الصبي بالمظنونات). وهي أيضًا نوع من الأنواع. دل ذلك على أنه وافق الناس على أنه لا يشترط العلم في الأنواع. هذا هو الصحيح.
وأما ما ذكره الإمام من (أن أصحاب رسول الله - صلى الله عليه وسلم -[كانت] تروى لهم أخبار، فيقبلها بعضهم ويردها آخرون، ولا ينكر هؤلاء على هؤلاء ولا هؤلاء','2','697',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1394,'على هؤلاء). فهذا كلام ضعيف عندي من وجهين:
أحدهما- أن هذا الذي نقله- إن صح- فلعله عن بعض أهل الإجماع، (١٤٩/أ) فلا تكون المسألة مجمعا عليها، إنها اجتهادية. وإضراب بعض أهل الإجماع عن الإنكار، لا يكون دليلًا قاطعا على أنها [إجماعية]. على أن ما ذكره لم نره منقولا عن جميعهم، ولا عن بعضهم.','2','698',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1395,'الثاني- أنه يمكن أن يكون القائل علم عدالة الشخص واستجماعه لشرائط القبول، ولا يلزم أن يكون الراوي معروفا عند كل من روى له، فيكون ذلك اختلافا في عين، باعتبار تحقيق مناط الشرط فيها، وليس اختلافا في جنس ولا في نوع. فهذا هو الذي يضعف قوله: إن الأنواع يتطرق إليها الاجتهاد.
قال الإمام: (مسألة: جرى رسم الأصوليين [بعقد] مسألة في التعديل والتجريح) إلى قوله (ولما استرسلت على سائر الأعصار). قال الشيخ: الافتقار إلى معرفة عدالة أصحاب رسول الله - صلى الله عليه وسلم - تشتد الحاجة إليها في الأصول.','2','699',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1396,'وقول الإمام: إنما تمس الحاجة إليها في أصول الدين، نظرا إلى الإمامة وشرائطها، ومن يصح أن يكون إماما. هذا لعمري وجه دخولها تحت علم الكلام. فأما الحاجة إليها في الأصول، فعظيمة، لأنهم نقلة الشريعة وحملتها. فلو لم تثبت عدالتهم، لم تنته الشريعة إلينا بحال، فكانت الحاجة إليها ماسة في الأصول.
ولننقل الآن المذاهب في تعديل الصحابة، فإن الإمام لم يستوعب النقل','2','700',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1397,'ولا الاحتجاج. وقد قال الله تعالى: {لا يستوى منكم من أنفق من قبل الفتح وقاتل أولئك أعظم درجة} إلى قوله: {وعد الله الحسنى}. وقال تعالى: {والسابقون الأولون من المهاجرين والأنصار}. وقال تعالى: {واذكروا نعمت الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا وكنتم على شفا حفرةٍ من النار فأنقذكم منها}.
وذكر الله تعالى المهاجرين والأنصار في عدة مواضع من كتابه. وقال - صلى الله عليه وسلم -: (إن الله تعالى اختار لي أصحابا وأصهارا وأنصارا). كيف ومعرفة','2','701',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1398,'أحوالهم في الجهاد وبذل المهج والأموال، ومقاطعة الآباء والإخوان، وهجرة المساكن والأوطان في طاعة الله وطاعة رسوله من أوضح الأدلة على العدالة واستقامة الحالة. هذا مذهب السلف وجماهير الخلف. فهو معتقدنا فيهم حتى يثبت لنا خلاف ذلك، فلا حاجة بهم إلى التعديل، لثبوته واشتهاره.','2','702',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1399,'وقد زعم قوم أن حالهم كحال غيرهم في لزوم البحث. وقال قوم: حالتهم العدالة في بداية الأمر إلى ظهور الحروب والخصومات، ثم تغيرت الحال، فسفكت الدماء، فلابد من البحث.
وقال جماهير المعتزلة: عائشة وطلحة والزبير وجميع أهل العراق','2','703',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1400,'والشام فساق بقتال الإمام الحق، تعالى الله عن قولهم.
وقال قوم من سلف القدرية: يجب رد شهادة علي وطلحة والزبير - رضي الله عنه - مجتمعين ومفترقين، لأن فيهم فاسقًا لا يعرف بعينه.','2','704',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1401,'[وقال] قوم: نقبل شهادة كل واحد إذا انفرد، لأنه لم يتعين فسقه، أما إذا كان مع مخالفه فترد، إذ [نعلم] أن أحدهما فاسق. وشك بعضهم في فسق عثمان وقتلته. وكل ذلك على خلاف السنة. بل لأهل السنة فيما جرى بينهم مذهبان. منهم من يقول: ما جرى بينهم مبني على الاجتهاد، وكل','2','705',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1402,'مجتهد مصيب، أو المصيب واحد، والمخطئ معذور، ولا ترد شهادته.
وقال قوم: ليس ذلك مجتهدًا فيه، لكن قتلة عثمان والخوارج مخطئون قطعًا، لكن جهلوا خطأهم، فكانوا متأولين، والفاسق المتأول فيه [كلام] نذكره بعد ذلك. هذا هو مذهب أهل الحق.
فأما المصير إلى القدح في مصابيح الأمة، وأصحاب رسول الله - صلى الله عليه وسلم -، ورد أقوالهم وإبطال رواياتهم، فخروج عن الدين، ومراغمة لإجماع المسلمين.
اعلم أن الناس اختلفوا في الكفر والفسق القادحين في الرواية والشهادة، هل قدحا بالنظر إلى كونهما يسلبان المنصب، أو قدحا بالنظر إلى تطرق التهمة؟ فقال الشافعي: تقبل شهادة أهل الأهواء إلا الخطابية من الرافضة، لأنهم','2','706',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1403,'يرون الشهادة بالزور لموافقة المذهب. وهذا يدل على أنه إنما رأى التردد للتهمة.
وقبل أبو حنيفة شهادة أهل الكفر بعضهم على بعض. وهذا يدل على أنه رأى الكفر لا يسلب المنصب. وقد صرح بعض أهل الأصول بأن من قال ببدعة اقتضت تكفيره، [وهو] معظم للدين، مصل إلى القبلة، فإن روايته وشهادته تقبل. وهذا قياس قول أبي حنيفة.','2','707',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1404,'وقال القاضي: الكفر والفسق يسلبان المنصب، وجهله بفسق نفسه كجهله بكفر نفسه ورق نفسه، فلا يعذر بذلك، بل يتضاعف فسقه بجهله نفسه. وهذا هو الظاهر عندنا.
والمعتمد في العمل بخبر الواحد ما تقدم من إرسال رسول الله - صلى الله عليه وسلم - رسله، وإجماع الصحابة على قبول [خبر] الواحد. أما الرسول فلم يقرب أهل الفسق ولا أهل الأهواء، وقد قال في المارقة: (لا تجاوز صلاتهم حناجرهم). ثم قال: (لئن أدركتهم لأقتلنهم قتل عاد).
وأما أصحاب رسول الله - صلى الله عليه وسلم - فقد نابذوهم وهجروهم، حتى قال عبد الله','2','708',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1405,'ابن عمر لأهل القدر: (إذا لقيتهم فأعلمهم أني برئ منهم وأنهم برآء مني). وكذلك قتال أصحاب رسول الله - صلى الله عليه وسلم - للخوارج، وإباحة دمائهم. كيف يليق مع هذا تفويض أمر الدين إليهم، وحمله عنهم، وقد قال - عليه السلام - فيهم: (تقتلهم أقرب الطائفتين إلى الحق، أو أولى الطائفتين بالحق)؟ فهذه أدلة بينة تدل على المقاطعة والمهاجرة والتباعد بالكلية. وفقدان الأدلة على قبول الرواية، كافٍ في الامتناع من القبول.
واعلم أنا لسنا نعني بعدالة كل واحد من الصحابة أن العصمة له ثابتة، والمعصية مستحيلة، وإنما نريد أن الرواية منه مقبولة من غير تكلف بحث عن أسباب العدالة، وطلب التزكية، إلا أن يثبت ارتكاب ما يقدح في العدالة، ولم يثبت ذلك، والحمد لله.
فنحن على استصحاب ما كانوا عليه في زمن رسول الله - صلى الله عليه وسلم -، حتى يثبت بطريق صحيح ما يقدح فيه. ولا التفات إلى ما يذكره أصحاب السير من الأخبار، فإن أكثرها ضعيفة. بل يتثبت الناظر حتى يصح عنده النقل بالطرق الصحاح من أهل هذا الفن. وإذا ثبت ذلك، فلا يقصر عن طلب التأويل والمعاذير، ولا يعدمها الموفق بحال. وإنما النفوس التي فيها الظعن والتعصب تقبل كل وارد، ولا تبالغ في التفتيش عن الأعذار، فيوجب ذلك نفرة في القلوب، وحطا من منصب خيار الخلق. نعوذ بالله من الحيرة، واتباع الجهل والهوى.','2','709',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1406,'والذي يدل على هذا من جهة السنة [أنه] لما ذكر الفئة قال: (تقتلهم أقرب الطائفتين إلى الحق، أو أولى الطائفتين بالحق). وهذا يدل على أن الأمر فيه غموض، وأن إحدى الفرقتين أقرب إلى الحق، ولم ينسب الأخرى إلى عناد، ولا اقتحام آثام. وقال - عليه السلام - في حق الحسن (١٥٠/أ): (إن ابني هذا لسيد، وسيصلح الله به بين فئتين عظيمتين من المسلمين).
فقد كانت هذه الأمور وأصحابها مكشوفون للنبي - عليه السلام -، فلو كان ذلك يقتضي فسقا وخروجا عن سمة العدالة، لما كان النبي - صلى الله عليه وسلم - يكرمهم ويجلهم، وينزلهم منازلهم مع علمه بما يؤول إليه أمرهم. وهذا عندي دليل واضح على أن المسألة اجتهادية، وأن المخطئ فيها معذور غير مفسق ولا مؤثم. اللهم إلا في قتلة عثمان، فإنهم مخطئون قطعا، سافكون الدم الحرام من غير جريان ما يقتضي سفكه، والله يقضي بالحق: {قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة أنت تحكم بين عبادك في ما كانوا فيه يختلفون}. هذا هو القول الحق.','2','710',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1407,'وما ذكره الإمام من [أن] إطلاق القول [بالإبطال]، بناء على ما نقل عنهم من الأحوال في مباشرة الحروب، يفضي إلى رد قول الجميع أو الأكثر. كلام صحيح، فإن أعيان الصحابة دخلوا في ذلك. هذا عمار بن ياسر يقول فيه - عليه السلام -: (ملئ إيمانا من قرنه إلى مشاشه).
ومعاوية كان يكتب الوحي لرسول الله - صلى الله عليه وسلم -. وعمرو بن العاص كان واليا لرسول الله - صلى الله عليه وسلم - على عمان، فقبض رسول الله - صلى الله عليه وسلم - وهو وال. واستقصاء القول في آحادهم يطول.','2','711',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1408,'قال الإمام: <span data-type="title" id=toc-239>(فصل - في المراسيل والمسندات، وذكر المذاهب فيها) </span>إلى قوله (والعدل الموثق به إذا أرسل وعمل بمرسله العاملون قبلته). قال الشيخ: حاصل [المراسيل] وإن تعددت صورها، أن تكون في طريق الخبر، أو ملتبس العين، إما بأن لا يذكر، أو بأن [يذكر] على الإبهام. هذا ضابط الإرسال.','2','712',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1409,'وأما اختلاف العلماء في القبول والرد، فليس هو على الإطلاق، بل بعض الصور لا يتصور فيها خلاف في الرد، وهو إذا عرف من الراوي أنه يروي عن العدل وغير العدل، فهذا لا يتصور أن يكون إرساله مقبولا، وكذلك إذا لم يكن بصيرا بالتعديل والتجريح، وكان ممن لو عدل صريحا، لم يقبل منه، فغاية الرواية عنه أن تكون كالتعديل له، فإذا لم يكن تعديله مقبولا، فكيف يثبت الخبر بإرساله؟
وأما قوله: (إنه إذا قال: أخبرني رجل، فهذا يقطع برده، إذ ليس في قوله: أخبرني رجل، إشارة إلى تعديله). هذا يختلف باختلاف أحوال المحدثين، فمن عرف منه أنه لا يروي أبدا إلا عن عدل، فلا فرق بين أن يقول: أخبرني ثقة عدل، وبين أن يقول: أخبرني رجل، إذ العدالة كصريح','2','713',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1410,'العبارة. وانحصر الخلاف فيما إذا عرف منه أنه لا يروي إلا عن عدل، والتبس أمره، مع المصير إلى [أن] [أكثر] الرواة لا [يروون] إلا عن العدول.
والمقصود تحصيل غلبة الظن بأن من روى له عدل عنده. وهذا قد يكون بصريح عبارته، كقوله: أخبرني الثقة عندي. وقد يكون بمعرفة عادته، كمالك ابن أنس - رضي الله عنه -، وقد قال لما سئل عن عدالة شخص: (هل رأيته في كتابي؟','2','714',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1411,'فقال: لا. فقال: لو كان عدلا، لرأيته في كتابي). أما الذي لا تعرف عادته في نفسه، فهذا ينقسم قمسين:
[الأول]- فقد يكون في لفظه ما يشير إلى عدالة من روى عنه، كما إذا قال التابعي: قال رسول الله - صلى الله عليه وسلم -، فهذا يقوى فيه جانب ثقة الواسطة، إذ لو لم','2','715',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1412,'يكن عدلا عنده، لما أطلق ذلك. وليس هذا بقاطع. إذ معنى قوله: قال رسول الله - صلى الله عليه وسلم -: أي أخبرت عنه. فهذا من الإضمار المقطوع (١٥٠/ب)، فيتنزل منزلة ما لو صرح به.','2','716',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1413,'[الثاني]- ألا يكون له عادة معروفة، ولا تشير ألفاظه إلى تعديل الواسطة. فهل يقال: إنه لا يقدم على الرواية مستندة إلى الرسول إلا أن يثبت عنده عدالة الواسطة، أو لا يحقق ذلك في حقه، بل لا يعلم إلا محض روايته؟ هذا مما يتردد فيه، وهي أضعف صور المرسل في القبول.
فإذا تصورت الصور على ما قررناه، فنقول: لا يخلو: إما أن يبني الافتقار في التزكية إلى ذكر السبب، أو يبني على الاكتفاء بالإطلاق. فإن وقع التفريع على الافتقار إلى ذكر الأسباب، وجب القطع بإبطال الإرسال. وإن وقع الاكتفاء بإطلاق التعديل، فلا يخلو: إما أن يبنى الأمر على اشتراط عدد','2','717',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1414,'المعدل، أو يبنى على الاكتفاء بتعديل الواحد. فإن فرع على القول باشتراط عدد المعدل، لزم رد المراسيل، إذ أقصى الأمر أن يقدر معدلا مصرحا معينا من عدله، وتعديله وحده غير مكتفى به. فلابد على هذا من رد المراسيل.
وإن فرع الأمر على الاستغناء عن ذكر الأسباب، وعلى الاكتفاء في التعديل بالأفراد، فعلى هذا يختلف العلماء. وحاصل الخلاف: أن القائل بنى الأمر على حصول ظن العدالة على الشرط الذي ذكرناه. وهو أمر معتمد حتى','2','718',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1415,'يظهر ما ينفيه.
وهذا يناظر عندي مسألة تقدمت، وهي [ما] إذا روى الراوي وخالف، [فإنا] قد قلنا: يظن عند مخالفته للرواية إطلاعه على ما يقتضي ترك التمسك بها.
فإذا قال القائل: فلعله متوهم. قلنا: غلبة الظن حاصلة، وإمكان الخطأ غير مضر، والنظر إلى الترجيح مع إبهام أحد الأمرين محال. فقد وجدت غلبة الظن، ولم يبق إلا إمكان الإسقاط، وذلك لا يمنع العمل. هذه نكتة','2','719',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1416,'القائلين، وهي لعمري صحيحة.
وأما الرادون فقد قالوا: إنه لو سلمنا أنه عدله فكذلك، إنما يكتفى به عند معرفة عين الراوي والبحث عن أسباب الترجيح مع تحقق فقدانها. وإذا تعذرت معرفة عين الراوي، امتنع العمل، لتعذر شرطه، وهو البحث عن حاله، ومعرفة عدالته، التي هي شرط قبول قوله.
قالوا: ونحن قد رددنا رواية المستور، لعدم تحقق العدالة، وإن كنا لم نطلع على فسق، فالمتهم أولى بالرد، لاحتمال أن لو عرفناه لعرفناه بالفسق. وهذا القول ضعيف، فإن المستور لم يستند تعديله إلى جهة. وهذا قد قدمنا أنا تلقينا تعديله ممن روى عنه على التفسير الذي مر.
وأما إذا لم يغلب على الظن أنه عدله بوجه، فلا سبيل إلى قبول هذا الخبر بحال. هذا مستند المذاهب، والتنبيه على سر كل قول منها، وبيان مأخذه، والصحيح منها.','2','720',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1417,'والصواب عندي في هذه المسألة قبول المراسيل، تفريعا على الاكتفاء في تعديل الراوي بقول الواحد. وأما على طلب عدد المزكي، فلا سبيل إلى القبول بحال.
وأما ما ذكره الشافعي: (من الشبيب بقول [مراسيلٍ ابن المسيب). وقوله أيضا: (العدل الموثوق به إذا أرسل) إلى آخره. فتتبع القاضي تتبع صحيح، فإنه قال: (مراسيل ابن المسيب حسنة، لست أدري ما الذي يحسنها؟ قال: وقد بلغت عن هذا الحبر أنه قال: تتبعت [معظمها] فوجدتها مسندة من غير طريقة. قال القاضي: وهذا فيه نظر). فرحم الله القاضي، ما أجمل هذا الرد، وما ألطف هذه العبارة. فإنه (١٥١/أ). فرحم [يقول]: إذا ثبت الإسناد، فالعمل يستند إلى المسند دون المرسل. هذا كلام صحيح، فلا معنى لقبول المراسيل على هذا التقدير.','2','721',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1418,'قال القاضي: وأما الوجه الثاني: وهو قوله: (العدل الموثوق به إذا أرسل وعمل بمرسله العاملون قبلته) فيقال له: إن كان العمل عمل أهل الإجماع، فالاعتماد على الإجماع، وإن لم يكن على العمل إجماع، فلا التفات إليه. فهذا معترضه على الشافعي، وهو كلام بالغ.','2','722',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1419,'قال الإمام رحمه الله: (والذي لاح لي أن الشافعي [لا] يرد المراسيل) إلى قوله (والقول [على تفصل وتحصل]). قال الشيخ: هذا لاكم واضح في نفسه، إلا أن هذا المذهب الذي حكاه الإمام عن الشافعي في قبول المراسيل، لم يحكه غيره من الأصوليين، بل أطلقوا القول بأن الشافعي يرد المراسيل. ويحتمل أن يكون هذا قولا آخر. وظاهر كلام الإمام منع ذلك','2','723',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1420,'عن الشافعي، وإنما رد المذهب إلى ترجيح المسند على المرسل. وهذا خلاف ما نقله الناس عنه، فلا ينبغي أن يترك النقل المصرح به، بناء على قضية استبد هو بفهمها من غير تصريح بنقلها.

قال الإمام: <span data-type="title" id=toc-242>(فصل - في تحمل الرواية، وجهة تليقها) </span>إلى قوله (ووضوح ذلك يغني الناظر عن مزيد بيان). قال الشيخ: هذا كله واضح، والمختار ما','2','724',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1421,'اختاره القاضي في منع الرواية، اعتمادًا على نظر غير الشيخ.
واعلم أن المعتمد في التحمل هو الضبط دون العدالة والبلوغ والإسلام، فيصح أن يروي الصبي بعد بلوغه ما سمعه في حال صباه، كما روى محمود','2','725',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1422,'ابن الربيع حديث المجة التي مجها النبي - صلى الله عليه وسلم - في وجهه وهو ابن سبع سنين وكذلك يجوز أن يروي الكافر بعد إسلامه ما سمعه في حال كفر ووعاه،','2','726',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1423,'وكذلك الفاسق بعد عدالته.
والسبب في ذلك: أن الفهم يُطلب للحفظ والوعي، والعدالة تطلب لتحصيل غلبة الظن بقوله، فلا يضره في التحمل فقدان الإسلام والعدالة.','2','727',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1424,'وكذلك الحكم [في الشهادة] مع ضيق بابها. فإذا شهد العبد بعد حريته بشهادة تحملها في حال رقه، قبلت شهادته. قال مالك رحمه الله: إلا أن يكون أداها في حال رقه وردت، فإنها لا تقبل بعد ذلك أبدا. إما لكونه قد حكم بردها، فلا تنقض الأحكام، أو لكونه يتهم على إزالة عيب الرد عن نفسه بإعادة أدائها. هذا تمام الكلام في ذلك.

قال الإمام: <span data-type="title" id=toc-243>(مسألة: إذا قال الشيخ المتلقى عنه [للراوي]: [أجزت لك] أن تروي عني ما صح عندك من مسموعاتي) </span>إلى قوله (وإن تحقق ظهور','2','728',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1425,'سماع موثوق به، فإذ ذاك، وهيهات). قال الشيخ: والذي نختاره في الإجازة أنه يجوز أن تستند إليها الرواية، والدليل عليه أن الحديث','2','729',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1426,'والخبر عندنا: هو كلام النفس، والألفاظ إنما تسمى كلامًا، توسعا وتجوزًا، والشيخ لم يسمع الراوي لفظا، ولا علم ما في قلبه ذكرًا، وقد لا يكون الشيخ حافظا للأحاديث التي أجازه فيها أصلاً، فكيف يصح أن يقول: حدثني أو أخبرني، ولم يحدثه ولا أخبره؟ وهذا ممتنع. وقد اختلف قول مالك في صحة إسناد الرواية إلى الإجازة، والصحيح عندي ما قدمته فيها.
قال الإمام: (ومما يتعلق [بتتمة] الكلام) إلى آخر المسألة. قال','2','730',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1427,'الشيخ: هذا كله واضح، لا إشكال فيه.
قال الإمام: (مسألة: إذا وجد الناظر حديثا مسندا) إلى قوله (وترتيب أبواب). قال الشيخ: مذهب جماهير المحدثين امتناع إسناد الأعمال إلى','2','731',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1428,'الأخبار المرسلة، لالتباس الوسائط، وعدم صفات (١٥١/ب) الرواة، واحتمال الرجوع إلى نقل من لا يصح استناد الرواية إلى نقله. وإذا كان كذلك، فصحة النسخة اقتصارًا عليهان لا يتحصل منه معرفة الواسطة. فإذا وجد نسخة مصححة مثلا من صحيح البخاري، فمن الذي أوصله إلى أن هذا تصحيح البخاري؟','2','732',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1429,'ولو قدرنا أنه ثبت عنده ذلك، افتقر إلى أن يثبت عنده الطريق من البخاري إلى النبي - صلى الله عليه وسلم -. فهؤلاء القوم لا يجيزون أبدا الرواية والعمل بها، إلا إذا عرفت الوسائط، وظهرت عدالتهما.
وما وراء ذلك، فلا يصح أن يكون من أخبار الآحاد، فلا يلتفت إلى صحة النسخة، إلا أن يقول الراوي: أنا أروي هذا عن فلان، وعين من روى له، وعلمه الفرع وتحقق عنده ضبطه وعدالته. فعلى هذه القاعدة يمنعون التعلق بالنسخ الصحيحة. وهذا هو الظاهر في طلب صحة المراد وانتفاء الإشكال عن طرق أدلة الأحكام. والمحدثون في هذا الباب هم أهل هذا الفن على','2','733',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1430,'الحقيقة، فلا معنى لاطراح أقوالهم بالكلية:

قال الإمام: <span data-type="title" id=toc-245>(مسألة: إذا قال الصحابي: من السنة كذا، فقد تردد فيه العلماء) </span>إلى قوله (ويلتحق الآن بذلك مسائل). قال الشيخ: ينبغي ههنا أن نتكلم على مراتب نقل الأخبار عن رسول الله - صلى الله عليه وسلم -، وبيان تفاوتها، وموضع الوفاق والخلاف، وهي على خمس مراتب:
الأولى: وهي أعلاها أن يقول الصحابي: أخبرني رسول الله - صلى الله عليه وسلم -، أو حدثني، أو شافهني، أو سمعته يقول. فهذا هو الأصل في الرواية: (نضر الله امرءًا سمع مقالتي فوعاها [فأداها] كما سمعها). فلا يتطرق إلى هذا شيء أصلاً، إلا احتمال الكذب سهوًا أو عمدًا، وهو معرض عنه بالإجماع، للأدلة الدالة على وجوب العمل عند أخبار الآحاد، ولا ينفك شيء منها عن هذا الاحتمال.','2','734',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1431,'المرتبة الثانية: أن يقول: قال رسول الله من لم يعاصر رسول الله - صلى الله عليه وسلم -، بناء على ما بلغه على ألسنه التواتر والآحاد، وإذا لم يمنع ذلك في حق التابعي، فكذلك يصح من الصحابي، ودليل الاحتمال الوقوع. فقد روى أبو هريرة: (أن من أصبح جنبًا، فلا صوم له). ولما روجع قال: أخبرني به الفضل بن عباس. وكذلك روى ابن عباس: (أن النبي - صلى الله عليه وسلم - لم يزل يُلبي حتى رمى جمرة العقبة). ثم لما روجع قال: (أخبرني به أخي الفضل بن عباس). وقد صودف في أخبار ابن عباس مرسلات كثيرة. ولكن الظاهر أن الصحابي إذا أطلق ذلك، فإنما يريد به السماع.
فإن قيل: ما مستند هذا الظهور ألفظ أم عادة؟ فنقول: إنما يرجع هذا الظهور إلى عادتهم في النقل وغلبة الإسناد على أخبارهم. وقد نقل مسلم بن','2','735',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1432,'الحجاج في هذا مقالة شاذة مردودة بقول السلف والخلف جميعًا، وتلك المقالة أن بعض الناس قال: إنما تقبل هذه الرواية إذا ثبت عندنا من جهة أن الراوي اجتمع بمن روى عنه ولو مرة. ولسنا نشترط أن ينقل الاجتماع في عين كل رواية، بل يكفينا مجرد الاجتماع. قال مسلم: وهذه المقالة لا أصل لها في أقوال السلف والخلف، ويقول: إن الصحابة وغيرهم قبلوا أخبار الآحاد من غير هذه الشريطة. فإذًا الأحوال في هذه المرتبة ثلاث:
الأولى - أن يتحقق الاجتماع في عين الرواية، فلا إشكال في قبول هذه الرواية.
[الثانية]- وهي في أقصى البعد منها أن يتبين أنهما لم يجتمعا (١٥٢/أ) قط، إما لتباعد الديار، أو لتراخي الأعصار، أو لغير ذلك من الأسباب. ففي هذه الصورة يتحقق الإرسال.
[الثالثة]- هي التي استنكر فيها [مسلم] المقالة، ونسب قائلها إلى الجهالة، ورد [كثير] من الأخبار الصحيحة.','2','736',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1433,'المرتبة الثالثة: أن يقول: أمر رسول الله - صلى الله عليه وسلم - بكذا، أو نهى عن كذا، فهذا يتطرق إليه ما يتطرق إلى قوله: (قال) من احتمال الإرسال. ووجه آخر: وهو احتمال أن يكون ظن ما ليس بأمر أمرا، إذ قد يظن غير العموم عمومًا، وغير الأمر أمرًا. ولهذا قال بعض أهل الظاهر: لا حجة فيه ما لم ينقل اللفظ. وهذا غير صحيح عندنا، بل الظاهر أن الصحابي لا يطلق ذلك إلا إذا ثبت عنده بصريح المقال أو بقرينة الحال أنه أمر، وفهم ذلك فهمًا ضروريًا.
أما بناء الأمر على احتمال الغلط فممنوع، وذلك أن أكثر الأخبار المنقولة عن النبي - صلى الله عليه وسلم - على هذه الصيغة. ولو قال الصحابي: أمرنا رسول الله - صلى الله عليه وسلم - إذا كنا مسافرين أن لا ننزع خفافنا ثلاثًا، لقبل ذلك منه، ولم نقل: لعله فهم [التوقيت]، ولم يجر في ألفاظ الرسول [للتوقيت] ذكر، وكذلك إذا روى وشرط شرطا، أو عين وصفا.
وما ذكره هؤلاء، يفضي إلى رد معظم الأخبار. والناس من المتقدمين والمتأخرين والفقهاء [والأصوليين] على خلاف ذلك. لكن القاضي قال: لو قال الصحابي: نسخ رسول الله - صلى الله عليه وسلم - حكم كذا، لم يقبل، حتى يقول: قال رسول الله - صلى الله عليه وسلم - نسخت حكم كذا. وقال: لأنه قد يظن ما ليس بنسخ نسخا. قال: وقد','2','737',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1434,'يظن قوم أن الزيادة على النص نسخ.
وهذا في ظاهره موافق لقول أهل الظاهر، ومخالف لما عليه الجمهور من أهل الفقه والأصول. وقد يمكن أن يفرق بينهما، ويقال: إذا كان الحكم الأول ثابتا بطريق لا يتطرق إليه هذا الاحتمال، واشترط في الرافع أن يكون مساويا أو زائدا، فإذا تطريق الاحتمال إلى الرافع بوجوده عن المثبت، لم ينتهض قاطعا لدوامه، ولكن ينبغي على هذا أن ينظر إلى طريق ثبوت الأول، فإن كان بلفظ مثل لفظ الثاني، كقوله: أمرنا رسول الله - صلى الله عليه وسلم -، فالاحتمالان في المثبت والرافع على حد سواء، فينبغي أن يقع النسخ بذلك. وإن كان الأول ثابتا بطريق لا يتطرق إليه الاحتمال، استقرت دلالته، وامتنع نسخه بما يضعف عنه. هذا عندي هو الصحيح في ذلك.
ويتطرق إليه احتمال آخر في اقتضائه التعميم: ذهب بعض أصحاب العموم إلى أنه منزل على العموم. وهذا ضعيف، لأن الفعل لا عموم له، والمنهي والمأمور لم يذكر حتى يعرف عموم اللفظ وخصوصه، فهذا تعميم بالتوهم.
وقوله: أمر رسول الله - صلى الله عليه وسلم -، يصح أن يكون أمر واحدًا أو جماعة، أو عموم الخلق، والراوي صدق في قوله (أمر) في الجميع، فلا يثبت له عموم بالوهم. وقد قالوا: لو كان أمرا لواحد، أو لأقوام مخصوصين، لذكر الراوي ذلك. قلنا: لفظه لم يتعرض لعموم المأمورين، والصدق يحصل في جميع الأحوال. ومن توهم العموم بغير مستند، فهو متحكم، ولا التفات إلى إزالة تحكم المتحكمين. هذا إذا لم يعرف من عادة الرواي أنه لا يقول: أمر رسول الله - صلى الله عليه وسلم - إلا إذا أمر الأمة. أما إذا عرف ذلك من عادته، فهو كالتصريح بأمر الجميع.','2','738',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1435,'المرتبة الرابعة: ما ذكره، وهو قول الصحابي: أمرنا بكذا ونهينا عن كذا، فهذا يتطرق إليه ما سبق من الاحتمالات. (١٥٢/ب) واحتمال آخر: وهو التردد في الآمر، هل هو الرسول أو غيره؟ ذهب الإمام إلى أن لا حجة فيه. وهذا الذي يقتضيه نظر الأصول، لأنه لم يسم فاعله، ولم يتحد الضمير في هذا الباب، إذ يصح أن يكون الآمر رسول الله - صلى الله عليه وسلم -، أو بعض الأمراء، إلا إذا عرف من قرينة حال الراوي أو عادته إنما يعني الرسول دون غيره، فيصير كصريح عبارته.
وهو الظاهر من قول علي - رضي الله عنه -: (أمرت أن أقاتل الناكثين والمارقين والقاسطين). فلا يظن به أن يقول: (أمرت) إلا إذا أمره الرسول - عليه السلام -، كأنه ذكر ذلك في معرض الحجة، فالمعروف منه مع جلالته وقدره أنه لا يتحج بقول غير الرسول، وكذلك الحكم في قول غيره من الصحابة رضوان الله عليهم أجمعين، إذا ظهرت منه هذه الحالة.
وقد يتفق أن يظهر من عالم عادة أخرى، وهو أن يطلق السنة على غير سنة الرسول [- صلى الله عليه وسلم -]، ويغلب ذلك على إطلاقه، فإذا صدر هذا من مثل','2','739',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1436,'[هذه] حاله، فلا وجه للتمسك. وهذا كما عرف [عن] مالك - رضي الله عنه - أنه يقول: من السنة كذا. ويريد ما استمر عليه عمل أهل بلده، فلا يكون هذا منه دليل خبر على حال.
المرتبة الخامسة: أن يقول: كانوا يفعلون، وأضاف ذلك إلى زمان رسول الله - صلى الله عليه وسلم -، ولم يرد أن ينقل عن الرسول وجميع الخلق، وإنما أراد فعل غير الرسول، فلا يصح أن يتمسك بذلك، بناء على أنه إجماع، إذ لا ينعقد الإجماع حجة إلا بعد موت رسول الله - صلى الله عليه وسلم -. وأما في حال حياته، فالاعتبار بقوله وفعله، فلا يكون إذًا نفس فعلهم حجة. هذا هو الذي قصده الإمام في هذا المكان.
ولكن هل ينزل الأمر على أن الراوي أراد ما فعلوه، وبلغ النبي - صلى الله عليه وسلم - فأقره، ولا يكون فيه إلا محض نقل الفعل؟ هذا موضع التردد. وظاهر احتجاج الراوي يدل على أنه كان ذلك يبلغ النبي - عليه السلام - فلا ينكره، إذ لو لم يكن كذلك، لما كان في النقل فائدة. وهذا هو الذي ذهب إليه أبو حامد وغيره.
واختار الإمام ترك هذا، لأنه لم يعين الوصل إلى الرسول. ولكن قد','2','740',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1437,'قال ابن عمر: (كنا نفاضل في زمان رسول الله - صلى الله عليه وسلم - فنقول: خير الناس بعد نبيهم أبو بكر، ثم عمر، فيبلغ ذلك رسول الله - صلى الله عليه وسلم - فلا ينكره). ويحتمل غير ذلك، وأنهم كانوا يفعلون ذلك رأيا منهم، فالأمر فيه مشكل.
أما إذا قال التابعي: كانوا يفعلون، وأضاف إلى زمان الصحابة، فهذا لا حجة فيه، إذ يحتمل أن يكون [أضاف] ذلك إلى جميع الصحابة أو إلى بعضهم، فهو تردد. فإن أضاف إلى جميع الصحابة، فهو نقل الإجماع على ألسنة الآحاد، وسيأتي الكلام عليه. وإن أضاف إلى البعض، فلا حجة فيه. وإذا دار بين أن يكون حجة، أو لا يكون حجة، فلا حجة فيه، لكن ينقدح فيه ما تقدم، وهو أن إيراده له في معرض الحجة، يدل على أنه فعل من يحتج بفعله، وهم أهل الإجماع. وهذا ليس بقاطع، إذ يحتمل أن يضيفه إلى','2','741',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1438,'العلماء، أو إلى الخلفاء، أو إلى أعمال مستمرة، فلا تثبت الأخبار بمثل هذا الوهم. وعلى الجملة الاحتمال في قول التابعي أظهر منه في قول الصحابي.
قال الإمام: (مسألة: إذا نقل الراوي العدل خبرا [عن شيخ]، فروجع الشيخ فيه فأنكره) إلى قوله (فلا فرق بين (١٥٣/أ) ذلك وبين تعارض قولين من شيخ وراوٍ عنه). قال الشيخ: ترجمة المسألة: إذا أنكر الشيخ الحديث.','2','742',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1439,'وهذه الترجمة لا يظهر منها أن الشيخ قال: لا أنكر الحديث، فإن الشاك ليس بمنكر، وإنما المنكر النافي. فكيف يصح للقاضي تنزيل كلام الشافعي على هذه الصورة التي لا يظهر اندراجها تحت اللفظ؟ اللهم إلا أن يكون قد روى له نصا على هذا، فيكون ذلك صرفا لظاهر المسألة عن بابها، وردًا لها إلى جهة تأويلها. ولعل القاضي إنما حمله على إيضاح وجه المسألة عنده، فلا يرى صورة الإنكار جزمًا محل خلاف، وحسن الظن بالشافعي، فاستبعد عنه أن يقول بالقبول في هذه الصورة. ولعمري إن الفقه يقتضي أن لا تقبل الرواية فيها.
وأما أبو حامد، فترجم المسألة ترجمة تخرج صورة الإنكار عن محل','2','743',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1440,'الخلاف فقال: إذا أنكر الشيخ الحديث إنكار جاحد، سقط التمسك به، ولم يجعل ذلك موضع خلاف. قال: أما إذا أنكره إنكار متوقف. قد بينا ضعفه، لأن المتوقف ليس بمنكر ولا معترف.
وقد تمسكوا بثلاثة أمور: أحدها: ما قدروه من انخرام غلبة الظن.
والثاني: القياس على الشهادة. وهذان الوجهان هما اللذان ذكرهما الإمام، وأجاب عنهما. وأمر ثالث: وهو أنهم قالوا: ليس للشيخ أن يعمل بالخبر، وهو الأصل، فكيف يعمل به غيره؟
أما الوجه الأول: وهو ادعاء فقدان غلبة الظن، فليس بصحيح، فإنه لا','2','744',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1441,'يعارض شك الشاك قطع غيره. وأي محدث يحفظ جميع ما سمعه؟ والنسيان ليس ببدع في الإنسان. فغلبة الظن حاصلة بصدق الفرع في النقل عنه، وليس بقاطع بالرد.
وأما الاعتبار بالشهادة، فيجاب عنه بأوجه: منها - أن القواعد لا سبيل إلى اعتبار بعضها ببعض. قالوا: إنما هذا اعتبار في التفصيل، بعد تقرير كل أصل، ولا يمتنع إجراء القياس في التفاصيل بعد تمهيد الأصول. قالوا: ولا نظر إلى اشتمال الشهادة على تعبدات، كالحرية والذكورية والعدد، فإنهما - مع افتراقهما في هذا - مشتركان فيما يتعلق بغلبة الظن، وما يقدح فيها، فهو مانع في القاعدتين جميعا. والجواب عن هذا ما قررناه من أن الشك من الشيخ لا يمنع','2','745',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1442,'من حصول غلبة الظن بقول العدل.
وقولهم: إن هذا إنما قدح في الشهادة، بالنظر إلى إبطال غلبة الظن، ليس بصحيح، بدليل ما قررناه من بقاء غلبة الظن، وإنما قدح، من جهة أن نفس وجود هذا الأصل يمنع من قبول شهادة الفرع، إذا أمكنت مراجعة هذا الأصل. فإذا كان إمكان مراجعته يمنع من قبول شهادة [الفرع]، فما الحال إذا روجع فأنكر؟ ولعمري إن إنكاره في هذا المكان غير محتاج إليه، إذا كان وجوده مانعا.
وأما ما ذكره الإمام من تقدير امتناع مراجعة الأصل في باب الرواية، فثابت معلوم، فإنه قد كان الصحابة يحضرون في مجلس واحد، أو يأتي بعضهم بعد مُضي شيء من القصة، فيسأل بعضهم بعضًا عما قاله الرسول عليه السلام','2','746',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1443,'فيها بمحضر الرسول. وقد جاءت في هذه أخبار كثيرة. منها ما روي في الحديث: (أنه لا تقوم الساعة حتى يكون اثني عشر خليفة، وأسر كلمة خفية، فقلت: ما قال؟ قال: (كلهم من قريش). (وكان عمر - رضي الله عنه - قد (١٥٣/ب) اتفق مع رجل على أن يأتيه بخبر الوحي يوما، ويأتيه الآخر بالخبر يوما آخر). هذا معناه دون لفظه. والأحاديث في ذلك كثيرة.
وأما الوجه الثالث: وهو كون الشيخ ليس له أن يعمل بالخبر، فكيف يعمل به غيره؟ قلنا: لسنا نسلم ذلك على الإطلاق، بل للشيخ حالتان:
إما أن يكون قول الراوي عنه يحرك له ظنا بالرواية، أو يبقى على شكه بعد أن زاوله غيره. فإن ظن صدقه، وجب عليه العمل به، وإن لم يذكر حقيقة الرواية، لكن صدق الراوي.
وأما إن بقي على شكه، فيمتنع عليه العمل به، لوجدان الشك المانع من ظن الصدق. وإنما يعمل بالأخبار إذا غلبت على الظن، فيعمل من غلب على ظنه الصدق، ولا يعمل غيره. وهذا لا تناقض فيه، وهو بمثابة ما لو روى عدل لرجلين: أحدهما يعرف عدالته، والآخر لا يعرفها، لكان حكم الله عز وجل على أحدهما وجوب العمل، وعلى الآخر منعه.','2','747',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1444,'هذا تقرير هذه الأوجه، ولكن يبقى وجه آخر: وهو أن قائلا لو قال: إنما وجب العمل بأخبار الآحاد، بناء على التواتر والإجماع. وذلك إنما هو في أخبار لم ينكرها الأصوليون. فأما هذا الضرب من الأخبار، فليس فيه إجماع. فانتفاء الدليل القاطع على القبول دليل. وهذا يقوى جدا على مذهب القاضي. وكذلك الإمام حيث يقول: لا تثبت أصول الشريعة إلا بمستند قطعي. وههنا لا قطع بحال. وليس يسلك مسلك القياس على محل الاتفاق. فلا يصح لوجهين:
أحدهما - منع القياس فيما يتعلق بأصول الشريعة.
والثاني - انقداح الفرق بإبداء التفاوت في غلبة الظن.
وهذا كلام واقع. ولكن طريق الجواب أن نقول: أما اشتراط القطع في تفاصيل القاعدة فغير مشترط، وقد تقدم كلامنا على ذلك بما يغني عن إعادته. فأما إبطال القياس على الموضع المتفق، فلسنا نقتصر على ذلك،','2','748',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1445,'ولكنا نقول: لما استقرئت الوقائع في العمل بأخبار الآحاد، وتفاوت أحوال الرواة في غلبات الظنون، مع القطع في الاشتراك في أصلها، فإنه قد قبلت رواية الصديق - رضي الله عنه -، مع أنه (لو وزن إيمانه بإيمان العالمين لرجح). وقبلت رواية غيره ممن لا يساويه ولا يقاربه ولا يدانيه. فلا جامع يجمع هذه الأطراف، إلا حصول غلبة الظن.
وإن وقع تفاوت في هذه الصفات، فذلك معتمد في الترجيحات، وأما من يعتبر الأقوى في أصل العمل، لا بالنظر إلى معارض، فمحال، باستقراء أحوال أهل الإجماع. هذا هو الكلام على إحدى الصورتين، وهو إذا توقف الشيخ، ولم يجزم بالإنكار.
أما إذا جزم بالإنكار فالنظر في طرفين: أحدهما - في بقاء العدالة. والثاني - في العمل بالرواية. أما العدالة فثابتة لهما جميعا، إذ هو مكذب لشيخه، كما أن شيخه مكذب له، فهما كبينتين متعارضتين. على أنا قد حققنا أن الجرح لا يثبت بقول وحد، فلا سبيل إلى جرح واحد منهما. وأما العمل، فالذي يقتضيه التحقيق أنه لا يعمل بالرواية بحال.','2','749',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1446,'وأما قول الإمام: (إنهما كبينتين متعارضتين). فلم يصر إليه أحد من الأصوليين فيما علمته، إلا ما يظهر من كلام (١٥٤/أ) الشافعي في ترجمة المسألة. وتلك الترجمة لا تقتضي هذا التقسيم الذي صار إليه الإمام. فإن الترجمة أن الخبر معمول به. وهذا الكلام فيه تفصيل، تارة يعمل به، وتارة يطرح. ولم نر لأحد من الأصوليين موافقة هذا القول بوجه.
ثم القول الحق أن هذا لا يتنزل منزلة بينتين متعارضتين، لاستقلال كل بينة بطريقها. وههنا وقع الرافع في الطريق الواحد. فإن تحقق أن الشيخ لم يرو الحديث، فكيف يسند عنه؟ وإن ثبت أن الراوي سمعه منه، فهو مصرح بأنه لا يبلغه إلى الرسول - صلى الله عليه وسلم -. فلا سبيل إلى قبول هذه الرواية بحال.','2','750',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1447,'قال الإمام: <span data-type="title" id=toc-247>(فصل - في كيفية الرواية وتفصيلها، وما يقبل منها وما يرد. مسألة: ما ذهب إليه معظم الأصوليين) </span>إلى قوله ([إن] كان يتوقع من الناقل زللا [إن] ترجم). قال الشيخ: هذه المسألة لها ثلاث صور:
الصورة الأولى - أن يبدل اللفظ بما يرادفه، كالجلوس بالعقود، والاستطاعة بالقدرة، والعلم بالمعرفة، وما يضاهي ذلك. فلا خلاف فيه بين','2','751',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1448,'الأصوليين. ولا تتنزل ألفاظ السنة منزلة ألفاظ الكتاب، لأن في القرآن مقاصد منها: فهم الأحكام، ومنها تحقيق الإعجاز. وهذا لا يتم إلا بنقل صورة اللفظ على ما هو عليه، والمقصد من السنة فهم الأحكام.
وإن كان - عليه السلام - في أقصى الفصاحة، فليست فصاحته مما تحدى بها. فالنظر في السنة فهم الأحكام خاصة، إلا أن يثبت تعبد في عين اللفظ، وهذا كألفاظ التشهد والتكبير والتسليم وألفاظ التلبية، وما يضاهي ذلك.
[الصورة الثانية]- وهي على الضد من الأولى: اللفظ الذي يظن أنه يدل على مثل ما دل عليه الأول، من غير أن يقطع بذلك. فهذا لا خلاف أنه لا يجوز التبديل فيه في هذه الصورة، فإنه لا يتعين أن يكون ظن الناس كلهم ظنًا واحدًا. وقد يظن إنسان شيئًا، ويظن غيره غيره، وهذا هو الأكثر.','2','752',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1449,'الصورة الثالثة - أن يقطع بفهم المعنى، ويعبر عما فهم بعبارة يقطع بأنها تدل على ذلك المعنى الذي فهمه، من غير أن تكون الألفاظ مترادفة. فهذا موضع الكلام. اعتمد الأكثرون على حصول القطع بفهم المعنى مستندًا، إما إلى اللفظ بمجرده، أو إليه مع قرائن الأحوال. فإذا قطع بالفهم، وعبر عنه بالعبارة الدالة عليه قطعًا، التحق ذلك بالإضافة إلى الألفاظ المترادفة.
وهذا هو الصحيح. ومعتمد الآخرين: أن الألفاظ إذا كانت غير مترادفة، فلا التفات لقطع المترجم بفهم المعنى، إذ قد يعتقد ما ليس بعلم علمًا. وهذا يجري في النظريات كثيرًا. وهذا الذي قاله هؤلاء القوم ليس بشيء، وإن قلنا ذلك، لزمنا أن نمنع في الألفاظ المترادفة، وهي محل اتفاق.','2','753',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1450,'الثاني - أنه كان يلزم أن يقع التجويز إذا قطع بالفهم ضرورة، مستندًا إلى قرائن الأحوال، ولا يشترط الترادف على حال. وقد جنح بعضهم إلى التعبد في الباب، تنزيلاً لألفاظ السنة منزلة ألفاظ الكتاب. وهذا يبطل بالألفاظ المترادفة التي هي محل الإجماع.
وأما ما ذكره من كون الصحابة يحضرون مجلسًا واحدًا وخطبة واحدة، ثم ينقلونها (١٥٤/ب) بالألفاظ المختلفة، فذلك إجماع منهم على جواز النقل بالمعنى. ففيه أسئلة:
أحدها - أن ذلك لا يصح عن جميع أهل الإجماع، إذ ما اجتمعوا على خطبة واحدة قط، وانفراد البعض بهذه القضية، لا يكون إجماعًا، اللهم إلا أن ينقل عنهم في وقائع متعددة، حتى يكون الذين لم يحضروا هذه الخطبة حضروا غيرها ونقلوا على المعنى.','2','754',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1451,'ويفتقر أيضًا إلى أن يثبت اتحاد الخطبة. أما إذا أمكن أن تتكرر الخطبة مرتين أو أكثر، فلا يدل ذلك على جواز النقل على المعنى. وهذا كله إذا لم يكن النقل على المعنى بالألفاظ المترادفة، أما إذا كان بها، فلا يحتج على القوم به، لأنهم لا يسلمونه. وقد تمسك بقوله - عليه السلام -: (نضر الله أمرءًا سمع مقالتي فوعاها فأداها كما سمعها). فما ذكره الإمام صحيح، والظاهر أن النبي - عليه السلام - إنما أراد بذلك من لا يحيط بفهم المعنى، ولذلك قال: (فرب حامل فقهٍ غير فقيه، ورب حامل فقهٍ إلى من هو أفقه منه). هذا هو الظاهر.
وأما الجهات التي تمسك بها الإمام، من جهة شرح الشرع للعجم بلسانهم. فإنه يرجع إلى الألفاظ المترادفة، وهو موضع اتفاق بين الناس، فلا وجه للاحتجاج به، إذ القوم لم يمنعوا التعبير والترجمة بالألفاظ المترادفة.
قال الإمام (مسألة: من سمع حديثا مشتملا على أحكام) إلى قوله (وبه يتم غرض المسألة). قال الشيخ: ما ذكره الإمام في أحد القسمين، وهو','2','755',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1452,'[ما] إذا كان للمسكوت عنه تعلق بالمنطوق به، فكان الاقتصار على أحدهما يخل بالفهم، بالإضافة إلى المروي، فلا يختلف في عدم جوازه. أما إذا كان لا تعلق لأحد الحكمين بالآخر، فهذه المسألة لها صور ثلاث: أحدها - أن يعلم التقاطع ضرورة. والثاني - أن يعلم نظرا. والثالث - أن يظن التقاطع من غير قطع.
فأما القسم الأول: أن يختلف فيه، والاختلاف فيه أبعد من الاختلاف في جواز نقل الحديث بالمعنى، إذ قد يتخيل المتخيل أن من نقل بالمعنى، لم يرو جواز نقل الحديث بالمعنى، إذ قد يتخيل المتخيل أن من نقل بالمعنى، لم يرو كما سمع. أما إذا نقل بعض الأحكام بغير لفظ الرسول - صلى الله عليه وسلم -، فهذا مندرج تحت الخبر من كل وجه. وما أرى المانعين لذلك إلا منعوه عند ما يمكن أن يكون','2','756',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1453,'لأحدهما تعلق بالآخر، أو يكون حسما للذريعة وسد للباب، حذرًا من الإفضاء إلى موضع الإشكال. وأما إن ظن التقاطع، فلا يجوز الاقتصار بحال. وإن علم ذلك بنوع من النظر، جرى على ما ذكرناه من جواز نقل الحديث بالمعنى. إذا كان يعلم ذلك نظرا. وقد مضى من الكلام ما فيه كفاية.
والصحيح عند العلماء بالحديث والأصول وأهل الفقه جواز الفصل. وقد جاء الحديث الطويل في صفة حج رسول الله - صلى الله عليه وسلم -، ساقه جابر بن عبد الله سياقا واحدًا، منذ خرج رسول الله - صلى الله عليه وسلم - من المدينة إلى أن فرغ من حجه. ذكره على هذا السياق مسلم، وأبو داود. وجزأه مالك بن أنس رحمه الله، والبخاري، وأبو عيسى الترمذي على الأبواب. وهذا هو الصحيح.','2','757',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1454,'قال الإمام: (قال الشافعي رحمه الله: نقل بعض النقلة عن ابن مسعود) إلى قوله (فهذا أحد الخبرين).
قال الشيخ: هذا الذي ذكره الإمام ضعيف، (١٥٥/أ) والذي تردد فيه الشافعي موضع تردد، فإنه إن نظر إلى المنصوص عليه، فهو ثابت لا إشكال فيه. وإن التفت إلى إيهام الاكتفاء، فالإيهام حاصل، قصد الراوي بيان حكمٍ بحاسة الرؤية، أو غير ذلك. والذي أراه في هذا صحة الاقتصار. وأما الخبر','2','758',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1455,'الثاني المتعلق بطلب الثالث، فيجب نقله لا بالنظر إلى افتقار المروي إليه، لكن بالإضافة إلى الحاجة إلى ذكر الخبر، ليتلقى منه حكمه.
فهذا هو الظاهر عندنا. وتوهم الإنسان أن الشيء إذا روي، فلا يزاد عليه، غلط. وهو مصير إلى إثبات مفهوم الألقاب. وقد مر في المفهوم قول بالغ. فلئن قيل: هذا من باب مفهوم العدد. قلنا: الصحيح عندنا إسقاط القول بالمفهوم، اقتصارا على طلب فوائد التخصيص.
قال الإمام: (الخبر الثاني من هذا القبيل) إلى قوله (فهذا منتهى القول في','2','759',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1456,'ذلك). قال الشيخ: كلام الشافعي والإمام ههنا واضح، لا زيادة عليه.','2','760',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1457,'قال الإمام: (مسألة: إذا روى طائفة من الأثبات [خبرًا] إلى قوله (في تحقيق الثقة). قال الشيخ: انفراد الثقة بزيادة تقع على ثلاثة أوجه مختلفة:
أحدها - أن يتبين عندنا تعدد المجالس، فلا يجتمع مع النقلة في مجلس واحد، فيحضرون في مجلس، ويحضر المنفرد بالزيادة في غيره. فهذا لا يتصور أن يكون في [قبوله] خلاف. وكذلك الحكم إذا التبس الأمر، ولم يتبين الاجتماع في مجلس واحد.','2','761',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1458,'الصورة الثانية - أن يتحد المجلس، وينقل بعضهم الحديث بغير الزيادة، وينقل بعضهم الزيادة، ولم يصرح أولئك القوم بنفيها، فهذا موضع الخلاف. والصواب عندنا التفصيل. فإن كان هذا الراوي ثقة، ولم يشتهر بنقل الزيادات في الوقائع مع اتحاد المجلس، وإنما كان ذلك منه على طريق الشذوذ، فهذا عندنا مقبول. وهذا كما روى الناس (أن النبي - صلى الله عليه وسلم - فرض زكاة الفطر من رمضان على كل حر أو عبد ذكر أو أنثى). وروى مالك بن أنس ما رووه وزاد فيه: (من المسلمين). فقبل ذلك منه وعمل به. وكذلك ما يضاهي ذلك، فهذا مقبول عند المحققين.
الصورة الثالثة - أن يشتهر من بعض الرواة الانفراد عن الحفاظ بكثرة الزيادات مع اتحاد المجلس، وامتناع الامتياز بسماع، فهذا مذهب الأصوليين قبول زيادته، ومذهب المحدثين الذين عرفناهم رد روايته، وذلك يقتضي تهمة','2','762',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1459,'في منصبه، فإن من يعمد إلى مثل الزهري في جلالته وكثرة أصحابه، والاعتبار منهم بحفظ أخباره، ثم يفترقون عن مجلسه، فينفرد واحد على الدوام بالزيادة على أصحابه، فهذه تهمة في حقه تمنع من قبول روايته فيما انفرد به.
فإن أراد أبو حنيفة هذه الصورة، فهي مسلمة له، وإن أراد غيرها، فقوله مردود عليه، والظاهر أنه أراد الصورة التي قبلها، ولذلك لم يشترط الإسلام في زكاة الفطر عن المملوك. والصحيح الاشتراط.
قال الإمام: (ثم [قال] الشافعي: من متناقض القول) إلى قوله (إن','2','763',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1460,'فرض الاطلاع عليه تحقيقا). قال الشيخ: ما ذكره الشافعي من التناقض، لا يقوى عندي تحقيق التناقض في ذلك. أما انفراد الثقة بزيادة، فقد تكلمنا عليه. وأما الرواية (١٥٥/ب) الشاذة فلا يظن كذب ناقلها بحال. وكيف يظن ذلك، والذين رووا الروايات الشاذة إذا جمع نقلتها كانوا زائدين على عدد التواتر؟ [فلا] سبيل إلى تكذيب الجميع. وقد كنا قلنا: إن كلام أبي حنيفة إنما ينزل على ما إذا اتحد [المجلس]، ولسنا نتبين أن راوي الرواية الشاذة كان مجتمعا مع خلق كثير، حتى انفرد بالنقل عنهم، ولعله سمع وحده، أو تكون القضية كانت مشهورة متواترة عند الجميع، ثم جاء من الشرع ما يمنع من تلك الرواية، فاطلع جمع عليها، فتركوا القراءة بها، ولم يسمع آخرون المنع، فبقوا على التلاوة. وسنزيده تقريرا في المسألة التي بعدها. فإذًا لا تناقض على الحقيقة.
وأما قول الإمام: (إنه إذا صرح الآخرون بالنفي، فهذا تناقض). فهذه','2','764',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1461,'المسألة قد اختلف الفقهاء فيها، إذا اتحد المجلس، وأثبت قوم، ونفى آخرون، فقال قائلون: هو تعارض، فينظر إلى أعدل البينتين. وقال آخرون: بل الإثبات مقدم. وهذا هو الأغلب على الظن عندنا.
وتحقيقه أنه إذا لم يكن بد من تطرق الوهم إلى أحدهما، لاستحالة صدقهما، وامتنع الحمل على تعمد الكذب، لم يبق وجه مع تقدير العدالة إلا الذهول والنسيان، والعادة ترشد إلى أن نسيان ما جرى، أقرب من تخيل ما لم يجر جار. وإذا كان كذلك، تحقق أن اعتبار قول المثبت أولى. والله المستعان.
قال الإمام: (مسألة: كل أمر خطير ذي بال) إلى قوله (ثم لابد أن يتواتر نقيض ما [تفرد] المنفرد بنقله). قال الشيخ: عموم البلوى على','2','765',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1462,'وجهين: عموم بلوى الفعل، وعموم بلوى في النقل. فأما ما تعم البلوى بنقله، إذا أطبق عليه عدد التواتر، فلابد من تواتر النقل فيه، وذلك فيما يجل خطره، ويعظم أمره، فإن النفوس متوفرة على نقله. وقد قدمنا ذلك في باب ما يجب أن يتواتر، وقسمناه قسمين: فمنه ما يتواتر على الفور، ثم يفضي الأمر إلى نقل الآحاد، وقد يفضي الأمر إلى الدروس. وقسم يدوم تواتر النقل فيه، وقد سبق تمثيل القسمين معا.
وأما ما ذكره أبو حنيفة فيما تعم بفعله البلوى، ولا ينفك الخلق عن تعاطيه، وهاذ كالمس واللمس والبول والغائط، فقد قال أبو حنيفة: لا نقبل خبر الواحد في هذا المصنف، واحتج بأنه لابد أن يلقيه الشارع للخلق، على وجه يحصل به العلم، ثم لابد من أن ينقله من يحصل العلم بنقله، حتى يبقى حكمه معلوما على الدوام. وهذا الذي قاله غير صحيح، ولا يوجب العقل أن يبلغ الشرع أصل الصلاة والوضوء لعدد التواتر، بل يجوز أن يتعبد الله الخلق بالعمل بأخبار الآحاد في الصلوات الخمس، فكيف بتفاصيل ما ينقض الطهارة؟
فإن قيل: كيف يجوز ذلك، وفيه تعريض صلوات الخلق للإبطال، وذلك','2','766',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1463,'غير جائز، قلنا: قد حققنا أن العقل لا يدل على إثبات أصول الأحكام، ولا تتحتم بعثة الأنبياء، وإنزال الشرائع، وإنما حظ العقل في هذا التجويز. فلا مانع من أن يتعبد الله سبحانه خلقه بالعمل بأخبار الآحاد فيما يعم فعله. وإذا كان ممكنا من جهة العقل، ولم يوجد في قواطع السمع ما يمنع منه، فلا وجه لرد خبر الواحد.
ونقول: [كل ما] رواه العدل وصدقه فيه غالب (١٥٦/أ) على الظن، ولم يتعبد الخلق فيه بالعلم، فلابد فيه من التصديق، وهذان أمران: ففي أيهما النزاع؟ أما كون الصدق غالبا على الطرفين، فإن الصديق أو غيره إذا روى: (ومن مس الذكر الوضوء). فلا يقطع بكذبه قطعا، بل الظن حاصل، والمحل من تفاصيل الفروع، فلا يشترط العلم فيه، فلا بعد في أن يذكره الرسول للآحاد. ولو قدرنا أنه ألقاه لعدد التواتر، فليس هذا من قبيل ما تتوفر الدواعي على نقله. ففرق بين أن تعم البلوى بالفعل، وبين أن تتوفر الدواعي على النقل.','2','767',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1464,'فهذا القسم الذي يتواتر، إن فرض أنه اطلع عليه عدد التواتر. وليس حكم مس الذكر مما يلزم أن يذكره الشارع لعدد التواتر، ولو اتفق ذكره له عند العدد الكامل، بل قد تقدم لنا ما هو أظهر من هذا.
وقد قبل أبو حنيفة خبر الواحد في الفصد والحجامة، وإن كان تعم البلوى بفعله، ولا يتأتى الانفكاك عنه. فإن قالوا: ليس عموم البلوى في الفصد كعموم البلوى في المس واللمس، فعنه جوابان:
أحدهما - أنه وإن لم يكن عمومه كعمومه، إلا أنه يحذر أن تعرض صلاة الكثير للإبطال، كما يحذر صلاة الأكثر، فلم لم يشع حكمه لولا التعبد؟ وما كان ليخالف أمر الله عليه.','2','768',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1465,'الثاني - أن عموم البلوى بالإضافة إلى مس الذكر، ليس كعمومه بالإضافة إلى مس النساء، ومع ذلك فقد سوى أبو حنيفة بينهما، دل على أنه لا التفات للمساواة في عموم البلوى.
فإن قيل: فهل من ضابط بين ما تعبد فيه بالإشاعة، وما لم يتعبد فيه بذلك؟ قلنا: لا ضابط لذلك من جهة العقل، فإن العقل يجوز ذلك ونقيضه في الجميع، ويجوز الفصل والفرق. وإن طلب ضابط [للوقوع]، فنحن قد وجدنا السمعيات منقسمة إلى ما تواتر، وإلى ما لم يتواتر. وهذان القسمان يقعان في العبادات والمعاملات والجنايات والأقضية وفصل الخصومات. واستقصاء ذلك هو علم الفقه، فلا معنى للإطناب فيما يتعذر استيفاؤه ههنا.
قال الإمام: (مسألة: ظاهر مذهب الشافعي أن القراءة الشاذة التي لم تنقل تواترا، لا يسوغ الاحتجاج بها) إلى قوله (وقد نجزت مسائل الأخبار). قال الشيخ: هذا المكان أمر عظيم وخطب جسيم، فإن القرآن قاعدة الدين، وإليه رجوع جميع الشريعة، فليقع به فضل اعتناء. والله المستعان.','2','769',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1466,'فنقول: اختلف الناس في العمل بالقراءة الشاذة، فالمشهور من مذهب مالك - رضي الله عنه - ومذهب الشافعي - رضي الله عنه - أنه لا تجوز القراءة بها، ولا يتلقى منها حكم. وذهب أبو حنيفة رحمه الله إلى أنه يصح أن يتلقى الحكم منها، لأنها إن لم يثبت كونها قرآنا، فلا أقل من كونها سنة. وأثبت الشافعي البسملة في أول الحمد وكل سورة، فإن كان نقلها قرآنا، لم تتواتر. فهاتان مسألتان نتكلم على كل واحدة منهما.
أما التتابع المذكور في قراءة ابن مسعود، فقد سلك الأصوليون في إبطال التمسك به مسلكا نذكره ونعترض عليه. قالوا: قد قررنا أن كل أمر خطير','2','770',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1467,'ذي بال، يقتضي العرف نقله متواترًا. فإذا انفرد بنقله آحاد، فهم مكذبون قطعًا، إما سهوًا أو غير ذلك. قالوا: (١٥٦/ب) والقرآن قاعدة الدين، ولا شيء تتوفر الدواعي على نقله توفرها على نقل القرآن، فهو القطب، وإليه المرجوع، فإذا انفرد واحد بنقله، لم يصدق قطعا. هذا إن عزاه إلى القرآن، وإن لم يعزه إلى القرآن، احتمل أن يكون خبرا، ويحتمل أن يكون مذهبا. وإنما يجب العمل بما صرح الراوي بكونه خبرا. أما العمل بناء على احتمال الخبر، فباطل.
قالوا: وأيضا فإن الصحابة أجمعت في زمن عثمان - رضي الله عنه - على جمع ما بين الدفتين واطرحوا ما سواه، فهو متروك بإجماع. وهذان دليلان قاطعان يمنعان التمسك بتلك الزيادة، وهي القراءة الشاذة. وقد تقدم بيان أن ما خالف حكم الاعتياد في الأخبار أنه باطل. وسنفتح إثبات كون الإجماع حجة إن شاء الله. هذا تمام كلام الأصوليين. وهذا الذي قالوه غير صحيح عندنا.
أما الأول: وهي الطريق التي يقضى فيها بكذب هذا النقل، بناء على حكم العادة، فذلك غير صحيح، ولم يذهب أحد من سلف الأمة، ولا من خلفها إلى القطع بكذب رواة الرواية الشاذة، فلا أحد يقول إن ابن مسعود لم يصدق في نقل قراءته، وإنما اختلاف العلماء في القراءة بها، وجواز بناء الأحكام عليها أم لا؟ أما الكذب، فلا ذاهب إليه.','2','771',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1468,'وأيضًا فإن ابن مسعود لم ينفرد بالرواية الشاذة، بل أكثر الصحابة قد نقل عنهم ذلك، وقد قرأ عمر قراءة، وأبي وزيد وأبو الدرداء وابن مسعود وابن الزبير، فكل قد قرأ على غير ما اشتمل المصحف عليه، وكذلك التابعون، الحسن وغيره.
وعلى الجملة فنقل القراءة الشاذة معلوم أيضًا، وأن رواة الحروف، وإن','2','772',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1469,'لم يتفقوا على نقل خبر معين، إلا أنهم بلغوا من الكثرة مبلغا، لو اتفقوا على نقل قضية واحدة، لتواترت بنقلهم، وسبيل العلم بالرواية الشاذة، سبيل العلم بجود حاتم، وشجاعة علي [- رضي الله عنه -]، فلا يتصور التشكيك فيه بحال، فكيف يتصور مع هذا القطع بالتكذيب؟
وقد اتفق علماء هذا الفن - أعني القراء - على نفي تكذيب من روى الرواية الشاذة، وإنما مما يغلب على الظن، وإنما تكلموا في جواز القراءة والعمل. قال مكي في كتاب (الإبانة عن معاني الحروف) قال: (جميع ما روي من القراءات على ثلاثة أوجه:
[القسم الأول]: يقرأ به اليوم، وذلك ما اجتمع فيه ثلاث خلال: وهو','2','773',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1470,'أن ينقل عن الثقات إلى رسول الله - صلى الله عليه وسلم -، ويكون وجهه في العربية التي نزل بها القرآن [سائغًا]، ويكون موافقًا لخط المصحف. وإذا اجتمعت فيه هذه الخلال الثلاث، قرئ به، وقطع على [مغيبه]، وصحته وصدقه، لأنه أخذ عن الإجماع، من جهة موافقته للمصحف، وكفر من جحده.
والقسم الثاني: ما صح نقله عن الآحاد الثقات، وصح وجهه في العربية، وخالف لفظه خط] المصحف، فهذا يقبل، ولا يقرأ به لعلتين:
إحداهما - أنه لم [يؤخذ] بإجماع، وإنما أخذ بأخبار الآحاد، ولا يثبت قرآن يقرأ به بخبر الواحد.
والعلة الثانية - أنه مخالف لما أجمع عليه، فلا يقطع على [مغيبه] وصحته، وما لم يقطع على صحته، لم تجز القراءة به، ولا يكفر من جحده، وبئس ما صنع [إن] جحده.
والقسم (١٥٧/أ) الثالث: ما نقله غير ثقة، ولا وجه [له] في العربية.','2','774',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1471,'فهذا لا يقبل، وإن وافق خط المصحف، ولكل صنف من هذه الأقسام تمثيل تركنا ذكره اختصارًا).
فانظر كيف قال في القسم الثاني: ولا يقرأ به، إذ القراءة عمل، ولم يثبت العمل به. قال: ولا يكفر جاحده، وبئس ما صنع، لأنه كذب العدل الناقل. فهذا تصريح من أهل هذا الفن بحصول غلبة الظن بالصدق في القراءة الشاذة.
وقال إسماعيل القاضي في كتاب (القراءات) له: (إن عمر بن الخطاب قرأ: (غير المغضوب عليهم وغير الضالين). قال: هذا - والله أعلم - على ما جاء أن القرآن أنزل على سبعة أحرف.
ثم قال إسماعيل: ليس ينبغي لأحد اليوم أن يتعمد القراءة بهذا وما أشبهه، يريد مما يخالف خط المصحف. قال إسماعيل: لأن هذا، وإن كان في الأصل [جائزًا]، إلا أنه إذا فعل ذلك، رغب عن اختيار أصحاب رسول الله حين اختاروا أن يجمعوا الناس على مصحف واحد، مخافة أن يطول بالناس زمان يختلفون في القرآن. ثم قال إسماعيل: وإذا اختار الإنسان أن يقرأ ببعض القراءة التي رويت، مما يخالف خط المصحف، صار إلى أن [يأخذ] القراءة','2','775',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1472,'برواية الآحاد، وترك ما [تلقاه] الجماعة عن الجماعة، الذين هم حجة على الناس كلهم، يعني خط المصحف.
قال إسماعيل: وكذلك ما روي من قراءة ابن مسعود وغيره، وليس ينبغي لأحد أن يقرأ به اليوم، يعني [مما] يخالف المصحف. قال إسماعيل: لأن الناس لا يعلمون علما يقينا أنها قراءة عبد الله، وإنما هو شيء يرويه بعض من يحمل الحديث، يعني أن ما خالف المصحف من القراءة، فإنما يؤخذ بأخبار الآحاد. وما وافق خط المصحف منها، فهو [يقين] بالإجماع على المصحف.
قال إسماعيل: فلا يجوز أن يعدل عن اليقين إلى ما لا يعرف بعينه، يعني أنه لا يجوز [أن يعدل] عما وافق خط المصحف الذي هو يقين، إلى ما يخالف خطه [مما] لا يقطع على صحته. قال إسماعيل: فإن جرى شيء من ذلك على لسان الإنسان، من غير أن يقصد له، كان له في ذلك سعة، إذا لم يكن معناه يخالف [معنى] خط المصحف المجمع عليه، فيدخل ذلك في معنى ما جاء: (أن القرآن أنزل على سبعة أحرف).
فهذا كله من قول أئمة هذا الشأن، يدل على أنه لا سبيل إلى تكذيب نقلة القراءة الشاذة، وإنما الكلام في جواز القراءة والعمل.','2','776',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1473,'فإن قيل: فما الانفصال عن قول من تمسك بالعادة المقتضية للإشاعة في تكذيب المنفرد بالنقل، جريا على أحكام الاعتياد؟ قلنا: عنه جوابان:
أحدهما - أن يكون النبي - صلى الله عليه وسلم - يذكر تلك القراءة لعدد التواتر، لكونه لم يتعبد بذلك، وإذا لم يطلع عدد التواتر على ذلك، استحال أن يصير الأمر يتواتر بعد ذلك، فبقي على نقل الآحاد.
الثاني - أن يكون لما أجمع الصحابة على المصحف، وأضربوا عما وراءه، لما فهموه من رسول الله - صلى الله عليه وسلم -، لم تتشوف النفوس إلى إدامة النقل، لامتناع تلقي الحكم ومنع التلاوة. وقد كنا قد بينا أن التلقي إنما يدوم على تقدير دوام [هذه] الحاجة، وهذا الطريق.
ننقل الآن آيات منسوخة يذكر رواتها أنها كانت قرآنا، ولم تتواتر الآن إلينا، ولم نكذب النقلة، لانصراف الهمم عن النقل بعد ورود النسخ.
منها: (الشيخ والشيخة إذا زنيا فارجموهما البتة نكالا من الله).
وكذلك: (لو كان لابن آدم واديان من ذهب لابتغى لهما ثالثا، ولا يملأ جوف (١٥٧/ب) ابن آدم إلا التراب، ويتوب الله على من تاب).
وقول عائشة رضي الله عنها: (كان فيما أنزل: (عشر رضعات محرمات)، فنسخن بخمس، فمات رسول الله - صلى الله عليه وسلم - وذلك مما يتلى). وكل هذا لم يتواتر،','2','777',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1474,'ولم يكذب النقلة فيه، لانصراف النفوس عن استدامة النقل، لأجل ورود النسخ، فيكون الانكفاف عن نقل القراءة الشاذة تواترا، إجماع الصحابة على الإضراب عنها.
فإن قيل: فما قولكم في جواز القراءة بها، وتلقي الأحكام منها؟ قلنا: الصواب منع ذلك، والدليل عليه: إضراب أصحاب رسول الله - صلى الله عليه وسلم - عن ذلك، وهم متوافرون من غير إبداء نكير.
فإن قيل: فهل تقطعون بذلك أم تظنونه؟ قلنا: الصحيح عندنا أن ذلك مظنون غير بالغ مبلغ القطع.
فإن قيل: فالإجماع حجة قاطعة، فكيف يكون الحكم مظنونا؟ قلنا: لم يتحقق إجماع الجميع، إذ ابن مسعود قد خالف في القضية وقال: (يا أهل الكوفة غلوا مصاحفكم، ومن يغلل يأت بما غل يوم القيامة). وإجماع الأكثر ليس بحجة عند المحققين.
فإن قيل: فما مستند الظن، وقد خرج الإجماع من أيديكم، فهذا ظن بلا مستند؟ قلنا: لما شبب ابن مسعود بنكير، أدبه الخليفة الأدب الشديد بمشهد','2','778',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1475,'المهاجرين والأنصار، ولم يبد أحد نكيرا، [فدل] هذا دلالة ظاهرة على أن الأمر كان عندهم مقطوعا به في الحصر على ما في المصحف، وأنهم كانوا لا يرون الإنكار في مسائل الاجتهاد بحال، فكيف تكون المسألة اجتهادية، وقد آل الأمر إلى هذا الحال العظيم من الضرب الوجيع، لمثل ذلك الحبر الفاضل العظيم؟
فالمسألة بالغة في القوة في غلبات الظنون، وما انتهى الأمر إلى القطع على حال. ولهذا كان الظاهر من مذاهب العلماء ترك القراءة الشاذة، تلاوة وعملا، ولا يتلقى منها حكم بحال، ولا يقطع بخطأ المخالف، ولهذا قال الإمام: ظاهر مذهب الشافعي، فقد صرح بأن المسألة اجتهادية. وكذلك مذهب مالك. فلا قطع بحال، والغالب على الظن ما ذكرناه.
وأما مسألة البسملة: فقد قال مكي بن أبي طالب: إن الإجماع من الصحابة والتابعين [على] أنها ليست آية إلا من سورة النمل. وإنما اختلف القراء في إثباتها من أول الفاتحة خاصة، والإجماع قد حصل على ترك عدها آية من كل سورة، فما حدث بعد الإجماع من الصحابة والتابعين من قول منفرد محدث، فقوله مرفوض غير مقبول.
وأيضا فقد أجمع أهل العدد من أهل الكوفة والبصرة والمدينة والشام على ترك عدها آية في أول كل سورة. فهذه حجة قاطعة، وإجماع ظاهر. وإنما','2','779',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1476,'اختلفوا في عدها وتركها في سورة الحمد، لا غير، فعدها آية الكوفي، والمكي، ولم يعدها آية، البصري، ولا الشامي والمدني. والاختلاف في أنها آية في الحمد مشهور في الصدر الأول، فقال جماعة منهم بذلك. فهو اختلاف كثير غير منكر.
لكنا نقول في هذا: إن الزيادة في القرآن لا تثبت بالاختلاف، وإنما تثبت بالإجماع. ولا إجماع على ذلك. فروى في ذلك المثبتون أحاديث، وروى النفاة أحاديث، فتوازى الأمران. وانتقاد صحة الأحاديث وتقديم بعضها (١٥٨/أ) على بعض يطول. وقد كنا قد قدمنا قبل هذا أن الصحابة أضربت','2','780',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1477,'عن نقل الأحاديث في القرآن، والتفتت إلى الثابت المعلوم. فلا يثبت اليوم قرآن، بناء على نقل الآحاد، على ما فهم من رأي الصحابة في ذلك. وقد جاءت الأحاديث الصحاح عن مالك وغيره أنها ليست آية من الحمد. هذا كلام الفقهاء والقراء، قد أتينا عليه بحمد الله وعونه، ولا قوة إلا به.
وقد سلك الأصوليون في المسألة مسلكا آخر، نذكره ونبين ضعفه. قال أبو حامد: البسملة آية من القرآن، [لكن] هل هي آية من أول كل سورة؟ فيه خلاف. وميل الشافعي إلى أنها آية [من سورة] الحمد وسائر السور، [ولكنها] في أول كل سورة آية برأسها، أو هي مع أول آية نم [سائر] [السور] آية؟ هذا [مما] نقل عن الشافعي فيه تردد.
[هذا] أصح [من قول من حمل] تردد الشافعي على أنها، هل هي آية من القرآن في أول كل سورة أم لا؟ بل الصحيح أنها حيث كتبت مع القرآن بخط القرآن، فهي من القرآن.
الاعتراض: قال القاضي وغيره: القرآن لا يثبت إلا بطريق قاطع متواتر،','2','781',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1478,'فإن كان هذا قاطعا، فكيف اختلفوا فيه؟ وإن كان مظنونا، فكيف يثبت القرآن بالظن؟ ولو جاز هذا، لجاز التتابع في صوم كفارة اليمين بقول ابن مسعود، ولجاز للروافض أن يقولوا: قد ثبتت إمامة علي بنص القرآن، ونزلت فيه آية أخفتها الصحابة بالتعصب.
ولكن طريقنا في الرد عليهم أن نقول: نزل القرآن معجزة لرسول الله - صلى الله عليه وسلم -، وأمر رسول الله - صلى الله عليه وسلم - بإظهاره لطائفة تقوم الحجة بقولهم، وما كان يظن مناجاة الاحاد به، ولا يظن بأهل التواتر التطابق على الإخفاء، حتى لا ينكر ذلك منكر. وكانوا يبالغون في حفظه، حتى منعوا من النقط والتعشير، كيلا يختلفط بالقرآن. ومنعوا من كتابة أسامي السور في أول كل سورة، إرادة للحفظ، [ومنعا] من الإيهام.
قال القاضي: أقطع بخطأ من قال: إن السلمة آية في غير سورة النمل. ولكنه قال: أخطئ القائل به، ولا أكفره، لأن نفيها أيضا لم يثبت بنص متواتر، قاطع للشك. فأخطئ القائل من غير تكفير، وأعترف بأن التسمية منزلة على الرسول - صلى الله عليه وسلم - في أول كل سورة، حتى قال ابن عباس: (ما كان رسول الله - صلى الله عليه وسلم - يعلم ختم سورة وابتداء أخرى، حتى ينزل عليه جبريل بفصل (بسم الله الرحمن الرحيم).','2','782',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1479,'قال القاضي: لا يمتنع أن ينزل عليه بما ليس بقرآن، وأعترف بأنها مكتوبة بخط القرآن، وأنكر قول من نسب عثمان إلى البدعة في كتابة (بسم الله الرحمن الرحيم) في أول كل سورة. وقال: لو ابتدع، لم يسكت عنه أهل الدين، مع صلابتهم وامتناعهم من المداهنة والمداجاة. ولما أنكر على من أثبت النقط والتعشير، فما بالهم لم يجيبوا: بأنا أبدعنا كما أبدع عثمان؟ لاسيما ورأس السور يكتب بخط آخر، ليتميز عن القرآن، والتسمية مكتوبة بخط القرآن، بحيث لا تتميز عنه، وتحيل العادة السكوت [عمن] ابتدع شيئا في القرآن هذا تمام كلام القاضي.
وقد اعتمد أبو حامد في الرد عليه طريقا، نذكره ثم نبين فساده، وننعطف على الاعتراض على طريق القاضي، ونبين الصحيح في ذلك، إن شاء الله تعالى. قال (١٥٨/ب) أبو حامد: لا وجه لقطع القاضي بتخطئة الشافعي، فإن من ألحق ما ليس منه فهو كافر، كما أنه مخطئ، والقاضي [قد] اعترف بأنه ليس بكافر، فلو ألحق بالقرآن ما ليس منه، لكان كافرا، كما أنه مخطئ، لا سبب لذلك إلا أنه قال: لم يثبت أيضًا كونها ليست قرآنا بنص صريح، قاطع للشك والاحتمال.
قال أبو حامد: فنحن نقول: لو لم تكن من القرآن، لوجب على رسول الله - صلى الله عليه وسلم - أن يبين أنها ليست قرآنا بنص صريح، قاطع للشك والاحتمال، وإشاعة ذلك على وجه يزيل الريب.','2','783',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1480,'فإن قيل: ما ليس من القرآن لا حصر له حتى ينفى، وإنما الحاجة إلى إثبات المنحصر، وهو القرآن. قال أبو حامد: قلنا: هذا صحيح، لو لم تكتب التسمية بأمر رسول الله - صلى الله عليه وسلم -، مع كونه كان لا يكتب إلا ما هو قرآن، فلا يظن به أنه لم يعلم أنه توهم، ولا جوز السكوت على الوهم.
فإذا قال القاضي: لو كانت من القرآن، لبين ذلك بيانا شافيا، قاطعا للشك والاحتمال. ونحن نقول: لو لم تكن من القرآن، لبين ذلك بيانا شافيا، قاطعا للشك والاحتمال، فتوازى الكلام من الجانبين. والآمر بالكتابة بخط القرآن، فإنه كان لا يكرر مع كل كلمة أنها من القرآن، بل كان جلوسه للإملاء والكتابة بين يديه، مع الاقتصار على كتابة القرآن، يرشد إلى المقصود، ويغني عن التعبير، مقتضٍ لكونها من القرآن، ولكن لما كانت التسمية أمر بها في ابتداء كل أمر خطير، ظن أن ذلك كتب على طريق التبرك. وهذا الظن خطأ، ولهذا قال ابن عباس: (سرق الشيطان من الناس آية من القرآن). ولم ينكر عليه.
فإن قيل: فبعد حدوث الوهم والظن، صارت المسألة اجتهادية، وخرجت عن مظنة القطع، فكيف يثبت القرآن بالاجتهاد؟ قلنا: جوز القاضي الخلاف في عدد الآيات من القرآن ومقاديرها، وأقر بأن ذلك منوط باجتهاد القراء، وأنه لم يبين بيانا شافيا، قاطعا للشك.
والتسمية [بعض] آية من القرآن في سورة النمل، فهي مقطوع بكونها من القرآن، وإنما الخلاف في أنها من القرآن مرة واحدة، أو مرات كثيرة؟ فهذا يجوز أن يقع الشك فيه، ويعلم بالاجتهاد، لأنه نظر في تعيين موضع الآية بعد كونها مكتوبة بخط القرآن.','2','784',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1481,'قال: والدليل على إمكانه الوقوع، فإن الاجتهاد قد تطرق إليه، وأن النافي يكفر المثبت، والمثبت لم يكفر النافي، بخلاف القنوت والتشهد، فصارت المسألة نظرية. وكتابتها بخط القرآن، مع تصلب الصحابة وتشددهم في حفظ القرآن عن الزيادة، قاطع أو كالقاطع، في أنها من القرآن.
قال الشيخ: أما ما ذكره القاضي من القطع بخطأ الشافعي، بناء على أن طريق نقل القرآن التواتر، فما لم يتواتر يقطع بكونه ليس بقرآن. فقد تكلمنا على ذلك بما فيه كفاية، فلا معنى للإعادة. والقول عندي في البسملة يجري على ذلك، إلا أن القراءة الشاذة عندي أبين في ثبوت كونها قرآنا من التسمية، لأن الثقات قد أسندوا القراءة الشاذة إلى القرآن بالنصوص الصريحة، ولم يثبت نص صريح في أن التسمية من أول السور على حال. وإنما تلقاها من تلقاها من مجرد الكتابة، وهذا ليس (١٥٩/أ) نصا صريحا في كونها قرآنا، لا تواترا ولا آحادا. فالأمر فيها عندي أضعف من قراءة ابن مسعود وغيرها.
وأما ما تمسك به أبو حامد من أنها لو لم تكن من القرآن، للزم أن يكفر المثبت، بناء منه على أن الخطأ والكفر في القرآن يتلازمان، فكل مخطئ فيه كافر، وكل كافر مخطئ. ولا كفر ههنا باتفاق. فينبغي أن لا يثبت الخطأ. وهذا يمثل بالطرد والعكس نفيا وإثباتا. فمن أخرج من القرآن شيئا. فقد أخطأ وكفر، ومن ألحق به شيئا ليس منه، أخطأ وكفر. هذا معتمده.
ولكن كشف الغطاء فيه أن نقول: قد يتلازم الحكمان، بناء على مثمر','2','785',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1482,'واحد، وقد يتفق تلازمهما، بناء على مثمرين مختلفين اتفق اجتماعهما، فاتفقت الأحكام لذلك. فمتى كان المثمر واحدا، والثمرتان لازمتان، لم يتصور الافتراق على حال. وأما إذا تعدد المثمر، فلا بعد في اجتماع الثمرات [وافتراقها].
ونحن نقرر ههنا أمر الخطأ والتكفير، ونبين تعدد المقتضيات للثمرتين. أما الخطأ، فإنه يثمره في الشريعة مخالفة الأدالة القطعية. فمن خالف أمرا مقطوعا به، فهو مخطئ قطعا، فإن خالف أمرا مظنونا، فهو مخطئ ظنا، عند من قال إن المصيب واحد. أما الخطأ في المظنونات، عند من صوب كل مجتهد، بعد بذلك الجهد، لمن هو من أهل الاجتهاد، فلا يتصور، ولا يلزم من مجرد الخطأ التكفير، كالخطأ في القياس وخبر الواحد، والعمل بالعموم، لا يكفر جاحده. وإن كفر جاحد وجوب الصلاة والصوم والزكاة والحج.
والضابط في ذلك فيما خرج عن العقائد، وما يتعلق بالإله وصفاته، والنبوات والمعجزات، أن من جحد أمرًا علم من دين الأمة ضرورة، فهو كافر. والسبب فيه أن النقل المتواتر ينزل العبد منزلة السامع من الرسول، فإذا جحد أن ذلك من الله تعالى، كان ذلك تكذيبا للرسول، وتكذيبه كفر بين. وأما إذا جحد من فروع الشريعة ما طريق معرفته النظر، لم يكفر بجحد وجوب العمل بالقياس وخبر الواحد.
فإذا استقر ذلك، فما ليس بقرآن انقسم قسمين: [قسم] علم ضرورة أنه ليس بقرآن. فمن قال: هو قرآن، فهو مخطئ كافر. أما خطؤه، فمن جهة عدوله عن الحق. وأما كفره، فمن جهة تكذيب الرسول المخبر عن كونه ليس','2','786',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1483,'بقرآن. ومن قال: إن التسمية من القرآن، لم يلحق بالقرآن [شيئا] علم ضرورة خروجه عنه. فلا يلزم من عدم الكفر عدم الخطأ.
ولكن الصحيح عندنا أن لا يقطع بالخطأ إلا من هذه الجهة. لكن من جهة ما قلناه في الرواية الشاذة. إلا أن يصح ما نقله محمد بن أبي طالب المقبري، وكان من أهل الفقه والقراءة والحديث، فإنه نقل الإجماع من الصحابة والتابعين على أن التسمية ليست آية من أول كل سورة، وإنما الخلاف فيها في أول الحمد. فيكون الخطأ من هذه الجهة، لا من جهة [تكذيب العدل الناقل].
وأما ما ذكره أبو حامد في إثبات كونها قرآنا، من جهة أنها لو لم تكن قرآنا، لبين ذلك الرسول [- صلى الله عليه وسلم -] بعد أن أمر بكتابتها مع القرآن، إلى آخره.
فقد كنا قدمنا جوابا عن مثل هذا، وقلنا: إن التناهي في إزالة ما يوهم، ليس','2','787',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1484,'بشرط في الشرع، ولا عذر بعد [أن] نصب الله الأدلة القاطعة. وإذا رد الخلق في إثبات القرآن إلى النقل المتواتر، فلا التفات لوهم (١٥٩/ب) من استند لهذا الطريق.
وأما مصيره إلى تنزيل هذه المسألة منزلة الاختلاف في عدد الآي، وبيان تمام الآية، فعجيب، فإن ذلك الاختلاف ليس فيه زيادة ولا نقصان، وإنما هو راجع إلى معرفة الإعراب، بخلاف آية التسمية. فإنه إثبات مائة آية وأربع عشرة آية.
والعجب كل العجب ممن يشترط التواتر في الكلمة الواحدة، أعني في قراءة ابن مسعود (متتابعات)، ويقول: لابد أن يكون طريق إثبات القرآن التواتر، ثم يجوز إثبات هذا العدد من الآي بطريق يغلب على الظن. وهل هذا إلا عكس الحق ونقيض الصدق؟ نعوذ بالله من التعصب بخلاف الحق.
وأما قول الإمام: (إن وجوه القراءة نقلت على ألسنة القراء تواترا).','2','788',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1485,'فليس بصحيح، بل المتواتر ما اشتمل عليه المصحف وحوته الأم، ولم يثبت في المصحف تعرض للإعراض بحال.
وإنما ذلك راجع إلى القراء، وما تقتضيه العربية مع صحة الإسناد إلى رسول الله - صلى الله عليه وسلم - على ألسنة العدول. هذا نافع بن أبي نعيم يقول: (قراءتي هذه أخذتها عن الصاحب، فما انفرد به الواحد، لم آخذه، وما اجتمع عليه اثنان قبلته، حتى ألفت قراءتي هذه). وانفرد قالون برواية حروف عن نافع، وكذلك ورش. وسائر الأئمة إنما نقل وجوه القراءة أفرادا، لا يبلغون مبلغ التواتر على حال.
والسبب في ذلك: أن الصحابة رضوان الله عليهم كانوا يسمعون من','2','789',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1486,'الرسول - عليه السلام - القراءة على جهات متعددة، مما يسوغ في العربية، ويذهبون في البلاد، فيقرئ كل صاحب أهل بلدة على حسب ما سمع من الرسول - عليه السلام -. فلما كتب المصحف، ولم يتعرض فيه للضبط بالنقط على حال، اكتفي بما في طباع العرب من الفصاحة، قرأ كل قوم على حسب ما كانوا قرأوه على الصاحب الذي كان عندهم.
والدليل على ذلك: ما رواه مالك وغيره: (أن عمر بن الخطاب - رضي الله عنه - سمع [هشام بن حكيم بن حزام] يقرأ سورة الفرقان على غير ما يقرأها، وكان عمر قرأها على رسول الله - صلى الله عليه وسلم -. قال عمر: فكدت أعجل عليه، ثم أمهلته فقلت: من أقرأك هذه السورة؟ قال: رسول الله - صلى الله عليه وسلم -. قلت له: فرسول الله - صلى الله عليه وسلم - أقرأنيها على غير ذلك. فلببته برادئه، ثم أتيت به رسول الله - صلى الله عليه وسلم - فقلت: يا رسول الله هذا يقرأ سورة (الفرقان) على غير ما أقرأتنيها. فقال: أرسله. قال: إقرأ [يا هشام]، فقرأ. فقال: هكذا أنزلت. ثم قال: اقرأ يا عمر، فقرأت. فقال: هكذا','2','790',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1487,'أنزلت. إن هذا القرآن أنزل على سبعة أحرف، فاقرأوا ما تيسر منه). فتحقق بهذا أنه أنزل على أنحاء كثيرة.
وقد اختلف الناس، هل هذه القراءات السبع هي الأحرف التي أراد النبي - صلى الله عليه وسلم -؟ أو هذه القراءات السبع حرف واحد، والأحرف الستة درست؟ والصحيح عند أهل العلم أن هذه القراءات هي حرف واحد. وأما الأحرف الستة فدراسة. وقد فسر ابن شهاب ذلك، بأن قال: هي (سميع عليم) في موضع (قدير حليم). فيرجع إلى اختلاف الألفاظ. هذا معنى ما ذكره.
والدليل على ذلك: أن هذه القراءات السبع [لو كانت] هي الأحرف السبعة التي أراد النبي - صلى الله عليه وسلم -، لكان لا يصح (١٦٠/أ) أن يكون قرأ بغيرها. وقد بينا بالنقل الصحيح أن الناس قد قرأوا بغير هذه الروايات. والنقل في ذلك أكثر من أن يحصى.
والدليل على أن القراءات إنما هو حرف واحد من الأحرف السبعة، أن تلك الأحرف السبعة كان يقرأ بها في زمان رسول الله - صلى الله عليه وسلم -، ولما كان في زمن عثمان - رضي الله عنه - سافر حذيفة إلى بعض بلاد العراق، فوجد الناس يختلفون في','2','791',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1488,'القرآن اختلافا شديدا، وينكر بعضهم على بعض أغلظ الإنكار، وكاد أن يكفر بعضهم بعضا، فرجع حذيفة إلى المدينة فزعًا، وقال: يا أمير المؤمنين أدرك هذه الأمة قبل أن تختلف في القرآن اختلاف اليهود والنصارى. فجمع عثمان الصحابة واستشارهم في القضية، فرأى أن يجمع الناس على حرف واحد. فكتب المصحف على الصحيفة التي كانت عند حفصة. واختار هو والصحابة حرفا من تلك الأحرف، وكتب المصاحف على ذلك. قيل: سبعة. وقيل: خمسة. وبعث إلى كل مصر من الأمصار مصحفا منها. وأمر ببقية المصاحف فحرقت. وقيل محيت).
فلو كان ما بأيدي الناس اليوم هي الأحرف السبعة التي نزل بها القرآن، لم يكن عثمان منع شيئا، وأبقى الأمر على الاختلاف الذي حذره هو والصحابة. ولما كتب ومحا ما سواه، لم يتعرض للإعراب فيه، فبقي أهل كل مصر على ما كانوا يقرأونه، مما نقل لهم الصاحب الذي كان علمهم مما يوافق مع انضباط. فهذا هو السبب في اختلاف القراء مع الانضباط على حرف واحد من الأحرف السبعة التي نزل بها القرآن. ولم يشترط أحد أن جهات القراءة بالإضافة إلى كل أمام من هؤلاء الأئمة متواتر. ولذلك أن أبا عمرو قرأ على','2','792',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1489,'ابن كثير، واختار في حروف كثيرة خلاف قراءته، وكذلك الكسائي وغيره. ثبت بمجموع ذلك أن التواتر ما وافق خط المصحف، وفهم معناه على لغة العرب.
وأما أوجه القراءة، فلا يشترط فيها التواتر بحال.','2','793',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1490,'وقد قال أئمة العربية: قراءة حمزة في قوله تعالى: {واتقوا الله الذي تساءلون به والأرحام}. ضعيفة. وكذلك قراءة قالون: {ومحياي}. بإسكان الياء، ضعيفة جدا.
وقد روى الداودي حديثا فيه قراءة الحمد وفيه: (ملك يوم الدين). وقال: وهذا حجة لأهل المدينة، لأنهم يقرأون (ملك) بغير ألف.','2','794',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1491,'فلو كانت القراءة على هذه الجهة متواترة، كيف يحتج على القراءة المتواترة بخبر [الواحد] وروي على الموافقة؟
ولم ينقل جهات قراءة نافع عنه إلا ورش وقالون. وكذلك رواية أبي عمرو نقلها السوسي والدوري. وانفرد كل واحد من هؤلاء الرواة بوجوه من الحركات والإعراب، ولم يشاركه الآخر فيها.
هذا (ورش) مع روايته عن (نافع) يخالفه (قالون) في أمور كثيرة من الحركات والسكتات والهمز وغيره. دل هذا على أن القوم راعوا خط المصحف مع ما تسوغه العربية، مع صحة الإسناد إلى الرسول - صلى الله عليه وسلم -، وإن كان على طريق الآحاد.
فإن قيل: فقد يختلفون في الحرف الواحد، كقوله تعالى في رواية: {سارعوا} وفي الأخرى} وسارعوا}. فما وجه ذلك؟ قلنا: يحتمل أمرين:','2','795',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1492,'أحدهما - أن يكون النبي - صلى الله عليه وسلم - قد قرأ في الحرف الواحد بالجهتين من القراءة، ويكون كل إمام اختار وجها. (١٦٠/ب)
ويحتمل - أن يكون النقل قد صح في الأمرين جميعا، فلم يثبت صحة أحدهما بعينه، [وهو قسيم] الآخر، فساغ الأمران، كما جاء أن النبي - صلى الله عليه وسلم - أفرد الحج. وروي عنه أنه قرن، وروي عنه أنه تمتع.
وقد ذهب جميع العلماء إلى جواز الأوجه الثلاثة، لصحة النقل فيها عن النبي - صلى الله عليه وسلم -، وإن اختلفوا في الأفضل. فكذلك قراءة الكلمة على الجهات المختلفة يصح أن تقرأ على الجميع، وإن اختلف في التفصيل. هذا تمام الكلام على هذه القاعدة على قدر ما يليق بالأصول.
واستيعاب الكلام فيها يخرجها عن غرض الأصول، وبالله التوفيق.

قال الإمام رحمة الله عليه: <span data-type="title" id=toc-255>(كتاب الإجماع </span>-[نصدر] هذا الكتاب','2','796',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1493,'[مستعنين] بالله بثلاث مسائل، ثم نخوض [بعدها] في ترتيب الكتاب تأصيلا وتفصيلاً) إلى قوله (فهذا منتهى الغرض في تصوير الإجماع). قال الشيخ: لابد من النظر في هذا الباب في أمور أربعة: أحدها - ما يدل عليه لفظ الإجماع. والثاني - في تصويره. والثالث - في إمكان الإطلاع عليه. والرابع - في بيان كونه حجة.
أما مدلول لفظ الإجماع، فإنه يطلق على الاتفاق، ويطلق على الإزماع، فهو مشترك بينهما. يقول القائل الواحد: أجمعت على كذا، يريد أزمعت. وقد قال الله تعالى: {فأجمعوا أمركم}. والمراد الإزماع. وقد قال مالك: إذا','2','797',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1494,'اغتسل الكافر بقرب الإسلام والإجماع عليه أجزاءه. ومقصودنا الإجماع الذي هو الاتفاق. وقال النظام: الإجماع عبارة عن [كل] قول قامت حجته، وإن كان قول واحد. ولكنه إنما قال ذلك، لأنه لا يرى الإجماع حجة، وتواتر إليه','2','798',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1495,'عن الشريعة أن الإجماع حجة فقال: هو عبارة عن [كل] قول قامت حجته. وهذا خلاف اللغة وعرف علماء الشريعة. ولسنا نضايقه في اللفظ [لو] سلم أن الاتفاق حجة.
وإذا قلنا: إن الإجماع هو الاتفاق، فقد اختلف الناس في حده. فقال قوم: الإجماع الشرعي الذي يجب المصير إليه وتحرم مخالفته هو: اتفاق أمة محمد - صلى الله عليه وسلم - على أمر من أمور الدين.
وقال قائلون: اتفاق أهل الحل والعقد. والتفاوت بين الحدين [يظهر','2','799',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1496,'في]، هل يدخل من ليس من أهل الاجتهاد في الإجماع الذي هو حجة، أو يكون مقصورًا على أهل الاجتهاد خاصة؟ والصحيح عندنا الاقتصار على المجتهدين، وأنه لا التفات لموافقة غيرهم ولا مخالفته. وسيأتي الكلام على ذلك بعد هذا، إن شاء الله تعالى.
وأما تصوير الإجماع، فواضح، لا إشكال فيه، وقد أجمع على الشبه خلق كثير، زائدون على عدد أهل الإسلام، فالإجماع على الحق مع ظهور أدلته أولى. نعم، العادة منعت إجماع الكافة. فأما الخلق الكثير، فلا تمنع العادة من اتفاقهم بوجه. ولعمري إن إمكان الإطلاع على الإجماع على ألسنة التواتر عسر مع تفرق البلاد وتنائي الديار. والأمر على ما قرره الإمام [من] أن أكثر','2','800',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1497,'الإجماعات استندت إلى أصحاب رسول الله - صلى الله عليه وسلم -، وهم مجتمعون أو متقاربون.
وأما احتجاجهم بأن مذهب أصحاب الشافعي معروف عندنا. فهذا الكلام ضعيف، لأنه لم يعرف بالتواتر عن كل شخص أنه يقول بمذهب الشافعي، مع كثرة القائلين (١٦١/ا) بمذهبه. وإنما الذي علم مثلاً مذهب الشافعي، ثم علمنا أن لا يكون شافعيًا في تلك المسألة إلا من قال بهذه المقالة. ولذلك أنه لو قيل لنا: كم عدد القائلين بهذه المقالة؟ لم نعلم العدد، وإنما نقول: من قال بها فهو شافعي، ومن لا فلا، بخلاف مسألة الإجماع، فإنا نفتقر إلى معرفة أعيان المجمعين واتفاقهم. نعم، لو سلك بهذا في الرد على من أحال اتفاق الكثير على أمر نظري، صح التمسك به على هذا الوجه، لعلمنا أن الذين','2','801',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1498,'صاروا إلى مذهب الشافعي خلق كثير. وما منعت الكثرة مع الاتفاق على القضية، فيصح التمسك بأصحاب المذاهب وأتباعهم على هذا الوجه.
وبقي مما ذكره الإمام سؤالاً وجهه على نفسه ولم يجب عنه، وذلك السؤال قولهم: ولو ذهب ذاهب من العلماء إلى مذهب، فما الذي يؤمن من بقائه عليه حتى يموت، لم ينعقد الإجماع؟ هذا سؤال صعب، ولم يجب الإمام عنه بحال. والجواب عنه على حسب ما قرره أولاً من اتفاقهم في مجلس واحد، وهم متقاربون، فحينئذٍ يعرف البقاء على القضية إلى أن يحصل الإطباق من البقية.
قال أبو حامد: وإن قدر رجوعه بعد انعقاد الإجماع، فلا التفات إليه. وهذا صحيح على أصله، فإنه لا يشترط انقراض العصر بحال. فأما من','2','802',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1499,'يشترط الانقراض، فيتعذر عليه الاستمرار على الحكم إلى موت [أهل] الإجماع، ولكنه يقول: إن تصور ذلك، فهو حجة وإلا فلا.
ويبقى في المسألة احتمال، وهو إمكان أن يكون في الدنيا عالم مجتهد لم يعلم به. وهذا شديد، ولكن إذا ثبت أن الإجماع حجة على ما سنبينه، ثم استحال انقطاع، علم أن هذا الاحتمال غير قادح على حال. ويتنزل هذا منزلة احتمال النسخ. وليس هذا الاحتمال مما يمنع علم الحكم، لأن هذه الاحتمالات، لو رفعت الحجج، لم يبق في الشرع حجة قاطعة. هذا تمام الكلام على التصوير وإمكان الإطلاع، وتحقيق المناط فيه تصورا ووقوعًا.
بقي الأمر الرابع، وهو إقامة الدليل على كون الإجماع حجة، وفيه الشأن','2','803',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1500,'كله. ولا يثبت كونه حجة إلا بدليل قاطع سمعي، والدليل السمعي: إما كتاب أو سنة مصرح بها، أو طريق استنباطي يرجع إليهما أو إلى أحدهما. أما الإجماع، فلا يمكن إثباته بالإجماع. وقد سلك كل فريق من الأصوليين طريقا من هذه الطرق. ونحن نذكر الطرق الثلاثة، ونتبع ما لا نرضى بالإبطال، ثم ننص على الطريق الذي يصح أن يستند إليه. مستعينين بالله وهو خير معين.

قال الإمام: <span data-type="title" id=toc-258>(المسألة الثانية: في كونه حجة إذا وقع) </span>إلى قوله (ولا يقوم للمحصل عن هذا جواب إن أنصف). قال الشيخ: هذا طريق للقوم والاعتراض عليه. وقد تقدم هذا الاستدلال قبل هذا. والذي نزيده الآن أن نقول: إن كانت الجملتان شرطا واحدا، لم يكن في الآية حجة، إذ يكون الإصلاء مشروطا بالمشاقة وسلوك غير الطريق. وإن كانت كل جملة على حيالها شرطا، قوي الاستدلال على هذا الوجه.
وهذا الثاني هو الذي يظهر لي من الآية، وذلك أن قوله تعالى: {ومن يشاقق الرسول من بعد ما تبين له الهدى}. مستقل في إصلاء النار. فإذا','2','804',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1501,'كان كذلك، فلو جعلت الثانية أيضًا معها، كان ذلك في حكم المستغنى عنه، لحصول الاستقلال للجملة الأولى. (١٦١/ب) فالمراد إذًا استئناف شرط آخر، لتكون كل جملة مفيدة فائدة جديدة. فعلى هذا يكون المراد: ومن يشاقق الرسول من بعد ما تبين الهدى نوله ما تولى، ونصله جهنم، ومن يتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم.
وهل يكون ذلك من أبواب العموم، أو من أبواب النكرة الواقعة في سياق الشرط؟ هذا يرجع إلى أن إضافة (غير) إلى المعرفة، هل تقتضي تعريفا أم لا؟ والإضافة في هذا لا تقتضي تعريفا. فإذا قال: رأيت القوم غير','2','805',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1502,'زيد، فـ) غير) نكرة، وإن أضيف إلى معرفة. قال ابن السراج: إلا إذا كانت القسمة محصورة في الشيء ومغايرة، ويكون المغاير قسما واحدا من غير تعدد، كقوله تعالى: {غير المغضوب عليهم}. فإنه قالوا (غير) ههنا معرفة.
وأما في الآية، فـ} غير سبيل المؤمنين} غير متحد، إذ غير سبيلهم متعدد، فيبقى (غير) على التنكير، فيقع في أبواب النكرة الواقعة في سياق الشرط، كقوله: (من أتاني بماله أجازه). وقد ذهب الإمام في أدوات الشرط إلى أنها نص في العموم، إلا أن تتقدم قرينة، مثل أن يقول قائل: قد جاء العلماء مثلا، فيقول رجل: من جاءني أكرمته، ويقصد المذكورين السابقين.','2','806',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1503,'فهذا يصح، وإذا لم يكن كذلك، كان اللفظ نصا.
وهذا - والعلم عند الله - هو الذي أراده الشافعي. على أن الشافعي يرى أن صيغ العموم بجملتها نص في الاستغراق عند انتفاء القرائن. ومع هذا الذي ذكرناه، لا تكون الآية نصا، لاحتمال أن تكون الجملتان شرطا، وتكون الثانية ذكرت توكيدا. وعلى هذا التقدير يمتنع الاستدلال بها على كون الإجماع حجة، إذا كان مثبته يقطع به.
وقد استدلوا أيضًا بآي كثيرة من كتاب الله تعالى زعموا أنها مع كونها تحصل العلم وتقطع الاحتمال، وإن كانت كل آية منها يتطرق الاحتمال إليها، ولا بعد - لعمري - في توافق الظاهر حتى يحصل منها العلم، فإن القائل إذا قال: الناس كلهم صغيرهم وكبيرهم، لم يفتني منهم أحد، فإن العلم محصول بالعلم، وإن كان كل واحد من هذه الألفاظ يتطرق إليه الاحتمال.','2','807',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1504,'قالوا: قال الله تعالى: {وكذلك جعلناكم أمةً وسطًا}. والوسط: العدل. وقال تعالى: {كنتم خير أمةٍ أخرجت للناس}. فكيف يصح مع هذا أن يتفقوا على الخطأ والباطل؟ وقوله: {وما اختلفتم فيه من شيءٍ فحكمه إلى الله}. مفهومه: أن ما اتفقتم عليه، فهو حق. وقوله: {واعتصموا بحبل الله جميعًا}، [وقوله: ]} ولا تنازعوا .. }. كل هذه الآيات تدل على أن في الموافقة الصواب، وعند المخالفة يمكن الخطأ.
وهذه الآي ضعيفة الدلالة، سيقت لأغراض أخر، ليست مما نحن فيه بسبيل. وهي تشير إلى تشريف هذه الأمة، وذلك لا ينكر. أما الإصابة على العموم، فلا يلزم من ذلك على حال. فإن خيار الصحابة كالمهاجرين الأولين أفضل هذه الأمة، ولا تتحقق العصمة لكل واحد منهم على انفراده، فما المانع من أن يقال بذلك في حق الأمة؟ فإذًا ليس في الآيات ما يناسب ما نحن فيه إلا الآية الأولى. وقد بينا ما فيها من دقائق الكلام.
قال الإمام: ([وإن] تمسك مثبتو الإجماع بما روي أن النبي - صلى الله عليه وسلم -) إلى','2','808',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1505,'قوله (فلا وجه للاحتجاج به في مظان القطع). قال الشيخ: قد تمسك القاضي وغيره من حذاق الأصوليين في إثبات كون الإجماع حجة (١٦٢/أ) بالسنة، واستوعبوا الكلام على ذلك، واختصره الإمام كثيرا. ونحن نذكر ما أوردوه، ثم نعترض على ما لا نرتضيه من ذلك.
قالوا: أوجه الاستدلال بالسنة أن نقول: تواترت الأخبار عن رسول الله - صلى الله عليه وسلم - بألفاظ مختلفة مع اتفاق المعنى في عصمة هذه الأمة عن الخطأ، واشتهر على لسان المرموقين الثقات من الصحابة، كعمر وابن مسعود، وأبي سعيد الخدري، وأنس بن مالك، وابن عمر، وأبي هريرة، وحذيفة بن [اليمان] وغيرهم ممن يطول ذكره، من نحو قوله [- صلى الله عليه وسلم - ٍ]: (لا تجتمع أمتي على الضلالة). [وقوله: ] (لم يكن الله تعالى بالذي يجمع أمتي على','2','809',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1506,'الضلالة). [وقوله: ] (سألت الله تعالى أن لا تجتمع أمتي على الضلالة فأعطانيها). و [قوله: ] (من سره أن يسكن بحبوحة الجنة فليلزم الجماعة، فإن دعوتهم تحيط من روائهم، وأن الشيطان مع الواحد وهو من الاثنين أبعد). وقوله: (يد الله مع الجماعة، ولا يبالي الله بشذوذ من شذ). و [قوله: ] (لا تزال طائفة من أمتي على الحق ظاهرين لا يضرهم خلاف من خالفهم، إلا ما أصابهم من لأواء). و [قوله] (من خرج عن الجماعة أو','2','810',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1507,'فارق الجماعة قيد شبر، فقد خلع ربقة الإسلام من عنقه). و [قوله] (من فارق الجماعة ومات فميتة جاهلية). وهذه الأخبار لم تزل ظاهرة في الصحابة والتابعين إلى زماننا هذا، لم يدفعها أحد من أهل النقل من سلف الأمة وخلفها، بل من موافقي الأمة ومخالفيها. ولم تزل الأمة تحتج بها في أصول الدين وفروعه.
هذا تقرير الاستدلال. وقد وجهوا على أنفسهم سؤالا فقالوا: فما وجه التمسك بهذه الأخبار، ودعوى التواتر غير ممكنة، ونقل الآحاد لا يفيد العلم، والظن في الباب غير مكتفى به؟
أجابوا بوجهين: [أحدهما]- أنهم قالوا: الأخبار متواترة من حيث الجملة، وإن لم تكن آحادها متواترة، وسبيلها سبيل العلم بجود حاتم، وشجاعة علي [- رضي الله عنه -] وخطابة الحجاج. قالوا: فقد حصل [بمجموع] هذه الأخبار','2','811',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1508,'أن الرسول - عليه السلام - أراد تعظيم هذه الأمة وعصمتها وإثبات هذه الفضيلة لها، بحيث لا نجد من أنفسنا ريبا، كما لا نجده في تعظيم رسول الله - صلى الله عليه وسلم - أصحابه وتقديره إياهم، وحثه على حبهم وإكرامهم. وكذلك أيضًا نعلم ميل رسول الله - صلى الله عليه وسلم - إلى عائشة ومحبته إياها، بمجموع أخبار نقلت آحادا، أفاد مجموعها العلم.
هذا تقرير هذا الطريق. وهو عندي لا يقوى، وحاصله راجع إلى إثبات الحكم من غير دليل عليه، فإن الشيء إذا علم ضرورة، استحال الاستدلال فيه، وكيف ينفع هذا الطريق مع الخصم المنكر؟ لاسيما المنازعون، والذين أنكروا الإجماع عدد كثير لا ينقصون عن عدد التواتر. فدعوى العلم الضروري في الباب بعيد عن التحقيق، وغير منتفع به مع الخصوم. فلا وجه للتمسك بذلك.
الطريق الثاني لهم: وهو الوجه الاستدلالي: قالوا: وتقريره من وجهين:
أحدهما - أن هذه الأخبار لم تزل مشهورة بين الصحابة والتابعين يتمسكون بها في إثبات الإجماع، ولا يظهر أحد فيها خلافا إلى زمان النظام، ويستحيل في مستقر العادة توافق الأمم في أعصار كثيرة على التسليم لما لم تقم الحجة بصحته، مع اختلاف الطباع وتفاوت المذاهب في الرد والقبول، ولذلك لم ينفك حكم ثبت بأخبار الآحاد عن خلاف مخالف أو إبداء تردد فيه.
الوجه الثاني - أن المحتجين بهذه الأخبار أثبتوا بها أصلا مقطوعا به، وهو الإجماع الذي يحكم به على كتاب الله تعالى وعلى السنة المتواترة،','2','812',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1509,'(١٦٢/ب) ويستحيل في العادة التسليم لخبر يرفع الكتاب المقطوع به، إلا إذا استند إلى مقطوع به، أما رفع المقطوع بما ليس مقطوعا، [فليس معلوما]. حتى لا يتعجب متعجب ولا يقول قائل: كيف ترفعون الكتاب القاطع بإجماع مستنده خبر غير معلوم الصحة؟ وكيف يذهل عنه جميع الأزمنة إلى زمان النظام، فيختص بالتنبه له ودركه؟ هذا وجه الاستدلال.
وقد وجه الأصوليون على هذا الدليل ثلاثة أوجه من الكلام، نذكرها ونعترض على ما لا نرتضي منها، ونوجه نحن أسئلة وبه تتم الطريقة. قال الأصوليون: هذه الأخبار مردودة، وينقدح في ردها [أربعة أسئلة: ]
أحدها - أنهم قالوا: لعل واحدا خالف في هذه الأخبار وردها، ولم يتفق نقل هذا الرد تواترا، فمن أين يعلم انقياد الجميع لها؟ وأجابوا عن هذا: بأن هذا تحيله العادة، إذ الإجماع أعظم أصول الدين، فلو خالف [فيه] مخالف لعظم الأمر فيه، واشتهر الخلاف، إذ لم يندرس خلاف الصحابة في مسألة الجنين، ومسألة الجد، فكيف اندرس الخلاف في أصل عظيم يلزم فيه التضليل والتبديع لمن أخطأ في نفيه وإثباته؟ وكيف اشتهر خلاف النظام مع سقوط قدره وخسة مرتبته، واختفى خلاف أكابر الصحابة والتابعين؟ هذا [مما','2','813',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1510,'لا يتسع] له عقل أصلا. هذا تمام تقريرهم وجوابهم عن السؤال.
وينقدح عندي في الاعتراض عليه أنه لا يلزم في العادة أن كل من خالف، لابد أن يظهر خلافه، إذ يصح أن يكون قد خالف ولم يظهر، أو أظهره لدون عدد التواتر، فلم يجب أن يتواتر النقل، إذ قدمنا أنه إنما يجب أن يتواتر النقل إذا كان المطلعون عدد التواتر، فأما إذا نقص العدد، فلا يلزم التواتر، بل يصح إفضاء الأمر إلى الدروس.
ووجهه واقع على هذه الطريقة، وهو أنه يمكن أن يكون بعض من يرى الإجماع حجة أسنده لغير هذا الطريق، وإن كان لا يقول بهذه الأخبار، ولم ير أن ينازعهم في طريق معين، لموافقته إياهم على الحكم، وإن كان لمستند آخر. هذا بين لا شك فيه، ولا تحيله العادة بوجه.
السؤال الثاني على أصل الاستدلال من الأسئلة الأربعة في الرد: أنهم استدلوا على الإجماع بالخبر، ثم استدلوا بالإجماع على صحة الخبر. فهب أنهم أجمعوا على الصحة، فما الدليل على أن ما أجمعوا [على صحته] فهو صحيح؟ وهل النزاع إلا فيه؟
وأجابوا بأن قالوا: استدللنا على الإجماع بالخبر، وعلى صحة الخبر بخلو الأعصار عن المخالفة والمدافعة، مع أن العادة تقتضي [إنكار] إثبات أصل قاطع يحكم [به] على القواطع بخبر غير معلوم الصحة، فعلمنا بالعادة كون','2','814',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1511,'الخبر مقطوعا به، لا بالإجماع. والعادة أصل يستفاد منه معارف، بها نعلم بطلان دعوى معارضة القرآن واندراسها، ودعوى بطلان نص الإمامة، وإيجاب صلاة الضحى، وصوم شوال، فإن ذلك لو كان، لاستحال في العادة السكوت عنه. هذا سؤال وجواب لهم عنه.
ويتوجه عندي على هذا كلام، وهو أنه لا يتأتى أن يسند العدد الكثير الجزم بالحكم إلى طريق مظنونة، ولهم أن يعتقدوا صحة الطريق، ولا يكون هو صحيحا في نفسه، فهذا غير ممتنع. فما المانع من اعتقاد الصحة، ويكون الأمر على خلاف ذلك؟ وهو بمثابة ما إذا اتفق العدد الكثير، وليسوا كل أهل الإجماع على إسناد حكم جزما (١٦٣/أ) إلى خبر غير مقطوع بثبوته. فإن جزمهم بالحكم، لا يصير الخبر مقطوع الثبوت.
والسؤال الثالث على أصل الدليل من أسئلة الرد: أنه قيل لهم: بم تنكرون على من يقول إنهم أثبتوا الإجماع لا بهذه الأخبار، بل بدليل آخر؟ أجابوا بأن قالوا: قد ظهر منهم الاحتجاج بهذه الأخبار في المنع من مخالفة الجماعة، [وتهديد] من يخالف الجماعة. هذا سؤال والجواب عنه.
وينقدح عندي في ذلك كلام، وهو أنه لم يثبت النقل المتواتر عن جميع الصحابة الاستدلال في عصمة الأمة بهذه الأخبار، بل لا يكاد يظفر للقوم باستدلال على الإجماع، وإنما كانوا يسندون الأحكام إلى الإجماع بعد أن استقر عندهم كونه حجة بأدلة ثابتة. وإن ذكرت هذه الأخبار بينهم، فليس يقصد بها إثبات عصمة، وإنما تورد لأغراض أخر.','2','815',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1512,'هذا لعمري تحقيق، وليس من أبواب المدافعات.
السؤال الرابع من أسئلة الرد: أنهم قيل لهم: لما علمت الصحابة صحة هذه الأخبار، لم [لم] يذكروا صحتها للتابعين، حتى كان ينقطع الارتياب، [ويشاركونهم] في العلم؟
أجابوا: بأنهم كانوا قد عرفوا تعظيم هذه الأمة وعصمتها بمجموع قرائن وأمارات وتكريرات ألفاظ، وأسباب دلت ضرورة على قصده إلى [بيان] نفي الخطأ عن هذه الأمة. وتلك القرائن لا تدخل تحت الحكاية، ولا تحيط بها العبارات، ولو حكوها، لتطرق إلى آحادها الاحتمالات، فاكتفوا بعلم التابعين بأن الخبر المشكوك فيه لا يثبت به أصل مقطوع به، ويقع [به] التسلم في العادة، فكانت العادة في حق التابعين أقوى من الحكاية.
هذا سؤال وجواب عنه. قال الشيخ أيده الله: حاصل هذا الكلام اعتراف [بأنها أخبار] الآحاد بالإضافة إلى التابعين، [إنما] يدل [على] أنها كانت متواترة في زمن الصحابة. وهذا استدلال على كون الخبر متواترا، وهل يقبل الخبر المتواتر الاستدلال؟ وإن قبله، فما الدليل على كونه كان متواترا عند الأولين؟ والسائل اعتقد أنه لو كان متواترا عند الصحابة، لتواتر عند','2','816',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1513,'التابعين، ويريد أن يجريه في قسم ما يجب أن يدوم تواتره، لأنه من مهمات الدين وكلياته.
وقد كنا قلنا: إن الكليات من الدين لابد من تواتر نقلها، وهو كلام صحيح، ولكنه يمكن أن يكون سبب عدم إدامة النقل على التواتر، كون الأخبار متعددة متفرقة [أفادت] علما ضروريا بقصد العصمة، فيقوى على إدامة نقل العصمة دون درك المستند، كما في الصلاة والصوم، فإن الذي [نقل] متواترًا وجوبها، دون أدلة الوجوب في التفصيل. هذا إذا ثبت أنهم أسندوه إلى الأخبار. وقد قدمنا في ذلك النزاع.
الوجه الثاني: من الاعتراض على أصل الدليل: التأويل. وقد تأول بعض الأصوليين الأخبار، وقالوا: إنها ليست نصوصا على حال، وإن قوع الاعتراف بالصحة في النقل. وقد وجهوا على ذلك ثلاثة أوجه من التأويل:
أحدها - قالوا: قوله - صلى الله عليه وسلم -: (لا تجتمع أمتي على الضلال). يعني الكفر والبدعة. [فعله] أراد عصمة جميعهم عن الكفر بالتأويل والشبهة. وقوله:','2','817',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1514,'(على الخطأ) لم يتواتر، وإن صح، فالخطأ عام يمكن حمله على الكفر.
وأجابوا بأن قالوا: الضلال في وضع اللسان لا يناسب الكفر، قال الله تعالى: {ووجدك ضالا فهدى}. وقال: {فعلتها إذًا وأنا من الضالين}. وما أراد: الكافرين، بل أراد من المخطئين. ويقال: ضل فلان (١٦٣/ب) عن الطريق، وضل سعي فلان، كل ذلك للخطأ. كيف وقد فهم على الضرورة من هذه الألفاظ تعظيم شأن هذه الأمة وتخصيصها بهذه الفضيلة؟
أما العصمة من الكفر، فقد أنعم بها في حق علي وزيد وأبي وابن مسعود على مذهب النظام، لأنهم ماتوا على الحق، وكم من آحاد عصموا عن الكفر حتى ماتوا، فأي خاصية للأمة؟ فدل أنه أراد: ما لا يعصم عنه الآحاد من سهو وخطأ وكذب، وتعصم عنه الأمة تنزيلاً لجميع الأمة منزلة الرسول - صلى الله عليه وسلم - في العصمة عن الخطأ في الدين، أما في غير الدين، من إنشاء حرب وصلح وعمارة بلدة، فالعموم يقتضي للأمة العصمة فيه، ولكن ذلك مشكوك فيه، وأمر الأمة في الدين مقطوع به.
هذا سؤال وجواب عنه. وعندي فيه وجه آخر خلاف هذا، وهو أن','2','818',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1515,'قولهم: الضلال لا يناسب الكفر، إن أرادوا اقتصارا عليه، فصحيح، وإن أرادوا أنه لا يحتمله مع غيره، فليس بصحيح، وقد قال الله تعالى: {غير المغضوب عليهم ولا الضالين}. قال أهل التفسير: هم اليهود والنصارى. وقال: {[الذين] ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعًا}. فالضلال يطلق على الكفر وغيره، لا خلاف في ذلك عند أهل اللسان.
وأما قولهم: إنه فهم من هذه الأخبار تعظيم شأن هذه الأمة، وإثبات مزية لا تثبت للآحاد. فعنه جوابان:
أحدهما - أنا لا نسلم أنه فهم منها استمرار هذا الدين إلى قيام الساعة، وقد جاء في الحديث: (أنه لا تقوم الساعة وعلى وجه الأرض من يقول: الله).
الثاني - أنا وإن سلمنا ذلك، فالخاصية التي تثبت للأمة العصمة عن الكفر، بخلاف الآحاد، فإنه لا نعلم عصمة الآحاد عن الكفر إلا بعد موته على الإسلام، بخلاف الأمة، فإنا نعلم هذا قبل انقراضهم. فهذا تبيين الفرق بين الآحاد والأمة.
التأويل الثاني: أن يقال لهم: غاية هذا أن يكون عاما، يتناول كل خطأ،','2','819',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1516,'ويحتمل أن يكون المراد بعض أنواع الخطأ، فإنه نكرة في نفي، وهي من صيغ العموم، ولا يمتنع تخصيصه. فإذا كان كذلك، لم يسغ الاحتجاج به في القطعيات. قالوا: هذا التفصيل غير صحيح، إذ لا ذاهب من الأمة إليه، والذي يجوز الخطأ عليهم في شيء يجوز في غيره. قالوا: وإذا لم يكن فارق، [لم] يكن تخصيص بالتحكم. وقد ذم من خالف الجماعة وأمر بالموافقة. وإن لم يكن ما فيه العصمة معلوما، استحال الاتباع إلا أن تثبت العصمة مطلقا، وبه تثبت فضيلة الأمة وشرفها. أما العصمة عن البعض دون البعض، فقد تثبت لكل كافر فضلا عن المسلم، إذ ما من شخص يخطئ في كل شيء، بل كل إنسان يعصم عن الخطأ في بعض الأشياء.
هذا سؤال وجواب عنه. وعندي أن التأويل متأت. وقولهم: لا فاصل في التجويز العقلي، فلا فصل من جهة العقل بحال، ولكن لم يعين القوم النوع الذي عصموا فيه، بل قالوا: يجوز أن يكون المراد هذا، وقوف بين التعيين في الوقوع، وبين الإمكان وتطرق الاحتمال.
وأما قولهم: إن لم تثبت العصمة على العموم، استحال الأمر بالاتباع. قلنا: هذا تمسك بظاهر في محاولة القطعيات، وذلك غير مفيد. ولو صح هذا، لجاز الاستدلال بالظواهر في محل طلب العلم، فلا ينتفع بذلك إلا على وجه، وهو أن يكون (١٦٤/أ) اللفظ [نصا خاصة، وهو ضعيف.
التأويل الثالث]: وهو أن أمته كل من آمن به إلى يوم القيامة، فجملة هؤلاء من أول الإسلام إلى آخر عمر الدنيا لا يجتمعون على خطأ، بل كل حكم انقضى على اتفاق [أهل] الأعصار كلها بعد [بعثته]، فهو حق، إذ الأمة','2','820',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1517,'عبارة عن الجميع، كيف والذين ماتوا في زمانه [هم] من الأمة؟ وإجماع من بعدهم ليس إجماع جميع الأمة، بدليل أنهم لو كانوا قد خالفوا ثم ماتوا، لم ينعقد بعدهم إجماع. [وقبلنا] من الأمة من خالف، وإن كان قد مات، فكذلك إذا لم يوافقوا.
الجواب عنه: أنه كما لا يجوز أن يراد بالأمة المجانبين والأطفال، وإن كانوا من الأمة، فلا يجوز أن يراد به الميت، والذي لم يخلق بعد، بل المفهوم قوم يتصور منهم الخلاف والإجماع. ولا يتصور الاختلاف والاجتماع من المعدوم. والدليل عليه أنه أمر باتباع الجماعة، وذم من شذ عن الموافقة. فإن كان المراد ما ذكروه، فإنما يتصور الاتباع والمخالفة في القيامة لا في الدنيا، فيعلم قطعا أن المراد إجماع يمكن خرقه ومخالفته في الدنيا، وذلك هم الموجودون في كل عصر. أما إذا مات بعد أن خالف، فإن مذهبه لا يموت بموته. وسيأتي تحقيقه بعد هذا، إن شاء الله تعالى.
الثالث: المعارضة بالآيات والأخبار التي تتضمن نهي الجميع عن المعصية من القتل والزنا والسرقة والكفر وشهادة الزور، وغير ذلك من محرمات الشريعة، وأن ذلك لو لم يكن ممكنا منهم، لما نهوا عنه، فإنه يشترط الإمكان في المنهيات، كما يشترط في المأمورات. وهذا كلام ضعيف، وجوابه من وجهين:','2','821',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1518,'أحدهما - أنا لا نسلم أنهم نهوا بصفة الإجماع، بل كل واحد منهم منهي على انفرداه، وكذلك الأمر بالإيمان والصلاة والصوم والزكاة والحج متوجهة إلى جميع الخلق، لا بصفة أن يجمعوا على فعل ذلك، ولكن يتوجه الأمر على كل واحد على تقدير انفراده، ويكون لفظ الجميع أغنى عن التكرار، والأوامر والنواهي متوجهة إلى كل واحد.
والدليل عليه: أنه لو أطاع البعض، لم يتوجه اللوم لمعصية البعض. ولو كان الاجتماع على العمل مقصودًا للطالب، لم يحصل الامتثال عند الاختلاف. وكذلك يقال هذا في جانب المنهيات. فإنه إن انكف قوم، لحصل الامتناع، وإن خالف الآخرون.
والوجه الثاني: أنه لا يشترط في المنهي عنه أن يكون واقعا، ولا يكون مقدور الوقوع لمن نهي عنه، فإن القدرة عندنا تقارن المقدور، وكم من نهي عن معصية ولم يفعلها، فهو غير قادر عليها، وقد قال الله تعالى لرسوله - صلى الله عليه وسلم -: {لئن أشركت ليحبطن عملك}. وقد علم أن الكفر لا يقع منه. وقد نهى الله تعالى الخلق عن جميع المعاصي، مع العلم بأن جميع المعاصي لا تقع من جميع العباد.
وأما ما تمسكوا به من السنة فقوله: (بدأ الإسلام غريبا وسيعود غريبا كما بدأ). وقوله: (خير القرون قرني ثم الذين يلونهم ثم الذين يلونهم ثم يفشوا الكذب حتى إن الرجل يحلف وما يستحلف ويشهد وما يستشهد). قالوا:','2','822',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1519,'فهذا يدل على شمول المعصية الجميع. وهذا الكلام ضعيف. نعم، هذه الأخبار تدل على كثرة المعاصي وقلة أهل الخير. فأما شمول المعصية لأهل الإجماع فلا.
قال الإمام: (فإن قيل: العقول لا تدل على القبول وثبوت كون الإجماع حجة) (١٦٤/ب) إلى قوله (فهذا مسلك إثبات الإجماع في هذه الصورة). قال الشيخ: هذا الذي ذكره في هذه الصورة لا يصح أن يكون معتمدا، مع اضطراب في لفظه، وغلط في الشبه والنظير.
أما الاضطراب في اللفظ فقوله: إنهم اجتمعوا على حكم مظنون، وللرأي فيه مضطرب. إلى آخره. ثم زعم بعد ذلك أن الحكم مقطوع به عندهم، فكيف يصح أن يكون الحكم مظنونا، وهم به قاطعون؟ هذا ظاهر التناقض.','2','823',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1520,'ولعل المراد به: أن يكون الحكم مظنونا عندنا لو لم نظفر بالإجماع، أي لم نصادف نحن فيه قاطعا، ولذلك أطلق عليه كونه مظنونا في الشريعة على الحقيقة، [و] لم يتصور لأهل الإجماع القطع به، فإن ذلك يكون غلطًا منهم وهم عنه معصومون.
وإنما قصد الإمام بهذا التقرير: أنا لو ظفرنا بالقاطع دون النظر إلى إجماعهم، لم تكن بنا حاجة إلى الإجماع، ولذلك افتقر إلى أن يصور في المسألة المظنونة عندنا لو لم نظفر بالإجماع. هذا مراده بذلك.
وأما الغلط في النظير، والاستشهاد بامتناع اتفاق العقلاء على اتفاق أهل الإجماع في أمر مظنون، لأن العادة تحيل اتفاق العقلاء على أمر مقطوع به. كلام ضعيف، إذ كيف ينزل اتفاق [أهل] الإجماع على اتفاق العقلاء؟ إذ لو','2','824',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1521,'كان [الأمر] على سواء، لاستحال اتفاق أهل الإجماع على أمر مقطوع به. وكيف يستقيم ذلك والإمام يجوز اتفاق أهل الإجماع على أمر مظنون؟ فكيف يقول: يستحيل أن يجتمعوا على أمر مظنون به؟ فيكون لهذا وجه.
وأما تلقي القاطع من جزمهم بالحكم على الإطلاق، فهذا هو مقصود المسألة، والخصم يعترف بأنهم لو كانوا عالمين بالحكم، لم يكن ذلك إلا لمستند قاطع، وإنما الخصم يقول: هم معتقدون غير عالمين، ولا يمتنع على عدد التواتر أن يظنوا ما ليس بقاطع قاطعا. فقد توهم خلق كثير قدم العالم، وصمموا على ذلك. وكذلك الذين جحدوا النبوات، وسائر فرق الكفر والضلالات. فالعادة تحيل على أهل التواتر التواطؤ على الكذب. فأما أن يطنوا ما ليس بقاطع قاطعا، [فهذا] سؤال صعب على هذه الطريقة. وهذا القائل يلزمه أن يجعل اتفاق عدد أهل التواتر من الفرق المبطلة حجة.','2','825',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1522,'فإذا تبين بطلان هذه الطرق بما قررناه، فنقول والله المستعان: الإجماع حجة قاطعة ومخالفة محرمة، وخارقه عاص، والدليل على ذلك أن الصحابة - رضي الله عنه - قطعوا بذلك واستمروا عليه، ولم يروا مخالفة الإجماع أمرا هينا، ولا يتصور أن يسند العاقل جزمه إلى أمر مظنون عنده، فإن ذلك محال. فإذا ثبت أنهم مصممون، علم بالاعتياد أن المستند عندهم غير مظنون.
لكن يبقى أن يقال: ما المانع من إضمار خلاف ما أظهروه، فيكونون ظانين، ولكن يظهرون الجزم، والباطن خلاف ذلك؟ هذا سؤال. وسؤال آخر، وهو الذي وجه على الإمام: أنهم وإن صمموا، فما المانع من أن يظنوا ما ليس بقاطع قاطعا؟ وهذا السؤال الثاني هو الغامض، وفي الانفصال عنه تتم المسألة.
والله المستعان.
أما السؤال الأول: [فالكثرة] تحيله، على حسب ما تقدم من أخبار التواتر. فإنا قد حققنا أن العادة (١٦٥/ا) تحيل عليهم التواطؤ على الكذب، والصحابة - رضي الله عنه - قد كثروا كثرة مفرطة، وزادوا على عدد التواتر، فمن المحال أن','2','826',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1523,'يضمروا خلاف ما يظهرون. فإذا جزموا بأن الإجماع حجة، فهو معتقدهم.
وأما السؤال الثاني: وهو أنهم يجزمون ولا يكونون عالمين، لاحتمال الغلط في المستند. وهذا هو المحل الغامض في المسألة، وفي الانفصال عنه تحصيل الغرض.
فنقول والله المستعان: الطريق في تقرير الدليل أن نضبط ما يصح أن يسند أهل الإجماع الحكم إليه، [ثم] ننظر في إمكان الغلط عليهم فيه واستحالته.
أما الوجه الأول: فإن وجوب الاتباع وتحريم المخالفة حكم شرعي، لا يتصور أن يتلقى من أمر شرعي عقلي، فإن الصحابة رضي الله عنهم إنما تلقوا أحكام الشرع من رسول الله - صلى الله عليه وسلم -، إما كتابا وإما سنة، فالقطع بالحكم مع اعتراف الحاكم بكون المستند مظنونا محال أيضًا. والقوم قد قطعوا بكون الإجماع حجة، فلابد أن يقطعوا بالمستند.
فإذا قال القائل: ما المانع من أن يظنوا ما ليس بقاطع في المستند؟ فنقول: يتطرق الاحتمال إلى المنقول من وجهين: إما أن يعتقد غير المتواتر متواترا، وإما أن يعتقد غير النص نصا، إذ يتوهم في الوجهين. وقد يجوز أن يجتمع الوجهان في حق شخص، فيظن ما ليس بمتواتر متواترا، وقد يظن ما ليس بنص نصا.
أما الأول: وهو أن يظن ما ليس بمتواتر متواترا، فهذا مستحيل على العدد الكثير وإن لم يكونوا علماء، فإن الخبر المتواتر يحصل علما ضروريا. وليست الأمور المستندة إلى الأخبار المتواترة وغير المتواترة ما يحصل للسامع من قبيل','2','827',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1524,'النظريات التي هي محل الغلط. فإذا كان الغلط في هذا لا يتفق لعوام الناس، فكيف يتصور تطرق هذا الغلط إلى الصحابة بجملتهم على استمرار العصور من غير تنبه للغلط فيه، وهم أعلم خلق الله، وأعظمهم فطنة، وأتقنهم رأيا، وأشدهم بجثا وبصيرة؟ فيعلم بالضرورة [بطلان نسبة ذلك] إليهم، من جهة اعتقاد ما ليس بمتواتر متواترا.
وأما الوجه الثاني: وهو إمكان الخطأ باعتبار فهم الدليل حتى يعتقدوا فيه أنه قاطع وليس بقاطع، [فهذا] أيضًا محال، فإن القاطع في ذلك هو النص، إما باعتبار وضعه المطلق، أو بانضمام [قرينة] حالية أو مقالية إليه. فإن قدرنا أنه نص مجرد، استحال عليهم الغلط فيه، لأنهم أو أكثرهم أهل اللغة، فكيف يتصور عليهم الغلط في لغتهم؟ وذلك محال. وأما من كان منهم من غير أهل اللغة، فقد مارس العلم وعرف الفرق بين النص الظاهر، فلا يخفى عليه.
واعلم أن علماء زماننا لا يتلقون من علماء الصحابة العشر من الشريعة، ولا يتفق في الاعتياد أن يلتبس على جملة علماء الإسلام اللفظ الظاهر فيعتقدوه نصا، حتى لا ينتبه منتبه لجهة ظهوره. هذا محال في العادة. وأما إن قدرنا أنه نص بسبب القرائن، فالأمر كذلك أيضًا، فإن القرائن تحصل علوما ضرورية (١٦٥/ب) لمن شاهدها. وتقدير الإنسان أن القرائن قد يمكن أن لا تكون انتهت إلى حد يحصل العلم، باطل، فإن هذا أيضا مع الكثرة مستحيل في العادة.
يبقى أن يقال: من استناد كل واحد إلى سبب بحيث يجوز عليه الغلط، ولا يكون صاحبه استند إلى مستنده، وعند انفراد كل إنسان بمستند يتصور','2','828',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1525,'الغلط. قلنا: مقتضى هذه القضية أن لا يثق الإنسان بالأخبار المتواترة، [إذ] يمكن تطرق الغلط إلى كل واحد، وإن أسندوه إلى رؤية كرؤية كل شخص منفردة عن رؤية غيره. على أن تعدد المستندات بعدد الصحابة بالإضافة إلى مسألة واحدة محال. فإن الذين اجتمعوا في زمان عثمان - رضي الله عنه - على جمع المصحف نحو من اثنى عشر ألفا. فعلى هذا التقدير يكون في الشريعة اثني عشر ألف دليل يقتضي عصمة الأمة اتفق الغلط للكل في كل واحد من تلك الأدلة.
هذا محال.
ولو قدرنا اشتمال الشريعة على اثني عشر ألف دليل ظواهر تقتضي عصمة الأمة، للزم أن تكون القضية معلومة، فإن اجتماع دون هذا العدد من الظواهر يحصل علما بما دلت [الظواهر] عليه. فقد علم بما ذكرناه استحالة الخطأ عليهم في المستند وقد قطعوا. فلابد أن يكون المستند قاطعا.
وأما إلزام إجماع النصارى وغيرهم من الفرق المبطلة، فإنما صح ذلك، لأنهم لم يسندوه إلى أمور ضرورية عندهم، وإنما أسندوه إلى نظر العقول، وهو محل الشبه، وموضع اللبس والوهم. نعم، إن اتفقوا على حكم على مقتضى لغتهم، وهم أيضا يفرقون بين النص والظاهر في لغتهم، وقد كثروا، بحيث يمتنع عليهم التواطؤ، استوى الأمر، ولزمت الإصابة على مقتضى لغتهم. هذا تقرير الدليل، فقد ثبت كون الإجماع حجة بحمد الله على كل حال.
وهذا الأصل الذي قررناه هو أعظم أصول الشريعة، ولو لم يكن في كتابنا هذا إلا هذا المسلك، لكان بالحري أن يغتبط به. وحسبنا الله ونعم الوكيل.','2','829',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1526,'قال الإمام: (فأما الصورة الثانية: وهي [أنهم] إذا أجمعوا على حكم مظنون) إلى قوله (وبرهان ساطع في الشرع). قال الشيخ: ما ذكره الإمام ههنا صحيح من تبكيت من يخالف الإجماع على الإطلاق. وهذا مقبول منهم، ثابت ثبوتا لا شك فيه. وإذا قطعوا بتحريم المخالفة ووجوب الموافقة، لم يكن ذلك إلا عند مستند قاطع. إذ قد قررنا أنهم بجملتهم لا يصح عليهم الغلط في مستند الأحكام. وإذا وجبت الموافقة وحرمت المخالفة، فهل ينتهض حكمهم دليلا قاطعا، أو أمارة على وجوب العمل علينا، بما أثبتوا أن النظر في تحقيق العصمة ووجوب الاتباع؟
أما القطع بتبكيت المخالف، فيدل على وجوب الاتباع، [تحقيقا للعصمة]، فإنه قد يجب على الحاكم أن يحكم بشهادة الشاهد [في الظاهر]، وإن أمكن أن يكون مزورا [في الباطن]. وقد لا يحكم بشهادة','2','830',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1527,'الشاهد الواحد وإن كان محقا.
وهل يلزم من تحقيق العصمة وجوب الاتباع أم لا؟ أما من ذهب إلى أن المصيب واحد، فيوجب الاتباع، بناء على علم العصمة، إذ ليس لله تعالى في المسألة إلا حكم واحد. فإذا عصم الحاكم، علم أنه أصاب حكم الله المتحد. فلا سبيل إلى العدول عنه. وأما من قال: كل مجتهد مصيب، فلا يرتب وجوب الاتباع على تحقيق العصمة، إذ يصح أن يكون (١٦٦/أ) معصوما وإن لم يجب اتباعه. والمجتهد مصيب معصوم إذا اجتهد وصادف المحل. ولا يجب على مجتهد آخر اتباعه.
فوجوب الاتباع على هذا المذهب قضية لا ترتبط بالعصمة. ومختارنا نحن أن المصيب واحد، فنرتب وجوب الاتباع على العصمة، ونرتب العصمة على القطع بوجوب الاتباع في خصوصية الحكم. على أن من يقول إن المصيب واحد، يقول في هذه المسألة: إنهم مصيبون، وقد قالوا: يجب اتباعنا، وتحرم','2','831',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1528,'مخالفتنا. فيجب أن يكونوا متبعين في هذا الحكم. فقد تعذرت المعصية [ووجب] الاتباع.
وأما ما ذهب إليه الإمام من أنهم إذا صرحوا بالظن، وجب اتباعهم، لقطعهم بتبكيت المخالف. فهذا لعمري حق، ولكنه لم يستمر عليه، وأبدى بعد ذلك كلاما طويلا، ومنع عند اتفاقهم وتصريحهم بالظن، أن تنتهض الحجة في الحال. وشرط شرطا عسيرا، سيأتي الكلام عليه.

قال الإمام: <span data-type="title" id=toc-259>(المسألة الثالثة: في التنصيص على المسلك الذي ثبت الإجماع به) </span>إلى قوله (وراء الله للمرء مذهب). قال الشيخ: أما إذا قطعوا','2','832',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1529,'بالحكم، فالأمر واضح في استنادهم إلى قاطع. وأما إذا ظنوا، فلا يصح أن يكون مستند الحكم المظنون قاطعا عندهم. إذ كيف يكون المستند قاطعا وهم ظانون؟ هذا محال. لا جرم ذهب بعض الأصوليين إلى أن الإجماع إذا صرح المجمعون فيه بالظن، لا يكون حجة. وهذا لعمري قياس من يجعل الإجماع دليل الدليل. وسيأتي الكلام على الإجماع المظنون بعد هذا.
أما من قطع نظره عن مستند الإجماع، ويقول: أوجب الله تعالى الاتباع','2','833',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1530,'على الإطلاق كيفما كان الحال، فلا ينظر إلى المستند على حال. وظاهر كلام الإمام أنه إنما ينظر إلى مستندهم. لا جرم يقول في الإجماع المظنون: لابد أن يطول الزمان، وتتكرر الواقعة، بحيث لو أمكن أن يكون بخلاف ما أسندوا الحكم إليه لظهر. وسنتكلم عليه بعد هذا، إن شاء الله.

قال الإمام: <span data-type="title" id=toc-260>(فصل - الكلام بعد هذه المسائل الثلاث في أربعة فنون) </span>إلى قوله ([ويجمعه] مضمون المسألة التي نرسمها). قال الشيخ: لما قلنا إن الإجماع اختلف الأصوليون في حده: فذهبت طائفة إلى أنه إجماع أمة محمد - صلى الله عليه وسلم - على أمر من الأمور. وقال آخرون: إجماع أهل الحل والعقد على أمر من أمور الدين. فأما أصحاب الحد الأول، فيعبرون العوام في الإجماع، حتى لو اتفق المجتهدون [وقول] أهل الفتوى، فلابد إذًا من النظر في العدد والصفة. هذا أحد الأركان الثلاثة. والركن الثاني تحقيق الاتفاق، والركن الثالث المتفق عليه. لأنه لما قلنا: اتفاق أهل الحل والعقد على أمر من أمور الدين،','2','834',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1531,'تضمن الكلام الأركان: المتفقين والاتفاق والمتفق عليه. وباستقصاء هذه يتنجز الكتاب.
وبقي الحدان عندي على الخلاف في السبيل الدال على عصمة الأمة. فإن أخذ من السنة، وهو قوله: (لا تجتمع أمتي على الضلال أو الخطأ). فلابد على هذا من الجميع، ليتحقق استقلال العموم في جميع الصور، إذ [هو] المقصود، وإن أخذ من جهة قطع الحاكمين بالحكم، فهذا لا مدخل للعوام فيه، إذ ليسوا ممكنين من الحكم بحال. وإذ نبهنا على سبب الاختلاف، فلنذكر الآن المسائل وما فيها (١٦٦/ب) من الكلام.
الأمة بالإضافة إلى الإجماع على ثلاثة أوجه:
طرف واضح في اعتبار القول، وتوقف الأمر عليه، وهو كل مجتهد مقبول القول، فهو من أهل الحل والعقد قطعًا.
الطرف الثاني: المجانين والأطفال ومن لا عقل له، إذ لا يتصور الاجتماع والاختلاف ممن لا يتصور منه فهم المسألة. فهذا الصنف يجب الإعراض عنه على الحدين جميعًا، سواء تمسكنا بالأخبار أو بالطريق الآخر من البحث عن مأخذ المجمعين.','2','835',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1532,'وبين الدرجتين وسائط كالعامي، والفقيه الذي ليس بأصولي، والأصولي الذي لبس بفقيه، والمفسر، والمجتهد الفاسق، ومن قارب درجة الاجتهاد. فلنذكر حكم كل مسألة من هذه المسائل.
مسألة: يتصور دخول العوام في الإجماع، فإن الشريعة منقسمة إلى:
ما علم ضرورة، واشترك في معرفته العوام والخواص، كوجوب الصلوات والصوم والزكاة والحج. فهذا قد علم المسلمون كلهم حكمه واتفقوا عليه، فيحسن تسمية هذا إجماع جميع الأمة.
الثاني: تفاصيل هذه الأصول. فهذا وإن كان العوام لا يعرفون التفاصيل، إلا أنهم قد سلموا للعلماء في ذلك، وعلموا أن حكم الله عليهم ما يقوله العلماء، فيصح أن يسمى هذا أيضًا إجماع جميع الأمة. كما أن أهل الجند إذا حكموا طائفة في صلح أو غيره ففعلوا، صح أن يقال: اتفق الجند على ذلك. فإذًا كل مجمع عليه من العلماء، فهو مجمع عليه من العوام أيضًا، وبه يتم إجماع جميع الأمة.
فإن قيل: فلو خالف عامي في مسألة أجمع عليها الخواص من العصر، فهل ينعقد الإجماع دونه أو لا ينعقد؟ فإن قلتم: إنه ينعقد، فكيف خرج العامي من الأمة؟ وإن قلتم: لا ينعقد، فهو محال. إذ كيف يتوقف قول العلماء على','2','836',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1533,'موافقة العامي؟ قلنا اختلف الناس في ذلك، فذهب ذاهبون إلى أنه ينعقد، لأنه من الأمة، فلابد من موافقته بالجملة أو التفصيل، ليتحقق اجتماع كلمة الأمة، لأنه مندرج تحت العموم. فإذا جرت صورة من الصور، لم يقطع ببقاء دلالة الدليل. وذهب المحققون إلى أنه لا يعتبر لدليلين:
أحدهما - أنه لا يفهم من العصمة عصمة من هو أهل الفتوى في المسألة بعد فهمها والإحاطة بها، والعامي ليس له أهلية هذا الشأن، فهو بمثابة المجنون لا شك فيه. فإن هذا لا يتصور ضرورة من عامي عاقل. فإن العاقل يفوض ما لا يدري لمن يدري. فإن أصر على المخالفة، فلا التفات إليه. ولهذا لا يتصور صدور هذا من عاقل. فهذه صورة فرضت لا وجود لها.
الثاني - أن الأمة مجمعة على أن العوام لا يلتفت إلى قولهم، أعني الصحابة رضوان الله على علمائهم وعوامهم، فإنهم لم يراجعوا العوام في الأحكام. وكيف يراجع من لا بصيرة عنده بحال؟ بل لو أفتى عامين لاشتد عليه النكير، وزجر عن ذلك أشد الزجر. وقد ذم الشارع من يقول بالجهل والهوى. وقال [- صلى الله عليه وسلم -]: (إن الله تعالى لا ينتزع العلم انتزاعًا من صدور الرجال، ولكن يقبض العلماء، فإذا لم يبق في الأرض عالم اتخذ الناس رؤوسًا جهالاً فسئلوا فأفتوا بغير علم فضلوا وأضلوا). ولكن هذا يدل على تحريم','2','837',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1534,'المخالفة، ولا يدل على انعقاد الإجماع دونهم. والأدلة هي التي تقدمت.
وإذا ثبت أن قول العوام لا يعتبر، فرب نحوي ومفسر (١٦٧/أ) ومتكلم كالعوام، بالإضافة إلى علم الإجماع، فلا التفات إلى قولهم بحال. إلا أن يكون الكلام في مسألة تنبني على النحو، أو على الكلام، فيكونون من أهل النظر فيها، فلا التفات إلى قولهم لنقصان الآلة.
وإنما يبقى النظر في الأصولي الذي لم يبلغ مبلغ المجتهدين، وفي الفروعي الذي لم يمارس الأصول، ولم يحط بها على ما ينبغي، والمجتهد الفاسق العالم بفسق نفسه، والمبتدع إذا قال ببدعة تقتضي تفسيقه. فلنفرض في كل واحد مسألة.
قال الإمام: (ذهب القاضي [رحمه الله] إلى أن الأصولي الماهر المتصرف في الفقه يعتبر وفاقه وخلافه) إلى قوله ([والقول] الكافي في ذلك','2','838',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1535,'أنه إن كان من [أهل] الفتوى اعتبر خلافه). قال الشيخ: ما ذكره الإمام في هذه المسألة حق واضح، وطريقه فيه لائح، والأصولي الذي ذكره القاضي ليس من أهل الاجتهاد، وإذا وقعت له واقعة في خاصة نفسه، وجب أن يراجع فيها غيره عقلا، من جهة وجوب الاستفتاء، والمنع من العمل بالرأي، فيساوي العامي من هذه الجهة. ولا التفات إلى ما عنده من آلة صالحة لأن يوزن بها ما يرد [عليه]. فإن الصيرفي لا يكون غنيا بالميزان والصنج ومعرفة النقد، إذا لم يكن عنده النقدان. فكذلك الأصولي حصل له ما يوزن به أفراد الأدلة، وبقي عليه تحصيلها لتوزن. والفتوى في الأفراد تفتقر إلى معرفة تفاصيل الأدلة. وليس هذا علم الأصول، فالتحق من هذه الجهة بالعامة في وجوب المراجعة، وتحريم الاستبداد. وإذا حرم عليه أن يفتي، فكيف يراجعه المفتون لأخذ قوله','2','839',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1536,'الذي لا يجوز له أن يقوله؟ هذا محال ومتناقض.
وقد قال أبو حامد: إنه لا يشترط في الفتوى حفظ الفروع. ويشير بهذا إلى أن الأصولي مجتهد، واستدل بأن طلحة والزبير والعباس لم يشتغلوا بحفظ الفروع، وقد كانوا مجتهدين تقبل أقوالهم. قال: وكيف لا يكونون كذلك، وقد كانوا صالحين للإمامة العظمى، وسمي أكثرهم في الشورى؟ قال: وكيف','2','840',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1537,'يشترط حفظ الفروع في صحة الفتوى والفروع هي التي ولدها المجتهدون، فلو كان علم الاجتهاد لا يحصل إلا بعد تحصيلها، وهي لا تحصل إلا بعد حصول درجة الاجتهاد [لأفضى] ذلك إلى الدور؟
وهذا الذي قاله غلط عندي، كأن أحدا لا يشترط حفظ الفروع في نيل درجة الاجتهاد، على حسب ما قصد، وكيف يشترط ذلك والمجتهدون هو الذين يولدون المسائل ويقرونها؟ فلا يشترط في بلوغ درجة الاجتهاد حفظها بحال. وأما العباس وغيره فقد كانوا مجتهدين، ولا يمنعهم من ذلك عدم الفتوى منهم بالإضافة إلى غيرهم. فلا يتلقى أنه لا علم عندهم بالفروع بالإضافة إلى عدم الخوض فيها. ولقد كان القوم مشغولين بغير الفتوى، فلا يدل كونهم لم','2','841',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1538,'يشتهروا بالفتوى، ولم يتظاهروا [بها تظاهر] العبادلة، أنهم كانوا لا يعلمون الفروع. وهل يعتد بالفروع إلا عن تلقي الأحكام الجزئية من الأدلة الخاصة؟ وهذا هو الذي كان القوم يعرفونه.
وأما إذا صار القاضي إلى أن الأصولي المحض مجتهد، فهذا غير صحيح، فإن علم الأصول يخالف علم الفقه، من جهة أن الأصولي إنما يخوض في تحقيق القواعد والأحكام (١٦٧/ب) والأمارات على مسائل الفروع. فليس هذا علم الأصول بحال. وإن صور أنه أحاط بالأصول كما ينبغي، وأحاط بتفاصيل أدلة الفروع، فهو المجتهد بعينه، وليس الكلام فيه. هذا هو الكلام على الأصولي الذي ليس بفروعي.
وأما الفقيه الذي ليس بأصولي، فالصحيح أيضًا أنه لا يتلفت إلى قوله في','2','842',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1539,'انعقاد الإجماع. والطريق [هو] الذي قررناه أولاً، فإنه يحرم عليه أن يقول باجتهاده، ويجب عليه الرجوع إلى قول غيره. فإذا وقعت الواقعة للمجتهدين، كيف يشاورون فيها من تحرم عليه الفتوى فيها؟ أو كيف يجوز استفتاء من يحرم استفتاؤه؟ فالفروعي والأصولي عندي على رتبة واحدة في تحريم المخالفة عليهم، ووجوب الرجوع إلى أقوال المجتهدين. وما أحسن قول الإمام: (ليس بين من يقلد ويقلد مرتبة ثالثة).
قال الإمام: (مسألة: ذهب معظم الأصوليين إلى أن الورع معتبر في أهل الإجماع) إلى قوله (فهذا ما تمس الحاجة [إليه] من صفات المجتهدين).
قال الشيخ: الفاسق على وجهين: فاسق يعلم فسق نفسه، وفاسق لا يعلم فسق نفسه، بل قال ببدعة أوجبت تفسيقه.
القسم الأول: وهو الذي تكلم عليه الإمام إذا كان يعلم فسق نفسه. وفي المسألة نظر. والصحيح عندي ألا يلتفت إلى مخالفته بالإضافة إلى','2','843',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1540,'غيره. وهذا بمثابة إمكان أن يكون في الأرض عالم لم يعلم به، فإنه لا يتصور أن يتوقف الإجماع على علم نفيه، إذ لو كان شرطا في قيام الإجماع حجة، لم يتصور ثبوته بحال، فلا يشترط في الحجج إلا ما يمكن الوصل إليه، وإن كان على عسر.
ويحقق [ذلك] أن المجتهد الفاسق لو وافق، فلا ذاهب يذهب إلى أن الإجماع لم ينعقد حجة، وباطنه غير مطلع عليه. فلو كان احتمال كونه مخالفا باطنا يقدح في الإجماع، لكان ذلك موجودا عند موافقته أيضًا. فإنه لا يظن به الصدق، خالف أو وافق. فلما استحال [علم] ما عنده، تنزل منزلة المعدوم فيما يتعلق بالإجماع والخلاف، فلا التفات إليه بحال. ويتنزل هذا','2','844',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1541,'منزلة الخضر مثلا، إذا قلنا إنه حي، وهو الظاهر من أقوال أهل العلم، وأنه من أهل الاجتهاد، وهو الظاهر. فإنه متعبد بهذه الشريعة، من جهة أن رسول الله - صلى الله عليه وسلم - بعث للكافة. وقال - عليه السلام -: (لو كان ابن عمران حيا، ما وسعه إلا اتباعي).
ولكن لما لم يكن للخلق طريق إلى [استفتائه]، لم يكلفوا بموافقته،','2','845',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1542,'ولا حرمت عليهم مخالفته. وما ذلك إلا لاستحالة اعتبار أقواله، بالإضافة إلى أقوال العلماء. فكذلك لما اختلفت ترجمة اللسان بالفسق الظاهر، تعذر الوقوف على مخالفته وموافقته، فوجب صرف النظر عنه في حق غيره، وإن كان له في حق نفسه حكم آخر. هذا هو الذي نراه. والله الموفق للصواب.
المسألة الثانية: مسألة المبتدع ولها صورتان: إحداهما - أن يقول ببدعة توجب التكفير. الصورة الثانية - أن يقول ببدعة لا توجب التكفير.
أما الصورة الأولى إذا كانت البدعة توجب تكفيرا، فلا التفات إلى موافقته، ولا تضر مخالفته، لأن العصمة ووجوب الاتباع ثبت للأمة، وهذا ليس من الأمة (١٦٨/أ) واعتقاد أنه منهم، لا يصيره منهم بعد أن كفرناه، وإن كان يصلي إلى القبلة ويعظم الدين، فإن ذلك كله غير ملتفت إليه.
فإن قيل: فلو خالف هذا المبتدع المكفر في مسألة، فبلغ قوله بعض الفقهاء فخالف، بناء منه على أنه لم يعلم أنه كافر، فهل ينخرق الإجماع بذلك؟
وهل يكون الفقيه معذورا أم لا؟ قلنا: للمسألة صورتان:
إحداهما - أن يبلغه خلافه، ولا تبلغه بدعته، فيخالف لأجل ذلك، فهذا معذور، إذ بنى الأمر على ظهور الإسلام، وهذا هو من المسلمين ظاهرا، فيكون الحكم منقوضا والحاكم معذورا. وهو بمثابة ما لو بلغه المنسوخ وعمل به ولم يبلغه الناسخ، لا لتفريط من قبله، فإنه لا يكون آثما بذلك، وكذلك هو.
الصورة الثانية: أنه تبلغه بدعته، ولكن لا يدري أنها تقتضي التكفير أم لا. ففي هذه الصورة لا يعذر بترك الإجماع لأجل مخالفته، ولكن عليه أن','2','846',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1543,'يبحث، هل هي بدعة تقتضي تكفيرا أم لا؟ قال أبو حامد: فإن قدر على ذلك، وإلا فليراجع علماء الكلام حتى يطلعوه على الدليل. فإن لم يفهمه، فليقلد في أن هذه البدعة تقتضي تكفيرًا، فإن لم يقنع بالتقليد، ولم يفهم دليل التكفير، لم يعذر، إذ لا عذر بعد نصب الله تعالى الأدلة القاطعة.
وهذا الذي قاله أبو حامد لا يصح عندي، لأن علم الكلام لا يرشد على ما يكفر به، بالنظر إلى محض المعقولات، وإنما يقع التكفير بالنظر إلى الأدلة الشرعية. فكيف يصح أن يكون المجتهد غير عالم بما يكفر به في الشريعة؟ وإن أراد بالتكفير الجهل بالله عز وجل، فلا يصح أن يكون مجتهدًا من هو جاهل بالله على حال. وإنما هذا بناه على أصله في صحة نيل درجة الاجتهاد مع التقليد في معرفة الله عز وجل ومعرفة رسول - صلى الله عليه وسلم -. [و] هذا ما لا يصح عندنا بحال.
فإن قيل: فما الضابط لما يكفر به؟ قلنا: قد أكثر الناس في ذلك، فالذي يصح عندنا يرجع إلى ثلاثة أمور:
أحدها - ما يكون نفس اعتقاده كفرًا، كإنكار الصانع وصفاته التي لا يصح أن يكون صانعًا إلا بها. إما بالتصريح بالإنكار، أو بالمصير إلى أمر يمنعه اعتقاده من الاعتراف بالصانع، ويلزمه ذلك من جهة التناقض وجحد النبوات، فإن ذلك كفر بلا خلاف.
الثاني - ما ورد الشرع بأنه لا يصدر إلا من كافر، كعبادة النيران والسجود للصنم، وما يضاهي ذلك.','2','847',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1544,'الثالث - إنكار ما علم من دين الأمة ضرورة، كإنكار سورة من القرآن، وجحد الصلوات الخمس، وبالجملة إنكار ما علم بالضرورة والبديهة. وقد بينا أن السبب الذي منه كفر، إنما هو من جهة أن النقل المتواتر يحصل علمًا بإسناد القضية إلى الرسول - صلى الله عليه وسلم -. فإذا وقع الجحد بعد ذلك بأنه ليس من الشريعة، كان ذلك آيلاً إلى تكذيب الشرع، ومكذب [الشرع] كافر. وسيأتي هذا الفصل في مسألة من خرق الإجماع.
الصورة الثانية: للمبتدع الذي يقول ببدعة لا تقتضي تكفيرًا. فهذا قد قدمنا اختلاف الناس في قبول شهادته. فمن [قال] تقبل شهادته، فهذا مجتهد مقبول الفتوى، فهو من أهل الحل والعقد قطعًا، فلابد من موافقته. وإن رددنا روايته وشهادته - وهو الصحيح عندنا - فالصواب عندي على هذا الرأي أن لا ينعقد الإجماع عند مخالفته. فإن رد شهادته، لم يكن لعدم ظن صدقه، بل قلنا: إن بدعته (١٦٨/ب) اقتضت الإعراض عنه وهجره والامتناع من استفتائه. فهو عقوبة له وتحقير من قدره، وزجر له عما تعاطاه من بدعته.
وأما انعقاد الإجماع عند مخالفته، فخارج عن هذا الباب. لأنا حصل لنا ظن بتعدد كلمة الأمة واختلافها، فكيف نقول: أجمعت الأمة ونحن نظن اختلافها؟ ولا يتنزل ذلك منزلة من يعلم فسق نفسه، لأن فسقه سد علينا طريق معرفة قوله. هذا هو الذي نختاره في ذلك. والله الموفق للصواب.','2','848',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1545,'قال الإمام ([والصفات المعتبرة في المجمعين من الصفات الشرعية في آحاد المفتين]) إلى قوله (فهذا موضع التردد). قال الشيخ: أما الكلام على المبتدع المكفر وغيره، فقد ذكرناه. وأما قبول فتوى المبتدع، ففيه الخلاف الذي في قبول شهادته وروايته. وقد نقلنا مذهب الشافعي في قبول قول أهل','2','849',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1546,'الأهواء إلا الخطابية من الرافضة، لأنهم يرون الشهادة بالزور [لموافقيهم] في المذهب.
قال الإمام: (ذهب بعض [الأصوليين] إلى أنه لا يجوز انحطاط علماء العصر عن [عدد] التواتر) إلى قوله (فهذا حاصل القول في أوصاف المجمعين وعددهم). قال الشيخ: ما ذكره الإمام من اشتراط أن يبلغ أهل الإجماع عدد التواتر، لا يصح على أصله، فإنه يرى أنه إذا أجمعوا على أمر مظنون، كان إجماعهم حجة، نظرا إلى قطع المجمعين بتبكيت من خالف أهل الإجماع. ولا معنى لاشتراط عدد التواتر عندما يظنون الحكم، ولم يقل المبكتون لمخالف الإجماع بأنهم، هل بلغوا عدد التواتر أم لا؟ وأي حاجة إلى عدد التواتر عندما يكون القوم يظنون؟ نعم، قد يتوهم المتوهم أنهم إذا قطعوا مع الكثرة، فلابد أن يكون المستند قاطعا. وقد بينا أن ذلك غير لازم، على حسب ما قررناه.
فإذا ثبت أنه لا يلزم في انتهاض الإجماع حجة في استمرار الزمان، وإن','2','850',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1547,'كان ذلك مشترطا في أول الإجماع، ولكن لما استقر الإجماع الأول تلقي منه وجوب الاتباع وتحرمي المخالفة على الإطلاق، فلا التفات إلى العدد في مستقبل الزمان، كما أنه لا التفات إلى القطع بعد الإجماع الأول.
وأما تصور نقصان عدد أهل الإجماع عن عدد التواتر في مستقبل الزمان، ففيه خلاف. قال قائلون: لا يصح ذلك، فإن هذه الشريعة متمادية إلى قيام الساعة، وقد ضمن الله تعالى دوامها واستمرارها إلى آخر الدهر، وإنما تدوم الشريعة بدوام حججها بثباتها. وإذا نقص عدد العلماء عن عدد التواتر، لم يقطع ببقاء الحجة، فلا يدوم التكليف، وهو خلاف ما تقرر من الدوام من الأخبار والآثار، قال النبي - صلى الله عليه وسلم -: (لا تزال طائفة من أمتي على الحق لا يضرهم خلاف من خالفهم إلى أن يأتي أمر الله وهم [ظاهرون]). وقال - عليه السلام -: (كيف بكم','2','851',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1548,'إذا نزل فيكم ابن مريم حكمًا مقسطا، فكسر الصليب، وقتل الخنزير، وإمامكم منكم). فهذه الأخبار وأمثالها تقتضي دوام الشرع بدوام أدلته. هذا تقرير هؤلاء القوم.
وقد قال كثير من الأصوليين: يجوز نقصان عدد المجمعين عن عدد التواتر. وهذا هو الصحيح عندنا. كيف ذلك والوجود كاشف له ويحقق أمره؟ ولسنا نعلم اليوم مجتهدا مستقلا بنفسه في استنباط الأحكام، (١٦٩/أ) غير مفتقر إلى التقليد للأئمة الماضين.
وأما ما تخيلوه من أنه لو نقص علماء الشريعة عن عدد التواتر، لبطلت الحجة، ولم تعلم الأدلة. فهذا غير صحيح، فإنه قد بقي متواترا بعثة الرسول - صلى الله عليه وسلم - وتحديه بالقرآن الذي هو المعجزة، فلا حجة على الله بعد الرسل. وإذا ثبتت النبوة ببقاء معجزتها، توجهت أحكام الإسلام، فما كان منها معلوما، وجب المصير إليه والتمسك به، وكذلك المظنون في العمليات. وما التبس وتعذر على المكلفين الوصول إليه، فالتكليف به ساقط.
وأما اندراس الأعلام بالكلية، فلا تندرس، بل يبقى أصل الدين منقولا متواترا، إما من العامة مع العلماء. فإن نقل العلم لا يفتقر إلى تحصيل درجة','2','852',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1549,'الاجتهاد. ألا ترى أن المجتهدين في زماننا مفقدون أو قليل، والأعلام معلومة متواترة، فلا ربط بين انقراض العلماء أو نقصان عددهم عن عدد التواتر، وبين انطماس [الشريعة] واندراسها. ومصداق ذلك وجوده.
وقد ذهب الإمام إلى تجويز الاندراس بالكلية، وانتهاء الأمر فيه إلى الفترة. وإن كان الظاهر عندنا خلافه، وأن هذه الشريعة باقية إلى قيام الساعة. وقد يتفق أن ينقص العدد ويحصل العلم بقول العدد القليل مع قرائن الأحوال، وليس ذلك مستنكرا في العادات. وقد يتفق أن تنقل الأعلام على ألسنة الجهلة، ولا يعرفون بكونها أعلاما. وكم من يهودي أو نصراني يقرأ سورة من القرآن ولا يعرف بكونه معجزا. فبمجموع هذه الأمور يبقى الدين محفوظا، وإن عدم المجتهدون أو قلوا.
فإن قيل: فإذا جاز أن يقل عددهم عن التواتر ويكون حجة، فلو رجع العلماء إلى واحد، هل يكون قوله حجة حتى تمتنع مخالفته على غيره من بعده؟ فإن قلتم: يمتنع، فهذا ليس بإجماع، إذ لا يتصور إجماع إلا باجتماع، وأقل ذلك اثنان. قلنا: هذه المسألة فيها نظر، ولكن إن قلنا: إن قول العوام','2','853',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1550,'معتبر في الإجماع، فقد تحقق مدلول الإجماع، وسهل الأمر على هذا لمن أخذ عصمة أهل الإجماع من الكتاب والسنة، فإن قول العالم مع العالم يطلق عليه لفظ الإجماع. وإن قلنا: إن قول العوام لا يعتبر، وهو الصحيح، لم تكن ألفاظ الكتاب والسنة تتناول هذه الصورة بحال.
لكن يصح أن يسلك مسلكا آخر، وهو حفظ الشريعة في كل زمان بكونه لا تخلو الأرض عن قائم الله تعالى بحجة. فعلى هذا إذا لم يكن في الأرض إلا عالم واحد، لزم أن يكون محقا في قوله.
ولكن هذا يخالف قوله - عليه السلام -: (لا تزال طائفة من أمتي على الحق حتى يخرج الدجال، وحتى تقوم الساعة). فلعل من يجوز هذا، يجعل هذا من أخبار الآحاد. والظاهر عندي أن هذه لا تكون، وقد أخبر النبي - صلى الله عليه وسلم - بأن: (هذه الأمة تقاتل الدجال، وينزل عيسى - عليه السلام - وإمام المسلمين منهم). فهذه أمور بينة تدل على أنه لا ينتهي الأمر إلى فترة الشريعة وانقراض العلماء بجملتهم.
هذا تمام ما ذكره الإمام في هذه المسألة. وكنا قد قلنا بعد إثبات كون الإجماع حجة في ثلاثة أركان: في المجمعين، وحقيقة الإجماع، وفي','2','854',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1551,'المجمع. وتكلمنا على صفة المجمعين وعددهم، وبقي النظر في أمر آخر، وهو أن الصفات والعدد مختصة ببعض أهل الإسلام، أو مطردة في الجميع؟
وهذه المسألة مقصودها (١٦٩/ب) أنه هل يكون إجماع غير الصحابة حجة أم لا؟ وكان حقها أن تذكر في هذا الموضع، ولكن أخر الإمام ذكرها، فلنذكرها حيث ذكرها.
الركن الثاني: في نفس الإجماع، وبيان ما يكون إجماعا، وما لا يكون إجماعا. والذي يقتضيه التحقيق أن الإجماع الذي هو حجة: اتفاق كلمة الأمة في مسألة، ولو في لحظة واحدة، [وكانت] الفتوى نطقا صريحا، سواء قطعوا بالحكم أو ظنوه، انقرض عليه العصر أو لم ينقرض.
وتمام البيان في هذا أن مجرد السكوت ليس بحكم، وأن انقراض العصر ليس بشرط، وأن الإجماع إذا استند إلى اجتهاد، فهو حجة. فهذه ثلاث مسائل.
قال الإمام: (مسألة: اختلفت مسالك القائلين بالإجماع في اشتراط انقراض [عصر] المجمعين) إلى قوله (فهذا منتهى القول في [الزمن] وما يتعلق به). قال الشيخ: ما ذكره الإمام من الإجماع الذي قطع به المجمعون،','2','855',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1552,'لا يشترط انقراض العصر فيه، كلام صحيح.
وأما ما ذكره من أنهم إذا أسندوا الحكم إلى الظن إلى آخره، فهذا يفتقر إلى كشف ومزيد بحث، فنقول: اختلف الناس، هل يجوز أن يستند الإجماع إلى ظن، ويتفق عليه المجمعون، أو ذلك ممتنع؟ فذهب ذاهبون إلى أنه ممتنع. وقال قائلون: إنه جائز. ثم اختلف المجوزون أنه إذا وجد، هل يكون حجة أو لا يكون حجة؟ فقال الأكثرون: هو حجة. وقال آخرون: إنه ليس بحجة. وفرق الإمام هذا التفريق الذي ذكره. وسنتكلم عليه.
فأما من قال إنه غير جائز، فإنه تمسك بأن قال: كيف تتفق الأمة مع تفاوت طباعها واختلاف أفهامها في الذكاء والبلادة على مظنون؟ وإنما ذلك بمثابة اتفاقهم على التغذي في صبيحة يوم، بنوع من الطعام، أو تناول شراب،','2','856',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1553,'فإن ذلك ممتنع من طريق الاعتياد. قلنا: هذا إنما يمتنع في الزمان الفرد والساعة الواحدة. فأما إذا طال الزمان، فلا يبعد أن يصير الأذكياء إلى الجهة الظاهرة، ويقررون ذلك عند ذوي البلادة، فيوافقونهم على هذا المذهب.
ثم أيضًا إنما يتعذر ذلك عند استواء الاحتمالات. فأما إذا ظهر التفاوت الجلي، فلا يمتنع الإطباق عليه. وأهل هذا المذهب قد أطبقوا على نفي القياس مع ظهور أدلته. فإذا جاز الاتفاق على نقيض الصواب، بناء على وهم وخيال، فكيف يمتنع الإطباق على أمر ظاهر جلي؟
الشبهة الثانية: أنهم قالوا: الخطأ في الاجتهاد [جائز]، فكيف تجتمع الأمة المعصومة على ما يجوز فيه الخطأ؟ وربما قالوا: الإجماع منعقد على جواز مخالفة الاجتهاد، فلو جاز اتفاقهم على اجتهادٍ، لحرمت المخالفة، التي هي جائزة بالإجماع، ويتناقض الإجماعان. قلنا: إنما يجوز الخطأ في اجتهاد ينفرد به الآحاد، أما اجتهاد الأمة المعصومة، فلا يحتمل الخطأ، كاجتهاد رسول الله - صلى الله عليه وسلم - وقياسه، فإنه لا يجوز خلافه، لثبوت عصمته، [فكذا] عصمة الأمة','2','857',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1554,'من غير فرق.
الشبهة الثالثة: قالوا: كيف يجوز أن تجتمع الأمة على قياس، وأصل القياس مختلف فيه؟ وكثير من الأمة على رده، فلا يتصور أن يسندوا الحكم إلى ما يعتقدون بطلانه. قلنا: إنما يصح (١٧٠/أ) ذلك ممن يعترف بصحة القياس. والذي عليه المحققون أن منكري القياس ليسوا من علماء الأمة، ولا ينخرق الإجماع بمخالفتهم، وكيف يعدون من المجتهدين، ولا اجتهاد عندهم؟ وإنما غايتهم التردد على ظواهر الألفاظ.
وقد يصح أن يسندوا الحكم إلى قياس على الحقيقة، وإن كانوا لا يرونه قياسا. فقد يظن القياس غير قياس. [فيسند] القائسون الحكم إلى قياس، يعترفون بكونه قياسا، [ويستند] الجادون إلى قياس، بناء منهم على أنه غير','2','858',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1555,'قياس. فقد قال النظام: النص على التعليل نص على التعميم، وليس بقياس، وهو غالط في ذلك. وإذا تصور ذلك، فالمصير إلى أنه غير حجة باطل، لأنا قد قررنا بالأدلة أن الإجماع حجة قاطعة على ما سبق، والأدلة لم تفرق بين إجماع وإجماع.
انقراض العصر، فتحكم، فإن اشتراط انقراض العصر لا يولد الإجماع، وإنما هو يرجع إلى استمرار. والحجة إذا لم تنتهض في وقت وقوعها، لم يتغير أمرها بدوامها. والشيء يدوم على حسب ما يثبت. فلئن كان الاستناد إلى الظن يمنع من حصول العلم بالعصمة، فذلك لا يتغير باستمراره على حال.
وقولهم: كيف يتمسك بالإجماع بعد قيام الإجماع؟ غلط بين، فإن الإجماع محقق، والاختلاف مفقود، فكيف لا تكون حقيقة الإجماع معقولة؟','2','859',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1556,'ولئن تمسكوا بأن القول بالاجتهاد تناقض مع المخالفة، فهذا ثابت، وإن انقرض العصر. فلئن قالوا: ما داموا في الأحياء، فرجوعهم متوقع. قلنا: والكلام في رجوعهم إن انقرض واحد، فهو محجوج بالإجماع، ورجوع الجميع محال.
ثم نقول: كيف ذلك، ونحن نعلم أن التابعين في زمان أنس بن مالك، وأواخر الصحابة كانوا يحتجون بإجماع الصحابة، وإن كان بقي بعضهم في الوجود، لم يكن جواز الاحتجاج متوقفا على موت الجميع؟ وقد قال عمر لعلي: (ومهما شككت فيه، فلا تشكن في أن يد الله مع الجماعة). وقد تمسكوا بشبه:
الأولى - أنهم قالوا: ربما قال بعضهم ما قال عن وهم وغلط، وكيف يمنع من الرجوع إلى تيقنه؟ أم كيف يحجر على المجتهد إذا تغير اجتهاده أن يرجع','2','860',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1557,'إليه؟ وكيف يؤمن الغلط في اجتهاد يجري في لحظة واحدة؟ قلنا: وإذا مات من أين يحصل الأمن من ذلك؟ وهل يؤمن من هذا الغلط إلا ثبوت عصمة الأمة؟ فلئن جاز أن تجتمع على الخطأ في زمان، جاز ذلك مطلقا، إذ ما يجوزه العقل في زمان، يجوزه في كل زمان.
فإن قال: تحققت أني قلت ما قلت عن اجتهاد كذا، وقد تبين لي خلافه قطعا. فهذا سؤال غامض. وقد أجاب عنه أصحابنا بأن قالوا: إذا قال ذلك، قلنا: أنت إنما غلطت في الطريق دون الحكم، فإن الحكم هو المتفق عليه، وأما الطريق فأنت منفرد به، فلا تتحقق العصمة فيه. وهذا عندي فيه نظر، وكيف يصح إصابته في الحكم، إذا سلمنا أنه أخطأ في المستند قطعا؟ هذا بعيد. ولكن الصواب عندي أن يقال: قولكم: إنه قال: إني أخطأت في المستند قطعا. عنه جوابان:
أحدهما - أن نمنع ذلك، فإنه يفضي إلى أن تتفق كلمة الأمة والخطأ متأت، وذلك ممتنع.','2','861',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1558,'الثاني - أنه إن قال ذلك، قلنا له: أنت غير قاطع بخطأ المستند، بل توهمت بطلانه، وليس بباطل.
الشبهة الثانية: قالوا: لا حجر على المجتهد إذا تغير اجتهاده في الرجوع. (١٧٠/ب) وإذا جاز له الرجوع، دل على أن الإجماع لم يتم. قلنا: هذا صحيح، لو كان منفردا باجتهاده، أما اجتهاد توافقه الأمة عليه، فلا يجوز له أن يرجع عنه، كالنص الذي يحرم الرجوع عنه إلى الاجتهاد. فإن الإجماع حجة قاطعة، فلا يبقى للاجتهاد في مخالفتهم مجال.
الشبهة الثالثة: أنهم قالوا: لو مات المخالف، لم تصر المسألة إجماعية، وإن كان الباقون كل الأمة، لكنهم في بعض العصر، دل ذلك على أنه لابد من اعتبار انقراض العصر في تحقيق الإجماع. وهذا غير صحيح، وليس السبب بقاء بعض، لكن السبب الصحيح أنهم ليسوا كل الأمة، بالإضافة إلى تلك التي أفتى فيها من مات بعد أن أفتى، فإنا سنبين أن المذاهب لا تموت بموت أربابها. فالباقون بعض الأمة، [ومخالفته] مسوغة، فلذلك لا يصير مذهب المخالف مهجورًا.
وقد قال قوم: يبطل مذهبه بموته، والباقون بعده كل الأمة، وتحرم مخالفتهم. وهذا غير صحيح، لما سنقرره. [فإنهم] كانوا كل الأمة في غير تلك المسألة التي خالف فيها من قد مات.','2','862',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1559,'الشبهة الرابعة: ما روي عن علي - كرم الله وجهه - أنه قال: (اجتمع رأيي ورأي عمر على منع بيع ام الولد، فأنا الآن أرى بيعها. فقال له عبيدة السلماني: (رأيك في الجماعة أحب إلينا من رأيك في الفرقة). فهذا من علي يدل اشتراط انقراض العصر. قلنا: هذا غير صحيح، فإنه لم يثبت أن عليًا وجميع الصحابة اتفقوا على ذلك، بل رأيه ورأي عمر. ويجوز الرجوع عن الرأي إذا لم ينعقد عليه الإجماع.
فإن قالوا: يدل على أن جميع الصحابة وافقوا على ذلك قول عبيدة: (رأيك في الجماعة أحب إلينا من رأيك في الفرقة). قلنا: لم يرد بالجماعة اجتماع الأمة على الحكم، بل أراد في حال اجتماع كلمة الناس، وعدم الاختلاف على الخلفاء، وقبل طريان الفتن أحب إلينا من رأيك عند وقوع الاختلاف على الخلفاء، وثوران الفتنة. ولذلك قال: (أحب إلينا من رأيك في الفرقة). أي افتراق الكلمة. على أن عليًا - رضي الله عنه - لو قال ذلك صريحًا، لم يجب','2','863',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1560,'تقليده في الأصول. وكان قصد عبيدة أن عليا لو انتقل عن ذلك الحكم، ربما نسب إلى التعصب والتبرؤ من أبي بكر وعمر، فكره عبيدة هذا. هذه شبهة من ذهب إلى اشتراط انقراض العصر على العموم.
وأما ما ذهب إليه الأستاذ من كون الإجماع إن كان سكوتيا، اعتبر فيه انقراض العصر، وإن كان نطقيا، لم يعتبر. فهذا كلام ضعيف جدا، وذلك أنه لا يخلو إذا قال العالم قولا، وسكت الباقون على ذلك، ولم يخالفوه: إما أن يظن الموافقة للقائل، أو لا يظن ذلك، فإن لم يظن ذلك، وجوز أن يسكت من لا يوافق، فهذا التجويز مستمر، وإن انقرض العصر، فإنه قد يسكت، وإن كان لا يوافق، إما تسويغا للمجتهد ما يقوله، أو لأسباب سنذكرها بعد ذلك.
وهذه الأسباب يصح استمرارها إلى انقراض العصر، وإن كان يظن من السكوت الموافقة، [فلماذا] يشترط انقراض العصر، وقد ظن الموافقة؟ وليس انقراض العصر بالذي يحصل علم الموافقة.
وقد حمل ذلك بعض الأصوليين على أنه ناقض، فإنه لا يرى اشتراط انقراض العصر في الإجماع، قال: وهذا إجماع ظني، فلم اشترط فيه الانقراض؟ وهذا النقض غير لازم، وفرق بين أن يقطع بالموافقة ومستند المتفقين ظن، وبين أن يظن الموافقة. والنقض على هذا (١٧١/أ) غير لازم، فإن الأستاذ اشترط الانقراض عندما يظن الاتفاق، ولم يشترط الانقراض عندما يعلم الاتفاق، والمتفقون ظانون. ولكن الاعتراض هو الذي قدمناه من استمرار السكوت إلى انقراض العصر، لا يحصل علما بأنهم متفقون.','2','864',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1561,'نعم، قد يقول القائل: إذا انقرض العصر من غير خلاف ولا إنكار، يقوي غلبة الظن بالموافقة. وهذا غير صحيح على الإطلاق، فإنه قد لا يتفق أن تتكرر الواقعة بحال، فلا تقوى غلبة الظن بالانقراض. وقد تتكرر الواقعة، ولا يظهر خلاف، فيقوى الظن بالموافقة، وإن لم ينقرض العصر. فالصواب أن لا يلتفت إلى انقراض العصر على حال.
وأما ما ذهب إليه الإمام، فأعجب من ذلك كله، وكلامه غير منضبط، فإنه قد حكم أولاً بأن للإجماع صورتين: قطعية وظنية، وقضى بأن الإجماع الظني حجة قاطعة، بناء على قطع الأولين بتبكيت المخالفين. ثم صار في هذا الموضع إلى خلاف هذا الإطلاق، وأبدى تفصيلا يناقض بعضه بعضا، فقال: الإجماع على الحكم، مع التردد في الأصل، لا يعد إجماعا. فكيف يصح الإجماع مع التردد في الأصل؟ إلا أن يريد بالتردد أن القوم غير قاطعين. فإذا كان كذلك، فكيف لا يعد هذا إجماعا؟ ولو كان الإمكان يمنع أن يكون هذا إجماعا، لكان الإمكان في قول الواحد يمنع أن يكون قولا ومذهبا حتى يطول الزمان. ولا ذاهب إليه.','2','865',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1562,'وقوله: (فإنهم إذا قالوا ما قالوه، قرنوه بما يرخي طول الناظر [المفكر] [ويسوغ] له طرق الفكر). قال الشيخ: وهذا الذي ذكره غير صحيح من وجهين:
أحدهما - أن هذا إنما يكون كذلك عند انفراد الآحاد بالاجتهاد. وأما عند الاجتماع فلا أخرى طول، ولا تسويغ فكر ونظرن لتحقيق عصمة الأمة بالأدلة السابقة.
الوجه الثاني - أنه إذا سوغ التفكر والنظر، لكون القوم لم يقطعوا بالحكم، فهذه القضية مستمرة، وإن طال الزمان كثيرا، ولا يؤمن ذلك إلا أن ينقطع الاحتمال ويقطع بالحكم، وإذ ذاك يخرج الإجماع الظني عن كونه حجة. ففي هذا تلويح وإشارة لمن منع كون الإجماع الظني حجة، وقد تقدم بيان فساده.
وقوله: (فإن طال الزمان، ولم يخالف مخالف، فهذا عسر التصوير، فإن','2','866',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1563,'الظنون يبعد الاستمرار عليها مع طول الأزمنة). قلنا: هذا يشير إلى ما سبق من أن تمادي الأزمنة وتطاولها مع تكرار الواقعة، يتعذر في المظنونات. وهذا مما تكلمنا عليه. ونزيده الآن تقريرًا فنقول: إن هذه إنما تمتنع في العادات، عند تقارب الأسباب المتناقضة. فأما إذا قوي السبب، فلا يبعد الاستمرار، وهو بمثابة ما لو روى الصديق أو غيره من جلة الصحابة - رضي الله عنه - خبرًا، وأسندوا الحكم إليه، فإن المستند مظنون، ولا تمنع العادة الاستمرار على الحكم عند ظهور خبر العدل. كما [رجع أبو بكر - رضي الله عنه - إلى ما] [رواه] عبد الرحمن بن','2','867',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1564,'عوف أن النبي - صلى الله عليه وسلم - قال في المجوس: (سنوا بهم سنة أهل الكتاب). وكذلك رجع الناس إلى ما روته عائشة رضي الله عنها من أن النبي - صلى الله عليه وسلم - قال: (إذا جاوز الختان الختان، [فقد] وجب الغسل).
وكثير من الأحكام استندت إلى أخبار صحيحة عمل بها المسلمون من زمن الصحابة إلى زماننا، ولم تصر المستندات قطعية، ولا عادت الأخبار متواترة. فتوهم (أن الاستمرار مع التكرار يلحقهم بالمصرين، ويرفعهم (١٧١/ب) عن رتبة المترددين). كلام يشترك فيه [الشادي]، ويعرف ضعفه المهرة من المحققين.','2','868',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1565,'ثم قال لما سئل عن ضبط ذلك الزمان: (المعتبر مضي زمان طويل مع ذكر تلك الواقعة، [وترداد] الخوض فيها، فلو وقعت الواقعة [وسبقوا] إلى الحكم فيها، ثم تناسوها، فلا أثر للزمان، والحالة هذه). فلا يكون الإجماع عنده في هذه الصورة معتبرًا ولا دليلاً. قال: (والمعتبر [مضي] زمان لا يفرض في مثله استمرار الجم الغفير على رأي إلا عن حامل قاطع، أو نازل منزلة القاطع على الإصرار، وهذا يمنع الإصرار على الحكم مع التصريح بالظن). وهذا الكلام أيضًا في غاية الضعف.
أما قوله: (لا يستمر الإصرار على الحكم إلا عن مستند قاطع). فقد بينا خلاف ذلك.
أما قوله: (أو نازل منزلة القاطع). فنقول: لا شيء عندنا يتنزل منزلة القاطع؛ فإنه إذا لم يحصل العلم، لم يبق إلا اعتقاد أو ظن، والاعتقاد ههنا غير','2','869',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1566,'مقصود، فلم يبق إلا أنه أراد أمرًا يثير ظنًا قويًا. والأسباب في إثارة غلبات الظنون تختلف باختلاف أحوال الناس، فقد يظن الإنسان من سبب أمرًا، ولا يظنه غيره من ذلك السبب. فكأن هذا الرجل يقول: إن أجمعوا على سبب قاطع، فلابد من الاتباع، وإن أجمعوا على سبب مظنون ظنًا قويًا، وجب أيضًا الاتباع، إلا أنه زعم أنه (لا يلوح جلاؤه إلا بالاستمرار عليه). وهذا عندي غير صحيح لوجهين:
أحدهما - أن الحاكم يعرف مستنده [بقدر] ما يحصل له من الظن. فلو صرح أهل الإجماع، وقالوا: السبب الذي أسندنا الحكم إليه من [أعلى] الأسباب المثيرة لغلبات الظنون، كما صورناه في خبر الصديق - رضي الله عنه - أو غيره، فهل كان يصدقهم في قوة السبب أم لا؟ فإن صدق، فهو ما طلب الاستمرار مع التكرار، إلا ليعرف قوة السبب. وقد عرفه بقول المجمعين، فيلزمه أن يبادر إلى القضاء بانعقاد الإجماع، إذ قد ظهرت قوة السبب.
الثاني - أنه يلزم إذا قالوا: مستندنا أثار لنا ظنًا ليس بالقوي، ولكن','2','870',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1567,'[لاحتمال] [أنا] لم نجد ما نعضد به، فحكمنا لذلك. واستمروا على هذه القضية، وقالوا في [كل] حكم: لم يتجدد عندنا أمر متبع، إلا أن تنتهض الحجة. وكل هذه تخييلات، لا تحقيق لشيء منها. فقد تبين بما ذكرناه أن الأمر على ما قرره القاضي رحمة الله عليه.
قال الإمام: ([وأما] الفن الثالث) إلى قوله (يبين [صورة] الوفاق والخلاف). قال الشيخ: هذا الذي ذكره الإمام هو الذي قلنا إنه يرجع إلى تصوير الاتفاق. وهل وجد من الأمة اتفاق أم لا؟ وهذا النظر - وهو تحقيق مناط الإجماع أو غيره - يقع على وجهين: أحدهما - خارج عن علم الأصول، والثاني - داخل في الفن.','2','871',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1568,'فأما ما هو خارج عن فن الأصول، فهو أن يقع الاتفاق على تصور الإجماع، وفهم حقيقته. ولا يبقى النظر إلا في وجوده في المسألة المفردة. فهذا مما لا يتكلم فيه الأصوليون في مسائل الفروع بحال. وهذا بمثابة انعقاد الإجماع على تحريم التفاضل في الأشياء الستة، أو غيرها من فروع الشريعة [مثلاً]. فإن أدخل الأصولي هذا في فنه، فهو خلط للفن بغيره، وشغف بنصرة مذهب، وذب عنه، أو تضعيف لغيره.
وأما ما يفتقر (١٧٢/أ) الأصولي إلى معرفته، ولا يسعه جهله في فنه فقسمان:
أحدهما - نظير هذا الذي ذكرناه، وهو أن يبين وجود الإجماع بعد فهمه وتصوره في مسائل الأصول، أجناسًا وأنواعًا. وهذا بمثابة كلام الأصوليين على بيان الإجماع على وجوب العمل عند خبر الواحد، والقياس، وسائر الأسباب والأمارات. فليس يرجع ذلك إلى تصور الإجماع، وإنما يرجع إلى بيان وجوده في مسألة النزاع، فهو يشبه النظر الأول لأصحاب الفروع، ولكنه يمتاز عنه بالنظر إلى التعلق. ففي الوجه الأول النظر في مسألة فردة فرعية. وفي هذا المكان النظر في تحقيق نوع أو أمارة أو جنس.
النوع الثاني - ما ينظر فيه الأصولي هو الكلام في اشتراط انقراض العصر ومستند الإجماع من قطع أو ظن. وقد تقدم هذان النوعان. وبقي الكلام في هذه المسألة، وهي هل يلتئم من قول البعض وسكوت البعض إجماع أم لا؟ ويخرج','2','872',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1569,'عليه أنه إذا اختلفوا على قولين: هل يعقل الإجماع في هذه الصورة، أو ليس إلا اختلاف محض؟ وهل إجماع البعض حجة أم لا؟ هذه المسائل تتعلق بفهم الإجماع في نظر الأصولي.
قال الإمام: ([مسألة] إذا قال واحد [قولاً] في شهود علماء العصر، [وكان] ذلك القول موافقًا لبعض مذاهب العلماء) إلى قوله (فهذا معنى قول الشافعي: (لا ينسب إلى ساكت قول)). قال الشيخ: ما ذكره الإمام في المسألة الأولى، وهي الاجتهادية، كلام حسن، لا مزيد عليه، ولا سبيل إلى','2','873',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1570,'تنزيل السكوت على الموافقة، خاصة وله أسباب غيرها، وقد ذكر الإمام سببًا واحدًا، وهو تسويغ القول لذلك القائل، وهو سبب صحيح، وتنقدح وراءه أسباب أخر.
وقد اختلف الناس في هذه المسألة، وخص الأصوليون الكلام بما إذا كان ذلك القول في محل الاجتهاد. والظاهر من كلامهم أن المسألة لو كانت قطعية، لانقطعت الاجتهادات كلها، ولم يبق إلا الموافقة. ونحن نتكلم على الصورتين جميعًا.
أما الأولى: فقد اختلف الناس فيها، فذهب ذاهبون إلى أنه إجماع، وهذا مشروط بما إذا بلغهم قوله، ولم ينكروا. هذا هو الذي يتصور أن يكون','2','874',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1571,'محل الاختلاف. وشرط قوم انقراض العصر على السكوت. وقال قوم: هو حجة، وليس بإجماع. وقال قوم: ليس بحجة، ولا إجماع، ولكنه دليل تجويزهم الخلاف في المسألة. وقد يسكت من غير إضمار الرضا لسبعة أسباب:
الأول - أنه قد يسكت، وهو مضمر الإنكار، ولكن يكون في باطنه ما يمنعه من إظهاره، ونحن لا نطلع عليه. وقد تظهر قرائن السخط عليه مع سكوته.
الثاني - ما ذكره الإمام من جهة أنه يراه قولاً سائغًا في الاجتهاد، وإن لم يكن هو موافقًا عليه، بل كان معتقدًا لخلافه وخطئه، إن كان ممن يقول إن المصيب واحد.','2','875',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1572,'الثالث - أن يكون ممن يرى كل مجتهد مصيب، فلا يرى الإنكار في المجتهدات أصلاً، ولا يرى الجواب إلا فرض كفاية. فإذا كفاه من هو مصيب سكت، وإن كان يرى خلاف رأيه.
الرابع - أن يسكت وهو منكر، ولكنه ينتظر فرصة الإنكار، ولا يرى المبادرة مصلحة، لعارض من العوارض، فينتظر زواله، ثم يموت قبل زوال ذلك العارض، (١٧٢/ب) أو يشغل عنه.
الخامس - أن يرى أنه لو أنكر لم يلتفت إليه، [وناله] ذل وهوان.','2','876',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1573,'السادس - أن يسكت، لأنه متوقف في المسألة، لأنه بعد في مهلة النظر.
السابع - أن يسكت، لظنه أن غير كفاه الإنكار، وأغناه عن إظهاره، ويكون قد غلط في ذلك، فيكون [ترك] الإنكار عن توهم، إذ يراه فرض كفاية، ويظن أن غيره قد قام به.
وقد تمسكوا بأمور ضعيفة: منها - أنهم قالوا: لو كان فيه خلاف لظهر وقد قيل لهم: لو كان فيه وفاق لظهر، فإن اتفق عدد يمنع من إظهار الخلاف، تصور مثله في إظهار الوفاق. وهذا تمام الكلام في هذه الصورة.
وأما الصورة الثانية: التي تظهر من كلام الأصوليين، أن السكوت فيها لا ينزل إلا على الموافقة، وهي ما إذا كانت المسألة قطعية. قالوا: فلا يدل السكوت إلا على الموافقة، وهذا فيه نظر عندي، وما المانع من أن يسكتوا وهم غير موافقين؟ فإن قيل: إذا كانوا مخالفين في المسألة القطعية، كان الإنكار واجبا. فيكون تركهم الإنكار معصية، ويكون ذلك تواطؤا على محرم. وهذا الذي ذكروه، لا يقوى عندي، وذلك أن المعصية إنما استحالت على جميع','2','877',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1574,'الأمة، إما في القول والفعل عند معظم الأصوليين، أو في الفتوى والقول على الخصوص عند القاضي.
وإذا كان كذلك، فلا يمتنع أن يعصي بعض الأمة معصية، ويعصي البعض الآخر معصية أخرى، فيقول بعض الأمة: يجب قتال مانعي الزكاة، والخوارج محقون، وتقول الطائفة الأخرى: الخوارج مبطلون، والزكاة مثلا غير واجبة. هذا لا امتناع فيه، ولا يناقض العصمة، فغنه لم تجتمع الأمة على معصية واحدة. فإذا أفتى نصف الأمة في مسألة قطعية، وأخطأ فيها آخرون، فهذا غير ممتنع، إذ الخطأ على البعض غير محال.
فإذا قال القائل: والطائفة التي لم تنكر أخطأت أيضا. قلنا: لم تخطئ في الحكم الذي أخطأ فيه الأولون. وإنما عصت في ترك إنكار النكير، والتي لم تنكر هي بعض الأمة أيضا. وقد قال القاضي: يجوز أن تجتمع الأمة على معصية لا تتعلق بالفتوى، حتى يسوغ أن يأكلوا طعاما يعترف كل واحد منهم بكونه حراما. فإذا جوز المعصية في غير محل الفتوى على الجميع، فما الحال في تجويز ترك إنكار النكير على البعض؟ إلا أن يتلقى منع السكوت من طريق','2','878',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1575,'العادات، لا من باب عصمة الأمة. فهذا إذا سلك، افتقر إلى أن يكون في الساكتين كثرة تمنعهم من السكوت في مواضع القطع بالمخالفة، فيكون هذا متلقى من العادات، لكن يشترط فيه زوال الموانع المانعة من الإنكار.
ولكن يبقى فيه نظر عندي، وذلك أنهم إذا سكتوا في هذه [الحالة]، حصل العلم بأنهم بجملتهم ليسوا مخالفين، ولابد من إضمار الموافقة. لكن القطع بأنهم بجملتهم موافقون فيه، فما المانع من اشتمال الساكتين على موافقين وعلى مخالفين؟ وهذا احتمال بين، لا سبيل إلى دفعه إلا بقاطع. فلا تحصل بالموافقة من الجميع على هذا التقدير، وإن كان الساكتون عدد التواتر، إلا أن تقترن بذلك قرائن تحصل علما بالسكوت موافقة. وإذا افتقر إلى هذه القرائن، فلا فرق بين المظنون والمقطوع به. فإن تلك القرائن لو اقترنت بفتواهم في محل الاجتهاد، لحصل علم الموافقة (١٧٣/أ)، [لبناء] الأمر على ذلك. ولكن لا يكون هذا مستندا إلى محض السكوت عند استماع القول.
وأما ما صوره الإمام من (أن رجلا لو قال قولا خرق فيه إجماع الأمة، فإن العلماء يثورون منكرين). فالأمر على ما قال، لأنه صور سبق الإجماع قبل مخالفة المخالف. فلابد من إنكار العلماء عليه. فإنهم بجملتهم لو لم ينكروا، لشملت المعصية الأمة بجملتها، وذلك ممتنع عندنا. وفي المسألة التي','2','879',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1576,'نحن فيها، إنما سكت بعض العلماء، فمن أين يتنزل سكوت بعضهم منزلة سكوت الجميع؟
فإن قيل: إذا كانت المسألة قطعية، وأخطأ فيها قوم، فلو خرجوا بالخطأ في القطعيات من أن يكونوا من أهل الاجتهاد، ويكون الباقون هم أهل الإجماع، فإذا سكتوا، فهل عصى أهل الإجماع، وذلك عليهم محال؟ قلنا: ليس خطؤهم في تلك المسألة بالذي يصير خصومهم كل الأمة. ولذلك أنه لا يسوغ الاحتجاج على غيرهم باتفاقهم، بل يطلب دليل المسألة القاطع من غير الإجماع.
ولما خولف أبو بكر في قتال مانعي الزكاة، لم يحتج بأنه رأى ذلك [رأيا]، ومن رآه معه، بل جنح إلى الدليل، وقرره من جهة السنة والقياس جميعا. فقال: (والله لأقاتلن من فرق بين الصلاة والزكاة). وقال: (والله لو منعوني عقالا كانوا يعطونه رسول الله - صلى الله عليه وسلم -، لقاتلتهم عليه). فالصواب بعد هذا كله، النظر إلى قرائن الأحوال الدالة على حمل السكوت على الموافقة. فهذا هو المعتمد في ذلك. والله المستعان.
قال الإمام: (ونحن نصور هذه المسألة في صورتين، ونذكر في كل واحدة [منهما] ما يليق بها) إلى قوله (هذا منتهى المسألة تصويرا','2','880',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1577,'وتقريرا). قال الشيخ: ما ذكره الإمام واضح في نفي القطع بالإجماع. ولكن الظاهر من جهة العادة، أن القول إذا انتشر في العلماء، ولم يبد خلاف، أنهم موافقون. إذ لو كانوا يخالفون، لذكرت المخالفة. هذا هو الأمر الغالب. وما ذكره الشافعي وغيره من الأصوليين هي احتمالات تمنع من القطع.','2','881',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1578,'فأما غلبة الظن بالموافقة، فإنها ثابتة. ولكن يبقى أن هذا النوع من غلبة الظن إذا منع القطع بالإجماع، فهل يصح أن يكون الإجماع المظنون أمارة في حق العمل؟ هذا فيه نظر.
وقد اختلف الأصوليون في نقل الإجماع على ألسنة الآحاد، هل يتنزل منزلة [نقل] السنة على ألسنة الآحاد أم لا؟ فذهب الإمام إلى أنه يتنزل','2','882',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1579,'منزلة [نقل] السنة على ألسنة الآحاد. فينتهض أمارة في حق العمل.
وذهب غيره إلى أنه لا يجوز أن يستند إليه عمل. قالوا: لأنا إنما أخذنا العمل بالأخبار بإجماع الصحابة، وذلك إذا نقل العدل السنة عن رسول الله - صلى الله عليه وسلم -. فأما إذا نقل الإجماع على ألسنة الآحاد، فلا يجوز أن يستند إليه العمل إلا بالقياس على محل الإجماع، ولم نتعبد بالقياس في قواعد الشريعة. هذا كلام أبي حامد. ولكن الصحيح عندنا ما ذهب إليه الإمام، فإن المقصد حصول غلبة الظن بثبوت الدليل، الذي لو ثبت أنار علما. فإذا نزل الأمر وأفاد ظنا، فهو معتبر في المظنونات. وهذا كأنواع القياس وأنواع الأخبار.','2','883',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1580,'وإذا بنينا على أن نقل الإجماع على ألسنة الآحاد لا يكتفي به، فلأن لا يكتفي بظن الإجماع مستندا إلى سكوت بقية العلماء أولى. وإن بنينا (١٧٣/ب) على القول بوجوب العمل بالإجماع المنقول على ألسنة الآحاد، فهل يتنزل الظن المتلقى من تارات وحالات منزلة الظن الحاصل من نقل العدول؟ هذا','2','884',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1581,'فيه خلاف. وقد ذهب الإمام فيما تقدم إلى أن غلبات الظنون المتلقاة من تارات وحالات لا يصح أن تكون مستند الإجماع، وإن غلب على الظن منها ما يغلب على الظن من قول الشارع فيما يظهر من لفظه ما يشعر به ظاهره. وقد تقدم هذا. فمن منع التعلق بظاهر الحال، وعليه عرف المقال، فلأن يمنع التعلق بغالب ظن حصول الإجماع، بناء على موافقة الساكت أولى. والذي أختاره سوى التمسك بظاهر ظن الموافقة مما يتعلق بالعمل في مجال الظنون، فيصح عندنا التمسك بفتوى المفتي عند انتشار قوله وعدم خلافٍ من العلماء له. وبالله التوفيق.

قال الإمام: <span data-type="title" id=toc-271>([مسألة] إذا اختلف أصحاب رسول الله - صلى الله عليه وسلم - على قولين</span>، واستمروا على الخلاف [فيهما] إلى قوله (فهذا مغزى المسألة). قال','2','885',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1582,'الشيخ: الذي ذكره الإمام بين على أصله من التفرقة بين الإجماع المقطوع به والمظنون. وقد تقدم أن رأينا خلاف رأيه.
فأما ذهابه ههنا إلى أنه ينبغي أن يكون تمادي الزمان ههنا أكثر من تماديه عندما يكون القول واحدًا وهم ظانون. فهذا ضعيف عندي من وجهين: فإنه بنى الأمر ههنا على زيادة طول الزمان، على أن الظن ههنا ضعيف، لثبوت الاختلاف. وهذا الكلام غير صحيح، فإن نفي الثالث مظنون منهما جميعًا، وليس ضعف الظن بكل واحد من القولين المذكورين بالذي يوجب ضعف الظن، بالإضافة إلى القول الثالث، الذي اتفق الفريقان على نفيه. هذا وجه.
الوجه الثاني: أنه إذا شرط زيادة التمادي، فإلى أي حد ينتهي التمادي؟ وما الضابط لذلك؟ وكيف يتصور أن تتوقف الحجة القاطعة على أمر غير منضبط؟ هذا محال، وتكليف ما لا يستطاع. فإن كان يجعل الضابط انتهاء الأمر','2','886',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1583,'إلى حد يحصل العلم بنفي الثالث، خرج الإجماع المظنون عن أن يكون معتبرا في الشريعة، وإن كان الإجماع المظنون حجة، فالظن قد حصل من أول الاتفاق على نفي الثالث.
وما حكاه عن الصحابة من أنهم كانوا يبكتون من خالف الإجماع المظنون، فليت شعري أي زمان كانوا ينتظرون ذهابه حتى يبكتوا؟ وأي تكرار في الموافقة حتى يشترطونه؟ هذا محال لا شك فيه. فليس إلا أحد أمرين: إما أن تنتهض الحجة ناجزة عند نفي القول الثاني والثالث، وإن كان ذلك مظنونًا، وإما أن يقال: لا يكون الإجماع حجة إلا إذا قطع المجمعون بالحكم. ومتى اعترف كل واحد منهم بكون الحكم مظنونًا، لم تنتهض الحجة،','2','887',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1584,'والأمر على خلاف ذلك. فلا سبيل إلى اشتراط أمر زائد على الاتفاق، سواء كان في لحظة أو أزمنة.
ولنزد الدليل تقريرًا فنقول: لا يجوز للمجتهد إذا وجد دليل المسألة، المبادرة إلى الحكم به، حتى يبحث في الشريعة عن معارض له أو دليل يصد ذلك الحكم. فإذا صح عنده أنه لا دليل لغير ذلك الحكم يجوز الاعتماد عليه، فحينئذٍ يجزم بالحكم أو يظنه. وإذا كان كذلك، وامتنع قبول المحل الحكمين، للتناقض (١٧٤/أ) الحاصل بين الإجماع وثبوت أحد الضدين، فإنه يمنع من الأخذ لا شك فيه. فالذي يقول مثلاً بالحل، ينفي الحرمة وبقية الأحكام، والذي يقول بالحرمة، ينفي الحل وبقية الأحكام. والفريقان متفقان على نفي ما سوى الحل والحرمة. فانتفاؤهما مجمع عليه إجماعًا محققًا، استغنى عن التعبير عن نفيه بالتعبير عن ثبوت ضده. وللعاقل في التعبير عن نفي الحركة عبارتان:
إحداهما - أن يقول: زيد ليس بمتحرك.
[والثانية]- أن يقول: إنه ساكن. وإذا كان كذلك، فما اتفق الفريقان على نفيه، فنفيه مجمع عليه، وهو حكم واحد. وما اختلفا في نفيه وإثباته، فلا قطع في إحدى الجهتين. فما كان ينبغي في هذه المسألة أن ينظر إلى أدلة، ولا إلى شبه، وإنما يختص النظر فيها بتحقيق ما ذكرناه من الإجماع على قضية،','2','888',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1585,'والاختلاف في أخرى. وأما أدلة كون الإجماع حجة، فقد تقدمت مستقصاة.
وذهب أبو حامد إلى طريق لا بأس به، فقال: لو قدرنا أن القول الثالث حق، لم يكن [بد] من دليل، ولم يكن بد من نسبة الأمة إلى تضييعه، وذلك ممتنع بدليل السمع. وهذا وإن كان له وجه، إلا أنه لا يقوى عندي إلا على القول بأن المصيب واحد. فأما على القول بأن كل مجتهد مصيب، فلا يقوى هذا الطريق. إذ للقائل أن يقول: إنهم عصموا عن الخطأ، وهذا يحصل بكونهم أصابوا دليل ما حكموا به. فأما إصابتهم لكل دليل، فذلك غير لازم.
فأما إذا بني الأمر على اتحاد الحق، فلا يصح أن يكون الحق في القول الثالث، لأن الأمة بجملتها تكون قد أخطأته. وهذا غير جائز على الأمة بجملتها. والطريق الذي ذكرناه أولاً يجري على قول من يقول كل مجتهد','2','889',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1586,'مصيب أو المصيب واحد. لأنا حققنا أن نفي الثالث متفق [فتوى] استغني عن التعبير عنها بذكر كل فريق بثوت ضد الحكم الثالث. هذا نهاية التقرير. وليس هو نظر في دليل الإجماع، وإنما هو بحث أصولي عن وجود الاتفاق. والذين سوغوا الخلاف، لم يلتفتوا إلى الإجماع الضمني، وإنما نظروا إلى ما صرح به، والمصرح به الاختلاف، والاختلاف لا [يجزم] الخلاف، وإنما [يجزمه] الإجماع. قالوا: ولا إجماع. وقد ثبت بحمد الله وعونه الإجماع على الحقيقة.
وقد تمسكوا بشبه، منها ما ذكرناه، وقد مر، ومر الجواب عنه. وقالوا أيضا: إنهم خاضوا خوض مجتهدين، ولم يجزموا [بالمصير] إلى قول ثالث. وعنه جوابان:
أحدهما - ما قررناه من أنهم نفوا القول الثالث بلا ريب.
الثاني - أن هذا يلزم منه أنهم إذا اتفقوا على قول واحد، وأسندوه إلى','2','890',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1587,'الظن، أن يجوز خلافه. ونحن إنما تكلمنا على هذا بعد المصير إلى أن الإجماع المظنون حجة.
وتمسكوا أيضا بأنه: لو استدل الصحابة بدليل أو علة، لجاز الاستدلال بعلة أخرى، لأنهم لم يصرحوا ببطلانها. وكذلك القول الثالث لم يصرحوا ببطلانه. وهذا باطل، لأنه يلزم منه أن تجوز المخالفة إذا اتفقوا على قول واحد عن اجتهاد.
وأما جواز الاستدلال بدليل غير دليلهم، فلا يمنع، إذ ليس من فرض دينهم الإطلاع على كل دليل، بل الفرض استناد الحكم إلى دليل. ولا ينسب إلى الخطأ من استند إلى دليل صالح عندما يظفر بغيره، بخلاف من حكم بخلاف حكم أهل الإجماع، فإنه نسبهم إلى الخطأ، وإثبات غير حكم الله تعالى.
أما التعليل بعلة غير علة الصحابة، فهذا ينبني على أمر آخر، وهو أنه هل يجوز أن يعلل (١٧٤/ب) الحكم بأكثر من علة واحدة؟ فمن منع هذا، لم يجوز','2','891',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1588,'التعليل بغير علة الصحابة. ومن جوزه، لم يمنع. إذ لا يلزم من إظهار علة أخرى نسبة الأولين إلى الغلط في شيء، اللهم إلا أن يقولوا: ليس لهذا الحكم إلا علة واحدة. فلا يجوز حينئذ إحداث علة أخرى، لأن الأمة اجتمعت على نفيها. وهذه الصورة هي التي تشبه ما نحن فيه، لأنا [قدرنا لو] أنهم أجمعوا على نفي الثالث.
وتمسكوا أيضا: بأنه لو ذهب بعض الصحابة إلى أن اللمس والمس ينقضان الوضوء، وبعضهم إلى أنهما لا ينقضان الوضوء، ولم يفرق واحد [بينهما]. فلو قال [تابعي]: ينقض أحدهما، ولا ينقض الآخر، فهذا جائز، وإن كان قولا ثالثا، ولم يمنع، فلا يمتنع إحداث قول ثالث.
والجواب عن هذا: أنه في كل مسألة وافق قولا متقدما، ولا يترتب حكم مسألة على أخرى، فهو إذا قال: اللمس ينقض، فقد وافق، وإن قال: اللمس لا ينقض، فقد وافق، فلم يحدث [قولا] آخر بحال، اللهم إلا أن تكون الأمة قد قالت: حكم المس واللمس عندنا بحيث لا يفترقان في الحكم على حال، فتكون حينئذ التسوية مقصودة، فلا يكون الفرق مسوغا. وهذا تطير ما نحن فيه، لأنا حققنا أن القوم اجتمعوا على نفي الثالث. فالذاهب إليه لا يجد أحدًا يساعده عليه. وكذلك إذا اتفقوا على التسوية بينهما، كان المفرق خارقا للإجماع.','2','892',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1589,'ونكتة المسألة ما قدمناه [من] وجود اتفاق ضمني على نفي قضية أو إثباتها. فإذا تحقق ذلك، فلا فرق بين التصريح والأمر الضمني المعلق به. ولم ترد الألفاظ لأعيانها، وإنما طلبت لأداء معناها. وإذا علم المعنى، فلا التفات إلى اللفظ بحال. فلا فرق بين قول القائل: (اسأل القرية) وبين قوله: (اسأل أهل القرية). وربما كان اللفظ الأول أحسن، لحصول العلم بالمعنى المقصود.
قالوا: قد أحدث مسروق في مسألة الحرام قولا ثالثا، ولم ينكر عليه منكر. قلنا: لأنه لم يثبت أن الصحابة اجتمعت بجملتها على قولين في مسألة الحرام، بل لعل بعضهم لم يخض فيها. ولو قدرنا أنهم بجملتهم خاضوا فيهان فمن أين يعلم أنه لم ينكر منكر؟ وما المانع من أن ينكر منكر ولم ينقل إلينا؟ فليس كل قول يجب أن ينقل ويدوم نقله متواترا. هذا بعد أن يثبت أن جميع الصحابة علموا إحداث مسروق القول الثالث. وكل هذه إمكانات قدرت موجودات، وبنيت عليها خيالات في مدافعة حجج قاطعة. وهذا باطل،','2','893',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1590,'لا خفاء به. وقد قدرنا تحقيق الإجماع على نفي الثالث، فلا التفات إلى الخيالات الفاسدة في مقابلة القواطع.
قال الإمام: (مسألة: إذا اختلف علماء العصر على قولين) إلى قوله (مع اتحاد العصر). قال الشيخ: قول الإمام: إذا قصر زمان الاختلاف، فلا التفات','2','894',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1591,'إليه، ولا يزيد هذا على ما إذا أجمعوا على قول، مع إسنادهم إياه إلى الظن، فإن ذلك لا يعد إجماعا. وهذا جار على أصله، فإذا كان هذا قوله في الاتفاق على قول واحد، فما الظن إذا اختلفوا على قولين، ولم يطل زمان','2','895',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1592,'الاختلاف؟ ثم نظر أيضًا - على أصله - إلى القول الذي اتفقوا عليه، هل طال الزمان، وتكررت الواقعة أم لا؟ فإن لم يطل الزمان، أو طال ولم تتكرر الواقعة، لم يحصل إجماع، لا على القولين، ولا على القول الآخر.
وإن طال زمان الاختلاف. فإنه قال: (يحصل من ذلك إجماع ضمني على تسويغ الخلاف). ثم إذا اتفقوا (١٧٥/أ) على أحد القولين، واستمروا عليه مدة متطاولة، اقتضى هذا إضرابا عن القول الآخر، فيتناقض الإجماعان على هذا التقدير. ولهذا [قال الإمام]: (إنه من المتعذر أن يقع الإضراب عن','2','896',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1593,'مذهب طال الذب عنه). فكأنه تخيل المسألة، لكنه فرضها عند كثرة المخالفين، واستمرارهم على الخلاف الدهر الطويل.
وهذا الذي قاله غير ممتنع، وإن كان بعيدا، فيقال له: فما قولك إذا اتفق ذلك؟ قال: ينزل على أنه بلغهم أمر غير ما كانوا عليه، يقتضي الرجوع إلى أحد القولين. وقد صور أنه إذا طال زمان الاختلاف، اقتضى ذلك إجماعا ضمنيا على تسويغ الأخذ، فلا سبيل إلى منع الأخذ بالقول الذي رجع عنه، لإطباق الفريقين على صحة الأخذ به، فيكون جواز الأخذ بكل واحد من القولين، أمرا مجمعا عليه. فإذا رجعوا إلى أحد القولين، واستمروا عليه دهرا طويلا، بحيث ينتهي الأمر إلى قريب من القطع - على زعمه - فإنه لا وجه لعذره، اقتضى ذلك منع الأخذ به من جميعهم، على حسب تقديره. وقد فرض أن طول الاختلاف يقتضي إجماعا ضمنيا على تسويغ الأخذ بكل قول. فهذا متناقض.
ثم إنه استبعد المسألة عند كثرة المخالفين، فما قوله إذا [قل]','2','897',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1594,'المخالفون؟ فما الذي يمنع من رجوع الواحد إلى قول الجماعة؟ وقد قيل: إن ابن عباس - رضي الله عنه - كان يبيح نكاح المتعة، وامتاز بذلك عن بقية الصحابة، واستمر على ذلك دهرا طويلا، حتى قال له علي: (إنك امرؤ تائه، إن رسول الله - صلى الله عليه وسلم - حرم نكاح المتعة، فرجع إليه). وكذلك ما يضاهي ذلك، فلا تحيل ذلك العادة.
وهذه المسألة قد اتخذها من يشترط انقراض العصر عمدتهم، وقالوا: لما كان إذا وقع الرجوع إلى أحد القولين، لا يلتفت إلى الخلاف السابق، ولو انقرضوا على الاختلاف، لم يكن رجوع التابعين إلى أحد القولين يقتضي منع الأخذ بالآخر، دل على أنه لا يحكم بانعقاد الإجماع، ما أمكن الرجوع من واحد من المجمعين أو من جميعهم. ونحن نقول: أما إذا رجع التابعون إلى','2','898',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1595,'أحد قولي الصحابة، فقد تكلمنا عليه. وأما إذا رجع بعض الصحابة إلى قول الفرقة الأخرى، فهذا موضع الكلام.
وقد ذهب القاضي إلى أن هذا القول المرجوع إليه، لا يتعين الأخذ به، ولم يكن القول الآخر مهجورا. وقيل له: فالأمة بجملتها على الحل مثلا أو الحرمة، فكيف تجوز مخالفة الجملة؟ وهذا صعب شديد عليه. فلئن قال: لا يبطل المذهب بالرجوع عنه، كما لا يبطل بموت قائله، فأقدر القول المرجوع عنه أن أصحابه باقون على القول به، فلا يكون الذين قالوا بالآخر منهم جميع الأمة، بل البعض، وإن كانوا الجميع بالإضافة إلى الصورة، ولكنهم بعض الأمة، بالإضافة إلى المعنى والحقيقة. فهذا كلام ضعيف، وهو يقتضي جواز رجوع أهل الإجماع بجملتهم عن قولهم، إذ تقرر أن القول الأول أهله قائلون به. وهذا لم يذهب إليه أحد من علماء الأصول. فلما تقرر عليهم ذلك، طلبوا الخلاص من خمسة أوجه:','2','899',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1596,'أحدها - ادعاء الاستحالة، قالوا: وهذا بمثابة فرض إجماعهم على شيء، ثم رجوعهم بجملتهم عنه، أو كفرض إجماع التابعين على خلاف الصحابة. قالوا: فهذا محال سمعًا، وإن كان العقل يجوزه. قالوا: والشيء يمنع تارة لنفسه، وتارة لإفضائه إلى محال، وهذا يفضي إلى محال، وهو إجماعهم على تجويز (١٧٥/ب) الأخذ بكل قول من القولين، ثم اتفقوا على أحدهما ومنعوا الأخذ بالآخر. وهذا قد بينا ضعفه، وأنه لا مانع منه اعتيادا وشرعا. وقد نقلنا الوقوع. فيغلب على الظن ذلك، ولا دليل يدل على الاستحالة.
وقول القائل: إنه يفضي إلى تناقض في الإجماعين. فغير مسلم، فإنا قد قلنا إن الإجماع عند الاختلاف إنما هو على نفي الثالث. وليس في واحد من القولين إجماع بحال. ولهذا إن الذين نظروا إلى القولين، فاقتصر نظرهم عليهما، جوزوا إحداث قول ثالث. ولا خلاف أنه يجوز الرجوع إلى أحد القولين في القطعيات، كما رجعوا إلى قتال مانعي الزكاة بعد اختلاف. وما ذلك إلا أنه إذا كانت المسألة قطعية، فلا يمكن القول بأن الأمة اجتمعت على تسويغ الأخذ بكل واحد من القولين، ولم يحصل اتفاق إلا على أحدهما أخيرا، فهو إذًا المجمع عليه.
المخلص الثاني: اشتراط انقراض العصر، فإنهم يقولون: لم يحصل إجماع عند الاختلاف، لبقاء المختلفين، فإنما يكون الإجماع على ما انقرضوا عليه. وقد بينا بطلان اشتراط انقراض العصر، فلا يصح أن يكون مخلصا.','2','900',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1597,'المخلص الثالث: اشتراط كون الإجماع مستندا إلى قاطع، لا إلى أمر مظنون. وإذا اشترط هذا، لم يحصل إجماع على تسويغ الأخذ بكل واحد من القولين، إذ ذلك إنما يكون [من] مسائل الاجتهاد. وقد بينا بطلان هذا أيضًا، وإنما الإجماع حجة قاطعة على الإطلاق.
قالوا: وإذا لم يكن الإجماع عند الظن حجة، فتسويغ الأخذ بكل واحد من القولين مستنده الاجتهاد أيضًا. ولكن هذا يقتضي أن لا يكون القول المرجوع إليه أيضًا حجة، إذ ذلك مستنده الاجتهاد. فإذا حتموا المصير إليه والأخذ به تناقض القول، فيكون حجة في المصير إلى القول الأخير، ولا يكون حجة بالإضافة إلى تسويغ الأخذ بكل واحد من القولين.
المخلص الرابع: أن يقال: النظر إلى الاتفاق الأخير، وأما في الابتداء، فإنما جوزوا الخلاف بشرط أن لا ينعقد الإجماع على تعيين الحق في واحد. وهذا مشكل، فإنه زيادة شرط في الإجماع، والحجج القاطعة لا تحتمل الشرط الذي يمكن أن يكون وأن لا يكون. ولو جاز هذا، لجاز أن يقال إن الإجماع الثاني ليس بحجة، بل إنما يكون حجة بشرط أن لا يكون اتفاق بعد اختلاف، وهذا أولى، لأنه يقطع عن الإجماع الشرط المحتمل.
المخلص الخامس: هو أن الأخير ليس بحجة، ولا [يحرم] المصير إلى القول المرجوع عنه، لأن الإجماع إنما يكون حجة بشرط أن لا يتقدم اختلاف. فإذا تقدم، لم يبق حجة. وعند هذا لا يصح التمسك بقوله - صلى الله عليه وسلم -: (لا تجتمع أمتي على الخطأ). فإن هذا القول يحسم عن الإجماع الشرط، ويثبت','2','901',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1598,'أن قولهم حجة وصواب متى اتفقوا. هذا نهاية ما ذكروه، وكل هذه الطرق من الانفصال باطلة.
فإن قيل: فما الذي ترونه؟ قلنا: الذي نراه أن القول المرجوع إليه هو الحجة، ولا يجوز التمسك بالقول المرجوع عنه، لأنا قد قررنا أن الإجماع عند القولين، إنما هو على نفي الثالث، ولم يقع اتفاق على واحد من القولين، والإجماع هو الذي يعين الحق في المجمع عليه، والمجمع عليه، هو الواحد، فيكون حجة قاطعة.
فإن قيل: (١٧٦/أ) فبم تنفصلون عن قول القاضي وغيره أنه حصل إجماع ضمني على صحة الأخذ بكل واحد من القولين، وتجويز الاستفتاء للعامة لمن شاء، وأمن المفتين، وثبوت الأقضية، ومنع نقض الأحكام، فهذا إجماع ضمني، وهو واجب الاتباع، كما قررتموه في جانب نفي القول الثالث؟ قلنا: إنما يلزم من يصوب كل مجتهد، فإنه يقدر إجماعا ضمنيا على تسويغ الاجتهاد بكل واحد من القولين، فيشكل عليه الأمر. وأما نحن فلا نرى ذلك، ونقول المصيب واحد، فلا نسلم الإجماع على تسويغ الأخذ بكل واحد من القولين، فبطل ما ذكروه.
فإن قيل: فهل يكون ما اتفقوا عليه إجماعا أم لا؟ قلنا: هو إجماع، لاتفاق كلمة الأمة بجملتها على قول واحد. فلا يصح أن يكون الحق في خلافه. فإطباقهم على حكم، يتبين أنه الحق.
فإن قيل: فهل رجوع بعضهم عن قوله يصيره كالعدم، أو يبقى القول','2','902',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1599,'معتبرا؟ فإن بقي معتبرًا، فالمسألة ذات قولين، فلا يتعين الأخذ بأحدهما، بل يرجع الأمر إلى تارات المجتهد. وإن الرجوع عنه يبطله بالكلية. فينبغي إذا رجع الحاكم أو المفتي عن قوله، أن تتعقب أحكامه، وأن يبطل ما تقدم من عباداته وغيرها. قلنا: أما قوله: ينقض أحكامه، فلا سبيل إلى ذلك، لمصلحة الحكم، فإن هذا لو فسخ، لأفضى إلى اضطراب، وعدم الوثوق بحل الأبضاع وانتقال الأملاك، فلا سبيل إليه. ونحن نقول: إذا كان الحكم في نفسه معينا عند الله، وإنما التبس علينا التعيين، وتحققنا أنه لا سبيل إلى أن يشذ الحق عن جميع الأمة في كل عصر. وإذا أطبقت الأمة على أحد الحكمين، تعين أنه الحق، ولم يجز المصير إلى الآخر، لحصول التعيين لقضية الإجماع على القول.
هذا هو مستند الجماهير في تعيين الأخذ بالمرجوع إليه. ومذهب القاضي قد بينا ضعفه. وقول الإمام راجع إليه، لأنه زعم أنه لم يتحقق تبكيت من يأخذ بالقول المرجوع عنه. وما كان ينبغي إذا لم يتحقق التبكيت أن يجوز الأخذ بالقول المرجوع عنه، بل كان ينبغي أن يتوقف عن الحكم حتى يتبين، هل كانوا يبكتون أو لا يبكتون؟ هذا منتهى الكلام في هذه الصورة.
قال الإمام: (فهذا قولنا مع اتحاد العصر. فأما إذا انقرض علماء العصر مع','2','903',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1600,'طول الزمان) إلى آخر المسألة. قال الشيخ: اختلف الناس في هذه المسألة، وهي إذا اتفق التابعون على أحد قولي الصحابة.
فالذي ذهب إليه القاضي وغيره من المحققين أن القول الثاني لا يكون مهجورا. وذهب الكرخي وكثير من أصحاب أبي حنيفة والشافعي، وكثير من القدرية، كالجبائي وابنه إلى أن القول الآخر يكون مهجورا، وما أخذ به التابعون يكون إجماعا. والصحيح أنه لا يكون إجماعا، فإن هذا القول الذي أخذ به التابعون، ليس قول جميع الأمة، وإنما هو قول البعض. وقد أخذ به الباقون، فمذهبهم اختيار أحد القولين، فإن الذين ماتوا على ذلك المذهب هم من الأمة، فإجماع من بعدهم لا يكون إجماع جميع الأمة، بل إجماع البعض، فلا يتعين الأخذ به، ولا المصير إليه. ولو كان موت بعضهم يبطل مذهبهم، لكان موت الجميع يبطل مذاهبهم حتى يجوز إحداث خلاف ما حكموا به، وذلك ممتنع سمعا. (١٧٦/ب).','2','904',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1601,'فإن قيل: فإن صرحوا بتحريم القول الآخر، فماذا تقولون؟ قلنا: يجوز أن [نقول: ] ذلك ممتنع، لأنه يفضي إلى تناقض الإجماعين، فإن الأمة مضت على التعيين على وجه القطع. فإن قطع هؤلاء القوم بالتعيين، فهو مناقض للإجماع الأول، فيكون ممتنعا. أو نقول: يصح أن يقولوا ذلك، ولكن يكونون محجوجين بالإجماع، ولا يمتنع أن يخالف بعض الأمة الإجماع، وإنما يمتنع مخالفة الجميع. وقد قلنا: إنهم بعض الأمة في هذه المسألة التي أفتى فيها الصحابة، وإن كانوا كل الأمة في مسألة أخرى. ولكن يلزم منه تجويز أن يخالفوا إجماع الصحابة بجملتهم، لأنهم بعض الأمة في المسألة التي حكم فيها الصحابة.
هذا هو اللازم على هذا الطريق، والأصوليون لا يلتزمون هذا، ويقولون: لا يجوز أن يجمع التابعون على خلاف قول الصحابة. والذي عندي أنه لم يقم له دليل على استحالة ذلك. والظاهر عندي جوازه، وهو مما أستخير الله','2','905',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1602,'فيه. ولكن هذا يفضي إلى مخالفة قوله - صلى الله عليه وسلم -: (لا تزال طائفة على الحق).
فإذا أجمع التابعون على خلاف ما قاله الصحابة، فقد تعين الحق بإجماع، فيكون التابعون على خلاف الحق، فيزول قوله: (لا تزال طائفة من أمتي على الحق). إلا أن يقال: هذا من أخبار الآحاد، فلا يحصل علما، أو يقدر أن المراد: لا يزال قول هو حق ثابت إلى قيام الساعة. وهذا بعيد عن ظاهر الخبر. والطائفة جماعة، ولهذا قال: (لا يضرهم خلاف من خالفهم إلى أن يأتي أمر الله وهم ظاهرون). فهذا ما في المسألة من الإشكال والغموض.
فإن قيل: فلو أجمع العلماء على أمر، ثم تذكر منهم واحد حديثا، وتحقق أنه سمعه من الرسول - صلى الله عليه وسلم -، وأنسي ذكره وقت الحكم، وعرف أن القوم، لو علموه، لما أفتوا بما أفتوا به، فهل يجوز أن يرجعوا إليه، أو لا يرجعوا؟ فإن لم يرجعوا، كانوا مخالفين، لا سيما الذي سمع الخبر وعلمه، فكيف يستمر على حكم مستند إلى رأيه، والسنة عنده على نقيضه؟ وإن رجع إلى مقتضى الخبر، فكيف يصح الرجوع عن أمر أجمعت عليه الأمة، وتعين الحق فيه؟
وهذا سؤال غامض. ولهذا السر اشترط بعض العلماء انقراض العصر،','2','906',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1603,'ليأمن من هذه الغائلة. وقد أجبت عن هذا بأمرين:
أحدهما - أن فرض ذلك محالن فإن الله يعصم الراوي عن نسيان ذلك، حتى يتم الإجماع، ويعصم الأمة عن الحكم على خلاف سنة ثابتة صحيحة. ولو جاز ذلك، لبطلت العصمة، ولم تتحقق الإصابة. فإن قطع الراوي بذلك؟ قلنا: هذا حديث نسخ، ولم تطلع أنت على نسخه. فلئن قيل: كيف يتصور النسخ، ولم ينقله أحد؟ قلنا: قد يتفق أن يموت من اطلع على النسخ قبل أن ينقله. فعصمة الأمة تدل على خروج الخبر عن كونه حجة. وكم من سنة أحياها عملهم المستمر؟
هذا وجه في الجواب كاف، وقد قال أبو حامد: ينظر إلى أهل الإجماع، فإن صمموا على الاستمرار على الحكم عندما بلغهم الخبر، تيقنا أنه منسوخ، أو ضعيف، أو غير ذلك من الوجوه التي تقتضي منع التعلق به. فإن رجعوا إليه، قلنا: كان ما أجمعوا عليه حقا في ذلك الوقت، ثم صار الانتقال عنه حقا.','2','907',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1604,'قال: وهذا بمثابة ما لو عمل بالمنسوخ بعد (١٧٧/أ) نزول الناسخ من لم يعلم بالناسخ، لا لتفريط من قبله، فإنه يكون محقا في عمله. فإذا بلغه الناسخ، حرم عليه العمل.
وهذا القول الضعيف، لأنه يفضي إلى أن تصح مخالفة الإجماع، ويقال: كان ذلك الذي اتفقوا عليه حقا، وقد صار تركه حقا، وكان يجوز للتابعين أن يروا خلاف رأيهم، ولا يكون الإجماع حجة على الدوام. فالصواب التمسك بالوجهين السابقين، وهما: إما الاستحالة، أو تعين سقوط العمل بالرواية. والله تعالى هو المعين، وبه الحول والقوة.
قال الإمام: (مسألة: إذا اتفق أهل الإجماع على عمل، ولم يصدر [منهم] قول) إلى قوله (ثبت ما دلت عليه القرينة). قال الشيخ: ما ذكره القاضي غامض. فإنه قال: اتفاق أهل الإجماع على فعل يبعد تصويره، فإنهم لا','2','908',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1605,'يعصمون عن الخطأ والزلل. كيف يقال هذا، وقد قال - صلى الله عليه وسلم -: (لا تجتمع أمتي على الخطأ)، والعصمة ثابتة لهم؟ فكيف يجعل الدليل على منع تصور ذلك، والعصمة غير ثابتة؟ وهي التي تضمنتها الأخبار، وقد سبقت الأدلة على عصمة أهل الإجماع. ولكنه يقول: تلك الأدلة دلت على العصمة عند الفتوى، فأما الفعل، فلم تدل عليه.','2','909',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1606,'ثم قال: (فإن زعم زاعم أن العصمة تجب لجمعيهم، فأما أن تجب لآحادهم فلا، فلم يمنع صدور الزلل عن بعضهم، فإذا كان كذلك، لم يصح ثبوت العصمة للجميع). يشير بذلك إلى أنه إن انكف بعض عن العصمة، فبعض يتعاطاها. فكأنه يمنع أن لا تصدر معصية من الأمة بجملتها. وهذا غير صحيح، فإنه يمنعون بجملتهم عن معاصٍ بينة، وهي التي تخل بالعدالة، وتمنع قبول الفتوى. فلعل مراده بذلك: المعاصي التي لا تعد من الموبقات، وهي التي يجوزها بعض الناس على الأنبياء. فلعمري إن مثل هذا الصنف، لم تجر العادة بانكفاف الجميع عنه. فإن أراد ذلك، فهو صحيح. وإذا كان كذلك، لم يصح أن يتعلق بفعلهم على إباحة الشيء الذي تعاطوه، لاحتمال أن يكون بعضهم يتعاطاه مع اعترافه بتحريمه. فإن قيل: فهذا أيضا مطرد في القول والفتوى، فينبغي أن لا تكون حجة. فله أن يقول: دلت الأدلة على العصمة في القول والفتوى، ولم تدل على العصمة في الفعل. هذا مغزى كلامه.
وأما الإمام فإنه قال: (إذا حصل اتفاقهم على فعل واحد، دل على نفي الحرج، كما تقدم في فعل رسول الله - صلى الله عليه وسلم -). فإن مستندنا في رفع الحرج عن','2','910',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1607,'الفعل المطلق الذي لا يعد من قبيل القرب، ما تقدم من اعتقاد الأول رفع الحرج عندما ينقل لهم فعل رسول الله - صلى الله عليه وسلم -. وهذا يطرد عنده فيما إذا نقل الفعل عن أهل الإجماع.
والذي قاله الإمام ظاهر عندي، ولكنه لا ينتهي عندي إلى درجة القطع. [وقياس فعل] أهل الإجماع على فعل الرسول، غير مكتفي به في هذا الفن. ولعمري لقد كان الأولون يتعلقون في كثير من الأحكام بأفعال بعض الصحابة المشهورين، فكيف إذا نقل الفعل عن الجميع؟ إلا أن القطع بالتبكيت عندما يخالف في حل ما فعله الجميع مقدر غير منقول. فلذلك تعذر العلم فيه، وحصل غالبا على الظن جدا، فينتهض علما في مسائل الظنون، على ما قررناه في نقل الإجماع على ألسنة الآحاد. ولا ينتفع به في القطعيات. هذا هو الذي (١٧٧/ب) نراه في هذه المسألة. والله الموفق للصواب.
قال الإمام رحمه الله: (الفن الرابع: في الأمر الذي ينعقد الإجماع فيه) إلى آخر المسألة. قال الشيخ: هذا الذي كنا قد ذكرنا أنه الركن الثالث. فإنا لما قلنا في حد الإجماع إنه: اتفاق أهل الحل والعقد في أمر من أمور الدين. وتكلمنا على الإجماع وحقيقته، وما يكون إجماعًا مصرحًا به وضمنيًا. وتكلمنا','2','911',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1608,'أيضًا على أهل الإجماع، باعتبار الصفة والعدد، وبقي الكلام في بيان المتفق عليه، الذي ينتهض الإجماع فيه حجة.
فأما قول الإمام: (إن الإجماع إنما يكون حجة في السمعيات دون العقليات). فغير مطرد. وهو قد تكلم على هذا في مقدمة الكتاب حيث قال: (كل مدرك يتقدم على ثبوت كلام صدق، يستحيل دركه من سمع، فإن مستند السمعيات كلها الكلام الحق الصدق). قال: (وأما من أحاط بكلام صدق، ثم','2','912',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1609,'نظر بعده في جواز الرؤية، وخلق الأعمال، [وإرادة الكائنات]، فما يقع من [هذه الأصول] [بعد ثبوت الكلام]، فلا يبعد أن يدرك من العقل). وهذا الكلام هو الصحيح.
والضابط: أن كل أمر يجوز دركه من النبي - صلى الله عليه وسلم -، يجوز دركه من أهل الإجماع. والذي يجوز دركه من الرسول: كل أمر أمكن أن يعلم صدق المدعي عند ظهور المعجزة، مع الغفلة عنه، فهذا يصح أن يدرك من الرسول. ثم إن كان أمرا يتعلق بالمعقولات، جاز أن يدرك من العقل والسمع. وإن كان لا يتلقى من المعقولات - وهو من المجوزات في العادات - اتخذ [كطريق] باعث على الشريعة. ومن هذا القبيل جميع الأحكام الشرعية، [وكل] أمر يتعلق بالدار الآخرة، من الحشر والنشر وعذاب القبر والميزان إلى تفاصيل أحكام الآخرة.
قال الإمام: (مسألة: [اختلف الأصوليون في الأمم السالفة هل كان','2','913',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1610,'إجماعهم حجة])؟ إلى آخر المسألة. قال الشيخ: ينبغي أن ينظر أولاً في هذه المسألة: هل يتعلق النظر فيها بالأصول، ولها فائدة في الشريعة أم لا؟ فإن لم يكن لها في معرفة الأحكام فائدة، تنزلت منزلة علم التواريخ، وصارت بمثابة الكلام فيما كان عليه رسول الله - صلى الله عليه وسلم - قبل أن يبعثه الله. وإن كان لها فائدة في هذه الشريعة، افتقر الأصولي إلى الكلام عليها.
فالصحيح في ذلك بناؤها على أن شرع من قبلنا شرع لنا أم لا؟ فإن ثبت أنه شرع لنا، افتقر إلى النظر في إجماعهم، هل كان حجة عندهم أم لا؟ وإن ثبت أن شرع من قبلنا ليس شرع لنا، لم نفتقر إلى النظر في هذه المسألة بحال. والذي ثبت عندنا مغلبا على الظن أن شرع من قبلنا شرع لنا. على حسب ما قررناه. فنفتقر في هذا البحث عن إجماعهم، هل كان حجة أم لا؟ قال القاضي: لم يثبت عندي في ذلك شيء. وهذا هو الذي يقتضيه أصله، فإنه إنما أسند كون الإجماع حجة إلى أخبار الشرع عن عصمة هذه الأمة، ولم يسنده','2','914',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1611,'إلى العادة، ولم يتقرر من الشارع أخبار عن عصمة الأمم السابقة. فليس على هذا إلا الوقف. وقال قائلون: لم يزل حجة. وهؤلاء يمكن أن يسلكوا مسلك العادات، في أنه لا يتأتي الاتفاق إلا عن مستند قاطع، وهو تلويح إلى طريق الإمام.
وأما تفرقة الإمام بين المقطوع به والمظنون، فإنه (١٧٨/أ) قياس قوله، لأنه لا يرى العادة تحيل على عدد التواتر أن يقطعوا بالحكم إلا عن مستند قاطع، وهذا مطرد. والاعتراض عليه قد سبق في تقرير دليل الإجماع. وقلنا: إن العادة إنما تحيل على عدد التواتر تعمد الكذب. فأما أن يظنوا ما ليس بقاطع قاطعا، فلا تحيل العادة ذلك.','2','915',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1612,'وأما الصورة المظنونة، فأول الكلام فيها جيد، وهو أن الإجماع في هذه الصورة إنما انتهض حجة لتبكيت المجمعين من خالف هذا الإجماع. قال: ولم يثبت عندنا تبكيت من خالف الإجماع المظنون في الشرائع المتقدمة. وهذا كلام صحيح، ولكنه قد قال في الإجماع المظنون في شريعتنا: إنه إنما يكون حجة بشرط الزمان وتكرار الواقعة حتى ينتهي الأمر إلى القطع. [وإن] كان كذلك، لزم المصير إليه. لأنه لا يكون مسلكا مسوغا قطعا. وكذلك ينبغي على هذا التقدير في الإجماع في الشرع القديم أنهم إذا ظنوا واستمروا الزمان الطويل مع تكرار الواقعة. فهذا لا يكون إلا عن مستند قاطع أو كالقاطع، لتناهيه في الظهور. وما كان كذلك، فهو مسلك. فإذًا إن قطعوا، دل على مستند قاطع، وهو دليل لا شك. إن ظنوا على الشرط المذكور، دل على مستند قوي، سواء كان ذلك في شرعنا أو شرعهم. فلا معنى على أصله للفرق بين المقطوع به والمظنون. لاسيما إذا كانت أحكامهم منقسمة إلى ما يقطع به وإلى ما يظن.
وحاصل الأمر [أنه] تمسك بالعادة في الموضعين، والعادة لا تختلف','2','916',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1613,'بحال. والذي نختاره في هذه المسألة التوقف في الإجماع في الملل السالفة، كما قال القاضي، لا بالنظر إلى طريقة، ولكن من جهة أنا إنما علمنا صحة الإجماع، عندما يقطع أهل الإجماع بالحكم، من جهة حصرنا جهات الخطأ، وبيان انتفائها عن الأمة، لحصول العلم بمفهوم المدلول، ولاستحالة الغلط في طريق النقل، على ما قررناه. ولم يثبت عندنا استحالة الغلط في مستند فهم الحكم من أهل الشرائع المتقدمة. فإن ثبت استحالة الغلط عليهم في فهم المستندات، فلا فرق على حال.
قال الإمام: (مسألة: نقل أصحاب المقالات عن مالك رحمه الله أنه كان يرى اتفاق أهل المدينة) إلى آخر المسألة. قال الشيخ: هذا المذهب مشهور عن مالك في الاحتجاج بإجماع أهل المدينة. ولكنه عندي لا ينزل منزلة','2','917',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1614,'إجماع الأمة، حتى يفسق المخالف، وينقض قضاؤه. ولكنه يقول: هو حجة على معنى أن المستند إليه مستند إلى مأخذ من مآخذ الشريعة، كما يستند إلى القياس وخبر الواحد.
فأما المصير إلى التفسيق والتأثيم، ونقض الحكم، فلا يقوله مالك بحال.
ثم الذي يظهر من مذهبه أن الأعمال التي نقلت عن أهل المدينة منقسمة: إلى ما نقل مستفيضا، نقله كابر عن كابر. فهذا لا يختلف مذهبه في أنه معتمد. وهذا بمثابة مسألة الأحباس، نقل لمالك أن شريحا لا يجيز الأحباس، فقال: (شريح تكلم في بلده، ولم يقدم المدينة، فيرى أحباس الصحابة والتابعين، وما ينبغي للإنسان أن يتكلم فيما لم يحط به خبرا). وكأن مذهبه في هذا يشير إلى','2','918',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1615,'أن هذه الأمور إنما استمرتـ بناء على سنة مستقرة عند القوم. ولهذا قال هو أو بعض أصحابه: (كم من سنة دارسة أحياها عملهم المستمر). وكذلك المد والصاع (١٧٨/ب) وغير ذلك من الأشياء التي يتواتر نقلها عن الأولين من الصحابة والتابعين. هذه صورة.
الصورة الثانية: أن يرووا أخبارا ويخالفوها. فهذا قد تقدم الكلام عليه. واختيار الإمام أن الراوي الواحد إذا فعل ذلك، سقط التمسك بروايته، ورجع إلى عمله. فما الظن بعلماء المدينة بجملتهم؟ فعلى هذا يدل عملهم على ناسخ اتفق بلوغه إليهم ولم يبلغنا. فكأنه في الحقيقة تمسك بخبر دل على اتفاقهم عليه.
الصورة [الثالثة]: أن لا ينقلوا الخبر، ولكن يصادف خبر على نفيض حكمهم. فهذه أضعف من الأولى. ولكن غلبة الظن حاصلة بأن الخبر لا يخفى عن جميعهم، لهبوط الوحي في بلدهم، ومعرفتهم بالسنة. ولهذا كان الناس إذا اختلفوا في غير المدينة في الأحكام الشرعية، أرسلوا إلى المدينة يسألون عن ذلك، ثم يصيرون إلى ما يذكره أهل المدينة. فالظاهر منهم على هذه الحالة أن','2','919',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1616,'يكون الخبر ثابتا عندهم، فيتنزل منزلة ما لو رووا وخالفوا. وقد قدمنا الكلام على هذا قبل ذلك.
الصورة الرابعة: أن لا ينقل خبر على خلاف قضائهم، ولكن يكون القياس على غير ذلك. فهذا موضع فيه نظر، فقد يقال: إنهم لم يخالفوا القياس مع معرفتهم به، وبكونه حجة في الشريعة، إلا لتوقيف منع من التمسك به. وقد يقال: قد ينظرون نظرا لا يوافقون عليه.
لا جرم اختلف قول مالك في هذه الصورة: كما في القصاص بين الحر والعبد، وبين الكافر والمسلم يجري في النفوس من أحد الجانبين، ونعني بذلك أن العبد إذا قتل الحر قتل به. وإن قتله الحر، لم يقتل به. وكذلك إن قتل الكافر المسلم، قتل به. وإن قتل المسلم الكافر، لم يقتل به. وأما إذا وقعت الجراح في الأطراف، فقد قال مالك في القول المشهور: إنه لا يجري القصاص بينهما في الأطراف. وهو قول الفقهاء السبعة. وإن كان القياس يقتضي إلحاق الأطراف بالنفوس في القصاص.
ولمالك قول آخر أن القصاص يجري، تمسكا بالقياس. والأول هو المشهور من مذهبه، وهو [يشير] إلى إثبات خبر، تلقيا من قضاء العلماء بخلاف القياس. وقد قال الشافعي: (نقل عن علي - كرم الله وجهه - أنه صلى','2','920',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1617,'في ليلة ست ركعات، في كل ركعة ست سجدات). قال الشافعي: لو ثبت هذا عن علي لقلت به). لأنه لا يفعل ذلك إلا عن توقيف. فهذا الذي أنكر على مالك، هو عين مذهب الشافعي. وطريقه من جهة تغليب الظن، ما ذكرناه.
الصورة الخامسة: أن يصادف قضاؤهم، لا على خلاف خبر منقول عنهم أو عن غيرهم، ولا على خلاف قياس، حتى يستدل به على خبر لأجل مخالفة القياس. والصواب عندي في هذه الصورة أن لا يلتفت إلى العمل المنقول، ويرى الناظر رأيه في المسألة، إما موافقا أو مخالفا. إلا أن يقول قائل: إنهم أعلم من غيرهم. فهذا جنوح إلى التقليد من غير استناد إلى دليل. وليس هذا عندنا من طرق الاجتهاد. فالصواب الإضراب عن العلم في [هذه الصورة].
وبالله التوفيق.
قال الإمام: (مسألة: إذا اتفق علماء التابعين على حكم في واقعة عنت لهم) إلى آخر المسألة. قال الشيخ: ما صار (١٧٩/أ) إليه المحققون من الأصوليين أن إجماع أهل كل عصر حجة، على الشرط المتقدم. وذهب داود','2','921',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1618,'وغيره من أهل الظاهر إلى أنه لا حجة إلا في إجماع الصحابة. وهذا غلط فاحش، فإن الأدلة [الثلاثة] وهي: الكتاب والسنة [والعادة]- على حسب اختلاف الناس في مستند الإجماع - لا تفرق بين عصر وعصر. والتابعون في زمانهم في المسألة التي انفردوا بالحكم فيها، هم كل الأمة، فهو إجماع من جميع الأمة. ومن خالفهم، فهو سالك غير سبيل المؤمنين. ويستحيل بحكم العادة أن يشذ الحق مع [كثرتهم] عند من يأخذه من العادة. وقد تمسكوا بشبهتين:
إحداهما - أنهم قالوا: مستند الإجماع الآية، وهو قوله تعالى: {ويتبع غير سبيل المؤمنين}. فيتناول الذين عقلوا الإيمان، وهم الموجودون وقت نزول','2','922',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1619,'الآية، فإن المعدوم لا يوصف بالإيمان، ولا يكون له سبيل.
وقوله - عليه السلام -: (لا تجتمع أمتي على الخطأ). يتناول أمته الذين آمنوا به، وتصور إجماعهم واختلافهم، وهم الموجودون. وهذا باطل من وجهين:
أحدهما - أن هذا غلط على مقتضى اللسان، فإن القائل إذا قال: من فعل كذا لزمه كذا. لا يكون ذلك مختصًا من جهة وضع اللسان بموجود وقت الخطاب، بل لهذا اللفظ صلاحية لتناول كل من كان أو يكون إلى آخر عمر الدنيا. وكذلك قول الرسول - عليه السلام -: (لا تجتمع أمتي على الضلالة أو الخطأ). لا يختص بالموجودين وقت القول على حال. ولذلك كان كل قول يتعلق بالآخرة، متناولاً لكل من يكون من أمته إلى يوم القيامة. وقد قال رسول الله - صلى الله عليه وسلم -: (شفاعتي لأهل الكبائر من أمتي). ولم يختص ذلك بأمته الذين كانوا موجودين وقت قوله.
وكذلك جميع الألفاظ التي في كتاب الله عز وجل التي لم تأت على جهة خطاب المواجهة، كقوله تعالى: {والسارق والسارقة ... } و} الزانية','2','923',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1620,'والزاني ... }} ومن يقتل مؤمنًا متعمدًا .. }، } ومن يطع الله ورسوله فقد فاز فوزًا عظيمًا}. فهذه الألفاظ باتفاق أهل اللسان وعلماء الإسلام لا اختصاص لها بحال. نعم، قد يتردد المتردد في الألفاظ التي جاءت على خطاب المواجهة، كقوله: {يا أيها الذين أمنوا}، } يا أيها الناس}.وما يجري مجرى ذلك، هل يتناول هذا من جاء بعد ذلك، أو يكون مختصا بالموجودين؟ هذا فيه نظر.
والصحيح أنه متناول للجميع، فإن الله تعالى هو المخاطب على الحقيقة، والرسول في معنى المبلغ. فإذا تم التبليغ، لم يؤثر موت المبلغ. فالله تعالى قائل، ونحن في زماننا: {يا أيها الذين أمنوا .. }. وقد قال الله تعالى خبرا عن رسوله - صلى الله عليه وسلم -: {وأوحى إلى هذا القرءان لأنذركم به ومن بلغ}. فالقرآن نذير للجميع، ومبشر أيضا.
ثم كان يلزم من مقتضى ما ذكروه، أنه إذا مات واحد من المسلمين بعد نزول هذه الآية، أن لا يلتفت إلى إجماع من بعده، لأن المعصومين قد مات بعضهم. وقد ساعدونا على انعقاد الإجماع حجة بعد موت رسول الله - صلى الله عليه وسلم -،','2','924',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1621,'بعد أن مات بعض من كان حيا وقت نزول الآية.
وكان يلزم أيضًا عليه أن من تجدد بعد نزول الآية، وبلغ مبلغ الاجتهاد، أن لا يلتفت إليه في إجماع، ولا في خلاف. وهم لا يقولون ذلك، بل اعترفوا بأن الميت بعد النزول، وقبل موت رسول الله - صلى الله عليه وسلم -، لا يضر في انعقاد الإجماع. وكذلك يعتبر من حدث بعد النزول وبلغ رتبة الاجتهاد في زمن الصحابة.
الشبهة (١٧٩/ب) الثانية: قالوا: الواجب اتباع سبيل جميع المؤمنين، والذين ماتوا لم يخرجوا من الأمة، بدليل ما لو قالوا قولا ثم ماتوا، فإنهم بموتهم لم يخرجوا من الأمة. فإجماع التابعين بعدهم، لا يكون إجماع جميع الأمة، بل إجماع البعض، فلا تكون فيه حجة. فإذا كان الخلاف يدفع الإجماع، فالوفاق مشترط، ولم يوجد الوفاق.
قالوا: وقياس هذا أيضًا يقتضي ألا يثبت نعت الكلية للصحابة، ولكن لو قيل بذلك، لم ينتفع بالإجماع بحال. فأثبتنا إجماع الصحابة حجة، ولم يثبت ما زاد عليه. وإلا فلولا ذلك، لكنا ننتظر لحوق التابعين أيضًا. وهذا يفضي إلى أن لا يكون إلا في القيامة. هذا خيالهم، وهو باطل على القطع بأوجه:
منها - أن ما ذكروه يبطل بالميت الأول من الصحابة، [لأنه] بموته لم يخرج من الأمة، ولا ينبغي أن ينعقد إجماع بعده.
وأيضا - فإنا نعلم أن الرسول - صلى الله عليه وسلم - ما أراد بعصمة الأمة التعرض لمن هلك','2','925',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1622,'قبل وقوع الواقعة. [و] إنما يقول هذا لمن يتصور منه الحكم في المسألة بعد فهمها. وأما الميت فهو معدوم على الحقيقة، فكيف يصح أن يكون المراد بقوله: (لا تجتمع أمتي على الخطأ). للمعدوم؟ هذا محال لا شك فيه.
وأيضا - فإنا لما قطعنا بأنه لا نظر إلى الآتين، فلا نظر إلى الماضين، بل انتظار الآتي أقرب، فإنه يصح أن يدخل الوجود، ويكون من أهل الاجتهاد. وأما من وجد ثم عدم، فنحن على بصيرة من أنه لا يعود إلى يوم القيامة، دل ذلك على أن الماضي لا يعتبر، كما أن الآتي لا ينتظر.
وأيضا - فإنه علم بالضرورة أنه ليس المراد بالأمة الطفل والمجنون، وإن ثبت لهما أصل الوجود، فلأن لا يراد به المعدوم والميت أولى. بل نعت الكلية ثابتة للموجودين في كل عصر، من غير نظر إلى سابق أو لاحق.
قالوا: لو كان الأمر كذلك، للزم أن يقال: إذا أجمع التابعون على أحد قولي الصحابة، أن يكون القول الآخر مهجورا، لأن الأمة في هذا الزمان على خلافه، وليس كذلك، دل ذلك على أن الميت معتبر. وعنه جوابان:
أحدهما - أن هذا يبطل بالميت الأول من الصحابة، لأن إجماع من بعده إجماع جميع الأمة. دل ذلك على أن لا التفات لموت من يموت في انعقاد الإجماع، في مسألة انفرد أهل العصر بالحكم فيها.
الثاني - أن السبب في أن قول التابعين لا يكون إجماع جميع الأمة، لأنهم في المسألة المعينة ليسوا كل الأمة، فإن المذاهب لا تموت بموت','2','926',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1623,'أربابها. قالوا: هذا تناقض، لأن التابعين كل الأمة في مسألة، وليسوا كل الأمة في مسألة، وهم بحالهم، هذا جمع بين النفي والإثبات. قلنا: ليس كذلك، فإن التناقض إنما يكون عند التوارد على الشيء الواحد، من وجه واحد. وههنا لا يثبت لهم نعت الكلية بالإضافة إلى المسألة التي انفردوا بالحكم فيها، بخلاف المسألة التي أفتى فيها غيرهم.
قالوا: لو خالف [واحد] من الصحابة، لم ينعقد الإجماع [إذا نقل]. فإذا لم ينقل، فيمكن أن يكون [خالف]، وإذا تطرق الإمكان، بطلت الحجة القاطعة. وعنه جوابان:
أحدهما - أن نقول: هذا يبطل بالميت الأول من الصحابة، (١٨٠/أ) فإن إمكان خلافه لا يكون كحقيقة خلافه.
الثاني - أنه يلزم إذا ثبت أن الصحابة لم يخوضوا في المسألة، أن يعرف بصحة إجماع التابعين، وكونه حجة، وهم لا يقولون بذلك. وقد ذهب بعض الناس إلى أن القول الآخر يصير مهجورًا، بناء على ثبوت نعت الكلية للتابعين. فعلى هذا لا ورود للشبهة على حال. ولكنا لا نختار هذا، ولا يكون إجماع التابعين على أحد القولين موجبًا لاطراح القول الآخر. وقد قدمنا بيان ذلك.','2','927',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1624,'الثالث - أنه لو التفت إلى هذا الاحتمال المجرد، لم يثبت شيء من الحجج، إذ يقول القائل: ما المانع من نسخ النص، ولم يعلم بذلك؟ وما المانع من رجوع واحد من المجمعين قبل اتفاق الكل؟ فنقول: هذا الاحتمال يبطل الحجج على الإطلاق. وما أفضى إلى رفع الشرع، وجب الإعراض عنه.
فإن قيل: الأصل عدم النسخ وعدم الرجوع، فلنستصحب الأصل. قلنا: هذا باطل من وجهين:
[أحدهما -] أن التمسك بالأصل، لا [يحصل] علمًا بالحكم، بل قد قال جماعة من الأصوليين: ليس الاستصحاب من الأمارات بحال. فكيف يقطع بدوام الحكم، بناء على الاستصحاب، لولا قواطع السمع؟ على أنه لا التفات لذلك الاحتمال المجرد.
الثاني - أن هذا يبطل بالميت الأول من الصحابة، فإن إمكان خلافه، لا يتنزل منزلة حقيقة خلافه. وفي انعقاد إجماع الصحابة على تحريم مخالفتهم، ووجوب الاتباع، يدل دلالة قاطعة على وجوب الإضراب عن هذا الاحتمال. هذا تمام الكلام. والله المستعان.','2','928',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1625,'قال الإمام: (مسألة: إذا ذهب معظم العلماء إلى حكم وخالف فيه واحد منهم) إلى آخر المسألة. قال الشيخ: اختلف الناس في مخالفة الأقل، هل يقدح في الإجماع أو لا يقدح؟ والصحيح عندنا أنها قادحة. وقال قوم: إن بلغ عدد الأقل عدد التواتر اندفع الإجماع وإلا فلا. وإنما قلنا ذلك، لأن الأدلة الدالة على عصمة الأمة إنما تتناول الجميع، وعند مخالفة البعض، لا يتحقق الإجماع. فإن قيل: قد تطلق الأمة ويراد بها الأكثر. كما يقال: بنو فلان يحمون الجار، ويكرمون الضيف، ويريدون الأكثر. قلنا: هذا مجاز، لا يصح أن تتلقى من مثله مآخذ الحقائق.
وقد تمسكوا: بأن مخالفة الواحد شذوذ، وقد نهى عن الانفراد، فكيف','2','929',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1626,'يتوقف الإجماع على موافقة الشاذ؟ وهذا غلط لوجهين:
أحدهما - أن المراد بتلك المناهي: الخروج عن رأي الأئمة، الذين إليهم ترد أمور الرعية، فيكون في ذلك إدخال فساد على الناس.
الثاني - أن هذا يطرد في الثالث، فإنه شاذ بالإضافة إلى بقية الأمة. هذا هو الذي نختاره.
أما ما ذهب إليه الإمام من الاعتراف بأن مخالفة الواحد مضرة في انعقاد الإجماع. قلنا: إن خصه بالإجماع المستند إلى ظن، فله وجه. وإن طرده في الصورتين جميعًا، فلا وجه له، لأنه تلقى صحة الإجماع في تلك الصورة بقطع المجمعين بالحكم مع كثرة العدد. فإذا كثر العدد، بحيث يبلغ عدد التواتر، ووقع القطع بالحكم قبل أن يراجع المخالف، قطعنا بالمستند.
وعلى هذا التقدير، ما كان يفتقر بعد بلوغ (١٨٠/ب) عدد التواتر إلى مراجعة بقية العلماء على حال، فيخرج الإجماع عن أن يكون دليلاً، ولا يبقى من مسائله وتفاريعه شيء البتة. وإنما يرجع النظر إلى حصول عدد التواتر، قاطعين بالحكم، فيعلم المستند القاطع، فلا حاجة إلى مراجعة الباقين، سواء','2','930',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1627,'وافقوا أو خالفوا: وهذا شديد، لا سبيل إلى المصير إليه بحال.
فإن قيل: فأنتم قد رجعتم إلى هذا الطريق، إذ تلقيتم القاطع من جهة حصر المستندات المانعة نم العلم ونفيها، فيرجع الأمر إلى تلقي قاطع من الإجماع. وهذا يقتضي أيضًا أن لا يشترط، بل يكتفى بعدد التواتر، فقد لزمكم ما ألزمتوه. قلنا: ليس كذلك، فإنا تلقيناه من اتفاق حصل [من] أزيد من عدد التواتر [قطعًا]، [فدل] على عصمة الأمة، لا باعتبار عدد مخصوص، بل عندما يحصل نعت الكلية للمجمعين. فلم يكن بد من حصول ما دل القاطع عليه من عصمة جميع الأمة. لا جرم أنا ننظر بعد ذلك في أن القوم قد كثر عددهم أم قل، ولا أنهم هل استندوا إلى قاطع أو مظنون؟
فكلام الإمام يقتضي النظر مستندًا إلى كل عصر على حياله. فهذا هو الفرق بين الطريقتين.
قال الإمام: (مسألة: من فروع القول في اشتراط انقراض العصر) إلى','2','931',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1628,'آخر المسألة. قال الشيخ: الذين ذهبوا إلى اشتراط انقراض العصر، اختلفوا في التابعي مثلاً: إذا بلغ رتبة الاجتهاد في زمن الصحابة، هل يتمكن من<span data-type="title" id=toc-283> إحداث الخلاف</span>؟ والسبب في ذلك النظر إلى سبب انقراض [العصر]، والامتناع من الإطلاق بانتهاض الحجة في الحال، هل ذلك لأن انقراض العصر شرط شرعًا مقصودًا بالاشتراط، أو إنما ذلك لإمكان اطلاع المجمعين أو بعضهم على أمر يقتضي الرجوع؟ فإن [كان] ذلك لثبوت الاشتراط شرعًا، لزم تسويغ','2','932',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1629,'الخلاف للتابعي، إذ قد بلغ رتبة الاجتهاد قبل تحقق الحجة القاطعة.
وهذا هو الذي حمل عليه الإمام كلامهم. وهذا لا شك - إذا قيل به - لا يتصور أن ينتهض الإجماع حجة أبدًا، لأنه إذا كان [وجود] التابعي يمنع انعقاد الإجماع لبلوغه رتبة الاجتهاد قبل انقراض الصحابة، فلما بلغ هو رتبة الاجتهاد، صار له قول، فيفتقر في انعقاد الإجماع إلى اشتراط موته. ثم كذلك فيمن يلحقه من الناس، وعلى هذا يمتنع القضاء بانعقاد الإجماع، وافق أو خالف، فلا يزال العلماء يتلاحقون والإجماع [لم] يتوقف أمره.
[فعلى] هذا لا ينتهض الإجماع حجة إلا بشرط انقراض العلماء','2','933',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1630,'بجملتهم، من غير أن يدركهم عالم مجتهد. وهذا عسر التصوير. فإذًا الصحيح أن الاشتراط عندهم، إنما كان لإمكان رجوع بعض المجتهدين. وشبههم تدل على ذلك، وقد بينا أن المقالة في أصلها ضعيفة، والتفاريع عليها أضعف منها. فالصواب الإضراب عن الاشتراط مطلقًا. وقد بينا وجه ذلك فيما سبق، فلا نعيده.
وأما ما ذكر عن ابن عباس، فلا يصح أن يكون أحدث الخلاف بعد سبق الإجماع. ولو كان كذلك، لكان النكير يشتد عليه. بل لا وجه لذلك إلا أنه كان وقت الفتوى من أهل الاجتهاد، ولم يبد مذهبه ذلك الوقت، لسبب اقتضاه. ولذلك أنه قال لما قيل له: (لم لم تظهر هذا في زمن عمر؟ (١٨١/أ)','2','934',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1631,'فقال: كان مهيبًا فخفته). فقد صرح بأنه كان يعتقد خلافهم وقت فتاويهم، ولكنه لم يظهر ذلك. والله أعلم.
قال الإمام: (مسألة: فشا في لسان الفقهاء أن خارق الإجماع يكفر) إلى آخر المسألة. قال الشيخ: قد ذكرنا فيما سبق ما يكفر [به]، وذكرنا أن المتفق عليه ثلاثة أقسام: منها ما يكون [نفس] اعتقاده كفرًا، ومنها ما ورد الشرع بأنه لا يصدر إلا من كافر، ومنها إنكار ما علم ضرورة من دين الأمة. وبينا أن التكفير في هذا إنما كان بالنظر إلى تكذيب الشارع، فإن من كذب الشارع، كفر على الإطلاق. وأما من كذب عليه، فيما لا يتعلق بالعقائد، بل في أصول الفقه وفروعه، فإنه لا يكفر.','2','935',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1632,'ومن هذا القبيل جحد كون الإجماع حجة، وكون القياس وخبر الواحد علامة، وكون الغرر مثلاً لا يفسد البيع، إلى غير ذلك من مسائل الأصول والفروع.
وأما كل من كذب الشارع، ولو في أيسر شيء، فهو كافر جاحد لمدلول المعجزة. ولا يصح الإيمان إلا بإثبات الصانع وتصديق رسله. ففرق بين تكذيب الرسول، وبين الكذب عليه.
وقد اختلف الناس في التكفير بمآل القول، وهو كقول المعتزلة بنفي الصفات مع الاعتراف بالأحكام، [وكقول] المعتزلة: إن الباري سبحانه تعالى عالم بنفسه، ولا علم له. فقال قائلون: هو بذلك كافر. وهذا قول أبي الحسن، وهو الجاري على أصله، فإنه ينفي الأحوال، ولا يثبت [لها]','2','936',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1633,'الأحكام، ولا ثابت عنده إلا الصفات.
وإذا كان كذلك، فلا فرق بين أن يقال: ليس بعالم، وبين أن يقال: لا علم له. إذ لا معنى لكونه عالما، إلا قيام العلم به. وهم مجمعون على أنه لو قال: ليس الباري تعالى عالما، فإنه كافر، فكذلك إذا قال: لا علم له. فهذا يرجع إلى التناقض، إذ صار معنى الكلام: عالم ليس بعالم، أو له علم ولا علم له. ويصح أن يقال بالتكفير: أخبر بصفات نفسه، كقوله: {أنزله بعلمه}. وتكون الأدلة القطعية قطعت الاحتمالات، وحقيقة النصوصية، فيصير ذلك إنكار متواتر. والأول أقوى.
وقد اختلف قول مالك في ذلك، فسئل عن القدري، فقال: كافر فاقتلوه.','2','937',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1634,'وقد قال أيضًا: إنه يضرب ويسجن حتى تظهر توبته. وسئل: هل يزوج القدري؟ فتلا قوله تعالى: [} ولعبد مؤمن خير من مشركٍ ولو أعجبكم]}. والقول الأول أبين، لأنهم يعتقدون الصانع وإثبات النبوات. وهذان الأصلان هما اللذان أنبأ عنهما [قولنا]: أشهد أن لا إله إلا الله، وأشهد أن محمدا رسول الله. ومآل القول غير معتقد في الحال. وقد قال - صلى الله عليه وسلم -: (القدرية والمرجئة مجوس هذه الأمة). فلم يخرجهم من الأمة، وإنما هم أهل أهواء وبدع، فأما التكفير فلا. هذا هو الظاهر عندنا. والله الموفق للصواب.','2','938',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1635,'والحمد لله حق حمده، وصلواته على محمد نبيه، وعلى آله وصحبه. وحسبنا الله ونعم الوكيل. ولا حول ولا قوة إلا بالله العلي العظيم.
نجز الجزء الأول من كتاب (التحقيق والبيان في شرح البرهان). تأليف الفقيه الإمام أبي الحسن علي بن إسماعيل الصنهاجي التلكاني، عرف بالأبياري، وفقه الله لطاعته. وذلك في أول يوم من شهر شعبان من سنة أربع عشرة بعد ستمائة.','2','939',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1636,'بسم الله الرحمن الرحيم
[وبه نستعين]. وصلى الله على سيدنا محمد وعلى آله وسلم تسليمًا.

[قال الإمام - رضي الله عنه -: ] <span data-type="title" id=toc-285>(كتاب القياس</span>-[القياس: مناط الاجتهاد وأصل الرأي]) إلى قوله (وخضنا في الوفاء ببيان الجمل والتفاصيل). [قال الشيخ أيده الله]: قول الإمام: القياس مناط الاجتهاد وأصل الرأي. هذا الكلام كله على التجوز والتوسع، وإطلاق اللفظ العام لإرادة البعض، فإن الاجتهاد غير مختص بالقياس، بل الاجتهاد في القياس أحد وظائف','3','5',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1637,'المجتهدين، ويكون الاجتهاد في الألفاظ و<span data-type="title" id=toc-287>تحقيق المناط</span>، وغير ذلك مما ينظر فيه المجتهد. وكذلك قوله: ومنه يتشعب الفقه. لا يعني أن الفقه يقتصر تشعبه على القياس والأساليب [و] الطرق، ولم تقتصر طرق الشريعة على القياس، ولكنه هو-لعمري [الكثير] الغالب.
وقوله: (وهو المفضي إلى الاستقلال بتفاصيل أحكام [الوقائع]، مع انتفاء الغاية والنهاية). قال: فإن النصوص في الكتاب والسنة والإجماع محصورة. قال: والوقائع التي يتوقع وقوعها لا نهاية لها. وقال: والذي يسترسل على الأحكام القياس. ثم إنه ذكر أن جميع مسائل الشريعة محكوم فيها بحكم شرعي. وهذا الذي ذكره صعب شديد، وقد قصد به محاولة إثبات شرف القياس، وبيان عموم فائدته. وهذا القدر الذي يقصده يحصل بدون هذه الدعوى، فإنا لا ننكر أن المسائل الراجعة إلى القياس والرأي، أكثر وأوسع من','3','6',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1638,'المسائل الراجعة إلى التوقيف.
وأما قوله: (إن الوقائع التي يتوقع وقوعها لا نهاية لها). فهذا أمر لا يفيد بالإضافة إلى شرف القياس وكثرة فوائده، [فإن] الوقائع التي يحكم فيها بالتوقيف لا نهاية لها آحادًا، بل يتوقع وقوعها إلى غير نهاية. وأما الأجناس التي يقضى فيها بالقياس والنصوص، فهي مضبوطة عند العلماء أجناسًا لا آحادًا.
وأما مصيره إلى أن كل واقعة لابد فيها من حكم الله تعالى، فهذه دعوى لا برهان عليها. وقد سلك هو في تقريرها مسلكًا فقال: ما من فن من فنون','3','7',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1639,'الأحكام إلا ويتقابل فيه نفي وإثبات، ثم النهاية تنتفي عن أحد المتقابلين، فينتظم من ذلك الحكم في النفي والإثبات جميعًا. واستدل القاضي بأنه لم ينقل عن واحد من الماضين أنه [توقف] في مسألة، بناء على أنه لا حكم فيها، فعلم من هذه الجهة، أن جميع الوقائع فيها أحكام ثابتة. قال: والمسترسل (٢/ ب) عليها هو القياس. وهذا الكلام مشكل كله.
أما كونه يدعي على أهل الإجماع أنهم اعتقدوا تعميم [الأحكام] لجميع المسائل، فهذا لم ينقل صريحًا، والاستقراء لا يرشد إليه. وقد نقل عن جماعة من الأولين التوقف في الحكم، ولم يقولوا إن ذلك لقصورٍ منا، ولا','3','8',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1640,'تخلو [المسألة] عن الحكم، [فالأمران] محتملان. ثم لو قدرنا أنهم لم يتوقفوا، فلعلهم صاروا إلى أن ما لا دليل عليه من الشرع، [لا] حكم [فيه]. فيكون قولهم: لا شيء عليك، أي لا حكم عليك. ولا يلزم على هذا [التقدير] أن تشمل الأحكام الشرعية الوقائع كلها، وإنما يكون ذلك لازما [لو جروا على] التفاصيل التي لا تنضبط (٢/ب) على أحكام مختلفة، لا يتأتي أن ترشد البراءة الأصلية إليها. فحينئذ يتبين أن القوم قضوا بأن [أحكام الشرع] تتناول جميع المسائل.','3','9',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1641,'وقد تقدم للإمام ما يناقض هذا، وهو ما إذا فرض إلقاء رجلٍ رجلًا على صدر جريح [محفوف] بجرحى، على ما صوره أبو هاشم. قال: [والذي] أراه في ذلك: [أن لا] عصيان ولا تكليف. وقد سئل عنها فقيل له: مذهبك [أن لا] تخلو واقعة عن الحكم، فما الحكم في هذه المسألة؟ فقال: حكم الله [أن لا] حكم. وهذا
[كلام] متناقض، وإثبات في تحقيق نفي عام.
وعلى الجملة، المقصود أن القياس النظر فيه، أوسع من النظر في غيره.
وإذا خص بفضل اعتناء، فلأجل اتساع أبوابه، لا لكثرة فوائده. فإنه يجب','3','10',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1642,'إكمال النظر، وإنهاء البحث نهايته في الدليل، وإن لم يترتب عليه إلا حكم واحد. فليس كثرة الفوائد [بالتي] تستدعي مزيد البحث، ولا قلتها [بالتي] تقتضي تسهيل الأمر في الاجتهاد. هذا عام في الشرع، بل لابد في كل مسألة من إنهاء النظر نهايته، فإن حصل بسط، فذلك لاتساع الأبواب وكثرة المنظور فيه. هذا هو التحقيق. [والله المستعان].
قال الإمام: (القول في ماهية القياس) إلى قوله (إلى غير ذلك مما لا نرى التطويل بذكره). قال الشيخ: قد تكلمنا على الحدود فيما سبق، وبينا انقسامها إلى النفسي وغيره، وحققنا أن النفسي إنما يتصور مما تركب من','3','11',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1643,'الجنس والفصل. وأما القياس، فلا يتصور ذلك فيه. وإنما المقصود العبارة [المحررة]، الجامعة المانعة، الموضحة للمقصود.
وقد اعترض على حد القاضي بعد فهم مقصوده في قوله: القياس: حمل معلومٍ على معلومٍ في إثبات حكم لهما [أو نفيه عنهما]. وقيل: كيف يكون القياس لإثبات حكم لهما، وحكم الأصل ثابت؟ وإنما يثب بالقياس حكم الفرع','3','12',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1644,'خاصة؟ . وقد أضرب بعض الناس عن هذه العبارة، [بسبب] ما ذكرناه (٣/ أ)، وقال: القياس إثبات حكم الأصل في الفرع، لاشتراكهما في علة الحكم. وهذه العبارة الاعتراض عليها أوسع من عبارة القاضي. وللقاضي أن يقول: القائس ليس يثبت حكمًا ولا ينفيه، وإنما هو يتعرف الحكم بجامعه.','3','13',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1645,'فقوله: في إثبات حكم لهما بجامع يجمع بينهما، أي الإثبات بالجامع.
ولكن هذا فيه نظر يتعلق بأن حكم الأصل، هل يضاف إلى النص أو إلى العلة؟
فالصحيح من مذهب مالك [رحمه الله]، وهو مذهب الشافعي، أنه يضاف إلى العلة. لا جرم إذا استنبط من محل عمومٍ علةً خاصةً، تخصص حكم الأصل. وهو بمثابة [ما] إذا استنبط الإسكار من آية تحريم الخمر، اقتضى هذا أنه لا يحرم إلا المقدار المسكر، وهو قول أبي حنيفة في النبيذ، بناءً منه','3','14',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1646,'على أن حكم النبيذ، هو الذي يستند إلى العلة، وأما حكم الخمر، [فإنه] يستند إلى اللفظ العام. ونحن نقول: يحرم الكثير، لأنه مسكر، ويحرم القليل، لأنه [داعية] إلى الكثير. وكذلك نقول في النبيذ، وعليه تخرج صحة العلة القاصرة [أو] فسادها. ولا شك في [أن] النفي يصح أن يكون جامعًا، ولكن هل يصح أن يكون مقتضيًا؟ هذا فيه [نظر] يتعلق بصحة التعليل بالعدم. وسيأتي الكلام على هذا عند تحقيق الكلام على العلة.','3','15',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1647,'وقد ذهب الفلاسفة إلى أن القياس يرجع إلى المقدمات والنتائج. وهذا أبعد الاصطلاحات في القياس.
فإنه لابد أن يشعر في اللغة بالاعتبار، فيقال: لا يقاس فلان بفلان، أي لا يعتبر (٣/ أ) به. وإذا قلنا: كل نبيذ مسكر، وكل مسكر حرام، لم يكن في ذلك اعتبار بحال، [وإنما النبيذ] أحد الصور المندرجة تحت العموم.
وبقية الحدود واضحة، والاعتراضات عليها لائحة، فلا معنى للإكثار فيها.
قال الإمام: ([وأما ما] نرى ختم الفصل به فشيئان) [إلى قوله]','3','16',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1648,'(تجانب صناعة [الحدود]). قال الشيخ: ما ذكره الإمام من أن التقاسيم التي ضمنها القاضي كلامه تجانب صناعة التحديد، والذي قاله ليس بحد، [يريد أنه ليس بحد] نفسي، وقد كنا [بينا] أن الحد لفظٌ مشتركٌ يطلق على أشياء متعددة. ولعمري إنه صحيح، أن ما ذكره ليس بحد نفسي.
وأما إخراجه عن كونه حدًا بالكلية، فغير صحيح. [والعجيب] من الإمام الذي ينكر الحال، ولا يعترف بها على حال، ولا يرى اشتراك العلم [القديم والحادث] إلا في اسم محض، كيف يتفق [له] أن يقول: إن','3','17',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1649,'[الحد هو] الذي ينبئ [عن] الاشتراك في الأجناس والأنواع، ويقع الفرق فيه [بالفصول]؟ وإذا لم يقع اشتراك إلا في الألفاظ، بطل الحد النفسي [على] الإطلاق، وآلت الحدود كلها إلى لفظية أو رسمية، ولم يبق جنس ولا فصل.
قال الإمام: (٣/ ب) (وحق المسؤول عن ذلك [أن يبين [بالطريق] الواضحة أن الحد غير ممكن، وأن الممكن ما ذكرناه، ثم يقول: أقرب عبارة في البيان عندي كذا وكذا. والفاضل]) إلى قوله: (فهذا أحد الأمرين). قال الشيخ: معنى قوله: إن الحد غير ممكن، يعني الحد الحقيقي، وليس المراد نفي الحد على الإطلاق، على ما قررناه. وقد نقض الإمام هذا القول بما تقدم له في كتاب (البيان)، فإنه قد اختار أن البيان: هو الدليل، ولم يبد اعتراضًا على','3','18',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1650,'ذلك. والدليل أيضًا: قد يكون نفيًا وقد يكون إثباتًا. وقد يكون الدليل عقليًا يدل بذاته، وقد يكون وضعيا يدل بالاصطلاح، فكيف يستطيع الطامع في حده حدًا [حقيقيًا]؟ فحد الدليل وحد القياس على جهة واحدة.
قال الإمام رحمه الله: (الثاني: أن القياس قد يتجوز بإطلاقه) [إلى قوله (تفصيل القول فيه)]. قال الشيخ: الظاهر من إطلاق لفظ القياس عند الفقهاء والأصوليين، ما ذكر أولًا، وأما إطلاقه على النظر المحض، فليس ذلك ثابتًا في عرف علماء الشريعة. نعم، قد يطلق القياس على الرأي في إثبات الأحكام، إذا لم تستند إلى توقيف، كأن [استند] الرأي إلى أصلٍ أو لم يستند.','3','19',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1651,'وبهذا الاعتبار تقول الفقهاء: المسائل تنقسم إلى توقيفية وقياسية، وإن كان بعض المسائل لا يرجع إلى أصل. هذا تجوزٌ منهم، مشهورٌ في عرفهم.
وأما ما ذكره الإمام، فلم يطلقه علماء الشريعة على حال. هذا منتهى الكلام على حد القياس، وبيان المقالات فيه. ولنذكر مقدمة في حصر [مجاري] الاجتهاد في مناط الحكم، حتى يعرف موضع الخلاف من محل الاتفاق.
فنقول: الاجتهاد في مناطق الحكم، يكون في تحقيقه، أو في تنقيحه، أو في تخريجه.
القسم الأول: في تحقيق المناط. ومعناه: أن يثبت مناط الحكم بالنص أو الإجماع، وإنما يبقى على الناظر الاجتهاد في التعيين. فلا خلاف بين الأمة في قبوله، ووجوب المصير إ ليه، وهو ضرورة كل شريعة، إذ التنصيص على آحاد الوقائع غير ممكن.','3','20',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1652,'ومثاله: الاجتهاد في تعيين القبلة عند [إشكال] جهتها، فإن الله تعالى [يقول]: {وحيث ما كنتم فولوا وجوهكم شطره}. ولم يقل إن هذه الجهة هي جهة الكعبة، فلزم الاجتهاد في التعيين، ولم يكلف الخلق علم ذلك، لتعذره في حق الأكثر.
ومن هذا القبيل: الحكم بشهادة العدول، فإن الله تعالى يقول: {وأشهدوا ذوي عدلٍ منكم}. ولم تأت النصوص بتعيين العدول، مع الاضطرار إلى الحكم على العموم، فلزم الاكتفاء بظن العدالة، بناءً على الاجتهاد. وكذلك في نصب الولاة والقضاة، ونفقة القرابات، وتقويم [أورش] المتلفات.
والشبه في جزاء الصيد [عندي] من هذا القبيل، فإن الله تعالى [يقول]: {فجزاء مثل ما قتل من النعم}. ولم يبين أن البدنة مثل النعامة، (٤/أ) ولم يرد المماثلة في كل وصف، فإن ذلك متعذر، فلم يبق إلا ما يظن المكلف أنه مثل. كما أنه يظن أن الدرهم مثلًا، قيمة المتلف. وليس هذا محل اختلاف، ولا هو مراد الأصوليين بطلب الدليل على القياس.

الاجتهاد الثاني: هو [الذي] يعبر عنه ب<span data-type="title" id=toc-288>تنقيح المناط</span>، والتنقيح في','3','21',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1653,'اللغة: هو التهذيب، يقال نقح الغلام، إذا هذب أخلاقه، وكلامٌ منقحٌ، أي لا حشو فيه. فالمراد بالتنقيح: أن يكون المناط [مذكورًا] مع غيره فيما لا مدخل له في التأثير، فينقح حتى يميَّز الصحيح من غيره.
ومثاله: ما روي: (أن أعرابيًا جاء يلطم وجهه، وينتف شعره، ويقول: هلكت وأهلكت، واقعت أهلي في [النهار] رمضان). ففي الواقعة [قيود] كثيرة، بعضها محذوفٌ قطعًا، وبعضها ثابتٌ قطعًا.
وكذلك الأمر في المظنون نفيًا وإثباتًا، فتمييز ما هو محذوف عما هو معتبر، يعبر عنه بتنقيح المناط. فإنا نلحق بالأعرابي التركي والهندي، ونلحق بالزوجة الأمة، والزنا أشد. ونلحق غير ذلك اليوم بذلك اليوم، وغير ذلك الشهر بذلك الشهر. وكذلك [القول] في الزمان والمكان والمكلفين. وهل [يلحق] الأكل بالوقاع، والإتيان في غير المأتى الأصلي بالوطء؟ فيه نظر.','3','22',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1654,'وهذا النوع قد قال به أكثر الأمة، وهو خارج عن باب القياس، [فكأنه] يرجع إلى تأويل الظواهر. ولهذا أنكر أبو حنيفة القياس في الكفارات، وقال: إن الكفارة تترتب على الأكل. قال: وليس ذلك بقياس. وليس هذا القسم أيضًا [محل] غموض، ولا هو المراد بإقامة الدليل على القياس.

[و] الاجتهاد الثالث: هو الملقب ب<span data-type="title" id=toc-289>تخريج المناط</span>. واشتقاقه من الإخراج، فكأنه راجع إلى اللفظ، لم يتعرض للمناط بحال، فكأنه مستور، أخرج بالبحث والنظر.
ومثاله: أن يذكر الشرع الحكم مضافًا إلى المحل، ويقتصر على ذلك، ولا يتعرض لفظه لمناطه، فإذا توصل الناظر إلى معرفة المناط بالبحث والنظر، عبر عن ذلك بتخريج المناط. [وهذا] هو الاجتهاد القياسي، الذي عظم','3','23',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1655,'الخطب فيه، وطال النزاع بسببه، فأنكره أهل الظاهر وغيرهم. وهو من أعظم مسائل الشريعة، دليلًا وتقسيمًا وتفصيلًا. فإذا تنبهت لهذا، فسنشرع في نقل المذاهب.

قال الإمام: <span data-type="title" id=toc-290>(القول في ذكر المقالات [في قبول القياس ورده]</span>. الوجه أن نذكر المذاهب) [إلى قوله (في وقوع التعبد بالقياس]). قال الشيخ:','3','24',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1656,'. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . .','3','25',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1657,'المذاهب التي نقلها الإمام ثابتة مشهورة، ولكنه أخل بنقل مذهبٍ آخر، وهو أن','3','26',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1658,'قومًا ذهبوا إلى أنه يجب الحكم بالقياس (٤/ أ) عقلًا. وهذا المذهب لم ينقله الإمام، فيكون على هذا الفرق المبطلة (٤/ ب) ثلاثة: المحيل له عقلاً، والموجب له عقلًا، والحاظر له شرعًا. فلابد من الرد على الفرق الثلاثة.

قال الإمام: <span data-type="title" id=toc-291>(مسألة: ذهب علماء الشريعة من أهل الحل والعقد إلى أن التعبد بالقياس [في مجال الظنون جائز غير ممتنع) </span>إلى قوله (وانعقاد الإجماع على العمل به]). قال الشيخ: ما ذكره الإمام في هذا المكان في ذكر الشبهة والتفصي عنها، هو النهاية في الفصاحة والاختصار، وحسن الرد، فلا مزيد عليه بحال. ولكنه بقي للقوم شبهة في إبطال القياس، [لم] يذكرها، فلابد من','3','27',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1659,'ذكرها، وهي عمدة القوم.
وذلك أنهم قالوا: الحكم لا يثبت إلا بتوقيف، فإن العقول لا تدل على إثبات الأحكام، والعلة غايتها أن تكون منصوصًا عليها، فلو قال الشارع: اتقوا الربا في كل مطعوم، فهو توقيف عام. ولو قال: اتقوا الربا في البر، فهذا لا يساويه، ولا يقتضي الربا في غير البر. كما لو قال المالك: اعتق من عبيدي كل أسود. ولو قال: اعتق [غانمًا]، لأنه سيئ الأدب حتى أتخلص منه، لم','3','28',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1660,'يلزم منه عتق سالم، وإن كان أسوأ خلقًا منه. [وإذا] كانت المنصوصة لا يمكن تعديتها لقصور لفظها، [فالمستنبطة] كيف تعدى؟ أو كيف يفرق بين كلام الشارع وكلام غيره في الفهم؟ وإنما منهاج الفهم وضع اللسان، [وذلك] لا يختلف. وهذا كلام واقع في مدافعة منع القياس. وفي','3','29',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1661,'الجواب عنه تمهيد [قاعدته] وبيان سره.

والجواب عنه: أن<span data-type="title" id=toc-292> نفاة القياس [ثلاث] فرق</span>، وهذا [السؤال] لا يستقيم من فريقين، إذ منهم من يقول: التنصيص على العلة كذكر اللفظ العام، وأنه لا فرق بين قوله: حرمت الخمر لشدتها، وبين قوله: حرمت كل مشتد، في أن كل [واحد] يوجب تحريم النبيذ، لكن بطريق اللفظ، لا بطريق القياس، بل فائدة قوله: لشدتها، إقامة الشدة مقام الاسم العام. فقد أقر القائل بالإلحاق، وإنما أنكر تسميته قياسًا.','3','30',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1662,'الفريق الثاني: وهم القاسانية والنهروانية فإنهم جوزوا القياس بالعلة المنصوصة دون المستنطبة، [وقالوا]: إذا كشف النص أو دليل آخر عن علة الأصل كانت العلة جامعة للحكم في جميع مجاريها. وما فارقهم الفريق الأول إلا في التسمية، حيث لم يسموا هذا الفن قياسًا. والفريقان مقران بأن هذا في العتق والوكالة لا يجري، فلا يصح فيها الاستشهاد مع الإقرار بالفرق.','3','31',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1663,'وأما الفريق الثالث: وهو من أنكر الإلحاق مع التنصيص على العلة، فيصح لهم التمسك بهذا الطريق. وجوابهم من ثلاثة أوجه: أحدها -أن الصيرفي صار إلى التسوية فقال: إذا [قال]: أعتقت غانمًا لسواده فاعتبروه، (٥/ أ) وقيسوا عليه كل أسود، يعتق كل عبد أسود وهو وزان مسألتنا إذا أمرنا بالقياس، ولو لم يثبت ذلك، لكان [التنصيص] على العلة لا يوجب إلحاقًا. ويجوز أن تكون العلة شدة الخمر خاصة.
ومنهم من قال: إن علم قطعًا قصده إلى عتقه لسواده عتق كل عبد أسود، بقوله: أعتقت غانمًا لسواده.
ومنهم من قال: [لا يكفي] العلم بقصده (٤/ ب) أن يعتق لسواده ما لم','3','32',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1664,'ينو بهذا اللفظ عتق جميع السودان، ولم يكن فيه إلا إرادة معنى عام بلفظ خاص، وذلك غير منكر، كما [لو] قال: والله لا أكلت لفلان خبزًا، ولا شربت له ماء، ونوى به دفع المنة، [حنث بأخذ] الدارهم والدنانير وغيرها من ماله، وصلح اللفظ الواحد مع هذه النية للمعنى العام، كما صلح قوله تعالى: {[إن الذين] يأكلون أموال اليتمى ظلمًا} للنهي عن الإنفاق العام. وقوله: {فلا تقل لهم أف} للنهي عن الإيذاء العام. فإذا [استتبت] لهذه الفرق التسوية بين الخطابين، فإنهم [إنما] يعممون الحكم إذا دلت الأدلة على إرادة [الشارع] تعليق الحكم بالشدة [المجردة].','3','33',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1665,'قال أبو حامد: وليس شيء من ذلك مرضيًا [عندنا بل الصحيح] أنه لا يعتق إلا [غانم] [بقوله: ] أعتقت غانمًا لسواده، [وإن] نوى عتق السودان، [لأنه] يبقى في حق [غير] غانم مجرد النية والإرادة فلم تؤثر. وهذا الذي قاله أبو حامد تجنٍّ لا نرتضيه، بل إذا قصد بذلك اللفظ العموم صح عندنا، وقد ينقل اللفظ إلى معنى العموم بالإرادة. هذا إذا قلنا لابد من مناسبة اللفظ للمقصود. وهذا غير مشروط على الصحيح من مذهب مالك','3','34',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1666,'[- رضي الله عنه -]. بل الصحيح من مذهبه أن كل لفظ تكلم الرجل ينوي به الطلاق أو العتق لزمه ذلك. ومأخذه فيه: أن الألفاظ إنما وضعت أدلة على ما في [النفوس]، وهي اصطلاحية، ولا يلزم [من] الاصطلاح، الجريان على اصطلاح مخصوص، إلا أن يثبت من الشرع تعبد في ذلك، كألفاظ التكبير والتشهد. ويا ليت شعري، لو كانت الألفاظ تعبدية، كيف كان العجم [يتوصلون] إلى العتق والطلاق؟ ولا خلاف في صحة ذلك منهم، وإن عبروا بغير لغة العرب.
فإن قيل: هم قد اصطلحوا على ألفاظ تقوم مقام تلك الألفاظ. قلنا: لما صح لهم ذلك، بطل التعبد بغير صورة اللفظ، ورجع الأمر إلى ما وضعه','3','35',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1667,'الواضع معبِّرًا به عما في نفسه.
فإن قيل: العجم تفهم بعضهم [عن] بعض مدلول تلك الألفاظ، فصارت كلغة العرب، بخلاف قوله: اسقني الماء، فإنه لا يفهم عنه أنه أراد الطلاق. وعنه جوابان:
أحدهما: [أن نقول: ] لا [يتوقف] الحكم (٥/ ب) بالطلاق والعتق على [مخاطب] يفهم، فإنه لو قال: امرأتي طالق وحده، لزمه ذلك، كذلك العتق. فليس اشتراط فهم القول لغير المتكلم شرط في لزوم هذه الأحكام.
الوجه الثاني: أن ذلك لو كان صحيحًا، للزم إذا فهم عنه بقرائن أحواله أنه أراد به الطلاق أن يقع، والقوم لا يقولون ذلك، ولا يفرِّقون، ولئن صار صائر إلى ذلك، لم يمكن منه، فإن الأمة مجمعة على خلاف [هذه] الفرق، بل الناس فريقان: فريق يشترط مناسبة اللفظ في وضع اللغة، وفريق لا يشترط','3','36',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1668,'ذلك. فأما المصير إلى الفرق بين أن يفهم عنه مراده أم لا، فلا ذاهب إليه.
قال أبو حامد: الوجه الثاني من الجواب: أن الأمة مجمعة على الفرق، إذ يجب التسوية في الحكم مهما قال: حرمت الخمر لشدتها، فقيسوا [عليها] كل مشتد. ولو قال: أعتقت غانمًا لسواده، فقيسوا عليه كل أسود. اقتصر العتق عند الأكثر على غانم. فكيف يقاس أحدهما على الآخر، مع الاعتراف بالفرق؟ وإنما اعترفوا بالفرق، لأن الحكم لله تعالى في أملاك (٥/ أ) العباد، وفي أحكام الشرع. وقد علق أحكام الأملاك حصولًا وزوالًا بالألفاظ، دون الإرادة المجردة.','3','37',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1669,'وأما أحكام الشرع، فتثبت بكل ما دل على رضا الشارع وإرادته، من قرينة أو دلالة، وإن لم يكن لفظ. بدليل أنه لو بيع مال التاجر [بمشهد] منه بأضعاف ثمنه، [فاستبشر] وظهر أثر الفرح عليه، لم ينعقد البيع إلا بتلفظه بإذن سابق، أو بإجازة لاحقة عند أبي حنيفة. ولو جرى بين يدي رسول الله - صلى الله عليه وسلم - فعلٌ وسكت عليه، دل سكوته على رضاه، وثبت الحكم به، فكيف يتساويان؟ بل ضيق الشرع تصرفات العباد حتى لم تحصل أحكامها بكل لفظ، بل ببعض الألفاظ؛ فإنه لو قال الزوج: فسخت النكاح، وقطعت علاقة الحل، ورفعت الزوجية بيني وبين زوجتي، لم يقع الطلاق، ما لم ينو الطلاق، فإن تلفظ بالطلاق وقع، وإن نوى غير الطلاق. فإذا لم تحصل الأحكام بجميع الألفاظ، بل ببعضها، فكيف يحصل بغير لفظ [مما] يدل على الرضا؟ .
وهذا الكلام الذي قاله أولًا فيه تناقض، فإنه قال: الأمة مجمعة على الفرق، إذ تجب التسوية في الحكم، إذا قال: حرمت الخمر لشدتها، فقيسوا','3','38',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1670,'عليها كل مشتد. ولو قال: أعتقت غانمًا لسواده، فقيسوا عليه كل أسود، اقتصر العتق عند الأكثرين [على غانم]. فيا ليت شعري، كيف أجمعت الأمة على الفرق وبعضهم يسوى، ونحن ممن يسوى إذا كان الأمر على ما صور؟
وأما ما ذكره ثانيًا، من أن سبب الفرق، بأن الله تعالى التصرف في أملاك العباد، فهو صحيح، وكذلك (٦/أ) في إثبات الأحكام.
وقوله: إن أملاك العباد مقصورة على طرق مخصوصة، بخلاف الأحكام، فإنها تحصل بكل طريق دل على رضا الشارع، [فالأمر] كذلك عندنا في الأملاك من غير فرقٍ على حال.
وقوله: لو بيع مال التاجر [بمشهده]، وظهرت عليه أمارات الرضا، لم ينعقد البيع، ممنوع عندنا، والبيع قد صحَّ والملك قد انتقل. وإنما هذا الذي ذكره هو مذهب الشافعي، ويظن أن الناس يوافقونه على ذلك، والأمر بعكس','3','39',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1671,'ما تخيل، فلا تعبد عندنا في أعيان الألفاظ، [لا] باعتبار تصرفات العباد، [ولا] باعتبار ثبوت الأحكام.
وفي بعض هذه المسائل نظر فقهي لا يليق بمحض الأصول، وهو أن صريح الظهار، هل ينصرف إلى الطلاق بالنية أم لا؟ قياس ما قررناه يقتضي ذلك. والظاهر من المذهب المنع. ومعتمده أن الظهار بنص القرآن يتضمن المنع من الزوجة حتى يقع التكفير. ولو صرفناه إلى الطلاق لتضمن جواز الوطء','3','40',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1672,'من غير تكفير، وذلك تغيير للنصوص، وتعطيل للأحكام المعلومة.
قال أبو حامد: الوجه الثالث من الجواب: أن قول القائل: لا تأكل هذه الحشيشة، لأنها سُمٌّ، ولا تأكل الأهليلج فإنه مسهِّل، ولا تكلم فلانًا، فإنه سيئ الخلق، فأهل اللغة متفقون على أن فهم هذا التعليل يقتضي تعدية النهي إلى كل ما وجدت فيه العلة، وهو أيضًا مقتضاه في العتق والوكالة، لكن الشرع منع من إثبات العتق والوكالة، بناءً على فهم التعليل، ولا مانع من ذلك في فهم الأحكام، فوجب أن يستعمل فيه ما يفهمه أهل اللغة، [إذ] قد ثبت في الشرع أن كل طريق يعرفنا حكمه ثبت به، فوجب الإجراء على ذلك. قال:','3','41',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1673,'فكيف يستويان مع الإجماع على الفرق؟ وقد بينَّا أن لا إجماع في ذلك.
فإن قيل: (٥/ب) إن قال من تجب طاعته: بعْ هذه الدابة لجماحها، وبع هذا العبد لسوء خلقه، فهل يجوز للمأمور بيع ما يشاركه في العلة؟ فإن قلتم يجوز، فقد خالفتم الفقهاء، وإن منعتم، فما الفرق بينه وبين كلام الشارع؟ مع أنه لا فرق في [الموضعين]. وإن ثبت تعبُّدٌ في لفظ العتق والطلاق بحصر صرائحه، فلِمَ يثبت في لفظ الوكالة؟ قلنا: إن كان قد قال له: [أين ما] ظهر لك إرادتي ورضاي [به]، بطريق الاستدلال دون صريح اللفظ فافعله، فله أن يفعل ذلك. وهو وِزَانُ حكم الشرع. ولكن بشرط أمرٍ آخر، وهو أن يقطع','3','42',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1674,'[بأنه] أمر ببيعه لمجرد سوء [الخلق] مع القبح أو مع الخرق في الخدمة، فإنه قد يذكر بعض أوصاف العلة. فإن لم يعلم قطعًا، ولكن ظن ذلك ظنًا، فينبغي أن يكون قد (٦/ب) قال له: ظنك نازلٌ منزلة العلم في تسليطك على التصرف. فإن اجتمعت هذه الشروط، جاز التصرف، وهو نظير مسألتنا.
فإن قيل: فإن كان الشارع قد قال: ما عرفتموه بالقرائن [والإشارات] من رضاي، فهو كما عرفتموه بالصريح، فلم يقل: إني إذا ذكرت علة شيء، ذكرت تمام أوصافه. فلعله علل تحريم الخمر بشدة الخمر، وتحريم الربا بطعم البر خاصة، لا بمجرد الشدة. ولله [تعالى] أسرار في الأعيان. فقد حرم الدم والميتة والموقوذة والحمر الإنسية، وكل ذي مخلب من الطير، وكل ذي ناب من السباع، [ولم] يطلع على عللها. فلم يبعد أن يكون لشدة الخمر من','3','43',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1675,'الخاصية، ما يمنع من [شدة] النبيذ. قال: وهذا كلام واقع في مدافعة القياس.
والجواب: أن خاصية المحل، قد يعلم ضرورة سقوطها، كقوله: (أيما رجل مات أو أفلس، فصاحب المتاع أحق بمتاعه). إذ يعلم أن المرأة في معناه. وقوله: (من أعتق شركًا له في عبد قوم عليه الباقي). فالأمة في معناه، لأنا عرفنا بتفحص أحكام العتق والبيع، [وبمجموع أمارات وتكريرات ألفاظ وقرائن، أنه لا مدخل للأنوثة في العتق والبيع]. وقد يظن ذلك [ظنًا] تسكن النفس إليه.
فإذا ثبت في الشرع أن الظن كالعلم فيما يتعلق بالعمل في مسائل الفروع،','3','44',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1676,'وجب الاعتماد على ذلك، ولم يكن الاحتمال مانعًا بحال. وهذا كلام الأصوليين في الجواب عن هذا السؤال. وإنما نشأ ذلك كله من قولهم: النص على التعليل لا يقتضي في وضع اللغة تعميمًا، فكيف إذا كانت العلة مستنبطة؟ أم كيف يفرق بين كلام الشارع وكلام غيره، ومنهاج الفهم واحد؟ والجواب الصحيح عندنا في ذلك أن نبين الوجه الذي فيه يدل القياس.
فنقول والله المستعان: قد تقدم لنا قبل هذا أن الألفاظ بالإضافة إلى وضع اللغة، تدل تارة من جهة الصيغة والمنظوم، ونعني به وجدان [العبارة] المطابقة للمدلول، وهي واضحة مستغنية عن التمثيل. وقد تدل من جهة المفهوم، على ما سبق تقريره وتصويره. ونحن وإن كنا لا نقول بالمفهوم، فليس [نعمم] الرد، [لأنا قد بينا أن نقول ببعض الصور. وليس المراد ههنا دليل القبول والرد]، وإنما المراد التصوير، ومعنى المفهوم عند الأصوليين: أن يكون واضع اللغة قد قرر التلازم بين أمرين أو أمور تقترن في القيام بالنفس، إما على جهة [مخالفة]، أو على جهة [موافقة]، إما على وجه مقطوع به، أو على وجه مظنون. [ثم أنه] قد يعبر عنهما جميعًا، كما تقول: في السائمة الزكاة، ولا زكاة في المعلوفة. وقد (٦/أ) يستغنى بالتعبير','3','45',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1677,'عن أحدهما عن التعبير عن الآخر، لما [تقرر] عندنا من أنهما (٧/أ) لا يفترقان عنده في القيام بالنفس. هذا تلخيص معقول القول بالمفهوم. ومن نازع في بعض صوره، فإنما نازع، لأنه لم يثبت عنده في ذلك النوع ما ذكرناه من التلازم في القياس بالنفس. وأما بعد الاعتراف بالتلازم قيامًا بالنفس، فلا سبيل إلى المناكرة. [ألا] ترى أنه لما ثبت [بالمعقول] أن المعبر عن القرية في قوله {وسئل القرية}، لابد من أن يقوم بنفسه ذكر الأهل. ولا يتصور النزاع في هذا.
فإذا تقرر ذلك فمثبتو القياس يزعمون أن الشرع أثبت [للألفاظ] اللغوية مفهومًا ثانيًا زائدًا على المفهوم الذي يعرفه أهل اللسان. وذلك المفهوم أنه إذا أشعرنا أنه [إذا] أناط حكمًا بوصف، إما معنويًا عند من يقتصر في التعدية على المعاني، أو معنويًا وشبهيًا عند القائلين [به]، أو بأي وصف كان عند الطاردين، فقد قصد إلى إثبات الحكم في كل محل وجد فيه [ذلك] الوصف، فاستغنى بتقدير التلازم [قيامًا] بالنفس عن التعبير [عن الجميع]. كما تقرر في قاعدة المفهوم.','3','46',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1678,'وإذا تقرر ذلك، وهو إثبات [دلالة] زائدة للألفاظ في الشرع، لم تكن ثابتة عند أهل اللسان، بطل بذلك وجه السؤال، لأنهم اعتقدوا استواء دلالة الألفاظ لغة وشرعًا، وليس الأمر كذلك. لكن الشأن كله في إثبات هذه الجهة الزائدة من الدلالة. ونحن بعون الله بعد هذا الكلام نستفتح الدليل على [إثباتها]. وهذا كلام حسن بالغ في الحسن، فلينتبه له.
وقد ذهب بعض القدرية إلى إجراء القياس في المنهيات دون المأمورات، فقالوا: إذا علل الشارع وجوب فعل بعلة، فلا يقاس عليه غيره، إلا بورود التعبد بالقياس. وإن علل ترك فعل بعلة، لزم تركه، كلما توجد العلة فيه. قالوا: فمن ترك العسل لحلاوته، [لابد] أن يترك كل حلو، ومن ترك الخمر لإسكارها، لزمه ترك كل مسكر، ومن أكل طعامًا لحلاوته، لم يلزمه أن يأكل كل حلو. ومن صلى [صلاة] لأنها عبادة، لا يلزمه أن يأتي بكل عبادة. وبنوا على هذا أن التوبة لا تصح من بعض الذنوب، بل من ترك ذنبًا لكونه معصية، يلزمه ترك كل ذنب. أما من أتى بطاعة، فلا يلزمه أن يأتي بكل طاعة.
وهذا باطل في [الطرفين]، لأنه لا يبعد في جانب [الترك] أن تحرم الخمر لخاصية يعلمها [الله عز وجل]، لا نطلع نحن عليها، والله تعالى مستأثر بها. وأما في جانب الفعل [فمن] أكل طعامًا لمحبته، ولفرط شهوته، [فإنه لا يفرق بينه وبين مثله. نعم، لا يأتي أن يأكل جميع ما يشاكله، لذهاب','3','47',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1679,'الجوع وفقدان الشهوة. فما ثبت] للشيء ثبت لمثله، كان ذلك فعلًا أو تركًا. (٧/ب)
هذا كله لو سلم الجريان على مقتضى التحسين والتقبيح. وليس الأمر [كذلك]. وإثبات الحكم على طريق الاختبار. [فلله] سبحانه وتعالى أن يفعل من ذلك ما يشاء، و [لا تحكم] بالعقول عليه على حال. هذا تمام الكلام على [من] منع القول بالقياس عقلًا أو شرعًا، عامًا أو خاصًا.

وبقي علينا<span data-type="title" id=toc-293> الرد على من أوجب العمل بالقياس عقلًا</span>. وقد ذهب قوم إلى أن العقل يدل على العمل بالقياس لولا الأدلة الشرعية. واستدلوا على ذلك بأمرين.
أحدهما -أن الأنبياء عليهم السلام مأمورون بتبليغ أحكام الوقائع للخلق أجمعين، والتنصيص على الآحاد لا يمكن، فلابد من الرد إلى الاعتبار والقياس، وإلا فتتعطل الأحكام، وتخلو عن التكليف. وفي ذلك فوات صلاح الخلق، وهو محال. وهذا الذي قالوه فاسد من أوجه:
منها -أن لا نسلم أن العقل يوجب تعميم الأحكام، بل لا يوجب العقل أصل الأحكام فضلًا عن تعميمها. فلله تعالى أن [يحكم وألا يحكم] في الجميع، وأن يحكم في البعض. ف {لا يسئل عما يفعل وهم يسئلون}.','3','48',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1680,'والوجه الثاني: أنا وإن سلمنا [أنه لابد] من تعميم الأحكام، فيصح أن تثبت الأحكام بناءً على [ذكر] الروابط الكلية، فنقول عوضًا من قوله: (لا تبيعوا البر بالبر إلا مثلًا بمثل)، لا تبيعوا المقتات بالمقتات [أو] المطعوم بالمطعوم [أو] المكيل بالمكيل إلا مثلًا بمثل. فثبت الحكم على العموم. نعم، يفتقر مع هذا إلى [تحقيق] مناط ما هو مقتات، أو مطعوم، أو مكيل، وليس ذلك بقياس.
والوجه الثالث: أنه كان يصح أن يقال: أصل الأحكام أنها غير ثابتة، فيبقى الخلق على النفي، ولا يكون انتقالٌ إلا بعد القطع [بالانتقال]، أما إذا [كان] ظن ذلك، فلا يثبت الانتقال بحال. فهذا ممكن، لا تناقض فيه من جهة العقل. وقد تمسك به قوم في إبطال القياس، فقالوا: النفي الأصلي معلوم، والقياس لا يفيد إلا ظنًا، فكيف ينتقل عن المعلوم [إلى المظنون]؟ وهذا أيضًا فاسد، [وبيان فساده] من أوجه:
أحدها -أن هذا يقتضي سياقه ألا يعمل بمظنون في الشريعة، فيبطل','3','49',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1681,'العمل بأخبار الآحاد والعمومات والظواهر، وشهادة [الشهود] في فصل الخصومات.
الثاني -أن البراءة، وإن كانت معلومة قبل وورد الشرع، أما بعد الورود وإمكان التغيير، فلا تبقى معلومة.
الثالث -ذكره من قال إن كل مجتهد مصيب. قالوا: إذا وجدنا القياس والأمارة المظنونة، قطعنا بالحكم بالإجماع، فلم ننتقل عن البراءة الأصلية إلا بقاطع. والصواب عندنا (٨/أ) ما سبق من استحالة تلقي إثبات حكم أو نفيه من [العقل]. وإنما [يدار] الأمر على ورود الشرع وثبوت الأدلة [السمعية].
الشبهة الثانية: لمن قال بوجوب القياس عقلًا. قالوا: كما دل العقل على العلل العقلية ووجب إتباعها، فكذلك يجب إتباع العلل السمعية. وهذا غلط من أوجه:
أحدها -أن القوم اقتصروا في الإلحاق على محض الاشتراك في الاسم. وهذا باطل باتفاق العقلاء.
الثاني: أن العلل العقلية ممنوعة عند أكثر المتكلمين، فكيف يصح [بها] الاعتبار؟','3','50',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1682,'الثالث -أنها وإن سلمت، فهي عند مثبتيها تقتضي معلولاتها لنفسها، ويستحيل أن يفارق الموصوف وصفه النفسي، بخلاف العلة الشرعية، فإنها أمارة لا توجب الحكم لذاتها، فكيف يتصور الإلحاق [بها]؟ هذا تمام الكلام على من أحال عقلًا، ومن أوجب عقلًا، ومن منع شرعًا. [وها نحن بعون الله [تعالى] نستنتج الدليل على وقوع التعبد بالقياس].
قال الإمام: (فنقول: نحن نعلم قطعًا أن الوقائع [التي جرت فيها فتاوى علماء الصحابة]) إلى قوله (ثم يسكت عنه من يعتقد بطلانه). قال الشيخ: ما ذكره الإمام من اتساع الأحكام [وقلة] [التوقيفات] واضح،','3','51',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1683,'ولكنه غير مستقل بتحصيل المقصود، ويفتقر إلى إثبات أمرين:
أحدهما -أن تكون الأحكام المتسعة مختلفة، حتى لا يصح أن يستند إلى البراءة الأصلية، فحينئذ يفتقر إلى طريق آخر زائد على التوقيفات وعلى البراءة الأصلية. وإذا ثبت ذلك، نظر في حقيقة ذلك الزائد وشرطه. أما مجرد التمسك بقلة التوقيفات وكثرة الفتاوى، فلا يحصل هذا الغرض. ألا ترى أن أهل الظاهر مع إنكارهم القياس يسترسلون في الأحكام من (٧/أ) غير توقف، وإن كانوا لا يفتون بالقياس؟ فيفتقر [المتمسك] بهذه الطريقة أن يبين أن الأحكام الكثيرة، لم تجر على مقتضى البراءة الأصلية. فهذا وجه، وإثباته سهلٌ على الفقيه العالم بأقضيتهم وفتاويهم.
والأمر الثاني -أنه يبين إجماعهم على زيادة الأحكام على النصوص، مع إثبات الاختلاف، وهذا أيضًا شديد.
وقول الإمام: إنه قد تواتر عنهم أنهم كانوا يطلبون حكم الواقعة أولًا من الكتاب، ثم من السنة، ثم من الرأي. عنه جوابان:','3','52',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1684,'أحدهما -أن هذا الترتيب غير صحيح عند الأصوليين، فإنه قد يتقدم بعض السنة على بعض الكتاب. وكذلك قد يتقدم بعض القياس على بعض الكتاب والسنة. وهو القياس المعلوم إذا عارضه ظاهر كتاب، أو [ظاهر] (٨/ب) سنة، أو نص سنة، إذا كانت آحادًا. فإن القياس يتقدم في هذه الصورة على القسمين.
والسبب فيه أن القياس على الشرط المعلوم يحصل علمًا، وهذه الأسباب لا يترتب عليها إلا ظن، فلزم تقديم القياس عليها. وأما الإجماع الحاصل [عن] الفتوى والسكوت، فقد تقدم كلامنا على الإجماع، هل يصح أن يستند إلى [قول] البعض وسكوت البعض؟ وقد تكلمنا عليه، وبينا انقسام حاله [في المقطوع] فيه بالحكم والمظنون، بما فيه كفاية وبلاغ، فليطلبه طالبه في موضعه هناك. فإذًا لا يصح التمسك بالقياس [مقطوعًا] [به] إلا على أحد وجهين:','3','53',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1685,'إما أن يثبت النقل المتواتر عن كل واحد من أهل الإجماع بإسناد الحكم إلى القياس.
أو يتبين باستقراء أحوالهم رضا الساكت بما فعله المفتي أو الحاكم، فبذلك يتم الإجماع، فأما الاكتفاء بمطلق السكوت، فلا يتم به الإجماع عندي أصلًا. نعم، ذلك [مما] يغلب على الظن، فأما الانتهاء إلى حد يحصل العلم فلا.
وما ذكره [الإمام] من إحالة العادة السكوت على من [يُحدِثُ] قاعدة في الدين يسند إليها معظم الأحكام لا يتصور. قد بينا ذلك إنما تحيله العادة على العدد الكثير الذين يمتنع عليهم أن يتواطئوا على الكتمان. فأما العدد القليل، فلا تحيل العادة ذلك عليهم، وإن كان بعيدًا.
والفرق بين العلم والاعتقاد شديد. وهذه القضية على الحقيقة إنما يعلمها المجتهدون.','3','54',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1686,'وأما من لم يبلغ إلى هذه الدرجة، فلا يتأتى له أن تنحصر الأدلة السمعية عنده، ولا يدري أيضًا تفاصيل الوقائع التي أفتى فيها المتقدمون. وهذا الطريق يفتقر إلى ضبط التوقيفات بجملتها، ثم عرض المسائل المفصلة عليها [حتى] يعرف قصورها عنها أو اتساعها لها.
ولقد نقل عن بعض علماء التابعين أنه بقي دهرًا طويلًا لا يفتي مستندًا إلى رأيه بحال. بل إنما يحكم بناء على التوقيف. هذا طريق الإنصاف عندي. وقد نقل الأصوليون آحاد وقائع مستدلين بها على القول بالرأي، أضرب الإمام عن ذكرها لكثرتها.
منها: أن الصحابة - رضي الله عنهم - صاروا إلى الاجتهاد في تعيين أبي بكر [- رضي الله عنه -] للخلافة، مع أنه لا نص فيه. إذ لو كان فيه نص لم يكتم يوم السقيفة، وقد قال أبو بكر: ([قد] رضيت لكم أحد هذين الرجلين). يعني: عمر بن','3','55',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1687,'الخطاب وأبا عبيدة بن الجراح. فهذا حكم بالرأي والاجتهاد. وكذلك عهد أبو بكر إلى عمر، ولم يعقد له. إذ العقد إنما يكون بعد الموت، لامتناع (٧/ب) خليفتين. فقاسوا العهد على (٩/أ) العقد. فكما لزم العقد من الأمة، لزم العهد من الخليفة الناظر لهم. وجعل عمر - رضي الله عنه - الخلافة شورى في ستة، ولم [يَعْدُهُم] الناس إلى غيرهم.
وما جاء عن أبي بكر - رضي الله عنه - أنه قال في [الكلالة]: (أقول فيها برأيي،','3','56',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1688,'فإن يكن صوابًا، فمن الله، وإن يكن خطأً، فمني ومن الشيطان. الكلالة: من لا ولد له ولا والد).
وما جاء عن عمر - رضي الله عنه - في الجدة -أم الاب، [حيث] جاءت تطلب ميراثها، فقال لها: (مالك في كتاب الله [من] شيء، وما علمت لك في سنة رسول الله - صلى الله عليه وسلم -[شيئًا]، وما كان القضاء الذي قضى به إلا لغيرك). وقد قيل: إنه أراد منعها، فقال له رجل: (كيف تورِّث التي لو ماتت وتركت الدنيا كلها لم يرثها، وتترك التي لو ماتت ورثها؟ فقال: (لست بزائد في الفرائض شيئًا، ولكن هو ذاك السدس، فإن اجتمعتما، فهو لكما، [وأيتكما] خلت به، فهو لها). وهذا قياس محض.','3','57',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1689,'وقال عمر - رضي الله عنه - لما بلغه أن سمرة أخذ الخمر من المجوس وباعها: (قاتل الله سمرة، ألم يقل رسول الله - صلى الله عليه وسلم -: (لعن الله اليهود حرمت عليهم الشحوم [فجملوها] فباعوها وأكلوا أثمانها)؟
وسئل ابن مسعود عن المفوضة فمكث شهرًا، ثم قال: (أقول فيها برأيي، فإن يكن صوابًا فمن الله، وإن يكن خطأ فمني ومن الشيطان، والله ورسوله منه بريئان، [أرى] لها مهر نسائها، لا وكس فيه ولا شطط).
وقال ابن عباس لما بلغه أن بعض الصحابة فرق بين دية الأسنان ولم يجعلها متساوية: (هلا اعتبرها بالأصابع).','3','58',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1690,'ولما سأل عمر عن القبلة للصائم، فقال له رسول الله - صلى الله عليه وسلم -: (أرأيت لو تمضمضت بماء ثم مججته).
وقال [- صلى الله عليه وسلم -] للتي سألته عن حج أبيها، وإمكان قضائه عنه: (أرأيت لو كان على أبيك دَيْنٌ أكنت قاضيته؟ قالت: نعم. فقال: فدين الله أحق بالقضاء). وفتوى الصحابة في مسائل الجد والإخوة بأقضية كلها راجعة إلى الرأي والاجتهاد، مع انتفاء التوقيف.
وتسوية أبي بكر في العطاء يستند إلى الرأي، فقال له عمر: (كيف تسوي بين الفاضل والمفضول؟ فقال: إنما أسلموا لله وحسابهم على الله). فلما انتهت','3','59',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1691,'[النوبة] إلى عمر فضل، ورأى عمر [أن] التفضيل بأصل الاستحقاق، فإذا استحقوا بالإسلام، جاز أن يتفاضلوا بتفاضلهم فيه. ورجعوا إلى جمع المصحف، ولم يكن مجموعًا في زمن رسول الله - صلى الله عليه وسلم -، بناءً على الرأي والعمل بالمصالح، ورتبه عثمان في زمانه، وأبطل ما سوى هذا المصحف الذي بين أيدي الناس، عند حدوث الاختلاف وخوف الفتنة.
وقياسم الشرب (٩/ب) على الافتراء في الحد. وهو قياس في غاية البعد، إذ قال [علي]: (من شرب سكر، ومن سكر هذى، ومن هذى افترى، فأرى عليه حد المفتري). بناء منهم على أن الشرع يقيم مظنة الشيء مقامه في بعض الأمور. كما أقام مغيب الحشفة مقام الإنزال في الغسل وغيره. وتفاصيل هذه الوقائع أكثر من أن تحصى.
بل لم يعهد القراض في زمن رسول الله - صلى الله عليه وسلم -، وإنما حدث هذا الضرب','3','60',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1692,'من المعاملة في زمن عمر - رضي الله عنه -. وقد جرت أحكامه وكثرت مسائله، مع فقدان التوقيف في أصل (٨/أ) القاعدة، فكيف في تفاصيلها؟ .
وعلى الجملة، فمن أنصف من نفسه وأحاط علمًا بسيرهم، لم يسترب في رجوعهم إلى الرأي وعدم اقتصارهم على [طلب] التوقيف في آحاد الوقائع. فإذا ثبتت مجاوزة التوقيف، بقي النظر في ضبط ما تجاوزوا إليه. وسنتكلم عليه بعد هذا، إن شاء الله.
ومجامع ما يعترض به على هذا التقرير خمسة أوجه: أحدها- إنكار كون الإجماع حجة، وهو مذهب النظام. وقد فرغنا من الرد عليه. وتارة بإنكار تمام الإجماع في القياس، من جهة أن الذي ذكرناه، إنما هو عن البعض، ولم ينقل عن الباقين إلا السكوت. وقد نقلوا الخلاف عن البعض. وتارة يسلمون السكوت، ولكنه يحملونه على المجاملة في ترك الاعتراض على الموافقة. وتارة يقرون بالإجماع، ولا يكثرون بتفسيق الصحابة، وتارة يحملون فتاويهم على التوقيف دون الرأي. فأما ما يتعلق بكون الإجماع حجة، وتفسيق','3','61',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1693,'الصحابة، فقد سبق الكلام عليه في كتاب الإجماع. وأما حمل السكوت على المجاملة، فقد تقدم الكلام على الصحيح في ذلك.
قال الإمام: (فإن [قيل]: بما تنكرون على من يزعم أنهم كانوا يتلقون','3','62',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1694,'الأحكام) إلى قوله (فلا حجة إذًا في قوله). قال الشيخ: الكلام على هذا الاعتراض ينحصر عندي في وجه واحد، وهو أنا إذا قبلنا الرأي وجوزنا استناد الأحكام إلى الاجتهاد من غير توقيف، لم نعمِّم القول في جواز الاعتماد على كل رأي، ولم يصر إلى ذلك أحد. وسنردُّ على الطاردين بعد هذا. والراد','3','63',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1695,'(١٠/أ) [للقياس] لا يقبل منه [شيئًا] على الحال، [والنافي] يعمم، والذي نقلناه في الإثبات يفيد تعميم الرد، والقابل لا يعمم القبول، بل يشترط فيه شروطًا. ومحال [تنزيل] ما ردوه من الرأي على ما [اختلفت]','3','64',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1696,'الشروط فيه. وأنكروا على من يقول بالرأي، وليس من أهله. كما قالت عائشة [رضي الله عنها] لأبي سلمة: (فروج يصرخ مع الديكة). فبهذا التقريب يندرج الاعتراض ويتوجه الدليل. والحمد لله على كل حال. ولهذا قال ابن مسعود: (لو عملتم بالرأي لأحللتم ما حرم الله .. ) إلى آخره. ونحن نعترف بفساد القياس إذا خالف النص، فمن أين يلزم ردُّ الرأي على الإطلاق؟','3','65',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1697,'قال الإمام: ([واحتج] الشافعي [رحمه الله] ابتداءً بحديث معاذ بن جبل [- رضي الله عنه -]) إلى قوله ([في تفصيل] [ما يُرَدُّ ويُقبل] من النظر). قال الشيخ: هذا الاستدلال الذي استدل به الشافعي على القياس بحديث معاذ، لا يصح الاستدلال به من وجوه:
أحدها -أن الخبر غير صحيح. وقول الإمام: إن الخبر مدونٌ في الصحاح، ليس على ما قال، فإن الكتب الصحاح في عرف الناس (البخاري) و (مسلم)، وقد يطلق على (الموطأ) أنه من الصحاح. وليس الحديث في','3','66',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1698,'شيء من هذه الكتب الثلاثة. نعم، ذكره الترمذي، وقال: إنه مرسل، لأنه روي عن بعض أهل حمص من أصحاب معاذ. فلم يثبت له الإسناد المتصل، فضلًا عن أن يكون صحيحًا متفقًا على صحته.
ثم لو قدرنا صحته، فهو خبر واحد، فلا يسوغ الاستدلال به في القطعيات. ومن يثبت القياس يخطئ جاحده ويضلله ويبدعه، ويمنعه بعضهم (٨/ب) درجة الاجتهاد. فكيف يصح أن يستند مثل هذا الأصل إلى أخبار الآحاد؟
ولو صح ذلك لصح إسناد الإجماع إليه. فيا ليت شعري، كيف أنكر الإمام الاستدلال على صحة الإجماع بقوله عليه السلام: (لا تجمته أمتي على الخطأ). فإنه قال: الخبر خبر واحد، والمطلوب [القطع]. [وهذا] بعينه جارٍ ههنا. فلو صح إثبات القواعد بالأسباب المظنونة، لصح ذلك في كل أصل، ولم يتناقض الأمر، فيقطع الحاكم بالحكم مع كونه يظن صحة المستند، وذلك محال.','3','67',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1699,'وأما قوله: إذا كان القياس مغزاه العمل، والدال عليه دال على العمل. فلا فرق أن يستند إلى القاطع بواسطة أو بغير واسطة، هذا بعينه ينتقض بالإجماع. ثم كان يلزمه أن يطرد هذا في كل أمارة، فيقطع بالحكم في كل موضع روي فيه خبر واحد، إذ الخبر يستند إلى الإجماع، فلا فرق بين أن يستند الحكم في مس الذكر مثلًا إلى قوله (١٠/ب): (من مس ذكره فليتوضأ). وبين أن يستند إلى الإجماع. وهذا غلط لا شك فيه.
وأما قوله: إن الصديق - رضي الله عنه - أو غيره من أئمة الصحابة، لو روى [أن] العمل بالقياس في الواقعة التي لا نص فيها واجب، لابتدره الصحابة بالقبول. تهويل بذكر [الصديق] مع بعد الكلام عن التحقيق، فإنه لا يلزم من رد [القول] اتهام [القائل]. قد يكون الاتهام بسبب الرد، وقد يكون','3','68',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1700,'غيره. نعم، كل موضع يكون الرد بسبب التهمة، فهذا لا يتطرق إلى الصديق، ولا لغيره، ممن ثبتت عدالته.
وأما الرد بسبب تعبدات الشرع، أو اشترط العلم في الحكم، فلا يكون الرد في هذه الصورة نقص من منصب الصديق ولا غيره. ألا ترى أن الصديق لو شهد بسرقة على انفرادٍ، لم يقطع السارق، ولا يكون في ذلك غظٌّ من منصبه، بل التوقف، لحصول [الشرط] الشرعي. فإذا كان هذا قوله في موضع يكتفى فيه بغلبة الظن، فما الحال إذا كنا نشترط في المحل العلم حتى يقطع بالحكم؟ كيف يتصور مع هذا أن يثبت بقول الواحد؟ هذا باطل لا خفاء به. فلا مستند في المسألة [إلا] الإجماع، منقولًا متواترًا عن جميع الصحابة، إما تصريحًا، وإما تلويحًا يفهم الرضا منه عن الحاكمين بالرأي.
والذي ننبه عليه أن من أراد تحصيل [علم] هذه المسألة من كلام الأصوليين، فليس على بصيرة. [فإن] قصارى كلامهم -لو صح -أن يتنزل منزلة نقل المحدثين العارفين بالنقل، وذلك إنما يحصل غلبة الظن بالإجماع. والمطلوب العلم، فلابد على هذا لمن أراد أن يعلم المسألة أن ينقل عن عدد','3','69',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1701,'التواتر بشرط استواء الأطراف والوسائط إلى أن تتصل بعصر الصحابة، فيثبت النقل عن جميعهم. فهذا شرط حصول العلم بهذه القاعدة ونظائرها من أخبار الآحاد وغيرها. والله [المستعان].
ونقول: مما [يتصل] بالكلام على الرادين للقياس، الرد على النظام، فإنه من الرادين للقياس، ولكنه ذهب إلى أن العلة المنصوصة توجب الإلحاق دون المستنبطة. وزعم أن ذلك بمقتضى اللسان ووضع اللغة وشمول اللفظ، لا بطريق الاعتبار والإلحاق، إذ لا فرق في اللغة بين أن يقول: حرمت كل مشتد، وبين قوله: حرمت الخمر لشدتها، في أنه يقتضي تحريم النبيذ المشتد. وهذا خطأٌ بيِّن، فإن الخمر [إذا] ثبت اختصاص لفظها في وضع اللسان بالمعتصر (٩/أ) من العنب دون غيره، فالتحريم متقصرٌ عليها، وشدتها مختصة بها، فالاسم قاصر، [والعلة] مختصة. فيكف يكون اللفظ عامًا، (١١/أ) وهو لو لم يعلل لكان خاصًا؟ فتعليله لا يوجب تعميمه بحال.','3','70',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1702,'هذا صريح وضع اللغة، إلا أن يكون النظام يقول [إن] فهم التعليل عند أهل اللسان، يفهم التعميم بناءً على المفهوم. فهذا له وجه. وقد كنا قلنا في منع إثبات [اللغة] بالقياس: إنهم يضعون للاسم مناطًا لمعنى، ثم يقصرونه على المحل، كما يسمون الفرس أدهما لسواده، وكميتًا لحمرته، والإنسان المتلون بذلك اللون لا يطلق عليه ذلك الاسم، وإن فهم [علة] التسمية. وكذلك هذا يطرد في فهم الأحكام المعللة بالإضافة إلى وضع اللغة دون وضع [الشرع].
و[إن] رد النظام الكلام إلى المصارح والمفاسد، فهذا أمرٌ زائد على اللفظ، يقتضي التسوية. وهو أيضًا باطل، إذ يعلم الله تعالى مصلحة في تحريم الخمر لشدتها، ويعلم في النبيذ خلاف ذلك. فلا سبيل إلى هذا القول بحال.
فإن قيل: قول السيد والوالد لولده أو لعبده: لا تأكل هذه الحشيشة لأنها سم، وكل هذا، لأنه غذاء، يفهم المعنى من أكل كل سم، والأمر بتناول ما يغذي. ولا يفهم العقلاء من هذا، الاختصاص على حال. قلنا: ليس ذلك من مقتضى النص على تلك الحشيشة على انفرادها، ولكن ما أظهره الحذار من موافقة الضرار، هو المفهوم لذلك. ولولا هذه القرينة لم يفهم النهي بحال. فإن قضية الأبوة تقتضي إشفاقًا مطلقًا، وكذلك قصد الملاك إلى حفظ الأملاك. هذا','3','71',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1703,'هو السبب في الفهم دون الاقتصار على محض اللفظ. ولو صح تنزيل فعل الآباء والسادات على التحكمات [الجامدات]، لم يفهم من هذه الألفاظ التعميم. والله تعالى إذا حرم شيئًا، فإنما يرجع الأمر إلى إرادته ووضعه، فليقتصر على ما يفهم من الأدلة.
فإن قيل: إن لم يفهم تحريم النبيذ من الخمر، فينبغي أن لا يفهم تحريم الضرب من التأفيف، وفهمه منه ضرورة. قلنا: قد اختلف الأصوليون في ذلك، فذهب ذاهبون إلى أنه إنما يفهم بطريق القياس والاعتبار. وذهب ذاهبون إلى أنه فهم من السياق والقرائن. وذهب ذاهبون إلى أن هذا فهم من عرف الاستعمال. [فإنه] قد صار بعرف الاستعمال قول القائل: لا تقل [له] [أف]، عبارة عن نفي الأذى على الإطلاق.
وقال قائلون: فهم من باب [إلحاق] الأدنى بالأعلى. وعلى الجملة [فالفرق] متفقةٌ على أن مجرد قول القائل: [لا تقل له أف]، لا يقتضي منع الضرب، بناءً على أن التأفيف صار عبارة عن نفي كل أذى بحال. وإنما النظر هل صار دالًا على ذلك بعرف الاستعمال، أو بقرينة الحال، أو بطريق','3','72',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1704,'الاعتبار؟ فيه نظر. والصحيح عندنا أنه إنما فهم بالنظر إلى السياق، (١١/ب) وإلا فقد يقول الملك في حق أخيه المنازع له في الملك للسياف: لا تقل له أف واضرب عنقه. فلو كان عبارة عن نفي [الإيذاء] المطلق، لتناقض القول. ولو افتقر إلى استنباط وتأمل، وكان على أبواب القياس، لرده من يرد القياس، ولافتقر في فهمه إلى ورود التعبد بالقياس. وكلا الوجهين باطل. فلم يبق إلا أنه استند إلى فهم سياق الآية، فإنه قال: {وبالوالدين إحسانًا}. إلى آخره. فأفهمت الآية فهمًا ضروريًا [أن] المقصود: الإكرام التام، والضرب يناقض ذلك من غير إشكال.
أما تحريم [النبيذ بتحريم] الخمر، فليس من هذا القبيل. (٩/ب) [ولا مستند له إلا القياس. وقد قال الأستاذ أبو إسحاق مثل هذا القول على غير هذا الوجه، فقال: إذا نص الشرع على العلة علة وجهٍ لا يقبل أصل النصب تأويلًا، فلابد من أن يعلم الحكم، إذ لو اختص الحكم، ولجب أن تختص العلة، ووضع التعليل يناقضه الاختصاص. وهذا إنما قاله الأستاذ لكثرة ممارسة المعقولات، وأن العلة العقلية لا يتصور تخصيصها بحال، فأحرى هذا في السمعية. وليس لأنه يرى النص على التعليل نصًا على التعميم، ولكن هذا عنده من ضرورة فهم التعليل، وهو يمنع النص على التعليل مع النص على التخصيص. وسيأتي','3','73',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1705,'الكلام عليه بعد هذا. وإنما أوردنا كلامه ههنا لنبين أن حكمه موافق حكم النظام، وطريقه يخالف طريقه.
ثم قال: (مسألة: ذهب القاساني والنهرواني) إلى آخرها. فنقول: الصحيح من مذهب هؤلاء القوم، أنهم يقولون بالقياس، ولكنهم لا يرون','3','74',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1706,'استنباط المناط أصلًا. وإذا ثبت عندهم بالتوقيف نصب المناط، اتبعوه حيث وجوده، وكأنهم وافقوا في أصل القياس، وحصروا طريق إثبات العلة على النص، وليس يقتصر عليه. ولذلك قالوا: إذا كانت العلة منصوصة كان الحكم في الفرع معلومًا، وأَمِنَ الناظر من الغلط. وإذا كانت مستنبطة، لم يأمن من ذلك. قلنا: هذا خطأ في الجهتين جميعًا، حيث اعتقدتم أن العلة إذا تُلقيت','3','75',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1707,'من التوقيف، كان الحكم معلومًا من الفرع، وإذا استنبطت أمكن الخطأ. أما الأول، فكيف نؤمن الغلط عند إيماء الألفاظ إلى التعليل، والظاهر يقبل التأويل؟ ولو قدرنا أن التعليل يثبت بالنص الذي لا يقبل التأويل، لكان القصر على المحل ممكنًا. وقد رددنا على من يتخيل أن النص على التعليل نص على التعميم. فإذا قال: حرمت الخمر لشدتها، أمكن أن يكون ذلك لشدة الخمر على الخصوص، إلا أن يصرح (١٢/أ) ويقول: وكذلك الشدة في كل مسكر، فيكون هذا تعميمًا للحكم بطريق اللفظ، لا بطريق القياس.','3','76',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1708,'وأما قولهم: إنه عند الاستنباط يمكن الغلط في حكم الفرع، وإمكان الغلط يستقل مانعًا. فهذا لا يستقيم على قول من يصوب كل مجتهد، لأن الحكم عنده معلوم، إذا ظن شهادة الأصل للفرع، فلا يمكن الخطأ فيه.
وأما من قال المصيب واحد، فلعمري إن الخطأ ممكن، ولكن لا يمنع الحكم في مسائل الظنون، وذلك كالعمل بخبر الواحد وشهادة الاثنين، وظن استقبال القبلة عند إشكال الجهة. والقوم [لم] يشترطوا القطع بالتعليل، بل اكتفوا بالظن، وعند الظن يمكن الخطأ، فينبغي ألا يعملوا إلا بقاطع في الشريعة. ثم إن الدليل الدال على وجوب العمل بالقياس، استقراء أحكام','3','77',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1709,'الماضين، ووجود الكثرة وقلة التوقيفات. وهذا بعينه موجود. وإن أثبتوا هذه الأضربة التي ذكروها، فإن ما أشعرت النصوص بتعليله قليل جدًا.
وأما ما ذكره أبو هاشم، فليس من أبواب القياس بسبيل، وإنما يرجع ذلك إلى تحقيق المناط، إذ قد ثبت بالنص وجوب استقبال القبلة، ولم يعين جهتها، فالمكلف مطلوب منه الاجتهاد في تعيين الجهة، وكذلك ثبت المِثْلُ في جزاء الصيد بالنص، ولم يعلمنا الباري سبحانه وتعالى آحاد الأمثال، فعلمنا أنَّا','3','78',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1710,'مردودون إلى الظن في ذلك. فهذا الكلام لا تعلق له بالقياس بحال. وإنما يرد على دخوله الاجتهاد في الأحكام من حيث الجملة.
ثم قال: (فإن قالوا: أنتم لا تصححون أيضًا كل نظر) إلى آخر المسألة. فنقول: هذا الكلام صحيح، وقد بينا وجه ذلك فيما سبق. وإذا وقع الاعتراض بالعلة التي دلت النصوص عليها، واستقر من إجماع الصحابة إقامة المستنبطة مقام المنطوق بها، وجب اتباعها بعد إقامة الدليل عليها. ونحن لا','3','79',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1711,'نجوز التحكم بنصب العلم على حال. فإن وجد الدليل الدال على النصب، فلا معنى للفصل. وإن فقد الدليل على التعليل، فلا سبيل إلى التعدية والتعميم. هذا هو التحقيق المغني عن الجملة والتفصيل. وما من طريق نذكره إن شاء الله تعالى، أنه صالح للدلالة على نصب العلة، إلا ونقرر وجهه على حسب الإمكان. والله المستعان.

ثم قال: <span data-type="title" id=toc-297>(القول في تقاسيم النظر الشرعي) </span>إلى آخره. فنقول: ما ذكره الإمام من تقاسيم النظر الشرعي، إنما ذكر هذه القسمة، بناء على ما اختاره، لا','3','80',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1712,'بمعنى أن هذه القسمة ثابتة عند جميع القائسين. بل من القائسين من زاد عليها، كالطاردين، (١٢/ب) ومنهم من نقص منها، كمن أنكر قياس الشبه الغريب، وإن استنبط من أصل معين، [ومنكروا] الاستدلال. وإنما هذا الذي ذكره هو انقسام المقبول عنده. وقد أخل هو ههنا بذكر المطرد المنعكس، ولكنه عنده يرجع إلى قياس الشبه، وإن كان بعض الناس يرى أن المطرد المنعكس يلتحق بأبواب الإيماء. ولذلك لم يذكر ههنا قياس الدلالة، لأنه أيضًا عنده','3','81',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1713,'يرجع إلى المعنى تارة، وإلى الشبه أخرى، فلم ير عده لذلك.
وأما تقاسيم ما يعلم من الأقيسة وما يظن، فلا يرشد العقل إلى ذلك أصلًا، وإنما يرشد إليه استقراء الشريعة، ومعرفة قصد الشارع إلى المعنى المجرد. والعلم بانتفاء الفارق بين الأصل والفرع، مع القطع بحصر الفوارق، والالتفات إلى عادة الشرع في المقبول والمردود. هذا الطرق المفضي إلى ذلك.
وقد يعلم بالضرورة من الشريعة الإعراض عن الفروق، والقصد إلى بعض المعاني، وقد يعلم ذلك بنوع تأمل، وقد يذكر ذلك ظنًا في القسمين جميعًا.','3','82',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1714,'والنظر إلى آحاد المسائل، ليس من علم الأصول، وإنما تذكر الآحاد لضرب الأمثال. فالمثال الذي ذكرناه في البول في الماء الدائم مع صب البول فيه، [علم] ضرورة، وسريان العتق في الأمة بتشطير الحد على العبد، يعلم نظرًا.
وقول الإمام: إنه إذا وقع الاشتراك في الاسم الذي منه الاشتقاق، كان أقرب مع قوله: وقد يقال [عبد] للأمة. لا يقال للأمة على انفرادها عبد،','3','83',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1715,'وإنما يتناولها الاسم تبعًا مع الذكور. وإن كان الاسم يتناولها على الانفراد، فلا معنى للإلحاق في ذلك، إذ يتلقى من نفس اللفظ. فهو إن التفت في الوضوح إلى تناول الاسم، بطل باب القياس، وإن التفت إلى القياس بطل باب الإشعار. اللهم إلا أن يقول: إن اللفظ إذا أشعر بالعلة]، ووجدت في الفرع، كان الإدراك أسرع. فهذا قد يكون له وجه، ولكن إذا علمت العلة مقترنة بسماع اللفظ [بالإدراك] لحكم الفرع على ذلك النحو، فلا يكون أقرب بحال.
وأما خروج أهل الظاهر عن أن يكونوا من أهل الإجماع، فهذا غير صحيح عندنا على الإطلاق، بل إذا كانت المسألة المنظور فيها مما يتعلق بالآثار والتوقيف واللفظ اللغوي، وليس للقياس [فيها] مجال، فلا يصح أن ينعقد الإجماع دونهم، إلا على رأي من يرى أن الاجتهاد قضية واحدة لا تتجزأ. فعلى هذا يخرجون من أهل الإجماع بلا إشكال. وأما على القول بالتجزؤ، فلا يمنع أن يقع النظر في نوع هم فيه محقون، كما لو وقع النظر في مسألة كلامية، [فإن] (١٣/أ) للمتكلمين مدخل فيها. وكذلك في مسألة أصولية، فللأصوليين [مدخل فيها]. وكذلك أهل الظاهر في غير [المسائل] القياسية، يعتد بخلافهم ووفاقهم.','3','84',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1716,'وأما الإلحاق في مسألة الربا، [فالظاهر] ما قاله القاضي، والزبيب غالب على بلاد، كما أن التمر غالب على بلاد، فهو مثله في التفكه وغيره.
وتحقيق هذا ليس من [علم الأصول].

قال الإمام: <span data-type="title" id=toc-300>(مسألة: ما علم قطعًا بهذه الجهات التحاقه [بالمنصوص]</span>','3','85',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1717,'[عليه]) إلى قوله: ([فهذا] القدر كافٍ في توطئة الكلام في هذا القسم). قال الشيخ: اختلف الأصوليون في السبب الذي لأجله امتنع من تسمية هذا النوع قياسًا. [وظاهر] كلام الإمام أن سبب الاختلاف كونه معلومًا. وعلى هذا الرأي، إنما يطلق لفظ القياس على المظنون دون المقطوع به. وهذا إذا كان المعلوم لا يفتقر إلى نظر وتأمل. على هذا ينزل كلامه. فأما','3','86',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1718,'المصير إلى أنه لا يطلق [لفظ] القياس على المعلوم بحال، فذلك غير صحيح، فإن العلماء متفقون على أن القياس ينقسم إلى مقطوع به ومظنون. هذا تأويل ظاهر لفظ الإمام. وعلى هذا الاصطلاح، لا فرق بين أن يكون الإلحاق لأجل فهم [العلة]، أو لأجل عدم الفرق على وجه مقطوع به.
وذهب أبو حامد إلى أن [سبب] الاختلاف مختص بما إذا كان طريق الإلحاق نفي الفارق، وعلله بأن قال: القياس في وضع اللغة: هو الاعتبار، وللاعتبار طريقان: أحدهما -بتعيين الجامع. والثاني -نفي الفارق. فإن كان طريق الاعتبار بتعيين الجامع، فهو قياسٌ [بالاتفاق]. وإن كان بنفي','3','87',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1719,'الفارق، فهذا موضع الاختلاف. لأن المقصود إليه في أول الأمر نفي الفارق، فيأتي الاتفاق في الجامع ثاني رتبة، فيحصل اعتبارٌ ضمني، فلا يطلق اسم القياس عليه، بل إنما يطلق اسم القياس إلى ما قصد فيه [إلى] التسوية، وهذا قصد فيه إلى نفي [الفارق]. والفريقان متفقان على أنه لا يتلقى من وضع اللغة، بل فهم [مستندًا] إلى اللفظ، من غير أن يكون اللفظ متناولًا له. والصواب أن يعد قياسًا، إذ الأحكام إما أن تستند إلى التوقيف، أو إلى الاعتبار والاستدلال.
وما قرره الإمام من صحة الفصل في الحكم بين الذكر والأنثى، صحيح، والتنصيص على الفرق -[إذا وجد]-لا يكون تناقضًا. فتحقق بذلك أن نفس اللفظ لا يكتفى به في هذا.
قال الإمام: (والقسم الثاني من أقسام النظر الشرعي) [إلى قوله (واختيار','3','88',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1720,'مسلك الحق فيه]). قال الشيخ: قوله: القسم الثاني من أقسام النظر الشرعي، (١٠/أ) يعني بذلك (١٣/ب) المظنون من الأقسية، وليس يقتصر هذا القسم على استنباط المعاني [المخيلة]. وإنما ذكره، [لأنه] الأمر الأعم الأغلب في هذا القسم، والشبه والطرد والاستدلال المرسل مرتب عليه. وعليه أيضًا ينبني رد الطرد عند الأصوليين، فلذلك ذكره مقتصرًا على ذكره في هذا الموضوع، وإلا فالأقيسة المظنونة [أوسع] من هذا بلا إشكال.

قال الإمام: <span data-type="title" id=toc-302>([مسألة]: الطرد: هو الذي لا يناسب [الحكم</span>، ولا يشعر','3','89',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1721,'به) إلى قوله (وهذا القدر فيه بلاغ ومقنع في الرد على أصحاب الطرد]). قال الشيخ: هذه المسألة غامضة جدًا، شديدة الغموض، وقد اضطرب العلماء فيها اضطرابًا كثيرًا. وسبب الغموض أن الأوصاف قسمان: قسم [تظهر] مناسبته للفعل، وتلوح في ترتيب الحكم [عليه] مصلحة. وهذا هو الذي يعبر عنه بالمخيل المناسب. وقسم لا تظهر فيه مصلحة، ولا يلوح فيه مناسبة للحكم بحال. والذين اكتفوا بظهور المصالح -على ما سيأتي الكلام [في] ذلك -','3','90',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1722,'اختلفوا في هذا القسم، فذهب ذاهبون إلى رده على الإطلاق، وهؤلاء هم الذين يردون الشبه. والقاضي في أكثر أجوبته يميل إلى هذا، ولا يعترف بفرقٍ بين الطرد والشبه.
وذهب ذاهبون من الفقهاء والأصوليين إلى قبول هذا القسم، ولا يضر عندهم عدم المناسبة، لكن بشرط الجريان والسلامة وعدم الأولى. وهذا اختيار أبي حامد في (شفاء الغليل).
وذهب ذاهبون إلى الفرق في هذا القسم، فقالوا: يقبل منه شيء، ويرد منه شيء، وعبروا عن المردود بالطرد، والمقبول بالشبه. وهذا مذهب الإمام،','3','91',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1723,'والقاضي في أحد قوليه، وأبي حامد في آخر عمره.
وأما الذين قبلوا القسم بكماله، فإنهم قالوا: الشبه مقبول. على ما سيأتي الكلام عليه وتقرير بالدليل. ولم [يدرك فرق] بينه وبين المعبر عنه بالطرد، فوجب قبول الجميع.
وتمسك الرادون للشبه: بأن بعض الأوصاف لا يصح التمسك بها قطعًا كقولهم: [مائع لا تبنى القنطرة على جنسه، فلا تزال النجاسة به كالدهن. وكقول القائل: طويل] مشقوق فلا ينتقض الوضوء بمسه كالبوق. إلى ما','3','92',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1724,'يضاهي ذلك من الأوصاف التي تشمئز النفوس من سماعها. قالوا: ولا سبب للرد إلا أنه لا مناسبة فيه. وما عبر عنه بالشبه على هذا الحد، فإن ظهر منه فقهٌ، فهو المناسب، وهو مقبول. قالوا: وإذا وجب الرد في البعض، ولم يدرك فرق، لزم الرد في الجميع.
وأما الإمام، فإنه قد فصل، فقبل بعضًا ورد بعضًا. وهذا الفرق','3','93',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1725,'صعبٌ، والتعبير عنه عسر، ولكن نعلم [بالضرورة] (١٤/أ) أن بعض الأوصاف لا يصح الاعتماد عليها بحال، لا بالنظر إلى ذوات الأوصاف ومعقولها، فإن العقل يجوز أن يرد الشارع بأعمال الطرديات وإبطال المناسبات. ولكن يعرف بطلان بعض الأوصاف باستقراء الشرع، وإعراضه عن بعضها. وإنما يعتبر الوصف، بناء على التفات الشريعة إليه في بناء الأحكام عليه. فإذا ظهر في فن من الأوصاف الإضراب عنه على الإطلاق، فكيف يصح الالتفات إليه، وقد عرف من الشريعة الإعراض عنه؟ وقد يصح أن يكون الوصف طرديًا بالإضافة إلى بضع الأحكام، [ومناسبًا] باعتبار بعضها، أو شبهًا. وهذا بمثابة الأنوثة فيما يتعلق بالخلافة والقضاء، مناسبةُ للمنع، وطردٌ بالإضافة إلى ما يتعلق بتشطير (١٠/ب) الحدود وتسوية العتق. وقد يكون شبهًا بالإضافة إلى [سلب] منصب الولاية على عقد النكاح، فإنا إذا رددنا الطرد، رددنا به هذا','3','94',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1726,'الجنس، ولا خلاف في رده. ولا يصير صائر إلى صحة الاعتماد على كل وصف يتفق. بل إذا لم يصادف في محل إلا هذا النوع [من الأوصاف] عبر عن هذا بكونه تعبدًا. هذا أصل الكلام. فمن قبل، فلم يقبل هذا الضرب على حال. ومن رد جعل النوع كله من هذا القبيل.
وقد اختلف المفرقون بين النوعين في الخاصية التي يمتاز بها أحد الضربين عن الآخر، هل يمكن التعبير [عنه] أو [لا يمكن] ذلك؟ وإذا أمكن التعبير [عنه]، فما العبارة في ذلك؟ فقال أبو حامد: لا يمكن التعبير عنها بحال. وهذا هو الذي يقتضيه قول الإمام، لأنه قال في الشبه: [لا]','3','95',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1727,'تتأتى فيه عبارة حدية. ولكنه يدعي أنه لا يقبل النقيض، [ويزعم] أن الطرد تعليق الحكم ونقيضه عليه، على حد واحدٍ. وإنما يرجع الأمر عنده إلى ذوقٍ يدركه الناظر في نفسه، يتعذر عليه التعبير عنه. [وقد] قال القاضي عندما يقبل الشبه ويرد الطرد للفرق بينهما: أن الشبه هو الذي يغلب على الظن أن الفرع في معنى الأصل. وقال أيضًا هو: [الظاهر الذي] يوهم الاجتماع في مخيلٍ هو مأخذ الحكم.','3','96',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1728,'وقال قائلون: الفرق بينهما أن الشبه هو: الوصوف الخاص أو المقصود. هذه عبارات الأصوليين في التمييز بين الضربين، وكلها مشكلة غامضة، والمنازعة فيها [متأتية]، ولا تنقطع المطالبة عن ذكر شيء منها. وليس الطريق عندنا في ذلك إلا الالتفات إلى عادة الشرع في بيان ما أعرض عنه من الأوصاف، وما التفت إليه. وهذا لا يتأتى ضبطه بضابط، وإنما (١٤/ب) يرد المجتهد إلى نفسه، وما يقع في قلبه. فإذا وجد ما يغلب على ظنه، فلا ينبغي له أن يغالط نفسه. والذي ذكره الأئمة في التغليظ على الطاردين، إنما هو عندما يعتمد المعتمد على وصفٍ يتفق له، ولا يلتفت إلى الشريعة في تمييز الجنس المردود. وهذا باطل لا شك فيه.
وأما ما ذكره القاضي [من أن] الطرد لا يثير ظنًا ولا علمًا، فلعمري','3','97',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1729,'إنه لكذلك، إذا كان الناظر يعلق الحكم على أي وصفٍ ظهر له، فإن ذلك لا يحصل منه ظن بحال. وإذا لم يحصل [علم ولا ظن]، بطل التحكم في الدين، إذ التحكم ليس من الدين.
والتحقيق في ذلك أن يقال [لمن] نصب الوصف الذي يظهر له [كونه] علة ومناطًا، اقتصارًا منه على كونه وصفًا: أتتحكم بذلك أم عندك عليه دليل؟ فإن اعترف بالتحكم سقطت مكالمته، وإن زعم أنه يدل على كونه علة، فيطالب بالدليل. [فإن قال: الدليل على كونه علة، عدم ما يفسده. قيل له: هذا تلاعبٌ بالدين، وليس عدم المفسد مما يقتضي الصحة بحال. بل يفتقر في الصحة إلى إقامة الدليل عليه]. وليس انتفاء [دليل الفساد دالًا] على الصحة. فإن قال: الدليل على كونه صحيحًا، أن الحكم يساوقه. قيل له: مسألة التنازع نقضٌ للطرد، فإنما حصل الاطراد على زعمك أنت خاصة.
فإن قيل: فلو سلم مساوقة الحكم له مطلقًا، هل يدل ذلك على الصحة؟','3','98',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1730,'قلنا: لا. لأن تمام هذا الكلام: [أن ما] اقترن بشيء، فهو علة فيه. وهذا قد اقترن به، فهو إذًا علة فيه. ومتى لم تكن المقدمة الأولى كلية، لم يلزمه النتيجة. وكون المقدمة الأولى كلية باطل، [إذ] لا يصح أن يقال: [كل ما] اقترن بشيء فهو علة. بل الصحيح (١١/أ) أن بعض المقترن علة. فلعل هذا المقترن من البعض الذي ليس بعلة. ولا [يبقى] بعد هذا إلا محض التحكم بالنصب، اعتمادًا على محض مساوقته للحكم، وجريانه معه.
وحاصله: أنه سَلِمَ [من] مفسدٍ واحد، وهو النقض. ونحن قد قررنا أنه لو سلم عن المفسدات جميعًا، لم يلزم النصب. هذا هو المعتمد في إبطال الاعتماد على محض الاطراد في انتصاب الأوصاف أعلامًا. فإن اعتضد بسبرٍ وتقسيم، أو ملاحظة عادة [الشرع] في المراعاة، فكلام آخر، وهي المسائل','3','99',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1731,'التي تأتي بعد هذا. وإنما غرضنا الآن [أن] مجرد الاطراد من غير زيادة، لا يغلب على الظن بحال.
وأما الكلام على الوجه الذي منه غلب الشبه على الظن، فسيأتي [الكلام عليه] إن شاء الله تعالى في قياس الشبه. وفيما ذكرناه إشارة إلى جميع طرق الأئمة في إبطال الرد، فإنهم إنما ردوا على (١٥/أ) من تمسك في نصب الوصف علمًا بمحض مقارنته للحكم. فأما إذا انكشف أن المقترن لا صلاحية له للحكم بحال، وأن [نسبة] الحكم إليه في الشريعة، و [نسبة] نقيضه على حد سواء، فالأم أوضح في الرد. وما ذكره الحليمي يشير [إلى] أن المقبول المناسب خاصة. وإذا قصرنا القبول على المناسب، ظهر الكلام. وإنما الصعب إذا فرق بين الطرد والشبه.
قال الإمام: (فأما من جوز الجدل به، ومنع [تعلق] ربط الحكم به [عقلًا أو عملًا وفتوى وحكمٍ) إلى قوله] (وخرج من كونه حجاجًا).','3','100',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1732,'قال الشيخ: هذا الذي ذكره الكرخي، تقريره على غير هذا الوجه، وذلك أنه لما قال بالشبه، وتعذر عليه وعلى غيره التمييز بين الطرد والشبه، فإذا جمع الجامع بوصفٍ لا يناسب مع قبول الأشباه ورد الطرود. فأما أن يقبل منه مطلق الجميع، وأما أن يبين [كون] الجميع من قبيل الأشباه. وبيان كونه من قبيل الأشباه غير ممكن، لأنه إن تكلف إبداء المناسبة وجهة المصلحة، خرج عن قبيل الأشباه.
وإن [افتقر] إلى سبرٍ [حاصرٍ]، و [تكلف] إبطال ما عدا الوصف المعين، فهذا أيضًا طريقٌ مستقل، لا يفتقر معه إلى تقدير جهة التشبيه. وإن رد الأمر إلى الاطراد والانعكاس، [فهذا لا يصادف]، وإن صادفه، فهو طريق مستقل عند بعض الناس. فإذا [تعذرت] هذه الطرق، فأما أن يقع الاقتصار في الجمع على إبداء المناسبة، فتبطل أبواب الأشباه، وإما أن يكلف بيان خصوصية الشبه، والفرق بينه وبين الطرد، فلا يجد إليه سبيلًا. وإن','3','101',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1733,'قال: هذا يوهم الاشتمال على مخيل، فالخصم يمنع [ذلك]، إما صادقًا وإما معاندًا. وإن قال: هو أخص من غيره. قيل له: كونه أخص من الأمور الإضافية، [فرب خاصٍ هو عامٌ بالإضافة] إلى ما هو أخص منه، وكذلك [عكسه]. ولا يشترط أن يقع الجمع بالأخص من كل وجه. وإذا تعذرت هذه الجهات، لزم الاكتفاء بمطلق الجامع، ويكون الظن بالمناظرة أنه لم يذكر الجامع، مع اعترافه بكونه طردًا، إذا كان ممن عنده إبطال الطرد. ولا يتنزل الأمر في حقه إلا على ما يعتقد صحته، فوجب الاكتفاء بما يذكره جامعًا، ولا يكلف الجامع إبداء خصوصية الجهة. هذا معنى قول الكرخي.
وقد ذهب أبو حامد إلى هذا، وذكر أنه هو رأي المشايخ المتقدمين. وقال: هو مصلحة المناظرة في حق من أثبت الشبه ورآه معتمدًا، بل لا طريق سواه. قال: فإما أن (١١/ب) يصار إلى إبطال الشبه رأسًا، وقصر الجامع على المخيل، وإما أن يقبل [من] المناظرة (١٥/ب) الجمع على الإطلاق، ويكلف خصمه الاعتراض، أو يشترط إلى الشبه السبر. وهذا طريق','3','102',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1734,'مستقل لا يفتقر معه إلى التشبيه بحال.
فإن قيل: فهذا المذهب يفضي إلى فتح بابٍ من الفساد، وهو صحة التمسك بالطرديات البعيدة عن غرض الشريعة، كمن يقول: مائع لا تبنى القنطرة على جنسه، إلى غير ذلك من أنواع الهذيان. قلنا: هو كذلك، ولكن إذا لم يكن بد من ارتكاب ضررٍ، فليعدل العاقل إلى أخف الضررين.
وبيانه: أن إلزام إبداء جهة الشبه، يفضي إلى انحسام الأشباه. وقبول مطلق الجمع، يفضي إلى فتح أبواب الطرد، والطرد باطل، ومنع التعلق بالأشباه باطل. وإذا لم يكن بدٌّ من أحد أمرين، فالطرد [الشنيع] يمكن إفساده، على [القول] ببيان نقضه، أو بإبداء فرقٍ من جنسه، وذلك مسكتٌ مغلصمٌ. وهو إذا قال: مائع لا تبنى القنطرة على جنسه، فلا تزال النجاسة به، كالدهن والمرق، أمكن المفرق أن يقول: هذا خلٌّ، وذلك دهنٌ، فيقع الفرق من جنس الجامع، فلا يجد الجامع بدًا من تكلف الفرق أو الانقطاع. وهذا مسكتٌ مغلصمٌ.
وإذا أمكن حسم باب الفساد بهذا النوع من الكلام، من غير عسرٍ ولا إشكال، [فهو] أولى من تكلف إبداء جهة التشبيه، ولا يجد إلى ذلك سبيلًا. هذا هو معنى قول الكرخي: أنه يجوز التمسك به جدلًا لا فتوى.','3','103',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1735,'قال الإمام: (فأما [الطاردون]، فمما تمسكوا [به] أن قالوا: للشارع أن ينصب الطرد علمًا، وإن لم يكن مناسبًا للحكم) إلى قوله (وهو منازعٌ فيها). قال الشيخ: أما قولهم: إن للشارع أن ينصب الطرد [علمًا]، إن أرادوا أن يكون سبب الحكم، فهذا عندنا لا يصح في [الوقائع]، بل ما نصب الله تعالى علة إلا مناسبة أو متضمنة أدنى مناسب. وإذا تحققنا في الوصف عدم المناسبة، أخرجناه عن كونه علمًا شرعيًا، حتى [يتناول]','3','104',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1736,'\ (١٦/أ) اللفظ الظاهر. قال القاضي رحمه الله في (اللام): إنها للتعليل [إلا أن يدل دليل أنها لغيره، ومثل ذلك بقوله تعالى: {أقم الصلاة لدلوك الشمس}. قال: لا يصح الدلوك لكونه علة، والمراد عند الدلوك. فهو استعمال اللفظ في غير موضعه. فإذا [امتنع] معنى ذلك في حق الشارع لوجدان الشريعة على خلاف ذلك، لا أنه ممنوع عقلًا، فلأن يمتنع ذلك في حق المعلل أولى].
قال الإمام: (ومما عدوه مستروحًا لهم أن قالوا: المعاني المخيلة','3','105',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1737,'المناسبة للحكم، لا توجب [الحكم] لعينها، كما لا يوجب الطرد الحكم لذاته. والشد التي اعتقدت مخيلة في إثارة التحريم، كانت ثابتة والخمر حلال. فإذا العلل كلها، وإن اعتقدت مخيلة، إذا كانت لا توجب الأحكام لأعيانها، فهي كالطرد. قلنا: هذا لا حاصل له) إلى قوله (وهذا باطل من دين الأمة، كما سبق تقريره). قال الشيخ: [هذا] الكلام واضح، لا يفتقر إلى زيادة [بحال].
قال الإمام: (مسألة: إذا ذكر المستنبط علة مخيلة [مناسبة، ولكنها منتقضة، فقيدها بلفظ يدرأ النقض) إلى قوله (ونحن نذكر بعده تفصيل القول فيما تثبت به علل الأصول]). قال الشيخ: هذه الزيادة التي تقيد بها العلة، لا','3','106',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1738,'فقه فيها، [لا] على حيالها، ولا على تقدير انضمامها. هذا الكلام [اقتصارًا] عليه، لا يصح (١٢/أ) لإبطال تلك الزيادة، فإن الإمام يقول: [يقبح] أن يكون الجامع الشبهي بجملته لا فقه فيه، ولا يشتمل على مخيل، فما المانع من كون هذه الزيادة على هذه [الرتبة]؟ [بل] مراده: أن الزيادة تكون على ذوق الطرديات من كل وجه. وإذا تصورت المسألة على جهة الطرد المحض، [فقول القائل: ] هذه الزيادة مقبولة، لأنها حصنت العلة. كلام ضعيف، فإن النظر إلى فوائد العلة والوصف، إنما يكون [علة] بعد ثبوت صحته بدليله. فأما أن يجعل الفائدة دليلًا على الصحة، فهذا غلط. وسيأتي لهذا','3','107',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1739,'مزيد تقرير في العلة القاصرة.
فلابد على هذا التقدير من حذف الزيادة، التي هي على ذوق الطرديات، بالنظر إلى ما يتعلق بالفتوى والدين. ولا يتصور أن تكون محصنة للعلة على حال. ثم ينظر بعد حذفها، هل يصح الاعتماد على العلة أم لا؟ نعم، إن صح الاعتماد عليها دينًا، وكان النقض الوارد غير مضرٍّ أصلًا، ولكنه لو أطلق التعليل، ولم يثبت التقييد، لورد النقض من جهة عموم اللفظ، [وعدم] التعرض لاستثناء صورة النظر. فهذا يصح أن يؤتى [بالتقييد] ليقطع العموم المتلقى من ظاهر لفظ التعليل. وهل يتعين ذلك عليه في أدب الجدل، أو يستحب الإتيان به، أو الالتفات إليه والاستحباب؟ هذا مما اختلف فيه أرباب الجدل، بشرعية وضعها الجدليون، فإليهم وضعها.
ووجه من اشترط الاحتراز عن مثل هذه النقوض، التي لا تضر من جهة الاجتهاد على إيراد الألفاظ المضبوطة، كراهة للتطويل. وقد يلزم المناظر في أدب الجدل ما لا يلزم الناظر.','3','108',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1740,'وقال قائلون: إذا كان النقض من باب المستثنيات، فكأنه منقطع عن الشريعة بالكلية. فالتعرض لكونه غير واردٍ، تطويلٌ لا حاجة إليه. وأما الذين استحبوا ذلك، فإنهم لاحظوا القاعدة المعنوية، في أنه غير [مضرٍ]، واستحبوا أن تكون الألفاظ [محررة]، [حتى] لا ينسب المستدل إلى الغفلة بمواقع الاستثناء (١٦/ب). وهذا قريب، فإن النظر فيه لا يتعلق بفقهٍ، بعد أن ثبت أن المعنى صحيح في نفسه، متمسك به شرعًا، ولا يضر النقض الوارد عليه أصلًا.
وأما قول الإمام: إنه كان ينقدح بين صورة النقض وبين محل التعليل فرقٌ فقهي، فالمذكور دونه بعض العلة. هذا الكلام غير صحيح على الإطلاق، [بل] فيه تفصيل، فإنه قد [ينشأ] الفقه بإبداء [معارض] في الصورة','3','109',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1741,'[الناقضة] يمنع من إثبات حكمها في الصورة المنقوضة، ولا [يؤخذ] عدم المعارض [فيه] قيدًا مضمومًا إلى العلة على رأي المحققين. فإنه لو كان عدمه قيدًا يضاف إلى العلة، لم تتصور المعارضة، إذ شرطها استجماع كل علة شرائط الصحة، ولكن يمنع الجمع بينهما، وكذلك القول في مانع الحكم، ويفوت على هذا التقدير الالتفات إلى الموانع، وتبطل أبواب الرخص، إذ يوجد في تحريم [الميتة] عدم [الاضطرار]. فإذا كانت حالة [الاضطرار]،','3','110',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1742,'[لم] توجد العلة بكمالها. وهذا هو رأي الأستاذ أبي إسحاق. والذي للفقهاء والأصوليين خلاف ذلك.
فلا يصح إذًا إطلاق القول بأنه متى انتهض فرق بين صورة النقض ومحل التعليل، [فالعلة] ناقضة. هذا إنما يكون إذا (١٢/ب) كان الوصف الذي أبدي يضم إلى العلة، فيكون بعض أجزائها. وسيأتي الكلام على هذا مبسوطًا في باب النقض.
وإنما جرى هذا معترضًا في الكلام، بالنظر إلى إبداء هذه الزيادة، فإن اتفق أن يكون اللفظ الذي أبدي لا فقه فيه، ولكن مناسبة المسمى به مشهورة [عند النظار]، فهل يقع الالتفات إلى عدم فقه اللفظ المذكور حتى يحذف، فتنتقض العلة؟ أو يقع الالتفات إلى ما يشعر به الإيماء عن القاعدة المختصة بخواصها؟
ومثاله: أنا أبا حنيفة ذهب إلى أن إضافة الطلاق إلى الجزء المعين لا يعتبر ولا يفيد، وساعد على أن إضافة الطلاق إلى الجزء الشائع معتبر. فإذا','3','111',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1743,'قال: نصفك طالق طلقت، وإذا قال: يدك طالق لم تطلق. وقال [في الفرج]: إنها تطلق إذا أضاف الطلاق إليه، وإن كان معينًا. فإذا قلنا: جزء يحله الطلاق، [يعني] عندما يطلق الجملة، فإضافة الطلاق إليه نافذة، كالجزء الشائع.
قال: قولكم: يحله الطلاق، لفظ الطلاق لا فقه فيه، إذ هو تصرف [كقوله: ] جزء يحل التصرف، فإضافة التصرف إليه جائزة، كالجزء الشائع. وإذا قلتم [ذلك] (١٧/أ) انتقض بالنكاح. فقول المعلل: الطلاق سلطانه ونفوذه يفارق النكاح. ولذلك أن مقيده يتأبد، [ومبعضه] يتمم، فذكره يشير إلى خاصيته. ولو قال القائل: تصرف قوي في الشرع مبنيٌّ على كمال','3','112',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1744,'الاحتياط [والتغليظ]، فوجب أن يتنزل المعين في منزلة الشائع، لم يكن للنكاح ورود، على تقدير أن يثبت المستدل القوة وبناء الأمر على [التغليظ] في الباب. ولا شك في ثبوته. فذكر الطلاق يشير إلى هذه الخواص. والأصوب قبوله على هذا الوجه.

قال الإمام: <span data-type="title" id=toc-305>(القول في تصحيح علة الأصل في أقيسة المعاني</span>. [إذا ثبت','3','113',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1745,'حكمٌ في أصلٍ متفق عليه، وادعى المستنبط أنه معلل بمعنىً أبداه] إلى قوله (وقد اضطربت الآراء في السبل التي تتضمن إثبات علة [الأصول]). قال الشيخ: لما انقسمت الأحكام إلى ما يعلل، وإلى ما لا يعلل، وانقسمت الأوصاف أيضًا إلى ما يصح أن يكون مناطًا، [وإلى ما لا يصح أن يكون مناطًا]، وجب بالضرورة أن من ادعى كون الوصف علمًا أن يبين ذلك. هذا لا شك فيه في مقتضى الدين [والفتوى].
ولكن اختلف الجدليون، وأبو حامد ينقل عن القدماء أنهم كانوا يكتفون بمطلق الجمع حتى ترد الاعتراضات. وهذا هو الذي اختاره أبو حامد. قال: (ولم','3','114',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1746,'يفتح المتقدمون أبواب المطالبة بحال). وهو مضطر إلى ذلك، لقبوله الشبه ورد الطرد، وتعذر الفصل على المناظر على حسب ما قررناه.
وقال قائلون: لابد من إقامة الدليل على كون الوصف مناطًا، حتى يخرج الحاكم عن صورة المدعي. وهذا هو الصحيح عندنا. وأما تكليف السائل الاعتراض، فقد لا ينتحله. وإذا قيل هذا في [المناظر]، فما يقال [في المسترشد] الطالب للدليل؟ كيف يحسن رد الدليل إلى عجزه وقدرته؟ هذا محال.
ومنهم من التزم إقامة الدليل، ولكنه سلكوا في تقريره طرقًا فاسدة، فقال','3','115',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1747,'بعضهم: الدليل على صحته عدم ما يعارضه وينقضه. وهذا باطل أيضًا. وقد قدمنا الكلام عليه.
وقال قائلون: الدليل على صحته عجز المعترض عن الاعتراض. قالوا: (١٣/أ) وهذا بمثابة المتحدي بالمعجزة؛ فإن تعذر المعارضة دليل صحة الإعجاز. وهذا أيضًا فاسد، ومن أين يلزم من عجز الخصم عن الاعتراض أن تكون الدعوى صحيحة؟ فأما ما يتعلق بالمعجزة، فله نظر آخر، وفن مستقل.
وقال قائلون: لابد من طريق شرعي، إما توقيفي، وإما استنباطي. وهذا كلام المحققين من الفقهاء والأصوليين، لأنه [ادعاء] أمرٍ على الشرع ليس للعقل فيه مجال، فلا يعرف إلا من الأدلة السمعية.','3','116',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1748,'قال الإمام: (فمما اعتمده المحققون، وارتضاه الأستاذ أبو إسحاق) إلى قوله (كما تقدم (١٧/ب) في إثبات القياس). قال الشيخ: ينبغي أن يفسر لفظ الإخالة والمناسبة، وهاتان حالتان للوصف المخيل [والمناسب]، فليس وضع ذلك في [اللغة] ومدلوله في عرف الأصوليين.
أما المناسبة في اللغة: فهي المقاربة، [يقال]: فلان يناسب فلانًا، أي يقاربه ويشاكله. والإخالة ترجع إلى الظن، يقال: خلت الشيء، إذا ظننته، وأخال كذا، أي أظنه، وأخالني، أي ظنني. فعلى هذا يكون معنى كون','3','117',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1749,'الوصف مناسبًا، أي مقاربًا للحكم، وإذا حصلت مقاربته له، ظن عند وجود الوصف وجود الحكم، [وأخال] بالحكم، أي ظن به الناظر، [أي] حصل له [ظن] به.
وعلى هذا كان يصح أن يطلق المخيل على كل سبب أثار ظنًا بالحكم، ولكنه خص في عرف الأصوليين بأن يكون أثار الظن بالنظر إلى المصالح، وإلا إذا حصل [ظن] بالتوقيف أو بالشبه لا يسمى مخيلًا. فهو تصرف في اللفظ بالتخصيص، كلفظ العلم والفقه. وليس الاسمان على هذا التقدير مترادفين، بل هما كلفظ المهند والصارم، فهو بالمباين أشبه.
ونظير هذا إذا أطلق في أسماء الله تعالى السميع البصير، فالذات واحدة والأسماء متعددة غير مترادفة، لا جرم إذا حلف بها وحنث، لزمته كفارات. ولو كرر الاسم على محلوف به وحنث، لم يلزمه إلا كفارة واحدة. والمخيل','3','118',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1750,'المناسب عندي من هذا القبيل.
فإذا فهم ذلك، فاعلم أنا نريد بالمناسب: ما هو على منهاج المصالح، بحيث إذا أضيف الحكم إليه انتظم. كقولنا: حرمت الخمر لإسكارها، وحرم اليسير منها، لأنه داعٍ للكثير. ووجب القصاص [حماية] للنفوس. وشرع القطع صيانة للأموال.
والمصلحة في الأصل: عبارة عن جلب منفعة أو دفع مضرة. ولم [تبق] على هذا الإطلاق عند الأصوليين وعلماء الشريعة، ولكن أريد بالمصلحة عندهم: رعاية مقصود الشرع، و [مقصوده] حفظ خمسة أمور على الخلق، وهي: دينهم وأنفسهم [ونسلهم] ومالهم وعقولهم. فما تضمن حفظ','3','119',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1751,'هذه الأمور: [فهو] مصلحة. وما تضمن تفويتها: فهو مفسدة، ونفيه مصلحة.
وقد حفظ الشرع الدين بقتل الكفر، وكذلك المبتدع المضل. وحفظ النفوس بشرعية القصاص. وحفظ [الفروج] بشرعية الحدود. وحفظ الأموال بشرعية قطع السارق ومعاقبة الغاصب. وحفظ العقل بتحريم المسكرات وإثبات الحدود. هذا بيان حقيقة المناسب، وبيان متعلقاته في نظر العلماء.
ثم ينقسم (١٨/أ) أقسامًا أخر، باعتبار قوته وضعفه. فمنه ما يقع في رتبة الضرورات، و [منه] ما (١٣/ب) يقع في رتبة الحاجات، ولا ينتهي إلى رتبة الضرورات. و [منه] ما يقع في رتبة التحسينات والتزيينات.','3','120',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1752,'فأما الواقع في رتبة الضرورات، [فكحفظ] القواعد الخمسة التي ذكرناها، فلا تنفك شريعة أريد بها صلاح الخلق من حفظها، فلابد من حفظ الدين، وهو الأصل، قال الله تعالى: {وقاتلوا المشركين}. وقال - صلى الله عليه وسلم -: (أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله). وحفظ الله تعالى النفوس [بشرعية] القود، قال الله تعالى: {ولكم في القصاص حياة يا أولى الألباب}. وحفظ الله تعالى الفروج بشرعية الحدود، وتغليظ الأمر فيها، فشرع الرجم تارة، والجلد أخرى، مع الوعيد العظيم في الآخرة. وكذلك حفظ الأموال بقوله: {[والسارق] والسارقة فاقطعوا أيديهما جزاء بما كسبا نكالًا من الله}. وحفظ العقول بقوله {إنما يريد الشيطان أن يوقع بينكم','3','121',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1753,'العداوة والبغضاء في الخمر والميسر ويصدكم عن ذكر الله وعن الصلاة فهل أنتم منتهون}؟
وأما يقع [منه] في رتبة الحاجات، ولا ينتهي إلى أبواب الضرورات، فمثاله: تسليط الولي على تزويج الصغير، فإنه لا ترهق إليه ضرورة النكاح، ولكن قد تدعو إليه الحاجة إلى اشتباك العشائر، وحصول التظاهر بالقرابات. [وكذلك] عندي شرعية المساقاة والقراض، فإنه من أبواب الحاجات، فإنه لا يتأتى لكل أحد تثمير ماله بيده، إما لعجزه أو لجهله. ولو أثبت للمنصوب للتجارة حق، [وهو] يستحقه، ربح أو خسر، فقد لا يجد جده، فدعت حاجة التجر إلى تجويز ذلك، وإن كان فيه نوع من الغرر.','3','122',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1754,'وقد عد بعض العلماء من مراتب الحاجات شرعية الإجارات، وليس كذلك عندنا، فإن الضرورة تدعو إلى ذلك، ولو منع الخلق الإجارات، لتداعي إلى جميعهم الفساد، فهو كالبيع الذي لابد للخلق منه. وسيأتي لهذا مزيد تقرير بعد هذا.
[الرتبة] الثالثة: ما يقع في مرتبة التزيينات والتحسينات، ولا ينتهي إلى أبواب الحاجات. وهذا بمثابة رد شهادة العبد، وسلب المرأة منصب الولاية في النكاح، فإن هذا لا يظهر التحاقه بقواعد الحاجات. ولكن يظهر وقوعه في قسم التحسينات [والتزيينات]، إذ تولي المرأة عقد النكاح [مشيرٌ] إلى قوة الشبق وشدة الشهوة، وذلك يناقض (١٨/ب) ذات الخفرات. وكذلك سلب العبد منصب الشهادة، بناء على خسة حاله ونقصان درجته، فلا يليق به أن يعطى المناصب السنية. هذا من باب التحسين.
ومن هذا القبيل منع بعض العلماء بيع النجاسات، لأن النجس مستخبث مستقذر، وجواز بيعه يدل على إقامة وزنٍ له. ولا يليق ذلك بالعادات.','3','123',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1755,'والمرتبتان السابقتان يتعلق بأذيال كل واحد منهما ما هو كالتتمة والتكملة.
ومن هذا القبيل شرعية التماثل في القصاص، فإنه لا تدعو إليه ضرورة، ولا تلوح فيه شدة حاجة، ولكنه [مكملي]، إذ المقصود منه التتمة، وحظ مستحقه [منه] شفاء الغليل، وذلك يحصل بالمماثلة من غير زيادة ولا نقصان، حتى قال - صلى الله عليه وسلم -: (المرء مقتول بما قتل به). فقد راعى الآلة في المماثلة، وكذلك في صفة القتل. كل ذلك تتمة لحكم القاعدة، ولو فقد ذلك لم (١٤/أ) [تبطل] القاعدة بحال.
وأما التتمة في قاعدة الحاجات، فقولنا: لا تزوج الصغيرة إلا من كفء ومهر المثل. فإنه لا يظهر وجه الحاجة لذلك، كما ظهر في أصل التزويج. ولا يمكن تعليل منع المرأة عقد النكاح بالإضافة إلى فتور رأيها ونقصان بصيرتها فإنه لو أمكن ذلك لوقع في رتبة الحاجات، ولكنه لما بطل العقد عندما تزوج نفسها من كفء، دل على بطلان ذلك. وكذلك اشتراط الشهود في النكاح يظهر منه أنه من تتمة القاعدة، وليس من أصلها. فإن قيل: فلعله إنما اشترط الشهود للثبوت عند النزاع. قلنا: ليس كذلك، فإنه لو كان هكذا، لجاز الإشهاد على الإقرار [لسبق] العقد. وهذا لا يصح عند الشافعي وأبي حنيفة','3','124',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1756,'بحال. ولا يصح عند مالك بعد الدخول. دل ذلك على أنه طلب لتفخيم أمر النكاح، والميز بينه وبين السفاح. إذا المعتاد في السفاح الاستسرار به والإخفاء، فقصد الشرع أن يكون النكاح على خلاف ذلك. ولهذا قال: (اجعلوه في المساجد واضربوا عليه بالغربال). (ونهى عمر عن نكاح السر)، لأنه يفوت فيه مقصد الفرق بينه وبين السفاح، فقد: (أتى بنكاح لم يشهد فيه إلا واحد، فقال: هذا نكاح السر ولا أجيزه، ولو كنت [قدمت] فيه لرجمت).
هذا بيان حقيقة المناسب ومراتبه ومتعلقاته. وهو ينقسم أيضًا باعتبار الشهادة: إلى ما شهد الشرع بقبوله، وإلى ما شهد برده، وإلى ما سكتت شواهد الشرع عنه.
فأما ما شهد [بقبوله]، فلابد من إعماله، و [إلا كان] (١٩/أ) [في] ذلك مناقضة للشريعة فيه. ومثاله: ما ذكرناه في القصاص، أنه شرع صيانة [للنفوس]، وكذلك في القطع في السرقة، والزنا وغيره، وسائر ما','3','125',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1757,'دلت النصوص عليه تصريحًا [وإيماء].
وأما ما شهد [برَّده]، فلا سبيل إلى قبوله، فإن المعنى لا يقتضي الحكم لنفسه، ولكن إذا ظهر المعنى على شرطه، أرشدنا إلى ملاحظة الشرع له. [فإذا أعرض] الشرع عنه، فلا خلاف في رده.
وأما ما سكتت عنه الشواهد الخاصة، فهذا لا يخلو: إما أن يستنبط من أصلٍ معين، أو لا يصادف نص على وفقه. فإن لم يصادف نص على وفقه، فهذا هو الاستدلال المرسل. وسيأتي الكلام عليه. وإن وجد نص على وفقه، فهذا هو المقصود الآن، وهو ينقسم إلى ملائم وغريب.
أما ما دل النص أو الإجماع على اعتباره، فهو المؤثر. وهذا مقبول من جميع القائسين. ولا فرق بين أن يكون التأثير ثبت بنص أو ظاهر أو إجماع. وسيأتي هذا القسم إن شاء الله بعد هذا. ولكن حق هذا القسم أن يتقدم، ولكن الإمام أخره فأخرناه حتى نذكره حيث ذكره.
أما الملائم [لجنس] تصرف الشرع، إذا وجد حكمٌ على وفقه -بحيث تجتمع فيه الملاءمة وورود الحكم على الوفق -أكثر [علماء الأمة] على قبوله، وذكر عن أبي زيد أنه قال: لا يقبل إلا المؤثر. فإذا رد الملائم،','3','126',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1758,'فهو يرد الغريب. ولكنه أورد للمؤثر أمثلة عرف بها أنه يقول به، وإنما سماه مؤثرًا.
والمؤثر على وجهين: أحدهما -متفق عليه، أعني على تسميته مؤثرًا، وهو ما ظهر تأثير عينه في عين الحكم، وهو كتأثير الصغر في ولاية النكاح، فإنه ظهر تأثير الصغر في إجبار الذكر على النكاح، فليطرد ذلك في الصغيرة، فقد ظهر تأثيره في عين (١٤/ب) الحكم، [لكن] في محل آخر. فأما إن ظهر تأثير عينه في الجنس، فهل هو ملائم أو مؤثر؟ فيه خلاف يتعلق باللفظ، وهو كتأثير الشقوقة في التقديم في الميراث. فإذا قلنا: إن الشقيق يتقدم في النكاح، كان هذا متنازعًا فيه، هل هو مؤثر أو ملائم؟ أما إذا ثبت التأثير بالنص أو الإجماع، فلا خلاف في القبول، [إذ] لا يبقي إلا تعدد المحال. وهذا غير مضرٍ في الإلحاق، بدليل شرعية القياس.
فإن قال قائل: [ما] المانع من أن يختص المعنى بمحله، وإن ظهر تأثيره فيه؟ قلنا: إذا خص ذلك، بطل القياس بالكلية. فكل معنى ثبت اعتباره شرعًا، فلابد من طرده وإثبات الحكم على وقفه حيث وجد. وإن وجد منعٌ في','3','127',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1759,'بعض الصور، فما ذاك إلا لتخيل فرق أو معارض يمنع من الإعمال، لا لأجل رد الوصف على حال.
أما الملائم: فهو الوصف الذي ظهر تأثير جنسه في جنس الحكم أو عينه. ومثاله: القضاء بأن [قليل] الخمر حرمت [لأنها] تدعو إلى الكثير. لأنه قد (١٩/ب) عهد من الشرع إقامة المظنة مقام ما يوصل إليها، إذ حرم الخلوة بالأجنبية، لأنها داعية إلى الزنا. وكذلك إذا قلنا: يصح الجمع ليلة المطر، للمشقة اللاحقة. فهو ملائم لجنس تصرف الشرع، إذ ورد الشرع بالقصر في حق المسافر، للمشقة [الداخلة عليه] في إدراك الرفقة. وكذلك جوز للمريض الصلاة جالسًا، لمشقة القيام عليه. وأسقط عن الحائض قضاء الصلاة، لمشقة التكرار، فقد صار التخفيف بالأعذار ملائمًا لتصرفات الشريعة. فإذا ورد الحكم واستبط [منه المعنى] الملائم، أشارت العقول إلى إسناده إلى السبب الملائم.
وأما أبو زيد، فقد قلنا إنه رده في ظاهر لفظه، وتحقيق [أمثلته] تدل على قبوله. فإن قدرنا موافقته، فقد حصل إطباق القائسين. وإن بقي على مخالفته، فقد قال: إن المصلحة التي يسند المعلل الحكم إليها، أمرٌ يرجع إلى','3','128',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1760,'قبول القلب وسلامة النفس، يتعذر تقريرها بالأدلة، ولا يمكن إثباتها على الخصم بالحجة، فيجب أن تكون مردودة.
وهذا الذي ذكره غير صحيح، فإن المناسبة أمر معقول، إذا عرض على العقول أذعنت له بالقبول، بحيث ينسب [الخصم] إلى العناد إن جحد ذلك. فليس المراد بالمناسب ما ذكره بحال. وقال أيضًا: إن المعنى إذا دل النص أو الإجماع على اعتباره، لم يبق إلا تعدد المحال. وهذا الافتراق هو الذي أعرض عنه أهل الإجماع، بدليل شرعية القياس.
أما إذا [لم] تثبت الشهادة لعين المعنى، لكن ثبتت لما يقاربه، فما المانع من اعتبار [شيء]، وترك اعتبار ما يدانيه؟ والأمر المجمع عليه وليس هذا النوع، وإنما ذلك عند تحقق المماثلة. ولكنا نقول: إذا ظهر التقارب بين المعاني، [وقلت] جهة الافتراق، وغلب على الظن غلبة قوية أن الحكم [لذلك] ثبت، فلابد من الاعتبار. وهو أولى من تنزيل الأمر على [التعبد] مع ظهور المصلحة [المعتبر] جنسها. هذا ظاهر لا شك فيه. وأما المناسب','3','129',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1761,'الغريب، فهو في محل الاجتهاد. وقد اختلف الأصوليون فيه.
اعلم أن المعاني إذا ظهرت، فيبعد أن لا يوجد لها نظير ولا مدانٍ، بل لا يكاد المعنى المناسب ينفك عن نظير بحال. وقد قلت أمثلة الغريب. وقد ذكر العلماء له أمثلة:
أحدها -قضاؤنا بأن المطلقة في مرض (١٥/أ) الموت ترث، إلحاقًا بالقاتل الممنوع من الميراث، تعليلًا بالمعارضة بنقيض المقصود، فإن المناسبة ظاهرة. ولكن هذا النوع من المصلحة لم تعهد معتبرة في غير هذا الموضع، فكانت غريبة لذلك. ولو قيل: القتل جناية، والحرمان عقوبة فيعلل به، لكان (٢٠/أ) في قسم الملائم، لا في قسم الغريب.
المثال الثاني -تحريم بيع الأشياء النجسة، قياسًا على الميتة المحرم بيعها لنجاستها، بناء على الخسة. وهذا معنى غريب، لم يعهد تأثير الخسة في منع البيع، ولا له نظير في الشرع.
[المثال] الثالث -تعليل تحريم التفاضل في الأشياء الستة لشرفها، فإن الاشتراط يقتضي تضييق الطرق، والشريف ينبغي أن تسهل طرق الوصول إليه، وقد منعت المناسبة فيه. وقيل: إذا كانت الحاجة داعية إليه، فينبغي أن يسهل','3','130',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1762,'على المكلفين، حتى تندفع الحاجة. وقيل: إنه من قسم الملائم، فإن الأبضاع لما كانت شريفة في نظر الشرع، ضيق الشرع طرق التحصيل، حتى لا تستباح إلا بعقد نكاح، أو بملك يمين على الجملة.
وإن ثبتت الملاءمة مع ورود الحكم على الوفق، فقد اختلف الأصوليون فيه. والمسألة اجتهادية، لا قطع فيها على حال. والإمام قد قطع بالقبول، مستندًا إلى إجماع الصحابة. وهذه الدعوى شديدة، والنقل على هذا التفصيل بعيد [عن التحصيل]. نعم، كانوا يحكمون بالمصالح، فأما أن يقع القطع باعتبار المعنى الغريب، فلا سبيل إلى ذلك بحال.
فإن قيل: إذا لم [تتحققوا] الإجماع، ورأيتم المسألة في محل الاجتهاد، فما [الذي] تختارون؟ قلنا: نحن نختار القبول والتعليل به. فإن قيل: وما الذي دل على ذلك؟ وليس كل محتمل علمًا، وليس كل استصلاح وجهًا [مرتضى]؟ وإذا ثبت الانقسام، وجب الوقف إلى [قيام] دليل التمييز. قلنا: نحن بتصفح الشرع، واستقراء أحكامه نتبين أن أكثر الأحكام أجري على المصالح، والتعبدات قليلة جدًا. فتشير العقول عند ظهور المصالح إلى إحالة الأحكام عليها. وهو بمثابة ما لو رأينا من [يشتم] رجلًا، وأمر المشتوم بضربه وعقوبته، لغلب على ظننا أنه لذلك، وإن أمكن أن يكون ضربه لسبب آخر، مع صفحه عن قضية الشتم. [كذلك إذا حكم الشرع] في محل','3','131',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1763,'بحكم، وقد ظهر وجهٌ من المصالح تشير العقول إلى إضافة الحكم إليه، غلب على الظن أنه له، وإن أمكن أن يكون [تعبدًا]، أو لسبب آخر لم نطلع عليه.
فإن قيل: إنما ثبت ذلك في المشتوم الآمر بالضرب، لأن الشتم يؤلم الطباع، والناس في ذلك متفاوتون، [فدل] الضرب على [الغيظ] المعروف من العادة، بخلاف الأحكام الشرعية، فإنه لا يقتضي المناسب الحكم لعينه. والمناسب وغير المناسب بالإضافة إلى الله [- عز وجل -] على حدٍّ واحد. ألا ترى أنه لو عرف من المشتوم مقابلة الإساءة بالإحسان، لم (٢٠/ب) يصح تعليل الضرب بالشتم السابق بحال، وإنما عرف ذلك من العادات. قلنا: الأمر كذلك، واستقراء عادة الشرع تعرِّفُنا [مقصوده]، إذ العادة الغالبة في الشريعة ملاحظة المعاني دون التعبدات. ولقد قال بعض العلماء: إن الشرع لا يحكم [إلا] لمصلحة. وهذا لعمري هو الظاهر من الاستقراء، وإن جوزنا [خلاف] ذلك، فهو قليل جدًا. وذلك عندما لا تظهر المصالح. فأما إذا ظهرت، فالتعليل أولى. هذا كلام الأصوليين.
وعندي فيه تفصيل آخر، وهو الذي يقتضيه مذهب مالك رحمه الله (١٥/ب)، وهو أنا لا ننظر في جميع الشريعة نظرًا واحدًا، بل ننظر إلى كل','3','132',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1764,'قاعدة على انفرادها. وإذا كان كذلك، فالغالب في القواعد المتعلقة بالأغراض العاجلة، من البيع والنكاح، وفصل [الخصومات]، وقضية الإجارات، والقصاص والحدود وغيرها، [الالتفات] إلى المعاني، والتعبدات في هذه الوقائع قليلة. فإذا ظهرت فيها المعاني المناسبة، وإن كانت غريبة، وجب اعتبارها، وذلك أن الاحتمالات ههنا، إما أن لا يكون المحل معللًا، وقد ظهرت المصلحة فيه، وهذا بعيد في القاعدة. وإما أن يكون [معللًا]، و [المعنى] لم يظهر لنا [فيه]، وهو بعيد في حق المجتهد الباحث، [إذ المصالح] أمور معقولة. فلم يبق إلا [التعليل] [بما] ظهر للعقل، بعد بطلان التعبد والخفاء جميعًا.
وإذا فصل الأمر هذا التفصيل، وجب أن يفرق بين قواعد العبادات وبين المعاملات وغيرها. فلا يعلل في العبادات بالمعاني الغريبة، وإن كانت ظاهرة، لأنا لم [نعتمد] هناك على نفس المعنى حتى أضفنا إليه استقراء العادة في غلبة المعاني وقلة التعبدات. ولم تثبت لنا هذه العادة في قاعدة العبادة. لا جرم أن مالكًا رحمه الله لم يلتفت في إزالة الأخباث ورفع الأحداث إلى مطلق النظافة، حتى اشترط النية. ولم يقم غير الماء مقامه، وإن حصلت','3','133',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1765,'النظافة. وامتنع من إقامة غير التكبير والتسليم مقامه في التحريم والتحليل. ومنع من إخراج القيم في الزكوات، واقتصر على مراعاة العدد في الكفارات. كل ذلك لأنه لم يتحقق في هذه [القواعد] التفات الشرع إلى محض المعنى. وأبو حنيفة رحمه الله جعل الشريعة شيئًا واحدًا، ورأى الاعتماد على المعاني أكثر من التعبدات، وله وجه. والأول أجود، والنظر إليه أخص.
وقد يتخيل من ذلك أن نقل الإجماع في الباب على الإطلاق. بل ثبت الانقسام إلى ثلاثة أقسام: مؤثر، ومقبول بإجماع، وغريب. [ومحل الاجتهاد عندما] يستنبط [من أصل] معين، وغريب استنبط من أصل (٢١/أ) معين. فالأكثرون على [قبوله]. وهو الصحيح. وقد ذكرنا دليله. والله الموفق للصواب.','3','134',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1766,'قال الإمام: (فإن قيل: فقد ثبت من رأيكم أنه لا تخلو واقعة عن حكم الله تعالى) إلى قوله (وسنقرره على أحسن الوجوه، إن شاء الله تعالى). قال الشيخ: استشكل الإمام هذا السؤال، ولا صعوبة فيه. والتهويل بأن ما لا يتناهى، كيف يندرج تحت المتناهي؟ كلام لا حاصل له، فإنه يصح في اللفظة الواحدة أن تتلقى منها أحكام في آحاد لا تتناهى، كقوله: (من بدل دينه فاقتلوه). أو ما يجري مجراه من الألفاظ العامة المتعلقة بكل متجدد.
وأما ما قرره ههنا من دوران الأمر بين قسمي نفي وإثبات، فليطلب الإنسان دليل الإثبات، وإن لم يجد ألحقه بقسم النفي، فهو كلام صحيح، ولكنه يضره في أمر آخر، وهو أنه قد قدم أن النصوص مضبوطة، ومواقع الإجماع محصورة، والمسائل التي يتوقع الحكم فيها لا نهاية لها. وإذا كان','3','135',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1767,'كذلك، فالأصل الذي يسترسل على جميع المسائل هو القياس. وإذا صح له ما ذكره من انضباط أحد القسمين، [وطلب] أدلة [المنضبط]، [استمر] له الحكم في الجميع، مع الاستغناء عن القياس (١٦/أ). فلا يكون ما ذكره أمرًا ملجئًا إلى إتباع القياس. لا جرم أن غير صالح للإثبات. وإنما المعتمد تصفح قضاء أهل الإجماع في إتباع الاعتبار في مجالس البحث والاشتوار. هذا هو الصحيح. والله الموفق [للصواب].
قال الإمام رحمه الله: (فأما ما اعتمده الشافعي [وارتضاه، ولا معدل عنه،','3','136',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1768,'ما وجد إليه سبيل]) [إلى قوله (ومعناه: إذا علمت ذلك فلا إذًا). فنقول: هذا الباب كان ينبغي أن يتقدم] على إثبات العلة بطريق الإخالة والمناسبة.
و[إذا] وقع الكلام فيه يتعلق بالألفاظ، فالعلة إذا تلقيت من الألفاظ ينقسم الأمر فيها: إلى ما يتلقى من صريح الألفاظ، وإلى ما يتلقى من الإيماء والتنبيه؛ [وهو] أنواع:
الأول: ما يتلقى من الصريح: [وهو قوله: ] لكذا، أو لأجل كذا، أو أجل كذا، أو لـ: {كي لا يكون دولة بين الأغنياء [منكم]}، [أو] {من أجل ذلك كتبنا على بني إسرائيل}، {ذلك بأنهم شاقوا الله','3','137',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1769,'ورسوله}. [وقوله - صلى الله عليه وسلم -: ] (إنما نهيتكم [لأجل] الدافة)، و (إنما جعل الاستئذان من أجل البصر). فهذه الألفاظ وما [يضاهيها] من [ألفاظ]، صريحة في التعليل عند أهل اللسان. ولسنا نعني بالصريح: النص الذي لا يقبل التأويل، وإنما نعني بأنه [منطوق] بالتعليل فيه، على حسب دلالة اللفظ الظاهر على المعنى. وقد قال القاضي - رضي الله عنه -: إنه للتعليل، إلا أن يدل دليل على غير ذلك. وهو بمثابة قوله تعالى: {أقم الصلاة لدلوك الشمس}.','3','138',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1770,'قال: لا يصلح الدلوك لكونه علة، فهو بمعنى: عند الدلوك. وهذا (٢١/ب). إنما حمله على تأويل اللفظ، أنه استقر عنده أن العلل الشرعية لابد أن تكون مناسبة، أو مشتملة على معنى مناسب. وليس ميل الشمس من هذا [القبيل]. هذا كافٍ في هذا القسم، فإنه يرجع إلى فهم ظواهر اللغة. والذي ذكرناه يكفي في التنبيه على النوع.
النوع الثاني: الإيماء والتنبيه، وهو يقع على أوجه: الأول -أن يرتب الحكم على الفعل] بـ (فاء) التعقيب والتسبيب، كقوله تعالى: {والسارق والسارقة فاقطعوا إيديهما}. و {الزانية والزاني فاجلدوا [كل واحد منهما]}. {وإذا قمتم إلى الصلاة فاغسلوا [وجوهكم]}. {وإذا حللتم فاصطادوا}.','3','139',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1771,'{[فإذا] طعمتم فانتشروا}. {[فإن] كان الذي عليه الحق سفيهًا أو ضعيفًا أو لا يستطيع أن يمل هو فليملل وليه [بالعدل]}، {ومن لم يستطع منكم طولًا أن ينكح المحصنات المؤمنات فمن ما ملكت إيمانكم}. [وقوله - عليه السلام -: (من أحيا أرضًا ميتة فهي له). وقوله لبريرة: (ملكت نفسك فاختاري). وقوله - عليه السلام -: (لعن الله اليهود حرمت عليهم الشحوم فباعوها وأكلوا أثمانها)]. فكل ذلك تنبيه على إضافة الحكم لهذه','3','140',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1772,'الأسباب. ولا يظن أن ذلك لأجل المناسبة، بل لأجل [ظاهر اللغة ومقتضى اللسان]. حتى يقول في قوله: (من مس ذكره فليتوضأ). أن سبب الوضوء مس الذكر. وكذلك إذا قال: (من أكل شيئًا [مسته] النار فليتوضأ). فإن هذا يرجع إلى نفس الربط بالفاء.
وقد كنا قدمنا في هذا الكلام على [معاني] الحروف. [وذكرنا] هناك أن (الفاء) لها وجهان: أحدهما -العطف. والثاني -التسبب. فإذا انحسمت جهة العطف، تعين التسبيب. والذي يمنع جهة العطف، أن يكون متى قدرت عاطفة، اختل الكلام وخرج عن الفائدة. [وهو] كقوله: (من أحيا أرضًا ميتة فهي له). لا تصح أن تكون عاطفة. إذ لو كانت عاطفة، لنقصت','3','141',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1773,'الجملة، وبقي الشرط بلا جواب، [ويكون] التقدير: من أحيا أرضًا ميتة وهي له. وهذا لا يصح الاقتصار عليه. فلما وقع القصر عليه، تبين أنه الجواب. وإذا جاء موضع يقبل [الأمرين]، فالصحيح الإجمال، إلا أن يتبين من خارج أحد الوجهين. وهو كقوله: (زنى ماعز [فرجمه] رسول الله - صلى الله عليه وسلم -). (١٦/ب)، و ([سها فسجد]). يحتمل العطف والتعليل، فيتوقف إلى الدليل. وهل يجعل فهم الراوي مزيلًا للاحتمال، وموضحًا للكلام؟ وقد تقدم هذا.
النوع [الثالث]: أن يعلم النبي - صلى الله عليه وسلم - بفعل، فيحكم عقبه بحكم،','3','142',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1774,'فيظهر من هذا الترتيب أن الفعل الجاري هو علة الحكم. مثاله: أن يقول له قائل: فعلت كذا، فيقول: [كما] جاء حديث الأعرابي (٢٢/أ) أنه قال: (واقعت أهلي في [نهار] رمضان. فقال: اعتق رقبة). فهم أن فعله هو سبب القضاء عليه بالكفارة. وليس ذلك لأجل المناسبة. بل لو قال: لقيت زيدًا، فقال: اعتق رقبة، لفهم أن المذكور هو سبب الحكم. هذا فيما أعلم به الرسول [- صلى الله عليه وسلم -]، وينزل هذا منزلة ما لو [رتب] [هذا] [الخبر] الثاني على كلام [بعينه]، فيصير بمثابة ما لو قال: إذا واقعت في نهار رمضان، فعليك [كفارة] رقبة.
القسم الآخر: أن يعلم الرسول الفعل المتجرد، فيحكم [عقيبه]','3','143',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1775,'بحكم، فهل يكون علمه كإعلامه، حتى يكون الفعل المتجرد المعلوم سببًا؟ هذا فيه نظر. وللأصوليين في اضطراب. والصحيح عندنا أنه لا يصح إسناد التعليل إليه. على ما سيأتي في الطرد والعكس. وليس يتحقق فيه التعليل قطعًا، لأنه يحتمل أن يكون ذلك حكمًا مبتدأً، ولكن جرى ذكر الواقعة اتفاقًا. هذا احتمال، والظاهر ربط، فإنه أبعد عن اللبس وأقرب إلى الفهم.
[النوع الرابع: ] أن يذكر الشارع في الحكم وصفًا لم يصرح بالتعليل به، ولكن لو قدر ذلك الوصف غير مؤثر في الحكم، ولا مرتبط به، لخرج الكلام عن كونه منتظمًا، ولنُسب المتكلم [به] إلى غير الجد. وهذا يقع على أوجه:
أحدها -أن يذكر الوصف المعروف بعد [تبيين] الحكم. مثاله: أنه قال في الهرة: (إنها ليس بنجس، إنما هي من الطوافين عليكم أو الطوافات). فلو لم يكن التطواف سببًا لنفي النجاسة، لما كان [لذكره] معنى. لأنه قد علم أنه طوافة.
ومن هذا قوله في النبيذ: (ثمرة طيبة وماء طهور، [ثم] توضأ به).','3','144',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1776,'إن صحت الحكاية.
ومن هذا القبيل، قوله - عليه السلام -: (لعن الله اليهود حرمت عليهم الشحوم فباعوها وأكلوا أثمانها). فإنه أخبر عن [لعنتهم]، وذلك معروف عندنا، فتكون الفائدة في اعتبار هذا الوصف، لأجل الفعل المذكور بعده.
ومن هذا [القبيل] الاستنطاق بوصف يعلمه المسؤول، ثم يرتب الجواب عليه، فإنه لو لم يكن للتعليل، لما كان للاستنطاق عن المعلوم والسؤال معنى. ففهم أن المراد تنبيه السائل عن سبب الحكم.
ومن هذا القبيل أن يجيب عن محل السؤال بذكر نظيره، فيما عرف حكمه، فيدل على أن جهة المشابهة بينهما هي السبب، وهو كما: (سئل عن القبلة [للصائم] فقال: (أرأيت لو تمضمضت بماء ثم مججته)؟
وكذلك (سئل عن قضاء الحج [عن الميت] فقال: (أرأيت لو كان على أبيك دين)؟','3','145',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1777,'النوع الخامس: أن يقتطع الشارع نوعًا من قاعدة، وعلق الاقتطاع [عى لوصف]، فيظهر أن الوصف هو سبب الاقتطاع. وذلك كما اقتطع القاتل من الميراث (٢٢/ب) بعد أن تقرر التوارث [بين] القرابات. فلما قال: (القاتل لا يرث)، فُهِم أن ذلك لأجل كونه قاتلًا. ولا يظن أن ذلك [بسبب] المناسبة، فإنه لو قال: الأبيض لا يرث، الطويل لا يرث، لَفُهِم أن المعنى لذلك، ثم ينظر في [السبر].
النوع [السادس]: (١٧/أ) النهي [عما] يمنع من [الإتيان] بالمطلوب، يفهم [منه] أن ذلك لسبب منعه. وهو كقوله: {إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله وذروا البيع}، فهم أن المنع لأجل كون البيع يمنع من [الجمعة]. فهذا ونظائره مما يكثر في اللسان. وقد تقدم','3','146',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1778,'طرف منه في باب المفهوم، وقلنا: إن تعليق الحكم على كل وصف يظهر منه مدح أو ذم، أو ترغيب أو ترهيب يفهم التعليل. وإذا ثبت [تعرض] الشرع للعلة بعد أن ثبت أصل القياس، لزم إتباع المنصوب علة، سواء كان على وجه التصريح، أو على وجه الإيماء والتنبيه، إلا أن يدل دليل على أنه لم يذكر لمقصد التعليل، فيقع ذلك في أبواب التأويل.
وأما الحديث الذي ذكره الإمام، ففيه تنبيه من ثلاثة أوجه:
أحدها -الاستنطاق، وقد بينا وجهه. والثاني -الترتيب بالفاء المسببة.
والثالث -قوله: إذًا، فإنه للتعليل. وكل واحد من الأوجه مستقل بالتعليل، ويقوى [الأمر] باجتماعها. هذا وجه تقرير [جهات] [الإيماء].
وأما ما ذكره الإمام من أن (إذًا) تتنزل منزلة (ظننت)، في أنها إذا تأخرت لم تعمل. الأمر كذلك في (إذًا)، وليس هو كذلك في (ظننت). وجهة [الفرق] بينهما: أن (إذًا) حرف، والحروف لا تعمل متأخرة، والأفعال يصح أن تعمل، وإن تأخرت. وقد بينا غلطة فيما سبق في (ظننت)، فلا نعيده.
وما إذا تقدمت، فإنه لا يتحتم لها العمل كما زعم، بل إنما تعمل إذا','3','147',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1779,'تقدمت، بشرط أن يكون الفعل الذي دخلت عليه مستقبلًا. فأما إذا كان فعل حالٍ، فإنها لا تعمل على حال. والسبب في ذلك أن النواصب [كلها تخلص الفعل للاستقبال، فجعلت (إذًا) كغيرها من الحروف النواصب].
وأما ما ذكره أصحاب أبي حنيفة من أن المراد: إذا نقص فلا يباع، فكلام فاسد، وتقرير فساده ما قرره الإمام، ولا مزيد عليه. وقد قال بعض أصحاب أبي حنيفة كلامًا آخر، [وهو]: ([إن الرسول - عليه السلام - سئل عن بيع الرطب بالتمر، فقال: أينقص الرطب] إذا جف؟ قيل له: نعم. قال: فلا إذًا). وهذا الكلام ناقص، فلابد أن يكمل بشيء [مقدر]. فيمكن أن يكمل: بيحرم أو يباح. فعلى أحد التقديرين يفهم التحريم، وعلى التقدير الآخر يفهم لا ينقص عند الجفاف، أن يمنع البيع، حتى يكون الاستواء عند الجفاف مقتضيًا الحل. وإذا تردد الأمر على سواء، وليس للفظ ظهور أصلًا، لزم الإجمال، وبطل الاستدلال. وهذا خيال (٢٣/أ). وبيان كشفه: أنه لا يصح أن يكون الاستنطاق والنقصان في ثاني [الحال] سببًا لجواز البيع على حال. فإنه لو','3','148',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1780,'كان كذلك، [لزم إذا كان للمنع، والنقصان عند الجفاف سببًا للجواز]. وهذا محال لا خفاء به، فتعين بالضرورة أن يكون المقدم الذي دخل [عليه] النفي [علة] الجواز. وهذا الحذف يحتمل أن يكون نفيًا، ويحتمل أن يكون نهيًا، وكلاهما واحد فيما يتعلق بالمنع. فإنه يصح أن يقال: فلا يباع، أو لا تبيعوه. وغرض المسألة يحصل على كل واحد من التقديرين.
فإن قيل: فهذه الجهات من الصريح والإيماء والتنبيه تدل على التعليل دلالة قطعية أو ظنية؟ قلنا: هذا عندنا يقع في أقسام الظواهر، إلا أن تظهر قرائن مقال أو حال تبين النصوصية. ولكن كل ظاهر لابد أن يقطع بأصل دلالته، [حتى] يحصل العلم بأنه لا يكون وجوده كالعدم، ولكن تعتبر جهة الظهور [مظنونة]. [فالذي] يتطرق إلى الظاهر التأويل، وإلى العام التخصيص دون التعطيل. فكذلك هذه الأوصاف التي دلت الألفاظ عليها، [لا] سبيل إلى إسقاط أثرها بالكلية. والظاهر أنها [للعلة]. ويحتمل أن تكون شرطًا لعمل العلة، ويحتمل أن تكون [مبهمًا لسبب] (١٧/ب) العلة','3','149',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1781,'[اعتيادًا]، كقوله في الصيد: {ومن قتله منكم متعمدًا}. فظاهر [القرآن] يدل على أن قتل الصيد عمدًا هو السبب. ونحن نرى أن الخاطئ أيضًا يُكفِّر. ولكن نقول ذكر العمد، لأن القتل لا يقع غالبًا إلا عليه، فلم يخرج عن كونه له أثر على الجملة.
قال الإمام: (ومما يجري تعليلًا: صيغة تتضمن تعليق الحكم باسم مشتق) إلى قوله (كما سيأتي في كتاب الترجيح، إن شاء الله تعالى). قال الشيخ: ما ذكره الأصوليون من أن تعليق الحكم بالاسم المشتق يتضمن تعليلًا. هذا في اللسان يختص بالصفة المناسبة، كقوله: [أكرم] العالم، [وأهن] الجاهل، وارفق باليتيم، إلى ما يضاهي ذلك. وأما إذا لم يكن مناسبًا، لم','3','150',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1782,'يفهم [منه] ذلك في وضع اللغة. فلو قال: [أكرم] الأسود أو الأبيض، لم يدرك [سر] ذلك، وافتقر إلى البحث عن السبب. ولهذا أنه لما: (أمر بقتل الكلب الأسود، قيل: ما بال الأسود من الأبيض والأحمر؟ قيل: كنا نتحدث أنه شيطان). وأما الإثنان فليسا من هذا القبيل. فإن الكلام اشتمل على (الفاء) التي هي [للتسبيب]، وإذا اشتمل الكلام على (فاء) التسبيب، لم يشترط (٢٣/ ب) ظهور مناسبة، بل يسبق [إلى الفهم] السببية، ثم يطلب بعد','3','151',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1783,'ذلك جهة المناسبة. ولا تدخل (الفاء) في هذا المكان إلا جوابًا لشرط، أو لتقدير شرط.
وأما قوله: إذا ثبت بلفظ ظاهر قصد الشارع إلى تعليل حكم شيء، فهو أقوى متمسك به. وقرر ذلك ههنا على حسب ما جرى في كتاب التأويل. وحاصله راجع إلى أنه لو علم أنه يقدم الخبر على القياس، لزم أن يتقدم الظاهر على القياس. وقد كنا قدمنا أن الأمر ليس كذلك، فإن المتأول لا يسلم أنه خالف الخبر، بل يقول ما خالف الخبر إلا من تمسك بظاهره.
على أن الإمام قد نقض ذلك، وقال: يصح أن يخصص العموم بالقياس إذا كان أظهر منه. ولا يكون المخصص معترفا بأنه قدم القياس على لفظ القرآن، بل القياس أرشده إلى أن هذه الصورة لم ترد بالقرآن. وكذلك إذا دل الظاهر على التعليل بشيء. فقصارى الإيماء والتنبيه أن يكون [كظواهر] الألفاظ. فإذا','3','152',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1784,'دل [القياس] على أن ذلك الذي دل عليه الظاهر ليس بعلة، وكان القياس أقوى في مراتب الظنون، مما دل عليه الظاهر من التعليل، وجب المصير إلى القياس. وهذا أمر معلوم، والغفلة وقعت من جهة اعتقاد أن القائل بالقياس مخالف للشرع. وهذا غير صحيح، وقد سبق هذا بعينه في كتاب التأويل. وإنما كررناه لأنه كرره، فلذلك ذكرناه.
نعم، يبقى النظر فيما إذا دل الظاهر على التعليل، وكان الذي دل عليه الظاهر لا مناسبة فيه، لكن قارنه أمر مناسب، أو اشتمل على أمر مناسب، فهل تكون [عدم] [المناسبة] فيما دل عليه الظاهر، وظهورها فيما يقارنه ويجاوزه؟ أو تكون في ضمنه مزيلة للظاهر مقوية للتأويل؟ هذا فيه نظر، نصوره ثم نتكلم فيه.
مثاله: قوله - عليه السلام -: (لا يقضي القاضي وهو غضبان). قد دل الإيماء على مطلق الغضب، ولكن مطلق الغضب لا يناسب منع القضاء، ولكن الغضب','3','153',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1785,'يتضمن الدهش والحيرة، ومنع استيفاء الفكر، فيعلل به حتى يتعدى إلى الجوع المفرط والألم المبرح. وإنما قوي الاعتماد على المعنى في التأويل، وإسقاط ما دل عليه ظاهر الإيماء، لاستقراء الشريعة، وحصول العلم بالإعراض عن الطرديات غالبًا. والشيء [يذكر] (١٨/ أ) تارة لعينه، وتارة لما [يتضمنه] أو يقارنه. فكانت هذه الجهة تقوي [أمر] الاحتمال، وتمنع الاعتماد على الظاهر. ونظائر ذلك في الشرع كثيرة. فما [أدري] بأي وجه منع الإمام هذا، ورأى أن الاقتصار على ما دل عليه الإيماء أولى؟
وقد تردد العلماء في مسائل فيها إيماء إلى أوصاف (٢٤/ أ)، [أيكفي] الاقتصار على تلك الأوصاف، أو الإضراب عنها؟ منها: أنه روي أنه (سها فسجد). أعني النبي [- عليه السلام -]. فقد رتب الراوي السجود على السهو، فهل ذلك لمطلق السهو، أو لما يتضمنه من الإخلال بأبعاض الصلاة؟','3','154',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1786,'فقال قائلون: إنما ذلك لأجل الإخلال بالأبعاض. ولو سها ثم [تيقن] أنه [لم يخل] بشيء، فلا سجود عليه. وهذا هو الصحيح.
وقال قائلون: ذلك لأجل السهو، مع أن هذا عندهم لا ينفك عن نوع من المناسبة، إذ المقصود نفي السهو عن الصلاة. وهذا ضعيف، لأن مقتضاه أن يسجد كل من [ليس] حاضر القلب من أول صلاته إلى آخرها. وهذا لا ذاهب إليه.
[فإذًا] العلماء متفقون على أن الإيماء إذا أشار إلى وصف لا مناسبة فيه، وبحث عن المحل فصودف فيه وصف مناسب للحكم، اعتمد عليه، وترك ما أشار [الإيماء إليه]. هذا تنبيه. واستقصاء القول فيه يتعلق بأبواب التأويل، وقد سبقت.','3','155',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1787,'فإذًا من أقر الوصف الذي أرشد الإيماء إليه، فهو متمسك بظاهر. ومن أراد التعويل على وصف غيره، فهو متأول يفتقر إلى دليل يعضد به التأويل، ثم تقع المقابلة بين الدليل العاضد للتأويل مع جهة الاحتمال، وبين ما دل عليه الظاهر، فأيهما كان أرجح وجب الاعتماد عليه. وليس كل تأويل بوسيلة كل دليل، بل ذلك يختلف، فإن قوي الاحتمال، لم يشترط قوة الدليل، وإن ضعف الاحتمال، افتقر إلى قوة الدليل. [وقرب] الوصف الذي دل الإيماء عليه من المناسبة، وظهورها في وصف [يقارنه] [أو يتضمنه]، أحد الأدلة الدالة على قبول التأويل، والمصير إليه. وقد بينا وجهه، [واستوعبنا الكلام فيه، فلا معنى لإعادته ههنا. والله المستعان].
قال الإمام: (فإن قيل: قد علل رسول الله - صلى الله عليه وسلم - وجوب الوضوء على','3','156',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1788,'المستحاضة [بكون الخارج دم عرقٍ]) إلى قوله (كالسرقة والزنا وغيرهما). قال الشيخ: قوله: (توضئي فإنه دم عرق). ليس هذا من ألفاظ العموم، فمن أين يقتضي هذا وجوب الوضوء بكل دم خارج؟ فإن الضمير في قوله: (إنه)، إنما هو يعود على نكرة، وهو قوله: (دم عرق). والنكرة إنما تعم إذا كانت في سياق نفي، ولا نفي ههنا. ولا يكون هذا أيضا من المطلق، لأن النكرة إنما تكون مطلقة إذا اتصل بها أمر أو مصدر، والفعل غير واقع، كقوله: (أعتق رقبة)، أو (عليك عتق رقبة). وليس هذا هكذا، فإنه أمر بالوضوء بعد خروج خارج. فلا يفهم من هذا الكلام: [إذا] خرج منك دم عرق، وكان العرق عرقا مخصوصًا.
فالصواب إذًا ما قاله الأصحاب، لبطلان [تلقي] العموم (٢٤/ ب) من الإطلاق، واختصاص الخارج الذي رتب الوضوء عليه. نعم، قد يقاس غير هذا الدم عليه، أما أن يكون ذلك [الاعتقاد] تعميم أو إطلاق فلا. وقد يفهم','3','157',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1789,'التعليل والتعميم نظرًا إلى التنكير، وذلك نظر قاصر. فلا وجه إلا اختصاص التعليل بذلك الدم. إلا أن [يدل] دليل على مساواة (١٨/ ب) غيره له. وقد قلنا لو قال: حرمت الخمر لإسكارها، [لما] كان هذا لفظا عاما يتناول تحريم كل مسكر بحال. هذا تمام هذا السؤال.
قال الإمام: (والجواب المرضي عندنا) إلى قوله (وهذا بين من فحوى كلامه [- صلى الله عليه وسلم -]). قال الشيخ: هذا الذي قاله الإمام من أن النبي - صلى الله عليه وسلم - لم يقصد إيجاب الوضوء، وإنما قصد نفي الغسل، لا يصح لوجهين:
أحدهما- هو أنه لا يتناقض أن يقصدهما جميعًا. وهذا بناه الإمام على أصله، من أنه إذا ظهر للعموم مقصد آخر سوى الاستغراق، لم يتعلق به في جهة الاستغراق. وقد تقدم له، [وسيأتي أيضا]. وهو كقوله - عليه السلام -: (فيما','3','158',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1790,'سقت السماء العشر). ونحن إنما نعترف بذلك، إذا كان بين المفهوم المقصود وبين العموم تناقض، بحيث لا يتأتى قصدهما جميعا.
أما إذا صح اجتماعهما، فيجب إثباتهما. أحدهما- نظرًا إلى عموم اللفظ. الثاني-[نظرًا] إلى قصد الفصل مع دلالة اللفظ. فكذلك إذا اعترف بأن قوله: (توضئي فإنه دم عرق). عام، فلابد من نفي الغسل، ووجوب الوضوء من كل دم عرق.
الوجه الثاني- أنه إذا كان المقصود نفي الغسل على الخصوص، فكأنه قد قال: لا غسل عليك، فما كان ينبغي أن يجب الوضوء على المستحاضة بحال. ولم يأت في وجوب الوضوء على المستحاضة إلا هذا الحديث. فإذا كان لا يتعرض لوجوب الوضوء، فلا يجب، وإن كان يتعرض لوجوب الوضوء، مع صلاحية اللفظ للعموم، فلا سبيل إلى القصر على البعض. فإنه يرى أن جهة','3','159',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1791,'الإيماء [لا تترك] بالقياس، فكيف إذا كان الأمر محالا على التعبد؟ فما ذكره ضعيف، والوجه الأول هو الصحيح.
قال الإمام: (فإن قيل: لم تركتم تعليل رسول الله - صلى الله عليه وسلم - تخيير المعتقة بملكها نفسها)؟ إلى قوله: ([فهذا غاية ما أردناه في هذا الفن]). قال الشيخ: هذا أولًا من الإمام [مناقض] لما سبق، فإنه يرى أنه إذا ظهر من لفظ الرسول [- عليه السلام -] تعليل بوصف، فلا سبيل إلى ترك ذلك [بقياس]','3','160',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1792,'(٢٥/ أ) مستنبط، على حسب ما قرره. والإيماء يدل على تعليل الخيار بملك النفس. وقوله: (إن ملكت) غير مورد النكاح، لم يشعر ذلك بخيرة في مورد النكاح، أي لا مناسبة لذلك. فقد عدل عن ظاهر التعليل المتلقى من الإيماء واللفظ عند فقد المناسبة. وهذا هو الذي كنا اخترناه. ومعنى كلام القاضي: إن ملكت مورد النكاح، معناه: إن كان العتق يقطع ملك الزوج عن الزوجة، فلا شك أنها لا تبقى له خيرة، إذ العقد قد انحل ببطلان عصمة الزوج. وإن ملكت غير مورد النكاح، لم يشعر ذلك بخيرة في مورد النكاح. وهذا صحيح من جهة الفقه، إذ تتنزل منزلة السيد [إذا] نقل إليها ما كان له. وهو كان ملكه بمعزل','3','161',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1793,'عن ملك الزوج، وهو الذي نقله إلى الزوجة [الأمة]. فما كان ينبغي من جهة الفقه أن يثبت لها خيار، ولكن قد دل الإيماء على أن علة الخيار العتق، وهو لعمري لا ينفك عن (١٩/ أ) مناسبة، إذ بعتقها يلحقها الضرر ببقائها تحت [الرقيق]. وهو الذي يفصل بين كونها عتقت تحت عبد أو حر. فمعنى قول القاضي: هذا على معنى التكرير، فكأنه قال: ملكت الخيار فاختاري، فخرج اللفظ على هذا عن التعليل، ويرجع إلى أبواب عطف الجمل، كقوله:
وقائلة: خولان فانكح فتاتهم .... .... .... ...
أي: هذه خولان فانكح فتاتهم. وهذا تأويل سلط عليه عدم المناسبة في الوصف المومأ إليه. فتبين بهذا أن قول الإمام: إذا دل الظاهر على التعليل بوصف، لم تسع مدافعته بقياس، أن ذلك ليس على الإطلاق.
قال الإمام: (ومما أجراه القاضي وغيره من الأصوليين) إلى قوله (فلا حاصل على هذا التقدير للسبر والتقسيم في إثبات علل الأصول). قال الشيخ: السبر يرجع إلى اختبار أوصاف المحل وضبطها، والتقسيم يرجع إلى','3','162',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1794,'إبطال ما يظهر إبطاله منها. فإذًا لا يكون السبر والتقسيم من الأدلة [بحال]، وإنما الأصوليون يتسامحون بذلك ويتجوزون. والمراد إذًا بالدليل: هو الذي دل على أن العلة في جملة الأوصاف. والدليل الثاني دل على التعيين، وإلا فالسبر والتقسيم ليس هو الدليل. وإذا فهم ذلك، فهذا يستعمل في المعقولات، وقد تقدم في المقدمة، فلا نعيده.
وإنما نذكر الآن ما يليق بالشرعيات. ولابد قبل الخوض في البيان من التعرض لأصل، وهو أنا [قد] قلنا: إنما يفيد السبر إذا كان الإنسان يتبين المطلوب في الجملة، ثم تنحصر الجملة عنده، ويقوم الدليل على أنه ليس واحد مما بقي، فيتعين أن يكون هو المبقى إن بقي [واحد]، أو في الجملة إن بقي أكثر من واحد. وإذا (٢٥/ ب) كان كذلك، فلابد من إثبات ثلاثة أمور:','3','163',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1795,'أحدها- المطلوب في الجملة. والثاني- سبر [حاصر]. والثالث- إبطال ما عدا المختار. وهذه الثلاثة الأمور، إن كانت معلومة، حصل العلم بالمطلوب. وإن كانت مظنونة أو بعضها، لم يحصل العلم، وحصلت غلبة الظن. فإن كان الموضع يكتفي فيه بغلبة الظن، [يقع] السبر على هذا الوجه فيه. فإن كان يشترط فيه العلم، لم يكتف بذلك فيه.
[وهذا] الذي قررناه لا خلاف فيه، ولا يتصور غيره، ولا يصلح أن يكون نزاع الأئمة لمخالفة هذا الأصل. ومن قال: لا يعتمد السبر والتقسيم، لأنه لا يحصل منه إلا ظن التعليل، فهو [بمعزل] عن التحصيل. وإنما سبب الاختلاف: المنازعة في إثبات بعض المقدمات، على ما سنبين.
وأما قول الإمام: إن القاضي عد السبر والتقسيم من أقوى ما تثبت به العلل، ولم يبين جهة [القوة]. فهذا من عيوب هذا الكتاب، وهو التعرض','3','164',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1796,'للرد على القائلين من غير بيان مأخذهم. ووجه قول القاضي: إن السبر والتقسيم من أقوى ما تثبت به العلل، أن الذي يسند العلة إلى الإخالة مثلا أو الشبه، يكتفي منه في النظر بذلك، وإن أمكن أن يبدي الخصم معارضًا راجحا يمنع التمسك بما ظهر أولًا. وكذلك القول في الوصف الشبهي، حتى يفتقر المناظر [أولًا] إلى الإبطال أو الترجيح. وأما إذا استند إلى السبر والتقسيم، فقد وفى [الوظيفة] من أول الأمر، ولم يبق متوقعا ظهور ما يقدح (١٩/ ب) أو يضر.
يبقى أن يقال: ما المانع من كونك أغفلت قسما عليه التعويل؟ أما المجتهد، فلا ينبغي له أن يغالط نفسه، وهو يعلم من نفسه أنه أنهى البحث نهايته. وأما المناظر، فإنه يقول: هذا بحثي بحسب قدرتي وجهد مقدرتي، فإن شاركتني في عدم الإطلاع على غيره، لزمك ما ألزمتني. وإن كنت اطلعت على غيره، فاذكره حتى أنظر فيه. وإن قال: لا أذكره، ولا يلزمني ذكره، فهذا معاند، وقد كتم علما مست الحاجة إلى إظهاره. (ومن كتم علمًا يعمله ألجم [يوم القيامة] بلجام من نار).','3','165',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1797,'وأما قول الإمام في طريق القاضي: إنها مشكلة، من جهة أن من أبطل جهات [يعددها]، لم يتضمن إبطاله لها صحة ما سواها. فهذا الكلام إنما ينشأ الخلاف فيه من أصل لابد من التنبيه عليه، وهو أنه: هل يعلل كل حكم، أو لا يعلل كل حكم؟ وإذا قلنا: إنه لا يتعين تعليل كل حكم، فهل يثبت عليه [دليل] في جانب التعليل؟ فمن قال يتعين تعليل كل حكم، فقد ثبت قولنا: لابد من علامة تفصل مجرى (٢٦/ أ) الحكم عن موقعه، وقد انحصرت الأوصاف في أربعة مثلًا، وقد بطلت ثلاثة، فيتعين التعليل بالرابع، إذ لو بطل الرابع، بطل قولنا: إنه لابد من علامة تتعلق بالمحل.
فإن قال قائل: ما المانع من كون المحل غير مفتقر إلى علامة زائدة على اسمه؟ وهذا [هو] الذي بنى عليه الإمام فساد الطريق بقوله: فإنه لا يتعين تعليل كل حكم. ولهذا قال في آخر الفصل: إن دل دليل على وجوب التعليل','3','166',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1798,'كان السبر مفيدًا. فإذًا النزاع [في أنه]، هل يفتقر في المحل المعين إلى دليل خاص على كونه [معللا]؟ أو لا يفتقر إلى ذلك لوجدان دليل كلي يغني عنه؟ وإن صودف الدليل الجزئي، فهو حسن، ويرتفع خلاف الأصوليين، إلا ما يقدر من توهم من يتوهم أنه لا يصلح التمسك، لاحتمال معنى آخر. وهذا هوس، فإن الاحتمال لا يمنع من التعلق بالظاهر في محل يكتفي فيه بغلبة الظن.
وأما الجواب بأن المسألة المعروفة بين النظار إذ طال بحثهم فيها عن معانيها، ولم يطلعوا على معنى زائد إلى آخره. فهذا الكلام ضعيف، وهو يقتضي ألا يتمسك بالسبر والتقسيم إلا في مسألة طال خوض النظار فيها. فأما إذا وقعت المسألة ابتداء، فليس يصح التمسك فيها بالسبر والتقسيم. وهذا غلط [كبير]. [فما] تقرير القاعدة التي تغني عن طلب الأدلة الجزئية في تعليل الأحكام حتى يتمسك بالسبر والتقسيم؟ قلنا: فيه ثلاثة أوجه:','3','167',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1799,'أحدها- أن نقول- كل حكم لا يخلو عن مصلحة ظاهرة أو مقدرة، ويرشد إلى ذلك استقراء الشريعة، وقد قال الله تعالى: {وما أرسلناك إلا رحمة للعالمين}. [أي] بإثبات ما يصلحهم في دنياهم وآخرتهم. ويحصل من استقراء الجزئيات علم بأصل القاعدة، حتى يعلم حكم ما لم يستقرأ، [لحصول] العلم من المستقرأ. وهذا بمثابة علمنا بأن [حز] الرقبة يعقبه الموت، ويقضي بذلك [على] من لم تحز رقبته، [لحصول] العلم من المستقرأ. فعلى هذا الوجه تكون الأحكام كلها معللة، فيصح قولنا: لابد لهذا [الحكم] من علة وعلامة. وهي لا تعدو أوصاف محله، وأوصاف المحل','3','168',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1800,'أربعة مثلًا، وقد بطلت ثلاثة، فيتعين أن يكون الرابع هو العلامة. هذا (٢٠/ أ) وجه يتمسك به في السبر والتقسيم مطلقًا.
الوجه الثاني- أن لا نلتزم التعميم في التعليل بالإضافة إلى كل حكم، ولكنا نقول: الغالب التعليل، إما [في كل] الشريعة على قول، وإما في قواعد مخصوصة على قول. وإذا ثبتت [الغلبة]، فعند وجدان حكم في واقعة يغلب على الظن أنه من القسم الكثير، فينتفع بذلك في (٢٦/ ب) مسائل الظنون. فقد ثبت قولنا: لابد من علامة، بناءً على غلبة الظن المتلقاة من الكثرة.
الوجه الثالث- وهو عام، أن نقول: لا سبيل إلى قصر الحكم على الاسم عندما يوجد مماثل له على قطع. وهو كقولنا في تحريم الربا في البر، إن سلكنا مسلك السبر والتقسيم فيقال: ما المانع من الاقتصار على الاسم، ولا حاجة إلى طلب علامة؟ قلنا: ليس كذلك، فإنه إذا صار دقيقًا وخبزًا، فقد فات اسم البر، وبقي حكم الربا.
فإذًا ما من حكم من الأحكام إلا ويفتقر إلى علامة زائدة على الاسم، إلا التعبدات الجامدة التي تقتصر الأحكام فيها على نفس الصور، ولا يجري فيها','3','169',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1801,'إلحاق الشيء بما هو في معناه، كألفاظ التكبير والتسليم، واتحاد الركوع وتعدد السجود. وهذا من الندرة بحيث لا يعرج عليه.
وإذا ثبت أنه لابد من طلب علامة، وقد انحصرت الأوصاف، وقامت الأدلة على بطلان الجملة إلا [واحدًا]، تعين التعليل به، فإنه لو قدر باطلا، أفضى الأمر إلى محال في الظاهر، وهو إما [أن لا] يصح قولنا: لابد من طلب علامة، وإما أن [يصح] قولنا: ولا علامة إلا كذا، [بأن] تقدر علامة أخرى، لم نطلع عليها. وإما أن يبطل قولنا: وما سوى [هذه] [الأوصاف] باطل. هذا طريق تقرير كون السبر والتقسيم منتفعًا به على الإطلاق. وهذا كله قد ساعد الإمام عليه، فيما إذا قام دليل على وجوب طلب المناط. إلا أن الفرق بينه وبين القاضي، أن القاضي لا يشترط قيام الدليل الخاص، والإمام يشترطه. وقد بينا وجهه، وجميع ما وجهه الإمام من الاعتراضات، إنما تنحو نحوًا واحدًا، وهو أنه لا يتعين تعليل كل حكم.','3','170',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1802,'وأما قوله [أخيرًا]: لو انتصب على معنى ادعاه المستنبط [دليل]، فلا يضر أن يفرض لذلك الحكم علة أخرى. فيشير إلى أن المقسم سلك مسلك الإبطال، ليتلقى منه التصحيح. وقد [تبين]- على زعمه- أنه لا يتلقى منه تصحيح المبقى. فإذا قال القائل: ما أردت أن أجعل الإبطال دليل [التصحيح]، بل شرطه، فأنا أفتقر إلى أن أدل على صحة المعنى. قال الإمام جوابا عن هذا: هذا أمر لا يحتاج إليه، فإن المعلل إذا دل على صحة وصفه، فلا حاجة في انتصابه علة إلى إبطال غيره. فلزم من ذلك أن يكون تكلف سبرًا وتقسيمًا من غير فائدة، إذ لا يصلح الإبطال لتصحيح الباقي، ولا يشترط الإبطال في تصحيح الباقي، إن وجد دليل التصحيح، فقد صار تكلف سبرًا وتقسيمًا من غير فائدة.
وهذا أيضًا من المغالطات وطرق التمويهات، فإن الإمام لا يرى تعليل (٢٧/ أ) الحكم بعلتين، وإنما يتأتى أن يقول بهذا من يعلل الحكم بعلتين.','3','171',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1803,'[ولا يتأتى] أيضا أن يقال: لا حاجة به إلى إبطال بقية الأوصاف، فإنه قد لا تصلح الأوصاف عنده للتعليل، فيتعرض إلى إبطالها لأجل ذلك. نعم، لا يفتقر على رأي من يجوز تعليل الحكم بعلتين إلى إبطال بعض (٢٠/ ب) الأوصاف، لصحة الاعتماد على ما يدل عليه. وهذا هو غرض الإمام في هذا المكان. فأما أن يقال لا يفتقر المجتهد إلى إبطال بعض الأوصاف، فهذا محال.
قال الإمام: (والآن ينشأ من منتهى هذا الكلام أمور خطيرة [بالباب])','3','172',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1804,'إلى قوله ([فهذا ما انتهى إليه الكلام واتصل به]). قال الشيخ: هذا الذي ذكره الإمام [ههنا] غير صحيح، فإن أهل الظاهر طائفة كثيرة ينكرون القياس، وكيف يتفق تواطؤ الجم الغفير على الكتمان؟ ولو كان هذا متأتيا، [لبطلت] أخبار التواتر. فما قاله غير صحيح.
وقد أكد القضية بأمر آخر، وهو أنه لا يثق بأخبارهم عن مذهبهم مع','3','173',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1805,'كثرتهم. وقد قال هو فيما تقدم في الرد على من قال إن المعتقد غير مطمئن إلى عقده: أن من أنكر الطمأنينة منهم مع إخبارهم عن أنفسهم بالطمأنينة، وهم الجم الغفير، فقد خرق حجاب الهيبة واستأصل قاعدة العرف. فكيف لا يكون هذا مطردًا في أهل الظاهر ومن تابعهم من نفاة القياس؟ كيف يشك في مذهبهم مع كثرتهم وإخبارهم عنها؟ .
وأما ما يتعلق بنسبتهم إلى الجهل، فقد تقدم الكلام على ذلك قبل هذا. والعجب من تباين هذه المذاهب، حتى اعتبر بعض الناس في الإجماع قول العوام، وأخرج آخرون نفاة القياس من أهل الإجماع. وهذه أطراف متباعدة في الإفرط والتفريط.

قال الإمام: (ومما يتصل [القول فيه] بذلك:<span data-type="title" id=toc-323> القول في اجتماع العلل للحكم الواحد) </span>إلى قوله (وبالتعدية القاصرة المقتصرة على محل النص).','3','174',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1806,'قال الشيخ: الأمر على ما ذكره [الإمام] من أن مذهب من جوز تعليل الحكم بعلتين وضعا واستنباطا واضح، فإن علل الشارع علامات، ولا يمتنع اجتماع علامات لحكم واحد. وإن منع مانع الوقوع، فسنبينه فيما بعد.
وأما اعتماد من منع تعليل الحكم بعلتين، فإنه تعليق بالترجيح، وإنما ترجح العلل إذا تناقضت وعسر الجمع [بينها]، فحينئذ يفتقر إلى الترجيح. والتناقض تارة من جهة التضاد، وتارة من جهة ضيق المحل عن [قبول] الأمثال. وإذا كان الاستنباط من محل واحد، استحال التناقض باعتبار أنفس العلل. فلم يبق إلا ضيق المحل عن الوفاء بهما جميعًا. وهذا تناقض شرعي لا عقلي، فيدل هذا من المرجحين على اعتقادهم (٢٧/ ب) امتناع اجتماع العلل للحكم الواحد، ولكن هل سلكوا مسلك الترجيح في كل علتين بحكم واحد،','3','175',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1807,'[أو إنما] سلكوا مسلك الترجيح في البعض دون البعض؟ فإن ثبت الترجيح على الإطلاق، دل على المنع مطلقًا. وإن كان ذلك في بعض العلل، لم يقض على المرجحين بذلك على التعميم في كل التعليل. فلابد من البحث على هذا، وهو نكتة المسألة [عندي]، سنبينه إن شاء الله تعالى.
وأما صرف الإمام العناية على الاعتراض على مسألة الربا في التعليل والترجيح، فجدال محض، فإنه سيقول بعد هذا: إن خوض العلماء في مسألة','3','176',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1808,'الربا، كخوضهم فيما سواها من المسائل. ومن زعم أنها مختصة باتفاق على علتها، فقد أحال فيما قال. فقد تولى هذا الرد على نفسه، وأغنى غيره عن ذلك.
وأما ما [يصار] إليه من (٢١/ أ) أن ابن عباس لم يرد تحريم ربا الفضل، فهو منقول عنه، كما ذكر، ويتعذر التمسك بالإجماع مع مخالفة ابن عباس. وقد صار جماعة من الفقهاء إلى إثبات التحريم، والامتناع من التعليل. وهذا الذي طول به في هذا المكان خروج عن غرض الأصول، وصرف [الهمة] إلى مسألة فروعية مشكلة. وإلا فهو قد قال: إن نظر العلماء','3','177',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1809,'في مسألة الربا كغيرها من المسائل. وإذ قد تكلم فيها، فلنمش على غرضه.
فأما مصيره إلى أن المسألة غير معللة، وإنما يتلقى الحكم من التوقيف. فهذا لا نقول به، بل الصحيح أنها معللة، إما بمعان، أو أوصاف شبهية. وقد كنا قررنا جهة المعنى في بيان المناسب والغريب، [وهي] مناسبات إقناعية، فإحالة الحكم عليها أولى من إحالته على التعبد.
وأما المصير إلى أن العلة القاصرة لا فائدة فيها، ولا حاجة إليها، فالذي نختاره صحة الاعتماد على العلة القاصرة، وأنها معتمدة. وسيأتي الكلام عليها بعد هذا إن شاء الله تعالى. والقول في [النقدية]، كالقول في الطعم. إما أن يستند إلى المناسب أو إلى الشبه، وكلاهما يصح تعليل الحكم الثابت به.
قال الإمام: (وأنا الآن أبدي اختياري في منع تعليل ربا الفضل) إلى قوله (فلاح سقوط التعليل في النقدين). قال الشيخ: قول الإمام ههنا أوضح إبطال الوزن في النقدين، ولم يبين الوجه في ذلك. وله سببان:','3','178',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1810,'أحدهما- أن أصحاب أبي حنيفة وغيرهم يقولون: كل عينين اشتركا في علة الربا، أو في وصف من أوصاف علة الربا، لم يجز إسلام أحدهما في الآخر. وليس هذا مذهبًا فقط، بل ذلك مدلول عليه، متلقى من الخبر، فإنه - عليه السلام - قال: (لا تبيعوا البر بالبر، ولا الشعير بالشعير، ولا التمر بالتمر، ولا الملح بالملح (٢٨/ أ) إلا مثلًا بمثلٍ، عينًا بعينٍ، يدًا بيدٍ). [ثم قال]: ([إذا] اختلف الجنسان، فبيعوا كيف شئتم يدًا بيد).
وليس المراد: فإذا اختلف الجنسان على الإطلاق. [فإنه] لو كان كذلك، لبطل بيع الأجل والسلم جميعًا. وهذان النوعان مسوغان في الشريعة. فالمراد إذًا: فإذا اختلف الجنسان من هذه المذكورات، فبيعوا كيف شئتم يدًا بيد، فإن اتحد الجنس مع وجود العلة، حرم التفاضل والنساء جميعًا. وإن ثبت الاشتراك في العلة، ووقع الاختلاف في الجنس، جاز [الفضل] وحرم النساء. وإذا كان الوزن هو العلة، فينبغي أن يحرم إسلام الدراهم والدنانير في','3','179',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1811,'الموزونات، وهو خلاف الإجماع. فبطل الاعتماد على الوزن بهذا الطريق.
فإن قالوا: القياس يقتضي ذلك، ولكن في المصير إليه نوع من الضرورة، وسد لباب السلم، فإن النقدين هما الأثمان، فأجيز ذلك للضرورة مع خروجه عن القياس. قلنا: لا يقبل من المدعي عند توجه النقض عليه، أن النقض خارج عن القياس، حتى يبين ذلك، إما بإجماع على كونه مستثنى، أو بنص يقتضي ذلك. كما جرى في حق أبي بردة بن نيار. وتكون المسألة ترد نقضا على كل قياس، كمسألة العرايا، فإنها تنقض قياس القوت والطعم والكيل والمالية، فيعبر عن هذا بأنه خارج عن القياس، لأنه لا سبيل إلى [إبطال] كل قياس، ولا سبيل إلى تخصيص الإبطال بقياس (٢١/ ب) دون قياس، مع الاشتراك في حقيقة اللزوم واستواء النسبة.
[السبب] الثاني: الذي يبطل الاعتماد على الوزن؛ أنه واقع في أقسام الطرود، إذ يرجع إلى معرفة مقادير الأشياء، ولا يشم من ذلك رائحة مناسبة أصلا. هذا معنى قوله: ويصح إبطال الوزن.','3','180',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1812,'قال الإمام: ([وأما الأشياء الأربعة]، [فقد أوضحنا أن الطعم ليس مخيلا في التحريم) إلى قوله] (والترجيح باطل مع تجويز ارتباط الحكم بعلل). قال الشيخ: قوله: إن الطعم ليس مشعرًا بتحريم ربا الفضل، قد بينا فيه مناسبة، من جهة أن به [قوام] البنية، وله شرفية، [ولذلك حرمت] استهانته، ومنع الخلق من التلطخ بالنجاسات قصدًا، ولا [يسامح] بذلك أصلًا. ومن المناسب عند (٢٨/ ب) الاحترام، تضييق طرق التحصيل.
ووجه آخر: [وهو] أن هذه الأشياء [حرمت] لشرفها، فلا ينبغي أن تجعل بذلة، وما كانت المناسبة تقتضي إبقاءها على المالكين. ألا ترى أن الأبضاع لما ثبت احترامها، خرجت عن أن تبذل، وتعين النكاح طريقًا؟ فالمنع من التفاضل يحسم باب المعاوضة. إلا إذا اختلفت الأجناس، فتلجيء الضرورة','3','181',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1813,'إلى التنقل من الطعام [إلى طعام]، واكتفى الشرع- لما منع الفضل في الجنس الواحد- بذلك عن تحريم المعاوضة. إذ لا يبقى غرض في المعاوضة، إلا أن [يظهر] تفاوت في وجود رداءة، وقد أمر بالإعراض عن ذلك، فيدخل الأمر في هذا الوجه عند [التفاضل] في قسم المكارمة، فيقرب من أبواب السلف، فيغتفر لهذا إذا وقع البيع [نقدا]. فإن جاء الأجل، احتمل دخول أعواض المعاوضات فيمتنع، إلا أن يقصد السلف، فيفوت مقصد المعاوضة. فهذا وجه معقول من المناسبة، إذا ورد الحكم على وفقه أمكن التعليل به.
وأما قوله: السبر قصاراه إبطال ما يدعي الخصم، وليس في إبطال ما يدعيه الخصم تصحيح لغيره. فقد أجبنا عن ذلك، وقلنا: ذلك مكتفى به على تقدير وجوب طلب المناط. وحققنا ذلك، وبينا أنه لا يخالف إذا ثبت وجوب طلب المناط.
وإذا بطل ما يدعيه الخصم بعدد ثبوت التعليل، صح ما [ينفيه] السبر.','3','182',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1814,'وأما قوله: لم يثبت بالإجماع كون ربا الفضل معللا، فقد أجبنا عن هذا بثلاثة أوجه، فلا نعيدها.
وأما قوله: الترجيح باطل مع تجويز تعليل الحكم بعلل، لعمري إنه كذلك، ولكنه لا يقول به، فإنه يمنع تعليل الحكم بعلل وقوعًا لا عقلًا.
قال الإمام رحمه الله: (فلم يبق إلا طريقة تكلفتها في (الأساليب)) إلى قوله (وهو جارٍ في التقدير للنص فيهما). قال الشيخ: قال الإمام في طريقته: إذا دار الحكم مع وصف وجودا وعدمًا، أشعر [ذلك] بأنه السبب. [قال]: وقد رأينا إذا اتحد المقصود، حرم التفاضل، [وإن اختلف] المقصود، جاز التفاضل، [أشعر] ذلك بأن اتحاد المقصود هو السبب. قال:','3','183',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1815,'والمقصود من هذه الأعيان الطعم لا الكيل ولا الوزن، فإن هذه الأشياء لا تقتنى إلا لتؤكل لا لتكال ولا لتوزن. قال: وهذا إن صح، فهو من أبواب الدلالة. معناه: أنه وقع الاستدلال بالحكم على السبب. [وهذه هي] الخاصة التي يمتاز بها قياس الدلالة عن المعنى والشبه. وذلك أن المعنى يستدل به على الحكم [مباشرة، والوصف الشبهي يستدل به على الحكم] بواسطة ما يشتمل (٢٩/ أ) عليه من معنى. وقياس الدلالة يستدل فيه [بالحكم] [على المعنى] (٢٢/ أ).','3','184',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1816,'وتقريره في هذا المكان: هو أنه استدل بتحريم التفاضل عند اتحاد الجنس، وإباحته عند اختلافه على السبب، وهو [المقصود]. وهذا معنى قوله: إنه [من] قياس الدلالة.
ومعنى قوله: وهو عندي من أبواب الشبه. هو أنه يرى أن قياس الدلالة ليس جزءًا على انفراده، وسببًا على استقلاله، ولكنه يدور بين المعنى تارة، [وبين] الشبه أخرى. وقال في هذا: إنه من قياس الشبه، لعدم ظهور المعنى له فيه. ثم اعترض على ذلك بأن قال: هذا إنما يستقيم لو جرى [في] الباب','3','185',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1817,'سليمًا. وهذا الذي قاله ينبغي أن [يصغى] له، وذلك أن من يقول بتعليل النقض، لا يقول به إذا كان الطريق إلى إثبات العلة الاطراد والانعكاس.
وسبب الفرق أن العلة إذا استندت إلى الإخالة والمناسبة مثلا، فهي ثابتة وإن وجدت مسألة ناقضة، إذ المناسبة لا تنخرم بجهة المعارضة، بخلاف ما إذا كان الطريق في إثبات العلة الاطراد والانعكاس ثم وجد النقض. [فتبين أن] الدليل قد فقد، ويتعذر إثبات الحكم بلا دليل. وإذا قلنا: إذا اتحد المقصود حرم التفاضل، وإذا اختلف المقصود جاز التفاضل، فهذا طريق الاطراد والانعكاس. فإذا وجدنا المقصود [مختلفا]، والتفاضل محرما فسد الطريق. هذا هو الاعتراض الذي وجهه على [طريقه]، ثم قال: وربا النساء فرع ربا [الفضل]. فكأنه يقول: إذا أسلم القمح في الشعير، فقد اختلف المقصود،','3','186',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1818,'فلم حرم الربا الذي هو الفضل والزيادة؟ [وتخيل] اعتراضًا لم يذكره، وقدر جوابا عنه، فكأن قائلًا يقول له: ليس تحريم النساء [مبنيًا] على تحريم الفضل. فقال هو جوابا عن هذا: وباب تحريم [ربا] النساء فرع ربا الفضل. وإذا ثبت تعليل في ربا الفضل، وجب أن يناسب ربا النساء فيما يليق به.
وهذا الذي قاله غير صحيح، ولم يحرم ربا النساء عند العلماء في الجنسين، بناء على الفضل على حال. أي كيف يحرم ربا [النساء] في الجنسين تقديرًا، والربا المحقق مسوغ؟ فلسنا نسلم أن تحريم ربا النساء فرع ربا الفضل، بل لنا طريقان:
أحدهما- أن نجنح إلى التعبد في ربا النساء، ولا يلزم من علل ربا الفضل أن يعلل ربا النساء. وعلى الخصم أن يبين المماثلة، وذلك محال ههنا، فإن الجنسين المختلفين يجوز بينهما حقيقة الفضل، فكيف يبنى النساء عليه؟ وفي الحديث إرشاد إلى التقاطع، وأن كل واحد منهما أصل بنفسه. ولذلك أنه لما حرم التفاضل عند مقابلة الجنس (٢٩/ ب) بجنسه قال: (فإذا اختلف الجنسان، فبيعوا كيف شئتم يدًا بيد). هذا وجه.','3','187',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1819,'الوجه الثاني في الجواب: أنا قد قررنا أن شرفية هذه الأعيان ينبغي لأجلها أن تعد للانتفاع دون الابتياع، إلا أن تدعو الحاجة على حسب ما قررناه. فعندما يكون عند الإنسان الطعام المجانس لطعام صاحبه، يمنع من البيع منه، إذ لا غرض له في ذلك. فإن قصد تفاضلًا، فهو محرم، فإن قصد قرضا، فهو معروف مسوغ، وإن بادل بزيادة صفة، فهي من باب المعروف في أحد الجانبين. فإذا كان عنده طعام يخالف جنس طعام صاحبه، فقد تدعو الحاجة إلى التبادل ناجزًا، فلا يشترط التماثل لتباين المقاصد.
أما إذا أعطاه سلمًا، فحاجته غير منقضية، لكونه لا يتعجل ما صرف طعامه فيه، فيمنع ربا النساء لذلك، [لا] لأجل الفضل الذي يقوله الإمام. ولما بطل التعليل عنده جنح إلى التعبد. وقد صار هو إلى خلاف (٢٢/ ب) ذلك بعد هذا، وذكر أن التعلق بالمقصود آخر مرتبة الأشباه، ومثل له بمسألة الربا. ويحتمل أن يكون ذلك جرى على طريق التمثيل، لا عن طريق التحقيق.
وأما قوله: وكذلك ما يكال ويوزن. فليس هذا لفظ الخبر، ولكن الثابت في كتاب مسلم: (وكذلك الميزان). والمعنى يؤول إلى ما ذكره القوم، وبقية الكلام [مفهوم].','3','188',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1820,'قال الإمام: (وسبيل [المسؤول] في المسألتين أن يذكر الحكم [ويستمسك] بالخبر) إلى قوله (فهذا منتهى المطالب في النفي والإثبات).','3','189',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1821,'قال الشيخ: تعلق من تعلق بتحريم المرأة بالحيض والإحرام تعلق صحيح، إن لم ينظر إلى تعدد الجهات. وأما إذا نظر إلى تعدد الجهات، فلا يقوى؛ وذلك أنا نقول: وإن كان الفصل واحدًا، إلا أنه إذا تعددت الجهات فيه، كان كتعدد الذوات، ولذلك قضينا بصحة الصلاة في الدار المغصوبة، مع تحقيق المعصية، إذ الواجب الصلاة، والمحرم الغصب. وكذلك يقال في هذا المكان: حرم عليه إفساد العبادة، وحرم عليه مخامرة الأذى. ففعله وإن كان واحدًا، إلا أن له جهتان: إفساد ومخامرة، فصح أن يثبت عليه تحريمان. وأما وجوب قتلين','3','190',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1822,'فمحال، إذ لا يصح [ثبوت] قتلين.
وأما قول الإمام: إن الواجب قتلان، ولكن المحل يضيق عنهما، فكيف يصح أن يثبت في محل حكم يضيق المحل عنه؟ هذا تكليف ما لا يستطاع، وإضافة الحكم إلى غير فعل، وذلك غير معقول.
قال الإمام: (والذي يتحصل عندنا في ذلك، (٣٠/ أ) أن الحكم إذا ثبت في أصل [ولاح] للمستنبط منه معنى مناسب للحكم) إلى قوله (ولا يمتنع','3','191',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1823,'ترجيح أحدهما على الثاني). قال الشيخ: قول الإمام: إنه إذا اتحد معنى الأصل، وجب المصير إليه، فليس هذا موضع خلاف، وإنما الكلام إذا تعددت المعاني.
وقوله: ولا بعد عند التعدد من وقوع أحدهما خارجا عن ضبط الشرع. هذا الكلام لا يتصور وروده على المعاني المؤثرة، لأن النص والإجماع [دلا] على اعتبارها.
وأما قوله: لا يتعين أحدهما للإسقاط دون الثاني. هذا الكلام يريد به أنه إذا اتحد المعنى، لزم القضاء به، لأنه لم يضف الحكم إليه، و [إذا] لم يظهر غيره، [نزل] على التعبد. وهذا باطل فيما ظهر المعنى فيه، على حسب ما قررناه فيما سبق.
فأما إذا تعدد المعنى، لم يلزم من إسقاط التعليل [بواحد]، رجوع','3','192',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1824,'الأمر إلى التعبد، لصحة التعليل بالباقي. وإنما تعين التعليل بالمفرد، لرجوعنا عند امتناع التعليل به إلى التعبد. وقد تبين [بطلان] المصير إليه، وهذا مفقود عند تعدد [المعنى] مع الاستواء.
فإن قيل: أليس يمكن أن تكونا علتين؟ قلنا: ويمكن أن تكون إحداهما علة والأخرى وفاقية، ولا يتعين لهذا [التقدير] [إحداهما].
فإن قيل: فإذا ظهر رجحان [إحداهما]، فما تقولون؟ قلنا: نقول إنه لابد من الاعتماد [على الراجحة، إذ الاحتمالات ثلاثة: إما أن تكون علتين، فيصح] الاعتماد على كل واحدة. وإما أن تكون الراجحة علة، والأخرى وفاقية، فيصح الاعتماد على العلة الراجحة.
يبقى أن يقال: ما المانع من أن تكون المرجوحة هي العلة، والراجحة','3','193',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1825,'مقترنة بها؟ وهذا ضعيف. إذ كيف يعتمد [الأضعف]، ولا يعتمد الأقوى؟ وهذا كله إنما يجري في المعنى الذي لا يستقل بالتعليل إلا بشهادة الأصل، فإنا إذا رأينا رجلًا أعطى رجلًا شيئًا، فبحثنا عنه (٣٠/ ب)، فوجدناه صالحًا، ولم (٢٣/ أ) نظفر بغير ذلك، ظننا أنه أعطاه لصلاحه، وإذا أعطى شخصا آخر، فوجدناه عالمًا، ولم نجد غيره، غلب على ظننا أنه أعطاه لعلمه. فإن أعطى شخصًا ثالثًا، فبحثنا عنه فوجدناه عالمًا صالحًا، تعذر علينا أن نظن أنه أعطاه لجهة معينة أو لهما جميعًا. هذا كله إنما يكون في المعاني المفتقرة إلى شهادة الأصول.
وأما المعاني المستغنية عن شهادة الأصول، فليس ظهور الثاني بالذي يمنع من حصول الظن بنصب الأول. ولا يزيد وجود الحكم على [وفقهما] [على] عدمه. والحكم لو لم يوجد، لجاز الاعتماد على معنى على انفراده. فإن لم يكن وجوده على الوفق مقويًا، فلا أقل من أن لا يكون مبطلًا. هذا هو التحقيق في ذلك.
وأما قوله: إن الحذاف ردوا الاستدلال. فهو ممن يقول بالاستدلال.','3','194',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1826,'فإذا كان التحقيق يقتضي رده، فلم قال به؟ وإن كان القول به هو الصحيح، فلم [بنى] مسألة المنع عليه، وهو يصح الاستدلال، ويبطل تعليل الحكم بعلل؟
قال الإمام: (فإن قال قائل: فبم تنفصلون عن الحائض المحرمة الصائمة؟ ) إلى قوله (و [هذه] نهاية لا تتعدى في هذا الفن). قال الشيخ: قوله: بم تنفصلون عن الحائض المحرمة الصائمة؟ كلام عجيب، وكيف يتفق أن تكون حائضا صائما؟ هذه غفلة، إما من المؤلف أو من [الناقل].
وقوله: قد قدمنا عن هذا جوابا سديدا عندنا، فإنا نقول: الحاصل في معنى أصول تجتمع علله وتزدحم أحكامها. كلام صحيح، نظرًا إلى تعدد الجهات، ولكن قد تفرض المسألة على وجه لا يتصور فيه تعدد الجهات، وهو إذا اجتمع المس واللمس، فكل واحد منهما ناقض للطهارة، ولا تصح الحوالة','3','195',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1827,'على أحدهما دون [الآخر]، وكذلك كل حدين يفرضان معًا، فلا يصح إضافة الحكم إلى أحدهما دون الآخر، فيعلل بهما جميعا. وكذلك إذا جمع لبن أختك وزوجة أخيك [وأوجره] صبية، فإنك تكون خالها وعمها، ولا تصح [الحوالة] على إحداهما دون الأخرى. ولا يتصور تعدد الجهات في هذا التقدير، فوجب صحة تعليل الحكم بعلتين جوازًا ووقوعًا. وكيف ينكر ذلك، وعلل الشرع علامات؟ ولا يمتنع اجتماع علامات لحكم واحد.
وقول الإمام: [وإن] أبي الطالب إلا استعجال الصواب في المسألة إلى','3','196',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1828,'آخره. واستدلاله باتفاق أرباب النظر على اتحاد العلة في كل مسألة خلافية. ليس الأمر على ما قال من ادعاء الإجماع، وما زالت هذه المسألة خلافية في كل وقت. (٣١/ أ)
وأما تمسكه باختلاف الشافعي وأبي حنيفة في مسألة المعتقة تحت الرقيق، بناءً على اختلافهم في العلة في الخيار. كلام ضعيف، وقد [يقال: ] لا ينتهي الأمر مع أبي حنيفة إلى الترجيح، فإنه يرى معناه فاسدًا، فكيف يصح له مع مصيره إلى فساد المعنى أن يبني عليه أن الشافعي أبطل العلة، لاعتقاده امتناع التعليل بعلتين؟ وكذلك كل مسألة يتنازع الناس في العلة فيها، يحتمل أن يكون ذلك [لاعتقاد] ضيق المحل عن الوفاء بالعلل، على ما ذكره. ويحتمل أن يكون ذلك [لاعتقاد] كل واحد منهما ضعف معنى الآخر وإبطاله، فلا يتحصل من هذا شيء ينتفع به، إلا أن يقع الاتفاق في مسائل على','3','197',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1829,'صحة التعليل بكل معنى لو انفرد، لم يتفق أهل الإجماع على الامتناع من الجمع. وهذا لا سبيل إلى ظنه أبدًا، فضلًا عن علمه.
واستدلاله (٢٣/ ب) على امتناع وقوعه في الزمان الطويل، لو سلم ذلك، استدلال صحيح، ولكنه لم يسلم أنه لم يقع. وأكثر مذهب مالك رحمه الله مبني على تعليل الحكم بعلل. وإنما ينكر ما يعلل به غيره من الأئمة، لعدم صلاحية ما ذكره الخصم للتعليل، وإلا فلو صح المعنى عنده، لعلل به ولم يتركه.
فإن قيل: فما الصحيح عندكم في تعليل الحكم بعلتين؟ قلنا: إن كانت كل واحدة، لو انفردت لكانت صحيحة، ولو يبق إلا اجتماعهما، فاجتماعهما غير مضر، ولا مانع من التعليل بهما. ولكن قد يكون [الانفراد] يبين جانب التعليل، وعند [التعدد] يقع الشك في [التعيين]، فيمتنع التعليل لعدم الدليل، لا لضيق المحل عن العلل.
[وبيانه: ] أن المعنى إذا اتحد في محل النص، غلب على الظن أنه','3','198',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1830,'الباعث، فيكون الورود على الوفق شاهدًا عند [الانفراد] لعلة التعبد، لاسيما عند ظهور المعنى. وإذا ثبت التعدد، ضعف الظن بكل معنى على انفراده، لاحتمال [القرابة] في الوصف المعين، وثبوت النعت لغيره، فيقع اختلال الشهادة، فيمتنع من التعليل بكل معنى، شرط الاعتماد عليه بشهادة الأصل له، لاختلال الشهادة، [لا] لامتناع اجتماع العلل للحكم الواحد.
فأما العلل المؤثرة، فلا يمتنع اجتماعها، كما ذكرناه في المس واللمس، [والخئولة] والعمومة. وأما المعنى الملائم، فينبني على قبول الاستدلال المرسل أو رده. فمن رده كان تعدد المعنى في الأصل مخلا بالشهادة. ومن قبله، لم يكن التعدد مضرًا، لأنه يجوز الاعتماد عليه، وإن لم يرد حكم على','3','199',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1831,'وفقه، فكيف إذا ورد الحكم على الوفق؟ والإمام رحمه الله يرى أنه أيضًا لا يضيق المحل عن الوفاء بالعلل.
هذا (٣١/ ب) حاصل كلامه، ولكنه يزعم أنه لا يتفق أن يصادف معنيان صحيحان لحكم واحد أبدًا. فيكون امتناع التعليل بالمعين عنده، لا لضيق المحل عن الوفاء بهما، ولكن لعدم مصادفتهما، ويكون الامتناع عند غيره لضيق المحل، إذ لا يقبل [التعليل].
وهذا هو الذي يأتي على قياس مذهب الأستاذ الذي يسلك بالعلل الشرعية مسلك العلل العقلية. وأما الذي اخترناه، فلا يمتنع أن يصادق المعنيان صحيحين، ولا يضيق المحل عن الوفاء بهما. ولكن إن كان شرط جواز المعنى شهادة الأصل، فعند تعدد المعنى، تضعف الشهادة لكل واحد منهما على التعيين، فيتعذر التعليل، فيتعذر التعليل، لأنا لم نصادف المعنى المعتمد عليه على شرطه. وأما ما لا يفتقر إلى الشهادة، لا يمتنع التعليل بالمعنيين فيه، بل يجب ذلك. هذا نهاية المسألة ومقصودها المنتخل.
قال الإمام: (وإنما نشأ هذا كله من قولنا في السبر والتقسيم) [إلى قوله] (من غير أن [يتجشم] سبرًا). قال الشيخ: هذا الذي ذكره [ههنا]، بناءً منه على أن الأحكام تنقسم إلى ما يعلل، وإلا ما لا يعلل. وإذا','3','200',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1832,'بني الأمر على ذلك، أمكن في الباقي أن يكون مناطًا. ولكنا قد [كنا] قدمنا الجواب عن هذا من ثلاثة أوجه، فلا نعيدها.
وأما قوله ههنا: إنه غير محتاج إلى السبر، ولا ينتفع به، لأنه- على رأيه- لا يصلح لتعيين المبقى للتعليل، ولابد له من إقامة دليل التصحيح. وإذا أقامه، ففي ضمنه إبطال ما سوى المعين، بناء منه على أنه لا يتصور اجتماع معنيين صحيحين. (٢٤/ أ) وهذا شيء عجيب، واحتج عليه بأنه لو تصور أن يكون كل معنى على انفراده صحيحًا، لوجب التعليل بهما جميعًا، [وهذا] الذي ادعى أنه لم يقع. وكان جوابنا عنه المنازعة في عدم الوقوع، أو الإحالة على تعدد الشهادة في المعاني المستنبطة، لا لاختلال المعنى في نفسه.','3','201',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1833,'قال الإمام: (فإن قيل: لو أبدى الخصم معنى آخر مخيلًا. [قلنا: ] هذا لا يكون أبدًا، [فإن صح ذلك فيما أبداه، أشعرنا [باختلال الإخالة] الأولى، إذ لو [فرضنا] جريان الإخالة فيهما، أدى [ذلك] إلى تعليل حكم بعلتين، ولو كان ذلك سائغًا لاتفق وقوعه]). قال الشيخ: [أحال الإمام إبداء مخيل آخر، وهو دعوى محضة. الظاهر خلافها، فإنا نجد من أنفسنا تعدد البواعث على الشيء الواحد، بحيث لو انفرد كل باعث لاستقل، وهو بمثابة إعطاء الفقير الصالح العالم [القريب]، بحيث يعلم الإنسان من نفسه أن كل','3','202',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1834,'وصف منها على حياله مستقل ببعثه على إعطائه. ولا يفهم من معاني الشريعة إلا أمورًا من هذا الجنس، فإن الباعث في حق الله تعالى لنفع أو دفع يرجع إليه محال.
وإنما المراد تنزيل الأحكام على ما ألفه العباد. فقضاؤه باستحالة وجدان مخيل آخر، تحكم محض.
وأما استدلاله بأنه لو اتفق وجوده صحيحا في نفسه مع وجدان مثله، للزم التعليل بهما. وقد قلنا إن ذلك يصح. وإن سلمنا الامتناع، فذلك لاختلال الشهادة في بعض الأوصاف، لا لاختلال المعنى في نفسه].
[قال الإمام: (ويبقى وراء هذا موقف آخر، وهو تجويز تقابل مخيلين مع ترجيح أحدهما على الثاني] [إلى قوله (ونبين تقرير المختار عندنا،','3','203',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1835,'والتنصيص على لبابه]). قال الشيخ: قد جرى من العلماء بلا ريب الترجيح بين المعاني المستنبطة من الأصل الواحد، وفيه جهتان: [إحداهما]- أن الترجيح يدل على اعتقاد التعارض لضيق المحل، إذ لو صح التعليل بهما جميعًا، لم يحتج إلى الترجيح.
والجهة الأخرى- أن الترجيح إنما يكون بعد استجماع كل علة شرائط الصحة، إذ لا يترجح فاسد على فاسد، ولا صحيح على فاسد. والإمام قد ذهب إلى أن أحد المعنيين لابد أن يكون غير صالح للتعليل، فلا يكون الترجيح [جاريا] على حقيقته، فقال: هذا الترجيح (٣٢/ أ) إنما هو من باب [التنزيل]. وكأنه يقول: لو كان معناك صحيحا، لكان معناي أرجح. فإجراؤهم الترجيح يدل على اعتقادهم امتناع الاجتماع.','3','204',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1836,'وهذا عجيب، فإنه يقول: لو قدرنا صحة المعنيين، لعلل بهما جميعا. وهذا المرجح إما أن يقول إن المعنى فاسد، فلا يتصور أن يقع بينه وبين الصحيح ترجيح، إذ لا أثر للفاسد، ولا وجود [له] من جهة التحقيق. وإما يتنزل على كونه صحيحا. وإذا تنزل على الصحة، لم يحتج إلى الترجيح عند الإمام، لأنه يقول: لو صحت المعاني، لعلل بها كلها، والأمر ظاهر الفساد.
نعم، إجراؤهم الترجيح يدل على اعتقادهم امتناع اجتماع المعاني المستنبطة، المفتقرة إلى الشهادة، حتى يعلل [بها] جميعا، لا على عدم اجتماع العلل. الفرق بين العبارتين: أن العلة إنما يطلق عليها علة بعد ثبوت دليل النصب، فإذا اجتمع في المحل علل علل بها. وإن [اجتمعت] معان لا تفتقر إلى الشهادة، علل به أيضا. وإن افتقرت إلى الشهادة، فقد بينا اختلال الشهادة على تقدير (٢٤/ ب) [التعدد].
قال الإمام: (ومما ذكره الجدليون وتردد فيه القاضي الطرد والعكس)','3','205',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1837,'إلى آخر المسألة. قال الشيخ: معنى الطرد: مساوقة وجود الحكم وجود الوصف. ومعنى العكس: مساوقة عدمه لعدمه. والطرد والعكس على وجهين:
أحدهما- أن يكون الوصف موجودًا مع حكم في محل، معدومًا مع عدمه في محل آخر. ومثاله: أن تكون الخمر محرمة مقترنة بالإسكار، والثاني غير محرم، وليس بمسكر. فهذا النوع لا يصح التمسك به، اقتصارًا عليه. وهذا هو مذهب الطاردين الذين تقدم الرد عليهم، فإنهم يصححون التعلق بكل وصف','3','206',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1838,'إذا كان مطردا، أي غير منقوض. وقد تبين فساد مذهبهم، فلا معنى لإعادة ما سبق من ذلك.
والوجه الثاني- أن نثبت المساوقة نفيًا وإثباتا في المحل الواحد. ومثاله في الخمر، أنها لما كانت عصيرًا، لم تكن محرمة، ولما اشتدت حرمت، فارتبط الوجود بالوجود، والعدم بالعدم. فهذا موضع الكلام ههنا. وأكثر أرباب الجدل على صحة اعتماده طريقًا في إثبات كون الوصف مناطًا. وقد تمسك القوم بالطرق التي لم يذكر الإمام شيئًا منها، بل اقتصر على دعوى العلم بغلبة الظن النصب ضرورة. وهذا لا يغني لا في الجدل، ولا في الدين.','3','207',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1839,'فمن الطرق التي اعتمدها القوم في ذلك أنهم قالوا: قد تقدم في رتب الإيماء أن الرسول - عليه السلام - إذا أعلم بحدوث أمر، فحكم عقيبه بحكم، غلب على الظن أن الوصف الذي أعلمه، هو السبب في الحكم الذي أثبته (٣٢/ ب)، كما صورناه في واقعة الأعرابي. فإنه لم قال له: (واقعت أهلي في نهار رمضان). قال - عليه السلام -: (اعتق رقبة). فقد قال العلماء بجملتهم: الفعل المسكوت عنه هو السبب في الحكم المذكور، وليس ذلك لظهور المناسبة، بل لمحض الترتيب على السؤال. حتى لو قال: مسست ذكري، فقال له: انتقض وضوءك، لهم السبب أيضا. فقالوا: فإذا كان الإعلام ثم الحكم','3','208',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1840,'عقيبه، تبين الربط والسبب، فكذلك إذا علم هو حالة طارئة، فحكم عقيبها بحكم، فالإعلام والعلم على رتبة واحدة. هذا تقرير القوم. وقد أشار إليه الإمام بقوله: أنا أقول لو ذكر لهم، أو عرض عليهم ثبوت حكم عند ثبوت علم، وانتفاؤه عند انتفائه، لابتدروه ابتدارهم الأخبار. يشير إلى ما ذكرناه. وهو غير صحيح لأوجه:
منها- أنه إذا سئل فحكم، [فقد أفهم ترتيب] الجواب على السؤال. فلو لم يكن المذكور جوابًا، لحصل اللبس و [تأخر] الجواب، وذلك لا يحسن، وليس أيضًا بمعتاد من الرسول - عليه السلام -، ولا من غيره، بخلاف إذا علم حدوث الوصف، فحكم عقيبه بحكم، فإنه لا تلبيس، ولا تأخير جواب. ولا','3','209',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1841,'يصح أن يقال: كل حادث اقترن بالحكم فهو سببه. هذا باطل لا شك فيه.
الثاني- أنه لو صح مذهب أصحاب الطرد، فإنهم يسلكون هذا الطريق على كل وصف قارن الحكم.
الثالث- إن مقتضى هذا الطريق، الاستغناء عن العكس، إذ صار حدوث الحكم عقيب حدوث الوصف يرشد إلى النصب. فأي حاجة بعد ذلك إلى انعدامه، وقد استقر كونه سببا له؟ هذا لا حاجة إليه.
الرابع- (٢٥/ أ) يختص بالإمام دون غيره، أنه قد ذهب إلى أن الطرد والعكس يتأخر عن الوصف الذي ثبت انتصابه علمًا بالإخالة والمناسبة. وطرق الإيماء مقدمة على مراتب الإخالات. فلو كان الطرد والعكس منها، لوجب تقديمه على المخيل المناسب. فتبين بهذه الجهات أن [هذا] نوع من المغالطات التي لا تحقيق فيها.','3','210',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1842,'وقال قائلون: وجه التعلق بالطرد والعكس: أن الوصف لما حدث، وحدث الحكم بحدوثه، أشعر بكونه علة فيه وعلامة له. وهذا تلبيس، فإن الخصم لا يسلم أنه حدث بحدوثه، [بل يقول حدث عند حدوثه]، ولا يسلم أن كل حكم حادث عند حدوث أمر، يكون ذلك الأمر سببًا فيه.
فإن قيل: وجه التمسك به أن نقول: حكم حادث، فلابد له من سبب حادث، ولا حادث إلا كذا، فهو إذًا السبب. هذا لعمري وجه، (٣٣/ أ) لكنه ينبني على [قبول] السبر والتقسيم، ويفتقر إلى تكميل الطريق بتمام السبر،','3','211',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1843,'فيخرج الطرد والعكس عن خصوصيته، ويرجع إلى السبر والتقسيم، وقد مر اختيارنا فيه. وليس الكلام عندما ينضم إلى الطرد والعكس دلالة السبر، بل ذلك طريق مستقل، كان سبرًا أو [لم] يكن.
فاعتراض القاضي بأن الطرد متنازع فيه، والعكس ليس بشرط في العلة التي تجري علما. كلام فيه نظر، وذلك أنه لا بعد في أن يكون الشيء غير مشروط، ولكنه إذا وجد أفاد، ولسنا نشترط في البينة التناهي في العدالة، ولكنه إذا وجدت هذه المزية، أفادت ترجيحًا. يظهر [الأثر] على تقدير وجدان المعارض.
والذي يحقق ذلك أنه لو تعارضت علتان، إحداهما منعكسة، والأخرى غير منعكسة، ترجحت المنعكسة على الأخرى. وأما كون الطرد لا يعم في','3','212',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1844,'صورة الخلاف على وفاق، فهو أيضًا ضعيف، فإن الاستقراء قد يحصل علمًا في بعض الصور، حتى يقضى به على صورة النزاع. وقد يحصل غلبة ظن، وإن كان الخصم يناكر في الصورة المعينة، ولكن [يكون] استقراء غيرها يرشد إلى حكمتها قطعا أو ظنا.
فهذه الجهات التي [ذكرناها] للقاضي ضعيفة عندي، وإنما الأمر الغامض في دعوى التعليل، بناءً على مجرد الاقتران [في النفيٍ] والإثبات، من غير زيادة، لا من جهة إيماء، ولا من جهة مناسبة، ولا من جهة سبر','3','213',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1845,'وتقسيم، أو نوع من [التشبيه]. هذا هو العسر، وحاصله راجع إلى أن كل مقترن بشيء سبب في وجوده. وهذا لا يصح دعواه على الإطلاق، إذ المقترنات منقسمة إلى سبب وإلى غير سبب، ولم تثبت الكثرة في جانب التشبيه، حتى يقع الاعتماد عليها، نظرًا إلى جانب الكثرة. فإذا لم يثبت ذلك، لم يبق وجه يتمسك به.
وما ادعاه الإمام على الصحابة، أمر لم ينقله، وإنما هو قدره، فقال: وأنا أقول: لو كان كذلك كان كذا. تقدير هو فيه منازع، ومن أين له العلم أو غلبة الظن بأنهم كانوا يبتدرونه؟ بل الظن أنهم كانوا لا يتعاطونه، لعدم الوجه المغلب على الظن بنصب الشارع له علمًا.
وأما الكلام الثاني: أنه إذا ثبت أنه مغلب على الظن قصد الشارع إلى نصبه علمًا إلى آخره. فكلام صحيح، وأن الحاصل من نظرهم في المسائل','3','214',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1846,'أنهم (٢٥/ ب) ما كانوا يبغون العلم والتبيين في كل مسألة فروعية، وإنما كان أقصى نظرهم أن يظنوا أن الشارع قصد إلى نصب شيء علمًا. وقد بينا فقدان التعبدات في أبواب القياس، (٣٣/ ب) إلا أن يقصد الشارع تخصيص صورة، فينص عليها، كما قال لأبي بردة بن نيار: (تجزئك ولا تجزئ عن أحدٍ بعدك). فمتى ثبتت غلبة الظن بكون الوصف منصوب الشارع، لم يكن بد من اتباعه، وعدم الاقتصار على محل دون محل [مما وجد] فيه العلم. ولكن الطريق المفضي إلى حصول غلبة الظن بنصب العلم لم يصادف، ولم يستعمل المخيل لعينه، ولكن لما استقرئت الشريعة وجدت تلتفت إلى المصالح وردًا وصدرًا، صح لنا اتباع المخيل في التعليل. وكذلك ما يظهر من ألفاظ الشارع الإيماء إلى التعليل به، فهو بذلك أولى.
والحاصل من تصرف الصحابة والتابعين ومن بعدهم من العلماء، أن الشارع إذا أناط الأحكام بالأوصاف، اتبع الحكم عموم الوصف، ولم يقتصر على خصوص المحل. والأمر الغامض في الباب معرفة المناط. وإذا صح، وجب الاتباع حتى يمنع مانع، ولم يكونوا يقصرون قصر فهم على مغلب دون مغلب.','3','215',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1847,'والسبب في ذلك: أنا رأيناهم يعلقون الحكم على الوصف الذي دل النص عليه، وعلى ما دل عليه الظاهر، وعلى ما أرشدت إليه الإخالة، مع تفاوت جهات الأدلة في القوة والضعف. ولا تجتمع هذه الجهات إلا في أصل حصول غلبة الظن، فأعرض عن التفاوت في ذلك. وبهذا الطريق اعتبرنا الظواهر مع تفاوتها. وكذلك رواية أخبار الآحاد، ولم يشترط في العمل بها قوة غلبة الظن في العدالة، فإن ذلك لو اشترط، لبطل العمل بأخبار الآحاد، فإن أصحاب رسول الله - صلى الله عليه وسلم - أعدل خلق الله تعالى، وليس أحد بعدهم يبلغ [شأوهم] في ذلك. فلو كانت النهاية في العدالة معتبرة، لما عمل بخبر غيرهم [على حال]. فليس النزاع بعد حصول غلبة الظن بنصب العلم، وإنما النزاع في طريق حصول غلبة الظن بالنصب، فإذا ثبتت، فلا إشكال بعد الثبوت.','3','216',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1848,'قال الإمام: <span data-type="title" id=toc-327>(مسألة: ذهب بعض المنتمين إلى الأصول إلى أن الانعكاس لابد منه [</span>في العلل وإن كانت مظنونة]) إلى قوله (وهذا مما يستدل به من لا يشترط العكس). قال الشيخ: تمسك مشترطي العكس باشتراط الانعكاس في العلل العقلية [باطل]، لاخفاء ببطلانه، لا بالنظر إلى منع العلل العقلية، فإن الرأي الظاهر عندنا، إثبات العلة والمعلول عقلًا، ولكن من جمع بين مختلف','3','217',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1849,'فيه ومتفق عليه، افتقر إلى جامع. وقد تقدم أن الجوامع أربعة: العلة والحقيقة والشرط والدليل. وليس في (٣٤/ أ) هذا المكان إلا الجمع باسم العلة، وذلك غير مكتفى به على الإطلاق في وجه من الوجوه.
ثم السبب في اشتراط الانعكاس في العلة العقلية، أن اقتضاءها لمعلولها اقتضاء نفسيًا، ولا يصح إثبات الموصوف دون وصفه، ولا الوصف النفسي مع فقدان الموصوف به. (٢٦/ أ) فلذلك وجب التلازم في النفي والإثبات. وأما العلل السمعية فعلامات، وحقها أن تقابل [الأدلة] العقلية. هذا هو التحقيق في ذلك.','3','218',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1850,'قال الإمام: (وقد تعلق الجمهور بأن العكس لو كان شرطا، لوجب أن لا يقتل إلا قاتل، [من حيث كان القتل علة قتل القاتل]) إلى قوله (فدخول المختلفات تحت صفةٍ واحدة عامة لا يوجب اتحادها). قال الشيخ: ما تعلق به الجمهور صحيح، من جهة أن العكس لو كان شرطًا على كل حال، لوجب إذ زالت العلة أن يزول الحكم. فلما كانت العلة قد تزول ويبقى الحكم مستمرًا، إما تعبدًا، وإما بعلة أخرى، دل على أن العكس [ليس] شرطًا على الإطلاق.
وأما قولهم: إن الطرد لما كان شرطا، فإذا تعذر الاطراد لعلة أخرى، بطلت العلة الأولى. فهذا الكلام غير صحيح عندنا، وقد قدمنا طرفًا من ذلك.','3','219',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1851,'وسيأتي له مزيد تقرير في باب النقض.
وأما المصير إلى أن القتل الواجب بالقتل بعدم القتل، وإنما الواجب عند عدم القتل قتل آخر. هذا خيال ضعيف، فإن استحقاق الدم قضية واحدة معقولة، لا تعدد فيها. [ولئن] كان هذا الخيال يتأتى في هذا، فما الجواب إذا وجد البول وفقد الغائط، أو بالعكس بالإضافة إلى انتقاض الطهارة؟ فهل يتصور أن يقال وجوب الوضوء مرتب على الغائط؟ هذا [محال] لا يقوله [لبيب].','3','220',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1852,'وأما قوله في التحريم بالحيض والإحرام والعدة والردة. فهذا غلط، فإنه لا يصح أن يجتمع الإحرام مع الردة على حال، بل الردة تبطل العمل، ولا يكون المرتد محرمًا. والمراد اجتماع بعض العلل، وليس المقصود تحقيق المثال.
أما إذا ظهر تعدد الأحكام، وارتبط حكم كل سبب، واتسع المحل لذلك، فيظهر أنه إذا زال السبب زال حكمه بزواهل. وهذا بمثابة تحريم وطء الحائض، فقد حرم عليه مخامرة الأذى، وحرم عليه اختلاط الأنساب، فهما تحريمان متعلقان بفعل من جهتين، كتعدد الجهات في الصلاة في الدار المغصوبة.
قال الإمام: (وإذا ألزم هؤلاء الأدلة (٣٤/ ب) العقلية) إلى قوله ([عدنا] إلى الأدلة العقلية عودة أخرى، إن شاء الله تعالى). قال الشيخ: أما المصير إذا التزم عكس الدليل، فصعب شديد، وكيف يتصور ذلك [وحدث] العالم يدل على الصانع، والفعل يدل على الفاعل، وليس للفاعل','3','221',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1853,'من فعله حال ترجع إليه؟ والله تعالى أزلي لا يتصور [التجدد] عليه، ولا حدوث أمر لم يكن له. فإذا [فعل العالم] دل العالم على صناعه ومخترعه، من جهة اختراعه، ودل على إرادته من جهة تخصيصه، ودل على علمه، إما من جهة إحكامه، أو من جهة كونه مرادًا لفاعله. وهذه الدلالة تجددت قطعًا، والمدلولات قديمة أزلية، فكيف يتصور على هذا أن يقال: إن','3','222',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1854,'انتفاء الفعل يدل على انتفاء الفاعلية؟
وقوله: والفاعلية هي وقوع الفعل على الحقيقة، وليس وقوع الفعل أمرًا زائدًا على الفعل. إذ ليس الوقوع أمرًا زائدًا على الواقع، بالنظر إلى المعقول. وكذلك ليس الخلق أمرًا زائدًا على المخلوق، والعالم معدوم في الأزل، والله تعالى خلق العالم، [والعالم] هو الخلق، وهو المخلوق. وقد حققنا هذا في مسألة (٢٦/ ب) تقدمت في كتاب الأوامر. [حيث] قال أبو حنيفة: إذا أمره بالخياطة، ونهاه عن إيقاعها وقت الزوال. فإذا خاط وقت الزوال، قال: فقد جمع بين المطلوب والممنوع، إذ المطلوب الخياطة، والمنهي عنه الإيقاع.','3','223',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1855,'ونحن نقول: الخياطة هي الإيقاع بعينها، وليس للفاعل فعل إلا الخياطة، فلا فرق بين أن [يقال] له: خط، [وبين أن يقال له: ] أوقع الخياطة.
فقوله: الفاعلية هي: وقوع الفعل، معناه: الفاعلية هي الفعل، فكأنه يقول: عدم الفعل يدل على عدم الفعل. وهذا لا يرتضيه محصل، فلم يصح القول بأن عدم الدليل يدل على عدم المدلول، فلا يلزم أن يكون عدم العلامة الشرعية، يدل على عدم الحكم، أو أخذت باعتبار كونها علامة.
قال الإمام: (ومما تعلق [به] من لا يشترط العكس [[أن قالوا] انتفاء التحريم ورفع الحرج من الأحكام، فإذا تعلق التحريم بعلم، لم يلزم أن','3','224',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1856,'ينتصب عدمه علمًا لحكم آخر]) إلى قوله (فقد وهت هذه الطريقة). قال الشيخ: هذا الذي ذكره هؤلاء القوم كلام صحيح، وإنما وقع الغلط في إطلاق لفظ الحكم على نفي الحكم. والمقصود أنه إذا انتصب علم على ثبوت حكم، لم يلزم أن ينتصب نفيه علمًا على نفيه، إذ قد ينتفي الدليل ويبقى المدلول، وهي الطريقة الأولى بعينها، وإنما حصل هنا زيادة، وهي إطلاق القول بأن نفي الحكم حكم، وهي عبارة مخيلة، لا حاجة إليها في تحصيل عرض المسألة، [في] أنه لا يشترط عكس العلامة.
قال الإمام: (ومما تمسك به بعض من نفي اشتراط الانعكاس، [ما قدمناه في أدراج الكلام في الأدلة العقلية، فإنها إذا دلت بوجودها على [مدلولاها]، لم يدل عدمها [على عدم المدلول]) إلى قوله (والمطلوب','3','225',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1857,'بعد من حقيقة المسألة بين أيدينا]). قال الشيخ: (٣٥/ أ) أما كون العلوم كلها ضرورية، فإن الإمام يطلقه عل معنيين:
أحدهما-[أنه] يرى أن العلوم كلها ليست مقدورة بالقدرة الحادثة،','3','226',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1858,'[بل] واقعة بقدرة الله تعالى. هذا مذهبه في العلوم، وقد تقدم ذلك. وليس المراد هذا ههنا.
الثاني- أنه يرى أن العلوم ليست منقسمة إلى نظري، بمعنى أن النظر في الدليل يوصل إليه، وإنما العلوم مرتبة عنده، فلا يحصل الثاني عنده إلا إذا حصل الأول. فلما توقف بعض العلوم على شرط، وهو سبق بعضها، سمي الثاني نظريا والأول ضروريا. هذا معنى تسميته نظريا.
فأما المصير إلى أن بعض العلوم يكون النظر في الدليل يفضي إلى العلم','3','227',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1859,'بالمدلول، فهذا محال عنده. وقد تقدم الكلام على هذا في مقدمة الكتاب، ووجهنا الاعتراض عليه. من جهة أن العلوم لو كانت كلها ضرورية، وأن الدليل لا يفضي إلى شيء [منها]، للزم إذا حصل المعلوم الأول، أن [لا يستريب] الإنسان في المعلوم الثاني، بعد حصول شرطه. ولما كان المعلوم الأول يحصل ويتوقف الإنسان في المعلوم الثاني، حتى تتضح له جهة الربط، تبين بذلك أنها ليست كلها ضرورية.
وما قاله من [التنبيه] بما [ينأى] عن [الناظر] البصير بعض النأي، فإنه يفتقر إلى تحديق النظر في جهة المطلوب. التحديق في هذا المكان هو (٢٧/ أ) الذي أراده الأصوليون بتجريد الفكر من جهة الربط. وهذا','3','228',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1860,'كله قد تقدم. نعم، في العلل الشرعية [ذوق] المصالح زائد على محض العلامة. فهذا هو الفرق بين الدليل العقلي والعلة الشرعية. وأما الاقتضاء العقلي، فهما جميعا مستويان فيه، والدلالة أيضًا ثابتة لهما، إلا أن أحدهما يدل عقلًا، والآخر يدل وضعًا، إما قطعًا وإما ظنًا، والإشعار الفقهي تختص به العلة الشرعية دون مجرد الدليل، عقليًا كان أو سمعيًا. نعم، [وزان] الأدلة العقلية العلامات الشرعية، التي لا اقتضاء فيها، كزوال الشمس [الذي] جعل علمًا على وجوب الصلاة. وإذا سلم الإمام ما قصده بهذا الكلام، من إشعار العلل','3','229',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1861,'الشرعية من جهة الفقه بالأحكام، فمن أين يلزم إشعار عدمها بدم الأحكام؟ هذه دعوى مجردة من غير برهان.
قال الإمام: ([وقد] قالوا: إذا كانت العلامات الشرعية لا تقتضي أحكامها لأعيانها) إلى قوله (فهذا صدر الكلام). قال الشيخ: قد قدمت الكلام أن [العلة] الشرعية في التحقيق علامة، إذ ليس فيها باعتبار ذاتها اقتضاء. وقد قدمت أيضًا أن الدليل وإن كان عقليًا (٣٥/ ب)، فإنه لا يدل عدمه','3','230',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1862,'على عدم المدلول، والأمارات الشرعية بذلك أولى.
وأما ما ذكره الإمام من إشعار العلة بالحكم، فهو صحيح، ولكنه لا يفيد فائدة فيما يتعلق باقتضاء النفي الانتفاء. وسيقرر هو بعد ذلك، أن المعنى المشعر بالثبوت، [قد] لا يشعر بالعدم بحال. وهذا هو الصحيح من القول.
وأما ما ذكره من أن [الصفات]، إما أن تذكر على صيغة شرط، أو على قضية تعليل. قال: فإن ذكرت على قضية شرط، فقد حققت أن انتفاء الشرط يشعر بانتفاء المشروط في قاعدة المفهوم. وقد بينا نحن خلاف قوله في ذلك، وأن الشرط لا يتضمن إلا تخصيص المشروط بشرطه، وأنه يصير عند','3','231',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1863,'عدم الشرط غير متكلم [به]. وهو كما مثلناه في اللفظ العام في قوله: (اقتلوا المشركين)، فإنه يتناول كل مشرك. فإذا قال: الحربيين، اقتصر اللفظ عليهم، ولم تبق له دلالة على أهل الذمة، بل لا يكون ناطقًا إلا بذلك القدر خاصة.
فأما أن يقال: إن مفهوم هذا اللفظ النهي عن قتل أهل الذمة، فهذا غلط. ففرق بين أن لا يدل على القتل، وبين أن يدل على منعه. وكذلك [إن] قال: اضرب زيدا مطلقا، اقتضى هذا أن يضربه على أي حالة كان عليها. فإذا قال: إن كان قائما، تقاعد اللفظ عن أن يدل على القاعد. فلا دلالة له عليه بحال. فليس الشرط إلا مخصصا للكلام ببعض ما كان يتناوله عند الإطلاق، فتقتصر دلالته على بعض الأحوال، ولا تبقى له دلالة على الحال الأخرى. وهذا كله مما تقدم','3','232',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1864,'القول فيه في المفهوم.
قال الإمام: ([ولكنا] مع ذلك [لا نبعد] أن [تعلق] المعلق [مشروط] بأفراد شرائط، [بحيث يستقل ذلك المشروط بكل واحد منها]) إلى قوله (والتقدير: أكرمك [إن] جئتني أو لم تجئني). قال الشيخ: هذا الكلام ظاهر الفساد، وذلك أنه جعل للشرط دلالتين، إحداهما مصرح بها، والأخرى ضمنية. فأما المصرح بها، فإثبات المشروط عند ثبوت الشرط. وقد اتفق [ذوو] التحقيق من أهل الأصول أن الشرط لا دلالة له في جانب الإثبات بحال، سواء كان عقليا، كالحياة للعلم، أو شرعيا (٢٧/ ب) كالطهارة للصلاة، أو عاديا كالبنية لقيام العمل بالمحدث. فكيف يجعل دلالة الشرط','3','233',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1865,'على الإثبات مصرحًا [بها]؟ [هذا] محال.
وأما المثال الذي ذكره، فقد وقع فيه غلط، فإن القائل إذا قال: إن جئتني أكرمتك، فنحن لا ننكر أنه إذا جاء استحق الإكرام، ولكن هل لوجود الشرط، أو لأجل الالتزام [المتوقف] على الشرط؟ وكذلك نقول إذا قال: إن دخلت الدار فأنت طالق، فإنها (٣٦/ أ) إذا دخلت وقع الطلاق، لا لاقتضاء الشرط ذلك، ولكن للالتزام والإيقاع من قبل المطلق. هذا بالنظر إلى وضع اللغة وحقيقة الشرط. وأما بالنظر إلى الفقه، فدخول الدار ليس هو سبب الطلاق، إذ لا يناسب ذلك، وإنما السبب تطليق الزوج الموقف على الدخول.
وأما قوله: إنه يدل على الإيتاء ضمنا، فهو غلط أيضا، بل لا دلالة للشرط إلا في جانب الانتفاء، إذ حقيقة الشرط: هو الذي يلزم من انتفائه انتفاء المشروط، وإن لم يلزم من وجوده وجوده. فيا ليت شعري إذا جعلت دلالة الشرط في النفي ضمنية، ودلالته على الإثبات مصرح بها، أي فرق بين الشرط والعلة؟','3','234',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1866,'فإن قيل: إذا قلتم إن الشرط يدل على النفي عند الانتفاء، ولا دلالة [له] إلا على هذا الوجه، حصل غرض الإمام في إشعار العلم المذكور على صيغة الشرط بالنفي، إلا أنه يقول ضمنا، وأنتم تقولون صريحا، فقد حصل مقصوده على وجه أبلغ. قلنا: ليس كذلك، فإنه معنى دلالة الشرط على النفي عندنا: أن الشرط يقتضي تخصيص الكلام المطلق، حتى يمنعه من الدلالة على غير تلك الصورة، التي يثبت الشرط بالإضافة إليها، فلا يكون متكلما بغيرها. [وهو] يريد إنه متكلم فيها بالنقيض، وذلك غير صحيح.
وأما توقفه عن القطع بالنفي عند الانتفاء، من جهة أنه قد يصح تعليق المشروط بأفراد شرائطه، وإذا صح ذلك، فلا يمتنع أن ينتفي [شرطه] ويبقى المشروط [متعلقا بشرط آخر، فهذا أيضا غير صحيح، بل نقطع نحن بانتفاء المشروط] عند انتفاء الشرط. وأما تعليق المشروط بآحاد شرائطه على البدل، فلا نسلم أنه يفوت شرط ويوجد آخر، بل الشرط أحدهما لا بعينه، فأي واحد وجد، لم ينتف الشرط أصلا. وهذا كما قال في خصال [كفارة اليمين]: إن','3','235',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1867,'الواجب واحد لا يعينه. وكذلك ههنا الشرط واحد لا بعينه، فلم يثبت قط المشروط مع فقدان شرطه، ولو ثبت مع [فقدان] [شرطه]، لم يكن شرطًا فيه، فلا وجه لعدم القطع بانتفاء المشروط عند عدم الشرط.
وأما تمثيله النقض بالخلف في المشروط عند وجود الشرط، فقد تقدم أن ذلك لم يكن للشرط، وإنما كان للكذب في الالتزام الثابت عند وجود الشرط.
وأما قوله: لو جرى إكرامه من غير مجيء، كان [هذا] مخالفًا لحكم الشرط من باب الضمن. فهذا أيضا غلط، بل نقول في هذا الإكرام الملتزم: هو الإكرام الذي يترتب على المجيء، وليس في الالتزام الإكرام في حالة، ما يقتضي (٣٦/ ب) منع الإكرام في غيرها. ومن توهم ذلك، فقد غلط غلطًا فاحشًا. فليس الإكرام عند عدم المجيء هو الملتزم بحال. ألا [ترى] أنه لو أكرمه من غير مجيء، ثم اتفق مجيئه، لزمه بحكم التزامه [إكرامه]، اللهم','3','236',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1868,'إلا أن يكون قد التزم إكرامه بشيء معين، [ثم أعطاه] له، فإنه لا يلزمه إعطاء غيره. وما ذاك إلا لفوات (٢٨/ أ) الملتزم. وهو بمثابة ما لو قال: إن جئتني أعطيتك عبدي ميمونا. فاتفق موت العبد ثم جاءه، فإنه لا يستحق عليه إعطاء غيره، بحكم شرطه والتزامه، ولا يمكن إعطاؤه بعد موته، فيسقط الالتزام لفوات محله.
ومثال ذلك: الرجل إذا قال لامرأته: إن دخلت الدار فأنت طالق، ثم طلقها قبل دخول الدار، فلا أحد من العقلاء يقول إن هذا التطليق مناقض لما سبق من التعليق، ولا أنه هو الطلاق المعلق، بل هو طلاق آخر. فإذا ألزم ذلك، ثم اتفق دخولها، طلقت أيضًا بمقتضى [ذلك] الطلاق الملتزم. إلا أن ما سبق من الطلاق أوجب البينونة الكبرى، فينزل ذلك منزلة موت العبد في المثال السابق، فلا تطلق إلا بمعنى أن الطلاق المعلق على الدخول قد سبق، ولكن لفوات المحل [بالبينونة].
وأما قوله: إن جئتني أكرمتك، وإن لم تجئني أكرمتك إلى آخره. فهو عندنا من باب الشرط على حقيقته، ولكنه عدل عن اللفظ المختصر إلى اللفظ الطويل، وذلك أنه لما قال: إن جئتني أكرمتك، اقتصر اللفظ في الدلالة على التزام الإكرام عند المجيء، وصار الإكرام عند [عدم] المجيء مسكوتًا عنه، ثم عبر عنه بعبارة تخصه، أعني المسكوت عنه، فقال: إن لم تجئني أكرمتك.','3','237',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1869,'ونظير ذلك أنه لو قال: اقتل الحربيين، كان هذا كلامًا صحيحًا [ونعتًا] معتبرًا. فلو قال مع ذلك: والذميين، لم يخرج النعتان عن كونهما نعتين على حقيقة اللغة، ولكنه عدل عن اللفظ المختصر إلى اللفظ [الطويل]، إما لعدم فصاحة، أو لغرض من الأغراض. فقول الإمام: إنه خارج عن باب الشرط باتفاق أهل اللسان. ليس على ما قال بحال. والله المستعان.
قال الإمام: (ونحن الآن نقول: من حكم كل ما يثبت علة أن ينعكس، [وأن يكون لوجوده على عدمه مزية]) إلى قوله (فهذا الآن يستدعي مقدمة في النقض). قال الشيخ: قوله: من حكم كل ما ثبت علة أن ينعكس، وأن يكون لوجوده على عدمه مزية. هاتان الجملتان إحداهما ممنوعة، والأخرى يقع فيها استفسار. أما الممنوعة، فهو قوله: إن كل علة لابد أن تنعكس. وهو يريد بذلك','3','238',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1870,'أن يقتضي انتفاؤها انتفاء الحكم. وهذا غير مسلم، وهو محل النزاع، وقد بينا مختارنا فيه.
وأما قوله: (٣٧/ أ) وأن يكون لوجوده على عدمه مزية. إن أراد الاكتفاء بإبداء مزية من حيث الجملة، فنحن نسلم ذلك. وإن أراد بالمزية الإشعار بالنفي، فهو الكلام الأول. وأما المزية التي نسلمها، فهو أن العلة إذا وجدت، لزم ثبوت الحكم، وإن انتفت، لم يثبت الحكم، لا لأن نفيها اقتضى النفي، ولكن لقصور الإثبات على وجدان السبب. فلسنا نسوي بين وجود العلة وعدمها على حال. ولكنها إذا عدمت، لم يثبت الحكم المعلق عليها.
هذا هو الصوات عندنا في ذلك. والكلام مفروض فيما إذا وقع التعلق بمحض نفي العلة. وإن قدر مقدر أن نفيها يشعر بنفي الحكم، أو بثبوت نقيضه، فهذا أمر زائد على مطلق العكس. فليتأمل الناظر هذا، فهو من لطيف الكلام.
وقوله: [فإن] لم يصح تعليل الحكم بعلتين، فيتعين العكس. [هو كما قال، ولكنه عندنا يرجع إلى امتناع ثبوت الحكم، لا بتوقيف ولا بعلة. فإذا','3','239',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1871,'عدما جميعا، لم يثبت الحكم، وعنده المقتضي لذلك عدم العلة المنفردة. قال: ولكنه لو امتنع العكس لخبر أو (٢٨/ ب) إجماع، فهذا الآن يستدعي مقدمة في النقض.
قال الإمام: (فلو اطردت العلة على صور المعاني، وتلقتها [علة] تخالف حكمها في مقتضى الطرد، وكان حكمها غير معلل) إلى قوله] (أن هذا غير مبطل للعلة). قال الشيخ: هذا الذي ذكره الإمام [هنا] صحيح، ولكنه لم يبين وجهه على مقتضى قاعدته إلا بقوله: فإن العكس في حكم الوارد على ضمن العلة، ويقول: إذا فات الأمر الضمني، لم يقتض ذلك اختلالا بالأصل. وأين هذا من قوله: إن جئتني أكرمتك، وإن لم تجئني أكرمتك؟ فقد قال هو: إن هذا خارج عن باب الشرط باتفاق أهل اللسان.
فإن كان إنما فات الأمر الضمني، فكان ينبغي على هذا [التقدير]، إذا','3','240',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1872,'ثبت الحكم مع زوال العلة، وإن كان بتوقيف أو غيره، أن يكون ذلك خارجًا عن أبواب التعليل، كما كان إثبات المشروط عند فقدان الشرط يخل بأبواب الشرط، ويفوت معقوله. نعم، نحن نقول: إذا ثبت الحكم مع فقدان العلة، إما بعلة أخرى، أو بتوقيف أو بإجماع، لم يقدح ذلك في العلة بحال، لأنا قد بينا أن ما وراء محل التعليل، انتفاء الحكم فيه، لا لحقيقة العلة، ولا لاقتضائها النفي، ولكن [لانتفاء] الحكم، لتعذر ثبوته بغير دليل، فإذا وجد دليل يقتضي الثبوت، لم يتعرض هذا الدليل للعلة على حال. أما في حال وجودها، فقد ثبت حكمها.
وأما في حال عدمها، فلا دلالة لها، حتى يقع في ذلك مناقضة توجب ضعفًا أو بطلانًا. نعم، الذين ذهبوا إلى أنها تبطل على كل حال، هم الذين طردوا أصل الإمام في قوله: إذا قال: إن جئتني أكرمتك، وإن لم تجئني أكرمتك. فهذا قد قال الإمام: إنه خارج عن باب الشرط. وكذلك يلزم هذا القائل أن يقول: إذا قال: إن (٣٧/ ب) أسكر الشراب، فهو حرام بعلة الإسكار، وإن لم يسكر، فهو حرام. فهذا خلاف التعليل. والتقدير: هو حرام على كل حال، أسكر أو لم يسكر.','3','241',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1873,'قال الإمام: (ولكن ينشأ من هذا الموضوع فصل جدلي [ممتزج بأمر ديني]) إلى قوله (إيضاح الغرض في هذا الفن). قال الشيخ: الأمر الممتزج من الدين والجدل، هو الانعكاس. أما ما يتعلق بأمر الدين، فهو أن المجتهد إذا علل الحكم بعلة، وانتفت ولم يثبت سبب آخر يقتضي استمرار الحكم، فلابد له من النفي. أما عندنا، فلتعذر بقائه غير مرتبط بسبب. وأما عند الإمام، فلإشعار العدم بالعدم، فلابد على الرأيين جميعا من الالتفات إلى انتفاء العلة على هذا التقدير.
وأما الأمر الثاني [الجدلي]، فهو إذا ثبت أن امتناع الانتفاء لخبر أو إجماع، فهل يقدح هذا في صحة العلة أو لا يقدح؟ أو يفرق بين تخلف الطرد وتخلف العكس؟ هذا مختلف فيه، والناس فيه على ثلاث فرق: فرقة ذهبت إلى الإفساد في الموضعين، وفرقة ذهبت إلى الصحة في الموضعين، وفرقة ذهبت إلى الفرق، فقالوا [بالإفساد] في البعض، ولم يقولوا ذلك في تخلف','3','242',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1874,'العكس. وقد حققنا أنه لا وجه للإفساد في العكس بحال. وكذلك المذهب الصحيح، أنه لا يضر ذلك في باب النقض، إذا كانت المسألة الناقضة مستثناة، [أو ثبت] الحكم فيها بعلة راجحة على علة المعلل. واستقصاء القول في ذلك يأتي في باب النقض. فأما على ما اخترناه (٢٩/ أ) من أن الانعكاس ليس من مقتضى العلة بحال، فلا يكون بيان كونها لم تنعكس، اعتراضًا عليها، فلا يلزم الجواب في الجدل.
وأما على ما اختاره الإمام من أنه يكون ذلك من ضمن العلة، فقد يتخيل أنه إذا بطل ما هو من ضمنها، لم تثبت صحتها في نفسها، ويكون [ذلك] على [هذا] تخلف الانعكاس في بعض الصور يقتضي فسادًا في العلة، وفي بعضها لا يقتضي ذلك، كما أن النقض تارة يكون قادحا، وتارة لا يكون قادحا،','3','243',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1875,'والناقض يقول: هذا النقض من قبيل النقض المبطل، والمستدل يقول: بل من الصنف الآخر.
وكذلك يتنازعون هذا النزاع في العكس المقدر، فهل يصار إلى قول المعلل في الموضعين، أو إلى قول المعترض فيهما، أو يقع التفضيل؟ ذهب طائفة إلى أن المعلل لابد أن يذكر العذر في الموضعين جميعا، وإن لم يفعل ذلك، كان (٣٨/ أ) منقطعا. وهؤلاء هم الذين يرون أن بعض صور تخلف العكس، لا يتبين منها فساد العلة.
وأما الذين ذهبوا إلى أن العكس شرط على الإطلاق، فلا يمكنون من العذر على حال.
وقالت طائفة: يفتقر إلى بيان عذر النقض دون العكس. فهذا هو اختيار الإمام، وقد ذكر هو شيئا يتعلق بآداب الجدل، فقال: سر الجدل قصر الكلام على محل النزاع، حتى يجدي [ويثمر] على قرب. وإذا ثبت أن تعذر','3','244',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1876,'العكس في بعض الصور لا يضر، أمكن المعلل أن يقول: هذا العكس من هذا القبيل. فإذا انتقلت إلى [تقريره] وإبداء السبب المانع من الانعكاس، كان ذلك خروجا على حكم المسألة.
وإذا توجه النقض عليه فقال: يصح أن يكون هذا من قبيل النقض غير المضر، وأمكن أن يعكس هذا عليه. وإذا قال: إن انتقلت إلى إثباته وتقريره وبيان كونه غير مضر، كان ذلك خروجا عن حكم المسألة. قيل له: [ليس] كذلك، فإنك [إنما] تلقيت الحكم من عموم العلة، وقد ظهر أنها غير عامة، فثبت أن الدليل لم يتحقق، والكلام في تحقيقه غير خارج عن مقصود المسألة. ولا يمكن أن [يقول]: [إنما] تلقيت حكم مسألة النزاع من عدم العلة. فهذا هو الفرق عنده في مقام الجدل، بين إلزام العذر المانع من الطرد، وإبداء العذر المانع من العكس.
قال الإمام: (وإذا [تقرر] [أن الحكم الواحد لا يعلل بأكثر من علة واحدة، ] فالحكم الثابت [مع انتفاء العلة إن لم يستند إلى نص أو إجماع،','3','245',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1877,'فهو مساو للحكم الأول [في الاسم]، ومخالف له في المأخذ والحقيقة]) إلى قوله (وقد حان الآن أن نخوض في قياس الشبه). قال الشيخ: هذا الذي تكلفه [الإمام]، إنما هو لما قرره من امتناع تعليل الحكم بعلتين، ففرض مسائل يتأتى له الجواب عنها. وأما المسائل العسرة، كما صورنا في الخؤولة والعمومة، والبول والغائط، والمس واللمس، فإنه لم يتعرض له بالفرض.
وأما الولاية، فإنها تثبت [بالصبا] والجنون جميعا. وقوله: إحداهما استصلاحية، والأخرى ضرورية، يقال: له: هذا نظر في العلة، وأما حقيقة الولاية، فلا اختلاف فيها، ولا تعدد بحال، فإن معنى الولاية: منع استبداد الشخص بالتصرف وإقامة غيره ناظرا له.','3','246',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1878,'وأما قوله: إن كان لا يميز، يعني الصغير، فهو المجنون بعينه. هذا ليس كذلك عند الفقهاء، ولا الأطباء، فإن الجنون: حالة تثبت للشخص، وليس الجنون عبارة عن فقد العقل بحال، فإنه لو كان كذلك، لكان النائم والغافل [مجنونا]. [وإذا] رجع [المجنون] إلى صفة تثبت [للمحل]، فهذه الصفة لا تلزم الصبي الذي لا يميز، (٢٩/ ب) فإذا اتصف بها مع صغره وعدم تمييزه، فعلام يحال حكم الولاية؟ فلا تصح الحوالة على أحدهما دون (٣٨/ ب) الآخر، لظهور تأثيرهما جميعا. ولا يصح أن يجعلا [جزئي علة]،','3','247',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1879,'لثبوت الاستقلال لكل واحدة منهما. فكل ما ذكر في منع تعليل الحكم بعلتين غير صحيح.
وأما قوله: إن الشافعي لا يرى توريث ذي قرابتين أقرب من الأخرى، وما ذاك لاستحالة تعليل حكم بعلتين، وإنما ذاك لأن البعيد نزله الشرع منزلة [القريب] عند عدم القريب، وإذا نزل منزلته، لم يصح أن يرث معه. وهو كما قدمنا في امتناع الجمع بين الشبه والمشبه به في صورة واحدة. وسيأتي لهذا مزيد تقرير في عدم التأثير. والصواب بعد كل [تقدير]، أنه لا يمتنع تعليل الحكم الواحد بعلتين.','3','248',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1880,'قال الإمام رحمه الله: (القول في قياس الشبه-[ومن أهم ما يجب الاعتناء [به] تصوير قياس الشبه وتمييزه عن قياس المعنى والطرد]) إلى قوله (ولعلنا أن نأتي في ضبط مداركها بأقصى الإمكان، إن شاء الله تعالى). قال الشيخ - رضي الله عنه -: قياس الشبه يعني به الأصوليون: الجمع بوصف لا يناسب الحكم، ولا يقبل النقيض. وهذا من باب التخصيص بالعرف، وقصر الاسم المشتق على بعض ما يقتضيه إطلاقه. فإن القياس الطردي [أيضا] [أشبه] الأصل والفرع [في] وصف أو أوصاف، فهما متشابهان باعتبار ذلك، ولكن يعبر عن الأمور بأخص أوصافها، فإن كان الوصف لا خاصية له سوى الاطراد، قيل طردي، لا بمعنى أنه يختص بذلك، لكن بمعنى أنه لم','3','249',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1881,'يوجد فيه سوى ذلك، وإلا فالمعنى المخيل أيضا مطرد، وكذلك الوصف الشبهي. ولما كانت الخاصية التي ثبتت للطرد الاطراد خاصة، فالخاصية التي تثبت للوصف الشبهي، يمتاز بها عن الشبه والطرد مشكلة. وقد قال القاضي في ضبط تلك الخاصية: هي إيهام الاشتمال على مخيل. وفي ذلك نظر من جهة أن الخصم قد ينازع في إيهام الاشتمال على مخيل، إما صادقًا وإما معاندًا. ولا يمكن [التقدير] عليه، فعضم الأمر من هذه الجهة، [وغمض] الفصل بينه وبين المخيل والطرد. ولست أرى في مسائل الأصول مسألة أغمض من هذه.
وقال قائلون: الجهة التي يمتاز بها الشبه عن الطرد، عبارة عن أوصاف بعدت عن أن تكون مقصودة للشرع في ورده وصدره، وعلم أو ظن بعدها عن الأحكام كالألوان وغيرها. فإن استقراء [الشريعة] وردًا وصدرًا يبين إضراب الشرع عنها. وهذا أيضًا عسر، فإنه (٣٩/ أ) إذا وقع النزاع، فهل هذا الوصف من قبيل ما أضربت الشريعة عنه أم لا؟ لم يقدر على الدليل، ويصار إلى دعوى الضرورة. وهذا قد يتفق في بعض الوصاف الطردية، فأما في جميعها، فلا يقدر','3','250',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1882,'عليه. فأما إذا قلنا: طهارة بالماء، فلم تفتقر إلى النية كإزالة النجاسة، أعني على طريق أبي حنيفة. فإذا قلنا هذا طردي، منع الخصم ذلك. ودعوى الضرورة في هذا المقام غير ممكنة. والوجه النظري [للفرق] بين الطرد والشبه لم يذكر حتى يكون هو المحكم (٣٠/ أ).
وقال قائلون: الوصف الشبهي: هو الذي يقتضي أن يكون الأصل والفرع- إذ اشتركا- في المعنى المناسب، وإن لم نطلع نحن على عين ذلك المعنى. وهذا هو اختيار أبي حامد، وهو تلخيص كلام القاضي حيث قال: هو الذي يوهم الاشتراك فيه الاشتراك [في] مخيل. وتحقيقه هو أنهم قالوا: كل حكم لابد له من مصلحة، ومصلحته لا تعدو أوصاف محله. وهذا قد تقدم الكلام عليه، وهو أنه إن لم يثبت ذلك على القطع في الجميع، فهو الغالب في الشريعة بلا ريب. فإذا ثبت ذلك كليا أو غالبا، فالمصلحة قد تكون مكشوفة، وقد تكون خفية، [فإن كانت مكشوفة، ووقع الجمع بها، فهو المناسب، وإن','3','251',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1883,'كانت خفية]، فبعض الأوصاف يدرك بعده عن المصلحة الخفية، إما قطعا، كاللون بالإضافة إلى تحريم التفاضل في البر، وإما ظنا، ككونه مقدرًا في مسألة تحريم التفاضل، بخلاف كونه مطعوما أو مقتاتا.
فإنا نقول: إنما حرم الشرع التفاضل في البر لمصلحة الله [تعالى] يعلمها. ويتبين بالاستقراء أن تلك المصلحة ليست ككونه [مدورًا] أو مستطيلًا. هذا مما يعلم ضرورة كونه مقدرًا قريبا من ذلك، إذ يرجع الأمر فيه إلى معرفة مقدار الشيء، وليس كذلك كونه مطعوما، إذ ربما يكون [السر] في كونه به [قوام الآدميين]. وإذا أضيف الحكم إلى ذلك الوصف انتظم، بخلاف الأول.
قال أبو حامد: وإن لم يرد الأصوليون [هذا] بقياس الشبه، فلست أدري ما الذي أرادوه، ولا [بماذا] فصلوه عن الطرد المحض وعن المناسب؟','3','252',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1884,'قال: وهذا هو الذي نريده نحن بقياس الشبه.
وقال قائلون: الوصف الشبهي: هو الذي يلائم الأوصاف التي عهد من [الشرع] إناطة الأحكام بها، فإنه كما عرف من [الشارع] ربط الأحكام بأوصاف مناسبة، ثبت تأثيرها بالنص والإجماع، ثم وجدت أصواف مناسبة قريبة منها، (٣٩/ ب) فسميت ملائمة. فكذلك عرف من الشرع ربط الأحكام بأوصاف لم تظهر مناسبتها، ثبت تأثيرها بالنص، كمنع بيع الرطب بالتمر، بالنقصان المتوقع عند الجفاف، وإيجاب الوضوء من مس الذكر، إلى غير ذلك من الأوصاف التي ظهر تأثيرها، ولم تظهر مناسبتها، قالوا: فالأوصاف الشبهية هي الملائمة لهذه الأوصاف المؤثرة التي لم تظهر مناسبتها، كما أن الاستدلالات المرسلة هي الأوصاف المناسبة الملائمة للمصالح التي ثبت اعتبارها، فالشبه يرجع إلى ملاءمة أوصافٍ لم تظهر مناسبتها، لأوصافٍ مؤثرة [لم تظهر مناسبتها].
هذا تلخيص قول هؤلاء القوم. وهو قريب من الطريق الأول، ولكن','3','253',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1885,'بينهما فرق، وهو أنه على الطريق الأول لا تثبت غلبة الظن بالوصف، إلا بعد ورود الحكم، فالنظر إلى إحدى الأوصاف يتضمن مصلحة. وعلى الطريق الثاني، قد تظهر مداناة الوصف للأوصاف غير المناسبة قبل ورود الحكم، كما تظهر المداناة في الأوصاف المناسبة قبل الحكم في الاستدلال المرسل.
[ولكن يبقى ههنا نظر دقيق، وهو أن الناس قد اختلفوا في الاستدلال المرسل]. فمن رده مع ظهور مناسبته، لعدم شهادة [الأصول] له، فلأن يرد الوصف الشبهي، إذا لم يرد [الحكم] على وفقه أولى. ومن [قبل] الاستدلال (٣٠/ ب) المرسل لأجل المداناة، فهل يقبل الوصف الشبهي حتى يترتب الحكم عليه ابتداء؟ هذا [غامض]، ولم أر [نصًا] على جوازه. ولم قيل به لم يبعد.
ومثاله: ما لو [لم] يرد الشرع بإيجاب النية في التيمم، وورد [بإيجابها','3','254',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1886,'في] الوضوء، تبينا أنه ليس له غرض عاجل، ولأمكن أن تثبت النية فيه، بناءً على استدلال كلي. هذا [نهاية] ما ذكره الأصوليون في تصوير قياس الشبه. وهذه الطرق كلها لا يمكن [تقريرها] على الخصم بالبرهان، لأنها كلها ترجع إلى أمر يقع في النفس من الاستقراء، فلهذا انقسم الناس من القبول والرد، والرادون قالوا: هذا يفضي إلى تكافؤ الأقوال، وسقوط الاستدلال والتحكم في المقال، وذلك يبطل أبواب الاحتجاج.
وقال قائلون: يقبل ذلك، فإن في منعه إبطال أمر يظنه [المعلل] ظنًا لا يغالط فيه نفسه. وسلكوا أيضا مسلك إثبات القياس في كثرة الأحكام وقلة المعاني. وقالوا: لا يجري على الإخالة الصحيحة [العشر] من المسائل.
وهذه الطريقة الغامضة، إنما يدريها من توغل في مسائل الشريعة، وترقى عن رتبة الشادين فيها، فيفتقر (٤٠/ أ) إلى حصر الأقضية عن أهل الإجماع، وتصفح وجوه المصالح على الإطلاق، ثم عرض المسائل، فلا يصادف المناسبة في كثير من المسائل، وتوجد الأحكام الكثيرة المختلفة، فيتبين مأخذًا آخر سوى المصالح.','3','255',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1887,'وهذا غامض جدًا، فإن جهات المصالح قد تختلف باختلاف الطباع والنفوس، والنظر في العواقب، وما يتعلق بالسياسات. [فرب] أمر لا يرى فيه شخص مصلحة، ويرى غيره فيه مصالح. وكذلك يقال في جانب المفاسد، لا جرم كان هذا الطريق لا ينتفع به إلا [المجتهدون]. فإنه قد يحصل من كثرة الاستقراء، ما يزيل هذا الاحتمال، ورب احتمال ينقدح، فإذا وقعت الكثرة [انمحى] أثره [من القلب] بالكلية، فإنا إذا رأينا [إنسانا] لازمه المرض فشرب له شيئاً فسكن، احتمل أن يكون لأجل ما تناوله، واحتمل أن يكون زال بغير ذلك. فإذا تعوطي مرة ثانية، وزال الألم، قوي في النفس أنه لأجله. فكلما ازداد كثرة، كلما ازداد الظن قوة، حتى ينتهض الأمر إلى حدٍّ يحصل العلم به.
ومن هذا القبيل حصول العلم مرتبا على خبر التواتر. واعترف الإمام رحمه الله بأن التحديد لا يتأتى، وإنما يرجع الأمر إلى التمثيل. وهذا أمر مشكل، فإن الأمثال لا تستقل حججا على الخصوم، وللخصم أن ينازع في المثال الذي نقول نحن إنه شبهي، ويقول [هو]: هو الطردي، وليس عندنا حقيقة كلية تعرض الآحاد عليها، لا جرم لما فهم ذلك، قال في المثال: إنه لا [ينتهي] إلى مقدار غرضنا.','3','256',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1888,'والمثال الذي ذكره [ذكر أنه] اشتمل على الشبهي المعلوم، وعلى المظنون، وعلى الطرد المردود. فأما المعلوم: فكإلحاق الوضوء بالغسل، والمظنون: كإلحاق الوضوء بالتيمم، والطرد كإلحاق الوضوء بإزالة النجاسة. أما إلحاق الوضوء بالغسل، والغسل بالوضوء فمعلوم، وعلم ذلك من استقراء الشرع وردًا وصدرًا، وأنه لم يفرق بين هاتين الطهارتين، ولا نظر في (٣١/ أ) الشرع إلى العموم والخصوص فيما يتعلق بالنيات والآلات، [فإنا] على قطعٍ نعلم أنه لم يفرق الشرع بين [محل] اليد التي عين فرضها إلى المرفق، وبين الرجل التي جعل فرضها منتهيا إلى الكعب. وأما الوضوء والتيمم، فقد يتأتى النزاع في ذلك، وقد يكون لكونه ماء [وترابا] أثر في الفرق والاستغناء عن النية، ولكن يظهر تقاربهما لغلبة التعبد.
وقول الإمام: فإذا لم يحصل علم، وعارض العلم المطلوب نقيضه من ظن أو شك، فهذا مما يغلب على الظن. عبارة مختلفة، فإنه إذا حصل شك (٤٠/ ب)، لم تبق غلبة الظن، فإن الظن والشك يتناقضان. فلئن قال: يطلق الشك ويراد به غلبة الظن تجوزًا. قلنا: يمنعه من ذلك حرف العطف بـ (أو)، فإنه قال: وعارض العلم المطلوب نقيضه من ظن أو شك. والعبارة ليست [جيدة] بحال، والمراد: إذا تعذر العلم وحصلت غلبة الظن.
وأما [ما] مثل به [للطردي] من إلحاق الوضوء بإزالة النجاسة،','3','257',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1889,'فلا يظهر أن هذا من الطرديات المعلومة بحال، ولا لعمري- إذا وقع الإنصاف- من [المظنونة] قويًا، وقد [يظن] أن المراد بالوضوء النظافة من حيث الجملة، كما في إزالة النجاسة. ولما تخيل الإمام هذا قال: فإن [تأصل للخصم تشبيه]، رددنا الكلام إلى الترجيح، كما [سننبه] على مأخذه. فلم يتحقق عنده أي طردي ولابد.
[وقول الإمام]: (ونحن نزيد [فنقول] إلحاق الشيء بالمنصوص عليه لكونه في معناه، [متقبل مقطوع به]) إلى قوله ([فيما يبغيه الطارد','3','258',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1890,'ويدعيه، والشبه متميز عن هذا]). [ثم أخذ يذكر المثال الذي قدمناه، وقدمنا الكلام عليه.
ومقصود الإمام بهذا الكلام، أنه لا يقتصر القياس على ظهور المعنى، فإنه قد يعلم الإلحاق مع عدم المعنى، فكيف يمتنع أن يثير غلبة الظن مع فقدان المعنى؟ وهذا الذي ذكره لا يغني في هذا المقام، فإنه لا ينكر الإلحاق مع عدم ظهور المعنى، وإنما المشكل تعيين وصف للجمع، مع كونه لا تظهر مناسبته.
[فقد] يدعي الجامع أنه من قبيل الأشباه المتضمنة للمعنى الخفي، ويقول الخصم إنه من قبيل [الطرود]، والتي لا تغلب على الظن بحال. هذا هو الأمر الغامض الذي لم يتحرر انفصال عنه. وأكثر الأصوليون في هذا المقام','3','259',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1891,'من ذكر الأمثال، وهذا لا يفيد شيئا، وللخصم أن ينازع في كل مثال. فإما أن يبدي المعنى، وإما أن يقول هو من الطرود، وغاية الأمر التسليم، فأين الدليل على الأصل؟','3','260',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1892,'وقول الإمام: (ومما ذكره القاضي في تمثيل تعارض الأشباه، القول في العبد المملوك) إلى قوله] ([بأقصى الإمكان، إن شاء الله تعالى]).
فنقول: قد قدمت أن الأمر الغامض في قياس الشبه، ربط الحكم بوصف لم تظهر مناسبته في الشريعة، ولم تدل النصوص على النصب فيه، [ولكنا] نتخيل أنه قالب مصلحة خفية لم تنضبط لنا حقيقتها. هذا هو السبب الغامض، الذي لأجله تحيرت الناس في هذا القسم.
فأما إذا ظهر كون الوصف مناطًا بظهور [مصلحة] [أو تأثير]، فليس من أبواب قياس الشبه، وليس المخيل معتبرًا لعينه، بل اعتبر لقيام الدليل على أنه مناط الحكم. فإذا ثبت كونه مناطًا بالنص أو [بالإجماع]، فهو أولى من ظهور الإخالة (٣١/ ب) من غير نزاع. فإذا تقرر هذا، فقد توهم قوم في أنواع أنها من الشبه المتنازع فيه، وليس منه، وهي ثلاثة أقسام:','3','261',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1893,'الأول- ما عرف المناط منه بالنص على الجملة، ورد المجتهدون فيه إلى تحقيق المناط بالرأي، ولم يعين لهم المناط، فردوا [فيه] إلى الاجتهاد. مثاله: الحكم بالمثل في جزاء الصيد، قال الله تعالى: {فجزاء مثل ما قتل من النعم}. ولم ينص على المثل تعيينا، ويعلم أنه ليس في النعم ما يماثل الصيد من كل وجه، فعلم أنه أراد ما تدرك مماثلته إياه بنوع من الاجتهاد. وكيف يكون هذا من الشبه المختلف فيه، وضرورة كل شريعة المصير إلى هذا الجنس؟ وكذلك مهر المثل، ونفقة المثل في المتلفات. فإنا إذا أوجبنا مهر المثل، لم ينص لنا على ذلك، بل رددنا إليه بنوع من الاجتهاد. وكذلك جميع أبواب تحقيق المناط.
وقد فسر بعض (٤١/ أ) الأصوليين الشبه بهذا. فإن أراد أن هذا النوع هو المتنازع فيه، والذي عظم الخطب بسببه، فليس كذلك، وإن أراد أنه','3','262',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1894,'[يسمى] هذا شبهًا، فلا يناقش [فيه]، وإنما الغموض [في] قاعدة الشبه، جعل الوصف الذي لم تظهر مناسبته مناطًا، من غير أن ترشد النصوص إليه، ولا حملت الضرورة [عليه].
القسم الثاني: ما ذكره القاضي [رحمه الله] في أن العبد هل يملك أم لا؟ على الوجه الذي قرره. وهذا أيضًا لا يصح أن يكون من أبواب الشبه المختلف فيه، لأنا على علم أن الحر يملك، وأن البهيمة لا تملك، والعبد إنسان كالحر، ومال كالبهيمة، فإذا تعارض المناطان وجب الترجيح، فإذا ثبت رجحان أحد المناطين، وجب الاعتماد عليه، ويكون هذا من باب قياس العلة، لا من أبواب الشبه بحال، فإنه إن ترجح جانب الإنسان، انتفت علة منصوص عليها، ومجمع عليها. وكذلك إن ثبت رجحان شائبة المالية، فليس هذا من قبيل قياس الشبه المتنازع فيه.','3','263',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1895,'القسم الثالث: أن توجد شائبة من كل مناط، ولا يوجد كل مناط على كماله، فهذا نمثله ثم نتكلم عليه. ومثاله: أن اللعان مركب من الشهادة واليمين، وليس بيمين محض، لأن يمين المدعي لا تقبل، والملاعن مدعٍ، وليس [بشاهد]، [لأن] الشاهد يشهد لغيره، [وهو] إنما يشهد لنفسه، وفي اللعان لفظ اليمين والشهادة، فإذا كان العبد من أهل اليمين، لا من أهل الشهادة، وترددنا في أنه هل هو من أهل اللعان؟ وبان لنا غلبة إحدى الشائبتين، فينبغي لنا أن نتبع الحكم الشائبة الغالبة. وليس هذا من الشبه المختلف فيه. وكذلك الظاهر لفظ محرم، وهو كلمة زور، فتدور بين القذف والطلاق، وزكاة الفطر مترددة بين المؤنة والقربة، والكفارة تتردد بين العقوبة والعبادة، [ففيه تشابه بينها]. [وإذا] تناقض حكم الشائبين، فإن الحكم يعلق على الشائبة الغالبة، وهذا فيه غموض كثير، وهو إقامة بعض المناط مقام المناط الكلي، حتى [يعلق] الحكم عليه. وكان من الممكن أن تقدر هذه مسألة أخرى، ويطلب لها حكم آخر، ولا يلتزم [ترتيب] أحد حكمي المناطين.','3','264',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1896,'فلابد ههنا من مزيد تقرير، فنقول: [هذا] يكون مختصا عندنا بما لم يكن (٣٢/ أ) لإخلاء الواقعة عن أحد الحكمين، كما ذكرناه في الأمثلة السابقة. فإنه لا يمكننا أن نقول: العبد لا يلاعن ويلاعن، لاستحالة الجمع بينهما، والخلو عنهما. فإذا تحتم أحد الحكمين، لم يكن (٤١/ ب) بد من إثبات الحكم على وفق الشائبة الراجحة [أو المرجوحة]، وإثباته على وفق المرجوحة باطل، فلم يبق إلا إثباته على وفق الراجحة، فتنزلت الراجحة لهذا الصورة منزلة المناط الكامل. هذا وجه تقريره، وهو على هذا أشبه الأقسام الثلاثة بمأخذ الشبه، فإنه يقول: الشرع إنما أخرج العبد عن أهلية الشهادة لمصلحة علمها، وإنما مكنه [الشرع] من اليمين [لمصلحة] اقتضت ذلك، فإذا تعارض الأمر في مسألة اللعان، وكان [أمر] اليمين فيه أغلب، غلب على ظننا أن مصلحة اليمين أغلب عليه، فليمكن من اليمين لهذا [التقدير].
ولقائل أن يقول في مقابلة هذا الكلام: ما المانع من الوقف في مسألة العبد، والامتناع من جزم الفتوى نفيًا وإثباتًا، حتى يوجد الدليل على ذلك؟ وهذا أولى من أن نقول: بعض المناط يعلق الحكم عليه، إلا أن يتبين بالدليل أن هذا الأمر المقترن بمقتضى اليمين لا أثر له في الفرق على حال، فيكون الحكم ثبت على المناط الكامل، وهذا أشد على هذا الطريق.
وأما ما ذكره الإمام: من كون كل واحد من الفريقين منخرط في سلك المعنى [المخيل] المناسب، فليس الأمر كذلك عندي، بل الحق أن','3','265',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1897,'المسألة لا تظهر فيها المعاني المناسبة، فإنا إذا قلنا: النطفة في الرحم يثبت لها الملك بالوصية والإرث، وطلب الفقه في ذلك، لم يظهر فيه إلا أمر [غيبي] يتعلق بتشريف بني آدم. وإذا قلنا: إن الفرس لا يملك، لم يكن في هذا شيء يظهر من أبواب المناسبات، ولو أضاف الشرع الملك إلى الفرس، [لم ترد العقول ذلك]. كما أضافه للمجنون، فإن ذلك تلقي بالقبول، وقد رجع الإمام بعد هذا إلى ما ذكره القاضي، من أن هذا واقع في أبوبا الأشباه.
قال الإمام: (ومما أجراه القاضي في تصوير الشبه أن قال: قياس المعنى: هو الذي يستند إلى معنى يناسب [الحكم المطلوب بنفسه من غير رابطة]) إلى قوله ([ثم الكلام يقع وراء ذلك] في الرد والقبول [وإثبات] الحق). قال الشيخ: هذا الذي ذكره القاضي وهو الشبه على الحقيقة عند القائلين به، فإنهم بنوا الأمر فيه على أن الله تعالى في كل حكم سرًا ومصلحة لا تعدو محله، على ما تقدم [ذكرنا له]. وذلك المعنى قد يكون منكشفًا، وقد','3','266',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1898,'يكون خفيًا يتضمنه بعض الأوصاف، فما بعد عن مقاصد الشرع بالكلية، لم يكن قالب تلك المصلحة المعينة ولا مظنتها. وإذا قرب، ظن أن [ذلك] الوصف القريب هو المتضمن للمعنى المخيل، [ككون] البر مقتاتًا، أو (٤٢/ أ) الذهب منقودًا قيمًا للمتلفات، فهذا أقرب من كونه موزونًا، أو كون البر مكيلًا.
وأما قول الإمام: إنا نجري قياس الشبه فيما لا يعقل معناه، فاعتراض ضعيف، لأنه من فهم من قول الأصوليين: لا يعقل معناه، أي لا معنى فيه. وليس هذا بصحيح، لا بالنظر إلى غرض المطلقين، ولا بالإضافة إلى مدلول اللفظ. أما غرض المطلقين (٣٢/ ب)، فإنهم أرادوا بذلك ما لم يظهر لهم','3','267',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1899,'المعنى فيه، ولا يلزم من عدم الظهور، [القطع] بالعدم.
وأما [باعتبار] مدلول اللفظ، فإنهم [لم يقولوا] يجري قياس الشبه فيما [لا معنى فيه]، بل قالوا فيما [لا] يعقل معناه، فمن أين يفم هو نفي المعنى بالكلية؟
وأما قوله آخرًا: [إنه] إذا ظهر الوصف الذي يغلب على الظن، أن الأصل والفرع إذا اشتركا فيه، فهما مشتركان في المعنى المناسب أن هذا الظاهر في معنى أول رتبة، [رتبته] من النظر، وعلى الناظر الاستتمام، فليس كذلك، بل لم يقدر الناظر في هذا المقام إلا على [هذا] المقدار،','3','268',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1900,'وقد أيس من الوقوف على عين المعنى لخفائه. وهذا بمثابة تعليقنا القصر في السفر على مرحلتين، ولم يبحث عن حقيقة المشقة. وكذلك في سرقة المال النفيس، فإنه ينوطه بربع دينار، وإن لم يبحث عن جهة كون ربع الدينار نفيسا، للعجز عن الوقوف على ذلك. وكذلك أيضًا إذا اطلع على الوصف الشبهي الذي يظن اشتماله على المناسب الخفي، لم يتكلف الناظر بعد ذلك بحثًا.
قال الإمام: (ومما أرى تقديم رسمه ربط الأحكام بالأحكام، [وهو كثير الجولان والجريان في أساليب الظنون]) إلى قوله (وقبوله [وما يلتحق به]، ونوضح الحق عندنا). قال الشيخ: ذكر الإمام قياس الدلالة، وجعل خاصيته أن يكون الجامع حكما، ثم قال: قد يكون الحكم مناسبا للحكم المعلق عليه، وقد يكون مناسبا. وهذا [كالجمع] بالأوصاف غير','3','269',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1901,'الأحكام، وليس للتقسيم عنده- على هذا الرأي- فائدة على حال، بل يرجع الأمر إلى قياس المعنى تارة، وإلى قياس الشبه [أخرى].
وأما كون الجامع حكما أو غير حكم، فهذا لا أثر له عنده. وأما غيره من الأصوليين، فلم يسلكوا هذا المسلك، ولكن سلكوا مسلكًا آخر فقالوا: الجامع إما أن يكون [معنى، وإما أن يكون] حكمًا دالًا على المعنى، وإما أن يكون شبها محضا. فإن كان معنى، فهو المخيل، وإن كان حكما دالا على المعنى، فهو قياس الدلالة، وإن لم يكن معنى، ولا دالا على المعنى، فهو الشبه. فتكون الأقسام على هذا التقدير ثلاثة أقسام. وهذه العبارة وإن كانت محومة على المقصود، فليست كاشفة عن الحقيقة (٤٢/ ب). فنقول وبالله التوفيق: البرهان ينقسم إلى ثلاثة أقسام: برهان اعتلال، وبرهان استدلال، وبرهان خلف.

ف<span data-type="title" id=toc-332>برهان الاعتلال: </span>أن يقع الاستدلال على الحكم بما يوجبه، كقولنا: مسكر فكان حراما، فإن الإسكار ثابت معلوم، وهو المقتضي لتحريم الخمر،','3','270',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1902,'لجعل الشرع إياه موجبا، إلى ما ضاهى ذلك من أنواع قياس المعنى. وكذلك الجمع بكل وصف عرف كونه مقتضيا بوضع الشرع، سواء علمت مناسبته، أو لم تعلم، وليس المناسبة مطلوبة لعينها، بل ليعرف بها كون الوصف مناطا وعلة. وإذا ثبت ذلك بالتأثير بالنص أو بالإجماع، كان أولى. ويلتحق بهذا القسم عندي قياس الشبه، فإنه إثبات الحكم بالمعنى، واعتماد الوصف الشبهي، لأنه أقيم مقام المعنى عند [عسر] الاطلاع على المعنى. فعلى هذا التقدير لا يعد قياس المعنى وقياس الشبه نوعين، إذ الاعتماد فيهما على المعنى، وبه (٣٣/ أ) يقع الاستدلال على الحكم.
النوع الثاني: قياس الدلالة: وهو أن يستدل بالحكم على المعنى أو','3','271',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1903,'بالموجب على الموجب، أو بالأثر على المؤثر، أو بالنتيجة على منتجها، أو بالشيء على نظيره. وهذه الجهات مشتركة في أنه وقع الاعتماد على أمرٍ لا يقتضي، بل يدل. وقياس العلة أيضًا فيه دلالة. ولكن فيه أمر أخص، وهو الاقتضاء، فعبر عنه بالخاصية التي يمتاز بها غيره، ولم يعبر عنه بما يقع فيه الاشتراك. فكل ما يقتضي يدل، وليس كل ما يدل يقتضي، فإن العالم يدل على الصانع، ولا يقتضيه، والعلم يقتضي كون الذات التي قام بها عالمة. فما كان من قبيل المعاني أو الأشباه، فهو تمسك بمقتضى، إما مباشرة أو بتوسط الوصف. وكل قياس دلالة، فهو تمسك بأمر لا اقتضاء فيه، فالأثر لا يقتضي المؤثر، والنتيجة لا تقتضي منتجها، [والنظير] لا يقتضي نظيره، لحصول التساوي بينهما في ذلك.
ومثال الاستدلال بالأثر على المؤثر قولنا: الوتر يؤدي على الراحلة، فلا','3','272',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1904,'يكون واجبا، إذ الفرض لا يؤدي على الراحلة، والنفل يؤدى عليها، فليكن نفلًا. وهذه الدلالة واضحة، إذا سلم أن الأداء على الراحلة أثر النفلية، فلا تبقى المنازعة بعد ذلك في كونه نفلًا، والخصم يقول: منع الأداء على الراحلة أثر الفرضية، وليس الجواز أثر النفلية، ويفرق بين الفرض والواجب. وليس من غرضنا تحقيق المسألة، وإنما أردنا التمثيل لأقسام قياس الدلالة.
ومن هذا القبيل قولنا: عتق المكاتب لا ينصرف إلى جهة الكفارة، لأنه دفع عن (٤٣/ أ) جهة الكتابة، ويستدل على ذلك باستتباعه الاكتساب والأولاد، وهو [من] خاصية الكتابة، فيدل على بقاء الكتابة وعدم انفساخها، وهي جهة ظاهرة، والتمسك بها صحيح، إلى أن يبين الخصم أن الكتابة باقية فيما على السيد، [منجمة] فيما له، بدليل انفساخه في حق [قرار] النجوم. وغرضنا أيضا [التمثيل] دون تحقيق المسألة، إذ ليس استتباع الأولاد والاكتساب علة صحة الكتابة، ولكن ذلك من أثرها. ويمكن التمسك في ذلك بالاطراد','3','273',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1905,'والانعكاس والتشبيه.
النوع الثاني: الاستدلال بالنتيجة على المنتج، [وبعدمها] على عدم المنتج، ووجه دلالتها بعد تسليم كونها نتيجة واضح، كالعملية نتيجة العلم، وقيامه بالذات. فنقول: الباري سبحانه [وتعالى] عالم، فدل على قيام العلم به. ومأخذ الدلالة أيضا الملازمة بين المعلول والعلة في النفي والإثبات.
ومثال ذلك في الفقه قولنا: بيع لا يفيد الملك، فلا ينعقد، أو نكاح لا يفيد الحل مع إمكان الاستمتاع، فيكون باطلا. وقولنا في المقارض: لو ملك الرحب لملك ربح الربح، فإنه نتيجة، وانتفاؤه يدل على [انتفائه]. وقولنا: لو ملكه كاملا، لملك ناقصا. ولما انحصر الخسران فيه، دل على أنه لم يملكه. وهذه الدلالة واضحة بعد ثبوت أن هذه الأحكام نتائج هذه العقود، ففوات النتيجة يدل على [عدم] منتجها.
النوع الثالث: هو الذي ذكره الإمام، وهو الاستدلال بالشيء على نظيره، كقولنا: من صح طلاقه صح ظهاره، ومن وجب عليه العشرة والفطرة، وجبت عليه الزكاة، والمخرج الذي لا ينقض القليل [الخارج] منه الوضوء، لا','3','274',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1906,'ينقض الكثير. ونظائر ذلك كثيرة في الشرع. ووجه الدلالة فيه أنه مهما ثبت لأحد المثلين من (٣٣/ ب) الحكم ثبت لمثله. وهذا هو أصل القياس. فإذا ثبت [للطلاق] حكم من نفوذ أو رد، وثبت مماثلة الظهار له، وجب ثبوت ذلك الحكم له، وليس الظهار موجبا، فالطلاق موجب لثبوت التساوي، فقد صح بما قررناه وجه انفصال قياس الدلالة عن المعنى والشبه، وأنه نوع آخر، خاصيته الدلالة من غير اقتضاء.
وأما الإمام فلم يلحظ هذا الذي ذكرناه، وبنى الأمر على أن الجامع قد يكون معنويا، وقد يكون شبهيا. وهذان القسمان يجريان في الجامع، سواء كان حكما أو وصفا. [وأخذ] يقرر الجامع الحكمي إذا كان مناسبا، وقرره في اقتضاء نفوذ الطلاق [نفوذ الظهار]. وإذا سلك إبداء الفقه في اقتضاء أحد (٤٣/ ب) الحكمين الآخر، فهو قياس معنوي، إن تأتى ذلك، ولا معنى لكونه قياس دلالة. وكذلك إذا أبدى جانب التشبيه في قولنا: [طهارة] حكمية، فافتقرت إلى النية. وسيأتي لهذا مزيد تقرير في باب قياس الدلالة في أبواب','3','275',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1907,'الترجيح، إن شاء الله تعالى.
وأما ما ذكره القاضي من قوله: اعتبار التكبير في التعيين بالركوع، واستعمال القياس في هذا مشكل، فإنه تعين التكبير بناء على التعبد، وانحسام القياس بالكلية، فكيف نقيس في قطع القياس؟ هذا صعب، والأمر على ما قاله القاضي: واستعمال القياس في منع القياس مناقضة وإلباس. هذا هو الحق، بل نقدر ثبوت اللفظ من الشارع، ونبين انبناء الصلاة على منع القياس، فيتعين الاقتصار على التوقيف.
وأما قوله: وتارة يقول هذا مسلك قياس الشبه فيما لا يعقل معناه، فكأنه يقول: القاعدة الكلية أنه لا قياس في أركان الصلاة كلها، بل نقتصر على أعيانها. فكذلك هذا يقتصر على عينه، قياسًا على بقية الأركان، التي يجب الاقتصار على أعيانها. وهذا كلام متناقض عندي، وهو قريب من قولهم: نفي','3','276',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1908,'الحكم حكم، فإنه إثبات ونفي في حالة واحدة. وكذلك هذا إثبات قياس في محل ينقطع القياس فيه.
وقول الإمام: الجوابان متقاربان لا يظهر بينهما اختلاف في المعنى. قصد بذلك أن الأمر مقصوده أن يتعين التكبير، ولا تنعقد الصلاة بغيره. وهذا لعمري هو المقصود، ولكن النظر في صلاحية الطريق لذلك.
وأما قوله في الطرد والعكس: [إذا] رأيناه طريقًا في إثبات العلة، فهو يلتحق الشبه. هذا قد تقدم كلامنا عليه، وقد عده قوم من قبيل الإيماء، وبينا وجه تمسكهم في ذلك، وأنهم قالوا: لو أعلم الرسول بحدوث الوصف [لحكم]، لعد ذلك إيماءً منه إلى التعليل بالوصف الحدث المذكور له. وكذلك إذا علم حدوثه، فحكم [عقيبه]، فليكن ذلك إيماء، إذ لا فرق بين أن يعلم أو يعلم. ورددنا نحن هذا على حسب ما قدمناه، فلا نعيده. ثم أنه','3','277',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1909,'لو صح ذلك، لم يكن إلى العكس حاجة، فإنا نتمسك بالوصف المسؤول عنه عند ترتيب الحكم عليه، ولا ننظر في عدمه على حال. فلو كان الطرد والعكس كذلك، [للزم] التمسك بمحض الاطراد، وذلك باطل على ما مر.
وأما قول الإمام في هذا المكان: إنه من أبواب الأشباه، إذ كل وصف ثبت كونه مناطًا للحكم من غير مناسبة، فمسلكه (٣٤/ أ) التشبيه. فهذا غير صحيح، فإن الوصف إذا ثبت كونه مناطًا بالنص أو الإجماع، لا يعد (٤٤/ أ) ذلك من أبواب الأشباه، كقوله - صلى الله عليه وسلم -: (من مس ذكره فليتوضأ). إلى ما يضاهي ذلك. ففي العبارة نقصان، ومراده: أنه إذا توصل بالاستنباط إلى إثبات كون الوصف مناطًا من غير ظهور إخالة، فمسلكه الشبه [في اعتماد] الوصف الذي ليس بطردي، ولا مناسبة ظاهرة فيه. هذا تمام [القول] في تصوير قياس الشبه وتمثيله، وبيان ما هو من الشبه، وما ليس منه.
قال الإمام: (مسألة: قال القاضي [رحمه الله] في كثير من مصنفاته:','3','278',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1910,'قياس الشبه باطل، [وإلى هذا صغوه الأظهر، وتابعه طوائف من الأصوليين، وذهب معظم الفقهاء إلى قبول الشبه والقول به]) إلى قوله (ولم يثبت في مأخذهم ضبط). قال الشيخ: قد اختلف الناس في صحة الاعتماد على الوصف الشبهي، على حسب ما تقدم بيانه. فقال قائلون: لا يجوز الاعتماد عليه. وقال قائلون: يجب الاعتماد عليه، [إذا] لم يمنع من ذلك مانع. وقال قائلون: إن ألجأت ضرورة إلى التعبد به اعتمد عليه، وإلا فلا.
وفرق الإمام بين أنواعه الشبه، فجعل في بعضها يصح الاعتماد عليه، اكتفاء بالتعبد بالقياس، وفي بعض أنواعه لا يجوز التمسك بها، إلا أن يقوم دليل خاص في الأصل المعين على وجوب التعبد به. وسيأتي تمثيله لهذه الأقسام بعد هذا.','3','279',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1911,'فأما الذين ردوا، فمعتمدهم عدم ظهور المناسبة، ووقوع الوصف من قبيل الطرديات المردودة، وفقدان دليل العمل، إذ ليس هو من معنى طلب المصالح في شيء، وإنما تبين من الصحابة استرسالهم على تعليق الأحكام في مجالس الأشتوار على المصالح. وإذا لم تظهر مناسبة، وأمكن ربط نقيض الحكم بالوصف، فكيف يتحكم بالنصب؟ هذا تلخيص كلام الرادين للوصف الشبهي. وهو كلام [غامض]، والفصل بين الطرد والشبه شديد.
وأما ما ذكره الإمام من المسلك الأول، وهو قوله: [إنه] قد تبين أنه لا تخلو واقعة عن حكم الله تعالى. فقد تقدم أن هذا أمر لم يدل عليه بحال، ولم','3','280',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1912,'يأت به إلا دعوى من غير برهان. ولو صرنا إلى ذلك تنزلا، فهذا لا يرشده إلى القول بقياس الشبه، لأنه [يصح] أن يسلك مسلك النفي والإثبات في تعميم الأحكام، كما قرره في أبواب الطاهر والنجس، فيطلب أدلة النجس من النصوص والمعاني، فإذا لم يوجد ذلك، التحق بالطاهرات من غير حاجة إلى إثبات قياس الشبه. هذا إذا اعتمد الأمر الكلي، وإن رجع إلى استقراء الجزئيات، وهو الوجه الثاني فيه، [فقد] قال: الاستقراء يرشد إلى حكم الأولين بالقياس في مسائل كثيرة، لا تظهر فيها المعاني المخيلة المناسبة. (٤٤/ ب) فهذا المسلك لا يحصل منه علم لسامعه لوجوه:
منها- أن هذه الطريقة إنما تكون نافعة على تقدير النقل عن كل أهل الإجماع، أنهم حكموا بالقياس مع فقدان المعاني عند الجميع. وإثبات هذا صعب [عسر].','3','281',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1913,'فلئن قيل: حكم بعض، وسكت آخرون، فهو إجماع ضمني. فقد قدمنا في هذا كلامًا بالغًا، على أنه لا يتحقق أن الساكت سكت، مع تحققه أن الحاكم أسند الحكم إلى شبه لا إلى معنى، فلعل الساكت سكت، بناءً [على أن الحاكم إنما حكم لمعنىً عنده.
الثالث: أنه وإن سكت بناءً] على أنه أسنده [لشبه]، فلا يحمل سكوته على (٣٤/ ب) الموافقة، إذ المسألة اجتهادية. وقد قال القاضي: القول بقياس الشبه أو رده ليس قطعيًا، وإنما هو مجتهد فيه، ولا [إنكار] في مسائل الاجتهاد.','3','282',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1914,'ودعوى أن الحكم عندهم إنما استند إلى الشبه المحض، دعوى من غير برهان. وقد اعترض الإمام على القاضي في هذا الطريق بما [وجهناه] نحن عليه، فإن القاضي لما مثل القول بالأشباه بمناظرات أصحاب رسول الله - صلى الله عليه وسلم - في مسائل الجد والإخوة، منعه الإمام ذلك، وقال: إنها معانٍ، بيد أنها تكاد تتعارض. فكذلك يمكن سلوك هذا المسلك فيما عين من الأقيسة المقبولة.
وزعم الزاعم أنها أشباه، يمكن خصمه المنازعة فيها، ويقول إنها معانٍ. فهاتان الطريقتان عندي ضعيفتان، اللهم إلا أن يحصل للناظر من استقراء الجزئيات علم باتباع الماضين في القياس الوصف غير المخيل، فيكون هذا طريقًا ينتفع به الناظر، إذ المعلوم لا يمكن دفعه. أما الاقتصار على هذه الأمور الجميلة، فلا يحصل منها علم بالمطلوب على حال.
وأما الطريق الآخر، وهو أن الشيء إذا كان عند انتفائه يحصل علما، فإذا','3','283',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1915,'بعد وأثار ظنًا، كان متقبلًا في الشرعيات، فنحن نسلم أن الأمر كذلك.
وما ذكره من إلحاق ظن المعنى بالنص، على المعنى في تحصيل غلبة الظن، فصحيح، إذ لم تقتصر محال الأعمال على طلب العلم.
وقوله: إن القياس الشبهي على منهاج ما يقال إنه في معنى الأصل، وما في معنى الأصل معلوم، والشبه يثير الظن بالإلحاق، فيجب أن يكون مقبولا. هذا فيه نظر من وجهين:
أحدهما- أن هذا على الحقيقة قياس الأصول، وقد قال هو: إن ذلك [معلوم]. وقوله: ما ذكرناه إلا على طريق التمثيل، فيقال له: أين الدليل على وجوب الاعتماد على الوصف الذي لا يخيل؟','3','284',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1916,'وقوله: وقياس الشبه على منهاج ما يقال إنه في معنى الأصل، ليس كذلك، فإن القياس الذي يسمى ما في معنى الأصل، هو الذي حصل (٤٥/ أ) التقارب بين الأصل والفرع، بحيث يحصل العلم بأنه لا فارق على حال، ولم يقصد فيه إلى جامع معين بوجه، وإنما يكون في منهاجه وطريقه ما إذا قصد نفي الفرق بعد الحصر على [وجه] مظنون، كما يقول الشافعي: إذا وجبت الكفارة في الخطأ، فلأن تجب في العمد أولى. هذا هو الذي على منهاج ما في معنى الأصل.
وأما القياس الشبهي، فليلحظ فيه نحو الجامع، اقتصارًا عليه، مع العجز عن إبداء مناسبة فيه، وتكلف الخصم الفرق بينه وبين الطرد، فلا يهتدي إليه، ويركن إلى الدعوى، [ويقول] ليس هو مثله أصلا. والخصم ينكر إدراك الفرق بين الوصفين، فلم يكن هذا على منهاج ما في معنى الأصل [أصلًا].
وما ذكره الإمام [آخرًا، من] أن النظر في الشبه يوقع في مستقر العادة','3','285',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1917,'ظنًا يكون الفرع مساويًا للأصل. هذا هو الذي ينكره رادو الشبه، ويقولون: الوجه الذي يثير الظن لم يقدر عليه، ولا وجدنا فرقًا بينه وبين الطرد أصلًا.
وما ذكره من التقارب الحاصل بين الوضوء والتيمم، ونسبة جاحدة إلى المناكرة. الخصم يقول: ذلك في إلحاق الوضوء بإزالة النجاسة، فلا يبقى إلا أن يقول كل واحد منهما لصاحبه: أنت مباهت. (٣٥/ أ) وهذا لا ينتهض منه حجة على تحقيق حق، ولا على إبطال باطل. لا جرم لهذه الدقيقة قال جماعة ممن يقبل الشبه: لا يجوز اعتماده إلا بشرط إرهاق الضرورة إليه. فإذا دعت الضرورة إليه، ولم يصادف إلا الوصف الشبهي، لزم اعتماده، إذ لو لم يعتمد مع بطلان غيره، لبطل الافتقار إلى طلب المناط. وإذا شرط هذا الشرط، بطل القول بقياس الشبه رأسًا، وآل الأمر إلى اشتراط السبر والتقسيم. وقد قررنا أن كل وصف نفاه السبر، لزم اعتماده حين يتبين غيره.','3','286',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1918,'فإن قيل: فمن أين يلزم أن يكون المصير إلى اشتراط السبر مبطلا للقول بالشبه، وكل علة تستعمل لابد فيها من السبر؟ قلنا: الأمر كذلك، وسننبه بعد هذا على خواص العلل، ونبين أن السبر لا يشترط في بعض المواضع، ويشترط في بعضها. [وإذا اشترط في بعضها]، فقد يشترط لدفع معارض، وقد يشترط مكملًا للدليل، حتى لا تستقر للناظر قدم إلا بعد تقريره.
قال الإمام: (فإن قيل: لسنا نسلم [اقتضاء الشبه غلبة الظن]) [إلى قوله (ويتنجز بنجازه القياس، إن شاء الله تعالى]). قال الشيخ: أما السؤال الذي وجهه الإمام، فلم يحصل عنه جواب على حال. وقصارى الكلام، دعوى','3','287',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1919,'[الاضطرار] (٤٥/ ب)، وذلك لا يغني في الجدال، ولا يتصور عن هذا السؤال إبداء انفصال. وليس هذا منازعة في أصل القياس، ولكنها منازعة في ترتيب حصول غلبة الظن على الوصف لذي لا يناسب، فإن الخصم إن اقتصر على دعوة الضرورة، لم يخلف فساد هذا الكلام.
وإن أبدى وجهًا من المعنى، خرج عن قياس الشبه، وإن سلك مسلك السبر والتقسيم، بطلت خاصية الشبه، إذ ما أبقاه السبر معتمدً. وإن قال: هو مغلب على ظني أنه مشتمل على المعنى الخفي، لم يساعده الخصم على ذلك. فمن توهم أنه يستقل بإثارة الوصف المعين غلبة الظن مع فقدان مناسبته، فقد توهم محالًا، لا يستقل بإيضاحه أبدًا. لا جرم الذين صاروا إلى','3','288',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1920,'قبول الأشباه، لم يكلفوا المستدل إبداء غلبة الظن في الوصف المعين، وألزموا الخصم الاعتراض. وهذا في أدب الجدل وشريعة المناظرة. فأما المجتهد، فإنه مردود إلى غلبة الظن، وإدراك الفرق في نفسه بين الوصف الطردي والشبه، وليس كل ما يثبت في النفس تتيسر عبارة دالة عليه، فلا ينبغي للمرء أن يغالط نفسه فيما يدركه، ونحن ندرك الفرق بين رائحة المسك والزعفران، ولا نقدر على التعبير على الفرق على حال.
وأما ما ذكره الإمام آخرًا: من [أن الوصف الشبهي يناسب مناسبة الفرع الأصل، فهو يشير إلى ما قررناه]، من أن الحكم يثبت في الأصل لمصلحة، وقد تكون [ظاهرة، وقد تكون خفية]، فإذا كانت ظاهرة، فهو المعنى المخيل المناسب، وإذا كانت خفية، وهي لا تعدو أوصاف المحل،','3','289',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1921,'فأحد الأوصاف يتضمن المصلحة الخفية، هو الوصف الشبهي. فإذا اشترك الأصل والفرع في ذلك الوصف، غلب على الظن اشتراكهما في ذلك المعنى. هذا معنى قوله: إن الأشباه وإن لم تناسب الأحكام، فهي تناسب اقتضاء مشابهة الفرع الأصل في الحكم.
وقوله بعد هذا: (٣٥/ ب) لو لم يفرض إلا الوضوء، لم يكن قولنا: طهارة حكمية، أو عن حدث اقتضى النية، لا عن علم ولا عن ظن. ولكن إذا فرض اشتراط النية في التيمم، كان ذكر الوضوء، وهو كونه طهارة حكمية، يقتضي تقريب أحدهما من الآخر. هذا الكلام حسن، ولكنه قد تقدم له في حد الطرد ما يقتضي خلاف هذا، فإنه قال في حد الطرد: هو الذي لا يناسب الحكم ولا يشعر به، ولو فرض ربط [نقيض] الحكم به، لم يترجح [في مسلك الظن] قبل البحث عن القوادح [في] النفي على الإثبات. قال: ولم يكن من فن الشبه. يريد أن الوصف الشبهي لا يستوي نسبة النقيضين إليه','3','290',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1922,'بحال، بخلاف (٤٦/ أ) الوصف الطردي، فإن النقيضين تستوي نسبتهما إليه. فهذا خلاف ذلك.
وقال بعد هذا: إذا قلنا: طهارة عن حدث لا يليق بهذا نفي النية، بخلاف قولنا: طهارة بالماء، فلم تفتقر إلى النية. قال: لو قال قائل: طهارة بالماء فافتقرت إلى النية، لم يكن في هذا بعد، حتى يقال نفي النية أليق بهذا اللفظ من إثباتها. فقد صرح في ذلك الموضع بأن الوصف الشبهي لا يقبل النقيضين، وقال في هذا الموضع: أن النسبة إلى الوصف قبل شرعية حكم الأصل، بالإضافة إلى النقيضين على حد واحد. فهذا كلام ظاهر التناقض.
وعلى الجملة، فسبب هذا أن الأشباه غلبت على الظن، بالنظر إلى قرب الأوصاف من مألوف الشريعة، فهذه القضية تثبت للوصف، وإن لم [يفرض] ورود حكم على [الوفق]، كالوصف المناسب الملائم، فإنه يظن أنه مطلوب الشارع، [وإن] لم يرد حكم على الوفق. ولهذا قبل الناس الاستدلال','3','291',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1923,'المرسل، وإن كانت غلبة الظن إنما تحصل، بناءً على تضمن الوصف للمصلحة [المبهمة].
فهذا لا يثبت دون ورود الحكم الذي يتلقى منه ثبوت المصلحة، فينظر حينئذ في أقرب الأوصاف التي تضمنها. هذا تمام القول في قياس الشبه. والله تعالى الموفق. والمسألة في نهاية الغموض.

قال الإمام: <span data-type="title" id=toc-336>(فصل- في مراتب الأقيسة [</span>يحوي ما يعد منها وفاقًا، وما','3','292',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1924,'يختلف في عده منها، ويتضمن بيان ترتيبها في الخفاء والجلاء]) إلى قوله (وستأتي أبواب الترجيح حاوية [لها] منطوية [عليها]، إن شاء الله','3','293',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1925,'تعالى). قال الشيخ: ما ذكره الإمام في الأقسام المعلومة، واضح لا خفاء به.','3','294',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1926,'وقد بينا أن العلم لا [يتفاوت] في نفسه، وطرقه أيضا لا [تفاوت] فيها، إذ لا يتصور أن يكون دليل قاطع أقوى من دليل. وقد [استقصينا] القول في ذلك.','3','295',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1927,'وانقسام المظنون إلى المعنى [والشبه]، التقسيم على رأي الإمام الذي رأى الطرد والعكس من أبواب الأشباه، وردد الدلالة بين الأشباه والمعاني. وأما نحن إذا رأينا قياس الدلالة [قسمًا] آخر، فلا ينحصر المظنون في المعاني والأشباه.','3','296',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1928,'وأما قوله: إن آخر رتبة [من مراتب] [المعاني]، لاستفتاح الأشباه إلى آخره، فغير مطرد ولا صحيح. أما كونه [غير] مطرد، فسيأتي [له] في كتاب الترجيح تقديم أشباه على أقيسة المعاني، كالقول في تقدير أروش أطراف العبيد مع تضمين القيمة. فالذي (٤٦/ ب) يقتضيه القياس المعنوي تضمين ما نقص، والذي (٣٦/ أ) يقتضيه التشبيه التقدير، اعتبارًا بالحر. وكذلك ضرب قليل العقل وكثيره على العاقلة، [مع تخصيص الضرب بالكثير]. والقياس المعنوي يقتضي أن لا ضرب على العاقلة، والقياس','3','297',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1929,'الشبهي يقتضي الضرب. وقد [قدم] هو في هاتين المسألتين الشبه على المعنى. فهذا يبين أنه لم يطرد القول.','3','298',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1930,'وأما كون المعنى لا يقتضي التقديم على الإطلاق، فمن جهة أنه جعل الأشباه تترتب ترتب المعاني المظنونة، بالإضافة إلى العلل المنصوص عليها، ثم فرض النزول قليلا قليلًا عن المعلوم في [البابين].
فإذا فرضنا أنا نزلنا في درجة الأشباه عن القياس المسمى ما في معنى الأصل رتبة واحدة، ونزلنا في أبواب المعاني عن المعاني المنصوصة خمس','3','299',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1931,'رتب مثلا، فكيف يتصور على هذا التقدير أن يكون المعنى الواقع بعد خمس رتب مثلا، من المعاني المعلومة مقدمًا على الشبه الواقع في الرتبة الثانية مثلا، من القياس الذي سمي في معنى الأصل؟ [هذا] لا يصح أن يقال بحال.
نعم، قد يقال: إذا فرض تساوي الرتبتين أن يكون المعنى مقدما. وهذا على التوسع، وإلا فإذا فرض تحقيق الاستواء بالنسبة إلى مراتب القطع في الموضعين، فلا وجه إلا التوقيف. أما [إذا كان الأمر] على خلاف هذه القضية، ولم ينظر إلى هذه النسبة، وناط [الناظر] حكما بمعنىً مناسب، وعورض بشبهٍ يوهم الاشتمال على مناسب، فالترجيح للمناسب على هذا التقدير.
قال الإمام: ([وقد] قدم الأصوليون أشباه الأحكام [على الأشباه','3','300',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1932,'الحسية، وليس الأمر على هذا الإطلاق] إلى قوله (من جهة إشعاره بالمعنى). قال الشيخ: وهذا أيضا يناقض ما سبق، فإنه يرى أن قياس الدلالة تارة يكون معنى، وتارة يكون شبهًا، وهو في طوريه لا يخلو من أن يكون معنى أو شبها، فيا ليت شعري [كيف] يقدمه على الشبه؟ هل [ذلك] في الحال التي يكون فيها شبها، أو في الحال التي يكون فيها معنى؟ فإن كان في الحال التي يكون فيها شبها، فمعنى الكلام أنه يقدم الشبه على الشبه. وإن كان في الحال التي يكون فيها معنى، فمعنى الكلام أنه يقدم المعنى على الشبه. وقد مر الكلام على ذلك.','3','301',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1933,'قال الإمام رحمه الله: (وما [يثبت] بالطرد والعكس مقدم على الشبه الذي لا يتصف بذلك، فإن الطرد والعكس يجريان في مجال الظنون [والحسبان] مجرى ظهور لفظ [ليس نصا من] الشارع، والشبه يبعد من هذا). [قال الشيخ: وهذا الذي قاله أيضا ضعيف، فإن تنزيل الطرد والعكس منزلة ظهور لفظ الشارع]، فقد بينا بطلانه، ولا سبيل إلى اعتماده في (٤٧/ أ) التقديم، [مع] ظهور البطلان. ولئن صح تنزيله منزلة ظهور لفظ الشارع، فهذا يقتضي تقديمه [أيضا] على المخيل. فليس لهذا الكلام تحصيل.
قال الإمام رحمه الله: (فليتخذ الناظر هذه المراسم قدوته [وإمامه، ولو قيس المخيل السديد بالمطرد المنعكس]) إلى قوله (ملتحق بظواهر ألفاظ','3','302',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1934,'الشارع). قال الشيخ أيده الله: هذا الذي ذكره الإمام ههنا ظاهر الفساد، فإنه [قال: ] إذا قوبل المطرد المنعكس بالمخيل السديد، قدم المخيل، فأين هذا من قضائه بأن المرد المنعكس، ملتحق بظواهر ألفاظ الشارع؟ وجميع العلل الثابتة بالظواهر، مقدمة (٣٦/ ب) على ما يتلقى من الاستنباط.
وقد اعتل هو في تقديم المخيل، بتحققه أن الصحابة كانت تعتمده، وقال: لتكلفنا [إلحاق] المطرد المنعكس [به]. فهذا اعتراف منه بأنه ليس يتنزل منزلة ظواهر الألفاظ. ثم قال: فإن خفي المعنى، وظهر المطرد المنعكس، [ورأى الناظر تقديم المطرد المنعكس، فلا بأس. قال: والسبب','3','303',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1935,'في ذلك، أن المعنى إذا تناهى خفاؤه]، فيكاد أن يخرج عن كونه منصوب الصحابة. وهذا الكلام غير صحيح، فإنا قد بينا أن الصحابة - رضي الله عنهم - لم يضبطوا وجوهًا من الرأي لا تتعدى، وإنما كانوا يعلقون الأحكام على المصالح، مع انقسامها إلى القوي والضعيف، وكذلك القول في اعتماد أخبار الآحاد، مع تفاوت الرواة في العدالة. فلا يؤثر تفاوت الدرجات عند النظر [في المفردات، وإنما يلتفت إلى ذلك عند التعارض فيما يتعلق بالترجيحات. ثم] قال: [والمطرد المنعكس] ملتحق بظواهر ألفاظ الشارع. فيا ليت شعري، كيف قدم المطرد المنعكس على المخيل الضعيف؟ ألأنه يعد من ألفاظ الشارع، وألفاظ الشارع مقدمة على طرق الاستنباط، وأخر عن المخيل القوي، لأنه لم يتحقق المطرد المنعكس معمول الصحابة؟ وقد تحقق هو كون المخيل [معتبرًا]. هذا ظاهر التناقض.
قال الإمام: (ثم لا ينبغي أن يظن ظان أن القول في هذا ينتهي إلى','3','304',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1936,'[القطع]، [بل هو موكول إلى نظر النظار، واجتهاد أصحاب الاعتبار، وكيف لا يكون كذلك، والتقديم والتأخير] مستنده الترجيح، [ومنشأ الترجيح] الظن)؟ قال الشيخ: [قوله]: لا ينبغي أن يظن الظان أن القول ههنا في التقديم ينتهي إلى القطع، وبين السبب في ذلك، أن الكلام يتعلق بالترجيح، ومستند الترجيح الظن.
وهذا الكلام غير مستقيم، ونحن لا ننكر أن الترجيح: تغليب بعض الأمارات على بعض في سبل الظن، ولكن يلزم من هذا أن لا يقطع بترجيح مظنون على مظنون، وكيف ينكر ذلك، وهو قد حكم بأنه إذا تعارض خبر واحد وقياس، أو خبر واحد وعموم كتاب، وجب تقديم خبر الواحد قطعًا؟ وكذلك بتقديم ظاهر التعليل على استنباط المستنبط؟ ولسنا (٤٧/ ب) نقول إنا لا نقطع بتقديم باب من المظنونات على باب من الأمارات، [منها] ما يقطع بتقديمه على بعضها، ومنها ما يظن تقديمه من غير قطع. فبناء على عدم القطع بالتقديم على','3','305',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1937,'أن الترجيح ينشأ من تغليب الأمارات، لا يصح على الأصل الذي قرره في المواضع التي ذكرناها.
فإن قيل: إذا كان الخبر المعين مثلا مظنون، وعارضه قياس مظنون أيضًا، كيف الحكم في ذلك؟ فإذا قضيتم بأن الخبر المعين مقدم، أتقطعون بذلك أم تظنون؟ فإن قطعتم بالتقديم، وهو كلام الأصوليين، كان ذلك مشكلًا، فإن ثبوت الخبر مظنون، والعمل به مظنون، فكيف تظنون استناد العمل إليه إذا كان مفردًا، وتقطعون بتقديمه على معارضه؟ وكيف يتصور هذا أن يكون الشيء إذا كان منفردًا ظن استناده إلى الشريعة، فإذا عارضه غيره، قطع بوجوب اعتماده، مع [كوننا] نظن ثبوته؟ هذا محال لا خفاء به.
فإن قيل: فما الذي ترون في ذلك؟ قلنا: هذه المسألة عندنا تنبني على القول بأن كل مجتهد كانت مفردة، [وقد] يقطعون بالتقديم عند المعارضة، وقد يظنون (٣٧/ أ) ذلك ظنًا، على حسب ما يتأتى لهم وجدان الإجماع على التقديم، على حسب ما يدعون. وقد [أيضًا] يظنون العمل [عند الأمارة المنفردة، فإنه ليس كل أمارة يقطعون بوجوب العمل عندها]، فجرى الأمر في الإفراد والمعارضة عندهم على وجه واحد.
فأما المصير إلى أن الأمارة إذا انفردت، يظن صحة استناد الحكم إليها، فإذا عارضتها أخرى، قطع بوجوب تقديمها عليها، ووجوب العمل بها، فهذا محال.','3','306',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1938,'وأما الصائرون إلى أن المصيب واحد، فإنهم لا يقطعون بوجوب العمل عند سبب مظنون، وإنما يظنون ذلك ظنا، فإذا عارضه سبب آخر، فترجيح أحدهما على الآخر مظنون أيضا، فلا يتصور من هؤلاء قطع بتقديم سبب مظنون على سبب مظنون، إذ في القطع بتقديمه عليه، وإسناد العمل إليه، قطع بكونه منصوب الشارع، [وأن] الحكم به، وقد بني الأمر على أنه مظنون نصبه، فالقطع بتقديمه على غيره في الحكم به، مع ظن نصبه في الشرع، تناقض بين. هذا هو الذي لا يصح عندي غيره. ولم أر هذا التقرير على هذا الوجه لأحد من الأصوليين. وبالله التوفيق. (٤٨/ أ).
قال الإمام: (وعلى هذا، فقد [يفرض] [تقديم] الشبه الجلي على المعنى الخفي، [والغاية فيه، أنه إذا انتهى الخفي إلى مبلغ يحول الكلام في إخراجه عن جنس الإخالة، والشبه الذي فيه الكلام لا ينقدح في إخراجه عن قبيل الشبه قول، فإذ ذاك ينظر الناظر]، ويرى رأيه في تقديم ما يقدم،','3','307',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1939,'وتأخير ما يؤخر). قال الشيخ: ذكر الإمام أنه يصح أن يتقدم الشبه الجلي على المعنى الخفي.
وقد ذكرنا نحن أنه لا ينبغي- على مقتضى ما قرره- أن يتوقف تقديم الشبه على المعنى، على هذا التقرير، فإنه جعل للمعاني أصولا معلومة تعرف قوتها بقربها منها، وضعفها ببعدها عنها. وجعل للشبه [أيضًا] رتبة معلومة، وهي إلحاق الشيء بما في معناه. ومقتضى هذا أن الشبه، إذا قربت نسبته من أصله المعلوم، بحيث لا يبقى بينه وبين إلحاق الشيء [بالشيء] بما في معناه إلا رتبة واحدة مثلا، وكان المعنى المخيل، [نسبته] إلى الأصل المعلوم تقد بعد خمس رتب مثلا، أن يتقدم الشبه القريب على المخيل البعيد.
[وسأثبت] ذلك بالمثال على ما [ذكره]: وهو ضرب قليل العقل','3','308',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1940,'على العاقلة. هذا عنده من أبواب الشبه القريب من مراتب العلم، فإذا قوبل ذلك بالمخيل، وهو اختصاص الجاني بالجناية، قال هو: يقدم الشبه في هذه الرتبة. وليس هذا المعنى من المعاني التي يحول الكلام فيها عن جنس الإخالة، بل هذا المعنى كلما قرر، اتضح وجهه، وظهرت مناسبته.
وأما كونه جعل الشرط في تقديم الشبه، أن يكون المعنى إذا حقق النظر فيه، خرج عن قبيل المعاني، والشبه لا يتأتى ذلك فيه. فهذا كلام عجيب، وحاصله راجع إلى أنا كنا نتخيل معنىً، فلما سلطنا البحث عليه، تلاشى واضمحل، فكأنا لم نقدم على الحقيقة شبهًا على معنى، وإنما تجرد الشبه للعمل، فحكم به. فأين هذا من قضايا الترجيح والتقديم والتأخير؟
ولننبه ههنا على أصل، وهو أن من المناسبات مناسبات تسمى الإقناعيات، وهي أمور تظهر أولًا على ذوق المصالح، ولكنها إذا حقق النظر فيها، لم تصبر صبر (٣٧/ ب) غيرها على السبر، ولكن أدار الشرع الأمر على ما يظهر منها، وإن كان ضعيفًا. ففرق بين أن يقول: إذا حقق النظر فيها ضعفت، [وبين] [أن يقول: ] إذا حقق النظر فيها بطلت.
ومن هذه المناسبات: القضاء بمنع بيع النجاسات عند بعض العلماء،','3','309',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1941,'والمناسبة فيه من جهة أن البيع إثبات مالية للمبيع، وتقدير حرمة له، لما أثبت الشرع للأموال [من الاحترام]. وقد قال تعالى: {ولا تؤتوا السفهاء أموالكم التي جعل الله لكم [قيامًا]}. وقال - عليه السلام -: (إن الله ينهاكم (٤٨/ ب) عن قيل وقال، وكثرة السؤال، وإضاعة المال). وقال: (حرمة مال المسلم كحرمة دمه). فإذا ثبت للأموال هذه الشرفية، فإثباتها للجنس الخبيث المبتعد، يظهر فيه نوع من التناقض. هذا [الكلام] معقول، [وليست] مناسبته كمناسبة شرعية القوض في القصاص، والقطع في السرقة، والحدود في الزنا، فتلك مناسبات جلية.
ومن المناسبات الإقناعية: الحكم برد شهادة العبد، بناء على أنه خسيس القدر، نازل المرتبة، وأهلية الشهادة درجة نفيسة، فإعطاء الخسيس المنصب النفيس، لا يليق بمكارم الأخلاق عند ذوي العادات. قالت عائشة رضي الله عنها: (أمرنا رسول الله - صلى الله عليه وسلم - أن لا نرفع الرجل الوضيع فوق منزلته، ولا نحط الرجل [الشريف] عن درجته، وأن نعطي كل ذي حق حقه). هذا معنى','3','310',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1942,'الخبر دون لفظه. فهذا وقبيله من المناسبات، تسمى الإقناعيات.
فإن كان الإمام أراد تقديم الشبه على هذا النوع، فهذا الكلام فيما يتخيل كونه مخيلًا، ولكن إذا بحث عنه، ظهر غير مخيل. فهذا لا وجه له عند ذوي التحصيل. وحاصله إعمال الشبه الذي لا معارض له. هذا الكلام كله في ترتيب الأقيسة، على ما رآه الإمام.
والأمر عندي في الترتيب على وجه أبلغ من هذا وأخصر، وهو أن القياس، كما ذكر، ينقسم إلى المعلوم والمظنون. فأما المعلوم، فلا ترتيب [فيه]، لا في نفس العلم، ولا في الطرق كما تقدم. وأما المظنون، فالنظر في أمرين: أحدهما- المعاني ذوات الأصول. والثاني- المعاني الراجعة إلى الأصول. أما المعاني في أنفسها، فإنها تنقسم ثلاثة أقسام: مؤثر وملائم وغريب.

أما<span data-type="title" id=toc-338> المؤثر: </span>فهو الذي دل النص والإجماع على كونه علة، ولسنا نعني ههنا بالنص، الذي لا يتطرق إليه التأويل، بل نعني به: ما تلقي كون الوصف علة من التوقيف، حتى يتناول ذلك الصريح والإيماء، وهو باعتبار عين العلة وجنسها، وعين الحكم وجنسه أربعة أقسام. فإنه إما أن يظهر تأثير عين العلة في عين الحكم، أو تأثير عينها في جنس الحكم، أو تأثير جنسها في عين الحكم، أو تأثير الجنس في الجنس. فإن أثر عين العلة في عين الحكم، فهو المخصوص [باسم المؤثر]، وهو مقبول باتفاق القائسين.
ونقل عن أبي زيد الاقتصار عليه، ورد ما سواه]. وإن أثر جنس العلة في عين الحكم، فهو [ملائم. وإن أثر جنس العلة في جنس الحكم، فهو]','3','311',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1943,'[غريب]، [إذ الجنس الأعم] للمعاني، كونها مصلحة، والجنس الأعم للأحكام، كونها حكما، فقد ظهر تأثير (٤٩/ أ) جنس المعاني في جنس (٣٨/ أ) الأحكام. هذا تقسيم المعاني باعتبار شهادة التوقيف.
أما باعتبار شهادة الأصل، فإن [جمع] [المعاني] الملائمة وشهادة الأصل، فهو مقبول بإجماع، إلا ما يحكى عن أبي زيد، وأمثلته تدل على أنه يقبله. وغريب لم يستنبط من أصل معين، هذا فيه غموض كثير، ويضاف إلى مالك رحمه الله أنه يقول به. وسيأتي الكلام عليه بعد هذا.
وملائم لم يستنبط من أصل معين، أكثر الفقهاء على قبوله، وقد رده القاضي أبو بكر رحمه الله، والكلام عليه يطول، وهو الاستدلال المرسل. وأما الوصف الشبهي، [فإنه] لا يعتمد [إلا] على [شريطة] ورود حكم على وفقه. [وهل] يكتفي في اعتماده بورود الحكم على وفقه، أو تشترط فيه زيادة؟
اختلف الأصوليون في ذلك، فقال الأكثرون: يكتفي بذلك [فيه]، كما يكتفي بظهور الإخالة عند ورود الحكم على الوفق. وقال قائلون: لابد من','3','312',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1944,'إرهاق الضرورة إلى الاعتماد، بحيث تجب [التعدية] بدليل آخر، سوى الدليل على أصل القياس. وقال أبو حامد: [وليس] ذلك ببعيد عندي في أكثر الأشباه، وقرر ذلك بأن قال: إذا أمكن تعريف الحكم بالمحل، فأي حاجة إلى طلب علامة لا تناسب؟ . وهذا الكلام ضعيف، فإنا قد [قررنا] فيما تقدم، أن الحكم لا يخلو عن مصلحة، ومصلحته لا تعدو أوصاف محله. وإذا تبين أن أولى الأوصاف يتضمن المصلحة الخفية، هذا الوصف مثلًا، لزم اعتباره، ولم يفتقر إلى أمر آخر سوى هذا.
وفرق الإمام بين مراتب الأشباه، فقال: ما [استند] من الأشباه إلى الأمثلة، كضرب قليل العقل على العاقلة، أو إلى معان كلية، كتقدير أروش أطراف العبيد، وافتقار الوضوء إلى النية، فإنه يكتفي بذلك من غير ضرورة، بل يكفي في اعتماده شرعية القياس. و [أما] ما كان من الأشباه يتعلق بالمقصود، كتعليل تحريم التفاضل في الربا بالطعم، فهذا لا يعتمد عنده، إلا أن ترهق ضرورة إلى التعليل. ولم يأت لهذا التقسيم بدليل. والذي نختاره خلاف ذلك، وهو أنه لا فرق بين هذه المراتب في الاعتماد. ويتعلق النظر ههنا بأمر، وهو التنبيه على خواص الأقيسة، إذ انجر الكلام إلى ذلك.
اعلم أن الكلام في هذا المقام في طرفين، في الناظر والمجتهد. أما','3','313',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1945,'الناظر، فإنه إذا ادعى كون وصف مناطًا، ودل على ذلك بما يليق به، اكتفي منه بذلك، ولم يكلف البحث والسبر، وإبطال ما سواه باتفاق النظار. وقد نقل عن القاضي رحمه الله أنه كان يرى ذلك، ويفعل في المناظرة ما يفعله المجتهد (٤٩/ ب)، من البحث والحصر والإبطال، حتى لا يبقى [لخصمه] كلام بحال. وهذا وإن كان منه اتساع في المقال، فهو أمر اتفق النظار على خلافه. [وأقام] الجدليون بانيا وهادما لمصلحة التهذيب والتمرين، وذلك أقرب إلى مصلحة الفريقين. وإذا استقرت قدم المستدل بتحقيق دعواه بما يعضدها، بما يكون من الأدلة، اكتفي منه بذلك. فإن كان خصمه لا اعتراض عنده، لزمه الانقياد إليه، فإن كان عنده غيره، فليبده ليتكلم عليه.
وأما المجتهد، فلا يكتفي بذلك، ونقل القاضي وغيره في ذلك الإجماع. غير أني رأيت لبعض الأصوليين نقل الخلاف فيه، وإن [كان] من الناس من ذهب إلى الاكتفاء بذلك، وسلك بالمجتهد مسلك المناظر، وربما تمسك في ذلك بقول معاذ حيث (٣٨/ ب) قال: (أحكم بكتاب الله، فإن لم أجد، حكمت بسنة رسول الله صلى الله عليه وسلم). الحديث. فلم ينظر في السنة إلا بعد فقدان الكتاب، وإن أمكن أن يكون في السنة معارض للكتاب. وهذا القول باطل قطعا،','3','314',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1946,'ولست أعدها مقالة معتدًا بها. وكيف يتفق أن يجد الإنسان ظاهرا في الكتاب، فيحكم به من غير بحث، هل ثم سنة متواترة [تخالفه] أو إجماع؟ هذا باطل لا خفاء ببطلانه، وإضراب الأئمة عن [نقل] مثل هذه المقالة صواب.
فإذا ثبت هذا، فنقول: إن اطلع المجتهد على علة، ثبت كونها علة بالتأثير، فهو مستغنٍ عن السبر في صحة الاعتماد عليها، لأنه عندما يسبر، لا يخلو من أمرين: إما أن لا يجد غيرها، وإما أن يجد غيرها]، فعلى تقدير أن لا يجد غيرها، فهي متعينة للعمل. فإن وجد غيرها، لم يقدح ذلك في صحتها، ولم يمتنع من الاعتماد عليها. وكل أمر يكون وجوده و [عدمه] على حد [واحد]، بالإضافة إلى أمر من الأمور، فطالب ذلك الأمر يستغني عن البحث عن وجوده وعدمه.
ومثال ذلك: أن الشرع إذا [قضى بتحريم] وطء الحائض، وثبت تأثير الحيض في تحريم الوطء بالنص، فلا حاجة في الاعتماد على الحيض إلى السبر، فإنه لو ظهرت العدة [أو الإحرام]، [لم يمتنع] التعليل [بالحيض].
وأما المناسب الذي ثبت كونه علة بشهادة الحكم له، فلابد فيه من السبر، لأنا قد حققنا فيما تقدم، أن الشهادة إنما تثبت على تقدير الاتحاد،','3','315',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1947,'وعند التعدد تضعف الشهادة، وقد مضى في ذلك قول شاف كاف.
وأما الشبه، فمن خاصيته أنه يفتقر إلى نوع ضرورة إلى طلب المناط عند بعض الأصوليين، [وهو] الذي قدمنا الكلام عليه. وقد كنا أيضا قدمنا أن السبر والتقسيم، قد يستعمل طريقًا إلى إثبات (٥٠/ أ) العلة، وقد يستعمل طريقا إلى نفي المعارض، فإن استعمل طريقا، افتقر إليه الناظر والمناظر، إذ هو الدليل، فإذا فقده المناظر، فبم يتمسك؟ وإنما الذي يستغنى عنه، هو السبر الذي يشترط في التعيين، فهذا يفتقر إليه المجتهد في بعض الأحايين دون المناظر على الإطلاق. هذا تمام الكلام في الطرق التي تثبت بها علل الأصول. ونحن نذكر بعد ذلك القول في تصويب المجتهدين.

قال الإمام: <span data-type="title" id=toc-340>(مسألة: قال القاضي أبو بكر: ليس في الأقيسة المظنونة تقديم ولا تأخير) </span>إلى قوله ([وهو على الجملة، هفوة عظيمة]، وميل عن الحق واضح). قال الشيخ: هذه المسألة يتعلق القول فيها بتصويب [المجتهدين]، وتصور الخطأ في المجتهدات، واستحالة ذلك، وما حقيقة','3','316',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1948,'الاجتهاد؟ ومن له أن يجتهد؟ والمحل الذي يقبل الاجتهاد، والمحل الذي لا يقبله، وما يتعلق بالتقليد. ولم يذكر الإمام هذا الكتاب في البرهان. وأحرى المواضع بذكره هذا الموضع. وقد رأينا أن نستوعب القول فيه، ليكون الكتاب محتويا على جملة أصول الفقه، مستعينين بالله. وهو خير معين.

<span data-type="title" id=toc-341>القول في الاجتهاد</span>
والبداية به أولى، إذ هو المصدر، وبه يعرف المجتهد والمجتهد فيه. أما الاجتهاد في وضع اللغة: عبارة عن بذل المجهود، واستفراغ الوسع في فعل من الأفعال (٣٩/ أ). فيقال: اجتهد في حمل صخرة، ولا يقال: اجتهد في حمل','3','317',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1949,'نواة أو برة. ولكنه صار بعرف العلماء عبارة عن: بذل المجهود في طلب الأحكام الشرعية. وهذا الاجتهاد، إنما يكون على شرط صدوره من أهله، مصادفًا محله، تامًا لا تقصير فيه، بحيث يدرك المجتهد من نفسه أنه لم يبق فضلة في الطلب. فهذه ثلاثة أمور لابد من النظر فيها:
الاجتهاد، والمجتهد، والمجتهد فيه.
أما الاجتهاد: فلا يجوز أن يقدم الإنسان على الحكم ببادئ الرأي، وأول مصادفة الدليل، إذا أمكن أن يكون لذلك الدليل معارض أو مسقط، بل لابد من البحث على وجود شرطه، ونفي معارضه. هذا واجب في كل دليل تتوقف دلالته على شرط، أو يصح أن يعارضه [معارض]. وجميع الأدلة الشرعية على ذلك، إلا دليل واحد، وهو الإجماع، فإن الصحيح عندنا أنه لا يشترط فيه، إذا وجدت حقيقته، ولا يتصور له معارض، إذ لا يتصور أن يتعارض الإجماعان، إذ يصير أحدهما خطأ، وقد ثبتت العصمة، (٥٠/ ب) ولا يصح نسخة، إذ الإجماع إنما يكون حجة بعد موت رسول الله - صلى الله عليه وسلم -، ولا نسخ بعد انقطاع الوحي. فمتى وجد المجتهد في مسألة الإجماع، بادر [إلى الحكم]','3','318',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1950,'من غير بحث ولا طلب أمر آخر.
وأما النص المتواتر، فلا يجوز الحكم به عند أول الاطلاع عليه، بل لابد من البحث، هل له ناسخ أم لا؟ فيجب التوقف حتى يثبت هذا الشرط. وإذا كان هذا قولنا في النص المتواتر، فلأن نقول ذلك في الظواهر وأخبار الآحاد أولى.
وقد كنا نقلنا عن بعض الأصوليين أنه نقل عن قوم: أن ذلك غير مشترط، وله الحكم بالدليل عند مجرد الاطلاع عليه، ورددنا على صاحب هذه المقالة، فلا معنى للإعادة. وقد نقل أبو حامد الإجماع على خلاف ما قال هذا القائل. وذلك لأن الدليل [المشترط علمه]، مشروط بانتفاء أمرٍ أو وجوده، فإذا شك في الشرط، فقد شك في المشروط، فكيف يجوز له أن يعمل بما يشك في كونه دليلا؟ فلابد إذًا من البحث عن وجدان [الشروط] واندفاع الموانع، في كل دليل يكون له شرط، أو يصح أن يمنعه مانع. فإذا وجب ذلك، فإلى أي حد يبحث؟ وإلى أي وقت يجوز له الحكم؟
وقد اختلف الناس في هذا على أربع فرق: فقالت فرقة: يجب البحث إلى','3','319',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1951,'أن يظن سلامة الدليل عن المعارض، وأن شرطه حاصل، أي لا يقدر على أكثر من ذلك في مسائل الظنون، كما لا يصح له أن يعتمد العموم مثلا، وإن كان لا يقطع بالاستغراق، فكذلك يحكم بالعموم، إذا ظن انتفاء المخصصات، وكذلك القول في أخبار الآحاد، إذا ظن نفي المعارض، جاز له إثبات الحكم. وهم جرًا إلى كل دليل مشروط.
وقال قائلون: لابد أن يبحث إلى أن يحصل اعتقاد جازم، وسكون نفس، بحيث لا يجوز وجدان مخصص، ولا معارض لدليل، وأما الظن، فلا يكتفى به. وكيف يحكم بدليل، وهو يحدث نفسه بإمكان كونه ليس بدليل؟
وقال القاضي [رحمه الله]: لابد أن يبحث حتى يحصل له علم بوجود الشرط ونفي المانع، [فإن الاعتقاد] من [غير] [علم] سلامة قلب و [سكون نفس]، بل [العالم] الكامل تشعر نفسه بالإمكان وتجويز الإجمال. وهذا القول قريب من الذي قبله، فإن المعتقد أيضا لا يجوز النقيض بحال، (٣٩/ ب) ولو جوز النقيض، لكان ظانًا أو مترددًا. لكن الفرق','3','320',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1952,'بين هذا وبين قول القاضي، أن المعتقد على هذا [الرأي] يكون مصيبًا في الحكم، وإن تبين له الغلط بعد ذلك. والقاضي يرى أن الاعتقاد من غير علم لا يكون مطلوبًا في الشريعة. وسيأتي لهذا تحقيق في مسألة التقليد.
وقال أبو حامد: يشترط في حقه قطع وظن، أما القطع فبالإضافة إلى نفسه بالعجز. قال: ويعلم أن سعيه ذلك ضائع. قال: وهذا أمر يتأتى للمكلف علمه. وأما الظن، فبانتفاء الدليل على نفسه، فلا يكفيه أن يعلم عجز نفسه مع كونه [يجوز] أن يكون المخصص عنده غيره إمكانًا مستويا. وهذا الذي قاله لم يدل عليه، وهو أيضا غير صحيح، إذا لا يتصور أن ينتهي إلى حالة يعلم أن سعيه في مستقبل الزمان ضائع، إلا بأن يعلم انتفاء المخصص في نفسه. أما إذا كان جوزًا له، [فهو] يجوز أن لو بحث أو طلب العلماء، لأمكنه الوصول إليه، فيكف يعلم عجز نفسه في مستقبل أمره، مع تجويزه ثبوت المخصص في نفسه، مطلعا عليه بعض المجتهدين؟ فهذا القول ضعيف.
وأما ما ذكره القاضي من أنه يشترط في حقه البحث إلى حد يحصل له العلم بنفي المخصصات، فهذا القول عسير، وحاصله تعطيل العمل بأكثر الأدلة والأمارات، فإن هذا لا يتأتى اعتيادًا أن يعلم نفيها. وقد ذكر القاضي مسلكين زعم أنهما يفضيان إلى العلم:','3','321',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1953,'أحدهما- أنه إذا نظر في مسألة قتل المسلم بالذمي مثلا، متمسكا بقوله - عليه السلام -: (لا يقتل مسلم بكافر). وقال: هذه مسالة طال خوض العلماء فيها، مع بحثهم عن المعارض والمخصص، والذي صح عندهم مخصص كذا، وجميع ما ذكروه لا يصح أن يكون مخصصا عندي، فيحصل له العلم بانتفاء المخصصات.
فهذا طريق ذكره القاضي. والذي ذكره من باب التقدير، [لا من باب] التحقيق، فإنه لم تتفق قط مسألة في الشريعة اجتمع أهل الإجماع على البحث عن مخصصات العموم [فيها وحصرها]، فكيف تدار أحكام الاجتهاد على المقدرات والمجوزات عقلا، الممنوعات وقوعا؟ ولو قدرنا أن العلماء بجملتهم بحثوا عن مخصصاتها، فمن أين انحصرت له المخصصات عندهم؟ وما المانع من اطلاع بعضهم على غير هذه المخصصات، ولم يذكره [الإنسان]، أو لم [يضمنه] كتابا، أو ذكره لمن لم يتفق للقاضي الاجتماع به؟
فمن أين اجتمع هو بجميع العلماء؟ أو من أين وقف على جميع ما قالوه؟ أو كيف اطلع على ما في نفوس الجميع؟ وهذا محال التصوير والتقدير، وليس فيه سوى التقدير.
المسلك الثاني: قال القاضي: لا يبعد أن يدعي (٥١/ ب) المجتهد','3','322',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1954,'اليقين، وإن لم [يبلغ] الإحاطة بجميع [المدركات]. [إذ] يقول: لو كان الحكم خاصا، لنصب الله تعالى للمكلفين دليلا على تخصيصه، ولما خفي عليهم ذلك. وإذا لم يبلغهم دليل التخصيص، دل على أنه لا مخصص. وهذا قريب من الأول. وما المانع من اطلاع بعض العلماء على المخصص، ويعود جدعا كما في الطريقة الأولى؟ اللهم إلا أن يكون القاضي بنى الأمر على أن كل مجتهد مصيب، فيكتفى بظن فقدان المخصص، كما اكتفي بظن وجدان خبر الواحد. وينتهض ظن انتفاء المخصص علمًا على تكليف القطع بانتفائه، كما قام ظن صدق الواحد علمًا على وجوب (٤٠/ أ) العمل، [على مقتضى] قوله، فهو يقطع بانتفاء المخصص بالإضافة إليه، لا [بانتفائه] باعتبار كل الخلق.
هذا عندنا هو الصحيح، أعني أنه لا يشترط علم انتفاء المخصص بالإضافة إلى كل الخلق، ويكفي ظن الانتفاء من الشريعة، [ثم تختلف] المذاهب بعد ذلك. فمن قال كل مجتهد مصيب، فإنه يقطع بأن الله تعالى [لم] يجعله مخصصا بالإضافة إليه، فيعلم [أن] الله تعالى [كلفه] إجراء اللفظ على عمومه. ومن يقول المصيب واحد، لا يقطع بهذا، بل يظن','3','323',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1955,'شمول التكليف في العموم، ويظن إسناد العمل إلى الخبر الصحيح، ولا يقطع بذلك على حال. هذا أحد الأركان الثلاثة، وهو بيان الاجتهاد التام، وما الذي يشترط في تمامه.

<span data-type="title" id=toc-342>الركن الثاني- المجتهد: </span>ويشترط فيه [شرط] لكونه مجتهدًا، [وشرط] لجواز تقليده.
أما الشرط الذي يكون عند حصوله مجتهدًا، فهو أن يكون محيطًا بأدلة الشرع في غالب الأمر، متمكنًا من اقتباس الأحكام منها، عارفًا بحقائقها ورتبها، عالمًا بتقديم ما يقدم منها، وتأخير ما يؤخر أيضًا. وقد عبر الشافعي [رحمه الله] عن هذا بعبارة وجيزة جامعة فقال: (من عرف كتاب الله [تعالى] نصا واستنباطا، استحق الإمامة في الدين). لأنه [رحمه الله] في مراتب البيان رد الرتب كلها إلى البيان. وليس من شرط المجتهد [بالاتفاق] أن يكون عالمًا بكل مسألة ترد عليه. فقد سئل مالك [رحمه الله] عن أربعين مسألة، فقال في ست وثلاثين منها: لا أدري. وكثير ما يقول الشافعي: لا أدري. وقد توقف كثير من الصحابة في بعض المسائل،','3','324',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1956,'وقال بعضهم: (من أفتى في كل ما سئل عنه فهو مجنون). بل الشرط أن لا يفتي إلا فيما يدري، ويقف [عما] لا يدري، ويميز بين ما يدري، وبين ما لا يدري، فيفوض ما لا يدري لمن يدري. (٥٢/ أ)
والشرط الثاني: وهو [أن] الذي يشترط في جواز الاعتماد على قوله أن يكون عدلًا، وليس هذا شرط في حصول الاجتهاد، بل إنما يشترط لقبول القول خاصة، وإلا فنحن نجوز أن يكون الكافر مجتهدًا. ومن مسائل الأصول: المجتهد إذا كفر [ببدعته]، لم يعتبر قوله في انعقاد الإجماع، ولا ينخرق بمخالفته، فقد سوغوا أن يبلغ مرتبة الاجتهاد مع كفره، ولكنهم أخرجوه من أهل الإجماع في اعتبار الموافقة، والإضراب عنه في حال المخالفة.
هذا تحقيق القول في صفات المجتهد، وما يعتبر في تحصيل المنصب، وما يعتبر في [اعتماد] القول، [وبالله التوفيق].
فإن قيل: وما القدر الذي يفتقر إلى تحصيله في نيل منصب الاجتهاد؟ [قلنا]: ذلك القدر هو معرفة أدلة الأحكام الشرعية، وجهة دلالتها جملة','3','325',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1957,'وتفصيلًا. وأدلة الأحكام أربعة: كتاب الله، وسنة رسوله، والإجماع، ودليل العقل [المقر] على النفي الأصلي. فإنه يصح أن نتلقى منه نفي الأحكام، وإن لم نتلق [منه] ثبوتها. وسيأتي الكلام عليه في استصحاب الحال، إن شاء الله تعالى.
والعلم هذه الأصول على الوجه المذكور، لا يحصل إلا بعد تحصيل العلم بأمور:
أحدها- معرفة أشكال البراهين الصحيح، ووجه دلالتها، وكيف تتلقى المدلولات منها. والفرق بين البرهان والشبهة، والعلم والاعتقاد. وهذا لابد من تحصيله بالإضافة إلى [تحصيل] كل علم نظري. فليس الافتقار إليه من خواص منصب الاجتهاد، بل من لا يحسن [ذلك]، [فلا طريق] له إلى حصول علم نظري. وقد قصدنا ههنا إلى بيان ما يفتقر المجتهد إلى تحصيله،','3','326',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1958,'لا إلى ما ينفرد (٤٠/ ب) هو بالحاجة إليه. واستقصاء القول في ذلك يطول. وقد اشتملت مقدمة الكتاب على كثير من هذه الأبواب، والمقصود أن يكون على بصيرة مما يسنده إلى المأخذ، فيعرف حقيقة الحكم، والحاكم والمحكوم عليه، والمحكوم فيه، ويعرف حقيقة الدليل والشرع، كما [ذكرناه جملة] فيما مضى من الكتاب.
قال الأصوليون: ولابد أن يعرف حقيقة العالم والصانع، و [إثبات حدوث العالم]، والأفعال الاختيارية. [ويصير] بذلك عالما بالحكم، غير مقلد فيه. وقد تقدم أيضا الكلام على هذا في أول الكتاب، حيث تكلمنا على استمداد أصول الفقه من الكلام، ونقلنا في ذلك خلاف أبي حامد، وأنه جوز في حق المجتهد التقليد في القواعد، (٥٢/ ب) وبينا أن ذلك يمنعه من علم الأحكام.
فإذا استقرت هذه الأدلة في نفسه، من حيث الجملة، وعرف الطريق الموصل إلى ذلك، فعليه أن يحصل علم اللغة والعربية، بحيث يفهم مدلول الألفاظ، ويفرق بين النص والظاهر، والمجمل، والعام والأعم، فإن الشريعة عربية، ولا يتصور أن يحيط بفهم الأحكام من لغة العرب من لا يفهمها. وليس يشترط أن يكون في ذلك كالخليل وسيبويه، وأبي عبيدة والأصمعي،','3','327',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1959,'الباحثين عن دقائق الإعراب، ومشكلات اللغة، وإنما يكفيه أن يحصل منها ما تتيسر معرفة ما يتعلق بالأحكام بالكتاب والسنة. فإذا أحاط بهاتين المادتين، افتقر إلى علمين:
أحدهما- معرفة الناسخ والمنسوخ، فإن في الكتاب والسنة ما هو منسوخ، يحرم العمل به، وإن كان متلوًا ومرويًا. وإن لم يحصل علم ذلك، حرم عليه الفتوى. وهو راجع إلى ما قررناه من معرفة حصول شرط [العلم].
الثاني- علم الرواة، من تجريح وتعديل، وطرق الأحاديث، من إسناد وإرسال، فلا يجوز له الحكم دون تحصيل هذه الشروط. وليس أيضًا يلزمه معرفة ذلك في جميع الأخبار المروية عن الرسول - صلى الله عليه وسلم -، [بل] يفتقر إلى ذلك بالإضافة إلى الأخبار المتعلقة بالأحكام. أما [ما] يتعلق بالقصص والمواعظ، وأمر الآخرة، فغير مشترط في حصول درجة الاجتهاد، وإن كان من العلم الشرعي المعين على العمل إعانةً بينة.
ثم أحوال الرواة منقسمة، فمنه: ما تعلم عدالته وضبطه بالضرورة، فهذا الصنف من الناس يستغنى عن البحث في أحوالهم، لظهور عدالتهم، واستقامة حالتهم. وهذا كما يرويه مالك عن نافع عن ابن عمر، فإن هؤلاء القوم، قد','3','328',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1960,'تواتر عند أهل الإسلام [حسن] أحوالهم، واستقامة طريقهم، وشهرتهم في العلم والورع. وما لم يكن على هذا الوجه، وكان أمره مستورًا عن المطلع على روايته، امتنع عليه الحكم بها، حتى يبحث عن حاله، وذلك بأن يسأل عن طريقته، ويستبين أمر سيرته، فيستدل [بذلك على ما] يثبت عنده. وهذا شديد في زماننا مع كثرة الوسائط، واندراس نقل الطرق، وتراخي الأزمنة.
وقد صار بعض الأصوليين لأجل هذه الضرورة، إلى جواز الاكتفاء بتعديل الأئمة، كما ثبت عند الكافة، الانقياد إلى تعديل من روى عنه البخاري ومسلم في (الصحيحين)، وإن كان أكثر الرواة عند أهل العصر مستورون.
هذا هو الذي اختاره أبو حامد، لأجل (٥٣/ أ) الضرورة، والإمام أيضا يشير إلى هذا، فإنه قال: ويبعد في حق الراوي أن يعرف حالة (٤١/ أ) كل من يروي له خبرًا، فيكتفي بتعديل الأئمة بعد أن يعرف أن مذهبهم في التعديل مذهب مستقيم، فإن كان الناس أيضًا قد اختلفوا فيما يعدل به ويجرح. والصحيح عندنا خلاف ذلك، وهذا تقليد محض، ولا يكون المجتهد على بصيرة في علم الحديث على هذا التقدير، بل لا يكون مقلدًا في بعض المواد. وإذا تطرق التقليد على المادة، لم يكن المقلد مجتهدًا فيما ينبني عليها.
وأما قوله: إن ذلك عسير، فهو لعمري كذلك، ولأجل','3','329',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1961,'[هذا] [فات] علم الاجتهاد، ولم يبق إلا المقلدة خاصة. فإذا حصلت هذه الأمور معلومة، [وتعرض لحفظ الجزئيات من الآيات والأخبار، وضبط ما أجمعوا عليه]، فقد حصل منصب الاجتهاد. هذا يشترط في المجتهد المطلق.
وهل يجوز أن يتجزأ منصب الاجتهاد، [حتى] يكون مجتهدًا في مسألة واحدة؟ هذا فيه نظر، وقد جوزه أبو حامد، وهو عندي بعيد، وكيف يتفق أن يكون مجتهدًا في المسألة الواحدة عند الظفر بدليلها؟ وقد قدمنا أنه لا يجوز الحكم بالدليل حتى تحصل غلبة الظن بفقدان المعارض من الشريعة، فإن لم يكن [الناظر] بصيرًا بما اشتملت عليه الشريعة، كيف يجزم أو يظن نفي المعارض؟ اللهم إلا أن تجمع الأمة في مسألة على ضبط مأخذها، ويكون الناظر المخصوص محيطًا بالنظر في تلك المآخذ، فيصح أن يكون مجتهدًا فيها على الوجه المذكور.
وإذا ثبتت هذه الأمور، فعليه في الترتيب وظيفة: وهي أن يجعل نفي الأحكام مستندة إلى البراءة الأصلية، مقدمًا في نظره، ثم ينظر فيما استثنته الأدلة، فما وجد فيها دليلًا يقتضي انتقالًا، انتقل به، وما لم يجد فيه دليلًا، أبقاه على النفي الأصلي. ثم الناقل لا يخلو: إما أن ينتقل مطلقًا أو مشروطًا، فإن نقل','3','330',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1962,'مطلقًا، اكتفى بالاطلاع، كالإجماع، ولم يبحث عما رواءه على حال. وإن كان ينقل مشروطًا، يبحث بعد اطلاعه على شرط نقله، كما تقدم بيانه.
ومعظم ذلك يشتمل على ثلاثة فنون: علم الأصول، وعلم الحديث، وعلم اللغة. وقد قلنا: إنه لابد من تحصيل المعرفة بالمرسل والرسول الصادق، بالاطلاع على وجه دلالة المعجزة.
وإذا قلنا: لابد من معرفة اللغة، فإن بنينا على أن الشرع [لم يتصرف] في اللغة العربية- وهو اختيار القاضي- اكتفينا في معرفة الأحكام بمعرفة اللغة العربية. وإن قلنا: إنه تصرف، لم نكتف بذلك (٣٥/ ب)، [وقلنا: ] لابد من معرفة لغة الشرع. وإن طلبنا- على هذا الرأي- معرفة اللغة العربية، فلكونها في أكثر أحوالها موافقة للغة الشرعية، وإلا فلا خفاء على هذا، بأنا لابد أن ننظر في كل لفظة، هل تصرفت الشريعة فيها أم لا؟ ولو جعل- على هذا الرأي- الاعتماد على فهم لغة [الشرع]، لكان حسنًا، بل لا يقال غيره.
وبيانه: أنا إذا فهمنا معنى الكلمة عند أهل الغلة، لم نكتف به، حتى ننظر ما مدلولها في الشرع؟ وإن فهمنا مدلولها في الشرع، اكتفينا، ولم ننظر في مدلولها في اللغة أصلًا، لأجل فهم الحكم. فلا وجه على رأي من أثبت التصرف، إذا قال: لابد من فهم اللغة، أن يكون مراده: إلا لغة الشارع - عليه السلام -. هذا تمام الكلام على ما يفتقر المجتهد إلى تحصيله من العلم. وهو تمام الركن الثاني.','3','331',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1963,'<span data-type="title" id=toc-344>الركن الثالث- المجتهد فيه: </span>وهو كل حكم شرعي لم نتعبد فيه [بالعلم]. واحترزنا بقولنا: كل حكم شرعي، عما يتعلق بالكلام في المعقولات، كقدم الصانع، وحدث العالم، (٤١/ ب) وثبوت الصفات [القديمة]، وما يتعلق بجواز بعثة الأنبياء، وجميع ما يتعلق بالكلام على الحقائق، فإن تلك الحق فيها واحد، لا يتصور [فيها]، [لا] التبديل ولا التغيير، وأما الأحكام الشرعية فوضعية، يجوز أن يثبتها الله تعالى، ويجوز أن لا يحكم على الخلق، ويجوز بعد الحكم أن يكلفهم تحصيل العلم، ويجوز أن يكتفي منهم بظن الإصابة.
وإنما نعني بالمتجهد فيه، ما لا يكون المخطئ [آثمًا، ونعني بغير المجتهد فيه من الأحكام الشرعية، ما يكون المخطئ] فيه آثمًا، كوجوب الصلوات الخمس والزكاة، وما اتفقت عليه الأمة، وعليها أدلة قطعية، يأثم فيه المخالف، فليس ذلك محل الاجتهاد. هذه هي الأركان. فإذا صدر الاجتهاد من أهله، وصادف محله، كان المكلف مأجورًا، [سواء] أصاب الحكم أم لم يصبه، على ما سيأتي بيانه بعد ذلك.
وقد ظن قوم أنه يشترط في الاجتهاد أن لا يكون في زمن الرسول','3','332',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1964,'[- صلى الله عليه وسلم -]. وقال قول: يشترط في المجتهد أن لا يكون رسولًا، فلم يجوزوا للرسول الحكم بالاجتهاد. فلنفرض في ذلك مسألتين:

<span data-type="title" id=toc-345>مسألة: اختلفوا في جواز التعبد بالاجتهاد في زمان رسول الله - صلى الله عليه وسلم </span>-، فمنعه قوم، وأجازه [آخرون]. وقال قوم: يجوز للقضاة والولاة في غيبته، لا في حضوره. والذين جوزوا، منهم من قال: يجوز بالإذن، ومنهم من قال: يكفي سكوت رسول الله - صلى الله عليه وسلم -، ثم اختلف (٥٤/ أ) المجوزون في وقوعه، والنظر في الجواز والوقوع.
أما الجواز العقلي، فإنه يشمل المذاهب كلها، إذ ليس في تجويزه مناقضة عقل، إذ ليس محالًا في نفسه، ولا يفضي إلى محال. وجهات الاستحالة مضبوطة عند العقلاء، والمستحيل لا يتصور تقديره بحال. ونحن باضطرار [من عقولنا، نعلم] أن الله تعالى لو أمر خلقه بشيء من هذه المذاهب، لم يتناقض، ولتلقته العقول بالقبول، والمستحيل لا يتصور بحال.
فإن قيل: تلقي الحكم من النص ممكن، ولا مصلحة للعباد في العدول عن النص الممكن إلى الاجتهاد، فهذا باب [من أبواب] الاستفساد. قلنا: قد [بينا] بطلان ما يتعلق بالصلاح والأصلح، فإن [جميع] ذلك مبني','3','333',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1965,'على التحسين والتقبيح بالعقل. وقد تقدم بطلانه، على أنا [لسنا نسلم] أن ذلك لا صلاح فيه، بل لعل الله تعالى علم صلاح عباده في ذلك. وكيف ينكر [هذا]، [وقد] كان تعميم [الأحكام] بالنص ممكنًا ولم يفعله الله تعالى؟ وشرع القياس، فليكن الاجتهاد [في زمان الرسول] كذلك.
فإن قيل: الاجتهاد مع النص محال، لأنه إنما يجتهد ليصادف النص، فكيف يجتهد مع وجدان النص؟ قلنا: المضاد للاجتهاد نفس النص [لا إمكان النص]، كيف وقد أمر رسول الله - صلى الله عليه وسلم - بالحكم بشهادة الشهود، وإن كان تلقى [الحكم من الوحي] ممكنا له، حتى قال: (إنكم لتختصمون إلي، ولعل بعضكم [أن يكون] ألحن بحجته [من بعض] فأقضي له على نحو ما أسمع، فمن قضيت له بشيء من حق أخيه، فلا [يأخذ منه] شيئا، فإنما أقطع له قطعة من النار).','3','334',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1966,'فإن قيل: إذا أثبتم الجواز من جهة العقل، فكيف الواقع من ذلك؟ قلنا: قال أبو حامد: الصحيح أنه ثبت الحكم [بالاجتهاد] في غيبته، بدليل قول معاذ حيث بعثه رسول الله - صلى الله عليه وسلم - إلى اليمن فقال [له]: (بم تحكم؟ قال: بكتاب الله). وفي [الحديث] [قال]: (فإن لم [تجد]؟ قال: أجتهد رأيي). فزكاه رسول الله - صلى الله عليه وسلم - في ذلك، ولم يقم دليل الاجتهاد في حضرته. قلنا: الأمر عندنا على خلاف ما قال أبو حامد، وقد قدمنا أن قول معاذ ليس [بصحيح] (٤٢/ أ) عند المحدثين، وإنما يرويه بعض أهل حمص عن بعض أصحاب معاذ، فهو من المراسيل، وأكثر المحدثين على ردها. ثم العجب بأنه قال: تلقته الأمة بالقبول. نعم، قام الدليل على الاجتهاد في حضرته، والحديث الصحيح: (أن بني قريضة نزلوا على (٥٤/ ب) حكم سعد بن معاذ، ورضي رسول الله - صلى الله عليه وسلم - بذلك، فحكم سعد بأن تقتل المقاتلة،','3','335',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1967,'[وأن] تسبى الذرية. فقال النبي - صلى الله عليه وسلم -: (لقد حكمت فيهم بحكم الله تعالى، أو بحكم الملك). فهذا [حديث]. صحيح، لا شك في ذلك.
وقد جاء أيضًا من حديث [صحيح]. أنه قال لبعض أمرائه: (إن سألوك أن تنزلهم على حكم الله، فلا تفعل، فإنك لا تدري هل تصيب حكم الله أو لا، وإن سألوك أن [تنزلهم] على حكمك فافعل). هذا معنى الحديث دون لفظه.
والصحيح عندنا أن الحكم بالاجتهاد في غيبته كثير، فإنه كان يرسل الحكام والقضاة يحكمون بين الناس، والعادة وأهلها على التداني، ومعظم الوقائع لا نصوص فيها، فيفضي الأمر إلى القضاء بالاجتهاد، أو الوقوف في أكثر الأحكام.
وقد ذكر الأصوليون أخبارًا غير هذه، فنقلوا أنه قال لعمرو بن العاص: (اجتهد). قال: أجتهد وأنت حاضر؟ قال: (اجتهد، فإن أصبت فلك أجران، وإن أخطأت فلك أجر [واحد]). وقال لعقبة بن عامر ولرجلين من','3','336',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1968,'أصحابه: (اجتهدوا، فإن أصبتم فلكم عشر حسنات، وإن أخطأتم فلكم حسنة). وهذا هو الصحيح عندنا.

<span data-type="title" id=toc-347>مسألة: اختلفوا في جواز تعبد النبي - صلى الله عليه وسلم - بالاجتهاد فيما لا نص فيه</span>. والاختلاف في الجواز والوقوع. أما الجواز فواضح، وطريقة ما تقدم في المسألة السابقة حرفًا حرفًا، فلا معنى لإعادته.
فإن قيل: المانع [من ذلك] أنه قادر على استكشاف الحكم بالوحي الصريح والنص الحق، فكيف يتلقاه [بحكم الظن]؟ قلنا: فإذا استكشف فقيل له: [حكم الله] عليك ما أدراك إليه اجتهادك، فهل له أن ينازع [الله]؟ أم يجب عليه تلقي ذلك بالقبول؟ وما كان له أن يخالف أمر [الله] المتوجه عليه.
فإن قيل: قوله نص قاطع يضاد الظن، والظن يتطرق إليه إمكان الخطأ، فيفضي إلى أن يكون الخطأ ممكنًا محالًا. قلنا: أما من يقول كل مجتهد مصيب، فإنه يمنع الخطأ في اجتهاد غيره، فكيف في اجتهاده؟ وأما من يقول المصيب واحد، فإن أهل التحقيق منهم [قد] منعوا أن يخطئ في اجتهاده، بل عينوا','3','337',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1969,'الحق فيه. وكذلك كل اجتهاد تجمع الأمة عليه، لتحقق عصمة الأمة والرسول جميعًا. ومنهم من جوز عليه الخطأ، ولكن لا يقر عليه.
فإن قيل: فإذا جاز أن يحكم بالاجتهاد، وخالف اجتهاده اجتهاد غيره، فإن منعتم المجتهد من اتباع اجتهاده، فقد خالفتم (٥٥/ أ) قاعدة الاجتهاد، وإن سوغتم المخالفة، فذلك يمنع الانقياد والقبول، ويوجب المخالفة [للنبي]، وذلك ممنوع. قلنا: إذا عرفهم على لسانه أن حكمهم ما غلب على ظنهم، وإن خالف اجتهاد [رسول الله - صلى الله عليه وسلم -]، فلا مناقضة، والمطاع هو الله تعالى على الحقيقة. وقد [بينا] أنه لا حكم إلا لله تعالى، وأن الرسول أو غيره، إذا أمروا وأوجبوا، لم يجب شيئا بإيجابهم، بل بإيجاب الله تعالى طاعتهم. ألا ترى أنه لو شهد عند رسول الله - صلى الله عليه وسلم - شاهد لا يعرف فسقه، [فيقضي] بشهادته، ثم يشهد ذلك الشاهد عند حاكم يعلم بفسقه، (٤٢/ ب) لم يقض بشهادته، ولا يكون في ذلك غض من منصب [رسول الله - صلى الله عليه وسلم -]، ولا قدح في النبوة، إذ إنما عمل العامل بحكم الله تعالى الموجه عليه.
فإن قيل: فلو قاس على أصلٍ، أتجوزون القياس على فروعه أم','3','338',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1970,'[تمنعون]؟ فإن منعتم، فهو باطل، إذ صار [أصلًا] بالنص من الرسول - عليه السلام -. وإن جوزتم كان باطلا، إذ القياس على فرع العلة ممنوع عند المحققين. قلنا: قد قال بعض الأصوليين: يجوز القياس على فرع العلة مطلقًا. وقال قائلون: يجوز القياس على فرع أجمعت الأمة على إلحاقه بأصله. وكذلك الفرع الذي ألحقه الرسول [- صلى الله عليه وسلم -] بأصل.
أما الذين جوزوا القياس على فرع العلة مطلقًا، فإنهم زعموا أن الحكم بالقياس حكم بالتوقيف المحض. لاسيما إذا رد الأمر إلى ما قررناه من أن القياس إثبات معقول الألفاظ، يضاهي المفهوم عند أهل اللسان. ولا فرق في القياس على الحكم الثابت بالمنظوم أو المفهوم، فكذلك الحكم الثابت بالمعنى المعقول وإذا كان هذا مذهبهم في فرع غير الرسول، فما القول في فرع الرسول - عليه السلام -؟
وقال قائلون: هذا إنما يسوغ بشرط أن يكون الفرع ثابتًا بطريقٍ توقيفي؟ أما أن يكون [بقياس] الرسول [- عليه السلام -]، أو أهل الإجماع، فإنه إذا كان كذلك، فقد صار أصلًا بالنص أو الإجماع، وخرج عن كونه فرعًا. والسؤال أيضًا ساقط على هذا المذهب.','3','339',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1971,'والصحيح عندنا أنه لا يجوز القياس على فرع العلة بحال، سواء كان فرعًا للرسول، أو لأهل الإجماع، والدليل عليه هو الدليل على منع القياس على الفرع مطلقًا.
وطريق تقرير الدليل أن نقول: لما قاس على فرع العلة، [أيراعي] في الإلحاق جامع الأصل، أو لا يراعيه؟ فإن راعى جامع الأصل، فالقياس على الحقيقة إنما هو على الأصل، وتوسط الفرع الأول تطويل (٥٥/ ب) طريقٍ من غير فائدة، [فليلحق] الفرع الثاني بالأصل الأول. وإن أضرب عن جامع الأصل، واستنبط جامعًا آخر، كان ذلك باطلًا، فإن الحكم في الفرع إنما يثبت مقيدًا بوجود الجامع، [وإن] لم يوجد الجامع الذي يقيد الحكم به، فالحكم في معنى المنفي على الحقيقة.
وبيان ذلك بالمثال: أنه إذا قال: الخمر حرام، وفهم أن العلة الإسكار، فتحقيق الكلام: الخمر المسكرة حرام. وإذا ألحقنا النبيذ بالخمر بجامع الإسكار، جعل الإسكار قيدًا للتحريم، فيكون تقدير التوقيف: النبيذ المسكر حرام. وإذا ألحقنا غير النبيذ به بغير الإسكار، كان ذلك حيدًا عن معنى القياس والاعتبار. فلا يجري الحكم على حقيقة التوقيف اللغوي، ولا التقدير الشرعي. فلا سبيل إلى الجمع بين الفرع الثاني والفرع الأول بغير جامع الأصل.
وإن وقع الجمع بينهما بجامع الأصل، فالمعتبر على الحقيقة هو الأصل.','3','340',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1972,'وإدخال الفرع الأول في الإلحاق تطويل الطريق من غير فائدة. ولا فرق على هذا بين فرع ألحقه الرسول [- صلى الله عليه وسلم -] بالأصل، أو فرع أجمعت الأمة [عليه]. هذا هو الصحيح عندنا. هذا هو الكلام في [طرفي الجواز.
أما الوقوع: فقد قال به [قوم]، وأنكره [آخرون]، وتوقف فيه فريق [ثالث].
والظاهر عندنا أنه كان ينتظر التوقيف. وإن حكم برأيه، فلا يكون ذلك إلا بتوقيف أيضًا. والقاطع في النفي (٤٣/ أ) والإثبات غير ثابت عندنا.
وقد احتج القائلون بأنه كان يحكم برأيه، أنه عوتب في أسارى بدر، قال الله تعالى: {ما كان لنبيٍ أن يكون له أسرى حتى يثخن في الأرض}. وهذا ليس بقاطع في الدلالة، وللتأويل تطرق إليه. إذ يحتمل أن يكون [مخيرًا بين] القتل والأسر، فعين بعض الأصحاب وطلب الفداء، [فنزل] العتاب','3','341',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1973,'مع أولئك، وأضيف الأمر إلى الرسول تشريفًا. ولهذا قال لعمر: (لقد عرض علي عذاب أصحابك). ولهذا قال تعالى: {تريدون عرض الدنيا}. والنبي - صلى الله عليه وسلم - منزه عن مثل هذا. وقال: {لولا كتاب من الله سبق لمسكم فيما أخذتم عذاب عظيم}.
احتجوا أيضًا: بأنه لما (نهى عن قطع شجر الحرم، قال له العباس: إلا الإذخر [يا رسول] الله. فإنه لقينهم وقبورهم. فقال [- صلى الله عليه وسلم -]: (إلا الإذخر). فلو كان توقيفًا، لم يستثن عند قول العباس. وهذا أيضًا ليس','3','342',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1974,'بقاطع، إذ يحتمل أن يكون التوقيف نزل عليه عند قول العباس: إلا الإذخر. وهو أيضًا، وإن قال باجتهاد، فلابد أن يكون ذلك الاجتهاد مأذونًا فيه (٥٦/ أ).
وقد احتج المانعون من الاجتهاد، بأنه لو كان [يجتهد]، لما توقف في أمر اللعان والظهار وغيرهما من المسائل، التي لا يتأتى الانفكاك عنها، ولكان ينتظر الوحي فيها، ولما توقف، دل على انتظار التوقيف. وهذا أيضًا غير قاطع، بل ليس له دلالة ظاهرة، فإنه وإن حكم بالاجتهاد، فالاجتهاد لا يجري في كل موضع، فلعله كان يتوقف في [الموضع] الذي لا مجال للاجتهاد فيه، وليس يتمكن [المجتهد] من الاجتهاد في كل موضع، بل لابد من مراعاة المحل واستيعاب الشروط. فالاجتهاد [قد] يتعذر في بعض الأمور، فلا يدل التوقيف [في] بعض المسائل على الامتناع على الإطلاق.
احتجوا بأنه لو كان يحكم بالاجتهاد، لأفضى ذلك إلى أن يتغير اجتهاده، فيكون ذلك سببًا للنفرة [عنه]، وإدخال التهمة في الشريعة، وذلك قدح في أصل الدين. قلنا: هذا باطل من وجهين:
أحدهما- أنه لا تهمة في ذلك، فإنه إنما يفعل ما يفعل بإذنٍ وشرعٍ، ولا تهمة على المجتهد إذا تغير اجتهاده، فليكن الرسول - عليه السلام - كأحدهم.
الوجه الثاني- إن التنفير غير ملتفت إليه في منع ورود الشرع بما يقتضي التنفير، وقد قال الله تعالى: {وإذا بدلنا ءايةً مكان ءايةٍ والله أعلم','3','343',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1975,'بما ينزل قالوا إنما أنت مفتر}. ولم يكن ذلك مانعا من ورود النسخ، فكذلك يصح أن يحكم بالاجتهاد، وإن أفضى ذلك إلى التنفير. فلا يلتفت إلى شيء من هذه الأوهام. ولا وهم بعد نصب الأدلة القاطعة.
احتجوا بقوله تعالى: {[قال] الذين لا يرجون لقاءنا ائت بقرءانٍ غير هذا أو بدله قل ما يكون لي أن أبدله من تلقاء نفسي إن أتبع إلا ما يوحى إلي}. قالوا: فهذا يدل على أنه لا يحكم بالاجتهاد. وهذا غير صالح للاستدلال من أوجه:
أحدها- أن هذا إنما هو أمر يختص [بتغيير] القرآن، كالإتيان بقرآن آخر. وهذا لا يقدر عليه إلا الله سبحانه [وتعالى]، وبذلك يتحقق كونه معجزًا. فأين هذا من جواز الحكم بالاجتهاد وامتناعه؟
الثاني- إن قوله: {إن أتبع إلا ما يوحى إلي}، كلام غايته أن يكون عامًا، فيخصصه بما لا يقبل الاجتهاد [من الأحكام، ولا يكون طريق ثبوته إلا التوقيف.
الثالث- أنا وإن جوزنا له الاجتهاد]، فلا يكون ذلك من تلقاء نفسه، بل لا يكون ذلك إلا بوحي وتوقيف، ويندرج تحت قوله: {إن (٤٣/ ب) أتبع إلا ما يوحى إلي}. وإذا حكم بالاجتهاد، فقد حكم [بما أوحي إليه، وقد كنا قدمنا أن الحكم بالاجتهاد، حكم] بالتوقيف المحض.','3','344',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1976,'الرابع- إن هؤلاء القوم [إن] أرادوا القطع بما حكموا [به]، فالآية لا تدل دلالة القواطع، ويلتحق الظاهر (٥٦/ ب) في محل طلب العلم بالمجملات، ولا يصح التمسك به. وإن أرادوا أن ذلك مظنون، فقد بينا ما في الآية من الكلام، والله الموفق [للصواب].
قالوا: لو حكم بالاجتهاد، لأمكن أن لا يوافق الصلاح في البعض، وإن أصاب في البعض، ويمتنع أن يثبت [الحكم] إلا لمصلحة. قلنا: قد تقدم من أصولنا أن الأحكام لا ينبني إثباتها ورفعها على المصالح على حال، ولله [تعالى] أن يتعبد عباده بما شاء، وإن علم أنه لا صلاح لهم في ذلك. وقد عبد الله [تعالى] الكفار بالإيمان، مع علمه بأنهم لا يؤمنون، فلا صلاح لهم في تكليف يعرضهم إلى النار [وغضب] الله تعالى إلى غير نهاية.
ويجوز أيضا أن يوفق الله تعالى رسوله في اجتهاده، بحيث لا يظهر له إلا ما فيه الصلاح في الدنيا والآخرة. كل ذلك ممكن من الله تعالى، فلا استحالة في ذلك أصلًا.','3','345',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1977,'والظاهر عندنا أن جميع أحكامه كانت بالتوقيف عن وحي صريح ناصٍ على التفصيل. وبالله التوفيق، وهو حسبنا ونعم الوكيل.

<span data-type="title" id=toc-349>النظر الثاني: في أحكام الاجتهاد</span>، والنظر في حق المجتهد؛ في تأثيمه وتخطئته وإصابته، وتحريم التقليد عليه، وتحريم نقض حكمه. فهذه أحكام.
الحكم الأول: النظر في تأثيم المجتهد في الاجتهاد، والإثم ينتفي عن كل من جمع صفات المجتهدين، والمجتهد إذا تمم الاجتهاد وصادف المحل، فهو غير آثم بحال، سواء أصاب أو أخطأ، والإثم عن المجتهد منتف. والذي نختاره أنه لا ملازمة بين الإثم والخطأ. وقد قال قوم: إن كل مخطئ آثم، وكل آثم مخطئ، وطردوا ذلك وعكسوه. فلنقدم حكم الإثم أولًا.
نقول: النظريات تنقسم إلى قطعية وإلى ظنية، ولا إثم في الظنيات بحال، والإثم في القطعيات [على تقدير الخطأ ثابت]، والقطعيات ثلاثة أقسام: كلامية وأصولية وفقهية.
أما الكلامية: فنعني بها العقليات، والحق فيها واحد، فمن أخطأ الحق فهو آثم، ويدخل [فيه] حدث العالم، وإثبات الصانع وصفاته، وما يستحيل عليه، وما يجوز في حكمه، وبعثة الرسل وتصديقهم بالمعجزات، وجواز الرؤية، وخلق الأعمال، وإرادة الكائنات، وجميع ما الكلام فيه مع المعتزلة والروافض والحشوية وغيرهم من أهل الأهواء. ولا يندرج المطلوب من','3','346',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1978,'الكلام تحت حد. وقد تقدم هذا في أول الكتاب.
فهذه المسائل الحق فيها واحد، وإن أخطأه فهو آثم، فإن أخطأ فيما يرجع إلى الإيمان بالله ورسوله فهو كافر، وإن أخطأ فيما لا يمنع من معرفة الله [سبحانه]، ومعرفة رسوله، كما في مسألة (٥٧/ أ) الرؤية، وخلق الأعمال، وإرادة الكائنات، وأمثالها، فهو آثم من حيث عدل عن الحق، [وضال] ومخطئ من حيث أخطأ الحق [المتيقن]، ومبتدع من حيث قال قولا مخالفًا لمذاهب السلف الصالح، ولا يلزم الكفر.
وأما الأصولية: فنعني بها كون الإجماع حجة، وكون خبر الواحد والقياس حجة، ووجوب العمل بالظواهر والعمومات، وكون صيغة (افعل) تقتضي الطلب ظاهرًا. وقد تقدم بيان تفصيل مسائل الأصول. وكذلك القول بالترجيح بين (٤٤/ أ) الأمارات عند التعارض. وكل ذلك عليه أدلة قطعية، يأثم من أخطأ فيها. وكذلك تفاصيل مسائل الإجماع، من اعتبار إجماع من بعد الصحابة، وكذلك المصير إلى إحداث قول ثالث، وكذلك اشتراط انقراض العصر، وانعقد الإجماع عن اجتهاد. ولا معنى للإطناب في التعداد واستقصاء التفصيل في ذلك. وما تقدم في الكتاب يبين مواقع القطع من مظان الاجتهاد.
ومن جملة ذلك عندنا اعتقاد كون الحق واحدًا في الظنيات على ما سنبينه. فإن هذه المسائل فيها أدلة قطعية، والمخالف فيها آثم.','3','347',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1979,'وأما السمعيات: فالقطعيات منقسمة إلى: ما علم ضرورة من دين الأمة، فجاحد ذلك كافر، كوجوب الصلاة والصوم والزكاة والحج، وتحريم الربا والغصب وشرب الخمر. فإن هذه ضرورية في الدين، وجاحد وجوبها [ومحلل] المحرم منها مكذب للدين، فيكون بذلك كافرًا. وإلى قطعية ثبتت بالنظر، فيكون جاحدها آثما لا كافرًا، وهو كل حكم [شرعي] علم بالإجماع، فجاحد مخطئ وليس بكافر، إذ إثبات الإجماع، وكونه حجة في الشريعة نظري، لا ضروري.
فإن قيل: وكيف يكون وجوب الصلوات معلوم ضرورة، وإنما يتلقى من أخبار الرسول [- صلى الله عليه وسلم -]، وصدق الرسول إنما يعلم نظرًا، وقد ذكرتم فيما تقدم أنه لا يتصور ترتب العلم الضروري على مقدمات نظرية، وهذا ضد ذلك؟ قلنا: نعني به أنه علم ضرورة، أن الرسول - صلى الله عليه وسلم - أخبر بوجوبه، فمن جحد الوجوب، فهو مكذب للرسول [- صلى الله عليه وسلم -]، من كذب الرسول، فهو كافر.
فأما ما عدا هذين القسمين من الفقهيات الظنيات، فليس فيها دليل قاطع، فلا إثم فيها بحال. وهل يتصور فيها الخطأ أم لا؟ [فيه] خلاف بين أهل الأصول، على ما سنذكره، إن شاء الله تعالى.','3','348',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1980,'وقد ذهب بشر المريسي إلى إلحاق [الفروع بالأصول، فأثبت التأثيم في المجتهدات. وذهب الجاحظ والعنبري إلى إلحاق] الأصول بالفروع (٥٧/ ب)، فأسقطوا] التأثيم في القطعيات. وقد تقدم الرد على الفرق الثلاث فيما سبق، فلا معنى [لإعادته].
والذي نذكره الآن إقامة الدليل على أنه لا إثم في المجتهدات، عند من [يقول] كل مجتهد مصيب، وعند من يقول المصيب واحد. والدليل القاطع في ذلك: الإجماع المنقول تواترًا عن أصحاب رسول الله - صلى الله عليه وسلم -، فإنهم كانوا يجتمعون ويتشاورون ويختلفون، مع إكرام بعضهم بعضا، وإجلاله والاعتراف بفضله ودينه، وتمكينه من الفتوى وتحسين الظن، وعرف منهم: (أقول برأيي وتقول برأيك). وهذا متواترًا لا [يشك] فيه بحال. وليس عندي في مسائل الأصول مسألة أظهر من هذه.
وكذلك ما جرى لهم في مسائل الفرائض، وكانوا لا يعترض بعضهم على بعض، ولا يتعرض لنقض قضائه، وقد بالغوا في تخطئة الخوارج، وقتال مانعي الزكاة. وكذلك من رأى نصب [إمامين] في [صقع]. بل لو أنكر منكر','3','349',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1981,'وجوب الصلاة والصوم، وتحريم الربا والسرقة، لبالغوا في الإنكار والتغليظ عليه، لأن فيها أدلة قطعية. ولو كانت المسائل كلها على هذا الوجه، لعظم الأمر واشتد النكير، ولأوشك أن ينقل ذلك تواترًا، والنقل على خلاف ذلك، كما ذكرناه. (٤٤/ ب).
وكما علم أنهم لم يوجبوا على العوام النظر والبحث عن أدلة المسائل، كذلك علم أنهم مكنوا العوام من تقليد من رأوا من المفتين. وكيف لا يكون كذلك، والحكام في آحاد البلدان يحكم كل واحد بما يراه، ولا يعترض عليه غيره، ولا يتعرض لتتبع أحكامه؟ والأمر على خلاف ذلك ضرورة، فثبت بهذا أنه لا إثم في المجتهدات على حال.
فإن قيل: إنما سكتوا خوف ثوران الفتنة، وإقامة الحروب وعظم المحذور. قلنا: هذا محال، فإن القوم مع صلابتهم في الدين، وتعظيم أمر الشرع، لا يتوقفون لمثل ذلك، ما بالهم لم [ينكفوا] عن تخطئة [مانعي] الزكاة، مع قرب وفاة رسول الله - صلى الله عليه وسلم -، وحصول الارتداد، وانتقاض أمر الإسلام؟ بل كان القوم لا يخافون في الله لومة لائم. هذا مع كثرتهم، فكيف يتفق من الجم الغفير التواطؤ على باطل، وأن يكونوا مع صفة [الصلابة] في الدين والغضب [له]؟ هذا مما تحيله العادة قطعًا.
فإن قيل: فقد نقل الإنكار والتشديد والتأثيم من بعضهم على بعض، حتى قال ابن عباس: (ألا لا يتقي الله زيد بن ثابت يجعل ابن الابن ابنًا، ولا يجعل','3','350',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1982,'أب الأب أبًا). وقال: (من شاء باهلته، إن الله لم يجعل في المال النصف والثلثين). وقالت عائشة رضي الله عنها: (اخبروا زيد بن أرقم أنه أبطل جهاده مع رسول الله - صلى الله عليه وسلم -، إلا أن يتوب). وقالت لأبي سلمة بن عبد الرحمن: (فروج يصرخ مع الديكة).
قلنا: ما تواتر إلينا من تعظيم بعضهم بعضًا، وتسليمهم له القضاء والفتيا، وتمكين العامة من الاستفتاء والانقياد إلى الوالي في الأحكام، وإلى القضاة في الشرع، معلوم ضرورة. وأما الإنكار، فمنقول على جهة الآحاد، فكيف يترك ما علم ضرورة إلى نقل آحاد يتطرق إلى السند الاحتمال، وإلى المتون التأويل؟ فلا سبيل إلى [التشكك] فيما علم ضرورة.
[ثم] هذه الوقائع [القليلة] التي نقل فيها الإنكار، فلعل المنكر أن يكون توهم فيها أدلة قطعية، وهو ظاهر قول ابن عباس في قوله: (إن الله (٥٨/ أ) لم يجعل في المال النصف والثلثين).
ولعل عائشة رضي الله عنها رأت أن حسم الذريعة قطعي، ولا بعد في [التنازع] في مسألة معينة، هل هي قطعية أو ظنية؟ فلا تترك لذلك القواعد المعلومة الكلية.
وأما أبو سلمة بن عبد الرحمن، فالظاهر أن عائشة أنكرت عليه الفتوى، من جهة أنها لم تره بلغ مبلغ المجتهدين، ولذلك قالت: (فروج يصرخ مع','3','351',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1983,'الديكة). أو يكون وضع عندها الاجتهاد في غير محله، بأن يكون اجتهد في موضع توقيف. ونحن لا ننفي الإثم في المسائل على الإطلاق، وإنما ننفيه إذا صدر من الأهل مستوفى الشروط، مصادف المحل، فحينئذ ينتفي التأثيم فيه. هذا هو الكلام على التأثيم.
وأما التخطئة: وهو الحكم الثاني، فقد اختلف الناس فيه، واختلف الرواة عن الشافعي وأبي حنيفة. وبالجملة فقد ذهب قوم إلى أن كل مجتهد في الظنيات مصيب. وقال قوم: المصيب واحد. واختلف الفريقان جميعا، هل في الواقعة التي لا نص فيها حكم معين لله تعالى، هو مطلوب المجتهد؟
فالذي ذهب إليه محققو المصوبة، أنه ليس في الواقعة التي لا نص فيها حكم معين يطلب بالظن، [بل الحكم يتبع (٤٥/ أ) الظن]، وحكم الله [تعالى] على كل مجتهد ما غلب على ظنه. وإليه ذهب القاضي.
وذهب قوم من المصوبة إلى أن فيه حكما معينا يتوجه إليه الطلب، إذ لابد للطلب من مطلوب، ولكن لم يكلف المجتهد إصابته، فلذلك كان مصيبا، وإن أخطأ ذلك الحكم المعين، الذي لم يؤمر بإصابته، بمعنى أنه أدى ما كلف وأصاب ما عليه.
وأما الذي ذهبوا إلى أن المصيب واحد، فقد اتفقوا على أن فيه حكما معينا لله تعالى، ولكن اختلفوا هل عليه دليل أم لا؟ فقال قوم: لا دليل عليه (٥٨/ ب)، وإنما هو مثل [الدفين]، يعثر الطالب عليه بالاتفاق، فلمن عثر','3','352',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1984,'[عليه] أجران، ولمن حاد عنه أجر واحد، لأجل تعبه وطلبه.
والذين ذهبوا إلى أن عليه دليلا، اختلفوا [هل عليه دليل] قاطع أو ظني؟ فقال قوم: هو قاطع، ولكن الإثم محطوط عن المجتهد، لغموض الدليل وخفائه. ومن هنا تمادى بشر المريسي فقال: إذا كان في كل مسألة دليل قاطع، وجب أن يكون المخطئ آثما، كما في سائر القطعيات. وهذا تمام القول بثبوت الدليل القاطع في آحاد المسائل الاجتهادية.
ومنهم من قال: عليه دليل ظني، [ثم] اختلفوا في أن المجتهد، هل أمر بإصابة ذلك الدليل؟ فقال قوم: لم يكلف المجتهد إصابته، لخفائه وغموضه، ولذلك كان معذورًا مأجورًا.
وقال قوم: أمر بطلبه، وإذا أخطأ لم يكن مأجورًا، لكن خط الإثم عنه تخفيفًا. هذا تفصيل المذاهب.
والمختار عندنا، وهو الذي نقطع به، ونخطئ المخالف فيه، أن المسائل الاجتهادية لا يتصور أن تثبت الإصابة للمختلفين على حال، فإنه إن كان في المسألة حكم، فلا يكون إلا حكمًا واحدًا معينًا. وإن قلنا: إنه لابد أن يثبت في كل واقعة حكم، فلا يتصور أن يكون الحكم إلا واحدًا [معينًا]، هذا في الواقع.','3','353',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1985,'وأما الجواز العقلي، فلا يشترط فيه ذلك. ولا يحيل العقل أن يجعل الله تعالى الحكم على كل مجتهد ما غلب على ظنه. ويتحقق ما قلناه بفرض الكلام في طرفين: أحدهما- في الجواز العقلي، والثاني- في الوقوع السمعي.
أما الجواز العقلي: فإنه يجوز [عندنا] أن يجعل الله تعالى في مسالة حكمًا معينًا يطلبه العلماء، فقد يصادفونه أجمعين، وقد يصادفه بعضهم، وقد يجوز أن يخطئه الجميع، لولا عصمة الله هذه الأمة.
وكيف ينكر ذلك من جهة العقل، والمسألة القطعية وجدت في الشرع [على] هذه الصفة، [والأحكام] ثابتة معينة، والخلق مأمورون بالطلب؟ ولم تجعل الأحكام فيها تابعة للمعتقدات أصلًا، بل الحكم ثابت، فقد يصادفه المعتقد، [وقد لا] يصادفه، [فيجوز] من الله [سبحانه] وتعالى أن يجعل الأحكام كلها على هذه الصفة، ولم يبق الفرق باعتبار ذات الحكم [ووصفه] [وتعيينه]، وإنما يرجع الفرق إلى نصب الدليل القاطع وعدم نصبه. وقد قررنا فيما تقدم أن الصحيح عندنا من جهة العقل، تجويز التكليف مع فقدان الدليل. وإنما توقف التكليف على نصب الأدلة من فضل الله [- عز وجل -] على عباده رحمة [بهم]. قال الله تعالى: {وما كنا معذبين حتى','3','354',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1986,'نبعث رسولًا}. هذا هو الكلام على المسألة من جهة التجويز العقلي. (٥٩/ أ)
أما المصير إلى أن الشيء الواحد [من وجه واحد على شخص واحد] يختلف المجتهدان فيه، فيقول أحدهما: (٤٥/ ب) هو حلال، مخبرًا عن الله - عز وجل -، ويقول الآخر: هو حرام، مخبرًا عن الله تعالى، فمحال أن يكونا صادقين.
مثاله: قليل النبيذ، إذا سئل عنه الشافعي مثلًا، فقال: حرمه الله [- عز وجل -] على جميع خلقه غير مختص ببعض الناس. وسئل عنه أبو حنيفة فقال: أحله الله [تعالى] للجميع على ذلك التقدير، فمحال أن يكون صادقين بلا ريب، إما استحالة ذلك عقلًا عند قوم، وإما استحالته شرعًا عند آخرين. وهذا أمر يعلم استحالته بلا إشكال. وقد قال مالك رحمه الله في هذه المسألة: يختلفان فيقول أحدهما: هذا الشيء حلال، ويقول الآخر: هو حرام، وهما مصيبان، وهذا والله ما لا يكون أبدًا. وصدق رحمه الله، كيف يقبل [الفعل] على الوجه الواحد في وقت واحد حكمي متناقضين؟ هذا مستحيل، لا يستريب فيه ذو تحصيل. [وهو] البرهان القاطع على أنه لا يتصور أن يكون المختلفان [مصيبين] جميعًا. بل هذا المذهب عين السفسطة، إذ يكون الشيء ونقيضه','3','355',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1987,'[حقًا]. وقد أجابوا عن [ذلك] بأجوبة نذكرها:
فقالوا: إنما لزم [هذا] من جهة اعتقاد أن الكلام أوصاف للأعيان، فاعتقاد الحل والحرمة، كالسواد والبياض، والصفات الحسية. وذلك وهم، [وهو] من أصول المعتزلة، ولا تنبني أصول أهل السنة عليه.
قالوا: أما إذا كان الحكم يرجع إلى الخطاب، فلا يتناقض أن يحل لزيد ما يحرم على عمرو، [كالمنكوحة] تحل للزوج، [والأمة] للسيد، وتحرم على الأجنبي، والميتة [تحرم] على المختار دون المضطر، والصلاة تجب على الطاهر، وتحرم على الحائض. وإنما [المتناقض] اجتماع التحريم والتحليل في فعل واحد من وجه واحد. فإذا تطرق التعدد والانفصال إلى شيء من هذه الجملة، زال التناقض، حتى نقول في الصلاة في الدار المغصوبة: إنما تقع [صلاة] وقربة من وجه، [وتقع غصبًا] من وجه آخر.
فاختلاف الأحوال ينفي التناقض، حتى نقول: يحرم ركوب البحر على من غلب على ظنه العطب، ويجوز لمن غلب على ظنه السلامة، فيختلف الشخصان باعتبار الشجاعة والجبن، وما ركب في القلوب من الجرأة والهلع، فيحل لأحدهما الركوب، ويحرم على الآخر. وكذلك الصلاة تجب على من','3','356',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1988,'غلب على ظنه أنه متطهر، وتحرم على [من] غلب على ظنه الحدث.
قالوا: فكذلك يحل شرب النبيذ لمن غلب على ظنه حله، ويحرم على من غلب على ظنه (٥٩/ ب) أنه بيسير الخمر أشبه. قالوا: فهذا [الأمر] لا يتناقض بوجه. والجواب: أنا لسنا ننكر [شيئا] مما ذكروه، وإنما الكلام إذا عينت المسألة في استحقاق زيد مثلًا [مالًا]، فقضى أحد العالمين بأنه يستحق، وقضى الآخر بأنه لا يستحق، هل يتصور أن يكونا مصيبين؟
مثاله: اختلاف العلماء في المكاتب إذا [كاتب وعليه] ديوان، وقام الغرماء عليه، وكان للسيد نجم قد حل، فأراد المحاصة، فقال [أهل المدينة]: يختص الغرماء بالمال، ولا يدخل السيد في [المحاصة] بالنجم الحال على حال. وقال شريح: [يحاصصهم] بنجمه الذي حل. والفريقان يحكمان مسندين الحكم إلى ما فهموه من إخبار رسول الله - صلى الله عليه وسلم - عن ربه، فإن كلًّا منهم يقول: هذا حكم الله، فيكون على قول من يقول كل مجتهد مصيب، حكم الله بأنه يحاص، وأنه لا يحاص، أعني السيد.','3','357',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1989,'وإنما فرضناه في مثل هذه (٤٦/ أ) الصورة، حتى يتحقق اتحاد المحل المحكوم فيه. وليس هذا بمثابة حل الميتة لزيد، وتحريم أكلها على عمرو. وهذا لا تنقاص فيه، ولا ننكر اشتمال الشريعة عليه، وعلى [أمثلةٍ] كثيرةٍ له. ونحن نقول: لم يختلف المجتهدون في المسائل إلا على هذه الجهة التي ذكرناها، فلا يتصور مع هذا التقدير المصير إلى تصويب الجميع بحال. وعلى هذا جرت أقضية العلماء، حتى قال الشافعي: (أحد الحنفي إذا شرب النبيذ).
ثم إنه ينبني على المصير إلى تصويب كل واحد من المجتهدين أمور فضيعة:
منها: أنه إذا تقاوم عند المجتهد الدليلان، تخير بين الشيء ونقيضه، ويقلد [العامي] من شاء من المفتين. فإذا نكح مجتهد مجتهدة، وقال لها: أنت بائن، ولم يقصد بذلك الطلاق، وكان شافعيًا يعتقد بقاء العصمة، وكانت هذه حنفيةً تعتقد انقطاعها، وكل مصيب، فيفضي إلى أن يكون وطأها حلالًا له، حرامًا عليها، وهو فعل لا يتصور إلا من اثنين، فيقع التناقض فيه حلًّا وحرمة.
وأيضًا: فإنه لو صح تصويب المجتهدين، لجاز لكل واحد من المجتهدين، إذا اختلفا في جهة القبلة، أن يقتدي كل واحد منهما بصاحبه، وإن صلى إلى غير جهته، إذ صلاة كل واحد صحيحة. وكذلك إذا اختلف اجتهادهما في طهارة الماء ونجاسته.','3','358',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1990,'وقد نقل بعض العلماء الاتفاق على فساد هذا الاقتداء. وقد منع بعض المصوبة الإجماع في ذلك، وقال: يجوز الاقتداء مع الاختلاف في الجهة والطهارة والنجاسة. والعمدة على الحقيقة، إنما هي على الدليل (٦٠/ أ) الأول، وهو [تعذر] الصدق مع تحقيق التناقض. وقد تمسك الإمام ههنا بطرفين:
أحدهما- أنه قال: الطلب للحكم واجب، والاجتهاد في الأحكام لازم، وكيف يتصور الطلب والأمر به من غير مطلوب محقق أو مقدر؟ فمن أقدم إقدام من لا يعتقد [شوفا] ومطلبا كيف يظن؟ تقديره: أن العلم [بالعدم] يمنع من الطلب. وإذا كان المصوبة يقولون: إنه لا يحكم أصلًا، وإنما يتبع [الحكم] الظن، [فكيف يتصور] أن يظن ثبوت الحكم من يعلم نفيه؟ هذا محال، ظاهر الاستحالة. والأمر على ما قاله: من أنه من علم بنفي أمرٍ، استحال أن يطلب وجوده.
والقاضي وغيره لا ينكر هذا، [ولكن] النزاع في المطلوب ما هو؟','3','359',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1991,'فالإمام يرى أن المطلوب [الحكم]، [مباشرٌ] بالطلب، فيلزم سبق الحكم، ثم يتوجه الطلب نحوه، فقد تمكن الإصابة، وقد يمكن الخطأ. والقاضي وغيره يقولون: إنما يتوجه الطلب نحو أمور ثابتة، غير الحكم، وهو مثلا: أن يطلب ظن صدق الراوي، أو ظن استغراق العموم، أو ظهور صيغة (افعل) في الطلب. وهذه أمور ثابتة، فإذا ثبتت هذه الأمور في النفس ظنًا، ترتبت الأحكام الشرعية على الظان ثانيًا، فيكون الحكم يثبت تابعا لحصول غلبات الظنون في تحقيق تلك الإمارات. فأما المصير إلى توجه الطلب لحصول ما يعلم نفيه، فلا يتصور.
يبقى أن يقال: فإذا كان المطلوب مثلا صدق الراوي، فإذا تناقض [الطالبان] في الصدق، فظن أحدهما صدق الراوي، وظن الآخر كذبه، هل يتصور أن يكونا مصيبين (٤٦/ ب) في ذلك أم لا؟ فيقال في النظر في الأمارات ما قيل في الأحكام. قلنا: محال أن يكونا مصيبين في الأمارات مع التناقض، لكن يجوز أن يجعل الله [تعالى] ظنه الصدق، سببا لثبوت الحكم عليه، فيكون مصيبا في الحكم، وإن غلط في الصدق، كما إذا قضى القاضي بشهادة من يظن أنه صادق، فالحاكم مصيب، وإن كان الشاهد كاذبًا. ولكن إذا حكم من صدق [المخبر] بمقتضى الخبر على العموم، وأضاف الحكم إلى الله تعالى، وحكم من ظن الكذب، أو لم يظن الصدق، بأن الحكم لم يوجهه الله تعالى على أحدٍ من عباده، استحال أن يكونا صادقين. هذا هو الذي يقوله من قال المصيب واحد.','3','360',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1992,'وأما الوجه الثاني الذي تمسك به الإمام في الترجيح، وهو أنه: كيف يسنح للمجتهد مسالك، فيرى بعضها أرجح من بعض، مع المصير إلى أن الظنون لا تختلف؟ فهذا لعمري لازم، فإنه إذا تحقق في القلب الاستواء مع تصوير الترجيح مع تحقيق الاستواء. (٦٠/ ب) وهذا يظهر [أنه] متناقض. ولكن [جواب القوم] عنه أنهم يقولون: إنما يكون الترجيح في الأمارات التي تترتب الأحكام عليها. فإنا نقول: إذا تناقض قول العدلين، فمن هو أعدل، فالظن بصدقه أقوى، فيطلب الترجيح في هذه الأمارات، لا بالنظر إلى أقرب الأمرين في الحكم الثابت عند من لا يرى ثبوت الحكم. هذا ممتنع [عندي]، وإنما يصرف الترجيح إلى ما ذكرناه [من أنه أقوى]. فإذا قوي في النفس أحد السببين، ترتب الحكم عليه حينئذ، فيثبت الترجيح لذلك.
وأما الترجيح على مذهب من يقول المصيب واحد، فلا يتصور الترجيح بين دليلين على الحقيقة، إذ ليس إلا حكم واحدٌ، ولا دليل على الحقيقة إلا واحدٌ، والترجيح بين فاسد وصحيح، غير صحيح. نعم، إذا ثبت أن أحد الأمرين أقوى في النفس، ظن أن الحكم على وفقه، [فيسمى] ترجيحًا من هذه الجهة خاصة، وإلا [فشرط] الترجيح الحقيقي أن يكون كل سبب، لو قدر منفردا صحيحًا.
وقد جاءت أدلة توقيفية تدل على أن المصيب واحد، وليست قواطع، لأنها منقسمة إلى متواترات ظواهر، وإلى نصوص آحاد. أما الظواهر، [فمن]','3','361',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1993,'كتاب الله - عز وجل -[قوله تعالى]: {وداود وسليمان إذ يحكمان في الحرث إذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين}. يقول الله [تعالى]: {ففهمناها سليمان وكلًّا ءاتينا حكمًا وعلمًا}. فأضاف الإصابة [إلى سليمان]، ولم يعنف داود - عليه السلام -. وقال [جل ذكره]: {ولو ردوه إلى الرسول وإلى أولي الأمر منهم لعلمه الذين يستنبطونه منهم}. [فدل] أن الحكم ثابت يتوصل إليه بالاستنباط.
وأقوى الأدلة قوله - عليه السلام -: (إذا اجتهد الحاكم وأصاب الحكم فله أجران، وإن أخطأ فله أجر [واحد). وقال - عليه السلام - لبعض أمرائه: (إن سألك من تقاتلهم أن تنزلهم على حكم الله، [فلا تنزلهم]، لأنك لا تدري هل تصيب حكم الله أم لا. وإن سألوك أن تنزلهم على حكمك، [فأنزلهم على حكمك)].','3','362',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1994,'فهذه أخبار ناصة في الإصابة والخطأ، مع ثبوت الأجر للمخطئ. والأخبار صحيحة مجمع على صحتها. ولا أدري هل بلغت عند علماء الحديث مبلغ المتواترات؟ فإن بلغت مبلغ المتواترات، فهي قواطع في الأدلة.
ومن الأدلة القوية في ذلك أقوال الصحابة [- رضي الله عنهم -]، فإنهم قد صرحوا بإمكان الخطأ في المجتهدات. جاء عن أبي موسى أنه قال في مسألة (٤٧/ أ) البنت وبنت الابن والأخت، لما حكم فيها بما حكم، قال هو والصاحب (٦١/ أ) الآخر للسائل: (وأت ابن مسعود [فإنه] سيتابعنا. فقال ابن مسعود بعد أن علم حكهما: لـ {قد ظللت إذًا وما أنا من المهتدين}. وقضى فيها بما قضى. فلما رجع السائل إلى أبي موسى قال: (لا تسألوني ما دام هذا الحبر بين أظهركم). وقال ابن مسعود في مسألة المفوضة: (أقول فيها برأيي، فإن يكن صوابا فمن الله، وإن يكن خطأ، فمني ومن الشيطان، والله ورسوله منه بريئان). وكذلك قال أبو بكر - رضي الله عنه - في مسألة الكلالة. وقال علي - رضي الله عنه -: (وإن اجتهدوا فقد أخطأوا، وإن لم يجتهدوا فقد غشوا). في مسألة إجهاض الجنين، والخوف من الخطأ. ويجري الصواب فيما اتفق سلف الأمة عليه.
وقد تبين بما ذكرنا أن المصير إلى الإصابة مع تحقيق التناقض محال. وترتيب حكم الشخص في خاصة نفسه على ظنه ليس بمحال. وأدلة السمع','3','363',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1995,'مصرحة بثبوت الحق وتعيينه، وتطرق الخطأ وإمكانه. هذا تمام الكلام ومجرى الدليل. فلنذكر [الآن] شبه المخالفين، وهي تنقسم إلى عقلية وإلى سمعية. فلنبدأ بالعقلية.
قالوا: كشف الغطاء في ذلك، أن المسائل انقسمت إلى ما لا نص فيها، وإلا ما فيها نص. فإن فرض الكلام فيما لا نص فيه، استحال الخطأ في ذلك، إذ كيف يتصور أن يقال: أخطأ الحكم، ولا حكم في المسألة؟ هذا محال، لا خفاء باستحالته، فلا معنى للإطناب فيه.
الطرف الثاني: مسألة فيها نص، فيقال: لا يخلو، إما أن يكون الاطلاع على النص ممكنا، وإما أن يكون مستحيلا، فإن كان مستحيلا، فلا تكليف علينا بالمستحيل، لاستحالة تكليف ما لا يطاق.
وبيان ذلك بالمثال: أنه لما كان التوجه إلى بيت المقدس ثابتا بالسنة المتواترة، فإذا نزل جبريل [- عليه السلام -] على رسول الله - صلى الله عليه وسلم - بالأمر بالتوجه إلى الكعبة، ولم تطلع الصحابة على ذلك، ولا يصح أن يكون التكليف توجه عليهم بالأمر باستقبال الكعبة، وكيف يقدر ذلك وهم لو استقبلوا لعصوا؟ وكذلك لو بلغ رسول الله - صلى الله عليه وسلم - أهل بيته أو أهل مسجده، فلم [يمكن] [وصول] التبليغ إلى غيرهم، لاستمر التكليف على من لم يبلغه الناسخ، ولو لم يفعل بمقتضى ذلك، [لكان] عاصيًا. وكيف يفرض وجوب الاستقبال عليهم، وهم لو استقبلوا لعصوا؟ فإذا كان هذا هو الحكم في مسألة فيها نص، [فالتي] لا نص فيها أولى.','3','364',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1996,'فلئن قيل: فيها نص. قلنا: ذلك النص يمكن الوصول إليه أو لا يمكن؟ (٦١/ ب) فإن أمكن الوصول إليه، [فقد تحقق التكليف، ولزمت المعصية بالمخالفة. وإن لم يكن الوصول إليه]، فليس بحكم في حق من لم يمكنه ذلك. ولئن قيل: أخطأ، فعلى سبيل تجوز، معناه: أخطأ ما سيصير عليه حكمًا عند بلوغه إياه. قالوا: والحاصل [أن] ما لا دليل عليه، فلا تكليف علينا فيه، وما عليه دليل قاطع، يتأتى للمكلف الوقوف عليه. فإذا لم يقف عليه، كان مقصرًا عاصيًا.
فإن قيل: سقط العصيان عنه للغموض. قلنا: إن انتهى الغموض إلى حدٍّ يحيل الاطلاع، فلا تكليف. وإن كان غامضا مع إمكان العثور، فالمشقة لا تنهض سببًا لسقوط التكليف. ولئن قيل: عليه دليل ظني، فالأمارات الظنية ليست أدلة على الحقيقة؛ (٤٧/ ب) وهي تختلف باختلاف النفوس والطباع، فرب شيء يحرك ظن زيد، وهو بعينه لا يحرك ظن عمرو، وهو بمثابة استحسان الأصوات والألوان، فرب شيء حسنٍ عند زيد، قبيح عند عمرو. وليست الأدلة القطيعة كذلك. قالوا: فسبب هذا الوهم الاعتقاد بأن الأمارات الظنية أدلة على الحقيقة، وليس كذلك.
فلو قيل: فيها أدلة قطيعة، ولكن حط الإثم عن المجتهد لغموضها، [لكان] ذلك باطلًا قطعًا، فإنا نعلم بالضرورة أن [أكثر] مسائل الشريعة ليس فيها أدلة قطعية، فإن الأدلة القطعية [نصوص] كتاب، أو [نصوص]','3','365',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1997,'سنة متواترة، أو إجماع، وقل ما يصادف ذلك في المسائل الفقهية. قالوا: والحاصل أن الإصابة محال أو ممكن، ولا تكليف بالمحال. وإن كان ممكنًا، فمن أمر [به] فتركه، عصى وأثم. قالوا: ومحال أن يقال هو مأمور به، لكن إذا خالف لم يعص ولم يأثم، وكان معذورًا، لأن هذا يخالف حد الأمر والإيجاب، إذ الواجب: ما يتعرض تاركه للعقاب واللوم. قالوا: وهذا التقسيم قاطع يرفع الخلاف مع كل منصف. هذه عيون كلام القوم.
وقد تمسكوا أيضا [بقريب] مما ذكرناه، وذلك أنهم قالوا: إذا قضيتم بأن الحكم متعين، وهو الجواب مثلًا، ثم أدى المجتهد اجتهاده إلى التحريم، فهو لو أقدم على الفعل، لكان فاعلًا واجبًا على التقدير، فينبغي ألا يكون عاصيًا، إذ قدر أنه صادف واجبًا، وينبغي إذا ترك، أن يكون عاصيا، التفاتًا إلى المقدر، وفي إطباق الأمة على وجوب الانكفاف عما يظن تحريمه، وعلى وجوب الإقدام على ما يظن وجوبه. ولو انكف عن الواجب في ظنه، لعصى على قطع، ولو أقدم على المحرم في ظنه، لعصى أيضًا قطعًا، [دليل على ما قلناه]. وهو سؤال غامض على مذهب من يقول المصيب واحد. وإذا أجيب عن هذين السؤالين، اندفع الإشكال (٦٢/ أ).
أما السؤال الأول، وهو أنه: هل عليه دليل أو لا دليل عليه؟ فنقول: الكلام مفروض فيما إذا أُبت الله الحكم، ونصب للمكلفين أمارة ظنية عليه، وهو بمثابة ما إذا روى العدل عن [رسول الله - صلى الله عليه وسلم -] أنه حكم بكذا، فالحكم','3','366',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1998,'من الرسول معين، وهو ثابت في نفسه، ومتعلق بأفعال المكلفين، على حسب شرعيته، والعدل المخبر عن ثبوته، مظنون صدقه، فمن صادف ذلك الحكم، وعثر عليه من قول المخبر، فقد أصاب حكم الله - عز وجل -. ومن ثبت عنده عدالة الشخص، ولم يعثر على الحكم، ولم يظنه، فلا إثم عليه في ذلك، لبذله المجهود.
وقولهم: إنه مأمور أو غير مأمور، [هو مأمور] بطلب حكم الله - عز وجل -، وواجب عليه ذلك.
وقولهم: إذا ثبت الوجوب، لم يكن بد من المعصية، والذم عند المخالفة. وقد كنا قدمنا في حد الواجب: أنه الذي يذم تاركه بوجه ما. وحققنا أن الصلاة تتصف بالوجوب في أول الوقت، وأن من مات في وسط الوقت لا يموت عاصيا، ولم نرتض قول من يجعل العزم بدلا عن الواجب. وهذا الذي نحن فيه من ذلك النوع. نعم، يتوجه الذم على تارك ذلك الواجب عندما يثبت عنده وجوبه، فقد تحقق نعت الوجوب في حقه، وامتاز عن غيره الواجب بذلك.
وأما الجواب عن السؤال الثاني: وهو أنه إذا ظن الوجوب، وجب الإقدام قطعا، بإجماع (٤٨/ أ) المسلمين، فكيف يقطع بوجوب الإقدام مع احتمال أن يكون الشيء غير واجب؟ وهذا من أغمض الأسئلة. وسبيل الجواب أن نقول: إذا ظن المكلف الوجوب مثلا، قطع في حقه بوجوب التعظيم، وتحريم الاستهانة. والإقدام على المخالفة بعد ظن الوجوب، استهانة، [وهو محرم]، وهي محرمة على وجه مقطوع به، لا على مظنون، فإن من ظن أن','3','367',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (1999,'زيدا سيده ومالكه مثلا، ثم استهان بأمره، وأعرض عن موافقته، استحق [التقريع] من مالكه، وإن كشف الغيب أن ذلك الآمر ليس هو المالك. فمن هذه الجهة يعصي من ظن الوجوب، ولم يقدم على العمل.
فأما النظر إلى خصوصية العمل، فلا يقطع بوجوبه على حال، وكيف يقطع بوجوبه من جهة خصوصيته، [وقد] يجوز أن يكون [حراما عليه فعله]؟
فإن قيل: فعلى تقدير تحريمه باطنا، كيف يتصور أن يقطع بكونه مطيعا مع تجويز التحريم؟ [فنقول]: لم نقطع بكونه مطيعا بالنظر إلى خصوصية الفعل، بل نظن ذلك [ظنا]، وإنما نقطع بالطاعة، بناء على الأمر الكلي الذي قررناه.
هذا هو الذي لا يصح [عندنا] سواه (٦٢/ ب)، ولا يجري على الكتاب والسنة وأقاويل الصحابة واختلاف أهل العلم في مسائل على التناقض في النفي والإثبات غيره. ومفرعو المسائل لم يفرعوها خاصة أصلا، بل وضعوها شاملا للكافة، كل يظن أن الله تعالى لم يحكم على عباده إلا بما ذكره المفرع أو المفتي، اللهم إلا في مسائل نادرة، تكون الأدلة من الجانبين متقاربة، فترجع الفتوى إلى التخيير، ويقع التعيين من باب التفصيل. وهذا كما يقال في السجود للسهو في النقصان والزيادة: أن سجود النقص قبل السلام، وسجود الزيادة بعد السلام، ولو عكس المكلف لكان في سعة، لأن الأمر في','3','368',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2000,'هذا الترتيب ليس بضيق على المكلفين. هذا تمام الكلام في حقيقة التخطئة والتصويب.
فلنتكلم بعد ذلك على حقيقة ثبوت الحكم المعين في المجتهدات. أما نحن إذا ذهبنا إلى أن المصيب واحد في المجتهدات، فلابد من حكم معين قبلة الطالب، وإليه يتوجه طلبه، فيصيب أو يخطئ.
وأما المصوبة، فقد اختلفوا فيه، فذهب بعضهم إلى إثباته، وإليه تشير نصوص الشافعي، لأنه قال: لابد للطالب من مطلوب. والإمام ذكر هذا، ولم يصرح بأن المصيب واحد، ولا أن كل مجتهد مصيب. ولكن اللائق [بالمعممين] أن يقولوا المصيب واحد، وربما عبروا عنه: أن مطلوب المجتهد الأشبه، والأشبه معين عند الله - عز وجل -. ومن العجائب في ذلك المصير إلى تعين حكم الله - عز وجل -، وأنه ليس له إلا حكم واحد، ثم القضاء بأن من حاد عنه، فهو مصيب لحكم الله تعالى. ولا معنى للإطناب في إفساد هذا، فإنه من الكلام الغث. ولا يتصور مع القول [بتعين] الحق أن يكون كل مجتهد مصيب، إلا أن يراد أنه مصيب باعتبار ما كلف من الطلب، وإن كان مخطئاً','3','369',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2001,'للحكم. فهذا هو الذي يقوله من يقول المصيب واحد، فإنه يقول: هو مطيع، وإن لم يكن مصادفًا للحكم.
مسألة: في وجوب الاجتهاد على المجتهد. وقد أجمعوا على أنه [إذا] تمم الاجتهاد وأنهاه نهايته، وحصل على ظن الحكم، فلا يجوز له أن يقلد غيره في هذه الصورة، بل عليه أن يحكم بما أداه إليه اجتهاده. أما إذا لم يجتهد، فإنه ينظر، فإن لم يكن من (٤٨/ ب) أهل الاجتهاد في المسألة، وإن كان عنده نظر، وإنما يكون من أهل الاجتهاد فيها بشرط أن يحصل علما على سبيل الابتداء. [فهذا حكمه] التقليد، ولا يتوقف العمل عليه على استئناف تحصيل [علم]، وهذا مجمع عليه. وإن كان من أهل الاجتهاد (٦٣/ أ) في المسألة، بحيث لو نظر، لتمكن من الوقوف على الحكم مستندا إلى دليله، بحيث يستغني عن تعلم علم من غيره، فهل [يجب] عليه الاجتهاد، أو يجوز له التقليد؟
فقال قوم: انعقد الإجماع على أنه لا يجوز تقليد من بعد الصحابة. وقال قوم: الصحابة والتابعون. وكيف يصح دعوى الإجماع، وممن قال [بتجويز]','3','370',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2002,'تقليد العالم: أحمد بن حنبل، وإسحاق بن راهويه؟ وقال محمد بن الحسن: يجوز أن يقلد من هو أعلم منه، ولا يجوز أن يقلد [من هو] دونه أو مثله.
وقال قوم: يجوز أن يقلد فيما يفتي به وفيما يخصه. وقال قوم: يجوز أن يقلد فيما يخشى فوات وقته، دون ما يتسع وقته. وقال القاضي: لا يجوز أن يقلد العالم العالم بحال، وسواء في ذلك ما يخصه، وما يفتي به، وسواء أيضًا في ذلك الصحابة وغيرهم. وهذا هو الأغلب على الظن عندنا.
والدليل عليه: أن تقليد من لم تثبت عصمته، ولم تتحقق إصابته، بل كان السهو والخطأ متطرقين إليه، لا يثبت إلا بنص، أو قياس على منصوص، ولا','3','371',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2003,'نص، ولا يمكن القياس على المنصوص، [إذ] المنصوص للعامي والمجتهد.
أما العامي، فيجب عليه التقليد، إذ ليس يمكنه غير ذلك، وهو مأمور ومنهي، ومن لا بصر له، فهو مفتقر إلى أن يستضيء ببصر غيره. وأما المجتهد، فإنه يعمل برأيه، وهو يعلم ظن نفسه، ولا يعلم ظن غيره، فكيف يصح قياسه عليه واعتباره به؟
فإن قيل: فهو لا يقدر إلا على تحصيل ظن، وظن غيره كظنه، لاسيما على القول بتصويب المجتهدين. قلنا: الذي نختاره أن المصيب واحد، وقد تقدم الكلام عليه. وأما كونه لا يقدر إلا على ظن، وظن غيره كظنه. قلنا: بينهما فرق: وهو أن المجتهد يعلم ظن نفسه، ويحق إمكان الغلط في ذلك، بخلاف ما إذا [قلد] غيره، فإنه يجوز أن لا يكون الغير ظانا أصلًا.
وإذا ثبت هذا الافتراق، بطل الإلحاق، ووجب الاقتصار على الشرع بالإجماع.
وأوضح الأدلة على ذلك، أنه إذا حصل له ظن بالحكم، امتنع أن يتركه لظن غيره، فيدل ذلك على أنه هو الأصل، وظن غيره بفي معنى البدل، ولذلك ترك ظن غيره لظن نفسه، فدل ذلك على أن ظن نفسه هو الأصل، وظن غيره] هو البدل.
وإذا تحقق ذلك، فالقدرة على الأصل، تمنع من البدل، كما في الماء','3','372',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2004,'والتراب، وكذلك في خصال كفارة اليمين، وجواز الصيام، وكذلك خصال كفارة الظهار، فإن القدرة على تحصيل عتق الرقبة، تتنزل منزلة ملك الرقبة. وهذا غالب في الشريعة في الأبدال والمبدلات.
وقد شذت مسألة مواحدة، وهي مسألة: من ملك خمسا وعشرين من الإبل، فإنه (٦٣/ ب) يجب عليه بنت مخاض، وإن لم تكن، فابن لبون ذكر، فوجدان بنت المخاض، يمنع من إخراج ابن اللبون. وليس التمكن من تحصيله، يمنع من ذلك، ولكن غالب الشريعة وأكثرها على خلاف ذلك، فلزم تنزيل الأمر على [الأعم] الأغلب، ولا [تنزيل] على الأقل الأندر.
فإن قيل: يدل على جواز تقليد العالم العالم ظواهر من الكتاب (٤٩/ أ). منها: قوله تعالى: {فسئلوا أهل الذكر إن كنتم لا تعلمون}. وقوله: {ولو ردوه إلى الرسول وإلى أولي الأمر منهم لعلمه الذين يستنبطونه منهم}. وقوله: {فسئلوا أهل الذكر} كلام عام، يتناول سؤال كل سائل.
قلنا: أما قوله {فسئلوا أهل الذكر}، فهذا خطاب مواجهة، وخطاب المواجهة لا عموم له عندنا، فإنه [قد] يقول [الملك] [لبعض] من حضر من غلمانه: اركبوا ولا يريد جميعهم. ولا يكون اللفظ مجازا على تقدير مخاطبة البعض، وإنما يفهم من قصده بالخطاب بقرينة الحال والمقال، فلا','3','373',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2005,'يثبت العموم للخطاب في هذه الصورة. ثم الظاهر أنه أراد سؤال العوام للعلماء، ولهذا قال: {إن كنتم لا تعلمون}. أما من هو قادر على علم المسألة، فهو بمثابة المسؤول. وسياق الآية يرشد إلى فرق بين السائل والمسؤول.
وأما قوله: [{ولو ردوه إلى الرسول وإلى أولي الأمر منهم}]، فيحتمل أن يكون المراد بأولي الأمر: الأمراء، فيكون الخطاب متوجها إلى الرعية، أي في الانقياد والطاعة، وبذل النصيحة، وعدم الخروج على الأئمة. وإن كان المراد بأولي الأمر: العلماء، فيكون ذلك خطابا للعوام الذين لا يدرون الشرع، ولا يعرفون طرق الاستنباط، فهؤلاء يلزمهم مراجعة جميع العلماء. إذ ليس لهم آلة هذا الشأن.
وقد جاءت عمومات تعارض هذه، وهي أقوى منها، كقوله تعالى: {فاعتبروا يأولي الأبصار}. وقوله تعالى: {لعلمه الذين يستنبطونه منهم}. وقوله {أفلا يتدبرون القرءان أم على قلوب أقفالها}. فهذا أمر [بالتدبر] والاستنباط والاعتبار، وليس خطابا على العوام، فلم يبق مخاطبا إلا العلماء، والمقلد تارك [للتدبر] والاعتبار والاستنباط. وكذلك قوله تعالى: {[اتبعوا]','3','374',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2006,'ما أنزل إليكم من ربكم ولا تتبعوا من دونه أولياء}. وهذا بظاهره يوجب الرجوع إلى الكتاب، لكن دل الكتاب على السنة، والسنة على الإجماع، والإجماع على القياس. وأما اتباع أقوال بعض [الناشئة]، فلم يتلق من شيء من هذه الأدلة، فلا يجوز التمسك به. والمسألة ظنية يقوى فيها التمسك بهذه الأدلة وبأمثالها.
ويدل (٦٤/ أ) على ذلك سيرة [الصحابة - رضي الله عنهم -] فإنهم كانوا يجتمعون ويتشاورون، ولا يكفي بعضهم من بعض بمجرد ذكر الحكم، حتى يبحث عن الدليل. وهذا أيضا ليس بقاطع، لأن الذي يقول: يقلد العالم العالم لا يوجب ذلك، بل يجوزه. فلعل الذين سلكوا هذا المسلك، إنما سلكوه [بناء] على أنه أحسن، لا على أنه حتم لازم. وقد [تباهلوا] في مسائل كثيرة، كمسألة الجدة والعول، والمفوضة، ولم [يركن] بعضهم فيه [إلى] تقليد بعض، بل سلكوا مسلك الاستدلال. هذا هو الأمر الواضح عندنا.
فإن قيل: لم ينقل عن طلحة والزبير و [سعيد] بن زيد تعرض','3','375',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2007,'للفتوى، مع أنهم [مجتهدون]. والظاهر أنهم كانوا يقلدون. قلنا: هذا خيال باطل، ووهم بين، فمن أين يلزم إذا لم يكونوا يشتغلون بالفتوى، أنهم كانوا يقلدون غيرهم؟ بل الظاهر أن المجتهد الذي له في نفسه بصر، لا يرد أمره إلى غيره بحال.
فإن قيل: فما تقولون في تقليد العلم؟ قلنا: إذا ثبت أن نظر غيره في معنى البدل، وأن نظره هو الأصل، لزم أن ينظر أولًا، فإن وافق نظره نظر الأعلم، [فذاك]، وإن خالف نظره نظره، فأي فائدة لكونه أعلم، [وهو] قد ظن خلاف (٤٩/ ب) رأيه، وظنه خاص به؟ وكون ذلك أعلم، لا يمنعه من حصول ظن المخالفة، فلا التفات إلى كونه أعلم بحال. وقد كان أصاغر الصحابة يخالفون الأكابر منهم. وكثيرا ما خالف ابن عباس [عمر بن الخطاب وعلي بن أبي طالب وغيرهما من جملة الصحابة، وإن كان ابن عباس] معترفا بأن عليا أعلم منه. وقد صرح بذلك.
والذي نراه وجوب الاجتهاد على المجتهد، ولا يقلد غيره بحال. فإن قيل: فهل من فرق بين ما يخصه وبين ما يفتي به؟ قلنا: يجوز أن ينقل للمستفتي مذهب الشافعي وأبي حنيفة، لكن لا يفتي من نفسه بتقليد غيره، إذ لو جاز ذلك، لجاز للعوام الفتوى. وأما ما يخصه وضاق الوقت، وكان في البحث تفويت، فهذا هل يلحق بالعاجز في جواز التقليد؟ فيه نظر فقهي، وهو يشبه العدول إلى التيمم عند ضيق الوقت، وتناوب جماعة على بئر. والصحيح عندنا','3','376',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2008,'[فيها] العدول إلى التيمم. وفيه قول: إن [المتيمم] [يعيد] بعد الوقت، والمشهور خلافه. فكذلك هذا يقلد، لضرورة فوات الوقت.
ويتصدى النظر أيضا في مسألة، [وهي] إذا كان صاحب الواقعة قد حصل أكثر مما تحتاج المسألة إليه، وبقي عليه تحصيل بعض المواد، فهذا فيه نظر، فهل يلتحق بالعالم أو بالعامي؟ (٦٤/ ب) والصحيح عندنا التحاقه بالعامي في جواز التقليد، وكونه قادرا على تحصيل علم تلك المادة، كذلك العامي قادر على تحصيل كل المواد. فإذًا إنما يمنع التقليد على المجتهد، وهو الذي قد حصل مدارك المسألة، ولم يبق إلا تصفح الجزئيات.

<span data-type="title" id=toc-354>مسألة: في نقض الاجتهاد بالاجتهاد</span>. إذا أداه اجتهاده إلى أن الخلع فسخ، فنكح امرأة خالعها ثلاثا، ثم تغير اجتهاده ورآه طلاقا، لزمه فراقها، ولم يجز له إمساكها على خلاف اجتهاده. [ولو] حكم بصحة النكاح بعد أن خالع الزوج ثلاثا، ثم تغير اجتهاده، لم يفرق بين [الزوجين]. ولم ينقض اجتهاده السابق بصحة النكاح، لمصلحة الحكم، فإنه لو نقض الاجتهاد بالاجتهاد، لنقض النقض أيضا، والتسلسل، واضطربت الأحكام، ولم يوثق بها.','3','377',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2009,'[أما] إذا نكح المقلد بفتوى مفت، وأمسك زوجته بعد دور الطلاق، وقد نجز الطلاق بعد الدور، ثم تغير اجتهاد المفتي، فهل على المقلد تسريح زوجته؟ هذا فيه نظر، والظاهر أنه يجب التسريح، كما لو تغير اجتهاد الناكح في نفسه، فإنه يلزمه التسريح. وهو إذا لم يكن من أهل النظر، فقد نزل مقلده منزلة. وكما لو تغير اجتهاده عن القبلة في أثناء الصلاة، فتحول إلى الجهة الأخرى، كما لو تغير اجتهاده في نفسه.
وإنما حكم الحاكم هو الذي لا ينقض، لكن بشرط ألا يخالف نصا ولا دليلا قاطعا، فإن أخطأ النص نقضنا حكمه. وكذلك إذا انتبهنا لأمر معقول في تحقيق مناط الحكم أو تنقيحه، بحيث يعلم أنه لو نبه له، لعلم قطعا بطلان حكمه، فينقض حكمه.
وقد اضطربت العلماء في مسائل، لا للنزاع في هذه [القواعد]، ولكن للتردد في أن الحاصل من الحاكم حكم، أو كف عن الحكم؟
وتلك المسائل: ما إذا تزوجت بغير إذن الولي، فرفع ذلك إلى حنفي يرى صحته، فقضى بالصحة، فقال أكثر أصحابنا: يجب إمضاء الحكم، ورأوا ذلك حكما (٥٠/ أ). وقال ابن الماجشون: يفسخ [النكاح]، ورأى أن قوله: الحكم، [أي] كف عن التعرض لفسخه، وكذلك ما ضاهى ذلك.
فإن قيل: فإذا خالف النص لا لتقصير من قبله، بل لكونه لم يبلغه،','3','378',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2010,'فيقضي بخلافه، ثم اطلع [عليه] هو أو غيره، فهل ينقض حكمه أو لا ينقضه؟ فإن قلتم ينقضه، فهو كان مأمورا به، مأذونًا له في الحكم بسببه، فقد فعل ما أمر به. وإن قلتم يستمر حكمه، فكيف يستمر حكمه على مخالفة النص؟ قلنا: هذا إنما يلزم من يقول كل مجتهد [مصيب]، (٦٥/ أ) أما نحن، فلا نرى ذلك، ونثبت الخطأ في المجتهدات، فكيف فيما تحقق النص فيه؟
وقد اعتذر الأولون عن هذا بأن قالوا: إنما يكون مصيبا بشرط أن لا يطلع على النص، فإذا اطلع عليه، وجب التدارك. ويجوز أن يختلف الحكم بالعلم والجهل، كما يختلف بالسفر والإقامة، والطهر والحيض، والصحة والمرض. لكن بين هذه الأمور وبني الجهل والعلم فرق، فإن من سقط عنه الوجوب بعجز أو عدم، لم يجب على غيره إزالة عجزه أو عدمه. ولو سقط عنه الوجوب لجهله، وجب على العالم إزالة جهله، فإن تعليم العلم ونشر الشرع وإزالة الجهل واجب. ولذلك نقول: من صلى وعلى ثوبه نجاسة لا يعلمها، صحت صلاته ولا يقضيها. ومن رأى على ثوبه تلك النجاسة، يلزمه تعريفه. و [لو] تيمم ليصلي، وقدر غيره على أن يزيل عجزه بحمل الماء إليه، لم يلزمه. ففي هذه الدقيقة يختلف حكم العلم والجهل، وحكم سائر الأوصاف.
فإن قيل: فلو خالف العالم قياسا جليا، هل ينقض حكمه؟ قلنا: قال الفقهاء: ينقض [حكمه]، فإن أرادوا المقطوع به، فصحيح، وإن أرادوا به','3','379',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2011,'قياسا [مظنونا]، ولكنه جلي، فهذا مشكل، والفرق بين مظنون ومظنون عسر، وضبط درجة [للظن]، يتوقف عندها في الإمضاء والنقض، لا قدرة عليه. وقد قال الفقهاء: ينقض القضاء إذا خالف قياسا جليا أو خبرا صحيحا أو عملا مستمرا.
أما الأعمال المستمرة، فإن كانت أعمال أهل الإجماع، فالأمر فيها واضح، وإن كانت أعمال أهل المدينة، كما جرى في الأحباس والمد والصاع، فهذه الأعمال تدل على سنن ثابتة، فيتنزل ذلك منزلة الأخبار الصحيحة. قلنا: الصحيح عندنا أنه لا ينتقض الحكم، إلا في مواضع القطع بالغلط. والقضاء بتقديم خبر الواحد على جنس القياس، ليس [بمقطوع] به عندنا. وكيف يقطع بتقديم الخبر المعين الذي عارضه القياس، وكون الخبر المعين مستند العمل مظنونا؟ فكيف يقطع بتقديمه على غيره في استناد العمل إليه، مع كوننا نظن أن حكم الله على مقتضاه؟ وهذا قد سبق تقريره قبل هذا.
وعلى الجملة، المقطوع به كون أخبار الآحاد يستند إليها العمل على الجملة. وكذلك القياس من حيث الجملة. وكذلك القياس من حيث الجملة. وأما الحكم [بخبر] معين، فليس مقطوعًا به، [بل] مظنونًا، وليس [ترك] العمل به في صورة مخصوصة، تركًا للعمل بأخبار الآحاد على الإطلاق.','3','380',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2012,'فإن قيل: [فإن] حكم بخلاف اجتهاده، لكنه وافق اجتهاد غيره، فما الذي تقولون؟ (٦٥/ ب) ولو حكم حاكم مقلد بخلاف قول من يقلده، فهل ينتقض حكمه؟ قلنا: هذا في حق المجتهد لا يعرف يقينا، بل يحتمل تغير اجتهاده. نعم، هو في خاصة نفسه، إذا علم أنه حكم بخلاف اجتهاده، وجب عليه نقض حكمه، إلا أن تكون القضية تتعلق بغيره، فلا يقبل قوله على غيره، لمصلحة الحكم أيضا. و [لذلك] منع الحاكم [أيضا] من أن يحكم بعلمه، لمصلحة الخلق في ذلك. (٥٠/ ب) وأما المقلد، فلا يصح حكمه عند الشافعي، وهو ظاهر قول مالك رحمه الله، فإنه قال: إذا اجتمع في القاضي خصلتان، رأيت أن يستقضى، وهما: العلم والورع.
وإن قضينا بصحة حكم المقلدين في وقتنا لضرورة الوقت، وفقدان المجتهدين، تصدى النظر في أمر [آخر]، وهو أنه: هل يتخير المقلد في تقليد من شاء، أو يجب عليه تقليد الأعلم؟ وهذه مسألة سيأتي الكلام عليها، إن شاء الله تعالى. فإن جوزنا، فهذا قد وافق مذهبنا من حيث الجملة، فلا ينقض قضاؤه. وإن قلنا: لا يتخير، بل يرجح- وهو الصحيح- أوجبنا عليه اتباع الأعلم عنده. فإذا قضى بخلاف قوله، فلم يفعل ما أمر به، فقد وقع الحكم في غير محله، فينبغي أن ينقض قضاؤه.','3','381',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2013,'قال الإمام [رحمه الله]: <span data-type="title" id=toc-355>(فصل- فيما يعلل وما لا يعلل) </span>إلى قوله (فإن رأى في وجهه [تقطيعها وتكريها]] فهذا مثال الأشباه). قال الشيخ أيده الله: ابتدأ الإمام من هذا المكان على<span data-type="title" id=toc-356> أركان القياس</span>، وبيان ما يعلل من الأصول، وما لا يعلل، وما يصح أن يتلقى من القياس، وما لا يصح. فلنتكلم على ذلك على الترتيب، ولنبين شروط كل ركن على وجه وجيز، ليحصل الغرض على الكمال، وبالله نستعين.
اعلم أن أركان القياس أربعة: الأصل والفرع والجامع والحكم. أما الأصل: فهو الذي ثبت حكمه، وكانت نسبة الفرع إليه والاعتبار [به]. وله شروط:
الأول: أن يكون [حكم الأصل] ثابتا، فإنه إن لم يكن حكمه ثابتا، خرج عن كونه أصلًا، ولم ينتفع به الناظر ولا المناظر، فعدم الحكم يخرج','3','382',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2014,'القياس عن حقيقته.
الثاني: أن يكون حكمًا شرعيًا، فإنه إن كان لغويا، لم يتأت القياس فيه عندنا، فكون الحكم لغويا، يمنع القياس أيضا. وقد نقلنا [اختلافا] في الأحكام العقلية، هل يجوز أن تثبت بالقياس أم لا؟ واخترنا جواز ذلك.
الثالث: أن يكون الطريق الذي ثبت به كون الوصف علة [سمعيا]، لأن كون العلامة أو الوصف (٦٦/ أ) الذي لا يقتضي لنفسه علة، أمر وضعي، ودعوى على الشرع، فلا يثبت إلا بطريق سمعي. وقد تقدم الكلام على','3','383',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2015,'[الطرق] التي تثبت بها علل الأصول. وهذا هو الذي قصده الإمام ههنا.
وقول الإمام: والكلام ههنا مخصوص بقياس المعنى والشبه، وليس الأمر كذلك، بل الحاجة ماسة في هذا المكان إلى جميع الطرق التي تثبت بها علل الأصول، سواء كان ذلك الطريق توقيفيا صريحًا، أو إيماء نصًا أو ظاهرًا، فإن جميعها يرشد إلى التعليل.
الرابع: أن لا يكون الأصل فرعًا لأصل آخر، فلا معنى لقياس الذرة على الأرز، ثم قياس الأرز على البر. وإن اعتبر الجامع الأول، فالاعتبار بالبر على الحقيقة، وإدخال الأرز تطويل الطريق من غير فائدة، وإن لم يلتفت إلى جامع الأصل، بطل الإلحاق. وقد قدمنا في ذلك قولًا بالغًا.','3','384',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2016,'ومما يحقق ذلك أن الأصل إن كان معللًا بمعنى مناسب غير مؤثر، فإنه عرف كون المعنى علة بشهادة الأصل له، وإن استنبط من الفرع غير ذلك المعنى، فقد [فقد ما] ثبت [به] كون وصف الأصل علة، وبطلت الشهادة، فيبقى الاعتماد على معنى مجرد، [لا أصل] له، فليستعمل [استدلالًا] لا قياسًا. وإن كان النظر في قياس الشبه، فربما يشبه [الأول] [الثالث] والرابع [خامسًا]، فينتهي الأمر بالآخر إلى وجهٍ لا يشبه الآخر الأول بوجه. (٥١/ أ) فثبت بهذا أن القياس على فرع العلة باطل.
الخامس: أن يكون دليل صحة [الأصل] مختصًا بالأصل، لا يتناول [الفرع] بحال، كما أن شرط تصور القياس، أن يكون حكم الأصل مختصًا به توقيفًا، فإنه لو كان اللفظ يتناول المحلين، خرج كل واحد منهما عن كونه','3','385',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2017,'أصلًا للآخر. فلو قال [- عليه السلام -: (لا تبيعوا الطعام بالطعام)، لاستحال أن يكون البر أصلًا للأرز على هذا التقدير، فكذلك إذا ألحق الأرز بالبر بجامع الطعم، واستدل على ذلك بقوله]- عليه السلام -: (لا تبيعوا الطعام بالطعام). من جهة أنه مشتق علق الحكم عليه، ودليل التعليل يثبت الحكم على التعميم في المحل الملحق، فهذا يخرج القياس عن حقيقته. وكذلك [إذا] فضل القاتل المقتول بفضيلة الإسلام، فوجب أن لا يقتل به، كما لو قتل الحربي. [ثم استدل] في منع قتل المسلم بالحربي بقوله - عليه السلام -: (لا يقتل مسلم بكافر). فهذا يخرج القياس عن حقيقته، وهو بمثابة قياس الشعير على البر، والدراهم على الدنانير، وذلك محال.
السادس: قال قوم: شرط الأصل المقيس عليه: أن يقول دليل على جواز القياس عليه. (٦٦/ ب) وقال قوم: على وجوب تعليله، ولم يكتفوا بقيام الدليل على أصل القياس. وأبدى أبو حامد في ذلك تفصيلًا فقال: أما قياس','3','386',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2018,'الشبه، فقد قال قوم: شرط جواز الاعتماد على الوصف الشبهي، دعاء [الضرورة] إلى التعليل، فلو لم يقم دليل وجوب التعدية في البر، في مسألة الربا، لما جاز القياس. والضرورة الداعية إلى طلب مناط زائد على الاسم، أنه إذا صار دقيقًا أو خبزًا، زال اسم البر، وبقي حكم الربا.
قال أبو حامد: وليس ذلك [بدعًا] عندي في أكثر الأشباه، فإنه إذا أمكن تعرف الحكم باسم المحل، فأي حاجة إلى طلب [محل] لا مناسبة فيه؟ وقد بينا نحن ضعف هذا القول، وقلنا إنما يعتمد الوصف الشبهي، بناءً منا على أنا نظن أنه قالب المصلحة الخفية. فإذا ظفرنا بالوصف في غير مورد النص، فيجب إثبات الحكم.
وفرق الإمام بين الأشباه، فقال في بعضها يكفي الإلحاق، الاطلاع على الوصف الشبهي، وفي بعضها لابد من دعاء الضرورة إلى التعليل. قال: فأما في الموضع [الذي] يكفي [في] الإلحاق الاطلاع على الوصف الشبهي، ففي الأشباه المعتضدة بالأمثلة، كالقول في حمل قليل العقل على','3','387',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2019,'العاقلة، وتقدير أروش أطراف العبيد، [بالسبب] الذي يتقدر به أروش أطراف الأحرار.
والموضع الثاني التعلق بمعان كلية، كإلحاق الوضوء بالتيمم. قال: ففي هذين الموضعين، يكفي في الإلحاق الاطلاع [على هذه الأوصاف]. وأما الشبه المتعلق بالمقاصد، فشرط الاعتماد عليه، إرهاق الضرورة إلى التعليل. ومثل ذلك بتعليل الربا في المطعومات بالطعم. قال: فلولا ضرورة إجماع القائسين على التعليل، [لم يجز التعليل] بالطعم. وسيأتي الكلام عليه في غير هذا الموضع [بأشبع] من هذا.
[فمعنى كلام] الإمام: إنه يتوصل إلى الكلي والجزئي بهذا النوع من النظر، فيكون الاطلاع على هذه الأوصاف، يتبين به الناظر كون المحل معللا، ويتبين [به] مع ذلك نفس العلة. إذ الاطلاع على عين العلة، أخص','3','388',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2020,'من كون المحل معللا، إذ قد ثبت كونه معللا، وإن لم تتعين العلة، ولا يتأتى أن يطلع على العلة، ولا يعرف كون المحل معللا، فإنه [يلزم من وجدان الأخص وجدان الأعم، وإن لم] يلزم من وجدان الأعم وجدان الأخص.
وهذا إنما يتحقق بعد تقرير أصل، وهو أن القياس ثبت دليلا في الشرع [في] العموم، وأنه متى ظهرت العلل، بطل الاقتصار على صور [المحال]. وهذا هو الذي ثبت عندنا في الشيعة، فإن أصحاب رسول الله - صلى الله عليه وسلم - خاضوا في القياس خوض من لا يعتقد حصرًا وضبطًا، تبين (٦٧/ أ) ذلك منهم (٥١/ ب) بكثرة أقيستهم واسترسالهم على اتباع المعاني، [والقضاء] في الوقائع المختلفة في قوة المعنى وضعفه بالقياس. وهذه الأمور لا يجمعها إلا الالتفات إلى بيان مناط الأحكام. فهذا [هو] الذي عرفنا أن المعاني إذا ظهرت، لم يقتصر على صور الأصول.
السابع: أن لا يتغير حكم الأصل بالتعليل. ومعناه: أن العلة إذا عكرت على الأصل بالإبطال، كانت باطلة. وإذا كان الجامع بين الأصل والفرع ينافي','3','389',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2021,'حكم الأصل، بطل القياس. [وهل] إذا كانت العلة المستنبطة تخصص اللفظ العام، هل تكون باطلة أم لا؟ هذا فيه تفصيل. فنقول: إن كان المعنى سابقا إلى الفهم عند سماع اللفظ من غير استئخار [عنه]، وافتقار إلى تأمل واستبناط، فهو المحكم في التخصيص والتعميم. فلنذكر مثاله ولنبين دليله.
أما مثاله: فكقوله - عليه السلام -: (لا يقضي القاضي وهو غضبان). فإنا نجوز القضاء مع الغضب اليسير، ونمنعه مع الألم المبرح، والجوع المفرط. وذلك متفق عليه. وقد ذكرنا هذا فيما سبق.
والسبب فيه: أن المحذور من الاستنباط المغير، أن يثبت حكم من اللفظ، فيستنبط معنى يسقط ما ثبت من اللفظ. وهذا لا يتحقق في المعنى المساوق لسماع اللفظ، إذ لم يستقر حكم حتى يكون الاستنباط مزيلا له. أما إذا فهم حكم العموم أو مقتضى الظاهر، فاستنبط المعنى الذي يقتضي تخصيص العموم وإزالة الظاهر، فلا يصح أن سيتنبط من مورده ما يناقضه. بل لا يتصور أن يستنبط منه إلا ما يوافقه. فإن وافقه في جميع صوره، فهو حسن بالغ. وإن وافقه في بعض الصور، وفقد في بعضها، فذلك البعض الذي فقد [منه] المعنى المستنبط، وجد فيه اللفظ الدال، فيكون وجدان اللفظ يقتضي ثبوت الحكم، وقد فقد المعنى.
فإن قلنا: إن [فقدان] المعنى لا يقتضي شيئا أصلا، فكيف يتصور ترك اللفظ [لغير] دليل معارض؟ وإن قلنا: إن عدم المعنى [قد] يدل،','3','390',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2022,'فدلالة عدم المعنى على عدم الحكم دون دلالة ثبوت اللفظ على الحكم، بل ربما يقال دلالة اللفظ على الحكم أقوى من دلالة طرد المعنى على ضده. فإذا كان طرد المعنى لا يقف لظهور اللفظ، فكيف يتقدم عكس المعنى على ظهور اللفظ؟ لا جرم قال أبو حنيفة: يعتمد على عموم لحكم في الأصل، بناء على اللفظ العام، ويتبع خصوص المعنى [في الفرع].
ومثله تحريم الخمر لعلة الإسكار، قال أبو حنيفة: يحرم قليلها وكثيرها، فإن اللفظ المحرم لم يفصل بين القليل والكثير، ويحرم المقدار المسكر، تمسكًا بمقتضى التعليل. وعن هذا قالوا: إنما يضاف (٦٧/ ب) إلى العلة حكم الفرع. فأما حكم الأصل، فيضاف إلى النص دون العلة. وسيأتي لهذا مزيد تقرير في العلة القاصرة.
[الثامن]: أن لا يكون الأصل معدولًا به عن [سنن] القياس. وهذا الشرط يتسع الكلام فيه. وقد ذكره الإمام بعد هذا، فلنؤخر الكلام عليه إلى الموضع الذي ذكره فيه.
قال الإمام رحمه الله: (وقد ينحسم [القياس الشبهي] وما يقال إنه في','3','391',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2023,'معنى الأصل) إلى قوله ([والمعلل] هو الذي ينقدح فيه معنى مخيل، أو شبه على شرط السلامة). قال الشيخ: قال الإمام: الناظر إذا فقد المعنى، رد نظره إلى [الشبه]، وهو أوسع الأبواب. وصدق رحمه الله، وذلك أن [التشبيه] ينقدح عند إمكان المعنى، وعند عدم فهم المعنى (٥٢/ أ) أيضا. ولا تنحسم الأشباه إلا في التعبدات الجامدة.
وأما قوله: قد ينحسم القياس الشبهي وما يقال إنه في معنى الأصل، حكاية حالية، وذكر المثال الذي صوره. وهذا الكلام غير صحيح، فإنه قد بان آخرًا أن القضية كانت في محل اللوث، فإذا أردنا إلحاق محلٍ لا لوث','3','392',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2024,'فيه [بها]، تعذر علينا ذلك. فمن كان قاصر النظر، لم ينظر [لقيد] الواقعة، فيتخيل الإعراض عن [المحل]، و [لا التفات] إلى محض] ما تعرض الذاكر لذكره. وهذا غلط بين، فإنه [قد] يستغني المفتي عن التعرض لقيد [الواقعة]، لاشتمال الحال [عليها].
[وهو] بمثابة ما لو سأل رجل فقير عالما عن حنثه في يمينه بالله تعالى، والعالم يعلم فقره، فإنه يقول له: صم ثلاثة أيام، لا بناءً على مطلق','3','393',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2025,'السؤال، بل بناءً على تنزيل الجواب على ما علم من قيد الواقعة، فكيف يقال على هذا التقدير: إن قياس الشيء على ما في معناه محسوم ممنوع؟ وقد بينا أن غيره لا يساويه بحال. دل هذا على أن الكلام أولًا، لم يجر على حقيقته، بل فيه بعض المسامحة، ويتحقق آخرًا ثبوت الاقتران في الأمور المعتبرة.
نعم، ينحسن القياس الشبهي، وما يقال إنه في معنى الأصل، بالتوقيفات التعبدية المتلقاة من النصوص، أو مواقع الإجماع، كما قال [- عليه السلام -] لأبي بردة بن دينار، [وقد ضحى بعناق]: (تجزئك ولا تجزئ عن أحد بعدك). وكذلك قبول شهادة خزيمة وحده. واختصاصه - عليه السلام - بخواص في النكاح والمغانم. فإن الأقيسة لا جريان لها في هذه الأبواب، وإن كانت من أبواب [إلحاق] الشيء بما في معناه.
وقد ينشأ من هذا [الكلام] شبه لمنكري القياس، فقالوا: إذا كانت الأقيسة العلمية لا جريان لها في الشريعة، فالأقيسة الظنية بذلك أولى.','3','394',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2026,'قالوا: وقد رأينا الشرع أثبت أحكامًا لعلل، ثم اقتصرت (٦٨/ أ) تلك الأحكام [على] الأشخاص، ولم تتبع عموم المعاني. وتمسكوا بالصور التي ذكرناها. وقد أشرنا إلى شيء من هذا في إثبات القياس على منكريه. ولكنا نقول: لما مهد الشرع طريق الاعتبار بالأدلة القاطعة التي أقمناها، كان - عليه السلام - إذا أراد قصر حكم على شخص نص على ذلك، أو [أرشد] إليه بقرائن أحواله، فقال لأبي بردة بن نيار: (تجزئك ولا تجزئ عن أحد بعد). ولما بقي على اشتراط شاهدين في الخصومات، وقبل شهادة خزيمة وحده، علم المسلمون اختصاصه بذلك. فهذا هو الطريق الذي يعرفنا امتناع جريان القياس المعلوم في الفرع.
وأما قول الإمام: فخرج من ذلك أن المعلل هو الذي يتطرق إليه معنى أو شبه، والذي لا يعلل هو الذي لا ينقدح فيه معنى ولا شبه. هذا [الكلام]','3','395',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2027,'ناقص، فإنا قد بينا أن ألفاظ الشارع من أقوى ما تثبت بها العلل، وإن كان لا تظهر فيها معان مخيلة، ولا أشباه على شرط الأصوليين. لكنه- والله أعلم- إنما خص هذا الكلام، إذا أردنا أن نعرف كون الأصل معللًا بطريق الاستنباط دون التوقيف. ولهذا قال: [أو ليس] من الممكن أن يعرف بطريق الاستنباط كون الأصل معللًا من حيث الجملة دون الوقوف على عين العلة؟ نعم، إن انعقد [عليه] إجماع، أو ورد فيه نص، أمكن ذلك، كما إذا اجتمع العلماء على أن الربا معلل، علم التعليل دون التعليل، فينظر الناظر في عين العلة. فهذا- والله أعلم- هو الذي حمل الإمام على الاقتصار في المعلل على المعنى والشبه. والله الموفق.
قال الإمام: (مسألة: نقل أصحاب المقالات عن أصحاب أبي حنيفة أنهم لا يرون إجراء القياس في الحدود (٥٢/ ب) [والكفارات والتقديرات والرخص،','3','396',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2028,'وكل معدول به عن القياس]) إلى قوله ([تناقض] عليه القول في تفصيل الفروع). قال الشيخ: ما ذكره الشافعي من المناقضات التي وجهها، أكثرها غير واردة على القوم، ونحن نتتبع ذلك على ما ينبغي، ثم ننعطف على بيان مرادهم، وتفسير مقصودهم، فإذا تعينت المآخذ، وقع الكلام عليها بعد ذلك.
أما ما ذكره الشافعي من [أنه] كثرت أقيستهم في الحدود، فإن أبا حنيفة لم يثبت حدًا بقياس على حال. وأما كونهم أثبتوها بالاستحسان، [فهذا] ليس بنقض على [الحقيقة]، وإنما هو إثبات حكم بمأخذ آخر عند القوم. فينبغي أن يبحث عن معنى الاستحسان وحقيقته، وبيان كونه دليلًا.','3','397',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2029,'في الشرع أو غير دليل، ولا يقضى بأن مستند الحكم إليه قياس، حتى تثبت [مناقضته] في إثبات (٦٨/ ب) القياس في الحدود.
وقد اختلف الأصوليون في تفسير الاستحسان، فقال قائلون: هو ما استحسنه المجتهد بفعله، ويميل إليه برأيه. وهو عند هؤلاء من جنس ما يستحسن في العوائد، وتميل إليه الطباع. قالوا: فيجوز الحكم بمقتضاه، [إذا] لم يوجد في الشرع ما [يرده]. وكان هؤلاء يرون ذلك من جملة أدلة الأحكام. ولا شك في أن العقل يجوز أن يرد الشرع بذلك، بل يجوز أن يرد بأن ما سبق إلى أوهام العوام مثلًا، فهو حكم الله [عليهم]، لقبل ذلك وعلم بمقتضاه. ولكن وقوع التعبد به، لا يعرف بضرورة العقل ولا بنظره، ولا بدليل قاطع من الشرع، بل لم يرد في ذلك [وجه يقتضي غلبة الظن]، فضلًا عن العلم، فلا يجوز إسناد الحكم إليه. فإن جعل هذا مأخذًا من مآخذ الأحكام، فلا يجوز الحكم به بحال.','3','398',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2030,'وأيضًا فإنا نعلم قطعًا من الصحابة [- رضي الله عنهم -] أنهم حصروا نظرهم في الوقائع التي لا نصوص فيها في الاستنباط، والرد على ما فهموه من [الأصول] الثابتة، ولم يقل أحد منهم: إني حكمت بكذا، لأن طبعي مال إليه، وأنه لموافق لمحبتي ولرضاي. ولو قال ذلك، لاشتد عليه النكير، وقيل له: من أين لك أن تحكم على عباد الله بمحض ميل نفسك وهوى قلبك؟ هذا مقطوع ببطلانه لا شك فيه، بل كانوا يتناظرون، ويتشاورون، ويعترض بعضهم على مأخذ بعض، وينحصرون إلى ضوابط الشرع.
ولو رجع الحكم إلى الاستحسان، لم يكن للمناظرة مجال، فإن الناس تختلف أهواؤهم وأغراضهم في الأطعمة والأشربة، ولا يناظر بعضهم بعضا على أن هذا: [لم] كان عندك أطيب من هذا؟ لبناء الأمر على الأغراض. وليس الحكم في الشريعة من هذا [القبيل] على حال.
وقد تمسكوا بشبه قررت لهم، وما أظن ذلك إلا تكلفًا من المصنفين، وتقرير مذاهب واعتراضات عليها، وإلا فلا شك أن أحدًا من العلماء لا [يجيز] الاستناد في الحكم إلى مثل هذا.
فلنذكر ما ذكروه، وقالوا: قال الله تعالى: {الذين يستمعون القول فيتبعون','3','399',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2031,'أحسنه}. قلنا: اتباع أحسن ما أنزل إلينا، اتباع الأدلة، فبينوا أن هذا مما أنزل إلينا، فضلا عن أن يكون من أحسنه؟ وهو كقوله: {[واتبعوا] أحسن ما أنزل إليكم من ربكم}. ثم نقول: نحسن نستحسن إبطال الاستحسان، وأن لا يكون لنا شرع إلا الأدلة المتلقاة من المصدق بالمعجزة.
ثم إنه يلزم من ظاهر هذا استحسان العامي، ومن ليس من أهل النظر، إذا كان الحكم (٥٣/ أ) يترتب على مجرد ميل النفوس وهو [الطباع]. وذلك (٦٩/ أ) محال قطعًا، فلا سبيل إلى القول بذلك، للعلم بإضراب الشريعة عنه.
الشبهة الثانية: تمسكهم بقوله - عليه السلام -: (ما رآه المسلمون حسنًا فهو عند الله حسن".','3','400',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2032,'وهذا لا حجة فيه من أوجه:
أحدها- أن ظاهره يدل على أن ما رآه المسلمون بجملتهم حسنًا، فهو حسن، [والأمة] لا تجتمع على باطل. فإذا أجمع المسلمون على حسن شيء، فهو حسن، وهو الإجماع، وهو من الأصول، من غير إشكال.
الثاني- أن هذا خبر واحد، فلا [يصلح] لإثبات القواطع. ثم أنه يلزم من ظاهره، إذا لم يرد به استحسان أهل الإجماع، وحمل على استحسان [الآحاد والاستحسان] العوام، وليسوا من أهل النظر والاجتهاد، فهو معرض عنه بالإجماع، فلئن قيل: المراد استحسان من هو من أهل الاجتهاد. فهذا ترك للظاهر، فيبطل الاستدلال. ثم إذا كان المستحسن [لا يحتكم] إلى الأدلة، فأي حاجة إلى اشتراط منصب الاجتهاد؟ فلئن قيل: اشتراط ذلك حذرًا من مخالفة الأدلة. قيل في مقابله: المراد استحسان ينشأ عن الأدلة، كيف والصحابة - رضي الله عنهم - قصروا تصرفهم على الكتاب والسنة، والرأي الناشئ منهما، وإن لم يظن بهم استفتاح أمرٍ من تلقاء أنفسهم، وإنما كانوا [يحومون] على فهم مقاصد الشرع من أدلته.','3','401',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2033,'الشبهة الثالثة: أنهم قالوا: إن الأمة استحسنت دخول الحمام من غير تقدير أجرة عوض [الماء]، ولا تقدير مدة اللبث، ولا تقدير الماء المستعمل، ولا سبب لذلك إلا أن المضايقة في مثل ذلك قبيحة في [العادة]، فاستحسن تركه. فهذا يدل على أن ما جرت العادة بكونه حسنًا عند [العقلاء]، فهو موافق لمقصود الشرع.
وهذا الوجه هو أقرب الوجوه من ذوق الشريعة أيضًا، فإن النبي - صلى الله عليه وسلم - قال: (بعثت لأتمم مكارم الأخلاق). واستقراء الشريعة أيضًا يرشد إلى طلب مكارم الأخلاق بأمور كلية، والنهي عن سفسافها. ولأجل ذلك حرم الكذب وغيره من مذام الأخلاق. [وخضت] الشريعة [على الصدق] والمروءة','3','402',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2034,'وحفظ المناصب. فبالنظر إلى هذه [الجهة]، قد يغلب على الظن أنه إذا ثبت كون الشيء حسنًا عند أهل العادات، فهو مطلوب الشرع، ولكن لما كان في الأمر انقسام في هذا النوع إلى الواجب والمندوب، [عسر القضاء] بذلك على العموم. وهذا أيضًا بشرط السلامة عن المعارض، ولا يكاد أن يكون للذاهبين إلى القول بالاستحسان أمثل من هذا التقدير على حال.
وأما مسألة دخول الحمام من غير [ذكر] العوض، (٦٠/ ب) فالعرف قدره، فلا يفتقر إلى تعيين التقدير. وأما مدة اللبث، وقدر الماء المستعمل، فيسقط ذلك للضرورة إليه، وعدم [المضايقة] فيه، فلا غرر [يحذر في ذلك]. [كالزوج] إذا تزوج لا يجد للزوجة نفقة.
ولننبه ههنا على أصل من الفقه، وذلك أن نفي جميع الغرر [في] العقود، لا يقدر عليه، وهو يضيق أبواب المعاملات، ويحسم جهات المعاوضات. ونفي الغرر إنما يشترط تكميلًا للقواعد، وإزالة لما يتوقع من','3','403',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2035,'خصام ونزاع. فهو إذًا من التكميلات، والتكميلات إنما تراعى إذا لم يفض اعتبارها إلى إبطال المهمات. فإذا أفضى إلى ذلك، وجب الإعراض عن التتمة، تحصيلًا للأمر المهم. ولذلك نظائر من الشريعة، سنذكرها عندما نأتي عليها، إن شاء الله تعالى.
فكل تتمة أفضى اعتبارها إلى (٥٣/ ب) إبطال الأصل المقرر، وجب الإعراض عنها. إذ في مراعاتها إبطال أصلها، وفي إبطال أصلها رفعها، وقاعدة الشرع ضرورية، لا غنية للخلق عنها. فلو اشترط انحسام [الغرر] بالكلية، لتعطلت القاعدة على الضرورة، فوجبت المسامحة في [الأغرار] التي [لا انفكاك] عنها، إذ يشق فيها، فسومح بيسير [الأغرار]، لضيق الاحتراز، مع حقارة ما يفوت، أو يحصل من الأغراض، ولا يسمح بكثرتها، إذ ليس [هو] في [محل] الضرورة بحال، ولعظيم ما يترتب عليه من الأخطار.
والفرق بين القليل والكثير غير منصوص [عليه] من قبل الشارع في كل','3','404',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2036,'الأمور. ولكن قد نهى عن أنواع يعظم فيها الغرر، فجعلت أصولًا، واعتبر بها غيرها، فقد صار القليل أصلًا في العفو، والكثير أصلًا في المنع، ويدور بين الأصلين فروع [تجاذب] العلماء النظر فيها، فمن مائل إلى جانب العفو والترخيص، ومن صائر إلى جانب المنع والتضييق. حسب ما يظهر لأهل التحقيق. فإذا قل الخطب، وسهل الأمر، وقل النزاع، ومست الحاجة إلى المسامحة، فلا إشكال.
ومن هذا القبيل: مسألة التقدير في ماء الحمام، ومدة اللبث فيه. [وقد] بالغ مالك رحمه الله في ذلك، وأمعن النظر فيه، فجوز أن يستأجر الأجير بطعام، ون كان لا ينضبط مقدار أكله، ليسارة أمره، وخفة خطبه، وعدم المضايقة فيه. فرحمه الله ما أجراه على قواعد الشريعة، ولقد قال في ذلك: فالزوج إذا تزوج، لا يجد للزوجة نفقة [من هذا القبيل].','3','405',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2037,'ومن هذا القبيل: تفرقته [رحمه الله] بين تطرق يسير الغرر (٧٠/ أ) إلى الأجل، ومنعه من ذلك في مقدار الثمن، قال رحمه الله: يجوز أن يشتري الإنسان سلعة إلى الحصاد، وإن كان اليوم بعينه لا ينضبط، ولو باع السلعة [بدرهم] أو ما [يقاربه]، لم يجز ذلك.
والسبب فيه أن المضايقة في تعيين الأثمان وتقديرها، ليست في العرف كالمضايقة في الأجل وتعيين يومه، إذ يسامح البائع في التقاضي الأيام، ولا يسامح في مقدار الثمن على حال. ويعتضد هذا المعنى بما جاء عن النبي - صلى الله عليه وسلم -: روى عبد الله بن عمرو بن [العاص]: (أن النبي - صلى الله عليه وسلم - أمر بشراء الإبل إلى خروج [المصدق)]. وذلك لا ينضبط يومه، ولا تتحرر ساعته، ولكنه على','3','406',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2038,'التقريب والتسهيل. فلابد من التنبه لهذا الأصل، فهو أصل عظيم في فهم مقاصد الشرع، المرتبة على أغراض العقلاء. فهذا هو الجواب عن تقدير الماء، ومدة اللبث في الحمام. فهذا أحد تأويلات الاستحسان.
التأويل الثاني: [قولهم]: المراد به: دليل في نفس المجتهد لا تساعده العبارة عنه، ولا يقدر على إظهاره وإبرازه. قال القاضي وغيره: وهذا باطل، لأنه لو فتح هذا الباب، لبطلت الحجج، وتقاومت الأدلة، وادعى كل من شاء ما شاء، [واكتفى] بمجرد القول، وألجئ الخصم [إلى الإبطال]، وهذا [يجر] خبالًا لا خفاء به. والأمر- لعمري- ظاهر على قول القاضي، [ولكن] قد تقدم في قياس الشبه أن العلماء اختلفوا، هل يجب على المناظر أن يبين الوجه الذي يثير الشبه منه [غلبة] الظن، أو لا يجب ذلك؟ [وقد] ذكرنا وجه كل واحدٍ من القولين، وبينا أن أبا حامد [ينقل] عن المتقدمين الاكتفاء بمطلق الجمع، ويكلفون الخصم الاعتراض. فإذا أمكن ذلك في قياس الشبه، أمكن في غيره.','3','407',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2039,'ومن هذا القبيل [عندي] اشتراط (٥٤/ أ) الملاءمة في الاستدلال المرسل، فإنه لا يقدر على بيان [أن] المناسب من قبيل الملائم بحال. فإن اشتراط التماثل بين الوصف وبين الأوصاف المعتبرة، هو التأثير بعينه، والاكتفاء بمجرد المناسبة هو الغريب. وبين الرتبتين درجات لا تنحصر، [فرجوع] الملاءمة إلى بعضها دون بعض عسير، فليخرج بيان الملاءمة في الوصف على بيان التشبيه في الوصف الذي لا يناسب. فإن أراد من قال بالاستحسان هذين، فقد قال بقول قاله جماعة من الناس، فلا ينبغي أن ينكر عليه ذلك.

<span data-type="title" id=toc-362>التأويل الثالث </span>للاستحسان: ذكره الكرخي وبعض (٧٠/ ب) أصحاب أبي حنيفة، قالوا: ليس هو عبارة عن قول من غير دليل، ولكنه حكم مستند إلى دليل. وهو أنواع:
منها- العدول بالمسألة عن نظائرها بدليل [الكتاب، مثل] قوله تعالى:','3','408',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2040,'{خذ من أموالهم صدقة}، [فإنه يختص بأموال الزكاة، فلو قال قائل: مالي صدقة]، فظاهر لفظه يعم كل مال، ولكنا نحمله على مال الزكاة، لكونه ثبت الحمل عليه في الكتاب. وكأن هذا يرجع إلى تخصيص العموم بعادة فهم خطاب القرآن.
ومنها- العدول بالمسالة عن نظائرها بدليل [السنة]، كالفرق في سبق الحدث والبناء على الصلاة، [فبناء] السبق والتعمد على خلاف قياس الأحداث. وهذا مما يجوز اعتباره على الجملة، وهو نوع من الاستدلال، فإن حاصله راجع إلى مخالفة القياس، لدليل اقتضى ذلك. فإن سمى مسم ذلك استحسانًا، فلا يناقش فيه. هذا ما ذكره الأصوليون في تفسير الاستحسان.
والذي يظهر من مذهب مالك رحمه الله القول بالاستحسان على غير [هذه] التأويلات، ولكنه يرجع حاصله إلى استعمال مصلحة جزئية في مقابلة قياس كلي، فهو كتقديم الاستدلال المرسل على القياس. فلنذكر مثاله، ثم نحقق دليله.','3','409',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2041,'أما مثاله: فقد قال أشهب من أصحابنا: إذا اشترى رجل سلعة بخيار، ثم مات وله ورثة، فاختلفوا، فقال بعضهم: نرد، وقال بعضهم: بل نختار الإمضاء. قال: والقياس الفسخ، [ولكن أستحسن] لمن أراد الإمضاء أن يأخذ [نصيبه] ممن لم يمض، إذا امتنع البائع من قبول نصيب الراد.
قال: وإنما قلنا: القياس الفسخ عند رد بعضهم، لأن الذي ورثوا عنه الخيار، لم يكن له رد بعض السلعة وقبول بعضها، بل إذا رد البعض تعين عليه رد الجميع، وهم في ذلك بمنزلة موروثهم. فمقتضى القياس عند رد بعضهم أن ينفسخ البيع في الجميع.
أما وجه الاستحسان، فهو [أنه] إذا تعارض الحقان، ووجد أحد الفريقين طريقا إلى دفع الضرر [عنه، ولم يجد الآخر طريقًا، فليدفع ضرر من لا يجد طريقا إلى دفع الضرر] عن نفسه، تقديمًا لأشد الضررين على أخفهما. وهذا أمر ثابت في الشريعة.
وبيان تفاوت الضرر [في المسألة] أن البائع متمكن من أخذ نصيب الراد، بحيث لا يكره على شيء، والمتمسك لا قدرة له على إلجاء شريكه إلى','3','410',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2042,'التمسك [ببعض السلعة]، فكان الأقرب إلى دفع الضرر عن الفريقين، تخيير البائع في قبول [نصيب] الراد، أو إلزام التمسك بالجميع، ويصير الذي لم يقبل- لهذه الضرورة- (٧١/ أ) كالمعدوم. فهذا حكم بالمصالح المرسلة، وليس يعري عن ملاءمة قواعد [الشريعة]. فهذا تفسير الاستحسان على هذا قول.
ولابن القاسم في الاستحسان قول غير هذا. قال في رجل مات وترك دارًا، ولم يترك في الظاهر إلا ولدًا واحدًا، فسكن الولد الدار، ثم جاء أخ له وطلب من الساكن أجرة نصيبه. قال غير ابن القاسم: له ذلك. وقال ابن القاسم أستحسن أنه لا شيء له على أخيه، لكن بثلاثة شروط: (٥٤/ ب)
أحدها- أن يكون الساكن لم يعلم بالأخ.
الثاني- أن يكون في نصيبه من الدار ما يكفيه لسكنه، وليس فيه ضرورة إلى سكن نصيب أخيه.
الثالث- أن لا يكون أخذ في النصيب [مالًا] بإجارة. فإذا اجتمعت هذه الشروط، فلا يطالب بشيء. وكأن [حاصله] راجع إلى أنه لم ينتفع بنصيب الأخ على الحقيقة. ولو كان كذلك، لكان هذا حكمًا بالقياس، ولكنه لما لم يكن انتفاعه مخصوصًا بنصيبه، فما من جزء إلا وللأخ فيه حق، فكان القياس من هذه الجهة يقتضي الرجوع، [ولكن] التفت إلى عدم المنفعة في التحقيق عند كون الساكن غير متعد. والتفت إلى شياع الانتفاع عند [العدوان].','3','411',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2043,'وهذا التفصيل من أدق تفريع، وهو أن يكون للشيء جهات تقتضي كل واحدة حكما في جميع الصور على التناقض، فيرجح العالم بعض تلك الجهات بالإضافة إلى بعض الأحكام، ويرجح الجهة الأخرى بالإضافة إلى غير تلك الأحكام. وكذلك فعل ابن القاسم في هذه المسألة، رجح جهة كونه غير محتاج إلى الانتفاع بنصيب الشريك عند عدم العلم. وهذا مناسب للتخفيف عنه، ورجح الالتفات إلى عدم اختصاص [الانتفاع عند] العدوان، وهو مناسب للضمان. فليس هذا الاستحسان خروجًا عن الأدلة بحال. [بل هو] تمسك بنوع من المصالح في مقابلة أقيسة كلية.
وقد قال [مالك] رحمه الله بالاستحسان في مسألة أخرى، قال ابن القاسم: قلت لمالك: لم قضيتم بالشاهد واليمين في جراح العمد وليست بمال؟ قال: إنه لشيء استحسناه، وما سمعت [من مالك] فيه شيئا. ولم يزد على ذلك، ولم يبين وجه الاستحسان [فيه]. ولكن الظاهر أنه رحمه الله ألحق ذلك بالشهادة في الأموال بالقياس، فإن سر الاكتفاء بالشاهد واليمين في الأموال، تعذر الاحتراز بكمال البينة [عند] كثرة وقوع المعاملات، مع تعذر الاستظهار بشهادة البينات، وجراح العمد أشد خفاء من المعاملات المالية (٧١/ ب)، فهي بالاكتفاء بالشاهد واليمين أولى.','3','412',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2044,'ووجه آخر، وهو أنه قد ثبت دخول [الأيمان] في أمر الدماء، بناءً على مجرد اللوث، وظهور الشبهات، حتى قدم المدعون في ذلك، وإن كان لا يجري هذا في المعاملات. فلما [تقررت] هذه الجهات، أمكن قياس جراح العمد على الأموال. والله الموفق للصواب.
هذا تقرير الاستحسان عند العلماء.
وأما قضاء أبي حنيفة بالاستحسان، فلنذكر المسألة، ولننبه على وجه الكلام فيها. قال أبو حنيفة: إذا شهد أربعة على رجل بالزنا، ولكن عين كل واحدٍ جهة غير التي عينها الآخر، قال أبو حنيفة: القياس [أن لا] يحد، ولكن أستحسن حده.
أما قوله: القياس [ألا يحد، فنعني بالقياس أنه ثبت في الشرع] أنه لا يحد إلا من شهد على زناه أربعة، فإن الله تعالى قال: {والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء [فاجلدوهم ثمانين جلدة]} [فيفتقر] الرامي بالزنا إلى أن يأتي على [ما] [قذف بأربعة] شهداء، فإذا عين كل واحدٍ دارًا، فلم يأت','3','413',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2045,'على كل زنية أربعة. فهذا لا يحد عند مالك والشافعي وأبي حنيفة. وقال ابن الماجشون [من أصحابنا]: يحد إذ [وقع اجتماع] الأربعة على كون زانيا، والأول أصح. ولو كان الأمر على [ما قال]، للزم إذا شهد [واحد] على الزنا [أن يحد]. وهذا قول الشافعي، ومالك لا يقوله. وظاهر [القرآن] يرده، فإنه من لم يأت بأربعة شهداء، فهو قاذف محدود. وقد [جلد] عمر أبا (٥٥/ أ) بكرة وصاحبيه في الشهادة على المغيرة عند عدم جزم','3','414',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2046,'[زياد] بالشهادة، لأنهم لم يأتوا قذفة، وإنما جاءوا شهداء.
وإذا تبين أنه لابد من اجتماع الأربعة على الزنا الواحد، فإذا عين كل واحدٍ دارًا، امتنع الاجتماع. [وإذا عينوا زنية] واحدة، فقد حصل الشرط، فإذا عين كل واحد زاوية، فالظاهر تعدد الفعل، وامتناع الاجتماع على الزنا الواحد، ومن الممكن [التزحف] كما قدره الخصم.
فمعنى قوله: القياس أن لا يحد، أي الأمر الظاهر أنه لم يجتمع الأربعة على زنًا واحدٍ، والأمر البعيد: التقدير الذي ذكرناه. ولكنه يقول: في المصير إلى الأمر الظاهر تفسيق العدول، فإنه إن لم يكن محدودًا، صار الشهود فسقة، ولا سبيل إلى تفسيقهم ما وجدنا إلى العدول عن ذلك طريقًا، فيكون وجوب الجريان على مقتضى العدالة عند الإمكان [عين] ذلك الاحتمال. فليس ذلك حكما بقياس على حال، ولكنه تمسك باحتمال تلقي الحكم فيه من القرآن. فإن','3','415',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2047,'القرآن (٧٢/ أ) أرشد إلى حد [الزنا]، وهذا يرجع إلى تحقيق مناط كونه زانيًا، فأين هذا من الحكم بالقياس، والرجل يقول: حددته بشهادة العدول؟ فهذا [عندي] من التحامل على الخصم من غير نقضٍ على التحقيق. نعم، هل يصلح [ذلك] الدليل [لعضد] هذا الاحتمال؟ فيه نظر يتعلق بالفروع من غير نقض قواعد الأصول.
وأما الشافعي رحمه الله، فلا يرى [ربطًا بين] رد [الشهادة] على الزاني والحد، فإنه إنما يحد من أتى قاذفًا لا شاهدًا، فيقول: لا يحد المشهود عليه، تمسكًا بالظاهر، ولا يحد الشهود، إذ ليسوا قذفة.
فهذا هو سبب اختلاف العلماء في حد الشهود. والظاهر عندنا خلاف قول [الشافعي] في كون الشاهد لا يحد، وظاهر القرآن من خلافه، وفعل عمر أيضا يرد عليه.
وأما قوله: إن الكفارات [قد] كثرت أقيستهم فيها، وقاسوا الإفطار بالأكل على الإفطار بالوقاع. فهذا موضع لطيف، يفتقر إلى إمعان نظر. أما','3','416',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2048,'أبو حنيفة، فإنه لم يقل هذا بناءً على القياس بحال. [بل] قال: هذا يرجع إلى تنقيح المناط. [وقد قدمنا نحن ما يريده الأصوليون بتنقيح المناط]، وبينا مأخذه في اللغة، ومقصد الأصوليين منه. والغرض الآن بيان مقصود المسألة في ذلك، وآل الأمر هناك إلى [أن] التمسك بالتنقيح، يقع الحكم فيه باحتمال اللفظ، والقول بالقياس لا يستند إلى ظاهر اللفظ، [ولا إلى احتماله]، بل يستنبط من مورد اللفظ معنىً يتعدى إلى محلٍ آخر، لا يدل اللفظ عليه، لا ظهورًا ولا احتمالًا. فهذا هو المعتمد الأصولي في الفرق بين تلقي الحكم من القياس، أو تلقيه من التنقيح.
وأبو حنيفة يزعم أنه نقح مناط واقعة الأعرابي، فتبين له أن المناط الإفطار لا الوقاع. ولو قال الأعرابي للشارع: أفطرت في نهار رمضان، فقال: اعتق رقبة، لم يكن الحكم على الأكل متلقى من القياس، بل من فتوى الرسول. ولو اقتصر النص بلا ريب على الجماع، لكان إلحاق الأكل بالجماع قياسًا، ولكن لما اشتملت الواقعة على ذكر [الهلاك]، فهم أن ذلك لسبب [الانتهاك].
ولما ذكر في الواقعة خصوصية ما وقع [الانتهاك] به، تردد','3','417',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2049,'العلماء، هل كان [ذكر] الوقاع حشوًا في الباب، والاعتماد [على] [الانتهاك]، أو جرى ذكر الوقاع مقصودًا؟ فإن لم يجر مقصودًا، وإنما أدير الحكم على الانتهاك، فالأكل منتهك، فلا يكون إثبات الحكم فيه قياسًا، ولكنه عدول عن الظاهر، وإسقاط بعض القيود، فيقع ذلك في أبواب التأويلات.
والقوم وإن منعوا إثبات الكفارات (٥٥/ ب) (٧٢/ ب) بالقياس، فليسوا يمنعون [إثباتها] بمحتملات الألفاظ، عند العضد بالأدلة. هذا هو السبب عند أبي حنيفة في إثبات الكفارات على الأكل، وليس عنده قياس على حال.
ولكن ننازعه في احتمال اللفظ لما ذكره، فيؤول النزاع إلى مسألة معينة، ولا يكون في ذلك نقض لقواعد الأصول.
والذي يصح عندنا أن اللفظ لا يحتمل ما ذكره بحال، فإن الإفطار لم يجر له ذكر على حال، ولم يذكر سوى الوقاع، ولم يجعل واضع اللغة لفظ الجماع قط عبارة عن الإفطار، لا على طريق الظهور، ولا عن طريق الاحتمال. ولو كان اللفظ محتملًا للإفطار، لكان لفظ الخمر محتملًا للنبيذ، بناءً على فهم علة الإسكار، ويبطل القياس على الإطلاق، وترجع جميع الأحكام المتلقاة من الاستنباط إلى تنقيح المناط، وفي ذلك إبطال القياس رأسًا.
وأما [قوله]: إنهم قاسوا قتل الصيد خطأ على قتله عمدًا، فلم','3','418',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2050,'يسلك أبو حنيفة في ذلك مسلك القياس، وإنما سلك فيه مسلك التأويل، وذلك أنه لم يجر ذكر العمد على طريق الاشتراك، وإنما ذكر لأنه الغالب، والتقدير عنده: ومن قتله منكم، [والغالب أنه لا يقتله إلا] [متعمدًا]، {فجزاء مثل ما قتل من النعم}. ولو كانت التلاوة [على ذلك]، لكان الخاطئ يكفر بمقتضى التلاوة، ولكن هذا، وإن لم يكن [ظاهرًا]، فهو تأويل مستقيم، إن أمكن عضده بدليل.
وقد قال الشافعي [رحمه الله] مثل هذا، فإنه من القائلين [بالمفهوم]، ويرى أن تخصيص الشيء بالذكر على جهة مخصوصة، يدل على نفي الحكم عما عداه. قال: إلا أن يجري التخصيص على حكم العرف، فإن المسكوت عنه يساوي المنطوق به. وهذا [عندي] في معنى التأويل، إذ الظاهر عنده القصر على المذكور، ولكنه يقدر التقدير الذي قدره أبو حنيفة. فليس هذا من أبواب القياس بسبيل، فلا مناقضة على [الحقيقة] في شيء من ذلك،','3','419',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2051,'وإنما يطالب [القوم] بالدليل العاضد للتأويل.
وأما كونه قاسوا في المقدرات في مسألة الدلو في البئر، فليس هذا بقياس أيضًا، وكيف يكون هذا قياسًا، وليس ههنا أصل يشهد لذلك. وإنما اعتمد القوم فيه أخبارًا، وإن لم تكن صحيحة، فيرجع النزاع إلى دليل مسألةٍ فرعية.
وإذا انتهى الكلام إلى [هذا] [المقدار] [المتقدم]، فلنذكر ههنا أصلًا، وننبه على سره، فنقول: لا يخلو إما أن يرجع التقدير إلى بيان محل الحكم، وإما أن يرجع إلى تقدير الحكم، فإن رجع التقدير إلى محل الحكم، فلا تمنع الزيادة بالقياس، [وهو] كقوله [- عليه السلام -]: (خمس فواسق يقتلن في الحل والحرم). فإنه لا يقتصر على هذا العدد، بل (٧٣/ أ) [نقيس]- عند فهم المعنى- ما يساوي الخمس في المعنى. ولم يشرع القياس إلا لتوسيع مجاري الحكم، وهو بمثابة قوله: (لا تبيعوا البر بالبر) الحديث. إلى آخره. فإنه [قد عد]','3','420',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2052,'أقسامًا، وزاد العلماء عليها [بالإلحاق]. وأما إذا رجع التقدير إلى مقدار الحكم، فإنه تمتنع الزيادة على ذلك، فإنه في الزيادة على ذلك مخالفة نص التقدير في الحكم، وإنما يطلب بالقياس حكم ما ليس منطوقا به.
مثاله: قوله تعالى: {الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة}. [فلا يجوز الزيادة على المائة بالقياس، إذا ثبت قصد الشارع إلى القصر على ذلك].
وقد اختلف العلماء في مسألة، وهي قوله - عليه السلام - لبعض الصحابة: (إذا بايعت فقل: لا خلابة، واشترط الخيار ثلاثًا). هل يجوز اشتراط الخيار أكثر من ثلاثة أيام؟ فقال الشافعي وأبو حنيفة: لا يجوز ذلك، وأجازه مالك رحمه الله. أما من منع، فق تمسك بما [قدمناه] في تقدير الحكم (٥٦/ أ). وأما من أجاز، فله مستندان:
أحدهما- أنه يقول: ورد ذلك على بيوع [مخصوصة]، لا تفتقر إلى اشتراط أكثر من ذلك، فإن القضية كانت في حبان بن منقذ، وكان يخدع في','3','421',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2053,'البيع، فقال - عليه السلام - له ذلك. وكان الذي يبيعه هي اسلع التي تباع في الأسواق، وهي لا تفتقر إلى زيادة. وليس في الحديث عموم، وإنما هو حكاية حال. فإذا تطرق إليها مثل ذلك، امتنع التمسك بها في غير تلك الواقعة.
الوجه الثاني- أن نقول: هل ذكرت الثلاث لأجل كونها ثلاثًا، أو لغرض يتحصل منها، وهو للاختبار والتروي؟ وفي الأول جنوح إلى التعبد عند فهم المعنى، وذلك [ممتنع]، فيجب المصير إلى مقتضى المعنى. ولكن في هذا مصير إلى جواز الزيادة في الحدود، نظرًا إلى حصول الزجر. ويجب عن هذا: بأن أمر الزجر لا ينضبط في العادة، لخفاء أحوال الناس، فأعرض عن الأمر الخفي، وناط الشرع الحكم بحد محدود، وعد معدود، بخلاف أحوال المشتريات، فإنها منضبطة في العادات، فالرد إلى العادة، تحصيلًا للغرض [أولى]، والأولى الطريقة الأولى.
وأما قوله: وأما الرخص، فقد قاسوا فيها، وتناهوا في البعد، فإن الاقتصار على الحجارة في الاستنجاء من أظهر الرخص إلى آخره. فأبو حنيفة [رحمه الله] قاس على محل النجو بلا إشكال، ولكنه لا يساعد أن الحكم في محل الاتفاق من الرخص، بل يقول إنه جارٍ على أقسام المعاني المعقولة، فإنه يزعم أن ذوي العادات يسامحون في يسير النجاسات،','3','422',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2054,'وإنما [يحترزون] عن الكثير منها. فقال: قد أدرك العقلاء فرقًا في المسامحة (٧٣/ ب) بين القليل والكثير، وإن كان يشق الوقوف على ضبط القليل، كما فرق بين التافه والنفيس في السرقة، وبين الغني والفقير في أموال الزكاة، ولكن لا ينص العقل على المقدار المرفق، لاختلاف أحوال الناس في ذلك، فجاءت الشريعة ببيان النصب، فتبين العقلاء أن ذلك غنيً شرعًا، ثم قاس الناس بعد فهم هذا المعنى. وكذلك القول في نصاب السرقة. وسيأتي تمام بيانه بعد هذا.
فكذلك [وقعت] المسامحة في يسير النجاسة، [وتحاشت] النفوس كثيرها، وورد الشرع بالعفو عن محل [النجو با] لاستجمار، فعرف أن هذا المقدار يسير في النجاسة، فقاس عليه سائر النجاسات. والشافعي رأى [أن] العفو كان بسبب التكرار، وتعذر الاحتراز، فرأى ذلك رخصة. وأبو حنيفة لا يعترف بالرخصة على حال. وعلى هذا أسقط أبو حنيفة وجوب استعمال الأحجار، نظرًا منه إلى قلة النجاسة، وأنها ليست في محل الضيق، فلا نقض على الحقيقة.
وأما قوله: مع قطع كل منصفٍ [بأن] الذين عاصروا رسول الله - صلى الله عليه وسلم - فهموا التخفيف في نجاسة البلوى. وأبو حنيفة ينكر هذا الإجماع، ولا','3','423',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2055,'يعترف به بحال. والشافعي لا ينقل ذلك نقلًا صريحًا، بل يقول إنه فهمه من قصد الأولين. وهذا أمر لا يغني مع الخصم المطالب بالدليل.
وأما قوله: ومن شنيع ما ذكروه في الرخص، إثباتهم لها على خلاف وضع الشرع إلى آخره. فهذا من عجيب الأمر، وكيف [يمكن] دعوى ذلك، وأنه [مناقضة] [للقياس]، وهم إنما تمسكوا في ذلك بظاهر التوقيف؟ ومنع القصر في سفر المعصية، تارك للظاهر، مفتقر إلى الدليل. فيا ليت شعري، كيف أورد مثل هذا مناقضة، والله تعالى يقول: {وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلوة}؟ فلم تثبت (٥٦/ ب) هذه الرخصة إلا بالتوقيف. [وهو] إنما منع إثبات الرخص بالقياس. فقد تبين أيضًا أنه لم يناقض.
وأما قوله: إن القياس تقرير المنصوص على إقراره، وإلحاق غيره به. فالأمر على ما قال، وأبو حنيفة إنما يحرص على تقرير هذه القاعدة، [ويخاف] من الحيد عنها.
أما قوله: وهذا الذي ذكره قلب لموضع النص في الرخص بالكلية. فهذا هو [مجرد] الدعوى، والرجل إنما يزعم أنه تمسك بالنص. ومن','3','424',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2056,'مذهبه أنه لا يجوز أن يستنبط من العموم [أو] الظاهر معنى يقتضي تخصيصًا أو تأويلًا، بل [يترك] الظاهر على ظهوره، والعام (٧٤/ أ) على عمومه.
وأما قوله في المعدول عن القياس: إنهم قاسوا فيه، فاعتبروا غير المحل بالمحل، وغير الخارج بالخارج. فقد قدمنا أن الاعتبار عنده ثابت، ولكنه غير معترف بأن الأصل معدول به عن القياس، فلا مناقضة أيضا على أصله.
وأما ما ذكروه من [أن] القهقهة تبطل الوضوء في الصلاة، واعتقدوا ذلك معدولًا به عن القياس، فلعمري إنه لكذلك، وقد صرح القوم بهذا. والقياس في قواعد الأحداث، أنه لا فرق أن يكون الحدث واردًا في الصلاة، أو في غيرها، ولما فرق في القهقهة بين الصلاة وغيرها، كان ذلك عدولًا عن القياس من غير إشكال. وأما إلحاق الصلاة ذات الركوع والسجود، فنعني به النافلة، لحصول العلم باستواء الفرض والنفل في شرط الطهارة. وأما صلاة الجنازة، فهي دعاء على الحقيقة، وإطلاق لفظ الصلاة [عليها] بالوضع اللغوي، ولذلك لا يقرأ عنده فيها بالفاتحة. وهي بمثابة الطواف بالبيت، وإن سمي صلاة، ولكنه على طريق التجوز، بالإضافة إلى العرف الشرعي، لا جرم حكم بصحة طواف المحدث. فالصلاة على الجنازة عنده كذلك.','3','425',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2057,'وأما النافلة، فصلاة شرعية كالفريضة، وقد جاء حديث: (من قهقه في صلاة فليتوضأ). وهذا دليل له، ويصرف لفظ الصلاة على الشرعي، وقد قال - صلى الله عليه وسلم -: (لا صلاة إلا بفاتحة الكتاب). يريد الصلاة في لسان الشرع. ولو صح هذا الخبر، لكان تمسكًا بظاهر لفظٍ لا بقياس. ولكن الخبر عندنا غير صحيح، فيرجع النزاع إلى مسألة فروعية، ولا تنتقض قواعد الأصول بحال. والقصة وإن لم تجر في نفلٍ، [بل] كانت في فرضٍ، ولكن القول العام إذا ورد على سبب خاص لم يختص به.
وأما قوله في الوضوء بنبيذ التمر: إنهم اعتبروا الغسل به، ولم يعتبروا نبيذ الزبيب بنبيذ التمر. فهذا أغمض ما في الأسئلة، فإنه قال - عليه السلام -: (ثمرة طيبة وماء طهور). فنبه على العلة، وهو طيب الثمر، وطهورية الماء، ولكنه قال: تمرة- بالتاء- ذات نقطتين مع سكون الميم، فكان ذلك مختصًا بالتمر دون الثمر. وقد يرى أن هذا لا يعقل معناه، فوجب الاقتصار على المنصوص، [فضعف] الإلزام من جهة اعتبار الغسل [بالوضوء]. ولكن الذي يلوح في العذر عن ذلك، حصول العلم باستواء أعضاء البدن، فيما يتعلق [بآلة]','3','426',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2058,'التطهير، ولا تعبد أصلًا في الفرق بين الأعضاء، وبين آلة وآلة، لحصول العلم بنفي التعبد (٧٤/ ب) في الفصل بين الأعضاء، بخلاف آلة التطهير في العبادات، فإنه قد ثبت تعبد في الآلة، حتى [جاز] الوضوء بالماء، منع بالورد، وإن كان يحصل كمال الوضاءة، مع صحة التوضؤ بالماء (٥٧/ أ) الكدر، ووجوب الوضوء على من خرج من الحمام، مع سقوط الوضوء [عن] [المتطلخ] بالطين. هذا تمام الكلام في الانفصال عن المتناقضات الورادة.
وبالجملة لم ينقض أبو حنيفة شيئاً من أصوله في هذه المواضع. وإنما [وجهت] هذه [الاعتراضات] على غير قواعده، وليس هذا بلازم عند ذوي التحقيق. والله المستعان.
قال الإمام رحمه الله: (ونحن [نرد] الكلام إلى الحجاج، [فنقول: لم منعتم إجراء القياس في هذه الأصول]؟ ) إلى قوله (وهي بمثابة سائر','3','427',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2059,'[المغارم]). قال الشيخ: هذا الذي ذكره الإمام ضعيف كله.
أما ما قرره عذرًا لأبي حنيفة من كون القياس لا يحصل علمًا، فهذا ليس مستند القوم على حال، وسننبه على ما يصح أن يكون مستندًا لهم.
وأما إلزام القصاص على الحدود، فالقواعد مختلفة في الغرض الخاص، فإن القصاص بني على كمال الزجر في صون الدماء، حتى أثبت أيمان القسامة مرتبة على اللوث، [وبدأ فيه بأيمان] المدعين، ولم يشترط اجتماع الشهداء فيه على شهادة واحدة، فإلزام إحدى القاعدتين على الأخرى تعسف محض.
وأما قوله: إنما كان يستقيم ما ذكروه، لو كانوا يثبتون الحد بمظنون، وهم قد أثبتوه بخبر الواحد. فقد ذكر الإمام العذر للقوم، وهو حصول اقطع [بوجوب] العمل بخبر واحد على العموم. وأما قضاؤه بمثل ذلك في القياس، فالإنصاف أنه ليس على هذا الضرب من القياس إجماع، وهو القياس في الأسباب، وسنبينه في إثر هذا الفصل.','3','428',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2060,'وأما ما [أورده] عليهم من الكفارات، وامتناعهم [من إجراء] القياس فيه، فهذا ليس بنقض، وإنما هو إلزام عكسٍ، إذ العلة منتفية، والحكم- وهو امتناع القياس- ثابت، فالعكس غير لازم في العلل الشرعية عند الأكثرين. على أنه إنما يلزم على تقدير أن لا تختلف علة أخرى. وقد أبدوا في الكفارات [سببًا] يقتضي منع القياس فيها، وذلك السبب هو الذي يتناول الحدود والكفارات جميعًا.
قال الإمام: (وأما المقدرات، فقد قالوا [فيها: لا تهتدي العقول إلى معانٍ تقتضيها، فلا يجري القياس فيها. قلنا: إن كان ينحسم فيها المعاني المخيلة المناسبة]، فلم ينسد مسلك الأشباه). قال الشيخ: [هذا الذي ذكره','3','429',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2061,'الإمام]، إنما يلزم من يقول بقياس الشبه، وأكثر أصحاب أبي حنيفة لا يقولون به. (٧٥/ أ) ثم إن قيل بقياس الشبه، فإنما يقال به عند تخيل معانٍ، من حيث الجملة. وأما التعبدات المحضة، فلا يثبت فيها معنى ولا شبه.
قال الإمام: (وأما الرخص، فقد قالوا: [إنها منح من الله تعالى وعطايا، فلا نتعدى بها مواضعها، فإن قياس غير المنصوص على المنصوص في الأحكام، احتكام على المعطي في غير محل إرادته ومنحته. فهذا هذيان، فإن كل ما يتقلب فيه العباد من المنافع، فهي] منح من الله تعالى، ولا تختص بها الرخص). قال الشيخ: ليس هذا معتمد القوم على الحقيقة، وإنما معتمدهم- فيما [يظهر]- أن الرخصة إنما [تفعل] بعد قيام سببٍ يقتضي المنع، فيعرض الشرع عن ذلك [المانع]، بالإضافة إلى محلٍ مخصوصٍ،','3','430',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2062,'على معنى المسامحة للخلق. وإذا خولف الدليل في موضع، تخفيفًا وتسهيلًا، لم تلزم المخالفة في غير ذلك المحل. وهذا في التمثيل بمثابة من مكن فقيرًا من شيء من ماله لحاجته، بعد أن ثبت ما يقتضي منع تناول الفقير له. فإنه لا يلزم من ذلك (٥٧/ ب) تمكين غيره [من مثله، ولا تمكينه في حالة أخرى] من مثل ما مكنه منه أولًا.
وهذا كلام فيه نظر، وذلك أن الفرع الذي فيه النزاع، تجذبه القاعدة الكلية، من جهة المعنى العام، وهو المقتضي لضد حكم الرخصة، ويجذبه محل الرخصة بمعناه المفهوم، فيمتاز هذا المعنى بخصوصية الاعتبار، ويمتاز المعنى المانع بعموم الجدوى، وكثرة [الفوائد]. فمن الناس من يرى الاعتبار على المعنى الخاص المستنبط من محل الرخصة، ومنهم من يرى عكسه. وسنزيده تقريرًا في بيان المعدول به عن القياس. هذا تمام ما أورده الإمام في تتبع [قول] المخالفين].
قال الإمام رحمه الله: (فإن قيل: فما الذي ترون؟ [قلنا: قد [وضح] بما قدمناه ما يعلل وما لا يعلل، ونحن نتخذ تلك الأصول معتبرًا في النفي والإثبات. فإن [جرت] مسالك التعليل في هذه الأبواب، أجريناها، وإن','3','431',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2063,'[انسدت]، حكمنا بنفي التعليل]، ولا يختص ذلك بهذه الأبواب). قال الشيخ: هذا الذي ذكره الإمام جملي، لا يتحصل منه مقصود في هذه الأبواب، والخصم يقوم إن القياس غير مستقيم في هذه القواعد. وإن سلم له ذلك، فقد حصل مقصوده. ولم يقم الإمام دليلًا على الجواز، ولا على الامتناع. ونحن بعون الله وتوفيقه نعتني ببيان ذلك وتحقيقه، فإنه محل غامض، وأمر ملتبس يشتد مسيس الحاجة إليه.
وليعلم الناظر في هذا الكتاب، أن المواضع التي حكم فيها القوم بمنع القياس، [انقسمت إلى جهات، منها: أن القياس] قد يمتنع في بعضها، من جهة الأصل الذي به الاعتبار، وقد يمتنع القياس في بعضها، باعتبار الفرع المعتبر بالأصل، وقد يمتنع القياس في بعضها، لأجل الحكم الذي يراد إثباته بالقياس، وقد يمتنع القياس، لخلل [الجامع] بين الأصل والفرع. ونحن نبين كل واحد [منها] بعون الله وتوفيقه.
أما ما ينشأ [من (٧٥/ ب) اعتبار] الحكم الثابت بالقياس، فصحة القياس في نصب الأسباب أسبابًا للأحكام، فإذا [أضيف] حكم [لسببٍ]، فيجوز عندنا أن يطلب علة السبب، فإذا وجدت في وصف آخر، جاز أن ينصب سببًا. ومنع أبو حنيفة ذلك، [وقال]: الحكم يتبع العلة دون','3','432',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2064,'حكم العلة. وقد قال أبو زيد: إنما يشرع القياس [لتعدية] [حكمٍ] ثبت بوصفه وشرطه، مناطًا بعلة مضبوطة، حتى لا ينصرف القياس إلا في توسيع مجاري الحكم، فأما [إذا] وقع النزاع في أصل الحكم، أو وصفه، أو شرطه، أو في أصل السبب، أو في وصفه، أو شرطه، لم يجز إثبات شيء من ذلك بالقياس. وعنى بذلك أنه لا يجوز أن يجعل اللواط سببًا، بالقياس على الزنا، ولا النبش سببًا للقطع، قياسًا على السرقة، ولا الأكل سببًا للكفارة، قياسًا على الوقاع. فلنتكلم على بيان شروط الحكم الثابت بالقياس.
وشروطه: أن يكون شرعيًا: زاد بعض الأصوليين: لم يتعبد فيه بالعلم. ونعني بكونه شرعيًا، احترازًا عن الحكم اللغوي، فإن الصحيح عندنا أن اللغة لا تثبت قياسًا، على ما تقدم القول فيه.
وهل يجوز إجراء القياس العقلي في العقليات؟ فيه خلاف سبق [أن] أشرنا إليه. وإنما المقصود ههنا القياس المنصوب من قبل الشرع، وهذا لا خلاف في امتناع جريانه في العقليات. أما الأسماء اللغوية الخارجة عن أسماء الفاعلين والمفعولين، فلا تثبت بالقياس أصلًا، فلا يعرف كون النبيذ خمرًا','3','433',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2065,'[بالقياس]، ولا كون النباش سارقًا، ولا كون اللائط زانيًا.
وكذلك لا يعرف كون [الغاصب للماشية] غاصبًا للنتاج بالقياس، بل بالبحث (٥٨/ أ) عن حقيقة الغصب وحده.
وكذلك النظر في كون المكره قاتلا لا يعرف بالقياس، وكذلك كون الشريك قاتلًا. فمن طلب تحقيق القتل في هذه الصورة بالقياس، فقد غلط.
نعم، يجوز أن يقال: حرمت الخمر لشدتها، فيحرم النبيذ وإن لم يكن خمرًا، بالقياس على الخمر. هذا هو حقيقة القياس، [وهو] راجع إلى إلحاق ما ليس بخمر في التحريم، وهو معقول القياس، إذ القياس تقرير المنصوص على قراره، وإلحاق غيره به، وإن لم يكن مسمى باسمه.
مسألة: قال بعض الأصوليين: ما تعبد فيه بالعلم، لم يجز إثباته بالقياس، كمن يريد أن يثبت خبر الواحد بالقياس على الشهادة. وهذا الذي قالوه ضعيف عندنا، بل ما تعبد فيه بالعلم، جاز أن يثبت بالقياس الذي يفيد العلم. وقد قسم المحققون القياس قسمين: (٧٦/ أ)
أحدهما- ما يحصل العلم، وقد قالوا في حكم ذلك: إنه يصح أن ينسخ به نص القرآن، وإن كان قاطعًا متواترًا. وقد تعبدنا بأن لا ينسخ النص المتواتر إلا بقاطع. ومثاله: لو ثبت بنص القرآن: [أن] من أعتق شركًا له [في أمة، لم','3','434',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2066,'يقوم عليه، ثم ورد بنص متواتر أن: (من أعتق شركًا له] في عبدٍ قوم عليه). ولم يظهر من قصد الشارع قطع الإلحاق، فإنه ينسخ النص الأول بالقياس على هذا المنصوص. [ولذلك] قال الفقهاء: من قضى على خلاف القياس الجلي نقض قضاؤه، وإن كان لا ينقض القضاء في مسائل الاجتهاد، دل ذلك على أنهم أرادوا بالجلي المقطوع به. فالصحيح أن القياس لا ينافي جريانه كون الحكم معلومًا، على ما قررناه.
مسألة: ينشأ النظر فيها من قولنا: إن الحكم الذي يصح إثباته بالقياس، يشترط [فيه] أن يكون شرعيًا: اختلفوا في النفي، هل يجوز أن يتلقى من القياس؟ هذا إنما يرد اعتراضًا، مع المصير إلى أن نفي الحكم الشرعي ليس حكمًا شرعيًا. أما من ذهب إلى أنه حكم شرعي، فلا إشكال عنده في صحة إثباته بالقياس.
والذي يصح عندنا في ذلك [أن نفي الحكم ليس بحكم على حال، وهذا عندي من أمحل المحال، أعني من يقول: ] إن نفي الحكم حكم، وكيف يعقل ذلك؟ وبيان فساده من وجهين:
أحدهما- أن النفي قط لا يكون إثباتًا، ولا يعقل ذلك، وتوهم من توهم أن نفي السكون حركة، غير صحيح، بل السكون ضد الحركة، فإذا انتفى','3','435',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2067,'السكون ثبتت الحركة. وكذلك القول في البياض والسواد، فليس في نفي السواد بياضًا، ولا [لونًا] آخر سواه.
الوجه الثاني- إن الذي نفى الحكم الشرعي، هل نفى [كل] الأحكام أو بعضها؟ فإن نفى بعضها، فالحكم الشرعي الذي لم ينتف ثابت، فلا يكون النفي [حكمًا]، بل الحكم هو الثابت. وإن نفى الأحكام الشرعية بجملتها، فكيف يتصور [مع] هذا أن يكون مثبتًا بعضها، [وهو] قد نفى [جميعها]؟
فإذا تقرر بهذا أن نفي الحكم ليس بحكم، فهل يجوز أن يعلم بالقياس؟ هذا مما اختلفوا فيه، فقال قائلون: يصح أن يتلقى من القياس.
وقال قائلون: لا يجوز أن يثبت بالقياس. وفرق مفرقون بين النفي المسبوق [بإثبات]، وبين النفي الأصلي، فقالوا: ما كان من النفي المسبوق بإثبات، فإنه يصح أن يثبت بقياس العلة، وما كان من النفي الأصلي، فلا يجوز إثباته بقياس العلة على حال، ويجوز إثباته بقياس الدلالة. والصحيح [عندنا] أنه لا فرق بين النفي المسبوق بإثبات، [وبين النفي الأصلي.','3','436',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2068,'وبيانه: (٧٦/ ب) هو أن النفي المسبوق بإثباتٍ]، يرجع إلى أن أدلة الإثبات متقاصرة عن الدلالة في حالةٍ من [الحالات]، فتبقى تلك الحالة على ما قبل ورود الشرع.
[ومثاله: أن الخمر كان تحريمها منتفيا قبل ورود الشرع]، فلما جاءت الشريعة، أثبتت التحريم مخصوصًا بحالة الاختيار، فبقي (٥٨/ ب) شربها [عند] الاضطرار على ما كان عليه قبل ورود الشرع. وهذا نفي مسبوق بإثبات. [فإذا] لم يصرح الشرع في حالة الاضطرار بجواز الشرب وإباحته قبل، وقع الاقتصار على نفي التحريم. وإذا تقيدت أدلة التحريم بحالة عدم الاضطرار، فليس لها تناول لهذه الحالة على حال. فلا حكم عند الاضطرار بحال.
فإذا ثبت ذلك، امتنع أن يتلقى هذا النفي من قياس العلة لأمرين:
أحدهما- أن النفي لا مقتضى له.
الثاني- أن العلة إنما كانت علة بنصب الشارع إياها، والمعلول لا يتقدم على العلة. وقد كان نفي الحكم معقولًا قبل ورود الشريعة. هذا بعد ما استقر عندنا أن الحكم: خطاب الشارع المتعلق بأفعال المكلفين. نعم، لا يمتنع أن يجري في ذلك قياس الدلالة، فإن الأدلة لا اقتضاء فيها، ولا يمتنع أن تتقدم مدلولاتها عليها.','3','437',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2069,'وبيان ذلك بالمثال: أن نتردد في تحريم أمرٍ من الأمور، ويلتبس علينا ثبوت تحريمه أو نفيه، ثم يرد [الشرع] معرفًا انتفاء التحريم على مثله، فتبين بعد شرعية القياس، وجريان المتماثلات في الشرع على قضية واحدة، انتفاء التحريم [عن الأمر] الذي ترددنا فيه. فقد وضح بهذا التقدير، صحة جريان قياس الدلالة في النفي دون قياس العلة.
ومما يتعلق [بالنظر] في الحكم الذي يصح إثباته بالقياس، انتصاب الأسباب للأحكام أسبابًا، فإنه يجوز عندنا أن يثبت سبب الحكم، قياسًا على سببٍ آخر. وذا هو المقصود من الكلام، فإذا حكم الله - عز وجل - برجم الزاني، جاز أن يطلب سبب ذلك، حتى يقف على سببه، وهو الزنا، فإذا ذكر أن الزنا علة الرجم، صح أن يعلله أيضا بعلة يعديها إلى غير الزنا، كما جاز أن يعلل [الحكم] الثابت على زيد، [ويعديه] إلى عمرو، عند فهم المعنى المقتضي للتعدية. وهذا القسم الأخير، هو قياس بالإجماع. أما الأول، ففيه النزاع.
وقد ذكرنا أن أبا زيد الدبوسي أنكر هذا النوع من التعليل، وقال: الحكم يتبع السبب دون حكمة السبب، وإنما الحكمة ثمرة، وليست بعلة، فلا يجوز أن يقال: جعل القتل سببًا للقصاص للزجر والردع، فينبغي أن يجب (٧٧/ أ) القصاص على شهود القصاص إذا رجعوا، [لمسيس] الحاجة إلى الزجر،','3','438',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2070,'وإن لم يتحقق القتل، إلى غير ذلك من تعليل الأسباب. وهذا الذي ذكره هؤلاء، يمكن أن ينزل على أحد ثلاثة [وجوه]:
إما أن يقولوا ذلك بناءً على توقيف ثابت، مانع من القياس في هذه [المحال]، فهذا إن ثبت فيه توقيف مانع، فلا قياس بحال. ولكنا نقول: لم يثبت ذلك متواترًا، ولا بنقل الآحاد، ويمتنع تقديره بالتحكم المحض.
وإما أن يقولوا: لم يثبت عندنا عموم شرعية القياس عند فهم المعاني. فهذا يجر أنواعًا من [الخبال]، ويقتضي منع القياس إلا في موضع الإجماع، وقل أن يصادف ذلك بحال. وقد قال أبو هاشم قريبًا من هذا، وقال: شرط الفرع الذي يتلقى حكمه من القياس أن يكون مما يثبت الحكم فيه على الجملة. وسنرد عليه بعد هذا، إن شاء الله.
فإن صار إلى ذلك صائر، فقد قدمنا في أول كتاب القياس ما هو المعتمد في إثباته، وبينا استرسال الصاحبة - رضي الله عنهم - في النظر [إلى المصالح]، استرسال من لا يرى لوجوه الرأي [منتهىً] وضبطًا، ثم كان اللاحقون يتبعون السابقين. فليراجع [الناظر] في هذا (٥٩/ أ) الكلام ما سبق من البرهان، ففيه كفاية وبلاغ.','3','439',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2071,'[وإما] أن يقول القوم: إن تعليل الأسباب تفوت معه حقيقة القياس، [إذا] شرط القياس على الأصول، تقديرها أصولًا، وفي تعليل الأسباب، ما يخرجها عن كونها أسبابًا.
وهذا- والعلم عند الله- هو [معتمد القوم]، ونحن نمثله ونزيده تقريرًا فنقول: إذا قلنا إن الزنا علة الرجم، واستنبطنا من إيلاج الفرج في الفرج المحرم قطعًا، إلى تمام القياس، واعتبرنا اللواط، فقد بان آخرًا أن الزنا لم يكن علة، وإنما العلة أمر أعم من الزنا، فقد علل الزنا في كونه سببًا بعلة أخرجت الزنا عن كونه سببًا، ويمتنع تعليل الأصول بما يخرجها عن كونها أصولًا. وكذلك يمتنع تعليل النصوص بعلة تزيل النصوصية.
هذا أعظم ما تمسك به القوم في منع تعليل الأسباب. وإذا كان كذلك، فخروج الأصل عن كونه أصلًا، يمنع معقول القياس، إذ الاعتبار بغير معتبر به محال. وقد تحير الأصحاب في الجواب عن هذا الكلام. ونحن بعون الله ندل أولًا على صحة ما نقول، ثم نحل الشبهة، مستعينين بالله وهو خير معين.
أما الدليل على أصل التعليل عند فهم معاني الأسباب، فهو الدليل الدال على أصل القياس، وذلك الدليل هو أنه لما علم من [الشريعة] إدارة الأحكام على المصالح، والإعراض (٧٧/ ب) عن صور المحال، وعرف ذلك باستقراء الشرع وردًا وصدرًا، وفهمت الإشارة إلى تعليل الأسباب، وأن الحكم لم يكن لأجل صورها، بل لما اشتملت عليه من المعاني، فلو وقع الاقتصار','3','440',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2072,'على صورها مع مشاركة غيرها لها في المعنى، [كان ذلك بعيدًا في اتباع المعنى]، وهو ضد فهم المعنى. فإن اعترفوا بإمكان فهم العلة، وإمكان تعديها، ثم توقفوا في الإلحاق، كان ذلك فاسدًا بما قررناه في إثبات أصل القياس، وثبت التحكم بالفرق بين حكم وحكم.
وقد تقرر أن الرجوع إلى المجمعين حتم، ولم يثبت في تخصيص المحال ضبط، وهذا بمثابة قول من يقول: يجري القياس في القصاص دون البيع، أو في الإجارة دون النكاح.
وقد قال بعضهم: [إنه] لا تلفي للأسباب علة مستقيمة، فيكون على هذا امتناع القياس لامتناع الجامع. ونحن نعترف بأنه لا يصح القياس دون تحقيق الجامع، ولكنا لسنا نسلم تعذر معرفة الجامع في الأسباب، فإن الطريق الذي يعرف أن الإسكار موجود في النبيذ وجوده في الخمر، يعرف أن أخذ مال الغير المحترم من [القبر]، كهو من البيت، على قطعٍ من غير فرقٍ. وكيف ينكر إمكان القياس وإلحاق النباش بالسارق، مع الاعتراف بكونه لا يسمى سارقًا، كإلحاق الأكل بالجماع في كفارة الإفطار، مع كونه لا يسمى جماعًا؟ وقد قال الأعرابي: (واقعت أهلي في نهار رمضان).
وقد اعتذروا عن هذا: بأنه ليس قياسًا، لأنا تعرفنا بالبحث أن الكفارة ليست كفارة الجماع، بل كفارة الإفطار. قلنا: كذلك نقول: ليس الحد حد الزنا، وإنما','3','441',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2073,'هو [حد] إيلاج الفرج في الفرج المحرم قطعًا، المشتهى طبعا، وكذلك ليس القطع قطع السرقة، بل قطع مال الغير من حرز مثله من غير [شبهة].
فإن قيل: إنما القياس أن يقال: علق الحكم في الأصل بعلة [كذا]، وهي موجودة في الفرع، وكذلك ينبغي أن يقال: علقت الكفارة بالوقاع لعلة [كذا]، وهي موجودة في الأكل، كما يقال أثبت التحريم في الخمر لعلة كذا، وهي موجودة في النبيذ، ونحن في الكفارة [نتبين] (٥٩/ ب) أن الكفارة لم تثبت [للجماع]، ولم تعلق [به]، فيعرف محل الحكم أولًا شرعًا أنه أين ورد؟ وكيف ورد؟ فليس هذا قياسا. فإن استمر مثل هذا في اللائط والنباش، فنحن لا ننازع فيه، فنقول: هذا بعينه جارٍ في النباش، فإنا نقول: تبينا من موارد الشرع أن القطع إنما كان في السرقة [لعلة] [أخذ] المال المحترم من حرز مثله، على جهة الخفية، وهو إلحاق (٧٨/ أ) لغير المنصوص بالمنصوص، من تعميم العلة التي هي مناط الحكم، فإن سمى مسم ذلك تنقيحًا، فلا ينازع في التلقيب.
[القسم الثاني]: أن نقول: إذا ساعد الخصم على ما قررناه، واتضح له','3','442',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2074,'ما [ذكرناه]، وهو صحة [فهم] معاني الأسباب، واتباع معنى السبب، [امتنع] الاقتصار على صورته عند فهم معناه. وهذا هو الذي يعبر عنه بالتنقيح. وما سلط على إزالة الظاهر إلا المعنى المفهوم، وهو كأنه معارض للظاهر ومزيل له، إذ الظاهر التعليل بنفس الجماع، فإذًا انضبط معنى الهتك، حتى عدل عن ظاهر الوقاع إلى معقول الإفطار. ولو لم ينضبط ذلك، لم يجز العدول عن الظاهر بحال. فكذلك يصح ضبط حكمة المعنى حتى يتعدى إلى محل غير مذكور أصلًا، اعتمادًا منا على فهم مقصود المعنى. وهذا هو الذي عبرنا عنه بتنقيح المناط، وقد سبق تمثيله وتحقيقه.
وكيف ينكر ذلك، وقد صار الجميع إلى فهم معنى قوله - صلى الله عليه وسلم -: (لا يقضي القاضي وهو غضبان)، أنه لم يرد به عين الغضب؟ وإنما أراد بذلك النهي عن القضاء في حالة دهش العقل وحيرته، حتى يلتحق به الجوع المفرط، والألم المبرح، وحتى يجوز القضاء مع الغضب اليسير. كل ذلك اعتمادًا منا على فهم أسرار العلل وبيان معانيها.
وكذلك نقول: الصبي يولى عليه، لمصلحة نفسه، لعجزه عن ضبط ماله وحفظه، [فالمجنون] بذلك أولى، فلينصب سببًا. هذا كلام معقول، لا يدفعه منصف، فكيف يقال: [إنه] لا يلفى للأسباب علة مستقيمة؟','3','443',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2075,'والدليل على صحة فهم المعنى، ووجوب اتباع حكم العلل، [أن الله تعالى] أوجب القصاص على القاتل بقوله: {ولكم في القصاص حيوة}. وقوله: {ومن قتل مظلومًا فقد جعلنا لوليه سلطانًا}. وأجمع المسلمون على التعليل بذلك، فإنه من أجلى المناسبات. ثم اجتمع جماعة على قتل واحد، قال الصحابة - رضي الله عنهم -: يقتلون. وهو إلحاق غير القاتل بالقاتل، نظرًا إلى فهم المعنى، وهو القصد إلى صيانة النفوس.
فهذا عين ما نحن فيه، فنقول: جعل القتل علة لكذا، وهو صيانة الدماء، وهو موجود في الشريك كالمنفرد، فليشرع القصاص على الشريك، وإن لم يكن قاتلا. ولكنهم قالوا: إنما وجب بالقصاص على القاتل، صيانة للدماء، فلو لم يقتص من الشريك، لبطلت الحكمة في ذلك، ولتطرق [الظلمة] إلى إتلاف الدماء على جهة الاشتراك. وفي ذلك إبطال [أصل] القاعدة. (٧٨/ ب) قال عمر - رضي الله عنه -: (لو تمالأ عليه أهل صنعاء لقتلهم به، حتى نزيد على ذلك).
فنقول: هذه الحكمة جريانها في الأطراف، كجريانها في النفوس، فنقطع المشتركين في الأطراف، قياسًا على المشتركين في النفوس، ونقول: ما صين','3','444',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2076,'بالقصاص [على] المنفرد، فليصن [بالقصاص] [على] المشتركين، إلحاقًا للأطراف بالنفوس.
وكذلك نقول: وجب القصاص [في القتل] بالمحدد، فنقيس عليه القتل بالمثقل، بناءً على فهم قاعدة الزجر. وهذا من أجلى أقيسة المعاني، وهو إلحاق غير الجارح [بالجارح]، اعتمادًا على فهم معنى القاعدة. فهذه تعديلات معقولة في هذه الأسباب، لا فرق بينها (٦٠/ أ) وبين تعليل تحريم الشرب بالشدة، وتعليل ولاية الصغر بالعجز، ومنع الحكم بالغضب.
وقد قال بعضهم: الزجر ثمرة وحكمة، وهي لا تحصل إلا بعد شرعية القصاص، فكيف يعلل القصاص بعلة لا تحصل إلا بعد القصاص؟ فنقول: هذا الكلام متجوز به، وإنما المراد: أن الحاجة إلى الصون هي علة شرعية القصاص، والحاجة إلى الصون متقدمة على شرعية القصاص. [وإذا] قال القائل: خرجت من البلد للقاء زيد، فهذا الكلام معقول، فظاهره أن لقاء زيد هو العلة الداعية إلى الخروج، ولا يلقاه إلا بعد الخروج، ولكن الحاجة إلى اللقاء هي الداعية إلى الخروج، أي احتياجي إلى لقاء زيد، هو الذي بعثني إلى الخروج. وكذلك احتياج الإنسان إلى أمور يستقبلها في أمر دنياه وآخرته،','3','445',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2077,'تحركه على تعاطي أمور يتوقع من حصولها حصول غرضه.
فإذا تقرر ما ذكرنا، [فالظاهر من مذهب أبي حنيفة أنه منع من إجراء القياس في الحدود والكفارات، بناء على ما ذكرناه] من امتناع إجراء القياس في الأسباب.
وما اعتذروا به من أن ذلك يرجع إلى تنقيح مناط الحكم دون تخريجه، فقد قال بعض الأصحاب: إن الإنصاف يقتضي مساعدتهم على ذلك. وزعم أن الجاري في تعليل الأسباب، تنقيح المناط دون تخريجه. وهذا هو الذي اختاره أبو حامد، وقال: لا وجه غيره، قال: هذا هو الحق. وذلك أنه تقرير عنده أن القياس الذي لا يرجع إلى تأويل اللفظ، هو إثبات حكمٍ في الفرع، لا يتعرض له اللفظ بحال، لا على ظهور، ولا على تأويل.
قال: ونحن إذا عللنا الزنا [بالإيلاج]، بان أن الزنا ليس مناطًا، بل المناط أمر أعم من [الزنا]، فكيف [يعلل] الزنا بعلة تخريجه عن كونه علة؟ بل سنبين آخرًا أن الزنا ليس مناطًا، فتعليل الأسباب يخرجها عن [كونها] (٧٩/ أ) أصولًا. فأما إذا ألحقنا الجنون بالصبا، بان لنا أن الصبا لم يكن مناطًا، بل أمر أعم منه.
[وكذلك إذا عللنا الغضب بأنه مدهش للعقل، بان لنا أن نغضب لم يكن مناطًا، بل أمر أعم منه]، وهو الدهش المانع من الفكر واستيفاء النظر، وعند','3','446',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2078,'ذلك يظهر الفرق للفطن بين تعليل الحكم وتعليل السبب، فإن تعليل الحكم يقتضي تعدية الحكم من محله، وتقريره في محله. فإنا نقول: حرم الشرع شرب الخمر، والخمر محل الحكم، ولكن يطلب مناط الحكم وعلته، فإذا برزت لنا الشدة، عديناها إلى النبيذ، فضممنا النبيذ إلى الخمر في التحريم، ولم يغير في الخمر [شيئًا].
أما ههنا إذا قلنا: علق الشرع الرجم بالزنا لعلة كذا، فيلحق به غير الزنا، ناقض آخر الكلام أوله، لأن الزنا إن كان مناطا [للحكم] من حيث إنه زنا، فإذا ألحقنا به شيئا ليس بزنا، فقد أخرجنا الزنا عن كونه مناطا، فكيف يعلل كونه مناطا بما يخرجه عن كونه مناطًا؟ والتعليل تقرير لا تغيير، ومن ضرورة تعليل الأسباب تغييرها، فإنك إذا اعترفت بكونه سببًا، ثم أثبت ذلك الحكم بعينه عند فقدان ذلك السبب، فقد نقضت قولك الأول: إنه سبب.
فإذا ألحقنا الأكل بالوقاع، بان لنا بالآخرة أن الوقاع لم يكن هو السبب، بل معنى أعم منه، وهو الإفطار، وإنما يكون هذا تعليلا، لو بقي الجماع مناطًا، وانضم إليه مناط آخر شاركه في العلة، كما بقي الخمر محلًا للتحريم، وانضم إليه محل آخر، وهو النبيذ، فلم يخرج المحل الذي طلبنا علة حكمه عن كونه مناطًا، فكيف يحلل الجماع بما يخرجه عن كونه مناطا؟ بل وجب إضافة الحكم إلى معنى، حتى يصير الجماع حشوًا زائدًا. (٦٠/ ب) وكذلك يصير وصف الزنا حشوًا، وهو إيلاج فرج في فرج.
قال أبو حامد: فإذًا مهما فسر مذهبهم على هذا الوجه، اقتضى الإنصاف المساعدة عليه. هذا تمام كلامه. وهو على التحقيق اعتراف بامتناع إجراء القياس في الأسباب، لا لخصوصٍ في التعبد، ولا لتعذر فهم المعنى، ولكن لاستحالة وجدان الأصل عند تعليل السبب، فيفوت القياس، لفوات بعض','3','447',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2079,'أركانه، ولا يبقى للقياس حقيقة على هذا الوجه.
و[نحن] نقول: هذا القول غير صحيح عندنا، بل الصحيح إجراء القياس على حقيقته في الأسباب، ولا فرق في تصور القياس بين تعليل الأسباب وتعليل الأحكام.
وبيانه: هو أنه [هل] حرمت الخمر باعتبار خصوصية وصفها، وهي الخمرية، حتى لا يتعدى الحكم إلى النبيذ [بحال]، أو حرمت من جهة كونها مزيلة للعقل، وهو الوصف الأعم؟ فإنه (٧٩/ ب) إنما كانت أصلًا، باعتبار حكم الشرع، وقد بان لنا أنه حكم فيها من جهة اشتدادها وإسكارها.
فكذلك إذا قلنا: جعل الزنا علة للرجم، فيقال: هل كان علة من جهة كونه زنا، أو من جهة أخرى هي أعم من هذا؟ أو لم تكن علة أصلًا؟ والباطل أن نقول: ليس بعلة من وجه من الوجوه بأسرها، فهذا هو الذي يناقض قولنا: إنه علة للرجم، وهو نظير ما لو استنبط معنىً يخرج الخمر عن كونها محرمة مطلقًا، أما [استنباط] يبين أنها محرمة من جهة ما، فليس يناقض ذلك كونها أصلًا، فكذلك الباطل ألا يجعل الزنا علة من وجه من الوجوه، أما إذا جعل علة من بعض الجهات، فلم يخرج عن كونه علة مطلقًا.
[فهكذا] ينبغي أن يفهم تعليل الأسباب، ولا فرق بينه وبين تعليل الأحكام [في الأصول] الثابتة، على ما قررناه قبل ذلك في تحقيق المثال. فقد بان بما قررناه صحة تعليل الأسباب والأحكام، والله الموفق للصواب.','3','448',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2080,'وهذه المسألة من أعظم مسائل الأصول، فلقد زل فيها الجماهير، وأثبتوا القياس في الأسباب على وجه يحيل معقول القياس عند ذوي الألباب. هذا تمام الكلام على [ما] منع القوم إجراء القياس [فيه]، بناء على أحد الأركان، وهو ركن الأحكام.
وأما ما منعوا القياس فيه بناء على الركن الآخر، وهو ركن الأصل، فهو منعهم من إجراء القياس على المعدول به عن القياس. وقد كنا قدمنا ذكر شروط الأصل المقيس عليه، وبقي علينا من شروطه الكلام على هذا النوع، وهو أن يكون الأصل الثابت حكمه غير معدول به عن القياس.
ولابد من النظر في مدلول هذا اللفظ عند أهل الأصول، فإن هذه اللفظة تطلق عند الفقهاء على أربعة أمور، وقد اشتهر من كلامهم أن المعدول به عن القياس لا يقاس عليه غيره. وهذا مما أطلق، ويحتاج إلى تفصيل، فنقول:
يطلق اسم الخارج عن القياس على أربعة أقسام: يطلق على ما استثنى من قاعدة عامة، وعلى ما استفتح [ابتداء] قاعدة منفردة، وكل واحد من المستثنى والمستفتح ينقسم إلى ما يعقل معناه، وإلى ما لا يعقل معناه، فهي أربعة أقسام:','3','449',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2081,'<span data-type="title" id=toc-371>القسم الأول: </span>ما استثنى من قاعدة عامة، وثبت اختصاص المستثنى بحكمه، فلا يقاس عليه غيره، [إذ كيف يقاس عليه غيره] في تعدية الحكم، وفهم من الشرع الاختصاص بالمحل المستثنى؟ وفي القياس إبطال الاختصاص المفهوم من النص. وهذا ينقسم إلى ما يعقل معناه، وإلى ما لا يعقل معناه، والقياس فيهما جميعا [ممنوع]، (٨٠/ أ) لما علم من قصد الاختصاص والاقتصار.
فأما ما لا يعقل (٦١/ أ) معناه: فكاختصاص خزيمة بقبول شهادته وحده. وأما ما يعقل معناه، فكجواز التضحية بعناقٍ لأبي بردة [بن دينار]، نظرًا إلى فقره، ولا يلحق بذلك غيره، للنص الثابت فيه، وهو قوله: (تجزئك ولا تجزئ عن أحدٍ بعدك).
وثبوت الاختصاص قد يعلم وقد يظن، فالعموم [كما] ذكرناه، والمظنون كما في حق المحرم: (لا تخمروا رأسه، ولا تقربوه طيبا، فإنه يبعث ملبيا). وكذلك [قال] في قتلى أحد: (زملوهم بكلومهم ودمائهم، فإنهم يحشرون أوداجهم تشخب دما).','3','450',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2082,'وقد اختلف العلماء في الصورتين جميعًا، فمن ذاهبٍ إلى [حمل] الخبرين على الخاصية، ورأى قاعدة غسل الموتى مطردة في الشهداء، والتطييب والتخمير مطردًا في المحرمين. وقال قائلون: [بطرد] القياس في الموضعين جميعًا، ولا تثبت الخاصية بحال. وفرق مالك رحمه الله، فرأى [أن] القياس في مسألة الشهداء، ولم ير الإلحاق في مسألة المحرم. وقد كنا قدمنا ذكر ذلك فيما مضى.
والبحث فيه نظر يتعلق بفن الفقه. وإنما قصدنا ههنا التمثيل للتقريب والتسهيل.
[ولما] قال للأعرابي الذي واقع [أهله] في نهار شهر رمضان: (تصدق [به] على أهل بيتك). ولم يقر الكفارة في ذمته عند عجزه، وجعل الشبق عذرًا عن الصوم، قال أكثر العلماء: هو خاصية. وقال بعض أصحاب الشافعي: يلتحق به من يساويه في الشبق والعجز. ومن جعله خاصية، استند فيه إلى أنه لو فتح هذا الباب، للزم مثله في كفارة الظهار وسائر الكفارات. والقرآن دل على أنهم لا ينفكون عن [الواجب]، وإن اختلفت','3','451',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2083,'أحوالهم في العجز، فحمله على الخاصية أهون من هدم هذه القاعدة المعلومة. هذا إن ثبت أنه لم [يقر] الكفارة في ذمته، وفيه احتمال. واستدلال من استدل على ذلك بأنه مكنه من الكفارة غير قوي، لاحتمال أن يكون ذلك لشدة الحاجة، فرأى إحياءه، وإحياء أهله أولى.
فإن قيل: [فهذا] تأخير البيان عن وقت الحاجة. قلنا: ليس كذلك، فإنه قد بين له الواجب، ولكنه اعتذر بالعجز عنه، فيبقى الأمر عليه إلى تحقيق الإمكان. وإنما أردنا بهذا أن نبين مواضع اجتهاد العلماء، وتطرق فنون الظنون إلى فهم الخاصية، وأن ذلك مكتفى به في حق العلماء، كسائر المجتهدات.

<span data-type="title" id=toc-372>القسم الثاني: [</span>ما] استثني من قاعدة عامة، وتطرق إلى المستثنى نوع معنى، ولم يدل دليل على (٨٠/ ب) وجوب القصر على موضع الاستثناء. وهذا عندنا ينقسم قسمين:
أحدهما- أن يكون الفرع الذي فيه النزاع، نسبته إلى النوع المقتطع نسبة العلم، وبقاؤه تحت القاعدة مظنون. فهذا يجب إلحاقه بموضع الاستثناء، إذ لا يعارض ظن علمًا. وإلحاقه بالأصل معلوم، وبقاؤه تحت مقتضى القاعدة- لو جرد النظر إليه- مظنون. وهذا مما لا يتجه فيه خلاف.
مثاله: ما استثني من بيع الرطب بالتمر من العرايا، فإنا [نلحق] قطعا عرية العنب بعرية الرطب، وإن كان النص واردًا في عرية التمر، للقطع بالمساواة.','3','452',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2084,'أما إذا لم يقع القطع بذلك، ولكن ظننا، فإنا نكتفي أيضا بذلك [في] الإلحاق، إذا ثبت أن معنى القاعدة منقوض، [و] تعارض [مع] الاستثناء، وتبين رجحانه عليه، بورود الحكم على وفقه. فيقع هذا في باب تعارض الخاص والعام. والخاص مقدم عند ذوي التحقيق. وقد أبدى بعض الأصوليين في ذلك خلافًا، وقد ذكرناه في باب تخصيص العموم، ومما نحن فيه من ذلك النوع.
أما إذا لم يلح في المستثنى معنىً، لا قريبٍ (٦١/ ب) ولا بعيدٍ، فإنا لا نلحقه به بحال. فإنا لو ألحقنا به شيئاً، من غير تقدير جامع مناسب، بل اعتمادًا على مجرد الاستثناء في الصورة، والمقتطع على صورة، بقية ما اندرج تحت القاعدة، أفضى ذلك إلى رفع جميع القاعدة. ونحن نعلم أن ذلك لم يرد مورد النسخ للقاعدة، بل مورد الاستثناء مع بقاء القاعدة على حقيقتها. وقد اختلف العلماء في مسائل مستثناة، هل يصح القياس عليها أم لا؟ لترددهم في فهم المعنى أو عدمه.
منها: الحكم ببقاء صوم الناسي عند بعض العلماء في الفرض، على خلاف قياس جميع المأمورات. قال أبو حنيفة: لا يقاس عليه كلام الناسي في الصلاة، ولا أكل المخطئ والمكره، لكنه قال: جماع الناسي في معناه، [لأن] الإفطار باب واحد. والشافعي قال: الصوم من جملة المأمورات،','3','453',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2085,'[معناه]: إذا افتقر إلى النية والتحق بأركان العبادات، وهو من جملة المنهيات في نفسه وحقيقته، إذ ليس فيه إلا ترك يتصور من النائم جميع النهار، فأسقط الشرع عهدة الناسي ترجيحًا، لنزوعه إلى المنهيات، فنقيس عليه كلام الناسي.
وأما مالك [رحمه الله]، فإنه التفت إلى اشتراط النية فيه، وألحقه بالعبادات، وحكم ببطلان الصوم، حتى فاتت حقيقة الصوم، إلا في صوم التطوع، تخفيفًا على المكلفين في ذلك. [وقد] اختصت التطوعات (٨١/ أ) بأنواعٍ من التخفيف، كجواز صلاة النافلة على الراحلة في السفر الطويل، وإن كن ذلك غير مفتقر في الفرائض. وإذا ثبتت المسامحة في النفل، امتنع إلحاق الفرض به.

<span data-type="title" id=toc-373>القسم الثالث: </span>القاعدة المستفتحة [المستقبلة]، حتى لا يعقل معناها فلا يقاس عليها غيرها، لعدم العلة الجامعة، فيسمى خارجا عن القياس على وجه تجوز، إذ المفهوم من الخروج عن القياس، أن يكون القياس يقتضي نقيض الحكم الثابت، فيعدل بالمسألة عن حكم القياس، ولكن لما لم يجر هذا على مقتضى القياس، سمي خارجًا عن القياس لذلك.
ومثاله: المقدرات، كأعداد الركعات، ونصب الزكوات، ومقادير الحدود والكفارات، وجميع التحكمات المبتدأة، التي لا ينقدح فيها معنىً، فلا يقاس عليها غيرها، لأنه لا يعقل معناها.','3','454',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2086,'<span data-type="title" id=toc-374>القسم الرابع: </span>القواعد المبتدأة العديمة النظير، لا يقاس عليها، مع أنه يعقل معناها، لأنه لا يجود ما يشابهها، خارج عن [النص] والإجماع، والمانع من القياس، [عدم] العلة [في غير] المنصوص، فكأنه معلل بعلة قاصرة.
ومثاله: رخص السفر، والمسح على الخفين، ورخصة المضطر في أكل الميتة، وضرب الدية على العاقلة، وتعلق الأرش برقبة العبد، وإيجاب غرة الجنين، والشفعة في العقار، وخاصية الإجارة والنكاح، وحكم اللعان، والقسامة ونظائرها. فإن هذه القواعد متباينة المآخذ، فلا يجوز أن يقال بعضها خارج عن قياس البعض، بل لكل واحدة مزية تنفرد بها، لا يوجد لها نظير. فليس البعض بأن يوضع أصلًا، ويجعل الآخر خارجًا عن قياسه، بأولى من عكسه. فلا ينظر فيه إلى كثرة العدد وقلته.
وتحقيقه أنا [نعلم] إنما جوز المسح على الخفين، للحاجة إلى اللبس وعسر النزع، ومسيس الحاجة إلى استصحابه، فلا نقيس عليه العمامة والقفازين، وما لا يستر جميع القدم، لا لأنه خارج عن القياس، ولكن لأنه لا يوجد ما يساويه في الحاجة وعسر النزع (٦٢/ أ)، وعموم الوقوع. وكذلك رخص السفر، فإنا لا نشك في ثبوتها للمشقة، فلا نقيس عليها مشقة أخرى، لأنه لا يشاركها غيرها في جملة معانيها ومصالحها، لأن المرض لا يحوج إلى القصر، بل إلى الجمع، وإسقاط المشقة من القيام وغيره إن مست الضرورة','3','455',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2087,'إليه. ولما ساوى المريض والمسافر في حاجة الفطر، سوى الشرع بينهما فيه.
وكذلك قولهم: أكل الميتة رخصة خارج عن القياس، كلام مؤول، [فإنه] (٨١/ ب) أريد به أنه لا قيقاس عليه غير المضطر، فإنه ليس في معناه، وإلا فنقيس الخمر على الميتة، والمكره على المضطر، فهو منقاس.
وكذلك بداءة الشرع بأيمان المدعين في القسامة، لشرف أمر الدم، ولخاصيةٍ لا يوجد مثلها في غيره، ولأنه عديم النظر، لا يقاس عليه.
وكذلك ضرب الدية على العاقلة، كان ذلك في الجاهلية فأقره الإسلام. ولو لم يعقل معناه، لم تتفق الجاهلية عليه قبل الإسلام، والسر فيه كثرة وقوع الخطأ، وشدة الحاجة إلى ممارسة السلاح، ولا نظير له في غير الدية. وهذا مما يكثر. [فبهذا] يعرف أن قول من قال: إن باب الإجارة خارج عن قياس البيع، غلط. وكذلك القول بأن النكاح خارج عن القياس. كقول من يقول: تأبد البيع والنكاح خارج عن قياس تأقيت الإجارة. وكذلك المساقاة خارجة عن قياس الإجارة والبيع. وكذلك أمر القراض. والجعل خارج عن تأقيت المساقاة. فإذًا هذه الأقسام الأربعة، لابد من الإحاطة بها، ليحصل الوقوف على سر هذه القاعدة. وسيأتي لهذا مزيد تقرير [بعد هذا]، إن شاء الله [- عز وجل -]. فهذا ما منع القوم القياس فيه، لأجل [الأصل] الذي يقاس عليه.','3','456',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2088,'وأما [ما] منع القياس فيه لأجل الفرع، فكل موضع لم يثبت مناط الأصل في الفرع، فلا يصح قياسه على الأصل. وذلك يؤول إلى أمرين: أحدهما- ينشأ من عدم فهم معنى الأصل.
والثاني- يرجع إلى فقد معنى الأصل من الفرع. وفي الوجهين يمتنع أن يجعل فرعًا، لعدم ما يربط بينه وبين الأصل. والرخص والتقديرات يمتنع القياس فيها لذلك.

أما الرخص، فقد قلنا: إن الأغلب فيها عدم مصادفة ما يساويها. وقد قدمنا الكلام على ذلك. وأما المقدرات، فلعدم الوقوف على سبب التقدير. وإذا نشأ النظر في هذا من<span data-type="title" id=toc-375> شروط الفرع</span>، فلابد من استيعاب النظر [في ذلك]. وللفرع خمسة شروط:
الأول: منها: أن يكون مناط الحكم في الأصل موجودًا في [الفرع]. فإن تعدي الحكم فرع تعدي العلة. فإن كان وجودها في الفرع مقطوعًا به، فلا شك في صحة الإلحاق. وإن كان مظنونا، فقد اختلفوا فيه، فذهب ذاهبون إلى أنه لا يجوز الإلحاق إلا إذا قطع بالوجود، ولا يكتفى بغالب الظن في تحقيق المناط. وقال قائلون: إذا ظن وجود العلة في الفرع، اكتفي بذلك. والفريقان متفقان على أنه لا يشترط القطع بكون الوصف علة، بل يكتفى بظن ذلك، إذ ذلك يرجع إلى إثبات الأحكام. (٨٢/ أ) واكتفى الشرع في كثير من الأحكام بغلبات الظنون. أما ما يرجع إلى تحقيق مناط المعنى في الفرع، فهو نظر في أمرٍ وجودي، فالعلم فيه متأتٍ، ولابد عندنا في ذلك من التفصيل.','3','457',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2089,'فنقول: إن كانت العلة حكمًا شرعيًا، فلا خفاء بعدم اشتراط العلم في ذلك. ومثاله: إذا قضينا بأن النجاسة علة منع البيع، ثم غلب على ظننا أن أزبال الدواب نجسة، فلابد (٦٢/ ب) من القضاء بمنع البيع، كما أنه لابد من القضاء بمنع الاستصحاب في الصلاة، فإن النجاسة يمتنع استصحابها في الصلاة، فإذًا ظن التنجيس يتنزل منزلة علمه، ولا ذاهب من الأمة إلى جواز الاستصحاب مع ظن التنجيس. وهذا عندنا مما لا يتجه فيه خلاف.
وإن كانت العلة أمرًا وجوديًا، ككون الزعفران غير مطعوم، حتى يجوز بيعه قبل قبضه، فإن العلة عندنا في تحريم المبيع قبل القبض، كون المبيع طعامًا غير مقبوض لمشتريه. فإذا غلب على ظننا أن الزعفران غير مطعوم، لم يمتنع بيعه قبل قبضه. وكذلك إذا جعل الشافعي الطعم علة تحريم التفاضل، ثم ظن أن الزعفران مطعوم، فلا سبيل إلى تجويز التفاضل فيه.
والسبب في ذلك، أنه إذا ظن وجود المناط، [ظن] ثبوت الحكم المرتبط به، لما تقرر [ما] بين الحكم والمناط من الارتباط. وإذا ظن وجود أحد المرتبطين، ظن وجود الآخر، فكيف يجوز الإقدام على التفاضل مع ظن التحريم؟ وقد قررنا أن كثيرًا من أحكام الفرع لا يشترط فيها العلم بحال وكذلك إذا تغير الماء بالنجاسة، ثم زال التغير بطول المكث وهبوب الريح، طهر الماء، لتحقق الزوال، وإن تغير بالزعفران، لم يطهر. وكأنه في معنى الساتر. فإذا تغير بالتراب وحصل التردد، هل هو مزيل كهبوب الريح، أو ساتر كالزعفران؟ [فمن] غلب على ظنه أنه ساتر، فقد ظن بقاء النجاسة، فكيف يجوز الإقدام على الاستعمال؟ ومن غلب على ظنه أنه مزيل، فلا نجاسة.','3','458',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2090,'وكيف يصح منه منع الاستعمال من تحقق الزوال؟
الثاني: أن لا يتقدم الفرع في الثبوت على الأصل. إذ كيف يتفق أن يكون حكمه متعديًا إليه من أصل لم يثبت الحكم فيه إلى الآن؟ فمن المحال أن يعدى إلى الفرع حكم أصلٍ لم يثبت. نعم، يجوز أن يكون الحكم ثابتًا بطريق سوى القياس، ثم يرد الشرع بإثبات الحكم في محل آخر، مماثل لذلك المحل، فلا يجوز أن يكون ذلك السابق فرعًا عند من علم النص فيه. نعم، قد يقال لمن لم (٨٢/ ب) يحط بمأخذ الحكم الأول، والتبس عليه أنه فرع للأصل الوارد بعده، على معنى أنه لم يثبت الحكم فيه عنده، إلا بعد ثبوت الحكم المتأخر. فهذا له وجه ما، والأدلة يجوز أن تتأخر. هذا وجه صحة الإطلاق. [وأما] من كان دليل الحكم ثابتًا عنده قبل النظر في الثاني، فلا وجه لإطلاق الفرع على الأول بحال.
الثالث: أن لا يتغير الحكم بالتعليل، بل يعدى إلى الفرع عين حكم الأصل، على وصفه وكيفيته، فلا زيادة ولا نقصان. فإن لم يكن كذلك، [بطل] معقول الاعتبار، وكان حكم الفرع لا شاهد له من جهة الأصل، فيقع في أبواب الاستدلال المرسل. فهذا واضح من حيث الجملة، ولكن ينشأ منه الالتباس في بعض الأمورـ فإنا نقيس المطلقة في مرض موت الزوج في أخذها الميراث على القاتل، إذ قتل وارثه، في حرمان الميراث. وظاهر هذا أن الثابت','3','459',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2091,'في الفرع ضد حكم الأصل، إذ الحكم الثابت في الأصل منع الميراث، والذي ثبت في الفرع استحقاقه، ولكن عدل في القياس عن خصوصية حكم الأصل إلى جهة عامة، وهي المقابلة بنقيض المقصود. وهذه [الجهة] تستوي [فيها] المطلقة والقاتل، إذ غرض المطلق حرمان الميراث، وغرض القاتل استحقاقه، فكل منهما عورض بنقيض [مقصوده]، ولم يعد إلى الفرع إلا ما ثبت في الأصل من الجهة التي ذكرناها. وإلا فمن المحال أن يقال: حرم (٦٣/ أ) كذا قياسًا على محل ثبت فيه الحل. هذا واضح.
وقد تنازع الأصوليون في صحة قياسٍ نذكره، ونتكلم عليه، وهو أن أصحابنا قالوا: لا يكون عوض السلم إلا نقدًا، لكن جوزوا التأخير اليسير بشرطٍ، كاليوم واليومين، وجوزوا التأخير الكثير بغير شرط. ومنع الشافعي ذلك، وقال: لابد من القبض في المجلس، وجوز السلم الحال. والصحيح من مذهب مالك رحمه [الله] منع السلم الحال. وعلل ذلك الأصحاب بأن قالوا: بلغ برأس المال أقصى مراتب الأعيان، فليبلغ بالمسلم فيه أقصى مراتب الديون، قياسًا لأحد العوضين على الآخر. وهذا إنما يستقيم على قول من لم يجوز في رأس المال [التأخير] اليسير.','3','460',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2092,'وقد ذهب الأصوليون إلى فساد هذا القياس، ورأوا أنه ليس على شكل الأقيسة، إذ حكم الأصل وجوب التقديم، وحكم الفرع وجوب التأخير، وهذا ظاهر التناقض. [ولكن] طريق التقريب في ذلك أن يقال: [أحد] العوضين (٨٣/ أ) [يوجب] أن يشرع على وجه يتوقف عليه مصلحة العقد، قياسًا على العوض الآخر.
وتقريره [هو]: أن السلم شرع لدفع الحاجة عن المسلم إليه، فإنه بيع محاويج، وإنما تتم المصلحة فيه، بأن يتعجل قبل العوض، إذ به يحصل مقصوده من الثمن، ويتأخر قبض المسلم فيه. فإنه لو كان حاضرًا عنده، كان موسرًا به، فلم تدعه ضرورة إلى المعاملة على ذمته.
وفيه أيضًا وجه آخر، وهو أن مقصود العقد لا يحصل إلا بالاسترخاص، إذ المسلم لو كان الذي يسلم [فيه] بسعره، كسعر المقبوض، لم يقدم عليه. وإنما يقدم عليه عند رجاء الاسترخاص، وذلك يستدعي ضرب أمدٍ تتغير في مثله الأسواق. هذا هو الأمر الذي اعتمد عليه مالك رحمه الله. ولذلك قال: إن الأجل: نحو الخمسة عشر يومًا في البلد الواحد، وأما إذا أسلم إليه في بلد [على] أن يقبض المسلم إليه في [غير بلده]، فلا يفتقر مع ذلك','3','461',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2093,'لضرب أجل، [ويتنزل] اختلاف البلدان، منزلة اختلاف الأجل. فهذا وجه تحرير هذا الدليل.
الرابع: أن يكون الفرع مما ثبت جملة حكمه بالنص. وهذا ذكره أبو هامش، وقال: لولا أن الشرع ورد بميراث الجد جملة، لما نظر الصحابة في توريث الجد مع الإخوة. وهذا فاسد، لأنهم قاسوا [قوله]: (أنت علي حرام) على الظهار، والطلاق واليمين، ولم يكن قد ورد فيه حكم، لا على العموم، ولا على الخصوص، بل الحكم [إذا] ثبت في الأصل لعلة، [تعدى] بتعدي العلة [كيفما كان].
الخامس: أن لا يكون الفرع منصوصًا عليه، وإنما يطلب الحكم بقياس على أصلٍ تأخر، إذا كان مسكوتًا عنه. فإن قيل: [فلم] قستم الظهار على القتل في اشتراط أيمان الرقبة، والظهار أيضا منصوص عليه، واسم الرقبة يشمل الكافرة؟ قلنا: اسم الرقبة ليس نصًا، بل مطلق يتناول الرقاب على البدل، تناول ظهور لا تنصيصٍ. فلما ورد الشرع باشتراط الأيمان في كفارة القتل،','3','462',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2094,'أرشدنا ذلك إلى اشتراطه في كفارة الظهار، فلم يطلب حكم الرقبة بالقياس في الظهار، إلا بعد ما استقر عندنا أن النص لا يتضمن جواز الكفارة، بل يكون اشتراط الأيمان في كفارة القتل، يغلب على الظن اشتراطه في كفارة الظهار. [هذا] ما يتعلق بالكلام على الفرع وشروطه.
وأما ما منع من القياس لأجل العلة، فهو ما لا تصادف المعاني فيه مستقيمة على شروطها. ومن هذا عندهم، منع القياس في الأسباب. قال أبو زيد: (٨٣/ ب) لا يلفى [للأسباب] (٦٣/ ب) علة مستقيمة. وإنما يتم النظر في ذلك ببيان حقيقة العلة، وشرط دلالتها، ومعرفة كيفية إضافة الحكم إليها، وذلك يطول، وله موضع من الكتاب غير هذا، فلنؤخره إلى مكانه، وذلك عند الكلام [على] النقض وعدم التأثير، فهناك نستوعب الكلام، إن شاء الله تعالى.
قال الإمام: (ومما نختم القول به: أن التعليل قد يمتنع لنص الشارع) إلى قوله (فهذه جمل كافية فيما يعلل [وما] لا يعلل). قال الشيخ أيده الله: ما','3','463',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2095,'ذكره الإمام في هذا المكان صحيح، وذلك أن القياس إنما يطلب ليرشدنا إلى حكم الشرع، وليس القياس في معنى من يستقل بإثبات حكمٍ. ولو قدرنا ذلك، لم يكن ما قال به المعلل مستندًا إلى الشريعة بحال. فإذا كان المقصود التوصل إلى معرفة حكم الشرع، فكيف يصح أن يقع الإلحاق مع منع النصوص من ذلك؟ فلا وجه لاستعمال القياس على هذا [التقدير].
ولكن يبقى ههنا [أمر] تتعين الإحاطة به، وهو أن قائلًا لو قال: إذا صح من الشريعة الفرق بين المتماثلات، ولا مستند للقياس إلا استقراء الشريعة في التسوية بين أحكام المتماثلات، فكيف يصح القياس مع ثبوت ذلك؟ وقد كنا قدمنا أن الشريعة، وإن خرجت عنها مسألة عن القياس، فتلك [نادرة]، لا تمنع القضاء بغالب الأمور. ونقلنا إجماع الصحابة على ذلك. وهذا السؤال جنوح إلى منع القياس، فلا يلتفت إليه.
فإن قيل: [إن] لم يمنع ذلك من الاعتبار ظنًا، فهو مانع [من القطع','3','464',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2096,'بالحكم، فإن الاحتمال، وإن ضعف جدًا، فهو مانع] من العلم قطعًا، فينبغي أن لا يتلقى من القياس حكم معلوم. [وهذا السؤال] مخيل. ولكنا نقول: قد نعلم بتصفح الشريعة وردًا وصدرًا، الإعراض عن التعبد في بعض المواضع، والالتفات إلى اطراد الحكم عند المساواة. وهذا كما فهمه العلماء أجمعون من قوله تعالى: {[فلا] تقل لهما أف}. أن هذا غير مختص بهذه الحالة، بل جميع أنواع الأذية مساوية [له] قطعًا. [وكذلك قوله تعالى: {إن الذين يأكلون أموال اليتامى ظلمًا إنما يأكلون}. فإن الإتلاف لغير جهة الأكل، ملحق به قطعًا].
فإذا حصل العلم مرتبًا على هذه الجهات، اندفع الإجمال على كل حال. وأيضا، فإنه لما قرر الرسول - صلى الله عليه وسلم - بما فهم عنه بطريق الاعتبار، وصح منه اقتطاع بعض الأمثال، فإنه إذا قصد القصر، أرشد إلى الاختصاص، لحصول التعبد بالتعميم عند فهم التعليل، وإذا تقرر (٨٤/ أ) ذلك، فالطريق إلى إزالة الإشكال [بقطع] بعض الأمثال، الإرشاد إلى ذلك بصريح المقال، كما قال لأبي بردة','3','465',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2097,'بن نيار: (تجزئك ولا تجزئ عن أحدٍ بعدك). أو يفهم ذلك عنه بقرينة الحال، فتقوم مقام صريح المقال. والله المستعان.
قال الإمام رحمه الله: (ونحن نختتمها بكلام نفيس قائلين: رب شيء [يجتمع] فيه جريان القياس) إلى قوله (وما يتعلق بالنقل وعدم النقل). قال الشيخ: ما ذكره الإمام من اعتماد كون الأصل معللًا على فهم علته، كلام صحيح، وقد تقدم تقريره. وحاصل القول في ذلك، تعميم النظر إلى المصالح عند ظهورها، وانتفاء التوقيف الدال على التعبد. يتبين ذلك من استقراء الشريعة في تقرير قاعدة القياس، وإناطة الأحكام بالمصالح. عرف ذلك من عمل الصحابة [- رضي الله عنهم -]، وسياق الآيات، وثبوت الأخبار، والإماء من الشارع [إلى] التعليل، وجهات المصالح. (٦٤/ أ) وإذا تقرر ذلك، فرب أصلٍ يجتمع فيه جريان القياس وامتناعه، وذلك بحسب ظهور المعنى في بعض الأطراف وانحسامه.','3','466',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2098,'وقد ضرب الإمام في ذلك الكتابة مثلًا، وبين جهات المعنى المعقول والتعبد المحض، ولكن هذا يقوى على أصل الشافعي، الذي يرى أن العبد غير مالك. أما نحن إذا رأينا أنه مالك، فلا يظهر جدًا أن السيد عامل ملكه بملكه، إلا أن تكون الكتابة على اكتسابه، فإن كسب العبد لسيده. وأما الكتابة من جهة كونها عقدًا من العقود، مشتملًا على الإيجاب والقبول إلى آخره، فجارٍ على ذوق الأقيسة. وإذا كان الأمر [منقسمًا] إلى جهتين مختلفتين، فمن عمم القضاء عليها نفيًا أو إثباتًا، فقد غلط من غير إشكال.
وما ذكره بعد ذلك من أن الأصول لو نظر إليها نظرًا [كليًا]، لتخيل فيها التناقض. ومن ذلك قال بعض من لا يعد من الراسخين: إن النكاح خارج عن القياس. وكذلك قالوا في الإجارة. وليس من قال هذا على بصيرة فيما يأتي ويذر، بل كل عقدٍ مقرٍ على قياسه ومصلحته. فخواص النكاح لا تلفى في غير النكاح. ومن أراد إلحاق أصلٍ بأصلٍ في خاصيته، تعذر ذلك عليه. فإنه لو وجدت الخاصية في غير موضعها، لم تكن خاصية بحال، ولجرت القواعد','3','467',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2099,'كلها على قضية واحدة، وذلك محال. وإذا تعذر أن تعتبر خواص الأصول بعضها ببعض، تعذر القياس ذو الفرع والأصل، ولا يبقى في ذلك إلا الاستدلال، إن صح القول به. هذا إذا تباعدت الخواص، أما إذا تقاربت، فإن اعتبرت الخاصية من أخص أوصافها، تعذر القياس (٨٤/ ب) أيضا. كما إذا أردنا أن نعتبر خاصية القراض، من إبهام مقدار الربح ومدة العمل، فإن ذلك لا يتصور في غير القراض. نعم، إذا عدلنا عن أخص الأوصاف، ونظرنا إلى ما فوق ذلك، أمكن الاشتراك، وهو بمثابة اعتبار المساقاة بالقراض، أو بالعكس من ذلك، فإن ذلك يصح، لكن بشرط أن يتنبه لأمر آخر، وهو أنا لا نقيس في عين الخاصية، لتعذر الوجدان في غير محل الاتفاق.
وقوله: إذا أردنا أن نقيس أصلا مشتملا على قريب من تلك الخاصية، إنما يصح بعد إقامة الدليل على إلغاء المزية. وأما مع الاعتراف بثبوت أثرها، فمن المحال إثبات الحكم مع فقدانها. فيريد أن المعتبر إنما جمع بين الأمرين المتقربين، بعد قيام الدليل على إلغاء ما تقاربا فيه. وإن لم يكن كذلك، لم يمكن القياس. إذ يكون قد جمع بين الفرع والأصل بغير جامع الأصل. وهذا مما لا يتجه فيه خلاف.','3','468',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2100,'و<span data-type="title" id=toc-380>اختلاف العلماء في إلحاق المساقاة بالقراض</span>، إنما نشأ من جهة أن الحكم الثابت في القراض، هل ربط بخاصية القراض من إبهام الربح والعمل؟ [أو] إنما ربط به من جهة دعاء الضرورة إلى المعاملة على نصيب ما يحصل من الربح، من غير نظر إلى ضبط العمل وعدم ضبطه؟ فالشافعي يزعم أنه إنما جاز من هذه الجهة. وأبو حنيفة يقول: إنما جاز من جهة [عسر] ضبط العمل، ولا يلزم من تجويز الشرع هذا العقد من جهة عسر ضبط العمل، أن يجوز شيئا آخر يمكن ضبط العمل فيه. وله وجه من المناسبة.
وفي كلامه من هذه الجهة قوة: [وإن] لم يكن للمساقاة مأخذ سوى شرعية القراض. وليس الأمر كذلك، فإنه قد ثبتت أخبار صحيح بجواز المساقاة. ولكن [ما ذكره تفريع] على عدم النصوص الدالة على جوازها.','3','469',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2101,'ثم جرى في المسألة كلام (٦٤/ ب) لابد من النظر فيه، وذلك أنا قلنا: شرط الفرع الثابت بالقياس، أن يكون مسكوتا عنه، والشافعي رحمه الله قد اعتبر بالمساقاة بالقراض، ثم عضده بالحديث الثابت في المساقاة، فقد صارة حكم المساقاة منصوصًا عليه، فكيف يبطل حكمه بالقياس؟ والذي أطلقه الفقهاء والأصوليون أنه قد يجتمع على حكم المسألة النص والإجماع والقياس. فهذا الكلام ظاهر التناقض، ولكن الظاهر عندي أن الممتنع أن يثبت حكم بالقياس على أصلٍ منصوص عليه، على وجه النص شاملًا للمعتبر به والملحق به. هذا مثال.
وصورته: أن نقيس البر على الشعير بجامع الاقتيات، ثم نستدل على صحة (٨٥/ أ) العلة وثبوت حكم الأصل بقوله: (لا تبيعوا الطعام بالطعام).','3','470',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2102,'هذا محال، إذ ليس [البر] بأن يعل أصلا للشعير مع هذا النص، بأولى من ضده، بل الصحيح أنه لا يعتبر واحد منهما بصاحبه. أما إذا كان المطلوب حكمه بالقياس، لا يتناوله مع أصله نص واحد، فهذا هو الذي يتأتى القياس فيه على أصل آخر، وكأنه مسكوت عنه باعتبار النص الدال على حكم أصله، فلابد من التنبه لذلك.
فأما قول الإمام: إن الشافعي أجرى في المسألة كلامًا بدعًا إلى آخره. فهو كلام حسن في فنه، وذلك أن أهل الإجماع ليس لهم رتبة الاستقلال بإثبات الأحكام، وإنما يثبتونها نظرًا إلى أدلتها ومأخذها. وإذا كان كذلك، فلابد من إسناد أهل الإجماع الحكم إلى مستندٍ شرعي، إلا أنه لا يجب البحث عن مستند أهل الإجماع، إذ قد ثبتت لهم العصمة، ولا يحكمون إلا عن مستند صحيح، إذ لو حكموا غير مستندين لسبب صحيح، لكان ذلك نقيض ما ثبت لهم من العصمة. فمن هذه الجهة لم يلزم البحث عن مستندهم، وإذا لم يلزم البحث','3','471',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2103,'عن المستند، فلا يمتنع الاطلاع عليه. وأكثر الإجماعات عرف مستندها. وإنما الشافعي تكلم عن أمرٍ غير لازم النظر فيه، وهو البحث عن مستند أهل الإجماع.
[ثم] قال رحمه الله: إن هذا الضرب من المعاملة لم يعهد متقدمًا في زمن رسول الله صلى الله عليه وسلم. وتقريره إياه يدل على كونه مشروعًا. قال: وأول ما جرى هذا الضرب من المعاملة في زمن عمر - رضي الله عنه -. أراد بذلك أن ينفي أن يكون مستنده علم [من] رسول الله - صلى الله عليه وسلم - وتقريره إياه، فقد بطل أن يكون ذلك مستند القراض. قال: ولا يصح أن يكون مستنده سنة، إذ لو كان ذلك لنقل، واعتني بنقله، ولم يندرس. وليس يصح أن يكون مستندًا إلى كتاب الله [تعالى]،','3','472',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2104,'وهذان هما الأصلان. ولا يصح أن يقال أجمع عليه، لأنه مجمع عليه، فامتنع إذًا أن يستند القراض [إلى] الإجماع، ولا إلى أصل من الأصول الثلاثة [التي هي] الكتاب والسنة والإجماع. ولابد أن يستند إلى مأخذ من مآخذ الشريعة، فإذا بطل أن يستند إلى التوقيف، وجب أن يستند إلى الرأي من حيث [الجملة].
وقوله: فلا أرى للقياس أصلًا إلا ما صح عن رسول الله - صلى الله عليه وسلم - في المساقاة. وهذا الذي ذكره الشافعي أخيرًا ظاهره مناقض لما سبق من ضرب [المثال]، وذلك أن المثال فيما إذا ثبت أصل ذو خاصية، فأراد الناظر أن يثبت أصلًا مشتملًا على قريب من تلك الخاصية، (٨٥/ ب) كما جر في القراض، حيث اعتبر المساقاة [به]، ثم (٦٥/ أ) آل الكلام إلى اعتبار القراض بالمساقاة، فالمساقاة إذًا هي الأصل، والقراض معتبرها، ولكن','3','473',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2105,'المقصود التمثيل. فأما الاعتبار بتحقيق المآل، فلا يتوقف عليه حصول المقصود.
فأما ما ذكره من امتنع كونه لا يصح أن يستند إلى خبر، إذ لو استند إليه، [لوجب] نقله في الاعتياد. [فعنه] جوابان:
أحدهما- أنه يحتمل أن يفهم الصحابة - رضي الله عنهم - جواز القراض بقرائن أحوال رسول الله - صلى الله عليه وسلم -، وتفاريق أمورٍ حصلت لهم علمًا ضروريًا، وإن لم يمكن نقل آحادها، فلم يمكنهم نقلها عن تفاصيلها، وعلموا أن الإجماع حجة، فأغناهم الإجماع عن نقل المستند. وكثير ما يكون هذا هكذا. وكذلك قلنا نحن في القياس، وأنهم لم يجمعوا على القياس إلا بعد حصول العلم لهم بكونه من الشريعة، ولكن لم ينقلوا الألفاظ الناصة الدالة على شرعية القياس. هذا وجه.
والوجه الثاني- أنه يمكن أن تكون الأدلة الدالة على صحة القراض ثابتة، ولكن تكون ظاهرة مؤولة، فلو نقلت، لتطرق إليها الاحتمال، ولم يحصل العلم','3','474',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2106,'بها على حال، [فاكتفوا] بالإجماع الذي لا يتطرق إليه الاحتمال بحال. ألا ترى أن الأمور المتواترة من الشريعة يعسر نظم الأسانيد عنها، فلو أردنا أن ننظم إسنادًا في أن رسول الله - صلى الله عليه وسلم - كان يصلي الصبح ركعتين، اقتصارًا على ذلك، لم نجد إليه سبيلًا.
ويجوز أن تكون الأخبار ثابتة عندهم، ثم أفضى الأمر إلى الدروس، لعدم الحاجة إلى النقل بعد حصول القطع بشرعية المعاملة.
ثم إن كان القوم بجملتهم، إنما أثبتوا القراض قياسًا على المساقاة، لزم أن تكون المساقاة مجمعًا عليها في الصدر الأول، ويكون من منعها خارقًا للإجماع. فلئن كان الشافعي يقطع بما [ذكره]، وجب أن نقطع بخرق أبي حنيفة الإجماع. وإن كان يظن ذلك، وجب أن يظن خرقه [للإجماع]، وكلا القولين عندي غير صحيح، فلا حاجة إلى هذا التقدير. وإنما حمله على ذلك ليقرر قاعدة أصولية، [وهي] إجراء القياس في القواعد عند تقارب المقاصد، وتداني الخواص. والحق عندي في ذلك أن القياس عند تقارب المعاني، لا يصح بحال، وحاصله راجع إلى حكم نقل الأصل إلى الفرع بغير مناط الحكم','3','475',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2107,'في الأصل عند المقاربة، وذلك ممتنع، لا شك فيه، وليس على حقيقة القياس. نعم، إن قام الدليل على خاصية الأصل غير معتبرة، بالنظر إلى أخص أوصافها، وإنما (٨٦/ أ) اعتبرت من وجهٍ أعم من ذلك، فهذا الآن قياس صحيح، إذا كان الوجه [الذي] [لأجله ثبت] الحكم في الأصل موجودًا في الفرع، إذ يكون [القياس] على حقيقته وشرطه. هذا هو التحقيق عندي في ذلك.
وما ذكره المعترض من أنه ينقض القياس إذا اعتبر المجتمع عليه بالمختلف فيه. فكلام واقع، وذلك أن الشافعي يقطع [بصحة] القراض، ويظن صحة المساقاة، فكيف يصح أن يقطع بحكم الفرع، ويظن حكم الأصل؟ الله إلا أن يقال: إن أهل الإجماع إذا أسندوا الحكم إلى مأخذ مظنون، قطع بالحكم مع بقاء المستند مظنونا، كما إذا أجمعوا [على] حكم [مستندين] إلى خبر','3','476',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2108,'واحد، فقد قال القاضي: لا يصير الخبر بسبب ذلك متواترًا، لأنهم قد أمروا أن يحكموا عند خبر الواحد، إذا ظنوا الصدق. لكن هذا غير صحيح ههنا، لأنهم إنما يجمعون على القراض، مستندين إلى المساقاة، بناء على إجماعهم على المساقاة، وإذا أجمعوا على المساقاة، كان حكمها (٦٥/ ب) مقطوعًا به. هذا هو الذي يصح عندنا في هذين المسألتين. والله المستعان.
قال الإمام: (ومما ينبغي أن ينتبه له الناظر قبل الخوض في تحرير المسائل) إلى قوله (إذا كان الأصل ينقدح فيه توجيه معنىً كلي). قال الشيخ: ما ذكره الإمام ههنا حسن بالغ، وذلك أنه إذا نظر الإنسان نظر ذي غرة، فقد يخيل إليه أن القواعد جاءت متناقضة، [إذ] ثبت البيع مؤبدًا والنكاح منتهيًا بالموت أو بالطلاق، وهذا على خلاف وضع البيع، والإجارة مضبوطة المنافع، إما بالعمل الموصوف، أو بالزمن المضروب، وهذا لا يقبله لا البيع ولا النكاح، والقراض أثبت مجهول المدة والعمل، والمساقاة مضبوطة فيهما جميعًا، والأصول متقاربة. فقد يظن أن هذا تناقض وتدافع، وليس الأمر كذلك، بل لا يقتضي مصلحة الخلق إلا التقرير على هذا الوجه. فإن الله تعالى أعلم بمصالح عباده، وما يصلحهم في دينهم ودنياهم:','3','477',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2109,'فمصلحة البيع لا تقتضي إلا تملك المنافع والأعيان، إذ العوض فيها منصرف إلى العين والمنافع، والإجارة هي المبيعة، فلو أثبتت مجهولة، لتنزل ذلك منزلة جهل المبيع، وذلك يمنع صحة البيع.
والنكاح أثبت غير مؤقت، بل التوقيت يناقض مقصوده، وهو نكاح المتعة، فإنما يلتئم أمر الزوجين، إذا اعتقد دوام الصحبة، [وإذ ذاك] يحصل مقصود الائتلاف. وإذا كان كل واحد منهم يتوقع وقتا تقع الفرقة فيها، لم يحصل هذا المقصود بصفة [التمام].
وهل يكون التوقيت الوارد بعد صحة العقد يوجب انقطاعه في الحال (٨٦/ ب) أم لا؟ فيه خلاف، ومذهب مالك رحمه الله [أنه] يقتضي انقطاعه، [ويخالف في ذلك الشافعي]، وقال: إذا سلم العقد من التوقيت في حين وقوعه، لم يضر التوقيت الوارد عليه.
وصورته: ما إذا قال لها: أنت طالق عند رأس الشهر، قال مالك رحمه الله: تطلق في الحال. وقال الشافعي: تبقى زوجة إلى دخول الوقت. وهما','3','478',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2110,'متفقان على أنه إذا قال لها: أنت حرة عند رأس الشهر، أنها لا تعتق في الحال. فجعل الشافعي ذلك أصلا، ولم ينجز الطلاق، كما لم ينجز العتق، وإن كانا مستويان في امتناع قبول التأقيت عند ابتداء العقد.
ولكن الفرق عند مالك بينهما، هو أن سلطنة ملك السيد، لا [تخرم] مقصودها باعتقاد العبد انتهاء الملك بعد مدة، إذ سلطنة القهر باقية عليه، وليس كذلك الاستماع بالزوجة، فإنه مبني على غير سلطنة القهر، بل إنما يحصل مقصوده بالتحاب والائتلاف على المستمتع الحلال. فكان طريان التوقيت على أحدهما، يناقض مقصوده، ويفوت المطلوب منه، وليس كذلك ورود [التأقيت] على الثاني.
وكذلك القول في جهل عمل القراض، فإنه يس المقصود منه كثرة الخدمة، وإدامة تحريك المال، وإنما المقصود منه تنمية المال، وحصول الأرباح، وذلك لا ينضبط بالزمان بحال، إذ [قد] تتحول الأسعار في الزمن القصير، وقد يستمر الكساد الزمن الطويل، [فكان] التوقيت فيه يخل','3','479',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2111,'بمقصوده، وليس المساقاة كذلك، إذ العمل فيها مقصود، والضبط فيه يكمل مقصوده.
ولهذا السر فرق مالك رحمه الله بين البابين في زكاة الثمار في المساقاة، وفي زكاة مال القراض، ورأى أن ثمار المساقاة تزكى على رب المال، إذ كان جميع الثمار خمسة أوسق، وكان المالك من أهل الزكاة. ولم يعتبر (٦٦/ أ) فيما يأخذه العامل في حصته من الثمار، تغليبًا لجانب الإجارة على المساقاة، وكأن رب المال استأجر العامل ببعض الثمار، وكأنه إنما يستحق الأجر على الحقيقة بعد تمام العمل. فعلى هذا تصير الثمار كأنها باقية على ملك مالك النخل، فتسبق الزكاة حق العامل. وليس القراض كذلك، إذ ليس الإجارة فيه مقصودة، فهما في الربح شريكان، فلذلك لا يرى على رب المال زكاة، إلا أن يكون في رأس ماله ونصيبه من الربح قد النصاب.
[فهذا] بيان تقارب هذه القواعد في خصائص هذه المصالح، وإن كانت كلها تنحو إلى نحوٍ واحد. فلله الحمد على ما أنعم من هذه النعم المتظاهرة.','3','480',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2112,'وليس هذا [في] التمثيل إلا بمثابة (٨٧/ أ) علم الطب، فالمقصود من مداواة جميع الأعضاء حصول الصحة، ولكن يكون للعين دواء يختص بها، وليس دواؤها دواء السن، بل لو عولج بعض العضاء بما يعالج به غيره، لفسد وتلف، كمن أراد أن يعالج العين [بالكي] [والفقأ]، وإن كان يعالج [السلع] والدماميل بذلك. فكذلك تصرف الشريعة في هذه القواعد، كلها منوطة بمصالح العباد، ولكن تختلف أوضاعها على حسب ما علمه مقدرها وواضعها. فهذا معنى قول الإمام: [إنه] إذا استند نظر الموافق، رأى كل شيء على أكمل ما يكون.
[فقول] من يقول: إن بعض القواعد خارج عن [القياس]، [إن','3','481',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2113,'أريد أنه خارج عن قياس] غيره، فهو صحيح، كما أنه خارج عن قياسه. وإن أريد به أن الحكمة لا تقتضيه، فليس الأمر كذلك، بل ما حكم الله تعالى بحكم على مناقضة مصالح الخلق، بل الناس فريقان: قائل يقول: لا حكم إلا لمصلحة، ومنهم من يقول: يجوز خلو البعض عن المصالح. فأما المصير إلى ثبوت الأحكام مناقضة لمصالح العباد، فلا يثبت ذلك بحال. قال الله تعالى: {وما أرسلناك إلا رحمة [للعالمين]}.
قال الإمام رحمه الله: (وقد ثبت أصل لا يتجه فيه استصلاح ظاهر، كالكتابة) [إلى آخر المسألة]. قال الشيخ: أما كون الكتابة خارجة عن القياس، بناءً على أن قضية المالكية تمنع من معاملة العبد، فهذا إنما يستقيم بعد تقرير أمرين: أحدهما- أن نسلك بالكتابة مسلك المعاوضة. والثاني: أن نبين أن العبد لا يملك بحال.
أما من ذهب إلى أن العبد يملك، فلا تكون المالكية تحيل المعاملة، فإن مالكًا رحمه الله رأى أن العبد يملك، [و] صحح معاملته مع السيد وغيره، وجعل للسيد أن يحاص غرماء العبد، بما ثبت له عليه من الدين. فلسنا نسلم','3','482',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2114,'أن المالكية تقتضي منع المعاملة. والدليل على أن العبد يملك أمران: أحدهما- النص. والثاني- القياس.
أما النص: فقوله - عليه السلام -: (من باع عبدًا وله مال .. ). فقد أضاف المال إليه، فيدل ظاهره على صحة ملكه. وأيضًا فإن الأسباب التي لأجلها ملك الأحرار موجودة في [العبيد]. وهذا هو الوجه الثاني. ويعتضد ذلك بتصور [ملك] النكاح، ولو كان كالبهيمة، لم يثبت له الشرع ملكًا على حال.
وأما كون الكتابة عقد معاوضة على الحقيقة، فليس كذلك عندنا، بل الكتابة فيها شوائب التعليق والاغتلال والمعاوضة، فإن أريد بكونها خارجة عن القياس، أنها لا تجري على المعاوضات المحضة، [فصحيح]، لامتيازها بخواص لا توجد في محض (٨٧/ ب) [المعاوضات]، كامتياز (٦٦/ ب) النكاح بخاصيةٍ عن البيع.
فشائبة المعاوضة: فيها: من جهة توقف الأمر على رضا الفريقين، وامتناع العقد على ما يعظم من الغرر.','3','483',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2115,'وشائبة التعليق: من جهة أن العبد لا يعتق منه بمقدار ما أدى، فيصير بمثابة ما لو علق العتق على مجموع أشياء، فلم يوجد بعضها، فكذلك: (المكاتب رق ما بقي عليه درهم).
وأما شائبة الاغتلال: فمن جهة أن العبد إذا لم يتعجل له العتق، فهو على الحقيقة باقٍ على الملك. وإذا كان كذلك، كان السيد مستغلًا لعبده. هذا وجه تقرير الشوائب في القاعدة.
فإذا قيل: إنها منتزعة عن القياس بالكلية، فهو غلط. وإن قيل: إنها خارجة عن قياس غيرها من القواعد، التي لم تجتمع فيها هذه الشوائب، فهو صحيح، وتصير أيضا هي خارجة عن قياس غيرها [من القواعد]، كما أن غيرها خارج عن قياسها.
[وأما] كوننا لا نلحق بها قاعدة أخرى، فلأجل أنا لا نجد ما يشاكلها، فكأنها معللة بعلة قاصرة، فيمتنع الإلحاق لعدم وجدان النظير، [لا] لأن الثابت غير معقول المعنى.','3','484',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2116,'وأما صحة جريان القياس على ما تخيله الإمام في إلحاق المساقاة بالقراض، أو على العكس من ذلك، [على] القول الآخر، فقد بينا وجه ذلك عندنا. والصحيح أنه لا قياس بحال، بل استند القراض إلى الإجماع، واستندت المساقاة إلى الخبر الصحيح، والنص الصريح. فهذا هو الطريق الحق.
فأما المصير إلى أن نعتبر القواعد عند تفاوت المعاني، مع الاعتراف بتوجه الفروق، فمحال. وإن لم يكن بد من القياس، فلابد من التعرض لإبطال الفرق، حتى يثبت التماثل في المناط، دون التقارب في الخواص. والله المستعان.
قال الإمام: (مسألة: ما صار إليه جماهير العلماء مع التزام القياس) إلى قوله ([ويستعدها] لما [يستعقب] المسائل [به]، إن شاء الله','3','485',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2117,'تعالى). قال الشيخ: ما ذكره أصحاب أبي حنيفة من إزالة النجاسة معقولة المعنى، صحيح، وكيف ينكر ذلك، وهو معدود من مكارم الأخلاق، ومستحسنات العادات؟ فلا شك في أن الشرع لاحظ ذلك وقصده.
وأما ما بنوه على ذلك من صحة إقامة كل مائع مقام الماء. فهذا لا يصح عندنا، وقد عين الرسول - عليه السلام -[الماء] لذلك، فقال (حتيه واقرصيه ثم اغسليه بالماء).','3','486',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2118,'وقد اختلف الناس في تعين الماء لإزالة الأخباث ورفع الأحداث، هل هو من أبواب التعبدات، أو [هو] جارٍ على مسالك المعاني [المعقولات]؟ ومن ذهب إلى (٨٨/ أ) أنه تعين تعبدًا، لا ينكر حصول فهم المعنى، فيما يتعلق بإزالة النجاسة، ولا يلزم من تعين الآلة تعبدًا، عدم فهم المعنى من القاعدة. كما أنه لا يلزم من عجز العقول عن تقدير نصب الزكاة، أن نقف عن فهم معنى الزكاة، من مواساة الفقراء من أموال الأغنياء. فإلزام التعبد، بناءً على تعين الآلة، بعد فهم المعنى، غير صحيح، كما ذكرناه.','3','487',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2119,'والصحيح عندنا أن نفس الماء ليس تعبدًا أيضًا، بل يلوح فيه معنى، وهو أنه لا يقوم غيره مقامه، لانفراده [بلطافةٍ] في الجرم، وانفرادٍ في التركيب. وأما غيره من الأجسام المائعة، فليس في معناه بحال.
ألا ترى أن له قوة الدفع عن نفسه، وليس لغيره من المائعات ذلك. وقد أرشد القرآن إلى ذلك بجعله الماء طهورًا. وهذا (٦٧/ أ) وصف يقتضي','3','488',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2120,'مبالغة. فلو كان بمثابة غيره، لما استحق وصف المبالغة. فتعيينه للإزالة لا يعرى عن مناسبة لائقة به.
وأما الجنوح إلى التعبد في اقتصار وجوب الإزالة على حالة الصلاة، فغير صحيح أيضا، بل يجوز أن يكون أصل المعنى مفهومًا، وإن [تعذر] معرفة سر التوقيف. وهذا بمثابة [فهم] وجوب الزكاة، لسد الخلة، والتوقيت بالحول من غير زيادة يوم ونقصانه، لا يلوح فيه معنىً للعقلاء.
الوجه الثاني: أنه لو وجب إزالة النجاسة في كل وقت، لشق ذلك على المكلفين، [فربطه] الشرع بأمور تتكرر، ليحصل مقصوده النظافة من تكرار الصلاة، ويسقط عن الخلق ملازمة الغسل على الإطلاق.','3','489',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2121,'وأما توقف صحة الصلاة على إزالة النجاسة، فلأهل العلم في ذلك خلاف، وحاصل مذاهبهم في ذلك تؤول إلى ثلاثة أقوال.
أحدها- أنه لا تصح الصلاة معها على الإطلاق، ويستوي في ذلك العمد والنسيان، إلا مواضع الضرورات التي يمكن الاحتراز منها، كدماء الجراح، والمريض الذي لا يستطيع الإزالة، ودم البراغيث. (وقد صلى عمر - رضي الله عنه - وجرحه يثعب دمًا). [وهذا] مذهب ابن وهب والشافعي رضي الله','3','490',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2122,'عنهما. وهذا ضعيف، [إذ قد] جاء عن النبي - صلى الله عليه وسلم -: (أنه أحرم بالصلاة وهو لابس خفين، ثم نزعهما في أثنائها، واكتفى بما سبق وبنى عليه). فلو كانت شرطًا على الإطلاق، لاستأنف الصلاة.
وذهب ذاهبون إلى أنها فرض مع الذكر والقدرة، ساقطة مع العجز والنسيان. وهذا ظاهر مذهب مالك رحمه الله.
أما دليل الوجوب على [الإزالة]، فقد دل عليه الكتاب والسنة','3','491',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2123,'[وعمل] الصحابة. (٨٨/ ب) أما الكتاب، فقوله تعالى: {وثيابك فطهر}. وأما السنة، فقد قال - عليه السلام - للتي سألته عن دم الحيض: (حتيه ثم اقرصيه ثم اغسليه بالماء). وأوجب الغسل. وقالت عائشة رضي الله عنها: (كانت إحدانا تحيض في الثوب، فإذا طهرت نظرت، فإن رأت شيئا غسلته، ثم [تعده] تصلي فيه). وقال عمر - رضي الله عنه - لما احتلم في الثوب: (بل أغسل ما رأيت، [وأنضح] ما لم [أره]). فقد ثبت بذلك أنه لابد من غسل النجاسة عند إرادة الصلاة، وسقط الأمر عن الناسي بما ذكرناه من [حديث النعلين].','3','492',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2124,'وقد ذهب [بعض] أصحابنا إلى أن إزالتها سنة في الصلاة، وتمسك بما ذكرناه في قضية [النعلين]، وقال: إن الشرط ينبغي أن يعم. وهذا الكلام فيه ضعف، ولا [بعد] في قصر الشرط على بعض الأحوال. فإن صوم التتابع [شرط] في صحة صوم الظهار وقتل النفس، ولو أفطر لمرض أو نسيان، لم يبطل [الصوم]. فكذلك تتنزل إزالة [النجاسات] هذه المنزلة.
وأما اشتراط الإزالة في صحة الصلاة، فلا يخلو عن وجه من المناسبة. وذلك أن الشرع جعل هذه العبادة أعلى قربة [من بين سائر القربات، حتى حرم','3','493',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2125,'الكلام فيها، الذي لا يليق بها، والأعمال التي] ليست من جنسها، وأوجب التوجه إلى جهة مخصوصة، وأوجب فيها أذكارًا، كل ذلك يشعر القلوب عظيم الأمر، في الوقوف بين يدي الله - عز وجل -، وجعل فيها شرطا، وهو ستر العورة، ولم يجعله شرطًا في غيرها. ففهم العلماء من هذه الجهات أنه قصد منها استشعار الوقوف بين يدي الله تعالى، ولذلك منع من العري، وأن يكون الثوب نقيًا من النجاسات والأقذار، تنزيلًا للعبادة منزلة ما يستحسنه (٦٧/ ب) أهل العادات عند ملاقاة ذوي الهيآت والمروءات. وهذا معنى واضح، وكلام لائح. وليس ينكر الفقهاء فهم سر ستر العورة في الصلاة، وذلك المعنى بعينه يطرد في إزالة [النجاسات].
وأما إلزام كونها لا تجب الإزالة في غير الصلاة، فقد بينا أن سبب سقوط ذلك، لزوم المشقات مع تعذر الانكفاف عن جنس النجاسات [بوجهين]:','3','494',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2126,'أحدهما- أن العري أفحش في العادات، وأشد مناقضة للمروءات، [فلو] أوجب الشرع التعميم والصيانة عن أعين البشر، فهو أفحش من كون الثوب نجسا.
ولا مشقة أيضًا تلزم من إدامة الستر. وإذا عظم الفحش، وخف الستر، ألزم الشرع الاستمرار على الستر. فلا يقدح ذلك في ثبوت اختصاص وجوب الإزالة للنجاسة (٨٩/ أ) [بحالة] الصلاة.
ومعنى قوله: إنه ينعكس بستر العورة، يعني أنه لما وجب ستر العورة في الصلاة، وجب في غيرها. ويقول: إذا لم تجب إزالة النجاسة في غير الصلاة، فينبغي أن لا تجب فيها، لو اعتمد على المعنى، ويجب الرجوع إلى','3','495',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2127,'التعبد. هذا تقرير كون إزالة النجاسة معقولة المعنى. وقد أومأنا إلى سبب الاختصاص بالماء والاقتصار عليه.
فأما ما ذهب إليه أصحاب الشافعي، من أن طهارة الحدث معقولة المعنى، فالكلام عليهم في ذلك أصعب من الكلام على من قال: إن إزالة النجاسة معقولة المعنى، ولذلك قال المحققون: إن الوضوء يفتقر إلى النية، وأن إزالة النجاسة لا تفتقر [إليها]. وقد سوى فريق بينهما في الافتقار إلى النية. وسوى آخرون بينهما في الاستغناء. والذي نختاره في ذلك أن المعنى مفهوم على الجملة، [وللتعبد] في المسألة مجال رحب.
أما فهم المعنى، فبالنظر إلى إشعار اللفظ، وإيماء التعليل وقضية العرف.
أما إشعار اللفظ، فالوضوء مشتق من الوضاءة، وهي النظافة. وقد أمر','3','496',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2128,'الله تعالى بالوضوء عند كل صلاة. فهذه النظافة مأمور بها.
وأما الإيماء، فقوله {ولكن يريد ليطهركم}. فأومات النصوص إلى [أن] إيجاب الوضوء لأجل التطهير.
وأما الأمر العرفي، فليس ينكر العاقل ما في الوضوء من النظافة، وإدامة التمرين على ذلك، يحصل مكارم الأخلاق. ويعتضد ذلك [بما] ذكرناه في إزالة النجاسة وستر العورة.
وأما ما فيه من شائبة التعبد، فارتباط بالأحداث، [ولو نيط] بكل صلاة، كما فعل في إزالة النجاسة، لكان المعنى أظهر.
ثم أيضا وجوب الوضوء على من خرج من الحمام عند الحدث،','3','497',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2129,'وسقوطه [عمن] [صادم] [عظيم] الغبرات، ينحو نحو التعبد. لا جرم قال المحققون يفتقر الوضوء إلى النية، بخلاف إزالة النجاسة، لظهور المعنى فيها، وغلبة التعبد في الوضوء.
وقد ذهب آخرون إلى أن الوضوء يستغني عن النية، وهو قول أبي حنيفة، وهو قول ضعيف عندنا. فمن هذه الجهة التحق بالعبادات البدنية، فلاق به ترغيب في جانب الثواب باشتراط النية، وجعل التيمم أيضا بدلًا منه عند العدم يغلب التعبد. وكلام الشافعي يشير إلى التماثل، حيث قال: (طهارتان فكيف تفترقان)؟ هذا هو الذي يصح عندنا في المسألتين.
وما ذكره الإمام من تخاوض الفريقين، كلام مفهوم، مع أنه ليس وراءه طائل، فلم نر تتبعه في ذلك، واقتصرنا على التنبيه (٨٩/ ب) على نكتتي المذهبين. فإذا (٦٨/ أ) حكمنا بتعين الماء في إزالة الأخباث، [فلأن]','3','498',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2130,'نحكم [بتعينه] في رفع الأحداث أولى، إذ لا يعد الناس لنظافتهم وطهورهم إلا الماء دون غيره، فهم ذلك المتمسكون بالشرائع وغيرهم، فلا ينكر العقلاء انفراد الماء بهذه الخاصية دون غيره من المائعات. هذا هو الصحيح، فإن بعض أهل العلم قد ذهب إلى أن تعيين الماء من أبواب التعبدات، ولكنا قد اخترنا سواه. والله ولي التوفيق.

قال الإمام: <span data-type="title" id=toc-383>(مسألة: قال الخائضون في هذا الفن: رب أصلٍ) </span>إلى قوله (في فن يقصد منه بغية القطع). قال الشيخ: قد بينا أن استقراء الشريعة يرشدنا إلى أن الأحكام، [إنما] أثبتت مرتبطة بالمصالح، ولكنها قد تظهر للناظرين، وقد تخفى، ويستأثر الله [تعالى] بعلم ذلك. والظاهر منها، قد','3','499',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2131,'يظهر من كل وجه، وقد يظهر من وجه دون وجه، فحق على الناظر التثبت والبحث والتمييز بين الجهات، فلا يطرد القياس، نظرًا إلى المحل المنقاس، ولا يطرد نفيه، نظرًا إلى مواقع [التعبد]، وقد يكون الوصف طردًا بالإضافة إلى حكم، وقد يكون مناسبًا بالإضافة إلى آخر.
وبيانه: أن الذكورة والأنوثة طرد، بالإضافة إلى ما يتعلق بالمعاملات، وقبول التمليكات، وصحة الخيارات، والقيام بأروش الجنايات، وقيم المتلفات. ويظهر تأثير الافتراق فيما يتعلق بالقضاء والولايات، ومراتب الشهادات، وتفاوت الديات. فمن أعرض عن هذه الجهات على الإطلاق، كان غالطًا، ومن التفت إليها على الإطلاق، كان غالطًا.
والسر فيه، أن الناظرين في الشريعة ليس إليهم وضعها، وإنما يحومون على مقاصد الشرع. فإذا ظهر من الشرع الإعراض عن وصفٍ على الأطلاق، امتنع اعتباره، وإن ظهر التفاته إليه، لزم ذلك في موضع التفاته. فعلى هذا يكثر في الشريعة أن يكون الشيء يتطرق إليه التعليل من وجه، ويتقاعد عنه التعليل من وجه. وإذا تقرر ذلك فنحن نذكر الأمثلة التي ذكرها.','3','500',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2132,'أما اختصاص القطع بالنفيس، فأمره واضح، والمعنى منه لائح، وذلك أن من المناسب أن يوضع من الأسباب ما يليق بالمسببات، ويستقل بتحصيل مصالحها، وما كان يكتفي في حصول مصلحة شيء بصفة القلة، مع كونه يتوقع من الزيادة مفسدة، فلا وجه لشرع الزيادة على الوجه المذكور، (٩٠/ أ) مع حصول الغرض بما دونها. وإذا تقرر ذلك، فإتلاف النفوس والأعضاء، وإيراد الأسباب المؤلمة [للحيوانات] محذورة. وإنما أبيح ما أبيح منها [ما] [يربي] على المفسدة. وإذا لم تدع حاجة إلى هذه الجهات، وحصلت المصلحة بدونها، كان في إثباتها التزام الضرر من غير غرض. وقد يقال إن في إثباتها كمال تحصيل المقصود، إلا أنه يفضي إلى إثبات الإيلام، المستقبل بمنع الإقدام، لغرضي الزيادة في التكميل، ولم يلتفت الشريعة لذلك.','3','501',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2133,'وإذا ثبت ذلك، فالمقدار اليسير من المال، وإن كانت النفس تتشوف إليه بعض التشوف، ولكن لا تهجم النفس عليه كل الهجوم، لحقارته ويسارته، فاكتفى الشرع عند ضعف الدواعي بإثبات التحريم في الدنيا، والوعيد في الآخرة بالعذاب الشديد، وسلب [منصب] العدالة في الحالة الراهنة، فكان ذلك كافيا في الامتناع غالبا، فلم يكن في شرع القطع حاجة ماسة مع شرعية (٦٨/ ب) التعزير في مثل هذه المحال. فمجموع هذه الأمور يغني عن شرعية الحدود في مثل هذه الصورة، فلا يفتقر إلى شرعية الحدود.
أما إذا عظم المال، وقويت الرغبة، وتأجل عذاب الآخرة، فلا [تنزجر] النفوس بذلك، لقوة الرغبة، وعظيم الشهوة، [فانتهضت]','3','502',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2134,'الحدود زواجر عن ذلك، وهذا مناسب لتصرف العقلاء، [جارٍ] على أبواب المصالح والعقول. وإن اقتضت على الجملة الفرق بين التافه والنفيس، كما [ذكرنا]، فليس في العقل ما ينص على مقدار النفيس، إذ الخسة والنفاسة لا يتصف بهما مبلغ لعينه، فافتقر إلى الشريعة في تعيين النفيس من الخسيس. [لكن لا ينكر] بوجهٍ أن الحبة وما يقاربها، خسيس عند أهل العرف، والقناطير من الأموال عظيمة عند الجميع، وما بينهما أوساط لا تقبل الضبط، فحجر الشرع الخلق عن مظان الاشتباه، وجعل نصابًا مقدرًا، وهو ربع دينار أو دينار. فإذا أثبت الشرع ذلك، تبين العقلاء أن هذا القدر استأثر الله تعالى بعلم نفاسته، بالإضافة إلى مصلحة الخلق في شرعية [القطع].
وكذلك القول في [نصب] الزكاة، فإن العقول تشير إلى مصلحة إرفاق','3','503',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2135,'الأغنياء الفقراء، [وكيف] ينكر ذلك، وقد رأى العقلاء [قبل] ورود الشرع ذلك، وتواصوا به، وتواطأوا عليه، لظهور [مصلحته]؟ ولكن تقدير الغني بنصاب مخصوص من مال مخصوص، تتعذر معرفته على حقيقته بالرأي، فجاء الشرع بتقدير النصاب من أموال (٩٠/ ب) مخصوصة، وقدر فيه [أوقاتًا] معلومة، فاتبع العلماء ذلك، وتبينوا أنه منطبق في الغيب على المصلحة بتحصيل المقصود.
وكذلك ما حرمه الشرع من الأشربة ينقسم إلى ما يعظم التشوف إليه، وتشتد المفسدة [فيه]، فلا يكتفي الشرع بتحريمه، بل شرع الحدود مانعة من تناوله، نظرًا إلى شدة الشوف إليه، وفرط الشهوة فيه، وعظيم المفسدة الحاصلة من تعاطيه.
وأما الفواحش التي لا صغو ولا ميل للنفوس إليها، فلا حاجة إلى وضع حدود زاجرة عنها، بل وقع الاكتفاء بمحض التحريم، والوعيد بالعذاب الأليم.','3','504',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2136,'فمثال الأول: الخمر. ومثال الثاني: تناول القاذوات المحرمات، فلا يفتقر فيها إلى وضع حدود زاجرة، لحصول المقصود بمحض التحريم، فلا حاجة إلى وضع الحدود. وأما ما يتعلق بشرب اليسير، فسر وضع الحد بالإضافة إليه، وإن كان لا يعظم التشوف إليه، أنه لا يتوصل إلى الكثير منها إلا بتعاطي القليل، فإنها لا تراد على مرارتها لعينها، وإنما تقصد لما لا يحصل إلا عند الإكثار، فكان القليل داعيًا إلى الكثير.
ومعنى تخيل ورود هذه المسألة على القاعدة اعتراضًا، أنا لما ربطنا الحدود بما يشتد التشوف إليه، [وتعظم] الرغبة فيه، تخيل متخيل أن هذا المعنى غير صحيح، لثبوت الحد متعلقًا بشرب القليل من الخمر. وهذا ليس بنقضٍ، وإنما هو تعلق بالعكس، إذ [قد ثبت] الحكم مع فقدان العلة. والكلام في اشتراط العكس بطول، وسيأتي في موضعه، إن شاء الله تعالى.
ولكن القدر المختص بما نحن فيه، أنه لم ينقطع التشوف عن القليل بالكلية، وليست النفوس منزجرة عنه بمحض التحريم، بل تتشوف النفوس إليه تشوفًا كثيرًا، لما يترتب عليه، والشيء تعظم الرغبة إليه، لكونه يحصل منه الغرض، وتارة يشتد الحرص عليه، لكونه يفضي إلى ما يترتب الغرض عليه','3','505',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2137,'(٦٩/ أ). وشرب [اليسير] يفضي إلى تعاطي الكثير، فرتب الشرع من الحد على القليل، رتبه على الكثير.
فإن قيل: فالخلوة بالأجنبية يفضي إلى الزنا، ولا تراد إلا لذلك، فليترتب على الخلوة حد الزنا، وليس الأمر كذلك؟ فلو كان كون [الشيء] اليسير داعيًا إلى غيره يقتضي أن يترتب عليه حكمه، لفعل ذلك في الخلوة، ولم يفعل، فيكون ذلك نقضًا صريحًا. وبه يتبين بطلان قول من يقول إن [يسير] الخمر إنما رتب عليه الحد، لأنه داعٍ إلى الكثير.
ومقصود الاعتراض فساد الاعتماد في شرع الحدود على قوة الشوف (٩١/ أ) وضعفه. وهذا الخيال باطل، وقد [تبينا] من استقراء الشريعة ضرورة ما ذكرناه، والثابت ضرورة لا يندفع بتخييلات. والفرق بين الخلوة وشرب [القليل] من وجهين:
أحدهما- أن الخلوة تقصد على انفرادها، لغرض يتعلق بها، ولذلك لا يصح إطلاق القول بأن كل خلوة يلازمها الزنا، وليس شرب الخمر اليسير كذلك، فإنه لا شهوة فيه، باعتبار قلته، فلا لذة لمتعاطيه، فلا يراد إلا','3','506',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2138,'[للتكثير]، ولا يلزم من ترتيب الحكم على سبب لا يفارقه مسببه بحال، أن يترتب على هذه الملازمة.
الثاني- إن تعاطي الكثير عند التمكن منه، لا يلزم منه ركوب خطر، واحتمال غرر، مع استبدال المتخلي بفعله، ورجوعه إلى اختياره على انفراد. وليس الزنا الحاصل من شخصين في [تأتي] الحصول كالشرب. على أن أمر الزنا قد يقع الزجر عنه بما يتخيل من ظهور أمارة [الحمل] أو غيره. وليس شرب [الخمر] كذلك. فعظم اتصال شرب اليسير منها إلى الكثير، فحسن لذلك إقامة القليل في شرعية الحد مقام الكثير.
وكذلك أيضا لم يترتب الشرع القطع على سرقة القليل، لعدم التشوف إلى ذلك، مع صيانة الأموال عند أهلها، وقد حصل مانع من سرقة قليل [المال] مع ثبوت التحريم، وليس قليل الخمر كذلك، [إذا] لا مانع من تعاطيها مع شدة [التشوف] إليها، فحسن أن يحد متعاطي قليلها كما يحد متعاطي الكثير منها.
قال الإمام: (فنقول: هذا الذي ذكره هؤلاء، أصول الشريعة) إلى قوله','3','507',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2139,'(والذي ذكرناه مقدار غرضنا الآن). قال الشيخ: جعل الإمام قاعدة القصاص ضرورية، وألحق بذلك البيع، وجعل قاعدة الإجارة حاجية، وليس يتجه ذلك عندنا، بل الصحيح أنها ضرورية أيضا، نظرًا منا إلى غالب أحوال الخلق، وإعراضًا عن النادر.
والدليل على ذلك أنه لو لم يشرع القصاص، لم يقض بعموم القتل الخلق. وغاية ما فيه أن يكون القوي مستوليا على الضعيف. وأما استئصال القاعدة وإبطال الجنس فلا.
وتحقيقه هو أن أهل الجاهلية لم يكن الشريعة عندهم ثابتة، وكانت الدماء بينهم متناولة، ولم يكن القصاص مشروعًا، ومع ذلك لا يهلك الخلق، وإن حصل هرج ظاهر، ولم يكن الخلق في عصر من الأعصار يستغنون عن','3','508',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2140,'الإجارة بحال. ولو منع الخلق ذلك، لتضرر الأكثرون، وأفضى ذلك، إلى فساد ظاهر، ومن الذي تيسر له من الخلق ملك جميع ما يحتاج إليه؟ وكما يضطر الخلق إلى تبادل الأعواض، فهم مضطرون إلى بيع المنافع، (٩١/ ب) فإن أكثر الخلق لا يملكون جميع ما يحتاجون إليه، ولا يستطيعون ملك كل رقبة احتاجوا تحصيل منفعةٍ منها. فإلحاق قاعدة الإجارة بالقواعد الضرورية عندنا أولى (٦٩/ ب).
ومعنى قوله: حاجة الجنس قد تبلغ مبلغ ضرورة الشخص الواحد،','3','509',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2141,'[يعني به أن الشرع كما اعتنى بدفع ضرورة الشخص الواحد]، فكيف لا يعتني بدفع حاجة الجنس؟ ولو منع الجنس ما تدعوا الحاجة إليه، لنال آحاد الجنس ضرورة تزيد على ضرورة الشخص الواحد، فهي بالرعاية أولى.
وقوله: وهذا يتعلق بأحكام الإيالة، أي ما يؤول الأمر إليه من الفساد على تقدير منع الشريعة.

قال الإمام رحمه الله: (و<span data-type="title" id=toc-387>الضرب الثالث: ما لا يتعلق بضرورة ولا حاجة عامة) [</span>إلى قوله] (والصورة الممثلة). قال الشيخ: معنى قوله: ما لاح ووضح الندب إليه تصريحا، يعني بذلك أن أدلة الشريعة صرحت بطلب النظافة على وجه الندب والاستحباب، ووردت بإيجاب الطهارة في الحدث والخبث جميعًا على وجه الوجوب، [وأشارت] أن ذلك لتحصيل النظافة من غير تصريح بذلك. فلو اتفق مثلًا عدم التصريح بوجوب إزالة النجاسة،','3','510',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2142,'[ووجوب] الوضوء، [وأشارت] النصوص إلى التعليل بالنظافة؛ فلو أراد [ناظر] [إيجاب] إزالة النجاسة إلحاقًا بقاعدة الطهارة من الحدث الواجبة، كان ذلك في الدرجة الأخيرة من المرتبة الثانية البعيدة.
وقوله: [وجرى] وضع التلويح فيه، [يعني] [اكتفاء] بذلك، نظرًا منا إلا تلويح التعليل، وإن كان لم يصرح بأن المقصود النظافة، ومنع حمل الخلق على مضمونة وجوبًا، بناءً على أنه إنما ثبتت الأحكام للمصالح، ولم تظهر إلا مصلحة النظافة، وألزم الخلق ذلك في أوقات مخصوصة، مع الاعتضاد بالدواعي الجبلية المستقلة بمنع الإقدام في كثير من الأحوال، فوكل الشرع إلى الطباع قدرًا، وبينت الوظائف قدرًا. هذا تفسير هذا الكلام، [وسنعود','3','511',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2143,'إليه] بعد هذا، إن شاء الله تعالى.

قال الإمام رحمه الله: (و<span data-type="title" id=toc-388>الضرب الرابع: ما لا يستند إلى حاجة وضرورة) </span>إلى قوله (على تحصين الزوجين [كما تقدم]). قال الشيخ: هذا الضرب الرابع هو كالثالث، في أن المقصود منه تحصيل مكرمة، وإثبات المستحسنات، (٩٢/ أ) وهذه [المرتبة] قد تكون حسية، كنظافة الثياب والأبدان، وقد تكون معنوية، كفضيلة الحرية، وأن القرقيق خسيس الحال والمرتبة، حقير في القدر والمنزلة، فرغب الشرع في العتق، تحصيلًا لهذه الفضيلة، ولذلك منع الرقيق لخسته عن كثير من المناصب، كالقضاء والشهادة عند أكثر العلماء، ولا يكون إماما في صلاة الجمعة عند الكثير. كل ذلك لنقصان حالة وخسة قدره. فمن هذه الجهة اشتراك النوعان. غير أن الإمام جعل هذه رتبة متأخرة عن الثالثة، بناءً منه على أن الشرع [خرم] فيها قياسًا كليًا، فلأجل ضعف المعنى، لا لأجل كونه تكميلًا، و [خرم] القياس الكلي تأخر هذا الضرب عنده. وهذا إنما يكون كما قال، إذا','3','512',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2144,'ثبت أن العبد لا يملك، وتحقق أن الكتابة عقد معاوضة، ولذلك قال: ومقابلة السيد ملكه بملكه. فلا شك أن هذا خارج [عن] قياس المعاوضات، ولكنا قد قلنا: إن الكتاب ليست عندنا (٧٠/ أ) عقد معاوضة بحال. قال مالك رحمه الله: الذي يؤديه المكاتب، [إنما هو] من جنس [الغلات]. هذا هو الصحيح من المذاهب. [ويدل] عليه قول النبي - عليه السلام -: (المكاتب رق ما بقي عليه من [كتابته شيء]). [ولو] كان [بيعًا] على الحقيقة، لم يبق رقًا، وكان يعتق بمقتضى عقد المعاوضة، فأقل الأمور أن يعتق منه بقدر ما أدى. ولأجل فهم هذا السر، لم تفتقر الكتابة إلى نيةٍ بحال، إذ العتق غالب عليها، وظهور المقصود محقق فيها. نعم، الضرب الثالث خارج عن القياس خروجًا واضحًا، فإن إيجاب التوصل إلى مندوب إليه خارج عن القياس. والنظافة','3','513',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2145,'مندوب إليها، فكيف تجب الطهارة وسيلة إليها؟
وقول الإمام: لا يرد على النظافة إلا ذلك، يريد إن كون الكتابة ناقضة لقياس المعاوضات أبلغ من هذا. ومعنى قوله: إن ذلك بمثابة النكاح، يعني في وجوب الإجابة على الزوجة، وسقوط الوجوب عن الزوج، اكتفاءً بما في طبيعة الرجل من اقتضاء الإقدام، واكتفى الشرع في جانبها بذلك. كذلك اكتفى الشرع في حصول النظافة من حيث الجملة، بما في الطباع من الامتناع. والذي قد يتسامح به أمر الفترات، [فأوجبه] الشرع، وذلك في أوقات لم يعم الوجوب، لأجل المشقات. هذا معنى قوله: ليس فيها إلا ورود التلويح على التصريح.
وأما نحن فلا نرى الكتابة معاوضة محضة بحال، والغالب عليها أمر الاستغلال. وقد ذكرنا أنها مركبة من ثلاث شوائب: شائبة التعلي، وشائبة (٩٢/ ب) البيع، وشائبة الاستغلال. فرتب عليها من جهة شائبة المعاوضة، الافتقار إلى التراضي من الجانبين، على الصحيح من المذهبين. وقد قيل: إنه يجبره ترجيحًا لجانب الاستغلال. [وللتعليق] أثر في توقف العتق على','3','514',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2146,'أداء جميع النجوم. فلا يظهر على هذا التقدير خروج الكتابة بالكلية عن القياس. ولئن قيست على البيع المحض، فالبيع لا يساويها في مقتضاها. ولأجل ضعف المعاوضة فيها، لم يكن للسيد أن يتحاص غرماء المكاتب بالنجوم، ولا بما حل منها، وقال شريح: يحاصهم بنجمه الذي حل. قال ابن المسيب: أخطأ شريح.

قال الإمام: (و<span data-type="title" id=toc-389>الضرب الخامس) </span>إلى آخره. قال الشيخ: أما تقسيم المصالح إلى ضروري وحاجي وتحسيني وتكميلي، فلا زيادة عليه. [وقد] قدمنا الكلام على ذلك عند ذكرنا إثبات العلة بطريق الإخالة والمناسبة. وكون الأمر المصلحي قد يرد على قياس كلي صحيح. وقد كنا بينا أن الاستحسان يرجع إلى ذلك. وثبوت قاعدةٍ في الشرع لا يظهر فيها معنىً بالجملة، لا كلي ولا جزئي، [بعيد] جدًا، لا يتفق له وجود.','3','515',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2147,'وانحسام قسم الضرورات في القصاص والبيع، غير صحيح. وقد كنا قدمنا اضطرار الخلق إلى حفظ خمسة أمور: وهي الأديان والنفوس والأموال والأبضاع والعقول. فالخلق مضطرون إلى حفظ هذه الأمور على الدوام، ومنع التطرق إلى إتلافها على كل حال.
فجاءت الشريعة بالأدلة الموصلة (٧٠/ ب) إلى حفظ الأديان، وأوجبت صيانتها عن أهل الزيغ والأهواء، وقتل الكافر، والمبتدع الداعي إلى بدعته، الصاد عن الشريعة.
وشرع النكاح [تحصينًا] للنفوس.
ووجب القصاص إبقاءً للحياة، والخلق مضطرون إلى شرع المحصل ومنعٍ المفوت، وحرمت الفواحش من الزنا واللواط، وأوجبت الحدود تحقيقًا للصيانة، وحرمت الدواعي إلى ذلك، مع شدة [الإرب] عند التعرض.
وسوغت البيوع لتحصيل المحتاج إليه [والتثمير]. وحرمت الأسباب المفوتة [للأموال]، من الغصب والسرقة وغيرها. وانتهضت الحدود زاجرة','3','516',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2148,'عن بعضها، والتعزيزات مع المغارم في البعض. والخلق أيضًا بهم ضرورة إلى شرعية طريق التحصيل، ومنع طريق التفويت.
وكذلك بالناس ضرورة في صلاح دينهم ودنياهم إلى عقولهم، فوجب تحريم المفوت والزجر عنه، فورد الشرع (٩٣/ أ) بتحريم المسكرات على العموم. وعلى ما في النفوس من التشوف إلى ذلك، فإنه لا ينزجر بمجرد وازع الدين، وأثبت الحدود تكميلًا للصون، وتحقيقًا للامتناع.
والعجب من الإمام رحمه الله كيف قدم هذه المسائل في صدر هذا الباب، وتكلم عليها باعتبار المصالح، ثم قال: فنقول: هذا الذي ذكره هؤلاء أصول الشريعة، ونحن نقسمها خمسة أقسام. ولم يذكر في الضرورات إلا القصاص والبيع، وأهمل ذكر الحدود بالكلية، وأخرج قسم [الإجارة] عن قاعدة الضرورات. وقد بينا أن الصحيح اندراجها تحت [قسم] الضرورات، بل جعلنا نحن من قسم الضرورات تحريم المسكرات، فكيف في الإجارة؟
أما ما هو من قبيل الحاجات، فقد ذكرنا أن مثاله: التسليط على تزويج','3','517',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2149,'والصغير والصغيرة، فإنه لا ترهق إليه ضرورة طلب النكاح والتحصين، ولكن قد تدعو إليه حاجة اشتباك العشائر، وطلب التناصر بالتصاهر، وأمور من هذا الجنس.
وأما ما يتعلق بالتكميلات والتزيينات، فكمنع المرأة من تولي عقد النكاح، وكذلك اشتراط التزويج من الكفء، وبمهر المثل، وكرد شهادة العبد، وما يضاهي ذلك. وملتحق بذلك وجوب رفع الحدث وإزالة الخبث. هذا تفصيل الأصول على الجملة التي تظهر مصالحها، فإذًا هي ثلاثة أقسام بلا زيادة.
التقسيم الرابع: العبادات البدنية، فإن مصالحها الجزئية لا يطلع عليها، ولكن لا يبعد تخيل معانٍ كليةٍ فيها.
وأما الكتابة، فإن غلب عليها- ما يراه الإمام- من المعاوضة، فلا تكون ضربًا على حياله، إلا من جهة خروجها عن قياس المعاوضات على زعمه، فكيف تعد من قسم المكارم، مع القول بأنها معاوضة؟ فلئن قيل: من جهة أنها لا ترهق إليها ضرورة. فنقول: لا يطلب تحقيق الضرورة في آحاد أنواع المبيعات، بل لو باع الإنسان ما يحتاج إليه، واستبدل عنه ما لا يحتاج إليه، لصح ذلك. فلا يطلب تحقيق الضرورة في الآحاد. وإن غلب عليها أمر المعاوضة، فهي من قبيل الضرورات، وإن غلب عليها أمر العتق، (٧١/ أ) فهي من قبيل التحسينات والتزيينات. نعم، إذا غلب عليها أمر المعاوضات، فهي مضادة لقياسها، على القول بأن العبد لا يملك.
ولكن كون العبد لا يملك قضية شرعية، إذ ليس يتناقض في مقتضى المصالح، بأن يكون العبد مالكًا، فكون العبد لا يملك- إن صح- فهو من أبواب (٩٣/ ب) الأشباه. وهذا إنما يكون عند تحقيق كمال الرق فيه. أما إذا','3','518',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2150,'كوتب، فيصح أن يثبت له ملك على ضعف، [فمن] أبى حصول الملك للمكاتب، واعتقد تمليكه خارجًا عن القياس بالكلية، [إنما] شبه بالعبد القن على قوله، والأصل ممنوع [الحكم]. والفرق بين الأصل والفرع واضح لي، فلسنا نسلم بوجه أن مسألة الكتابة خارمة لأقيسة المعاوضات. فلا وجه [لعدها] قسمًا رابعًا.
قال الإمام: (فأما الضرب الأول، وهو ما [ينسب] إلى [الضرورات]) إلى قوله: (احتمل فيه الخروج عن قياس التماثل [لدى التقابل]). قال الشيخ: ما ذكره من اعتبار أجزاء هذا الأصل بعضها ببعض، فهو في أعلى درجات المعاني، إذ أجلى المعاني ما يرجع إلى الضرورة، وأجلى الأقيسة اعتبار بعض أجزاء الأصل الواحد ببعض. وليس يتنزل هذه المنزلة، ما إذا كان المعنى يرجع إلى الحاجة أو التكملة، ولا إذا كان المعتبر بعض أجزاء نوعٍ','3','519',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2151,'ببعض أجزاء نوعٍ آخر. فقد حصل في هذا القسم القوة من الجهتين جميعًا.
وأما قوله: ومن خصائص هذا القسم أن القياس الجزئي، وإن كان جليا، إذا عارض القاعدة الكلية، ترك القياس الجزئي للقاعدة الكلية. فهذا صحيح على الجملة. وتحقيق القول فيه: أن كل تكملة أفضى اعتبارها إلى رفض أصلها، وجب اطراحها. ويتقرر ذلك من وجين:
أحدهما- أن في إبطال أصلها إبطال التكملة. فتفويت مصلحة مع تحصيل أخرى، أولى من تفويتهما جميعا.
الثاني- أنه لو قدرنا [تقديرًا] أن المصلحة التكميلية تحصل مع فوات المصلحة الضرورية، لكان مراعاة حصول الضرورية أولى، لما بينهما من التفاوت.
وبيان ذلك بالمثال: أن حفظ المهجة مهم كلي، وتحصيل المزايا مستحسن، فحرمت النجاسات حفظًا للمروءات، وحملًا للخلق على [الأحسن] في العادات. فإن دعت الضرورة إلى إحياء المهج بتناول الشيء','3','520',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2152,'[النجس]، كان تناوله أولى. وكذلك أصل البيع ضروري، ومنع الأغرار واقع في أقسام الاستصلاح، فلو اشتراط نفي الغرر على الإطلاق، لانحسم باب البيع. فلم يرد الشرع بالمنع من مطلق الغرر. وكذلك القول في بقية [الشريعة].
إلا أن قول الإمام: إن هذا من خصائص هذا الضرب، غير صحيح، فإن الإجارة يثبت فيها مثل ذلك. فإن قاعدة المعاوضات ضرورية، على ما قررناه. واشتراط حضور العوضين من أبواب التكميلات (٩٤/ أ). ولما كان ذلك ممكنا في بيع الأعيان بلا عسر ولا صعوبة، منع من بيع المعدوم، إلا على [جهة] السلم في الذمة.','3','521',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2153,'وأما البيع من غير أن يصادف معينا ولا ذمة، فغير صحيح. والإجارة ضرورية عندنا، وحاجية عند الإمام. فاعتبار حصول كمال المنع، حتى يشترط فيه الوجود، [سد] باب (٧١/ ب) المعاملة، وأن المنافع لا يمكن حصولها في الوجود، حتى يقع العقد عليها، فلما أفضى اعتبار هذا القياس الجزئي إلى إبطال القاعدة، وجب اطراحه. وسيأتي لهذا مزيد تقرير. وإنما غرضنا الآن أن الذي ذكره، ليس من خاصية قاعدة الضرورة عنده.
وما ذكره من المثال، صحيح، واشتراط التماثل يوجب أن لا تقتل الجماعة بالواحد، وفي المصير إلى ذلك خرم أصل الباب، فوجب اطراح التكملة للأصل [المهم]، وإهمال التماثل ليس يخرم أمرًا ضروريًا، واشتراطه يخرم الأمر الضروري، على حسب ما قررناه قبل هذا. فوجب الإضراب عن اشتراط التماثل، إن مست الحاجة إليه، ولزم من اعتباره خرم أصل الباب.','3','522',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2154,'وما فسره الإمام من كون هذا جزئيا، تفسير حسن، وأراد به أنه كالتتمة للقاعدة.
والتعبير عنه بكونه تتمة للقاعدة، أدل على الغرض، وأبعد عن اللبس من التعبير بكونه جزءاً، ولكن إذا فهم المعنى، فلا مشاحة في اللفظ.
وما ذكره من حكمة القصاص، وأنه ليس [يطلب] منه رد فائتٍ، وإنما مقصوده الزجر، وحظ مستحقه [منه] شفاء الغليل. يريد بذلك أنه لا يلزم من اعتبار المماثلة في إتلاف [الأموال]، [اعتبار المماثلة في إتلاف النفوس]. هذا وإن كان كلامًا حسنًا في فهم سر التفاوت بين القواعد، [فلا] يتوقف مقصود المسألة عليه، ولا حاجة إلى تكلف الفرق بين البابين، بعد أن ثبت أن اعتبار [التماثل] يخل بأصل القاعدة. ولكن ما ذكره هو جواب عن تخيل إشكال في لزوم إيراد إشكالٍ، [فتعرض] للجواب.','3','523',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2155,'وأما قوله: فلما خرج أصله عن مضاهاة الأعواض هذا الخروج، احتمل فيه الخروج عن قياس التماثل لدى التقابل. وهذا أيضا زيادة [تقرير]. ومقصود المسألة غير متوقفٍ عليه عند التحقيق.
قال الإمام رحمه الله: (وإذا قسنا الأطراف عند فرض الاشتراك) إلى قوله (في الضرب الأعلى من القياس). قال الشيخ: مقصود الإمام بهذا الكلام، أنه لا ينبغي أن يعتقد أن مخالفة التماثل في قتل الجماعة بالواحد، يقتضي خروجه عن القياس، حتى يمتنع القياس عليه، بل هو جارٍ على قياس (٩٤/ ب) أصل القاعدة. وفوات التكملة حفظًا لأصل القاعدة، جلي منقاس. وقد ذكرنا تقريره. فإذا وضح جريانه على المعاني، أمكن القياس عليه، إذا [اتسق] الجامع، فيجوز حينئذ قياس الأطراف على النفوس. وليس المراد بالجائز: [ما يسوغ] فعله وتركه، فإن ذلك باطل في القياس، بل متى تأتى القياس على شرطه في الشريعة، وجب المصير إليه. فلا يقدح في إلحاق الأطراف بالنفوس، في قضية الاشتراك، خروج قتل المشتركين عن قياس التماثل، على ما ذكرناه. ويعتضد','3','524',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2156,'ذلك باستواء المحلين في شرعية القصاص، تحقيقًا للصون، فيظهر تساويهما في الباب. فمن هذه الجهات كان ذلك معنىً جليًا معتضدًا بالمعنى الأصلي.
وقول الإمام: ولا [يغمض] معه شيء إلا فرض صدور القطع من شخصين، مع تميز كل واحد منهما عن الثاني، فإن ذلك، إن جرى، لا تقطع يد واحدٍ منهما إلى آخره. ووجه تقرير الإشكال: أنا قدرنا أن التماثل إذا أفضى (٧٢/ أ) اعتباره إلى خرم أصل الباب، وجب الإعراض عنه، حتى نزلنا الشريك كالمنفرد في توجه القود عليه، فما بالنا لم ننزل الشريك كالمنفرد في قطع يده، كما نزلناه منزلته في إتلاف نفسه؟ هذا وجه غموض المسألة على الإمام، وكأنها تهدم القاعدة [المقرة]، ولم ينقدح له عنها جواب. قال: والمصير إلى أنه يقطع من يد كل واحدٍ من الجانبي قدر ما قطعه من يد المجني عليه. قال: فهذا انفصال على وجهٍ. والانفصال أيضا ضعيف، فإنه يجعل الجرح عوضًا عن القطع، وهذا لا يتحصل منه [الحكم] المرعية في القصاص.','3','525',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2157,'قال الإمام بعد هذا: وهذا محل غامض، ويتوجه فيه خلاف العلماء. وإذا ثبت لأبي حنيفة سقوط القصاص عن المشتركين في القطع في بعض الأحوال، أخذ- كون المجني عليه غير قابل للتمييز- قيدًا [في التعليل]، [فيمنع] إلحاق الأطراف بالنفوس.
[وقول الإمام: ] [فقد] يتناوش [المتناظران] [عند ذلك الكلام، ويتجاذبان] أطراف [النظر]، يشير إلى أن الشافعي يقول: يثبت القصاص في الموضع الذي لا تمييز فيه، ويقول أبو حنيفة: إذا كان المحل قابلًا للانقسام، بعد فيه تنزيل الشريك منزلة المنفرد، إذ إنما ثبت ذلك فيما لا يقبل الانقسام بوجه. فهذا معنى قوله: يتجاذب المتناظران الكلام.
والصحيح عندنا أن هذا غير لازم أصلًا، والدليل عليه أنه لو تمالأ','3','526',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2158,'شخصان على فقء عيني شخصٍ، وتولى كل واحدٍ (٩٥/ أ) منهما فقأ عين، لم يتصور أن يفقأ عينا كل واحد، وإن تمالآ على إتلاف بصره جملة. والسبب في ذلك، أن الشرع لم جعل الإبانة عوضًا عن الجرح، ولا العمى عوضًا عن العور، ولا مقابلًا له. فإذا أناط الشرع حكمًا [بحكمة]، وجب [اعتبار] تلك الحكمة في تعدية ذلك الحكم. فأما أن نرتب نحن حكمًا آخر، مخالفًا للحكم على تلك المصلحة، فلا سبيل إلى ذلك.
وإذا جرح رجل رجلًا جرحًا، وكان عند الجارح جرأة بحيث لا ينجز إلا بقطع يده، لم يصر أحد من الأمة إلى جواز ذلك في حقه. وكذلك في سائر الجنايات. وإذا نزل الشرع الشريك منزلة المنفرد في الموضع الذي لا يثبت فيه التمييز، أمكن أن يقال: هم بجملتهم قاتلون، لاستواء نسبة القتل إلى فعل الجميع، وكذلك القطع، إذا تمالأوا على حديدة واحدة. [أما] إذا قطع كل واحد من ناحية، فالحاصل من كل واحدٍ جرح معين، فكيف يصح [أن تقطع] يد الجارح؟ وليس إلينا وضع الحكم، ولكن إذا وضعها الشرع اتبعناها. وسيأتي لهذا مزيد تقرير في كتاب الترجيح، إن شاء الله تعالى.
قال الإمام رحمه الله: (ولو أراد [القائس] أن يعتبر قاعدة أخرى','3','527',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2159,'بقاعدة) إلى قوله (والجامع الضرورة الكلية). قال الشيخ أيده الله: ما ذكره الإمام من صحة اعتبار قاعدة بقاعدة ضرورية، كلام على التقدير، لا بمعنى [أن] ذلك محتا إليه في الشريعة، فإن الشريعة مشتملة على تقدير القواعد الضرورية، وكيف لا يكون ذلك، والله تعالى يقول: {[اليوم] أكملت لكم دينكم}؟ وما يضطر إليه أهل عصر، فإنه يضطر إليه أهل كل عصر، وقد بقي رسول الله - صلى الله عليه وسلم - بين ظهراني [الناس] مدة تزيد على عشرين سنة، فكيف يصح (٧٢/ ب) أن تبقى قاعدة ضرورية لم يثبت حكمها، ولم يبين أمرها؟ فهذا كلام مقدر غير واقع، ولا يقع. نعم، قياس بعض فروع القاعدة على بعضها، يصح ويحتاج إليه. فأما اعتبار القواعد، لاسيما [الضرورية] بعضها ببعض، فهذا مما لا يكون بحال.','3','528',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2160,'قال الإمام: (وأما الضرب الثاني: وهو ما [ينبني] على الحاجة) إلى قوله (فيكله إلى فاطر البرية). قال الشيخ: ما ذكره أيضا من قياس قاعدة حاجية على قاعدة أخرى، كلام مقدر، غير واقعٍ في الشرع على حال. والطريق فيه ما ذكرناه، والاستقراء يرشد إلى ما حققناه؛ فإن القاعدة الحاجية عنده هي الإجارة، والإجارة (٩٥/ ب) ثابتة [في الشريعة] نصًا [وإجماعًا]. فأين','3','529',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2161,'تصادف قاعدة أخرى حاجية حتى تعتبر بالإجارة؟ أم كيف تتغير القواعد وتنخرق، حتى يحتاج الناس إلى غير ما احتاج إليه أهل العصور الخالية في الأزمنة المتمادية؟
وقوله: فهذا امتنع منه معظم القائسين، ما أدري ما معناه؟ إلا أن يكون القوم تكلموا على التقدير دون الوقوع، فهذا له وجه. أو يكون الكلام وقع في بعض أجزاء القاعدة، فتوهم أنه قاعدة أخرى. وهذا بمثابة قياس القراض على المساقاة، أو بالعكس من ذلك. فهذا تفصيل قاعدة الإجارة.
وأما ما ذكره الإمام وقدره [سببًا للاختلاف]، فقد قلنا إنه لا يتصور ما ذكره، حتى يقع الخلاف فيه. فأما كون الإجارة جاءت خارجة عن قياس البيوع، فهو كذلك، ولكن مصلحتها لا تقتضي إلا ذلك. وفي التزام اشتراط الوجود في','3','530',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2162,'الإجارة، إبطال القاعدة. وقد تقدم أنه إذا لزم من اعتبار التتمة إبطال [الأصل]، وجب إبطال التتمة والتكملة، مراعاة لحفظ الأصل.
وقد ذهب قوم إلى أنه لا تجوز الإجارة، نظرًا إلى هذا الخيال، وهو خلاف الإجماع، ومخالفة الكتاب والسنة وقضايا الفقه والمعنى. وقال قائلون: إنها صحيحة، ولكنها خارجة عن القياس، نظرًا منهم إلى اشتمالها على بيع المعدوم، وبيع المعدوم على خلاف الأصل. وهذا قول عندنا في المذهب مأخوذ بالاستنباط من قولٍ لمالك في منع اجتماع الإجارة والبيع. ولكن لم يتأت له معها الأدلة الدالة على التجويز، فسوغها ومنع اجتماعها مع البيع. وهذا مطرد عنده في كل عقدين متبايني الخواص، يمتنع اجتماعهما في عقدٍ واحدٍ،','3','531',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2163,'كالبيع والنكاح، والبيع والقراض، والبيع والشركة، وكذلك الجعل. وكذلك يمنع الإجارة مع هذه العقود الخارجة عن القياس.
والسبب في ذلك اتحاد العقد، فلا يثمر العقد المتحد الأحكام المتناقضة، ولا يصح أن يتعدد العقد بتعدد المعقود بتعدد المعقود عليه، فلا يقال هو رخصة باعتبار القراض، وعزيمة بالنظر إلى البيع، إذ لو كان كذلك، لوجب أن يفسد العقد إذا تناول سلعًا متعددة لمالك واحد، [إذ يتضمن] ذلك أن يكون ثمن كل سلعة مجهولًا، وقد وقع الاتفاق على صحة ذلك. فهذا يدل دلالة واضحة على أن العقد لا يتعدد بتعدد المعقود عليه. وإذا اتحد العقد، امتنع أن يترتب على (٩٦/ أ) المتحد الأحكام المتناقضة. (٧٣/ أ) ولا يلزم على ذلك البيع والسلم في عقد واحد، إذ الكل [بيع]، الثمرات متفقة، وإن كان السلم فيه رخصة، من جهة [أنه] بيع ما في الذمة، ولكن ثمرة البيع واحدة. وكذلك لا يمتنع اشتمال العقد على سلعتين، إحداهما اشتريت بيع بت، والأخرى بيع خيار، لما ذكرناه من الاشتراك في خاصية البيع، ووقع الافتراق في غير ذلك.
[والصحيح] أن الإجارة على ذوق القياس الصحيح، ولا [يتأتى]','3','532',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2164,'أن تشرع إلا كذلك، [وما فات منها] من حصول المنافع [لدى] العقد، لا يمنع من صحة العقد، تحصيلًا للحاجة.
وما ذكره الإمام رحمه الله من المثال التقديري، قصد به أن يبين تفاوت المصالح، ليحصل مقصوده من الكلي والجزئي، [فعبر بالكلي عما يظهر للعقلاء، وإن قدر دروس الشرائع، وعبر بالجزئي] عما لا يظهر تعيين المقصود منه إلا بالشرع، وذلك أن جهات الاستصلاح تتعارض.
وبيانه: أنا إذا نظرنا إلى البيع الذي يتطرق إليه نوع من الغرر، وقلنا قد ينشأ [نزاع] وخصام، بالنظر إلى الأغرار، فليمتنع حسمًا للنزاع، لزم في مقابلة ذلك فوات مصلحة الأرباح، ولا يحاط بما في الغيب من الترجيح، فيتوقف العاقل حتى يرد الشرع بتعيين أحد الأمرين، فيظهر حينئذ الرجحان.','3','533',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2165,'ويكاد أن يكون مستنده غيبا لا يطلع عليه. وكذلك القول في جميع الاستصلاحات. فأراد بالكلي: ما يتطرق العقل إليه مع نسيان التفاصيل.
قال الإمام رحمه الله: (ثم قد يظهر الاستصلاح، وهو مع ذلك جزئي) إلى قوله (والحجر [المتمهد] في ربا النساء). قال الشيخ: معنى هذا الكلام: أنا إذا منعنا بيع الغرر مثلًا، حذرًا من كون المبيع لا يحصل مطلقا، [أو لا] يحصل على صفةٍ توافق، فلا تكون المالية مضبوطة، فمن المناسب المنع، إبقاء للأموال على الملاك، وحفظًا لها عن الإضاعة، وتحقيقًا للمعاوضة على الحقيقة. فيكون هذا كلامًا في صيانة الأموال على المالكين. وهذا مقصود الشرع من شرع هذه [القواعد]. ويرشد إلى ذلك الكتاب والسنة، قال الله تعالى: {ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قيمٍا}. وقال النبي - صلى الله عليه وسلم -: (إن الله نهاكم عن قيل [وقال] وكثرة السؤال وإضاعة المال). فقصد الشرع','3','534',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2166,'حفظ الأموال على الملاك. ولهذا السر أثبت الشرع الأوصياء على الأطفال والمجانين.
وأورد إشكالًا يتخيل منه (٩٦/ ب) إفساد هذا المعنى، وذلك أنه قيل له: لو كان سبب المنع في بيع الغرر وغيره حفظ المال على الملاك، لاحتمال النقصان، وفوات تمام الأغراض، حفظًا للأموال على الملاك. قيل: لو كان الأمر كذلك، لما أجاز الشرع الهبة والبذل العري عن العوض، فإذا أمكن في الهبة، وهو إضاعة المال بالكلية، فكيف [يضايق] في بيع الغرر لاحتمال الغيبة؟ أجاب عن ذلك بقوله: [ضنة] النفوس وازعة عن البذل العري [عن] العوض، فاكتفى الشرع بذلك عند وفور العقل، بخلاف الانكفاف عن الغرر في البيع، فإن شح النفوس يحمل على ذلك، فينشأ من ذلك أغرار وأخطار. فإذًا الداعي إلى الغرر موجود، والوازع عنه موجود، والداعي [في]','3','535',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2167,'الغائب مفقود، فاكتفى الشرع بذلك.
ونظيره ما تقدم من اكتفاء الشرع بما في النفوس من [الصوارف] عن التضمخ بالطين، فلم (٧٣/ ب) يجوب الاحتراز عن ذلك، وإنما الذي يتسامح فيه أهل المروءات ما يتعلق [بمصادمة] الغبرات، فأوجب الشرع الوضوء في بعض الأوقات، تحصيلًا لأصل [النظافة]، ولم يوجبه في كل الحالات، لما يلحق [الخلق] في ذلك من المشقات. وهذا باب من محاسن الشريعة.
وقول الإمام: وقد يغيب كلي الاستصلاح [وجزئيه] إلى آخره. فنقول: قد اختلف الناس في مسألة الربا اختلافًا كثيرًا، هل تحريمه معلل [بمعنى] مناسب]؟ أو واقع في أبواب الأشباه، أو مقتصر على التوقيف والتعبد','3','536',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2168,'[الجامد]؟ وقول الإمام فيه مضطرب، وقد تقدم له أنه من أبواب الأشباه، ورجع في هذا المكان إلى التعبد. ولا يبعد إظهار مناسبة فيه؛ وذلك أن هذه الأقوات بها قوام [الأبدان]، الذي هو شريف عند الله - عز وجل -.
ويظهر السر فيه بأن لا يجعل عرضة للأسواق، ولا محلًا للتجر. ألا ترى أن الحر لما شرف في نظر الشرع، أبطل الشرع القيمة، وقدر فيه الدية، حذرًا بأن يتصور [بصور] الأموال التي تغلو أسواقها وتنحط أسعارها. ومتضمن هذا أن لا تباع الأقوات، ولا تجعل [محطا] للتجارات، غير أنه لزم من ذلك ضرر آخر، وهو تعذر الوصول إليها مع تعذر الاستغناء عنها، فشرع الشرع بيعها لهذه الضرورة.
أما إذا كان الإنسان مالكًا [لجنس]، فلا ضرورة [تدعوه] إلى المعاوضة بجنسٍ، إلا طلبًا للتجر ومقصود الربح، [أو زيادة] رفاهية، (٩٧/ أ) فأعرض الشرع عن ذلك، [إظهارًا] للشرف، وإثباتًا للحرمة، فقال: جيدها [كرديئها] سواء، فلم يشرع [المعاوضة]، تحصيلًا للربح، ولا','3','537',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2169,'طلبًا لمزيد الترفه، وإنما شرعه لضرورة تحصيل الأجناس، لتعذر الاستغناء [ببعضها] عن بعض.
أما إذا اتحد الجنس، فلا ضرورة إلى التبادل. وكان مقتضى هذا أن لا يشرع التعاوض أيضا مع التماثل، وهو مشروع. لكن لما كانت الدواعي لا تتحرك إلى ذلك، والصوارف تمنع منه، اكتفى الشرع في الانكفاف بذلك، لعدم الداعية إليه، على حسب ما قررنا فيما سبق من التضمخ بالطين، والبذل العري عن العوض في الأموال. وذلك أنه إذا استوت الصفات، فلا غرض في التبادل على الحال. وإذا تفاوتت الصفات، فصاحب الجيد [يضن] به غالبًا، فاكتفى الشرع بذلك.
والإمام إنما قال: وقد يغيب كلي الاستصلاح وجزئيه، بناءً على التعبد. وما قررناه يضعف قوله، ولكنه يقول: [وإن] لم يظهر للعقلاء وه المصلح في ذلك، فهي [مغيبة]. وظاهر قوله أن المصلحة ثابتة، ولكنها لم تظهر، ولذلك قال: وقد يغيب كلي الاستصلاح وجزئيه. ولم يقل [يعدم].
وهذا أيضا [يلتفت] إلى أصل، وهو جواز عرو بعض الأحكام عن المصالح. والذي تقدم له في قياس الشبه صحة الخلو، ذكره عندما قال القاضي في حد الشبه: إنه الذي يوهم الاشتمال في مخيل، فقال: لا يصح ذلك، فإنا','3','538',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2170,'نجري قياس الشبه فيما لا يعقل معناه، تقريبًا له من الذي يقال إنه في معنى الأصل. ثم قال: فإذا كان قياس الشبه يجري حيث لا معنى، فلا وجه لضبطه بالإشعار بالمعنى. وقد كنا نحن بينا ضعف هذا الكلام فيما سبق. والمقصود منه الآن بيان التناقض في ظاهر الحال (٧٤/ أ).
فأما ربا النساء، فهو غير مختص بالجنس، بل يجري في [الجنسين] من الطعام عندنا. وقد اختلفوا فيه، هل هو معلل، أو متلقى من التوقيف، وهو قوله: (لا تبيعوا الطعام بالطعام إلا يدًا بيد)؟ والصحيح عندنا أنه جارٍ على الأصل السابق، وذلك أنه إذا قوبل الطعام بالطعام، وهو خلاف جنسه، لم تدع الضرورة إلى الطعامين جميعا، إذ التأخير في الذمة يدل على عدم الضرورة. وقد [كنا] بينا أن البيع في الأطعمة، إنما شرع للضرورة، والضرورة مفقودة عند التأخير فيهما [أو في] أحدهما، يحرم النساء لأجل ذلك في الطعام بالطعام.
قال الإمام: (ومن دقيق ما يجري في هذا الفن، وهو العلق','3','539',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2171,'[الأنفس]) إلى قوله (٩٧/ ب) (فهذا كافٍ في هذا الضرب). قال الشيخ وفقه الله: إلحاق الشافعي الخيار بأبواب الرخص، كلام صحيح، وبيانه: أنه البيع شرع لدفع الضرورة أو الحاجة، على حسب ما ذكره، وإقدام العاقل على البيع دليل احتياجه إليه، فلذلك اقتضى مطلق العقد اللزوم، وبيع الخيار غير لازم، [فالملك] فيه غير منعقد، [فقد] وجد العقد فاقدًا للمقصود الذي شرع له من عدم الملك [المحتاج] إلى انتقاله. فقد تبين [بهذا] أن قاعدة','3','540',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2172,'البيع تقتضي [ترتيب] الملك على العقد. والصحيح عندنا أن بيع الخيار لا [يعدم] الرضا بالنقل في الحال. فعلى هذا يكون البيع واقعا، غير محصلٍ للمقصود منه، فلا يخفى خروجه عن القياس لمصلحة اقتضت ذلك، وهو اختيار المبيع والتروي في البيع.
وإن قيل: إن بيع الخيار ينقل الملك، فالخروج عن القياس- على هذا القول- أظهر، من جهة أن البيع يفيد ملكًا مطلقًا مؤبدًا، فكأنه [عقد أفاد]','3','541',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2173,'الملك على التأبيد، مشترطًا فيه الحل، إن أراد أحد المتعاقدين، فلم يدر المتعاقدان، هل يتأبد الملك أم يتوقف؟ والبيوع على خلاف [ذلك].
وما ذكره الإمام من كون المبيع مستندًا إلى الضرورة أو إلى الحاجة العامة، صحيح لا [تنازع] فيه، وانتقال الملك بمطلق العقد مصلحي تكميلي، وهذه التكميلات اعتبارها من المناسبات، [إلا إذا] هدمت القواعد الكليات، فيطرح حينئذ [التكميلي]، حفظًا للكلي. أما إذا كان اعتبار التكميلي لا [يخرم] أمرًا ضروريًا، فاعتباره مناسب جلي. ونحن [نجزم] إذا حكمنا أن مقتضى البيع اللزوم، وأوجبنا ذلك، ولم يشرع الخيار بحال، لم ينحسم باب البيوع، ولم يبطل أصل القاعدة. وليس هذا بمثابة إسقاط القصاص عن الشركاء. فإن ذلك لو قيل به، لأفسد قاعدة الدماء. فالصحيح إذًا خروج بيع الخيار عن الأصل، ووقوعه في أبواب الرخص.
والدليل على ذلك، اتفاق العلماء قاطبة على أنه لا يسوغ أن يشترط في','3','542',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2174,'البيع الخيار الطويل، الذي لا يحتاج إليه المبيع. أما الشافعي وأبو حنيفة [فيقصرانه] على ثلاثة أيام، فلا [يجوزان] الزيادة على [ذلك] يحال. وأما مالك، فإنه يجوز ما يحتاج إليه المبيع، وذلك يختلف باختلاف المبيعات. فأما الزيادة على ذلك، فلا تجوز عند الجميع. ولو كانت على مقتضى القياس، لجاز اشتراطها. بل [لو] قال أكثرها أهل العلم إن البيع إذا اشتمل على خيارٍ لا يسوغ، أثر ذلك في فساد [العقد]. (٩٨/ أ)','3','543',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2175,'وأبو حنيفة (٧٤/ ب) يزعم أنه يؤثر في الوصف لا في الأصل. وقد تقدم مراده بذلك، وضعف [مقالته]. ويقول: إذا أسقط خيار اليوم [الرابع]، قبل الدخول فيه، [صح] العقد. وأما بعد الدخول، فيتحتم الفسخ، لتعذر الإسقاط، لأن غايته أن يلتزم البيع، وذلك مقتضى الخيار [أن يمكن مما اختار، فإن اختار الإمضاء، فقد حصل مقتضى الخيار]. فلذلك منع من الإسقاط بعد دخول اليوم الرابع.
وأما بيع [الأجل] عندنا، فالصحيح أنه ليس من الرخص، وإن كان الحق يصادف الذمة، ف المعاملة على الذمة صحيحة، بدليل انعقاد الإجماع في الشراء [بالدنانير والدراهم] صحيح، وإن لم تنقد لدى العقد. فإذا','3','544',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2176,'صحت المعاملة على الذمة، فالبيع بالثمن المؤجل [أو السلم] معاملة على الذمة. وقد بينا صحته، وأن المصلحة أيضا لا تنافيه، [إذ] لم يبق إلا عدم القبض في الحال. وقد تدعو مصلحة المعاوضة إلى تنجيز الملك، تحصيلًا لمصلحة ذلك، وخوفًا من التفويت. ولكن هذا مشروط بتنجيز قضاء حاجةٍ من أحد الجانبين، ليحصل مقصود العقد من الحاجة الداعية إلى المعاوضة. ولذلك منعنا من الدين بالدين، على ما جاء في الحديث أنه: (نهى عن الكالئ بالكالئ). [إذ] لم يحصل مقصود من أحد الجانبين.
[وفيه أيضا] وجه آخر، [وهو] كثرة الغرر عند الدخول على الذمة','3','545',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2177,'في العوضين. ولا يلزم إذا كان كثير الغرر يؤثر منعه، أن يؤثر قليله ذلك.
فإن قيل: [فقد] أخرتم السلم ولم تشترطوا قبض العوض لدى العقد، وهو الدين بالدين المنهي عنه. قلنا: قد اختلف الفقهاء في ذلك، والظاهر من مذهب مالك جواز تأخير رأس السلم بشرط اليوم واليومين. وهذا وإن كان ظاهره أنه الدين بالدين، ولكن هذا التأخير القريب لا يخرج البيع عن كونه بيع نقدٍ، فكأنه رحمه الله ينزل الحديث على ما يعتاد من بيع الديون. وهذا القدر لا يعد في العرف بيع أجل، بل له حكم النقد. هذا وجه تقرير الظاهر من المذهب.
وجميع ما بناه الإمام إنما هو راجع إلى اتباع الرضا، بمثابة شرعية القصاص. ويجعل البت والقبض بمثابة التماثل. فإذا كان الجزئي يعرض على الكلي، وجب إسقاط الجزئي. وهذا إنما يكون كذلك، لو أعرضنا عن الرضا على الإطلاق، فحينئذ ينحسم أصل الباب. أما إذا منعنا وجهًا من وجوه الرضا، لمصلحة اقتضت ذلك، لم يقتض ذلك بطلان الأصل بحال، وهو بمثابة [ما] إذا أسقطنا القصاص (٩٨/ ب) في بعض الأحوال، وهو شبه العمد مثلًا، [أو عند] عدم [تحقق] المماثل، كمنع القصاص بين الحر والعبد، والمسلم','3','546',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2178,'والكافر، فلا تنحسم القاعدة بسبب الامتناع من شرعية القصاص في هذه الأمثلة.
وأما ما ذكره الإمام من دروس الشرائع وتفاني النقلة، وعسر الاطلاع على التفصيل، [وانحسام] مسلك الظن، فالتكليف ساقط على الوجه المذكور، وما بقي من الشريعة معلومًا أو مظنونًا، فالتكليف به قائم، وما درس، سقط التكليف به، سواء كان ذلك المعلوم كليًا أو (٧٥/ أ) جزئيًا. وكذلك القول في المندرس.
نعم، دل القرآن على جواز الاعتماد على الرضا بقوله: {[لا تأكلوا] أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراضٍ منكم}. وكذلك دلت السنة على ذلك بقوله - صلى الله عليه وسلم -: (لا يحل مال امرئ مسلم إلا عن طيب قلب منه). ولكن هذه الظواهر مشروطة عند العلماء بشروط كثيرة دقيقة. فإذا التبست الشروط، لم يصح الاعتماد على هذه الظواهر. وكذلك القول في كل دليل مشروط عند التباس شروطه، يمنع التمسك بمطلقه. وإنما هذا الكلام فيه يرجع إلى قانون السياسات في المصالح المعاملات، غير مرتبطة بالأدلة. فلا معنى له في هذا المكان في بيان خروج الخيار عن الرخص بحال. والله الموفق للصواب.
قال الإمام رحمه الله: (وأما الضرب الثلاث: وهو ما لا [يستند] إلى','3','547',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2179,'ضرورة ولا إلى حاجة) إلى قوله (فإن [أمرهما] [هين] [ودركهما] سهل). قال الشيخ: ما ذكره الإمام في هذا الضرب الثالث من تحصيل مكارم الأخلاق، وإزالة الأخباث، ورفع الأحداث، كلام [فيه عندنا تفصيل]. أما إزالة النجاسة، فهي مبنية على معنى النظافة بلا استرابة. ولذلك قال المحققون من العلماء: إنها لا تفتقر إلى النية، لظهور معناها. وإذا ثبت الحكم ولاح للمستنبط فيه معنى، فليسند الحكم إلى المعنى الظاهر، إذا أمكن، وذلك أولى من التنزيل على التعبد الجامد. وقد ظهر مقصد النظافة في إزالة النجاسة، لكن يبقى أمران:','3','548',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2180,'أحدهما- في تعين الماء لذلك، وقد خالف فيه أبو حنيفة، وقال تجوز الإزالة بكل مائع طاهرٍ، مزيلٍ [للعين] والأثر، طردًا للمعنى المفهوم على ما قررناه. وهو بناه على نفي التعبد على الإطلاق. وقد اختلف أصحابنا في الجواب عن ذلك، وقالوا: لا [بعد] في ثبوت التعبد في [جهة]، [و] التعليل [في] جهة (٩٩/ أ) أخرى. ومثلوا ذلك بما قدمنا ذكره من نصب الزكوات، ومقدار ما يقطع فيه، وتعين مقادير الحدود، فإنه يرجع إلى','3','549',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2181,'التعبد، مع فهم معنى أصل الحكم. كذلك قالوا: إزالة النجاسة معقولة المعنى، والاقتصار على الماء من التعبد المحض. وذهب آخرون إلى [أن لا] تعبد في الباب، ولا في تعين الماء، وإنما عين الماء للنظافة في الخلقة، وانفرادٍ في التركيب، فهو مزيل، بحيث لا يشاركه غيره في ذلك، وهذا هو الصحيح عندنا. ولذلك أن أهل العرف لا يعدون للغسل سواه، فكان اشتراطه لذلك لا للتعبد.
وأما الأمر الثاني- الذي يشكل على فهم المعنى، فاختصاص وجوب الإزالة بحالة الصلاة، وعنه أيضا ثلاثة أجوبة:
أحدها- أنه لا يلزم من إبهام وقت الإيقاع، أن لا يفهم معنى الأصل. ويقال فيه كما قيل في تعين الماء.
الثاني- إن وجوب الإزالة على العموم متعذر، والإضراب عن الأزمان على الإطلاق مناقض للمروءات. ولا ينضبط الوقت الذي في بتحصيل المصلحة، فعينت الشريعة أوقاتًا. ونظير هذا ما (٧٥/ ب) ذكرناه في إرشاد','3','550',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2182,'العقول إلى إرفاق الأغنياء الفقراء، والقدر [المعين] لا ينص عليه الرأي، فاتبع القائسون موجب الشريعة في النصب، وإن عللوا الأصل تعليلًا كليا.
الثالث- إن الصلاة اختصت بوجوب الإزالة، لأنها قربة مثلت بالإضافة إلى قرب الشريعة، بمثابة الوقوف بين يدي الملوك، وإن كان الله سبحانه [وتعالى] لا يخفى عليه شيء، ولكن أثبتت العبادات على حسب أوامر السادات. والعبد وإن كان مترددًا في خدمة مولاه، إلا [أنه] يكون له عند وقوفه بين يديه حالة لا تثبت له عندما يكون غير واقف بين يديه. وقد أرشد إلى ذلك الصلاة، في استقبال جهة مخصوصة، وتحريم الكلام، وما كثر من الأفعال، و [التزام] الذكر دون ما سواه من الأقوال.','3','551',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2183,'وقد أرشدت إلى ذلك سير النبي - صلى الله عليه وسلم - فقال: (إذا كان أحدكم يصلي، فلا [يتنخمن] قبل وجهه، فإن الله تعالى قبل وجهه). وقال - عليه السلام -: (إذا كان أحدكم يصلي، فإنما يناجي ربه، فلينظر بما يناجيه). هذا معنى الحديث دون لفظه. فأرشدت هذه الأدلة إلى اختصاص هذه العبادة بمضاهاة الوقوف بين أيدي الملوك. فكل هذا يتوجب ستر العورة، وإزالة النجاسة، تحصيلًا لمكارم','3','552',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2184,'الأخلاق. [ولذلك] إذا لم يجد إلا ثوبا نجسا صلى [فيه]، لأنه أقرب (٩٩/ ب) من الصلاة عريانًا. هذا هو القول الصحيح. وقد قيل غيره، وليس بصواب على ما قررناه.
وأما طهارة الحدث، فهي لعمري مشكلة، والتعبدات فيها كثيرة، ودخول التيمم بدلًا عن الماء، وانتقاض الوضوء بمس الذكر، وملامسة النساء. فهذه أمور كثيرة تدل على التعبد. وفيها أيضا نوع من المعنى فيما يرجع إلى النظافة، فلذلك كان القول الصحيح تغليب التعبد، والافتقار إلى النية. وفي المسألة قول آخر تغليبًا لجانب المعنى. وهذا هو قول أبي حنيفة. وإن قلنا بالتعبد، فلا إشكال في تعين الماء. وإن قلنا بالمعنى، كان الجواب عن تعين الماء، على حسب ما قررناه في إزالة النجاسة. غير أن الإمام رحمه الله جنح في قياس','3','553',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2185,'الشبه، عند الكلام على النية [في إلحاق] الوضوء بالتيمم، إلى تغليب التعبد، وأن الوضوء ليس له غرض عاجل. ورتب على ذلك أنه كالتيمم في انحصار الغرض [في] العقبى، فلزم اشتراط النية فيه. وهذا الكلام [مناقض] لما في هذا الموضع.
والصحيح عندنا [في ذلك]، أن أصل المعنى مفهوم، وللتعبد فيها مجال رحب، فافتقر إلى النية باعتبار شوائب التعبد، وتعين الماء، لأنه لا يقوم غير مقامه في التنظيف، وحصول الوضاءة. [وهو] المعدود عرفًا لذلك. وقد أشار إلى ذلك الكتاب العزيز فقال: {وينزل عيكم من السماء ماءً ليطهركم به}. فهو المتعين لإزالة الأخباث، ورفع الأحداث، مع اشتراط النية في طهارة الحدث، دون إزالة [الخبث]. وهذا القول هو أسد الأقوال عندنا.','3','554',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2186,'[وبالله التوفيق].
وقوله الإمام: وليس لنا أن نؤسس في هذا الضرب أصلًا ونلحقه به، لتعذر الوقوف على معنى (٧٦/ أ) القاعدة. وليس من الممكن ربط الظن به إلى آخره. هذا إنما يتوجه على تقدير فهم المعنى، أما مع المصير إلى التعبد، فلا ورود لهذا الكلام بحال. وهذا أيضا، إنما هو كلام تقديري، أما القواعد، فقد مهدت [وفرغ] منها، ولم يبق أصل من هذه الأصول إلا وقد دلت عليه التوقيفات، واستقر أمره في الشريعة. ولا يصادف الناظر أبدًا أصلًا كليًا لم يعرف حكمه، حتى يتلقاه من القياس. نعم، قد يصادف بعض الفروع [الملتحقة] بالقاعدة غير محكوم فيه، فيطلب حكمه بالقياس. وهذا بمثابة إلحاق المس باللمس [في الوضوء]، وبالعكس. وكذلك إلحاق الوضوء','3','555',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2187,'بالتيمم، وإلحاق غسل الميت بغسل الجنب، وكون إزالة النجاسة (١٠٠/ أ) شرطا في صحة الصلاة. هذه الفروع التي يحتاج إلى اتباع أحكامها [بالقياس]. أما [أصول] القواعد، فقد فرغ من شرعيتها، فلم يبق [مهم] كلي من الدين، إلا وقد ثبت حكمه عند المسلمين، قال الله تعالى: {اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينًا}.
قال الإمام: (ثم للشرع تصرف في [الضرورات]) إلى قوله (وليس البيع قبيحًا في نفسه عرفًا أو شرعًا). قال الشيخ أيده الله: هذا الكلام في فهمه إشكال، فإنه جعل الأمور التي تستند إلى الضرورة منقسمة باعتبار الالتفات إلى الضرورة والإعراض عنها، وجعل ذلك ثلاثة أقسام. والصحيح عندنا أنها قسمان:
أحدهما- أن تكون المفسدة اللاحقة من الشرعية تزيد على مصلحة الشرعية، فحينئذ لا يشرع الإقدام، لأجل ترجيح ما في الإقدام من الفساد.
والثاني- إن شرع الإقدام، وهذا على وجوه: منها ما يجب فيه الإقدام،','3','556',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2188,'ومنها ما يباح فيه الإقدام، ومنها ما يندب فيه إلى الإقدام، وقد يندب إلى الترك.
أما القسم الأول: الذي لا يبيحه الاضطرار بحال، فهو قتل النفس المحترمة، التي لا يجوز إتلافها بحال. فليس للإنسان أن يقتل غيره لإحياء نفسه، لاستواء المفسدة في ذلك، مع أن الآخر لم [يجز] حياته. فأوجب الشرع في مثل هذه الصورة الانقياد للهلكة من غير خلاف. وأبو حنيفة- وإن أسقط القصاص عن المكره- فإنه يعترف بوجوب الانكفاف عليه.
وأما الإكراه على الزنا، ففيه خلاف، والصحيح أنه لا يباح الإقدام [عليه]، وقد علل بأن الإكراه لا يتصور فيه، وذلك أنهم قالوا: لولا الشهوة الداعية لم يكن الفعل. وهذا التعليل غير صحيح، وليس من ضرورة من تحركت شهوته أن يوقع الفعل الممنوع. وإذا أمكنه كف نفسه عن تحصيل شهوته، [فهل يكون] خوفه على نفسه [مرخصًا له] الإقدام على الفعل؟','3','557',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2189,'هذا موضع الكلام. وقال الأكثرون: لا يبيح الإكراه الزنا بحال. واحتجوا بأنه أشد في الشرع من إتلاف النفوس، أو مساويًا لذلك، فإن الشرع قد وضع زاجرًا عنه في بعض الأوقات [بتحتيم] القتل دون العفو. وهذا غير موجود (٧٦/ ب) في محض إتلاف النفوس، [وإذا] كان أشد منه مساويًا له، لم يتجه [الإكراه].
وقد اختلفوا في وجوب الاستسلام للصائل، هل يجوز أو يحرم؟ [وكذلك] أيضا يجوز الإقدام على كلمة الكفر دون اعتقاد القلب عند الإكراه، (١٠٠/ ب) ولا يتحتم ذلك، وقد قال الله تعالى: {إلا من أكره وقلبه مطمئن بالإيمان}. ولما [عذب] أصحاب رسول الله - صلى الله عليه وسلم - في أول الإسلام، فمنهم من نطق بما يوافق المشركين، ومنهم من امتنع من ذلك. ولم','3','558',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2190,'[يوبخ] رسول الله - صلى الله عليه وسلم - بعض الفريقين. فدل ذلك على التسويغ، وانتفاء [التحتيم]. وتمام الكلام في بقية الأقسام يتعلق بفن الفقه.
فأما ارتباط البيع بالضرورة، فلعمري إن الخلق مضطرون إلى المعاملة، ولكن المصير إلى أنه لا يستباح إلا بالضرورة، كلام غير صحيح، فإن هذا الضرب يختص بما كان فيه معنى يقتضي المنع، فحينئذ تحال الشرعية على دعاء الضرورة [إليه]. وليس عندنا معنى يقتضي منع البيع، حتى يقال إنه أبيح الضرورة إليه، بل لا مانع يمنع منه، بل هو مناط بمصالح [الخلق]، ولذلك يعتبر الرضا من غير تعريج على ضرر أو حاجة أو مصلحة، حتى لو باع ما يحتاج إليه بما يستغني عنه، لكان البيع [مجرى] على الصحة.','3','559',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2191,'نعم، ما كان فيه معنى يقتضي [منعًا] وأجيز لمعارض راجح، اقتصر التجويز على وجدان المعارض. فأما لو أجزنا من غير وجدان معارض [راجح]، لكان في [ذلك] تعطيل الدليل مع السلامة مع المعارضة. وذلك باطل لا شك فيه. وهذا بمثابة حل الميتة وطعام الغير، إذ قد وجدنا في الميتة ما يقتضي التحريم، وهو خبثها، وذلك قائم عند الضرورة. فإذا دعت الحاجة إلى التناول، فقد عطل المعنى المقتضى للمنع. هذا بيان قوله: تراعى الضرورة في الآحاد، ولا يكتفي بتخيله في الجنس.
قال الإمام - رضي الله عنه -: (فأما الطهارات وما يضاهيها مما قصاراه) إلى قوله (وليس [معه] ثبت في النفي). قال الشيخ أيده الله: أما [انحسام]','3','560',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2192,'القياس في هذه القاعدة [فبين]، أعني قاعدة [الطهارة]. فإنا قد كنا ذكرنا ترددًا بين العلماء، هل هي مبنية على التعبد، أو يلوح فيها معانٍ جميلة؟ فإن بني الأمر على التعبد، فلا إشكال في وجوب الاقتصار والانحصار. وإن قلنا إنه يلوح فيها معانٍ كلية، فليست من المعاني المستنبطة التي يعتمد عليها في تعدية الأحكام، وكيف يتأتى ذلك، ونحن في المعاني الجلية إذا رجعنا إلى التوقيف كنصب الزكوات، يتعذر علينا الوقوف على ذلك؟ فكيف نطلع على [فقهٍ]','3','561',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2193,'يقتضي توقيفه وجوب النظافة بأوقات مخصوصة؟ وكما لا يهتدي [العقل] إلى توقيف وقت يحصل المقصود، فلا (١٠١/ أ) يصح أن يقال إن المعنى يقتضي نفي التوقيف في بعض الأحداث، إذ النفي والإثبات بعد الاطلاع على حقيقة المصلحة، إذ الكلام على الشيء بالإثبات والنفي بعد الوقوف على [معقوليته].','3','562',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2194,'وإذا انحسم مسلك الرأي، لم يبق إلا التوقيف. والظاهر [معتمد] في مثل هذا المكان، وتطرق التأويل (٧٧/ أ) إليه لا يسقط التمسك به. والعضد بالدليل من مقومات التأويل في مسائل الظنون. وإذا بطل أن يجري القياس في هذا، بطل أن يكون عاضدًا، فتعين أن يكون العاضد بوصف آخر، فإذا صودف العاضد، نظر إلى [الترجيح] بين المتعارضين، فيقضى بالأقوى. ومن ينفي الحدث، [يتمسك] بعدم الدليل الدال على الإثبات، والأصل الانتفاء. والتمسك بالنفي، إنما يفيد على تقدير فقدان دليل مثبت. هذا تمام الكلام في ذلك.
قال الإمام رحمه الله: (ومما يتعلق بتمام الكلام في هذا [الأصل]) إلى قوله (فهذا منتهى الغرض [في ذلك). قال الشيخ: ما ذكره الإمام من امتناع','3','563',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2195,'قياس المعنى في هذا الضرب، صحيح، [إذا] تعذر الوقوف على عين المعنى، فكيف يصح الإلحاق بالمعنى مع انحسام فهم المعنى؟ وقد كنا ذكرنا خلافا بين الأصوليين فيما إذا ظن وجدان علة الأصل في الفرع، هل يجوز الإلحاق أم لا؟ فكيف بنا إذا لم [نظن]؟
وأما قوله: نعم، لا ينحسم مسلك قياس الشبه فيه، فهذا [لعمري] نظر، وقد [يصح] أن يظن معنى جميلًا مناطًا بالوصف الشبهي. ومن هذا القبيل إلحاق مس ذكر الغير بذكر الإنسان. وكذلك أيضا تردد العلماء في مس المرأة فرجها، هل يجب عليها الوضوء أم لا؟ والأغلب على ظننا أيضا في ذلك امتناع القياس جملة. ومس ذكر الغير يقع في أبواب اللمس، فينظر فيه إلى وجدان اللذة وغيرها أو قصد ذلك.
وأما قوله في الملامسة: إنه لا يصح إثباتها حدثًا، بالقياس على الخارج','3','564',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2196,'من السبيلين. فكلام صحيح، فإنه لا يجمعهما معنى ولا شبه.
وأما قوله: وقياس أصحاب أبي حنيفة الخارج من غير السبيلين على الخارج من السبيلين، ففيه فقه. فكلام ضعيف، مع مصيره إلى أن القاعدة لا تدرك [فيها] المعاني على حال. فكيف يصح كون القياس مشتملًا على فقه؟ وإنما يعني- والعلم عند الله- أن إزالة النجاسة مناسب، لما فيه من نظافة، وعلى ذلك اعتمد القوم، فكأنه يقول: فيه فقه [لو] [بنيت] القاعدة على مجرد إزالة النجاسة، ولكنه (١٠١/ ب) أبطل ذلك حيث قال: إن السابق إلى الفهم في النجاسة رفع عينها واستئصال أثرها، فأما على غير موضع النجاسة وترك موضعها، فلا محمل لذلك إلا التوقيف أو التعبد. وكلاهما يمنع إلحاق غير النجاسات المعتادة بها، إذ اللاحق بالتأقيت الربط بمتكرر.','3','565',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2197,'فأما ما لا يتفق إلا نادرًا، فلا يصح قياسه على الأحداث المعتادة. هذا إن سلك القوم في الفصد والحجامة مسلك القياس، وإن سلكوا مسلك التوقيف، وتعلقوا بالأخبار، فالوجه المطالبة بصحة النقل، لا وجه إلا ذلك. فإنا قد قررنا أن القياس لا يقتضي إثباتًا ولا نفيًا.
وأما تردد قول الشافعي في المسألة المفروضة، فهو ينبني على ما قررناه من الالتفات إلى التعبد، [أو اعتبار] حدث يتكرر، تحصيلًا لمقصود النظافة، [المترتب] على التوقيف. فإن غلب التعبد، أوجب الاقتصار على المعتاد، وإن التفت إلى التوقيف في (٧٧/ ب) المتكرر، اقتضى ذلك وجوب الوضوء عند مثل هذه الحالة، إذ الطبيعة تقتضي دفع الفضلات من مخرج إلى مخرج، [كل] هذا فيه نظر واحتمال.
واعلم أن أسد المذاهب في قاعدة الأحداث مذهب مالك رحمه [الله]، [فإنه أثبت الأحداث الثابتة نصا وإجماعًا، وامتنع من إثبات غيرها','3','566',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2198,'مطلقا، وراعى فقه القاعدة]، من خروج الفضلات المتكررة، فلم يوجب الوضوء من الدم الخارج من قبل أو دبر، ولا بالحصاة والدود. إذ ليس بولًا ولا غائطًا، فلا يجب الوضوء إلا من الخارج المعتاد من المخرج المعتاد، إذ ذلك هو المتكرر في الاعتياد، وهو اللائق بتحصيل النظافة المفهومة من حيث الجملة. فأما خروج غير هذه النجاسات من المخارج المعتادة، [فلا يتكرر، ولا يسمى بولا ولا غائطا. وكذلك خروج هذه النجاسات أو غيرها من المخرج المعتاد]، مما لا يتكرر على حال، ولا يستحق الاسم الذي علق عليه الحكم بحال.
وهل يشترط في هذه الأحداث خروجها على حكم [العادة]؟ فيه خلاف [بين]، أصحابنا ينبني على قاعدتين:
إحداهما- لفظية، والأخرى فقهية. أما [القضية] اللفظية، فهو أن اللفظ العام إذا ورد، وجرت العادة من جهة الفعل على الاختصاص ببعض المسميات، فهل [ينزل] اللفظ العام على مقتضى عرف الاستعمال في','3','567',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2199,'[التعاطي] دون النطق، أو يسترسل اللفظ على مقتضاه في اللسان؟ الظاهر من جهة الأصول الشمول دون القصور. وقد ذكرنا (١٠٢/ أ) في ذلك خلافًا في تلك المسالة. وإذا كان كذلك، فقد قال الله تعالى: {أو جاء أحد منكم من الغائط}، [وهذه] العبارة متجوز بها، كناية عن الحدث، فينبغي أن [تبقى] على عمومها، إلا أن يدل دليل آخر سوى العرف الفعلي على التخصيص.
الأمر الثاني- الذي ينبني الخلاف عليه، ما قررناه من فقه القاعدة، بالنظر','3','568',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2200,'إلى التكرار اللائق بالتوقيف. [فمن جرد إلى ذلك النظر، لم يوجب الوضوء بالسلس، إذ ذلك لا يليق بحكمة التوقيف]. ومن عول على التعبد، أوجب الوضوء بذلك، وهذا هو الظاهر من المذهب. وقد يصح أن يراعى المعنى، ويعرض عن حال المرض، للندرة [والقلة]، ومثل ذلك جارٍ في الأسباب إذا تمهدت واسترسلت على حكم العرف المطرد فيها، فلا نظر إلى ما يشد ويندر منها. كما إذا ثبتت رخص السفر، نظرًا منا إلى المشقة، ثم نثبت ذلك في حق الملك [المترفه]، نظرًا إلى أصل القاعدة، وإعراضًا عن [الصورة] النادرة، [فكذلك] يجب الوضوء بخروج الأحداث المتكررة، للحكمة اللائقة، ولا نظر إلى الصورة النادرة من مرض أو غيره.
قال الإمام: (وأما الضرب الرابع) إلى قوله (وحمل الأخرى على الاستحباب). قال الشيخ: ما ذكره الإمام في هذا الضرب الرابع، أنه كالضرب الثالث في تحصيل مكارم الأخلاق، من العتاقة والنظافة فالأمر كما','3','569',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2201,'[ذكر] في العتاقة، إذ المقصود (٧٨/ أ) منها [تحصيل] العتق، لا ريب في ذلك.
وأما ما ذكره من أنها [خارمة] لقياس المعاوضات، فقد تقدم كلامنا على ذلك، وبينا أن هذا إنما يكون أن لو قضى بكونها معاوضة محضة. أما إذا سلك مسلك التعليق والاستسعاء، وهو الظاهر من المذهب، فلا [تكون خارمة] للقياس بحال.
ومعنى قوله: إن الكتابة ليست واجبة، وإن وجبت الطهارة، ولا تجب النظافة. يشير إلى اعتراض معترض قال له: كما نقضت الكتابة قياس المعاوضات، فكذلك نقضت الطهارة قياس الأسباب والمسببات، إذ لا تجب','3','570',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2202,'وسيلة إلى مندوب بحال. والطهارة وسيلة إلى تحصيل النظافة المندوب إليها. هذا مراده ههنا. وقد قدم هو عن هذا جوابًا سبق، وهو اكتفاء الشرع بزاجر الطبع عن التضمخ بالطين، من غير احتياج إلى ذلك. والذي قد يتسامح فيه أهل المروءات ما يتعلق [بالغبرات] (١٠٢/ ب)، فوكل الشارع إلى الطباع قدرًا، وحصلت الوظائف قدرًا. والكتابة لم تخرم هذا القياس، فإنها لم تجب وسيلة إلى العتق المندوب إليه، فكأنها على قوله أقرب من هذه الجهة من قاعدة الطهارة الواجبة، تحصيلا للنظافة المندوب إليها.
وأما قوله: إن مالكًا رحمه الله ذهب إلى وجوبها، وإسعاف العبد إذا طلبها، فليس الأمر كذلك، وليس هذا مذهب مالك رحمه الله، ولا أحد من أصحابه فيما علمناه. نعم، تردد أصحابنا، هل هي مباحة، نظرًا إلى جانب المعاوضة، أو مندوب [إليها]، أو سبب إلى العتق؟ وأما الوجوب، فلا','3','571',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2203,'ذاهب إليه بحال. وأما مصير الشافعي إلى وجوب الإيتاء، فضعيف من ثلاثة أوجه:
أحدهما- أن المذهب الصحيح للشافعي [في الأصول] أن صيغة (افعل) مترددة بين الوجوب الندب، وقد قدمنا ذلك في موضعه. فكيف يعين في هذا الموضع الوجوب من مطلق الصيغة؟
الثاني- أنه يرى إذا اقترنت صيغ، واشتهر من شيم العلماء القائمين بالشريعة حمل بعض تلك المناهي على الفساد، فلا يقبل تأويل من أراد حمل بعضها على الكراهة. ويرى الاقتران قرينة تمنع التأويل. [فلم] لم يقل ذلك في ما إذا اقترنت أوامر، وظهر من شيم العلماء حمل بعضها على الندب، أن تكون قرينة تمنع من حمل اللفظ على الوجوب؟
الثالث- إن الفقه يقتضي سقوط الوجوب في الإيتاء، لأنه وضع مال مستحق، ولا عهد في الشريعة بوجوب الإرفاق وإسقاط الأموال، لاسيما على مذهب [من] يرى أن الكتابة واقعة في أقسام المعاوضات. وعلى الجملة،','3','572',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2204,'المصير إلى وجوب الإيتاء لا نظير له في الشريعة، ولا يقتضيه فقه بحال.
قال الإمام: (وأما ما أشرنا إليه من تشبيه هذا الضرب على رأي [مالك] بالطهارات) إلى قوله ([لا أرى على مذهب الشافعي مسألة أضيق مسلكًا من الإيتاء]). قال الشيخ: أما قوله: ما أشرنا إليه من تشبيه هذا الضرب على رأي مالك إلى آخره، [فمعنى] قوله: [فهل] يصلح لعقد المذهب؟ يعني لإبداء مأخذ صاحبه، والوقوف على سر قوله. ولا يلزم من إبداء مأخذٍ (٧٨/ ب) يضبط المذهب، أن يكون صحيحًا. [ولذلك] قال: إن','3','573',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2205,'هذا لا يصلح مأخذًا على الحقيقة، إذ القياس [عنده] لا يجري في هذا النوع من اعتبار شيء بشيء على طريق المعنى، وإنما يجري [على] [طرفٍ من التشبيه] في جزئيات النوع. يقول: فلا [يصح] أن يتلقى إيجاب الكتابة، قياسا على إيجاب الطهارة. وهذا من العجيب أنه أضاف [إليه] (١٠٣/ أ) حكمًا لا يقول به، وقدر له مأخذًا وأفسده. كل هذا خروج عن الإنصاف، [وركوب] لمركب الاعتساف.
وأما التعلق بالظاهر، فله وجه؛ لولا ما قررناه من مدافعة القياس له. فإن الكتابة إن كانت معاوضة، لم تجب بل تباح، وإن كانت عتاقة، لم يثبت فيها سوى الاستحباب والندب. فهذا هو الذي صدنا عن التمسك بالظاهر في الأمرين جميعًا.','3','574',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2206,'وأما تفرقة الشافعي بين الأمرين، فقد بينا ضعفها. وما احتج به من فعل الأولين [في اطراد] الإسقاط عندهم، [فهذا] ضعيف من وجوه:
أحدها- أن هذا لم ينقل عن أهل الإجماع، إذ ليس فيه فتوى يعتمد عليها، وإنما فيه أن قومًا أسقطوا عن المكاتبين من آخر نجومهم. وهذا ينقل عن عدد قليل من الأولين، فلا يصح في ذلك إجماع بحال.
والوجه الثاني- أن هذا وإن كان منقولا، فليس فيه ما يقتضي أنهم رأوا وجوب الإيتاء، بل فعلوه، ويصح أن يروه مندوبًا ويفعلونه.
وقوله: إن التبرعات لا تطرد، هذا إذا لم يظهر ترغيب خاص في المندوب إليه. والأضحية مندوب إليها، والمسلمون يبالغون [في] فعلها، وإن لم يروا وجوبها. وكذلك متعة المطلقة عندنا مندوب إليها. والمنقول','3','575',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2207,'عن الأولين فعلها والمواظبة عليها. وهي أكثر وأعم وقوعًا من الكتابة. والذين كاتبوا العبيد من الأولين العدد القليل.
وأما قوله: إن الكتابة متضمنها إرفاق من كل وجهٍ، والإيتاء منه، وإذا كانت الكتابة ليست واجبة، كيف يجب ما هو من فروعها؟ وقدر الإمام اعتراضًا، وأجاب عنه ضمنًا فقال: ومهما [يلزم] شيئا إذا صح، لم يلزم الإقدام عليه. يشير بذلك إلى أنه لا تجب الكتابة، بل يندب إليها، فكيف يجب الإيتاء المترتب عليها؟ فقال: ما لا يلزم من إيجاب الإيتاء إيجاب الكتابة. هذا معنى قوله: وما يلزم شيئا، وفاعل (يلزم) مسكوت عنه، راجع على (ما) يعني: الإيتاء الذي يلزم في الكتابة. والمراد بالشيء ههنا: الكتابة، لم يلزم الإقدام على الكتابة. هذا مراده- والعلم عند الله. ولقد رجع الإمام إلى الإنصاف بقوله: ولست أرى على مذهب الشافعي مسألة أضيق مسلكًا من وجوب الإيتاء.
قال الإمام: (ونحن نقول وراء ذلك: أما مالك). إلى آخر المسألة.','3','576',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2208,'قال الشيخ: أما قوله: إن الكتابة تلزم في حق السيد، فلا خلاف في ذلك (١٠٣/ ب). واختلف علماؤنا في وجوبها في حق العبد، فالظاهر من المذهب أنها لازمة له، نظرًا إلى مصلحته في تحصيل العتق [له]، والتفاتًا أيضا إلى جانب التعليق فيها، ولشوف المعاوضة، فإنه لو علق السيد عتق عبده على حصول حالة، لم يكن للعبد إسقاط (٧٩/ أ) ذلك. فشوائبها جميعا تقتضي أن لا يتمكن العبد من حلها، وسواء كان له مال ظاهر، أو لم يكن.
وقال قائلون: له أن يعجز نفسه، فإنه عقد إرفاق به، وله إسقاطه. وقد جاء ذلك عن ابن عمر في [قصة] طويلة.','3','577',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2209,'والقول الثالث، وهو المشهور، أنه ليس له تعجيز نفسه، إذا كان له مال ظاهر، لأنه رجوع إلى الرق بعد أن وجد الطريق إلى قطعه. وإن لم يكن [له] مال ظاهر، عجز نفسه، إذ العتق يرتبط بالآداء، وهو عاجز عنه. وتكليفه الرفع إلى الحاكم لا معنى له، عند عدم المال الظاهر، إذ الحاكم لا يحكم إلا بالتعجيز. وللقائل الأول [أن يقول: ] لعل الحاكم إذا رفع إليه الأمر يجتهد في البحث عن المال الموصل إلى [الحرية]. وهذا فيه [بعد، إذ] قوله مقبول عند عدم المال الظاهر.
وأما قوله: إن مالكا رحمه الله سوى بين أبواب الطهارات وقاعدة الكتابة، [لعسر] وجوب الوسيلتين، [إلا] أنه لا يوجب الإقدام عليه. فقد بينا','3','578',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2210,'أن الحكم ليس على ما تخيله ونقله.
وقوله: [ولاح على أصله [إجراء] قسم الكتابة في وضع الشرع على باب الطهارات]. هذا كأنه [يقرر] مأخذًا لمالك. يقول: إذا وجبت الطهارة توصلًا إلى النظافة- وإن لم تخرم قياسا- فإيجاب الكتابة الخارمة للمعاوضات أولى. يقول: إن الشرع لا يخرم هذا القياس العظيم، إلا لغرض مهم، لابد من تحصيله. فرأى خرم القياس دليل الإيجاب. هذا كله على ما تخيله مذهبًا لمالك، ومأخذًا له. وكل ذلك فاسد.
وأما قول الشافعي: للشرع [تحكم] في الإيجاب متبع، وإن لم يكن منقاسا، كإيجاب الطهارة، وإن لم تجب النظافة. فهذا كلام صحيح، ولا يكون خرم القياس دليل الوجوب بحال، وللشرع تحكم في الإيجاب متبع، وإن','3','579',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2211,'لم يكن منقاسا، فربما طرد الشارع حجرًا في مكان القياس، أن لا يحجر على المكلف فيه، وربما فك حجر القياس اطراده، فلا [يتلقى] [إثبات] الإيجاب ولا نفيه من موافقة القياس، ولا من مخالفته. هذا كلام صحيح. وما أظن أحدًا من العلماء يرى خرم القياس دليل الوجوب بحال، ومأخذ الوجوب غير هذا.
وأما تفرقة الشافعي بين الكتابة والطهارة، لظهور غرض الكتابة (١٠٤/ أ)، واستغنائها عن النية، وافتقار الطهارة إلى النية، من جهة أنه لم يظهر فيها غرض ناجز، فلاق بها ترغيب في الثواب، فكلام صحيح، وقد قدما تقريره، وهو يرد ما ذكره الإمام، من بناء القاعدة على فهم معنى النظافة، إلا أن يصير ظهور المعنى من وجه، وثبوت التعبد من وجه، فيصح قوله على هذا التقدير.
قال الإمام: (مسألة: قد ثبت أن الكتابة الفاسدة تثبت فيها أحكام) إلى','3','580',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2212,'قوله ([وأما الشبه فقصاراه ظن على بعدٍ، فإذا عارضه نقض، وهي وانحل]). قال الشيخ: البيع الفاسد لا يفيد الملك عند مالك والشافعي [رضي الله عنهما]. وعمدتهما في ذلك ما قدمناه قبل هذا، من أن البيع مشروع لمصلحة الآدمي، فلو كان في البيع الفاسد مصلحة، لما نهى الشرع عنه، وإذا نهى عنه، فلا مصلحة للمكلف فيه. وإذا ظهرت مصلحة [ببادئ] الرأي، فقد علم الله تعالى أن المصلحة في أن لا يقدم (٧٩/ ب) عليه.
وأيضا فلو كان يفيد الملك، لاقتضى ذلك تحريك الداعية إلى تحصيله، والتحريم يمنع من الإقدام، فتظهر مناقضة في شرع الأحكام، وذلك لم يثبت من استقراء الشريعة على حال. وكذلك نقول في سائر الأسباب المحرمة، فإنها [لا] تفيد ثمرات الأسباب الصحيحة، إلى أن يظهر انصراف التحريم إلى غير','3','581',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2213,'السبب ووصفه إلى أمرٍ يجاوره. وهذا قدمناه.
[ولكن] الشافعي يقول: يجب الرد إلى البائع وإن تغير المبيع. ومالك رحمه الله يقول: إنما يرد إذا كان المبيع [قائمًا]، لم يتغير في سوق ولا بدل. أما إذا تغير، فقد فات الرد، لا لاقتضاء البيع ذلك، ولكن يقدره بمثابة الهالك، ولذلك قال: يضمن بالقيمة. ولو كان البيع الفاسد مفوتا، للزم الثمن، وليس الأمر كذلك. والشافعي يقول هذا في فوات الأعيان، وأما التغيرات، فلا ينزلها هذه المنزلة.
والمقصود أن البيع الفاسد على المذهبين، لم ينقل ملكا. وليس المقصود الآن بيان ما يمنع الرد، وإنما المقصود أن البيع الفاسد لا ينقل الملك، وقد','3','582',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2214,'قررنا وجه ذلك. وأما الكتابة الفاسدة فإنها تثبت فيها أحكام مشابهة [لأحكام الكتابة] الصحيحة. ولذلك اعتبر أصحاب أبي حنيفة البيع الفاسد بالكتابة الفاسدة.
وقول الشافعي: لا يقبل القياس في الفرعين، [يريد] به البيع الصحيح والبيع الفاسد، [وأراد بالأصلين: الكتابة الفاسدة والصحيحة]، وليس يعني أن البيع [الصحيح] يختلف فيه، ولكنه يقول: نسبة البيع الفاسد إلى الكتابة الفاسدة (١٠٤/ ب)، نسبة البيع الصحيح إلى الكتابة الصحيحة. وبين أن صحيح البيع لا يشبه صحيح الكتابة، فكذلك فاسد البيع. وحقق ذلك','3','583',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2215,'بأن الكتابة الصحيحة خارجة عن قياس المعاوضات، والفاسدة [مترتبة] عليها، قال: فإذا امتنع القياس في الأصلين، يعني صحيح الكتابة وفاسدها، ترتب عليها امتناع القياس في الفرعين.
أما كون الكتابة الصحيحة خارجة عن قياس المعاوضات، فهو مسلم، لأنها لم يتمحض كونها معاوضة محضة، لما قررناه من اجتماع شوائب التعليق والاستسعاء. فيمتنع أن تجري فيها أحكام المعاوضة المحضة، والفاسدة متفرعة عليها. فإذا تعذر تنزيل [صحيح] البيع منزلة [صحيح] الكتابة، فكيف يصح تنزيل فاسد البيع منزلة فاسد الكتابة؟
فعلى هذا يصح قوله: إنه يهون على الفطن تقرير خروج الكتابة الفاسدة','3','584',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2216,'[في نزولها] منزلة الكتابة الصحيحة عن أقيسة المعاني، لما قررناه من استقراء الشريعة من امتناع إثمار المحرم ثمرة المشروع.
وأما أصحاب أبي حنيفة، فإنهم زعموا أن الكتابة [الصحيحة] في إثمار ثمراتها، جارٍ على أبواب قياس المعاوضات. قالوا: وإنما الخارج عن القياس، جواز معاملة السيد ملكه بملكه، وهذا موضع عفو الشرع. فإذا ثبتت، قضى فيها وعليها بأحكام المعاوضات، حتى يعتبر فيه التراضي، وإعلام العوض، واتصال الإيجاب بالقبول، [إلى] غير ذلك من أحكام المعاوضات. والفاسد في كل باب حائد. فإذا حكم الشرع بكون الكتابة الفاسدة تتنزل في عوض (٨٠/ أ) الكتابة منزلة الصحيحة، لم يمتنع مثل ذلك في البيع الفاسد. هذا تقرير ما قالوه.
وأما أصحاب الشافعي، فإنهم يقولون: إن الكتابة الفاسدة لم تتنزل منزلة الكتابة الصحيحة، لفقهٍ اقتضى ذلك. فإن الأسباب إنما تثمر، إذا جرت موافقةً لقضايا الشرع. وقد قدمنا في هذا قولًا بالغًا، وأن استقراء الشريعة يرشد إلى أن','3','585',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2217,'العقود الفاسدة لا تثمر بحال، ولا يرجع ذلك إلى معقول النهي والتحريم، فإنه لا يتناقض أن يرحم الشرع عقدًا ويقول: إن أقدم عليه المكلف أثمر له ثمرة العقد المسوغ. وقد قدمنا في ذلك قولًا بالغًا في مسألة الصلاة في الدار المغصوبة، فلا نعيده. [إلا أن] تحقيق الغرض الآن، تفهم وضع الشرع الأسباب، فإذا انحسم المعنى في الكتابة الفاسدة، استحال إلحاق البيع الفاسد بها، بجامع المعنى، لانحسامه في الأصل، وثبوت ضده فيه.
وقوله: فإن تعلق الخصم بذلك، وقال: البيع الفاسد بالإضافة [إلى الصحيح، كالكتابة الفاسدة، بالإضافة] (١٠٥/ أ) إلى الصحيحة، ولم يلتزم إبداء معنى بوجه، انتقض عليه بالنكاح، فإن فاسده لا يتنزل منزلة صحيحه بوجهٍ من الوجوه.
وقوله: وأحق قياسٍ بالنقص، قياس الشبه، فإن المتمسك بالمعنى، قد يعن له طرده، إذا لم يمنع منه مانع. فإذا ظهر مانع علله، وبقي متمسكًا','3','586',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2218,'بالمعنى فيما وراءه. [مع] أن القول الصحيح، بطلان المعنى بالانتقاض، على ما سيأتي.
وأما الشبه، فضعيف في بابه، والمناسبة لم يوقف عليها، غير أنه قد يظن مصلحة خفية، بكون هذا الوصف قبالها، فإذا تخلف الحكم المذكور، مع عدم الإحاطة بالمعنى المقدر، لم تبق غلبة ظن، فإن الوصف هو [المتضمن] للمصلحة المعينة.
ولكن قد يظهر للقوم عن النكاح عذر، فإنهم إنما قضوا بذلك في العقود الفاسدة إلى الأمور التي [يصح] رضا العباد بإسقاط حقوقهم [فيها]، كالأملاك. ولذلك امتنعوا على قضية الرضا، ورأوا في ذلك مناسبة. وهو كلام له وجه، لولا ما قررناه من إعراض الشارع عن الرضا في بعض الأمور.
وأما الفروج فللشرع فيها حق، فلا يصح تباذل الناس لها، ولا رضاهم بتعاطيها. ولا يلزم من الالتفات إلى الرضا في الأموال، الالتفات إلى ذلك في الفروج. والنقض على هذا غير لازم، مع ظهور هذا الفرق.','3','587',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2219,'وأما قوله: وأقرب من ذلك البيع نفسه. فهذا كلام أيضًا لهم عنه جواب، وذلك أن العبد [حائز] نفسه، فلا يصح أن يقال يفتقر بعد الكتابة إلى قبض نفسه. فلا نقض أيضًا على هذا التقدير. نعم، يلزم الخصم أن يبين الافتقار في الانتقال إلى القبض في الفاسد دون الصحيح.
وعمدته في ذلك أنه لما كان هذا البيع غير مقدور عليه شرعًا، أشبه العقود غير اللازمة، [من] الخيار وغيره، فلا يترتب الضمان فيه إلا على القبض، وبه يتم الملك عند القوم، كعقد الهبة عند الشافعي. وهذا الكلام فيه نظر، ولكن المقصود أنه لا يلزم ما ألزموه من الفرق بين الكتابة الفاسدة، والبيع الفاسد، من اشتراط القبض في البيع الفاسد، (٨٠/ ب) إذ لا يكون البيع الفاسد مشابهًا للكتابة الفاسدة، إلا إذا وجد القبض فيه، إذ هو [متحقق] في الكتابة الفاسدة.
قال الإمام: (فهذا فن من الكلام [يصدهم] عن النزول إلى التشبيه) إلى قوله (لم يبق لمتمسكهم بالكتابة الفاسدة وجه). قال الشيخ: هذا الكلام','3','588',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2220,'ذكره الإمام مختصرًا، ولم يبين وجه قولهم على الحقيقة، [ولم يرد] أيضا عليهم بوجه (١٠٥/ ب) فقهي، سوى السب والفحش. ونحن نقرر مأخذهم في ذلك.
وذلك أنهم قالوا: القياس المناسب يقتضي اتباع رضا العقلاء في أموالهم، وصحة تصرفهم في أملاكهم، غير أن الشريعة قد منعت من اتباع مطلق الرضا في بعض الأمور، كالنكاح وغيره، ووسعت طرق الأملاك. يشيرون بذلك إلى جواز انتقالها على حسب رضا مالكيها، ولذلك تثبت فيها الإجارة والعارية، والقراض والهبة، والصدقة والبيع والارتهان وغيره، فيشعر اتساع الطرق بعدم التعبد في طرق النقل، [وإدارة] الأمر على الرضا، وهو معنى مناسبته.
هذا معنى قولهم: ما اتسعت طرقه، [فالفاسد] أحد طرقه، إذ لا تجتمع هذه الجهات المذكورة إلا في رضا الملاك بانتقال الأملاك. وهذا وجه من المناسبة ظاهر، ولكن يعترض عليه بمنع كونها لم تشترك إلا في الرضا، بل قد اشتركت في الرضا، وشرعية جميع العقود. فعلى الخصم أن يبين أنه لا أثر للشرعية، وإنما الاعتماد على مطلق الرضا. ولا يجد إلى ذلك سبيلا أبدًا. فإنا','3','589',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2221,'قد بينا أن [للشرعية] أثرًا بينًا فيما يتعلق بتحصيل ثمرات الأسباب، [وأن] امتناع الإثمار عند فساد الأسباب وتحريمها مناسب.
قال الإمام: (ومما نذكره في ذلك أن الكتابة الفاسدة في وضعها، مخالفة للبيع الفاسد) إلى قوله (في معرض النقض المعنوي). قال الشيخ: ما ذكره الإمام ظاهر من جهة الظاهر، ولكن ينحل عند التحقيق، وذلك أن البيع الفاسد، وجب نقضه عند القوم، ورجوع المبيع إلى ربه، إلا أن يفوت، فتتحتم القيمة فيه. والفوات على وجهين: تارة ببطلان الأعيان وعدمها، وتارة بتغيرها. [والتغير] على وجهين: حسي ومعنوي، والمعنوي على وجهين: عرفي وشرعي. وإنما ترد الأملاك، ويجب ذلك فيها، إذا لم [تطرأ] تغيرات. أما لو اشترى العبد شراءً فاسدًا، واتفق عتقه، فإنه لا ينقض البيع، ولا يرد العتق، لفواته بحصول الحرية.','3','590',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2222,'وإنما تتنزل الكتابة الفاسدة منزلة البيع الفاسد، إذا اتصل به العتق، وأن بعقد الكتابة حصل للعبد شوف الحرية. والدليل على ذلك أن العبد المشترى شراء فاسدًا، إذا أعتقه المشتري أو كاتبه، مضى ذلك، ولم تنقض الكتابة عند القوم. ولا فرق بين يشتريه أجنبي فيكاتبه، وبين أن يكاتب نفسه، إذ عقد الكتابة ورد على الجهة الفاسدة، ففات المكاتب بها. فمن أين يلزم أن يرد هذا نقضًا على (١٠٦/ أ) البيع [الفاسد] قبل فوات المبيع؟ فهذه إلزامات ضعيفة.
أما قوله: وهذا يستعمل في معرض النقض المعنوي، يريد بذلك أنه إذا وقع (٨١/ أ) الاعتماد في جانب التصرفات، من استحقاق القبض على الفساد في عقد البيع، لزم مثله في الكتابة الفاسدة. ولا يكون لفظ البيع مصونًا إلا من جهة اللفظ خاصة. [وأما] المعنى فمنقوض، على حسب ما تخيله الإمام.
وعذر القوم عن الكتابة، الفوات على ما قررناه. وإنما تشبه الكتابة البيع الفاسد، إذا تغير فيه المبيع تغيرًا يمنع الرد، فإنه يتصرف فيه تصرفا صحيحًا، ويصير حينئذ كالبيع الصحيح في تسويغ التصرفات. وأراد الإمام بما ذكره، أن الكتابة الفاسدة لا تجري على ذوق المعاملات بحال.
قال الإمام رحمه الله: (ومن دقيق القول في ذلك أن تحصيل العتاقة) إلى','3','591',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2223,'قوله (فهذا منتهى كلام الفقهاء). قال الشيخ: الذي ذكره الإمام في هذا المكان، يشير إلى أن الكتابة لم يتمحض فيها أمر المعاوضات، [ولوجود] شائبة التعليق. وهذا من أهم ما ينبغي أن ينتبه له، فإن هذا النوع غامض في الشريعة، وهو أن تكون المسألة مشتملة على شوائب، الالتفات إلى كل شائبة على حيالها، يقتضي حكمًا، لا تقتضيه الشائبة الأخرى. فإن نظر الناظر إلى شائبة منها، وأغفل النظر إلى الأخرى، كان نظره قاصرًا. وإنما الكامل هو الناظر في جميع الجهات، الموفي كل شائبة حقها.
وبيانه في هذه المسألة: أن البيع [اتحد] فيه غرض المعاوضة، وليست الكتابة كذلك، فإنها مشتملة على شائبة التعليق، كما قررناه قبل هذا بديل قوله: (المكاتب رق ما بقي عليه درهم). وفيها شائبة المعاوضة أيضا، بدليل أن السيد لا يتمكن من الكتابة من غير مؤامرة العبد، وهو مفتقر إلى ذلك في أبواب التعليق. فالتفت الشرع عند وقوع الكتابة الفاسدة، إلى شوب التعليق، نظرًا','3','592',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2224,'منه إلى تحصيل العتق، الذي هو [أنفذ] التصرفات، وآثر فساد الكتابة- أعني ناحية المعاوضة- ولو اعتمد عليها في دفع وجوب التعلق، وآثر الالتفات إلى خصائص التعليق في الإعراض عن جانب الفساد، التفاتًا إلى شائبة المعاوضة. فرجح الشرع الالتفات إلى التعليق، لتحصيل الإعتاق، ولا يلزم مثل هذا في جانب المعاوضة المحضة بحال.
ومعنى قوله: وهذا خارج ظاهر الخروج عن قياس بابين، يعني أن الكتابة ليست تعليقا محضًا، ولا معاوضة صرفة، بل فيها جهتان جميعًا. فإذا رجحت إحدى الشائبتين (١٠٦/ ب) فيه، وقضى بالنفوذ، [ليحصل] المقصود من الإعتاق، لم يلزم [على] ذلك إنفاذ البيع الفاسد، لاتحاد الجهة، وضعف الثمرة المقصودة. وليس حرص الشرع على تحصيل الأملاك، كحصرصه على تحصيل الإعتاق. ولذلك قضى بأن مبعضه يتمم ومؤقته يتأبد.','3','593',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2225,'[وقوله]: فكيف اكتسب ما ليس يفسد؟ وإن ذكر على صيغة الفساد، وقضية الفساد في معاملة واهية بالجواز والفساد، يعني أن التعليق لا يصح أن يفسد بالنظر إلى شوب المعاوضة في الكتابة، لضعف ذلك. ومعنى ضعف المعاوضة: أنها تقع تارة جائزة، [كبيع الخيار]، وتارة فاسدة تنقض. وأما التعليق، فلا سبيل إلى انعقاده جائزًا، ولا إلى وقوعه فاسدًا. فإذا (٨١/ ب) تعددت الشوائب، وظهر لبعضها قوة على البعض، وجب تغليب [الأقوى]، نظرًا إلى قوة [وضعه] في نظر الشرع.
وقوله: فهذا يمنع من التشبيه، يعني من تشبيه البيع الضعيف بالكتابة، لاشتمالها على هذه المزية، بالنظر إلى شائبة التعليق القوية، وحصول العتق، الذي هو شوف الشريعة.
وقوله: فهذا يمنع التشبيه، [ويبين] تباعد القواعد، وإن عين التحاق بالمنصوص عليه، لكونه في معناه. فلا سبيل على هذا التقدير إلى اعتبار إحدى المسألتين بالأخرى، مع ما قررناه. هذا كلام حسن بالغ، فليمعن المنتهي إليه نظره، وليتدبره، وليتخذه مرجعه في تفاصيل المسائل. وما أكثر هذا النوع من التصرف في مذهب مالك [رحمه الله]. [وإنما الفروق دقيقة، والنظر غامض. والله المعين على جميع الأمور].','3','594',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2226,'قال الإمام: ([وأنا] أذكر مسلكا [أصوليا] يغني عن جميع ذلك، فأقول: قد تمهد [أن القوانين]) إلى قوله (لم يرتبط الفرع بالأصل). قال الشيخ: أما قوله: إن المكارم الكلية لا يجري فيها قياس أصلٍ على أصل، إذا لم يظهر فيها معنى ضروري [أو حاجي]. وإنما يصح ذلك في الضرورات والحاجات. وقد بينا نحن أن هذه القواعد قد استقرت في الشريعة ضرورياتها وحاجياتها، وكذلك قواعد التحسين والتكميل، ولم تبق قاعدة من هذه القواعد إلا وقد سبق تقريرها، وثبت تأسيسها. وإنما ينظر العلماء في الجزئيات خاصة. فإذا صح جامع من معنى أو شبه، صح الاعتماد عليه. والكتابة من حيث [الجملة] قد استقرت، وإنما بقي اعتبار الجزئيات بعضها ببعض،','3','595',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2227,'فليراع شرط القياس في الجامع وصحته، [وبطلان] الفرق، (١٠٧/ أ) [أو إيماء] أثره. هذا لو سلم أن الكتابة قاعدة [مستقلة]. ولذلك لا [يمتنع] اعتبار بعض فروع النكاح بفروع البيع، وإن تباينت القواعد. وكذلك [يصح] قياس [قواعد] المساقاة في بعض الجهات على البيع، والبيع عليها. وكذلك القول في القراض والإجارة.
وأما إذا صرنا إلى أن الكتابة نوع من المعاوضات، فلا إشكال في صحة الاعتبار، [وإخراج] الكتابة عن قياس المعاوضات، فإنها غير مضطر إليها. كلام [غير مفيد]، فإنا لسنا نشترط تحقيق الضرورة في كل صورة من صور البيع، فلتكن الكتابة إذا [غلبت] فيها شائبة المعاوضة، من قبيل ما [لا]','3','596',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2228,'تدعو إليه الضرورة من البيوع. وكيف ينكر ذلك، والكتابة إنما قدر الشافعي خروجها عن القياس؟ من جهة أنه رآها معاوضة، ولذلك قال: إن السيد قابل ملكه بملكه. فمن هذه الجهة كانت مناقضة للمعاوضات، ولو كانت قاعدة أخرى، لم ينقض قياس المعاوضات أصلًا، إذ لا يصح أن تكون الطهارات وشرعيتها ناقضة لقياس المعاوضات بحال. فالذي سبق تقريره، يناقض هذا الكلام مناقضة بينة.
وأما قوله: وأما القياس في الأصول، إذا لاحت المعاني، وإنما تظهر المعاني في الضرورات والحاجات. هذا ليس كما يقول، فإن المعاني تظهر في الضرورات والحاجات، وكذلك فيما يتعلق بالتحسينات والتزيينات، كمنع المرأة من تولي عقد النكاح، ورد شهادة العبد، إلى غير ذلك. نعم، هي في الضرورات والحاجات أظهر. وأما المصير إلى أنها لا تظهر في [تلك] بحال، فمحال.
وأما قوله: وأقرب قاطع للشبه، تعدد الأصول فيما لا ينقدح فيه جامع ضروري أو حاجي. يشير بذلك إلى أن الأصول تتعدد وأحكامها مختلفة، إذ هي معلقة بأمر [غيبي]، إذ لا يجري كل أمر في الغيب على قضية واحدة','3','597',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2229,'(٨٢/ أ). وهذا الكلام غير صحيح، فإنه كما يتصور تعدد الأصول فيما لا ينقدح فيه جامع ضروري أو حاجي، فكذلك يمكن تعدد الأصول فيما ينقدح [ذلك فيه]. ولذلك يختلف العلماء في مسائل [من] البيوع والإجارة، بناء على تعدد الأصول، حتى يجوز قوم عقدًا، ويمنع منه آخرون.
ومن هذا القبيل اختلافهم في بيع الغائب على الصفة، وعلى غير صفة. سبب ذلك تعدد الأصول. فليس تعدد الأصول بالذي يستدل به على قطع التشبيه، [بل] إذا تعددت، وأمكن التغليب، ثبت الحكم على وفق [الغالب]. وإن وقع الاستواء (١٠٧/ ب)، لزم الوقف، [أو التخيير أو الاحتياط].
[فأما] قوله: إن هذا يمنع الإلحاق، يعني عند تعدد الأصول. فقد سبق الكلام عليه. فإن اعترفنا بتعدد القواعد، لم يمتنع إجراء القياس في التفصيل. وإن قلنا إن الكتابة نوع من أنواع البيوع، فالأمر أظهر، إذ يرجع إلى اعتبار جزئيات النوع بعضها ببعض.
قال الإمام: (نعم، إذا كفي الشافعي احتجاج الخصم بالكتابة) إلى قوله','3','598',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2230,'(كما [سيأتي تمهيده] في باب النقض، إن شاء الله تعالى). قال الشيخ: معنى قوله: إذا كفي الشافعي احتجاج الخصم بالكتابة، يعني: إذا تعذر أن يجعل الكتابة الفاسدة أصلا للبيع الفاسد، إما من جهة تباين القواعد، أو من جهة أن إمضاء الكتابة الفاسدة خارج عن القياس، [والخارج عن القياس] لا يقاس عليه.
بقيت عليه غائلة فيما يقرره ويعتمده، من أن [الفاسد] لايثمر بحال، ويبدي المعنى المناسب، المقتضي [لذلك]، ويسنده مثلا إلى النكاح [الفاسد]، فترد الكتابة الفاسدة نقضًا. وليس [يلزم] الناقض إبداء مساواة المسألة الناقضة للمنقوضة، حتى يصح أن تكون أصلًا لها، وإنما يلزم من جهة','3','599',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2231,'قطعه [لطرد] المعنى. فالكتابة الفاسدة قد قطعت طرد المعنى، فيتوجه الاعتراض من هذه الجهة، ويلزم الانفصال.
وقوله: فلا وجه [في دفعه] إلا مسلكان: أحدهما- أن يدعي أن الكتابة الفاسدة صحيحة، [من] جهة مقصودها. يعني بذلك أنها لا [تجري] على أحكام المعاوضات، وإنما يغلب عليها أمر التعليق، وهو المراد بقوله: إن الكتابة وإن فسد عوضها، فليس يفسد تعليق العتق عليها. وهذا تقرير وجهٍ من الصحة، فلم يثمر من جهة فساد العوض، وإنما [يثمر] من [جهة التعليق]، فلا فساد فيه. [فإذًا] لم يثمر فاسد بحال. وهذا أيضا يحقق كون الكتابة الفاسدة، ليست خارجة عن القياس، على خلاف ما سبق، من أنها من قاعدة المعاوضات، ناقضة لقياسها.
وأما قوله: أن لا يلزم في [الأصل] انتقاض القواعد بالمنتزع عنها.','3','600',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2232,'وقد تكلمنا على أن الخارج عن القياس، هل يرد نقضا أم لا؟ أم إذا ثبت كونه مستثنى، فلا إشكال في كونه لا يرد نقضا، وإن لم يثبت كونه مستثنى، ولم يظهر فيه معنى، فهذا فيه نظر، يتعلق بباب النقض. وسنتكلم عليه [هناك]، إن شاء الله تعالى.
قال الإمام رحمه الله: (والضرب الخامس: متضمنه [العبادات البدنية]) إلى قوله (التي [لا] يتعين فيها [مقصود] أولى [وأحرى]). قال الشيخ: (١٠٨/ أ) ما ذكره الإمام في هذا الضرب من العبادات البدنية، أنه (٨٢/ ب) لا يفهم منها معانٍ جزئية مفصلة، [تقتضي] هذه الأعداد، وتعين الأوقات، فلا شك فيه، ولكن يفهم منها معانٍ أخر، أومأ إليه القرآن، قال الله تعالى: {إن','3','601',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2233,'الصلاة تنهى عن الفحشاء والمنكر}. ونحن نذكر من هذه العبادات مثالًا أو مثالين، ونبين ما في ذلك من الأسرار، مستعينين بالله، وهو خير معين.
أما الصلاة، فهي أعظم العبادات بعد تحصيل القواعد، ولذلك كانت ثابتة [الآثار] في [كتاب] الله [- عز وجل -]، قال الله تعالى: {فإن تابوا وأقاموا الصلاة وءاتوا الزكاة}. وقال - عليه السلام -: (بني الإسلام على خمس: شهادة أن لا إله إلا الله، وإقام الصلاة). الحديث. و (أول ما ينظر [فيه] من عمل العبد الصلاة). وإنما كانت أهم [الفروض] وأولاها بالتقديم، من جهة أن','3','602',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2234,'الله [سبحانه] كلف الخلق طاعته على الدوام، والانكفاف عن المعاصي على الإطلاق، وليس يمر على العبد زمان، والعقل فيه حاضر، إلا والتكليف إليه متوجه بفعل طاعات، إما على جهة الوجوب أو الدب، في أعمال الجوارح والقلوب. وكذلك تكليف النهي متوجه إليه على ذلك في السمع والبصر واليد والرجل والبطن والفرج، [وأعمال] القلوب من كبر وحسد، [وبغي] ورياء، وغير ذلك. فإذا كانت الأحكام ثابتة على الدوام، افتقر المكلف إلى يقظة ومراقبة، وذلك لا يكون إلا بدوام الذكر. ولسنا نعني حركة اللسان، بل ذكر القلب وتطلعه لما يرد من الرب، قال الله [تعالى] {ولذكر الله أكبر}. وقال: {والذاكرين الله كثيرًا والذاكرات}. وقال - عليه السلام -: (اذكر الله عند همك إذا هممت، وعند حكمك إذا حكمت، وعند قسمك إذا قسمت). وقال معاذ أو غيره من الصحابة رضي الله [عنهم]: (لا شيء','3','603',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2235,'أنجى للعبد من عذاب الله من ذكر لله تعالى). وقال بعض الصحابة: (لا يستوي من يتصدق [الدينار والدرهم] مع الذاكر الله، الذاكر [لله] أفضل).
وكل هذه الأدلة تدل على أن العبد محتاج إلى ذكر ربه على كل حال. والإضراب عن الذكر مطلقًا، يمنع من الطاعة، وإيجاب الذكر على الدوام يشق على العباد، ويحسم طرق الأعمال، [أعني كثيرًا] من أشغال الدنيا. فأوجب الله تعالى الصلوات الخمس، وندب إلى النوافل في أكثر الأوقات، ووضعها على صفة هي أقرب إلى الذكر والحضور، [وأوجب] استقبال القبلة، تعظيمًا','3','604',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2236,'لله تعالى، إذ أضاف الله تعالى البيت إلى نفسه، وأوجب في ابتداء الصلاة (١٠٨/ ب) التكبير، تعظيمًا لله تعالى، مع حضور النية، فإنه إذا وافق اللسان القلب، كان أقرب إلى الذكر، وأبعد من الغفلة، وأوجب فيها [تلاوة]، [واستكانة]، حتى يصير العبد كالمخاطب] وحرم فيها الكلام، والاستماع إلى محادثة الغير، إلا لاشيء القليل. كل ذلك ليحصل الضبط، فيتيسر الفهم بضبط الجوارح، وفهم القلب. وجعل ذكر الله تعالى بالتكبير شعارًا عند االنتقال من ركن إلى ركن، ليكون تجديد الأركان مستدعيًا [دوام] النية، ورافعًا للغفلات. وجعل العبادة متوجهة على جميع البدن، فكل عضو منه له عمل، فقال - عليه السلام -: (أمرت بالسجود على سبعة أعظم). ووضع فيها الركوع، وهو انحطاط للتعظيم. وكانت العرب تكرهه كثيرًا، ولذلك قال بعضهم عند الإسلام في شروطٍ اشترطها: (وأن لا يجب). يعني الركوع: فقال - عليه السلام -: (لا خير في دين ليس فيه ركوع). وأثبت فيها (٨٣/ أ) السجود، وهو [تعفير] الوجه الذي هو أعز الأعضاء، ووضعه في الأرض، التي هي تراب، إظهارًا للتواضع، وتمام الخشية. ولهذا كان: (أقرب ما يكون العبد من الله تعالى إذا كان ساجدًا). وجعل عند كل ركن ذكرًا، وهو (الله أكبر)، أي الله تعالى أجل','3','605',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2237,'وأعظم، لتستقين القلوب عظمة الله تعالى، وكبريائه وإجلاله.
هذا بيان مقصود هذه الأعمال والأقوال، ثم [جعلها] [متفرقة] على أوقات متعددة، فجعل أول الصلوات [صلاة] الغداة، ليكون الإنسان عند استيقاظه من نومه مقدمًا لأمر دينه، وذاكرًا لربه، فيكون ذلك أبعد لغفلته، [وأدعى] لذكره. فإذا حصلت هذه الحالة في قلبه، بقي أثر وقتًا من يومه. والله تعالى أعلم. وكلن إذا بعد [عهد] العبد، انحل الذكر من قلبه قليلًا قليلًا، ولذلك يجد الإنسان من نفسه عند حضوره مجالس الذكر حالة، ثم لا يستديمها، بل يأخذ الأمر [في] النقصان، فأثبت الله تعالى صلاة الظهر، بعد مضي زمان من اليوم، لتتجدد للإنسان تلك الحالة الأولى، ويبقى أيضًا أثرها، إذا فعلت على وجه ما قررناه، [مدةً] أيضا من الزمن، فرتب صلاة العصر على هذا الترتيب، ثم كذلك إلى آخر النهار. واستفتح الليل بمثل ما استقبل به النهار، وجعل آخر ذلك صلاة العشاء، إذ تلك الحالة في غالب الأمر هي آخر الأوقات، فينام الإنسان، وهو على ذكر الله تعالى. فإذا فعلت [الصلوات] على هذه الجهات، (١٠٩/ أ) صدت عن [الشر]، ونهت [عن] الفحشاء والمنكر، وحركت الدواعي إلى الخيرات، والله تعالى أعلم بما يصلح لعباده.','3','606',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2238,'ثم ندب [الشرع] إلى النوافل في كثير من الأوقات، لعلمه أن الإيجاب يشق على البشر، مع الاهتمام بأمر الدنيا، [وركون النفس] إلى الراحات، وسآمة العبادات. هذا بيان مقصود هذه العبادة، وهي الصلاة. ولذلك جاء: (من لم تنهه صلاته عن الفحشاء والمنكر، لم يزدد من الله إلا بعدًا). وذلك [أن] الرجل الذي قبل المرأة، وسأل رسول الله - صلى الله عليه وسلم - عن ذلك، فأنزل الله تعالى: {إن الحسنات يذهبن السيئات}. يعني بالحسنات. الصلوات.
وسبب ذلك أنه إذا صلى، فقد ذكر الله تعالى، فذلك يحركه على التوبة والندم. فلو روعي في الصلوات هذه الجهات، لحملت على الخيرات، ومنعت من المحرمات، ولكنها تفعل بقلوب غافلة، [وأنفس] بالسرور مشغولة، فلا يظهر أثرها في تحصيل هذه المقاصد. ولقد جاء عن بعض صالحي هذه الأمة أنه سئل عن الصلاة، فقال للسائل: [أتسألني] عن آدابها أم عن تأديتها؟ فقال عن آدابها. فقال: (أقوم بالأمر، وأمشي بالخشية، وأدخل بالتواضع، وأكبر بالإجلال، وأجعل الجنة عن يميني، والنار عن شمالي، والله تعالى مطلع علي، فإذا كبرت لم أظن أني أركع، وإذا ركعت لم أظن أني أرفع، ثم كذلك حتى','3','607',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2239,'تفرغ الصلاة، ثم أستغفر الله [تعالى] من التقصير فيها، إلى الصلاة الأخرى). هذا معنى الحكاية دون نقلها. فإذا وقعت الصلاة على هذا الوجه، نهت عن الفحشاء والمنكر.
وتحريك الرغبات بفعل المندوبات، يستوعب أوقات الإمكان، ولم [يوجب] الله [تعالى] ذلك على العباد، رحمة منه لعباده، فلله الحمد رب السموات ورب الأرض، سبحانه ما أكرمه، لم يطلب من عباده أمرًا يعود عليه منه فائدة، لأنه سبحانه وتعالى يتقدس عن قبول (٨٣/ ب) النفع والضرر. وإنما طلب منهم ما فيه صلاحهم، {وما بكم من نعمة فمن الله}، {قل بفضل الله وبرحمته فبذلك فليفرحوا}. هذه إشارة إلى معنى هذه العبادة، ولو ذهبنا نستقصي أسرار الصلوات، لطال الكلام، ولخرج عن حد الاقتصاد. وهذا الكلام دخيل في الأصول، فلم نر الإكثار منه.
العبادة الثانية- الزكاة: قال الله تعالى: {[وأقيموا] الصلاة وءاتوا الزكاة}. وقال الله تعالى: {فإن تابوا وأقاموا الصلاة وءاتوا الزكاة فخلوا سبيلهم}. فلم يسقط [القتل] إلا بفعل الثلاثة الأمور. وقال - عليه السلام -:','3','608',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2240,'(أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، ويقيموا الصلاة، ويؤتوا الزكاة، فإذا فعلوا (١٠٩/ ب) ذلك، عصموا مني دماءهم وأموالهم إلا بحقها، وحسابهم على الله). [وقال تعالى]: {خذ من أموالهم صدقة تطهرهم وتزكيهم بها}. والمعاني الناجزة في الزكاة ظاهرة.
وفيها مصالح ترجع إلى المعطي [والآخذ]، أما الآخذ فسد خلته، ودفع حاجته، وتفريغ [قلبه] لعبادة ربه، والتعاون على دفع الأعداء، ومقصود الجهاد الذي ترجع منفعته إلى جميع أهل الإسلام، وتخليص الرقاب من الرق إلى الحرية. وقد أرشد تعدد المصارف إلى هذه الجهات، وقضاء دين [المدين]، وإعطاء ابن السبيل المحتاج. وهذه أمور لا تنكر. وفيه أيضا سقوط وجوب المواساة عن ذوي اليسار، [إذ] حصل بالزكاة سد خلة المحتاج.
وأما ما يرجع إلى المعطي، فالطهارة من رذيلة البخل، فإن الإنسان إذا اعتاد الإمساك، استولى الشح على قلبه، واستأنس بإمساك المال، فلا تهون على النفس مفارقته. وإذا اعتاد إخراج الزكاة، كان ذلك سببًا لتعود البذل، والنفس [بما] عودتها [تعودت]، وقد قال الشاعر:','3','609',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2241,'تحلم عن الأدنين واستبق ودهم ... ولن تستطيع الحلم حتى تحلما
يعني أن [الخيرات] تكتسب بالتعود، وحمل النفس عليها كرهًا، ثم يصير الخير عادة، والحلم بعد التحلم، والكرم بعد التكرم. وأظن أن هذا [هو] [مقصود] الآية بقوله تعالى: {تطهرهم وتزكيهم [بها]}.
فقد ظهر في هذه العبادة صلاح في الدنيا والعقبى، وحمل النفس على مكارم الأخلاق، من الكرم والعطف على المحتاج.
وفيه [أيضا] إدخال السرور على القلوب، وزيادة الإشفاق على الخلق. فإن الإنسان إذا وجد رقة على ضعيف، فإن عمل بمقتضى تلك الرقة وأعطاه، ازدادت رحمته له، وإشفاقه عليه. فهذه [الأعمال] كالأغذية لمعاني القلوب، فإن المعاني تبدو في القلوب ضعيفة، فإن عمل بمقتضاها قويت، وإن عطلت عن الأعمال، انحلت وتصرمت. [وإذا] عمل بمقتضاها، رجع أثرها على المعاني بالتوكيد [والتقرير]، فيكون كل واحد منهما ممدًّا لصاحبه. والله تعالى يوفقنا لما [يحبه] ويرضاه، بمنه وفضله، إنه على كل شيء قدير، وهو حسبنا ونعم الوكيل.
والعبادة الثالثة- صوم شهر رمضان: قال الله تعالى: {فمن شهد منكم','3','610',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2242,'(٨٤/ أ) الشهر فليصمه}. وقال: {كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون}. وقال - عليه السلام -: (بني الإسلام على خمس). فذكر [منها] الصيام. وقال - عليه السلام - حكاية عن ربه: (كل عمل ابن آدم له، إلا [الصيام] فإنه لي، وأنا [أجزي] به). وقال (١١٠/ أ): (إذا دخل رمضان، فتحت أبواب الجنة، وغلقت أبواب النار، وصفدت [الشياطين]، ونادى مناد: يا طالب الخير هلم، ويا باغي الشر اقصر). فنبه الشرع على أن صوم رمضان يضعف دواعي الشيطان، ويحرك الهمم إلى [صالح] الأعمال.
والسبب في ذلك أن الشهوات [أسلحة] [الشياطين]، وهي تنبعث من الشبع. فإن الشبع يقسي القلب، ويضعف الفهم، ويثقل [البدن عن العبادة]، ويكثر النوم، وتستولي منع الغفلات. ولذلك اجتنب جماعة من','3','611',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2243,'العرب الشبع، [إبقاءً لفهمهم]. قال عمرو بن العاص: ([متى] بعدت همة امرئ، بات بطينا). وقال - عليه السلام -: (حسب ابن آدم من الطعام لقيمات [يقمن] بها صلبه). ولما كان الجوع يفيد هذه الفوائد، وإدامة الشبع تصد عن هذه المقاصد، وإلزام الصوم على العموم يشق على الخلق، ألزم الشرع العباد صوم مدة من الزمان، لعلم الله تعالى [أن] ذلك يحصل لهم خيرًا، [إذ أنه] لا يضيق عليهم أمر القيام به. ولذلك أن شهر رمضان أقل الشهور معاصي، والقلوب فيه إلى الخير مائلة، وعن المحرمات لاهية. وهذه حكمة ظاهرة، ومصلحة ناجزة.
وقد أجمع علماء طريق الآخرة على أن الجوع من أقوى الأسلحة في مدافعة الشيطان. وقد جاء: (إن الشيطان يجري من ابن آدم مجرى الدم، فضيقوا مجاريه بالجوع والعطش). يعني بتضييق المجاري، إضعاف شهوات النفوس، التي هي أسلحة الشيطان. والمألوف [عند] الناس أن كثرة البطنة تذهب الفطنة. ولذلك يستحب الناس أن يكون [درسهم] [للعلم] أو','3','612',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2244,'عملهم [الحساب] في أول النهار، عند خلاء المعدة من الطعام، ليقرب الفهم في تلك الحال. فهذه تلويحات إلى [بعض] أسرار هذه العبادات.
وفيه أيضا أن الصوم إذا غلب وتم الجوع، قنع الإنسان بما تيسر له، فكان ذلك سببًا للرضا بالموجود، وتلقي النعم بالشكر. وإن قلت بالإضافة إلى حال العبد، وإلا فنعم الله [تعالى] على العبد لا تحصى. وقد جاء عن سالم بن عبد الله أنه دخل على بعض خلفاء بني أمية، فرآه حسن [الحسم]، فقال له: ما غذاؤك؟ فقال: الخبز والزيت. قال: وتشتهيه؟ قال: أتركه حتى أشتهيه). [فالصوم] من أعظم الأعوان على ما يتعلق بقناعة النفس بما يجده الإنسان. والله المستعان.
قال الإمام: (وأما اعتبار البعض من هذا [النوع] بالبعض) إلى قوله','3','613',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2245,'(وليس هو من معاني الشرع في وردٍ ولا صدرٍ). قال الشيخ: قوله: إن اعتبار البعض بالبعض في هذا الضرب، قد (١١٠/ ب) يظهر فيه معانٍ فقهية. هذا عجيب من كون المعنى في أصل القاعدة لا ينضبط، ولا يوقف عليه. فإذا كان أصل القاعدة لا يفهم معناه، كيف يصح فهم المعنى في التفصيل؟ هذا بعيد. ولكن الوجه في ذلك عندي- والله أعلم- أن يكون المكلف يتلقى من الشرع حكما، ويبنى الأمر على أن فيه مصلحة، وإن كانت خفية، أو يشترط في (٨٤/ ب) حصولها شرطا، فيتبين سر الاشتراط، [ثم يجده-[أي سر] فقدان الشرط فيه- بعد أن يبينه الاشتراط]، فيظهر له فساد ما [فقد] الشرط منه.
بيانه: أن الشرع لما ورد بإيجاب الصوم، ولم يظهر له غرض ناجز [يصح] على السبر، غلب على الظن، أنه إنما طلب لمصلحة أخروية، وتلك المصلحة هي الثواب على الفعل الواقع طاعة. وإنما يقع طاعة في غالب الأمر، عند قصد التقرب به، فقال - عليه السلام -: (لا صيام لمن لم يبيت الصيام من الليل).','3','614',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2246,'فإذا ثبت ذلك، وصح اعتبار الآداء بالقضاء، والاكتفاء بالنية في أثناء النهار، فلا يصير الفعل المتقدم على النية قربة، إذ النية قصد، ومتعلقه الحال، أو عزم ومتعلقه الاستقبال. فيظهر من جانب المعاني الوقوف على حقيقة العبادة في الشريعة. وتصور النية امتناع رجوع القصد على الماضي، إذ التخصيص إنما يتصور في متجدد. أما [الحال]، فكيف يتصور تعلق قصد التخصيص به؟ وإذا لم نتصور النية منعطفة على سابق، بطل أن تكون قربة، إذا القرب يفتقر فيها إلى قصد التقرب وامتثال الأمر، ولا يصح أن يقال إنه صائم بعض يوم، إذ ذلك ليس ثابتا في الشريعة. وأيضا، فإنه لو كان إنما صام من وقت النية، لزمه تدارك ما مضى بنية أيضا، فيقصد المكلف في ليلة أن يصوم إلى وقت الزوال مثلا أو غيره. وهذا غير ثابت في شريعة الإسلام. فقد اقتضى القياس بعد ثبوت اشتراط النية، بطلان الصوم الذي [أحدث] بنية في [أثناء] النهار.
وأما اتحاد الركوع وتعدد السجود، فلا [مطمع] للخلق في معرفة سر ذلك.','3','615',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2247,'أما المصير إلى تعيين التكبير والتسليم، فقد اختلف العلماء فيه، فذهب الأكثرون إلى التعيين، وخالف في ذلك أبو حنيفة. واختلف الناس في مستند ذلك، فقال قائلون: مستنده التوقيف، أعني في إقامة غير التسليم مقامه، ويروي فيه حديثا، وهو الخروج من الصلاة بالحدث بعد الإتمام. وليس المقصود ههنا التعرض لتحقيق المسألة، وإنما المقصود [التمثيل].
وقال قائلون: بل مستنده القياس، فاعتبروا غير التكبير [بالتكبير]، بجامع [التحميد]، واعتبروا غير السلام به. بناءً (١١١/ أ) على أنه مناقض للصلاة. فصح الخروج عندهم بعد الإتمام بكل مناقض، من غير تخصيص على حال. ولهم في ذلك كلام ينبني على ما سبق منا الإشارة إليه. وذلك أنهم قالوا: المصلي في التمثيل المعتاد، كالواقف بين يدي الملوك، ومن المستحسن عند الدخول على الملك الثناء عليه، وإظهار الذل والتواضع. هذا المفهوم من','3','616',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2248,'إثبات الذكر عند ابتداء الصلاة.
وأما [السلام]، فقد كان المصلي محالًا بينه وبين الخلق مادام في الصلاة، ومنع من مكالمتهم، وغير ذلك مما يمنع المصلي منه. وجعل الشرع أمارة فراغه، رجوعه إلى ما كان ممنوعًا منه، فيصح الخروج بكل مناقض. وهذا الكلام بين، ولكن غلب التعبد في الباب، فلزم [مراعاة] الألفاظ.
على أن المسألة بينة من جهة الاعتياد. فالمشهور بين [الناس] أن الرجل إذا أقبل على جماعة سلم عليهم، وكأن المصلي كان بعيدًا عن الخلق مشتغلًا بصلاته، فلما فرغ منها، جعل الشرع هذا الذكر عنوان الفراغ، فيحصل منه [مقصد] المناقضة، مع حسن [العبادة]، وإبداء (٨٥/ أ) التحية عند الرجوع إلى الخلق، فليس [ينفك] الاختصاص عن [معنى] الوفاء بالتعبد. والقاعدة غلب التعبد فيها.','3','617',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2249,'وما ذكره أصحاب أبي حنيفة من فهم معنى التكبير. [وقوله]: هذا [يرجع] إلى معرفة وضع الصيغ، وليس الكلام في ذلك، وإنما الكلام في الوجه المقتضي لاشتراط التكبير عند الابتداء، وقد أشرنا إلى ما يراه القوم. وفيه وجه من المناسبة. ولكن الباب باب التعبد، ففي الاقتصار على التكبير، حصول المقصود من الدخول في العبادة، مع الخلاص عن غرر المخالفة. وهذا هو الذي نختاره، وقد بينا وجهه.
قال الإمام: (قال الشافعي [- رضي الله عنه -] في مجاري كلامه في رتب النظر) إلى قوله (من الانهماك في [أوضار] التقليد). قال الشيخ: ما ذكره الشافعي من أن الشرع لابد أن يكون له غرض في تعيين التكبير، مسلم، ولا أحد يذهب إلى أن ذلك جرى على لسان [رسول الله - صلى الله عليه وسلم -] اتفاقًا، غير مقصودٍ','3','618',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2250,'بالاختصاص، فإن هذا لا يتفق إلا ندرة في [قلة كلية]. أما [على] الاستمرار، فتقدير الاتفاق في هذا [محال]. ومن ذهب إلى خلاف هذا، فلا عقل له بحال.
وأما كونه ترتب على ذلك منع قيام غيره مقامه، فهذا ينبني على [قاعدته] في المفهوم. وإلى مثل هذا رجع التقرير، ولكنه قد نقضه على نفسه حيث قال: الألقاب لا مفهوم لها، وإن كان قصد التخصيص ثابتًا فيها. لكن قيل له: [أكل] الفائدة [مقصورة] على ختصاص الحكم بالمذكور؟ [قد] [يقصر] لأسباب غير ذلك. (١١١/ ب) منها: أن يذكره لقياس عليه. ومنها: أن يذكره ليخرجه عن محل الاجتهاد عن وقوعه. ومنها: أن يذكره لكونه أبلغ وأفضل، فيكون تعينه على جهة الندب.','3','619',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2251,'وأما ما ذكره من المثال، [فغير صحيح] التمسك به. وذلك أنه قال - عليه السلام -: (لا تبيعوا البر بالبر) الحديث. إلى آخره. والمعنى الجامع عند الشافعي الطعم، فلو قيل للرسول: فما تقول في الزبيب والأرز والذرة؟ كان يقول: كذلك أيضا. فيقال: فإذا كان الحكم يعم، فلم خصصت ذكر أشياء؟ مع أن قولك: (لا تبيعوا الطعام بالطعام إلا مثلا بمثل)، [لم يعم ذلك]، ولم يقبح النطق بمثل هذا؟ [فكذلك] أيضا في التكبير. لكن لو اتفق انه ليس فيه غرض بوجه، لأمكن أن يعقد بعض المتقدمين الصلاة بغيره، ولم ينقل ذلك بحال. وهذا عنه جوابان:
أحدهما- أنا لا نسلم [أن] أحدًا من علماء الأمة لم يعقد الصلاة إلا بالتكبير، ومن أين يعلم هذا عن الأمة بجملتها؟','3','620',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2252,'الثاني- أنه وإن ثبت ذلك عن جميع الأمة، لم يكن دليلا على [الشرطية] على الحقيقة، لاحتمال [أمر] الندب [بموافقة] ما كان الرسول [- صلى الله عليه وسلم -] عليه.
وأما قوله: وإن اعترفوا [بالتخصيص] [بدءاً]، ثم طمعوا في الإلحاق، فقد تناقض كلامهم. ليس الأمر كذلك، فإنه يصح الاعتراف بالتخصيص مع الإلحاق، وهذا هو باب القياس، [فلا وجه لمنع القياس]، نظرًا إلى قصد تخصيص المذكور بالذكر، نعم، إن منع الخصم فهم المعنى، وإبطال القياس لذلك، فهذا له وجه. واعتماد المعاني الضعيفة في باب التعبدات (٨٥/ ب)، [بعيد] جدًا.
وأما قول الإمام: [ومن] استجاز إقامة عمد الحدث مقام ما اختاره','3','621',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2253,'الشارع من التسليم عند الفراغ من الصلاة، فهو بين معاندٍ [يظهر خلاف ما يضمر]، وبين من أعمى الله تعالى قلبه. فكلام صعب، وركوب مركبٍ عظيم، وجرأة على الله تعالى، وعلى علماء المسلمين، نسأل الله تعالى أن يعفو عنه، وأن يخلصه من غرمائه يوم القيامة.
أما نسبتهم إلى أنهم يضمرون خلاف ما [يظهرون]، فهذا ممنوع شرعًا واعتيادًا.
أما العادة- فإنها تحيل على العدد [الكثير] الكتمان على مر [الأزمان]، وأصحاب أبي حنيفة وقول عند أصحاب [مالك] على ذلك. فكيف يصح فيه العناد على الدوام؟
الثاني- إن هذا حرام، ونسبة العلماء إلى الفسق والمروق، فلا يوثق بالعلماء على هذا على حال.
وأما الوجه الثاني- وهو النسبة إلى عمى القلوب، هذا لا يجوز إطلاقه','3','622',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2254,'في مسائل الاجتهاد، لا عند من يقول المصيب واحد، (١١٢/ أ) ولا على [قول من يصوب] كل مجتهد، كيف وقد جاء في ذلك حديث ثابت، ذكره أبو داود: (أن من جلس في التشهد، ثم خرج منه حدث، فقد تمت صلاته). هذا معنى الحديث دون لفظه. فنعوذ بالله من الإعجاب بالرأي، فإنه من المهلكات، وقد قال - عليه السلام -: (ثلاث مهلكات: شح مطاع، وهوىً متبع، وإعجاب كل ذي رأي برأيه). [وفي حديث آخر: (مروا بالمعروف، وتناهوا عن المنكر، حتى إذا رأيتم شحًا مطاعًا وهوىً [متبعًا]، ودنيا مؤثرة، وإعجاب كل ذي رأي برأيه]، فعليك [نصح] نفسك، ودع عنك أمر العامة). فقول الإمام في هذا المكان من الإعجاب بالرأي، حتى ألحق المسائل الظنية بالأمور القطعية، وارتكب ما ارتكب من هذه الأقوال العظيمة. ولا يليق بحملة الشريعة. نعوذ بالله من شرور أنفسنا، ومن سيئات أعمالنا.','3','623',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2255,'قال الإمام: (ثم إن [أجرى] مجرٍ في هذا القسم كلامًا) إلى قوله (وطلب المعنى حيث لا معنى بعيد جدًا). قال الشيخ: [الأصلح] عندنا الطريقة الأولى، فإن القاعدة [بنيت] على منع القياس. هذا هو الصحيح في الرد على أصحاب أبي حنيفة، إذا اعتبروا التكبير بغيره، فكيف يصح أن يرد عليهم في المصير إلى القياس، بكون القاعدة [بنيت] على منع القياس بالقياس؟ وإثبات القياس في [مقابلة] منع القياس، مناقضة وإلباس.','3','624',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2256,'وأما الطريق الثاني: فإن القائل يقول: القاعدة يجري القياس فيها، ولكن لا يصادف المعنى المقتضي للانحصار [والاقتصار]، إنما كان لنفي المعنى المتعدي. نعم، قد يوجد [السبب] المانع من التعدية، لكنه يجعل الاقتصار على المذكور قضية كلية تتلقى من القياس. [فالصواب] [إذًا] الطريق الأول، [وهو] أن يذكر ما [يذكر]، للإيناس لا للقياس.','3','625',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2257,'قال الإمام رحمه الله: (وقد نجز غرضنا من تقاسيم هذه الضروب) إلى قوله (والقسم الثاني [يحتوي على] ما يفسد من [هذه] الاعتراضات عند المحققين). قال الشيخ: سمي الاعتراض اعتراضًا، من جهة أنه يعرض في صوب جريان الدليل، فيمنعه من الجريان. هذا معنى تسميته اعتراضًا. ثم إنه ينقسم ثلاثة أقسام:
أحدها- أن يتخيل المورد له [أن له ورودًا]، ولا يكون له ورود على الحقيقة. فهذا هو الذي يعبر عنه بالاعتراض الفاسد.
الثاني- أن يكون للاعتراض ورود، ولكنه يتأتى الجواب عنه (٨٦/ أ)،','3','626',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2258,'فيصح من جهة تكلف الجواب، لاعتراضه [من] جهة جريانه، [ولكن] لا يفسد عند إيراد الجواب السديد.
وتارة يتعذر على المستدل الجواب (١١٢/ ب) عنه، [سواء] انقدح عنه جواب أو لم ينقدح، ولا يظن أنه إذا انقدح جواب يتبين فساد الاعتراض، بل لا يتبين فساد إلا [من انحرافه] عن سنن الدليل.
قال الإمام: (القول في الاعتراضات الصحيحة- فمنها: المنع) إلى قوله','3','627',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2259,'(وبيان محله ومنصبه في الجدال). قال الشيخ: ما ذكره [الإمام] في الوجه الأول، وهو منع كون الأصل معللًا. وقوله: إن هذا إنما يتوجه على من لم يذكر تحريرًا بعد، فيخرج الكلام عن الاعتراض على القياس، فإن صورة القياس لا تعقل دون التحرير.','3','628',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2260,'ويرجع الكلام إلى أمر آخر، وهو أن يدعي المدعي أن هذا الحكم علل، فيقال له: وما الدليل على ذلك؟ ولا نسلم أنه معلل. أما إذا [حرر] القياس، فهذا السؤال غير وارد عند الإمام، [وقرره] بما ذكره، أنه يجمع بين [الفرع والأصل] بجامع، وهو مطالب بتصحيح ما ادعاه علة. فإذا ثبت [له] ذلك، ثبت كون الأصل معللًا، من غير حاجة إلى فرض دليل آخر على إثبات كونه معللًا.
وقد تقدم الكلام على هذا في فصل ما يعلل وما لا يعلل. ولكن هذا إنما يكون سؤالًا مستقلًا، على قول من يرى أنه لا يكفي في التعليل إقامة الدليل على صحة الوصف للتعليل، بل لابد من دليل خاص يدل على التعليل. وهذا ذكره بعض الأصوليين، وأبو حامد يميل إلى ذلك في قياس الشبه، والإمام','3','629',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2261,'يقوله في بعض أنواع الشبه. وقد قدمنا الكلام على المذاهب، وما تخيله كل فريق.
والمعتمد في الاستغناء عن إقامة دليل خاص، على كون الأصل معللًا بعد ظهور الوصف الصالح للتعليل، [ما] تمهد من قاعدة القياس، وهو أن الصحابة - رضي الله عنهم - فهموا من الشريعة عند ظهور المعاني عدم الاقتصار على صور المحال. فلذلك حكما بأن المعنى متى ظهر، لزم اتباعه، ووجب القضاء به، وإلحاق الفروع بالأصول، بناء على وجوده. فما ذكره الإمام صحيح. لا بمعنى [أنه] يستغنى عن إقامة الدليل على صحة التعليل، بل ذلك يقع ضمنا من إقامة الدليل على صحة الوصف للتعليل. فهو يثبت ضمنًا، من غير حاجة إلى تخصيص. ولو كلفناه إقامة الدليل على كون الأصل معللًا، ثم كلفناه إقامة الدليل على كون الوصف علة، كان ذلك إلزامه الدليل على كون الأصل معللًا مرتين، وهذا خلف من [الكلام].

قال الإمام: (و<span data-type="title" id=toc-406>النوع الثاني من المنع) </span>إلى قوله (في تقاسيم المركبات، إن شاء الله تعالى). قال الشيخ: لابد من إثبات وجود العلة، ولكن هل يكتفى','3','630',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2262,'في إثباتها بدليل مظنون، أو لابد من القطع بذلك؟ وقد ذكرنا في ذلك خلافا، واخترنا أنه يكتفي في ذلك بغالب الظن (١١٣/ أ). وقسمنا القول إلى أن العلة قد تكون حكما شرعيا، كالنجاسة في السرقين، إذا عللنا منع البيع بذلك. وقد تكون أمرًا محسوسًا، كالطعم في الزعفران، عند من يرى ذلك. وكون التراب ساترًا لنجاسة الماء كالزعفران، أو مزيلا كهبوب الريح. وقد مر ذلك بما فيه كفاية، فلا نعيده.
وأما كثرة وقوع هذا في المركبات، فإن الأصل لم يثبت في المركبات بالتنصيص، (٨٦/ ب) وإنما ثبت باتفاق الخصمين، بناء على مأخذين مختلفين، كثر في المركبات المنازعة في وجود المناط. وسيأتي بيانه في موضعه، إن شاء الله تعالى.
قال الإمام: (والنوع الثالث: منع الحكم في الأصل) إلى قوله (بل يدل حيث [يبني]) قال الشيخ: اختلف الناس فيما إذا توجه على السؤال منع حكم الأصل، هل يتمكن من الاستدلال عليه، [أو لا يتمكن]؟ [أو','3','631',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2263,'يتمكن] في بعض الأحوال دون بعض؟ أو [في] بعض البلدان دون بعض؟ فقال قائلون: إنه يتمكن من الاستدلال عليه، كما يتمكن من الاستدلال على بقية الأركان. وقال قائلون: لا يتمكن من الاستدلال بحال، لأنه غالط في اعتقاد ما ليس بأصلٍ أصلا، وليس ما عينه لكونه أصلا، بأولى من جعله فرعًا.
وقال قائلون: إن كان الخلاف فيه خفيا، صح الاستدلال، وقبل منه الدليل. وإن كان جليا، بحيث لا يخفى على الفقهاء، لم يسمع الدليل عليه.
وقال آخرون: يرجع الأمر إلى عادة البلاد في ذلك. وهذا أضعف الأقوال. ولكن شجعهم على ذلك، أن الجدل شريعة وضعها الجدليون، [فليتبع] كل قوم [ما] وضعوا.
والذي نختاره في ذلك، أن نفصل القول فنقول: إن فزع المستدل عندما','3','632',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2264,'منع حكم الأصل إلى التوقيف، فلا يعاب عليه، ولا يتوقف الاستدلال بالقياس، وإلحاق الفروع بالأصول على انعقاد الإجماع على حكم الأصل، ولو اشترط ذلك، [لقلت] الأصول، وانحسم القياس في أكثر الأمور. ولا التفات إلى خلاف المخالف، مع ثبوت التوقيفات الصحيحة، سواء كانت قواطع أو مظنونات في المواضع التي يكتفى فيها بغلبة الظن، [إذ الملحق] [به] أصل في الحقيقة. فإن نازع الخصم في كونه أصلًا، لم يلتفت إلى منازعته.
أما إذا فزع في إثبات حكم الأصل إلى القياس على أصل آخر، فهذا [يرجع] إلى أمر، وهو القياس على فرع العلة. وقد اخلفوا فيه، والصحيح عندنا امتناعه في الجدل على كل حال، فأنه إن جمع بين الفرع الثاني [والفرع الأول] بجامع الأصل الأول، [فإدخال] الفرع الأول، تطويل طريق من غير فائدة. والجدل مبني على الاختصار، والحذر من الانتشار، حتى [منعوا] الجمع بين دليلين، وإن كان فيه فائدة الإرشاد إلى (١١٣/ ب) تعدد','3','633',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2265,'الطرق، فكيف يقبل ما لا يفيد بحال؟ وإن كان يجمع بين الفرع الثاني والفرع الأول بغير جامع الأصل، فذلك باطل قطعا، فإن الحكم إنما [يثبت] في الفرع بجامع الأصل، ولولاه لم يثبت فيه، فكيف يثبت في الفرع الثاني مع فقدنه؟ هذه مخالفة ظاهرة بين الفرع وأصله، وقد ذكرنا خيال من جوز وأبطلناه.
قال الإمام: (ثم المسؤول لا يدل في كل موضع، بل يدل حيث يبني) إلى قوله (بطريق المعارضة إذا عارض السائل). قال الشيخ: [لما] قصد الجدليون حصر الكلام وضبطه وعدم نشره، قصدوا مع ذلك حصول الفائدة للفريقين، فجعلوا على المستدل الابتداء والاستدلال، وأقاموا المعترض مقام الهادم. وهذا يقتضي أن يمكن السائل من صورة الاستدلال، إذا لم يقصد بذلك تأسيسًا وبناءً، بل قصد إبطال ما [أتى] به المستدل. [وقد لا يتفق له الهدم إلا بالمعارضة، فيقبل منه على القول الصحيح، على ما سيأتي الكلام على','3','634',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2266,'المعارضة في موضعه.
أما إذا رجع المستدل] معترضا، امتنع عليه الاستدلال، كما امتنع ذلك على السائل ابتداء. فلا نظر في تمكنه (٨٧/ أ) من الاستدلال مطلقا على كونه مستدلًا، إنما يمكن من ذلك في كل موضع يكون فيه بانيا. أما إذا صار في مرتبة المعترض، توجه عليه من [الوظائف] ما يتوجه على السائل.
وقوله: وقد يأتي السائل بما يصلح [للبناء]، وهو يبغي به الاعتراض بطريق المعارضة، كما سيأتي. وأما المسؤول فيضطر إلى الاعتراض بطريق المعارضة، إذا عارض السائل، فيقول: إذا وجب في أدب الجدل [منع] المسؤول من الجمع بين دليلين، [فإذا استدل بدليل، وعورض بدليل في مقابلة دليله، وأراد أن يعارض من العارض بدليل آخر، فهذا في صورته جمع بين دليلين]، ولكنه ممكن من ذلك، لأنه ليس يقصد الاستدلال بدليلين لغرض','3','635',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2267,'واحد، إذا قال: إنما منع لكونه في غنية عن أحدهما. أما ههنا، فليس مستغنيا عن الإتيان بالثاني، إذ لو لم يأت به، لكان [منقطعًا]، فمكن من ذلك لكونه محتاجًا إليه، ولتعذر استغنائه عنه، إذ غرضه إثبات معارضة السائل، فينبغي أن يقتصر على قدر الحاجة، ولا يتكلف مزية الترجيح في هذا المكان، إذ في المساواة حصول مقصوده. أما إذا تكلف ترجيحًا، فقد عدل عن المقصود السديد، والطريق الحسن المستقيم، وصار في هذه الحالة كالجامع بين دليلين، فلا يسوغ له ذلك في الجدل.

قال الإمام: ([و<span data-type="title" id=toc-408>النوع] الرابع من المنع: </span>المنع من كون ما أبداه','3','636',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2268,'المستدل علة) إلى قوله (وقد انتهى غرضنا من القول في المنع). (١١٤/ أ) قال الشيخ: ذكر الإمام أن [وجوه] المنع أربعة، وقد ذكر خمسة، إذ قال: المنع من [أصل] التعليل، والمطالبة [بتعيين] [التعليل]، والمطالبة بتحقيق ما ادعاه [المعلل علة]، ومنع الحكم، والمطالبة بإثبات ما عينه، ولكنه قد قال: إن أصل التعليل، وتعيين العلة يثبت بمسلك واحد. فعدها وجهًا واحدًا لذلك.','3','637',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2269,'وأما قوله: إن المنع يتوجه على الأصل، ويقدر متوجهًا على الفرع، يعني أنه إذا منع حكم الأصل، فهو لمنع حكم الفرع أجدر، وكذلك إذا منع كون الوصف علة لحكم الأصل، قدر ذلك منعًا في [الفرع] أيضا، وكذلك باقي الوجوه، فلا يتوجه على الفرع تخصيصًا، إلا منع وجود علة الأصل في الفرع. أما إذا منع كونها علة في الفرع، وقد دل المستدل على كونها علة لحكم الأصل، لم يصح هذا [المنع]، لما قررناه من إثبات القياس، ووجوب اتباع الحكم العلة دون خصوصية المحل. وهذا الكلام واضح.

قال الإمام: (و<span data-type="title" id=toc-409>من [الاعتراضات] الصحيحة: طلب الإخالة) </span>إلى قوله (فقد شبه تشبيه [مغلبًا] على الظن). قال الشيخ: ومن أهم الأسئلة طلب الإخالة، كلام صحيح، وهو المطالفة بإبداء وجه الدليل.','3','638',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2270,'وأما قول القاضي: إن هذا ليس باعتراض، لأنه [لو] لم يذكر الإخالة، لم يكن آتيا بصورة الدليل. ليس الأمر كما ذكره، فإن الدليل هو الذي يكون فيه ارتباط بالمدلول، فإذا ذكر المستدل ذلك، فقد استدل على الحقيقة، اللهم إلا [أن لا] يكون في اللفظ تعرض لذكر الجهة المناسبة، فهذا جمع بغير جامع. أما إذا كان الوصف المناسب مذكورًا معتمدًا عليه جامعًا، فقد أتى بالدليل على التحقيق.
وأما القاضي فإن له في الجدل طريقة انفرد بها، ولم يساعده عليها أحد، وهو أنه كان (٨٧/ ب) يرى أن يذكر المستدل في مناظرته كل ما يعتمد المجتهد في نظره، فيذكر الدليل والاعتراض عليه، والجواب عن الاعتراض، وتوجيه الاعتراض على الجواب، وتكلف الجواب عنه، إلى استقصاء كل ما يتعلق بالمسألة.
وهذا أمر اتفق الناس على خلافه، فإن المناظرة شرعت لتذليل طرق الاجتهاد، وحصول المقصود للسائل والمجيب جميعا، ولذلك شرط فيها أمور','3','639',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2271,'لا تشترط في حق المجتهد، كامتناع الجمع بين دليلين، ومنع السائل من الاستدلال، امتناع الترجيح عليه إذا عارض. وكل هذه الجهات غير ممنوعة في حق المستدل. وإنما فعلوا ذلك لحصول الضبط وعدم النشر، وأن يحصل المقصود على قرب، وأن يذلل الطرق بالإضافة إلى الفريقين جميعًا. وهذا غرض صحيح، ومقصد مستقيم.
فإذا كان هذا رأيه، فكيف (١١٤/ ب) به في إبداء الإخالة؟ فالصواب إذًا قبول الجمع، إن اطلع السائل على وجه الفقه، اكتفى بذلك، وإن لم يطلع عليه، وسأل الإرشاد إليه، فإن كان اللفظ ظاهرًا في الدلالة على ذلك، لم يلزم المستدل غيره، وإن كان خفيا، فعليه إرشاده إليه، وتنبيه عليه.
وأما قياس الشبه، فإذا أمكن أن يكون طرديًا مطرحًا، أو شبهًا مقبولًا، فهل على المسؤول أن يبين ذلك مطلقًا، أو لا يلزمه البيان بحال؟ هذا فيه [اختلاف]، [قدمت] الكلام عليه. وأبو حامد يختار أنه لا يلزم، لتعذر الانفصال. فإنه إن عدل في التقدير إلى باب المناسبة، فقد أسقط التمسك بالوصف الشبهي. فإن سلك مسلك السبر، فهو كذلك أيضا، وإن سلك مسلك الاطراد والانعكاس، عند من يقول به، فهو طريق مستقل برأسه، فتفوت خاصية','3','640',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2272,'الشبه. وينقل عن المتقدمين سقوط المطالبة، فإنهم كلفوا الخصم الاعتراض. وقد وجهنا عليه سؤالًا، وهو الوقوع في أبواب الطرديات، وهو محسوم، فأجاب عنه: [بأن الطرد الشنيع يمكن إفساده على القرب. وإن لزم من قبول مطلق الجامع الوقوع] في الطرديات، لزم من إبداء جهة الربط الوقوع في إبطال الاعتماد على الأشباه، وكل في الشريعة محذور، فيرتكب أخف الأمرين، وهو الاعتماد على مطلق الجامع، [وتكليف] الخصم الاعتراض، فإنه سهل لا عسر فيه. فلو بان [بفرقٍ] بين الأصل والفرع، [فبذكر] صورة الأصل في معرض الفرق.
وأما الإمام [فإنه] لا يرى ذلك، ويقول: لابد للمستدل من إبداء كون الوصف ليس بطردي. وذلك مشكل عليه، ولكنه يرى أن الشبه يعتمد على','3','641',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2273,'أصلين، ووجه ثالث، فأحد أصليه: أن يعتضد بالأمثلة. والثاني: أن يشتمل على معانٍ كلية. والثالث: أن تلجئ الضرورة إلى التعدية، فلا يصح إلا وصفا واحدًا مثلًا. فإذا وجد بعض هذه [الأوجه]، فقد خرج عن أبواب الطرديات، ودخل في أبواب الأشباه. هذا وجه الجواب عن السؤال على أصل الإمام، فلا تستقر قدم الجامع إلا بإبداء وجه [الجمع]، كان قياسه معنويًا أو شبهيًا.

قال الإمام: (و<span data-type="title" id=toc-410>من الاعتراضات الصحيحة: القول بالموجب) </span>إلى قوله (فكيف يتصور أن يتطابقا والأمر كذلك)؟ قال الشيخ: معنى القول بالموجب: (٨٨/ أ) تسليم ما ذكره المستدل مع استباق الخلاف. ومعنى ذلك: أن يكون دليله لا يتعرض لحكم المسالة التي فيها النزاع. وهذا أمر ملتبس، فإنه لا يخفى على ذي بصيرة أن الاستدلال على غير مسألة النزاع، بحيث لا يرتبط (١١٥/ أ) بعمل النزاع، لا يعتد [به] على حال، ولا يقع في','3','642',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2274,'ذلك من له أدنى بصيرة وأيسر اعتبار، والاستدلال على المطلوب، لا يمكن القول بموجب الدليل، مع بقاء النزاع، فكيف الخلاص من ذلك؟
فنقول: إنما يمكن على وجه، وهو أن يكون المستدل تخيل من الخصم مانعًا لحكم المستدل، بحيث [يقدر] أن لو بطل ذلك المانع، لم يمنع الخصم الحكم، فلما تخيل ذلك عنه، جعل عمدته في الاستدلال لإبطال ما تخيله [مانعا]، ظنًا منه أنه إذا بطل كونه مانعا، سلم الحكم، فكأنه قد استدل على غير الحكم المسؤول عنه، إذ استدل على أن الأمر المذكور غير مانع من الحكم المطلوب. وإذا لم يكن مانعًا، لزم الحكم. وهذا غلط، فإنه لا يلزم من نفي المانع ثبوت الحكم، إذ يفتقر إلى حصول مقتضيه، ويكون [المسؤول] على هذا تخيل من المعترض تسليم المقتضى بكماله، وإنما بقي الحكم، لتخيل مانعه، فإذا أقيم الدليل على إبطال المانع، لزم الحكم [من غير] [ريب].
وأما اختلاف الأصوليين [في أنه] هل هو اعتراض [أم لا]؟ [أما','3','643',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2275,'قول من قال إنه ليس باعتراض]، يعني على الدليل، فإن الخصم قد سلم دلالته، ولم ينازع في ذلك. وقد قلنا: إن الاعتراض هو الذي يعرض على [الدليل] فيمنعه من الجريان. فإذا لم يكن للكلام ورود على الدليل بحال، لم يكن اعتراضًا عليه. وأما من قال إنه اعتراض، فإنه يعني بذلك أن المستدل لا يتم غرضه بالإضافة إلى الحكم الذي قصد إثباته، فقد صار منقطعا بذلك.
وأما قوله: ولا يتصور قول بموجب، ومضمون العلة نفي حكم وإثبات حكم. معناه: أن المستدل إذا كان يثبت حكمًا، فرتب الحكم المطلوب على علة، وسلم له ذلك، لم تتصور المنازعة بعده، وكذلك إذا كان غرضه نفي الحكم ورتب ذلك على قياسه، وسلم له صحة الترتيب، لم يبق للمنازعة في الدليل سبيل. وهذا كلام مستقيم.
قال الإمام رحمه الله: (نعم، إذا قال الحنفي في مسألة ماء الزعفران: ماء طاهر) إلى قوله (فلا يجد المعلل محيصًا عن التعرض للنقض، أو القول بالموجب). قال الشيخ: ما ذكره الإمام، مثال لما قررناه، وذلك أن أبا حنيفة','3','644',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2276,'يرى أن الماء طهور، ولا ينازعه الشافعي في ذلك، فإذا قضى الشافعي أن الماء المتغير لا يصح التوضؤ به، مع أنه طهور في الأصل، لم يبق منع التوضؤ إلا لما يقدر مانعًا، إذ يختلف الحكم، إما أن يكون لنفي المقتضي، أو لوجود المانع، فإذا كان المقتضي حاصلًا، تعين أن التخلف للمانع. وأبو حنيفة أفتى بصحة التوضؤ. ولما طولب بالدليل، تعرض لنفي ما تخيله مانعًا عند (١١٥/ ب) الشافعي، فقال: ماء طاهر خالطه طاهر، فالمخالطة لا تمنع [التوضي]، يشير إلى نفي المانع، وقد ثبت المقتضي، فليثبت [هذا]. هذا مقصوده، وإن لم يصرح به في الدليل، فيقول خصمه: أسلم أن (٨٨/ ب) المخالطة بمطلقها لا تمنع. وقد اختلفوا في أنه: هل عليه أن يبين مانعًا آخر، أو لا يلزمه ذلك؟ فمن قال: [إنه] لا يلزمه، تمسك بأنه إذا صح له منع ما ذكره','3','645',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2277,'خصمه، اكتفى بذلك، ولم يفتقر إلى إبداء غيره، ويكون بمنزلة ما لو منع الخصم الدليل، فإنه لا يلزمه إبداء دليل على ما يحكم به، فكذلك هذا. وهذا هو الأصح.
وقال قائلون: لابد من إبداء مانع آخر في حق السائل. وليس ذلك بمثابة ما إذا منع المستدل الدليل، فإن السائل لا يجيز ذلك. ويصح أن يكون مستند الفرق عندهم ما أشرنا إليه، من الاعتراف ضمنا بوجود المقتضي، فيفتقر إلى إبداء مانع غير ما تعرض المجيب لبيانه وإبطاله. [والإمام يرى] أن ذلك غير لازم، ولكنه إن أمكن، فهو حسن بالغ. ولذلك قال: فإن تمكن السائل [من إبداء] مقتضىً آخر، [فهو الغاية] في هذا الفن. وهذا يدل على أنه غير لازم.
وقوله: [والغالب] أن يكون المستدل ذاكرًا لبعض ما هو علة عند','3','646',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2278,'الخصم، يعني أنه وقف على بعض المانع، وتخيله مانعًا عند الخصم بكماله. فإذا تعرض لإبطاله عند اعتقاده كماله، وقد سبق المقتضي في ظنه، لم يبق إلا تسليم حكمه، ويكون قد غلط في أن الذي ذكره هو كل المانع عند خصمه. فلو لم يكن الأثر في المنع، لبعد التعذر له أن يكون ذلك نهاية الجهل بمذهب الخصم، بل تخيل أن مطلب المخالطة مانع عند [الخصم]، وليس كذلك، بل للمخالطة أثر [لا] على الاستقلال.
فإن قال: والمخالطة المغيرة لا تمنع التوضؤ، ألزم السائل القول بالموجب، فإن المخالطة مع مطلق التغيير لا تستقل بالمنع، فإن زاد وقيد الاحتراز [بتفاحش التغير] مع إمكان الاحتراز، [فهذا] هو المانع عند الشافعي.
فإذا حصل التعرض له بكماله، لم يسلم الخصم صحة التوضؤ به. فإن اقتصر على ذكر كونه ليس بمانع، منع الخصم ذلك، وإن قال: فيجوز التوضؤ به، انتقضت العلة، ولم يجد أصلا يقيس عليه، فلا ينفك عن القول بالموجب،','3','647',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2279,'إن لم يتعرض لكمال المانع، وإن كمل، منع الخصم الحكم، ولم يجد المعلل أصلًا يرجع إليه، وهو معنى قوله: مضيق بديع.
قال الإمام رحمه الله: ([ومما] يطرأ في هذا الفن شيء ليس للرد والقبول [فيه مجال]) إلى قوله (فلا يمكنه-[والحال] هذه- أن يقول بموجب العلة) , قال الشيخ: قوله: وقد يطرأ في هذا الفن شيء (١١٦/ أ) ليس للرد والقبول فيه مجال، معناه أن يكون في الكلام احتمال، فيحمله المسؤول على جهة، ويحمله السائل على غيرها، [فيجر] التفاوض لبسًا. والمثال الذي ذكره: أن العلماء اختلفوا في المرأة العاقلة إن مكنت مجنونًا، فزنا بها، قال أبو حنيفة: لا تحد. وقال مالك والشافعي: إنها تحد.','3','648',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2280,'واتفقوا على ما إذا زنى العاقل بمجنونة أنه يحد. فجعل [الإمامان] هذا أصلًا، [وقالا: ] الزنا سبب الرجم والجلد، وقد وجد في تمكين العاقلة مجنونا، فيجب أن تحد. وتعرضوا لإبطال كون أحدهما مانعا من الحد في حق العاقل، فهو أيضا تعرض لتخيل مانع ونفيه، فيقول أبو حنيفة: أقول بموجب ما قلتم في أن الجنون ليس درائا، وإنما الدارئ خروج فعلها عن كونه زنا.
وهذه العبارة فيها ضعف، والصحيح فيها أن الأمر يرجع إلى المنازعة في المقتضي، فيقول: لا أسلم وجود المقتضي، إذ المقتضي الزنا، وليست (٨٩/ أ) العاقلة ممكنةً زانيا، وإذا لم يكن زنًا، لم تكن مزنيا بها، فامتنع الحكم، لفقدان مقتضيه، لا مانع منه، فيقول المستدل: إن صح ما قلت، فجنون الواطئ هو الذي أخرج فعلها عن كونه زنا، فلم يمكنك إسقاط أثر زناه بالكلية. وإذا قصدت في الاستدلال أن زناه لا يؤثر بوجه، ولست تقدر على القول بموجب ذلك، إذ لو قلت بموجبه، لأوجبت الحد عليها، فيقول المعترض: نصب الزنا علة، وهو عندي علة العلة. وإطلاق التعليل بالزنا، يشعر بكونه [مناسبا] للحكم، فيجر التفاوض لبسًا على هذا التقدير.','3','649',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2281,'وقوله: والذي نختاره للمعلل أن يصون علته عما يلحق بمجملات الألفاظ، [فيقول: ] فلا ينتهض الزنا سببًا، فلا يمكن القول بالموجب على هذه العبارة، فإنه إذا قال: فلا ينتهض الجنون سببا، أراد بذلك أنه لا التفات إليه بوجه. ولا يمكن أبو حنيفة القول بموجب ذلك، فإنه يثبت لزناه أثرًا من حيث الجملة، [بناء] على أن علة العلة. هذا مراد الإمام ههنا. ولابد من البحث [عن] حقيقته لغة وعرفها للفقهاء، إذ بذلك يتضح [مقصود] هذه المسألة.
أما السبب في وضع اللغة: فهو عبارة عما يحصل المسبب عنده بواسطة العلة، فإن أصله [الحبل] والطريق، [سمي] الحبل سببًا، لحصول الماء عنده بواسطة الاستقاء، وسمي الطريق سببًا، لحصول الوصول إلى البلد بالسير','3','650',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2282,'[فيه]. فما يحصل [الشيء] عنده [لا به]، سمي سببًا. فاستعاره الفقهاء وأطلقوه على أربعة أوجه:
أحدها -وهو الأقرب إلى وضع اللغة: ما يطلق (١١٦/ ب) في مقابلة المباشرة، كحفر البئر مع التردية فيه، إذ يقال: الحافر صاحب سبب، والمردي صاحب علة.
والثاني -إطلاقهم السبب على علة العلة، وإن لم تكن مباشرة للحكم، وهذا بمثابة الرمي، فإن الموت حصل بالجرح لا بالرمي، ولكن الجرح حصل بالرمي، فيسمى علة العلة، سببا للحكم المرتب على العلة المباشرة.
الثالث -تسميتهم العلة مع تخلف وصفها سببا، إذ يقال: اليمين سبب الكفارة عند عدم الحنث، وملك النصاب سبب لوجوب الزكاة دون الحول.','3','651',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2283,'[وبهذا] يفرق الفقهاء بين السبب والشرط، [فيقولون: ] الغنى سبب، والحول شرط.
الرابع -تسميتهم العلة بكمالها سببًا، [فيقولون: ] سبب الحد الزنا، وسبب القصاص القتل. فهذه الأربعة أطلقها الفقهاء على السبب، وبعضها أقرب إلى وضع اللغة من البعض. وأقربها ما يطلق في مقابلة المباشرة، كحفر بئر مع التردية فيه، إذ الهلاك حصل بالتردية لا بالحفر، ولكن لابد من الحفر، كما أن الماء حصل [بالاستقاء]، لا بالحبل، وكذلك ما يضاهي هذا.
الثاني: علة العلة تسمى سببا، ووجهه أنه لما حصل الهلاك بغيرها، وهو الجرح مثلا، ولكن لابد من الرمي، إذ لولا الرمي، لم يحصل الجرح، فأشبه ما يحصل الشيء عنده لا به. ولكن جهة البعد عن الأول، أن الحبل ليس علة الإسقاء، بخلاف الرمي؛ فإنه علة الجرح. [فبهذا] الوجه بعد الثاني عن الأول بعض البعد.','3','652',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2284,'وأما الثالث: وهو تسميتهم العلة عند تخلف وصفها سببا، (٨٩/ ب) فمن جهة أنها لما حصلت، ولم يحصل الحكم، نزلت منزلة الحبل، إذا لم يقع إسقاء، فحسن إطلاق السبب عليها من هذا الوجه. ووجه البعد: هو أنه إذا حصل الوصف الباقي، لم يحل الحكم عليه وحده، وأحيل على المجموع، بخلاف الحبل بالإضافة إلى [الاستقاء]، فإنه إذا حصل الماء، لم يحل على الحبل، بل على الإسقاء.
وأما إطلاق الرابع على العلة بكمالها، فوجهه أن علل الشرع علامات، والأحكام تثبت بالنصوص عند وجدان العلل، فأشبه ما يحصل [العلم] عنده لا به. وهذا ضعيف، لأن علل الأحكام، وإن كانت لا توجبها لأعيانها، فقد تنزلت في نظر الشرع منزلة المقتضيات، حتى نتبين لولا هذه المصالح، لم تشرع هذه الأحكام، والحكم إذا حصل أحيل على علته. فمن هذا الوجه، كان هذا أبعد الاصطلاحات عن وضع اللغة.
فإذا ثبت ذلك، فالإمام نزل السبب على بعض (١١٧/ أ) هذه الوجوه الأربعة، وهو إطلاقه على ما لا أثر له مستقلا، [ولو] كان له أثر [لاستقل]. فإذا قيل: لا يكون [هذا] سببا، فقد نفى ما له أثر، وإن كان','3','653',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2285,'[لا يستقل]، فلا يتصور على هذا قول بموجب، إذا كان لما ذكره أثر عند الخصم. فإذا قال الشافعي: فلا ينتهض الجنون سببًا، ومقصوده أنه لا أثر له على حال، فلا يتصور من أبي حنيفة أن يقول بموجب ذلك، إذ الجنون له أثر من حيث الجملة، فيمتنع عليه القول بالموجب على هذا التقدير.
أما إذا أطلق السبب على العلة بكمالها، فلا يكون للعدول إلى لفظ السبب محصول في تحقيق مقصود. والله المستعان.

قال الإمام: (و<span data-type="title" id=toc-413>من الاعتراضات [الصحيحة]: النقض) </span>إلى قوله (سوى','3','654',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2286,'ما عداه المتمسك [بالطريقة]). قال الشيخ: الذي ذكره المتقدمون، كلام صحيح، والإلزام نقض على ما [أرادوه]، ولذلك أن المتمسك بالمعنى، إنما تمسك به، بناء على أنه مقصود الشارع، وبين ذلك بورود الحكم على وفقه. وإذا كان المعنى ثابتا، والحكم [متخلفا]، لم تبق غلبة الظن في نصبه، إذ تبينا بالإعراض عنه، ثبوت حكم يضاد ما يقتضيه، خلاف ما كنا نظنه من نصبه.
[فلئن] قيل: ثبوت الحكم على [وفقه] شاهد له. قيل: ونفي الحكم مع وجوده شاهد عليه. فهذا معنى قولهم: إن النقض يلحق العلة بعد أن نقضت','3','655',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2287,'بالأقوال المتكافئة، لا بمعنى جهة التعارض في الشهادة بلا مزية. هذا مراد القوم، وهذا يطرد في المعاني المناسبة، كما ذكره هو في الأوصاف الطردية، ولم يرد المثال في الطرديات، لغرضٍ يختص بها، ولكن جرى ذلك اتفاقًا.
قال الإمام رحمه الله: (٩٠/ أ) (ومما نمسك به هؤلاء أن قالوا: من ادعى علة) إلى قوله (واستمرت على الطرد في غيره). قال الشيخ: الذي ذكره هؤلاء صحيح، وذلك أن وجه إسناد الحكم إلى العلة، أنا تبينا باستقراء الشريعة أن الأحكام إذا ظهرت في موردها معانٍ، أتبعت الأحكام المعاني، ولم','3','656',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2288,'تقتصر على صور المحال. هذا هو الغالب في الشريعة. فنحن يغلب على ظننا لذلك تعميم الحكم، حيثما صودف المعنى، استنادًا إلى الشرع، لا إلى [التشهي]، والحكم من قبلنا، بناء على أن ذلك غالب عادة الشريعة. وقد يكون المعنى وفاقيا، وهو قليل، فإذا وجد المعنى، والحكم تخلف شرعًا، تبينا أن هذا المعنى ليس من الأمر الغالب، إذ لو كان منه، لساوقه الحكم في جميع صوره. فالتخلف يخرجه عن الأمر (١١٧/ ب) المعتاد، وتبين أنه من المعاني الاتفاقية.
وقول الإمام: إني [أطردها]، ما لم يمنعني [منه] مانع. يقال له: إنما طردته، ظنا أن الحكم يتبعه دون خصوصية المحال. فإذا ثبتت المفارقة، بطلت غلبة الظن، ولم يبق إلا صورة المعنى من غير استناد إلى عادة الشرع، والمعاني لا تقتضي الأحكام لأعيانها.
قال الإمام: (ومما تتعلق به هذه الطائفة: أن من يطرد العلة) إلى قوله (من فرض صدورها من كاذب). قال الشيخ: الذي ذكره القوم صحيح، وعبارة','3','657',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2289,'التحدي غير مستحبة. والمراد أن [الاعتماد] على العلة، مشروط [بغلبة الظن، أنها باعث الشرع على الحكم. من التناقض أن يكون باعثًا على الحكم، مع عموم النصب ويختص الحكم]. [فهذا] معنى قولهم: إنه لا يأتي أحد بمثلها، أي لا يغلق الشرع الحكم المناقض لحكمها على غيرها، إذ قد فرض التفاته إليها، [فإذا ثبت ضد حكمها، لم تبق غلبة الظن بعموم نصبها، مع ما استقرت من عموم نصب المعاني الصحيحة]. فإذا لم يثبت عموم النصب، فات وصف الصحة، وامتنع الاعتماد عليها. فترك الإمام جميع هذا الكلام، واعتمد على لفظ التحدي، وذلك اشتغال بالنشور، وحيد بين عن المقصود.
وأما قوله: إن المعجزة لا تدل على الصدق قطعًا، كلام رديء، وإن لم يؤول كان عظيما في الدين، فإنها إذا لم تدل على الصدق قطعًا، لم يكن تصديق الأنبياء معلومًا، بل إما مظنونًا أو معتقدًا، ونعوذ بالله من ذلك، فلعله أراد: لا تدل على الصدق عقلاً، ووضع موضع ذلك قطعا، وهذا تجوز رديء، ولكن لا يظن به غير ذلك. وقد قدم [هو] في مقدمة الكتاب: أن العلم','3','658',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2290,'حاصل بتصديق الأنبياء، بناًء على العوائد، فيرجع الأمر إلى نفي [الدلالة] العقلية لا القطعية.
قال الإمام: ([وربما] [يستدل] القاضي لهؤلاء) إلى قوله (فلا وجه لما ذكره القاضي إذًا). قال الشيخ: الذي ذكره القاضي، صحيح، وذلك أن المعنى المطرد لا خلاف في اعتماده عند القائسين، وهو محل مجمع عليه عند الجميع. أما المعنى [المنقوض]، فلم يثبت التعليق به. وهذا كلام صحيح، فإذًا ليس الاعتماد على المعنى المنقوض منقولاً عنهم [التعليق] [به]، وليس أيضًا المعنى مقتضيًا الحكم لعينه، ولا يصح الاعتماد عليه، قياسًا على المجمع عليه. وإذا لم يكن عليه دليل، وجب [اطراحه].','3','659',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2291,'[وما] ذكره الإمام (٩٠/ ب) من أنهم رضي الله عنهم ما كانوا يعتنون بتعين أصلٍ، وتلقي معنًى منه، فهو وإن كان كذلك، إلا أنهم قد فهم منهم إتباع المصالح لأعيانها، لكن لفهمهم أن الشارع قصد لربط الأحكام بها. وهذا عند الانتقاض (١١٨/ أ) لا يظن، لظهور جانب الاعتراض.
وأما قوله: ولم يكن كلامهم محررًا مزورًا في النفوس، منضجًا بنار الفكر، [منتقدًا بيد] السبر. كلام رديء، وظاهره يوهم أنهم ما كانوا يتثبتون فيما يأتون، إذ قال: لم يكن مزورًا في النفوس، [منضجًا] بنار الفكر، ولكن [لا] [يريد] ما [ينبئ عنه ظاهر] كلامه، وإنما يريد بذلك أنهم','3','660',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2292,'ما كانوا يتحرزون كل التحرز، على ما يعتاده بنو الزمان. وكان كلامهم يجري كثيرًا على التوسع والتجوز، [لفصاحة] الألسنة، وتمام الإدراك، والعلم بمقاصد الكلام، وفهم بعضهم عن بعض، وقد يكتفي الفهم بالإشارة، ولا يفهم البليد إلا بكمال التصور في العبارة.
وأما إطلاق القول بأن الكلام مزورًا في النفوس، ولا منضجا بنار الفكر، فذهاب عن التحصيل، ونسبة القوم إلى المجازفة في القول، فالله تعالى قد رفع قدرهم عن ذلك، إذ هم القدوة والأسوة، وعلماء الزمان بالنسبة إليهم، كالمبتدئ للطلب في زماننا. نسأل الله تعالى أن يعلمنا ما علمهم، ويستعملنا [فيما] استعملهم، ولا يخالف بنا عن طريقتهم، إنه على ما يشاء قدير.
قال الإمام: (وأما [من لم ير] النقض مفسدًا للعلة) إلى قوله (وإنما','3','661',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2293,'[جولانها] في المظنونات). قال الشيخ: وجه تمسك هؤلاء بتخصيص العموم، أن التعويل على عموم التعلق بالمعنى حيث وجد، [لما] استقر في الشرع من تعليق الأحكام على المعاني على التعميم، فيصير ذلك مضاهيًا للتعلق بصيغة العموم حيث وجدت. ثم قام دليل التخصيص، لا يمنع من التعليق [بالصفة فيما وراء محل التخصيص، فكذلك قيام دليل على منع الاعتماد على العلة، لا يمنع التعلق] بها في غير ذلك المكان. وانفصال القاضي عن ذلك، وذهابه إلى أن اللفظ مجمل، لا يفيد، فإن القاضي يقول: إذا خصص اللفظ العام، وجب التمسك به في غير محل التخصيص، وينقل في ذلك الإجماع، فكيف يصح منه ذلك الانفصال، على أنه إنما يذهب إلى الإجمال، على تقدير انتفاء القرائن. وهو معترف بالاستغراق [إذا] دلت القرائن عليه؟ فإن كانت','3','662',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2294,'الدلالة قطعية، امتنع التخصيص، وإن كانت ظنية، جاز التخصيص، ووجب التمسك فيما وراء ذلك، فلتكن العلة إذا خصصت كذلك.
وأما ما وجهه المعتزلة، فلازم على ما قرره الإمام، فالشافعي أيضًا [يسلك] مسلك المعتزلة، في أن الصيغة إذا تجردت عن القرائن، كانت نصًا، ولكن لي ذلك من جهة امتناع تأخير البيان إلى وقت [الحاجة]، وإنما ذلك عنده يرجع إلى (١١٨/ ب) وضع اللغة وعرف الاستعمال.','3','663',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2295,'[فالصيغة] في العموم، من النص الذي يزول عن مقتضاه بالقرائن. واللفظ ينقسم إلى ما هو نص، لا يتأتى انصرافه عن مدلوله [تقديرًا]، كألفاظ الأعداد، وإلى ما يكون نصا على تقدير انتفاء القرائن المانعة له من الجريان، على مقتضى الوضع الأصلي. (٩١/ أ) وصيغ العموم من هذا القبيل.
أما انتقال الظاهر بالقرائن، فجائز على الإطلاق، بل هذا يصح في المجملات، فضلاً عن الظواهر. والإمام يسلك مسلك الشافعي في بعض الصيغ، كأدوات الشرط، فإنها إذا تجردت عن القرائن المخصصة، كانت عنده نصوصًا. وقد تقدم ذلك في كتاب العموم والخصوص.','3','664',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2296,'وأما قوله: إنا لو رددنا القياس، لم تعتمد صيغ العموم، هذا منه مخصوص بالصيغ الظواهر، بناء منه على أن الأحكام كلها معلومة. وهذا إنما يصح، بناء على القول بتصويب المجتهدين. أما نحن إذا صرنا إلى أن المصيب واحد، فلسنا نقطع بحكم رتب على أمارة مظنونة.
وقوله: إن الحكم المبتوت لا يترتب على أمر مشكوك فيه ولا مظنون، لأن العلم لا يترتب على [الظن]، كلام صحيح، ولكنا لا نعترف بأن جميع أحكام الفروع معلومة بحال. وأما الانفصال عن الإلزام فظاهر، وذلك أن تخصيص العموم، إنما لم يمتنع التعلق به في غير محل التخصيص، إما للإجماع المنعقد على ذلك، على ما ذكره القاضي، وقد تقدم ذلك في كتاب العموم والخصوص، وإما لكون اللفظ متناولاً لجميع المسميات. فإذا منع','3','665',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2297,'مانع من التعلق به في بعضها، فلا [يمتنع] ذلك من التعليق به في غير محل التخصيص. وهذا يناظر ما ثبت كونه علة بوضع الشرع، ثم ورد بعد ذلك استثناء، فإن ذلك غير مانع من التعلق.
أما إذا كان مستند النصب شهادة [للحكم] الوارد على الوفق، فإنه إذا ثبت النقض، بطلت الشهادة، فلا يبقى إلا نصب الوصف. وكما جرى الحكم على وفقه في صورة، فقد انقطع عنه في أخرى، فلا يبقى لغلبة ظن النصب وجه على حال. [فليس] معنى صيغةٍ تتناول بقية الصور، حتى يرجع التعلق إليها، فوجب المنع من الاعتماد على الوصف عند انتقاضه، إلا أن يرد نص بإجرائها في غير محل النقض، فيتلقى ذلك بالقبول. هذا وجه الانفصال عن السؤال.
قال الإمام: (ومما يتعلق به من يجوز تخصيص العلة) إلى قوله (فأين','3','666',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2298,'يقع هذا من جواز تبديل الأحكام). (١١٩/ أ) قال الشيخ: هذا الذي ذكره هؤلاء ضعيف، وذلك أنهم حاولوا بيان أن العلل الشرعية لا تتنزل منزلة العلل العقلية عند مثبتيها. هذا كلام صحيح، إلا أنه لا ينتفع به ههنا، فإن الأوصاف، وإن كانت مناسبة، لا تقتضي الأحكام الشرعية لأعيانها، وإنما تبينا من وضع الشريعة مراعاة المصالح. وهذا إنما يكون بعد ورود الشرائع. ولو ثبت لنا أن الوصف علة شرعًا، لم يبعد أن يخصصه الشرع ببعض المحال.
كما أن تماثل الأجزاء [سبب] للغرم بالمثل في غير [المصراة]، وجعل الجناية سببا لضمان الجاني في غير مسألة العاقلة، وجعل الاقتيات أو الطعم علة لوجوب التناجز في غير العرايا، فلا يتحكم على الشارع في تخصيص وتعميم، لا في المسائل، ولا في الأزمنة، ولكن وجود النقض يشكك في أصل النصب، فلذلك كان النقض قادحا، لا من جهة اعتقاد أنه لا يصح من الشارع التخصيص. هذا هو الفرق بين ما ذكرناه، وبين ما ألزموه.
قال الإمام: (ومما يتعلق به هؤلاء، جواز تخصيص علة الشارع) إلى قوله','3','667',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2299,'([فهذه] عيون كلام [هؤلاء] الفريقين). (٩١/ ب) قال الشيخ: وجه خيال هذا القول على الجملة، أن الذي ينصب الوصف علة، إنما يسنده إلى الشارع، بناء منه على ظن النصب على العموم، فيصير ذلك بمثابة لفظ عام يدل على التعليل. وإذا فرض ذلك، ثم ورد تخصيص، لم يمنع ذلك [من التعميم] في غير موضع التخصيص، فكذلك إذا ظن المستنبط العلة، قصد التعليل والتعميم، فإذا ثبت ما يمنع التعميم، لم يلزم من ذلك بطلان [التعليل]. وهذا خيال باطل، والكلام الذي [قدمناه] يجيب عن هذا.
فأما في الاستنباط بظن أن الشارع قصد إلى الوصف من جهة إجرائه الحكم على وفقه، وإنما تبقى غلبة الظن، إذا لم يقطع الحكم عنه، أما إذا قطع الحكم عنه، فاتت غلبة الظن بأنه الباعث. هذا هو الفرق الصحيح. وما ذكره','3','668',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2300,'الإمام جنوح إلى أنه يصح للشارع التحكم، ويمتنع ذلك على المستنبط. فهذا كلام يشير إلى بطلان الاعتبار من حيث الجملة. وخصوصية الجواب هو ما ذكرناه، من بطلان [غلبة] ظن النصب عند مصادفة النقض.
قال الإمام: (والمسلك الذي نختاره) إلى قوله (وهذا حقيقة القول في ذلك). قال الشيخ: ما ذكره الإمام في هذه المسألة كلام مشكل، وإطلاق','3','669',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2301,'بعيد. أما المصير إلى أنه إذا انتظم فرق (١١٩/ ب) بين ما طرد العلة فيه، وبين ما ورد نقضًا، فإنه يصير ما أطلقه قيدًا منضمًا إلى العلة، فليس ذلك مطلقًا، فإنه [قد] يكون الذي أبدى في مسألة النقض مانعًا يمنع من الحكم، ولا يصح أن يكون عكس المانع قيدا مضمونًا إلى العلة بحال، فكيف يطلق القول بأن عكس ما أطلقه قيد مضموم إلى العلة؟ إلا أن يكون بنى الأمر في ذلك على ما تخيله الأستاذ، فإنه يقول: انتفاء المانع قيد مضموم إلى العلة، وعند ذلك يتناقض الكلام، فإنه إنما [يعمل] المانع بعد تحقيق المقتضي، [فإذا] كان نفي المانع قيدًا مضمومًا إلى العلة، لم يعمل المانع بحال. ومن سوى الأستاذ لا يصير إلى هذا، ويعترفون بالفرق بين نفي المانع، وبين جزء العلة. فإذا تحقق هذا، بطل أن يكون انتفاء المانع قيدًا مضمومًا إلى التعليل.','3','670',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2302,'ثم قوله: إن العلة تبطل، كلام ضعيف، والمراد أن ما اعتمده المعلل، وجعله علة كاملة، ليس كما تخيله، فإطلاق الباطل على العلة، بناء على أنها لم تكمل، فيه ضعف، والمراد بذلك: أن المتمسك بها منقطع، لاعتماده على بعض العلة، ولذلك أنها إذا تكملت بما يضم إليها، اندفع الاعتراض، وصح الاعتماد. [بهذا نتبين] أن العلة ليست مناطًا له، وفرق بين أن يتخلف الحكم عن المناط لخلله، وبين أن لا يترتب الحكم على بعضه. فهذا هو الكلام على هذا الوجه.
قال الإمام: (ولو اعترضت مسألة على العلة نقضًا) إلى قوله (فكيف يلزم الخصم حق طردها في موضع قصده)؟ قال الشيخ: هذا الذي ذكره في هذا القسم أيضًا، كلام يفتقر إلى مزيد إيضاح، وذلك أنه حكم بأن العلة تبطل، فإن أراد أن المتمسك بها منقطع، فهو صحيح، لأنه إنما يثبت الحكم في الفرع، بناء على دعوى عموم النصب، فإذا ترك هو عمومها، كيف يلزم خصمه ذلك؟ وإن أراد أن الخصم إذا استثنى عنها صورة، بطلت العلة (٩٢/ أ) في نفسها. فهذا كلام باطل، والدليل على فساده، [لو] أن مستنبطا آخر [استنبط]','3','671',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2303,'تلك العلة من مورد النص، وطردها ولم ينقضها، صح له ذلك، ولا يقال إن هذه العلة باطلة، لأن بعض المستنبطين قد نقضها، فإطلاق القول ببطلان العلة في هذا القسم لا وجه له. والله أعلم. وإنما الإمام عبر بالبطلان عن انقطاع المستدل، وهو تعبير بعيد.
قال الإمام: (وإذا [طرأت] مسألة إجماعية) إلى قوله ([فالتي] ترد مناقضة وقاطعة للطرد أولى بالإبطال). قال الشيخ: (١٢٠/ أ) هذا الذي ذكره في هذا المكان، كلام مجمل، وفيه أيضا تفصيل، وذلك أن المسألة الناقضة الإجماعية، إذا أنقدح فيها معنى، فلا شك في كونه أرجح من المعنى الذي استنبطه المستنبط، ولذلك ترك حكمه [للمعنى] الآخر، ولكن المصير إلى بطلان المعنى المستنبط على الإطلاق لا يستمر، إذ يحتمل أن يكون ذلك المعنى الموجود في المسألة الناقضة مانعًا، وضرورة كونه مانعا، تسليم المقتضى، وإحالة العمل عليه لو كان منفردًا. ففرق بين ظن يترك لمعارض راجح، وبين أن يمنع ترتيب الحكم عليه للخلل. ويتبين ذلك بأنه إذا ترك [حكمه] للمانع، صح اعتماده على تقدير الانفراد. أما إذا لم','3','672',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2304,'[يترتب] الحكم عليه لخللٍ فيه، امتنع إعماله مطلقًا. فلا يصح القول بأن المسألة الناقضة إذا كانت معللة، بطل المعنى المستنبط مطلقًا.
وأما المصير إلى أن المعارضة تبطل العلة، فكلام أيضا مشكل، إن أراد امتناع الاعتماد على المعنى في موضع المعارضة دون الترجيح، فصحيح، وإن أراد أن العلة تبطل مطلقا في محل المعارضة وفي غيره، فليس الأمر كذلك، وهذا في التمثيل بمثابة البينة المزكاة إذا عارضتها بينة أخرى، تساويا، [فإنه] يمتنع القضاء بها في موضع المعارضة، ولا يمتنع ذلك في موضع لا معارضة فيه. هذا إذا ثبت أن الحكم إنما تخلف لأجل المعارضة. أما إذا وقع التردد في أصل التعليل، فلابد من إقامة الدليل على [صحة] صلاحية الوصف للتعليل.
وقوله: إن الناقضة أولى بالإبطال من المعارضة، كلام صحيح، فإن','3','673',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2305,'النقض يقطع طرد العلة، ويمنعها من الجريان، فلم يثبت عموم النصب. وأما المعارضة فيتلقى الحكم عليها من أصل آخر، حتى لو قدر قبول المحل للحكمين، لبقيا جميعا، بخلاف المسألة الناقضة إذا قصد الشارع قطع الحكم عنها في الصورة الناقضة، فهي أجدر [بالإبطال].
قال الإمام: (وإن طرأت المسألة قاطعة للطرد) إلى قوله (ولا يعارض ظن علمًا). قال الشيخ: ما ذكره القاضي من التردد في هذه الصورة، مكان يحتمل التردد، وذلك أنه إذا ثبت أن الناقضة مستثناة، فلاشك أنها لا تعترض على العلة بحال. وهل إذا كانت نادرة قليلة تكون بهذه المثابة، ويغلب على الظن الاستثناء، بناء على الندرة والقلة وفقدان المعنى، أو لا يرشد ذلك إلى الاستثناء، ويكون المعنى معرضا عنه؟ فهذا لعمري موضع تردد (١٢٠/ ب)، على ما بيناه.
أما ما ذهب إليه الإمام، من كون محل (٩٢/ ب) النزاع، نسبته إلى المسألة الناقضة نسبة العلم، فلاشك في أن ذلك يمنع من إجراء العلة في ذلك','3','674',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2306,'الفرع، أو قطع بحكمه، نظرًا إلى الإلحاق المعلوم، فلا يبقى ظن بإلحاقه بالأصل الذي منه الاستنباط، فليس في ذلك نزاع، وهو الذي [أراده] الإمام ببطلان العلة، أن لا يثبت الحكم على وفقها في الفرع. [وهل] يتضمن ذلك بطلانها مطلقا؟ هذا موضع تردد القاضي. وظاهر كلام الإمام خلافه، وهو المصير إلى البطلان مطلقا، [وأنه لا] يتوقف في ذلك. والصحيح عندي الامتناع من جزم القول بأحد الوجهين.
قال الإمام: (وإن لم يكن محل العلة منها بهذه المثابة) إلى قوله (فهذا','3','675',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2307,'سبيل إجرائها، [وإن كان ينقدح فيها معنى على كل حال، فهو ملحق بالأقسام المبطلة التي تقدم ذكرها. فهذا بيان الأصول]). قال الشيخ: ما ذكره الإمام ههنا ظاهره التناقض، فإنه حمل كلام القاضي في التردد على ما إذا كانت المسألة الناقضة لا يعقل معناها، فقال: وهي لعمري موضع تردد، ثم صار إلى القطع بأنها لا ترد نقضا، فكيف تكون موضع تردد، وهو قاطع بأنها غير ناقضة؟ إلا أن يريد أنها موضع للنظر فيه مجال. وهذا أيضًا لا فائدة فيه، فإن الذي تقدم من [الصور للنظر فيه مجال.
وأما اعتماده نفي القطع بأنها غير ناقضة، من] جهة أنها لا تعلل، فكلام لا أصل له، [فكيف] يصح أن يكون القطع يستند إلى ذلك، ولم ينكر','3','676',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2308,'على من يقول تثبت بورود الحكم على مناقضتها، أنها ليست معتبرة في موضع المصادفة، بل جرت اتفاقية؟ إّ لو كانت معتبرة، لكان الحكم ملازمًا لها.
وأما مصيره إلى أنها إذا كانت غير معقولة المعنى، [ثبت] أنها مستثناة, فليس يرشد ذلك إلى الاستثناء بحال، فإنها إنما تكون مستثناة، بشرط صحة المعنى المقرر، فإذا صح أنه باعث، صح أن الخارج عنه مستثنى. أما المصير إلى أن فقدان المعنى من الناقض، دليل الاستثناء، فليس بصحيح.
وأما قوله: إنها إذا كانت معقولة المعنى، فليست مستثناة، فكلام [غير] صحيح، وقد يكون المستثنى معقول المعنى، فإن المستثنى ينقسم إلى ما يعقل معناه، وإلى ما لا يعقل معناه، وقد استثنيت مسألة العرايا عن بيع','3','677',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2309,'الرطب باليابس، تسهيلاً على المعرين والمَعرين، واستثنى أبو بردة بن نيار في التضحية بعناقٍ لفقره. واستثنى الشارع العاقلة تحمل العقل، لوقوع الخطأ، [وكثرة] العقل. فالمصير إلى أن المستثنى لابد أن يكون غير معقول المعنى، وأن الذي يعقل معناه، لا يكون مستثنى، غير صحيح. ولم أر ذاهبًا إليه، بل العلماء مصرحون بأن (١٢١/ أ) المستثنى ينقسم إلى ما يعقل معناه، [وإلى ما لا يعقل معناه]. [هذا] هو الصحيح.
فإن قيل: فالعلة الواردة على [خلاف] حكم علة المعلل، في أي موضع تضر، و [في] أي موضع لا تضر؟ قلنا: [لا تضر] إذا ثبت أن الحكم ثبت فيها، إنما [ثبت] لمانعٍ راجحٍ على معنى المعلل، فهذه الصورة لا تقدح في صحة التعليل. فهذا وجه.
[والوجه] الثاني -أن يثبت بنص الشارع أنها مستثناة، كقوله لأبي','3','678',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2310,'بردة: "تجزئك ولا تجزئ [عن أحدٍ بعدك] ". وإذا ثبت الاستثناء، لم يكن النقض متوجهًا على حال.
الثالث -أن تكون المسألة الواردة نقضا، ترد على كل قياس. [وهذا] بمثابة جواز العرايا مثلا. فإن من علل [تحريم] التفاضل بالقوت، فإن علة العرايا تنقض قياسه. وكذلك من (٩٣/ أ) علل بالطعم، أو بالكيل، أو بالمالية. فإذا كانت ترد نقضًا على كل قياس، مع العلم بأن الشارع لم ينقض قاعدة الربا، ثبت بذلك أنها مستثناة، فلا ترد نقضا. وكأن المستثنى [مقطع] عن الشريعة، فلا يرد على شيء نقضًا، ولا ينقض شيئا أصلا. ومقصود الإمام [من] هذا الحرف الواحد، وهو أن المستثنى لا يرد نقضًا، [وهو] كلام صحيح. [ولكن] مصيره إلى أن الاستثناء يثبت بكون الوارد لا يعقل معناه، أما إذا عقل معناه، فليس باستثناء، فليس [ذلك] بصحيح على ما [حققناه].
قال الإمام: (ونحن نضرب أمثالاً، وننزل [عليها] تحقيق ما نبغيه) إلى','3','679',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2311,'قوله ([فليس] [لمثل] هذه التخييلات اعتبار). قال الشيخ: ما ذكره الإمام في تمثيل ما لا يعقل معناه، [تحمل] العاقلة [العقل]، قصد بذلك صحة العلة، وهي وجوب الغرم على المتلف، وإن كان ذلك ينتقض بتحمل العاقلة العقل، وهو يرى أن الناقض إذا عقل معناه، كان مبطلاً للعلة، ولم يمكنه إبطال هذه العلة. والتزم أن الوارد نقضا لا يعلل. وهذا الذي قاله غير صحيح عندنا من وجهين:
أحدهما: [أن الكلام في الإبطال بالنقض، إنما هو في العلل المظنونة، أما العلل المقطوع بها، [فلا] يفسد شيء [منها] على حال، سواء كان الوارد يعقل معناه، أو لا يعقل معناه.
الثاني: أن مسألة العاقلة معقولة المعنى، والدليل على ذلك من وجهين]:','3','680',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2312,'أحدهما: اتفاق الجاهلية على ذلك قبل ورود الإسلام، والتعبدات لا تهتدي إليها عقول العقلاء، وإنما تتلقى من [الشرائع].
الثاني: إن المعنى فيه معقول منكشف، وهو المعاونة من القبيلة على حمل الجناية.
وقول الإمام: إن هذا منقوض بالجناية على الأموال، [وغير] منعكس بالحمل عند اليسار. أما الجناية على الأموال، فالسبب في كونها لا تحمل (١٢١/ ب) أمران:
أحدهما: أن الأموال في غالب الأحوال، لا يتفق تلف ما يشق صونه، وإنما يكون ذلك في أمور يسيرة. وأما إتلاف النفوس، فالغرم فيها يشق، فلا يلزم من إثبات الحمل في الموضع الذي يعظم الغرم، أن يثبت في الموضع الذي لا يشق.
الثاني: أن القتل [خطأ] عند العرب، يكون من تجاول الفرسان عند الطراد، وبهم حاجة إلى ذلك، فكأنه أمر ترجع فائدته على العاقلة. فتوزيع','3','681',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2313,'الغرم الذي لا يشق على العاقلة، مع رجوع المنفعة إليهم، مصلحة من الجانبين. فهذا تقرير فهم المعنى. ولم ترد المسألة نقضًا، لما قررناه من القطع [بمعناها].
قال الإمام: (وكذلك إذا طردنا طريقة في إيجاب المثل) إلى قوله (واستبان أن أمثال هذه المستثناة لا ترد نقضا على القياس [المعنوي]). قال الشيخ: ما ذكره الإمام في مسألة المصراة، وكونها لا ترد نقضًا، فصحيح، والسبب فيه عندنا، انعقاد الإجماع على صحة العلة في وجوب ضمان المتلفات، التي تتشابه أجزاؤها بالمثل، والمناسبة في واضحة، فإنا لو قدرنا [على] رد العين، لم نعدل عن ذلك. فإذا تعذر رد العين بالفوات، فالمثل أقرب إليها، فإذا تعذر المثل، رجعنا إلى القيمة. وهذا مناسب، معتبر بإجماع. وإذا كان المعنى مقطوعًا به، فكيف يتصور إبطاله بورود النقض؟','3','682',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2314,'وأما كون مسألة المصراة معقولة المعنى أو تعبدية، [فالصحيح] عندنا في إثبات أصل الغرم التعبد، والدليل على ذلك النقل والمعنى. أما النقل، فهو أنه لو رد عندنا (٩٣/ ب) بعيب آخر سوى التصرية، لم يغرم شيئًا أصلاً. ولو كان الصاع بدلاً من اللبن، الذي [لم] يقدر مشترًى مع الشاة، للزم الغرم إذا رد بغير عيب التصرية، ولذلك قال مالك رحمه الله عن ذلك: " [أو لأحدٍ] [في هذا الحديث] رأي"؟ وتلا قوله تعالى: {فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم فتنة أو يصيبهم عذاب أليم}.
[وأما] المعنى، فهو أن اللبن [المحتلب] عندنا غلة، والغلات ليست مبيعة، ولا حصة لها من الأثمان. [وإذا] كان المشتري [لها]','3','683',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2315,'[إنما] حلب على ملكه، فكيف يتوجه عليه غرم لذلك؟ وقد قال أشهب من أصحابنا: لا يرد [مع لبن] المصراة [شيئًا]. وزعم أن الحديث منسوخ بقوله عليه السلام: "الخراج بالضمان". وفي هذا المذهب بعد. وحديث المصراة أخص منه، ولا سبيل إلى ترك الخاص للعام.
وأما تعين جنس التمر، فلا يتعين عندنا، بل ذلك راجع إلى ما يغلب من عيش أهل كل مصر. [ويرى] مالك [رحمة الله عليه] أن هذا إنما ذكره، لأنه غالب عيش (١٢٢/ أ) أهل المدينة، فيخرج أهل كل بلدٍ من غالب عيشهم. هذا مذهب مالك [رحمه الله].','3','684',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2316,'وأما [الشافعي] [رضي الله عنه]، فإنه بنى المسألة على عدم فهم المعنى، [فلذلك] قضى بأنه [إذا] رد بعيبٍ آخر سوى عيب التصرية، رد صاعًا من تمر، واقتصر على عين التمر، تغليبًا للتعبد. والفريقان جميعا صائران إلى فهم المعنى من حيث الجملة.
قال الإمام: (ومما نضرب مثلاً: الكتابة الفاسدة) إلى قوله ([وهذا هو','3','685',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2317,'الشذوذ والاستثناء]). قال الشيخ: أما كون الأملاك لا تنتقل إلا بمسلك شرعي، فهذا المعنى [ليس] في القوة والظهور كالمعاني السابقة، لأن تلك المعاني [مجمع] عليها، سالمة من معانٍ أخر تقاومها. وأما العقد الفاسد، فيعني بالفساد ههنا: الممنوع شرعًا، ولا مناقضة من جهة المعقول بين المنهي','3','686',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2318,'عنه، وبين ترتيب الثمرات عليه، ومن [المناسبة] اتباع الرضا في تحصيل مصالح العقلاء. فإذا حكم بأن العقد الممنوع يثمر، نظرًا إلى الرضا، ثم يخرج عن قبيل المناسب، ويكون نهي الشرع عنه لمفسدة فيه، ويكون الرضا دليل المصلحة، ولذلك حكمنا بأن أمورًا محرمة أثمرت، كاستيلاد الأب جارية الابن، فإنه مثمر، وإن كان محرمًا. وعلى هذا لا نسلم أن الكتابة الفاسدة خارجة عن القياس بالكلية، هذا إن قدرنا إنها من العقود التي تجري المعاوضة فيها. وهذا إنما يرد نقضًا على هذا التقدير. أما [إذا] ذهبنا [إلى] ما فيها من [شوب] [التعليق] [والاستغلال]، [فلا] ورود لها على حال، فإن الصائر إلى ذلك، يثبت لها وجهًا من الصحة، فلا [ترد] نقضًا بهذا الاعتبار.','3','687',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2319,'أما إذا صار إلى أنها أثمرت من جهة الرضا بها، فحينئذ يتوجه النقض، ويفتقر إلى الأعتذار، فيرد النظر إلى ما قررناه من إشعار الفساد، بعدم المصلحة في الفعل للعباد، اتضح ذلك بالمنع من الإقدام. هذا وجه تقرير الكلام. وقد سبق تحقيقه فيما مر.
وقوله: إذا قال القائل: [النقض] إنما يلزم من جهة قطعه طرد العلة، لا من جهة انتظام جامع بين الأصل والفرع. مقصود هذا الكلام: أن المسائل إذا تباعدت بالكلية، لم يرد بعضها نقضًا على (٩٤/ أ) بعض، وإن [تماثلت]، فلاشك في ورود النقض، يعني إذا [تماثلت] في مناط الحكم. هذا أيضا لا لبس فيه. وإن لم يعرف [التماثل]، ولم يتحقق التباين، فالاعتماد على توجه النقض على وجود المعنى في المناقضة مع عدم الحكم، وسواء على هذا أمكن [إلحاق] الفرع بالمسألة الناقضة، [أو لم يمكن].','3','688',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2320,'هذا هو مراد [هذا] الكلام. وقد تحقق الانتقاض عند جريان المعنى (١٢٢/ ب) مع تخلف الحكم. وهذا سؤال صحيح، وأراد الإمام أن يقول: هذا لا [يتوجه] إذا كان [الناقض] مستثنى، بدليل ما حققه من الصور السابقة، وإيراد الأمثلة، وجعل الكتابة الفاسدة من تلك الأمثلة، والكلام [فيها] على ما سبق.
قال الإمام: (وإن قال [الملزم]: خذوا الكتابة الفاسدة) إلى قوله (لا على سبيل التعليل، ولا على [سبيل] [المشابهة]). قال الشيخ: إذا تحقق أن الكتابة الفاسدة خارجة عن القياس بما سبق تقريره، فلا ورود لها على قياسٍ على حال، لا من جهة المعاني، ولا من [جهة] أبواب الأشباه. وقد بينا','3','689',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2321,'وجه ذلك فيما سبق. فالمصير إلى إلزامها نقضًا، مع خروجها عن القياس، لا وجه له. إذ المستثنى لا ينقض به بحال. إذ معنى كونه مستثنى: أن الشارع اقتطعه على وجهٍ ينفرد [به] المستثنى، لا يجري في بقية الشريعة. وهذا لا خفاء به، بعد ثبوت كونه مستثنى، وإنما الشأن [كله] في بيان كونه مستثنى.
ولبيانه ثلاثة أسباب: إما أن ينص الشارع على ذلك، كقوله لأبي بردة بن نيار: "تجزئك ولا تجزئ عن أحدٍ بعدك". وإما أن ير نقضا على [قياسٍ] معلوم، فيتبين بذلك كونه [مستثنى]. وإما أن يرد نقضًا على كل قياس لأهل الإجماع. فهذا يبين كونه مستثنى، لأنه إما أن [يبطل] الأقيسة كلها، وهو محال، [لأنه] يفضي إلى نسبة أهل الإجماع إلى الخلف والإبطال. وإما أن يخص قياسًا بالإبطال، مع استواء النسبة إلى الجميع، وهو محال أيضًا، لحصول التحكم. وإما أن لا يرد نقضا بحال، [فهذا] متعين. فبهذه الطرق','3','690',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2322,'يعرف كون الناقض مستثنى.
قال الإمام: (ومن أمثلة هذا الفصل: إلزام الاكتفاء بالخرص) [إلى] قوله (فوجه رد الخرص بالمسلك الذي ذكرته فيما تقدم). قال الشيخ: [اختلف] الناس في الخرص في الموضع [المسموح]، هل هو أصل منفرد بنفسه غير رخصة، أو [هو] معدود من الرخص؟ ويظهر أثر هذا [الاختلاف] في مسائل:
منها -أنه هل يجوز أن يجمع في عقد واحد [بين] مكيل وجزاف، أو يمتنع ذلك، كما يمتنع البيع والقراض، على رأي من منع ذلك؟ والمشهور عندنا المنع، بناء على اعتقاد الرخصة فيما يشق معرفة مقداره، وهو المكيل والموزون. أما ما لا يشق معرفة مقداره، فلا يجوز الخرص فيه، وهو المعدود','3','691',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2323,'والمقيس. اللهم إلا أن يكون المعدود مما يشق عده لكثرته، كالجوز واللوز مثلا، أو يكون متفاوت الأجرام، بحيث لا يفضي عدده إلى [ضبطٍ] بحال. فيصير هذا في التحقيق كغير المعدود، إذ العدد فيه لا يحصل [مقصوده]. (١٢٣/ أ)
وكذلك أيضا اختلف العلماء في جواز العلماء في جواز بيع العرايا بخرصها تمرًا، فيما دون خمسة أوسق، فقال قائلون: لا يجوز ذلك إلا في أقل من خمسة [أوسق] (٩٤/ ب). وقال قائلون: يجوز في الخمسة. وهذا الاختلاف مبني على أن الأصل جواز الخرص، [إلا] في موضع تحقق المنع، ولم يتحقق المنع إلا فيما زاد على خمسة أوسق، أو الأصل المنع إلا في موضع الإباحة، ولم تتحقق الإباحة إلا فيما دون خمسة [أوسق]، والأول هو المذهب، والثاني قول لبعض الأصحاب.','3','692',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2324,'فإذا قال قائل: لابد من الكيل والوزن الضابطين، إذ بهما تتحقق المعرفة، وينتفي الغرر، والغرر ممنوع في الشرع. فيرد على ذلك أصل الخرص [المسوغ] نقضًا، لوقوع الجواز في الشريعة، فيقول القائل [الأول]: [هذا] في محل [الخرص] مستثنى، ويلحقه بما [تقدم] من امتناع وروده نقضا، فلا يعترف الملزم بالاستثناء، ويدعي أنه جارٍ على ذوق المعاني. والذي يحقق ذلك، امتناع الخرص فيما لا يأخذه [الحزر] على الحقيقة، كالأغنام وغيرها، وكذلك الطعام في مخزن لا يرى إلا بعضه، فلما جاز عند المشاهدة للجميع فيما لا [ينضام]، دل ذلك على أنه [توصل] إلى ضبط المقدار.','3','693',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2325,'[ويزيده] تأكيدًا، أن الشرع عين الكيل في بعض المواضع، مع إمكان الوزن، وإن كان الوزن أضبط، ولكن أعرض الشرع عن هذا [التجويز]، وأجرى الأمر على ما تعارفه الناس في التقدير، فيصح أن يكون الخرص فيما يمكن كيله، كالكيل فيما يمكن وزنه. فلا يعد على هذا من أبواب الرخص، بل يكون جاريًا على مقتضى القياس، [فيتسع] فيه الباب، ويستعمل على الإطلاق، إلا في موضع المنع. هذا هو مراد الإمام في هذا المكان.
والصواب عندنا هو أنه معقول المعنى، وفيه وجه من التحقيق، عند لزوم المشقة من الكيل والوزن، فلا يحكم بأنه غير معقول المعنى بحال. والإمام يرى أنه متى فهم معنى النقض على وجهٍ قريب أو بعيد، لزم نقضًا وأفسد المعنى، ونحن لا نرى ما رآه أصلاً.
وأما قوله: إن الأصل الرجوع إلى ما يعد [تقديرًا]، والخرص معدود','3','694',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2326,'من الحدس والتخمين، المجانبين لمدارك اليقين. كلام ضعيف، لانعقاد الإجماع على جواز بيع الثمار في الأشجار، بناء على مجرد الخرص والتخمين، ولا يعد ذلك من أبواب الغرر، الذي لم ترد الشريعة به على حال، ولتحاماه العقلاء. وليس الأمر كذلك، والإمام يشير إلى أنه غير معقول المعنى. وقد بينا خلاف ذلك.
قال الإمام: (وإن علل معلل في قطع الخيار) (١٢٣/ ب) إلى قوله (فلينعم الناظر تأمله ويستعين بالله تعالى). قال الشيخ: هذه المسألة صورتها: ما إذا سود ثوب العبد، وعلمه إصلاح الأقلام، وجعل معه اللوح والدواة،','3','695',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2327,'وباعه، ولم يشترط أنه كاتب، فاشتراه المشتري، بناء على ظن ذلك من غير اشتراط.
[فالصحيح أنه لا يثبت له خيار، بناء على أنه فرط في عدم الاشتراط]. وهذه الأشياء لا تدل على [الكتابة] دلالة قطعية، فوجب الإضراب عنها، والاعتماد على مطلق الشراء، من غير شرط الصفة. وهذا الكلام واضح، ولكنه منقوض بالرد بعيب التصرية، فإنه ليس فيه إلا ظهور مخيل تشعر [بغزر] اللبن.
والإمام يرى أنه إذا ظهر في مسألة النقض معنى على حال، لزم نقضًا، ولا ينفع عنده إبداء فرقٍ بين صورة النقض ومحل التعليل، فكانت مسألة المصراة لازمة على أصله. ولا يمكن (٩٥/ أ) أن [يقال: ] الحكم بالرد عند الاطلاع على التصرية من أبواب التعبدات.
هذا لا خلاف في أنه معقول المعنى، عند من يرى الرد به. وإنما محل التردد في التعبد غرم شيءٍ عن لبن المصراة. أما أصل الرد فمعقول المعنى بلا شك، فيلزم نقضا على أصله. هذا مقصود المسألة.','3','696',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2328,'وللفقهاء نظر في الفرق بين التصرية وبين تسويد ثوب الغلام، فإنه لا يبعد أن يكون مصاحبًا لابن سيده في إصلاح اللوح والدواة والأقلام، فهذا أضعف في الدلالة على الكتابة، من كبر الضرع، والدلالة على غزارة اللبن. هذا تفاوت لاشك فيه، ولا يلزم من فسخ البيع بالتدليس بغزارة اللبن، لظهور أمره، وامتناع الفسخ بتسويد اليد والثوب، لضعف دلالته.
وأما قوله: إن المسألة الناقضة إذا كان يلوح فيها [معنًى]، هو أضعف من علة المعلل في الرتبة، والتفاوت بين الرتبتين لا ينتهض [دارئا] للنقض، فمقصوده به: أن النقض لم يلزم من جهة معارضة العلة بعلة من قبل الناقض، وإنما لزم من جهة قطعه طرد العلة، وقطع الطرد [محقق]، سواء كانت القاطعة معللة بمعنى قوي أو ضعيف، وأوجهٍ من التشبيه. هذا مختاره، وكان يلزم على مقتضاه، أن يكون ما لا يعقل معناه نقضا أيضًا، من جهة أنه قطع طرد العلة، ولكنه امتنع من ذلك، لزعمه أن ما لا يعلل مستثنى، والمستثنى لا ينقض أصلاً.
وقد تكلمنا على ذلك، وبينا أنه لا يلزم من عدم فهم المعنى الاستثناء،','3','697',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2329,'(١٢٤/ أ) ولا يلزم من فهمه تحقيق النقض وإفساد العلة، على حسب ما قدمناه. بل متى تحقق أن الحكم في الصورة الناقضة [إذا] تخلف، وثبت نقيضه لمعارض راجح، كان دليلا على صحة العلة المنقوضة، وإعمالها في غير محل النقض. نعم، لا يصح أن تكون علة في محل النقض، لأن العلة في عرف الفقهاء متلقاة من أحد ثلاثة أمور:
منها: من يتلقاها من العلة العقلية، وهذا هو رأى الأستاذ، لأنه كثير الممارسة لمسائل الكلام، فلم [ير] العلة في الشريعة إلا على ذلك الذوق، لا جرم حكم بأنها لا تنتقض بحال، ولا يلزم من عموم النصب فيها. وإذا أوردت عليه مسألة ناقضة لمعنى، أخذ عدم المعنى قيدًا مضمومًا [إليها]، فلا يصح عنده نقض العلة بحال. وقد بينا وجه الرد عليه فيما سبق. ولكن','3','698',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2330,'المقصود [أنه] على أصله، لا يصح منه الاعتراف بوجود العلة في مسألة النقض.
المأخذ الثاني لاسم العلة: علة المرض، وهو ما يتأثر به المحل، ويظهر الأثر به، وإن كان إنما [يحصل] [المرض] بهذا، مع [تقدير] أمور أخر. ومثاله: أن الطبيب قد يضيف المرض مثلاً إلى تناول طعام [حاًر] في زمن الصيف، للشاب الحاد المزاج، [فيقول]: السبب في مرضه أكل العسل، وإن كان لولا الزمان والسن، لم يستقل العسل بالتأثير، ولكن لما كان الزمان موجودًا، والسن [ثابتًا]، والمرض منتفيا، بعدت الإضافة إليهما، فلما حدث أكل العسل وحدث الفصل، ضيف إليه. وبهذا الاعتبار فرق الفقهاء بين السبب والمحل والشرط، فلا يصح أيضا على هذا (٩٥/ ب) التفسير إطلاق العلة في المسألة الناقضة، إذا لم يتأثر بها المحل بحال.
المأخذ الثالث لاسم العلة: [الملتقى] من العلامة، نظرًا منهم إلى أنها لا توجب، وإنما الله سبحانه هو الموجب عندها، فيقال: علل الشرع علامات، وإنما تكون علامات، إذا عرفت الحكم، وهي لم تعرف الحكم','3','699',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2331,'في مسألة النقض، فلا يكون علامة فيه، وإن أطلق لفظ العلامة عليها في ذلك [المحل]، فمجاز، [أي] هذه علامة في محل مماثل لهذا المحل.
هذا تمام ما أراده الإمام في مسألة النقض. فنقول: الصواب عندي إجراء مسألة النقض على غير هذا الطريق، وذلك [أن] نقول: تخلف الحكم عن العلة، يفرض على ثلاثة أوجه:
أحدها- أن يفرض في صوب جريان العلة ما يمنع أطرادها، (١٢٤/ ب) وهو الذي يسمى نقضًا عند الفقهاء، وهذا ينقسم إلى ما يرد مورد الاستثناء، وإلى ما يرد على ذلك الوجه.
فأما الذي يرد مورد الاستثناء، فلا يرد نقضًا، [سواء] ورد على علة مظنونة، أو على علة مقطوع بها. وقد بينا وجه ذلك فيما تقدم. مثال الوارد على العلة المقطوع [بها]: تحمل العاقلة العقل، ناقض لاختصاص الغرم بصاحب الجناية، وكذلك إيجاب رسول الله صلى الله عليه وسلم صاعًا من التمر في مقابلة لبن المصراة، [ورد] ناقضًا [لغرم] المثليات التي تتشابه أجزاؤها، فإنها','3','700',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2332,'مضمونة بالمثل، إن عرف المثل، [وإلا بالقيم]، وإن كان ورود هذا لا ينقض العلة، ولا يبطلها في غير [محل] النقض. ولكن يبقى أمران:
أحدهما- ما قررناه قبل من أنها ليست علة في مسألة النقض، على ما أسلفناه.
[الثاني -] ما يتعلق [بآداب] الجدل، [فهل] عليه أن يقول: تماثل في غير الجنابة، [قياسًا] على الحر خطًأ؟ هذا مما اختلف فيه الجدليون. والجدل شريعة وضعها الجدليون فإليهم وضعها. وتكليف ذلك قبيح، فإن المعلل [إنما] يلتزم طرد العلة، إذا لم يمنعه الشارع من ذلك، أما إذا منعه، فلا يلزم الطرد، ولكن ربما يكون الاحتراز أضبط لنشر الكلام. وهو أيضا يشير إلى أن المعلل غير غافلٍ عن مواضع الاستثناء.
ومثال الوارد على العلة المظنونة: مسألة العرايا، فإنها تنقض علل الربا، [فإنها] تنقض علة القوت والطعم والكيل والمالية، على حسب [اختلاف] الفقهاء في التعليل، وعلى أنها مستثناة من جهة ورودها نقضًا على كل قياس، فلم يلزم بحال. فقد بينا [الطرق] التي تعرف الاستثناء.','3','701',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2333,'أما إذا لم يرد مورد الاستثناء، فلا يخلو: إما أن يرد على العلة [المنصوصة]، [أو على االمظنونة. أما الوارد على العلة [المنصوصة]، فقد جعل الإمام له مسألة، فلتؤجر الكلام عليه إلى الموضع الذي ذكره. ونعني [بالمنصوصة]: ما دل اللفظ عليها، سواء كانت الدلالة قطعية أو ظنية.
أما إذا ورد على العلة المستنبطة، وانقدح جواب عن محل النقض من طريق الإخالة، بحيث ينعكس القيد إلى العلة [المظنونة]، فهذا هو الذي ذكره الإمام، وقال فيه: [إذا تبين] ما ذكرناه أولاً، [لم] يكن تمام العلة. والذي قاله في هذا الوجه صحيح.
أما إذا كانت العلة مخيلة، ولم ينقدح الاطلاع (٩٦/ أ) على وصفٍ مكمل [لما] أغفله المستنبط أولاً، وكانت العلة تامة عنده، فهذا موضع احتمال، فإنه يمكن أن يكون النقض دليلاً على عدم الاعتبار، (١٢٥/ أ) وأمكن أن يكون تخلف الحكم لمانع، أو التخصيص ببعض المحال، على جهة الاستثناء. فهذا الاحتراز عنه مهم في الجدل.
ولكن المجتهد الناظر، ماذا عليه أن يعتقد في هذه العلة؟ الانتقاض [والفساد]، [أو التخصيص] ببعض المحال؟ هذا عندي في محل الاجتهاد، ويتبع كل مجتهد ما غلب على ظنه.','3','702',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2334,'مثاله: قولنا: إن صوم رمضان يفتقر إلى تبييت النية، [لأن النية] لا [تنعطف] على ما مضى، وصوم جميع النهار واجب، فإنه لا يتجزأ، فينتقض هذا بالتطوع، فإنه يصح بغير نية، عند بعض العلماء، ولا يتجزأ عندهم على المذهب الصحيح، ولا مبالاة بقول من يقول: إنه صائم بعض يوم، فيجوز أن ينقدح عند المجتهد فساد هذه العلة، بورود التطوع نقضًا، ويحتمل أن ينقدح له أن التطوع ورد مستثنًى، تكثيرًا للنوافل، وتسهيلاً لأمرها، فإن الشرع قد [يتسامح] في النفل، ما لا [يتسامح] به في الفرض، كجواز [التنفل] على الراحلة، وجواز فعلها جالسًا، مع القدرة على القيام إلى غير ذلك. فيستعمل المخيل في [غير] موضعه في الفرض، ويجعل وصف الفريضة فاصلاً بين صورة النقض، ومحل التعليل، ويكون ذلك وصفًا شبهيا، اعتبر في استعمال المخيل، وإن لم يكن على ذوق العلة. فإن كثيرا من علل الشريعة قيدت بقيود لا تظهر مناسبتها، ظهور مناسبة العلل، كتخصيص السرقة بنصابٍ، وتخصيص الرجم بالمحصن. ومن ينكر استعمال الشبه، لا ينكر الاحتراز به في درء النقض من المخيل.
وهذا الكلام يقوى، إذا كان المعنى لا يفتقر إلى شهادة الأصل، كالاستدلال المرسل، عند من يرى التمسك به.
فإن قيل: فإذا كان المعنى لا يفتقر إلى شهادة الأصل في صحة الاعتماد عليه عند من يراه، فكيف تكون هذه المسألة اجتهادية على هذا الرأي؟ بل','3','703',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2335,'ينبغي أن يتفق على إعمال هذه العلة، جميع من يرى الاستدلال المرسل حجة، ولا وجه للتردد في القبول بعد ذلك. فنقول: ليس كذلك، والمسألة محتملة للتردد، ولا يتنزل هذا في القوة منزلة الاستدلال المرسل مطلقا، فإنا نظن في ذلك المعنى، أن الشارع لو حكم، لم يحكم إلا على وفقه، وليس كذلك إذا ظهر أنه أعرض عنه في بعض المواضع، فالأمر أضعف لا شك فيه، فلذلك رددنا القول في القبول، وإن وقع [البناء] على الاستلال المرسل.
ومثاله: ما قدمناه في مسألة (١٢٥/ ب) الصوم، فإن مقدمات هذا القياس مؤثرة بالاتفاق، من قولنا: إن كل صوم واجب، وأن النية عزم لا ينعطف على الماضي، وأن الصوم لا يصح إلا بنية. فإن كانت العلة مناسبة، بحيث تفتقر إلى شهادة الأصل لصحتها، فإن شهد الحكم لها في صورة الموافقة، فلتنقض هذه الشهادة تخلف الحكم [عنها في موضع آخر. [فإن] إثبات الحكم] على وفق المعنى، إن دل على التفات [الشرع] [بقطع الحكم]، فقطع الحكم أيضا يدل على إعراض الشرع. فقول القائل: إني أتبعه (٩٦/ ب) إلا في محل إعراض [الشرع] بالنص، ليس هو [بأولى] [ممن] قال: أعرض عنه إلا في محل اعتبار الشرع إياه بالتنصيص على الحكم.
وعلى الجملة يصح تصريح الشرع بتخصيص العلة ببعض المحال، ويجوز','3','704',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2336,'إعراض الشرع عن بعض المناسب. فإذا لم يصرح بأحد الأمرين، واحتمل أن يكون ذلك للتخصيص، واحتمل أن يكون للإعراض، فتعيين أحدهما بحكم من غير دليل، يعارضه عكسه من غير ترجيح. فإن كانت العلة قطعية، تعين تنزيلها على التخصيص، وإن كانت مظنونة، نظرًا إلى ظاهر اللفظ، فسيأتي الكلام عليه. وإن كانت ملائمة، بحيث استغنى عن شهادة الأصل، ففيها نظر. وإن كانت لا تثبت إلا بالشهادة، فالنقض قادح. هذا هو الذي نختاره في المسألة. وبالله التوفيق.
وللجدليين كلام في النقض يشير إلى ما ذكرناه، على غير هذا التقدير، قالوا: ورود الحكم على الوفق في بعض الصور، دليل الإعراض مطلقًا، فدليل النصب يقتضي [الإطلاق والعموم، ودليل الإعراض يقتضي إطلاقا أيضًا]، فتقاوم الأصلان، فلا يبقى وجه لتقديم دليل [الصحة]، ويمتنع الاقتصار محض الدعوى.
فإن قيل: الإخالة والمناسبة دليل الصحة ظاهرًا، وتخلف الحكم يمكن أن يكون لخلل أو إعراض، ويمكن أن يكون لتخصيصٍ من الشرع، أو [لمانعٍ] لم يظهر. وإذا ظهر الدليل، فلا يدل [بمحض] الإمكان. وعلى هذا التقدير يستغنى المستدل عن إبداء سبب تلف الحكم، إذا لم يتعين أن الوارد [مبطل]، لا علمًا ولا ظنًا، بل [مجرد] احتمال، والاحتمال الذي لا','3','705',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2337,'يعتضد بدليل، لا يترك له الدليل، وهذا كلام مخيل، ولكن طريق الجواب عنه، وبيان [حلة] هو أنه: إذا مكن أن يكون [تخلف الحكم لخلل في المعنى وإعراض، وأمكن أن يكون] لتخصيصٍ أو مانعٍ، فقد حصل الشك في الفساد، فمن المحال أن يبقى ظن [صحة] الوصف مع (١٢٦/ أ) الشك في فساده.
ومثاله: أن يشك الإنسان في الغيم ويظن الصحو، فإن ذلك محال، لا يعقل. فإن معقول الشك: التردد بين أمرين على السواء. فإذا حصل الشك في الفساد، حصل الشك في الصحة لا محالة. ومن المحال في الشريعة، الاعتماد على أمرٍ يشك في [استناده] إلى الشريعة. هذا هو الحق والمسلك الصدق. وبالله التوفيق.
الوجه الثاني لتخلف الحكم: هو أن يتخلف الحكم لمانع منع الاعتبار، وهذا على وجهين: قد يمنع من ثبوت الحكم، وينقل إلى بدلٍ عنه. وهذا وإن كان ظاهرة تخلف الحكم، فهو على التحقيق في معنى الثابت، فلا سبيل إلى ورود مثل ذلك نقضًا.
ومثاله: إذا قلنا ملك الأمهات سبب لملك الأولاد، في مسألة الغصب، والرد بالعيب وغير ذلك. [فإذا] ورد علينا نقضًا مسألة المغرور بحرية الأم، فإنه ينعقد ولده على الحرية، فلم يكن ملك الأم [سبب ملك] الولد. وهذا','3','706',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2338,'ضعيف، بل هو في معنى المملوك، ولهذا استحق القيمة فيه، ولو لم [يكن] يقدر الملك، لما ثبت على الواطئ الغرم، فهو بمعنى الثابت، الساقط من وجه آخر. وكذلك إذا قلنا: إن الشراء (٩٧/ أ) الصحيح سبب الملك على الحقيقة، [فإذا] ورد علينا شراء القريب الذي [يمتنع] تملكه، وقيل: لو قبل الملك زمانًا، لا ستمر عليه الملك. فنقول: يقدر فيه ملك، ليترتب عليه [عتق]، فلا تنافي القرابة مثل هذا ملك، وإن نافت الاستدامة، ولذلك يقضى بكونه عتيقًا على المشتري، ولو لم يثبت الملك، لما عتق عليه. فهذا وأمثاله لا ينبغي أن يورد نقضًا، لا على المجتهد، ولا على المناظر، فإن الحكم في معنى الثابت.
الوجه الثالث: أن يكون النقض مائلا عن صوب جريان العلة، ويكون تخلف الحكم، لا لخلل في ركن العلة، لكن لعدم مصادفتها محلها أو شرطها أو أهلها، كقولنا: السرقة [علة] القطع، وقد [وجدت] في النباش، فليجب القطع. فقيل: يبطل بسرقة ما دون النصاب، وبسرقة الصبي، وبالسرقة من غير الحرز. [ونقول: ] [البيع] علة الملك، وقد جرى في [الغائب]، وفي الأمة المتواضعة، فليثبت الملك. فقيل: يبطل ببيع المستولدة','3','707',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2339,'والجنين، وأمثال ذلك. فهذا [جنس] لا يلتفت إليه المجتهد، لأن نظره في تحقيق العلة، لا في [الأصل] والمحل والشرط، فهو مائل عن صوب نظره.
أما [المناظر] فهل يلزمه الاحتراز عنه، أم يقبل منه العذر [بأن] هذا منحرف عن مقصد النظر، وليس البحث (١٢٦/ أ) عن المحل والشرط؟ هذا مما اختلف الجدليون فيه، والخطب فيه يسير، فإذًا العلة غير منتقضة على التحقيق، وكذلك في مسألة المغرور.
فإن قيل: فهل تقولون في مسألة النطق القاطع لجريان العلة في صورة الاستثناء، إذا كان لا ينقدح، أن العلة موجودة، [ولكن] اندفع الحكم لمانع النص، ن تأبون ذلك؟ قلنا: لا نقول إنها علة، وقد بينا وجه ذلك.
فإن قيل: ففي مسألة المغرور بحرية الأم، الحكم أيضًا -وهو ملك الولد -منتفٍ، فهل تقولون إن الملك للأم ليس علة في تلك الصورة؟ قلنا: لا نقول ذلك، فإنا قد قررنا أن الحكم في معنى الثابت، ولذلك وجبت القيمة، فهو [في] معنى الثابت الساقط إلى عوض. هذا تحقيق القول في المسألة نظرًا وجدلاً.
ثم للمستدل في الجواب عن النقض وجوه [ثلاثة]: أحدها -منع وجود العلة في المسألة الناقضة. الثاني -منع تخلف الحكم. الثالث -الاستشهاد على العلة بالنص والإجماع، فيندفع النقض منها. ويتعين الإحالة على الاستثناء، وإبداء مانع أو فوات شرط، فإذا منع وجود العلة في صورة النقض، فليس','3','708',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2340,'للمعترض إثباته بالدليل، إذ ينقل الكلام لمسألة أخرى، ويتصدى المعترض لنصب الاستدلال، وكل واحدٍ منهما على خلاف ما يقتضيه سياق الجدل.

قال الإمام: <span data-type="title" id=toc-420>(مسألة: [اختلفت] مذاهب الأصوليين في أن علة الشارع، هل يرد عليها ما<span data-type="title" id=toc-421> يخالف طردها) </span></span>إلى قوله (وتخصيص الظواهر ليس بدعًا).
قال الشيخ: هذا القسم الذي ذكره الإمام، وهو إذا كان أصل النصب ظاهرًا، ثم ورد التخصيص، هل يتبين أن المذكور ليس بعلة، أو يتبين أنه غير عام النقض على التخصيص، إما بناًء على الاستثناء (٩٧/ ب)، وإما للحوالة على مانع، أو تخلف شرطٍ للعلة. وهذا قد اختاره بعض المتأخرين، وزعم أن النقض لا يسمع، إذا ورد على العلة المومأ إليها والمنبه عليها، لأنه عين إحالة تخلف','3','709',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2341,'الحكم، على [وجهٍ] لا يقدح في النصب.
وذهب الإمام إلى ذكره، من تنزيل الأمر على تأويل الظاهر، وأن الذي كنا نظنه علًة لم يبق على ظن، وكلا الفريقين اقتصر على ذكر المذهب معرًى عن الدليل.
ونحن ننبه على سبب الاختلاف، ثم نذكر الظاهر عندنا، فنقول: سبب هذا الاختلاف (١٢٧/ أ) تعارض الظاهر اللفظي والظاهر العرفي في الشريعة، فإن ظاهر اللفظ قد دل على تعين العلة، ولكن الظاهر في عرف الشرع أن العلة عامة لا اختصاص فيها، فيلزم من النظر إلى ظاهر اللفظ أن المذكور علة، وإحالة تخلف الحكم في الصورة الناقضة، على وجهٍ لا يقدح في صحة العلة، من استثناءٍ أو مانعٍ أو غيره. ويلزم من النظر في عموم النصب، أن المذكور ليس بعلة على الكمال، إذ لو كان علًة على الكمال، لكان الظاهر عمومه، فيتعين أن يكون ذكر ليعلق به، لا على طريق الاستقلال، إما أن يكون جزء علة، أو شرطًا أو [مهيًأ] لعمل العلة، فيتقابل [الظاهران]، فيلزم الترجيح عند التعين، أو [الوقف] عند الاستواء.','3','710',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2342,'أما من ذهب إلى إبقاء العلة على ما دل عليه الظاهر، فيقول: الاعتماد على الظاهر أولى من استنباط يزيل الظاهر عن ظهوره، [لاسيما] إذا أمكن ما قررناه من تخلف الحكم، بناء على التخصيص أو المانع.
أما من ذهب إلى أنا نتبين بورود النقض إزالة الظاهر، فيقول: إجراء العلل وعمومها أغلب في الشريعة من بقاء الظواهر على ظهورها، وإذا ثبت قوة في أحد الظاهرين، فهو أرجح، فيتعين المصير إليه. هذا لم يظهر سبب التخلف الاستثناء أو المانع، بل كان ذلك أمرًا مجوزًا. أما إذا اتضح ذلك، فلا وجه لإزالة الظاهر بحال، وكان الأقوى الوجه الأول.
لكن في مقابلة ذلك، [أنه] لو كان تخلف الحكم لمانع، لما خفي على الناظر، فإذا لم يطلع عليه، غلب على الظن فقده. فأما الاستثناء في الشرع، فقليل جدًا، وقل أن يأتي إلا مقرونا بأدلةٍ [تبين] وجه الاستثناء فيه. فعلى هذا الوجه، ربما قوي الأمر في التأويل، وإخراج الظاهر عن الدلالة. وإنما يكون هذا في حق المجتهد الباحث عن انتفاء الموانع، وتعذر جهات الاستثناء، فحينئذ يغلب على ظنه التأويل.','3','711',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2343,'قال الإمام: (وإن نص على التعليل على وجهٍ لا يقبل التأويل، تصدى [في ذلك]) إلى [آخر المسألة]. قال الشيخ: إنما صار الأستاذ إلى ما حكي عنه، من [جهة] ما قررناه له، من أن العلة في الشرع عنده على حد العلة العقلية، ولم [يستقرأ] اللفظ إلا منها. والعلة العقلية لا يمكن','3','712',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2344,'تخصيصها بحال، فإنها تقتضي معلولها لنفسها، فلا يتصور نقض بحال. وهو إذا قال ذلك، فإذا تخلف الحكم [عن] المنصوب علة، جعل المحل قيدًا، (١٢٧/ ب) [فيتلقى] الحكم من غير ذلك المحل، لنقصان العلة، وهو لا','3','713',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2345,'يفرق بين محل ولا شرط، ولا يعترف بمانع، ليكون نفيه قيدًا مضموما للعلة، بل متى تخلف الحكم في (٩٨/ أ) محلٍ، جعل عدم ذلك المحل قيدًا، فيتعين عنده أن يكون الحكم ثابتا أبدًا مع العلة. وهذا لعمري يسوغ أن يبنى الأمر على أن علل الشرع أعلام، فيكون التعريف في بعض المحال دون بعض. فيكون المحل قيدا في التعريف. وكل من فهم من العلل هذا المعنى، سلك هذا المسلك.
ولكن الظاهر عندنا خلافه، فإن في العلل ذوقًا من المناسبات، لم تجر على ذوق الأعلام المخصة. وإذا كان كذلك، فلا [يفيد] في تقابل','3','714',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2346,'المصالح، وترجيح بعضها على بعض. وبهذا الاعتبار تثبت الرخص في الشريعة، فإن الرخصة إنما تعقل بعد قيام سببٍ يقتضي النقيض، إذ هي: فسحة في مقابلة تضييق. ونحن نعلم أن الخمر، إنما حرمت لأجل الإسكار، غير معرجين على حالة الإكراه. وهذا المعنى موجود في صورة الاختيار والإكراه، ولكن الإكراه معارض راجح. وعلى هذا جرت مسائل الشريعة وعزائمها ورخصها، وقد قال عليه السلام: "إن الله يحب أن تؤتى رخصه، كما [يجب أن] تؤتى عزائمه". والرخصة: فسحة في مقابلة تضييق.','3','715',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2347,'قال الإمام رحمه الله: <span data-type="title" id=toc-423>(فصل -من توابع القول في النقض) [</span>إلى قوله (ثم نذكر بعد نقل مذاهبهم وجه التحقيق، إن شاء الله تعالى]). قال الشيخ: القول في النقض ينشأ من جهة إضافة الحكم إلى العلة، فلابد ههنا من البحث [عما] يصح أن يكون علة، وفي وجه إضافة الحكم إليها.','3','716',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2348,'أما العلة فقد بينا وجه استعارة الفقهاء اللفظ، ومن أي [وجهٍ] استعيرت، وبينا أنها مستعارة من أحد ثلاثة أوجه: فإما أن تستعار من العلة العقلية عند مثبتيها، وإما من علة المرض، وهي التي يتأثر بها المحل، وهذا الوجه هو الأغلب على رأي الفقهاء، وقد تستعار من العلامة، إذ علل الشرع علامات.
أما ما يصح أن يكون علة، كقوله: بطل بيع الخمر، لأنه حرم الانتفاع','3','717',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2349,'به، أو لأنه نجس، وقد غلط من قال إن الحكم مما يحتاج إلى علة، فلا يعلل به، واعتقد التناقض من ذلك، وهذا زلل. فإنا لسننا نقول إنه علة لمعلوله، وإنما يكون علة لحكم آخر، ويجوز أن يكون من أفعال المكلفين، كالقتل والسرقة، ويجوز أن يكون وصفًا مجردًا، أو مركبًا من وصفين أو أكثر، ويجوز أن يكون إثباتًا. وهل (١٢٨/ أ) يجوز أن يكون نفيا؟ أما من أخذ العلة من العلامة، فيصح ذلك عنده. وأما من أخذها من المقتضي، فيفتقر إلى أن يكون في الوصف مناسبة.
وهل يصح أن يكون نفي أمرٍ مناسبٍ نفيًا خاصًا، [أو] إثباتًا خاصًا؟ هذا مما اختلف فيه. والصحيح عندنا أنه لا مناسبة للنفي بحال، ولا فرق بين أن يقول القائل: هذا الحكم لا مقتضى له، أو له مقتضى منفي، بل قولنا: لا مقتضى له، لا تناقض فيه، وقولنا: له مقتض منفي، تناقض في الكلام. ويجوز','3','718',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2350,'أن يكون مكشوف المناسبة، ويجوز أن يكون متضمنا للمناسب، ضابطًا [له]. والصحيح عندنا في الوصف الشبهي أنه إنما اعتبر من جهة اشتماله على مخيل، هو مناط الحكم، فلا يبعد إطلاق لفظ (٩٨/ ب) العلة على الوصف الشبهي تجوزًا، ولكن عرف الأصوليين على خلاف ذلك. ويجوز أن تكون العلة غير موجودة في محل الحكم، كتحريم نكاح الأمة، لعلة رق الولد.
[النظر] الثاني: في وجه إضافة الحكم إلى العلة، وإنما تلقيت أحكام الفروع من وجود المعنى، من [جهة] اعتقاد عموم النصب، وأن الشارع إذا أناط [الأحكام] بالمعاني، أعرض عن الاقتصار على صور الأصول، واتبع','3','719',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2351,'المعنى حيث وجد، فلذلك اتبع المعنى. [وهذا] المعنى يسر القياس. وإذا كان كذلك، أشعر تخلف الحكم عن المعنى، بأنه ليس مرتبطًا به، [فنشأ] من ذلك سؤال النقض، وقد استقصيناه بحسب الإمكان.
الأمر الثاني: وجود الحكم مع تخلف العلة، وهو الملقب بعدم التأثير، وهو مترتب على صحة تعليل الحكم بعلتين، وامتناع ذلك.
والثالث: أن الحكم في محل النص [هل] يضاف إلى النص، أو إلى العلة؟ وعنه يتشعب النظر في العلة القاصرة. ونحن بعون الله [تعالى] نبتدئ القول في عدم التأثير.
قال الإمام: (قال أصحاب الجدل: عدم التأثير) إلى قوله (وهذا على','3','720',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2352,'حسنه غير صافٍ عن القذى والكدر). قال الشيخ: إذا صح تعليل الحكم بعلتين، لم يلزم من انتفاء العلة الحكم مطلقا، لصحة زوال علة وبقاء أخرى، فيستمر الحكم للمعنى الباقي. أما إذا امتنع تعليل الحكم بعلتين، لا على الاجتماع، ولا على التعاقب، فلا يصح نفي العلة مع بقاء الحكم على حال، إلا أن يفرض إجماع أو نص يقتضي الاستمرار مع زوال المعنى، ولكن سبب نفي الحكم، امتناع ثبوته، غير مرتبط بتوقيف أو علة، لا بمعنى أن عدم المعنى يقتضي نفي الحكم. هذا (١٢٨/ ب) عندنا لا يصح، لما قررناه من [أن] النفي لا اختصاص له بحال. فنسبة النفي إلى جميع الأحكام نسبة واحدة، فكيف يصح أن يناسب بعضها دون بعض؟
وأما منع الإمام تعليل حكمٍ بعلتين على الإطلاق، فقد سبق الكلام عليه في موضعه. ومذهبه ومذهب الأستاذ واحد، ولكن المآخذ مختلفة، فإن الأستاذ','3','721',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2353,'إنما تلقى العلة من [العلل] العقلية، وحذا بها حذوها. ولا يصح أن يعلل حكم عقلي بعلتين. وأما الإمام، فإنه يجوز التعليل بالمعنيين، لو وجدوا مستقيمين، ولكنه يزعم أنه لا يتصور في الواقع معنيان صحيحان، يصح إضافة الحكم إلى كل واحد منهما. هذا معتقده. فهو إن ثبت [تعدد] المعاني الصحيحة، تكلف تعدد الأحكام، وإن ثبت اتحاد الحكم، قضى بامتناع صحة جميع المعاني. وهذا الذي قاله تكلف عظيم، ومركب صعب.
وقد أورد على نفسه مسألة المرأة المحرمة الصائمة المعتدة الحائض. وهذه اللفظة كذلك وقعت في الكتاب، وهي غفلة. إذ لا يصح في الشرع أن تكون صائما حائضا. وتكلف هو أنها أحكام. وسيأتي لهذا مزيد تقرير، [إن شاء الله]. وقد ألزمناه نحن ما إذا [اجتمع] لبن أختك وزوجة أخيك','3','722',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2354,'وأوجزته صبية، فإنه يمتنع نكاحها عليك، لأنك خالها وعمها. [ولما] رأى الإمام أن [العكس]، إنما يلزم على تقدير اتحاد العلة، احتاج إلى تقدير ذلك، ودفع أسئلة عنه. وإن كان فيما تقدم من كلامه غنية عن هذا، فإنه (٩٩/ أ) قد اتسع القول في امتناع ذلك على حسب ما [يراه].
ولكن وجه ههنا أسئلة، لم يوجهها على نفسه [فيما سبق]، وهي مسائل الفرض، ومعنى الفرض من حيث الجملة هو: أن يشمل السائل سؤاله صورًا، فيفرض المجيب صورة منها، ويدل عليها، ويستفيد من الاستدلال صحة الجواب عن جميع ما اندرج تحت السؤال. هذا مسوغ في الجدال.','3','723',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2355,'ووجه تخيل الإشكال من هذا الكلام، أنه إن لم يكن محل الفرض يمتاز بوجهٍ عن بقية الصور، لم يكن لفرض الكلام فيه معنى، وإن لم يشترك محل الفرض مع بقية الصور في معنى يقتضي الحكم، لم يكن إلى التعميم سبيل، [ولزم] من ذلك معنيان:
أحدهما -يختص [بمحل] الفرض، ولذلك فرض المجيب الكلام فيه.
والثاني -يشمل محل الفرض وبقية الصور، ولذلك صح عموم الحكم وشموله.
هذا وجه تخيل لزوم تعليل الحكم بعلتين. وهو معنى قول الإمام: فقد علل الحكم بعلة خاصة، (١٢٩/ أ) هي مقصود [الفارض]، وعلة عامة، وهي التي صح لأجلها تعميم الحكم.
وقوله: وهذا على حسنه غير صافٍ عن القذى والكدر، معناه: أن المحل غامض، والكلام في فهمه عسر، فافتقر لأجل ذلك إلى ضرب الأمثال واتساع المقال.
قال الإمام: (وأنا أضرب في ذلك أمثلة توضح الفرض، فأقول: ) إلى قوله','3','724',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2356,'([لا ارتباط لها] بمآخذ [الأحكام] في صورة الغرور). قال الشيخ: مذهب أبي حنيفة أن [المتسبب] لا يترتب عليه ضمان، وإنما يترتب الضمان على المباشر للإتلاف. والتجأ لأجل هذا الأصل إلى [الحكم] بأن المكره على القتل لا يتوجه عليه القصاص، وجعل المكره في معنى الآلة، [ولذلك] أسقط عنه القصاص. والذي قاله في سقوط أثر السبب عند المباشرة، [فيه] فقه، إذ السبب أضعف من المباشرة. ومصيره إلى أن المكره كالآلة، ضعيف، مع توجه النهي [عليه]. ونحن نقول: إن أمكن','3','725',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2357,'إحالة الحكم على المباشرة، فهو أولى، وإن [تعذر] ذلك بوجه من الوجوه، فتصح الإحالة على السبب على وجه مخصوص. فقال أبو حنيفة لأجل ذلك: إن الآكل يضمن لمباشرته، ولا ضمان على الغار، لأنه متسبب. وقال الشافعي: الضمان على [المقدم]، لأنه [مغرور]، والتغرير من أسباب الضمان.
وأما مالك [رحمه الله] فإنه يقول: يستقر الضمان ابتداء على الغار، فإن تعذر تقديمه، لعدم أو غيره، ضمن الآكل. وبعض أصحابه يخير المالك في تضمين من شاء منهما.
ووجه مذهب مالك [رحمه الله]: أن كل واحد منهما وجد في حقه سبب يقتضي التضمين. ففي حق الغار التسليط، والذي يحقق أن التسليط من','3','726',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2358,'[أسباب] الضمان: أنه لو سلط كلبا على إنسان فقتله، لوجب عليه الضمان. وإن كان التسليط سببا، فالإتلاف أيضا سبب بلا إشكال. وإذا اجتمع السببان، وامتنع إعمال كل واحد منهما [مع] صاحبه، إذ لو فعل ذلك، لضممنا جميعا المال مرتين. وذلك مجمع على خلافه، فلم يبق بعد ذلك إلا الترجيح أو [التساقط]. أما [التساقط]، فلا ذاهب إليه. فكان الترجيح عنده للغار، لانتسابه إلى الظلم والعدوان. هذا [إذا أمكن] تغريمه، فإن تعذر ذلك، غرمنا المباشر.
وهذا هو المختار عندنا، نظرًا إلى المصالح، ودفع الضرر عن [الملاك]، وانتصاب زاجرٍ عن الإتلاف والسبب إليه. وبعض أصحابنا يرى التخيير، لتعادل السببين (٩٩/ ب). والأول أصح وأجلب للمصالح على ما مر. فإذا سلم الشافعي عن ضمان المغرور، فأفتى [بأنه] لا ضمان (١٢٩/ ب)','3','727',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2359,'عليه، [وإذا] طولب بالدليل فقال: أفرض الكلام في المكره. فهذه الصورة لا تصلح للفرض. وأنا قد بينا أن الفرض، إنما يكون عند شمول السؤال [الصور كلها]، فيفرض المجيب الكلام في بعضها، لغرضٍ له في ذلك. وسنبين -بعد هذا -الغرض الذي يحسن الفرض لأجله.
أما إذا كان الذي فرض الكلام فيه، ليس يندرج تحت سؤال السائل، فهذا انتقال إلى مسألة أخرى، وإضراب عن محل السؤال، وذلك لا يقع في أبواب الفرض بحال.
فإن قال قائل: لا يتحقق الإضراب عن السؤال، فإن المكره يصح أن يعتقد أن الطعام ملك المكره، ويكون أيضا مغرورًا، [وإذا صح أن يكون مغرورًا]، فقد اندرج تحت السؤال، فإن السؤال: هل يضمن المغرور أم لا؟ قلنا: هذا ليس بشيء، فإن المفهوم من [الآكل] المغرور، أن الغرور هو الذي دعاه إلى الأقدام. ومن ضرورة [الاغترار] بقاء الاختيار، [وإسناد اغتراره إلى اختياره. وإذا لزم من الاغترار بقاء الاختيار]، والإجبار ينافي','3','728',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2360,'الاختيار، فهو لا يتحقق معه [الاغترار]. وإن فرض من المكره ظن على بعدٍ، فليس هو الحمل على التناول، وإنما جاء أمر [وفاقي]، لا أثر له في الحكم بحال.
فإذا لم يصح أنه مغرور غرورًا [يحمله] عل التناول، تعين أن الحامل الإكراه، وهو مفقود في المغرور، فمن أين يلزم إذا سقط الضمان عن المكره المحمول، أن يسقط عن المختار؟ فلا يصح وقوع هذا في أقسام الفرض بحال، إذ الفرض المستحسن، فرض الكلام في طرفٍ يشتمل عليه سؤال السائل.
وفائدته ضم نشر الكلام، وخروجه عن الإطالة، حذرًا من أن لا يفي مجلس واحد بالمقصود. أما إذا كان ما فرض الكلام فيه، لا يندرج تحت عموم [سؤال] السائل، لم يكن من فن الفرض.
وقول الإمام: (وإنما هو بناء، ولست أرى البناء) إلى قوله (سقوط','3','729',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2361,'الضمان عن المختار الطاعم) فنقول: أما البناء، فله صورتان: إحداهما تصح، والأخرى يختلف فيها. أما الصحيحة، فهو أن تكون مسألة النزاع فرعًا لمسألة توقيفية نازع الخصم فيها، فيفتقر المستدل إلى فرض الكلام في الأصل الذي تعتبر المسألة به، فإذا نوزع فيه، أسنده إلى التوقيف، فإذا استقام له الحكم فيه سليمًا عما يعارضه، اعتبر المسألة به. وقد أبى ذلك بعض الناس، وقال: لابد أن يكون الأصل متفقًا عليه بين الخصمين. والصحيح خلافه.
وأما إن كان المحل الذي فرض الكلام فيه، لا يسلمه الخصم، وليس فيه توقيف، وافتقر المستدل إلى اعتباره بغيره، ثم اعتبار مسألة النزاع به، فهذا عندنا (١٣٠/ أ) غير صحيح، وهو القياس على فرع العلة، لأنه لا يخلو: إما أن يجمع بين الفرع الأول والثاني بجامع الأصل، أو بغيره. فإن جمع بجامع الأصل، فلا حاجة إلى الفرع الأول، فيكون تطويل طريقٍ بلا فائدة. [وإن جمع بغير جامعٍ الأصل]، لم ينتفع به. وقد بينا ذلك فيما مضى، ونبهنا على خيال من أجازه، وأبطلناه عليه. فلا أرى البناء إلا في [مواضع] التوقيف على الخصوص.','3','730',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2362,'وقول الإمام: أساء أبو حنيفة، إذ قرر الضمان على من لا اختيار له، لفظة موهمة، (١٠٠/ أ) فإنه قد يطلق لفظ عدم الاختيار، ويراد به سلب القدرة، وتنزيل الشخص منزلة الآلة. وهذا بمثابة ما لو حمل الشخص، وألقي على مال رجلٍ فأتلفه، هذا لا خلاف أنه لا يصلح أن يكون سببًا للضمان. أما إذا أكره على الإتلاف، فلا بعد في توجه الضمان عليه، إذ هو على الحقيقة متمكن من الانكفاف، وغاية ما فيه، تغريمه إتلاف مال غيره، إبقاء على نفسه، فهو بمثابة ما لو أكل مال غيره، إحياء لنفسه عند المخمصة، فإنه ضامن لذلك المال، فكذلك إذا أكره على الإتلاف.
وكيف ينكر ذلك، [والشافعي رحمه الله يرى] أن المكره على القتل يقتل، وإن كان مكرها؟ لكن قد يفرق بينهما، من جهة أن الإكراه لا [يبيح] القتل، وهو يبيح الإتلاف. ولكن لا ينبغي أن يلتفت إلى هذا، فإن الضمان لا يستدعي العدوان، بل قد يجب الإتلاف، كما صورناه في مسألة المضطر في المخمصة. وقد يجب مع سقوط التكليف، وهو [ما] إذا أتلف النائم أو الغافل، فالضمان جبران للفائت، فلا يكون تقدير الضمان على المكره إساءة بحال.
قال الإمام: (ونحن نفرض صورًة من الفرض المستحسن) إلى قوله (فلم','3','731',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2363,'ينتظم [على المسلكين] [علتان] عامة وخاصة في صورة الفرض). قال الشيخ: هذا الفرض الذي ذكره الإمام حسن في باب الفرض، فإن السائل إذا [سأل] عن عتق الراهن، عم سؤاله الموسر والمعسر، [فإذا فرض المجيب الكلام في المعسر، كان ذلك فرضًا صحيحًا، وقد يتخيل المتخيل في هذه الصورة معنيين:
أحدهما-يخص محل الفرض، ولذلك اختار الفارض الفرض فيه.','3','732',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2364,'والثاني -يعم المعسر والموسر]، ولذلك [صح] أن يعم الحكم الصورتين جميعا.
هذا وجه تخيل من تلقى من الفرض تعليل حكم بعلتين، أي في صورة الفرض معنى خاص، ومعنى عام يشملها (١٣٠/ ب) مع الصورة الأخرى.
وبيانه في المثال المذكور: أن الراهن إذا فقد عتقه، وهو معسر، فقد أبطل حق المرتهن من [عين] الرهن، وهو أمر مقصود، وأبطل أيضًا حقه من المالية. وإبطال حق العين، هي العلة العامة، لأنها تتناول المعتق الموسر والمعسر. وأما قطع [المالية]، فيختص به المعسر، وهي العلة الخاصة.','3','733',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2365,'[ولذلك] اختار الفارض الفرض فيها. وهذا أقرب مسلك في تخيل اجتماع المعنيين للحكم الواحد. وهذا عندنا غير صحيح، ومذهبنا جواز التعليل بالعلتين جميعًا.
وإنما حمل الإمام على هذا الكلام، مصيره إلى امتناع تعليل الحكم بعلتين، فزعم أن المعنى المانع من نفوذ العتق، قطع حق المرتهن من عين الرهن خاصة، ولا أثر للمالية عنده، وإنما المعتبر عنده حق التعيين، ولذلك ثبت استحقاق الاختصاص بالعين المقبوضة، عند طريان العسر على الدين الواقع في الذمة، قال: [لو] رجعت المطالبة في الرهن على الذمة، لفات [مقصود] [التوثقة]، ولذلك لا يجوز رهن الدين. أما رهن الدين، فله صورتان:','3','734',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2366,'إحداهما: أن يرهنه دينا على الغير، فهذا يصح رهنه عندنا، إذا [خلي] [ما] بينه وبين المديون، وانقطعت مطالبة رب الدين به، ورجعت المطالبة للمرتهن. والمقصود من الرهن رفع يد الراهن عنه بحسب الإمكان. والديون عندنا يصح بيعها وهبتها. فكذلك يصح رهنها، ويتنزل المرتهن (١٠٠/ ب) فيها منزلة ربها. ويدل على صحة بيع الدين، جواز الحوالة في الدين، وهو تمليك الدين لطالب ربه، فقد صح نقل الملك فيه. وإذا جاز بيعه وتحقق الملك [فيه] وقبضه، فلم لا يصح رهنه؟
الصورة الأخرى في الدين: أن يلتزم له رهنا غير [معين] متعلقًا بذمته. فهذا عندنا يصح، ويجبر على إقباضه وتسليمه، وتمكين مشترطه من فسخ','3','735',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2367,'عقده، إن عقده بشرط رهنه، إذا تعذر عليه قبضه، إلا إن يشاء إمضاءه دونه، ولكن لا يكون له أحكام [الرهن]، من اختصاصه به دون غرماء من عليه الدين لا بقبضه. هذا لابد منه، إذ كيف يكون أحق بما يتعلق بذمة غريمه دون بقية غرمائه، ونسبتهم إلى ذمته في ديونهم على جهة واحدة، ولم يحصل في الرهن قبض؟ وكذلك لو كان الرهن بعينه، ولم يتفق قبضه حتى قام غرماؤه، لم يكن أحق به.
وأما قوله: إذا كفي التعلق بقطع الحق من [العين]، فأي غرض يتعلق بقطع المالية؟ قلنا: لا يلزم من الاكتفاء بمعنًى، إبطال غيره، إذا سلم في','3','736',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2368,'نفسه، وظهرت (١٣١/ أ) مناسبته وملاءمته. وقطع حق المالية [مناسب للمنع]، لما يفضي إليه من تفويت الحق بكماله، ولذلك أنه يتعلق الحق بالعين تارة، وبالمالية أخرى. والتصرف الذي يفضي إلى قطعهما وإبطالهما، أولى بالاعتبار. وهذا إنما يتخيل على أصل الشافعي خاصة. أما مالك رحمه الله، فإنه في مسألة العتق، إنما يعتمد على قطع حق المالية، ولا التفات عنده إلى عين الرهن، إذ ليس تعينه مقصودًا لنفسه، بل لغرض الوصول إلى المال. ولذلك لو أراد من عليه الدين تعجيله قبل محله، لاستعجال أخذ رهنه، لم يمنع من ذلك، ولم يكن للمرتهن امتناع لحبس الرهن. فلا يمكن الاعتماد على علتين على هذا المذهب، لانحصار الغرض في تحصيل المالية.
وأما أبو حنيفة [رحمه الله]، فإنه إنما يعتمد على صحة ملكه، ونفوذ عبارته. وجامعه: أنه إذا سرى عتقه إلى غير ملكه، فكيف ينبو عن محل ملكه؟','3','737',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2369,'وهذا القول ضعيف، لأنه لا يسري مع العسر على حال. [ولذلك] كان ينبغي أن لا ينفذ عتق المعسر الراهن. نعم، يصح أن يعتمد على هذا المعنى مالك، فإنه جعل نفوذ العتق من الراهن بمثابة تسرية عتق الشريك. وهذا الكلام صحيح، فإنه إذا [سرى] العتق، أو حكم به متعديا إلى ملك الشريك، عند حصول مقصود الشريك من المالية، فكذلك إذا حصل مقصود المرتهن من الدين، لا من قيمة العبد، فلا يمتنع عتقه، كما قررناه.
وأما الإمام [رحمه الله] فإنه لم ير الاعتماد على المعنيين جميعا، لأن أحدهما عنده في معنى البدل عن الأصل، فلا يرى الجمع بين الأصل والبدل، والأصل عنده قطع حق المرتهن من العين، فإنه إذا حفظت العين عليه، ففي ذلك حفظ المالية ضمنا. قال: وإنما نعود إلى التمسك بالمالية عند فوات العين، فيرجع إلى البدل، جبرانًا للفائت، ولا يصح أن يجمع بين الأصل والبدل، فلذلك أنا في الأموال نحافظ على الأعيان، ولا نقول يجب الضمان مع بقاء الأعيان، بل نطلب ردها بحسب الإمكان. فإذا تعذر ذلك، رجعنا إلى','3','738',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2370,'المالية. هذا [هو] مقصود الإمام في هذا المقام. (١٠١/ أ) وله وجه بين في فن المسألة.
وأما امتناع اجتماع العلتين للحكم الواحد، فقد سبق ما نختاره فيه، وسيعود إن شاء الله تعالى.
وقول الإمام: وهذا يناظر [عندي] [مسلكين] إلى آخره. قلنا: قصد (١٣١/ ب) الإمام في هذا [المكان] في التشبيه أن رجوع المرتهن إلى طلب مالية الرهن عند فرض التلف، لا يوجب أن تكون المالية مطلوبًا أصليًا، فيما يتعلق بغرض [التوثقة]، وإنما هو أمر جرت إليه الأحكام، ودعت إليه الضرورة، فلا ينبغي أن يظن أن ذلك مقتضى الأصول ابتداء. [وكذلك] إذا','3','739',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2371,'[شمل] العقد الواحد عرضين [بثمن واحد]، فلا نقول إن التوزيع يثبت بمقضى العقد، حتى نقول ينصرف بعض الثمن إلى أحد المبيعين، والبعض الآخر إلى المبيع الآخر، حتى يكون ذلك في معنى عقدين، بل جملة الثمن منسوبة إلى جملة المبيع، ويتنزل [العرض] الثاني منزلة بعض أجزاء العرض المبيع، فلا يقدر التوزيع في المبيع الواحد بحال. وكذلك يكون الأمر في المبيعين عند اتحاد العقد.
وإن استدل مستدل على التوزيع، بما إذا فرض استحقاق أو عيب في أحد المبيعين [ورد]، فإنا ننسب الثمن إليهما، [ويرجع بما] ينوب المردود منهما. فيقال: هذا أمر ضروري دعت إليه الحاجة عند الاستحقاق، [والأخذ] بالشفعة. فلا يظن أن ذلك مقتضى الأصول عند انعقادها. وكذلك نحكم بثبوت الأملاك بالملاك، وتوقف على الاختيار. ولا يقدح في ذلك إلزام التقويم في مسألة عتق الشريك، إلى غير ذلك من مظان الضروريات.
هذا مقصود الإمام. وقد تكلمنا عليه، وبينا مستنده فيه، فلا نعيده. ولا يصح على مذهبه علتان عامة وخاصة في هذا المثال. ولكن مذهبه صحة تعليل الحكم بعلتين، إذا أمكن ذلك، وسلمت المعاني من المفسدات.
قال الإمام: (ومما [نجريه] مثالاً في ذلك، أن الشافعي إذا فرض','3','740',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2372,'الكلام في مسألة ضمان المنافع) إلى قوله (إذا الحكم في هذا التقدير [مستقل جامع] من المعاني). قال الشيخ: ما ذكره الإمام في هذا المكان من فرض الكلام في جانب [إتلاف المنافع]، يعني به أن الإتلاف ثبت في الضمان، والتلاف أيضًا تحت اليد العادية، بسبب مستقل، وهو إذا أتلف الشيء فقد تلف. وهذا من أعظم ما يتمسك به في اجتماع العلل للحكم الواحد. وهو من أحسن [صور] الفرض، إذ كل متلفٍ تالف، وإذا أتلف فقد تلف الشيء تحت يده على حكم العدوان. ولا يمكن إنكار كون هذا سببا للضمان، فقد علل الحكم بعلة خاصة، وهي الإتلاف، [وأخرى] عامة، وهي التلف تحت اليد العادية.','3','741',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2373,'أجاب عن ذلك بأن قال: (١٣٢/ أ) العلة في الضمان الإتلاف فحسب، وإنما أقيم التلف تحت اليد العادية مقام الإتلاف، دفعًا للظالم، ومنعًا للتعدي. [فكان] من المناسب أن يقدر المتعدي كالمتلف، وإن لم يكن متلفا. والذي يحقق الأصالة في جانب الإتلاف، أن المناسبة تقتضي أن من أتلف شيئًا، غرم مثل ما أتلف أو قيمته. وأما [التلاف] الذي يقع سماويا، فلا تظهر فيه هذه المناسبة.
وهذا الذي قاله ظاهر في تفاوت المناسبتين، ولكن ليس فيه (١٠١/ ب) ما يدل على أن أحدهما أقيم مقام الآخر، بل يكون الشرع جعل الإتلاف موجبا للضمان، وجعل العدوان سببا للضمان، على تقدير الفوات. وعلى هذا التقدير، لا يمتنع اجتماع المعنيين للحكم الواحد، ويكون غرض الفارض ذلك. أما إذا ثبت للإمام [ما رامه] من أن التلاف تحت اليد العادية إذا ضمن، أقامه مقام الإتلاف، فلا يتصور مع ذلك أن يجمع بينهما [بحال]، إذ لا يصح أن','3','742',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2374,'[يقال] الشيء كنفسه، فكيف يقال ضمن التلاف، لأنه كالإتلاف في موضع الإتلاف. هذا لا يصح بحال.
ولكن هذا الكلام من الجانبين، إنما يستقيم أن لو أمكن الجمع بين الإتلاف والتلف حتى ينظر، هل هما معنيان مستقلان، أو أحدهما مشبه بصاحبه؟ وليس الأمر كذلك عندنا، بل معنى الإتلاف: إهلاك الشيء. وكيف يتصور أن يجتمع إهلاكه وهلاكه معا؟ هذا محال، وإنما يرجع ذلك إلى تعدد الألفاظ دون المعاني. ومعنى إهلاك الشيء: أنه هلك مستندا إلى جناية جانٍ. فكيف يصح مع ذلك أن يهلك غير مستند لجناية تتخيل [الاجتماع] [بحال]؟ فلا نظر بعد ذلك إلى الأصالة والبدلية.
وأما تشبيه الإمام هذا المسلك بمسلك نفي الجواز والكمال، فمقصوده به أنه: كما لا يصح نفي الجواز والكمال معا، فكذلك لا يصح الإتلاف والتلف جميعا، ولكن ذلك له مأخذ آخر، وذلك أنا إذا قلنا: إن هذا الشيء ليس بكامل، اقتضى ذلك كونه مجزئا، فإذا كان نفي أحدهما يتضمن ثبوت الآخر، فكيف يصح أن يعتقد نفيهما جميعا؟ هذا محال. ومن هذا الوجه','3','743',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2375,'يحسن التشبيه، أي كما لا يصح نفي الإجزاء والكمال جميعا، فكذلك لا يتصور اجتماع الإتلاف والتلف جميعا. فلا يكون ذلك ممتنعا من جهة أن أحدهما يتنزل منزلة الآخر، بل امتنع ذلك لاستحالة الاجتماع. وإذا لم يتصور في [صورة] الفرض اجتماع معنيين لحكم واحد، فما فائدة الفرض مع أن الاعتماد على المعنى الفرد، وهو لو ذكره (١٣٢/ ب)، لكان شاملا لجميع الصور؟
قال الإمام: ([فائدة]: دفع أسئلة قد يعتاص الجواب عنها) إلى قوله (اندفع هذا الكلام [الواقع] فضلة). قال الشيخ: هذا ضعيف، لأنه إذا زعم أن حكم الموسر حكم المعسر، توجه السؤالان، إلا أن يتمسك بانعقاد [إجماع المسلمين] على نفي الفرق، فيكون في الفرض فائدة على هذا التقدير.','3','744',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2376,'قال الإمام: (فلم يقع تعليل حكم بعلتين، ولكن إن لم يتفق) إلى قوله (إشعار آحاد الصفات). قال الشيخ: قصد الإمام بجميع ما ذكره من مسائل الفرض، أنه إذا صح تعليل الحكم بعلتين، لم يكن التعلق بالعكس اعتراضا في رسم الجدول، لجواز انتفاء علة وبقاء أخرى. فأما إذا لم يصح تعليل حكم [بعلتين]، فإذ [ذاك] يتمسك بتخلف الانعكاس. والإمام يرى أن العلل لا تجتمع وقوعًا، ولكنها لا يمتنع اجتماعها (١٠٢/ أ)، نظرًا إلى المصالح. فإذا قدر وقوع هذا المجوز -لما اقتضى انتفاء المعنى انتفاء الحكم -من باب الإشعار، اقتضى الاستقلال، لصحة إمكان معنى آخر يستند الحكم إليه.','3','745',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2377,'وكلام الإمام في هذه المسألة مضطرب اضطرابا شديدا، فإنه يقول: إذا انتفى المعنى مع ما سبق من ثبوت اتحاد العلة، وجب أن ينتفي الحكم، إذا لم يرد توقيف باستمراره، لاستحالة بقاء الحكم غير مرتبط بتوقيف أو علة. وعلى هذا، يكون انتفاء الحكم لعدم ما يقتضي ثبوته.
وتارة يرى أن لعدم المعنى [أثر] في نفي الحكم، لا على طريق الاستقلال، فلا يستقل على هذا نفي المعنى في اقتضاء نفي الحكم. وهذا هو الذي ذكره ههنا، فلا يستقل على هذا التقدير نفي المعنى الفرد بنفي الحكم. وإنما ينتفي الحكم على تقدير [انتفاء] كل ما يتخيل معنى. وهذا عندنا ضعيف لوجهين:','3','746',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2378,'أحدهما -أنا قد بينا أن النفي لا يناسب أصلا، [لا] على جهة الاستقلال، و [لا] على جهة [الجزئية].
الثاني -أن الإمام يرى أنه لا يصح أبدًا اجتماع معنيين صحيحين لحكم واحد. وإذا كان كذلك، واتحد معنى الحكم، فعند انتفائه يتحقق انتفاء جميع المعاني، فما المانع من استقلال نفي المعنى بنفي الحكم؟ وكيف يصح أن ينزل عدم المعنى بالإضافة إلى نفي الحكم منزلة فقدان وصف من الأوصاف في العلة المركبة؟ وانتفاء وصف لا يصح معه ثبوت الحكم، وانتفاء علة لا يلزم معها نفي الحكم. ولكن مقصوده، أن الأوصاف إنما تثبت الحكم إذا اجتمعت، فلكل وصفٍ على حياله حظ من المناسبة، لا على الاستقلال. وكذلك كل علة لها -في خال (١٣٣/ أ) عدمها -أثر في نفي الحكم. فإذا انتفى جميعها، اقتضى ذلك نفي الحكم.
وتارة يقول: انتفاء العلة الواحدة، يضاهي انتفاء ترجيح. وهذا القول أيضا','3','747',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2379,'ضعيف، إذ كيف يصح أن يقال: [إن] زوال وصفٍ من أوصاف العلة المركبة، يضاهي زوال ترجيح، وإنما يصح الترجيح بعد تحقيق الاستقلال، فلا يستقل نفي الحكم -على قوله -إلا بنفي الجميع.
وتارة يقول: [إن] نفي المعنى، قد يناسب نفي الحكم. وهذا أيضا مثله بمثال لا يصح، وسنتكلم عليه إذا جئنا إليه، إن شاء الله.
وتارة يزعم أن انتفاء الحكم عند انتفاء العلة متلقًى من وضع التعليل ضمنًا. فعلى هذا، إذا نصب الوصف علة، فقد ارتبط الحكم بوجوده صريحا، وارتبط [بنفيه] ضمنًا، فلا حاجة على هذا التقدير إلى أن يتبين انتفاء جميع العلل، بل متى ثبت كون الشيء علة، أخذ من ضمن ذلك نفي الحكم عند انتفائها. والصواب عندنا خلاف ذلك كله، وأن العكس غير لازم على حال، ولا يقتضي نفي الحكم مطلقًا. نعم، إن أتحدث العلة وفقدت، وانتفت التوقيفات، امتنع ثبوت الحكم. إذ ثبوته في ذلك الوقت، تحكم بغير برهان.
قال الإمام: (وإذا [تقرر] ما ذكرناه، فنقول: إذا طرد المعلل علة','3','748',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2380,'واطردت له) إلى قوله (في مفاوضة من يناظره). قال الشيخ: أما بطلان العلة بالمسألة الناقضة، فقد (١٠٢/ ب) تقدم الكلام فيه، وتبين أن الإمام لا يرى النقض بمواقع الاستثناء، ونحن نقول كذلك، وكذلك مصيره إلى أن كون الناقضة، متى كانت معللة [أبطلت]، أمر لا يوافق عليه، [ونقل] أن بعض الناس يقول ببطلان العلة عند ورود الاستثناء عليها. وهذا لا يتصور فيه خلاف أبدًا، إذ كيف تبطل العلة مع تحقق الاستثناء، وهو كالصريح في [تقدير] التعليل؟ فمن العجب، كيف ينقل الإمام الخلاف في هذا؟ نعم، يمكن أن يكون الخلاف فيما إذا كانت الناقضة [لا] يعقل معناها، فيقول بعض الناس إنها لا تبطل، إما بناء على أنها مستثناة، أو إعراضًا عن النادر. ويرى المبطل أن المعنى قد انقطع طرده، لما تحقق كونه مستثنى. هذا هو الذي','3','749',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2381,'يمكن الخلاف فيه. فأما بعدد صحة الاستثناء في الناقض، فلا يتصور فيه خلاف بحال. وإذا كان لا يتصور الخلاف في الناقض المستثنى، فكيف يتصور أن تبطل العلة إذا منع الاستثناء من عكسها؟
وقوله: إذا اعترضت المسألة الناقضة، وهي غير معللة، فعلى المستدل أن يبين السبب المانع من الطرد، ويلزمه ذلك في أدب الجدل. ولو (١٣٣/ ب) فرض عدم الانعكاس بمثل ذلك، فلا يلزمه أن يبين السبب التوفيقي المانع من العكس. وهذا الكلام فيه غموض، وذلك أنه إذا انقسم الناقض إلى ما يقدح، وإلى ما لا يقدح، وألزم المستدل [أن يبين] أنه من قبيل ما لا يقدح، فكذلك انقسم عدم التأثير، إلى ما يقدح، وإلى ما لا يقدح، [فهل] على المستدل أن يبين أنه من قبيل ما لا يقدح؟
اختلف الجدليون فيه، فألزمه بعضهم ذلك، وامتنع منه المحققون. [أما] من ألزمه، فوجهه ما ذكره في لزوم النقض، إذ الدليل معرض للبطلان،','3','750',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2382,'فلابد من الجواب. وقال المحققون: إنه غير لازم، لأنه إذا كان في بعض المواضع لا يقدح، فلم يتحقق المعترض الاعتراض، وقد سبق المستدل إلى الاستدلال. فإذا كان الوارد يمكن أن يكون قادحًا، ويمكن أن يكون غير قادح، لم يتحقق الاعتراض. وهذا لعمري -إذا قيل به -يمنع من لزوم الجواب عن [النقض] أيضا، ولكن الصحيح الفرق بينهما، وذلك أن الحكم في الفرع، إنما ثبت على تقدير عموم النصب في العلة. فإذا ظهر التخصيص، فقد فات الدليل، وليس يتلقى الحكم في مسألة النزاع من انتفاء العلة على حال. [وسر] الجدل قصر الكلام على محل النزاع. فأما إذا انتقل الكلام إلى بيان السبب المانع من الانعكاس، فقد خرج الكلام إلى الانتشار، وجاوز حد [الاختصار]. فالصواب الفرقان بين الإلزامين، ووجوب الجواب عن أحدهما دون الثاني.
قال الإمام: (فإن قيل: هل يسوغ أن يضع المستدل كلامه، مبنيا على','3','751',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2383,'الدعاء إلى العكس؟ ) إلى قوله (عند أصحاب الجدل من عدم التأثير في الوصف). قال الشيخ: معنى قوله: هل يسوغ أن يضع المستدل كلامه مبنيا على الدعاء [إلى] العكس، أنه يقول: [يثبت] الحكم المتنازع فيه بثبوت العلة، وينتفي [بانتفائها]، فيلزم الخصم الإثبات والنفي جميعًا، لما (١٠٣/ أ) [قررناه] من اقتضاء نفي العلة نفي الحكم اقتضاًء ضمنيًا؟ قال: [لا','3','752',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2384,'يستقل في هذا كلامه] المطلق، بل عليه أن يبن معناه، ويبين فساد ما عداه. وهذا عجيب منه، مع مصيره إلى أنه متى صح معنى، بطل ما سواه. [هذا] مذهبه، فأي حاجة له بعد بيان صحة معناه، أن يبين فساد ما عداه؟ ولكنه لما [أبدى] الخصم معنى سواه، لم يقبل منه تعيين معناه للصحة، إذ يمكن للخصم أن يعكس ذلك، فافتقر لذلك [أن] يبين فساد ما ادعاه الخصم، ويفتقر مع ذلك إلى الإشارة إلى نفي التوقيف المانع من العكس، فحينئذ يسوغ الدعاء إلى العكس، إما لاقتضاء نفي المعنى ذلك، وإما (١٣٤/ أ) لامتناع ثبوت الحكم غير مستند إلى معنى أو توقيف.
وأما قوله: إن هذا معدود [عند] أصحاب الجدل من عدم التأثير في الوصف، فسنتكلم عليه في آخر المسألة، إن شاء الله تعالى.
قال الإمام: (وأما ما عد من عدم التأثير في [الأصل]) إلى قوله','3','753',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2385,'(كترك [السنن] والهيآت في العبادات]). قال الشيخ: إذا ضم إلى علة الأصل وصف لا أثر له في الحكم، لا استقلالاً ولا انضمامًا، فلا حاجة إلى ذكره بحال، فإنه إنما يجمع بين الفرع والأصل بعلة الحكم في الأصل. [فإذا كان معنى الأصل على مجرده لا [يباين] وجوده في الفرع، فكيف يستند حكم الفرع إلى أمر لا أثر له في الأصل]؟
وهذا كما مثله في الأمة الكتابية، فإنه لا يجوز تزويجها عند مالك والشافعي. فإذا نظم القياس وقيل: أمة كافرة، فلا يصح لمسلم تزويجها، كالأمة المجوسية، فلاشك أن الرق لا أثر له في الأصل، إذ الحرة المجوسية لا يجوز تزويجها. وإذا بطل أن يكون للرق أثر في الأصل، وجب حذفه، فلا يبقى إلا التمجس، وهو مفقود في الفرع. وهذا قد قبله بعض الجدليين، بناًء','3','754',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2386,'منهم على أن الحكم ثابت في الأصل، وقد حصل تركيب العلة من وصفين. وإذا نوزع المستدل في سقوط أحد الوصفين واعتباره، فعليه إثباته بالدليل. وهذا يلتفت على التركيب، وسيأتي تقريره فيه.
والصحيح عندنا بطلان هذا القياس، فإنه إذا كان الحكم يستقل في الأصل بمجرد التمجس، فهذا مفقود في الفرع بلا شك، فلا يصح أن يثبت حكم الأصل في الفرع مع فقدان جامع الأصل. وكون الرق [مؤثرا] من حيث الجملة، لا يغني في هذا المقام، إذ تحقق أنه ليس بعلة فيه، ولا جزء علة.
نعم، إن أمكن إلحاق الفرع بأصل آخر، واستقل الرق مع الكفر فيه، صح الاعتبار. ولكن هذا لا يصادف، ولو فعل ذلك، لكان ما عين أصلاً [عين] مسألة النزاع. وإذا بطل الجمع على هذا الوجه المذكور، نظر، فإن كانت الزيادة لو حذفت، لأمكن الإلحاق واستمر القياس، صح ذلك، ولكن يكون ذاكرها [آتٍ] [بلغو] لا فائدة [فيه]، ولا ينبغي أن يحكم [ببطلان]','3','755',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2387,'العلة. أما في الدين، فيتعين إعمالها. وأما في الجدل، فلا ينبغي أن يقضى بكون ذاكرها منقطعًا، وإن كان إذا قدر حذفها، امتنع الإلحاق، كما صورناه، فهذا يمنع التمسك بهذا القياس دينًا وجدلاً.
وأما ما تمسك به [المجوزون] من تنزيلها منزلة الشاهد الثالث في الاستظهار، فغير صحيح، فإن ذلك مما يقوى غلبة (١٣٤/ ب) الظن بصدق الشاهدين (١٠٣/ ب)، [وهو] أيضًا معرض لأن يقع ركنًا، على تقدير تطرق خللٍ إلى أحد الشاهدين، وليس هذا الوصف صالحًا [للعلية] بحال. على أن الشاهد الثالث، لا يتحتم الإتيان به، بل إن وجد كان استظهارًا، وهذا القياس لا يتم إلا بذكر الزيادة.
ثم فيه أيضًا وجه آخر، وهو أنه إذا ركب علة الأصل من التمجس والرق، فالتمجس مفقود في الفرع، وذلك يمنع القياس، إلا أن يقول: أخذته من جهة كونه كفرًا، لا من جهة [كونه تمجسًا. وإذا سلك المستدل هذا المسلك، بطل','3','756',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2388,'عليه] ذلك بصحة نكاح الحرة الكتابية، فإن التمجس إذا أثر في الأصل، من جهة كونه كفرًا مجردًا، لا من جهة كونه تمجسًا، وجب أن يؤثر كذلك في الفرع، فلا وجه لذكر هذه الزيادة بحال.
قال الإمام: (ولو كانت العلة تنقض لو قدر حذف الزيادة) إلى قوله (فهذا تمام القول فيما أردناه). قال الشيخ: هذا الذي ذكره، ليس هو مما نحن فيه،','3','757',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2389,'فإن تلك المسألة الحكم في الأصل مضاف إلى علة، وقد وجدت في الفرع، وتخلف الحكم. وههنا علة الأصل، لم توجد في الفرع، فهي عكسها. وإنما قصد الإمام بما ذكره، أن العلة المنقوضة قيدت بقيد يدرأ النقض، وفي هذا المكان ضم إلى العلة وصف حتى يوجد في الفرع. فمن هذه الجهة، اشتبهت المسألتان، والفقه يقتضى حذفهما جميعًا، والإعراض عنهما، ثم يحكم بعد ذلك بما يصح. والكلام على الزيادة [الدارئة] للنقض، قد تقدم مستقصًى، فلا حاجة إلى إعادته.
قال الإمام: (وعلينا الآن [فضل] كلامٍ في [فصل] الأصحاب) إلى قوله (فوضح أن تقسيم الكلام إلى الأصل والوصف، لا حاصل له). قال الشيخ: أما تقسيم عدم التأثير إل الأصل والوصف، فواضح، أما عدم التأثير في الوصف، فالمراد به: أن العلة توصف بكونها مطردة منعكسة، فيكون انعكاسها وصفًا لها. [فإذا] كان عكسها يقتضي نفي الحكم عند من يراه، فإذا لم','3','758',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2390,'تنعكس، لم [يظهر] أثر وصفها، وهو الانعكاس.
وأما عدم التأثير في الأصل، فأرادوا به أمرًا، وأراد الإمام غيره. أرادوا به: أن أصل الوصف لا أثر له، أي لوجوده، إذ لحكم مضاف إلى غيره. ولذلك يستقل معنى الأصل دونه، وهو بمثابة انضمام الرق إلى التمجس. وأراد الإمام بذلك: أنه لا أثر له في الأصل الذي منه الاستنباط. والأمر في ذلك قريب، فإن حاصله راجع إلى أن حكم الأصل غير مضاف إليه، لا على جهة الاستقلال، (١٣٥/ أ) ولا على جهة الاشتراك.
وقوله: والذي نراه أن القسمين يرجعان إلى الأصل، أي إلى الأصل الذي منه الاستنباط. فإن فرض تعدد معنى الأصل، لم يلزم من عدم معنًى عدم الحكم، فإن فرض اتحاد المعنى، جر ذلك الانعكاس.','3','759',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2391,'وقوله: فوضح أن تقسيم القول إلى الأصل والوصف، لا حاصل له، ليس الأمر على ما يقول في هذا المكان، وقد بينا صحة الانقسام، وما [أرادوه] بالفصل بين الوصف والأصل، هو كلام حسن.
قال الإمام: (ونحن الآن نسرد في بسط المقالات كلامًا) إلى قوله (فهذا بيان (١٠٤/ أ) المقالات في العكس). قال الشيخ: أما [المصير] إلى اشتراط الانعكاس جملة، فهذا رديء مزيف، وكيف يشترط الانعكاس جملة؟ وقد بينا أن الطرد لا يشترط في جميع الصور، وحققنا أن الناس لا يختلفون فيما إذا كان القاطع للطرد من المستثنيات. فإذا كان النقض على هذا الوجه لا يضر، وهو وارد على نص العلة، فماذا يقال في تخلف العكس على الوجه المذكور؟ نعم، إذا فقد المعنى، ولم يخلف غيره، ولم يوجد توقيف يتضمن استمرار الحكم، فمن المحال بقاؤه غير مستند لشيء.','3','760',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2392,'وأما من قال إن العكس غير لازم على الإطلاق، إن أراد أنه لا يقتضي نفي الحكم، فكلام صحيح. وإن أراد أن الحكم يستمر مع فقدان المعنى والتوقيف، فكلام باطل.
وقوله في التعليل: لأن إشعار النفي بالنفي [في المقصود] طرد خفي. كلام ملتبس، فإن أراد بالطرد ما لا يناسب، فكيف [يكون] إشعاره خفيًا؟ وإن أراد أنه لا يستقل، وهو الظاهر من قوله، فلا يلزم من حصول ما لا يستقل بالنفي نفي الحكم. وهذا هو اللائق مذهب الإمام، حيث يرى أن لفقدان كل علة من العلل أثر في نفي الحكم، يضاهي أثر الوصف الواحد [في] العلة المركبة من أوصافٍ في جلب الحكم. والذي اخترناه خلاف ذلك، وهو أن نفي المعنى لا يناسب نفي [الحكم بحال.
وأما الفصل بين لزومه في الدين دون الجدل، فلابد منه، فإنه يجب على] المجتهد البحث عن نفي الحكم، كما يجب عليه البحث عن ثبوته. وإذا كان كذلك، لزم البحث عند انتفاء المعنى، هل للحكم سبب آخر يرتبط به أم لا؟ لأن عدم المعنى، يقتضي نفي الحكم. وأما المناظر، فلا يلزمه هذا البحث، وإنما عليه البحث عما يقتضي الحكم الذي فرض الاستدلال عليه، وقد استتب له ذلك بتقرير المعنى وتحقيقه.','3','761',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2393,'قال الإمام: (وأما ما سموه عدم التأثير في الأصل) إلى قوله (وقد (١٣٥/ ب) نجز بنجازها القول في عدم التأثير). قال الشيخ: هذا الكلام واضح، لا يفتقر إلى مزيد، وقد تقدم بيانه، فلا معنى لإعادته.

قال الإمام: <span data-type="title" id=toc-431>(ومن الاعتراضات: فساد الوضع) </span>إلى آخر المسألة. قال الشيخ: سمى الجدليون القسم الأول فساد الوضع، وسموا الثاني فساد','3','762',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2394,'الاعتبار، والخطب في ذلك يسير، والأمر فيه قريب. أما إذا كان القياس على خلاف النصوص المتواترة، فلا خفاء في فساد وضعه. وأما إذا كان [على خلاف] خبر الواحد، [فهذا موضع خلاف، وقد تقدم الكلام على ذلك في أبواب تخصيص العموم بالقياس وخبر الواحد]، وبينا أن المسألة في [تقديم] خبر الواحد على القياس ليس مقطوعًا، وإنما هو في محل الظن.','3','763',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2395,'وهل يقضى عل القياس بكونه فاسد الوضع، سواء كان على خلاف الخبر أو وفقه؟ هذا مما تنوزع فيه.
قال بعض الأصوليين: متى كان في المسألة توقيف، فلا سبيل إلى القياس بحال، إذ القياس إنما يطلب به حكم ما ليس منطوقا به. أما إذا كان الحكم منطوقا به، فكيف يطلب حكمه بالقياس؟ فعلى هذا يكون القياس فاسد الوضع، متى كان في المسألة توقيف موافق أو مخالف.
والذي عليه الأكثرون أن وجدان التوقيف، لا يمنع من القياس الموافق، ولهذا يتمسك في المسألة (١٠٤/ ب) الواحدة بالنص والإجماع والقياس. نعم، يمتنع أن يكون فرع الأصل منصوصًا على حكمه وحكم أصله بنص واحد، إذ ليس أحدهما بأن يجعل أصلا، بأولى من عكسه. هذا تمام الكلام في فساد الوضع.','3','764',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2396,'قال الإمام: <span data-type="title" id=toc-432>(مسألة: إذا اعتبر القائس القصاص) </span>إلى آخرها. قال الشيخ: الذي ذكره الحذاق ينزل على صورة، والذي ذكره الإمام ينزل على أخرى. الذي ذكره الحذاق مفروض فيما إذا تباينت القواعد، وتفاوتت تفاريعها في نظر [الشرع]، فإنه إذا ظهر لك، امتنع أن يستعمل [معنى] إحدى','4','5',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2397,'القاعدتين في الأخرى. هذا بمثابة إخراج القيم في الزكوات، فإنه يمتنع عندنا، وإن كان المعنى لائحًا. وسبب ذلك أن المعنى فيما لم يتمحض اعتباره، لما خامر المعنى من شوب التعبد، وإلا فالقيمة للفقراء أرفق من الشاه. ومع ذلك قضينا بمنع الإجزاء، وإن كان المعنى لائحًا. وكذلك قلنا لا بد من رعاية عدد','4','6',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2398,'المساكين في الكفارات، ومنعنا من الصرف إلى مسكين واحد في أيام، وإن كان الشرع يقصد سد خُلته كل يوم، ولكن رأينا في الباب تعبدات، فقلنا: يمكن (١٣٦/ أ) أن يكون [العدد] من جملتها. فإذا [بنيت] القواعد على المخالفة، تعذر اعتبار معنى قاعدة في أخرى. ولذلك [يمنع] من اعتبار غرر الصداق بغرر الثمن، لكون الصداق بُني على نوع من المكارمة، فلا سبيل إلى اعتباره بالثمن بحال. هذا هو الذي [أراده] الحذاق.
وقصد الإمام ما إذا افترقت القواعد من وجه، [وتقاربت] من وجه آخر، فإن القياس يستعمل في جهة [التقارب] دون التباعد. وإذا اتضح ذلك،','4','7',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2399,'فقوله صحيح، ولا يمكن فيه نزاع. وذلك بمثابة ما إذا بيعت الأموال الربوية بعضها ببعض، فإنه يشترط فيها من الشروط ما لا يشترط في غيرها، ولكن مقتضى القواعد في [بيع] الأغرار سواء، فيجوز أن يفسد الصرف، [إذا] اشتمل على عوض مجهول، اعتبارًا بالبيع. فهذا هو [الذي] قصده الإمام. فإذا رُدت المذاهب المطلقة إلى التفصيل، لم يبق للخلاف تحصيل.

قال الإمام: (و<span data-type="title" id=toc-433>من الاعتراضات الصحيحة: القلب) </span>إلى قوله (وإنما هو مسلك [بديع] جدًا، لا يعهد له نظير). قال الشيخ: أورد مسح الرأس','4','8',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2400,'مثالًا لتصور معنى القلب، وقصد المورد بإيراده، أن الحكم الذي نفاه أحدهما، لم يتعرض الآخر لإثباته، والذي [أثبته] الثاني، لم يتعرض الأول [لنفيه]، فكأن كل واحد منهما فرض الكلام في طرف من أطراف المسألة، لا يتعرض للحكم الذي صرح به خصمه. هذا وجه من [أبطل] القلب، فصرف الإمام','4','9',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2401,'[عنايته] إلى الاعتراض على المثال، فقال: إن القياس من الجانبين طرد، ثم أنه عبر عن ذلك ببيان أنه لا مناسبة بين لفظ العضو، وبين التقدير ولا نفيه. ولما قيل له: لعله من أبواب الشبه، قال إنها أيضًا لم تشابه، لا في [قدر] الفرض، ولا في كيفية [تأدية] الفرض، نظرًا إلى المسح والغسل، وتفاوت','4','10',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2402,'المحال. ولما قيل له: إنها اشتبهت في عدم (١٠٥/ أ) التقدير بالربع، جعل ذلك أمرًا وقفيًّا، أي [غير] مقصود إليه بالإثبات.
وهذا الكلام ضعيف، فإنه يصح، وإن تفاوتت مقاديرها، أن يكون الشارع قصد إلى نفي تقديرها بالربع. وإبطال الشبه في هذه الجهة الخاصة، بناء على تفاوتها في غيرها، لا يتضح. ثم نقل الكلام إلى بيان جواز الاكتفاء بأقل','4','11',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2403,'[مما] ينطلق عليه الاسم، على ما يراه الشافعي، فقال: ورد ذكر الرأس [محلًا] للمسح، وثبت بالسنة المأثورة أن الاستيعاب غير واجب، إلى آخره. يريد الإمام بهذا: (١٣٦/ ب) أن ظاهر القرآن لا يرشد إلى الاستيعاب، ولا للاكتفاء، ولا لتقدير، وإنما سيق لبيان المحل خاصة. وهذا الذي قاله غير صحيح عندنا، بل لو تُركنا وظاهر القرآن، لاقتضى الاستيعاب، فإن من مَسَحَ بجميع رأسه، يُعد ماسحًا برأسه حقيقة. ومن مسح ببعض رأسه، لا يُسمى ماسحًا إلا مجازًا. والذي يدل على ذلك، إطباق الجميع على وجوب','4','12',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2404,'استيعاب الوجه في التيمم، وكذلك المسح على الخفين. فلا فرق في اللغة بين أن يقال: امسح رأسك أو اغسله. فإن ذلك لا يقتضي اقتصارًا على البعض، ولا بد من التعميم. هذا هو الظاهر، إلا أن يدل دليل على جواز الاكتفاء بالبعض.
وأما قوله: وثبت بالسنة المأثورة أن الاستيعاب غير واجب، فليس كما قال، بل الذين وصفوا وضوء رسول الله - صلى الله عليه وسلم - وبينوه في إقامة الواجب، كلهم','4','13',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2405,'يذكر أنه مسح رأسه، وبينوا أنه: "أقبل بيديه وأدبر، بدءًا من مقدم رأسه، ثم ذهب بهما إلى قفاه، ثم ردهما إلى المكان الذي منه بدأ". ومنهم من أطلق ذلك، ولم يفصله هذا التفصيل. والمطلق محمول على المقيد، لا سيما إذا اتحدت الواقعة. والذي نقل في ذلك: "أن النبي - صلى الله عليه وسلم - مسح بناصيته وعلى عمامته". فإذا لم يثبت بالسنة جواز الاقتصار على البعض، وقد دل الظاهر على الاستيعاب، فلا وجه لتركه من غير برهان.
قال الإمام: (فإن قيل: فلو قُدِّر التعليل الذي ذكرتموه مثلًا، مخيلًا مناسبًا) إلى قوله (فهذا وجهٌ لائح من طريق الجدل). قال الشيخ: ما ذكره الإمام من هذا الكلام، [لا] أدري ما مراده به؟ ولست أعرف وجهه،','4','14',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2406,'وذلك أنه منع من أن يكون القلب والعلة مناسبين، واحتج بأنه لا يتصور أن يشهد الأصل الواحد لنقيضين من طريق الإخالة. وهذا تخصيص عجيب، وكيف يتصور أن ينكر ذلك لأجل المناسبة، وقد صح [اعتزاء] العلة وقلبها للأصل الواحد؟ فلو كان الحكمان متناقضين، لما تصور رجوعهما إلى أصل واحد. وإذا صح ثبوت الحكمين في الأصل الواحد، فما الذي [يمنع] من استنادهما جميعًا للمناسبة؟ لا وجه لمنع ذلك بحال. فإنه كما يستحيل أن يناسب النقضان حكمًا من جهة واحدة، فكذلك يستحيل رجوع الحكمين المتناقضين لأصل واحد ويستقران فيه.
وأول دليل على فساد هذا الكلام، أنه جوز أن تكون العلة وقلبها شبهين، ولم يمنع ذلك، ولم يره تناقضًا. فلئن كانت المعاني المناسبة في الباب مستحيلة، (١٣٧/ أ) فلتستحل الأشباه، (١٠٥/ ب) لأنها قوالب المعاني عندنا،','4','15',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2407,'وهي لا تقبل النقيضين عنده. وبهذا فرق [هو] بين الطرد والشبه. فإن الطرد لا يناسب الحكم، ولا يشعر به. فلو فرض ربط نقيض الحكم به، لم يترجح- قبل البحث عن القوادح- النفي على الإثبات، وليس الشبه كذلك بحال.
وإذا صح في العلة والقلب أن يكونا شبهين، صح أن يكونا معنيين، ولا تناقض بين حكم العلة وحكم قلبها، إذ هما جميعًا ثابتان في الأصل. ولو كانا متناقضين، لم يثبتا فيه. هذا هو التحقيق في ذلك.
نعم، لا يجتمع في الأصل حكم [المستدل] الذي قصده، فإنه لما نفى حكم [الأصل]، حاول بذلك إثبات حكمه، بناء على أن القضية منحصرة','4','16',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2408,'فيما بينهما. [فإذا] نفى ما أثبته [الخصم]، لزم ضمنًا إثبات ما يدعيه هو، وخصمه ينفيه، فلزم القلب من هذه الجهة، لأنهما تناقضا ضمنًا، وإن لم يتناقضا صريحًا. هذا وجه لزومه عند من يراه اعتراضًا.
قال الإمام: (فإن قال السائل: اتباع المقاصد أولى) إلى قوله (فهذا [منتهى] كلام الجدليين وأصحاب المعاني من الأصوليين). قال الشيخ:','4','17',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2409,'قد ثبت أن الكلام إن حمل على ما وقع التصريح به، فليس القالب معترضًا على ما استدل عليه المستدل، ولا شك أنه لو أضرب عنه بالكلية، لم يكن ما جاء به اعتراضًا، ولو ناقضه في [عين] ما دل عليه، [كانت] معارضة. وسيأتي الكلام عليها. ولا شك أن القالب إنما أتى بالقلب، لما فهم أن مقصد المعلل [بالاستدلال] على ما نفاه، إثبات ما يدعيه، فكأنه استعمل فيه برهان الخلف. وإذا فهم هذا المقصود، فهو مقطوع به.
وبيانه: لما سئل عن مسح الرأس، فأفتى بأنه يكتفي فيه بأقل [ما] ينطلق عليه الاسم، واستدل بأنه لا يتقدر فرضه بالربع، وقُبل منه ذلك. فلولا أنه رأى الحصر في المذهبين، وأنه إذا [بطل] أحدهما تعيَّن الثاني، لَما كان مستدلًا على الحكم الذي ذكره بحال، ولكان هذا الكلام مائلًا عن المسألة ميلًا كثيرًا. فقبول مثل هذا الاستدلال، يدل على الالتفات إلى الإثبات الضمني. فإذا قلب القالب، وتعرض بالتصريح إلى نفي ما أراد المستدل إثباته ضمنًا، كان ذلك بالغًا في القوة، ولا يضره في ذلك تصوره بصورة المستدلين، وليس','4','18',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2410,'الغرض الاستدلال والبناء، وإنما الغرض الهدم والإبطال.
قال الإمام: (ولي بعد المسلكين نظر آخر، وهو مختاري) إلى قوله (في [توجه] الاعتراض، إذا لم يكن الكلام في وضعه قادحًا). قال الشيخ (١٣٧/ ب): قول الإمام: إذا كان مضمون القلب متعرضًا لطرف لا يناسب مضمون العلة من طريق المعنى، يريد بذلك أن قول القائل: لا يتقدر [بالربع]، لا يلزمه منه أن يكتفي بما ينطلق عليه الاسم، فنقول في مثل هذا القلب: إنه غير مفيد، إذ لا يعترض على الحكم الذي أتى به المستدل، فيكون هذا قلبًا مائلًا عن مقصود المسألة.
ومعنى قوله: ولكن اتفق مذهب الخصمين على طرفين في النفي والإثبات، يعني بذلك أن الشافعي ذهب (١٠٦/ أ) إلى الاكتفاء ونفي التقدير، وأبو حنيفة ذهب إلى عكس ذلك.
وقوله: ولكن القائل قائلان: أحدهما يثبت أمرًا فيهما، ظاهره يخالف','4','19',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2411,'الأمر الثابت. إذ الشافعي يثبت أحدهما [وينفي الآخر]، وأبو حنيفة كذلك أيضًا. فليس واحد منهما يثبت الحكمين والآخر ينفيهما، ولكن مراده بذلك: أن أحدهما قال: يكتفى بالاسم، ولا يتقيد بالربع، فجعل ذلك إثباتًا لحكمين، والآخر عكس ذلك. هذا مراده هنا.
وقوله: وهذا يقوى جدًا، إذا صح مذهب معتبر [غيرهما]، والأمر كذلك في مسح الرأس، فإن مالكًا أثبت الاستيعاب. فلو كان ينشأ من المذهبين فقه يناقض المذهب الثالث، ويوجب حصر الأمر في المذهبين، لكان فقه المذهبين يقتضي الرد على مالك. ولمالك أن يقول بموجب القياسين، فلا يتقدر الفرض بالربع، ولا يكتفى بأقل ما ينطلق عليه الاسم، بل يجب الاستيعاب. ولمًّا صح ذلك أن الفقه لا يقتضي حصر الحكم في المذهبين،','4','20',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2412,'ولكنا [قد] قدمنا أن الاستدلال إنما يصح بناء من الخصمين على أن الحق مقصور عندهما على القولين. فمن هذه الجهة يصح القلب اعتراضًا من غير تفصيل.
وأما قوله: وهذا [يقوي كون] القلب ليس اعتراضًا، [إذا كان في المسألة] مذهب ثالث معبر. ومسألة مسح الرأس كذلك. أما إذا لم يكن في المسألة إلا قولان، فإن فرض مع ذلك إجماع عليهما، فلا بد بالضرورة من لزوم القلب، فإنه إن صح أحد الكلامين، بطل الآخر قطعًا، فلا يتصور على هذا التقدير أن لا يكون القلب اعتراضًا. وإن كان في المسألة مذهبان، ولم ينعقد على ذلك إجماع، [ولم] يصادف [خلاف]، فالصحيح أنه اعتراض في الجدل، من جهة اتفاق الخصمين على نفي الحكم الثالث، فينزل اتفاقهما منزلة الإجماع.','4','21',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2413,'قال الإمام: (فأما إذا (١٣٨/ أ) كان في القلب تعرض من طريق المعنى) إلى قوله (كان ذلك قدحًا بينًا). قال الشيخ: معنى قوله: إذا كان في القلب تعرض لمناقضة علة المعلل أصلًا، يعني أن القالب صرح بنقيض ما قصد المعلل لإثباته ضمنًا أو سياقًا [أو ترعيضًا]، يشير به إلى القسم الثاني، وهو','4','22',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2414,'قوله: فلا يقع قربة بنفسه. [وسياق] كلامه وتعريضه يقتضي أن لا بد من الصوم، ولكنه لم يتمكن من التصريح به، فأبهم وأثبت طرفًا من المذهب. هذا هو [المفهوم] من السياق والتعريض.
قال الإمام: (وفي القلب [سر] يجب التنبيه له) إلى قوله (فهذا منتهى الكلام في القلب المصرح به). قال الشيخ: إذا لم يتمكن المستدل من التصريح بمذهبه، وأمكن القالب نفي حكمه تصريحًا، فلا خفاء بكونه اعتراضًا','4','23',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2415,'قادحًا، [ولا يتصور] (١٠٦/ ب) عندي في ذلك خلاف.
ومثاله: إذا قال الحنفي في الاعتكاف: مكث في مكان مخصوص، فلا يقع قربة بنفسه، كالوقوف بعرفة. ومقصوده اشتراط الصوم، ولكنه لو صرح به، لم يجد أصلًا، إذ الوقوف بعرفة لا يشترط فيه صوم.
فإذا قال الشافعي: فلا يشترط في وقوعه قربة صوم، كالوقوف بعرفة. فكيف يصح أن يقال: هذا لا يقدح فيما قصد المعلل إثباته؟ إلا إذا بني الأمر على قول من يرى أن السائل ممنوع من الإتيان بما يصح أن يكون دليلًا.
والكلام في توجه القلب مبني على غير هذا الأصل. أما إذا فرض الكلام على هذا الأصل، امتنع القلب والمعارضة جميعًا. والفرق على بعض المذاهب، وصحة استدلال أبي حنيفة في المسألة ينبني على حصر الأمر في المذهبين، فإنه لا ذاهب يذهب إلى اشتراط غير الصوم، فصار قوله: فلا يصير','4','24',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2416,'قربة بنفسه، يشير إلى اشتراط الزيادة، وهي متعينة بإجماع الخصمين، [لا أن] الدليل اقتضى تعينها. وإذا كان التعليل مبهمًا، ووقع القلب مصرحًا به، كان بالغًا في القوة.
قال الإمام: (وأما القلب المبهم) إلى قوله (هذا اعترافه بتعارض العلتين). قال الشيخ: الذي ذكره القاضي صحيح، والقلب المبهم لا','4','25',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2417,'يقدح، [لأن] المستدل قد سبق إلى تعيين حكمه والدليل عليه، وما ذكره القالب لا سبيل إلى أن يناقض المعلل في غير الحكم الذي استدل عليه، إذ [أصلهما] واحد. وقد بينا ذلك فيما سبق.
ومعنى قول (١٣٨/ ب) القاضي: إن هذا القالب ينقلب عليه قلبه، فلو كان يقدح، لوجب أن يبطل من حيث يقدح، إذا أمكن قلبه، كلام حسن.
ومعنى قوله: ينقلب عليه، يقول في المثال: فلا يختص بزيادة هو ركوع، وإن كان [دليلك] يقتضي زيادة، فلا يقدح ذلك في استدلالي، [لأنه قلب]، فلا يختص بزيادة هي ركوع، [فإذا] تمكن من التصريح بما أراد','4','26',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2418,'والدلالة عليه، ولم [يستطع] القالب أن يناقضه في عين ما استدل عليه، لم يكن كلامًا قادحًا.
والذي حمل الإمام الأمر عليه من أن المستدل يعد بدليله في معرض القلب مطلقًا، ليس الأمر على ما تخيله، ولو كان كذلك، لوجب رد القلب مطلقًا، مصرحة ومبهمة، والقاضي لا يقول بذلك، فلو كان الأمر على ما فهمه الإمام، لكان [اعتراضه] متوجهًا من غير إشكال، وللزم أيضًا فساد المعارضة، إذ يتمكن المستدل من إعادة دليله في معرض المعارضة. وهذا لا يتخيله لبيب، وإنما الأمر على حسب ما يقتضيه كلام القالب. [هذا] تقرير هذا الاعتراض والجواب.
قال الإمام: (ومما تمسك به القاضي أن قال: المصرح مرجح على المبهم) إلى قوله (وفي المصير إلى هذا قبول القلب والنظر إلى الترجيح). قال الشيخ: الظاهر عندي ما ذكره القاضي، وذلك أن المبهم لم يقدر على التصريح بمراده، لعدم (١٠٧/ أ) شهادة الأصل [له]، وهو شاهد لحكم','4','27',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2419,'المستدل من أخص جهاته، وفرق بين أن يشهد الأصل لحكم من جهة عامة، وبين أن يشهد له من جهة خاصة، فإن الشهادة من الجهة الخاصة مرجحة. وبهذا الاعتبار، إذا تعارض شبه خاص وعام، كان الخاص مرجحًا، فكذلك هذا.
وأما قول الإمام: وفي هذا قبول القلب والمصير إلى الترجيح، فليس بشيء، لأن هذا إنما يكون على تقدير أن يكون كل واحد من المتعارضين يصح أن يكون في حالة ما على معارضة. فها هنا لا يصح القول بتقديم أحدهما [مطلقًا. أما إن كان أحدهما] يترجح على الإطلاق، فلا معنى لإطلاق القول بقبول المرجوح على كل حال. بل لا يقال إلا أنه مردود. وهذا بمثابة حكمنا بأن خبر الواحد النص، مقدم على القياس المظنون، فإنا لا نقول [نُعمل] القياس في معارضة الخبر، ويرد الأمر إلى الترجيح. وكذلك القول في كل دليلين يتقدم أحدهما على الآخر على كل حال. فلا وجه إلا إطلاق القول برد المرجوح في مقابلة الراجح (١٣٩/ أ).','4','28',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2420,'قال الإمام: (ومما تمسك به القاضي أيضًا أن قال: المبهم قاصر النظر) إلى قوله (فقد ظهر غرض [القالب]). قال الشيخ: هذا الذي ذكره القاضي ها هنا فيه نظر، وذلك أن القالب لم يساعده دليله على التصريح بمقصوده، إذ لو صرح بمقصوده في المثال بعينه، وقال: فيفتقر إلى الصوم، لما أبهم.
ومعنى قصور نظره: أن دليله لا يُفضي إلى عين [مطلوبه] من طلب زيادة مخصوصة. [فهذا] معنى قصور النظر، بالإضافة إلى حصول المقصود. نعم، هذا قد يرد اعتراضًا على ما فهم من غرض المستدل، إذ غرضه أن يقع اللبث بمجرده قربة. وهذا ليس هو الذي استدل عليه المستدل، وإنما استدل','4','29',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2421,'على بطلان اشتراط الصوم، وقد تم نظره، وأفضى به إلى ذلك، فخصمه تقاعد دليله عن الإفضاء إلى رده، فنظره قاصر بهذا الاعتبار، وهو تام بالنظر إلى الأمر الضمني المفهوم من كلام المستدل. فرده القاضي، نظرًا إلى ما وقع التصريح به، وقبله الإمام بالنظر إلى الغرض الضمني الذي قصده المستدل، والأول أجرى على ذوق الأدلة، والثاني أقرب إلى مقصد المستدل.
قال الإمام: (فإن قيل: فما [المرتضى] عندكم) إلى قوله (فهذا قسم من الإبهام في [القلب]). قال الشيخ: هذا الذي ذكره الإمام ها هنا [هو] من جنس ما تقدم، ومصيره إلى استحالة القلب في المعاني من الجانبين، لا يصح له عندي وجه.','4','30',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2422,'وذهابه إلى أن ذلك يفضي إلى تناقض، يرد عليه وجوب الرد إلى الأصل الواحد، إذ أصل المعلل والقالب واحد، فلو تحقق [التناقض] صريحًا أو ضمنًا، لبطل [الرد] إلى الأصل الواحد. وكذلك اعترافه بصحة وقوع العلة وقلبها شبهين، يمنع التناقض أيضًا. وقد قدمنا الكلام على ذلك على أبلغ وجه.
وأما كونه كلف المستدل الجواب عن القلب المبهم، (١٠٧/ ب) فذلك التفات منه إلى الأمر الضمني المقصود للمستدل. وأما بالإضافة إلى ما وقع الاستدلال عليه، [فالقلب] المبهم لا يصرح بالمناقضة فيه.
قال الإمام" ([فأما] القسم الثاني، وهو [قسم] قلب التسوية) إلى','4','31',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2423,'قوله (وقد نجز القول في القلب). قال الشيخ: [قلب] التسوية لا بد فيه من الإبهام، فمن رد القلب المبهم، فلا بد له من رد التسوية، إذ القالب في التسوية يبتغي ضد ما قصده المعلل. وإذا قصد ضد حكمه (١٣٩/ ب)، أمكن أن يشهد لذلك أصله، فلزم الإبهام، إذ الحنفي يقصد بقوله: إنه كالمختار، رأي في لزوم الطلاق، والشافعي يقصد بقوله: فيستوي إقراره وإنشاؤه كالمختار، أي كما لزم في حق المختار الإقرار والإنشاء، أي فكذلك يستوي في المكره الإقرار والإنشاء.
وقد اتفقوا على أن إقراره لا يلزم، فيقول الشافعي: فكذلك [إنشاؤه]، فينبغي أن لا يلزم. ولو صرح بأن لا يلزم إنشاؤه، لم يشهد الأصل له، فأبهم','4','32',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2424,'وأثبت طرفًا من المذهب. وهذا لعمري قد يظهر [منه] فقه [يقتضي] التسوية بين الإقرار والإنشاء، من حيث الجملة. فإن أفضى ذلك إلى اختلاف في خصوصيات الأحكام، فذلك غير قادح، ولذلك إنما نقيس المطلقة في مرض الموت في استحقاق الميراث، قياسًا على القاتل [في المرض] الذي حُرم الميراث. وهذا في ظاهره تناقض بين، إذ كيف يقاس الإعطاء على المنع؟ ولكن وجه القياس الإعراض عن الخصوصية في الموضعين، ورد الأمر إلى وجه أعم من ذلك، وهو المقابلة بنقيض المقصود. فقوبل الوارث بنقيض مقصوده، [إذ مقصوده] استعجال الميراث، وقوبل الزوج بنقيض مقصوده، حتى ورثت الزوجة.
والفقه فيه أنه إذا لم يحصل له ما قصده، لم يتعاط السبب الذي منع من','4','33',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2425,'تعاطيه، إذ يكون إقدامه عليه [لا] لغرض يحصل له منه، حتى يتجاوز ذلك.
ويقول: بلغ برأس المال أقصى مراتب الأعيان، فليبلغ بالمسلم فيه أقصى مراتب الديون، قياسًا لأحد العوضين على الآخر، والفرع المسلم فيه، ووجوب الأجل فيه، والأصل رأس المال الذي اشترط فيه الانتقاد. وهذا ظاهر التناقض.
وقد امتنع طوائف من الأصوليين من قبوله، بناء على ما ظهر من تناقض الحكمين. ونحن نعدل عن الجهة الخاصة، ونجعل الجمع من وجه آخر، ونقول: أحد ركني العقد، فوجب أن يشرع على وجه يزيل الحاجة، ويحصل المصلحة، قياسًا على الركن الآخر. ونقول: إن الحاجة تقتضي أن يكون رأس المال حالًا، وأن يكون المسلم فيه مؤجلًا، فإنه إذا كان رأس المال على غير الحلول، لم يحصل مقصد البائع المحتاج. ولو كان ما |أخذ السلم فيه [ياسرًا]','4','34',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2426,'عنده، لم يكن به حاجة أن يبيع ما في ذمته، مع [تيسر نفعه]، وهو يكون موسرًا به، [فاقتضت] الحاجة [حلول] أحد العوضين وتأجيل الثاني.
وعلى الجملة، لا يصح اعتبار الفرع بالأصل (١٠٨/ أ) في ضد حكمه، ولا في خلافه. بل لا يصح [اعتباره به] إلا في مثل حكمه. ويختلف ذلك (١٤٠/ أ) باختلاف مقصد الجامع من النظر إلى الجهة العامة أو الخاصة. ولكل مسألة ذوق ينظر فيه المجتهد. وبالله التوفيق.

قال الإمام: (و<span data-type="title" id=toc-436>من الاعتراضات: المعارضة) </span>إلى قوله (فالجواب عنها','4','35',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2427,'ينحصر في مسلكين). قال الشيخ: لا خلاف بين العلماء أن المعارضة في حق المجتهد، يجب عليه أن لا يعمل دليله قبل الانفصال عنها، وتحقيق دفعها. وإن ثبت رجحان الدليل عليها، [أمضى] الحكم به. وإن ترجحت، وجب المصير إليها. وإن استوى الأمر، فسيأتي ما على المجتهد في ذلك.
والذي نقله الأصوليون أنه [لا يجوز] للمجتهد إعمال دليله، حتى','4','36',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2428,'يبحث عن انتفاء المعارضات، وقالوا: إن ذلك مجمع عليه. وقد نقل بعض الناس خلافًا فيه، وأنه إذا ظهر له دليل، لزمه المصير إليه، من غير حاجة إلى بحث عما يعارضه. وهذا مذهب [مرذول]، وعلى قلة البصيرة محمول. ولست أراها مقالة معتدًا بها. وقد تقدم كلامنا على ذلك. وسيعود إن شاء الله في أبواب الترجيح.','4','37',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2429,'وأما المعارضة في رسم الجدل، فمختلف فيها، والصواب القبول. وصورة الدليل ليست ممنوعة لعينها، وإنما منعت، حذرًا من ذهاب كل واحد من [المناظرين] في صوب، لا يجتمع [معه الآخر فيه].
فأما إذا كانت المعارضة مقصود موردها [إيقاف] الدليل، حتى يبين الانفصال عنها، وترجيح الدليل [عليها]، فلا معاب في ذلك، ولا منع منه.','4','38',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2430,'وما ذكره الإمام من قبول التأويل [من السائل]، كلام حسن، وتقسيم مستقيم، والضرورة تلجئ إلى تمكينه من الدليل العاضد للتأويل، المزيل للظهور، وإن كان فيه [صورة] الإتيان بالدليل، ولكنه لم يقصد به ذلك،','4','39',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2431,'وإنما قصد الهدم، فليكن الأمر كذلك في قبول المعارضة. ولعمري إن [معظم] كلام الفقهاء في مسائل متعلق بفنون المعارضات، والتزام طرق الترجيحات، فلتقبل المعارضة.
قال الإمام: (والجواب عنها ينحصر في مسلكين) إلى قوله (إذ غرضه الإفساد المحض لا البناء). قال الشيخ: أما الجواب عن المعارضة، فكما ذكره الإمام، إذ هي في صورة الدليل، وإفسادها يكون بما تفسد به الأقيسة. وقد ذكرنا منها جملًا في الاعتراضات الصحيحة، فلنسلك مثل هذا في المعارضة، وإن تعذرت طرق الإفساد، فلنسلك مسلك الترجيح.
وأما قول الإمام: إن العلتين إذا استوتا فسدتا، فكلام مؤول، ومراده به: أنه لا يمكن اعتماد واحدة (١٤٠/ ب) منهما على التعيين دون صاحبتهما، ولكن هل يتمكن المكلف من التخير بينهما، أو يجب [التوقف]؟ هذا موضع','4','40',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2432,'فيه خلاف، والقاضي [يرى] التخيير، وغيره يوجب الوقف [والاحتياط]. فهذا من ذلك الفن.
وإن أراد أنهما تفسدان مطلقًا، بحيث لا يجوز استناد [الحكم] إلى كل واحدة منهما، فذلك غير (١٠٨/ ب) صحيح، ففرق بين أن لا يترتب الحكم على الوصف لخلله، وبين أن يمنع مانع من ترتبه عليه، فإن امتنع للوجه الأول، فهو الفاسد، وإن امتنع لأجل المعارضة اقتصر الامتناع على محل المعارضة، وهذا بمثابة الشهادة، إذا لم يحكم بها [لخلل]، فإنها مردودة على الإطلاق، وإن تعارضت البينتان، لم يقتض ذلك ترجيح إحداهما، فيعتمد كل واحدة على تقدير انفرادها، فكذلك العلتان المتعارضتان، تعتمد كل واحدة إذا كانت منفردة.
وأما ما تخيله السائل من أن عدم الترجيح، يمنع الاعتماد على ما جاء به السائل، وباب الترجيح لا يمكن منه، فلتبطل المعارضة. [قيل: ] هذا','4','41',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2433,'[أولًا] دليل على صحة المعارضة، من جهة أن غرض المعارض الإيقاف لا البناء والإثبات.
قال الإمام: (ومما يتصل بأحكام المعارضة، أن المجيب) [إلى آخر المسألة]. قال الشيخ: إذا أبدى السائل في الأصل معنى غير معنى المعلل، [مقتصرًا] على ذلك، فهذا ينبني على ما تقدم من جواز تعليل الحكم بعلتين','4','42',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2434,'وامتناعه. وقد كنا قدمنا أن كل معنى شرط الاعتماد عليه، شهادة الأصل له، فإن السبر فيه لازم، وظهور معنى آخر مضر، فإذا أبدى السائل معنى غير ما أبداه المعلل، [فهذا] يقدح في ما عينه المعلل، إن لم يُجب عنه.
والسبب في كونه قادحًا، أنه يبين عدم السبر الحاصل، ويشكك في الشهادة للوصف الذي أبداه المستدل، فلا بد حينئذ من الجواب بالإفساد أو بالترجيح. أما كل معنى لا يفتقر إلى شهادة الأصل، إما بأن يكون ثبت كونه علة','4','43',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2435,'بالنص أو ما يضاهيه من الإيماء والتنبيه والملاءمة- على القول بقبول الاستدلال المرسل- فإذا تمسك المستدل بمثل هذا المعنى، وأبدى المعترض معنى آخر من الأصل الذي منه الاستنباط، فالذي أبداه المعرض، لا يقدح فيما عينه المستدل، فالقول في ذلك كالقول فيما تقدم من المعنى المستغني عن الشهادة، أو المفتقر إليها، فليراجع ذلك الكلام، فإن هذا مرتب عليه. (١٤١/ أ).

قال الإمام: <span data-type="title" id=toc-437>(مسألة: إذا قبلنا من السائل معارضة العلة بالعلة) </span>إلى قوله','4','44',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2436,'(يعود [في الفرق]، وحقائق القول فيه). قال الشيخ: لا شك أن هذا الاعتراض، إنما ينشأ على القول بامتناع تعليل الحكم بعلتين، وإذا قيل بمنع ذلك، فإذا أبدى المعترض معنى، وعضده بما يبين كونه علة، تحققت المعارضة، وافتقر إلى الإفساد أو الترجيح، وإن اقتصر [على ذكر] معنى أبداه، فهذا موضع خلاف.
والقائلون تمسكوا بأن شرط الاعتماد على المعنى، السبر وإفساد ما سواه. وإذا أبدى المعترض معنى، فهو يقصد من المستدل بيان [فساده]، أو','4','45',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2437,'ترجيح معناه عليه. وإذا لم يفعل المستدل ذلك، لم يكن وافيًا بحقيقة السبر. وأما الراد، فإنه يقول: المعارض في صورة من يأتي بما يقاوم ما جاء به المستدل، والمستدل قد سبق إلى تقرير معناه بالدليل، فلا يعارضه ما هو على صورة الدعوى.
وهذا القول هو أجرى على أصل الإمام، الذي يرى أنه متى صح معنى بالدليل، اقتضى ذلك فساد (١٠٩/ أ) كل ما سواه، فلا يفتقر المستدل مع هذا إلى بيان وجه آخر في الإفساد، إذ قد ظهر فساد ما ذكره، بإقامة الدليل على صحة غيره، وليس كذلك ما إذا عضد السائل ما جاء به من الدليل، فإن المراتب حينئذ تستوي، ولا يتبين الصحيح من الباطل إلا بالإفساد أو الترجيح.','4','46',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2438,'وقولنا على هذا الرأي بالترجيح، كلام ضعيف، إذ شرط الترجيح اجتماع شرائط الصحة في كل وصف لو كان منفردًا. وهذا لا يصح عند الإمام [بحال]، بل يقول لو صحت المعاني، لعلل بها. فكل ما ذكره الإمام في أبواب المعارضة من الترجيح، كلام متجوز به على أصله، لأن من معتقده أنه لا يتصور أن يصادف لحكم [واحد] معنيان، يصح كل واحد منهما على السبر لإسناد الحكم إليه، فلا طريق عند المعارضة إلا الإفساد خاصة.

قال الإمام: <span data-type="title" id=toc-438>(فصل- فأما الفرق، فقد ظهر اختلاف أرباب الجدل فيه)</span>، [إلى] [قوله: (حد] ما يعرفه الفقيه. قال الشيخ: أما الفرق، فكما قرره','4','47',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2439,'القاضي، وأكثر كلام الفقهاء في المسائل فرقًا وجمعًا، وأعظم الاعتراضات عند الفقهاء يتعلق بهما، وإنما كان الفرق هو أهم الأسئلة وأكثرها ورودًا على المجتهدين، أن الاعتراضات السابقة، قل أن يخفى أمرها على من بلغ مبلغ الاجتهاد، إذ من الاعتراضات النقض، والمعاني المنقوضة في الشريعة ظاهرة عند من أحاط بها، وكذلك القول في المنع، والقول بالموجب. وإنما الذي (١٤١/ ب) يكثر المعارضات، وأكثر منها الفرق، إذ لا يصح أن يكون كل','4','48',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2440,'وصف عند جميع المجتهدين على وتيرة واحدة، فيرى بعضهم الاعتماد على وصف، ويرى الآخرون الاعتماد على غيره، وكل واحد مستقل لو انفرد على حياله. فهذا هو السبب في كثرة المعارضات والفروق.
وأما قول الإمام: رُب فرقٍ يُلحق فقه الجامع بالطرد، ولولاه لكان ذلك الجامع مخيلًا، فهذا الفرق لا ينبغي أن يختلف في قبوله، فإنه عند النظر إليه، لا يبقى للجامع فقه بحال. فكيف يعتمد على الجامع مع غلبة ظن [الافتراق]؟ وهذا إذا تصور، لا سبيل إلى رده، ولا إلى [اعتماد] الجامع معه.','4','49',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2441,'ولكن ما مثل به، غير مسلم [له]، وليس يسلم أبو حنيفة أن الذي ذكره يبطل فقه الجامع، فإن الاعتماد على التراخي في نقل الأملاك من أجلى المناسبات، وثبوت التحريم في بعض العقود، لا يمنع انتقال الأملاك. وقد ضربنا لذلك أمثلة [من] وطء الأب جارية الابن، وكذلك تحريم النكاح [في الحيض]، [يترتب] عليه لحوق النسب، ووقوع الطلاق في الحيض يترتب عليه أحكامه. والعجب من الإمام الذي أنكر على الشافعي إلحاق أبواب الخيار والإجارة بالرخص، مستندًا إلى تمهيد التعبدات في العقود، وقال: اتباع الرضا','4','50',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2442,'من غير اقتحام ضروري أمس للقياس الكلي من أبواب الاستصلاح. فنرى ها هنا قد جعل العمدة على اتباع الرضا، فلا يكون [حيد] العقد [عن] مراسم الشرع [بالذي] يتضمن [أنه] لا يثمر (١٠٩/ ب) بحال، وهو المراد [بكونه] فاسدًا.
وأما قول الإمام: إن من خصائصه إمكان البوح به، لا على صيغة الفرق، بأن يقال: لا عبرة بالتراضي. مقصوده بذلك أن الفرق لما أبطل فقه الجامع، صار كالطرد عند النظر إلى الفارق. وعلى هذا قد يكون الوصف طرديًا نظرًا إليه، لا باعتبار غيره، وقد يكون طرديًّا عند وجود ما يخل بمناسبته، لظهور [أربى]','4','51',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2443,'المناسبة عليه، وفي انخرام المناسبة بجهة المعارضة كلام يطول، والصواب في ذلك أنه، إذا انتهت المناسبة في الخفاء إلى حد لا يكاد أن يقف الناظر عليه، لغلبه ما يعارضها، أنه لا التفات إليها، ولا يتنبه الذهن لها. وهذا بمثابة مشقة المضغ عند تناول المحتاج الطعام. ولو كلف ذلك من غير هذه الضرورة، لوجد لذلك مشقة، ولكن هذه المشقة مغمورة بمصلحة الطعام، فلا يدرك الآكل المشقة بحال.
وكذلك يرى الإمام أن بعض الفروق يلحق بالطرد، وإن كان لو جوَّد النظر إلى الجامع، لكان مناسبًا، فلا يجوز الاعتماد (١٤٢/ أ) على الجامع على هذا الوجه المذكور، ونحن نساعده على أنه لا يجوز الاعتماد عليه في هذه الصورة. أما المصير إلى التحاقه بالطرد [تحقيقًا)، فلا نقوله، والمقصود من تعطيله حاصل من غير تجشم هذه المشقة في [التصوير].','4','52',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2444,'قال الإمام: (ومما يقع مدانيًا لهذا) إلى قوله (فليفهم الفاهم ما يُلقى إليه من دقائق الكلام). قال الشيخ: هذا المثال أيضًا أضعف من الكلام الأول، وذلك أن [كلام الإمام] في هذا الموضع يتضمن أن الجامع، لو جوّد النظر إليه، لأفاد ربطًا، ولكنه يرى أن الفرق أخص منه، فهو مبطل ظن الجمع، بناء على التشبيه الحاصل بين الأصل والفرع، [المحرك] لظن الإلحاق. ويرى أن [للفرق] أثر في الافتراق من الجمع. والذي تقدم له قبل هذا أن قول أبي حنيفة: طهارة بالماء، [من باب الطرد، فإنه لو قال القائل: طهارة بالماء] فافتقرت إلى النية، لكان الكلام مقاومًا لقول من يقول: طهارة بالماء، فلا تفتقر','4','53',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2445,'إلى النية. وليس يظهر وجه حتى يقال نفي النية أليق بهذا اللفظ من إثباتها، فانظر كيف جعله هناك طردًا، نظرًا إلى نفس الوصف، [ورآه] في هذا المكان أنه إنما [بطل] وجه الجمع فيه، بالنظر إلى الفرق وخصوصيته؟
[فالصحيح] عندنا أن المسألة مجتهد فيها، والنظر في الجمع والفرق فيها على حسب ما يغلب على ظن كل مجتهد، والتعبدات غالبة في الوضوء، وإزالة النجاسة الغالب فيها الاعتماد على المعنى. ولقد اختلف قول مالك في افتقار الوضوء إلى النية، ولم يختلف قوله في أن إزالة النجاسة لا يفتقر إليها، لظهور المعنى فيها.
قال الإمام رحمة الله عليه: (ومما نجريه مثلًا أن المالكي إذا قال: الهبة عقد تمليك) إلى قوله (فلا شك في كونه اعتراضًا). قال الشيخ: ما ذكره','4','54',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2446,'الإمام في مسألة الهبة، وكون مالك رحمه الله ذهب إلى ترتيب الملك على الإيجاب والقبول، كلام صحيح، إذ المعتبر في نقل الملاك الرضا، وهو أمر خفي، يدل عليه اللفظ تارة، أو ما يقوم مقامه أخرى. فإذا وجد التصريح بنقل الأملاك، فأي حاجة إلى القبض فيه؟
وقول القائل: إنه اشترط [القبض] المؤذن بنهاية الرضا، كلام مجرد عن الدليل، مقتصر فيه على نفس الدعوى، وإذا دل الإيجاب والقبول على الرضا بنقل الملك، وجب (١١٠/ أ) انتقاله، [فأين] الفقه الذي ينشأ من [اشتراط] القبض الذي يخل بفقه الجامع أو يعارضه؟ فاشتراط القبض عندنا، لا وجه له في تحقيق الملك للموهوب.
يبقى على هذا الطريق (١٤٢/ ب) اعتراض، وهو أنه لو [طرأت] أعذار من موت أو دين قبل القبض، فإن مالكًا [رحمه الله] أسقط بذلك حق','4','55',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2447,'الموهوب له. [ولهذا] قد يعترض على القاعدة في القضاء بترتب الملك على الإيجاب والقبول.
وهل اشتراط القبض في عقود التبرعات من أبواب التعبدات، أو شطر لقطع التهم؟ عندنا في ذلك خلاف، قال مالك رحمه الله فيمن وهب شيئًا لابنته وهو مسافر، بحيث لا يمكنها الحيازة ثم مات: ثبت لها [ما] وهبه لها. بناء منه على أن شرط الحيازة، إنما كان، حذرًا من إظهار صورة الهبة والتمليك، ثم يبقى الشيء بيد واهبه. [فإن طرأت] أعذار، قال: هو [منه، وإن لم تطرأ أعذار، قال: هو] ملكي. وهذا بناه على أصله في مراعاة التهم، وصحة بناء الأحكام عليها في بعض المواضع.
وعلى الجملة، فاشتراط القبض عند قيام الأعذار، لا يمنع من الملك ناجزًا ظاهرًا. ولذلك أجمع مالك وأصحابه على توجه حق المطالبة للموهوب على الواهب بالإقباض، ويقضى عليه بذلك، إذا لم تكن أعذار، ولولا تعلق الحق به، لم يجبر الواهب على التسليم، نظرًا إلى رضاه بنقل ملكه إلى الموهوب له.
قال الإمام: (ثم الفرق والجمع إذا ازدحما على أصل) إلى قوله','4','56',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2448,'([ووجوب] الجواب عنه). قال الشيخ: هذا الكلام صحيح، واختيار الإمام في هذه المسألة هو الحق الصريح، وذلك أنا إنما نحكم على الفرع بحكم الأصل، إذا ثبت عندنا مساواته له، فإذا كان مباينًا له، لم يثبت حكمه فيه. فإذا ظهر وجه يقتضي التداني، ووجه آخر يقتضي التباعد، لم يكن أحدهما بأولى من الآخر بالاعتبار. فمن هذه الجهة يتنزل الفرق مع الجمع منزلة العلتين المتعارضتين، فيجب الترجيح إن وجد إليه سبيل، وإلا فالوقف إلى طلب الدليل، فهذا هو الصواب عندنا.
قال الإمام: (فإن قيل: فهلا قلتم: الفرق يشتمل على معارضة معنى','4','57',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2449,'الأصل) إلى آخر الفصل. قال الشيخ: الذي ذكره الإمام [في] الفرق كلام','4','58',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2450,'حسن، بالغ في فنه. وقد قدمنا وجه ذلك. ومدار الأمر على إدراك التباعد بين الأصل والفرع عند الافتراق في الوجه الخاص، وإذا تحقق التباعد، لزم الاختلاف في الحكم، فإن [المعهود] من قاعدة القياس استواء الحكم في [المتماثلات]، وأما المختلفات، فلا يكون حكمها واحدًا، فإذا أشعر الفرق','4','59',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2451,'بالاختلاف بين المحلين، لم يستو حكمهما بحال. إلى هذا يرجع أمر (١٤٣/ أ) الفرق، وهو مراد الإمام: أن الجمع يكون في محل [يأتي] الفرق فيه صفة','4','60',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2452,'عامة. وإذا وقع الافتراق في الوجه الأخص، كان الحكم باختلافهما أوقع من الحكم بتماثلهما.
قال الإمام: (إذا ذكر الذاكر معنى في الأصل [مغايرًا] لمعنى المعلل، وعكسه في الفرع) إلى قوله (فلا يشترط فيه ما يشترط في العلة [المستقلة]). قال الشيخ: أما بناء المسألة على المعارضة في الافتقار إلى رد معنى الفرع إلى أصل، فبناء صحيح.
وأما المصير إلى أنه استدل في جانب الفرع بنفي علة الأصل (١١٠/ ب)، فهذا ينبني على أن النفي، هل يصح أن يكون علة مناسبة مقتضية للحكم؟ وقد','4','61',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2453,'كنا قدمنا أن ذلك لا يصح عندنا. ولعل المراد بالنفي: ثبوت ضد المعنى الذي في الأصل في الفرع، [وهو] المراد بالانعكاس. فيكون ضد معنى الأصل موجودًا في الفرع، مقتضيًا ضد حكم الأصل.
فإذا قيل ذلك، لزم رد معنى الفرع إلى أصل، إذ يصير في الفرع معنيان: أحدهما- معنى الجامع يقتضي مثل حكمه.
والثاني- معنى الفارق يقتضي ضد حكمه. وقد وجد الجامع [لمعناه] شاهدًا، وهو أصل. فيفتقر الفارق إلى أصل أيضًا يشهد لحكمه. وهذا لازم، سواء قيل بالاستدلال أو لم يقل به. [وأما] إذا رد الاستدلال، فالأمر واضح. وإن قيل [بالاستدلال]، فلا يلزم من القول به- إذا كان منفردًا- القول به إذا كان في مقابلته معنى مستندًا إلى أصل.','4','62',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2454,'وهذا الكلام أيضًا لا يصح القول به على الإطلاق، وقد يجوز أن يكون المعنى المرسل أقوى في بعض المسائل من غريب مستند إلى أصل، فلا ينبغي أن يطلق القول بأن المعنى متى رجع إلى أصل، فهو مقدم على المعنى الذي لا أصل له، بل لا بد من تفصيل، وهو إن تساوى المعنيان في القوة والضعف، وانفرد [أحدهما] بالرد إلى أصل، ولم يستند الآخر إليه، فهذا يجب ترجيحه بلا إشكال. وأما إذا امتاز أحدهما بالرد إلى أصل، وامتاز الآخر بقوة في المناسبة مع الملاءمة، فلينظر المجتهد في مراتب الترجيح.
وأما قول القاضي: رأينا تصحيح الاستدلال، والمشهور عنه رد الاستدلال، وهو الذي يذكره الإمام عنه بعد ذلك، ويمكن أن يكون له فيه قولان.
وأما قوله: [لقبلته] على صيغة الفرق، فكلام صحيح، وقد بينا أن','4','63',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2455,'الفرق ليس بمعارضة على التناقض، وإنما المقصود منه إظهار [فقه يناقض] غرض الجامع. (١٤٣/ ب) وهذا يحصل من غير رد معنى الفرع إلى [الأصل]، فإنه إذا ظهر افتراقهما في الأمر الأخص، غلب على الظن الاختلاف، وإنما يستوي الحكم فيهما، على تقدير ثبوت التماثل، والتماثل مع الافتراق في الوصف الأخص باطل.
قال الإمام: (مسألة: قريبة المأخذ من التي تقدمت) إلى آخرها. قال','4','64',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2456,'الشيخ: قد تقدم أن من الذين قبلوا الفرق من يقول إنه يشتمل على معارضتين: معارضة على الأصل بعلة، ومعارضة علة الفرع بعلة، فغن الفارق يبدي في الأصل معنى غير ما أبداه المعلل، وبعكسه في جانب الفرع، إما أن يقتصر على نفيه على قول، أو بإثبات ضده في الفرع على قول. وإذا تصور ذلك، قالوا: فيفتقر في جانب الأصل إلى رد معناه إلى [أصله]، فإنه لما وقعت المعارضة في جانب الأصل، افتقر المعارض إلى رد معناه إلى أصل.','4','65',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2457,'وقد ذكر الإمام وجه المذهبين. والمشترط يقول: كل كلام لا أصل له، فهو استدلال مردود. والذي لا يشترط يقول: القول بهذا يجر إلى الخروج عن الضبط، ويفضي إلى تعدد أصول لا ينتهي القول فيها إلى ضبط، عند إمكانية المعارضة في كل أصل. فهذا الذي ذكره [الإمام] للقوم.
وعندي في ذلك كلام [آخر]، [وهو] أنه قد تقرر أنه متى استوت رتبة المستدل والسائل، ولم يقدر المستدل على ترجيح كلامه، (١١١/ أ) [كان منقطعًا]، وليس للسائل التشوف إلى الترجيح، وإظهار مذهبه، ولو فعل ذلك، لكان بانيًا قصدًا إلى ذلك، وهذا محذور عند النظار. وإذا كان كذلك،','4','66',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2458,'وصح [له] إبداء معنى في الأصل غير معنى المعلل، على القول بأن الفرق معارضة، اكتفى بذلك، إذا ساوى المستدل في الاستنباط بين الأصل الواحد، ونسبة المعنيين إلى الأصل نسبة واحدة، فكيف يصح مع هذا التقدير أن يفتقر الفارق إلى رد معنى الأصل الذي أبداه إلى أصل آخر، ويتكلف زيادة على ما جاء به المستدل؟ وهذا في التحقيق بمثابة ما إذا عارض علته بعلة، ثم أخذ يتكلف مسلك ترجيح ما جاء به بعد حصول المساواة.
وأما الذين ذهبوا إلى أن المقصود من الفرق إظهار فقه يقتضي تباعدًا، فلا خفاء على هذا المذهب أنه يستغني في الفرع والأصل جميعًا، بإبداء معنى في الأصل، معكوسًا في الفرع [من] غير رد إلى أصل فيهما جميعًا.
قال الإمام: (مسألة: إذا تمكن الفارق من إبداء معنى في الأصل .. ) إلى آخرها. قال الشيخ: قد تقدم بيان اختلاف (١٤٤/ أ) الناس في الفرق، وأن','4','67',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2459,'طائفة ذهبت إلى أنه مشتمل على معارضتين: معارضة في جانب الأصل، بإبداء معنى آخر- على القول بمنع تعليل الحكم بعلتين- ومعارضة في جانب الفرع، بإبداء معنى يقتضي نقيض حكم الأصل.
ولا بد ها هنا من بيان معنى المعارضة، وهل يتصور ذلك في الأصل والفرع جميعًا؟ فاعلم أن [المتعارضين] هما [المتناقضان]، [والمتناقضان] هما اللذان يمتنع اجتماعهما في المحل الواحد. وعلى هذا نقول: المثلان ضدان، لاستحالة اجتماعهما. وإنما حكمنا باستحالة اجتماع المثلين، من جهة أن أحدهما إذا قام بالمحل، وأوجب حكمه، استحال قيام الآخر [به] في حالة قيام الأول، وذلك أنه عند قيامه مع الأول على أحد وجهين: إما أن يوجب حكمه، وإما أن لا يوجبه، [فإن لم] يوجبه، فهو محال، لأن إيجاب المعنى لحكمه إيجاب نفسي، فلا يتصور أن يثبت غير موجب، ولو تصور ذلك، لقامت حركة [بمحل] من [غير] أن [يتحرك] المحل، وذلك غير معقول. وإما','4','68',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2460,'أن يوجب حكمه، فيحصل الحاصل، وهو أيضًا لا يعقل، فوجب لذلك القضاء بتضاد المثلين.
ومقصودنا بهذا الكلام أن لا يعتقد أنه لا يتضاد إلا [الضدان]، كالحركة والسكون، والسواد والبياض، بل متى امتنع الاجتماع، تحقق التعارض. وإذا بني الأمر على امتناع تعليل الحكم بعلتين، كان الاجتماع ممتنعًا شرعًا، فيكون بمثابة الامتناع العقلي.
وأما المعارضة في جانب الفرع، فتكون بإبداء معنى يقتضي ضد حكم المستدل. هذا بيان تحقيق المعارضة في الأصل والفرع جميعً. وإنما احتجنا إلى أن نفرق بين الوجهين، لأنه لا يتصور أن يستنبط من الأصل معنى يقتضي ضد حكمه، فلزم [ألا يستنبط] إلا معنى يطابق حكم الأصل، وكذلك المعنى الذي استنبطه الجامع، بخلاف المعنى الذي أبداه الفارق في الفرع، فإنه يكون مقتضيًا ضد حكم المستدل، فإذا سلك بالفرق مسلك المعارضة في الفرع، فلا [حجر] عليه في أن لا يعارض، إلا بضد معنى الأصل الذي','4','69',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2461,'أبداه، بل عليه أن يأتي (١١١/ ب) بما يقتضي المناقضة، سواء أتى به على صيغة الفرق، أو على صيغة أخرى.
[أما] نحن إذا صرنا إلى أن الفرق هو إبداء جهة خاصة في مقابلة الجهة العامة، التي جمع بها الجامع، فإذا كان ما أبداه الفارق على تمحضه، لا يقتضي تباعدً، فليس هو الفرق المعروف (١٤٤/ ب)، [فإنه] لو فرق بجهة خاصة، لكانت مستقلة في التباعد ومنع الاجتماع، فإن الزيادة التي أبداها في جانب الفرع، ليس لها أثر في جانب الأصل. [فلو] كان عكسها ثابتًا في جانب الأصل، لكان ذلك فرقًا صحيحًا.
نعم، قد يكون الاقتصار على إبداء المعنى في الأصل المعكوس في الفرع يقتضي تباعدًا ومنع الحكم، ولكن يكون ذلك الفقه منقوضًا، فيذكر الفارق الزيادة لدرء الناقض لا ليستحصل منها فقهًا. فهذا [يتعلق] الكلام فيه بالنقض، وما يفسد منه، وما لا يفسد، وإن كان النقض غير مفسد من طريق','4','70',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2462,'المعنى، لم تضر الزيادة، فإنها منبهة على اطلاع الناظر على مواقع الاستثناء، واحترازه في لفظة منها. وقد تقدم القول فيه مستقصى قبل هذا.

قال الإمام: <span data-type="title" id=toc-443>(مسألة: [مما] ذكره الذاكرون على صيغة الفرق</span>، وليس هو [على التحقيق فرقًا]) إلى آخرها. قال الشيخ: هذا الكلام ليس من الفرق بسبيل، وإنما أورده الإمام، لأنه من جهة اللفظ يضاهي الفرق، إذ أبدي في جانب الأصل أمر، [ونُفي] في الفرع. فمن هذه الجهة أشبه بالفرق.','4','71',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2463,'وصورته: كما ذكر الإمام، وذلك أن أبا حنيفة يقول: إذا قصد التطوع في رمضان، انصرف إلى الفرض دون التطوع، وكذلك لو قصد القضاء والنذر. وتحكم بأنه [لو] لم يقصد صومًا أصلًا، واتفق إمساكه جميع النهار، لم يجزه. فإذا قال: ما تعين أصله بنفسه، لم يشترط فيه تعيين النية، كرد الغصوب والودائع. فيقول المعرض: المعنى في الأصل أنه يستغني عن أصل النية، فاستغنى عن التعيين، بخلاف مسألة النزاع. فإن أصل النية لا بد منه. فهذا مبطل للجميع، وليس من أبواب الفرق. فإن الكلام في تعيين النية، إنما يكون بعد الاتفاق على اشتراط الأصل، وأصل النية غير معتبر في [رد] الغصوب والودائع. والكلام في التفصيل مع جحد التأصيل ذهاب عن التحصيل. والفرق يقدر فيه الجامع، ويفرق وراءه بفرق أخص. فيكون ما يأتي به الجامع في محل [يأتي] الفرق فيه صفة عامة، كما سبق.','4','72',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2464,'قال الإمام: <span data-type="title" id=toc-445>(فصل- في الاعتراض على [الفرع]) </span>إلى آخره. قال الشيخ: قد قدمنا اختلاف العلماء في الفرق، ومصير بعضهم إلى أنه معارضة جانب الأصل، ومعارضة في جانب الفرع. فأما الذي في جانب الأصل، فإبداء معنى غير ما أبداه المعلل، وهذا إنما يكون اعتراضًا على القول (١٤٥/ أ) بامتناع تعليل الحكم الواحد بأكثر من علة واحدة.
وأما المعنى الذي أبدي [في الفرع]، فمعنى يضاد معنى الجامع، فيقع في أبواب المعارضات المحضة، فيعترض عليه بما يعترض به على المعنى الذي لا أصل له، إلا أن يكون الفارق قد تكلف رد معنى الفرع إلى الأصل، على رأي','4','73',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2465,'من يرى ذلك. فيكون حينئذ معنى له أصل، فيقع في أبواب القياس المركب من الأصل والفرع والجامع والحكم. وقد سبق القول في الاعتراضات الصحيحة.
وأما قول الإمام: إنا نقدر لأنفسنا مذهبًا (١١٢/ أ)، وهو أنا لو صرنا [إلى] أن الفرق معارضة، [وكنا] ممن يقول بتعليل حكم بعلتين، لما رأينا مصير المعلل إلى ذلك جوابًا عن السؤال، من جهة أن الفرق يتضمن إبداء فقه يقتضي بُعد الفرع من الأصل، فليس المصير إلى تجويز اجتماع العلل للحكم الواحد، بالذي يبطل هذا الفرض.
وهذا الذي قاله الإمام تركيب منه، وبناء مذهب على آخر، والفقه الذي ذكره يقتضي تباعدًا ليس مرتبًا على قول من يقول إن الفرق معارضتان، [وإذا كان الفرق على هذا المذهب، إنما يكون باعتبار اجتماع معارضتين]، فإذا بطلت المعارضة في جانب الأصل، [لقبوله] المعاني المتعددة، بطل الفرق على الوجه المذكور.','4','74',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2466,'نعم، يبقى المعارض في الفرع على مجرده، مفتقرًا إلى الجواب، وليس هذا [بسؤال] الفرق عند القائلين به، وإنما هو بمثابة من لم يتعرض للأصل على حال. وإنما قصر نظره على إبداء معنى في الفرع على الخصوص، ولكن هذا أيضًا لا يتصور، فإن المعنى الذي أُبدي في الفرع [مناقضًا] [لمعنى] [المعلل]، لا يتصور وجوده في الأصل على حال، إذ يستحيل أن يبدي في الأصل معنى يقتضي نقيض حكمه. ولكن هذا، وإن كان لازمًا، إلا أنه لا يلتفت إليه في إثارة منع حكم الفرع، نظرًا إلى المعنى الذي يقتضي نقيضه، وينحصر الاعتراض على معنى الفرع، عندما لا يشترط رده إلى أصل، في الاعتراضات المتوجهة على الاستدلال من إفساد، أو ترجيح معنى الجامع عليه.','4','75',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2467,'قال الإمام: <span data-type="title" id=toc-446>(مسألة: إذا لم يذكر [الفارق] معنى في الأصل </span>.. ) إلى آخره. قال الشيخ: إذا لم يذكر [الفارق] معنى في الأصل، على حسب ما قدمناه في الفرق، ولكنه [ذكره] في جانب الأصل وعكسه في الفرع، فهذا على ذوق الفرق وحقيقته. فإنا قد قلنا إن الجامع بين الأصل والفرع قد يكون صفة أو حكمًا، وإذا صح (١٤٥/ ب) الجمع بالصفة تارة، وبالحكم أخرى، صح الفرق أيضًا على ذلك. وقد كنا قدمنا أن الجمع بالأحكام، هو المعبر عنه بقياس الدلالة، وسمي بذلك من جهة الاستدلال بالأثر على المؤثر، فالأثر يدل، ولا يقتضي، والمؤثر يقتضي، ومن ضرورة ذلك أن يدل، فعُبِّر عن المؤثر','4','76',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2468,'بأخص صفاته، وهو المقتضي والموجب، [أو العلة] والباعث، وعبر عن الأثر بالدلالة، إذ لا اقتضاء فيه، ولكنه يلزم من الأثر الاشتراك في المؤثر المقتضي، وإذا لزم من الاشتراك في الأثر الاشتراك في المؤثر، فكذلك يلزم من الافتراق في [الآثار] الافتراق في المؤثرات، فيحصل الافتراق في المعنى ضمنًا، للافتراق في الحكم الدال. هذا هو اختيارنا في هذا النوع.
وأما الإمام، فإنه اضطرب قوله في هذا النوع. وقد تقدم له في قياس الدلالة، [أنه] ليس قسمًا على حياله، وجرى على استقلاله، فإنه لا يخرج عن كونه معنويًّا أو شبهيًّا. فعلى قوله هذا، يكون من أبواب الفرق السابق على الحقيقة، ولا حاجة إلى فرض مسألة تتعلق به. وقد قال في هذا المكان: (١١٢/ ب) إنه لا يتمحض معنى ولا شبهًا أيضًا. فهذا قول يناقض ما سبق. ثم لو صرنا إلى أنه ليس معنى محضًا، وأنه من أبواب الأشباه تقديرًا، فتعميم القول بأنه إذا كان الجامع معنى، لم يقبل الفرع على هذا الوجه.','4','77',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2469,'وقوله: وهذا من الفارق محاولة معارضة المعاني بالأشباه، [أو ما] في معناها، ولا يقع ذلك موقع القبول. هذا أيضًا لا يتصور القول به على العموم، وعنده أن بعض الأشباه تقدم على بعض المعاني، ومن هذا القبيل عنده ضرب قليل العقل على العاقلة، تشبيهًا له بحصة آحاد الشركاء، وإن كان القياس المخيل يقتضي عدم الضرب. فكيف يصح مع هذا الإطلاق القول بأن الجامع إن كان معنويًّا لم يقبل الفرق؟ [وعلى هذه] الصفة، فقد ظهرت المناقضة من وجهين على ما حققناه.

قال الإمام: <span data-type="title" id=toc-447>(القول في الاعتراضات الفاسدة) </span>إلى قوله (ونرسمها مسائل، إن شاء الله تعالى). قال الشيخ: قد قدمت أن كل سبب تقصد منه ثمرة، فإذا ترتب عليه مقصوده، يقال إنه صح، وإذا تخلف عنه مقصوده، يقال إنه فسد.','4','78',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2470,'وقد قلنا: إن الاعتراض مأخوذ من اعتراضه [للدليل] حتى يمنعه من الجريان. فإن كان الوارد لا يعرض على الدليل بحال، ولا يتعرض له بوجه قريب أو بعيد، [فهو] فاسد. [فإن] اعترض حتى افتقر المجيب (١٤٦/ أ) إلى الدفع، فهو صحيح، وإن تعذر على المستدل الجواب، كان الدليل باطلًا. والذي لا يناقض الدليل من الكلام، لا حصر له، فطلب حصره محال.
وقول الإمام: [وفي ضبط] الصحيح منها حكم بفساد ما سواه، كلام صحيح إن ثبت انحصار الصحيح فيما ذكره. وقد كنا قدمنا قبل هذا، أن كل فن من قبيل الأحكام يتعارض فيه نفي وإثبات، ثم النهاية [تنتهي عند] أحد المتقابلين، فليرجع الضبط إلى القسم المنحصر، فيتبين منه القسم الآخر، كذلك إذا انضبط ما يصح، تبين فساد ما سواه. ومقتضى هذا [التقدير] أن لا يشتغل بتفصيل اعتراض فاسد، إذ التفصيل مستحيل. وإنما ذكر الأصوليون منها','4','79',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2471,'ما توهم بعض الناس أنه صحيح، فذكرت من هذه الجهة، ليتبين غلط من قضى بالصحة، ولم تذكر من جهة كونها فاسدة أصلًا. ولذلك قال الإمام: إنا لا نذكر [صنفًا] منها، إلا وفيه خلاف. يشير إلى [أن] سبب ذكرها وقوع الخلاف فيها.

قال الإمام: <span data-type="title" id=toc-448>(مسألة: إذا استنبط القائس علة في محل النص) </span>إلى قوله (فلا وجه [للحكم] بفسادها). قال الشيخ: هذا الكلام صحيح في صحة الاعتماد على العلة القاصرة، ونزيده تأكيدًا بوجهين:','4','80',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2472,'أحدهما- أنه لا يصح التحكم بنصب العلل، وقد قدمنا في ذلك قولًا بالغًا، ورددنا على الطاردين، وبينا الطرق التي تثبت بها علل الأصول، واستقر الرأي على أن الدليل الدال على [صحة] نصب الوصف علة، لا يختص بمحل دون محل، لما فُهم من الصحابة - رضي الله عنهم - في صرف النظر إلى المعاني المفهومة، دون الجمود على التعبد. وإذا ثبت ذلك، فقد يقام (١١٣/ أ) الدليل على انتصاب المعنى المتعدي، وقد ينتصب على انتصاب المعنى القاصر. وهذا معلوم لا شك فيه.
الوجه الثاني- أن علل الشرع دواعي وبواعث على الأحكام، ويصح أن يكون الباعث مختصًّا بمورد النص، ويجوز أن يكون متعديًا عنه إلى غيره. وإن أخذت العلة من العلامة، وهو الذي تخيله مًن مًنع التعليل بالعلة القاصرة، فيقول: النص يغني عنها. فنقول: لا يمتنع أن ينصب على الحكم دليلان، إذ لا مناقضة في تعدد الأدلة بحال. فوجب من ذلك القضاء بصحة العلة، إذا دلت الأدلة على صحتها، ولا يقدح في ذلك اقتصارها وانحصارها على محل النص.','4','81',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2473,'قال الإمام: (ويتوجه وراء ذلك سؤالان، وبالانفصال عنهما يتبين حقيقة المسألة) إلى قوله (والمسألة مفروضة في العلة القاصرة). قال الشيخ: الذي ذكره (١٤٦/ ب) الإمام مخيل، وطريق الجواب عنه ما أشرنا إليه، وذلك أنا نعني بعلة الشرع باعث الشرع على الحكم، أي لأجلها حكم، ولولا هي لم يحكم، [ثم] إذا ظهرت العلة، اتبعت حيث وجدت، ورتب الحكم عليها','4','82',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2474,'[أين] صودفت، [فتعدية] العلة في رتبة ما يدعى لها، وذلك إنما يكون بعد استنباطها، فلا يقدح ذلك في صحتها، والنظر في الثمرة: هل هي عامة أو خاصة بعد النظر في صحة المثمر مستندًا إلى دليله؟
قال الإمام: (وقال قائلون: العلة القاصرة تفيد بعكسها) إلى قوله (طردًا وعكسًا من غير [إثمار] فائدة). قال الشيخ: الذي ذكره من قال إن العلة','4','83',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2475,'[القاصرة] تفيد بعكسها، كلام صحيح، وأما قولهم: عدم التعدية يشعر بانتفاء تحريم الربا، فقد قدمنا ما نختاره في ذلك، وبينا أن النفي لا إشعار له بحال، أعني من أبواب المخيل المناسب. أما إذا لم يرد بالإشعار إلا الدلالة، فيصح أن يكون العدم دليلًا، ولكن عدم العلة عندنا لا [يحيل] نفي الحكم، ولا يدل عليه. نعم، إن انتفت العلة، ولم يخلف غيرها، وفقد التوقيف، انتفى الحكم ضرورة، لا لدلالة [عدم] العلة، [ولكن] لأنه لا يثبت إلا المستند. وإنما أفاد استنباط القاصرة من محل النص، لأنا قد بينا أن كل علة يفتقر في الاعتماد عليها إلى شهادة الأصل، فتعدد المعنى في هذه الصورة، يمنع من الاعتماد على واحدة بعينها، إلا بعد الترجيح. فقد يقال:
استنباط القاصرة يمنع من اعتماد المتعدية','4','84',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2476,'وأما جواب الإمام عن الفائدة، فالمصير إلى صحة ثبوت علة تخلف العلة الزائلة، فغير صحيح على أصله، فإنه يرى أن الحكم لا يثبت بعلتين، لا مجتمعتين ولا متعاقبتين، ولذلك قال: إذا زالت علة وخلفت أخرى، فالحكم الأول قد زال لا محالة، وأما الثاني، فإنه يشاركه في الاسم، ويخالفه في المعنى والحقيقة، فكيف يصح له أن يقول: إن العلة الزائلة يجوز أن تخلفها أخرى؟ والناظر مفتقر إلى إبطال ما يُدّعى علة، فينتفي الحكم حينئذ من غير حاجة إلى تلقي نفيه من عكس العلة. قال: فآل الأمر إلى تكليفه طردًا وعكسًا من غير فائدة.
قال الإمام: (ومما يوضح الغرض في ذلك) إلى قوله (فقد سقط طلب إفادتها من جهة الانعكاس). قال الشيخ: قوله: إن العلة إنما تنعكس ويتعين','4','85',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2477,'التعلق [بنفسها] (١١٣/ ب) في إثبات نقيض حكم الطرد] بعكسها [في نفي الحكم] بشرطين:
أحدهما- أن يكون العدم يشعر بالعدم، كما يشعر الوجود بالوجود. [فقد] بينا أن هذا لا يصح، ولا اقتضاء عندنا في العدم ولا مناسبة. ثم [إنه] أيضًا لا يصح على ما يراه (١٤٧/ أ) الإمام، فإنه [قد] يرى لزوال العلة [أثرًا] يضاهي زوال الترجيح، فكيف يصح مع هذا أن يقال إن العدم يشعر بالعدم، كما يشعر الوجود بالوجود؟','4','86',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2478,'وأما الشرط الثاني: وهو كونه لا يخلف العلة الزائلة علة أخرى، فقد بينا أنه لا يرى ذلك أصلًا، فكيف يشترط ما لا يراه؟ على أن هذا كله غير ما أراده القوم، فإنهم منعوا ذلك لضيق المحل عن الوفاء [بالعلتين]، فيمتنع التمسك بالمتعدية دون ترجيحها على القاصرة، وكذلك الأخرى. فترك الإمام هذا الحرف، وتعلق بالعكس، ومراد القوم ما ذكرناه.
قال الإمام: (فإن قال قائل: إذا سلمتم أن العلة إذا لم تفسد، فلا يحكم عليها بصحة ولا [بفساد]) إلى قوله (فليفهم الناظر ما يرد عليه من ذلك). قال الشيخ: هذا الذي ذكره في هذا المكان، كلام فيه نظر، وذلك أنه رد فائدة','4','87',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2479,'العلة [القاصرة] إذا استنبطت من ظاهر خاصة، فإنها عنده تعصم الظاهر، وتمنعه من التخصيص، على تقدير استنباط علة منه تقتضي تخصيصه، فجعل كونها عصمت الظاهر مفيدة حكمًا، فهو في معنى التعدية، وإن لم يكن تعديًا تحقيقًا.
وهذا الذي قاله غير صحيح، على مقتضى قواعده، وذلك أنه لا يخلو الظاهر إذا استنبط منه العلة القاصرة، إما أن يستنبط منه علة متعدية، أو لا يستنبط منه، فإن لم يستنبط منه علة متعدية تقتضي تخصيصه، فالظاهر مُقر على ظهوره، ولا يجوز تخصيصه، فلا حاجة إلى العلة القاصرة على هذا التقدير. وإن استنبط علة متعدية تقتضي التخصيص، فهي العلة الصحيحة دون الأخرى، فيجب الاعتماد عليها [واطراح] القاصرة.
فلئن قيل: لا تعتمد المتعدية إلا على تقدير ترجيحها على القاصرة في محل الاستنباط، فهذا يفضي إلى القول بصحة القاصرة على الإطلاق، لأنها متهيئة لمعارضة ما يقدر متعديًا. وهذا هو الذي أبديناه من فوائدها قبل هذا.
قال الإمام: (فإن قيل: قول رسول الله - صلى الله عليه وسلم -: "لا تبيعوا الورق بالورق")','4','88',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2480,'إلى قوله (وإن لم تصح فلتطرح). قال الشيخ: الكلام على هذا، هو ما سبق بعينه، ولم يأت هو في هذا المكان بزيادة حتى يتكلم عليها. وقد بينا أن استنباط القاصرة مفيد مطلقًا، سواء كانت مستنبطة من محل ظاهر أو نص، فلا معنى لإعادة ما سبق.
قال الإمام: (فإن قال قائل: ما ذكرتموه تصريح [منكم] بإبطال التعليل بالنقدية) إلى قوله (وليس بعد هذا نهاية). قال الشيخ: اضطرب قول الإمام.','4','89',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2481,'في مسألة الربا (١٤٧/ ب)، فتارة يميل إلى القياس فيه، ويعتبر بالأشياء الأربعة غيرها، ويرى أنها من آخر الأشباه، نظرًا إلى المقصود عند دعاء الضرورة إلى الاستنباط. وتارة يقول إنها تعبدية محضة، ويثبت الربا في كل مطعوم [بقوله]: "لا تبيعوا الطعام بالطعام .. ". وهذا هو الذي استقر عليه كلامه قبل هذا، [وهو] اضطراب كثير.
قال الإمام: (السؤال الثاني: (١١٤/ أ) إلى قوله (ثم نبهنا على التحقيق فيه. فكفى هذا البيان). قال الشيخ: الذي ذكره في هذا السؤال، إعادة','4','90',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2482,'[لما] سبق، والفائدة عندنا محققة، سواء كان الحديث نصًّا أو ظاهرًا. وقد قدمنا وجه ذلك، فلا نعيده.
قال الإمام: ([وقد ذكر] بعض المنتمين إلى الأصول، وهو: الحُليمي) إلى قوله (فلا مزيد [إذًا] على ما تقدم). قال الشيخ: الذي ذكره الحليمي، كلام ضعيف، فإن الخلاف ليس في وجوب النظر على المجتهد، وإنما الخلاف في صحة العلة القاصرة. فمن أين يلزم من وجوب الاجتهاد، القضاء بصحة العلة القاصرة؟ وهو بمثابة ما لو نظر فلم يجد إلا وصفًا طرديًّا. هذا هو اعتراض الإمام عليه، ولكنه يورد الكلام على غير هذا الوجه فيصح، وهو أن يقول: إذا اطلع المستنبط على المعنى الذي يصح الاستدلال على كونه علة، وأسنده إلى دليله، غلب على ظنه انتصابه علة، إن كان الدليل ظنيًّا، أو قطع بذلك إن كان الدليل قطعيًّا. فإذا ثبت له ذلك، اتبع الحكم المعنى، سواء كان قاصرًا أو','4','91',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2483,'متعديًّا. فإذا قرر الكلام على هذا الوجه كان مستقيمًا، وهو- والله أعلم- مراد الحليمي.
قال الإمام: (ثم تكلم القائلون بالعلة القاصرة إذا عارضتها متعدية) إلى قوله (لو فرضت كل واحدة منهما منفردة). قال الشيخ: الصحيح عندنا أنه لا يقع ترجيح بالقصور ولا بالتعدي، وهذا هو اختيار القاضي، وإنما تُرجح العلل إما بقوتها في أنفسها، كالمعنى الضروري إذا عارضه حاجي أو مصلحي، أو بالنظر إلى الأدلة التي تثبت بها علل الأصول، وقد يرجح المعنى بكثرة الشواهد، وكأنه راجع إلى قوة في طرق. فأما التعدية والقصور [فلا]. فمن النظر في آثار العلل [لا] بعد ثبوتها، فلا يتحقق بها ترجيح. وأما من رجح المتعدية، بأن النص يغني عن القاصرة، فهذا كلام ضعيف، وهو يشير إلى أنه لا فائدة فيها، فإذا كان كذلك، كانت غير صحيحة، فلا معنى لترجيح غيرها عليها.','4','92',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2484,'وأما ما ذكره الأستاذ من أن النص شاهد للقاصرة، فهو أيضًا شاهد للمتعدية) ١٤٨/ أ)، فلا أثر لما قاله. وأما ما ذكره الإمام من اختصاص المتعدية بالفائدة، فهو يشير إلى بطلان القاصرة، فلا معنى لطلب الترجيح فيها.
وأما قوله: فإذا جرت المتعدية سليمة، لم يقدح فيها غير معارضة القاصرة، واختياره اعتماد المتعدية. فهذا الذي قاله صحيح على أصله، [إذا] لم يلتفت هو للمتعدية، إلا عندما يستنبط من ظاهر، فكأنه يصونه عن التخصيص على حسب ما مر. [فإذا] كانت المتعدية كذلك، وصودفت في [الفرع]، فقد [وجد] في الفرع معنى يقتضي الحكم على شرط الصحة، فلا معنى لتركه، لفقدان حكمة سنحت في مورد النص. إلا أنه لما قال: وهذا إذا استويا خفاء وجلاء، فمفهوم كلامه أنه إذا كانت القاصرة أرجح، تركت','4','93',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2485,'لها المتعدية، لاشتراطه المساواة في اعتماد المتعدية.
وهذا الكلام يكاد بعضه ينقض بعضًا، لأنه إذا نظر إلى انفراد المتعدية في الفرع، فيجب اعتمادها، وإن كانت القاصرة أرجح منها، وإذا [تركت] المتعدية للقاصرة الراجحة، أشعر ذلك بالمعارضة. فإذا تحقق استواء (١١٤/ ب) [المتعارضين]، وجب الوقف، وبطل التعيين بالتحكم. فلا أعرف وجهًا لصحة هذا الكلام.
وأما قوله: وهذا الذي قدرناه لا يجري في النقدين، فإن العلة فيها باطلة بوجوه سوى الترجيح، ولم يذكر من الوجوه شيئًا. ونحن ننبه على ما أراده، فمن الوجوه المقتضية: إبطال التعليل بالوزن، [وعنده] أن [الوزن] طرد، ولا مناسبة، ولا رائحة مناسبة بين [الوزن] وتحريم التفاضل، وإنما اعتبر','4','94',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2486,'ذلك معرِّفًا لمقدار الأموال، فالوزن في الموزون، وكالعدد والذرع في المعدود والمذروع، وعلى قطع نعلم التماثل بين هذه الأمور، ولم يكن الذرع ولا العدد علة في الشرع على حال، فليكن الوزن كذلك، إذا ما ثبت [لأحد] [المثلين] ثبت للآخر. هذا وجه.
والوجه الثاني: أن كل عينين اجتمعا في علة الربا، أو في وصف من أوصاف علة الربا، فلا يجوز إسلام أحدهما في الآخر، وإنما [كان] [كذلك] لإيماء النص، وهو قوله - عليه السلام - في الحديث: "لا تبيعوا الذهب بالذهب، ولا [الورق] [بالورق] إلا مثلًا بمثل، عينًا بعين، يدًا بيد". [ثم قال: "فإذا اختلف الجنسان، فبيعوا كيف شئتم يدًا بيد]. ولو أراد الأجناس على العموم، لحُرم بيع الأجل والسلم. فعلم بذلك أنه إنما أراد [الجنسين] من هذه المذكورات. فعند اتحاد الجنس، يحرم التفاضل، فإذا تعدد الجنس مع الاجتماع في العلة، حرُم النَّساء خاصة، ولو كان الوزن علة، لحَرُم إسلام الدراهم والدنانير في الموزونات، ولا ذاهب إلى ذلك. فثبت بهذا أن الوزن لا يصلح للاعتماد، حتى يقع (١٤٨/ ب) الترجيح بينه [وبين غيره].
قال الإمام: (ومن الاعتراضات الفاسدة: ما إذا تعلق المتعلق بما يدل على','4','95',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2487,'فساد في الفرع) إلى قوله (فلا يستريب في اختلال العقد [إن] تخلف عن اقتضائه). قال الشيخ: هذا الاعتراض فاسد لا شك فيه، والأمر فيه على ما [قرره] الإمام بلا مزيد، وليس الغرض [في الخوض] في التفصيل، الاعتراف بصحة الأصل، وإنما هو استدلال على الفساد بفقدان ثمرات الصحة، وهذا يناظر في المعقولات أن يُذكر كلام ويُنظر ما يفضي إليه، فإن أفضى إلى محال كان محالًا. وكذلك هذا ثبوت العقد الصحيح مع منع تخلف ثمرته باطل، إذا لم يكن معارض يمنع من الثمرة.','4','96',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2488,'وأما قول الإمام: إن من لا يقول بالاستدلال، ويشترط الأصل في كل معنى، فقياس مذهبه أن يقول بهذا، وإن لم يستند إلى أصل، فكلام صحيح، والسبب في الفرق أن المعاني قد يثبت الشرع الأحكام على وفقها، وقد يعطلها في بعض المواضع. فلما ثبت هذا الانقسام، افتقر المعنى إلى أن يحكم الشرع على وفقه، ليتبين أنه من قبيل ما اعتبره الشرع. وأما ثبوت المثمر من غير معارض مع تخلف الثمرة، فهذا لم يثبت على حال، فاستغنى فيه عن الرد إلى الأصل. وهذا إذا قدر المستدل أن يثبت أن العقد إذا صح بثمرته ما يدعيه، وإذا ثبت ذلك، فلا خفاء في صحة الاستدلال.
قال الإمام: (ثم من صحح هذا النوع، اضطربوا في أنه [هل هو] من','4','97',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2489,'قياس المعنى، أو من قياس الشبه؟ ) إلى قوله (والظهار والطلاق حكمان متغايران). قال الشيخ: هذا الاستدلال صحيح، [غير] مفتقر إلى الرد إلى أصل، [فإذا لم يرد إلى أصل]، [لم] يصح أن (١١٥/ أ) يكون قياسًا، إذ شرط القياس الأصل لا محالة. [نعم]، إن تكلف المستدل الرد إلى أصل مع غنيته عنه، فهذا حينئذ الجمع بالأحكام، وهو قياس الدلالة. وقد تقدم القول فيه، وسيعود إن شاء الله [تعالى] بعد ذلك.
أما إذا اقتصر على بيان نفي الثمرة مستدلًا بها على نفي الصحة، فلا يصح أن يكون هذا من القياس بحال، وهذا هو قول الإمام: إن هذا أعلى من قياس الدلالة، فإن الظهار والطلاق حكمان متغايران، لا يبعد أن يثبت أحدهما وينتفي الآخر، ولا يجوز المحصل مباينة المقتضي مقتضاه.','4','98',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2490,'قال الإمام: (ومن الاعتراضات الفاسدة: أنه إذا طرد طارد علية في حكم واستمر له) إلى قوله (وليس كل ما يلزمه (١٤٩/ أ) [في نفسه يلزمه] البوح به في النظر). قال الشيخ: هذا الاعتراض فاسد، ومقصود مورده من حيث الجملة، أنه قصد أن يبين انتقاض معنى المعلل، بأن يريه ثبوته مع انتفاء الحكم، هذا مقصوده، ولكنه لم يقدر على بيان تخلف نفس الحكم الذي أثبته المعلل، فزعم أنه لم تترتب عليه أحكامه. وزعم أن حكم [الربا] مترتب على علة الزكاة، ولو كان الأمر على ما ذكره، ثم وُجد أحد الحكمين، لأشعر ذلك بفساد','4','99',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2491,'المعنى، إن لم يتخلف الحكم لمعارض، ولكنه في هذا المقام مدع، وعليه أن يبين استناد الحكمين لسبب واحد، وإلا فكلام المعلل قد جرى سديدًا على ما [يريده]، فإن قدر المعترض على [بيان] رجوع الحكمين إلى مقتض واحد، كان ذلك اعتراضًا صحيحًا.

قال الإمام: (و<span data-type="title" id=toc-451>من الاعتراضات الفاسدة: التعرض للفرق) </span>إلى قوله (وكونه مجتهدًا فيه في الفرع). قال الشيخ: قد قدمنا أنه يصح أن يفرق الإنسان بين الأصل والفرع بالأحكام، على حسب ما سبق، ولكن إنما يصح ذلك، إذا لم يكن الحكم الذي وقع به الفرق مترتبًا على الإجماع والخلاف، [فبان]','4','100',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2492,'التفاوت بين الفرع والأصل في القطع بحكم الأصل، وكون حكم الفرع مظنونًا، لا يمنع الإلحاق، إذ لو كان ذلك يمنع الإلحاق، لم يثبت من القياس إلا ما كان مقطوعًا به، وليس الأمر كذلك، على ما قررناه في كتاب القياس وإثباته على منكريه. وإذا كان الحكم الذي وقع الفرق فيه مترتبًا على هذا، لم يكن قادحًا، وإن كان على غير هذا الوجه، فقد سبق حكمه.

قال الإمام: (و<span data-type="title" id=toc-452>من الاعتراضات الفاسدة: قول القائل: الحكم يثبت في الأصل متأخرًا) </span>إلى آخره. قال الشيخ: السؤال في ظاهره لازم، وذلك أنا إذا قلنا: القياس: حمل معلوم على معلوم، إلى آخره. فحاصله اعتبار مسكوت عنه','4','101',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2493,'بمنطوق به. فيفتقر على هذا إلى أن يكون حكم الأصل ثابتًا، وحكم الفرع غير ثابت، [فيتلقى] من القياس على الأصل، وإذا كان الفرع ثابت الحكم قبل الأصل، كيف يتصور أن يثبت حكمه قياسًا على الأصل؟ هذا محال. وقد كنا قدمنا أنه ينبغي أن يجري في هذا قياس العلة، فإن العلة تقتضي الحكم وتستدعيه، والحكم كان ثابتًا قبل ذلك في الوضوء. [وإنما] يجري في ذلك قياس الدلالة. فإن الدليل لا يشترط اقترانه بالمدلول، بل يصح اقترانه (١١٥/ ب) وتأخره، ولا يمتنع على هذا أن يكون الوضوء الثابت، النية فيه مشترطة عند من يثبتها، مدلولًا (١٤٩/ ب) عليها بدلالة أخرى من نص أو','4','102',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2494,'غيره. وإذا ثبتت النية في التيمم، وثبت استواؤه مع الوضوء في الشرع، أرشد ذلك إلى سبق [الشرط في] النية، وقد يكون المرشد إلى ذلك [توقيف] أو معنى مرسل. فأما إذا ثبتت النية في الوضوء مستندًا غلى قياس العلة، متلقىً من التيمم المتأخر، فلا وجه لذلك.
وما ذكره الإمام من أن المناظرة في [مأخذ] النزاع، فكلام الخصم سؤال عن أمر مقتضٍ، كلام صحيح، والمقصود منه [أن الخصم] إن أنكر وجه الدلالة بيناه، وإن قال: فبأي شيء كانت العلة معلومة قبل شروع التيمم؟ لم يلزم الجواب عن ذلك في التحقيق، إذ قد استقل الدليل في التعريف في الحال.
قال الإمام: (ومن الاعتراضات الفاسدة: جعل المعلول علة) إلى قوله','4','103',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2495,'(فقد تبين سقوط الاعتراض). قال الشيخ: لا بد أن يكون باب المعلول يتميز [عن] باب العلة، إذ العلة مؤثر، والمعلول أثر، فلا بد أن يتميز الأثر عن المؤثر، فإن لم يتميز أحدهما عن صاحبه، بطل هذا المعقول. هذا وجه تقرير السؤال. ولكن لا يلزم من الجامع أن يكون مؤثرًا ولا بد، بل قد يجمع بالمؤثر، وقد [يجمع] بالأثر، استدلالًا على المؤثر، وقد يستدل بأحد [الأثرين] على الآخر.
وهذا النوع الثالث هو الذي يتوجه فيه هذا الاعتراض، فإن الظهار والطلاق أثران عن مؤثر واحد، وهو صحة الملك، وقبول المحل، وكون [المتصرف] أهلًا، فإذا تحقق ذلك، دل ثبوت أحدهما على ثبوت الآخر،','4','104',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2496,'[إذا] ثبت تلازمهما وكونهما [ثمرتين] لمثمر واحد. هذا تحقيق هذا السؤال، ولا يتفق هذا الاعتراض إلا في هذا النوع من الاستدلال، في قياس الدلالة على الخصوص، لما قررناه من تلازم الحكمين.
قال الإمام: (فأما ما ذكره من الاستشهاد بالعلة والمعلول في [المعقولات]) إلى قوله ([مشعر] بوقوعه [عند وقوعه]). قال الشيخ: أما الكلام على العلة والمعلول في العقليات، فليس هذا موضع استقصائه،','4','105',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2497,'وكذلك الكلام في إثبات الأحوال. وأما [العلل] في الشرعيات، فقد نقلنا [اختلافًا] في مأخذ اللغة. أما من ذهب إلى أنها مقتضية للحكم، وباعث عليه، فلا يصح على رأيه أن يكون الحكم المعلول [باعثًا] على العلة، ولا مقتضيًا لها.
[فأما] إذا بنينا ذلك على أن علل الشرع علامات، ولم نشترط فيه الاقتضاء، فلا بُعد في أن يكون أحد الحكمين عًلًمًا على الآخر، ويكون الآخر علمًا على ذلك الحكم الذي كان علمًا، ويختلف ذلك باختلاف [وجوه] الحاجات [إلى] الاستدلال. وهذا كما نقوله في الأوقات، فإن كل حادث','4','106',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2498,'قُرن [بحادث] آخر (١٥٠/ أ)، فذلك الحادث وقته، فيصح أن يعرف قدوم زيد بطلوع الشمس، وقد يقول: طلعت الشمس عند قدوم زيد، إذا كان قدوم زيد معروفًا، ووقت طلوع الشمس مجهولًا.
فعلى هذا، لا يمتنع أن يجعل نفوذ الطلاق معرفًا لنفوذ الظهار، إن وقعت المنازعة مع من يعترف بنفوذ الطلاق، ويمنع نفوذ الظهار. وقد يكون الأمر بالعكس من ذلك في الطرفين. وهذا بمثابة ما مثلناه [من الوقت] من غير فرق. [فليفهم] الناظر الفرق بين جهات الاستدلال، وليتبين موضع ورود هذا السؤال (١١٦/ أ). وبالله التوفيق.

قال الإمام: (و<span data-type="title" id=toc-454>من الاعتراضات الفاسدة: أن يقول القائل: هذا الذي نصبته علمًا هو صورة المسألة) </span>إلى آخره. قال الشيخ: مثال ما ذكرناه: أنا إذا سئلنا عن النبيذ المشتد، هل هو حرام مثلًا؟ فقلنا: مسكر فكان حرامًا، قياسًا على الخمر، قال المعترض: لم تزيدوا في الاستدلال على ذكر صورة المسألة، فإن','4','107',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2499,'السؤال عن المسكر، هل يحرم أم لا؟ والدليل ينبغي أن يكون زائدًا على ما وقع السؤال عنه. [وهذا] الاعتراض فاسد، فإنه قد يتفق أن يكون في نفس ذكر المسؤول عنه ما يقتضي حكمه، ولو تنبه الخصم له، لاستغنى عن الاستدلال. وهذا بمثابة من يقول: هل [يدل] الجائز إذا حصل على المخصص؟ فإنه يقول: [يدل]، ويبين وجه الربط بين وقوع الجائز، وبين الافتقار إلى المخصِّص. ولا يفسد هذا الاستدلال تنبه السائل على وجهه. وكذلك إذا كان الإسكار مقتضيًا للتحريم، وسأل السائل عن تحريم المسكر، [فنبه] على وجه، بالنظر إلى إخالته ومناسبته، واستنباطه من الأصل الشاهد له، فلا يقدح في ذلك كون سؤال السائل تعرض له.
وأما ما ذكره الإمام من أن المستدل لو نص على خاصية الفرع، لم يجد أصلًا، فكلام صحيح، إذ خاصية الشيء هي التي تثبت له، ولا تثبت لغيره.','4','108',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2500,'وعند فقدان الجامع، يمتنع الإلحاق. [هذا] تمام البيان في الاعتراضات.

قال الإمام: <span data-type="title" id=toc-455>(القول في المركبات) </span>إلى قوله (فلا يتصور- والحالة هذه- الخلاف في [الفرع]). قال الشيخ: المراد بالمركبات: أن يكون الحكم المتفق عليه بين الخصمين، إنما يثبت عندهما لعلتين مختلفتين، [أي] يركب الحكم من السببين، ولا يعتقد الناظر في هذا الباب أنا نريد أنه متى اختلفت العلتان في الأصل المعتبر به، كان ذلك تركيبًا، فإنه لو كان كذلك، لكان أكثر الأقيسة مركبة، بل لا يوجد أصل تنازع الفقهاء في اعتبار الفروع به إلحاقًا، إلا بعد الاختلاف في علته. بل المراد أن (١٥٠/ ب) الأصل إنما صار أصلًا باتفاق الخصمين عليه، مستندًا إلى سببين [مختلفين]، [فهو] بالنظر إلى الصورة أصل، [لاتفاقهما] على حكمه، وبالنظر إلى المعنى، [فهو] في','4','109',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2501,'معنى الفرع، إذ الخصم إذا منع معناه منع الحكم. فصورة القياس المركب المنازعة في المعنى، لا أن كل قياس اختلف في معناه لعلة يكون مركبًا. فلأجل أن ضرورة القياس المركب، الاختلاف في المعنى، افتقر إلى تجديد العهد بالطرق التي تثبت بها علل الأصول. [إذ المنازعة في المعنى قائمة، والحاجة إلى الاستدلال ماسة].
قال الإمام" (ومما تمس الحاجة إلى ذكره، أن من ذكر في علة الأصل صفة) إلى قوله (وهذه مقدمات لا بد من التنبه لها). قال الشيخ: ما ذكره الإمام ها هنا من افتقار [القائس] في الجمع إلى ضم صفة إلى العلة، لا تستقل علة، وعلة الأصل تستقل دونها، يفسد القياس، ويمنع المصير إليه، وذلك أنه إذا ضم إلى علة الأصل صفة لا حاجة إليها، ومعنى الأصل يستقل دونها، فلا يخلو: إما أن يكون معنى الأصل موجودًا في الفرع أو غير موجود، فإن كان موجودًا في الفرع، صح الجمع، اعتمادًا عليه، والصفة مستغنى عنها في الأصل والفرع جميعًا، ولا حاجة إليها بحال، ولا سبيل إلى أن يقضى بفساد (١١٦/ ب) الجامع لأجل ما ضم إليه من الوصف الزائد، ولكن يكون ذاكره أتى بأمر غير محتاج إليه، فيكون من فن اللغو. وإن كان معنى الأصل على الحقيقة غير موجود في الفرع، والموجود في الفرع لا أثر له في الأصل، فهذا القياس باطل قطعًا، وهو على الحقيقة الجمع بلا جامع، وذلك باطل، [فإذا] تحقق','4','110',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2502,'بطلان الجمع لفقدان الجامع، لم يبق إلا الاقتصار على ذكر الفرع، غير مردود في الحقيقة إلى أصل.
ومثاله: ما إذا قال: مسٌّ فصار كما لو مس وبال، وقد حصل الاتفاق على أن البول مستقل في الأصل، وانضمام المس إليه لا أثر له. فإذا قال القائس فيمن مس على الخصوص: مسٌّ، [وقال: قيل له: البول مستقل، وهو غير موجود في الفرع، فأسقط التعرض له، فلا يبقى معك إلا أنه مس]، فصار كما لو مس، [فالقياس] على هذا باطل قطعًا، لأن ما اعتقد أصلًا [عين] مسألة النزاع.
فإن قيل: هذا يلزمكم في الخمر، فإنه إذا قلنا في النبيذ: مسكر فكان حرامًا، قياسًا على الخمر، قيل: الخمر ليس نبيذًا أصلًا، فأسقطوا ذكر الخمر، فيبقى معكم كونه مسكرً، فيكون القياس: مسكر فكان حرامًا، قياسًا على المسكر. وإذا قيل هذا (١٥١/ أ) في الخمر، فليجز في قولنا: مسٌّ فصار كما لو مس وبال. قلنا: هذا غلط بين، فإنا لم نذكر الخمر لاعتقادنا أن الخمرية علة، بل إنما ذكرت لأنها المحل المحكوم فيه، والأحكام تتبع العلل، ولا تقتصر على صور المحال. فأما التعدية مع فقدان العلة، فباطل لا شك فيه، والبول هو العلة هناك، وهو مفقود في محل النزاع، فامتنع الإلحاق من غير جامع.','4','111',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2503,'قال الإمام: (والتركيب يقع في [الوصف وفي الأصل]) إلى قوله (وبين سقوط العلة على تقدير). قال الشيخ: أما تقسيم التركيب [إلى الأصل [والوصف]، فاعلم أنا قد بينا أن التركيب] إنما يعقل عند استناد الحكم عند الخصمين إلى علتين مختلفتين، فلا تركيب أبدًا إلا في الأحكام، ولكن قد يكون المركب حكم الأصل على الحد المذكور، وقد يكون المركب الحكم الجامع بين الأصل والفرع، فيسمى وصفًا من جهة كونه جامعًا، إذ يطلق الوصف على الجامع، حكمًا كان أو غيره.
فأما التركيب في الأصل، فقد مثله الإمام بمسألة المرأة حيث قال: أنثى فلا تزوج نفسها، كابنة الخمس عشرة سنة، وأبو حنيفة [يسلم] أن ابنة','4','112',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2504,'الخمس عشرة لا تزوج نفسها، بناء منه على كونها صغيرة، والشافعي قضى بأنها لا تزوج نفسها، بناء على أنها أنثى، فقد حصل الحكم متفقًا عليه بينهما، بناءً على علتين مختلفتين، فهذا هو التركيب في الأصل، والاختلاف في قبول هذا التركيب واضح، وسببه لائح.
والقائلون تمسكوا بأن حكم الأصل متفق عليه، والمنازعة في العلة، ولا بد من إقامة الدليل عليها. [فإن] وفي المستدل بإقامة الدليل، صح كلامه، وإن عجز، كان منقطعًا. فلا أثر للتركيب، كان أو لم يكن.','4','113',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2505,'والصحيح عندنا خلاف ذلك، وحاصل القول في التركيب (١١٧/ أ) الاعتماد على [النكتتين] في مثل هذه الصورة، والنكتة في الإبطال: أن المقيس عليه، وإن كان أصلًا من جهة الصورة، فهو على التحقيق فرع، فإنه إن ثبت للشافعي أن ابنة الخمس عشرة كبيرة، منع الحنفي الحكم، وإن ثبت أنها صغيرة، فالصغر مستقل، فيصير كالقياس على ما لو مس وبال، فالقياس متردد بين منع الحكم، [على تقدير البلوغ، وبين منع العلة]، على تقدير الصغر، فتفوت حقيقة القياس، ويصير معنى الكلام: صغيرة فلا تزوج نفسها كالصغيرة. فالوجه القطع ببطلان هذا القياس، ولا التفات إلى صورة الاتفاق.
وقوله: إنه لا بد من إثبات علة الأصل، [يقال] له: وأين الأصل حتى تثبت علته؟ فآل الأمر إلى أنه فرع. فإن أجرى الكلام على أبواب الاستدلال، انقطع القياس بالكلية.
قال الإمام: (فإن قيل: أرأيتم (١٥١/ ب) لو أثبت المعلل الأنوثة علة) إلى قوله (فالصغر مستقل بالمنع). قال الشيخ: أما إثبات كون الأنوثة علة، فإنه يتأتى [بيانه] بالمناسبة الراجعة إلى مكارم الأخلاق، وأبواب','4','114',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2506,'التحسينات، وهو آخر مرتبة المناسبات، فإن المستحسن في العادات، واللائق بالمكارم والمروءات، أن لا تتولى المرأة عقد النكاح، إما بالنظر إلى ما يشعر به ذلك من شدة الشبق، وتمام الشهوة إلى النكاح، أو للخروج عن [ذوات] الخفرات. وبهذا علله بعض الأصوليين. وهذا المعنى ضعيف، فإن ذلك لا ينبغي أن يمنع من تولي [عقد النكاح] على غيرها. والصحيح أن ذلك إنما كان تفخيمًا لأمر النكاح، وتمييزه عن السفاح، ولذلك اشترط الإشهاد فيه. فقصد الشرع أن يتولاه من له منصب من حيث الجملة، إلحاقًا له بالولاية والقضاء، ولذلك قال: "لا نكاح إلا بولي". وكانت عائشة رضي الله عنها تقرر مهر من تحت حجرها، وتختار الأزواج، وإذا حضر العقد قالت: "اعقدوا فإن النساء لا يعقدن". وفي حديث أم سلمة: "أنها لما خطبها رسول الله - صلى الله عليه وسلم - واتفقا على النكاح، قالت لوالدها: قم فزوج رسول الله - صلى الله عليه وسلم - ". كذلك كانت سيرة العرب قبل مبعث رسول الله - صلى الله عليه وسلم - تخطب إلى الرجل كريمته: أخته أو ابنته،','4','115',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2507,'فيزوجها، ولا يتولى النساء ذلك. فلا يبعد وقوع هذا في أقسام المناسبات في الدرجة الثالثة. فهذه الصورة في التركيب في الأصل هي المتفاحشة.
قال الإمام: (صورة أخرى: إذا قلنا في تزويج [الأب] البكر: بكر يزوجها أبوها) إلى قوله (فيلغو تعيين بنت الخمس عشرة سنة). قال الشيخ: سبب الإجبار عند الشافعي البكارة، ولذلك قضى بأن [الثيب] الصغيرة لا يجبرها الأب. وسبب الإجبار عند أبي حنيفة الصغر، واتفق الفريقان على أن البكر إذا كانت بنت الخمس عشرة سنة أجبرت، أما الشافعي، فلأنها بكر عنده، وأبو حنيفة، فلأنها صغيرة عنده (١١٧/ ب). فإذا قاس الشافعي على ابنة','4','116',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2508,'الخمس عشرة سنة، وسلم الخصم ذلك، فإنما سلمه لاعتقاد صغرها. فإذا ثبت بلوغها، منع الحكم. ومن هذه الجهة تشبه الصورة الثانية الصورة الأولى. فإن ثبت صغرها، لم يصح لأبي حنيفة أن يقول للشافعي: العلة الصغر. وهذا هو مذهب أبي حنيفة.
وأما الشافعي، فإنه لا يرى الصغر علة، فيتصدى فيها منع الحكم على تقدير البلوغ، ولا تثبت العلة عنده على تقدير الصغر. وبهذا تنفصل هذه الصورة عن الصورة الأولى.
قال الإمام: (وأما التركيب في الوصف) إلى قوله (فالعلة بين منع','4','117',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2509,'وبطلان). قال الشيخ: قد قدمنا بيان منع التركيب في الأصل، وقلنا إنه مختص بما إذا كان الجامع بين الأصل والفرع حكمًا، تركب الحكم على المذهبين من علتين مختلفتين، كل واحد من الخصمين أثبته لعلة.
وبيانه: هو أن الشافعي وأبا حنيفة اتفقا على أن المسلم إذا قتل الذمي بالمثقل، لم يقتل به. واختلفا إذا قتله بالمحدد، فقال الشافعي: لا يقتل به، وقال أبو حنيفة: يقتل. وتحرير القياس أن يقال: من لا يستحق القصاص بقتل شخص بالمثقل، لا يستحقه بقتله بالمحدد، كما إذا قتل المسلم عبده، والأب ابنه، أو غير ذلك من صور الوفاق، فقد صار كونه لا يستحق القود، إذا قتله بالمثقل جامعًا.
وإنما اتفق على [هذا] الحكم لعلتين مختلفتين، فمنع الشافعي القصاص في هذه الصورة، لأن الدم لا يتكافأ، ومنع أبو حنيفة القود، لأنه لا يرى المثقل آلة، فكأنه عنده خطأ. فكان هذا القياس باطلًا عند المحققين.','4','118',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2510,'ومن سلمه يقول: الجامع مسلم، والمنازعة في علته، والمستدل مطالب بإقامة الدليل على التعيين، فإذا استدل على ما [يعينه] [استد] قياسه، ونحن نرده بناء على ما سبق، فإنه إذا ثبت أن المثقل آلة [القصاص]، منع الخصم الحكم الجامع، وإذا ثبت أنه من قبيل الخطأ، أبطل القياس.
قال الإمام: (وقد يجري في الوصف تركيب قريب) إلى قوله (فهذا منتهى القول فيما يصح ويبطل من التركيب [في الأصل والوصف]). قال الشيخ: مذهب أبي حنيفة أن الثمرة إذا لم تكن مأبورة وقت البيع، فهي باقية للبائع، كما لو كانت مأبورة. ومذهب مالك والشافعي أنها تكون للمشتري. وفي المسألة كلام يتعلق بالأخبار. والمقصود ها هنا ضرب المثال في القياس','4','119',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2511,'المركب الوصف، الذي لا يتفاحش تركيبه، ويصح التمسك به في [الجدل].
وصورته أن نقول: ما يستحقه الشفيع من الشجرة يندرج تحت مطلق العقد كالأغصان، فالجامع بين الفرع والأصل استحقاق الشفيع، والأصل الأغصان، والفرع الثمرة التي لم تؤبر في حال الشراء، [والجامع] استحقاق الشفيع، والحكم المتنازع في الاندراج تحت مطلق التسمية، ويثبت الاتفاق على استحقاق الشفيع الثمرة التي لم تؤبر. إن الشافعي (١١٨/ أ) يرى أنها في معنى جزء الشجرة، وأبو حنيفة يقول إنما أخذها الشفيع، بناء على دفع ضرر المداخلة. وهذا الذي قاله ضعيف، وبيان ضعفه من وجهين:
أحدهما- ان الشفعة في أصلها لا تتعلق بالمنقولات، فأشعر أخذ الشفيع بكونها معدودة (١٥٢/ ب) من أجزاء الشجرة.
الوجه الثاني- أنه لو كان الأخذ [لأجل] [قطع] ضرر المداخلة من غير اعتبار كون المشتري عقارًا، لكان ثبوت الشفعة في الجواري اللاتي يردن للتسري أولى. فلم يصح لأبي حنيفة الاعتماد في الأخذ على مجرد ضرر','4','120',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2512,'[المداخلة]، وتعلق به غرض المسألة تعلقًا بينًا. فالمعنى صحيح، والمناسبة كما يريد واضحة، ومعناه باطل على السبر.
وسببه مناقضتهم في قطع النظر عن [الصورة] في بعض الصور. وهذا التركيب لا يقدح في صحة الاعتماد في الجدل، وهو بمثابة التعلق بمناقضة الخصم، فإنه يفتح باب النظر، ويفضي إلى التدرب في استعمال الأدلة. وليس هذا من قبيل ما تقدم، فإن الأمثلة السابقة لم يتمسك [القائس] فيها بفقه، وإنما جعل عمدته على غلط الخصم، كغلط يتفق في سن البلوغ، [وآلة] القود. واعتبار القصاص في النفس [بالقصاص] في الطرف اعتبار حسن، فإن','4','121',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2513,'تفاوت الحرمة مشعر بمنع القصاص، وللمسلم من الحرمة ما ليس للكافر، فكان [إسقاط] القصاص في الأطراف مستندًا لذلك. فإذا ذهبوا يخبطون في الأطراف، وقالوا: إنها تقتضي التبعيض والتقويم، ولا يستوي الكامل [من] الناقص، كان ذلك من فساد نظرهم، وسوء تصرفهم، ثم كما [تتفاوت] [قيم] الأطراف، فكذلك [تتفاوت] [قيم] النفوس في غير موضع الديات المقدرة، أعني قيم العبيد. فهذه التركيبات لا تمنع من الاعتماد على أصولها، لما قررناه من إشعار الجوامع بالفقه، وحصول الاتفاق من الخصمين','4','122',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2514,'على الحكم، ولم يكن هذا من القبيل الأول، لعدم الفقه بالكلية، ورجوع الأمر إلى محض التلبيس والغلط.
قال الإمام: (ثم ضري أهل الزمان بفن من الكلام) إلى قوله (وهذا يطرد للمعدي في الصغيرة الثيب التي يتفق على صغرها). قال الشيخ: هذا النوع من الكلام لا أعلم وجهًا لدخوله في الجدال، وأي مقصود يترتب عليه؟ وبأي وجه يُعد من الاعتراضات؟ وحاصل التعدية أن كل واحد من الخصمين','4','123',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2515,'[رتب] على علته ما يليق بها من الأحكام، وليس ترتيب التفريع على التعليل بالذي يقوي الدليل، ولا يؤثر فيه. اللهم إلا أن يكون [مقصود] المعدي أن يبين لخصمه أن الذي أبداه من المعنى هو يعتقده، ولا يورده إيراد المدافعة والمجادلة، فيبين أن مذهبه مبني على ذلك. وهذا أمر قريب وخطب يسير، والأمر في الدين يغني عن ذلك، ويقتضي تصديق المعلل فيما [يذكره]، ولا ينزل الأمر على المراغمة في الجدال، فإن ذلك حرام، [وليس] من (١٥٣/ أ) الدين بحال. فتبين أن التعدية لا حاجة إليها، [وليست] من قبيل الاعتراضات، (١١٨/ ب) ولا من وجوه الاستدلالات.
قال الإمام: (ثم تكلم أصحاب التركيب على التعدية) إلى قوله (فهذا','4','124',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2516,'مضطرب المركبين والمعدين). قال الشيخ: قد قدمنا أن التعدية لا معنى لها بحال، ولا [تصح] اعتراضًا ولا دليلًا، ولا حاجة إليها بوجه، وقصارى أمرها: تحقيق أن الخصم فيما أبداه من المعنى المناقض لمعنى المستدل هو معتقده، وهذا قليل النيل، فلا معنى لإطالة القول فيه، ويوجه البحث عنه،','4','125',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2517,'والمنع تارة والتسليم أخرى، [فليشتغل] بغير ذلك مما هو مفيد.
قال الإمام: (وقد بان أصلنا فيما نقبله ونرده في تركيب الأصل والوصف) إلى آخره. قال الشيخ: هذا التقسيم الذي ذكره الإمام تقسيم','4','126',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2518,'حسن، أما التركيب الذي لا يجوز التمسك به بحال، فهو الذي لم يبق على فقه، ولا على شبه، وإنما يرجع إلى محض [ألفاظ]، كقولنا في بنت الخمس عشرة سنة: إنها صغيرة. وأما التركيب الذي يسوغ التمسك به في الجدال، ويُعد أصلًا فيما بين المتناظرين، حتى يبدي الخصم السبب الذي سلم الحكم لأجله،','4','127',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2519,'ويصير ذلك مضاهيًا للتعلق بمناقضة الخصم في المناظرة، فإنه صحيح، والخوض فيه يفتح باب الفقه.
وأما الذي يجوز التعلق به في الدين، فهو أن [يكون] الحكم ثابتًا إجماعًا. فهذا لا يضر [التركيب فيه] بحال. وإنما [يضر] التركيب فيما نقله من جهة أن الأمر دائر بين منع الحكم على تقدير، وبين منع العلة على تقدير. وإذا كان الحكم [مجمعًا] عليه، امتنع منعه، والتحق بالأصول','4','128',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2520,'الصحيحة، ولم يبق فيه إلا المنازعة في علة الأصل، وذلك [يمنع] الإلحاق بالاتفاق.
قال الإمام: (القول في الاستدلال) إلى قوله (وهو في الحقيقة خروج عما درج عليه الأولون). قال الشيخ: يفتقر في هذا المكان إلى تفسير الاستدلال، وبيان موضع الخلاف.','4','129',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2521,'أما الاستدلال: [فيعبر عنه: بربط] الحكم بالمعنى المناسب، الذي لا يستند إلى أصل معين. هذا تفسيره. وللعلماء فيه مذاهب، ذكر [الإمام] ثلاثة منها، ولم [يذكر] الرابع. فلا بد من ذكره.','4','130',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2522,'والمذهب الرابع: ما ذهب إليه أبو حامد، وهو أن المناسب الذي لا يشهد له أصل معين، إن كان واقعًا في الرتبة الأخيرة من رتب المناسبات، لم يقبل. واختلف قوله في الواقع في الرتبة المتوسطة، وهي رتبة الحاجات، فقبله في "شفاء الغليل"، ورده في "المستصفى". [وهو] آخر مصنفاته','4','131',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2523,'(١٥٣/ ب). فهي في محل الاجتهاد. ولا بُعد في أن يؤدي إليه اجتهاد مجتهد، وميله إلى قبوله. [ولكنه اشترط] فيه شروطًا لا يتأتى الوفاء بها، وكلامه [فيها] يطول، فلنؤخره إلى آخر الفصل، إن شاء الله تعالى. وقد ذكرنا مأخذ القاضي.
قال الإمام: (أما الشافعي فإنه قال: [أنا أعلم] قطعًا) إلى قوله (ويكتفى [أن لا]) يكن في الشريعة أصل يدرؤه من نص كتاب أو سنة أو','4','132',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2524,'إجماع). قال الشيخ: ما ذكره القاضي حيث (١١٩/ أ) حاول رد الاستدلال من حصر طرق الأدلة، وإبانة انتفائها، فالحصر غير مسلم، وفقدانها ممنوع، وإذا نظر المنصف غلى أقضية الصحابة - رضي الله عنهم - تبين منهم أنهم كانوا يتعلقون بالمصالح في وجوه الرأي، وظهر ذلك منهم في مواضع:
منها- حد شارب الخمر، فإنهم لم يصادفوا في المسألة نصًّا، وإنما أثبتوا الحد في المسألة رأيًا. وسنبين هذا بعد ذلك أتم البيان.
ومنها- أن الخلفاء الراشدين قضوا بتضمين الصناع، قال علي - رضي الله عنه -: "لا يصلح العامة إلا ذلك". [وهذه] أمور مفهومة من منع الرسول [- صلى الله عليه وسلم -]','4','133',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2525,'من تلقي السلع حتى يهبط بها الأسواق. ومن المنع أن يبيع حاضر لباد. وغير ذلك من [تفاريع] أدلة دلت على التفات الشريعة [إلى المصالح] في أثناء الأحكام.
فأما ما ذكره من انتفاء دليل العمل بالاستدلال دليل على رده، فهذا إنما يكون [أن] لو سلم له دليل انتفاء العمل بالاستدلال.
وأما قوله: إن المعاني إذا اعتبرت بمجردها لم ينضبط الأمر، وأفضى','4','134',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2526,'ذلك إلى الاختلاف والتباين. فعنه جوابان:
أحدهما-[أن لا أحد] يجوز اتباع المصلحة المجردة، بل المصالح التي فهم من الشريعة ملاحظتها.
وقد قدمنا أن مقصد الشر عان يحفظ على الخلق خمسة أمور وهي: الدين والنفس والعقل والمال [والنسل]، فحفظ هذه الأمور مصلحة، وتفويتها مفسدة، فإذا لحظ العلماء هذه الأصول، لم يتباعد اختلافهم أصلًا.
والأمر الثاني- أن هذا وإن أفضى إلى الاختلاف، فلا يضر، وليس الاختلاف [مقصودًا] لعينه، واختلاف هذه الأمة رحمة من الله تعالى، سواء [قلنا إن] كل مجتهد مصيب، أو المصيب واحد، والاختلاف أيضًا لا يُعدم، وإن ردت المعاني إلى الأصول، وما كان يزيل الاختلاف إلا النصوص القاطعة المتواترة، ولم يفعل [الله] تعالى ذلك.','4','135',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2527,'وأما قوله: إنه كان يلزم أن تختلف الأحكام [باختلاف الأصقاع] والبقاع وأحوال المكلفين. فما ذكره ينقسم قسمين: أحدهما- أعرضت (١٥٤/ أ) الشريعة عنه، فهذا لا سبيل إليه.
والثاني- أمر لم يتحقق الإضراب عنه، فهذا عندنا تختلف به الأحكام، إن أفضت المصلحة إليه. وقد قال مالك [رحمه الله] في قوله في لبن المصراة: "ردها وصاعًا من تمر": هذا لم عيشهم التمر، وأما من عيشهم غير ذلك، فيجزئ عنهم. وكذلك القول في زكاة الفطر والكفارات، في','4','136',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2528,'اختلاف الأحوال والبلدان.
أما قوله: إنه كان يتسع الأمر، ويرجع ذوو الأرحام بمثابة الأنبياء عليهم السلام، [فهذا] تهويل ليس وراءه تحصيل، والعلماء ورثة الأنبياء، وما كان النبي يذكر إلا ما فهمه عن ربه [تعالى].
وإذا جعل الله تعالى للعلماء أن يحكموا بما يرونه صوابًا، محصلًا لمقصود الشارع، لم يكن لهم مجاوزة ذلك ولا تعديه، وكانوا إنما حكموا بحكم الله تعالى. ألا ترى أنهم إذا استنبطوا المعاني من الأصول، فإنهم','4','137',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2529,'بعقولهم أدركوا ذلك، ولا ينسب الحكم غليهم بحال، فأي فرق بين أن يفهموا (١١٩/ ب) المصالح من أصول معينة، وبين أن يفهموها مستندين إلى [كل] الشريعة؟
وكذلك قوله: إنه مصير إلى إبطال [أبهة] الشريعة، وذهاب إلى أن يفعل كل ما يريد. أما إبطال [أبهة] الشريعة، فغير صحيح، لأنهم إنما فعلوا ذلك مستندين إلى الشريعة.
وأما إفضاء الأمر إلى أن كلًا يفعل ما يرى، فهو كذلك، ولكنه حكم [الله] عليهم، وقد قال معاذ حبر الأمة: "أحكم بكتاب [الله]. فقال له الرسول - صلى الله عليه وسلم -: فإن لم تجد؟ قال: بسنة رسول الله - صلى الله عليه وسلم -. قال: فإن لم تجد؟','4','138',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2530,'قال: أجتهد رأيي. فقال رسول الله - صلى الله عليه وسلم -: "الحمد لله الذي وفق رسول رسول الله - صلى الله عليه وسلم - لما يرضاه رسول الله [- صلى الله عليه وسلم -] ". فكيف يكون المصير إلى أن كل واحد يحكم بما يرى، حيدًا عن الشريعة؟ وبالشريعة [عملوا] وإياها قصدوا، وفي مقاصدها اجتهدوا.
وأما الطريق الذي سلكه الشافعي في إعمال الاستدلال من [بنائه] القول على [أنه لا] تخلو واقعة عن حكم الله تعالى، فبناء على أصل عظيم، ودعوته [عرية] [عن] الدليل.
وما ذكره من أن الأئمة السابقين لم يخلو واقعة عن الحكم، مصيرًا منهم','4','139',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2531,'إلى أنه لا حكم فيها، فهذه أمور عظيمة، ودعاوى [يشق] الاستدلال عليها، ويفتقر في تقرير هذا الكلام إلى النقل المتواتر عن جميع أهل الإجماع، بأن كل واقعة فلله تعالى فيها حكم. وهذه دعوى لا تصادف أبدًا، والشافعي أيضًا لم يدعها، وإنما أخذها استقراء من كثرة الوقائع، وتعميم (١٥٤/ ب) الفتاوى، فيقول: قد اشتمل عصر الصحابة [- رضي الله عنهم -] على كل ممكن، فلسنا نتخيل وقوع واقعة لم تقع لهم. ثم يقول: وقد حكموا في كل ما لهم، فترتب على ذلك تعميم الأحكام للوقائع. وهذا كلام مرتب على مقدمتين كلاهما ممنوعة.
أما المصير إلى أن كل واقعة وقعت للصحابة، فممنوع، والمسألة التي أوردها أبو هاشم، [وهو] إذا سقط رجل على صدر جريح محفوف بجرحى، إلى آخر المسألة. قال الأئمة: هذه المسألة ليست منصوصة، ولا عهد بمثلها في المنصوصات. قال أبو حامد: ولا يبعد خلو واقعة عن الحكم.
وأما [كونهم] أفتوا في جميع ما وقع لهم، فالمنقول خلافه، وقد نقل','4','140',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2532,'الوقف عن أكثر الصحابة [أو عن] جميعهم، غايته أن يقول: الواقف وقفه وقف عاجز، مع كونه يرى في المسألة حكمًا من حيث الجملة، ولذلك كان يحيل الأمر على غيره. وهذه دعوى يقابلها عكسها، وهو أنه يحتمل أن يقف وقف من لا يرى وراءه [مرمى]، ويحتمل أن يقف [وقف] من يضيف العجز إليه، ويرى أن غيره قادر، ويحتمل أن يقف وقف شاك، هل وراءه مرمى [أم لا]؟ فمن أين [تحقق] أنه إنما وقف وقف عاجز مع كون غيره متمكنًا؟ هذا لا يعرف إلا من قوله: ولم ينقل، ولو نقل هذا عن واحد، فيفتقر إلى أن ينقل هذا عن الجميع نقلًا متواترًا، نقل الآحاد في الأبواب مفقود، فضلًا عن التواتر. فبناء مسألة الاستدلال على هذه الأصول، صعب شديد، وإذا لم يثبت ذلك بالدليل، لم يعلم ما ينبني عليه.
وأما الكلام الثاني: وهو أنه لو لم [يعمل] بالاستدلال، لكان الوقوف','4','141',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2533,'عن الحكم يزيد على الفتوى. فهذا لعمري يفتقر إلى تقرير عظيم، وهو أن تضبط المعاني المستندة (١٢٠/ أ) إلى الأصول، ثم تعرض الأحكام المفصلة عليها، فإذا صودفت زائدة زيادة بينة، بحيث يحصل العلم بأنه لا بد من مأخذ زائد على المعاني ذوات الأصول، علم الافتقار إلى مستند آخر. وانحصاره في الاستدلال المرسل، يفتقر إلى زيادة تقرير، وهو أن تكون تلك المسائل التي حكم فيها لا تستند إلى الأشباه، ولا يصح أن ترجع إلى استصحاب الحال، [لوجود] أحكامها مختلفة، وظهرت فيها مصالح، فحينئذ يثبت الاستدلال، ولكن هذا بشرط أن ينقل ذلك عن جميع أهل الإجماع. وهذه أمور إنما يصح أن يتصرف فيها المجتهدون، وإلا فقد يظهر للناظر أن الأصل [لا معنى] له، ويتضح لغيره تقدير معناه. (١٥٥/ أ) فسبحان الله ما أعز درجة الاجتهاد، وما أبعدها عن الخلق.
وأما قوله: إن كان التمسك بأقضية الصحابة، وهم القدوة والأسوة، فمن نظر أحكامهم، لم يجد لواحد منهم تعيين أصل، والاعتناء بالاستنباط منه، ثم','4','142',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2534,'بناء الواقعة عليه، وإنما كانوا [ينيطون] الأحكام بالمصالح، ولا يعرجون على الأصول، كانت أو لم تكن.
وهذا نقل، لو كان صحيحًا منقولًا على ألسنة التواتر، لكان دليلًا قاطعًا في قبول الاستدلال. ولكن مَن علمه، وجب عليه العمل به، والظن بالشافعي [أنه لا] يقوله إلا عن تحقيق. هذا إذا ثبت أنهم ما كانوا يعرجون على الأصول. وأما إذا كان مستند النقل عدم التصريح، فهذا غير دال، لاحتمال أنها كانت [معلومة] عندهم، فاستغنوا عن [التصريح]. وعلى هذا نزل القاضي رحمه الله اختلاف علي وعبد الرحمن، وقد تقدم ذلك.','4','143',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2535,'وأما ما ذكره الشافعي أخيرًا، من أن المعاني إذا استندت إلى الأصول، فالتمسك بها جائز، فعبارة ضعيفة، بل إذا استندت المعاني إلى الأصول، فالتمسك بها واجب. وليس الأصول وأحكامها حججًا، وإنما الحجة في المعاني، فهذا كلام صحيح، وليس التمسك بصورة الأصل، ولا [يحصل] الوفاق عليه، وإنما التمسك بالمعنى، كان الأصل أو لم يكن.
فهذا عندنا كلام صحيح، وهو يقتضي الالتفات إلى المعاني على الإطلاق، على حسب ما أسند إلى مالك [رحمة الله عليه].
وما طرده القاضي على الشافعي من الاعتراضات، فقد أجبنا عنها، فلا معنى للإعادة.
[وأما] ما أضافه الإمام إلى مالك [رحمة الله عليه]، واعتنى بالرد عليه، [فقد قدمنا] أن مذهب مالك هو مذهب الشافعي بعينه، وإلزامه اختلاف الأحكام باختلاف الأشخاص والبقاع والأوقات، فقد أجبنا عنه بما فيه','4','144',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2536,'مقنع. وكذلك قوله: فيصير ذوو الأحلام بمثابة الأنبياء عليهم السلام، هو الذي سبق بعينه.
وأما الزيادة التي أوردها الإمام ردًّا بها على مالك [رحمه الله]، [وهي] أن يكون العاقل العالم بوجوه السياسات، إذا [راجع] العلماء في واقعة، فأعلموه أنها ليست منصوصة في كتاب ولا سنة، ولا أصل لها يدانيها، فأنى للعاقل أن يحكم فيها بعقله؟ فهذا من مورده وملزمه غفلة عظيمة. وبيان الغلط فيه من وجهين:
أحدهما- أنا نشترط في اتباع المصلحة ألا تناقض أمرًا مفهومًا من الشريعة، ومن أين يعلم العامي هذا؟ وما المانع من كون ما يراه من الرأي مناقضًا للشرع؟
الأمر الثاني- (١٥٥/ ب) أنا قلنا: لسنا (١٢٠/ ب) نريد بالمصلحة في هذا المكان: مجرد جلب المنفعة ودفع المضرة، وإنما نريد بها: المحافظة على رعاية مقصود الشرع، وهذا إنما يعرفه العلماء دون العوام، ولا يتصور لذي عقل أن يُمكن العامي من الفتوى في الشريعة.
وعلى الجملة فليس بين مذهب مالك والشافعي فرق بوجه، وأما الإمام فإنه يقصد أن يفرق بين المذهبين، وهو لا يجد إلى ذلك سبيلًا أبدًا.
قال الإمام: (فإن قيل: فما معنى التقريب الذي نسبتموه إلى الشافعي) إلى','4','145',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2537,'آخره. قال الشيخ: هذا الذي ذكره الإمام في جواب السائل، [لا يتحصل] منه المقصود على حال، وذلك أنه قال: تثبت معان اتفق القائسون عليها، فأنا أجعلها معتبري، وأقرب الاستدلالات منها حتى كأنها هي، وإن لم تكن أعيانها. فهذا التقريب ما مأخذه؟ وما المراد به؟ وفي أي جهة يشترط [التقارب]؟ أفي مجرد المصلحة؟ أم في وجه آخر أقرب من ذلك؟ و [ما] مأخذ هذا التقريب؟ فليس في هذا الكلام شيء من البيان، فإن اكتفى بمجرد [التقارب] في المصلحة، أعملت جميع المصالح، وإن اشترط الاشتراك في الوجه الأخص، فهو المؤثر بعينه، وبين الدرجتين رُتب [متفاوتة] فيلا القرب والبعد لا تنضبط بحال.','4','146',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2538,'[وأما] قوله: فاعتبار المعنى بالمعنى تقريبًا، أولى من اعتبار صورة بصورة. وهذا أيضًا ليس بشيء. [فإذا] قربنا الصورة بالصورة، نظرًا منا إلى الاشتراك في المعنى، فإذا أردنا أن نقرب المعنى من المعنى، فبأي شيء نقربه منه؟ فتحصل أن هذا الكلام ليس فيه شيء من البيان، والسؤال [عن الفرق] بين المذهبين قائم. هذا تمام ما أورده الإمام.
فأما ما ذهب إليه أبو حامد في قبول الاستدلال، فهو أنه خص القبول بالضرب الأول من رُتب المصالح، وقال: المقبول منه ما وقع في رتبة الضرورات، واقتصر [في هذا المكان على الدعوى]، ولم يدل على حصر القبول فيما عين بحال، وذكر مثالًا فقال: إذا تترس الكفار بالمسلمين، [فلو] رمينا الترس، لقتلنا مسلمًا معصومًا، لم يذنب ذنبًا. وهذا لا عهد [به] في الشرع، ولو كففنا، لسلطنا الكفار على جميع المسلمين، فيقتلونهم، ثم يقتلون','4','147',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2539,'الترس. فيجوز أن يقول قائل: هذا الأسير [لا بد أن يصير] [مقتولًا] بكل حال، وحفظ جميع المسلمين عن القتل أولى من حفظ حياة الترس ساعة واحدة، ثم يعودون إليه فيقتلونه، فحفظ جميع المسلمين أهم، لأنه يعلم قطعًا (١٥٦/ أ) قصد الشرع إلى حقن دماء المسلمين، إن قدر عليه، وإن عجزنا عنه، فقد قدرنا على التعليل، فيكون هذا أقرب إلى حفظ مقصود الشرع. فكان هذا التفاتًا غلى مصلحة عُهد من الشرع قطعًا الالتفات غليها، لا بدليل واحد، وأصل معين، بل بأدلة لا تحصى. هذا كلامه. وكله دعوى [معرى] عن البرهان. والموضع الذي علم أنه مقصود الشرع، صون الدماء من غير إراقة دم [مسلم] معصوم، [فأما ما] في هذه الصورة، فلم يعلم ذلك بحال، وليس في (١٢١/ أ) أدلة [الشريعة] ما يدل عليه التحقيق.
قال: وانقدح اعتبارها بثلاثة شروط: أنها ضرورية قطعية كلية، وليس في معناها ما إذا تترس الكفار في قلعة بمسلم، إذ لا يحل رمي [المتر سبه]، إذ لا ضرورة، [لأن بنا] غنية عن القلعة، [فنعدل] عنها، وليس في معناها ما إذا [لم نقطع] [بظفرهم بنا]، لأنها ليست قطعية، بل ظنية، وليس','4','148',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2540,'في معناها [جماعة] في سفينة، لو طرحوا واحدًا [لنجوا]، وإلا غرقوا بجملتهم، لأنها ليست كلية، إذ يحصل [بها] هلاك عدد محصور، وليس ذلك كاستئصال كافة المسلمين. ولأنه ليس يتعين واحد إلا أن يُعين بالقرعة، ولا أصل لها.
وكذلك جماعة في [مخمصة] لو أكلوا واحدًا بالقرعة، [لنجوا]، فلا رخصة فيه، لأن المصلحة ليست كلية، وليس في معناها قطع اليد للآكلة، حفظًا [للروح]، فإنه تنقدح الرخصة فيه، لأنه إضرار به لمصلحته، وقد شهد الشرع للإضرار [بالشخص]، قصدًا لإصلاحه، كالفصد والحجامة وغيره. وكذلك قطع المضطر سببًا ظاهرًا في الهلاك، فيمنع منه، لأنه ليس فيه [يقين الخلاص]، فلا تكون المصلحة قطعية.
فنقول: هذا تمام [كلامه]. وهو عندنا غير صحيح، واقتصر في [جميع] ما [ذكره] على الدعوى من غير دليل.','4','149',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2541,'أما مصيره إلى أنها إذا كانت من قبيل الضروريات، فتثبت دون غيرها، فلم يذكر دليلًا على الفرق بحال.
وأما اعتباره القيود الثلاثة، [فهذا] أمر لا يتصور، ولا وقوع له في الشريعة أصلًا. ورد الحكم بالمصالح المرسلة التي يزعم الشافعي أنه الذي يسترسل على أكثر المسائل، هو هذا النوع من الاستدلال، كيف يتصور مع ذلك أن تعتبر فيه هذه الشروط، ولم تقع في الوجود هذه الواقعة؟ فهذه صورة تجري في أدراج الوسواس، يضعها المتكلفون، والشريعة عرية [عنها].
[ثم] (١٥٦/ ب) لو تصورت على زعمه، فما دليل جواز قتل الترس؟ وكيف يجوز الهجوم على قتله؟ ومن أين نعلم أنا إذا لم نقتله استؤصلت شأفة المسلمين؟ ودعوى أنهم إذا [قتلونا] عادوا إليه فقتلوه، وهم محض، وخيال باطل، ولعلهم لا يقتلونه.
ثم قوله بعد ذلك: [لحفظ] دماء المسلمين أهم من بقاء هذا ساعة أو يومًا، ثم يعودون إليه فيقتلونه. كل هذا وهم محض، وخيال باطل. وسنبين في المسألة التي تلي هذه، أمثلة تبين للناظر الموفق أن الصحابة - رضي الله عنهم - في إعمال الاستدلال المرسل، لم يشترطوا شيئًا من هذه الشروط، ولم يعولوا عليها بحال.
فإذًا الصحيح اعتبار المصالح على حسب ما قررناه، إذا لم تناقض الأصول، ولم يوجد في الشرع ما يصد عنها، وخلت عن المعارض. [فتحصل] من هذا الذي ذكرناه أن المناسب باعتبار شهادة الشرع ينقسم: إلى ما شهد الشرع لاعتبار','4','150',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2542,'عينه، فهو حجة باتفاق القائسين، ومناسب (١٢١ م ب) رده الشرع، وأعرض عنه، فهو مردود باتفاق المسلمين، ومناسب لم يشهد الشرع لاعتباره، ولا لرده في عينه، فهذا قد قسمه بعض الناس قسمين: [ملائم وغريب].
فأما الغريب، فلا يخلو: إما أن يرد حكم على وفقه، أو لا يرد، فإن ورد على وفقه، فهو مختلف في قبوله، ومستند الاختلاف، هل ورود الحكم على الوفق شهادة له أم لا؟ فإن كانت شهادة فقد ترجح جانب الإعمال، وغن لم تكن شهادة، ترجح جانب الإهمال، إذ ليس المناسب مقبولًا لعينه.
وأما الملائم، فإن ورد حكم على وفقه، فهو حجة باتفاق القائسين. وذكر عن أبي زيد أنه لا يقبل غلا المؤثر، ولكن ذكر أمثلة تدل على أنه قبل الملائم وسماه مؤثرًا. [وما] أقل ما يصادف المناسب الغريب، فإنه إذا تحققت المناسبة، لم تنفك عن مدناة ومقاربة، وهو المعبر عنه بالملاءمة. وسنذكر أمثلة كثيرة نبين بها اعتماد العلماء على المناسبة [التي] لا ترجع غلى أصل معين. إن شاء الله تعالى.

قال الإمام: <span data-type="title" id=toc-462>(مسألة: الرجعية محرمة الوطء عند الشافعي) </span>إلى قوله ([سرًّا بديعًا يتخذه الناظر معبرًا في أمثاله]). قال الشيخ: [لما] ذكر الإمام مسألة','4','151',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2543,'الرجعية، ممثلًا بها صحة التمسك بالمعاني التي لا أصول لها، نازعه المنازع،','4','152',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2544,'وزعم أن تحريم الرجعية يعتمد على معنى راجع إلى أصل، فيكون من أبواب القايس، لا من أبواب الاستدلال المرسل، فألحقه بالمعتدة البائن، ويجعل (١٥٧/ أ) الاعتداد علة، ويجعل البائن محلًا، ويلحق الرجعية به، فينتظم القياس من أصل وفرع وجامع وحكم. فإن قيل له: البينونة علة، [قال]: لا','4','153',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2545,'يمتنع اجتماع علتين [لحكم] واحد، فيرجع الكلام إلى ما سبق من تعليل الحكم بعلتين، والقول فيه طويل، وقد استقصيناه، فلا معنى لإعادته.','4','154',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2546,'قال الإمام: (فإن قال قائل: إنما يستقيم ما ذكرتموه من تجريد النظر إلى العدة) إلى قوله (ولا [مستند للتعلق] بالعدة في اقتضاء التحريم [إلا الاستدلال]). قال الشيخ: قد قدمنا أنه إذا ذكر الجامع وصفًا مضمونًا إلى علة الأصل، لا يستقل علة، وعلة الأصل تستقل دونه، كان ذلك باطلًا. وقد قدمنا في ذلك أمثلة: منها- ما إذا قال الشافعي: أمة كافرة، فوجب أن تكون محرمة، كالأمة المجوسية. وكذلك قوله: مس، فصار كما لو مس وبال، وبينا','4','155',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2547,'أن هذا القياس باطل على القطع، فإن علة الأصل غير موجودة في الفرع، والذي في الفرع لم يثبت كونه علة، والنزاع فيه، [فإن] قدر المستدل على إثبات كونه علة، تعذر عليه أن يشهد الأصل له، فلو ثبت ذلك، لوقع الكلام في أبواب الاستدلال. فكذلك إذا قال: معتدة فحرمت، كالمعتدة البائنة، فإن البينونة مستقلة بالمنع، معتدة كانت أو غير معتدة، وكونها معدة، هو المتنازع فيه، وهو منضم إلى البينونة المستقلة بإثارة التحريم، [فهو] من قبيل القياس على ما لو مس وبال.
وأما المصير إلى أن هذا ينبني على القول بتجويز تعليل الحكم بعلتين أو امتناع ذلك، فغير صحيح، (١٢٢/ أ) وذلك أنه لا يلزم من صحة إحدى العلتين أو فسادها، إبطال حكم الأصل، ولا منعه، ولو منعت البينونة ها هنا، أو قدر انتفاؤها، لمنع الحكم، فمن هذه الجهة كان ما يعتقد أصلًا، هو مسألة الخلاف، فيقع الكلام على هذا في أبواب الاستدلال.
قال الإمام: (فإن [قيل: لو] قال من يحرم الرجعية: معتدة فشابهت','4','156',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2548,'المعتدة عن وطء شبهة) إلى قوله ([إن] عول على العدة، لم يجد أصلًا). قال الشيخ: هذا الكلام هو أقرب من الأول، وذلك أنا قد بينا في الأول أن الحكم في الأصل ثابت [العلة، متفقًا] عليها، وهي البينونة، والاعتداد لا أثر له في الانضمام، فوقع ذلك في باب القياس، على ما لو مس وبال، [فعدل] الخصم إلى هذا السؤال، وهو قوله: معتدة فكانت محرمة، كالمعتدة عن وطء شبهة طارئ على النكاح، إذ العلة في الأصل متحدة، وهي كونها معتدة، ويرى أن كونها عن وطء شبهة، لا أثر له في التحريم، وإنما ذكر ذلك','4','157',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2549,'محلًا محضًا، [كذكر] الخمر [١٥٧/ ب) عند إلحاق النبيذ بها. هذا مراد الجامع من هذا القياس.
[وقوله]: هذا مطرد، ولكنه طردي، فنقول: قوله قبل هذا: والعلة الأولى فيها إخالة ربط حكم أو حكمين متماثلين بعلتين، قصد بذلك اختلاف الأصوليين في التعبير عن علة الأصل وحكمه، هل يقال: علة الأصل موجودة في الفرع أو مثلها؟ وكذلك، هل الثابت في الفرع حكم الأصل أو مثله؟ والمقصود واحد، والعبارة مختلفة، وليس الثابت في الفرع عين علة','4','158',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2550,'الأصل ولا مثلها. والذي يحقق ذلك، أن العدة عن الغير تمنع ابتداء النكاح لغير من منه العدة، ولو كانت العدة من ماء الزوج، ولم تقع البينونة الكبرى، لما امتنع [عن] الزوج النكاح، فدل هذا على اختلاف أحكام العدد وآثارها، وإن شملها اسم واحد. [فإذا] اعتمد على اسم العلة، كان [طاردًا]، وإن التفت إلى الفرع، لم يجد أصلًا.
قال الإمام: (فإن قيل: فما رأيكم في استعمال ذلك استدلالًا) إلى قوله (فليكن الوطء كذلك). قال الشيخ: هذا الكلام هو عمدة الشافعي في تحريم الرجعية، فغنه قال: [هي مأمورة] بالتربص لبراءة الرحم، فتسليط','4','159',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2551,'الزوج على شغل رحمها في الزمان الذي [تؤمر] بتبرئة الرحم متناقض. وهذا الذي ذكره قريب من القواعد، إذ ليس في الشريعة الأمر بتحصيل أسباب، ثم [شرعيته مما] يبطل أثر تلك الأسباب. إذ يفضي ذلك إلى شرع ما لا فائدة في شرعه. ولكن هذا الكلام يفتقر إلى الإتمام بالاستدلال على بطلان إثبات كون الوطء رجعة، فإذا ثبت ذلك، استقل الكلام وحصل المقصود.
قال الإمام: (فإن قيل: فما الرأي في قول من يتمسك بالاحتساب بالعدة؟ ) إلى قوله (فهذا وجه الكلام). قال الشيخ: وجه الاستدلال ما تقرر','4','160',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2552,'في الشرع من الربط بين إباحة الوطء، وبين الإعراض عن الأقراء، فإنها لو اعتزلت دهرًا طويلًا، لم تكن الأقراء موصلة إلى بينونة، وما ذاك إلا (١٢٢/ ب) لأنها مباحة. فلا يجتمع الاحتساب بالأقراء مع إباحة الوطء.
وقول الإمام: هذا أمثل قليلًا، يريد بذلك أنه استدلال بالتلازم، والأصل المعبر به ما قبل الطلاق، فهو جار على شرط القياس الخالي عن التركيب.
ومعنى قوله: إنه تمسك بالعكس، أي لما كانت مباحة، لم [تحتسب]، وإذا [احتسبت]، [فينبغي] ألا تكون مباحة، [وهو] الاستدلال بنفي أحد المتلازمين على انتفاء الآخر.
وقوله: وجواب الخصم (٥٨/ أ) عنه أوضح منه، يريد إن التربص قبل الطلاق لا يفضي إلى البينونة، فلم يكن للاحتساب بالأقراء معنى، والاعتداد بعد الطرق في الممسوسة يؤدي إلى البينونة، فكان الفعل بين ما قبل الطلاق وبعهد. هذا، وهو كلام واضح في الفرق.','4','161',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2553,'قال الإمام: (فإن تعلق المحرم بأن الطلاق أوجب المصير غلى البينونة) إلى قوله (فلم ينصب دليلًا عليه بعد). قال الشيخ: هذا الكلام صحيح في وضعه، لكن على أصل مالك [رحمه الله]، فإن حل النكاح لا بد أن يكون مطلقًا، والتأقيت يبطل النكاح، [ويعتمد] في ذلك على بطلان نكاح المتعة، فإنه باطل، وسبب ذلك أن مقصود النكاح الائتلاف، وحسن الصحبة والعشرة، قال الله [تعالى]: {ومن آياته أن خلق لكم من أنفسكم أزواجًا لتسكنوا إليها وجعل بينكم مودة ورحمة}. وهذا ينتقض بالتأقيت، فأثبت الشرع النكاح مؤبدًا، والتأبيد يجر جهالة، ولكن هذه الجهالة منطبقة على مقصود العقد. فإذا علق الطلاق على أجل، [فإنه] جعل التأقيت ينافي مقصود النكاح. هذا مذهب مالك [رحمه الله]، فيصح أن تحرم المعتدة، بناء على هذا الأصل','4','162',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2554,'الذي قرره. وأما الشافعي، فلا يتأتى له ذلك، لأنه يفرق بين أن يعقد عقد النكاح مؤقتًا، وبين أن يرد التوقيت عليه، [فلم] يستقم له تعليل تحريم الرجعية بالمصير إلى البينونة.
قال الإمام: ([فإن قيل: ] فلو كانت مستحلة، لما احتيج إلى الرجعة) إلى قوله (ومسلك مستقيم). قال الشيخ: هذا مقصود المثال عند الإمام، وذلك أنها إذا تربصت لتبرئة الرحم، فإذا سلط الزوج على شغل رحمها في الزمان الذي تؤمر فيه بتبرئة الرحم، كان ذلك تناقضًا بينًا، وذلك بعيد عن مقاصد الشريعة الكلية. ولكن يبقى في هذا الكلام تقرير أن الوطء الجاري لا يكون رجعة، والإمام لم يدل على هذا بحال، وهو نكتة المسألة.
وأبو حنيفة يرى أن الوطء الجاري رجعة، فلا يجتمع عنده الشغل مع الاستمرار على التربص. فحرف المسألة لم يتعرض له الإمام بالاستدلال. ووجه','4','163',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2555,'تقرير أن الوطء لا يكون رجعة، أن الفعل المجرد لا خصوصية له في الدلالة بحال، والوطء [صورته] لا تستدعي زوجًا. وإذا تقرر أن الطلاق محرم، افتقر في حِل الأبضاع إلى سبب يرفع التحريم، وقد ثبتت [الرجعة] مبيحة، وهو أن يقصد الإنسان بقلبه، أو ينطق بلسانه، برد المرأة إلى العصمة. هذه الرجعة المتفق عليها.
[أما إقامة] الفعل المطلق مقام القصد، (١٥٨/ ب) أو النظر إلى توقيف [المعنى]، فلا يقتضيه، لما قررناه من عدم اختصاص [التسمية]. فثبت بهذا أن الوطء لا يكون رجعة. (١٢٣/ أ) هذا هو الصحيح عندنا.
ويتوجه على هذا سؤال يختص بالمذهب، وذلك أن مالكًا رحمه الله يرى أن المشتري بالخيار، إذا وطئ الجارية في أيام الخيار، عُد ذلك اختيارًا، ولم ينزل وطأه [دليلًا] على التحريم بحال، وإن كان لولا الوطء لم يكن مختارًا. فكذلك ينبغي أن يكون وطء الزوجة في عدة الطلاق الرجعي','4','164',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2556,'[ارتجاعًا]. ولكن الفرق بينهما أن الرجعة فيها حق لله [عز وجل]، وليس للمرأة أن تسلم نفسها للواطئ تصديقًا له. وبائع السلعة يتعلق الحق له، وإذا مكن المشتري منها، فلا اعتراض عليه فيها. وإذا دار أمر الشرع على تعلق حق العباد، صح التمسك بالفعل الدال من جهة الاعتياد، ولا يقبل من المشتري أنه لم يفعل ذلك اختيارًا. ولذلك يتمكن البائع من تصديقه، وإبقاء سلعته له.
وليس كذلك الارتجاع في الزوجة، فلم يجعل الوطء [من الزوج] رجعة. وكيف يستدل على قصده، وهو ينكره؟ وليست مسألة النزاع ما إذا قال: [إنني] قصدت بالوطء الرجعة، فحل الوطء يتعلق بقصده، وهو يعلم فقده. هذا بيان تقرير هذا المثال، وهو الذي اشتمل عليه الكتاب. ولنذكر الآن أمثلة يتعلق القول فيها بالاستدلال، وحكم بها أهل الإجماع.
فمن أمثلة ذلك: اتفاق أصحاب رسول الله - صلى الله عليه وسلم - بحد شارب الخمر ثمانين، وإطباقهم فيه [على] التقدير إما بأربعين أو بثمانين، ومستندهم ذلك الرجوع إلى المصالح، والتمسك بالاستدلالات المرسلة، ولذلك قال علي - رضي الله عنه -: "من سكر هذى، ومن هذى افترى، فأرى عليه حد المفتري".
ووجه إجراء المسألة على الاستدلال: أن الصحابة - رضي الله عنهم - رأوا الشرع يقيم الأسباب في بعض المواضع مقام المسببات، إذ جعل الإيلاج في أحكام [كالإنزال]. وكذلك جعل الحافر في محل العدوان، عند عدم مُرد يحال','4','165',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2557,'الحكم عليه [كالمردي]، وحرم الخلوة بالأجنبية، حذرًا من الذريعة إلى الفساد. إلى غير ذلك من مسائل الشريعة. ورأوا الشرب ذريعة إلى الافتراء، فأقاموه مقامه في ترتيب الحد عليه. هذا من أوضح الأدلة على صحة [استناد] الأحكام إلى المعاني التي لا أصول لها، وهو مقطوع به من الصحابة. وتقرير الدليل بتوجيه أسئلة واعتراضات وأجوبة عنها.
فإن قيل: ليس هذا (١٥٩/ أ) باستدلال، بل هو قياس، إذ ردوه إلى أصل، وهو الافتراء، ولذلك رتبوا الحكم عليه. قلنا: هذا ليس بصحيح، ولا يتصور القياس على هذا الوجه، إذ القياس يرجع إلى استنباط معنى من أصل موجود في [الفرع]. وصورته أن يقال: حد القاذف ثمانين، لعلة كذا، وهذه العلة موجودة في الشارب، فليضرب ثمانين، والقاذف جلد ثمانين، لأنه افترى، لا لأنه تعاطى سبب الافتراء، فلم يكن هذا قياسًا أصلًا.
فإن قيل: الشرب سبب لمعاص كثيرة، فمن أي وجه رتبوا عليه حكم الافتراء دون غيره؟ قيل: المعصية الخاصة بزوال العقل، انطلاق اللسان (١٢٣/ ب) بالهذيان. وأما ما يتعلق بالزنا والسرقة والقتل وغيرها من المعاصي، فلا يتوصل إليها إلا بنوع من العقل، وضرب من النظر والفكر.
فإن قيل: فالردة أيضًا من هذيان اللسان، والعقل عنها زاجر، فإذا فقد العقل، نطق اللسان، فلأي شيء لم يترتب عليه حد الردة؟ قلنا: لا يترتب عليه لثلاثة أمور:
أحدها: ان السكر لا يكون سببًا في الاعتياد للنطق بالكفر، وإنما ينطق','4','166',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2558,'غالبًا في حال هذيانه بما كان يعتاد النطق به في حال عقله، والألسنة لا تنطق بالكفر، ولا تتسامح به بوجه، ويشهد بذلك أمران:
أحدهما- أن السكران إنما ينطق في حال سكره بما اعتاد النطق به قبل ذلك، [فينطق كل إنسان في حال سكره بما كان يألفه].
الثاني- أن العادة ترشد غلى أن السكران لا يجري على لسانه أمر الردة والكفر بحال، وإنما ينطق بما في نفسه من الشهوة إلى الزنا وغيره من الفواحش.
الثاني: أن حد الردة عظيم، وهو إتلاف النفوس، فلم يتجاسروا عليه، مع إمكان أن يشرع زاجر دونه، وما ثبت للضرورة يقيد بقدرها.
الثالث: أن أثر الردة يسقط بالتوبة، وذلك إنما تعرض عليه بعد إفاقته، وهو بعد إفاقته لا يبقى على قوله، [فلو] رتب عليه حد الردة، لم يُحد بحال. فلهذه الأسباب، لم يترتب عليه حد الردة.
فإن قيل: شرط الاستدلال أن لا يخالف نصًا، وشرب الخمر قد جرى في زمن رسول الله - صلى الله عليه وسلم -، وضُرب الشارب بالجريد والنعال وأطراف الثياب. فإن كان ما جرى من رسول الله - صلى الله عليه وسلم -[تعزيرًا]، فكيف صاروا إلى الحد؟ وإن كان حدًا، فكيف عدلوا عنه إلى حد آخر؟ وكلا الوجهين مخالف للنص.
وهذا السؤال سديد، وقد قال بعض الأصوليين: لم يكن فيه حد، وإنما جرى ذلك مجرى التعزير. ولما انتهى الأمر إلى زمان أبي بكر الصديق - رضي الله عنه - (١٥٩/ ب) قدّر ذلك على طريق التعديل بأربعين، ولما انتهى الأمر إلى عمر','4','167',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2559,'[- رضي الله عنه -]، وتتابع [الناس]، أي تتابعوا وكثروا، جمع الصحابة [- رضي الله عنهم -] واستشارهم، فقال علي ما قال. وهذا لعمري ليس انفصالًا عن السؤال، فإن ظاهر هذا أنهم جعلوه حدًّا ينتهي إليه. ولقد بقي في نفس علي إشكال حتى أنه قال: "أما أنا فلا أقيم الحد على أحد فيموت، فأجد في نفسي منه شيئًا إلا شارب الخمر، فإنه إن مات وديته، فإنه أمر أحدثناه". أو كلامًا هذا معناه.
فإن قيل: فما وجه الجواب عن السؤال؟ قلنا: الذي [يصح] عندنا في ذلك أنهم - رضي الله عنهم - رأوا الواقعة- لما كثر شرب الخمر- واقعة أخرى ليست الواقعة الأولى بحال، فاجتهدوا في حكمها بعد اعتقاد الابتداء فيها، فلا يكون في ذلك مناقضة نص أصلًا.
فإن قيل: يلزمكم [على] هذا إذا تتابع الناس في الزنا أو في القذف أن تغيروا الأحكام، وتلتفتوا إلى الأحوال، واختلاف الأزمان [والمكان]، وذلك باطل من دين الأمة بغير إشكال (١٢٤/ أ). وعن هذا جوابان:
أحددهما- أن التعزير يشعر بالتخفيف، وعدم التضييق، ولذلك رُد الأمر [فيه] إلى الأئمة في القيود، وتفاوت المقادير. وأما الحدود فقد ثبتت على كمال التضييق، فلا يلزم من انضمام الكثرة، قيدًا مؤثرًا في قاعدة التعزير، أن يفعل مثل ذلك في قاعدة الحدود.','4','168',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2560,'الوجه الثاني- أنهم إنما لم يفعلوا ذلك في الحدود، لفقدان الأصول التي يكون إليها الاستناد، وعليها القياس، وقد وجد للشارب أصل، وهو القاذف. ولو قدرنا تتابع الناي في القذف، فبأي أصل نلحقه بناء على أنه وسيلة إليه، وسبب فيه؟ وكذلك القول في الزنا والسرقة. وهذه المسألة من أغمض مسائل الشريعة، والسؤال في غاية الغموض، والجواب حسن في الباب.
[مثال] آخر: قضى الخلفاء الراشدون - رضي الله عنهم - بتضمين الصناع، قال علي - رضي الله عنه -: "لا يصلح الناس إلا ذلك". ووجه المصلحة فيه أن الناس لهم حاجة إلى الصناع، وهم يغيبون على الأمتعة في غالب الأحوال، والأغلب عليهم التفريط، وترك الحفظ، فلو لم يثبت تضمينهم مع مسيس الحاجة إلى استعمالهم، لأفضى ذلك إلى أحد أمرين: إما ترك الاستصناع بالكلية، وذلك أمر يشق على الخلق، ويضيق معه الوجود، أو يعملون ولا يضمنون عند دعوى الضياع والهلاك، فتضيع الأموال، ويقل الاحتراز، وتتطرق الجناية، فكانت المصلحة في التضمين. هذا (١٦٠/ أ) معنى قول أمير المؤمنين علي - رضي الله عنه -: "لا يصلح العامة غلا ذلك".
فإن قيل: [في] هذا أيضًا نوع من الفساد، وهو تضمين من عنيناه، [لا] أتلف، ولا فرّط، [فالتضمين] مع الإمكان، نوع من الفساد. [قلنا]: قد قررنا ما في ترك التضمين من المفسدة العامة، وفي مقابلتها أيضًا','4','169',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2561,'مصلحة [للصانع]، فغن وقع التضمين، انتفع أرباب الأموال بالتصوين، وتضرر الصانع، وإذا سقط الضمان، كان الأمر بالعكس. قلنا: قد تقابلت المصلحة والمضرة، ولكن شأن العقلاء إذا وقع التقابل، نظروا إلى التفاوت، ووقوع التلف من الصناع من غير انتساب إلى تفريط [أو عدوان] بعيد، والغالب عند فوات الأموال، أنها لا تستند إلى التلف السماوي، بل يرجع إلى صنع العباد، إما على وجه مباشرة الإتلاف، أو على وجه التفريط، وقد قال - عليه السلام -: "لا ضرر ولا [ضرار] ".
[هذا] مثال للقول بالاستدلال المرسل. والشاهد [لهذه] الأصول من حيث الجملة: "نهى رسول الله - صلى الله عليه وسلم -: أن يبيع حاضر لباد". وقال: "دعوا الناس يرزق [الله] بعضهم من بعض". وقوله - عليه السلام -: "لا تلقوا الركبان للبيع حتى يهبط بالسلع الأسواق". كل ذلك ترجيح للمصلحة العامة على الخاصة. فتضمين الصناع من هذا القبيل.
مثال آخر: اختلف العلماء في الضرب بالتهم، فذهب مالك [رحمة الله عليه] إلى جواز السجن في التهم، حتى يستبرئ الأمر، والسجن نوع من','4','170',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2562,'العذاب، ونص أصحابه على جواز الضرب. وهذا عندنا هو من قبيل تضمين الصناع، فإنه لو لم يثبت الضرب والسجن [١٢٤/ ب) بالتهم، لتعذر استخلاص الأموال من السراق [والغصاب]. إذ قد يتعذر إقامة البينة، فكانت المصلحة في التعذيب وسيلة إلى التحصيل بالتعيين [أو الإقرار].
فلئن قيل: في هذا فتح باب إلى تعذيب البرآء، وفي الإضراب عنه إبطال استرجاع الأموال. ولكنا نقول: الإضراب عن [التعذيب] أشد ضررًا، فإنه لا يُعذب بمجرد الدعوى، بل إذا ظهرت تهمة تحيك في النفس، وتؤثر في القلب. وإذا كان كذلك، فالتعذيب في غالب الأمر لا يصادف البريء، وإن أمكن مصادفته، فيغتفر أمره، كما قررناه في تضمين الصناع.
فإن قيل: ما فائدة الضرب والحبس، وهو لو أقر لم يلتفت إليه؟ قلنا له فائدتان:
إحداهما- أن يعين المتاع، فتشهد عليه البينة [فيلزمه]، وهذه فائدة ظاهرة.
الثاني- (١٦٠/ ب) أن غيره ينزجر حتى لا يكثر الإقدام، فتقل هذه الأنواع من الفساد.
وفيه أيضًا فائدة أخرى: [وهي] أن يُقر [ثم يستمر] على إقراره بعد أمنه، فيؤخذ به. وقد ذهب يحنون إلى أنه يؤخذ بما أقر به في حال','4','171',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2563,'تعذيبه. وهذا ضعيف، وقد قال الله تعالى: {لا إكراه في الدين}. نزله سحنون على من أكره بطريق غير مشروع. كما إذا أكره على طلاق زوجته، [أو غير] ذلك من الإكراه المحرم. والأول عندنا هو الصواب، والإقرار في حال الإكراه كالعدم. وبالله التوفيق.
والقول في تتبع المسائل التي بنيت على المصالح خارج عن الحصر، فليكتف فيه بما ذكرناه.

قال الإمام رحمة الله عليه: <span data-type="title" id=toc-464>(فصل- فإن قيل: قد أثبتم الاستدلال ولم تقبلوه) </span>إلى قوله (وجريانه على الاسترسال في الاستصواب [من غير','4','172',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2564,'اقتصاد]). قال الشيخ: هذا الذي ذكره الإمام هو عين الاسترسال في الاستصواب، وذلك أنه حد [الاستدلال] المقبول بأنه: كل معنى لو ربط به حكم، لجرى واستد، أي ناسب ولم ينتقض. فإذا استعمله استدلالًا [صح].
وقوله: فإن الحكم إذا ربط بمعنى، رده إلى أصل، فهو صحيح، وليس التمسك بصورة الأصل، ولا بحصول الوفاق عليه. وأما التمسك بالمعنى، وقد [ضبط] هو فيما سلف بيان المعنى الذي يصلح الاعتماد عليه في تعليل الأصول الثابتة الأحكام، فقال: ومن الطرق الدالة على إثبات علل الأصول،','4','173',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2565,'التمسك بإخالتها ومناسبتها [الحكم]. وعبر الأستاذ عنه في تصانيفه بالاطراد والجريان، وعنى بالاطراد: السلامة عن المبطلات. وإذا كان مجرد المعنى المناسب السالم عن المناقضة، [يصح] تعليل الحكم [الثابت] به، صح أن يترتب عليه الحكم ابتداءً. وهذا عين الاسترسال في الاستصواب. وزاده تقريرًا بقوله: فليقدر الناظر مسألة مختلفًا فيها متفقًا عليها، فالمعنى الذي يصح تعليل الحكم الثابت [به]، صح أن يرتب الحكم عليه ابتداءً. وهذا هو صريح','4','174',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2566,'الاسترسال في الاستصواب، فلم يبق بين هذا المذهب ومذهب مالك فرق، لا أن يضاف إلى مالك أنه يتبع المعاني المرسلة، وإن كانت مناقضة لقواعد الشريعة، ونعوذ بالله أن يكون هذا مذهبًا لأحد.
قال الإمام رحمه الله: (ونحن [نضرب] في ذلك مثالًا ثم نذكر بحسبه لمالك) إلى قوله (١٢٥/ أ) ("أنا أقتل ثلث الأمة استبقاء لثلثيها"). قال الشيخ: هذا الذي ذكره الإمام ظاهر الفساد، وذلك أنه (١٦١/ أ) ألزم القول بالاستدلال على خلاف الإجماع، وذلك أن الأعضاء إنما أباح الشرع إيلامها في القصاص دون التعزير. وإذا كانت الأعضاء مصانة عن التعزير بالتوقيف، فكيف يصح أن يلزم إتلافها في التعزير على القول بالاستدلال؟
الوجه الثاني: أنه يعترف بأنه لو فعل ذلك، لكان على خلاف الإجماع، فكيف يلزم خصمه الذي يشترط في القول بالاستدلال [ألا] يخالف أصلًا من أصول الشريعة؟ وأي شيء أعظم في الفساد من اتباع مصلحة إلى خرق الإجماع؟ فهذا الإلزام بعيد عن الصواب.','4','175',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2567,'وأما قوله: إن مالكًا [رحمه الله] التزم مثل هذا، حيث جوز لأهل الإيالة القتل في التهم العظيمة. وهذا الذي ذكره عن مالك، لم يقف عليه، ولا يعترف به أصحابه، وإن قال ذلك، فله وجه ممكن من الصواب، وذلك إذا كثر أهل الفساد، واستولوا على العباد والبلاد، وخرجوا على أهل الحق، ودعوا إلى الباطل، فإنهم يقاتلون ليرجعوا، فإن استمروا، ولم يقدر على دفع شرهم إلا بقتلهم قتلوا. سئل عمر بن عبد العزيز [رحمه الله] عن القدرية، قال: "أرى أن يُدعوا غلى السنة والجماعة، فإن أبوا قتلوا". قال مالك [رحمه الله]: "وهو رأيي فيهم". وقد قاتل الأئمة في أزمنة الفتنة، دفعًا للفساد،','4','176',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2568,'وإحياءً للخلق، فلا يستبشع ذلك بحال. وأما قتل من لا يستحق القتل، فمعاذ الله أن يقول ذلك أحد.
قال الإمام: (فغن قيل: فبم تردون ما ذكرتموه) إلى قوله (وهذا تنبيه على ما نريده). قال الشيخ: هذا الذي ذكره عن مالك من الطراز الأول، وهو أنه نسب إليه القول بمصالح حائدة عن الشريعة، مصادمة لأصولها، وهذا لا يقوله أحد، وقد بينا أنه لا فرق بين ما اختاره الشافعي، وبين ما نسبه إلى مالك','4','177',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2569,'[رحمة الله عليه] على الجملة والتفصيل. وهذه المصالح التي ألزمه القول بها، ردتها الشريعة إجماعًا، فكيف يصح أن ينسب إلى مالك القول بها؟

قال الإمام: <span data-type="title" id=toc-465>(فصل- فإن قال قائل: ما الاعتراض على الاستدلال) </span>إلى قوله (هذا يستقصى في كتاب الترجيح، إن شاء الله). قال الشيخ: هذا الكلام، أعني الاعتراض على الاستدلال واضح، وأما ترجيح المعنى المستند','4','178',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2570,'إلى أصل على آخر، فهذا ينقسم عندنا، فغن كان المعنى المستند إلى أصل في (١٦١/ ب) رتبة الاستدلال المرسل، بحيث يجوز الاعتماد عليه، فإن ما يستند إلى أصل، لا شك في كونه مرجحًا، لاستوائه معه في القوة، وانفراد المستند إلى أصل بالشهادة. وإن كان المستند إلى أصل غريبًا، وهو الذي لا يجوز الاعتماد عليه دون الرد إلى [أصل]، [فهذا موضع اجتهاد، لانفراد احدهما بالشهادة، وانفراد الآخر بالملاءمة]. فهذا محل يرى المجتهد فيه رأيه، ولينظر في كل مسألة نظرًا خاصًّا.

قال الإمام: <span data-type="title" id=toc-466>(فصل- في استصحاب الحال) </span>إلى قوله (مع كثرة الإبل','4','179',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2571,'بعيد، وهذا ليس استصحابًا). قال الشيخ: معنى قوله: قد قال باستصحاب الحال قائلون، يريد بذلك أن بعض الناس قال به، بناء على أنه دليل، وقال به آخرون، بناء على أنه ترجيح، وآخرون يقولون إنه ليس دليلًا (١٢٥/ ب) على إثبات حكم، ولا يصلح لترجيح دليل. ولا بد من تفسير لفظ الاستصحاب أولًا، وهو يطلق على أربعة أمور: يصح منها ثلاثة، ويبطل الرابع:
الأول: منها- وهو المشهور-: دلالة العقل على انتفاء الحكام السمعية قبل ورود الأنبياء عليهم السلام، فلا تكليف على العقلاء قبل ورود الشرع، وهذا يدرك عقلًا عند قوم، وعُلم سمعًا عند آخرين. وقد قال الله تعالى: {وما كنا معذبين حتى نبعث رسولًا}. والأمة مجمعة على أنه لا تكليف قبل ورود الأنبياء، وبعد الورود، لا تكليف إلا بعد نصب دليل. وإذا تقرر ذلك، فكل حكم لم ينصب الله للمكلفين دليلًا عليه، فهو غير ثابت عليهم، فنحن على ذلك حتى يرد التغيير على ألسنة الأنبياء.','4','180',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2572,'فإذا ورد نبي مثلًا، وأوجب خمس صلوات، بقي ما [وراء] ذلك على أصل النفي، من غير حاجة إلى تصريح النبي بنفيها، بل علم أنها كانت منتفية، وأن الإيجاب مقتصر على ما سواها، فبقيت [منتفية] على ما كانت عليه. وهذا يخرج الاستصحاب عن أن يكون دليلًا شرعيًّا، فكأن الشرع بالإضافة إلى ما زاد على الخمس لم يرد.
وكذلك إذا أوجب صوم رمضان، بقي صوم شوال على ما كان عليه. وإذا أوجب عبادة في وقت، بقيت الذمة بعد خروج الوقت على البراءة الأصلية. وإذا أوجب على قادر، بقي العاجز على ما كان. فإذًا النظر في الحكام، إما أن يكون في إثباتها، أو في نفيها.
أما في إثباتها، فالعقل قاصر عندنا عن الدلالة على الإثبات. وإن كان في نفيها، ففيه خلاف. فمن [أحال] تكليف المحال، جعل العقل (١٦٢/ أ) مستقلًا بالنفي، ومن أجازه، لم يحكم بإثبات ولا نفي، إلا مستندًا للشرع. وهذا هو الصحيح عندنا، ولكن بعد أن وردت الأنبياء عليهم السلام، وأخبروا الخلق أن الله [تعالى] لا يحكم على العباد إلا إذا نصب لهم دليلًا، فإذا فقدوا الأدلة، قطعوا بنفيها علم انتفاء الأحكام.','4','181',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2573,'فإن قيل: [فبأي] طريق [يعلم] نفي الدليل؟ قلنا: تارة بالنص، كما إذا قال: لم يوح إلي في هذا شيء، وهو كقوله [تعالى]: {قل لا أجد في ما أوحي إلي محرمًا} الآية.
وقد [يعلم] ذلك من غير أن يوجد هذا النص، وهو كعلمنا بنفي وجوب صوم شوال، وصلاة سادسة، فإنها لو وجبت، لتواترت، وتوفرت الدواعي على نقلها. وقد يظن انتفاء الدليل، فيظن نفي الحكم، ثم يرجع النظر إلى أن قلنا: ثبوت العلامة أو نفيها، هل يقطع [بمقتضاها]، أو يظن ذلك؟ هذا ينبني على أن كل مجتهد مصيب، أو المصيب واحد؟ وقد تكلمنا على ذلك بما فيه مقنع وبلاغ.','4','182',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2574,'فإن قيل: وما المانع من أن يكون عليه [دليل]، ولم يبلغنا؟ قلنا: قد أجبنا عن هذا بأن الدليل، قد يعلم انتفاؤه، وقد يظن، على حسب ما سبق.
فإن قيل: [أفيقدر] العامي على النفي، بناء على أنه لم يبلغه الدليل؟ قلنا: هذا إنما يكون للمجتهد، المحيط بمآخذ الأدلة، المطلع على الشريعة، فإذا بحث ولم يجد، فقد يقطع وقد يظن.
أما العامي، فليس له أهلية النظر بحال، فهو كالأعمى الذي لا أهلية له للتفتيش (١٢٦/ أ) عن الأجسام الخفية في البيت. فهذا لا يتأتى له أن يعلم النفي ولا يظنه، بخلاف من له بصر. هذا أحد الأوجه التي يطلق الاستصحاب عليها، وهو الأشهر، وهذا هو الذي اختلف الناس [فيه]، هل هو دليل الحكم، أو ترجيح، أو خارج عنهما جميعًا؟
الثاني: استصحاب العموم إلى أن يرد تخصيص، واستصحاب النص إلى','4','183',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2575,'أن يرد نسخ، فالعموم حجة إلى قيام المخصصات، والنص حجة إلى قيام النسخ، وليس هذا من قبيل الاستصحاب بحال. فإن الحكم مستند إلى الدليل، لا إلى الاستصحاب.
الثالث: استصحاب حكم دل الشرع على ثبوته ودوامه، كالملك بعد جريان العقد المملك ودوامه، وكشغل الذمة عند جريان الإتلاف [أو الالتزام]، فإن هذا وإن لم يكن [حكمًا] أصليًّا، فهو حكم شرعي، دل الشرع على ثبوته ودوامه، [ولولا دلالة الشرع على دوامه]، لما جاز استصحابه، [فالاستصحاب] ليس بحجة، إلا فيما إذا دل الدليل على دوامه، بشرط عدم [المغير]، (١٦٢/ ب) كما دل على البراءة العقلية، وعلى الشغل الشرعي، وعلى الملك الشرعي.','4','184',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2576,'ومن هذا القبيل: الحكم بتكرر اللزوم والوجوب، إذا تكررت أسبابها، كتكرر شهر رمضان، وأوقات الصلاة، ونفقات الأقارب عند تكرر الحاجات، إذ فهم انتصاب هذه المعاني أسبابًا، فهذه الأحكام من أدلة الشرع، إما بمجرد العموم عند القائلين به، أو بالعموم وجملة من القرائن عند الجميع، وتلك القرائن تكريرات وتأكيدات، وأمارات عرفت حملة الشريعة قصد الشارع إلى نصبها أساسًا، إذا لم يمنع مانع، فلولا دلالة الدليل على كونها أسبابًا، لم يجز استصحابها.
فإذًا الاستصحاب عبارة عن التمسك بدليل عقلي عند قوم، أو شرعي عند الجميع، وليس راجعًا إلى عدم العلم بالدليل، بل إلى دليل مع العلم بانتفاء المغير، أو مع ظن انتفاء المغير عند بذل الجهد في البحث والطلب.
والرابع: استصحاب حال الإجماع في محل الخلاف، وهو باطل. وصورته: المتيمم إذا رأى الماء في أثناء الصلاة، فقال قائلون: يمضي على صلاته، لأن الإجماع منعقد على صحة الصلاة ودوامها، وطريان وجود الماء، كطريان هبوب الريح، وطلوع الفجر، وسائر الحوادث، فنحن نستصحب دوام','4','185',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2577,'الصلاة، إلا أن يدل دليل على كون رؤية الماء قاطعة. وهذا فاسد، لأنا إنما نستصحب الحكم الذي ثبت دوامه شرعًا، [أما] [إذا] علمنا انتفاء الدليل، فكيف يتصور استصحابه مع العلم بانتفائه؟ والإجماع إن دل على صحة الصلاة عند وجود الماء وعدمه، فمن أبطل عند الوجود، فهو خارق للإجماع، وإن دل على الصحة [عند] العدم، فهو عند الوجود لا يدل قطعًا، فكيف يستصحب حكمه مع العمل بنفيه؟
فإذا بطل أن يستصحب الإجماع مع نفيه، لزم أن يستدل على دوام الصحة بدليل سوى الإجماع، إما بتوقيف، وإما أن تقاس حالة الوجود على حالة العدم. وهذا يتعلق بفن الفقه، وهو خارج عن فن الأصول. وإنما المقصود أنه لا يصح استصحاب دليل مع العلم بنفيه بحال.
مسألة: اختلفوا في النافي، هل عليه دليل [أم لا]؟ فقال قوم: لا دليل عليه، وقال قوم (١٢٦/ ب): لا بد من دليل، وفرق بين العقليات والسمعيات، وأوجبوا الدليل في العقليات دون السمعيات. والصحيح أن ما ليس بضروري، لا يعرف إلا بدليل، ويستوي في ذلك النفي والإثبات، والسمعيات والعقليات.','4','186',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2578,'وتحقيقه أن يقال لمن ادعى النفي: أعلمت ذلك أم لا؟ فإن قال: إنه علمه، قيل له: أضرورة [أم] نظرًا؟ (١٦٣/ أ) فإن ادعى العلم الضروري، لم يطالب بالدليل، ولا بُعد في أن يعلم النفي ضرورة، فإنا نعلم أنا لسنا على جناح نسر، أو على لجة بحر، وأنه ليس بين أيدينا [فيل] في الحال. وإن ادعى العلم النظري، طولب بالدليل، فإن ما ليس [بضروري]، لا يعرف إلا بدليل. هذا تقرير الدليل، ويتأيد بلزوم إشكالين:
أحدهما- سقوط الدليل [عمن] ينفي تحريم المحرمات، فيقول مثلًا: ليس النبيذ حرامًا، وكذلك ما سواه من المسائل، وهذا باطل قطعًا.
الثاني- أنه إذا سقط الدليل عن هؤلاء، لم يعجز المثبت عن التعبير عن مقصود إثباته بالنفي، فيقول بدل قوله: العالم قديم، ليس بحادث، وكذلك ما سواه من العقليات والسمعيات، فيتحقق بذلك لزوم المطالبة بالدليل في النظريات، عقلية كانت أو سمعية، نفيًا كان ذلك أو إثباتًا.
رجع بنا الكلام إلى مسألة الكتاب، ولم ير الإمام الأحكام المستندة إلى الأدلة الثابتة من نص أو عموم راجعة إلى استصحاب الحال، وإن أمكن طريان النسخ أو التخصيص. قال: وإن سمى [مُسم] ذلك استصحابًا، لم يناقش فيه، وليس [هو] مقصودنا.','4','187',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2579,'وأما قوله: إذا ثبت حكم في صورة، ثم تغيرت وحالت، ورام الناظر طرد الحكم الثابت في الصورة الأخرى، فإن لم يكن للصورة الثانية ترتيب على الأولى، لم يتصور الاستصحاب [فيها. وهذا] كما صوره فيمن أراد أن يستصحب [صدقة البقر في صدقة الغنم]، حتى لا يثبتها إلا على ذلك القياس. وهذا باطل قطعًا. لأنه إثبات حكم من غير توقيف ولا قياس مخيل ولا شبه. وهذا باطل من دين الأمة.
أما إذا تغيرت في وصفها، وأثبتت في الخلقة عليها، فمثاله: أن الإبل تُزكى بالغنم إلى خمس وعشرين، فإذا بلغت خمسًا وعشرين زُكيت بالإبل، بالنص إلى مائة وعشرين، ثم قال - صلى الله عليه وسلم - بعد المائة والعشرين: "فما زاد ففي كل خمسين حقة، وفي كل أربعين بنت لبون". قال أبو حنيفة: ترجع إلى الأصل الأول، لأنها زكيت أولًا بالغنم، ثم انقلبت إلى الإبل إلى هذا الحد. [وهو] آخر ما ذكره الرسول - صلى الله عليه وسلم -، ويستأنف الأمر بعد ذلك على الحكم الأول. وهذا غلط بين، وكيف يتفق أن يُستصحب حكم ثبت الانتقال عنه، وصار الأمر إلى غيره؟ مع أن الفقه لا يقتضي ذلك، والأصل أن زكاة كل مال منه، وإنما تثبت الشاة اجتنابًا للتشقيص، وحذرًا من إسقاط الزكاة، مع [أن] إيجاب بعير','4','188',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2580,'يجحف بالخمس. فلا وجه (١٦٣/ ب) للرد إلى الشاة مع كثرة الإبل.
وأما قول الشافعي: إن الفريضة إذا انتقلت (١٢٧/ أ) إلى الإبل، وجب استصحابها. فهو أيضًا غلط، وهو نظير ما قررناه من استصحاب حال الإجماع في محل الخلاف، إذ كيف يكون الدليل متناولًا للصورة، ثم يمكن أن تسقط دلالته بطريان مغير، فيقال: يستصحب حكم الدليل، ولا يضر احتمال ورود المغير. أما إذا كانت الصورة لا تدخل تحت مقتضى الدليل بحال، فكيف يمكن استصحاب الدليل فيها؟ والاستصحاب [عبارة عن الاستدامة، وإنما يدوم الشيء على حسب ثبوته، وغاية الاستصحاب] الإعراض عن احتمال الانقطاع. فأما أن يصير الدليل دالًا على ما [لم] يدل عليه في ابتدائه فمحال.
[ولا شك] أن النصوص الدالة على اطراد الفريضة في الإبل، لم تتناول [الخمس] الزائدة بعد المائة والعشرين، حتى يستصحب حكم الدليل فيها. نعم، الذي قاله أصحاب الشافعي أقرب إلى فقه الزكاة، لما قررناه. فأما أن يكون من أبواب الاستصحاب فلا.
قال الإمام: (فإن قيل: من [تيقن] الطهارة وشك في الحدث) إلى قوله','4','189',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2581,'(ولكنه أصل ثابت في الشريعة، مدلول عليه بالإجماع). قال الشيخ: أما قول الفقهاء: لا [يرفع] اليقين بالشك، كلام متجوز [به]، فإنه يوهم أنهما يتعارضان ويجتمعان في النفس، ثم يغلب أحدهما، فيستمر حكمه، وهذا محال. فإن اليقين يضاد الشك، ولكن مرادهم بذلك: أن الأمر الذي يتبين ثبوته ابتداء، يدوم حكمه، وإن حصل الشك في دوامه. هذا مرادهم بالإطلاق.
وأما ما ذكره الإمام من [تقسيم] الأحوال، وقوله: لم يخل المشكوك [فيه] من ثلاثة [أحوال]:','4','190',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2582,'أحدهما- أن يرتبط بعلامة بينة في محل الظنون، فهذا إخراج للصورة عن تحقيقها، فإن الظن يضاد الشك، فكيف يتفق أن يكون مشكوكًا فيه يرتبط بعلامة ظاهرة في محل الظنون؟
[وقوله]: فإنه يتصدى للمرء شك في بقاء ما سبق، واجتهاد ظاهر في زواله. هذا عين التناقض، وهو تصريح بما [تجوز] الفقهاء بإطلاق لفظه. وإذا ظن الانتقال [عنه]، كيف يبقى معه شك في الدوام؟ هذا محال.
وأما قوله: إذا ثبتت علامة خفية كالعلامات التي يقع التمسك بها في تمييز النجس من الطاهر. هذا أيضًا لا يبقى معه شك، لما قررناه من أن الظن','4','191',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2583,'يضاد الشك، وهو (١٦٤/ أ) يضاده، قويًّا كان أو ضعيفًا، فقال فيه: إن عارض يقين الطهارة يقين النجاسة، فعلم صاحب الواقعة أن أحد الإناءين نجس والآخر طاهر، فيضطر إلى التمسك بالأمارة، وإن خفيت، فإن الأمرين يتعارضان. وإن لم توجد النجاسة، ولكن تيقنا الطهارة وشككنا في طريان نجاسة، وثبتت علامة خفية، هذا أيضًا من الطراز الأول، فإن العلامة وإن خفيت، إذا حركت الظن، استحال معها بقاء الشك، فالظن بالنجاسة حاصل.
فما كان الصواب في هذا الصنف إلا اتحاد القول بالترتيب على حكم العلامة، إذا مالت النفس بسببها إلى النجاسة.
قال: وإن تقدم يقين وطرأ (١٢٧/ ب) شك، وليس لما فيه الشك علامة [جلية ولا خفية]، فعند ذلك [تأسيس] الشرع على التعلق بحكم ما','4','192',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2584,'تقدم. هذا القسم هو مقصود المسألة، وما تقدم خارج عن مقصودها، ولهذا قال: فهذا نوع من الاستصحاب صريح، [وسببه] ارتفاع العلامات.
قال: [وليس هذا] من فنون الأدلة. يعني أن الأدلة إما أن تكون قطعية، وإما أن تكون ظنية، وهذا إنما [نحن] على شك في الحال، فكيف يكون هذا من فنون الاستدلال؟
قال: ولأنه أصل ثابت في الشريعة، مدلول عليه بالإجماع. وهذا كلام عجيب، أنكر أن يكون من الأدلة، [ثم] قال: إنه أصل ثابت، مدلول عليه بالإجماع. والأصل: الدليل، اللهم إلا أن يقول ليس هو من الأدلة المعروفة، التي هي: الكتاب والسنة والإجماع والقياس، فيكون لهذا وجه. وليس هذا ظاهر كلامه، بل يشير إلى أنه لا يتحصل منه قطع ولا ظن بحكم.
قال الإمام: (فإن طرأ مثل ذلك في مثار المجادلات) إلى قوله (من اعتبار','4','193',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2585,'صورة بصورة). قال الشيخ: قوله: [وإذا] طرأ مثل ذلك في مثار المجادلات إلى آخره، شرع للمستدل التمسك به، وجعل فيه أنه يعتبر بنظائره [بتشبيه] أو [قياس] معنوي، فيخرج التمسك بالاستصحاب عن كونه دليلًا [مستغنى] به، ويرجع إلى القياس في صحة التمسك به. وما أراه بنى هذا إلا على أصله في: أن نفي الحكم الشرعي حكم شرعي، والحكم الشرعي لا يثبت إلا بحكم شرعي، فافتقر إلى رد الأمر إلى القياس، وإلا فإذا كان هذا أصلًا ثابتًا بنفسه، مدلولًا عليه بالإجماع، فأي حاجة بعد ذلك إلى نظم قياس، واعتبار الصور؟
قال الإمام: (وبيان ذلك بالمثال: أن المسؤول عن وجوب الأضحية) إلى','4','194',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2586,'قوله (وقد نجز بنجازه القول المقصود في الاستدلال). قال الشيخ: (١٦٤/ ب) هذا الكلام الأخير منه يدل على صحة التمسك باستصحاب الحال بعد [البحث والسبر]، من غير حاجة إلى [قياس و] تشبيه. فهذا الكلام مضطرب عندي، والله أعلم.
فإن قيل: [إن] صح التمسك بالأصل السابق عند طريان الشك في انقطاعه، فما وجه قول مالك [رحمه الله] المشهور: إن من تيقن الطهارة وشك في الحدث، وجب عليه الوضوء؟ فهذا ترك لحكم الأصل السابق عند طريان الشك، وأنتم تقولون: إن التمادي على الأصل الأول مجمع عليه. قلنا: نحن نقول كذلك، ولا خلاف فيه بين الناس، وإن اختلف في بعض المسائل، فليس النزاع في هذه [القاعدة]، ولكن سبب الخلاف تقابل الأصول عند بعض العلماء. وهذا بمثابة مسألة الوضوء، فغن الأصل ثبوت الطهارة، ولا','4','195',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2587,'يحكم بانتقاضها بمجرد الاحتمال. والأصل ثبوت الصلاة في الذمة، فلا تبرأ الذمة منها بمجرد الشك. وإذا طرأ شك في الحدث، لزم منه الشك في براءة الذمة، على [هذه] الحالة بمثل هذه الصلاة. فهذا هو سبب [الخلاف]، لتقابل الأصلين، لا للنزاع في الأصل السابق الأصولي. [وبالله التوفيق، وهو حسبي ونعم الوكيل].
قال الإمام: (كتاب [الترجيحات]-[الترجيح: تغليب بعض الأمارات (١٢٨/ أ) على بعض) إلى آخر المسألة]. قال الشيخ: حد الإمام الترجيح','4','196',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2588,'بأنه: تغليب بعض الأمارات على بعض في [سُبل] الظن. كلام حسن، ولا يصح أن يحد الترجيح بأنه ترجيح ظن على ظن، فإن ذلك يشعر بحصول الظنين في النفس، ثم [يرجح] أحدهما على الآخر، وذلك محال، وهو مصير إلى اجتماع الضدين، ثم المصير إلى الترجيح، وذلك غير معقول.
فإن قيل: هذا يلزمكم، إذا قلتم إنه ترجيح بعض الأمارات على بعض، فهذا يتضمن القضاء باجتماع الأمارتين المتناقضتين، ثم المصير إلى ترجيح إحداهما، فقد وقعتم فيما منه فررتم. قلنا: لو كان الظن يرتبط بالأمارة ارتباطًا لازمًا، لكان كذلك، ولكنه قد لا يترتب الظن على الأمارة، بأن [يعارضها] ما في النفس مثلها. وإذا كان كذلك، أمكن تقابل الأمارات على التساوي وعلى التفاوت.
فإذا ثبت معقول الترجيح، عُلم بهذا أنه لا يتصور في القطعيات، وذلك مُتلقى من نفس الحد، فإن لفظ التغليب يشعر بالتفاوت، والعلوم لا تفاوت','4','197',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2589,'فيها. ولفظ الأمارة أيضًا يقتضي الدلالة على غير القاطع.
وقولنا: في سُبل الظن، فهذا الحد من هذه الجهات، يمنع تصور (١٦٥/ أ) الترجيحات في القطعيات، ولا يتصور الترجيح في العلم بحال، إذ لا يتصور علم أبين من علم.
فإذا ثبت ذلك، فالترجيح مما اجتمعت الأمة عليه، من حيث الجملة، ولا مبالاة بخلاف من شذ. والدليل في الترجيح قاطع منقول تواترًا، والمعنى المفهوم من الشريعة يدل عليه ويرشد إليه، وذلك أن الشرع إذا اكتفى من الخلق في بعض المسائل بغلبات الظنون، فعند اجتماع الأمارات في النفس وتغليب بعضها على بعض، فغلبة الظن حاصلة بثبوت الحكم على وفق الأمارات القوية. وهذا- والعلم عند الله- مستند أهل الإجماع في المصير إلى الترجيح- وما حكى القاضي من الخلاف عمن حكى الخلاف عنه، فلا سبيل إلى تغليط في النقل.
وما ذكره الإمام تعريض لتغليط العدول، مع كمال المعرفة والبصر، وهذا','4','198',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2590,'لا سيبل إليه. ومما تمسك به جُعل من امتناع ترجيح البينات، له وجه سنذكره. والظاهر من كلام الإمام أنه حمل على القاضي أنه بنى النقل عنه [تخريجًا] واستنباطًا، من مصيره إلى نفي الترجيح في البينات. ولهذا [قال] القاضي: وليس من الإنصاف إلزام مجتهد فيه على مسألة مدركها القطع. ولئن كان القاضي إنما حكى عن هذا، إلزامًا على قوله بنفي الترجيح في البينات، وهذا لا يظن بالقاضي، بل الذي يصح عندنا أنه نقل مذهبه صريحًا. وما أتى به من البينات، جاء به استدلالًا، والاستدلال بالخيالات في مقابلة الإجماع باطل، على أنا قد قررنا أنه لو لم يصادف [إجماع]، لكان قياس الشريعة في الحكم [بغلبات] الظنون يقتضي القضاء بالترجيح على ما مر.','4','199',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2591,'وأما ما يمكن ان يكون خيالًا لجُعل، ووجهه التشبيه بين الأمارات والبينات، أن الشرع أثبت الأمارات ورتب الأحكام عليها، على تقدير انفراد كل واحدة منها، بدليل الإجماع من أهل الإسلام، مع قطع النظر عن تفاوت الدرجات. وإذا ثبت كون كل واحدة أمارة بدليل قاطع، فلا نظر (١٢٨/ ب) بعد ذلك إلى التفاوت، مع القطع بصحة النصب. وقد قال القاضي [رحمه الله] مثل هذا في مسألة، [وهي]: أنه إذا تعارض ظاهر من الكتاب بظاهر نقله الآحاد، فقد قال القاضي: إنهما يتعارضان، وإن تفاوتا في طرق النقل. وهذا ترجيح واضح. فإن الظاهرين استويا في الدلالة، وانفرد أحدهما بالقطع بصحة الثبوت، لا وجه لذلك إلا أنهما جميعًا استويا في كونهما أمارتين، فلا نظر إلى التفاوت بعد ذلك، كما في البينات.
هذا وجه هذا المذهب، (١٦٥/ ب) وهو باطل عندنا، وذلك أن لا يلزم من كون الشارع نصبه أمارة، على تقدير انفراده إجماعًا، أن يكون أمارة على','4','200',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2592,'تقدير أن يعارضه غيره. إذ المجمع عليه حالة الانفراد. وأما حالة الإجماع، فلا إجماع عليها. فيكون هذا من باب استصحاب حال الإجماع في محل الخلاف. وقد بينا بطلانه.
ومعنى قول الإمام: فكيف يجري في المعقولات ترجيح ما لا يجري أصلها فيه، مع أن المرجح أقوى من الترجيح؟ يريد بذلك أن [للترجيح] قوة في الأمارة، والأمارة أقوى من ترجيحها. وإذا كانت الأمارات لا يتأتى التمسك بها في القطعيات، فجهات ترجيحها أولى بالامتناع. وهذا عند الأصوليين يقارب قول أهل العربية: إنه لا يصح أن يكون النعت أوضح من المنعوت. فكذلك لا يصح أن يكون الترجيح أقوى من المرجح بحال. وقد امتنع إجراء [المرجح] في القطعيات.

قال الإمام: <span data-type="title" id=toc-468>(مسألة: أطلق الأئمة القول: بأن المعقولات لا تجري الترجيحات فيها) </span>إلى قوله (مجرى الظنون في [المظنونات]). قال الشيخ: الذي ذهب إليه الأئمة حق، لا خفاء به، ولا يتصور تطرق الترجيح إلى العلم.','4','201',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2593,'ولا إلى طرقه. أما العلم فهو: معرفة المعلوم على ما هو به. فلا يتصور علم أبين من علم. وقد قررنا ذلك فيما سبق. ولا يتصور أيضًا الترجيح في طرق العلم، فإن طرقه قصارها كلها العلم، [ولا يصح] أن يكون أحد [الطريقين] أشد ارتباطًا بالعلم من الآخر، إذ ليس بعد القطع مرتبة يرتقي إليها، فاستحال- والأمر كذلك- الترجيح في العلم وطرقه.
وأما ما ذهب إليه الإمام من تطرق الترجيح إلى المعتقدات، فهذا عندنا لا يصح أيضًا بحال، وإنما يتصور الترجيح على [تقدير] اجتماع الراجح والمرجوح في النفس، ثم القضاء بالتفاوت، وهذا لا يتصور، فإن اعتقاد النقيضين لا يتأتى [أيضًا]، فإن المصمم لا يتصور أن يدرك فرقًا بين [تصميمَيْه]، ولو أدرك فرقًا وتفاوتًا، لانحل الاعتقادان جميعًا.
أما رجوح الترجيح إلى أسبابها، فلا يتصور ذلك أيضًا عندنا، فإنه [من] أدرك تفاوتًا بين السببين، [قوة وضعفًا]، خرج عن كونه معتقدًا،','4','202',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2594,'وصار إلى رتبة الظانين. [فالصحيح] أن الترجيحات لا تتطرق [إلا] إلى الأمارات على الخصوص. ومن العجيب ذهاب الإمام إلى هذا، مع كونه يحد الترجيح بأنه: تغليب بعض الأمارات على بعض في سبل الظن. ولو صح ما قال ها هنا، لبطل الحد، [فهو] دائر بين اختلال الحد، وبين فساد ما ذكر ها هنا.
وقول (١٦٦/ أ) الإمام: فإنا نكتفي من [العوام] بعقود سليمة، لا تبلغ الثلج ومسلك اليقين. وهذه المسألة عظيمة (١٢٩/ أ) في الدين، وقد امتنع من ذلك المتكلمون، وأوجبوا معرفة الله تعالى بالأدلة، وتمسكوا بأن مطلق الاعتقاد لا يكتفى به، والواجب لا بد أن يكون المكلف متمكنًا من معرفته، والاعتقاد لا يتصور أن يعلم المعتقد كونه معتقدًا، فكيف يتصور أن يكلف الاعتقاد مع استحالة عدم علمه؟ لا سيما إذا كلف الاعتقاد الصحيح.
قالوا: وهذا بمثابة ما لو كلفه عتق عبد، وهو معين عند الآمر، ولم يعينه للمخاطب، فإن صادفه عتقه، فقد امتثل، [وإن] لم يصادفه، [عرضه] للعقاب. فهذا عندهم من تكليف ما لا يطاق.','4','203',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2595,'والعجب من الإمام أنه حكم باستحالة التكليف بمثل هذا، ثم صار إلى تحقيق التكليف بالاعتقاد الصحيح، وهو لا يتصور علمه بحال. إلا أن يقول: له طريق ها هنا إلى الخروج عن الغرر، بأن يعلم، فيكون على تحقيق من الامتثال. وهذا ضعيف، فإنه يجري مثله فيما إذا أمره بعتق عبد معين عند الطالب، ولم يعينه للمخاطب، وهو ملتبس بعدد من العبيد، يمكن المأمور عتق الجميع، فإن ذلك لا يوجب كون المكلف [متمكنًا] لقصد المخاطب إليه.
وتمسكوا أيضًا بادعاء الإجماع على وجوب [معرفة الله سبحانه. هذه مستندات المتكلمين في وجوب] المعرفة، ومنع الاكتفاء بالاعتقاد. والصحيح عندنا خلاف ذلك. فإن الله تعالى لم يوجب على الخلق المعرفة، بل إما علمًا، وإما اعتقادًا صحيحًا. والمسألة سمعية لا عقلية.
والدليل على ذلك [ما نقل] على التواتر عن رسول الله - صلى الله عليه وسلم - من الاكتفاء من عوام الخلق بالإقرار بالتوحيد، وتصديق [الرسل] من العدد الكثير في الزمن القريب، ومن [النسوان والصبيان، الذين لم يبلغوا الحلم، والإماء العبيد، ومن] ليس من أهل النظر بحال. ونحن من طريق الاعتقاد','4','204',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2596,'نعلم أنهم لم يحصل لهم ما يستند إلى البراهين، وكيف يتأتى منهم ذلك وليسوا ممن يحصي النظر، ولا يهتدي غلى طريق الفكر؟ هذا محال من جهة العادات. فالأمر في الحكم الشرعي كما ذكر الإمام، وقد تبين اختيارنا في امتناع الترجيح في الاعتقادات.

قال الإمام: <span data-type="title" id=toc-469>(مسألة: قال الأئمة - رضي الله عنهم -: الترجيح لا يستعمل في المذاهب) </span>إلى قوله (ونحن الآن نرسم ما يترجح به مذهب الإمام المطلبي). قال الشيخ: ما ذكره الأئمة من أن الترجيح يجري في الأدلة لا في المذاهب،','4','205',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2597,'صحيح، لأن المذاهب دعوى محضة. وقد قلنا: إن الترجيح: تغليب بعض الأمارات (١٦٦/ ب) على بعض في سُبل [الظن]. [ونفس] المذاهب دعوى ليست أمارة، فلا دخول لها تحت الحد.
وأيضًا [فإن] الترجيح إنما يثبت نظرًا إلى قوة في الدليل، ولو تقدم المذهب على المذهب بمحض الترجيح، لاستقل الترجيح دليلًا. هذا لو قدرنا تصور الترجيح. هذا كلام الأئمة، وهو حق واضح.
فأما ما ذكره الإمام من أن العامي لا يتخير في تقليد من يشاء [من المفتين]، [ولكن عليه] ضرب من النظر. فهذا مما [اختلف فيه الناس]. وهذا الكلام يتعلق بالتقليد والاستفتاء، ولم يورده الإمام في هذا','4','206',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2598,'الكتاب، [ووعدنا به يصنف فيه كتابًا تتمة لهذا المجموع، ولم نظفر به في هذا الكتاب]. ولم نعلم هل صنفه أم لا؟ ونحن نذكر ها هنا ما تمس الحاجة إليه. وبالله (١٢٩/ ب) التوفيق. ويتم الغرض منه برسم مسائل.

<span data-type="title" id=toc-470>مسألة: التقليد: </span>قبول الغير بلا حجة. وليس ذلك طريقًا للعلم، لا في الفروع ولا في الأصول. وذهبت الحشوية [والتعليمية] إلى أن طريق معرفة الحق التقليد، وأن النظر والبحث حرام. ويدل على بطلان مذهبهم مسالك:
الأول: أن صدق المقلد لا يعلم ضرورة، بل بدليل، ودليل صدق مدعي الرسالة المعجزة، فيما علم صدقه، وصدق من صدقه الرسول [- صلى الله عليه وسلم -]. وصدق كلام الله تعالى. إما أن يرجع إلى ثبوت كلام النفس على وفق العلم، وإما أن يستند إلى إخبار الرسول [- صلى الله عليه وسلم -] عن صدقه، على حسب اختلاف','4','207',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2599,'المتكلمين في ذلك. وقد قدمنا فيه قولًا مقنعًا. [ويجب] على القاضي الحكم بقول العدول، لا على معنى أنه يعتقد صدقهم، بل لأن الله أوجب عليه الحكم عند ظن الصدق. وكذلك المفتي عند رواية خبر الواحد. وكذلك يجب على العامي اتباع المفتي، إذ دل الإجماع على أن فرض العوام ذلك، كذب المفتي أو صدق، أصاب أم أخطأ. فقبول قول المفتي والشاهد، لزم بحجة الإجماع، فهو قبول قول بحجة، فلم يكن تقليدًا على ما حددنا به التقليد. فحيث تقوم الحجة، فليس تقليدًا. وإذا لم تقم حجة، فيجب القول، بل بحكم مجرد.
الثاني: أن يقال: أتحيلون الخطأ على من قلدتموه أم تجوزونه؟ فإن جوزتموه، فأنتم شاكون في صحة مذهبكم، وإن أحلتموه، فبم عرفتم استحالته؟ أبضرورة أم بنظر؟ أم تقليد ولا ضرورة [ولا دليل]؟ فإن قلدتموه في قوله: إن مذهبه حق، فمن أين عرفتم [صدقه في تصديق نفسه، وإن قلدتم فيه غيره، فبم عرفتم] صدق المقلد الآخر؟ وإن عولتم على سكون النفس إلى قوله، فبم تفرقون بين سكون أنفسكم (١٦٧/ أ)، وسكون نفوس غيركم من الكفار؟ وبم تفرقون بين قول مقلدكم: [إني] صادق، وبين قول مخالفيكم؟
المسلك الثالث: أن يقال لهم: هل تعلمون وجوب التقليد أم لا؟ فإن لم تعلموا، فلم قلدتم؟ وإن علمتم، فبضرورة أو نظر أو تقليد؟ ويعود الكلام الأول حرفًا حرفًا. فإن قيل: عرفنا صحته، لأنه مذهب الأكثرين، فهو أولى بالاتباع.','4','208',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2600,'فنقول: بم أنكرتم على من يقول الحق دقيق غامض، لا يدركه إلا الأقلون، ويعجز عنه الأكثرون، لأنه يحتاج إلى شروط [كثيرة] من الممارسة والتفرغ للنظر، واتقاد القريحة، والخلو عن الشواغل. ويدل عليه أنه - صلى الله عليه وسلم - كان محقًا، وكان في أول [أمره] منفردًا بدينه عن بقية الخلق. فلو استدل مستدل على بطلان قوله بانفراده بدينه، هل كان مصيبًا في استدلاله؟ وقد قال الله تعالى: {وإن تطع أكثر من في الأرض يضلوك عن سبيل الله}. وقال - صلى الله عليه وسلم -: "بدأ الإسلام غريبًا وسيعود غريبًا". وقال: "وهم يومئذ الأقلون".
[فإن قيل: ] [فقد] قال - صلى الله عليه وسلم -: "عليكم بالسواد الأعظم". "ومن سره أن يسكن بحبوحة الجنة، فليلزم الجماعة، وإن الشيطان مع الواحد، وهو من الاثنين أبعد". قلنا أولًا: وبم عرفتم صحة هذه الأخبار، وليست متواترة؟ فإن كانت عن تقليد، فبم تنكرون على من يعتقد بطلانها؟ ثم إن صح ذلك، فالمراد بهذه الأخبار: اتباع أهل الإجماع، أو النهي عن الخروج على الأئمة الصالحين، أو الحث على طلب الرفيق في الطريق، وليس (١٣٠/ أ) يرجع هذا إلى التقليد. فإن صح أن المراد اتباع الأكثر، فليس هذا تقليدًا، بل هو اتباع قول','4','209',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2601,'بحجة. وقد تمسكوا بشبهتين:
إحداهما- أن الناظر متورط في شبهات، وقد كثر ضلال الناظرين، فترك الخطر وركوب السلامة أولى. قلنا: وقد كثر ضلال المقلدين من أهل الكفر والضلالات، فبم فرقتم بين تقليدكم وتقليد سائر الكفار، حيث قالوا: {إنا وجدنا آباءنا على أمة}. ثم نقول: إذا وجدت المعرفة، كان التقليد ضدها، فكأنكم أنكرتم النظر حذرًا من الوقوع في الجهل، [فاستعجلتم] الوقوع فيه حذرًا من إفضاء النظر إليه.
الشبهة الثانية: تمسكهم بقول الله تعالى: {ما يجادل في آيات الله إلا الذين كفروا}. نهى عن الجدال في القرآن، والنظر يفتح باب [الجدال]. قلنا: نهى عن الجدال في الباطل دون الجدال في الحق، وقد قال الله تعالى: {وجادلهم بالتي هي أحسن}. وقال: {ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن}. وقال حكاية عن (١٦٧/ ب) قوم نوح: {قالوا يا نوح قد جادلتنا فأكثرت جدالنا}. وقال تعالى: {ولا تقف ما ليس ل كبه علم}. وقال: {وأن تقولوا على الله ما لا تعلمون}. وذم الله تعالى قومًا بقولهم: {إنا','4','210',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2602,'وجدنا آباءنا على أمة وإنا على آثارهم مهتدون}. وفي آية أخرى: {مقتدون}. وقال الله تعالى: {وإن الظن لا يغني من الحق شيئًا}. [والآيات] الدالة على النظر والاعتبار في كتاب الله تعالى كثيرة، كقوله: {أولم يتفكروا}. {أولم ينظروا}. {فاعتبروا يا أولي الأبصار}. وعظم الله شأن العلماء فقال [تعالى]: {يرفع الله الذين آمنوا منكم والذين أوتوا العلم درجات}. وقال - صلى الله عليه وسلم -: "يحمل هذا العلم من كل خلف عدوله، ينفون عنه تحريف الغالين، وتأويل الجاهلين، وانتحال المبطلين". وقال ابن مسعود: "لا تكونن إمعة، قيل: وما الإمعة؟ قال: أن يقول الرجل: أنا مع الناس إن ضلوا ضللت، وإن اهتدوا اهتديت، ألا لا يوطنن أحدكم نفسه أن يكفر إن كفر الناس".','4','211',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2603,'<span data-type="title" id=toc-472>مسألة: [العامي] يجب عليه الاستفتاء واتباع العلماء</span>. وقال قوم من القدرية: يلزمهم النظر في الدليل واتباع الإمام المعصوم. وهذا مختص بمسائل الشريعة. وأما ما يتعلق بأصول الدين، فقد نقلوا مذهب المتكلمين في ذلك. وذهب الأستاذ أبو إسحاق إلى إلحاق قواطع السمع بالمعقولات، فأوجب على العوام تحصيل علم كل مسألة، مدركها القطع، وإن كانت فقهية، وهذا [باطل] بمسلكين:
أحدهما- إجماع الصحابة، فإنهم كانوا يفتون العوام ولا يأمرونهم بنيل درجة الاجتهاد، وذلك معلوم على الضرورة تواترًا.
المسلك الثاني- أن العامي بالإجماع يكلف بالأحكام، [وتكليفه] طلب رتبة الاجتهاد محال، وذلك غير ممكن بحال. ولو كلف ذلك، لأفضى إلى خراب العالم، وتلاف الدنيا، وإقبال الخلق بجملتهم على طلب العلم، والإضراب عن أسباب المعاش، وذلك أمر يعلم بطلانه ضرورة.
ثم أنه لو قدر ذلك، فما الحكم عليه قبل نيل درجة (١٣٠/ ب) الاجتهاد؟ أليس لله تعالى [عليه] أحكام ثابتة في القلب والجوارح؟ فهل تكون الأحكام ساقطة عنه إلى بلوغ هذه الدرجة؟ سقوطها عنه باطل، فإنها لو كانت ساقطة، لم يجب الطلب، وإن كانت متوجهة، فقد استقرت الأحكام عليه قبل بلوغ هذه [الرتبة]، فيصح ثبوت الأحكام عليه من غير أن يجب تحصيلها.
فإن قيل: فقد رفعتم التقليد، وهذا عين التقليد. قلنا: ليس كذلك، فإنا','4','212',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2604,'فسرنا التقليد بأنه: قبول قول الغير بلا حجة. وهذه أقوال قامت عليها أدلة، فلا يكون تقليدًا.
فإن قيل: فقد رفعتم التقليد [من الدين] (١٦٨/ أ)، وقد قال الشافعي: لا يحل تقليد أحد إلا رسول الله - صلى الله عليه وسلم -. فقد جعله تقليدًا. قلنا: التقليد يطلق حقيقة، ويطلق مجازًا، فالحقيقي ما ذكرناه، وقد يطلق مجازًا على: قول لا يطلب عليه دليل خاص. والمعجزة لما دلت على الصدق جملة، ولم تفتقر إلى الإقامة، مقترنة بكل قول، [سُميت] تقليدًا مجازًا.
والذي يدل على التجوز، أنه أبطل تقليد من سوى الرسول [- صلى الله عليه وسلم -]، [واستثنى] قول الرسول [- صلى الله عليه وسلم -]، وهو مدلول على صدقه، فلم يكن من القبيل الذي رددناه.

<span data-type="title" id=toc-473>مسألة: لا يستفتي العامي إلا من عرفه بالعلم والعدالة</span>، أما من عرفه بالجهل، فلا يسأله وفاقًا. وإن سأل من لا يعرف جهله، فقد قال قوم: يجوز، وليس عليه البحث. وهذا فاسد، لأن كل من وجب عليه قبول قول غيره، وجب عليه معرفة حاله، فيجب على الأمة معرفة حال الرسول [- صلى الله عليه وسلم -]، بالبحث عن معجزته، فلا يؤمن بكل مجهول يدعي النبوة، ووجب على الحاكم معرفة حال','4','213',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2605,'الشاهد في العدالة، وفي الستر عند قوم، إذ الستر أمارة العدالة عندهم، ولا يصح أن يكون الستر علامة العلم بحال، إلى غير ذلك من مسائل الشريعة، كحال الرواة والقضاة. وعلى الجملة، كيف يتصور أن يسأل من هو أجهل من السائل؟
فإن قيل: إذا لم يعرف عدالة المفتي، هل يلزمه البحث؟ أم يجوز له الاستفتاء من غير بحث؟ قلنا: من عرفه بالفسق، فلا يقلده، ومن عرفه بالعدالة يسأله، ومن لم يعرف حاله، فيحتمل أن يقال لا يقتحم، بل [يسأل] عن عدالته أولًا، فإنه لا يأمن كذبه وتلبيسه. ويحتمل أن يقال ظاهر حال العالم العدالة، لا سيما إذا اشتهر بالفتوى. ولا يمكن أن يقال ظاهر حال الخلق العلم، ونيل درجة الاجتهاد. والجهل [أغلب] على الخلق، والناس كلهم عوام، إلا [الأفراد]، والعلماء كلهم عدول إلا الشواذ.
فإن قيل: فإن وجب السؤال لمعرفة علمه وعدالته، فيشترط التواتر أم لا يفتقر إليه؟ قلنا: يحتمل أن يشترط التواتر، فإن ذلك يمكن، ويحتمل أن يقال: يكفي غالب الظن، الحاصل بقول عدل أو عدلين، وهذا هو الظاهر عندنا. وقد اخترنا جواز نقل الإجماع على ألسنة الآحاد، وترتب العمل عليه. وهذا قريب منه.

<span data-type="title" id=toc-474>مسألة: إذا لم يكن في البلد إلا مفتٍ واحد، وجب على العامي مراجعته</span>. وإن كانوا جماعة، قال القاضي: فله أن يقلد من يشاء، ولا يلزمه (١٣١/ أ) تقليد','4','214',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2606,'الأعلم، كما فعل في زمن الصحابة - رضي الله عنهم -: إذ سأل العوام الفاضل والمفضول، ولم يحجر على الخلق في شؤال [غير] أبي بكر (١٦٨/ ب) وعمر وغير الخلفاء. وأكثر الفقهاء على خلاف ذلك، وقالوا: يجب تقليد الأعلم، وإن استووا تخير العامي.
وقال القاضي: وهذا يخالف إجماع الصحابة، إذ لم يحجر الفاضل على المفضول الفتوى، بل لا تجب إلا مراجعة العالم العدل، وقد عرف كلهم بذلك. نعم، إذا اختلف عليه مفتيان في حكم، فإن تساويا، راجعهما مرة أخرى، وقال: [تناقضت] فتواكما، وتساويتما عندي، فما الذي يلزمني؟ فإن خيراه تخير، وإن اتفقا على الأمر بالاحتياط، أو [الميل] إلى جانب معين، [فعل]، وإن أصر على الخلاف، فلم يبق إلا التخير، فإنه لا سبيل إلى تعطيل الحكم، وليس أحدهما أولى من الآخر. والأئمة كالنجوم فبأيهم اقتدى اهتدى. هذا تمام كلام القاضي.
والصحيح عندنا خلاف ذلك، بل على العامي أن يبحث بحسب إمكانه، فإذا غلب على ظنه تقديم واحد معين على غيره، لزمه اتباعه. والدليل على ذلك: أنا قلنا: الحكم يترتب على العلم تارة، وعلى الظن أخرى. [فإذا] غلب على ظن العامي أن أحد الرجلين أعلم، غلب على ظنه أنه إلى الصواب أقرب، والحق في قوله أغلب. فإذا خافه متمسكًا بقول غيره، فهو يقدم على الفعل، وهو يظن [أنه] خلاف حكم الله تعالى عليه. وهذا محذور على','4','215',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2607,'الجملة، إلا في مواضع التعبد، كترك الحكم بشهادة العبيد العدول، والامتناع من القضاء بقول المرأة الواحدة، وإن غلب على الظن صدقها. ولكن هذا في مواضع قليلة، ثبت فيها التعبد، وأكثر الشريعة على خلاف ذلك.
اعترض القاضي على هذا بأن قال: لا يصح الاعتماد على مجرد ميل النفوس، بل ينبغي أن يفرق العاقل بين الوهم والظن، فإن الظن يرجع إلى أسباب صالحة، والوهم ميل النفس من غير سبب صالح لذلك. والعامي لا يقدر على تحصيل الظن، بأن أحد [العالمين] أعلم من الآخر، فإن قضايا التفضيل أمور غامضة، وعليها أدلة خفية، فمن أين يقدر العامي على ذلك؟ فإن قدر عليه، فلينظر في نفس المسألة، وليس له أهلية ذلك.
وهذا كلام عظيم، ولكن الجواب عنه أنا نقول: من مرض له مريض- وإن لم يكن طبيبًا- أو اجتمع في البلد أطباء، وغلب على ظنه أن أحدهم أطب من الباقين، فعدوله إلى الضعيف، مع التمكن من القوي، عين التقصير. ولا ينكر حصول غلبة الظن بأطب الرجلين للعامي. فكذلك يجري هذا في أعلم الرجلين.
ولكن في هذا الجواب نظر، وذلك أنه يتأتى [للعامي] حصول غلبة الظن بتفاصيل الأطباء في الطب، لأن الآثار عاجلة، فيعرف ذلك بكثرة نجح معالجته، وحصول (١٦٩/ ا) البرء لمن يلاطفه، وآثار العلم مدخرة في الدار الآخرة، لا يوقف عليها إلا بإخبار الصادق، وأدلتها في الدنيا غامضة، لا يقف عليها العوام بحال. فمن هذه الجهة تغمض المسألة.','4','216',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2608,'ولكن قد يحصل الظن بالقول الصريح من أحد العالمين بان يقول: فلان أعلم مني، كما قال أبو موسى الأشعري [- رضي الله عنه -] عندما خالفه ابن مسعود: "لا تسألوني ما دام هذا الحبر (١٣١/ ب) بين أظهركم". فهذا طريق يغلب على الظن كونه أعلم منه.
الوجه الثاني: أن يعرف ذلك بقول ذوي المعرفة من العلماء وأهل العلم، أو تطول المجالسة، ورجوع أحدهما إلى الآخر في كثير من الأحوال. فهذه الأمور وما يضاهيها تحصل غلبة الظن بمزية العلم في حق [العامي]. فإذا ثبت حصول غلبة الظن بالتفاوت، وجب الاعتماد على قول الأعلم، على ما قررناه. وبالله التوفيق.

قال الإمام: <span data-type="title" id=toc-475>(مسألة: أجمع المحققون على أن العوام ليس لهم أن يتعلقوا بمذاهب<span data-type="title" id=toc-476> أعيان الصحابة</span>، بل عليهم أن يتبعوا مذاهب الأئمة) </span>إلى قوله (وسيأتي تفصيل ذلك في كتاب الفتوى، إن شاء الله تعالى). قال الشيخ: هذا الذي ذكره الإمام ممنوع من حيث الجملة والتفصيل. أما [مصيره] إلى أن المتأخر','4','217',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2609,'أقدر على التفصيل والتهذيب، فغير صحيح مطلقًا. وكيف يمكن دعوى ذلك على العموم، وقد صودف [الوجود] على خلاف ذلك؟ والذين سبقوا إلى وضع العلوم من الهندسة والطب، كبقراط وغيره من الحكماء اعترف بعدهم بالعجز عما وضعه الأولون.
وهذا سيبويه [رحمه الله] أول من صنف كتابًا مبسوطًا في العربية، وكل من جاء بعده حذا حذوه، وسلك مسلكه، واعترفوا بالعجز عن بلوغه فيما وضعه وأسسه. وكيف ينكر ذلك، والتصنيف على قدر قوة العلم؟ وإنما أضرب الأولون من الصحابة والتابعين عن التصنيف، ولو صنفوا، لم ينته إليهم وضع المتأخرين. إذ الوضع على حسب العلم.
وأما ما مثل به من الحرف والصناعات، فهذه أمور تختلف باختلاف الأغراض. فقد يحدث لقوم من الغرض، ويستحسنون ما لا يحسن عند غيرهم،','4','218',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2610,'فلا يجعل الثاني أولى بالاقتداء به من الأول. وكيف تمكن المنازعة في أن من سبق أعلم؟ أم كيف تمكن المنازعة في أن التصنيف يتفاضل بحسب تفاضل علم الوضعين؟ [هذا بين] لا شك فيه، ولم يضرب الأولون عن [تلفيق] العبارات، لقصورهم عنها، ولكن لانشغالهم بما هو أهم، ولفهم بعضهم عن بعض الأمر بالإشارة العربية، والعبارة المختصرة.
فهذا هو سبب قلة كلامهم، (١٦٩/ ب) [فاعتنى المتأخرون] بتهذيب العبارات، إذ لم يبق لهم شيء يفعلونه سواها. والشريعة كانت في [زمان] رسول الله - صلى الله عليه وسلم - غضة طرية، قال الله تعالى: {اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينًا}. فبكى أبو بكر - رضي الله عنه - وقال: "ما تم شيء إلا وأخذ في النقصان". وقال النبي - صلى الله عليه وسلم -: "لا ينتزع العلم من صدور','4','219',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2611,'الرجال انتزاعًا، ولكن بقبض العلماء، حتى إذا لم يبق عالم، اتخذ الناس رؤوسًا جهالًا". فالعلم يتناقص، اعترف بذلك خيار [العلماء من] التابعين، بالإضافة إلى الصحابة، بل اعترف به أحداث الصحابة، بالإضافة إلى مشايخهم، مثل ابن عباس [سئل]: " [أيهما أعلم هو أو علي]." فذكر مثالًا يدل على قلة علمه بالإضافة إلى علي كرم الله وجهه. فإذا كان كلما تقادم الزمان نقص العلم، فالوضع على حسب ذلك بلا ريب، إذ لا يضع إلا ما علم.
وأما ما ذكره من أن (١٣٢/ أ) الشافعي [- رضي الله عنه -] تأخر عن الأئمة','4','220',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2612,'الناخلين، فيكون مذهبه أولى، فقد بينا أن ذلك غير لازم أصلًا، وحققنا أن حسن الوضع، فرع كمال العلم، ومزيد الإدراك. وأما ما وجهه على نفسه، فلازم على قاعدته، فإنه جعل جودة النخل تزيد على تقدير التأخير. قيل له: فقد تأخر عن الشافعي غيره، فليكن نخله أولى، كابن سريج وغيره من','4','221',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2613,'العلماء. وجوابه أنه قال: لسنا نرى أحدًا بلغ [منصب] الشافعي في ذلك، وإن كان متأخرًا عنه. دل ذلك على أن مجرد التأخير في النخل، لا يقتضي اتباعًا حتى ينضم إليه إثبات المزية في العلم. وإذا منع الإمام من الرجوع إلى [منتخل] غير الشافعي، وإن كان متأخرًا عنه، بناءً على أنه لا يساوسه في العلم، أمكن أصحاب مالك، وغيرهم [ممن ينخل] المذاهب أن يدعوا مثل ذلك لأئمتهم. فلا يكاد يخفى أنه إذا تساوت الدعاوى [من] غير برهان تساقطت، ويبقى [الفضل] [للمتقدم].
قال الإمام: (طريقة أخرى: وهي أن المذاهب تمتحن بأصولها) إلى','4','222',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2614,'آخرها. قال الشيخ: قوله: المذاهب تمتحن بأصولها، فإنها تصح بصحتها، وتعوج باعوجاجها، وهذا كلام صحيح، إذا جرى التفريع على مقتضى الأصل، وإلا فقد يكون الأصل مستقيمًا، ويسوء التفريع. وإنما الكلام عند جريان التفصيل على مقتضى التفريع.
وقوله: سبيل محنة [الأصول] معرفتها أفرادًا ثم تنزيلها منازلها، كلام صحيح، ورجوع الأصل إلى الكتاب والسنة والإجماع والاستنباط، لا مراء فيه. وأما المصير إلى أن الشافعي أعرف الخلق بكتاب الله [تعالى]، فغير مسلم.
وقوله: إنه عربي مبين، تفقأت عينه (١٧٠/ أ) بيضة قريش، لا نزاع في كونه قرشيًّا. وأما أن [يترتب] على ذلك أنه أعلم [الخلق] بالكتاب، فلا','4','223',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2615,'يُسلم، وليس للشافعي ممن ينطقه طبعه، ولذلك رد الأصوليون عليه في اعتقاد المفهوم، ولم يأخذوه منه [رد القبول].
والذي يتعلق من الكتاب بالأحكام مضبوط، وهو قريب من خمسمائة آية، وقد تتبعها العلماء فوجدوا اختلافًا في مواضع الاحتمال، ولم ينفرد الشافعي بفهم معنى منها على حال. ولو ذهبنا نتتبع تلك المسائل، ونتقصى أنواع الكلام، لخرج الكلام عن مقصود الأصول.
وأما ما يتعلق بالسنة، ومعرفة الناسخ والمنسوخ، وأسباب النزول، فغيره هو المشهور بذلك، المعروف [به]، وهو مالك [رحمة الله عليه]. وقد أطبق أهل هذا الفن على أن الشافعي [رحمه الله] لا يبلغ مبلغ مالك فيه. وقد اجمع علماء الحديث على ضبط مالك وحفظه وإتقانه للرواية من غير خلاف بين [أهل] النقل. قال النسائي: "لا أحد بعد التابعين أجل ولا أحفظ، ولا آمن','4','224',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2616,'على سنة رسول الله - صلى الله عليه وسلم - من مالك بن أنس رحمه الله". وكذلك القول في معرفة الإجماع، فإنه يتعلق بنقل الآثار، وما يصح منها وما لا يصح، والقول في معرفة الآثار كالقول في معرفة الأخبار، إذ المنهاج واحد، فالأعلم بأحدهما إذا اعتنى بالآخر، فهو به أعلم، ولا يكاد يخفى اعتناء مالك بآثار الصحابة [- رضي الله عنهم -] وسننهم، وهي أفعالهم وأقوالهم. هذا قولنا في معرفة الأصول (١٣٢/ ب) أفرادًا.
وأما تنزيلها منازلها، وما حكاه الإمام عن الشافعي من أنه قدم كتاب الله تعالى، ثم أتبعه سنة رسول الله - صلى الله عليه وسلم -، فهذا الترتيب ليس [تشوفًا] على حال. وذلك أن الكتاب والسنة يقعان عندنا في درجة واحدة، [لأنا] لو صادفنا نص سنة متواترة، لم نتركها [لظاهر كتاب، وكذلك إذا وجدنا نص كتاب، لم','4','225',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2617,'[نتركه]] لظاهر متواتر، ولا لخبر واحد.
وأما كونه لا ينظر إلى القياس إلا بعد فقدان الكتاب والسنة، فهذا أيضًا غير صحيح، وكيف يمكن ذلك، وقد يكون القياس محصلًا علمًا مستندًا إلى أصل مقطوع به؟ وتكون السنة خبر واحد ظاهر؟ [وكذلك قد] يكون الكتاب مشتملًا على ظاهر في مخالفة مثل هذا القياس. فتأخر النظر في القياس حتى تفقد المتعلقات من الكتاب والسنة، غير صحيح.
وأما المصير إلى أنه أعلم خلق الله بمصالح الشريعة، وما يقبل من','4','226',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2618,'المعاني، وما لا يقبل، فهذا غير مسلم، وذلك أن المعاني لا تعتمد أعيانها لذواتها، وإنما تعتمد لالتفات الشريعة إليها، وليست مستوية في التفات الشريعة. وإذا كانت تقوى بقوة الشهادة، (١٧٠/ ب) [وتضعف] أيضًا على هذا الحد، فكل من كان أعلم بالمنقولات وصحيح الأخبار وسقيمها، ومعرفة آثار الصحابة ومستنداتهم، وما أضربوا عنه من المعاني، وما قبلوه، وما قدموه [منها]، وما أخروه، فهو الأعلم بالمصالح.
والسبب في ذلك، أن الكتاب قل أن يتعرض للأحكام إلا من جهة كلية، والسنة هي المفسرة المبينة، وأصحاب رسول الله - صلى الله عليه وسلم - كانوا أعلم بسنته، لحصول الشهادة، ودلالة قرائن الأحوال، ونزول الوحي بين أظهرهم، فهم أعلم خلق الله بالشريعة، وأجراهم على العمل بها. ولما ذكر رسول الله - صلى الله عليه وسلم - اختلاف هذه الأمة [قال]: "خير القرون ما أنا عليه وأصحابي". فهم القدوة والأسوة، وأعلم الخلق بهذه الشريعة. هذا لا يخالجنا فيه ريب أصلًا. فمن كان أعلم بآثارهم وأحوالهم، كان أعلم بتفاصيل المعاني المقبولة والمردودة.
قال الإمام: (طريقة أخرى: وهي تشتمل على نظر كلي إلى الفروع) إلى','4','227',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2619,'قوله: (فهذه مرامز كافية فيما نحاوله). أما تقسيم الشريعة إلى مأمور به ومنهي عنه ومباح، [فتقسيم] صحيح.
وأما قوله: "الأئمة من قريش". فهذا الحديث لم يرو على هذا الوجه، ولو كان كذلك، لما خالف الأنصار يوم السقيفة، ولانقادوا [إلى الخبر]، ولكان المهاجرون يتمسكون بذلك، وما كانت المسألة إلا اجتهادية. نعم، الذي','4','228',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2620,'صح أنه قال: "لا تقوم الساعة حتى تكون اثني [عشر] خليفة كلهم من قريش". وكيف يصح حصر الأئمة في قريش، وقد قال - عليه السلام -: "أعلم أمتي بحلال الله وحرامه: معاذ بن جبل". وكان من الأنصار؟ وقال: "أفرضكم زيد". وقال لأبيّ: "إن الله أمرني أن أقرأ عليك [القرآن] "؟ ولم يذهب أحد من الأئمة إلى حصر الأئمة في قريش. ثم لو قدرنا ورود ذلك، عُلم','4','229',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2621,'على قطع أن الرسول [- صلى الله عليه وسلم -] لم يرد بذلك أمر الفتوى والعلم، وإنما هذا يشير إلى الخلافة.
وأما ما ذهب إليه من أن مالكًا من الموالي، فتخرص وافتراء، ومالك رحمه الله من بني الأصبح، وهم أهل دين وفضل وعلم بالمدينة. وعمه أبو سهيل بن مالك، من أهل الحديث، وخيار التابعين، معروف لهم ذلك. على أنه لا يتعلق بكونه من الموالي شيء من التهجين فيما يتعلق بالعلم. وقد كان أعيان التابعين من الموالي، كزيد بن أسلم، والحسن البصري، وابن سيرين وغيرهم. فهذا التهجين الذي ذكره الإمام، لم يصر إليه أحد من أهل الإسلام. وقد كان علي بن الحسين، على ما خصه الله به من الشرف والدين،','4','230',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2622,'يحضر مجلس زيد بن أسلم، وهو من الموالي. والاشتغال بهذا اشتغال بما لا يفيد.
قال الإمام: (وإذا أردنا (١٧١/ أ) أن نعبر عن الأئمة الثلاثة) إلى قوله (ثم يستأنس بما يبلغه وفاقا). قال الشيخ: هذا الذي ذكره الإمام هو الظاهر من حال أبي حنيفة، وسبب ذلك عدم إحاطته بالأخبار والآثار، فربما لم يقف على الصحيح، فيفوته الحكم المتلقى منه ومعناه، [فيختل] عليه أمر الأصل','4','231',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2623,'والفرع جميعًا. وربما يتمسك بخبر صحيح، فيختل عليه حكم الأصل ومعناه، إذ قد يستنبط المعنى من أصل غير صحيح. فإذًا أتى عليه [الخلل] من أجل [إهمال] الأخبار والآثار، فكل من كان أعلم بذلك، فهو أحق بالاتباع، وأقرب إلى الصواب، وأبعد عن الزلل في الأصول والفروع جميعًا. وفي هذا تنبيه لذوي العقول على ما نريد من التفضيل والتقديم.
قال الإمام: (وأما الإمام مالك - رضي الله عنه -، فلا يشق غباره) إلى قوله (فلا وجه لإطلاق أيدي الولاة في الدماء والأموال). قال الشيخ: ما ذكره عن مالك [رحمه الله] من الحفظ والضبط والأمانة في الحديث والعلم والآثار، كلام صحيح، [معروف عند المسلمين]، وقد بينا أن فهم الشريعة على ذلك يترتب، فهو عندنا أعلم الأئمة بكتاب الله وسنة رسوله [- صلى الله عليه وسلم -] والمعاني','4','232',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2624,'المستنبطة منهما، وما يتعلق بالإجماع والاختلاف.
وأما ما أضافه إليه [من أنه] ينحل بعض الانحلال، ويثبت أمورًا عظيمة مرتبة على أقضية [للصحابة]، لم يحط بمعناها، فتخرص منه عليه وتقول.
وأما ما حكاه عنه من أنه يجوز إراقة الدماء وأخذ الأموال من غير استحقاق، فمعاذ الله أن يكون كذلك.
وأما كونه قال: إنه استند في المال والدم إلى قضاء عمر [- رضي الله عنه -] في السياسة والإيالة، فهذا غير صحيح، والمنقول عن عمر - رضي الله عنه - التأديب بأخذ الأموال. وقد "ألقى عمر - رضي الله عنه -[لبنًا] في الأرض غُش [بماء]، أدبًا لصاحبه".
وأما قضية خالد وعمر، فالإمكان فيما قاله الإمام محال، ولكنه [بعيد','4','233',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2625,'جدًا]، فإنه يتعذر في العادة الإحاطة بمقدار المحاورة حتى ينتهي الأمر إلى خلع [إحدى] النعلين، فالظاهر التأديب.
وأما المصير إلى أنه لا سبيل إلى التأديب بأخذ المال، فقد صح عن عمر - رضي الله عنه - أن عبدًا لحاطب بن بلتعة انتحر ناقة للبراء، فذكر الأمر لعمر - رضي الله عنه - فأحضر حاطبًا وقال: "أراك تجيعهم، كم تساوي ناقتك يا براء؟ فقال: كنت والله يا أمير المؤمنين أمنعها من أربعمائة درهم. فقال: يا حاطب قم فأغرم ثمان مائة درهم". فهذا تأديب بالمال. وكيف ينكر ذلك) ١٧١/ ب)، وقد قال أبو','4','234',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2626,'سعيد الخدري: "إن النبي [- صلى الله عليه وسلم -] قال: "من وجدتموه يصيد في حرم المدينة (١٣٣ م ب) فلكم سلبه"؟ وأخذ بذلك سلب غلام وجده يصيد في حرم المدينة، وقال: "لا أرد شيئًا نفلنيه رسول الله - صلى الله عليه وسلم - ". فمالك رحمه الله أعلم بما يعتمد من المعاني وما لا يعتمد. ولقد قال في قضاء عمر - رضي الله عنه - على صاحب الجنان: أن يمر الماء عليه لصاحبه لما امتنع، فقال [له] عمر: "والله ليمرنه ولو على بطنك". قال مالك: "ليس العمل عليه، ولا أرى أن يجبر أحد على هذا".
قال الإمام: (وأما الإمام الشافعي فإنه أعرف [خليقة] الله بأصول','4','235',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2627,'الشريعة وأضبطهم لها) إلى قوله (وسنصف ذلك النظر وحدَّه). قال الشيخ: هذا كلامه في تقديم الشافعي [وتعين] تقليده، ونحن نرى خلاف ذلك، ونقول: مالك أولى بالاتباع، ويدل على ذلك مسالك:
الأول: ما نقل عن النبي - صلى الله عليه وسلم - أنه بشر به فقال: "ينقطع العلم حتى لا يبقى عالم أعلم من عالم المدينة، ويضرب الناس إليه أكباد الإبل". ويتأول فيه سفيان بن عيينة- على فضله- أنه عالم المدينة، وكذلك تأول فيه ابن جريج وغيره. وذكر أن سفيان قال: "كانوا يرونه مالك بن أنس". ولم يشر سفيان- والله أعلم- إلا إلى من أدركه، وقد أدرك التابعين. قال عبد الرحمن بن','4','236',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2628,'مهدي: "سمعت ابن عيينة يقول: كانوا يرونه [مالك بن أنس [. قال ابن مهدي: يعني التابعين. فحسبك [ذلك] من بعض مناقبه، مع الإشفاق، وورعه في الفتوى [وتوقفه]. وفيه أمور أكثر من هذا. هذا تقديمه بالنظر إلى الخبر، وقد اشتهر بكونه إمام دار الهجرة، ولم يثبت هذا الاسم لأحد غيره. وما أعظم هذا النعت. وما أتم هذا التفضيل.
ومجموع ما [نوضح] به ترجيح هذا المذهب بالنظر طرق:
منها: أنه - رضي الله عنه - من أهل دار الهجرة، وهي المدينة على ساكنها [أفضل] السلام، وأهلها هم أصحاب رسول الله - صلى الله عليه وسلم -، نصر الله بهم الدين، وأظهر بهم','4','237',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2629,'[كلمته]، قال الله سبحانه [وتعالى]: {كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر}. يعني: [أنتم]. قال مالك: هم سلف هذه الأمة، وهم العصر الأول من أمة نبيه [- صلى الله عليه وسلم -] الذين أخبر - صلى الله عليه وسلم - أنهم خير القرون من أمته، وهم الذين اختارهم [لصحبة نبيه]، وانتخبهم لنصرته، ورضيهم أئمة لأمته، وضرب بهم الأمثال في سالف كتبه، (١٧٢/ أ) وارتضى منهم خلفاء لإقامة دينه، وإحياء سنة رسوله، وأثنى على السابقين بسبقهم، ومدح التابعين بحسن اتباعهم إياهم، ورضي بذلك منهم، [قال]: {[والذين] جاءوا من بعدهم}. وجعل [متبوأهم] دار النبوة والعلم والحكمة.
فما علموه ونقلوه، لزم أهل الآفاق الائتمام بهم فيه، إذ هم الذين وعوا عن رسول الله - صلى الله عليه وسلم - ما شرع من الدين، وسن من السنن، مع [توقيفهم] إياهم على ماضي ذلك، فكان لهم بذلك فضل العيان والمشاهدة على من نأت به الدار، وبلغته الأخبار، التي لا تحل من القلوب محل المعاينة.
وقد كان الرسول - عليه السلام - بغير المدينة في أسفاره، وخرج عنها بعض','4','238',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2630,'أصحابه، (١٣٤/ أ) إلا أن أكثر مصير من صحبه إليها. والمقيم بها أكثر من الظاعن عنها. فكان بذلك عملهم في القلوب أثوى من عمل غيرهم.
وقد كان إجماع من بالمدينة في غير شيء حجة على من سواهم. ولم يجد مخالفنا بُدًّا من الرجوع إليهم في المد والصاع والأحباس وغيرها. فكانوا - رضي الله عنهم - على الحق الواضح، والطريق اللائح، لا يغيرون ولا يبدلون، بل لم يزالوا على ذلك حتى [لقوا الله - عز وجل -].
ثم سلك مسلكهم، ونهج منهجهم التابعون لهم بإحسان، فكانوا أعلم خلق الله بسنة رسول الله - صلى الله عليه وسلم -. ولقد كان الناس إذا اختلفوا في غيرها، جاءوا إليها ليعلموا ما عند أهلها. وهذا بين في النفوس، مستقر في القلوب. وليس [من] هو من أهل البلد في معرفة سيرة أهلها، كمن نقلت إليه أخبارهم. هذا معروف بالعادات.
ولم يزل الأمر على ذلك إلى [زمن] مالك - رضي الله عنه - وأفضت [إليه] الإمامة، ورجع الناس إلى قوله. قال بعض الناس: [قدمت] المدينة بعد موت نافع مولى ابن عمر بسنة، فوجدت الحلقة لمالك، وهو غلام. وكان - رضي الله عنه - أمير المؤمنين في السير في عصره، ورأس الراسخين في العلم في دهره، شهد بذلك رؤساء قرنه.
وشتان بين من نشأ في دار الهجرة على الوجه الذي ذكرناه، وبين من','4','239',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2631,'[تنقل] إليه بعض أخبار عن بعض أهلها. فهذا وجه في ترجيح مذهبه، والمصير إلى اتباع قوله.
الوجه الثاني: قُرب زمانه من التابعين، [وهو] من تابعي التابعين، وهو القرن الثالث الذي انتهت إليه شهادة الرسول [- صلى الله عليه وسلم -] حيث قال: "خير القرون قرني الذين بعثت فيهم، ثم الذين يلونهم، ثم الذين يلونهم". فقد أدرك - رضي الله عنه - جملة من التابعين، وسلك مسلكهم، واتبع طريقتهم في الديانة والصيانة، وإجلال السنة وتعظيم العلم، (١٧٢/ ب) حتى أنه - رضي الله عنه - مر يومًا على مجلس أبي حازم وهو يحدث، فجاوزه ولم يجلس، فقيل له في ذلك، فقال: لم أجد مجلسًا، وكرهت أن أحمل حديث رسول الله - صلى الله عليه وسلم - وأنا قائم. فهذا وجه.
ووجه آخر: وهو طول زمانه، واتساع أيامه، فإنه أقام دهره كله في مسجد رسول الله [- صلى الله عليه وسلم -]، لقراءة العلم وإقرائه، إلا ما يتفق له من حج أو عمرة، ولم تكن له سفرات، ولا ترددات في الأرض، بل كان العلم شغله [وطلبته].','4','240',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2632,'ومات رحمه الله وقد نيف على الثمانين سنة. ولشتان بين من لم يتعلق بالطلب إلا قليلًا، وبين من طالت مدته فيه.
ووجه آخر: وهو أنه - رضي الله عنه - لم يشتغل بغير العلم، ولم يسلك غير طريقه، مع شدة الملازمة وتمام العناية بالتحصيل، قال - رضي الله عنه -: كنت أخرج من منزلي صلاة الغداة إلى ابن هرمز، فأعود إلى الزوال، أو قريبًا من ذلك. [فانظر] هذه الملازمة، ودوام هذا الطلب.
فهذه الوجوه التي ذكرناها، لا يشاركه أحد من الأئمة المذكورين فيها، مع ما قدمناه من قوة الحفظ وشدة الوعي والبحث عن السنة والآثار، وشهادة الرسول [- صلى الله عليه وسلم -] (١٣٤/ ب). وفي هذا كفاية وبلاغ. والله الموفق [للصواب].
قال الإمام: (ثم نقول: ليس على المستفتي تعلق بمبادئ النظر) إلى','4','241',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2633,'آخره. قال الشيخ: قد قدمنا هذا الكلام للأستاذ، ورددنا عليه. وأكثر المسائل القطعية تفتقر إلى أدلة غامضة، من تقرير إثبات مستند الإجماع، والعلم بقواطع النصوص، ومعرفة الأقيسة القطعية، إلى غير ذلك من أمور غامضة، لا يقدر العوام على الإحاطة بها بحال.

قال الإمام: <span data-type="title" id=toc-480>(مسألة: ذهب معظم الأصوليين إلى أن المجتهد لا يجوز له الاقتصار على ترجيح مذهب [على مذهب]) </span>إلى آخره. قال الشيخ:','4','242',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2634,'[قد] قدمنا في حد الترجيح وبيانه ما يغني عن هذا، وذلك أنا قلنا: إن [المذاهب] [دعاوى]، إذا لم تعضد بأدلة متساوية، وإنما يتبين أمرها إذا نظر إلى أسبابها، وكل ذي مذهب [قل] أن يدل على مذهبه، لو اكتفى بترجيحه، لكان الترجيح دليلًا، والترجيح لا يكون دليلًا بحال. نعم، إذا تعارض عند العامي مجتهدان، وتناقضت عليه أقوالهما، ومالت نفسه إلى تفاوت بينهما، على حسب ما قررناه. فغنه أوجب عليه الرجوع إلى قول الأعلم عنده،','4','243',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2635,'وإن كان مستنده أمرًا لا يصح أن يكون مستندًا عند العلماء فيما يعملون به. فقد يظن الظان أن العامي تمسك في التقديم بترجيح مجرد، وليس الأمر كذلك، فإن الذي تمسك به هو دليل مثله، والأدلة (١٧٣/ أ) لا تدل [بأنفسها]، أعني الأدلة السمعية، وإنما تدل بنصب ناصب إياها أدلة. والعامي إذا كلف التمييز، ولا يقدر على هذا، فهو دليله، ويجوز عند التباس أوقات [الصلاة] الاعتماد على أمور، لولا الضرورة لما اعتمد عليها. والله المستعان.

قال الإمام: <span data-type="title" id=toc-481>(القول في ترجيحات الأدلة) </span>إلى قوله (ونحن نرسم ما يتعلق','4','244',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2636,'بهذا القسم). قال الشيخ: قوله: [إنما] مظنة الترجيحات تعارض صور الأدلة، هذا إنما يكون على إطلاق الدليل على الأمارة، فأما الدليل القاطع، فلا يتصور التعارض فيه.
ومعنى قوله: تعارض صور الأدلة، ولم يقل تعارض الأدلة، [فإن] الدليل على هذا الاصطلاح يرجع إلى العلامة، ولا يتصور تعارض علامتين على حكمين متناقضين. فإذًا يرجع الترجيح إلى تعارض صور العلامات، على معنى أن كل واحدة، لو انفردت، لكانت علامة على التحقيق عند قدوم، وهم الذاهبون إلى أن كل مجتهد مصيب.
وأما الذين ذهبوا إلى أن المصيب واحد، فلا يتصور عندهم التعارض بين العلامات على الحقيقة. فإنا قد قلنا إن التعارض على الحقيقة، إنما يكون على تقدير اشتمال كل واحدة منهما على شرائط الصحة، لو كانت منفردة. [ومن يقول المصيب واحد، لا يتصور ذلك عنده، لأنه يرى إحداهما ليست أمارة','4','245',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2637,'قطعًا، وإن كانت منفردة]. ولكن لما كان التعيين مردود شرعًا إلى الاجتهاد، فقد يطلق على ذلك لفظ الترجيح تجوزًا. وعلى الجملة البحث الخاص عن إدراك ما يظن كونه مناطًا مجمع عليه، وهو المقصود الآن، ونحن نذكر المسائل على هذا الشرط.
قال الإمام - رضي الله عنه -: (مسألة: إذا تعارض نصان على الشرط الذي ذكرناه) إلى قوله (ومجرد الترجيح لا يجوز التمسك به). قال الشيخ: إذا تعارض نصان وتأرخا، فالمتأخر (١٣٥/ أ) ينسخ المتقدم. وهذا لا ريب فيه، هذا إذا علمت','4','246',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2638,'التواريخ، فإن ظن ذلك بنقل الآحاد، فلا يخلو [إما] أن تكون الأخبار متواترة أو أحادًا، فإن كانت الأخبار أحادًا، فلا خلاف في قبول التواريخ، بناء على نقل الآحاد أيضًا، إذ أعلى درجة الشرط أن يكون كالمشروط، وإن كانت الأخبار متواترة، وانفرد الآحاد بنقل التواريخ، فهذا محتمل، إذ قد يقال: إذا لم يستقل كل واحد من النصين بإسقاط الآخر، فلو صرنا إلى قبول [قول] الآحاد في التواريخ، لأسقط المتواتر بالآحاد، وذلك ممنوع، ويصح أن [يمال] إلى قول العدول، فلا يكون هذا بمثابة استقلال قول [العدل] بنقل المتواتر، وهذا هو الأظهر (١٧٣/ ب) عندنا.
أما إذا تطرق إلى أحدهما ظن النسخ، بناء على [ظن] تقدم إسلام أحد [الراويين]، وتأخر إسلام الآخر، هذا لا يثبت به عندنا ظن النسخ، وليس من ضرورة من تقدم إسلامه أن تتقدم روايته، ولا بالعكس من ذلك. أما إذا [تعين] زمان رواية احدهما، وأطلق زمان رواية الآخر، مثل قول','4','247',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2639,'[الصحابي] في مرض موته [- صلى الله عليه وسلم -]: جالسنا، أو ورد علينا كتاب رسول الله - صلى الله عليه وسلم - قبل موته بشهر، إلى ما يضاهي ذلك. فالذي نراه أن الخبر الذي انضبط وقت نقله، مقدم على الآخر، ولا فرق على هذا بين أن يقرب الوقت المنضبط من وفاة رسول الله - صلى الله عليه وسلم - أو يبعد.
وبيانه بالمثال في قوله: "ورد علينا كتاب رسول الله - صلى الله عليه وسلم - قبل موته بشهر". أنا تحققنا الحكم، ذلك الوقت المعين، ونحن على تردد، هل الخبر الآخر [متقدم] عليه، أو [متأخر] عنه؟ فالحكم معلوم الثبوت، ونحن على تردد في ورود ما يزيله، ولا يثبت النسخ بمجرد الإمكان.','4','248',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2640,'وإذا كان هذا الوجه هو الصواب، وإن تقادم زمان ورود المنضبط الوقت، فما القول إذا قرب الزمان وبعد ورود النسخ؟ فإنه يغلب على الظن أن الحكم الثابت في مرض موته، لم يرد [نسخ عليه]، لقرب زمانه من وفاته. وقد قال مالك: إذا تناقضت وصيتان، وإحداهما مؤرخة، والأخرى غير مؤرخة، فالوصية المؤرخة مقدمة. وهو من هذا الذي نحن فيه.
وأما قول الشافعي: إن تجرد نص، ولم يعارضه آخر، فإمكان النسخ [مردود]، ولا يكفي إمكان النسخ في ترك الخبر. وهذا الكلام فيه تجوز، وأراد بالإمكان الظن. وأما مجرد الإمكان، فلا يضر على حال، فإن الإمكان غير منقطع عن كل واحد من المتعارضين. فالمراد إذًا: ظن النسخ غير ضار عند الانفراد. وهذا [الكلام] لا يصح القول به على الإطلاق، فإنا بينا أن الظن إذا استند إلى قول العدل وأخبار الآحاد، وقع التمسك به بإجماع. وإنما أراد بالظن ما ذكرناه من الجهات التي ذكرناها. فهذا موضع الخلاف.','4','249',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2641,'والصواب عندنا بعد تسليم الظن بالنسخ عند التعارض، وانتفاء دليل آخر، أن يصار إلى ما بعد عن ظن النسخ، فإن تعطيل الواقعة عن الحكم، لا سبيل إليه، وقد فقد متأخر آخر، وترتيب الحكم على ما يظن نسخه باطل، فلم يبق إلا الحكم بما بعد عن ظن النسخ.
وأما الذين ذهبوا إلى أن (١٣٥/ ب) الخبرين يتعارضان، فقد [ذكروا] وجه مذهبهم، وقالوا: الخبر الذي تطرق إليه ظن النسخ (١٧٤/ أ) لا يخفى سقوط التمسك به، والخبر الآخر [يهي] به، وينحط عن [الرتبة] المقطوع بها، أي ليس على العمل به إجماع. وإنما اعتمدت أخبار الآحاد، لانعقاد الإجماع على العمل بها. وهذا التصوير [يمنع] الإجماع، فوجب الحكم بسقوطها، وطلب حكم المسألة من مأخذ آخر.
وتحقيق هذا المذهب يؤول إلى إسقاط الخبرين جميعًا، على الوجه الذي ذكرناه. ولا يبقى بعد تعارض نصين، إلا ظن ترجيح، ومجرد الترجيح لا يجوز التمسك به.','4','250',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2642,'قال الإمام: (ووجه الحق في ذلك، أن الحادثة إذا عريت عن مسلك) إلى قوله (وسنسند إليه أمثاله). قال الشيخ: هذا الذي ذكره الإمام ها هنا، اقتصارًا على محض المذهب، غير معضود بالأدلة، فلم [يورد] دليلًا لنفسه، ولا [ردًّا] على خصمه، ومثل هذا لا يقنع به في الفروع، فكيف بمسائل الأصول؟ وفي العبارة نوع من الإشكال، فإنه قال: فالوجه النزول عنهما، والتمسك بقياس إن وجدناه، ثم يستعمل الخبر الذي بعد عن ظن النسخ، [مرجحًا] لأحد القياسين على الآخر. وهذا يدل على أنه فرض المسألة','4','251',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2643,'أيضًا في قياسين متعارضين، على وفق خبرين متناقضين، ثم جعل التمسك بأحد القياسين، وجعل الخبر الذي بعُد عن ظن النسخ مرجحًا، [فهلا] عكس الأمر، وجعل التمسك بالخبر الذي بعد عن ظن النسخ ورجحه بالقياس؟ وهذا كان أولى من أن يستعمل القياس مرجحًا.
وأصل هذا كله، هل المرجوح في معنى المعدوم، أو [نثبت] له أثرًا؟ فلا يكون الراجح ممتازًا عنه إلا بمزية ترجيحه، والمزايا الترجيحية يعتمد عليها عند الاضطرار، وفقدان دليل مستقل. وهذا هو الذي بنى عليه الإمام الكلام، وهو لعمري ظاهر، ولا يصح أن يكون الخبر المرجوح كالعدم بالكلية، ولذلك لا يبقى الإنسان على ظنه في الراجح، بمثابة ما لو كان الراجح منفردًا، ومن ظن ذلك، [فإنه] يغالط نفسه.
وهذا القول هو المشهور عندنا. قال مالك رحمه الله: إذا تعارضت بينتان، وكانت إحداهما أعدل، قضى بالحق لصاحب البينة التي قضي بكونها','4','252',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2644,'أعدل، مع يمين أصحابها. ولو لم [تتعارض]، لثبت القضاء بها من غير يمين. وهذا يدل على أنه رأى للتعارض أثرًا في التضعيف. ولذلك افتقر إلى اليمين، وقصر الحكم [على القضاء] [بالأعدل] على الموضع الذي لليمين فيه مدخل. ولم ير ذلك في النكاح، لعدم دخول الأيمان فيه، فلا يقضي [بالمرة] لأحد الرجلين، بناء على أن بينته أعدل. وخالف في ذلك سحنون، وكأنه رأى [أن] المرجوحة كالعدم (١٧٤/ ب) في القضاء بالراجحة. وليس الأمر على ما قال.

قال الإمام: <span data-type="title" id=toc-482>(مسألة: إذا تعارض خبران نصان نقلهما الآحاد) </span>إلى قوله (فهذا منتهى المراد). قال الشيخ: الترجيح بكثرة العدد ظاهر، ومرجع هذه المسائل إلى أصل واحد، وهو إدارة الأمر في الحكم على (١٣٦/ أ) حصول','4','253',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2645,'غلبة الظن في أحد الجانبين. فإذا تعارض الخبران، وكثرت رواة أحدهما، وقلت رواة الآخر، فغلبة الظن حاصلة بظن ثبوت الخبر الذي كثرت رواته، وإذا لم يكن في الرواة والروايات تعبدات سمعية، وغلبة الظن حاصلة في أحد الجانبين، وهي عن الآخر منتفية، لم يجز التمسك بجهة يظن انتفاؤها.
هذا مرجع هذه المسائل بجملتها. والصواب عندنا الاعتماد على كل وجه مُغلب على الظن، لانتفاء التعبدات في ذلك. والأصح ما قاله القاضي من كون ذلك مظنونًا غير مقطوع به.
وأما الذين منعوا ذلك، فقد شبهوا الرواية بالشهادة، فإنه لا [ترجح] بينة على بينة بمزيد العدد، على القول الظاهر للفقهاء.','4','254',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2646,'وفي ترجيح البينة على البينة بمزيد العدالة والعدد ثلاثة أقوال: قول بالترجيح فيهما جميعًا، وقول بنفي الترجيح، وقول بالفرق بين العدد والعدالة، فيقع الترجيح بمزية العدالة دون العدد. هذا هو المشهور.
والفرق بينهما أن مزية العدد زيادة لا اتصال لها بالأصل، فهي فضلة مستغنى عنها، ومزية العدالة لا يمكن فصلها، ولا يتصور الاستغناء عنها. وعلى هذا القول، هل يرجح بمزية العدالة على الإطلاق، أو في المواضع التي لليمين فيها [مدخل]؟
وأما ما ذهب إليه الإمام من [التفصيل]، وقطعه بالتقديم عند خلو الواقعة عن دليل زائد، فلست أرى الأمر مقطوعًا به كما ذكر. وما ذكره من الدليل مبني على محض التقدير، فإنه قال: نحن نعلم أن أصحاب رسول الله - صلى الله عليه وسلم - لو تعارض عندهم خبران، كثرت رواة أحدهما، وقلت رواة الآخر،','4','255',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2647,'والواقعة في مجال لا تقدير للقياس فيها، لكانوا لا يعطلون الواقعة، بل يحكمون بالخبر الذي يرويه الجمع. هذا مستنده، وهو في غاية الضعف. ومن أين يعلم هذا المقدر، والواقعة لم ينقل عنهم فيها حكم؟ ويمكن أن يكون بعضهم أو جميعهم لا يرى ذلك. فكيف يدعي القطع، [مع] إمكان نقيض المقدر؟ وكثيرًا ما يستعمل الإمام مثل هذا الكلام عند الاحتياج إلى الدليل. وهو عند ذوي التحقيق حائد عن التحصيل.
فأما إذا كان في المسألة قياس وخبران [متعارضان]، كثرت رواة','4','256',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2648,'أحدهما، وقلت رواة الآخر، فللمسألة (١٧٥/ أ) [ثلاث] صور:
[أحدها: )] أن يكون القياس على وفق الخبر الذي كثرت رواته، فهذا خفاء [بأن] الحكم على وفق الخبر. ولكن يتصدى في ذلك نوع [آخر]','4','257',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2649,'من النظر، وهو أنا هل نحكم [بإسقاط] الخبرين وإسناد العمل إلى القياس؟ أو نقول: [يرجح] الخبر الذي كثرت رواته من وجهين: أحدهما كثرة الرواة، والثاني العضد بالقياس، فيكون مستندًا إليه؟
والذي يأتي على طريق الإمام النزول عنهما، لأنه يرى كثرة الرواة مزية ترجيحية، [والمزايا الترجيحية] يتعلق بها عند فقدان الأدلة، لا جرم قال: (١٣٦ م ب) والذي يقتضيه هذا المسلك النزول عنهما، والتمسك بالقياس،','4','258',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2650,'وترجيح القياس الذي [يعضده] الخبر الذي يرويه الجمع. [فتحصل] تلك المزية في الخبر من كثرة الرواة مرجحة للقياس.
الصورة الثانية: أن يتجرد القياس في الجانب الآخر، فهو [متمسك] الحكم، بناء على ما سبق من أن الترجيح يرجع إلى مزية يتعلق بها عند فقدان الأدلة. قال: ولكن [قد] يظن أن الصحابة كانوا يقدمون الخبر الذي يرويه الجمع، ويضربون عن القياس، كدأبهم في تعظيم الخبر وتقديمه. وهذا عندنا ضعيف، وذلك أنه إن سلم أنهم كانوا يقدمون الأخبار على الأقيسة، فذلك في خبر لم يعارضه خبر آخر. وأما إذا تعارض الخبران، فليس هذا موضع تركهم القياس.
وأما قوله: فآل حاصل القول إلى أن الخبر، وإن رواه جمع من الثقات، إذا عارضه خبر نقله عدل واحد، [يرجح] [على] ما رواه الجمع، عن مراتب الأدلة المقطوع بها. يعني في مقابلة القياس. وإن عدمنا مأخذًا','4','259',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2651,'سواهما، كان [تعلقنا] بالأرجح تعلق من لا يجد مضربًا. قال: وكذلك القول في القياس إذا كان مع الخبر الذي يرويه الواحد، وكان مجردًا وحده، فالمسألة ظنية، منزلة على ما يؤدي إليه الاجتهاد. وهذا كله عندنا صحيح، وإنما خالفناه في القسم الأول، وهو إذا كان القياس على وفق الخبر الذي كثرت رواته، فإنه مقطوع به عنده، وهو مظنون عندنا أيضًا.
وأما إذا كان [الخبر] يتطرق إليه ظن النسخ، لا بطريق النقل من الجهات التي ذكرناها، فإن وجد للمسألة مأخذ آخر، تمسك به. وهذا أقوى في بابه، فإن التعارض هناك قوي، والقطع في المسألة أيضًا مفقود، والاعتماد','4','260',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2652,'على النقل عن الصحابة في عين المسألة غير موجود، فإن ثبت عندهم شيء بطريق قاطع، وجب المصير إليه.
قال الإمام: (ومما نذكره في فروع هذا [الفصل]) إلى قوله (ويستعمل الخبر توقيفًا ينتهي إليه). قال الشيخ: أهل الحديث مطبقون على أنه لا التفات إلى رواية المفضول مع رواية الفاضل، لأنهم (١٧٥/ ب) لا يلتفتون إلى الأقيسة، إلا بعد فقدان الأخبار على الجملة، والظن حاصل بثبوت الخبر الذي رواه الأعدل. والأمر واضح على ما قرروه، لا سيما إذا كانت المسألة في محل','4','261',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2653,'لا مجال للقياس فيه، وتحتم المصير إلى الخبر، واستقر في النفس سقوط أحد الخبرين، فلا يبقى [وجه] إلا التمسك برواية الأعدل الأضبط. وهذا مما لا يبعد ادعاء الإجماع فيه. وقد صار الفقهاء المعتبرون إلى التمسك بحديث عبيد الله في قسم الغنائم، بناء على ما قررناه.','4','262',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2654,'قال الإمام: (ومما يتصل بذلك أنه إذا روى أحد الخبرين ثقة) إلى آخره. قال الشيخ: هذا الذي ذكره في هذا الفصل [بين]، وهو رد [الأمر] إلى الاجتهاد، والنظر في آحاد المسائل يختلف، وقد يكون الذي بين الرواة من التفاوت قليلًا، والكثرة غالبة جدًّا حتى تقارب التواتر. ففي مثل هذه الصورة يظهر [تقديم] الخبر الذي كثرت رواته، وقد يكون الأمر على العكس من ذلك، وهو بمثابة ما إذا روى غمام العصر، ومرموق الوقت، كمالك ابن أنس (١٣٧/ أ) مثلًا خبرًا، وروى جمع من الثقات خبرًا، وبينهم وبين مالك','4','263',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2655,'تفاوت [كبير]، ويكون الرواة مثلًا راويين أو ثلاثة، ففي هذه الصورة قد يغلب على الظن ما رواه مالك دونهم. وهذه التفاصيل المتفاوتة، لا يأخذها حد واحد، ولا يسترسل عليها حكم متحد، ولكل مسألة ذوق ينفرد بالنظر فيه المجتهد.

قال الإمام: <span data-type="title" id=toc-483>(مسألة: في تقديم أحد الخبرين على الآخر لموافقة أقضية الصحابة - رضي الله عنهم </span>-) إلى قوله (فتحسين الظن بخبر الشارع المعصوم أولى). قلت: أما إذا صودف الإجماع على خلاف الخبر، فلا بد من التمسك بالإجماع، سواء كان الخبر متواترًا أو آحادًا. والأمر فيه على [حسب] ما فصل وبين. وإن كان العمل من بعض الصحابة، فهذا موضع الخلاف، فالشافعي يرى التمسك','4','264',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2656,'بالخبر. وحاصل ما ذكره، أنه قابل بين عمل العاملين، الذين هم بعض الأمة، وظن الخبر، فظهر التمسك بالخبر على هذا التقدير.','4','265',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2657,'وكذلك قوله: الخبر مقدم على القياس، والقياس مقدم على العمل، فكيف يتأخر الخبر ويتقدم العمل؟ هذا [معنى] كلامه.
والذين تمسكوا بالأعمال، إنما صاروا إلى أنها تدل على سنن ثابتة، تركت الأخبار للسنن الثابتة، التي دلت عليها الأعمال. إلى هذا يرجع الخلاف. وهذا إنما يقال فيما إذا استمرت أعمال أهل المدينة، وهي دار الهجرة ومهبط الوحي، وأهلها العالمون بالناسخ والمنسوخ. ولا يقول مالك [بترك] الخبر بناء على عمل واحد، (١٧٦/ أ) بل إنما يقوله عند التواطؤ على العمل بخلافه، واستمرار الناس على ذلك. هذا هو مدار الأمر، وجهة الفرق','4','266',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2658,'بين المذهبين. ونحن الآن نعتني بذلك، فإن الحاجة إليه ماسة، [والطاعنون] به على مالك لم يقفوا على سر مذهبه.
اعلم أن الله تعالى بعث محمدًا - صلى الله عليه وسلم - رحمة للعالمين، ومبينًا شرع الله تعالى لعباده، قال الله تعالى: {وما أرسلناك غلا رحمة للعالمين}. وقال الله تعالى: {لتبين للناس ما نزل إليهم}. وقال تعالى: {كنتم خير أمة أخرجت للناس}. قال مالك: هم سلف هذه الأمة، وهم الصدر الأول من أمة نبيه [- صلى الله عليه وسلم -]، الذين أخبر - صلى الله عليه وسلم - أنهم خير القرون من أمته، وهم الذين اختارهم الله [تعالى] لصحبة نبيه، وانتخبهم لنصرته، وضرب بهم الأمثال في سالف كتبه، قال الله تعالى: {محمد رسول الله والذين معه أشداء على الكفار رحماء بينهم','4','267',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2659,'تراهم ركعًا سجدًا يبتغون فضلًا من [الله] ورضوانًا سيماهم في وجوههم من أثر السجود ذلك مثلهم في التوراة ومثلهم في الإنجيل كزرع أخرج شطأه فآرزه فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار}. [وارتضى منهم خلقًا] لإقامة دينه، وإحياء [سنن رسول الله - صلى الله عليه وسلم -]، وأثنى على السابقين بسبقهم، ومدح التابعين لحسن اتباعهم إياهم، [ورضي ذلك] منهم، ومن الذين جاءوا من بعدهم، وجعل متبوأهم دار النبوة والعلم بالحكم، فما علموه وما نقلوه، لزم أهل الآفاق الائتمام بهم [فيه]، إذ هم الذين وعوا عن رسول الله - صلى الله عليه وسلم - ما شرع، فكان لهم بذلك فضل العيان والمشاهدة، على من نأت به الدار، وبلغته الأخبار، التي لا تحل في القلوب محل المعاينة.
وقد كان الرسول - صلى الله عليه وسلم - بغير المدينة [في أسفاره] وخرج عنها بعض','4','268',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2660,'أصحابه [إلا] أن أكثر مصير من صحبه إليها، والثاوي بها أكثر من الظاعن عنها. (١٣٧/ ب) فكان بذلك عملهم حجة على من سواهم من سائر البلدان. وقد كان اجتماع مَن بالمدينة من [القرن] الأول في غير شيء حجة على من كان بغيرها، وكان الواجب عليهم التسليم لما قام به جمهور الأئمة ببلد النبوة.
ولما بعد في العقول أن تذهب السنة عن [جميع] الوفر الأعظم ممن هم بها من الصحابة والتابعين، وتكون محفوظة في غيرها من البلدان، أو تموت في الموضع الذي اختاره الله لإحيائها، وتحيا بموضع لم يخصه الله بذلك منها على قرب عهد نبيه، ثبت أنهم الحجة على من عداهم.
وذلك أن الله تعالى خص بلد النبوة [بسكنى] نبيه، وجعلها متبوأ خيار خلقه، ومهبط وحيه، ولكم يخرج (١٧٦/ ب) منها أحد من الصحابة رغبة عنها،','4','269',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2661,'ولكن لأمر من الديانة، من جهاد، وبث علم، وإقامة حكم، أو غير ذلك مما تدعو إليه الضرورة. والله أعلم. لترغيب النبي - صلى الله عليه وسلم - في سكناها والصبر على لأوائها.
فيها كان الوفر العظيم والعلم الجسيم. وقد كان الخارج منها من الصحابة إذا شك في أمر، لم يقطع بحقيقته حتى يقدم المدينة، فيسأل عن ذلك.
[كذلك] فعل ابن مسعود وابن عمر وأبو هريرة وغيرهم. وقد انقاد إلى تقليدهم من ينكر مخالفتهم، فلم يجد [بدًّا] من تقليدهم في صاعهم ومُدهم وأحباسهم وصدقاتهم، ولا يكون ذلك غلا بينة أحياها عملهم، واستغنوا عن نصها. وكذلك لزم اتباعهم في إسقاطهم عن الخضر الزكاة، إذ الزكاة غير [مؤخرة]، وكان الرسول [- صلى الله عليه وسلم -] يأخذها والخلفاء على سيرته. فلما بطل أن','4','270',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2662,'يكون ذلك من الحوادث التي حدثت بعد الرسول - صلى الله عليه وسلم -، فقالوا فيها برأيهم، لم يبق إلا أنها توقيف من نبيهم [- صلى الله عليه وسلم -]، فصارت بذلك الأعمال أقوى في النفوس من أخبار الآحاد، التي يدخلها من الأغفال ما لا يدخل الأعمال. إذ الأعمال نقل عامة عن عامة.
فإن قيل: فهذا في عملهم الذي لا يعارضه خبر مأثور، فما دليلكم على أن المصير إلى ما عملوه أولى من الخبر الذي نقلوه؟ قيل: لأن ذلك لا يعدو: [إما] أن يكون لسنة أحياها عملهم، فاستغنوا عن نصها، من علمهم بنسخ أو خاص أو ترغيب ونحوه، مما يوجب رفع الخبر [الأول]، إلا أن يقول زائغ إنهم أحدثوا شريعة شرعوها، رغبة عن سنة علموها، فلما فسد القول بذلك، ثبت ما تقدم ذكرنا إياه.
وقد سقط بالعمل [أحكام] منها: الحديث في قتل شارب الخمر في الثالثة أو الرابعة. ولا سبيل إلى استعماله، ولا يكون ذلك إلا لسنة على','4','271',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2663,'نحو ما [بينا].
فإن قيل: فهل يطلق في عمل جمهور الصحابة أنه سنة، كإطلاق ذلك في الأخبار؟ قيل: قد قال ذلك سعيد بن المسيب لربيعة، إذ احتج عليه في نقصان عقل المرأة عند كثرة جراحتها، فقال: "هي السنة يا ابن أخي". ومع ذلك قال عمر: "لولا أن يقال زاد عمر في كتاب الله [تعالى] ". في شأن آية الرجم. والحكم عنده بها كالحكم بكتاب الله [تعالى]، فسماها بذلك قولًا، ولم يلحقها بأن تكون آية تتلى. ولم يمنع ذلك من وجوب حكمها، مع ما قيل أن معنى قول النبي - صلى الله عليه وسلم -: "لأقضين بينكما بكتاب [الله] ". أي: بحكم','4','272',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2664,'الله، الذي هو وحي، وليس بقرآن. يقول الله تعالى: {أم عندهم الغيب فهم يكتبون} أي: (١٣٨/ أ) يحكمون. وقال بعض أصحابنا: إن هذا من مجمل الكتاب في قوله سبحانه: {ويدرؤُ عنها العذاب}. وهي (١٧٧/ أ) التي يرميها زوجها، فأبان الرسول [- صلى الله عليه وسلم -] بفعله، أن ذلك العذاب: الرجم على من زنى من الزوجين المحصنين الحرين. والله أعلم.
ولما بطل أن يرغبوا عن سنة علموها لغير السنة، واستحال أن يجهل السنة أهل خاصة رسول الله - صلى الله عليه وسلم - وأهل وزارته، والمستخلفون الله في بلاده على الحكم بين عباده، ويعلمها غيرهم على قرب عهد نبيهم، ثبت [أن] أعمالهم وأحكامهم، وما عمل به جماهيرهم، كالسنة الواجبة. ولولا طول الكلام وخروجه عن خصوص الأصول، لدللنا في كل ما خولف فيه أهل المدينة على صحة مذهبهم فيه.','4','273',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2665,'فهذا هو سر المذهب في تقديم الأعمال على الأخبار التي نقلها الآحاد. وقد قال بعض التابعين: "لو رأيت أهل المدينة يتوضؤون إلى الكوع لتوضأت إليه، وإن كنت أقرأ في المصحف: {وأيديكم إلى المرافق} ". يشير بذلك إلى أنهم لا يتفقون على هذا، إلا لعلمهم بناسخ. فظهر بذلك أن الشافعي تكلم على أمر لم نتكلم نحن [عليه]، بل تلقينا من استمرار الأعمال سننًا ثابتة على وفقها، أستغني عن نقلها، فكم من سنة دارسة أحياها عملهم المستمر. [والله الموفق للصواب].
قال الإمام: (والرأي الحق [عندنا] ما نوضحه في ذلك بتقسيم) [إلى','4','274',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2666,'قوله] (وإن لم يقل مالك بخيار المجلس). قال الشيخ: قد اعترف الإمام بأنه إذا بلغ الخبر طائفة من أئمة الصحابة، ثم عملوا بخلافه، لا يتمسك بالخبر، ولا محمل لذلك إلا ثبوت النسخ عندهم. وإلى هذا يرجع ما بسطنا','4','275',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2667,'القول فيه قبل هذا. وإنما خالف فيما إذا لم يبلغ الخبر. ونحن نرى أنه إذا استمرت أعمالهم على خلاف الخبر، فلا بد أن يكونوا أو بعضهم اطلعوا عليه، أو على سنة استقرت بعده مخالفة له. والذي [قدمناه] [يحقق] هذا. إذ','4','276',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2668,'يبعد أن تخفى السنة عليهم، ويطلع عليها غيرهم، فلذلك تعلقنا بالأعمال على الإطلاق. وهذا هو رأي مالك [رحمه الله].
وأما ما ذهب إليه من أن الأعمال من الصحابة، إذا لم ينعقد عليها إجماع، فلا يتمسك بها، إلا إذا خالفت أخبارًا. فهذا لا نقوله نحن، إذا استمرت الأعمال من الجماهير، إذا لم تكن المسألة في محل القياس، فإن كانت قياسية، ففيها احتمال. وقد تكلمنا على ذلك في كتاب الإجماع.
قال الإمام: (ومما يجب تنزيله على هذا التقسيم) إلى قوله (وهو مقدمة غرضنا في الترجيح). قال الشيخ: الذي ذكره الإمام كلام جيد في هذا التفصيل، وتقديمه ترك التارك على عمل العامل، صواب على حسب ما ذكره. فالعامل تمسك بظاهر الخبر، والتارك لا مستند لتركه (١٧٧/ ب) إلا الاطلاع','4','277',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2669,'على الناسخ، [وعلمه بضعف] الخبر.
يبقى أن يقال: تحققنا ثبوت الخبر الصحيح، ولم يتعين لنا ناسخه، ومن الممكن أن يعتقد الراوي التارك ما ليس بنسخ نسخًا. قلنا: هذا يطرد في قوله: نسخ رسول الله - صلى الله عليه وسلم - حكم كذا، وكذلك في قوله: أمر بكذا، أو نهى عن كذا. وكل هذا لا يسقط التمسك بهذه المنقولات، بل تحصل (١٣٨/ ب) غلبة الظن بحصول [النسخ]، بناء على مجرد العدل الذاكر، سواء كان راويًا أو لم يكن.
فإن قيل: هذا تقليد منكم له في قوله: إنه منسوخ. قلنا: ليس هذا من أبواب التقليد، وإنما هو تمسك بنقله من جهة المعنى، كما لو قال: نسخ رسول الله - صلى الله عليه وسلم - حكم كذا، وكذلك في قوله: أمرنا بكذا، فلا يقال: إن هذا تقليد للراوي، بل هو تصديق له في النقل، ولذلك يقبل منه إذا نقل حكمًا، وشرط','4','278',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2670,'فيه شرطًا، ووقت وقتًا. كقوله: "أمرنا إذا كنا مسافرين ألا ننزع خفافنا ثلاثًا". فلا يقال: لعله غلط في فهم الشرط وتعيين الوقت.
قال الإمام: ([وعاد بنا الكلام إلى القول] في الترجيح) إلى قلوه (ثم لم يعملوا [به]. ولكن قد يظن ذلك ظنًّا). قال الشيخ: حديث أنس في [الغنم] فيه: "في أربع وعشرين من الإبل فما دونها الغنم، في كل خمس شاة، فإذا بلغت خمسًا وعشرين، ففيها بنت مخاض، فإن لم تكن، فابن لبون ذكر". هذا معنى الحديث دون لفظه. وحديث علي - رضي الله عنه - يقول: "في الخمس شاة، وفي العشر شاتان، وفي خمس عشرة ثلاث شياه، وفي عشرين أربعة','4','279',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2671,'شياه، وفي خمس وعشرين خمس شياه". هذا أيضًا معنى الحديث دون لفظه، وعمل أبو بكر وعمر رضي الله عنهما بحديث أنس. قال الشافعي: أرجح حديث أنس لعمل الشيخين به. وقال القاضي [رحمه الله]: الخبران متعارضان، ولا يقوى دليل بمصير مجتهد إليه. وهذا هو اللائق بأصول الشافعي.
والدليل على أنه اللازم له، أنه لو ثبت عنده قوة في أحد الخبرين، لم يجز له التمسك بما ضعف عنده، بناء على عمل بعض الصحابة [به]. فإنه لو فعل ذلك، لكان مقلدًا، مضربًا عما غلب على ظنه قوته. وإذا منعه المصير إلى الأخذ بالأضعف، [فإنه] عين التقليد، وترك لما ثبت عنده من الاستواء.','4','280',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2672,'قال الإمام: (فإن قيل: فما الوجه [والحالة كما وصفتم]) إلى قوله (وقد تقدم ما فيه بلاغ). قال الشيخ: ما ذهب إليه الإمام فيما إذا ظن البلوغ والنسخ استمراره على العمل بما ظن نسخه، كلام ضعيف، ومناقض لما سبق له. أما بيان ضعفه، فكيف يجوز أن يحكم بما يغلب على ظنه (١٧٨/ أ) سقوطه، وخروجه عن الشريعة؟ وهل هذا إلا قصد المخالفة؟
وأما وجه المناقضة، فإنه يقول: إذا تطرق إلى أحد الخبرين ظن النسخ من جهة ضعيفة، وهو أن يكون راويه ممن تقدم إسلامه، وراوي [الخبر] الآخر ممن تأخر إسلامه، قال هو في ذلك: إذا لم يجد متعلقًا سوى الخبرين، فالوجه التعلق بما بعد عن ظن النسخ. وإن كانت هذه الصورة قد [منعنا] [فيها] تصور ظن النسخ. فإذا رأى العمل في مثل ذلك، فكيف يصح له الإضراب عن ظن النسخ المتلقى من إضراب الراوي عن العمل بالخبر ظنًّا؟','4','281',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2673,'فإن قيل: لا يثبت النسخ بمثل ذلك. قلنا: وكذلك إذا تحقق أنه بلغه وأضرب عن العمل به، فإن علم النسخ مستندًا إلى ذلك، ظن نصًّا عند استناده لهذا.
قال الإمام: (ومما [يجب] (١٣٩/ أ) [التفطن] له أن النصب مقادير) إلى آخره. قال الشيخ: جعل الإمام موافقة بعض الصحابة أحد الخبرين ترجيحًا، ولم يبين وجه ذلك، واقتصر على الدعوى فيه، ولم يبن الأمر على النسخ في أحدهما، لأنه لم ير تحقيق القول بأن الخبر بلغهم، وظن البلوغ لا يمنع التمسك بالخبر الآخر. فليت شعري ما وجه هذا الترجيح؟ إلا أن يكون قد صار إلى ما يقوله الشافعي من أن موافقة عمل بعض الصحابة، يقضي ترجيح الدليل على مناقضه. وهذا الموضع القاضي ينازع فيه، ويقول: لا يقوى دليل بمصير مجتهد إليه. وقد قدمنا وجه ذلك، والإلزام الوارد على الشافعي، فلا نعيده.','4','282',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2674,'قال الإمام: <span data-type="title" id=toc-485>(مسألة: إذا تعارض خبران نصان [وانضاف] إلى أحدهما قياس) </span>إلى قوله (والعمل بما اجتمع عليه [الخبر والنظر]). قال الشيخ: الذي ذكره القاضي أصح في نظر الأصول، وذلك أن الخبر يترجح [على الآخر] إما بمزية في الراوي، وذلك يرجع إلى تفاوت في طرق الثبوت، أو إلى تفاوت فيما يتعلق بالدلالة كالنص [مع الظاهر]، [والظاهر] مع الأظهر. وإذا','4','283',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2675,'تساوى الخبران متنًا وسندًا، فكيف يثبت الترجيح مع موافقة القياس لأحدهما؟
فالصواب التساقط، والقضاء بالقياس، إلا أن يقال: إن القياس إذا وافق أحد الخبرين، أشعر ذلك بأنه الثابت دون الآخر، إذ الغالب على الشريعة مراعاة القياس. فمن هذه الجهة قد يترجح الخبر الموافق.','4','284',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2676,'وأما الطريق الذي ذكره القاضي، ففيه نظر، وذلك أنه أراد أن يجعل حالة التعارض في الإعراض عن الخبر كحالة انفراد الخبر في مقابلة القياس، وليس الأمر كذلك، فإنه قد يجوز أن يكون للانفراد حالة تخالف الاجتماع، وكيف لا يكون كذلك، وفي حالة انفراد الخبر، يتعين المصير (١٧٨/ ب) إليه، [ويقدم] على الراوي والاستنباط؟ وحالة تناقض الخبرين، فلا عمل بواحد منهما على القول الصحيح، فإن لم يتمسك القاضي بما قررناه، لم يقو كلامه، ويتعين المصير إلى ما يقوله [الشافعي].
قال الإمام: (ونبني على هذا مسائل نسردها) إلى قوله (والتعلق بالقياس بعدهما). قال الشيخ: هذه المسألة تقارب الأولى من وجه، وهو القرب من القواعد، فيجري الأمر من هذه الجهة، على ما مضى في التي تقدمت.
وأما جهة مباينتها لها، فهو أنه لا يتصور أن يكون القياس مستندًا إلى ما في رواية خوات بن جبير، فمن الباطل أن يصير القاضي في هذه المسألة إلى','4','285',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2677,'تساقط الخبرين، وإيقاع الصلاة على حسب [خبر] خوات بن جبير، بل يظهر في هذه المسألة [مذهب] الشافعي في الترجيح. ولكن لا يكون ذلك ترجيحًا بالقياس، بل بالقرب من القواعد خاصة، فإن الصلاة بنيت على ترك الحركات التي لا تناسبها، وإذا تضمنت رواية البقاء على هذه الصورة [أو قريبًا] منها، كان أرجح من الرواية التي تقتضي البعد عنها.
قال الإمام: (١٣٩/ ب) (ويجري في هذه الواقعة نوعان آخران من النظر) إلى قوله (ورد الأمر إلى التفصيل). قال الشيخ: أما إذا اتحدت الواقعة،','4','286',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2678,'فيعلم أن إحدى الروايتين وهم وزلل، ثم لا يتعين لذلك إحداهما. وهل تكون موافقة القياس لإحداهما أنها الصدق؟ هذا مما يتردد فيه، والظاهر أن ذلك عندنا يتعين بموافقة القياس، وذلك أنه إذا علم أنه لم يصدر من الشارع إلا أحدهما، وكانت له عادة غالبة في إحدى الروايتين، غلب على الظن في الصورة الملتبسة أنها من قبيل الأغلب الكثير.
وأما [إذا] لم يتحقق اتحاد الواقعة، وتحقق مع ذلك التعدد، وعرفت التواريخ، فهل يكون الآخر ناسخًا للأول، أو لا يكون كذلك؟ ذهب القاضي إلى أن ذلك لا يثبت به نسخ، بل يجوز الفعلان جميعًا، وعلى هذا لا يفتقر إلى معرفة التواريخ، إذ لا حاجة إليها. ولا يتوقف العمل عليها، اللهم إلا أن يصير أن الأخير أفضل، وقد ذهب إليه بعض الأصوليين. فيكون مفتقرًا إلى معرفة الفضيلة، لا إلى تحقيق الأخرى.','4','287',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2679,'والذي عندنا في ذلك في مسألة الصلاة في وقتين على حالتين مختلفتين، أن الأخير يمنع من التمسك بالفعل الأول، لأنه قال: "صلوا كما رأستموني أصلي". ولا سبيل إلى أن يصلي على الوجهين المختلفين. فمن هذه الجهة يتعين التمسك بالفعل الأخير. وهذا هو المظنون بالصحابة - رضي الله عنهم -. ولذلك قالوا: "كان آخر الأمرين من (١٧٩/ أ) رسول الله - صلى الله عليه وسلم - ترك الوضوء مما مست النار". إلى غير ذلك مما تمسكوا فيه بالفعل الأخير. وقد نزل الإمام ذلك على الفضيلة.
وأما إذا وردت الأخبار مطلقة، ولم يتعين اتحاد الواقعة، ولا التعدد [فيها]، فهذا يحتمل الاتحاد والتناقض، ويحتمل أيضًا التعدد والاختلاف. فعلى أيهما ينزل؟ الصواب التنزيل على التعدد، إذ ليس فيه تكذيب العدول، لا عمدًا ولا سهوًا، وذلك أولى. أما العمد، فلأنه حرام ومعصية، وأما السهو، فلأنه نادر قليل، مع كمال الحفظ والضبط والاعتناء بالوعي، فتنزيل الخبر على','4','288',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2680,'الواقعتين أحق وأولى. ثم يرجع الكلام إلى طلب التقديم والتأخير، إن أمكن ذلك، وإن لم يمكن، فالتوقف على قول، والاحتياط على قول، والتخيير على قول، على ما يلزم في تعارض الدليلين.
قال الإمام: (وقد ذكر القاضي وجهًا في تقديم رواية ابن عمر) إلى آخره. قال الشيخ: هذان الوجهان لا يصح الترجيح بهما عندنا.
أما الأول: فعلى حسب ما ذكره الإمام في الرد على القاضي.
وأما الثاني: فمن أين يلزم أن يكون حكم القرينة مرجحًا؟ اللهم إلا أن [يقال] إن القرائن تبين الأمثال. وهذا هو الذي يأتي على أصل الشافعي، فإنه قال في كتاب التأويل: إذا وردت مناهٍ عن رسول الله - صلى الله عليه وسلم -، اشتهر من شيم','4','289',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2681,'العلماء [العاملين] بالشريعة حمل بعض تلك المناهي على الفساد، فالشافعي لا يرى حمل بعضها على الكراهة، بناء منه على حكم القرينة. وهذا من ذلك القبيل، وقد رددنا على ذلك. وقد تعطف العرب المندوب على الواجب، والمكروه على المحرم.
لكن ها هنا أمر آخر، وهو أنه قال (١٤٠/ أ): {وأتموا الحج والعمرة لله}. فهل يصح أن يتعلق الأمر بشيئين: أحدهما على جهة الوجوب، والآخر على جهة الندب؟ هذا عندنا يرجع إلى صحة إطلاق اللفظ المشترك على معنييه جميعًا، والشافعي يرى ذلك. وقد قال في قوله تعالى: {أو لامستم النساء}. هو محمول على الجس باليد حقيقة، وعلى الوقاع مجازًا. فكذلك يكون قوله: {وأتموا} يقتضي وجوب إتمام الحج، واستحباب إتمام العمرة. وهذا يلزم الشافعي الذي يقول: إن المتطوع بالخيار بعد الشروع، كما','4','290',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2682,'كان بالخيار قبله. فعلى هذا يفتقر إلى التقدير.
وأما مالك رحمه الله الذي يرى أنه إذا دخل في المندوب [إليه]، وجب الوفاء به. فيبقى قوله: {وأتموا الحج والعمرة لله} على الوجوب فيهما جميعًا. ولا يتلقى- على قوله- وجوب الابتداء من وجوب الإتمام. والشافعي، وإن كان يقول بالمقالة الأولى، فهو يساعد على وجوب إتمام','4','291',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2683,'(١٧٩/ ب) العمرة وحج التطوع، فلا يكون على مذهبه استدلال بوجوب الإتمام على وجوب الشروع [بحال]، ثبت بذلك بطلان الترجيح بهذا ونظائره.

قال الإمام: <span data-type="title" id=toc-486>(مسألة: إذا تعارض خبران، ولم يترجح أحدهما على الثاني) </span>إلى قوله: (والمعنى متجرد [للتعلق] به). قال الشيخ: قد قدمنا فيما سبق أن المصيب واحد، وأن حكم الله تعالى في المجتهدات معين. وعلى هذا لا يتصور أن يقع التعارض مع انتفاء الترجيح، إذ الحاصل دليل واحد، والآخر شبه، فكيف يستويان؟
ويتصدى بعد هذا ثلاثة أوجه: أحدهما- التوقف، والثاني- الاحتياط، والثالث- تقليد مجتهد آخر إن وجد. وهذا الوجه أجود الأمور، لتحقق العجز، والعاجز يلمه المصير إلى رأي من له نظر، إذا لم يتبين له فساد نظره.
وأما الذين قالوا كل مجتهد مصيب اختلفوا، فمنهم من قال يتوقف، لأنه','4','292',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2684,'متعبد باتباع غالب ظنه، ولم يغلب على ظنه شيء. وهذا هو الظاهر على هذا القول. وقال القاضي: يتخير، لأنه تعارض عنده دليلان، وليس أحدهما أولى من الآخر، فليعمل بأيهما شاء.
وهذا الذي قاله القاضي قد استدل عليه بدليل، وذلك أنه قال: ليس التخيير بين الشيء ونقيضه محال، ولا ينافي ذلك الطلب بحال، وقد ورد الشرع بالتخيير في خصال الكفارة، وعقل مع ثبوت التخيير، لانصراف التخيير إلى جهة، والتضييق إلى أخرى. قال: وإذا كان ذلك معقولًا، فيدل عليه أن الضرورة تدعو إليه، فإن إثبات الحكمين تناقض، وخلو الواقعة عنهما لا يصح، والتعيين مع الاستواء تحكم، فلم يبق إلا التخيير. وق قال - عليه السلام -: "فإذا زادت الإبل على المائة والعشرين، ففي كل خمسين حقة، وفي كل أربعين بنت لبون، فإذا بلغت مائتين كان فيها أربع حقاق، أو خمس بنات لبون". [والساعي]','4','293',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2685,'يخير، فإن أخذ الحقاق، فقد أخذ بقوله: "في كل خمسين حقة"، وإن أخذ بنات لبون، فقد أخذ بقوله: "وفي كل أربعين بنت لبون".
وكذلك إذا نظرنا إلى المعاني، فقد تدور المسألة بين أصلين متقابلين في أبواب المعاني والأشباه، ويقع الاستواء، ولا يتبين ترجيح بحال. وكذلك يجري القول (١٤٠/ ب) في تقابل استصحابين، كما إذا أعتق عبدًا آبقًا عن ظهاره، فالأصل بقاء الحياة للعبد، حتى يثبت الموت، والأصل استصحاب امتناع الزوجة حتى يثبت الإعتاق. والمسائل التي تتعارض فيها الأدلة كثيرة.
قال: وهذا بمثابة ما إذا اجتمع على العامي مفتيان، استوى حالهما عنده، فلا طريق إلا التخيير. وللفقهاء في تعارض البينتين مذاهب. فمنهم من قال: (١٨٠/ أ) يقسم المال، ومعناه: تصديق البينتين جميعًا، ويقدر أنه [قام] لكل واحد سبب كامل في استحقاق المال، ولكن ضاق المحل عن الوفاء بهما، ولا ترجيح، فصار كما لو استحقاه بالشفعة.
وهذا الذي قاله القاضي كله مبني على أصل، وهو أنه لا سبيل إلى إخلاء','4','294',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2686,'الواقعة عن الحكمين. وهذا الذي قاله غير صحيح، بل نقول: إن بنينا على أن كل مجتهد مصيب، فالإصابة مرتبة على تحصيل غلبة الظن، والظن مفقود ها هنا، إذا لم يترجح أحدهما في ظنه على الثاني، فكيف يحكم من غير قطع ولا ظن؟ فالصواب إما الوقف، بناء على التباس الحكم، وهو مذهب من يقول المصيب واحد، وإما وقف نقي. وهذا هو اللازم على قول من صوب كل مجتهد، إذ الحكم عند هؤلاء يتبع الظن، ولا ظن بحال. فكيف يصح أن يثبت الحكم بلا سبب يقتضي ثبوته؟
وأما قول الإمام: وإن تحقق التعارض والتساوي إلى آخره، هذا الكلام هو الذي سبق بعينه. والأغلب على الظن عندنا التمسك بالخبر الذي [يعتضد] بالقياس، وقد بينا وجهه فيما سبق.
قال الإمام: (فصل- كل ما قدمناه [في] تعارض النصوص) إلى','4','295',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2687,'آخره. قال الشيخ: هذا كلام ظاهره يناقض [ما سبق]، من جهة أنه قضى فيما سبق بأن الخبرين إذا تعارضا وامتاز أحدهما بمزية في العدالة ظاهرة، قطع [بتقديم] من قويت عدالة رواته، إذا لم يكن في المسألة مأخذ آخر. وتلقى ذلك من عمل الصحابة تقديرًا. ورجع في هذا المكان إلى أن هذه الصورة لم يثبت عنده وقوعها، أعني تعارض النصوص مع ثبوت المزية في العدالة،','4','296',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2688,'بخلاف الظواهر عنده. فهذا دليل بين على أن تلك المسألة قطعية. وهو الذي كنا نحن اخترناه فيما سبق.
والقول عندي في تعارض الظاهرين يجري على ذلك، إذا استويا في الدلالة، فأي فرق بين مزيد العدالة في النصوص، وبين مزيد العدالة في الظواهر المستوية في الدلالة؟ فما يقال في أحدهما يقال في الآخر، بلا ريب.

قال الإمام: <span data-type="title" id=toc-487>(مسألة: إذا تعارض ظاهران، ظاهر من الكتاب، وظاهر من السنة () إلى آخرها</span>. قال الشيخ: الصحيح عندنا أنه إذا تعارض ظاهران من','4','297',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2689,'الكتاب والسنة المتواترة، وهما في الدلالة على رتبة واحدة، فالتعارض محقق، ويعود الكلام إلى الاختلاف في تعارض (١٨٢٠/ ب) الدليلين من غير ترجيح. وأما الذين قدموا الكتاب، فقد تمسكوا بقول معاذ، ولا يتأتى ذلك لوجهين:
أحدهما- النقض، فإنهم لا يقدرون على تقديم كل ما في الكتاب على السنة، لتعذر تقديم ظواهر الكتاب على نصوص السنة المتواترة. (١٤١/ أ) فكيف يصح تقديم الكتاب مع هذا على السنة على الإطلاق؟
الوجه الثاني- أن الرسول - عليه السلام - ما كان يقول من تلقاء نفسه شيئًا، وإنما الكل [وحي] من الله - عز وجل -، قال الله تعالى: {وما ينطق عن الهوى (٣) إن هو إلا','4','298',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2690,'وحيٌ يوحى}. وقد بينا في ترتيب نظر المجتهد في الأدلة، أنه ينظر في الكتاب والسنة، وهما في رتبة واحدة. فإن قيل: فإذا كانا في رتبة واحدة، فما معنى قول معاذ: "أحكم بكتاب الله" الحديث؟ . قلنا: إذا تقرر بالدليل وجوب النظر إليهما جميعًا قبل الفتوى، وجب تأويل هذا الظاهر، وتأويله عندنا أن المجتهد أول مطلوبه الدليل، فإذا [ظفر] به، [نظر] نظرًا ثانيًا في معارضه. وإذا كان المطلوب في إحدى جهتين، وإحداهما أقرب إلى الضبط من الأخرى، فمن حسن الطلب البداية بالجهة القريبة الضبط. فسلك معاذ [- رضي الله عنه -] هذا في طلب الأدلة.','4','299',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2691,'فإن قيل: الفرق بين هذه المسألة وبين المثال، هو أن الذي يطلب مطلوبه في الجهة المنضبطة على قرب، إذا ظفر بمطلوبه، لا يفتقر إلى طلبه في الجهة الأخرى. وإذا ظفر الإنسان بالدليل في الكتاب، فلا بد له من النظر في السنة، وإذا كان ينظر فيهما جميعًا، لم يبق للبداية بأحدهما معنى. قلنا: قد بينا أن المطلوب بالقصد [الأول] الدليل، وإنما ينظر ثانيًا، لاحتمال المعارض، فلما كان الغرض الدليل، وكان طلبه في الكتاب أقرب، بدأ بذلك، لا وجه غيره.
وأما من قدم السنة، بناء على أنها مفسرة للكتاب، فقد أجاب الإمام عن ذلك بجواب سديد، إذ ليس الخلاف في السنة المفسرة، وإنما الخلاف في السنة المعارضة. فالصحيح إذًا التعارض.
وأما ما ذكره القاضي من حصول المعارضة [بين] الكتاب وظاهر','4','300',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2692,'أخبار الآحاد، فوجهه من حيث الجملة، استقلال كل واحد منهما بكونه أمارة، على تقدير الانفراد بالإجماع. وإذا كانا أمارتين إجماعًا، فكيف يتصور ترجيح أحدهما على الآخر، وقد حصل القطع بالنصب؟ ولا ترجيح مع القطع. هذا هو الذي نظن أنه مستنده، وهو باطل من ثلاثة أوجه:
أحدهما: أنه لو قطع بالنصب في هذه الحالة، لاستحالت المسألة، إذ (١٨١/ أ) يستحيل التعارض في القطعيات.
الوجه الثاني: أن هذا يفسد باب الترجيح أجمع، فإنه يطلب الترجيح، بعد استجماع كل أمارة لشرائط الصحة. [وهذا] جنوح إلى مذهب جُعل من المعتزلة. وقد [ذكرناه] قبل هذا، وبينا بطلانه.
الثالث: أنه لا يلزم من الإجماع على النصب عند الانفراد، مثل ذلك على تقدير الاجتماع. والتفاوت في هذه الصورة بين، فإنهما قد استويا في الدلالة، وامتاز ظاهر الكتاب بعلم ثبوت الأصل، ولا ينكر الترجيح بذلك. وقد ألزمه القاضي إذا تعارض ظاهر متواتر وظاهر آحاد، فإن الظاهر المتواتر مقدم. وهذا إن سلكه القاضي، فنقض صريح، وإن منعه، اكتفينا بالأوجه السابقة. وبالله التوفيق.
قال الإمام (١٤١/ ب): (مسألة: قال الله تعالى: {قل لا أجد في ما أوحي إلي','4','301',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2693,'محرمًا}) إلى قوله (في كثير من المطعومات). قال الشيخ: هذا الكلام الذي ذكره الإمام، غير صحيح، لا من جهة الأصول، ولا من جهة الفروع، ولا من جهة التفسير، ولا من جهة معرفة الناسخ والمنسوخ.
أما ما يتعلق بمعرفة أوقات النزول، فقد قال إنها من آخر ما نزل على رسول الله - صلى الله عليه وسلم -، وأنها ليست منسوخة، وادعى الإجماع في ذلك. وأصحاب [هذا] الفن قد ذكروا اختلاف العلماء، وكونها منسوخة. ذكر النحاس في كتاب "الناسخ والمنسوخ": أن جماعة من أهل العلم ذهبوا إلى أنها منسوخة، وجماعة ذهبوا إلى أنها محكمة، وهؤلاء الذين ذهبوا إلى أنها محكمة، اختلفوا','4','302',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2694,'من وجه آخر، هل ضُم إليها غيرها مما نزل بعدها يقتضي التحريم، أو اقتصر التحريم على ما تلقي منها؟
وأما ما حكاه عن مالك أنه تعلق بها، ونزل مذهبه عليها، فحرم ما اقتضت الآية تحريمه، وأحل ما عداه، فليس هذا مذهب مالك، ولا أحد من أصحابه، ولا صائر [منهم] يصير إلى أن ما سوى المذكور في الآية حلال.
وأما قوله: وتقديم أخبار الآحاد على نص القرآن، مشكل في غير محل الإجماع، فاعتقد أن الآية نص لا يقبل التأويل، فليس كذلك. فإن قال قائل: لعله أراد بالنص الظاهر، وهو قول الشافعي. قلنا: ليس كذلك، فإنه قال: وليس القرآن في مرتبة الظواهر في هذا، وإنما هو يشتمل على النفي والإثبات، والإبقاء والاستثناء. فهذا يدل على أنه اعتقد النصوصية في كل ما وراء ما اقتضت الآية تحريمه. ولذلك صعب عليه مذهب الشافعي. ولو كان يراه دالًا من جهة الظواهر والعمومات، لم يكن في مذهب الشافعي إشكال، إذ الأخبار','4','303',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2695,'(١٨١/ ب) إذا كانت نصوصًا، يترك لها ظواهر الكتاب.
والذي غره حتى اعتقد النصوصية، اشتمال الكلام على جهات من التنصيص، منها أصل النفي في قوله: {[قل] لا أجد}، فأصل النفي معلوم، ثم وقع استثناء أشياء أربعة، وذلك أيضًا معلوم، فلما اشتمل الكلام في أوله على أمر مقطوع به، واشتمل آخره على مثل ذلك، تطرق الوهم إلى الأمر المتوسط، وهو اعتقاد النصوصية في تعميم نفي التحريم، وذلك غلط، وليس عندنا في تحريم المنفي إلا نكرة واقعة في سياق نفي، وهي ظاهرة في التعميم، ليست ناصة فيه.
قال الإمام: (والذي اعتمده الشافعي في الكلام على الآية) إلى آخر المسألة. قال الشيخ: [صدق] الشافعي فيما ذكره [من] تنزيل الآية على','4','304',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2696,'سبب. ومن مذهبه أن العام إذا ورد على سبب مخصوص، اختص به.
والسبب [عنده] أن الآية سيقت لغرض الرد عليهم فيما أحلوه، وليس المراد منها إثبات حل ما سواه، فكانوا يقولون: إن هذه الأشياء الأربعة حلال، فكأنه - عز وجل - قال: هي حرام، ولكن عبارة النفي والإثبات في هذا أبلغ فيما يتعلق بتحريمها.
وكم من لفظ يكون له مفهوم يصرفه القصد عن المفهوم المشهور فيه، وهو كقول القائل: جئتك ألف مرة، فليس يراد بهذا تحديد (١٤٢/ أ) العدد بالألف، وإنما يراد به التكثير دون التحديد. فكذلك قول القائل: لم تأكل اليوم حلاوة، فيقول: [ما أكلت إلا حلاوة]، [مضادة] له فيما نفاه عنه، لا النفي لما سواه. فكذلك الآية عند الشافعي منزلة على هذا. وهذا في التأويل بعيد، ولا يتجاسر على مثل هذا إلا لضرورة، وأي ضرورة تدعو إلى مثل هذا؟ ولا مانع من إجراء الآية على ما تقتضيه من النفي والإثبات جميعًا.
وأما الأخبار التي نقلت في معارضة الآية، فتارة نقول: تكون مخصصة','4','305',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2697,'لعموم النفي، وتارة نقول: تحمل على [الكراهية]. والآية تدل على انتفاء التحريم، فلا معارضة. وتارة نقول: تدل على التحريم، أعني الأخبار، وتكون مضمومة إلى تحريم ما اقتضت الآية تحريمه، وتكون الآية نزلت قبل هذه الأخبار، فيصح الجمع بينها جميعًا.
وأما ما ذكره الإمام من الرد على مالك بنسبته إلى خرق الإجماع، فقد بينا غلطه [فيه] في كتاب العموم والخصوص. وما نقله عن الصحابة من اجتناب الحشرات، فهو مساعد عليه، إذ ذلك مما يعافه الطبع، وتنفر عنه النفس (١٨٢/ أ)، والعقلاء في ذلك متقاربون.
وأما قوله: واعتقادهم أنها بمثابة المحرمات، فمردود عليه، وقد نقلنا','4','306',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2698,'عن ابن عباس [- رضي الله عنه -] خلاف ذلك. ثم قال: فهذا يمنع من اعتقاد التنصيص في الآية ويلحقها بالظواهر، يريد بذلك أنها إذا وقعت في مرتبة الظواهر، صح تخصيصها بأخبار الآحاد. ونحن قد اعترفنا بظهور الآية في نفي التحريم بغير هذا التكلف الشديد.

قال الإمام: <span data-type="title" id=toc-489>(مسألة: إذا ورد عام وخاص في حادثة) </span>إلى آخرها. قال الشيخ: اختلف العلماء فيما إذا تعارض خاص وعام، فالذي ذهب إليه الأكثرون أن الخاص مقدم، سواء تقدم أو تأخر، وأجروه مجرى البيان. وذهب القاضي','4','307',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2699,'[رحمه الله] إلى أنهما يتعارضان. ولكنه يقول: إن ثبت تأخير الخاص، وجب المصير إليه، لأنه إما أن يكون بيانًا، وإما أن يكون ناسخًا.
وإن تأخر العام، فهذا موضع الخلاف. فذهب الفقهاء إلى أنه بيان وإن تقدم، أعني الخاص. وذهب القاضي إلى الوقف، لاحتمال أن يكون المراد بالعموم الشمول، فيكون ناسخًا، ويحتمل [أن يكون المراد به] البعض، فيكون الخاص سببًا. وهذا تقدم بيانه في كتاب العموم والخصوص.
وأما المصير إلى أن العام مقدم على الخاص، فلم يصر إلى ذلك أحد من الناس. وظاهر كلام أبي حنيفة أنه قدم العام على مقتضى الخاص، وذلك باطل قطعًا، إن كان مقتصرًا على محض تعارضهما. نعم، يمكن أن يكون لم يطلع على الخاص ورآه ضعيفًا، أو استند العام إلى أمر يقتضي تقديمه. فهذا له وجه ما.','4','308',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2700,'وأما المصير إلى ترك الخاص لأجل العام من غير زيادة، فذلك باطل قطعًا. ومثاله: ما ذكره من قوله: "فيما سقت السماء العشر". وقد قدمنا أيضًا هذا المثال بعينه، وأوضحنا الوجه فيه، فلا معنى لإعادته.
وأما ما ذكره الشافعي ها هنا من الاحتجاج بموضع الوفاق، فيما يتعلق بزكاة النقدين، فكلام صحيح، فإن الصورتين على حد واحد. ومصير أبي حنيفة إلى تسليم أحدهما يوجب عليه تسليم الأخرى، إلا أن تنفصل إحدى المسألتين [عن] الأخرى بأمر، فحينئذ يندفع الإلزام.
والذي يظهر من مذهب أبي حنيفة) ١٤٢/ أ) أنه ذهب إلى أن الواجب فيما أنبتته الأرض، فلا يختص بقليل ولا كثير، ولا بمقتات ولا بغيره. ولذلك قضى بأنه لا يجتمع الخراج والزكاة. فلهذا فرق بين المسألتين، لا لأمر يرجع إلى اختلاف مفهومات الألفاظ. هذا هو المقصود من هذه المسألة وباقيها ظاهر.','4','309',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2701,'قال الإمام: (مسألة: (١٨٢/ ب) إذا تعارض عمومان من الكتاب والسنة) إلى آخرها. قال الشيخ: هذا موضع أكثر الفقهاء من التمسك به، وذلك أنهم يقولون: تخصيص كل عام بالآخر إعمال لهما جميعًا، والتعارض ترك لهما، والإعمال مقدم على الإهمال. وهذا باطل من ثلاثة أوجه:
أحدها: أن هذا ترجيح مذهب، لا ترجيح دليل. الثاني: إن في المصير إلى ذلك تركهما جميعاً بلا حجة.','4','310',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2702,'الثالث: إن المقدم الذي عُين للبقاء تحت أحد العمومين، ليس بالإقرار أولى من البعض الذي [أخرج]. وإذا تحقق استواء القولين، وجب تساقطهما جميعًا. هذا هو الصحيح في نظر الأصول، وهو مختار الإمام، ولكنه أتى لهذا الأصل بمثال لا يصلح.
وذلك المثال قوله: {[فاقتلوا] المشركين حيث وجدتموهم}. قال: هذا ظاهر في وضع السيف فيهم حيث يثقفون، وذلك صحيح. قال: إنه يعارضه','4','311',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2703,'على التقابل قوله: {حتى يعطوا الجزية عن يد وهم صاغرون}. قال: هذا يقتضي أخذ الجزية من كل كافر، كتابيًّا كان [أو وثنيًّا]. وهذا غير صحيح، فإن في أول الآية قوله تعالى: {قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين أوتوا الكتاب}. فكيف يصح مع هذا التقييد أن تكون الآية عامة في كل كافر؟
وقوله: ونحن إنما خصصنا الجزية بأهل الكتاب بأخبار وآثار مسطورة في','4','312',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2704,'كتب الفقه. من العجب، بل الصحيح اقتصار القرآن على اختصاص أخذ الجزية بأهل الكتاب.

قال الإمام: <span data-type="title" id=toc-491>(مسألة: إذا تعارض ظاهران، أحدهما وارد على سبب خاص) </span>إلى آخرهما. قال الشيخ: ذكر الإمام أن الوارد على السبب الخاص أضعف في الدلالة، وبناء على ذلك، أنه إذا كان أضعف، وجب تقديم الأقوى عليه. ولا شك في ذلك بعد ثبوت أنه أضعف، ولكنه اقتصر في ذلك','4','313',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2705,'على محض الدعوى، [ويمكن] أن يكون رأى في ذلك إجماعًا على الترجيح، فيدل ذلك على الضعف في المرجوح، وإن لم يكن رأى ذلك، فهذا القول غير مكتفى به، وهو إلزام مذهب من غير دليل. وقد كنا قدمنا هذه المسألة فيما سبق، وبينا وجه الضعف فيها، فلا نعيده. فليراجع ذلك الموضع.

قال الإمام: <span data-type="title" id=toc-492>(مسألة: إذا تعارض ظاهران، وفي أحدهما ما يقتضي التعليل، فهو مرجح) </span>إلى آخرها. قال الشيخ: قوله: إذا تعارض ظاهران وفي','4','314',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2706,'أحدهما ما يقتضي التعليل، فهذه المسألة تقع على وجهين:
أحدهما- استواء اللفظين في الظهور، وامتياز أحدهما بما يقتضي التعليل، فهذا لا شك (١٨٣/ أ) في تقديمه، لامتيازه بجهة مغلبة على الظن. فإن ظهور التعليل من أسباب قوة التعميم.
[الثاني]- من جهة أن التخصيص إنما قُبل من جهة أن تلك الجهة يصح أن يقصدها المتكلم. فإذا ظهر في الكلام التعليل، وهو جار في جميع صيغ العموم، مثل أن يكون أحد اللفظين من صيغ الشرط، والثاني من صيغ','4','315',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2707,'[الجموع]، فيصح أن يقال: ما في لفظ المعلل من القوة (١٤٣/ أ) [يعارضه] ما في صيغ الشرط من المزية، فيرجع الأمر إلى نظر الناظر فيما يقدم ويؤخر. هذا إذا لم ينته الأمر عند [فهم] التعليل إلى القطع بالتعميم. أما إذا انتهى إلى هذا الحد، فلا نظر أيضًا إلى الترجيح، ولكن يصح أن يقال في','4','316',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2708,'اللفظ الذي يقابله: [لم] يظهر فيه قصد التعميم.
وأما ما ذكره الإمام من أن اللفظ العام إذا ظهر فيه التعليل، فقد ظهر قصد التعميم، فيمتنع التخصيص مطلقًا، فهذا غير صحيح، بل هذا مخصوص بما إذا قطع بقصد التعميم. وعند القطع بالتعميم، لا تخصيص بحال. فهذا إذًا مما قدمناه في كتاب التأويل وأشبعنا القول فيه.','4','317',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2709,'وأما ما ذهب إليه الإمام في [صيغ] العموم، [أنه] إذا ظهر قصد التعميم، فلا يجوز التخصيص فيه، فقد قلنا: إن هذا يصح فيما إذا قطع بالعموم. أما مع اعتقاد القوة في الظهور، فهذا غير صحيح.
وقوله: إن اللفظ في المتماثلات نص، ما أدري ما المراد بالمتماثلات؟ إن أراد أنها تماثلت في تناول اللفظ لها بجميع ما يندرج تحت العموم بهذه الصفة. وإن أراد أنها تماثلت في غرض المتكلم، فهذا معناه أنه ظهر [في] غرضه التعميم. وإذا كانت كذلك، نظر، هل علم غرضه أو ظن؟ فنعود إلى ما سبق من الكلام.

قال الإمام: <span data-type="title" id=toc-493>(مسألة: إذا تعارض ظاهران وقد تطرق التخصيص إلى أحدهما) </span>إلى آخرها. قال الشيخ: ذكر الإمام أن اللفظ العام إذا خص ضعفت دلالته، ولم يبين السبب في ذلك. والذي يصح عندنا فيه أن اللفظ إذا خُص','4','318',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2710,'صار مجازًا، وجهات المجاز متعددة. ولولا الإجماع على العمل، لكان الوجه ما قاله المعتزلة من الإجمال، وجهة العموم حقيقة. وإذا تعارضت الحقيقة والمجاز، فالحكم للحقيقة، بناء على اتحاد الجهة.
وأما الإمام الذي يقول [إن] اللفظ باق على حقيقته، بالإضافة إلى ما [لم] يخرج منه، فالأمر عليه يصعب، إلا أن يصير إلى جهة أخرى، وهو أن يقول: إذا اجتمع في اللفظ موجب الحقيقة والمجاز، كان أضعف في الدلالة. والذي يصح على مذهبه، أن لا يقع ترجيح بحال، لبقاء دلالة اللفظ في [بقية] المسميات على ما كانت عليه. هذا هو الصحيح على أصله، [وهو] (١٨٣/ ب) أيضًا يدل على ضعف قوله.','4','319',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2711,'قال الإمام: <span data-type="title" id=toc-494>(مسألة: إذا تعارض ظاهران أو نصان، وأحدهما أقرب إلى الاحتياط) </span>إلى آخرها. قال الشيخ: لا مزيد في هذه المسألة على ما ذكره الإمام، غير أنا نختار في هذه المسألة، الأخذ بالخبر الذي يقتضي الاحتياط، [لا لأنه أرجح]، ولكن بناء على ما يتعلق بالورع، فإن الذي نختاره أن الحكم متعين، والمصيب واحد. فإذا تعارض المبيح والمحرم، أو المبيح والموجب، ففي المصير إلى الأخذ بالمبيح، [تسويغ] الإقدام','4','320',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2712,'والإحجام، [فعلى تقدير أن يعارضه المحرم]، فإذا ترك، فلا حرج في الترك، نظرًا إلى المبيح، وإن فعل، أمكن أن يصادف المحرم، فكان الترك أقرب إلى السلامة. وكذلك عكس ذلك، إذا تعارض [الموجب والمبيح]، فعلى تقدير الإقدام، فلا حرج في المبيح، وعلى تقدير الانكفاف، يمكن أن يكون الشيء واجبًا، فتفوت المكلف مصلحة الفعل، فيكون الاحتياط على هذا أولى.
قال الإمام: (مسألة: (١٤٣/ ب) إذا تعارض لفظان [يتضمن] أحدهما','4','321',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2713,'النفي، ويتضمن الآخر الإثبات) إلى آخرها. قال الشيخ: هذا الكلام صحيح، إلا أن المذهب الأول في الصورة التي نقل فيها خبرًا يتضمن النفي، فالصحيح كما ذكر الإمام التعارض، ويصح للفريق الآخر التمسك في الترجيح بالاعتضاد بالبراءة الأصلية، وفي الترجيح بها نظر، فالصواب التعارض.
قال الإمام: (مسألة: إذا تعارض ظاهران أو نصان، وأحدهما يوافق','4','322',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2714,'المعروف المعتاد) إلى آخرها. قال الشيخ: هذا الذي ذكره الإمام بالغ في ترجيح الألفاظ: النصوص منها والظواهر، ومن أحاط بها وأحكم أصولها، لم يخف عليه مدار الكلام فيما يرد عليه من أمثالها.

قال الإمام رحمه الله: <span data-type="title" id=toc-496>(باب في ترجيح الأقيسة) </span>إلى قوله ([وإنما حدثت هذه الأمور يعد انقراض عصر الصحابة]). قال الشيخ: الكلام في ترجيح الأقيسة، يترتب على معرفتها أفرادًا، وبيان حقيقة كل نوع منها، حتى إذا عرفت المفردات، نظر بعد ذلك في ترجيح بعضها على بعض.
فالقياس على نوعين: مقطوع به ومظنون، فأما المقطوع به، فلا ترتيب فيه، إذ لا يتصور الترتيب في العلم وطرقه. والمظنون ثلاثة أقسام: قياس','4','323',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2715,'المعنى، وقياس الدلالة، وقياس الشبه. وقد تقدم الكلام، هل قياس الدلالة قسم برأسه، أو هو دائر بين المعنى والشبه؟ واضطرب قول الإمام فيه، (١٨٤/ أ) فقال قبل هذا: إنه ليس [قسمًا] [مستقلًا، وجعله في هذا المكان قسمًا] مستقلًا بنفسه. فأما المطرد والمنعكس، فمن أبواب الشبه بلا ريب.
وإذا عُرفت المفردات، قال هو: أول رتب الأقيسة المظنونة: قياس المعنى، ثم قياس الدلالة، ثم قياس الشبه. وقد تقدم بيان كل نوع','4','324',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2716,'منها. وهذا الذي ذكره هو في غالب الأمر، وإلا فلا يتأتى تقديم كل معنى على كل شبه، ولا كل نوع من أنواع الدلالة على جميع الأشباه. [وهذا] قد تقدم الإرشاد إليه. وسيأتي تقديم كثير من الأشباه على أقيسة معنوية.
فإذا خرج الكلام عن التناقض، قلنا: إنما أراد بما ذكره الغالب دون التعميم. وقد قال هو أيضًا: إن بعض أنواع قياس الدلالة يقع في أقسام قياس المعنى، فكيف يصح إذا كان هذا يقع في أقسام قياس المعنى، أن يتأخر في الرتبة عن قياس المعنى؟ هذا محال. ومنه أيضًا ما يرجح إلى الشبه، فكيف يتقدم ما هو من الشبه على نوع قياس الشبه؟ هذا إنما يتصور على رأي من يعده','4','325',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2717,'أصلًا مستقلًا بنفسه. والصحيح عندنا في قياس الدلالة خلاف هذا، وهو أنه يرجع إلى الاستدلال بالثمرات، إما على المثمر، وإما على ثمرة أخرى. وإذا كان كذلك، نظر إلى قوة الدلالة وضعفها. هذا تحقيق القول فيه.
ومن وجوه النظر الشرعي، التمسك بالاستدلال المرسل، وقد تقدم بيانه وتحقيقه، ووجه التمسك به، والشرط الذي يعتبر فيه، فلا نعيده.
[وقول الإمام]: [وشرطه]: ألا يناقض أصلًا من أصول الشريعة. وهذا صحيح لا بد منه، وهل يكتفى بذلك، أو لا بد من زيادة؟ الإمام يقول لا بد من زيادة، وهي [القرب] من قواعد الشريعة، بيان مداناتها. وقد قدمنا أنه لا يقدر على إبداء جهة المداناة، فإن الحدود التي أتى بها تتضمن إثبات التعلق بكل مصلحة إذا لم تكن منقوضة. وقد مر تحقيق [الإلزام] فيما سبق من الكلام. وهو قد أضاف إلى مالك [رحمة الله عليه] فيما سبق القول بذلك،','4','326',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2718,'ثم منع في هذا المكان من كون (١٤٤/ أ) ذلك مذهب مالك، وقال: من ظن ذلك بمالك فقد أخطأ. لا وجه لهذا إلا تناقض القول، وعدم التحقيق في النقل الأول. وكذلك عدم الإحاطة عندنا بمعنى مذهبه في النقل الثاني.
والذي يدل على انه استرسل في القول بالمصالح، أنه قال ها هنا: إذا وجدنا أصلًا واستنبطنا منه معنى مناسبًا، [فيكفي] في الضبط فيه، استناده إلى أصل متفق على الحكم فيه. وقال في الذي سبق في ضبط المعنى الذي يجوز التمسك به استدلالًا: كل معنى لو ربط (١٨٤/ ب) به حكم في أصل لجرى واستد. فإذا ربط الحكم به من غير وجدان أصل، فهو استدلال مقبول. فهذا عين الاسترسال في الاستدلال.
وأما ما أضافه إلى مالك ها هنا، وهو أنه لم يعتبر مطلق المصالح، فلسنا نظن ذلك بمالك، والصحيح عندنا من مذهبه اتباع [مطلق] المصالح، إذا','4','327',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2719,'رجعت إلى حفظ مصادر [الشريعة] في الأديان والنفوس والنسل والمال والعرض، بشرط أن لا يناقض شيئًا من قواعد الشريعة.
وأما قوله: إنه لم يعتبر مطلق المصالح، وإنما أسند معانيه إلى أقضية الصحابة، ولم يظن بهم افتتاح أمر. فهذا عندنا على وجهين: الأول- أن يكون القضاء من واحد من الصحابة أو عدد يسير منهم.
والآخر- أن يكون ذلك هو الأمر الغالب، والعمل المشهور المستمر من أكثرهم. فهذا عندنا يتبع، ويدل على أنه شرع ثابت. ولهذا يقول هو وغيره في أخبار نقلت على خلاف عمل أهل المدينة: "ليس العمل عليه"، يريد أن استمرار الأعمال أقوى في النفوس من أخبار الآحاد. وقد قدمنا هذا فيما سبق.','4','328',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2720,'فأما المصير إلى وجوب الرجوع إلى قول واحد من الصحابة مطلقًا، فلا يقوله مالك بحال. ولهذا يقول في بعض قضاء عمر: "ليس العمل عليه". قال رحمه الله: "ليس [العمل] في القراءة في المغرب: بـ {ربنا لا تزغ قلوبنا بعد إذ هديتنا}. وإن روي ذلك عن بعض الصحابة. ولا [على [حديث عمر في ترك القراءة. وكذلك قال: ليس العمل على ما جاء عن عمر في غير موضع. وقال: ومن قرأ بما يذكر من قراءة ابن مسعود في الصلاة أعاد أبدًا. وإنما خص مالك عمله بالعمل من الكل أو الجماهير، ولأنهم أعلم بالسنة، والناسخ والمنسوخ من غيرهم. وهذا قد سبق الكلام عليه، فلا نعيده. فهذا هو الذي زعم الإمام أن مالكًا انحل فيه.','4','329',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2721,'ثم عقب ذلك بكلام عجيب، فقال: وكذلك كل واقعة ربط بها مالك أصلًا من أصوله، فإنه لم يفهم تلك القضية على حقها. وذكر مثال عمر في حق خالد [وعمرو]، وبنى عليه أن مالكًا جوز المصادرة بأخذ المال تأديبًا. وهذا لا يقوله مالك مطلقًا. نعم، نقل أصحابه جواز العقوبة بأخذ المال اليسير، وتلقوه من كونه قال: إذا شيب اللبن بالماء على وجه التدليس، أريق أدبًا لصاحبه. قالوا: فهذا تجويز التأديب بإضاعة المال، ولكنه منوط عندهم بصفة القلة. وكذلك إذا اشترى المسلم من ذمي خمرًا، أهريقت على المسلم، وتصدق بالثمن على الذمي أدبًا له. هذا منصوص لا شك فيه.
وأما تأويل الإمام فعل عمر على أنه وقف على حقيقة ما [أخذاه] من','4','330',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2722,'بيت (١١٤/ أ) المال، فقد بينا أن ذلك بعيد في الاعتياد (١٨٥/ أ). وما عذره في إراقة اللبن [المشوب]، وفي تغريم حاطب مثلي قيمة الناقة التي انتحرها غلمانه للبراء بن عازب؟ هل لذلك [سر] سوى التأديب؟
وأما تجويزه القتل للسياسة في حق من لا يجب القتل عليه، فهذا باطل لا شك فيه. وإذا جرى سبب القتل، فلا تهويل لقتل الثلث لاستبقاء الثلثين.
وأما كون مالك رحمه الله لم يظن بالصحابة افتتاح الأمر، فما أحسن هذا الظن.
وقوله: الأخبار انقسمت إلى ما [نقلت] إلينا صريحًا، وإلى ما فهمناها ضمنًا، هذا يقوله مالك فيما إذا وجدت أخبار على مخالفة سنن [مشتهرة]، وأعمال مستقرة. ويقول مالك: ما كان ذلك إلا [لأنهم] ثبت عندهم ما يمنع من ذلك. وهو كلام صحيح.','4','331',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2723,'[وإن] حملت أعمالهم على المخالفة، لا يصح، بل قال الإمام أشد من هذا، وهو أنه لو روى الراوي خبرًا وعمل بخلافه، لأشعر ذلك بكونه متروكًا عنده. فإذا كان هذا حاله عند ترك الواحد له، فما القول إذا تركه جل الصحابة، واستمروا على تركه؟
لم يبق إلا أن يقال: فاعلهم لم يعلموا السنة فيه. وهذا بعيد [أن] تشيع سنة رسول الله - صلى الله عليه وسلم - في الكوفة والبصرة، وتندرس بالمدينة على قرب عهد، وكثرة العلماء، ودوام الاقتداء، وكمال البحث عن السنة، والتبري بالكلية عن البدع. وهذا أيضًا قد حققناه فيما سبق.
[وقول الإمام]: والمرجع عندنا إلى الأخبار وأقضية الصحابة محمولة عليها، وعند مالك الأعمال هي المتبعة، والأخبار مردودة إليها، فهذا وجه انفصال أحد المذهبين [عن] الثاني. يعني بهذا أن جميع العلماء طلبوا الأصول للاستدلال، ولكن تعيين الأصل هو الذي وقع فيه النزاع على زعمه.','4','332',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2724,'وأما قوله: كل معنى لو اطرد جر طرده حكمًا بديعًا لم يعهد [مثله] في الزمن السابق، فيدل خروج أثره عن النظير على خروج معناه عن كونه معتبر النظر. هذا كلام صحيح، ونحن لا ننكره، وحاصله راجع إلى أن المعاني المنقوضة لا يجوز التمسك بها. وقد شرطنا في اتباع المعنى أن لا يناقض أصلًا بحال. فهذا لا نزاع فيه.
وعلى الجملة فالذي يصح عندنا في القول بالاستدلال المرسل، أنه لا فرق بين مذهب مالك والشافعي والإمام فيه، وشرط الجميع فيه أن لا يناقض. واكتفوا بكون المعنى سديدًا مطردًا، وجوزوا التعلق ابتداء بكل معنى يجوز أن يعلل به حكم ثابت، واكتفوا في تعليل الحكم الثابت بالمعنى المخيل المناسب، فلا يبقى بعد هذا للاختلاف معنى. والله الموفق للصواب.
قال الإمام: (١٨٥/ ب) (فإذا تجدد العهد بما ذكرناه، فنحن نرسم بعد','4','333',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2725,'ذلك) إلى قوله (فليفهم الفاهم مواقع التعبد). قال الشيخ: هذا المثال الذي اختاره الإمام [مثال] حسن، وقد أتى فيه بغاية ما يكون من البيان، إذ حفظ الدماء أظهر معاني الشريعة، إذ الأموال [بذلة] حقيرة بالإضافة إليها، فلذلك وقعت قاعدة القصاص في أعلى درجات المناسبات. وقد تواطأ عليها [أهل] الجاهلية، وأقرها الإسلام، ولم تخل شريعة من الشرائع من تحريم ذلك (١٤٥/ أ)، وعن إيجاب الزاجر عنه.
ولما اتفقت الشرائع على ذلك، واستمر العقلاء عليه أيضًا، أشعر ذلك بكونه ضروريًّا عند الجميع. ولذلك قال علماء الشريعة: كل مسلك يطرق إلى الدماء الهرج من غير معاناة شاقة ولا عسر، فهو مردود. وهذا يمثل بالقول','4','334',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2726,'في القتل بالمثقل، إذ القصد بالقتل بهذه الآلات ليس عسيرًا ولا نادرًا، فنفي القصاص فيه يفسد قاعدة الدماء، وهو مضاد لهذه القاعدة، التي هي أهم قواعد الشريعة وأحقها بالحفظ والرعاية. وقد منع أبو حنيفة القصاص بالمثقل، وتارة يقول إنه ليس آلة القود، بناء على تعبد في الآلة، وتارة يقول: [إن] لم يقصد القتل به، فهو كالخطأ.
أما مناكرة العمد فيه، فضعيف في العقل، فإن من جملة صوره أن يقع القطع بالقصد إلى القتل، وهو بمثابة ما لو أحرقه بالنار، حتى صار رمادًا، أو طحنه طحنًا بحجر الرحى، أو ما يجري مجرى هذا. ولكنه تخيل تخيلًا رديئًا فقال: لما كان القصد أمرًا خفيًّا، أعرض الشرع عنه، وناطه بأمر واضح، وهو القتل بالمدد، الذي هو آلة القتل غالبًا، [ولذلك] اتخذت المحددات آلة للحرب دون المثقلات. وهذا الذي قاله خطأ من وجهين:','4','335',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2727,'أحدهما- أنا نكتفي بالمظنة، إقامة لها مقام المعنى، فإذا قطعنا بوجود المعنى، كيف يتأتى الإعراض عنه عند القطع به، ويترتب الأمر على ظن وجوده؟ هذا أمر ظاهر البطلان.
الوجه الثاني- أن من المثقلات ما هو آلة القتل في الاعتياد، كزير الحديد من الدبابيس وغيرها، فكيف يصح أن يقال إن المثقل ليس آلة القتل مطلقًا؟ ثم صغير المثقل قد يقال فيه ذلك، قال - صلى الله عليه وسلم -: "ألا في قتيل عمد الخطأ، قتيل السوط والعصا، مائة من الإبل".
وأما قوله: وإن ادعى الخصم تعبدًا في الآلة، كان ذلك في حكم العبث. فهذا من الإمام كلام حسن، ونحن نوضحه، فنقول: لما تقرر بناء القاعدة على أعظم المصالح، واشتمالها على أبلغ المناسبات، بالنظر إلى حفظ','4','336',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2728,'النفوس، التي هي أهم الأمور، لم يتصور (١٨٦/ أ) مع هذا التقدير إثبات ما ينافيه بحال، قال الله تعالى: {ولكم في القصاص حياة يا أولي الألباب}. إشارة إلى أنه شرع القصاص [لكف] الظلمة عن القتل، حذرًا من القتل. ولذلك كانت العرب تقول: القتل [أنفى] للقتل.
فإذا أثبت الخصم تعبدًا في الآلة يضاد هذه المعاني الضرورية، عُلم بالقطع بطلان ذلك، إذ كيف يتصور نفي ما عُلم ثبوته؟ ومواقع التعبدات عند العلماء، إما أن تشتمل على معان خفية، لا تنتبه لها عقول البشر، وهذا هو الأمر الظاهر منها، فيظهر للعلماء ما اتضح منها، أو تثبت الشريعة أمورًا مشتملة على أسرار تكميلية للقاعدة، تضيق عقول البشر عن الاطلاع على أسرارها، فكيف يتصور إثبات تتمة تناقض أصل القاعدة؟ وإذا تضمن ما يثبت نفي الأصل، فكيف تثبت معه التتمة؟ هذا محال.
وإن قلنا إن التعبدات ليس فيها معانٍ على حال، إلا أنها لا تكون مناقضة، لما عُلم من الشريعة ثبوته، والاعتناء بحصوله، وقد قطعنا بأن','4','337',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2729,'المقصود من شرع القصاص صيانة النفوس، فإذا صرنا إلى تعبد في الآلة (١٤٥/ ب)، حتى لا يقع القصاص إلا إذا وجدت تلك الآلة، عُدل إلى الآلة الأخرى، فلا يثبت القصاص بحال. فتبطل القاعدة على القطع. فعلى العالم أن يعرف الموضع الذي يجري فيه التعبد، من الموضع الذي لا يجري فيه.
قال الإمام: (وإن تمسك [الخصوم] بصورة في العكس) إلى قوله (ونضرب لهذا مثالًا آخر قياسيًّا). قال الشيخ: معنى قوله: إذا تمسك [الخصم] بصورة في العكس، يريد بذلك أنه يقول: لما وجب القتل بصغير المحدد وكبيره، فينبغي أن ينعكس هذا في المثقل، فلا يجب بكبيره، كما لم يجب بصغيره.
وصغير المثقل [مما اختلف فيه الناس]، [فالمشهور] من مذهب','4','338',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2730,'مالك [رحمه الله]: أنه يجب القود بصغير المثقل، كما يجب بكبيره. فعلى هذا لا يتوجه الإلزام بحال، إذ استوى القسمان في ثبوت القصاص. والقول الآخر لمالك إسقاط القصاص بصغير المثقل. وقد [مال] [بعض أصحاب الشافعي] إلى إسقاط القصاص بصغير المحدد، كالضرب بالإبرة. وعلى هذا [أيضًا] لا يتوجه الإلزام إذ حصل الاستواء في الآلتين جميعًا. وأما المذهب الظاهر لأصحاب الشافعي، [فالفرق].','4','339',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2731,'وسبب ترتب القصاص على صغير المحدد، انه لما كان الجرح فيه غرر من جهة السريان، ناط الشرع القصاص بمطلقه، توكيدًا لأمر العصمة، فكيف يترتب على هذا (١٨٦/ ب) سقوط القصاص عن كبير المثقل، وهو على مناقضة مقصود القاعدة؟ فهذا هو فساد الاعتبار، على ما مضى تقريره. فالمصير إلى هذا، ميل عن الحق قطعًا. وليس الكلام في هذه المرتبة واقعًا في محال الظنون.
قال الإمام: ([فنقول]: الغرض من شهادة الشهود) إلى قوله (ولو طلبنا مثال ذلك، وجدنا منه الكثير). قال الشيخ: هذا الذي ذكره الإمام في','4','340',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2732,'هذا المثال، واضح أيضًا، والتقدير فيه كما ذكره. والذي حمل أبا حنيفة على تخيله، أن البينة إنما تقام على المنكر، قال - عليه السلام -: "البينة على المدعي". وأما المقر، فلا تقام البينة عليه. وإذا أقر، فليس هذا موضع إقامة البينة. ومذهبه أنه لا يقام الحد في الزنا إلا بالإقرار أربع مرات. فالإقرار الواحد أبطل إقامة البينة، ولم يقم الحد، انتظارًا لبقية الإقرار. وهذا وجه مذهبه على الجملة.
ونحن نقول: إنما لم تقم على المقر بينة، لأجل الاستغناء عنها، وعدم الحاجة إليها. ولو أقر من يمتنع إقراره بإتلاف أو غيره، لم نستغن عن إقامة البينة، كإقرار المحجور عليه، والعبد، والمديان عند التفليس. فإذًا هذا الإقرار، لا ينفع عنده في إقامة الحد، فيصح إقامة البينة.
فهذا وجه الرد عليه. ولكن لا يبلغ عندي هذا المثال في الظهور والقطع ببطلان مذهب الخصم مبلغ المثال الأول. وجهة الفرق أن المثال الأول، الأمر فيه [ينبني] على محض المعنى، ولم يتصور فيه تعبدًا أصلًا. وقد ظهر في هذا المثال الالتفات إلى التعبدات في غير موضع:','4','341',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2733,'منها: أنه لا تقبل شهادة البينة إلا على [زنية] واحدة، فلو عينوا أماكن عديدة، لم يثبت الحد، وإن شهد الأربعة (١٤٦/ أ) على كونه زانيًا.
ومنها: أنه يشترط البصر في هذه الشهادة، ولو حصل العلم للشهود بالإحساس باليد، لم يترتب الحد. فلما تطرقت التعبدات في [الباب]، أمكن أن يتعبد الشرع بسقوط البينة عند وجود الإقرار، ثم ينتظر تمام الإقرار. هذا وجه يطرق إلى المسألة بعض الإمكان، وإن كان مظنونًا، [فهو في أعلى مراتب الظنون].
قال الإمام: (والمرتبة الثانية: [تشتمل] على قياس معتضد بالأصل) إلى قوله (وهو منشأ الشبهات، على ما نوضحه، إن شاء الله تعالى). قال','4','342',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2734,'الشيخ: قد بينا أن شرع القصاص [أمر] مضطر إليه، لا يتصور خلو شرع أريد به صلاح الخلق. وإذا [تقرر] ذلك، فكل مسلك يطرق إلى الدماء الهرج، فلا بد من حسمه، ووضع الزاجر عنه. والاشتراك في القتل مما يكثر، فلو لم يشرع القود فيه، لانخرم (١٨٧/ أ) أصل القاعدة. هذا هو الأصل الذي به الاعتبار، وإليه المرجوع. فعلى هذا يقتل [المشتركون].
هذا تقرير كلام الأئمة، وفيه عندنا نظر، وذلك أن الشرع راعى هذه المصلحة في شرعية القصاص، ومعناه المماثلة، وكل واحد من الشركاء ليس بقاتل، فكيف يقتل غير القاتل، [بناء] على هذه القاعدة؟ [فلو] كان كذلك، نظرًا لمطلق الزجر، لصح أن يقتل القاطع، إذ ذلك آكد في الكف عن','4','343',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2735,'الأطراف. فالشريعة إذا ناطت حكمًا بعلة، ربط الحكم بمثل تلك العلة. أما أن يناط بخلاف تلك العلة، ولا بما يشتمل على معناها، فهذا باطل لا شك فيه. هذا وجه غرض هذه المسألة، وهو الذي حمل بعض المعتبرين على أن قال: هذا خارج عن القياس.
ولعمري إنه كذلك من هذا الوجه. ولكن تقرير وجوب قتل المشركين أن نقول: هذا الأمر الذي لا يقبل الانقسام، إذا حصل اشتراك فيه، فقد يصح أن يقصد الشرع إلى اتحاد المشترك فيه، فيقدر لأجل تعذر القسمة، استقلال لكل واحد. ويصح أن يلتفت إلى عدد المشتركين، حتى يثبت التبعيض لعدد الشركاء. وهذا النوع من المسائل غامض، وهو دائر بين أصلين:
أحدهما- الاتحاد من الجانبين، كما إذا قتل الواحد الواحد.
والثاني- التعدد من الجانبين، كما إذا قتل كل واحد واحدًا. أما إذا اشترك القاتلون واتحد المقتول، فهذا لا تتحقق مماثلته لكل واحد من الأصلين، فينظر إلى ما تقتضيه المصلحة في الإلحاق.','4','344',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2736,'أما إذا كان القتل خطأ، فلا التفات إلى جانب عدد الشركاء، حتى يكون الثابت دية واحدة، إذ المال لا يثبت عقوبة ولا زاجرًا، وإنما أثبت قليلًا. ولا عهد في الشريعة بإتلاف شيء يغرم المتلف أمثاله.
أما في جانب العمد، فلا التفات إلى تعدد الشركاء، لمنع القود والجراح جميعًا، وذلك مبطل للقاعدة قطعًا. وتعين واحد للقود باطل [من وجهين]:
أحدهما- أن العصمة لا تتحقق معه، لأن كل واحد يجوز أن يكون هو المعفو عنه.
الثاني- أن السبب الذي منعنا من قتل الجميع، موجود في قتل الواحد، فإن استقل دارئًا للقصاص من الجميع، استقل دارئًا في حق المعين. فهذا أيضًا يمنع (١٤٦/ ب) من قتل واحد بالقرعة.
وقول الإمام: إن المشتركين يقتلون بحكم قاعدة القصاص، يريد بذلك أنا لم نشرع القتل لأجل صيانة عضو، بل لصيانة النفس، فبهذا يفارق. هذا ما ألزمناه من أنه لا يشرع القتل إلا في مقابلة القتل، زجرًا عنه.
وقد قررنا أن مقتضى القاعدة الالتفات في العمد إلى جهة (١٨٧/ ب) الاتحاد، حتى يقدر كل واحد قاتلًا، وأفضى درء القتل إلى الهرج الظاهر. وإن','4','345',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2737,'كان القتل بالمثقل أسهل من الاشتراك، فيتطرق قرب من وجه، إذ الواحد في غالب الأمر يستقل بالواحد، فقد تعادلا، إذ تطرق إلى كل واحد يسر من وجه، وعسر من آخر، مع أن القياس أيضًا ليس على مضاهاة الأعواض. فلما خرج أصله عن قياس الأعواض هذا الخروج، احتمل فيه هذا التفاوت. ألا ترى أنه لما غلب في الخطأ أمر الأعواض، ثبت فيه التوزيع؟
وأما قول القائل: إن الجاني معصوم، فإذا تطرق إلى السبب هذا الاحتمال، بقيت العصمة.
[وقول] الإمام: فلا نظر إلى الحرمة، مع كونه يسعى فيما يبطل حرمته، ويسقط عصمته من غير شبهة. وأما قوله: يسعى فيما يبطل حرمته، فهو محل النزاع، ودليله ما ذكرناه.
وأما قوله: ومنشأ الشبهات ما يقدر للجاني عذرًا، هذا الكلام في نهاية','4','346',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2738,'الإجمال، والمطلوب [معرفة] ما يقدر عذرًا. والذي صار إليه الفقهاء أن الشبهات تنشأ من أحد أمرين:
إما وجدان صورة المبيح، كالعقود الفاسدة في البيع والنكاح وغيره. فإن المشتري إذا اشترى شراء فاسدًا، فوطئ واستولد، سقط الحد، والتحق الولد، بناء على الشبهة الحاصلة من العقد الفاسد، الذي هو صورة المبيح.
وقد تنشأ الشبهة من اختلاف العلماء، [ولذلك قال مالك في النكاح الفاسد: إن ما اختلف الناس في إجازته وفسخه، فالفسخ فيه بطلاق، نظرًا إلى اختلاف العلماء]. [فكان ذلك] شبهة منعت من الفسخ، ولكن بناء الشبهات على مطلق الاختلاف، لا سبيل إليه. ولذلك أن مالكًا رحمه الله يحد الحنفي إذا شرب النبيذ. ولا يلتفت إلى هذا الاختلاف، وإن قال - عليه السلام -: "ادرءوا الحدود بالشبهات". والفرق بين اختلاف ينشأ منه شبهة، وبين','4','347',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2739,'اختلاف لا ينشأ منه شبهة، غامض، وإنما يرجع هذا إلى قوة الاختلاف عند الناظر وقوة دليله، وإمكان أن يكون الصواب معه، ولكل مسألة ذوق، وكثير من المسائل يقضي مالك فيها بفساد العقود، ويمتنع من الفسخ بعد الوقوع، نظرًا لقوة الخلاف.
قال الإمام: (فإذا تمهدت القاعدة [نفرض] هذه [الرتبة]) إلى قوله (ولكنه يقع في مجاري الظنون). قال الشيخ: أما المصير إلى بطلان القصاص على الإطلاق، فلا ذاهب إليه، وأما تفويض الخيرة إلى الولي، ففيه','4','348',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2740,'بعض الصون، ولكنه لا يبلغ مبلغ شرع القصاص على الجميع، إذ كل واحد من المشتركين يتوقع سلامته، فلا يقوى الزاجر في حقه. على أن المحذور الذي يلزم (١٤٧/ أ) في قتل (١٨٨/ أ) الجميع، هو لازم في الواحد. وإذا تحقق الاستواء، فإما أن يكون هذا عذرًا، فلا يقتل واحد منهم، وهذا إذا قيل به، تبطل القاعدة، وإما أن لا يكون عذرًا، [فيستوي بالإضافة إلى الكل.
فأما أن يكون عذرًا في حق بعضهم، ولا يكون عذرًا] في حق البعض، مع تحقق الاستواء، فباطل من جهة القياس، إلا أن يثبت في ذلك تعبد، فيصار إليه. ومالك يقول هذا فيما إذا كان القصاص يفتقر في الثبوت إلى قسامة، وهو إذا ثبت الموت على جماعة أنهم قتلوا عمدًا، ويكمل السبب بالقسامة، فإنهم إنما يقسمون ويقتلون واحدًا من الجماعة، ولا سبيل غلى قتل الجميع. قال مالك رحمه الله: "لم يقتل بالقسامة إلا واحد منذ كان الإسلام إلى اليوم". وهذا فهم من الحديث، وهو قوله - عليه السلام -: "تحلفون خمسين يمينًا على رجل من يهود فيسلم إليكم". هذا معنى الحديث. فانظر [لحكم] الشرع كيف [جعل]','4','349',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2741,'الاشتراك في القتل على ثلاث درجات:
إحداها _أنه نزل المشتركين منزلة الواحد، حتى أوجب القصاص على الجميع.
وتارة التفت إلى التعدد، وحكم بالتقسيط، وهو في الموضع الذي يجب فيه المال دون الدم.
وفي موضع، وهو الذي افتقر السبب [فيه] إلى [نفس] التكميل، لم يقتل الجميع، [و] تعذر التقسيط، وأفضى الإسقاط إلى إبطال الدماء، وتوسط الأمر، أو قتل واحد، وفيه أصل الزجر دون كماله، لأجل نقصان السبب، والافتقار إلى القسامة.
وصورة رابعة: وهو أن يكون القتل على جهة شبه العمد، ويمنع القصاص على قول، [وتؤخذ] الدية مغلظة، فها هنا يجب الغرم على القاتلين في أموالهم، وتعدد الواجب بتعدد القاتلين، وكأنه واقع في أبواب العمد، الذي نزل فيه الشركاء منزلة [المنفردين]، ولكن منع مانع من القود، فرجعنا إلى قيم النفوس القاتلة، يعني بذلك الديات المغلظة. هذا كلام الفقهاء في تفاصيل أنواع','4','350',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2742,'القتل العمد منه، والخطأ، وشبه العمد، والشركاء المنفردين.
وأما إلحاق الطرف بالنفس عند تحقق العمد، فواضح، [والتحرير] فيه ما ذكر، والتقدير أن الشرع شرع القتل عصمة للأطراف، ونزل الشركاء في النفس على جهة العدوان كالمنفردين، لما ذكرناه من [حكمة] الصون.
قال الإمام: ([وأما السبب في خروج هذا القياس عن كونه معلومًا]) إلى قوله ([امتناع] المعارضة المحوجة إلى الترجيح). قال الشيخ: الوجوه التي ذكرها الإمام مانعة من حصول العلم، ضعيفة كلها، ولكنها _وإن (٨٨/ ب) كانت ضعيفة_ فقد يحيك في النفس بسببها احتمال، والاحتمال _وإن بعد_ يمنع العلم.
أما الأول: وهو أن الطرف إذا أفضى إلى النفس، وجب القود، وهو ممكن، فيقدر ذلك وازعًا، من حيث الجملة. ولسنا في النفس نتوقع أمرًا يفضي','4','351',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2743,'إليه القتل، حتى يكون توقع ذلك الأمر مانعًا من الإقدام على القتل. فلما لم يوجد وازع آخر عن النفس، شرع القود فيها، وليس الطرف كذلك. وهذا لو كان أمرًا ملتفتًا إليه، لسقط القود في الطرف رأسًا.
هذا جوابه. ويصح للخصم أن يقول: قد بينا الاشتراك من وجه تنزيله منزلة الانفراد (١٤٧/ ب). ومن وجه آخر يلتفت فيه إلى الاشتراك، كما ذكرناه في الاشتراك في النفس عمدًا وخطًا. وإذا كان فيه انقسام، فليرجح تنزيله منزلة الانفراد، عندما تشتد الضرورة إليه، وهو النفس، إذ لا يتوقع زاجر آخر.
ويلتفت إلى جانب الاشتراك في الطرف، لتوقع الزاجر الذي ذكرناه. وهذا كلام بين، لو ورد الشرع به، لصح تعليله بما ذكرناه، ولم يسلك به مسلك التعبدات بحال. فلأجل ذلك، امتنع هذا الإلحاق أن يكون معلومًا.
وأما الوجه الثاني: وهو أن المعنى منقوض بما إذا تمايز فعل كل واحد منهما عن الثاني، فإن التمالؤ على إتلاف الطرف ثابت ولا قصاص.
[وقول] الإمام: هذا سؤال واقع، ورآه أنه مبطل للقاعدة في قصاص الأطراف للنقض الحاصل. وقد صح فيه منع على ما يعرفه الفقهاء، ولم ينقدح','4','352',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2744,'له عنه جواب. وهذا [أمر عجيب] أن يبقى متمسكًا بالمعنى، ولم ينقدح له عن النقض جواب أصلًا. وهل هذا إلا عين التحكم، ومحض التقليد، وإلزام الخصم [الانقياد] إلى معنى نقضه الملزم؟ فإذا لم يف هو بطرد علته، فكيف يلزم خصمه ذلك؟
ولكن الجواب [الحق] عن هذا الإلزام، وهو أنا نقدر ها هنا قاعدة كثيرة [الفائدة، عظيمة الجدوى]، وذلك أنه إذا ربط الشرع حكمًا بمعنى، فلا سبيل إلى ربط ذلك الحكم بغير ذلك المعنى، على وجهة القياس. وقد قررنا أن القصاص في النفس لا يكون إلا عند إتلاف النفس، والقطع في مقابلة القطع، فلا يشرع القتل زاجرًا عن القطع، ولا القطع زاجرًا عن الجرح. وهذا باطل في الشريعة، وليس قصاصًا على حال. فإذا تحقق هذا، فنحن إنما','4','353',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2745,'[قتلنا] المشتركين عند عسر التمييز، ذهابًا منا إلى الإعراض عن عدد القاتلين، والتفاتًا منا إلى أن الجناية لا تقبل التعدد. فما ظهرت (١٨٩/ أ) هذه الجهة، أمكن أن يتنزل الشركاء على الوجه المخصوص منزلة الواحد.
أما إذا تعددت الجهات، وتميزت الأفعال، فليس الحاصل من كل واحد منهما إلا جرح، فكيف يتفق أن يشرع القطع زاجرًا عن الجرح؟ فلا يكون الذي ورد نقضًا بحال. والمناقضة الحاقة إنما تكون على تقدير تساوي المسألتين في الاشتراك في المعنى المعتمد عليه، ويصير ما ألزمه الخصم، بمنزلة ما لو تمالأ على قطع يديه، ولكن تولى كل واحد منهما قطع يد بانفراده، فإنه لا يتصور أن يقال تقطع أيديهما جميعًا. وكذلك القول في إتلاف العينين على التمالؤ.
فليفهم المنتهي إلى هذا المقام هذا السر، فهو من الأسرار التي يقل على وجه الأرض من يتنبه لها. وبهذا يعلم الإنسان دقة نظر الأولين، وما خصهم الله به من الوقوف على حكم الشريعة.','4','354',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2746,'فأما الوجه الثالث: فهو أن الطرف [مما] يقبل القسمة، فيتصور الخصم وقوعه من المشركين على حكم التمييز. وهذا غلط بين، وكيف يتصور ترك الأمر المعلوم، بناء على أنه يمكن أن يقع على خلاف ذلك؟ فهذا الوجه هو أردأ الوجوه الثلاثة. ولكن على الجملة، القياس ها هنا في أعلى درجات الظنون.
قال الإمام: (فإن عارضوا القصاص في الطرف بقطع السرقة) إلى قوله (لو قطع جان يدًا واحدة بدفعات، استوجب القصاص [عند الإبانة]). قال الشيخ: وجه الإلزام: أن أبا حنيفة والشافعي رضي الله عنهما قالا: إذا وقع الاشتراك في سرقة (١٤٨/ أ) نصاب، لم يثبت القطع. وإذا وقع الاشتراك في إتلاف النفس، وجب القود على الجميع. واختلفا إذا وقع الاشتراك في الطرف، فقال أبو حنيفة: لا يقطع الجميع، كما لو اشتركوا في سرقة النصاب الموجب للقطع. [فالاشتراك في النصاب الموجب للقطع، كالاشتراك في قطع الطرف الموجب للقطع]. فورد الإلزام من هذه الجهة. فأراد الإمام أن يفرق بين','4','355',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2747,'البابين، لاختلاف حكمة القاعدتين، وأخذ يفرق من وجهين: أحدهما _من جهة المعاني والمناسبات، والثاني _من جهة الأحكام.
أما الفرق الناشئ من اختلاف المعاني، فهو أن القطع إنما شرع صونًا عن أخذ المال المحترم، أما المال التافه، ففي النفوس وازع عنه، فلا يفتقر إلى وازع آخر، وكل واحد من المشتركين على حصته المسروق، وهي تافهة، فلا حاجة إلى وضع الزاجر عن ذلك، بخلاف قطع الطرف، فإنه ليس فيه انقسام إلى التافه والنفيس، [كالمال]، إذ (١٨٩/ ب) ليس المقصود من القتل والقطع تملك شيء، وإنما المقصود الإتلاف، فلم يلزم حمل أحد البابين على الآخر. وهذا كلام بين على أصلهما.
فأما الفرق الثاني من جهة الأحكام، فهو ما صوره فيما إذا سرق نصابًا بدفعات، وهو في كل دفعة يهتك حرزًا، فإنه لا يقطع، ولو أبان اليد بدفعات، وجب القود. مقصوده بهذا الكلام، تباعد البابين، وأنهما بنيا في الشرع على','4','356',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2748,'تفاوت، فلا يلزم حمل أحدهما على الثاني. هذا مذهب الشافعي وأبي حنيفة.
وأما مالك [رحمه الله]، فلا يقول ذلك، ويقول: إن كان الشيء المسروق مما يفتقر [فيه] إلى معاونة، كثقله، ولا يستقل الواحد به، يقطعون، وإن كثر عددهم، ولم يحصل لكل واحد منهم إلا الشيء اليسير. وإن كان المسروق مما يستقل أحدهم بسرقته، والمسروق نصاب واحد، فلا قطع على واحد منهم. هذا تفصيل عجيب، وهو راجع إلى ما قررناه من أن الشركة على الفعل الواحد، الذي لا انقسام فيه، فيها اتحاد من وجه، نظرًا إلى اتحاد متعلق الأفعال، وفيها تعدد من وجه، نظرًا إلى تعدد الأفعال، فإن دعت الحاجة إلى الالتفات إلى جهة الاتحاد، أعرض عن عدد الفاعلين، وإن لم تدع الحاجة إلى ذلك، أعرض عن اتحاد الفعل. وقد بينا هذا فيما يتعلق بقتل العمد والخطأ. فكذلك إن لم تدع ضرورة إلى التمالؤ عند خفة المسروق. [والتفتنا إلى تعدد الأفعال، إذ لا حاجة للمعاونة. وإن ثقل الشيء المسروق، التفتنا إلى اتحاد المسروق]، دفعًا للمعاونة في أخذه. هذا تفصيل مالك [رحمه','4','357',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2749,'الله]، وهو بالغ حسن. والشاهد له حصول الإطباق على التفصيل في قتل العمد والخطأ، فلينتبه لهذا الكلام، فإنه من نفائس الكلام.
وأما إذا سرق نصابًا بدفعات، ففيه تفصيل عندنا، وهو أنه إذا كان الفعل في حكم المتحد، نظرًا إلى العادة، فالقطع واجب. وصورته: ما إذا نقل قمحًا من مخزن، في مكيل مرارًا كثيرة، في فور واحد، حتى كمل نصابًا، فإنه يقطع، إذ الفعل يعد في العادة فعلًا واحدًا، وهذا يناظر ما إذا قطع اليد بدفعات.
أما إذا انفصل الأخذ الثاني عن الأخذ الأول، بحيث يقدر سرقة جديدة، فلا قطع. وصورته في قطع الطرف: ما إذا قطع بعض يده، واندمل الجرح، وبقي بعض اليد على السلامة مع الاندمال فهذا يقتص منه بمقدار ما قطع خاصة، ثم إذا قطع شيئًا آخر، صبر عليه أيضًا، حتى يبلغ الاندمال، إذ لا قصاص عندنا إلا بعد الاندمال، ثم يقتص منه [أيضًا] بمقدار ما قطع. فهي في الحقيقة جراحات (١٩٠/ أ) اقتص منها، ووقعت الإبانة ضمنًا. فلا فرق عندنا بين السرقة والقطع في القصاص (١٤٨/ ب).','4','358',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2750,'قال الإمام: (ويتعلق بالكلام في هذا القسم أم يتعين الاعتناء به) إلى قوله (في المنصوص عليه إلى القاعدة الكلية). قال الشيخ: هذا الذي ذكره الإمام، صحيح، والكلام فيه يتعلق بالأسباب. وقد قدمنا في ذلك قولًا بالغًا، ونحن الآن نذكر ما يتعلق بهذا الغرض.
اعلم أن القياس يجري على حقيقته، إذا عدي عن حكم الأصل إلى الفرع بعين جامع الأصل، وإذ ذاك تعقل حقيقة القياس. أما إذا عدي الحكم بغير جامع الأصل، بناء على وصف يقاربه، فهذا باطل قطعًا. فإن حقيقة القياس مفقودة، مع وجه آخر من الفساد.
أما بيان فقدان حقيقة القياس، فواضح، وذلك أن الجامع الذي ثبت له الحكم، غير موجود، والمعنى الآخر _وإن دانى_ فالأصل غير شاهد له. فإن استعمل ذلك المعنى، فليس له وجه إلا الاستدلال المرسل. أما على حد القياس فلا.','4','359',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2751,'الوجه الثاني: ما ذكره الإمام، وهو أن ذلك يؤدي إلى الانتشار وعدم الضبط. فإن المعنى الثاني إذا قارب الأول، ولم يكن مثله، أمكن أن يفعل ذلك في الثالث والرابع، فيفضي الأمر إلى أن يكون المعنى العاشر، إذا نسب إلى الأول، ولم يشبهه بحال. فإن الفروق الدقيقة إذا اجتمعت، ظهر أثرها. وهذا في التمثيل كمن التقط نواة ثم أخذ تشبهها، ثم أخذ أخرى تشبه الثانية، فكلما كثر العدد، بعد الشبه من الأول، فأفضى الأمر إلى اعتبار مصارح ليست مدانية للمصالح [السابقة] بحال.
وزعم الإمام أن مالكًا فعل مثل ذلك فانتشر مذهبه. ومن العجب انه ينسب إليه هذا الاعتبار، وهو ينفيه ويقول: إنما استنبط تلك المعاني من أقضية','4','360',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2752,'الصحابة، ولم يحط بمعناها. فحاصل هذا الكلام استنباط المعاني مما لا يصح أن يكون أصلًا، إذ لم يفهم المعنى على وجهه، فأين هذا مما نحن فيه من تقريب المعاني؟
ومثل الإمام هذا الثاني بقوله: إن القطع شرع صونًا للأموال، فإذا ألزمنا عليه صون الحرم عن مراودات دون الجماع، يريد أدناها على أخذ [نصاب]، لم يحتفل بهذا الإلزام. وكان الجواب: أنه ليس لنا وضع [الأحكام]، لكن إذا وضعها الشرع اتبعناها، وليس صون الحرم','4','361',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2753,'[صون] للمال، [ولا مماثل له]. فلم يكن للإلزام وروه بحال.
أما قوله: وقد تدنو المآخذ فيزل [الفطن]، إذا لم يكن متهذبًا دريًا (١٩٠/ ب) في مسالك الاجتهاد. يريد بذلك أنه عند التماثل في المصالح، فلا ريب في الإلحاق، وعند التباعد وإلزام صون الحرم على صون الأموال، لا إشكال في منع الاعتبار. وقد تدور بين الأصلين صور تقرب نسبتها إلى التماثل.
ومثاله: ما إذا نقب واحد، وسرق الآخر، فإنه لا قطع على واحد منهما، وإن تلف المال، لكن سبب ذلك أن السارق لم يأخذ المال إلا من مضيعة، إذ النقب هتك الحرز، وإذا شرع الشارع القطع في السرقة، وهي أخذ المال المحترم من حرز مثله، فليس لنا وضع القطع فيما ليس بسرقة. وهذا معنى','4','362',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2754,'واضح يقتضي منع القطع. ولم يوجد من الناقب إلا هتك الحرز، وهو ليس بسرقة، ولم يوجد من الآخر إلا المال من غير حرز، وهذا أيضًا ليس بسرقة. وكما يقدر أن يوضع القطع زاجرًا عن الغصب والخلس، لفقدان حقيقة السرقة، فكذلك ها هنا. وهذا الكلام واضح لا إشكال فيه.
وأما ما تخيله الإمام نقضًا على هذا، فهو ما إذا استند النقب والأخذ إلى واحد، فإن القطع واجب. والذي تخيله أن النقب أيضًا في هذه الصورة (١٤٩/ أ) أخرج المال عن كونه في حرز، فما أقدم السارق عليه إلا وهو في مضيعة. وهذا الذي قاله ضعيف، فإن الفعل ها هنا يعد واحدًا منسوبًا إلى السارق، والسرقة تكون على وجوه:
منها: مالا حاجة فيها إلى هتك الحرز، وهو ما إذا سهل عليه الأخذ، والحرز باق بحاله.
ومنها: ما لا يتأتى له السرقة فيه إلا بهتك الحرز. ولما كان الفعل في وقت واحد، منسوبًا إلى فاعل واحد، مقصودًا به أخذ المال، صار هذا [اقتضاء] أخذ المال من حرزه، فوجب القطع فيه، بخلاف الصورة الأولى عند تباين الفعلين من الفاعلين على ما قررناه.','4','363',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2755,'فلما صعب الأمر على الإمام، أضرب عن الجواب عن السؤال، وعدل إلى مقتضى الأصول، وقال: ولا يلتزم الأصولي الذب عن كل مذهب. والذي قاله صحيح، ووظيفة الأصولي تقرير القواعد، فأما تحقيق الأمثلة، فشأن المجتهد، والكمال أن يشتمل الكتاب على جميع الأطراف.
وأما قوله: إن المعلل إذا قيد تعليله بقيد لا يناسب، لا على الاستقلال، ولا على الانضمام، فذلك التقييد باطل، إلا في مثل هذه المحال. يعني أنه إذا قيد المعنى بمحله، وقال: وجب القطع صيانة للمال. فإذا قيل له: قولك المال لا معنى له. قيل: شرع القطع صيانة للمحرم، حتى يلزم صون الحرم. وهذا يطرد في جملة مسائل الشريعة.','4','364',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2756,'قال الإمام رحمه الله: (فإن قيل: إذا قسمتم [الطرف في حق الاشتراك] على النفس) إلى قوله (في المرتبة (١٩١/ أ) الثانية من قياس المعنى). قال الشيخ - رضي الله عنه -: لما قرر الإمام أن المعاني إذا اختلفت أيسر اختلاف، وتباينت الأحكام أقل تباين، كان ذلك مانعًا من القياس. وهذا القول صحيح، وكذلك قوله: وقد يدنو المأخذ، يريد: فيستحيل التماثل حتى يعم الحكم، وهو أيضًا باطل. قيل له: فالقطع غير القتل، وإنما ثبت أن الاشتراك يتنزل منزلة الانفراد، بالإضافة إلى القتل، فإذا عديتم القضية إلى القطع، فهل تنسبون إلى المحذور الذي ذكرتموه من تجاوز حد القياس؟ أجاب عن ذلك بأن قال: إن كان ذلك [مجاوزة]، فلا قياس إذًا، وأخذ يبين استواء القتل والقطع في [نظر] الشرع. وإذا تحقق التماثل، صار ذلك بمثابة تحريم النبيذ، اعتبارًا بتحريم الخمر، فيثبت التماثل لا التقارب. قال: ساوى الطرف النفس في وجوب القصاص على المنفرد، وثبت أن الاشتراك في غير الصون لا يسقط القصاص','4','365',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2757,'عن الشركاء، فاعتبرنا الاشتراك في الطرف بالاشتراك في النفس، بعد أن ثبت عندنا أن قصد الشارع إلى حفظهما على وتيرة واحدة. وهذا غاية القياس في هذا النوع.
وأما قوله: إن أسد المذاهب في القول بالقياس الحق مذهب الشافعي. فهذا منه دعوى من غير عضد ببرهان. وقد بينا مأخذ مالك في المسائل التي تقدمت، ونبهنا على دقة نظره، وعلمه بتفاصيل الشرع وأبوابه، والتفاته إلى تعدد الجهات، وترتب الأحكام على ما تقتضيه قوة الشوائب.
وأما ما استشكله الإمام من قتل تارك الصلاة، وقوله: إنه لم يرد فيه نص، فليس الأمر على ما قال، بل فيه نص من الكتاب والسنة وإجماع ضمني عن الصحابة - رضي الله عنهم -.
أما الكتاب: فإن الله تعالى يقول في كتابه: {[فاقتلوا] المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا لهم كل مرصد}. فأمر بقتلهم على','4','366',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2758,'هذا الوجه من التضييق، ثم شرط في رفع ذلك ثلاثة شروط، فقال: {فإن تابوا وأقاموا الصلاة وآتوا الزكاة فخلوا سبيلهم إن الله غفور رحيم}. فإنما أمر بالكف عنهم عند اجتماع هذه الأمور. (١٤٩/ ب)
فإن قال الخصم: هذا تعلق بالمفهوم، أي مفهوم أنهم إذا لم يفعلوها، فلا يخلوا عنهم. قلنا: ليس الأمر كذلك، فإنه أمرنا بالقتل، وضيق قيه، ثم جعل السبب في الترك فعل هذه الأمور، فإذا لم يفعلوها، قتلوا بمقتضى اللفظ، لا بقضية المفهوم.
وأما السنة: [فقول النبي - صلى الله عليه وسلم -]: "أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله ويقيموا الصلاة ويؤتوا الزكاة، فإذا فعلوا ذلك، (١٩١/ ب) عصموا مني دماءهم وأموالهم إلا بحقها وحسابهم على [الله] ". كيف وقد جاء: "من ترك الصلاة فقد كفر"؟ وما كان أصحاب رسول الله - صلى الله عليه وسلم - يكفرون بذنب إلا بترك الصلاة". وهو الظاهر من قول عمر - رضي الله عنه -: " [نعم]، ولا حظ في','4','367',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2759,'الإسلام لمن ترك الصلاة". إلا أنا لا نقول بذلك على الإطلاق، إذ تارك الصلاة عندنا إما أن يكون معترفًا بالوجوب، أو منكرًا له، فإن كان منكرًا له، فهو كافر، من جهة أنه جحد أمرًا معلومًا في الشريعة. وقد بينا في كتاب الإجماع الوجه في تكفير من ذهب إلى ذلك. فإن كان معترفًا بالوجوب، فليس بكافر عند مالك وجماهير أصحابه. وذهب [ابن حبيب] من أصحابنا إلى أنه كافر. وهذا لا نختاره.
فأما الإجماع: الضمني [الدال] على [قتل] تارك الصلاة، فقول أبي بكر [الصديق]- رضي الله عنه - في قتال ما نعني الزكاة، لما خولف في ذلك فقال:','4','368',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2760,'"والله لأقاتلن من فرق بين الصلاة والزكاة، فإن الزكاة حق المال". هذا يدل على أن قتل تارك الصلاة كان مفروغًا منه عندهم، ولذلك جعله أبو بكر أصلًا، ورجع المسلمون إلى موافقته فيه. سلم أن تارك الصلاة، لم يقتل إلا بالقياس؟
وأما قوله: وإن [قنع] من قصر نظره باعتبار المأمور به بالمنهي عنه، فهذا طريق سلكه بعض الفقهاء، وذلك أنه قال: مطلوب الشريعة انقسم قسمين: فمنه ما طلب فعله، ومنه ما طلب تركه، ثم أعلى المنهيات الكفر بالله تعالى، ودونه في الرتبة إتلاف النفوس، فإنها تلي الكفر بالله تعالى في الدرجة، ولذلك كان "أول ما يقضى فيه بين الخلائق الدماء". وأعلى المأمورات الإيمان بالله تعالى، ودونه الصلاة، فإنها ثانية الإيمان، ولذلك كان "أول ما يسأل عنه من عمل العبد الصلاة".
وإذا انتهض القتل داعيًا إلى تحصيل أعظم المأمورات، فلينتهض داعيًا إلى ما يليه في الرتبة، وهي الصلاة. وهذا كلام فيه نوع من المناسبة، ولكنه لا يتجاسر بمثل هذا الأمر الكلي على إتلاف النفوس. فالصواب التمسك','4','369',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2761,'بالتوقيف، على حسب ما قررناه. [وهو كلام حسن بالغ]. والله الموفق للصواب.
قال الإمام رحمه الله: ([المرتبة] الثالثة: نمثلها بالقول في المكره على القتل) إلى قول (ولو ذهبنا نستقصي هذه المسائل لطال الكلام). (١٩٢/ أ) قال الشيخ - رضي الله عنه -: المذاهب في المكره كما ذكرها، ولمالك رحمه الله مذهب رابع، مركب من مذهب زفر والشافعي، فإنه يقول: إن كان المكره ممن ينقاد له في الاعتياد، كالسيد بالإضافة إلى عبده، والسلطان في رعيته، قتلا','4','370',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2762,'جميعًا، كما يقول الشافعي. وإن كان على غير ذلك، قتل المكره دون المكره، كما يقول زفر. وقد حققنا فيما تقدم أن القتل شرع زاجرًا عن القتل. فعلى هذا الأصل ينبغي أن يدور النظر.
وإذا كان كذلك، كان أوضح المذاهب مذهب زفر، والمصير إلى تنزيل المكره منزلة الآلة باطل شرعًا وعرفًا. أما الشرع، فبالنظر إلى بقاء التكليف واستمراره، ولو كان آلة، لسقط التكليف. وأما بالنظر إلى العرف، فإنه قادر من الامتناع (١٥٠/ أ) عن الإقدام، وإن كان في ذلك إتلاف نفسه. والمباشرة تغلب السبب، إذا أمكن أن يناط الحكم بها.','4','371',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2763,'هذا وجه تقرير هذا المذهب. ولكن في المصير إليه خرم أصل الباب، فإن المكره إذا حمل بالسيف على القتل، فإنه يقدم عليه غالبًا، للخلاص من سيف المكره. وشرعية القود عليه في مستقبل الزمان، لا ينتهض دارئًا وكافًا في الحال.
فإن قيل: [قد] قلتم: إن شرعية القود، توجب انكفافًا وإحجامًا، فليكن كذلك ها هنا. قلنا: لا نراه يستقل في هذا المكان، لأجل كونه يقتل في الحال، على تقدير الامتناع، ولم يلزم من استقلاله بالانكفاف هناك، أن يستقل كافًا ها هنا. وهذا واضح، فمن هذه الجهة، لم يكن في شرع القود على المكره','4','372',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2764,'على انفراده، تحقيق مقصود العصمة. فإذا ما حصل إتلاف النفس بإكراه المكره وإقدام المكره، فحسن لذلك تنزيلهما منزلة الشريكين. وهذا لعمري أقرب إلى مقصود الصون، وإبقاءً لإتلاف النفوس.
فإن قيل: فهذا ينقض ما قررتموه من أنه لا يقتل إلا قاتل، وتكلفتم في المشتركين الالتفات إلى اتحاد المجني عليه، وقدرتم كل واحد جانيًا على الكمال، حتى يستقيم لكم نقل الحكم إلى هذا المحل، مرتبًا على معناه، وأنتم لا يمكنكم في هذا المقام أن تجعلوا المكره شريكًا في النفس على حال. قلنا: هو شريك شركة معنوية، يدل على ذلك أنه إنما حصل إهلاك النفس بفعلهما جميعًا، ولو انفرد كل واحد منهما، لم يقع القتل. هذا وجه مذهب الشافعي، وفيه فقه، على [ما مهدناه] من تغليب أمر الصون في القاعدة.
فإن قيل: فما وجه تفرقة مالك رحمه الله حتى اختار في صورة مذهب','4','373',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2765,'[الشافعي]، وفي صورة مذهب [زفر]؟ قلنا: الذي يظهر لنا في ذلك _والعلم عند الله_ أنه بنى الأمر على قضية ظاهرة، فيما يعتاد (١٩٢/ ب) فيه الإكراه، ومن يكون له الإكراه من الناس، وأعرض عن الأمر الخفي. إذ يكون للمقدم على القتل قدرة على الانكفاف، ولكنه لم يستعمل قدرته رغبة له في إتلاف النفوس، فكأنه لم يتحقق [عنده] أنه مكره. فيرجع فقه مذهبه إلى [فقه] مذهب الشافعي. ولكن الشافعي نظر إلى صورة الإكراه، ونظر مالك إلى الإكراه الظاهر في العادات. وهذا كلام حسن في هذا النوع.
أما ما يتعلق بقتل شهود الزنا، إذا رجعوا بعد إقامة الحد، فلا قصاص عليهم عند مالك رحمه الله. لأن القوم لم يقتلوا، ولم يكرهوا على القتل. وقد قلنا: إنه لا يقتل إلا قاتل، أو من أقيم مقام القاتل، كالشركاء. [وبينا]','4','374',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2766,'وجه تنزيل المكره والمكره منزلة الشريكين، وقلنا إنه [إن لم] يقتلا جميعًا، لم تتحقق عصمة الدماء. أما المكره فإنه [يقدم] لاستعمال خلاص نفسه. أما المكره فإنه يقدم على الإكراه، ليشفي غيظه من عدوه.
والمعنى الذي لأجله أقدم المكره على القتل، لا يوجد في حق القاضي، لأن الباعث هناك الخلاص من سيف المكره ناجزًا. وهل يكون الخوف من عذاب الله يتنزل منزلة الإقدام، حذرًا من الهلاك في الحال؟ أما في الاعتبار، فلا يتنزل منزلته في حق الأكثرين. وقد يتفق أن يوجد في بعض الخواص هذا المعنى، ولكن إدارة الأحكام على مثل هذه النوادر، بعيدة في الشرع، لا جرم كانت مسألة في محل الاجتهاد. واختيارنا ألا قصاص على شهود الزنا إذا رجعوا.
[وأما شهود القصاص إذا رجعوا]، فإن فرض مع ذلك رجوع المدعي، فلا إشكال في سقوط القصاص عن الشهود، وإن فرض تمادي المدعي على','4','375',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2767,'دعوى الاستحقاق، فهذا في توجه القصاص على الشهود، أضعف من شهود الزنا إذا رجعوا. فمن أسقط القصاص عن أولئك (١٥٠/ ب)، فهؤلاء أولى بالإسقاط. وأما القاضي، فلا خيرة له شرعًا. فهذا بيان هذه المراتب، وكيفية تدرج النظر فيها. أما نحن، فلا تتوجه هذه المسائل علينا، لأنا لا نرى قتل الشهود. وأما من ذهب إلى قتلهم، فهو يحوم على إلحاقهم بالمكره. والفرق على ما قررناه بين. والله الموفق للصواب.
قال الإمام رحمه الله: (ولم يوجب الشافعي عقوبة في هذا المساق) إلى قوله ([ما ذكرناه] من قواعد الشريعة). قال الشيخ: أما قوله: لم يوجب الشافعي عقوبة في هذا المساق، أبعد من إيجابه حد الزنا [على] المرأة إذا نكلت عن لعان الزوج. هذا يريد به أن المرتبة التي قبل هذه، [كان','4','376',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2768,'الكلام فيها] على تحقيق القتل الموجب للقتل (١٩٣/ أ) في الشركاء، والمكرِه والمكرَه وشهود الزنا والقصاص، فالذي قيل، يحقق الاشتراك في الدم، إما حسًا وإما معنى. ومن نفى القتل، لم يتحقق عنده اشتراك أصلا، [وكما أن] ذلك المعنى روعي في القتل، كذلك حد الزنا عنده ينبغي أن يترتب على الزنا، وذلك يكون بالبينة تارة، وبالاعتراف أخرى.
فأما إقامة الحد بناءً على مجرد دعوى المدعي، فخارج عن القانون. فنقول: هذه المرأة حدت حد الزنا، ولم يثبت أنها زانية أصلًا. ثم أراد أن يبين وجه من قال بذلك، فقال: هذا يشبه أيمان المدعين في القسامة، فإنه قول استند إلى قول المدعي. [فيقول: ] قد وجد لذلك نظير.
وأخذ يعترض على هذا بأن قال: المعنى لا يقتضيه، ولولا الخبر، لما اقتضاه المعنى بحال. ثم قال: واللعان أبعد من القسامة، إذ تلك يشترط فيها','4','377',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2769,'استناد الأيمان إلى اللوث، وهذا لا يشترط في اللعان، فيتعذر الإلحاق. فإذا بطل أن يكون ذلك قياسًا، قلنا بعده مستندان:
أحدهما- أنه لا يجد بدًا من الخروج عن قوانين الحجج. معناه: أنه إذا توجهت اليمين، ثم ردت على الخصم [فنكل] [عنها]، [ترتب] [عليه] [القيام] بمقتضى ما نكل عنه، ويصير نكوله بمثابة الإقرار. بيانه: أنه إذا أقام رجل شاهدًا على حق، وتوجهت عليه اليمني، فنكل عنها وردها على خصمه، فإن حلف برئ، وإن نكل غرم، ولا ترد اليمين على الأول، فيصير امتناع المرأة من اليمين نكول، بعد توجه اليمين، فتتنزل منزلة المقر. هذا معنى قولنا: أنا لا نجد بدًا من الخروج عن قوانين الحجج.
الأمر الثاني- أن المسلط على القتل قوله تعالى: {ويدرؤ عنها العذاب أن تشهد أربع شهادات بالله}. فالدارئ إنما يكون بعد جريان السبب، فإذا لم يكن دارئ، تعين عمل السبب.','4','378',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2770,'وما ذكره أصحاب أبي حنيفة من أن المراد بالعذاب الحبس، بعيد، فإن الحبس ليس مقتضى هذه الدعوى التي ادعاها الزوج، مع أنه لم يجر للحبس ذكر أصلًا. هذا معنى قوله: إن حمل العذاب على الحبس بعيد. وهذا الكلام ظاهر جدًا، ولا أدري بماذا رده الإمام. ومعنى القرآن: أن الكذب يخرج الزوج عن كونه صادقًا بيمينها. كما أنه لو قذف الزوج ولم يلاعن، لثبت في حقه حكم القذف. فالأمر في هذا دائر بين [بعد] أيمانهما جميعًا، فلا يصدق واحد منهما على الآخر، وبين نكول أحدهما، فيصدق خصمه عليه. هذا هو المفهوم من القرآن. والله الموفق للصواب.
وأما قوله: إن من عجيب الأمر أن اختلف قوله في القتل بأيمان القسامة، ولم يختلف (١٩٣/ ب) قوله في وجوب الحد على المرأة إذا نكلت عن اللعان. [مع] قوله: من جهة أن أيمان القسامة جاءت في الغرم.','4','379',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2771,'[كما] جاء في بعض طرق الحديث: "تحلفون خمسين يمينًا (١٥١/ أ) وتستحقون دم صاحبكم". وفي حديث آخر: "تحلفون على رجل من يهود ويسلم إليكم برمته". فالمفهوم من قوله: تستحقون دم صاحبكم، أي حق الدم، ونحن نقول: المراد بصاحبكم، أي قاتل وليكم، فيتفق مع الحديثين.
قال الإمام رحمه الله: (ونحن [نختتم] بكلام بدع [يقضي الفطن','4','380',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2772,'العجب منه]) إلى قوله (ولهذا وقع في [المرتبة] الثانية). قال الشيخ: أما مجال قياس الأشباه، فهو عند انحسام المعاني المستقيمة، فيرد الأمر إلى التشبيه. ثم لما كان قياس المعنى ينقسم إلى المعلوم والمظنون، [فكذلك] قياس الشبه. هذا قوله. وفيه نظر، وذلك أن قياس الشبه هو الذي يعتمد فيه على جامع غير مخيل، والذي مثل به المعلوم، هو قياس يعتمد فيه على نفي الفارق. فإن سمي هذا قياس شبه، فليس هو على ذوق الأشباه، التي عين فيها جامع لم تظهر مناسبته.
وإنما عد هذا من مسالك الأشباه، من جهة صحة الجمع، مع عدم','4','381',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2773,'الاطلاع على المعنى المخيل المناسب، وفيه خلاف. أعني: إذا انتهى هذا إلى حد العلم، هل يسمى قياسًا أم لا؟ وقد بينا الوجه في الاختلاف في ذلك.
وأما اختيار الإمام، فضعيف جدًا، لأنه إذا كان في اللفظ إشعار بحكم الفرع، فلا معنى لعد هذا من القياس بحال. وليس هذا محل اختلاف، وإنما الاختلاف في التشبيه بعد الاتفاق على عدم دلالة اللفظ، وهو كإلحاق صب البول في الماء بالبول فيه.
فأما ما مثل به هذا القسم من إلحاق عرق الكلب بلعابه في التعبد، ثم في','4','382',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2774,'العدد والتقدير، فليس عندنا معلومًا ولا مظنونًا، والحكم مختص بولوغ الكلب، حتى لو وقع لعابه في الماء من غير ولوغ، فلا غسل على المكلف فيه. هذا هو الذي يقتضيه قول مالك [رحمه الله]، وإن لم أره نصًا، ولكن النص عنه: أنه لو ولغ في اللبن والطعام، فالصحيح أن الغسل واجب، وإنما يجب في الولوغ في الماء على الخصوص. فإذا كان [هو] لا يوجب الغسل في الولوغ في بعض الأواني، فما قوله إذا وقع اللعاب من غير ولوغ؟
أما العرق، فلا خلاف فيه عندنا أنه لا يجب فيه غسل. والسبب في هذا كله أن الكلب طاهر العين، وإنما ضيق الشرع في الأمر بغسل الإناء من ولوغه خمسًا، لدواعي العرب عن المخالطة. وهذا يظهر في الولوغ دون العرق. والله أعلم.','4','383',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2775,'وأما قوله: إذا زال العلم، وكان الشبه شرطيًا (١٩٤/ أ)، فهو الشبه المعروف، وهو على ثلاث رتب عنده:
المرتبة الأولى: التي تقرب من العلم، إلحاق الزبيب بالتمر في الربا، وأبعد منه قليلًا بحيث لا يخرج عن الرتبة، إلحاق الأرز بالحنطة، والشعير بالذرة، فقد يظهر بينهما فرق، وإن كان خفيًا. قال الإمام [رحمه الله] في إلحاق الزبيب بالتمر: فلا أراه قاطعًا، لأن التمر قوت عام غالب، فقد يرى الشرع فيه استصلاحًا، ولم يبلغنا أن أمة من الأمم كانت تجتزئ بالزبيب. فكلامه الأول يدل على أن إلحاق الأرز بالبر أقرب، وهو خلاف ما ها هنا.
وأما إلحاق الوضوء بالتيمم، فهو لا يبلغ هذا المبلغ. وما يقوله الخصم، له وقع في النفس، من جهة ما قررناه قبل هذا، من فهم مقصود الوضوء في تحصيل التنظيف. ولهذا اختلف قول مالك في اشتراط النية في الوضوء.','4','384',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2776,'ولكن لما غلب في الباب التعبد، ظهر أن القصد غرض العقبى. وإذا كان كذلك (١٥١/ ب)، لزم اشتراط النية. ولهذا قال الشافعي [رحمه الله]: طهارتان فكيف تفترقان؟ يشير إلى ما قررناه من اتحاد الغرض، وثبوت التعبد في الطريق.
وقد قدمنا قبل هذا أن جميع ما طلب من الخلق فعله، إنما طلب لمصلحتهم، إما في الدنيا أو في الآخرة. وأمور الدنيا أغراض ناجزة تحصل، سواء قصد المكلف امتثالًا أم لا، كرد الغصوب والودائع.
فأما إذا لم يظهر غرض ناجز، لاح من مقصود الشريعة ربط ما لا غرض له ناجزًا بصلاح في العقبى، وهو الثواب.
وقد جاء الشرع بأنه إنما يحصل غالبًا إذا قصد التعبد به. قال - عليه السلام -: "الأعمال بالنيات". وفي حديث آخر: "لا عمل إلا بنية". فإذا ثبت','4','385',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2777,'[طهر] [لا] غرض فيه، افتقر إلى النية فيه، ثم ظهر [طهر] آخر كذلك ظهر فيه، وقع التشبيه في الافتقار إلى النية المحضة غرض العقبى، ولم يكن معلومًا، للاختلاف الواقع بين الطهرين في أحكام وشرائط.
قال الإمام رحمه الله: (وإلحاق المطعومات التي لا تتقدر بكيل أو وزن طريقة التشبيه عندنا). وهذا عجيب من كلامه في أول كتاب القياس، فإنه اختار هناك الجنوح إلى التعبد، وإثبات الربا في المطعومات بالنص. وزعم هو أن شرط الاعتماد على الشبه المتعلق بالمقصود دعاء الضرورة إلى التعليل. وقد قال هو في هذا الموضع في تقرير جهة الشبه: إذا اضطررنا إليه لإجماع القائسين. عد ذلك من مسالك الأشباه، وقد منع [هو أيضًا] فيما تقدم دعاء الضرورة. وقد يعتذر عن الإلزام الأول باختلاف القول (١٩٤/ ب) في','4','386',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2778,'الرأي، فيكون هناك رأي الجنوح إلى التعبد، ورأى [هنا] التمسك بالقياس والاعتبار. أما اشتراط دعاء الضرورة، فلا عذر له فيه. فإنه هناك قد بين أن الضرورة لم تدع بطريق النقل، فإنه قال هناك كلامًا صحيحًا: أن ابن عباس أنكر تحريم الربا في المطعومات، أعني ربا الفضل، فكيف يدعي الإجماع على التعليل مع الاختلاف في الحكم؟
وقال جماعة من القائسين بالتحريم في المحل المخصوص، متمسكين بالنص لا بالتعليل. فهذا تناقض بين في النقل. وهو شيء يشكك الناظر فيما ينقله، لحصول هذا الاضطراب الشديد.
قال: وإذا ظهر اعتبار المقصود، ظهر وقع التشبيه. والمقصود من هذه الأشباه أن تؤكل، وليس المقصود منها أن تكال وتوزن. وهذا الذي قاله بين، لو ظهر الاضطرار إلى الاعتبار، فإن التمسك بالوصف الأخص أولى، لاسيما إذا كان العام يظهر فيه ذوق الطرديات، كالكيل والوزن، فذلك معنى ما قدمناه','4','387',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2779,'من ظهور [النقض] المعنوي له.
وإذا كان التعلق بالمقصود أقرب، فالقوت أخص من الطعم، ولكنه زعم أن النص على الملح منع من ذلك. وهذا ليس من أبواب النقض، وإنما هو إلزام العكس، وهو غير لازم، إذا لم يسلم اتحاد العلة.
وإن لم يكن الملح مقتاتًا، فهو مصلح للقوت، على جهة المشاركة، بحيث لا يسهل الانتفاع بهذه المقتاتات على الدوام إلا بالملح، ولو لم يوجد الملح لاشتد لحرق الضرر بالخلق، فيصح أن يكون أدرج الملح معها لافتقارها إليه. فهذا هو التنبيه على سر المرتبة، وهي [المرتبة] الأخيرة من أبواب الأشباه.
قال الإمام رحمه الله: (فإن قيل: هل ترون الشبه الخلقي) في قوله (في الجواهر المعدنية [في الزكاة] طرد عندنا). قال الشيخ - رضي الله عنه -: الوصف الشبهي هو وصف لا تظهر مناسبته، ولكن يمكن أن يكون مشتملًا على مصلحة خفية، لا يحيط [الخلق] بحقيقتها. فإذا أمكن أن يكون الوصف المعقول متضمنًا لها، كالطعم مثلُا، أو الشرعي، ككون الطهارة حكمية، صح أن يكون','4','388',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2780,'الوصف الخلقي متضمنًا لها، كالمثل (١٥٢/ أ) في جزاء الصيد، فإن المماثلة جعلها الشرع معتبرة المتلفات. [أما عدم] المثليات بأمثالها، فمن أبواب المعاني، إذ ذلك يرجع إلى قيم المتلفات.
واعتبار أبي حنيفة الانطباع والانضراب في وجوب الزكاة، لا يلوح منه معنى فقهي، ولا يقدر على تقدير اشتماله على المصلحة. فإن قيم الزكاة من حيث الجملة تتعلق بالأموال النامية. والنمو في الأموال، يكون تارة بزيادتها في أنفسها، كالزكاة الثابتة [في] الحيوانات، وما أنبتت الأرض.
والقسم الثاني: ما يكون نموه بالأرباح، [وهي النقود]، فإنها المعدودة (١٩٥/ أ) في العرف أثمانًا، فيها الأرباح، وبها تقوم المتلفات.
وأما الانطباع والانضراب، فيرجع ذلك إلى اختلاف طباع الأجسام على حكم الاعتياد، فلا ربط بينه وبين وجوب الزكاة بحال.','4','389',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2781,'قال الإمام رحمه الله: (ومن أبواب الشبه ما يتعارض فيه المعنى والشبه) إلى قوله (فالحزم ألا يضطرب فيه بالخطو الوساع). قال الشيخ: ذكر في هذه الأمثلة تعارض المعنى والشبه المعتبر، وقد قدم في بعض الشبه على المعنى. وليس النظر في هذه الأمثلة من فن الأصول إلا في وجه واحد، وهو أنه قد [تقدم] أن أخر وقدم في بعضهما معنى على الشبه رتبة للمعاني تعد لاستفتاح الأشباه، وبين هو ذلك بأن قال: الجامع بالمعنى المناسب مشابه على الحقيقة، ومعه زيادة معنى يصح التمسك به دون الأصل، على القول بالاستدلال. فلهذا قضي بأن جملة المعاني مقدمة على الأشباه، وأتى ها هنا بتفصيل، وهذا التفصيل يقضي على ذلك المطلق.','4','390',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2782,'ولكن الصواب عندنا البقاء على الإطلاق الأول، وأن لا يقدم الشبه على المعنى بحال. فإن إثبات الشبه أولًا عسير، والحكم به مختلف فيه، والتحريم على أمر عيني بعيد خفي، فكيف يترك الواضح [المضبوط] لمثل هذا؟ هذا هو نظر الأصول. وسنتكلم على الأمثلة بما ينبغي.
المثال الأول: اختلف الفقهاء في قيمة العبد إذا قتل خطأ، هل تحمله العاقلة؟ فمالك رحمه الله يرى أنها لا تحمل، وغيره يقول: تحمله العاقلة اعتبارًا بدية الحر. وإنما قلنا إن العاقلة لا تحملها، تمسكُا بالنص والمعنى. أما النص: فقوله تعالى: {ولا تزر وازرة وزر أخرى}. وأما المعنى فهو أن المتلف أحق بالغرم من غيره، اعتبارًا بسائر المتلفات.
وهذا معنى واضح، مجمع على اعتباره. والشبه يقتضي الضرب على العاقلة، وهو أنه يفرض قتل العبد على الجهات التي يفرض عليها قتل الحر.','4','391',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2783,'ولكن هذا شبه ضعيف.
وقد صار كثير من الناس، منهم الإمام، فيما سبق، إلى أن ضرب العقل على العاقلة تعبد محض، ولا معنى لقصد التساعد في الحمل. وإذا كان الشبه ضعيفًا، لم يقف للمعنى القوي، على أنه أيضًا لا يصح التشبيه عندنا، فإن السبب في حمل العاقلة العقل، الحذر من الإجحاف بسبب كثرة الحمل في الحر، إذ جميع ما يتلف منه يشتق الغرم فيه غالبًا. [ألا ترى أن مالكًا رحمه الله يقول: إن قلة الجناية لا تحملها العاقلة. وإذا كان سبب الحمل كثرة الغرم، فلا خفاء في أن ذلك يختص بالحر غالبًا]. أما العبيد فالظاهر أن قيمتهم لا تبلغ مبلغ الإجحاف، ولا نظر إلى القادر. فلهذا اخترنا ألا تحمل العاقلة [العبد]. وهذا هو مذهبنا ومذهب الشافعي.','4','392',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2784,'وأما المثال الآخر: (١٩٥/ ب) وهو تقدير أروش العبيد بالسبب الذي تتقد ربه أطراف الأحرار. والذي يقتضيه المعنى ألا تقدير، والذي يقتضيه الشبه التقدير، اعتبارًا بالحر. وإنما قلنا إن القياس المعنوي يقتضي نفي التقدير، فلأن العبد مال يضمن بأقصى قيمته، ولذلك لو أنه تلف وقيمته تزيد على الألف، لوجب القضاء بأقصى القيم. فهو كالبهائم وسائر الأموال.
وإذا كان كذلك، وجب أن يضمن ما نقص منه، وهو قياس جلي. وذهب الشافعي إلى التقدير، اعتبارًا بالحر في تقدير الأروش. وقد ساعد هو على أنه لا تتقدر قيمة العبد، وإن (١٥٢/ ب) تقدرت دية الحر. وهذا الفرق أيضًا يضعف [التشبيه، ولكنه قال: الشرع قدر للحر إذا قتل بدلًا، وتشبيه أطرافه إلى جملته بشبه، لا تهتدي إليها العقول. وأضر ب عن] تقدير قيمة العبيد، رجوعًا إلى العرف. ونحن نعلم أن عنى يد العبد من جملته، كغنى يد الحر من جملته، فليكن هذا منسوبًا إلى القيمة، كما كان الطرف في الحر منسوبًا إلى','4','393',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2785,'الدية. ونحن نقول: إنما قدر الشرع أطراف الأحرار، احترامًا وتعظيمًا، كما فعل في النفوس. ولا يلزم مثل ذلك في أطراف العبيد، لتسليط القيمة على الأصل، فالأطراف بذلك أولى.
فإن قيل: فقد أثبت الشرع في الأحرار حكومات، ولم يقدرها، وردها إلى التقويم، فما باله لم يفعل ذلك في الأطراف؟ قلنا: للأطراف من الاحترام ما ليس للجراح، مع أن الجراح لا ينضبط أمرها، ولا يقدر شينها ونقصها، إذ بعضها سهل يسير، وبعضها صعب يشين، فكان الرد إلى التقويم أولى. هذا مذهب مالك رحمه الله، ولكنه ورد عليه في الباب اعتراض شديدـ، وهو أنه قدر من جراح العبد أربعة جراح: المأمومة والجائفة والمنقلة والموضحة، فجعل فيها نسبتها من القيمة، نسبتها في الأحرار من الدية. وهذا في معنى النقض عليه [إلا] أن يجد عنده عذرًا. وسيأتي ذلك، إن شاء الله تعالى.','4','394',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2786,'والسبب في التقدير في هذه الجراح بالإضافة إلى العبيد، [أن] الشرع قدر أروش أطراف الأحرار، إثباتًا للتعظيم، ولم يقدر الجراح، لما ذكرناه، وقدر في الأحرار هذه الجراح الأربعة، لأنها مخطرات، وإذا برئن لم يكن فيهن نقص بحال، ولم ير الشرع إهمال أمرها، لعظم خطرها، وشدة غررها فقدرها. فهذا [هو] السبب في الرد إلى الدية.
وهن أيضًا مخطرات مستقلات في حق العبيد، فوجب التقدير ردًا إلى القيم. هذا هو السبب في تقدير هذه الجراحات (١٩٦/ أ)، شدة غررها، وكونها إذا برئت لا يظهر فيها نقص بحال. وهذا الوصف يفارق الأطراف. وإذا كان الحكم معللًا بوصفين، لم يتوجه النقض على أحدهما.','4','395',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2787,'وإما إلزام تقدير أطراف البهائم، فنحن قد اخترنا ألا نقدر أطراف العبيد، فكيف بأطراف البهائم؟ وقد أجاب المقدرون بجواب سديد، فقالوا: لسنا نتحقق أنها تقع منها موقع أطراف الأحرار من الأحرار، بخلاف أطراف العبيد، فإنا نقطع باستواء [النسبة].
لكن الشأن كله في تخيل السبب الذي قدرت به أطراف الأحرار. فنحن نرى أنها قدرت [بالسبب الذي تقدرت به] النفوس، وغيرنا يرى أنها قدرت، لأن النسبة إلى الجملة نسبة النصف. وهذا يكاد يكون معلوم البطلان، إذ يتفق قطع إحدى اليدين وإحدى الرجلين، وإحدى العينين، وإحدى الأذنين، وإحدى الخصيتين، وكل واحد من زوج في الإنسان، فيغرم الجاني ديات، وهو لو أتلف الجملة، لم يكن عليه إلا دية واحدة، اقتضى ذلك أن التقدير لم يكن لهذا، وإنما هو تعبد من الله تعالى، إثباتًا لكمال الحرية.
قال الإمام رحمه الله: (ومما يعده الفطن قريبًا مما نحن فيه) إلى قوله (وهو السر وكشف الغطاء). قال الشيخ: هذا مثال واضح في تقابل المعاني','4','396',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2788,'والأشباه، فالمعنى يقتضي عدم ضرب القليل والكثير، فقد ضرب بالإجماع، وأما القليل، ففيه نزاع.
وتحرير الدليل أن نقول: بذل الجنابة على الحر على وجه الخطأ، فوجب أن تحمله العاقلة، اعتبارًا بالكثير. وهذا وجه من الشبه لائح. ويقول مالك رحمه الله: متلف فوجب أن يختص بالغرم، قياسًا على سائر الأموال. وهذا معنى مخيل، ولكنه منقوض بتحمل العاقلة العقل على الجملة، فإن ضم إلى التعليل عدم الإجحاف، وعكس قيدًا في محل الحمل، بطل بحصة آحاد (١٥٣/ أ) الشركاء، مع [تناهيها] في القلة. وهذا مثال من أشق الأمثلة وأصعبها في الجواب. ولأجل وجدان هذا المثال، زعم الإمام أن هذا يكاد أن يكون معلومًا.
ولكن طريق الانفصال عن السؤال، أن العقل مضروب على العاقلة، مساعدة وتخفيفًا عن الجاني، فلا يناسب ذلك أن يثبت على وجه يديم الخصومة، ويوجب التردد إلى الحكام في كل الأحوال. فهذا يناظر إيجاب الزكاة على الأغنياء، مواساة للفقراء. فما خف من الجراح، لا يشق حملها، ويكثر وقوعها، فلم تدع الحاجة إلى وجوب حمل فيها، مع ما يفضي إليه الأمر','4','397',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2789,'من شدة دوام الخصومة على العاقلة، فاستغنى في (١٩٦/ ب) الجراح القليلة عن الحمل لما ذكرناه.
وأما حمل حصة أحد الشركاء، فالجناية قد وقعت بصفة الكثرة، والأمور التي لا تقع إلا نادرة، فصادفت محل الحمل وكثرة العواقل، كاتساع عاقلة القاتل في الواحد، فلم يكن ذلك موجبًا ترك الجمل، نظرًا إلى كثرة الجناية. فهذا هو الفرق بين حصة آحاد الشركاء، وبين كون الجرح يسيرًا. وهذه أسرار غامضة في الشريعة ومآخذ خفية. والله يوفق من يشاء من عباده.
قال الإمام رحمه الله: (فإن قيل: [إذا] تعلق الناظر بوجه من الشبه) إلى قوله ([فهذه معاقد الأشباه، ثم] لا حاجة إلى تكلف الميز بينها وبين','4','398',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2790,'الطرد). قال الشيخ - رضي الله عنه -: أما مصيره إلى أن بعض الأشباه تخيل فيها معان جميلة دون بعضها، فهذا لا يسلمه القاضي وأصحابه، بل يقولون: كل شبهة لابد أن تشتمل على مصلحة خفية لا يطلع عليها. ولهذا قال في حد الشبه: إنه','4','399',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2791,'الذي يوهم الاجتماع في مخيل. وأما الشبه الراجع إلى المقصود عنده، فإنه لا يشتمل على معان خفية، ولكن يرجع الحكم إلى حصر أوصاف المحل. وشرط الاعتماد عليه عنده، دعاء الضرورة إليه.
وأما قوله: ثم ينتصب عليه شاهدان:
أحدهما- من قبيل اختلاف الحكم بتعدد المقصود واتحاده. وقد كنا بينا ذلك فيما سبق، وذلك أنه اتحد المقصود عند اتحاد الجنس، حرم','4','400',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2792,'التفاضل. وإن اختلف المقصود باختلاف الجنس، جاز. أرشد إلى أن سبب الاختلاف، هو المناط، وهو الطعم. ومالك يقول ذلك أيضًا بالإضافة إلى القوت.','4','401',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2793,'الثاني- قوله: "لا تبيعوا الطعام [بالطعام] ". يشير بذلك إلى الإيماء المتلقي من لفظ الطعم. وهذا لا يصح على قوله: إن المشتق إذا لم يحل، حل محل القلب.
قال الإمام: (فإن قيل: المعلوم الذي سمي [قياسًا] في معنى الأصل) إلى قوله (فليفهم الفاهم هذه المنازل. والترتيب بعد ذلك كله). قال الشيخ: اعلم إنما يعلم التفات الشرع إلى أوصاف لم تظهر مناسبتها، من النظر في عادة ما يشرع في موارده ومصادره.','4','402',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2794,'أما المناسب، فأمر ذوقي وقضاء مصلحي يطلع عليه، [وإن] قدر عدم ورود الشرائع. وإنما تطلب الشرائع لبيان الاعتبار دون ذوق المصلحة. وإذا كان كذلك، فالأوصاف التي لا تظهر مناسبتها [هي] أوصاف علم إعراض','4','403',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2795,'الشريعة عنها، كما علم الإعراض عن الفرق بين صب البول في الماء والبول فيه. وهذا يمثل [في المناسب.
وأما إدراك الإعراض في غير المناسب، فكما يعلم الإعراض عن الذكورة (١٩٧/ أ) والأنوثة في باب سراية العتق، وكذلك في باب] تشطير الحد، علم ذلك باستقراء [الشريعة]. [كما علم الإعراض عن الألوان والطول والقصر في جميع الأحكام. فاستقراء الشرع] هو الذي يعرف مقصوده بما أضرب عنه والتفت إليه. وكذلك القول في كونه أعرابيًا فيما يتعلق بكفارة','4','404',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2796,'الوقاع. وأمثلة ذلك كثيرة.
فإن قيل: فقد جاءت الشريعة (١٥٣/ ب) بالفرق بين الأمثال. وغاية هذا الكلام يرجع إلى تحقيق التماثل. وأما القطع بأن حكم المتماثلات واحد، فلا سبيل إلى القطع به. أجاب الإمام عن هذا بجواب سديد، فقال: لما قرر الشرع هذه القاعدة على الوجه المذكور، لزم في حكم البيان إذا أراد [الاقتطاع] أن ينص عليه، وإلا وقع التمثيل، كما قال لأبي بردة: "تجزئك ولا تجزئ عن أحد بعدك". فهذا يعرف قصد الشرع إلى التعميم في الأحكام.
قال الإمام رحمه الله: ([والترتيب بعد ذلك كله) إلى قوله (وهو','4','405',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2797,'[دون] [الرتبة] الثالثة). قال الشيخ]: هذا الكلام واضح على مقصوده، والكلام على تحقيق المراتب قد تقدم، فلا معنى لإعادته.
قال الإمام رحمه الله: (ونحن [نختتم] هذا [الفصل] بمسألة','4','406',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2798,'يتعارض فيها [شبهان]) إلى قوله (وهو حق [المستمع] في النكاح). قال الشيخ - رضي الله عنه -: الذي تقدم للإمام قبل هذا، خلاف هذا، وذلك أن القاضي أتى بهذا المثال بعينه في تعارض الأشباه. فأنكر الإمام هذا، وقال: هذا من قبيل المعاني، فإن كل واحد من المتكلمين في المسألة منخرط في سلك المعنى المناسب. [قال]: والإخالات على رتب ودرجات. قال: فإنها معان','4','407',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2799,'يبدو أنها تكاد تتعارض، وإنما تعب [المجتهدون] فيها في الترجيح. ثم رجع في هذا المكان إلى أنها من الأشباه. وهذا هو الصحيح، ولا تظهر مناسبته على ذوق المناسبات، [بمقتضى] كون العبد مالكًا، ولا [يقتضي] ذلك. ولكنا نقول: ملك الشرع الحر، إما تشريفًا، وإما لسر اطلع عليه. ولم يملك البهيمة. والعبد آدمي كالحر، ومال كالفرس. وكل شبه يقتضي حكمًا خلاف حكم الآخر، فأي الشبهين كان أغلب، فهو المقدم.
وقد قدم أبو المعالي ها هنا كونه مملوكًا، إذ هو يرجع إلى الحكم.','4','408',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2800,'وهذا ترجيح ضعيف. وقد قدم غيره كونه آدميًا، بناءً على هذا الوجه [الشاذ]. إذ لا يتصور زوال الإنسانية، ويتصور زوال الرق، والأقرب الملك، نظرًا إلى تصور الحاجة، وثبوت الإيالة والسياسة، وصحة القيام على [المملوكات]. وهذا هو الذي يظن أنه سبب ملك الحر، وقد دل عليه ظاهر النص، حيث قال - عليه السلام -: "من باع عبدًا وله مال". (١٩٧/ ب)
قال الإمام رحمه الله: (فإن قيل: والسيد إذا ملك عبده، فالحق لا يعدوهما) إلى قوله ([ولا خلى] الشرع وحكمه). قال الشيخ: هذا','4','409',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2801,'الاعتراض إنما يتوجه على الشافعي، الذي يقول أنه لا يملك. أما نحن إذا قلنا إن العبد يملك، فلا يتوجه علينا هذا الكلام. ووجه الإلزام أنه امتنع كونه مالكًا لحق السيد، فلا يتوجه علينا هذا الكلام. ووجه الإلزام أنه إذا امتنع كونه مالكًا لحق السيد، وجب أن يملك، لزوال حقه بإذنه. [فأجاب] الإمام عن هذا بأن قال: هذا إنما يلزم من رآه أهلًا للملك، وإنما امتنع عليه لحق السيد.
أما من ذهب إلى أنه لا يتهيأ للملك بحال، للرق الثابت فيه، فإذن السيد [لم] يخرجه عن كونه رقيقًا متصرفًا فيه. فلم يجامعه التمليك، كما لم يجامعه [إلزام] الملك. يريد بذلك أنه لا يتصور أن يملك ملكًا لازمًا يتعذر على السيد التصرف فيه. فلو كان يسقط حقه، للزم ملكه، ولا ذاهب إليه. فيرى أنه لا يملك، لبقاء الرق فيه. [فإن ضعف الرق] بثبوت الكتابة، تصور له ملك بحسبه. والملك الذي يتصور له بحسبه، ملك يتصرف فيه بأنواع المعاوضات [والتسببات] دون إتلاف، إذ هو اللائق بحال المكاتب، إذ هو [مطالب] [بأداء] النجوم. فلو لم يسلط على الكسب، لم يحصل مقصود الكتابة. فهذا هو تمام المراد في هذه المراتب.','4','410',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2802,'قال الإمام رحمه الله: <span data-type="title" id=toc-500>(فصل - المرتبة الأولى من قياس المعنى) [</span>إلى قوله (فلينظر الناظر في جريان الحقائق في هذه المضايق]). قال الشيخ: (١٥٤/ أ) أراد الإمام بهذا الكلام أن يبين أن الأشباه تترتب ترتب المعاني منسوبة إلى أصول معلومة، فيقع التفاوت بالإضافة إلى القرب من الدرجة المعلومة والبعد عنها. فما قرب من المرتبة المعلومة قدم، وما بعد عنها أخر. وهذا الكلام صحيح، وجعل كل رتبة من رتب المعاني في مقابلتها رتبة من','4','411',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2803,'رتب الأشباه. إلا أنه [إنما] [أدخل] ها هنا [ضمنًا] من رتب المعاني ما ثبت بإيماء الشرع، ولم يطلع على وجه مناسبته، ولم يذكره في المراتب السابقة، وجعله ها هنا يناظر الأشباه ما يضطر إليه من طلب المقاصد.','4','412',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2804,'وأما ما يتعلق بتقديم المعاني على الأشباه، أو تقديم بعض الأشباه على بعض المعاني، فقد تكلما عليه، وكان اللازم على مقتضى تقديره، ألا بحكم بتقديم شيء من الشبه على شيء من المعنى، ولا يعكس ذلك أيضًا. بل كان مقتضى طريقه أن يقدم ما قرب من القاعدة العلمية على ما بعد. وكذلك يلزم هذا في تقديم بعض الأشباه على بعض المعاني.
وبيانه: هو أنه إذا (١٩٨/ أ) كان شبه المعنى [بالنسبة] إلى قاعدته يقع بين رتبتين مثلًا، [وكانت] نسبة الشبه إلى قاعدته معلومة يقع ثاني رتبة، فلا وجه لتقديم المعنى الواقع بعد رتبتين على الشبه الواقع بعد رتبة. هذا هو اللائق بهذا الطريق، وهو لا يقول هذا، ولا غيره أيضًا يقوله. فدل على أن هذه النسبة والموازنة غير صحيحة.','4','413',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2805,'وأما ترجيحه الشبه المقتضي "ضرب القليل" على المعنى، فقد بينا لترك الضرب وجهًا سديدًا.
وأما مصيره إلى نفي التقدير في أطراف العبيد، وترجيحه المعنى، من جهة أن من [يوجب] ما [ينقص] [فيطرده] معنى، [فلا] ينتقض [عليه] [بحال]. فهذا عندنا لا يصح، [لأنا] قد بينا أن بعض جراحات [العبد] مقدرة، ولا يلتفت إلى ما نقص منها، فكلام المعنيين منقوض عندنا. إلا أن يكون بنى الأمر على مذهب مالك. وأما مالك، فالأمران عنده سواء.

قال الإمام رحمه الله: <span data-type="title" id=toc-501>(فصل - أحدث المتأخرون لقبًا لباب من أبواب القياس) </span>إلى قوله (هذا المسلك يلتحق بأقيسه المعاني). قال الشيخ: هذا','4','414',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2806,'الذي ذكره الإمام ها هنا، فيه احتمال كونه قسمًا زائدًا، [وفيه ما يرشد إلى أنه ليس قسمًا زائدًا.
فأما الذي فيه يمنع كونه قسمًا زائدًا]، فقوله: أحدث المتأخرون لقبًا لباب من أبواب القياس، ووجه الدليل فيه من وجهين: أحدهما-[أنه] جعل الأمر راجعًا إلى التقليب.
الثاني- أنه جعل هذا أمرًا محدثًا، ولو كان قسمًا من أقسام القياس، لم يكن محدثًا.
وأما ما فيه يدل على الزيادة، أنه قال: وهو _إذا صح_[يلتحق بقياس','4','415',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2807,'الشبه] من وجه، ولم يجعله قياس شبه على الإطلاق. وكذلك قوله: وقد [يتأتى] في بعض أمثلته ما يلحقه بقياس المعنى.
والصحيح عندنا انحصار الأقسية في المعاني والأشباه، ولا سبيل إلى إثبات قسم زائد، وذلك أنه لا بد من مناط الحكم، فإن كان المناط مناسبًا، فهو الذي يعبر عنه بقياس المعنى، وإن كان غير مناسب، فهو المعبر عنه بقياس الشبه.
وأما قياس الدلالة، فقد بينا أنه إنما سمي [بهذا] عند الأصوليين، لأنه جمع بما يدل ولا ينقضي، لا مباشرة ولا متضمنًا للمناسبة. وهو الاستدلال بالآثار. ثم تلك الآثار ترجع إلى مناسب، أو لما يتضمن المناسب. فإن عد','4','416',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2808,'قياس الدلالة خارجًا عن [قسم] المعاني والأشباه، فسببه ما ذكرناه، وإن عد من أحد القبيلين، فسببه أنه لا يثبت [الأثر] الجامع إلا مستندًا لأحد أمرين.
وأما ما ذكره: من أن التمسك بهذه الصيغة يتوجه عليه سؤال [المطالبة] لا محالة، إذ كل جامع بين [أصل] وفرع بجامع، يطالب ببيان كونه (١٩٨/ ب) جامعًا. (١٥٤/ ب) هذا هو المشهور، وقد كنا قدمنا عن أبي حامد أن سؤال المطالبة على الجامع لا يتوجه، ويزعم أنه قول المتقدمين من الأصحاب. والتفريع على غير هذا [الأصل]. فإذا تمسك بأن المحل قابل [للملك]، والمالك أهل للتصرف، فمن المناسب نفوذ تصرفه، والطلاق دليل الاعتبار. ولم يبق إلا أنه في الظهار قابل منكرًا وزورًا، [والكفر] لا','4','417',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2809,'ينافي ذلك. وإذا [تعين] المقتضي [وفقد] المانع، [لزم] ثبوت الحكم.
قال الإمام رحمه الله: (والمسلك الثاني في [الخروج] عن المطالبة) إلى قوله (فهذا [مضرب] [النظار فيما ذكرناه]). قال الشيخ - رضي الله عنه -: قد تقدم القول في الطرد والعكس، وهل يصح التعلق بهما في إثبات كون الوصف','4','418',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2810,'علة؟ واستقر رأي الإمام على ذلك.
[وهل] يكون من أبواب الإيماء، أو من ناحية الأشباه؟ والصحيح أنه من ناحية الأشباه. فإذا طولب الجامع بإبداء وجه الجمع، وإبداء الطرد والعكس، كان منفصلًا عن السؤال. وقد ذكر الإمام وجه الاطراد والانعكاس في ذلك، [من] اقترانهما نفيًا وإثباتًا باختلاف صفات المحل.
قال الإمام رحمه الله: (والقسم الثاني من هذا القسم: أن يذكر المطالب)','4','419',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2811,'إلى قوله (هذا النوع من القياس، [فإنا] من القائلين به). قال الشيخ: أما قوله: إذا سلك مسلك إبداء المعنى، فقد تبين أن ما سكت [عنه]، لم يكن كلامًا تامًا، فإن [إبداء] المناسبة إذا كان محتومًا، ولم يذكره السائل، فقد تبين أن ما جاء به مفتاح الحجة ومبدؤها. فهذا الذي قاله مما اختلف الناس فيه، هل يتعين على المستدل في أول الأمر تقرير جهة الإخالة، [أو لا] يلزمه ذلك حتى يطلب منه؟ وهذا هو الصحيح، لاسيما إذا كان في الكلام ما يرشد إلى المناسبة، كقولنا: مسكر فكان حرامًا، قياسًا على الخمر. فلفظ الإسكار يشعر بالتحريم عند ذوي العقول. وكذلك قولنا: من صح طلاقة صح ظهاره، إذ صحة الطلاق تدل على اعتبار عبارة المتصرف وقبول المحل. فعلى','4','420',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2812,'هذا إذا قرر وجه ذلك عند المطالبة، إنما يكون منبهًا للخصم على جهة الدلالة، فلا يكون أتى بزيادة، بل بإرشاد وهداية.
وأما قوله: إنه إذا أبدى مناسبة بين نفوذ الظهار والطلاق، صار الطلاق أصلًا، واستغنى عن التمسك بالمسلم. ويصير [التقدير: ] كلمة تتضمن التحريم، فيجب أن تكون عاملة في حق الذمي [كالطلاق]. وهذا السؤال لازم على مقتضى الجدال. فإن لم يبد المعنى، ولكنه سلك مسلك التشبيه، وقال: الطلاق: (١٩٩/ أ) يشبه الظهار فيما يرجع إلى النفوذ، وإنما يعسر النظم أيضًا، كما سبق في المعنى. وقوله: لابد من إخالة فقهية أو قضية شبهية، وكلاهما يتضمن [التغيير] ويوجب اختلال نظم الدليل.
قال الإمام رحمة الله: (والوجه في الجواب عن السؤال ودفع المطالبة)','4','421',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2813,'إلى قوله (في ضرب العقل على العاقلة). قلت: أجاب الإمام عن السؤال بجوابين:
أحدهما- الاطراد والانعكاس، وعلى هذا لا يستغني عن المسلم بحال. [بهذا يدفع] السؤال الأول، إذا اعتبر الظهار بالطلاق، فإنه عند ذلك يجب اطراح المسلم، [فيتغير] النظم. وأيضًا فإن الاطراد والانعكاس يظهر من لفظ الشرط صريحًا وضمنًا أمرهما عند الإمام، فإن مطلق الشرط يقتضي ضمنًا عنده بالعكس، على ما مر في قاعدة المفهوم.
وأما قوله: من تتمة القول: إن قياس المعنى إذا انعكس، كان [العكس فيه] ترجيحًا. [فإذا لم [يلتزم] المعلل المعنى، كان متمسكه شبهًا.','4','422',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2814,'أما كون المعنى إذا انعكس، كأن ترجيحًا]، وهو ظاهر من جهة ثبوت شدة الارتباط، فإنه قد يقال إن اقترانهما في الثبوت وفاقي. فإذا زال عند زواله (١٥٥/ أ)، كان أقوى في كونه باعثًا. ومن هذه الجهة كان الطرد والعكس مغلبًا على الظن في انتصاب الوصف علمًا.
وأما قوله: إذا انعكس كان قريبًا من ضرب الأمثلة. ومعنى ذلك أنا نجد في غير محل [النزاع] صورًا تداني محل النزاع في الارتباط بالوصف المذكور. كما نجد في مسألة ضرب القليل من العقل حصة آحاد الشركاء، وإنما لم يكن هذا من ذلك على التحقيق، أن الدليل مستقل هناك بالقياس على الكثير، وكانت الأمثلة في معنى الزيادة. وها هنا إذا سلك الأطراد والانعكاس، كانت الصورة مأخوذة من حد الدليل.
قال الإمام رحمة الله: (ومما ينقدح في هذا النوع) إلى قوله (في إثبات الطريقة جريانها طردًا وعكسًا). قال الشيخ - رضي الله عنه -: أما هذا الوجه، وهو قوله:','4','423',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2815,'الأصل المسلم وظهاره، والفرع الكافر وظهاره، كلام ضعيف، وكيف يصح أن يقال الأصل المسلم وظهاره، والأصل في عرف الأصوليين وأصحاب الجدل هو: المحل المحكوم فيه بالتوفيق، والفرع هي مسألة النزاع، أي التي وقع النزاع في حكمها؟
وإذا كان كذلك، كان الأصل السلم دون ظهاره حكم ثابت ليس بأصل، بل حكم في أصل. والفرع الكافر، والحكم [المتنازع] فيه ظهاره، فكيف يجعل حكم الفرع المتنازع فيه مع الفرع (١٩٩/ ب) فرعًا؟ هذا الفرع المتنازع فيه مع الفرع (١٩٩/ ب) فرعًا؟ هذا','4','424',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2816,'خلاف الاصطلاح، وحيد عن طريق الصواب.
وأما ما ذكرناه من [شروط] الفرع، فغير محقق في الحكم المتنازع [فيه]. وكذلك شروط الأصل لا يتصور تحققها في حكم الأصل، الذي هو صحة الظهار. لا جرم بينه الإمام فقال: والأحزم الرجوع في قياس الدلالة [إلى] الطرد والعكسٍ]، فيجعل الطلاق وصفًا، ويربط الظهار به حكمًا، ويتخذ المسلم أصلًا، [ويجعل] اعتماده في الطريقة جريانها طردًا وعكسًا.
قال الإمام رحمه الله: (ومما يتعين الإحاطة به في هذا الصنف، أن','4','425',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2817,'المعنى المخيل حكمه مناسب) إلى قوله (من جهة تطرق المطالبة بالجمع وعدم التزام المعلل له). قال الشيخ: قال الإمام: إن المعنى المخيل حكمه مناسب لحكم أو صورة، تنبئ العبارة عنها، وقد يكون الجامع نفي حكم أو نفي صورة.
أما كون المعنى قد يكون مناسبًا صفة أو حكمًا، فهو صحيح. أما الصفة فكقولنا: مسكر فكان حرامًا، وأما الحكم فكقولنا: نجس محرم بيعه. فهذا حكم مناسب. وقد قررنا مناسبته فيما سبق.
وأما المصير إلى أنه قد يكون المناسب نفي حكم أو نفي صورة، فقد تقدم منا أنه لا يصح أن يكون النفي مناسبًا بحال، [إلا أن يراد] أنه إذا [انتفى] وصف ثبت ضده، فتكون المناسبة للضد الثابت، لا للوصف المنفي. فهذا يصح. [فإذا] جعلنا الحكم علة لثبوت حكم آخر، وبينا','4','426',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2818,'المناسبة، صح الربط ولا يتصور على هذا أن يطلب وجه الجمع، بل [إنما] يرجع الطلب إلى تقرير جهة [الربط]. وإذا [تصور ذلك، لم يتصور ورود الفرق، إذا لا يتصور الفرق إلا حيث يتصور الجمع. وإنما] يصح الجمع بين الأصول والفروع.
وأما طلب الجمع بين العلل والأحكام فمحال، فإذا بطل الجمع، بطل [الفرق]، فإنهما أمران متلازمان، لا يتصور ثبوت أحدهما إلا حيث يتصور ثبوت الثاني. ولذلك، أنا حققنا أن صور الفرق تقرر الجمع، ويفرق وراءه من وجه آخر. وإنما يرجع الجمع إلى إلحاق الفروع بالأصول، وليست العلة','4','427',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2819,'الجامعة فرعًا يتصور ورود الفرق.
فعلى هذا لا يتصور إيراد فرق بين الحكم المجعول علة، وبين الحكم المرتبط به. وهذه الخاصية [هي] التي يمتاز بها قياس الدلالة، فإن جعل الطلاق أصلًا والظهار فرعًا، أمكن الفرق، وإن جعل المسلم أصلًا، ونفوذ الظهار جامعًا، والطلاق حكمًا متنازعًا فيه، لم يمكن الفرق بحال.
[نعم]، [قد يبدي] كلامًا يقدح في المناسبة. (١٥٥/ ب) وهذا يجري في جميع الجوامع، أوصافًا كانت أو (٢٠٠/ أ)، [ويتعين] الجواب بإبداء الفقه المقتضي للحكم المرتب على الجامع المذكور. وقد بينا المناسبة في ذلك بين نفوذ الظهار ونفوذ الطلاق، بالنظر إلى قبول المحل، وصحة عبارة المالك.','4','428',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2820,'وأما قوله: إن التحريم ينقسم إلى ما يقع حقًا لله تعالى، [وإلى ما يقع حقًا للآدمي، والظهار يقع حقًا لله تعالى، والكافر لا يخاطب بما يقع حقًا لله تعالى] من الفروع، لم يكن هذا فرقًا، وإنما [يرجع] هذا إلى [توهين] الإخالة، وبيان انتفائها. فكان الصنف الذي فيه الكلام [بين] قياس المعنى، من جهة إبداء مناسبة تصرف [تصرفًا] على الجملة، وبين مسالك الأشباه، من جهة تطرق المطالبة بالجمع وعدم التزام المعلل له.
قال الإمام رحمة الله: (ومما يحيك في [النفس]) إلى قوله (فقد حان','4','429',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2821,'أن يرجع بنا الكلام [إلى] الترجيح). قال الشيخ - رضي الله عنه -: أما التفصيل الأول، وهو إذا اجتمع في المسألة أنواع من الأدلة، فإن كانت [مستوية] الرتب، تمسك المستدل بما شاء منها، وإن كانت متفاوتة، فلا يخلو: إما أن يكون فيها قطعي، أو تكون كلها ظنية، فإن كان فيها قطعي، فالدليل القاطع يحصل علمًا، فلا أثر للظنيات في الباب، إذ الحكم معلوم، مستند [لدليله] القاطع، وإن كانت ظنية، فلا يخلو عند تفاوتها: إما أن يكون التفاوت أيضًا معلومًا، وإما أن','4','430',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2822,'يكون مظنونًا، فإن كان التفاوت معلومًا، فإن كانت الأمارات كلها ظنية، فهذا موضع خلاف، منع بعض الجدليين من ذلك، لحصول القطع بالتفاوت.
والصحيح عندنا جواز التعلق، [كمن يتعلق] بالقياس الموافق لخبر ثابت ناص من قبيل الآحاد. وإنما يظهر التفاوت على تقدير التناقض. [وأما]','4','431',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2823,'إن [كان] التفاوت مظنونًا، فلا خلاف في جواز التمسك بالأدنى مع الاستمكان من الأعلى. وهذا كله أمر يرجع إلى أدب الجدل ومعرفة الاصطلاح. وليس في [الدين] [ما] يقتضي المنع بحال. وكما قلنا إن شرط إثبات الحكم بالقياس أن يكون حكم الفرع مسكوتًا عنه. فإذا كان منصوصًا عليه، فكيف يتلقى حكمه بالقياس؟ وبينا أن المراد بذلك أن يكون مسكوتًا عنه، بالإضافة إلى ذكر حكم الأصل المعتبر به، وليس المراد أن يكون مسكوتًا عنه، مطلقًا، ولذلك قال الإمام ها هنا: يجوز أن يتمسك بقياس وإن كان في المسألة خبر ثابت. فهذا يبين أن المراد ما ذكرناه.','4','432',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2824,'قال الإمام رحمه الله: (فنقول: إنما يجري الترجيح في أقيسة لا [يعترض] عليها) (٢٠٠/ ب) إلى قوله (و [في] استيفائها استكمال القول في الترجيح). قال الشيخ - رضي الله عنه -: ما ثبت بإيماء [الشارع] مقدم على','4','433',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2825,'المناسب الذي لا يتصف بذلك، والسبب فيه انه ليس كل مخيل عامًا، وليس كل استصلاح وجهًا مرتضى، وإنما يظن ذلك استنباطًا. وما يرجع إلى دلالة اللفظ يقدم على الاستنباط. وهذا ذكره الأصوليون، والأمر على ما ذكروه، إلا أن يقوى المناسب جدًا، بحيث يقرب من الأصول المعلومة، وهي [العلامة] في القوة، فلا بد من تقديم المناسب حينئذ، ولذلك أن النصوص قد تومئ إلى أوصاف، ويستنبط المستنبط أمورًا مناسبة تناط الأحكام بها. وهذا كقوله: "لا','4','434',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2826,'يقضي القاضي وهو غضبان". فإنه إيماء إلى التعليل بعين الغضب، ونحن نستنبط الدهش والحيرة، حتى يجوز القضاء مع الغضب اليسير، ويمتنع مع الجوع المفرط والألم المبرح، وإن لم يكن فيه غضب.
وكذلك القول في تقديم المعنى المستند إلى أصل على الاستدلال، قدمه الأصوليون على الإطلاق. والصواب عندي (١٦٥/ أ) في ذلك أن يقال: [إن] كان معنى الأصل في رتبة الاستدلال في القوة والضعف، [قدم]','4','435',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2827,'الأصل [حينئذ]، وإن كان معنى الأصل غريبًا، والاستدلال ملائمًا، فهذا موضع اجتهاد، ويعمل كل مجتهد بما غلب على ظنه. والقول في تقديم الشبه الخاص والاستدلال يجري هذا المجرى. فإطلاق القول بالتقديم والتأخير في هذه [المراتب] بعيد، ولكل مسألة ذوق.

قال الإمام رحمه الله: <span data-type="title" id=toc-502>(مسألة: إذا تعارضت علتان إحداهما منعكسة [والأخرى غير منعكسة]) </span>إلى قوله (ثم يعود الكلام إلى غرضنا من الترجيح). قال الشيخ - رضي الله عنه -: [قوله: ] إذا تعارضت علتان وانعكست إحداهما دون الأخرى، [فالذي] ذهب إليه الأصوليون أن المنعكسة مرجحة.','4','436',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2828,'وقوله: وهذا يتجه على قوله: إن الاطراد مع الانعكاس دليل صحة العلة. إن أراد بهذا الكلام البناء على قول من يشترط العكس، فهذا غير صحيح، لأن العلة إذا لم تنعكس كانت باطلة، فكيف يقع الترجيح بين الباطل والصحيح؟ وإن لم يرد ذلك، وإنما أراد أن الطرد والعكس من جملة الطرق التي تثبت بها على الأصول، وهو الذي أراه في الظاهر، فحينئذ يقال إن المنعكسة مرجحة. لأنه وجد فيها دليل النصب من وجهين: أحدهما- من جهة الإخالة والمناسبة. والثاني- من جهة الاطراد والانعكاس.
وقوله في القياس الشبهي: إنه إذا انعكس الوصف الشبهي (٢٠١/ أ) كان الانعكاس فيه مخيلة [معتمدة]. لأنه يرى أن الاطراد [والانعكاس] من فن الشبه، فقد اجتمع فيه الوصف [الشبهي] ما يقتضي اعتماده من وجهين:','4','437',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2829,'أحدهما- جهة المشابهة فيه. والثاني- من جهة طرده وعكسه، وإن فرض الانعكاس في أقيسة المعاني.
قال الإمام رحمه الله: ([فنقول: ] رب معنى مخيل مناسب لا يشعر انتفاؤه بانتفاء الحكم) إلى قوله (إذا اجتمع فيه إخالة فقهية وقوة شبهية). قال الشيخ: أما قوله: إن المعنى لا يشعر عدمه بعدم الحكم، فالأمر كما قال.
وأما قوله: إنه قد يشعر به، فقد بينا أن ذلك غير صحيح عندنا. وأما المثال الذي مثل به، ففيه غلط، لأنه قال: قلنا [إنه] مستقل بالتصرف،','4','438',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2830,'فلا يولى عليه، كان انتفاء الاستقلال يشعر بالولاية، هذا [الوصف] يتضمن إثباتًا.
[وأما] معنى كونه لا يستقل، أنه عاجز، والعجز وصف إثباتي مناسب. فهذا المثال ضعيف. قال: [فإذا] كان المعنى بحيث لا [يخيل] عدمه عدم الحكم، واتفق مع ذلك انعكاسه، فقد تجمعت فيه إخالة، [يعني] في كونه علة، وقوة شبهية، بناء على ما [قرره] من أن الطرد والعكس يدل على صحة العلة، [ويلتحق بأبواب الأشباه. هذا مراده. ومن منع كون الطرد والعكس يدل على صحة العلة]، [بطل] هذا الترجيح عنده. وهذا الرأي هو الذي يقوى عندنا.','4','439',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2831,'قال الإمام رحمه الله: (وإن تعارض معنيان، وأحدهما يشعر بالطرد والعكس نفيًا وإثباتًا، والآخر يخيل في [وجه] الطرد) إلى قوله ([وهذا المعنى لا يتحقق فيما لا يقتضي الإخالة] [في] جهة العكس). قال الشيخ: أما المصير إلى إخالة العلة في الانعكاس، فقد تكلمنا عليه، وبينا أن المثال الذي تمسك به مدخول، وأن ضد المعنى ناسب ضد الحكم. وإذا بني الأمر على [قوله]، كانت العلة التي تخيل نفيًا وإثباتًا، مرجحة على العلة التي تخيل في جانب الإثبات، لأنها إذا خالت في النفي والإثبات جميعًا، فهي أقوى.
فإما إذا اتفق الحكم عنده بانتفائها، من غير مناسبة لها فيه، ولكن','4','440',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2832,'[كان] ذلك من جهة التمسك بالطرد والعكس على ما مر [الكلام] عليه. والاطراد والانعكاس في [هذا] الوجه مفقود، فمن [أي] جهة تثبت قوة العلة ورجحانها مع بطلان القضية الشبهية منها؟ قال هو: هي في معنى المنعكسة، (١٥٦/ ب) وإنما منعها مانع من الانعكاس. يقال له: [فقد] فات الوجه الشبهي الذي استند إليه الترجيح في القسم السابق، إلا أن يقول [إنها] إذا أخالت نفيًا وإثباتًا، قوي طردها. وهذا فيه نظر، على أنه يقول إن العلة إذا أخالت في جانب (٢٠١/ ب) الطرد والعكس [تنزل] طرد العلة وعكسها منزلة علتين [المسألتين]. وإذا كان كذلك، فمن أين تتقوى علته بانضمام أخرى إليها تقتضي خلاف حكمها؟ هذا بعيد.','4','441',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2833,'قال الإمام رحمه الله: (ولو لم تنعكس علة مقتضاها الانعكاس) إلى قوله ([أقوى من إخالة العلة الأولى في العكس لا محالة]). قال الشيخ: الصحيح عندنا أن العلة لا تخيل في جانب العكس بحال، وأنه لا يدل الطرد والعكس على انتصاب الوصف علة. وإذا كان كذلك، بطل جميع هذا الكلام. ولكنا إذا صرنا إلى ما يقوله، وكانت العلة مشعرة في جانب العكس، فلا يتقوى بذلك أمرها في جانب الطرد. أما إذا انعكست على رأيه، فقد اجتمع فيها إخالة فقهية وفقه شبهي. فينبغي أن تكون [هي] المرجحة على هذا الرأي.
[وقوله]: ومما يتم به الغرض في ذلك إلى آخره. [فنقول: ] هذا','4','442',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2834,'الذي قاله صحيح، لأنه إذا جعل عدم العلة في نفي الحكم، فإذا اختلفت علة تقتضي الإثبات، فلا يثبت الحكم إلا إذا كانت العلة المناقضة للنفي مرجحة على علة النفي، كالقول في كل متعارضين أثبت أحدهما الحكم، فإنه يجب أن يكون أقوى.
قال الإمام رحمه الله: (فإن امتنع الانعكاس لنص أو إجماع، فهذا موضع [التوقف]) إلى قوله ([فهذا هو اللائق [بغرض] الترجيح في فصل] العكس). قال الشيخ - رضي الله عنه -: أما من ذهب إلى أن تخلف الانعكاس لنص أو إجماع، فلا يغيره عنده بحال، فإنا قد قلنا إنما يلزم نفي الحكم عند انتفاء العلة، لتعذر بقائه واستمراره، غير مستند لشيء. أما إذا دل الإجماع على استمراره، فكيف ينكر ثبوته ولا مستند لعلته؟ وهذا كما جاء أن النبي - صلى الله عليه وسلم - في [عمرته]، قال كفار مكة: "يقدم عليكم قوم وهنتم حمى يثرب". فقال النبي','4','443',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2835,'- صلى الله عليه وسلم -: "رحم الله امرءًا أراهم [اليوم] من نفسه قوة، فرملوا". وهذا لا شك، فيه معنى معقول، قصد به إظهار قوة أهل الإسلام. ولما حج رسول الله - صلى الله عليه وسلم - حجة الوداع، ولم [يبق] للمشركين قوة على حال، استمر رسول الله - صلى الله عليه وسلم - وأصحابه على مثل تلك الأعمال، ولا يبين ذلك عدم التعليل في الأولى.
قال الإمام رحمه الله: (مسألة: قد تقدم القول في العلة القاصرة) إلى قوله','4','444',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2836,'([وتلويح] متلقى من مسالك الاجتهاد). قال الشيخ: هذا الذي ذكره','4','445',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2837,'الإمام في هذا المكان غاية في البيان، وهو كلام لا مزيد عليه، ولكنه مبني على امتناع تعليل الحكم بعلتين مطلقًا على قوله، أو في الموضع الذي تفتقر العلة إلى شهادة الأصل عندنا.','4','446',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2838,'قال الإمام رحمه الله: ([ينبغي] وراء ما [ذكرناه] أن العلة المتعدية) إلى قوله ([والشريعة عرية] عن اتفاق وقوعها). قال الشيخ: جميع ما ذكره الإمام في هذه الأبواب من ترجيح [العلل]، إنما [هو] كلام على التنزيل [والتقدير]، وأما على أصله، فلا يتصور منه شيء بحال. إذ شرط الترجيح اشتمال كل علة على شرائط [الصحة] لو كانت','4','447',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2839,'منفردة، وهو لا يجوز تعليل [حكم] بعلتين، لا مجتمعتين ولا متعاقبتين، إذا [استنبطتا] من أصلين، وهما متناقضتان في الفرع، رد الأمر إلى أصل [آخر]، وهو [هل] الحكم معين في المجتهدات أم لا؟ فإذا بنينا على [أن] الحكم معين في المجتهدات، لم يكن الترجيح جاريًا على حقيقته، إذ أحدهما غير علة [بلا] إشكال، فكيف ترجح العلة على غير علة؟ ولكن نتكلم على ما تكلم عليه [تنزلًا].
أما مصيره إلى [أن] المتعدية مرجحة، من جهة أنها [المفيدة]،','4','448',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2840,'فلا تترك لمكان حكمه [تسنح]. فهذا منه إشارة إلى مذهب من يبطل العلة القاصرة، ولا يراها متعلق الأمر بالاجتهاد والاستنباط. وكيف نتكلم على الترجيح مع المصير إلى بطلان القاصرة؟ هذا كلام ضعيف.
وأما كلامه آخرًا، وهو قوله: [قد وجدنا] في الفرع معنى على شرط الصحة خاليًا عن المعارضة، فكيف نترك [حكمة]، لعدم حكمة سنحت في الأصل؟
فقوله: وجدنا معنى صحيحًا، هذا عين المتنازع فيه، فإن من لا يرى','4','449',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2841,'تعليل حكم بعلتين، ويرى صحة [العلة] القاصرة، يقول: شرط الصحة الاعتبار في الأصل، والتعدد يمنع الاعتماد إلا بعد التعيين، فلم يصح الاعتماد في الأصل إلا بعد النظر في الأصل، ويتعين عليه. فلست أدري ما مراده بهذا الكلام. فالصواب التعارض على ما قاله القاضي رحمه الله.
قال الإمام رحمه الله: (فإن قيل: فقد علل أبو حنيفة [الربا] في [النقدين] بالوزن) إلى قوله (ولسنا نرى [للمسألة] الموضوعة جدوى ولا','4','450',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2842,'فائدة). قال الشيخ - رضي الله عنه -: الكلام على إبطال كون الوزن علة، قد قدمناه.
وأما قوله: إن العلة القاصرة إن لم تكن مخيلة في جانب العكس، فلا معارضة. كلام غير صحيح، [لأنا] قد قلنا إن المعارضة تكون على وجهين:
أحدهما- من جهة تعدد العلة في الأصل، على القول بامتناع تعليل الحكم بعلتين، فيقع التعارض من جهة ضيق المحل عن [الوفاء] بالعكس شرعًا. (٢٠٢/ أ)
والمعارضة تأتي على جهة التناقض في الأصل، فقال: إذا لم [تخل] في جانب العكس، [فلا معارضة [بينها] وبين المتعدية. قال: والمتعدية','4','451',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2843,'ليست مخيلة في جانب الطرد، فكيف يتخيل إخالتها في جانب العكس]؟
قال الإمام رحمه الله: [فإن قال قائل: ] لو استنبط ناظرٌ علة في محل التحريم) إلى قوله (وهذا لا سبيل إليه). قال الشيخ: هذا الذي ذكره إذا [استنبطت] [علة] قاصرة من محل تحريم، وعُدمت في [فرع]، واستنبطت من محل تحريم علة متعدية، ووجدت تلك [العلة] المتعدية في [فرع]، اقتضى وجودها التحريم في الفرع، واقتضى نفي القاصرة التحريم، فقد جاءت المعارضة بين وجود المتعدية ونفي القاصرة.
وصورته: ما قررناه في تحريم التفاضل عند استنباط النقدية أو الوزن.','4','452',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2844,'استنباط النقدية يقتضي جواز التفاضل في الموزونات، واستنباط الوزن يقتضي تحريم ذلك.
قال الإمام [رحمه الله]: هذه محاولة ترك الطرد [لمكان] العكس، ولا يتصور أن يعارض عكسُ طردًا، فإن العكس يقع ضمن العلة، [والطرد هو الأصل]. وهذا إنما هو كلام في العلل التي لا يكون عكسها مستقلًا بالنفي. [أما إذا كان عكسها مستقلًا بالنفي]، فلا تعدٍّ في المعارضة، وطُلب الترجيح. وإنما نتكلم هنا عن العكس الذي يقع ضمن العلة.
قال الإمام رحمه الله: (فإن قيل: علة الشافعي في تثبيت الخيار للمعتقة تحت العبد) إلى قوله (وهما عريتان عن الفوائد). قال الشيخ: لما رجَّح الإمام المتعدية على القاصرة، وجَّه على نفسه سؤالًا في المصير إلى مذهب الشافعي، فإنه علَّل الخيار بظهور شرفها على الزوج، وهذا يقتضي ألا تخير إذا','4','453',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2845,'عتقت تحت الحر. وعلل أبو حنيفة [بملكها] نفسها، وهذا يقتضي أن تخير وإن عتقت تحت حر، فكانت علته متعدية، وعلة الشافعي قاصرة.
قال الإمام: علة أبي حنيفة باطلة عندنا، فلا ينتهي الأمر معه إلى الترجيح، ثم عطف على ذلك إبطال علة الشافعي أيضًا. قال: وخيارها تحت العبد مما لا يعلل أصلًا. (١٥٧/ ب) وإذا كان المعنيان باطلين أو أحدهما، كيف يتصور الترجيح؟
أما إبطال علة أبي حنيفة، فإنه علل خيارها [بملكها] نفسها. فنقول: قال القاضي - رضي الله عنه -: إن [ملكت] غير مورد النكاح، لم يشعر ذلك باختيار في مورد النكاح، وإن ملكت مورد النكاح فالخيار. لماذا؟ هذا كلامه. ونزيده تقريرًا وتحقيقًا فنقول: ينزل المملِّك منزلة المالك، والسيد نقل إلى الأمة ما كان يملك منها، والسيد لم يكن له (٢٠٣/ أ) سلطان على فسخ النكاح، [فالمنزَّل] منزلته أولى بذلك. فهذا التقدير يبطل التعليل بعلة أبي حنيفة والشافعي جميعًا.
قال الإمام: وأيضًا فإن من يثبت الخيار لها، إذا عتقت تحت حر،','4','454',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2846,'يزعم أن بريرة عتقت والزوج حر، ولم يثبت ذلك بالنظر إلى تعدية العلة، [بل] بالتوقيف، فلم ينته الأمر مع أبي حنيفة إلى مقام الترجيح.
[ويتصل] الكلام في العلة القاصرة بمسألة، وهي أن الحكم في الأصل، هل يضاف إلى العلة، أو الذي يضاف إلى العلة حكم الفرع خاصة؟ فالظاهر من قول مالك [رحمه الله] من جهة استقراء الفروع أن حكم الأصل يضاف إلى العلة. [وهذا هو مذهب الشافعي - رضي الله عنه -. ولمالك قول آخر مستنبط من فروعه، أن المضاف إلى [العلة] حكم الفرع. وهذا هو قول أبي حنيفة. وإنما قلنا إن الظاهر من مذهب مالك هو الأول، من جهة أن مالكًا رحمه الله قال: من جامع في نهار رمضان ناسيًا، فلا كفارة عليه. وإن كانت قضية الأعرابي مطلقة، لم يفصَّل فيها عمدٌ من نسيان. ولكنه لما استنبط معنى','4','455',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2847,'الهتك، لم يتناول ذلك الناسي.
فإن قيل: في الواقعة ما يرشد إلى العمد، لأنه قال: «هلكت وأهلكت». [والهلاك] والإهلاك إنما يكون عند التعمد. قلنا: ليس كذلك، فإنه قد يظن الظان أنه لما بطل صومه، كان ذلك هلاكًا.
ولمالك قول آخر: أن الكفارة تجب على المجامع مطلقًا، نظرًا منه لترك الاستفصال في حكايات الأحوال. وعلى هذا [كان] يجب [أن] تثبت الكفارة على من أكل ناسيًا، ولم يصر إلى ذلك أحد من أصحابنا. دل هذا على أنه إنما بطلت العلة لأحكام الفروع دون حكم الأصل، لثبوته مستغنيًا عن التعليل. وقال أبو حنيفة: يحرم قليل الخمر وكثيره، تمسكًا بالنص، ولا يحرم من النبيذ إلا القدر المسكر طلبًا للتعليل.
والصحيح عندنا أن الحكم في الأصل يضاف إلى العلة، كما يضاف إليها حكم الفرع. والدليل على ذلك من وجهين:','4','456',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2848,'أحدهما- أنا بالاستنباط نتبين قيدًا في اللفظ المطلق، وذلك أنه لا فرق بين الاستنباط وبين النص على التعليل أو الإيماء إليه. ولو قال: حرمت الخمر المسكرة، أو الخمر لأنها مسكرة، اقتضى ذلك قصر الحكم على المسكر دون غيره. [فكذلك] إذا استنبطنا الإسكار، فالتقدير كالأول. هذا وجه.
الوجه الثاني- أن العلة إنما تثبت في الفرع، بناءً [على] أنها مشهود لها في الأصل. [فإذا] كان حكم الأصل لا يضاف إليه، وكانت وفاقية فيه، فكيف يضاف إليها حكم الفرع، والأصل لم يشهد لها (٢٠٣/ ب) بحال؟ هذا يمنع الاعتماد عليها مطلقًا.
فإن قيل: فلتستعمل هذه العلة استعمال المعاني المرسلة. قلنا: [عنه] جوابان:','4','457',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2849,'أحدهما- أنا ألزمنا بطلان القياس على مقتضى التقدير السابق.
[الجواب] الثاني- أن المتمسك بالمعنى المرسل يظن [بأن] الشارع لو سئل عنه لحَكَمَ بمقتضاه. فإذا كان الحكم الوارد بمعزل عن المعنى، خرج المعنى عن الاستدلال [المرسل. والله أعلم].
قال الإمام رحمه الله: (مسألة: قال من يرجح العلة المتعدية) إلى آخرها. قال الشيخ - رضي الله عنه -: الكلام في هذه المسألة كالكلام في القاصرة','4','458',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2850,'والمتعدية (١٥٨/ أ)، والإمام سلك ها هنا مسلكه هناك. والرد عليه في هذه، كالرد [عليه] هناك. ونزيد ها هنا زيادة في الرد، وذلك أنه في القاصرة يميل إلى أنها غير مفيدة. فإذا قدَّم المتعدية هناك، فقد يتخيل لمذهبه وجه. وإن كنا [قد بينا] أيضًا فساد الترجيح على هذا التقدير. أما ها هنا، فقد ثبت لكل علة شرائط الصحة، بالنظر إلى التعدية، وبحصول الفروع.
وعمدته أنه صرف نظره إلى الفرع الذي وجدت فيه إحدى العلتين، وفقدت منه الأخرى، ولا يعارض [دليل دليلًا]. ونحن قطعنا نظرنا [ابتداءً] عن الفرع، ونظرنا إلى الأصل، باعتبار تعدد المعنى، وبيننا الأمر','4','459',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2851,'على امتناع اجتماع العلتين للحكم الواحد. والكلام فيه على حسب ما سبق. (٢٠٣/ ب)

ثم قال: <span data-type="title" id=toc-504>(مسالة: من اعتقد أن كثرة الفروع تقتضي ترجيحًا) </span>إلى آخر المسألة. فنقول: أما الكلام على كثرة الفروع وقلتها، فقد تقدم الكلام عليه. وإذا بني الأمر على الترجيح بكثرة الفروع، فإذا كانت القليلة الفروع لها نظائر تساوي في عدتها كثرة الفروع، فهل يقوم كثرة النظائر مقام كثرة الفروع؟ هذا فيه نظر.
واعلم أنه إذا وقع التفريغ على أصل مختل، اختلت الفروع لا محالة.','4','460',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2852,'وقد حققنا أنه لا التفات إلى كثرة الفروع، فإذا كانت القليلة الفروع ظهر تأثيرها في نظائر للحكم المطلوب، كان ذلك من بابِ ملائمٍ عارَضَ غريبًا. والترجيح ذلك واقع. هذا حكم الأصول.
وأما الذين رجحوا بكثرة الفروع، فسبب ترددهم في هذه المسألة، ميلهم إلى النظائر في قوة الشهادة للعلة. وإذا كان الإمام لا ينظر إلى الأصل، وإنما ينظر إلى الفروع التي وجد فيها أحد المعنيين دون الثاني، فلا معنى لتردده في وجوب إعماله، وقد وجد (٢٠٤/ أ) في الفرع المعنى الصحيح غير معارض. هذا حكم الأصول تحقيقًا وتخريجًا.
وأما مثال المسألة، فهو ما ذكره من الوقاع الذي رتب عليه الكفارة، فإن الشافعي خصه بالإتيان في المأتى الأصلي، وفيه واقعة الأعرابي. فإن قيل: فهذا','4','461',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2853,'من قبيل العلة القاصرة. قلنا: ليس كذلك، فإنه يرى أنه لو وطئ البهيمة أو المرأة في دبرها، لوجبت الكفارة. فالعلة إذًا متعدية. ولكن [أبا] حنيفة إذا اعتبر كل مفطر بمتنوع النوع، يعني بذلك جنس المشتهى، ففروع علتها أكثر. وعلة الشافعي لها نظائر في الشرع من الحد والغسل والإحلال والإحصان، وأشياء كثيرة. فهذا معنى كثرة النظائر، أي أن الأحكام المترتبة على الوقاع كثيرة في الشريعة.
قال الإمام: (وهذا ليس بشيء، فإن الأحكام عندنا ليست معللة، وليست من قبيل الأمثلة، فإن تلك الأمثلة تجري في غير المطلوب). معناه: أن الأمثلة كانت في ضرب القليل من العقل على العاقلة، وليست هذه النظائر كذلك.','4','462',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2854,'وأما قوله: إن معنى أبي حنيفة باطل بالنقض، فهو رأى أنه لا كفارة على مبتلع الحصاة والنواة، وإن كان قد أفسد صومه وهتكه، ولذلك وجب القضاء عليه. وإذا بطل المعنى بالنقض، خرج عن أبواب المعارضة والترجيح. وأما قوله: المذهب المطرد مذهب مالك، فلمالك قولان:
أحدهما- وهو الظاهر، ما قاله أبو حنيفة من أنه لا كفارة على مبتلع الحصاة.
والثاني: التعميم في كل مفطر تعاطاه المكلف عامدًا عاصيًا به. وهذا','4','463',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2855,'القول ضعيف عندنا، وهو الذي نسبه الإمام لمالك رحمه الله.
فإن قيل: فبم تعتذرون عن النقض المتوجه على القول المشهور؟ قلنا: لا نقض على الحقيقة، فإنا نضيف إلى العلة قيدًا، مناسبًا فنقول: تجب الكفارة بكل مفطر مشتهى الجنس. أما ما لا يشتهى جنسه، فلا تجب فيه الكفارة وإن أفسد الصوم.
ونبدي المناسبة ونقول: على تقدير قوة الداعي على تقدير الاحتياج إلى قوة الصارف. وإذا ضعف الداعي، اكتفي بمجرد وازع الدين. وهذا بمثابة تحريم الخمر والنجاسات التي لا تشتهى، لم يفتقر في الانكفاف عن النجاسات إلى وضع، لعدم الشهوة غالبًا، وافتقر في الخمر إلى ذلك. فإذًا لا نقض متوجه على أبي حنيفة.','4','464',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2856,'وأما اختيار الإمام القول الذي نسبه لمالك، فهو اختيار ضعيف، ينبني على ما ذكره من مصادفة المعنى في بعض الفروع، فلا يقدح فيه عدم مصادفته (٢٠٤/ ب) معنى غيره. والرد عليه كما سبق، من وجوب الالتفات إلى علة الأصل قبل النظر في الفروع.
وأما قوله: إن الوطء ينبغي أن يكون على مزية، اعتبارًا بالنسك، يريد بذلك أن يظهر تأثير إفساد الصوم في الحج، لا في إفساد ولا في هدي، وقد ظهر أثر الوطء في ذلك، فكان حكمه في نظر الشرع أغلظ.
قال: فليس من الإنصاف تقديم شبه على هذا الوجه على معنى الهتك لمالك، أي المعنى قوي في إثبات الحكم في الفرع، فلا يترك لمثل هذا الشبه. وإنما صعبت المسألة عنده على ما أصَّله. وأما على ما اخترناه نحن، فالعسر إنما يكون في تقرير قول مالك رحمه الله. فإنه إذا كان الحكم في الأصل','4','465',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2857,'مضافًا إلى وصف، يظهر أن يكون له أثر في قوة المناسبة، فمن أراد إثباته في الفرع مع فقدان هذه المزية، افتقر إلى الدليل. وفي الوطء من قوة الداعي إليه، ما ليس في الطعام، فكيف يصح الإضراب عما فيه من قوة الداعي؟ وقد يقول مالك: لما كانت الطباع مختلفة في قوة الداعي إلى الجماع والطعام، ولا ينضبط التفاوت بحال، وجب تعليق كفارة الإفطار بكل مشتهى. وهذا القول محتمل، وللآخر وجه بيِّن. والمسألة اجتهادية، والذي نأخذ به تعليق الكفارة على كل مشتهى الجنس. والله أعلم.
ثم قال: (مسألة: [تتعلق] ببقايا الكلام في هذا الفن) إلى آخرها.','4','466',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2858,'فنقول: هذا الذي ذكره الإمام ها هنا حسن بالغ، فلا حاجة إلى مزيد عليه.
ثم قال: (مسألة: ذهب ذاهبون إلى أن ما تجاذبه أصلان) إلى آخرها. فنقول: إذا دار الفرع بين أصلين، فلا يخلو: إما أن يوجد فيه كل مناط بكماله، وإما أن توجد شائبة من كل مناط، فإن وجد فيه كل مناط بكماله، وكان حكم','4','467',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2859,'المناطين متناقضًا، فلا يخلو: إما أن يترجح أحد المناطين فيه، وإما أن لا يترجح. فإن ترجح أحد المناطين، فإن الاعتماد عليه واجب، وإن لم يترجح، فهذا قد قدمنا اختلاف الأصوليين فيه. يخير الناظر بين الدليلين، لا من جهة اعتقاده توفير شبهي الأصلين، فإنه أجلُّ قدرًا من أن يرى ذلك، ولكن قال: إذا تحقق استواء الدليلين، ولم تظهر قوة في أحد الجانبين، وامتنع إعراء المسألتين عن الحكم، واستحال اجتماعهما جميعًا، وبطل التعيين بالتحكم، لم يبق إلا التخيير. وقد ذكرنا طريقه فيما سبق. واعترضنا عليه.','4','468',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2860,'هذا إذا وجد كل مناط في الفرع. وأما إذا وجد شائبة في كل مناط، ففي المسألة إشكال قدمناه، (٢٠٥/ أ) وهو أن تنزيل الشائبة منزلة المناط الكامل عَسِرٌ، إلا أن يدل دليل على أنها مستقلة في هذا المكان. فهذا يصح، وليس من المستحيل أن يكون الوصف علة في محل وجزء علة في محل آخر، ولكن يشترط ألا يتماثل المَحال. فإذا اجتمعت الشائبتان ووقع التخيير، كما بين الدليلين، فيجري ذلك على ما قاله القاضي رحمه الله.
أما المصير إلى أنا نوفر شبهي الأصلين، فلا شك في بطلان ذلك. والأمر على ما قاله الإمام من أن فاعل ذلك يقطعه عنهما جميعًا، إذ موجب كل أصل يحتم كل حكم. والمصير إلى التخيير يخالف الأصلين.
ثم قال: (مسألة: إذا تعارضت علتان، واختصت إحداهما بالاستناد إلى','4','469',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2861,'أصول) إلى قوله (فليفهم الناظر ما يرد عليه). فنقول: المسألة مصورة فيما إذا كانت إحدى العلتين ترجع إلى أصل، والأخرى ترجع إلى أصول. وأما الذي يكره الإمام في الصورة الثانية، فخارج عن المسألة بالكلية، لأنه صورها في اجتماع معان في مسألة يرجع كل معنى إلى أصل. فليس هذا من المسألة بسبيل. ثم هو أيضًا لا يجري على أصله في امتناع اجتماع العلل للحكم الواحد.
فلنرجع إلى المسألة في الحقيقة، وهو المعنى الواحد إذا رجع إلى أصول متعددة. أما من لم ير الترجيح بذلك، فقد احتج لهم الإمام بحجة، وهي أن الاعتماد في الحقيقة إنما هو على المعنى والأصل، يعني لانحصار المسألة. وهذا يحصل بأصل واحد، وما وراء ذلك، لا حاجة إليه.
وهذا الذي قاله ممكن، ولكنه قد يقال في مقابلة ذلك: إن المعنى إذا','4','470',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2862,'صودف الحكم على وفقه في أصل واحد، فقد [تمكن] الوقفية فيه دون السببية. إذ كثرت الأصول، واستمر الحكم بعد أمر الاتفاق. وهذا ظاهر في العادة، فإنه إذا دخل شخص مكانًا، ودخل معه آخر إليه، احتمل أن يكون دخل بدخوله، واحتمل أن يكون دخل عند دخوله، فإذا تكرر دخوله معه، أرشد ذلك إلى قصد الاقتران، وضَعُفَ احتمال الوقفية.
فإن قيل: المناسبة الظاهرة مع الاقتران ترشد إلى [السبب] دون','4','471',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2863,'المصادفة، وإلا إذا كانت كثرة الاقتران على حياله ترشد إلى السببية، كانت مقربة لجانب المناسبة في انتفاء المصادفة. فهذا له وجه لا ينكر، والمسألة اجتهادية.
وأما مصير الإمام إلى أن مثل ذلك يقتضي تقديمًا في الأشباه، وينزِّل كثرة الأصول في أبواب الأشباه منزلة (٢٠٥/ ب) الرواة في مراتب البينات. وهذا والأول عندي سواء. فيقال فيه ما قيل فيه.
وبيانه: هو أن الوصف الشبهي: هو وصفٌ لا على ذوق الطرديات، وافتقر إلى ورود الحكم على الوفق ليقوِّي في النفس الملاحظة. وهذا يحصل بأصل واحد، وليس يشترط كثرة الأصول في الأوصاف السببية. فإن وقع الترجيح بكثرة الأصول في أبواب الأشباه، وجب أن يكون كذلك في أبواب المعاني. وبالله التوفيق.
ثم قال: (فإن قيل: إلحاق الرأس بالرجلين) إلى آخر المسألة. فنقول:','4','472',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2864,'خرج الكلام عن مقصود المثال في الالتفات إلى كثرة الأصول، واعتمد في منع المسح على العمامة على الفرق بين العمامة على الرأس، والخف على القدمين، من جهة عسر النزع في أحدهما وخفته في الآخر. وهذا الفرق صحيح، ولكن ليس هذا الكلام من قبيل الترجيح بكثرة الأصول، ولكنه كلام يقطع الإلحاق، ويوجب إمساس البشرة بالماء.

ثم قال: <span data-type="title" id=toc-506>(مسألة: إذا تعارض قياسان ومع أحدهما ظاهر) </span>إلى آخرها. فنقول: أما المذهب الأول، فباطل بما ذكره الإمام، والمذهبان الآخران متقاربان أيضًا. والظاهر عندي إعمال الظاهر مرجحًا بأحد القياسين أو مستندًا للعمل، والقياسان متعارضان. وإنما اخترنا ذلك، لأن الظاهر دليل، فلما عارضه','4','473',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2865,'القياس، وقف لأجل معارضه، فإذا عارض المعارض معارض آخر يساويه، كان وجوده كالعدم، فيبقى الظاهر معتمدًا.
وقد يقال إن القياس إذا انضم إلى الظاهر، صلح لكونه ترجيحًا، وإن لم يصلح لكونه دليلًا، نظرًا إلى معارضه. واختار الإمام أن يستند العمل إلى القياس ويرجح بالظاهر، واعتل بأنه يبعُد أن يكون الظاهر دليلًا وفي مقابله قياس يخصصه. وهذا ضعيف، إذ المخصص إنما يكون مخصصًا لو انفرد، وأما إذا عارضه مثله، لم يقرَّ على التخصيص بحال، والمذاهب كلها متقاربة.','4','474',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2866,'ثم قال: <span data-type="title" id=toc-507>(مسألة: إذا تعارض قياسان واعتضد أحدهما بمذهب صحابي) </span>إلى قوله ([وفيه نستقصي] القول في مذاهب الصحابة). فنقول: اختلف الناس في مذهب الصحابي، هل يجوز إسناد الحكم إليه، ويكتفى بذلك عن المطالبة بقيام دليل أو لا يكتفى به؟ وإذا قلنا إنه لا يكتفى به، فهل يكون ترجيحًا لدليل على دليل، أو يكون ترجيحًا أيضًا؟ أو يفرق بين صحابي يشهد له الشرع بمزية العلم وتمام الدرك؟
فالعلماء على مذاهب، فذهب (٢٠٦/ أ) قوم إلى أن مذهب الصحابي حجة مطلقًا، وقوم إلى أن مذهب الصحابي حجة إن خالف القياس، وقوم إلى أن الحجة قول أبي بكر وعمر رضي الله عنهما خاصة، لقوله - عليه السلام -: «اقتدوا باللَّذين من بعدي: أبي بكر وعمر». وقوم إلى أن الحجة قول الخلفاء الراشدين إذا اتفقوا. والكل باطل عندنا.','4','475',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2867,'وأقرب المذاهب إلى الصواب قول من يقول: إنه إذا خالف القياس وجب اتباعه. هذا إذا تحققنا أن جميع وجوه الرأي لا [تقتضي] ما حكم به الصحابي، وإن كنا لا نرى ذلك أيضًا.
والدليل عليه أن من يجوز عليه الغلط والسهو، ولم تثبت عصمته، فلا حجة في قوله، فكيف يحتج بقوله مع جواز الخطأ عليه؟ وكيف تدَّعى عصمته من غير حجة متواترة؟ وكيف يتصور عصمة قوم يختلفون؟ وكيف يختلف المعصومون؟ وكيف وقد أجمعت الصحابة على جواز مخالفة آحاد الصحابة؟ فلم ينكر أبو بكر وعمر رضي الله عنهما على من خالفهما في الاجتهاد، بل عمل كل مجتهد بما غلب على ظنه. فانتفاء الدليل على العصمة [ووقوع] الاختلاف، وتصريحهم بجواز مخالفتهم، أدلة بينة في منع وجوب الاتباع.
وقد قال [المخالف]: وإن لم تجب العصمة، فإذا أوجب الاتباع لزم. وكان بمثابة خبر الواحد والقياس. وقد قال - صلى الله عليه وسلم -: «أصحابي كالنجوم، بأيهم اقتديتم اهتديتم». والجواب: أن هذا خطاب مع عوام أهل عصره بتعريف','4','476',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2868,'درجة الفتوى لأصحابه، حتى يلزمهم اتباعهم، وهو تخيير لهم في الاقتداء بمن شاءوا. ولكن هذا عندنا إنما يكون إذا لم يترجح أحدهم بالعلم عند المستفتي. أما إذا ترجح عنده، فقد قدمنا الكلام عليه. على أن هذا لا يدل على وجوب الاتباع، بل على [الاهتداء] إن وجد الاقتداء.
الشبهة الثانية: إن دعوى وجوب الاتباع إن لم [تصح لجميع الصحابة]، فلتثبت للخلفاء الأربعة، لقوله - عليه السلام -: «عليكم بسنتي وسنة الخلفاء الراشدين من بعدي». وقوله: «عليكم» للإيجاب. والجواب: أن هذا لو كان يدل، لحَرُم على بقية الصحابة الاجتهاد، ووجب تقليد الخلفاء الأربعة، فلئن جاز خروج الصحابة بدليل، جاز خروج العلماء بدليل، وقد كانوا - رضي الله عنهم -','4','477',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2869,'يجتمعون ويتشاورون ويختلفون، ولا ينكر بعضهم على بعض. وهذا منقول تواترًا.
فالظاهر أنه أراد من الأمة سلوك نهجهم في السير والدين وتقديم أمر الله تعالى، وإيثار الآخرة على الدنيا، زهدًا فيها وإعراضًا عنها.
وقد يمكن أن يكون المراد حث الرعية على طاعة (٢٠٦/ ب) الأئمة، والنهي عن المخالفة وإبداء المشاقة، لا الأمر بالتقليد بحال.
الشبهة الثالثة: قولهم: إن لم يجب تقليد الخلفاء، فيجب تقليد أبي بكر وعمر رضي الله عنهما، لقوله - صلى الله عليه وسلم -: «اقتدوا باللذين من بعدي: أبي بكر وعمر». قلنا: فلنقتد بهما في تجويزهما لغيرهما مخالفتهما. ثم رد عليه من التأويل ما سبق. ثم الأخبار المتقدمة تعارضه. ولو اختلفنا كما اختلفا في التسوية في العطاء والتفضيل، فأيهما يتبع؟
الشبهة الرابعة: أن عبد الرحمن ولَّى عليًا الخلافة بشرط الاقتداء بالشيخين فأبى، وولَّى عثمان فقبِل ذلك، فعقد له، ولم ينكر عليه. قلنا: قد خالف في ذلك عليٌّ - رضي الله عنه -، فمن أين يصير قول خصمه حجة عليه؟ ويحتمل أن','4','478',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2870,'يكون عبد الرحمن لم يرد بالاقتداء التقليد، وإنما أراد سلوك طريق الحق دون التقليد في الأحكام.
فإن قيل: لو كان كذلك، لم يمتنع عليٌّ منه. قلنا: يكون عليٌّ قد فهم التقليد في الأحكام فامتنع، ويكون عبد الرحمن إنما قصد سلوك طريق العدل، أو يكون عبد الرحمن فهم جواز تقليد العالم للعالم. وهذا لعمري ضعيف، وهو أن المجوِّز لا يجوِّز الاتباع، ولكنه يسوِّغه. وظاهر حال عبد الرحمن الإلزام. فلا وجه إلا أن يحمل الأمر على أنه قصد العدل والإنصاف، ولم يفهم عنه عليٌّ - رضي الله عنه - ذلك.
وقد كنا قلنا: إن أقرب المذاهب قول من يفرِّق بيم كونه موافقًا للقياس أو مخالفًا له، ويرى أنه لا يخالف القياس إلا عن توقيف اتفق وقوفه عليه وإن لم يبلغنا. وهذا لا يتحقق أنه لم يستند إلى الرأي بحال.
ولو تحققنا استناده إلى غير الرأي وللبراءة الأصلية، ويتعين عندنا أنه إنما أسنده إلى التوقيف. فهذا موضع كلام. وهو يشبه إذا ما روى خبرًا وخالفه، فإنا اخترنا هناك سقوط التمسك بالخبر، والرجوع إلى رأيه. وهذا من ذلك القبيل.
فإذا تقرر هذا، فإن بنينا على أن قول الصحابي حجة، كان هذا من باب ضم دليل إلى دليل عند تعارض القياسين. وقد تقدم القول: هل يحكم بتعارض','4','479',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2871,'القياسين ويستند العمل إلى مذهب الصحابي، أو يكون القياس الذي عضده قول الصحابي مقدمًا؟
وإن قلنا: إنه لا يكون حجة، وقد اختلفوا في كونه مرجحًا، ذهب القاضي رحمه الله إلى أنه لا يقع به ترجيح، وقوله كقول غيره. قال: ولا يقوى دليل بمصير مجتهد إليه. وقول القاضي أصح الأقوال في نظر الأصول. وفرَّق الشافعي بين أن يكون الصحابي ممن (٢٠٧/ أ) شهد له الشرع بمزية الدرك في ذلك الفن أم لا. فإن لم يشهد له الشرع، كان قوله كقول غيره من العلماء، وإن شهد له، فمذهبه يرجح القياس الذي عضده.
والصواب عندنا أيضًا أن هذا لا يعول عليه إذا فرع القول على أن كل مجتهد مصيب. وبيانه: لو ثبت تفاوت بين الدليلين عند الناظر، وكان الصحابي قد أخذ بدليل ضعيف، لم يصح للمجتهد الذي ثبت عنده ضعف مأخذه أن يصير إليه، بناء على أن الشارع شهد له بمزية العلم في ذلك الفن.','4','480',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2872,'والسبب في ذلك أن هذه الشهادة تفيد غلبة الظن من حيث الجملة في أن قوله صواب. فإذا كان النظر الخاص في عين المسألة يوجب ضعف دليله، فكيف يترك الأمر الخاص لأمر جلي؟ فإن مقصوده من المحل المخصوص، أنه لا تبقى غلبة الظن بقوة مأخذه مع ظهور القوة في خلافه.
وإذا امتنع أن يصير إلى قوله عندما يثبت عنده ضعف مأخذه، فكذلك إذا غلب على الظن الاستواء. فالمصير إلى التقديم يقتضي حصول غلبة ظنٍّ بقوة ما يصير إليه من الدليل، والنظر الخاص يقتضي غلبة ظن التسوية. فلا وجه لترك الاستواء المظنون من الجهة الخاصة.
هذا هو الجاري على قول من يقول كل مجتهد مصيب. أما من يقول المصيب واحد، فلا يبعد أن يرجِّح بقول الصحابي. إذ هذا القائل لم يظن الاستواء بحال، إذ الأمر ملتبس عليه، والاستواء عنده مفقود. وقد لا يصير إلى تقديم ما صار إليه الصحابة، لأنه قد تبين بالبحث الخاص أن الجهات الظاهرة ليس فيها ما يقتضي تقديمًا، فيغلب على ظنه بسببها أن المرجِّح سلك في الترجيح غير مسلكه، فيجوز ألا يترجح عنده أحد السببين بمصير الصحابي إليه. هذا محل احتمال. والظاهر عندي على هذا الأصل الترجيح بمذهب الصحابي. ولعل الشافعي رحمه الله إنما بنى الترجيح على ذلك، وهو الظن به.
وما ذكره الإمام من تلك الرتب، أمور مستغنى عنها عندنا. وبيانه: أن','4','481',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2873,'تلك الأخبار تدل على مزية العلم، وتمام الفهم والمعرفة بأحكام الشرع. وهذا عندنا يثبت للصحابة على العموم، بالإضافة إلى من بعدهم من الناس.
والصواب أنه متى لم يظهر للمجتهد دليل الترجيح في أحد الدليلين، وصادف قول صاحبٍ على وفق أحدهما أن يرجِّح به. نعم، تلك الشهادات يظهر أثرها إذا اختلف الصحابة فيما بينهم، واستوت عند المجتهد مآخذهم، فقد ترجَّح بعض المآخذ على بعض بهذه الشهادة، وإذا رُجِّح بالشهادة، فقد قدمنا قبل هذا أنه إذا تحققت المعارضة، كان الخاص مقدمًا على العام. وهذا (٢٠٧/ ب) من هذا القبيل.
وما ذكره الشافعي من الترجيح يرجع إلى هذا [التقدير] من طلب خصوص الشهادة وعمومها. فإن الشهادة لعلي أعم من الشهادة لزيد، إذ الحلال والحرام يكون في المواريث وغيرها، والشهادة لزيد في الفرائض أخص الشهادات، فإذا عارض قول علي قول معاذ في الحلال والحرام، قدِّم قول معاذ، وإذا عارض قول معاذ في الفرائض قول زيد، قدم قول زيد فيها. وإذا عارض قول معاذ قول علي في القضاء في غير الفرائض، قدم قول علي. وإذا عارض قول معاذ في الحلال والحرام قول زيد في غير الفرائض، قدم قول معاذ. فعلى هذا [التقدير] تجري هذه المراتب. والله تعالى الموفق بعونه وحسن توفيقه وتأييده.
ثم قال: (فإن قيل: فإذا اعتضد مذهب أبي بكر وعمر) إلى قوله (كقول','4','482',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2874,'زيد في الفرائض). فنقول: وهذا كله قد قدمناه فلا نعيده.

ثم قال: <span data-type="title" id=toc-508>(مسألة: إذا تعارضت علتان وإحداهما مستندة إلى أصل مجمع عليه) </span>إلى آخرها. فنقول: الترجيح واقع في مثل ذلك، فلا إشكال، والسبب فيه أن الأصل الشاهد للعلة إذا علم ثبوته، فقد قويت شهادته. وإذا كان مظنون الثبوت، فشهادته مرتبة على ثبوته، وثبوته مظنون. وما يتطرق','4','483',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2875,'إليه الظن من وجهين، فهو أضعف مما إذا كانت إحدى الجهتين معلومة. ولكن هذا بشرط تساوي العلتين في الدلالة، وإلا فلا يبعد أن يكون أحد المعنيين أقوى في نفسه، بأن يكون من قبيل الضرورية أو الحاجية، فيكون أصله مختلفًا فيه، ولكنه ثابت بخبر واحد نص، أو مستند إلى عموم، والمعنى الآخر ضعيف من أبواب المناسب الإقناعي، ولكنه مستنبط من أصل مجمع عليه، فلا بُعد في','4','484',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2876,'أن يترجح المعنى القوي على المعنى الضعيف. فهذا تفصيلٌ لابد منه في هذا المكان.
ثم قال: (وإذا [تعارضت] علتان وإحداهما ذات وصف واحد) إلى قوله (ويعود الكلام إلى القول في تعليل الحكم بعلتين). فنقول: أما القول بالترجيح بكثرة الفروع، فقد تقدم الكلام عليه، وحققنا القول فيه. وأما كون الاجتهاد يقل، فهذا باطل، فإنه لابد للمجتهد من النظر في الوصف الذي يجوز اعتماده، والذي لا يجوز، فإن أسقط معتبرًا كان غالطًا، وإن اعتبر ساقطًا، فكذلك أيضًا.','4','485',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2877,'وأما المصير إلى أن ذات الوصف [الواحد] أكثر فروعًا مطلقًا، فغلط، وقد تكون ذات الوصف قاصرة، لا فرع (٢٠٨/ أ) لها، وتكون ذات الوصفين متعدية. وهذا كالتعليل بالنقدية، فهي قاصرة. وعلل أبو حنيفة بالوصف والجنسية، وإن كانت علته مركبة متعدية.
وأما قول الإمام - رضي الله عنه -: إن العلتين إذا استنبطتا من أصل واحد، وإحداهما ذات وصف والأخرى ذات وصفين، فإذ ذاك تكون ذات الوصف أكثر فروعًا. فليس الأمر كذلك أيضًا مطلقًا، بل يفتقر إلى أن يقال: إذا كان أحد','4','486',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2878,'الوصفين في العلة المركبة هو العلة المستقلة عند الآخر، فإذ ذاك تكون ذات الوصف أكثر فروعًا. ووجهه أن من يرى الوصف الواحد علة يرتب الحكم عليه متى وجده، فالآخر ينتظر التكميل، فالأول يحكم في محل لا يحكم الآخر فيه، فتكثر فروعه على هذا الرأي.
ثم قال: (ونحن نقول وقد انتهى الكلام إلى هذا الحد) إلى قوله (وإلا فلا','4','487',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2879,'ريب أن الشافعي رأى في القديم الاقتصار على الطعم فاسدًا). فنقول: صوَّر الإمام المسألة فيما إذا كان أحد الوصفين في العلة المركبة هو العلة الأخرى، فقال: إذا اقتصر أحد الفريقين على الوصف الواحد، فلينظر إلى الوصف الثاني، فإن استقل الأول دونه، فالثاني فضلةٌ مستغنىً عنها، لا أثر لها، فيجب الإعراض عنه. وإن تعذر الاقتصار على الوصف الأول، وجب ضمُّ الثاني إليه، ولا يكون هذا من مواقع الترجيح، فإن الترجيح إنما يكون بعد ثبوت الاستقلال لكل علة. فلا يصلح هذا التمثيل.
نعم، يصلح للمثال ما إذا استنبط أحدهما وصفًا على حياله، وصلح لإضافة الحكم إليه، واستنبط الآخر وصفين سواه يصلحان للتعليل بهما على حكم الترتيب. فهذا موضع نظر، ولا يلزم على هذا [التقدير] أن يكون','4','488',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2880,'الوصف الواحد أكثر فروعًا، فتخرج المسألة عن ذوقها، نظرًا من أصحابنا إلى ترتيب الترجيح على كثرة الفروع، لما بيناه من عدم اللزوم.
وأما ما مثَّلوه من قول الشافعي - رضي الله عنه - في القديم بضم [النقدية] إلى الطعم. وقوله الجديد بالإعراض عن [النقدية]، فالأمر على ما قاله الإمام من إبطال الترجيح، لأنه رأى في القديم الاقتصار على الطعم باطلًا، لا باعتبار ترجيح، ورأى في الجديد وجوب الاقتصار عليه، وأنه لا يجوز ضم التقدير إليه بكل علة في زمان يعرض صاحبها عن الأخرى. فليس هذا من مواقع الترجيح بحال.
وأما ما يتعلق بقلة الاجتهاد وكثرته، فقد تكلمنا عليه، ونقحنا القول فيه.

ثم قال: <span data-type="title" id=toc-509>(مسألة: إذا تضمنت إحدى العلتين نفيًا والأخرى إثباتًا) </span>إلى','4','489',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2881,'آخرها. فنقول: أما إذا صرنا إلى (٢٠٨/ ب) ما يقوله الإمام من أن نفي الحكم حكم، فالأمر واضح في أنه لا ترجَّح واحدة من العلتين على الأخرى. وإن بنينا على ما نختاره من أن نفي الحكم ليس بحكم، فها هنا موضع احتمال، إذ يقال إن نفي الحكم ليس قضية شرعية حتى يستند إلى علة شرعية، فكيف تكون العلة','4','490',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2882,'مقتضية غير حكم؟ وكأنه قال في التحقيق يرجع إلى أنها غير مقتضية. فهذا أمر ينبغي أن ينتبه له ويتأنَّى فيه. والله تعالى الموفق للصواب.
ثم قال: (مسألة: إذا تقابلت علتان إحداهما حكم، والأخرى أمر ثابت','4','491',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2883,'محسوس) إلى آخرها. فنقول: التفصيل الذي ذكره الإمام هو الذي يرفع الإشكال، ويتبين منه للمحصِّل التحصيل. وإذا تحقق القطع من جهتين، فلا ترجيح بحال. وقد بينا أنه لا يصح أن يكون علمٌ أوضح من علم.
ثم قال: (مسألة: إذا كانت إحدى العلتين تعم الأحوال) إلى آخرها. فنقول: الذي ذكره الإمام رحمه الله ها هنا، صحيح. وهذا الذي قاله هؤلاء القوم:','4','492',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2884,'إن كونه منتفعًا به، لا يتناول [الجرو]، كلام ضعيف، والمراد أن يكون المبيع من جنس ما ينتفع به. وليس المعنى كونه منتفعًا به ناجزًا، ولكن كونه سببًا للانتفاع في بعض الأحوال، يناسب جواز البيع في الحال. وأكثر المبيعات لا يتأتى الانتفاع بها حالة بيعها، بل ينظر زمان الانتفاع بها. نعم،','4','493',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2885,'[المثال يصلح لهذا].

<span data-type="title" id=toc-511>مسألة: من قال العبد يملك لكونه آدميًّا</span>، إذا قوبلت علته بعلة من منع ملكه، نظرًا منه لكونه مالًا، فإن هذه العلة يمكن زوالها، فلا تعم جميع أحواله، بخلاف العلة الأولى. والصحيح أنه لا ينشأ من مثل هذا ترجيح، فإنه إنما يطلب تأثير العلة عند وجودها. أما إذا عدمت، فكيف يطلب حكمها؟ فليس استمرار تلك بالذي يوجب قوة فيها.
وأما قول الإمام: إن التعلق بالنجاسة في منع البيع واقع في أبواب الأشباه. فقد كنا قلنا إنه واقع في آخر رتبة من رتب المعاني، وتأثير الانتفاع في جواز الابتياع من أوضح المعاني.
ولكن قال الإمام: إنه منقوض إذا بنينا أنه ينتفع بها ويمتنع بيعها كأم الولد. ولكن الصحيح أن المنتفع به لا يمتنع بيعه إلا لمعارض. وقد قلنا إن تخلف الحكم لمعارض، لا يوجب بطلان العلة. هذا تمام ما ذكره الإمام في كتاب الترجيح. وبالله التوفيق، لا رب غيره، وهو حسبنا ونعم الوكيل (٢٠٩/ أ).

ثم قال الإمام: <span data-type="title" id=toc-512>([كتاب] النسخ) </span>إلى قوله (ثم نذكر بعد نجاز','4','494',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2886,'تفاوضهم ما هو الحق [المبين] عندنا). فنقول: النسخ في اللغة معناه: الرفع، كما ذكر، وقد يطلق بإزاء نسخ الكتاب، وهو مشترك بيتهما، والفرض ها هنا النسخ بمعنى الرفع، وليس المراد بمعنى نسخ الكتاب. ونحن','4','495',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2887,'نحرر العبارات في [حدِّه] على حسب اختلاف الناس في فهم معناه.
قال أبو حامد: [نحرر كلام القاضي الذي رأى النسخ بمعنى الرفع، وحدَّه] بأنه: الخطاب الدال على ارتفاع الحكم الثابت بالخطاب المتقدم على وجهٍ لولاه لكان ثابتًا به، مع تراخيه عنه.
قال: وإنما آثرنا لفظ «الخطاب» على لفظ النص، ليكون شاملًا للفظ والفحوى والمفهوم وكل دليل، فإنه يجوز النسخ بجميع ذلك.
وإنما قيدنا الحد بالخطاب «المتقدم»، لأن ابتداء إيجاب العبادات في الشرع مزيلٌ حكم العقل من براءة الذمة، فلا يسمى نسخًا، لأنه لم يُزِل حكمَ خطابٍ.','4','496',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2888,'وإنما قيدنا «بارتفاع الحكم»، ولم نخصص بارتفاع الأمر والنهي، ليعم جميع أنواع الحكم من الندب والإباحة والكراهة. فجميع ذلك قد ينسخ.
وإنما قلنا: «لولاه لكان الحكم ثابتًا»، لأن حقيقة النسخ الرفع، فلو لم يكن ثابتًا، لم يكن هذا رفعًا، لأنه إذا ورد أمرٌ بعبادة مؤقتة، وأمرٌ بعبادة أخرى بعد تصرُّم ذلك الوقت، لا يكون ذلك نسخًا. وإذا قال: {ثم أتموا الصيام إلى الليل}، ثم قال في الليل: لا تصوموا، لا يكون ذلك نسخًا، بل الرافع ما لا يرتفع الحكم لولاه.
وإنما قلنا: «مع تراخيه»، لأنه لو اتصل به، لكان بيانًا وإتمامًا لمعنى الكلام، وتقديرًا له بمدة أو شرط، وإنما يكون رافعًا إذا ورد بعد أن يكون ورد الحكم واستقر وورد الخطاب، بحيث يدوم لولا الناسخ. هذا حدُّه وتقييده، والتنبيه على سر التقييد. وهذا الذي قاله غير صحيح من حيث الجملة، وفيه ألفاظ مستغنى عنها.
وأما كونه غير صحيح، فإنه يختار أن النسخ يرجع إلى الرفع، وعليه أورد الحد، والذي ذكره ليس هو رفع الحكم، فإنه قال: النسخ هو الخطاب الدال، والخطاب الدال ليس هو الرفع، ولذلك قال: الدال على ارتفاع الحكم، فيصير الرفع يستند إلى الخطاب الدال، لأن الخطاب الدال هو الرفع. ففَهْمُ لفظ حدِّه يبطله.','4','497',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2889,'ولو أضربنا عن هذه المؤاخذة، فالعبارات التي ذكرها طويلة، فإنه لما قال: هو الخطاب الدال على ارتفاع الحكم الثابت بالخطاب المتقدم على وجه لولاه لكان ثابتًا، لا يحتاج مع هذا إلى أن يقول: «مع (٢٠٩/ ب) تراخيه عنه»، بل نفس قوله: «الدال على ارتفاع الحكم الثابت»، يتضمن أن يكون مؤخَّرًا عنه، فإنه لو اتصل به، لكان تمامًا وتقييدًا، لا رافعًا ناسخًا.
فالصواب إذًا على هذا المعنى أن يقال: النسخ هو: رفع الحكم الشرعي بخطاب. أما قولنا: هو رفع الحكم، فلأنا نختار ذلك في حد النسخ. وأما قولنا: الشرعي، فلأن رفع حكم براءة الذمة بإيجاب العبادات ابتداء، أو شرعية الأحكام المزيلة عن براءة الذمة، لا يكون نسخًا، ولذلك افتقرنا إلى أن نقول: رفع الحكم الشرعي بخطاب، احترازًا عما إذا ارتفع الحكم بأعذار من نوم أو غفلة أو نسيان، فإن الحكم يرتفع، ولا يكون نسخًا إذا لم يرتفع بخطاب.','4','498',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2890,'وأما الفقهاء فالحد هو الذي ذكره الإمام عنهم. واختلفت عبارات المعتزلة فيه، فقالوا ما ذكره الإمام عنهم، وربما أبدلوا لفظ الحكم وقالوا: على أن مثل الحكم الثابت زائل في المستقبل، وهذا هو الصحيح على أصولهم، لأن الحكم الأول لا يزول ولا يتبدل ولا يسقط، وإنما مِثْله هو الذي يتعرض لذلك، لأنه لو لم يرد الناسخ، لتجدد مثل الحكم الأول، فورود النسخ يمنع التجدد. وإنما صار المعتزلة إلى ذلك، لأن الحكم قول، والأقوال عندهم لا تبقى، فلابد من تجددها.
وأيضًا فإنهم وإن صاروا إلى بقاء الأقوال، إلا أنهم لا يصح عندهم النسخ بمعنى الرفع بخال، إذ الأمر عندهم يتبع الصفات، إما الذاتية أو التابعة للحدوث، وهي مستمرة ثابتة، لا تتبدل ولا تزول، فاستحال أن يرجع النسخ عندهم إلى الرفع.
ومذاهب الفقهاء قريبة من مذاهبهم، لا على مقتضى أصولهم، ولكن لأنهم لم يعقلوا الرفع لكلام الله تعالى، فرجعوا إلى ما يتعقل بالبيان، وإزالة ما كان المكلف يظن دوامه. فلفظ الإزالة تجوُّز، وإنما هو بيان محض.','4','499',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2891,'ثم قال: (قال القاضي: إذا كان النسخ يرجع إلى البيان) إلى قوله (فإنا نفتتح بعده سؤالًا موجهًا على القاضي ينكشف به وجه الحق). فنقول: إلزام القاضي صحيح، والذين يذهبون إلى أن النسخ يرجع إلى البيان أنكروا النسخ بمعنى الرفع لا شك فيه، ولو كانت الألفاظ متعرضة لِلَازِمه بطريق العموم، لما توقف أحدٌ على التخصيص، فكيف يرد النسخ إلى تخصيص الزمان مع كون اللفظ لم يتعرض له بحال؟ ولو تعرضت الألفاظ لِلَازِمه، لصح تخصيصها عند الجميع قطعًا. فكيف يكون التعرض الضمني يزيد ويربي على التعرض المصرح','4','500',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2892,'به؟ فهذا يدل دلالة واضحة (٢١٠/ أ) على أنه لم يسلك بالنسخ مسلك تخصيص الأزمنة بحال. وكذلك اشتراط الصحابة القطع في النسخ، وامتناعهم من نسخ القاطع بالمظنون، يدل دلالة قاطعة على أنهم لم يسلكوا مسلك البيان والتخصيص.
وما ذكره الإمام لا يصح أن يكون عذرًا، فإنهم لو اعترفوا بأن الأمر غير معلوم، والثبوت غير محقق، وإنما هو أمر مظنون أو ملتبس، فكيف كان يصح منهم اشتراط القطع في البيان مع أن الظواهر المصرح بها لا يشترط في بيانها قواطع؟ هذا لو فعل، لكان ظاهر التناقض، والصحابة - رضي الله عنهم - مبرؤون من ذلك، فيدل اشتراطهم القاطع في الرافع، على أنهم علموا ثبوت الحكم وطلبوا أن يكون القاطع لتخلفه قاطعًا. هذا هو الصحيح عندنا. وقد وجه على قول','4','501',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2893,'القاضي أن النسخ بمعنى الرفع أسئلة:
منها: ما ذكره الإمام، وهو أنه قال: (إذا أثبت الله حكمًا على المكلفين، فمعناه تعلق قوله الأزلي [به] في حق المكلف) إلى قوله (والعبارة عن هذا المقصود: أن النسخ هو اللفظ الدال على ظهور انتفاء شرط دوام الحكم الأول).
السؤال الثاني: أن الرفع لا حقيقة له، فإنه إما أن يرد على ثابت أو على منتف، والثابت لا سبيل إلى رفعه، والمنتفي لا حاجة إلى رفعه.','4','502',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2894,'[السؤال] الثالث: كيف يتصور الرفع في الكلام القديم، وهو لا يقبل التغيير.
الرابع: إن الذي أمر به لابد أن يريد وجوده، فلو نهى عنه، للزم أن يكون مريدًا عدمه، فيكون الشيء الواحد في الوقت الواحد، مرادَ الوجود والعدم جميعًا.','4','503',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2895,'الخامس: أنه إذا أمر به اقتضى ذلك حسنه، فلو نهى عنه، لاقتضى ذلك قبحه، فيكون حسنًا قبيحًا.
السادس: أنه يدل على البَدّاء وانكشاف ما لم يكن منكشفًا. هذه شبه الذاهبين إلى استحالة النسخ بمعنى الرفع، وأغمضها ما تمسك به الإمام.
فالجواب عنه، وهو الأول، أنا نقول: المعنى بثبوت الحكم على التأبيد، أنا نريد به أنه تعلق بفعل العبد ما دام حيًّا مثلًا، وكذلك ثبت الحكم الذي يرد النسخ عليه، ولا يلزم من تعلق الحكم بصوم سنة مثلًا، دوام هذا التكليف، بل يصح أن يبقى مستمرًا، ويصح رفعه بالنسخ.
ومثاله: أنه إذا قال: وكلتك ببيع داري غدًا، فهو يعلم التوكيل في الحال، ولا يخالجه في ذلك شك بحال. ولو حلف على ذلك، لكان بارًا، ولو حلف على نقيضه، لكان حانثًا. وإذا قيل له هل تستمر وكيلًا حتى تفعل؟ لم يعلم ذلك، وجوز عزله أو (٢١٠/ ب) موته، ففرْقٌ بين علم الثبوت وعلم الدوام، وهما قضيتان متعددتان. ولذلك أنا نعلم ثبوت الباري سبحانه وصفاته، ونعلم ثبوت الجواهر والأعراض، ثم نقطع باستمرار شيء من هذه الموجودات، وهو','4','504',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2896,'الباري سبحانه. ويجوز نفي شيء منها، وهي الجواهر، وتخيل بقاء الأعراض، فلم يلزم من علم الثبوت علم الدوام بحال.
وإنما وجه الإمام هذا الكلام من حيث أنه لم يقف على الفرق بين الثبوت والدوام. وهذا هو سر المسألة وغاية مقصودها. فمن وُفِّق لدركه، فقد أحاط بحقيقة المسألة.
وجميع ما أورده إنما أورده على اتحاد الجهة. وألزم القاضي أن الله تعالى إذا علم دوام الحكم، كيف يتصور مع ذلك ارتفاعه في حق الجميع؟ هذا محال لا ريب فيه، ولو كان الأمر على ما فهم وتخيل، لكان مستحيلًا كما زعم.
ولكنا نقول: علم الله تعالى أنه خاطب الخلق بصوم عشر سنين مثلًا، وقد علم أن هذا التكليف لا يستمر، فلم يمنع ذلك من ثبوت التكليف، ثم الحكم بقطعه ورفعه عن المكلفين. فهكذا ينبغي أن يفهم حقيقة هذه المسألة. ولا','4','505',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2897,'يبقى بعد هذا التقرير في المسألة غموض.
وأما السؤال الثاني: وهو عدم عقلية الرفع، بأنا لسنا نعني بالرفع إزالةً وتبديلًا، ونقلًا من موضع إلى موضع، فإن ذلك غير معقول في المعاني لاسيما في الكلام القديم، ولكنا نعني بذلك أن العبد كان مكلفًا بصوم يوم الخميس مثلًا، فلما ورد النسخ لم يبق مكلفًا به، ويصير هذا بمثابة العزل الطارئ على الوكيل، فإنه قبل العزل كان وكيلًا، ولما عُزل انقطع التوكيل، وكذلك النسخ. وهذا بعينه هو الجواب عن قولهم: كيف [يرفع] القديم؟ وكيف يقبل التبديل؟ ولو كان تبديل الأحكام مستحيلًا باعتبار الكلام القديم، لكان إثبات الأحكام ابتداءً يستحيل، إذ القديم لا يقبل التبديل، وليس المراد [بتجديد]','4','506',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2898,'الأحكام بغير القديم، وإنما المراد بذلك صيرورة العبد مكلفًا بعد أن لم يكن مكلفًا، كصيرورته مراد الوجود بعد أن كان مراد العدم.
فهذه العبارات واردة على المتعلِّقات لا على المتعلَّقات. وكذلك القول في البصر والسمع، فالصفات كلها تجري مجرى واحدًا. وقد ساعدت المعتزلة على صيرورة العبد مقدورًا بعد أن لم يكن مقدورًا، مع كونهم يقولون إن الله تعالى قادر لنفسه أزلًا.
وأما ما يتعلق بالمصلحة والمفسدة والإرادة والكراهية، فهذه أصول المعتزلة، وقد (٢١١/ أ) أشرنا فيما تقدم إلى إبطال جميعها.
وأما قولهم: إن ذلك يفضي إلى البداء، فغلطٌ، والإمام يتكلم عليه فيما يعد. ويبقى للقوم سؤال واحد، وهو جدير أن يجعل سؤالًا شائعًا، وهو أنهم قالوا: هذا يفضي إلى أن يكون الشيء الواحد على الوجه الواحد مأمورًا به منهيًا عنه، فإن الله تعالى عندكم آمر في الأزل، وكذلك هو ناهٍ في الأزل، فيكون قائلًا في الأزل: صوم عاشوراء مثلًا واجب، صوم عاشوراء غير واجب. وهذا كلام متناقض، وجمعٌ بين الضدين. وهذا السؤال أصعب مما وجه الإمام،','4','507',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2899,'وفي الجواب عنه طرق:
أحدها- أن من أصحابنا من يقول: لا يكون الكلام أمرًا ونهيًا إلا بعد توجهه على المخاطب، وإذا كان كذلك، فنحن نقول: نشترط في النسخ التراخي، فلابد من تقديم زمان الورود، فلا يكون أمرًا نهيًا في وقت واحد، بل في وقتين. وهذا القول ضعيف عندنا، والله تعالى آمر في الأزل عند المحققين.
وقد أكثر الأصوليون في الجواب على هذا السؤال بأجوبة تطول. حاصلها راجع إلى [تحايلهم] على إبداء تعدد الجهات، حتى لا يكون آمرًا على الوجه الذي كان ناهيًا.
والصحيح عندنا خلاف ما ذهبوا إليه. وقد تبين من مذهبنا جواز تكليف ما لا يطاق عقلًا، وإنما منعنا بعض أنواعه سمعًا. والذي منعناه من جهة السمع أصناف، حاصلها راجع إلى أمور لا يتصور المكلف بها قصد الطاعة، ونخرج عن المطالب المعروفة من جهة العادة.
فعلى هذا لو سمع المكلف الأمر والنهي معًا، لم يتصور منه فعل إقدام ولا إحجام، وإذا سمعها في وقتين، جرى الأمر على ما ألف من التكليف عند العقلاء. وقد كنا قدمنا في مسألة الأمر بالشرط ما يرشد إلى هذا الكلام، فإن','4','508',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2900,'تعليق الفعل على الشرط الذي يعلم المكلف انتفاءه، ويصح إذا لم يكن المكلف عالمًا بذلك، لتصور قصد الإقدام والإحجام. فهذا هو الجواب، والله المستعان.
ثم قال: (فإن قيل: لا فرق بين هذا الاختيار ومذهب الفقهاء) إلى قوله (مع [بناء] الأمر على امتناع تكليف ما لا يطاق). فنقول: هذا الكلام الذي ذكره هو الذي قدمنا تحقيقه، وبينا غفلة الإمام عن سر المسألة.','4','509',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2901,'وأما ما ذكره ها هنا من تقدير شرط لا يتضمنه الكلام الأول، فهذا لا يصح على الإطلاق، ولو أضمرت الشروط، لاستحال أن يعلم مدلول','4','510',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2902,'النصوص، ولا يتصور أن يعلم صدق صادق، ولا كذب كاذب، ولا أفضى عقدٌ إلى اللزوم. وأهل اللغة لا يجوِّزون إضمار ما يمنع دلالة (٢١١/ ب) الألفاظ على الإطلاقـ فكيف يصح هذا، وهو قد أنكر إضمار الاستثناء، وأنكر ما أضيف إلى ابن عباس من جواز فصله، وتمسكه بمثل ما تمسكنا به؟ وقد جعل منكرو فصل الاستثناء مسألة الشرط أصلًا.','4','511',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2903,'ثم قال: (منعت اليهود النسخ) إلى آخر المسألة. فنقول: الأمر على ما','4','512',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2904,'ذكره الإمام في أدلة [الجواز] من جهة العقل، وفي إثبات الوقوع، نظرًا إلى قيام المعجزات، وإخبار النبي بخلاف ما أثبته النبي الأول من الأحكام. وإذا','4','513',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2905,'ثبت الوقوع، لزم ثبوت الجواز.
ولكن هذا الكلام كله عندي إنما يصح على القول بأن النسخ رفع. أما إذا رجع النسخ إلى البيان لمقتضى ألفاظٍ ظاهرة، فلا يتصور أن يذهب ذو عقل إلى','4','514',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2906,'استحالة ذلك. وكيف ينكر أن تشتمل شريعة ثابتة على أمر لم تتعرض له الشريعة الأولى؟ فهذا الذي نصب الإمام الدليل عليه، [والاشتغال] بإثبات جوازه، لا يترتب على قاعدته بحال.

ثم قال: <span data-type="title" id=toc-514>(مسألة مترجمة: بالنسخ قبل الفعل) </span>إلى قوله (حتى يؤجروا على صدق نياتهم ويوفوا ما لا يستقلون به في علم الله تعالى). فنقول: أما خلل الترجمة فبيِّن، لأن قولهم: هل يجوز النسخ قبل الفعل؟ يفهم منه أنه يصح النسخ بعد الفعل، وهذا غير صحيح، ولا نسخ أبدًا إلا قبل الفعل، سواء قيل إن النسخ رفعٌ أو بيان. فإذا ورد [أمر] بعبادة، فهل يجوز النسخ قبل دخول','4','515',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2907,'وقت تلك العبادة؟ منعت المعتزلة ذلك، وبنوا مذهبهم على ما حققناه من أن الحكم الثابت لا يرفع بحال، للجهات التي تقدم ذكرها. فإذًا إنما يكون النسخ مانعًا مثل الحكم الثابت من الورود. هذا مستند القوم في الاستحالة. وإذا بينا أن الرفع للحكم الثابت معقول، بطل ما أسندوا إليه الاستحالة.
وأما الإمام فقد ظهر من كلامه تناقض في هذه المسألة، فإنه قال في أولها: إن النسخ راجع إلى إظهار انتفاء شرط بقاء الحكم، فإن الحكم الموجه','4','516',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2908,'مشروط بأن لا ينسخ، فإذا ثبت النسخ قبل انقضاء زمان يمتنع الفعل، بانَ أنه لا حكم أصلًا. فأول الكلام يقتضي أن النسخ يمنع بقاء الحكم، وهذا هو الصحيح، وآخره يقتضي أن يتعين أنه لا حكم أصلًا. والمصير إلى نفي بقاء ما لا ثبوت له باطل، فإن النظر في البقاء ثبوتًا وعدمًا بشرط الاعتراف بالثبوت.
ثم قال: ويجوز أن يقع في أحكام الغيب أن يخاطبوا ويقبلوا ثم يرفع عنهم حكم التكليف. وهذا الذي صار إليه ها هنا من رفع الحكم هو الذي اخترناه (٢١٢/ أ)، فهو لا يتخلص من المصير إليه بحال.
وسبب الغلط عندي أنه لم يقدر على الفرق بين الثبوت والدوام، فلما لم يتحقق هذا عنده اضطربت المسألة عليه. ولو لم يكن في كتابنا إلا كشف هذه الدقيقة، لكان حريًّا أن يغتبط به، فلم أرَ لأحدٍ من الأصوليين تحريرًا لأمرٍ على هذا التحقيق. ولله الحمد على ما أنعم به، فإنه المنعم على من يشاء من','4','517',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2909,'عباده. وهو حسبنا ونعم الوكيل، ولا حول ولا قوة إلا بالله.
ثم قال: (ثم استدل أصحابنا في تجويز النسخ قبل الفعل) إلى قوله (فهذا منتهى [المراد] في ذلك). فنقول: الآية دالة قاطعة على صحة النسخ، وهو رفع الحكم قبل التمكن من الامتثال، فإن إبراهيم صلوات الله على نبينا وعليه كان مأمورًا بذبح ولده، ولم يقصِّر، ورفع ذلك عنه، ورُدَّ إلى الفداء. والقرآن بقيوده يحصِّل علمًا بذلك. لأن ولده فهم الأمر حيث قال: {افعل ما','4','518',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2910,'تؤمر} وسمَّاه الله بلاءً مبينًا، وشرع الفداء. فهذه القرائن والقيود تحصِّل علمًا بالمطلوب. وقد طاشت عقول المعتزلة لهذه الآية، وتخبطوا في الجواب، وطلبوا الخلاص من وجوه:
أحدها- أنهم قالوا: كان ذلك منامًا لا وحيًا.
الثاني- قالوا: كان مأمورًا، ولكن بالتلِّ للجبين وإمرار السكين.
الثالث- إنه كان مأمورًا بالذبح، ولكن قَلَبَ الله عنقه حديدًا، ففات التكليف، للاستحالة الحاصلة.
الرابع- إنه كان يذبح ويلتحم.
الخامس- إنهم قالوا: توهم إبراهيم لأمر، ولم يكن مأمورًا. وهذه الوجوه كلها باطلة.','4','519',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2911,'أما الأول: وكونه منامًا، فمنام الأنبياء وحي، وكيف كان إبراهيم يُقدِم على ذبح ولده لمنامٍ لا أصل له، وقال ولده: {افعل ما تؤمر}؟
وأما الأمر بالشد والتل للجبين، فقد أجاب الإمام عنه.
وأما كونه انقلب عنقه حديدًا، فباطل من وجهين:
أحدهما- أن ذلك لو كان صحيحًا، لكان ذلك أظهر، وأحق منقول، وأعظم معجزة.
الثاني- أن هذا لا يصح من المعتزلة، لأنهم ينكرون الأمر بالشرط من العالِم بعواقب الأمور.
وأما كونه كان يذبح ويندمل، فباطل من وجهين:','4','520',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2912,'أحدهما- أن هذا لو كان صحيحًا، لتواتر نقله، فإنه من الخوارق الظاهرة.
الثاني- أن هذا لا يسمى ذبحًا، ولا هو معقول، إذ الذبح إنما هو إفراء الأوداج.
وأما إذا كان كلما قطع جزءًا التأم، فلا يكون ذلك ذبحًا.
وأما نسبتهم إبراهيم إلى أنه فهم الأمر غالطًا فيه، فهذا كفرٌ صراحٌ، ونسبة الأنبياء إلى الغلط في أحكام الله تعالى. وما المانع (٢١٢/ ب) أن يكون كل ما فهمه عن ربه من هذا القبيل؟ ولا حاجة إلى اعتناءٍ بأكثر من هذا، فالرد على [الولعة يحسِّنها].
ومصير الإمام إلى الاعتراف بأن إبراهيم - عليه السلام - كان مأمورًا بالذبح ولم يقصِّر فيه، ثم منع منه بعد ذلك، اعتراف بأن النسخ يرجع إلى الرفع دون ما قرره قبل ذلك. وهذا أمر مقطوع به، لا يتصور أن ينفصل عنه أبدًا.','4','521',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2913,'ثم قال: <span data-type="title" id=toc-515>(مسألة: قطع الشافعي رحم الله جوابه لأن الكتاب لا ينسخ بالسنة) </span>إلى آخرها. فنقول: قد صار الشافعي إلى أنه لا ينسخ القرآن إلا بالقرآن، ولا السنة إلا بالسنة. وهذا القول غير صحيح، لأن الكل من عند الله، فما المانع منه؟ ولم يعتبر التجانس، وكل ما يقوله الرسول فمن الله - عز وجل -','4','522',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2914,'قال الله تعالى: {وما ينطق عن الهوى (٣) إن هو إلا وحيٌ يوحى}. والكلام معه إما في الجواز، وإما في الوقوع.
أما الجواز العقلي: فالشافعي رحمه الله أجل قدرًا وأعظم خطرًا من أن ينكره. فلا يبقى عندي لقوله وجهٌ إلا أن يقول: لم يثبت ذلك في الشريعة. بل المستقر أن السنة تنسخ بالسنة والقرآن بالقرآن. وقد [نوزع] في ذلك. وذكرت له آيات في القرآن ثبت ناسخها بالسنة، وسنة ثبت ناسخها في القرآن. إذ التوجه إلى بيت المقدس كان ثابتًا بالسنة، وناسخه في القرآن.
وأما قوله: إذا جاء القرآن بخلاف السنة، فالرسول يذكر عند ذلك سنة تخالف سنة، لكنها دارسة فلم تنقل. قيل: ما الذي يلجئ إلى هذه الضرورة، مع أنه مسوغ من جهة العقل؟ فلئن قال: فلأن الأكثر كان كذلك، فلا نسلِّم له بأن هذا هو الأكثر. قم لو سُلِّم، فلا يحصل العلم بذلك في التفصيل.','4','523',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2915,'والجواب عن الآية، وهو قوله [تعالى]: {ما ننسخ من آيةٍ أو نُنسِها}. أجاب الإمام عنه، ونزيد نحن جوابًا آخر: وهو أن القرآن لا يدل على أنه لا ينسخ القرآن إلا بالقرآن، بل يقتضي ظاهره أنه متى نسخت آية، فلابد من نزول آية أخرى. أما كون الثانية هي الناسخة للأولى، فلا يُتلقى ذلك من القرآن أصلًا.

ثم قال: <span data-type="title" id=toc-516>(مسألة مشهورة: بالزيادة على النص) </span>إلى قوله (ولكنا نضرب','4','524',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2916,'للتمثيل صورًا). فنقول: أما الكلام على أن الزيادة على النص، هل هي تنسخ أم لا؟ فقد قدمناه في باب المطلق والمقيد. ونبهنا على ما تخيله أبو حنيفة في تقييد الرقبة المطلقة بالإيمان هناك. والمتفق عليه أن إزالة الظواهر ليست نسخًا. وإذا ثبتت (٢١٣/ أ) أحكام متلقاة من نصوص مقطوع بها، ثم ورد بعد ذلك ما يمنع تعلقها ويقطع دوامها، وتصير دلالة النص على مدلوله، فهو رفعٌ على الحقيقة. وقد بينا أن النسخ يرجع إلى رفع الحكم بعد الثبوت على ما مر.','4','525',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2917,'وهذا أيضًا من الإمام اعتراف بأن النسخ يرجع إلى الرفع، فإنه قال: إن كان الكلام ظاهرًا، فبيانه لا يكون نسخًا، وإنما يكون نسخًا على تقدير أن يكون الأول اقتضى الآخر اقتضاءً مقطوعًا به. فكيف يتصور مع القطع [بالإجزاء] أن يتبين آخرًا أنه لم يكن [إجزاءً] بحال؟ وهل هذا إلا تغيير دلالات النصوص بعد القطع بها؟ وهو راجع إلى خلاف معلوم، وهو محال. فإذًا لابد للإمام في كل مسألة من أن يتعرض لكون النسخ رفعًا، وإن كان يأباه في اختياره.
ثم قال: ([وإذا تحقق أن النسخ إنما يكون واردًا على النصوص]) إلى قوله (ونحن لا ننكر الندب إلى بيِّنة كاملة مغنية عن الحلف). فنقول: أما الكلام على آية الوضوء، والرقبة المطلقة في كفارة الظهار، فقد قدمنا بيانه،','4','526',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2918,'وحققنا القول فيه، وأشرنا إلى مأخذ أبي حنيفة في إبداء الوصف، وكونه لا يشعر به باللفظ. فإن التأويل: هو إبداء ما يحتمله اللفظ. إلى آخره، فلا نعيده.
وأما الآية المشتملة على ذكر البيِّنات، فالقرآن لم يصرح برد الشاهد واليمين، ولكنه ذكر بينتين: إحداهما رجلان، والأخرى رجل وامرأتان، وليس في ذكر حجة أو حجتين ما يمنع من ورود الشرع بزائد على ذلك. وقد قدمنا أن النسخ يرجع إلى رفع الحكم بعد ثبوته، والآية لم تتضمن منع الحكم بالشاهد واليمين، فكيف يكون ورود الشرع بذلك نسخًا؟
قالوا: اقتضت الآية منع ذلك. قلنا: من طريق المنظوم، أو من جهة المفهوم؟ أما المنظوم فمفقود، وأما المفهوم فأصحاب أبي حنيفة لا يقولون به، وهو اختيارنا على ما سبق. وإن صرنا إليه، فرفع المفهوم كتخصيص العموم، وليس كرفع مقتضى اللفظ.
فإن قيل: عُلِّق المفهوم على الشرط، وهو قوي في الدلالة. قلنا: لا فرق عندنا بين الشرط وغيره، فإن قيل به، فهو ظاهر يزال بحكم التأويل، والقرآن أيضًا لم يتعرض للحكم بالشاهد والنكول على تقدير رد المشهود له اليمين على','4','527',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2919,'المشهود عليه. وقد ذكر الإمام وجهًا للحصر في أثناء كلامه حيث قال: وجه الندب إلى ذكر (٢١٣/ ب) الإرشاد إلى بينة كاملة مغنية عن الحلف.
ثم قال: (ومنها قوله تعالى {الزانية والزاني}) إلى آخر المسألة. فنقول: قدمنا القول في حقيقة التغريب فيما سبق، وبيننا أنه لا يتضمن ذكر عقوبة نفي أخرى على الحقيقة، فلا يكون إثباتها نسخًا بحال.

ثم قال: <span data-type="title" id=toc-517>(مسألة: أجمع العلماء على أن الثابت قطعًا لا ينسخه مظنون) </span>إلى قوله (وهذا الذي ذكرناه في الخبر يطرد في القياس أيضًا). فنقول: هذا','4','528',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2920,'الذي ذكره الإمام هو قول جميع الأصوليين، وقد خالف في ذلك شذوذ. وقالوا: ما جاز التخصيص به، جاز النسخ به، ولكنهم مع هذا الإطلاق اعترفوا بأن نص القرآن لا ينسخ بخبر الواحد، فهذا تبيين فساد الإطلاق. فقد جوَّزه قوم في زمان الرسول - صلى الله عليه وسلم - ومنعوه بعده. وهذا اختيار أبي حامد.
واحتج بقضية أهل مسجد قباء، فإنهم انتقلوا عن القبلة بخبر واحدٍ أخبرهم، ولكنه اعترف بالإجماع بعده. وهذا الذي قاله ضعيف، فإنه إذا تبيَّن خلاف في المسألة، ثم رجعت إحدى الطائفتين إلى قول الأخرى، فقد قدمنا في ذلك كلامًا كثيرًا.
ثم استدلاله بقضية أهل مسجد قباء على الجواز، يفتقر فيه إلى معرفة من كان فيه، وهل كانوا علماء ليكون مصيرهم قولًا معتبرًا أم لا؟ فالوجه التمسك','4','529',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2921,'بالإجماع الكائن، ولا التفات إلى مثل هذا.
ثم قال - رضي الله عنه -: (يجوز نسخ رسم آية من القرآن في التلاوة) إلى قوله (وأما غير القرآن فلا يرد عليه النسخ أصلًا). فنقول: هذا الكلام واضح، وذلك أن الأمر بالتلاوة حكم، وانعقاد الصلاة بالفاتحة مثلًا [حكم] آخر، وما يفهم منها من تحريم أو حل حكم آخر، ولا ارتباط لبعض الأحكام ببعض، فيجوز ورود النسخ على جميعها وعلى بعضها. هذا حكم الجواز من حيث العقل.
وأما الوقوع فآيٌ كثيرة تتلى وأحكامها منتفية، وأحكام ثابتة ذكر النقلة أنها تُلُقيت من القرآن ثم نسخت التلاوة. وقال عمر - رضي الله عنه -: «كان فيما أنزل الله: «الشيخ والشيخة إذا زنيا فارجموهما البتة نكالًا من الله». فالحكم ثابت','4','530',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2922,'والتلاوة ممنوعة.
ثم قال: (إذا ثبت النسخ ولم يبلغ خبره قومًا) إلى آخر المسألة.','4','531',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2923,'فنقول: الخلاف في المسألة مشهور، وهو مبني على أن كل مجتهد مصيب أو المصيب واحد؟ أما من صوَّب كل مجتهد، فيقول كما قال (٢١٤/ أ) الإمام. وأما من قال المصيب واحد، والحكم متعين، فليس لله في المسألة إلا الحكم الناسخ، ولكن المكلف معذور، لعدم العثور من غير تفريط. فإذا بلغه ذلك، فإن كان الوقت قائمًا، لزمه العمل بمقتضى ذلك، إن كان طَلَبَ، وإن انقضى وقت العمل، افتقر القضاء إلى أمر مجدَّد. هذا تحقيق المسألة. والله أعلم.

ثم قال: <span data-type="title" id=toc-518>(مسألة: [يجوز] نسخ الحكم من غير بدل عنه) </span>إلى آخرها. فنقول: المعتزلة منعت ذلك بناءً على أصولها. وتمسكوا أيضًا بقوله تعالى: {ما ننسخ من آية أو ننسِها نأتِ بخيرٍ منها أو مثلها}. قالوا: فهذا يتضمن إثبات البدل.','4','532',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2924,'أما تمسكهم من جهة العقل، فقالوا: إذا كان في الفعل مصلحة، فلو نسخ إلى غير بدل، لفاتت المصلحة، والبناء على المصالح قد أبطلناه. ولو سلمنا، فيجوز أن يعلم الله تعالى صلاح العباد في رفع الحكم عنهم، وردِّهم إلى ما كانوا عليه قبل ورود الشرع.
ثم كيف يستقيم للمعتزلة ذلك، والنسخ عندهم تبيين انتهاء مدة التكليف؟ فإذا انتهت مدة التكليف بالفعل الذي فيه الصلاح، فأي حاجة إلى فعل آخر يكون فيه مصلحة بدلًا عن الأولى، وقد نفدت الأولى؟

ثم قال: <span data-type="title" id=toc-519>(مسألة: إذا ورد نص واستنبط منه قياس ثم نسخ النص) </span>إلى آخرها. فنقول: هذا عندي ينبني على ما سبق من أن حكم الأصل، هل','4','533',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2925,'يضاف إلى العلة أم لا؟ ومذهب أبي حنيفة أن حكم الأصل لا يضاف إلى العلة، والمضاف إلى العلة حكم الفرع. وإذا كان كذلك، فالذي نسخ، وهو حكم الأصل، لا تعلق له بالعلة، ولا يتعرض لها، والمضاف إلى العلة هو الفرع، ولم يتعرض له. وقد بينا فيما تقدم فساد هذا الكلام.
وأما ما ذكره الإمام من أنه [يبقى] معنى لا أصل، قريب من قول أصحاب أبي حنيفة، وهو أيضًا فاسد، لأن المعنى المرسل إنما يعتمد - عند من يراه - لظنه أن الشارع لو حكم، [لحكم] على وفقه، فإذا حكم على نقيضه، أو قطع الحكم عن أن يكون مضافًا إليه، كيف يصح أن يكون استدلالًا؟

ثم قال: <span data-type="title" id=toc-520>(فصل - في الفرق بين النسخ والتخصيص) </span>إلى آخره. فنقول: قد قدمنا أن النسخ: رفع الحكم الشرعي بعد ثبوته، والتخصيص: بيان المراد باللفظ الموضوع للعموم ظاهرًا، وهما يشتركان في أن المكلف لا يبقى على','4','534',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2926,'ما كان يفهمه من اللفظ، لكن ينقطع عنه مقتضى اللفظ في النسخ، ويزول عنه الظاهر في التخصيص. فهذا هو حقيقة الفرق بينهما. ثم يقتضي ذلك أمورًا يفترق (٢١٤/ ب) فيها النسخ والتخصيص:
منها- أن التخصيص لا يرد على النصوص، ولا ورود للنسخ إلا على النص، إما بالوضع، وإما بالقرينة، إذ هو رفع، وإنما يكون الرفع بعد الثبوت.
الثاني- أن التخصيص لا يرد على الأمر بمأمور واحد على جهة واحدة، والنسخ يصح وروده عليه.','4','535',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2927,'الثالث- أن النسخ يشترط تراخيه، والتخصيص لا يشترط ذلك فيه، بل شرط قوم اقترانه. وإنما كان كذلك، لأن التخصيص بيان ودليل، والأدلة يجوز تقدمها وتأخرها واقترانها.
الرابع- أن التخصيص يصح بسائر الأدلة من العقل والسمع وقرائن الحال والمقال، والنسخ لا يكون إلا بدليل سمعي، إذ لا يتصور رفع الحكم بمعنى النسخ بشيء من تلك الأمور.','4','536',NULL,NULL,'0');
INSERT INTO [b13791] VALUES (2928,'الخامس- أنه يشترط ألا ينسخ القاطع إلا بقاطع، ويجوز تخصيص القرآن والسنة المتواترة بخبر الواحد والقياس المظنون. هذا تمام ما أورده الإمام في كتاب البرهان.
فرغ من نسخ هذا النصف الثاني من شرح كتاب البرهان صبيحة يوم السبت لثلاث عشرة ليلة خلت من شعبان المكرم سنة ثمان وعشرين وستمائة. والحمد لله على إحسانه وصلواته على سيدنا محمد خاتم أنبيائه وسلامه.
** ** **','4','537',NULL,NULL,'0');
