INSERT INTO [b13846] VALUES (22,'(أ)

ابن أبي إياس [١٣٢ - ٢٢٠ هـ / ٧٤٩ - ٨٣٥ م]

<span data-type="title" id=toc-4>آدم بن أبي اياس عبد الرحمن بن محمد، أبو الحسن، الخراساني العسقلاني:</span>
محدث، مفسر، كان مشهورا بالسنة شديد التمسك بها والحض على اعتقادها. أصله من خراسان، ونشأ ببغداد وأخذ عن علمائها، ثم رحل إلى الكوفة والبصرة والحجاز والشام ولقي الشيوخ وسمع منهم. ثم استوطن عسقلان - بفلسطين - فنسب اليها وتوفي بها. قال الخطيب البغدادي: حدث عن شعبة والليث بن سعد، وروى عنه الأئمة الأعلام من المحدثين كالبخاري وأبي حاتم الرازي وأبي زرعة الدمشقي، وغيرهم. وقال الصفدي: "اتفقوا على صدقه وثقته وزهده وورعه". "احتفظ لنا البخاري ب ٢٨١ اقتباسا أخذها من كتبه". من آثاره " تفسير','1','7',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (23,'القرآن" قال صاحب كشف الظنون: جمع فيه أقوال الصحابة والتابعين .. " (١).

الجريري [... - ١٤١ هـ / ... - ٧٥٨ م]

<span data-type="title" id=toc-5>أبان بن تغلب بن رباح، البكري الجريري بالولاء، أبو سعيد، ويقال أبو أميمة:</span>
مقرىء جليل، مفسر، نحوي، لغوي، محدث، من أهل الكوفة. كان جده رباح مولى لجرير بن عباد البكري (من بكر ابن وائل) فنسب اليه. قال ابن عدي وغيره: غال في التشيع، وقال الجوزاني:
_________
(١) تاريخ بغداد ٧: ٢٧ والوافي ٥: ٢٩٧ والجرح والتعديل ١/ ١: ٢٦٨ وهدية العارفين ١: ١ وكشف الظنون ١: ٤٣٠ والشذرات ٢: ٤٧ والتاريخ الكبير للبخاري ١/ ٢: ٣٩ وتذكرة الحفاظ ٤٠٩ وتاريخ التراث العربي ١: ٢٨٣ وتهذيب التهذيب ١: ١٩٦ والرجال للقيسراني ٣٩ واللباب ٢: ٣٣٩.','1','7',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (24,'زائغ مذموم المذهب، ووثقه أحمد وابن معين وأبو حاتم .. ". خرج له مسلم والأربعة. من آثاره " معاني القرآن " و " غريب القرآن"، ولعله أول من صنف في هذا الموضوع (١).

اللقاني [... - ١٠٤١ هـ / ... ١٦٣١ م]

<span data-type="title" id=toc-6>إبراهيم بن إبراهيم بن حسن بن علي اللقاني، أبو الإمداد، أبو إسحاق، برهان الدين:</span>
فقيه مالكي، عالم بالحديث وأصوله، متصوف، نسبته إلى لقانة من البحيرة بمصر. توفي وهو راجع من الحج ودفن بقرب العقبة. له كتب كثيرة، منها "تفسير القرآن" (٢).

بصيلة [.. - ١٣٥٢ هـ / .. - ١٩٣٣ م]

<span data-type="title" id=toc-7>إبراهيم بن إبراهيم الجناجي الملقب ببصيلة:</span>
فقيه مالكي، مفسر، منطقي،
_________
(١) العبر وفيات ١٤١ والشذرات ١: ٢١٠ وغاية النهاية ١: ١ وأعيان الشيعة ٥: ٤٧ وفهرست الطوسي ١٧ وهدية العارفين ١: ١ وكشف الظنون ١٢٠٧ ومنهج المقال ١٤ وتنقيح المقال ١: ٣ ومنتهى المقال ١٧ والخلاصة للخزرجي ١٣ والأعلام ١: ٢٠ ومعجم المؤلفين ١: ١.
(٢) هدية العارفين ١: ٣٠ وخلاصة الأثر ١: ٦ واليواقيت الثمينة ١: ٨٥ وفهرس الفهارس ١: ٩٠ وهو =','1','8',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (25,'نحوي، من أهل قرية جناج - من أعمال جرجا بمصر - له كتب منها "الكنز الجليل" حاشية على تفسير النسفي، مخطوط (١).

الرياضي [٢٢٣ - ٢٩٨ هـ / ٨٣٨ - ٩١٠ م]

<span data-type="title" id=toc-8>إبراهيم بن أحمد الشيباني، أبو اليسر، المعروف بالرياضي:</span>
أديب، كاتب، نحوي، له مشاركة في عدة علوم، من أهل بغداد، سمع بها من جلة المحدثين والفقهاء والنحويين، وجال في البلاد من خراسان إلى الأندلس، واستقر بالقيروان واستكتبه أمير إفريقية إبراهيم بن أحمد بن الأغلب ثم ابنه أبو العباس عبد الله. ثم كان على بيت الحكمة في أيام زيادة الله بن عبد الله آخر ملوك الأغالبة. لقي الجاحظ والمبرد وثعلبا وابن قتيبة، ولقي من الشعراء أبا تمام والبحتري ودعبلا وابن الجهم. وهو الذي أدخل إلى إفريقية رسائل المحدثين وأشعارهم وطرائف أخبارهم. توفي بالقيروان. له كتب منها " سراج الهدى" في معاني القرآن ومشاكله وإعرابه (٢).
_________
= فيه "إبراهيم بن حسن بن علي" والأعلام ١: ٢١ ومعجم المؤلفين ١: ٢.
(١) فهرس الأزهرية.
(٢) نفح الطيب ٣: ١٣٤ وهدية العارفين ١: ٤ وإيضاح المكنون ٢: ٩ ومعجم المؤلفين ١: ٥ ومعجم المصنفين ٣: ٦٥ والتكملة لابن الأبار ١: ١٦٣ والأعلام ١: ٢٢ ومنهج المقال ١٧ وسفينة البحار ١: ٨.','1','8',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (26,'البكري [.. - ٣٩٩ هـ / .. - ١٠٠٩ م]

<span data-type="title" id=toc-9>إبراهيم بن أحمد بن علي بن أسلم الجبنياني البكري، من بكر بن وائل، أبو إسحاق:</span>
فقيه مالكي، كان من أعلم الناس باختلاف العلماء، يحسن تفسير القرآن وإعرابه وناسخه ومنسوخه. مولده ووفاته في جنبيانة بافريقية، وأخباره كثيرة ذكر بعضها القاضي عياض في "ترتيب المدارك" (١).

السلماسي [٤٣٣ - ٤٩٦ هـ / ١٠٤١ - ١٠٩٣ م]

<span data-type="title" id=toc-10>إبراهيم بن أحمد بن محمد بن أحمد السلماسي، أبو طاهر:</span>
مفسر، محدث، واعظ، كان علامة في علم الأدب ومعرفة الأسانيد والمتون. ولد ب"سلماس" مدينة بآذربيجان، ومات ب" خوي" على مرحلة منها (٢).

الرقي [٦٤٧ - ٧٠٣ هـ / ١٢٤٩ - ١٣٠٣ م م]

<span data-type="title" id=toc-11>إبراهيم بن أحمد بن محمد بن معالي (الرقي، أبو إسحاق)</span>
_________
(١) ترتيب المدارك ٤: ٤٩٧ - ٥١٦ وطبقات المفسرين للداودي ١: ١ وفيه وفاته سنة ٣٦٩ هـ ومثله في الديباج المذهب ٨٦. ورحلة التجاني ٨٠ وما بعدها.
(٢) طبقات المفسرين للسيوطي ٣ وطبقات المفسرين للداودي ١: ٣.','1','9',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (27,'الرقي، أبو إسحاق: فقيه حنبلي، زاهد، عني بتفسير القرآن، وتقدم في علم الطب، وشارك في علوم الإسلام. سمع منه البرزالي والذهبي وغيرهما. ولد بالرقة، واستقر بدمشق إلى أن توفي. قال الصفدي: له "تفسير الفاتحة" في مجلد. وقال ابن رجب: صنف "تفسيرا للقرآن" ولا أعلم هل أكمله أم لا. (١).

الجزري [.. - ٧٠٩ هـ / .. - ١٣٠٩ م]

<span data-type="title" id=toc-12>إبراهيم بن أحمد بن محمد الأنصاري الخزرجي الجزري، أبو إسحاق:</span>
نحوي، من فقهاء المالكية، له مشاركة في كثير من العلوم. أخذ علماء إفريقية عنه العربية والبيان والأصلين والجدل والمنطق. من تصانيفه "إيجاز البرهان في إعجاز القرآن" (٢).
_________
(١) الوافي ٥: ٣١٣ والدرر الكامنة ١: ١٤ وذيل طبقات الحنابلة ٢: ٣٤٩ والشذرات ٦: ٧ والبداية والنهاية ١٤: ٢٩ وهدية العارفين ١: ١٣ وكشف الظنون ١: ٤٥٦ ومعجم المؤلفين ١: ٩ وطبقات المفسرين للداودي ١: ٣ والأعلام ١: ٢٣
(٢) بغية الوعاة ١: ٤٠٦ وهدية العارفين ١: ١٨ والديباج المذهب ٩١ ومعجم المصنفين ٣: ٦٠ وعنه أخذنا تاريخ وفقه. وكشف الظنون ٢٠٥ و ١٨٨٣ ووفاته فيه سنة ٦٧٥ ومعجم المؤلفين ١: ٨.','1','9',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (28,'فقيه اليشبكية [.. - ٩٣٣ هـ / .. - ١٥٢٧ م]

<span data-type="title" id=toc-13>إبراهيم بن أحمد بن يعقوب الكردي القصيري، برهان الدين:</span>
عارف في التفسير والفقه والقراءات والمنطق والفرائض والحساب. ولد في قرية - عادة - من القصير من أعمال حلب، وسكن حلب صغيرا وتعلم بها وبدمشق والقاهرة. قال في الشذرات: وعرف بفقيه اليشبكية بحلب لتأديبه الأطفال بها. وأثنى عليه الغزي وابن الشماع (١).

ابن الملا [.. - بعد ١٠٣٠ هـ / .. - بعد ١٦٢١ م]

<span data-type="title" id=toc-14>إبراهيم بن أحمد بن محمد بن علي بن أحمد، المعروف بابن الملا:</span>
عالم بالتفسير وأصول الفقه، والأدب والنحو وغير ذلك. أصله من حصن كيفا (في ديار بكر) ومولده ووفاته بحلب. من تآليفه "ملح البيان في تفسير القرآن" (٢).

السنوسي [.. - ١٣٠٤ هـ / .. - ١٨٨٧ م]

<span data-type="title" id=toc-15>إبراهيم بن إدريس الحسني السنوسي:</span>
_________
(١) شذرات الذهب ٨: ١٩٢ والكواكب السائرة.
(٢) أعلام النبلاء ٦: ٢١١ ومعجم المصنفين ٣: ٤٥ وهدية العارفين ١: ٣٠ وخلاصة الأثر ٢: ١١ والكشاف ٣٠١ والأعلام ١: ٢٤ ومعجم المؤلفين ١: ٦','1','10',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (29,'فقيه مالكي، عارف بالحديث والتفسير، ولد بفاس، ثم انتقل إلى الإسكندرية، ومنها إلى القاهرة، وتوفي بها. شرع في "تفسير القرآن" ولم يكمله (١).

الحربي [١٩٨ - ١٨٥ هـ / ٨١٥ - ٨٩٨ م]

<span data-type="title" id=toc-16>إبراهيم بن إسحاق بن بشير بن عبد الله البغدادي الحربي، أبو إسحاق:</span>
من أعلام المحدثين، أصله من مرو، واشتهر وتوفي ببغداد، ونسبته إلى محلة فيها. كان إماما في العلم، عارفا بالفقه حافظا للحديث مميزا لعلله، قيما بالأدب، زاهدا، أرسل إليه المعتضد ألف دينار فردها. سمع أبا نعيم وهوذة بن خليفة ومسددا، وطبقتهم ... وتفقه على الإمام أحمد، فكان من جلة أصحابه. قال الدارقطني: كان يقاس بأحمد بن حنبل في زهده وعلمه وورعه .. ". صنف كتبا كثيرة منها "ناسخ القرآن ومنسوخه" (٢).
_________
(١) هدية العارفين ١: ٤٤ ومعجم المؤلفين ١: ١١
(٢) فوات الوفيات ١: ١٤ وطبقات الشافعية ٢: ٢٦ والوافي ٥: ٣٢٠ وطبقات المفسرين للداودي ١: ٥ والنسخ في القرآن الكريم ١: ٣٢١ والمنتظم ٦: ٣ وتاريخ بغداد ٦: ٢٧ وطبقات الحنابلة ١: ٨٦ - ومعجم الأدباء ١: ٢١٢ وبغية الوعاة ١: ٤٠٨ وصفة الصفوة ٢: ٢٢٨ وتذكرة الحفاظ ٥٨٤ وابن النديم ٢٣١ ومرآة الجنان ٢: ٢٠٩ وإنباه الرواة ١: ١٥٥ وشذرات =','1','10',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (30,'الأنماطي [.. - ٣٠٣ هـ / .. - ٩١٥ م]

<span data-type="title" id=toc-17>إبراهيم بن إسحاق النيسابوري الأنماطي، أبو إسحاق:</span>
حافظ للحديث، من كبار الرحالين في طلبه. قال الذهبي: كان الإمام أحمد ينبسط في منزله ويفطر عنده. نسبته إلى بيع الأنماط وهي الفرش التي تبسط، له (تفسير كبير" (١).

الطليطلي [.. - ٣٨٢ هـ / .. - ٩٩٦ م]

<span data-type="title" id=toc-18>إبراهيم بن إسحاق الأموي الطليطلي، أبو إسحاق، المعروف بابن أبي زرد:</span>
فقيه مالكي، محدث، مفسر، من أهل طليطلة، رحل إلى المشرق وسمع جماعة من العلماء. ذكره الداودي وقال: كان فاضلا خيرا عابدا حافظا للتفسير، شهد جنازة النسائي العابد بالقيروان، وحدث" (٢).
_________
= الذهب ٢: ١٩٠ ونزهة الالباء٣١٣ والعبر ٢: ٧٤ والأعلام ١: ٢٤ ومعجم المؤلفين ١: ١٢.
(١) طبقات المفسرين ١: ٥ وتذكرة الحفاظ ٧٠١ والشذرات ٢: ٢٤٢ والعبر ٢: ١٢٥ وهدية العارفين ١: ٥ وكشف الظنون ٤٤٣ ومعجم المصنفين ٣: ٧١ والأعلام ١: ٢٥ واللباب ١: ٧٣.
(٢) طبقات المفسرين للداودي ١: ٥ والصلة ١:','1','11',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (31,'النبيسي [.. - ٩١٥ هـ / .. - ١٥٠٩ م]

<span data-type="title" id=toc-19>إبراهيم بن حسن النبيسي، برهان الدين:</span>
نحوي، صوفي، مفسر، من أهل نبيس، من قرى حلب، وأصله من الشيشر في بلاد العجم. قتله بعض الخوارج في أرزنجان له "تفسير القرآن" وصل فيه إلى سورة يوسف (١).

ابن العمادي [بعد ٨٨٠ - ٩٥٤ هـ / بعد ١٤٧٥ - ١٥٤٧ م]

<span data-type="title" id=toc-20>إبراهيم بن حسن بن عبد الرحمن بن محمد الحلبي، الشهير بابن العمادي، برهان الدين:</span>
عالم بالتفسير، والحديث وعلومه، والفقه وأصوله، والعربية، والقراءات، من أهل حلب، انتهت اليه رياسة الشافعية بها. أخذ عن شيوخ القاهرة ومكة وغزة. وأفتى ودرس. أثنى عليه صاحب شذرات الذهب (٢).

الكوراني [١٠٢٥ - ١١٠١ هـ / ١٦١٦ - ١٦٩٠ م]

<span data-type="title" id=toc-21>إبراهيم بن حسن بن شهاب الدين (الشهرزوري الكوراني، برهان الدين، أبو العرفان، أبو إسحاق، أبو محمد، أبو الوقت)</span>
_________
(١) الشذرات ٨: ٦٨ والكواكب السائرة ١: ١١٠ وهدية العارفين ١: ٢٤ وهو فيه: إبراهيم بن الحسين. ومعجم المؤلفين ١: ٢٢ والشذرات ٨: ٦٨.
(٢) شذرات الذهب ٨.','1','11',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (32,'الشهرزوري الكوراني، برهان الدين، أبو العرفان، أبو إسحاق، أبو محمد، أبو الوقت: فقيه شافعي، محدث، بلغ رتبة الاجتهاد. ولد بشهران (من أعمال شهرزور) بجبال الكرد، ورحل في طلب الحديث فسمع بالشام ومصر والحجاز، وسكن المدينة المنورة وتوفي بها. وكان مع علمه بالعربية يجيد الفارسية والتركية. قيل إن كتبه تنيف على ثمانين، منها "تفسير القرآن" وسماه صاحب هدية العارفين "الإلمام بتجريد قولي سعدي وعصام" (١).

ابن مرتيل [.. - ٢٤٩ هـ / .. - ٨٦٤ م]

<span data-type="title" id=toc-22>إبراهيم بن حسين بن خالد بن مرتيل، أبو إسحاق القرطبي:</span>
فقيه مالكي، عالم بالتفسير، من أهل قرطبة. رحل إلى المشرق وأخذ عن جماعة. وعاد إلى بلده فولي أحكام الشرطة بها، وكان صلبا في حكمه عدلا. له "تفسير القرآن" (٢).
_________
(١) سلك الدرر ١: ٥ والدر الطالع ١: ١١ وهدية العارفين ١: ٣٥ وإيضاح المكنون ١: ١٢٢ ومشاهير الكرد ١: ٦٢ وتحفة الإخوان ٢٧ وفهرس الفهارس ١: ٢٢٩ ورحلة العياشي ١: ٣٢٠ والأعلام ١: ٢٨ ومعجم المؤلفين ١: ٢١ والمجددون في الإسلام ٤٠٧
(٢) هدية العارفين ١: ٣ وطبقات المفسرين ١: ٦ وبغية الملتمس ٢٠١ والديباج ٨٤ وترتيب المدارك ٣: ١٣٦ وجذوة المقتبس ١٤٥ ومعجم المصنفين ٣: ١١٠ ومعجم المؤلفين ١: ٢٣.','1','12',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (33,'الهمذاني [.. - ١٠٢٦ هـ / .. - ١٦١٧ م]

<span data-type="title" id=toc-23>إبراهيم بن حسين الحسني الحسيني الهمذاني:</span>
مفسر، فقيه إمامي، عالم بالكلام والإلهيات، من أهل همذان، ولي قضاءها بعد أبيه ولم يشتغل به. وكان حظيا عند الشاه عباس الأول. من كتبه "حاشية على الكشاف" للزمخشري في التفسير (١).

تاتار شيخ إبراهيم [.. - ١٠٠١ هـ / .. - ١٥٩٣ م]

<span data-type="title" id=toc-24>إبراهيم بن حق محمد أفندي الدشتي ثم القرمي:</span>
متصوف، كان كثير الاشتغال بالتفسير، من أهل القرم بروسيا، هاجر إلى القسطنطينية وتوفي بها (٢).

الأدرنوي [.. - نحو ٩٧٠ هـ / .. نحو ١٥٦٢ م]

<span data-type="title" id=toc-25>إبراهيم بن حمزة بن مسعود، تاج الدين التيروي، الأدرنوي:</span>
واعظ رومي، مفسر، من أهل تيرة - في تركيا -. درس (سنة ٩٣٣ هـ) في جامع (نقطة جي) بأدرنة، ونسب اليها. ورحل إلى مصر، ثم هاجر إلى مكة مجاورا إلى أن توفي. من آثاره
_________
(١) أعيان الشيعة ٥: ١٥٢ وسلافة العصر ٤٨٨.
(٢) الاعلام ١: ٣٧.','1','12',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (34,'"جامع الأنوار ونزهة الابصار" مخطوط، في التفسير والمواعظ (١).

أبو ثور الكلبي [.. - ٢٤٠ هـ / .. - ٨٥٤ م]

<span data-type="title" id=toc-26>إبراهيم بن خالد بن أبي اليمان الكلبي البغدادي، أبو ثور:</span>
من كبار الفقهاء وصاحب الإمام الشافعي. قال ابن حبان: كان أحد أئمة الدنيا فقها وعلما وورعا وفضلا، صنف الكتب وفرع على السنن، وذب عنها. وقال الصفدي: "كان مبدأ اشتغاله بمذهب أهل الرأي حتى قدم الشافعي إلى العراق فاختلف إليه واتبعه ورفض مذهبه الأول". مات ببغداد. من كتبه "أحكام القرآن" (٢).

حقي الأرضرومي [.. - ١١٩٥ هـ / .. - ١٧٨١ م]

<span data-type="title" id=toc-27>إبراهيم بن درويش عثمان الحسني الأرضرومي، الشهير بحقي:</span>
صوفي، فلكي، تركي مستعرب، مشارك في عدة
_________
(١) هدية العارفين ١: ٢٧ وكشف الظنون ٥٣٧ ومعجم المصنفين ٣: ١٢٢ ومعجم المؤلفين ١: ٢٦ وذخار الأوقاف ١٣٦.
(٢) تاريخ بغداد ٦: ٦٥ والوافي ٥: ٣٤٤ وطبقات المفسرين للداودي ١: ٧ وتذكرة الحفاظ ٥١٢ وهدية العارفين ١: ٢ والعبر ١: ٤٣١ ووفيات الأعيان ١: ٢٦ وطبقات السبكي ١: ٢٢٧ والأعلام ١: ٣٠ ومرآة الجنان ٢: ١٣٠ والشذرات ٢: ٩٣.','1','13',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (35,'علوم. له تصانيف بالعربية والتركية والفارسية، منها كتابفي "التفسير" سماه البغدادي: "هينة إسلامية" (١).

ابن رجاء [.. - ١٦٣ هـ / .. - ٧٨٠ م]

<span data-type="title" id=toc-28>إبراهيم بن رجاء بن نوح:</span>
فقيه، مفسر، نحوي، شاعر، ذكره السيوطي نقلا عن تاريخ بلخ (٢).

الزجاج [٢٤١ - ٣١١ هـ / ٨٥٥ - ٩٢٩ م]

<span data-type="title" id=toc-29>إبراهيم بن السري بن سهل، أبو إسحاق الزجاج:</span>
نحوي، لغوي، مفسر، مولده ووفاته ببغداد. كان يخرط الزجاج، ثم مال إلى النحو فعلمه المبرد. وطلب عبيد الله بن سليمان الحارثي (وزير المعتمد ثم المعتضد) مؤدبا لابنه القاسم، فدله المبرد على الزجاج، فطلبه عبيد الله، فأدب له ابنه إلى أن ولي الوزارة مكان أبيه، فجعله القاسم من كتابه. فأصاب في أيامه ثروة كبيرة، وكانت للزجاج مناقشات مع ثعلب وغيره. من كتبه "معاني القرآن" (٣).
_________
(١) هدية العارفين ١: ٣٩ ومعجم المؤلفين ١: ٥٧.
(٢) بغية الوعاة ١: ٤١٠.
(٣) طبقات المفسرين للداودي ١: ٧ وانباء الرواة ١ =','1','13',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (36,'الرضي الرومي [٦٤٦ - ٧٣٢ هـ / ١٢٢١ - ١٣٣٢ م]

<span data-type="title" id=toc-30>إبراهيم بن سليمان، رضي الدين، أبو إسحاق الرومي، ثم الحموي، ويعرف بالآب كرمي:</span>
عالم بالتفسير والحديث والمنطق. نسبته إلى آب كرم من قرى قونية في تركيا. حج وجاور، ثم سكن دمشق، ودرس بها إلى أن مات. أثنى عليه جماعة من العلماء الأعلام (١).

الحيدري [١٢٣٥ - ١٢٩٩ هـ / ١٨٢٠ - ١٨٨١ م]

<span data-type="title" id=toc-31>إبراهيم بن صبغة الله بن محمد أسعد بن عبيد الله الحيدري، فصح الدين، ويقال له إبراهيم فصح:</span>
عالم بغدادي المولد
_________
=: ١٥٩ وبغية الوعاة ١: ٤١١ والعبر ٢: ١٤٨ وتاريخ بغداد ٦: ٨٩ وأخبار النحويين البصريين ١٠٨ ومعجم الأدباء ١: ١٣٠ وطبقات ابن قاضي شهبة ١: ١٦٥ وروضات الجنات ٤٤ وطبقات الزبيدي ٨١ والنجوم الزاهرة ٣: ٢٠٨ وشذرات الذهب ٢: ٢٥٩ ونزهة الالباء ٢٤٤ والمنتظم ٦: ١٧٦ ومفتاح السعادة ١: ٣٦٣ وابن النديم ٦٠ واللباب ١: ٣٩٧ ومرآة الجنان ٢: ٢٦٢ والبداية والنهاية ١١: ١٤٨ وتهذيب الأسماء واللغات ٢: ١٧٠ وتراجم الرجال ٣ ومعجم المصنفين ٤: ٣٥٥ والأعلام ١: ٤٠ ومعجم المؤلفين ١: ٣٣ وآداب زيدان ٢: ٢١٠ وهدية العارفين ١: ٣٣.
(١) المنهل الصافي ١: ٤٩ والفوائد البهية ٩ والجواهر المضية ١: ٣٩ وتاج التراجم ٣ ومعجم المصنفين ٣: ١٥١ ومعجم المؤلفين ١: ٢٥ وشذرات الذهب ٦: ٩٧ والدرر الكامنة ١: ٢٨ والبداية والنهاية.','1','14',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (37,'والمنشأ والوفاة، كردي الأصل، من بيت علم وفضل. رحل إلى الآستانة وحصلت له رتبة الحرمين الشريفن. تولى نيابة القضاء ببغداد. له تآليف، منها "فصح البيان في تفسير القرآن" (١).

الخراساني [.. - ١٦٨ هـ / .. - ٧٨٤ م]

<span data-type="title" id=toc-32>إبراهيم بن طهمان بن شعبة، أبو سعيد الخراساني:</span>
مفسر، من رجال الحديث، كان شيخ خراسان في وقته. ولد بهراة، واستوطن نيسابور، وقدم بغداد، ثم جاور بمكة إلى أن مات. قال الخطيب البغدادي: "هو من ثقات الأئمة وقد تفرد عن الثقات بأشياء معضلة، وروى له الجماعة". وتكلم فيه للإرجاء، ويقال: رجع عنه. من كتبه "تفسير القرآن" (٢).
_________
(١) الأعلام ١: ٣٧ وهدية العارفين ١: ٤٢ وإيضاح المكنون ٢: ١٩٥ وتاريخ العراق ٣: ٣٣١ ومجلة المقتبس ٨: ٣٣٤ وأعيان القرن الثالث عشر ٢٤٨ ومجلة لغة العرب ٣: ٣٤١ ومعجم المؤلفين ١: ٤٠ وحلية البشر ١.
(٢) تهذيب التهذيب ١: ١٢٩ والعبر ١: ١٤١ والوافي ٦: ٢٣ وميزان الاعتدال ١: ١٩ وتذكرة الحفاظ ١: ٢١٣ وتاريخ بغداد ٦: ١٠٥ وخلاصة تهذيب الكمال ١٨ وابن النديم ٢٨٤ وشذرات الذهب ١: ٢٥٧ وطبقات المفسرين ١: ١٠ وهدية العارفين ١: ١ وأعيان الشيعة ٥: ٢٧٦ ومعجم المصنفين ٣: ١٦٦ ومعجم المؤلفين ١: ٤١. وتاريخ البخاري ١/ ١: ٢٩٤ وتاريخ التراث ١: ٢٦٦٠ وميزان الاعتدال ١: ١٩.','1','14',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (38,'ابن جماعة [٧٢٥ - ٧٩٠ هـ / ١٣٢٥ - ١٣٨٨ م]

<span data-type="title" id=toc-33>إبراهيم بن عبد الرحيم بن محمد بن إبراهيم بن سعد الله بن جماعة، برهان الدين، أبو إسحاق الحموي الأصل ثم المقدسي:</span>
مفسر، قاض، من فقهاء الشافعية. ولد بمصر، وقدم دمشق صغيرا وأخذ عن علمائها. ولي خطابة القدس الشريف، ثم قضاء مصر "فباشر بنزاهة وعفة ومهابة وحرمة". ثم ولي قضاء الشام واستمر إلى أن توفي. قال ابن حجر: وإليه انتهت رياسة العلماء في زمانه، فلم يكن أحد يدانيه في سعة الصدر، وكثرة البذل، وقيام الحرمة، والصدع بالحق، وقمع أهل الفساد مع المشاركة الجيدة في العلوم، وجمع "تفسيرا" في عشر مجلدات، وقفت عليه بخطه وفيه غرائب وفوائد" (١).

الكجي [١٩٢ - ٢٩٢ هـ / ٨٠٨ - ٩٠٥ م]

<span data-type="title" id=toc-34>إبراهيم بن عبد الله بن مسلم الكجي، ويقال فيه الكشي، البصري، أبو مسلم:</span>
_________
(١) رفع الإصر: ١: ٢٩ والدرر الكامنة ١: ٣٩ وطبقات المفسرين ١: ١٢ وقضاة دمشق ١١٢ ونزهة النفوس والابدان ١: ١٧٩ والسلوك ٣/ ٢: ٥٨٦ والنجوم الزاهرة ١١: ٣١٤ والانس الجليل ٢: ٢٠٧ والشذرات ٦: ٣١١ وهدية العارفين ١: ١٨ ومعجم المصنفين ٣: ١٩٢ ومعجم المؤلفين ١: ٤٧ وإنباه الغمر١: ٣٥٥ وكشف الظنون ٤٣٧.','1','15',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (39,'حافظ للحديث، عالم بالتفسير، ويوسف بأنه "صاحب السنن". قال في الانساب: "بصري، وإنما قيل الكجي لأنه كان يبني دارا بالجص في البصرة فكان يقول: هاتوا الكج، وأكثر منه، فقيل له الكجي، وأظن إنما قيل له الكشي نسبة إلى جده الأعلى كش .. ". وفي معجم البلدان مادة "كج": قرية بخوزستان يقال لها زير كج، وأظن أن أبا مسلم ... منسوب اليها". وكرر ذلك في مادة "كش"، وزاد: ولا أرى لما ذكر عن نسبته إلى الكج (الجص) أصلا، ولو كان كذلك لما قيل إلا الكجي، بالجيم". وللبحتري قصيدة في مدحه، مات ببغداد، وحمل إلى البصرة. من كتبه "ناسخ القرآن ومنسوخه" (١).

الحكري [.. - ٧٨٠ هـ / ١٣٧٨ م]

<span data-type="title" id=toc-35>إبراهيم بن عبد الله بن علي بن يحيى بن خلف، برهان الدين الحكري:</span>
نحوي، مقرىء، مفسر، من أهل الحكرة بقرب
_________
(١) اللباب ٣: ٨٥ وتاريخ بغداد ٦: ١٢٠ والمشتبه ٢: ٥٣ ومختصر دول الإسلام ١: ١٣٩ والعبر ٢: ٩٢ ومرآة الجنان ٢: ٢٢٠ وتذكرة الحفاظ ٦٢٠ ومعجم البلدان مادة "كج" ثم "كش" وشذرات الذهب ٢: ٢١٠ والنسخ في القرآن الكريم ١: ٣٢٢ والاعلام ١: ٤٢ والانساب ٢/ ٤٧٥ ومعجم المصنفين ٣: ٢١٧ ومعجم المؤلفين ١: ٥٥ وتاريخ التراث العربي.','1','15',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (40,'الطائف. سكن مصر، وتولى القضاء بالمدينة، وناب في الحكم في الخليل والقدس، وتوفي بها. أثنى عليه السيوطي وغيره (١) -

القريمي [.. - ١٠٠١ هـ / .. - ١٥٩٣ م]

<span data-type="title" id=toc-36>إبراهيم بن عبد الله القريمي:</span>
شيخ زاوية الخلوتية قرب آيا صوفيا بالآستانة، له "الرسالة النورية في كشف الاسرار النارية" في تفسر آية النور (٢).

العجمي [.. - ٩٤٠ هـ / .. - ١٥٣٣ م]

<span data-type="title" id=toc-37>إبراهيم العجمي، الصوفي، نزيل مصر:</span>
مفسر، كان له يد في المعقولات، وعلم الكلام. أصله من قرى تبريز. قدم مصر وأقام بمدرسة بباب زويلة، قال في الشذرات: "كان يفسر القرآن العظيم، ويقرىء في رسائل القوم مدة طويلة، أخذ عنه خلق كثير من الأعجام والأروام، وتوفي بمصر" (٣).
_________
(١) بغية الوعاة ١: ٤١٥ وطبقات المفسرين للداودي ١: ١١ وحسن المحاضرة ١: ٥٠٩ والشذرات ٦: ١٥٨ وغاية النهاية ١: ١٧ والدرر الكامنة ١: ٣٠ ولحظ الالحاظ ١١٧.
(٢) هدية العارفين ١: ٢٨.
(٣) شذرات الذهب ٨: ٢٣٦ والكواكب السائرة ٢: ٨٤ ومعجم المؤلفين ١: ٥٨.','1','16',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (41,'الطبري [٤٩٢ - ٥٣٣ هـ / ١٠٩٩ - ١١٣٩ م]

<span data-type="title" id=toc-38>إبراهيم بن علي بن الحسين الشيباني الطبري، أبو إسحاق:</span>
مفسر، قاض، من فقهاء الشافعية، ولي قضاء مكة. قال السيوطي: "له تصانيف مفيدة" وقال السبكي: كان إماما في المذهب والفرائض والتفسير" (١).

ابن الفهاد [.. - ٧١٥ هـ / .. - ١٣١٥ م]

<span data-type="title" id=toc-39>إبراهيم بن علي بن عمر القوصي، برهان الدين، المعروف بابن الفهاد:</span>
فقيه شافعي، قاض، مفسر، أصولي، من أهل قوص بصعيد مصر. ولي قضاء دمامن. قال ابن حجر: مهر في التفسير والفقه، وكان مرضي السيرة. مات بقوص (٢).

الكفعمي [٨٤٠ - ٩٠٥ هـ / ١٤٣٦ - ١٥٠٠ م]

<span data-type="title" id=toc-40>إبراهيم بن علي بن الحسن بن محمد بن صالح الحارثي العاملي الكفعمي، تقي (الدين)</span>
_________
(١) طبقات المفسرين للسيوطي ٣ وطبقات المفسرين للداودي ١: ٤٤ وطبقات السبكي ٧: ٣٤ ومعجم المؤلفين ١: ٦٥ ومعجم المصنفين ٣: ٢٥٩.
(٢) الدرر الكامنة ١: ٤٧ والطالع السعيد ٦١ وطبقات المفسرين للداودي ١: ١٤.','1','16',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (42,'الدين: فقيه إمامي، مفسر، أديب، له نظم ونثر. مولده ووفاته في كفرعيما من ناحية الشقيف بجبل عامل. من كتبه "قراضة النضير" في التفسير (١).

السقا [١٢١٢ - ١٢٩٨ هـ / ١٧٩٨ - ١٨٨١ م]

<span data-type="title" id=toc-41>إبراهيم بن علي بن حسن السقا:</span>
خطيب، فقيه، من أهل القاهرة. تولى الخطابة بالجامع الأزهر نيفا وعشرين عاما. له "حاشية" على تفسير أبي السعود. لم يتمها، منها ستة أجزاء مخطوطة (٢).

البقاعي [٨٠٩ - ٨٨٥ هـ / ١٤٠٦ - ١٤٨٠ م]

<span data-type="title" id=toc-42>إبراهيم بن عمر بن حسن الرباط بن علي الخرباوي البقاعي، أبو الحسن، برهان الدين:</span>
مؤرخ، مفسر، محدث، أديب. ولد بقرية خربة روحا من عمل البقاع بلبنان، وبها نشأ وتعلم. وسكن دمشق، ودخل بيت المقدس والقاهرة، ومات
_________
(١) أعيان الشيعة ٥: ٣٣٦ وأمل الآمل ١: ٢٨ ومنتهى المقال ١: ٢٧ وروضات الجنات ١: ٧ والذريعة ٧: ١١٥ وهدية العارفين ١: ٢٤ والأعلام ١: ٤٧ ومعجم المؤلفين ١: ٦٥.
(٢) مرآة العصر: ١: ٢٣٣ وهدية العارفين ١: ٤٢ ومعجم المصنفين ٣: ٢٥٥ ومعجم المؤلفين ١: ٦٤ ومعجم المطبوعات ١٠٣٠ وفهرس الفهارس ٢: ٣٤٨ والأعلام ١: ٤٨.','1','17',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (43,'بدمشق. من كتبه "نظم الدرر في تناسب الآيات والسور" في التفسير، ويعرف بمناسبات البقاعي، أو "تفسير البقاعي"، مخطوط سبع مجلدات، أو "الفتح القدسي في تفسير آية الكرسي" و "مصاعد النظر للإشراف على مقاصد السور" مخطوط، في خزانة الرباط. قال صاحب كشف الظنون: "جمع فيه ما لم يحوه كتاب كالبحر العباب، وهو في مجلد صغير" (١).

بيوض [١٣١٦ - ١٤٠٠ هـ / ١٨٩٩ - ١٩٨٠ م]

<span data-type="title" id=toc-43>إبراهيم بن عمر بن بابة بن إبراهيم بن حمو، الملقب بيوض:</span>
عالم اباضي، مفسر، مجاهد، من أكابر العلماء المسلمين ورجال الإصلاح في وقته، من أهل القرارة بالجزائر، مولدا وإقامة ووفاة. اشتغل بالتعليم الديني مدة طويلة، وشارك في النهضة الإصلاحية، السياسية والدينية، التي مهدت لقيام الثورة الجزائرية ضد الاستعمار الفرنسي سنة ١٩٥٤. من آثاره "تفسير القرآن الكريم" اشتغل به تدريسا
_________
(١) الضوء اللامع ١: ١٠١ ونظم العقيان ٢٤ وكشف الظنون ٤٤٤ و ١٢٣٣ و ١٧٠٤ والبدر الطالع ١: ١٩ والتاج المكلل ٣٥٨ والأعلام ١: ٥٠ ومعجم المؤلفين ١: ٧١ ومعجم المصنفين ٣: ٢٧٧ وهدية العارفين ١: ٢١ والشذرات ٧: ٣٣٩ وتاريخ آداب اللغة العربية ٣: ٧١٢.','1','17',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (44,'زهاء خمسة وأربعين عاما (١٣٥٤ هـ ١٩٣٥ م - ١٤٠٠ هـ - ١٩٨٠ م) (١).

الزواوي [٧٩٦ - ٨٥٧ هـ / ١٣٩٤ - ١٤٥٣ م]

<span data-type="title" id=toc-44>إبراهيم بن فائد بن موسى بن عمر بن سعيد، أبو إسحاق الزواوي القسنطيني:</span>
فقيه مالكي، مفسر، مشارك في بعض العلوم، ولد في جبل جرجرة بالجزائر، وتعلم في بجاية وتونس وقسنطينة، وحج مرارا وجاور. واستقر في قسنطينة. من كتبه " تفسير القرآن" (٢).

الأصفهاني [.. - ١١٦٠ هـ / .. - ١٧٤٧ م]

<span data-type="title" id=toc-45>إبراهيم القاضي الاصفهاني:</span>
مفسر، محدث، قاض، من أهل أصفهان، مولدا ووفاة. له "تفسير" كبير (٣).
_________
(١) أوراق جزائرية، ونهضة الجزائر الحديثة ٣: ٦ وانظر فهرسته، وأعلام الإصلاح في الجزائر ١: ٨١ و ٢: ٧٧ و ٣: ٧٥ و ٤: ١٥.
(٢) معجم أعلام الجزائر ١٦٠: وشجرة النور ١: ٢٦٢ ودرة الحجال ١: ١٩٣ وتعريف الخلف ٢: ٥ ونيل الابتهاج ٥٢ وطبقات المفسرين ١: ١٥ والضوء اللامع ١١١٦ والاهلام ١: ٥١ والحلل السندسية ١/ ٢: ٧٤٣.
(٣) أعيان الشيعة ١٤: ٥٣٧ ومعجم المؤلفين ١: ٧٧.','1','18',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (45,'ابن لاجين [٦٧٣ - ٧٤٩ هـ / ١٢٧٤ - ١٣٤٨ م]

<span data-type="title" id=toc-46>إبراهيم بن لاجين بن عبد الله الرشيدي الأغري، برهان الدين:</span>
عالم بالنحو والتفسير والفقه والطب والقراءات، ولي تدريس التفسير بالقبة المنصورية بالقاهرة، وتوفي بها بالطاعون (١).

الثقفي [.. - ٢٨٣ هـ / .. - ٨٩٦ م]

<span data-type="title" id=toc-47>إبراهيم بن محمد بن سعيد بن هلال الثقفي:</span>
فقيه، من العلماء، كان يرى رأي الزيدية ثم انتقل إلى القول بالإمامية. من أهل الكوفة، سكن أصبهان فحدث ومات فيها. من كتبه "تفسير القرآن" (٢).

نفطويه [٢٤٤ - ٣٢٣ هـ / ٨٥٨ - ٩٣٥ م]

<span data-type="title" id=toc-48>إبراهيم بن محمد بن عرفة بن سليمان بن (المغيرة الأزدي العتكي، أبو عبد الله)</span>
_________
(١) بغية الوعاة ١: ٤٣٤ والشذرات ٦: ١٥٨ والدرر الكامنة ١: ٧٧ وأعيان العصر ٦٩ والسبكي ٦: ٨٣ وغاية النهاية ١: ٢٨ والنجوم الزاهرة، ١٠: ٤٤٣ والوافي ٦: ١٦٤ والمنهل الصافي ١: ١٧١ وطبقات الاسنوي.
(٢) تاريخ أصبهان ١: ١٨٧ ولسان الميزان ١: ١٥٢ ومعجم الأدباء ١: ٢٩٤ ومنهج المقال ٢٦ وفهرست الطوسي ١٦ وطبقات الداودي ١: ١٨ وروضات الجنات ٢ وأعيان الشيعة ٥: ٤١٨ وهدية العارفين ١: ٣ والأعلام ١: ٥٦.','1','18',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (46,'المغيرة الأزدي العتكي، أبو عبد الله: إمام في النحو، عالم باللغة والحديث، حافظ للقرآن وللسير وأيام الناس، وكان فقيها، رأسا في مذهب داود. ولد بواسط، وسكن بغداد، وأخذ عن ثعلب والمبرد وجالس الملوك والوزراء، وكان على جلالة قدره تغلب عليه سذاجة الملبس. وكان يؤيد مذهب سيبويه في النحو فلقبوه نفطويه. مات ببغداد. من كتبه "إعراب القرآن" و "غريب القرآن" و "أمثال القرآن" (١).

الطائي [.. - ٦٢٠ هـ / .. - ١٢٢٣ م]

<span data-type="title" id=toc-49>إبراهيم بن محمد بن إبراهيم بن عليب الطائي، أبو إسحاق:</span>
مفسر، مقرىء، من أهل قيطاجة بالأندلس، رحل إلى
_________
(١) انباء الرواة ١: ١٧٦ وبغية الوعاة ١: ٤٢٧ وتاريخ بغداد ٦: ١٥٩ وميزان الاعتدال ١: ٦٤ ولسان الميزان ١: ١٠٩ ونزهة الالباء٢٦٠ وغاية النهاية ١: ٢٥ والعبر ٢: ١٩٨ وابن النديم ٨١ ومرآة الجنان ٢: ٢٨٧ والمنتظم ٦: ٢٧٧ والنجوم الزاهرة ٣: ٢٤٩ وشذرات الذهب ٢: ٢٩٨ والبداية والنهاية ١١: ١٨٣ والاعلام ١: ٧٥ ومعجم المصنفين ٤: ٣٧٩ ومعجم المؤلفين ١: ١٠٢ وطبقات المفسرين للداودي ١: ١٩ ومعجم الأدباء١: ٢٥٤ والمختصر في أخبار البشر ٢: ٨٨ وأعيان الشيعة ٥: ٧٠٩ ووفيات الأعيان ١: ٤٧ وطبقات الزبيدي ١٧١ ونور القبس ٣٤٤ وهدية العارفين ١: ٥.','1','19',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (47,'المشرق، وحج صغيرا، وعاد إلى بلده فأقرأ بها. قال الداودي: "كان عارفا بالقراءات وبالعربية صالحا عالما دراية". من كتبه "مختصر" تفسير ابن عطية (١).

السفاقسي [٦٩٧ - ٧٤٢ هـ / ١٢٩٨ - ١٣٤٢ م]

<span data-type="title" id=toc-50>إبراهيم بن محمد بن إبراهيم بن أبي القاسم السفاقسي، أبو إسحاق، برهان الدين:</span>
مفسر، نحوي، من فقهاء المالكية، تفقه في بجاية، ورحل إلى المشرق فأخذ عن علماء مصر والشام. وأفتى ودرس سنين. من كتبه "المجيد في إعراب القرآن المجيد" ويسمى إعراب القرآن، قال في كشف الظنون: "ذكر فيه البحر لأبي حيان، وذكر أنه سلك سبيل المفسرين في الجمع بين التفسير والإعراب فتفرق فيه هذا المقصود، وصعب جمعه إلا بعد بذل الجهد، فجمعه ولخصه" (٢).
_________
(١) طبقات المفسرين للداودي ١: ١٤.
(٢) الوافي ٦: ١٣٨ والديباج ٩٢ ونيل الابتهاج ٣٩ وكشف الظنون ١٦٠٧ والنجوم الزاهرة: ١: ٩٨ وهدية العارفين ١: ١٥ وبغية الوعاة ١: ٤٢٥ والدرر الكامنة ١: ٥٧ وبروكلمان، الذيل ٢: ٣٥٠ ومعجم المؤلفين ١: ٨٢ والأعلام ١: ٦١ وشجرة النور ٢٠٩ وفيه أن كتاب المجيد له ولأخيه محمد.','1','19',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (48,'ابن ابي شريف [٨٣٦ - ٩٢٣ هـ / ١٤٣٣ - ١٥١٧ م]

<span data-type="title" id=toc-51>إبراهيم بن محمد بن أبي بكر بن علي بن مسعود المري المقدسي ثم القاهري، أبو إسحاق، برهان الدين المعروف بابن أبي شريف:</span>
قاض، مفسر، من فقهاء الشافعية، ولد ونشأ وتعلم في بيت المقدس، وأكمل دروسه بالقاهرة، وتصدر للإقراء والافتاء، ثم ولى قضاء مصر سنة ٩٠٦ هـ ولم يكمل السنة. ودرس التفسير في جامع ابن طولون، والفقه في المدرسة الحجازية. توفي بالقاهرة في أيام الخليفة المتوكل على الله العباسي فصلى عليه. من كتبه "تفسير سورة الكوثر" و "تفسير سورة الإخلاص" وكتاب في "الآيات التي فيها الناسخ والمنسوخ" (١).

الأسفراييني [٨٧٣ - ٩٤٥ هـ / ١٤٦٨ - ١٥٣٨ م]

<span data-type="title" id=toc-52>إبراهيم بن محمد بن عرب شاه الاسفراييني، عصام الدين:</span>
من علماء خراسان وما وراء النهر. كان أبوه قاضيا في أسفرايين وولد هو فيها وأخذ عن علمائها.
_________
(١) الكواكب السائرة ١: ١٠٢ وطبقات المفسرين للداودي ١: ١٦ والضوء اللامع ١: ١٣٤ ونظم العقيان ٢٦ والبدر الطالع ١: ٢٦ والشذرات ٨: ١١٨ ومعجم المصنفين ٤: ٤١٩ والاعلام ١: ٦٣ وهدية العارفين ١: ٢٥ ومعجم المؤلفين ١: ٨٨.','1','20',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (49,'زار سمرقند في أواخر عمره فتوفي بها. من كتبه، حاشية، على تفسير البيضاوي لسورة عم، مخطوطة في خزانة الرباط، و "حاشية" من أول سورة النبأ إلى آخر القرآن، أهداها إلى السلطان سليمان العثماني. قال آغا بزرك: نسختان منها في مكتبة مدرسة سبهسالار بطهران، و "حاشية" على تفسير البيضاوي، مخطوطة في الأزهر (١).

الحلبي [.. - ٩٥٦ هـ / .. - ١٥٤٩ م]

<span data-type="title" id=toc-53>إبراهيم بن محمد بن إبراهيم الحلبي:</span>
من كبار فقهاء الحنفية، كان إماما عالما بالعلوم العربية والتفسير والحديث والقراءات. من أهل حلب وبها نشأ وتعلم، وانتقل إلى مصر وأخذ عن علمائها، ثم استقر في القسطنطينية وولي الخطابة بجامع السلطان محمد ومدرسا بدار القراء. وكان مفتي الديار الرومية يعول عليه في مشكلات الفتاوى (٢).
_________
(١) الشذرات ٨: ٢٩١ وفيه: وفاته في حدود ٩٥١ عن ٧٢ عاما. وهدية العارفين ١: ٢٦ وفيه: وفاته سنة ٩٤٤ ومعجم المصنفين ٤: ٣٧٥ ومعجم المطبوعات ١٣٣٠ وكشف الظنون ١٩٠ وعنه أخذنا وفاته، ومعجم المؤلفين ١: ١٠١ والأعلام ١: ٦٦.
(٢) الشذرات ٨: ٣٠٨ والكواكب السائرة ٢: ٧٧ والتاج المكلل ٣٩١ وأعلام النبلاء ٥: ٥٦٩ والشقائق النعمانية ٢: ٢٤ ومعجم المصنفين ٤: ٣١٣ ومعجم المطبوعات ١٣ والأعلام ١: ٦٤ ومعجم المؤلفين ١: ٨٠.','1','20',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (50,'الشيرازي [.. - ١٠٧٠ هـ / .. - ١٦٦٠ م]

<span data-type="title" id=toc-54>إبراهيم بن محمد بن إبراهيم الشيرازي:</span>
مفسر، متكلم، من فقهاء الشيعة الإمامية. من أهل شيراز، وتوفي بها. له "العروة الوثقى" في تفسير القرآن (١).

الميموني [٩٩١ - ١٠٧٩ هـ / ١٥٨٣ - ١٦٦٨ م]

<span data-type="title" id=toc-55>إبراهيم بن محمد بن عيسى، أبو إسحاق، برهان الدين الميموني:</span>
عارف بالتفسير والحديث والعربية والعلوم العقلية والنقلية. نسبته إلى الميمون من صعيد مصر. مات بالقاهرة. من تصانيفه "حاشية" على تفسير البيضاوي، و "رسالة" في قوله تعالى: {وما خلقت الانس والجن إلا ليعبدون}. و "كشف الغشاء" عن تفسير قوله تعالى: {لله ملك السموات والأرض يخلق ما يشاء" (٢).
_________
(١) الذريعة ٦: ١٤١ وأعيان الشيعة ٥: ٣٩١.
(٢) خلاصة الأثر ١: ٤٥ وصفحة من انتشر ١٤٥ وهدية العارفين ١: ٣٢ والأعلام ١: ٦٤ ومعجم المصنفين ٤: ٣٨٧ وإيضاح المكنون ٢: ٣١١ وبروكلمان ٢: ٣٥٧ وكشف الظنون ٨٩٩.','1','21',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (51,'القمي [.. - ١١٦٨ هـ / .. - ١٧٥٥ م]

<span data-type="title" id=toc-56>إبراهيم بن محمد باقر بن محمد علي بن محمد مهدي القمي، الرضوي:</span>
عالم في التفسير والحديث والأصول والكلام، من فقهاء الشيعة الإمامية. نسبته إلى مدينة قم بإيران (١).

الأمير [١١٤١ - ١٢١٣ هـ / ١٧٢٩ - ١٧٩٩ م]

<span data-type="title" id=toc-57>إبراهيم بن محمد بن إسماعيل بن صلاح الحمزي الحسنى الهاشمي المعروف بالأمير:</span>
مفسر، من علماء الزيدية. من أهل صنعاء، رحل إلى مكة مرات ثم سكنها إلى أن توفي فيها. نعته صاحب نيل الوطر بعالم الدنيا وحافظها، وخطيب الأمة وواعظها. من كتبه "مفاتيح الرضوان في تفسير القرآن بالقرآن" ويسمى أيضا "فتح الرحمن ... " (٢).

القيصري [.. - ١٢٥٣ هـ / .. ١٨٣٧ م]

<span data-type="title" id=toc-58>إبراهيم بن محمد القيصري، الرومي، المعروف بكوزي بيوك زاده:</span>
مفسر، من
_________
(١) أعيان الشيعة ٥: ٣٩٦ ومعجم المؤلفين ١: ٨٧.
(٢) نيل الوطر ١: ٢٨ والبدر الطالع ١: ٤٢٢ وهدية العارفين ١: ٤٠ وإيضاح المكنون ٥٢٠ والأعلام ١: ٦٥ ومعجم المؤلفين ١: ٨٦.','1','21',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (52,'فقهاء الحنفية، من علماء الأتراك المستعربين، اشتغل بالتدريس. ذكره البغدادي في "هدية العارفين" وقال: له "تفسير جزء النبأ". وذكره في إيضاح المكنون وسماه إبراهيم بن عبد الله (١).

الجارم [١٢٠٢ - بعد ١٢٧١ هـ / ١٧٨٨ - بعد ١٨٥٣ م]

<span data-type="title" id=toc-59>إبراهيم بن محمد بن محمد بن أحمد بن عبد المحسن الحسني الإدريسي الشافعي، برهان الدين الجارم:</span>
نحوي، عارف بالتفسير، مولده ووفاته بثغر رشيد بمصر. له "حاشية على تفسير الجلالين" (٢).

اللكهنوئي [١٢٥٩ - ١٣٠٧ هـ / ١٨٤٣ - ١٨٩٠ م]

<span data-type="title" id=toc-60>إبراهيم بن محمد تقي بن حسين بن علي التقوي النصير آبادي اللكهنوئي:</span>
مفسر، من فقهاء الشيعة الإمامية. من أهل لكهنوء بالهند، ووفاته فيها. من تصانيفه "تكملة ينابيع الأنوار" في التفسير، لوالده (انظر
_________
(١) هدية العارفين ١: ٤١ وإيضاح المكنون ١: ٣٥٤ ومعجم المؤلفين ١: ٥٤ وهو فيه إبراهيم بن عبد الله، وصفحة ١٠٥ إبراهيم بن محمد. ومعجم المطبوعات ١٥٧٨ ودار الكتب ٦: ١٦٤ وما بعدها.
(٢) معجم المصنفين ٤: ٣٣١ وهدية العارفين ١: ٤١ ومعجم المؤلفين ١: ٩٠.','1','22',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (53,'ترجمته)، و "البضاعة المزجاة" في تفسير سورة يوسف (١).

البرغمه وي [.. - ١٠١٤ هـ / .. - ١٦٠٥ م]

<span data-type="title" id=toc-61>إبراهيم بن مصطفى البرغمه وي، الرومي، المعروف بلوح خوان:</span>
قاض، مفسر، من فقهاء الحنفية، متكلم، من أهل بلدة برغمه. اشتغل بالتدريس بالقسطنطينية وأدرنة ومغنيسيا، ثم ولي قضاء بورصة، قال المحبي: "له على التفسير رسائل وتعليقات كثيرة تدل على تبحره، فقد كان بحرا زاخرا عالما بالتفسير والحديث والكلام ... " (٢).

حنيف الرومي [.. - ١١٨٩ هـ / .. - ١٧٧٥ م]

<span data-type="title" id=toc-62>إبراهيم بن مصطفى، المعروف بحنيف الرومي:</span>
قاض، عالم بالتفسير، من فقهاء الحنفية، تركي الأصل. ولي القضاء ببلدة غلطة. من تصانيفه بالعربية "لطائف التفسي" والراسخ في المنسوخ والناسخ" (٣).
_________
(١) أعيان الشيعة ٥: ٤٠٠ ومعجم المؤلفين ١: ٨٩.
(٢) خلاصة الاثر ١: ٥١ وهدية العارفين ١: ٢٩ ومعجم المصنفين ٤: ٤٣٤ ومعجم المؤلفين ١:
(٣) هدية العارفين ١: ٣٩ وإيضاح المكنون ١: ٥٤٦ و ٢: ٤٠٣ ومعجم المؤلفين ١: ١١٣.','1','22',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (54,'ابن معقل [.. - ٢٩٥ هـ / .. - ٩٠٨ م]

<span data-type="title" id=toc-63>إبراهيم بن معقل بن الحجاج بن خراش النسفي، أبو إسحاق:</span>
محدث، مفسر، كان قاضي نسف وعالمها. سمع هشام بن عمار وطبقته، وحدث بصحيح البخاري عنه. قال المستغفري: كان فقيها حافظا بصيرا باختلاف العلماء .. ". من آثاره "تفسير القرآن" (١).

الفقال [١٠٢٨ - ١٠٩٨ هـ / ١٦١٩ - ١٦٨٧ م]

<span data-type="title" id=toc-64>إبراهيم بن منصور الفقال، الدمشقي:</span>
أديب، عارف بالتفسير، من أهل دمشق، كانت له حلقة بالجامع الأموي، ودرس التفسير وغيره في داره. أثنى عليه المحبي وقال: وله تحريرات على مواطن من التفسير (٢).
_________
(١) تذكرة الحفاظ ٦٨٦ والشذرات ٢: ٢١٨ ومرآة الجنان ٢: ٢٢٣ والعبر ٢: ١٠٠ ومعجم المصنفين ٤: ٤٣٥ وابن عساكر ٢: ٣٠٠ والأعلام ١: ٧٠ والوافي ٦: ١٤٩ وهدية العارفين ١: ٤ وكشف الظنون ٤٣٦ وطبقات المفسرين للداودي ١: ٢٢ والنجوم الزاهرة
(٢) خلاصة الاثر ١: ٥١ وهدية العارفين ١: ٣٤ ومعجم المصنفين ٤: ٤٤١.','1','23',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (55,'الشاطبي [.. - ٧٩٠ هـ / .. - ١٣٨٨ م]

<span data-type="title" id=toc-65>إبراهيم بن موسى بن محمد اللخمي الغرناطي الشهير بالشاطبي، أبو إسحاق:</span>
حافظ ثقة، محدث، مفسر، أصولي، من أئمة فقهاء المالكية، من أهل غرناطة، أخذ عن الشريف السبتي والشريف التلمساني وغيرهما. أثنى عليه علماء المغرب، وقال التنبكتي: "له القدم الراسخ والإمامة العظمى في الفنون فقها وأصولا وتفسيرا وحديثا وعربية وغيرها ... " (١).

الكركي [٧٧٦ - ٨٥٣ هـ / ١٣٧٤ - ١٤٤٩ م]

<span data-type="title" id=toc-66>إبراهيم بن موسى بن بلال بن عمران بن مسعود بن دمج، برهان الدين الكركي:</span>
عالم بالقراءات والتفسير والفقه والعربية والأصول. ولد بكرك الشوبك، (بالأردن) وأخذ عن علماء بيت المقدس ودمشق والخليل والقاهرة والرملة. قال السخاوي: "وحج، وزار بيت المقدس مرارا، وتردد للقاهرة غير مرة. ثم استوطنها سنة ٨٠٨ هـ ". ولي قضاء المحلة
_________
(١) فهرس الفهارس ١: ١٣٤ ونيل الابتهاج ٤٦ وشجرة النور ٢٣١ والمجددون في الإسلام ٣٠٧ والأعلام ١: ٧١ وهدية العارفين ١: ١٨ ومعجم المؤلفين ١: ١١٨ ومعجم المصنفين ٤: ٤٤٨.','1','23',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (56,'بمصر سنة ٨٢٧ هـ، وناب في القضاء بمنوف سنة ٨٢٩ هـ. ثم عاد إلى القاهرة وتوفي فيها. من آثاره "إعراب المفصل" من سورة الحجرات إلى آخر القرآن، و "حاشية" على تفسير العلاء التركماني الحنفي (١).

اليزيدي [.. - ٢٢٥ هـ / .. - ٨٤٠ م]

<span data-type="title" id=toc-67>إبراهيم بن يحيى بن المبارك، أبو إسحاق اليزيدي العدوي:</span>
عالم بالأدب، شاعر مجيد، من ندماء المأمون العباسي وقدم إلى دمشق في صحبته، وله أخبار معه في مجالس أنسه. وهو بصري، سكن بغداد. أخذ عن الأصمعي وغيره. من تصانيفه "مصادر القرآن" بلغ فيه إلى سورة آلم - كما في طبقات المفسرين، وإلى سورة الحديد، كما في الفهرست لابن النديم (٢).
_________
(١) الضوء اللامع ١: ١٤٥ ونظم العقيان ٢٩ وعنوان الزمان ١: ٤٢٨ ومعجم المصنفين ٤: ٤٤٦ ومعجم المؤلفين ١: ١١٨ والأعلام ١: ٧١ وطبقات المفسرين ١: ٢٢ والتبر المسبوك ٢٧٣.
(٢) بغية الوعاة ١: ٤٣٤ ونزهة الالباء ١٦٥ وأنباه الرواة ١: ١٨٩ وتاريخ بغداد ٦: ٢٥٩ وطبقات المفسرين ١: ٢٨ وغاية النهاية ١: ٢٩ وكشف الظنون ٣: ١٧ ومعجم الأدباء ٢: ٩٧ ومعجم المصنفين ٤: ٤٧٦ والأعلام ١: ٧٤ ومعجم المؤلفين ١: ١٢٦.','1','24',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (57,'النخعي [٤٦ - ٩٦ هـ / ٦٦٦ - ٧١٥ م]

<span data-type="title" id=toc-68>إبراهيم بن يزيد بن قيس بن الأسود، أبو عمران النخعي، من مذحج:</span>
فقيه العراق، تابعي، من أكابرهم صلاحا وصدق رواية وحفظا للحديث، من أهل الكوفة، ومن مشاهير مفسري مدرستها. روى عن علقمة ومسروق وخاله الأسود بن يزيد (راجع تراجمهم في هذا الكتاب) وغيرهم. ودخل على عائشة وهو صبي. قال الصفدي: كان إماما مجتهدا له مذهب. ولما بلغ الشعبي موته قال: والله ما ترك بعده مثله (١).

ابن المرأة [.. - ٦١١ هـ / .. - ١٢١٤ م]

<span data-type="title" id=toc-69>إبراهيم بن يوسف بن محمد بن دهاق الأوسي، أبو إسحاق، ويعرف بابن المرأة:</span>
عالم في التفسير والفقه والحديث والتاريخ والكلام. سكن مالقة مدة، ثم انتقل إلى مرسية وتوفي بها. قال ابن
_________
(١) تهذيب التهذيب ١: ١٧٧ وتذكرة الحفاظ ٧٣ وابن خلكان ١: ٦ وصفة الصفوة ٣: ٤٧ وطبقات ابن سعد ٦: ١٨٨ وحلية الأولياء ٤: ٢١٩ والشذرات ١: ١١١ والوافي ٦: ١٦٩ وكشف الظنون ١: ٤٣٠ وغاية النهاية ١: ٢٩.','1','24',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (58,'الخطيب: وتواليفه نافعة في أبوابها، حسنة الرصف والمباني (١).

ابي بن كعب [... - ٣٠ هـ / ... - ٦٥١ م]

<span data-type="title" id=toc-70>ابي بن كعب بن قيس بن عبيد بن زيد بن معاوية، من بني النجار، من الخزرج، أبو المنذر:</span>
من فقهاء الصحابة وأقرأهم لكتاب الله، أنصاري. كان قبل إسلامه حبرا من أحبار اليهود، مطلعا على الكتب القديمة، يحسن القراءة والكتابة - على قلة العارفين فيها في عصره -. ولما أسلم كان ممن كتب الوحي لرسول الله - صلى الله عليه وسلم - قبل زيد بن ثابت ومعه. قال ابن سعد في طبقاته: "أول من كتب لرسول الله - صلى الله عليه وسلم - مقدمه المدينة أبي بن كعب، وهو أول من كتب في آخر الكتاب: "وكتب فلان .. ". شهد العقبة الثانية وبايع فيها، ثم شهد المشاهد كلها مع رسول الله - صلى الله عليه وسلم -، وأفتى على عهده، ولكنه كان من المقلين في الفتيا. وشهد مع عمر بن الخطاب وقعة الجابية (على مسيرة يوم جنوبي غربي دمشق) وكتب كتاب الصلح لأهل بيت المقدس. عن أنس بن مالك أن رسول الله
_________
(١) الديباج ٩٠ والوافي ٦: ١٧١.','1','25',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (59,'- صلى الله عليه وسلم - دعا أبي فقال: إن الله، تبارك وتعالى، أمرني أن أقرأ عليك، قال: الله سماني لك؟ قال: الله سماك لي. قال: فجعل أبي يبكي ... ". وقال أبي: قال لي رسول الله - صلى الله عليه وسلم -: يا أبا المنذر أي آية معك في كتاب الله أعظم؟ فقلت: "الله لا إله إلا هو الحي القيوم" (آل عمران: ٢) قال: فضرب صدري وقال: ليهنك العلم يا أبا المنذر". في تاريخ وفاته خلاف بين المؤرخين، قيل سنة ١٩، وقيل سنة ٢٠، وقيل سنة ٢٣، وقيل سنة ٣٠ وقيل سنة ٣٢ هـ، وقيل قبل مقتل عثمان (٣٥ هـ) بجمعة أو شهر، والأرجح أنه مات سنة ٣٠ هـ، بالمدينة، ذلك أن عثمان بن عفان أمره أن يجمع القرآن، فاشترك في جمعه. وله في الصحيحين وغيرهما ١٦٤ حديثا. وكان نحيفا قصيرا أبيض الرأس واللحية. ويعد من كبار المفسرين من الصحابة (١).
_________
(١) طبقات ابن سعد ٣: ٤٩٨ وانظر فهرسته، وتهذيب تاريخ ابن عساكر ٢: ٣٢٥ وحلية الأولياء ١: ٢٥٥ وتهذيب التهذيب ١: ١٨٧ وصفة الصفوة ١: ١٨٨ وغاية النهاية ١: ٣١ وأسد الغابة ١: ٤٩ والشذرات ١: ٢٠ و ٣١ - ٣٢ والاستيعاب ٢٥ ودراسات في التفسير ورجاله ٧١ والوافي ٦: ١٩٠ والقرآن والتفسير ٩١ وتذكرة الحفاظ ١٦. وسمط اللآلي ٤٩٤.','1','25',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (60,'الفاروثي [٦١٤ - ٦٩٤ هـ / ١٢١٧ - ١٢٩٥ م]

<span data-type="title" id=toc-71>أحمد بن إبراهيم بن عمر بن الفرج، عز الدين، أبو العباس الفاروثي - نسبة إلى فاروث، قرية على دجلة - الواسطي:</span>
مقرىء كبير، مفسر، واعظ، خطيب، من أعيان فقهاء الشافعية. ولد بواسط، وسمع بها وببغداد وأصبهان، ثم دخل دمشق وولي مشيخة الحديث بالظاهرية، ثم الخطابة بجامعها الكبير. وعاد إلى واسط سنة ٦٩١ هـ فمات فيها (١).

ابن الزبير [٦٢٧ - ٧٠٨ هـ / ١٢٣٠ - ١٣٠٨ م]

<span data-type="title" id=toc-72>أحمد بن إبراهيم بن الزبير بن محمد بن إبراهيم بن الزبير، أبو جعفر، الثقفي الغرناطي:</span>
محدث، مؤرخ، من أبناء العرب الداخلين إلى الأندلس، انتهت اليه الرياسة بها في العربية ورواية الحديث والتفسير والأصول، وارتحل إلى بابه العلماء لسعة معارفه. ولد في جيان، وأقام بمالقة، فأوذي، فغادرها إلى غرناطة وبها
_________
(١) ذيل تذكرة الحفاظ ٨٥ وغاية النهاية ١: ٣٤ والنجوم الزاهرة ٨: ٧٦ والشذرات ٥: ٥٢٤ وطبقات القراء للذهبي ٢: ٥٥٢ والعبر ٥: ٣٨١ والسبكي ٥: ٣ وطبقات المفسرين ١: ٢٧ وتاريخ علماء بغداد ١٨ وابن كثير ١٣: ٤٣٢ والوافي ٦: ٢١٩ والدارس ١: ٣٥٥.','1','26',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (61,'أكمل ما شرع فيه من مصنفاته، وتوفي فيها. قال ابن حجر: "كان ثقة قائما بالأمر بالمعروف والنهي عن المنكر قامعا لأهل البدع، وله مع ملوك عصره وقائع، وكان معظما عند الخاصة والعامة". من كتبه "ملاك التأويل في المتشابه اللفظ في التنزيل" مخطوط، في خزانة الرباط، و "البرهان في ترتيب سور القرآن" مخطوط، في خزانة الرباط (١).

العسلقي [.. - ٨٠٦ هـ / .. - ١٤٠٣ م]

<span data-type="title" id=toc-73>أحمد بن إبراهيم بن علي العسلقي:</span>
مفسر، محدث، نحوي، من أهل اليمن. قال السخاوي: نسبته إلى العسالق طائفة من العرب" وقال ابن الأهدل: "كان فقيها نحويا لغويا مفسرا محدثا، والغالب عليه الفقه والحديث
_________
(١) الإحاطة ١: ١٩٥ وتذكرة الحفاظ ١٤٨٤ والدرر الكامنة ١: ٨٩ والبدر الطالع ١: ٣٣ والذيل والتكملة ١: ٣٩ والمنهل الصافي ١: ١٩٧ وغاية النهاية ١: ٣٢ وطبقات المفسرين ١: ٢٦ والديباج ٤٢ وبغية الوعاة ١: ٢٩١ والوافي ٦: ٢٢٢ ودرة الحجال ١: ١١ وهدية العارفين ١: ١٠٣ ومقدمة صلة الصلة. وبروكلمان، الذيل ١: ٧٣٣ وأعيان العصر ٤٧ ب والأعلام ١: ٨٣ وفهرس الفهارس ١: ٣٤١ ومعجم المؤلفين ١: ١٣٨ وكشف الظنون ١٨١٣.','1','26',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (62,'والتفسير، وله معرفة تامة بالرجال والتواريخ والسير، ويد قوية في أصول الدين .. " لازم التدريس واسماع الحديث والعكوف على العلم، وقد كشف بصره في أواخر أيامه (١). الحلبي [.. - ٩٧١ هـ .. - ١٥٦٣ م]

<span data-type="title" id=toc-74>أحمد بن إبراهيم، غرس الدين، الحلبي:</span>
طبيب، فرضي، عالم بالرياضيات والتفسير، مشارك في عدة علوم، نشأ في مدينة حلب، وتعلم بها وبدمشق والقاهرة، ورحل إلى القسطنطينية واستوطنها إلى أن مات، قال في "العقد المنظوم": "كان رأسا في جميع العلوم، مستجمعا لشروط الفضائل، وجامعا لعلوم الأواخر والأوائل، لم يقبل مدة عمره وظيفة السلطان ... ، له "شرح" تفسير البيضاوي، حوى جزأين من القرآن الكريم" (٢).

الهكاري [.. - ٧٦٣ هـ / .. - ١٣٦٢ م]

<span data-type="title" id=toc-75>أحمد بن أحمد بن الحسن بن موسى، (شهاب الدين، أبو سعيد بن أبي الحسن الهكاري)</span>
_________
(١) الضوء اللامع ١: ١٩٧ وبغية الوعاة ١: ٢٩٤ والشذرات ٧: ٥٥.
(٢) العقد المنظم ٣٥٧ وكشف الظنون ١٩٢ وهدية العارفين ٨: ١٤٥ ومعجم المؤلفين ١: ١٣٧.','1','27',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (63,'شهاب الدين، أبو سعيد بن أبي الحسن الهكاري: عالم برجال الحديث، مفسر، من أهل مصر، كردي الأصل، درس بجامع الحاكم بأمر الله. من كتبه "تفسير القرآن" يوجد ستة مجلدات منه، هي ١ و ٢ و ٤ و ٥ و ٦ و ٧ (١).

طاشكبري زاده [.. - ١٠٣٠ هـ / .. - ١٦٢١ م]

<span data-type="title" id=toc-76>أحمد بن أحمد بن مصطفى بن خليل، كمال الدين، طاشكبري زاده:</span>
من علماء الحنفية، مفسر، تركي الأصل، مستعرب. وهو ابن طاشكبري زاده المؤرخ المعروف صاحب الشقائق النعمانية. من آثاره "حاشية" على تفسير البيضاوي، وصل فيها إلى سورة الكهف (٢).

الشدادي [.. - ١١٤٦ هـ / .. - ١٧٣٣ م]

<span data-type="title" id=toc-77>أحمد بن أحمد بن محمد الشدادي، الإدريسي الحسني، أبو العباس:</span>
قاض، من العلماء في التفسير والحديث والنحو، من
_________
(١) الدرر الكامنة ١: ١٠٤ وهدية العارفين ١: ١١٢ والأعلام ١: ٨٧ وحسن المحاضرة ١: ٢٠٣ ومعجم المؤلفين ١: ١٤٥ وبروكلمان ٢: ٢٠١ والذيل ٢: ٢٧٤ والفهرس التمهيدي ٤٧ و ٤٨ وهو فيه: أحمد بن الحسين بن موسى.
(٢) إيضاح المكنون ١: ١٤١.','1','27',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (64,'فقهاء المالكية. ولي الافتاء والتدريس بفاس، والقضاء والإمامة بزاوية زرهون (١).

السجاعي [.. - ١١٩٧ هـ / .. - ١٧٨٣ م]

<span data-type="title" id=toc-78>أحمد بن أحمد بن محمد السجاعي البدراوي الأزهري:</span>
فقيه شافعي، نحوي، مشارك في عدة علوم، نسبته إلى السجاعية من غربية مصر. من آثاره "الدرر في إعراب أوائل السور" رسالة، مخطوطة (٢).

القرافي [٦٢٦ - ٦٨٤ هـ / ١٢٢٨ - ١٢٨٥ م]

<span data-type="title" id=toc-79>أحمد بن ادريس بن عبد الرحمن بن عبد الله، شهاب الدين، أبو العباس، الصنهاجي الأصل، البهنسي، المشهور بالقرافي:</span>
فقيه، أصولي، مفسر، من علماء المالكية. قال الصفدي: أصله من قرية من كورة بوش من صعيد مصر الأسفل تعرف ب"بهفشيم" ونسبته إلى قبيلة
_________
(١) سلوة الأنفاس ٣: ١٩٦ واحتاف أعلام الناس ١: ٣٤١ واليواقيت الثمينة ١: ٤٦.
(٢) معجم المطبوعات ١٠٠٥ والمكتبة الأزهرية ١: ١٤٩ وخطط مبارك ١٢: ٩ والفهرس التمهيدي ٥٦٣ وهدية العارفين ١: ١٧٨ وعجائب الآثار ٢: ٧٥ ومعجم المؤلفين ١: ١٥٤.','1','28',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (65,'صنهاجة المغربية، وإلى القرافة، المحلة المجاورة لقبر الإمام الشافعي، بالقاهرة، ولم يسكنها، وكان إماما في أصول الفقه وأصول الدين عالما بالتفسير وبعلوم أخر، درس بالمدرسة الصالحية ومدرسة طيبرس وبجامع مصر". توفي بدير الطين بالقرب من مصر القديمة، ودفن بالقرافة" (١).

القاضي التنوخي [٢٣١ - ٣١٨ هـ / ٨٤٦ - ٩٣٠ م]

<span data-type="title" id=toc-80>أحمد بن إسحاق بن البهلول بن حسان ابن سنان، أبو جعفر التنوخي:</span>
محدث ثقة، نحوي، لغوي، مفسر، من فقهاء الحنفية، له شعر، ولد بالأنبار، وولي قضاءها وقضاء هيت وطريق الفرات من قبل الموفق العباسي، ثم قلده المقتدر - بعد فتنة المعز - القضاء بمدينة المنصور فوليها عشرين سنة (٢٩٦ - ٣١٦ هـ). قال الصفدي: (كان تام العلم باللغة حسن القيام بالنحو، حفظة للشعر القديم والحديث والأخبار الطوال والسير والتفسير
_________
(١) المنهل الصافي ١: ٢١٥ والديباج ٦٢ والوافي ٦: ٢٣٣ وروضات الجنات ٩١ وهدية العارفين ١: ٩٩ وشجرة النور ١٨٨ والحياة العقلية في عصور الحروب الصليبية ١٧٤ ومعجم المؤلفين ١: ١٥٨ وبروكلمان الذيل ١: ٦٦٥.','1','28',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (66,'شاعرا خطيبا". من كتبه "الناسخ والمنسوخ" (١)

الغزنوي [.. - بعد ٥٢٠ هـ / .. - بعد ١١٢٦ م]

<span data-type="title" id=toc-81>أحمد بن إسماعيل بن عيسى، أبو بكر الغزنوي الجوهري:</span>
مفسر، من علماء غزنة وبها نشأ وتعلم. رحل في طلب العلم فدخل خراسان والحجاز والعراق، ولقي أبا القاسم القشيري وسمع منه. قال الداودي: "وعاش إلى بعد العشرين وخمسمائة" (٢).

القزويني [٥١٢ - ٥٩٠ هـ / ١١١٨ - ١١٩٣ م]

<span data-type="title" id=toc-82>أحمد بن إسماعيل بن يوسف بن محمد بن العباس الطالقاني، أبو الخير، رضي الدين القزويني:</span>
عالم بالحديث والتفسير والأصول والخلاف والنظر، إمام في فقه الشافعية. من أهل قزوين، تعلم بها
_________
(١) الوافي ٦: ٢٣٥ ومعجم الأدباء٢: ١٣٨ وتاريخ بغداد ٤: ٣٠ والجواهر المضية ١: ٥٧ والمنتظم ٦: ٢٣١ ونزهة الالباء ٢٥٣ والشذرات ٢: ٢٧٦ وبغية الوعاة ١: ٢٩٥ وهدية العارفين ١: ٥٨ وكشف الظنون ١٩٢٠ والأعلام ١: ٩١ ومعجم المؤلفين ١: ١٦٠.
(٢) طبقات المفسرين للداودي ١: ٣١.','1','29',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (67,'وبنيسابور، ودخل بغداد مرات، وأقام بها مدة ودرس بالنظامية، ثم عاد إلى قزوين وتوفي بها. قال عبد اللطيف البغدادي: "كان يعمل في اليوم والليلة ما يعجز المجتهد عن عمله في شهر". من كتبه "التبيان في مسائل القرآن" رد به على الحلولية والجهمية والقائلين بخلق القرآن (١).

ابن الحسباني [٧٤٩ - ٨١٥ هـ / ١٣٤٩ - ١٤١٢ م]

<span data-type="title" id=toc-83>أحمد بن إسماعيل بن خليفة بن عبد العال، شهاب الدين، أبو العباس، المعروف بابن الحسباني:</span>
مؤرخ، حافظ للحديث، مفسر، ولد بدمشق وبها نشأ وتعلم، ثم رحل إلى مصر وأخذ عن جماعة. وعاد، فدرس بالأمينية وولي درس الحديث بالأشرفية، وناب في الحكم مدة ثم ولي قضاء القضاة غير مرة. قال في "ذيل
_________
(١) النجوم الزاهرة ٦: ١٣٤ وغاية النهاية ١: ٣٩ والبداية والنهاية ١٣: ٩ وطبقات السبكي ٦: ٧ والعبر ٤: ٢٧١ وشذرات الذهب ٤: ٣٠٠ وطبقات المفسرين للسيوطي ٣ وطبقات المفسرين للداودي ١: ٣١ ومرآة الزمان ٨: ٤٤٣ والوافي بالوفيات ٦: ٢٥٣ ومختصر ابن الدبيثي ١: ١٧٤ وهدية العارفين ١: ٨٨ وكشف الظنون ٣٤١ و ٤٣٦ والأعلام ١: ٩٦ ومعجم المؤلفين ١: ١٦٨، ووفاته في بعض هذه المصادر سنة ٥٨٩ هـ.','1','29',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (68,'تذكرة الحفاظ": "هو شيخ دمشق وابن شيخها، كانت له حلقة بالجامع الأموي وغيره". من كتبه "جامع التفاسير" وقف عليه البلقيني وأثنى عليه، وقال ابن فهد: أجاد في تهذيبه وجمع فيه فأوعى" (١).

الإبشيطي [٨٠٢ - ٨٨٣ هـ / ١٤٠٠ - ١٤٧٨ م]

<span data-type="title" id=toc-84>أحمد بن إسماعيل بن أبي بكر بن عمر ابن بريدة، شهاب الدين الإبشيطي:</span>
عارف بالحديث والتفسير، فرضي، من فقهاء الشافعية. ولد بأبشيط (من قرى المحلة بمصر)، وانتقل إلى القاهرة سنة ٨٢٠ هـ فتعلم في الأزهر. ثم درس فانتفع به جماعة. وحج سنة ٨٥٧ هـ وسكن المدينة، ثم جاور بمكة سنة ٨٧١ هـ ولقيه السخاوي فيها. وتوفي بالمدينة. من آثاره "ناسخ القرآن ومنسوخه" (٢).
_________
(١) ذيل تذكرة الحفاظ ٢٤٤ وأنباء الغمر ٢: ٥٢٣ والضوء اللامع ١: ٣٢٧ والشذرات ٧: ١٠٨ والمنهل الصافي ٢٢٤ وقضاة دمشق ١٣٢ وطبقات المفسرين ١: ٢٠ والأعلام ١: ٩٣ ومعجم المؤلفين ١: ١٦٤.
(٢) الضوء اللامع ١: ٢٣٥ وعنه أخذت تاريخ وفاتقه. ونظم العقيان ٣٧ وفيه ولادته سنة ٨١٠ هـ. والبدر الطالع ١: ٣٧ وهدية العارفين ١: ١٣٥ وشذرات الذهب ٧: ٣٣٦ وإيضاح المكنون ٢: ٦١٥ والأعلام ١: ٩٤ ومعجم المؤلفين ١: ١٦٣.','1','30',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (69,'الكوراني [٨١٣ - ٨٩٣ هـ / ١٤١٠ - ١٤٨٨ م]

<span data-type="title" id=toc-85>أحمد بن إسماعيل بن عثمان بن أحمد بن رشيد بن إبراهيم الشهرزوري، الهمداني، التبريزي، الكوراني، ثم القاهري، الشافعي ثم الحنفي، شهاب الدين، ويقال شرف الدين:</span>
مفسر، كردي الأصل، من أهل شهرزور، وبها نشأ وتعلم، ثم رحل إلى مصر وأخذ عن كبار الشيوخ، وأجازه ابن حجر العسقلاني في الحديث. ودرس بالقاهرة. ثم رحل إلى بلاد الترك، فولي التدريس في بروسا وغيرها، وعهد إليه السلطان مراد بن عثمان بتعليم ولي عهده "محمد الفاتح"، ولما ولي الفاتح السلطنة ولاه قضاء العسكر ثم الإفتاء، ثم قضاء بروسا، وتوفي بالقسطنطينية. من كتبه "غاية الأماني في تفسير السبع المثاني" قال طاشكري زاده: "أورد فيه مؤاخذات كثيرة على العلامتين الزمخشري والبيضاوي" مخطوط، قطعة منه في صوفية (١).

النجفي [.. - ١١٥٠ هـ / .. - ١٧٣٧ م]

<span data-type="title" id=toc-86>أحمد بن إسماعيل الجزائري النجفي:</span>
_________
(١) الشقائق النعمانية ٥١ وكشف الظنون ٤٥٧ و ١١٩٠ والضوء اللامع ١: ٢٤١ ونظم العقيان ٣٨ ومعجم المؤلفين ١: ١٦٦ و ٢: ٢٨٨.','1','30',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (70,'فقيه إمامي، أصله من جزائر خوزستان، جاور بالنجف، وتوفي فيه. من آثاره "قلائد الدرر في بيان آيات الأحكام بالأثر" (١).

ابن بقي [٢٦٠ - ٣٢٤ هـ / ٨٧٤ - ٩٣٦ م]

<span data-type="title" id=toc-87>أحمد بن بقي بن مخلد بن يزيد، أبو عمر، ويقال أبو عبد الله، القرطبي الأندلسي:</span>
قاض، خطيب بليغ، حافظ للقرآن عالم بتفسيره وعلومه، قوي المعرفة باختلاف العلماء فيه، كان في شبابه من مستشاري الأمير عبد الله بن محمد الأموي (صاحب الأندلس). ولي الصلاة بقرطبة ثم قضاءها سنة ٣١٤ هـ، وكان من خير القضاة، وأكثرهم رفقا واشفاقا، بحيث يقال إنه لم يقرع أحدا من الناس في طول مدة قضائه بسوط - وكانت نحوا من عشرة أعوام - إلا رجلا واحدا مجمعا على فسقه. وقال له بعض أصحاب السلطان: "إنا لنعيبك بلين الجانب، والتطويل في الأحكام" فقال: أعوذ بالله من لين يؤدي إلى ضعف، ومن شدة تبلغ إلى عنف" (٢).
_________
(١) أعيان الشيعة ٧: ٤١٩ وهدية العارفين
(٢) طبقات المفسرين ١: ٣٢ والعبر ٢: ٢٠٠ وبغية الملتمس ١٦٠ وجذوة المقتبس ١١٠ والشذرات ٢: ٣٩١ وقضاة قرطبة ١٩١ وتاريخ قضاة الأندلس ٦٣ والديباج ٣٧ والنجوم الزاهرة ٣: ٢٥٩.','1','31',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (71,'القزويني [.. - حيا ٦٢٥ هـ / .. - حيا ١٢٢٨ م]

<span data-type="title" id=toc-88>أحمد بن أبي بكر بن عبد الوهاب القزويني، بديع الدين، أبو عبد الله:</span>
مفسر، من أعيان فقهاء الحنفية، أصله من قزوين وإليها نسبته. كان مقيما بسيواس - مدينة في أواسط تركية الآسيوية - في سنة ٦٢٥ هـ. له "الجامع الحريز الحاوي لعلوم كتاب الله العزيز" (١).

الاخنف [٦٤١ - ٧١٧ هـ / ١٢٤٣ - ١٣١٧ م]

<span data-type="title" id=toc-89>أحمد بن أبي بكر بن عمر، أبو العباس، المعروف بالأخنف:</span>
مفسر، فقيه، حافظ للحديث، لغوي، من أهل بلدة جبلة في اليمن. درس بالمدرسة الشرفية ثم المؤيدية بتعز. قال الخزرجي: صنف في التفسير والحديث واللغة، وتوفي بجبلة (٢).

الكيلاني [.. - ١٠١٥ هـ / .. - ١٦٤١ م]

<span data-type="title" id=toc-90>أحمد بن توفيق الكيلاني:</span>
قاض، من
_________
(١) تاج التراجم ٥ والجواهر المضية ١: ٥٦ وكشف الظنون ٥٤٠ والطبقات السنية ١: ٣٣٠ وطبقات المفسرين ١: ٣٣ وبروكلمان ١: ٣٨.
(٢) بغية الوعاة ١: ٢٩٩ وطبقات المفسرين ١: ٣٣ والعقود اللؤلؤية ١: ٤٢٣ وهدية العارفين ١: ١٠٤ والأعلام ١: ١٠٠.','1','31',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (72,'علماء الحنفية، ولد في القسطنطينية، وولي قضاء سلانيك والشام ومصر وأدرنة، وبها توفي. من آثاره "حاشية" على تفسير البيضاوي (١).

ابن جزي [.. - نحو ٦٢٠ هـ / .. - نحو ١٢٢٣ م]

<span data-type="title" id=toc-91>أبو أحمد بن جزي الكلبي:</span>
قال في "الديباج المذهب": "كان شفي جليلا، ورعا، زاهدا، عابدا متقللا من الدنيا، وكان فقيها مفسرا، وله "تفسير القرآن العزيز" (٢).

الدينوري [.. - ٢٨٩ هـ / .. - ٩٠٢ م]

<span data-type="title" id=toc-92>أحمد بن جعفر الدينوري، أبو علي:</span>
نحوي، لغوي، من أهل الدينور (من بلاد الجبل) رحل إلى البصرة، وأخذ عن المازني، وحمل عنه كتاب سيبويه. ثم دخل بغداد وقرأ على المبرد. ثم نزل مصر وتوفي فيها. من كتبه "ضمائر القرآن" كتاب مختصر، استخرجه من كتاب "معاني القرآن" للفراء (٣).
_________
(١) هدية العارفين ١: ١٥٩ وإيضاح المكنون ١: ١٤٢ ومعجم المؤلفين ١: ١٨٠ وخلاصة الأثر
(٢) الديباج ١: ٣٢٠ وطبقات المفسرين ١: ١٠١.
(٣) معجم الأدباء٢: ٢٣٩ والوافي ٦: ٢٨٥ وطبقات الزبيدي ٢١٥ وبغية الوعاة ١: ٣٠١ وإنباه الرواة ١: ٣٣ وشذرات الذهب ٢: ١٧.','1','32',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (73,'ابن المنادي [٢٥٦ - ٣٣٦ هـ / ٨٧٠ - ٩٤٧ م]

<span data-type="title" id=toc-93>أحمد بن جعفر بن محمد بن عبيد الله، أبو الحسن، المعروف بابن المنادي:</span>
عالم بالتفسير والحديث، مقرىء جليل، عارف بالنحو واللغة، من أهل بغداد، قال ابن النديم: "كان يغرب فى ألقاب كتبه، ويتعاطى الفصاحة في تأليفه، فأخرجه ذلك إلى الاستثقال، له مئة ونيف وعشرون كتابا في علوم متفرقة، وكان الغالب عليه علوم القرآن". وقال ابن الجوزي: "من وقف على مصنفاته علم فضله واطلاعه ووقف على فوائد لا توجد في غير كتبه، جمع بين الرواية والدراية، ولا حشو في كلامه .. ". توفي ببغداد ودفن في مقبرة الخيزران. من كتبه "ناسخ القرآن ومنسوخه" و "متشابه القرآن" (١).
_________
(١) تاريخ لغداد ٤: ٦٩ وتذكرة الحفاظ ١ - ٨٤٩ وابن النديم ٤١ وطبقات ابن أبي يعلى ٢: ٣ و ٢٩١ والبداية والنهاية ١١: ٢١٩ وتاريخ التراث العربي ١: ٢١٣ والمنتظم ٦: ٣٥٧ والنجوم الزاهرة ٣: ٢٩٥ وبغية الوعاة ١: ٣٠٠ وغاية النهاية ١: ٤٤ ومناقب الإمام أحمد ٥١١ وطبقات القراء للذهبي ١: ٢٢٩ والعبر ٢: ٢٤٢ وطبقات المفسرين ١: ٣٣ وهدية العارفين ١: ٦١ والأعلام ١: ١٠٣ والنسخ في القرآن الكريم ٢: ٨٤٤ ومعجم المؤلفين ١: ١٨٣ وكشف الظنون ١٩٢١.','1','32',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (74,'الختلي [٢٧٨ - ٣٦٥ هـ / ٨٩١ - ٩٧٦ م]

<span data-type="title" id=toc-94>أحمد بن جعفر بن محمد بن سلم الختلي، أبو بكر:</span>
محدث ثقة، مقرىء، مفسر، من أهل بغداد، وأخذ عن مشيختها. قال الخطيب البغدادي: "كان صالحا ثقة ثبتا، كتب عنه الدارقطني". وقال أبو نعيم: "كتب من القراءات والتفاسير شينا عظيما". توفي ببغداد، ودفن في مقبرة الخيزران (١).

السلفكهوي [.. - ١٠٩٣ هـ / .. - ١٦٨٣ م]

<span data-type="title" id=toc-95>أحمد بن جعفر بن عبد الفتاح السلفكهوي:</span>
مفسر، قاض، من فقهاء الحنفية، تركي الأصل، مستعرب، تولى القضاء بالقسطنطينية (٢).

ابن عصية [.. - ٧٢٠ هـ / .. - ١٣٢٠ م]

<span data-type="title" id=toc-96>أحمد بن حامد، المعروف بابن عصية، جمال الدين، البغدادي:</span>
مفسر، فرضي، من فقهاء الحنابلة، من أهل
_________
(١) تاريخ بغداد ٤: ٧١ وغاية النهاية ١: ٤٤ والبداية والنهاية ١١: ٢٨٣ والمنتظم ٧: ٨٠ والوافي ٦: ٢٩ وتاريخ التراث العربي ١: ٤٩٣.
(٢) هدية العارفين ١: ١٦٣ ومعجم المؤلفين ١: ١٨٢.','1','33',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (75,'بغداد، ولي القضاء بالجانب الشرقي منها، ودرس بالبشيرية، ثم عزل، ونالته محنة، ثم أعيد إلى التدريس سنة ٧١٣ هـ. قال الطوفي: "حضرت درسه، وكان بارعا في الفقه والتفسير والفرائض، وأما معرفة القضاء والأحكام، فكان أوحد عصره في ذلك" (١).

السيروزي [.. - ١٠٣٣ هـ / .. - ١٦٢٤ م]

<span data-type="title" id=toc-97>أحمد بن حسام الدين السيروزي، المعروف بملاحق:</span>
من أفاضل قضاة الترك في عصره، مفسر، نسبته إلى - سيروز، بلدة بولاية روم ايلي، بالقرب من ينكي شهر - ولي القضاء في "تيمور حصار" و "زغرة العتيقة" و "هزار غراد" و "سيروز". كما ولي التدريس وإلافتاء. له "رسالة على مواطن من التفسير" (٢).

الزازي [٦٥١ - ٧٤٥ هـ / ١٢٥٣ - ١٣٤٥ م]

<span data-type="title" id=toc-98>أحمد بن الحسن بن أحمد بن الحسن بن أنوشروان، الرازي الأصل، ثم الرومي، (أبو المفاخر)</span>
_________
(١) شذرات الذهب ٦: ٥٣ وذيل طبقات الحنابلة ٢: ٣٧٣ والدرر الكامنة ١: ١٢٦ ووفاته فيه سنة ٧٢١ هـ.
(٢) خلاصة الأثر ١: ١٧٩ وهدية العارفين ١: ١٥٦ ومعجم المؤلفين ١: ١٨٩.','1','33',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (76,'أبو المفاخر: عالم بالتفسير والفقه والنحو، من أعيان فقهاء الحنفية. ولد بأنكورية (انقرة) وبها نشأ وتعلم. ولي قضاء "خرت برت - في أقصى ديار بكر من بلاد الترك" وعمره سبع عشرة سنة. وقدم مع أبيه دمشق، وولي قضاء قضاة الحنفية بها عوضا عن أبيه، ودرس بالخاتونية والقصاعية، وعمي في آخر عمره (١).

الجاربردي [.. - ٧٤٦ هـ / .. - ١٣٤٦ م]

<span data-type="title" id=toc-99>أحمد بن الحسن بن يوسف، فخر الدين، أبو المكارم، الجاربردي:</span>
مفسر من فقهاء الشافعية. سكن تبريز وتوفي بها. أثنى عليه السبكي والأسنوي وابن قاضي شهبة. من كتبه "حواش" على الكشاف في التفسير، مخطوطة، في عشر مجلدات. توفي بتبريز (٢).
_________
(١) النجوم الزاهرة ١٠: ١٠٩ والبداية والنهاية ١٤: ٢١٤ والدرر الكامنة ١: ١٢٦ والفوائد البهية ١٦ وقضاة دمشق ١٩٢ والجواهر المضينة ١: ٦٣ وطبقات الداودي ١: ٣٤ والطبقات السنية ١: ٣٧٤.
(٢) البدر الطالع ١: ٤٧ ومفتاح السعادة ١: ١١٩ مرآة الجنان ٤: ٣٠٧ وطبقات السبكي ٥: ١٦٩ والشذرات ٦: ١٤٨ والدرر الكامنة ١: ١٣٢ وهدية العارفين ١: ١٠٨ وكشف الظنون ١٤٧٨ والتيمورية ١: ١٩٧','1','34',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (77,'ابن الطبري [.. - ٣٧٧ هـ / .. - ٩٨٧ م]

<span data-type="title" id=toc-100>أحمد بن الحسين بن علي المروزي، أبو حامد، المعروف بابن الطبري:</span>
حافظ للحديث، عارف بالتفسير والتاريخ، من القضاة. أصله من همذان، ونشأ في طبرستان، وقدم بغداد فتفقه بها، ودرس على أبي الحسن الكرخي مذهب أبي حنيفة. ثم عاد إلى خراسان فولي بها قضاء القضاة، ثم دخل بغداد، وقد علت سنه، فحدث. بها، وكتب الناس عنه. وفي تاريخ بغداد انه سكن بخارى ومات بها سنة ٣٧٧ هـ، وقيل مات بمرو يا نفس السنة (١).

الرملي [٧٧٣ - ٨٤٤ هـ / ١٣٧١ - ١٤٤٠ م]

<span data-type="title" id=toc-101>أحمد بن حسين بن حسن بن علي بن أرسلان، أبو العباس، شهاب الدين، الرملي، ويعرف بابن رسلان:</span>
عالم بالفقه وأصوله والعربية، مشارك في الحديث والتفسير والكلام وغيرها، زاهد، من كبار الشافعية. ولد بالرملة (بفلسطين) وبها
_________
(١) الوافي ٦: ٣٤٧ وتاريخ بغداد ٤: ١٠٧ والجواهر المضية ١: ٦٥ والمنتظم ٧: ١٣٧ والفوائد البهية ١٨ وتاج التراجم ٩ والبداية والنهاية ١١: ٣٠٥ والكامل لابن الأثير حوادث سنة ٣٧٦ هـ والطبقات السنية ١: ٣٩٣ وهدية العارفين ١: ٩٨ وفيه وفاته سنة ٦٧٧ هـ وهو خطأ واضح.','1','34',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (78,'نشأ وتعلم، ثم انتقل إلى بيت المقدس وأخذ عن جماعة من الأعلام، واشتهر، فكثرت تلامذته ومريدوه وقصده الناس من أنحاء فلسطين. توفي بالقدس. قال السخاوي: "وله تصانيف نافعة في التفسير والحديث والأصلين والعربية وغيرها، كقطع متفرقة من التفسير" (١).

خان داود [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-102>أحمد ... .. المعروف بخان داود:</span>
مفسر، له "تيسير البيان في تخريج آيات القرآن" التي استشهد بها المصنفون في كتبهم .. قال آغا بزرك: "أوله، الحمد لله الذي أنزل القرآن حجة باهرة .. " موجود في كتب مدرسة فاضل خان بمشهد خراسان، وفي مكتبة السيد خليفة الاحسائي، اعتبر فيه حروف أوائل الآيات وحروف أواخرها بتعيين الجزء والركوع، وهو كبير، والاستخراج منه عسير" (٢).
_________
(١) الضوء اللامع ١: ٢٨٢ وعنوان الزمان ١: ٤٠ والشذرات ٧: ٢٤٨ وطبقات المفسرين ١: ٣٧ والبدر الطالع ١: ٤٩ والانس الجليل ٢: ١٧٤ والأعلام ١: ١١٥ والمكتبة الأزهرية ٢: ٥٣٧.
(٢) تعليق آغا بزرك على كشف الظنون ٣٨.','1','35',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (79,'الجذامي [٤٥٤ - ٥٣١ هـ / ١٠٦٢ - ١١٣٧ م]

<span data-type="title" id=toc-103>أحمد بن خلف بن عيشون بن خيار بن سعيد الجذامي، أبو العباس ابن النخاس:</span>
مقرىء كبير، مفسر، محدث، من فقهاء المالكيه. من أهل اشبيلية مولدا ووفاة. قال ابن عبد الملك: "كان مقرئا مقدما في التجويد، مبرزا في إتقان الأداء وإحكام الإقراء بز في ذلك أهل طبقته حتى عرف بينهم بالمجود وجرى عليه كاللقب يشهر به، وصنف في "ناسخ القرآن ومنسوخه" مصنفا مفيدا" (١).

الخويي [٥٨٣ - ٦٣٧ هـ / ١١٨٧ - ١٢٤٠ م]

<span data-type="title" id=toc-104>أحمد بن خليل بن سعادة بن جعفر بن عيسى، أبو العباس، شمس الدين المهلبي الخويي:</span>
عالم في الحكمة والطب والنحو والأصول والكلام والفقه. ولد في خوي بأذربيجان، وتعلم بها وبخراسان، قرأ على فخر الدين الرازي وغيره. ثم ولي قضاء القضاة بالشام، وتوفي بدمشق. من آثاره "تتمة تفسير القرآن" لشيخه فخر الدين الرازي (٢).
_________
(١) بغية الملتمس ١٦٤ وابن الأبار ١: ٣٨ وغاية النهاية ١: ٥٢ والذيل والتكملة ١: ١٠٧ وطبقات القراء للذهبي ١: ٣٩٠.
(٢) البدايه والنهاية ١٣: ١٥٥ والذيل على الروضتين =','1','35',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (80,'الخوانساري [.. - حيا ١٢٧٩ هـ / .. - حيا ١٨٦٢ م]

<span data-type="title" id=toc-105>أحمد الخوانساري:</span>
محدث، مفسر، من فقهاء الشيعة الإمامية. كان حيا سنة ١٢٧٩ هـ. له تصانيف (١).

أبو الكلام ازاد [١٣٠٢ - ١٣٧٧ هـ / ١٨٨٥ - ١٩٥٨ م]

<span data-type="title" id=toc-106>أحمد (المكنى محي الدين) بن خير الدين، مولانا أبو الكلام آزاد:</span>
مفسر، خطيب، من كبار زعماء المسلمين في الهند أيام حركتها التحررية. أصله من دلهي، ومولده بمكة حيث كان أبوه قد هاجر إليها من الهند. ولما بلغ الثالثة - وقيل الخامسة - من عمره، عاد أبوه مع أسرته إلى وطنه وسكن كلكته، وبها نشأ صاحب الترجمة وتعلم. وفي سنة ١٩٠٨ قام برحلة إلى بعض البلاد الإسلامية والعربية فزار العراق والشام وتركيا ومصر. وعاد، فأنشأ مجلة "الهلال" باللغة الأردية (١٢ يونيو ١٩١٢ م)، وهاجم الاستعمار البريطاني فاعتقله الانجليز في رانتجي سنة ١٩١٤ م، فألف في معتقله "تفسيرا" للقرآن الكريم في ١٥
_________
= ١٦٩ ومرآة الجنان ٤: ٢٢ وفيه وفاته سنة ٦٩٣ هـ. والسبكي ٥: ٨ وشذرات الذهب ٥: ١٨٣ وقضاة دمشق لابن طولون ٦٥ والقلائد الجوهرية ٤٤ وعيون الانباء٦٤٦ والوافي ٦: ٣٧٥ وهدية العارفين ١: ٩٢.
(١) أعيان الشيعة ٨: ٣٧٤ وأعلام الشيعة ٢: ٧٥ وإيضاح المكنون ٢: ٤٨٩ ومعجم المؤلفين ١: ٢١٧.','1','36',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (81,'جزءا بالأردية. وأطلق سراحه سنة ١٩٢٠ م فأنشأ مجلة "البلاغ". وكان من أعضاء حزب المؤتمر الهندي الذي أقر برنامج المهاتما غاندي القائل بالمقاومة السلبية لحكومة الهند (سنة ١٩١٧ م)، وحوكم أمام المحكمة الانجليزية (سنة ١٩٢٢ م) بتهمة إثارة الشغب ضد الحكومة، وتكرر اعتقال الانجليز له. وتولى رئاسة حزب المؤتمر بدلهي سنة ١٩٢٣ و ١٩٣٩ م. وفي أيامه نالت الهند استقلالها وانقسمت إلى دولتين الهند " و " باكستان"، وبقي هو في الهند، فتولى رئاسة البرلمان ثم وزارة المعارف في دلهي ثم أضيف إليها وزارة الموارد الطبيعية والأبحاث العلمية، واستمر إلى أن توفي مشلولا. في تاريخ ولادته خلاف، قيل سنة ١٣٠٢ هـ ١٨٨٥ م وقيل ١٣٠٦ هـ ١٨٨٨ م (١).

الدينوري [.. - ٢٨٢ هـ / ... - ٨٩٥ م]

<span data-type="title" id=toc-107>أحمد بن داود بن ونند، أبو حنيفة الدينوري:</span>
نحوي، لغوي، مهندس، منجم، حاسب، مفسر، راوية ثقة فيما يرويه ويحكيه، من نوابغ الرجال، قال أبو
_________
(١) العربي عدد ١٧٥ ومحلة ثقافة الهند عدد مارس ومايو ويونيو ويوليو وسبتمبر وديسمبر ١٩٥٨ ومجلة الحج س ٥ عدد ٧: ٤٠ وتراجم الأعلام المعاصرين ٢١ ومعجم المؤلفين ٨: ١٤٥ وهو فيه أبو الكلام آزاد.','1','36',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (82,'حيان: "جمع بين حكمة الفلاسفة وبيان العرب، له في كل فن ساق وقدم ورواء وحكم". ولد بالدينور. من كتبه "تفسير القرآن" ثلاثة عشر مجلدا، قال أبو حيان: "ولقد قيل لي إنه له كتاب يبلغ ثلاثة عشر مجلدا في القرآن ما رأيته، وإنه ما سبق إلى ذلك النمط" (١).

الكلالي [.. - ١٢٦٩ هـ / .. - ١٨٥٣ م]

<span data-type="title" id=toc-108>أحمد بن داود الكلالي، الكردي الأصل، نزيل دمشق:</span>
مفسر، من فقهاء الشافعية، توفي بدمشق، له "صفوة التفاسير" (٢).

الطهطاوي [١٢٧٥ - ١٣٥٥ هـ / ١٨٥٩ - ١٩٣٦ م]

<span data-type="title" id=toc-109>أحمد رافع بن محمد بن عبد العزيز بن (رافع الحسيني القاسمي الطهطاوي)</span>
_________
(١) الوافي ٦: ٣٧٧ ومعجم الأدباء٣: ٢٦ وإنباه الرواة ١: ٤١ والجواهر المضينة ١: ٦٧ وهدية الأحباب ١٢ ونزهة الالباء ٢٤٠ وسلم الوصول ٧٢ والمختصر في اخبار البشر ٢: ٦٠ وخزانة الأدب ١: ٦٠ وابن النديم ٨٦ وبغية الوعاة ١: ٣٥٦ والأعلام ١: ١١٩ وسير ملهمة ١٥ وتلخيص ابن مكتوم ١٢ والبداية والنهاية ١١: ٧٢ ومجلة المجمع العلمي العربي ٢٦: ٣٤٦ و ٢٩: ٣٧٤: ٣٠: ٤٥٩ وتراث العرب العلمي ١٥٣ وكشف الظنون ٤٤٧ ومعجم المؤلفين ١: ٢١٨ وطبقات المفسرين ١: ٤١ وهدية العارفين ١: ٥٢ وبروكلمان، الذيل ١: ١٨٧.
(٢) روض البشر ١٨ ومعجم المؤلفين ١: ٢١٨.','1','37',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (83,'رافع الحسيني القاسمي الطهطاوي: مفسر، أديب، من فقهاء الحنفية، ولد في طهطا - بمديرية جرجا بمصر - ونشأ بها، وقدم القاهرة وتخرج في الأزهر، ثم تصدر للتدريس سنة ١٢٩٩ هـ، فاستمر إلى أن توفي. من تصانيفه "شرح الصدر بتفسير سورة القدر" و "بلوغ السول بتفسير: لقد جاءكم رسول" رسالة، و "نفحات الطيب على تفسير الخطيب" (١).

الجابري [.. - ١٠٠٨ هـ / .. - ١٦٠٠ م]

<span data-type="title" id=toc-110>أحمد بن روح الله بن ناصر الدين بن غياث الدين بن سراج الدين الأنصاري الجابري الرومي:</span>
قاضي القضاة بالقسطنطينية، والشام، وادرنة ومصر، مفسر، ولد في بلاد "كنجة وبردعة" من بلاد العجم، وبها نشأ. ورحل إلى القسطنطينية، فدرس بها وبمدينة اسكدار، ثم تدرج في القضاء إلى أن ولي قضاء العسكر بروم إيلي. وتوفي بالقسطنطينية. من تصانفيه "تفسير سورة يوسف" و "تفسير سورة القدر" و "حاشية
_________
(١) الكنز الثمين لعظماء المصريين ١٤١ وصفوة العصر ١: ٥١٠ والتحرير الوجيز ٤٥ والثغر الباسم ٤٢ والأعلام الشرقية ٢: ٧٢ ومعجم المطبوعات ١٢٤٥ والأعلام ١: ١٢١ ومعجم المؤلفين ٢: ١١٩.','1','37',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (84,'على تفسير البيضاوي إلى آخر سورة الأعراف" (١).

زناتي [١٢٨٧ - ١٣٤٨ هـ / ١٨٧٠ - ١٩٢٩ م]

<span data-type="title" id=toc-111>أحمد زناتي:</span>
مدرس مصري. اختاره الخديوي عباس مدرسا لابنائه، ثم معاونا فى ديوانه. وفي سنة ١٩١٣ نقل إلى وزارة المعارف مدرسا فوكيلا للوزارة (سنة ١٩٢٣) واستمر إلى أن توفي. له "الصراط المستقيم" طبع، في تفسير بعض الآيات و "الهداية إلى الصراط المستقيم" طبع، مختصر الأول (٢).

الأحسائي [١١٦٦ - ١٢٤١ هـ / ١٧٥٣ - ١٨٢٦ م]

<span data-type="title" id=toc-112>أحمد بن زين الدين بن إبراهيم بن صقر بن إبراهيم بن داغر بن راشد بن دهيم الصقري المطيرفي الاحسائي البحراني:</span>
مؤسس مذهب الكشفية، ومتفلسف إمامي، كان له اتباع ولهم شطحات وزندقات. ولد في الاحساء، وتعلم ببلاد
_________
(١) خلاصة الأثر ١: ١٨٩ وكشف الظنون ١٩٣، ٤٥٠، ٤٥١ وهدية العارفين ١: ١٥٢ ومعجم المؤلفين ١: ٢٢٤ وتراجم الأعيان ١: ١٦١ والطبقات السنية
(٢) تقويم دار العلوم ١٥٨ ودار الكتب ١: ٥٥ و ٦٥.','1','38',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (85,'فارس، وتنقل بينها وبين العراق، وسكن البحرين، وتوفي وهو متوجه إلى الحج بمنزل هدية قريبا من المدينة، وحمل إليها فدفن فيها. من آثاره "تفسير سورة الإخلاص" (١).

البكري [.. - ١٠٤٨ هـ / .. - ١٦٣٨ م]

<span data-type="title" id=toc-113>أحمد بن زين العابدين بن محمد بن علي البكري الصديقي:</span>
أديب، شاعر، مفسر، من فضلاء الشافعية. من أهل القاهرة، وبها نشأ وتعلم. ثم أقرأ بالجامع الأزهر. قال المحبي: "كانت له اليد الطولى في تفسير القرآن، وعقد مجلسا للتفسير في بيته بالأزبكية .. " من كتبه "حسن الوصف في تفسير سورة الصف" (٢).

الأندرشي [.. - ٧٥٠ هـ / .. - ١٣٤٩ م]

<span data-type="title" id=toc-114>أحمد بن سعد بن محمد العسكري، الأندرشي، الأندلسي، ثم الدمشقي، (ضياء الدين، أبو العباس)</span>
_________
(١) هدية العارفين ١: ١٨٥ وأعيان الشيعة ٨: ٣٩٠ وأعلام الشيعة ٢: ٨٨ وروضات الجنات ١: ٥ والذريعة ٧: ٢٤، و ١٢٥ والأعلام،: ١٢٩ وأنوار البدرين ٤٠٦ ومعجم المؤلفين ١: ٢٢٨.
(٢) خلاصة الأثر ١: ٢٠١ والأعلام ١: ١٢٥ وهدية العارفين ١: ١٥٩.','1','38',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (86,'ضياء الدين، أبو العباس: نحوي، مقرىء، مفسر، ولد في الأندلس في حدود السبعمائة، أو على رأسها، وتعلم بمالقة وغيرها. ثم رحل، فدخل مصر وأخذ عن أبي حيان وجماعة. ثم حج، واستوطن دمشق. قال الصفدي: "شيخ العربية، بدمشق في زمانه" وقال الذهبي: "تخرج به علماء ... وشرع في "تفسير" كبير" (١).

ابن اللورانكي [.. - ٤٦٩ هـ / .. - ١٠٧٦ م]

<span data-type="title" id=toc-115>أحمد بن سعيد بن غالب الأموي، أبو جعفر، ويعرف بابن اللورانكي:</span>
أديب، لغوي، فرضي، عارف بالحديث والتفسير، من فقهاء المالكية. من أهل طليطلة بالأندلس. قال عياض: كان من كبار فقهائها، ومفتيها وأجدر علمائها" امتحن بابن ذي النون يحيى المأمون محنته المشهورة سنة ٤٦٠ هـ، فسجن وأصيب بالعمى وأفرج عنه في أواخر أيامه (٢).
_________
(١) غاية النهاية ١: ٥٦ والشذرات ٦: ١٦٦ وبغية الوعاة ١: ٣٠٩ وطبقات المفسرين ١: ٤١ وهدية العارفين ١: ١١٥ والدرر الكامنة ١: ١٤٥ وهو فيه: أحمد بن سعد بن عبد الله.
(٢) ترتيب المدارك ٤: ٨١٩ والصلة ١: ٦٤ وطبقات المفسرين ١: ٤٤.','1','39',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (87,'شيخ جيون [١٠٤٧ - ١١٣٠ هـ / ١٦٣٧ - ١٧١٨ م]

<span data-type="title" id=toc-116>أحمد بن أبي سعيد بن عبد الله بن عبد الرزاق بن خاصة، المكي، الصالحي، ثم الهندوي، اللكنوي، المعروف بشيخ جيون، أو ملا جيون:</span>
عارف بالحديث، مفسر، أصولي، من فقهاء الحنفية. من أهل "أميتي" بالهند، وبها نشأ وتعلم. وانتقل إلى دلهي، واتصل بالسلطان عالمكير، فأكرمه وقربه. قال في "سبحة المرجان": "وكان الملا ذا حافظة قوية، يحفظ قصيدة طويلة بسماع وقعة واحدة". توفي بدلهي، ونقل جثمانه إلى أميتي. من كتبه "التفسيرات الأحمدية في بيان الآيات الشرعية مع تعريفات المسائل الفقهية" في التفسير (١).

ابن كمال باشا [.. - ٩٤٠ هـ / .. - ١٥٣٤ م]

<span data-type="title" id=toc-117>أحمد بن سليمان بن كمال باشا، شمس الدين:</span>
قاض، فقيه حنفي، مفسر، من العلماء بالحديث ورجاله. ولد في طوقات
_________
(١) سبحة المرجان ٧٩ ومعجم المطبوعات ١١٦٤ وهدية العارفين ١: ١٧٥ إيضاح المكنون ١: ٣٠١ وفيه أن التفسيرات الأحمدية هي لأحمد المكي صاحب بغية الطالبين المتوفي سنة ١٣٢٧ هـ، وفي صفحة ١٨٨ ذكر أن البغية هي لأحمد المكي المتوفي سنة ١١٣٠ هـ. قلت: التاريخ الأول خطأ، والصواب التاريخ الأخير، والتفسيرات هي لصاحب الترجمة.','1','39',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (88,'من نواحي سيواس، وتعلم بأدرنة، ودرس بها وبأسكوب، ثم ولي قضاء أدرنة فقضاء العسكر في ولاية أناطولي. وعزل، فولي مدرسة دار الحديث بأدرنة، فالافتاء بالقسطنطينية إلى أن مات. من مؤلفاته الكثيرة "تفسير القرآن" بلغ فيه إلى سورة الصافات، قال في كشف الظنون: وهو تفسير لطيف فيه تحقيقات شريفة وتصرفات عجيبة". وتفسير "سورة الملك" و "حواش" على الكشاف في التفسير أيضا، قال صاحب كشف الظنون: وهو من أحسن تأليفاته (١).

البلخي [٢٣٥ - ٣٢٢ هـ / ٨٤٩ - ٩٣٤ م]

<span data-type="title" id=toc-118>أحمد بن سهل، أبو زيد البلخي:</span>
الملقب "الجاحظ الثاني" أحد كبار المشاهير من علماء الإسلام، كان يسلك في مصنفاته مسلك الفلاسفة، إلا أنه كان بأهل الأدب أشبه. ولد بقرية شامستيان - قرية ببلخ، واشتغل بالتعليم، ثم دخل بغداد وأخذ
_________
(١) شذرات الذهب ٨: ٢٣٨ والفوائد البهية ٢١ والشقائق النعمانية ٢٢٦ والكواكب السائرة ٢: ١٠٧ وهدية العارفين ١: ١٤١ وعقود الجوهر ٢١٧ وكشف الظنون ٤٣٩ و ٤٥٠ و ١٤٨١، ودائرة المعارف الإسلامية في: كمال باشا زاده. والأعلام ١: ١٣٠ ومعجم المؤلفين ١: ٢٣٨.','1','40',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (89,'عن العلماء، وطوف بالبلاد المجاورة، وتتلمذ على الكندي الفيلسوف، ثم عاد وقد علت شهرته، فعرض عليه أحمد بن سهل المروزي حاكم تخوم بلخ وزارته فأباها، وذكر له الكتابة فرضيها. وكان شيعيا إماميا ثم عدل. واتهم بالالحاد، ولكن الكثيرين برءوه. يعد رأس مدرسة في الجغرافية العربية لعنايته بالخرائط في كتابه "صور الأقاليم الإسلامية". مات ببلخ. من كتبه "تفسير الفاتحة" و "الحروف المقطعة في أوائل السور" و "ما أغلق من غريب القرآن" و "نظم القرآن" و "بيان ان سورة الحمد تنوب عن جميع القرآن" و "قوارع القرآن" (١).

أبو حريبة [١٢٠٨ - ١٢٦٨ هـ / ١٧٩٣ - ١٨٥١ م]

<span data-type="title" id=toc-119>أحمد الشنتناوي - نسبة لقرية شنتنا بالمنوفية في مصر - المعروف بأبي حريبة:</span>
مفسر، صوفي، تعلم بالقاهرة وأقام بها إلى أن توفي. له "فتح الرحمن في معاني
_________
(١) الوافي ٦: ٤٠٩ ومعجم الأدباء ١: ١٤١ وبغية الوعاة ١: ٣١١ ولسان الميزان ١: ١٣٨ وابن النديم ١٨٣ وطبقات المفسرين للداودي ١: ٤٢ وحكماء الإسلام ٢٢ والامتاع والمؤانسة ٢: ١٥ وهدية العافرين ١: ٥٩ وبروكلمان، الذيل ١: ٤٠٨ والأعلام ١: ١٣١ وأعيان الشيعة ١٠: ٣٩٦ ومعجم المؤلفين ١: ٢٤٠.','1','40',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (90,'القرآن" تفسير، مخطوط، في المكتبة التيمورية (١).

ابن أبي الرجال [١٠٢٩ - ١٠٩٢ هـ / ١٦٢٠ - ١٦٨١ م]

<span data-type="title" id=toc-120>أحمد بن صالح بن محمد بن أحمد بن صالح المعروف بابن أبي الرجال:</span>
عالم زيدي، مشارك في التاريخ والتفسير والنحو والأصول والصرف والمعاني والبيان. من أهل صنعاء مولدا ووفاة. ولي القضاء، وأقرأ أولاد الإمام المهدي العباس بن الحسن، قال في البدر الطالع: "وارتفعت درجته عند الإمام، وكان يجالسه ويحادثه، ورفع منزلته حتى كان تارة بمنزلة الوزير وأخرى بمنزلة المشير .. ". من آثاره "حاشية" على تفسير الكشاف، للزمخشري (٢).

ابن الصيرفي [٨٢٩ - ٩٠٥ هـ /١٤٢٦ - ١٥٠٠ م]

<span data-type="title" id=toc-121>أحمد بن صدقة بن أحمد بن حسين بن عبد الله بن محمد بن محمد العسقلاني، (المكي الأصل، القاهري، شهاب الدين، أبو الفضل، ويعرف بابن الصيرفي):</span>
المكي الأصل، القاهري، شهاب الدين، أبو الفضل، ويعرف بابن الصيرفي: فقيه شافعي، لغوي، مقرىء، مفسر، محدث، أديب، ولد بالقاهرة، وتعلم بها، وأخذ عن جماعة.
_________
(١) الخزانة التيمورية ٣: ٧٤ والأعلام ١: ١٣٦.
(٢) البدر الطالع ١: ٦١: خلاصة الأثر ١: ٢٢٠ وهدية العارفين ١: ١٧٩ ودائرة المعارف الإسلامية ١: ٧٥ والاعلام ١: ١٣٧ ومعجم المؤلفين ١: ٢٥٣','1','41',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (91,'قال السخاوي: "ودرس وأفتى، وأسمع الحديث بالطيبرسية، ثم ولي مشيختها، وأخذ عنه الفضلاء بالقاهرة ومكة، وناب في القضاء، ودرس التفسير بالبرقوقية ... " ومات بالقاهرة. قال الداودي: "وصنف التصانيف المفيدة، منها "تفسير مزج على القرآن العظيم" (١).

ابن تيمية [٦٦١ - ٧٢٨ هـ / ١٢٦٣ - ١٣٢٨ م]

<span data-type="title" id=toc-122>أحمد بن عبد الحليم بن عبد السلام بن عبد الله بن أبي القاسم الخضر بن محمد النميري الحراني الدمشقي، أبو العباس، تقي الدين ابن تيمية:</span>
الإمام العالم العلامة، المفسر الفقيه المجتهد الحافظ المحدث، شيخ الإسلام، نادرة العصر. ولد في حران، وتحول به أبوه إلى دمشق، عند استيلاء التتار على البلاد (سنة ٦٦٧ هـ). وفيها أقبل على العلوم الإسلامية يحصلها،
_________
(١) الضوء اللامع ١: ٣١٦ وطبقات المفسرين ١: ٤٤ وهدية العارفين ١: ١٣٧ ومعجم المؤلفين ١: ٢٥٣ وبدائع الزهور ٢: ٣٦٥.','1','41',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (92,'وأمده الله بكثرة الكتب وسرعة الحفظ وقوة الإدراك والفهم، فألم بالفقه والتفسير والحديث والحساب وغيرها، وهو ابن بضع عشرة سنة، وناظر وجادل وأفتى وهو ابن سبع عشرة سنة، وقام بوظائف أبيه - بعد وفاته - فدرس بدار الحديث وهو ابن إحدى وعشرين سنة، ولقب بمحيي السنة، وإمام المجاهدين، وهو ابن ثلاثين. قال الذهبي: "كان من بحور العلم، ومن الأذكياء المعدودين، والزهاد الأفراد، والشجعان الكبار، والكرماء الأجواد، أثنى عليه الموافق والمخالف، حدث بدمشق ومصر والثغر .. " امتحن وأوذي مرات، طلب أولا إلى مصر أيام الأمير ركن الدين بيبرس الجاشنكير، من أجل فتوى أفتى بها، فتعصب عليه جماعة من أهلها، فسجن مدة في خزانة البنود، ثم نقل إلى الاسكندرية. وأطلق سراحه، فسافر إلى دمشق سنة ٧١٢ هـ. واعتقل بها سنة ٧٢٠ هـ، وأطلق، ثم أعيد إلى قلعة دمشق سنة ٧٢٦ هـ، فلم يزل معتقلا بها إلى أن مات صابرا، فخرجت دمشق كلها فى جنازته. أما آثاره في تفسير القرآن الكريم، فجملة ما وصل إلينا منها أربعة مجلدات، تضم تفسيره لبعض الآيات ولبعض السور القصار، منها سور "الأعلى" و "الشمس" و "الليلا" و "العلق" و "التن" و "الكافرون" و "تبت" والمعوذتي"، و','1','42',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (93,'"البينة، وغيرها. وله أيضا "مقدمة في أصول التفسير، حققها ونشرها الأستاذ الدكتور عدنان زرزور (١).

الوارثي [.. - ١٠٤٥ هـ / ١٦٣٥ م]

<span data-type="title" id=toc-123>أحمد بن عبد الرحمن بن محمد البكري الصديقي، المعروف بالوارثي:</span>
قاضي القضاة بمصر، مفسر، عارف بالحديث، أديب، شاعر، من أهل القاهرة مولدا ووفاة. من كتبه "الأجوبة عن الأسئلة لابن عبد السلام" في التفسير، و "تفسير" بعض المفصل من السور (٢).
_________
(١) حياة شيخ الإسلام ابن تيمية، للبيطار، وابن تيمية حياته وعصره، لأبي زهرة، وابن تيمية لمحمد يوسف موسى، والعقود الدرية في مناقب ابن تيمية، لابن عبد الهادي، وأسماء مؤلفات ابن تيمية، لابن القيم. والدرر الكامنة ١: ١٥٤ والبداية والنهاية ١٤: ١٦٣ وطبقات المفسرين ١: ٤٥ والبدر الطالع ١: ٦٣ والوافي ٧: ١٥ وتاريخ ابن الوردي ٢: ٢٨٤ والنجوم الزاهرة ٩: ٢٧١ وتذكرة الحفاط ١٤٩٦ وذيل طبقات الحنابلة ٢: ٣٨٧ وفوات الوفيات ١: ٧٤ والمنهل الصافي ١: ٣٣٦ ومرآة الجنان ٤: ٢٧٧ ودائرة المعارف الإسلامية ١: ١٥٩ وتاريخ آداب اللغة العربية ٣: ٢٤٣ والدارس ١: ٧٥ والمجددون في الإسلام ٢٦٢ وكنوز الأجداد ٣٦٠ وأعيان العصر ١٧٤ والأعلام ١: ١٤١ ودائرة المعارف للبستاني. ومعجم المؤلفين ١: ٢٦١.
(٢) خلاصة الأثر ١: ٢٣٤ واليواقيت الثمينة ١: ٣٠ وهدية العارفين ١: ١٥٨ وفهرس التيمورية ٣: ٣١٢ والأعلام ١: ١٤٧ وخطط مبارك ٣: ١٢٨.','1','42',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (94,'أحمد المجاهد [١٢٢٤ - ١٢٨١ هـ / ١٨٠٩ - ١٨٦٤ م]

<span data-type="title" id=toc-124>أحمد بن عبد الرحمن بن عبد الله بن حسين بن علي المجاهد:</span>
قاض، عارف بالتفسير، أصولي، من فقهاء الزيدية بصنعاء، انتهت إليه رياسة التدريس والفتوى فيها من مؤلفاته "فتح الله الواحد على عبده أحمد المجاهد" مقدمة في علم التفسير (١).

ابن العراقي [٧٦٢ - ٨٢٦ هـ / ١٣٦١ - ١٤٢٣ م]

<span data-type="title" id=toc-125>أحمد بن عبد الرحيم بن الحسين بن عبد الرحمن الكردي الرازياني ثم المصري، أبو زرعة، ولي الدين، ابن العراقي:</span>
قاضي الديار المصرية، فقيه شافعي، أصولي، عارف بالتفسير والحديث والأدب. ولد بالقاهرة. رحل به أبوه (الحافظ العراقي) إلى دمشق وبيت المقدس فقرأ فيهما على جماعة من كبار الشيوخ. وعاد، فأخذ عن شيوخ الديار المصرية، ودخل دمشق ثانية، وحج غيرة مرة، فسمع من شيوخ مكة والمدينة. أفتى ودرس، وناب في القضاء نحو عشرين سنة. ثم ولاه الملك الظاهر قطز (٧٦٩ - ٨٢٤ هـ) قضاء الديار
_________
(١) نيل الوطر ١: ١١١ والدر الفريد ٢٣ والأعلام ١: ١٤٣ ومعجم المؤلفين ١: ٢٦٦.','1','43',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (95,'المصرية في منتصف شوال سنة ٨٢٤ هـ، بعد الجلال البلقيني، وحمدت سيرته، واستمر في القضاء حتى صرف في ٦ ذي الحجة سنة ٨٢٥ هـ، في أيام الأشرف برسباي، لعدم محاباته لأحد من أجله، وتصميمه في أمور لا يحتملها أهل الدولة. فكانت مدة ولايته سنة ودون شهرين. توفي بالقاهرة. ترجم له السخاوي ترجمة مطولة وأثنى عليه. من آثاره "حاشية" على الكشاف للزمخشري، قال صاحب كشف الظنون: "في مجلدين، لخص فيها كلام ابن المنير وعلم الدين العراقي وأبي حيان وأجوبة السمين الحلبي والسفاقسي ح زيادة تخريج أحاديثه .. " (١).

شاه ولي الله [١١١٠ - ١١٧٦ هـ / ١٦٩٩ - ١٧٦٢ م]

<span data-type="title" id=toc-126>أحمد بن عبد الرحيم بن وجيه الدين بن معظم بن منصور الفاروقي الدهلوي الهندي، أبو عبد العزيز، الملقب شاه ولي الله:</span>
محدث، مفسر، من فقهاء الحنفية. من أهل دهلي بالهند، وبها نشأ وتعلم. زار الحجاز سنة ١١٤٣ - ١١٤٥ هـ، ولقي
_________
(١) الضوء اللامع ١: ٣٣٦ والبدر الطالع ١: ٧٢ والشذرات ٧: ١٧٣ وحسن المحاضرة ١: ٢٠٦ وكشف الظنون ١٤٨٠ وفهرس الفهارس ٢: ٤٣٥ والمنهل الصافي ١: ٣١٢ ولحظ الألحاظ ٢٨٤ والأعلام ١: ١٤٤ ومعجم المؤلفين ١: ٢٧١.','1','43',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (96,'جماعة من العلماء، ثم عاد إلى الهند، ودرس وتوفي في دهلي: قال صاحب فهرس الفهارس: "أحيا الله به وبأولاده وأولاد بنته وتلاميذهم الحديث والسنة بالهند بعد مواتهما، وعلى كتبه وأسانيده المدار في تلك الديار". من كتبه "الفوز الكبير في أصول التفسير" طبع، و "فتح الخبير بما لا بد من حفظه في علم التفسير" طبع، وترجم القرآن إلى الفارسية على شاكلة النظم العربي، وسمى كتابه "فتح الرحمن في ترجمة القرآن". وفي "اليانع الجني" عند ذكره لهذا الكتاب "وقد نسج على منواله ابنه عبد القادر فأحسن الترجمة إلى الهندية للقرآن اقتباسا من مشكاته، ولقد سهل الترجمة من بعده على الناس قدوة به وبمن تبعه، وهو أول من أتقن هذا الفن ودون أصوله" (١).

الأيوبي [.. - ١٢٨٩ هـ / .. - ١٨٧٢ م]

<span data-type="title" id=toc-127>أحمد بن عبد العزيز بن حسين بن خليل الأيوبي:</span>
مفسر، من المشتغلين بالحديث
_________
(١) فهرس الفهارس ١: ١٢٥ و ٢: ٤٣٧ وهدية العارفين ٢: ٥٠٠ ومجلة الحج س ١١ عدد ٧: ٤٤٧ والمجددون في الإسلام ٤٤٢ ومعجم المطبوعات ٨٩٠ وأبجد العلم ٩١٢ واليانع الجني ٧٩ وحديقة الافراح ٢١٩ والأعلام ١: ١٤٤ ومعجم المؤلفين ١: ٢٧٢ و ١٣: ١٦٩ وهو في بعض هذه المصادر ولي الله بن عبد الرحيم، وولادته سنة ١١١٤ هـ ووفاته سنة ١١٧٤ او ١١٧٩ أو ١١٨٠ هـ.','1','44',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (97,'ورجاله، من آثاره "سهولة البيان في تفسير القرآن" مخطوط، فرغ من تأليفه سنة ١٢٨٨ هـ (١).

الغزي [.. - ١١٥٠ هـ / .. - ١٧٣٧ م]

<span data-type="title" id=toc-128>أحمد بن عبد الغزي:</span>
محدث، مفسر، صوفي، شاعر، سكن بروسة وتوفي بها. قال صاحب هدية العارفين: "له "تبيين المقامات وتزيين المراتبات" تفسير القرآن" (٢).

ابن مكتوم [٦٨٢ - ٧٤٩ هـ / ١٢٨٤ - ١٣٤٨ م]

<span data-type="title" id=toc-129>أحمد بن عبد القادر بن أحمد بن مكتوم ابن أحمد بن محمد بن سليم القيسي، أبو محمد، تاج الدين:</span>
نحوي، لغوي، عارف بالتفسير والحديث، عالم بالتراجم وفقه الحنفية. من أهل القاهرة مولدا ووفاة. أخذ عن ابن النحاس والدمياطي وغيرهما، ولازم أبا حيان مدة طويلة. وناب في الحكم بالقاهرة. من كتبه "الدر اللقيط من البحر المحيط" مخطوط، في تفسير القرآن، قال الصفدي: "ملكته بخطه في مجلدين، التقط فيه إعراب "البحر المحيط، لشيخنا أثير الدين فجاء في
_________
(١) معجم المؤلفين ١: ٢٧٥ والأعلام ١: ١٥١.
(٢) هدية العارفين ١: ١٧٢ ومعجم المؤلفين ١: ٢٧٧.','1','44',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (98,'غاية الحسن، وقد اشتهر هذا الكتاب، وورد إلى الشام، ونقلت به النسخ". وقال ابن حجر العسقلاني: "قصره على مباحث أبي حيان مع ابن عطية والزمخشري" (١).

النعيمي [٩٢٤ - ٩٩٨ هـ / ١٥١٨ - ١٥٩٠ م]

<span data-type="title" id=toc-130>أحمد بن عبد القادر بن محمد بن عمر النعيمي، الشافعي ثم الحنفي الدمشقي، بهاء الدين:</span>
فقيه، خطيب، عارف بالتفسير والأدب، من أهل دمشق وبها نشأ وتعلم. رحل إلى القسطنطينية وولي الإمامة بجامع آيا صوفيا، ومات بها. من آثاره "جواهر القرآن" (٢).

الترمانيني [١٢٠٨ - ١٢٩٣ هـ / ١٧٩٣ - ١٨٩٦ م]

<span data-type="title" id=toc-131>أحمد بن عبد الكريم بن عيسى بن أحمد (نعمة الله الترمانيني)</span>
_________
(١) الدرر الكامنة ١: ١٨٦ والمنهل الصافي ١: ٣١٧ وكشف الظنون ٢٢٦ و ٧٣٢، ٤٧٧ وبغية الوعاة ١: ٣٢٦ والوافي ٧: ٧٤ وروضات الجنات ٨٤ وحسن المحاضرة ٤٧٠ وتاج التراجم ١٢ وطبقات القراء للذهبي ٢: ٦٠٢ والطبقات السنية ٤٤٠ وطبقات المفسرين ١: ٥١ والجواهر المضية ١: ٧٥ وشدرات الذهب ١٥٩ وهدية العارفين ١: ١١٠ والمكتبة الأزهرية ١: ٢٢٧ وفهرس المخطوطات المصورة ٢: ٢١٣ وبروكلمان ٢: ١١٠ والذيل ٢: ١٣٧ والأعلام ١: ١٥٣ ومعجم المؤلفين ١: ٢٧٨.
(٢) الكواكب السائرة ٣: ١١٩ وبروكلمان الذيل ٢: ٤٥٢.','1','45',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (99,'نعمة الله الترمانيني: عارف بالتفسير، منطقي، نحوي، من الزهاد العباد. ولد في ترمانين - من ترى حلب - وتعلم بالأزهر. وعاد إلى حلب فتصدر للافتاء والتدريس بها. من كتبه "حاشية" على تفسير البيضاوي، سماها "تلخيص العبارات الرائقة" و "حاشية" على تفسير الجلالين (١).

محب الدين الطبري [٦١٥ - ٦٩٤ هـ / ١٢١٨ - ١٢٩٥ م]

<span data-type="title" id=toc-132>أحمد بن عبد الله بن محمد بن أبي بكر بن محمد بن إبراهيم، أبو العباس، محب الدين الطبري:</span>
شيخ الحرم المكي وحافظ الحجاز وعالمه، عارف بالتفسير، من أعيان فقهاء الشافعية. ولد بمكة، وأخذ عن مشيختها. وأفتى ودرس وحدث، وسمع عليه غير واحد من المشايخ والأعيان. ومات بمكة. من كتبه "الكافي في غريب القرآن" و "تفسير القرآن" لم يكمله، وكتاب يتضمن ترتيب القرآن على السور (٢).
_________
(١) إعلام النلاء ٧: ٣٧٢ وحلية البشر. وأدباء حلب ٣٢ وفيه ولادته سنة ١٢٠٤ والاعلام ١: ١٤٨ ومعجم المؤلفين ١: ٢٨١.
(٢) شذرات الذهب ٥: ٤٢٥ والمنهل الصافي ١: ٣٢٠ وتذكرة الحفاظ ١٤٧٤ والنجوم الزاهرة ٨: ٧٤ ومختصر دول الإسلام ٢: ١٥٣ ومرآة الجنان ٤: ٢٢٤ وطبقات السبكي ٥: ٨ والوافي ٧: ١٣٥ =','1','45',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (100,'ابن البابا [.. - ٧٤٩ هـ / .. - ١٣٤٨ م]

<span data-type="title" id=toc-133>أحمد بن عبد الله (أبي الفرج) بن شهاب الدين، التجيبي، المعروف بابن البابا، شهاب الدين:</span>
فقيه شافعي، عالم بالحديث والتفسير والأصول والكلام والنحو والطب والموسيقى، من أهل مضر، أخذ عن ابن دقيق العيد وغيره، ودرس الحديث بالقبة من خانقاه بيبرس وبالجامع الأزهر. وله شعر. مات مطعونا (١).

ابن المتوج [.. - ٨٢٠ هـ / .. - ١٤١٧ م]

<span data-type="title" id=toc-134>أحمد بن عبد الله بن محمد، أبو الناصر. جمال الدين، المعروف بابن المتوج:</span>
مفسر، أديب، من فقهاء الشيعة الإمامية، من أهل البحرين. من كتبه "تفسير القرآن المجيد" و "الناسخ والمنسوخ من القرآن" و "منهاج الهداية في شرح آيات الأحكام الخمسماية" (٢).
_________
= والأعلام ١: ١٥٣ ومعجم المؤلفين ١: ٢٩٨ وكشف الظنون ٤٦٥ و ١٢٠٤ وفيه اسم كتاب "الكافي": تقريب المرام في غريب القاسم بن سلام ومثله في هدية العارفين ١: ١٠١ وقيل في ولادته سنة ٦١٠ وسنة ٦١٤ هـ.
(١) طبقات المفسرين للداودي ١: ٥٠ وقيل تذكرة الحفاظ ١٢٨.
(٢) أعيان الشيعة ٩: ٣٨ وروضات الجنات ١٩ وهدية العارفين ١: ١١٩ وفيه وفاته سنة ٨١٠ هـ. والأعلام ١: ١٥٣ ومعجم المؤلفين ١: ٣٠٠ وإيضاح المكنون ١: ٣٠٣ و ٢: ٣٤٧.','1','46',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (101,'الغزي [٧٧٠ - ٨٢٢ هـ / ١٣٦٨٦ - ١٤١٩ م]

<span data-type="title" id=toc-135>أحمد بن عبد الله بن بدر بن مفرج بن بدر، أبو نعيم، شهاب الدين العامري الغزي ثم الدمشقي:</span>
فقيه شافعي، أصولي، مشارك في بعض العلوم. ولد ونشأ بغزة (بفلسطين)، وانتقل إلى دمشق بعد سنة ٧٨٠ هـ، فتعلم بها وبالقدس. ولي افتاء دار العدل والتدريس في عدة أماكن. قال السخاوي: وتفرد برياسة الفتوى بدمشق فلم يبق في أواخر عمره من يقاربه في رياسة الفقه، وصار من مفاخر دمشق وأذكر أهلها للفقه وأصله .. ". ثم حج وجاور بمكة ومات فيها. من كتبه "حاشية على أنوار التنزيل" في التفسير، للبيضاوي (١).

القريمي [.. - ٨٧٩ هـ / .. - ١٤٧٤ م]

<span data-type="title" id=toc-136>أحمد بن عبد الله القريمي:</span>
أديب بالعربية والفارسية والتركية، عالم مشارك في بعض العلوم، أصله من القريم. وفي أيامه فتح السلطان محمد (الفاتح) استنابول وقضى على مملكة الرومان، فكان القريمي من المقربين إليه. توفي باستانبول ودفن في
_________
(١) الضوء اللامع ١: ٣٥٦ ومعجم أعلام فلسطين (مخطوط) للمؤلف، والبدر الطالع ١: ٧٥ وشذرات الذهب ٧: ١٥٣ والمنهل الصافي ١: ٣٢٩.','1','46',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (102,'جوار قبر الفاتح. من آثاره "مصباح التعديل في كشف أنوار التنزيل" حاشية على الببضاوي، مخطوطة (١).

المؤذن اليسابوري [٣٨٨ - ٤٧٠ هـ / ٩٩٨ - ١٠٧٨ م]

<span data-type="title" id=toc-137>أحمد بن عبد الملك بن علي بن أحمد بن عبد الصمد بن بكر، أبو صالح، المؤذن النيسابوري:</span>
محدث وقته بخراسان، مؤرخ مفسر، سمع ببلده، ثم تنقل في البلدان، فسمع بجرجان ودمشق وأصبهان ومنبج ومكة وبغداد. قال ياقوت في حقه: "الحافظ الأمين، الفقيه المفسر، المحدث الصوفي، نسيج وحده، في طريقته وجمعه وإفادت". وقال السمعاني: "كان يؤذن على منارة المدرسة البيهقية سنين احتسابا" (٢).

ابن عبدون [.. - ٤٢٣ هـ / .. - ١٠٣٢ م]

<span data-type="title" id=toc-138>أحمد بن عبد الواحد بن أحمد البزار، أبو عبد الله، المعروف بابن عبدون، وبابن (الحاشر)</span>
_________
(١) هدية العارفين ١: ١٣١ وكشف الظنون ١٩٢ و ١٥٤٥ وعثمانلي مؤلفلري ١: ٣٩٧ والشقائق النعمانية.
(٢) تذكره الحفاظ ١١٦٢ وتاريخ بغداد ٤: ٢٦٧ ومعجم الادباء ٣: ٢٢٤ والمنتظم ٨: ٣١٤ والنجوم الزاهرة ٥: ١٠٦ والبداية والنهاية ١٢: ١١٨ والوافي ٧: ١٥٦ وهدية العارفين ١: ٧٩ والأعلام ١: ١٥٧.','1','47',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (103,'الحاشر: فقيه إمامي، مفسر، مؤرخ، من أهل بغداد. أخذ عنه الطوسي. من كتبه. "تفسير القرآن" (١).
أحمد بن عصية = أحمد بن حامد.

النسائي [٢١٥ - ٣٠٣ هـ / ٨٣٠ - ٩١٥ م]
أحمد بن علي بن شعيب بن علي بن سنان ابن بحر بن دينار، أبو عبد الرحمن النسائى: الحافظ الإمام، شيخ الإسلام صاحب السنن. من أهل نسا بخراسان. بدأ رحلاته الدراسية سنة ٢٣٠ هـ، فسمع من كبار علماء الحديث فيه خراسان والعراق والحجاز ومصر والشام والجزيرة، وأقام بمصر وقتا طويلا، فحسده مشايخها، فخرج إلى الرملة بفلسطين سنة ٣٠٢ هـ، فسئل عن فضائل معاوية، فقال: ألا يرضى معاوية أن يخرج رأسا برأس، حتى يفضل؟؟ وفي رواية أخرى: ما أعرف له فضيلة إلا "لا أشبع الله بطنك، فضربوه في الجامع وأخرجوه عليلا، فمات، ودفن ببيت المقدس. وفي رواية أنه خرج من مصر إلى دمشق، فلما امتحن بها، قال: احملوني إلى مكة، فحمل إليها فتوفي بها. قال الدارقطني: وهو مدفون بين الصفا
_________
(١) أعيان الشيعة ٩: ٥٠ وهدية العارفين ١: ٧٣.','1','47',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (104,'والمروة". ويروى أنه كان قاضيا. وهو أقل رواة السنن أحاديث ضعيفة، وأغلب كتبه في الحديث، وأهمها "السنن" ويضم - بصفة أساسية تلخيصا لكتب الحديث التي وجدت في عصره. له كتاب في "التفسير" مخطوط، في مكتبة جامعة استنبول (١).

ابن الإخشاذ [٢٧٠ - ٣٢٦ هـ / ٨٨٣ - ٩٣٦ م]

<span data-type="title" id=toc-139>أحمد بن علي بن بيعجور، أبو بكر بن الإخشاذ:</span>
من رؤساء المعتزلة وزهادهم في زمانه، عارف بالتفسير، من أهل بغداد، وبها توفي. قال ابن النديم: "كان فصيحا له معرفة بالعربية والفقه". من تصانيفه "نقل القرآن" و "اختصار تفسير الطبري" (٢).
_________
(١) تاريخ التراث العربي ١: ٤٢٢ ووفيات الأعيان ١: ٧٧ وطبقات السبكي ٢: ٨٣ وتذكرة الحفاظ ٦٩٨ وخلاصة تهذيب الكمال ١: ٦ والبداية والنهاية ١١: ١٢٣ ومرآة الجنان ٢: ٢٤٠ والشذرات ٢: ٢٣٩ وحسن المحاضرة ١: ١٩٧ والتهذيب ١: ٣٦ والأنساب ٥٥٩ والرسالة المستطرفة ١٠ الموسوعة العربية ١٨٣١ والعبر ٢: ١٢٣ ومختصر دول الإسلام ١: ١٣٥ والنجوم الزاهرة ٣: ١٨٨ ومفتاح السعادة ٢: ١١ وأعيان الشيعة ٨: ٤٤٤ ومعجم المطبوعات ١٨٥١ والمختصر في أخبار البشر ٢: ٧٧ وروضات الجنات ٥٨ والأعلام ١: ١٦٤ ومعجم المؤلفين ١: ٢٤٤.
(٢) الوافي ١: ٧: ٢١٦ وابن النديم ٢٢٠ وتاريخ بغداد ٤: ٣٠٩ وهدية العارفين ١: ٦٠ ولسان الميزان ١: ٢٣١ =','1','48',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (105,'الجصاص [٣٠٥ - ٣٧٠ هـ / ٩١٨ - ٩٨١ م]

<span data-type="title" id=toc-140>أحمد بن علي الرازي، أبو بكر الجصاص:</span>
إمام أصحاب الرأي في وقته، كان مشهورا بالزهد والورع، من أهل الري. انتقل إلى بغداد سنة ٣٢٥ هـ وتفقه على أبي الحسن الكرخي، ثم تصدر للتدريس. ترك بغداد مرتين، مرة إلى الأهواز، وأخرى إلى نيسابور، وعاد إلى بغداد سنة ٣٤٤ هـ وأقام بها إلى أن توفي. قال الخطيب البغدادي: انتهت إليه رياسة الحنفية، ورحل إليه المتفقهة، وخوطب في أن يلي القضاء فامتنع. من كتبه "أحكام القرآن" طبع (١).

المهرجاني [.. - قبل ٣٨٠ هـ / .. - قبل ٩٩٠ م]

<span data-type="title" id=toc-141>أحمد بن علي المهرجاني:</span>
مقرىء،
_________
= وفيه: يقال الاخشيد والأخشاد، كأن الشين ممالة. والأعلام ١: ١٦٥.
(١) تاريخ بغداد ٤: ٣١٤ والمنتظم ٧: ١٠٥ وتاريخ التراث العربي ٢: ٩٦ وتذكرة الحفاظ ٩٥٩ والفوائد البهية ٢٧ والجواهر المضية ١: ٨٤ والنجوم الزاهرة ٤: ١٣٨ والوافي ٧: ٢٤١ ومفتاح السعادة ٢: ٥٣ والشذرات ٣: ٧١ والعبر ٢: ٣٥٤ وتاج التراجم ٦ وطبقات المفسرين للداودي ١: ٥٥ والبداية والنهاية ١١: ٢٩٧ والأعلام ١: ١٦٥ ومعجم المؤلفين ٢: ٧ وفهرست ابن النديم.','1','48',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (106,'مفسر، ذكره ابن النديم وقال: له كتاب في "جوابات القرآن" ولم يزد على ذلك (١).

الباغاني [٣٤٥ - ٤٠١ هـ / ٩٥٦ - ١٠١١ م]

<span data-type="title" id=toc-142>أحمد بن علي بن أحمد بن محمد بن عبد الله الربعي، أبو العباس الباغاني:</span>
محدث، مفسر، مقرىء، من فقهاء المالكية، ولد بمدينة باغاية (بالجزائر) ودخل الأندلس سنة ٣٧٦ هـ، فأقرأ بالمسجد الجامع بقرطبة، واستأدبه المنصور محمد بن أبي عامر لابنه عبد الرحمن ثم أقصاه، ثم رقاه المؤيد بالله ابن الحكم في دولته الثانية إلى خطة الورى. رحل إلى المشرق فروى بمصر عن أبي الطيب بن غلبون، وأبي بكر الأدفوي وغيرهما. قال الداودي: "كان بحرا من بحور العلم، لا نظير له في حفظ القرآن قراءاته وإعرابه وأحكامه وناسخه ومنسوخه، وله كتاب حسن في "أحكام القرآن" نحا فيه نحوا حسنا وهو على مذهب مالك" (٢).
_________
(١) الفهرست ٤١ وطبقات المفسرين ١: ٥٥.
(٢) معجم أعلام الجزائر ٣٦١ ومعجم البلدان مادة باغاية، والديباج ٣٨ وهدية العارفين ١: ٧٠ وإيضاح المكنون، ١: ٣٦ وطبقات المفسرين ١: ٥٣ وترتيب المدارك ٤: ٦٨٠ والصلة ١: ٨٧.','1','49',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (107,'الخطيب البغدادي [٣٩٢ - ٤٦٣ هـ / ١٠٠٢ - ١٠٧٢ م]

<span data-type="title" id=toc-143>أحمد بن علي بن ثابت بن أحمد بن مهدي، أبو بكر، المعروف بالخطيب:</span>
مؤرخ بغداد ومحدثها، انتهت إليه الرياسة في الحفظ والإتقان والقيام بعلوم الحديث وحسن التصنيف. ولد في درزيجان من قرى العراق، ونشأ في بغداد، وسمع بها، ثم رحل، فسمع شيوخ وقته بالبصرة ونيسابور وأصبهان والدينور وهمذان والكوفة والري والحرمين ودمشق (سنة ٤٤٥ هـ) ثم حج وعاد إلى بغداد، فقربه وزير القائم بأمر الله العباسي، رئيس الرؤساه أبو القاسم ابن المسلمة، وعرف قدره، ثم كانت فتنة استيلاء البساسيري على بغداد، ودعوته للفاطمين، وقتله لابن المسلمة، فخرج الخطيب على أثرها مستترا إلى دمشق فوصلها سنة ٤٥١ هـ، وخرج منها إلى صور في صفر سنة ٤٥٧ هـ، وكان يزور منها القدس ويعود، إلى أن عاد إلى بغداد سنة ٤٦٢ هـ بعد أن أقام في طرابلس وحلب أياما، ولما مرض مرضه الأخير في رمضان سنة ٤٦٣ هـ وقف كتبه وفرق جميع ماله في وجوه البر وعلى أهل العلم والحديث. له ٧٩ كتابا، منها "السابق واللاحق" في تفسير القرآن (١).
_________
(١) الوافي ٧: ١٩٠ وفيات الأعيان ١: ٩٢ وتهذيب =','1','49',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (108,'المرسي [.. - ٥٤٢ هـ / .. - ١١٤٨ م]

<span data-type="title" id=toc-144>أحمد بن علي بن أحمد بن يحيى بن خلف بن أفلح بن رزقون، أبو العباس المرسي:</span>
مقرىء كبير، محدث، مفسر، نحوي، من فقهاء المالكية، من أهل مرسية بالأندلس. رحل في طلب العلم فأخذ عن كبار الشيوخ في بلنسية وشاطبة وقرطبة ومالقة واشبيلية، ثم استوطن الجزيرة الخضراء. ولي القضاء بكورة أركش، ثم صرف عنه، فلازم الاقراء واسماع الحديث بمسجد الجزيرة الخضراء إلى أن توفي. قال ابن عبد الملك المراكشي: "كان مقرئا مفسرا محدثا فقيها مشاورا نحويا عدديا" (١).
_________
= ابن عساكر ١: ٣٩٩ والمنتظم ٨: ٢٦٥ والعبر ٣: ٢٥٣ وشذرات الذهب ٣: ٣١١ وطبقات السبكي ٣: ١٢ ومعجم الأدباء ٤: ١٣ وتذكرة الحفاظ ١١٣٥ وروضات الجنات ٧٨ والنجوم الزاهرة ٥: ٨٧ ومرآة الجنان ٣: ٨٧ وتاريخ دولة سلجوق ٤٢ ومفتاح السعادة ١: ٢١٠ و ٢: ١٥ والمختصر في أخبار البشر ٢: ١٩٦ والكامل لابن الأثير. والبداية والنهاية ١٢: ١٠١ ومختصر دول الإسلام ١: ٢١١ وهدية العارفين ١: ٧٧٩ وتاريخ آداب اللغة العربية ٢: ٣٧٥ وذيل تاريخ دمشق. ودائرة المعارف الإسلامية ١: ٣٢٩ ومجلة الكتاب (المصرية) ١: ٩٧٧ ومجلة لغة العرب ٧: ٨٧٠ وكشف الظنون ٩٧٣ والأعلام ١: ٢٦٦ و "الخطيب البغدادي" ليوسف العش ومجلة المجمع العلمي العربي ٢٨: ٣٣٠ وبروكلمان ١: ٣٢٩ والذيل ١: ٥٦٢ ومجلة الثقافة (السورية) ١: ٤ واللباب ١: ٤٥٣.
(١) التكملة ١: ٥٤ والذيل والتكملة ١: ٢٩٥ =','1','50',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (109,'البيهقي [٤٧٠ - ٥٤٤ هـ / ١٠٧٧ - ١١٥٠ م]

<span data-type="title" id=toc-145>أحمد بن علي بن محمد بن أحمد، أبو جعفر، البيهقي، المعروف ب "بوجعفرك</span>":
إمام في القراءة والتفسير والنحو واللغة، من أهل نيسابور. قال السمعاني: "انتشرت تصانيفه في البلاد، وظهر له أصحاب نجباء، وتخرج به خلق .. ". من كتبه "المحيط بعلم القرآن" والمحيط بلغات القرآن" (١).

الرفاعي [٥١٢ - ٥٧٨ هـ / ١١١٨ - ١١٨٢ م]

<span data-type="title" id=toc-146>أحمد بن علي بن أحمد بن يحيى بن حازم بن علي بن رفاعة، أبو العباس، الرفاعي:</span>
الزاهد الكبير، مؤسس الطريقة الرفاعية. مغربي الأصل، قدم أبوه العراق وسكن قرية أم عبيدة بالبطائح، بين واسط والبصرة، وولد أحمد بها، وتفقه وتأدب في
_________
= ومعجم أصحاب الصدفي ٣٤ وطبقات المفسرين للسيوطي ٤ وطبقات المفسرين للداودي ١: ٥٣ وغاية النهاية ١: ٨٣ وطبقات القراء للذهبي ٢: ٤٥٨ والديباج ٢: ٥.
(١) إنباه الرواة ١: ٨٩ وطبقات المفسرين للسيوطي ٤ وبغية الوعاة ١: ٣٤٦ والوافي ٧: ٢١٤ وطبقات المفسرين للداودي ١: ٥٤ ومعجم الأدباء ٤: ٤٩ والأعلام ١: ١٦٨ وهدية العارفين ١: ٨٤ وتذكرة الحفاظ ١٣٠٦ وكشف الظنون ١٦١٩ ومعجم المؤلفين ٢: ٤ وسلم الوصول ١٠٦.','1','50',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (110,'واسط. وتصوف فانضم إليه خلق من الفقراء وأحسنوا فيه الاعتقاد وتبعوه، قال الصفدي: ويقال لهم "الأحمدية" و " البطائحية" ولهم أحوال عجيبة ... " توفي بأم عبيدة، ولم يخلف عقبا، أما العقب فلأخيه. أخباره كثيرة. من آثاره "تفسير سورة القدر" و "البرهان ومعاني بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ" (١).

ابن حجر العسقلاني [٧٧٣ - ٨٥٢ هـ / ١٣٧٢ - ١٤٤٩ م]

<span data-type="title" id=toc-147>أحمد بن علي بن محمد، الكناني العسقلاني، أبو الفضل، شهاب الدين، ابن حجر:</span>
حافظ الإسلام في عصره، مؤرخ، أديب، شاعر، من فقهاء الشافعية. أصله من عسقلان بفلسطين. ولد بالقاهرة، وفقد أبويه في سن مبكرة، فتعهده أوصياؤه. ودرس الفقه والأدب والنحو واللغة، ثم أقبل على الحديث، وسافر في طلبه إلى الشام والحجاز واليمن.
_________
(١) الوافي ٧: ٢١٩ ووفيات الأعيان ١: ١٧١ والسبكي ٤٠ وابن الساعي ١١٢ والشذرات ٤: ٢٥٩ ومرآة الزمان ٣٧٠ وطبقات الشعراني ١: ١٢١ وتنوير الابصار ٣ وجامع الكرامات ٧٧ ومعجم المطبوعات ٩٤٧ والأعلام ١: ١٦٩ ومعجم المؤلفين ٢: ٢٥. والعقود الجوهرية في مدائح الحضرة الرفاعية. ونور بهجة الصدق ٣٣٦ وبروكلمان، الذيل ١: ٧٨٠.','1','51',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (111,'وعلت له شهرة، فتوافد عليه الطلاب والعلماء للأخذ عنه. وولي القضاء نحو إحدى وعشرين سنة، ثم أصبح قاضي قضاة مصر. ودرس التفسير بالحسينية والمنصورية، وخطب بالأزهر وجامع عمرو. زادت تصانيفه في الحديث والتاريخ والأدب والفقه على مئة وخمسين مصنفا، قال السخاوي: "واعتنى بتحصيل تصانيفه كثير من شيوخه وأقرانه فمن دونهم، وكتبها الأكابر، وانتشرت في حياته، وأقرأ الكثير منها .. ". من آثاره التي تخص موضوع التفسير "أسباب النزول" قال في كشف الظنون: لم يبيض. و "الآيات النيرات للخوارق المعجزات" (١).

المنيني [١٠٨٩ - ١١٧٢ هـ / ١٦٧٨ - ١٧٥٩ م]

<span data-type="title" id=toc-148>أحمد بن علي بن عمر بن صالح بن أحمد ابن سليمان، شهاب الدين، أبو النجاح المنيني:</span>
عالم دمشقي، أديب، مفسر، له
_________
(١) رفع الإصر ١: ٨٥ وإنباه الغمر مقدمة الدكتور حسن حبشي، ولسان الميزان ٧ خاتمته، لمصحح طبعه، والضوء اللامع ٢: ٣٦ ونظم العقيان ٤٥ والبدر الطالع ١: ٨٧ ومؤرخو مصر الأسلاية ١٠٥ والدرر الكامنة (طبعة مصر) مقدمة محمد جاد عبد الحق. ودائرة المعارف الإسلامية ١: ١٣١ وبدائع الزهور ٢: ٧ والفوائد البهية ١١ وتهذيب التهذيب ١٢ خاتمته لمصحح طبعه. وشذرات الذهب ٧: ٢٧٠ وزيدان ٣: ١٨٢.','1','51',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (112,'شعر جيد. أصله من احدى قرى طرابلس، ومولده في منين من قرى دمشق، ومنشأه ووفاته فيها. من كتبه "العقد المنظم في قوله تعالى: {واذكر في الكتاب مريم} و "القول المرغوب في قوله تعالى: {فهب لي من لدنك وليا يرثني ويرث من آل يعقوب} (١).

المراغي [.. - ١٣١٠ هـ / .. - ١٨٩٢ م]

<span data-type="title" id=toc-149>أحمد بن علي أكبر المراغي:</span>
فقيه إمامي، عارف بالتفسير، مشارك في عدة علوم، من أهل تبريز. من كتبه "تفسير مشكلات القرآن" (٢).

المهدوي [.. - ٤٤٠ هـ / .. - ١٠٤٨ م]

<span data-type="title" id=toc-150>أحمد بن عمار بن أبي العباس، أبو العباس المهدوي:</span>
مقرىء كبير، مفسر، نحوي. من أهل المهدية - بتونس -. روى عن أبي الحسن القابسي وغيره، وحج، فأخذ عن علماء الحرمين. وعاد، فدخل الأندلس في حدود سنه ٤٣٥ هـ. من كتبه "التفصيل الجامع لعلوم التنزيل"
_________
(١) هدية العارفين ١: ١٧٥ وسلك الدرر ١: ١٠٣ والظاهرية ٦٧.
(٢) اعلام الشيعة ١: ١١٤ وأعيان الشيعة ١٤: ٥٩٠ و ١٧: ٤٠٩.','1','52',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (113,'في التفسير، قال صاحب كشف الظنون: "وهو تفسير كبير بالقول، فسر الآيات أولا ثم ذكر القراءات ثم الإعراب. وكتب في آخره قواعد القراءات، ثم اختصره وسماه "التحصيل". وقال القفطي: "التفصيل هو كتابه الكبير في التفسير، ولما أظهر هذا الكتاب في الأندلس، قيل لمتولي الجهة التى نزل بها من الأندلس: ليس الكتاب له، وإذا أردت علم ذلك فخذ الكتاب إليك واطلب منه تأليف غيره. ففعل ذلك، وطلب غيره، فألف له "التحصيل" وهو كالمختصر منه، وان تغير الترتيب بعض تغير. والكتابان مشهوران في الآفاق، سائران على أيدي الرفاق". وانظر ترجمة أحمد بن محمد بن أحمد بن برد في هذا المعجم (١).

القطيعي [٥١٢ - ٥٦٣ هـ / ١١١٨ - ١١٦٨ م]

<span data-type="title" id=toc-151>أحمد بن عمر بن الحسين بن خلف القطيعي، أبو العباس:</span>
محدث، مفسر،
_________
(١) طبقات المفسرين للسيوطي ٥ وطبقات المفسرين للداودي ١: ٥٦ وطبقات ابن قاضي شهبة ١: ٢٢٧ والوافي ٧: ٢٥٧ وانباء الرواة ١: ٤٥٩ وبغية الوعاة ١: ٣٥١ والصلة ٨٦ ومعجم الأدباء ٥: ٣٩ ومفتاح السعادة ٢: ٨٤ وكشف الظنون ١: ٣٦٠ و ٤٥٩ و ٤٦٢ وهدية العارفين ١: ٧٥ وتلخيص ابن مكتوم ١٥ وغاية النهاية ١: ٩٢ وبروكلمان ١: ١١١ والذيل ١: ٧٣٠ ومعجم المؤلفين ٢: ٢٧.','1','52',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (114,'واعظ، من فقهاء الحنابلة. من أهل بغداد، وبها نشأ وتعلم. قال ابن النجار: "برع في الفقه، وتكلم في مسائل الخلاف، وكان حسن المناظرة، جرينا في الجدال، ويعظ الناس على المنبر". من تصانيفه "الشمول في أسباب النزول" (١).

الكبري [٥٤٥ - ٦١٨ هـ / ١١٤٥ - ١٢٢١ م]

<span data-type="title" id=toc-152>أحمد بن عمر بن محمد بن عبد الله، نجم الدين، أبو الجناب الكبري الخيوقي:</span>
محدث، مفسر، صوفي، من فقهاء الشافعية، كان شيخ خوارزم في وقته. ولد في خيوق - من قرى خوارزم -، ورحل في طلب العلم فسمع بالقاهرة والاسكندرية وأصبهان وهمذان ونيسابور، وحدث بخوارزم، وكتب عنه عامة الرحالة من أهل الحديث وغيرهم. قال ابن هلالة: كان من كبار مشايخ الصوفية، انتهت إليه المشيخة بناحية خوارزم وما يليها، وكثر أتباعه وانتشر مريدوه في تلك النواحي" وقال ابن نقطة: "هو شافعي المذهب إمام في السنة". ولما وصل التتار على باب خوارزم خرج فيمن خرج لققالهم مع جماعة من مريديه، وكانوا نحو الثمانين، فقاتلوا إلى
_________
(١) الوافي ٧: ٢٥٩ والشذرات ٤: ٢٠٧ وذيل طبقات الحنابلة ١: ٣٠١.','1','53',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (115,'أن استشهدوا جميعا على باب البلد، بعد أن قاتلوا معه، وجاهدوا في سبيل الله، حتى أكرمهم الله معه بالشهادة. له تفسير القرآن، في اثني عشر مجلدا، و "عين الحياة" مخطوط، جزء منه في تفسير الفاتحة (١).

ابن أبي الرضى [.. - ٧٩١ هـ / .. - ١٣٨٩ م]

<span data-type="title" id=toc-153>أحمد بن عمر بن محمد بن أبي الرضى، أبو الخير، شهاب الدين:</span>
قاض، عالم بالقراءات، مفسر، من فقهاء الشافعية، من أهل حماه (بسورية). تعلم بها وبدمشق. ولي قضاء العسكر بحلب. ثم ولي قضاءها استقلالا، فحمدت سيرته، ثار على الملك الظاهر برقوق، وأنكر سلطنته، فطلبه، فاختفى مدة حج في أثنائها، ثم عاد إلى حلب مستخفيا. وقامت فتنة يلبغا الناصري في حلب سنة ٧٩١ هـ، فخرجت عن طاعة الملك الظاهر، وتولى ابن أبي الرضى قضاءها للمرة الثالثة، فلم يلبث أن ثار على نائب حماه كمشبغا الحموي وقاتله، فظفر به كمشبغا وأخذه معه فأعدمه في خان شيخون - بين
_________
(١) الوافي ٧: ٢٦٣ والعبر ٥: ٧٣ والشذرات ٥: ٧٩ وطبقات السبكي ٥: ١١ وطبقات الداوودي ١: ٥٧ وهدية العارفين ١: ٩٠ وكشف الظنون ٤٥٩ و ١١٨١ ومعجم المؤلفين ٢: ٣٤.','1','53',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (116,'المعرة وحماه. من كتبه "الناسخ والمنسوخ" و "عقد البكر في نظم غريب الذكر" منظومة في غريب القرآن (١).

الربعي [.. - ٧٩٥ هـ / .. - ١٣٩٣ م]

<span data-type="title" id=toc-154>أحمد بن عمر بن علي بن هلال، أبو العباس شهاب الدين الربعي:</span>
فقيه مالكي، أصولي، عارف بالعربية والمعاني والبيان والتفسير، ولد بالاسكندرية، وتعلم بها وبالقاهرة. ثم سكن دمشق وتوفي بها. من كتبه "تفسير آية الكرسي" وسماه "الفتح القدسي" مخطوط، في مكتبة مغنيسا. قال الداودي: أتى فيه بفوائد كثيرة" (٢).

الزاولي [.. - ٨٤٨ هـ / .. - ١٤٤٤ م]

<span data-type="title" id=toc-155>أحمد (شهاب الدين) بن عمر (شمس الدين) الزاولي، الدولت آبادي، الهندي:</span>
قاض، مفسر، نحوي، من فقهاء الحنفية. ولد بدولت آباد دهلي، وبها نشأ وتعلم، ثم ولي القضاء. من كتبه
_________
(١) إنباه الغمر ١: ٢٣٤ و ٢٧٧ و ٣٧٥ و ٣٨١ والدرر الكامنة ١: ٢٤١ وشذرات الذهب ٦: ٣١٤ وهدية العارفين ١: ١١٦ وإيضاح المكنون ٢: ١٠٤.
(٢) طبقات المفسرين للداودي ١: ٥٦ وإنباه الغمر ١: ٤٥٨ والدرر الكامنة ١: ٢٤٦ والديباج ٨٢ والشذرات ٦: ٣٣٨ وهدية العارفين ١: ١١٦.','1','54',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (117,'(البحر المواج والسراج الوهاج" في تفسير القرآن (١).

أحمد بن فارس [٣٢٩ - ٣٩٥ هـ / ٩٤١ - ١٠٠٤ م]

<span data-type="title" id=toc-156>أحمد بن فارس بن زكريا بن محمد بن حبيب القزويني الرازي، أبو الحسن:</span>
من أئمة اللغة والأدب، مشارك في علوم شتى. ولد بقزوين، وأقام بهمذان إلى أن استدعي إلى الري لتعليم "مجد الدولة" البويهي، فنسب إليها. قرأ عليه بديع الزمان الهمذاني والصاحب بن عباد وغيرهما. تابع الكوفيين في النحو، واحتذى به الحريري في بعض مسائله اللغوية. وكان شافعيا فتحول مالكيا، وقال: أخذتني الحمية لهذا الإمام المقبول القول على جميع الألسنة أن يخلو هذا البلد عن مذهبه، فإن الري أجمع البلاد للمقالات والاختلاف". وكان كريما جوادا لا يبقى شينا. من كتبه "جامع التأويل" في تفسير القرآن و "غريب إعراب القرآن" (٢).
_________
(١) كشف الظنون ٦٨ و ٣٧٥ و ١٣٧١ و ١٧٢٥ وإيضاح المكنون ١: ١٦٦ و ١٧٠ و ١٧٢ و ٢: ٥٦١ وسبحة المرجان ٣٩ ومعجم المؤلفين ١: ٢٤٥ و ٢: ٣٠ و ٤: ٣٥٩ وهو في هذه المراجع شهاب الدين بن شمس الدين بن عمر، وأحمد بن عمر، وأحمد بن شمس الدين بن عمر، وقيل في وفاته سنة ٨٤٩ هـ.
(٢) الوافي ٧: ٢٧٨ ووفيات الأعيان ١: ١١٨ ومعجم الأدباء٢: ٦ ويتيمة الدهر ٣: ٤٠٠ ودمية القصر ٢٥٧ وبغية الوعاة ١: ٣٥٢ ونزهة الالباء ٢١٩ =','1','54',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (118,'ابن الفرات [.. - ٢٥٨ هـ / .. - ٨٧٢ م]

<span data-type="title" id=toc-157>أحمد بن الفرات بن خالد الضبي الرازي، أبو مسعود:</span>
محدث أصبهان وعالمها، أصله من الري، رحل في طلب الحديث فسمع بالبصرة والكوفة وبغداد والشام واليمن ومصر، ثم استوطن أصبهان خمسا وأربعين عاما يحدث بها إلى أن مات. كان معاصرا للإمام أحمد بن حنبل مقدما عنده، وقد قال فيها ما أظن بقي أحد أعرف بالمسندات من ابن الفرات، وما تحت أديم السماء أحفظ لأخبار رسول الله - صلى الله عليه وسلم - منه". قال الداودي: له "تفسير القرآن" (١).
_________
= وطبقات السيوطي ٥ والديباج ٣٧ وروضات الجنات، ٦ والشذرات ٣: ١٣٢ والفلاكة والمفلوكون ١٠٨ وطبقات ابن قاضي شهبة ١: ٢٣٥ والبداية والنهاية ١١: ٥٣٥ وسلم الوصول ١١٢ والنجوم الزاهرة٤: ٢١٢ ومفتاح السعادة ١: ١٠٩ وانباه الرواة ١: ٩٢ وطبقات المفسرين للداودي ١: ٥٩ وتاريخ آداب اللغة العربية ٢: ٣٥٧ وهدية العارفين ١: ٦٨ والأعلام ١: ١٨٤ ودائرة المعارف الإسلامية ١: ٢٤٧.
(١) تاريخ بغداد ٤: ٣٤٣ وتذكرة الحفاظ ٢: ٥٤٤ وتهذيب التهذيب ١: ٦٦ وتاريخ التراث العربي ١: ٣٥١ وابن عساكر ١: ٤٣٠ وميزان الاعتدال ١: ١٢٧ والوافي ٧: ٢٨٠ والنجوم الزاهرة ٣: ٢٩ والعبر ٢: ١٦ وطبقات المفسرين، للداودي ١: ٦٢ وخلاصة تهذيب الكمال ٩ ومرآة الجنان ٢: ١٦٩ والشذرات ٢: ١٣٨ والرسالة المستطرفة ٨٧ وأخبار أصبهان ١: ٨٢ وهدية العارفين ١: ٤٩.','1','55',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (119,'العسكري [.. - ٣٠٣ هـ ٩١٥ م]

<span data-type="title" id=toc-158>أحمد بن فرح بن جبريل، أبو جعفر، البغدادي العسكري:</span>
مقرىء كبير، محدث، مفسر، من أهل بغداد. حدث بالأنبار وغيرها. وأقرأ بالكوفة مدة طويلة ومات بها، وكان ضريرا. قال الداودي: "كان ثقة عالما بالقرآن واللغة، بصيرا بالتفسير" ونعته ابن الجزري "بالمفسر المشهور" (١).

الساعاتي [.. - نحو ١٣٤٨ هـ / .. - نحو ١٩٣٠ م]

<span data-type="title" id=toc-159>أحمد فوزي بن أحمد الساعاتي:</span>
باحث، متكلم، من أهل دمشق، كردي الأصل، ولي إدارة البرق والبريد العامة. من آثاره "البرهان في إعجاز القرآن" طبع (٢).

الاقليشي [٣٦٣ - ٤١٠ هـ / ٩٧٣ - ١٠١٩ م]

<span data-type="title" id=toc-160>أحمد بن قاسم بن عيسى بن فرج بن عيسى اللخمي الاقليشي الأندلسي، أبو العباس:</span>
عالم بالقراءات، سكن قرطبة،
_________
(١) تاريخ بغداد ٤: ٣٤٥ وغاية النهاية ١: ٩٥ والعبر ٢: ١٢٥ وطبقات المفسرين ١: ٦٣ والشذرات ٢:
(٢) معجم المطبوعات ٩٩٥ ومجلة المجمع العلمي العربي ١: ٤٩ ومجلة المكتبة ١: ٢٨٠ وجامع التصانيف ٦٩ و ٨٣ وفهرس دار الكبت ٧: ٦٣.','1','55',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (120,'ورحل إلى المشرق، واستمر بطليطلة وتوفي بها. من آثاره "تفسير العلوم والمعاني المستودعة في السبع المثاني" وهو تفسير للفاتحة، مخطوط (١).

القونوي [.. :؟ / .. -؟]

<span data-type="title" id=toc-161>أحمد بن قلمشاه القونوي، أبو العباس:</span>
قاض، من العلماء بالتفسير والفقه والنحو والأصلين، من أهل قونية، وولي قضاءها أكثر من ثلاثين سنة، ودرس بها بالمدرستين المصلحية والنظامية. ولم أعثر على تاريخ وفاته (٢).

الشجري [٢٦٠ - ٣٥٠ هـ / ٨٧٤ - ٩٦١ م]

<span data-type="title" id=toc-162>أحمد بن كامل بن خلف بن شجرة بن منصور بن كعب، أبو بكر، البغدادي الشجري:</span>
قاض، عالم بالأحكام وعلوم القرآن والنحو والشعر وأيام الناس وتواريخ أصحاب الحديث، من فقهاء الحنفية. ولد بسامراء، وسكن بغداد، وولي قضاء الكوفة. "كان أولا جريري المذهب ثم اختار لنفسه مذهبا، وأملى كتابا في
_________
(١) غاية النهاية ١: ٩٩٧ وجذوة المقتبس ١٣٣ والأزهرية ١: ٣٠٤ وبغية الملتمس ١٨٩ والأعلام
(٢) طبقات المفسرين ١: ٦٣ والجواهر المضية ١: ٩٠.','1','56',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (121,'السير" وتكلم على الأخبار". من كتبه "غريب القرآن" و "موجز التأويل عن حكم معجز التنزيل" و "التقريب في كشف الغريب" (١).

أحمد كمال [.. - ١٣٠٤ هـ / .. - ١٨٨٧ م]

<span data-type="title" id=toc-163>أحمد كمال باش١: أ</span>
ديب، تركي مستعرب، من وزراء الأوقاف والمعارف في الدولة العثمانية. له "النوادر واللطائف في تفسير الآيات التي احتوت على النكت والظرائف" (٢).

ابن النقيب [٧٠٢ - ٧٦٩ هـ / ١٣٠٢ - ١٣٦٨ م]

<span data-type="title" id=toc-164>أحمد بن لؤلؤ بن عبد الله، شهاب الدين، أبو العباس، المعروف بابن النقيب:</span>
عالم بالقراءات والتفسير والأصول والنحو، أديب، شاعر، من فقهاء الشافعية. من أهل القاهرة مولدا ووفاة، قال ابن حجر: "اشتغل بالعلم وله عشرون
_________
(١) تاريخ بغداد ٤: ٣٥٧ والوافي ٧: ٢٩٨ ومعجم الأدباء ٤: ١٠٢ وانباء الرواة ١: ٩٧ وغاية النهاية ١: ٩٨ والجواهر المضية ١: ٩ ولسان الميزان ١: ٢٤٩ واللباب ٢: ١٨٦ وتاج التراجم ١٤ والعبر ٣: ٢٨٥ وميزان الاعتدال ١: ١٢٩ وطبقات المفسرين ١: ٦٣ وابن النديم ٣٥ وهدية العارفين ١: ٦٤ والأعلام ١: ١٩٠.
(٢) هدية العارفين ١: ١٩١ وإيضاح المكنون ٢: ٦٨٢.','1','56',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (122,'سنة، وكان وقورا ساكنا خاشعا قانعا، انتفع به الطلبة وتخرج به الفضلاء" وترجم له الاسنوي في طبقاته وأثنى عليه (١). السجلماسي ١٠٩٠ - ١١٥ هـ / ١٦٧٩ - ١٧٤٢]

<span data-type="title" id=toc-165>أحمد بن مبارك بن محمد بن علي السجلماسي:</span>
عالم بالحديث والتفسير والقراءات والفقه والأصول والبيان، من فقهاء المالكية. من أهل سجلماسة، انتقل إلى فاس وسكنها وأخذ عن كبار شيوخها، انتهت إليه الرياسة في جميع العلوم في وقته. مات بفاس. له تأليف في تفسير قوله تعالى {وهو معكم أينما كنتم ..} (٢).

أحمد بن حنبل [١٢٤ - ٢٤٠ هـ / ٧٨٠ - ٨٥٥ م]

<span data-type="title" id=toc-166>أحمد بن محمد بن حنبل، أبو عبد الله الشيباني الوائلي:</span>
أحد الأئمة الأربعة الكبار عند أهل السنة. وإليه نسبة الحنابلة كافة. أصله من مرو، وكان أبوه والي سرخس. وولد هو ببغداد. فنشأ بها منكبا على طلب
_________
(١) الدرر الكامنة ١: ٢٥٤ والشذرات ٦: ٢١٣ وحسن المحاضرة ١: ٢٤٦ والنجوم الزاهرة ١١: ١٠١ وهدية العارفين ١: ١١٢ وانباء الغمر ١: ١٠٩ ووفاته فيه سنة ٧٧٧ هـ.
(٢) سلوة الانفاس ٢: ٢٠٣ واليواقيت الثمينة ١: ٤٧ ودليل مؤرخ المغرب ٢٠٩ و ٣١٧ وهدية العارفين ١: ١٧٤ وشجرة النور ٣٥٢.','1','57',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (123,'العلم، وفي سبيله رحل رحلات مختلفة، لاقى فيها عنتا، حتى أنه كان يحمل أمتعته بنفسه، فسافر إلى الكوفة والبصرة والحجاز واليمن والشام والثغور والجزيرة وفارس وخراسان والجبال والأطراف. وقد التقى بالشافعي بمكة، فسمعه واستطاب فقهه، كما تأثر بدروس سفيان بن عيينة الذي كان حجة مدرسة الحجاز بالفقه. وطلب ابن حنبل الفقه ولم يترك الحديث، وكان إماما فيهما. وفي أيامه دعا المأمون العباسي إلى القول بخلق القرآن، ومات (سنة ٢١٨ هـ) قبل أن يناظره، وولي المعتصم، فسجن ابن حنبل ٢٨ شهرا لامتناعه عن القول بخلق القرآن: وأطلق سنة ٢٢٠ هـ. ولم يصبه شر في زمن الواثق بالله، ومات الواثق (سنة ٢٣٢ هـ) وولي بعده المتوكل، فأكرم الإمام أحمد وقدمه، فبدأ دروسه من جديد، والتقى حوله عدد كبير من التلاميذ، واستمر إلى أن توفي. من كتبه "تفسير القرآن" و "الناسخ والمنسوخ" و "المقدم والمؤخر في القرآن" و "جوابات القرآن" (١).
_________
(١) مناقب الإمام أحمد بن حنل لابن الجوزي، وابن حنبل حياته وعصره، لأبي زهرة، وتاريخ بغداد ٤: ٤١٢ وطبقات الحنابلة ١: ٤ والتاريخ الكبير للبخاري ٢/ ١: ٥ وتذكرة الحفاظ ٢: ٤٣١ والوافي ٦: ٣٦٣ والعبر ١: ٤٣٥ ووفيات الأعيان ١: ٦٣ وابن عساكر ٢: ٢٨ وحلية الأولياء ٩: ١٦١ ومرآة =','1','57',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (124,'الجلفري [.. - نحو ٢٥٠ هـ / .. - نحو ٨٦٤ م]

<span data-type="title" id=toc-167>أحمد بن محمد بن هاشم الجلفري:</span>
محدث، مفسر، من أهل جلفر إحدى قرى مرو: ذكره الداودي في "طبقات المفسرين" وقال: "صاحب التفسير، سمع مغيث بن بديل، وعنه خارجة". ولم يكمل الترجمة (١).

الطبري [.. - حيا ٣٠٤ هـ / .. - حيا ٩١٦ م]

<span data-type="title" id=toc-168>أحمد بن محمد بن يزداد بن رستم الطبري، أبو جعفر:</span>
نحوي، محدث، مفسر، استوطن بغداد وحدث بها. ذكر عمر بن محمد بن سيف الكاتب أنه سمع منه في سنة ٣٠٤ هـ. ولي جباية خراج الشجر ببلاد فارس لأمير المؤمنين المقتدر بالله العباسي. وفي كتاب "الوزراء"
_________
= الجنان ٢: ١٣٢ والنجوم الزاهرة ٢: ٣٠٤ وطبقات المفسرين ١: ٧٠ وصفة الصفوة ٢: ١٩٠ والبداية والنهاية ١٠: ٣٢٥ وتهذيب التهذيب ١: ٧٢ والجرح والتعديل ١: ٦٨ وطبقات السبكي ١: ١٩٩ والشذرات ٢: ٩٦ وتاريخ التراث العربي ٢: ١٩٦ والنسخ في القرآن الكريم ١: ٣١٥ والمجددون في الإسلام ١٣٨ وطبقات العبادي ١٤ وتهذيب الاسماء واللغات ١: ١١٠ وابن النديم ٢٢٩ ودائرة المعارف الإسلامية، ودائرة المعارف للبستاني.
(١) طبقات المفسرين ١: ٩١.','1','58',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (125,'للصابي بعض أخباره مع الوزير ابن الفرات. له "غريب القرآن" (١).

الخلال [.. - ٣١١ هـ / .. - ٩٢٣ م]

<span data-type="title" id=toc-169>أحمد بن محمد بن هارون، أبو بكر، الخلال:</span>
مفسر، عالم بالحديث واللغة. من كبار الحنابلة، من أهل بغداد. "صرف عنايته إلى جمع علوم أحمد بن حنبل وسافر لأجلها وكتبها عالية ونازلة، وصنفها كتبا". كانت حلقته بجامع المهدي. قال ابن ناصر الدين: "هو رحال واسع العلم شديد الاعتناء بالآثار". وقال ابن أبي يعلى: "له التفاسير الدائرة والكتب السائرة". من كتبه "تفسير الغريب" (٢).

الطحاوي [٢٣٩ - ٣٢١ هـ / ٨٥٣ - ٩٣٣ م]
أحمد بن محمد بن سلامة بن عبد الملك

<span data-type="title" id=toc-170>ابن سلمة بن سليمان بن حامد، أبو جعفر (الأزدي الحجري الطحاوي)</span>
_________
(١) تاريخ بغداد ٥: ١٢٥ وغاية النهاية ١: ١١٤ ومعجم الأدباء ٤: ١٩٣ وبغية الوعاة ١: ٣٧٨ وانباه الرواة ١: ١٢٨ والوافي ٨: ١١١ وأعيان الشيعة ٩: ٤٢١ وطبقات الداودي ١: ٧٢ وهدية العارفين ١: ٥٦ وكتاب الوزراء٢٣١ وانظر فهرسته. وابن النديم ٦٥.
(٢) شذرات الذهب ٢: ٣٦١ والعبر ٢: ١٤٨ وتاريخ بغداد ٥: ١١٢ والوافي ٨: ٩٩ وهدية العارفين ١: ٥٧ وطبقات ابن أبي يعلى ٢: ١٢ والبداية والنهاية ١١: ١٤٨ ومناقب الإمام أحمد ٥١٢ والأعلام ١: ١٩٦.','1','58',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (126,'الأزدي الحجري الطحاوي: محدث، من فقهاء الحنفية، قال الشيرازي: " انتهت إليه رياسة أصحاب أبي حنيفة بمصر". ولد ونشأ في "طحا" - قرية من صعيد مصر -. تفقه على مذهب الإمام الشافعي ثم تحول إلى مذهب أبي حنيفة. سمع بالقاهرة، ثم رحل إلى الشام سنة ٢٦٨ هـ فسمع بدمشق وبيت المقدس وغزة وعسقلان. وعاد، فناب في القضاء بعد السبعين ومائتين، ويقال أنه دخل السجن بسبب حقد أحد أعوانه عليه. وهو ابن أخت المزني. توفي بالقاهرة. من كتبه "أحكام القرآن" و "نوادر القرآن" في نحو ألف ورقة (١).
_________
(١) الحاوي في سيرة الإمام أبي جعفر الطحاوي، للكوثري، وتاج التراجم ٨ والجواهر المضية ١: ١٠٢ والفوائد البهية ٣١ والوافي ٨: ٩ وطبقات المفسرين ١: ٧٣ وطبقات الشرازي ١٢٠ والعبر ٢: ١٨٦ وتهذيب ابن عساكر ٢: ٥٤ والانساب ٣٦٨ والمنتظم ٦: ٢٥٥ ولسان الميزان ١: ٢٧٤ ومفتاح السعادة ٢: ٢٧٥ والبداية والنهاية ١١: ١٧٤ وتاريخ التراث العرب ٢: ٨٥ والنجوم الزاهرة ٣: ٢٣٩ وغاية النهاية ١: ١١٦ وشذرات الذهب ٢: ٢٨٨ وحسن المحاضرة ١: ٣٥٠ واللباب ٢: ٢٧٥ وتذكرة الحفاظ ٨٠٨ والفهرست ٢٦٠ وولاة مصر للكندي، انظر فهرسته، ومرآة الجنان ٢: ٢٨١ والأعلام ١: ١٩٧ وروضات الجنات ٥٩ والمختصر في أخبار البشر ٢: ٨٤ وتراجم الرجال ٦ ومعجم المؤلفين ٣: ١٠٧ وهدية العارفين ١: ٥٨ وتاج العروس ١٠: ٣٢٣ وهو فيه أحمد بن سلامة بن إسماعيل. وخطط مبارك ١٣: ٣٠ وكشف الظنون ٢٠.','1','59',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (127,'القرشي [.. - نحو ٣٢٥ هـ / .. - نحو ٩٣٧ م]

<span data-type="title" id=toc-171>أحمد بن محمد بن موسى بن أبي عطاء، القرشي بالولاء، أبو بكر:</span>
مفسر، من أهل دمشق. ذكره السيوطي وقال: روى عن بكار بن قتيبة (١٨٢ - ٢٧٠ هـ) وعنه عبد الوهاب الكلابي (٣٠٦ - ٣٩٦ هـ) (١).

ابن عقدة [٢٤٩ - ٣٣٢ هـ / ٨٦٣ - ٩٤٤ م]

<span data-type="title" id=toc-172>أحمد بن محمد بن سعيد بن عبد الرحمن الكوفي مولى بني هاشم، المعروف بابن عقدة، وهو لقب لأبيه:</span>
حافظ كبير جمع الأبواب والتراجم، وأكثر الرواية، وانتشر حديثه، وروى عنه الحفاظ والأكابر، كان يقول: أحفظ مئة ألف حديث بأسانيدها وأذاكر بثلاث مئة ألف. ولد بالكوفة وسمع بها وببغداد. قال الذهبي: "حافظ العصر والمحدث البحر، كتب العالي والنازل، والحق والباطل، حتى كتب عن أصحابه، وكان إليه المنتهى في قوة الحفظ وكثرة الحديث، ورحلته قليلة، ولهذا كان يأخذ عن الذين يرحلون إليه، ولو صان نفسه وجود لضربت إليه أكباد الإبل ولضرب
_________
(١) طبقات المفسرين للسيوطي ٦ وطبقات المفسرين للداودي ١: ٩٠.','1','59',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (128,'بإمامته المثل، لكنه جمع فأوعى وخلط الغث بالسمين والخرز بالدر الثمين، ومقت لتشيعه" وقال ابن عدي: "كان صاحب معرفة وحفظ مقدما في هذه الصنعة إلا أنني رأيت مشايخ بغداد يسينون الثناء عليه ورأيت فيه مجازفات". وكانت كتبه ستمائة حمل. توفي بالكوفة. من كتبه "تفسير القرآن" (١).

النحاس [.. - ٣٣٨ هـ / .. - ٩٥٠ م]

<span data-type="title" id=toc-173>أحمد بن محمد بن إسماعيل بن يونس المرادي المصري، أبو جعفر النحاس:</span>
مفسر، نحوي، أديب، من أهل مصر، رحل إلى بغداد وأخذ عن أصحاب المبرد وعن نفطويه والزجاج، وسمع بالرملة والقاهرة. قال الزبيدي: "كان واسع العلم غزير الرواية، ولم يكن له مشاهدة، وإذا خلا بقلمه جود وأحسن .. " مات غرقا بالنيل، من كتبه "تفسير القرآن" قال صاحب كشف الظنون: "قصد فيه الإعراب لكن ذكر القراءات التي يحتاج أن
_________
(١) تاريخ بغداد ٥: ١٤ وتذكرة الحفاظ ٨٣٩ والعبر ٢: ٢٣٠ والشذرات ٢: ٣٣٢ والوافي ٧: ٣٩٥ وأعيان الشيعة ٩: ٤٢٨ والطوسي ٢٨ ومنهج المقال ٤٣ وهدية العارفين ١: ٦٠ والرجال ٧٣ والمنتظم ٦: ٣٣٦ وميزان الاعتدال ١: ٦٤ ولسان الميزان ١: ٢٦٣ ومنتهى المقال ٤٢ وتنقيح المقال ١: ٨٥ وروضات الجنات ٥٨ والأعلام ١: ١٩٨.','1','60',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (129,'بين إعرابها والعلل فيها وما يحتاج فيه من المعاني". وله أيضا "إعراب القرآن" و "ناسخ القرآن ومنسوخه" و "معاني القرآن" مخطوط، الجزء الأول منه (١).

ابن دول [.. - ٣٥٠ هـ / .. - ٩٦٢ م]

<span data-type="title" id=toc-174>أحمد بن محمد بن الحسين بن دول القمي:</span>
مفسر، عارف بالطب، من فقهاء الشيعة الإمامية. من أهل قم بإيران. قال النجاشي: له مائة كتاب، وأورد صاحب "أعيان الشيعة" أسماء ٧٧ كتابا له، منها "تفسير القرآن" (٢).

النيسابوري [.. - ٣٥١ هـ / .. - ٩٦٢ م]
أحمد بن محمد بن عبد الله، أبو

<span data-type="title" id=toc-175>الحسين، ويقال أبو الفضل، (النيسابوري)</span>
_________
(١) وفيات الأعيان ١: ٩٩ وانباه الرواة ١: ١٠١ وبغية الوعاة ١: ٣٦٢ ونزهة الالباء ٢٩١ وحسن المحاضرة ١: ٥٣١ والبداية والنهاية ١١: ٢٢٢ والعبر ٢: ٢٤٦ والشذرات ٢: ٣٤٦ ومرآة الجنان ٢: ٣١١ ومفتاح السعادة ٢: ٨٢ والنجوم الزاهرة ٣: ٣٠٠ والمنتظم ٦: ٣٦٤ ومعجم الأدباء ٢: ٧٢ وطبقات المفسرين ١: ٦٧ وروضات الجنات ٦٠ والوافي ٧: ٣٦٢ والمختصر من تاريخ اللغويين والنحويين ٤٣ وكشف الظنون ٤٦٠ والأعلام ١: ١٩٩.
(٢) أعيان الشيعة ٩: ٣٩١ ومنهج المقال ٤٢ وهدية العارفين ١: ٦٣ والذيل عل كشف الظنون ١: ٣٠٩ والأعلام ١: ٢٠٠ ومعجم المؤلفين ١: ٩٥.','1','60',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (130,'النيسابوري: قاضي الحرمين وشيخ الحنفية في زمانه. من أهل نيسابور. تفقه ببغداد، واتصل بعلي بن عيسى ابن الجراح وزير المقتدر العباسي والقاهر. ولي قضاء الموصل والرملة والحرمين، وغاب عن نيسابور نيفا وأربعين سنة، ثم عاد إليها وولي قضاءها وتوفي بها. من كتبه (تفسير القرآن) (١).

النيسابوري [.. - ٣٥٣ هـ / .. - ٩٧٤ م]

<span data-type="title" id=toc-176>أحمد بن محمد بن سعيد بن إسماعيل، أبو سعيد، الحيري النيسابوري:</span>
حافظ كبير، مفسر، من فقهاء الشافعية، سمع بخراسان والعراق والجبال. روى عنه الحاكم كثيرا وقال: "ولما خرج إلى بغداد خرج بعسكر كثير وأموال، واجتمع عليه ببغداد خلق كثير مجاهدون، وكان من محبته للحديث يكتب بخطه ويسمع، إلى أن استشهد بطرسوس وله خمس وستون سنة". من كتبه "تفسير القرآن" في مجلدات (٢).
_________
(١) الفرائد البهية ٣٦ وتاج التراجم ١٥ والعبر ٢: ٢٩٠ والوافي ٨: ٣٤ والشذرات ٣: ٧ وهدية العارفين ١: ٦٤.
(٢) تذكرة الحفاظ ٩٢٠ وطبقات الشافعية ٣: ٤٣ والعبر ٢: ٢٩٦ وطبقات المفسرين ١: ٧٢ والشذرات ٣: ١٢ وهدية العارفين ١: ٦٤ وكشف الظنون ١: ٤٦٠.','1','61',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (131,'الشاركي [.. - ٣٥٥ هـ / .. - ٩٦٦ م]

<span data-type="title" id=toc-177>أحمد بن محمد بن شارك الهروي، أبو حامد:</span>
مفتي هراة ومحدثها ومفسرها وأديبها وعالمها في زمانه. رحل الكثير وعني بالحديث. روى عنه أبو عبد الله الحاكم، أثناء إقامته في نيسابور. مات في هراة (١).

الفارسي [.. - ٣٦٤ هـ / .. - ٩٧٤ م]

<span data-type="title" id=toc-178>أحمد بن محمد بن أيوب، أبو بكر الفارسي:</span>
واعظ مشهور، عالم بالتفسير. استوطن نيسابور. كان أتباعه ومريدوه كثيرين، ووعظ ببخارا فكثر جمعه، وكان يحضر مجلسه نحو عشرة آلاف، وخاف الحنفية من تغلبه (٢).

الهروي [.. - ٤٠١ هـ / .. - ١٠١١ م]

<span data-type="title" id=toc-179>أحمد بن محمد بن عبد الرحمن (وقيل: محمد) أبو عبيد الفاشاني الهروي:</span>
لغوي، محدث، مفسر، من فقهاء الشافعية، من أهل هراة. قال ابن خلكان: "كان من
_________
(١) السبكي ٣: ٤٥ والداودي: ٧٥ والشذرات ٣: ٣٦ والرسالة المستطرفة ٢٢ وطبقات المفسرين للسيوطي ٥ والأعلام ١: ٢٠١ ومعجم المؤلفين ٢: ١١٠.
(٢) طبقات المفسرين للسيوطي ٥ وطبقات المفسرين للداودي ١: ٧٠ والوافي ٧: ٣٦٢.','1','61',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (132,'العلماء الأكابر، ولم أقف على شيء من أخباره سوى أنه كان يصحب أبا منصور الأزهري اللغوي". له كتاب "الغريبين" جمع فيه بين تفسير غريب القرآن الكريم والحديث النبوي الشريف، قال الذهبي: "وهو من الكتب النافعة السائرة في الآفاق". رواه عنه أبو سعيد الحسن بن أحمد الطبي وأبو بكر الأردستاني وأبو عمرو المليحي (١).

الثعلبي [.. - ٤٢٧ هـ / .. - ١٠٣٥ م]

<span data-type="title" id=toc-180>أحمد بن محمد بن إبراهيم الثعلبي، أبو إسحاق:</span>
مفسر، حافظ للحديث، عالم بالعربية، كان أوحد زمانه في علم القرآن. قال السمعاني: "يقال له الثعلبي والثعالبي وهو لقب لا نسب". وقال عبد الغافر: "هو صحيح النقل موثوق به، حدث عن ابن خزيمة وابن مهران المقرىء، وكان كثير الحديث كثير الشيوخ". من كتبه "الكشف
_________
(١) روضات الجنات ٦٧ وطبقات المفسرين للداودي ١: ٧٩ ومرآة الجنان ٣: ٣ ووفيات الأعيان ١: ٩٥ والسبكي ٣: ٣٤ والعبر ٣: ٧٥ والوافي ٨: ١١٤ ومعجم الأنباء ٤: ٢٦٠ والنجوم الزاهرة ٤: ٢٢٨ وبغية الوعاة ١: ٣٧١ والبداية والنهاية ١١: ٢٤٤ والشذرات ٣: ١٦١ وكشف الظنون ٢: ١٢٠٩ وهدية العارفين ١: ٧٠ والأعلام ١: ٢٥٣ وروضات الجنات ٢٧ ومعجم المؤلفين ٢: ١٥٠ ودمية القصر ٣: ١٥٠٥.','1','62',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (133,'والبيان عن تفسير القرآن" ويعرف بتفسير الثعلبي، مخطوط، قال صاحب كشف الظنون: "أوله بحمد الله يفتتح الكلام وبتوفيقه. يستنجح المطلب والمرام .. " وفي خزانة الرباط السفر السادس منه، كتب سنة ٨١٢ هـ (١)

المعافري [٣٤٠ - ٤٢٩ هـ / ٩٥١ - ١٠٣٨ م]

<span data-type="title" id=toc-181>أحمد بن محمد بن عبد الله بن أبي عيسى ابن لب بن يحيى، أبو عمر، المعافري الأندلسي الطلمنكي:</span>
عالم أهل قرطبة، كان رأسا في علم القرآن حروفه وإعرابه وناسخه ومنسوخه وأحكامه ومعانيه، ذا عناية تامة بالآثار ومعرفة الرجال، عارفا بأصول الديانة. وهو أول من أدخل علم القراءات إلى الأندلس. أصله من طلمنكة (من ثغر الأندلس الشرقي) وسكن قرطبة
_________
(١) الوافي ٧: ٣٠٧ وانباه الرواة ١: ١١٩ وغاية النهاية ١: ١٠٠ والسبكي ٣: ٢٣ وطبقات المفسرين للسيوطي ٥ وطبقات المفسرين للداودي ١: ٦٥ والعبر ٣: ١٦١ والشذرات ٣: ٢٣٠ والنجوم الزاهرة ٤: ٨٣ واللباب ١: ومعجم الأدباء٢: ١٠٤ ومفتاح السعادة ٢: ٦٧ وابن خلكان ١: ٧٩ وهدية العارفين ١: ٧٥ والأعلام ١: ٢٠٥ والبداية والنهاية ١٢: ٤٠ وتذكرة الحفاظ ١٠٩٠ ومرآة الجنان ٣: ٤٦ والمختصر في أخبار البشر ٢: ١٦٨ وكشف الظنون ١٤٩٦ وروضات الجنات ٦٨ والمكتبة الأزهرية ١: ٢٥٩ ومعجم المطبوعات ٦٦٣ وتاريخ آداب اللغة العربية ٢: ٣٧٢ ودائرة المعارف الإسلامية.','1','62',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (134,'وأقرأ بها. وحج، فسمع بمكة والمدينة ودمياط والقيروان. وعاد إلى الأندلس فسكن المرية، فمرسية، فسرقسطة، ثم عاد إلى طلمنكة وتوفي بها. من كتبه "تفسير القرآن" نحو مئة جزء. و "البيان في إعراب القرآن" (١).

ابن برد [.. - بعد ٤٤٠ هـ / .. - بعد ١٠٤٨ م]

<span data-type="title" id=toc-182>أحمد بن محمد بن أحمد بن برد، أبو حفص:</span>
شاعر أندلسي، من بلغاء الكتاب، من بيت أدب ورياسة. قال الحميدي: "هو مولى أحمد بن عبد الملك بن عمر بن محمد بن شهيد، له رسالة في "السيف والقلم والمفاخرة بينهما"، وهو أول من سبق إلى القول في ذلك بالأندلس، وقد رأيته بالمرية بعد الأربعين وأربعمائة". قال ياقوت: "ذكره الحميدي وقال: ... وله كتب في علم القرآن، منها كتاب "التحصيل في تفسير القرآن" وكتاب "التفصيل" في تفسيره أيضا .. "، ومثله
_________
(١) ترتيب المدارك ٤: ٧٤٩ والديباج ١٧٨ والصلة ١: ٤٨ وغاية النهاية ١: ١٢٠ وطبقات القراء للذهبي ١: ٣٥٩ وطبقات المفسرين للسيوطي ٥ وطبقات المفسرين للداودي ١: ٧٨ والنجوم الزاهرة ٥: ٢٨ والعبر ٣: ١٦٨ والشذرات ٣: ٢٤٣ وجدوة المقتبس ١٠٦ وبغية الملتمس ١٥١ وتذكرة الحفاظ ١٠٩٨ والوافي ٨: ٣٢.','1','63',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (135,'في "الوافي" للصفدي و "طبقات المفسرين" للسيوطي نقلا عن الحميدي. وما نقله الثلاثة غير موجود في النسخة المتداولة من كتاب جذوة المقتبس للحميدي، فلعل النقل كان من كتاب آخر له، أو أن النخة المطبوعة من الجذوة ناقصة. وذكر صاحب كشف الظنون أن كتاب "التفصيل الجامع لعلوم التنزيل" هو لأحمد بن عمار المهدوي - راجع ترجمته في هذا المعجم - ثم اختصره وسماه "التحصيل" وقال: "وذكر السيوطي نقلا عن الحميدي أن الكتاب لأبي حفص أحمد بن محمد الأندلسي، وكان حيا سنة ٤٤٠ هـ " (١).

ابن الخروبي [.. - ٥٠٢ هـ / .. - ١١٠٨ م]

<span data-type="title" id=toc-183>أحمد بن محمد بن محمد بن سعيد بن عبد الله الأنصارى الوادآشي، أبو العباس ابن الخروبي:</span>
قاض، مفسر، من فقهاء المالكية، من أهل "وادي آش" - مدينة في جنوب الأندلس على الأطلسي -. ولي
_________
(١) كشف الظنون ٤٥٩ و ٤٦٢ وهدية العارفين ١: ٧٣ و ٧٦ والوافي ٧: ٣٥٠ وجذوة المقبس ١١٥ وبغية الملتمس ١٥٣ ومعجم الأدباء ٥: ٤١ وطبقات المفسرين للسيوطي ٦ وطبقات المفسرين للداودي ١: ٦٧ والأعلام ١: ٢٠٦ والذخيرة ١/ ٢: ١٨ ومعجم المؤلفين ٢: ٦٥.','1','63',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (136,'الصلاة والخطبة في جامعها، ثم قضاءها، وتوفي بها. قال ابن عبد الملك المراكشي: "كان مقرئا مجودا، حسن القيام على تفسير القرآن، محدثا راوية مكثرا، فقيها عارفا بأصول الفقه وعلم الكلام، حسن المشاركة في فنون العلم، يغلب عليه حفظ اللغة والآداب، موفور الحظ من علم العربية، يقرض يسيرا من الشعر". في تاريخ وفاته خلاف بين المؤرخين، وقد أخذت بما ذكره المراكشي وابن الأبار (١).

الغزالي [.. - ٥٢٠ هـ / .. - ١١٢٦ م]

<span data-type="title" id=toc-184>أحمد بن محمد بن محمد بن أحمد، أبو الفتوح، مجد الدين الطوسي الغزالي:</span>
أخو حجة الإسلام أبي حامد الغزالي، واعظ، كان من الفقهاء، غير أنه مال إلى الوعظ فغلب عليه. درس بالنظامية نيابة عن أخيه لما ترك التدريس زهادة فيه. ولد بطوس (مدينة في خراسان) وطاف البلاد وخدم الصوفية بنفسه، وكان مائلا إلى الانقطاع والعزلة. توفي بقزوين. وشهرته بالغزالي،
_________
(١) التكملة ١: ٧٠ والذيل والتكملة ٢/ ١: ٤٨١ وغاية النهاية ١: ١٣٦ وجذوة الاقتباس ٥٧ وطبقات المفسرين للسيوطي ٦ وفيه وفاته سنة ٥٦٢ ومثله في بغية الوعاة ١: ٣٨٢ والديباج ٥٧. وطبقات المفسرين للداودي ١: ٨٥ ووفاته فيه سنة ٥٢٢ هـ. وغاية النهاية ١: ١٣٦ ولم يذكر تاريخ وفاته.','1','64',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (137,'كأخيه، بتشديد الزاي، قال ابن الأثير في اللباب: أظن أن هذه النسبة إلى الغزال على عادة أهل جرجان وخوارزم، كالعضاري نسبة إلى العضار، وسمعت من يقول إنه بالتخفيف نسبة إلى غزالة قرية من طوس، وهو خلاف المشهور". وذكر الصفدي في ترجمته لحجة الإسلام أنه قال في بعض مصنفاته: "ونسبني قوم إلى الغزال، وإنما أنا الغزالي نسبة إلى قرية يقال لها غزالة، بتخفيف الزاي". من تصانيف صاحب الترجمة. "بحر المحبة في أسرار الموفة" في تفسير سورة يوسف (١).

القزويني [.. - حيا ٥٣٣ هـ / .. - حيا ١١٣٩ م]

<span data-type="title" id=toc-185>أحمد بن محمد بن الفضل، أبو بكر الخطيبي القزويني:</span>
محدث، مفسر، من فقهاء الشافعية، من أهل قزوين، سمع بها وبالري، وأجاز له كبار شيوخ قزوين. قال الرافعي: كان له حظ من الفقه والتفسير واللغة والنحو والشروط، ويقرأ عليه كل من هذه الفنون وهو ملازم
_________
(١) ابن خلكان ١: ٩٧ والوافي ٨: ١١٥ والمنتظم ٩: ٢٦٠ والسبكي ٤: ٥٤ والعبر ٤: ٤٥ والشذرات ٤: ٦٠ وطبقات الشافعية للحسيني ١٩٥ وهدية العارفين ٨٣٠١ ولسان الميزان ١: ٢٩٣ وروضات الجنات ٧٥ ومرآة الجنان ٣: ٢٢٤ والبداية والنهاية ١٢: ١٩٦ وذيل كشف الظنون ١: ١٦٦ واللباب ٢: ٣٧٩ والوافي ١: ٢٧٤ ترجمة حجة الإسلام الغزالي.','1','64',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (138,'مسجده، وكان ينظم الشعر، والقضاة يثقون بخطه وبجرحه وتعديله، ويعتمدون قوله" (١).

ابن ورد [٤٦٥ - ٥٤٠ هـ / ١٠٧٤ - ١١٤٥ م]

<span data-type="title" id=toc-186>أحمد بن محمد بن عمر بن يوسف بن إدريس بن عبد الله المري، أبو القاسم، المعروف بابن ورد:</span>
حافظ، محدث، مفسر، أصولي، من كبار فقهاء المالكية. من أهل المرية بالأندلس. قال ابن الزبير: "كان موفور الحظ من الأدب والنحو والتاريخ، متقدما في علم الأصول والتفسير، حافظا متفننا، انتهت الرياسة في مذهب مالك إليه وإلى القاضي أبي بكر العربي في وقتهما، لم يتقدمهما بالأندلس أحد في ذلك بعد وفاة القاضي ابي الوليد بن رشد، وكان له مجلس يتكلم فيه على الصحيحين، ويخص الأخمسة بالتفسير". وقال الصفدي: كان من بحور العلم بالأندلس (٢).
_________
(١) طبقات المفسرين للداودي ١: ٨٥.
(٢) الصلة ١: ٨٣ والديباج ٤١ والوافي ٨: ٧٢ وهدية العارفين ١: ٨٤ ومعجم الصدفي رقم ١٧ والمقتضب من التحفة ٢١ وبغية الملتمس ٣٦٢ وطبقات المفسرين للداودي ١: ٨٤.','1','65',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (139,'العتابي [.. - ٥٨٦ هـ / .. - ١١٩٠ م]

<span data-type="title" id=toc-187>أحمد بن محمد بن عمر العتابي البخاري، أبو نصر أو أبو القاسم، زين الدين:</span>
عالم بالفقه والتفسير، من كبار فقهاء الحنفية، نسبته إلى محلة عتاب ببخارا، ومات بها. من كتبه "تفسير القرآن" (١).

الرازي [.. - ٦٣١ هـ / .. - ١٢٣٤ م]

<span data-type="title" id=toc-188>أحمد بن محمد بن المظفر بن المختار الرازي، أبو العباس بدر الدين:</span>
مفسر، قاض، أديب، محدث، له نظم حسن، من فقهاء الحنفية، أصله من الري، وبها نشأ وتعلم. قدم دمشق وفسر القرآن الكريم على منبر جامعها. ورحل إلى بلاد الروم فتصدر للتدريس، ثم ولي القضاء. من كتبه "فضائل القرآن" و "حجج القرآن لجميع الملل والأديان" طبع، و "مباحث التفسير" مخطوط، في دار الكتب المصرية، وهو مناقشات لتفسير أبي إسحاق الثعلبي،
و
_________
(١) الجواهر المضية ١: ١١٤ والفوائد البهية ٣٦ وتاج التراجم ٩ والوافي ٨: ٧٤ وطبقات المفسرين للسيوطي ٦ وطبقات المفسرين للداودي ١: ٨٤ وكشف الظنون ٤٥٣ وهدية العارفين ١: ٨٧ والأعلام ١: ٢٠٩ ومعجم المؤلفين ٢: ١٤٠ واللباب ٢: ٣١٩. وطوبقبو ٢: ٤٣٢ وتذكرة النوادر ٥٨.','1','65',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (140,'"لطائف القرآن" فرغ منه سنة ٦٣٠ هـ، وتوفي في حدود التي بعدها (١).

ابن أبي عرفة [٥٥٧ - ٦٣٣ هـ / ١١٦٢ - ١٢٣٦ م]

<span data-type="title" id=toc-189>أحمد بن محمد بن أحمد بن أبي عرفة اللخمي العزفي السبتي:</span>
فقيه مالكي أندلسي، عارف بالتفسير، له نظم حسن. لزم التدريس بجامع سبتة طول حياته. من آثاره "منهاج الرسوخ في علم الناسخ والمنسوخ" مخطوط، في بغداد (٢).

ابن المنير [٦٢٠ - ٦٨٣ هـ /١٢٢٣ - ١٢٨٤ م]

<span data-type="title" id=toc-190>أحمد بن محمد بن منصور بن أبي القاسم ابن مختار، الجروي الجذامي السكندري، أبو العباس، ناصر الدين، المعروف بابن المنير:</span>
قاضي الاسكندرية وعالمها، كان إماما بارعا في الفقه والأصلين والعربية، له الباع الطويل في علم التفسير والقراءات والنظر والبلاغة والانشاء، خطيبا مصقعا، له شعر لطيف. ولي نظر الأحباس والمساجد وديوان النظر، ثم القضاء نيابة
_________
(١) طبقات المفسرين للداودي ١: ٨٦ وهدية العارفين ١: ٩٢ وإيضاح المكنون ٥٣ و ٧٠ و ١٩٧ و ٤٠٥ ومعجم المؤلفين ٢: ١٥٨ ومعجم المطبوعات ٢٤٦ والأزهرية ٣: ١٨٤ وفهرس المخطوطات المصورة ٣٩٠.
(٢) المكتبة ٥٤: ٦٢ ونيل الابتهاج ٦٣.','1','66',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (141,'عن القاضي ابن التنسي (سنة ٦٥١ هـ) ثم ولي القضاء استقلالا وخطابتها في سنة ٦٥٢ هـ ثم عزل، ثم ولي، ثم عزل. قال عز الدين بن عبد السلام: "ديار مصر تفتخر برجلين في طرفيه١: ابن المنير بالاسكندرية، وابن دقيق العيد بقوص". من كتبه "البحر الكبير في نخب التفسير" في تفسير القرآن العظيم، واعترض عليه في هذه التسمية بأن البحر الكبير مالح، وأجيب عن ذلك بأنه محل العجائب والدرر. وله أيضا "الانتصاف من الكشاف" طبع، ألفه في عنفوان الشبيبة، أثنى عليه عز الدين بن عبد السلام وشمس الدين الخسرو شاهي، وغيرهما من العلماء (١).

القرشي [.. - ٦٩٢ هـ / .. - ١٢٩٢ م]

<span data-type="title" id=toc-191>أحمد بن محمد القرشي الغرناطي، أبو العباس:</span>
محدث، فف! ر، من فقهاء المالكية، من أهل غرناطة بالأندلس،
_________
(١) فوات الوفيات ١: ١٤٩ والديباج ٧١ والوافي ٨: ١٢٨ والشذرات ٥: ٣٨١ والزركشي ٥٧ والعبر ٥: ٣٤٢ وحسن المحاضرة ١: ٣١٦ والنجوم الزاهرة ٧: ٣٦١ وبغية الوعاة ١: ٣٨٤ ومرآة الجنان ٤: ١٩٨ وطبقات المفسرين للداودي ١: ٨٨ وهدية العارفين ١: ٩٩ والذيل على كشف الظنون ١: ١٦٦ ومختصر دول الإسلام ٢: ١٤٣ ومفتاح السعادة ١: ٤٤٣ وروضات الجنات ٨٣ والأعلام ١: ٢١٢ ومعجم المؤلفين ٢: ١٦١.','1','66',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (142,'انتقل إلى بجاية في المغرب الأوسط، ومنها إلى المغرب الأقصى فجال فيه ولقي جماعة من العلماء، ثم دخل تونس - حاضرة افريقية - وتصدر للتدريس فيها واستمر إلى أن مات. قال صاحب عنوان الدراية: "له تآليف، منها على كتاب الله تعالى، طالعت بعضها". وقال صاحب شجرة النور له تآليف منها "تفسير القرآن الكريم" (١).

ابن عطاء الله [.. - ٧٠٩ هـ / .. - ١٣٠٩ م]

<span data-type="title" id=toc-192>أحمد بن محمد بن عبد الكريم بن عطاء الله بن عبد الرحمن، أبو الفضل، وأبو العباس، تاج الدين، ابن عطاء الله الاسكندري:</span>
متصوف شاذلي من أشهر صوفيي زمانه، له مشاركة في علم التفسير والحديث والفقه والأصول والنحو وغير ذلك. من أهل الاسكندرية، قدم القاهرة وتكلم بالجامع الأزهر وغيره بكلام على طريقة القوم، فأحبه الناس وكثرت أتباعه، وكان من أشد خصوم شيخ الإسلام ابن تيمية. وتوفي بالمدرسة المنصورية فيها (٢).
_________
(١) عنوان الدراية ٣٤٧ وشجرة النور ١: ١٩٩ وهو فيه أحمد بن عبد الله، ونيل الابتهاج ٦٤ ومعجم المؤلفين ٢: ١٤٥.
(٢) حسن المحاضرة ١: ٥٢٤ وشذرات الذهب ٦: ١٩ والنجوم الزاهرة ٨: ٢٨٠ والديباج ١: ٢٤٢ وشجرة النور ١: ٢٠٤ ودرة الحجال ١: ١٢ وطبقات =','1','67',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (143,'ابن البناء [٦٥٤ - ٧٢١ هـ / ١٢٥٦ - ١٣٢١ م]

<span data-type="title" id=toc-193>أحمد بن محمد بن عثمان الأزدي العددي، أبو العباس، ابن البناء:</span>
عالم في الرياضة والفلك، شارك في كثير من العلوم، من أهل مراكش، مولدا ووفاة. كان أبوه بناءا، وطلب هو العلم، فنبغ في فنون شتى. أخرج أكثر من سبعين كتابا في العدد والحساب والهندسة والجبر والفلك والتنجيم، وبقي كتابه "تلخيص أعمال الحساب" معمولا به في المغرب حتى نهاية القرن السادس عشر، وشرحه كثيرون من العلماء، واقتبس عنه علماء الغرب. قال ابن رشيد: "لم أر عالما بالمغرب إلا رجلين، ابن البنا العددي بمراكش وابن الشاط بسبتة". من كتبه "تفسير الباء من البسملة" و "حاشية على الكشاف" و "عنوان الدليل في مرسوم خط التنزيل" رسالة، مخطوطة، وجزء صغير على سورتي، "الكوثر" و "العصر" و "كتاب تسمية الحروف وخاصية وجودها في أوائل سور القرآن" (١).
_________
= السبكي ٥: ١٧٦ والدرر الكامنة ١: ٢٩١ والرحلة العياشية ١: ٣٥٧ وطبقات المفسرين للداودي ١: ٧٦ ومعجم المؤلفين ٢: ١٢١ ومعجم المطبوعات ١٨٤ ودائرة المعارف الإسلامية (ط ١) ١: ٢٤٠ وخطط مبارك ٧: ٦٩.
(١) الوفيات لابن قنفذ ٣٤٣ ونيل الابتهاج ٦٥ =','1','67',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (144,'القمولي [٦٤٥ - ٧٢٧ هـ / ١٢٤٧ - ١٣٢٧ م]

<span data-type="title" id=toc-194>أحمد بن محمد بن أبي الحزم مكي، أبو العباس، نجم الدين القرشي المخزومي القمولي:</span>
قاض، عارف بالتفسير، من فقهاء الشافعية، من أهل "قمولة" قرب "قوص" بصعيد مصر، تعلم بقوص ثم بالقاهرة. ولي قضاء قوص ثم أخميم ثم أسيوط والمنية والشرقية والغربية، ثم ولي نيابة الحكم بالقاهرة وحسبة مصر مع الوجه القبلي، ودرس بالفخرية وبالفائزية. من كتبه "تكلمة تفسير مفاتيح الغيب" للإمام فخر الدين الرازي (١).

ابن جبارة [٦٤٧ - ٧٢٨ هـ / ١٢٤٩ - ١٣٢٨ م]

<span data-type="title" id=toc-195>أحمد بن محمد بن عبد الولي بن جبارة (المقدسي المرداوي ثم الصالحي، شهاب الدين، أبو العباس)</span>
_________
= والدرر الكامنة ١: ٢٩٧ والبدر الطالع ١: ١٠٨ وهدية العارفين ١: ١٠٤ والأعلام ١: ٢١٣ وجذوة الاقتباس ٧٣ وتراث العرب العلمي ٣٧٨ والموسوعة العربية الميسرة ١١. وهو غير أحمد بن محمد المالقي المعروف بابن البنا المتوفي سنة ٧٢٤ هـ. وقد خلط بعض المؤرخين بينهما. وكشف الظنون ١٤٨٢.
(١) الطالع السعيد ١٢٥ وبغية الوعاة ١: ٣٨٣ وحسن المحاضرة ١: ٤٢٤ والدرر الكامنة ١: ٣٢٤ وطبقات السبكي ٥: ١٧٩ والنجوم الزاهرة ٨: ٢٧٩ والوافي ٨: ٩٢ وطبقات الأسنوي ٢٣١ وشذرات الذهب ٦: ٧٥ والبداية والنهاية ١٤: ١٣١ وطبقات المفسرين للداودي ١: ٨٦ وأعيان العصر ١٢٧ أوهدية العارفين ١: ١٠٥ وكشف الظنون ١٧٥٦ وطبقات النحويين واللغويين ٢٠٢.','1','68',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (145,'المقدسي المرداوي ثم الصالحي، شهاب الدين، أبو العباس: نحوي، مفسر، مقرىء، من فقهاء الحنابلة، سمع من ابن خطيب مردا وابن عبد الدائم وغيرها. ورحل إلى مصر وتعلم بها. وجاور بمكة، وأقرأ بدمشق وحلب. ثم استوطن بيت المقدس وتصدر لإقراء القرآن والعربية. ومات بها. قال الذهبي: "كان إماما مقررا بارعا فقيها نحويا، انتهت إليه مشيخة بيت المقدس". من كتبه "فتح القدير" في تفسير القرآن" (١).

العشاب [٦٤٩ - ٧٣٦ هـ / ١٢٥١ - ١٣٣٥ م]

<span data-type="title" id=toc-196>أحمد بن محمد بن إبراهيم بن محمد بن يوسف، أبو العباس المرادي القرطبي، المعروف بالعشاب:</span>
محدث، مفسر، مقرىء، نحوي، من أهل قرطبة بالأندلس. رحل إلى حاضرة إفريقية "تونس" ووزر لصاحبها أبي يحيى زكريا بن
_________
(١) الوافي ٨: ٢٥ وبغية الوعاة ١: ٣٦٣ والذيل على طبقات الحنابلة ٢: ٣٨٦ وغاية النهاية ١: ١٢٢ والدرر الكامنة ١: ٢٧٦ وطبقات المفسرين للداودي ١: ٨٠ والشذرات ٦: ٨٧ وطبقات القراء للذهبي ٢: ٥٩٣ وابن الوردي ٢: ٢٨٤ وابن كثير ١٤: ١٤٢ وهدية العارفين ١: ١٠٧ والأنس الجليل ٥٩٥ وكشف الظنون ٢: ١٢٣٣ وأعيان العصر ١١٨ ي ومعجم المؤلفين ٢: ١٢٦ والأعلام ١: ٢٢٢، وفيه: له كتاب في التفسير "مختصر الكشاف" مخطوط، الجزء الأول منه في الظاهرية. ومعجم أعلام فلسطين (مخطوط).','1','68',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (146,'أحمد الحفصي (٦٥٠ - ٧٢٧ هـ) ثم غادرها إلى الإسكندرية وحدث بها. له "تفسير" مختصر، وقد خلط بعض المؤرخين بينه وبين أحمد بن محمد بن أبي الخليل مفرج المعروف بابن الرومية، وبالعشاب، والمتوفى سنة ٦٣٧ هـ (١).

السمناني [٦٥٩ - ٧٣٦ هـ / ١٢٦١ - ١٣٣٦ م]

<span data-type="title" id=toc-197>أحمد بن محمد بن أحمد بن محمد، علاء الدولة البيابانكي، ركن الدين أبو المكارم السمناني:</span>
فقيه شافعي، محدث، مفسر، من علماء الصوفية. ولد في سمنان (بين الري والدامغان) وبها نشأ وتعلم. وداخل التتار في أول أمرهم، ثم رجع وأقبل على شأنه. ومرض، فأقام بتبريز مدة، ودخل بغداد مرات، وحج. وكان أبوه وعمه من الوزراء. قال الذهبي: كان أماما جامعا كثير التلاوة، حدث بصحيح مسلم وبعدة كتب ألفها وهي كثيرة. وكان يحط على محي الدين ابن عربي وعلى كتبه ويكفره .. وكان كثير البر، ينفق كل ما يحصل له من ريع أملاكه وهو نحو تسعين ألفا في العام. له مصنفات قيل: تزيد على ٣٠٠،
_________
(١) غاية النهاية ١: ١٠٠ والدرر الكامنة ١: ٢٥٦ وطبقات المفسرين للداودي ١: ٦٦ وشذرات الذهب ٦: ١١٢ والوافي ٧: ٣١٩ وأعيان العصر ١٠٧ ب وهدية العارفين ١: ٩٣ وإيضاح المكنون ١: ٣٠٨.','1','69',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (147,'منها "تفسير القرآن" في ثلاثة عشر مجلدا، و "نجم القرآن في تأويلات القرآن". توفي بقرية بيابانك (١).

ابن خضر [٧٠٦ - ٧٨٥ هـ / ١٣٠٦ - ١٣٨٣ م]

<span data-type="title" id=toc-198>أحمد بن محمد بن عمر بن الخضر بن مسلم، شهاب الدين، أبو العباس العمري الصالحي الدمشقي، المعروف بابن خضر:</span>
فقيه حنفي، أصولي، محدث، مفسر، من أهل دمشق. قال ابن حجر: "حدث بدمشق، وولي إفتاء دار العدل بها، وهو أول من وليه، وكان جلدا قويا، ودرس بأماكن .. " له "الصراط المستقيم في تبيان القرآن الكريم" في التفسير، قال صاحب كشف الظنون: وهو تفسير مختصر ممزوج كالجلالين، أوله التعوذ وتفسير الفاتحة إجمالا ثم الديباجة، ذكر فيها أنه تفسير وجيز وسيط في التبيان بسيط في الفوائد، اعتمد فيه على حديث حسن أو صحيح. قال: وسماه بعض الأبرار بطوالع الأنوار والمخطوطة جديرة بالنشر (٢).
_________
(١) الدرر الكامنة ١: ٢٦٦ والوافي بالوفيات ٧: ٣٥٦ وشذرات الذهب ٦: ١٢٥ وطبقات المفسرين للداودي ١: ٦٦ وطبقات الشافعية للأسنوي ١٤٣ وهدية العارفين ١: ١٠٨ وكشف الظنون ٤٤٩ و ١٩٣٠ وأعيان العصر ١٠٩ ب وفهرس المخطوطات المصورة ١: ١٧٢ وشستربتي ٣٥٤٣ وبروكلمان ٢: ٢٨١ والأعلام ١: ٢٢٣.
(٢) هدية العارفين ١: ١١٥ والدرر الكامنة ١: =','1','69',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (148,'الخجندي [٧١٩ - ٨٠٢ هـ / ١٣١٩ - ١٤٠٠ م]

<span data-type="title" id=toc-199>أحمد بن محمد بن محمد بن محمد الخجندي، جلال الدين، أبو الطاهر، ويعرف بالأخوي:</span>
محدث، مفسر، من فقهاء الحنفية. من أهل خجندة - مدينة كبيرة على طرف سيحون - وبها نشأ وتعلم. رحل في طلب العلم، فدخل سمرقند وبخارا وخوارزم ودمشق والحجاز وفلسطين وبغداد والمدائن. ثم استقر بالمدينة المنورة يدرس ويفتي ويصنف إلى أن مات. ودفن مع شهداء أحد. من كتبه "تفسير القرآن" لم يكمله و "حاشية على الكشاف" (١).

الطنبذي [.. - ٨٠٩ هـ / .. - ١٤٠٦ م]

<span data-type="title" id=toc-200>أحمد بن محمد بن عمر بن محمد، بدر الدين، الطنبذي:</span>
(نسبة إلى طنبذا قرية بمصر) مفسر، نحوي، تعلم بالقاهرة،
_________
= ٢٧٩ والشذرات ٦: ٢٨٦ وكشف الظنون ٢: ١٠٧٧ وفيه أنه شافعي المذهب، نزيل مكة، ونسبته الكازروزني. وغاية النهاية ١: ١١٣ ومعجم المؤلفين ٢: ٩٨ وفيه - نقلا عن بروكلمان - أنه كان حيا سنة ٩٢٣ هـ. ثم صفحة ١٣٨ وفبه وفاته ٧٨٥ هـ. وإنباه الغمر ١: ٢٨١ والأعلام ١: ٢٢٥ و ٢٣٢
(١) الضوء اللامع ٢: ١٩٤ والشذرات ٧: ١٦ وانباء الغمر ٢: ١١٦ و ١٥٦ ومراصد الاطلاع ١: ٤٥٣ وبلدان الخلافة الشرقية ٥٢٢ والبقاعي ١١٦ ترجمة رقم ٢٠ وهدية العارفين ١: ١١٧ ووفاته فيه سنة ٨٠٣ هـ. وطوبقبو ٤: ٣١٩ والأعلام ١: ٢٢٥.','1','70',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (149,'وكان من مشاهير الشافعية الأعلام بها. قال ابن قاضي شهبة: "اشتغل كثيرا، ولازم الأسنوي والبلقيني، وأفتى ودرس ووعظ، ومهر في العربية والتفسير والأصول والفقه، وسمع الحديث من جماعة، وكان ذكيا فصيحا يلقي على الطلبة دروسا حافلة، وتخرج به جماعة كثيرة، لكنه لم يكن مرضي الديانة" (١)

ابن الهائم [٧٥٣ - ٨١٥ هـ / ١٢٥٢ - ١٤١٢ م]

<span data-type="title" id=toc-201>أحمد بن محمد بن عماد الدين بن علي، أبو العباس، شهاب الدين، ابن الهائم:</span>
عالم بالرياضيات، والفقه وفرائضه، والنحو وإعرابه، ولد بالقاهرة وبها نشأ وتعلم. وانتقل إلى القدس فانقطع للتدريس والإفتاء، ودرس بأماكن وانتفع به الناس. قال ابن حجر: "عني بالفرائض والحساب حتى فاق الأقران في ذلك ورحل إليه الناس من الآفاق". وقال السخاوي: "انتهت إليه الرياسة في الحساب والفرائض وجمع في ذلك عدة تآليف عليها معول من بعده". مات في القدس. من كتبه "التبيان في تفسير غريب القرآن" مخطوط، جزء غير كبير (٢).
_________
(١) شذرات الذهب ٧: ٨٣ والسلوك ج ٤ ق ١: ٤٧ وهو فيه: أحمد بن عمر.
(٢) الضوء اللامع ٢: ١٥٧ والأنس الجليل ٢: ١١٥ والشذرات ٧: ١٠٩ وهدية العارفين ١: ١٢٠ =','1','70',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (150,'الزاهد [.. ٨١٩ هـ / .. - ١٤١٦ م]

<span data-type="title" id=toc-202>أحمد بن محمد بن سليمان، أبو العباس، شهاب الدين، المعروف بالزاهد:</span>
فقيه شافعي، متصوف. من أهل القاهرة. قال ابن حجر العسقلاني: "انقطع في بعض الأمكنة فاشتهر بالصلاح، ثم صار يتبع المساجد المهجورة فيبني بعضها ويستعين بأنقاض البعض في البعض، وأنشأ جامعا بالمقس يعظ الناس فيه خصوصا النساء. ونقموا عليه فتواه برأيه، من غير نظر جيد في العلم" وقال السخاوي: "وصنف كثيرا للمريدين ونحوهم". مات بالقاهرة ودفن في جامعه بالمقس. من آثاره "الفيض القدسي في آية الكرسي" (١).

البسيلي [.. - ٨٣٠ هـ / .. - ١٤٢٧ م]

<span data-type="title" id=toc-203>أحمد بن محمد بن أحمد البسيلي، أبو العباس:</span>
فقيه مالكي، مفسر، من أهل
_________
= وانباء الغمر ٢:.٢. وطبقات المفسرين للداودي ١: ٨١ والبدر الطالع ١: ١١٧ ومعجم المطبوعات ٢٦٩ وإيضاح المكنون ١: ٢٢٣ والأعلام ١: ٢١٧ ومعجم المؤلفين ٢: ١٣٧.
(١) الضوء اللامع ٢: ١١١ ومعجم المطبوعات ٣٧٧ وهدية العارفين ١: ٢١١ والخطط التوفيقية ٥: ٢ والسلوك ٣: ١٠٠ ونزهة النفوس والأبدان ٢: ٣٧٦.','1','71',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (151,'تونس، وقيل من أهل المسيلة بالجزائر. كان من تلاميذ محمد بن محمد بن عرفة (٧١٦ - ٨٠٣ هـ) حضر دروسه ابتداء من سنة ٧٨٥ هـ. له "تفسير كبير" جمع فيه املاءات شيخه ابن عرفة في دروسه التفسيرية، وأضاف له زيادات، يقع في مجلدين، منه عدة نسخ في الخزانة العامة بالرباط. وله "تقييد" صغير عن ابن عرفة يقف عند سورة "الصف" كما أنه لا يوجد به تفسير سور "الشورى" و "الزخرف" و "النجم" و "القمر". قال محمد المنوني: "وقام بتكميل هذا النقص الواقع في "التقييد الصغير" ابن غازي المكناسي المتوفى سنة ٩١٩ هـ) (١).

ابن زاغو [٧٨٢ - ٨٤٥ هـ / ١٣٨٠ - ١٤٤١ م]

<span data-type="title" id=toc-204>أحمد بن محمد بن عبد الرحمن، المعروف بابن زاغو:</span>
مفسر، من فقهاء المالكية، من أهل تلمسان. قال القلصادي: "كان أعلم الناس في وقته بالتفسير وأفصحهم". من كتبه "مقدمة في التفسير" و "تفسير
_________
(١) الأستاذ الباحث محمد المنوني في مجلة البحث العلمي عدد ٧: ٢٦١ وفي مجلة دعوة الحق العدد الثالث ديسمبر ١٩٧٣: ١٥٥ وانظر أيضا نيل الابتهاج ٧٧ والحلل السندسية ٣/ ١: ٦٥٠ وكشف الظنون ٤٣٨ ومعجم أعلام الجزائر (للمؤلف) ٢٩٩ وهو قيه المسيلي نقلا عن تعريف الخلف ٢: ٧٣. والأعلام ١: ٢٢٧ ومعجم المؤلفين ٢: ٨٥ وشجرة النور ٢٥١ وهو فيه ١: أحمد بن عمر.','1','71',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (152,'الفاتحة" قال صاحب نيل الابتهاج: "أنه في غاية الحسن كثير الفوائد" (١).

ابن زيد [٧٨٩ - ٨٧٠ هـ / ١٣٨٧ - ١٤٦٥ م]

<span data-type="title" id=toc-205>أحمد بن محمد بن أحمد بن أبي بكر بن زيد، شهاب الدين، أبو العباس:</span>
محدث، مفسر، له اشتغال بالتاريخ، من علماء الحنابلة. وهو صاحب "محاسن المساعي في مناقب أبي عمرو الأوزاعي" الذي نشره أمير البيان شكيب أرسلان. قال العليمي: "اعتنى بعلم الحديث كثيرا ودأب فيه، وكان استاذا في العربية، وله يد طولى في التفسير" (٢).

الشمني [٨٠١ - ٨٧٢ هـ / ١٣٩٩ - ١٤٦٨ م]

<span data-type="title" id=toc-206>أحمد بن محمد بن محمد بن حسن بن علي الشمني القسنطينى الأصل، الاسكندري، أبو العباس، تقي الدين:</span>
محدث، مفسر، نحوي، أصولي، متكلم، من
_________
(١) معجم أعلام الجزائر ٤٠ وشجرة النور ١: ٢٠٤ وهو فيه أحمد بن عبد الرحمن، ومثله في البستان ٤١ والأعلام ١: ١٤٣. وانظر الحلل السندسية ١/ ٤: ١٠٨٦ ومعجم المؤلفين ٢: ١١٦ ومجلة دعوة الحق عدد ذي القعدة ١٣٩٣ ص ١٥٩.
(٢) الضوء اللامع ٢: ٧١ والقلائد الجوهرية ٢٨٩ وشذرات الذهب ٧: ٣١٠ والأعلام ١: ٢١٩ ومقدمة محاسن المساعي.','1','72',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (153,'فقهاء الحنفية. ولد بالاسكندرية، وقدم القاهرة مع والده - وكان من علماء المالكية - فأخذ عن كبار علمائها. أخذ عنه السيوطي وقال في حقه: شيخنا الإمام العلامة المفسر المحدث الأصولي المتكلم النحوي البياني: إمام النحاة في زمانه، وشيخ العلماء في أوانه، أما التفسير فهو بحره المحيط، وكشاف دقائقه بلفظه الوجيز الفائق على الوسيط والبسيط ... " مات بالقاهرة (١).

ابن زكري [.. - ٨٩٩ هـ / .. - ١٤٩٣ م]

<span data-type="title" id=toc-207>أحمد بن محمد بن زكري، أبو العباس التلمساني:</span>
عالم تلمسان ومفتيها في زمنه أخذ عن ابن مرزوق الحفيد وابن زاكو وغيرهما. قال صاحب نيل الابتهاج: "العالم الحافظ المتفنن الإمام الأصولي الفروعي المفسر الأبرع المؤلف الناظم الناثر" (٢).
_________
(١) بغية الوعاة ١: ٣٧٥ والضوء اللامع ٢: ١٧٤ وشذرات الذهب ٧: ٣١٣ والبدر الطالع ١: ١١٩ وحوادث الدهور ٣: ٦٦٨ وحسن المحاضرة ١: ٢٧١ وبدائع الزهور ٢: ٩٩ وروضات الجنات ٩٢ وهدية العارفين ١: ١٣٢.
(٢) معجم أعلام الجزائر ٤٠ ونيل الابتهاج ٨٤ والبستان ٣٨ ودرة الحجال ١: ٩٠ وشجرة النور ٢٦٧ وتعريف الخلف ١: ٣٨ والاعلام ١: ٢٢٠ ومعجم المؤلفين ٢: ١٠٣.','1','72',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (154,'الأصم [.. - ٩٣٢ هـ / .. - ١٥٢٦ م]

<span data-type="title" id=toc-208>أحمد بن محمد الباني، شهاب الدين، أبو العباس، المعروف بالأصم:</span>
مفسر، من فقهاء الشافعية، من أهل مصر. له "تفسير" من سورة يس إلى آخر القرآن. قال صاحب شذرات الذهب "باعه مع بقية كتبه لفقره وفاقته" (١).

نشانجي زاده [٩٣٤ - ٩٨٦ هـ / ١٥٢٨ - ١٥٧٨ م]

<span data-type="title" id=toc-209>أحمد بن محمد بن رمضان الرومي، المعروف بنشانجي زاده:</span>
قاض، مفسر، من فقهاء الحنفية. ولد بالقسطنطينية وبها نشأ وتعلم، ثم درس بمدارسها المعروفة. ولي قضاء مكة، ثم قضاء مصر، ثم المدينة المنورة، وعزل قبل أن يتوجه إليها، وحج، ومات وهو في طريق العودة بالقرب من دمشق، فدفن بها. له "إعراب القرآن" وصل به إلى سورة الأعراف، و "حاشية" على تفسير البيضاوي (٢).
_________
(١) شذرات الذهب ٨: ١٨٣ وهدية العارفين ١: ١٤٢ وإيضاح المكنون ١: ٣٠٣ الكواكب السائرة ١: ١٢٩.
(٢) شذرات الذهب ٨: ٤٠٩ والعقد المنظوم ٤٩١ وهدية العارفين ١: ١٤٨ ومعجم المؤلفين ٢: ١٦٥ وذيل كشف الظنون ١: ١٤٢ وكشف الظنون ١: ١٢٣.','1','73',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (155,'الأردبيلي [.. - ٩٩٣ هـ / .. - ١٥٨٥ م]

<span data-type="title" id=toc-210>أحمد بن محمد الأردبيلي:</span>
فقيه إمامي، زاهد، عارف بالتفسير، من أهل أردبيل بأذربيجان. مات بكربلاء. له "زبدة البيان في شرح آيات أحكام القرآن" طبع (١)

ابن الملا [٩٣٧ - ١٠٠٣ هـ / ١٥٣٠ - ١٥٩٥ م]

<span data-type="title" id=toc-211>أحمد بن محمد بن علي بن محمد بن أحمد الحصكفي، شهاب الدين، المعروف بابن الملا:</span>
أديب، نحوي، عارف بالتفسير، مشارك في بعض العلوم، أصله من حصن كيفا (مدينة في تركية على نهر دجلة) ونسبته إليها. ولد بحلب، وتعلم بها وبدمشق والقسطنطينية. وقتل بقرية باتشا بالقرب من معرة نسرين. له كتب ورسائل منها "مبحث" عن موضع من أنوار التنزيل للبيضاوي في التفسير. وفي فهرس التيمورية (١: ٥٥) ذكر لرجل آخر اسمه: أحمد بن محمد الشهابي، توفي في نفس السنة، وله نفس الكتاب، وأغلب الظن أنهما شخص واحد (٢).
_________
(١) أعيان الشيعة ٩: ٢٩٢ ومنتهى المقال ٤٠ وروضات الجنات ٢٢ والأعلام ١: ٢٢٣ ومعجم المؤلفين ٢: ٩/ ١ وإيضاح المكنون ١: ٦٠٩.
(٢) إعلام النبلاء ٦: ١٣٨ وهدية العارفين ١: =','1','73',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (156,'السيواسي [.. - ١٠٠٦ هـ / .. - ١٥٩٧ م]

<span data-type="title" id=toc-212>أحمد بن محمد بن عارف الزيلي الرومي السيواسي، شمس الدين، أبو الثناء:</span>
أديب، فقيه حنفي، عارف بالتفسير والتاريخ، من أهل سيواس، (في أواسط تركيا الآسيوية). له تصانيف بالتركية والعربية، فمن التركية "نقد الخاطر" في تفسير سورة الكهف، أهداه للسلطان مراد الثالث الذي ولي السلطنة من سنة ٩٨٢ هـ (١٥٧٤ م) إلى سنة ١٠٠٣ هـ (١٥٩٥ م). و "لطائف الآيات ونقوش البينات" (١). شيخ زاده [.. - ١٠٣٣ هـ]

<span data-type="title" id=toc-213>أحمد بن محمد الزاهد الأدرنه وي، الرومي، الشهير بشيخ زاده:</span>
قاض، مفسر، من فقهاء الحنفية، له تصانيف منها "رسالة" في تفسير قوله تعالى: {فلا تجعلوا لله أندادا ..}. قال صاحب كشف الظنون: علقها حال كونه مدرسا باحدى المدارس السليمانية لتعيين مراد
_________
= ١٥١ وخلاصة الأثر ١: ٢٧٧ والشذرات ٨: ٤٤١ والتيمورية ١: ٥٥ و ٢: ٢٩٣ والأعلام ١: ٢٢٤ ومعجم المؤلفين ١: ١١٠ و ١٣٣.
(١) كشف الظنون ١٥٥٢ و ١٩٧٣ وهدية العارفين ١: ١٥٠ ومعجم المؤلفين ٢: ١١٤.','1','74',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (157,'(قصد) الزمخشري والبيضاوي، أوله١: الحمد لله الذي بين وحدانيته بإنزال الآيات التشريعية .. " الخ (١).

الآقحصاري [.. - ١٠٤٣ هـ / .. - ١٦٣٣ م]

<span data-type="title" id=toc-214>أحمد بن محمد الآقحصاري، المعروف بالرومي:</span>
فقيه حنفي، مفسر، تركي مستعرب، من مشايخ الخلوتية. نسبته إلى آق حصار في البوسنة، له "حاشية، على تفسير" أبي السعود من سورة الروم إلى سورة الدخان (٢).

الغنيمي [٩٦٤ - ١٠٤٤ هـ / ١٥٥٧ - ١٦٣٥ م]

<span data-type="title" id=toc-215>أحمد بن محمد بن علي، شهاب الدين الغنيمي الأنصاري الخزرجي:</span>
نحوي، فقيه حنفي، عارف بالتفسير، من أهل مصر. نسبته إلى غنيم - وهو أحد جدوده - كان يلقي دروسا في التفسير بجامع ابن طولون في القاهرة، وجمع ما علقه فيها على تفاسير البيضاوي والزمخشري وأبي السعود في كتاب سمي "حاشية الغنيمي في التفسير" مخطوط، في الظاهرية، بدمشق (٣).
_________
(١) كشف الظنون ٨٥٤ و ٨٨٠ وهدية العارفين ١: ١٥٦.
(٢) هدية العارفين ١: ١٥٧ وكشف الظنون ٦٠.
(٣) خلاصة الأثر ١: ٣١٢ ودار الكتب المصرية ٢: =','1','74',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (158,'ابن عميرة [.. - ١٠١٥ هـ / .. - ١٦٠٦ م]

<span data-type="title" id=toc-216>أحمد بن محمد بن عبد الرحمن الطهراني الصبري، المعروف بابن عميرة:</span>
من آثار. "المسائل الواضحة في الاستعاذة بالله والبسملة والفاتحة" (١).

الحموي [.. - ١٠٩٨ هـ / .. - ١٦٨٧ م]

<span data-type="title" id=toc-217>أحمد بن محمد مكي الحسني - ويقال الحسيني - الحموي، أبو العباس، شهاب الدين:</span>
من علماء الحنفية، مدرس، حموي الأصل، مصري. كان مدرسا بالمدرسة السليمانية بالقاهرة، وتولى إفتاء الحنفية. من تصانيفه الكثيرة "تحفة الأكياس في تفسير قوله تعالى: {إن أول بيت وضع للناس} - سورة آل عمران: ٦٩ - (٢).

الشهاب الخفاجي [٩٧٩ - ١٠٦٩ هـ / ١٥٧١ - ١٦٥٩ م]

<span data-type="title" id=toc-218>أحمد بن محمد بن عمر، شهاب الدين الخفاجي المصري:</span>
قاضي القضاة، الفقيه
_________
= ٧٣ والأزهرية ٣: ١٠٩ و ٧: ٣٤٨ وعلوم القرآن ٢٣٩ وهدية العارفين ١: ١٥٨ ومعجم المؤلفين ٢: ١٣٢.
(١) هدية العارفين ١: ١٥٣ وإيضاح المكنون ٢:
(٢) عجانب الآثار ١: ١٦٧ وسركيس ٣٧٥ وهدية العارفين ١: ١٦٤ ومعجم المؤلفين ٢: ٩٣ والأعلام ١: ٢٣٩.','1','75',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (159,'الأديب، الطبيب، وصاحب التصانيف في الأدب واللغة والسير. ولد ونشأ بمصر، وتعلم بها وبالقسطنطينية، وتولى القضاء في أسكوب وسلانيك ثم في مصر. ثم عزل عنها، فرحل إلى دمشق وحلب وعاد إلى بلاد الروم، فنفي إلى مصر وولي قضاءا يعيش منه، فاستقر يؤلف ويقرىء إلى أن مات. من كتبه "عناية القاضي وكشفاية الراضي" حاشية على أنوار التنزيل للبيضاوي، في التفسير، طبعت ببولاق سنة ١٢٨٣ هـ في ثمانية أجزاء (١).

الكواكبي [١٠٥٤ - ١١٢٤ هـ / ١٦٤٤ - ١٧١٢ م]

<span data-type="title" id=toc-219>أحمد بن محمد بن حسن بن أحمد الكواكبي:</span>
فقيه حنفي، قاض، مشارك في بعض العلوم، ولد بحلب، وتولى إفتاء الحنفية بها (سنة ١٠٩٦ هـ) ثم ولي قضاء القدس فقضاء أزنيق، فقضاء طرابلس الشام، ثم عزل، فرحل إلى الآستانة وتوفي
_________
(١) خلاصة الأثر ١: ٣٣١ وسلافة العصر ١: ٤٢٠ وصفوة من انتشر ١٢٨ وهدية العارفين ١: ١٦٠ ومعجم المطبوعات ٨٣٠ والفوائد البهية ٢٤٢ (التعليقات). وفهرس الفهارس ١: ٢٨٥ والأعلام ١: ٢٢٧ وتاريخ آداب اللغة العربية ٣: ٣٠٩ وبروكلمان ٢: ٢٨٥ والذيل ٢: ٣٩٦. وترجم الخفاجي لنفسه في كتابه "ريحانة الألبا". وكشف الظنون ٨٥٥.','1','75',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (160,'بها. له "حاشية، على جزء النبأ، كما كتب على مواضع كثيرة في التفسير (١).

أشرف زاده [.. - ١١٥٢ هـ / .. - ١٧٣٩ م]

<span data-type="title" id=toc-220>أحمد بن محمد (فخر الدين) الأزنيقي البرسوي، عز الدين، الرومي، المعروف بأشرف زاده:</span>
مفسر، صوفي، تركي مستعرب. توفي بالآستانة. له "أنيس الجنان في تفسير القرآن" (٢).

القازآبادي [.. - ١١٦٣ هـ / .. - ١٧٤٩ م]

<span data-type="title" id=toc-221>أحمد بن محمد بن إسحاق القازابادي الرومي، أبو النافع:</span>
فقيه حنفي، أصولي، مفسر، من القضاة، ولي قضاء مكة المكرمة في عهد السلطان محمود الأول، وعزل، فعاد إلى الآستانة وتوفي بها. من كتبه "تنوير البصائر بأنوار التنزيل وتوقير السرائر بأسرار التأويل"، حاشية على تفسير البيضاوي. (مخطوطة) طبع منه "حاشية على تفسير الفاتحة" (٣).
_________
(١) اعلام النبلاء ٦: ٣٨٦ وسلك الدرر ١: ١٧٥ والأعلام ١: ٢٢٩ ومعجم المؤلفين ٢: ٩٠ وهدية العارفين ١: ١٦٩.
(٢) هدية العارفين ١: ١٧٣ وإيضاح المكنون ١: ١٤٨ ومعجم المؤلفين ٢: ٨٠.
(٣) هدية العارفين ١: ١٧٥ وإيضاح المكنون ١: ٣٣٣ ومعجم المؤلفين ٢: ٨١.','1','76',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (161,'السحيمي [.. - ١١٧٨ هـ / .. - ١٧٦٥ م]

<span data-type="title" id=toc-222>أحمد بن محمد بن موفق الدين علي السحيمى القرشي الحسني:</span>
عارف بالتفسير، فقيه، من أعيان الشافعية وصلحائهم، كان يقيم بقلعة الجبل بالقاهرة، وتصدر للتدريس بجامعها. من كتبه "تفسير سورة والفجر" مخطوط و "تاج البيان لألفاظ القرآن" مخطوط، الجزء الأول منه، و "مناهج الكلام على آيات الصيام" (١).

الدردير [١١٢٧ - ١٢٠١ هـ / ١٧١٥ - ١٧٨٦ م]

<span data-type="title" id=toc-223>أحمد بن محمد بن أحمد بن أبي حامد العدوي، أبو البركات، الشهير بالدردير:</span>
صوفي، من فقهاء المالكية، مشارك في بعض العلوم. ولد في بني عدي، من صعيد مصر، وتعلم بالأزهر، وتولى مشيخة الطريقة الخلوتية، والافتاء بمصر، وتوفي بالقاهرة. من كتبه رسالة في "متثابهات القرآن" (٢).
_________
(١) عجائب الآثار ١: ٢١٨ وهدية العارفين ١: ١٧٧ وإيضاح المكنون ٢: ١٠٢ و ٥٦٤ والأعلام ١: ٢٣٠ ومعجم المؤلفين ٢: ١٣٠ وشجرة النور ٣٥٩ ومعجم المطبوعات ١٠١٢.
(٢) عجائب الآثار ٤: ٤٧ وهدية العارفين ١: ١٨١ واليواقيت الثمينة ١: ٥٦ ومعجم المطبوعات ٨٦٩ =','1','76',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (162,'ابن عجيبة [١١٦٠ - ١٢٢٤ هـ / ١٧٤٧ - ١٨٠٩ م]

<span data-type="title" id=toc-224>أحمد بن محمد بن المهدي، ابن عجيبة، الحسني الأدريسي الشاذلي الفاسي، أبو العباس:</span>
مفسر صوفي، مشارك في أنواع من العلوم، من أهل المغرب. من تصانيفه "البحر المديد في تفسير القرآن المجيد" مخطوط، في أربعة مجلدات، طبع جزء منه (١).

الموسوي [.. - ١٢٣٥ هـ / .. - ١٨٢٠ م]

<span data-type="title" id=toc-225>أحمد بن محمد بن علي الموسوي المرعشي الخراساني:</span>
مفسر، محدث، متكلم حكيم، من فقهاء الشيعة الإمامية. مات مسموما. له تصانيف. ترجم له العاملي وأثنى عليه (٢).

الصاوي [١١٧٥ - ١٢٤١ هـ / ١٧٦١ - ١٨٢٥ م]

<span data-type="title" id=toc-226>أحمد بن محمد الخلوتي، الشهير بالصاوي:</span>
فقيه مالكي، مفسر، بباني،
_________
= وفهرس الفهارس ١: ٢٩٣ وشجرة النور ٣٥٩ وكنز الجوهر ١٦٠ وجامع الكرامات ١٥٥.
(١) فهرس الفهارس ٢: ٢٢٨ وجامع الكرامات ١٦١ ومعجم المطبوعات ١٦٩ واليواقيت الثمينة ١: ٧٠ وشجرة النور ٤٠٠ ومعجم المؤلفين ٢: ١٦٣ والأعلام ١: ٢٣٤.
(٢) أعيان الشيعة ١٤: ٥٨٤.','1','77',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (163,'ولد في "صاء الحجر" من إقليم الغربية بمصر. وانتقل إلى القاهرة (سنة ١١٨٧ هـ) وتعلم بالأزهر. مات بالمدينة المنورة. من كتبه "حاشية على تفسير الجلالين" طبعت (١)

الوحيد البهبهاني [١١٩١ - ١٢٤٣ هـ / ١٧٧٧ - ١٨٢٧ م]

<span data-type="title" id=toc-227>أحمد بن محمد علي بن محمد باقر، المعروف بالوحيد البهبهاني، الحائري الكرمانشاهي:</span>
من علماء الشيعة الإمامية، مشارك في أنواع من العلوم كالفقه والتفسير والحديث والأصول والتاريخ. من أهل كرمانشاه مولدا ووفاة. من كتبه "تفسير القرآن" (٢).

المرصفي [.. - ١٣٠٦ هـ / .. - ١٨٨٨ م]

<span data-type="title" id=toc-228>أحمد بن محمد، شرف الدين المرصفي:</span>
عالم أزهري، من فقهاء الشافعية. قام بتدريس التفسير والحديث في دار العلوم بالقاهرة. نسبته إلى مرصفا من قرى مصر الكبيرة (٣).
_________
(١) معجم المطبوعات ٣٧٦ وهدية العارفين ١: ١٨٤ واليواقيت الثمينة ٦٤ والمكتبة البلدية قسم التفسير ٢٢ والأعلام ١: ٢٣٣ ومعجم المؤلفين ٢: ١١١.
(٢) أعيان الشيعة ١٠: ٣٥ وأعلام الشيعة ٢: ١٠٠ ومعجم المؤلفين ٢: ١٣٣.
(٣) معجم المطبوعات ١٧٣٤ وهدية العارفين ١: ١٩٣.','1','77',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (164,'اليملاحي [.. - ١٣٥٨ هـ / .. - ١٩٣٩ م]

<span data-type="title" id=toc-229>أحمد بن محمد العلمي اليملاحي:</span>
مفسر، فقيه مالكي، مولده ووفاته بمراكش، وكان عالمها ومدرسها. من كتبه " تفسير القرآر" في عدة أجزاء (١).

أحمد محمد شاكر [١٣٠٩ - ١٣٧٧ هـ / ١٨٩٢ - ١٩٥٨ م]

<span data-type="title" id=toc-230>أحمد بن محمد شاكر بن أحمد بن عبد القادر، من آل أبي علياء الحسيني، شمس الدين، أبو الأشبال:</span>
عالم بالحديث والتفسير، أديب، مولده ووفاته بالقاهرة. وأصله من جرجا بصعيد مصر. رحل مع والده إلى السودان (١٩٠٠) فألحقه بكلية غوردون، ثم بمعهد الإسكندرية (١٩٠٤) ثم التحق بالأزهر وحاز على شهادة العالمية منه (١٩١٧). وعين مدرسا بمدرسة ماهر، فموظفا قضائيا، فقاضيا، حتى سنة ١٩٥١ فرئيسا للمحكمة الشرعية العليا. وأحيل إلى المعاش فانقطع للتأليف والنشر. اختصر تفسير ابن كثير وسماه "محمدة التفسير" طبع أربعة أجزاء منه (٢).
_________
(١) الأعلام ١: ٢٥١.
(٢) مجلة معهد المخطوطات ٤: ٣٥٦ والأهرام ١٥/ ٦/ ٥٨ والمجلة (بالقاهرة) عدد تموز (يوليو) ١٩٥٨ والأديب س ١٧ عدد ٨: ٥١.','1','78',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (165,'السيواسي [.. - ٩٦٠ هـ / .. - ١٤٥٦ م]

<span data-type="title" id=toc-231>أحمد بن محمود السيواسي، شهاب الدين:</span>
مفسر، نحوي، فرضي، من فقهاء الحنفية، ولد وتعلم بسيواس، ثم انتقل إلى بلدة "آياثلوغ" ودرس بها إلى أن توفي. من آثاره "عيون التفاسير للفضلاء السماسير" مخطوط، قال صاحب كشف الظنون: "ذكر فيه أن العلماء صنفوا تفاسير بعبارات رائعة، لكن كان الاطلاع لبعض الطلاب صعبا منها لدقة مسالكها، فالتجأت إلى الله أن انتخب منها تفسيرا مختصرا قريبا من التناول ضافيا وافيا تيسيرا لكل طالب فهيم ... " (١).

القرماني [.. - ٩٧١ هـ / .. - ١٥٦٣ م]

<span data-type="title" id=toc-232>أحمد بن محمود الأصم اللارندي القرماني:</span>
مفسر، من فقفاء الحنفية، من أهل قرمان (في وسط تركيا الآسيوية) واسمها القديم لارندة. ذكره صاحب هدية العارفين وقال: "له "تفسير القرآن" إلى سورة المجادلة، في اثني عشر مجلدا". وزاد صاحب كشف الظنون: "ولم يكملها. وقد خلط بعض الباحثين
_________
(١) هدية العارفين ١: ١١٨ وكشف الظنون ١١٨٥ والبلدية قسم التفسير ٣٠ وعثمانلي مؤلفاري ١: ٩٠ وعنه أخذت تاريخ وفاته. والشقائق النعمانية ٢٢.','1','78',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (166,'بينه وبين حمزة بن عمود القرماني (انظر ترجمته) صاحب الحاشية عل تفسير البيضاوي المسماة "تفسير التفسير". وحمزة هذا مات سنة ٨٩٩ كما في الفوائد البهية. أو سنة ٨٧١ كما في كشف الظنون ١٩٠ ومثله في هدية العارفين ١: ٣٣٧، أو سنة ٩٦٥ كما في شذرات الذهب، أو في أوائل المائة التاسعة كما في الشقائق النعمانية (١).

الشيرازي [.. - ١١٢٦ هـ / .. - ١٧١٤ م]

<span data-type="title" id=toc-233>أحمد بن مرتضى الحسني الشيرازي:</span>
مفسر، من علماء الشيعة الإمامية، من أهل شيراز. من آثاره "تفسير القرآن" (٢).

الخزرجي [.. - ٦٠١ هـ / .. - ١٢٠٤ م]

<span data-type="title" id=toc-234>أحمد بن مسعود بن محمد القرطبي الخزرجي، أبو العباس:</span>
من علماء المالكية في وقته. قال المقري: كان إماما في التفسير والفقه والحساب والفرائض والنحو واللغة والعروض والطب، وله تآليف حسان وشعر رائق. رحل من الأندلس إلى المشرق (٣).
_________
(١) هدية العارفين ١: ١٤٥ وكشف الظنون ١٩٥،
(٢) أعيان الشيعة ١: ٢١١.
(٣) نفح الطيب ٢: ٦١٤ وهدية العارفين ١: ٨٩ ومعجم الأطباء ١٢٥ والبداية والنهاية ١٣: ٤٢.','1','79',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (167,'طاشكبري زاد [٩٠١ - ٩٦٨ هـ / ١٤٩٥ - ١٥٦١ م]

<span data-type="title" id=toc-235>أحمد بن مصطفى بن خليل، أبو الخير، عصام الدين طاشكبري زاده:</span>
مؤرخ، مشارك في كثير من العلوم، تركي الأصل، مستعرب. ولد في بروسة، وتعلم في أنقرة والآستانة وأماسية، ثم تنقل في المدن التركية مدرسا، ثم ولي القضاء بالقسطنطينية وحلب. وكشف بصره سنة ٩٦١ هـ. من كتبه "حاشية على الكشاف" في التفسير، و "رسالة" في تفسير آية الوضوء"، و "صورة الخلاص في سورة الإخلاص" وتفسير "قوله تعالى: {هو الذي خلق لكم ..} (١).

القزويني [١٢٤٧ - ١٣٠٧ هـ / ١٨٣١ - ١٨٩٠ م]

<span data-type="title" id=toc-236>أحمد بن مصطفى بن أحمد بن مصطفى الخويني القزويني:</span>
عالم إمامي، من أهل قزوين. له "حاشية" عل تفسير الصافي، إلى آخر سوره البقرة (٢).
_________
(١) الشقائق النعماية ٣٢٥ والعقد المنظوم ٣٣٦ والشذرات ٨: ٣٥٢ والبدر الطالع ١: ١٢١ وهدية العارفين ١: ١٤٣ وتاريخ آداب اللغة العربية ٣: ٣٣٩ والأعلام ١: ٢٤١ ومعجم المؤلفين ٢: ١٧٧.
(٢) أعلام الشيعة ١: ١٧٠.','1','79',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (168,'ابن عليوة [١٢٩١ - ١٣٥٣ هـ / ١٨٧٤ - ١٩٣٤ م]

<span data-type="title" id=toc-237>أحمد بن مصطفى بن محمد بن أحمد المستغانمي، أبو العباس، الشهير بالعلوي:</span>
متصوف، من أهل مستغانم. رحل إلى المغرب الأقصى وتونس وليبيا والحجاز والشام والآستانة، ثم عاد إلى بلده. كان من معارضي الحركة الإصلاحية التي قادها عبد الحميد بن باديس. من آثاره "باب العلم في تفسير سورة "والنجم" (١).

المراغي [.. - ١٣٧١ هـ / .. - ١٩٥٢ م]

<span data-type="title" id=toc-238>أحمد بن مصطفى المراغي:</span>
مفسر، من العلماء. تخرج بدار العلوم بالقاهرة ثم درس بها. وعين أستاذا للعربية والشريعة الإسلامية بكلية غوردون بالخرطوم. توفي بالقاهرة، من آثاره "تفسير القرآن" ويعرف بتفسير المراغي، طبع، في ثمانية مجلدات (٢).

ابن الأقليشي [.. - ٥٥٠ هـ / .. - ١١٥٥ م]

<span data-type="title" id=toc-239>أحمد بن معد بن عيسى بن وكيل التجيبي، أبو العباس ابن الاقليشي:</span>
عالم
_________
(١) معجم أعلام الجزائر ٣٦٧ وفي الحاشية أسماء مراجع أخرى.
(٢) الأزهرية ١: ٢٤٥ والأعلام ١: ٢٥٨.','1','80',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (169,'بالحديث والتفسير، من فقهاء المالكية. ولد ونشأ في دانية بالأندلس، وأصله من أقليش. رحل إلى المشرق، وجاور بمكة سنين. وعاد يريد المغرب، فتوفي بقوص - من صعيد مصر -. من آثاره "تفسير العلوم والمعاني" لسورة الفاتحة، مخطوط (١).

ابن المعذل [.. : - نحو ٢٤٠ هـ / .. - نحو ٨٥٤ م]

<span data-type="title" id=toc-240>أحمد بن المعذل بن غيلان بن الحكم العبدي - من بني عبد القيس - أبو الفضل:</span>
عالم بالحديث والتفسير والفقه، من كبار فقهاء المالكية. كوفي الأصل، من أهل البصرة، وكان أهلها يسمونه الراهب لفقهه ونسكه. قال عياض: "وجدت في بعض الكتب أنه توفي وقد قارب الأربعين". وذكره الذهبي في وفيات سنة ٢٤٠ هـ. من كتبه "أحكام القرآن" (٢).
_________
(١) نفح الطيب ٢: ٥٩٩ وإنباه الرواة ١: ١٧٦ وأخبار وتراجم أندلسية ٢٤ والتكملة لابن الأبار ٦٠ ومعجم البلدان مادة اقليش. والأزهرية (ط ٢) ١: ٢٣٨ وهدية العارفين ٨٥.
(٢) الوافي ٨: ١٨٤ والأغاني ١٣: ٢٥١ وطبقات ابن المعتز ٨٣٦٨ والعبر ١: ٤٣٤ وطبقات المفسرين للداودي ١: ٩١ والديباج ٣٠ وترتيب المدارك ١: ٥٥٠ والشذرات ٢: ٩٥ وصمط اللالي ٣٢٥ ترجمة أخيه عبد الصمد.','1','80',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (170,'الصدفي [٤٠٦ - ٤٥٩ هـ / ١٠١٥ - ١٠٦٧ م]

<span data-type="title" id=toc-241>أحمد بن مغيث بن أحمد بن مغيث الصدفي الطليطلي، أبو جعفر:</span>
عالم بالحديث وعلله، وبالفرائض والحساب واللغة والنحو، وله يد طولى في التفسير. من أهل طليطلة بالأندلس. مولدا ووفاة (١).

ابن مردويه [٣٢٣ - ٤١٠ هـ / ٩٣٥ - ١٠١٩ م]

<span data-type="title" id=toc-242>أحمد بن موسى بن مردويه بن فورك بن موسى بن جعفر الأصبهاني، أبو بكر، ويقال له ابن مردويه الكبير:</span>
محدث، حافظ، مفسر، مؤرخ، من أهل أصبهان. قال الذهبي: "عمل المستخرج على صحيح البخاري وكان قيما بمعرفة هذا الشأن بصيرا بالرجال طويل الباع مليح التصانيف". منها "التفسير الكبير" في سبع مجلدات (٢).
_________
(١) إنباه الرواة ١: ١٣٥ وطبقات المفسرين للسيوطي ٦ وطبقات المفسرين للداودي ١: ٩٣ والديباج ٤٠ والصلة ١: ٦٠ وهدية العارفين ١: ٧٨ وكشف الظنون ٢: ١٨٠٩.
(٢) الوافي ٨: ٢٠١ وتذكرة الحفاظ ١٠٥٠ والعبر ٣: ١٠٢ والشذرات ٣: ١٩٠ وتاريخ أصبهان ١: ١٦٨ وطبقات الداودي ١: ٩٣ والرسالة المستطرفة ٢٦ والنجوم الزاهرة ٤: ٢٤٥٠ وهدية العارفين ١: ٧١.','1','81',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (171,'ابن طاووس [.. - ٦٧٣ هـ / .. - ١٢٧٤ م]

<span data-type="title" id=toc-243>أحمد بن موسى بن جعفر بن محمد بن أحمد بن محمد بن طاووس العلوي الحسني، جمال الدين:</span>
فقيه إمامي، من كبارهم، محدث، عارف بالأدب، له شعر. من أهل الحلة. قال في أمل الآمل: "فقيه أهل البيت، حقق الرجال والرواية والتفسير تحقيقا لا يزيد عليه، وكتبه تقع في اثنين وثمانن مجلدا من أحسن التصانيف .. ". منها "شواهد القرآن" مجلدان (١).

الحسيني [.. - ٦٨٩ هـ / .. - ١٢٩٠ م]

<span data-type="title" id=toc-244>أحمد بن ناصر بن طاهر الحسيني، برهان الدين، أبو المعالي:</span>
مفسر، أصولي، من علماء الحنفية وصلحائهم، من أهل دمشق، وإمام محراب الحنفية بجامعها الكبير. من كتبه "تفسير القرآن" في سبع مجلدات (٢).
_________
(١) أمل الآمل ٢: ٢٩ وأعيان الشيعة ١٠: ٢٧٥ ومنهج المقال ٤٨ وروضات الجنات ١٩ وشعراء الحلة ١: ١٠٧ وبروكلمان، الذيل ١: ٧١١.
(٢) الجواهر المضية ١: ١٢٩ والوافي ٨: ٢٠٩ وتاج التراجم ١١ وكشف الظنون ٤٤٣ وطبقات المفسرين للداودي ١: ٩٤ وطبقات المفسرين للسيوطي ٧ وهو فيه: إسماعيل بن ناصر. وهدية العارفين ١: ١٠٠.','1','81',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (172,'ابن نصر الله [٧٦٥ - ٨٤٤ هـ / ١٣٦٤ - ١٤٤٠ م]

<span data-type="title" id=toc-245>أحمد بن نصر الله بن أحمد بن محمد بن عمر البغدادي ثم المصري، أبو الفضائل، محب الدين، المعروف بابن نصر الله:</span>
قاض، أصولي، مفسر، فقيه حنبلي، انتهت إليه مشيخة الحنابلة بمصر في وقته. ولد ببغداد، وسمع بها وبدمشق وحلب والقاهرة. وسكن القاهرة فولي بها الافتاء وقضاء الحنابلة. قال في الشذرات: "كان متضلعا بالعلوم الشرعية من تفسير وحديث وفقه وأصول، وانتفع به الناس" (١).

ثعلب [٢٠٠ - ٢٩١ هـ / ٨١٦ - ٩٠٤ م]

<span data-type="title" id=toc-246>أحمد بن يحيى بن زيد بن سيار الشيباني بالولاء، أبو العباس، المعروف بثعلب:</span>
نحوي، لغوي، ولد ومات ببغداد. درس النحو والشعر والمعاني والغريب على ابن الأعرابي والفراء وسلمة بن عاصم وغيرهم. اشتهر بالحفظ ومعرفة العربية ورواية الشعر القديم، ولكنه لم يكن فصيح العبارة ولا قادرا على التعليلات والأقيسة النحوية. وكان إمام الكوفيين في بغداد في زمانه، ورأى أحد عشر خليفة أولهم المأمون
_________
(١) الضوء اللامع ٢: ٢٣٣ والقلائد الجوهرية ٣٧٤ وشذرات الذهب ٧: ٢٥٠ وتاريخ العراق ٣: ١١٨ والأعلام ١: ٢٥٠ ومعجم المؤلفين ٢: ١٩٥.','1','82',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (173,'وآخرهم المكتفي. ثقل سمعه قبل موته، ثم صم، فصدمته فرس فسقط على رأسه في هوة، فتوفي على الأثر. من كتبه "إعراب القرآن" و "معاني القرآن" و "غريب القرآن" (١).

الناصر العلوي [.. - ٣٢٥ هـ / .. ٩٣٧ م]

<span data-type="title" id=toc-247>أحمد بن يحيى بن الحسين بن القاسم الحسني العلوي، الناصر لدين الله:</span>
إمام زيدي بماني، من علمائهم وبسلائهم. تولى الإمامة سنة ٣٠٩ هـ عندما اعتزلها أخوه الأكبر، وقاتل القرامطة بعدن وظفر بهم. واستمر إلى أن توفي. من كتبه "تفسير القرآن" مخطوط، قال صاحب تاريخ التراث العربي: هذا إكمال للتفسير
_________
(١) إنباه الرواة ١: ١٣٨ وبغية الوعاة ١: ٣٩٦ وتاريخ بغداد ٥: ٢٠٤ والوافي ٨: ٢٤٣ والعبر ٢: ٨٨ والبداية والنهاية ١١: ٩٨ ومرآة الجنان ٢: ٢١٩ ووفيات الأعيان ١: ١٠٢ وطبقات الحنابلة ١: ٨٣ والشذرات ٢: ٢٠٨ والنجوم الزاهرة ٣: ١٣٣ وغاية النهاية ١: ١٤٨ ونزهة الألباء١٥٧ وتذكرة الحفاظ ٦٦٦ وطبقات النحاة لابن قاضي شهبة ١: ٢٠٢ وطبقات المفسرين ١: ٩٤ وتهذيب الاسماء واللغات ٢: ٢٥٧ ومعجم الأدباء٥: ١٠٢ وابن النديم وطبقات الزبيدي ١٥٥ ونور القبس ٣٣٤ ومفتاح السعادة ١: ١٨٠ وتاريخ آداب اللغة العربية ٢: ٢٠٩ وهدية العارفين ١: ٥٤ وسلم الوصول ١٥٨ وتلخيص ابن مكتوم ٢٤ ومختصر دول الإسلام ١: ١٣٨ ومقدمة كتاب "مجالس ثعلب".','1','82',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (174,'الذي بدأ جده وأضاف إليه أبوه وأخوه" (١).

حفيد التفتازاني [.. - ٩١٦ هـ / .. - ١٥١٠ م]

<span data-type="title" id=toc-248>أحمد بن يحيى بن محمد بن مسعود، سيف الدين، المعروف بحفيد التفتازاني:</span>
شيخ الإسلام ورئيس العلماء بهراة في وقته. توفي مقتولا سنة ٩١٦ هـ وقيل ٩٠٦ هـ. من كتبه "تعليقة" عل أوائل الكشاف للزمخشري في التفسير، وصل فيها إلى أواسط سورة البقرة (٢).

ابن بقي [٥٣٧ - ٦٢٥ هـ / ١١٤٣ - ١١٢٨ م]

<span data-type="title" id=toc-249>أحمد بن يزيد بن عبد الرحمن بن أحمد بن بقي بن مخلد الأموي، أبو القاسم، ويعرف بابن بقي:</span>
قاضي القضاة بالمغرب، كانت له إمامة في اللغة وعلم العربية. من أهل قرطبة. ولي قضاء الجماعة بمراكش مضافا ذلك إلى خطتي المظالم والكتابة العليا، ثم ولي قضاء بلدة قرطبة. له كتاب في "الآيات المتشابهات"
_________
(١) تاريخ التراث العربي ٢: ٣٠٨ والأعلام ١: ٢٥٣ وبلوغ المرام ٣٣ ومعجم المؤلفين ٢: ٢٥٢ وتراجم الرجال ٦.
(٢) روضات الجنات ٩٣ وهدية العارفين ١: ١٣٨ وكشف الظنون ٢: ١٤٨٠ ومعجم المؤلفين ٢: ٢٠٦.','1','83',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (175,'قيل أنه أحسن ما كتب في بابه، وكان لا يفارقه في سفر، ولا في حضر (١).

ابن أصبع [.. - ٤٧٩ هـ / .. - ١٠٨٦ م]

<span data-type="title" id=toc-250>أحمد بن يوسف بن أصبع بن خضر الأنصاري، أبو عمر:</span>
مفسر، محدث، فرضي، قاض، من فقهاء المالكية، من أهل طليطلة بالأندلس، وبها نشأ وتعلم، ثم رحل إلى المشرق، وحج.: عاد، فولي القضاء ببلده. قال ابن بشكوال: "كان ماهرا في الحديث والتفسير والفرائض وشوور في الأحكام". توفي بقرطبة (٢).

الكواشي [٥٩٠ - ٦٨٠ هـ / ١١٩٤ - ١٢٨١ م]

<span data-type="title" id=toc-251>أحمد بن يوسف بن الحسن بن رافع بن الحسين بن سويدان الشيباني الموصلي، موفق الدين، أبو العباس الكواشي:</span>
عالم بالتفسير، مقرىء، من فقهاء الشافعية. ولد بكواشة (قلعة بالموصل) ودخل دمشق وأخذ عن السخاوي وغيره، وحج وزار بيت المقدس، ثم عاد إلى بلده. وقد كف
_________
(١) ابن الأبار ١: ١١٥ وقضاة الأندلس ١١٧ وبغية الوعاة ١: ٣٩٩ وهدية العارفين ١: ٩١ والاعلام ١: ٢٥٧ ومعجم المؤلفين ٢: ٢٠٦.
(٢) طبقات المفسرين للداودي ١: ٩٨ وطبقات المفسرين للسيوطي ٦ والصلة ١: ٧١.','1','83',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (176,'بصره بعد بلوغه السبعين. قال الذهبي: كان منقطع القرين عديم النظير زهدا وصلاحا وتبتلا وصدقا واجتهادا، وكان يزوره السلطان فمن دونه فلا يعبأ بهم ولا يقم لهم، ولا يقبله لهم شينا" من كتبه "تبصرة المتذكر وتذكرة المتبصر" (مخطوط) "تفسير القرآن" قال صاحب كشف الظنون: ثم لخصه في مجلد وسماه "التلخيص" فرغ منه سنة ٦٤٩ هـ ". وقال الذهبي: "جود فيه الإعراب، وحرر أنواع الوقوف، وأرسل منه نسخة إلى مكة والمدينة والقدس". وقال السيوطي: وعليه اعتمد الشيخ جلال الدين المحلى في تفسيره، واعتمدت عليه أنا في تكملته مع "الوجيز" و "تفسير البيضاوي" و "ابن كثير". وله "كشف الحقائق" في التفسير أيضا، مخطوط، الجزء الأول منه، ويعرف بتفسير الكواشي. قال آغا بزرك: "توجد قطعة منه من سورة العنكبوت إلى الحجرات، بمدرسة النواب بالمشهد الرضوي وعليها تملك محمد بن محمد بن أبي طالب الطبيب في رجب ٧٨٢ هـ يتعرض فيه للاختلاف فى عدد الآيات والقرآن" (١).
_________
(١) كشف الظنون ٣٣٩ و ٤٥٧ و ٤٨٠ و ١٦٦ او ١٣٦٦ و ١٤٨٩ والوافي ٨: ٢٩١ وبغية الوعاة ١: ٤٠١ والنجوم الزاهرة ٧: ٣٤٨ وغاية النهاية ١: ١٥١ ونكت الهميان ١١٦ والعبر ٥: ٣٢٧ وتذكرة الحفاظ =','1','84',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (177,'السمين [.. - ٧٥٦ هـ / .. - ١٣٥٥ م]

<span data-type="title" id=toc-252>أحمد بن يوسف بن عبد الدايم بن محمد الحلبي، أبو العباس، شهاب الدين، المعروف بالسمين:</span>
نحوى، مقرىء، مفسر، من فقهاء الشافعية. سكن القاهرة وناب في الحكم بها وولي نظر الأوقاف، ودرس القراءات والنحو بالجامع الطولوني. من كتبه "الدر المصون في علم الكتاب المكنون" في إعراب القرآن، مجلدان ضخمان، قال صاحب كشف الظنون: "هو مع اشتماله على غيره أجل ما صنف فيه - أي في علم إعراب القرآن - لأنه جمع العلوم الخمسة: الإعراب، والتصريف، واللغة، والمعاني، والبيان، ولخصه من البحر المحيط لأبي حيان وناقشه فيه كثيرا" و "محمدة الحفاظ في تفسير أشرف الألفاظ" مخطوط، في غريب القرآن، "وتفسير القرآن" عشرون جزءا، و "أحكام القرآن" ويعرف باسم "القول الوجيز في أحكام الكتاب العزيز" مخطوط، الأول منه (١).
_________
= ١٤٦٥ وطبقات القراء للذهبي ٢: ٥٤٧ والشذرات ٥: ٣٦٥ والسبكي ٥: ١٨ وطبقات المفسرين للداودي ١: ٩٨ وروضات الجنات ٨٣ ومفتاح السعادة ١: ٤٣٥ وهدية العارفين ١: ٩٨.
(١) روضات الجنات ٨٥ واعلام النبلاء ٥: ٢٤ وطبقات المفسرين للداودي ١: ١٠٠ والشذرات ٦: ١٧٩ وحسن المحاضرة ١: ٥٣٦ وغاية النهاية ١: ١٥٢.','1','84',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (178,'الغزنوي [.. - ٥٥٢ هـ / .. ١١٥٧ م]

<span data-type="title" id=toc-253>أحمشاد بن عبد السلام بن محمود الغزنوي، أبو المكارم:</span>
من شعراء الخريدة، قاض، مفسر، واعظ. قال صاحب الخريدة: "كان واعظا من فحول العلماء، لقيته بأصبهان في سني ثلاث وأربع وخمس وأربعين وخمسماية. وكان عارفا بتفسير كتاب الله تعالى. وتولى قضاء "أرانية" و "حيرة" سنين، وقدم بغداد والتقى بالوزير عون الدين ابن هبيرة". وترجم له صاحب الطبقات السنية فقال: "أحمد شاد، كذا رأيته في غالب الكتب والأشعار التي له فيها ذكر، وبعضهم كتبها "أحمشاد" فوصل بين الميم والشين وأسقط الدال، وأتى به في الشعر كذلك، بحيث لو أتى بالدال لذهب الوزن فيه، ولعل اسقاط الدال لضرورة الشعر، (١).

اسباط بن نصر [.. - ١٧٠ هـ / .. - ٧٨٦ م]

<span data-type="title" id=toc-254>أسباط بن نصر الهمداني الكوفي:</span>
_________
= والدرر الكامنة ١: ٣٦ وبغية الوعاة ١: ٤٠٢ وهدية العارفين ١: ١١١ وكشف الظنون ١٢٢ وطبقات النحاة لابن قاضي شهبة ٢١٠ وبروكلمان ٢: ١١١ والذيل ٢: ١٣٧ ومعجم المؤلفين ٢: ٢١١.
(١) الوافي ٨: ٣٠٨ وطبقات المفسرين للداودي ١: ١٠١.','1','85',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (179,'مفسر، من رجال الحديث. كان راوية مشهورا لتفسير السدي. ويرى (هورست  Horst)  أن اسباط كتب تفسيرا بعد السدي. خرج له البخاري في تاريخه، ومسلم وأبو داود والترمذي وابن ماجه والنسائي. وقال النسائي: ليس بالقوي. وتوقف الإمام أحمد في الرواية عنه (١).

ابن راهويه [١٦١ - ٢٣٨ هـ / ٧٧٨ - ٨٥٣ م]

<span data-type="title" id=toc-255>إسحاق بن إبراهيم بن مخلد بن إبراهيم الحنظلي التميمي المروزي، أبو يعقوب ابن راهويه:</span>
أحد كبار الحفاظ الثقات، كان عالم خراسان في عصره. من أهل مرو، رحل في طلب الحديث (سنة ١٨٤ هـ) فدخل العراق والحجاز واليمن والشام، أخذ عنه ابن حنبل والبخاري ومسلم وغيرهم. وهو الذي أوصى البخاري بأن يصنف كتب الحديث - التي زاد عددها - ويضم بعضها إلى بعض. قال أحمد بن حنبل: "لم يعبر الجسر إلى خراسان مثله، ولا أعرف له بالعراق نظيرا" وقال
_________
(١) طبقات ابن سعد ٦: ٣٧٦ والعبر ١: ٢٥٩ وشذرات الذهب ١: ٢٧٩ والتاريخ الكبير للبخاري ٢/ ١: ٥٣ وتهذيب التهذيب ١: ٢٢١ والوافي ٨: ٣٨٣ وتاريخ التراث العربي ١: ١٩٢ والكنى والأصول ٢: ١٦٠ والجمع بين رجال الصحيحين ٤٦ والأعلام ١: ٢٨٢. والجرح والتعديل. و  H. Horst : ZDMG ١٠٣/ ١٩٥٣/ ٣٠٢','1','85',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (180,'الدارمي: "ساد إسحاق اهل المشرق والمغرب بصدقه". قيل في سبب تلقيبه "ابن راهويه" أن أباه ولد في طريق مكة، فقال أهل مرو: راهويه، أي ولد في الطريق، وكان أبوه يكره هذا. استوطن نيسابور وتوفي بها. من كتبه "تفسير القرآن" وقد رواه عنه محمد بن يحي بن خالد المروزي (١). الملتاني [.. - حوالي ٧٣٦ هـ .. - حوالي ١٣٣٥ م]

<span data-type="title" id=toc-256>إسحاق بن علي بن أبا بكر بن أبي صاعد، أبو بكر، البكري الملتاني:</span>
مفسر، من فقهاء الحنفية، من أهل "ملتان" بالهند. من كتبه "خلاصة جواهر القرآن في بيان معاني لغات القرآن" (٢).
_________
(١) وفيات الأعيان ١: ١٩٩ والتاريخ الكبير للبخاري ١/ ١: ٣٧٩ وابن النديم ٢٨٦ وتاريخ بغداد ٦: ٣٤٥ وتذكرة الحفاظ ٤٣٣ وطبقات الحنابلة ١: ١٠٩ والجرح والتعديل ١/ ٢٠٩١ وحلية الأولياء ٩: ٢٣٤ والرجال للقيسراني ٢٨ وميزان الاعتدال ١: ١٨٢ ومفتاح السعادة ٢: ٢٩٧ وتهذيب ابن عساكر ٢: ٤١٢ وتهذيب التهذيب ١: ٢١٦ والنجوم الزاهرة ٢: ٢٩٣ والعبر ١: ٤٢٦ والشذرات ٢: ٨٩ والرسالة المستطرفة ٦٥ وطبقات السبكي ١: ٢٣٢ وطبقات المفسرين للداودي ١: ١٠٢ وتاريخ التراث العربي ١: ٢٩٧ وهدى الساري ٤ وهدية العارفين ١: ١٩٧ والأعلام ١: ٢٨٤ ومعجم المؤلفين ٢: ٢٢٨ وكشف الظنون ٤٤٢ ومختصر دول الإسلام ١: ١١٢ وروضات الجنات ١٠٠.
(٢) هدية العارفين ١: ٢٠٠ وبروكلمان ٢: ٢٢٠ والذيل ٢: ٣١٠.','1','86',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (181,'الرومي [.. - بعد ٨٠١ هـ / .. - بعد ١٣٩٨ م]

<span data-type="title" id=toc-257>إسحاق بن محمد بن حمزة الرومي:</span>
من علماء الحنفية. كان تلميذا لعبد اللطيف بن عبد العزيز الكرماني المعروف بابن ملك،
المتوفي سنة ٨٠١ هـ = ١٣٩٨ م. له
"إعراب القرآن" (١).

القرماني [.. - ٩٣٣ هـ / .. - ١٥٢٧ م]

<span data-type="title" id=toc-258>إسحاق بن محمد القرماني، الشهير بجمال خليفة:</span>
مفسر، صوفي فقيه حنفي، من أهل قرمان - في وسط - تركيا الآسيوية -. من آثاره "تفسيرا من سورة المجادلة إلى آخر القرآن" و "حاشية" على أنوار التنزيل للبيضاوي في التفسير (٢).

ابن علية [١١٠ - ١٩٣ هـ / ٧٢٨ - ٨٠٩ م]

<span data-type="title" id=toc-259>إسماعيل بن إبراهيم بن مقسم الأسدي بالولاء، البصرى، أبو بشر:</span>
من أكابر حفاظ الحديث الثقات. مفسر، كوفي الأصل، من أهل البصرة، وولي صدقاتها. ثم انتقل إلى بغداد وحدث بها، وولي المظالم في آخر خلافة هارون الرشيد.
_________
(١) هدية العارفين ١: ٢٠٢.
(٢) هدية العارفين ١: ٢٠٢ وكشف الظنون ١٨٩ و ٤٤٥ وإيضاح المكنون ١: ١٤١.','1','86',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (182,'قال الخطيب البغدادي: "كان ثقة ثبتا في الحديث حجة، وكان يكره أن يقال له ابن علية وهي أمه". روى له البخاري ومسلم وأبو داود والترمذي والنسائي وابن ماجة. وقال أبو داود: "ما أحد من المحدثين إلا وقد أخطأ إلا ابن علية وبشر بن المفضل"، له "تفسير القرآن" (١).

الثقفي [.. - ٢٨٢ هـ / .. - ٨٩٥ م]

<span data-type="title" id=toc-260>إسماعيل بن أحمد بن أسيد الثقفي، أبو إسحاق:</span>
محدث، كثير الحديث، مفسر، من أهل أصبهان، حدث عن المكيين والبصريين والكوفيين. له "تفسير القرآن" (٢).

الحيري [٣٦١ - بعد ٤٣٠ هـ / ٩٧٢ - بعد ١٠٣٩ م]

<span data-type="title" id=toc-261>إسماعيل بن أحمد بن عبد الله الحيري، أبو عبد الرحمن:</span>
مفسر، مقرىء، محدث، من فقهاء الشافعية. نسبته إلى"الحيرة" محلة كانت بنيسابور. حدث ببغداد (سنة ٤٢٣ هـ) وسمع منه الخطيب البغدادي.
_________
(١) الوافي ٩: ٧٠ وتاريخ بغداد ٦: ٢٢٩ وطبقات المفسرين للداودي ١: ١٠٤ وتذكرة الحفاظ ٣٢٢ وطبقات الحنابلة ١: ٩٩ وميزان الاعتدال ١: ٢١٦ وتهذيب التهذيب ١: ٢٧٥.
(٢) أخبار أصبهان ١: ٢١٢ والوافي ٩: ٨٥.','1','87',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (183,'وكان ضريرا. قال الصفدي: "أحد أئمة المسلمين، له التصانيف المشهورة في علوم القرآن والقراءات والحديث والوعظ والتذكير" منها "الكفاية" في تفسير القرآن (١).

الأنقروي [.. - ١٤٠٢ هـ / .. - ١٦٣٢ م]

<span data-type="title" id=toc-262>إسماعيل بن أحمد البيرامي الأنقروي، رسوخ الدين المولوي:</span>
درويش تركي، متأدب، له مشاركة في بعض العلوم. ولد بأنقرة، وساح، وولي المشيخة بغلطة، قال المحبي: "وكانت مجالسه غاصة بالأدباء والظرفاء، وكان متشرعا أديبا ..). من آثاره "الفاتحة العينية" في تفسير سورة الفاتحة، باللغة التركية قال صاحب كشف الظنون: "وضعها حين فتحت عيناه من الرمد شكرا لله سبحانه وتعالى، جمعها من التفاسير والحواشي، فصارت مجموعة، أوله١: الحمد لله الذي أنزل القرآن هدى للناس، .. رتبها على سبع فواتح، الأولى في بعض الفضائل، والثانية في معاني،
_________
(١) معجم الأدباء٢: ٢٥٦ وطبقات المفسرين للداودي ١: ١٠٤ والسبكي ٤: ٢٦٥ وطبقات المفسرين للسيوطي ٧ وتاريخ بغداد ٧: ٣١٣ والوافي ٩: ٨٤ والمتظم ٨: ١٠٥ ونكت الهميان ١١٩ والعبر ٣: ١٧١ والشذرات ٣: ٢٤٠ وهدية العارفين ١: ٢٠٩ وكشف الظنون ١٤٩٨ والأعلام ١: ٣٠٣.','1','87',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (184,'الاستعاذة، والثالثة في البسملة، والرابعة في الفاتحة، والخامسة في السورة والآية، والسادسة في أسماء الفاتحة، والسابعة في سبب النزول" (١).

الجهضمي [٢٠٠ - ٢٨٢ هـ / ٨١٥ - ٨٩٦ م]

<span data-type="title" id=toc-263>إسماعيل بن إسحاق بن إسماعيل بن حماد بن زيد بن درهم الجهضمي الأزدي، أبو إسحاق:</span>
من أعيان فقهاء المالكية، عالم بالحديث والتفسير، قاض، ولد في البصرة وبها نشأ، ثم استوطن بغداد وولي قضاءها وقضاء المدائن والنهروانات، ثم ولي قضاء القضاة إلى أن توفي فجأة ببغداد. قال في "تقريب المسالك": "كان بيت آل حماد بن زيد، على كثرة رجالهم، وشهرة أعلامهم، من أجل بيوت العلم بالعراق، وأرفع مراتب السؤدد في الدين والدنيا، وهم نشروا مذهب الإمام مالك هناك، وعنهم أقتبس، وتردد العلم في طبقاتهم وبيتهم نحو ثلاثمائة عام .. " من كتبه "أحكام القرآن" قال الخطيب البغدادي: وهو كتاب لم يسبقه إليه أحد من أصحابه إلى مثله، و "معاني القرآن وإعرابه" خمسة
_________
(١) خلاصة الأثر ١: ٤١٨ وهدية العارفين ١: ٢١٨ والأعلام ١: ٣٠٩ ومعجم المؤلفين ٢: ٢٥٩. وكشف الظنون ١٢١٤.','1','88',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (185,'وعشرون جزءا، و " الاحتجاج بالقرآن" مجلدان (١).

قره كمال [.. - ٩٢٠ هـ / .. - ١٥١٤ م]

<span data-type="title" id=toc-264>إسماعيل بن بالي القرماني، كمال الدين، المعروف ب "قره كمال</span>":
من علماء الدولة العثمانية، درس بمدينة أدرنة وغيرها، وكان مفسرا، فقيها حنفيا، متكلما. له "حاشية" على الكشاف للزمخشري في التفسير، و "حاشية" على أنوار التنزيل للبيضاوي في التفسير أيضا (٢).

إسماعيل حقي [.. - ١١٢٧ هـ / .. - ١٧١٥ م]

<span data-type="title" id=toc-265>إسماعيل حقي بن مصطفى (الإسلامبولي، المولى أبو الفداء)</span>
_________
(١) تاريخ بغداد ٦: ٢٨٤ والوافي ٩: ٩١ والديباج ٩٢ والعبر ٢: ٦٧ والمنتظم ٥: ١٥١ وغاية النهاية ١: ١٦٢ ومعجم الأدباء ٦: ١٢٩ وبغية الرعاة ١: ٤٤٣ وتذكرة الحفاظ ٢: ٢: ٦٢٥ ومرآة الجنان ٢: ١٩٤ والرسالة المستطرفة ٣٧ وتاريخ قضاة الأندلس ٣٣ والبداية والنهاية ١١: ٧٢ والشذرات ٢: ١٧٨ وطبقات المفسرين للداودي ١: ١٠٥ وتاريخ التراث العربي ٢: ١٥٠ واللباب ١: ٣١٦ والأعلام ١: ٣٠٥ ومعجم المؤلفين ٢: ٢٦١ وهدية العارفين ١: ٢٠٧ ومختصر دول الإسلام ١٣٣ وكشف الظنون ١: ٢٥.
(٢) الفوائد البهية ٤٩ والشقائق النعمانية ٢٥ وهدية العارفين ١: ٢١٧ وإيضاح المكنون ١: ١٤١ ومعجم المؤلفين ٢: ٢٨٧ وكشف الطنون ١٤٨١.','1','88',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (186,'الإسلامبولي، المولى أبو الفداء: مفسر، متصوف، حنفي المذهب، خلوتي الطريقة، تركي مستعرب، ولد في آيدوس، وسكن القسطنطينية ثم انتقل إلى بروسة. وفيما كان يبحث عن مسائل تتعلق بالتصوف وشى به بعض العلماء فنفي إلى نكفور طاغ، وأوذي هناك من عامة الشعب، ثم عاد إلى بروسة فمات بها. له تصانيف عربية وتركية، فمن العربية "روح البيان في تفسير القرآن" طبع، أربعة أجزاء، ويعرف بتفسير حقي. و "شرح تفسير الفاتحة" و "حاشية" على تفسير سورة النبأ للبيضاوي في مجلدين (١).

السرقسطي [.. - ٤٥٥ هـ / .. - ١٠٦٣ م]

<span data-type="title" id=toc-266>إسماعيل بن خلف بن سعيد الأنصاري السرقسطي، أبو الطاهر:</span>
عالم بالقراءات، نحوي، أديب، من أهل سرقسطة بالأندلس. رحل إلى المشرق، وأقرأ الناس بجامع عمرو بن العاص بالقاهرة. له "إعراب القرآن" في تسع
_________
(١) هدية العارفين ١: ٢١٩ ومعجم المطبوعات ٤٤١ وفيه أسماء تصانيفه العربية المطبوعة. والأعلام ١: ٣٠٩ وإيضاح المكنون ١: ٥٨٥ والمكتبة الأزهرية ١: ٢٣٣ والمكتبة البلدية فهرس التفسير ٢٧ ومعجم المؤلفين ٢: ٢٦٦.','1','89',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (187,'مجلدات، مخطوط، النصف الثاني منه في الاسكندرية رقم ٣٤٧٥ ج (١).

الصدر [١٣٣٩ - ١٣٨٩ هـ / ١٩٢١ - ١٩٦٩ م]

<span data-type="title" id=toc-267>إسماعيل الصدر:</span>
كبير علماء الشيعة الإمامية في عصره ببغداد. من كتبه "محاضرات في تفسير القرآن الكريم" طبع (٢).

ابن اليازجي [.. - ١١٢١ هـ / .. - ١٧٠٩ م]

<span data-type="title" id=toc-268>إسماعيل بن عبد الباقي اليازجي:</span>
فقيه حنفي، عارف بالتفسير، واعظ، من أهل دمشق مولدا ووفاة. من آثاره "شرح على الجلالين" في التفسير، لم يكمله (٣).
_________
(١) وفيات الأعيان ١: ٢٣٣ وغاية النهاية ١: ١٦٤ والصلة ١٠٥ ووفاته فيه سنة ٤٥٣ هـ وبغية الوعاة ١: ٤٤٨ وحسن المحاضرة ١: ٢٨٣ وروضات الجنات ١١٣ ومعجم الأدباء ٦: ١٦٥ وهو فيه إسماعيل بن خلف الصقلي، وأنه كان بعد سنة ٥١٠ هـ. والوافي ٩: ١١٦ وهو ينقل عن الوفيات ومعجم الأدباء وهدية العارفين ١: ٢١٠ والأعلام ١: ٥١٠ ومعجم المؤلفين
(٢) معجم المؤلفين العراقيين ١: ٥١١ وجريدة الحياة ١/ ٣/ ١٩٦٩. والأعلام ١: ٣١٥.
(٣) سلك الدرر ١: ٢٥٥ والكتبخانة ٧: ٣٦٣.','1','89',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (188,'السدي [.. - ١٢٨ هـ / .. - ٧٤٥ م]

<span data-type="title" id=toc-269>إسماعيل بن عبد الرحمن بن أبي كريمة السدي، أبو محمد:</span>
مفسر كبير، محدث، ومؤلف في المغازي والسير، حجازي الأصل، عاش في الكوفة، روى عن أنس بن مالك وابن عباس وغيرهما. وقد جرحت روايته لأنه حصل عليها عن طريق المناولة. وروى له مسلم وأبو داود والترمذي وابن ماجه. ورمي بالتشيع. له "تفسير كبير". قال فؤاد سزكين: "يضم، على ما يبدو، كل القرآن. واستخدم كثيرا في التفاسير المتأخرة التي جاءت بعده. وقد استخدمه الطبري بالرواية الآتية: حدثني موسى بن هارون الهمداني قال: حدثنا عمرو بن حماد الفناد قال: حدثنا اسباط بن نصر الهمداني عن إسماعيل بن عبد الرحمن السدي" ويبدو أنه من الممكن جمع نصوص هذا التفسير وإعادة تكوينه من جديد (١).
_________
(١) التاريخ الكبير للبخاري ١/ ١: ٣٦١ والجرح والتعديل ١/ ١/: ١٨٤ وذكر أخبار أصبهان ١: ٢٠٤ والوافي ٩: ١٤٢ والشذرات ١: ١٧٤ والمعارف ٢٩١ وخلاصة تهذيب الكمال ٣٠ وطبقات المفسرين للداودي ١: ١٠٩ والنجوم الزاهرة ١: ٣٠٤ وأعيان الشيعة ١٢/ ١: ٩ وتاريخ التراث العربي ١: ١٩١ واللباب ٢: ١١٥ وميزان الاعتدال ١: ٢٣٦ وتاريخ الطبري انظر فهرسته. والأعلام ١: ٣١٣ ومعجم الأدباء ٧: =','1','90',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (189,'الصابوني [٣٧٣ - ٤٤٩ هـ / ٩٨٣ - ١٠٥٧ م]

<span data-type="title" id=toc-270>إسماعيل بن عبد الرحمن بن أحمد بن إسماعيل بن إبراهيم الصابوني، أبو عثمان:</span>
محدث، مفسر، من كبار العلماء. ولد في بوشنج، وسمع بنيسابور والشام والحجاز، ودخل معرة النعمان فلقي بها أبا العلاء المعري. حدث بنيسابور، وخراسان إلى غزنة وبلاد الهند، وجرجان، وآمل، وطبرستان، وبالشام وبيت المقدس والحجاز. قال عبد الغافر الفارسي: "هو الإمام شيخ الإسلام الخطيب المفسر الواعظ، أوحد وقته في طريقته، وكان أكثر أهل العصر من المشايخ سماعا وحفظا ونشرا لمسموعاته وتصنيفاته وجمعا وتحريضا على السماع وإقامة مجالس الحديث" (١).
_________
= ١٣ وهدية العارفين ١: ٢٠٦ وكشف الظنون ٤٤٨ ومعجم المؤلفين ٢: ٢٧٦ وتذكرة الحفاظ ١٥٩.
(١) الوافي ٩: ١٤٣ وطبقات الشافعية ٤: ٢٧١ والعبر ٣: ٢١٩ وطبقات المفسرين للسيوطي ٧ وطبقات المفسرين للداودي ١: ١٠٧ ومعجم الأدباء ٧: ١٦ والنجوم الزاهز ٥: ٦٢ والشذرات ٣: ٢٨٢ وابن عساكر ٣: ٢٧ والبدايه والنهاية ١٢: ٧٦ والرسالة المستطرفة ١٠٣ والأعلام ١: ٣١٠ وهدية العارفين: ٢٠٧ واللباب ٢: ٢٢٨ والأنساب ٣٤٦ ب.','1','90',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (190,'النابلسي [١٠١٧ - ١٠٦٢ هـ / ١٦٠٨ - ١٦٥٢ م]

<span data-type="title" id=toc-271>إسماعيل بن عبد الغني بن إسماعيل بن أحمد النابلسي:</span>
فقيه حنفي، مفسر، من أهل دمشق مولدا ووفاة. وأصله من نابلس (بفلسطين). تعلم بدمشق والقسطنطينية، وحج، ودخل القاهرة وأخذ عن علمائها. قال المحبي: "وأملى تفسير البيضاوي بالجامع الأموي، وكان يورد عليه عبارات تفاسير عديدة وكلها إلقاء من حفظه، ووقفت على "مجموع" بخطه فيه خطب دروسه التفسيرية" (١).

الخلوقي [.. - ٨٩٩ هـ / .. - ١٤٩٤ م]

<span data-type="title" id=toc-272>إسماعيل بن عبد الله الرومي الصوفي الخلوتي، جمال الدين:</span>
مفسر، تركي الأصل، مشارك في بعض العلوم، توفي في طريقه إلى الحج. من كتبه "تفسير سورة الفاتحة" و "تفسير" من سورة الضحى إلى آخر القرآن" وتفسير "آية الكرسي" (٢).
_________
(١) خلاصة الأثر ١: ٤٠٨ وهدية العارفين ١: ٢١٨ والأعلام ١: ٣١٤ ومعجم المؤلفين ٢: ٢٧٧ وتراجم بعض أعيان دمشق ٦٣ وعلوم القرآن ٣٧٧ فهرست البلدية (تفسير) ٣٠.
(٢) هدية العارفين ١: ٢١٧ والأعلام ١: ٣١٥ ومعجم المؤلفين٢: ٢٧٨.','1','91',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (191,'الشرواني [.. - ٩٤٢ هـ / .. - ١٥٣٥ م]

<span data-type="title" id=toc-273>إسماعيل بن عبد الله الشرواني:</span>
محدث، مفسر، من فقهاء الحنفية، نسبته إلى "شروان" مدينة ب "دربندخزران". سكن مكة المكرمة وتوفي بها. له "حاشية على تفسير البيضاوي" (١).

البستي [.. - ٤٢٠ هـ / .. - ١٠٢٩ م]

<span data-type="title" id=toc-274>إسماعيل بن علي بن أحمد البستي، الزيدي، أبو القاسم:</span>
مفسر، متكلم، فقيه. توفي في حدود سنة ٤٢٠ هـ. له "تفسير القرآن" (٢).

السمان [.. - ٤٤٧ هـ / .. - ١٠٥٥ م]

<span data-type="title" id=toc-275>إسماعيل بن علي بن الحسين بن زنجوية الرازي، أبو سعد السمان:</span>
الحافظ الكبير المتقن، كان إماما في القراءات والحديث والرجال والفرائض والشروط، عالما بالفقه الحنفي وبالخلاف بين الشافعية والحنفية وفقه الزيدية، معتزليا، من أهل الري مولدا
_________
(١) الشقائق النعمانية ٢١٤ والكواكب السائرة ٢: ١٢٣ وهدية العارفين ١: ٢١٨ ومعجم المؤلفين ٢: ٢٧٨ وشذرات الذهب ٨: ٢٤٧ وإيضاح المكنون ١: ١٤١.
(٢) معجم المؤلفين ٢: ٢٧٩ وتراجم الرجال ٧.','1','91',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (192,'ووفاة. رحل في طلب العلم فسمع بالعراق والحجاز والشام والمغرب، قيل: بلغت شيوخه ثلاثة آلاف وستمائة. قال الصفدي: كان زاهدا، ولم يكن لأحد عليه منة، ولم يضع يده في قصعة أحد طول عمره، ووقف كتبه التي لم يوجد مثلها على المسلمين". وقال العليمي: "كان تاريخ الزمان وشيخ الإسلام، فقال الذهبي: بل شيخ الاعتزال، ومثل هذا عبرة، فإنه مع براعته في علوم الدين ما تخلص ذلك من البدعة". قيل في وفاته: سنة ٤٤٣، و ٤٤٥، و ٤٤٧ هـ. من كتبه "البستان في تفسير القرآن" في عشر مجلدات (١).

أبو الفداء [٦٧٢ - ٧٣٢ هـ / ١٢٧٣ - ١٣٣١ م]

<span data-type="title" id=toc-276>إسماعيل بن علي بن محمود بن محمد بن عمر بن شاهنشاه بن أيوب، الملك المؤيد، عماد الدين، أبو الفداء:</span>
صاحب حماة، مؤرخ جغرافي. قال الأسنوي: "كان جامعا لأشتات العلوم، أعجوبة من أعاجيب
_________
(١) العبر ٣: ٢٠٩ وابن عساكر ٣: ٣٥ والوافي ٩: ١٥٦ وتذكرة الحفاظ ١١٢١ والرسالة المستطرفة ٥٩ أعيان الشيعة ١٢: ٣٥ والجواهر المضية ١: ١٥٦، ولسان الميزان ١: ٤٢١ وميزان الاعتدال ١: ٢٣٩ وطبقات المفسرين للداودي ١: ١٠٩ والشذرات ٣: ٢٧٣ والنجوم الزاهرة ٥: ٥١ والوافي ٩: ١٥٦ وهدية العارفين ١: ٢١٠ والأعلام ١: ٣١٦ وإيضاح المكنون ١: ١٨١.','1','92',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (193,'الدنيا، ماهرا في الفقه والتفسير والأصلين والنحو وعلم الميقات والفلسفة والمنطق والطب والعروض والتاريخ، شاعرا ماهرا كريما إلى الغاية .. " ولد ونشأ وتعلم في دمشق. واتصل بالملك الناصر محمد بن قلاوون لما كان بالكرك، وخدمه، فكافأه الناصر بتعيينه سلطانا مستقلا في حماه، ليس لأحد أن ينازعه السلطة، وأركبه في القاهرة بشعار الملك، فانصرف إلى حماه، واستمر في السلطنة إلى أن توفي، ويحتل أبو الفداء مقاما رفيعا بين من حكموا حماه، وتقوم شهرته على كتابيه "المختصر في أخبار البشر" و "تقويم البلدان" الذي ذاعت شهرته في الشرق والغرب (١).

ابن كثير [٧٠١ - ٧٧٤ هـ / ١٣٠٢ - ١٣٧٣ م]

<span data-type="title" id=toc-277>إسماعيل بن عمر بن كثير بن ضو بن درع القرشي البصروي ثم الدمشقي، أبو الفداء، عماد الدين:</span>
مؤرخ، مفسر، محدث، من فقهاء الشافعية. ولد في قرية
_________
(١) الدرر الكامنة ١: ٣٩٦ والنجوم الزاهرة ٩: ٢٩٢ وطبقات السبكي ٦: ٨٤ والشذرات ٦: ٩٨ والبداية والنهاية ١٤: ١٥٨ والوافي ٩: ١٧٣ ودائرة المعارف الإسلامية ١: ٣٨٦ والموسوعة العربية الميسرة ٣٨ والفهرس التمهيدي ٢٥٣ والأعلام ١: ٣١٩ وآداب اللغة العربية ٣: ٢٠١ والبدر الطالع ١: ١٥١ وابن اياس ١: ١٦٠ ومعجم أدباء الأطباء ١٤٢ والسلوك ٢: ٣٥٠ ومعجم المؤلفين ٢: ٢٨٢.','1','92',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (194,'شرقي بصرى الشام. وقدم مع أخيه إلى دمشق سنة ٧٠٦ هـ بعد موت أبيه، وبها نشأ وتعلم .. قال ابن حبيب: "إمام ذوي التسبيح والتهليل، وزعيم أرباب التأويل، سمع وجمع وصنف، وأطرب الاسماع بقوله وشنف، وحدث وأفاد، وطارت أوراق فتاويه إلى البلاد، واشتهر بالضبط والتحرير، وانتهت إليه رياسة العلم في التاريخ والحديث والتفسير". توفي بدمشق، وكان قد أضر بآخر عمره. من كتبه "تفسير القرآن الكريم" وقد تكررت طبعاته، واختصره أحمد محمد شاكر وسمى المختصر "محمدة التفسير عن الحافظ ابن كثير" (١).

كمال باشا [.. - ١٣١٠ هـ / .. - ١٨٩٢ م]

<span data-type="title" id=toc-278>إسماعيل كمال باشا بن وجهي باشا (الوزير) الرومي:</span>
أديب، شاعر، مفسر، تركي مستعرب، كان عضوا في
_________
(١) انباء الغمر ١: ٣٩ والدرر الكامنة ١: ٣٩٩ والبدر الطالع ١: ١٥٣ والنجوم الزاهرة ١١: ١٢٣ وطبقات المفسرين للداودي ١: ١١٠ وشذرات الذهب ٦: ٢٣١ وذيل تذكرة الحفاظ ٥٧ و ٣٦١ والدارس ١: ٣٦ و ٢: ٥٨٢ وهدية العارفين ١: ٢١٥ ومفتاح السعادة ١: ٢٥٤ ومجلة الحج ١٠: ١٥٣ والرد الوافر ٤٨ ومعجم المؤلفين ٢: ٢٨٣ ومحمدة التفسير ١: ٢٢. والاعلام ١: ٣٢٠ وفيه أقوال في اسم ابيه. ومعجم المؤلفين ٢: ٢٨٣ ومجلة معهد المخطوطات ٢: ١١٥.','1','93',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (195,'مجلس المالية العثماني. له تصانيف بالتركية والعربية، فمن العربية "تفسير سورة الإخلاص" (١).

قوام السنة [٤٥٧ - ٥٣٥ هـ / ١٠٦٥ - ١١٤١ م]

<span data-type="title" id=toc-279>إسماعيل بن محمد بن الفضل بن علي بن أحمد القرشي الطليحي التيمي الأصبهاني، أبو القاسم، الملقب بقوام السنة:</span>
حافظ كبير، إمام في التفسير والحديث واللغة والأدب، عارف بالمتون والأسانيد. سمع بأصبهان وبغداد، وحج، وجاور بمكة سنة، وعاد إلى أصبهان وتصدر للتحديث والإملاء والتصنيف، إلى أن مات. وهو من شيوخ السمعاني وابن عساكر في الحديث. من كتبه "الجامع الكبير في معالم التفسير" ثلاثون مجلدة، و "الإيضاح في التفسير" أربع مجلدات، و "المعتمد" في التفسير، عشر مجلدات، و "الموضح" في التفسير، بلسان أهل أصبهان، و "إعراب القرآن" مخطوط (٢).
_________
(١) هدية العارفين ١: ٢٢٣ ومعجم المؤلفين ٢:
(٢) المنتظم ١٠: ٩٠ وبغية الوعاة ١: ٤٥٥ والعبر ٤: ٩٤ والبداية والنهاية ١٢: ٢١٧ وتذكرة الحفاظ ١٢٧٧ ومعجم المؤلفين ٢: ٢٩٣ والشذرات ٤: ١٠٥ وطبقات المفسرين للسيوطي ٨ والنجوم الزاهرة ٥: ٢٦٧ والوافي ٩: ٢٠٨ و ٢١١ وطبقات المفسرين للداودي ١: ١١٢ ومرآة الجنان ٣: ٢٦٣ ومختصر دول =','1','93',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (196,'خواهر زاده [القرن الثامن الهجري / القرن ١٤ الميلادي]

<span data-type="title" id=toc-280>إسماعيل بن محمد بن محمود، المعروف بخواهر زاده:</span>
مفسر، ذكره صاحب هدية العارفين وقال: لعله كان في أواخر القرن الثامن، توفي سنة ٧٠٠ (كذا)، وله "منهاج البيان في تفسير لغات القرآن" (١).

العجلوني [١٠٨٧ - ١١٦٢ هـ / ١٦٧٦ - ١٧٤٩ م]

<span data-type="title" id=toc-281>إسماعيل بن محمد بن عبد الهادي بن عبد الغني الجراحي العجلوني الدمشقي، أبو الفداء:</span>
محدث الشام في وقته، مفسر، نحوي، ولد بعجلون (بالأردن) ونشأ بدمشق، وتوفي بها. من كتبه "إسعاف الطالبين بتفسير كتاب الله المبين" و "فتح المولى الجليل على أنوار التنزيل" في التفسير (٢).

القونوي [.. - ١١٩٥ هـ / .. - ١٧٨١ م]

<span data-type="title" id=toc-282>إسماعيل بن محمد بن مصطفى القونوي، عصام الدين، أبو الفداء:</span>
أول
_________
= الإسلام ٢: ٣٩ والأعلام ١: ٣٢٢ وهدية العارفين ١: ٢١١ وكشف الظنون ٢١١ و ٤٤٢ و ٥٧١ و ٥٧٥ و ١٧٣٢ و ١٩٠٤.
(١) هدية العارفين ١: ٢١٣ وإيضاح المكنون ٢.
(٢) سلك الدرر ١: ٢٥٩ والدر الفريد ٤٦ وفهرس الفهارس ٦٤ وهدية العارفين ١: ٢٢٠ وإيضاح المكنون ١: ٧٨ و ٢: ١٧٥ والأعلام ١: ٣٢٤ ومعجم','1','94',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (197,'من درس التفسير بحضور السلاطين، فقيه حنفي، مشارك في بعض العلوم. ولد بقونية وبها نشأ وتعلم، درس بالقسطنطينية. وحج سنة ١١٩٥ هـ، فمات بدمشق أثناء عودته. من كتبه "حاشية على أنوار التنزيل" للبيضاوي، في التفسير، طبع سبع مجلدات (١) -

المرندي [.. - ١٣١٧ هـ / .. - ١٨٩٩ م]

<span data-type="title" id=toc-283>إسماعيل المرندي (نسبة إلى مرند من بلاد أذربيجان) التبريزي:</span>
من علماء الشيعة الإمامية، مفسر، أصولي، فقيه، مشارك في بعض العلوم. توفي بتبريز، وعمره تسعون سنة تقريبا، ونقل إلى النجف. أثنى عليه العاملي (٢).

مفيد الرومي [١١٣٢ - ١٢١٧ هـ / ١٧٢٠ - ١٨٠٣ م]

<span data-type="title" id=toc-284>إسماعيل مفيد بن علي العطار الرومي النقشبندي:</span>
أديب، مفسر، فقيه حنفي، خطاط، من موالي الحرمين، له "حاشية" على تفسير جزء النبأ للبيضاوي (٣). المؤلفين ٢: ٢٩٢.
_________
(١) سلك الدرر ١: ٢٥٨ وهدية العارفين ١: ٢٢٢ والأعلام ١: ٣٢٥ ومعجم المؤلفين ٢: ٢٩٤ والمكتبة البلدية فهرس التفسير ٨ وإيضاح المكنون ١: ١٤٢.
(٢) أعيان الشيعة ١٢: ٢٨١ واعلام الشيعة ١: ١٦٥.
(٣) هدية العارفين ١: ٢٢٣.','1','94',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (198,'القطان [.. - نجو ٢٦٠ هـ / .. - نحو ٨٧٤ م]

<span data-type="title" id=toc-285>إسماعيل بن يزيد بن حريث القطان، ابو أحمد:</span>
محدث، مفسر، من أهل أصبهان. قال أبو نعيم: اختلط عليه بعض حديثه في آخر أيامه، يذكر بالزهد والعبادة، حسين الحديث كثير الغرائب والفوائد، صنف "المسند" و "التفسير". وقال الصفدي: "محدث رحال عالي الإسناد" (١).

الأسود النخعي [.. - ٧٥ هـ / .. - ٦٩٤ م]

<span data-type="title" id=toc-286>الاسود بن يزيد بن قيس النخعي، أبو عمرو:</span>
تابعي، من مشاهير مفسري مدرسة الكوفة، فقيه، حافظ، كان عالم الكوفة في عصره. أخذ عن معاذ وابن مسعود وطبقتهما. قال الذهبي: " كان من العبادة والحج على أمر كبير، سافر ثمانين حجة وعمرة لم يجمع بينهما، وكانوا يسمونه من أهل الجنة" (٢).
_________
(١) الوافي ٩: ٢٤١ ولسان الميزان ١: ٤٤٣ وأخبار أصبهان ١: ٢٠٩ وطبقات المفسرين للداودي ١: ١١٤.
(٢) تذكرة الحفاظ ٥٠ وحلية الأولياء٢: ١٠٢ وعلم التفسير ٣١ والشذرات ١: ٨٢ وكشف الظنون ٤٣٠ واللباب ٣: ٣٥٤.','1','95',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (199,'تاج العلاء [.. - ٦١٠ هـ / .. - ١٢١٣ م]

<span data-type="title" id=toc-287>الأشرف بن الأغر بن هاشم بن محمد بن سعد الله العلوي، أبو هاشم، الملقب تاج العلاء:</span>
نسابة، مفسر، ولد بالرملة (بفلسطين) ودخل مكة ودمشق والجزيرة والبصرة، وسكن آمد، ثم استوطن حلب إلى أن توفي. كان يقول إن مولده سنة ٤٨٢ هـ وأنه لقي ابن الفحام والحريري. قال الذهبي: "ما كان هذا إلا وقحا جرينا على الكذب، انظر كيف ادعى هذه السن وكيف كذب في لقاء ابن الفحام والحريري". وقال ابن النجار: "كان أديبا فاضلا حفظة للأخبار والآثار، ولم يكن موثوقا به فيما يقوله ويرويه". له "جنة الناظر وجنة المناظر" في التفسير، خمس مجلدات (١).

آلامرهوي [.. - ١٣٤٠ هـ / .. - ١٩٢٢ م]

<span data-type="title" id=toc-288>إعجاز حسين آلامرهوي:</span>
مفسر، من فقهاء الشيعة الإمامية. له كتاب في تفسير آيات من القرآن الكريم (٢).
_________
(١) الوافي ٩: ٢٦٨ ونكت الهميان ١١٩ ولسان الميزان ١: ٤٤٩ وأعيان الشيعة ١٢: ٤٠٣ وهدية العارفين ١: ٢٢٤ وإيضاح المكنون ١: ٣٦٩ والاعلام ١: ٣٣٣.
(٢) أعلام الشيعة ١: ١٦٦.','1','95',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (200,'إفتخار الدامغاني =
محمد بن نصر الله ٧٥٥ هـ الشيرازي [.. - ١٢٦٣ هـ .. - ١٨٤٧ م]

<span data-type="title" id=toc-289>أكبر نواب الشيرازي، الحاج أكبر، المعروف ببسمل:</span>
مفسر، مشارك في بعض العلوم، من أهل شيراز، له "حاشية" على أنوار التنزيل للبيضاوي في التفسير (١).

السينوي [.. - ٨٩١ هـ / .. - ١٤٨٦ م]

<span data-type="title" id=toc-290>الياس بن إبراهيم السينوي، ثم البروسوي:</span>
مفسر، من فقهاء الحنفية، تركي مستعرب، أقام ببروسة ودرس بها إلى أن مات. أثنى عليه طاشكبري زاده وذكر له من مؤلفاته "رسالة" في تفسير بعض آيات من كتاب الله العزيز وقال: أظهر فيها حذاقته في علم التفسير (٢).

القرماني [.. - ٩٨٢ هـ / .. ١٠٢٥ م]

<span data-type="title" id=toc-291>الياس القرماني:</span>
طبيب، مفسر، تركي مستعرب، ولد بولاية قرمان،
_________
(١) ايضاح المكنون ١: ١٣٩.
(٢) الشقائق النعمانية ٦٣ وهدية العارفين ١: ٢٢٥ والكشاف لطلس ١١٣ وعثمانلي مؤلفلري ١: ٢٢٢.','1','96',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (201,'ورحل في طلب العلم، فشهر بالطب. ثم برع في التفسير وتصدر لتدريسه، فانتفع به كثيرون، واستمر إلى أن مات قتلا (١).

البنارسي [.. - ١١٣٣ هـ / .. - ١٧٢١ م]

<span data-type="title" id=toc-292>أمان الله بن نور الله بن حسين البنارسي الهندي:</span>
مفسر، باحث، من فقهاء الحنفية، من أهل بنارس (من بلاد يورب، شمال شرقي الهند) وهي من مدن الهندوس المقدسة. تقلد صدارة "لكنوء" - في شمال الهند - من قبل السلطان عالمكير. وتوفي في بنارس. من كتبه "حاشية" على أنوار التنزيل في التفسير للبيضاوي (٢).

أنس بن مالك [١٠ ق هـ - ٩٣ هـ / ٦١٢ - ٧١٢ م]

<span data-type="title" id=toc-293>أنس بن مالك بن النضر بن ضمضم بن زيد النجاري الخزرجي الأنصاري، أبو ثمامة، أو أبو حمزة:</span>
صاحب رسول الله - صلى الله عليه وسلم - وخادمه. ولد بالمدينة، وأسلم صغيرا، وخدم النبي - صلى الله عليه وسلم - إلى أن قبض. روى عن النبي وأبي بكر وعمر وعثمان وابن مسعود وغيرهم. يعد من مفسري الصحابة الذين
_________
(١) العقد المنظم ٤٥٦ وشذرات الذهب ٨: ٣٩٦
(٢) هدية العارفين ١: ٢٢٧ وإيضاح المكنون ١: ١٣٩ و ٢: ٤٤٤ و ٥٣٠ وأبجد العلوم ٩٠٦ والأعلام ١: ٣٥١.','1','96',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (202,'ليس لهم تصانيف. رحل إلى دمشق بعد وفاة النبى - صلى الله عليه وسلم - ومنها إلى البصرة، فكان آخر من مات فيها من الصحابة. روى عنه رجال الحديث ٢٢٨٦ حديثا (١).
_________
(١) تهذيب التهذيب ١: ٣٧٦ وتهذيب تاريخ ابن عساكر ٣: ١٤٢ وطبقات ابن سعد ٧: ١١٦ وانظر فهرسته، وصفة الصفوة ١: ٢٩٨ وكشف الظنون ٤٢٩ والشذرات ١: ١٠٠ والكامل في التاريخ ٢: ٥٤٧ وانظر فهرسته. وتاريخ الطبري ٢: ١٨٠ والجمع بين رجال الصحيحين ٣٥.','1','97',NULL,NULL,'0');
