INSERT INTO [b13846] VALUES (229,'(ج)

جابر بن زيد [٢١ - ٩٣ هـ / ٦٤٢ - ٧١٢ م]

<span data-type="title" id=toc-324>جابر بن زيد الأزدي البصري، أبو الشعثاء:</span>
تابعي ثقة، فقيه مشهور، مفسر، من الأئمة. من أهل البصرة، أصله من عمان. روى عن ابن عباس وابن عمر وغيرهما. قال ابن حبان: "كان من أعلم الناس بكتاب الله" وقال ابن عباس: لو أن أهل البصرة نزلوا عند قول جابر بن زيد لأوسعهم علما عما في كتاب الله". وفي كتاب "الزهد" لأحمد بن حنبل: لما مات جابر بن زيد قال قتادة: "اليوم مات أعلم أهل العراق". وصفه الشماخي بأنه أصل المذهب (الإباضي) وأسه الذي قامت عليه آطامه .. ". قال صاحب تاريخ التراث العربي: "لم تناقش مصادر ترجمته قضية آثاره، ولكنها تتفق على وصفه بأنه عالم','1','123',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (230,'عظيم، وقد يكون ممن ألفوا في التفسير، فقد وصلت إلينا بعض أقواله في الزهد عند أبي نعيم في الحلية" (١).

الجعفي [.. - ١٢٨ هـ / .. - ٧٤٥ م]

<span data-type="title" id=toc-325>جابر بن يزيد بن الحارث الجعفي، أبو عبد الله:</span>
تابعي، فقيه إمامي، من أهل الكوفة، كان واسع الرواية غزير العلم بالدين. روى عن عكرمة وعطاء وغيرهما. وعنه شعبة والثوري وجماعة. أثنى عليه بعض رجال الحديث، واتهمه آخرون
_________
(١) تهذيب التهذيب ٢: ٣٨ وتذكرة الحفاظ ٧٢ والتاريخ الكبير للبخاري ٢/ ١: ٢٠٤ والبدابة والنهاية ٩: ٩٣ والطبقات الكبرى لابن سعد ٥: ٢٨٨ وابن الأثير ٤: ٧٨. وحلية الأولياء ٣: ٨٥ والسير للشماخي ٧٠ وتاريخ التراث العربي ٢: ٢٤٠ وشذرات الذهب ١: ١٠١ والاشتقاق ٥٠٦ وكشف الظنون ٤٢٩ وتاريخ الطبري ١: ٢٦٦ وانظر فهرسته.','1','123',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (231,'بالقول بالرجعة. مات بالكوفة. له "تفسير القرآن" (١).

جبير بن غالب [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-326>جبير بن غالب، أبو فراس:</span>
فقيه، عارف بالتفسير، شاعر. عده صاحب الفهرست من فقهاء "الشراة" وذكر له من كتبه "السنن والأحكام" و "أحكام القرآن" وقال: "كان فقيها شاعرا خطيبا فصيحا" والشراة صقع بين طريق الشام والمدينة في إقليم البلقاء من الأردن (٢).

الاسترابادي [١١٩٧ - ١٢٦٣ هـ / ١٧٨٣ - ١٨٤٧ م]

<span data-type="title" id=toc-327>جعفر (ويقال محمد جعفر) الأسترابادي، الحائري:</span>
مجتهد إمامي، قرأ على الطباطبائي، وجاور في الحائر الحسيني. وعلى أثر محاصرة داود باشا لكربلاء وتخريبها، انتقل المترجم إلى طهران وبقي فيها نحوا من عشر سنين،
_________
(١) هدية العارفين ١: ٢٤٩ وميزان الاعتدال ١: ٣٧٩ وتهذيب التهذيب ٢: ٤٦ والطوسي ٤٥ وذيل المذيل ٩٨ والأعلام ٢: ٩٣ وإيضاح المكنون ١: ٣٠٤ وأعيان الشيعة ١٥: ١٩٩ ومنتهى المقال ٧٢ وتنقيح المقال ١: ٢٠١.
(٢) ابن النديم ٢٩٥ وطبقات المفسرين للداودي ١:','1','124',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (232,'مشتغلا بالإمامة والتدريس والقضاء والفتيا إلى أن توفي. ونقل جثمانه إلى النجف. من كتبه "مظاهر الأسرار في بيان وصف إعجاز كلام الجبار" في تفسير القرآن، لم يكمله (١).

ابن أيوب [القرن الثاني الهجري / أواخر الثامن الميلادي]

<span data-type="title" id=toc-328>أبو جعفر بن أيوب المقرىء:</span>
لغوي، محدث، كان تلميذا لعبد الملك بن جريج (٨٠ - ١٥٥ هـ) وعلى ذلك فقد عاش في النصف الثاني من القرن الثاني الهجري. له كتاب "غريب القرآن" (٢).

ابن حرب [١٧٧ - ٢٣٦ هـ / ٧٩٣ - ٨٥٠ م]

<span data-type="title" id=toc-329>جعفر بن حرب، أبو الفضل الهمداني:</span>
من كبار معتزلة بغداد. أخذ الكلام عن أبي الهذيل العلاف بالبصرة. قال الخطيب البغدادي: "وكان له اختصاص بالواثق، وصنف كتبا معروفة عند المتكلمين" وقال المسعودي: هو رجل من همدان ووجوه قحطان، وإلى أبيه يضاف شارع باب حرب في الجانب الغربي من مدينة السلام، وهو
_________
(١) روضات الجنات ١٥٤ وهدية العارفين ١: ٢٥٧ وأعيان الشيعة ١٥: ٣٥٧ والأعلام ٢: ١٢٢ ومعجم المؤلفين ٣: ١٣٤. وأعلام الشيعة ٢: ٢٥٣.
(٢) تاريخ التراث العربي ١: ٢٠٣.','1','124',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (233,'شيخ البغداديين من المتكلمين" من كتبه "متشابه القرآن" (١).

الحويزي [.. - ١١١٥ هـ / .. - ١٠٧٣ م]

<span data-type="title" id=toc-330>جعفر - ويقال محمد جعفر - بن عبد الله ابن إبراهيم الحويزي الاصفهاني:</span>
من علماء الشيعة الإمامية. قال العاملي: عالم بالأخبار والتفسير والفقه والأصول والكلام والحكمة والعربية، وذكر أنه ولي القضاء بأصبهان (٢).

البحراني [.. - ١٠٨٨ هـ / .. - ١٦٧٧ م]

<span data-type="title" id=toc-331>جعفر بن كمال الدين البحراني:</span>
عارف بالتفسير والحديث والعربية، شاعر، من أهل البحرين، لقيه صاحب "أمل الآمل" في مكة المكرمة، وقال توفي بحيدر آباد. وقال العاملي أن "له تصانيف وتعليقات في الحديث وعلوم العربية والتفسير" وقيل في وفاته سنة ١٠٨٨ هـ، وقيل سنة ١٠٩١ هـ (٣).
_________
(١) طبقات المفسرين ١: ١٢٤ ولسان الميزان ٢: ١١٣ وابن النديم ٢١٣ وتاريخ بغداد ٧: ١٦٢ والمسعودي ٣: ١٩٤.
(٢) أعيان الشيعة ١٥: ٤٧٨ وروضات الجنات
(٣) أمل الآمل ٢: ٥٣ وأعيان الشيعة ١٦: ٩٨ وروضات الجنات ١٤٩.','1','125',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (234,'جعفر بن مبشر [.. - ٢٣٤ هـ / .. - ٨٤٨ م]

<span data-type="title" id=toc-332>جعفر بن مبشر بن أحمد بن محمد الثقفي، أبو محمد:</span>
متكلم، من كبار المعتزلة، مولده ووفاته ببغداد. قال المسعودي: "كان جعفر من علماء المعتزلة وحذاقها وزهادها، وأخوه حنش من علماء أصحاب الحديث ورؤساء الحشوية بالضد من أخيه جعفر، وطالت بينهما المناظرة والتباين، وآلى كل واحد منهما ألا فيطب الآخر إلى أن لحق بخالقه" وقال ابن النديم: "كان حنش أيضا متكلما لكنه لم يقارب جعفرا، وكان جعفر صاحب حديث وله خطابة وبلاغة وزهد وفقه" وذكر له تصانيف، منها "ناسخ القرآن ومنسوخه" (١).

جعفر الصادق [٨٠ - ١٤٨ هـ / ٦٩٩ - ٧٦٥ م]

<span data-type="title" id=toc-333>جعفر بن محمد الباقر بن علي زين العابدين بن الحسن السبط، الهاشمي القرشي، أبو عبد الله، الملقب بالصادق:</span>
سادس أئمة الشيعة الإمامية. ولد بالمدينة
_________
(١) تاريخ بغداد ٧٠: ١٦٢ وطبقات المفسرين للداودي ١: ١٢٥ ولسان الميزان ٢: ١٢١ وأعيان الشيعة ١٦: ١٠٥ ومروج الذهب ٣: ١٩٤ والنخ في القرآن الكريم ١: ٣١٤ و ٢: ١٤٤ وابن النديم','1','125',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (235,'المنورة، وأمه حفيدة الخليفة أبي بكر الصديق. عاش زمنا طويلا في العراق، وعاصر الدولة الأموية والعباسية. وكان مفسرا ومحدثا وعالما في الفقه، حكيما زاهدا. أخذ عنه الإمامان أبو حنيفة ومالك. ولقب بالصادق لأنه لم يعرف عنه الكذب قط. مات بالمدينة. من آثاره "تفسير القرآن" و "منافع صور القرآن" و "خواص القرآن العظيم" (١).

الزعفراني [.. - ٢٧٩ هـ / .. - ٨٩٢ م]

<span data-type="title" id=toc-334>جعفر بن محمد بن الحسن بن زياد بن صالح الرازي، أبو يحيى، الزعفراني، المعروف بالتفسيري:</span>
محدث، من أئمة المفسرين، من أهل الري. قدم بغداد وحدث بها. وثقه الدارقطني وابن أبي حاتم. وفي تاريخ بغداد (٧: ١٨٥) أنه قرىء على ابن المنادي أن أبا يحيى الزعفراني "صاحب التفسير" توفي بالري سنة ٢٧٩ هـ (٢)
_________
(١) أعيان الشيعة ٤: ٢٩ وحلية الأولياء ٣: ١٩٢ وصفة الصفوة ٢: ٩٤ ووفيات الأعيان ١: ٣٢٧ والإمام جعفر الصادق حياته وعصره، لأبي زهزه. وجعفر بن محمد لعبد العزيز سيد الأهل. ومجلة المجمع العلمي العربي بدمشق ٣٤: ٣٦٧ وفرق الشيعة ٥٧ وتاريخ اليعقوبي ٢: ٣٨١ والمعارف لابن تيمية ٨٧ والتاريخ الكبير للبخاري ١/ ٢: ١٩٨.
(٢) طبقات المفسرين للسيوطي ١٠ وطبقات المفسرين =','1','126',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (236,'الفريابي [٢٠٧ - ٣٠١ هـ / ٨٢٢ - ٩١٣ م]

<span data-type="title" id=toc-335>جعفر بن محمد بن الحسن بن المستفاض، أبو بكر الفريابي:</span>
قاض، عالم بالحديث، تركي الأصل، من أهل فرياب - من ضواحي بلخ - طاف كثيرا من البلدان ولقي الأعلام. وعاش فترة في بغداد، ثم رحل إلى مصر حيث تولى منصب القضاء بالدينور مدة. ولما دخل بغداد استقبل فيها بالطبول، وكان يحضر مجلسه بها نحو عشرة آلاف من أصحاب المحابر. مات ببغداد. من آثاره "فضائل القرآن" مخطوط (١).

المستغفري [٣٥٠ - ٤٣٢ هـ / ٩٦١ - ١٠٤١ م]

<span data-type="title" id=toc-336>جعفر بن محمد بن المعتز بن محمد بن المستغفر بن الفتح بن إدريس المستغفري النسفي، أبو العباس:</span>
حافظ، محدث، له اشتغال بالتاريخ، من فقهاء الحنفية. من أهل نسف (من بلاد ما وراء النهر) وكان
_________
= للداودي ١٢٥ وتاريخ بغداد ٧: ١٨٤ وميزان الاعتدال ١: ٤١٦ ولسان الميزان ٢: ١٢٦.
(١) تاريخ بغداد ٧: ١٩٩ الديباج ١٠٢ ومرآة. الجنان ٢: ٢٣٨ وتذكرة الحفاظ ٦٩٢ والشذرات ٢: ٢٣٥ والأعلام ٢: ١٢٣ ومعجم المؤلفين ٣: ١٤٦ وصلة الطبري لعريب ٤٦ والفهرست ٢٨٧ ومختصر دول الإسلام ١: ١٤٤ وهدية العارفين ١: ٢٠٢ وتاريخ التراث العربي ١: ٤١٩.','1','126',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (237,'خطيبها. قال ابن ناصر الدين: "كان حافظا مصنفا ثقة مبرزا على أقرانه لكنه يروي الموضوعات من غير تبين". من كتبه "فضائل القرآن" (١).

جمال الدين القاسمي [١٣٨٣ - ١٣٣٢ هـ / ١٨٦٦ - ١٩١٤ م]

<span data-type="title" id=toc-337>جمال الدين بن محمد سعيد بن قاسم القاسمي، الحلاق:</span>
إمام الشام في علم الدين وفنون الأدب. مولد. ووفاته في دمشق. اشتغل بإلقاء الدروس العامة في المدن والقرى السورية لمدة أربع سنوات (١٣٠٨ - ١٣١٢ هـ). ثم رحل إلى مصر، وزار المدينة المنورة. ولما عاد شنع عليه خصومه بأنه يذهب في الدين مذهبا جديدا سموه "المذهب الجمالي". فقبض عليه (سنة ١٣١٣ هـ) وحقق معه، فرد التهمة، فأخلي سبيله، واعتذر إليه والي دمشق. فعكف في بيته على التصنيف وإلقاء الدروس الخاصة والعامة، في التفسير والتوحيد والحديث والأخلاق والتاريخ
_________
(١) طبقات المفسرين للداودي ١: ١٢٠ والجواهر المضية ١: ١٨٠ والفوائد البهية ٧. والنجوم الزاهرة ٥: ٣٣ والعبر ٣: ١٧٧ والشذرات ٣: ٢٤٩ وهدية العارفين ١: ٢٠٣ والرسالة المستطرفة ٣٩ وتاج التراجم ٢١ وتذكرة الحفاظ ١١٠٢ وأعيان الشيعة ١٦: ٢٤٦ واللباب ٣: ٢٠٨ ومرآة الجنان ٣: ٥٤ والأعلام ٢: ١٢٣ وكشف الظنون ١٢٧٧.','1','127',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (238,'والأدب وغير ذلك من علوم الشريعة الإسلامية". خلف آثارا قيمة، منها "محاسن التأويل" في تفسير القرآن الكريم، في ١٧ مجلدا، حققه محمد بهجة البيطار عضو المجمع العلمي بدمشق، ونشرته دار إحياء الكتب العربية بالقاهرة (١).

الجنيد البغدادي [.. ٢٩٧ هـ / .. - ٩١٠ م]

<span data-type="title" id=toc-338>الجنيد بن محمد بن الجنيد البغدادي الخزاز، أبو القاسم:</span>
صوفي، عالم بالدين، أصله من نهاوند، ومولده ونشأه ووفاته ببغداد. تتلمذ على خاله السري السقطي، واجتمع بالحارث المحاسبي، وتفقه على أبي ثور وكان يفتي في حلقته. بلغ منزلة رفيعة بين صوفية عصره، حتى لقب. بسيد الطائفة، وطاووس الفقراء، وشيخ المشايخ، قال السلمي: "هو من أئمة القوم وسادتهم، مقبول على جميع الألسنة". وقال أبو القاسم الكعبي: "ما رأت عيناي مثله، الكتبة يحضرونه لألفاظه، والفلاسفة لدقة معانيه،
_________
(١) جمال الدين القاسمي وعصره، للأستاذ ظافر القاسمي. وحلية البشر ١: ٤٣٠ والمذكرات لكرد علي ٣: ٦٨٧ وتراجم أعيان دمشق ١١٨ ومنتخبات التواريخ لدمشق ٢: ٧١٦ ومعجم المطبوعات ١٤٨٣ والموسوعة العربية الميسرة، والأعلام ٢: ١٣١.','1','127',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (239,'والمتكلمون لزمام علمه". وكلامه في الحقيقة مدون مشهور. من آثاره "أمثال القرآن" (١).
_________
(١) تاريخ بغداد ٧: ٢٤١ وحلية الأولياء ١٠: ٢٥٥ وطبقات الشعراء ١: ٨٤ وصفوة الصفوة ٢: ٣٢٥ والسبكي ٢: ٢٦٥ والعبر ٢: ١٠ والبداية والنهاية ١١: ١١٣ ومرآة الجنان ٢: ٢٣١ والنجوم الزاهرة ٣: ١٧٧ وابن خلكان ١: ٣٧٣ والكامل في التاريخ ٨: ٦٢ وطبقات الشافعية لابن هداية الله ٣٩ وابن النديم ٣٣٨ واللباب ٣: ٦٢ وطبقات المفسرين للداودي ١: ١٢٦ وطبقات الصوفية ١٥٥ وطبقات الحنابلة ٨٩ والأعلام ٢: ١٣٧ والمناوي ١: ٢١٢ والموسوعة العربية الميسرة ٦٥٢ والرسالة القشيرية، ٢ والمنتظم ٦: ١٠٥ وروضات الجنات ١٦٤ ومعجم المؤلفين ٣: ١٦٢.','1','128',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (240,'<span data-type="title" id=toc-339>حرف الحاء</span>
الحاثك = عبد الرحمن بن محمد ... ١٢٣٧ هـ
ابن أي حاتم = عبد الرحمن بن محمد ٣٢٧ هـ
ابن الحاج = حمدون بن عبد الرحمن ... ١٢٣٢ هـ
ابن الحاج حسن = محمد شاه بن محمد ٩٣٩ هـ
