INSERT INTO [b13846] VALUES (242,'حقي الأرضروي = إبراهيم بن درويش ... ١١٩٥ هـ
الحكري = إبراهيم بن عبد الله ... ٧٨٠ هـ
الحكيم = عبد العزيز بن عمر ... بعد ١٣٢٤ هـ
الحكيم = علي بن محمد ١٣٠٠ هـ
الحكيم = مصطفى بن احمد ... ١٣٤١ هـ
حكيم شاه = محمد بن مبارك ... ٩٢٠ هـ
الحلاج = الحسن بن منصور ... ٣٠٩ هـ
الحلالي = عبد الرحمن بن محمد ٨٣٦ هـ
الحلبي = إبراهيم بن محمد ٩٥٦ هـ
الحلبي = إبراهيم بن محمد ٩٥٦ هـ
الحلبي = احمد بن إبراهيم ... ٩٧١ هـ
الحلوائي = يوسف بن الحسن ... ٨٠٤ هـ
الحلواني = عبد الرحمن بن محمد ٥٤٦ هـ
الحلي (العلامة) = الحسن بن يوسف ... ٧٢٦ هـ
الحلي = المقداد بن عبد الله ... ٨٢٦ هـ
الحليمي = محمد بن أسعد ... ٥٦٧ هـ
حمد الخطابي = حمد بن محمد ٣٨٨ هـ
حمدي عبيد = حمدي بن محمد ١٣٩١ هـ
حمزة = محمود بن محمد نسيب ... ١٣٠٥ هـ
ابن حمشاد = علي بن محمد ٣٣٨ هـ
ابن حموش = مكي بن أبي طالب حموش ... ٤٣٧ هـ
الحموي = أحمد بن محمد ١٠٩٨ هـ
الحموي = عبد النافع بن عمر ... ١٠١٦ هـ
الحموي = علي بن محمد ٧٩٣ هـ
ابن حموية = محمد بن حموية ... ٥٣٠ هـ
الحميري = محمد بن محمد ... حيا ٧٢٦ هـ
الحميري = نشوان بن سعيد
ابن الحنائي = علي جلبي بن أمر الله ... ٩٧٩ هـ
حناوي زاده = علي بن محمد ٩٧٩ هـ
ابن الحنبلي = عبد الوهاب بن عبد الواحد ... ٥٣٦ هـ','1','131',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (243,'ابن الحنبلي = محمد (ويقال سعد) بن عبد الوهاب ... ٦٥٢ هـ
حنيف الرولي = إبراهيم بن مصطفى ... ١١٨٩ هـ
الحوفي = علي بن إبراهيم ... ٤٣٠ هـ
الحويزي = جعفر بن عبد الله ... ١١١٥ هـ
الحويزي (عبد علي) = عبد علي بن ناصر ... ١٠٥٣ هـ
الحويزي = علي بن خلف ... بعد ١٠٨٧ هـ
الحويزي = فرج الله بن محمد ١١٠٠ هـ
أبو حيان النحوي = محمد بن يوسف ... ٧٤٥ هـ
الحيدري = إبراهيم بن صبغة الله ... ١٢٩٩ هـ
الحيدري = صبغة الله بن إبراهيم ... ١١٨٧ هـ
الحيري = إسماعيل بن احمد ... بعد ٤٣٠ هـ
الحيسي = يحيى بن علي ... حيا ١١٠٤ هـ','1','132',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (244,'(ح)

القونوي [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-340>حاجي باشا بن خواجه علي مراد بن خواجه علي بن حسام الدين القونوي:</span>
مفسر، ذكره صاحب كشف الظنون وقال له "مجمع الأنوار في جميع الأسرار" تفسير كبير في مجلدات، أوله: الحمد لله الذي هدانا بالقرآن" (١).

<span data-type="title" id=toc-341>الحارث بن عبد الرحمن</span>
[؟ -؟ /؟ -؟]
الحارث بن عبد الرحمن: عارف بالتفسير. ذكره ابن النديم وقال "له ناسخ القرآن ومنسوخه" ولم يزد على ذلك (٢).
_________
(١) كشف الظنون ١٥٩٨.
(٢) الفهرست ٤٠ وطبقات المفسرين ١: ١٢٧ وينقل عن الفهرست.','1','133',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (245,'القارصي [.. - ١٢٩١ هـ / .. - ١٨٧٤ م]

<span data-type="title" id=toc-342>حامد بن عبد الله القارصي:</span>
نحوي مفسر، تركي مستعرب، من أهل قارص، (شمال شرق تركيا) مولدا ووفاة. له تصانيف بالتركية والعربية، فمن العربية "تفسير سورة عبس" (١).

العمادي [١١٠٣ - ١١٧١ هـ / ١٦٩٢ - ١٧٥٨ م]

<span data-type="title" id=toc-343>حامد بن علي بن إبراهيم بن عبد الرحيم بن عماد الدين الدمشقي، المعروف بالعمادي:</span>
مفتي دمشق وابن مفتيها، عالم بالفقه والفرائض والأدب، من كبار فقهاء الحنفية، مولده ووفاته بدمشق، تعلم بها وبمكة وأستنبول، ودرس بالجامع الأموي
_________
(١) هدية العارفين ١: ٢٦١ ومعجم المؤلفين ٣: ١٧٩.','1','133',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (246,'وغيره، ثم تولى الإفتاء سنة ١١٣٧ هـ واستمر إلى أن توفي. قال المرادي: "وكان يستفتح أكثر دروسه بخطب من إنشائه، جمعت في مجلد كبير". من كتبه "التفصيل بين التفسير والتأويل" (١).

القمي [١٢٨٩ - ١٣٥٩ هـ / ١٨٧٢ - ١٩٤٠ م]

<span data-type="title" id=toc-344>حبيب الله بن زين العابدين القمي، الزيداني:</span>
فقيه إمامي، أصولي، منطقي، عارف بالتفسير، من أهل "قم" وبها نشأ وتعلم، ثم انتقل إلى زيران وأقام بها إلى أن مات. من كتبه "جوامع الخيرات في تفسير الآيات" في خمس مجلدات (٢).

المصيصي [.. : ٢٠٦ هـ / .. - ٨٢١ م]

<span data-type="title" id=toc-345>حجاج بن محمد المصيصي الأعور، أبو محمد:</span>
من رجال الحديث الثقات، عارف بالتفسير، ترمذي الأصل، من أهل بغداد، سمع من ابن جريج وغيره، وروى عنه يحيى بن معين وجماعة. ثم تحول
_________
(١) سلك الدرر ٢: ١١ وهدية العارفين ١: ٢٦١ وإيضاح المكنون ١: ٣١١ وفهرس الفهارس ٢: ٢٥٨ ومعجم المؤلفين ٣: ١٨٠ والأعلام ٢: ١٦٦.
(٢) أعلام الشيعة ١: ٣٥٦ ومعجم المؤلفين ٣: ١٨٧.','1','134',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (247,'بولد. وعياله إلى المصيصة (مدينة على الساحل قرب طرسوس) وأقام بها فنسب إليها. وفي آخر حياته عاد إلى بغداد في حاجة له، فاختلط، ثم مات. أخرخ له أصحاب الكتب الستة، ووثقه جميع النقاد وأثنى عليه الإمام أحمد بن حنبل. له "ناسخ القرآن ومنسوخه" (١).

الطريحي [١٠٠٥ - ١٠٩٥ هـ / ١٥٩٧ - ١٦٨٤ م]

<span data-type="title" id=toc-346>حسام الدين بن جمال الدين بن محمد بن علي بن أحمد بن طريح المسيلمي، العزيزي، الطريحي، الرماحي:</span>
فقيه إمامي، عارف بالتفسير والأصول والحديث واللغة، شاعر، من أهل النجف مولدا ووفاة. له "الوجيز في تفسير القرآن العزيز" (٢).

المداري [القرن الثاني الهجري / القرن الثامن الميلادي]

<span data-type="title" id=toc-347>حسان المداري:</span>
محدث، عارف بالتفسير، أدرك بعض الصحابة، وروى
_________
(١) تاريخ بغداد ٨: ٢٣٦ وتذكرة الحفاظ ٣٤٥ وطبقات المفسرين للداودي ١: ١٢٧ وتهذيب التهذيب ٢: ٢٠٥ وخلاصة تهذيب الكمال ٦٢ والعبر ١: ٣٤٩ وابن النديم ٤٠ وميزان الاعتدال ١: ٤٦٤ والنجوم الزاهرة ٢: ١٨١ وشذرات الذهب ٢: ١٥ والنسخ في القرآن الكريم ١: ٣١١ وطبقات القراء ١: ٢٠٣.
(٢) أمل الآمل ٥٩ وأعيان الشيعة ٢٠: ٤٠٨ ومعجم المؤلفين ٣: ١٩١.','1','134',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (248,'عن علي بن الحسن زين العابدين (٣٨ - ٩٤ هـ) وروى عنه ابن جريج (٨٠ - ١٥٥ هـ) وغيره. ذكره الكشي في رجال الشيعة وقال: كان عارفا، بالتفسير، ثقة مستقيم" (١).

أبو علي الفارسي [٢٨٨ - ٣٧٧ هـ / ٨٤٣ - ٩٨٧ م]

<span data-type="title" id=toc-348>الحسن بن أحمد بن عبد الغفار بن محمد بن سليمان بن أبان الفارسي الأصل، أبو علي:</span>
نحوي واسع العلم من أئمة النحاة. ولد في فسا (من أعمال فارس) ودخل بغداد سنة ٣٠٧ هـ فاستوطنها وأخذ عن علماء النحو بها كالزجاج وابن السراج، وعلت منزلته في النحو، حتى قال قوم من تلامذته: "هو فوق المبرد وأعلم". ثم تجول في البلدان، ودخل حلب سنة ٣٤١ هـ، فأتام مدة عند سيف الدولة. وعاد إلى فارس، فصحب عضد الدولة بن بويه، وتقدم عنده، فعلمه النحو، وصنف له كتاب الايضاح في قواعد العربية، حتى قال عضد الدولة: "أنا غلام أبي علي في النحو ... ". ثم رجع إلى بغداد فأقام بها إلى أن توفي. من تصانيفه الكثيرة كتاب في تفسير قوله تعالى {يا أيها الذين آمنوا إذا
_________
(١) لسان الميزان ٢: ١٩٠ وطبقات المفسرين للداودي ١: ١٢٨.','1','135',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (249,'قمتم إلى الصلاة، وكتاب "التتبع لكلام أبي علي الجبائي" في التفسير (١).

أبو العلاء الهمذاني [٤٨٨ - ٥٦٩ هـ / ١٠٩٥ - ١١٧٣ م]

<span data-type="title" id=toc-349>الحسن بن أحمد بن الحسن بن أحمد بن محمد بن سهل العطار، أبو العلاء الهمذاني:</span>
شيخ همذان، وإمام العراقيين في القراءات، وأحد الحفاظ الثقات، له باع في التفسير والحديث والأنساب والأسماء والكنى والقصص والسير. من أهل همذان. رحل في طلب القراءات والحديث إلى أصبهان وبغداد وواسط ونيسابور. قال الذهبي: "كان لا يأكل من أموال الظلمة ولا يقبل منهم مدرسة ولا رباطا، ولا يغشى السلاطين ولا تأخذه في الله لومة لائم" من كتبه "زاد المسافر" في تفسير القرآن، خمسون جزءا (٢).
_________
(١) تاريخ بغداد ٧: ٢٧٥ والشذرات ٣: ٨٨ ونزهة الألباء٣٨٧ والنجوم الزاهرة ٤: ١٥١ ومعجم الأدباء ٧: ٢٣٢ ولسان الميزان ٢: ١٩٥ والمزهر ٢: ٤٢٠ وغاية النهاية ١: ٢٠٦ والبداية والنهاية ١١: ٣٥٦ وانباء الرواة ١: ٢٧٣ والفهرست ٦٩ وهدية العارفين ١: ٢٧٢ وروضات الجنات ٢١٩ وأعيان الشيعة ٢١: ١١ ومرآة الجنان ٢: ٤٠٦ وتذكز الحفاظ ٩٧٢ والمنتظم ٧: ١٣٨ والمختصر في أخبار البشر ٢: ١٢٤ وبغية الوعاة ١: ٤٩٦ ومختصر دول الإسلام ١: ١٨٥ والأعلام ٢: ١٩٣ وتلخيص ابن مكتوم ٤٩ وابن قاضي شهبة ١: ٢٩٥.
(٢) طبقات المفسرين للداودي ١: ١٢٨ وبغية الوعاة ١: ٤٩٤ وتذكرة الحفاظ ١٣٢٤ وغاية النهاية ١: ٢٠٤ =','1','135',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (250,'الجلال اليمنى [١٠١٤ - ١٠٨٤ هـ / ١٦٠٥ - ١٦٧٣ م]

<span data-type="title" id=toc-350>الحسن بن أحمد بن محمد بن علي بن صلاح بن أحمد، الحسني العلوي، المعروف بالجلال:</span>
عارف بالتفسير والعربية والمنطق. ولد في هجرة رغافة - قرية بين الحجاز وصعدة. - وبها نشأ وتعلم، ثم رحل فسمع بصعدة وشهارة وصنعاء. واستوطن "الجراف" ومات فيها. قال الشوكاني: "كان الإمام المتوكل على الله إسماعيل بن القاسم يجله غاية الإجلال، ولا يعرف أهل الفضل إلا أهله". من كتبه "فتح الألطاف في تكملة الكشف على الكشاف، وفي التفسير (١).

الداماد [.. - ١٢٢٣ هـ / .. - ١٨٠٨ م]

<span data-type="title" id=toc-351>حسن بن أحمد الزعفراني الروماي المعروف بالداماد:</span>
قاض، عارف بالتفسير، من فقهاء الحنفية، تركي مستعرب، استوطن بروسة وتوفي بها. له
_________
= وروضات الجنات ٢٢٢ والعبر ٤: ٢٠٦ والنجوم الزاهرة ٦: ٧٢ والشذرات ٤: ٢٣١ وإرشاد الأريب ٣: ٣٨٩ وأعيان الشيعة ٢٠: ٤٦٨ ومرآة الجنان ٣: ٣٨٩ والمنتظم ١٠: ٢٤٨ وايضح المكنون ١: ٦٠٦ والأعلام ٢: ١٩٥ وهدية العارفين ١: ٢٨٥.
(١) خلاصة الأثر ٢: ١٧ وهدية العارفين ١: ٢٩٥ والبدر الطالع ١: ١٨٩ وايضع المكنون ٢: ١٥٩ ومعجم المؤلفين ٣: ٢٠٢ والأعلام ٢: ١٩٦.','1','136',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (251,'"حاشية" على أنوار التنزيل، للبيضاوي، في التفسير (١).

العدوي [١٢٥٧ - حيا ١٣٢٩ هـ / ١٨٤٨ - حيا ١٩١١ م]

<span data-type="title" id=toc-352>حسن بن أحمد بن أحمد سالم الرفاعي، أبو محمد، الشهير بالهواري العدوي:</span>
فقيه مالكي، محدث، مقرىء، من العلماء، ولد في بني عدي (بالقرب من منفلوط - بمصر) وتعلم بها وبالقاهرة، قال صاحب شجرة النور: "فاقتبس بها العلوم على فطاحل ذلك العصر كالشيخ عليش وغيره،، ثم تحول إلى أسيوط وأخذ عن علمائها. من أشهر تلاميذه الذين أخذوا عنه الشيخ علي يوسف صاحب جريدة المؤيد. له "فتح الجليل بذكر طرف فيما يتعلق بالتنزيل" (٢).

الطويل [١٢٥٠ - ١٣١٧ هـ / ١٨٣٤ - ١٨٩٩ م]

<span data-type="title" id=toc-353>حسن بن أحمد بن علي، أبو محمد الطويل:</span>
مفسر، محدث، أصولي، من فقهاء المالكية. ولد في منية شهالة بالمنوفية بمصر، وتعلم بطنطا ثم بالأزهر. درس الحديث والتفسير والأصول بمدرسة دار
_________
(١) هدية العارفين ١: ٢٩٧ وإيضاح المكنون ١: ١٤٠ وفيه وفاته ١١١٣ هـ.
(٢). شجرة النور ٤١١ اواليواقيت الثمينة ١: ٢٥٧','1','136',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (252,'العلوم. وكان شديد الإنكار على المبتدعة. له "عنوان البيان في تفسير القرآن" طبعت مقدمته قبل وفاته بسنة (١٣١٦ هـ) (١).

البنا [١٣٢٤ - ١٣٦٨ هـ / ١٩٠٦ - ١٩٤٦ م]

<span data-type="title" id=toc-354>حسن بن أحمد بن عبد الرحمن البنا:</span>
مؤسس جمعية "الاخوان المسلمين" بمصر، ومرشدهم وقائدهم وخطيبهم، مصلح ديني وإجتماعي، وأحد زعماء حركة بعث الروح الإسلامية وإحيائها في العصر الحديث. ولد في "المحمودية" بمديرية البحيرة، قرب الإسكندرية. وتعلم بالرحمانية ودمنهور، وتخرج (١٩٢٧) بمدرسة دار العلوم بالقاهرة. واشتغل بالتعليم، فعين مدرسا لعلوم العربية والدينية بالمدرسة الابتدائية بمدينة الإسماعيلية. وأنس في الشعب شعورا إسلاميا قويا، فاستخلص أفرادا صارحهم بما في نفسه، فعاهدوه، على السير معه لإعلاء كلمة الإسلام، فبدأ بدعوته في ٢٨ مارس ١٩٢٨، وأقام بالإسماعيلية أول دار للإخوان، واختار لنفسه لقب المرشد العام. ثم بادر وجماعته إلى إعلان "الدعوة" بالدروس والمحاضرات والنشرات في المدن، فحظيت
_________
(١) شجرة النور ٤١٠ والاعلام الشرقية ٢: ٩٧ وتراجم اعيان القرن الثالث عشر ١٢٠ والتيمورية ٣: ١٨٦ والأعلام ٢: ١٩٦.','1','137',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (253,'بما لم تحظ به أية هينة دينية أو سياسية أو إجتماعية من قبل. وأصبح لجماعته دار في كل بلد سعى إليه. وأنشأ في الإسماعيلية "معهد أمهات المسلمين" لتربية البنات تربية دينية صالحة. ونقل مدرسا إلى القاهرة، فانتقل معه المركز العام ومقر قيادة الجمعية، واتسع فيها مجال النشاط أمامه، وزاد الإقبال على دعوته، فأنشأ جريدة "الإخوان المسلمون" اليومية، فكانت منبره الكتابي إلى جانب منابره الخطابية. وامتدت الدعوة إلى مختلف المدن والقرى المصرية، وانشئت لها مئات الفروع، وناهز عدد أعضائها قبل حرب فلسطين (١٩٤٨) نصف مليون، وخشي المسيطرون على مقدرات مصر السياسية اصطدامهم بهم، ونظروا إلى قادتهم نظرة تحسب وخشية، وحاولوا إبعادهم عن السياسة، فقام المرشد العام يعرف الإسلام في إحدى خطبه بأنه "عقيدة وعبادة ووطن وجنسية وسماحة وقوة وخلق ومادة وثقافة وقانون". ونشب القتال بفلسطين بين العرب واليهود (سنة ١٩٤٨) فاشتركت كتيبة من الإخوان في القتال وأبلت بلاء حسنا، ونودي بالهدنة، وفي أيدي الإخوان سلاح دربوا على استعماله وادخروه للملمات، فحدثت في الإسكندرية والقاهرة أحداث إرهابية عجزت السلطات القائمة عن معالجتها، فأمر رئيس الوزراء','1','137',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (254,'محمود فهمي النقراشي (١٨٨٨ - ١٩٤٨) بإغلاق أنديتهم، ومطاردة البارزين من أعضاء الجمعية، واعتقال الكثيرين، ومضايقة مرشدهم العام الشيخ حسن البنا، فتحولوا إلى "خلايا" سرية تعمل في الخفاء، وتصدى للنقراشي أحد شبانها - وهو طالب في كلية الطب البيطري، اسمه عبد المجيد أحمد حسن، فقتله بثلاث رصاصات، أمام مصعد وزارة الداخلية. وفي مساء ١٢ شباط (فبراير) ١٩٤٩ تصدى للبنا ثلاثة أشخاص، وهو خارج من بيت جمعية الشبان المسلمين، في القاهرة، فأطلقوا عليه الرصاص وفروا، ولم يجد من يضمد جراحه، فتوفي بعد ساعتين. له رسائل كثيرة منها في التفسير "مقدمة في علم التفسير". (١)

حسن باندونج [١٣٠٤ - ١٣٧٨ هـ / ١٨٨٧ - ١٩٥٧ م]

<span data-type="title" id=toc-355>حسن بن أحمد باندونج الأندونيسي الجاوي:</span>
مفسر، عالم بالفقه والحديث وعلم الكلام، من رجال الإصلاح الإسلامي. ولد في سنغافورة وبها نشأ
_________
(١) حسن البن١: حياة رجل وتاريخ مدرسة، لأنور الجندي، وروح وريحان من حياة داع ودعوة، لأحمد أنس الحجاجي، وحسن البنا كما عرفته لفتحي العسال، والموسوعة العربية الميسرة ٧١٨ والصحف المصرية ٨/ ١١/ ١٩٤٩ وفيها أسماء قاتليه، ومصادر الدراسة الأدبية ٢: ٢٠٩ وفي الحاشية ذكر لمصادر أخرى.','1','138',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (255,'وتعلم. ورحل إلى باندونج (بأندونيسيا) سنة ١٩٢٤ واستقر بها ونسب إليها، وأنشأ فيها مجلة "الدفاع عن الإسلام". وانتقل سنة ١٩٤١ إلى بلدة بانفيل بجاوة الشرقية فأقام بها إلى أن توفي. من آثاره "الفرقان في تفسير القرآن" بالأندونيسية (١).

الفناري [٨٤٠ - ٨٨٦ هـ / ١٤٦٣ - ١٤٨١ م]

<span data-type="title" id=toc-356>حسن جلبي بن محمد شاه بن محمد بن حمزة الرومي، بدر الدين الفناري:</span>
فقيه حنفي، مشارك في بعض العلوم، تركي مستعرب. ولد ببلاد الروم، وأخذ عن علمائها حتى برع في الكلام والمعاني والعربية والمعقولات وأصول الفقه، ودرس في أدرنة، ثم رحل إلى مصر فأخذ عن تلامذة ابن حجر العسقلاني، ولقي السخاوي والسيوطي، وحج من القاهرة، ثم عاد إلى بلاده، واشتغل بالتدريس، وسكن بروسة إلى أن مات. من كتبه "حاشية" على حاشية الشريف الجرجاني على الكشاف للزمخشري. و "حاشية" على تفسير البيضاوي" (٢).
_________
(١) مجلة المسلمون ٦: ٥٧٦.
(٢) الشقائق النعمانية ١١٤ والضوء اللامع ٣: ١٢٧ والفوائد البهية ٦٤ والبدر الطالع ١: ٢٥٨ ونظم العقيان ١٠٥ وشذرات الذهب ٧: ٣٢٤ وكشف الظنون ١٤٧٩ وعثمانلي مؤلفلري ١: ٢٧٢.','1','138',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (256,'الموصلي [١٢٤٨ - ١٣١٦ هـ / ١٨٣٣ - ١٨٩٨ م]

<span data-type="title" id=toc-357>حسن حسني بن محمد بن إسماعيل بن محمد بن درويش بن عبد الله الموصلي:</span>
قاض، مفسر، أصله من المدينة المنورة، ولد بالموصل، وولي قضاءها، وقضاء الشام، والمدينة، ثم عهد إليه بتفتيش الأوقاف "الهمايونية" في الآستانة، وتوفي بها. من كتبه " فتح الرحمن بتفسير القرآن " يوجد منه مجلدان وصل فيهما إلى سورة الأنعام (١).

الخبري [.. - ٤٢٦ هـ / .. - ١٠٣٥ م]

<span data-type="title" id=toc-358>الحسن بن الحسن بن عمر بن خشنام الخبري، أبو محمد:</span>
عالم بالتفسير والفقه، من كتبه "عيون التفاسير". والخبري نسبة إلى قرية خبر من قرى شيراز (٢).

ابن الخطير النعماني [٥٤٧ - ٥٩٨ هـ / ١١٥٢ - ١٢٠٢ م]
الحسن بن الخطير بن أبي الحسن علي

<span data-type="title" id=toc-359>الفارسي، ظهير الدين، أبو علي، (النعماني)</span>
_________
(١) تراجم أعيان دمشق ٧٤ وحلية البشر ١: ٥٢٦ والأعلام ٢: ٢٠٢ وفيه ولادته سنة ١٢٤٧ ووفته سنة ١٣١٧ هـ. وتاريخ الموصل ٢: ٢٦٩ ومعجم المؤلفين
(٢) معجم المؤلفين ٣: ٢١٩ وشد الإزار ١١٤.','1','139',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (257,'النعماني: نحوي، لغوي، عروضي، عالم بالتفسير والفقه والخلاف والكلام والحساب والمنطق والهينة والطب واللغة العبرية، مقرىء كبير، من فقهاء الحنفية. نسبته إلى مدينة النعمانية - بين بغداد وواسط -، ويقال له الفارسي لأنه تفقه بشيراز. دخل الشام، وأقام بالقدس مدة، وعرف الملك العزيز عثمان بن صلاح الدين الأيوبي منزلته في العلم، فدعاه للإقامة في مصر، فذهب معه (حوالي سنة ٥٩٣ هـ) وتوفي بها. من كتبه "تفسير القرآن" ويعرف بتفسير النعماني (١).

المطوعي [.. - ٣٧١ هـ / .. - ٩٨١ م]

<span data-type="title" id=toc-360>الحسن بن سعيد بن جعفر بن الفضل بن شاذان أبو العباس، المطوعي، العباداني، البصري، العمري:</span>
شيخ القراء في وقته، عارف بالتفسير، له اشتغال بالحديث. سكن اصطخر، ورحل في طلب الحديث والقراءات، فروى عن كبار الأئمة في العراق والشام ومصر وغيرها، وانتهى إليه علو الإسناد في القراءات. قال الذهبي:
_________
(١) هدية العارفين ١: ٢٨٠ وتاج التراجم ٢٣ ومعجم الأدباء ٨: ١٠٠ وبغية الوعاة ١: ٥٠٢ وطبقات المفسرين للداودي ١: ١٣١ والجواهر المضية ١: ١٩١ وكشف الظنون ٤٦٠ وروضات الجنات ٢٢٣ ومعجم المؤلفين ٣: ٢٢٢.','1','139',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (258,'"ضعفه ابن مردويه، وقال أبو نعيم لين في روايته، عاش مائة سنة وسنتين". له كتاب "معرفة اللامات وتفسيرها" (١).

الأنطاكي [.. - ٣٩٩ هـ / .. - ١٠٠٩ م]

<span data-type="title" id=toc-361>الحسن بن سليمان بن الخير، أبو علي الأنطاكي النافعي:</span>
من كبار القراء في وقته، نحوي، عارف بالتفسير، من أهل أنطاكية (على نهر العاصي جنوبي تركية) رحل إلى مصر واستوطنها. والنافعي نسبة إلى قراءة نافع. قال أبو عمرو الداني: "كان أحفظ أهل زمانه للقراءات والغرائب من الروايات والشاذ من الحروف، يحفظ تفسيرا كثيرا، ومعاني جمة، وإعرابا وعللا". قتله الحاكم بأمر الله العبيدي. قال الذهبي: "كان مداخلا للعبيديين أصحاب مصر، فسلط عليه الحاكم، وقتله في آخر سنة ٣٩٩ هـ ". وقال المقريزي في حوادث السنة المذكورة: "وفيها قتل أسامة ابن محمد اللغوي، والحسن بن سليمان الأنطاكي، واستتر عبد الغني بن سعيد،
_________
(١) غاية النهاية ١: ٢١٣ وتذكرة الحفاظ ٩٥٠ وشذرات الذهب ٣: ٧٥ وطبقات المفسرين للداودي ١: ١٣٣ وهو ينقل عن غاية النهاية، ولكنه أخطأ فنقل ترجة تلي ترجمة المطوعي هذا. واللباب وفيه: المطوعي: يضم الميم وفتح الطاء المشددة وكسر الواو وفي آخرها عين مهملة، نسبة إلى المطوعة، وهم جماعة فرغوا أنفسهم للغزو ومرابطة الثغور وقصدوا جهاد العدو في بلادهم لا إذا قصد العدو بلاد الإسلام".','1','140',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (259,'وكان ذلك بسبب إجتماعهم بدار العلم وجلوسهم فيها" (١).

الرامهرمزي [نحو ٢٦٥ - ٣٦٠ هـ / ٨٧٩ - ٩٧٠ م]

<span data-type="title" id=toc-362>الحسن بن عبد الرحمن بن خلاد الرامهرمزي، أبو محمد:</span>
حافظ، محدث، أديب، شاعر، قاض، نسبته إلى مدينة رامهرمز، إحدى كور الأهواز من بلاد خوزستان في إيران. وبها نشأ، ثم رحل في طلب الحديث، فسمع من نحو مائتي شيخ. وعاد إلى بلده، فولي القضاء في خوزستان. ذكره الثعالبي في يتيمة الدهر وقال: " ... بن خلاد، من أنياب الكلام، وفرسان الأدب، وأعيان الفضل، وأفراد الدهر، وجملة القضاة الموسومين بمداخلة الوزراء والرؤساء، وكان مختصا بابن العميد، تجمعهما كلمة الأدب، ولحمة العلم، وتجري بينهما مكاتبات بالنثر والنظم، وهكذا كانت حاله مع الوزير المهلبي". مات في رامهرمز. من آثاره "إمام التنزيل" في القرآن الكريم (٢).
_________
(١) غاية النهاية ١: ٢١٥ وطبقات المفسرين للداودي ١: ١٣٣ واتعاظ الحنفا ٢: ٨٠ والحاكم بأمر الله ١١٣ وهو فيه حسين خطأ.
(٢) يتيمة الدهر ٣: ٤٢٣ ومعجم الأدباء ٩: ٥ وشذرات الذهب ٣: ٣٠ وتذكرة الحفاظ ٩٠٥ والعبر ٣: ٣٢١ وأعيان الشيعة ٢٢: ٦٩ وابن النديم ١٧٢ =','1','140',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (260,'أبو هلال العسكري [... - حيا ٣٩٥ هـ / .. - حيا ١٠٠٥ م]

<span data-type="title" id=toc-363>الحسن بن عبد الله بن سهل بن سعيد بن يحيى بن مهران العسكري، أبو هلال:</span>
عالم بالأدب، مفسر، له شعر، من أهل عسكر مكرم بالأهواز. تعلم ببغداد والبصرة وأصفهان. واتجر بالثياب. من تصانيفه الكثيرة "المحاسن" في تفسير القرآن، خمس مجلدات (١).

الديلمي [١٢٢٩ - ١٢٨١ هـ / ١٨١٤ - ١٨٦٤ م]

<span data-type="title" id=toc-364>الحسن بن عبد الوهاب بن الحسن بن يحيى بن إبراهيم بن يحيى الحسني الطالبي اليمني، المعروف بالديلمي:</span>
فقيه زيدي، عارف بالنحو والمنطق والتفسير، ولد ونشأ في ذمار باليمن، وتوفي حاجا بمكة. من كتبه "مختصر الاتقان في علوم القرآن" (٢).
_________
= ومعجم المؤلفين ٣: ٢٣٥ وإيضاح المكنون ١: ١٢٤ والمحدث الفاصل بين الراوي والواعي، مقدمته، للأستاذ الدكتور محمد عجاج الخطيب.
(١) بغية الوعاة ١: ٥٠٦ ومعجم الأدباء ٨: ٢٥٨ وأعيان الشيعة ٢٢: ١٥٤ وطبقات المفسرين للسيوطي ١٠ وطبقات المفسرين للداودي ١: ١٣٤ وهدية العارفين ١: ٢٧٣ والأعلام ٢: ٢١١ وكشف الظنون ٤٥٣ ومعجم المؤلفين ٣: ٢٤٠.
(٢) م نيل الوطر ١: ٣٤٠ ومعجم المؤلفين ٣: ٢٤٣ والأعلام ٢: ٢١٣.','1','141',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (261,'البطايني [.. - حيا قبل ٢٢٤ هـ / .. - حيا قبل ٨٣٩ م]

<span data-type="title" id=toc-365>الحسن بن علي بن أبي حمزة سالم البطايني الكوفي:</span>
محدث، عارف بالتفسير، من فقهاء الشيعة الإمامية، من أهل الكوفة. روى عنه الحسن بن علي بن فضال (الآتي) أحاديث كثيرة، وكتب عنه "تفسير القرآن" (١).

ابن فضال [.. - ٢٢٤ هـ / .. - ٨٣٩ م]

<span data-type="title" id=toc-366>الحسن بن علي بن فضال التيمي، بالولاء، أبو محمد:</span>
محدث، مفسر، من فقهاء الإمامية، من أهل الكوفة. حدث عن الحسن بن علي (السابق) وكتب عنه تفسير القرآن. من كتبه "الناسخ والمنسوخ في القرآن" و "تفسير القرآن" (٢).

الحسن العسكري [٢٣٢ - ٢٦٠ هـ / ٨٤٦ - ٨٧٤ م]

<span data-type="title" id=toc-367>الحسن بن علي الهادي بن محمد الجواد بن علي الرضا بن موسى الكاظم بن جعفر (الصادق الحسيني الطالبي، أبو محمد)</span>
_________
(١) أعيان الشيعة ٢٢: ٣٦٥ وفهرست الطوسي ٥١ ومعجم المؤلفين ٣: ٢٥٣.
(٢) طبقات المفسرين للداودي ١: ١٣٧ وابن النديم والطوسي ٩٣ ولسان الميزان ٢: ٢٢٥ وكتاب الرجال ٢٤ واعيان الشيعة ٢٢: ٤١٠ ومنتهى المقال ١: ٢٩٧ والأعلام ٢: ٢١٥ ومعجم المؤلفين ٣: ٢٥٧ وإيضاح المكنون ٥١٦.','1','141',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (262,'الصادق الحسيني الطالبي، أبو محمد: الإمام الحادي عشر عند الشيعة الإمامية. ولد في المدينة المنورة .. وشي بأبيه إلى المتوكل العباسي، فاستقدمه إلى بغداد وأنزله سامراء، وانتقل الحسن مع أبيه. وكانت سامراء تسمى "مدينة العسكر" لأن الخليفة المعتصم لما بناها انتقل إليها بعسكره. فنسب إليها علي الهادي، وقيل لصاحب الترجمة "العسكري" كأبيه، نسبة إليها. بويع بالإمامة سنة ٢٥٤ هـ. وكان من الأتقياء الصبحاء. توفي بسامراء. ذكر له كتاب في "تفسير القرآ "، أشار صاحب "تاريخ التراث العربي إلى أماكن مخطوطاته، ولكنه قال: "إن مؤلف هذا التفسير لا يمكن التأكد منه في هذا الموضع" (١).

الناصر العلوي [٢٢٥ - ٣٠٤ هـ / ٨٤٠ - ٩١٧ م]
الحسن بن على بن الحسن بن عمر بن

<span data-type="title" id=toc-368>علي بن الحسن العلوي الهاشمي، أبو (محمد)</span>
_________
(١) وفيات الأعيان ٢: ٩٤ وأعيان الشيعة ٢/ ٤: ١٨٣ والذريعة ٤: ٢٨٥ وابن الأثير ٧: ٢٧٤ وسفينة البحار ١: ٢٥٩ والأئمة الاثنى عشر ١١٣ ونزهة الجليس ٢: ١٢٠ وكشف الحجب ١٣٩ وتاريخ التراث العربي ١: ٢٠٧ ونور الأبصار ١٥٩ ومجلة المجمع العلمي العربي ٢٢: ٤٠٩ والأعلام ٢: ٢١٥ ومعجم المؤلفين ٣: ٢٦١ وفيه: له "كشف الحجب" في التفسير. والصلة بين التصوف والتشيع ٢: ٣٤.','1','142',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (263,'محمد: ثالث ملوك الدولة العلوية بطبرستان. شاعر، مشارك في التفسير والفقه والكلام والحديث والأدب والأخبار واللغة. ولد بالمدينة المنورة. ووفد على طبرستان في عهد مؤسس الدولة العلوية فيها، الحسن بن زيد، (٢٥٠ - ٢٧٠ هـ) فاستراب فيه الإمام، فخرج إلى نيسابور، فسجنه عاملها وضربه حتى أصيب بالصم، فلقب بالأطروش. وأطلق سراحه، فعاد إلى آمل. وسقطت طبرستان في أيدي السامانيين (سنة ٢٨٧ هـ) وقتل الإمام محمد بن زيد، ففر الأطروش إلى الديلم، وكان أهلها مجوسا، فقام بنشر الدعوة للدين الإسلامي، فأسلم منهم عدد وافر، ثم زحف بهم إلى طبرستان، فاستولى عليها سنة ٣٠١ هـ ودخل آبل سنة ٣٠٢ هـ، وتوفي بها. قال الطبري: "لم ير الناس مثل عدل الأطروش وحسن سيرته وإقامته الحق". من كتبه "تفسير القرآن" في مجلدين، احتج فيه بألف بيت من ألف قصيدة (١).
_________
(١) أعيان الشيعة ٢٢: ٢٨٨ وابن الأثير ٨: ٨١ وتراجم الرجال ١١ والدر الفاخر ٢٤٦ وروضات الجنات ١٦٧ وهدية العارفين ١: ٢٦٩ ومروج الذهب ٤: ٣٧٣ وتأسيس الشيعة ٣٣٧ والمختصر في تاريخ البشر ٢: ٧٣ وتاريخ الطبري حوادث سنة ٣٠٤ هـ. والسلوك ١: ٢٣ وتاريخ اليمن ٢٣ ودائرة المعارف الإسلامية ٢: ٣٠٩.','1','142',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (264,'الطوسي [٢٢٢ - ٣١٢ هـ / ٨٣٧ - ٩٢٤ م]

<span data-type="title" id=toc-369>الحسن بن علي بن نصر بن منصور الطوسي، أبو علي، الملقب بكردوش:</span>
محدث، حافظ، رحال، مولده ووفاته بطوس. حدث بقزوين. قال في الشذرات: "له تصانيف تدل على معرفته" وقال الحاكم: "تكلموا في روايته لكتاب الأنساب للزبير". من كتبه "نظم القرآن" (١).

المهذب الأسواني [.. - ٥٦١ هـ / .. - ١١٦٦ م]

<span data-type="title" id=toc-370>الحسن بن علي بن إبراهيم بن محمد بن الحسين بن الزبير الغساني الأسواني، صفي الدين، عميد الدولة، أبو محمد، الملقب بالقاضي المهذب: </span>"
شاعر، كاتب، نسابة، عارف بالتفسير، من أهل اسوان بصعيد مصر. ذكره صاحب الخريدة وأثنى عليه وقال: "لم يكن بمصر في زمنه أشعر منه". وقال قاضي القضاة ابن عين الدولة: "له تفسير في خمسين مجلدة، وقفت منها على نيف وثلاثين جزءا" (٢).
_________
(١) طبقات المفسرين ١: ١٣٨ وتذكرة الحفاظ ٣: ٧٨٧ والشذرات ٢: ٢٦٤ ولسان الميزان ٢: ٢٣٢ ومعجم المؤلفين ٣: ٢٦٤.
(٢) الخريدة قسم مصر ١: ٢٥٤ ومعجم الأدباء ٩: ٤٧ والطالع السعيد ١٠٠ وفوات الوفيات ١: ٣٣٧ =','1','143',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (265,'الياسري [.. - ٦٢٢ هـ / .. - ١٢٢٥ م]

<span data-type="title" id=toc-371>الحسن بن علي بن الحسن بن علي بن الحسن بن علي بن عمار، محي الدين، أبو علي الياسري، نسبة إلى عمار بن ياسر:</span>
مفسر، فرضي، خطيب، له شعر. من أهل بغداد. قال ابن كثير: "له مصنفات في "التفسير" و "الفرائض"، وله خطب ورسائل وأشعار حسنة، وكان مقبول الشهادة عند الحكام". مات بالموصل (١).

الهمذاني [.. - بعد ٥٠٠ هـ / .. - بعد ١١٠٧ م]

<span data-type="title" id=toc-372>الحسن بن الفتح بن حمزة بن الفتح، أبو القاسم الهمذاني:</span>
مفسر، فرضي، لغوي، أديب، من أهل همذان. استوطن بغداد في آخر عمره. قال السلفي: "كان من أهل الفضل والتقدم في الفرائض والتفسير والأداب واللغة والمعاني والبيان والكلام، وله "تفسير" حسن وشعر رائق" وقال ابن الصلاح: "رأيت مجلدين من تفسيره، واسمه "البديع في البيان عن
_________
= وأعيان الشيعة ٢٢: ١٨١ والنكت العصرية ٣٥ وخطط مبارك ٨: ٧٠ ومعجم المؤلفين ٣: ٢٤٧ وهدية العارفين ١: ٢٧٩ والأعلام ٢: ٢٢٥ وشذرات الذهب ٤: ١٩٧ وحسن المحاضرة ١: ٢٤٢ وطبقات المفسرين ١: ١٣٥ والروضتين ١: ١٤٧ والوافي ١٢: ١٤٧.
(١) البداية والنهاية ١٣: ١١١ ومعجم المؤلفين ٣: ٢٥١.','1','143',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (266,'غوامض القرآن" فوجدته ذا عناية بالعربية والكلام، ضعيف الفقه، مات بعد الخمسمائة" (١).

ابن أم قاسم [.. - ٧٤٩ هـ / .. - ١٣٤٨ م]

<span data-type="title" id=toc-373>حسن بن قاسم بن عبد الله بن علي المرادي، بدر الدين، أبو محمد، المعروف بابن أم قاسم:</span>
نحوي، مفسر، أصولي، مقرىء. من فقهاء المالكية. ولد بمصر. وأصله من مدينة آسفي بالمغرب الأقصى. توفي ودفن بسرياقوس بمصر. من كتبه "تفسير القرآن العظيم" في عشر مجلدات، و "إعراب القرآن" (٢).

ابن محبوب [١٤٩ - ٢٢٤ هـ / ٧٦٦ - ٨٣٩ م]

<span data-type="title" id=toc-374>الحسن بن محبوب السراد، أو الزراد، أبو علي:</span>
مفسر، فرضي، من فقهاء الشيعة الإمامية، من أهل الكوفة. من كتبه "تفسير القرآن" و "فضائل القرآن" (٣).
_________
(١) طبقات المفسرين للسيوطي ١٥ وطبقات المفسرين للداودي ١: ١٣٨ وكشف الظنون ٢٣٦ وهدية العارفين ١: ٢٢٨ والوافي ١٢: ٢٠٠.
(٢) غاية النهاية ١: ٢٢٧ والدرر الكامنة ٢: ١١٦ وطبقات الداودي ١: ٣٩ وبغية الوعاة ١: ٥١٧ وشذرات الذهب ٦: ١٦٠ وحسن المحاضرة ١: ٣٥٩ والأعلام ٢: ٢٢٨ وهدية العارفين ١: ٢٨٦ ومعجم المؤلفين ٣: ٢٧١ وروضات الجنات ٢٢٠.
(٣) طبقات المفسرين للداودي ١: ١٣٩ وفهرست =','1','144',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (267,'الزعفراني [.. - ٢٦٠ هـ / .. - ٨٧٤ م]

<span data-type="title" id=toc-375>الحسن بن محمد بن الصباح الزعفراني البغدادي، أبو علي:</span>
حافظ ثقة، فقيه كبير، مفسر، نسبته إلى الزعفرانية قرب بغداد. تفقه بالإمام الشافعي وحمل عنه قوله القديم. قال الماوردي: "هو أثبت رواة القديم". وقال أبو عاصم: "الكتاب العراقي منسوب إليه". روى عنه الجماعة إلا مسلما. وفي "الطبقات الكبرى" للسبكي: "أنه سكن بغداد في بعض دروبها فنسب الدرب إليه وصار يقال درب الزعفراني ببغداد". له "تفسير القرآن" ذكره الرافعي في "شرح مسند الشافعي" (١).
_________
= الطوسي ٤٦ وأعيان الشيعة ٢٣: ٥١ ولسان الميزان ٢: ٢٤٨ وهدية العارفين ١: ٢٦٦ والأعلام ٢: ٢٢٩ وابن النديم ٢٧٦ ومعجم المؤلفين ٣.: ٢٧٣.
(١) طبقات المفسرين للداودي ١: ١٤٤ وتاريخ بغداد ٧: ٤٠٧ والسبكي ٢: ١١٤ وتذكرة الحفاظ ٢: ٥٢٥ وتهذيب التهذيب ٢: ٣١٨ واللباب ٢: ٦٩ وروضات الجنات ٢١٤ وابن خلكان ٢: ٧٣ وكشف الظنون ٤٦٠ وابن النديم ٢٦٥ والشذرات ٢: ١٤٠ وطبقات الاسنوي ١٠ وطبقات الشيرازي ٨٢ ومرآة الجنان ٢: ١٧١ والانتقاء ١٠٥ وطبقات ابن هداية والأعلام ٢: ٢٣٠ والنجوم الزاهرة ٣: ٢٣ ومعجم المؤلفين ٣: ٢٨٤ ومعجم البلدان مادة الزعفرانية وطبقات العبادي ٢٣ والجرح والتعديل ١/ ٢: ٣٦ والمنتظم ٥: ٢٣ وطبقات الحنابلة ٩٧ وخلاصة تهذيب الكمال ٨٠ والوافي ١٢: ٢٣٥ والعبر ٢: ٢٠.','1','144',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (268,'النيسابوري [.. ٤٠٦ هـ / .. - ١٠١٦ م]

<span data-type="title" id=toc-376>الحسن بن محمد بن الحسن بن حبيب بن أيوب، أبو القاسم النيسابوري:</span>
إمام عصره في معاني القرآن وعلومه، أديب، نحوي، عارف بالمغازي والقصص والسير. من أهل نيسابور. قال عبد الغافر الفارسي: "هو أشهر مفسري خراسان، له "التفسير المشهور"، وانتشر عنه بنيسابور العلم الكثير". وله أيضا "كتاب التنزيل وترتيبه" مخطوط (١).

النابلسي [.. - ٧٧٢ هـ / .. - ١٣٧٠ م]

<span data-type="title" id=toc-377>الحسن بن محمد بن صالح بن محمد المجاور القرشي المطلبي، بدر الدين النابلسي:</span>
مفسر، مقرىء، من فقهاء الحنابلة. من أهل نابلس (بفلسطين).
_________
(١) طبقات المفسرين للسيوطي ١١ وطبقات المفسرين للداودي ١: ١٤٠ والشذرات ٣: ١٨١ وبغية الوعاة ١: ٥١٩ والعبر ٣: ٩٣ وهدية العارفين ١: ٢٧٤ وفيه اسم كتابه في التفسير "غرائب القرآن" رغائب الفرقان: ومثله في معجم المؤلفين ٣: ٢٧٨ وهو يقل عنه. وفي كشف الظنون ٤٦٠ و ١١٩٥ إن غرائب القرآن هو لحسن بن محمد بن حسين القمي النيسابوري المتوفي بعد سنة ٧٢٨ هـ. وهو ما ذكره صاحب هدية العارفين (٢٨٣١) في ترجمته للقمي المذكور. وانظر أيضا الذريعة ٢٥٦ وأعيان الشيعة ٢٣: ١١٢ وروضات الجنات ٢٢٥. وتاريخ التراث العربي ١: ٢١٨ والوافي ١٢: ٢٣٩.','1','145',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (269,'سمع بها وبالقاهرة والإسكندرية ودمشق، وولي إفتاء دار العدل بالقاهرة. وهو أحد شيوخ ابن الجزري صاحب غاية النهاية (١).

ابن يعيش [.. - ٧٩١ هـ / .. - ١٣٨٩ م]

<span data-type="title" id=toc-378>الحسن بن محمد بن الحسن بن سابق الدين، ابن يعيش الصنعاني، المعروف بالنحوي:</span>
عالم الزيدية في زمانه وشيخ شيوخهم وناشر علومهم، من أهل صنعاء، ولي قضاءها إلى أن مات. قال صاحب "البدر الطالع": له "تفسير"، وسماه صاحب "تراجم الرجال": "التيسير في التفسير" (٢).

النظام النيسابوري [.. - بعد ٨٥٠ هـ / .. - بعد ١٤٤٦ م]

<span data-type="title" id=toc-379>الحسن بن محمد بن الحسن القمي النيسابوري، نظام الدين، ويقال له الأعرج:</span>
مفسر، من كبار علماء الشيعة الإمامية في عصره. أصله من مدينة "قم"
_________
(١) طبقات المفسرين للداودي ١: ١٤٤ والدرر الكامنة ٢: ١٢١ والشذرات ٦: ٢٢٣ وغية النهاية ١: ٢٣١ والأعلام ٢: ٢٣٤.
(٢) البدر الطالع ١: ٢١ وتراجم الرجال ١١ والأعلام ٢: ٢٣٤ ومعجم المؤلفين ٣: ٢٨٠.','1','145',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (270,'ونشأ وأقام في نيسابور. من كتبه "غرائب القرآن ورغائب الفرقان" طبع، في ثلاثة مجلدات، يعرف بتفسير النيسابوري، ألفه سنة ٨٢٨ هـ. و "لب التأويل" طبع، و "أوقاف القرآن" طبع (١).

الأستراباذي [.. - ٨٩١ هـ / .. - ١٤٨٦]

<span data-type="title" id=toc-380>حسن بن محمد بن الحسن، كمال الدين، ويقال جمال الدين، الأستراباذي:</span>
فقيه إمامي، متكلم، عارف بالتفسير، من أهل استراباذ (مدينة في شمالي طهران) وبها نشأ وتعلم، ثم استوطن مدينة النجف بالعراق. أثنى عليه العاملي (٢). البوريني ٩٦٣ - ١٠٢٤ هـ / ١٥٥٦ - ١٦١٥ م]

<span data-type="title" id=toc-381>الحسن بن محمد بن محمد بن حسن بن عمر بن عبد الرحمن الصفوري البوريني، (بدر الدين)</span>
_________
(١) معجم المطبوعات ١٥٢٧ وهدية العارفين ١: ٢٨٣ وروضات الجنات ٢٢٥ واعيان الشيعة ٢٣: ١١٢ والذريعة ٤: ٢٠٦ وكشف الظنون ٤٦٠ و ١١٩٥ والأعلام ٢: ٢٨١ والمخطوطات المطبوعة ٢: ١٢٠ ومعجم المؤلفين ٣: ٢٨١ وتعليقات العلامة آغا بزرك الطهراني على كشف الظنون، وذيل كشف الظنون: ٦٧.
(٢) أعيان الشيعة ٢٣: ٨٩ ومعجم المؤلفين ٣: ٢٧٨.','1','146',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (271,'بدر الدين: مؤرخ، عالم بالتفسير والحديث والفقه والأدب. ولد في صفورية (قرية في فلسطين شمال غربي الناصرة) ونسبته إلى بورين (من قرى نابلس) ولد بها أبوه فلزمته النسبة. انتقل صغيرا إلى دمشق (حوالي سنة ٩٧٤ هـ) وتعلم بها وبالقدس. ثم اشتغل بالتدريس في بعض مدارس دمشق، وتوفي بها. من كتبه "حاشية" على أنوار التنزيل في التفسير للبيضاوي (١).

البحراني [.. - ١١٩٣ هـ / .. - ١٧٧٩ م]

<span data-type="title" id=toc-382>أبو الحسن بن محمد البحراني الأصل، الشيرازي المسكن:</span>
مفسر، أديب، شاعر، من فقهاء الإمامية. أصله من البحرين، استوطن شيراز وتوفي بها. من آثاره "تفسير كبيرر" (٢).

الشيرواني [.. - حيا ١٣٠١ هـ / .. - حيا ١٨٨٤ م]

<span data-type="title" id=toc-383>أبو الحسن بن محمد الحسيني، الشيرواني:</span>
مفسر، من فقهاء الإمامية.
_________
(١) خلاصة الأثر ٢: ٥١ وريحانة الألبا ١٧ والروض العاطر ٢: ٢٨٩ وحديقة الأفراح ١٢٨ وهدية العارفين ١: ٢٩١ وإيضاح المكنون ١: ١٣٩ وبروكلمان ٢: ٢٩ وتاريخ آداب اللغة العربية ٣: ٣١٥ ودائرة المعارف الإسلامية ٤: ٢٨٨ والأعلام ٢: ٢٣٦.
(٢) أعيان الشيعة ١٤: ٥٤٢ ومعجم المؤلفين ٣: ٧٢٧٧.','1','146',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (272,'نسبته إلى شيروان، قرية ببخاري. له "مصباح المفسرين" (١). البعقيلي ١٣٠١ - ١٣٦٨ هـ / ١٨٨٣ - ١٩٤٩ م]

<span data-type="title" id=toc-384>الحسن - ويقال الاحسن - بن محمد بن بوجمعة البيضاوي البعقيلي:</span>
فقيه، متصوف، أصولي، مفسر، مشارك، أصله من بعقيلة في سوس بالمغرب الأقصى، تعلم بها ثم بفاس. واستقر في الدار البيضاء (سنة ١٣٤٨ هـ) إلى أن توفي. له كتاب في "تفسير القرآن" طبع (٢).

السقا [١٢٦٢ - ١٣٢٦ هـ / ١٨٤٦ - ١٩٠٨ م]

<span data-type="title" id=toc-385>حسن بن محمد بن حسن، المعروف بالسقا:</span>
من علماء الشافعية ومدرسي وخطباء الأزهر. مولده ووفاته بمصر. له رسائل في التفسير منها "فتح الجواد الكريم فيما يتعلق ببِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ" في البسملة (٣).
_________
(١) أعلام الشيعة ٩: ٤٤ ومعجم المؤلفين ٣: ٢٨٢.
(٢) دليل مؤرخ المغرب ٨٨ وسوس العالمة ٢٠٨ و ٢١٨ والمعسول ١١: ١٥٥ والأعلام ٢: ٢٢١
(٣) معجم المطبوعات ١٠٣١ وكنز الجوهر في تاريخ الأزهر، والأعلام الشرقية ٢: ٩٦ والاعلام ٢: ٢٣٧ ومعجم المؤلفين ٣: ٢٨٠.','1','147',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (273,'الحسن بن مسلم [القرن ٣ هـ / القرن ٩ م]

<span data-type="title" id=toc-386>الحسن بن مسلم بن سفيان، أبو علي:</span>
مقرىء معروف، مفسر، من أهل البصرة. روى القراءة عن جماعة من كبار القراء، منهم زيد بن أحمد بن يعقوب المتوفي بعد سنة ٢٠٥ هـ. ذكره ابن الجزري ولم يؤرخ مولده ولا وفاته (١).

ابن الجرموزي [١٠٤٤ - ١١٠ هـ / ١٦٣٤ - ١٦٨٩ م]

<span data-type="title" id=toc-387>الحسن بن مطهر بن محمد بن أحمد بن عبد الله بن محمد الحسني الجرموزي:</span>
وال، عالم بالتفسير والحديث والفقه والنحو والبيان والمنطق، من أهل عتمة باليمن. اتصل بالمتوكل على الله إسماعيل بن القاسم ابن محمد، وتنقل في الولايات، فكان والي "حراز" ثم "بندر المخا". قال الشوكاني: "مدحه أعيان شعراء اليمن والبحرين وعمان، وعظمت رياسته وطار صيته ونال من العز ما لم يكن له في حساب، ومات في صنعاء بعد أن تغيرت به الأحوال" (٢).
_________
(١) طبقات المفسرين للداودي ١: ١٤٠ وغاية النهاية ١: ٢٣٣.
(٢) البدر الطالع ١: ٢١٠ ونشر العرف ١: ٥٠٥ وهدية العارفين ١: ٢٩٦ والأعلام ٢: ٢٣٨ ومعجم المؤلفين ٣: ٢٩٥.','1','147',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (274,'النوبختي [.. - ٣١٠ هـ / .. - ٩٢٢ م]

<span data-type="title" id=toc-388>الحسن بن موسى بن الحسن بن محمد النوبختي، أبو محمد:</span>
فلكي، عرف بنزعته إلى الفلسفة اليونانية. كان يجتمع إليه جماعة من النقلة مثل أبي عثمان الدمشقي وإسحاق بن ثابت وغيرهما. وكانت تدعيه المعتزلة والشيعة، وهو من أهل بغداد. نسبته إلى جده نوبخت، مؤسس الأسرة وأول منجم نال حظوة لدى المنصور العباسي. من كتب صاحب الترجمة "كتاب التنزيه وذكر متشابه القرآن" (١).

الحسن البصري [٢١ - ١١٠ هـ / ٦٤٢ - ٧٢٨ م]

<span data-type="title" id=toc-389>الحسن بن يسار البصري، أبو سعيد:</span>
من سادات التابعين وكبرائهم، كان إمام أهل البصرة وحبر الأمة في زمنه، اشتهر بتقواه وأثر تأثيرا عميقا على الفكر الإسلامي وحركة التصوف. ولد بالمدينة ورأى بعض الصحابة وسمع من بعضهم، ثم سكن البصرة وولي قضاءها لعمر بن عبد العزيز،
_________
(١) ابن النديم ٢٢٥ ولسان الميزان ٢: ٢٥٨ وهدية العارفين ١: ٢٦٨ وإيضاح المكنون ٢٨٣ واعيان الشيعة ٢٣: ٣٣٣ وفهرست الطوسي ٤٦ وتنقيح المقال ١: ٣١١ وكتاب الرجال ٤٦ ومجلة لغة العرب ٩: ٧٨٤ واللباب ٣: ٣٢٨.','1','148',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (275,'ثم استعفى. وفيها عرف بزهده وعلمه وفضله. تخرج عليه عمرو بن عبيد (٨٠ - ١٤٤ هـ) وواصل بن عطاء (٨٥ - ١٣١ هـ) من أئمة المعتزلة، ولهذا يعدونه واحدا منهم. قال الغزالي: "كان الحسن البصري أشبه الناس كلاما بكلام الأنبياء، وأقربهم هديا من الصحابة. وكان غاية في الفصاحة، تتصبب الحكمة من فيه .. " أخرج له الجماعة. له "تفسير" يعد من أشهر التفاسير القديمة، وكان راويته المشهور هو عمرو بن عبيد. قال صاحب تاريخ التراث العربي "ولقد استخدم الثعلبي في كتابه الكشف والبيان هذا التفسير، وتوجد بقايا منه في تاريخ الطبري، وبقايا أخرى كثيرة في كتب التفسير". وله أيضا "نزول القرآن" وكتاب "العدد في القرآن" (١).
_________
(١) الحسن البصري للدكور احسان عباس. وطبقات المفسرين للداودي ١: ١٤٧ وحلية الأولياء٢: ١٣١ وميزان الاعتدال ١: ٥٢٧ وغاية النهاية ١: ١٣٥ وتذكرة الحفاظ ٧١ وابن خلكان ٢: ٦٩ وتهذيب التهذيب ٢: ٢٦٣ وأمالي المرتضى ١: ١٠٦ وطبقات ابن سعد ٧: ١٥٦ وذيل المذيل ٩٣ والنثر الفني لزكي مبارك ١: ٦١ والمحبر ٢٣٥ وطبقات الفقهاء ٦٨ والجرح والتعديل ١/ ٢: ٤٠ والمعارف ٢٢٥ والمعتزلة لابن المرتضى ١٨ والأعلام ٢: ٢٤٢ وتاريخ التراث العربي ١: ١٨٧ و ٢: ٣٥١ وهدية العارفين ١: ٢٦٥ وذكر أخبار أصبهان ١: ٢٠٤ والوافي ١٢: ٣٠٦ وشذرات الذهب ١: ١٣١ وخلاصة تهذيب الكمال ٧٧ والجرح والتعديل ١/ ٢: ٤٠ وروضات الجنات ٢٠٧.','1','148',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (276,'العلامة الحلي [٦٤٨ - ٧٢٦ هـ / ١٢٥٠ - ١٣٥٢ م]

<span data-type="title" id=toc-390>الحسن بن يوسف بن علي بن محمد بن المطهر، جمال الدين، أبو منصور، الشهير بالعلامة الحلي:</span>
انتهت إليه زعامة الشيعة الإمامية في عصره في المعقول والمنقول والفروع والأصول. ولد في الحلة، وبها أخذ عن كبار العلماء. ثم اشتغل بالتصنيف والتدريس، وكان مجلس تدريسه حافلا يغص بعشرات العلماء الذين أخذوا عنه وتخرجوا به. توفي بالحلة. من كتبه "السر الوجيز في تفسير الكتاب العزيز" أو "القول الوجيز ... " أو "التفسير الوجيز ... ". منه نسخة من أول سورة الفاتحة إلى آخر سورة البقرة، وهو المجلد الأول. و "نهج الإيمان في تفسير القرآن" (١).
_________
(١) أعيان الشيعة ٢٤: ٢٧٧ وتنقيح المقال ١: ٣١٤ وروضات الجنات ١٧١ وشعراء الحلة ٢: ٨٨ ومنهج المقال ٩٩ والنجوم الزاهرة ٩: ٢٦٧ وأمل الأمل ٢: ٨١ والدرر الكامنة ٢: ١٣٥ و ١٥٨ (ذكره في الحسن والحسين) ولسان الميزان ٢: ٣١٧ وسماه الحسين، والذريعة ٢: ٤٥ وفي أمكنة متعددة، والرجال لابن داود ١١٩ ولؤلؤة البحرين ٢١٥ وابن الوردي ٢: ٢٧٩ ومرآة الجنان ٤: ٢٧٦ والبداية والنهاية ١٤: ١٢٥ وذيل دول الإسلام ٢: ١٨١ وخلاصة الأقوال في معرفة الرجال ٤٥ وسفينة البحار ١: ٧٣٤ ودلائل الصدق ١: ٣٢٢ وهدية العارفين ١: ٢٨٤٠ ومعجم المؤلفين ٣: ٣٠٣ والأعلام ٢: ٢٤٤.','1','149',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (277,'السجزي [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-391>الحسين بن إبراهيم، أبو منصور، الغواص، المنصوري، السجزي:</span>
ذكره صاحب كشف الظنون وقال: له "عيون التفاسير بحذف التكارير"، ولم يزد. ولم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال (١).

البيطار [... - ٣٦٣ هـ / .. - ٩٧٤ م]

<span data-type="title" id=toc-392>الحسين بن أحمد، أبو عبد الله، المعروف بالبيطار:</span>
عالم بالتفسير والحديث والقراءات والفقه والتصوف والآداب والطب. رحل إلى الحجاز والعراق. له تصانيف (٢).

ابن خالويه [.. - ٣٧٠ هـ / .. - ٩٨٠ م]

<span data-type="title" id=toc-393>الحسين بن أحمد بن خالويه بن حمدان، أبو عبد الله، الهمذاني:</span>
نحوي، لغوي، أصله من همذان. دخل بغداد (سنة ٣١٤ هـ) ودرس النحو واللغة على السيرافي وابن دريد ونفطويه وأبي بكر الانباري. وزار اليمن وأقام بذمار، مدة، ثم انتقل إلى
_________
(١) كشف الظنون ١١٨٥ وهدية العارفين ١:
(٢) شد الازار ١٠٢.','1','149',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (278,'الشام فسكن حلب، وصحب سيف الدولة، وأدب بعض أولاده، وعاش بعده في صحبة ولده سعد الدولة وغيره من آل حمدان، وتصدر بحلب وميافارقن وحمص للإفادة والتصنيف. وكانت له مع المتنبي مناظرات عند سيف الدولة. توفي بحلب. من كتبه "إعراب ثلاثين سورة من القرآن العزيز" طبع بمصر سنة ١٣٦٥ هـ. و "مختصر في شواذ القرآن" (١).

الزوزني [.. - ٤٨٦ هـ / .. - ١٠٩٣ م]

<span data-type="title" id=toc-394>حسين بن أحمد بن حسين الزوزني، أبو عبد الله:</span>
إمام عصره في النحو واللغة والعربية. من أهل زوزن - بين هراة
_________
(١) طبقات المفسرين للداودي ١: ١٤٨ ولسان الميزان ٢: ٢٦٧ والنجوم الزاهرة ٤: ١٣٩ وإنباه الرواة ١: ٣٢٤ والسبكي ٣: ٢٦٩ وابن كثير ١١: ٢٦٧ وبغية الوعاة ١: ٥٢٩ ونزهة الالباء٣١١ ومرآة الجنان ٢: ٣٩٤ ويتيمة الدهر ١: ١٢٣ والعبر ٢: ٣٥٦ وغاية النهاية ١: ٢٣٧ والشذرات ٣: ٧١ ومعجم الأدباء ٩: ٢٠٥ وتنقيح المقال ١: ٣٢٧ وروضات الجنات ٢٣٧ ومنتهى المقال ١١٩ وأعيان الشيعة ٢٥: ٤٨ والنجاشي ٥٠ وابن خلكان ٢: ١٧٨ وتلخيص ابن مكتوم ٦٢ وطبقات ابن قاضي شهبة ١: ٣١٧ والفلاكة والمفلوكين والمزهر ٢: ٤٢١ وهدية العارفين ١: ٣٠٦ وكشف الظنون ١: ١٢٣ وتاريخ آداب اللغة العرية ٢: ٣٤٧ ودائرة المعارف الإسلامية ١: ١٤٨ وإعلام النبلاء ٤: ٥٤ ومنجد الأعلام ٢٠١ والفهرست لابن النديم ٩٢ والأعلام ٢: ٢٤٨ ومعجم المؤلفين ٣: ٣١٠ والموسوعة العربية الميسرة والوافي ١٢: ٣٢٣.','1','150',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (279,'ونيسابور -. من كتبه "ترجمان القرآن" بالعربية والفارسية، مخطوط (١).

الكيلاني [٨٤٢ - ٨٨٩ هـ / ١٤٣٨ - ١٤٨٤ م]

<span data-type="title" id=toc-395>حسين بن أحمد بن محمد بن أحمد الكيلاني، ثم المكي، بدر الدين، المعروف بابن قاوان:</span>
صوفي، عالم بالأصول والنحو والتفسير. من أهل كيلان، وبها نشأ وتعلم. ثم رحل فسمع بنيسابور ومكة والشام ومصر. لقيه السخاوي وأثنى عليه. مات بمكة. من كتبه "حاشية على خطبة تفسير البيضاوي" (٢).

القوصي [.. - ٦٨٣ هـ / .. - ١٢٨٣ م]

<span data-type="title" id=toc-396>الحسين بن أبي بكر بن عياض بن موسى السبتي، ثم القوصي، وينعت بالمعين:</span>
_________
(١) تاريخ الأدب في إيران من الفردوسي إلى السعدي ٤٤٩ وفيه وفاته سنة ٤٨٦ هـ. ومثله في بغية الوعاة ١: ٥٣١ نقلا عن تاريخ نيسابور لعبد الغافر الفارسي. وهدية العارفين ١: ٣١٠ وإيضاح المكنون ٢: ٣٣٢ وإنباه الرواة ١: ٣٢٠ وفيه: كان هذا الشيخ موجودا في المائة السادسة" وبروكلمان: الذيل ١: ٥٠٥ وهو فيه الحسين بن علي بن أحمد. ومثله في تاريخ آداب اللغة العربية ٢: ٤٦ وكشف الظنون ١٧٤١ وهو فيه الحسين بن أحمد و ١٧٠٣ وهو فيه محمد بن أحمد، وقد صححه محقق الكشف.
(٢) الضوء اللامع ٣: ١٣٥ وإيضاح المكنون ١: ١٣١ وهدية العارفين ١: ٣١٦ ومعجم المؤلفين ٣: ٣١٢.','1','150',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (280,'فقيه شافعه، أصولي، عارف بالتفسير، أصله من سبتة، ولد ونشأ بقوص. ودرس بأسوان وأقام بها إلى أن توفي. من كتبه "مختصر" الكشف والبيان في تفسير القرآن للثعلبي. (١).

الكندي [٦٥٤ - ٧٤١ هـ / ١٢٥٦ - ١٣٤٠ م]

<span data-type="title" id=toc-397>أبو الحسين بن أبي بكر بن الحسين، عماد الدين الكندي:</span>
مفسر، نحوي، محدث، من فقهاء المالكية. من أهل الإسكندرية مولدا ووفاة، وولي قضاءها. سمع من شرف الدين الدمياطي، وحدث وأفتى ودرس، ونعت بقاضي القضاة، وكان شيخ العلماء في وقته. انفرد صاحب كشف الظنون بالقول أنه استوطن غرناطة بالأندلس. من آثاره "الكفيل بمعاني التنزيل" مخطوط بخطه في ٢١ مجلدا بدار الكتب المصرية. قال صاحب كشف الظنون: "وهو تفسير ضخم، في ثلاث وعشرين مجلدة كبيرة. وطريقته فيه أن يتلو الآية أو الآيات فإذا فرغ منها قال: "قال الزمخشري، ويسوق كلامه، فإذا انتهى اتبعه بما عليه من مناقشة وما يحتاج إليه من توجيه وما يكون هناك من الزيادات الواقعة في غير الكشاف من التفاسير، وأكثر نظره
_________
(١) الطالع السيعد ٢٢١ ومعجم المؤلفين ٣: ٣١٧.','1','151',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (281,'فيه في النحو، فإنه كان متقدما في معرفته" (١).

التفليسي [.. - حيا ١١٩٧ هـ / .. - حيا ١٧٨٣ م]

<span data-type="title" id=toc-398>حسين التفليسي:</span>
فقيه إمامي، مفسر، نسبته إلى تفليس آخر بلدة من بلاد أذربيجان مما يلي الثغر. كان معاصرا لمحمد ابن محمد رفيع الجيلاني المتوفي سنة ١١٩٧ هـ. مات بأصفهان. له "تفسير القرآن" (٢).

المجتهد الموسوي [.. - ١٠٠١ هـ / .. - ١٥٩٣ م]

<span data-type="title" id=toc-399>حسين بن الحسن بن محمد الموسوي الكرير العاملي:</span>
من علماء الشيعة الإمامية، سكن قزوين زمانا، وارتحل إلى أردبيل فكان شيخ الإسلام فيها. وعاد إلى قزوين فمات بها مطعونا، ونقل جثمانه إلى النجف بالعراق فدفن فيها. له "تفسير" قوله تعالى: {أحل لكم الطيبات وطعام الذين أوتوا الكتاب} (٣).
_________
(١) الديباج المذهب ١٠٠ والدرر الكامنة ٢: ١٦١ وطبقات المفسرين ١: ١٦١ وحسن المحاضرة ١: ٤٥٩ وبغية الوعاة ١: ٥٣٢ وكشف الظنون ٤٤٢ وشذرات الذهب ٦: ١٣٠ وهدية العارفين ١: ٣١٤ ومعجم المؤلفين ٣: ٣١٦ واسمه في بعض هذه المصادر: الحسين بن أبي بكر. وانظر الأزهرية ٤: ٢٩٦.
(٢) اعيان الشيعة ٢٦: ٢٠١ ومعجم المؤلفين ٣: ٣١٧.
(٣) أمل الآمل ١: ٦٩ وهدية العارفين ١: ٣٢٠ =','1','151',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (282,'الخلخالي [.. - ١٠١٤ هـ / .. - ١٦٠٥ م]

<span data-type="title" id=toc-400>حسين بن حسن الحسيني الخلخالي:</span>
مفسر، نحوي، منطقه، فلكي، نسبته إلى خلخالة - قرية في سورية جنوبي دمشق - قال المحبي: "له مؤلفات كثيرة منها "حاشية" على حاشية العصام على تفسير البيضاوي"، مخطوطة (١).

الميصيصي [.. - ٢٢٦ هـ / .. - ٨٤١ م]

<span data-type="title" id=toc-401>الحسن بن داود المصيصي، أبو علي، ولقبه سنيد، وبه اشتهر:</span>
حافظ، محدث، مفسر، قال ابن ناصر الدين: "أحد أوعية العلم والأثر، تكلم فيه أحمد وغيره، ووثقه ابن حبان والخطيب البغدادي". له "تفسير" رواه عنه محمد ابن إسماعيل الصائغ (٢). وأعيان الشيعة ٢٥: ٢٧٥ وروضات الجنات ٢: ١٩ والأعلام ٢ ومعجم المؤلفين ٤: ٣.
_________
(١) خلاصة الأثر ٢: ١٢٢ والتحرير الوجيز ١٧ وهدية العارفين ١: ٢٩١ و ٣٢١ وكشف الظنون ١٩٢ ومعجم المؤلفين ٣: ٣١٩.
(٢) تذكرة الحفاظ ٢: ٤٥٩ وخلاصة تهذيب الكمال ١٣٧ وميزان الاعتدال ٢: ٢٣٦ وتهذيب التهذيب ٤: ٢٤٤ وطبقات المفسرين للداودي ١: ٢٥٩ وهو في هذه المراجع سنيد بن داود. والشذرات ٢: ٥٩ وهدية العارفين ١: ١٠٤ والنسخ في القرآن الكريم ١: ٢٥١ وتاريخ بغداد ٨: ٤٢.','1','152',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (283,'البروجردي [١٢٣٨ - ١٢٦٨ هـ / ١٨٢٣ - ١٨٥٢ م]

<span data-type="title" id=toc-402>حسين بن رضا البروجردي:</span>
أديب، أصولي، متكلم، مفسر، من فقهاء الشيعة الإمامية. نسبته إلى بروجرد، قرية من بلاد الجبل على ثمانية عشر فرسخا من همدان. من كتبه "تفسير القرآن" منه مجلد كبير في مقدمات التفسير وتفسير الفاتحة ونحو الثلث من سورة البقرة" (١). التنبي [القرن العاشر الميلادي]

<span data-type="title" id=toc-403>الحسن ين زيد الحلبي التنبي:</span>
مفسر، من أهل تنب، من قرى حلب. ذكره الداودي في طبقات المفسرين وقال: "يروي عنه أحمد بن طاهر المقرىء" (٢).

الفارسي [.. - ٤٥٧ هـ / .. - ١٠٦٥ م]

<span data-type="title" id=toc-404>الحسين بن زيد بن علي بن خديجة الفارسي، الإمام المظفر:</span>
عارف
_________
(١) أحسن الوديعة ٥٥ ووفاته فيه سنة ١٢٦٨ هـ، وأعيان الشيعة ٢٦: ٥٧ وفيه: مولده سنة ١٢٢٨ وقيل ١٢٣٨ ووفاته سنة ١٢٧٦ وقيل ١٢٧٧ هـ و ٢٧: ١٩١ وهو فيه حسين بن محمد رضا ومولده سنة ١٢٣٨ ووفاته سنة ١٢٨٤ هـ. وأعلام الشيعة ٢: ٣٩١ وهو فيه حسين بن محمد رضا ومولده سنة ١٢٢٨ ووفاته سنة ١٢٧٧ هـ. والأعلام ٢: ٢٥٦ وهو ينقل عن أحسن الوديعة. ومعجم المؤلفين ٤: ٧ و ٥٠ وبروكلمان، الذيل: ٢: ٨٣٠.
(٢) طبقات المفسرين للداودي ١: ١٤٩ وتبصير المنتبه ١: ٢١١.','1','152',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (284,'بالتفسير، من آثاره "كتاب" جامع ما في القرآن الكريم من الآيات الكريمة الناسخة والمنسوخة" (١).

الزنجاني [.. - نحو ١٣٢٢ هـ / .. - نحو ١٩٠٤ م]

<span data-type="title" id=toc-405>الحسين السجاسي الزنجاني:</span>
مفسر، فقيه إمامي، أصولي، نسبته إلى زنجان، مدينة في إيران الشمالية. له "تفسير سورة الزمر" في مجلد (٢).

الأهوازي [.. - بعد ٣٠٠ هـ / .. - بعد ٩١٣ م]

<span data-type="title" id=toc-406>الحسين بن سعيد بن حماد بن سعيد الأهوازي:</span>
أوسع أهل زمانه علما بالفقه والآثار والمناقب وغير ذلك من علوم الشيعة، من أهل الكوفة، رحل إلى إيران واستوطن قم. ذكره الطوسي والكشي في الرواة عن علي الرضا. من كتبه "تفسير القرآن" (٣).
_________
(١) بروكلمان، الذيل ١: ٧٢٢ ومعجم المؤلفين ٤: ٩.
(٢) اعيان الشيعة ٢٦: ٩٥ ومعجم المؤلفين ٤: ٩.
(٣) الفهرست ٢٧٧ ولسان الميزان ٢: ٨٤ وطبقات الفسرين للداودي ١: ١٥٠ وفهرست الطوسي ٩٦ وأعيان الشيعة ٢٦: ٩٨ وتنقيح المقال ١: ٣٢٨ وهدية العارفين ١: ٣٣١ وفيه: توفي في حدود سنة ٢٧٥ هـ.','1','153',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (285,'ابن جاندار [١٠١٢ - ١٠٧٦ هـ / ١٦٠٣ - ١٦٦٥ م]

<span data-type="title" id=toc-407>حسين بن شهاب الدين بن حسين بن جاندار البقاعي الكركي العاملى:</span>
أديب، شاعر، عالم، كان متكلما حكيما، سكن أصفهان مدة، ثم انتقل إلى حيدر آباد فأقام إلى أن توفي فيها. قال العاملي: "الظاهر أن المراد بالحكيم الطبيب لوجود تأليف له بالطب واشتغاله به في آخر عمره، ولو أريد الحكمة العقلية لأغنى عنه وصفه بالمتكلم". من كتبه "حاشية" على تفسير البيضاوي (١).

الرومي [.. - ١١٠٥ هـ / .. - ١٦٩٤ م]

<span data-type="title" id=toc-408>حسين بن عباس الرومي، القسطنطيني:</span>
من كبار الوعاظ، عارف بالتفسير، من فقهاء الحنفية. من أهل القسطنطينية، كان يعظ بجامع الحاج أوحد. من آثاره "مجمع التفاسير" مجالس في الوعظ (٢).
_________
(١) سلافة العصر ٣٥٥ وخلاصة الأثر ٢: ٩٠ وهدية العارفين ١: ٣٢٧ ومعجم أدباء الأطباء ٩٧٢ وأمل الآمل ١: ٧٠ والأعلام ٢: ٢٥٤ وإيضاح المكنون ٣٨٧ ومعجم المؤلفين ٤: ١٢ وأعيان الشيعة. وهو في هدية العارفين وأمل الآمل: حسين بن شهاب الدين بن حسين بن محمد بن حيدر العاملي الكركي الحكيم.
(٢) هدية العارفين ١: ٣٢٤ وإيضاح المكنون ٤٣٤.','1','153',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (286,'الأردبيلي [.. - ٩٥٠ هـ / .. - ١٥٤٣ م]

<span data-type="title" id=toc-409>حسين بن عبد الحق، كمال الدين، الأردبيلي:</span>
من كبار فقهاء الشيعة الإمامية في المعقول والمنقول، عارف بالرياضيات والفلك والطب. كان في عصر الشاه إسماعيل الصفوي. درس في شيراز وهراة. مولده ووفاته بهراة. من كتبه "تفسير القرآن" في مجلد ويعرف بالتفسير الإلهي، وله تفسير بالفارسية، ويقع في مجلدين (١).

الحارثي [٩١٨ - ٩٨٤ هـ / ١٥١٢ - ١٥٧٦ م]

<span data-type="title" id=toc-410>حسين بن عبد الصمد بن محمد الجبعي العاملي الحارثي الهمداني، عز الدين:</span>
من أعيان فقهاء الشيعة الإمامية، عالم بالتفسير والحديث والأصول والكلام والأدب، له نظم حسن. أصله من جبع في جبل عامل (بلبنان). انتقل إلى أصفهان في عهد الشاه طهماسب الصفوي، فولاه منصب مشيخة الإسلام بقزوين، واستمر في ذلك سبع سنين. ثم توجه إلى خراسان وأقام بهراة وكان شيخ الإسلام بها. وحج، وجاور بمكة، ثم أقام بالبحرين إلى أن
_________
(١) أعيان الشيعة ٢٦: ٢٥٤ وروضات الجنات ١٨٥ وفلاسفة الشيعة ٢٥٣ والذريعة ٤: ٢٦١ وكشف الظنون ٤٤١ ومعجم المؤلفين ٣: ٣١٤ و ٤: ١٤.','1','154',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (287,'توفي. وهو والد بهاء الدين العاملي صاحب الكشكول (١).
حسين بن أبي العز = المنتجب بن أبي العز

ابن الناظر [٦٠٣ - ٦٧٩ هـ / ١٢٠٧ - ١٢٨٠ م]

<span data-type="title" id=toc-411>الحسين بن عبد العزيز بن محمد بن أبي الأحوص، القرشي الفهري، أبو علي، المعروف بابن الناظر:</span>
قاض، عالم بالتفسير والحديث والقراءات والفقه والنحو. أصله من بلنسية، ولد بجيان - وسكن غرناطة، ثم مالقة، واستقر بهذه بضعا وعشرين سنة، مقرئا ومحدثا. وجرت فتنة بمالقة، ففر إلى غرناطة، فولي قضاء المرية، فقضاء بسطة، فقضاء مالقة، وتوفي بها وقد نحي عن القضاء (٢).

ابن سينا [٣٧٠ - ٤٢٨ هـ /٩٨٠ - ١٠٣٧ م]

<span data-type="title" id=toc-412>الحسين بن عبد الله بن الحسين بن علي (ابن سينا، أبو علي، شرف الملك)</span>
_________
(١) أمل الآمل ١: ٧٤ وأعيان الشيعة ٢٦: ٢٢٦ وتنقيح المقال ١: ٣٣٢ وروضات الجنات ٢: ٢٥ وزيدان ٣: ٣٠٥ وهدية العارفين ١: ٣٢٠. ولؤلؤة البحرين. والذريعة. والأعلام ٢: ٢٦٥.
(٢) غاية النهاية ١: ٢٤٢ وبغية الوعاة ١: ٥٣٥ وطبقات المفسرين للداودي ١: ١٥٠ وروضات الجنات ٢٥٦ وهدية العارفين ١: ٣٠٣ وقضاة الأندلس ١٢٧ وهو في الهدية الحسين بن عبد الله.','1','154',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (288,'ابن سينا، أبو علي، شرف الملك: يلقب بالشيخ الرئيس، فيلسوف، طبيب، من كبار فلاسفة المسلمين وأطبائهم. أصله من بلخ، ولد في أفشنة قرب بخارى، ونشأ وتعلم في بخارى، فدرس العلوم الشرعية والعقلية، وأصبح حجة في الطب والفلك والرياضيات والفلسفة، ولما يبلغ العشرين. ثم طاف البلاد، وناظر العلماء، واتسعت شهرته واتصل بشمس الدولة بن فخر الدولة البويهي حاكم همذان وكرمانشاه، فاستوزره. وثار عليه عسكر همذان، ونهبوا بيته، فتوارى، ثم دخل أصفهان، وصنف بها أكثر كتبه. وعاد في أواخر أيامه إلى همذان، فتوفي بها. له تصانيف كثيرة يربو عددها على المئتين، بين كتب ورسائل، وهي في فروع الأدب والعلم والحكمة والطب والدين والسياسة. منها "تفسير سورتي المعوذتين" و "تفسير آية النور" و "تفسير سورة الأعلى" (١).
_________
(١) وفيات الأعيان ٢: ١٥٧ وتاريخ حكماء الإسلام ٥٢ وتاريخ الحكماء ٤١٣ ولسان الميزان ٢: ٢٩١ ومختصر تاريخ الدول ٣٢٥ وخزانة الأدب ٤: ٤٦٦ وتاج التراجم، وروضات الجنات ٢٤٠ وعيون الأنباء ٤٣٧ وشذرات الذهب ٣: ٢٣٤ واعيان الشيعة ٢٦: ٢٨٧ والجواهر المضية ١: ١٩٥ والنجوم الزاهرة ٥: ٢٥ والبداية والنهاية ١٢: ٤٢، والشيخ الرئيس ابن سينا للعقاد، وابن سينا الفيلسوف لبولس مسعد، وابن سينا بين الدين والفلسفة لحمودة عزابة، ومؤلفات ابن سينا لجورج قنواتي، وابن سينا لجميل صليبا، وتاريخ فلاسفة =','1','155',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (289,'الجعل [٢٨٨ - ٣٦٩ هـ / ٩٠٠ - ٩٨٠ م]

<span data-type="title" id=toc-413>الحسين بن علي بن إبراهيم البصري، أبو عبد الله، الملقب بالجعل:</span>
فقيه، من شيوخ المعتزلة، انتهت إليه الرياسة في علم الكلام في عصره، مولده في البصرة ووفاته ببغداد. قال أبو حيان فيما وصفه به: "كان الرجل ملتهب الخاطر، واسع أطراف الكلام، مع غثاثة اللفظ، يرجع إلى قوة عجيبة في التدريس، وطول نفس في الإملاء، مع ضيق صدر عند لقاء الخصم ومعاركة القرن، بعيد العهد بالمصاع والدفاع والوقاع .. الخ". من كتبه "الناسخ والمنسوخ" (١).
_________
= الإسلام ١٦٤ وشخصيات ومذاهب فلسفية لعثمان أمين ٦٢ والرد على المنطقيين ١٤١ ودائرة المعارف الإسلامية (ط ١) ١: ٢٠٣ ومعجم المطبوعات ١: ١٢٧ ومصادر الدراسة الأدبية ١: ٢٦٢ وما فيه من مراجع، ومعجم المؤلفين ٤: ٢٠ وفي الحاشية أسماء مصادر أخرى كثيرة وبخاصة ما كتب عن ابن سينا في المجلات العربية. وانظر أيضا الوافي ١١: ٣٩١ والأعلام ٢: ٢٤١ والكتاب الذهبي للمهرجان الألفي لابن سينا (بغداد ١٩٥٢) نشر جامعة الدول العربية.
(١) طبقات المفسرين للداودي ١: ١٥٥ وابن النديم ٢٢٢ وفيه مولده سنة ٣٠٨ ووفاته سنة ٣٦٩ هـ. وتاريخ بغداد ٨: ٧٣ وفيه ولادته سنة ٢٩٣ هـ والوافي (مخطوط) ١١: ١٠١ وفي ولادته سنة ٢٨٩ هـ. ولسان الميزان ٢: ٣٠٣ والشذرات ٣: ٦٨ وهدية العارفين ١: ٣٠٧ وولادته فيه سنة ٣٠٣ ووفاته سنة ٣٩٩ هـ. والإمتاع والمؤانسة ١: ١٤٠ والمنتظم ٧: =','1','155',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (290,'الوزير المغربي [٣٧٠ - ٤١٨ هـ / ٩٨٠ - ١٠٢٧ م]

<span data-type="title" id=toc-414>الحسين بن علي بن الحسين بن علي بن محمد، أبو القاسم المغربي:</span>
وزير، كان عالما أديبا بليغا على ذكاء جم، وبراعة في الكتابة. أصله من أبناء الأكاسرة. ولد بمصر. وكان والده من وجوه الدولة الحاكمية الفاطمية ومن جلساء الحاكم، ثم تغير عليه الحاكم وقتله (سنة ٤٠٠ هـ) ففر الحسن واستجار بحسان بن مفرج الجراح أمير بادية الشام، فأجاره، ثم رحل إلى بغداد في أيام القادر بالله العباسي، فاتهمه القادر لقدومه من مصر، فانتقل إلى الموصل، واتصل بالأمير قرواش بن المقلد، أمير بني عقيل - وكتب له. ثم عاد عنه. وتقلبت به الأحوال إلى أن استوزره حاكم العراق مشرف الدولة البويهي ببغداد، عشرة أشهر وأياما. ثم اضطرب أمره، فعاد إلى الموصل، فطلب الخليفة إبعاده، فرحل إلى ديار بكر وأقام بميافارقين إلى أن توفي. وحمل إلى الكوفة بوصية منه فدفن فيها. من كتبه "خصائص علم القرآن". وقال صاحب "طبقات المفسرين": "وله املاءات عدة في تفسير القرآن العظيم وتأويله" (١).
_________
= ١٠١ وطبقات الفقهاء للشيرازي ١٢١ والنجوم الزاهرة ٤: ١٣٥ والأعلام ٢: ٢٦٦ ومعجم المؤلفين ٤: ٢٧.
(١) لسان الميزان ٢: ٣٠١ وذيل تاريخ دمشق ٦٢','1','156',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (291,'الكاشغري [.. - ٤٨٤ هـ - / .. - ١٠٩١ م]

<span data-type="title" id=toc-415>الحسين بن علي بن خلف بن جبيل بن الخليل، أبو عبد الله، الكاشغري:</span>
واعظ، من أهل كاشغر، في وسط بلاد الترك. قال ابن النجار: "كان شفي صالحا متدينا إلا أنه كتب الغرائب، وقد ضعفوه واتهموه". وقال السمعاني: "قرأت فهرست مصنفاته، وهي في التفسير والفقه والرقائق وغيرها، تزيد على مائة وعشرين مصنفا" (١).

الخزاعي [.. - بعد ٥٥٢ هـ / .. - بعد ١١٥٧ م]

<span data-type="title" id=toc-416>الحسن بن علي بن محمد بن أحمد بن الحسين الخزاعي النيسابوري الرازي، أبو (الفتوح، .. جمال الدين)</span>
ومعجم الأدباء ١٠: ٧٩ واتعاظ الحنفا ٢: ٨٢ وشذرات الذهب ٣: ٢١٠ ومرآة الجنان ٣: ٣٢ وطبقات المفسرين للداودي ١: ١٥٢ ووفيات الأعيان ٢: ١٧٢ ورجال النجاشي ٥١ وروضات الجنات ٢٤١ والحاكم بأمر الله ١٨٢ وأعيان الشيعة ٢٧: ٦ وتنقيح المقال ١: ٣٣٨ وتاريخ ابن خلدون ٤: ٥٥١ والكامل لابن الأثير ٩: ٣٢١ و ٣٣١ وانظر فهرسته. وابن عساكر ٤: ٣٢١ والأنطاكي ٢٠٠ والخطط المقريزية ٣: ٢٥٥ وهدية العارفين ١: ٣٠٨ والأعلام ٢: ٢٦٦ ومعجم المؤلفين ٤: ٣٥.
_________
(١) معجم البلدان مادة كاشغر، واللباب ٣: ٧٦ والأنساب ١٤٧٢ وطبقات المفسرين للسيوطي ١١ وهو فيه الحسن، ومثله في لسان الميزان ٢: ٣٥٥ وهدية العارفين ١: ٣١٠ وطبقات المفسرين للداودي ١: ١٥٤ والأعلام ٢: ٢٦٧ ومعجم المؤلفين ٤: ٣١.','1','156',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (292,'الفتوح، .. جمال الدين: مفسر، من فقهاء الإمامية. أصله من نيسابور، سكن الري. من كتبه "روض الجنان وروح الجنان" في تفسير القرآن، فارسي، في عشرين مجلد" (١).

الهروي [.. - ٩١٠ هـ / .. - ١٥٠٤ م]

<span data-type="title" id=toc-417>حسين بن علي الكاشفي، البيهقي، الهروي، كمال الدين، المعروف بالواعظ الهروي:</span>
محدث، مفسر، صوفي، عارف بالأدب، من فقهاء الشيعة الإمامية. سكن هراة وتوفي بها. من كتبه "تفسير سورة يوسف" و "جواهر التفسير لتحفة الأمير" قال حاجي خليفة: "وهو تفسير الزهراوين في مجلد، ألفه لأمير عليشير، أورد في أوله العلوم المتعلقة بالتفسير وهي اثنان وعشرون فنا في أربعة فصول، وذكر التفسير والتأويل ونحو ذلك". و "المواهب العلية في تفسير القرآن" قال مصحح كشف الظنون: لعله جواهر التفسير (٢).
_________
(١) أعيان الشيعة ٢٧: ٦١ وهدية العارفين ١: ٣١٢ وفيه وفاته في حدود سنة ٥٣٥ وقيل سنة ٥٦٠ هـ وإيضاح المكنون ١: ٥٨٨ و ٥٩٤ وفيه وفاته في حدود سنة ٥٣٥ وقيل سنة ٥٦٠ هـ.
(٢) هدية العارفين ١: ٣١٤ وكشف الظنون ٤٤٦ هـ و ٤٤٨ و ٦١٣ و ١٨٩٦ وفيه وفاته سنة ٩٠٦ هـ. وأعيان الشيعة ٢٧: ٥٠ وروضات الجنات ٢٥٦ ومعجم المؤلفين ٤: ٣٤.','1','157',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (293,'النقوي [١٢١١ - ١٢٧٣ هـ / ١٧٩٦ - ١٨٥٦ م]

<span data-type="title" id=toc-418>حسين بن علي بن محمد النقوي، النصير آبادي، الرضوي، اللكهنوئي، المعروف بسيد العلماء:</span>
فقيه إمامي، متكلم، واعظ، مفسر. له "أمال" في التفسير (١).

النيسابوري [.. - ٢٨٢ هـ / .. - ٨٩٥ م]

<span data-type="title" id=toc-419>الحسين بن الفضل بن عمير البجلي الكوفي، ثم النيسابوري، أبو علي:</span>
مفسر، محدث ثقة، من أكابر فقهاء الشيعة الإمامية. كوفي الأصل، استوطن نيسابور ونزل في دار ابتاعها له أمير خراسان عبد الله بن طاهر سنة ٢١٧ هـ، فبقي فيها يعلم الناس ويفتي خمسا وستين سنة. وقال في لسان الميزان: "لو كان الحسين بن الفضل في بني إسرائيل لكان من عجائبهم". وقال الحاكم: "كان إمام عصره في علوم القرآن، مات وله مائة وأربع سنين وقبره مشهور يزار" ثم ذكر طائفة من مشايخه (٢).
_________
(١) أعلام الشيعة ٢: ٣٨٧ وأعيان الشيعة ٢٦: ٣٤ ومعجم المؤلفين ٤: ٣٦.
(٢) طبقات المفسربن للسيوطي ١٢: وطبقات المفسرين للداودي ١: ١٥٦ ولسان الميزان ٢: ٣٠٧ والشذرات ٢: ١٧٨ وأعيان الشيعة.','1','157',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (294,'المهدي العياني [٣٧٤ - ٤٠٤ هـ / ٩٨٤ - ١٠١٣ م]

<span data-type="title" id=toc-420>الحسن بن القاسم بن علي بن عبد الله، المهدي لدين الله، خامس الأئمة الزيدية باليمن</span>.
ولي الإمامية بعد أبيه المنصور بالله، المتوفي سنة ٣٩٣ هـ. وكانت إقامته كأبيه في صنعاء. وثار عليه معارضوه، وقاتلهم، فقتل، في البون شمالي صنعاء، وهو في حد الثلاثين سنة. من آثاره "تفسير الغريب في كتاب الله"، مخطوط (١).

السبزواري [١٢٦٨ - ١٣٥٢ هـ / ١٨٥٢ - ١٩٣٣ م]

<span data-type="title" id=toc-421>حسين بن محسن العلوي السبزواري، المعروف بالحاج ميرزا حسين الكبير:</span>
فقيه إمامي، عارف بالحكمة والكلام والأدب والتفسير. من أهل سبروار، - مدينة غربي نيسابور في شمال شرقي إيران - مولدا ووفاة: من كتبه "تفسير آية الخلافة" (٢).

الزعفراني [.. - ٣٦٩ هـ / .. - ٩٧٩ م]

<span data-type="title" id=toc-422>الحسين بن محمد بن علي الأصبهاني، أبو (سعيد، المعروف بالزعفراني)</span>
_________
(١) معجم الأنساب ١: ١٨٧ وغاية الأماني ١: ٢٣٧ وتاريخ الدول الإسلامية ١: ١١١ وتاريخ التراث العربي ٢: ٣١٠ وهدية العارفين ١: ٣٠٧.
(٢) أعيان الشيعة ٢٧: ١٢٥ وعلماء معاصرين ١٦٨.','1','158',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (295,'سعيد، المعروف بالزعفراني: محدث، حافظ، فقيه، أصولي، مفسر، من أهل أصبهان. قال أبو نعيم: كان بندار بلدنا في كثرة الأصول والحديث، صاحب معرفة واتقان، له من المصنفات شيء كثير، منها "تفسير القرآن" (١).

الراغب الأصفهاني [.. - ٥٠٢ هـ / .. - ١١٠٨ م]

<span data-type="title" id=toc-423>الحسين بن محمد بن المفضل، أبو القاسم، المعروف بالراغب الأصفهاني:</span>
أديب، إمام من حكماء العلماء، اشتهر بالتفسير واللغة. أصله من أصفهان، وعاش ببغداد. قال السيوطي: "كان في ظني أن الراغب معتزلي، حتى رأيت بخط الشيخ بدر الدين الزركشي على ظهر نسخة من "القواعد الصغرى" لابن عبد السلام ما نصه "ذكر الإمام فخر الدين الراغب من أئمة السنة" وقرنه بالغزالي، قال: وهي فائدة حسنة، فإن كثيرا من الناس يظنون أنه معتزلي". من كتبه "تحقيق البيان في تأويل القرآن" و "تفسير الراغب" - لعله جامع التفاسير - وقد طبعت مقدمته. قال صاحب كشف الظنون: "وهو تفسير معتبر
_________
(١) نذكز الحفاظ ٩٥٦ وطبقات المفسرين للسيوطي ١٢ وطبقات المفسرين للداودي ١: ١٥٧ والأعلام ٢: ٢٧٧ وذكر أخبار أصبهان ١: ٢٨٣ ومعجم المؤلفين ٤: ٥٥ وهدية العارفين ١: ٣٠٥.','1','158',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (296,'في مجلد، أورد في أوله مقدمات نافعة في التفسير وطرزه أنه أورد جملا من الآيات ثم فسرها تفسيرا مشبعا، وهو أحد مآخذ أنوار التنزيل للبيضاوي". و "درة التأويل في متشابه التنزيل" أوله: "اعلموا حملة الكتاب الكريم ... ". و "المفردات في غريب القرآن"، "تتبع فيه دوران كل لفظ في الآيات القرآنية، وأتى بالشواهد عليه من الحديث والشعر، وأورد ما أخذ منه من مجاز وتشبيه ورتبه على الألفباء، فأصبح من أهم الكتب المفسرة لألفاظ القرآن" (١).

الطيبي [.. - ٧٤٣ هـ / .. - ١٣٤٢ م]

<span data-type="title" id=toc-424>الحسين بن محمد بن عبد الله الطيبي، شرف الدين:</span>
عالم بالحديث والتفسير والعربية والمعاني والبيان. قال ابن حجر: "كان آية في استخراج الدقائق من القرآن والسنن، مقبلا على نشر العلم، متواضعا
_________
(١) أعيان الشيعة ٢٧: ٢٣٠ روضات الجنات ٢٤٩ وتاريخ حكماء الإسلام ١١٢ والذريعة ٥: ٤٥ ومعجم المطبوعات ٩٢٢ وتاريخ آداب اللغة العربية ٣: ٧ وسفينة البحار ١: ٥٢٨ وكنوز الأجداد ٢٦٨ وبغية الوعاة ١: ٢٩٧ وهو فيه المفضل بن محمد. والأعلام ٢: ٢٧٩ وكشف الظنون ٣٧٧ و ٤٤٧ و ٧٣٩ و ٧٧٣ وهدية العارفين ١: ٣١١ ومجلة المقتبس ٢: ٩٨ ومجلة المجمع العلمي العربي ٢٤: ٢٧٥ والموسوعة العربية الميسرة ٨٥٤ ومنجد الأعلام ٣٠٢ ودائرة المعارف الإسلامية.','1','159',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (297,'حسن المعتقد، شديد الرد على الفلاسفة والمبتدعة، مظهرا فضائحهم، مع استبلائهم حينئذ، شديد الحب لله ورسوله، كثير الحياء، ملازما لأشغال الطلبة في العلوم الإسلامية بغير طمع، بل يخدمهم ويعينهم، ويعير الكتب النفيسة لأهل بلده وغيرهم، من يعرف ومن لا يعرف، محبا لمن عرف منه تعظيم الشريعة. وكان ذا ثروة من الإرث والتجارة، فلم يزل ينفقه في وجوه الخيرات حتى صار في آخر عمره فقيرا، وكان يشتغل في التفسير من بكرة إلى الظهر .. ". من كتبه، فتوح الغيب على الكشاف عن قناع الريب" حاشية على الكشاف للزمخشري، في ست مجلدات، مخطوطة، قال في كشف الظنون: "وهي أجل حواش الكشاف" وقال ابن حجر: "وهو شرح حسن، كبير، أجاب عما خالف فيه الزمخشري أهل السنة بأحسن جواب". وله أيضا "تفسير القرآن" لم يكمله (١).
_________
(١) البدر الطالع ١: ٢٢٩ والدرر الكامنة ٢: ١٥٦ وروضات الجنات ٢٢٤ وطبقات المفسرين للداودي ١: ١٤٣ ومفتاح السعادة ٢: ١٠١ وتراث العرب العلمي ٣٨٥ والأعلام ٢: ٢٨٠ ومعجم المؤلفين ٤: ٥٣ وبغية الوعاة ٢: ٥٢٢ وهو في الحسن بن محمد، ومثله في شذرات الذهب ٦: ١٣٧ وهدية العارفين ١: ٢٨٥ وكشف الظنون ١٤٧٨ وفهرس المكتبة الأزهرية ١: ٣١٥. وعلق مصحح الدرر الكامنة بقوله: "في هامش ١ - إحدى النسخ المخطوطة - بخط السخاوي: هذا =','1','159',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (298,'خليفة سلطان [١٠٠١ - ١٠٦٤ هـ / ١٥٩٣ - ١٦٥٤ م]

<span data-type="title" id=toc-425>حسين بن محمد بن محمود بن علي الحسيني نسبا، المرعشي الآملي أصلا، الأصفهاني منشأ وموطنا، أبو طالب، علاء الدين:</span>
وزير، من أكابر علماء الشيعة الإمامية. وزير للسلطان شاه عباس الصفوي، ثم من بعده للشاه صفي الصفوي. وعزله هذا الأخير ونفاه إلى قم، ثم عفا عنه وأعاده إلى أصفهان. ولما ولي الشاه عباس الثاني أرجعه إلى الوزارة وقربه. توفي ببلدة أشرف من بلاد مازندران، وهو راجع مع الشاه عباس من فتح قندهار، ونقل نعشه إلى النجف. من كتبه "حاشية" على الكشاف للزمخشري في التفسير (١).

العنابي - ابن [.. - ١١٥٠ هـ / .. - ١٧٣٧ م]

<span data-type="title" id=toc-426>حسين بن محمد، المعروف بابن العنابي:</span>
مفسر، واسع المعرفة في علوم
_________
= الرجل - أي الطيبي - سمى نفسه في أول شرح المشكاة الحسين بن عبد الله بن محمد، وكذا سماه شيخنا المؤلف - يعني صاحب الدرر الكامنة في أول تخريجه أحاديث المصابيح.
(١) أعيان الشيعة ٢٧: ٢٣٥ وروضات الجنات ٢: ٢٧ والذريعة ٢: ٤٠٥ ومعجم المؤلفين ٤: ٥٨ والأعلام ٢: ٢٨١ وأمل الآمل ٢: ٩٢ وسلافة العصر ٤٩٩.','1','160',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (299,'الشريعة، من فقهاء الحنفية. نسبته إلى مدينة عنابة (في الجزائر، على المتوسط) وسكن مدينة الجزائر (العاصمة) وولى الافتاء فيها أربع مرات. وتوفي بها. له "تفسير القرآن" (١). الحدادي [.. - بعد١٢٠٧ هـ .. - بعد ١٧٩٣ م]

<span data-type="title" id=toc-427>حسين بن محمد الحدادي البله جكي، الرومي:</span>
تركي مستعرب، عارف باللغة والتفسير. له "معراج الدراية" في تفسير القرآن، فرغ منها في رجب سنة ١٢٠٧ هـ (٢).

حسين عصفور [.. ١٢١٦ هـ / .. - ١٨٠٢ م]

<span data-type="title" id=toc-428>حسين بن محمد بن أحمد بن إبراهيم، ابن عصفور الدرازي الشاخوري البحراني:</span>
فقيه إمامي، محدث، مفسر، متكلم، من أهل قرية الشاخورة (بالبحرين)، وقتل في معركة بها. من مؤلفاته "مفاتيح الغيب والتبيان في تفسير القرآن" (٣).
_________
(١) معجم أعلام الجزائر ٢٤٤ (ط ٢) وابن العنابي ١٣ والمجلة الافريقية ١٨٦٧.
(٢) هدية العارفين ١: ٣٢٨ وإيضاح المكنون ٢: ٥١١.
(٣) أعيان الشيعة ٢٧: ١٢٦ وشهداء الفضيلة ٣٠٧ وهدية العارفين ١: ٣٣٠.','1','160',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (300,'الكاشاني [١٢١٥ - ١٢٨٥ هـ / ١٨٠٠ - ١٨٦٨ م]

<span data-type="title" id=toc-429>حسين بن محمد رضي الدين بن حسين الحسيني اللاجوردي الكاشاني:</span>
واعظ، فقيه إمامي، مفسر، من أهل كاشان مولدا ووفاة. من كتبه "تفسير القرآن" من سورة مريم إلى آخر القرآن" (١).

الطباطبائي [.. - ١٣٠٧ هـ / .. - ١٨٨٩ م]

<span data-type="title" id=toc-430>حسين بن مرتضى الطباطبائي، الحائري:</span>
واعظ، من فقهاء الشيعة الإمامية. توفي بكربلاء. من آثاره "لوامع الظهور في تفسيرآية النور" (٢).

البغوي [.. - ٥١٦ هـ / .. - ١٢٢٢ م]

<span data-type="title" id=toc-431>حسين بن مسعود بن محمد، المعروف بابن الفراء، أو الفراء، ويلقب بمحيي السنة، وركن الدين، البغوي:</span>
حافظ، محدث، مفسر، من فقهاء الشافعية. نسبته إلى "بغا" من قرى خراسان، بين هراة ومرو. ودرس في مرو، وأخذ عنه جماعة من الأعيان. ترجع شهرته إلى كتابه
_________
(١) أعيان الشيعة ٢٧: ١٩١ وأعلام الشيعة ٢:
(٢) أعيان الشيعة ٢٧: ٣٦٨ ومعجم المؤلفين ٤: ..','1','161',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (301,'"مصابيح السنة". قال في "طبقات المفسرين": "كان إماما في التفسير، إماما في الحديث، إماما في الفقه، جليلا ورعا زاهدا، بورك له في تصانيفه، ورزق فيها القبول الحسن بنيته، وكان قانعا يأكل الخبز وحده، ثم عذل في ذلك، فصار يأكله في زيت". من كتبه "معالم التنزيل" في تفسير القرآن الكريم، طبع مرات (١).

الآيديني [.. - ١١٩١ هـ / .. - ١٧٧٧ م]

<span data-type="title" id=toc-432>حسين بن مصطفى الآيديني، المعروف بابن قره تبه لي:</span>
فقيه حنفي، مشارك في عدة علوم، نسبته إلى ولاية آيدين بتركية، وولي الإفتاء بها. من كتبه "حاشية" على بعض أقسام "أنوار التنزيل" في التفسير للبيضناوي (٢).
_________
(١) طبقات المفسرين للسيوطي ١٢ وطبقات المفسرين للداودي ١: ١٥٧ والبداية والنهاية ١٢: ١٩٣ والنجوم الزاهرة ٥: ١٢٣ وتذكرة الحفاظ ١٢٥٧ ومفتع السعادة ٢: ١٠٢ والشذرات ٤: ٤٨ وطبقات الشافعية للسبكي ٧: ٧٥ وطبقات الشافعية لابن هداية ٢٠٠ ومرآة الجنان ٣: ٢١٣ والعبر ٤: ٣٧ ووفيات الأعيان ٢: ١٣٦ وروضات الجنات ٢٤٦ ومعجم البلدان مادة (بغ) ودائرة المعارف الإسلامية ٤: ٢٧ والأعلام ٢: ٢٨٤ والمختصر في أخبار البشر ٢: ٢٤٠ ومختصر دول الإسلام ٢: ٣٠ ومعجم المؤلفين ٤: ٦١ وهدية العارفين ١: ٣١٢ وكشف الظنون ١٧٢٦. ووفاته في بعض هذه المصادر سنة ٥١٦ هـ.
(٢) هدية العارفين ١: ٣٢٧ ومعجم المؤلفين ٤: ٦٢.','1','161',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (302,'الحلاج [.. - ٣٠٩ هـ / .. - ٦٢٢ م]

<span data-type="title" id=toc-433>الحسن بن منصور بن محمى البيضاوي، أبو المغيث، المعروف بالحلاج:</span>
فيلسوف، صوفي، ولد في قرية الطور في الشمال الشرقي من مدينة البيضاء، من مدن مقاطعة فارس بإيران، ونشأ بواسط - بين البصرة وبغداد -. ودرس على شيوخ الصوفية، التستري والمكي والجنيد، في تستر والبصرة وبغداد. ثم حج، وتنقل في البلدان داعيا إلى الزهد، حتى استقر ببغداد (نحو سنة ٢٩١ هـ)، وجمع حوله كثيرا من المريدين. واتهم بالزندقة والقول بالحلول وكثرت الوشايات به إلى الخليفة المقتدر بالله، فأمر بالقبض عليه، وبعد سجن دام ثماني سنوات، ومحاكمة استمرت سبعة أشهر، أمر بقتله، فقيد وضرب بالسياط وهو صابر لا يتأوه ولا يستغيث، ثم صلب، قال ابن خلكان: "وقطعت أطرافه الأربعة ثم حز رأسه وأحرقت جثته، ولما صارت رمادا ألقيت في دجلة، ونصب الرأس على جسر بغداد". أنشأ مذهبا في التصوف وأثار حوله الجدل، فعده البعض في كبار المتعبدين والزهاد، وعده البعض الآخر في زمرة الملحدين. من كتبه "تفسير سورة: {قل هو الله أحد} و "كتاب الظل المعدود والماء المسكوب والحياة الباقية" في','1','162',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (303,'تفسير قوله تعالى: {وظل ممدود وماء مسكوب وفاكهة كثيرة ... (الواقعة ٥٦: ٣٠ - ٣٢) في صفة أهل الجنة وحياتهم الباقية. وكتاب "الذاريات ذروا" في تفسير سورة الذاريات، وكتاب "النجم إذا هوى" في تفسير سورة النجم، بحذف الواو من أولها. وكتاب في "إن الذي أنزل القرآن لرادك إلى معاد" وصحة الآية الكريمة: {إن الذي فرض عليك القرآن لرادك إلى معاد} (القصص ٢٨: ٨٥) (١).

المروزي [.. - ١٥٩ هـ / .. - ٧٧٥ م]

<span data-type="title" id=toc-434>الحسن بن واقد المروزي، أبو عل:</span>
مفسر، محدث، قاض، من أهل مرو، وولي قضاءها. أخرج له مسلم وأصحاب السنن الأربعة، كما أخرج له البخاري في التعاليق. مختلف فيه، لكن الراجح
_________
(١) طبقات المفسرين للداودي ١: ١٥٩ وطبقات الصوفية للسلمي ٣٠٧ وتاريخ بغداد ٨: ١١٢ وابن الأثير ٨: ١٢٦ والبداية والنهاية ١١: ١٣٢ وميزان الاعتدال ١: ٥٤٨ ولسان الميزان ٢: ٣١٤ والعبر ٢: ٣٨ والمنتظم ٦: ١٦٠ وصلة غريب ٨٦ ومرآة الجنان ٢: ٢٥٣ وابن النديم ٢٤١ وتجارب الأمم ١: ٧٦ والنجوم الزاهرة ٣: ٢٠٢ وابن خلكان ٢: ١٤٠ والشذرات ٢: ٢٥٣ والتنبيه والأشرات ٣٨٧ وروضات الجنات ٢٢٦ وتاريخ الخميس ٢: ٣٤٧ وطبقات الشعراني ١: ٩٢ والأعلام ٢: ٢٨٥ ومعجم المؤلفين ٤: ٦٣.','1','162',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (304,'توثيقه. له "تفسير القرآن" و "الناسخ والمنسوخ" و "وجوه القرآن" (١).

ابن مخارق [.. - نحو ٢٠٠ هـ / .. - نحو ٨١٥ م]

<span data-type="title" id=toc-435>حصين بن مخارق بن عبد الرحمن بن ورقاء بن حبشي بن جنادة السلولي، أبو جنادة:</span>
مفسر، له اشتغال بالحديث والقراءات، من أهل الكوفة، عده النجاشي من مصنفي الشيعة، وقال أنه ضعيف. وقال الدارقطني: يضع الحديث. وفي "المعجم الصغير" للطبراني أنه ثقة. من آثاره "تفسير القرآن" (٢).

حفص القارىء [.. - ٢٤٦ هـ / .. - ٨٦٠ م]

<span data-type="title" id=toc-436>حفص بن عمر بن عبد العزيز الأزدي الدوري، أبو عمر:</span>
إمام القراءة، وشيخ الناس في زمانه، كان ثقة ثبتا ضابطا. نسبته إلى الدور - محلة ببغداد - ونزل
_________
(١) طبقات المفسرين للداودي ١: ١٦٠ وتاريخ البخاري ٢: ٣٨٩ والعبر ١: ٢٢٦ والفهرست لابن النديم ٥٧ وهو فيه: الحسن بن واقد: وتهذيب التهذيب ٣: ٣٧٣ والنسخ في القرآن الكريم ١: ٣٠٤ وشذرات الذهب ١: ٢٤١ وخلاصة تهذيب الكمال ٧٢ وميزان الاعتدال ١: ٥٤٩ ومرآة الجنان ١: ٣٣٤ والنجوم الزاهرة ٢: ٣١ وهدية العارفين ١: ٣٠٤. ق
(٢) طبقات المفسرين ١: ١٦١ وميزان الاعتدال ١: ٥٥٤ ولسان الميزان ٢: ٣١٩ وابن النديم ١٩٣ وهدية العارفين ١: ٣٣٣.','1','163',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (305,'سامراء. وهو أول من جمع القراءات. وكان ضريرا. قال أبو حاتم: "صدوق، وطال عمره، وقصد من الآفاق، وازدحم عليه الحذاق لعلو سنده، وسعة علمه" من كتبه "أحكام القرآن" و "ما اتفقت ألفاظه ومعانيه في القرآن" (١).

حمد الخطابي [٣١٩ - ٣٨٨ هـ / ٩٣١ - ٩٩٨ م]

<span data-type="title" id=toc-437>حمد بن محمد بن إبراهيم بن الخطاب البستي، أبو سليمان:</span>
محدث، فقيه، له شعر، ولد بمدينة بست - في أفغانستان - وسمع الحديث بمكة وبالبصرة وببغداد. وتوفي في بست. من آثاره "بيان إعجاز القرآن" طبع (٢).
_________
(١) طبقات المفسرين للداودي ١: ١٦٢ وطبقات القراء للذهبي ١: ١٥٧ وغاية النهاية ١: ٢٠٠ وتاريخ بغداد ٨: ٢٠٣ وتهذيب التهذيب ٢: ٤٠٨ والعبر ١: ٤٤٦ ومفتاح السعادة ٢: ٣٣ وإرشاد الأريب ٤: ١١٨ والنجوم الزاهرة ٢: ٣٢٣ وشذرات الذهب ٢: ١١١ واللباب ١: ٤٢٨ وميزان الاعتدال ١: ٥٦٦ والأعلام ٢: ٢٩١ ومعجم المؤلفين ٤: ٦٩ وهدية العارفين ١: ٣٣٣ ووفاته في بعض هذه المصادر سنة ٢٤٠ أو ٢٤٥ أو ٢٤٨ هـ.
(٢) تذكرة الحفاظ ١٠١٨ والسبكي ٢: ٢١٨ وتحفة ذوي الأرب ١٥٤ ويتيمة الدهر ٤: ٣٣٤ والشذرات ٣: ١٥٥ ومجلة المجتمع العلمي العربي ١٥: ٢٤١ ومعجم الأدباء ٤: ٢٤٦ ومرآة الجنان ٢: ٤٣٥ وإنباه الرواة ١: ١٢٥ وابن خلكان ٢: ٢١٤ ومفتاح السعادة ٢: ١٥ وروضات الجنات ٢٦٢ والبداية والنهاية ١١: ٢٣٦ والمنتظم ٦: ٣٩٧ واسمه في بعض هذه المصادر =','1','163',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (306,'ابن الحاج [١١٧٤ - ١٢٣٢ هـ / ١٧٦٠ - ١٨١٧ م]

<span data-type="title" id=toc-438>حمدون بن عبد الرحمن بن حمدون بن عبد الرحمن السلمي المرداسي، أبو الفيض، المعروف بابن الحاج:</span>
مفسر، أديب، من فقهاء المالكية، من أهل فاس. من كتبه "تفسير سورة الفرقان" و "حاشية على تفسير أبي السعود" وغير ذلك (١).

حمدى عبيد [١٣٠٧ - ١٣٩١ هـ / ١٨٨٩ - ١٩٧١ م]

<span data-type="title" id=toc-439>حمدي بن محمد حسن بن يوسف بن عبيد بن سليمان آغا:</span>
باحث، أديب، له اشتغال بالتفسير، من أهل دمشق، مولدا ووفاة، كان يعمل في العقادة، ثم انضم مع أخ له اسمه محمد توفيق، بعد الحرب العالمية الأولى، إلى أخيهما الثالث الأستاذ الأديب أحمد عبيد مؤسس المكتبة العربية في دمشق، نسبته إلى جد أبيه عبيد بن سليمان، وهو الذي تنتسب الأسرة إليه. من آثاره "تفسير غريب القرآن" طبعه على هامش المصحف (٢).
_________
= أحمد. وفي وفيات الأعيان: سمع في اسه أحمد، والصحيح حمد.
(١) شجرة النور ٣٧٩ وسلوة الانفاس ٣: ٤ واليواقيت ١: ١ والاستقصا ٤: ١٥١.
(٢) الاعلام ٢: ٢٧٥ ومعلومات أمدني بها الشيخ زهير الشاويش.','1','164',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (307,'الناشري [٨٣٣ - ٩٢٦ هـ / ١٤٣٠ - ١٥٢٠ م]

<span data-type="title" id=toc-440>حمزة بن عبد الله بن محمد بن علي الناشري اليمني، تقي الدين، أبو العباس:</span>
فقيه شافعي، عارف بالتفسير والتاريخ والأدب، من أهل وادي زبيد، ونشأ بزبيد وتوفي بها. تردد إلى مكة كثيرا. قال السخاوي لقيته سنة ٨٨٦ هـ وكتب لي من نظمه أشياء، وأفادني نبذة من تراجم أهل بلده، ولم تنقطع عني كتبه. من تصانيفه "ألفية" في غريب القرآن (١).

القرماني [.. - ٨٧١ هـ / .. - ١٤٦٧ م]

<span data-type="title" id=toc-441>حمزة بن محمود القرماني، نور الدين:</span>
عارف بالتفسير والحديث والففه، من أهل قرمان - في وسط تركيا الآسيوية - ولي الافتاء بها، واشتغل بالتدريس. من آثاره "تفسير التفسير في التيسير والتسيير" حاشية على أنوار التنزيل للبيضاوي في تفسير الزهراوين. في تاريخ وفاته خلاف بين المؤرخين، ففي الفوائد البهية أنه مات سنة ٨٩٩ هـ وفي الشذرات سنة ٩٦٥ هـ وفي الشقائق النعمانية في أوائل المائة التاسعة،
_________
(١) الضوء اللامع ٣: ١٦٤ والبدر الطالع ١: ٢٣٨ والنور السافر ١٣٠ وشذرات الذهب ٨: ١٤٢ وهدية العارفين ١: ٣٣٧.','1','164',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (308,'وانظر ترجمة أحمد بن محمود الأصم في هذا المعجم (١).

الآملي [.. - حيا ٧٨٢ هـ / .. - حيا ١٣٨٠ م]

<span data-type="title" id=toc-442>حيدر بن علي بن حيدر العبيدي الآملي، بهاء الدين:</span>
متكلم، صوفي، مفسر، فقيه إمامي، كان متعصبا، ثم صار صوفيا فترك التعصب واتخذ التسامح. أصله من آمل طبرستان. دخل العراق، وأقام في الحلة، وأخذ عن ابن المطهر الحلي. الف أهم كتبه في العراق. من كتبه "المحيط الأعظم" في تفسير القرآن الكريم، و "البحر الخضم في تفسير القرآن الأعظم" (٢).

الفيض آبادي [١٢١٠ - بعد ١٢٨٣ هـ / ١٦٨٢ - بعد ١٧٩٥ م]

<span data-type="title" id=toc-443>حيدر علي بن محمد الفيض آبادي:</span>
متكلم هندي، عارف بالتفسير، قال صاحب هدية العارفين: "رأيته في بغداد في حدود سنة ١٢٨٣ هـ وسنه إذ ذاك نيف وسبعون". من آثاره "إزالة الغين" طبع،
_________
(١) هدية العارفين ١: ٣٣٧ وكشف الظنون ١٩٠، ٤٥٦، ٤٦٧، والشقائق النعمانية ٦٢ والفوائد البهية ٦٩ وشذرات الذهب ٨: ٣٤٤.
(٢) هدية العارفين ١: ٣٤١ وأعيان الشيعة ٢٩: ٢٥ وروضات الجنات ٢٠٢.','1','165',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (309,'تكلمة لتفسير العزيزي. قال صاحب الذريعة: ألفه في دهلي في ٢٧ مجلدا (١).

الهروي [٧٨٠ - ٨٥٤ هـ / ١٣٧٨ - ١٤٥٠ م]

<span data-type="title" id=toc-444>حيدر بن محمد بن إبراهيم الخوافي الهروي، برهان الدين، المعروف بالصدر الهروي:</span>
مفسر، عالم بالمعاني والبيان والعربية. قال السيوطي: "أخذ عن التفتازاني، وقدم الروم وأقرأ، وأخذ عنه شيخنا محي الدين الكافيجه" له "حاشية" على حاشية سعد الدين مسعود التفتازاني على الكشاف، في تفسير القرآن، للزمخشري. قال طاش كبري زاده: "رأيتها أورد فيها أجوبة عن اعتراضات الشريف على أستاذه، (٢).
_________
(١) الذريعة ١٦: ٤١٠ وفيه: وهو عامي، أي سنى. وهدية العارفين ١: ٣٤٢ والأعلام ٢: ٢٩
(٢) هدية العارفين ١: ٣٤١ وبغية الوعاة ١: ٥٤٩ وفيه: مات بعد ٨٢٠ هـ، ومثله في شذرات الذهب ٧: ١٤٥ وكشف الظنون ١٤٧٩ وفيه وفاته سنة ٨٣٠ هـ. والشقائق النعمانية ٣٧.','1','165',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (310,'<span data-type="title" id=toc-445>حرف الخاء</span>
الخاتون آبادي = محمد حسين ... ١١٥١ هـ
الخانون آبادي = محمد صالح بن عبد الواسع ... ١١١٦ هـ
الخادمي = سعيد بن محمد ١٢١٣ هـ
الخادمي = محمد بن محمد ١١٧٦ هـ
الخازن = علي بن محمد ٧٤١ هـ
خالد الأزهري = خالد بن عبد الله ... ٩٠٥ هـ
ابن خالويه = الحسين بن أحمد ... ٣٧٠ هـ
الخاني = محى الدين بن أحمد ... ١٣٥٠ هـ
الخبري = الحسن بن الحسين ... ٤٢٦ هـ
الختلي = أحمد بن جعفر ... ٣٦٥ هـ
الختن = محمد بن الحسن ... ٣٨٦ هـ
الخجندي = أحمد بن محمد ٨٠٢ هـ
الخراز = مقاتل بن سليمان ... نحو ١٥٠ هـ
الخراساني = إبراهيم بن طهمان ... كا اه
الخراساني = سعيد بن منصور ... ٢٢٧ هـ
الخراساني = علي نقي (ميرزا هادي) ... ?
الخراساني = محمد بن عثمان ... ٧٤٢ هـ
ابن الخراط الاشبيلى = عبد الحق بن عبد الرحمن ... ٥٨١ هـ
الخرتق = عمر بن الحسين ... ٣٣٤ هـ
الخربتاوي = محمد بن أحمد ... ١٢١٧ هـ
الخروبي = أحمد بن محمد ٥٠٢ هـ
الخروبي = محمد بن علي ... ٩٦٣ هـ
الخزاز = عبد الله بن محمد ٣٢٥ هـ
الخزاعي = الحسين بن علي ... بعد ٥٥٢ هـ','1','167',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (311,'الخزرجي = أحمد بن مسعود ... ٦٠١ هـ
ابن خزيمة = محمد بن إسحاق ... ٣١١ هـ
ابن الخشاب = عبد الله بن أحمد ... ٥٦٧ هـ
ابن الخشاب = محمد بن أحمد ... نحو ٦٥٠ هـ
خضر = أحمد بن محمد ٧٨٥ هـ
خضر = عبد الرحمن خضر ... ١٣٧٦ هـ
الخضر بن نصر ... ٥٦٧ هـ
الخضري = محمد بن مصطفى ... ١٢٨٧ هـ
الخطيب = عبد الحميد بن أحمد ... ١٣٨١ هـ
الخطيب الإسكافي = محمد بن عبد الله ... ٤٢٠ هـ
الخطيب البغدادي = أحمد بن علي ... ٤٦٣ هـ
الخطيب التبريزي = يحيى بن علي ... ٥٠٢ هـ
خطيب السقيفة = منصور بن عبد الرحمن ... ٩٦٧ هـ
الخطيب الشربيني = محمد بن أحمد ... ٩٧٧ هـ
الخطيب الوزيري = محمد بن إبراهيم ... ٨٩١ هـ
ابن الخطيب = محمد بن إبراهيم ... ٩٠١ هـ
ابن خطيب القلعة = يعقوب بن عبد الرحمن ... ٧٧٤ هـ
ابن خطيب الناصرية = علي بن محمد ٨٤٣ هـ
ابن الخطير (النعماني) = الحسن بن الخطير ... ٥٩٨ هـ
الخفاجي (الشهاب) = أحمد بن محمد ١٠٦٩ هـ
الخفاف = زكريا بن داود ... ٢٨٦ هـ
الخفاف = عبد الوهاب بن عطاء
خلاف = عبد الوهاب بن عبد الواحد ... ١٣٧٥ هـ
الخلال = أحمد بن محمد ٣١١ هـ
الخلخال = نصر الله بن محمد ٩٦٢ هـ
الخلخالي = حسين بن حسن ... ١٠١٤ هـ
الخلخالي = علي قلي بن محمد ق ١٣ هـ
خلف القارىء = خلف بن هشام ... ٢٢٩ هـ
ابن خلفة الأبي = محمد بن خلفة ... ٨٢٧ هـ
الخلوتي = إسماعيل بن عبد الله ... ٨٩٩ هـ
ابن الخلوف = يحيى بن خلف ... ٥٤١ هـ
خليفة = عبد الفتاح خليفة ... ١٣٦٥ هـ','1','168',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (312,'خليفة سلطان = حسين بن محمد ١٠٦٤ هـ
الخوانساري = أحمد الخوانساري ... ١٢٧٩ هـ
الخوانساري = زين الدين بن محمد باقي ... ١٢٤٥ هـ
الخوانساري = عبد العلي بن جعفر ... ١٣٤٦ هـ
خواهرزاده = إسماعيل بن محمود ... ق ٨ هـ
الخوبوي = عثمان بن حسن ... ١٢٤١ هـ
الخوشناوي = عيسى بن أحمد ... بعد ١٢٠٠ هـ
الخولاني = عبد الرحمن بن عبد الله ... ١٠٠٣ هـ
الخولي = محمد بن عبد العزيز ... ١٣٤٩ هـ
الخوبي = أحمد بن خليل ... ٦٣٧ هـ
الخوبي = محمد بن أحمد ... ٦٩٣ هـ
ابن الخياط = محمد بن أحمد ... ٣٢٠ هـ
ابن أبي الخير = محمد بن أبي الخير ... بعد ٧٠٠ هـ
ابن أبي الخير = مصطفى بن محمد ٦٧٨ هـ
ابن الخيمي = محمد بن علي ... ٦٤٢ هـ','1','169',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (313,'(خ)

خالد الأزهري [٨٣٨ - ٩٠٥ هـ / ١٤٣٤ - ١٤٩٩ م]

<span data-type="title" id=toc-446>خالد بن عبد الله بن أبي بكر بن محمد الجرجاوي الأزهري، زين الدين، ويعرف بالوقاد:</span>
نحوي، لغوي، عارف بالتفسير، ولد بجرجا من صعيد مصر، ونشأ وعاش في القاهرة. قال السخاوي: "سمع مني، وبرع في العربية، وشارك في غيرها، وأقرأ الطلبة .. " توفي ببركة الحاج خارج القاهرة راجعا من الحج. له "رسالة" في تفسير قوله تعالى: {فلا أقسم بمواقع النجوم} (الواتعة - ٧٥) (١).
_________
(١) الضوء اللامع ٣: ١٧١ والشذرات ٨: ٣٦ وبدائع الزهور ٢: ٣٦١ ودائرة المعارف الإسلامية ٢: ٧٥ والخطط التوفيقية ١٠: ٥٣ وروضات الجنات ٢٧٠.','1','171',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (314,'العرضي [.. - ١١١٥ هـ / .. - ١٧٠٣ م]

<span data-type="title" id=toc-447>خالد بن محمد بن عمر بن عبد الوهاب بن إبراهيم بن محمود بن علي الحلبي، المعروف بالعرضي:</span>
فقيه حنفي، أديب، له نظم، من أهل حلب مولدا ووفاة. أثنى عليه والمرادي. من كتبه "حاشية" على تفسير أبي السعود العمادي (١).

ناصر خسرو [.. - ٤٣١ هـ / .. - ١٠٤٠ م]

<span data-type="title" id=toc-448>خسرو بن حارث بن عيسى بن حسن بن محمد العلوي الأصبهاني، ناصر الدين، المعروف بحجت:</span>
شاعر، حكيم، مشارك في الفقه والحديث والتفسير، توفي بجبال بوخشان. قال صاحب هدية
_________
(١) سلك الدرر ٢: ٧٨.','1','171',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (315,'العارفين: "له تفسير القرآن" على مذهب الملاحدة" (١).

حضر بك [٨١٠ - ٨٦٣ هـ / ١٤٠٧ - ١٤٥٩ م]

<span data-type="title" id=toc-449>خضر بك بن جلال الدين بن أحمد باشا، خير الدين، المولى الرومي الحنفي:</span>
أحد علماء الروم ومدرسيهم وأعيانهم، وأول من ولي قضاء القسطنطينية بعد فتحها كان غزير الاطلاع على الآداب العربية والتركية والفارسية. ولد ونشأ في بلدة "سيوري حصار" وأعطاه السلطان محمد الفاتح مدرسة جده في بروسة، ولما افتتح القسطنطينية ولاه قضاءها. دخل مكة سنة ٨٥٩ هـ. له "حواش" على حاشية التفتازاني على الكشاف في التفسير (٢).

الأزدي [.. - ٧٧٣ هـ / .. - ١٣٧١ م]

<span data-type="title" id=toc-450>خضر بن عبد الرحمن الأزدي، الدمشقي:</span>
مفسر، مشارك في بعض العلوم، له "التبيان في تفسير القرآن" (٣).
_________
(١) هدية العارفين ١: ٣٤٥ وكشف الظنون ١٤٣ وروضات الجنات ٤: ٢٢٣ وهو في المصدرين الأخيرين ناصر خسرو ووفاته بعد سنة ٤٥٠.
(٢) هدية العارفين ١: ٣٤٦ والضوء اللامع ٣: ١٧٨ وكشف الظنون ٣٤١ ومعجم المطبوعات ٨٢٤ والفوائد البهية ٧٠ وعثمانلي مؤلفلري ١: ٢٩٠.
(٣) كشف الظنون ٣٤١ وهدية العارفين ١: ٣٤٥ وبروكالمان ٢: ١١١.','1','172',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (316,'حاجى باشا [.. - ٨٢٠ هـ / .. - ١٤١٧ م]

<span data-type="title" id=toc-451>خضر بن علي بن مروان بن علي القونوي الأصل، المعروف بحاجي باشا الآيديني، ثم المصري:</span>
متكلم، طبيب، مفسر، من علماء الحنفية، أصله من قونية، ولد ونشأ في آيدين، وسكن مصر وتوفي بها. له "مجمع الأنوار في جميع الأسرار" قال صاحب كشف الظنون: وهو تفسير كبير في مجلدات، أوله الحمد لله الذي هدانا بالقرآن (١).

الأماسي [.. - ١٠٨٦ هـ / .. - ١٦٧٥ م]

<span data-type="title" id=toc-452>خضر بن محمد الأماسي:</span>
فقيه حنفي، فرضي، متأدب، عارف بالتفسير، من علماء الروم، من أهل أماسية، وولي الإفتاء بها. قال حاجي خليفة: "هو من علماء عصرنا". من كتبه "حاشية" على أنوار التنزيل في التفسير، للبيضاوي (٢).
_________
(١) هدية العارفين ١: ٣٤٥ وكشف الظنون
(٢) هدية العارفين ١: ٣٤٧ وفيه وفاته سنة ١١٠٠ هـ وقيل ١٠٨٦، وهو الصواب، لأنه من المعاصرين لحاجي خليفة (١٠١٧ - ١٠٦٧ هـ). وفهرس دار الكتب ٢: ١٧٧ والأعلام ٢: ٣٠٧ ومعجم المؤلفين ٤: ١٠٢.','1','172',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (317,'العطوفي [.. - ٩٤٨ هـ / .. - ١٥٤١ م]

<span data-type="title" id=toc-453>خضر بن محمود بن عمر المرزيفوني الرومي المعروف بخير الدين العطوفي:</span>
فقيه حنفي، من علماء الدولة العثمانية، من بلدة مرزيفون. كان واعظا باسطنبول، وتوفي ودفن بها. له كتب منها "حاشية" على أنوار التنزيل، في التفسير للبيضاوي (١).

الخضر بن نصر [٤٧٨ - ٥٦٧ هـ / ١٠٨٥ - ١١٧٢ م]

<span data-type="title" id=toc-454>الخضر بن نصر بن عقيل بن نصر الإربلي، أبو العباس:</span>
فقيه شافعي، فرضي، مفسر، من أهل إربل، تعلم ببغداد، وعاد إلى إربل فاشتغل بالتدريس إلى حين وفاته (٢).

السجستاني [٣٢٦ - ٣٩٩ هـ / ٩٣٧ - ١٠٠٩ م]

<span data-type="title" id=toc-455>خلف بن أحمد بن خلف بن الليث، من (بني يعقوب بن الليث الصفار)</span>
_________
(١) هدية العارفين ١: ٣٤٦ وكشف الظنون ١٤٨٠ والشقائق النعمانية ٢٤٩ والأعلام ٢: ٣٠٧.
(٢) شذرات الذهب ٥: ٨٦ وفيه أنه توفي سنة ٦١٩، خطأ. وطبقات المفسرين للداودي ١: ١٦٣ وطبقات المفسرين للسيوطى ١٣ والبداية والنهاية ١٢: ٢٨٧ وهدية العارفين ١: ٣٤٥ وطبقات السبكي ٧: ٨٣ ووفيات الأعيان ٢: ٢٣٧ وتهذيب ابن عساكر ٥: ١٦٥.','1','173',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (318,'بني يعقوب بن الليث الصفار: أمير سجستان، وينسب إليها، فيقال: السجزي، والسجستاني. نشأ بها في بيت الإمارة، ورحل في صباه في طلب العلم إلى خراسان والعراق، فتفقه وروى الحديث. وعاد إلى سجستان، فوليها مستقلا سنة ٣٥٠ هـ، فضبط أمورها، وضم إليها كرمان، وكانت لبني بويه، ثم استردوها منه. ونزل عن الإمارة مكرها إلى ابنه طاهر سنة ٣٩٥، ثم فتك بطاهر، فانقلب عليه قواد جيشه، وحاصره السلطان محمود بن سبكتكين سنة ٣٩٣ هـ فاستسلم له، فنفاه إلى الجوزجان، ثم سجنه بقرية جرديز (بقرب غزنة) فمات فيها سجينا. له "تفسير القرآن" وهو من أكبر الكتب، اشتمل على أقوال من تقدمه من المفسرين والقراء والنحاة والمحدثين، صنفه مع كبار العلماء في بلاده. قال العتبي: "أنفق على العلماء مدة اشتغالهم بمعونته على تصنيفه عشرين ألف دينار، ونسخته بنيسابور موجودة في مدرسة الصابونية تستغرق عمر الكاتب وتستنفد عمر الناسخ" (١).
_________
(١) معجم البلدان مادة جستان، والكامل في التاريخ لابن الأثير ٨ وانظر فهرسته، و "أيضا، وهدية العارفين ١: ٣٤٨ وكشف الظنون ٤٤٦ واليميني ١: ٩٦ و ٣٥١ - ٣٦٠ و ٣٦٨ - ٣٨٢.','1','173',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (319,'الباجي [.. - ٣٢٠ هـ / .. - ٩٣٢ م]

<span data-type="title" id=toc-456>خلف بن جامع بن حاجب، وقيل حبيب، الباجي:</span>
قال ابن الفرضي: "كان مفتيا مفسرا، وهو من أهل باجة (١).

خلف القارىء [١٥٠ - ٢٢٩ هـ / ٧٦٧ - ٨٤٤ م]

<span data-type="title" id=toc-457>خلف بن هشام البزار الأسدي، أبو محمد:</span>
أحد القراء العشرة، محدث، مفسر، عالم عابد، أصله من (فم الصلح) قرب واسط، واشتهر ببغداد. حدث عنه مسلم في صحيحه وأبو داود في سننه، وأحمد بن حنبل وأبو زرعة الرازي وغيرهم. مات ببغداد. له "فضائل القرآن" (٢).

غرس الدين ابن النقيب [٩٠٠ - ٩٧١ هـ / ١٤٩٤ - ١٥٦٣ م]

<span data-type="title" id=toc-458>خليل ابن أحمد بن خليل، غرس الدين، المعروف بابن النقيب:</span>
طبيب،
_________
(١) تاريخ علماء الأندلس ١٣٥ وطبقات المفسرين للداودي ١: ١٦٣.
(٢) طبقات المفسرين للداودي ١: ١٦٤ وغاية النهاية ١: ٢٧٢ وتاريخ بغداد ٨: ٣٢٢ والعبر للذهبي ١: ٤٠٤ وطبقات القراء للذهبي ١: ١٧١ والشذرات ٢: ٧٢ والنوحم الزاهرة ٢: ٢٥٦.','1','174',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (320,'عالم بالحساب والفلك، عارف بالهندسة والموسيقى، وله نظم حسن، أصله من حمص، ولد بحلب، وتعلم بالقاهرة، ومات بالقسطنطينية. قال مترجموه: "كان صاحب فنون غريبة، ماهرا في وضع الآلات النجومية والهندسية كالربع والأسطرلاب وسائر الأسباب". من كتبه "شرح" جزءين من تفسير البيضاوي. وقال في كشف الظنون: له "تعليقة" على أنوار التنزيل، في التفسير، للبيضاوي (١).

المغنيساوي [.. - ١٢٣٠ هـ / .. - ١٨١٥ م]

<span data-type="title" id=toc-459>خليل بن أحمد مسبحي زاده المغنيساوي، الملقب بنعيمي:</span>
فقيه حنفي، من أهل مغنيسيا (قاعدة بلاد ساروخان في الأناضول الغربية) ولي الافتاء بها. له حواش وشروح على بعض كتب الفقه والتفسير منها "حاشية" على تفسير البيضاوي. كذا في هدية العارفين ١: ٣٥٢، وفي صفحة ٣٥٦ ترجمة أخرى باسم خليل بن أحمد بن همت القونوي، وهو فقيه حنفي، تولي افتاء مغنيسيا، وتوفي سنة
_________
(١) إعلام النبلاء ٦: ٥٢ والشذرات ١٨: ٣٦٤ وكشف الظنون ١٩٢ والأعلام ٢: ٣٦٤ ومعجم المؤلفين ٤: ١١١ و ٨: ٣٨ وورد اسمه في بعض هذه المصادر: غرس الدين بن إبراهيم بن أحمد.','1','174',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (321,'١٢٢٤ هـ، وله نفس الحواشي والشروح التي للسابق، عدا الحاشية على تفسير البيضاوي. وأغلب الظن أن الترجمتين لشخص واحد (١).

قره خليل [... . - ١١٢٣ هـ / .. - ١٧١١ م]

<span data-type="title" id=toc-460>خليل بن حسن بن محمد التيراوي الرومي، المعروف ب "قره خليل</span>":
فقيه حنفي، مفسر، منطقي، قاض، ولي قضاء العسكر بالروم إيلي. من تصانيفه "تفسير سورة تبارك" و "تفسير سورة الملك" وغير ذلك (٢).

الإسعردي [١١٦٧ - ١٢٥٩ هـ / ١٧٥٣ - ١٨٤٣ م]

<span data-type="title" id=toc-461>خليل بن حسين الإسعردي:</span>
مفسر، محدث، من فقهاء الشافعية. له كتب منها "تبصرة القلوب من كلام علام الغيوب" في التفسير، مختصر، و "تفسير القرآن" مطول، لم يكمل (٣).
_________
(١) بالاضافة إلى المرجع المذكور في الترجمة، انظر إيضاح االمكنون ١: ١٤٢ و ٢: ٥٧٢ ومعجم المؤلفين
(٢) هدية العارفين ١: ٣٥٤ ومعجم المؤلفين ٤: ١١٧ وعثمانلي مؤلفلري ١: ٤٠٣.
(٣) هدية العارفين ١: ٣٥٧.','1','175',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (322,'السيرطي [.. - حيا ١٢٥٢ هـ / .. - حيا ١٨٣٦ م]

<span data-type="title" id=toc-462>خليل بن عبد الله السيرطي:</span>
مفسر، من فقهاء الشافعية، نسبته إلى بلدة سيرط (على مرحلة من مدينة وان). له "تفسير القرآن" المسمى ب "تفسير الخليلي" (١).

القزويني [١٠٠١ - ١٠٨٩ هـ / ١٥٩٣ - ١٦٧٨ م]

<span data-type="title" id=toc-463>خليل بن الغازي القزويني:</span>
محدث، مفسر، نحوي، من فقهاء الشيعة الإمامية، من أهل قزوين مولدا ووفاة. من كتبه "حاشية" على "مجمع البيان" في تفسير القرآن، للطبرسي (٢).

صلاح الدين العلائي [٦٩٤ - ٧٦١ هـ / ١٢٩٥ - ١٣٥٩ م]

<span data-type="title" id=toc-464>خليل بن كيكلدي بن عبد الله العلائي الدمشقي، أبو سعيد، صلاح الدين:</span>
محدث، أصولي، من فقهاء الشافعية، مشارك في أنواع من العلوم، كالتفسير والنحو والكلام الخ. ولد وتعلم في
_________
(١) هدية العارفين ١: ٣٥٦،
(٢) هدية العارفين ١: ٣٥٤ وروضات الجنات ٢٦٧ وأعيان الشعية ٣٠: ١٠٤ والأعلام ٢: ٣٦٨ ومعجم المؤلفين ٤: ١٢٥.','1','175',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (323,'دمشق، وسمع بمصر والحجاز. ثم ولي التدريس في المدرسة الصلاحية بالقدس، سنة ٧٣١ هـ وأقام بها إلى أن توفي. قال الداودي: "فسر آيات متفرقة، وأما الحديث فلم يكن في عصره من يدانيه فيه، وأما بقية علومه من فقه ونحو وتفسير وكلام، فكان في كل واحد منها حسن المشاركة". من كتبه "برهان التيسير في عنوان التفسير" (١).

جابي [١٠٨٥ - ١١٣٤ هـ / ١٦٧٤ - ١٧٢٢ م]

<span data-type="title" id=toc-465>خليل بن مصطفى بن عيسى فايض المنجم، المعروف بجابي زاده:</span>
فاضل، مشارك في بعض العلوم، مات منتحرا بإستنبول. من كتبه "رسالة تفسيرية" (٢).

الشماخي [.. - ٦٨٠ هـ / .. - ١٢٨١ م]

<span data-type="title" id=toc-466>أبو الخير بن منصور بن أبي الخير الشماخي السعدي الحضرمي:</span>
فقيه،
_________
(١) طبقات المفسرين للداودي ١: ١٦٥ والأنس الجليل ٢: ١٠٦ والرد الوافر ٥٢ وهدية العارفين ١: ٣٥١ والبدر الطالع ١: ٢٤٥ والدارس ١: ٥٩ والدرر الكامنة ٢: ١٧٩ وشذرات الذهب ٦: ١٩٠ وطبقات الشافعية للأسنوي ١٩٨ وطبقات الشافعية للسبكي ٦: ١٠٤ وذيل تذكرة الحفاظ ٤٣ وفهرس الفهارس ٢:
(٢) سلك الدرر ٢: ١٠٥ وهدية العارفين ١: ٣٥٥','1','176',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (324,'مشارك في التفسير والحديث والفرائض والنحو واللغة، من أهل حضرموت، نزل زبيد ومات بها. له تصانيف (١).

الرملي [٩٩٣ - ١٠٨١ هـ / ١٥٨٥ - ١٦٧٠ م]

<span data-type="title" id=toc-467>خير الدين بن أحمد بن علي بن زين الدين بن عبد الوهاب الأيوبي العليمي الفاروقي الرملي:</span>
شيخ الحنفية في عصره، مفسر، محدث، فقيه. من أهل مدينة الرملة - بفلسطين - مولدا ووفاة. تعلم بها وبالأزهر الشريف في القاهرة. وعاد إلى الرملة فاشتغل بالتدريس وأفتى، وشاعت فتاواه في الآفاق ووردت إليه الأسئلة من كل جهة. قال صاحب خلاصة الأثر: "كان متين الدين عظيم الهيبة، تهابه الحكام من القضاة وأهل السياسة. وكانت الرملة في زمنه أعدل البلاد وللشرع بها ناموس عظيم، وكذا في غالب البلاد القريبة منها .. ". ولمكانة الرملي العلمية قصده العلماء والفقهاء والمفتون وطلاب العلم للأخذ عنه والاستفادة من مكتبته التي جمع فيها الكثير من نفائس الكتب (٢).
_________
(١) العقود اللؤلؤية ١: ٢١٩.
(٢) خلاصة الأثر ٢: ١٣٤ وهدية العارفين ١: ٣٥٨ وفهرس الفهارس ١: ٦٨٧ ومعجم أعلام فلسطين (مخطوط).','1','176',NULL,NULL,'0');
