INSERT INTO [b13846] VALUES (328,'(د)

الظاهري [٢٠١ - ٢٧٠ هـ / ٨٠٦ - ٨٨٤ م]

<span data-type="title" id=toc-469>داود بن علي بن خلف الأصبهاني، أبو سليمان، الملقب بالظاهري:</span>
إمام مجتهد، حافظ للحديث، كان من تلاميذ أصحاب الإمام الشافعي ومن أكثر الناس تعصبا له، وصنف في فضائله. ثم أخذ بظاهر الكتاب والسنة، وأعرض عن التأويل والرأي والقياس، ولذا سمي ظاهريا. وإليه تنسب طائفة الظاهرية. وكان قوي الحجة، سئل: "لماذا خالفت الشافعي ورددت القياس" فقال: طبقت أدلته في إبطال الاستحسان، فوجدتها تبطل القياس". أصله من بلدة قاشان بأصبهان، ومولده بالكوفة، ونشأ وسمع الحديث ببغداد وغيرها. وكان ناسكا متعبدا لم يقبل عطاء من أحد. قال','1','181',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (329,'الشيرازي: انتهت إليه رياسة العلم في بغداد". "وقال ثعلب: كان عقل داود أكثر من علمه. من تصانيفه الكثيرة: "أحكام القرآن" (١).

البصري [.. - ١٣٩ هـ / .. - ٧٥٦ م]

<span data-type="title" id=toc-470>داود بن أبي هند دينار، القشيري بالولاء، البصري، أبو بكر، ويقال أبو محمد:</span>
حافظ للحديث، مفسر، من أهل
_________
(١) طبقات المفسرين ١: ١٦٦ وميزان الاعتدال ٢: ١٤ ولسان الميزان ٢: ٤٢٢ وتهذيب الأسماء واللغات ١: ١٨٢ وتاريخ بغداد ٨: ٣٦٩ والعبر ٢: ٤٥ والشذرات ٢: ١٥٨ والسبكي ٢: ٢٨٤ والشيرازي ٧٦ وروضات الجنات ٢٧٦ والجواهر المضية ٢: ٤١٩ وابن خلكان ١: ٢٥٥ وتذكرة الحفاظ ٥٧٢ والنجوم الزاهرة ٣: ٤٧ وتاريخ أصبهان ١: ٣٠٢ ومرآة الجنان ٢: ١٨٤ ومفتاح السعادة ٢: ٣٠٢ وهدية العارفين ١: ٣٠٩.','1','181',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (330,'البصرة، سمع بها وبواسط. قال الذهبي: رأى أنس بن مالك، وروى عن أبي العالية وعكرمة والشعبي وغيرهم. وكان من حفاظ أهل البصرة ومفتيهم، حديثه في الكتب الستة، لكن في البخارى استشهادا، وقال ابن ناصر الدين: "كان أحد القانتين، رأسا في العمل والعلم، قدوة في الدين". ترجم له صاحب طبقات المفسرين وقال: له "تفسير القرآن" (١).

الأودني [.. - ٣٢٠ هـ / .. - ٩٣٢ م]

<span data-type="title" id=toc-471>داود بن محمد بن موسى بن هارون الأودني، أبو سليمان:</span>
فقيه حنفي، عارف بالحديث والتفسير، من أهل "أودنة" من قرى بخارا. من تصانيفه "فضائل القرآن" (٢).
_________
(١) تذكرة الحفاظ ١: ١٤٦ وخلاصة تهذيب الكمال ٩٤ والعبر ١: ١٨٩ وطبقات المفسرين للداودي ١: ١٦٩ وفيه: وفاته سنة ١٤٠ هـ ومثله في شذرات الذهب ١: ٢٠٨ والنجوم الزاهرة ١: ٣٤٢ وفيه وفاته سنة ١٤١، والتاريخ الكبير ١/ ٢: ٢٣١ وعنه أخذت تاريخ الوفاة.
(٢) تاج التراجم ٢٨ والجواهر المضية ١: ٢٣٨ وهدية العارفين ١: ٣٥٩ وكشف الظنون ١٢٧٧.','1','182',NULL,NULL,'0');
